const tumAyetler = [
  {id: 1, sure_no: 1, ayet_no: 1, sure_adi: "Fatiha", text: "بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّح۪يمِ", translation: "Rahmeti Bol ve Kesintisiz Olan Allah'ın Adıyla.", transliteration: "Bismillahir rahmanir rahim."},
  {id: 2, sure_no: 2, ayet_no: 2, sure_adi: "Fatiha", text: "اَلْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَۙ", translation: "Övülmeye değer olan yalnızca Alemlerin Rabb'i Allah'tır.", transliteration: "El hamdu lillahi rabbil alemin ."},
  {id: 3, sure_no: 3, ayet_no: 3, sure_adi: "Fatiha", text: "اَلرَّحْمٰنِ الرَّح۪يمِۙ", translation: "O'nun Rahmeti Bol ve Kesintisizdir.", transliteration: "Er rahmanir rahim."},
  {id: 4, sure_no: 4, ayet_no: 4, sure_adi: "Fatiha", text: "مَالِكِ يَوْمِ الدّ۪ينِۜ", translation: "O, Hesap Günü'nün sahibidir.", transliteration: "Maliki yevmid din."},
  {id: 5, sure_no: 5, ayet_no: 5, sure_adi: "Fatiha", text: "اِيَّاكَ نَعْبُدُ وَاِيَّاكَ نَسْتَع۪ينُۜ", translation: "Yalnız Sana kulluk eder ve yalnız Sen'den yardım dileriz.", transliteration: "İyyake na'budu ve iyyake nestain."},
  {id: 6, sure_no: 6, ayet_no: 6, sure_adi: "Fatiha", text: "اِهْدِنَا الصِّرَاطَ الْمُسْتَق۪يمَۙ", translation: "Bize doğru yolu göster;", transliteration: "İhdinas sıratel mustakim."},
  {id: 7, sure_no: 7, ayet_no: 7, sure_adi: "Fatiha", text: "صِرَاطَ الَّذ۪ينَ اَنْعَمْتَ عَلَيْهِمْۙ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلَا الضَّٓالّ۪ينَ", translation: "Nimet verdiklerinin yolunu. Gazabına uğrayanların ve sapkınların yolunu değil.", transliteration: "Sıratallezine en'amte aleyhim gayril magdubi aleyhim ve lad dallin."},
  {id: 8, sure_no: 1, ayet_no: 1, sure_adi: "Bakara", text: "الٓمٓ ۚ", translation: "Elif. Lam. Mim.", transliteration: "Elif, lam, mim."},
  {id: 9, sure_no: 2, ayet_no: 2, sure_adi: "Bakara", text: "ذٰلِكَ الْكِتَابُ لَا رَيْبَۚۛ ف۪يهِۚۛ هُدًى لِلْمُتَّق۪ينَۙ", translation: "Bu Kitap, mutlak gerçeğin ta kendisidir. O, muttakiler için hidayettir.", transliteration: "Zalikel kitabu la reybe fih, huden lil muttekin."},
  {id: 10, sure_no: 3, ayet_no: 3, sure_adi: "Bakara", text: "اَلَّذ۪ينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُق۪يمُونَ الصَّلٰوةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَۙ", translation: "Onlar; gayba iman ederler, salatı ikame ederler ve verdiğimiz rızıktan infak ederler.", transliteration: "Ellezine yu'minune bil gaybi ve yukimunes salate ve mimma razaknahum yunfikun."},
  {id: 11, sure_no: 4, ayet_no: 4, sure_adi: "Bakara", text: "وَالَّذ۪ينَ يُؤْمِنُونَ بِمَٓا اُنْزِلَ اِلَيْكَ وَمَٓا اُنْزِلَ مِنْ قَبْلِكَۚ وَبِالْاٰخِرَةِ هُمْ يُوقِنُونَۜ", translation: "Onlar, sana indirilene ve senden önce indirilmiş olanlara iman ederler. Ve onlar, ahirete de kesin olarak iman ederler.", transliteration: "Vellezine yu'minune bi ma unzile ileyke ve ma unzile min kablik ve bil ahireti hum yukınun."},
  {id: 12, sure_no: 5, ayet_no: 5, sure_adi: "Bakara", text: "اُو۬لٰٓئِكَ عَلٰى هُدًى مِنْ رَبِّهِمْ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "Onlar, Rabb'lerinden bir hidayet üzerindedirler. Kurtuluşa erenler onlardır.", transliteration: "Ulaike ala huden min rabbihim ve ulaike humul muflihun."},
  {id: 13, sure_no: 6, ayet_no: 6, sure_adi: "Bakara", text: "اِنَّ الَّذ۪ينَ كَفَرُوا سَوَٓاءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُونَ", translation: "Şu bir gerçektir ki: Kafirleri uyarsan da, uyarmasan da onlar için birdir; iman etmezler.", transliteration: "İnnellezine keferu sevaun aleyhim e enzertehum em lem tunzirhum la yu'minun."},
  {id: 14, sure_no: 7, ayet_no: 7, sure_adi: "Bakara", text: "خَتَمَ اللّٰهُ عَلٰى قُلُوبِهِمْ وَعَلٰى سَمْعِهِمْۜ وَعَلٰٓى اَبْصَارِهِمْ غِشَاوَةٌۘ وَلَهُمْ عَذَابٌ عَظ۪يمٌ۟", translation: "Allah, onların kalplerini ve kulaklarını mühürlemiştir. Onların gözlerinde perde vardır. Onları büyük bir azap beklemektedir.", transliteration: "Hatemallahu ala kulubihim ve ala sem'ıhim, ve ala ebsarihim gışaveh, ve lehum azabun azim."},
  {id: 15, sure_no: 8, ayet_no: 8, sure_adi: "Bakara", text: "وَمِنَ النَّاسِ مَنْ يَقُولُ اٰمَنَّا بِاللّٰهِ وَبِالْيَوْمِ الْاٰخِرِ وَمَا هُمْ بِمُؤْمِن۪ينَۢ", translation: "Kimi insanlar, iman etmedikleri halde, Allah'a ve ahiret gününe iman ettiklerini söylerler.", transliteration: "Ve minen nasi men yekulu amenna billahi ve bil yevmil ahıri ve ma hum bi mu'minin."},
  {id: 16, sure_no: 9, ayet_no: 9, sure_adi: "Bakara", text: "يُخَادِعُونَ اللّٰهَ وَالَّذ۪ينَ اٰمَنُواۚ وَمَا يَخْدَعُونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَشْعُرُونَۜ", translation: "Onlar, Allah'ı ve iman edenleri aldatmaya çalışıyorlar. Oysa sadece kendilerini aldatıyorlar da bunun ayırdında değiller.", transliteration: "Yuhadiunallahe vellezine amenu, ve ma yahdeune illa enfusehum ve ma yeş'urun."},
  {id: 17, sure_no: 10, ayet_no: 10, sure_adi: "Bakara", text: "ف۪ي قُلُوبِهِمْ مَرَضٌۙ فَزَادَهُمُ اللّٰهُ مَرَضاًۚ وَلَهُمْ عَذَابٌ اَل۪يمٌۙ بِمَا كَانُوا يَكْذِبُونَ", translation: "Onların kalplerinde hastalık vardır. Allah da bu hastalıklarını arttırmıştır. Bu yalancılıklarından dolayı onlara can yakıcı bir azap vardır.", transliteration: "Fi kulubihim maradun, fe zadehumullahu marada ve lehum azabun elimun bi ma kanu yekzibun."},
  {id: 18, sure_no: 11, ayet_no: 11, sure_adi: "Bakara", text: "وَاِذَا ق۪يلَ لَهُمْ لَا تُفْسِدُوا فِي الْاَرْضِۙ  قَالُٓوا اِنَّمَا نَحْنُ مُصْلِحُونَ", translation: "Onlara, \"Yeryüzünde bozgunculuk yapmayın.\" denildiğinde, \"Biz ancak düzelticileriz.\" derler.", transliteration: "Ve iza kile lehum la tufsidu fil ardı, kalu innema nahnu muslihun."},
  {id: 19, sure_no: 12, ayet_no: 12, sure_adi: "Bakara", text: "اَلَٓا اِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلٰكِنْ لَا يَشْعُرُونَ", translation: "İyi bilin ki, onlar bozguncuların ta kendileridir. Fakat bunun ayırdında değiller.", transliteration: "E la innehum humul mufsidune ve lakin la yeş'urun."},
  {id: 20, sure_no: 13, ayet_no: 13, sure_adi: "Bakara", text: "وَاِذَا ق۪يلَ لَهُمْ اٰمِنُوا كَمَٓا اٰمَنَ النَّاسُ قَالُٓوا اَنُؤْمِنُ كَمَٓا اٰمَنَ السُّفَـهَٓاءُۜ  اَلَٓا اِنَّهُمْ هُمُ السُّفَـهَٓاءُ وَلٰكِنْ لَا يَعْلَمُونَ", translation: "Ne zaman onlara, \"İman eden kimseler gibi iman edin.\" dense, \"Biz, hiç aklı ermeyenler gibi iman eder miyiz?\" derler. İyi bilin ki asıl aklı ermeyenler onlardır; fakat bunun ayırdında değiller.", transliteration: "Ve iza kile lehum aminu kema amenen nasu kalu e nu'minu kema amenes sufehau, e la innehum humus sufehau ve lakin la ya'lemun."},
  {id: 21, sure_no: 14, ayet_no: 14, sure_adi: "Bakara", text: "وَاِذَا لَقُوا الَّذٖينَ اٰمَنُوا قَالُٓوا اٰمَنَّاۚ وَاِذَا خَلَوْا اِلٰى شَيَاطٖينِهِمْۙ قَالُٓوا اِنَّا مَعَكُمْۙ اِنَّمَا نَحْنُ مُسْتَهْزِؤُ۫نَ", translation: "İman edenlerle karşılaştıkları zaman: \"İnandık.\" derler. Şeytanları ile baş başa kaldıkları zaman, \"Biz, sizinle beraberiz; onlarla sadece alay ediyoruz.\" derler.", transliteration: "Ve iza lekullezine amenu kalu amenna, ve iza halev ila şeyatinihim, kalu inna meakum, innema nahnu mustehziun."},
  {id: 22, sure_no: 15, ayet_no: 15, sure_adi: "Bakara", text: "اَللّٰهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ", translation: "Allah da onları alay konusu yapar. Azgınlıkları içinde bocalayıp durmalarına zaman tanır.", transliteration: "Allahu yestehziu bihim ve yemudduhum fi tugyanihim ya'mehun."},
  {id: 23, sure_no: 16, ayet_no: 16, sure_adi: "Bakara", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدٰىۖ فَمَا رَبِحَتْ تِجَارَتُهُمْ وَمَا كَانُوا مُهْتَد۪ينَ", translation: "Onlar, doğru yola karşılık sapkınlığı satın almışlardır. Bu ticaretlerinde bir kazanç yoktur. Ve doğru yolu bulamamışlardır.", transliteration: "Ulaikellezineşterevud dalalete bil huda, fe ma rabihat ticaretuhum ve ma kanu muhtedin."},
  {id: 24, sure_no: 17, ayet_no: 17, sure_adi: "Bakara", text: "مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نَاراًۚ فَلَمَّٓا اَضَٓاءَتْ مَا حَوْلَهُ ذَهَبَ اللّٰهُ بِنُورِهِمْ وَتَرَكَهُمْ ف۪ي ظُلُمَاتٍ لَا يُبْصِرُونَ", translation: "Onların durumu karanlıkta ateş yakan kimselerin durumu gibidir. Ateş, etraflarını aydınlatır aydınlatmaz, Allah onların ışığını yok eder ve onları karanlıklar içinde görmez halde bırakır.", transliteration: "Meseluhum ke meselillezistevkade nara, fe lemma edaet ma havlehu zeheballahu bi nurihim ve terekehum fi zulumatin la yubsirun."},
  {id: 25, sure_no: 18, ayet_no: 18, sure_adi: "Bakara", text: "صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُونَۙ", translation: "Sağır, dilsiz ve kördürler. Artık doğru yola dönmezler.", transliteration: "Summun bukmun umyun fe hum la yerciun."},
  {id: 26, sure_no: 19, ayet_no: 19, sure_adi: "Bakara", text: "اَوْ كَصَيِّبٍ مِنَ السَّمَٓاءِ ف۪يهِ ظُلُمَاتٌ وَرَعْدٌ وَبَرْقٌۚ يَجْعَلُونَ اَصَابِعَهُمْ ف۪ٓي اٰذَانِهِمْ مِنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِۜ وَاللّٰهُ مُح۪يطٌ بِالْكَافِر۪ينَ", translation: "Ya da onların durumu; içinde karanlıklar, gök gürültüsü ve şimşekler bulunan, sağanak bir yağmurda; ölüm korkusuyla, yıldırım sesinden kulaklarını parmaklarıyla tıkayan kimselerin durumuna benzer. Oysaki Allah, Kafirleri her yönden kuşatmıştır.", transliteration: "Ev ke sayyibin mines semai fihi zulumatun ve ra'dun ve berk, yec'alune esabiahum fi azanihim mines savaiki hazaral mevt, vallahu muhitun bil kafirin."},
  {id: 27, sure_no: 20, ayet_no: 20, sure_adi: "Bakara", text: "يَكَادُ الْبَرْقُ يَخْطَفُ اَبْصَارَهُمْۜ  كُلَّمَٓا اَضَٓاءَ لَهُمْ مَشَوْا ف۪يهِۙ وَاِذَٓا اَظْلَمَ عَلَيْهِمْ قَامُواۜ وَلَوْ شَٓاءَ اللّٰهُ لَذَهَبَ بِسَمْعِهِمْ وَاَبْصَارِهِمْۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ۟", translation: "Şimşek, neredeyse görmelerini yok edecekti. Şimşek, aydınlık verince ışığında yürürler; üzerlerine karanlık çökünce de oldukları yerde kalakalırlar. Allah dileseydi onların işitme ve görme yeteneklerini tamamen yok ederdi. Kuşkusuz, Allah'ın gücü her şeye yeter.", transliteration: "Yekadul berku yahtafu ebsarehum kullema edae lehum meşev fihi, ve iza azleme aleyhim kamu ve lev şaellahu le zehebe bi sem'ihim ve ebsarihim innallahe ala kulli şey'in kadir."},
  {id: 28, sure_no: 21, ayet_no: 21, sure_adi: "Bakara", text: "يَٓا اَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذ۪ي خَلَقَكُمْ وَالَّذ۪ينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَۙ", translation: "Ey insanlar! Sizi ve sizden öncekileri yaratan Rabb'inize kulluk edin ki takva sahibi olasınız.", transliteration: "Ya eyyuhen nasu'budu rabbekumullezi halakakum vellezine min kablikum leallekum tettekun."},
  {id: 29, sure_no: 22, ayet_no: 22, sure_adi: "Bakara", text: "اَلَّذ۪ي جَعَلَ لَكُمُ الْاَرْضَ فِرَاشاً وَالسَّمَٓاءَ بِنَٓاءًۖ وَاَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَخْرَجَ بِه۪ مِنَ الثَّمَرَاتِ رِزْقاً لَكُمْۚ فَلَا تَجْعَلُوا لِلّٰهِ اَنْدَاداً وَاَنْتُمْ تَعْلَمُونَ", translation: "O ki sizin için yeri bir döşek, göğü de bir bina yaptı ve gökten su indirip onunla size rızık olarak çeşitli ürünler çıkardı. O halde, Allah'a bile bile denkler koşmayın.", transliteration: "Ellezi ceale lekumul arda firaşen ves semae binaa, ve enzele mines semai maen fe ahrece bihi mines semarati rızkan lekum, fe la tec'alu lillahi endaden ve entum ta'lemun."},
  {id: 30, sure_no: 23, ayet_no: 23, sure_adi: "Bakara", text: "وَاِنْ كُنْتُمْ ف۪ي رَيْبٍ مِمَّا نَزَّلْنَا عَلٰى عَبْدِنَا فَأْتُوا بِسُورَةٍ مِنْ مِثْلِه۪ۖ وَادْعُوا شُهَدَٓاءَكُمْ مِنْ دُونِ اللّٰهِ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Eğer kulumuza indirdiğimizden kuşku duyuyorsanız, o zaman onun benzeri bir sure getirin. Allah'tan başka bütün tanıklarınızı da çağırın. Eğer doğru söyleyen kimselerseniz!", transliteration: "Ve in kuntum fi reybin mimma nezzelna ala abdina fe'tu bi suretin min mislihi, ved'u şuhedaekum min dunillahi in kuntum sadıkin."},
  {id: 31, sure_no: 24, ayet_no: 24, sure_adi: "Bakara", text: "فَاِنْ لَمْ تَفْعَلُوا وَلَنْ تَفْعَلُوا فَاتَّقُوا النَّارَ الَّت۪ي وَقُودُهَا النَّاسُ وَالْحِجَارَةُۚ اُعِدَّتْ لِلْكَافِر۪ينَ", translation: "Eğer bunu yapamazsanız -ki asla yapamayacaksınız- o halde Kafirler için hazırlanmış, yakıtı insanlar ve taşlar olan ateşten korunun.", transliteration: "Fe in lem tef'alu ve len tef'alu fettekun narelleti vakuduhan nasu vel hicaratu, uiddet lil kafirin."},
  {id: 32, sure_no: 25, ayet_no: 25, sure_adi: "Bakara", text: "وَبَشِّرِ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ اَنَّ لَهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ كُلَّمَا رُزِقُوا مِنْهَا مِنْ ثَمَرَةٍ رِزْقاًۙ قَالُوا هٰذَا الَّذ۪ي رُزِقْنَا مِنْ قَبْلُ وَاُتُوا بِه۪ مُتَشَابِهاًۜ وَلَهُمْ ف۪يهَٓا اَزْوَاجٌ مُطَهَّرَةٌ وَهُمْ ف۪يهَا خَالِدُونَ", translation: "İman edip, salihatı yapanları; içlerinde ırmaklar akan cennetler ile müjdele. Onlara ne zaman yiyecek bir şey sunulsa: \"Bu daha önce rızıklandığımız şeydir.\" derler. Oysa bu onlara benzer olarak verilmiştir. Onlar için arındırılmış eşler vardır. Ve onlar, orada kalıcıdırlar.", transliteration: "Ve beşşirillezine amenu ve amilus salihati enne lehum cennatin tecri min tahtihel enhar, kullema ruziku minha min semeretin rızkan kalu hazellezi ruzıkna min kabl ve utu bihi muteşabiha, ve lehum fiha ezvacun mutahharatun ve hum fiha halidun."},
  {id: 33, sure_no: 26, ayet_no: 26, sure_adi: "Bakara", text: "اِنَّ اللّٰهَ لَا يَسْتَحْـي۪ٓ اَنْ يَضْرِبَ مَثَلاً مَا بَعُوضَةً فَمَا فَوْقَهَاۜ  فَاَمَّا الَّذ۪ينَ اٰمَنُوا فَيَعْلَمُونَ اَنَّهُ الْحَقُّ مِنْ رَبِّهِمْۚ  وَاَمَّا الَّذ۪ينَ كَفَرُوا فَيَقُولُونَ مَاذَٓا اَرَادَ اللّٰهُ بِهٰذَا مَثَلاًۢ يُضِلُّ بِه۪ كَث۪يراً وَيَهْد۪ي بِه۪ كَث۪يراًۜ وَمَا يُضِلُّ بِه۪ٓ اِلَّا الْفَاسِق۪ينَۙ", translation: "Doğrusu Allah, bir sivrisineği veya ondan daha küçüğünü örnek olarak vermekten çekinmez. İman Edenler, bunun, Rabb'lerinden bir gerçek olduğunu bilirler. Kafirler ise: \"Allah, bu örnekle neyi amaçlamıştır?\" derler. Allah, bu örnekle birçok kimseyi saptırır, birçok kimseyi de doğru yola iletir. Bununla, ancak fasıkları saptırır.", transliteration: "İnnallahe la yestahyi en yadribe meselen ma beudaten fe ma fevkaha fe emmellezine amenu fe ya'lemune ennehul hakku min rabbihim, ve emmellezine keferu fe yekulune maza eradallahu bi haza mesela, yudıllu bihi kesiran ve yehdi bihi kesira ve ma yudıllu bihi illel fasıkin."},
  {id: 34, sure_no: 27, ayet_no: 27, sure_adi: "Bakara", text: "اَلَّذ۪ينَ يَنْقُضُونَ عَهْدَ اللّٰهِ مِنْ بَعْدِ م۪يثَاقِه۪ۖ وَيَقْطَعُونَ مَٓا اَمَرَ اللّٰهُ بِه۪ٓ اَنْ يُوصَلَ وَيُفْسِدُونَ فِي الْاَرْضِۜ اُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ", translation: "O kimseler misaklerinden sonra Allah'ın ahdini bozarlar. Allah'ın bağlı kalınmasını istediği şeyle bağlarını koparırlar ve yeryüzünde bozgunculuk yaparlar. İşte onlar, hüsrana uğrayanlardır.", transliteration: "Ellezine yenkudune ahdallahi min ba'di misakıh, ve yaktaune ma emerallahu bihi en yusale ve yufsidune fil ard ulaike humul hasirun."},
  {id: 35, sure_no: 28, ayet_no: 28, sure_adi: "Bakara", text: "كَيْفَ تَكْفُرُونَ بِاللّٰهِ وَكُنْتُمْ اَمْوَاتاً فَاَحْيَاكُمْۚ ثُمَّ يُم۪يتُكُمْ ثُمَّ يُحْي۪يكُمْ ثُمَّ اِلَيْهِ تُرْجَعُونَ", translation: "Allah'a, nasıl küfrediyorsunuz? Siz ölüler iken sizi var etti, sonra sizi öldürecek, sonra tekrar dirilterek kendisine döndürecektir.", transliteration: "Keyfe tekfurune billahi ve kuntum emvaten fe ahyakum, summe yumitukum summe yuhyikum summe ileyhi turceun."},
  {id: 36, sure_no: 29, ayet_no: 29, sure_adi: "Bakara", text: "هُوَ الَّذ۪ي خَلَقَ لَكُمْ مَا فِي الْاَرْضِ جَم۪يعاً ثُمَّ اسْتَوٰٓى اِلَى السَّمَٓاءِ فَسَوّٰيهُنَّ سَبْعَ سَمٰوَاتٍۜ وَهُوَ بِكُلِّ شَيْءٍ عَل۪يمٌ۟", translation: "O ki yeryüzündekilerin tamamını sizin için yarattı. Sonra O, göğe isteva etti; onları yedi gök halinde düzenledi. O, Her Şeyi Bilen'dir.", transliteration: "Huvellezi halaka lekum ma fil ardı cemian summesteva iles semai fe sevvahunne seb'a semavat, ve huve bi kulli şey'in alim."},
  {id: 37, sure_no: 30, ayet_no: 30, sure_adi: "Bakara", text: "وَاِذْ قَالَ رَبُّكَ لِلْمَلٰٓئِكَةِ اِنّ۪ي جَاعِلٌ فِي الْاَرْضِ خَل۪يفَةًۜ  قَالُٓوا اَتَجْعَلُ ف۪يهَا مَنْ يُفْسِدُ ف۪يهَا وَيَسْفِكُ الدِّمَٓاءَۚ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَۜ قَالَ اِنّ۪ٓي اَعْلَمُ مَا لَا تَعْلَمُونَ", translation: "Hani! Bir zamanlar Rabb'in, meleklere: \"Ben yeryüzünde bir halife tayin edeceğim.\" demişti. Melekler: orada bozgunculuk yapacak, kan dökecek birisini mi halife yapacaksın? Oysa biz Seni övgü ile yüceltip kutsuyoruz.\" dediler. Allah: \"Ben sizin bilmediklerinizi bilirim.\" dedi.", transliteration: "Ve iz kale rabbuke lil melaiketi inni cailun fil ardı halifeh, kalu e tec'alu fiha men yufsidu fiha ve yesfikud dima, ve nahnu nusebbihu bi hamdike ve nukaddisu lek, kale inni a'lemu ma la ta'lemun."},
  {id: 38, sure_no: 31, ayet_no: 31, sure_adi: "Bakara", text: "وَعَلَّمَ اٰدَمَ الْاَسْمَٓاءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلٰٓئِكَةِ فَقَالَ اَنْبِؤُ۫ن۪ي بِاَسْمَٓاءِ هٰٓؤُ۬لَٓاءِ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Allah, Adem'e bütün isimleri öğretti. Sonra onları meleklere sunup: \"Eğer doğru söyleyenlerden iseniz bunların isimlerini bana bildirin.\" dedi.", transliteration: "Ve alleme ademel esmae kulleha summe aradahum alel melaiketi fe kale enbiuni bi esmai haulai in kuntum sadikin."},
  {id: 39, sure_no: 32, ayet_no: 32, sure_adi: "Bakara", text: "قَالُوا سُبْحَانَكَ لَا عِلْمَ لَنَٓا اِلَّا مَا عَلَّمْتَنَاۜ اِنَّكَ اَنْتَ الْعَل۪يمُ الْحَك۪يمُ", translation: "Melekler de: \"Sen subhansın. Bize öğrettiklerinden başka bizim hiçbir bilgimiz yoktur. Şüphesiz Sen her şeyi gerçeği ile bilensin.\" dediler.", transliteration: "Kalu subhaneke la ilme lena illa ma allemtena inneke entel alimul hakim."},
  {id: 40, sure_no: 33, ayet_no: 33, sure_adi: "Bakara", text: "قَالَ يَٓا اٰدَمُ اَنْبِئْهُمْ بِاَسْمَٓائِهِمْۚ فَلَمَّٓا اَنْبَاَهُمْ بِاَسْمَٓائِهِمْۙ قَالَ اَلَمْ اَقُلْ لَكُمْ اِنّ۪ٓي اَعْلَمُ غَيْبَ السَّمٰوَاتِ وَالْاَرْضِ وَاَعْلَمُ مَا تُبْدُونَ وَمَا كُنْتُمْ تَكْتُمُونَ", translation: "Allah: \"Ey Adem! Bunların isimlerini onlara bildir.\" dedi. Adem isimleri onlara bildirince, Allah meleklere: \"Göklerin ve yerin gaybını Ben bilirim; Ben, sizin açıkladıklarınızı da içinizde gizlediklerinizi de bilirim, dememiş miydim?\" dedi.", transliteration: "Kale ya ademu enbi'hum bi esmaihim, fe lemma enbeehum bi esmaihim, kale e lem ekul lekum inni a'lemu gaybes semavati vel ardı ve a'lemu ma tubdune ve ma kuntum tektumun."},
  {id: 41, sure_no: 34, ayet_no: 34, sure_adi: "Bakara", text: "وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ اَبٰى وَاسْتَكْبَرَ وَكَانَ مِنَ الْكَافِر۪ينَ", translation: "Sonra meleklere: \"Adem için secde edin.\" dedik. İblis hariç hemen secde ettiler. O, yüz çevirip büyüklük tasladı. O Kafirlerdendi.", transliteration: "Ve iz kulna lil melaiketiscudu li ademe fe secedu illa iblis, eba vestekbere ve kane minel kafirin."},
  {id: 42, sure_no: 35, ayet_no: 35, sure_adi: "Bakara", text: "وَقُلْنَا يَٓا اٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَداً حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِم۪ينَ", translation: "Dedik ki: \"Ey Adem! Eşinle birlikte cennette oturun. Orada dilediğiniz her şeyden bol bol yiyin. Fakat şu şecereye yaklaşmayın; yoksa haksızlık yapmış olursunuz.\"", transliteration: "Ve kulna ya ademuskun ente ve zevcukel cennete ve kula minha ragaden haysu şi'tuma ve la takraba hazihiş şecerete fe tekuna minez zalimin."},
  {id: 43, sure_no: 36, ayet_no: 36, sure_adi: "Bakara", text: "فَاَزَلَّهُمَا الشَّيْطَانُ عَنْهَا فَاَخْرَجَهُمَا مِمَّا كَانَا ف۪يهِۖ وَقُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّۚ وَلَكُمْ فِي الْاَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ اِلٰى ح۪ينٍ", translation: "Fakat şeytan onları, oradan kaydırdı. Böylece ikisini de içinde bulundukları durumdan çıkardı. Biz de: \"Bir kısmınız bir kısmınıza düşman olarak inin. Yeryüzü, belli bir süreye kadar size barınak ve geçinme yeri olacak.\" dedik.", transliteration: "Fe ezellehumaş şeytanu anha fe ahrecehuma mimma kana fih, ve kulnahbitu ba'dukum li ba'din aduvv, ve lekum fil ardı mustekarrun ve metaun ila hin."},
  {id: 44, sure_no: 37, ayet_no: 37, sure_adi: "Bakara", text: "فَتَلَقّٰٓى اٰدَمُ مِنْ رَبِّه۪ كَلِمَاتٍ فَتَابَ عَلَيْهِۜ اِنَّهُ هُوَ التَّوَّابُ الرَّح۪يمُ", translation: "Derken Adem, Rabb'inden kelimeler aldı. Böylece, Adem'in tevbesini kabul etti. Kuşkusuz O, Tevbeleri Kabul Eden'dir, Rahmeti Kesintisiz'dir.", transliteration: "Fe telekka ademu min rabbihi kelimatin fe tabe aleyh, innehu huvet tevvabur rahim."},
  {id: 45, sure_no: 38, ayet_no: 38, sure_adi: "Bakara", text: "قُلْنَا اهْبِطُوا مِنْهَا جَم۪يعاًۚ فَاِمَّا يَأْتِيَنَّكُمْ مِنّ۪ي هُدًى فَمَنْ تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "\"Hepiniz oradan inin dedik. Ben'den bir hidayet geldiğinde kim hidayetime uyarsa, artık onlara korku yoktur ve onlar üzülmeyeceklerdir.", transliteration: "Kulnahbitu minha cemia, fe imma ye'tiyennekum minni hudenfe men tebia hudaye fe la havfun aleyhim ve la hum yahzenun."},
  {id: 46, sure_no: 39, ayet_no: 39, sure_adi: "Bakara", text: "وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ۟", translation: "Ayetlerimizi küfredip yalanlayanlar, Cehennemliktirler. Ve onlar, orada sürekli kalacaklardır.", transliteration: "Vellezine keferu ve kezzebu bi ayatina ulaike ashabun nar, hum fiha halidun."},
  {id: 47, sure_no: 40, ayet_no: 40, sure_adi: "Bakara", text: "يَا بَن۪ٓي اِسْرَٓائ۪لَ اذْكُرُوا نِعْمَتِيَ الَّت۪ٓي اَنْعَمْتُ عَلَيْكُمْ وَاَوْفُوا بِعَهْد۪ٓي اُو۫فِ بِعَهْدِكُمْ وَاِيَّايَ فَارْهَبُونِ", translation: "Ey İsrailoğulları! Size bağışladığım nimetimi anımsayın. Bana verdiğiniz sözü tutun ki Ben de size verdiğim sözü yerine getireyim. Ve yalnızca, Bana karşı gelmekten sakının.", transliteration: "Ya beni israilezkuru ni'metiyelleti en'amtu aleykum ve evfu bi ahdi ufi bi ahdikum ve iyyaye ferhebun."},
  {id: 48, sure_no: 41, ayet_no: 41, sure_adi: "Bakara", text: "وَاٰمِنُوا بِمَٓا اَنْزَلْتُ مُصَدِّقاً لِمَا مَعَكُمْ وَلَا تَكُونُٓوا اَوَّلَ  كَافِرٍ بِه۪ۖ وَلَا تَشْتَرُوا بِاٰيَات۪ي ثَمَناً قَل۪يلاًۘ وَاِيَّايَ فَاتَّقُونِ", translation: "Yanınızdakini \"tasdik edici\" olarak gönderdiğimize iman edin. Onu kafirlik edenlerin ilki siz olmayın. Ayetlerimi az bir değere değişmeyin. Ve Bana karşı takvalı olun.", transliteration: "Ve aminu bi ma enzeltu musaddikan li ma meakum ve la tekunu evvele kafirin bih, ve la teşteru bi ayati semenen kalilen ve iyyaye fettekuni."},
  {id: 49, sure_no: 42, ayet_no: 42, sure_adi: "Bakara", text: "وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَاَنْتُمْ تَعْلَمُونَ", translation: "\"Hakk'ı Batıl'la\" karıştırıp, bile bile \"Hakk'ı\" gizlemeyin.", transliteration: "Ve la telbisul hakka bil batılı ve tektumul hakka ve entum ta'lemun."},
  {id: 50, sure_no: 43, ayet_no: 43, sure_adi: "Bakara", text: "وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَارْكَعُوا مَعَ الرَّاكِع۪ينَ", translation: "Salatı ikame edin, zekatı yapın. Ve ruku edenlerle birlikte ruku edin.", transliteration: "Ve ekimus salate ve atuz zekate verkeu mear rakiin."},
  {id: 51, sure_no: 44, ayet_no: 44, sure_adi: "Bakara", text: "اَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ اَنْفُسَكُمْ وَاَنْتُمْ تَتْلُونَ الْكِتَابَۜ اَفَلَا تَعْقِلُونَ", translation: "İnsanlardan birr olmalarını istiyorsunuz da kendinizi unutuyor musunuz? Oysa Kitap'ı da okuyorsunuz. Hala aklınızı kullanmayacak mısınız?", transliteration: "E te'murunen nase bil birri ve tensevne enfusekum ve entum tetlunel kitab e fe la ta'kılun."},
  {id: 52, sure_no: 45, ayet_no: 45, sure_adi: "Bakara", text: "وَاسْتَع۪ينُوا بِالصَّبْرِ وَالصَّلٰوةِۜ وَاِنَّهَا لَكَب۪يرَةٌ اِلَّا عَلَى الْخَاشِع۪ينَۙ", translation: "Sabır ve salatla yardım isteyin. Kuşkusuz bu içtenlikle itaat edenlerden başkasına ağır gelir.", transliteration: "Vesteinu bis sabri ves salat, ve inneha le kebiretun illa alel haşiin."},
  {id: 53, sure_no: 46, ayet_no: 46, sure_adi: "Bakara", text: "اَلَّذ۪ينَ يَظُنُّونَ اَنَّهُمْ مُلَاقُوا رَبِّهِمْ وَاَنَّهُمْ اِلَيْهِ رَاجِعُونَ۟", translation: "Onlar ki: Rabb'lerine kavuşacaklarını ve kesinlikle O'na döneceklerini bilirler.", transliteration: "Ellezine yezunnune ennehum mulaku rabbihim ve ennehum ileyhi raciun."},
  {id: 54, sure_no: 47, ayet_no: 47, sure_adi: "Bakara", text: "يَا بَن۪ٓي اِسْرَٓائ۪لَ اذْكُرُوا نِعْمَتِيَ الَّت۪ٓي اَنْعَمْتُ عَلَيْكُمْ وَاَنّ۪ي فَضَّلْتُكُمْ عَلَى الْعَالَم۪ينَ", translation: "Ey İsrailoğulları! Size verdiğim nimetimi ve size alemler üzerinde lütufta bulunduğumu hatırlayın.", transliteration: "Ya beni israilezkuru ni'metiyelleti en'amtu aleykum ve enni faddaltukum alel alemin."},
  {id: 55, sure_no: 48, ayet_no: 48, sure_adi: "Bakara", text: "وَاتَّقُوا يَوْماً لَا تَجْز۪ي نَفْسٌ عَنْ نَفْسٍ شَيْـٔاً وَلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَلَا هُمْ يُنْصَرُونَ", translation: "Öyle bir günden korunup sakının ki: Hiç kimse bir başkasına yardım edemez. Kimseden şefaat kabul edilmez. Kimseden fidye de alınmaz. Kimseye yardım da edilmez.", transliteration: "Vetteku yevmen la teczi nefsun an nefsin şey'en ve la yukbelu minha şefaatun ve la yu'hazu minha adlun ve la hum yunsarun."},
  {id: 56, sure_no: 49, ayet_no: 49, sure_adi: "Bakara", text: "وَاِذْ نَجَّيْنَاكُمْ مِنْ اٰلِ فِرْعَوْنَ يَسُومُونَكُمْ سُٓوءَ الْعَذَابِ يُذَبِّحُونَ اَبْنَٓاءَكُمْ وَيَسْتَحْيُونَ نِسَٓاءَكُمْۜ وَف۪ي ذٰلِكُمْ بَلَٓاءٌ مِنْ رَبِّكُمْ عَظ۪يمٌ", translation: "Hatırlayın! Sizi, kadınlarınızı sağ bırakıp, oğullarınızı boğazlayarak, azabın en kötüsüne uğratan Firavun'un adamlarından kurtarmıştık. Bunda, sizin için Rabb'inizden büyük bir sınav vardı.", transliteration: "Ve iz necceynakum min ali fir'avne yesumunekum suel azabi yuzebbihune ebnaekum ve yestahyune nisaekum ve fi zalikum belaun min rabbikum azim."},
  {id: 57, sure_no: 50, ayet_no: 50, sure_adi: "Bakara", text: "وَاِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَاَنْجَيْنَاكُمْ وَاَغْرَقْـنَٓا اٰلَ فِرْعَوْنَ وَاَنْتُمْ تَنْظُرُونَ", translation: "Hatırlayın! Denizi yararak sizi kurtarmış, gözlerinizin önünde Firavun taraftarlarını denizde boğmuştuk.", transliteration: "Ve iz farakna bikumul bahre fe enceynakum ve agrakna ale fir'avne ve entum tenzurun."},
  {id: 58, sure_no: 51, ayet_no: 51, sure_adi: "Bakara", text: "وَاِذْ وٰعَدْنَا مُوسٰٓى اَرْبَع۪ينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِه۪ وَاَنْتُمْ ظَالِمُونَ", translation: "Hatırlayın! Musa ile kırk gece için sözleşmiştik. Sonra siz, onun arkasından buzağı yaparak zalimleştiniz.", transliteration: "Ve iz vaadna musa erbaine leyleten summettehaztumul icle min ba'dihi ve entum zalimun."},
  {id: 59, sure_no: 52, ayet_no: 52, sure_adi: "Bakara", text: "ثُمَّ عَفَوْنَا عَنْكُمْ مِنْ بَعْدِ ذٰلِكَ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Sonra, bunun ardından, belki şükredersiniz diye sizi affettik.", transliteration: "Summe afevna ankum min ba'di zalike leallekum teşkurun."},
  {id: 60, sure_no: 53, ayet_no: 53, sure_adi: "Bakara", text: "وَاِذْ اٰتَيْنَا مُوسَى الْكِتَابَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُونَ", translation: "Doğru yolu bulasınız diye, Musa'ya Kitap'ı ve Furkan'ı verdik.", transliteration: "Ve iz ateyna musal kitabe vel furkane leallekum tehtedun."},
  {id: 61, sure_no: 54, ayet_no: 54, sure_adi: "Bakara", text: "وَاِذْ قَالَ مُوسٰى لِقَوْمِه۪ يَا قَوْمِ اِنَّكُمْ ظَلَمْتُمْ اَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوبُٓوا اِلٰى بَارِئِكُمْ فَاقْتُلُٓوا اَنْفُسَكُمْۜ  ذٰلِكُمْ خَيْرٌ لَكُمْ عِنْدَ بَارِئِكُمْۜ  فَتَابَ عَلَيْكُمْۜ  اِنَّهُ هُوَ التَّوَّابُ الرَّح۪يمُ", translation: "Hatırlayın! Musa, halkına: \"Ey halkım! Siz buzağıyı edinmekle kuşkusuz kendinize zulmettiniz. Hemen tevbe edin ve böylece nefislerinizi öldürün. Bu Bari'nizin yanında sizin için hayırlı olandır.\" demişti. Sonra da O, tevbenizi kabul etmişti. Kuşkusuz O, Tevbeleri Kabul Eden'dir, Rahmeti Kesintisiz'dir.", transliteration: "Ve iz kale musa li kavmihi ya kavmi innekum zalemtum enfusekum bittihazikumul icle fe tubu ila bariikum faktulu enfusekum zalikum hayrun lekum inde bariikum fe tabe aleykum innehu huvet tevvabur rahim."},
  {id: 62, sure_no: 55, ayet_no: 55, sure_adi: "Bakara", text: "وَاِذْ قُلْتُمْ يَا مُوسٰى لَنْ نُؤْمِنَ لَكَ حَتّٰى نَرَى اللّٰهَ جَهْرَةً فَاَخَذَتْكُمُ الصَّاعِقَةُ وَاَنْتُمْ تَنْظُرُونَ", translation: "Hani siz: \"Ey Musa! Allah'ı açıkça görmedikçe sana asla inanmayız.\" demiştiniz de o an, bakıp dururken, sizi yıldırım gürültüsü yakalamıştı.", transliteration: "Ve iz kultum ya musa len nu'mine leke hatta nerallahe cehreten fe ehazetkumus saikatu ve entum tenzurun."},
  {id: 63, sure_no: 56, ayet_no: 56, sure_adi: "Bakara", text: "ثُمَّ بَعَثْنَاكُمْ مِنْ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Sonra, şükredesiniz diye ölümünüzün ardından sizi dirilttik.", transliteration: "Summe beasnakum min ba'di mevtikum leallekum teşkurun."},
  {id: 64, sure_no: 57, ayet_no: 57, sure_adi: "Bakara", text: "وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰىۜ   كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْۜ وَمَا ظَلَمُونَا وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Ve bulutları üzerinize gölge yaptık. Size menn ve bıldırcın bağışladık. \"Size rızık olarak verdiklerimizin temiz olanlarından yiyin.\" dedik. Onlar, Bize zulmetmediler, fakat kendilerine zulmediyorlardı.", transliteration: "Ve zallelna aleykumul gamame ve enzelna aleykumul menne ves selva kulu min tayyibati ma razaknakum ve ma zalemuna ve lakin kanu enfusehum yazlimun."},
  {id: 65, sure_no: 58, ayet_no: 58, sure_adi: "Bakara", text: "وَاِذْ قُلْنَا ادْخُلُوا هٰذِهِ الْقَرْيَةَ فَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ رَغَداً وَادْخُلُوا الْبَابَ سُجَّداً وَقُولُوا حِطَّةٌ نَغْفِرْ لَكُمْ خَطَايَاكُمْۜ وَسَنَز۪يدُ الْمُحْسِن۪ينَ", translation: "Hani: \"Şu şehre girin, orada dilediğinizden bol bol yiyin. O kapıdan secde ederek girin. Ve \"Bizi bağışla.\" deyin ki Biz de yanlışlarınızı bağışlayalım. İyilik yapanlara daha fazlasını vereceğiz.\" demiştik.", transliteration: "Ve iz kulnadhulu hazihil karyete fe kulu minha haysu şi'tum ragaden vedhulul babe succeden ve kulu hıttatun nagfir lekum hatayakum ve senezidul muhsinin."},
  {id: 66, sure_no: 59, ayet_no: 59, sure_adi: "Bakara", text: "فَبَدَّلَ الَّذ۪ينَ ظَلَمُوا قَوْلاً غَيْرَ الَّذ۪ي ق۪يلَ لَهُمْ فَاَنْزَلْنَا عَلَى الَّذ۪ينَ ظَلَمُوا رِجْزاً مِنَ السَّمَٓاءِ بِمَا كَانُوا يَفْسُقُونَ۟", translation: "Fakat zalimler, sözü, söylenenden başka bir şekle soktular. O zalimlere, doğru yoldan sapmalarına karşılık gökten bir azap indirdik.", transliteration: "Fe beddelellezine zalemu kavlen gayrellezi kile lehum fe enzelna alellezine zalemu riczen mines semai bima kanu yefsukun."},
  {id: 67, sure_no: 60, ayet_no: 60, sure_adi: "Bakara", text: "وَاِذِ اسْتَسْقٰى مُوسٰى لِقَوْمِه۪ فَقُلْنَا اضْرِبْ بِعَصَاكَ الْحَجَرَۜ فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْناًۜ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَشْرَبَهُمْۜ كُلُوا وَاشْرَبُوا مِنْ رِزْقِ اللّٰهِ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ", translation: "Hani! Musa, halkı için su istemişti. Biz de demiştik ki: \"Asanla kayaya vur.\" Bunun üzerine kayadan on iki göz su fışkırmıştı. Her grup hangi kaynaktan içeceğini bilmişti. Allah'ın verdiği rızıktan yiyin, için fakat asilik yaparak yeryüzünde bozgunculuk çıkarmayın.", transliteration: "Ve izisteska musa li kavmihi fe kulnadrib bi asakel hacer fenfeceret minhusneta aşrete ayna, kad alime kullu unasin meşrebehum kulu veşrebu min rızkıllahi ve la ta'sev fil ardı mufsidin."},
  {id: 68, sure_no: 61, ayet_no: 61, sure_adi: "Bakara", text: "وَاِذْ قُلْتُمْ يَا مُوسٰى لَنْ نَصْبِرَ عَلٰى طَعَامٍ وَاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْبِتُ الْاَرْضُ مِنْ بَقْلِهَا وَقِثَّٓائِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۜ قَالَ اَتَسْتَبْدِلُونَ الَّذ۪ي هُوَ اَدْنٰى بِالَّذ۪ي هُوَ خَيْرٌۜ اِهْبِطُوا مِصْراً فَاِنَّ لَكُمْ مَا سَاَلْتُمْۜ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَٓاؤُ۫ بِغَضَبٍ مِنَ اللّٰهِۜ ذٰلِكَ بِاَنَّهُمْ كَانُوا يَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَيَقْتُلُونَ النَّبِيّ۪نَ بِغَيْرِ الْحَقِّۜ ذٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ۟", translation: "Hani! Musa'ya: \"Ey Musa, asla tek çeşit yiyeceğe dayanamayız. Rabb'inden bizim için yerden çıkan ürünlerden; sebzesinden, acurundan, sarımsağından, mercimeğinden, soğanından çıkarmasını iste.\" demiştiniz. Musa da: \"Daha değerli olanı daha değersiz olanla değiştirmek mi istiyorsunuz? O halde şehre inin; sizin istedikleriniz orada var.\" dedi. Böylece, onların üzerine alçaklık ve yoksulluk damgası vuruldu. Ve Allah'ın gazabına uğradılar. Bu, Allah'ın ayetlerine inanmadıklarından ve nebilerini haksız yere öldürmelerindendi. Bütün bunlar, onların asileşip haddi aşmalarındandır.", transliteration: "Ve iz kultum ya musa len nasbira ala taamin vahidin fed'u lena rabbeke yuhric lena mimma tunbitulardu min bakliha ve kıssaiha ve fumiha ve adesiha ve basaliha, kale e testebdilunellezi huve edna billezi huve hayr, ihbitu mısran fe inne lekum ma seeltum ve duribet aleyhimuz zilletu vel meskenetu ve bau bi gadabin minallah, zalike bi ennehum kanu yekfurune bi ayatillahi ve yaktulunen nebiyyine bi gayril hak, zalike bi ma asav ve kanu ya'tedun."},
  {id: 69, sure_no: 62, ayet_no: 62, sure_adi: "Bakara", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَادُوا وَالنَّصَارٰى وَالصَّابِـ۪ٔينَ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحاً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "İman Edenler, Yahudiler, Nesara ve Sabiin; kim Allah'a ve ahiret gününe iman edip salihatı yaptı ise ödüllerini Rabb'leri verecektir. Ve onlar için korku yoktur ve onlar üzülmeyeceklerdir.", transliteration: "İnnellezine amenu vellezine hadu ven nasara ves sabiine men amene billahi vel yevmil ahiri ve amile salihan fe lehum ecruhum inde rabbihim, ve la havfun aleyhim ve la hum yahzenun."},
  {id: 70, sure_no: 63, ayet_no: 63, sure_adi: "Bakara", text: "وَاِذْ اَخَذْنَا م۪يثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَۜ خُذُوا مَٓا اٰتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا ف۪يهِ لَعَلَّكُمْ تَتَّقُونَ", translation: "Hani bir zamanlar, takva sahibi olmanız için, size verdiğimiz şeylere kuvvetle sarılıp kendinize mal etmek ve içindekilerini sürekli aklınızda tutmanız konusunda sizden söz almıştık. Ve Tur'u üzerinize kaldırmıştık.", transliteration: "Ve iz ehazna misakakum ve refa'na fevkakumut tur huzu ma ateynakum bi kuvvetin vezkuru ma fihi leallekum tettekun."},
  {id: 71, sure_no: 64, ayet_no: 64, sure_adi: "Bakara", text: "ثُمَّ تَوَلَّيْتُمْ مِنْ بَعْدِ ذٰلِكَۚ فَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ لَكُنْتُمْ مِنَ الْخَاسِر۪ينَ", translation: "Yine de yüz çevirdiniz. Eğer Allah'ın iyilikseverliği ve bağışlayıcılığı olmasaydı elbette kaybedenlerden olurdunuz.", transliteration: "Summe tevelleytum min ba'di zalik, fe lev la fadlullahi aleykum ve rahmetuhu le kuntum minel hasirin."},
  {id: 72, sure_no: 65, ayet_no: 65, sure_adi: "Bakara", text: "وَلَقَدْ عَلِمْتُمُ الَّذ۪ينَ اعْتَدَوْا مِنْكُمْ فِي السَّبْتِ فَقُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِـ۪ٔينَۚ", translation: "Elbette siz, Cumartesi yasağını çiğnemekle hadlerini aşanları biliyorsunuz. Bu nedenle onlara, \"Düşkün maymunlar olun.\" dedik.", transliteration: "Ve lekad alimtumullezine'tedev minkum fis sebti fe kulna lehum kunu kıradeten hasiin."},
  {id: 73, sure_no: 66, ayet_no: 66, sure_adi: "Bakara", text: "فَجَعَلْنَاهَا نَكَالاً لِمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِلْمُتَّق۪ينَ", translation: "Biz bunu, onlara ve onlardan sonra gelecek kuşaklara ibret; muttakilere de bir öğüt olarak yaptık.", transliteration: "Fe cealnaha nekalen li ma beyne yedeyha ve ma halfeha ve mev'ızaten lil muttakin."},
  {id: 74, sure_no: 67, ayet_no: 67, sure_adi: "Bakara", text: "وَاِذْ قَالَ مُوسٰى لِقَوْمِه۪ٓ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تَذْبَحُوا بَقَرَةًۜ قَالُٓوا اَتَتَّخِذُنَا هُزُواًۜ قَالَ اَعُوذُ بِاللّٰهِ اَنْ اَكُونَ مِنَ الْجَاهِل۪ينَ", translation: "Hani, Musa halkına: \"Allah, sizden bir sığır kesmenizi istiyor.\" demişti. Onlar da: \"Sen bizimle alay mı ediyorsun?\" dediler. Musa; \"Cahillerden olmaktan, Allah'a sığınırım.\" dedi.", transliteration: "Ve iz kale musa li kavmihi innallahe ye'murukum en tezbehu bakarah, kalu e tettehızuna huzuva, kale euzu billahi en ekune minel cahilin."},
  {id: 75, sure_no: 68, ayet_no: 68, sure_adi: "Bakara", text: "قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا هِيَۜ قَالَ اِنَّهُ يَقُولُ اِنَّهَا بَقَرَةٌ لَا فَارِضٌ وَلَا بِكْرٌۜ عَوَانٌ بَيْنَ ذٰلِكَۜ فَافْعَلُوا مَا تُؤْمَرُونَ", translation: "Onlar: \"Bizim için Rabb'ine söyle, onun nasıl bir sığır olduğunu açıklasın.\" dediler. Musa, \"Allah, onun ne tam yaşlı ne de yavru; ikisinin arasında bir yaşta olduğunu söylüyor; öyleyse sizden isteneni yapın.\" dedi.", transliteration: "Kalud'u lena rabbeke yubeyyin lena ma hiy, kale innehu yekulu inneha bakaratun la faridun ve la bikr, avanun beyne zalik fef'alu ma tu'merun."},
  {id: 76, sure_no: 69, ayet_no: 69, sure_adi: "Bakara", text: "قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا لَوْنُهَاۜ قَالَ اِنَّهُ يَقُولُ اِنَّهَا بَقَرَةٌ صَفْرَٓاءُۙ فَاقِعٌ لَوْنُهَا تَسُرُّ النَّاظِر۪ينَ", translation: "Onlar: \"Bizim için Rabb'ine söyle, onun rengi nedir; bize bildirsin.\" dediler. Musa: \"Allah; onun parlak sarı renkte, bakanlara keyif veren bir sığır olduğunu söylüyor.\" dedi.", transliteration: "Kalud'u lena rabbeke yubeyyin lena ma levnuha, kale innehu yekulu inneha bakaratun safrau, fakiun levnuha tesurrun nazırin."},
  {id: 77, sure_no: 70, ayet_no: 70, sure_adi: "Bakara", text: "قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا هِيَۙ اِنَّ الْبَقَرَ تَشَابَهَ عَلَيْنَاۜ وَاِنَّٓا اِنْ شَٓاءَ اللّٰهُ لَمُهْتَدُونَ", translation: "Yine onlar: \"Bizim için Rabb'ine sor; onun niteliğini açıklasın. Zira bizce sığırların hepsi birbirine benziyorlar; eğer Allah dilerse biz doğru olanı bulmuş oluruz.\" dediler.", transliteration: "Kalud'u lena rabbeke yubeyyin lena ma hiye, innel bakara teşabehe aleyna, ve inna in şaallahu le muhtedun."},
  {id: 78, sure_no: 71, ayet_no: 71, sure_adi: "Bakara", text: "قَالَ اِنَّهُ يَقُولُ اِنَّهَا بَقَرَةٌ لَا ذَلُولٌ تُث۪يرُ الْاَرْضَ وَلَا تَسْقِي الْحَرْثَۚ مُسَلَّمَةٌ لَا شِيَةَ ف۪يهَاۜ قَالُوا الْـٰٔنَ جِئْتَ بِالْحَقِّۜ فَذَبَحُوهَا وَمَا كَادُوا يَفْعَلُونَ۟", translation: "Musa: \"Allah, onun ekin sulayarak, çifte koşularak yıpranmamış; alacası olmayan, kusursuz bir sığır olduğunu söylüyor.\" dedi. Onlar: \"İşte şimdi gerçeği bildirdin.\" dediler. Ve nihayet sığırı boğazladılar. Neredeyse bunu yapmayacaklardı.", transliteration: "Kale innehu yekulu inneha bakaratun la zelulun tusirul arda ve la teskıl hars, musellemetun la şiyete fiha kalul'ane ci'te bil hakk, fe zebehuha ve ma kadu yef'alun."},
  {id: 79, sure_no: 72, ayet_no: 72, sure_adi: "Bakara", text: "وَاِذْ قَتَلْتُمْ نَفْساً فَادّٰرَءْتُمْ ف۪يهَاۜ وَاللّٰهُ مُخْرِجٌ مَا كُنْتُمْ تَكْتُمُونَۚ", translation: "Hani! Siz, bir kimseyi öldürmüştünüz de birbirinizle atışmıştınız. Oysa Allah, gizlediğiniz şeyi ortaya çıkarandır.", transliteration: "Ve iz kateltum nefsen feddare'tum fiha vallahu muhricun ma kuntum tektumun."},
  {id: 80, sure_no: 73, ayet_no: 73, sure_adi: "Bakara", text: "فَقُلْنَا اضْرِبُوهُ بِبَعْضِهَاۜ   كَذٰلِكَ يُحْـيِ اللّٰهُ الْمَوْتٰى وَيُر۪يكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَعْقِلُونَ", translation: "\"Onun bir kısmı ile ona vurun\" dedik. İşte Allah, böylece ölüleri dirilterek ayetlerini size gösterir. Umulur ki gerçeği kavrarsınız.", transliteration: "Fe kulnadribuhu bi ba'dıha kezalike yuhyillahul mevta ve yurikum ayatihi leallekum ta'kılun."},
  {id: 81, sure_no: 74, ayet_no: 74, sure_adi: "Bakara", text: "ثُمَّ قَسَتْ قُلُوبُكُمْ مِنْ بَعْدِ ذٰلِكَ فَهِيَ كَالْحِجَارَةِ اَوْ اَشَدُّ قَسْوَةًۜ وَاِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْاَنْهَارُۜ وَاِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَٓاءُۜ وَاِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللّٰهِۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", translation: "Sonra kalpleriniz yine katılaştı; kaya gibi, hatta kayadan da katı. Zira öyle kayalar var ki içinden nehirler fışkırır, yine öyle kayalar vardır ki yarılıp bağrından su çıkar. Yine öyleleri vardır ki Allah'a duyduğu içtenlikli saygıdan harekete geçerler. Allah, yaptıklarınızdan habersiz değildir.", transliteration: "Summe kaset kulubukum min ba'di zalike fe hiye kel hıcareti ev eşeddu kasveh, ve inne minel hıcareti lema yetefecceru minhul enhar, ve inne minha lema yeşşakkaku fe yahrucu minhul mau, ve inne minha lemayehbitu min haşyetillah, ve mallahu bi gafilin amma ta'melun."},
  {id: 82, sure_no: 75, ayet_no: 75, sure_adi: "Bakara", text: "اَفَتَطْمَعُونَ اَنْ يُؤْمِنُوا لَكُمْ وَقَدْ كَانَ فَر۪يقٌ مِنْهُمْ يَسْمَعُونَ كَلَامَ اللّٰهِ ثُمَّ يُحَرِّفُونَهُ مِنْ بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَ", translation: "Şimdi onların size inanacaklarını mı umuyorsunuz? Onlardan bir grup, Allah'ın kelamını dinleyip iyice anladıktan sonra, onu, bile bile çarpıtırlar.", transliteration: "E fe tatmeune en yu'minu lekum ve kad kane ferikun minhum yesmeune kelamallahi summe yuharrifunehu min ba'di ma akaluhu ve hum ya'lemun."},
  {id: 83, sure_no: 76, ayet_no: 76, sure_adi: "Bakara", text: "وَاِذَا لَقُوا الَّذ۪ينَ اٰمَنُوا قَالُٓوا اٰمَنَّاۚ وَاِذَا خَلَا بَعْضُهُمْ اِلٰى بَعْضٍ قَالُٓوا اَتُحَدِّثُونَهُمْ بِمَا فَتَحَ اللّٰهُ عَلَيْكُمْ لِيُحَٓاجُّوكُمْ بِه۪ عِنْدَ رَبِّكُمْۜ اَفَلَا تَعْقِلُونَ", translation: "Onlar, İman Edenlerle karşılaştıkları zaman, \"Biz de iman ettik.\" derler. Baş başa kaldıklarında: \"Rabb'inizin size açıkladığını, size karşı delil olarak kullansınlar diye mi onlara söylüyorsunuz! Neden aklınızı kullanmıyorsunuz?\" derler.", transliteration: "Ve iza lekullezine amenu kalu amenna, ve iza hala ba'duhum ila ba'din kalu e tuhaddisunehum bi ma fetehallahu aleykum li yuhaccukum bihi inde rabbikum e fe la ta'kılun."},
  {id: 84, sure_no: 77, ayet_no: 77, sure_adi: "Bakara", text: "اَوَلَا يَعْلَمُونَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ", translation: "Allah'ın, onların gizlediklerini de açıkladıklarını da bildiğini bilmiyorlar mı?", transliteration: "E ve la ya'lemune ennallahe ya'lemu ma yusirrune ve ma yu'linun."},
  {id: 85, sure_no: 78, ayet_no: 78, sure_adi: "Bakara", text: "وَمِنْهُمْ اُمِّيُّونَ لَا يَعْلَمُونَ الْكِتَابَ اِلَّٓا اَمَانِيَّ وَاِنْ هُمْ اِلَّا يَظُنُّونَ", translation: "İçlerinde ummiler vardır. Kitap'ı bilmezler. Kuruntularından başka bir şey bilmezler. Ancak zanda bulunuyorlar.", transliteration: "Ve minhum ummiyyune la ya'lemunel kitabe illa emaniyye ve in hum illa yezunnun."},
  {id: 86, sure_no: 79, ayet_no: 79, sure_adi: "Bakara", text: "فَوَيْلٌ لِلَّذ۪ينَ يَكْتُبُونَ الْكِتَابَ بِاَيْد۪يهِمْ ثُمَّ يَقُولُونَ هٰذَا مِنْ عِنْدِ اللّٰهِ لِيَشْتَرُوا بِه۪ ثَمَناً قَل۪يلاًۜ فَوَيْلٌ لَهُمْ مِمَّا كَتَبَتْ اَيْد۪يهِمْ وَوَيْلٌ لَهُمْ مِمَّا يَكْسِبُونَ", translation: "Yazıklar olsun o kimselere ki: Kitabı elleriyle yazıyorlar, sonra da az bir kazanç elde etmek için \"Bu Allah'ın katındandır.\" diyorlar. Yazıklar olsun onlara, elleriyle yazdıklarından dolayı. Yazıklar olsun onlara, kazandıklarından dolayı!", transliteration: "Fe veylun lillezine yektubunel kitabe bi eydihim summe yekulune haza min indillahi li yeşteru bihi semenen kalila, fe veylun lehum mimma ketebet eydihim ve veylun lehum mimma yeksibun."},
  {id: 87, sure_no: 80, ayet_no: 80, sure_adi: "Bakara", text: "وَقَالُوا لَنْ تَمَسَّنَا النَّارُ اِلَّٓا اَيَّاماً مَعْدُودَةًۜ قُلْ اَتَّخَذْتُمْ عِنْدَ اللّٰهِ عَهْداً فَلَنْ يُخْلِفَ اللّٰهُ عَهْدَهُٓ اَمْ تَقُولُونَ عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "Sayılı günlerin dışında ateş bize dokunmaz.\" dediler. De ki: \"Allah'tan buna dair bir söz mü aldınız? Eğer böyle ise Allah, kesinlikle verdiği sözden dönmez. Yoksa Allah'a karşı bilmediğiniz şeyi mi söylüyorsunuz?\"", transliteration: "Ve kalu len temessenen naru illa eyyamen ma'dudeh, kul ettehaztum indallahi ahden fe len yuhlifallahu ahdehu em tekulune alallahi ma la ta'lemun."},
  {id: 88, sure_no: 81, ayet_no: 81, sure_adi: "Bakara", text: "بَلٰى مَنْ كَسَبَ سَيِّئَةً وَاَحَاطَتْ بِه۪ خَط۪ٓيـَٔتُهُ فَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "Hayır! Kim bir kötülük yapar da yanlışları kendisini kuşatırsa, onlar Cehennem ehlidir ve onlar, orada kalıcıdırlar.", transliteration: "Bela men kesebe seyyieten ve ehatat bihi hatietuhu fe ulaike ashabun nar, hum fiha halidun."},
  {id: 89, sure_no: 82, ayet_no: 82, sure_adi: "Bakara", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ۟", translation: "İman edip, salihatı yapanlar Cennet ehlidirler. Ve onlar orada kalıcıdırlar.", transliteration: "Vellezine amenu ve amilus salihati ulaike ashabul cenneh, hum fiha halidun."},
  {id: 90, sure_no: 83, ayet_no: 83, sure_adi: "Bakara", text: "وَاِذْ اَخَذْنَا م۪يثَاقَ بَن۪ٓي اِسْرَٓائ۪لَ لَا تَعْبُدُونَ اِلَّا اللّٰهَ وَبِالْوَالِدَيْنِ اِحْسَاناً وَذِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَقُولُوا لِلنَّاسِ حُسْناً وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۜ ثُمَّ تَوَلَّيْتُمْ اِلَّا قَل۪يلاً مِنْكُمْ وَاَنْتُمْ مُعْرِضُونَ", translation: "Hani! Bir zaman İsrailoğullarından, \"Allah'tan başkasına kulluk etmeyin; anne ve babaya, öksüzlere, düşkünlere iyilik yapın; insanlara iyi söz söyleyin, \"Salatı ikame edin, zekatı yapın.\" diye kesin söz almıştık. Sonra, içinizden pek azınız hariç sözünüzden döndünüz. Ve sizler, döneklik yapanlarsınız.", transliteration: "Ve iz ehazna misaka beni israile la ta'budune illallahe ve bil valideyni ihsanen ve zil kurbavel yetama vel mesakini ve kulu lin nasi husnen ve ekimus salate ve atuz zekat, summe tevelleytum illa kalilen minkum ve entum mu'ridun."},
  {id: 91, sure_no: 84, ayet_no: 84, sure_adi: "Bakara", text: "وَاِذْ اَخَذْنَا م۪يثَاقَكُمْ لَا تَسْفِكُونَ دِمَٓاءَكُمْ وَلَا تُخْرِجُونَ اَنْفُسَكُمْ مِنْ دِيَارِكُمْ ثُمَّ اَقْرَرْتُمْ وَاَنْتُمْ تَشْهَدُونَ", translation: "Hani! Bir zamanlar sizden, birbirinizin kanlarını dökmeyeceğinize ve birbirinizi yurtlarınızdan çıkarmayacağınıza dair kesin söz almıştık. Bu sözümüzü kabul ettiğinizin tanıkları sizlersiniz.", transliteration: "Ve iz ehazna misakakum la tesfikune dimaekum ve la tuhricune enfusekum min diyarikum summe ekrartum ve entum teşhedun."},
  {id: 92, sure_no: 85, ayet_no: 85, sure_adi: "Bakara", text: "ثُمَّ اَنْتُمْ هٰٓؤُ۬لَٓاءِ تَقْتُلُونَ اَنْفُسَكُمْ وَتُخْرِجُونَ فَر۪يقاً مِنْكُمْ مِنْ دِيَارِهِمْۘ تَظَاهَرُونَ عَلَيْهِمْ بِالْاِثْمِ وَالْعُدْوَانِۜ وَاِنْ يَأْتُوكُمْ اُسَارٰى تُفَادُوهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ اِخْرَاجُهُمْۜ اَفَتُؤْمِنُونَ بِبَعْضِ الْكِتَابِ وَتَكْفُرُونَ بِبَعْضٍۚ فَمَا جَزَٓاءُ مَنْ يَفْعَلُ ذٰلِكَ مِنْكُمْ اِلَّا خِزْيٌ فِي الْحَيٰوةِ الدُّنْيَاۚ وَيَوْمَ الْقِيٰمَةِ يُرَدُّونَ اِلٰٓى اَشَدِّ الْعَذَابِۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", translation: "Sonra siz o kimselersiniz ki; birbirinizi öldüren, bir kısmınızı yurtlarından çıkarıp, onlara karşı günahta ve düşmanlıkta iş birliği yapanlarsınız. Bir de esir olarak size gelirlerse, onlarla fidyeleşiyorsunuz. Oysa onları yurtlarından çıkarmak, üzerinize haram kılınmıştı. Yoksa böyle yapmakla Kitap'ın bir kısmına inanıp bir kısmını inkar mı ediyorsunuz? Sizden böyle yapanların cezası dünya hayatında aşağılanma, kıyamet gününde de en şiddetli azaba uğramaktır. Zira Allah, yaptıklarınızdan habersiz değildir.", transliteration: "Summe entum haulai taktulune enfusekum ve tuhricune ferikan minkummin diyarihim, tezaharune aleyhim bil ismi vel udvan, ve in ye'tukum usara tufaduhum ve huve muharremun aleykum ihracuhum e fe tu'minune bi ba'dil kitabive tekfurune bi ba'd, fe ma cezau men yef'alu zalike minkum illa hızyun fil hayatid dunya, ve yevmel kıyameti yureddune ila eşeddil azab, ve mallahu bi gafilin amma ta'melun."},
  {id: 93, sure_no: 86, ayet_no: 86, sure_adi: "Bakara", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ اشْتَرَوُا الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِۘ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْصَرُونَ۟", translation: "Onlar, dünya hayatına karşılık ahiret hayatını satan kimselerdir. Bundan dolayı onlardan azap hafifletilmeyecek ve onlara yardım da edilmeyecektir.", transliteration: "Ulaikellezineşteravul hayated dunya bil ahireti, fe la yuhaffefu anhumul azabu ve la hum yunsarun."},
  {id: 94, sure_no: 87, ayet_no: 87, sure_adi: "Bakara", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِنْ بَعْدِه۪ بِالرُّسُلِ وَاٰتَيْنَا ع۪يسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَاَيَّدْنَاهُ بِرُوحِ الْقُدُسِۜ اَفَكُلَّمَا جَٓاءَكُمْ رَسُولٌ بِمَا لَا تَهْوٰٓى اَنْفُسُكُمُ اسْتَكْبَرْتُمْۚ فَفَر۪يقاً كَذَّبْتُمْۘ وَفَر۪يقاً تَقْتُلُونَ", translation: "Ant olsun ki Musa'ya Kitap'ı verdik. Ardı sıra Resuller gönderdik. Meryem Oğlu İsa'ya da beyyinatı verdik ve onu Kudus'un Ruhu ile destekledik. Ne zaman bir elçi hoşunuza gitmeyen bir şey getirdiyse, büyüklük taslayarak kimini yalanlayıp, kimini de öldürmediniz mi?", transliteration: "Ve lekad ateyna musal kitabe ve kaffeyna min ba'dihi bir rusuli ve ateyna isabne meryemel beyyinati ve eyyednahu bi ruhil kudus, e fe kullema caekum resulun bima la tehva enfusukumustekbertum, fe ferikan kezzebtum ve ferikan taktulun."},
  {id: 95, sure_no: 88, ayet_no: 88, sure_adi: "Bakara", text: "وَقَالُوا قُلُوبُنَا غُلْفٌۜ بَلْ لَعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَقَل۪يلاً مَا يُؤْمِنُونَ", translation: "ve onlar, \"Kalplerimiz örtülüdür.\" dediler. Hayır, öyle değil, Allah; onları, kafirlik etmelerinden dolayı lanetlemiştir. Bu yüzden çok azı iman eder.", transliteration: "Ve kalu kulubuna gulf, bel leanehumullahu bi kufrihim fe kalilen ma yu'minun."},
  {id: 96, sure_no: 89, ayet_no: 89, sure_adi: "Bakara", text: "وَلَمَّا جَٓاءَهُمْ كِتَابٌ مِنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِمَا مَعَهُمْۙ وَكَانُوا مِنْ قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذ۪ينَ كَفَرُواۚ فَلَمَّا جَٓاءَهُمْ مَا عَرَفُوا كَفَرُوا بِه۪ۘ فَلَعْنَةُ اللّٰهِ عَلَى الْكَافِر۪ينَ", translation: "Onlara, Allah katından yanlarındakini tasdik edici bir Kitap gelince; daha önce Allah'tan Kafirlere karşı üstünlük kazanmak için böyle bir şey istedikleri halde, onlara bildikleri şey gelince bu kez onu inkar ettiler. Allah'ın laneti, Kafirlerin üzerinedir.", transliteration: "Ve lemma caehum kitabun min indillahi musaddikun lima meahum, ve kanu min kablu yesteftihune alellezine keferu, fe lemma caehum ma arafu keferu bihi, fe la'netullahi alel kafirin."},
  {id: 97, sure_no: 90, ayet_no: 90, sure_adi: "Bakara", text: "بِئْسَمَا اشْتَرَوْا بِه۪ٓ اَنْفُسَهُمْ اَنْ يَكْفُرُوا بِمَٓا اَنْزَلَ اللّٰهُ بَغْياً اَنْ يُنَزِّلَ اللّٰهُ مِنْ فَضْلِه۪ عَلٰى مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۚ فَبَٓاؤُ۫ بِغَضَبٍ عَلٰى غَضَبٍۜ وَلِلْكَافِر۪ينَ عَذَابٌ مُه۪ينٌ", translation: "Allah'ın, dilediği kimseye kendi lütfundan indirmesini çekemeyerek; Allah'ın indirdiğini küfretmekle kendilerini ne kötü bir şeye karşılık sattılar. Bundan dolayı art arda gelen gazabı hak ettiler. Kafirler için alçaltıcı bir azap vardır.", transliteration: "Bi'semeşterav bihi enfusehum en yekfuru bi ma enzelallahu bagyen en yunezzilallahu min fadlihi ala men yeşau min ibadih, fe bau bi gadabin ala gadab, ve lil kafirine azabun muhin."},
  {id: 98, sure_no: 91, ayet_no: 91, sure_adi: "Bakara", text: "وَاِذَا ق۪يلَ لَهُمْ اٰمِنُوا بِمَٓا اَنْزَلَ اللّٰهُ قَالُوا نُؤْمِنُ بِمَٓا اُنْزِلَ عَلَيْنَا وَيَكْفُرُونَ بِمَا وَرَٓاءَهُ وَهُوَ الْحَقُّ مُصَدِّقاً لِمَا مَعَهُمْۜ قُلْ فَلِمَ تَقْتُلُونَ اَنْبِيَٓاءَ اللّٰهِ مِنْ قَبْلُ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Onlara, \"Allah'ın indirdiklerine iman edin.\" denildiğinde, \"Biz, ancak bize indirilene iman ederiz.\" dediler. Ve ondan başkasını kabul etmezler. Oysa O, yanlarındakini tasdik eden, hak bir kitaptır. Onlara de ki: \"Madem iman ediyordunuz, ne diye daha önce Allah'ın Nebilerini öldürüyordunuz?\"", transliteration: "Ve iza kile lehum aminu bi ma enzelallahu kalu nu'minu bi ma unzile aleyna ve yekfurune bi ma veraehu ve huvel hakku musaddikan lima meahum kul fe lime taktulune enbiyaallahi min kablu in kuntum mu'minin."},
  {id: 99, sure_no: 92, ayet_no: 92, sure_adi: "Bakara", text: "وَلَقَدْ جَٓاءَكُمْ مُوسٰى بِالْبَيِّنَاتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِه۪ وَاَنْتُمْ ظَالِمُونَ", translation: "Gerçekten Musa size beyyinat ile geldi. Sonra siz onun arkasından buzağı figürünü yaptınız. İşte siz, o zalimlersiniz.", transliteration: "Ve lekad caekum musa bil beyyinati summettehaztumul icle min ba'dihi ve entum zalimun."},
  {id: 100, sure_no: 93, ayet_no: 93, sure_adi: "Bakara", text: "وَاِذْ اَخَذْنَا م۪يثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَۜ خُذُوا مَٓا اٰتَيْنَاكُمْ بِقُوَّةٍ وَاسْمَعُواۜ قَالُوا سَمِعْنَا وَعَصَيْنَا وَاُشْرِبُوا ف۪ي قُلُوبِهِمُ الْعِجْلَ بِكُفْرِهِمْۜ  قُلْ بِئْسَمَا يَأْمُرُكُمْ بِه۪ٓ ا۪يمَانُكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Hani sizden, \"Size verdiğimizi kuvvetlice alın ve dinleyin.\" diye kesin söz almış ve Tur'u üzerinize yükseltmiştik. Demişlerdi ki: \"Dinledik; ama itaat etmiyoruz.\" Küfürleri yüzünden kalplerine buzağı sevgisi içirildi. De ki: \"Eğer gerçekten inanıyorsanız, inancınız sizden ne kötü şey istiyor!\"", transliteration: "Ve iz ehazna misakakum ve refa'na fevkakumut tur, huzu ma ateynakum bi kuvvetin vesmeu kalu semi'na ve aseyna ve uşribu fi kulubihimul icle bi kufrihim kul bi'se ma ye'murukum bihi imanukum in kuntum mu'minin."},
  {id: 101, sure_no: 94, ayet_no: 94, sure_adi: "Bakara", text: "قُلْ اِنْ كَانَتْ لَكُمُ الدَّارُ الْاٰخِرَةُ عِنْدَ اللّٰهِ خَالِصَةً مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "De ki: \"Eğer Allah katında; ahiret yurdunun, diğer insanların değil de yalnızca size ait olduğunu iddia eden sözünüzde samimi iseniz, o zaman ölümü istesenize!\"", transliteration: "Kul in kanet lekumud darul ahiretu indallahi halisaten min dunin nasi fe temennevul mevte in kuntum sadikin."},
  {id: 102, sure_no: 95, ayet_no: 95, sure_adi: "Bakara", text: "وَلَنْ يَتَمَنَّوْهُ اَبَداً بِمَا قَدَّمَتْ اَيْد۪يهِمْۜ وَاللّٰهُ عَل۪يمٌ بِالظَّالِم۪ينَ", translation: "Ama elleriyle yaptıkları şeylerden dolayı, ölümü asla istemezler. Allah, zalimleri en iyi bilendir.", transliteration: "Ve len yetemennevhu ebeden bima kaddemet eydihim vallahu alimun biz zalimin."},
  {id: 103, sure_no: 96, ayet_no: 96, sure_adi: "Bakara", text: "وَلَتَجِدَنَّهُمْ اَحْرَصَ النَّاسِ عَلٰى حَيٰوةٍۚ وَمِنَ الَّذ۪ينَ اَشْرَكُوا يَوَدُّ اَحَدُهُمْ لَوْ يُعَمَّرُ اَلْفَ سَنَةٍۚ وَمَا هُوَ بِمُزَحْزِحِه۪ مِنَ الْعَذَابِ اَنْ يُعَمَّرَۜ وَاللّٰهُ بَص۪يرٌ بِمَا يَعْمَلُونَ۟", translation: "Ve sen, onları, yaşamaya karşı insanların en ihtiraslısı, hatta Allah'a ortak koşanlardan bile daha çok ihtiraslı bulacaksın. Her biri bin yıl yaşamak ister. Oysaki uzun ömürlü olmak, böyle birini azaptan kurtarmaz. Zira Allah, yaptıkları her şeyi görmektedir.", transliteration: "Ve le tecidennehum ahrasan nasi ala hayatin, ve minellezine eşraku yeveddu ehaduhum lev yuammeru elfe seneh, ve ma huve bi muzahzihıhi minel azabi en yuammer, vallahu basirun bima ya'melun."},
  {id: 104, sure_no: 97, ayet_no: 97, sure_adi: "Bakara", text: "قُلْ مَنْ كَانَ عَدُواًّ لِجِبْر۪يلَ فَاِنَّهُ نَزَّلَهُ عَلٰى قَلْبِكَ بِاِذْنِ اللّٰهِ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ وَهُدًى وَبُشْرٰى لِلْمُؤْمِن۪ينَ", translation: "De ki kim Cibril'e düşmansa, Bilsin ki O, onu iki eli arasındakileri tasdik edici olarak Allah'ın izni ile senin kalbine çokça indirdi. Mü'minler için bir hidayet ve müjde olarak.", transliteration: "Kul men kane aduvven li cibrile fe innehu nezzelehu ala kalbike bi iznillahi musaddikan lima beyne yedeyhi ve huden ve buşra lil mu'minin."},
  {id: 105, sure_no: 98, ayet_no: 98, sure_adi: "Bakara", text: "مَنْ كَانَ عَدُواًّ لِلّٰهِ وَمَلٰٓئِكَتِه۪ وَرُسُلِه۪ وَجِبْر۪يلَ وَم۪يكَالَ فَاِنَّ اللّٰهَ عَدُوٌّ لِلْكَافِر۪ينَ", translation: "Kim Allah'a ve O'nun meleklerine, resullerine, Cibril'e, Mikal'e düşman olursa; iyi bilsin ki Allah da Kafirlere düşmandır.", transliteration: "Men kane aduvven lillahi ve melaiketihi ve rusulihi ve cibrile ve mikale fe innallahe aduvvun lil kafirin."},
  {id: 106, sure_no: 99, ayet_no: 99, sure_adi: "Bakara", text: "وَلَقَدْ اَنْزَلْـنَٓا اِلَيْكَ اٰيَاتٍ بَيِّنَاتٍۚ وَمَا يَكْفُرُ بِهَٓا اِلَّا الْفَاسِقُونَ", translation: "Ant olsun Biz, sana apaçık ayetler indirdik. Ancak, onları, fasık olanlar kafirlik eder.", transliteration: "Ve lekad enzelna ileyke ayatin beyyinat, ve ma yekfuru biha illel fasikun."},
  {id: 107, sure_no: 100, ayet_no: 100, sure_adi: "Bakara", text: "اَوَكُلَّمَا عَاهَدُوا عَهْداً نَبَذَهُ فَر۪يقٌ مِنْهُمْۜ بَلْ اَكْثَرُهُمْ لَا يُؤْمِنُونَ", translation: "Onlar, ne zaman bir söz verdilerse, içlerinden bir grup onu bozmadı mı? Zaten onların çoğu iman etmiyorlar.", transliteration: "E ve kullema ahedu ahden nebezehu ferikun minhum bel ekseruhum la yu'minun."},
  {id: 108, sure_no: 101, ayet_no: 101, sure_adi: "Bakara", text: "وَلَمَّا جَٓاءَهُمْ رَسُولٌ مِنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِمَا مَعَهُمْ نَبَذَ فَر۪يقٌ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَۗ كِتَابَ اللّٰهِ وَرَٓاءَ ظُهُورِهِمْ كَاَنَّهُمْ لَا يَعْلَمُونَ", translation: "Ne zaman onlara, Allah'tan, yanlarındakini tasdik edici bir elçi gelse, Kitap Ehli'nden bir grup, sanki hiç haberleri yokmuş gibi, elçinin getirdiğine sırt çevirirler.", transliteration: "Ve lemma caehum resulun min indillahi musaddikun lima meahum nebeze ferikun minellezine utul kitab, kitaballahi verae zuhurihim ke ennehum la ya'lemun."},
  {id: 109, sure_no: 102, ayet_no: 102, sure_adi: "Bakara", text: "وَاتَّـبَعُوا مَا تَتْلُوا الشَّيَاط۪ينُ عَلٰى مُلْكِ سُلَيْمٰنَۚ وَمَا كَفَرَ سُلَيْمٰنُ وَلٰكِنَّ الشَّيَاط۪ينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَۗ وَمَٓا اُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوتَ وَمَارُوتَۜ وَمَا يُعَلِّمَانِ مِنْ اَحَدٍ حَتّٰى يَقُولَٓا اِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْفُرْۜ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُفَرِّقُونَ بِه۪ بَيْنَ الْمَرْءِ وَزَوْجِه۪ۜ وَمَا هُمْ بِضَٓارّ۪ينَ بِه۪ مِنْ اَحَدٍ اِلَّا بِاِذْنِ اللّٰهِۜ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْۜ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرٰيهُ مَا لَهُ فِي الْاٰخِرَةِ مِنْ خَلَاقٍ۠ وَلَبِئْسَ مَا شَرَوْا بِه۪ٓ اَنْفُسَهُمْۜ  لَوْ كَانُوا يَعْلَمُونَ", translation: "Ve onlar, Süleyman'ın sahip olduğu güç konusunda şeytanların uydurdukları şeylere uydular. Oysa Süleyman gerçeğe aykırı bir şey yapmadı. Ancak insanlara büyü yapmayı öğreten şeytanlar gerçeği gizliyordu; Babil'deki iki melike; Harut ve Marut'a bir şey indirilmiş değildi. Hatta bu iki melik: \"Biz fitneyiz, sakın kafir olma!\" demedikçe, hiç kimseye bir şey öğretmiyorlardı. Fakat onlar, o ikisinden karı ile kocanın arasını açacak şeyler öğrenmeye çalışıyorlardı. Ancak, Allah'ın izni olmadıkça bu şeyle hiç kimseye zarar veremezler. Onlar, kendilerine bir faydası olmayan, sadece zarar verecek şeyleri öğreniyorlardı. Halbuki onlar, büyü ile uğraşanların ahirette bir nasiplerinin olmayacağını biliyorlardı. Kendilerini karşılığında sattıkları şey ne kötüdür. Keşke bilselerdi.", transliteration: "Vettebeu ma tetluş şeyatinu ala mulki suleyman ve ma kefere suleymanu ve lakinneş şeyatine keferu yuallimunen nases sihra, ve ma unzile alel melekeyni bi babile harute ve marut, ve ma yuallimani min ehadin hatta yekula innema nahnu fitnetun fe la tekfur fe yeteallemune minhuma ma yuferrikune bihi beynel mer'i ve zevcih, ve ma hum bi darrine bihi min ehadin illa bi iznillah, ve yeteallemune ma yadurruhum ve la yenfeuhum ve lekad alimu le menişterahu ma lehu fil ahireti min halakın, ve le bi'se ma şerev bihi enfusehum lev kanu ya'lemun."},
  {id: 110, sure_no: 103, ayet_no: 103, sure_adi: "Bakara", text: "وَلَوْ اَنَّهُمْ اٰمَنُوا وَاتَّقَوْا لَمَثُوبَةٌ مِنْ عِنْدِ اللّٰهِ خَيْرٌۜ لَوْ كَانُوا يَعْلَمُونَ۟", translation: "Eğer onlar iman edip takva sahibi olsalardı, Allah katında kazanacakları sevap kendileri için daha hayırlı olurdu. Keşke bilselerdi.", transliteration: "Ve lev ennehum amenu vettekav le mesubetun min indillahi hayr, lev kanu ya'lemun."},
  {id: 111, sure_no: 104, ayet_no: 104, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَقُولُوا رَاعِنَا وَقُولُوا انْظُرْنَا وَاسْمَعُواۜ وَلِلْكَافِر۪ينَ عَذَابٌ اَل۪يمٌ", translation: "Ey İman Edenler! \"raina\" demeyin, \"unzurna.\" deyin ve dinleyin. Kafirler için can yakıcı bir azap vardır.", transliteration: "Ya eyyuhellezine amenu la tekulu raina ve kulunzurna vesmeu ve lil kafirine azabun elim."},
  {id: 112, sure_no: 105, ayet_no: 105, sure_adi: "Bakara", text: "مَا يَوَدُّ الَّذ۪ينَ كَفَرُوا مِنْ اَهْلِ الْكِتَابِ وَلَا الْمُشْرِك۪ينَ اَنْ يُنَزَّلَ عَلَيْكُمْ مِنْ خَيْرٍ مِنْ رَبِّكُمْۜ وَاللّٰهُ يَخْتَصُّ بِرَحْمَتِه۪ مَنْ يَشَٓاءُۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ", translation: "Kitap Ehli'nin ve Müşriklerin kafirleri Rabb'inizden, size bir hayrın indirilmesini istemezler. Oysa Allah, rahmetini dilediği kimseye ayırır. Allah, Büyük Lütuf Sahibi'dir.", transliteration: "Ma yeveddullezine keferu min ehlil kitabi ve lel muşrikine en yunezzele aleykum min hayrin min rabbikum vallahu yahtassu bi rahmetihi men yeşau, vallahu zul fadlil azim."},
  {id: 113, sure_no: 106, ayet_no: 106, sure_adi: "Bakara", text: "مَا نَنْسَخْ مِنْ اٰيَةٍ اَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِنْهَٓا اَوْ مِثْلِهَاۜ اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Biz, bir ayeti yürürlükten kaldırır veya unutturursak ya onun dengini ya da ondan daha iyisini getiririz. Sen, Allah'ın her şeye gücü yeten olduğunu bilmez misin?", transliteration: "Ma nensah min ayetin ev nunsiha ne'ti bi hayrin minha ev misliha e lem ta'lem ennallahe ala kulli şey'in kadir."},
  {id: 114, sure_no: 107, ayet_no: 107, sure_adi: "Bakara", text: "اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ", translation: "Göklerin ve yerin egemenliğinin Allah'a ait olduğunu bilmez misin? Sizin için Allah'ın yanı sıra ne bir veli ne de bir yardımcı vardır.", transliteration: "E lem ta'lem ennellahe lehu mulkus semavati vel ard, ve ma lekum min dunillahi min veliyyin ve la nasir."},
  {id: 115, sure_no: 108, ayet_no: 108, sure_adi: "Bakara", text: "اَمْ تُر۪يدُونَ اَنْ تَسْـَٔلُوا رَسُولَكُمْ كَمَا سُئِلَ مُوسٰى مِنْ قَبْلُۜ  وَمَنْ يَتَبَدَّلِ الْكُفْرَ بِالْا۪يمَانِ فَقَدْ ضَلَّ سَوَٓاءَ السَّب۪يلِ", translation: "Yoksa siz de daha önce Musa'yı sorguya çekenler gibi, elçinizi sorguya çekmek mi istiyorsunuz? Her kim, iman yerine küfrü tercih ederse o dosdoğru yoldan sapmış olur.", transliteration: "Em turidune en tes'elu resulekum kema suile musa min kabl, ve men yetebeddelil kufra bil imani fe kad dalle sevaes sebil."},
  {id: 116, sure_no: 109, ayet_no: 109, sure_adi: "Bakara", text: "وَدَّ كَث۪يرٌ مِنْ اَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ ا۪يمَانِكُمْ كُفَّاراًۚ حَسَداً مِنْ عِنْدِ اَنْفُسِهِمْ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّۚ فَاعْفُوا وَاصْفَحُوا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِه۪ۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Kitap Ehli'nin çoğu, Hakk kendilerine bildirildiği halde, benliklerindeki kıskançlıktan dolayı, imanınızdan sonra sizi küfre döndürmek isterler. Allah'ın emri gelinceye kadar onlara aldırış etmeyin. Onları affedin ve onlarla iyi geçinin. Kuşkusuz Allah, Her Şeye Güç Yetiren'dir.", transliteration: "Vedde kesirun min ehlil kitabi lev yeruddunekum min ba'di imanikum kuffara, haseden min indi enfusihim min ba'di ma tebeyyene lehumul hakk, fa'fu vasfehu hatta ye'tiyallahu bi emrih, innallahe ala kulli şey'in kadir."},
  {id: 117, sure_no: 110, ayet_no: 110, sure_adi: "Bakara", text: "وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۜ وَمَا تُقَدِّمُوا لِاَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللّٰهِۜ اِنَّ اللّٰهَ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Salatı ikame edin ve zekatı yapın. Kendiniz için her ne iyilik yaparsanız onu Allah katında bulacaksınız. Kuşkusuz Allah, bütün yaptıklarınızı görmektedir.", transliteration: "Ve ekimus salate ve atuz zekat, ve ma tukaddimu li enfusikum min hayrin teciduhu indallah innallahe bi ma ta'melune basir."},
  {id: 118, sure_no: 111, ayet_no: 111, sure_adi: "Bakara", text: "وَقَالُوا لَنْ يَدْخُلَ الْجَنَّةَ اِلَّا مَنْ كَانَ هُوداً اَوْ نَصَارٰىۜ تِلْكَ اَمَانِيُّهُمْۜ قُلْ هَاتُوا بُرْهَانَكُمْ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Onlar, \"Yahudi ve Nesara'dan başkası Cennet'e giremeyecek.\" derler. Bu, onların kuruntusudur. De ki: \"Eğer doğru söylüyorsanız iddianızı kanıtlayın.\"", transliteration: "Ve kalu len yedhulel cennete illa men kane huden ev nasar, tilke emaniyyuhum kul hatu burhanekum in kuntum sadikin."},
  {id: 119, sure_no: 112, ayet_no: 112, sure_adi: "Bakara", text: "بَلٰى مَنْ اَسْلَمَ وَجْهَهُ لِلّٰهِ وَهُوَ مُحْسِنٌ فَلَهُٓ اَجْرُهُ عِنْدَ رَبِّه۪ۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ۟", translation: "Bilakis, gerçekten kim iyi bir kimse olarak benliğini Allah'a teslim etmişse, onun ödülü Rabb'inin yanındadır. Onlar için hiçbir korku yoktur ve onlar üzülmeyeceklerdir.", transliteration: "Bela men esleme vechehu lillahi ve huve muhsinun fe lehu ecruhu inde rabbihi, ve la havfun aleyhim ve la hum yahzenun."},
  {id: 120, sure_no: 113, ayet_no: 113, sure_adi: "Bakara", text: "وَقَالَتِ الْيَهُودُ لَيْسَتِ النَّصَارٰى عَلٰى شَيْءٍۖ وَقَالَتِ النَّصَارٰى لَيْسَتِ الْيَهُودُ عَلٰى شَيْءٍۙ وَهُمْ يَتْلُونَ الْكِتَابَۜ كَذٰلِكَ قَالَ الَّذ۪ينَ لَا يَعْلَمُونَ مِثْلَ قَوْلِهِمْۚ فَاللّٰهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ", translation: "Kitap'ı okuyup durdukları halde, Yahudiler: \"Nesara geçerli bir inanç üzerinde değildirler.\" derler. Nesara da: \"Yahudiler geçerli bir inanca sahip değildirler.\" derler. Oysa Kitap'ı okuyorlar. Kitap'tan habersiz olanlar da onların sözlerinin aynısını söylediler. Allah, onların anlaşmazlığa düştükleri konuda kıyamet günü hüküm verecektir.", transliteration: "Ve kaletil yahudu leysetin nasara ala şey' ve kaletin nasara leysetil yahudu ala şey'in ve hum yetlunel kitab, kezalike kalellezine la ya'lemune misle kavlihim, fallahu yahkumu beynehum yevmel kıyameti fima kanu fihi yahtelifun."},
  {id: 121, sure_no: 114, ayet_no: 114, sure_adi: "Bakara", text: "وَمَنْ اَظْلَمُ مِمَّنْ مَنَعَ مَسَاجِدَ اللّٰهِ اَنْ يُذْكَرَ ف۪يهَا اسْمُهُ وَسَعٰى ف۪ي خَرَابِهَاۜ اُو۬لٰٓئِكَ مَا كَانَ لَهُمْ اَنْ يَدْخُلُوهَٓا اِلَّا خَٓائِف۪ينَۜ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْاٰخِرَةِ عَذَابٌ عَظ۪يمٌ", translation: "Allah'ın mescitlerinde, O'nun adının anılmasını engelleyen ve onların harap olmasına çalışan kimseden daha zalim kim olabilir? Buralara, ancak Allah'a karşı gelmekten korkularak girilebilir. Onlar için dünyada aşağılanma, ahirette de büyük bir azap vardır.", transliteration: "Ve men azlemu mimmen menea mesacidallahi en yuzkere fihesmuhu ve sea fi harabiha ulaike ma kane lehum en yedhuluha illa haifin lehum fid dunya hızyun ve lehum fil ahireti azabun azim."},
  {id: 122, sure_no: 115, ayet_no: 115, sure_adi: "Bakara", text: "وَلِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُ فَاَيْنَمَا تُوَلُّوا فَثَمَّ وَجْهُ اللّٰهِۜ اِنَّ اللّٰهَ وَاسِعٌ عَل۪يمٌ", translation: "Doğu da Allah'ındır, batı da. Nereye yönelirseniz yönelin, Allah'ın yönü orasıdır. Kuşkusuz, Allah, Yardımı Çok Kapsamlı Olan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve lillahil meşriku vel magribu fe eynema tuvellu fe semme vechullah innallahe vasiun alim."},
  {id: 123, sure_no: 116, ayet_no: 116, sure_adi: "Bakara", text: "وَقَالُوا اتَّخَذَ اللّٰهُ وَلَداًۙ سُبْحَانَهُۜ بَلْ لَهُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ كُلٌّ لَهُ قَانِتُونَ", translation: "\"Allah, çocuk edindi!\" dediler. Haşa! O, bundan münezzehtir. Bilakis, gökte ve yerde ne varsa her şey O'nundur. Ve her şey O'na gönülden boyun eğmektedir.", transliteration: "Ve kaluttehazellahu veleden, subhaneh, bel lehu ma fis semavati vel ard, kullun lehu kanitun."},
  {id: 124, sure_no: 117, ayet_no: 117, sure_adi: "Bakara", text: "بَد۪يعُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاِذَا قَضٰٓى اَمْراً فَاِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ", translation: "O, göklerin ve yerin Bedi'sidir. Bir şeyin olmasını istediğinde, ona sadece \"Ol.\" der, o da oluverir.", transliteration: "Bedius semavati vel ard, ve iza kada emren fe innema yekulu lehu kun fe yekun."},
  {id: 125, sure_no: 118, ayet_no: 118, sure_adi: "Bakara", text: "وَقَالَ الَّذ۪ينَ لَا يَعْلَمُونَ لَوْلَا يُكَلِّمُنَا اللّٰهُ اَوْ تَأْت۪ينَٓا اٰيَةٌۜ  كَذٰلِكَ قَالَ الَّذ۪ينَ مِنْ قَبْلِهِمْ مِثْلَ قَوْلِهِمْۜ تَشَابَهَتْ قُلُوبُهُمْۜ قَدْ بَيَّنَّا الْاٰيَاتِ لِقَوْمٍ يُوقِنُونَ", translation: "Bilmeyen kimseler: \"Allah'ın bizimle konuşması veya bize bir ayet göndermesi gerekmez mi?\" dediler. Onlardan öncekiler de onların sözlerine benzer sözler söylemişlerdi. Kalpleri birbirlerine benziyor. Bilmek isteyen kimseler için ayetleri iyice açıkladık.", transliteration: "Ve kalellezine la ya'lemune lev la yukellimunallahu ev te'tina ayeh, kezalike kalellezine min kablihim misle kavlihim, teşabehet kulubuhum, kad beyyennal ayati li kavmin yukınun."},
  {id: 126, sure_no: 119, ayet_no: 119, sure_adi: "Bakara", text: "اِنَّٓا اَرْسَلْنَاكَ بِالْحَقِّ بَش۪يراً وَنَذ۪يراًۙ وَلَا تُسْـَٔلُ عَنْ اَصْحَابِ الْجَح۪يمِ", translation: "Doğrusu Biz, seni, Hakk ile müjdeleyici ve uyarıcı olarak gönderdik. Cehennem ehlinden sorumlu tutulacak değilsin.", transliteration: "İnna erselnake bil hakkı beşiren ve neziren, ve la tus'elu an ashabil cahim."},
  {id: 127, sure_no: 120, ayet_no: 120, sure_adi: "Bakara", text: "وَلَنْ تَرْضٰى عَنْكَ الْيَهُودُ وَلَا النَّصَارٰى حَتّٰى تَتَّبِعَ مِلَّتَهُمْۜ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰىۜ وَلَئِنِ اتَّبَعْتَ اَهْوَٓاءَهُمْ بَعْدَ الَّذ۪ي جَٓاءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ", translation: "Yahudiler ve Nesara, sen onların milletlerine uymadıkça senden asla razı olmazlar. De ki: \"Gerçek şu ki; doğru yol Allah'ın yoludur.\" Ve sana gelen ilimden sonra onların arzularına uyarsan; Allah'tan, sana ne bir veli ne bir yardımcı bulabilirsin.", transliteration: "Ve len terda ankel yahudu ve len nasara hatta tettebia milletehum kul inne hudallahi huvel huda ve leinitteba'te ehvaehum ba'dellezi caeke minel ilmi, ma leke minallahi min veliyyin ve la nasir."},
  {id: 128, sure_no: 121, ayet_no: 121, sure_adi: "Bakara", text: "اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَتْلُونَهُ حَقَّ تِلَاوَتِه۪ۜ  اُو۬لٰٓئِكَ يُؤْمِنُونَ بِه۪ۜ وَمَنْ يَكْفُرْ بِه۪ فَاُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ۟", translation: "Kendilerine Kitap verdiğimiz kimseler, ona uymak için gereği gibi okurlar. İşte bunlar ona iman edenlerdir. İman etmeyenler ise, işte onlar kaybedenlerdir.", transliteration: "Ellezine ateynahumul kitabe yetlunehu hakka tilavetih ulaike yu'minune bih, ve men yekfur bihi fe ulaike humul hasirun."},
  {id: 129, sure_no: 122, ayet_no: 122, sure_adi: "Bakara", text: "يَا بَن۪ٓي اِسْرَٓائ۪لَ اذْكُرُوا نِعْمَتِيَ الَّت۪ٓي اَنْعَمْتُ عَلَيْكُمْ وَاَنّ۪ي فَضَّلْتُكُمْ عَلَى الْعَالَم۪ينَ", translation: "Ey İsrailoğulları! Size verdiğim nimetimi ve lütufta bulunarak sizi alemlerde nimetlendirdiğimi hatırlayın.", transliteration: "Ya beni israilezkuru ni'metiyelleti en'amtu aleykum ve enni faddaltukum alel alemin."},
  {id: 130, sure_no: 123, ayet_no: 123, sure_adi: "Bakara", text: "وَاتَّقُوا يَوْماً لَا تَجْز۪ي نَفْسٌ عَنْ نَفْسٍ شَيْـٔاً وَلَا يُقْبَلُ مِنْهَا عَدْلٌ وَلَا تَنْفَعُهَا شَفَاعَةٌ وَلَا هُمْ يُنْصَرُونَ", translation: "Hiç kimsenin bir başkasının yerine bir şey ödeyemeyeceği, hiç kimseden fidye kabul edilmeyeceği ve hiç kimseye şefaatin fayda vermeyeceği ve hiç kimseye yardım edilmeyeceği günden korunup sakının.", transliteration: "Vetteku yevmen la teczi nefsun an nefsin şey'en ve la yukbelu minha adlun ve la tenfeuha şefaatun ve la hum yunsarun."},
  {id: 131, sure_no: 124, ayet_no: 124, sure_adi: "Bakara", text: "وَاِذِ ابْتَلٰٓى اِبْرٰه۪يمَ رَبُّهُ بِكَلِمَاتٍ فَاَتَمَّهُنَّۜ قَالَ اِنّ۪ي جَاعِلُكَ لِلنَّاسِ اِمَاماًۜ قَالَ وَمِنْ ذُرِّيَّت۪يۜ قَالَ لَا يَنَالُ عَهْدِي الظَّالِم۪ينَ", translation: "Hani! Rabb'i, İbrahim'i birtakım kelimeler ile sınamış; o da onları yerine getirmişti. Allah, \"Ben seni insanlara önder yapacağım.\" dedi. İbrahim, \"Soyumdan olanları da.\" deyince; Allah, \"Benim ahdim zalimlere ulaşmaz!\" dedi.", transliteration: "Ve izibtela ibrahime rabbuhu bi kelimatin fe etemmehun, kale inni cailuke lin nasi imama, kale ve min zurriyyeti kale la yenalu ahdiz zalimin."},
  {id: 132, sure_no: 125, ayet_no: 125, sure_adi: "Bakara", text: "وَاِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِلنَّاسِ وَاَمْناًۜ وَاتَّخِذُوا مِنْ مَقَامِ اِبْرٰه۪يمَ مُصَلًّىۜ  وَعَهِدْنَٓا اِلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ اَنْ طَهِّرَا بَيْتِيَ لِلطَّٓائِف۪ينَ وَالْعَاكِف۪ينَ وَالرُّكَّعِ السُّجُودِ", translation: "Hani Biz, Beyt'i insanlar için toplanma yeri ve güvenli bir yer kıldık. \"İbrahim'in makamından kendinize bir salat yeri edinin.\" dedik. İbrahim ve İsmail'den, \"Evimi tavaf edenler, kendilerini adamak amacıyla orda toplananlar, \"rüku ve secde edenler\" için temiz tutun.\" diye söz aldık.", transliteration: "Ve iz cealnal beyte mesabeten lin nasi ve emna, vettehizu min makamı ibrahime musalla ve ahidna ila ibrahime ve ismaile en tahhira beytiye lit taifine vel akifine ver rukkais sucud."},
  {id: 133, sure_no: 126, ayet_no: 126, sure_adi: "Bakara", text: "وَاِذْ قَالَ اِبْرٰه۪يمُ رَبِّ اجْعَلْ هٰذَا بَلَداً اٰمِناً وَارْزُقْ اَهْلَهُ مِنَ الثَّمَرَاتِ مَنْ اٰمَنَ مِنْهُمْ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ قَالَ وَمَنْ كَفَرَ فَاُمَتِّعُهُ قَل۪يلاً ثُمَّ اَضْطَرُّهُٓ اِلٰى عَذَابِ النَّارِۜ وَبِئْسَ الْمَص۪يرُ", translation: "Hani İbrahim: \"Ey Rabb'im! Bu beldeyi güvenli bir yer kıl, halkından, Allah'a ve ahiret gününe iman edenlerı, çeşitli ürünlerle rızıklandır.\" dedi. O da: \"Kafir olanı dahi az bir süre geçindiririm; sonra onu ateşin azabına uğratırım. Orası, varacaklar için ne kötü bir yerdir.\" dedi.", transliteration: "Ve iz kale ibrahimu rabbic'al haza beleden aminen verzuk ehlehu mines semerati men amene minhum billahi vel yevmil ahir, kale ve men kefere fe umettiuhu kalilen summe adtarruhu ila azabin nar, ve bi'sel masir."},
  {id: 134, sure_no: 127, ayet_no: 127, sure_adi: "Bakara", text: "وَاِذْ يَرْفَعُ اِبْرٰه۪يمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَاِسْمٰع۪يلُۜ رَبَّنَا تَقَبَّلْ مِنَّاۜ اِنَّكَ اَنْتَ السَّم۪يعُ الْعَل۪يمُ", translation: "Hani bir zamanlar İbrahim, İsmail ile birlikte Beyt'in temellerini yükseltirken: \"Ey Rabb'imiz! Bunu bizden kabul et; kuşkusuz Sen, Her Şeyi İşiten ve Her Şeyi Bilen'sin.\" demişti.", transliteration: "Ve iz yerfeu ibrahimul kavaide minel beyti veismail rabbena tekabbel minna inneke entes semiul alim."},
  {id: 135, sure_no: 128, ayet_no: 128, sure_adi: "Bakara", text: "رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَٓا اُمَّةً مُسْلِمَةً لَكَۖ وَاَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَاۚ اِنَّكَ اَنْتَ التَّوَّابُ الرَّح۪يمُ", translation: "\"Ey Rabb'imiz! Bizi, Sana teslim olanlardan kıl. Bizim soyumuzdan da Sana teslim olan bir toplum yap. Bize, menasikimizi göster. Tevbelerimizi kabul et. Kuşkusuz Tevbeleri Kabul Eden, Rahmeti Kesintisiz Olan yalnızca Sen'sin.\"", transliteration: "Rabbena vec'alna muslimeyni leke ve min zurriyyetina ummeten muslimeten leke ve erina menasikena ve tub aleyna, inneke entet tevvabur rahim."},
  {id: 136, sure_no: 129, ayet_no: 129, sure_adi: "Bakara", text: "رَبَّنَا وَابْعَثْ ف۪يهِمْ رَسُولاً مِنْهُمْ يَتْلُوا عَلَيْهِمْ اٰيَاتِكَ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُزَكّ۪يهِمْۜ اِنَّكَ اَنْتَ الْعَز۪يزُ الْحَك۪يمُ۟", translation: "\"Ey Rabb'imiz! İçlerinden onlara Sen'in ayetlerini okuyacak, Kitap'ı ve Hikmeti öğretecek ve onları arındıracak bir elçi gönder. Kuşkusuz Sen, Mutlak Üstün Olan'sın, En İyi Hüküm Veren'sin.\"", transliteration: "Rabbena veb'as fihim resulen minhum yetlu aleyhim ayatike ve yuallimuhumul kitabe vel hikmete ve yuzekkihim inneke entel azizul hakim."},
  {id: 137, sure_no: 130, ayet_no: 130, sure_adi: "Bakara", text: "وَمَنْ يَرْغَبُ عَنْ مِلَّةِ اِبْرٰه۪يمَ اِلَّا مَنْ سَفِهَ نَفْسَهُۜ وَلَقَدِ اصْطَفَيْنَاهُ فِي الدُّنْيَاۚ وَاِنَّهُ فِي الْاٰخِرَةِ لَمِنَ الصَّالِح۪ينَ", translation: "İbrahim'in milletinden, kendini bilmezlerden başka kim yüz çevirir? Ant olsun onu dünyada Biz seçtik; o, ahirette de iyiler arasında yer alacaktır.", transliteration: "Ve men yergabu an milleti ibrahime illa men sefihe nefseh, ve lekadistafeynahufid dunya, ve innehu filahireti le mines salihin."},
  {id: 138, sure_no: 131, ayet_no: 131, sure_adi: "Bakara", text: "اِذْ قَالَ لَهُ رَبُّهُٓ اَسْلِمْۙ قَالَ اَسْلَمْتُ لِرَبِّ الْعَالَم۪ينَ", translation: "Hani Rabb'i ona: \"Müslim ol\" dediğinde; o da: \"Alemlerin Rabb'i için müslim oldum.\" demişti.", transliteration: "İz kale lehu rabbuhu eslim kale eslemtu li rabbil alemin."},
  {id: 139, sure_no: 132, ayet_no: 132, sure_adi: "Bakara", text: "وَوَصّٰى بِهَٓا اِبْرٰه۪يمُ بَن۪يهِ وَيَعْقُوبُۜ يَا بَنِيَّ اِنَّ اللّٰهَ اصْطَفٰى لَكُمُ الدّ۪ينَ فَلَا تَمُوتُنَّ اِلَّا وَاَنْتُمْ مُسْلِمُونَۜ", translation: "İbrahim, bunu, çocuklarına vasiyet etmişti. Yakub da: \"Ey oğullarım! Şüphesiz Allah, bu dini sizin için seçti. O halde müslimler olarak ölmenin dışında başka bir şekilde ölmeyin!\" demişti.", transliteration: "Ve vassa biha ibrahimu benihi ve ya'kub, ya beniyye innallahestafa lekumud dine fe la temutunne illa ve entum muslimun."},
  {id: 140, sure_no: 133, ayet_no: 133, sure_adi: "Bakara", text: "اَمْ كُنْتُمْ شُهَدَٓاءَ اِذْ حَضَرَ يَعْقُوبَ الْمَوْتُۙ اِذْ قَالَ لِبَن۪يهِ مَا تَعْبُدُونَ مِنْ بَعْد۪يۜ قَالُوا نَعْبُدُ اِلٰهَكَ وَاِلٰهَ اٰبَٓائِكَ اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ اِلٰهاً وَاحِداًۚ وَنَحْنُ لَهُ مُسْلِمُونَ", translation: "Yoksa siz, Yakub'un ölümü anında onun yanında mıydınız? Hani oğullarına: \"Benden sonra neye kulluk edeceksiniz?\" demişti. Onlar da: \"Senin ilahına ve ataların İbrahim, İsmail ve İshak'ın ilahı olan tek ilaha kulluk edeceğiz. Biz, O'na teslim olanlardanız.\" demişlerdi.", transliteration: "Em kuntum şuhedae iz hadara ya'kubel mevtu, iz kale li benihi ma ta'budune min ba'di kalu na'budu ilaheke ve ilahe abaike ibrahime ve ismaile ve ishaka ilahen vahida ve nahnu lehu muslimun."},
  {id: 141, sure_no: 134, ayet_no: 134, sure_adi: "Bakara", text: "تِلْكَ اُمَّةٌ قَدْ خَلَتْۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْۚ وَلَا تُسْـَٔلُونَ عَمَّا كَانُوا يَعْمَلُونَ", translation: "Onlar gelip geçen bir ümmetti. Onların kazandıkları onlara; sizin kazandıklarınız sizedir. Siz, onların yaptıklarından sorumlu değilsiniz.", transliteration: "Tilke ummetun kad halet, leha ma kesebet ve lekum ma kesebtum, ve la tus'elune amma kanu ya'melun."},
  {id: 142, sure_no: 135, ayet_no: 135, sure_adi: "Bakara", text: "وَقَالُوا كُونُوا هُوداً اَوْ نَصَارٰى تَهْتَدُواۜ قُلْ بَلْ مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ", translation: "Onlar, \"Yahudi veya Nesara olun ki, doğru yolu bulasınız.\" dediler. De ki: \"Hayır; biz, hiçbir zaman Müşrik olmayan, hanif olan İbrahim'in milletindeniz.\"", transliteration: "Ve kalu kunu huden ev nasara tehtedu kul bel millete ibrahime hanifa, ve ma kane minel muşrikin."},
  {id: 143, sure_no: 136, ayet_no: 136, sure_adi: "Bakara", text: "قُولُٓوا اٰمَنَّا بِاللّٰهِ وَمَٓا اُنْزِلَ اِلَيْنَا وَمَٓا اُنْزِلَ اِلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطِ وَمَٓا اُو۫تِيَ مُوسٰى وَع۪يسٰى وَمَٓا اُو۫تِيَ النَّبِيُّونَ مِنْ رَبِّهِمْۚ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِنْهُمْۘ وَنَحْنُ لَهُ مُسْلِمُونَ", translation: "Deyin ki: \"Biz; Allah'a, bize indirilene, İbrahim'e, İsmail'e, İshak'a, Yakub'a ve onların soyundan gelenlere indirilene; Musa'ya, İsa'ya ve diğer Nebilere Rabb'lerinden verilenlere, iman ettik. Onları birbirinden ayırt etmeyiz. Ve biz O'na teslim olanlardanız.\"", transliteration: "Kulu amenna billahi ve ma unzile ileyna ve ma unzile ila ibrahime ve ismaile ve ishaka ve ya'kube vel esbatı ve ma utiye musa ve isa ve ma utiyen nebiyyune min rabbihim, la nuferriku beyne ehadin minhum ve nahnu lehu muslimun."},
  {id: 144, sure_no: 137, ayet_no: 137, sure_adi: "Bakara", text: "فَاِنْ اٰمَنُوا بِمِثْلِ مَٓا اٰمَنْتُمْ بِه۪ فَقَدِ اهْتَدَوْاۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا هُمْ ف۪ي شِقَاقٍۚ فَسَيَكْف۪يكَهُمُ اللّٰهُۚ وَهُوَ السَّم۪يعُ الْعَل۪يمُۜ", translation: "Eğer onlar da sizin iman ettiğiniz gibi iman ederlerse, kuşkusuz doğru yolu bulmuş olurlar. Eğer yüz çevirirlerse, derin bir ayrılık ve çıkmaza düşmüş olurlar. Onlara karşı Allah sana yeter. O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Fe in amenu bi misli ma amentum bihi fe kadihtedev ve in tevellev fe innema hum fi şikak fe se yekfike humullah, ve huves semiul alim."},
  {id: 145, sure_no: 138, ayet_no: 138, sure_adi: "Bakara", text: "صِبْغَةَ اللّٰهِۚ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ صِبْغَةًۘ وَنَحْنُ لَهُ عَابِدُونَ", translation: "Allah'ın boyası! Allah'tan daha iyi boyası olan kim olabilir? Biz, yalnızca O'na kulluk edenlerdeniz.", transliteration: "Sıbgatallah ve men ahsenu minallahi sıbgaten, ve nahnu lehu abidun."},
  {id: 146, sure_no: 139, ayet_no: 139, sure_adi: "Bakara", text: "قُلْ اَتُحَٓاجُّونَنَا فِي اللّٰهِ وَهُوَ رَبُّنَا وَرَبُّكُمْۚ وَلَـنَٓا اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۚ وَنَحْنُ لَهُ مُخْلِصُونَۙ", translation: "De ki: \"Bizimle Allah hakkında mı tartışıyorsunuz? Oysa O, bizim de Rabb'imiz sizin de Rabb'inizdir. Bizim yaptıklarımız bize, sizin yaptıklarınız sizedir. Biz ona içtenlikle bağlananlarız.\"", transliteration: "Kul e tuhaccunena fillahi ve huve rabbuna ve rabbukum, ve lena a'maluna ve lekum a'malukum ve nahnu lehu muhlisun."},
  {id: 147, sure_no: 140, ayet_no: 140, sure_adi: "Bakara", text: "اَمْ تَقُولُونَ اِنَّ اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطَ كَانُوا هُوداً اَوْ نَصَارٰىۜ قُلْ ءَاَنْتُمْ اَعْلَمُ اَمِ اللّٰهُۜ وَمَنْ اَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهُ مِنَ اللّٰهِۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", translation: "İbrahim'in, İsmail'in, İshak'ın, Yakub'un ve onların soyundan gelenlerin Yahudi veya Nesara olduklarını mı iddia ediyorsunuz? De ki: \"Siz mi daha iyi biliyorsunuz, yoksa Allah mı?\" Allah'tan gelen bir gerçeği kendi yanında gizleyen kimseden daha zalim kim olabilir? Allah, yaptıklarınızdan habersiz değildir.", transliteration: "Em tekulune inne ibrahime ve ismaile ve ishaka ve ya'kube vel esbata kanu huden ev nasara kul e entum a'lemu emillah, ve men azlemu mimmen keteme şehadeten indehu minallah, ve mallahu bi gafilin amma ta'melun."},
  {id: 148, sure_no: 141, ayet_no: 141, sure_adi: "Bakara", text: "تِلْكَ اُمَّةٌ قَدْ خَلَتْۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْۚ وَلَا تُسْـَٔلُونَ عَمَّا كَانُوا يَعْمَلُونَ۟", translation: "Onlar, gelip geçen bir ümmetti. Onların kazandıkları onlara; sizin kazandıklarınız sizedir. Siz, onların yaptıklarından sorumlu değilsiniz.", transliteration: "Tilke ummetun kad halet leha ma kesebet ve lekum ma kesebtum ve la tus'elune amma kanu ya'melun."},
  {id: 149, sure_no: 142, ayet_no: 142, sure_adi: "Bakara", text: "سَيَقُولُ السُّفَـهَٓاءُ مِنَ النَّاسِ مَا وَلّٰيهُمْ عَنْ قِبْلَتِهِمُ الَّت۪ي كَانُوا عَلَيْهَاۜ قُلْ لِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُۜ يَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "İnsanlardan, birtakım beyinsizler: \"Daha önce yöneldiğiniz kıbleden sizi çeviren nedir?\" diyecekler. De ki: \"Doğu da Batı da Allah'ındır. O, hak edeni doğru yola iletir.\"", transliteration: "Se yekulus sufehau minen nasi ma vellahum an kıbletihimulleti kanu aleyha kul lillahil meşrıku vel magrıb, yehdi men yeşau ila sıratın mustakim."},
  {id: 150, sure_no: 143, ayet_no: 143, sure_adi: "Bakara", text: "وَكَذٰلِكَ جَعَلْنَاكُمْ اُمَّةً وَسَطاً لِتَكُونُوا شُهَدَٓاءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَه۪يداًۜ وَمَا جَعَلْنَا الْقِبْلَةَ الَّت۪ي كُنْتَ عَلَيْهَٓا اِلَّا لِنَعْلَمَ مَنْ يَتَّبِـعُ الرَّسُولَ مِمَّنْ يَنْقَلِبُ عَلٰى عَقِبَيْهِۜ  وَاِنْ كَانَتْ لَكَب۪يرَةً اِلَّا عَلَى الَّذ۪ينَ هَدَى اللّٰهُۜ وَمَا كَانَ اللّٰهُ لِيُض۪يعَ ا۪يمَانَكُمْۜ اِنَّ اللّٰهَ بِالنَّاسِ لَرَؤُ۫فٌ رَح۪يمٌ", translation: "Ve böylece, sizi vasat bir toplum yaptık ki insanlara karşı gerçeğin tanıkları olasınız; elçi de sizin üzerinizde tanık olsun. Senin arzulayıp da yönelemediğin Kıble'yi, Resul'e uyanları ökçeleri üzerinde geri dönenlerden ayırt etmek için kıble yaptık. Doğrusu, bu, Allah'ın hidayet ettiği kimselerden başkasına ağır gelir. Allah, imanınızı boşa çıkaracak değildir. Kuşkusuz Allah, insanlara karşı Çok Şefkatli ve Çok Bağışlayıcı'dır.", transliteration: "Ve kezalike cealnakum ummeten vasatan li tekunu şuhedae alen nasi ve yekuner resulu aleykum şehida, ve ma cealnal kıbletelleti kunte aleyha illa li na'leme men yettebiur resule mimmen yenkalibu ala akibeyh, ve in kanet le kebireten illa alellezine hedallah ve ma kanallahu li yudia imanekum innallahe bin nasi le raufun rahim."},
  {id: 151, sure_no: 144, ayet_no: 144, sure_adi: "Bakara", text: "قَدْ نَرٰى تَقَلُّبَ وَجْهِكَ فِي السَّمَٓاءِۚ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضٰيهَاۖ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِۜ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُۜ وَاِنَّ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ لَيَعْلَمُونَ اَنَّهُ الْحَقُّ مِنْ رَبِّهِمْۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا يَعْمَلُونَ", translation: "Senin, yüzünü göğe doğru çevirip durduğunu görüyoruz. Seni, razı olacağın bir kıbleye çevireceğiz. Bundan böyle yüzünü, Mescid-i Haram tarafına çevir. Ve siz de nerede olursanız olun, yüzlerinizi o tarafa çevirin. Kitap verilenler, onun Rabb'lerinden gelen bir gerçek olduğunu bilirler. Allah, onların yaptıklarından habersiz değildir.", transliteration: "Kad nera tekallube vechike fis semai, fe le nuvelliyenneke kıbleten terdaha, fe velli vecheke şatral mescidil haram, ve haysu ma kuntum fe vellu vucuhekum şatrah, ve innellezine utul kitabe le ya'lemune ennehul hakku min rabbihim ve mallahu bi gafilin amma ya'melun."},
  {id: 152, sure_no: 145, ayet_no: 145, sure_adi: "Bakara", text: "وَلَئِنْ اَتَيْتَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ بِكُلِّ اٰيَةٍ مَا تَبِعُوا قِبْلَتَكَۚ وَمَٓا اَنْتَ بِتَابِـعٍ قِبْلَتَهُمْۚ وَمَا بَعْضُهُمْ بِتَابِـعٍ قِبْلَةَ بَعْضٍۜ وَلَئِنِ اتَّبَعْتَ اَهْوَٓاءَهُمْ مِنْ بَعْدِ مَا جَٓاءَكَ مِنَ الْعِلْمِۙ اِنَّكَ اِذاً لَمِنَ الظَّالِم۪ينَۢ", translation: "Ant olsun ki Kitap verilenlere hangi ayeti getirirsen getir, yine de onlar senin kıblene uymazlar. Sen de onların kıblesine uyacak değilsin. Onlar, birbirlerinin kıblesine de uymazlar. Eğer, sana verilen bunca ilimden sonra, onların arzularına uyarsan, o zaman zalimlerden olursun.", transliteration: "Ve le in eteytellezine utul kitabe bi kulli ayetin ma tebiu kıbletek ve ma ente bi tabiın kıbletehum, ve ma ba'duhum bi tabiın kıblete ba'd, ve le initteba'te ehvaehum min ba'di ma caeke minel ilmi inneke izen le minez zalimin."},
  {id: 153, sure_no: 146, ayet_no: 146, sure_adi: "Bakara", text: "اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ اَبْنَٓاءَهُمْۜ وَاِنَّ فَر۪يقاً مِنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَهُمْ يَعْلَمُونَ", translation: "Kendilerine Kitap verdiğimiz kimseler, O'nu öz oğullarını bildikleri gibi bilirler. Buna rağmen içlerinden bir zümre, bile bile gerçeği gizlemektedir.", transliteration: "Ellezine ateynahumul kitabe ya'rifunehu kema ya'rifune ebnaehum ve inne ferikan minhum le yektumunel hakka ve hum ya'lemun."},
  {id: 154, sure_no: 147, ayet_no: 147, sure_adi: "Bakara", text: "اَلْحَقُّ مِنْ رَبِّكَ فَلَا تَكُونَنَّ مِنَ الْمُمْتَر۪ينَ۟", translation: "Gerçek, Rabb'inden gelendir. Sakın kuşku duyanlardan olma.", transliteration: "El hakku min rabbike fe la tekunenne minel mumterin."},
  {id: 155, sure_no: 148, ayet_no: 148, sure_adi: "Bakara", text: "وَلِكُلٍّ وِجْهَةٌ هُوَ مُوَلّ۪يهَا فَاسْتَبِقُوا الْخَيْرَاتِۜ اَيْنَ مَا تَكُونُوا يَأْتِ بِكُمُ اللّٰهُ جَم۪يعاًۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Herkesin yöneldiği bir yönü vardır. Öyleyse siz de yararlı işler yapmada birbirinizle yarışın. Nerede olursanız olun Allah hepinizi bir araya getirir. Kuşkusuz, Allah Her Şeye Güç Yetiren'dir.", transliteration: "Ve li kullin vichetun huve muvelliha festebikul hayrat, eyne ma tekunu ye'ti bikumullahu cemia, innallahe ala kulli şey'in kadir."},
  {id: 156, sure_no: 149, ayet_no: 149, sure_adi: "Bakara", text: "وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِۜ وَاِنَّهُ لَلْحَقُّ مِنْ رَبِّكَۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", translation: "Her nereden çıkarsan çık, yüzünü Mescid-i Haram tarafına çevir. Çünkü bu Rabb'inden gelen Hakk'tır. Allah, yaptıklarınızdan habersiz değildir.", transliteration: "Ve min haysu harecte fe velli vecheke şatral mescidil haram, ve innehu lel hakku min rabbik, ve mallahu bi gafilin amma ta'melun."},
  {id: 157, sure_no: 150, ayet_no: 150, sure_adi: "Bakara", text: "وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِۜ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُۙ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌۗ  اِلَّا الَّذ۪ينَ  ظَلَمُوا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْن۪ي وَلِاُتِمَّ نِعْمَت۪ي عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَۙ", translation: "Her nereden çıkarsan çık, yüzünü Mescid-i Haram tarafına çevir. Siz de nerede olursanız olun, yüzünüzü o tarafa çevirin. Zalimler hariç, insanların size karşı bir kanıtları olmasın. Siz, onlara huşu duymayın, Bana duyun ki size olan nimetimi tamamlayayım; böylece doğru yolu bulasınız.", transliteration: "Ve min haysu harecte fe velli vecheke şatral mescidil haram, ve haysu ma kuntum fe velluvucuhekum şatrahu li ella yekune lin nasi aleykum hucceh, illellezine zalemu minhum fe la tahşevhum vahşevni ve li utimme ni'meti aleykum ve leallekum tehtedun."},
  {id: 158, sure_no: 151, ayet_no: 151, sure_adi: "Bakara", text: "كَمَٓا اَرْسَلْنَا ف۪يكُمْ رَسُولاً مِنْكُمْ يَتْلُوا عَلَيْكُمْ اٰيَاتِنَا وَيُزَكّ۪يكُمْ وَيُعَلِّمُكُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُعَلِّمُكُمْ مَا لَمْ تَكُونُوا تَعْلَمُونَۜ", translation: "Nitekim içinizden, size ayetlerimizi okuyan, sizi arındıran, size Kitap'ı ve Hikmet'i öğreten, size bilmediğiniz şeyleri öğreten bir Resul gönderdik.", transliteration: "Kema erselna fikum resulen minkum yetlu aleykum ayatina ve yuzekkikum ve yuallimukumul kitabe vel hikmete ve yuallimukum ma lem tekunu ta'lemun."},
  {id: 159, sure_no: 152, ayet_no: 152, sure_adi: "Bakara", text: "فَاذْكُرُون۪ٓي اَذْكُرْكُمْ وَاشْكُرُوا ل۪ي وَلَا تَكْفُرُونِ۟", translation: "Öyleyse Beni zikredin ki Ben de sizi zikredeyim. Bana şükredin, sakın nankörlük etmeyin.", transliteration: "Fezkuruni ezkurkum veşkuru li ve la tekfurun."},
  {id: 160, sure_no: 153, ayet_no: 153, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اسْتَع۪ينُوا بِالصَّبْرِ وَالصَّلٰوةِۜ اِنَّ اللّٰهَ مَعَ الصَّابِر۪ينَ", translation: "Ey İman Edenler! Sabır ve salatla yardım dileyin. Kuşkusuz, Allah, sabredenlerle beraberdir.", transliteration: "Ya eyyuhellezine amenustainu bis sabri ves salat, innallahe meas sabirin."},
  {id: 161, sure_no: 154, ayet_no: 154, sure_adi: "Bakara", text: "وَلَا تَقُولُوا لِمَنْ يُقْتَلُ ف۪ي سَب۪يلِ اللّٰهِ اَمْوَاتٌۜ بَلْ اَحْيَٓاءٌ وَلٰكِنْ لَا تَشْعُرُونَ", translation: "Allah yolunda öldürülenler için, \"ölüler\" demeyin. Bilakis, onlar \"diri\"dirler, ancak siz, bunu kavrayamazsınız.", transliteration: "Ve la tekulu li men yuktelu fi sebilillahi emvat, bel ehyaun ve lakin la teş'urun."},
  {id: 162, sure_no: 155, ayet_no: 155, sure_adi: "Bakara", text: "وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِنَ الْخَوْفِ وَالْجُوعِ وَنَقْصٍ مِنَ الْاَمْوَالِ وَالْاَنْفُسِ وَالثَّمَرَاتِۜ وَبَشِّرِ الصَّابِر۪ينَۙ", translation: "Kesinlikle sizi belalandıracağız: Korku ve açlıkla; mallarınızı, canlarınızı ve ürünlerinizi kayba uğratarak. Sabredenleri müjdele.", transliteration: "Ve le nebluvennekum bi şey'in minel havfi vel cui ve naksın minel emvali vel enfusi ves semerat, ve beşşiris sabirin."},
  {id: 163, sure_no: 156, ayet_no: 156, sure_adi: "Bakara", text: "اَلَّذ۪ينَ اِذَٓا اَصَابَتْهُمْ مُص۪يبَةٌۙ قَالُٓوا اِنَّا لِلّٰهِ وَاِنَّٓا اِلَيْهِ رَاجِعُونَۜ", translation: "Onlar, bir musibetle karşılaştıkları zaman, \"Bizler, Allah için varız ve muhakkak O'na döneceğiz.\" derler.", transliteration: "Ellezine iza esabethum musibetun, kalu inna lillahi ve inna ileyhi raciun."},
  {id: 164, sure_no: 157, ayet_no: 157, sure_adi: "Bakara", text: "اُو۬لٰٓئِكَ عَلَيْهِمْ صَلَوَاتٌ مِنْ رَبِّهِمْ وَرَحْمَةٌ وَاُو۬لٰٓئِكَ هُمُ الْمُهْتَدُونَ", translation: "İşte Rabb'lerinden, onlara salatlar ve rahmet vardır. İşte onlar, doğru yolu bulanlardır.", transliteration: "Ulaike aleyhim salavatun min rabbihim ve rahmetun ve ulaike humul muhtedun."},
  {id: 165, sure_no: 158, ayet_no: 158, sure_adi: "Bakara", text: "اِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَٓائِرِ اللّٰهِۚ فَمَنْ حَجَّ الْبَيْتَ اَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ اَنْ يَطَّوَّفَ بِهِمَاۜ وَمَنْ تَطَوَّعَ خَيْراًۙ فَاِنَّ اللّٰهَ شَاكِرٌ عَل۪يمٌ", translation: "Kuşkusuz Safa ile Merve Allah'ın belirlediği nişanelerdir. Kim hacc ya da umre amacıyla Beyt'i ziyaret ederse, ikisini de tavaf etmesinde bir sakınca yoktur. Ve yine kim gönülden bir iyilik yaparsa; kuşkusuz, Allah Karşılık Veren'dir, Her Şeyi Bilen'dir.", transliteration: "İnnes safa vel mervete min şeairillah, fe men haccel beyte evı'temera fe la cunaha aleyhi en yettavvefe bi hima ve men tetavvaa hayran, fe innallahe şakirun alim."},
  {id: 166, sure_no: 159, ayet_no: 159, sure_adi: "Bakara", text: "اِنَّ الَّذ۪ينَ يَكْتُمُونَ مَٓا اَنْزَلْنَا مِنَ الْبَيِّنَاتِ وَالْهُدٰى مِنْ بَعْدِ مَا بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتَابِۙ اُو۬لٰٓئِكَ يَلْعَنُهُمُ اللّٰهُ وَيَلْعَنُهُمُ اللَّاعِنُونَۙ", translation: "Bizim indirdiğimiz apaçık kanıtları ve hidayeti; insanlara Kitap'ta açıkça gösterdikten sonra, onları gizleyenler var ya! Onlara hem Allah lanet eder hem de bütün lanet edebilenler lanet ederler.", transliteration: "İnnellezine yektumune ma enzelna min el beyyinati vel huda min ba'di ma beyyennahu lin nasi fil kitabi, ulaike yel'anuhumullahu ve yel'anuhumul lainun."},
  {id: 167, sure_no: 160, ayet_no: 160, sure_adi: "Bakara", text: "اِلَّا الَّذ۪ينَ تَابُوا وَاَصْلَحُوا وَبَيَّنُوا فَاُو۬لٰٓئِكَ اَتُوبُ عَلَيْهِمْۚ وَاَنَا التَّوَّابُ الرَّح۪يمُ", translation: "Ancak tevbe edip, kendisini düzelten ve gerçeği gizlemeyenlerin tevbelerini kabul ederim. Zira Ben, Tevbeleri Kabul Eden'im, Rahmetim Kesintisiz'dir.", transliteration: "İllellezine tabu ve aslahu ve beyyenu fe ulaike etubu aleyhim, ve enet tevvabur rahim."},
  {id: 168, sure_no: 161, ayet_no: 161, sure_adi: "Bakara", text: "اِنَّ الَّذ۪ينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ اُو۬لٰٓئِكَ عَلَيْهِمْ لَعْنَةُ اللّٰهِ وَالْمَلٰٓئِكَةِ وَالنَّاسِ اَجْمَع۪ينَۙ", translation: "Kafir olup da kafir olarak ölen kimselere gelince; işte, Allah'ın, meleklerin ve bütün insanların laneti onların üzerinedir.", transliteration: "İnnellezine keferu ve matu ve hum kuffarun ulaike aleyhim la'netullahi vel melaiketi ven nasi ecmain."},
  {id: 169, sure_no: 162, ayet_no: 162, sure_adi: "Bakara", text: "خَالِد۪ينَ ف۪يهَاۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُونَ", translation: "Onlar, bu halde sürekli kalacaklardır. Onlardan ne azap hafifletilecek ve ne de onların yüzlerine bakılacak.", transliteration: "Halidine fiha, la yuhaffefu anhumul azabu ve la hum yunzarun."},
  {id: 170, sure_no: 163, ayet_no: 163, sure_adi: "Bakara", text: "وَاِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ  لَٓا اِلٰهَ اِلَّا هُوَ الرَّحْمٰنُ الرَّح۪يمُ۟", translation: "Sizin ilahınız bir tek ilahtır. O'ndan başka ilah yoktur. O, Rahmeti Bol ve Kesintisiz Olan'dır.", transliteration: "Ve ilahukum ilahun vahid, la ilahe illa huver rahmanur rahim."},
  {id: 171, sure_no: 164, ayet_no: 164, sure_adi: "Bakara", text: "اِنَّ ف۪ي خَلْقِ السَّمٰوَاتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّت۪ي تَجْر۪ي فِي الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ وَمَٓا اَنْزَلَ اللّٰهُ مِنَ السَّمَٓاءِ مِنْ مَٓاءٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ ف۪يهَا مِنْ كُلِّ دَٓابَّةٍۖ وَتَصْر۪يفِ الرِّيَاحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَٓاءِ وَالْاَرْضِ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَ", translation: "Göklerin ve yerin yaradılışında, gece ile gündüzün birbirlerini ardı sıra takip etmesinde; insanların yararlanmaları için denizde yüzen gemilerde, Allah'ın gökten indirip, onunla ölü toprağa hayat vererek, orada her türlü canlının yaşamasını sağladığı suda, rüzgarın yönlendirilmesinde, emre hazır bulutların yer ile gök arasında hareket ettirilmesinde aklını kullanan bir halk için birçok kanıt vardır.", transliteration: "İnne fi halkıs semavati vel ardı vahtilafil leyli ven nehari vel fulkilleti tecri fil bahri bima yenfeun nase ve ma enzelallahu mines semai min main fe ahya bihil arda ba'de mevtiha ve besse fiha min kulli dabbe, ve tasrifir riyahı ves sehabil musahhari beynes semai vel ardı le ayatin li kavmin ya'kılun."},
  {id: 172, sure_no: 165, ayet_no: 165, sure_adi: "Bakara", text: "وَمِنَ النَّاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللّٰهِ اَنْدَاداً يُحِبُّونَهُمْ كَحُبِّ اللّٰهِۜ وَالَّذ۪ينَ اٰمَنُٓوا اَشَدُّ حُباًّ لِلّٰهِۜ وَلَوْ يَرَى الَّذ۪ينَ ظَلَمُٓوا اِذْ يَرَوْنَ الْعَذَابَۙ اَنَّ الْقُوَّةَ لِلّٰهِ جَم۪يعاًۙ وَاَنَّ اللّٰهَ شَد۪يدُ الْعَذَابِ", translation: "Bazı insanlar, Allah'ın yanı sıra başka varlıkları O'na denk tutarlar ve onları Allah'ı sevdiği gibi severler. İman Edenlerin, Allah'ı sevmeleri ise her türlü sevgiden daha üstündür. Zulmedenler, azabı gördükleri zaman kuvvetin bütünüyle Allah'a ait olduğunu ve Allah'ın azabının çok şiddetli olduğunu keşke daha önceden anlayabilselerdi!", transliteration: "Ve minen nasi men yettehızu min dunillahi endaden yuhıbbunehum ke hubbillah, vellezine amenu eşeddu hubben lillah, ve lev yerallezine zalemu iz yeravnel azabe, ennel kuvvete lillahi cemian, ve ennellahe şedidul azab."},
  {id: 173, sure_no: 166, ayet_no: 166, sure_adi: "Bakara", text: "اِذْ تَبَرَّاَ الَّذ۪ينَ اتُّبِعُوا مِنَ الَّذ۪ينَ اتَّبَعُوا وَرَاَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْاَسْبَابُ", translation: "Kendilerine tabi olunanlar, azabı gördükleri zaman, tabi olanlardan uzaklaşacak ve aralarındaki bütün bağlar kopacaktır.", transliteration: "İz teberreellezinettubiu minellezinettebeu ve reavul azabe ve takattaat bihimul esbab."},
  {id: 174, sure_no: 167, ayet_no: 167, sure_adi: "Bakara", text: "وَقَالَ الَّذ۪ينَ اتَّبَعُوا لَوْ اَنَّ لَنَا كَرَّةً فَنَتَبَرَّاَ مِنْهُمْ كَمَا تَبَرَّؤُ۫ا مِنَّاۜ كَذٰلِكَ يُر۪يهِمُ اللّٰهُ اَعْمَالَهُمْ حَسَرَاتٍ عَلَيْهِمْۜ وَمَا هُمْ بِخَارِج۪ينَ مِنَ النَّارِ۟", translation: "Tabi olanlar, diyecekler ki: \"Keşke bize, tekrar dünyaya dönüş olsaydı da onların şimdi bizden uzaklaştıkları gibi biz de onlardan uzak dursaydık.\" Böylece, Allah, pişmanlıklarına sebep olan bütün bu yaptıklarının sonucunu onlara gösterir. Ve onlar ateşten çıkacak da değillerdir.", transliteration: "Ve kalellezinettebeu lev enne lena kerreten fe neteberree minhum kema teberreu minna kezalike yurihimullahu a'malehum haseratin aleyhim ve ma hum bi haricine minen nar."},
  {id: 175, sure_no: 168, ayet_no: 168, sure_adi: "Bakara", text: "يَٓا اَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الْاَرْضِ حَـلَالاً طَـيِّباًۘ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌ", translation: "Ey insanlar! Yeryüzünde bulunan şeylerin helal ve tayyib olanlarından yiyin. Şeytanın izinden gitmeyin, çünkü o sizin apaçık düşmanınızdır.", transliteration: "Ya eyyuhen nasu kulu mimma fil ardı halalen tayyiben, ve la tettebiu hutuvatiş şeytan, innehu lekum aduvvun mubin."},
  {id: 176, sure_no: 169, ayet_no: 169, sure_adi: "Bakara", text: "اِنَّمَا يَأْمُرُكُمْ بِالسُّٓوءِ وَالْفَحْشَٓاءِ وَاَنْ تَقُولُوا عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "Şeytan size, ancak kötülüğü, fahşayı ve Allah'a karşı bilmediğiniz şeyleri söylemeyi telkin eder.", transliteration: "İnnema ye'murukum bis sui vel fahşai ve en tekulu alallahi ma la ta'lemun."},
  {id: 177, sure_no: 170, ayet_no: 170, sure_adi: "Bakara", text: "وَاِذَا ق۪يلَ لَهُمُ اتَّبِعُوا مَٓا اَنْزَلَ اللّٰهُ قَالُوا بَلْ نَـتَّبِـعُ مَٓا اَلْفَيْنَا عَلَيْهِ اٰبَٓاءَنَاۜ اَوَلَوْ كَانَ اٰبَٓاؤُ۬هُمْ لَا يَعْقِلُونَ شَيْـٔاً وَلَا يَهْتَدُونَ", translation: "Onlara, \"Allah'ın indirdiğine uyun.\" denildiği zaman, onlar: \"Hayır! Biz, atalarımızdan gördüğümüz şeylere uyarız.\" derler. Ya ataları akıllarını kullanmayan ve doğru yolu bulamamış kimselerse?", transliteration: "Ve iza kile lehumuttebiu ma enzelallahu kalu bel nettebiu ma elfeyna aleyhi abaena e ve lev kane abauhum la ya'kılune şey'en ve la yehtedun."},
  {id: 178, sure_no: 171, ayet_no: 171, sure_adi: "Bakara", text: "وَمَثَلُ الَّذ۪ينَ كَفَرُوا كَمَثَلِ الَّذ۪ي يَنْعِقُ بِمَا لَا يَسْمَعُ اِلَّا دُعَٓاءً وَنِدَٓاءًۜ صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُونَ", translation: "Kafirlerin durumu, çağırmaktan, seslenmekten başka bir şey duymayan haykıran kimsenin durumu gibidir. Onlar; sağırdırlar, dilsizdirler, kördürler. Bu nedenle, akıllarını da kullanmazlar.", transliteration: "Ve meselullezine keferu ke meselillezi yen'ıku bi ma la yesmeu illa duaen ve nidaa, summun bukmun umyun fe hum la ya'kılun."},
  {id: 179, sure_no: 172, ayet_no: 172, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَاشْكُرُوا لِلّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُونَ", translation: "Ey İman Edenler! Eğer, sadece O'na kulluk ediyorsanız, size rızık olarak verdiğimiz şeylerin sağlıklı, temiz, ve iyi olanlarından yiyin ve Allah'a şükredin.", transliteration: "Ya eyyuhellezine amenu kulu min tayyibati ma razaknakum veşkuru lillahi in kuntum iyyahu ta'budun."},
  {id: 180, sure_no: 173, ayet_no: 173, sure_adi: "Bakara", text: "اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْز۪يرِ وَمَٓا اُهِلَّ بِه۪ لِغَيْرِ اللّٰهِۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَلَٓا اِثْمَ عَلَيْهِۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "O; size, sadece ölü hayvan etini, kanı, domuz etini ve Allah'tan başkası adına kesilenleri haram kıldı. Mecbur kalan bir kimsenin, haddi aşmamak ve istismar etmemek koşuluyla, bunları yemesinde bir günah yoktur. Kuşkusuz, Allah'ın Rahmeti Bol ve Kesintisiz'dir.", transliteration: "İnnema harrame aleykumul meytete ved deme ve lahmel hınziri ve ma uhille bihi li gayrillah, fe menidturra gayra bagin ve la adin fe la isme aleyh, innallahe gafurun rahim."},
  {id: 181, sure_no: 174, ayet_no: 174, sure_adi: "Bakara", text: "اِنَّ الَّذ۪ينَ يَكْتُمُونَ مَٓا اَنْزَلَ اللّٰهُ مِنَ الْكِتَابِ وَيَشْتَرُونَ بِه۪ ثَمَناً قَل۪يلاًۙ اُو۬لٰٓئِكَ مَا يَأْكُلُونَ ف۪ي بُطُونِهِمْ اِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللّٰهُ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكّ۪يهِمْۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Allah'ın indirdiği Kitap'tan bir kısmını gizleyip, onu az bir değer karşılığında değiştirenler var ya, işte onlar, bununla karınlarına ateşten başka bir şey doldurmuyorlar. Allah, onlarla kıyamet gününde ne konuşacak ne de onları temize çıkaracaktır. Ve onlar için can yakıcı bir azap vardır.", transliteration: "İnnellezine yektumune ma enzelallahu minel kitabi ve yeşterune bihi semenen kalilen, ulaike ma ye'kulune fi butunihim illen nare ve la yukellimuhumullahu yevmel kıyameti ve la yuzekkihim, ve lehum azabun elim."},
  {id: 182, sure_no: 175, ayet_no: 175, sure_adi: "Bakara", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدٰى وَالْعَذَابَ بِالْمَغْفِرَةِۚ فَمَٓا اَصْبَرَهُمْ عَلَى النَّارِ", translation: "İşte onlar, hidayete karşılık sapkınlığı satın alanlardır; bağışlanmaya karşılık azabı. Ateşe ne kadar dayanıklıdırlar!", transliteration: "Ulaikellezineşteravud dalalete bil huda vel azabe bil magfireh, fe ma asberehum alen nar."},
  {id: 183, sure_no: 176, ayet_no: 176, sure_adi: "Bakara", text: "ذٰلِكَ بِاَنَّ اللّٰهَ نَزَّلَ الْكِتَابَ بِالْحَقِّۜ وَاِنَّ الَّذ۪ينَ اخْتَلَفُوا فِي الْكِتَابِ لَف۪ي شِقَاقٍ بَع۪يدٍ۟", translation: "İşte bu, Allah'ın Hakk olarak indirdiği Kitap hakkında, onların anlaşmazlığa düşmeleri ve derin bir ayrılık içinde olmalarındandır.", transliteration: "Zalike bi ennellahe nezzelel kitabe bil hakk, ve innellezinahtelefu fil kitabi le fi şikakin baid."},
  {id: 184, sure_no: 177, ayet_no: 177, sure_adi: "Bakara", text: "لَيْسَ الْبِرَّ اَنْ تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلٰكِنَّ الْبِرَّ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَالْمَلٰٓئِكَةِ وَالْكِتَابِ وَالنَّبِيّ۪نَۚ وَاٰتَى الْمَالَ عَلٰى حُبِّه۪ ذَوِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينَ وَابْنَ السَّب۪يلِ وَالسَّٓائِل۪ينَ وَفِي الرِّقَابِۚ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَۚ وَالْمُوفُونَ بِعَهْدِهِمْ اِذَا عَاهَدُواۚ وَالصَّابِر۪ينَ فِي الْبَأْسَٓاءِ وَالضَّرَّٓاءِ وَح۪ينَ الْبَأْسِۜ اُو۬لٰٓئِكَ الَّذ۪ينَ صَدَقُواۜ وَاُو۬لٰٓئِكَ هُمُ الْمُتَّقُونَ", translation: "Yüzünüzü doğu ve batı tarafına çevirmeniz birr değildir. Ama birr: Allah'a, ahiret gününe, meleklere, Kitaplara ve Nebilere iman etmek; malını sevdiği halde onu yakınlarına, yetimlere, yoksullara, yol oğluna, yardım isteyenlere, rikab olanlara vermek; \"salatı ikame etmek, zekat yapmak,\" söz verdiği zaman sözünü yerine getirmek, sıkıntıda, zorlukta ve felakete uğrama durumunda sabretmektir. İşte bunlar, sadık olanlardır. Ve işte bunlar, takva sahibi olanlardır.", transliteration: "Leysel birre en tuvellu vucuhekum kıbelel maşrıkı vel magrıbi ve lakinnel birre men amene billahi vel yevmil ahırı vel melaiketi vel kitabi ven nebiyyin, ve atel male ala hubbihi zevil kurba vel yetama vel mesakine vebnes sebili, ves sailine ve fir rıkab, ve ekames salate ve atez zekat, vel mufune bi ahdihim iza ahed, ves sabirine fil be'sai ved darrai ve hinel be's ulaikellezine sadaku, ve ulaike humul muttekun."},
  {id: 185, sure_no: 178, ayet_no: 178, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلٰىۜ اَلْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْاُنْثٰى بِالْاُنْثٰىۜ فَمَنْ عُفِيَ لَهُ مِنْ اَخ۪يهِ شَيْءٌ فَاتِّبَاعٌ بِالْمَعْرُوفِ وَاَدَٓاءٌ اِلَيْهِ بِاِحْسَانٍۜ ذٰلِكَ تَخْف۪يفٌ مِنْ رَبِّكُمْ وَرَحْمَةٌۜ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهُ عَذَابٌ اَل۪يمٌ", translation: "Ey İman Edenler! Öldürülmelerde size kısas yazıldı; hüre hür, abde abd, kadına kadın. Ancak öldüren, öldürülenin kardeşi tarafından bağışlanırsa; örfe uygun şekilde, diyeti iyilikle ödemelidir. Bu Rabb'inizden bir hafifletme ve rahmettir. Bundan sonra kim haddi aşarsa, onun için can yakıcı bir azap vardır.", transliteration: "Ya eyyuhellezine amenu kutibe aleykumul kısasu fil katla el hurru bil hurri vel abdu bil abdi vel unsa bil unsa fe men ufiye lehu min ahihi şey'un fettibaun bil ma'rufi ve edaun ileyhi bi ihsan, zalike tahfifun min rabbikum ve rahmeh, fe meni'teda ba'de zalike fe lehu azabun elim."},
  {id: 186, sure_no: 179, ayet_no: 179, sure_adi: "Bakara", text: "وَلَكُمْ فِي الْقِصَاصِ حَيٰوةٌ يَٓا اُو۬لِي الْاَلْبَابِ لَعَلَّكُمْ تَتَّقُونَ", translation: "Ey sağlıklı düşünen, sağduyu sahipleri! Kısasta sizin için hayat vardır. Umulur ki takva sahibi olursunuz.", transliteration: "Ve lekum fil kısası hayatun ya ulil elbabi leallekum tettekun."},
  {id: 187, sure_no: 180, ayet_no: 180, sure_adi: "Bakara", text: "كُتِبَ عَلَيْكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ اِنْ تَرَكَ خَيْراًۚ اَلْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْاَقْرَب۪ينَ بِالْمَعْرُوفِۚ حَقاًّ عَلَى الْمُتَّق۪ينَۜ", translation: "Birinize ölüm geldiği zaman; eğer geride bir hayır bırakıyorsa, anneye, babaya, yakın akrabaya örfe uygun bir şekilde vasiyette bulunmak, takva sahibi olanların üzerine bir hak olarak yazıldı.", transliteration: "Kutibe aleykum iza hadara ehadekumul mevtu in tereke hayra, el vasiyyetu lil valideyni vel akrabine bil ma'ruf, hakkan alel muttekin."},
  {id: 188, sure_no: 181, ayet_no: 181, sure_adi: "Bakara", text: "فَمَنْ بَدَّلَهُ بَعْدَ مَا سَمِعَهُ فَاِنَّمَٓا اِثْمُهُ عَلَى الَّذ۪ينَ يُبَدِّلُونَهُۜ اِنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌۜ", translation: "Her kim, bu vasiyeti duyduktan sonra onu değiştirirse, doğrusu onun günahı değiştirenin üzerinedir. Kuşkusuz Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Fe men beddelehu ba'de ma semiahu fe innema ismuhu alellezine yubeddiluneh, innallahe semiun alim."},
  {id: 189, sure_no: 182, ayet_no: 182, sure_adi: "Bakara", text: "فَمَنْ خَافَ مِنْ مُوصٍ جَنَفاً اَوْ اِثْماً فَاَصْلَحَ بَيْنَهُمْ فَلَٓا اِثْمَ عَلَيْهِۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟", translation: "Kim, vasiyet edenin yanılacağından veya günaha gireceğinden endişe ederse, taraflar arasında uzlaşma sağlamasında, ona bir günah yoktur. Kuşkusuz Allah Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Fe men hafe min musın cenefen ev ismen fe aslaha beynehum fe la isme aleyh, innallahe gafurun rahim."},
  {id: 190, sure_no: 183, ayet_no: 183, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذ۪ينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَۙ", translation: "Ey İman Edenler! Sizden öncekilere yazıldığı gibi, siyam size de yazıldı. Umulur ki takva sahibi olursunuz.", transliteration: "Ya eyyuhellezine amenu kutibe aleykumus sıyamu kema kutibe alellezine min kablikum leallekum tettekun."},
  {id: 191, sure_no: 184, ayet_no: 184, sure_adi: "Bakara", text: "اَيَّاماً مَعْدُودَاتٍۜ فَمَنْ كَانَ مِنْكُمْ مَر۪يضاً اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِنْ اَيَّامٍ اُخَرَۜ وَعَلَى الَّذ۪ينَ يُط۪يقُونَهُ فِدْيَةٌ طَعَامُ مِسْك۪ينٍۜ فَمَنْ تَطَوَّعَ خَيْراً فَهُوَ خَيْرٌ لَهُۜ وَاَنْ تَصُومُوا خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "Sayılı günlerdir. Artık sizden kim hastalanır veya seferde olursa, tutamadığı günler sayısınca başka günlerde tutsun. Onu tutmaya gücü yetenlerin, bir yoksulu doyuracak kadar fidye vermesi gerekir. Böyle olmakla birlikte, kim gönlünden gelerek daha fazlasını yaparsa, bu, onun için daha hayırlıdır. Eğer bilirseniz, siyam yapmanız sizin için hayırlı olandır.", transliteration: "Eyyamen ma'dudat, fe men kane minkum maridan ev ala seferin fe iddetun min eyyamin uhar ve alellezine yutikunehu fidyetun taamu miskin, fe men tatavvaa hayran fe huve hayrun leh, ve en tesumu hayrun lekum in kuntum ta'lemun."},
  {id: 192, sure_no: 185, ayet_no: 185, sure_adi: "Bakara", text: "شَهْرُ رَمَضَانَ الَّذ۪ٓي اُنْزِلَ ف۪يهِ الْقُرْاٰنُ هُدًى لِلنَّاسِ وَبَيِّنَاتٍ مِنَ الْهُدٰى وَالْفُرْقَانِۚ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُۜ وَمَنْ كَانَ مَر۪يضاً اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِنْ اَيَّامٍ اُخَرَۜ يُر۪يدُ اللّٰهُ بِكُمُ الْيُسْرَ وَلَا يُر۪يدُ بِكُمُ الْعُسْرَۘ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰيكُمْ وَلَعَلَّكُمْ تَشْكُرُونَ", translation: "Ramazan ayı ki: İnsanlar için hidayet rehberi olan, doğru yola ileten, doğru ile yanlışı birbirinden ayıran; apaçık kanıtları içeren Kur'an o ayda indirildi. Sizden, kim bu aya erişirse savm/siyam yapsın. Kim de hasta veya yolcu olursa, tutamadığı günler sayısınca başka günlerde tutsun. Allah, sizin için zorluk değil kolaylık diler. Allah, belirlenen günlerin sayısını tamamlamanızı ve size doğru yolu gösterdiğinden, Kendisini yüceltmenizi ister ki böylece şükretmiş olursunuz.", transliteration: "Şehru ramadanellezi unzile fihil kur'anu huden lin nasi ve beyyinatin minel huda vel furkan, fe men şehide minkumuş şehra fel yesumh, ve men kane maridan ev ala seferin fe iddetun min eyyamin uhar yuridullahu bikumul yusra ve la yuridu bikumul usra, ve li tukmilul iddete ve li tukebbirullahe ala ma hedakum ve leallekum teşkurun."},
  {id: 193, sure_no: 186, ayet_no: 186, sure_adi: "Bakara", text: "وَاِذَا سَاَلَكَ عِبَاد۪ي عَنّ۪ي فَاِنّ۪ي قَر۪يبٌۜ اُج۪يبُ دَعْوَةَ الدَّاعِ اِذَا دَعَانِۙ فَلْيَسْتَج۪يبُوا ل۪ي وَلْيُؤْمِنُوا ب۪ي لَعَلَّهُمْ يَرْشُدُونَ", translation: "Kullarım sana, Ben'i sorarlarsa bilsinler ki Ben, yakınım. Bana dua edenin, duasına karşılık veririm. O halde onlar da Benim çağrıma uysunlar ve Bana gerçek anlamda iman etsinler. Böylece irşad olurlar.", transliteration: "Ve iza seeleke ıbadi anni fe inni karib ucibu da'veted dai iza deani, fel yestecibu li vel yu'minu bi leallehum yerşudun."},
  {id: 194, sure_no: 187, ayet_no: 187, sure_adi: "Bakara", text: "اُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ اِلٰى نِسَٓائِكُمْۜ هُنَّ لِبَاسٌ لَكُمْ وَاَنْتُمْ لِبَاسٌ لَهُنَّۜ عَلِمَ اللّٰهُ اَنَّكُمْ كُنْتُمْ تَخْتَانُونَ اَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْۚ فَالْـٰٔنَ بَاشِرُوهُنَّ وَابْتَغُوا مَا كَتَبَ اللّٰهُ لَكُمْۖ وَكُلُوا وَاشْرَبُوا حَتّٰى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْاَبْيَضُ مِنَ الْخَيْطِ الْاَسْوَدِ مِنَ الْفَجْرِۖ ثُمَّ اَتِمُّوا الصِّيَامَ اِلَى الَّيْلِۚ وَلَا تُبَاشِرُوهُنَّ وَاَنْتُمْ عَاكِفُونَۙ فِي الْمَسَاجِدِۜ تِلْكَ حُدُودُ اللّٰهِ فَلَا تَقْرَبُوهَاۜ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ اٰيَاتِه۪ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَ", translation: "Siyam gecesinde kadınlarınıza yaklaşmanız sizin için helal kılındı. Onlar, sizin için örtüdür; siz de onlar için örtüsünüz. Allah, nefsinize sahip olmadığınızı bildi ve tevbenizi kabul edip sizi bağışladı. Artık onlara yaklaşın, Allah'ın sizin için yazdığı şeyi isteyin. Şafak vaktinin siyah ipliği, beyaz ipliğinden ayırt edilme anına kadar, yiyin için. Sonra da geceye dek siyamı tamamlayın. Eğer mescitlerde itikaftaysanız onlara yaklaşmayın. Bunlar, Allah'ın koyduğu sınırlardır. Bu sınırlara yaklaşmayın. İşte böylece Allah, insanlara ayetlerini açıklar. Umulur ki takva sahibi olursunuz.", transliteration: "Uhılle lekum leyletes sıyamir refesu ila nisaikum hunne libasun lekum ve entum libasun lehun alimallahu ennekum kuntum tahtanune enfusekum fe tabe aleykum ve afa ankum, fel ane başiruhunne vebtegu ma keteballahu lekum, ve kulu veşrabu hatta yetebeyyene lekumul haytul ebyadu minel haytıl esvedi minel fecri, summe etimmus sıyame ilel leyli, ve la tubaşiruhunne ve entum akifune fil mesacid, tilke hududullahi fe la takrabuha kezalike yubeyyinullahu ayatihi lin nasi leallehum yettekun."},
  {id: 195, sure_no: 188, ayet_no: 188, sure_adi: "Bakara", text: "وَلَا تَأْكُلُٓوا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوا بِهَٓا اِلَى الْحُكَّامِ لِتَأْكُلُوا فَر۪يقاً مِنْ اَمْوَالِ النَّاسِ بِالْاِثْمِ وَاَنْتُمْ تَعْلَمُونَ۟", translation: "Birbirinizin mallarını haksız şekilde yemeyin. Günah olduğunu bildiğiniz halde, başkasının bir kısım mallarına haksız yere sahip olabilmek için hukuki hileye başvurmayın.", transliteration: "Ve la te'kulu emvalekum beynekum bil batılı ve tudlu biha ilel hukkami li te'kulu ferikan min emvalin nasi bil ismi ve entum ta'lemun."},
  {id: 196, sure_no: 189, ayet_no: 189, sure_adi: "Bakara", text: "يَسْـَٔلُونَكَ عَنِ الْاَهِلَّةِۜ قُلْ هِيَ مَوَاق۪يتُ لِلنَّاسِ وَالْحَجِّۜ وَلَيْسَ الْبِرُّ بِاَنْ تَأْتُوا الْبُيُوتَ مِنْ ظُهُورِهَا وَلٰكِنَّ الْبِرَّ مَنِ اتَّقٰىۚ وَأْتُوا الْبُيُوتَ مِنْ اَبْوَابِهَاۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَ", translation: "Sana, ayın evrelerini soruyorlar. De ki: \"O, insanlar ve hacc için bir zaman ölçüsüdür.\" Evlerinize arkalarından girmeniz birr değildir; birr, takvalı davranmaktır. Öyleyse evlerinize kapılarından girin. Allah'a karşı takvalı olun ki kurtuluşa erebilesiniz.", transliteration: "Yes'eluneke anil ehilleh, kul hiye mevakitu lin nasi vel hacc, ve leysel birru bi en te'tul buyute min zuhuriha ve lakinnel birre menitteka, ve'tul buyute min ebvabiha, vettekullahe leallekum tuflihun."},
  {id: 197, sure_no: 190, ayet_no: 190, sure_adi: "Bakara", text: "وَقَاتِلُوا ف۪ي سَب۪يلِ اللّٰهِ الَّذ۪ينَ يُقَاتِلُونَكُمْ وَلَا تَعْتَدُواۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَد۪ينَ", translation: "Sizinle savaşanlara karşı siz de Allah yolunda onlarla savaşın. Ama haddi aşmayın. Kuşkusuz Allah, haddi aşanları sevmez.", transliteration: "Ve katilu fi sebilillahillezine yukatilunekum ve la ta'tedu innallahe la yuhıbbul mu'tedin."},
  {id: 198, sure_no: 191, ayet_no: 191, sure_adi: "Bakara", text: "وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَاَخْرِجُوهُمْ مِنْ حَيْثُ اَخْرَجُوكُمْ وَالْفِتْنَةُ اَشَدُّ مِنَ الْقَتْلِۚ وَلَا تُقَاتِلُوهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتّٰى يُقَاتِلُوكُمْ ف۪يهِۚ فَاِنْ قَاتَلُوكُمْ فَاقْتُلُوهُمْۜ  كَذٰلِكَ جَزَٓاءُ الْكَافِر۪ينَ", translation: "Onları yakaladığınız yerde öldürün. Sizi çıkardıkları yerden siz de onları çıkartın. Zaten, fitne öldürmekten daha kötüdür. Onlar, Mescid-i Haram çevresinde sizinle savaşmadıkça siz de onlarla savaşmayın. Eğer savaşırlarsa, siz de savaşın. İşte Kafirlere verilecek karşılık böyledir.", transliteration: "Vaktuluhum haysu sekıftumuhum ve ahricuhum min haysu ahracukum vel fitnetu eşeddu minel katli, ve la tukatiluhum indel mescidil harami hatta yukatilukum fih, fe in katelukum faktuluhum kezalike cezaul kafirin."},
  {id: 199, sure_no: 192, ayet_no: 192, sure_adi: "Bakara", text: "فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Eğer vazgeçerlerse, kuşkusuz, Allah, Bağışlayıcı, Rahmeti Kesintisiz Olan'dır.", transliteration: "Fe inintehev fe innallahe gafurun rahim."},
  {id: 200, sure_no: 193, ayet_no: 193, sure_adi: "Bakara", text: "وَقَاتِلُوهُمْ حَتّٰى لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدّ۪ينُ لِلّٰهِۜ  فَاِنِ انْتَهَوْا فَلَا عُدْوَانَ اِلَّا عَلَى الظَّالِم۪ينَ", translation: "Fitne kalmayıp, din tamamıyla Allah'ın oluncaya kadar onlarla savaşın. Eğer vazgeçerlerse zalimlerden başkasına düşmanlık yoktur.", transliteration: "Ve katiluhum hatta la tekune fitnetun ve yekuned dinu lillah, fe inintehev fe la udvane illa alez zalimin."},
  {id: 201, sure_no: 194, ayet_no: 194, sure_adi: "Bakara", text: "اَلشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمَاتُ قِصَاصٌۜ فَمَنِ اعْتَدٰى عَلَيْكُمْ فَاعْتَدُوا عَلَيْهِ بِمِثْلِ مَا اعْتَدٰى عَلَيْكُمْۖ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ مَعَ الْمُتَّق۪ينَ", translation: "Haram ay, haram aya karşılıktır. Hürmetler de karşılıklıdır. Kim, size saldırırsa siz de onlara aynı şekilde karşılık verin. Allah'a karşı takvalı olun. Ve iyi bilin ki Allah, takvalı olanlarla beraberdir.", transliteration: "Eş şehrul haramu biş şehril harami vel hurumatu kısas, fe meni'teda aleykum fa'tedu aleyhi bi misli ma'teda aleykum, vettekullahe va'lemu ennellahe meal muttekin."},
  {id: 202, sure_no: 195, ayet_no: 195, sure_adi: "Bakara", text: "وَاَنْفِقُوا ف۪ي سَب۪يلِ اللّٰهِ وَلَا تُلْقُوا بِاَيْد۪يكُمْ اِلَى التَّهْلُكَةِۚۛ وَاَحْسِنُواۚۛ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِن۪ينَ", translation: "Allah yolunda malınızı infak edin. Kendi ellerinizle kendinizi tehlikeye atmayın. İyilik yapın. Kuşkusuz, Allah, iyilik yapanları sever.", transliteration: "Ve enfiku fi sebilillahi ve la tulku bi eydikum ilet tehluketi, ve ahsinu, innallahe yuhıbbul muhsinin."},
  {id: 203, sure_no: 196, ayet_no: 196, sure_adi: "Bakara", text: "وَاَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلّٰهِۜ فَاِنْ اُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ وَلَا تَحْلِقُوا رُؤُ۫سَكُمْ حَتّٰى يَبْلُغَ الْهَدْيُ مَحِلَّهُۜ   فَمَنْ كَانَ مِنْكُمْ مَر۪يضاً اَوْ بِه۪ٓ  اَذًى مِنْ رَأْسِه۪ فَفِدْيَةٌ مِنْ صِيَامٍ اَوْ صَدَقَةٍ اَوْ نُسُكٍۚ فَاِذَٓا اَمِنْتُمْ۠  فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ اِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ فِي الْحَجِّ وَسَبْعَةٍ اِذَا رَجَعْتُمْۜ  تِلْكَ عَشَرَةٌ   كَامِلَةٌۜ  ذٰلِكَ لِمَنْ لَمْ يَكُنْ اَهْلُهُ حَاضِرِي الْمَسْجِدِ الْحَرَامِۜ  وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ۟", translation: "Allah için haccı ve umreyi tam yapın. Eğer engellenirseniz, o zaman hediyeden kolayınıza gelen şeyi gönderin! Ancak hediye yerine ulaşıncaya kadar başınızı tıraş etmeyin. Sizden hasta olan veya başından bir rahatsızlığı bulunan; oruç tutmalı veya sadaka vermeli ya da nusuktan sayılacak bir fidye vermeli! Emin olduğunuz vakitte; kim, hacc vaktine kadar umre ile faydalanmak isterse, hediyeden kolayına geleni göndermeli! Fakat kim bulamazsa, hacc günlerinde üç, döndükten sonra da yedi gün olmak üzere toplam on gün oruç tutsun. Bu, ailesi Mescid-i Haram çevresinde oturmayanlar içindir. Allah'a karşı takvalı davranın. Ve bilin ki Allah'ın cezası çok şiddetlidir.", transliteration: "Ve etimmul hacce vel umrete lillah, fe in uhsirtum fe mesteysera minel hedyi ve la tahliku ruusekum hatta yeblugal hedyu mahilleh, fe men kane minkum maridan ev bihi ezen min ra'sihi fe fidyetun min sıyamin ev sadakatin ev nusuk fe iza emintum, fe men temettea bil umreti ilel haccı fe mesteysera minel hedyi, fe men lem yecid fe sıyamu selaseti eyyamin fil haccı ve seb'atin iza reca'tum tilke aşaratun kamileh, zalike li men lem yekun ehluhu hadırıl mescidil haram, vettekullahe va'lemu ennellahe şedidul ikab."},
  {id: 204, sure_no: 197, ayet_no: 197, sure_adi: "Bakara", text: "اَلْحَجُّ اَشْهُرٌ مَعْلُومَاتٌۚ فَمَنْ فَرَضَ ف۪يهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِي الْحَجِّۜ وَمَا تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللّٰهُۜ وَتَزَوَّدُوا فَاِنَّ خَيْرَ الزَّادِ التَّقْوٰىۘ وَاتَّقُونِ يَٓا اُو۬لِي الْاَلْبَابِ", translation: "Hacc, bilinen aylardır. Kim haccı farz edinirse; o esnada, uygunsuz davranmak, fasıklık yapmak ve kavga etmek yoktur. Ne hayır işlerseniz Allah onu bilir. Azık edinin. Kuşkusuz azığın en hayırlısı takvadır. Ey selim akıl sahipleri! Bana karşı takvalı olun!", transliteration: "El haccu eşhurun ma'lumat, fe men farada fihinnel hacca fe la refese ve la fusuka ve la cidale fil hacc, ve ma tef'alu min hayrın ya'lemhullah, ve tezevvedu fe inne hayraz zadit takva, vettekuni ya ulil elbab."},
  {id: 205, sure_no: 198, ayet_no: 198, sure_adi: "Bakara", text: "لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْۜ فَاِذَٓا اَفَضْتُمْ مِنْ عَرَفَاتٍ فَاذْكُرُوا اللّٰهَ عِنْدَ الْمَشْعَرِ الْحَرَامِۖ وَاذْكُرُوهُ كَمَا هَدٰيكُمْۚ وَاِنْ كُنْتُمْ مِنْ قَبْلِه۪ لَمِنَ الضَّٓالّ۪ينَ", translation: "Rabb'inizden lütuf istemenizde bir sakınca yoktur. Artık Arafat'tan ayrılıp, akın ettiğinizde Meş'ar-i Haram'da Allah'ı anın. O, size nasıl doğru yolu gösterdiyse, siz de O'nu öyle anın. Kuşkusuz, O, doğru yolu göstermeden önce siz sapkınlardandınız.", transliteration: "Leyse aleykum cunahun en tebtegu fadlan min rabbikum fe iza efadtum min arafatin fezkurullahe indel meş'aril haram, vezkuruhu kema hedakum, ve in kuntum min kablihi le mined dallin."},
  {id: 206, sure_no: 199, ayet_no: 199, sure_adi: "Bakara", text: "ثُمَّ اَف۪يضُوا مِنْ حَيْثُ اَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللّٰهَۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Sonra, insanların dağıldığı yerden siz de dağılın. Allah'tan bağışlanma dileyin. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Summe efidu min haysu efadan nasu vestagfirullah, innallahe gafurun rahim."},
  {id: 207, sure_no: 200, ayet_no: 200, sure_adi: "Bakara", text: "فَاِذَا قَضَيْتُمْ مَنَاسِكَكُمْ فَاذْكُرُوا اللّٰهَ كَذِكْرِكُمْ اٰبَٓاءَكُمْ اَوْ اَشَدَّ ذِكْراًۜ فَمِنَ النَّاسِ مَنْ يَقُولُ رَبَّنَٓا اٰتِنَا فِي الدُّنْيَا وَمَا لَهُ فِي الْاٰخِرَةِ مِنْ خَلَاقٍ", translation: "Hacc menasiklerinizi bitirince, atalarınızı andığınız gibi; hatta daha güçlü bir şekilde Allah'ı anın. Kimi insanlar; \"Ey Rabb'imiz, bize dünyada ver.\" derler. Bu kimselerin ahirette bir payı yoktur.", transliteration: "Fe iza kadaytum menasikekum fezkurullahe ke zikrikum abaekum ev eşedde zikra, fe minen nasi men yekulu rabbena atina fid dunya ve ma lehu fil ahirati min halak."},
  {id: 208, sure_no: 201, ayet_no: 201, sure_adi: "Bakara", text: "وَمِنْهُمْ مَنْ يَقُولُ رَبَّنَٓا اٰتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْاٰخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ", translation: "Kimileri de: \"Rabb'imiz, bize dünyada da iyilik, ahirette de iyilik ver ve bizi ateşin azabından koru.\" derler.", transliteration: "Ve minhum men yekulu rabbena atina fid dunya haseneten ve fil ahirati haseneten ve kına azaben nar."},
  {id: 209, sure_no: 202, ayet_no: 202, sure_adi: "Bakara", text: "اُو۬لٰٓئِكَ لَهُمْ نَص۪يبٌ مِمَّا كَسَبُواۜ وَاللّٰهُ سَر۪يعُ الْحِسَابِ", translation: "İşte bunların, kazandıklarına karşılık payları vardır. Allah, hesabı çabuk görendir.", transliteration: "Ulaike lehum nasibun mimma kesebu vallahu seriul hısab."},
  {id: 210, sure_no: 203, ayet_no: 203, sure_adi: "Bakara", text: "وَاذْكُرُوا اللّٰهَ ف۪ٓي اَيَّامٍ مَعْدُودَاتٍۜ فَمَنْ تَعَجَّلَ ف۪ي يَوْمَيْنِ فَلَٓا اِثْمَ عَلَيْهِۚ وَمَنْ تَاَخَّرَ فَلَٓا اِثْمَ عَلَيْهِۙ لِمَنِ اتَّقٰىۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّكُمْ اِلَيْهِ تُحْشَرُونَ", translation: "Sayılı günlerde Allah'ı anın. Kim acele edip, iki gün içinde dönerse ona günah yoktur. Kim geri kalırsa takvalı olduğu takdirde ona da günah yoktur. Allah'a karşı takvalı olun ve bilin ki O'nun huzurunda toplanacaksınız.", transliteration: "Vezkurullahe fi eyyamin ma'dudat, fe men teaccele fi yevmeyni fe la isme aleyh, ve men teahhara fe la isme aleyhi, li menitteka vettekullahe va'lemu ennekum ileyhi tuhşerun."},
  {id: 211, sure_no: 204, ayet_no: 204, sure_adi: "Bakara", text: "وَمِنَ النَّاسِ مَنْ يُعْجِبُكَ قَوْلُهُ فِي الْحَيٰوةِ الدُّنْيَا وَيُشْهِدُ اللّٰهَ عَلٰى مَا ف۪ي قَلْبِه۪ۙ وَهُوَ اَلَدُّ الْخِصَامِ", translation: "Kimi insanın, dünya hayatı ile ilgili sözü senin hoşuna gider. Kalbindekine Allah'ı şahit tutar. Oysaki o düşmanların en azılısıdır.", transliteration: "Ve minen nasi men yu'cibuke kavluhu fil hayatid dunya ve yuşhidullahe ala ma fi kalbihi, ve huve eleddul hısam."},
  {id: 212, sure_no: 205, ayet_no: 205, sure_adi: "Bakara", text: "وَاِذَا تَوَلّٰى سَعٰى فِي الْاَرْضِ لِيُفْسِدَ ف۪يهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَۜ وَاللّٰهُ لَا يُحِبُّ الْفَسَادَ", translation: "Senden ayrılınca, yeryüzünde bozgunculuk yapmaya, ürünü ve nesli yok etmeye çalışır. Allah ise bozgunculuğu sevmez.", transliteration: "Ve iza tevella sea fil ardı li yufside fiha ve yuhlikel harse ven nesl, vallahu la yuhıbbul fesad."},
  {id: 213, sure_no: 206, ayet_no: 206, sure_adi: "Bakara", text: "وَاِذَا ق۪يلَ لَهُ اتَّقِ اللّٰهَ اَخَذَتْهُ الْعِزَّةُ بِالْاِثْمِ فَحَسْبُهُ جَهَنَّمُۜ وَلَبِئْسَ الْمِهَادُ", translation: "Ona, \"Allah'a karşı takvalı ol.\" dendiği zaman, kendisini üstün görmesi onu günaha sevk eder. İşte böylesine Cehennem yeter. O, ne kötü bir döşektir!", transliteration: "Ve iza kile lehuttekıllahe ehazethul izzetu bil ismi fe hasbuhu cehennem, ve le bi'sel mihad."},
  {id: 214, sure_no: 207, ayet_no: 207, sure_adi: "Bakara", text: "وَمِنَ النَّاسِ مَنْ يَشْر۪ي نَفْسَهُ ابْتِغَٓاءَ مَرْضَاتِ اللّٰهِۜ وَاللّٰهُ رَؤُ۫فٌ بِالْعِبَادِ", translation: "Kimi insanlar da kendisini Allah'ın rızasını kazanmaya adar. Allah, kullarına karşı Çok Şefkatli'dir.", transliteration: "Ve minen nasi men yeşri nefsehubtigae mardatillah, vallahu raufun bil ıbad."},
  {id: 215, sure_no: 208, ayet_no: 208, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا ادْخُلُوا فِي السِّلْمِ كَٓافَّةًۖ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌ", translation: "Ey İman Edenler! Bir bütün olarak Silm'e girin. Şeytanın izinden gitmeyin. Kuşkusuz o, sizin apaçık düşmanınızdır.", transliteration: "Ya eyyuhellezine amenudhulu fis silmi kaffeh, ve la tettebiu hutuvatiş şeytan, innehu lekum aduvvun mubin."},
  {id: 216, sure_no: 209, ayet_no: 209, sure_adi: "Bakara", text: "فَاِنْ زَلَلْتُمْ مِنْ بَعْدِ مَا جَٓاءَتْكُمُ الْبَيِّنَاتُ فَاعْلَمُٓوا اَنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ", translation: "Size gelen açık kanıtlara rağmen yine de ayağınız kayarsa, şunu iyi bilin ki Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Fe in zeleltum min ba'di ma caetkumul beyyinatu fa'lemu ennallahe azizun hakim."},
  {id: 217, sure_no: 210, ayet_no: 210, sure_adi: "Bakara", text: "هَلْ يَنْظُرُونَ اِلَّٓا اَنْ يَأْتِيَهُمُ اللّٰهُ ف۪ي ظُلَلٍ مِنَ الْغَمَامِ وَالْمَلٰٓئِكَةُ وَقُضِيَ الْاَمْرُۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ۟", translation: "Yoksa onlar, Allah'ın ve meleklerin bulut gölgeleri içinden çıkıp gelmesini ve işlerini bitirmesini mi bekliyorlar? Oysa bütün işler, yalnızca Allah'a döndürülür.", transliteration: "Hel yenzurune illa en ye'tiyehumullahu fi zulelin minel gamami vel melaiketu ve kudiyel emr, ve ilallahi turceul umur."},
  {id: 218, sure_no: 211, ayet_no: 211, sure_adi: "Bakara", text: "سَلْ بَن۪ٓي اِسْرَٓائ۪لَ كَمْ اٰتَيْنَاهُمْ مِنْ اٰيَةٍ بَيِّنَةٍۜ وَمَنْ يُبَدِّلْ نِعْمَةَ اللّٰهِ مِنْ بَعْدِ مَا جَٓاءَتْهُ فَاِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ", translation: "İsrailoğullarına sor; Biz, onlara nice açık ayetler verdik. Kim, Allah'ın nimeti kendisine geldikten sonra onu değiştirirse; artık kuşkusuz Allah, Azabı Çok Şiddetli Olan'dır.", transliteration: "Sel beni israile kem ateynahum min ayetin beyyineh, ve men yubeddil ni'metallahi min ba'di ma caethu fe innallahe şedidul ikab."},
  {id: 219, sure_no: 212, ayet_no: 212, sure_adi: "Bakara", text: "زُيِّنَ لِلَّذ۪ينَ كَفَرُوا الْحَيٰوةُ الدُّنْيَا وَيَسْخَرُونَ مِنَ الَّذ۪ينَ اٰمَنُواۢ وَالَّذ۪ينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيٰمَةِۜ وَاللّٰهُ يَرْزُقُ مَنْ يَشَٓاءُ بِغَيْرِ حِسَابٍ", translation: "Kafirlere dünya hayatı süslü gösterildi. Onlar, İman Edenlerle alay ederler. Oysa takva sahibi olan kimseler, Kıyamet Günü'nde onlardan üstün durumdadırlar. Allah, dilediğine hesapsız rızık verir.", transliteration: "Zuyyine lillezine keferul hayatud dunya ve yesharune minellezine amenu, vellezinettekav fevkahum yevmel kıyameh, vallahu yerzuku men yeşau bi gayrihisab."},
  {id: 220, sure_no: 213, ayet_no: 213, sure_adi: "Bakara", text: "كَانَ النَّاسُ اُمَّةً وَاحِدَةً فَبَعَثَ اللّٰهُ النَّبِيّ۪نَ مُبَشِّر۪ينَ وَمُنْذِر۪ينَۖ وَاَنْزَلَ مَعَهُمُ الْكِتَابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ ف۪يمَا اخْتَلَفُوا ف۪يهِۜ وَمَا اخْتَلَفَ ف۪يهِ اِلَّا الَّذ۪ينَ اُو۫تُوهُ مِنْ بَعْدِ مَا جَٓاءَتْهُمُ الْبَيِّنَاتُ بَغْياً بَيْنَهُمْۚ فَهَدَى اللّٰهُ الَّذ۪ينَ اٰمَنُوا لِمَا اخْتَلَفُوا ف۪يهِ مِنَ الْحَقِّ بِاِذْنِه۪ۜ وَاللّٰهُ يَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "İnsanlar bir tek ümmetti. Allah; onlara, haber verici ve uyarıcı Nebiler gönderdi. Anlaşmazlığa düştükleri konularda aralarında hakk ile hükmetmeleri için onlarla beraber Kitap indirdi. Kitap verilenler, kendilerine apaçık kanıtlar gelmesine rağmen, aralarındaki ihtiras nedeniyle onda anlaşmazlığa düştüler. Bunun üzerine Allah, İman Edenleri Kendi izni ile onların üzerinde ayrılığa düştükleri gerçeğe iletti. Zira Allah, hak edeni doğru yola iletir.", transliteration: "Kanen nasu ummeten vahıdeten fe beasallahun nebiyyine mubeşşirine ve munzirine, ve enzele meahumul kitabe bil hakkı li yahkume beynen nasi fi mahtelefu fih, ve mahtelefe fihi illellezine utuhu min ba'di ma caethumul beyyinatu bagyen beynehum, fe hedallahullezine amenu li mahtelefu fihi minel hakkı bi iznih, vallahu yehdi men yeşau ila sıratın mustakim."},
  {id: 221, sure_no: 214, ayet_no: 214, sure_adi: "Bakara", text: "اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَثَلُ الَّذ۪ينَ خَلَوْا مِنْ قَبْلِكُمْۜ مَسَّتْهُمُ الْبَأْسَٓاءُ وَالضَّرَّٓاءُ وَزُلْزِلُوا حَتّٰى يَقُولَ الرَّسُولُ وَالَّذ۪ينَ اٰمَنُوا مَعَهُ مَتٰى نَصْرُ اللّٰهِۜ اَلَٓا اِنَّ نَصْرَ اللّٰهِ قَر۪يبٌ", translation: "Sizden öncekilerin çektikleri gibi sıkıntı ve zorluklar çekmeden Cennet'e gireceğinizi mi sandınız? Onlara dokunan sıkıntı ve zorluklarla öylesine sarsıldılar ki Resul ve onunla birlikte olan Mü'minler, \"Allah'ın yardımı ne zaman?\" dediler. İyi bilin ki Allah'ın yardımı yakındır.", transliteration: "Em hasibtum en tedhulul cennete ve lemma ye'tikum meselullezine halev min kablikum messethumul be'sau ved darrau ve zulzilu hatta yekuler resulu vellezine amenu meahu meta nasrullah, e la inne nasrallahi karib."},
  {id: 222, sure_no: 215, ayet_no: 215, sure_adi: "Bakara", text: "يَسْـَٔلُونَكَ مَاذَا يُنْفِقُونَۜ قُلْ مَٓا اَنْفَقْتُمْ مِنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْاَقْرَب۪ينَ وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَابْنِ السَّب۪يلِۜ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِه۪ عَل۪يمٌ", translation: "Sana neyi infak edeceklerini soruyorlar. De ki: \"Hayır adına ne harcayabilirseniz; o, anne ve babaya, akrabalara, yetimlere, yoksullara ve yol oğlunadır. Hayır adına her ne yaparsanız kuşkusuz Allah, Her Şeyi Bilir.\"", transliteration: "Yes'eluneke maza yunfikun, kul ma enfaktum min hayrin fe lil valideyni vel akrabine vel yetama vel mesakini vebnis sebil, ve ma tef'alu min hayrin fe innallahe bihi alim."},
  {id: 223, sure_no: 216, ayet_no: 216, sure_adi: "Bakara", text: "كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَكُمْۚ وَعَسٰٓى اَنْ تَكْرَهُوا شَيْـٔاً وَهُوَ خَيْرٌ لَكُمْۚ وَعَسٰٓى اَنْ تُحِبُّوا شَيْـٔاً وَهُوَ شَرٌّ لَكُمْۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ۟", translation: "Hoşunuza gitmese de savaş üzerinize yazıldı. Olur ki, hoşunuza gitmeyen bir şeyde sizin için hayır, yine olur ki hoşunuza giden bir şeyde de sizin için şer vardır. Allah bilir, siz bilmezsiniz.", transliteration: "Kutibe aleykumul kitalu ve huve kurhun lekum, ve asa en tekrehu şey'en ve huve hayrun lekum, ve asa en tuhıbbu şey'en ve huve şerrun lekum vallahu ya'lemu ve entum la ta'lemun."},
  {id: 224, sure_no: 217, ayet_no: 217, sure_adi: "Bakara", text: "يَسْـَٔلُونَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ ف۪يهِۜ قُلْ قِتَالٌ ف۪يهِ كَب۪يرٌۜ وَصَدٌّ عَنْ سَب۪يلِ اللّٰهِ وَكُفْرٌ بِه۪ وَالْمَسْجِدِ الْحَرَامِ وَاِخْرَاجُ اَهْلِه۪ مِنْهُ اَكْبَرُ عِنْدَ اللّٰهِۚ   وَالْفِتْنَةُ اَكْبَرُ مِنَ الْقَتْلِۜ  وَلَا يَزَالُونَ يُقَاتِلُونَكُمْ حَتّٰى يَرُدُّوكُمْ عَنْ د۪ينِكُمْ اِنِ اسْتَطَاعُواۜ وَمَنْ يَرْتَدِدْ مِنْكُمْ عَنْ د۪ينِه۪ فَيَمُتْ وَهُوَ كَافِرٌ فَاُو۬لٰٓئِكَ حَبِطَتْ اَعْمَالُهُمْ فِي الدُّنْيَا وَالْاٰخِرَةِۚ وَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَـالِدُونَ", translation: "Sana, Haram ayını ve onda savaşmanın durumunu soruyorlar. De ki: \"O ayda savaşmak, büyük (günahtır.) \" Ancak, Allah'ın yolundan alıkoymak, onu ve Mescid-i Haram'ı küfretmek onun halkını oradan çıkarmak, Allah katında daha büyük (günahtır.) Zira fitne, öldürmekten daha kötüdür. Onlar, eğer güç yetirebilseler, sizi dininizden döndürünceye kadar, sizinle savaşmaktan vazgeçmezler. Sizden kim, dininden döner ve Kafir olarak ölürse işte onların dünyada da ahirette de yaptıkları boşa gitmiş olur. İşte onlar, ateşin ehlidir. Ve orada sürekli kalacaklardır.", transliteration: "Yes'eluneke aniş şehril harami kıtalin fih, kul kıtalun fihi kebir, ve saddun an sebilillahi ve kufrun bihi vel mescidil harami ve ihracu ehlihi minhu ekberu indallah, vel fitnetu ekberu minel katl, ve la yezalune yukatilunekum hatta yeruddukum an dinikum inistetau ve men yertedid minkum an dinihi fe yemut ve huve kafirun fe ulaike habitat a'maluhum fid dunya vel ahireh, ve ulaike ashabun nar, hum fiha halidun."},
  {id: 225, sure_no: 218, ayet_no: 218, sure_adi: "Bakara", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَاجَرُوا وَجَاهَدُوا ف۪ي سَب۪يلِ اللّٰهِۙ اُو۬لٰٓئِكَ يَرْجُونَ رَحْمَتَ اللّٰهِۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "İman Edenler, hicret edenler ve Allah yolunda cihad edenler; ancak bunlar, Allah'ın rahmetini umabilirler. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İnnellezine amenu vellezine haceru ve cahedu fi sebilillahi, ulaike yercune rahmetallah, vallahu gafurun rahim."},
  {id: 226, sure_no: 219, ayet_no: 219, sure_adi: "Bakara", text: "يَسْـَٔلُونَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِۜ قُلْ ف۪يهِمَٓا اِثْمٌ  كَب۪يرٌ وَمَنَافِـعُ لِلنَّاسِۘ وَاِثْمُهُمَٓا اَكْبَرُ مِنْ نَفْعِهِمَاۜ وَيَسْـَٔلُونَكَ مَاذَا يُنْفِقُونَۜ قُلِ الْعَفْوَۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَۙ", translation: "Sana hamrın ve şans oyunlarının hükmünü soruyorlar. De ki: \"Her ikisinde de büyük günah vardır. İnsanlar için yararları olsa da. Günahları yararlarından daha büyüktür.\" Sana neyi infak edeceklerini soruyorlar. De ki: \"Afvı.\" Allah size ayetlerini böylece açıklamaktadır. Umulur ki düşünürsünüz.", transliteration: "Yes'eluneke anil hamri vel meysir, kul fihima ismun kebirun ve menafiu lin nasi, ve ismuhuma ekberu min nef'ihima ve yes'eluneke maza yunfikun kulil afve, kezalike yubeyyinullahu lekumul ayati leallekum tetefekkerun."},
  {id: 227, sure_no: 220, ayet_no: 220, sure_adi: "Bakara", text: "فِي الدُّنْيَا وَالْاٰخِرَةِۜ وَيَسْـَٔلُونَكَ عَنِ الْيَتَامٰىۜ قُلْ اِصْلَاحٌ لَهُمْ خَيْرٌۜ وَاِنْ تُخَالِطُوهُمْ فَاِخْوَانُكُمْۜ وَاللّٰهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِۜ وَلَوْ شَٓاءَ اللّٰهُ لَاَعْنَتَكُمْۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ", translation: "Dünya ve ahiret hakkında… Bir de sana öksüzleri soruyorlar. De ki: \"Onların hayatlarını düzene sokmak, sahipsiz bırakmaya göre hayırlı olandır.\" Eğer birlikte yaşayacak olursanız, onlar sizin kardeşlerinizdir. Allah, bozguncu olanla yapıcı olanı ayırt etmesini bilir. Eğer, Allah dileseydi, sizi zora koşardı. Zira Allah, Çok Güçlü ve Her Şeye Egemen'dir.", transliteration: "Fid dunya vel ahirah ve yes'eluneke anil yetama kul ıslahun lehum hayr ve in tuhalituhum fe ıhvanukum vallahu ya'lemul mufside minel muslih ve lev şaallahu le a'netekum innallahe azizun hakim."},
  {id: 228, sure_no: 221, ayet_no: 221, sure_adi: "Bakara", text: "وَلَا تَنْكِحُوا الْمُشْرِكَاتِ حَتّٰى يُؤْمِنَّۜ وَلَاَمَةٌ مُؤْمِنَةٌ خَيْرٌ مِنْ مُشْرِكَةٍ وَلَوْ اَعْجَبَتْكُمْۚ وَلَا تُنْكِحُوا الْمُشْرِك۪ينَ حَتّٰى يُؤْمِنُواۜ وَلَعَبْدٌ مُؤْمِنٌ خَيْرٌ مِنْ مُشْرِكٍ وَلَوْ اَعْجَبَكُمْۜ اُو۬لٰٓئِكَ يَدْعُونَ اِلَى النَّارِۚ وَاللّٰهُ يَدْعُٓوا اِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِاِذْنِه۪ۚ وَيُبَيِّنُ اٰيَاتِه۪ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ۟", translation: "İman etmedikçe Müşrik kadınlarla evlenmeyin. Beğenseniz de Mü'min bir emet, Müşrik bir kadına göre hayırlı olandır. İman etmedikçe, Müşrik erkeklerle evlenmeyin. Beğenseniz de abd olan bir Mü'min, Müşrik bir erkeğe göre hayırlı olandır. Zira onlar ateşe çağırırlar. Allah ise izni ile Cennet'e ve bağışlanmaya çağırır. İnsanlara ayetlerini böyle açıklar. Umulur ki öğüt alıp düşünürler.", transliteration: "Ve la tenkihul muşrikati hatta yu'minn, ve le emetun mu'minetun hayrun min muşriketin ve lev a'cebetkum, ve la tunkihul muşrikine hatta yu'minu ve le abdun mu'minun hayrun min muşrikin ve lev a'cebekum, ulaike yed'une ilen nar, vallahu yed'u ilel cenneti vel magfireti bi iznih, ve yubeyyinu ayatihi lin nasi leallehum yetezekkerun."},
  {id: 229, sure_no: 222, ayet_no: 222, sure_adi: "Bakara", text: "وَيَسْـَٔلُونَكَ عَنِ الْمَح۪يضِۜ قُلْ هُوَ اَذًىۙ فَاعْتَزِلُوا النِّسَٓاءَ فِي الْمَح۪يضِۙ وَلَا تَقْرَبُوهُنَّ حَتّٰى يَطْهُرْنَۚ فَاِذَا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ اَمَرَكُمُ اللّٰهُۜ اِنَّ اللّٰهَ يُحِبُّ التَّوَّاب۪ينَ وَيُحِبُّ الْمُتَطَهِّر۪ينَ", translation: "Sana \"Adet görme durumundan\" soruyorlar. De ki: \"O bir rahatsızlıktır.\" Kadınlar adet hali durumundayken onlardan uzak durun. Adet görme bitinceye kadar onlara yaklaşmayın. Adet hali bitince, onlara Allah'ın buyruğuna uygun şekilde yaklaşın. Kuşkusuz, Allah, tevbe edenleri sever. Ve temizlenenleri de sever.", transliteration: "Ve yes'eluneke anil mahid, kul huve ezen, fa'tezilun nisae fil mahidi, ve la takrabuhunne hatta yathurn fe iza tetahherne fe'tuhunne min haysu emerekumullah innallahe yuhıbbut tevvabine ve yuhibbul mutetahhirin."},
  {id: 230, sure_no: 223, ayet_no: 223, sure_adi: "Bakara", text: "نِسَٓاؤُ۬كُمْ حَرْثٌ لَكُمْۖ فَأْتُوا حَرْثَكُمْ اَنّٰى شِئْتُمْۘ وَقَدِّمُوا لِاَنْفُسِكُمْۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّكُمْ مُلَاقُوهُۜ وَبَشِّرِ الْمُؤْمِن۪ينَ", translation: "Kadınlarınız sizin için ürün veren kimselerdir. O halde, ürün vereninize istediğiniz gibi varın. Kendiniz için önceden hazırlık yapın. Allah'a karşı takvalı olun. Bilin ki muhakkak O'na kavuşacaksınız. Ve Mü'minleri müjdele.", transliteration: "Nisaukum harsun lekum, fe'tu harsekum enna şi'tum ve kaddimu li enfusikum vettekullahe va'lemu ennekum mulakuh, ve beşşiril mu'minin."},
  {id: 231, sure_no: 224, ayet_no: 224, sure_adi: "Bakara", text: "وَلَا تَجْعَلُوا اللّٰهَ عُرْضَةً لِاَيْمَانِكُمْ اَنْ تَبَرُّوا وَتَتَّقُوا وَتُصْلِحُوا بَيْنَ النَّاسِۜ  وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ", translation: "Sakın, Allah adına ettiğiniz yeminleri; iyilik yapmanıza, takva sahibi olmanıza ve insanların arasını düzeltmenize engel kılmayın. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve la tec'alullahe urdaten li eymanikum en teberru ve tetteku ve tuslihu beynen nas, vallahu semiun alim."},
  {id: 232, sure_no: 225, ayet_no: 225, sure_adi: "Bakara", text: "لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ ف۪ٓي اَيْمَانِكُمْ وَلٰكِنْ يُؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوبُكُمْۜ وَاللّٰهُ غَفُورٌ حَل۪يمٌ", translation: "Allah, kasıtsız olarak yaptığınız yeminlerden sizi sorumlu tutmaz. Ancak, kasıtlı yaptığınız yeminlerden sizi sorumlu tutar. Allah; Çok Bağışlayan'dır, Çok Hoşgörülü'dür.", transliteration: "La yuahızukumullahu bil lagvi fi eymanikum ve lakin yuahızukum bi ma kesebet kulubukum vallahu gafurun halim."},
  {id: 233, sure_no: 226, ayet_no: 226, sure_adi: "Bakara", text: "لِلَّذ۪ينَ يُؤْلُونَ مِنْ نِسَٓائِهِمْ تَرَبُّصُ اَرْبَعَةِ اَشْهُرٍۚ فَاِنْ فَٓاؤُ۫ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Kadınlarına yaklaşmamaya yemin eden kimseler için, dört ay bekleme süresi vardır. Eğer bu yeminlerinden dönerlerse; doğrusu Allah, Çok Bağışlayan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Lillezine yu'lune min nisaihim terabbusu erbaati eşhur, fe in fau fe innallahe gafurun rahim."},
  {id: 234, sure_no: 227, ayet_no: 227, sure_adi: "Bakara", text: "وَاِنْ عَزَمُوا الطَّـلَاقَ فَاِنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌ", translation: "Eğer boşamaya kesin karar verirlerse; kuşkusuz, Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve in azemut talaka fe innallahe semiun alim."},
  {id: 235, sure_no: 228, ayet_no: 228, sure_adi: "Bakara", text: "وَالْمُطَلَّقَاتُ يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ ثَلٰثَةَ قُرُٓوءٍۜ وَلَا يَحِلُّ لَهُنَّ اَنْ يَكْتُمْنَ مَا خَلَقَ اللّٰهُ ف۪ٓي اَرْحَامِهِنَّ اِنْ كُنَّ يُؤْمِنَّ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ وَبُعُولَتُهُنَّ اَحَقُّ بِرَدِّهِنَّ ف۪ي ذٰلِكَ اِنْ اَرَادُٓوا اِصْلَاحاًۜ وَلَهُنَّ مِثْلُ الَّذ۪ي عَلَيْهِنَّ بِالْمَعْرُوفِۖ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ۟", translation: "Boşanan kadınlar, evlenmeksizin üç adet dönemi beklerler. Eğer Allah'a ve ahiret gününe iman ediyorlarsa, Allah'ın rahimlerinde yarattığını gizlemeleri onlara helal değildir. Eğer bu dönemde kocaları barışmak isterlerse, onlarla yeniden evlenmede daha fazla hak sahibidirler. Erkeklerin, kadınlar üzerindeki haklarına denk, kadınların da erkekler üzerinde meşru hakları vardır. Ancak erkekler, onlar üzerinde öncelik sahibidirler. Kuşkusuz, Allah; Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Vel mutallakatu yeterabbasne bi enfusihinne selasete kuruin, ve la yahıllu lehunne en yektumne ma halakallahu fi erhamihinne in kunne yu'minne billahi vel yevmil ahır, ve buuletuhunne ehakku bi reddihinne fi zalike in eradu ıslaha, ve lehunne mislullezi aleyhinne bil ma'ruf, ve lir ricali aleyhinne dereceh, vallahu azizun hakim."},
  {id: 236, sure_no: 229, ayet_no: 229, sure_adi: "Bakara", text: "اَلطَّـلَاقُ مَرَّتَانِۖ فَاِمْسَاكٌ بِمَعْرُوفٍ اَوْ تَسْر۪يحٌ بِاِحْسَانٍۜ وَلَا يَحِلُّ لَكُمْ اَنْ تَأْخُذُوا مِمَّٓا اٰتَيْتُمُوهُنَّ شَيْـٔاً اِلَّٓا اَنْ يَخَافَٓا اَلَّا يُق۪يمَا حُدُودَ اللّٰهِۜ فَاِنْ خِفْتُمْ اَلَّا يُق۪يمَا حُدُودَ اللّٰهِۙ فَلَا جُنَاحَ عَلَيْهِمَا ف۪يمَا افْتَدَتْ بِه۪ۜ تِلْكَ حُدُودُ اللّٰهِ فَلَا تَعْتَدُوهَاۚ وَمَنْ يَتَعَدَّ حُدُودَ اللّٰهِ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ", translation: "Boşanma iki defadır. Bundan sonra evliliğinizi ya meşru bir şekilde sürdürün ya da iyilikle ayrılın. Kadınlara verdiklerinizden bir şeyi; kadın ve erkek, Allah'ın koyduğu yasaları ihlal etmedikçe geri almanız size helal olmaz. Fakat Allah'ın yasalarını çiğnemelerinden korkarsanız o zaman kadının boşanmak için fidye vermesinde her iki taraf için de sakınca yoktur. Bunlar, Allah'ın koyduğu yasalardır. Sakın bunları çiğnemeyin. Kim Allah'ın koyduğu yasaları çiğnerse işte onlar zalimlerin ta kendileridir.", transliteration: "Et talaku merratan, fe imsakun bi ma'rufin ev tesrihun bi ihsan, ve la yahıllu lekum en te'huzu mimma ateytumuhunne şey'en illa en yehafa ella yukima hududallah, fe in hıftum ella yukima hududallahi, fe la cunaha aleyhima fi meftedet bih, tilke hududullahi fe la ta'teduha, ve men yeteadde hududallahi fe ulaike humuz zalimun."},
  {id: 237, sure_no: 230, ayet_no: 230, sure_adi: "Bakara", text: "فَاِنْ طَلَّقَهَا فَلَا تَحِلُّ لَهُ مِنْ بَعْدُ حَتّٰى تَنْكِحَ زَوْجاً غَيْرَهُۜ  فَاِنْ طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَٓا اَنْ يَتَرَاجَعَٓا اِنْ ظَـنَّٓا اَنْ يُق۪يمَا حُدُودَ اللّٰهِۜ وَتِلْكَ حُدُودُ اللّٰهِ يُبَيِّنُهَا لِقَوْمٍ يَعْلَمُونَ", translation: "Yine de karısını boşarsa, karısı başka biriyle evlenmedikçe artık kendisine helal olmaz. Şayet ikinci koca da onu boşarsa ve her ikisi de Allah'ın koyduğu yasalara uyacaklarını umut ederlerse, birbirlerine dönmelerinde bir sakınca yoktur. Bunlar, Allah'ın, anlayan bir topluma iyice açıkladığı yasalarıdır.", transliteration: "Fe in tallakaha fe la tahıllu lehu min ba'du hatta tenkiha zevcen gayrah, fe in tallakaha fe la cunaha aleyhima en yeteracea in zanna en yukima hududallah, ve tilke hududullahi yubeyyinuha li kavmin ya'lemun."},
  {id: 238, sure_no: 231, ayet_no: 231, sure_adi: "Bakara", text: "وَاِذَا طَلَّقْتُمُ النِّسَٓاءَ فَبَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوهُنَّ بِمَعْرُوفٍ اَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍۖ وَلَا تُمْسِكُوهُنَّ ضِرَاراً لِتَعْتَدُواۚ وَمَنْ يَفْعَلْ ذٰلِكَ فَقَدْ ظَلَمَ نَفْسَهُۜ وَلَا تَتَّخِذُٓوا اٰيَاتِ اللّٰهِ هُزُواًۘ وَاذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ وَمَٓا اَنْزَلَ عَلَيْكُمْ مِنَ الْكِتَابِ وَالْحِكْمَةِ يَعِظُـكُمْ بِه۪ۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ۟", translation: "Boşadığınız kadınlar, iddetlerini tamamlayınca, onları ya meşru bir şekilde tutun ya da meşru bir şekilde bırakın. Haklarını çiğneyip, zarar verecek şekilde onları tutmayın. Kim böyle davranırsa, kendisine zulmetmiş olur. Allah'ın ayetlerini hafife almayın. Allah'ın üzerinizdeki nimetini; size öğüt vermek için indirdiği Kitap'ı ve Hikmet'i aklınızdan çıkarmayın. Allah'a karşı gelmekten sakının. Ve bilin ki Allah, Her Şeyi Gerçeğiyle Bilen'dir.", transliteration: "Ve iza tallaktumun nisae fe belagne ecelehunne fe emsikuhunne bi ma'rufin ev serrihuhunne bi ma'ruf, ve la tumsikuhunne dıraran li ta'tedu, ve men yef'al zalike fe kad zaleme nefseh, ve la tettehızu ayatillahi huzuva, vezkuru ni'metallahi aleykum ve ma enzele aleykum minel kitabi vel hikmeti yeızukum bih, vettekullahe va'lemu ennallahe bi kulli şey'in alim."},
  {id: 239, sure_no: 232, ayet_no: 232, sure_adi: "Bakara", text: "وَاِذَا طَلَّقْتُمُ النِّسَٓاءَ فَبَلَغْنَ اَجَلَهُنَّ فَلَا تَعْضُلُوهُنَّ اَنْ يَنْكِحْنَ اَزْوَاجَهُنَّ اِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوفِۜ ذٰلِكَ يُوعَظُ بِه۪ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ ذٰلِكُمْ اَزْكٰى لَكُمْ وَاَطْهَرُۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ", translation: "Boşadığınız kadınlar, bekleme sürelerini tamamlayınca; aralarında meşru bir şekilde anlaştıkları takdirde, onların eşleriyle evlenmelerine engel olmayın. Bu, içinizden Allah'a ve ahiret gününe iman edenlere yapılan bir öğüttür. Bu sizin için daha iffetli, daha temiz bir yoldur. Allah bilir, siz bilmezsiniz.", transliteration: "Ve iza tallaktumun nisae fe belagne ecelehunne fe la ta'duluhunne en yenkıhne ezvacehunne iza teradav beynehum bil ma'ruf, zalike yuazu bihi men kane minkum yu'minu billahi vel yevmil ahır, zalikum ezka lekum ve ather, vallahu ya'lemu ve entum la ta'lemun."},
  {id: 240, sure_no: 233, ayet_no: 233, sure_adi: "Bakara", text: "وَالْوَالِدَاتُ يُرْضِعْنَ اَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ لِمَنْ اَرَادَ اَنْ يُـتِمَّ الرَّضَاعَةَۜ وَعَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوفِۜ لَا تُكَلَّفُ نَفْسٌ اِلَّا وُسْعَهَاۚ لَا تُضَٓارَّ وَالِدَةٌ بِوَلَدِهَا وَلَا مَوْلُودٌ لَهُ بِوَلَدِه۪ وَعَلَى الْوَارِثِ مِثْلُ ذٰلِكَۚ فَاِنْ اَرَادَا فِصَالاً عَنْ تَرَاضٍ مِنْهُمَا وَتَشَاوُرٍ فَلَا جُنَاحَ عَلَيْهِمَاۜ وَاِنْ اَرَدْتُمْ اَنْ تَسْتَرْضِعُٓوا اَوْلَادَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ اِذَا سَلَّمْتُمْ مَٓا اٰتَيْتُمْ بِالْمَعْرُوفِۜ وَاتَّقُوا اللّٰهَ وَاعْلَمُٓوا اَنَّ اللّٰهَ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Emzirme süresini tamamlamak isteyenler için; annelerin çocuklarını emzirme süresi tam iki yıldır. Onların yiyeceklerini ve giyeceklerini meşru bir şekilde temin etmek babaya aittir. Hiç kimse, gücünün yeteceğinden daha fazlasından sorumlu değildir. Hiçbir anne, çocuğu nedeniyle sıkıntıya sokulmasın; hiçbir baba, çocuğu nedeniyle sıkıntıya sokulmasın. Ve mirasçı da aynı şekilde sorumludur. Eğer anne ve baba anlaşarak kendi rızaları ile çocuklarını sütten kesmek isterlerse, ikisi için de bir sakınca yoktur. Eğer çocuklarınızı emzirtmek isterseniz, meşru bir ücret ödediğiniz takdirde emzirtmenizde bir sakınca yoktur. Allah'a karşı takvalı olun. Ve bilin ki Allah, yaptığınız her şeyi görür.", transliteration: "Vel validatu yurdı'ne evladehunne havleyni kamileyni li men erade en yutimmer radaah, ve alel mevludi lehu rızkuhunne ve kisvetuhunne bil ma'ruf, la tukellefu nefsun illa vus'aha, la tudarra validetun bi velediha ve la mevludun lehu bi veledihi ve alel varisi mislu zalik, fe in erada fısalen an teradın min huma ve teşavurin fe la cunaha aleyhima ve in eradtum en testerdıu evladekum fe la cunaha aleykum iza sellemtum ma ateytum bil ma'ruf, vettekullahe va'lemu ennellahe bi ma ta'melune basir."},
  {id: 241, sure_no: 234, ayet_no: 234, sure_adi: "Bakara", text: "وَالَّذ۪ينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ اَزْوَاجاً يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ اَرْبَعَةَ اَشْهُرٍ وَعَشْراًۚ فَاِذَا بَلَغْنَ اَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ ف۪يمَا فَعَلْنَ ف۪ٓي اَنْفُسِهِنَّ بِالْمَعْرُوفِۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ", translation: "Sizden ölenlerin geride bıraktıkları eşleri, kendi kendilerine dört ay on gün beklerler. Bu süreyi tamamlayınca, kendi haklarında verecekleri meşru kararlarda sizin için bir sorumluluk yoktur. Allah yaptığınız her şeyden haberdardır.", transliteration: "Vellezine yuteveffevne minkum ve yezerune ezvacen yeterabbasne bi enfusihinne erbeate eşhurin ve aşra, fe iza belagne ecelehunne fe la cunahe aleykum fi ma fealne fi enfusihinne bil ma'ruf, vallahu bi ma ta'melune habir."},
  {id: 242, sure_no: 235, ayet_no: 235, sure_adi: "Bakara", text: "وَلَا جُنَاحَ عَلَيْكُمْ ف۪يمَا عَرَّضْتُمْ بِه۪ مِنْ خِطْبَةِ النِّسَٓاءِ اَوْ اَكْنَنْتُمْ ف۪ٓي اَنْفُسِكُمْۜ عَلِمَ اللّٰهُ اَنَّكُمْ سَتَذْكُرُونَهُنَّ وَلٰكِنْ لَا تُوَاعِدُوهُنَّ سِراًّ اِلَّٓا اَنْ تَقُولُوا قَوْلاً مَعْرُوفاًۜ وَلَا تَعْزِمُوا عُقْدَةَ النِّكَاحِ حَتّٰى يَبْلُغَ الْكِتَابُ اَجَلَهُۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ يَعْلَمُ مَا ف۪ٓي اَنْفُسِكُمْ فَاحْذَرُوهُۚ وَاعْلَمُٓوا اَنَّ اللّٰهَ غَفُورٌ حَل۪يمٌ۟", translation: "Bekleme süresini tamamlamamış kadınlara evlenme isteğinizi sezdirmeniz veya bunu içinizden geçirmenizde bir sakınca yoktur. Allah, sizin onlara karşı olan duygularınızı bilir. Ancak onlara meşru olmayan bir istekte bulunmayın. Kitap'taki emredilen bekleme süresi sona ermeden onlarla nikah akdi yapmaya kalkışmayın. Bilin ki Allah içinizden ne geçirdiğinizi bilir, O'na karşı gelmekten sakının. Bilin ki Allah, Çok Affedici, Çok Hoşgörülüdür.", transliteration: "Ve la cunahe aleykum fima arradtum bihi min hitbetin nisai ev eknentum fi enfusikum, alimallahu ennekum se tezkurunehunne ve lakin la tuvaıduhunne sirran illa en tekulu kavlen ma'rufa, ve la ta'zimu ukdeten nikahı hatta yeblugal kitabu eceleh, va'lemu ennallahe ya'lemu ma fi enfusikum fahzeruh, va'lemu ennallahe gafurun halim."},
  {id: 243, sure_no: 236, ayet_no: 236, sure_adi: "Bakara", text: "لَا جُنَاحَ عَلَيْكُمْ اِنْ طَلَّقْتُمُ النِّسَٓاءَ مَا لَمْ تَمَسُّوهُنَّ اَوْ تَفْرِضُوا لَهُنَّ فَر۪يضَةًۚ وَمَتِّعُوهُنَّۚ عَلَى الْمُوسِعِ قَدَرُهُ وَعَلَى الْمُقْتِرِ قَدَرُهُۚ مَتَـاعاً بِالْمَعْرُوفِۚ حَقاًّ عَلَى الْمُحْسِن۪ينَ", translation: "Dokunmadığınız veya onlar için farz kılınanı henüz farz kılmadığınız kadınları boşamanızda bir sorumluluk yoktur. Ancak onları yararlandırın. Maddi durumu iyi olan kendi imkanına göre, iyi olmayan da kendi imkanına göre onları meşru bir şekilde yararlandırsın. Bu, iyi kimselerin üzerinde bir haktır.", transliteration: "La cunaha aleykum in tallaktumun nisae ma lem temessuhunne ev tefridu lehunne faridah ve mettiuhunne alel musiı kaderuhu ve alel muktiri kaderuh metaan bil ma'ruf, hakkan alel muhsinin."},
  {id: 244, sure_no: 237, ayet_no: 237, sure_adi: "Bakara", text: "وَاِنْ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَر۪يضَةً فَنِصْفُ مَا فَرَضْتُمْ اِلَّٓا اَنْ يَعْفُونَ اَوْ يَعْفُوَا الَّذ۪ي بِيَدِه۪ عُقْدَةُ النِّكَاحِۜ وَاَنْ تَعْفُٓوا اَقْرَبُ لِلتَّقْوٰىۜ وَلَا تَنْسَوُا الْفَضْلَ بَيْنَكُمْۜ اِنَّ اللّٰهَ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Farz kılıp ta kendilerine dokunmadığınız kadınlardan ayrılırsanız, farz kılınanın yarısı onlarındır. Ancak kendisinin veya nikah akdini elinde tutanın, bundan vazgeçmesi hariç. Bununla birlikte, mehirin tamamının verilmesi takvaya daha uygundur. Aranızda birbirinize iyilik yapmayı ihmal etmeyin. Kuşkusuz, Allah, yaptığınız her şeyi görür.", transliteration: "Ve in tallaktumuhunne min kabli en temessuhunne ve kadfaradtum lehunne faridaten fe nısfu ma faradtum illaen ya'fune ev ya'fuvellezi bi yedihi ukdetun nikah, ve en ta'fu akrabu lit takva ve la tensevul fadla beynekum innallahe bi ma ta'melune basir."},
  {id: 245, sure_no: 238, ayet_no: 238, sure_adi: "Bakara", text: "حَافِظُوا عَلَى الصَّلَوَاتِ وَالصَّلٰوةِ الْوُسْطٰى وَقُومُوا لِلّٰهِ قَانِت۪ينَ", translation: "Salatları ve salatı vustayı koruyucu olun. Allah için içtenlikli olmaya özen gösterin.", transliteration: "Hafizu alas salavati ves salatil vusta ve kumu lillahi kanitin."},
  {id: 246, sure_no: 239, ayet_no: 239, sure_adi: "Bakara", text: "فَاِنْ خِفْتُمْ فَرِجَـالاً اَوْ رُكْبَـاناًۚ فَاِذَٓا اَمِنْتُمْ فَاذْكُرُوا اللّٰهَ كَمَا عَلَّمَكُمْ مَا لَمْ تَكُونُوا تَعْلَمُونَ", translation: "Eğer korkarsanız yaya veya binek üzerinde bulunduğunuzda da güvende olduğunuz zamanda da bilmediğiniz şeyleri size öğrettiği şekilde Allah'ı anın.", transliteration: "Fe in hıftum fe ricalen ev rukbana, fe iza emintum, fezkurullahe kema allemekum ma lem tekunu ta'lemun."},
  {id: 247, sure_no: 240, ayet_no: 240, sure_adi: "Bakara", text: "وَالَّذ۪ينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ اَزْوَاجاًۚ وَصِيَّةً لِاَزْوَاجِهِمْ مَتَاعاً اِلَى الْحَوْلِ غَيْرَ اِخْرَاجٍۚ فَاِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ ف۪ي مَا فَعَلْنَ ف۪ٓي اَنْفُسِهِنَّ مِنْ مَعْرُوفٍۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ", translation: "Sizden, ölüp de geride eşler bırakacak olan erkekler; eşlerinin evlerinden çıkarılmaksızın bir yıla kadar geçimlerinin teminini vasiyet etsinler. Eğer, kendileri çıkmak isterlerse, kendi haklarında uygun olanı yapmalarında sizin için bir sorumluluk yoktur. Allah, Mutlak Üstün Olan'dır ve En Doğru Hüküm Veren'dir.", transliteration: "Vellezine yuteveffevne minkum ve yezerune ezvaca, vasıyyeten li ezvacihim metaan ilel havli gayre ıhrac, fe in harecne fe la cunaha aleykum fi ma fealne fi enfusihinne min ma'ruf, vallahu azizun hakim."},
  {id: 248, sure_no: 241, ayet_no: 241, sure_adi: "Bakara", text: "وَلِلْمُطَلَّقَاتِ مَتَاعٌ بِالْمَعْرُوفِۜ حَقاًّ عَلَى الْمُتَّق۪ينَ", translation: "Boşanmış kadınların geçimlerini meşru bir şekilde sağlamak, takva sahipleri için bir yükümlülüktür.", transliteration: "Ve lil mutallakati metaun bil ma'ruf hakkan alel muttekin."},
  {id: 249, sure_no: 242, ayet_no: 242, sure_adi: "Bakara", text: "كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَعْقِلُونَ۟", translation: "İşte Allah, size ayetlerini böyle açıklar. Umulur ki aklınızı kullanırsınız.", transliteration: "Kezalike yubeyyinullahu lekum ayatihi leallekum ta'kılun."},
  {id: 250, sure_no: 243, ayet_no: 243, sure_adi: "Bakara", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ خَرَجُوا مِنْ دِيَارِهِمْ وَهُمْ اُلُوفٌ حَذَرَ الْمَوْتِۖ فَقَالَ لَهُمُ اللّٰهُ مُوتُوا ثُمَّ اَحْيَاهُمْۜ اِنَّ اللّٰهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُونَ", translation: "Binlerce kişi oldukları halde, ölüm korkusuyla yurtlarından çıkanları görmedin mi? Allah onlara \"Ölün.\" dedi; sonra, onlara hayat verdi. Allah, insanlara karşı lütuf sahibidir. Ancak insanların çoğu şükretmezler.", transliteration: "E lem tera ilellezine haracu min diyarihim ve hum ulufun hazaral mevti, fe kale lehumullahu mutu summe ahyahum innallahe le zu fadlin alen nasi ve lakinne ekseren nasi la yeşkurun."},
  {id: 251, sure_no: 244, ayet_no: 244, sure_adi: "Bakara", text: "وَقَاتِلُوا ف۪ي سَب۪يلِ اللّٰهِ وَاعْلَمُٓوا اَنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌ", translation: "O halde Allah yolunda savaşın. Ve bilin ki: Allah, Her Şeyi İşiten ve Her Şeyi Bilen'dir.", transliteration: "Ve katilu fi sebilillahi va'lemu ennallahe semiun alim."},
  {id: 252, sure_no: 245, ayet_no: 245, sure_adi: "Bakara", text: "مَنْ ذَا الَّذ۪ي يُقْرِضُ اللّٰهَ قَرْضاً حَسَناً فَيُضَاعِفَهُ لَهُٓ اَضْعَافاً كَـث۪يرَةًۜ وَاللّٰهُ يَقْبِضُ وَيَبْصُۣطُۖ وَاِلَيْهِ تُرْجَعُونَ", translation: "Kim Allah'a, Allah'ın karşılığını kat kat vereceği, karşılıksız bir borç vermek ister? Daraltan da genişleten de Allah'tır. Ve dönüşünüz yalnızca O'nadır.", transliteration: "Menzellezi yukridullahe kardan hasenen fe yudaifehu lehu ed'afen kesirah, vallahu yakbidu ve yebsut ve ileyhi turceun."},
  {id: 253, sure_no: 246, ayet_no: 246, sure_adi: "Bakara", text: "اَلَمْ تَرَ اِلَى الْمَلَأِ مِنْ بَن۪ٓي اِسْرَٓائ۪لَ مِنْ بَعْدِ مُوسٰىۢ اِذْ قَالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنَا مَلِكاً نُقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِۜ قَالَ هَلْ عَسَيْتُمْ اِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ اَلَّا تُقَاتِلُواۜ قَالُوا وَمَا لَـنَٓا اَلَّا نُقَاتِلَ ف۪ي سَب۪يلِ اللّٰهِ وَقَدْ اُخْرِجْنَا مِنْ دِيَارِنَا وَاَبْنَٓائِنَاۜ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا اِلَّا قَل۪يلاً مِنْهُمْۜ وَاللّٰهُ عَل۪يمٌ بِالظَّالِم۪ينَ", translation: "Musa'dan sonra İsrailoğullarının meleleri, Nebi'lerine: \"Bize bir komutan tayin et de Allah yolunda savaşalım\" demişlerdi. O: \"Ya savaş üzerinize yazılır da savaşmazsanız?\" dedi. Onlar: \"Yurdumuzdan çıkarılıp çocuklarımızdan koparılmışken, niçin Allah yolunda savaşmayalım?\" dediler. Fakat üzerlerine savaş yazılınca da içlerinden pek azı hariç, yüz çevirdiler. Allah, zalimleri çok iyi bilir.", transliteration: "E lem tera ilel melei min beni israile min ba'di musa, iz kalu li nebiyyin lehumub'as lena meliken nukatil fi sebilillah, kale hel aseytum in kutibe aleykumul kıtalu ella tukatil, kalu ve ma lena ella nukatile fi sebilillahi ve kad uhricna min diyarina ve ebnaina fe lemma kutibe aleyhimul kıtalu tevellev illa kalilen minhum vallahu alimun biz zalimin."},
  {id: 254, sure_no: 247, ayet_no: 247, sure_adi: "Bakara", text: "وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اللّٰهَ قَدْ بَعَثَ لَكُمْ طَالُوتَ مَلِكاًۜ قَالُٓوا اَنّٰى يَكُونُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ اَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِنَ الْمَالِۜ قَالَ اِنَّ اللّٰهَ اصْطَفٰيهُ عَلَيْكُمْ وَزَادَهُ بَسْطَةً فِي الْعِلْمِ وَالْجِسْمِۜ وَاللّٰهُ يُؤْت۪ي مُلْكَهُ مَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌ", translation: "Nebi'leri onlara: \"Allah size Talut'u komutan olarak tayin etti.\" dedi. Onlar: \"Biz komutanlığa ondan daha layık olduğumuz ve o fazla bir servete de sahip değilken, bize nasıl komutan olabilir?\" dediler. O da: \"Allah, onu üzerinize seçti, ona geniş bir bilgi ve üstün bir güç verdi.\" dedi. Zira Allah, gücü hak edene verir. Allah, Her Şeyi Kuşatan ve Her Şeyi Bilen'dir.", transliteration: "Ve kale lehum nebiyyuhum innallahe kad bease lekum talutemelika, kalu enna yekunu lehul mulku aleyna ve nahnu ehakku bil mulki minhu ve lem yu'te seaten minel mal, kale innallahestafahu aleykum ve zadehu bestaten fil ilmi vel cism, vallahu yu'ti mulkehu men yeşau, vallahu vasiun alim."},
  {id: 255, sure_no: 248, ayet_no: 248, sure_adi: "Bakara", text: "وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اٰيَةَ مُلْكِه۪ٓ اَنْ يَأْتِيَكُمُ التَّابُوتُ ف۪يهِ سَك۪ينَةٌ مِنْ رَبِّكُمْ وَبَقِيَّةٌ مِمَّا تَرَكَ اٰلُ مُوسٰى وَاٰلُ هٰرُونَ تَحْمِلُهُ الْمَلٰٓئِكَةُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَ۟", translation: "Nebi'leri onlara: \"Onun komutanlık kanıtı, içinde Rabb'inizden bir sekine ve Musa ile Harun soyundan bakiye kalanların bulunduğu ve meleklerin taşıdığı, yüklendiği bir sandığın size gelmesidir. Eğer iman edenlerdenseniz, kuşkusuz bunda sizin için kesin bir ayet vardır.\" dedi.", transliteration: "Ve kale lehum nebiyyuhum inne ayete mulkihi en ye'tiyekumut tabutu fihi sekinetun min rabbikum ve bakiyyetun mimma terake alu musa ve alu harune tahmiluhul melaikeh, inne fi zalike le ayeten lekum in kuntum mu'minin."},
  {id: 256, sure_no: 249, ayet_no: 249, sure_adi: "Bakara", text: "فَلَمَّا فَصَلَ طَالُوتُ بِالْجُنُودِۙ قَالَ اِنَّ اللّٰهَ مُبْتَل۪يكُمْ بِنَهَرٍۚ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنّ۪يۚ وَمَنْ لَمْ يَطْعَمْهُ فَاِنَّهُ مِنّ۪ٓي اِلَّا مَنِ اغْتَرَفَ غُرْفَةً بِيَدِه۪ۚ فَشَرِبُوا مِنْهُ اِلَّا قَل۪يلاً مِنْهُمْۜ فَلَمَّا جَاوَزَهُ هُوَ وَالَّذ۪ينَ اٰمَنُوا مَعَهُۙ قَالُوا لَا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوتَ وَجُنُودِه۪ۜ قَالَ الَّذ۪ينَ يَظُنُّونَ اَنَّهُمْ مُلَاقُوا اللّٰهِۙ  كَمْ مِنْ فِئَةٍ قَل۪يلَةٍ غَلَبَتْ فِئَةً كَث۪يرَةً بِاِذْنِ اللّٰهِۜ وَاللّٰهُ مَعَ الصَّابِر۪ينَ", translation: "Talut, askerleriyle yola çıkınca onlara: \"Allah, sizi bir nehirle imtihan edecek. Kim ondan içerse benden değildir. Bir avuç kadar tatmakla yetinirse o bendendir.\" dedi. Çok azı hariç, ondan doyasıya içtiler. O ve yanında yer alan İman Edenler, nehri geçince: \"Bugün Calut'a ve askerlerine karşı savaşacak gücümüz kalmadı.\" dediler. Allah'a kavuşacaklarına iman edenler ise: \"Nice az topluluklar, Allah'ın izni ile nice çok topluluklara galip gelmiştir. Allah, sabredenlerle beraberdir.\" dediler.", transliteration: "Fe lemma fesale talutu bil cunudi, kale innallahe mubtelikum bi neher, fe men şeribe minhu fe leyse minni, ve men lem yat'amhu fe innehu minni illa menigterafe gurfeten bi yedih, fe şeribu minhu illa kalilen minhum fe lemma cavezehu huve vellezine amenu meahu, kalu la takate lenal yevme bi calute ve cunudih, kalellezine yezunnune ennehum mulakullahi, kem min fietin kaliletin galebet fieten kesiraten bi iznillah, vallahu meas sabirin."},
  {id: 257, sure_no: 250, ayet_no: 250, sure_adi: "Bakara", text: "وَلَمَّا بَرَزُوا لِجَالُوتَ وَجُنُودِه۪ قَالُوا رَبَّنَٓا اَفْرِغْ عَلَيْنَا صَبْراً وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِر۪ينَۜ", translation: "Onlar, Calut ve askerleriyle karşı karşıya geldikleri zaman: \"Ey Rabbimiz! Üzerimize sabır yağdır ve ayaklarımızı sabit kıl, Kafir kavme karşı bize yardım et.\" dediler.", transliteration: "Ve lemma berazu li calute ve cunudihi kalu rabbena efrig aleyna sabren ve sebbit ekdamena vensurna alel kavmil kafirin."},
  {id: 258, sure_no: 251, ayet_no: 251, sure_adi: "Bakara", text: "فَهَزَمُوهُمْ بِاِذْنِ اللّٰهِۙ وَقَتَلَ دَاوُ۫دُ جَالُوتَ وَاٰتٰيهُ اللّٰهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهُ مِمَّا يَشَٓاءُۜ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَفَسَدَتِ الْاَرْضُ وَلٰكِنَّ اللّٰهَ ذُوفَضْلٍ عَلَى الْعَالَم۪ينَ", translation: "Allah'ın izniyle onları yenilgiye uğrattılar. Davud, Calut'u öldürdü. Allah, O'na güç ve hikmet verdi. O'na dilediğinden öğretti. Eğer, Allah, insanların bir kısmını bir kısmıyla savmasaydı, yeryüzü bozguna uğrardı. Ancak, Allah, bütün alemlere karşı sınırsız lütuf sahibidir.", transliteration: "Fe hezemuhum bi iznillahi, ve katele davudu calute ve atahullahul mulke vel hikmete ve allemehu mimma yeşau, ve lev la def'ullahin nase, ba'dahum bi ba'din le fesedetil ardu ve lakinnallahe zu fadlin alel alemin."},
  {id: 259, sure_no: 252, ayet_no: 252, sure_adi: "Bakara", text: "تِلْكَ اٰيَاتُ اللّٰهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّۜ وَاِنَّكَ لَمِنَ الْمُرْسَل۪ينَ", translation: "İşte bunlar Allah'ın ayetleridir. Biz onları sana gerçek olarak okuyoruz. Kuşkusuz sen gönderilenlerdensin.", transliteration: "Tilke ayatullahi netluha aleyke bil hakk, ve inneke le minel murselin."},
  {id: 260, sure_no: 253, ayet_no: 253, sure_adi: "Bakara", text: "تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۢ مِنْهُمْ مَنْ كَلَّمَ اللّٰهُ وَرَفَعَ بَعْضَهُمْ دَرَجَاتٍۜ وَاٰتَيْنَا ع۪يسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَاَيَّدْنَاهُ بِرُوحِ الْقُدُسِۜ وَلَوْ شَٓاءَ اللّٰهُ مَا اقْتَتَلَ الَّذ۪ينَ مِنْ بَعْدِهِمْ مِنْ بَعْدِ مَا جَٓاءَتْهُمُ الْبَيِّنَاتُ وَلٰكِنِ اخْتَلَفُوا فَمِنْهُمْ مَنْ اٰمَنَ وَمِنْهُمْ مَنْ كَفَرَۜ وَلَوْ شَٓاءَ اللّٰهُ مَا اقْتَتَلُوا وَلٰكِنَّ اللّٰهَ يَفْعَلُ مَا يُر۪يدُ۟", translation: "İşte o Resuller ki her birine farklı lütuflarda bulunduk. Allah, onların kimisi ile konuşmuş, kimisinin de derecelerini yükseltmiştir. Meryem Oğlu İsa'ya beyyinat verdik ve onu Kudus'un Ruhu ile destekledik. Allah dileseydi, onlardan sonra gelenler, bunca açık kanıttan sonra birbirleriyle savaşmazlardı. Yalnız onlar ihtilafa düştüler; onlardan kimisi iman etti, yine onlardan kimisi de kafirlik etti. Eğer Allah dileseydi birbirleriyle savaşmazlardı. Ancak, Allah neyi dilerse onu yapar.", transliteration: "Tilker rusulu faddalna ba'dahum ala ba'd, minhum men kellemallahu ve rafea ba'dahum derecat, ve ateyna isabne meryemel beyyinati ve eyyednahu bi ruhıl kudus, ve lev şaallahu maktetelellezine min ba'dihim min ba'di ma caethumul beyyinatu ve lakinihtelefu fe minhum men amene ve minhum men kefer, ve lev şaallahu maktetelu ve lakinnallahe yef'alu ma yurid."},
  {id: 261, sure_no: 254, ayet_no: 254, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَنْفِقُوا مِمَّا رَزَقْنَاكُمْ مِنْ قَبْلِ اَنْ يَأْتِيَ يَوْمٌ لَا بَيْعٌ ف۪يهِ وَلَا خُلَّةٌ وَلَا شَفَاعَةٌۜ وَالْكَافِرُونَ هُمُ الظَّالِمُونَ", translation: "Ey İman Edenler! İçinde hiçbir alışverişin, hiçbir dostluğun ve hiçbir şefaatin olmadığı gün gelmeden önce size rızık olarak verdiklerimizden infak edin. Kafirler, zalimlerin ta kendileridir.", transliteration: "Ya eyyuhellezine amenu enfiku mimma razaknakum min kabli en ye'tiye yevmun la bey'un fihi ve la hulletun ve la şefaah, vel kafirune humuz zalimun."},
  {id: 262, sure_no: 255, ayet_no: 255, sure_adi: "Bakara", text: "اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۚ اَلْحَيُّ الْقَيُّومُۚ لَا تَأْخُذُهُ سِنَةٌ وَلَا نَوْمٌۜ لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ مَنْ ذَا الَّذ۪ي يَشْفَعُ عِنْدَهُٓ اِلَّا بِاِذْنِه۪ۜ يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْۚ وَلَا يُح۪يطُونَ بِشَيْءٍ مِنْ عِلْمِه۪ٓ اِلَّا بِمَا شَٓاءَۚ وَسِعَ كُرْسِيُّهُ السَّمٰوَاتِ وَالْاَرْضَۚ وَلَا يَؤُ۫دُهُ حِفْظُهُمَاۚ وَهُوَ الْعَلِيُّ الْعَظ۪يمُ", translation: "Allah: O'ndan başka ilah yoktur. O, sürekli diridir, koruyup gözetendir. O'nda ne bir dalgınlık olur ne de O'nu bir uyku tutar. Göklerde ve yerde olan her şey O'nundur. İzni olmaksızın O'nun katında şefaatte bulunabilecek kimmiş? Onların önlerinde ve arkalarında olan her şeyi bilir. Onlar, O'nun ilminden ancak dilediği kadarını kavrayabilirler. O'nun egemenliği yeri ve göğü kuşatmıştır. Bunları korumak O'na ağır gelmez. O, Çok Yüce ve Çok Güçlü'dür.", transliteration: "Allahu la ilahe illa huvel hayyul kayyum, la te'huzuhu sinetun ve la nevm, lehu ma fis semavati ve ma fil ard, menzellezi yeşfeu indehu illa bi iznih ya'lemu ma beyne eydihim ve ma halfehum, ve la yuhitune bi şey'in min ilmihi illa bi ma şae, vesia kursiyyuhus semavati vel ard, ve la yeuduhu hıfzuhuma ve huvel aliyyul azim."},
  {id: 263, sure_no: 256, ayet_no: 256, sure_adi: "Bakara", text: "لَٓا اِكْرَاهَ فِي الدّ۪ينِ قَدْ تَبَيَّنَ الرُّشْدُ مِنَ الْغَيِّۚ فَمَنْ يَكْفُرْ بِالطَّاغُوتِ وَيُؤْمِنْ بِاللّٰهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۗ لَا انْفِصَامَ لَهَاۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ", translation: "Dinde zorlama yoktur. Artık, doğru olan yanlış olandan kesin olarak ayrılmıştır. Kim tağutu reddedip, Allah'a iman edersa, kuşkusuz ki kopması mümkün olmayan en sağlam kulpa tutunmuş olur. Allah, Her Şeyi İşiten ve Her Şeyi Bilen'dir.", transliteration: "La ikrahe fid dini kad tebeyyener ruşdu minel gayy, fe men yekfur bit taguti ve yu'min billahi fe kadistemseke bil urvetil vuska, lenfisame leha, vallahu semiun alim."},
  {id: 264, sure_no: 257, ayet_no: 257, sure_adi: "Bakara", text: "اَللّٰهُ وَلِيُّ الَّذ۪ينَ اٰمَنُواۙ يُخْرِجُهُمْ مِنَ الظُّلُمَاتِ اِلَى النُّورِۜ وَالَّذ۪ينَ كَفَرُٓوا اَوْلِيَٓاؤُ۬هُمُ الطَّاغُوتُۙ يُخْرِجُونَهُمْ مِنَ النُّورِ اِلَى الظُّلُمَاتِۜ اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ۟", translation: "Allah, İman Edenler'in velisidir. Onları karanlıklardan aydınlığa çıkarır. Küfredenlerin velileri ise tağutlardır; onları aydınlıktan çıkarıp karanlıklara sokar. İşte onlar ateş ehlidir ve orada sürekli kalacaklardır.", transliteration: "Allahu veliyyullezine amenu, yuhricuhum minez zulumati ilen nur, vellezine keferu evliyauhumut tagutu yuhricunehum minen nuri ilaz zulumat, ulaike ashabun nar, hum fiha halidun."},
  {id: 265, sure_no: 258, ayet_no: 258, sure_adi: "Bakara", text: "اَلَمْ تَرَ اِلَى الَّذ۪ي حَٓاجَّ اِبْرٰه۪يمَ ف۪ي رَبِّه۪ٓ اَنْ اٰتٰيهُ اللّٰهُ الْمُلْكَۢ اِذْ قَالَ اِبْرٰه۪يمُ رَبِّيَ الَّذ۪ي يُحْـي۪ وَيُم۪يتُۙ قَالَ اَنَا۬ اُحْـي۪ وَاُم۪يتُۜ قَالَ اِبْرٰه۪يمُ فَاِنَّ اللّٰهَ يَأْت۪ي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذ۪ي كَفَرَۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَۚ", translation: "Allah, kendisine mülk verdi diye İbrahim'le Rabb'i hakkında tartışanı görmedin mi? Hani İbrahim: \"Benim Rabb'im diriltir ve öldürür.\" demişti. O da: \"Ben de diriltir ve öldürürüm.\" demişti. İbrahim: \"Öyleyse, Allah Güneş'i doğudan çıkarıyor, sen de batıdan çıkar.\" deyince, küfreden şaşırıp kaldı. Allah, zalim olan halka doğru yolu göstermez.", transliteration: "E lem tera ilellezi hacce ibrahime fi rabbihi en atahullahul mulk, iz kale ibrahimu rabbiyellezi yuhyi ve yumitu, kale ene uhyi ve umit, kale ibrahimu fe innallahe ye'ti biş şemsi minel maşrıkı fe'ti biha minel magribi fe buhitellezi kefer, vallahu la yehdil kavmez zalimin."},
  {id: 266, sure_no: 259, ayet_no: 259, sure_adi: "Bakara", text: "اَوْ كَالَّذ۪ي مَرَّ عَلٰى قَرْيَةٍ وَهِيَ خَاوِيَةٌ عَلٰى عُرُوشِهَاۚ قَالَ اَنّٰى يُحْـي۪ هٰذِهِ اللّٰهُ بَعْدَ مَوْتِهَاۚ فَاَمَاتَهُ اللّٰهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهُۜ قَالَ كَمْ لَبِثْتَۜ قَالَ لَبِثْتُ يَوْماً اَوْ بَعْضَ يَوْمٍۜ قَالَ بَلْ لَبِثْتَ مِائَةَ عَامٍ فَانْظُرْ اِلٰى طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْۚ وَانْظُرْ اِلٰى حِمَارِكَ وَلِنَجْعَلَكَ اٰيَةً لِلنَّاسِ وَانْظُرْ اِلَى الْعِظَامِ كَيْفَ نُنْشِزُهَا ثُمَّ نَكْسُوهَا لَحْماًۜ فَلَمَّا تَبَيَّنَ لَهُۙ قَالَ اَعْلَمُ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Veya temelleri üzerine yıkılıp, harap olmuş beldeye uğrayan kimse gibi: \"Ölümünden sonra Allah bunu nasıl diriltecek? demişti. Bunun üzerine Allah, onu öldürüp yüz yıl ölü bıraktıktan sonra diriltti. Ona: \"Ne kadar süre ölü kaldın?\" dendi. O da: \"Bir gün veya bir günden daha az.\" dedi. Allah, \"Hayır, yüz yıl kaldın.\" dedi. \"Buna rağmen yiyeceğine ve içeceğine bak, henüz bozulmamış. Ve eşeğine de bak. Bu, insanlara ayet olman içindir. Şu kemiklere bir bak, onları nasıl düzenleyip sonra et giydiriyoruz.\" Ona bu detaylı açıklama yapıldıktan sonra: \"Artık anladım ki, kuşkusuz Allah, Her Şeye Güç Yetiren'dir.\" dedi.", transliteration: "Ev kellezi merra ala karyetin ve hiye haviyetun ala uruşiha, kale enna yuhyi hazihillahu ba'de mevtiha, fe ematehullahu miete amin summe beaseh, kale kem lebist, kale lebistu yevme ev ba'da yevm, kale bel lebiste miete amin fenzur ila taamike ve şerabike lem yetesenneh, venzur ila hımarike ve li nec'aleke ayeten lin nasi venzur ilal izami keyfe nunşizuha summe neksuha lahma, fe lemma tebeyyene lehu, kale a'lemu ennallahe ala kulli şey'in kadir."},
  {id: 267, sure_no: 260, ayet_no: 260, sure_adi: "Bakara", text: "وَاِذْ قَالَ اِبْرٰه۪يمُ رَبِّ اَرِن۪ي كَيْفَ تُحْـيِ الْمَوْتٰىۜ قَالَ اَوَلَمْ تُؤْمِنْۜ قَالَ بَلٰى وَلٰكِنْ لِيَطْمَئِنَّ قَلْب۪يۜ قَالَ فَخُذْ اَرْبَعَةً مِنَ الطَّيْرِ فَصُرْهُنَّ اِلَيْكَ ثُمَّ اجْعَلْ عَلٰى كُلِّ جَبَلٍ مِنْهُنَّ جُزْءاً ثُمَّ ادْعُهُنَّ يَأْت۪ينَكَ سَعْياًۜ وَاعْلَمْ اَنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ۟", translation: "Hani bir zamanlar İbrahim: \"Ey Rabb'im! Bana ölüleri nasıl dirilttiğini göster.\" demişti. Allah: \"İnanmıyor musun?\" deyince; İbrahim: \"Hayır, inanıyorum; ancak kalbimin yatışmasını istiyorum.\" dedi. Allah: \"Kuşlardan dört tane tut, onları iyice tanı, sonra her dağın başına onlardan bir parça koy, sonra onları kendine çağır, koşarak sana gelecekler.\" dedi. Allah, Mutlak Üstün Olan ve En Doğru Hüküm Veren'dir.", transliteration: "Ve iz kale ibrahimu rabbi erini keyfe tuhyil mevta kale e ve lem tu'min kale bela ve lakin li yatmainne kalbi kale fe huz erbeaten minet tayri fe surhunne ileyke summec'al ala kulli cebelin minhunne cuz'en summed'uhunne ye'tineke sa'ya, va'lem ennallahe azizun hakim."},
  {id: 268, sure_no: 261, ayet_no: 261, sure_adi: "Bakara", text: "مَثَلُ الَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ ف۪ي سَب۪يلِ اللّٰهِ كَمَثَلِ حَبَّةٍ اَنْبَتَتْ سَبْعَ سَنَابِلَ ف۪ي كُلِّ سُنْبُلَةٍ مِائَةُ حَبَّةٍۜ وَاللّٰهُ يُضَاعِفُ لِمَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌ", translation: "Mallarını Allah yolunda infak edenlerin durumu, yedi başak bitiren ve her bir başakta yüz dane olan bir tohum tanesi gibidir. Allah hak edene kat kat verir. Allah, Yardımı Çok Kapsamlı Olan'dır, Her Şeyi Bilen'dir.", transliteration: "Meselullezine yunfikune emvalehum fi sebilillahi ke meseli habbetin enbetet seb'a senabile fi kulli sunbuletin mietu habbeh, vallahu yudaifu li men yeşau, vallahu vasiun alim."},
  {id: 269, sure_no: 262, ayet_no: 262, sure_adi: "Bakara", text: "اَلَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ ف۪ي سَب۪يلِ اللّٰهِ ثُمَّ لَا يُتْبِعُونَ مَٓا اَنْفَقُوا مَناًّ وَلَٓا اَذًۙى لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "Mallarını Allah yolunda infak edip de ardından yaptığı iyiliği başa kakmayarak yardımcı oldukları kimseleri incitmeyenlerin ödülleri Rabb'leri katındadır. Onlara korku yoktur, onlar üzülmeyeceklerdir.", transliteration: "Ellezine yunfikune emvalehum fi sebilillahi summe la yutbiune ma enfeku mennen ve la ezen lehum ecruhum inde rabbihim, ve la havfun aleyhim ve la hum yahzenun."},
  {id: 270, sure_no: 263, ayet_no: 263, sure_adi: "Bakara", text: "قَوْلٌ مَعْرُوفٌ وَمَغْفِرَةٌ خَيْرٌ مِنْ صَدَقَةٍ يَتْبَعُهَٓا اَذًىۜ وَاللّٰهُ غَنِيٌّ حَل۪يمٌ", translation: "Ma'ruf bir söz ve bağışlayıcı olmak, ardından başa kakılarak eziyete dönüşen bir sadakadan daha hayırlıdır. Allah, Hiçbir Şeye Muhtaç Olmayan'dır, Çok Şefkatli'dir.", transliteration: "Kavlun ma'rufun ve magfiretun, hayrun min sadakatin yetbeuha eza, vallahu ganiyyun halim."},
  {id: 271, sure_no: 264, ayet_no: 264, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُبْطِلُوا صَدَقَاتِكُمْ بِالْمَنِّ وَالْاَذٰىۙ كَالَّذ۪ي يُنْفِقُ مَالَهُ رِئَٓاءَ النَّاسِ وَلَا يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ فَمَثَلُهُ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَاَصَابَهُ وَابِلٌ فَتَرَكَهُ صَلْداًۜ لَا يَقْدِرُونَ عَلٰى شَيْءٍ مِمَّا كَسَبُواۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ", translation: "Ey İman Edenler! Allah'a ve ahiret gününe iman etmediği halde, insanlara malını gösteriş yapmak için harcayan kimse gibi; sadakalarınızı başa kakarak ve inciterek boşa çıkarmayın. Böylelerinin durumu üzeri toprakla örtülü kaygan bir kayaya benzer. Sağanak bir yağmur yağınca, kaya çırılçıplak ortaya çıkar. Onlar, yaptıklarından hiçbir şey elde edemezler. Allah, Kafir halka doğru yolu göstermez.", transliteration: "Ya eyyuhellezine amenu la tubtılu sadakatikum bil menni vel eza, kellezi yunfiku malehu riaen nasi ve la yu'minu billahi vel yevmil ahır, fe meseluhu ke meseli safvanin aleyhi turabun fe esabehu vabilun fe terakehu salda, la yakdirune ala şey'in mimma kesebu vallahu la yehdil kavmel kafirin."},
  {id: 272, sure_no: 265, ayet_no: 265, sure_adi: "Bakara", text: "وَمَثَلُ الَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمُ ابْتِغَٓاءَ مَرْضَاتِ اللّٰهِ وَتَثْب۪يتاً مِنْ اَنْفُسِهِمْ كَمَثَلِ جَنَّةٍ بِرَبْوَةٍ اَصَابَهَا وَابِلٌ فَاٰتَتْ اُكُلَهَا ضِعْفَيْنِۚ فَاِنْ لَمْ يُصِبْهَا وَابِلٌ فَطَلٌّۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Allah'ın rızasına kavuşmak için, içten gelerek malını harcayan kimsenin durumu; kuvvetli yağmur yağdığında kat kat ürün veren, kuvvetli yağmur olmasa da çisentisinin yeteceği yüksek bir yerdeki cennete benzer. Allah, yaptığınız her şeyi görendir.", transliteration: "Ve meselullezine yunfikune emvalehumubtigae mardatillahi ve tesbiten min enfusihim ke meseli cennetin bi rabvetin esabeha vabilun fe atet ukuleha dı'feyn, fe in lem yusıbha vabilun fe tall, vallahu bima ta'melune basir."},
  {id: 273, sure_no: 266, ayet_no: 266, sure_adi: "Bakara", text: "اَيَوَدُّ اَحَدُكُمْ اَنْ تَكُونَ لَهُ جَنَّةٌ مِنْ نَخ۪يلٍ وَاَعْنَابٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۙ لَهُ ف۪يهَا مِنْ كُلِّ الثَّمَرَاتِۙ وَاَصَابَهُ الْكِبَرُ وَلَهُ ذُرِّيَّةٌ ضُعَفَٓاءُۖ فَاَصَابَهَٓا اِعْصَارٌ ف۪يهِ نَارٌ فَاحْتَرَقَتْۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ۟", translation: "Sizden biriniz ister mi ki: Kendisi yaşlanmış ve bakıma muhtaç çocukları da varken, içinde nehirler akan, her türlü meyvesi olan, hurma ve üzüm ağaçları bulunan bahçesini ateşten bir kasırga gelip yaksın. İşte, Allah, düşünesiniz diye ayetlerini böyle açıklıyor.", transliteration: "E yeveddu ehadukum en tekune lehu cennetun min nahilin ve a'nabin tecri min tahtihel enharu, lehu fiha min kullis semarati ve esabehul kiberu ve lehu zurriyyetun duafau fe esabeha ı'sarun fihi narun fahterakat kezalike yubeyyinullahu lekumul ayati leallekum tetefekkerun."},
  {id: 274, sure_no: 267, ayet_no: 267, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَنْفِقُوا مِنْ طَيِّبَاتِ مَا كَسَبْتُمْ وَمِمَّٓا اَخْرَجْنَا لَكُمْ مِنَ الْاَرْضِۖ وَلَا تَيَمَّمُوا الْخَب۪يثَ مِنْهُ تُنْفِقُونَ وَلَسْتُمْ بِاٰخِذ۪يهِ اِلَّٓا اَنْ تُغْمِضُوا ف۪يهِۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ غَنِيٌّ حَم۪يدٌ", translation: "Ey İman Edenler! Kazandığınız şeylerin temiz ve iyi olanlarından ve sizin için yerden çıkardıklarımızdan infak edin. Gözünüz kapalı kendinize almayacağınız kötü şeyleri infak etmeyin. Bilin ki: Allah, Hiçbir Şeye Muhtaç Olmayan'dır, Övgüye Değer Yegane Varlık'tır.", transliteration: "Ya eyyuhellezine amenu enfiku min tayyibati ma kesebtum ve mimma ahracna lekum minel ard, ve la teyemmemul habise minhu tunfikune ve lestum bi ahızihı illa en tugmidu fih, va'lemu ennallahe ganiyyun hamid."},
  {id: 275, sure_no: 268, ayet_no: 268, sure_adi: "Bakara", text: "اَلشَّيْطَانُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَٓاءِۚ وَاللّٰهُ يَعِدُكُمْ مَغْفِرَةً مِنْهُ وَفَضْلاًۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌۚ", translation: "Şeytan, yoksullukla size fahşayı telkin eder. Allah ise sizi bağışlama ve bol nimet sözü veriyor. Allah, Yardımı Çok Kapsayıcı Olan'dır, Her Şeyi Bilen'dir.", transliteration: "Eş şeytanu yeidukumul fakra ve ye'murukumbil fahşai vallahu yeidukum magfireten minhuve fadla, vallahu vasiun alim."},
  {id: 276, sure_no: 269, ayet_no: 269, sure_adi: "Bakara", text: "يُؤْتِي الْحِكْمَةَ مَنْ يَشَٓاءُۚ وَمَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ اُو۫تِيَ خَيْراً كَث۪يراًۜ وَمَا يَذَّكَّرُ اِلَّٓا اُو۬لُوا الْاَلْبَابِ", translation: "O hikmeti dilediğine verir. Kime hikmet verilmişse ona pek büyük bir hayır verilmiş demektir. Bunu ancak sağlıklı düşünen akıl sahipleri anlar.", transliteration: "Yu'til hikmete men yeşau, ve men yu'tel hikmete fe kad utiye hayran kesira, ve ma yezzekkeru illa ulul elbab."},
  {id: 277, sure_no: 270, ayet_no: 270, sure_adi: "Bakara", text: "وَمَٓا اَنْفَقْتُمْ مِنْ نَفَقَةٍ اَوْ نَذَرْتُمْ مِنْ نَذْرٍ فَاِنَّ اللّٰهَ يَعْلَمُهُۜ وَمَا لِلظَّالِم۪ينَ مِنْ اَنْصَارٍ", translation: "Nafaka olarak neyi infak eder veya adak olarak neyi adarsanız, Allah onu bilir. Zulmedenlerin hiçbir yardımcısı yoktur.", transliteration: "Ve ma enfaktum min nafakatin ev nezertum min nezrin fe innallahe ya'lemuh, ve ma liz zalimine min ensar."},
  {id: 278, sure_no: 271, ayet_no: 271, sure_adi: "Bakara", text: "اِنْ تُبْدُوا الصَّدَقَاتِ فَنِعِمَّا هِيَۚ وَاِنْ تُخْفُوهَا وَتُؤْتُوهَا الْفُقَـرَٓاءَ فَهُوَ خَيْرٌ لَكُمْۜ وَيُكَفِّرُ عَنْكُمْ مِنْ سَيِّـَٔاتِكُمْۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ", translation: "Sadakaları açıktan vermeniz güzeldir. Ancak fakirlere yapacağınız yardımı gizliden yaparsanız bu sizin için daha hayırlıdır. Bu, kötülüklerinizin bir kısmının kapanmasını sağlar. Allah yaptığınız her şeyden haberdardır.", transliteration: "İn tubdus sadakati fe niimma hiy, ve in tuhfuha ve tu'tuhal fukarae fe huve hayrun lekum ve yukeffiru ankum min seyyiatikum vallahu bi ma ta'melune habir."},
  {id: 279, sure_no: 272, ayet_no: 272, sure_adi: "Bakara", text: "لَيْسَ عَلَيْكَ هُدٰيهُمْ وَلٰكِنَّ اللّٰهَ يَهْد۪ي مَنْ يَشَٓاءُۜ وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَلِاَنْفُسِكُمْۜ وَمَا تُنْفِقُونَ اِلَّا ابْتِغَٓاءَ وَجْهِ اللّٰهِۜ وَمَا تُنْفِقُوا مِنْ خَيْرٍ يُوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُونَ", translation: "Onları hidayete iletmek sana düşmez. Allah, hak edeni doğru yola iletir. Hayır olarak her ne infak ederseniz, kendiniz içindir. Zaten siz, ancak Allah'ı hoşnut etmek için infak yaparsınız. Yapacağınız her hayrın karşılığı size tam olarak verilir ve size asla haksızlık yapılmaz.", transliteration: "Leyse aleyke hudahum ve lakinnallahe yehdi men yeşau, ve ma tunfiku min hayrin fe li enfusikum, ve ma tunfikune illebtigae vechillah, ve ma tunfiku min hayrin yuveffe ileykum ve entum la tuzlemun."},
  {id: 280, sure_no: 273, ayet_no: 273, sure_adi: "Bakara", text: "لِلْفُقَـرَٓاءِ الَّذ۪ينَ اُحْصِرُوا ف۪ي سَب۪يلِ اللّٰهِ لَا يَسْتَط۪يعُونَ ضَـرْباً فِي الْاَرْضِۘ يَحْسَبُهُمُ الْجَاهِلُ اَغْنِيَٓاءَ مِنَ التَّعَفُّفِۚ تَعْرِفُهُمْ بِس۪يمٰيهُمْۚ لَا يَسْـَٔلُونَ النَّاسَ اِلْحَافاًۜ وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِه۪ عَل۪يمٌ۟", translation: "Yardımlar Allah yolunda mahsur olan, çalışmaya güç yetiremeyen yoksullar içindir. Bilmeyenler, utangaçlıklarından dolayı onları zengin sanır. Sen, onları yüzlerinden tanırsın. Yüzsüzlük ederek kimseyi rahatsız etmezler. Hayır olarak ne infak ederseniz muhakkak Allah, onu bilir.", transliteration: "Lil fukaraillezine uhsiru fi sebilillahi la yestatiune darben fil ardı, yahsebuhumul cahilu agniyae minet teaffuf, ta'rifuhum bi simahum, la yes'elunen nase ilhafa, ve ma tunfiku min hayrin fe innallahe bihi alim."},
  {id: 281, sure_no: 274, ayet_no: 274, sure_adi: "Bakara", text: "اَلَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ بِالَّيْلِ وَالنَّهَارِ سِراًّ وَعَلَانِيَةً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "Mallarını gece ve gündüz, gizli ve açık infak edenlerin ödüllerini Rabb'leri verecektir. Ve onlar için korku yoktur ve onlar üzülmeyeceklerdir.", transliteration: "Ellezine yunfikune emvalehum bil leyli ven nehari sirran ve alaniyeten fe lehum ecruhum inde rabbihim, ve la havfun aleyhim ve la hum yahzenun."},
  {id: 282, sure_no: 275, ayet_no: 275, sure_adi: "Bakara", text: "اَلَّذ۪ينَ يَأْكُلُونَ الرِّبٰوا لَا يَقُومُونَ اِلَّا كَمَا يَقُومُ الَّذ۪ي يَتَخَبَّطُهُ الشَّيْطَانُ مِنَ الْمَسِّۜ ذٰلِكَ بِاَنَّهُمْ قَالُٓوا اِنَّمَا الْبَيْعُ مِثْلُ الرِّبٰواۢ وَاَحَلَّ اللّٰهُ الْبَيْعَ وَحَرَّمَ الرِّبٰواۜ فَمَنْ جَٓاءَهُ مَوْعِظَةٌ مِنْ رَبِّه۪ فَانْتَهٰى فَلَهُ مَا سَلَفَۜ وَاَمْرُهُٓ اِلَى اللّٰهِۜ وَمَنْ عَادَ فَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "Riba yiyenler, ancak şeytanın dokunuşuyla çarptığı kimselerin kalktığı gibi kalkarlar. Bu, onların, \"Alışveriş de riba gibidir.\" demelerindendir. Oysa Allah, alışverişi helal, ribayı haram kılmıştır. Kim Rabb'inden gelen öğüde uyarak, ribadan vazgeçerse, geçmişte aldığı onundur. Onun kararı Allah'a kalmıştır. Kim tekrar ribaya dönerse, işte onlar ateş ehlidirler ve orada sürekli kalacaklardır.", transliteration: "Ellezine ye'kuluner riba la yekumune illa kema yekumullezi yetehabbetuhuş şeytanu minel mess, zalike bi ennehum kalu innemal bey'u mislur riba, ve ehallallahul bey'a ve harramer riba fe men caehu mev'izatun min rabbihi fenteha fe lehu ma selef, ve emruhu ilallah, ve men ade fe ulaike ashabun nar, hum fiha halidun."},
  {id: 283, sure_no: 276, ayet_no: 276, sure_adi: "Bakara", text: "يَمْحَقُ اللّٰهُ الرِّبٰوا وَيُرْبِي الصَّدَقَاتِۜ وَاللّٰهُ لَا يُحِبُّ كُلَّ كَفَّارٍ اَث۪يمٍ", translation: "Allah, ribayı eksiltir, sadakaları da artırır. Allah, nankörlük ederek günahta ısrar edenleri sevmez.", transliteration: "Yemhakullahur riba ve yurbis sadakat, vallahu la yuhıbbu kulle keffarin esim."},
  {id: 284, sure_no: 277, ayet_no: 277, sure_adi: "Bakara", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "İman edip, salihatı yapanların, salatı ikame edenlerin ve zekatı yapanların ödülleri kuşkusuz Rabb'lerinin yanındadır. Onlara korku yoktur ve onlar üzülmeyeceklerdir.", transliteration: "İnnellezine amenu ve amilus salihati ve ekamus salate ve atevuz zekate lehum ecruhum inde rabbihim, ve la havfun aleyhim ve la hum yahzenun."},
  {id: 285, sure_no: 278, ayet_no: 278, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَذَرُوا مَا بَـقِيَ مِنَ الرِّبٰٓوا اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Ey İman Edenler! Allah'a karşı takvalı olun. Eğer Mü'min'seniz, ribadan geriye kalanı almayın.", transliteration: "Ya eyyuhellezine amenuttekullahe ve zeru ma bakiye miner riba in kuntum mu'minin."},
  {id: 286, sure_no: 279, ayet_no: 279, sure_adi: "Bakara", text: "فَاِنْ لَمْ تَفْعَلُوا فَأْذَنُوا بِحَرْبٍ مِنَ اللّٰهِ وَرَسُولِه۪ۚ وَاِنْ تُبْتُمْ فَلَكُمْ رُؤُ۫سُ اَمْوَالِكُمْۚ لَا تَظْلِمُونَ وَلَا تُظْلَمُونَ", translation: "Eğer bırakmazsanız, o zaman Allah ve Resul'ünün size savaş açacağını bilin. Eğer tevbe ederseniz, ana malınız sizindir. Böylece ne haksızlık etmiş ne de haksızlığa uğramış olursunuz.", transliteration: "Fe in lem tef'alu fe'zenu bi harbin minallahi ve resulih, ve in tubtum fe lekum ruusu emvalikum, la tazlimune ve la tuzlemun."},
  {id: 287, sure_no: 280, ayet_no: 280, sure_adi: "Bakara", text: "وَاِنْ كَانَ ذُوعُسْرَةٍ فَنَظِرَةٌ اِلٰى مَيْسَرَةٍۜ وَاَنْ تَصَدَّقُوا خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "Eğer borçlu dardaysa ona ödemede kolaylık sağlayın, eğer alacağınızı bağışlarsanız, bunun sizin için daha hayırlı olduğunu bilin.", transliteration: "Ve in kane zu usratin fe naziratun ila meysereh ve en tesaddeku hayrun lekum in kuntum ta'lemun."},
  {id: 288, sure_no: 281, ayet_no: 281, sure_adi: "Bakara", text: "وَاتَّقُوا يَوْماً تُرْجَعُونَ ف۪يهِ اِلَى اللّٰهِ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ۟", translation: "Allah'a döndürüleceğiniz gün için takvalı olun. O gün, hiç kimseye haksızlık yapılmaksızın, yaptıklarının karşılığı tam olarak verilecektir.", transliteration: "Vetteku yevmen turceune fihi ilallahi summe tuveffa kullu nefsin ma kesebet ve hum la yuzlemun."},
  {id: 289, sure_no: 282, ayet_no: 282, sure_adi: "Bakara", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا تَدَايَنْتُمْ بِدَيْنٍ اِلٰٓى اَجَلٍ مُسَمًّى فَاكْتُبُوهُۜ وَلْيَكْتُبْ بَيْنَكُمْ كَاتِبٌ بِالْعَدْلِۖ وَلَا يَأْبَ كَاتِبٌ اَنْ يَكْتُبَ كَمَا عَلَّمَهُ اللّٰهُ فَلْيَكْتُبْۚ وَلْيُمْلِلِ الَّذ۪ي عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللّٰهَ رَبَّهُ وَلَا يَبْخَسْ مِنْهُ شَيْـٔاًۜ فَاِنْ كَانَ الَّذ۪ي عَلَيْهِ الْحَقُّ سَف۪يهاً اَوْ ضَع۪يفاً اَوْ لَا يَسْتَط۪يعُ اَنْ يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِۜ وَاسْتَشْهِدُوا شَه۪يدَيْنِ مِنْ رِجَالِكُمْۚ فَاِنْ لَمْ يَكُونَا رَجُلَيْنِ فَرَجُلٌ وَامْرَاَتَانِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدَٓاءِ اَنْ تَضِلَّ اِحْدٰيهُمَا فَتُذَكِّرَ اِحْدٰيهُمَا الْاُخْرٰىۜ وَلَا يَأْبَ الشُّهَدَٓاءُ اِذَا مَا دُعُواۜ وَلَا تَسْـَٔمُٓوا اَنْ تَكْتُبُوهُ صَغ۪يراً اَوْ كَب۪يراً اِلٰٓى اَجَلِه۪ۜ ذٰلِكُمْ اَقْسَطُ عِنْدَ اللّٰهِ وَاَقْوَمُ لِلشَّهَادَةِ وَاَدْنٰٓى اَلَّا تَرْتَابُٓوا اِلَّٓا اَنْ تَكُونَ تِجَارَةً حَاضِرَةً تُد۪يرُونَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَلَّا تَكْتُبُوهَاۜ وَاَشْهِدُٓوا اِذَا تَبَايَعْتُمْۖ وَلَا يُضَٓارَّ  كَاتِبٌ وَلَا شَه۪يدٌۜ وَاِنْ تَفْعَلُوا فَاِنَّهُ فُسُوقٌ بِكُمْۜ وَاتَّقُوا اللّٰهَۜ وَيُعَلِّمُكُمُ اللّٰهُۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Ey İman Edenler! Belli bir süre için birbirinize borç verdiğiniz zaman onu yazın. Yazan her kimse, onu adaletle yazsın. Allah'ın kendisine öğrettiği şekilde yazmaktan kaçınmasın, yazsın. Borçlu olan da yazdırsın. Rabb'i olan Allah'a karşı takvalı olsun, ondan hiçbir şeyi eksik bırakmasın. Eğer borçlu aklı ermez, aciz veya kendi söyleyip yazdıramayacak durumda birisi ise, velisi, onu adaletli bir şekilde yazdırsın. Erkeklerinizden de iki tanık tutun. Eğer iki erkek bulunmazsa, o zaman razı olacağınız tanıklardan bir erkek ve biri şaşırdığında diğeri ona hatırlatacak iki kadın tanık tutun. Tanıklar, çağrıldıkları zaman kaçınmasınlar. Az olsun çok olsun onu vadesiyle birlikte yazmaktan üşenmeyin. Bu, Allah katında en adil, tanıklık için daha sağlam ve şüphe etmemeniz için daha uygundur. Ancak, aranızda hemen devredip durduğunuz ve peşin olarak yaptığınız ticaret başka, bunu yazmamanızda sizin için bir sakınca yoktur. Alışveriş yaptığınız zaman tanık bulundurun. Tanık olana da yazana da zarar verilmesin. Eğer bunu yaparsanız kendinize kötülük yapmış olursunuz. Allah'a karşı takvalı olun. Allah, size gerekli olanı öğretiyor. Ve Allah, Her Şeyi Bilen'dir.", transliteration: "Ya eyyuhellezine amenu iza tedayentum bi deynin ila ecelin musemmen fektubuh, velyektub beynekum katibun bil adl, ve la ye'be katibun en yektube kema allemehullahu felyektub, velyumlilillezi aleyhil hakku velyettekıllahe rabbehu ve la yebhas minhu şey'a, fe in kanellezi aleyhil hakku sefihan ev daifen ev la yestatiu en yumille huve felyumlil veliyyuhu bil adl, vesteşhidu şehideyni min ricalikum, fe in lem yekuna raculeyni fe raculun vemraetani mimmen terdavne mineş şuhedai en tedılle ıhdahuma fe tuzekkire ıhdahumal uhra ve la ye'beş şuhedau iza ma duu, ve la tes'emu en tektubuhu sagiran ev kebiran ila ecelih, zalikum aksatu indallahi ve akvemu liş şehadeti ve edna ella tertabu illa en tekune ticareten hadıraten tudiruneha beynekum fe leyse aleykum cunahun ella tektubuha ve eşhidu iza tebaya'tum, ve la yudarra katibun ve la şehid, ve in tef'alu fe innehu fusukun bikum, vettekullah, ve yuallimukumullah, vallahu bi kulli şey'in alim."},
  {id: 290, sure_no: 283, ayet_no: 283, sure_adi: "Bakara", text: "وَاِنْ كُنْتُمْ عَلٰى سَفَرٍ وَلَمْ تَجِدُوا كَاتِباً فَرِهَانٌ مَقْبُوضَةٌۜ فَاِنْ اَمِنَ بَعْضُكُمْ بَعْضاً فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ اَمَانَتَهُ وَلْيَتَّقِ اللّٰهَ رَبَّهُۜ وَلَا تَكْتُمُوا الشَّهَادَةَۜ وَمَنْ يَكْتُمْهَا فَاِنَّهُٓ اٰثِمٌ قَلْبُهُۜ وَاللّٰهُ بِمَا تَعْمَلُونَ عَل۪يمٌ۟", translation: "Eğer yolculukta olup da yazıcı bulamazsanız, alınan rehinler yeterlidir. Eğer, birbirinize güveniyorsanız, güvenilen kimse, üzerindeki emaneti ödesin. Rabb'i olan Allah'a karşı takvalı olsun. Ve tanık olduğunuz şeyi gizlemeyin. Kim onu gizlerse kalben günah işlemiş olur. Allah, yaptığınız her şeyi bilir.", transliteration: "Ve in kuntum ala seferin ve lem tecidu katiben fe rihanun makbudah, fe in emine ba'dukum ba'dan felyueddillezi'tumine emanetehu velyettekıllahe rabbeh, ve la tektumuş şehadeh, ve men yektumha fe innehu asimun kalbuh, vallahu bi ma ta'melune alim."},
  {id: 291, sure_no: 284, ayet_no: 284, sure_adi: "Bakara", text: "لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاِنْ تُبْدُوا مَا ف۪ٓي اَنْفُسِكُمْ اَوْ تُخْفُوهُ يُحَاسِبْكُمْ بِهِ اللّٰهُۜ فَيَغْفِرُ لِمَنْ يَشَٓاءُ وَيُعَذِّبُ مَنْ يَشَٓاءُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Gökte ve yerde olan her şey, Allah'ındır. İçinizde olanı açıklasanız da gizleseniz de Allah, sizi onunla hesaba çeker. Allah hak eden kimseyi bağışlar, hak eden kimseye de azap eder. Allah, Her Şeye Güç Yetiren'dir.", transliteration: "Lillahi ma fis semavati ve ma fil ard, ve in tubdu ma fi enfusikum ev tuhfuhu yuhasibkum bihillah, fe yagfiru limen yeşau ve yuazzibu men yeşau, vallahu ala kulli şey'in kadir."},
  {id: 292, sure_no: 285, ayet_no: 285, sure_adi: "Bakara", text: "اٰمَنَ الرَّسُولُ بِمَٓا اُنْزِلَ اِلَيْهِ مِنْ رَبِّه۪ وَالْمُؤْمِنُونَۜ كُلٌّ اٰمَنَ بِاللّٰهِ وَمَلٰٓئِكَتِه۪ وَكُتُبِه۪ وَرُسُلِه۪ۜ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِنْ رُسُلِه۪۠ وَقَالُوا سَمِعْنَا وَاَطَعْنَا غُفْرَانَكَ رَبَّنَا وَاِلَيْكَ الْمَص۪يرُ", translation: "Resul, Rabb'inden kendisine indirilene iman etti, Mü'minler de. Hepsi; Allah'a, meleklerine, kitaplarına ve resullerine iman ettiler: \"Biz, O'nun resullerinden hiçbirini diğerinden ayırt etmeyiz. İşittik ve itaat ettik. Rabb'imiz! Bizi bağışla, dönüşümüz ancak Sana'dır.\" dediler.", transliteration: "Amener resulu bima unzile ileyhi min rabbihi vel mu'minun, kullun amene billahi ve melaiketihi ve kutubihi ve rusulih, la nuferriku beyne ehadin min rusulih, ve kalu semi'na ve ata'na gufraneke rabbena ve ileykel masir."},
  {id: 293, sure_no: 286, ayet_no: 286, sure_adi: "Bakara", text: "لَا يُكَلِّفُ اللّٰهُ نَفْساً اِلَّا وُسْعَهَاۜ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْۜ رَبَّنَا لَا تُؤَاخِذْنَٓا اِنْ نَس۪ينَٓا اَوْ اَخْطَأْنَاۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَٓا اِصْراً كَمَا حَمَلْتَهُ عَلَى الَّذ۪ينَ مِنْ قَبْلِنَاۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِه۪ۚ وَاعْفُ عَنَّا۠ وَاغْفِرْ لَنَا۠ وَارْحَمْنَا۠ اَنْتَ مَوْلٰينَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِر۪ينَ", translation: "Allah, hiç kimseye gücünün yeteceğinden fazla yük yüklemez. Herkesin yaptığı iyilik lehine, kötülük de aleyhinedir. \"Rabb'imiz! Unutur veya yanlış yaparsak, bizi sorumlu tutma. Rabb'imiz! Bize daha öncekilere yüklediğin gibi zor şeyler yükleme. Rabb'imiz! Bize gücümüzün üzerinde bir sorumluluk yükleme. Bizi affet, bizi bağışla, bize merhamet et. Sen, Mevlamız'sın. Kafirlere karşı bize yardım et.", transliteration: "La yukellifullahu nefsen illa vus'aha leha ma kesebet ve aleyha mektesebet rabbena la tuahızna in nesina ev ahta'na, rabbena ve la tahmil aleyna ısran kema hameltehu alellezine min kablina, rabbena ve la tuhammilna ma la takate lena bih, va'fu anna, vagfir lena, verhamna, ente mevlana fensurna alel kavmil kafirin."},
  {id: 294, sure_no: 1, ayet_no: 1, sure_adi: "Ali İmran", text: "الٓمٓۚ", translation: "Elif. Lam. Mim.", transliteration: "Elif lam mim."},
  {id: 295, sure_no: 2, ayet_no: 2, sure_adi: "Ali İmran", text: "اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۙ الْحَيُّ الْقَيُّومُۜ", translation: "Allah, kendisinden başka ilah olmayandır. O, Mutlak Diri'dir, Varlığı Koruyup Yöneten'dir.", transliteration: "Allahu la ilahe illa huvel hayyul kayyum."},
  {id: 296, sure_no: 3, ayet_no: 3, sure_adi: "Ali İmran", text: "نَزَّلَ عَلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ وَاَنْزَلَ التَّوْرٰيةَ وَالْاِنْج۪يلَۙ", translation: "Kendinden öncekilerini onaylayan Kitap'ı Hakk olarak sana indiren O'dur. Tevrat'ı ve İncil'i de O indirmişti.", transliteration: "Nezzele aleykel kitabe bil hakkı musaddikan lima beyne yedeyhi ve enzelet tevrate vel incil."},
  {id: 297, sure_no: 4, ayet_no: 4, sure_adi: "Ali İmran", text: "مِنْ قَبْلُ هُدًى لِلنَّاسِ وَاَنْزَلَ الْفُرْقَانَۜ اِنَّ الَّذ۪ينَ كَفَرُوا بِاٰيَاتِ اللّٰهِ لَهُمْ عَذَابٌ شَد۪يدٌۜ وَاللّٰهُ عَز۪يزٌ ذُوانْتِقَامٍ", translation: "Daha önce insanlara doğru yolu göstermek için Furkan'ı indirdi. Kuşkusuz, Allah'ın ayetlerini küfredenler için şiddetli bir azap vardır. Allah, Mutlak Üstün Olan'dır, Suçlulara Hak Ettikleri Cezayı Veren'dir.", transliteration: "Min kablu huden lin nasi ve enzelel furkan, innellezine keferu bi ayatillahi lehum azabun şedid, vallahu azizun zuntikam."},
  {id: 298, sure_no: 5, ayet_no: 5, sure_adi: "Ali İmran", text: "اِنَّ اللّٰهَ لَا يَخْفٰى عَلَيْهِ شَيْءٌ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِۜ", translation: "Kuşkusuz, yerde ve gökte hiçbir şey Allah'a gizli kalmaz.", transliteration: "İnnallahe la yahfa aleyhi şey'un fil ardı ve la fis sema'."},
  {id: 299, sure_no: 6, ayet_no: 6, sure_adi: "Ali İmran", text: "هُوَ الَّذ۪ي يُصَوِّرُكُمْ فِي الْاَرْحَامِ كَيْفَ يَشَٓاءُۜ   لَٓا اِلٰهَ اِلَّا هُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Sizi, rahimlerde dilediği gibi şekillendiren O'dur. O'ndan başka ilah yoktur. Mutlak Üstün Olandır, En Doğru Hüküm Veren'dir.", transliteration: "Huvellezi yusavvirukum fil erhami keyfe yeşa', la ilahe illa huvel azizul hakim."},
  {id: 300, sure_no: 7, ayet_no: 7, sure_adi: "Ali İmran", text: "هُوَ الَّـذ۪ٓي اَنْزَلَ عَلَيْكَ الْكِتَابَ مِنْهُ اٰيَاتٌ مُحْكَمَاتٌ هُنَّ اُمُّ الْكِتَابِ وَاُخَرُ مُتَشَابِهَاتٌۜ فَاَمَّا الَّذ۪ينَ ف۪ي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مَا تَشَابَهَ مِنْهُ ابْتِغَٓاءَ الْفِتْنَةِ وَابْتِغَٓاءَ تَأْو۪يلِه۪ۚ وَمَا يَعْلَمُ تَأْو۪يلَهُٓ اِلَّا اللّٰهُۢ وَالرَّاسِخُونَ فِي الْعِلْمِ يَقُولُونَ اٰمَنَّا بِه۪ۙ   كُلٌّ مِنْ عِنْدِ رَبِّنَاۚ وَمَا يَذَّكَّرُ اِلَّٓا اُو۬لُوا الْاَلْبَابِ", translation: "Bu Kitap'ı sana indiren O'dur. O'nun bir kısım ayetleri muhkemdir ki bunlar Kitap'ın anasıdır. Diğer ayetler de muteşabihtir. Böyleyken kalbinde eğrilik bulunanlar, fitne çıkarmak ve kendi anlayışlarına uydurmak için muteşabih ayetlere yönelirler. Oysa onun en doğru te'vilini ancak Allah bilir. İlimde derinleşmiş olanlar: \"Biz ona iman ettik, bütün ayetler Rabb'imizdendir.\" derler. Bunu ancak selim akıl sahibi olanlar düşünüp öğüt alır.", transliteration: "Huvellezi enzele aleykel kitabe minhu ayatun muhkematun hunne ummul kitabi ve uharu muteşabihat, fe emmellezine fi kulubihim zeygun fe yettebiune ma teşabehe minhubtigael fitneti vebtigae te'vilih, ve ma ya'lemu te'vilehu illallah, ver rasihune fil ilmi yekulune amenna bihi, kullun min indi rabbina, ve ma yezzekkeru illa ulul elbab."},
  {id: 301, sure_no: 8, ayet_no: 8, sure_adi: "Ali İmran", text: "رَبَّنَا لَا تُزِغْ قُلُوبَنَا بَعْدَ اِذْ هَدَيْتَنَا وَهَبْ لَنَا مِنْ لَدُنْكَ رَحْمَةًۚ اِنَّكَ اَنْتَ الْوَهَّابُ", translation: "Onlar derler ki: \"Rabb'imiz! Bizi doğru yola ilettikten sonra kalplerimizi saptırma. Bize katından rahmet bağışla. Çünkü bağışlayıcı olan yalnız Sen'sin.\"", transliteration: "Rabbena la tuziğ kulubena ba'de iz hedeytena veheb lena min ledunke rahmeh, inneke entel vehhab."},
  {id: 302, sure_no: 9, ayet_no: 9, sure_adi: "Ali İmran", text: "رَبَّنَٓا اِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَا رَيْبَ ف۪يهِۜ  اِنَّ اللّٰهَ لَا يُخْلِفُ الْم۪يعَادَ۟", translation: "\"Rabb'imiz! Sen, geleceği kesin olan günde, insanları toplayacaksın. Mutlak gerçek şudur ki Allah sözünden dönmez.\"", transliteration: "Rabbena inneke camiun nasi li yevmin la raybe fih, innallahe la yuhliful miad."},
  {id: 303, sure_no: 10, ayet_no: 10, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَٓا اَوْلَادُهُمْ مِنَ اللّٰهِ شَيْـٔاًۜ وَاُو۬لٰٓئِكَ هُمْ وَقُودُ النَّارِۙ", translation: "Kuşkusuz Kafirlerin ne mallarının ne de evlatlarının Allah'ın yanında bir yararı olacaktır. Onlar ateşin yakıtıdırlar.", transliteration: "İnnellezine keferu len tuğniye anhum emvaluhum ve la evladuhum minallahi şey'a, ve ulaike hum vekudun nar."},
  {id: 304, sure_no: 11, ayet_no: 11, sure_adi: "Ali İmran", text: "كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَذَّبُوا بِاٰيَاتِنَاۚ فَاَخَذَهُمُ اللّٰهُ بِذُنُوبِهِمْۜ وَاللّٰهُ شَد۪يدُ الْعِقَابِ", translation: "Onların durumu, tıpkı Firavuncuların ve onlardan önceki kimselerin durumu gibidir. Ayetlerimizi yalanladılar; Allah da onları suçları nedeniyle yakaladı. Allah'ın cezası çok çetindir.", transliteration: "Ke de'bi ali fir'avne, vellezine min kablihim kezzebu bi ayatina, fe ehazehumullahu bi zunubihim vallahu şedidul ıkab."},
  {id: 305, sure_no: 12, ayet_no: 12, sure_adi: "Ali İmran", text: "قُلْ لِلَّذ۪ينَ كَفَرُوا سَتُغْلَبُونَ وَتُحْشَرُونَ اِلٰى جَهَنَّمَۜ وَبِئْسَ الْمِهَادُ", translation: "Kafirlere de ki: \"Yakında yenileceksiniz ve Cehennem'e sürüleceksiniz. Orası ne kötü bir yerdir.\"", transliteration: "Kul lillezine keferu se tuglebune ve tuhşerune ila cehennem, ve bi'sel mihad."},
  {id: 306, sure_no: 13, ayet_no: 13, sure_adi: "Ali İmran", text: "قَدْ كَانَ لَكُمْ اٰيَةٌ ف۪ي فِئَتَيْنِ الْتَقَتَاۜ فِئَةٌ تُقَاتِلُ ف۪ي سَب۪يلِ اللّٰهِ وَاُخْرٰى كَافِرَةٌ يَرَوْنَهُمْ مِثْلَيْهِمْ رَأْيَ الْعَيْنِۜ وَاللّٰهُ يُؤَيِّدُ بِنَصْرِه۪ مَنْ يَشَٓاءُۜ اِنَّ ف۪ي ذٰلِكَ لَعِبْرَةً لِاُو۬لِي الْاَبْصَارِ", translation: "İki topluluğun karşılaşmasında kesinlikle sizin için bir gösterge vardır: Topluluğun birisi Allah yolunda savaşan, diğeri de Kafirlerdendi. Kafirler, onları kendilerinin iki katı olarak görüyorlardı. Allah, dilediğini yardımı ile destekler. Kuşkusuz, basiret sahipleri için bunda bir ders vardır.", transliteration: "Kad kane lekum ayetun fi fieteynil tekata fietun tukatilu fi sebilillahi ve uhra kafiratun yeravnehum misleyhim ra'yel ayn, vallahu yueyyidu bi nasrihi men yeşa' inne fi zalike le ibreten li ulil ebsar."},
  {id: 307, sure_no: 14, ayet_no: 14, sure_adi: "Ali İmran", text: "زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوَاتِ مِنَ النِّسَٓاءِ وَالْبَن۪ينَ وَالْقَنَاط۪يرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْاَنْعَامِ وَالْحَرْثِۜ ذٰلِكَ مَتَاعُ الْحَيٰوةِ الدُّنْيَاۚ وَاللّٰهُ عِنْدَهُ حُسْنُ الْمَاٰبِ", translation: "Kadınlara, çocuklara, yığınlarla altın ve gümüşe, soylu atlara, davarlara ve ekinlere karşı tutkulu sevgi insanlara cazip gösterilmiştir. Bunlar, dünya hayatının geçimlikleridir. Oysa asıl iyi olan sonuç Allah'ın yanındadır.", transliteration: "Zuyyine lin nasi hubbuş şehevati minen nisai vel benine vel kanatiril mukantarati minez zehebi vel fıddati vel haylil musevvemeti vel en'ami vel hars, zalike metaul hayatid dunya, vallahu indehu HUSNUL MEAB."},
  {id: 308, sure_no: 15, ayet_no: 15, sure_adi: "Ali İmran", text: "قُلْ اَؤُ۬نَبِّئُكُمْ بِخَيْرٍ مِنْ ذٰلِكُمْۜ لِلَّذ۪ينَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا وَاَزْوَاجٌ مُطَهَّرَةٌ وَرِضْوَانٌ مِنَ اللّٰهِۜ وَاللّٰهُ بَص۪يرٌ بِالْعِبَادِۚ", translation: "De ki: \"Size bundan daha hayırlısını haber vereyim mi? Takva sahipleri için, Rabb'lerinin katında, içinde sürekli kalacakları, içinden nehirlerin aktığı Cennetler, arındırılmış eşler ve Allah'ın rızası vardır.\" Kuşkusuz, Allah, kullarını Hakkıyla Gören'dir.", transliteration: "Kul e unebbiukum bi hayrın min zalikum, lillezinettekav inde rabbihim cennatun tecri min tahtıhel enharu halidine fiha ve ezvacun mutahharatun ve rıdvanun minallah, vallahu basirun bil ıbad."},
  {id: 309, sure_no: 16, ayet_no: 16, sure_adi: "Ali İmran", text: "اَلَّذ۪ينَ يَقُولُونَ رَبَّنَٓا اِنَّـنَٓا اٰمَنَّا فَاغْفِرْ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ النَّارِۚ", translation: "Bu kimseler: \"Rabb'imiz! Biz, Sana iman ettik. Suçlarımızı bağışla, ateşin azabından bizi koru.\" derler;", transliteration: "Ellezine yekulune rabbena innena amenna fagfir lena zunubena ve kına azaben nar."},
  {id: 310, sure_no: 17, ayet_no: 17, sure_adi: "Ali İmran", text: "اَلصَّابِر۪ينَ وَالصَّادِق۪ينَ وَالْقَانِت۪ينَ وَالْمُنْفِق۪ينَ وَالْمُسْتَغْفِر۪ينَ بِالْاَسْحَارِ", translation: "Bunlar: Sabreden, dürüst olan, gönülden bağlı olan, infak eden ve seher vaktinde bağışlanma dileyenlerdir.", transliteration: "Es sabirine ves sadıkine vel kanitine vel munfikine vel mustagfirine bil eshar."},
  {id: 311, sure_no: 18, ayet_no: 18, sure_adi: "Ali İmran", text: "شَهِدَ اللّٰهُ اَنَّهُ لَٓا اِلٰهَ اِلَّا هُوَۙ وَالْمَلٰٓئِكَةُ وَاُو۬لُوا الْعِلْمِ قَٓائِماً بِالْقِسْطِۜ لَٓا اِلٰهَ اِلَّا هُوَ الْعَز۪يزُ الْحَك۪يمُۜ", translation: "Kendisinden başka ilah olmadığına Allah tanıktır. Melekler ve hakkaniyeti gözeten ilim sahipleri de tanıktırlar ki: O'ndan başka ilah yoktur. O, Mutlak Üstün Olan ve En Doğru Hüküm Veren'dir.", transliteration: "Şehidallahu ennehu la ilahe illa huve, vel melaiketu ve ulul ilmi kaimen bil kıst, la ilahe illa huvel azizul hakim."},
  {id: 312, sure_no: 19, ayet_no: 19, sure_adi: "Ali İmran", text: "اِنَّ الدّ۪ينَ عِنْدَ اللّٰهِ الْاِسْلَامُ۠ وَمَا اخْتَلَفَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ اِلَّا مِنْ بَعْدِ مَا جَٓاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْۜ وَمَنْ يَكْفُرْ بِاٰيَاتِ اللّٰهِ فَاِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ", translation: "Kuşkusuz, Allah katında din, İslam'dır. Kitap verilenler, kendilerine bilgi geldikten sonra ihtirasları nedeniyle ihtilafa düştüler. Kim, Allah'ın ayetlerini kabul etmezse bilsin ki, kuşkusuz Allah, Hesabı Çabuk Gören'dir.", transliteration: "İnned dine indallahil islam, ve mahtelefellezine utul kitabe illa min ba'di ma caehumulılmu bagyen beynehum, ve men yekfur bi ayatillahi fe innallahe seriul hısab."},
  {id: 313, sure_no: 20, ayet_no: 20, sure_adi: "Ali İmran", text: "فَاِنْ حَٓاجُّوكَ فَقُلْ اَسْلَمْتُ وَجْهِيَ لِلّٰهِ وَمَنِ اتَّـبَعَنِۜ وَقُلْ لِلَّذ۪ينَ اُو۫تُوا الْكِتَابَ وَالْاُمِّيّ۪نَ ءَاَسْلَمْتُمْۜ فَاِنْ اَسْلَمُوا فَقَدِ اهْتَدَوْاۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلَاغُۜ وَاللّٰهُ بَص۪يرٌ بِالْعِبَادِ۟", translation: "Eğer, seninle tartışırlarsa de ki: \"Ben, bütün benliğimle Allah'a teslim oldum, bana tabi olanlar da.\" Kendilerine kitap verilenlere ve kitap verilmeyenlere: \"Siz de teslim oldunuz mu?\" de. Eğer teslim olmuşlarsa, kuşkusuz doğru yolu bulmuşlardır. Eğer yüz çevirirlerse, sana düşen, yalnızca duyurmaktır. Allah, Kullarını Hakkıyla Gören'dir.", transliteration: "Fe in haccuke fe kul eslemtu vechiye lillahi ve menittebean, ve kul lillezine utul kitabe vel ummiyyine e eslemtum, fe in eslemu fe kadihtedev, ve in tevellev fe innema aleykel belag, vallahu basirun bil ibad."},
  {id: 314, sure_no: 21, ayet_no: 21, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ يَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَيَقْتُلُونَ النَّبِيّ۪نَ بِغَيْرِ حَقٍّۙ وَيَقْتُلُونَ الَّذ۪ينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ النَّاسِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَل۪يمٍ", translation: "Allah'ın ayetlerini küfredenler, haksız yere Nebi'lerini öldürenler, insanlardan hakkaniyetli olmayı isteyenleri öldürenler var ya, onlara can yakıcı bir azabı haber ver.", transliteration: "İnnellezine yekfurune bi ayatillahi ve yaktulunen nebiyyine bi gayri hakkın ve yaktulunellezine ye'murune bil kıstı minen nasi, fe beşşirhum bi azabin elim."},
  {id: 315, sure_no: 22, ayet_no: 22, sure_adi: "Ali İmran", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ حَبِطَتْ اَعْمَالُهُمْ فِي الدُّنْيَا وَالْاٰخِرَةِۘ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ", translation: "İşte o kimselerin, dünyada da ahirette de yaptıkları boşa gitmiştir. Ve onların hiçbir yardımcıları da yoktur.", transliteration: "Ulaikellezine habitat a'maluhum fid dunya vel ahirah, ve ma lehum min nasırin."},
  {id: 316, sure_no: 23, ayet_no: 23, sure_adi: "Ali İmran", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ اُو۫تُوا نَص۪يباً مِنَ الْكِتَابِ يُدْعَوْنَ اِلٰى كِتَابِ اللّٰهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلّٰى فَر۪يقٌ مِنْهُمْ وَهُمْ مُعْرِضُونَ", translation: "Kitap'tan nasiplenmiş olanları görüyorsun değil mi? Bunlar, aralarında hükmetmesi için Kitap'a çağrıldıklarında bir kısmı, döneklik ederek ondan yüz çeviriyor.", transliteration: "E lem tera ilellezine utu nasiben minel kitabi yud'avne ila kitabillahi li yahkume beynehum summe yetevella ferikun minhum ve hum mu'ridun."},
  {id: 317, sure_no: 24, ayet_no: 24, sure_adi: "Ali İmran", text: "ذٰلِكَ بِاَنَّهُمْ قَالُوا لَنْ تَمَسَّنَا النَّارُ اِلَّٓا اَيَّاماً مَعْدُودَاتٍۖ وَغَرَّهُمْ ف۪ي د۪ينِهِمْ مَا كَانُوا يَفْتَرُونَ", translation: "Bu dönekliklerinin nedeni, onların: \"Ateş bize sayılı birkaç günün dışında dokunmayacak.\" şeklindeki inançlarıdır. Uydurup dinlerine yakıştırdıkları bu tür şeyler onları yanıltmaktadır.", transliteration: "Zalike bi ennehum kalu len temessenen naru illa eyyamen ma'dudat, ve garrahum fi dinihim ma kanu yefterun."},
  {id: 318, sure_no: 25, ayet_no: 25, sure_adi: "Ali İmran", text: "فَكَيْفَ اِذَا جَمَعْنَاهُمْ لِيَوْمٍ لَا رَيْبَ ف۪يهِ وَوُفِّيَتْ كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ", translation: "Gelmesi kesin olan günde, onları topladığımız zaman, halleri nasıl olacak? Onlara haksızlık yapılmadan yaptıklarının karşılığı tam olarak verilecek.", transliteration: "Fe keyfe iza cema'nahum li yevmin la raybe fihi ve vuffiyet kullu nefsin ma kesebet ve hum la yuzlemun."},
  {id: 319, sure_no: 26, ayet_no: 26, sure_adi: "Ali İmran", text: "قُلِ اللّٰهُمَّ مَالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشَٓاءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَٓاءُۘ وَتُعِزُّ مَنْ تَشَٓاءُ وَتُذِلُّ مَنْ تَشَٓاءُۜ بِيَدِكَ الْخَيْرُۜ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "De ki: \"Ey mülkün sahibi Allah'ım! Sen mülkü hak edene verirsin, mülkü hak edenden çekip alırsın. Hak edeni aziz, hak edeni zelil edersin. Hayır, Sen'in elindedir. Kuşkusuz Sen Her Şeye Güç Yetiren'sin.\"", transliteration: "Kulillahumme malikel mulki tu'til mulke men teşau ve tenziul mulke mimmen teşa', ve tuizzu men teşau ve tuzillu men teşa', bi yedikel hayr, inneke ala kulli şey'in kadir."},
  {id: 320, sure_no: 27, ayet_no: 27, sure_adi: "Ali İmran", text: "تُولِجُ الَّيْلَ فِي النَّهَارِ وَتُولِجُ النَّهَارَ فِي الَّيْلِۘ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّۘ وَتَرْزُقُ مَنْ تَشَٓاءُ بِغَيْرِ حِسَابٍ", translation: "\"Geceyi gündüze dönüştürür, gündüzü de geceye dönüştürürsün. Diriyi, ölüden çıkarırsın, ölüyü de diriden çıkarırsın. Hak edene hesapsız rızık verirsin.\"", transliteration: "Tulicul leyle fin nehari ve tulicun nehara fil leyl, ve tuhricul hayya minel meyyiti ve tuhricul meyyite minel hayy, ve terzuku men teşau bi gayri hısab."},
  {id: 321, sure_no: 28, ayet_no: 28, sure_adi: "Ali İmran", text: "لَا يَتَّخِذِ الْمُؤْمِنُونَ الْكَافِر۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ الْمُؤْمِن۪ينَۚ وَمَنْ يَفْعَلْ ذٰلِكَ فَلَيْسَ مِنَ اللّٰهِ ف۪ي شَيْءٍ اِلَّٓا اَنْ تَتَّقُوا مِنْهُمْ تُقٰيةًۜ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهُۜ وَاِلَى اللّٰهِ الْمَص۪يرُ", translation: "Mü'minler, Mü'minleri bırakıp da Kafirleri evliya edinmesinler. Kafirleri evliya edinenin, Allah'la bir bağı kalmaz. Ancak onlardan korunmanız başka. Allah, sizi, kendisine karşı gelmekten sakındırır. Dönüşünüz yalnızca Allah'adır.", transliteration: "La yettehizil mu'minunel kafirine evliyae min dunil mu'minin, ve men yef'al zalike fe leyse minallahi fi şey'in illa en tetteku minhum tukata, ve yuhazzirukumullahu nefseh, ve ilallahil masir."},
  {id: 322, sure_no: 29, ayet_no: 29, sure_adi: "Ali İmran", text: "قُلْ اِنْ تُخْفُوا مَا ف۪ي صُدُورِكُمْ اَوْ تُبْدُوهُ يَعْلَمْهُ اللّٰهُۜ وَيَعْلَمُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "De ki: \"İçinizdekini gizleseniz de açığa vursanız da Allah onu bilir. O, yerde ve gökte olan her şeyi bilir. Kuşkusuz, Allah Her Şeye Güç Yetiren'dir.\"", transliteration: "Kul in tuhfu ma fi sudurikum ev tubduhu ya'lemhullah, ve ya'lemu ma fis semavati ve ma fil ard, vallahu ala kulli şey'in kadir."},
  {id: 323, sure_no: 30, ayet_no: 30, sure_adi: "Ali İmran", text: "يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَا عَمِلَتْ مِنْ خَيْرٍ مُحْضَراًۚۛ وَمَا عَمِلَتْ مِنْ سُٓوءٍۚۛ تَوَدُّ لَوْ اَنَّ بَيْنَهَا وَبَيْنَهُٓ اَمَداً بَع۪يداًۜ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهُۜ وَاللّٰهُ رَؤُ۫فٌ بِالْعِبَادِ۟", translation: "O Gün herkes, iyilik ve kötülük olarak ne yaptıysa onu karşısında bulur. Yaptığı kötülükle kendi arasında uzak bir mesafe bulunmasını ister. Allah, sizin kendisine karşı gelmekten sakınmanızı ister. Allah, kullarına karşı Çok Şefkatli'dir.", transliteration: "Yevme tecidu kullu nefsin ma amilet min hayrin muhdaran, ve ma amilet min su', teveddu lev enne beyneha ve beynehu emeden baida, ve yuhazzirukumullahu nefseh, vallahu raufun bil ıbad."},
  {id: 324, sure_no: 31, ayet_no: 31, sure_adi: "Ali İmran", text: "قُلْ اِنْ كُنْتُمْ تُحِبُّونَ اللّٰهَ فَاتَّبِعُون۪ي يُحْبِبْكُمُ اللّٰهُ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْۜ  وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "De ki: \"Eğer Allah'ı seviyorsanız, bana uyun ki Allah da sizi sevsin ve suçlarınızı bağışlasın.\" Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Kul in kuntum tuhibbunallahe fettebiuni yuhbibkumullahu ve yagfir lekum zunubekum, vallahu gafurun rahim."},
  {id: 325, sure_no: 32, ayet_no: 32, sure_adi: "Ali İmran", text: "قُلْ اَط۪يعُوا اللّٰهَ وَالرَّسُولَۚ فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ لَا يُحِبُّ الْكَافِر۪ينَ", translation: "De ki: \"Allah'a ve Resul'e itaat edin.\" Eğer yüz çevirirlerse, kuşkusuz, Allah, Kafirleri sevmez.", transliteration: "Kul etiullahe ver resul, fe in tevellev fe innallahe la yuhibbul kafirin."},
  {id: 326, sure_no: 33, ayet_no: 33, sure_adi: "Ali İmran", text: "اِنَّ اللّٰهَ اصْطَفٰٓى اٰدَمَ وَنُوحاً وَاٰلَ اِبْرٰه۪يمَ وَاٰلَ عِمْرٰنَ عَلَى الْعَالَم۪ينَۙ", translation: "Allah, Adem'i, Nuh'u, İbrahim Soyu'nu ve İmran Soyu'nu insanların üzerine seçti.", transliteration: "İnnallahestafa ademe ve nuhan ve ale ibrahime ve ale imrane alel alemin."},
  {id: 327, sure_no: 34, ayet_no: 34, sure_adi: "Ali İmran", text: "ذُرِّيَّةً بَعْضُهَا مِنْ بَعْضٍۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌۚ", translation: "Onlar, birbirlerinden türemiş bir soydur. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Zurriyyeten ba'duha min ba'd, vallahu semiun alim."},
  {id: 328, sure_no: 35, ayet_no: 35, sure_adi: "Ali İmran", text: "اِذْ قَالَتِ امْرَاَتُ عِمْرٰنَ رَبِّ اِنّ۪ي نَذَرْتُ لَكَ مَا ف۪ي بَطْن۪ي مُحَرَّراً فَتَقَبَّلْ مِنّ۪يۚ اِنَّكَ اَنْتَ السَّم۪يعُ الْعَل۪يمُ", translation: "Hani, bir zaman İmran'ın Hanımı: \"Rabb'im! Karnımdaki hür bırakılmış olanı sana adadım. Benden kabul et. Kuşkusuz, Sen Her Şeyi Duyan'sın, Her Şeyi Bilen'sin.\" demişti.", transliteration: "İz kalet imraetu ımrane rabbi inni nezertu leke ma fi batni muharraran fe tekabbel minni, inneke entes semiul alim."},
  {id: 329, sure_no: 36, ayet_no: 36, sure_adi: "Ali İmran", text: "فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ اِنّ۪ي وَضَعْتُهَٓا اُنْثٰىۜ وَاللّٰهُ اَعْلَمُ بِمَا وَضَعَتْۜ وَلَيْسَ الذَّكَرُ كَالْاُنْثٰىۚ وَاِنّ۪ي سَمَّيْتُهَا مَرْيَمَ وَاِنّ۪ٓي اُع۪يذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطَانِ الرَّج۪يمِ", translation: "Onu doğurunca: \"Rabb'im! Ben onu kız olarak doğurdum.\" Zaten Allah onun ne doğurduğunu daha iyi biliyordu. \"Erkek kız gibi değildir. İsmini, Meryem koydum. Onu ve soyunu, kovulmuş şeytana karşı Sen'in himayene bırakıyorum.\" dedi.", transliteration: "Fe lemma vadaatha kalet rabbi inni vada'tuha unsa vallahu a'lemu bi ma vadaat ve leysez zekeru kel unsa, ve inni semmeytuha meryeme ve inni uizuha bike ve zurriyyeteha mineş şeytanir racim."},
  {id: 330, sure_no: 37, ayet_no: 37, sure_adi: "Ali İmran", text: "فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٍ وَاَنْبَتَهَا نَبَاتاً حَسَناًۙ وَكَفَّلَهَا زَكَرِيَّاۜ  كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَۙ وَجَدَ عِنْدَهَا رِزْقاًۚ قَالَ يَا مَرْيَمُ اَنّٰى لَكِ هٰذَاۜ قَالَتْ هُوَ مِنْ عِنْدِ اللّٰهِۜ اِنَّ اللّٰهَ يَرْزُقُ مَنْ يَشَٓاءُ بِغَيْرِ حِسَابٍ", translation: "Bunun üzerine Rabb'i onu iyi bir şekilde kabul etti. Ve onu iyi bir şekilde yetiştirdi. Onu, Zekeriya'nın korumasına verdi. Zekeriya ne zaman mihraba girse, onun yanında yiyecek bir şey bulurdu. \"Ey Meryem! Bunlar sana nereden geldi?\" derdi. O da \"Bunlar, Allah'tandır.\" derdi. Kuşkusuz, Allah, hak edeni, hesapsız olarak rızıklandırır.", transliteration: "Fe tekabbeleha rabbuha bi kabulin hasenin ve enbeteha nebaten hasenen, ve keffeleha zekeriyya kullema dehale aleyha zekeriyyal mihrabe, vecede indeha rızka, kale ya meryemu enna leki haza kalet huve min indillah, innallahe yerzuku men yeşau bi gayri hısab."},
  {id: 331, sure_no: 38, ayet_no: 38, sure_adi: "Ali İmran", text: "هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُۚ قَالَ رَبِّ هَبْ ل۪ي مِنْ لَدُنْكَ ذُرِّيَّةً طَيِّبَةًۚ اِنَّكَ سَم۪يعُ الدُّعَٓاءِ", translation: "Orada Zekeriya, Rabb'ine dua etti: \"Rabb'im! Bana katından iyi bir nesil bağışla. Kuşkusuz, Sen duayı işitensin.\"", transliteration: "Hunalike dea zekeriyya rabbeh, kale rabbi hebli min ledunke zurriyyeten tayyibeh, inneke semiud dua'."},
  {id: 332, sure_no: 39, ayet_no: 39, sure_adi: "Ali İmran", text: "فَنَادَتْهُ الْمَلٰٓئِكَةُ وَهُوَ قَٓائِمٌ يُصَلّ۪ي فِي الْمِحْرَابِۙ اَنَّ اللّٰهَ يُبَشِّرُكَ بِيَحْيٰى مُصَدِّقاً بِكَلِمَةٍ مِنَ اللّٰهِ وَسَيِّداً وَحَصُوراً وَنَبِياًّ مِنَ الصَّالِح۪ينَ", translation: "O, mihrapta salat ederken, melekler: \" Kuşkusuz Allah, seni Yahya ile müjdeliyor. O, Allah'tan gelen kelimeyi tasdik eden, toplumuna öncülük yapan, kendisine sahip olan, iyilerden bir Nebi olacak.\" diye seslendiler.", transliteration: "Fe nadethul melaiketu ve huve kaimun yusalli fil mihrabi, ennallahe yubeşşiruke bi yahya musaddikan bi kelimetin minallahi ve seyyiden ve hasuran ve nebiyyen mines salihin."},
  {id: 333, sure_no: 40, ayet_no: 40, sure_adi: "Ali İmran", text: "قَالَ رَبِّ اَنّٰى يَكُونُ ل۪ي غُلَامٌ وَقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَاَت۪ي عَاقِرٌۜ قَالَ كَذٰلِكَ اللّٰهُ يَفْعَلُ مَا يَشَٓاءُ", translation: "\"Ey Rabb'im! Ben iyice yaşlanmışken, hanımım da çocuktan kesilmişken, benim nasıl çocuğum olabilir!\" dedi. O da: \"Pekala olur, Allah dilediğini yapar.\" dedi.", transliteration: "Kale rabbi enna yekunu li gulamun ve kad beleganiyel kiberu vemraeti akir, kale kezalikellahu yef'alu ma yeşa'."},
  {id: 334, sure_no: 41, ayet_no: 41, sure_adi: "Ali İmran", text: "قَالَ رَبِّ اجْعَلْ ل۪ٓي اٰيَةًۜ قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَةَ اَيَّامٍ اِلَّا رَمْزاًۜ وَاذْكُرْ رَبَّكَ كَث۪يراً وَسَبِّـحْ بِالْعَشِيِّ وَالْاِبْكَارِ۟", translation: "\"Ey Rabb'im! Bana, bir ayet yap,\" dedi. O da: \"Bunun ayeti, işaret dili dışında insanlarla üç gün konuşmayacak olmandır. Rabb'ini çokça an, O'nu sabah akşam tesbih et.\" dedi.", transliteration: "Kale rabbic'al li ayeh, kale ayetuke ella tukellimen nase selasete eyyamin illa remza, vezkur rabbeke kesiran ve sebbih bil aşiyyi vel ibkar."},
  {id: 335, sure_no: 42, ayet_no: 42, sure_adi: "Ali İmran", text: "وَاِذْ قَالَتِ الْمَلٰٓئِكَةُ يَا مَرْيَمُ اِنَّ اللّٰهَ اصْطَفٰيكِ وَطَهَّرَكِ وَاصْطَفٰيكِ عَلٰى نِسَٓاءِ الْعَالَم۪ينَ", translation: "Hani melekler: \"Ey Meryem! Allah, seni seçti, arındırdı ve seni bütün dünya kadınlarına tercih etti.\" demişti.", transliteration: "Ve iz kaletil melaiketu ya meryemu innallahastafaki ve tahhareki vestafaki ala nisail alemin."},
  {id: 336, sure_no: 43, ayet_no: 43, sure_adi: "Ali İmran", text: "يَا مَرْيَمُ اقْنُت۪ي لِرَبِّكِ وَاسْجُد۪ي وَارْكَع۪ي مَعَ الرَّاكِع۪ينَ", translation: "\"Ey Meryem! Rabb'ine içtenlikle bağlan, secde et ve ruku edenlerle birlikte ruku et.\"", transliteration: "Ya meryemuknuti li rabbiki vescudi verkai mear rakiin."},
  {id: 337, sure_no: 44, ayet_no: 44, sure_adi: "Ali İmran", text: "ذٰلِكَ مِنْ اَنْـبَٓاءِ الْغَيْبِ نُوح۪يهِ اِلَيْكَۜ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يُلْقُونَ اَقْلَامَهُمْ اَيُّهُمْ يَكْفُلُ مَرْيَمَۖ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يَخْتَصِمُونَ", translation: "İşte bunlar, sana vahiy ile bildirdiğimiz gayba ait bilgilerdir. Meryem'i kim himaye edecek diye kalemlerini attıkları zaman da çekiştikleri zaman da sen yanlarında değildin.", transliteration: "Zalike min enbail gaybi nuhihi ileyk, ve ma kunte ledeyhim iz yulkune eklamehum eyyuhum yekfulu meryeme, ve ma kunte ledeyhim iz yahtesımun."},
  {id: 338, sure_no: 45, ayet_no: 45, sure_adi: "Ali İmran", text: "اِذْ قَالَتِ الْمَلٰٓئِكَةُ يَا مَرْيَمُ اِنَّ اللّٰهَ يُبَشِّرُكِ بِكَلِمَةٍ مِنْهُۗ اِسْمُهُ الْمَس۪يحُ ع۪يسَى ابْنُ مَرْيَمَ وَج۪يهاً فِي الدُّنْيَا وَالْاٰخِرَةِ وَمِنَ الْمُقَرَّب۪ينَۙ", translation: "Hani melekler: \"Ey Meryem! Allah, Kendi tarafından bir kelime ile seni müjdeliyor: İsmi Mesih, Meryem Oğlu İsa'dır. Dünya'da da ahiret'te de şeref sahibi, Allah'a çok yakın olanlardandır.\" dediler.", transliteration: "İz kaletil melaiketu ya meryemu innallahe yubeşşiruki bi kelimetin minh, ismuhul mesihu isebnu meryeme vecihan fid dunya vel ahıreti ve minel mukarrebin."},
  {id: 339, sure_no: 46, ayet_no: 46, sure_adi: "Ali İmran", text: "وَيُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلاً وَمِنَ الصَّالِح۪ينَ", translation: "\"O, beşikte de yetişkinliğinde de insanlarla konuşacak. O, salihlerden olacaktır.\"", transliteration: "Ve yukellimun nase fil mehdi ve kehlen ve mines salihin."},
  {id: 340, sure_no: 47, ayet_no: 47, sure_adi: "Ali İmran", text: "قَالَتْ رَبِّ اَنّٰى يَكُونُ ل۪ي وَلَدٌ وَلَمْ يَمْسَسْن۪ي بَشَرٌۜ قَالَ كَذٰلِكِ اللّٰهُ يَخْلُقُ مَا يَشَٓاءُۜ اِذَا قَضٰٓى اَمْراً فَاِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ", translation: "Rabb'im! Bana hiçbir beşer dokunmamışken, benim nasıl bir çocuğum olabilir?\" dedi. Allah: \"Öyle de olsa, Allah neyi dilerse onu yaratır.\" dedi. \"O, bir şeyin olmasına karar verdiği zaman ona sadece \"Ol!\" der, o da oluverir.\"", transliteration: "Kalet rabbi enna yekunu li veledun ve lem yemsesni beşer, kale kezalikillahu yahluku ma yeşa' iza kada emren fe innema yekulu lehu kun fe yekun."},
  {id: 341, sure_no: 48, ayet_no: 48, sure_adi: "Ali İmran", text: "وَيُعَلِّمُهُ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرٰيةَ وَالْاِنْج۪يلَۚ", translation: "\"Allah, Ona Kitap'ı, Hikmet'i, Tevrat'ı ve İncil'i öğretecek.\"", transliteration: "Ve yuallimuhul kitabe vel hikmete vet tevrate vel incil."},
  {id: 342, sure_no: 49, ayet_no: 49, sure_adi: "Ali İmran", text: "وَرَسُولاً اِلٰى بَن۪ٓي اِسْرَٓائ۪لَ اَنّ۪ي قَدْ جِئْتُكُمْ بِاٰيَةٍ مِنْ رَبِّكُمْۙ اَنّ۪ٓي اَخْلُقُ لَكُمْ مِنَ الطّ۪ينِ كَهَيْـَٔةِ الطَّيْرِ فَاَنْفُخُ ف۪يهِ فَيَكُونُ طَيْراً بِاِذْنِ اللّٰهِۚ وَاُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ وَاُحْـيِ الْمَوْتٰى بِاِذْنِ اللّٰهِۚ وَاُنَبِّئُكُمْ بِمَا تَأْكُلُونَ وَمَا تَدَّخِرُونَۙ ف۪ي بُيُوتِكُمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ", translation: "Bir Resul olarak İsrailoğullarına: \"Doğrusu size Rabb'inizden bir ayetle geldim. Sizin için çamurdan kuş şeklinde bir şey yaratırım. Ona üflerim, Allah'ın izniyle hemen kuş oluverir. Körü ve alacalıyı iyileştiririm. Allah'ın izni ile ölüleri diriltirim. Evlerinizde ne yediğinizi, ne biriktirdiğinizi size haber veririm. Eğer Mü'min iseniz kuşkusuz bunda sizin için bir ayet vardır.\"", transliteration: "Ve resulen ila beni israile enni kad ci'tukum bi ayetin min rabbikum, enni ehluku lekum minet tini ke heyetit tayri fe enfuhu fihi fe yekunu tayran bi iznillah, ve ubriul ekmehe vel ebrasa ve uhyil mevta bi iznillah, ve unebbiukum bi ma te'kulune ve ma teddehırune, fi buyutikum inne fi zalike le ayeten lekum in kuntum mu'minin."},
  {id: 343, sure_no: 50, ayet_no: 50, sure_adi: "Ali İmran", text: "وَمُصَدِّقاً لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰيةِ وَلِاُحِلَّ لَكُمْ بَعْضَ الَّذ۪ي حُرِّمَ عَلَيْكُمْ وَجِئْتُكُمْ بِاٰيَةٍ مِنْ رَبِّكُمْ فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِ", translation: "\"Ben, size Rabb'inizden, elinizin altında bulunan Tevrat'ı tasdik edici olarak ve daha önce size haram kılınmış bazı şeyleri helal kılmak için bir ayetle geldim. Allah'a karşı takvalı olun ve bana itaat edin.\"", transliteration: "Ve musaddikan lima beyne yedeyye minet tevrati ve li uhılle lekum ba'dallezi hurrime aleykum ve ci'tukum bi ayetin min rabbikum fettekullahe ve etiun."},
  {id: 344, sure_no: 51, ayet_no: 51, sure_adi: "Ali İmran", text: "اِنَّ اللّٰهَ رَبّ۪ي وَرَبُّكُمْ فَاعْبُدُوهُۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ", translation: "\"Allah, benim de Rabb'im, sizin de Rabb'inizdir. O halde O'na kulluk edin. Doğru yol budur.\"", transliteration: "İnnallahe rabbi ve rabbikum fa'buduh, haza sıratun mustakim."},
  {id: 345, sure_no: 52, ayet_no: 52, sure_adi: "Ali İmran", text: "فَلَمَّٓا اَحَسَّ ع۪يسٰى مِنْهُمُ الْكُفْرَ قَالَ مَنْ اَنْصَار۪ٓي اِلَى اللّٰهِۜ قَالَ الْحَوَارِيُّونَ نَحْنُ اَنْصَارُ اللّٰهِۚ اٰمَنَّا بِاللّٰهِۚ وَاشْهَدْ بِاَنَّا مُسْلِمُونَ", translation: "İsa, onların küfrünü sezince: \"Bana, Allah yolunda kim yardımcı olacak?\" dedi. Havariler: \"Biz Allah'ın yardımcılarıyız, Allah'a iman ettik, bizim teslim olduğumuza şahit ol.\" dediler.", transliteration: "Fe lemma ehassa isa min humul kufre kale men ensari ilallah, kalel havariyyune nahnu ensarullah, amenna billah, veşhed bi enna muslimun."},
  {id: 346, sure_no: 53, ayet_no: 53, sure_adi: "Ali İmran", text: "رَبَّنَٓا اٰمَنَّا بِمَٓا اَنْزَلْتَ وَاتَّبَعْنَا الرَّسُولَ فَاكْتُبْنَا مَعَ الشَّاهِد۪ينَ", translation: "\"Ey Rabb'imiz! İndirdiğine iman ettik, Resul'üne tabi olduk. Öyleyse bizi tanıklarla birlikte yaz.\"", transliteration: "Rabbena amenna bi ma enzelte vetteba'nar resule fektubna meaş şahidin."},
  {id: 347, sure_no: 54, ayet_no: 54, sure_adi: "Ali İmran", text: "وَمَكَرُوا وَمَكَرَ اللّٰهُۜ وَاللّٰهُ خَيْرُ الْمَاكِر۪ينَ۟", translation: "Onlar, plan yaptılar. Allah da plan yaptı. Allah, en iyi plan yapandır.", transliteration: "Ve mekeru ve mekarallah, vallahu hayrul makirin."},
  {id: 348, sure_no: 55, ayet_no: 55, sure_adi: "Ali İmran", text: "اِذْ قَالَ اللّٰهُ يَا ع۪يسٰٓى اِنّ۪ي مُتَوَفّ۪يكَ وَرَافِعُكَ اِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذ۪ينَ كَفَرُوا وَجَاعِلُ الَّذ۪ينَ اتَّبَعُوكَ فَوْقَ الَّذ۪ينَ كَفَرُٓوا اِلٰى يَوْمِ الْقِيٰمَةِۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاَحْكُمُ بَيْنَكُمْ ف۪يمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ", translation: "Hani, Allah, İsa'ya: \"Ey İsa! Seni öldürecek olan Ben'im. Seni Bana yükselteceğim. Seni Kafirlerden arındıracağım. Sana tabi olanları da Kıyamet Günü'ne kadar küfredenlerden üstün tutacağım. Sonra dönüşünüz yalnız Bana olacak; anlaşmazlığa düştüğünüz konular hakkında hükmü Ben vereceğim.\" dedi.", transliteration: "İz kalellahu ya isa inni muteveffike ve rafiuke ileyye ve mutahhiruke minellezine keferu ve cailullezinettebeuke fevkallezine keferu ila yevmil kıyameh, summe ileyye merciukum fe ahkumu beynekum fima kuntum fihi tahtelifun."},
  {id: 349, sure_no: 56, ayet_no: 56, sure_adi: "Ali İmran", text: "فَاَمَّا الَّذ۪ينَ كَفَرُوا فَاُعَذِّبُهُمْ عَذَاباً شَد۪يداً فِي الدُّنْيَا وَالْاٰخِرَةِۘ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ", translation: "\"Kafirlere gelince: Onları Dünya'da da Ahiret'te de şiddetli bir azaba çarptıracağım. Ve onların yardımcıları da olmayacaktır.\"", transliteration: "Fe emmellezine keferu fe uazzibuhum azaben şediden fid dunya vel ahıreti, ve ma lehum min nasirin."},
  {id: 350, sure_no: 57, ayet_no: 57, sure_adi: "Ali İmran", text: "وَاَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفّ۪يهِمْ اُجُورَهُمْۜ  وَاللّٰهُ لَا يُحِبُّ الظَّالِم۪ينَ", translation: "İman edip salihatı yapanlara gelince: Allah, yaptıklarının karşılığını eksiksiz verecektir. Allah, haksızlık yapanları sevmez.", transliteration: "Ve emmellezine amenu ve amilus salihati fe yuveffihim ucurehum vallahu la yuhibbuz zalimin."},
  {id: 351, sure_no: 58, ayet_no: 58, sure_adi: "Ali İmran", text: "ذٰلِكَ نَتْلُوهُ عَلَيْكَ مِنَ الْاٰيَاتِ وَالذِّكْرِ الْحَك۪يمِ", translation: "Bu sana anlattıklarımız, ayetlerden ve hakim olan Zikir'dendir.", transliteration: "Zalike netluhu aleyke minel ayati vez zikril hakim hakimi)."},
  {id: 352, sure_no: 59, ayet_no: 59, sure_adi: "Ali İmran", text: "اِنَّ مَثَلَ ع۪يسٰى عِنْدَ اللّٰهِ كَمَثَلِ اٰدَمَۜ خَلَقَهُ مِنْ تُرَابٍ ثُمَّ قَالَ لَهُ كُنْ فَيَكُونُ", translation: "İsa'nın durumu Allah'ın yanında Adem'in durumu gibidir. Onu topraktan yarattı, sonra ona \"Ol!\" dedi o da oluverdi.", transliteration: "İnne mesele isa indallahi ke meseli adem, halakahu min turabin summe kale lehu kun fe yekun."},
  {id: 353, sure_no: 60, ayet_no: 60, sure_adi: "Ali İmran", text: "اَلْحَقُّ مِنْ رَبِّكَ فَلَا تَكُنْ مِنَ الْمُمْتَر۪ينَ", translation: "Hakk, Rabb'indendir. Sakın kuşku duyanlardan olma.", transliteration: "El hakku min rabbike fe la tekun minel mumterin."},
  {id: 354, sure_no: 61, ayet_no: 61, sure_adi: "Ali İmran", text: "فَمَنْ حَٓاجَّكَ ف۪يهِ مِنْ بَعْدِ مَا جَٓاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ اَبْنَٓاءَنَا وَاَبْنَٓاءَكُمْ وَنِسَٓاءَنَا وَنِسَٓاءَكُمْ وَاَنْفُسَنَا وَاَنْفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَعْنَتَ اللّٰهِ عَلَى الْكَاذِب۪ينَ", translation: "Sana gelen ilimden sonra, her kim seninle tartışmaya girerse, onlara de ki: \"Gelin çocuklarımızı ve çocuklarınızı, kadınlarımızı ve kadınlarınızı, kendimizi ve kendinizi çağıralım; sonra da içtenlikle Allah'ın lanetinin yalancıların üzerine olmasını dileyelim.\"", transliteration: "Fe men hacceke fihi min ba'di ma caeke minel ilmi fe kul tealev ned'u ebnaena ve ebnaekum ve nisaena ve nisaekum ve enfusena ve enfusekum summe nebtehil fe nec'al la'netallahi alel kazibin."},
  {id: 355, sure_no: 62, ayet_no: 62, sure_adi: "Ali İmran", text: "اِنَّ هٰذَا لَهُوَ الْقَصَصُ الْحَقُّۚ وَمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُۜ وَاِنَّ اللّٰهَ لَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Onlar, gerçeğin haberleridir. Allah'tan başka ilah yoktur. Kuşkusuz, Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "İnne haza le huvel kasasul hakk, ve ma min ilahin illallah, ve innellahe le huvel azizul hakim."},
  {id: 356, sure_no: 63, ayet_no: 63, sure_adi: "Ali İmran", text: "فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ عَل۪يمٌ بِالْمُفْسِد۪ينَ۟", translation: "Eğer yüz çevirirlerse, Allah, kimlerin bozguncu olduğunu bilir.", transliteration: "Fe in tevellev fe innallahe alimun bil mufsidin."},
  {id: 357, sure_no: 64, ayet_no: 64, sure_adi: "Ali İmran", text: "قُلْ يَٓا اَهْلَ الْكِتَابِ تَعَالَوْا اِلٰى كَلِمَةٍ سَوَٓاءٍ بَيْنَنَا وَبَيْنَكُمْ اَلَّا نَعْبُدَ اِلَّا اللّٰهَ وَلَا نُشْرِكَ بِه۪ شَيْـٔاً وَلَا يَتَّخِذَ بَعْضُنَا بَعْضاً اَرْبَاباً مِنْ دُونِ اللّٰهِۜ فَاِنْ تَوَلَّوْا فَقُولُوا اشْهَدُوا بِاَنَّا مُسْلِمُونَ", translation: "De ki: \"Ey Kitap Ehli! Gelin aramızda ortak olan bir kelimede anlaşalım: Allah'tan başka hiçbir şeye kulluk etmeyelim, O'na hiçbir şeyi şirk koşmayalım ve Allah'ın yanı sıra kimimiz kimimizi rabler edinmeyelim.\" Eğer yüz çevirirlerse deyin ki: \"Tanık olun, biz gerçek Müslim olanlarız.\"", transliteration: "Kul ya ehlel kitabi tealev ila kelimetin sevain beynena ve beynekum ella na'bude illallahe ve la nuşrike bihi şey'en ve la yettehize ba'duna ba'den erbaben min dunillah, fe in tevellev fe kuluşhedu bi enna muslimun."},
  {id: 358, sure_no: 65, ayet_no: 65, sure_adi: "Ali İmran", text: "يَٓا اَهْلَ الْكِتَابِ لِمَ تُحَٓاجُّونَ ف۪ٓي اِبْرٰه۪يمَ وَمَٓا اُنْزِلَتِ التَّوْرٰيةُ وَالْاِنْج۪يلُ اِلَّا مِنْ بَعْدِه۪ۜ اَفَلَا تَعْقِلُونَ", translation: "Ey Kitap Ehli! İbrahim hakkında niçin tartışıyorsunuz? Oysaki Tevrat da İncil de ondan sonra indirildi. Hala akletmiyor musunuz?", transliteration: "Ya ehlel kitabi lime tuhaccune fi ibrahime ve ma unziletit tevratu vel incilu illa min ba'dih, e fe la ta'kılun."},
  {id: 359, sure_no: 66, ayet_no: 66, sure_adi: "Ali İmran", text: "هَٓا اَنْتُمْ هٰٓؤُ۬لَٓاءِ حَاجَجْتُمْ ف۪يمَا لَكُمْ بِه۪ عِلْمٌ فَلِمَ تُحَٓاجُّونَ ف۪يمَا لَيْسَ لَكُمْ بِه۪ عِلْمٌۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ", translation: "Diyelim ki bildiğiniz şeyler hakkında tartıştınız. Peki, hiçbir bilginiz olmayan konularda ne diye tartışıyorsunuz? Allah bilir, siz bilmezsiniz.", transliteration: "Ha entum haulai hacectum fi ma lekum bihi ilmun fe lime tuhaccune fi ma leyse lekum bihi ilm, vallahu ya'lemu ve entum la ta'lemun."},
  {id: 360, sure_no: 67, ayet_no: 67, sure_adi: "Ali İmran", text: "مَا كَانَ اِبْرٰه۪يمُ يَهُودِياًّ وَلَا نَصْرَانِياًّ وَلٰكِنْ كَانَ حَن۪يفاً مُسْلِماًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ", translation: "İbrahim ne Yahudi ne de Nasraniy'di. O, Allah'ı birleyen ve O'na teslim olandı. O, Müşriklerden değildi.", transliteration: "Ma kane ibrahimu yahudiyyen ve la nasraniyyen ve lakin kane hanifen muslima, ve ma kane minel muşrikin."},
  {id: 361, sure_no: 68, ayet_no: 68, sure_adi: "Ali İmran", text: "اِنَّ اَوْلَى النَّاسِ بِاِبْرٰه۪يمَ لَلَّذ۪ينَ اتَّبَعُوهُ وَهٰذَا النَّبِيُّ وَالَّذ۪ينَ اٰمَنُواۜ وَاللّٰهُ وَلِيُّ الْمُؤْمِن۪ينَ", translation: "İnsanların İbrahim'e en yakın olanları ona tabi olanlar, bu Nebi ve İman Edenlerdir. Allah, Mü'minlerin velisidir.", transliteration: "İnne evlen nasi bi ibrahime lellezinettebeuhu ve hazan nebiyyu vellezine amenu vallahu veliyyul mu'minin."},
  {id: 362, sure_no: 69, ayet_no: 69, sure_adi: "Ali İmran", text: "وَدَّتْ طَٓائِفَةٌ مِنْ اَهْلِ الْكِتَابِ لَوْ يُضِلُّونَكُمْۜ وَمَا يُضِلُّونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَشْعُرُونَ", translation: "Kitap Ehli'nden bazı kimseler, sizi yolunuzdan saptırmak ister. Onlar, ancak kendilerini saptırıyorlar. Fakat bunu idrak edemiyorlar.", transliteration: "Veddet taifetun min ehlil kitabi lev yudillunekum ve ma yudıllune illa enfusehum ve ma yeş'urun."},
  {id: 363, sure_no: 70, ayet_no: 70, sure_adi: "Ali İmran", text: "يَٓا اَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَاَنْتُمْ تَشْهَدُونَ", translation: "Ey Kitap Ehli! Gerçeği bildiğiniz halde, niçin Allah'ın ayetlerini küfrediyorsunuz?", transliteration: "Ya ehlel kitabi lime tekfurune bi ayatillahi ve entum teşhedun."},
  {id: 364, sure_no: 71, ayet_no: 71, sure_adi: "Ali İmran", text: "يَٓا اَهْلَ الْكِتَابِ لِمَ تَلْبِسُونَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُونَ الْحَقَّ وَاَنْتُمْ تَعْلَمُونَ۟", translation: "Ey Kitap Ehli! Niçin Hakkı Batıl'la karıştırıyor ve bildiğiniz halde gerçeği gizliyorsunuz?", transliteration: "Ya ehlel kitabi lime telbisunel hakka bil batılı ve tektumunel hakka ve entum ta'lemun."},
  {id: 365, sure_no: 72, ayet_no: 72, sure_adi: "Ali İmran", text: "وَقَالَتْ طَٓائِفَةٌ مِنْ اَهْلِ الْكِتَابِ اٰمِنُوا بِالَّـذ۪ٓي اُنْزِلَ عَلَى الَّذ۪ينَ اٰمَنُوا وَجْهَ النَّهَارِ وَاكْفُرُٓوا اٰخِرَهُ لَعَلَّهُمْ يَرْجِعُونَۚ", translation: "Kitap Ehli'nden bir grup dedi ki: \"İman Edenlere indirilene günün başında inanın, günün sonunda da küfredin. Belki dinlerinden dönerler.\"", transliteration: "Ve kalet taifetun min ehlil kitabi aminu billezi unzile alellezine amenu vechen nehari vekfuru ahirahu leallehum yerciun."},
  {id: 366, sure_no: 73, ayet_no: 73, sure_adi: "Ali İmran", text: "وَلَا تُؤْمِنُٓوا اِلَّا لِمَنْ تَبِـعَ د۪ينَكُمْۜ قُلْ اِنَّ الْهُدٰى هُدَى اللّٰهِۙ اَنْ يُؤْتٰٓى اَحَدٌ مِثْلَ مَٓا اُو۫ت۪يتُمْ اَوْ يُحَٓاجُّوكُمْ عِنْدَ رَبِّكُمْۜ قُلْ اِنَّ الْفَضْلَ بِيَدِ اللّٰهِۚ يُؤْت۪يهِ مَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌۚ", translation: "\"Kendi dininize tabi olanlardan başkasına inanmayın.\" dediler. De ki: \"Hidayet, Allah'ın hidayetidir. Birine, size verilmiş olanın benzerinin verilmesinden veya Rabb'inizin katından aleyhinize kanıt getirebileceklerinden ötürü mü böyle söylüyorsunuz?\" De ki: \"Lütuf, Allah'ın elindedir, onu hak edene verir.\" Allah, Yardımı Çok Kapsamlı Olan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve la tu'minu illa li men tebia dinekum, kul innel huda hudallahi en yu'ta ehadun misle ma utitum ev yuhaccukum inde rabbikum, kul innel fadla bi yedillah, yu'tihi men yeşa', vallahu vasiun alim."},
  {id: 367, sure_no: 74, ayet_no: 74, sure_adi: "Ali İmran", text: "يَخْتَصُّ بِرَحْمَتِه۪ مَنْ يَشَٓاءُۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ", translation: "Allah, rahmetini hak edene has kılar. Allah, Büyük Lütuf Sahibi'dir.", transliteration: "Yahtassu bi rahmetihi men yeşa', vallahu zul fadlil azim."},
  {id: 368, sure_no: 75, ayet_no: 75, sure_adi: "Ali İmran", text: "وَمِنْ اَهْلِ الْكِتَابِ مَنْ اِنْ تَأْمَنْهُ بِقِنْطَارٍ يُؤَدِّه۪ٓ اِلَيْكَۚ وَمِنْهُمْ مَنْ اِنْ تَأْمَنْهُ بِد۪ينَارٍ لَا يُؤَدِّه۪ٓ اِلَيْكَ اِلَّا مَا دُمْتَ عَلَيْهِ قَٓائِماًۜ ذٰلِكَ بِاَنَّهُمْ قَالُوا لَيْسَ عَلَيْنَا فِي الْاُمِّيّ۪نَ سَب۪يلٌۚ وَيَقُولُونَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ", translation: "Kitap Ehli'nden öylesi vardır ki, kendisine yüklerle mal emanet etsen, onu sana eksiksiz iade eder. Öylesi de var ki bir dinar emanet etsen, başına dikilmedikçe onu sana iade etmez. Bunun sebebi: \"Ummilerin malını yemede vebal yoktur.\" diye düşünmelerindendir. Onlar, bile bile, Allah adına yalan söylerler.", transliteration: "Ve min ehlil kitabi men in te'menhu bi kıntarin yueddihi ileyk, ve minhum men in te'menhu bi dinarin la yueddihi ileyke illa ma dumte aleyhi kaima, zalike bi ennehum kalu leyse aleyna fil ummiyyine sebil, ve yekulune alallahil kezibe ve hum ya'lemun."},
  {id: 369, sure_no: 76, ayet_no: 76, sure_adi: "Ali İmran", text: "بَلٰى مَنْ اَوْفٰى بِعَهْدِه۪ وَاتَّقٰى فَاِنَّ اللّٰهَ يُحِبُّ الْمُتَّق۪ينَ", translation: "Hayır! Kim sözünü yerine getirir ve takvalı olursa, kuşkusuz Allah, takvalı olanları sever.", transliteration: "Bela men evfa bi ahdihi vetteka fe innallahe yuhibbul muttekin."},
  {id: 370, sure_no: 77, ayet_no: 77, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ يَشْتَرُونَ بِعَهْدِ اللّٰهِ وَاَيْمَانِهِمْ ثَمَناً قَل۪يلاً اُو۬لٰٓئِكَ لَا خَلَاقَ لَهُمْ فِي الْاٰخِرَةِ وَلَا يُكَلِّمُهُمُ اللّٰهُ وَلَا يَنْظُرُ اِلَيْهِمْ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكّ۪يهِمْۖ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Allah'a verdikleri sözü ve yeminlerini az bir değere değiştirenler var ya; işte onlara ahirette bir pay yoktur. Allah, onlarla Kıyamet Günü konuşmayacak, onlara bakmayacak ve onları temize çıkarmayacaktır. Onlar için can yakıcı bir azap vardır.", transliteration: "İnnellezine yeşterune bi ahdillahi ve eymanihim semenen kalilen ulaike la halaka lehum fil ahırati ve la yukellimuhumullahu ve la yenzuru ileyhim yevmel kıyameti ve la yuzekkihim ve lehum azabun elim."},
  {id: 371, sure_no: 78, ayet_no: 78, sure_adi: "Ali İmran", text: "وَاِنَّ مِنْهُمْ لَفَر۪يقاً يَلْوُ۫نَ اَلْسِنَتَهُمْ بِالْكِتَابِ لِتَحْسَبُوهُ مِنَ الْكِتَابِ وَمَا هُوَ مِنَ الْكِتَابِۚ وَيَقُولُونَ هُوَ مِنْ عِنْدِ اللّٰهِ وَمَا هُوَ مِنْ عِنْدِ اللّٰهِۚ وَيَقُولُونَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ", translation: "Onlardan bir kısım kimseler de Kitap'ta olmadığı halde, Kitap'tan sanasınız diye Kitap'ı dillerini eğip bükerek okurlar. Oysa o Kitap'tan değildir. \"Bu Allah katındandır.\" diyorlar, oysa o, Allah'tan değildir. Allah adına bile bile yalan söylüyorlar.", transliteration: "Ve inne minhum le ferikan yelvune elsinetehum bil kitabi li tahsebuhu minel kitabi ve ma huve minel kitab, ve yekulune huve min indillahi ve ma huve min indillah, ve yekulune alallahil kezibe ve hum ya'lemun."},
  {id: 372, sure_no: 79, ayet_no: 79, sure_adi: "Ali İmran", text: "مَا كَانَ لِبَشَرٍ اَنْ يُؤْتِيَهُ اللّٰهُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِبَاداً ل۪ي مِنْ دُونِ اللّٰهِ وَلٰكِنْ كُونُوا رَبَّانِيّ۪نَ بِمَا كُنْتُمْ تُعَلِّمُونَ الْكِتَابَ وَبِمَا كُنْتُمْ تَدْرُسُونَۙ", translation: "Allah'ın kendisine Kitap, Hüküm ve Nebi'lik verdiği bir beşerin, insanlara \"Allah'ın yanı sıra bana da kulluk edin.\" demesi yakışmaz. Ancak, okuyup öğrendiğiniz Kitap'ın gereği olarak, \"Kulluğunuz yalnızca Rabb'inize ait olsun.\" demesi gerekir.", transliteration: "Ma kane li beşerin en yu'tiyehullahul kitabe vel hukme ven nubuvvete summe yekule lin nasi kunu ıbaden li min dunillahi ve lakin kunu rabbaniyyine bi ma kuntum tuallimunel kitabe ve bima kuntum tedrusun."},
  {id: 373, sure_no: 80, ayet_no: 80, sure_adi: "Ali İmran", text: "وَلَا يَأْمُرَكُمْ اَنْ تَتَّخِذُوا الْمَلٰٓئِكَةَ وَالنَّبِيّ۪نَ اَرْبَاباًۜ اَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ اِذْ اَنْتُمْ مُسْلِمُونَ۟", translation: "O, sizden Melekleri ve Nebileri Rabb'ler edinmenizi istemez. Siz, Müslim olduktan sonra, sizden Kafir olmanızı mı isteyecek?", transliteration: "Ve la ye'murekum en tettehizul melaikete ven nebiyyine erbaba, e ye'murukum bil kufri ba'de iz entum muslimun."},
  {id: 374, sure_no: 81, ayet_no: 81, sure_adi: "Ali İmran", text: "وَاِذْ اَخَذَ اللّٰهُ م۪يثَاقَ النَّبِيّ۪نَ لَـمَٓا اٰتَيْتُكُمْ مِنْ كِتَابٍ وَحِكْمَةٍ ثُمَّ جَٓاءَكُمْ رَسُولٌ مُصَدِّقٌ لِمَا مَعَكُمْ لَتُؤْمِنُنَّ بِه۪ وَلَتَنْصُرُنَّهُۜ قَالَ ءَاَقْرَرْتُمْ وَاَخَذْتُمْ عَلٰى ذٰلِكُمْ اِصْر۪يۜ قَالُٓوا اَقْرَرْنَاۜ قَالَ فَاشْهَدُوا وَاَنَا۬ مَعَكُمْ مِنَ الشَّاهِد۪ينَ", translation: "Hani Allah, Nebilerden söz almıştı: \"Size, Kitap ve Hikmet verdim; şimdi yanınızdakini tasdik eden bir resul geldiğinde, ona kesinlikle inanacak ve yardım edeceksiniz.\" \"Bunu kabul ettiniz mi? Bu önemli görevi üstlendiniz mi?\" demişti. \"Kabul ettik.\" dediler. Allah: \"Öyleyse tanık olun, Ben de sizinle birlikte tanık olanlardanım.\" dedi.", transliteration: "Ve iz ehazallahu misakan nebiyyine lema ateytukum min kitabin ve hikmetin summe caekum resulun musaddikun lima meakum le tu'minunne bihi ve le tensurunneh, kale e akrartum ve ehaztum ala zalikum ısri, kalu akrarna, kale feşhedu ve ene meakum mineş şahidin."},
  {id: 375, sure_no: 82, ayet_no: 82, sure_adi: "Ali İmran", text: "فَمَنْ تَوَلّٰى بَعْدَ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَ", translation: "Bundan sonra kim yüz çevirirse, işte onlar fasıklardır.", transliteration: "Fe men tevella ba'de zalike fe ulaike humul fasikun."},
  {id: 376, sure_no: 83, ayet_no: 83, sure_adi: "Ali İmran", text: "اَفَغَيْرَ د۪ينِ اللّٰهِ يَبْغُونَ وَلَهُٓ اَسْلَمَ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ طَوْعاً وَكَرْهاً وَاِلَيْهِ يُرْجَعُونَ", translation: "Yoksa onlar, Allah'ın dininden başka bir din mi arıyorlar? Oysa yerde ve gökte bulunanların tamamı isteyerek veya istemeyerek, O'na teslim olmuşlardır. Ve hepsi O'na döndürüleceklerdir.", transliteration: "E fe gayre dinillahi yebgune ve lehu esleme men fis semavati vel ardı tav'an ve kerhen ve ileyhi yurceun."},
  {id: 377, sure_no: 84, ayet_no: 84, sure_adi: "Ali İmran", text: "قُلْ اٰمَنَّا بِاللّٰهِ وَمَٓا اُنْزِلَ عَلَيْنَا وَمَٓا اُنْزِلَ عَلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطِ وَمَٓا اُو۫تِيَ مُوسٰى وَع۪يسٰى وَالنَّبِيُّونَ مِنْ رَبِّهِمْۖ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِنْهُمْۘ وَنَحْنُ لَهُ مُسْلِمُونَ", translation: "De ki: \"Biz; Allah'a, bize indirilene, İbrahim'e, İsmail'e, İshak'a, Yakub'a ve onun soyundan gelenlere indirilene; Musa'ya, İsa'ya, Nebilere Rabb'lerinden verilene iman ettik. Onları, birbirinden ayırt etmeyiz. Biz, O'na teslim olanlardanız.\"", transliteration: "Kul amenna billahi ve ma unzile aleyna ve ma unzile ala ibrahime ve ismaile ve ishaka ve ya'kube vel esbatı ve ma utiye musa ve isa ven nebiyyune min rabbihim, la nuferriku beyne ehadin minhum, ve nahnu lehu muslimun."},
  {id: 378, sure_no: 85, ayet_no: 85, sure_adi: "Ali İmran", text: "وَمَنْ يَبْتَغِ غَيْرَ الْاِسْلَامِ د۪يناً فَلَنْ يُقْبَلَ مِنْهُۚ وَهُوَ فِي الْاٰخِرَةِ مِنَ الْخَاسِر۪ينَ", translation: "Kim İslam'dan başka bir din seçerse, bilsin ki o din ondan asla kabul edilmeyecektir ve o ahirette hüsrana uğrayacaktır.", transliteration: "Ve men yebtegi gayrel islami dinen fe len yukbele minh, ve huve fil ahireti minel hasirin."},
  {id: 379, sure_no: 86, ayet_no: 86, sure_adi: "Ali İmran", text: "كَيْفَ يَهْدِي اللّٰهُ قَوْماً كَفَرُوا بَعْدَ ا۪يمَانِهِمْ وَشَهِدُٓوا اَنَّ الرَّسُولَ حَقٌّ وَجَٓاءَهُمُ الْبَيِّنَاتُۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ", translation: "İman ettikten, Resul'ün Hakk olduğuna tanıklık ettikten ve kendilerine apaçık kanıtlar geldikten sonra, Kafir olan bir halka, Allah nasıl doğru yolu gösterir? Allah, zalim olan bir halka doğru yolu göstermez.", transliteration: "Keyfe yehdillahu kavmen keferu ba'de imanihim ve şehidu enner resule hakkun ve caehumul beyyinat vallahu la yehdil kavmez zalimin."},
  {id: 380, sure_no: 87, ayet_no: 87, sure_adi: "Ali İmran", text: "اُو۬لٰٓئِكَ جَزَٓاؤُ۬هُمْ اَنَّ عَلَيْهِمْ لَعْنَةَ اللّٰهِ وَالْمَلٰٓئِكَةِ وَالنَّاسِ اَجْمَع۪ينَۙ", translation: "Böylelerinin cezası, Allah'ın, meleklerin ve tüm insanların lanetine uğramalarıdır.", transliteration: "Ulaike cezauhum enne aleyhim la'netallahi vel melaiketi ven nasi ecmain."},
  {id: 381, sure_no: 88, ayet_no: 88, sure_adi: "Ali İmran", text: "خَالِد۪ينَ ف۪يهَاۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُونَۙ", translation: "Onlar, bu halde sürekli kalacaklardır. Onlardan ne azap hafifletilecek ve ne de onların yüzlerine bakılacak.", transliteration: "Halidine fiha, la yuhaffefu anhumul azabu ve la hum yunzarun."},
  {id: 382, sure_no: 89, ayet_no: 89, sure_adi: "Ali İmran", text: "اِلَّا الَّذ۪ينَ تَابُوا مِنْ بَعْدِ ذٰلِكَ وَاَصْلَحُوا فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Ancak, bundan sonra tevbe edip kendilerini düzeltenler hariç. Kuşkusuz, Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İllellezine tabu min ba'di zalike ve aslehu fe innallahe gafurun rahim."},
  {id: 383, sure_no: 90, ayet_no: 90, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ كَفَرُوا بَعْدَ ا۪يمَانِهِمْ ثُمَّ ازْدَادُوا كُفْراً لَنْ تُقْبَلَ تَوْبَتُهُمْۚ وَاُو۬لٰٓئِكَ هُمُ الضَّٓالُّونَ", translation: "İman ettikten sonra Kafir olup ve küfürlerinde ısrarcı olanların tevbeleri asla kabul edilmeyecektir. İşte onlar sapkın olanlardır.", transliteration: "İnnellezine keferu ba'de imanihim summezdadu kufran len tukbele tevbetuhum, ve ulaike humud dallun."},
  {id: 384, sure_no: 91, ayet_no: 91, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ فَلَنْ يُقْبَلَ مِنْ اَحَدِهِمْ مِلْءُ الْاَرْضِ ذَهَباً وَلَوِ افْتَدٰى بِه۪ۜ اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ اَل۪يمٌ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ۟", translation: "Kuşkusuz Kafir olup, Kafir olarak ölenler; kurtulmak için yeryüzü dolusu altını fidye olarak verseler de onlardan kabul edilmeyecektir. Onlar için can yakıcı bir azap vardır. Ve onların hiçbir yardımcıları da yoktur.", transliteration: "İnnellezine keferu ve matu ve hum kuffarun fe len yukbele min ehadihim mil'ul ardı zeheben ve levifteda bih, ulaike lehum azabun elimun ve ma lehum min nasırin."},
  {id: 385, sure_no: 92, ayet_no: 92, sure_adi: "Ali İmran", text: "لَنْ تَنَالُوا الْبِرَّ حَتّٰى تُنْفِقُوا مِمَّا تُحِبُّونَۜ وَمَا تُنْفِقُوا مِنْ شَيْءٍ فَاِنَّ اللّٰهَ بِه۪ عَل۪يمٌ", translation: "Sevdiğiniz şeylerden infak etmedikçe asla birr'e erişemezsiniz. Ne infak ederseniz, Allah, onu bilir.", transliteration: "Len tenalul birre hatta tunfiku mimma tuhibbun, ve ma tunfiku min şey'in fe innallahe bihi alim."},
  {id: 386, sure_no: 93, ayet_no: 93, sure_adi: "Ali İmran", text: "كُلُّ الطَّعَامِ كَانَ حِلاًّ لِبَن۪ٓي اِسْرَٓائ۪لَ اِلَّا مَا حَرَّمَ اِسْرَٓائ۪لُ عَلٰى نَفْسِه۪ مِنْ قَبْلِ اَنْ تُنَزَّلَ التَّوْرٰيةُۜ قُلْ فَأْتُوا بِالتَّوْرٰيةِ فَاتْلُوهَٓا اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Tevrat indirilmeden önce, İsrailin kendisine haram kıldıkları hariç bütün yiyecekler İsrailoğullarına helaldi. De ki: \"Eğer doğru söylüyorsanız, haydi Tevrat'ı getirip okuyun.\"", transliteration: "Kullut taami kane hillen li beni israile illa ma harrame israilu ala nefsihi min kabli en tunezzelet tevrat, kul fe'tu bit tevrati fetluha in kuntum sadıkin."},
  {id: 387, sure_no: 94, ayet_no: 94, sure_adi: "Ali İmran", text: "فَمَنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ مِنْ بَعْدِ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ", translation: "Artık bundan sonra kim Allah adına yalan uydurursa, işte onlar zalimlerin ta kendileridir.", transliteration: "Fe meniftera alallahil kezibe min ba'di zalike fe ulaike humuz zalimun."},
  {id: 388, sure_no: 95, ayet_no: 95, sure_adi: "Ali İmran", text: "قُلْ صَدَقَ اللّٰهُ فَاتَّبِعُوا مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ", translation: "De ki: \"Allah doğru söyledi. Öyle ise hanif olarak İbrahim'in Milleti'ne tabi olun. O Müşriklerden değildi.\"", transliteration: "Kul sadakallahu fettebiu millete ibrahime hanifa, ve ma kane minel muşrikin."},
  {id: 389, sure_no: 96, ayet_no: 96, sure_adi: "Ali İmran", text: "اِنَّ اَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذ۪ي بِبَكَّةَ مُبَارَكاً وَهُدًى لِلْعَالَم۪ينَۚ", translation: "İnsanlar için kurulan ilk ev, Bekke'deki, kutlu ve bütün insanlığa yol gösteren (Kabe) dir.", transliteration: "İnne evvele beytin vudia lin nasi lellezi bi bekkete mubareken ve huden lil alemin."},
  {id: 390, sure_no: 97, ayet_no: 97, sure_adi: "Ali İmran", text: "ف۪يهِ اٰيَاتٌ بَيِّنَاتٌ مَقَامُ اِبْرٰه۪يمَۚ وَمَنْ دَخَلَهُ  كَانَ اٰمِناًۜ وَلِلّٰهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ اِلَيْهِ سَب۪يلاًۜ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ عَنِ الْعَالَم۪ينَ", translation: "Orada apaçık ayetler vardır. İbrahim'in makamı oradadır. Kim oraya girerse güvende olur. Yoluna gücü yetenin, Beyt'i haccetmesi insanlar üzerinde Allah'ın bir hakkıdır. Her kim kefere olursa, bilsin ki Allah'ın hiçbir şeye ihtiyacı yoktur.", transliteration: "Fihi ayatun beyyinatun makamu ibrahim, ve men dahalehu kane amina, ve lillahi alen nasi hiccul beyti menistetaa ileyhi sebila, ve men kefere fe innallahe ganiyyun anil alemin."},
  {id: 391, sure_no: 98, ayet_no: 98, sure_adi: "Ali İmran", text: "قُلْ يَٓا اَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِاٰيَاتِ اللّٰهِۗ وَاللّٰهُ شَه۪يدٌ عَلٰى مَا تَعْمَلُونَ", translation: "De ki: \"Ey Kitap Ehli! Neden Allah'ın ayetlerini küfrediyorsunuz? Allah, yaptıklarınıza tanıktır.\"", transliteration: "Kul ya ehlel kitabi lime tekfurune bi ayatillahi, vallahu şehidun ala ma ta'melun."},
  {id: 392, sure_no: 99, ayet_no: 99, sure_adi: "Ali İmran", text: "قُلْ يَٓا اَهْلَ الْكِتَابِ لِمَ تَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ مَنْ اٰمَنَ تَبْغُونَهَا عِوَجاً وَاَنْتُمْ شُهَدَٓاءُۜ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ", translation: "De ki: \"Ey Kitap Ehli! Gerçeği görüp bildiğiniz halde, niçin Allah'ın yolunu eğri göstermeye yeltenerek, iman etmek isteyenleri O'nun yolundan döndürmeye çalışıyorsunuz! Allah, yaptıklarınızdan habersiz değildir.\"", transliteration: "Kul ya ehlel kitabi lime tesuddune an sebilillahi men amene tebguneha ivecen ve entum şuhedau ve mallahu bi gafilin amma ta'melun."},
  {id: 393, sure_no: 100, ayet_no: 100, sure_adi: "Ali İmran", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ تُط۪يعُوا فَر۪يقاً مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ يَرُدُّوكُمْ بَعْدَ ا۪يمَانِكُمْ كَافِر۪ينَ", translation: "Ey İman Edenler! Kitap verilenlerden herhangi bir gruba uyarsanız, sizi, imanınızdan sonra tekrar Kafir yaparlar.", transliteration: "Ya eyyuhellezine amenu in tutiu ferikan minellezine utul kitabe yeruddukum ba'de imanikum kafirin."},
  {id: 394, sure_no: 101, ayet_no: 101, sure_adi: "Ali İmran", text: "وَكَيْفَ تَكْفُرُونَ وَاَنْتُمْ تُتْلٰى عَلَيْكُمْ اٰيَاتُ اللّٰهِ وَف۪يكُمْ رَسُولُهُۜ وَمَنْ يَعْتَصِمْ بِاللّٰهِ فَقَدْ هُدِيَ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ۟", translation: "Allah'ın ayetleri size okunmaktayken, elçisi de aranızda yaşarken, nasıl küfre saparsınız? Kim Allah'a sımsıkı sarılırsa o doğru yola iletilmiştir.", transliteration: "Ve keyfe tekfurune ve entum tutla aleykum ayatullahi ve fikum resuluh, ve men ya'tesim billahi fe kad hudiye ila sıratın mustakim."},
  {id: 395, sure_no: 102, ayet_no: 102, sure_adi: "Ali İmran", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ حَقَّ تُقَاتِه۪ وَلَا تَمُوتُنَّ اِلَّا وَاَنْتُمْ مُسْلِمُونَ", translation: "Ey İman Edenler! Allah için nasıl takvalı olmanız gerekiyorsa öyle takvalı olun. Ve Müslim olmanın dışında başka bir şekilde ölmeyin.", transliteration: "Ya eyyuhellezine amenuttekullahe hakka tukatihi ve la temutunne illa ve entum muslimun."},
  {id: 396, sure_no: 103, ayet_no: 103, sure_adi: "Ali İmran", text: "وَاعْتَصِمُوا بِحَبْلِ اللّٰهِ جَم۪يعاً وَلَا تَفَرَّقُواۖ وَاذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ  كُنْتُمْ اَعْدَٓاءً فَاَلَّفَ بَيْنَ قُلُوبِكُمْ فَاَصْبَحْتُمْ بِنِعْمَتِه۪ٓ اِخْوَاناًۚ وَكُنْتُمْ عَلٰى شَفَا حُفْرَةٍ مِنَ النَّارِ فَاَنْقَذَكُمْ مِنْهَاۜ   كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَهْتَدُونَ", translation: "Hep birlikte Allah'ın ipine sımsıkı tutunun ve ayrılığa düşmeyin. Allah'ın üzerinizdeki nimetini hatırlayın. Hani bir zamanlar, birbirinize düşmandınız da O'nun kalplerinizi kaynaştırması sayesinde kardeş oldunuz. Ve yine ateş çukurunun tam kıyısında bulunuyorken, sizi ona düşmekten O korudu. İşte Allah ayetlerini böyle açıklıyor ki doğru yolu bulasınız.", transliteration: "Va'tasımu bihablillahi cemian ve la teferreku, vezkuru ni'metallahi aleykum iz kuntum a'daen fe ellefe beyne kulubikum fe asbahtum bi ni'metihi ihvana, ve kuntum ala şefa hufretin minen nari fe enkazekum minha, kezalike yubeyyinullahu lekum ayatihi leallekum tehtedun."},
  {id: 397, sure_no: 104, ayet_no: 104, sure_adi: "Ali İmran", text: "وَلْتَكُنْ مِنْكُمْ اُمَّةٌ يَدْعُونَ اِلَى الْخَيْرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِۜ  وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "Siz, hayra çağıran, ma'rufu yapıp telkin eden, munkeri engelleyen bir topluluk olun. İşte onlar kurtuluşa erenlerdir.", transliteration: "Veltekun minkum ummetun yed'une ilel hayri ve ye'murune bil ma'rufi ve yenhevne anil munker, ve ulaike humul muflihun."},
  {id: 398, sure_no: 105, ayet_no: 105, sure_adi: "Ali İmran", text: "وَلَا تَكُونُوا كَالَّذ۪ينَ تَفَرَّقُوا وَاخْتَلَفُوا مِنْ بَعْدِ مَا جَٓاءَهُمُ الْبَيِّنَاتُۜ وَاُو۬لٰٓئِكَ لَهُمْ عَذَابٌ عَظ۪يمٌۙ", translation: "Kendilerine apaçık belgeler geldikten sonra, ayrılığa düşüp parçalanan kimseler gibi olmayın. Onlar için büyük bir azap vardır.", transliteration: "Ve la tekunu kellezine teferraku vahtelefu min ba'di ma caehumul beyyinat, ve ulaike lehum azabun azim."},
  {id: 399, sure_no: 106, ayet_no: 106, sure_adi: "Ali İmran", text: "يَوْمَ تَبْيَضُّ وُجُوهٌ وَتَسْوَدُّ وُجُوهٌۚ فَاَمَّا الَّذ۪ينَ اسْوَدَّتْ وُجُوهُهُمْ۠  اَكَفَرْتُمْ بَعْدَ ا۪يمَانِكُمْ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ", translation: "O gün, kimi yüzler ağaracak, kimi yüzler kararacak. Yüzleri kararanlara: \"İmanınızdan sonra Kafir mi oldunuz? Öyle ise kafir olmanız nedeniyle azabı tadın.\" denilecek.", transliteration: "Yevme tebyaddu vucuhun ve tesveddu vucuh, fe emmellezinesveddet vucuhuhum e kefertum ba'de imanikum fe zukul azabe bima kuntum tekfurun."},
  {id: 400, sure_no: 107, ayet_no: 107, sure_adi: "Ali İmran", text: "وَاَمَّا الَّذ۪ينَ ابْيَضَّتْ وُجُوهُهُمْ فَف۪ي رَحْمَةِ اللّٰهِۜ هُمْ ف۪يهَا خَالِدُونَ", translation: "Yüzleri ağaranlara gelince, onlar Allah'ın rahmetinin içindedirler ve orada sürekli kalacaklardır.", transliteration: "Ve emmellezinebyaddat vucuhuhum fe fi rahmetillah, hum fiha halidun."},
  {id: 401, sure_no: 108, ayet_no: 108, sure_adi: "Ali İmran", text: "تِلْكَ اٰيَاتُ اللّٰهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّۜ وَمَا اللّٰهُ يُر۪يدُ ظُلْماً لِلْعَالَم۪ينَ", translation: "Bunlar, sana gerçek olarak okuduğumuz Allah'ın ayetleridir. Allah, alemlere haksızlık yapmak istemez.", transliteration: "Tilke ayatullahi netluha aleyke bil hakk, ve mallahu yuridu zulmen lil alemin."},
  {id: 402, sure_no: 109, ayet_no: 109, sure_adi: "Ali İmran", text: "وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ۟", translation: "Göklerde ve yerde olanlar Allah'ındır. Ve bütün işler Allah'a döndürülür.", transliteration: "Ve lillahi ma fis semavati ve ma fil ard, ve ilallahi turceul umur."},
  {id: 403, sure_no: 110, ayet_no: 110, sure_adi: "Ali İmran", text: "كُنْتُمْ خَيْرَ اُمَّةٍ اُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُونَ بِاللّٰهِۜ وَلَوْ اٰمَنَ اَهْلُ الْكِتَابِ لَكَانَ خَيْراً لَهُمْۜ مِنْهُمُ الْمُؤْمِنُونَ وَاَكْثَرُهُمُ الْفَاسِقُونَ", translation: "Siz, insanlar için çıkarılmış hayırlı bir ümmetsiniz. Ma'rufu yapıp telkin eder, munkere engel olursunuz. Ve Allah'a iman edersiniz. Eğer Kitap Ehli de iman etseydi, bu onlar için daha hayırlı olurdu. İçlerinde iman edenler varsa da çoğunluğu sapkındır.", transliteration: "Kuntum hayra ummetin uhricet lin nasi te'murune bil ma'rufi ve tenhevne anil munkeri ve tu'minune billah, ve lev amene ehlul kitabi le kane hayran lehum, minhumul mu'minune ve ekseruhumul fasikun."},
  {id: 404, sure_no: 111, ayet_no: 111, sure_adi: "Ali İmran", text: "لَنْ يَضُرُّوكُمْ اِلَّٓا اَذًىۜ وَاِنْ يُقَاتِلُوكُمْ يُوَلُّوكُمُ الْاَدْبَارَ۠  ثُمَّ لَا يُنْصَرُونَ", translation: "Onlar, eziyetten başka size bir zarar veremezler. Sizinle savaşsalar bile geri dönüp kaçarlar. Sonra onlara yardım da edilmez.", transliteration: "Len yedurrukum illa eza, ve in yukatilukum yuvellukumul edbar, summe la yunsarun."},
  {id: 405, sure_no: 112, ayet_no: 112, sure_adi: "Ali İmran", text: "ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ اَيْنَ مَا ثُقِفُٓوا اِلَّا بِحَبْلٍ مِنَ اللّٰهِ وَحَبْلٍ مِنَ النَّاسِ وَبَٓاؤُ۫ بِغَضَبٍ مِنَ اللّٰهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُۜ ذٰلِكَ بِاَنَّهُمْ كَانُوا يَكْفُرُونَ بِاٰيَاتِ اللّٰهِ وَيَقْتُلُونَ الْاَنْبِيَٓاءَ بِغَيْرِ حَقٍّۜ ذٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ۠", translation: "Onlar, nerede bulunurlarsa bulunsunlar, Allah'ın ve insanların ipine tutunmadıkları sürece zillet içinde olurlar. Onlar, Allah'ın gazabına uğradılar. Yoksunluğa tutsak oldular. Bunun nedeni, onların asileşip hadlerini aşarak, Allah'ın ayetlerini yalanlamaları ve haksız yere Nebilerini öldürmeleridir. Bu ceza, onların isyan etmelerinden ve haddi aşmış olmalarındandır.", transliteration: "Duribet aleyhimuz zilletu eyne ma sukıfu illa bi hablin minallahi ve hablin minen nasi ve bau bi gadabin minallahi ve duribet aleyhimul meskeneh, zalike bi ennehum kanu yekfurune bi ayatillahi ve yaktulunel enbiyae bi gayri hakk, zalike bima asav ve kanu ya'tedun."},
  {id: 406, sure_no: 113, ayet_no: 113, sure_adi: "Ali İmran", text: "لَيْسُوا سَوَٓاءًۜ مِنْ اَهْلِ الْكِتَابِ اُمَّةٌ قَٓائِمَةٌ يَتْلُونَ اٰيَاتِ اللّٰهِ اٰنَٓاءَ الَّيْلِ وَهُمْ يَسْجُدُونَ۠", translation: "Ama hepsi aynı değildir. Kitap Ehli'nden, Allah'ın ayetlerini okuyan ve gece saatlerinde secde eden bir topluluk da vardır.", transliteration: "Leysu seva', min ehlil kitabi ummetun kaimetun yetlune ayatillahi anael leyli ve hum yescudun."},
  {id: 407, sure_no: 114, ayet_no: 114, sure_adi: "Ali İmran", text: "يُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُونَ فِي الْخَيْرَاتِۜ وَاُو۬لٰٓئِكَ مِنَ الصَّالِح۪ينَ", translation: "Bunlar, Allah'a ve ahiret gününe inanırlar, ma'rufu yapıp telkin ederler, munkere engel olurlar; hayırda birbirleriyle yarışırlar, işte bunlar salihlerdendir.", transliteration: "Yu'minune billahi vel yevmil ahiri ve ye'murune bil ma'rufi ve yenhevne anil munkeri ve yusariune fil hayrat, ve ulaike mines salihin."},
  {id: 408, sure_no: 115, ayet_no: 115, sure_adi: "Ali İmran", text: "وَمَا يَفْعَلُوا مِنْ خَيْرٍ فَلَنْ يُكْفَرُوهُۜ وَاللّٰهُ عَل۪يمٌ بِالْمُتَّق۪ينَ", translation: "Yaptığınız hiçbir iyilik karşılıksız bırakılmayacaktır. Allah, kendisine karşı takvalı davrananları en iyi bilendir.", transliteration: "Ve ma yef'alu min hayrin fe len yukferuh, vallahu alimun bil muttekin."},
  {id: 409, sure_no: 116, ayet_no: 116, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَٓا اَوْلَادُهُمْ مِنَ اللّٰهِ شَيْـٔاًۜ وَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "Kuşkusuz Kafirlerin ne malları ne de evlatları Allah'ın yanında bir yarar sağlamayacaktır. İşte onlar, ateş ehlidir. Onlar, orada sürekli kalacaklardır.", transliteration: "İnnellezine keferu len tugniye anhum emvaluhum ve la evladuhum minallahi şey'a, ve ulaike ashabun nar, hum fiha halidun."},
  {id: 410, sure_no: 117, ayet_no: 117, sure_adi: "Ali İmran", text: "مَثَلُ مَا يُنْفِقُونَ ف۪ي هٰذِهِ الْحَيٰوةِ الدُّنْيَا كَمَثَلِ ر۪يحٍ ف۪يهَا صِرٌّ اَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُٓوا اَنْفُسَهُمْ فَاَهْلَكَتْهُۜ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Onların, bu dünya hayatında, infak ettikleri şeylerin durumu, kendilerine haksızlık etmiş bir halkın ekinine isabet ederek onu mahveden kavurucu bir rüzgara benzer. Allah onlara haksızlık yapmadı, fakat onlar, kendi kendilerine haksızlık etmektedirler.", transliteration: "Meselu ma yunfikune fi hazihil hayatid dunya ke meseli rihin fiha sırrun esabet harse kavmin zalemu enfusehum fe ehlekethu ve ma zalemehumullahu ve lakin enfusehum yazlımun."},
  {id: 411, sure_no: 118, ayet_no: 118, sure_adi: "Ali İmran", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا بِطَانَةً مِنْ دُونِكُمْ لَا يَأْلُونَكُمْ خَبَالاًۜ وَدُّوا مَا عَنِتُّمْۚ قَدْ بَدَتِ الْبَغْضَٓاءُ مِنْ اَفْوَاهِهِمْۚ وَمَا تُخْف۪ي صُدُورُهُمْ اَكْـبَرُۜ قَدْ بَيَّنَّا لَكُمُ الْاٰيَاتِ اِنْ كُنْتُمْ تَعْقِلُونَ", translation: "Ey İman Edenler! Birbirinizden başkasını, kendinize sırdaş edinmeyin. Onlar, size zarar vermekten geri durmazlar, sizin sıkıntıya düşmenizi isterler. Gerçi, kinlerini ağızları ile dışa vuruyorlar, ancak kalplerinde gizledikleri kin daha büyüktür. Eğer aklınızı kullanırsanız, ayetlerimizi size açık açık bildirdik.", transliteration: "Ya eyyuhellezine amenu la tettehızu bitaneten min dunikum la ye'lunekum habala, veddu ma anittum, kad bedetil bagdau min efvahihim, ve ma tuhfi suduruhum ekber, kad beyyenna lekumul ayati in kuntum ta'kılun."},
  {id: 412, sure_no: 119, ayet_no: 119, sure_adi: "Ali İmran", text: "هَٓا اَنْتُمْ اُو۬لَٓاءِ تُحِبُّونَهُمْ وَلَا يُحِبُّونَكُمْ وَتُؤْمِنُونَ بِالْكِتَابِ كُلِّه۪ۚ وَاِذَا لَقُوكُمْ قَالُٓوا اٰمَنَّاۗ وَاِذَا خَلَوْا عَضُّوا عَلَيْكُمُ الْاَنَامِلَ مِنَ الْغَيْظِۜ قُلْ مُوتُوا بِغَيْظِكُمْۜ اِنَّ اللّٰهَ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "İşte siz öyle kimselersiniz ki, onları seversiniz. Siz, bütün kitaplara inandığınız halde onlar sizi sevmezler. Sizinle karşılaştıkları zaman; \"iman ettik.\" derler, yalnız kaldıkları zaman ise size olan öfkelerinden parmaklarını ısırırlar. De ki: \"Öfkenizden ölün!\" Kuşkusuz, Allah, kalplerin gizlediklerini bilir.", transliteration: "Ha entum ulai tuhıbbunehum ve la yuhıbbunekum ve tu'minune bil kitabi kullih, ve iza lekukum kalu amenna, ve iza halev addu aleykumul enamile minel gayz, kul mutu bi gayzikum, innallahe alimun bi zatis sudur."},
  {id: 413, sure_no: 120, ayet_no: 120, sure_adi: "Ali İmran", text: "اِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْۘ وَاِنْ تُصِبْكُمْ سَيِّئَةٌ يَفْرَحُوا بِهَاۜ وَاِنْ تَصْبِرُوا وَتَتَّقُوا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْـٔاًۜ اِنَّ اللّٰهَ بِمَا يَعْمَلُونَ مُح۪يطٌ۟", translation: "Eğer size bir iyilik dokunursa bu onları üzer. Eğer size bir kötülük isabet ederse bu onları sevindirir. Eğer sabreder ve takvalı olursanız, onların hileleri size bir zarar vermez. Kuşkusuz, Allah, ilmiyle onların yaptıkları her şeyi kuşatmıştır.", transliteration: "İn temseskum hasenetun tesu'hum, ve in tusibkum seyyietun yefrahu biha ve in tasbiru ve tetteku la yadurrukum keyduhum şey'a, innallahe bi ma ya'melune muhit."},
  {id: 414, sure_no: 121, ayet_no: 121, sure_adi: "Ali İmran", text: "وَاِذْ غَدَوْتَ مِنْ اَهْلِكَ تُبَوِّئُ الْمُؤْمِن۪ينَ مَقَاعِدَ لِلْقِتَالِۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌۙ", translation: "Hani! Sen, Mü'minleri savaş düzenine sokmak için, sabah erkenden ailenden ayrılmıştın. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve iz gadavte min ehlike tubevviul mu'minine makaide lil kıtal, vallahu semiun alim."},
  {id: 415, sure_no: 122, ayet_no: 122, sure_adi: "Ali İmran", text: "اِذْ هَمَّتْ طَٓائِفَتَانِ مِنْكُمْ اَنْ تَفْشَلَاۙ وَاللّٰهُ وَلِيُّهُمَاۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ", translation: "Hani! İçinizden iki grup korkuya kapılıp, geri çekilmeye yeltenmişti. Halbuki Allah, onların velisiydi. Mü'minler yalnız Allah'a tevekkül etsinler.", transliteration: "İz hemmet taifetani minkum en tefşela vallahu veliyyuhuma ve alallahi fel yetevekkelil mu'minun."},
  {id: 416, sure_no: 123, ayet_no: 123, sure_adi: "Ali İmran", text: "وَلَقَدْ نَصَرَكُمُ اللّٰهُ بِبَدْرٍ وَاَنْتُمْ اَذِلَّةٌۚ فَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Ant olsun! Çok zayıf olduğunuz Bedir'de de Allah size yardım etmişti. O halde, Allah'a karşı takvalı olun ki şükretmiş olasınız.", transliteration: "Ve lekad nasarakumullahu bi bedrin ve entum ezilleh, fettekullahe leallekum teşkurun."},
  {id: 417, sure_no: 124, ayet_no: 124, sure_adi: "Ali İmran", text: "اِذْ تَقُولُ لِلْمُؤْمِن۪ينَ اَلَنْ يَكْفِيَكُمْ اَنْ يُمِدَّكُمْ رَبُّكُمْ بِثَلٰثَةِ اٰلَافٍ مِنَ الْمَلٰٓئِكَةِ مُنْزَل۪ينَۜ", translation: "Hani! Sen Mü'minlere: \"Rabb'inizin, indirilmiş üç bin melekle yardım etmesi size yetmez mi?\" diyordun.", transliteration: "İz tekulu lil mu'minine e len yekfiyekum en yumiddekum rabbukum bi selaseti alafin minel melaiketi munzelin."},
  {id: 418, sure_no: 125, ayet_no: 125, sure_adi: "Ali İmran", text: "بَلٰٓىۙ اِنْ تَصْبِرُوا وَتَتَّقُوا وَيَأْتُوكُمْ مِنْ فَوْرِهِمْ هٰذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ اٰلَافٍ مِنَ الْمَلٰٓئِكَةِ مُسَوِّم۪ينَ", translation: "Evet! Sabreder ve takvalı davranırsanız, düşman size hemen saldırsa bile, Rabb'iniz, size, seçilmiş beş bin melekle yardım eder.", transliteration: "Bela in tasbiru ve tetteku ve ye'tukum min fevrihim haza yumdidkum rabbukum bi hamseti alafin minel melaiketi musevvimin."},
  {id: 419, sure_no: 126, ayet_no: 126, sure_adi: "Ali İmran", text: "وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى لَكُمْ وَلِتَطْمَئِنَّ قُلُوبُكُمْ بِه۪ۜ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ الْعَز۪يزِ الْحَك۪يمِۙ", translation: "Allah, bununla sevinmenizi ve kalplerinizin yatışmasını dilemiştir. Çünkü yardım ancak Mutlak Üstün Olan ve En İyi Hüküm Veren Allah'ın yanındadır.", transliteration: "Ve ma cealehullahu illa buşra lekum ve li tatmeinne kulubukum bih, ve men nasru illa min indillahil azizil hakim."},
  {id: 420, sure_no: 127, ayet_no: 127, sure_adi: "Ali İmran", text: "لِيَقْطَعَ طَرَفاً مِنَ الَّذ۪ينَ كَفَرُٓوا اَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوا خَٓائِب۪ينَ", translation: "Bu yardım, Kafirlerin bir kısmının imha edilmesi veya bozguna uğrayarak umutsuzca geri dönüp gitmeleri için yapıldı.", transliteration: "Li yaktaa tarafen minellezine keferu ev yekbitehum fe yenkalibu haibin."},
  {id: 421, sure_no: 128, ayet_no: 128, sure_adi: "Ali İmran", text: "لَيْسَ لَكَ مِنَ الْاَمْرِ شَيْءٌ اَوْ يَتُوبَ عَلَيْهِمْ اَوْ يُعَذِّبَهُمْ فَاِنَّهُمْ ظَالِمُونَ", translation: "Bu konuda senin yapabileceğin bir şey yoktur. O, ya onların tevbelerini kabul edecek ya da onlara azap edecek. Çünkü onlar, zalim kimselerdir.", transliteration: "Leyse leke minel emri şey'un ev yetube aleyhim ev yuazzibehum fe innehum zalimun."},
  {id: 422, sure_no: 129, ayet_no: 129, sure_adi: "Ali İmran", text: "وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ يَغْفِرُ لِمَنْ يَشَٓاءُ وَيُعَذِّبُ مَنْ يَشَٓاءُۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ۟", translation: "Göklerde ve yerde olan her şey Allah'ındır. O hak edeni bağışlar, hak edene azap eder. Allah Çok Affedici ve Çok Bağışlayıcı'dır.", transliteration: "Ve lillahi ma fis semavati ve ma fil ard, yagfiru li men yeşau ve yuazzibu men yeşa', vallahu gafurun rahim."},
  {id: 423, sure_no: 130, ayet_no: 130, sure_adi: "Ali İmran", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَأْكُلُوا الرِّبٰٓوا اَضْعَافاً مُضَاعَفَةًۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَۚ", translation: "Ey İman Edenler! Kat kat artırarak riba yemeyin. Allah'a karşı takva sahibi olun. Umulur ki kurtuluşa erersiniz.", transliteration: "Ya eyyuhellezine amenu la te'kulur riba ad'afen mudaafeh, vettekullahe leallekum tuflihun."},
  {id: 424, sure_no: 131, ayet_no: 131, sure_adi: "Ali İmran", text: "وَاتَّقُوا النَّارَ الَّت۪ٓي اُعِدَّتْ لِلْكَافِر۪ينَۚ", translation: "Kafirler için hazırlanmış ateşten korunmak için takva sahibi olun.", transliteration: "Vettekun narelleti uiddet lil kafirin."},
  {id: 425, sure_no: 132, ayet_no: 132, sure_adi: "Ali İmran", text: "وَاَط۪يعُوا اللّٰهَ وَالرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَۚ", translation: "Allah'a ve Resul'e itaat edin ki size merhamet edilsin.", transliteration: "Ve atiullahe ver resule leallekum turhamun."},
  {id: 426, sure_no: 133, ayet_no: 133, sure_adi: "Ali İmran", text: "وَسَارِعُٓوا اِلٰى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمٰوَاتُ وَالْاَرْضُۙ اُعِدَّتْ لِلْمُتَّق۪ينَۙ", translation: "Rabb'inizin bağışlaması ve muttakiler için hazırlanmış, yer ile gök arası kadar geniş olan Cennet için yarışın.", transliteration: "Ve sariu ila magfiretin min rabbikum ve cennetin arduhas semavatu vel ardu, uiddet lil muttekin."},
  {id: 427, sure_no: 134, ayet_no: 134, sure_adi: "Ali İmran", text: "اَلَّذ۪ينَ يُنْفِقُونَ فِي السَّرَّٓاءِ وَالضَّرَّٓاءِ وَالْكَاظِم۪ينَ الْغَيْظَ وَالْعَاف۪ينَ عَنِ النَّاسِۜ وَاللّٰهُ يُحِبُّ الْمُحْسِن۪ينَۚ", translation: "Onlar, bollukta da darlıkta da infak ederler. Öfkelerini yenerler, insanların kusurlarını bağışlarlar. Allah muhsin olanları sever.", transliteration: "Ellezine yunfikune fis serrai ved darrai vel kazıminel gayza vel afine anin nas, vallahu yuhibbul muhsinin."},
  {id: 428, sure_no: 135, ayet_no: 135, sure_adi: "Ali İmran", text: "وَالَّذ۪ينَ اِذَا فَعَلُوا فَاحِشَةً اَوْ ظَلَمُٓوا اَنْفُسَهُمْ ذَكَرُوا اللّٰهَ فَاسْتَغْفَرُوا لِذُنُوبِهِمْۖ وَمَنْ يَغْفِرُ الذُّنُوبَ اِلَّا اللّٰهُۖ وَلَمْ يُصِرُّوا عَلٰى مَا فَعَلُوا وَهُمْ يَعْلَمُونَ", translation: "Ve onlar, kötü bir şey yaptıkları veya kendilerine zulmettikleri zaman, Allah'ı hatırlayıp, suçlarının bağışlanması için O'na yalvarırlar. Zaten Allah'ın dışında kim suçları bağışlayabilir ki? Onlar, yaptıkları kötülüklerde bile bile ısrar etmezler.", transliteration: "Vellezine iza fealu fahişeten ev zalemu enfusehum zekerullahe festagferu li zunubihim, ve men yagfiruz zunube illallahu ve lem yusırru ala ma fealu ve hum ya'lemun ."},
  {id: 429, sure_no: 136, ayet_no: 136, sure_adi: "Ali İmran", text: "اُو۬لٰٓئِكَ جَزَٓاؤُ۬هُمْ مَغْفِرَةٌ مِنْ رَبِّهِمْ وَجَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَنِعْمَ اَجْرُ الْعَامِل۪ينَۜ", translation: "İşte onların yaptıklarının karşılığı, Rabb'lerinin bağışlaması ve içinden ırmaklar akan ve içinde sürekli kalacakları Cennetlerdir. Böyle yapanlara verilen karşılık, ne güzeldir.", transliteration: "Ulaike cezauhum magfiretun min rabbihim ve cennatun tecri min tahtihal enharu halidine fiha, ve ni'me ecrul amilin."},
  {id: 430, sure_no: 137, ayet_no: 137, sure_adi: "Ali İmran", text: "قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌۙ فَس۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّب۪ينَ", translation: "Sizden önce de yasalar uygulandı. Yeryüzünü dolaşın da yalanlayanların sonları nasıl oldu görün.", transliteration: "Kad halet min kablikum sunenun, fe siru fil ardı fenzuru keyfe kane akıbetul mukezzibin."},
  {id: 431, sure_no: 138, ayet_no: 138, sure_adi: "Ali İmran", text: "هٰذَا بَيَانٌ لِلنَّاسِ وَهُدًى وَمَوْعِظَةٌ لِلْمُتَّق۪ينَ", translation: "Bu; insanlar için bir açıklama, takva sahipleri için bir hidayet ve öğüttür.", transliteration: "Haza beyanun lin nasi ve huden ve mev'ızatun lil muttekin."},
  {id: 432, sure_no: 139, ayet_no: 139, sure_adi: "Ali İmran", text: "وَلَا تَهِنُوا وَلَا تَحْزَنُوا وَاَنْتُمُ الْاَعْلَوْنَ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Gevşemeyin, üzülmeyin! Eğer, gerçekten iman etmişseniz, üstün olan sizsiniz.", transliteration: "Ve la tehinu ve la tahzenu ve entumul a'levne in kuntum mu'minin."},
  {id: 433, sure_no: 140, ayet_no: 140, sure_adi: "Ali İmran", text: "اِنْ يَمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِثْلُهُۜ وَتِلْكَ الْاَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِۚ وَلِيَعْلَمَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَيَتَّخِذَ مِنْكُمْ شُهَدَٓاءَۜ وَاللّٰهُ لَا يُحِبُّ الظَّالِم۪ينَۙ", translation: "Eğer size bir sıkıntı isabet ederse, başka halklara da benzeri sıkıntı isabet etmiştir. Bu günleri, insanlar arasında döndürüp dururuz. Bu, Allah'ın içinizdeki gerçek iman edenleri ayırt etmesi ve gerçeğin tanıklarını belli etmesi içindir. Allah, zalimleri sevmez.", transliteration: "İn yemseskum karhun fe kad messel kavme karhun misluh, ve tilkel eyyamu nudaviluha beynen nas, ve li ya'lemallahullezine amenu ve yettehize minkum şuhedae vallahu la yuhibbuz zalimin."},
  {id: 434, sure_no: 141, ayet_no: 141, sure_adi: "Ali İmran", text: "وَلِيُمَحِّصَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَيَمْحَقَ الْكَافِر۪ينَ", translation: "Bu, Allah'ın, İman Edenleri arındırması ve Kafirleri mahvetmesi içindir.", transliteration: "Ve liyumahhisallahullezine amenu ve yemhakal kafirin."},
  {id: 435, sure_no: 142, ayet_no: 142, sure_adi: "Ali İmran", text: "اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللّٰهُ الَّذ۪ينَ جَاهَدُوا مِنْكُمْ وَيَعْلَمَ الصَّابِر۪ينَ", translation: "Yoksa siz; Allah, içinizdeki cihad edenleri ve sabredenleri ortaya çıkarmadan Cennet'e gireceğinizi mi sanıyorsunuz?", transliteration: "Em hasibtum en tedhulul cennete ve lemma ya'lemillahullezine cahedu minkum ve ya'lemes sabirin."},
  {id: 436, sure_no: 143, ayet_no: 143, sure_adi: "Ali İmran", text: "وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ اَنْ تَلْقَوْهُۖ فَقَدْ رَاَيْتُمُوهُ وَاَنْتُمْ تَنْظُرُونَ۟", translation: "Siz, ölümle yüz yüze gelmeden önce, ölmeyi temenni ediyordunuz; ama onu görünce de bakakaldınız.", transliteration: "Ve lekad kuntum temennevnel mevte min kabli en telkavhu, fe kad raeytumuhu ve entum tenzurun."},
  {id: 437, sure_no: 144, ayet_no: 144, sure_adi: "Ali İmran", text: "وَمَا مُحَمَّدٌ اِلَّا رَسُولٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۜ اَفَا۬ئِنْ مَاتَ اَوْ قُتِلَ انْقَلَبْتُمْ عَلٰٓى اَعْقَابِكُمْۜ وَمَنْ يَنْقَلِبْ عَلٰى عَقِبَيْهِ فَلَنْ يَضُرَّ اللّٰهَ شَيْـٔاًۜ وَسَيَجْزِي اللّٰهُ الشَّاكِر۪ينَ", translation: "Muhammed, ancak bir Resuldür. Ondan önce de Resuller gelip geçti. Eğer o ölür ya da öldürülürse, siz topuklarınız üzerinde gerisin geri mi döneceksiniz? Kim topukları üzerinde gerisin geri dönerse, Allah'a hiçbir zarar veremez. Allah, şükredenleri ödüllendirecektir.", transliteration: "Ve ma muhammedun illa resul, kad halet min kablihir rusul, e fein mate ev kutilenkalebtum ala a'kabikum, ve men yenkalib ala akıbeyhi fe len yadurrallahe şey'a, ve se yeczillahuş şakirin."},
  {id: 438, sure_no: 145, ayet_no: 145, sure_adi: "Ali İmran", text: "وَمَا كَانَ لِنَفْسٍ اَنْ تَمُوتَ اِلَّا بِاِذْنِ اللّٰهِ كِتَاباً مُؤَجَّلاًۜ وَمَنْ يُرِدْ ثَوَابَ الدُّنْيَا نُؤْتِه۪ مِنْهَاۚ وَمَنْ يُرِدْ ثَوَابَ الْاٰخِرَةِ نُؤْتِه۪ مِنْهَاۜ وَسَنَجْزِي الشَّاكِر۪ينَ", translation: "Allah'ın izni dışında, hiç kimse ölmez. Ölüm, vakti belirlenmiş bir süreye göre yazılmıştır. Kim dünya kazancını isterse; ona, onu veririz, kim de ahiret kazancını isterse, ona da onu veririz. Şükredenleri ödüllendireceğiz.", transliteration: "Ve ma kane li nefsin en temute illa bi iznillahi kitaben mueccela, ve men yurid sevabed dunya nu'tihi minha, ve men yurid sevabel ahirati nu'tihi minha, ve se necziş şakirin."},
  {id: 439, sure_no: 146, ayet_no: 146, sure_adi: "Ali İmran", text: "وَكَاَيِّنْ مِنْ نَبِيٍّ قَاتَلَۙ مَعَهُ رِبِّيُّونَ كَث۪يرٌۚ فَمَا وَهَنُوا لِمَٓا اَصَابَهُمْ ف۪ي سَب۪يلِ اللّٰهِ وَمَا ضَعُفُوا وَمَا اسْتَكَانُواۜ وَاللّٰهُ يُحِبُّ الصَّابِر۪ينَ", translation: "Nice Nebilerle, rabbani olanlar birlikte savaştı. Bunlar, Allah yolunda başlarına gelenlerden dolayı yılmadılar, zayıflık göstermediler ve boyun eğmediler. Allah sabredenleri sever.", transliteration: "Ve keeyyin min nebiyyin katele, meahu rıbbiyyune kesir, fe ma vehenu li ma asabehum fi sebilillahi ve ma daufu ve mestekanu vallahu yuhibbus sabirin."},
  {id: 440, sure_no: 147, ayet_no: 147, sure_adi: "Ali İmran", text: "وَمَا كَانَ قَوْلَهُمْ اِلَّٓا اَنْ قَالُوا رَبَّنَا اغْفِرْ لَنَا ذُنُوبَنَا وَاِسْرَافَنَا ف۪ٓي اَمْرِنَا وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِر۪ينَ", translation: "Onların sözleri ancak şuydu: \"Ey Rabbimiz! Suçlarımızı ve yaptığımız taşkınlıkları bağışla, ayaklarımızı sabit kıl, Kafirlere karşı bize yardım et.\"", transliteration: "Ve ma kane kavlehum illa en kalu rabbenagfir lena zunubena ve israfena fi emrina ve sebbit akdamena vensurna alel kavmil kafirin."},
  {id: 441, sure_no: 148, ayet_no: 148, sure_adi: "Ali İmran", text: "فَاٰتٰيهُمُ اللّٰهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْاٰخِرَةِۜ وَاللّٰهُ يُحِبُّ الْمُحْسِن۪ينَ۟", translation: "Bunun üzerine, Allah da onlara hem dünya kazancını hem de ahiretin en iyi kazancını verdi. Allah, muhsin olanları sever.", transliteration: "Fe atahumullahu sevabed dunya ve husne sevabil ahireh, vallahu yuhibbul muhsinin."},
  {id: 442, sure_no: 149, ayet_no: 149, sure_adi: "Ali İmran", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ تُط۪يعُوا الَّذ۪ينَ كَفَرُوا يَرُدُّوكُمْ عَلٰٓى اَعْقَابِكُمْ فَتَنْقَلِبُوا خَاسِر۪ينَ", translation: "Ey İman Edenler! Eğer Kafirlere uyarsanız, sizi, topuklarınız üzerinde gerisin geriye döndürürler de hüsrana uğrayanlardan olursunuz.", transliteration: "Ya eyyuhellezine amenu in tutiullezine keferu yeruddukum ala a'kabikum fe tenkalibu hasirin."},
  {id: 443, sure_no: 150, ayet_no: 150, sure_adi: "Ali İmran", text: "بَلِ اللّٰهُ مَوْلٰيكُمْۚ وَهُوَ خَيْرُ النَّاصِر۪ينَ", translation: "Hayır! Sizin Mevla'nız Allah'tır. O, yardım edenlerin en hayırlısıdır.", transliteration: "Belillahu mevlakum, ve huve hayrun nasırin."},
  {id: 444, sure_no: 151, ayet_no: 151, sure_adi: "Ali İmran", text: "سَنُلْق۪ي ف۪ي قُلُوبِ الَّذ۪ينَ كَفَرُوا الرُّعْبَ بِمَٓا اَشْرَكُوا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ سُلْطَاناًۚ وَمَأْوٰيهُمُ النَّارُۜ وَبِئْسَ مَثْوَى الظَّالِم۪ينَ", translation: "Yetki verdiğine dair, hakkında hiçbir kanıt indirmediği varlıklara, ilahlık yakıştırmalarından dolayı, Kafirlerin kalplerine korku salacağız. Onların sığınakları ateştir. Zalimlerin varacağı yer ne kötüdür.", transliteration: "Se nulki fi kulubillezine keferur ru'be bima eşraku billahi ma lem yunezzil bihi sultana, ve me'vahumun nar, ve bi'se mesvez zalimin."},
  {id: 445, sure_no: 152, ayet_no: 152, sure_adi: "Ali İmran", text: "وَلَقَدْ صَدَقَكُمُ اللّٰهُ وَعْدَهُٓ اِذْ تَحُسُّونَهُمْ بِاِذْنِه۪ۚ حَتّٰٓى اِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِي الْاَمْرِ وَعَصَيْتُمْ مِنْ بَعْدِ مَٓا اَرٰيكُمْ مَا تُحِبُّونَۜ مِنْكُمْ مَنْ يُر۪يدُ الدُّنْيَا وَمِنْكُمْ مَنْ يُر۪يدُ الْاٰخِرَةَۚ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْۚ وَلَقَدْ عَفَا عَنْكُمْۜ وَاللّٰهُ ذُوفَضْلٍ عَلَى الْمُؤْمِن۪ينَ", translation: "Elbette Allah, size verdiği sözü tuttu; O'nun izni ile onları kırıp geçiriyordunuz. Ne var ki arzuladığınız zafere kavuştuktan sonra, gevşediniz. Verilen emre uymayarak, itaatsizlik ettiniz. Kiminiz dünyayı isterken, kiminiz de ahireti istiyordu. Bundan dolayı Allah sınamak için, size olan desteğini kesti. Ancak yine de sizi affetti. Allah, Mü'minlere karşı gerçekten lütuf sahibidir.", transliteration: "Ve lekad sadakakumullahu va'dehu iz tehussunehum bi iznih, hatta iza feşiltum ve tenaza'tum fil emri ve asaytum min ba'di ma erakum ma tuhıbbun, minkum men yuridud dunya ve minkum men yuridul ahireh, summe sarafekum anhum li yebteliyekum, ve lekad afa ankum, vallahu zu fadlin alel mu'minin."},
  {id: 446, sure_no: 153, ayet_no: 153, sure_adi: "Ali İmran", text: "اِذْ تُصْعِدُونَ وَلَا تَلْوُ۫نَ عَلٰٓى اَحَدٍ وَالرَّسُولُ يَدْعُوكُمْ ف۪ٓي اُخْرٰيكُمْ فَاَثَابَكُمْ غَماًّ بِغَمٍّ  لِكَيْلَا  تَحْزَنُوا عَلٰى مَا فَاتَكُمْ وَلَا مَٓا اَصَابَكُمْۜ  وَاللّٰهُ خَب۪يرٌ بِمَا تَعْمَلُونَ", translation: "Hani, Resul sizi çağırdığı halde; siz, kimseye dönüp bakmadan uzaklaştınız. Bunun üzerine, Allah, sizi üzüntü üstüne üzüntüyle cezalandırdı. Allah'ın sizi affetmesi, elinizden kaçırdığınıza ve başınıza gelene üzülmeyesiniz diyedir. Allah, yaptığınız her şeyden haberdardır.", transliteration: "İz tus'idune ve la telvune ala ehadin ver resulu yed'ukum fi uhrakum fe esabekum gammen bi gammin li keyla tahzenu ala ma fatekum ve la ma asabekum, vallahu habirun bima ta'melun."},
  {id: 447, sure_no: 154, ayet_no: 154, sure_adi: "Ali İmran", text: "ثُمَّ اَنْزَلَ عَلَيْكُمْ مِنْ بَعْدِ الْغَمِّ اَمَنَةً نُعَاساً يَغْشٰى طَٓائِفَةً مِنْكُمْۙ وَطَٓائِفَةٌ قَدْ اَهَمَّتْهُمْ اَنْفُسُهُمْ يَظُنُّونَ بِاللّٰهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِۜ يَقُولُونَ هَلْ لَنَا مِنَ الْاَمْرِ مِنْ شَيْءٍۜ قُلْ اِنَّ الْاَمْرَ كُلَّهُ لِلّٰهِۜ يُخْفُونَ ف۪ٓي اَنْفُسِهِمْ مَا لَا يُبْدُونَ لَكَۜ يَقُولُونَ لَوْ كَانَ لَنَا مِنَ الْاَمْرِ شَيْءٌ مَا قُتِلْنَا هٰهُنَاۜ قُلْ لَوْ كُنْتُمْ ف۪ي بُيُوتِكُمْ لَبَرَزَ الَّذ۪ينَ كُتِبَ عَلَيْهِمُ الْقَتْلُ اِلٰى مَضَاجِعِهِمْۚ وَلِيَبْتَلِيَ اللّٰهُ مَا ف۪ي صُدُورِكُمْ وَلِيُمَحِّصَ مَا ف۪ي قُلُوبِكُمْۜ وَاللّٰهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Sonra O, üzüntünün ardından, sizden bir kısmınıza, güven duygusu, sarıp kuşatan bir iç dinginlik indirdi. Bir kısmınız da can kaygısına düşmüştü. Allah hakkında, tıpkı Cahiliye dönemindekine benzer biçimde gerçeğe aykırı bir sanı besliyorlardı. \"Bu işten bize ne?\" diyorlardı. De ki: \"Her şeyin takdiri yalnız Allah'ındır.\" Sana, açıklamadıkları şeyleri içlerinde gizliyorlar. \"Elimizden bir şey gelseydi buralarda öldürülmezdik.\" diyorlar. De ki: \"Evlerinizde kalmış olsaydınız bile, üzerlerine öldürülme yazılmış2 olanlar düşüp ölecekleri yere kendiliğinden çıkıp gelirlerdi. Allah, bunu, göğüslerinizde olanı sınamak ve kalplerinizdekilerini arındırmak için yaptı. Allah, göğüslerin özünü bilir.\"", transliteration: "Summe enzele aleykum min ba'dil gammi emeneten nuasen yagşa taifeten minkum, ve taifetun kad ehemmethum enfusuhum yezunnune billahi gayrel hakkı zannel cahiliyyeh, yekulune hel lena minel emri min şey', kul innel emre kullehu lillah, yuhfune fi enfusihim ma la yubdune lek, yekulune lev kane lena minel emri şey'un ma kutilna hahuna, kul lev kuntum fi buyutikum le berezellezine kutibe aleyhimul katlu ila medaciihim, ve li yebteliyallahu ma fi sudurikum ve li yumahhısa ma fi kulubikum, vallahu alimun bi zatis sudur."},
  {id: 448, sure_no: 155, ayet_no: 155, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعَانِۙ اِنَّمَا اسْتَزَلَّهُمُ الشَّيْطَانُ بِبَعْضِ مَا كَسَبُواۚ وَلَقَدْ عَفَا اللّٰهُ عَنْهُمْۜ اِنَّ اللّٰهَ غَفُورٌ حَل۪يمٌ۟", translation: "İki toplumun, savaş alanında karşılaştıkları gün, içinizden geri dönenlere gelince! Bu yaptıklarından dolayı, şeytan onların ayaklarını kaydırmak istedi. Doğrusu, Allah, yine de onları affetti. Kuşkusuz, Allah, Çok Bağışlayıcı'dır, Çok Hoş Görülü'dür.", transliteration: "İnnellezine tevellev minkum yevmel tekal cem'ani, inne mestezellehumuş şeytanu bi ba'di ma kesebu, ve lekad afallahu anhum innallahe gafurun halim."},
  {id: 449, sure_no: 156, ayet_no: 156, sure_adi: "Ali İmran", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَكُونُوا كَالَّذ۪ينَ كَفَرُوا وَقَالُوا لِاِخْوَانِهِمْ اِذَا ضَرَبُوا فِي الْاَرْضِ اَوْ كَانُوا غُزًّى لَوْ كَانُوا عِنْدَنَا مَا مَاتُوا وَمَا قُتِلُواۚ لِيَجْعَلَ اللّٰهُ ذٰلِكَ حَسْرَةً ف۪ي قُلُوبِهِمْۜ وَاللّٰهُ يُحْـي۪ وَيُم۪يتُۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Ey İman Edenler! Yolculuğa çıkan ya da savaşa katılan kardeşleri hakkında, \"Eğer bizim yanımızda olsalardı ölmez ve öldürülmezlerdi.\" diyen, Kafirler gibi olmayın. Allah, bunu, kalplerinde bir hasret olsun diye yaptı. Allah, yaşatan ve öldürendir. Kuşkusuz, Allah, bütün yaptıklarınızı görmektedir.", transliteration: "Ya eyyuhellezine amenu la tekunu kellezine keferu ve kalu li ıhvanihim iza darabu fil ardı ev kanu guzzen lev kanu indena ma matu ve ma kutilu, li yec'alallahu zalike hasreten fi kulubihim vallahu yuhyi ve yumit, vallahu bi ma ta'melune basir."},
  {id: 450, sure_no: 157, ayet_no: 157, sure_adi: "Ali İmran", text: "وَلَئِنْ قُتِلْتُمْ ف۪ي سَب۪يلِ اللّٰهِ اَوْ مُتُّمْ لَمَغْفِرَةٌ مِنَ اللّٰهِ وَرَحْمَةٌ خَيْرٌ مِمَّا يَجْمَعُونَ", translation: "Eğer Allah yolunda ölür veya öldürülürseniz, Allah'tan size bir bağışlama ve bir rahmet vardır. Bilin ki bu, insanların toplayacakları her şeyden daha hayırlıdır.", transliteration: "Ve lein kutiltum fi sebilillahi ev muttum le magfiretun minallahi ve rahmetun hayrun mimma yecmeun."},
  {id: 451, sure_no: 158, ayet_no: 158, sure_adi: "Ali İmran", text: "وَلَئِنْ مُتُّمْ اَوْ قُتِلْتُمْ لَاِلَى اللّٰهِ تُحْشَرُونَ", translation: "Elbette ölseniz de öldürülseniz de muhakkak sonunda Allah'ın huzurunda toplanacaksınız.", transliteration: "Ve lein muttum ev kutiltum le ilallahi tuhşerun."},
  {id: 452, sure_no: 159, ayet_no: 159, sure_adi: "Ali İmran", text: "فَبِمَا رَحْمَةٍ مِنَ اللّٰهِ لِنْتَ لَهُمْۚ وَلَوْ كُنْتَ فَظًّا غَل۪يظَ الْقَلْبِ لَانْفَضُّوا مِنْ حَوْلِكَۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِي الْاَمْرِۚ فَاِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللّٰهِۜ اِنَّ اللّٰهَ يُحِبُّ الْمُتَوَكِّل۪ينَ", translation: "Allah'tan gelen rahmet sayesinde onlara yumuşak davrandın. Eğer kaba ve katı kalpli biri olsaydın etrafından dağılıp giderlerdi. Artık onları affet. Onlar için bağışlanma dile. Yapacağın işlerde onlara danış. Karar verdikten sonra da artık Allah'a tevekkül et. Kuşkusuz Allah, kendisine tevekkül edenleri sever.", transliteration: "Fe bima rahmetin minallahi linte lehum, ve lev kunte fazzan galizal kalbi lenfaddu min havlik, fa'fu anhum vestagfir lehum ve şavirhum fil emr, fe iza azamte fe tevekkel alallah, innallahe yuhibbul mutevekkilin."},
  {id: 453, sure_no: 160, ayet_no: 160, sure_adi: "Ali İmran", text: "اِنْ يَنْصُرْكُمُ اللّٰهُ فَلَا غَالِبَ لَكُمْۚ وَاِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذ۪ي يَنْصُرُكُمْ مِنْ بَعْدِه۪ۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ", translation: "Eğer Allah, size yardım ederse hiç kimse size güç yetiremez. Eğer sizi yardımsız bırakırsa, ondan sonra size kim yardım edebilir? Mü'minler, yalnızca Allah'a tevekkül etsinler.", transliteration: "İn yansurkumullahu fe la galibe lekum, ve in yahzulkum fe menzellezi yansurukum min ba'dih, ve alallahi fel yetevekkelil mu'minun."},
  {id: 454, sure_no: 161, ayet_no: 161, sure_adi: "Ali İmran", text: "وَمَا كَانَ لِنَبِيٍّ اَنْ يَغُلَّۜ وَمَنْ يَغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيٰمَةِۚ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ", translation: "Bir Nebi'nin, emanete ihanet etmesi olur şey değildir! Kim ihanet ederse, kıyamet günü, ihanet ettiğiyle gelecektir. Sonra hiçbir haksızlık olmaksızın herkesin kazandığı tastamam verilecektir.", transliteration: "Ve ma kane li nebiyyin en yagull, ve men yaglul ye'ti bima galle yevmel kıyameh, summe tuveffa kullu nefsin ma kesebet ve hum la yuzlemun."},
  {id: 455, sure_no: 162, ayet_no: 162, sure_adi: "Ali İmran", text: "اَفَمَنِ اتَّبَعَ رِضْوَانَ اللّٰهِ كَمَنْ بَٓاءَ بِسَخَطٍ مِنَ اللّٰهِ وَمَأْوٰيهُ جَهَنَّمُۜ وَبِئْسَ الْمَص۪يرُ", translation: "Allah'ın rızasını kazanan kimse, Allah'ın gazabına uğrayan ve varacağı yer Cehennem olan kimse ile bir olur mu? Orası ne kötü bir sonuçtur.", transliteration: "E femenittebea rıdvanallahi ke men bae bi sehatin minallahi ve me'vahu cehennem, ve bi'sel masir."},
  {id: 456, sure_no: 163, ayet_no: 163, sure_adi: "Ali İmran", text: "هُمْ دَرَجَاتٌ عِنْدَ اللّٰهِۜ وَاللّٰهُ بَص۪يرٌ بِمَا يَعْمَلُونَ۟", translation: "Onların, Allah katındaki dereceleri farklıdır. Allah, yaptıkları her şeyi görendir.", transliteration: "Hum derecatun indallah, vallahu basirun bi ma ya'melun."},
  {id: 457, sure_no: 164, ayet_no: 164, sure_adi: "Ali İmran", text: "لَقَدْ مَنَّ اللّٰهُ عَلَى الْمُؤْمِن۪ينَ اِذْ بَعَثَ ف۪يهِمْ رَسُولاً مِنْ اَنْفُسِهِمْ يَتْلُوا عَلَيْهِمْ اٰيَاتِه۪ وَيُزَكّ۪يهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَۚ وَاِنْ كَانُوا مِنْ قَبْلُ لَف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Ant olsun ki, Allah, içlerinden, onlara ayetlerini okuyan, onları arındıran, onlara Kitap'ı ve Hikmet'i öğreten bir resul göndermekle, Mü'minlere iyilikte bulundu. Oysa onlar daha önce açık bir sapkınlık içindeydiler.", transliteration: "Le kad mennallahu alel mu'minine iz bease fihim resulen min enfusihim yetlu aleyhim ayatihi ve yuzekkihim ve yuallimuhumul kitabe vel hikmeh, ve in kanu min kablu le fi dalalin mubin."},
  {id: 458, sure_no: 165, ayet_no: 165, sure_adi: "Ali İmran", text: "اَوَلَمَّٓا اَصَابَتْكُمْ مُص۪يبَةٌ قَدْ اَصَبْتُمْ مِثْلَيْهَاۙ قُلْتُمْ اَنّٰى هٰذَاۜ قُلْ هُوَ مِنْ عِنْدِ اَنْفُسِكُمْۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "ki katını tattırdığınız musibet, kendinize isabet edince, \"Bu nereden geldi\" mi diyorsunuz? De ki: \"Bu kendi nefsinizdendir.\" Kuşkusuz Allah Her Şeye Güç Yetiren'dir.", transliteration: "E ve lemma asabetkum musibetun kad asabtum misleyha, kultum enna haza, kul huve min indi enfusikum innallahe ala kulli şey'in kadir."},
  {id: 459, sure_no: 166, ayet_no: 166, sure_adi: "Ali İmran", text: "وَمَٓا اَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ فَبِاِذْنِ اللّٰهِ وَلِيَعْلَمَ الْمُؤْمِن۪ينَۙ", translation: "İki toplumun karşılaştığı gün, başınıza gelen musibet, Allah'ın izniyleydi. Bu Mü'minlerin belirlenmesi içindi.", transliteration: "Ve ma asabekum yevmel tekal cem'ani fe bi iznillahi ve li ya'lemel mu'minin."},
  {id: 460, sure_no: 167, ayet_no: 167, sure_adi: "Ali İmran", text: "وَلِيَعْلَمَ الَّذ۪ينَ نَافَقُواۚ وَق۪يلَ لَهُمْ تَعَالَوْا قَاتِلُوا ف۪ي سَب۪يلِ اللّٰهِ اَوِ ادْفَعُواۜ قَالُوا لَوْ نَعْلَمُ قِتَالاً لَاتَّبَعْنَاكُمْۜ هُمْ لِلْكُفْرِ يَوْمَئِذٍ اَقْرَبُ مِنْهُمْ لِلْا۪يمَانِۚ يَقُولُونَ بِاَفْوَاهِهِمْ مَا لَيْسَ ف۪ي قُلُوبِهِمْۜ وَاللّٰهُ اَعْلَمُ بِمَا يَكْتُمُونَۚ", translation: "Bir de münafık olanların belirlenmesi içindi. Onlara: \"Gelin bizimle birlikte Allah yolunda savaşın veya savunmada bulunun.\" denildiğinde; onlar: \"Savaşı bilseydik elbette size uyardık.\" dediler. Onlar, o gün imandan çok küfre yakındılar. Kalplerinde olmayan şeyi ağızlarıyla söylüyorlardı. Oysa Allah, onların içlerinde sakladıklarını çok iyi bilmektedir.", transliteration: "Ve li ya'lemellezine nafeku, ve kile lehum tealev katilu fi sebilillahi evidfeu kalu lev na'lemu kıtalen letteba'nakum, hum lil kufri yevmeizin akrabu minhum lil iman, yekulune bi efvahihim ma leyse fi kulubihim, vallahu a'lemu bi ma yektumun."},
  {id: 461, sure_no: 168, ayet_no: 168, sure_adi: "Ali İmran", text: "اَلَّذ۪ينَ قَالُوا لِاِخْوَانِهِمْ وَقَعَدُوا لَوْ اَطَاعُونَا مَا قُتِلُواۜ قُلْ فَادْرَؤُ۫ا عَنْ اَنْفُسِكُمُ الْمَوْتَ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Savaşa katılmayanlar, kardeşleri için: \"Eğer onlar bize uysalardı öldürülmezlerdi.\" dediler. De ki: \"Eğer doğru söylüyorsanız, o zaman ölümü kendinizden savın.\"", transliteration: "Ellezine kalu li ihvanihim ve kaadu lev atauna ma kutil, kul fedreu an enfusikumul mevte in kuntum sadıkin."},
  {id: 462, sure_no: 169, ayet_no: 169, sure_adi: "Ali İmran", text: "وَلَا تَحْسَبَنَّ الَّذ۪ينَ قُتِلُوا ف۪ي سَب۪يلِ اللّٰهِ اَمْوَاتاًۜ بَلْ اَحْيَٓاءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَۙ", translation: "Allah yolunda öldürülenleri ölü sanma. Bilakis onlar diridirler, Rabb'leri katında rızıklanmaktadırlar.", transliteration: "Ve la tahsebennellezine kutilu fi sebilillahi emvata, bel ahyaun inde rabbihim yurzekun."},
  {id: 463, sure_no: 170, ayet_no: 170, sure_adi: "Ali İmran", text: "فَرِح۪ينَ بِمَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ۙ وَيَسْتَبْشِرُونَ بِالَّذ۪ينَ لَمْ يَلْحَقُوا بِهِمْ مِنْ خَلْفِهِمْۙ اَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۢ", translation: "Allah'ın, lütfundan, kendilerine verdiklerine sevinirler. Arkalarından gelecek olanlara, bir korkunun olmadığını ve üzülmeyeceklerini müjdelemek isterler.", transliteration: "Ferihine bi ma atahumullahu min fadlıhi, ve yestebşirune billezine lem yelhaku bihim min halfihim, ella havfun aleyhim ve la hum yahzenun."},
  {id: 464, sure_no: 171, ayet_no: 171, sure_adi: "Ali İmran", text: "يَسْتَبْشِرُونَ بِنِعْمَةٍ مِنَ اللّٰهِ وَفَضْلٍۙ وَاَنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُؤْمِن۪ينَۚۛ ۟", translation: "Onlar; Allah'ın nimetini, lütfunu ve Allah'ın Mü'minlerin ecirlerini zayi etmeyeceğini müjdelemek isterler.", transliteration: "Yestebşirune bi ni'metin minallahi ve fadlin, ve ennallahe la yudiu ecrel mu'minin."},
  {id: 465, sure_no: 172, ayet_no: 172, sure_adi: "Ali İmran", text: "اَلَّذ۪ينَ اسْتَجَابُوا لِلّٰهِ وَالرَّسُولِ مِنْ بَعْدِ مَٓا اَصَابَهُمُ الْقَرْحُۜۛ لِلَّذ۪ينَ اَحْسَنُوا مِنْهُمْ وَاتَّقَوْا اَجْرٌ عَظ۪يمٌۚ", translation: "Yaralanmalarına rağmen, Allah ve Resul'ünün davetine uyanlar, iyilik yapanlar ve Allah için takvalı olanlar için çok değerli bir karşılık vardır.", transliteration: "Ellezinestecabu lillahi ver resuli min ba'di ma asabehumul karh, lillezine ahsenu minhum vettekav ecrun azim."},
  {id: 466, sure_no: 173, ayet_no: 173, sure_adi: "Ali İmran", text: "اَلَّذ۪ينَ قَالَ لَهُمُ النَّاسُ اِنَّ النَّاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ ا۪يمَاناًۗ وَقَالُوا حَسْبُنَا اللّٰهُ وَنِعْمَ الْوَك۪يلُ", translation: "\"İnsanlar size karşı toplandılar, onlara derin saygı duyun.\" diyenlerin sözleri, onların imanlarını daha da artırdı: \"Allah bize yeter, O ne güzel vekildir.\" dediler.", transliteration: "Ellezine kale lehumun nasu innen nase kad cemeu lekum fahşevhum fe zadehum imana, ve kalu hasbunallahu ve ni'mel vekil."},
  {id: 467, sure_no: 174, ayet_no: 174, sure_adi: "Ali İmran", text: "فَانْقَلَبُوا بِنِعْمَةٍ مِنَ اللّٰهِ وَفَضْلٍ لَمْ يَمْسَسْهُمْ سُٓوءٌۙ وَاتَّبَعُوا رِضْوَانَ اللّٰهِۜ وَاللّٰهُ ذُوفَضْلٍ عَظ۪يمٍ", translation: "Ve Allah'tan gelen nimet ve lütufla kendilerine bir kötülük dokunmadan geri döndüler. Allah'ın rızasını kazandılar. Allah, Büyük Lütuf Sahibi'dir.", transliteration: "Fenkalebu bi ni'metin minallahi ve fadlin lem yemseshum suun, vettebeu rıdvanallah, vallahu zu fadlin azim."},
  {id: 468, sure_no: 175, ayet_no: 175, sure_adi: "Ali İmran", text: "اِنَّمَا ذٰلِكُمُ الشَّيْطَانُ يُخَوِّفُ اَوْلِيَٓاءَهُۖ فَلَا تَخَافُوهُمْ وَخَافُونِ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Şeytan, ancak kendi velilerini korkutur. Eğer Mü'minseniz, onlardan korkmayın, yalnızca Bana karşı gelmekten sakının.", transliteration: "İnnema zalikumuş şeytanu yuhavvifu evliya'eh, fe la tehafuhum ve hafuni in kuntum mu'minin."},
  {id: 469, sure_no: 176, ayet_no: 176, sure_adi: "Ali İmran", text: "وَلَا يَحْزُنْكَ الَّذ۪ينَ يُسَارِعُونَ فِي الْكُفْرِۚ اِنَّهُمْ لَنْ يَضُرُّوا اللّٰهَ شَيْـٔاًۜ يُر۪يدُ اللّٰهُ اَلَّا يَجْعَلَ لَهُمْ حَظًّا فِي الْاٰخِرَةِۚ وَلَهُمْ عَذَابٌ عَظ۪يمٌ", translation: "Küfürde yarışanlar, seni üzmesin. Çünkü onlar, Allah'a hiçbir zarar veremezler. Allah, onları ahirette nasipsiz bırakmak istiyor. Onlar için çok büyük bir azap vardır.", transliteration: "Ve la yahzunkellezine yusariune fil kufr, innehum len yadurrullahe şey'a, yuridullahu ella yec'ale lehum hazzan fil ahireh, ve lehum azabun azim."},
  {id: 470, sure_no: 177, ayet_no: 177, sure_adi: "Ali İmran", text: "اِنَّ الَّذ۪ينَ اشْتَرَوُا الْكُفْرَ بِالْا۪يمَانِ لَنْ يَضُرُّوا اللّٰهَ شَيْـٔاًۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "İmana karşılık küfrü satın alanlar, Allah'a kesinlikle zarar veremezler. Onlar için can yakıcı bir azap vardır.", transliteration: "İnnellezineşteravul kufra bil imani len yedurrullahe şey'a, ve lehum azabun elim."},
  {id: 471, sure_no: 178, ayet_no: 178, sure_adi: "Ali İmran", text: "وَلَا يَحْسَبَنَّ الَّذ۪ينَ كَفَرُٓوا اَنَّمَا نُمْل۪ي لَهُمْ خَيْرٌ لِاَنْفُسِهِمْۜ اِنَّمَا نُمْل۪ي لَهُمْ لِيَزْدَادُٓوا اِثْماًۚ وَلَهُمْ عَذَابٌ مُه۪ينٌ", translation: "Kafirler, kendilerine süre tanımamızın, yararlarına olduğunu sanmasınlar. Onlara süre tanıyoruz, onlar ise günahlarını artırıyorlar. Onlar için, alçaltıcı bir azap vardır.", transliteration: "Ve la yahsebennellezine keferu ennema numli lehum hayrun li enfusihim, innema numli lehum li yezdadu isma, ve lehum azabun muhin."},
  {id: 472, sure_no: 179, ayet_no: 179, sure_adi: "Ali İmran", text: "مَا كَانَ اللّٰهُ لِيَذَرَ الْمُؤْمِن۪ينَ عَلٰى مَٓا اَنْتُمْ عَلَيْهِ حَتّٰى يَم۪يزَ الْخَب۪يثَ مِنَ الطَّيِّبِۜ وَمَا كَانَ اللّٰهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلٰكِنَّ اللّٰهَ يَجْتَب۪ي مِنْ رُسُلِه۪ مَنْ يَشَٓاءُ فَاٰمِنُوا بِاللّٰهِ وَرُسُلِه۪ۚ وَاِنْ تُؤْمِنُوا وَتَتَّقُوا فَلَكُمْ اَجْرٌ عَظ۪يمٌ", translation: "Allah, tayyib olanı habis olandan ayırmadan; Mü'minleri içinde bulunduğunuz şu durumda bırakacak değildir. Allah, sizi gaipten haberdar edecek de değildir. Ancak Allah, resullerinden dilediğini seçer. O halde, Allah'a ve Resullerine iman edin. Eğer iman edip, takvalı davranırsanız, sizin için büyük bir ödül vardır.", transliteration: "Ma kanallahu li yezerel mu'minine ala ma entum aleyhi hatta yemizel habise minet tayyib, ve ma kanallahu li yutliakum alel gaybi ve lakinnallahe yectebi min rusulihi men yeşau fe aminu billahi ve rusulih, ve in tu'minu ve tetteku fe lekum ecrun azim."},
  {id: 473, sure_no: 180, ayet_no: 180, sure_adi: "Ali İmran", text: "وَلَا يَحْسَبَنَّ الَّذ۪ينَ يَبْخَلُونَ بِمَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ هُوَ خَيْراً لَهُمْۜ بَلْ هُوَ شَرٌّ لَهُمْۜ سَيُطَوَّقُونَ مَا بَخِلُوا بِه۪ يَوْمَ الْقِيٰمَةِۜ وَلِلّٰهِ م۪يرَاثُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ۟", translation: "Allah'ın, lütuf olarak verdiği şeylerden cimrilik eden kimseler, bu tutumlarının kendi yararına olacağını sanmasınlar. Bu, onlar için sadece bir kötülüktür. Cimrilikle yanlarında tuttukları mal, Kıyamet Günü boyunlarına dolanacaktır. Göklerin ve yeryüzünün mirası yalnızca Allah'a aittir. Allah, yaptığınız her şeyden haberdardır.", transliteration: "Ve la yahsebennellezine yebhalune bi ma atahumullahu min fadlıhi huve hayran lehum, bel huve şerrun lehum se yutavvekune ma bahilu bihi yevmel kıyameh, ve lillahi mirasus semavati vel ard, vallahu bi ma ta'melune habir."},
  {id: 474, sure_no: 181, ayet_no: 181, sure_adi: "Ali İmran", text: "لَقَدْ سَمِـعَ اللّٰهُ قَوْلَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ فَق۪يرٌ وَنَحْنُ اَغْنِيَٓاءُۢ سَنَكْتُبُ مَا قَالُوا وَقَتْلَهُمُ الْاَنْبِيَٓاءَ بِغَيْرِ حَقٍّۙ وَنَقُولُ ذُوقُوا عَذَابَ الْحَر۪يقِ", translation: "\"Allah fakirdir, biz zenginiz.\" diyenlerin sözünü, Allah elbette işitmiştir. Hem bu söylediklerini hem de haksız yere Nebileri öldürmelerini yazacağız. Ve onlara, \"Yakıcı azabı tadın!\" diyeceğiz.", transliteration: "Lekad semiallahu kavlellezine kalu innallahe fakirun ve nahnu agniyau se nektubu ma kalu ve katlehumul enbiyae bi gayri hakkın, ve nekulu zuku azabel harik."},
  {id: 475, sure_no: 182, ayet_no: 182, sure_adi: "Ali İmran", text: "ذٰلِكَ بِمَا قَدَّمَتْ اَيْد۪يكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِلْعَب۪يدِۚ", translation: "Bu, ellerinizle yaptığınız şeylerin karşılığıdır. Yoksa Allah, kullarına, haksızlık edici değildir.", transliteration: "Zalike bima kaddemet eydikum ve ennallahe leyse bi zallamin lil abid."},
  {id: 476, sure_no: 183, ayet_no: 183, sure_adi: "Ali İmran", text: "اَلَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ عَهِدَ اِلَيْنَٓا اَلَّا نُؤْمِنَ لِرَسُولٍ حَتّٰى يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُۜ قُلْ قَدْ جَٓاءَكُمْ رُسُلٌ مِنْ قَبْل۪ي بِالْبَيِّنَاتِ وَبِالَّذ۪ي قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Onlar dediler ki: Allah, \"Ateş tarafından yenen bir kurban getirmedikçe, hiçbir Resul'e inanmamamız konusunda bizden söz aldı.\" De ki: \"Kuşkusuz, benden önce nice Resuller açık kanıtlarla ve sizin istediğiniz şeyi getirmişti. Eğer doğru söyleyenlerdenseniz, onları niçin öldürdünüz?\"", transliteration: "Ellezine kalu innallahe ahide ileyna ella nu'mine li resulin hatta ye'tiyena bi kurbanin te'kuluhun nar, kul kad caekum rusulun min kabli bil beyyinati ve billezi kultum fe lime kateltumuhum in kuntum sadıkin."},
  {id: 477, sure_no: 184, ayet_no: 184, sure_adi: "Ali İmran", text: "فَاِنْ كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٌ مِنْ قَبْلِكَ جَٓاؤُ۫ بِالْبَيِّنَاتِ وَالزُّبُرِ وَالْكِتَابِ الْمُن۪يرِ", translation: "Şimdi seni yalanlayanlar; senden önce açık kanıtlarla, zeburlar ve aydınlatıcı kitaplarla gelen Resulleri de yalanlamışlardı.", transliteration: "Fe in kezzebuke fe kad kuzzibe rusulun min kablike cau bil beyyinati vez zuburi vel kitabil munir."},
  {id: 478, sure_no: 185, ayet_no: 185, sure_adi: "Ali İmran", text: "كُلُّ نَفْسٍ ذَٓائِقَةُ الْمَوْتِۜ وَاِنَّمَا تُوَفَّوْنَ اُجُورَكُمْ يَوْمَ الْقِيٰمَةِۜ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَاُدْخِلَ الْجَنَّةَ فَقَدْ فَازَۜ وَمَا الْحَيٰوةُ الدُّنْيَٓا اِلَّا مَتَاعُ الْغُرُورِ", translation: "Her nefis ölümü tadıcıdır. Kıyamet Günü'nde yaptıklarınızın karşılığı, tam olarak verilecektir. Her kim Cehennem'den uzaklaştırılıp Cennet'e konursa, kuşkusuz o kurtulmuştur. Zaten dünya hayatı, aldatıcı geçimlikten başka bir şey değildir.", transliteration: "Kullu nefsin zaikatul mevt, ve innema tuveffevne ucurekum yevmel kıyameh, fe men zuhziha anin nari ve udhılel cennete fe kad faz, ve mal hayatud dunya illa metaul gurur."},
  {id: 479, sure_no: 186, ayet_no: 186, sure_adi: "Ali İmran", text: "لَتُبْلَوُنَّ ف۪ٓي اَمْوَالِكُمْ وَاَنْفُسِكُمْ وَلَتَسْمَعُنَّ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَمِنَ الَّذ۪ينَ اَشْرَكُٓوا اَذًى كَث۪يراًۜ وَاِنْ تَصْبِرُوا وَتَتَّقُوا فَاِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُورِ", translation: "Mallarınızla ve canlarınızla deneneceksiniz. Sizden önce kendilerine Kitap verilenlerden ve şirk koşanlardan çok üzücü sözler işiteceksiniz. Eğer sabreder ve takvalı olursanız, bilin ki bu, kesin kararlılık gösterilmesi gereken işlerdendir.", transliteration: "Le tublevunne fi emvalikum ve enfusikum ve le tesmeunne minellezine utul kitabe min kablikum ve minellezine eşraku ezen kesira, ve in tasbiru ve tetteku fe inne zalike min azmil umur."},
  {id: 480, sure_no: 187, ayet_no: 187, sure_adi: "Ali İmran", text: "وَاِذْ اَخَذَ اللّٰهُ م۪يثَاقَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ لَتُبَيِّنُنَّهُ لِلنَّاسِ وَلَا تَكْتُمُونَهُۘ فَنَبَذُوهُ وَرَٓاءَ ظُهُورِهِمْ وَاشْتَرَوْا بِه۪ ثَمَناً قَل۪يلاًۜ فَبِئْسَ مَا يَشْتَرُونَ", translation: "Hani Allah, Kitap Ehli'nden, onu insanlara açıklayacaklarına ve asla gizlemeyeceklerine dair kesin bir söz almıştı. Fakat onlar, az bir çıkar karşılığında, bu sözlerine sırt çevirdiler. Bu ne kötü bir alışveriştir!", transliteration: "Ve iz ehazallahu misakallezine utul kitabe le tubeyyinunnehu lin nasi ve la tektumuneh, fe nebezuhu verae zuhurihim veşterav bihi semenen kalila, fe bi'se ma yeşterun."},
  {id: 481, sure_no: 188, ayet_no: 188, sure_adi: "Ali İmran", text: "لَا تَحْسَبَنَّ الَّذ۪ينَ يَفْرَحُونَ بِمَٓا اَتَوْا وَيُحِبُّونَ اَنْ يُحْمَدُوا بِمَا لَمْ يَفْعَلُوا فَلَا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِنَ الْعَذَابِۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Yaptıklarıyla sevinen, yapmadıkları şeylerle övülmeyi seven kimselerin, azaptan kurtulacaklarını sanma. Onlar için can yakıcı bir azap vardır.", transliteration: "La tahsebennellezine yefrahune bi ma etev ve yuhıbbune en yuhmedu bi ma lem yef'alu fe la tahsebennehum bi mefazetin minel azab, ve lehum azabun elim."},
  {id: 482, sure_no: 189, ayet_no: 189, sure_adi: "Ali İmran", text: "وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ۟", translation: "Göklerin ve yerin mülkü Allah'a aittir. Allah, Her Şeye Güç Yetiren'dir.", transliteration: "Ve lillahi mulkus semavati vel ard, vallahu ala kulli şey'in kadir."},
  {id: 483, sure_no: 190, ayet_no: 190, sure_adi: "Ali İmran", text: "اِنَّ ف۪ي خَلْقِ السَّمٰوَاتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ لَاٰيَاتٍ لِاُو۬لِي الْاَلْبَابِۚ", translation: "Kuşkusuz, göklerin ve yerin yaradılışında, gece ile gündüzün art arda gelişinde, sağlıklı düşünenler için kesin kanıtlar vardır.", transliteration: "İnne fi halkıs semavati vel ardı vahtilafil leyli ven nehari le ayatin li ulil elbab."},
  {id: 484, sure_no: 191, ayet_no: 191, sure_adi: "Ali İmran", text: "اَلَّذ۪ينَ يَذْكُرُونَ اللّٰهَ قِيَاماً وَقُعُوداً وَعَلٰى جُنُوبِهِمْ وَيَتَفَكَّرُونَ ف۪ي خَلْقِ السَّمٰوَاتِ وَالْاَرْضِۚ رَبَّنَا مَا خَلَقْتَ هٰذَا بَاطِلاًۚ سُبْحَانَكَ فَقِنَا عَذَابَ النَّارِ", translation: "Onlar; ayaktayken, otururken ve yanları üzerine iken Allah'ı anarlar. Göklerin ve yerin yaradılışı hakkında düşünürler: \"Rabb'imiz! Sen, bunu boşuna yaratmadın, Seni her türlü noksanlıktan tenzih ederiz. Bizi ateşin azabından koru.\"", transliteration: "Ellezine yezkurunallahe kıyamen ve kuuden ve ala cunubihim ve yetefekkerune fi halkıs semavati vel ard, rabbena ma halakte haza batıla, subhaneke fekına azaben nar."},
  {id: 485, sure_no: 192, ayet_no: 192, sure_adi: "Ali İmran", text: "رَبَّنَٓا اِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ اَخْزَيْتَهُۜ وَمَا لِلظَّالِم۪ينَ مِنْ اَنْصَارٍ", translation: "\"Ey Rabb'imiz! Kuşkusuz, kimi ateşe sokarsan, onu perişan edersin. Zalimlerin yardımcıları yoktur.\"", transliteration: "Rabbena inneke men tudhılin nare fe kad ahzeyteh, ve ma liz zalimine min ensar."},
  {id: 486, sure_no: 193, ayet_no: 193, sure_adi: "Ali İmran", text: "رَبَّنَٓا اِنَّـنَا سَمِعْنَا مُنَادِياً يُنَاد۪ي لِلْا۪يمَانِ اَنْ اٰمِنُوا بِرَبِّكُمْ فَاٰمَنَّاۗ رَبَّنَا فَاغْفِرْ لَنَا ذُنُوبَنَا وَكَفِّرْ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّـنَا مَعَ الْاَبْرَارِۚ", translation: "\"Ey Rabb'imiz! Biz, \"Rabb'inize iman edin.\" diye, iman etmeye çağıran bir davetçiyi işittik ve hemen iman ettik. Rabb'imiz! Suçlarımızı bağışla, kötülüklerimizi ört ve canımızı iyilikle al.\"", transliteration: "Rabbena innena semi'na munadiyen yunadi lil imani en aminu bi rabbikum fe amenna, rabbena fagfir lena zunubena ve keffir anna seyyiatina ve teveffena meal ebrar."},
  {id: 487, sure_no: 194, ayet_no: 194, sure_adi: "Ali İmran", text: "رَبَّنَا وَاٰتِنَا مَا وَعَدْتَنَا عَلٰى رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيٰمَةِۜ اِنَّكَ لَا تُخْلِفُ الْم۪يعَادَ", translation: "\"Rabb'imiz! Resullerinin diliyle vadettiklerini bize ver, kıyamet gününde bizi alçaltma; kuşkusuz, Sen, verdiğin sözden dönmezsin.\"", transliteration: "Rabbena ve atina ma vaadtena ala rusulike ve la tuhzina yevmel kıyameh, inneke la tuhliful miad."},
  {id: 488, sure_no: 195, ayet_no: 195, sure_adi: "Ali İmran", text: "فَاسْتَجَابَ لَهُمْ رَبُّهُمْ اَنّ۪ي لَٓا اُض۪يعُ عَمَلَ عَامِلٍ مِنْكُمْ مِنْ ذَكَرٍ اَوْ اُنْثٰىۚ بَعْضُكُمْ مِنْ بَعْضٍۚ فَالَّذ۪ينَ هَاجَرُوا وَاُخْرِجُوا مِنْ دِيَارِهِمْ وَاُو۫ذُوا ف۪ي سَب۪يل۪ي وَقَاتَلُوا وَقُتِلُوا لَاُكَفِّرَنَّ عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَاُدْخِلَنَّهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۚ ثَوَاباً مِنْ عِنْدِ اللّٰهِۜ وَاللّٰهُ عِنْدَهُ حُسْنُ الثَّوَابِ", translation: "Ve Rabb'leri, onlara cevap verdi: \"Ben, sizden; erkek olsun, kadın olsun -ki hepiniz birbirinizdensiniz- iyi şeyler yapanların yaptıklarının karşılığını boşa çıkarmam. Onlar ki benim yolumda hicret edenler, yurtlarından çıkarılanlar, yolumda eziyet görenler, savaşanlar ve öldürülenlerdir. İşte bunların kötülüklerini örterim. Onların yaptıklarının karşılığı Allah'ın yanındadır. Kuşkusuz, onları içinden nehirler akan Cennetlere koyacağım. Karşılıkların en iyisi, Allah katındadır.\"", transliteration: "Festecabe lehum rabbuhum enni la udiu amele amilin minkum min zekerin ev unsa, ba'dukum min ba'd, fellezine haceru ve uhricu min diyarihim ve uzu fi sebili ve katelu ve kutilu le ukeffirenne anhum seyyiatihim ve le udhılennehum cennatin tecri min tahtihal enhar, sevaben min indillah vallahu indehu husnus sevab."},
  {id: 489, sure_no: 196, ayet_no: 196, sure_adi: "Ali İmran", text: "لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذ۪ينَ كَفَرُوا فِي الْبِلَادِۜ", translation: "Kafirlerin diyar diyar gezip dolaşmaları sakın seni aldatmasın.", transliteration: "La yegurranneke tekallubelluzine keferu fil bilad."},
  {id: 490, sure_no: 197, ayet_no: 197, sure_adi: "Ali İmran", text: "مَتَاعٌ قَل۪يلٌ ثُمَّ مَأْوٰيهُمْ جَهَنَّمُۜ وَبِئْسَ الْمِهَادُ", translation: "Bu, az bir yararlanmadır. Sonra varacakları yer Cehennem'dir. Orası ne kötü bir yerdir!", transliteration: "Metaun kalilun summe me'vahum cehennem, ve bi'sel mihad."},
  {id: 491, sure_no: 198, ayet_no: 198, sure_adi: "Ali İmran", text: "لٰكِنِ الَّذ۪ينَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا نُزُلاً مِنْ عِنْدِ اللّٰهِۜ وَمَا عِنْدَ اللّٰهِ خَيْرٌ لِلْاَبْرَارِ", translation: "Fakat Rabb'lerine karşı gelmekten sakınan kimseler için, içinden ırmaklar akan Cennetler vardır. Onlar, orada sürekli kalıcıdırlar. Bu, Allah katından bir konaklamadır. İyi kimseler için, Allah katında olan daha hayırlıdır.", transliteration: "Lakinillezinettekav rabbehum lehum cennatun tecri min tahtihal enharu halidine fiha nuzulen min indillah, ve ma indallahi hayrun lil ebrar."},
  {id: 492, sure_no: 199, ayet_no: 199, sure_adi: "Ali İmran", text: "وَاِنَّ مِنْ اَهْلِ الْكِتَابِ لَمَنْ يُؤْمِنُ بِاللّٰهِ وَمَٓا اُنْزِلَ اِلَيْكُمْ وَمَٓا اُنْزِلَ اِلَيْهِمْ خَاشِع۪ينَ لِلّٰهِۙ لَا يَشْتَرُونَ بِاٰيَاتِ اللّٰهِ ثَمَناً قَل۪يلاًۜ اُو۬لٰٓئِكَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۜ اِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ", translation: "Kitap Ehli'nden öyle kimseler var ki, Allah'a, size indirilene, kendilerine indirilene tam bir samimiyetle iman ederler. Allah'ın ayetlerini az bir değer karşılığında satmazlar. İşte onların hak ettikleri şey Rabb'lerinin yanındadır. Kuşkusuz, Allah, hesabı çabuk görendir.", transliteration: "Ve inne min ehlil kitabi le men yu'minu billahi ve ma unzile ileykum ve ma unzile ileyhim haşiine lillahi, la yeşterune bi ayatillahi semenen kalila, ulaike lehum ecruhum inde rabbihim innallahe seriul hısab."},
  {id: 493, sure_no: 200, ayet_no: 200, sure_adi: "Ali İmran", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اصْبِرُوا وَصَابِرُوا وَرَابِطُوا وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَ", translation: "Ey İman Edenler! Sabredin ve zorluklara karşı dirençli olun. Sürekli duyarlı olun. Allah'a karşı takva sahibi olun. Umulur ki kurtuluşa erenlerden olursunuz.", transliteration: "Ya eyyuhellezine amenusbiru ve sabiru ve rabitu vettekullahe leallekum tuflihun."},
  {id: 494, sure_no: 1, ayet_no: 1, sure_adi: "Nisa", text: "يَٓا اَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمُ الَّذ۪ي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ وَخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالاً كَث۪يراً وَنِسَٓاءًۚ وَاتَّقُوا اللّٰهَ الَّذ۪ي تَسَٓاءَلُونَ بِه۪ وَالْاَرْحَامَۜ اِنَّ اللّٰهَ كَانَ عَلَيْكُمْ رَق۪يباً", translation: "Ey insanlar! Sizi, tek bir nefisten yaratan, ondan eşini yaratan ve o ikisinden birçok erkek ve kadını üreten Rabb'inize karşı takvalı olun. Birbirinizden yararlanasınız diye akrabalık bağını kuran Allah'a karşı takvalı olun. Kuşkusuz, Allah, sizi gözetmektedir.", transliteration: "Ya eyyuhan nasutteku rabbekumullezi halakakum min nefsin vahidetin ve halaka minha zevceha ve besse minhuma ricalen kesiran ve nisaa, vettekullahellezi tesaelune bihi vel erham. İnnallahe kane aleykum rakiba."},
  {id: 495, sure_no: 2, ayet_no: 2, sure_adi: "Nisa", text: "وَاٰتُوا الْيَتَامٰٓى اَمْوَالَهُمْ وَلَا تَتَبَدَّلُوا الْخَب۪يثَ بِالطَّيِّبِۖ وَلَا تَأْكُلُٓوا اَمْوَالَهُمْ اِلٰٓى اَمْوَالِكُمْۜ  اِنَّهُ كَانَ حُوباً كَب۪يراً", translation: "Yetimlere, mallarını verin; onların iyi mallarını kötü mallarınızla değiştirmeyin. Onların mallarını, kendi mallarınıza karıştırarak yemeyin. Kuşkusuz, bu büyük bir vebaldir!", transliteration: "Ve atul yetama emvalehum ve la tetebeddelul habise bit tayyib, ve la te'kulu emvalehum ila emvalikum. İnnehu kane huben kebira."},
  {id: 496, sure_no: 3, ayet_no: 3, sure_adi: "Nisa", text: "وَاِنْ خِفْتُمْ اَلَّا تُـقْسِطُوا فِي الْيَتَامٰى فَانْكِحُوا مَا طَابَ لَكُمْ مِنَ النِّسَٓاءِ مَثْنٰى وَثُلٰثَ وَرُبَاعَۚ فَاِنْ خِفْتُمْ اَلَّا تَعْدِلُوا فَوَاحِدَةً اَوْ مَا مَلَكَتْ اَيْمَانُكُمْۜ ذٰلِكَ اَدْنٰٓى اَلَّا تَعُولُواۜ", translation: "Eğer yetimler konusunda hakkaniyetli olamayacağınızdan korktuysanız, o zaman hoşnut etmek, sevindirmek istediğiniz o kadınlardan ikişer, üçer, dörder nikahlayın. Eğer o takdirde de adaleti gözetemeyeceğinizden korktuysanız, o zaman bir tanesini ya da yeminle hak sahibi olduğunuzu nikahlayın. Haksızlık etmemek için en uygun olan budur.", transliteration: "Ve in hıftum ella tuksitu fil yetama fenkihu ma tabe lekum minen nisai mesna ve sulase ve rubaa, fe in hıftum ella ta'dilu fe vahideten ev ma meleket eymanukum. Zalike edna ella teulu."},
  {id: 497, sure_no: 4, ayet_no: 4, sure_adi: "Nisa", text: "وَاٰتُوا النِّسَٓاءَ صَدُقَاتِهِنَّ نِحْلَةًۜ  فَاِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِنْهُ نَفْساً فَكُلُوهُ هَن۪ٓيـٔاً مَر۪ٓيـٔاً", translation: "O kadınlara, vadettiklerinizi bir hak olarak verin. Eğer gönül rızası ile ondan bir kısmını size verirlerse, o zaman onu dilediğiniz gibi yiyin.", transliteration: "Ve atun nisae sadukatihinne nıhleh. Fe in tıbne lekum an şey'in minhu nefsen fe kuluhu henien meria."},
  {id: 498, sure_no: 5, ayet_no: 5, sure_adi: "Nisa", text: "وَلَا تُؤْتُوا السُّفَـهَٓاءَ اَمْوَالَكُمُ الَّت۪ي جَعَلَ اللّٰهُ لَكُمْ قِيَاماً وَارْزُقُوهُمْ ف۪يهَا وَاكْسُوهُمْ وَقُولُوا لَهُمْ قَوْلاً مَعْرُوفاً", translation: "Allah'ın, sizi kaim kıldığı malların idaresini aklı ermezlere bırakmayın. O mallarla onların geçimlerini temin edin ve onları giydirin. Onlara uygun şekilde davranın.", transliteration: "Ve la tu'tus sufehae emvalekumulleti cealallahu lekum kıyamen verzukuhum fiha veksuhum ve kulu lehum kavlen ma'rufa."},
  {id: 499, sure_no: 6, ayet_no: 6, sure_adi: "Nisa", text: "وَابْتَلُوا الْيَتَامٰى حَتّٰٓى اِذَا بَلَغُوا النِّكَاحَۚ فَاِنْ اٰنَسْتُمْ مِنْهُمْ رُشْداً فَادْفَعُٓوا اِلَيْهِمْ اَمْوَالَهُمْۚ وَلَا تَأْكُلُوهَٓا اِسْرَافاً وَبِدَاراً اَنْ يَكْـبَرُواۜ وَمَنْ كَانَ غَنِياًّ فَلْيَسْتَعْفِفْۚ وَمَنْ كَانَ فَق۪يراً فَلْيَأْكُلْ بِالْمَعْرُوفِۜ فَاِذَا دَفَعْتُمْ اِلَيْهِمْ اَمْوَالَهُمْ فَاَشْهِدُوا عَلَيْهِمْۜ وَكَفٰى بِاللّٰهِ حَس۪يباً", translation: "Yetimlerinizi, nikah çağına erişinceye kadar gözetleyin. Olgunluk yaşına geldiklerinde mallarını kendilerine verin. Büyüyünce onlara kalacak düşüncesiyle, mallarını acelece ve haddi aşarak yemeyin. Durumu iyi olan malı yemeye tenezzül etmesin. Durumu iyi olmayan da maldan uygun bir şekilde yararlansın. Onlara mallarını teslim ettiğinizde, onlar adına tanıklar bulundurun. Hesap görücü olarak Allah yeter.", transliteration: "Vebtelul yetama hatta iza belagun nikah, fe in anestum minhum ruşden fedfeu ileyhim emvalehum, ve la te'kuluha israfen ve bidaren en yekberu. Ve men kane ganiyyen felyesta'fif, ve men kane fakiran felye'kul bil ma'ruf. Fe iza defa'tum ileyhim emvalehum fe eşhidu aleyhim. Ve kefa billahi hasiba."},
  {id: 500, sure_no: 7, ayet_no: 7, sure_adi: "Nisa", text: "لِلرِّجَالِ نَص۪يبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالْاَقْرَبُونَۖ وَلِلنِّسَٓاءِ نَص۪يبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالْاَقْرَبُونَ مِمَّا قَلَّ مِنْهُ اَوْ كَثُرَۜ نَص۪يباً مَفْرُوضاً", translation: "Anne, baba ve yakın akrabanın bıraktıklarından; erkeklere, anne ve baba ile yakın akrabanın bıraktıklarından; kadınlara, az olsun çok olsun farz olarak bir pay vardır.", transliteration: "Lir ricali nasibun mimma terakel validani vel akrabune, ve lin nisai nasibun mimma terakel validani vel akrabune mimma kalle minhu ev kesur. Nasiben mefruda."},
  {id: 501, sure_no: 8, ayet_no: 8, sure_adi: "Nisa", text: "وَاِذَا حَضَرَ الْقِسْمَةَ اُو۬لُوا الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينُ فَارْزُقُوهُمْ مِنْهُ وَقُولُوا لَهُمْ قَوْلاً مَعْرُوفاً", translation: "Mirasın paylaşılması esnasında, orada bulunan yakınları, yetimleri ve yoksulları da yararlandırın ve onları incitmeyecek bir üslup kullanın.", transliteration: "Ve iza hadaral kısmete ulul kurba vel yetama vel mesakinu ferzukuhum minhu ve kulu lehum kavlen ma'rufa."},
  {id: 502, sure_no: 9, ayet_no: 9, sure_adi: "Nisa", text: "وَلْيَخْشَ الَّذ۪ينَ لَوْ تَرَكُوا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعَافاً خَافُوا عَلَيْهِمْۖ فَلْيَتَّقُوا اللّٰهَ وَلْيَقُولُوا قَوْلاً سَد۪يداً", translation: "Arkalarında küçük ve aciz evlat bırakanlar, onlara karşı nasıl endişe duyuyorlarsa, aynı endişeyi onlar hakkında da duysunlar. Allah'a karşı takva ehl-i olsunlar ve doğru olan şey neyse onu söylesinler.", transliteration: "Velyahşellezine lev teraku min halfihim zurriyeten dıafen hafu aleyhim, felyettekullahe velyekulu kavlen sedida."},
  {id: 503, sure_no: 10, ayet_no: 10, sure_adi: "Nisa", text: "اِنَّ الَّذ۪ينَ يَأْكُلُونَ اَمْوَالَ الْيَتَامٰى ظُلْماً اِنَّمَا يَأْكُلُونَ ف۪ي بُطُونِهِمْ نَاراًۜ وَسَيَصْلَوْنَ سَع۪يراً۟", translation: "Haksız şekilde yetimlerin mallarını yiyen kimseler, ancak karınlarına ateş doldurmuş olurlar. İşte onlar, yakında kızgın alevli ateşe atılacaklardır.", transliteration: "İnnellezine ye'kulune emvalel yetama zulmen innema ye'kulune fi butunihim nara. Ve se yaslevne seira."},
  {id: 504, sure_no: 11, ayet_no: 11, sure_adi: "Nisa", text: "يُوص۪يكُمُ اللّٰهُ ف۪ٓي اَوْلَادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِۚ فَاِنْ كُنَّ نِسَٓاءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۚ وَاِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُۜ وَلِاَبَوَيْهِ لِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُ مِمَّا تَرَكَ اِنْ كَانَ لَهُ وَلَدٌۚ فَاِنْ لَمْ يَكُنْ لَهُ وَلَدٌ وَوَرِثَهُٓ اَبَوَاهُ فَلِاُمِّهِ الثُّلُثُۚ فَاِنْ كَانَ لَهُٓ اِخْوَةٌ فَلِاُمِّهِ السُّدُسُ مِنْ بَعْدِ وَصِيَّةٍ يُوص۪ي بِهَٓا اَوْ دَيْنٍۜ اٰبَٓاؤُ۬كُمْ وَاَبْنَٓاؤُ۬كُمْۚ لَا تَدْرُونَ اَيُّهُمْ اَقْرَبُ لَكُمْ نَفْعاًۚ فَر۪يضَةً مِنَ اللّٰهِۜ  اِنَّ اللّٰهَ كَانَ عَل۪يماً حَك۪يماً", translation: "Allah size çocuklarınız hakkında vasiyet eder: Erkek çocuğun payı kız çocuğunun iki katıdır. Fakat onlar, ikiden fazla kadın iseler miras üçte iki onlarındır. Eğer bir kadın ise mirasın yarısı onundur. Eğer ölenin çocuğu varsa, anne ve babanın her birine mirastan altıda bir pay vardır. Eğer ölenin çocuğu yok da anne ve baba mirasçı olmuşsa, anneye üçte bir pay vardır. Eğer ölenin kardeşleri varsa, anneye altıda bir pay düşer. Bu paylaşma, ölenin yaptığı vasiyetten ve borçlarının ödenmesinden sonradır. Babalarınız ve oğullarınızdan hangisinin size fayda bakımından daha yakın olduğunu bilemezsiniz. Bu, Allah'ın farz kıldığı hükümdür. Kuşkusuz, Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Yusikumullahu fi evladikum liz zekeri mislu hazzıl unseyeyn, fe in kunne nisaen fevkasneteyni fe lehunne sulusa ma terak, ve in kanet vahideten fe lehan nısf. Ve li ebeveyhi li kulli vahidin min humas sudusu mimma terake in kane lehu veled, fe in lem yekun lehu veledun ve varisehu ebevahu fe li ummihis sulus, fe in kane lehu ıhvetun fe li ummihis sudusu, min ba'di vasiyyetin yusi biha ev deyn. Abaukum ve ebnaukum, la tedrune eyyuhum akrabu lekum nef'a, faridaten minallah. İnnallahe kane alimen hakima."},
  {id: 505, sure_no: 12, ayet_no: 12, sure_adi: "Nisa", text: "وَلَكُمْ نِصْفُ مَا تَرَكَ اَزْوَاجُكُمْ اِنْ لَمْ يَكُنْ لَهُنَّ وَلَدٌۚ فَاِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْ بَعْدِ وَصِيَّةٍ يُوص۪ينَ بِهَٓا اَوْ دَيْنٍۜ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ اِنْ لَمْ يَكُنْ لَكُمْ وَلَدٌۚ فَاِنْ كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ مِنْ بَعْدِ وَصِيَّةٍ تُوصُونَ بِهَٓا اَوْ دَيْنٍۜ وَاِنْ كَانَ رَجُلٌ يُورَثُ كَلَالَةً اَوِ امْرَاَةٌ وَلَهُٓ اَخٌ اَوْ اُخْتٌ فَلِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُۚ فَاِنْ كَانُٓوا اَكْثَرَ مِنْ ذٰلِكَ فَهُمْ شُرَكَٓاءُ فِي الثُّلُثِ مِنْ بَعْدِ وَصِيَّةٍ يُوصٰى بِهَٓا اَوْ دَيْنٍۙ غَيْرَ مُضَٓارٍّۚ وَصِيَّةً مِنَ اللّٰهِۜ وَاللّٰهُ عَل۪يمٌ حَل۪يمٌۜ", translation: "Eğer, hanımlarınızın çocukları yoksa bıraktıklarının yarısı sizindir. Eğer, çocukları varsa, bıraktıklarının dörtte biri sizindir. Bu, yaptığı vasiyetten ve borçlarının ödenmesinden sonradır. Eğer çocuğunuz yoksa bıraktığınızın dörtte biri hanımlarınızındır. Eğer çocuğunuz varsa bıraktığınızın sekizde biri onlarındır. Bu, yaptığınız vasiyetten ve borçlarınızın ödenmesinden sonradır. Kocanın veya hanımın anne, baba ve çocukları bulunmadığı takdirde miras bırakır ve kendisinin bir erkek veya bir kız kardeşi varsa her birine altıda bir düşer. Bundan daha fazla iseler mirasın üçte birine ortaktırlar. Bu paylaşma, kimse zarara uğratılmaksızın yapılacaktır. Bu, vasiyetten ve borçların ödenmesinden sonradır. Bu, Allah'tan bir vasiyettir. Allah, Her Şeyi Bilen'dir, Çok Şefkatli'dir.", transliteration: "Ve lekum nısfu ma terake ezvacukum in lem yekun lehunne veled, fe in kane lehunne veledun fe lekumur rubuu mimma terakne min ba'di vasıyyetin yusine biha ev deyn. Ve lehunner rubuu mimma teraktum in lem yekun lekum veled, fe in kane lekum veledun fe lehunnes sumunu mimma teraktum min ba'di vasıyyetin tusune biha ev deyn. Ve in kane raculun yurasu kelaleten ev imraetun ve lehu ahun ev uhtun fe li kulli vahidin min humas sudus, fe in kanu eksera min zalike fe hum şurakau fis sulusi min ba'di vasiyyetin yusa biha ev deynin gayra mudarr, vasıyyeten minallah. Vallahu alimun halim."},
  {id: 506, sure_no: 13, ayet_no: 13, sure_adi: "Nisa", text: "تِلْكَ حُدُودُ اللّٰهِۜ وَمَنْ يُطِـعِ اللّٰهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَذٰلِكَ الْفَوْزُ الْعَظ۪يمُ", translation: "İşte bunlar, Allah'ın yasalarıdır. Kim Allah'a ve Resul'üne itaat ederse, onu içinden ırmaklar akan Cennetlere koyacak, orada sürekli olarak kalacaktır. İşte büyük başarı budur.", transliteration: "Tilke hududullah. Ve men yutııllahe ve resulehu yudhılhu cennatin tecri min tahtihal enharu halidine fiha. Ve zalikel fevzul azim."},
  {id: 507, sure_no: 14, ayet_no: 14, sure_adi: "Nisa", text: "وَمَنْ يَعْصِ اللّٰهَ وَرَسُولَهُ وَيَتَعَدَّ حُدُودَهُ يُدْخِلْهُ نَاراً خَالِداً ف۪يهَاۖ وَلَهُ عَذَابٌ مُه۪ينٌ۟", translation: "Kim de Allah'a ve Resul'üne karşı asilik edip O'nun yasalarını çiğnerse, sürekli kalmak üzere ateşe konacaktır. Ve onun için alçaltıcı bir azap vardır.", transliteration: "Ve men ya'sıllahe ve resulehu ve yeteadde hududehu yudhılhu naran haliden fiha.Ve lehu azabun muhin."},
  {id: 508, sure_no: 15, ayet_no: 15, sure_adi: "Nisa", text: "وَالّٰت۪ي يَأْت۪ينَ الْفَاحِشَةَ مِنْ نِسَٓائِكُمْ فَاسْتَشْهِدُوا عَلَيْهِنَّ اَرْبَعَةً مِنْكُمْۚ فَاِنْ شَهِدُوا فَاَمْسِكُوهُنَّ فِي الْبُيُوتِ حَتّٰى يَتَوَفّٰيهُنَّ الْمَوْتُ اَوْ يَجْعَلَ اللّٰهُ لَهُنَّ سَب۪يلاً", translation: "Kadınlarınızdan fahişelik yapanlara gelince; onların fahişelik yaptıklarına dair aranızdan dört kişinin tanıklık yapması halinde; onları, ölüm alıp götürünceye veya Allah onlara bir yol gösterinceye kadar evlerinde gözetim altında tutun.", transliteration: "Vellati ye'tinel fahişete min nisaikum festeşhidu aleyhinne erbaaten minkum, fe in şehidu fe emsikuhunne fil buyuti hatta yeteveffahunnel mevtu ev yec'alallahu lehunne sebila."},
  {id: 509, sure_no: 16, ayet_no: 16, sure_adi: "Nisa", text: "وَالَّذَانِ يَأْتِيَانِهَا مِنْكُمْ فَاٰذُوهُمَاۚ فَاِنْ تَابَا وَاَصْلَحَا فَاَعْرِضُوا عَنْهُمَاۜ اِنَّ اللّٰهَ كَانَ تَـوَّاباً رَح۪يماً", translation: "Sizden onu yapan iki er kişiye de eziyet edin. Eğer tevbe eder, kendilerini düzeltirlerse onları rahat bırakın. Allah, Tevbeleri Kabul Eden'dir ve Rahmeti Kesintisiz Olan'dır.", transliteration: "Vellezani ye'tiyaniha minkum fe azuhuma, fe in taba ve asleha fe a'rıdu anhuma. İnnallahe kane tevvaben rahima."},
  {id: 510, sure_no: 17, ayet_no: 17, sure_adi: "Nisa", text: "اِنَّمَا التَّوْبَةُ عَلَى اللّٰهِ لِلَّذ۪ينَ يَعْمَلُونَ السُّٓوءَ بِجَهَالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَر۪يبٍ فَاُو۬لٰٓئِكَ يَتُوبُ اللّٰهُ عَلَيْهِمْۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً", translation: "Allah katında tevbe; cehaletle bir kötülük yapıp hemen ardından o kötülüğü terk edenlerin tevbesidir. Allah, ancak bu gibilerin tevbelerini kabul eder. Zira Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "İnnemat tevbetu alallahi lillezine ya'melunes sue bi cehaletin summe yetubune min karibin fe ulaike yetubullahu aleyhim. Ve kanallahu alimen hakima."},
  {id: 511, sure_no: 18, ayet_no: 18, sure_adi: "Nisa", text: "وَلَيْسَتِ التَّوْبَةُ لِلَّذ۪ينَ يَعْمَلُونَ السَّيِّـَٔاتِۚ حَتّٰٓى اِذَا حَضَرَ اَحَدَهُمُ الْمَوْتُ قَالَ اِنّ۪ي تُبْتُ الْـٰٔنَ وَلَا الَّذ۪ينَ يَمُوتُونَ وَهُمْ كُفَّارٌۜ اُو۬لٰٓئِكَ اَعْتَدْنَا لَهُمْ عَذَاباً اَل۪يماً", translation: "Kötülük yapıp da kendilerine ölüm gelip çatınca, \"Ben şimdi tevbe ettim.\" diyenlerin ve Kafir olarak ölenlerin tevbeleri geçersizdir. İşte onlara can yakıcı bir azap vardır.", transliteration: "Ve leysetit tevbetu lillezine ya'melunes seyyiat, hatta iza hadara ehadehumul mevtu kale inni tubtul'ane ve lallezine yemutune ve hum kuffar. Ulaike a'tedna lehum azaben elima."},
  {id: 512, sure_no: 19, ayet_no: 19, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا يَحِلُّ لَكُمْ اَنْ تَرِثُوا النِّسَٓاءَ كَرْهاًۜ  وَلَا تَعْضُلُوهُنَّ لِتَذْهَبُوا بِبَعْضِ مَٓا اٰتَيْتُمُوهُنَّ اِلَّٓا اَنْ يَأْت۪ينَ بِفَاحِشَةٍ مُبَيِّنَةٍۚ وَعَاشِرُوهُنَّ بِالْمَعْرُوفِۚ فَاِنْ كَرِهْتُمُوهُنَّ فَعَسٰٓى اَنْ تَكْرَهُوا شَيْـٔاً وَيَجْعَلَ اللّٰهُ ف۪يهِ خَيْراً كَث۪يراً", translation: "Ey İman Edenler! Kadınlara istemedikleri halde mirasçı olmanız, size helal değildir. Apaçık bir fuhuş işlemedikçe, onlara vermiş olduğunuz şeylerin bir kısmını almak için baskı yapmayın. Onlarla iyi geçinin. Şayet onlardan hoşlanmıyorsanız, bilin ki hoşlanmadığınız bir şeyde Allah birçok hayır kılmış olabilir.", transliteration: "Ya eyyuhallezine amenu la yahıllu lekum en terisun nisae kerha. Ve la ta'duluhunne li tezhebu bi ba'dı ma ateytumuhunne illa en ye'tine bi fahışetin mubeyyineh, ve aşiruhunne bil ma'ruf, fe in kerihtumuhunne fe asa en tekrahu şey'en ve yec'alallahu fihi hayran kesira."},
  {id: 513, sure_no: 20, ayet_no: 20, sure_adi: "Nisa", text: "وَاِنْ اَرَدْتُمُ اسْتِبْدَالَ زَوْجٍ مَكَانَ زَوْجٍۙ وَاٰتَيْتُمْ اِحْدٰيهُنَّ قِنْطَاراً فَلَا تَأْخُذُوا مِنْهُ شَيْـٔاًۜ اَتَأْخُذُونَهُ بُهْتَاناً وَاِثْماً مُب۪يناً", translation: "Eğer eşinizden boşanıp, başka biriyle evlenecek olursanız, boşadığınız eşinize yığınla mal vermiş olsanız bile, verdiğinizden hiçbir şeyi geri almayın. Ona verdiğinizi, iftira ederek ve apaçık günah işleyerek mi geri alacaksınız?", transliteration: "Ve in eradtumustibdale zevcin mekane zevcin, ve ateytum ihdahunne kıntaren fe la te'huzu minhu şey'a. E te'huzunehu buhtanen ve ismen mubina."},
  {id: 514, sure_no: 21, ayet_no: 21, sure_adi: "Nisa", text: "وَكَيْفَ تَأْخُذُونَهُ وَقَدْ اَفْضٰى بَعْضُكُمْ اِلٰى بَعْضٍ وَاَخَذْنَ مِنْكُمْ م۪يثَاقاً غَل۪يظاً", translation: "Hem onu nasıl geri alacaksınız ki? Birbirinizle içli dışlı olmuştunuz ve sizden kesin bir söz almışlardı.", transliteration: "Ve keyfe te'huzunehu ve kad efda ba'dukum ila ba'dın ve ehazne minkum misakan galiza."},
  {id: 515, sure_no: 22, ayet_no: 22, sure_adi: "Nisa", text: "وَلَا تَنْكِحُوا مَا نَكَحَ اٰبَٓاؤُ۬كُمْ مِنَ النِّسَٓاءِ اِلَّا مَا قَدْ سَلَفَۜ اِنَّهُ كَانَ فَاحِشَةً وَمَقْتاًۜ وَسَٓاءَ سَب۪يلاً۟", translation: "Babalarınızın daha önce evlenmiş oldukları kadınlarla evlenmeyin. Ancak geçmişte olanlar istisna. Bu utanç verici, çirkin ve kötü bir yoldur.", transliteration: "Ve la tenkihu ma nekaha abaukum minen nisai, illa ma kad selef. İnnehu kane fahışeten ve makta. Ve sae sebila."},
  {id: 516, sure_no: 23, ayet_no: 23, sure_adi: "Nisa", text: "حُرِّمَتْ عَلَيْكُمْ اُمَّهَاتُكُمْ وَبَنَاتُكُمْ وَاَخَوَاتُكُمْ وَعَمَّاتُكُمْ وَخَالَاتُكُمْ وَبَنَاتُ الْاَخِ وَبَنَاتُ الْاُخْتِ وَاُمَّهَاتُكُمُ الّٰت۪ٓي اَرْضَعْنَكُمْ وَاَخَوَاتُكُمْ مِنَ الرَّضَاعَةِ وَاُمَّهَاتُ نِسَٓائِكُمْ وَرَبَٓائِبُكُمُ الّٰت۪ي ف۪ي حُجُورِكُمْ مِنْ نِسَٓائِكُمُ الّٰت۪ي دَخَلْتُمْ بِهِنَّۘ فَاِنْ لَمْ تَكُونُوا دَخَلْتُمْ بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْۘ وَحَلَٓائِلُ اَبْنَٓائِكُمُ الَّذ۪ينَ مِنْ اَصْلَابِكُمْۙ وَاَنْ تَجْمَعُوا بَيْنَ الْاُخْتَيْنِ اِلَّا مَا قَدْ سَلَفَۜ اِنَّ اللّٰهَ كَانَ غَفُوراً رَح۪يماًۙ", translation: "Annelerinız, kızlarınız, kız kardeşleriniz, halalarınız, teyzeleriniz, erkek kardeşlerinizin kızları, kız kardeşlerinizin kızları, sizi emziren süt anneleriniz, süt kız kardeşleriniz, hanımlarınızın anneleri, kendileriyle ilişkiye girdiğiniz hanımlarınızın himayeniz altında bulunan kızları, öz oğullarınızın hanımları ve aynı anda iki kız kardeşi birlikte almanız size haram kılındı. Evlenip de ilişkide bulunmadığınız hanımlarınızın kızlarını almanızda bir sakınca yoktur. Geçmişte olan geçmişte kalmıştır. Kuşkusuz, Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Hurrimet aleykum ummehatukum ve benatukum ve ehavatukum ve ammatukum ve halatukum ve benatul ahi ve benatul uhti ve ummehatukumullati erda'nekum ve ehavatukum miner radaati ve ummehatu nisaikum ve rabaibukumullati fi hucurikum min nisaikumullati dehaltum bihinn, fe in lem tekunu dehaltum bihinne fe la cunaha aleykum, ve halailu ebnaikumullezine min aslabikum, ve en tecmeu beynel uhteyni illa ma kad selef. İnnallahe kane gafuran rahima."},
  {id: 517, sure_no: 24, ayet_no: 24, sure_adi: "Nisa", text: "وَالْمُحْصَنَاتُ مِنَ النِّسَٓاءِ اِلَّا مَا مَلَكَتْ اَيْمَانُكُمْۚ كِتَابَ اللّٰهِ عَلَيْكُمْۘ وَاُحِلَّ لَكُمْ مَا وَرَٓاءَ ذٰلِكُمْ اَنْ تَبْتَغُوا بِاَمْوَالِكُمْ مُحْصِن۪ينَ غَيْرَ مُسَافِح۪ينَۜ فَمَا اسْتَمْتَعْتُمْ بِه۪ مِنْهُنَّ فَاٰتُوهُنَّ اُجُورَهُنَّ فَر۪يضَةًۜ وَلَا جُنَاحَ عَلَيْكُمْ ف۪يمَا تَرَاضَيْتُمْ بِه۪ مِنْ بَعْدِ الْفَر۪يضَةِۜ اِنَّ اللّٰهَ كَانَ عَل۪يماً حَك۪يماً", translation: "Antlaşma ile sahip olduğunuz kimseler hariç muhsenat kadınlar Allah'ın üzerinize yasasıdır. Bunların dışında kalanlar ise; muhsin olanlar, musafihin olmayanları, mallarınızla almanız size helal kılındı. O halde, onlardan hangisiyle yararlandıysanız, ücretlerini farz kıldığınız şekilde verin. Anlaşma yaptığınız miktar üzerinde, karşılıklı olarak değişiklik yapmanızda bir sakınca yoktur. Allah, Her Şeyi Bilen'dir ve En İyi Hüküm Veren'dir.", transliteration: "Vel muhsanatu minen nisai illa ma meleket eymanukum, kitaballahi aleykum, ve uhille lekum ma varae zalikum en tebtegu bi emvalikum muhsinine gayra musafihin. Fe mastemta'tum bihi minhunne fe atuhunne ucurehunne faridah. Ve la cunaha aleykum fima teradaytum bihi min ba'dil faridah. İnnallahe kane alimen hakima."},
  {id: 518, sure_no: 25, ayet_no: 25, sure_adi: "Nisa", text: "وَمَنْ لَمْ يَسْتَطِـعْ مِنْكُمْ طَوْلاً اَنْ يَنْكِـحَ الْمُحْصَنَاتِ الْمُؤْمِنَاتِ فَمِنْ مَا مَلَكَتْ اَيْمَانُكُمْ مِنْ فَتَيَاتِكُمُ الْمُؤْمِنَاتِۜ وَاللّٰهُ اَعْلَمُ بِا۪يمَانِكُمْۜ بَعْضُكُمْ مِنْ بَعْضٍۚ فَانْكِحُوهُنَّ بِاِذْنِ اَهْلِهِنَّ وَاٰتُوهُنَّ اُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَنَاتٍ غَيْرَ مُسَافِحَاتٍ وَلَا مُتَّخِذَاتِ اَخْدَانٍۚ فَاِذَٓا اُحْصِنَّ فَاِنْ اَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنَاتِ مِنَ الْعَذَابِۜ ذٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْۜ وَاَنْ تَصْبِرُوا خَيْرٌ لَكُمْۜ  وَاللّٰهُ غَفُورٌ رَح۪يمٌ۟", translation: "Sizden kim muhsenat Mü'min kadınlarla evlenecek güce sahip değilse, yeminle sahip olduğunuz Mü'min genç kadınlarla evlensin. Allah, imanınızı en iyi bilendir. Sizler, birbirinizdensiniz. O halde iffetli, edepli, hayasızlık etmeyen ve gizli dost edinmemiş olanlarla; sorumlularının izni ve ücretlerini meşru bir şekilde vererek evlenin. Evlendikten sonra bir fuhuş yaparlarsa, onlara hür kadınlara verilen cezanın yarısı verilir. Bu içinizden günaha girme korkusu taşıyanlar içindir. Ancak sabretmeniz sizin için daha hayırlıdır. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve men lem yestetı' minkum tavlen en yenkıhal muhsanatil mu'minati fe min ma meleket eymanukum min feteyatikumul mu'minat. Vallahu a'lemu bi imanikum. Ba'dukum min ba'd, fenkihuhunne bi izni ehlihinne ve atuhunne ucurehunne bil ma'rufi muhsanatin gayra musafihatin ve la muttehızati ahdan, fe iza uhsinne fe in eteyne bi fahışetin fe aleyhinne nısfu ma alal muhsanati minel azab. Zalike li men haşiyel anete minkum. Ve en tasbiru hayrun lekum. Vallahu gafurun rahim."},
  {id: 519, sure_no: 26, ayet_no: 26, sure_adi: "Nisa", text: "يُر۪يدُ اللّٰهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذ۪ينَ مِنْ قَبْلِكُمْ وَيَتُوبَ عَلَيْكُمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Allah, size açıklamak; sizi, sizden öncekilerin yasalarına iletmek ve tevbenizi kabul etmek ister. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Yuridullahu li yubeyyine lekum ve yehdiyekum sunenellezine min kablikum ve yetube aleykum. Vallahu alimun hakim."},
  {id: 520, sure_no: 27, ayet_no: 27, sure_adi: "Nisa", text: "وَاللّٰهُ يُر۪يدُ اَنْ يَتُوبَ عَلَيْكُمْ وَيُر۪يدُ الَّذ۪ينَ يَتَّبِعُونَ الشَّهَوَاتِ اَنْ تَم۪يلُوا مَيْلاً عَظ۪يماً", translation: "Allah, tevbelerinizi kabul etmek ister. Şehvetlerine tabi olanlar ise, sizin derin bir sapkınlığa düşmenizi isterler.", transliteration: "Vallahu yuridu en yetube aleykum ve yuridullezine yettebiuneş şehevati en temilu meylen azima."},
  {id: 521, sure_no: 28, ayet_no: 28, sure_adi: "Nisa", text: "يُر۪يدُ اللّٰهُ اَنْ يُخَفِّفَ عَنْكُمْۚ وَخُلِقَ الْاِنْسَانُ ضَع۪يفاً", translation: "Allah, sorumluluğunuzu hafifletmek ister. Çünkü insan zayıf yaratılmıştır.", transliteration: "Yuridullahu en yuhaffife ankum, ve hulikal insanu daifa."},
  {id: 522, sure_no: 29, ayet_no: 29, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَأْكُلُٓوا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ اِلَّٓا اَنْ تَكُونَ تِجَارَةً عَنْ تَرَاضٍ مِنْكُمْ وَلَا تَقْتُلُٓوا اَنْفُسَكُمْۜ اِنَّ اللّٰهَ كَانَ بِكُمْ رَح۪يماً", translation: "Ey İman Edenler! Birbirinizin mallarını karşılıklı rızaya dayanan ticaret yoluyla da olsa, haksız şekilde yemeyin. Ve kendinizi öldürmeyin. Kuşkusuz, Allah, size karşı çok merhametlidir.", transliteration: "Ya eyyuhallezine amenu la te'kulu emvalekum beynekum bil batılı, illa en tekune ticaraten an teradın minkum, ve la taktulu enfusekum. İnnallahe kane bikum rahima."},
  {id: 523, sure_no: 30, ayet_no: 30, sure_adi: "Nisa", text: "وَمَنْ يَفْعَلْ ذٰلِكَ عُدْوَاناً وَظُلْماً فَسَوْفَ نُصْل۪يهِ نَاراًۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يراً", translation: "Kim, düşmanlıkla ve haksızlıkla bunu yaparsa, onu yakında ateşe atacağız. Bu, Allah için pek kolaydır.", transliteration: "Ve men yef'al zalike udvanen ve zulmen fe sevfe nuslihi nara. Ve kane zalike alallahi yesira."},
  {id: 524, sure_no: 31, ayet_no: 31, sure_adi: "Nisa", text: "اِنْ تَجْتَنِبُوا كَـبَٓائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّـَٔاتِكُمْ وَنُدْخِلْكُمْ مُدْخَلاً كَر۪يماً", translation: "Eğer siz, yasaklananların büyüklerinden sakınırsanız, kötülüklerinizi küfrederiz. Ve sizi şerefli bir meskene yerleştiririz.", transliteration: "İn tectenibu kebaira ma tunhevne anhu nukeffir ankum seyyiatikum ve nudhılkum mudhalen kerima."},
  {id: 525, sure_no: 32, ayet_no: 32, sure_adi: "Nisa", text: "وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللّٰهُ بِه۪ بَعْضَكُمْ عَلٰى بَعْضٍۜ لِلرِّجَالِ نَص۪يبٌ مِمَّا اكْتَسَبُوا وَلِلنِّسَٓاءِ نَص۪يبٌ مِمَّا اكْتَسَبْنَۜ وَسْـَٔلُوا اللّٰهَ مِنْ فَضْلِه۪ۜ اِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَل۪يماً", translation: "Allah'ın; bazınıza, bazınıza göre verdiği fazla şeyleri arzu etmeyin. Erkeklerin, kendi kazançlarından bir pay; kadınların da kendi kazançlarından bir pay vardır. Allah'tan, O'nun lütfunu isteyin. Kuşkusuz, Allah, Her Şeyi Bilen'dir.", transliteration: "Ve la tetemennev ma faddalallahu bihi ba'dakum ala ba'd. Lir ricali nasibun mimmaktesebu ve lin nisai nasibun mimmaktesebn,  ves'elullahe min fadlih innallahe kane bi kulli şey'in alima."},
  {id: 526, sure_no: 33, ayet_no: 33, sure_adi: "Nisa", text: "وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدَانِ وَالْاَقْرَبُونَۜ وَالَّذ۪ينَ عَقَدَتْ اَيْمَانُكُمْ فَاٰتُوهُمْ نَص۪يبَهُمْۜ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَه۪يداً۟", translation: "Anne, baba ve akrabaların bıraktıklarına varisler belirledik. Yeminlerinizin bağladığı kimselere paylarını verin. Kuşkusuz, Allah Her Şeye Tanık'tır.", transliteration: "Ve li kullin cealna mevaliye mimma terakel validani vel akrabun. Vellezine akadet eymanukum fe atuhum nasibehum. İnnallahe kane ala kulli şey'in şehida."},
  {id: 527, sure_no: 34, ayet_no: 34, sure_adi: "Nisa", text: "اَلرِّجَالُ قَوَّامُونَ عَلَى النِّسَٓاءِ بِمَا فَضَّلَ اللّٰهُ بَعْضَهُمْ عَلٰى بَعْضٍ وَبِمَٓا اَنْفَقُوا مِنْ اَمْوَالِهِمْۜ فَالصَّالِحَاتُ قَانِتَاتٌ حَافِظَاتٌ لِلْغَيْبِ بِمَا حَفِظَ اللّٰهُۜ وَالّٰت۪ي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَاهْجُرُوهُنَّ فِي الْمَضَاجِعِ وَاضْرِبُوهُنَّۚ فَاِنْ اَطَعْنَكُمْ فَلَا تَبْغُوا عَلَيْهِنَّ سَب۪يلاًۜ اِنَّ اللّٰهَ كَانَ عَلِياًّ كَب۪يراً", translation: "Erkekler, kadınlar üzerinde kavvamdırlar. Kendi mallarından infak etmelerinden dolayı Allah bazınızı bazınıza göre faddale yapmıştır. İyi düzeltici kadınlar; bağlılık gösteren ve Allah'ın korumasını istediğini, kocalarının bulunmadığı zamanlarda da koruyanlardır. Nuşuzundan endişe ettiğiniz kadınlara önce öğüt verin, sonra yataklarında yalnız bırakın, sonra bir süre ayrılın. Eğer size uyarlarsa onların aleyhine bir yol aramayın. Kuşkusuz Allah Çok Yüce'dir ve Çok Büyük'tür.", transliteration: "Er ricalu kavvamune alan nisai bi ma faddalallahu ba'dahum ala ba'dın ve bi ma enfeku min emvalihim. Fes salihatu kanitatun hafizatun lil gaybi bi ma hafizallah. Vellati tehafune nuşuzehunne fe ızuhunne vahcuruhunn  fil medacıı vadrıbuhunne, fe in ata'nekum fe la tebgu aleyhinne sebila. İnnallahe kane aliyyen kebira."},
  {id: 528, sure_no: 35, ayet_no: 35, sure_adi: "Nisa", text: "وَاِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوا حَكَماً مِنْ اَهْلِه۪ وَحَكَماً مِنْ اَهْلِهَاۚ اِنْ يُر۪يدَٓا اِصْلَاحاً يُوَفِّقِ اللّٰهُ بَيْنَهُمَاۜ اِنَّ اللّٰهَ كَانَ عَل۪يماً خَب۪يراً", translation: "Eğer, her ikisinin arasının bozulmasından endişe ederseniz, erkeğin ailesinden bir hakem, kadının ailesinden bir hakem belirleyin, eğer uzlaşmak isterlerse, Allah onların aralarını bulur. Kuşkusuz, Allah, Her Şeyi Bilen'dir, Her Şeyden Haberdar'dır.", transliteration: "Ve in hıftum şıkaka beynihima feb'asu hakemen min ehlihi ve hakemen min ehliha, in yurida ıslahan yuveffikıllahu beynehuma. İnnallahe kane alimen habira."},
  {id: 529, sure_no: 36, ayet_no: 36, sure_adi: "Nisa", text: "وَاعْبُدُوا اللّٰهَ وَلَا تُشْرِكُوا بِه۪ شَيْـٔاً وَبِالْوَالِدَيْنِ اِحْسَاناً وَبِذِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَالْجَارِ ذِي الْقُرْبٰى وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْبِ وَابْنِ السَّب۪يلِۙ وَمَا مَلَكَتْ اَيْمَانُكُمْۜ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ مُخْتَالاً فَخُوراًۙ", translation: "Allah'a kulluk edin. Hiçbir şeyi O'na ortak koşmayın. Anne ve babaya, yakın akrabaya, yetimlere, yoksullara, yakın komşuya, uzak komşuya, yakın arkadaşa, yol oğluna, yeminle hak sahibi olduğunuz kimselere iyilik edin. Kuşkusuz Allah, kibirli ve kendini övenleri sevmez.", transliteration: "Va'budullahe ve la tuşriku bihi şeyen ve bil valideyni ihsanen ve bizil kurba vel yetama vel mesakini vel cari zil kurba vel caril cunubi ves sahıbi bil cenbi vebnis sebili, ve ma meleket eymanukum. İnnallahe la yuhıbbu men kane muhtalen fehura."},
  {id: 530, sure_no: 37, ayet_no: 37, sure_adi: "Nisa", text: "اَلَّذ۪ينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُونَ مَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ۜ وَاَعْتَدْنَا لِلْكَافِر۪ينَ عَذَاباً مُه۪يناًۚ", translation: "Cimrilik edenler, insanları da cimri olmaya teşvik ederler; Allah'ın kendi lütfundan verdiklerini gizlerler. Biz, o kafirler için alçaltıcı bir azap hazırladık.", transliteration: "Ellezine yebhalune ve ye'murunen nase bil buhli ve yektumune ma atahumullahu min fadlıhi. Ve a'tedna lil kafirine azaben muhina."},
  {id: 531, sure_no: 38, ayet_no: 38, sure_adi: "Nisa", text: "وَالَّذ۪ينَ يُنْفِقُونَ اَمْوَالَهُمْ رِئَٓاءَ النَّاسِ وَلَا يُؤْمِنُونَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِۜ وَمَنْ يَكُنِ الشَّيْطَانُ لَهُ قَر۪يناً فَسَٓاءَ قَر۪يناً", translation: "Bunlar, mallarını insanlara gösteriş olsun diye infak eden, Allah'a ve ahiret gününe de iman etmeyen kimselerdir. Şeytan, kime arkadaşsa, o çok kötü arkadaş edinmiştir.", transliteration: "Vellezine yunfıkune emvalehum riaen nasi ve la yu'minune billahi ve la bil yevmil ahir. Ve men yekuniş şeytanu lehu karinen fe sae karina."},
  {id: 532, sure_no: 39, ayet_no: 39, sure_adi: "Nisa", text: "وَمَاذَا عَلَيْهِمْ لَوْ اٰمَنُوا بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَنْفَقُوا مِمَّا رَزَقَهُمُ اللّٰهُۜ وَكَانَ اللّٰهُ بِهِمْ عَل۪يماً", translation: "Onlara ne olurdu sanki, Allah'a ve ahiret gününe iman etselerdi; Allah'ın verdiği rızıktan infak etselerdi. Allah, onları en iyi bilendir.", transliteration: "Ve maza aleyhim lev amenu billahi vel yevmil ahıri ve enfeku mimma razakahumullah. Ve kanallahu bihim alima."},
  {id: 533, sure_no: 40, ayet_no: 40, sure_adi: "Nisa", text: "اِنَّ اللّٰهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍۚ وَاِنْ تَكُ حَسَنَةً يُضَاعِفْهَا وَيُؤْتِ مِنْ لَدُنْهُ اَجْراً عَظ۪يماً", translation: "Allah, zerre kadar haksızlık yapmaz. Yaptığınız iyiliği kat kat arttırır ve kendinden büyük bir ödül verir.", transliteration: "İnnallahe la yazlimu miskale zerreh, ve in teku haseneten yudaıfha ve yu'ti min ledunhu ecran azima."},
  {id: 534, sure_no: 41, ayet_no: 41, sure_adi: "Nisa", text: "فَكَيْفَ اِذَا جِئْنَا مِنْ كُلِّ اُمَّةٍ بِشَه۪يدٍ وَجِئْنَا بِكَ عَلٰى هٰٓؤُ۬لَٓاءِ شَه۪يداًۜ", translation: "Her ümmetten bir tanık getirdiğimiz ve seni de onların üzerine tanık yaptığımız zaman, halleri nice olacak!", transliteration: "Fe keyfe iza ci'na min kulli ummetin bi şehidin ve ci'na bike ala haulai şehida."},
  {id: 535, sure_no: 42, ayet_no: 42, sure_adi: "Nisa", text: "يَوْمَئِذٍ يَوَدُّ الَّذ۪ينَ كَفَرُوا وَعَصَوُا الرَّسُولَ لَوْ تُسَوّٰى بِهِمُ الْاَرْضُۜ وَلَا يَكْتُمُونَ اللّٰهَ حَد۪يثاً۟", translation: "Nankörlük edip Resul'e karşı çıkanlar, O Gün yerle bir olmayı isterler ve Allah'tan hiçbir sözü gizleyemezler.", transliteration: "Yevme izin yeveddullezine keferu ve asavur resule lev tusevva bihimul ard. Ve la yektumunallahe hadisa."},
  {id: 536, sure_no: 43, ayet_no: 43, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَقْرَبُوا الصَّلٰوةَ وَاَنْتُمْ سُكَارٰى حَتّٰى تَعْلَمُوا مَا تَقُولُونَ وَلَا جُنُباً اِلَّا عَابِر۪ي سَب۪يلٍ حَتّٰى تَغْتَسِلُواۜ وَاِنْ كُنْتُمْ مَرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَٓاءَ اَحَدٌ مِنْكُمْ مِنَ الْغَٓائِطِ اَوْ لٰمَسْتُمُ النِّسَٓاءَ فَلَمْ تَجِدُوا مَٓاءً فَتَيَمَّمُوا صَع۪يداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَاَيْد۪يكُمْۜ اِنَّ اللّٰهَ كَانَ عَفُواًّ غَفُوراً", translation: "Ey İman Edenler! Sarhoşken ne dediğinizi bilinceye kadar; cünupken -yolculukta olmanız hariç- yıkanıncaya kadar salata yaklaşmayın. Eğer hasta veya yolcuyken sizden biriniz tuvaletten geldiyse veya kadınlarınızla ilişkiye girdiyseniz; o anda su bulamadıysanız, temiz kumla teyemmüm edin; onunla ellerinizi ve yüzlerinizi mesh edin. Kuşkusuz Allah, Çok Affedici'dir ve Çok Bağışlayıcı'dır.", transliteration: "Ya eyyuhallezine amenu la takrabus salate ve entum sukara hatta ta'lemu ma tekulune ve la cunuben illa abiri sebilin hatta tagtesilu. Ve in kuntum marda ev ala seferin ev cae ehadun minkum minel gaiti ev lamestumun nisae fe lem tecidu maen fe teyemmemu saiden tayyiben femsehu bi vucuhikum ve eydikum. İnnallahe kane afuvven gafura."},
  {id: 537, sure_no: 44, ayet_no: 44, sure_adi: "Nisa", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ اُو۫تُوا نَص۪يباً مِنَ الْكِتَابِ يَشْتَرُونَ الضَّلَالَةَ وَيُر۪يدُونَ اَنْ تَضِلُّوا السَّب۪يلَۜ", translation: "Kendilerine Kitap'tan bir pay verilenleri görmüyor musun? Sapkınlığı satın alıyorlar. Sizi de saptırmak istiyorlar!", transliteration: "E lem tera ilallezine utu nasiben minel kitabi yeşteruned dalalete ve yuridune en tedıllus sebil."},
  {id: 538, sure_no: 45, ayet_no: 45, sure_adi: "Nisa", text: "وَاللّٰهُ اَعْلَمُ بِاَعْدَٓائِكُمْۜ وَكَفٰى بِاللّٰهِ وَلِياًّۗ وَكَفٰى بِاللّٰهِ نَص۪يراً", translation: "Allah, düşmanlarınızı daha iyi bilendir. Veli olarak Allah yeter ve yardımcı olarak Allah yeter.", transliteration: "Vallahu a'lemu bi a'daikum. Ve kefa billahi veliyyen, ve kefa billahi nasira."},
  {id: 539, sure_no: 46, ayet_no: 46, sure_adi: "Nisa", text: "مِنَ الَّذ۪ينَ هَادُوا يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِه۪ وَيَقُولُونَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَرَاعِنَا لَياًّ بِاَلْسِنَتِهِمْ وَطَعْناً فِي الدّ۪ينِۜ وَلَوْ اَنَّهُمْ قَالُوا سَمِعْنَا وَاَطَعْنَا وَاسْمَعْ وَانْظُرْنَا لَكَانَ خَيْراً لَهُمْ وَاَقْوَمَۙ وَلٰكِنْ لَعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ اِلَّا قَل۪يلاً", translation: "Yahudilerin bir kısmı, kelimelerin aslını değiştirerek: \"İşittik ve reddettik.\", \"Kulak vermeden dinleyin.\", \"Bizi güt.\" derler; dillerini eğip bükerek dinle alay ederler. Eğer onlar: \"İşittik, itaat ettik.\", \"Bizi gözet.\" deselerdi bu onlar için daha hayırlı ve daha doğru olurdu. Ancak Allah, Kafir oldukları için onları lanetlemiştir. Artık pek azı hariç iman etmezler.", transliteration: "Minellezine hadu yuharrifunel kelime an mevadııhi ve yekulune semi'na ve asayna vesma' gayra musmeın ve raına leyyen bi elsinetihim ve ta'nan fid din. Ve lev ennehum kalu semi'na ve ata'na vesma'  venzurna le kane hayran lehum ve akvem, ve lakin leanehumullahu bi kufrihim fe la yu'minune illa kalila."},
  {id: 540, sure_no: 47, ayet_no: 47, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اُو۫تُوا الْكِتَابَ اٰمِنُوا بِمَا نَزَّلْنَا مُصَدِّقاً لِمَا مَعَكُمْ مِنْ قَبْلِ اَنْ نَطْمِسَ وُجُوهاً فَنَرُدَّهَا عَلٰٓى اَدْبَارِهَٓا اَوْ نَلْعَنَهُمْ كَمَا لَعَنَّٓا اَصْحَابَ السَّبْتِۜ وَكَانَ اَمْرُ اللّٰهِ مَفْعُولاً", translation: "Ey kendilerine Kitap verilenler! Bazı yüzlerin azalarını silip, arkaları gibi dümdüz yapmadan veya Cumartesi yasağını çiğneyenleri lanetlediğimiz gibi sizi de lanetlemeden önce yanınızda bulunanı, doğrulayıcı olarak indirdiğimize iman edin. Zira Allah'ın hükmü mutlaka gerçekleşir.", transliteration: "Ya eyyuhallezine utul kitabe aminu bi ma nezzelna musaddikan li ma meakum min kabli en natmise vucuhen fe neruddeha ala edbariha ev nel'anehum kema leanna ashabes sebt. Ve kane emrullahi mef'ula."},
  {id: 541, sure_no: 48, ayet_no: 48, sure_adi: "Nisa", text: "اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُشْرَكَ بِه۪ وَيَغْفِرُ مَا دُونَ ذٰلِكَ لِمَنْ يَشَٓاءُۚ وَمَنْ يُشْرِكْ بِاللّٰهِ فَقَدِ افْتَرٰٓى اِثْماً عَظ۪يماً", translation: "Allah, kendisine şirk koşanları bağışlamaz. Bunun dışında hak edeni bağışlar. Kim Allah'a ortak koşarsa, büyük bir günahla iftirada bulunmuş olur.", transliteration: "İnnallahe la yagfiru en yuşrake bihi ve yagfiru ma dune zalike li men yeşau ve men yuşrik billahi fe kadiftera ismen azima."},
  {id: 542, sure_no: 49, ayet_no: 49, sure_adi: "Nisa", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ يُزَكُّونَ اَنْفُسَهُمْۜ بَلِ اللّٰهُ يُزَكّ۪ي مَنْ يَشَٓاءُ وَلَا يُظْلَمُونَ فَت۪يلاً", translation: "Kendilerini temize çıkaranları görmüyor musun? Hayır! Allah, hak edeni temize çıkarır. Hiç kimseye hurma çekirdeğinin lifi kadar haksızlık edilmez.", transliteration: "E lem tera ilallezine yuzekkune enfusehum. Belillahu yuzekki men yeşau ve la yuzlemune fetila."},
  {id: 543, sure_no: 50, ayet_no: 50, sure_adi: "Nisa", text: "اُنْظُرْ كَيْفَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَۜ وَكَفٰى بِه۪ٓ اِثْماً مُب۪يناً۟", translation: "Bak! Nasıl Allah adına yalan uyduruyorlar. Apaçık bir günah olarak bu yeter.", transliteration: "Unzur keyfe yefterune alallahil kezib. Ve kefa bihi ismen mubina."},
  {id: 544, sure_no: 51, ayet_no: 51, sure_adi: "Nisa", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ اُو۫تُوا نَص۪يباً مِنَ الْكِتَابِ يُؤْمِنُونَ بِالْجِبْتِ وَالطَّاغُوتِ وَيَقُولُونَ لِلَّذ۪ينَ كَفَرُوا هٰٓؤُ۬لَٓاءِ اَهْدٰى مِنَ الَّذ۪ينَ اٰمَنُوا سَب۪يلاً", translation: "Kendilerine Kitap'tan bir pay verilenleri görmüyor musun? Cipte ve Tağuta inanıyorlar ve Kafirler için: \"Bunlar, İman Edenlerden daha doğru yoldadırlar.\" diyorlar.", transliteration: "E lem tera ilallezine utu nasiben minel kitabi yu'minune bil cibti vet taguti ve yekulune lillezine keferu haulai ehda minellezine amenu sebila."},
  {id: 545, sure_no: 52, ayet_no: 52, sure_adi: "Nisa", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ لَعَنَهُمُ اللّٰهُۜ وَمَنْ يَلْعَنِ اللّٰهُ فَلَنْ تَجِدَ لَهُ نَص۪يراًۜ", translation: "Bunlar Allah'ın lanetlediği kimselerdir. Allah'ın lnetine uğramış kimse için hiçbir yardımcı bulamazsın.", transliteration: "Ulaikellezine leanehumullah. Ve men yel'anillahu fe len tecide lehu nasira."},
  {id: 546, sure_no: 53, ayet_no: 53, sure_adi: "Nisa", text: "اَمْ لَهُمْ نَص۪يبٌ مِنَ الْمُلْكِ فَاِذاً لَا يُؤْتُونَ النَّاسَ نَق۪يراًۙ", translation: "Yoksa onların mülkten bir payları mı var? Öyle olsa, insanlara hurma çekirdeğinin bir parçasını bile vermezlerdi.", transliteration: "Em lehum nasibun minel mulki fe izen la yu'tunen nase nakira."},
  {id: 547, sure_no: 54, ayet_no: 54, sure_adi: "Nisa", text: "اَمْ يَحْسُدُونَ النَّاسَ عَلٰى مَٓا اٰتٰيهُمُ اللّٰهُ مِنْ فَضْلِه۪ۚ فَقَدْ اٰتَيْنَٓا اٰلَ اِبْرٰه۪يمَ الْكِتَابَ وَالْحِكْمَةَ وَاٰتَيْنَاهُمْ مُلْكاً عَظ۪يماً", translation: "Yoksa onlar, Allah'ın kendi lütfundan insanlara bağışladığı şeyleri mi kıskanıyor? İbrahim Soyu'na da Kitap'ı ve Hikmeti bağışladık ve onlara büyük bir mülk verdik.", transliteration: "Em yahsudunen nase ala ma atahumullahu min fadlıhi, fe kad ateyna ale ibrahimel kitabe vel hikmete ve ateynahum mulken azima."},
  {id: 548, sure_no: 55, ayet_no: 55, sure_adi: "Nisa", text: "فَمِنْهُمْ مَنْ اٰمَنَ بِه۪ وَمِنْهُمْ مَنْ صَدَّ عَنْهُۜ وَكَفٰى بِجَهَنَّمَ سَع۪يراً", translation: "Onların bir kısmı O'na inandı, bir kısmı da O'ndan kaçındı. Böylelerine kızgın alevli Cehennem yeter.", transliteration: "Fe minhum men amene bihi ve minhum men sadde anhu. Ve kefa bi cehenneme saira."},
  {id: 549, sure_no: 56, ayet_no: 56, sure_adi: "Nisa", text: "اِنَّ الَّذ۪ينَ كَفَرُوا بِاٰيَاتِنَا سَوْفَ نُصْل۪يهِمْ نَاراًۜ  كُلَّمَا نَضِجَتْ جُلُودُهُمْ بَدَّلْنَاهُمْ جُلُوداً غَيْرَهَا لِيَذُوقُوا الْعَذَابَۜ اِنَّ اللّٰهَ  كَانَ عَز۪يزاً حَك۪يماً", translation: "Ayetlerimizi küfredenleri, yakında ateşe atacağız. Derileri piştikçe, azabı iyice tatsınlar diye onlara yeni deriler giydireceğiz. Kuşkusuz, Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "İnnellezine keferu bi ayatina sevfe nuslihim nara. Kullema nadicet culuduhum beddelnahum culuden gayraha li yezukul azab. İnnallahe kane azizen hakima."},
  {id: 550, sure_no: 57, ayet_no: 57, sure_adi: "Nisa", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ  لَهُمْ ف۪يهَٓا اَزْوَاجٌ مُطَهَّرَةٌۘ وَنُدْخِلُهُمْ ظِلاًّ ظَل۪يلاً", translation: "İman edip salihatı yapanları da altlarından ırmaklar akan Cennetlere koyacağız. Onlar, orada kesintisiz olarak sürekli kalıcıdırlar. Orada, kendileri için arındırılmış eşler vardır. Ve onları serin bir gölgeye yerleştireceğiz.", transliteration: "Vellezine amenu ve amilus salihati se nudhıluhum cennatin tecri min tahtihal enharu halidine fiha ebeda. Lehum fiha ezvacun mutahharatun, ve nudhıluhum zıllen zalila."},
  {id: 551, sure_no: 58, ayet_no: 58, sure_adi: "Nisa", text: "اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تُؤَدُّوا الْاَمَانَاتِ اِلٰٓى اَهْلِهَاۙ وَاِذَا حَكَمْتُمْ بَيْنَ النَّاسِ اَنْ تَحْكُمُوا بِالْعَدْلِۜ اِنَّ اللّٰهَ نِعِمَّا يَعِظُـكُمْ بِه۪ۜ اِنَّ اللّٰهَ  كَانَ سَم۪يعاً بَص۪يراً", translation: "Allah, emanetleri ehline vermenizi ve insanlar arasında hükmettiğiniz zaman adaletle hükmetmenizi buyurmaktadır. Allah, bununla size ne güzel öğüt veriyor. Kuşkusuz, Allah, Her Şeyi Duyan'dır, Her Şeyi Gören'dir.", transliteration: "İnnallahe ye'murukum en tueddul emanati ila ehliha ve iza hakemtum beynen nasi en tahkumu bil adl. İnnallahe niımma yeızukum bihi. İnnallahe kane semian basira."},
  {id: 552, sure_no: 59, ayet_no: 59, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَ وَاُو۬لِي الْاَمْرِ مِنْكُمْۚ فَاِنْ تَنَازَعْتُمْ ف۪ي شَيْءٍ فَرُدُّوهُ اِلَى اللّٰهِ وَالرَّسُولِ اِنْ كُنْتُمْ تُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ ذٰلِكَ خَيْرٌ وَاَحْسَنُ تَأْو۪يلاً۟", translation: "Ey İman Edenler! Allah'a itaat edin; Resul'e itaat edin ve sizden olan ulu'l-emre itaat edin. Herhangi bir konuda anlaşmazlığa düşerseniz onu Allah'a ve Resul'e götürün; eğer Allah'a ve ahiret gününe inanıyorsanız. Hayırlı olan budur. Ve sonuç bakımından iyi olandır.", transliteration: "Ya eyyuhallezine amenu atiullahe ve atiur resule ve ulil emri minkum, fe in tenaza'tum fi şey'in fe rudduhu ilallahi ver resuli in kuntum tu'minune billahi vel yevmil ahir. Zalike hayrun ve ahsenu te'vila."},
  {id: 553, sure_no: 60, ayet_no: 60, sure_adi: "Nisa", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ يَزْعُمُونَ اَنَّهُمْ اٰمَنُوا بِمَٓا اُنْزِلَ اِلَيْكَ وَمَٓا اُنْزِلَ مِنْ قَبْلِكَ يُر۪يدُونَ اَنْ يَتَحَاكَمُٓوا اِلَى الطَّاغُوتِ وَقَدْ اُمِرُٓوا اَنْ يَكْفُرُوا بِه۪ۜ وَيُر۪يدُ الشَّيْطَانُ اَنْ يُضِلَّهُمْ ضَلَالاً بَع۪يداً", translation: "Görüyor musun? Sana indirilene ve senden önce indirilene iman ettiklerini ileri sürenleri! Tağuti yasalarla yargılanmak istiyorlar. Oysa onlara, onu küfretmeleri emredilmişti. Zaten şeytan onları derin bir sapkınlıkla saptırmak istiyor.", transliteration: "E lem tera ilallezine yez'umune ennehum amenu bima unzile ileyke ve ma unzile min kablike yuridune en yetehakemu ilat taguti ve kad umiru en yekfuru bihi. Ve yuriduş şeytanu en yudıllehum dalalen baida."},
  {id: 554, sure_no: 61, ayet_no: 61, sure_adi: "Nisa", text: "وَاِذَا ق۪يلَ لَهُمْ تَعَالَوْا اِلٰى مَٓا اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُولِ رَاَيْتَ الْمُنَافِق۪ينَ يَصُدُّونَ عَنْكَ صُدُوداًۚ", translation: "Onlardan, ne zaman Allah'ın indirdiğine ve Resul'üne gelmeleri istense, o münafıkların, senden tam bir uzaklaşma ile uzaklaştıklarını görürsün.", transliteration: "Ve iza kile lehum tealev ila ma enzelallahu ve ilar resuli raeytel munafıkine yesuddune anke sududa."},
  {id: 555, sure_no: 62, ayet_no: 62, sure_adi: "Nisa", text: "فَكَيْفَ اِذَٓا اَصَابَتْهُمْ مُص۪يبَةٌ بِمَا قَدَّمَتْ اَيْد۪يهِمْ ثُمَّ جَٓاؤُ۫كَ يَحْلِفُونَ بِاللّٰهِ اِنْ اَرَدْنَٓا اِلَّٓا اِحْسَاناً وَتَوْف۪يقاً", translation: "Kendi elleriyle yaptıklarından dolayı, onlara bir musibet isabet edince, sana gelerek: \"Biz yalnızca iyilik etmek ve arayı bulmaktan başka bir şey istemedik.\" diye nasıl da Allah'a yemin ediyorlar.", transliteration: "Ve keyfe iza esabethum musibetun bima kaddemet eydihim summe cauke yahlıfune billahi in eradna illa ihsanen ve tevfika."},
  {id: 556, sure_no: 63, ayet_no: 63, sure_adi: "Nisa", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ يَعْلَمُ اللّٰهُ مَا ف۪ي قُلُوبِهِمْ فَاَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَهُمْ ف۪ٓي اَنْفُسِهِمْ قَوْلاً بَل۪يغاً", translation: "Allah, bu kimselerin kalplerinde olanı biliyor. Onlara aldırma, onlara öğüt ver ve onlara kendilerini etkileyici söz söyle.", transliteration: "Ulaikellezine ya'lemullahu ma fi kulubihim fe a'rıd anhum vaızhum ve kul lehum fi enfusihim kavlen beliga."},
  {id: 557, sure_no: 64, ayet_no: 64, sure_adi: "Nisa", text: "وَمَٓا اَرْسَلْنَا مِنْ رَسُولٍ اِلَّا لِيُطَاعَ بِاِذْنِ اللّٰهِۜ وَلَوْ اَنَّهُمْ اِذْ ظَلَمُٓوا اَنْفُسَهُمْ جَٓاؤُ۫كَ فَاسْتَغْفَرُوا اللّٰهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُولُ لَوَجَدُوا اللّٰهَ تَـوَّاباً رَح۪يماً", translation: "Biz, hiçbir Resul'ü Allah'ın izni ile yalnızca kendisine itaat edilmesinden başka bir amaçla göndermedik. Eğer onlar, kendi kendilerine haksızlık yaptıklarında, sana gelip, Allah'tan bağışlanmalarını dileselerdi ve sen de Resul olarak onların bağışlanmasını dileseydin; Allah'ın tevbeleri kabul edici ve çok merhamet edici olduğunu göreceklerdi.", transliteration: "Ve ma erselna min resulin illa li yutaa bi iznillah. Ve lev ennehum iz zalemu enfusehum cauke festagferullahe vestagfera lehumur resulu le vecedullahe tevvaben rahima."},
  {id: 558, sure_no: 65, ayet_no: 65, sure_adi: "Nisa", text: "فَلَا وَرَبِّكَ لَا يُؤْمِنُونَ حَتّٰى يُحَكِّمُوكَ ف۪يمَا شَجَرَ بَيْنَهُمْۙ ثُمَّ لَا يَجِدُوا ف۪ٓي اَنْفُسِهِمْ حَرَجاً مِمَّا قَضَيْتَ وَيُسَلِّمُوا تَسْل۪يماً", translation: "Hayır! Rabb'ine ant olsun ki, aralarında anlaşmazlığa düştükleri işlerde seni hakem tayin edip, sonra da verdiğin hükme tam bir teslimiyetle teslim olmadıkça iman etmiş olmazlar.", transliteration: "Fe la ve rabbike la yu'minune hatta yuhakkimuke fima şecera beynehum, summe la yecidu fi enfusihim haracen mimma kadayte ve yusellimu teslima."},
  {id: 559, sure_no: 66, ayet_no: 66, sure_adi: "Nisa", text: "وَلَوْ اَنَّا كَتَبْنَا عَلَيْهِمْ اَنِ اقْتُلُٓوا اَنْفُسَكُمْ اَوِ اخْرُجُوا مِنْ دِيَارِكُمْ مَا فَعَلُوهُ اِلَّا قَل۪يلٌ مِنْهُمْۜ وَلَوْ اَنَّهُمْ فَعَلُوا مَا يُوعَظُونَ بِه۪ لَكَانَ خَيْراً لَهُمْ وَاَشَدَّ تَثْب۪يتاًۙ", translation: "Eğer biz onlara: \"Kendinizi öldürün veya yurtlarınızdan çıkın\" diye yazsaydık, çok azı hariç bunu yapmazlardı. Eğer kendilerine verilen öğüde uysalardı, elbette bu onlar için hem daha hayırlı hem kalıcı olarak daha sağlam olurdu.", transliteration: "Ve lev enna ketebna aleyhim enıktulu enfusekum evihrucu min diyarikum ma fealuhu illa kalilun minhum. Ve lev ennehum fealu ma yuazune bihi le kane hayran lehum ve eşedde tesbita."},
  {id: 560, sure_no: 67, ayet_no: 67, sure_adi: "Nisa", text: "وَاِذاً لَاٰتَيْنَاهُمْ مِنْ لَدُنَّٓا اَجْراً عَظ۪يـماًۙ", translation: "O zaman onlara Kendi katımızdan büyük bir ecir verirdik.", transliteration: "Ve izen le ateynahum min ledunna ecran azima."},
  {id: 561, sure_no: 68, ayet_no: 68, sure_adi: "Nisa", text: "وَلَهَدَيْنَاهُمْ صِرَاطاً مُسْتَق۪يماً", translation: "Onları dosdoğru bir yola iletirdik.", transliteration: "Ve le hedeynahum sıratan mustekima."},
  {id: 562, sure_no: 69, ayet_no: 69, sure_adi: "Nisa", text: "وَمَنْ يُطِـعِ اللّٰهَ وَالرَّسُولَ فَاُو۬لٰٓئِكَ مَعَ الَّذ۪ينَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِنَ النَّبِيّ۪نَ وَالصِّدّ۪يق۪ينَ وَالشُّهَدَٓاءِ وَالصَّالِح۪ينَۚ وَحَسُنَ اُو۬لٰٓئِكَ رَف۪يقاًۜ", translation: "Kim Allah'a ve Resul'e itaat ederse, işte onlar, Allah'ın kendilerine nimet verdiği, Nebiler, sıddıklar, şahitler ve salihlerle beraberdirler. Onlar ne iyi arkadaştırlar!", transliteration: "Ve men yutiıllahe ver resule fe ulaike meallezine en'amellahu aleyhim minen nebiyyine ves sıddikine veş şuhedai ves salihin, ve hasune ulaike rafika."},
  {id: 563, sure_no: 70, ayet_no: 70, sure_adi: "Nisa", text: "ذٰلِكَ الْفَضْلُ مِنَ اللّٰهِۜ وَكَفٰى بِاللّٰهِ عَل۪يماً۟", translation: "Bu, Allah'tan bir bağıştır. Her şeyi bilen olarak Allah yeter.", transliteration: "Zalikel fadlu minallah. Ve kefa billahi alima."},
  {id: 564, sure_no: 71, ayet_no: 71, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا خُذُوا حِذْرَكُمْ فَانْفِرُوا ثُبَاتٍ اَوِ انْفِرُوا جَم۪يعاً", translation: "Ey İman Edenler! Önleminizi alın. Savaşa, küçük birlikler halinde veya topyekun olarak çıkın.", transliteration: "Ya eyyuhallezine amenu huzu hızrakum fenfiru subatin evinfiru cemia."},
  {id: 565, sure_no: 72, ayet_no: 72, sure_adi: "Nisa", text: "وَاِنَّ مِنْكُمْ لَمَنْ لَيُبَطِّئَنَّۚ فَاِنْ اَصَابَتْكُمْ مُص۪يبَةٌ قَالَ قَدْ اَنْعَمَ اللّٰهُ عَلَيَّ اِذْ لَمْ اَكُنْ مَعَهُمْ شَه۪يداً", translation: "İçinizden ağır davranan bazı kimseler, size bir musibet isabet ederse, \"Allah bana nimet verdi de onlarla beraber bulunmadım.\" der.", transliteration: "Ve inne minkum le men le yubattienn, fe in esabetkum musibetun kale kad en'amallahu aleyye iz lem ekun meahum şehida."},
  {id: 566, sure_no: 73, ayet_no: 73, sure_adi: "Nisa", text: "وَلَئِنْ اَصَابَكُمْ فَضْلٌ مِنَ اللّٰهِ لَيَقُولَنَّ كَاَنْ لَمْ تَكُنْ بَيْنَكُمْ وَبَيْنَهُ مَوَدَّةٌ يَا لَيْتَن۪ي كُنْتُ مَعَهُمْ فَاَفُوزَ فَوْزاً عَظ۪يماً", translation: "Eğer size Allah'tan bir lütuf erişse, bu sefer de sanki sizinle onun arasında bir bağlılık/yakınlık yokmuş gibi: \"Keşke ben de onlarla beraber olsaydım da büyük bir başarı elde etseydim.\" der.", transliteration: "Ve le in esabekum fadlun minallahi le yekulenne ke en lem tekun beynekum ve beynehu meveddetun ya leyteni kuntu meahum fe efuze fevzen azima."},
  {id: 567, sure_no: 74, ayet_no: 74, sure_adi: "Nisa", text: "فَلْيُقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِ الَّذ۪ينَ يَشْرُونَ الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِۜ وَمَنْ يُقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِ فَيُقْتَلْ اَوْ يَغْلِبْ فَسَوْفَ نُؤْت۪يهِ اَجْراً عَظ۪يماً", translation: "Öyleyse, ahiret hayatını dünya hayatına tercih edenler, Allah yolunda savaşsınlar. Kim, Allah yolunda savaşır da öldürülür veya galip gelirse, Biz, ona ileride büyük bir karşılık vereceğiz.", transliteration: "Felyukatil fi sebilillahillezine yeşrunel hayated dunya bil ahirah. Ve men yukatil fi sebilillahi fe yuktel ev yaglib fe sevfe nu'tihi ecran azima."},
  {id: 568, sure_no: 75, ayet_no: 75, sure_adi: "Nisa", text: "وَمَا لَكُمْ لَا تُقَاتِلُونَ ف۪ي سَب۪يلِ اللّٰهِ وَالْمُسْتَضْعَف۪ينَ مِنَ الرِّجَالِ وَالنِّسَٓاءِ وَالْوِلْدَانِ الَّذ۪ينَ يَقُولُونَ رَبَّنَٓا اَخْرِجْنَا مِنْ هٰذِهِ الْقَرْيَةِ الظَّالِمِ اَهْلُهَاۚ وَاجْعَلْ لَنَا مِنْ لَدُنْكَ وَلِياًّۚ وَاجْعَلْ لَنَا مِنْ لَدُنْكَ نَص۪يراًۜ", translation: "Size ne oluyor da Allah yolunda ve: \"Ey Rabb'imiz! Bizi halkı zalim olan bu beldeden çıkar, katından bize bir veli ver, bize katından yardım edecek kimseler ver.\" diyen mustaz'af erkekler, kadınlar ve çocuklar için savaşmıyorsunuz?", transliteration: "Ve ma lekum la tukatilune fi sebilillahi vel mustad'afine miner ricali ven nisai vel vildanillezine yekulune rabbena ahricna min hazihil karyetiz zalimi ehluha, vec'al lena min ledunke veliyya, vec'al lena min ledunke nasira."},
  {id: 569, sure_no: 76, ayet_no: 76, sure_adi: "Nisa", text: "اَلَّذ۪ينَ اٰمَنُوا يُقَاتِلُونَ ف۪ي سَب۪يلِ اللّٰهِۚ وَالَّذ۪ينَ كَفَرُوا يُقَاتِلُونَ ف۪ي سَب۪يلِ الطَّاغُوتِ فَقَاتِلُٓوا اَوْلِيَٓاءَ الشَّيْطَانِۚ اِنَّ  كَيْدَ الشَّيْطَانِ كَانَ ضَع۪يفاً۟", translation: "İman Edenler Allah yolunda savaşırlar, Kafirler de tağutun yolunda savaşırlar. O halde şeytanı evliya edinenlerle savaşın. Kuşkusuz, şeytanın hilesi/düzeni zayıftır.", transliteration: "Ellezine amenu yukatilune fi sebilillah, vellezine keferu yukatilune fi sebilit taguti fe katilu evliyaeş şeytan, inne keydeş şeytani kane daifa."},
  {id: 570, sure_no: 77, ayet_no: 77, sure_adi: "Nisa", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ ق۪يلَ لَهُمْ كُفُّٓوا اَيْدِيَكُمْ وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۚ  فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ اِذَا فَر۪يقٌ مِنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللّٰهِ اَوْ اَشَدَّ خَشْيَةًۚ وَقَالُوا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَۚ  لَوْلَٓا اَخَّرْتَنَٓا اِلٰٓى اَجَلٍ قَر۪يبٍۜ  قُلْ مَتَاعُ الدُّنْيَا قَل۪يلٌۚ وَالْاٰخِرَةُ خَيْرٌ لِمَنِ اتَّقٰى وَلَا تُظْلَمُونَ فَت۪يلاً", translation: "Kendilerine, ellerinizi çekin, salatı ikame edin, zekatı yapın denilen kimseleri görmedin mi? Üzerlerine savaş yazılınca, içlerinden bir kısmı Allah'ın haşyeti gibi, hatta daha fazla insanlara haşyet duyarlar. Ve \"Ey Rabb'imiz! Neden üzerimize savaş yazdın, bizi yakın bir zamana kadar erteleseydin ya?\" dediler. De ki: \"Dünya geçimliği önemsizdir. Ahiret, takva sahibi kimseler için daha hayırlıdır. Ve hurma çekirdeğinin içindeki lif kadar size haksızlık yapılmaz.\"", transliteration: "E lem tera ilallezine kile lehum kuffu eydiyekum, ve ekimus salate ve atuz zekat, fe lemma kutibe aleyhimul kıtalu iza ferikun minhum yahşevnen nase ke haşyetillahi ev eşedde haşyeh, ve kalu rabbena lime ketebte aleynal kıtal, lev la ahhartena ila ecelin karib. Kul metaud dunya kalil, vel ahıratu hayrun li menitteka ve la tuzlemune fetila."},
  {id: 571, sure_no: 78, ayet_no: 78, sure_adi: "Nisa", text: "اَيْنَ مَا تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَلَوْ كُنْتُمْ ف۪ي بُرُوجٍ مُشَيَّدَةٍۜ وَاِنْ تُصِبْهُمْ حَسَنَةٌ يَقُولُوا هٰذِه۪ مِنْ عِنْدِ اللّٰهِۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَقُولُوا هٰذِه۪ مِنْ عِنْدِكَۜ قُلْ كُلٌّ مِنْ عِنْدِ اللّٰهِۜ فَمَالِ هٰٓؤُ۬لَٓاءِ الْقَوْمِ لَا يَكَادُونَ يَفْقَهُونَ حَد۪يثاً", translation: "Nerede olursanız olun, sağlam kalelerde de olsanız, ölüm gelir sizi bulur. Onlara, bir iyilik isabet etse, \"Bu Allah'tandır.\" derler. Onlara, bir kötülük isabet etse, \"Bu senin yüzündendir.\" derler. De ki: \"Hepsi Allah'tandır.\" Bu halka ne oluyor ki söylenen sözü anlamaya yanaşmıyorlar!", transliteration: "Eyne ma tekunu yudrikkumul mevtu ve lev kuntum fi burucin muşeyyedeh. Ve in tusıbhum hasenetun yekulu hazihi min indillah, ve in tusıbhum seyyietun yekulu hazihi min indike. Kul kullun min indillah. Fe mali haulail kavmi la yekadune yefkahune hadisa."},
  {id: 572, sure_no: 79, ayet_no: 79, sure_adi: "Nisa", text: "مَٓا اَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللّٰهِۘ وَمَٓا اَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَفْسِكَۜ وَاَرْسَلْنَاكَ لِلنَّاسِ رَسُولاًۜ  وَكَفٰى بِاللّٰهِ شَه۪يداً", translation: "Sana isabet eden iyilik Allah'tandır. Sana isabet eden kötülük kendindendir.\" Biz, seni, insanlara Resul olarak gönderdik. Tanık olarak Allah yeter.", transliteration: "Ma esabeke min hasenetin fe minallah, ve ma esabeke min seyyietin fe min nefsike. Ve erselnake lin nasi resula. Ve kefa billahi şehida."},
  {id: 573, sure_no: 80, ayet_no: 80, sure_adi: "Nisa", text: "مَنْ يُطِـعِ الرَّسُولَ فَقَدْ اَطَاعَ اللّٰهَۚ وَمَنْ تَوَلّٰى فَمَٓا اَرْسَلْنَاكَ عَلَيْهِمْ حَف۪يظاًۜ", translation: "Kim Resul'e itaat ederse, Allah'a itaat etmiş olur. Kim de yüz çevirirse çevirsin; biz seni onlara bekçi olarak göndermedik.", transliteration: "Men yutiır resule fe kad ataallah, ve men tevella fe ma erselnake aleyhim hafiza."},
  {id: 574, sure_no: 81, ayet_no: 81, sure_adi: "Nisa", text: "وَيَقُولُونَ طَاعَةٌۘ فَاِذَا بَرَزُوا مِنْ عِنْدِكَ بَيَّتَ طَٓائِفَةٌ مِنْهُمْ غَيْرَ الَّذ۪ي تَقُولُۜ وَاللّٰهُ يَكْتُبُ مَا يُبَيِّتُونَۚ فَاَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللّٰهِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً", translation: "Sana, itaat ettiklerini söylüyorlar. Senin yanından ayrılıp, yalnız kaldıkları zaman, onlardan bir grup, arkandan, senin yanında söylediklerinden farklı şeyler tasarlıyorlar. Allah, onların, arkandan gizlice tasarladıkları şeylerin hepsini kaydediyor. Onlara aldırma, yalnız Allah'a dayan, vekil olarak Allah sana yeter.", transliteration: "Ve yekulune taatun, fe iza berazu min indike beyyete taifetun minhum gayrallezi tekul. Vallahu yektubu ma yubeyyitun, fe a'rıd anhum ve tevekkel alallah. Ve kefa billahi vekila."},
  {id: 575, sure_no: 82, ayet_no: 82, sure_adi: "Nisa", text: "اَفَلَا يَتَدَبَّرُونَ الْقُرْاٰنَۜ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللّٰهِ لَوَجَدُوا ف۪يهِ اخْتِلَافاً كَث۪يراً", translation: "Onlar, Kur'an üzerinde, gereği gibi düşünmezler mi? Eğer, Allah'tan başkası tarafından gönderilmiş olsaydı, onda birçok çelişki bulurlardı.", transliteration: "E fe la yetedebberunel kur'an.Ve lev kane min indi gayrillahi le vecedu fihihtilafen kesira."},
  {id: 576, sure_no: 83, ayet_no: 83, sure_adi: "Nisa", text: "وَاِذَا جَٓاءَهُمْ اَمْرٌ مِنَ الْاَمْنِ اَوِ الْخَوْفِ اَذَاعُوا بِه۪ۜ وَلَوْ رَدُّوهُ اِلَى الرَّسُولِ وَاِلٰٓى اُو۬لِي الْاَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذ۪ينَ يَسْتَنْبِطُونَهُ مِنْهُمْۜ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ لَاتَّبَعْتُمُ الشَّيْطَانَ اِلَّا قَل۪يلاً", translation: "Onlara, güven veya korkuyla ilgili bir haber geldiği zaman, onu hemen yayarlar. Oysaki onu Resul'e ve kendilerinden olan ulu'l-emre bildirselerdi; işin iç yüzünü bilenler, ne olup bittiğini, bilirlerdi. Eğer Allah'ın lütfu ve rahmeti üzerinizde olmasaydı, pek azınız hariç hepiniz şeytana uyardınız.", transliteration: "Ve iza caehum emrun minel emni evil havfi ezau bihi.Ve lev redduhu ilar resuli ve ila ulil emri minhum le alimehullezine yestenbitunehu minhum. Ve lev la fadlullahi aleykum ve rahmetuhu letteba'tumuş şeytane illa kalila."},
  {id: 577, sure_no: 84, ayet_no: 84, sure_adi: "Nisa", text: "فَقَاتِلْ ف۪ي سَب۪يلِ اللّٰهِۚ لَا تُكَلَّفُ اِلَّا نَفْسَكَ وَحَرِّضِ الْمُؤْمِن۪ينَۚ عَسَى اللّٰهُ اَنْ يَكُفَّ بَأْسَ الَّذ۪ينَ كَفَرُواۜ وَاللّٰهُ اَشَدُّ بَأْساً وَاَشَدُّ تَنْك۪يلاً", translation: "O halde, sen, Allah yolunda savaş. Çünkü sen, ancak kendinden sorumlusun. İman Edenleri de teşvik et. Umulur ki Allah Kafirlerin baskısını kırar. Çünkü Allah'ın baskısı daha güçlüdür. Cezalandırması daha şiddetlidir.", transliteration: "Fe katil fi sebilillah, la tukellefu illa nefseke ve harrıdıl mu'minin, asallahu en yekuffe be'sellezine keferu. Vallahu eşeddu be'sen ve eşeddu tenkila."},
  {id: 578, sure_no: 85, ayet_no: 85, sure_adi: "Nisa", text: "مَنْ يَشْفَعْ شَفَاعَةً حَسَنَةً يَكُنْ لَهُ نَص۪يبٌ مِنْهَاۚ وَمَنْ يَشْفَعْ شَفَاعَةً سَيِّئَةً يَكُنْ لَهُ كِفْلٌ مِنْهَاۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُق۪يتاً", translation: "Her kim, iyi bir işe şefaat ederse, ona o işten bir pay vardır. Her kim de kötü bir işe şefaat ederse, ondan da ona bir pay vardır. Allah Her Şeyi Gözeten'dir.", transliteration: "Men yeşfa' şefaaten haseneten yekun lehu nasibun minha, ve men yeşfa' şefaaten seyyieten yekun lehu kiflun minha. Ve kanallahu ala kulli şey'in mukita."},
  {id: 579, sure_no: 86, ayet_no: 86, sure_adi: "Nisa", text: "وَاِذَا حُيّ۪يتُمْ بِتَحِيَّةٍ فَحَيُّوا بِاَحْسَنَ مِنْهَٓا اَوْ رُدُّوهَاۜ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ حَس۪يباً", translation: "Size, selam verildiği zaman, ondan daha iyisiyle veya aynısıyla karşılık verin. Kuşkusuz ki Allah, Her Şeyi Hesaplayan'dır.", transliteration: "Ve iza huyyitum bi tahıyyetin fe hayyu bi ahsene minha ev rudduha. İnnallahe kane ala kulli şey'in hasiba."},
  {id: 580, sure_no: 87, ayet_no: 87, sure_adi: "Nisa", text: "اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۜ  لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ ف۪يهِۜ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ حَد۪يثاً۟", translation: "Kendisinden başka ilah olmayan Allah, gerçekleşeceği kesin olan Kıyamet Günü'nde, sizi kesin olarak toplayacaktır. Allah'tan daha doğru sözlü kim olabilir?", transliteration: "Allahu la ilahe illa huve. Le yecmeannekum ila yevmil kıyameti la raybe fihi. Ve men asdeku minallahi hadisa."},
  {id: 581, sure_no: 88, ayet_no: 88, sure_adi: "Nisa", text: "فَمَا لَكُمْ فِي الْمُنَافِق۪ينَ فِئَتَيْنِ وَاللّٰهُ اَرْكَسَهُمْ بِمَا كَسَبُواۜ اَتُر۪يدُونَ اَنْ تَهْدُوا مَنْ اَضَلَّ اللّٰهُۜ وَمَنْ يُضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهُ سَب۪يلاً", translation: "Size ne oluyor ki; yaptıklarından dolayı, Allah onları ters yüz ettiği halde, münafıklar hakkında iki gruba ayrıldınız! Allah'ın saptırdığı kimseyi, doğru yola erdirmek mi istiyorsunuz? Allah'ın saptırdığı kimse için asla bir çıkış yolu bulamazsın.", transliteration: "Fe ma lekum fil munafikine fieteyni vallahu erkesehum bi ma kesebu. E turidune en tehdu men edallallah. Ve men yudlilillahu fe len tecide lehu sebila."},
  {id: 582, sure_no: 89, ayet_no: 89, sure_adi: "Nisa", text: "وَدُّوا لَوْ تَكْفُرُونَ كَمَا كَفَرُوا فَتَكُونُونَ سَوَٓاءً فَلَا تَتَّخِذُوا مِنْهُمْ اَوْلِيَٓاءَ حَتّٰى يُهَاجِرُوا ف۪ي سَب۪يلِ اللّٰهِۜ فَاِنْ تَوَلَّوْا فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ وَجَدْتُمُوهُمْۖ وَلَا تَتَّخِذُوا مِنْهُمْ وَلِياًّ وَلَا نَص۪يراًۙ", translation: "Onlar, sizin kendileri gibi küfre dönmenizi isterler, ki onlar gibi olasınız. O halde, Allah yolunda hicret edinceye kadar onları evliya edinmeyin. Eğer yüz çevirirlerse onları yakalayın, bulduğunuz yerde öldürün. Onlardan hiç kimseyi veli de yardımcı da edinmeyin.", transliteration: "Veddu lev tekfurune kema keferu fe tekunune sevaen fe la tettehızu minhum evliyae hatta yuhaciru fi sebilillah. Fe in tevellev fe huzuhum vaktuluhum haysu vecedtumuhum, ve la tettehızu minhum veliyyen ve la nasira."},
  {id: 583, sure_no: 90, ayet_no: 90, sure_adi: "Nisa", text: "اِلَّا الَّذ۪ينَ يَصِلُونَ اِلٰى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ م۪يثَاقٌ اَوْ جَٓاؤُ۫كُمْ حَصِرَتْ صُدُورُهُمْ اَنْ يُقَاتِلُوكُمْ اَوْ يُقَاتِلُوا قَوْمَهُمْۜ وَلَوْ شَٓاءَ اللّٰهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوكُمْۚ فَاِنِ اعْتَزَلُوكُمْ فَلَمْ يُقَاتِلُوكُمْ وَاَلْقَوْا اِلَيْكُمُ السَّلَمَۙ فَمَا جَعَلَ اللّٰهُ لَكُمْ عَلَيْهِمْ سَب۪يلاً", translation: "Ancak, aranızda antlaşma olan halka sığınanlar veya ne sizinle ne de kendi halkıyla savaşmayı içine sindiremeyip size gelenler hariç. Eğer Allah dileseydi, onları başınıza musallat ederdi de sizinle savaşırlardı. Eğer tarafsız kalarak ve sizinle savaşmayıp barış isterlerse, Allah onların aleyhinde size bir yol vermemiştir.", transliteration: "İllallezine yasılune ila kavmin beynekum ve beynehum misakun ev caukum hasırat suduruhum en yukatilukum ev yukatilu kavmehum. Ve lev şaallahu le selletahum aleykum fe le katelukum, fe inı'tezelukum fe lem yukatilukum ve elkav ileykumus seleme, fe ma cealallahu lekum aleyhim sebila."},
  {id: 584, sure_no: 91, ayet_no: 91, sure_adi: "Nisa", text: "سَتَجِدُونَ اٰخَر۪ينَ يُر۪يدُونَ اَنْ يَأْمَنُوكُمْ وَيَأْمَنُوا قَوْمَهُمْۜ كُلَّمَا رُدُّٓوا اِلَى الْفِتْنَةِ اُرْكِسُوا ف۪يهَاۚ فَاِنْ لَمْ يَعْتَزِلُوكُمْ وَيُلْقُٓوا اِلَيْكُمُ السَّلَمَ وَيَكُفُّٓوا اَيْدِيَهُمْ فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْۜ وَاُو۬لٰٓئِكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطَاناً مُب۪يناً۟", translation: "Başkalarını da bulacaksınız hem sizden hem de kendi halklarından güvende olmak isteyen. Fitne ortamı bulduklarında, hemen onun içine baş aşağı dalarlar. Eğer bunlar, sizden uzak durmazlar, sizinle barış yapmaya yanaşmazlarsa, sizden ellerini çekmezlerse onları yakaladığınız yerde, her nerede bulursanız öldürün. İşte bu kimseler hakkında size apaçık bir yetki verdik.", transliteration: "Se tecidune aharine yuridune en ye'menukum ve ye'menu kavmehum. Kullema ruddu ilal fitneti urkisu fiha, fe in lem ya'tezilukum ve yulku ileykumus seleme ve yekuffu eydiyehum fe huzuhum vaktuluhum haysu sekıftumuhum. Ve ulaikum cealna lekum aleyhim sultanen mubina."},
  {id: 585, sure_no: 92, ayet_no: 92, sure_adi: "Nisa", text: "وَمَا كَانَ لِمُؤْمِنٍ اَنْ يَقْتُلَ مُؤْمِناً اِلَّا خَطَـٔاًۚ وَمَنْ قَتَلَ مُـؤْمِناً خَطَــٔاً فَـتَـحْر۪يرُ رَقَبَةٍ مُؤْمِنَةٍ وَدِيَةٌ مُسَلَّمَةٌ اِلٰٓى اَهْلِه۪ٓ اِلَّٓا اَنْ يَصَّدَّقُواۜ فَاِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَكُمْ وَهُوَ مُؤْمِنٌ فَتَحْر۪يرُ رَقَبَةٍ مُؤْمِنَةٍۜ وَاِنْ كَانَ مِنْ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ م۪يثَاقٌ فَدِيَةٌ مُسَلَّمَةٌ اِلٰٓى اَهْلِه۪ وَتَحْر۪يرُ رَقَبَةٍ مُؤْمِنَةٍۚ فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِۘ تَوْبَةً مِنَ اللّٰهِۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً", translation: "Hata ile olması dışında, bir Mü'min'in bir Mü'min'i öldürmesi olacak şey değildir. Kim, hata ile bir Mü'min'i öldürürse, Mü'min bir rekabeyi özgürlüğüne kavuştursun, ailesi bağışlamadığı takdirde, ölenin ailesine diyet ödesin. Eğer, öldürülen Mü'min; düşmanınız olan bir halka mensupsa, Mü'min bir rekabeyi özgürlüğüne kavuştursun. Eğer, aranızda anlaşma bulunan bir halktansa, ailesine diyet vermek ve Mü'min bir rekabeyi özgürlüğüne kavuşturmak gerekir. Kim bunları bulamazsa, Allah'tan tevbesini kabul etmesi için ardı ardına iki ay siyam yapmalıdır. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve ma kane li mu'minin en yaktule mu'minen illa hataa, ve men katele mu'minen hataen fe tahriru rakabetin mu'minetin ve diyetun musellemetun ila ehlihi illa en yessaddaku. Fe in kane min kavmin aduvvin lekum ve huve mu'minun fe tahriru rakabetin mu'mineh. Ve in kane min kavmin beynekum ve beynehum misakun fe diyetun musellemetun ila ehlihi ve tahriru rakabetin mu'mineh, fe men lem yecid fe sıyamu şehreyni mutetabiayni tevbeten minallah. Ve kanallahu alimen hakima."},
  {id: 586, sure_no: 93, ayet_no: 93, sure_adi: "Nisa", text: "وَمَنْ يَقْتُلْ مُؤْمِناً مُتَعَمِّداً فَجَزَٓاؤُ۬هُ جَهَنَّمُ خَالِداً ف۪يهَا وَغَضِبَ اللّٰهُ عَلَيْهِ وَلَعَنَهُ وَاَعَدَّ لَهُ عَذَاباً عَظ۪يماً", translation: "Kim, bir Mü'min'i isteyerek öldürürse, onun karşılığı, içinde sürekli kalmak üzere Cehennem'dir. Allah ona gazap etmiş, lanetlemiş ve büyük bir azap hazırlamıştır.", transliteration: "Ve men yaktul mu'minen muteammiden fe cezauhu cehennemu haliden fiha ve gadıballahu aleyhi ve leanehu ve eadde lehu azaben azima."},
  {id: 587, sure_no: 94, ayet_no: 94, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا ضَرَبْتُمْ ف۪ي سَب۪يلِ اللّٰهِ فَتَبَيَّنُوا وَلَا تَقُولُوا لِمَنْ اَلْقٰٓى اِلَيْكُمُ السَّلَامَ لَسْتَ مُؤْمِناًۚ تَبْتَغُونَ عَرَضَ الْحَيٰوةِ الدُّنْيَاۘ فَعِنْدَ اللّٰهِ مَغَانِمُ كَث۪يرَةٌۜ كَذٰلِكَ كُنْتُمْ مِنْ قَبْلُ فَمَنَّ اللّٰهُ عَلَيْكُمْ فَتَبَيَّنُواۜ اِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يراً", translation: "Ey İman Edenler! Allah yolunda sefere çıktığınız zaman, iyice araştırın; size selam veren kimseye; dünya hayatının geçici menfaatine göz dikerek: \"Sen Mü'min değilsin.\" demeyin. Allah'ın yanında sayısız ganimetler vardır. Daha önce siz de öyleydiniz de Allah size iyilik yaptı. Öyleyse, iyice araştırın. Kuşkusuz, Allah, yaptığınız her şeyden haberdardır.", transliteration: "Ya eyyuhallezine amenu iza darabtum fi sebilillahi fe tebeyyenu ve la tekulu li men elka ileykumus selame leste mu'mina, tebtegune aradal hayatid dunya, fe indallahi meganimu kesirah. Kezalike kuntum min kablu fe mennallahu aleykum fe tebeyyenu. İnnallahe kane bima ta'melune habira."},
  {id: 588, sure_no: 95, ayet_no: 95, sure_adi: "Nisa", text: "لَا يَسْتَوِي الْقَاعِدُونَ مِنَ الْمُؤْمِن۪ينَ غَيْرُ اُو۬لِي الضَّرَرِ وَالْمُجَاهِدُونَ ف۪ي سَب۪يلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۜ فَضَّلَ اللّٰهُ الْمُجَاهِد۪ينَ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ عَلَى الْقَاعِد۪ينَ دَرَجَةًۜ وَكُلاًّ وَعَدَ اللّٰهُ الْحُسْنٰىۜ وَفَضَّلَ اللّٰهُ الْمُجَاهِد۪ينَ عَلَى الْقَاعِد۪ينَ اَجْراً عَظ۪يـماًۙ", translation: "Mü'minlerden, bir özrü olmaksızın, Allah yolunda cihad etmekten geri kalanlarla; mallarıyla ve canlarıyla Allah yolunda cihad edenler, bir değildir. Allah, malları ve canları ile cihad edenleri, derece bakımından, geri kalanlardan üstün kıldı. Her ne kadar Allah, her ikisine de iyilikle muamele etmeyi söz vermiş ise de cihad edenleri, geri kalanlara karşı çok daha büyük bir ecirle üstün kılmıştır;", transliteration: "La yestevil kaıdune minel mu'minine gayru ulid darari vel mucahidune fi sebilillahi bi emvalihim ve enfusihim. Faddalallahul mucahidine bi emvalihim ve enfusihim alal kaidine dereceh. Ve kullen vaadallahul husna. Ve faddalallahul mucahidine alal kaıdine ecran azima."},
  {id: 589, sure_no: 96, ayet_no: 96, sure_adi: "Nisa", text: "دَرَجَاتٍ مِنْهُ وَمَغْفِرَةً وَرَحْمَةًۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً۟", translation: "Kendinden derecelerle, bağışlama ve rahmetle. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Deracatin minhu ve magfiraten ve rahmet. Ve kanallahu gafuran rahima."},
  {id: 590, sure_no: 97, ayet_no: 97, sure_adi: "Nisa", text: "اِنَّ الَّذ۪ينَ تَوَفّٰيهُمُ الْمَلٰٓئِكَةُ ظَالِم۪ٓي اَنْفُسِهِمْ قَالُوا ف۪يمَ كُنْتُمْۜ  قَالُوا كُنَّا مُسْتَضْعَف۪ينَ فِي الْاَرْضِۜ قَالُٓوا اَلَمْ تَكُنْ اَرْضُ اللّٰهِ وَاسِعَةً فَتُهَاجِرُوا ف۪يهَاۜ فَاُو۬لٰٓئِكَ مَأْوٰيهُمْ جَهَنَّمُۜ وَسَٓاءَتْ مَص۪يراًۙ", translation: "Doğrusu, kendilerine haksızlık eden kimselere, melekler canlarını alırken: \"Neden bu durumdaydınız?\" derler. Onlar: \"Biz yeryüzünde mus'tezaf kimselerdik\" derler. Melekler: \"Allah'ın arzı geniş değil miydi, hicret etseydiniz ya!\" derler. İşte bunların yeri Cehennem'dir. Orası ne kötü bir yerdir.", transliteration: "İnnellezine teveffahumul melaiketu zalimi enfusihim kalu fime kuntum. Kalu kunna mustad'afine fil ard. Kalu e lem tekun ardullahi vasiaten fe tuhaciru fiha. Fe ulaike me'vahum cehennem ve saet masira."},
  {id: 591, sure_no: 98, ayet_no: 98, sure_adi: "Nisa", text: "اِلَّا الْمُسْتَضْعَف۪ينَ مِنَ الرِّجَالِ وَالنِّسَٓاءِ وَالْوِلْدَانِ لَا يَسْتَط۪يعُونَ ح۪يلَةً وَلَا يَهْتَدُونَ سَب۪يلاً", translation: "Ancak, çaresiz kalan, gücü yetmeyen, bir çıkış yolu bulamayan mus'tezaf erkek, kadın ve çocuklar hariç.", transliteration: "İllal mustad'afine miner ricali ven nisai vel vildani la yestatiune hileten ve la yehtedune sebila."},
  {id: 592, sure_no: 99, ayet_no: 99, sure_adi: "Nisa", text: "فَاُو۬لٰٓئِكَ عَسَى اللّٰهُ اَنْ يَعْفُوَ عَنْهُمْۜ وَكَانَ اللّٰهُ عَفُواًّ غَفُوراً", translation: "Umulur ki Allah, bunları affeder. Kuşkusuz Allah, Çok Affedici'dir, Çok Bağışlayıcı'dır.", transliteration: "Fe ulaike asallahu en ya'fuve anhum. Ve kanallahu afuvven gafura."},
  {id: 593, sure_no: 100, ayet_no: 100, sure_adi: "Nisa", text: "وَمَنْ يُهَاجِرْ ف۪ي سَب۪يلِ اللّٰهِ يَجِدْ فِي الْاَرْضِ مُرَاغَماً كَث۪يراً وَسَعَةًۜ وَمَنْ يَخْرُجْ مِنْ بَيْتِه۪ مُهَاجِراً اِلَى اللّٰهِ وَرَسُولِه۪ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ اَجْرُهُ عَلَى اللّٰهِۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً۟", translation: "Kim, Allah yolunda hicret ederse, yeryüzünde gidecek pek çok yer ve genişlik bulur. Kim, Allah ve Resul'ü için hicret edip, yurdundan ayrılır da sonra onu ölüm yakalarsa, onun ecri, kesinlikle Allah'a aittir. Kuşkusuz, Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve men yuhacir fi sebilillahi yecid fil ardı muragamen kesiran veseah. Ve men yahruc min beytihi muhaciran ilallahi ve resulihi summe yudrikhul mevtu fe kad vakaa ecruhu alallah. Ve kanallahu gafuran rahima."},
  {id: 594, sure_no: 101, ayet_no: 101, sure_adi: "Nisa", text: "وَاِذَا ضَرَبْتُمْ فِي الْاَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَقْصُرُوا مِنَ الصَّلٰوةِۗ اِنْ خِفْتُمْ اَنْ يَفْتِنَكُمُ الَّذ۪ينَ كَفَرُواۜ اِنَّ الْكَافِر۪ينَ كَانُوا لَكُمْ عَدُواًّ مُب۪يناً", translation: "Yeryüzünde sefere çıktığınız zaman, eğer Kafirlerin size kötülük yapmalarından korkarsanız, salatı kısaltmanızda bir sakınca yoktur. Kuşkusuz, Kafirler sizin apaçık düşmanınızdır.", transliteration: "Ve iza darabtum fil ardı fe leyse aleykum cunahun en taksuru mines salati, in hıftum en yeftinekumullezine keferu. İnnel kafirine kanu lekum aduvven mubina."},
  {id: 595, sure_no: 102, ayet_no: 102, sure_adi: "Nisa", text: "وَاِذَا كُنْتَ ف۪يهِمْ فَاَقَمْتَ لَهُمُ الصَّلٰوةَ فَلْتَقُمْ طَٓائِفَةٌ مِنْهُمْ مَعَكَ وَلْيَأْخُذُٓوا اَسْلِحَتَهُمْ۠ فَاِذَا سَجَدُوا فَلْيَكُونُوا مِنْ وَرَٓائِكُمْۖ وَلْتَأْتِ طَٓائِفَةٌ اُخْرٰى لَمْ يُصَلُّوا فَلْيُصَلُّوا مَعَكَ وَلْيَأْخُذُوا حِذْرَهُمْ وَاَسْلِحَتَهُمْۚ وَدَّ الَّذ۪ينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ اَسْلِحَتِكُمْ وَاَمْتِعَتِكُمْ فَيَم۪يلُونَ عَلَيْكُمْ مَيْلَةً وَاحِدَةًۜ وَلَا جُنَاحَ عَلَيْكُمْ اِنْ كَانَ بِكُمْ اَذًى مِنْ مَطَرٍ اَوْ كُنْتُمْ مَرْضٰٓى اَنْ تَضَعُٓوا اَسْلِحَتَكُمْۚ وَخُذُوا حِذْرَكُمْۜ اِنَّ اللّٰهَ اَعَدَّ لِلْكَافِر۪ينَ عَذَاباً مُه۪يناً", translation: "Sen de içlerinde bulunup; onlara salatı ikame ettirdiğin zaman, onların bir kısmı seninle beraber salata dursun ve silahlarını da yanlarına alsınlar. Bunlar, secde edince, arkanıza geçsinler. Sonra, salat etmemiş olan diğer kısım gelsin, seninle beraber salatı ikame etsin. Önlemlerini ve silahlarını da alsınlar. Kafirler, silahlarınızdan ve eşyalarınızdan uzak kalmanızı arzu ederler ki, size aniden baskın düzenlesinler. Eğer yağmurdan dolayı bir eziyet görürseniz veya hasta olursanız, önlemlerinizi alarak silahlarınızı bırakmanızda sizin için bir sakınca yoktur. Kuşkusuz, Allah Kafirler için alçaltıcı bir azap hazırlamıştır.", transliteration: "Ve iza kunte fihim fe ekamte lehumus salate fel tekum taifetun minhum meake vel ye'huzu eslihatehum fe iza secedu fel yekunu min varaikum, vel te'ti taifetun uhra lem yusallu fel yusallu meake vel ye'huzu hızrahum ve eslihatehum, veddellezine keferu lev tagfulune an eslihatikum ve emtiatikum fe yemilune aleykum meyleten vahıdeh. Ve la cunaha aleykum in kane bikum ezen min matarin ev kuntum marda en tedau eslihatekum, ve huzu hızrakum. İnnallahe eadde lil kafirine azaben muhina."},
  {id: 596, sure_no: 103, ayet_no: 103, sure_adi: "Nisa", text: "فَاِذَا قَضَيْتُمُ الصَّلٰوةَ فَاذْكُرُوا اللّٰهَ قِيَاماً وَقُعُوداً وَعَلٰى جُنُوبِكُمْۚ فَاِذَا اطْمَأْنَنْتُمْ فَاَق۪يمُوا الصَّلٰوةَۚ اِنَّ الصَّلٰوةَ كَانَتْ عَلَى الْمُؤْمِن۪ينَ كِتَاباً مَوْقُوتاً", translation: "Salatı ikame ettikten sonra ayakta, oturarak ve yanlarınız üzerinde Allah'ı anın. Güvene kavuştuğunuz zaman, salatı gereği gibi ikame edin. Kuşkusuz salat, belirlenmiş vakitlerde Mü'minler üzerine yazılmıştır.", transliteration: "Fe iza kadaytumus salate fezkurullahe kıyamen ve kuuden ve ala cunubikum, fe izatma'nentum fe ekimus salat, innes salate kanet alal mu'minine kitaben mevkuta."},
  {id: 597, sure_no: 104, ayet_no: 104, sure_adi: "Nisa", text: "وَلَا تَهِنُوا فِي ابْتِغَٓاءِ الْقَوْمِۜ اِنْ تَكُونُوا تَأْلَمُونَ فَاِنَّهُمْ يَأْلَمُونَ كَمَا تَأْلَمُونَۚ وَتَرْجُونَ مِنَ اللّٰهِ مَا لَا يَرْجُونَۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً۟", translation: "Düşman halkı takip etmekte gevşeklik göstermeyin. Eğer siz acı çekiyorsanız, onlar da sizin acı çektiğiniz gibi acı çekiyorlar. Üstelik siz, Allah'tan onların ummadıkları şeyleri umuyorsunuz. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve la tehinu fibtigail kavm. İn tekunu te'lemune fe innehum ye'lemune kema te'lemun, ve tercune minallahi ma la yercun. Ve kanallahu alimen hakima."},
  {id: 598, sure_no: 105, ayet_no: 105, sure_adi: "Nisa", text: "اِنَّٓا اَنْزَلْـنَٓا اِلَيْكَ الْكِتَابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَٓا اَرٰيكَ اللّٰهُۜ وَلَا تَكُنْ لِلْخَٓائِن۪ينَ خَص۪يماًۙ", translation: "Biz, insanlar arasında, Allah'ın sana gösterdiği gibi hükmedesin diye, Kitap'ı hakikat olarak indirdik. Hainlerin savunucusu olma.", transliteration: "İnna enzelna ileykel kitabe bil hakkı li tahkume beynen nasi bima erakallah. Ve la tekun lil hainine hasima."},
  {id: 599, sure_no: 106, ayet_no: 106, sure_adi: "Nisa", text: "وَاسْتَغْفِرِ اللّٰهَۜ اِنَّ اللّٰهَ كَانَ غَفُوراً رَح۪يماًۚ", translation: "Allah'tan bağışlanma dile. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Vestagfirillah. İnnallahe kane gafuran rahima."},
  {id: 600, sure_no: 107, ayet_no: 107, sure_adi: "Nisa", text: "وَلَا تُجَادِلْ عَنِ الَّذ۪ينَ يَخْتَانُونَ اَنْفُسَهُمْۜ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ خَوَّاناً اَث۪يماًۚ", translation: "Kendilerine ihanet eden kimselerden yana savunma yapma. Kuşkusuz Allah, ihanette ısrar eden günahkarları sevmez.", transliteration: "Ve la tucadil anillezine yahtanune enfusehum innallahe la yuhıbbu men kane havvanen esima."},
  {id: 601, sure_no: 108, ayet_no: 108, sure_adi: "Nisa", text: "يَسْتَخْفُونَ مِنَ النَّاسِ وَلَا يَسْتَخْفُونَ مِنَ اللّٰهِ وَهُوَ مَعَهُمْ اِذْ يُبَيِّتُونَ مَا لَا يَرْضٰى مِنَ الْقَوْلِۜ وَكَانَ اللّٰهُ بِمَا يَعْمَلُونَ مُح۪يـطاً", translation: "İnsanlardan gizliyorlar da Allah'tan gizleyemezler. Oysa Allah, razı olmayacağı sözü geceleyin düzüp kurarlarken onlarla beraberdi. Kuşkusuz, Allah, onların yaptığı her şeyi kuşatandır.", transliteration: "Yestahfune minen nasi ve la yestahfune minallahi ve huve meahum iz yubeyyitune ma la yarda minel kavl. Ve kanallahu bi ma ya'melune muhita."},
  {id: 602, sure_no: 109, ayet_no: 109, sure_adi: "Nisa", text: "هَٓا اَنْتُمْ هٰٓؤُ۬لَٓاءِ جَادَلْتُمْ عَنْهُمْ فِي الْحَيٰوةِ الدُّنْيَا فَمَنْ يُجَادِلُ اللّٰهَ عَنْهُمْ يَوْمَ الْقِيٰمَةِ اَمْ مَنْ يَكُونُ عَلَيْهِمْ وَك۪يلاً", translation: "Diyelim ki bu dünya hayatında onları savundunuz, peki ya kıyamet günü, Allah'a karşı onları kim savunacak veya onlara kim vekil olacak?", transliteration: "Ha entum haulai cadeltum anhum fil hayatid dunya fe men yucadilullahe anhum yevmel kıyameti em men yekunu aleyhim vekila."},
  {id: 603, sure_no: 110, ayet_no: 110, sure_adi: "Nisa", text: "وَمَنْ يَعْمَلْ سُٓوءاً اَوْ يَظْلِمْ نَفْسَهُ ثُمَّ يَسْتَغْفِرِ اللّٰهَ يَجِدِ اللّٰهَ غَفُوراً رَح۪يماً", translation: "Kim bir kötülük yapar veya kendisine haksızlık eder de, sonra Allah'tan bağışlanma dilerse; Allah'ı, Çok Bağışlayıcı ve Kesintisiz Rahmet Edici bulur.", transliteration: "Ve men ya'mel suen ev yazlim nefsehu summe yestagfirillahe yecidillahe gafuran rahima."},
  {id: 604, sure_no: 111, ayet_no: 111, sure_adi: "Nisa", text: "وَمَنْ يَكْسِبْ اِثْماً فَاِنَّمَا يَكْسِبُهُ عَلٰى نَفْسِه۪ۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً", translation: "Kim bir günah işlerse, onu ancak kendi aleyhine işlemiş olur. Kuşkusuz, Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve men yeksib ismen fe innema yeksibuhu ala nefsihi. Ve kanallahu alimen hakima."},
  {id: 605, sure_no: 112, ayet_no: 112, sure_adi: "Nisa", text: "وَمَنْ يَكْسِبْ خَط۪ٓيـَٔةً اَوْ اِثْماً ثُمَّ يَرْمِ بِه۪ بَر۪ٓيـٔاً فَقَدِ احْتَمَلَ بُهْتَـاناً وَاِثْماً مُب۪يناً۟", translation: "Kim bir yanlış yapar veya günah işler, sonra da onu suçsuz birinin üzerine atarsa, muhakkak ki büyük bir iftira ve apaçık bir günah yüklenmiş olur.", transliteration: "Ve men yeksib hatieten ev ismen summe yermi bihi berien fe kadihtemele buhtanen ve ismen mubina."},
  {id: 606, sure_no: 113, ayet_no: 113, sure_adi: "Nisa", text: "وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكَ وَرَحْمَتُهُ لَهَمَّتْ طَٓائِفَةٌ مِنْهُمْ اَنْ يُضِلُّوكَۜ وَمَا يُضِلُّونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَضُرُّونَكَ مِنْ شَيْءٍۜ وَاَنْزَلَ اللّٰهُ عَلَيْكَ الْكِتَابَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَعْلَمُۜ وَكَانَ فَضْلُ اللّٰهِ عَلَيْكَ عَظ۪يماً", translation: "Allah'ın, sana lütfu ve rahmeti olmasaydı, onlardan bazıları seni saptırmaya yeltenmişti. Oysa onlar, kendilerinden başkasını saptıramazlar. Sana hiçbir zarar veremezler. Allah, sana Kitap'ı ve Hikmet'i indirdi ve sana bilmediklerini öğretti. Kuşkusuz, Allah'ın sana lütfu çok büyüktür.", transliteration: "Ve lev la fadlullahi aleyke ve rahmetuhu le hemmet taifetun minhum en yudılluke. Ve ma yudıllune illa enfusehum ve ma yadurruneke min şey'. Ve enzelallahu aleykel kitabe vel hikmete ve allemeke ma lem tekun ta'lem. Ve kane fadlullahi aleyke azima."},
  {id: 607, sure_no: 114, ayet_no: 114, sure_adi: "Nisa", text: "لَا خَيْرَ ف۪ي كَث۪يرٍ مِنْ نَجْوٰيهُمْ اِلَّا مَنْ اَمَرَ بِصَدَقَةٍ اَوْ مَعْرُوفٍ اَوْ اِصْلَاحٍ بَيْنَ النَّاسِۜ وَمَنْ يَفْعَلْ ذٰلِكَ ابْتِغَٓاءَ مَرْضَاتِ اللّٰهِ فَسَوْفَ نُؤْت۪يهِ اَجْراً عَظ۪يماً", translation: "Hak gözetmeyi, iyi şeyler yapmayı ve insanların arasını düzeltmeyi isteyenlerin görüşmeleri hariç, onların gizli görüşmelerinde bir hayır yoktur. Kim bunları yalnızca Allah'ın rızasını kazanmak için yaparsa, ileride ona büyük bir ödül vereceğiz.", transliteration: "La hayra fi kesirin min necvahum illa men emera bi sadakatin ev ma'rufin ev ıslahın beynen nas. Ve men yef'al zalikebtigae mardatillahi fe sevfe nu'tihi ecran azima."},
  {id: 608, sure_no: 115, ayet_no: 115, sure_adi: "Nisa", text: "وَمَنْ يُشَاقِقِ الرَّسُولَ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدٰى وَيَتَّبِـعْ غَيْرَ سَب۪يلِ الْمُؤْمِن۪ينَ نُوَلِّه۪ مَا تَوَلّٰى وَنُصْلِه۪ جَهَنَّمَۜ وَسَٓاءَتْ مَص۪يراً۟", translation: "Kendisine doğru yol belli olduktan sonra, her kim Resul'e karşı gelir, Mü'minlerin yolundan başkasına yönelirse, onu saptığı yolda bırakırız. Onu Cehennem'e atarız. Orası ne kötü bir dönüş yeridir.", transliteration: "Ve men yuşakıkır resule min ba'di ma tebeyyene lehul huda ve yettebi' gayra sebilil mu'minine nuvellıhi ma tevella ve nuslihi cehennem. Ve saet masira."},
  {id: 609, sure_no: 116, ayet_no: 116, sure_adi: "Nisa", text: "اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُشْرَكَ بِه۪ وَيَغْفِرُ مَا دُونَ ذٰلِكَ لِمَنْ يَشَٓاءُۜ وَمَنْ يُشْرِكْ بِاللّٰهِ فَقَدْ ضَلَّ ضَلَالاً بَع۪يداً", translation: "Allah, kendisine şirk koşanları bağışlamaz. Bunun dışında hak edeni bağışlar. Kim Allah'a şirk koşarsa, derin bir sapkınlıkla sapmış olur.", transliteration: "İnnallahe la yagfiru en yuşrake bihi ve yagfiru ma dune zalike li men yeşau. Ve men yuşrik billahi fe kad dalle dalalen baida."},
  {id: 610, sure_no: 117, ayet_no: 117, sure_adi: "Nisa", text: "اِنْ يَدْعُونَ مِنْ دُونِه۪ٓ اِلَّٓا اِنَاثاًۚ وَاِنْ يَدْعُونَ اِلَّا شَيْطَاناً مَر۪يداًۙ", translation: "Onlar, O'nu bırakıp, bir takım dişilere yalvarıyorlar, oysa onların yalvardıkları azgın şeytandan başkası değildir.", transliteration: "İn yed'une min dunihi illa inasa, ve in yed'une illa şeytanen merida."},
  {id: 611, sure_no: 118, ayet_no: 118, sure_adi: "Nisa", text: "لَعَنَهُ اللّٰهُۢ وَقَالَ لَاَتَّخِذَنَّ مِنْ عِبَادِكَ نَص۪يباً مَفْرُوضاًۙ", translation: "Allah onu lanetledi ve o da: \"Ant olsun ki senin kullarından farz olarak alacağım.\" dedi.", transliteration: "Leanehullah, ve kale le ettehizenne min ibadike nasiben mefruda."},
  {id: 612, sure_no: 119, ayet_no: 119, sure_adi: "Nisa", text: "وَلَاُضِلَّنَّهُمْ وَلَاُمَنِّيَنَّهُمْ وَلَاٰمُرَنَّهُمْ فَلَيُبَتِّكُنَّ اٰذَانَ الْاَنْعَامِ وَلَاٰمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللّٰهِۜ وَمَنْ يَتَّخِذِ الشَّيْطَانَ وَلِياًّ مِنْ دُونِ اللّٰهِ فَقَدْ خَسِرَ خُسْرَاناً مُب۪يناًۜ", translation: "Mutlaka onları saptırıp, asılsız kuruntulara daldıracağım; onlara buyuracağım, davarların kulaklarını yaracaklar; onlara buyuracağım, Allah'ın yarattığını bozacaklar.\" Kim, Allah'ın yanı sıra şeytanı veli edinirse, apaçık bir zarara uğramış olur.", transliteration: "Ve le udillennehum ve le umenniyennehum ve le amurannehum fe le yubettikunne azanel en'ami, ve le amurannehum fe le yugayyirunne halkallah."},
  {id: 613, sure_no: 120, ayet_no: 120, sure_adi: "Nisa", text: "يَعِدُهُمْ وَيُمَنّ۪يهِمْۜ وَمَا يَعِدُهُمُ الشَّيْطَانُ اِلَّا غُرُوراً", translation: "Şeytan, onlara vaatlerde bulunup, onları kuruntulara sürükler. Şeytan, ancak aldatmak için vaatte bulunur.", transliteration: "Yeıduhum, ve yumennihim. Ve ma yeıduhumuş şeytanu illa gurura."},
  {id: 614, sure_no: 121, ayet_no: 121, sure_adi: "Nisa", text: "اُو۬لٰٓئِكَ مَأْوٰيهُمْ جَهَنَّمُ وَلَا يَجِدُونَ عَنْهَا مَح۪يصاً", translation: "Onların varacakları yer Cehennem'dir ve oradan kurtulmak için hiçbir yol bulamayacaklar.", transliteration: "Ulaike me'vahum cehennemu ve la yecidune anha mahisa."},
  {id: 615, sure_no: 122, ayet_no: 122, sure_adi: "Nisa", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ وَعْدَ اللّٰهِ حَقاًّۜ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ ق۪يلاً", translation: "İman edip, salihatı yapanları, içinde kesintisiz ve ebedi olarak kalacakları, altlarından ırmaklar akan Cennetlere koyacağız. Allah'ın verdiği söz hakikattir. Allah'tan daha doğru sözlü kim olabilir?", transliteration: "Vellezine amenu ve amilus salihati se nudhiluhum cennatin tecri min tahtihal enharu halidine fiha ebeda. Va'dallahi hakka.  Ve men asdaku minallahi kila."},
  {id: 616, sure_no: 123, ayet_no: 123, sure_adi: "Nisa", text: "لَيْسَ بِاَمَانِيِّكُمْ وَلَٓا اَمَانِيِّ اَهْلِ الْكِتَابِۜ مَنْ يَعْمَلْ سُٓوءاً يُجْزَ بِه۪ۙ وَلَا يَجِدْ لَهُ مِنْ دُونِ اللّٰهِ وَلِياًّ وَلَا نَص۪يراً", translation: "Ne sizin kuruntularınız ne de Kitap Ehli'nin kuruntularına göre değil; kim bir kötülük yaparsa, onun karşılığını bulur. O, kendisine Allah'tan başka ne bir veli ne de bir yardımcı bulabilir.", transliteration: "Leyse bi emaniyyikum ve la emaniyyi ehlil kitab. Men ya'mel suen yucze bihi, ve la yecid lehu min dunillahi veliyyen ve la nasira."},
  {id: 617, sure_no: 124, ayet_no: 124, sure_adi: "Nisa", text: "وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُو۬لٰٓئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ نَق۪يراً", translation: "Erkek ve kadın; her kim Mü'min olarak salihatı yaparsa, işte onlar Cennet'e gireceklerdir. Ve onlara zerre kadar haksızlık edilmeyecektir.", transliteration: "Ve men ya'mel mines salihati min zekerin ev unsa ve huve mu'minun fe ulaike yedhulunel cennete ve la yuzlemune nakira."},
  {id: 618, sure_no: 125, ayet_no: 125, sure_adi: "Nisa", text: "وَمَنْ اَحْسَنُ د۪يناً مِمَّنْ اَسْلَمَ وَجْهَهُ لِلّٰهِ وَهُوَ مُحْسِنٌ وَاتَّبَعَ مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَاتَّخَذَ اللّٰهُ اِبْرٰه۪يمَ خَل۪يلاً", translation: "Kimin dini, kendisini muhsin olarak Allah'a teslim etmiş; hanif olan İbrahim'in milletine tabi olandan daha iyi olabilir? Allah, İbrahim'i \"halil\" edinmişti.", transliteration: "Ve men ahsenu dinen mimmen esleme vechehu lillahi ve huve muhsinun vettebea millete ibrahime hanifa. Vettehazallahu ibrahime halila."},
  {id: 619, sure_no: 126, ayet_no: 126, sure_adi: "Nisa", text: "وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ مُح۪يطاً۟", translation: "Göklerde ve yerde olan her şey Allah'a aittir. Ve Allah, Her Şeyi Kuşatmıştır.", transliteration: "Ve lillahi ma fis semavati ve ma fil ard. Ve kanallahu bi kulli şey'in muhita."},
  {id: 620, sure_no: 127, ayet_no: 127, sure_adi: "Nisa", text: "وَيَسْتَفْتُونَكَ فِي النِّسَٓاءِۜ قُلِ اللّٰهُ يُفْت۪يكُمْ ف۪يهِنَّۙ وَمَا يُتْلٰى عَلَيْكُمْ فِي الْكِتَابِ ف۪ي يَتَامَى النِّسَٓاءِ الّٰت۪ي لَا تُؤْتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُونَ اَنْ تَنْكِحُوهُنَّ وَالْمُسْتَضْعَف۪ينَ مِنَ الْوِلْدَانِۙ وَاَنْ تَقُومُوا لِلْيَتَامٰى بِالْقِسْطِۜ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ كَانَ بِه۪ عَل۪يماً", translation: "Senden, o kadınlar hakkında fetva istiyorlar. De ki: \"Onlar hakkında size fetvayı Allah vermektedir: Yazılmış olanı vermediğiniz halde kendileri ile evlenmek istediğiniz yetim kadınlarla zayıf, çaresiz çocukların ve yetimlerin haklarını hakkaniyetle vermeniz konusundaki hükümler size bu Kitap'ta okunuyor. Hayır adına ne yaparsanız Allah onu bilir.\"", transliteration: "Ve yesteftuneke fin nisai. Kulillahu yuftikum fihinne, ve ma yutla aleykum fil kitabi fi yetamen nisaillati la tu'tunehunne ma kutibe lehunne ve tergabune en tenkihuhunne vel mustad'afine minel vildani, ve en tekumu lil yetama bil kıst. Ve ma tef'alu min hayrin fe innallahe kane bihi alima."},
  {id: 621, sure_no: 128, ayet_no: 128, sure_adi: "Nisa", text: "وَاِنِ امْرَاَةٌ خَافَتْ مِنْ بَعْلِهَا نُشُوزاً اَوْ اِعْرَاضاً فَلَا جُنَاحَ عَلَيْهِمَٓا اَنْ يُصْلِحَا بَيْنَهُمَا صُلْحاًۜ وَالصُّلْحُ خَيْرٌۜ وَاُحْضِرَتِ الْاَنْفُسُ الشُّحَّۜ وَاِنْ تُحْسِنُوا وَتَتَّقُوا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يراً", translation: "Eğer bir kadın, kocasının nuşuzundan veya kendisinden yüz çevirmesinden endişe ederse, uzlaşmaya çalışmalarında bir sakınca yoktur. Uzlaşmak, daha hayırlıdır. Zira benlikler bencilliğe eğimlidir. Eğer arayı düzeltmek ister ve takvalı davranırsanız; Allah, Yaptığınız Her Şeyden Haberdar'dır.", transliteration: "Ve in imraetun hafet min ba'liha nuşuzen ev ı'radan fe la cunaha aleyhima en yuslıha beynehuma sulha. Ves sulhu hayr. Ve uhdıratil enfusuş şuhh. Ve in tuhsinu ve tetteku fe innallahe kane bi ma ta'melune habira."},
  {id: 622, sure_no: 129, ayet_no: 129, sure_adi: "Nisa", text: "وَلَنْ تَسْتَط۪يعُٓوا اَنْ تَعْدِلُوا بَيْنَ النِّسَٓاءِ وَلَوْ حَرَصْتُمْ فَلَا تَم۪يلُوا كُلَّ الْمَيْلِ فَتَذَرُوهَا كَالْمُعَلَّقَةِۜ وَاِنْ تُصْلِحُوا وَتَتَّقُوا فَاِنَّ اللّٰهَ كَانَ غَفُوراً رَح۪يماً", translation: "Ne kadar isteseniz de kadınlar arasında tam anlamı ile adaletli olmaya kesinlikle güç yetiremezsiniz. O halde, anlaşmazlığı çözümsüz hale getirip, onları yüzüstü bırakmayın. Eğer, arayı düzelterek, takvalı davranırsanız kuşkusuz ki Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve len testatiu en ta'dilu beynen nisai ve lev harastum fe la temilu kullel meyli fe tezeruha kel muallakah. Ve in tuslihu ve tetteku fe innallahe kane gafuran rahima."},
  {id: 623, sure_no: 130, ayet_no: 130, sure_adi: "Nisa", text: "وَاِنْ يَتَفَرَّقَا يُغْنِ اللّٰهُ كُلاًّ مِنْ سَعَتِه۪ۜ وَكَانَ اللّٰهُ وَاسِعاً حَك۪يماً", translation: "Eğer karı-koca ayrılırlarsa, Allah, kudretiyle her birini kendi kendilerine yeterli kılar. Kuşkusuz Allah, Yardımı Çok Kapsamlı Olan'dır, Egemenlik Sahibi'dir.", transliteration: "Ve in yeteferreka yugnillahu kullen min seatihi. Ve kanallahu vasian hakima."},
  {id: 624, sure_no: 131, ayet_no: 131, sure_adi: "Nisa", text: "وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَلَقَدْ وَصَّيْنَا الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَاِيَّاكُمْ اَنِ اتَّقُوا اللّٰهَۜ وَاِنْ تَكْفُرُوا فَاِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَانَ اللّٰهُ غَنِياًّ حَم۪يداً", translation: "Göklerde ve yerde olan her şey yalnızca Allah'ındır. Ant olsun, sizden önce Kitap verilenlere ve size Allah'a karşı takvalı olun diye tavsiyede bulunduk. Eğer kafirlik ederseniz, göklerde ve yerde olan her şey yalnızca Allah'ındır. Hiçbir Şeye Muhtaç Olmayan, Övgüye Değer Yegane Varlık yalnızca Allah'tır.", transliteration: "Ve lillahi ma fis semavati ve ma fil ard. Ve lekad vassaynallezine utul kitabe min kablikum ve iyyakum enittekullah. Ve in tekfuru fe inne lillahi ma fis semavati ve ma fil ard. Ve kanallahu ganiyyen hamida."},
  {id: 625, sure_no: 132, ayet_no: 132, sure_adi: "Nisa", text: "وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً", translation: "Göklerde ve yerde olan her şey, yalnızca Allah'ındır. Vekil olarak Allah yeter.", transliteration: "Ve lillahi ma fis semavati ve ma fil ard. Ve kefa billahi vekila."},
  {id: 626, sure_no: 133, ayet_no: 133, sure_adi: "Nisa", text: "اِنْ يَشَأْ يُذْهِبْكُمْ اَيُّهَا النَّاسُ وَيَأْتِ بِاٰخَر۪ينَۜ وَكَانَ اللّٰهُ عَلٰى ذٰلِكَ قَد۪يراً", translation: "Ey insanlar! Eğer Allah dilerse sizi yok edip, yerinize başkalarını getirir. Kuşkusuz, Allah'ın bunu yapmaya gücü yeter.", transliteration: "İn yeşa' yuzhibkum eyyuhan nasu ve ye'ti bi aharin. Ve kanallahu ala zalike kadira."},
  {id: 627, sure_no: 134, ayet_no: 134, sure_adi: "Nisa", text: "مَنْ كَانَ يُر۪يدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللّٰهِ ثَوَابُ الدُّنْيَا وَالْاٰخِرَةِۜ وَكَانَ اللّٰهُ سَم۪يعاً بَص۪يراً۟", translation: "Kim dünya sevabını isterse; bilsin ki dünyanın da ahiretin de sevabı yalnızca Allah katındadır. Allah, Her Şeyi Duyan, Her Şeyi Gören'dir.", transliteration: "Men kane yuridu sevabed dunya fe indallahi sevabud dunya vel ahırah. Ve kanallahu semian basira."},
  {id: 628, sure_no: 135, ayet_no: 135, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُونُوا قَوَّام۪ينَ بِالْقِسْطِ شُهَدَٓاءَ لِلّٰهِ وَلَوْ عَلٰٓى اَنْفُسِكُمْ اَوِ الْوَالِدَيْنِ وَالْاَقْرَب۪ينَۚ اِنْ يَكُنْ غَنِياًّ اَوْ فَق۪يراً فَاللّٰهُ اَوْلٰى بِهِمَا فَلَا تَتَّبِعُوا الْهَوٰٓى اَنْ تَعْدِلُواۚ وَاِنْ تَلْـوُٓ۫ا اَوْ تُعْرِضُوا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يراً", translation: "Ey İman Edenler! Kendinizin, anne ve babanızın ve akrabalarınızın aleyhine bile olsa; tanıklık ettiğiniz kimseler, zengin de olsa, fakir de olsa, Allah için hakkaniyetli tanıklar olarak adaleti gerçek anlamıyla yerine getirin. Allah, onlara sizden daha yakındır. Haddi aşarak, tutkunuza tabi olmayın. Eğer gerçeği çarpıtıp, yüz çevirirseniz, Allah, bütün yaptıklarınızdan haberdardır.", transliteration: "Ya eyyuhallezine amenu kunu kavvamine bil kıstı şuhedae lillahi ve lev ala enfusıkum evil valideyni vel akrabin, in yekun ganiyyen ev fakiran fallahu evla bihima fe la tettebiul heva en ta'dilu, ve in telvu ev tu'rıdu fe innallahe kane bi ma ta'melune habira."},
  {id: 629, sure_no: 136, ayet_no: 136, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اٰمِنُوا بِاللّٰهِ وَرَسُولِه۪ وَالْكِتَابِ الَّذ۪ي نَزَّلَ عَلٰى رَسُولِه۪ وَالْكِتَابِ الَّـذ۪ٓي اَنْزَلَ مِنْ قَبْلُۜ  وَمَنْ يَكْفُرْ بِاللّٰهِ وَمَلٰٓئِكَتِه۪ وَكُتُبِه۪ وَرُسُلِه۪ وَالْيَوْمِ الْاٰخِرِ فَقَدْ ضَلَّ ضَلَالاً بَع۪يداً", translation: "Ey İman Edenler! Allah'a, Resul'üne ve Resul'üne indirdiği Kitap'a ve daha önce indirilmiş Kitaplara iman edin. Kim, Allah'ı, meleklerini, kitaplarını, resullerini ve ahiret gününü küfrederse; o, çok derin bir sapkınlığa düşmüştür.", transliteration: "Ya eyyuhallezine amenu, aminu billahi ve resulihi vel kitabillezi nezzele ala resulihi vel kitabillezi enzele min kabl. Ve men yekfur billahi ve melaiketihi ve kutubihi ve rusulihi vel yevmil ahıri fe kad dalle dalalen baida."},
  {id: 630, sure_no: 137, ayet_no: 137, sure_adi: "Nisa", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا ثُمَّ   كَفَرُوا ثُمَّ اٰمَنُوا ثُمَّ   كَفَرُوا ثُمَّ ازْدَادُوا كُفْراً لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَب۪يلاًۜ", translation: "İman edip sonra küfreden, sonra iman edip, tekrar küfreden; sonra küfründe ileri gidenleri, Allah ne affedecek, ne de doğru yola iletecektir.", transliteration: "İnnellezine amenu, summe keferu, summe amenu, summe keferu, summezdadu kufran lem yekunillahu li yagfira lehum ve la li yehdiyehum sebila."},
  {id: 631, sure_no: 138, ayet_no: 138, sure_adi: "Nisa", text: "بَشِّرِ الْمُنَافِق۪ينَ بِاَنَّ لَهُمْ عَذَاباً اَل۪يماًۙ", translation: "Münafıklara duyur! Onlar için can yakıcı bir azap vardır.", transliteration: "Beşşiril munafikine bi enne lehum azaben elima."},
  {id: 632, sure_no: 139, ayet_no: 139, sure_adi: "Nisa", text: "اَلَّذ۪ينَ يَتَّخِذُونَ الْكَافِر۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ الْمُؤْمِن۪ينَۜ اَيَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ فَاِنَّ الْعِزَّةَ لِلّٰهِ جَم۪يعاًۜ", translation: "Çünkü onlar Mü'minlerin yanı sıra Kafirleri evliya ediniyorlar. İzzeti onların yanında mı arıyorlar! Kuşkusuz, izzet, tamamıyla Allah'ın yanındadır.", transliteration: "Ellezine yettehızunel kafirine evliyae min dunil mu'minin, e yebtegune indehumul izzete fe innel izzete lillahi cemia."},
  {id: 633, sure_no: 140, ayet_no: 140, sure_adi: "Nisa", text: "وَقَدْ نَزَّلَ عَلَيْكُمْ فِي الْكِتَابِ اَنْ اِذَا سَمِعْتُمْ اٰيَاتِ اللّٰهِ يُكْفَرُ بِهَا وَيُسْتَهْزَاُ بِهَا فَلَا تَقْعُدُوا مَعَهُمْ حَتّٰى يَخُوضُوا ف۪ي حَد۪يثٍ غَيْرِه۪ۘ اِنَّكُمْ اِذاً مِثْلُهُمْۜ اِنَّ اللّٰهَ جَامِـعُ الْمُنَافِق۪ينَ وَالْكَافِر۪ينَ ف۪ي جَهَنَّمَ جَم۪يعاًۙ", translation: "Ve O, size indirdiği Kitap'ta: \"Ayetlerinin küfredildiğini ve alaya alındığını duyduğunuz zaman başka bir söze geçinceye kadar onlarla beraber bulunmayın, yoksa onlar gibi olursunuz.\" diye bildirdi. Kuşkusuz, Allah, bütün Münafıkları ve Kafirleri Cehennem'de toplayacaktır.", transliteration: "Ve kad nezzele aleykum fil kitabi en iza semi'tum ayatillahi yukferu biha ve yustehzeu biha fe la tak'udu meahum hatta yehudu fi hadisin gayrihi, innekum izen misluhum. İnnallahe camiul munafikine vel kafirine fi cehenneme cemia."},
  {id: 634, sure_no: 141, ayet_no: 141, sure_adi: "Nisa", text: "اَلَّذ۪ينَ يَتَرَبَّصُونَ بِكُمْۚ فَاِنْ كَانَ لَكُمْ فَتْحٌ مِنَ اللّٰهِ قَالُٓوا اَلَمْ نَكُنْ مَعَكُمْۘ وَاِنْ كَانَ لِلْكَافِر۪ينَ نَص۪يبٌۙ قَالُٓوا اَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِنَ الْمُؤْمِن۪ينَۜ فَاللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِۜ وَلَنْ يَجْعَلَ اللّٰهُ لِلْكَافِر۪ينَ عَلَى الْمُؤْمِن۪ينَ سَب۪يلاً۟", translation: "Onlar, sürekli sizi gözetliyorlar, eğer Allah size bir zafer verirse: \"Biz de sizinle beraber değil miydik?\" derler. Eğer, Kafirler üstünlük sağlarlarsa: \"Biz sizin üstün gelmenizi sağlamadık mı, Mü'minlerden korumadık mı?\" derler. Kuşkusuz, Allah, Kıyamet Günü aranızda hükmünü verecektir. Allah, Mü'minlere karşı, Kafirlere asla bir yol vermeyecektir.", transliteration: "Ellezine yeterabbesune bikum, fe in kane lekum fethun minallahi kalu e lem nekun meakum, ve in kane lil kafirine nasibun, kalu e lem nestahviz aleykum ve nemna'kum minel mu'minin. Ve len yec'alallahu lil kafirine alal mu'minine sebila."},
  {id: 635, sure_no: 142, ayet_no: 142, sure_adi: "Nisa", text: "اِنَّ الْمُنَافِق۪ينَ يُخَادِعُونَ اللّٰهَ وَهُوَ خَادِعُهُمْۚ وَاِذَا قَامُٓوا اِلَى الصَّلٰوةِ قَامُوا كُسَالٰىۙ يُرَٓاؤُ۫نَ النَّاسَ وَلَا يَذْكُرُونَ اللّٰهَ اِلَّا قَل۪يلاًۘ", translation: "Münafıklar, Allah'ı aldatmaya çalışırlar. Oysa O, onların planlarını boşa çıkarandır. Onlar, \"salatı ikame ettikleri\" zaman üşene üşene yaparlar. İnsanlara gösteriş yaparlar. Allah'ı da pek az zikrederler.", transliteration: "İnnel munafikine yuhadiunallahe ve huve hadiuhum, ve iza kamu ilas salati kamu kusala yuraunen nase ve la yezkurunallahe illa kalila."},
  {id: 636, sure_no: 143, ayet_no: 143, sure_adi: "Nisa", text: "مُذَبْذَب۪ينَ بَيْنَ ذٰلِكَۗ لَٓا اِلٰى هٰٓؤُ۬لَٓاءِ وَلَٓا اِلٰى هٰٓؤُ۬لَٓاءِۜ وَمَنْ يُضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهُ سَب۪يلاً", translation: "Ne onlara ne bunlara, arada bocalayıp dururlar. Allah, kimi doğru yoldan yoksun bırakırsa, sen ona asla bir yol bulamazsın.", transliteration: "Muzebzebine beyne zalike, la ila haulai ve la ila haulai. Ve men yudlilillahu fe len tecide lehu sebila."},
  {id: 637, sure_no: 144, ayet_no: 144, sure_adi: "Nisa", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا الْكَافِر۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ الْمُؤْمِن۪ينَۜ اَتُر۪يدُونَ اَنْ تَجْعَلُوا لِلّٰهِ عَلَيْكُمْ سُلْطَاناً مُب۪يناً", translation: "Ey İman Edenler! Mü'minlerin yanı sıra Kafirleri evliya edinmeyin. Allah'a, aleyhinize olacak apaçık bir sultan mı vermek istiyorsunuz?", transliteration: "Ya eyyuhallezine amenu la tettehızul kafirine evliyae min dunil mu'minin. E turidune en tec'alu lillahi aleykum sultanen mubina."},
  {id: 638, sure_no: 145, ayet_no: 145, sure_adi: "Nisa", text: "اِنَّ الْمُنَافِق۪ينَ فِي الدَّرْكِ الْاَسْفَلِ مِنَ النَّارِۚ وَلَنْ تَجِدَ لَهُمْ نَص۪يراًۙ", translation: "Münafıklar, ateşin en alt tabakasında olacaklar. Ve onlara asla bir yardımcı bulamazsın.", transliteration: "İnnel munafikine fid derkil esfeli minen nar, ve len tecide lehum nasira."},
  {id: 639, sure_no: 146, ayet_no: 146, sure_adi: "Nisa", text: "اِلَّا الَّذ۪ينَ تَابُوا وَاَصْلَحُوا وَاعْتَصَمُوا بِاللّٰهِ وَاَخْلَصُوا د۪ينَهُمْ لِلّٰهِ فَاُو۬لٰٓئِكَ مَعَ الْمُؤْمِن۪ينَۜ وَسَوْفَ يُؤْتِ اللّٰهُ الْمُؤْمِن۪ينَ اَجْراً عَظ۪يماً", translation: "Ancak tevbe edip, kendilerini düzeltenler, Allah'ın buyruklarına sımsıkı sarılanlar, dinlerini yalnızca Allah'a has kılanlar hariç. İşte bunlar, Mü'minlerle beraberdirler. Allah, zamanı geldiğinde, Mü'minlere büyük bir ödül verecektir.", transliteration: "İllallezine tabu ve aslehu va'tesamu billahi ve ahlesu dinehum lillahi fe ulaike meal mu'minin. Ve sevfe yu'tillahul mu'minine ecran azima."},
  {id: 640, sure_no: 147, ayet_no: 147, sure_adi: "Nisa", text: "مَا يَفْعَلُ اللّٰهُ بِعَذَابِكُمْ اِنْ شَكَرْتُمْ وَاٰمَنْتُمْۜ وَكَانَ اللّٰهُ شَاكِراً عَل۪يماً", translation: "Eğer şükreder ve iman ederseniz, Allah size neden azap etsin? Allah Şakir'dir. Her Şeyi Bilen'dir.", transliteration: "Ma yef'alullahu bi azabikum in şekertum ve amentum. Ve kanallahu şakiran alima."},
  {id: 641, sure_no: 148, ayet_no: 148, sure_adi: "Nisa", text: "لَا يُحِبُّ اللّٰهُ الْجَهْرَ بِالسُّٓوءِ مِنَ الْقَوْلِ اِلَّا مَنْ ظُلِمَۜ وَكَانَ اللّٰهُ سَم۪يعاً عَل۪يماً", translation: "Allah, kendisine haksızlık yapılan kişinin dışında, kötü sözün açıkça dillendirilmesini sevmez. Kuşkusuz, Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "La yuhibbullahul cehra bis sui minel kavli illa men zulim. Ve kanallahu semian alima."},
  {id: 642, sure_no: 149, ayet_no: 149, sure_adi: "Nisa", text: "اِنْ تُبْدُوا خَيْراً اَوْ تُخْفُوهُ اَوْ تَعْفُوا عَنْ سُٓوءٍ فَاِنَّ اللّٰهَ كَانَ عَفُواًّ قَد۪يراً", translation: "Eğer bir iyiliği açıklar veya gizlerseniz veya bir kötülüğü bağışlarsanız bilin ki, Allah da Çok Affedici'dir, Her Şeye Güç Yetiren'dir.", transliteration: "İn tubdu hayran ev tuhfuhu ev ta'fu an suin fe innallahe kane afuvven kadira."},
  {id: 643, sure_no: 150, ayet_no: 150, sure_adi: "Nisa", text: "اِنَّ الَّذ۪ينَ يَكْفُرُونَ بِاللّٰهِ وَرُسُلِه۪ وَيُر۪يدُونَ اَنْ يُفَرِّقُوا بَيْنَ اللّٰهِ وَرُسُلِه۪ وَيَقُولُونَ نُؤْمِنُ بِبَعْضٍ وَنَكْفُرُ بِبَعْضٍۙ وَيُر۪يدُونَ اَنْ يَتَّخِذُوا بَيْنَ ذٰلِكَ سَب۪يلاًۙ", translation: "Allah'ı ve Resullerini küfredenler, Allah ile Resullerinin arasını ayırmak isterler: \"Bir kısmına iman eder bir kısmına iman etmeyiz.\" derler. Böylece, arada bir yol tutmak isterler.", transliteration: "İnnellezine yekfurune billahi ve rusulihi ve yuridune en yuferriku beynallahi ve rusulihi ve yekulune nu'minu bi ba'din ve nekfuru bi ba'dın, ve yuridune en yettehızu beyne zalike sebila."},
  {id: 644, sure_no: 151, ayet_no: 151, sure_adi: "Nisa", text: "اُو۬لٰٓئِكَ هُمُ الْكَافِرُونَ حَقاًّۚ وَاَعْتَدْنَا لِلْكَافِر۪ينَ عَذَاباً مُه۪يناً", translation: "İşte onlar gerçekten, Kafirlerdir. Biz de Kafirlere alçaltıcı bir azap hazırladık.", transliteration: "Ulaike humul kafirune hakka, ve a'tedna lil kafirine azaben muhina."},
  {id: 645, sure_no: 152, ayet_no: 152, sure_adi: "Nisa", text: "وَالَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَرُسُلِه۪ وَلَمْ يُفَرِّقُوا بَيْنَ اَحَدٍ مِنْهُمْ اُو۬لٰٓئِكَ سَوْفَ يُؤْت۪يهِمْ اُجُورَهُمْۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً۟", translation: "O kimseler ki Allah'a ve Resullerine iman edip, Resuller arasında hiçbir ayırım yapmazlar. İşte onlara, gelecekte ödülleri verilecektir. Kuşkusuz, Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Vellezine amenu billahi ve rusulihi ve lem yuferriku beyne ehadin minhum ulaike sevfe yu'tihim ucurahum. Ve kanallahu gafuran rahima."},
  {id: 646, sure_no: 153, ayet_no: 153, sure_adi: "Nisa", text: "يَسْـَٔلُكَ اَهْلُ الْكِتَابِ اَنْ تُنَزِّلَ عَلَيْهِمْ كِتَاباً مِنَ السَّمَٓاءِ فَقَدْ سَاَلُوا مُوسٰٓى اَكْبَرَ مِنْ ذٰلِكَ فَقَالُٓوا اَرِنَا اللّٰهَ جَهْرَةً فَاَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْۚ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْ بَعْدِ مَا جَٓاءَتْهُمُ الْبَيِّنَاتُ فَعَفَوْنَا عَنْ ذٰلِكَۚ وَاٰتَيْنَا مُوسٰى سُلْطَاناً مُب۪يناً", translation: "Kitap Ehli, senden, kendilerine gökten bir kitap indirmeni istiyorlar. Daha önce Musa'dan bundan daha büyüğünü istemişlerdi: \"Bize Allah'ı açıkça göster.\" demişlerdi. Bu haksızlıklarından dolayı, onları yıldırım çarpmıştı. Sonra kendilerine apaçık kanıtlar içeren bilgiler geldiği halde yine de buzağıyı ilah edindiler. Biz onları bağışladık ve Musa'ya apaçık bir yetki verdik.", transliteration: "Yes'eluke ehlul kitabi en tunezzile aleyhim kitaben mines semai fe kad seelu musa ekbera min zalike fe kalu erinallahe cehraten fe ehazethumus saikatu bi zulmihim, summettehazul ıcle min ba'di ma caethumul beyyinatu fe afevna an zalik, ve ateyna musa sultanen mubina."},
  {id: 647, sure_no: 154, ayet_no: 154, sure_adi: "Nisa", text: "وَرَفَعْنَا فَوْقَهُمُ الطُّورَ بِم۪يثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّداً وَقُلْنَا لَهُمْ لَا تَعْدُوا فِي السَّبْتِ وَاَخَذْنَا مِنْهُمْ م۪يثَاقاً غَل۪يظاً", translation: "Kesin söz vermelerinden dolayı Tur'u üzerlerine yükseltmiştik. Onlara, \"Kapıdan secde ederek girin.\" dedik. Yine onlara, \"Cumartesi'nde haddi aşmayın.\" dedik. Biz, onlardan kesin söz aldık.", transliteration: "Ve rafa'na fevkahumut tura bi misakıhim ve kulna lehumudhulul babe succeden ve kulna lehum la ta'du fis sebti ve ehazna minhum misakan galiza."},
  {id: 648, sure_no: 155, ayet_no: 155, sure_adi: "Nisa", text: "فَبِمَا نَقْضِهِمْ م۪يثَاقَهُمْ وَكُفْرِهِمْ بِاٰيَاتِ اللّٰهِ وَقَتْلِهِمُ الْاَنْبِيَٓاءَ بِغَيْرِ حَقٍّ وَقَوْلِهِمْ قُلُوبُنَا غُلْفٌۜ بَلْ طَبَعَ اللّٰهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ اِلَّا قَل۪يلاًۖ", translation: "Verdikleri sözü bozdukları, Allah'ın ayetlerini küfrettikleri, nebileri haksız yere öldürdükleri ve \"Bizim kalplerimiz örtülüdür.\" dedikleri için; evet Allah, küfürlerinden dolayı, onların kalplerini mühürlemiştir. Bu nedenle pek azı hariç, iman etmezler.", transliteration: "Fe bima nakdıhim misakahum ve kufrihim bi ayatillahi ve katlihimul enbiyae bi gayrı hakkın ve kavlihim kulubuna gulf. Bel tabaallahu aleyha bi kufrihim fe la yu'minune illa kalila."},
  {id: 649, sure_no: 156, ayet_no: 156, sure_adi: "Nisa", text: "وَبِكُفْرِهِمْ وَقَوْلِهِمْ عَلٰى مَرْيَمَ بُهْتَاناً عَظ۪يماًۙ", translation: "Küfretmeleri ve Meryem'e büyük bir iftira atmaları;", transliteration: "Ve bi kufrihim ve kavlihim ala meryeme buhtanen azima."},
  {id: 650, sure_no: 157, ayet_no: 157, sure_adi: "Nisa", text: "وَقَوْلِهِمْ اِنَّا قَتَلْنَا الْمَس۪يحَ ع۪يسَى ابْنَ مَرْيَمَ رَسُولَ اللّٰهِۚ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلٰكِنْ شُبِّهَ لَهُمْۜ وَاِنَّ الَّذ۪ينَ اخْتَلَفُوا ف۪يهِ لَف۪ي شَكٍّ مِنْهُۜ مَا لَهُمْ بِه۪ مِنْ عِلْمٍ اِلَّا اتِّبَاعَ الظَّنِّۚ وَمَا قَتَلُوهُ يَق۪يناًۙ", translation: "Ve yine Allah'ın Resulü Meryem Oğlu İsa Mesih'i, \"Kesinlikle biz öldürdük.\" demeleri nedeniyle. Aslında onu öldürmediler ve onu asmadılar da. Fakat kendilerine öyle göründü. Onlar, herhangi bir bilgi sahibi olmadıklarından, ayrılığa düştükleri bu konuda kesin olarak şüphe içindedirler. Onlar, sadece zanna uyuyorlar. Kesin olan şu ki, onu öldürmediler.", transliteration: "Ve kavlihim inna katelnal mesiha isabne meryeme resulallah, ve ma kateluhu ve ma salebuhu ve lakin şubbihe lehum. Ve innellezinahtelefu fihi le fi şekkin minhu. Ma lehum bihi min ilmin illattibaaz zann, ve ma kateluhu yakina."},
  {id: 651, sure_no: 158, ayet_no: 158, sure_adi: "Nisa", text: "بَلْ رَفَعَهُ اللّٰهُ اِلَيْهِۜ وَكَانَ اللّٰهُ عَز۪يزاً حَك۪يماً", translation: "Aksine Allah, onu kendisine yükseltti. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Bel rafaahullahu ileyh. Ve kanallahu azizen hakima."},
  {id: 652, sure_no: 159, ayet_no: 159, sure_adi: "Nisa", text: "وَاِنْ مِنْ اَهْلِ الْكِتَابِ اِلَّا لَيُؤْمِنَنَّ بِه۪ قَبْلَ مَوْتِه۪ۚ وَيَوْمَ الْقِيٰمَةِ يَكُونُ عَلَيْهِمْ شَه۪يداًۚ", translation: "Kitap Ehl-i'nden her biri ölümünden önce ona inanmak zorundadır. Kıyamet günü, O, onlar hakkında tanık olur.", transliteration: "Ve in min ehlil kitabi illa le yu'minenne bihi kable mevtihi, ve yevmel kıyameti yekunu aleyhim şehida."},
  {id: 653, sure_no: 160, ayet_no: 160, sure_adi: "Nisa", text: "فَبِظُلْمٍ مِنَ الَّذ۪ينَ هَادُوا حَرَّمْنَا عَلَيْهِمْ طَيِّبَاتٍ اُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَب۪يلِ اللّٰهِ كَث۪يراًۙ", translation: "Böylece, zulümleri ve Allah yolundan çokça alıkoymaları nedeniyle daha önce helal olan temiz birçok şeyi Yahudilere haram kıldık.", transliteration: "Fe bi zulmin minellezine hadu harramna aleyhim tayyibatin uhıllet lehum ve bi saddihim an sebilillahi kesira."},
  {id: 654, sure_no: 161, ayet_no: 161, sure_adi: "Nisa", text: "وَاَخْذِهِمُ الرِّبٰوا وَقَدْ نُهُوا عَنْهُ وَاَكْلِهِمْ اَمْوَالَ النَّاسِ بِالْبَاطِلِۜ وَاَعْتَدْنَا لِلْكَافِر۪ينَ مِنْهُمْ عَذَاباً اَل۪يماً", translation: "Yine, yasaklandığı halde, onlardan riba alan ve haksız yoldan insanların mallarını yiyen kafirlere; çok acı bir azap hazırladık.", transliteration: "Ve ahzihimur riba ve kad nuhu anhu ve eklihim emvalen nasi bil batıl. Ve a'tedna lil kafirine minhum azaben elima."},
  {id: 655, sure_no: 162, ayet_no: 162, sure_adi: "Nisa", text: "لٰكِنِ الرَّاسِخُونَ فِي الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُونَ يُؤْمِنُونَ بِمَٓا اُنْزِلَ اِلَيْكَ وَمَٓا اُنْزِلَ مِنْ قَبْلِكَ وَالْمُق۪يم۪ينَ الصَّلٰوةَ وَالْمُؤْتُونَ الزَّكٰوةَ وَالْمُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ اُو۬لٰٓئِكَ سَنُؤْت۪يهِمْ اَجْراً عَظ۪يماً۟", translation: "Ancak, onlardan ilimde derinleşmiş olanlar ve Mü'minler, sana ve senden önce indirilene iman ederler. Salatı ikame edenler, zekatı yapanlar, Allah'a ve ahiret gününe iman edenler; işte onlara büyük bir ödül vereceğiz.", transliteration: "Lakinir rasihune fil ilmi minhum vel mu'minune yu'minune bi ma unzile ileyke ve ma unzile min kablike vel mukimines salate vel mu'tunez zekate vel mu'minune billahi vel yevmil ahir. Ulaike se nu'tihim ecran azima."},
  {id: 656, sure_no: 163, ayet_no: 163, sure_adi: "Nisa", text: "اِنَّٓا اَوْحَيْنَٓا اِلَيْكَ كَمَٓا اَوْحَيْنَٓا اِلٰى نُوحٍ وَالنَّبِيّ۪نَ مِنْ بَعْدِه۪ۚ وَاَوْحَيْنَٓا اِلٰٓى اِبْرٰه۪يمَ وَاِسْمٰع۪يلَ وَاِسْحٰقَ وَيَعْقُوبَ وَالْاَسْبَاطِ وَع۪يسٰى وَاَيُّوبَ وَيُونُسَ وَهٰرُونَ وَسُلَيْمٰنَۚ وَاٰتَيْنَا دَاوُ۫دَ زَبُوراًۚ", translation: "Biz, Nuh'a ve ondan sonraki bütün Nebilere vahyettiğimiz gibi, sana da vahyettik. Ve biz İbrahim'e, İsmail'e, İshak'a, Ya'kup'a, torunlarına, İsa'ya, Eyyub'a, Yunus'a, Harun'a ve Süleyman'a vahyettik. Ve Davud'a da Zebur'u verdik.", transliteration: "İnna evhayna ileyke kema evhayna ila nuhin ven nebiyyine min ba'dihi, ve evhayna ila ibrahime ve ismaile ve ishaka ve ya'kube vel esbati ve isa ve eyyube ve yunuse ve harune ve suleyman, ve ateyna davude zebura."},
  {id: 657, sure_no: 164, ayet_no: 164, sure_adi: "Nisa", text: "وَرُسُلاً قَدْ قَصَصْنَاهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلاً لَمْ نَقْصُصْهُمْ عَلَيْكَۜ وَكَلَّمَ اللّٰهُ مُوسٰى تَكْل۪يماًۚ", translation: "Senden önce gönderdiğimiz Resullerden birçoğunu sana anlattık, birçoğunu da anlatmadık. Ve Allah, Musa'yla kelimelerle konuştu.", transliteration: "Ve rusulen kad kasasnahum aleyke min kablu ve rusulen lem naksushum aleyk. Ve kellemallahu musa teklima."},
  {id: 658, sure_no: 165, ayet_no: 165, sure_adi: "Nisa", text: "رُسُلاً مُبَشِّر۪ينَ وَمُنْذِر۪ينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى اللّٰهِ حُجَّةٌ بَعْدَ الرُّسُلِۜ وَكَانَ اللّٰهُ عَز۪يزاً حَك۪يماً", translation: "Haber verici ve uyarıcı olarak Resuller gönderdik ki, Resullerden sonra insanların Allah'a karşı bir bahaneleri olmasın. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Rusulen mubeşşirine ve munzirine li ella yekune lin nasi alallahi huccetun ba'der rusul. Ve kanallahu azizen hakima."},
  {id: 659, sure_no: 166, ayet_no: 166, sure_adi: "Nisa", text: "لٰكِنِ اللّٰهُ يَشْهَدُ بِمَٓا اَنْزَلَ اِلَيْكَ اَنْزَلَهُ بِعِلْمِه۪ۚ وَالْمَلٰٓئِكَةُ يَشْهَدُونَۜ وَكَفٰى بِاللّٰهِ شَه۪يداً", translation: "Allah, sana indirdiği şeyi, Kendi ilmi ile indirdiğine tanıklık eder. Melekler de tanıklık ederler. Ve tabii ki tanık olarak Allah yeter.", transliteration: "Lakinillahu yeşhedu bi ma enzele ileyke enzelehu bi ılmihi, vel melaiketu yeşhedun. Ve kefa billahi şehida."},
  {id: 660, sure_no: 167, ayet_no: 167, sure_adi: "Nisa", text: "اِنَّ الَّذ۪ينَ كَفَرُوا وَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ قَدْ ضَلُّوا ضَلَالاً بَع۪يداً", translation: "Kuşkusuz Kafir olanlar ve Allah'ın yolundan alıkoyanlar, gerçekten derin bir sapkınlıktadırlar.", transliteration: "İnnellezine keferu ve saddu an sebilillahi kad dallu dalalen baida."},
  {id: 661, sure_no: 168, ayet_no: 168, sure_adi: "Nisa", text: "اِنَّ الَّذ۪ينَ كَفَرُوا وَظَلَمُوا لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَر۪يقاًۙ", translation: "Kuşkusuz Allah Kafir olanları ve zulmedenleri bağışlayacak değildir. Onları bir yola iletecek de değildir;", transliteration: "İnnellezine keferu ve zalemu lem yekunillahu li yagfira lehum ve la li yehdiyehum tarika."},
  {id: 662, sure_no: 169, ayet_no: 169, sure_adi: "Nisa", text: "اِلَّا طَر۪يقَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يراً", translation: "Ancak Cehennem yolundan başka. Orada, kesintisiz ve ebedi olarak kalıcıdırlar. Bu, Allah için çok kolaydır.", transliteration: "İlla tarika cehenneme halidine fiha ebeda. Ve kane zalike alallahi yesira."},
  {id: 663, sure_no: 170, ayet_no: 170, sure_adi: "Nisa", text: "يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَكُمُ الرَّسُولُ بِالْحَقِّ مِنْ رَبِّكُمْ فَاٰمِنُوا خَيْراً لَكُمْۜ وَاِنْ تَكْفُرُوا فَاِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماً", translation: "Ey insanlar! Resul size Rabb'inizden gerçeği getirdi. Öyleyse kendi iyiliğiniz için ona iman edin. Eğer kafirlik ederseniz, bilin ki gökte ve yerde olan her şey Allah'a aittir. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ya eyyuhan nasu kad caekumur resulu bil hakkı min rabbikum fe aminu hayran lekum. Ve in tekfuru fe inne lillahi ma fis semavati vel ard. Ve kanallahu alimen hakima."},
  {id: 664, sure_no: 171, ayet_no: 171, sure_adi: "Nisa", text: "يَٓا اَهْلَ الْكِتَابِ لَا تَغْلُوا ف۪ي د۪ينِكُمْ وَلَا تَقُولُوا عَلَى اللّٰهِ اِلَّا الْحَقَّۜ اِنَّمَا الْمَس۪يحُ ع۪يسَى ابْنُ مَرْيَمَ رَسُولُ اللّٰهِ وَكَلِمَتُهُۚ اَلْقٰيهَٓا اِلٰى مَرْيَمَ وَرُوحٌ مِنْهُۘ فَاٰمِنُوا بِاللّٰهِ وَرُسُلِه۪ۚ وَلَا تَقُولُوا ثَلٰثَةٌۜ اِنْتَهُوا خَيْراً لَكُمْۜ اِنَّمَا اللّٰهُ اِلٰهٌ وَاحِدٌۜ سُبْحَانَهُٓ اَنْ يَكُونَ لَهُ وَلَدٌۢ لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً۟", translation: "Ey Kitap Ehli! Dininiz hakkında haddi aşmayın. Allah hakkında, gerçek olandan başka bir şey söylemeyin. Allah'ın Resulü İsa Mesih, Meryem'in oğludur. Ve o, Allah'ın Meryem'e attığı kelime ve Kendisinden bir ruhtur. O halde Allah'a ve Resullerine iman edin. Ve \"Üçtür.\" demeyin. Buna son verin. Bu, sizin için hayırlı olandır. Kuşkusuz Allah, tek bir ilahtır. O, çocuk sahibi olmaktan münezzehtir. Göklerde ve yerde ne varsa O'nundur. Vekil olarak Allah yeter.", transliteration: "Ya ehlel kitabi la taglu fi dinikum ve la tekulu alallahi illal hakk. İnnemal mesihu isabnu meryeme resulullahi ve kelimetuhu.  Elkaha ila meryeme ve ruhun minhu, fe aminu billahi ve rusulihi, ve la tekulu selaseh. İntehu hayran lekum. İnnemallahu ilahun vahid. Subhanehu en yekune lehu veled, lehu ma fis semavati ve ma fil ard. Ve kefa billahi vekila."},
  {id: 665, sure_no: 172, ayet_no: 172, sure_adi: "Nisa", text: "لَنْ يَسْتَنْكِفَ الْمَس۪يحُ اَنْ يَكُونَ عَبْداً لِلّٰهِ وَلَا الْمَلٰٓئِكَةُ الْمُقَرَّبُونَۜ وَمَنْ يَسْتَنْكِفْ عَنْ عِبَادَتِه۪ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ اِلَيْهِ جَم۪يعاً", translation: "Ne Mesih ne de mukarrabin melekler Allah'a kul olmaktan kaçınmazlar. Kim büyüklenerek O'na kulluk etmekten kaçınırsa, bilsin ki O, yakında onların tamamını huzuruna toplayacaktır.", transliteration: "Len yestenkifel mesihu en yekune abden lillahi ve lal melaiketul mukarrabun. Ve men yestenkif an ibadetihi ve yestekbir fe se yahşuruhum ileyhi cemia."},
  {id: 666, sure_no: 173, ayet_no: 173, sure_adi: "Nisa", text: "فَاَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفّ۪يهِمْ اُجُورَهُمْ وَيَز۪يدُهُمْ مِنْ فَضْلِه۪ۚ وَاَمَّا الَّذ۪ينَ اسْتَنْكَفُوا وَاسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذَاباً اَل۪يماًۙ وَلَا يَجِدُونَ لَهُمْ مِنْ دُونِ اللّٰهِ وَلِياًّ وَلَا نَص۪يراً", translation: "Ama iman edip, salihatı yapan kimselerin, yaptıklarının karşılığı eksiksiz verilecektir. Ve Kendi fazlından daha da artıracaktır. Ama büyüklük taslayan ve kibirlenen kimselere gelince, onlara can yakıcı bir azap ile azap edecek. Ve onlar, Allah'tan başka ne bir yardımcı ne de bir veli bulabilirler.", transliteration: "Fe emmallezine amenu ve amilus salihati fe yuveffihim ucurahum ve yeziduhum min fadlihi, ve emmallezinestenkefu vestekberu fe yuazzibuhum azaben elimen, ve la yecidune lehum min dunillahi veliyyen ve la nasira."},
  {id: 667, sure_no: 174, ayet_no: 174, sure_adi: "Nisa", text: "يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَكُمْ بُرْهَانٌ مِنْ رَبِّكُمْ وَاَنْزَلْـنَٓا اِلَيْكُمْ نُوراً مُب۪يناً", translation: "Ey insanlar! Kesinlikle Rabb'inizden size bir \"burhan\" geldi. Ve size apaçık bir nur indirdik.", transliteration: "Ya eyyuhan nasu kad caekum burhanun min rabbikum ve enzelna ileykum nuran mubin."},
  {id: 668, sure_no: 175, ayet_no: 175, sure_adi: "Nisa", text: "فَاَمَّا الَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَاعْتَصَمُوا بِه۪ فَسَيُدْخِلُهُمْ ف۪ي رَحْمَةٍ مِنْهُ وَفَضْلٍۙ وَيَهْد۪يهِمْ اِلَيْهِ صِرَاطاً مُسْتَق۪يماًۜ", translation: "Ama Allah'a inanıp ona sımsıkı tutunanlara gelince, onları, yakında Kendi lütfundan, rahmetinin içine koyacak ve onları kendisine varan dosdoğru yola iletecektir.", transliteration: "Fe emmallezine amenu billahi va'tesamu bihi fe se yudhıluhum fi rahmetin minhu ve faldın, ve yehdihim ileyhi sıratan mustekima ."},
  {id: 669, sure_no: 176, ayet_no: 176, sure_adi: "Nisa", text: "يَسْتَفْتُونَكَۜ قُلِ اللّٰهُ يُفْت۪يكُمْ فِي الْكَلَالَةِۜ اِنِ امْرُؤٌا هَلَكَ لَيْسَ لَهُ وَلَدٌ وَلَهُٓ اُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَۚ وَهُوَ يَرِثُـهَٓا اِنْ لَمْ يَكُنْ لَهَا وَلَدٌۜ فَاِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثَانِ مِمَّا تَرَكَۜ وَاِنْ كَانُٓوا اِخْوَةً رِجَالاً وَنِسَٓاءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِۜ يُبَيِّنُ اللّٰهُ لَكُمْ اَنْ تَضِلُّواۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Sana soruyorlar. De ki: \"Allah kelale hakkında size hükmünü veriyor: Ölen herhangi bir kimsenin çocuğu yoksa yalnızca bir kız kardeşi varsa, mirasın yarısı onundur. Kız kardeşi ölür de çocuğu da yoksa erkek kardeşi onun malına varis olur. Kız kardeş, iki taneyse mirasın üçte ikisi onlarındır. Eğer erkek ve kadın çok kardeşlerse, o zaman erkeğe iki kadın payı vardır. Şaşırıp sapıtmamanız için, Allah, size açıklıyor. Ve Allah Her Şeyi En İyi Bilen'dir.\"", transliteration: "Yesteftuneke. Kulillahu yuftikum fil kelaleh. İnimruun heleke leyse lehu veled, ve lehu uhtun fe leha nısfu ma terak, ve huve yerisuha in lem yekun leha veled. Fe in kanetesneteyni fe lehumas sulusani mimma terak. Ve in kanu ıhveten ricalen ve nisaen fe liz zekeri mislu hazzıl unseyeyn. Yubeyyinullahu lekum en tadıllu vallahu bi kulli şey'in alim."},
  {id: 670, sure_no: 1, ayet_no: 1, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَوْفُوا بِالْعُقُودِۜ  اُحِلَّتْ لَكُمْ بَه۪يمَةُ الْاَنْعَامِ اِلَّا مَا يُتْلٰى عَلَيْكُمْ غَيْرَ مُحِلِّي الصَّيْدِ وَاَنْتُمْ حُرُمٌۜ اِنَّ اللّٰهَ يَحْكُمُ مَا يُر۪يدُ", translation: "Ey İman Edenler! Sözlerinizi yerine getirin. İhramlı iken helal saymamanız şartı ile okunacak olanların dışında kalan en'am size helal kılındı Kuşkusuz, Allah, dilediği hükmü verir.", transliteration: "Ya eyyuhellezine amenu evfu bil ukud uhıllet lekum behimetul en'ami illa ma yutla aleykum gayre muhillis saydi ve entum hurum innallahe yahkumu ma yurid."},
  {id: 671, sure_no: 2, ayet_no: 2, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُحِلُّوا شَعَٓائِرَ اللّٰهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَٓائِدَ وَلَٓا آٰمّ۪ينَ الْبَيْتَ الْحَرَامَ يَبْتَغُونَ فَضْلاً مِنْ رَبِّهِمْ وَرِضْوَاناًۜ وَاِذَا حَلَلْتُمْ فَاصْطَادُواۜ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ اَنْ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ اَنْ تَعْتَدُواۢ وَتَعَاوَنُوا عَلَى الْبِرِّ وَالتَّقْوٰىۖ وَلَا تَعَاوَنُوا عَلَى الْاِثْمِ وَالْعُدْوَانِۖ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ", translation: "Ey İman Edenler! Allah'ın işaretlerine, haram aya, kurbana, gerdanlıklara; Rabb'lerinin lütuf ve rızasını dileyerek Beyt-i Haram'a gelenlere saygısızlık etmeyin. İhramdan çıktığınız zaman avlanabilirsiniz. Sizi, Mescid-i Haram'dan alıkoymalarından dolayı bir halka olan kızgınlığınız, aşırı gitmenize neden olmasın. Günah ve düşmanlık üzerinde değil, iyilik ve takva üzerinde yardımlaşın. Allah'a karşı takvalı olun. Kuşkusuz Allah'ın azabı çok şiddetlidir.", transliteration: "Ya eyyuhellezine amenu la tuhıllu şe'airallahi veleş şehral harame ve lal hedye ve lal kalaide ve la amminel beytel harame yebtegune fadlan min rabbihim ve rıdvana ve iza haleltum fastadu ve la yecrimennekum şeneanu kavmin en saddukum anil mescidil harami en ta'tedu, ve teavenu alel birri vet takva ve la teavenu alel ismi vel udvani vettekullah innallahe şedidul ıkab."},
  {id: 672, sure_no: 3, ayet_no: 3, sure_adi: "Maide", text: "حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْز۪يرِ وَمَٓا اُهِلَّ لِغَيْرِ اللّٰهِ بِه۪ وَالْمُنْخَنِقَةُ وَالْمَوْقُوذَةُ وَالْمُتَرَدِّيَةُ وَالنَّط۪يحَةُ وَمَٓا اَكَلَ السَّبُعُ اِلَّا مَا ذَكَّيْتُمْ وَمَا ذُبِحَ عَلَى النُّصُبِ وَاَنْ تَسْتَقْسِمُوا بِالْاَزْلَامِۜ ذٰلِكُمْ فِسْقٌۜ  اَلْيَوْمَ يَـئِسَ الَّذ۪ينَ كَفَرُوا مِنْ د۪ينِكُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِۜ اَلْيَوْمَ اَكْمَلْتُ لَكُمْ د۪ينَكُمْ وَاَتْمَمْتُ عَلَيْكُمْ نِعْمَت۪ي وَرَض۪يتُ لَكُمُ الْاِسْلَامَ د۪يناًۜ فَمَنِ اضْطُرَّ ف۪ي مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لِاِثْمٍۙ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Ölü, kan, domuz eti, Allah'tan başkasının adı anılarak kesilen ve boğularak ölmüş, vurularak ölmüş, yüksekten düşerek ölmüş, boynuzlanarak ölmüş; yırtıcı hayvan tarafından yenmiş -canı çıkmadan kesilmiş olanlar hariç- ve putlar adına kesilenler haram kılındı. Fal okları ile kısmet aramanız size haram kılındı. Bunlar fısktır. Artık, Kafirler, dininize bir şey yapmaktan umutlarını kesmişlerdir. Öyleyse, onlara huşu duymayın. Bana huşu duyun. Bugün, sizin için dininizi kemale erdirdim/ikmal ettim ve sizin üzerinize nimetimi tamamladım. Sizin için din olarak İslam'ı seçtim/ beğendim. Kim açlıktan dolayı mecbur kalırsa, istekli olmaksızın bunlardan yiyebilir. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Hurrimet aleykumul meytetu veddemu ve lahmul hınziri ve ma uhılle li gayrillahi bihi vel munhanikatu vel mevkuzetu vel mutereddiyetu ven natihatu ve ma ekeles sebuu illa ma zekkeytum ve ma zubiha alen nusubi ve en testaksimu bil ezlam, zalikum fisk, elyevme yeisellezine keferu min dinikum fe la tahşevhum vahşevn el yevme ekmeltu lekum dinekum ve etmemtu aleykum ni'meti ve raditu lekumul islame dina fe menidturra fi mahmasatin gayra mutecanifin li ismin fe innallahe gafurun rahim."},
  {id: 673, sure_no: 4, ayet_no: 4, sure_adi: "Maide", text: "يَسْـَٔلُونَكَ مَاذَٓا اُحِلَّ لَهُمْۜ قُلْ اُحِلَّ لَكُمُ الطَّيِّبَاتُۙ وَمَا عَلَّمْتُمْ مِنَ الْجَوَارِحِ مُكَلِّب۪ينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اللّٰهُۘ فَكُلُوا مِمَّٓا اَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهِۖ وَاتَّقُوا اللّٰهَۜ  اِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ", translation: "Sana, kendilerine neyin helal olduğunu soruyorlar. De ki: \"Temiz ve iyi olanlar, size helal kılındı. Ve Allah'ın size verdiği bilgi ile eğittiğiniz eğitimli av hayvanlarının yakaladıklarını yiyin. Ve Allah'ın adını onun üzerine anın. Allah'a karşı takvalı olun. Kuşkusuz, Allah, Hesabı Çok Çabuk Gören'dir.\"", transliteration: "Yes'eluneke ma za uhılle lehum kul uhılle lekumut tayyibatu ve ma allemtum minel cevarihi mukellibine tuallimunehunne mimma allemekumullahu fe kulu mimma emsekne aleykum vezkurusmellahi aleyhi vettekullah innallahe seriul hısab."},
  {id: 674, sure_no: 5, ayet_no: 5, sure_adi: "Maide", text: "اَلْيَوْمَ اُحِلَّ لَكُمُ الطَّيِّبَاتُۜ وَطَعَامُ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ حِلٌّ لَكُمْۖ وَطَعَامُكُمْ حِلٌّ لَهُمْۘ وَالْمُحْصَنَاتُ مِنَ الْمُؤْمِنَاتِ وَالْمُحْصَنَاتُ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ اِذَٓا اٰتَيْتُمُوهُنَّ اُجُورَهُنَّ مُحْصِن۪ينَ غَيْرَ مُسَافِح۪ينَ وَلَا مُتَّخِذ۪ٓي اَخْدَانٍۜ وَمَنْ يَكْفُرْ بِالْا۪يمَانِ فَقَدْ حَبِطَ عَمَلُهُۘ وَهُوَ فِي الْاٰخِرَةِ مِنَ الْخَاسِر۪ينَ۟", translation: "Bugün size, temiz şeyler helal kılındı. Ve Kitap verilenlerin yiyecekleri size, sizin yiyecekleriniz de onlara helaldir. İman eden muhsenat kadınlar ve sizden önce kendilerine Kitap verilenlerin muhsenat kadınları; karşılıklarını vermeniz, iffetli olmanız, iffetsiz olmamanız ve gizli dost tutmamanız koşuluyla size helaldir. Kim imanı küfrederse yaptığı işler boşa gider ve o, ahirette hüsrana uğrayanlardan olur.", transliteration: "El yevme uhılle lekumut tayyibat ve taamullezine utul kitabe hıllun lekum ve taamukum hıllun lehum vel muhsanatu minel mu'minati vel muhsanatu min ellezine utul kitabe min kablikum iza ateytumuhunne ucurehunne muhsınine gayra musafihine ve la muttehızi ehdan ve men yekfur bil imani fe kad habita ameluhu ve huve fil ahıreti minel hasirin."},
  {id: 675, sure_no: 6, ayet_no: 6, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا قُمْتُمْ اِلَى الصَّلٰوةِ فَاغْسِلُوا وُجُوهَكُمْ وَاَيْدِيَكُمْ اِلَى الْمَرَافِقِ وَامْسَحُوا بِرُؤُ۫سِكُمْ وَاَرْجُلَكُمْ اِلَى الْكَعْبَيْنِۜ وَاِنْ كُنْتُمْ جُنُباً فَاطَّهَّرُواۜ وَاِنْ كُنْتُمْ مَرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَٓاءَ اَحَدٌ مِنْكُمْ مِنَ الْغَٓائِطِ اَوْ لٰمَسْتُمُ النِّسَٓاءَ فَلَمْ تَجِدُوا مَٓاءً فَتَيَمَّمُوا صَع۪يداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَاَيْد۪يكُمْ مِنْهُۜ مَا يُر۪يدُ اللّٰهُ لِيَجْعَلَ عَلَيْكُمْ مِنْ حَرَجٍ وَلٰكِنْ يُر۪يدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Ey İman Edenler! Salata durduğunuz zaman, yüzlerinizi ve ellerinizi -dirseklerinizle beraber- yıkayın. Başlarınızı ve -aşık kemiklerinizle beraber- ayaklarınızı mesh edin. Eğer cünüpseniz; tam olarak temizlenin. Eğer hasta veya yolcukta iseniz veya tuvaletten gelmişseniz veya kadınlarınızla ilişkiye girdiyseniz ve o anda su bulamadıysanız, temiz kumla teyemmüm edin; onunla ellerinizi ve yüzlerinizi mesh edin. Allah size herhangi bir zorluk dilemiyor. Ancak sizi tertemiz etmek ve üzerinize nimetini tamamlamak istiyor. Umulur ki şükredersiniz.", transliteration: "Ya eyyuhellezine amenu iza kumtum iles salati fagsilu vucuhekum ve eydiyekum ilel merafikı vemsehu bi ruusikum ve erculekum ilal ka'beyn ve in kuntum cunuben fattahheru ve in kuntum marda ev ala seferin ev cae ehadun minkum minel gaitı ev lamestumun nisae fe lem tecidu maen fe teyemmemu saiden tayyiben femsehu bi vucuhikum ve eydikum minh ma yuridullahu li yec'ale aleykum min haracin ve lakin yuridu li yutahhirekum ve li yutimme ni'metehu aleykum leallekum teşkurun."},
  {id: 676, sure_no: 7, ayet_no: 7, sure_adi: "Maide", text: "وَاذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ وَم۪يثَاقَهُ الَّذ۪ي وَاثَقَكُمْ بِه۪ٓۙ اِذْ قُلْتُمْ سَمِعْنَا وَاَطَعْنَاۘ وَاتَّقُوا اللّٰهَۜ  اِنَّ اللّٰهَ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Allah'ın, size olan nimetini ve \"İşittik ve itaat ettik.\" dediğinizde, onunla sizi bağladığı sözünüzü hatırlayın. Allah'a karşı takvalı olun. Kuşkusuz, Allah göğüslerde olanı gerçeğiyle bilendir.", transliteration: "Vezkuru ni'metellahi aleykum ve misakahullezi vasekakum bihi iz kultum semi'na ve ata'na vettekullah innallahe alimun bizatis sudur."},
  {id: 677, sure_no: 8, ayet_no: 8, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُونُوا قَوَّام۪ينَ لِلّٰهِ شُهَدَٓاءَ بِالْقِسْطِۘ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ عَلٰٓى اَلَّا تَعْدِلُواۜ اِعْدِلُوا۠ هُوَ اَقْرَبُ لِلتَّقْوٰىۘ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ خَب۪يرٌ بِمَا تَعْمَلُونَ", translation: "Ey İman Edenler! Allah için gerçeğe bağlı kalmada kararlı olun! Hakkaniyetli tanıklar olun! Bir halka olan düşmanlığınız sizi adaletsizliğe yöneltmesin. Adil olun, bu, takvalı olmaya en uygun olandır. Allah'a karşı takvalı olun. Allah, yaptığınız her şeyden haberdardır.", transliteration: "Ya eyyuhellezine amenu kunu kavvamine lillahi şuhedae bil kıstı ve la yecrimennekum şeneanu kavmin ala ella ta'dilu. I'dilu, huve akrabu lit takva vettekullah innallahe habirun bima ta'melun."},
  {id: 678, sure_no: 9, ayet_no: 9, sure_adi: "Maide", text: "وَعَدَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِۙ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ عَظ۪يمٌ", translation: "Allah, iman edip salihatı yapanlara söz verdi, onlar için bağışlanma ve büyük bir karşılık vardır.", transliteration: "Veadellahullezine amenu ve amilus salihati lehum magfiretun ve ecrun azim."},
  {id: 679, sure_no: 10, ayet_no: 10, sure_adi: "Maide", text: "وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ الْجَح۪يمِ", translation: "Küfreden ve ayetlerimizi yalanlayan kimselere gelince, işte onlar Cehennem'lik kimselerdir.", transliteration: "Vellezine keferu ve kezzebu bi ayatina ulaike ashabul cehim."},
  {id: 680, sure_no: 11, ayet_no: 11, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ هَمَّ قَوْمٌ اَنْ يَبْسُطُٓوا اِلَيْكُمْ اَيْدِيَهُمْ فَكَفَّ اَيْدِيَهُمْ عَنْكُمْۚ وَاتَّقُوا اللّٰهَۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ۟", translation: "Ey İman Edenler! Allah'ın üzerinizdeki nimetini hatırlayın. Hani bir topluluk size saldırmayı tasarlamıştı da Allah, onları, size saldırmaktan vazgeçirmişti. Allah'a karşı takvalı olun. Mü'minler, yalnızca Allah'a güvenip yönelsinler.", transliteration: "Ya eyyuhellezine amenuzkuru ni'metallahi aleykum iz hemme kavmun en yebsutu ileykum eydiyehum fe keffe eydiyehum ankum, vettekullah ve alallahi fel yetevekkelil mu'minun."},
  {id: 681, sure_no: 12, ayet_no: 12, sure_adi: "Maide", text: "وَلَقَدْ اَخَذَ اللّٰهُ م۪يثَاقَ بَن۪ٓي اِسْرَٓائ۪لَۚ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَق۪يباًۜ وَقَالَ اللّٰهُ اِنّ۪ي مَعَكُمْۜ لَئِنْ اَقَمْتُمُ الصَّلٰوةَ وَاٰتَيْتُمُ الزَّكٰوةَ وَاٰمَنْتُمْ بِرُسُل۪ي وَعَزَّرْتُمُوهُمْ وَاَقْرَضْتُمُ اللّٰهَ قَرْضاً حَسَناً لَاُكَفِّرَنَّ عَنْكُمْ سَيِّـَٔاتِكُمْ وَلَاُدْخِلَنَّكُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۚ فَمَنْ كَفَرَ بَعْدَ ذٰلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوَٓاءَ السَّب۪يلِ", translation: "Doğrusu Allah İsrailoğullarından kesin söz aldı. Onlardan on iki temsilci gönderdik. Ve Allah, \"Sizinle beraberim.\" dedi. Ant olsun eğer salatı ikame eder, zekatı yapar, Resul'lerime iman eder ve onlara yardımcı olur, böylece Allah'a iyi bir ödünç verirseniz, o zaman elbette kötülüklerinizi örterim, muhakkak içinden ırmaklar akan Cennetlere koyarım. Bundan sonra, sizden kim küfrederse düz yoldan sapmış olur.", transliteration: "Ve lekad ehazallahu misaka beni israil, ve beasna minhumusney aşera nakiba ve kalellahu inni meakum lein ekamtumus salate ve ateytumuz zekate ve amentum bi rusuli ve azzertumuhum ve akradtumullahe kardan hasenen le ukeffirenne ankum seyyiatikum ve le udhılennekum cennatin tecri min tahtıhel enhar, fe men kefere ba'de zalike minkum fe kad dalle sevaes sebil."},
  {id: 682, sure_no: 13, ayet_no: 13, sure_adi: "Maide", text: "فَبِمَا نَقْضِهِمْ م۪يثَاقَهُمْ لَعَنَّاهُمْ وَجَعَلْنَا قُلُوبَهُمْ قَاسِيَةًۚ يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِه۪ۙ وَنَسُوا حَظاًّ مِمَّا ذُكِّرُوا بِه۪ۚ وَلَا تَزَالُ تَطَّلِعُ عَلٰى خَٓائِنَةٍ مِنْهُمْ اِلَّا قَل۪يلاً مِنْهُمْ فَاعْفُ عَنْهُمْ وَاصْفَحْۜ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِن۪ينَ", translation: "Anlaşmalarını bozmalarından dolayı onlara lanet ettik ve kalplerini katılaştırdık. Kelimeleri bağlamlarından kopararak çarpıtıyorlar, öğütlendikleri şeyden nasiplenmeyi unuturlar, içlerinden çok azı hariç, daima onların hainlik ettiklerini görürsün. Yine de vazgeç ve yaptıklarına aldırma. Kuşkusuz Allah, iyi davrananları sever.", transliteration: "Fe bima nakdihim misakahum leannahum ve cealna kulubehum kasiyet, yuharrifunel kelime an mevadııhi ve nesu hazzan mimma zukkiru bih, ve la tezalu tettaliu ala haınetin minhum illa kalilen minhum fa'fu anhum vasfah innallahe yuhıbbul muhsinin."},
  {id: 683, sure_no: 14, ayet_no: 14, sure_adi: "Maide", text: "وَمِنَ الَّذ۪ينَ قَالُٓوا اِنَّا نَصَارٰٓى اَخَذْنَا م۪يثَاقَهُمْ فَنَسُوا حَظاًّ مِمَّا ذُكِّرُوا بِه۪ۖ فَاَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَٓاءَ اِلٰى يَوْمِ الْقِيٰمَةِۜ وَسَوْفَ يُنَبِّئُهُمُ اللّٰهُ بِمَا كَانُوا يَصْنَعُونَ", translation: "Ve \"Biz Nesara'yız.\" diyenlerden de söz aldık. Öğütlendikleri şeyden nasiplenmeyi unuttular. Biz de kıyamet gününe kadar aralarına düşmanlık ve kin yerleştirdik. Ve Allah, ne iş yaptıklarını yakında haber verecektir.", transliteration: "Ve minellezine kalu inna nasara ehazna misakahum fe nesu hazzan mimma zukkiru bihi fe agrayna beynehumul adavete vel bagdae ila yevmil kıyameh ve sevfe yunebbiuhumullahu bima kanu yasnaun."},
  {id: 684, sure_no: 15, ayet_no: 15, sure_adi: "Maide", text: "يَٓا اَهْلَ الْكِتَابِ قَدْ جَٓاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ كَث۪يراً مِمَّا كُنْتُمْ تُخْفُونَ مِنَ الْكِتَابِ وَيَعْفُوا عَنْ كَث۪يرٍۜ قَدْ جَٓاءَكُمْ مِنَ اللّٰهِ نُورٌ وَكِتَابٌ مُب۪ينٌۙ", translation: "Ey Kitap Ehli! Doğrusu, Kitap'tan gizlediğiniz birçok şeyi size açıklayan ve bir kısmından da söz etmeyen Resul'ümüz geldi. Doğrusu size Allah'tan bir nur ve apaçık bir Kitap geldi.", transliteration: "Ya ehlel kitabi kad caekum resuluna yubeyyinu lekum kesiran mimma kuntum tuhfune minel kitabi ve ya'fu an kesir kad caekum minallahi nurun ve kitabun mubin."},
  {id: 685, sure_no: 16, ayet_no: 16, sure_adi: "Maide", text: "يَهْد۪ي بِهِ اللّٰهُ مَنِ اتَّبَعَ رِضْوَانَهُ سُبُلَ السَّلَامِ وَيُخْرِجُهُمْ مِنَ الظُّلُمَاتِ اِلَى النُّورِ بِاِذْنِه۪ وَيَهْد۪يهِمْ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Allah, onunla, rızasına bilerek tabi olanları esenlik yollarına iletir. Onları, izniyle karanlıklardan aydınlığa çıkartır. Ve onları dosdoğru olan yola iletir.", transliteration: "Yehdi bihillahu menittebea rıdvanehu subules selami ve yuhricuhum minez zulumati ilen nuri bi iznihi ve yehdihim ila sıratın mustakim."},
  {id: 686, sure_no: 17, ayet_no: 17, sure_adi: "Maide", text: "لَقَدْ كَفَرَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ هُوَ الْمَس۪يحُ ابْنُ مَرْيَمَۜ قُلْ فَمَنْ يَمْلِكُ مِنَ اللّٰهِ شَيْـٔاً اِنْ اَرَادَ اَنْ يُهْلِكَ الْمَس۪يحَ ابْنَ مَرْيَمَ وَاُمَّهُ وَمَنْ فِي الْاَرْضِ جَم۪يعاًۜ وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۜ يَخْلُقُ مَا يَشَٓاءُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Ant olsun ki, Meyem Oğlu Mesih için, \"O Allah'tır.\" diyenler kuşkusuz Kafir oldu. De ki: \"Allah, Meyem Oğlu Mesih'i, annesini ve yeryüzündekilerin tamamını yok etmek isterse, O'na kim engel olabilir?\" Göklerin, yeryüzünün ve ikisinin arasındakilerin tamamı, Allah'a aittir. Dilediğini yaratır. Ve Allah, Her Şeye Gücü Yeten'dir.", transliteration: "Lekad keferellezine kalu innallahe huvel mesihubnu meryem kul fe men yemliku minallahi şey'en in erade en yuhlikel mesihabne meryeme ve ummehu ve men fil ardı cemia ve lillahi mulkus semavati vel ardı ve ma beynehuma. Yahluku ma yeşa vallahu ala kulli şey'in kadir."},
  {id: 687, sure_no: 18, ayet_no: 18, sure_adi: "Maide", text: "وَقَالَتِ الْيَهُودُ وَالنَّصَارٰى نَحْنُ اَبْنَٓاءُ اللّٰهِ وَاَحِبَّٓاؤُ۬هُۜ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوبِكُمْۜ بَلْ اَنْتُمْ بَشَرٌ مِمَّنْ خَلَقَۜ يَغْفِرُ لِمَنْ يَشَٓاءُ وَيُعَذِّبُ مَنْ يَشَٓاءُۜ وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۘ وَاِلَيْهِ الْمَص۪يرُ", translation: "Yahudiler ve Nesara, \"Bizler Allah'ın oğulları ve O'nun sevgilileriyiz.\" dediler. De ki: \"O halde suçlarınızdan dolayı, size niçin azap ediyor? Doğrusu, siz de yarattıklarından bir beşersiniz. Dilediğini bağışlar, dilediğine azap eder. Göklerin, yerin ve ikisi arasındakilerin mülkü Allah'a aittir. Son varış O'nadır.\"", transliteration: "Ve kaletil yahudu ven nasara nahnu ebnaullahi ve ehıbbauh kul fe lime yuazzibukum bi zunubikul bel entum beşerun mimmen halak yagfiru limen yeşau ve yuazzibu men yeşa ve lillahi mulkus semavati vel ardı ve ma beynehuma ve ileyhil masir."},
  {id: 688, sure_no: 19, ayet_no: 19, sure_adi: "Maide", text: "يَٓا اَهْلَ الْكِتَابِ قَدْ جَٓاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ عَلٰى فَتْرَةٍ مِنَ الرُّسُلِ اَنْ تَقُولُوا مَا جَٓاءَنَا مِنْ بَش۪يرٍ وَلَا نَذ۪يرٍۘ فَقَدْ جَٓاءَكُمْ بَش۪يرٌ وَنَذ۪يرٌۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ۟", translation: "Ey Kitap Ehli! Resullerin arasının kesildiği bir dönemde size gerçekleri açıklayan Resul'ümüz geldi. \"Bize herhangi bir müjdeleyici ve uyarıcı gelmedi.\" demeyesiniz diye, müjdeleyici ve uyarıcı olarak. Allah, Her Şeye Gücü Yeten'dir.", transliteration: "Ya ehlel kitabi kad caekum resuluna yubeyyinu lekum ala fetretin min er rusuli en tekulu ma caena min beşirin ve la nezirin fe kad caekum beşirun ve nezir vallahu ala kulli şey'in kadir."},
  {id: 689, sure_no: 20, ayet_no: 20, sure_adi: "Maide", text: "وَاِذْ قَالَ مُوسٰى لِقَوْمِه۪ يَا قَوْمِ اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَعَلَ ف۪يكُمْ اَنْبِيَٓاءَ وَجَعَلَكُمْ مُلُوكاًۗ وَاٰتٰيكُمْ مَا لَمْ يُؤْتِ اَحَداً مِنَ الْعَالَم۪ينَ", translation: "Hani Musa, halkına: \"Ey halkım! Allah'ın size verdiği nimetleri hatırlayın. Hani O, içinizden Nebiler seçti; sizi melikler yaptı ve alemlerden hiç kimseye vermediği birçok şeyi size verdi.\" dedi.", transliteration: "Ve iz kale musa li kavmihi ya kavmizkuru ni'metallahi aleykum iz ceale fikum enbiyae ve cealekum muluk, ve atakum ma lem yu'ti ehaden minel alemin."},
  {id: 690, sure_no: 21, ayet_no: 21, sure_adi: "Maide", text: "يَا قَوْمِ ادْخُلُوا الْاَرْضَ الْمُقَدَّسَةَ الَّت۪ي كَتَبَ اللّٰهُ لَكُمْ وَلَا تَرْتَدُّوا عَلٰٓى اَدْبَارِكُمْ فَتَنْقَلِبُوا خَاسِر۪ينَ", translation: "(Musa) \"Ey kavmim! Allah'ın, sizin için yazdığı, kutsal topraklara girin. Gerisin geri dönmeyin. Yoksa ters yüz olarak hüsrana uğrarsınız.\"", transliteration: "Ya kavmidhulul ardal mukaddesetelleti keteballahu lekum ve la terteddu ala edbarikum fe tenkalibu hasirin."},
  {id: 691, sure_no: 22, ayet_no: 22, sure_adi: "Maide", text: "قَالُوا يَا مُوسٰٓى اِنَّ ف۪يهَا قَوْماً جَبَّار۪ينَۗ وَاِنَّا لَنْ نَدْخُلَهَا حَتّٰى يَخْرُجُوا مِنْهَاۚ فَاِنْ يَخْرُجُوا مِنْهَا فَاِنَّا دَاخِلُونَ", translation: "\"Ey Musa! Kuşkusuz, orada çok zorlu bir halk var. Onlar, oradan çıkmadıkça, biz oraya asla girmeyiz. Eğer çıkarlarsa, o zaman gireriz.\" dediler.", transliteration: "Kalu ya musa inne fiha kavmen cebbarin, ve inna len nedhuleha hatta yahrucu minha, fe in yahrucu minha fe inna dahılun."},
  {id: 692, sure_no: 23, ayet_no: 23, sure_adi: "Maide", text: "قَالَ رَجُلَانِ مِنَ الَّذ۪ينَ يَخَافُونَ اَنْعَمَ اللّٰهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبَابَۚ فَاِذَا دَخَلْتُمُوهُ فَاِنَّكُمْ غَالِبُونَ وَعَلَى اللّٰهِ فَتَوَكَّلُٓوا اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Korkanların içinden, Allah'ın kendilerine nimet verdiği iki kişi, şöyle dedi: \"Onların üzerine bildik kapıdan girin. Eğer oraya girerseniz, o zaman galip gelirsiniz. Eğer iman edenlerseniz, Allah'a tevekkül edin.\"", transliteration: "Kale raculani minellezine yehafune en'amallahu aleyhim edhulu aleyhimul bab, fe iza dehaltumuhu fe innekum galibune ve alallahi fe tevekkelu in kuntum mu'minin."},
  {id: 693, sure_no: 24, ayet_no: 24, sure_adi: "Maide", text: "قَالُوا يَا مُوسٰٓى اِنَّا لَنْ نَدْخُلَـهَٓا اَبَداً مَا دَامُوا ف۪يهَا فَاذْهَبْ اَنْتَ وَرَبُّكَ فَقَاتِلَٓا اِنَّا هٰهُنَا قَاعِدُونَ", translation: "\"Ey Musa! Onlar, orada oldukları sürece biz oraya asla girmeyiz; haydi sen git, sen ve Rabb'in birlikte savaşın. Kuşkusuz, işte biz buradan öteye gitmeyiz.\" dediler.", transliteration: "Kalu ya musa inna len nedhuleha ebeden ma damu fiha fezheb ente ve rabbuke fe katila inna hahuna kaıdun."},
  {id: 694, sure_no: 25, ayet_no: 25, sure_adi: "Maide", text: "قَالَ رَبِّ اِنّ۪ي لَٓا اَمْلِكُ اِلَّا نَفْس۪ي وَاَخ۪ي فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفَاسِق۪ينَ", translation: "(Musa) \"Ey Rabb'im! Kendimden ve kardeşimden başkasına söz geçiremiyorum. O halde bizimle fasık halkın arasını ayır.\" dedi.", transliteration: "Kale rabbi inni la emliku illa nefsi ve ahi fefruk beynena ve beynel kavmil fasikin."},
  {id: 695, sure_no: 26, ayet_no: 26, sure_adi: "Maide", text: "قَالَ فَاِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ اَرْبَع۪ينَ سَنَةًۚ يَت۪يهُونَ فِي الْاَرْضِ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفَاسِق۪ينَ۟", translation: "\"Onlar, oradan kırk yıl yoksun kalacaklar.\" dedi. \"Onlar, o yerde şaşkınlık içinde dolaşacaklar. Artık fasık olan halkın durumuna üzülme.\"", transliteration: "Kale fe inneha muharremetun aleyhim erbaine senet, yetihune fil ardı fe la te'se alel kavmil fasikin."},
  {id: 696, sure_no: 27, ayet_no: 27, sure_adi: "Maide", text: "وَاتْلُ عَلَيْهِمْ نَبَاَ ابْنَيْ اٰدَمَ بِالْحَقِّۢ اِذْ قَرَّبَا قُرْبَاناً فَتُقُبِّلَ مِنْ اَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْاٰخَرِۜ قَالَ لَاَقْتُلَنَّكَۜ قَالَ اِنَّمَا يَتَقَبَّلُ اللّٰهُ مِنَ الْمُتَّق۪ينَ", translation: "Onlara, iki adem oğlunun gerçek öyküsünü anlat: İkisi de birer sunu sundular. Birisininki kabul olundu, diğerininki ise kabul olunmadı. Dedi ki: \"Yemin olsun seni öldüreceğim.\" Dedi ki: \"Allah, yalnız takva sahiplerinden kabul eder.\"", transliteration: "Vetlu aleyhim nebeebney ademe bil hakkı iz karreba kurbanen fe tukubbile min ehadihima ve lem yutekabbel minel ahar kale le aktulennek kale innema yetekabbelullahu minel muttekin."},
  {id: 697, sure_no: 28, ayet_no: 28, sure_adi: "Maide", text: "لَئِنْ بَسَطْتَ اِلَيَّ يَدَكَ لِتَقْتُلَن۪ي مَٓا اَنَا۬ بِبَاسِطٍ يَدِيَ اِلَيْكَ لِاَقْتُلَكَۚ اِنّ۪ٓي اَخَافُ اللّٰهَ رَبَّ الْعَالَم۪ينَ", translation: "\"Yemin olsun ki! Beni öldürmek istesen de ben sana elimi kaldırmayacağım. Ben alemlerin Rabb'i olan Allah'a karşı gelmekten korkarım.\"", transliteration: "Lein besadte ileyye yedeke li taktuleni ma ene bi basitın yediye ileyke li aktulek, inni ehafullahe rabbel alemin."},
  {id: 698, sure_no: 29, ayet_no: 29, sure_adi: "Maide", text: "اِنّ۪ٓي اُر۪يدُ اَنْ تَبُٓوأَ بِاِثْم۪ي وَاِثْمِكَ فَتَكُونَ مِنْ اَصْحَابِ النَّارِۚ وَذٰلِكَ جَزٰٓؤُا الظَّالِم۪ينَۚ", translation: "\"Dilerim ki, benim günahımla birlikte kendi günahını da yüklenerek Ateş'e gidenlerden olursun. Çünkü zalimlerin cezası budur.\"", transliteration: "İnni uridu en tebue bi ismi ve ismike fe tekune min ashabin nar, ve zalike cezauz zalimin."},
  {id: 699, sure_no: 30, ayet_no: 30, sure_adi: "Maide", text: "فَطَوَّعَتْ لَهُ نَفْسُهُ قَتْلَ اَخ۪يهِ فَقَتَلَهُ فَاَصْبَحَ مِنَ الْخَاسِر۪ينَ", translation: "Nefsi kardeşini isteyerek öldürmesini kolaylaştırdı. O da onu öldürdü. Böylece hüsrana uğrayanlardan oldu.", transliteration: "Fe tavveat lehu nefsuhu katle ahihi fe katelehu fe asbaha minel hasirin."},
  {id: 700, sure_no: 31, ayet_no: 31, sure_adi: "Maide", text: "فَبَعَثَ اللّٰهُ غُرَاباً يَبْحَثُ فِي الْاَرْضِ لِيُرِيَهُ كَيْفَ يُوَار۪ي سَوْاَةَ اَخ۪يهِۜ قَالَ يَا وَيْلَتٰٓى اَعَجَزْتُ اَنْ اَكُونَ مِثْلَ هٰذَا الْغُرَابِ فَاُوَارِيَ سَوْاَةَ اَخ۪يۚ فَاَصْبَحَ مِنَ النَّادِم۪ينَۚۛ", translation: "Allah, bir karga gönderdi. Kardeşinin cesedini nasıl örteceğini ona göstermek için yeri eşeliyordu. \"Eyvah! Bana yazıklar olsun, kardeşimin cesedini örtmekte karga kadar bile olamayacak kadar aciz miyim!\" dedi. Pişman olanlardan oldu.", transliteration: "Fe beasallahu guraben yebhasu fil ardı li yuriyehu keyfe yuvari sev'ete ahih kale ya veyleta e aceztu en ekune misle hazel gurabi fe uvariye sev'ete ahi, fe asbaha minen nadimin."},
  {id: 701, sure_no: 32, ayet_no: 32, sure_adi: "Maide", text: "مِنْ اَجْلِ ذٰلِكَۚۛ  كَتَبْنَا عَلٰى بَن۪ٓي اِسْرَٓائ۪لَ اَنَّهُ مَنْ قَتَلَ نَفْساً بِغَيْرِ نَفْسٍ اَوْ فَسَادٍ فِي الْاَرْضِ فَكَاَنَّمَا قَتَلَ النَّاسَ جَم۪يـعاًۜ وَمَنْ اَحْيَاهَا فَكَاَنَّمَٓا اَحْيَا النَّاسَ جَم۪يعاًۜ وَلَقَدْ جَٓاءَتْهُمْ رُسُلُنَا بِالْبَيِّنَاتِۘ ثُمَّ اِنَّ كَث۪يراً مِنْهُمْ بَعْدَ ذٰلِكَ فِي الْاَرْضِ لَمُسْرِفُونَ", translation: "Bundan dolayı İsrailoğullarına yazdık: \"Kuşkusuz kim, bir cana karşılık olmaksızın veya yeryüzünde bozgunculuk yapmamış birini öldürürse, sanki bütün insanları öldürmüş gibidir. Kim de onu yaşatırsa, sanki bütün insanları yaşatmış gibi olur.\" Resul'lerimiz onlara kanıtlayıcı açık bilgilerle geldiği halde onların çoğu bundan sonra da yeryüzünde israf etmektedirler.", transliteration: "Min ecli zalik, ketebna ala beni israile ennehu men katele nefsen bi gayri nefsin ev fesadin fil ardı fe ke ennema katelen nase cemia ve men ahyaha fe ke ennema ahyen nase cemia ve lekad caethum rusuluna bil beyyinati summe inne kesiran minhum ba'de zalike fil ardı le musrifun."},
  {id: 702, sure_no: 33, ayet_no: 33, sure_adi: "Maide", text: "اِنَّمَا جَزٰٓؤُا الَّذ۪ينَ يُحَارِبُونَ اللّٰهَ وَرَسُولَهُ وَيَسْعَوْنَ فِي الْاَرْضِ فَسَاداً اَنْ يُقَتَّلُٓوا اَوْ يُصَلَّـبُٓوا اَوْ تُقَطَّعَ اَيْد۪يهِمْ وَاَرْجُلُهُمْ مِنْ خِلَافٍ اَوْ يُنْفَوْا مِنَ الْاَرْضِۜ ذٰلِكَ لَهُمْ خِزْيٌ فِي الدُّنْيَا وَلَهُمْ فِي الْاٰخِرَةِ عَذَابٌ عَظ۪يمٌۙ", translation: "Allah ve Resul'üyle savaşanların ve yeryüzünde bozgunculuk yapmaya çalışanların cezası öldürülmeleri veya asılmaları veya ellerinin ve ayaklarının çapraz kesilmesi veya yerlerinden sürgün edilmeleridir. İşte bu durum, onlar için dünyada bir rezilliktir. Ahiret'te ise onlar için büyük bir azap vardır.", transliteration: "İnnema cezaullezine yuharibunallahe ve resulehu ve yes'avne fil ardı fesaden en yukattelu ev yusallebu ev tukattaa eydihim ve erculuhum min hılafin ev yunfev minel ard, zalike lehum hızyun fid dunya ve lehum fil ahırati azabun azim."},
  {id: 703, sure_no: 34, ayet_no: 34, sure_adi: "Maide", text: "اِلَّا الَّذ۪ينَ تَابُوا مِنْ قَبْلِ اَنْ تَقْدِرُوا عَلَيْهِمْۚ فَاعْلَمُٓوا اَنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟", translation: "Ancak onlar üzerinde bir karara varmanızdan önce tevbe edenler hariç. İyi bilin ki Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İllellezine tabu min kabli en takdiru aleyhim, fa'lemu ennallahe gafurun rahim."},
  {id: 704, sure_no: 35, ayet_no: 35, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَابْتَغُٓوا اِلَيْهِ الْوَس۪يلَةَ وَجَاهِدُوا ف۪ي سَب۪يلِه۪ لَعَلَّكُمْ تُفْلِحُونَ", translation: "Ey İman Edenler! Allah'a karşı takva sahibi olun ve O'na yakınlaşmak için vesile arayın. O'nun yolunda gayret gösterin ki kurtuluşa eresiniz.", transliteration: "Ya eyyuhellezine amenuttekullahe vebtegu ileyhil vesilete ve cahidu fi sebilihi leallekum tuflihun."},
  {id: 705, sure_no: 36, ayet_no: 36, sure_adi: "Maide", text: "اِنَّ الَّذ۪ينَ كَفَرُوا لَوْ اَنَّ لَهُمْ مَا فِي الْاَرْضِ جَم۪يعاً وَمِثْلَهُ مَعَهُ لِيَفْتَدُوا بِه۪ مِنْ عَذَابِ يَوْمِ الْقِيٰمَةِ مَا تُقُبِّلَ مِنْهُمْۚ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Yeryüzünde olanların tamamı ve bir o kadarı daha, Kafirlerin olsa ve kıyamet gününün azabından kurtulmak için bunları karşılık olarak verseler, bu onlardan asla kabul edilmeyecektir. Onlar için elem verici bir azap vardır.", transliteration: "İnnellezine keferu lev enne lehum ma fil ardı cemian ve mislehu meahu li yeftedu bihi min azabi yevmil kıyameti ma tukubbile minhum, ve lehum azabun elim."},
  {id: 706, sure_no: 37, ayet_no: 37, sure_adi: "Maide", text: "يُر۪يدُونَ اَنْ يَخْرُجُوا مِنَ النَّارِ وَمَا هُمْ بِخَارِج۪ينَ مِنْهَاۘ وَلَهُمْ عَذَابٌ مُق۪يمٌ", translation: "Ateşten çıkmak isterler. Onlar, o ateşten asla çıkamayacaklar. Onlar için kalıcı bir azap vardır.", transliteration: "Yuridune en yahrucu minen nari ve ma hum bi haricine minha, ve lehum azabun mukim."},
  {id: 707, sure_no: 38, ayet_no: 38, sure_adi: "Maide", text: "وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُٓوا اَيْدِيَهُمَا جَزَٓاءً بِمَا كَسَبَا نَكَالاً مِنَ اللّٰهِۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ", translation: "Hırsızlık yapan erkeğin ve kadının, her ikisinin de ellerini, Allah'tan caydırıcı bir karşılık olarak kesin. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ves sariku ves sarikatu faktau eydiyehuma cezaen bima keseba nekalen minallah vallahu azizun hakim."},
  {id: 708, sure_no: 39, ayet_no: 39, sure_adi: "Maide", text: "فَمَنْ تَابَ مِنْ بَعْدِ ظُلْمِه۪ وَاَصْلَحَ فَاِنَّ اللّٰهَ يَتُوبُ عَلَيْهِۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Kim yaptığı haksızlıktan sonra tevbe eder ve uslanırsa, kuşkusuz Allah onun tevbesini kabul eder. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Fe men tabe min ba'di zulmihi ve aslaha fe innallahe yetubu aleyh innallahe gafurun rahim."},
  {id: 709, sure_no: 40, ayet_no: 40, sure_adi: "Maide", text: "اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ يُعَذِّبُ مَنْ يَشَٓاءُ وَيَغْفِرُ لِمَنْ يَشَٓاءُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Bilmez misin? Kuşkusuz ki göklerin ve yerin mülkü Allah'a aittir. Hak edene azap eder, Hak edeni bağışlar. Allah Her Şeye Güç Yetiren'dir.", transliteration: "E lem ta'lem ennallahe lehu mulkus semavati vel ardı yuazzibu men yeşau ve yagfiru limen yeşa vallahu ala kulli şey'in kadir."},
  {id: 710, sure_no: 41, ayet_no: 41, sure_adi: "Maide", text: "يَٓا اَيُّهَا الرَّسُولُ لَا يَحْزُنْكَ الَّذ۪ينَ يُسَارِعُونَ فِي الْكُفْرِ مِنَ الَّذ۪ينَ قَالُٓوا اٰمَنَّا بِاَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوبُهُمْۚ وَمِنَ الَّذ۪ينَ هَادُوا سَمَّاعُونَ لِلْكَذِبِ سَمَّاعُونَ لِقَوْمٍ اٰخَر۪ينَۙ لَمْ يَأْتُوكَۜ يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَوَاضِعِه۪ۚ يَقُولُونَ اِنْ اُو۫ت۪يتُمْ هٰذَا فَخُذُوهُ وَاِنْ لَمْ تُؤْتَوْهُ فَاحْذَرُواۜ وَمَنْ يُرِدِ اللّٰهُ فِتْنَتَهُ فَلَنْ تَمْلِكَ لَهُ مِنَ اللّٰهِ شَيْـٔاًۜ اُو۬لٰٓئِكَ الَّذ۪ينَ لَمْ يُرِدِ اللّٰهُ اَنْ يُطَهِّرَ قُلُوبَهُمْۜ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْاٰخِرَةِ عَذَابٌ عَظ۪يمٌ", translation: "Ey Resul! Küfre koşuşanlar seni üzmesin. O kimseler ki ağızlarıyla iman ettik dedikleri halde, kalben iman etmediler. Ve bir de yalan uydurmak amacıyla kasıtlı dinleyen Yahudiler, sana gelmeyen başka bir halk adına casusluk yapmak için dinlerler. Sözleri bağlamlarından kopararak değiştiriyorlar: \"Eğer bu size verilirse onu alın, eğer bu verilmezse sakının!\" diyorlar. Allah, kimin fitneye düşmesini isterse, onun için Allah'a karşı elinden hiçbir şey gelmez. İşte onlar, Allah'ın kalplerini arındırmak istemediği kimselerdir. Onlar için dünyada aşağılanma, ahirette de büyük bir azap vardır.", transliteration: "Ya eyyuher resulu la yahzunkellezine yusariune fil kufri minellezine kalu amenna bi efvahihim ve lem tu'min kulubuhum, ve minellezine hadu semmaune lil kezibi semmaune li kavmin aharine lem ye'tuk yuharrifunel kelime min ba'di mevadııh, yekulune in utitum haza fe huzuhu ve in lem tu'tevhu fahzeru ve men yuridillahu fitnetehu fe len temlike lehu minallahi şey'a ulaikellezine lem yuridillahu en yutahhire kulubehum lehum fid dunya hızyun ve lehum fil ahıreti azabun azim."},
  {id: 711, sure_no: 42, ayet_no: 42, sure_adi: "Maide", text: "سَمَّاعُونَ لِلْكَذِبِ اَكَّالُونَ لِلسُّحْتِۜ فَاِنْ جَٓاؤُ۫كَ فَاحْكُمْ بَيْنَهُمْ اَوْ اَعْرِضْ عَنْهُمْۚ وَاِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَضُرُّوكَ شَيْـٔاًۜ وَاِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِۜ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِط۪ينَ", translation: "Yalan uydurmak için can kulağı ile dinlerler ve sürekli suhtla geçinirler. Eğer hüküm vermen için sana gelirlerse, aralarında hüküm ver veya onlardan yüz çevir. Eğer onlardan yüz çevirirsen sana hiçbir zarar veremezler. Ve eğer hüküm verirsen, aralarında hakkaniyetle hüküm ver. Kuşkusuz, Allah, hakkaniyetli olanları sever.", transliteration: "Semmaune lil kezibi ekkalune lis suht fe in cauke fahkum beynehum ev a'rıd anhum, ve in tu'rıd anhum fe len yedurruke şey'a ve in hakemte fahkum beynehum bil kıst innallahe yuhıbbul muksıtin."},
  {id: 712, sure_no: 43, ayet_no: 43, sure_adi: "Maide", text: "وَكَيْفَ يُحَكِّمُونَكَ وَعِنْدَهُمُ التَّوْرٰيةُ ف۪يهَا حُكْمُ اللّٰهِ ثُمَّ يَتَوَلَّوْنَ مِنْ بَعْدِ ذٰلِكَۜ وَمَٓا اُو۬لٰٓئِكَ بِالْمُؤْمِن۪ينَ۟", translation: "İçinde, Allah'ın hükmünün bulunduğu Tevrat yanlarında olduğu halde, nasıl olur da sana hüküm verdiriyorlar? Sonra da o hükümden döneklik ediyorlar. Onlar, aslında inanmış değiller.", transliteration: "Ve keyfe yuhakkimuneke ve indehumut tevratu fiha hukmullahi summe yetevellevne min ba'di zalik ve ma ulaike bil mu'minin."},
  {id: 713, sure_no: 44, ayet_no: 44, sure_adi: "Maide", text: "اِنَّٓا اَنْزَلْنَا التَّوْرٰيةَ ف۪يهَا هُدًى وَنُورٌۚ يَحْكُمُ بِهَا النَّبِيُّونَ الَّذ۪ينَ اَسْلَمُوا لِلَّذ۪ينَ هَادُوا وَالرَّبَّانِيُّونَ وَالْاَحْبَارُ بِمَا اسْتُحْفِظُوا مِنْ كِتَابِ اللّٰهِ وَكَانُوا عَلَيْهِ شُهَدَٓاءَۚ فَلَا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلَا تَشْتَرُوا بِاٰيَات۪ي ثَمَناً قَل۪يلاًۜ وَمَنْ لَمْ يَحْكُمْ بِمَٓا اَنْزَلَ اللّٰهُ فَاُو۬لٰٓئِكَ هُمُ الْكَافِرُونَ", translation: "Kuşkusuz, Biz, doğru yola iletici ve aydınlatıcı olan Tevrat'ı indirdik. Teslim olmuş Yahudiler için Nebiler onunla hüküm veriyorlardı. Rabbaniler ve ahbar olanlar da Allah'ın kitabından ezberletilmiş olmasından dolayı, ona tanıklık ediyorlardı. Öyleyse siz insanlardan değil, Bana karşı gelmekten sakının. Benim ayetlerimi az bir bedele değişmeyin! Allah'ın indirdiği ile hükmetmeyenler, Kafirlerin ta kendileridirler.", transliteration: "İnna enzelnet tevrate fiha huden ve nur, yahkumu bihen nebiyyunellezine eslemu lillezine hadu ver rabbaniyyune vel ahbaru bimestuhfizu min kitabillahi ve kanu aleyhi şuhedae, fe la tahşevun nase vahşevni ve la teşteru bi ayati semenen kalila ve men lem yahkum bima enzelallahu fe ulaike humul kafirun."},
  {id: 714, sure_no: 45, ayet_no: 45, sure_adi: "Maide", text: "وَكَتَبْنَا عَلَيْهِمْ ف۪يهَٓا اَنَّ النَّفْسَ بِالنَّفْسِۙ وَالْعَيْنَ بِالْعَيْنِ وَالْاَنْفَ بِالْاَنْفِ وَالْاُذُنَ بِالْاُذُنِ وَالسِّنَّ بِالسِّنِّۙ وَالْجُرُوحَ قِصَاصٌۜ فَمَنْ تَصَدَّقَ بِه۪ فَهُوَ كَفَّارَةٌ لَهُۜ وَمَنْ لَمْ يَحْكُمْ بِمَٓا اَنْزَلَ اللّٰهُ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ", translation: "Ve Biz, onda, onların üzerine yazdık: \"Cana can, göze göz, buruna burun, kulağa kulak, dişe diş ve yaralamalarda kısas vardır.\" Artık kim onunla tasadduk ederse o, onun için bir kefarettir. Allah'ın indirdiği ile hükmetmeyenler, zalimlerin ta kendileridirler.", transliteration: "Ve ketebna aleyhim fiha ennen nefse bin nefsi vel ayne bil ayni vel enfe bil enfi vel uzune bil uzuni ves sinne bis sinni vel curuha kısas fe men tesaddeka bihi fe huve keffaretun leh ve men lem yahkum bima enzelallahu fe ulaike humuz zalimun."},
  {id: 715, sure_no: 46, ayet_no: 46, sure_adi: "Maide", text: "وَقَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِع۪يسَى ابْنِ مَرْيَمَ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰيةِۖ وَاٰتَيْنَاهُ الْاِنْج۪يلَ ف۪يهِ هُدًى وَنُورٌۙ وَمُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰيةِ وَهُدًى وَمَوْعِظَةً لِلْمُتَّق۪ينَ", translation: "Onların ardından, Tevrat'ı tasdik edici olarak, Meryem Oğlu İsa'yı gönderdik. Biz ona, içinde hidayet ve nur bulunan, Tevrat'ı tasdik eden, takva sahipleri için hidayet ve öğüt olan İncil'i verdik.", transliteration: "Ve kaffeyna ala asarihim bi isebni meryeme musaddıkan lima beyne yedeyhi minet tevrati ve ateynahul incile fihi huden ve nurun ve musaddıkan lima beyne yedeyhi minet tevrati ve huden ve mev'ızeten muttekin."},
  {id: 716, sure_no: 47, ayet_no: 47, sure_adi: "Maide", text: "وَلْيَحْكُمْ اَهْلُ الْاِنْج۪يلِ بِمَٓا اَنْزَلَ اللّٰهُ ف۪يهِۜ وَمَنْ لَمْ يَحْكُمْ بِمَٓا اَنْزَلَ اللّٰهُ فَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَ", translation: "İncil Ehli, Allah'ın onda indirdiği ile hükmetsinler. Ve kim Allah'ın indirdiği ile hükmetmezse işte onlar Fasıklardır.", transliteration: "Vel yahkum ehlul incili bima enzelallahu fih ve men lem yahkum bima enzelallahu fe ulaike humul fasıkun."},
  {id: 717, sure_no: 48, ayet_no: 48, sure_adi: "Maide", text: "وَاَنْزَلْـنَٓا اِلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ مِنَ الْكِتَابِ وَمُهَيْمِناً عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمَٓا اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَٓاءَهُمْ عَمَّا جَٓاءَكَ مِنَ الْحَقِّۜ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْعَةً وَمِنْهَاجاًۜ وَلَوْ شَٓاءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَاحِدَةً وَلٰكِنْ لِيَبْلُوَكُمْ ف۪ي مَٓا اٰتٰيكُمْ فَاسْتَبِقُوا الْخَيْرَاتِۜ اِلَى اللّٰهِ مَرْجِعُكُمْ جَم۪يعاً فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَۙ", translation: "Biz sana, kendinden önceki Kitap'ı tasdik eden, onu düzenleyen bu Kitap'ı hakk olarak indirdik. O halde, aralarında Allah'ın indirdiği ile hükmet. Ve sakın sana gelen hakkı bırakıp onların hevalarına uyma. Ve Biz, sizin her biriniz için, bir şeriat ve yöntem belirledik. Allah dileseydi, sizi tek tip bir topluluk yapardı. Ancak sizlere verdiği ile sizi sınıyor. O halde hayırlarda yarışın. Hepinizin dönüşü Allah'adır. Allah, üzerinde ayrılığa düştüğünüz şeyleri bildirecektir.", transliteration: "Ve enzelna ileykel kitabe bil hakkı musaddıkan lima beyne yedeyhi minel kitabi ve muheyminen aleyhi fahkum beynehum bima enzelallahu ve la tettebi' ehvaehum amma caeke minel hakk li kullin cealna minkum şir'aten ve minhaca ve lev şaallahu le cealekum ummeten vahıdeten ve lakin li yebluvekum fi ma atakum festebikul hayrat ilallahi merciukum cemian fe yunebbiukum bima kuntum fihi tahtelifun."},
  {id: 718, sure_no: 49, ayet_no: 49, sure_adi: "Maide", text: "وَاَنِ احْكُمْ بَيْنَهُمْ بِمَٓا اَنْزَلَ اللّٰهُ وَلَا تَتَّبِـعْ اَهْوَٓاءَهُمْ وَاحْذَرْهُمْ اَنْ يَفْتِنُوكَ عَنْ بَعْضِ مَٓا اَنْزَلَ اللّٰهُ اِلَيْكَۜ فَاِنْ تَوَلَّوْا فَاعْلَمْ اَنَّمَا يُر۪يدُ اللّٰهُ اَنْ يُص۪يبَهُمْ بِبَعْضِ ذُنُوبِهِمْۜ وَاِنَّ  كَث۪يراً مِنَ النَّاسِ لَفَاسِقُونَ", translation: "Ve aralarında, Allah'ın sana indirdiğiyle hükmet. Onların hevalarına uyma. Allah'ın sana indirdiğinin bir kısmından seni şaşırtmalarından ve onlardan sakın. Eğer verdiğin kararlara uymazlarsa, bilesin ki Allah, bazı günahları nedeniyle onlara musibet isabet ettirmeyi istiyor. İnsanların pek çoğu elbette fasıktır.", transliteration: "Ve enıhkum beynehum bima enzelallahu ve la tettebi' ehvaehum vahzerhum en yeftinuke an ba'dı ma enzelallahu ileyk fe in tevellev fa'lem ennema yuridullahu en yusibehum bi ba'dı zunubihim ve inne kesiran minen nasi le fasıkun."},
  {id: 719, sure_no: 50, ayet_no: 50, sure_adi: "Maide", text: "اَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُونَۜ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ حُكْماً لِقَوْمٍ يُوقِنُونَ۟", translation: "Cahiliye hükmünü mü istiyorlar? Gerçeği kavramış bir toplum için, Allah'tan daha iyi hüküm veren kim olabilir?", transliteration: "E fe hukmel cahiliyyeti yebgun ve men ahsenu minallahi hukmen li kavmin yukınun."},
  {id: 720, sure_no: 51, ayet_no: 51, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا الْيَهُودَ وَالنَّصَارٰٓى اَوْلِيَٓاءَۢ  بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۜ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَاِنَّهُ مِنْهُمْۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ", translation: "Ey İman Edenler! Yahudileri ve Nesara'yı veliler edinmeyin. Onlar, birbirlerinin velileridir. Sizden kim onları mütevelli edinirse, kuşkusuz o, onlardandır. Kuşkusuz, Allah, zalim topluma hidayet etmez.", transliteration: "Ya eyyuhellezine amenu la tettehızul yehude ven nasara evliyae ba'duhum evliyau ba'd ve men yetevellehum minkum fe innehu minhum innallahe la yehdil kavmez zalimin."},
  {id: 721, sure_no: 52, ayet_no: 52, sure_adi: "Maide", text: "فَتَرَى الَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ يُسَارِعُونَ ف۪يهِمْ يَقُولُونَ نَخْشٰٓى اَنْ تُص۪يبَنَا دَٓائِرَةٌۜ  فَعَسَى اللّٰهُ اَنْ يَأْتِيَ بِالْفَتْحِ اَوْ اَمْرٍ مِنْ عِنْدِه۪ فَيُصْبِحُوا عَلٰى مَٓا اَسَرُّوا ف۪ٓي اَنْفُسِهِمْ نَادِم۪ينَۜ", translation: "Kalplerinde hastalık bulunanların, \"Başımıza bir bela gelmesinden korkuyoruz.\" diyerek, onların aralarında koşuştuklarını görürsün. Olur ki, Allah bir fetih veya kendi katından bir emir getirir ve böylece onlar içlerinde gizledikleri şeyden pişman olurlar!", transliteration: "Fe terallezine fi kulubihim maradun yusariune fihim yekulune nahşa en tusibena daireh fe asallahu en ye'tiye bil fethi ev emrin min indihi fe yusbihu ala ma eserru fi enfusihim nadimin."},
  {id: 722, sure_no: 53, ayet_no: 53, sure_adi: "Maide", text: "وَيَقُولُ الَّذ۪ينَ اٰمَنُٓوا اَهٰٓؤُ۬لَٓاءِ الَّذ۪ينَ اَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ اِنَّهُمْ لَمَعَكُمْۜ حَبِطَتْ اَعْمَالُهُمْ فَاَصْبَحُوا خَاسِر۪ينَ", translation: "İman Edenler şöyle derler: \"Bütün yeminleri ile senin yanında olacaklarına dair Allah'a yemin edenler bunlar mı?\" İşte onların yaptığı bütün işler boşa gitti. Ve hüsrana uğrayanlar oldular.", transliteration: "Ve yekulullezine amenu e haulaillezine aksemu billahi cehde eymanihim innehum le meakum habitat a'maluhum fe asbahu hasirin."},
  {id: 723, sure_no: 54, ayet_no: 54, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ د۪ينِه۪ فَسَوْفَ يَأْتِي اللّٰهُ بِقَوْمٍ يُحِبُّهُمْ وَيُحِبُّونَهُٓ  اَذِلَّةٍ عَلَى الْمُؤْمِن۪ينَ اَعِزَّةٍ عَلَى الْكَافِر۪ينَۘ يُجَاهِدُونَ ف۪ي سَب۪يلِ اللّٰهِ وَلَا يَخَافُونَ لَوْمَةَ لَٓائِمٍۜ ذٰلِكَ فَضْلُ اللّٰهِ يُؤْت۪يهِ مَنْ يَشَٓاءُۜ وَاللّٰهُ وَاسِعٌ عَل۪يمٌ", translation: "Ey İman Edenler! Sizden kim dininden dönerse bilsin ki Allah, Kendisinin onları sevdiği ve onların da O'nu sevdiği; Mü'minlere karşı alçak gönüllü, Kafirlere karşı izzetli, Allah yolunda cihad eden, kınayıcının kınamasından korkmayan bir halk getirir. İşte bu Allah'ın fazlıdır ki o fazlı dilediğine verir. Ve Allah, Yardımı Çok Kapsamlı Olan'dır, Her Şeyi Bilen'dir.", transliteration: "Ya eyyuhellezine amenu men yertedde minkum an dinihi fe sevfe ye'tillahu bi kavmin yuhıbbuhum ve yuhıbbunehu ezilletin alal mu'minine eizzetin alal kafirin, yucahidune fi sebilillahi ve la yehafune levmete laim zalike fadlullahi yu'tihi men yeşa vallahu vasiun alim."},
  {id: 724, sure_no: 55, ayet_no: 55, sure_adi: "Maide", text: "اِنَّمَا وَلِيُّكُمُ اللّٰهُ وَرَسُولُهُ وَالَّذ۪ينَ اٰمَنُوا الَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَهُمْ رَاكِعُونَ", translation: "Sizin veliniz; ancak Allah, O'nun Resul'ü ve \"salatı ikame edip ruku halinde zekat yapan\" Mü'minlerdir.", transliteration: "İnnema veliyyukumullahu ve resuluhu vellezine amenullezine yukimunes salate ve yu'tunez zekate ve hum rakıun."},
  {id: 725, sure_no: 56, ayet_no: 56, sure_adi: "Maide", text: "وَمَنْ يَتَوَلَّ اللّٰهَ وَرَسُولَهُ وَالَّذ۪ينَ اٰمَنُوا فَاِنَّ حِزْبَ اللّٰهِ هُمُ الْغَالِبُونَ۟", translation: "Ve kim Allah'ı ve O'nun Resul'ünü ve Mü'minleri veli edinirse, Allah'ın taraftarları onlardır. Galip gelecek olanlar da onlardır.", transliteration: "Ve men yetevellallahe ve resulehu vellezine amenu fe inne hızbellahi humul galibun."},
  {id: 726, sure_no: 57, ayet_no: 57, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا الَّذ۪ينَ اتَّخَذُوا د۪ينَكُمْ هُزُواً وَلَعِباً مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ اَوْلِيَٓاءَۚ وَاتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Ey İman Edenler! Sizden önce kendilerine Kitap verilenlerden ve Kafirlerden, dininizi alay ve eğlenceye alan kimseleri veliler edinmeyin. Eğer iman etmiş kimselerseniz, Allah'a karşı takvalı olun.", transliteration: "Ya eyyuhellezine amenu la tettehızullezinettehazu dinekum huzuven ve leiben min ellezine utul kitabe min kablikum vel kuffara evliya, vettekullahe in kuntum mu'minin."},
  {id: 727, sure_no: 58, ayet_no: 58, sure_adi: "Maide", text: "وَاِذَا نَادَيْتُمْ اِلَى الصَّلٰوةِ اتَّخَذُوهَا هُزُواً وَلَعِباًۜ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَ", translation: "Salata çağrıldığınız zaman, onu alay ve eğlence konusu yaparlar. Bu, onların kesinlikle aklını kullanmayan bir topluluk olmalarındandır.", transliteration: "Ve iza nadeytum iles salatittehazuha huzuven ve leıba zalike bi ennehum kavmun la ya'kılun."},
  {id: 728, sure_no: 59, ayet_no: 59, sure_adi: "Maide", text: "قُلْ يَٓا اَهْلَ الْكِتَابِ هَلْ تَنْقِمُونَ مِنَّٓا اِلَّٓا اَنْ اٰمَنَّا بِاللّٰهِ وَمَٓا اُنْزِلَ اِلَيْنَا وَمَٓا اُنْزِلَ مِنْ قَبْلُۙ وَاَنَّ اَكْثَرَكُمْ فَاسِقُونَ", translation: "De ki: \"Ey Kitap Ehli! Allah'a, bize indirilene ve bizden önce indirilene inandığımız için mi bizden nefret ediyorsunuz? Kuşkusuz, çoğunuz fasıksınız.\"", transliteration: "Kul ya ehlel kitabi hel tenkımune minna illa en amenna billahi ve ma unzile ileyna ve ma unzile min kablu ve enne ekserekum fasıkun."},
  {id: 729, sure_no: 60, ayet_no: 60, sure_adi: "Maide", text: "قُلْ هَلْ اُنَبِّئُكُمْ بِشَرٍّ مِنْ ذٰلِكَ مَثُوبَةً عِنْدَ اللّٰهِۜ مَنْ لَعَنَهُ اللّٰهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَاز۪يرَ وَعَبَدَ الطَّاغُوتَۜ اُو۬لٰٓئِكَ شَرٌّ مَكَاناً وَاَضَلُّ عَنْ سَوَٓاءِ السَّب۪يلِ", translation: "De ki: \"Allah'ın katında ceza olarak bunlardan daha kötüsünü size haber vereyim mi? Allah'ın lanet ettiği, gazabına uğrattığı ve onlardan maymunlar, domuzlar ve tağuta kulluk eden kimseler yapmışsa, işte bunlar, durumları bakımından en kötü olan ve doğru yoldan sapmış olanlardır.\"", transliteration: "Kul hel unebbiukum bi şerrin min zalike mesubeten ındallah men leanehullahu ve gadıbe aleyhi ve ceale min humul kıredete vel hanazire ve abedet tagut ulaike şerrun mekanen ve edallu an sevais sebil."},
  {id: 730, sure_no: 61, ayet_no: 61, sure_adi: "Maide", text: "وَاِذَا جَٓاؤُ۫كُمْ قَالُٓوا اٰمَنَّا وَقَدْ دَخَلُوا بِالْـكُفْرِ وَهُمْ قَدْ خَرَجُوا بِه۪ۜ وَاللّٰهُ اَعْلَمُ بِمَا كَانُوا يَكْتُمُونَ", translation: "Onlar, size geldiklerinde, \"İman ettik.\" dediler. Oysaki onlar, yanınıza küfürle girdiler, küfürle çıktılar. Allah, gizledikleri şeyi en iyi bilendir.", transliteration: "Ve iza caukum kalu amenna ve kad dehalu bil kufri ve hum kad haracu bih vallahu a'lemu bima kanu yektumun."},
  {id: 731, sure_no: 62, ayet_no: 62, sure_adi: "Maide", text: "وَتَرٰى كَث۪يراً مِنْهُمْ يُسَارِعُونَ فِي الْاِثْمِ وَالْعُدْوَانِ وَاَكْلِهِمُ السُّحْتَۜ لَبِئْسَ مَا كَانُوا يَعْمَلُونَ", translation: "Ve onlardan pek çoğunun, günah işlemede, düşmanlık yapmada ve suht yemede birbirleriyle yarıştıklarını görürsün. Yapmakta oldukları şey ne kadar kötüdür!", transliteration: "Ve tera kesiran minhum yusariune fil ismi vel udvani ve eklihimus suht lebi'se ma kanu ya'melun."},
  {id: 732, sure_no: 63, ayet_no: 63, sure_adi: "Maide", text: "لَوْلَا يَنْهٰيهُمُ الرَّبَّانِيُّونَ وَالْاَحْبَارُ عَنْ قَوْلِهِمُ الْاِثْمَ وَاَكْلِهِمُ السُّحْتَۜ لَبِئْسَ مَا كَانُوا يَصْنَعُونَ", translation: "Rabbanilerin ve ahbarın; günah söz söylemekten ve suht yemekten sakındırmaları gerekmez miydi? Yaltaklanmaları ne kötüdür.", transliteration: "Lev la yenhahumur rabbaniyyune vel ahbaru an kavlihimul isme ve eklihimus suht lebi'se ma kanu yasneun."},
  {id: 733, sure_no: 64, ayet_no: 64, sure_adi: "Maide", text: "وَقَالَتِ الْيَهُودُ يَدُ اللّٰهِ مَغْلُولَةٌۜ غُلَّتْ اَيْد۪يهِمْ وَلُعِنُوا بِمَا قَالُواۢ بَلْ يَدَاهُ مَبْسُوطَتَانِۙ يُنْفِقُ كَيْفَ يَشَٓاءُۜ وَلَيَز۪يدَنَّ كَث۪يراً مِنْهُمْ مَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ طُغْيَاناً وَكُفْراًۜ وَاَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَٓاءَ اِلٰى يَوْمِ الْقِيٰمَةِۜ   كُلَّمَٓا اَوْقَدُوا نَاراً لِلْحَرْبِ اَطْفَاَهَا اللّٰهُۙ وَيَسْعَوْنَ فِي الْاَرْضِ فَسَاداًۜ وَاللّٰهُ لَا يُحِبُّ الْمُفْسِد۪ينَ", translation: "O Yahudiler, \"Allah'ın eli bağlıdır.\" dediler. Böyle söylemelerinden dolayı elleri bağlandı ve söyledikleri yüzünden lanetlendiler. Oysaki O'nun iki eli de açıktır. Nasıl dilerse öyle infak eder. Yemin olsun ki sana Rabb'inden indirilen şey onların çoğunun azgınlığını ve küfrünü arttıracaktır. Biz de aralarına kıyamet gününe kadar düşmanlık ve nefret yerleştirdik. Savaş için ateş yaktıkları her seferinde Allah onu söndürdü. Yeryüzünde bozgunculuk için koşuşuyorlar. Ve Allah bozguncuları sevmez.", transliteration: "Ve kaletil yehudu yedullahi magluleh gullet eydihim ve luınu bima kalu bel yedahu mebsutatani yunfıku keyfe yeşa ve leyezidenne kesiran minhum ma unzile ileyke min rabbike tugyanen ve kufra ve elkayna beynehumul adavete vel bagdae ila yevmil kıyameh kullema evkadu naran lil harbi etfeehallahu ve yes'avne fil ardı fesada vallahu la yuhıbbul mufsidin."},
  {id: 734, sure_no: 65, ayet_no: 65, sure_adi: "Maide", text: "وَلَوْ اَنَّ اَهْلَ الْكِتَابِ اٰمَنُوا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَاَدْخَلْنَاهُمْ جَنَّاتِ النَّع۪يمِ", translation: "Eğer Kitap Ehli de iman edip takva sahibi olsaydı, kötülüklerini örter, nimet dolu Cennetlere koyardık.", transliteration: "Ve lev enne ehlel kitabi amenu vettekav le kefferna anhum seyyiatihim ve le edhalnahum cennatin naim."},
  {id: 735, sure_no: 66, ayet_no: 66, sure_adi: "Maide", text: "وَلَوْ اَنَّهُمْ اَقَامُوا التَّوْرٰيةَ وَالْاِنْج۪يلَ وَمَٓا اُنْزِلَ اِلَيْهِمْ مِنْ رَبِّهِمْ لَاَكَلُوا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْۜ مِنْهُمْ اُمَّةٌ مُقْتَصِدَةٌۜ وَكَث۪يرٌ مِنْهُمْ سَٓاءَ مَا يَعْمَلُونَ۟", translation: "Eğer onlar, Tevrat'ı, İncil'i ve Rabb'lerinden kendilerine indirileni gereğince uygulasalardı hem üstlerinden hem de ayaklarının altından yiyeceklerdi. İçlerinde muktesid bir topluluk var. Ve onlardan çoğu da kötü işler yapıyorlar.", transliteration: "Ve lev ennehum ekamut tevrate vel incile ve ma unzile ileyhim min rabbihim le ekelu min fevkıhim ve min tahti erculihim. Minhum ummetun muktesıdeh ve kesirun minhum sae ma ya'melun."},
  {id: 736, sure_no: 67, ayet_no: 67, sure_adi: "Maide", text: "يَٓا اَيُّهَا الرَّسُولُ بَلِّـغْ مَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَۜ وَاِنْ لَمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسَالَتَهُۜ وَاللّٰهُ يَعْصِمُكَ مِنَ النَّاسِۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ", translation: "Ey Resul! Rabb'inden sana her indirileni tebliğ et. Eğer yapmazsan, O'nun mesajını tebliğ etmemiş sayılırsın. Allah, seni insanlardan koruyor. Kuşkusuz, Allah, Kafirleri doğru yola iletmez.", transliteration: "Ya eyyuherresulu bellıg ma unzile ileyke min rabbik ve in lem tef'al fema bellagte risaleteh vallahu ya'sımuke minen nas innallahe la yehdil kavmel kafirin."},
  {id: 737, sure_no: 68, ayet_no: 68, sure_adi: "Maide", text: "قُلْ يَٓا اَهْلَ الْكِتَابِ لَسْتُمْ عَلٰى شَيْءٍ حَتّٰى تُق۪يمُوا التَّوْرٰيةَ وَالْاِنْج۪يلَ وَمَٓا اُنْزِلَ اِلَيْكُمْ مِنْ رَبِّكُمْۜ وَلَيَز۪يدَنَّ كَث۪يراً مِنْهُمْ مَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ طُغْيَاناً وَكُفْراًۚ فَلَا تَأْسَ عَلَى الْقَوْمِ الْكَافِر۪ينَ", translation: "De ki: \"Ey Kitap Ehli! Tevrat'ı ve İncil'i ve Rabb'inizden size indirileni gereğince uygulamadığınız sürece inancınızı sağlam bir temele oturtmuş olmazsınız.\" Elbette Rabb'inden sana indirilmekte olan şey, onlardan çoğunun azgınlığını ve küfrünü artırır. O halde Kafir halka üzülme.", transliteration: "Kul ya ehlel kitabi! lestum ala şey'in hatta tukimut Tevrate vel İncile ve ma unzile ileykum min rabbikum ve le yezidenne kesiren minhum ma unzile ileyke min rabbike tugyanen ve kufr, fe la te'se alal kavmil kafirin."},
  {id: 738, sure_no: 69, ayet_no: 69, sure_adi: "Maide", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَادُوا وَالصَّابِـؤُ۫نَ وَالنَّصَارٰى مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحاً فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "İman Edenler, Yahudiler, Sabiiler ve Nesara; kim Allah'a ve Ahiret Günü'ne iman etti ve salihatı yaptı ise onlara korku yoktur ve üzülmeyeceklerdir de.", transliteration: "İnnellezine amenu vellezine hadu ves sabiune ven nasara men amene billahi vel yevmil ahıri ve amile salihan fe la havfun aleyhim ve la hum yahzenun."},
  {id: 739, sure_no: 70, ayet_no: 70, sure_adi: "Maide", text: "لَقَدْ اَخَذْنَا م۪يثَاقَ بَن۪ٓي اِسْرَٓائ۪لَ وَاَرْسَلْـنَٓا اِلَيْهِمْ رُسُلاًۜ  كُلَّمَا جَٓاءَهُمْ رَسُولٌ بِمَا لَا تَهْوٰٓى اَنْفُسُهُمْۙ فَر۪يقاً كَذَّبُوا وَفَر۪يقاً يَقْتُلُونَ", translation: "Elbette İsrailoğullarından söz aldık ve onlara Resuller gönderdik. Ne zaman kendi hevalarına uymayan bir resul geldiyse, bir kısmını yalanlıyor ve bir kısmını da öldürüyorlardı.", transliteration: "Lekad ehazna misaka beni israile ve erselna ileyhim rusula kullema caehum resulun bima la tehva enfusuhum ferikan kezzebu ve ferikan yaktulun."},
  {id: 740, sure_no: 71, ayet_no: 71, sure_adi: "Maide", text: "وَحَسِبُٓوا اَلَّا تَكُونَ فِتْنَةٌ فَعَمُوا وَصَمُّوا ثُمَّ تَابَ اللّٰهُ عَلَيْهِمْ ثُمَّ عَمُوا وَصَمُّوا كَث۪يرٌ مِنْهُمْۜ وَاللّٰهُ بَص۪يرٌ بِمَا يَعْمَلُونَ", translation: "Bir fitne olmayacağını sandılar da kör ve sağır kesildiler. Sonra Allah, onların üzerine tevbe etti. Daha sonra onlardan birçoğu yine kör ve sağır kesildiler. Allah ne yaptıklarını gerçeğiyle görmektedir.", transliteration: "Ve hasibu ella tekune fitnetun fe amu ve sammu summe taballahu aleyhim summe amu ve sammu kesirun minhum vallahu basirun bima ya'melun."},
  {id: 741, sure_no: 72, ayet_no: 72, sure_adi: "Maide", text: "لَقَدْ كَفَرَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ هُوَ الْمَس۪يحُ ابْنُ مَرْيَمَۜ وَقَالَ الْمَس۪يحُ يَا بَن۪ٓي اِسْرَٓائ۪لَ اعْبُدُوا اللّٰهَ رَبّ۪ي وَرَبَّكُمْۜ اِنَّهُ مَنْ يُشْرِكْ بِاللّٰهِ فَقَدْ حَرَّمَ اللّٰهُ عَلَيْهِ الْجَنَّةَ وَمَأْوٰيهُ النَّارُۜ وَمَا لِلظَّالِم۪ينَ مِنْ اَنْصَارٍ", translation: "Ant olsun Meyem Oğlu Mesih'e, \"O Allah'tır.\" diyenler Kafir oldular. Oysaki Mesih: \"Ey İsrailoğulları! Benim de sizin de Rabb'iniz olan Allah'a kulluk edin. Kim Allah'a şirk koşarsa, Allah ona Cennet'i haram etmiştir. Ve onun varacağı yer ateştir. Zalimler için hiçbir yardımcı yoktur.\" dedi.", transliteration: "Lekad keferallezine kalu innallahe huvel mesihubnu meryem ve kalel mesihu ya beni israila'budullahe rabbi ve rabbekum innehu men yuşrik billahi fekad harremallahu aleyhil cennete ve me'vahun nar ve ma liz zalimine min ensar."},
  {id: 742, sure_no: 73, ayet_no: 73, sure_adi: "Maide", text: "لَقَدْ كَفَرَ الَّذ۪ينَ قَالُٓوا اِنَّ اللّٰهَ ثَالِثُ ثَلٰثَةٍۢ وَمَا مِنْ اِلٰهٍ اِلَّٓا اِلٰهٌ وَاحِدٌۜ وَاِنْ لَمْ يَنْتَهُوا عَمَّا يَقُولُونَ لَيَمَسَّنَّ الَّذ۪ينَ كَفَرُوا مِنْهُمْ عَذَابٌ اَل۪يمٌ", translation: "Ant olsun \"Allah, üçün üçüncüsüdür.\" diyenler Kafir oldular. Oysaki bir tek ilahtan başka ilah yoktur. Ve eğer bu söylediklerinden vazgeçmezlerse, onlardan Kafirlik edenlere elbette elem verici bir azap dokunacaktır.", transliteration: "Lekad keferellezine kalu innallahe salisu selasetin ve ma min ilahin illa ilahun vahid ve in lem yentehu amma yekulune le yemessennellezine keferu minhum azabun elim."},
  {id: 743, sure_no: 74, ayet_no: 74, sure_adi: "Maide", text: "اَفَلَا يَتُوبُونَ اِلَى اللّٰهِ وَيَسْتَغْفِرُونَهُۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "Hala Allah'a tevbe edip mağfiret dilemiyorlar mı? Oysaki Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "E fe la yetubune ilallahi ve yestagfiruneh vallahu gafurun rahim."},
  {id: 744, sure_no: 75, ayet_no: 75, sure_adi: "Maide", text: "مَا الْمَس۪يحُ ابْنُ مَرْيَمَ اِلَّا رَسُولٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۜ وَاُمُّهُ صِدّ۪يقَةٌۜ كَانَا يَأْكُلَانِ الطَّعَامَۜ اُنْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْاٰيَاتِ ثُمَّ انْظُرْ اَنّٰى يُؤْفَكُونَ", translation: "İyi bilin ki, Meyem Oğlu Mesih, yalnızca bir Resul'dür. Ondan önce de Resuller gelip geçti. O'nun annesi çok dürüsttür. İkisi de diğer insanlar gibi yer, içerlerdi. Onlar için ayetleri nasıl açıkladığımıza bak ve sonra nasıl döndürüldüklerini gör.", transliteration: "Melmesihubnu meryeme illa resul, kad halet min kablihir rusul ve ummuhu sıddikah kana ye'kulanit taam unzur keyfe nubeyyinu lehumul ayati summenzur enna yu'fekun."},
  {id: 745, sure_no: 76, ayet_no: 76, sure_adi: "Maide", text: "قُلْ اَتَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَمْلِكُ لَكُمْ ضَراًّ وَلَا نَفْعاًۜ وَاللّٰهُ هُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "De ki: \"Allah'ı bırakıp, size bir yarar sağlamaya da bir zarar vermeye de gücü yetmeyen şeylere mi kulluk yapıyorsunuz? Allah, Her Şeyi Duyan, Her Şeyi Bilen'dir.\"", transliteration: "Kul e ta'budune min dunillahi ma la yemliku lekum darran ve la nef'a vallahu huves semiul alim."},
  {id: 746, sure_no: 77, ayet_no: 77, sure_adi: "Maide", text: "قُلْ يَٓا اَهْلَ الْكِتَابِ لَا تَغْلُوا ف۪ي د۪ينِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُٓوا اَهْوَٓاءَ قَوْمٍ قَدْ ضَلُّوا مِنْ قَبْلُ وَاَضَلُّوا كَث۪يراً وَضَلُّوا عَنْ سَوَٓاءِ السَّب۪يلِ۟", translation: "De ki: \"Ey Kitap Ehli! Gerçeğin dışına çıkarak, dininizin sınırlarını ihlal etmeyin. Daha önce sapan ve birçoğunu saptıran ve düzgün yoldan sapmış olan bir halkın hevasına uymayın.", transliteration: "Kul ya ehlel kitabi, la taglu fi dinikum gayral hakkı ve la tettebi'u ehvae kavmin kad dallu min kablu ve edallu kesiran ve dallu an sevais sebil."},
  {id: 747, sure_no: 78, ayet_no: 78, sure_adi: "Maide", text: "لُعِنَ الَّذ۪ينَ كَفَرُوا مِنْ بَن۪ٓي اِسْرَٓائ۪لَ عَلٰى لِسَانِ دَاوُ۫دَ وَع۪يسَى ابْنِ مَرْيَمَۜ ذٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ", translation: "İsrailoğullarından Kafir olan kimseler, asi olmaları ve haddi aşmaları nedeniyle, Davud'un ve Meyrem Oğlu İsa'nın diliyle lanetlendiler.", transliteration: "Luinellezine keferu min beni israile ala lisani davude ve isebni meryem zalike bima asav ve kanu ya'tedun."},
  {id: 748, sure_no: 79, ayet_no: 79, sure_adi: "Maide", text: "كَانُوا لَا يَتَنَاهَوْنَ عَنْ مُنْكَرٍ فَعَلُوهُۜ لَبِئْسَ مَا كَانُوا يَفْعَلُونَ", translation: "Yaptıkları kötü şeylerde, birbirlerini uyarmıyorlardı. Gerçekten yapmakta oldukları şey ne kadar çirkindi.", transliteration: "Kanu la yetenahevne an munkerin fealuh lebi'se ma kanu yef'alun."},
  {id: 749, sure_no: 80, ayet_no: 80, sure_adi: "Maide", text: "تَرٰى كَث۪يراً مِنْهُمْ يَتَوَلَّوْنَ الَّذ۪ينَ كَفَرُواۜ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ اَنْفُسُهُمْ اَنْ سَخِطَ اللّٰهُ عَلَيْهِمْ وَفِي الْعَذَابِ هُمْ خَالِدُونَ", translation: "Onlardan çoğunun, Kafirleri veli edindiklerini görürsün. Nefislerinin kendileri için sunduğu şey ne kötüdür. Allah, onlara gazap etmiştir ve onlar sürekli azapta kalacaklardır.", transliteration: "Tera kesiran minhum yetevellevnellezine keferu lebi'se ma kaddemet lehum enfusuhum en sehıtallahu aleyhim ve fil azabi hum halidun."},
  {id: 750, sure_no: 81, ayet_no: 81, sure_adi: "Maide", text: "وَلَوْ كَانُوا يُؤْمِنُونَ بِاللّٰهِ وَالنَّبِيِّ وَمَٓا اُنْزِلَ اِلَيْهِ مَا اتَّخَذُوهُمْ اَوْلِيَٓاءَ وَلٰكِنَّ كَث۪يراً مِنْهُمْ فَاسِقُونَ", translation: "Eğer onlar, Allah'a, Nebi'ye ve ona indirilene iman etselerdi, onları evliya edinmezlerdi. Fakat onlardan pek çoğu fasıktır.", transliteration: "Ve lev kanu yu'minune billahi ven nebiyyi ve ma unzile ileyhi mettehazuhum evliyae ve lakinne kesiren minhum fasikun."},
  {id: 751, sure_no: 82, ayet_no: 82, sure_adi: "Maide", text: "لَتَجِدَنَّ اَشَدَّ النَّاسِ عَدَاوَةً لِلَّذ۪ينَ اٰمَنُوا الْيَهُودَ وَالَّذ۪ينَ اَشْرَكُواۚ وَلَتَجِدَنَّ اَقْرَبَهُمْ مَوَدَّةً لِلَّذ۪ينَ اٰمَنُوا الَّذ۪ينَ قَالُٓوا اِنَّا نَصَارٰىۜ ذٰلِكَ بِاَنَّ مِنْهُمْ قِسّ۪يس۪ينَ وَرُهْبَاناً وَاَنَّهُمْ لَا يَسْتَكْبِرُونَ", translation: "İman Eden kimselere düşmanlık yönünden insanların en çetininin Yahudiler ve Müşrikler olduğunu görürsün. Ve yine İman Eden kimseler için sevgice daha yakın olanların da biz Nesara'yiz diyenler olduğunu göreceksin. Bunun nedeni, kuşkusuz bunların içinde gerçekten büyüklük taslamayan keşişler ve rahipler olmasıdır.", transliteration: "Le tecidenne eşedden nasi adaveten lillezine amenul yehude vellezine eşraku, ve le tecidenne akrabehum meveddeten lillezine amenullezine kalu inna nasara zalike bi enne minhum kıssisine ve ruhbanen ve ennehum la yestekbirun."},
  {id: 752, sure_no: 83, ayet_no: 83, sure_adi: "Maide", text: "وَاِذَا سَمِعُوا مَٓا اُنْزِلَ اِلَى الرَّسُولِ تَرٰٓى اَعْيُنَهُمْ تَف۪يضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّۚ يَقُولُونَ رَبَّنَٓا اٰمَنَّا فَاكْتُبْنَا مَعَ الشَّاهِد۪ينَ", translation: "Resul'e indirileni duydukları zaman, gerçeği anlamalarından dolayı onların gözlerinin yaşla dolup taştığını görürsün. Derler ki: \"Ey Rabb'imiz! İman ettik, öyleyse bizi şahitlerle beraber yaz.\"", transliteration: "Ve iza semiu ma unzile ilerresuli tera a'yunehum tefidu mined dem'ı mimma arefu minel hakk, yekulune rabbena amenna fektubna meaş şahidin."},
  {id: 753, sure_no: 84, ayet_no: 84, sure_adi: "Maide", text: "وَمَا لَنَا لَا نُؤْمِنُ بِاللّٰهِ وَمَا جَٓاءَنَا مِنَ الْحَقِّۙ وَنَطْمَعُ اَنْ يُدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصَّالِح۪ينَ", translation: "\"Ve bize ne oluyor ki, Allah'a ve bize gelen gerçeğe iman etmeyelim: Rabb'imizin bizi iyi kimseler topluluğuna dahil etmesi umut ve beklentisi içinde iken.\"", transliteration: "Ve ma lena la nu'minu billahi ve ma caena minel hakkı ve natmeu en yudhılena rabbuna meal kavmis salihin."},
  {id: 754, sure_no: 85, ayet_no: 85, sure_adi: "Maide", text: "فَاَثَابَهُمُ اللّٰهُ بِمَا قَالُوا جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَذٰلِكَ جَزَٓاءُ الْمُحْسِن۪ينَ", translation: "Böyle söylemelerinden dolayı Allah da karşılık olarak içinden ırmaklar akan Cennetlerle ödüllendirdi. Onlar, orada sürekli kalacaklardır. İşte muhsinlere verilen karşılık budur.", transliteration: "Fe esabehumullahu bima kalu cennatin tecri min tahtihel enharu halidine fiha ve zalike cezaul muhsinin."},
  {id: 755, sure_no: 86, ayet_no: 86, sure_adi: "Maide", text: "وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ الْجَح۪يمِ۟", translation: "Kafir olup ayetlerimizi yalanlayan kimseler, işte onlar Cehennem halkıdırlar.", transliteration: "Vellezine keferu ve kezzebu bi ayatina ulaike ashabul cahim."},
  {id: 756, sure_no: 87, ayet_no: 87, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُحَرِّمُوا طَيِّبَاتِ مَٓا اَحَلَّ اللّٰهُ لَكُمْ وَلَا تَعْتَدُواۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَد۪ينَ", translation: "Ey İman Edenler! Allah'ın size helal kıldığı tayyibat olan şeyleri haram saymayın. Haddi aşmayın. Allah, haddi aşanları sevmez.", transliteration: "Ya eyyuhellezine amenu la tuharrimu tayyibati ma ehallallahu lekum ve la ta'tedu innallahe la yuhibbul mu'tedin."},
  {id: 757, sure_no: 88, ayet_no: 88, sure_adi: "Maide", text: "وَكُلُوا مِمَّا رَزَقَكُمُ اللّٰهُ حَلَالاً طَيِّباًۖ وَاتَّقُوا اللّٰهَ الَّـذ۪ٓي اَنْتُمْ بِه۪ مُؤْمِنُونَ", translation: "Allah'ın sizi rızıklandırdığı şeylerin helal ve tayyib olanlarından yiyin. İman ettiğiniz Allah'a karşı takvalı olun.", transliteration: "Ve kulu mimma razakakumullahu halalen tayyiben vettekullahellezi entum bihi mu'minun."},
  {id: 758, sure_no: 89, ayet_no: 89, sure_adi: "Maide", text: "لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ ف۪ٓي اَيْمَانِكُمْ وَلٰكِنْ يُؤَاخِذُكُمْ بِمَا عَقَّدْتُمُ الْاَيْمَانَۚ فَكَفَّارَتُهُٓ اِطْعَامُ عَشَرَةِ مَسَاك۪ينَ مِنْ اَوْسَطِ مَا تُطْعِمُونَ اَهْل۪يكُمْ اَوْ كِسْوَتُهُمْ اَوْ تَحْر۪يرُ رَقَبَةٍۜ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍۜ ذٰلِكَ كَفَّارَةُ اَيْمَانِكُمْ اِذَا حَلَفْتُمْۜ وَاحْفَظُٓوا اَيْمَانَكُمْۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Allah, kasıtsız yeminlerinizden dolayı sizi sorumlu tutmaz, ancak bilinçli olarak ettiğiniz yeminlerden dolayı sorumlu tutar. Bunun bedeli ailenize yedirdiğinizin ortalaması üzerinden on yoksulu yedirmek veya onları giydirmek veya bir rekabeyi özgürlüğüne kavuşturmaktır. Fakat bunlara gücü yetmeyene üç gün siyam vardır. Bozduğunuz yeminlerinizin bedeli budur. Yeminlerinizi bozmayın. Allah, size ayetlerini böyle açıklıyor. Umulur ki şükredersiniz.", transliteration: "La yuahizukumullahu bil lagvi fi eymanikum ve lakin yuahizukum bima akkadtumul eyman, fe keffaretuhu it'amu aşereti mesakine min evsatı ma tut'ımune ehlikum ev kisvetuhum ev tahriru rakabeh fe men lem yecid fe sıyamu selaseti eyyam zalike keffaretu eymanikum iza haleftum vahfezu eymanekum kezalike yubeyyinullahu lekum ayatihi leallekum teşkurun."},
  {id: 759, sure_no: 90, ayet_no: 90, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْاَنْصَابُ وَالْاَزْلَامُ رِجْسٌ مِنْ عَمَلِ الشَّيْطَانِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَ", translation: "Ey İman Edenler! Hamr, kumar, dikili taşlar ve şans oyunları şeytan işi birer pisliktir. Bunlardan kaçının ki kurtuluşa eresiniz.", transliteration: "Ya eyyuhellezine amenu innemel hamru vel meysiru vel ensabu vel ezlamu ricsun min ameliş şeytani fectenibuhu leallekum tuflihun."},
  {id: 760, sure_no: 91, ayet_no: 91, sure_adi: "Maide", text: "اِنَّمَا يُر۪يدُ الشَّيْطَانُ اَنْ يُوقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَٓاءَ فِي الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَنْ ذِكْرِ اللّٰهِ وَعَنِ الصَّلٰوةِۚ فَهَلْ اَنْتُمْ مُنْتَهُونَ", translation: "Şeytan, hamr ve kumarla aranıza düşmanlık ve kin sokmak; sizi Allah'ın öğütlerini dinlemekten ve salattan uzaklaştırmak ister. O halde bunlardan vazgeçmeyecek misiniz?", transliteration: "İnnema yuriduş şeytanu en yukia beynekumul adavete vel bagdae fil hamri vel meysiri ve yasuddekum an zikrillahi ve anis salah, fe hel entum muntehun."},
  {id: 761, sure_no: 92, ayet_no: 92, sure_adi: "Maide", text: "وَاَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَ وَاحْذَرُواۚ فَاِنْ تَوَلَّيْتُمْ فَاعْلَمُٓوا اَنَّمَا عَلٰى رَسُولِنَا الْبَلَاغُ الْمُب۪ينُ", translation: "Ve Allah'a itaat edin ve Resul'e itaat edin. Sakının. Eğer bu buyrukları dinlemezseniz, bilin ki resulümüze düşen açıkça duyurmaktır.", transliteration: "Ve etiullahe ve etiur resule vahzeru, fe in tevelleytum fa'lemu ennema ala resulinel belagul mubin."},
  {id: 762, sure_no: 93, ayet_no: 93, sure_adi: "Maide", text: "لَيْسَ عَلَى الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جُنَاحٌ ف۪يمَا طَعِمُٓوا اِذَا مَا اتَّقَوْا وَاٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ ثُمَّ اتَّقَوْا وَاٰمَنُوا ثُمَّ اتَّقَوْا وَاَحْسَنُواۜ وَاللّٰهُ يُحِبُّ الْمُحْسِن۪ينَ۟", translation: "İman eden ve salihatı yapan kimselere; iman edip salihatı yaptıkları sürece takva sahipleri değilken yiyip içtiklerinden dolayı bir sorumluluk yoktur. Sonra takvalı olup, iman ettikleri, sonra takvalı olup iyilik yaptıkları takdirde, bilin ki Allah, muhsin olanları sever.", transliteration: "Leyse alellezine amenu ve amilus salihati cunahun fima taimu iza mettekav ve amenu ve amilus salihati summettekav ve amenu summettekav ve ahsenu vallahu yuhibbul muhsinin."},
  {id: 763, sure_no: 94, ayet_no: 94, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَيَبْلُوَنَّكُمُ اللّٰهُ بِشَيْءٍ مِنَ الصَّيْدِ تَنَالُهُٓ  اَيْد۪يكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللّٰهُ مَنْ يَخَافُهُ بِالْغَيْبِۚ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهُ عَذَابٌ اَل۪يمٌ", translation: "Ey İman Edenler! Allah, ellerinizin ve oklarınızın ulaşabildiği bir kısım avla sizi dener. Ki görünmeyen yerde kendisine karşı gelmekten sakınanların kimler olduğunu bilmek için. Bundan sonra kim haddi aşarsa onun için elem verici bir azap vardır.", transliteration: "Ya eyyuhellezine amenu le yebluvennekumullahu bi şey'in mines saydı tenaluhu eydikum ve rimahukum li ya'lemallahu men yahafuhu bil gayb, fe meni'teda ba'de zalike fe lehu azabun elim."},
  {id: 764, sure_no: 95, ayet_no: 95, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَقْتُلُوا الصَّيْدَ وَاَنْتُمْ حُرُمٌۜ وَمَنْ قَتَلَهُ مِنْكُمْ مُتَعَمِّداً فَجَزَٓاءٌ مِثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِه۪ ذَوَا عَدْلٍ مِنْكُمْ هَدْياً بَالِغَ الْكَعْبَةِ اَوْ كَفَّارَةٌ طَعَامُ مَسَاك۪ينَ اَوْ عَدْلُ ذٰلِكَ صِيَاماً لِيَذُوقَ وَبَالَ اَمْرِه۪ۜ عَفَا اللّٰهُ عَمَّا سَلَفَۜ وَمَنْ عَادَ فَيَنْتَقِمُ اللّٰهُ مِنْهُۜ وَاللّٰهُ عَز۪يزٌ ذُوانْتِقَامٍ", translation: "Ey İman Edenler! İhramlıyken av hayvanı öldürmeyin. Sizden kim bile bile öldürürse, karşılık olarak yapacağı şey, sizden adil iki kişinin kararıyla, avlanılan şeye denk Kabe'ye ulaşacak hediye göndermek veya yoksulları doyurmak veya buna denk siyamdır ki bu yaptığı işin vebalini tatması içindir. Allah geçmiştekini affetti. Kim aynı suçu tekrar ederse, Allah onun hesabını sorar. Ve Allah, Mutlak Üstün Olan'dır, Hesap Sorucu'dur.", transliteration: "Ya eyyuhellezine amenu la taktulus sayde ve entum hurum ve men katelehu minkum muteammiden fe cezaun mislu ma katele min en neami yahkumu bihi zeva adlin minkum hedyen baligal ka'beti ev keffaratun taamu mesakine ev adlu zalike siyamen li yezuka vebale emrih afallahu amma selef ve men ade fe yentakimullahu minh vallahu azizun zuntikam."},
  {id: 765, sure_no: 96, ayet_no: 96, sure_adi: "Maide", text: "اُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهُ مَتَاعاً لَكُمْ وَلِلسَّيَّارَةِۚ وَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُماًۜ وَاتَّقُوا اللّٰهَ الَّـذ۪ٓي اِلَيْهِ تُحْشَرُونَ", translation: "Deniz avı ve onu yemek ve ondan geçim temin etmek size ve yolculukta olanlara helal kılındı. Ve ihramlı olduğunuz süre içinde kara avı size haram kılındı. Huzuruna toplanacağınız Allah'a karşı takva sahibi olun.", transliteration: "Uhille lekum saydul bahri ve taamuhu metaan lekum ve lis seyyarah, ve hurrime aleykum saydul berri ma dumtum huruma vettekullahellezi ileyhi tuhşerun."},
  {id: 766, sure_no: 97, ayet_no: 97, sure_adi: "Maide", text: "جَعَلَ اللّٰهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيَاماً لِلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَٓائِدَۜ ذٰلِكَ لِتَعْلَمُٓوا اَنَّ اللّٰهَ يَعْلَمُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَاَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Allah, Beyt-i Haram olan Kabe'yi ve o haram olan ayı ve hediye kurbanlığı ve gerdanlıkları insanlar için kıyam yaptı. Bilesiniz ki Allah, göklerdekini ve yerdekini bilmektedir. Kuşkusuz, Allah, Her Şeyi Bilen'dir.", transliteration: "Cealallahul ka'betel beytel harame kıyamen lin nasi veş şehral harame vel hedye vel kalaid zalike li ta'lemu ennellahe ya'lemu ma fis semavati ve ma fil ardı ve ennellahe bikulli şey'in alim."},
  {id: 767, sure_no: 98, ayet_no: 98, sure_adi: "Maide", text: "اِعْلَمُٓوا اَنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ وَاَنَّ اللّٰهَ غَفُورٌ رَح۪يمٌۜ", translation: "İyi bilin ki; Allah, Cezası Çok Şiddetli Olan'dır ve kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "I'lemu ennellahe şedidul ikabi ve ennellahe gafurun rahim."},
  {id: 768, sure_no: 99, ayet_no: 99, sure_adi: "Maide", text: "مَا عَلَى الرَّسُولِ اِلَّا الْبَلَاغُۜ وَاللّٰهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ", translation: "Resule düşen, yalnızca çağrıda bulunmaktır. Açığa vurduğunuzu da gizlediğinizi de Allah bilir.", transliteration: "Ma aler resuli illel belag vallahu ya'lemu ma tubdune ve ma tektumun."},
  {id: 769, sure_no: 100, ayet_no: 100, sure_adi: "Maide", text: "قُلْ لَا يَسْتَوِي الْخَب۪يثُ وَالطَّيِّبُ وَلَوْ اَعْجَبَكَ كَـثْرَةُ الْخَب۪يثِۚ فَاتَّقُوا اللّٰهَ يَٓا اُو۬لِي الْاَلْبَابِ لَعَلَّكُمْ تُفْلِحُونَ۟", translation: "\"De ki:\" Habis olanla tayyib olan bir değildir. Velev ki habis olanın çokluğu hoşuna gitse de. Ey selim akıl sahipleri! Allah'a karşı takvalı olun ki kurtuluşa eresiniz.", transliteration: "Kul la yestevil habisu vet tayyibu ve lev a'cebeke kesretul habis, fettekullahe ya ulil elbabi leallekum tuflihun."},
  {id: 770, sure_no: 101, ayet_no: 101, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَسْـَٔلُوا عَنْ اَشْيَٓاءَ اِنْ تُبْدَ لَكُمْ تَسُؤْكُمْۚ وَاِنْ تَسْـَٔلُوا عَنْهَا ح۪ينَ يُنَزَّلُ الْقُرْاٰنُ تُبْدَ لَكُمْۜ عَفَا اللّٰهُ عَنْهَاۜ وَاللّٰهُ غَفُورٌ حَل۪يمٌ", translation: "Ey İman Edenler! Her şeyi sormayın! Açıklandığında sizi zor duruma düşürecek şeyler vardır. Oysaki Kur'an indirildiği anda o şeyleri sorarsanız size açıklanır. Allah, onlardan vazgeçmiştir. Allah, Çok Bağışlayıcı'dır, Çok Hoş Görülü'dür.", transliteration: "Ya eyyuhellezine amenu la tes'elu an eşyae in tubde lekum tesu'kum, ve in tes'elu anha hine yunezzelul kur'anu tubde lekum afallahu anha vallahu gafurun halim."},
  {id: 771, sure_no: 102, ayet_no: 102, sure_adi: "Maide", text: "قَدْ سَاَلَهَا قَوْمٌ مِنْ قَبْلِكُمْ ثُمَّ اَصْبَحُوا بِهَا كَافِر۪ينَ", translation: "Gerçekten, sizden önce de onları bir halk sormuştu da sonra onları küfredenler olmuşlardı.", transliteration: "Kad seeleha kavmun min kablikum summe asbahu biha kafirin."},
  {id: 772, sure_no: 103, ayet_no: 103, sure_adi: "Maide", text: "مَا جَعَلَ اللّٰهُ مِنْ بَح۪يرَةٍ وَلَا سَٓائِبَةٍ وَلَا وَص۪يلَةٍ وَلَا حَامٍۙ وَلٰكِنَّ الَّذ۪ينَ كَفَرُوا يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَۜ وَاَكْثَرُهُمْ لَا يَعْقِلُونَ", translation: "Allah, ne \"Bahire\" ne \"Saibe\" ne \"Vasile\" ne de \"Ham\" diye bir şeyi meşru kıldı. Fakat Kafirler yalan söyleyerek Allah'a iftira ediyorlar. Ve onların çoğu akıllarını kullanmıyorlar.", transliteration: "Ma cealallahu min bahiretin ve la saibetin ve la vasiletin ve la hamin ve lakinnellezine keferu yefterune alallahi kezib ve ekseruhum la ya'kılun."},
  {id: 773, sure_no: 104, ayet_no: 104, sure_adi: "Maide", text: "وَاِذَا ق۪يلَ لَهُمْ تَعَالَوْا اِلٰى مَٓا اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُولِ قَالُوا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ اٰبَٓاءَنَاۜ اَوَلَوْ كَانَ اٰبَٓاؤُ۬هُمْ لَا يَعْلَمُونَ شَيْـٔاً وَلَا يَهْتَدُونَ", translation: "Onlara, Allah'ın indirdiğine ve Resul'e gelin dendiği zaman, \"Atalarımızı üzerinde bulduğumuz şey bize yeter.\" dediler. Peki ya ataları bir şey bilmeyen ve doğru yolda olmayan kimselerse?", transliteration: "Ve iza kile lehum tealev ila ma enzelallahu ve iler resuli kalu hasbuna ma vecedna aleyhi abaena e ve lev kane abauhum la ya'lemune şey'en ve la yehtedun."},
  {id: 774, sure_no: 105, ayet_no: 105, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا عَلَيْكُمْ اَنْفُسَكُمْۚ لَا يَضُرُّكُمْ مَنْ ضَلَّ اِذَا اهْتَدَيْتُمْۜ اِلَى اللّٰهِ مَرْجِعُكُمْ جَم۪يعاً فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Ey İman Edenler! Siz, kendinizden sorumlusunuz. Eğer siz doğru yol üzerindeyseniz, sapan kimseler size zarar veremez. Dönüşünüz Allah'adır. Yaptığınız şeyleri size haber verecektir.", transliteration: "Ya eyyuhellezine amenu aleykum enfusekum, la yadurrukum men dalle izehtedeytum ilallahi merciukum cemian fe yunebbiukum bima kuntum ta'melun."},
  {id: 775, sure_no: 106, ayet_no: 106, sure_adi: "Maide", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا شَهَادَةُ بَيْنِكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ ح۪ينَ الْوَصِيَّةِ اثْنَانِ ذَوَا عَدْلٍ مِنْكُمْ اَوْ اٰخَرَانِ مِنْ غَيْرِكُمْ اِنْ اَنْتُمْ ضَرَبْتُمْ فِي الْاَرْضِ فَاَصَابَتْكُمْ مُص۪يبَةُ الْمَوْتِۜ تَحْبِسُونَهُمَا مِنْ بَعْدِ الصَّلٰوةِ فَيُقْسِمَانِ بِاللّٰهِ اِنِ ارْتَبْتُمْ لَا نَشْتَر۪ي بِه۪ ثَمَناً وَلَوْ كَانَ ذَا قُرْبٰىۙ وَلَا نَكْتُمُ شَهَادَةَ اللّٰهِ اِنَّٓا اِذاً لَمِنَ الْاٰثِم۪ينَ", translation: "Ey İman Edenler! Eğer birinizde ölüm belirtileri ortaya çıkarsa, vasiyet anında içinizden adalet sahibi iki kişi aranızda tanıklık etsin. Veya yeryüzünde yolculuk ederken ölüm musibeti size isabet ederse, sizden olmayan iki kişi tanıklık etsin. Eğer kuşku duyarsanız o iki kişiyi salattan sonra alıkoyun. \"Yakınımız da olsa tanıklığımızı hiçbir bedele satmayacağız ve Allah'ın tanıklığını gizlemeyeceğiz. Yoksa öyle yaparsak, günahkarlardan oluruz.\" diye Allah'a yemin etsinler.", transliteration: "Ya eyyuhellezine amenu şehadetu beynikum iza hadara ehadekumul mevtu hinel vasiyyetisnani zeva adlin minkum ev aharani min gayrikum in entum darabtum fil ardı fe esabetkum musibetul mevt tahbisunehuma min ba'dis salati fe yuksimani billahi in irtebtum la neşteri bihi semenen ve lev kane za kurba ve la nektumu şehadetallahi inna izen le minel asimin."},
  {id: 776, sure_no: 107, ayet_no: 107, sure_adi: "Maide", text: "فَاِنْ عُثِرَ عَلٰٓى اَنَّهُمَا اسْتَحَقَّٓا اِثْماً فَاٰخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ الَّذ۪ينَ اسْتَحَقَّ عَلَيْهِمُ الْاَوْلَيَانِ فَيُقْسِمَانِ بِاللّٰهِ لَشَهَادَتُـنَٓا اَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَاۘ اِنَّٓا اِذاً لَمِنَ الظَّالِم۪ينَ", translation: "Eğer o ikisinin günah işledikleri anlaşılırsa, onların yerine mirasa hak sahibi olanlardan iki kişi geçer: \"Bizim tanıklığımız o ikisinin tanıklığından daha doğrudur ve biz haddi aşmadık, öyle olsaydı kuşkusuz biz elbette zalimlerden oluruz.\" diye Allah'a yemin ederler.", transliteration: "Fe in usire ala ennehumastehakka ismen fe aharani yekumani makamehuma minellezinestehakka aleyhimul evleyani fe yuksimani billahi le şehadetuna ehakku min şehadetihima ve ma'tedeyna, inna izen le minez zalimin."},
  {id: 777, sure_no: 108, ayet_no: 108, sure_adi: "Maide", text: "ذٰلِكَ اَدْنٰٓى اَنْ يَأْتُوا بِالشَّهَادَةِ عَلٰى وَجْهِهَٓا اَوْ يَخَافُٓوا اَنْ تُرَدَّ اَيْمَانٌ بَعْدَ اَيْمَانِهِمْۜ وَاتَّقُوا اللّٰهَ وَاسْمَعُواۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ۟", translation: "İşte bu, tanıklığın tam anlamıyla yerine getirilmesi; yoksa yeminden sonra yeminlerinin başkalarının yeminleriyle geri çevrilmesinden korkulması bakımından daha uygundur. Allah için takvalı olun ve dinleyin. Allah, fasık olan toplumu doğru yola iletmez.", transliteration: "Zalike edna en ye'tu biş şehadeti ala vechiha ev yehafuen turadde eymanun ba'de eymanihim vettekullahe vesmeu vallahu la yehdil kavmel fasikin."},
  {id: 778, sure_no: 109, ayet_no: 109, sure_adi: "Maide", text: "يَوْمَ يَجْمَعُ اللّٰهُ الرُّسُلَ فَيَقُولُ مَاذَٓا اُجِبْتُمْۜ قَالُوا لَا عِلْمَ لَنَاۜ اِنَّكَ اَنْتَ عَلَّامُ الْغُيُوبِ", translation: "Allah, Resulleri topladığı gün, \"Nasıl karşılandınız der?\" \"Bizim bir bilgimiz yok, doğrusu gaybleri bilen ancak Sen'sin.\" derler.", transliteration: "Yevme yecmeullahur rusule fe yekulu maza ucibtum kalu la ilme lena inneke ente allamul guyub."},
  {id: 779, sure_no: 110, ayet_no: 110, sure_adi: "Maide", text: "اِذْ قَالَ اللّٰهُ يَا ع۪يسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَت۪ي عَلَيْكَ وَعَلٰى وَالِدَتِكَۢ اِذْ اَيَّدْتُكَ بِرُوحِ الْقُدُسِ تُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلاًۚ وَاِذْ عَلَّمْتُكَ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرٰيةَ وَالْاِنْج۪يلَۚ وَاِذْ تَخْلُقُ مِنَ الطّ۪ينِ كَـهَيْـَٔةِ الطَّيْرِ بِاِذْن۪ي فَتَنْفُخُ ف۪يهَا فَتَكُونُ طَيْراً بِاِذْن۪ي وَتُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ بِاِذْن۪يۚ وَاِذْ تُخْرِجُ الْمَوْتٰى بِاِذْن۪يۚ وَاِذْ كَفَفْتُ بَن۪ٓي اِسْرَٓائ۪لَ عَنْكَ اِذْ جِئْتَهُمْ بِالْبَيِّنَاتِ فَقَالَ الَّذ۪ينَ كَفَرُوا مِنْهُمْ اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ", translation: "Allah, \"Ey Meryem Oğlu İsa! Senin ve annenin üzerinde olan nimetimi hatırla.\" Hani seni Kudus'un Ruhu ile desteklemiştim, insanlarla beşikte ve yetişkinlikte konuşuyordun. Ve hani sana Kitap'ı, Hikmet'i, Tevrat'ı ve İncil'i öğrettim. Ve hani Ben'im iznimle çamurdan kuş şeklinde bir şey tasarlıyordun ve ona üflüyordun, Ben'im iznimle hemen kuş oluyordu; kör olarak doğanı ve abrası Ben'im iznimle iyileştiriyordun. Hani Ben'im iznimle ölüleri çıkarıyordun. Hani İsrailoğullarını senden uzaklaştırdım. Hani onlara apaçık beyyinelerle geldiğinde, onlardan Kafir olanlar, \"Bu apaçık bir sihirden başka bir şey değildir.\" dediler.", transliteration: "İz kalellahu ya isebne meryemezkur ni'meti aleyke ve ala validetike iz eyyedtuke bi ruhil kudusi tukellimun nase fil mehdi ve kehl, ve iz allemtukel kitabe vel hikmete vet tevrate vel incil, ve iz tahluku minet tini ke hey'etit tayri bi izni fe tenfuhu fiha fe tekunu tayran bi izni ve tubriul ekmehe vel ebrasa bi izni, ve iz tuhricul mevta bi izni, ve iz kefeftu beni israile anke iz ci'tehum bil beyyinati fe kalellezine keferu minhum in haza illa sihrun mubin."},
  {id: 780, sure_no: 111, ayet_no: 111, sure_adi: "Maide", text: "وَاِذْ اَوْحَيْتُ اِلَى الْحَوَارِيّ۪نَ اَنْ اٰمِنُوا ب۪ي وَبِرَسُول۪يۚ قَالُٓوا اٰمَنَّا وَاشْهَدْ بِاَنَّـنَا مُسْلِمُونَ", translation: "Havarilere, Bana ve Ben'im Resul'üme iman etmelerini vahyettim. \"İman ettik.\" dediler. \"Ve tanık ol ki kuşkusuz, biz müslimleriz.\"", transliteration: "Ve iz evhaytu ilel havariyyine en aminu bi ve bi resuli, kalu amenna veşhed bi ennena muslimun."},
  {id: 781, sure_no: 112, ayet_no: 112, sure_adi: "Maide", text: "اِذْ قَالَ الْحَوَارِيُّونَ يَا ع۪يسَى ابْنَ مَرْيَمَ هَلْ يَسْتَط۪يعُ رَبُّكَ اَنْ يُنَزِّلَ عَلَيْنَا مَٓائِدَةً مِنَ السَّمَٓاءِۜ قَالَ اتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Hani havariler dedi: \"Ey Meryem Oğlu İsa, senin Rabb'inin üzerimize gökten bir sofra indirmeye gücü yeter mi?\" \"Eğer inanıyorsanız Allah'a karşı takvalı olun\" dedi.", transliteration: "İz kalel havariyyune ya isebne meryeme hel yestetiu rabbuke en yunezzile aleyna maideten mines sema kalettekullahe in kuntum mu'minin."},
  {id: 782, sure_no: 113, ayet_no: 113, sure_adi: "Maide", text: "قَالُوا نُر۪يدُ اَنْ نَأْكُلَ مِنْهَا وَتَطْمَئِنَّ قُلُوبُنَا وَنَعْلَمَ اَنْ قَدْ صَدَقْتَنَا وَنَكُونَ عَلَيْهَا مِنَ الشَّاهِد۪ينَ", translation: "\"Ondan yemeyi ve kalplerimizin iyice yatışmasını; senin bize doğru söylediğini bilmeyi ve buna doğrudan tanık olmayı istiyoruz.\" dediler.", transliteration: "Kalu nuridu en ne'kule minha ve tetmainne kulubuna ve na'leme en kad sadaktena ve nekune aleyha mineş şahidin."},
  {id: 783, sure_no: 114, ayet_no: 114, sure_adi: "Maide", text: "قَالَ ع۪يسَى ابْنُ مَرْيَمَ اللّٰهُمَّ رَبَّنَٓا اَنْزِلْ عَلَيْنَا مَٓائِدَةً مِنَ السَّمَٓاءِ تَكُونُ لَنَا ع۪يداً لِاَوَّلِنَا وَاٰخِرِنَا وَاٰيَةً مِنْكَۚ وَارْزُقْنَا وَاَنْتَ خَيْرُ الرَّازِق۪ينَ", translation: "Meyrem Oğlu İsa: \"Ey Allah'ım, Rabb'imiz, gökten üzerimize bir sofra indir. Ki o sofra bizim için; öncemiz ve sonramız için bir bayram olur ve Sen'den de bir ayet. Bizi rızıklandır ve Sen rızıklandıranların en hayırlısısın.\" dedi.", transliteration: "Kale isebnu meryemellahumme rabbena enzil aleyna maideten mines semai tekunu lena iden li evvelina ve ahirina ve ayeten mink, verzukna ve ente hayrur razikin."},
  {id: 784, sure_no: 115, ayet_no: 115, sure_adi: "Maide", text: "قَالَ اللّٰهُ اِنّ۪ي مُنَزِّلُهَا عَلَيْكُمْۚ فَمَنْ يَكْفُرْ بَعْدُ مِنْكُمْ فَاِنّ۪ٓي اُعَذِّبُهُ عَذَاباً لَٓا اُعَذِّبُهُٓ  اَحَداً مِنَ الْعَالَم۪ينَ۟", translation: "Allah: \"Ben, üzerinize onu indireceğim. Ama ondan sonra sizden kim kafirlik ederse, alemlerden hiç kimseyi azaplandırmadığım şekilde ona azap ederim.\" dedi.", transliteration: "Kalellahu inni munezziluha aleykum, fe men yekfur ba'du minkum fe inni uazzibuhu azaben la uazzibuhu ehaden minel alemin."},
  {id: 785, sure_no: 116, ayet_no: 116, sure_adi: "Maide", text: "وَاِذْ قَالَ اللّٰهُ يَا ع۪يسَى ابْنَ مَرْيَمَ ءَاَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُون۪ي وَاُمِّيَ اِلٰهَيْنِ مِنْ دُونِ اللّٰهِۜ قَالَ سُبْحَانَكَ مَا يَكُونُ ل۪ٓي اَنْ اَقُولَ مَا لَيْسَ ل۪ي بِحَقٍّۜ اِنْ كُنْتُ قُلْتُهُ فَقَدْ عَلِمْتَهُۜ تَعْلَمُ مَا ف۪ي نَفْس۪ي وَلَٓا اَعْلَمُ مَا ف۪ي نَفْسِكَۜ اِنَّكَ اَنْتَ عَلَّامُ الْغُيُوبِ", translation: "Allah: \"Ey Meryem Oğlu İsa! İnsanlara, Allah'tan başka beni ve annemi iki ilah edinin diye sen mi söyledin?\" buyurduğunda, \"Sen yücesin.\" dedi. \"Gerçek olmayan bir şeyi söylemek haddim değil. Ben onu söyleseydim, Sen onu bilirdin. Nefsimde olanı bilirsin, ben ise Sen'in zatında olanı bilmem. Sen, gaipleri eksiksiz bilensin.\"", transliteration: "Ve iz kalellahu ya isebne meryeme e ente kulte lin nasittehizuni ve ummiye ilaheyni min dunillah kale subhaneke ma yekunu li en ekule ma leyse li bi hakk in kuntu kultuhu fe kad alimteh ta'lemu ma fi nefsi ve la a'lemu ma fi nefsik inneke ente allemul guyub."},
  {id: 786, sure_no: 117, ayet_no: 117, sure_adi: "Maide", text: "مَا قُلْتُ لَهُمْ اِلَّا مَٓا اَمَرْتَن۪ي بِه۪ٓ  اَنِ اعْبُدُوا اللّٰهَ رَبّ۪ي وَرَبَّكُمْۚ وَكُنْتُ عَلَيْهِمْ شَه۪يداً مَا دُمْتُ ف۪يهِمْۚ فَلَمَّا تَوَفَّيْتَن۪ي كُنْتَ اَنْتَ الرَّق۪يبَ عَلَيْهِمْۜ وَاَنْتَ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ", translation: "\"Onlara, bana emrettiklerinden başkasını söylemedim. Benim ve sizin Rabb'iniz olan Allah'a kulluk edin dedim. Ben içlerinde olduğum sürece onlara gözetleyiciydim. Fakat beni vefat ettirince onların üzerinde gözetleyici yalnızca Sen oldun. Ve Sen, Her Şeye Tanıksın.\"", transliteration: "Ma kultu lehum illa ma emerteni bihi eni'budullahe rabbi ve rabbekum, ve kuntu aleyhim şehiden ma dumtu fihim, fe lemma teveffeyteni kunte enter rakibe aleyhim ve ente ala kulli şey'in şehid."},
  {id: 787, sure_no: 118, ayet_no: 118, sure_adi: "Maide", text: "اِنْ تُعَذِّبْهُمْ فَاِنَّهُمْ عِبَادُكَۚ وَاِنْ تَغْفِرْ لَهُمْ فَاِنَّكَ اَنْتَ الْعَز۪يزُ الْحَك۪يمُ", translation: "\"Eğer onlara azap edersen onlar Sen'in kullarındır. Ve eğer onları bağışlarsan, Mutlak Üstün Olan ve En İyi Hüküm Veren Sen'sin.\"", transliteration: "İn tuazzibhum fe innehum ibaduk, ve in tagfir lehum fe inneke entel azizul hakim."},
  {id: 788, sure_no: 119, ayet_no: 119, sure_adi: "Maide", text: "قَالَ اللّٰهُ هٰذَا يَوْمُ يَنْفَعُ الصَّادِق۪ينَ صِدْقُهُمْۜ لَهُمْ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوا عَنْهُۜ ذٰلِكَ الْفَوْزُ الْعَظ۪يمُ", translation: "Allah: \"Bugün, doğruluklarının doğrulara fayda sağlayacağı gündür.\" dedi. Onlar için, içinden nehirlerin aktığı Cennetler vardır. Orada süresiz kalacaklar. Allah onlardan, onlar da O'ndan razı olmuşlardır. İşte büyük başarı budur.", transliteration: "Kalellahu haza yevmu yenfeus sadikine sıdkuhum, lehum cennatun tecri min tahtihel enharu halidine fiha ebeda radiyallahu anhum ve radu anh zalikel fevzul azim."},
  {id: 789, sure_no: 120, ayet_no: 120, sure_adi: "Maide", text: "لِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا ف۪يهِنَّۜ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Göklerin, yerin ve bunların içindekilerin mülkü Allah'ındır. Ve O, Her Şeye Gücü Yeten'dir.", transliteration: "Lillahi mulkus semavati vel ardı ve ma fihin ve huve ala kulli şey'in kadir."},
  {id: 790, sure_no: 1, ayet_no: 1, sure_adi: "Enam", text: "اَلْحَمْدُ لِلّٰهِ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَجَعَلَ الظُّلُمَاتِ وَالنُّورَۜ  ثُمَّ الَّذ۪ينَ كَفَرُوا بِرَبِّهِمْ يَعْدِلُونَ", translation: "Hamd, gökleri ve yeri yaratan, karanlıkları ve aydınlığı var eden Allah'a özgüdür. Yine de Kafirler ilahlarını Rabb'leriyle denk tutuyorlar.", transliteration: "Elhamdu lillahillezi halakas semavati vel arda ve cealez zulumati ven nur, summellezine keferu bi rabbihim ya'dilun."},
  {id: 791, sure_no: 2, ayet_no: 2, sure_adi: "Enam", text: "هُوَ الَّذ۪ي خَلَقَكُمْ مِنْ ط۪ينٍ ثُمَّ قَضٰٓى اَجَلاًۜ وَاَجَلٌ مُسَمًّى عِنْدَهُ ثُمَّ اَنْتُمْ تَمْتَرُونَ", translation: "O, sizi çamurdan yarattı. Sonra eceli tayin etti. Ve adı belirlenmiş ecel O'nun yanındadır. Sonra da sizler hala kuşkulanıyorsunuz.", transliteration: "Huvellezi halakakum min tinin summe kada ecela, ve ecelun musemmen ındehu summe entum temterun."},
  {id: 792, sure_no: 3, ayet_no: 3, sure_adi: "Enam", text: "وَهُوَ اللّٰهُ فِي السَّمٰوَاتِ وَفِي الْاَرْضِۜ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُونَ", translation: "Göklerde ve yerde Allah O'dur. Gizlinizi de açığınızı da bilir. Ve ne kazandığınızı da bilir.", transliteration: "Ve huvellahu fis semavati ve fil ard, ya'lemu sirrakum ve cehrekum ve ya'lemu ma teksibun."},
  {id: 793, sure_no: 4, ayet_no: 4, sure_adi: "Enam", text: "وَمَا تَأْت۪يهِمْ مِنْ اٰيَةٍ مِنْ اٰيَاتِ رَبِّهِمْ اِلَّا كَانُوا عَنْهَا مُعْرِض۪ينَ", translation: "Onlara, ne zaman Rabb'lerinin ayetlerinden bir ayet gelse illaki ondan yüz çevirirler.", transliteration: "Ve ma te'tihim min ayetin min ayati rabbihim illa kanu anha mu'rıdin."},
  {id: 794, sure_no: 5, ayet_no: 5, sure_adi: "Enam", text: "فَقَدْ كَذَّبُوا بِالْحَقِّ لَمَّا جَٓاءَهُمْۜ فَسَوْفَ يَأْت۪يهِمْ اَنْبٰٓـؤُ۬ا مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Fakat kendilerine gelen hakkı yalanladılar. Onlara, yakında kendisi ile alay ettikleri şeyin haberleri gelecek.", transliteration: "Fe kad kezzebu bil hakkı lemma caehum, fe sevfe ye'tihim enbau ma kanubihi yestehziun."},
  {id: 795, sure_no: 6, ayet_no: 6, sure_adi: "Enam", text: "اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ مَكَّنَّاهُمْ فِي الْاَرْضِ مَا لَمْ نُمَكِّنْ لَكُمْ وَاَرْسَلْنَا السَّمَٓاءَ عَلَيْهِمْ مِدْرَاراًۖ وَجَعَلْنَا الْاَنْهَارَ تَجْر۪ي مِنْ تَحْتِهِمْ فَاَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَاَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْناً اٰخَر۪ينَ", translation: "Kendilerinden önce nice nesilleri yok ettiğimizi idrak etmiyorlar mı? Yeryüzünde size vermediğimiz imkanları onlara vermiştik. Üzerlerine bol bol yağmur göndermiş ve yerlerde ırmaklar akıtmıştık. İşledikleri suçları nedeniyle kendilerini yok ettik. Onlardan sonra başka nesiller meydana getirdik.", transliteration: "E lem yerev kem ehlekna min kablihim min karnin mekkennahum fil ardı ma lem numekkin lekum ve erselnes semae aleyhim midraren ve cealnal enhare tecri min tahtihim fe ehleknahum bi zunubihim ve enşe'na min ba'dihim karnen aharin."},
  {id: 796, sure_no: 7, ayet_no: 7, sure_adi: "Enam", text: "وَلَوْ نَزَّلْنَا عَلَيْكَ كِتَاباً ف۪ي قِرْطَاسٍ فَلَمَسُوهُ بِاَيْد۪يهِمْ لَقَالَ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ", translation: "Biz, sana kağıt üzerine yazılı bir metin indirmiş olsaydık da onlar da ona elleriyle dokunsalardı yine de Kafirler \"Bu, ancak apaçık bir sihirdir.\" derlerdi.", transliteration: "Ve lev nezzelna aleyke kitaben fi kırtasin fe le mesuhu bi eydihim le kalelezine keferu in haza illa sihrun mubin."},
  {id: 797, sure_no: 8, ayet_no: 8, sure_adi: "Enam", text: "وَقَالُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ مَلَكٌۜ وَلَوْ اَنْزَلْنَا مَلَكاً لَقُضِيَ الْاَمْرُ ثُمَّ لَا يُنْظَرُونَ", translation: "\"Ona bir melek indirilmeli değil miydi?\" dediler. Eğer bir melek indirmiş olsaydık iş bitirilmiş olurdu. Sonra onlara göz açtırılmazdı.", transliteration: "Ve kalu lev la unzile aleyhi melek, ve lev enzelna meleken, le kudıyel emru summe la yunzarun."},
  {id: 798, sure_no: 9, ayet_no: 9, sure_adi: "Enam", text: "وَلَوْ جَعَلْنَاهُ مَلَكاً لَجَعَلْنَاهُ رَجُلاً وَلَلَبَسْنَا عَلَيْهِمْ مَا يَلْبِسُونَ", translation: "Ve Biz, bir melek görevlendirseydik, onu da insan şeklinde gönderirdik ve onları düştükleri kuşkuya yine düşürürdük.", transliteration: "Ve lev cealnahu meleken le cealnahu raculen ve le lebesna aleyhim ma yelbisun."},
  {id: 799, sure_no: 10, ayet_no: 10, sure_adi: "Enam", text: "وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذ۪ينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟", translation: "Ant olsun, senden önce de Resullerle alay edildi. Onlarla alay edenleri, alaya aldıkları şey çepeçevre kuşatıverdi.", transliteration: "Ve lekadistuhzie bi rusulin min kablike fe haka billezine sehıru minhum ma kanu bihi yestehziun."},
  {id: 800, sure_no: 11, ayet_no: 11, sure_adi: "Enam", text: "قُلْ س۪يرُوا فِي الْاَرْضِ ثُمَّ انْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّب۪ينَ", translation: "De ki: \"Yeryüzünü dolaşın da yalanlayanların sonunun nasıl olduğunu görün!\"", transliteration: "Kul siru fil ardı summenzuru keyfe kane akıbetul mukezzibin."},
  {id: 801, sure_no: 12, ayet_no: 12, sure_adi: "Enam", text: "قُلْ لِمَنْ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ قُلْ لِلّٰهِۜ  كَتَبَ عَلٰى نَفْسِهِ الرَّحْمَةَۜ  لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ ف۪يهِۜ اَلَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ", translation: "De ki: \"Gökte ve yerde olan şeyler kimindir?\" De ki: \"Rahmeti Kendi üzerine yazan Allah'ındır.\" O, gerçekleşeceği kesin olan kıyamet gününde sizi mutlaka toplayacaktır. O kimseler ki kendi nefislerine hüsran ettiler, işte onlar iman etmezler.", transliteration: "Kul li men ma fis semavati vel ard, kul lillah, ketebe ala nefsihir rahmeh, le yecmeannekum ila yevmil kıyameti la reybe fih, ellezine hasiru enfusehum fe hum la yu'minun."},
  {id: 802, sure_no: 13, ayet_no: 13, sure_adi: "Enam", text: "وَلَهُ مَا سَكَنَ فِي الَّيْلِ وَالنَّهَارِۜ وَهُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Gece ve gündüzde barınıp yaşayan her şey O'nundur. O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve lehu ma sekene fil leyli ven nehar, ve huves semiul alim."},
  {id: 803, sure_no: 14, ayet_no: 14, sure_adi: "Enam", text: "قُلْ اَغَيْرَ اللّٰهِ اَتَّخِذُ وَلِياًّ فَاطِرِ السَّمٰوَاتِ وَالْاَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُۜ قُلْ اِنّ۪ٓي اُمِرْتُ اَنْ اَكُونَ اَوَّلَ مَنْ اَسْلَمَ وَلَا تَكُونَنَّ مِنَ الْمُشْرِك۪ينَ", translation: "De ki: \"Göklerin ve yerin fıtratını belirleyen; herkesi yedirip içiren, fakat Kendi'sinin yeme ve içmeye ihtiyacı olmayan Allah'tan başkasını mı veli edineceğim?\" De ki: \"Bana, teslim olanların ilki olmam emredildi.\" Ve sakın Müşriklerden olma!", transliteration: "Kul e gayrallahi ettehızu veliyyen fatırıs semavati vel ardı ve huve yut'ımu ve la yut'am, kul inni umirtu en ekune evvele men esleme ve la tekunenne minel muşrikin."},
  {id: 804, sure_no: 15, ayet_no: 15, sure_adi: "Enam", text: "قُلْ اِنّ۪ٓي اَخَافُ اِنْ عَصَيْتُ رَبّ۪ي عَذَابَ يَوْمٍ عَظ۪يمٍ", translation: "\"Eğer Rabb'ime isyan edersem; büyük günün azabından korkarım.\"", transliteration: "Kul inni ehafu in asaytu rabbi azabe yevmin azim."},
  {id: 805, sure_no: 16, ayet_no: 16, sure_adi: "Enam", text: "مَنْ يُصْرَفْ عَنْهُ يَوْمَئِذٍ فَقَدْ رَحِمَهُۜ وَذٰلِكَ الْفَوْزُ الْمُب۪ينُ", translation: "O Gün kim kurtarılırsa, ona rahmet edilmiştir. İşte apaçık kurtuluş da budur.", transliteration: "Men yusraf anhu yevme izin fe kad rahımeh, ve zalikel fevzul mubin."},
  {id: 806, sure_no: 17, ayet_no: 17, sure_adi: "Enam", text: "وَاِنْ يَمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهُٓ اِلَّا هُوَۜ وَاِنْ يَمْسَسْكَ بِخَيْرٍ فَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Eğer Allah, sana bir sıkıntı verirse onu Kendi'sinden başka giderecek yoktur. Ve eğer sana bir iyilik verirse kuşkusuz Her Şeye Kadir Olan O'dur.", transliteration: "Ve in yemseskellahu bi durrin fe la kaşife lehu illa huve, ve in yemseske bi hayrın fe huve ala kulli şey'in kadir."},
  {id: 807, sure_no: 18, ayet_no: 18, sure_adi: "Enam", text: "وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِه۪ۜ وَهُوَ الْحَك۪يمُ الْخَب۪يرُ", translation: "O, kullarının üzerinde mutlak egemenlik sahibidir. En İyi Hüküm Veren'dir, Her Şeyden Haberdar'dır.", transliteration: "Ve huvel kahiru fevka ıbadih, ve huvel hakimul habir."},
  {id: 808, sure_no: 19, ayet_no: 19, sure_adi: "Enam", text: "قُلْ اَيُّ شَيْءٍ اَكْبَرُ شَهَادَةًۜ  قُلِ اللّٰهُ شَه۪يدٌ بَيْن۪ي وَبَيْنَكُمْ وَاُو۫حِيَ اِلَيَّ هٰذَا الْقُرْاٰنُ لِاُنْذِرَكُمْ بِه۪ وَمَنْ بَلَغَۜ  اَئِنَّكُمْ لَتَشْهَدُونَ اَنَّ مَعَ اللّٰهِ اٰلِهَةً اُخْرٰىۜ قُلْ لَٓا اَشْهَدُۚ قُلْ اِنَّمَا هُوَ اِلٰهٌ وَاحِدٌ وَاِنَّن۪ي بَر۪ٓيءٌ مِمَّا تُشْرِكُونَۢ", translation: "De ki: \"Tanıklık yönünden hangi şey daha güvenilirdir?\" De ki: \"Aramızda tanık Allah'tır. Bu Kur'an; bana, sizi ve ulaştığı herkesi uyarmam için vahyedildi. Yoksa Allah ile beraber başka ilahlar olduğuna tanıklık mı ediyorsunuz?\" De ki: \"Ben tanıklık etmem.\" De ki: \"O ancak tek ilahtır. Kuşkusuz ben, sizin şirk koştuğunuz şeylerden uzağım.\"", transliteration: "Kul eyyu şey'in ekberu şehadeh, kulillahu şehidun, beyni ve beynekum ve uhiye ileyye hazal kur'anu li unzirekum bihi ve men belag, e innekum le teşhedune enne meallahi aliheten uhra, kul la eşhed, kul innema huve ilahun vahidun ve inneni beriun mimma tuşrikun."},
  {id: 809, sure_no: 20, ayet_no: 20, sure_adi: "Enam", text: "اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ اَبْنَٓاءَهُمْۢ اَلَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ۟", translation: "Kendilerine kitap verdiklerimiz, onu çocuklarını tanır gibi tanırlar. O kimseler, kendi nefislerine hüsran ettiler, işte onlar iman etmezler.", transliteration: "Ellezine ateynahumul kitabe ya'rifunehu kema ya'rifune ebnaehum ellezine hasiru enfusehum fe hum la yu'minun."},
  {id: 810, sure_no: 21, ayet_no: 21, sure_adi: "Enam", text: "وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ كَذَّبَ بِاٰيَاتِه۪ۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ", translation: "durduğu yalanı Allah'a dayandıran veya O'nun ayetlerini yalan sayandan daha zalim kim olabilir? Kuşkusuz O, zalimleri kurtuluşa erdirmez.", transliteration: "Ve men azlemu mimmeniftera alallahi keziben ev kezzebe bi ayatih, innehu la yuflihuz zalimun."},
  {id: 811, sure_no: 22, ayet_no: 22, sure_adi: "Enam", text: "وَيَوْمَ نَحْشُرُهُمْ جَم۪يعاً ثُمَّ نَقُولُ لِلَّذ۪ينَ اَشْرَكُٓوا اَيْنَ شُرَكَٓاؤُ۬كُمُ الَّذ۪ينَ كُنْتُمْ تَزْعُمُونَ", translation: "O Gün hepsini toplayacağız. Sonra şirk koşanlara, \"Hani nerede boş yere davasını güttüğünüz ortaklar?\" diye soracağız.", transliteration: "Ve yevme nahşuruhum cemian summe nekulu lillezine eşraku eyne şurekaukumullezine kuntum tez'umun."},
  {id: 812, sure_no: 23, ayet_no: 23, sure_adi: "Enam", text: "ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ اِلَّٓا اَنْ قَالُوا وَاللّٰهِ رَبِّنَا مَا كُنَّا مُشْرِك۪ينَ", translation: "Sonra başvurdukları fitne \"Rabb'imiz Allah'a ant olsun ki, biz Müşriklerden değildik.\" demelerinden başka bir şey olmadı.", transliteration: "Summe lem tekun fitnetuhum illa en kalu vallahi rabbina ma kunna muşrikin."},
  {id: 813, sure_no: 24, ayet_no: 24, sure_adi: "Enam", text: "اُنْظُرْ كَيْفَ كَذَبُوا عَلٰٓى اَنْفُسِهِمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ", translation: "Bak! Kendi aleyhlerine nasıl yalan söylediler. Ve uydurdukları şeyler onlardan ayrılıp yok oldu.", transliteration: "Unzur keyfe kezebu ala enfusihim ve dalle anhum, ma kanu yefterun."},
  {id: 814, sure_no: 25, ayet_no: 25, sure_adi: "Enam", text: "وَمِنْهُمْ مَنْ يَسْتَمِعُ اِلَيْكَۚ وَجَعَلْنَا عَلٰى قُلُوبِهِمْ اَكِنَّةً اَنْ يَفْقَهُوهُ وَف۪ٓي اٰذَانِهِمْ وَقْراًۜ وَاِنْ يَرَوْا كُلَّ اٰيَةٍ لَا يُؤْمِنُوا بِهَاۜ حَتّٰٓى اِذَا جَٓاؤُ۫كَ يُجَادِلُونَكَ يَقُولُ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ", translation: "Onlardan seni dinleyenler vardır; oysa onu anlamalarına engel olmak için kalplerine perde, kulaklarına ağırlık koyduk. Onlar her türlü ayeti görseler de yine de ona inanmazlar. Kafirler sana geldiklerinde, \"Bunlar ancak geçmişin masallarıdır.\" diye seninle tartışırlar.", transliteration: "Ve minhum men yestemiu ileyk, ve cealna ala kulubihim ekinneten en yefkahuhu ve fi azanihim vakra, ve in yerev kulle ayetin la yu'minu biha, hatta iza cauke yucadiluneke yekulullezine keferu in haza illa esatirul evvelin."},
  {id: 815, sure_no: 26, ayet_no: 26, sure_adi: "Enam", text: "وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْـَٔوْنَ عَنْهُۚ وَاِنْ يُهْلِكُونَ اِلَّٓا اَنْفُسَهُمْ وَمَا يَشْعُرُونَ", translation: "Onlar, başkalarını ondan vazgeçirmeye çalışırlar, kendileri de ondan uzaklaşırlar. Ve onlar, ancak kendilerini mahvederler, ama bunu idrak etmezler.", transliteration: "Ve hum yenhevne anhu ve yen'evne anh, ve in yuhlikune illa enfusehumve ma yeş'urun."},
  {id: 816, sure_no: 27, ayet_no: 27, sure_adi: "Enam", text: "وَلَوْ تَرٰٓى اِذْ وُقِفُوا عَلَى النَّارِ فَقَالُوا يَا لَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِاٰيَاتِ رَبِّنَا وَنَكُونَ مِنَ الْمُؤْمِن۪ينَ", translation: "Onların, ateşin karşısında durdurulduklarında: \"Keşke geri döndürülsek de Rabb'imizin ayetlerini yalanlamasak ve Mü'minlerden olsak.\" dediklerini bir görsen.", transliteration: "Ve lev tera iz vukıfu alen nari fe kalu ya leytena nureddu ve la nukezzibe bi ayati rabbina ve nekune minel mu'minin."},
  {id: 817, sure_no: 28, ayet_no: 28, sure_adi: "Enam", text: "بَلْ بَدَا لَهُمْ مَا كَانُوا يُخْفُونَ مِنْ قَبْلُۜ وَلَوْ رُدُّوا لَعَادُوا لِمَا نُهُوا عَنْهُ وَاِنَّهُمْ لَكَاذِبُونَ", translation: "Hayır! Daha önce gizledikleri şeyler açığa çıktı. Eğer onlar, geri döndürülseler yasaklanan kötülüklere tekrar dönerler. Onlar kesinlikle yalancıdırlar.", transliteration: "Bel beda lehum ma kanu yuhfune min kabl,ve lev ruddu le adu li ma nuhu anhuve innehum le kazibun."},
  {id: 818, sure_no: 29, ayet_no: 29, sure_adi: "Enam", text: "وَقَالُٓوا اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوث۪ينَ", translation: "\"Dünya hayatımızdan başka hayat yoktur, öldükten sonra diriltilecek de değiliz.\" dediler.", transliteration: "Ve kalu in hiye illa hayatuned dunya ve ma nahnu bi meb'usin."},
  {id: 819, sure_no: 30, ayet_no: 30, sure_adi: "Enam", text: "وَلَوْ تَرٰٓى اِذْ وُقِفُوا عَلٰى رَبِّهِمْۜ  قَالَ اَلَيْسَ هٰذَا بِالْحَقِّۜ قَالُوا بَلٰى وَرَبِّنَاۜ قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ۟", translation: "Rabb'lerinin huzurunda durdurulduklarında, onları bir görsen! Allah, \"Bu, gerçek değil miymiş?\" dedi. Onlar: \"Rabb'imize ant olsun ki gerçekmiş.\" dediler. \" O halde kafirliğinizden dolayı azabı tadın.\" dedi.", transliteration: "Ve lev tera iz vukıfu ala rabbihim, kale e leyse haza bil hakk, kalu bela ve rabbina, kale fe zukul azabe bima kuntum tekfurun."},
  {id: 820, sure_no: 31, ayet_no: 31, sure_adi: "Enam", text: "قَدْ خَسِرَ الَّذ۪ينَ كَذَّبُوا بِلِقَٓاءِ اللّٰهِۜ حَتّٰٓى اِذَا جَٓاءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوا يَا حَسْرَتَنَا عَلٰى مَا فَرَّطْنَا ف۪يهَاۙ وَهُمْ يَحْمِلُونَ اَوْزَارَهُمْ عَلٰى ظُهُورِهِمْۜ  اَلَا سَٓاءَ مَا يَزِرُونَ", translation: "Allah ile karşılaşmayı yalan sayanlar, gerçekten hüsrana uğramışlardır. Nihayet o Sa'at ansızın gelip çatınca, günahlarını sırtlarına yüklenerek: \"Dünyada iyi şeyleri yapmamaktan dolayı yazıklar olsun bize.\" diyecekler. Dikkat edin yüklendikleri şey ne kötüdür!", transliteration: "Kad hasirellezine kezzebu bi likaillah hatta iza caethumus saatu bagteten kalu ya hasretena ala ma farratna fiha ve hum yahmilune evzarehum ala zuhurihim, e la sae ma yezirun."},
  {id: 821, sure_no: 32, ayet_no: 32, sure_adi: "Enam", text: "وَمَا الْحَيٰوةُ الدُّنْيَٓا اِلَّا لَعِبٌ وَلَهْوٌۜ وَلَلدَّارُ الْاٰخِرَةُ خَيْرٌ لِلَّذ۪ينَ يَتَّقُونَۜ  اَفَلَا تَعْقِلُونَ", translation: "Dünya hayatı, bir oyun ve oyalanmadan başka bir şey değildir. Ahiret yurdu ise, takva ehli olanlar için daha hayırlıdır. Hala aklınızı kullanmayacak mısınız?", transliteration: "Ve mal hayatud dunya illa leibun ve lehv, ve led darul ahiretu hayrun lillezine yettekun, e fe la ta'kılun."},
  {id: 822, sure_no: 33, ayet_no: 33, sure_adi: "Enam", text: "قَدْ نَعْلَمُ اِنَّهُ لَيَحْزُنُكَ الَّذ۪ي يَقُولُونَ فَاِنَّهُمْ لَا يُكَذِّبُونَكَ وَلٰكِنَّ الظَّالِم۪ينَ بِاٰيَاتِ اللّٰهِ يَجْحَدُونَ", translation: "Doğrusu, onların söyledikleri sözlerin seni üzdüğünü biliyoruz. Aslında onlar seni yalanlamıyorlar; o zalimler açıkça Allah'ın ayetlerini reddediyorlar.", transliteration: "Kad na'lemu, innehu le yahzunukellezi yekulune fe innehum la yukezzibuneke ve lakinnez zaliminebi ayatillahi yechadun."},
  {id: 823, sure_no: 34, ayet_no: 34, sure_adi: "Enam", text: "وَلَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ فَصَبَرُوا عَلٰى مَا كُذِّبُوا وَاُو۫ذُوا حَتّٰٓى اَتٰيهُمْ نَصْرُنَاۚ وَلَا مُبَدِّلَ لِكَلِمَاتِ اللّٰهِۚ وَلَقَدْ جَٓاءَكَ مِنْ نَبَا۬ئِ الْمُرْسَل۪ينَ", translation: "Ant olsun, senden önce de Resuller yalanlanmıştı. Yalanlanmalarına ve eziyet edilmelerine karşı Bizim yardımımız ulaşıncaya kadar sabrettiler. Allah'ın kelimelerini değiştirebilecek bir güç yoktur. Nitekim senden önce gönderilmiş elçilerin bir kısım haberleri sana gelmiştir.", transliteration: "Ve lekad kuzzibet rusulun min kablike fe saberu ala ma kuzzibu ve uzu hatta etahum nasruna, ve la mubeddile li kelimatillah, ve lekad caeke min nebeil murselin."},
  {id: 824, sure_no: 35, ayet_no: 35, sure_adi: "Enam", text: "وَاِنْ كَانَ كَبُرَ عَلَيْكَ اِعْرَاضُهُمْ فَاِنِ اسْتَطَعْتَ اَنْ تَبْتَغِيَ نَفَقاً فِي الْاَرْضِ اَوْ سُلَّماً فِي السَّمَٓاءِ فَتَأْتِيَهُمْ بِاٰيَةٍۜ وَلَوْ شَٓاءَ اللّٰهُ لَجَمَعَهُمْ عَلَى الْهُدٰى فَلَا تَكُونَنَّ مِنَ الْجَاهِل۪ينَ", translation: "Eğer yüz çevirmeleri sana ağır geldiyse, yapabilirsen bir tünelle yerden veya bir merdivenle gökten onlara bir ayet getir! Eğer Allah dileseydi, elbette onları doğru yol üzerinde toplardı. O halde sakın cahillerden olma!", transliteration: "Ve in kane kebure aleyke i'raduhum fe inisteta'te en tebtegıye nefekan fil ardı ev sullemen fis semai fe te'tiyehum bi ayeh, ve lev şaallahu le cemeahum alel huda fe la tekunenne minel cahilin."},
  {id: 825, sure_no: 36, ayet_no: 36, sure_adi: "Enam", text: "اِنَّمَا يَسْتَج۪يبُ الَّذ۪ينَ يَسْمَعُونَۜ وَالْمَوْتٰى يَبْعَثُهُمُ اللّٰهُ ثُمَّ اِلَيْهِ يُرْجَعُونَ", translation: "Ancak kulak veren kimseler bu daveti kabul eder. Ölülere gelince onları ancak Allah diriltir. Sonra O'na döndürülürler.", transliteration: "İnnema yestecibullezine yesmeun, vel mevta yeb'asuhumullahu summe ileyhi yurceun."},
  {id: 826, sure_no: 37, ayet_no: 37, sure_adi: "Enam", text: "وَقَالُوا لَوْلَا نُزِّلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۜ قُلْ اِنَّ اللّٰهَ قَادِرٌ عَلٰٓى اَنْ يُنَزِّلَ اٰيَةً وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "Ona, \"Rabb'inden bir ayet indirilmeli değil miydi?\" dediler. De ki: \"Elbette ki Allah, bir ayet indirmeye kadirdir. Ancak onların çoğu bunu bilmezler.\"", transliteration: "Ve kalu lev la nuzzile aleyhi ayetun min rabbih, kul innallahe kadirun ala en yunezzile ayeten ve lakinne ekserehum la ya'lemun."},
  {id: 827, sure_no: 38, ayet_no: 38, sure_adi: "Enam", text: "وَمَا مِنْ دَٓابَّةٍ فِي الْاَرْضِ وَلَا طَٓائِرٍ يَط۪يرُ بِجَنَاحَيْهِ اِلَّٓا اُمَمٌ اَمْثَالُكُمْۜ مَا فَرَّطْنَا فِي الْكِتَابِ مِنْ شَيْءٍ ثُمَّ اِلٰى رَبِّهِمْ يُحْشَرُونَ", translation: "Yeryüzünde hareket eden hiçbir dabbeh ve iki kanadıyla uçan hiçbir kuş yoktur ki, sizin gibi ümmetler olmasın. Biz, Kitap'ta hiçbir şeyi eksik bırakmadık. Sonunda hepsi de Rabb'lerinin huzurunda toplanacaklardır.", transliteration: "Ve ma min dabbetin fil ardı ve la tairin yatiru bi cenahayhi illa umemun emsalukum, ma farratna fil kitabi min şey'in summe ila rabbihim yuhşerun."},
  {id: 828, sure_no: 39, ayet_no: 39, sure_adi: "Enam", text: "وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا صُمٌّ وَبُكْمٌ فِي الظُّلُمَاتِۜ مَنْ يَشَأِ اللّٰهُ يُضْلِلْهُۜ وَمَنْ يَشَأْ يَجْعَلْهُ عَلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Ayetlerimizi yalanlayan kimseler, karanlıklar içinde kalmış, sağırlar ve dilsizlerdir. Allah hak edeni saptırır hak edeni de dosdoğru yola iletir.", transliteration: "Vellezine kezzebu bi ayatina summun ve bukmun fiz zulumat, men yeşaillahu yudlilhu, ve men yeşe' yec'alhu ala sıratın mustakim."},
  {id: 829, sure_no: 40, ayet_no: 40, sure_adi: "Enam", text: "قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰيكُمْ عَذَابُ اللّٰهِ اَوْ اَتَتْكُمُ السَّاعَةُ اَغَيْرَ اللّٰهِ تَدْعُونَۚ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "De ki: \"Eğer doğru söyleyen kimselerseniz söyleyin bakalım. Size Allah'ın azabı gelse veya son sa'at gelse, Allah'tan başkasına mı yalvarırsınız?\"", transliteration: "Kul e reeytekum in etakum azabullahi ev etetkumus saatu e gayrallahi ted'un, in kuntum sadıkin."},
  {id: 830, sure_no: 41, ayet_no: 41, sure_adi: "Enam", text: "بَلْ اِيَّاهُ تَدْعُونَ فَيَكْشِفُ مَا تَدْعُونَ اِلَيْهِ اِنْ شَٓاءَ وَتَنْسَوْنَ مَا تُشْرِكُونَ۟", translation: "\"Hayır! Yalnız O'na yalvarırsınız, O da dilerse yalvarmanıza konu olan sıkıntıyı kaldırır ve siz de ortak koştuklarınızı anmazsınız.\"", transliteration: "Bel iyyahu ted'une fe yekşifu ma ted'une ileyhi in şae ve tensevne ma tuşrikun."},
  {id: 831, sure_no: 42, ayet_no: 42, sure_adi: "Enam", text: "وَلَقَدْ اَرْسَلْـنَٓا اِلٰٓى اُمَمٍ مِنْ قَبْلِكَ فَاَخَذْنَاهُمْ بِالْبَأْسَٓاءِ وَالضَّرَّٓاءِ لَعَلَّهُمْ يَتَضَرَّعُونَ", translation: "Ant olsun ki Biz, senden önceki ümmetlere de gönderdik. Belki tederruda bulunurlar diye onları darlık ve sıkıntı ile yakaladık.", transliteration: "Ve lekad erselna ila umemin min kablike fe ehaznahum bil be'sai ved darrai leallehum yetedarraun."},
  {id: 832, sure_no: 43, ayet_no: 43, sure_adi: "Enam", text: "فَلَوْلَٓا اِذْ جَٓاءَهُمْ بَأْسُنَا تَضَرَّعُوا وَلٰكِنْ قَسَتْ قُلُوبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطَانُ مَا كَانُوا يَعْمَلُونَ", translation: "Hiç olmazsa onlara sıkıntılarımız dokunduğu vakit tederru etselerdi! Fakat kalpleri katılaştı ve şeytan da onlara yaptıklarını çekici gösterdi.", transliteration: "Fe lev la iz caehum be'suna tedarrau ve lakin kaset kulubuhum ve zeyyene lehumuş şeytanu ma kanu ya'melun."},
  {id: 833, sure_no: 44, ayet_no: 44, sure_adi: "Enam", text: "فَلَمَّا نَسُوا مَا ذُكِّرُوا بِه۪ فَتَحْنَا عَلَيْهِمْ اَبْوَابَ كُلِّ شَيْءٍۜ حَتّٰٓى اِذَا فَرِحُوا بِمَٓا اُو۫تُٓوا اَخَذْنَاهُمْ بَغْتَةً فَاِذَا هُمْ مُبْلِسُونَ", translation: "Onlar, verilen öğüdü unutunca, onlara her şeyin kapılarını açtık; kendilerine verilen şeylerle sevince daldıkları sırada onları ansızın yakalayıverdik. O zaman bütün ümitleri boşa çıktı.", transliteration: "Fe lemma nesu ma zukkiru bihi fetahna aleyhim ebvabe kulli şey', hatta iza ferihu bima utu ehaznahum bagteten fe izahum mublisun."},
  {id: 834, sure_no: 45, ayet_no: 45, sure_adi: "Enam", text: "فَقُطِـعَ دَابِرُ الْقَوْمِ الَّذ۪ينَ ظَلَمُواۜ وَالْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَ", translation: "Böylece zulmeden halkın kökü kurudu. Alemlerin Rabb'i olan Allah'a hamdolsun.", transliteration: "Fe kutia dabirul kavmillezine zalemu, vel hamdu lillahi rabbil alemin."},
  {id: 835, sure_no: 46, ayet_no: 46, sure_adi: "Enam", text: "قُلْ اَرَاَيْتُمْ اِنْ اَخَذَ اللّٰهُ سَمْعَكُمْ وَاَبْصَارَكُمْ وَخَتَمَ عَلٰى قُلُوبِكُمْ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْت۪يكُمْ بِهِۜ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيَاتِ ثُمَّ هُمْ يَصْدِفُونَ", translation: "De ki: \"Söyleyin bana! Eğer Allah; işitmenizi, görmenizi ve kalbinizin idrak etmesini yok etse, Allah'tan başka hangi ilah onları size geri verebilir?\" Bak, ayetlerimizi nasıl çok yönlü açıklıyoruz. Buna rağmen yine de yüz çeviriyorlar.", transliteration: "Kul e reeytum in ehazallahu sem'akum ve ebsarekum ve hateme ala kulubikum men ilahun gayrullahi ye'tikum bih, unzur keyfe nusarriful ayati summe hum yasdifun ."},
  {id: 836, sure_no: 47, ayet_no: 47, sure_adi: "Enam", text: "قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰيكُمْ عَذَابُ اللّٰهِ بَغْتَةً اَوْ جَهْرَةً هَلْ يُهْلَكُ اِلَّا الْقَوْمُ الظَّالِمُونَ", translation: "De ki: \"Söyleyin bana! Eğer size Allah'ın azabı ansızın veya göz göre göre gelse, zalimlerden başkası mı yok edilir?\"", transliteration: "Kul e reeytekum in etakum azabullahi bagteten ev cehreten hel yuhleku illel kavmuz zalimun."},
  {id: 837, sure_no: 48, ayet_no: 48, sure_adi: "Enam", text: "وَمَا نُرْسِلُ الْمُرْسَل۪ينَ اِلَّا مُبَشِّر۪ينَ وَمُنْذِر۪ينَۚ فَمَنْ اٰمَنَ وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "Biz elçileri ancak haberdar edici ve uyarıcı olarak gönderdik. Kim iman eder kendisini düzeltirse, onlara korku yoktur, onlar üzülmeyecekler de.", transliteration: "Ve ma nursilul murseline illa mubeşşirine ve munzirin, fe men amene ve asleha fe la havfun aleyhim ve la hum yahzenun."},
  {id: 838, sure_no: 49, ayet_no: 49, sure_adi: "Enam", text: "وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوا يَفْسُقُونَ", translation: "Ayetlerimizi yalanlayanlara da yaptıkları fasıklık yüzünden azabımız dokunacaktır.", transliteration: "Vellezine kezzebu bi ayatina yemessuhumul azabu bima kanu yefsukun."},
  {id: 839, sure_no: 50, ayet_no: 50, sure_adi: "Enam", text: "قُلْ لَٓا اَقُولُ لَكُمْ عِنْد۪ي خَزَٓائِنُ اللّٰهِ وَلَٓا اَعْلَمُ الْغَيْبَ وَلَٓا اَقُولُ لَكُمْ اِنّ۪ي مَلَكٌۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوحٰٓى اِلَيَّۜ قُلْ هَلْ يَسْتَوِي الْاَعْمٰى وَالْبَص۪يرُۜ اَفَلَا تَتَفَكَّرُونَ۟", translation: "De ki: \"Ben size, Allah'ın hazineleri yanımdadır demiyorum. Gaybı da bilmem. Size, ben bir meleğim de demiyorum. Ben, sadece bana vahyolunana uyuyorum.\" De ki: \"Kör ile gören bir olur mu? Niçin düşünmüyorsunuz?\"", transliteration: "Kul la ekulu lekum indi hazainullahi ve la a'lemul gaybe ve la ekulu lekum inni melek, in ettebiu illa ma yuha ileyy, kul hel yestevil a'ma vel basir,e fe la tetefekkerun."},
  {id: 840, sure_no: 51, ayet_no: 51, sure_adi: "Enam", text: "وَاَنْذِرْ بِهِ الَّذ۪ينَ يَخَافُونَ اَنْ يُحْشَرُٓوا اِلٰى رَبِّهِمْ لَيْسَ لَهُمْ مِنْ دُونِه۪ وَلِيٌّ وَلَا شَف۪يعٌ لَعَلَّهُمْ يَتَّقُونَ", translation: "Rabb'lerinin huzurunda hesaba çekilme korkusu olanları, onunla uyar. Kendileri için ondan başka ne bir şefaatçi ne de bir veli yoktur. Umulur ki takva sahibi olurlar.", transliteration: "Ve enzir bihillezine yehafune en yuhşeru ila rabbihimleyse lehum min dunihi veliyyun ve la şefiun leallehum yettekun."},
  {id: 841, sure_no: 52, ayet_no: 52, sure_adi: "Enam", text: "وَلَا تَطْرُدِ الَّذ۪ينَ يَدْعُونَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُر۪يدُونَ وَجْهَهُۜ مَا عَلَيْكَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظَّالِم۪ينَ", translation: "Rabb'lerinin vechini dileyerek sabah akşam O'na yönelenleri uzaklaştırma. Onların hesabından sen, senin hesabından da onlar sorumlu değil. Eğer onları kendinden uzaklaştırırsan zalimlerden olursun!", transliteration: "Ve la tatrudillezine yed'une rabbehum bil gadati vel aşiyyi yuridune vecheh, ma aleyke min hısabihim min şey'in ve ma min hısabike aleyhim min şey'in fe tatrudehum fe tekune minez zalimin."},
  {id: 842, sure_no: 53, ayet_no: 53, sure_adi: "Enam", text: "وَكَذٰلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُٓوا اَهٰٓؤُ۬لَٓاءِ مَنَّ اللّٰهُ عَلَيْهِمْ مِنْ بَيْنِنَاۜ اَلَيْسَ اللّٰهُ بِاَعْلَمَ بِالشَّاكِر۪ينَ", translation: "Biz, böylece onların bir kısmını, bir kısmı ile fitnelendirdik ki: \"Allah'ın, aramızdan lütfuna layık gördüğü kimseler bunlar mıdır?\" desinler diye. Allah, şükredenleri daha iyi bilen değil midir?", transliteration: "Ve kezalike fetenna ba'dahum bi ba'din li yekulu e haulai mennallahu aleyhim min beynina, e leysallahu bi a'leme biş şakirin."},
  {id: 843, sure_no: 54, ayet_no: 54, sure_adi: "Enam", text: "وَاِذَا جَٓاءَكَ الَّذ۪ينَ يُؤْمِنُونَ بِاٰيَاتِنَا فَقُلْ سَلَامٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلٰى نَفْسِهِ الرَّحْمَةَۙ اَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُٓوءاً بِجَهَالَةٍ ثُمَّ تَابَ مِنْ بَعْدِه۪ وَاَصْلَحَ فَاَنَّهُ غَفُورٌ رَح۪يمٌ", translation: "Ayetlerimize iman etmiş kimseler sana geldiklerinde, \"Size selam olsun.\"de. Rabb'iniz rahmet etmeyi Kendi üzerine yazdı. Sizden kim cehaletle kötü bir şey yapar da ardından tevbe edip kendini düzeltirse, bilsin ki O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve iza caekellezine yu'minune bi ayatina fe kul selamun aleykum ketebe rabbukum ala nefsihir rahmete ennehu men amile minkum suen bi cehaletin summe tabe min ba'dihi ve asleha fe ennehu gafurun rahim."},
  {id: 844, sure_no: 55, ayet_no: 55, sure_adi: "Enam", text: "وَكَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ وَلِتَسْتَب۪ينَ سَب۪يلُ الْمُجْرِم۪ينَ۟", translation: "Mücrimlerin yolu besbelli olsun diye ayetleri ayrıntılı biçimde açıklıyoruz.", transliteration: "Ve kezalike nufassılul ayati ve li testebine sebilul mucrimin."},
  {id: 845, sure_no: 56, ayet_no: 56, sure_adi: "Enam", text: "قُلْ اِنّ۪ي نُه۪يتُ اَنْ اَعْبُدَ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِۜ قُلْ لَٓا اَتَّبِـعُ اَهْوَٓاءَكُمْۙ قَدْ ضَلَلْتُ اِذاً وَمَٓا اَنَا۬ مِنَ الْمُهْتَد۪ينَ", translation: "De ki: \"Ben, Allah'ın yanı sıra yalvardıklarınıza kulluk etmekten men olundum.\" De ki: \"Hevanıza tabi olmam, yoksa kesinlikle sapmış olurum da o zaman hidayete erenlerden olmamış olurum.\"", transliteration: "Kul inni nuhitu en a'budellezine ted'une min dunillah, kul la ettebiu ehvaekum kad dalaltu izen ve ma ene minel muhtedin."},
  {id: 846, sure_no: 57, ayet_no: 57, sure_adi: "Enam", text: "قُلْ اِنّ۪ي عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَكَذَّبْتُمْ بِه۪ۜ مَا عِنْد۪ي مَا تَسْتَعْجِلُونَ بِه۪ۜ اِنِ الْحُكْمُ اِلَّا لِلّٰهِۜ يَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفَاصِل۪ينَ", translation: "De ki: \"Ben, Rabb'imden açık bir kanıt üzerindeyim. Siz onu yalanladınız. Acelece istediğiniz şey benim yanımda değil. Hüküm ancak Allah'a aittir. Zira O, hakkı anlatır. Ve O, ayırt edenlerin en hayırlısıdır.\"", transliteration: "Kul inni ala beyyinetin min rabbi, ve kezzebtum bih, ma indi ma testa'cilune bih, inil hukmu illa lillah, yakussul hakka ve huve hayrul fasılin."},
  {id: 847, sure_no: 58, ayet_no: 58, sure_adi: "Enam", text: "قُلْ لَوْ اَنَّ عِنْد۪ي مَا تَسْتَعْجِلُونَ بِه۪ لَقُضِيَ الْاَمْرُ بَيْن۪ي وَبَيْنَكُمْۜ وَاللّٰهُ اَعْلَمُ بِالظَّالِم۪ينَ", translation: "De ki: \"Eğer acelece istediğiniz o şey benim yanımda olsaydı, benimle sizin aranızdaki iş çoktan olup biterdi.\" Allah, zalimleri en iyi bilendir.", transliteration: "Kul lev enne indi ma testa'cilune bihi le kudıyel emru beyni ve beynekum, vallahu a'lemu biz zalimin."},
  {id: 848, sure_no: 59, ayet_no: 59, sure_adi: "Enam", text: "وَعِنْدَهُ مَفَاتِـحُ الْغَيْبِ لَا يَعْلَمُهَٓا اِلَّا هُوَۜ وَيَعْلَمُ مَا فِي الْبَرِّ وَالْبَحْرِۜ وَمَا تَسْقُطُ مِنْ وَرَقَةٍ اِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ ف۪ي ظُلُمَاتِ الْاَرْضِ وَلَا رَطْبٍ وَلَا يَابِسٍ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍ", translation: "Gaybın anahtarı yalnızca O'nun yanındadır. O'ndan başka hiç kimse onları bilemez. Karada ve denizde olan her şeyi bilir. Bir yaprak düşse mutlaka onu bilir. Yerin karanlığında tek bir dane, canlı ve cansız yoktur ki apaçık bir kitapta olmasın.", transliteration: "Ve indehu mefatihul gaybi la ya'lemuha illa huve, ve ya'lemu ma fil berri vel bahr, ve ma teskutu min varakatin illa ya'lemuha ve la habbetin fi zulumatil ardı ve la ratbin ve la yabisin illa fi kitabin mubin."},
  {id: 849, sure_no: 60, ayet_no: 60, sure_adi: "Enam", text: "وَهُوَ الَّذ۪ي يَتَوَفّٰيكُمْ بِالَّيْلِ وَيَعْلَمُ مَا جَرَحْتُمْ بِالنَّهَارِ ثُمَّ يَبْعَثُـكُمْ ف۪يهِ لِيُقْضٰٓى اَجَلٌ مُسَمًّىۚ ثُمَّ اِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ۟", translation: "O'dur geceleyin sizi öldüren, gündüz elde ettiğiniz şeyleri bilen. Sonra, bilinen ecelin gerçekleşmesi için diriltendir. Sonunda O'nadır dönüşünüz. Sonra da her ne yaptıysanız onu size haber verecektir.", transliteration: "Ve huvellezi yeteveffakum bil leyli ve ya'lemu ma cerahtum bin nehari summe yeb'asukum fihi li yukda ecelun musemma, summe ileyhi merci'ukum summe yunebbiukum bima kuntum ta'melun."},
  {id: 850, sure_no: 61, ayet_no: 61, sure_adi: "Enam", text: "وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِه۪ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةًۜ حَتّٰٓى اِذَا جَٓاءَ اَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُونَ", translation: "O'dur kulları üzerinde kahir olan. Üzerinize gözeticiler gönderir. Sonra da sizden birine ölüm vakti geldiği zaman, Resullerimiz hiç zaman geçirmeden onun canını alırlar.", transliteration: "Ve huvel kahiru fevka ibadihi ve yursilu aleykum hafazah, hatta iza cae ehadekumul mevtu teveffethu rusuluna ve hum la yuferritun."},
  {id: 851, sure_no: 62, ayet_no: 62, sure_adi: "Enam", text: "ثُمَّ رُدُّٓوا اِلَى اللّٰهِ مَوْلٰيهُمُ الْحَقِّۜ  اَلَا لَهُ الْحُكْمُ وَهُوَ اَسْرَعُ الْحَاسِب۪ينَ", translation: "Sonra onlar, gerçek mevlaları olan Allah'a döndürülürler. Dikkat edin, hüküm yalnız O'nundur ve O, hesabı çabuk görendir.", transliteration: "Summe ruddu ilallahi mevlahumul hakk, e la lehul hukmu ve huve esraul hasibin."},
  {id: 852, sure_no: 63, ayet_no: 63, sure_adi: "Enam", text: "قُلْ مَنْ يُنَجّ۪يكُمْ مِنْ ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ تَدْعُونَهُ تَضَرُّعاً وَخُفْيَةًۚ  لَئِنْ اَنْجٰينَا مِنْ هٰذِه۪ لَنَكُونَنَّ مِنَ الشَّاكِر۪ينَ", translation: "De ki: \"Yerin ve denizin karanlıklarından sizi kim kurtarır?\" \"Eğer bundan bizi kurtarırsa kuşkusuz şükredenlerden oluruz.\" diye O'na açık ve tedarruan yakarırsınız.", transliteration: "Kul men yuneccikum min zulumatil berri vel bahri ted'unehu tedarruan ve hufyeh, le in encana min hazihi le nekunenne mineş şakirin."},
  {id: 853, sure_no: 64, ayet_no: 64, sure_adi: "Enam", text: "قُلِ اللّٰهُ يُنَجّ۪يكُمْ مِنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ اَنْتُمْ تُشْرِكُونَ", translation: "De ki: \"Sizi ondan ve bütün sıkıntılardan Allah kurtarır da sonra siz yine O'na şirk koşarsınız.\"", transliteration: "Kulillahu yuneccikum minha ve min kulli kerbin summe entum tuşrikun."},
  {id: 854, sure_no: 65, ayet_no: 65, sure_adi: "Enam", text: "قُلْ هُوَ الْقَادِرُ عَلٰٓى اَنْ يَبْعَثَ عَلَيْكُمْ عَذَاباً مِنْ فَوْقِكُمْ اَوْ مِنْ تَحْتِ اَرْجُلِكُمْ اَوْ يَلْبِسَكُمْ شِيَعاً وَيُذ۪يقَ بَعْضَكُمْ بَأْسَ بَعْضٍۜ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيَاتِ لَعَلَّهُمْ يَفْقَهُونَ", translation: "De ki: \"Sizin üstünüzden ve ayaklarınızın altından azap göndermeye, sizi topluluklar halinde ayırıp kiminizin hıncını kiminize tattırmaya kadir olan O'dur.\" Bak, iyice anlasınlar diye ayetlerimizi nasıl her yönüyle açıklıyoruz.", transliteration: "Kul huvel kadiru ala en yeb'ase aleykum azaben min fevkıkum ev min tahti erculikum ev yelbisekum şiyean ve yuzika ba'dakum be'se ba'd, unzur keyfe nusarrıful ayati leallehum yefkahun."},
  {id: 855, sure_no: 66, ayet_no: 66, sure_adi: "Enam", text: "وَكَذَّبَ بِه۪ قَوْمُكَ وَهُوَ الْحَقُّۜ قُلْ لَسْتُ عَلَيْكُمْ بِوَك۪يلٍۜ", translation: "O hakikat olduğu halde halkın onu yalanladı. \"Ben üzerinize vekil değilim.\" de.", transliteration: "Ve kezzebe bihi kavmuke ve huvel hakk,kul lestu aleykum bi vekil."},
  {id: 856, sure_no: 67, ayet_no: 67, sure_adi: "Enam", text: "لِكُلِّ نَبَأٍ مُسْتَقَرٌّۘ وَسَوْفَ تَعْلَمُونَ", translation: "Her haberin kararlaştırılmış bir zamanı vardır. Yakında bileceksiniz.", transliteration: "Likulli nebein mustekar, ve sevfe ta'lemun."},
  {id: 857, sure_no: 68, ayet_no: 68, sure_adi: "Enam", text: "وَاِذَا رَاَيْتَ الَّذ۪ينَ يَخُوضُونَ ف۪ٓي اٰيَاتِنَا فَاَعْرِضْ عَنْهُمْ حَتّٰى يَخُوضُوا ف۪ي حَد۪يثٍ غَيْرِه۪ۜ وَاِمَّا يُنْسِيَنَّكَ الشَّيْطَانُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرٰى مَعَ الْقَوْمِ الظَّالِم۪ينَ", translation: "Ayetlerimiz hakkında dalanlarla karşılaştığın zaman, onlar başka bir konuya geçinceye kadar, onlardan yüz çevir. Eğer şeytan sana unutturursa, hatırlar hatırlamaz zalim halkla beraber oturma.", transliteration: "Ve iza reeytellezine yahudune fi ayatina fe a'rıd anhum hatta yahudu fi hadisin gayrih, ve imma yunsiyennekeş şeytanu fe la tak'ud ba'dez zikra meal kavmiz zalimin."},
  {id: 858, sure_no: 69, ayet_no: 69, sure_adi: "Enam", text: "وَمَا عَلَى الَّذ۪ينَ يَتَّقُونَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَلٰكِنْ ذِكْرٰى لَعَلَّهُمْ يَتَّقُونَ", translation: "Takva sahibi kimselere, onların hesaplarından bir sorumluluk yoktur. Fakat bu bir hatırlatmadır, belki korunurlar.", transliteration: "Ve ma alellezine yettekune min hısabihim min şey'in ve lakin zikra leallehum yettekun."},
  {id: 859, sure_no: 70, ayet_no: 70, sure_adi: "Enam", text: "وَذَرِ الَّذ۪ينَ اتَّخَذُوا د۪ينَهُمْ لَعِباً وَلَهْواً وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَذَكِّرْ بِه۪ٓ اَنْ تُبْسَلَ نَفْسٌ بِمَا كَسَبَتْۗ لَيْسَ لَهَا مِنْ دُونِ اللّٰهِ وَلِيٌّ وَلَا شَف۪يعٌۚ وَاِنْ تَعْدِلْ كُلَّ عَدْلٍ لَا يُؤْخَذْ مِنْهَاۜ اُو۬لٰٓئِكَ الَّذ۪ينَ اُبْسِلُوا بِمَا كَسَبُواۚ لَهُمْ شَرَابٌ مِنْ حَم۪يمٍ وَعَذَابٌ اَل۪يمٌ بِمَا كَانُوا يَكْفُرُونَ۟", translation: "Dinlerini oyun ve eğlence edinen, dünya hayatının aldattığı kimseleri bırak. Hiç kimsenin kazandığı şeyle bir felaket yaşamaması için Kur'an ile uyar. O kimse için Allah'tan başka ne bir veli ne de bir şefaatçi vardır. O, bütün varlığını fidye olarak verse de ondan kabul edilmez. Onlar, kazandıklarından dolayı mahvolan kimselerdir. Onlar için kaynar sudan bir içecek ve can yakıcı bir azap vardır.", transliteration: "Ve zerillezinettehazu dinehum leiben ve lehven ve garrethumul hayatud dunya ve zekkir bihi en tubsele nefsun bima kesebet, leyse leha min dunillahi veliyyun ve la şefi', ve in ta'dil kulle adlin la yu'haz minha, ulaikellezine ubsilu bima kesebu, lehum şarabun min hamimin ve azabun elimun bima kanu yekfurun."},
  {id: 860, sure_no: 71, ayet_no: 71, sure_adi: "Enam", text: "قُلْ اَنَدْعُوا مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلٰٓى اَعْقَابِنَا بَعْدَ اِذْ هَدٰينَا اللّٰهُ كَالَّذِي اسْتَهْوَتْهُ الشَّيَاط۪ينُ فِي الْاَرْضِ حَيْرَانَۖ لَهُٓ اَصْحَابٌ يَدْعُونَهُٓ اِلَى الْهُدَى ائْتِنَاۜ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰىۜ وَاُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعَالَم۪ينَۙ", translation: "De ki: \"Allah'ı bırakıp da bize faydası da zararı da olmayan şeylere mi yalvaralım? Allah, bizi doğru yola ilettikten sonra, ökçelerimiz üzerinde gerisin geri mi dönelim? Arkadaşlarının \"Bize gel\" diye doğru yola çağırdıkları; şeytanların ise ayartıp şaşırttığı kimse gibi mi olalım?\" De ki: \"Doğru yol ancak Allah'ın gösterdiği yoldur. Ve biz Alemlerin Rabb'ine teslim olmakla emrolunduk.\"", transliteration: "Kul e ned'u min dunillahi ma la yenfeuna ve la yadurruna ve nureddu ala a'kabina ba'de iz hedanallahu kellezistehvethuş şeyatinu fil ardı hayrane lehu ashabun yed'unehu ilel hude'tina, kul inne hudallahi huvel huda, ve umirna li nuslime li rabbil alemin."},
  {id: 861, sure_no: 72, ayet_no: 72, sure_adi: "Enam", text: "وَاَنْ اَق۪يمُوا الصَّلٰوةَ وَاتَّقُوهُۜ وَهُوَ الَّـذ۪ٓي اِلَيْهِ تُحْشَرُونَ", translation: "\"Bir de salatı ikame edin ve O'na karşı takvalı olun. O'dur huzuruna varıp toplanacağınız.\"", transliteration: "Ve en ekimus salate vettekuh, ve huvellezi ileyhi tuhşerun."},
  {id: 862, sure_no: 73, ayet_no: 73, sure_adi: "Enam", text: "وَهُوَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ وَيَوْمَ يَقُولُ كُنْ فَيَكُونُۜ قَوْلُهُ الْحَقُّۜ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِي الصُّورِۜ عَالِمُ الْغَيْبِ وَالشَّهَادَةِۜ وَهُوَ الْحَك۪يمُ الْخَب۪يرُ", translation: "Gökleri ve yeri hakikat ile yaratan O'dur. O Gün, \"Ol!\" der o da oluverir. O'nun sözü haktır. Sur'a üfleneceği gün mülk O'nundur. Gaybı da görüneni de bilendir. O, En İyi Hüküm Veren'dir, Her Şeyden Haberdar'dır.", transliteration: "Ve huvellezi halakas semavati vel arda bil hakk, ve yevme yekulu kun fe yekun, kavluhul hakk, ve lehul mulku yevme yunfehu fis sur, alimul gaybi veş şehadeh, ve huvel hakimul habir."},
  {id: 863, sure_no: 74, ayet_no: 74, sure_adi: "Enam", text: "وَاِذْ قَالَ اِبْرٰه۪يمُ لِاَب۪يهِ اٰزَرَ اَتَـتَّخِذُ اَصْنَاماً اٰلِهَةًۚ اِنّ۪ٓي اَرٰيكَ وَقَوْمَكَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Hani İbrahim, babası Azer'e: \"Sen, putları ilahlar mı ediniyorsun? Doğrusu ben seni ve halkını apaçık bir sapkınlık içinde görüyorum.\" demişti.", transliteration: "Ve iz kale ibrahimu li ebihi azere, e tettehizu esnamen aliheh, inni erake ve kavmeke fi dalalin mubin."},
  {id: 864, sure_no: 75, ayet_no: 75, sure_adi: "Enam", text: "وَكَذٰلِكَ نُر۪ٓي اِبْرٰه۪يمَ مَلَكُوتَ السَّمٰوَاتِ وَالْاَرْضِ وَلِيَكُونَ مِنَ الْمُوقِن۪ينَ", translation: "Böylece, göklerin ve yerin melekutunu İbrahim'e gösteriyorduk ki kesin iman edenlerden olsun.", transliteration: "Ve kezalike nuri ibrahime melekutes semavati vel ardı ve li yekune minel mukınin."},
  {id: 865, sure_no: 76, ayet_no: 76, sure_adi: "Enam", text: "فَلَمَّا جَنَّ عَلَيْهِ الَّيْلُ رَاٰ كَوْكَباًۚ قَالَ هٰذَا رَبّ۪يۚ فَلَمَّٓا اَفَلَ قَالَ لَٓا اُحِبُّ الْاٰفِل۪ينَ", translation: "Gece karanlığı basınca, bir yıldız gördü. \"Rabb'im budur.\" dedi. O, batınca da \"Ben batanları sevmem!\" dedi.", transliteration: "Fe lemma cenne aleyhil leylu rea kevkeba, kale haza rabbi, fe lemma efele kale la uhıbbul afilin."},
  {id: 866, sure_no: 77, ayet_no: 77, sure_adi: "Enam", text: "فَلَمَّا رَاَ الْقَمَرَ بَازِغاً قَالَ هٰذَا رَبّ۪يۚ فَلَمَّٓا اَفَلَ قَالَ لَئِنْ لَمْ يَهْدِن۪ي رَبّ۪ي لَاَكُونَنَّ مِنَ الْقَوْمِ الضَّٓالّ۪ينَ", translation: "Sonra Ay'ın ortaya çıkışını görünce, \"Rabb'im budur.\" dedi. Ay kaybolunca, \"Eğer Rabb'im bana doğru yolu göstermemiş olsaydı sapkın olan halktan olurdum!\" dedi.", transliteration: "Fe lemma reel kamere bazigan kale haza rabbi, fe lemma efele kale le in lem yehdini rabbi le ekunenne minel kavmid dallin."},
  {id: 867, sure_no: 78, ayet_no: 78, sure_adi: "Enam", text: "فَلَمَّا رَاَ الشَّمْسَ بَازِغَةً قَالَ هٰذَا رَبّ۪ي هٰذَٓا اَكْبَرُۚ  فَلَمَّٓا اَفَلَتْ قَالَ يَا قَوْمِ اِنّ۪ي بَر۪ٓيءٌ مِمَّا تُشْرِكُونَ", translation: "Sonra Güneş'i doğarken görünce, \"Bu daha büyük, Rabb'im budur.\" dedi. Ama o batınca, \"Ey halkım, ben sizin şirk koştuğunuz şeylerden uzağım!\" dedi.", transliteration: "Fe lemma reeş şemse bazigaten kale haza rabbi,haza ekber, fe lemma efelet kale ya kavmi inni beriun mimma tuşrikun."},
  {id: 868, sure_no: 79, ayet_no: 79, sure_adi: "Enam", text: "اِنّ۪ي وَجَّهْتُ وَجْهِيَ لِلَّذ۪ي فَطَرَ السَّمٰوَاتِ وَالْاَرْضَ حَن۪يفاً وَمَٓا اَنَا۬ مِنَ الْمُشْرِك۪ينَۚ", translation: "\"Ben hanif olarak yüzümü göklerin ve yerin fıtratını belirleyene yönelttim; ben Müşriklerden değilim.\"", transliteration: "İnni veccehtu vechiye lillezi fatares semavati vel arda hanifen ve ma ene minel muşrikin."},
  {id: 869, sure_no: 80, ayet_no: 80, sure_adi: "Enam", text: "وَحَٓاجَّهُ قَوْمُهُۜ قَالَ اَتُحَٓاجُّٓونّ۪ي فِي اللّٰهِ وَقَدْ هَدٰينِۜ وَلَٓا اَخَافُ مَا تُشْرِكُونَ بِه۪ٓ اِلَّٓا اَنْ يَشَٓاءَ رَبّ۪ي شَيْـٔاًۜ وَسِعَ رَبّ۪ي كُلَّ شَيْءٍ عِلْماًۜ اَفَلَا تَتَذَكَّرُونَ", translation: "Halkı onunla tartıştı. \"Bana doğru yolu gösteren Allah hakkında benimle niçin tartışıyorsunuz? Ben, O'na eş koştuğunuz şeylerden korkmam. Ancak Rabb'imin dilediği olur. Rabb'imin İlmi Her Şeyi Kuşatmıştır. Hala düşünmeyecek misiniz?\"", transliteration: "Ve haccehu kavmuh, kale e tuhaccunni fillahi ve kad hedan, ve la ehafu ma tuşrıkune bihi illa en yeşae rabbi şey'a, vesia rabbi kulle şey'in ilma, e fe la tetezekkerun."},
  {id: 870, sure_no: 81, ayet_no: 81, sure_adi: "Enam", text: "وَكَيْفَ اَخَافُ مَٓا اَشْرَكْتُمْ وَلَا تَخَافُونَ اَنَّكُمْ اَشْرَكْتُمْ بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ عَلَيْكُمْ سُلْطَاناًۜ فَاَيُّ الْفَر۪يقَيْنِ اَحَقُّ بِالْاَمْنِۚ اِنْ كُنْتُمْ تَعْلَمُونَۢ", translation: "\"Siz, Allah'ın size, hakkında hiçbir yetki vermediği şeyleri O'na ortak koşmaktan korkmazken, ben nasıl olur da sizin ortak koştuklarınızdan korkarım? Bu iki taraftan hangisi emin olmaya daha layıktır? Keşke bilseydiniz!\"", transliteration: "Ve keyfe ehafu ma eşrektum ve la tehafune ennekum eşrektum billahi ma lem yunezzıl bihi aleykum sultana, fe eyyul ferikayni ehakku bil emn, in kuntum ta'melun."},
  {id: 871, sure_no: 82, ayet_no: 82, sure_adi: "Enam", text: "اَلَّذ۪ينَ اٰمَنُوا وَلَمْ يَلْبِسُٓوا ا۪يمَانَهُمْ بِظُلْمٍ اُو۬لٰٓئِكَ لَهُمُ الْاَمْنُ وَهُمْ مُهْتَدُونَ۟", translation: "İman etmiş ve imanlarına zulmü bulaştırmamış olanlar var ya işte emniyet içinde olanlar da hidayete ermiş olanlar da onlardır.", transliteration: "Ellezine amenu ve lem yelbisu imanehumbi zulmin ulaike lehumul emnu ve hum muhtedun."},
  {id: 872, sure_no: 83, ayet_no: 83, sure_adi: "Enam", text: "وَتِلْكَ حُجَّتُنَٓا اٰتَيْنَاهَٓا اِبْرٰه۪يمَ عَلٰى قَوْمِه۪ۜ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَٓاءُۜ اِنَّ رَبَّكَ حَك۪يمٌ عَل۪يمٌ", translation: "İşte bunlar, halkına karşı İbrahim'e verdiğimiz huccetlerimizdir. Dilediğimiz kimselerin derecelerini yükseltiriz. Kuşkusuz Rabb'in, En İyi Hüküm Veren'dir, Her Şeyi Bilen'dir.", transliteration: "Ve tilke huccetuna ateynaha ibrahime ala kavmih, nerfeu derecatin men neşa', inne rabbeke hakimun alim."},
  {id: 873, sure_no: 84, ayet_no: 84, sure_adi: "Enam", text: "وَوَهَبْنَا لَهُٓ اِسْحٰقَ وَيَعْقُوبَۜ كُلاًّ هَدَيْنَاۚ وَنُوحاً هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِه۪ دَاوُ۫دَ وَسُلَيْمٰنَ وَاَيُّوبَ وَيُوسُفَ وَمُوسٰى وَهٰرُونَۜ وَكَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَۙ", translation: "Biz ona İshak'ı ve Ya'kub'u bağışladık. Her birine de hidayet verdik. Daha önce Nuh'a da hidayet verdik. O'nun soyundan Davud'a, Süleyman'a, Eyyub'a, Yusuf'a, Musa'ya ve Harun'a da. İşte muhsin olanları böylece ödüllendiririz.", transliteration: "Ve vehebna lehu ishaka ve ya'kub, kullen hedeyna ve nuha hedeyna min kablu ve min zurriyyetihi davude ve suleymane ve eyyube ve yusufe ve musa ve harun ve kezalike neczil muhsinin."},
  {id: 874, sure_no: 85, ayet_no: 85, sure_adi: "Enam", text: "وَزَكَرِيَّا وَيَحْيٰى وَع۪يسٰى وَاِلْيَاسَۜ كُلٌّ مِنَ الصَّالِح۪ينَۙ", translation: "Zekeriya'yı, Yahya'yı, İsa'yı ve İlyas da. Hepsi de salihlerdendi.", transliteration: "Ve zekeriyya ve yahya ve isa ve ilyas, kullun mines salihin."},
  {id: 875, sure_no: 86, ayet_no: 86, sure_adi: "Enam", text: "وَاِسْمٰع۪يلَ وَالْيَسَعَ وَيُونُسَ وَلُوطاًۜ وَكُلاًّ فَضَّلْنَا عَلَى الْعَالَم۪ينَۙ", translation: "İsmail'i, Elyesa'yı, Yunus'u ve Lut'u da. Hepsini alemlere öncü kıldık.", transliteration: "Ve ismaile velyesea ve yunuse ve luta, ve kullen faddalna alel alemin."},
  {id: 876, sure_no: 87, ayet_no: 87, sure_adi: "Enam", text: "وَمِنْ اٰبَٓائِهِمْ وَذُرِّيَّاتِهِمْ وَاِخْوَانِهِمْۚ وَاجْتَبَيْنَاهُمْ وَهَدَيْنَاهُمْ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Onların; atalarından, soylarından ve kardeşlerinden bir kısmını da. Onları seçtik ve dosdoğru yola ilettik.", transliteration: "Ve min abaihim ve zurriyyatihim ve ihvanihim, vectebeynahum ve hedeynahum ila sıratın mustekim."},
  {id: 877, sure_no: 88, ayet_no: 88, sure_adi: "Enam", text: "ذٰلِكَ هُدَى اللّٰهِ يَهْد۪ي بِه۪ مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَلَوْ اَشْرَكُوا لَحَبِطَ عَنْهُمْ مَا كَانُوا يَعْمَلُونَ", translation: "İşte bu, Allah'ın hidayetidir. Kullarından dilediğini bununla hidayet eder. Eğer şirk koşsalardı, yaptıkları boşa giderdi.", transliteration: "Zalike hudallahi yehdi bihi men yeşau min ıbadih, ve lev eşreku le habita anhum ma kanu ya'melun."},
  {id: 878, sure_no: 89, ayet_no: 89, sure_adi: "Enam", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَۚ فَاِنْ يَكْفُرْ بِهَا هٰٓؤُ۬لَٓاءِ فَقَدْ وَكَّلْنَا بِهَا قَوْماً لَيْسُوا بِهَا بِكَافِر۪ينَ", translation: "İşte bunlar, kendilerine Kitap, Hüküm ve Nubuvvet verdiğimiz kimselerdir. Eğer onlar, bunları küfrederlerse yerlerine bunları küfretmeyecek bir halkı vekil etmişizdir.", transliteration: "Ulaikellezine ateynahumul kitabe vel hukme ven nubuvveh, fe in yekfur biha haulai fe kad vekkelna biha kavmen leysu biha bi kafirin."},
  {id: 879, sure_no: 90, ayet_no: 90, sure_adi: "Enam", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ هَدَى اللّٰهُ فَبِهُدٰيهُمُ اقْتَدِهْۜ قُلْ لَٓا اَسْـَٔلُكُمْ عَلَيْهِ اَجْراًۜ اِنْ هُوَ اِلَّا ذِكْرٰى لِلْعَالَم۪ينَ۟", translation: "İşte bunlar, Allah'ın hidayet ettikleridir. Onların yoluna uy. De ki: \"Ben, buna karşılık sizden bir ücret istemiyorum. Bu ancak, alemler için bir öğüttür.\"", transliteration: "Ulaikellezine hedallahu, fe bi hudayuhumuktedih, kul la es'elukum aleyhi ecra, in huve illa zikra lil alemin."},
  {id: 880, sure_no: 91, ayet_no: 91, sure_adi: "Enam", text: "وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِه۪ٓ اِذْ قَالُوا مَٓا اَنْزَلَ اللّٰهُ عَلٰى بَشَرٍ مِنْ شَيْءٍۜ قُلْ مَنْ اَنْزَلَ الْكِتَابَ الَّذ۪ي جَٓاءَ بِه۪ مُوسٰى نُوراً وَهُدًى لِلنَّاسِ تَجْعَلُونَهُ قَرَاط۪يسَ تُبْدُونَهَا وَتُخْفُونَ كَث۪يراًۚ وَعُلِّمْتُمْ مَا لَمْ تَعْلَمُٓوا اَنْتُمْ وَلَٓا اٰبَٓاؤُ۬كُمْۜ قُلِ اللّٰهُۙ ثُمَّ ذَرْهُمْ ف۪ي خَوْضِهِمْ يَلْعَبُونَ", translation: "Allah'ı gereği gibi takdir etmediler. \"Allah, beşere hiçbir şey indirmedi.\" dediler. De ki: \"Musa'nın insanlar için bir nur ve hidayet olarak getirdiği; sizin yazılı sayfalar haline getirip bir kısmını açıklayıp ama çoğunu da gizlediğiniz; sizin de atalarınızın da bilmediğiniz şeyler, kendisiyle size öğretilen Kitap'ı kim indirdi?\" Sen, \"Allah\" de. Ve sonra bırak onları, daldıkları bataklıkta oynaya dursunlar!", transliteration: "Ve ma kaderullahe hakka kadrihi iz kalu ma enzelallahuala beşerin min şey, kul men enzelel kitabellezi cae bihi musa nuren ve huden lin nasi tec'alunehu karatise tubduneha ve tuhfune kesira, ve ullimtum ma lem ta'lemu entum ve la abaukum, kulillahu summe zerhum fi havdıhim yel'abun."},
  {id: 881, sure_no: 92, ayet_no: 92, sure_adi: "Enam", text: "وَهٰذَا كِتَابٌ اَنْزَلْنَاهُ مُبَارَكٌ مُصَدِّقُ الَّذ۪ي بَيْنَ يَدَيْهِ وَلِتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَاۜ وَالَّذ۪ينَ يُؤْمِنُونَ بِالْاٰخِرَةِ يُؤْمِنُونَ بِه۪ وَهُمْ عَلٰى صَلَاتِهِمْ يُحَافِظُونَ", translation: "Bu, şehirlerin anası ve çevresindekileri uyarman için sana indirdiğimiz ve kendisinden öncekileri doğrulayan kutlu bir Kitap'tır. Ahiret'e iman edenler, buna da iman ederler. Ve onlar salatlarını korurlar.", transliteration: "Ve haza kitabun enzelnahu mubarekun musaddıkullezi beyne yedeyhi ve li tunzire ummel kura ve men havleha, vellezine yu'minune bil ahireti yu'minune bihi ve hum ala salatihim yuhafizun."},
  {id: 882, sure_no: 93, ayet_no: 93, sure_adi: "Enam", text: "وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ قَالَ اُو۫حِيَ اِلَيَّ وَلَمْ يُوحَ اِلَيْهِ شَيْءٌ وَمَنْ قَالَ سَاُنْزِلُ مِثْلَ مَٓا اَنْزَلَ اللّٰهُۜ وَلَوْ تَرٰٓى اِذِ الظَّالِمُونَ ف۪ي غَمَرَاتِ الْمَوْتِ وَالْمَلٰٓئِكَةُ بَاسِطُٓوا اَيْد۪يهِمْۚ اَخْرِجُٓوا اَنْفُسَكُمْۜ اَلْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنْتُمْ تَقُولُونَ عَلَى اللّٰهِ غَيْرَ الْحَقِّ وَكُنْتُمْ عَنْ اٰيَاتِه۪ تَسْتَكْبِرُونَ", translation: "Allah'a karşı yalan uydurandan veya kendisine hiçbir şey vahyedilmemiş iken, \"Bana da vahyolundu.\" diyenden ya da \"Ben de Allah'ın indirdiği ayetlerin benzerini indireceğim.\" diyenden daha zalim kim olabilir? Melekler, canlarını almak için ellerini uzatıp, \"Canlarınızı verin; Allah'a karşı gerçek olmayanı söylemenizden ve onun ayetlerine karşı büyüklük taslamanızdan dolayı bugün alçaltıcı azabı tadın.\" dediklerinde, can çekişirlerken bu zalimleri bir görsen!", transliteration: "Ve men azlemu mimmeniftera alallahi keziben ev kale uhıye ileyye ve lem yuha ileyhi şey'un ve men kale seunzilu misle ma enzelallah, ve lev tera iziz zalimune fi gameratil mevti vel melaiketu basitu eydihim, ahricu enfusekum, el yevme tuczevne azabel huni bima kuntum tekulune alallahi gayrel hakkı ve kuntum an ayatihi testekbirun."},
  {id: 883, sure_no: 94, ayet_no: 94, sure_adi: "Enam", text: "وَلَقَدْ جِئْتُمُونَا فُرَادٰى كَمَا خَلَقْنَاكُمْ اَوَّلَ مَرَّةٍ وَتَرَكْتُمْ مَا خَوَّلْنَاكُمْ وَرَٓاءَ ظُهُورِكُمْۚ وَمَا نَرٰى مَعَكُمْ شُفَعَٓاءَكُمُ الَّذ۪ينَ زَعَمْتُمْ اَنَّهُمْ ف۪يكُمْ شُرَكٰٓؤُ۬اۜ لَقَدْ تَقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْكُمْ مَا كُنْتُمْ تَزْعُمُونَ۟", translation: "Ant olsun, sizi ilk yarattığımız gibi yine tek başınıza Bize geldiniz. Sizi hayaline daldırdığımız şeyleri arkanızda bıraktınız. Hani! Ortaklarımız sandığınız şefaatçılarınızı yanınızda görmüyoruz. Ant olsun ki, aranızdaki bağlar artık kopmuştur. Umduklarınızın tamamı sizden kaybolup gitmiştir.", transliteration: "Ve lekad ci'timuna furada kema halaknakum evvele merretin ve terektum ma havvelnakum verae zuhurikum, ve ma nera meakum şufeaekumullezine zeamtum ennehum fikum şureka', lekad tekattaa beynekum ve dalle ankum ma kuntum tez'umun."},
  {id: 884, sure_no: 95, ayet_no: 95, sure_adi: "Enam", text: "اِنَّ اللّٰهَ فَالِقُ الْحَبِّ وَالنَّوٰىۜ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّۜ ذٰلِكُمُ اللّٰهُ فَاَنّٰى تُؤْفَكُونَ", translation: "Evet; Allah, tohumu ve çekirdeği yarandır: Ölüden diriyi, diriden de ölüyü çıkarandır. İşte budur Allah! O halde nasıl çevriliyorsunuz?", transliteration: "İnnallahe falikul habbi ven neva, yuhrıcul hayye minel meyyiti ve muhricul meyyiti minel hayy, zalikumullahu fe enna tu'fekun."},
  {id: 885, sure_no: 96, ayet_no: 96, sure_adi: "Enam", text: "فَالِقُ الْاِصْبَاحِۚ وَجَعَلَ الَّيْلَ سَكَناً وَالشَّمْسَ وَالْقَمَرَ حُسْبَاناًۜ ذٰلِكَ تَقْد۪يرُ الْعَز۪يزِ الْعَل۪يمِ", translation: "O karanlığı yarıp sabahı çıkarandır. Geceyi bir sükunet, Güneş'i ve Ay'ı bir hesap ölçüsü kılandır. İşte bunlar, Mutlak Üstün Olan'ın, Her Şeyi Bilen'in takdiridir.", transliteration: "Falikul ısbah, ve cealel leyle sekenen veş şemse vel kamere husbana, zalike takdirul azizil alim."},
  {id: 886, sure_no: 97, ayet_no: 97, sure_adi: "Enam", text: "وَهُوَ الَّذ۪ي جَعَلَ لَكُمُ النُّجُومَ لِتَهْتَدُوا بِهَا ف۪ي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِۜ قَدْ فَصَّلْنَا الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ", translation: "Karanın ve denizin karanlıklarında yönünüzü bulasınız diye yıldızları size kılavuz yapan O'dur. Kuşkusuz akleden bir halk için ayetleri böylece ayrıntılı şekilde açıkladık.", transliteration: "Ve huvellezi ceale lekumun nucume li tehtedu biha fi zulumatil berri vel bahr, kad fassalnal ayati li kavmin ya'lemun."},
  {id: 887, sure_no: 98, ayet_no: 98, sure_adi: "Enam", text: "وَهُوَ الَّـذ۪ٓي اَنْشَاَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ فَمُسْتَقَرٌّ وَمُسْتَوْدَعٌۜ قَدْ فَصَّلْنَا الْاٰيَاتِ لِقَوْمٍ يَفْقَـهُونَ", translation: "Sizi tek bir nefisten inşa eden O'dur. Sizin için bir kalış ve emanet olarak konuluş yeri vardır. Akleden bir halk için ayetlerimizi böylece ayrıntılı bir şekilde açıkladık.", transliteration: "Ve huvellezi enşeekum min nefsin vahıdetin fe mustekarrun ve mustevda', kad fassalnal ayati li kavmin yefkahun."},
  {id: 888, sure_no: 99, ayet_no: 99, sure_adi: "Enam", text: "وَهُوَ الَّـذ۪ٓي اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۚ فَاَخْرَجْنَا بِه۪ نَبَاتَ كُلِّ شَيْءٍ فَاَخْرَجْنَا مِنْهُ خَضِراً نُخْرِجُ مِنْهُ حَباًّ مُتَرَاكِباًۚ وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَجَنَّاتٍ مِنْ اَعْنَابٍ وَالزَّيْتُونَ وَالرُّمَّانَ مُشْتَبِهاً وَغَيْرَ مُتَشَابِهٍۜ اُنْظُـرُٓوا اِلٰى ثَمَرِه۪ٓ اِذَٓا اَثْمَرَ وَيَنْعِه۪ۜ اِنَّ ف۪ي ذٰلِكُمْ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ", translation: "O, gökten su indirendir. Onunla her türlü bitkiyi bitirdik. Ondan her çeşit bitkiyi çıkardık. Onlardan yığın yığın daneler, hurmaların tomurcuklarından sarkan salkımlar, birbirine hem benzeyen hem benzemeyen; üzüm bağları, zeytin ve nar bahçeleri meydana getirdik. Meyve verirken ve meyvesi olgunlaştığında her birine bir bakın! Bunlarda iman eden bir kavim için ayetler vardır.", transliteration: "Ve huvellezi enzele mines semai ma', fe ahrecna bihi nebate kulli şey'in fe ahrecna minhu hadıran nuhricu minhu habben muterakiba, ve minen nahli min tal'ıha kınvanun daniyetun ve cennatin min a'nabin vez zeytune ver rummane muştebihen ve gayre muteşabih, unzuru ila semerihi iza esmere ve yen'ıh, inne fi zalikum le ayatin li kavmin yu'minun."},
  {id: 889, sure_no: 100, ayet_no: 100, sure_adi: "Enam", text: "وَجَعَلُوا لِلّٰهِ شُرَكَٓاءَ الْجِنَّ وَخَلَقَهُمْ وَخَرَقُوا لَهُ بَن۪ينَ وَبَنَاتٍ بِغَيْرِ عِلْمٍۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يَصِفُونَ۟", translation: "Cinnleri Allah'a ortak koştular. Oysa onları da O yaratmıştır. Bir bilgiye dayanmadan O'na oğullar ve kızlar isnat ettiler! O, onların niteledikleri şeylerden uzaktır ve yücedir.", transliteration: "Ve cealu lillahi şurekael cinne ve halakahum ve haraku lehu benine ve benatin bi gayri ilm, subhanehu ve teala amma yasifun."},
  {id: 890, sure_no: 101, ayet_no: 101, sure_adi: "Enam", text: "بَد۪يعُ السَّمٰوَاتِ وَالْاَرْضِۜ اَنّٰى يَكُونُ لَهُ وَلَدٌ وَلَمْ تَكُنْ لَهُ صَاحِبَةٌۜ وَخَلَقَ كُلَّ شَيْءٍۚ وَهُوَ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Gökleri ve yeri yaratan Bedi odur. Eşi benzeri olmayanın nasıl olur da çocuğu olabilir? O, Her Şeyi Yaratan'dır. Ve Her Şeyi Hakkıyla Bilen'dir.", transliteration: "Bedius semavati vel ard, enna yekunu lehu veledun ve lem tekun lehu sahıbeh, ve halaka kulle şey', ve huve bikulli şey'in alim."},
  {id: 891, sure_no: 102, ayet_no: 102, sure_adi: "Enam", text: "ذٰلِكُمُ اللّٰهُ رَبُّكُمْۚ لَٓا اِلٰهَ اِلَّا هُوَۚ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوهُۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ وَك۪يلٌ", translation: "İşte Rabb'iniz Allah budur; O'ndan başka ilah yoktur. O, her şeyi yaratandır. O halde O'na kulluk edin. O, Her Şeye Vekil'dir.", transliteration: "Zalikumullahu rabbukum, la ilahe illa huve, haliku kulli şey'in fa'buduh,ve huve ala kulli şey'in vekil."},
  {id: 892, sure_no: 103, ayet_no: 103, sure_adi: "Enam", text: "لَا تُدْرِكُهُ الْاَبْصَارُۘ وَهُوَ يُدْرِكُ الْاَبْصَارَۚ وَهُوَ اللَّط۪يفُ الْخَب۪يرُ", translation: "Gözler O'nu idrak edemez; O, gözleri idrak eder. O, Bütün Ayrıntıları Bilen'dir, Her Şeyden Haberdar'dır.", transliteration: "La tudrikuhul ebsaru ve huve yudrikul ebsar ve huvel latiful habir."},
  {id: 893, sure_no: 104, ayet_no: 104, sure_adi: "Enam", text: "قَدْ جَٓاءَكُمْ بَصَٓائِرُ مِنْ رَبِّكُمْۚ فَمَنْ اَبْصَرَ فَلِنَفْسِه۪ۚ وَمَنْ عَمِيَ فَعَلَيْهَاۜ وَمَٓا اَنَا۬ عَلَيْكُمْ بِحَف۪يظٍ", translation: "Rabb'inizden size basiret verilmiştir. Kim gerçeği görürse kendi yararınadır, kim de gerçeği görmezse kendi aleyhinedir. \"Ben üzerinize bekçi değilim.\"", transliteration: "Kad caekum basairu min rabbikum fe men ebsara fe li nefsih ve men amiye fe aleyha, ve ma ene aleykum bi hafiz."},
  {id: 894, sure_no: 105, ayet_no: 105, sure_adi: "Enam", text: "وَكَذٰلِكَ نُصَرِّفُ الْاٰيَاتِ وَلِيَقُولُوا دَرَسْتَ وَلِنُبَيِّنَهُ لِقَوْمٍ يَعْلَمُونَ", translation: "İşte böylece Biz, ayetleri döne döne açıklıyoruz. Varsın \"Sen ders almışsın.\" desinler. Oysa bilen bir halk için onu ayrıntılı bir şekilde açıklıyoruz.", transliteration: "Ve kezalike nusarriful ayati ve li yekulu dereste ve li nubeyyinehu li kavmin ya'lemun."},
  {id: 895, sure_no: 106, ayet_no: 106, sure_adi: "Enam", text: "اِتَّبِعْ مَٓا اُو۫حِيَ اِلَيْكَ مِنْ رَبِّكَۚ لَٓا اِلٰهَ اِلَّا هُوَۚ وَاَعْرِضْ عَنِ الْمُشْرِك۪ينَ", translation: "Rabb'inden sana vahyolunana uy, O'ndan başka ilah yoktur. Ortak koşanlara aldırma.", transliteration: "İttebi' ma uhıye ileyke min rabbik, la ilahe illa huve, ve a'rıd anil muşrikin."},
  {id: 896, sure_no: 107, ayet_no: 107, sure_adi: "Enam", text: "وَلَوْ شَٓاءَ اللّٰهُ مَٓا اَشْرَكُواۜ وَمَا جَعَلْنَاكَ عَلَيْهِمْ حَف۪يظاًۚ وَمَٓا اَنْتَ عَلَيْهِمْ بِوَك۪يلٍ", translation: "Eğer Allah dileseydi, onlar Müşrik olmazlardı. Seni onların üzerine koruyucu yapmadık. Ve Sen onların vekili de değilsin.", transliteration: "Ve lev şaallahu ma eşreku, ve ma cealnake aleyhim hafiza, ve ma ente aleyhim bi vekil."},
  {id: 897, sure_no: 108, ayet_no: 108, sure_adi: "Enam", text: "وَلَا تَسُبُّوا الَّذ۪ينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ فَيَسُبُّوا اللّٰهَ عَدْواً بِغَيْرِ عِلْمٍۜ   كَذٰلِكَ زَيَّنَّا لِكُلِّ اُمَّةٍ عَمَلَهُمْ ثُمَّ اِلٰى رَبِّهِمْ مَرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوا يَعْمَلُونَ", translation: "Allah'ın yanı sıra yöneldiklerine hakaret etmeyin ki onlar da hadlerini aşarak cahillikle Allah'a hakaret etmesinler. Her ümmetin yaptıklarını, kendilerine süslü gösterdik. Sonra Rabb'lerine döneceklerdir. O, onlara yaptıklarını haber verecektir.", transliteration: "Ve la tesubbullezine yed'une min dunillahi fe yesubbullahe adven bi gayri ilm, kezalike zeyyenna li kulli ummetin amelehum summe ila rabbihim merciuhum fe yunebbiuhum bima kanu ya'melun."},
  {id: 898, sure_no: 109, ayet_no: 109, sure_adi: "Enam", text: "وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَئِنْ جَٓاءَتْهُمْ اٰيَةٌ لَيُؤْمِنُنَّ بِهَاۜ قُلْ اِنَّمَا الْاٰيَاتُ عِنْدَ اللّٰهِ وَمَا يُشْعِرُكُمْۙ اَنَّـهَٓا اِذَا جَٓاءَتْ لَا يُؤْمِنُونَ", translation: "Eğer kendilerine bir ayet gelirse, ona inanacaklarına dair kesin bir yeminle Allah'a yemin ettiler. De ki: \"Ayetler ancak Allah'ın yanındadır.\" O ayet gelmiş olsa da onların yine de iman etmeyeceklerinin bilincinde değil misiniz?", transliteration: "Ve aksemu billahi cehde eymanihim le in caethum ayetun le yu'minunne bih, kul innemel ayatu indallahi ve ma yuş'irukum enneha iza caet la yu'minun."},
  {id: 899, sure_no: 110, ayet_no: 110, sure_adi: "Enam", text: "وَنُقَلِّبُ اَفْـِٔدَتَهُمْ وَاَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوا بِه۪ٓ اَوَّلَ مَرَّةٍ وَنَذَرُهُمْ ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ۟", translation: "İlkin ona inanmadıkları gibi, onların gönüllerini ve basiretlerini tersyüz ederiz ve onları azgınlıkları içinde bocalar halde bırakırız.", transliteration: "Ve nukallibu ef'idetehum ve ebsarehum kema lem yu'minu bihi evvele merretin ve nezeruhum fi tugyanihim ya'mehun."},
  {id: 900, sure_no: 111, ayet_no: 111, sure_adi: "Enam", text: "وَلَوْ اَنَّـنَا نَزَّلْـنَٓا اِلَيْهِمُ الْمَلٰٓئِكَةَ وَكَلَّمَهُمُ الْمَوْتٰى وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلاً مَا كَانُوا لِيُؤْمِنُٓوا اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُ وَلٰكِنَّ اَكْثَرَهُمْ يَجْهَلُونَ", translation: "Eğer onlara gerçekten melekleri indirmiş olsaydık, ölüler onlarla konuşsaydı, her şeyi karşılarında toplasaydık, Allah dilemedikçe, yine de iman etmezlerdi. Fakat onların çoğu cahillik ederler.", transliteration: "Ve lev ennena nezzelna ileyhimul melaikete ve kellemehumulmevta ve haşerna aleyhim kulle şey'in kubulen ma kanu li yu'minu illa en yeşaallahu ve lakinne ekserehum yechelun."},
  {id: 901, sure_no: 112, ayet_no: 112, sure_adi: "Enam", text: "وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُواًّ شَيَاط۪ينَ الْاِنْسِ وَالْجِنِّ يُوح۪ي بَعْضُهُمْ اِلٰى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوراًۜ وَلَوْ شَٓاءَ رَبُّكَ مَا فَعَلُوهُ فَذَرْهُمْ وَمَا يَفْتَرُونَ", translation: "Böylece her Nebi'ye ins ve cinn şeytanları düşman yaptık. Onların kimileri kimilerine aldatıcı yaldızlı sözler vahyederler. Eğer Rabb'in dileseydi bunu yapamazlardı. Onları iftiralarıyla baş başa bırak.", transliteration: "Ve kezalike cealna li kulli nebiyyin aduvven şeyatinel insi vel cinni, yuhi ba'duhum ila ba'dın zuhrufel kavli gurura, ve lev şae rabbuke ma fealuhu fe zerhum ve ma yefterun."},
  {id: 902, sure_no: 113, ayet_no: 113, sure_adi: "Enam", text: "وَلِتَصْغٰٓى اِلَيْهِ اَفْـِٔدَةُ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوا مَا هُمْ مُقْتَرِفُونَ", translation: "Böylece ahirete iman etmeyenlerin gönülleri ona yönelsin, ondan hoşlansınlar: yaptıkları şeyleri yapmaya devam etsinler.", transliteration: "Ve li tesga ileyhi ef'idetullezine la yu'minune bil ahıreti ve li yerdavhu ve li yakterifu ma hum mukterifun."},
  {id: 903, sure_no: 114, ayet_no: 114, sure_adi: "Enam", text: "اَفَغَيْرَ اللّٰهِ اَبْتَغ۪ي حَكَماً وَهُوَ الَّـذ۪ٓي اَنْزَلَ اِلَيْكُمُ الْكِتَابَ مُفَصَّلاًۜ وَالَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَعْلَمُونَ اَنَّهُ مُنَزَّلٌ مِنْ رَبِّكَ بِالْحَقِّ فَلَا تَكُونَنَّ مِنَ الْمُمْتَر۪ينَ", translation: "\"O, size Kitap'ı ayrıntılı olarak indirmişken, Allah'tan başka bir hakem mi arayayım?\" Kendilerine Kitap verdiklerimiz, bilirler ki bu Rabb'inden Hakk olarak indirilmiştir. O halde, sakın kuşku duyanlardan olma!", transliteration: "E fe gayrallahi ebtegi hakemen ve huvellezi enzele ileykumul kitabe mufassala, vellezine ateynahumul kitabe ya'lemune ennehu munezzelun min rabbike bil hakkı fe la tekunenne minel mumterin."},
  {id: 904, sure_no: 115, ayet_no: 115, sure_adi: "Enam", text: "وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقاً وَعَدْلاًۜ لَا مُبَدِّلَ لِكَلِمَاتِه۪ۚ وَهُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Rabb'inin kelimesi doğruluk ve adaletçe tamdır. O'nun kelimelerini değiştirebilecek yoktur. O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve temmet kelimetu rabbike sıdkan ve adla, la mubeddile li kelimatih, ve huves semiul alim."},
  {id: 905, sure_no: 116, ayet_no: 116, sure_adi: "Enam", text: "وَاِنْ تُطِـعْ اَكْثَرَ مَنْ فِي الْاَرْضِ يُضِلُّوكَ عَنْ سَب۪يلِ اللّٰهِۜ اِنْ يَتَّبِعُونَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُونَ", translation: "Eğer yeryüzündekilerin çoğunluğuna uyarsan, seni Allah'ın yolundan saptırırlar. Onlar ancak zanna uyarlar ve onlar yalnız var sayıyorlar.", transliteration: "Ve in tutı' eksere men fil ardı yudılluke an sebilillah, in yettebiune illez zanne ve in hum illa yahrusun."},
  {id: 906, sure_no: 117, ayet_no: 117, sure_adi: "Enam", text: "اِنَّ رَبَّكَ هُوَ اَعْلَمُ مَنْ يَضِلُّ عَنْ سَب۪يلِه۪ۚ وَهُوَ اَعْلَمُ بِالْمُهْتَد۪ينَ", translation: "Rabb'in, yolundan sapanları en iyi bilendir. Ve O, doğru yolda olanları da en iyi bilendir.", transliteration: "İnne rabbeke huve a'lemu men yadıllu an sebilih, ve huve a'lemu bil muhtedin."},
  {id: 907, sure_no: 118, ayet_no: 118, sure_adi: "Enam", text: "فَكُلُوا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ اِنْ كُنْتُمْ بِاٰيَاتِه۪ مُؤْمِن۪ينَ", translation: "Eğer Allah'ın ayetlerine iman ediyorsanız, üzerine Allah'ın adı anılanlardan yiyin.", transliteration: "Fe kulu mimma zukiresmullahi aleyhi in kuntum bi ayatihi mu'minin."},
  {id: 908, sure_no: 119, ayet_no: 119, sure_adi: "Enam", text: "وَمَا لَكُمْ اَلَّا تَأْكُلُوا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَا حَرَّمَ عَلَيْكُمْ اِلَّا مَا اضْطُرِرْتُمْ اِلَيْهِۜ وَاِنَّ كَث۪يراً لَيُضِلُّونَ بِاَهْوَٓائِهِمْ بِغَيْرِ عِلْمٍۜ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِالْمُعْتَد۪ينَ", translation: "Size ne oluyor ki, üzerine Allah'ın adı anılmış olanlardan yemiyorsunuz? Oysaki O, mecbur yemek zorunda kaldıklarınız dışında, haram kıldığı şeyleri size ayrıntılı olarak açıklamıştır. Doğrusu birçokları bir bilgiye dayanmaksızın hevasına uyarak saptırıyorlar. Kuşkusuz Rabb'in haddi aşanları en iyi bilendir.", transliteration: "Ve ma lekum ella te'kulu mimma zukiresmullahi aleyhi ve kad fassale lekum ma harreme aleykum illa madturirtum ileyh, ve inne kesiren le yudıllune bi ehvaihim bi gayri ilm, inne rabbeke huve a'lemu bil mu'tedin."},
  {id: 909, sure_no: 120, ayet_no: 120, sure_adi: "Enam", text: "وَذَرُوا ظَاهِرَ الْاِثْمِ وَبَاطِنَهُۜ اِنَّ الَّذ۪ينَ يَكْسِبُونَ الْاِثْمَ سَيُجْزَوْنَ بِمَا كَانُوا يَقْتَرِفُونَ", translation: "Günahın açığını da gizlisini de bırakın. Günah işleyenler, işledikleri günahın karşılığını göreceklerdir.", transliteration: "Ve zeru zahirel ismi ve batıneh, innellezine yeksibunel isme seyuczevne bima kanu yakterifun."},
  {id: 910, sure_no: 121, ayet_no: 121, sure_adi: "Enam", text: "وَلَا تَأْكُلُوا مِمَّا لَمْ يُذْكَرِ اسْمُ اللّٰهِ عَلَيْهِ وَاِنَّهُ لَفِسْقٌۜ وَاِنَّ الشَّيَاط۪ينَ لَيُوحُونَ اِلٰٓى اَوْلِيَٓائِهِمْ لِيُجَادِلُوكُمْۚ وَاِنْ اَطَعْتُمُوهُمْ اِنَّكُمْ لَمُشْرِكُونَ۟", translation: "Üzerine Allah'ın adı anılmayanlardan yemeyin. Çünkü o fısktır. Şeytanlar kendi evliyasına sizinle mücadele etmelerini vahyederler, eğer onlara uyarsanız kuşkusuz siz de Müşriklerden olursunuz.", transliteration: "Ve la te'kulu mimma lem yuzkerismullahi aleyhi ve innehu le fısk, ve inneş şeyatine le yuhune ila evliyaihim li yucadilukum ve in eta'tumuhum innekum le muşrikun."},
  {id: 911, sure_no: 122, ayet_no: 122, sure_adi: "Enam", text: "اَوَمَنْ كَانَ مَيْتاً فَاَحْيَيْنَاهُ وَجَعَلْنَا لَهُ نُوراً يَمْش۪ي بِه۪ فِي النَّاسِ كَمَنْ مَثَلُهُ فِي الظُّلُمَاتِ لَيْسَ بِخَارِجٍ مِنْهَاۜ   كَذٰلِكَ زُيِّنَ لِلْكَافِر۪ينَ مَا كَانُوا يَعْمَلُونَ", translation: "Ölü iken dirilttiğimiz; ona, insanlar arasında, onunla yürüyeceği bir nur verdiğimiz bir kimse; karanlıklar içinde kalıp bir türlü çıkamayan kimse gibi olur mu? Kafirlerin yaptıkları, kendilerine sevimli gösterildi.", transliteration: "E ve men kane meyten fe ahyeynahu ve cealna lehu nuren yemşi bihi fin nasi ke men meseluhu fiz zulumati leyse bi haricin minha, kezalike zuyyine lil kafirine ma kanu ya'melun."},
  {id: 912, sure_no: 123, ayet_no: 123, sure_adi: "Enam", text: "وَكَذٰلِكَ جَعَلْنَا ف۪ي كُلِّ قَرْيَةٍ اَكَابِرَ مُجْرِم۪يهَا لِيَمْكُرُوا ف۪يهَاۜ وَمَا يَمْكُرُونَ اِلَّا بِاَنْفُسِهِمْ وَمَا يَشْعُرُونَ", translation: "Böylece her kentin ileri gelenlerini, aldatmalarından ötürü oranın suçluları yaptık. Onlar kendilerinden başkasını aldatmıyorlar ama bunun bilincinde değiller.", transliteration: "Ve kezalike cealna fi kulli karyetin ekabire mucrimiha li yemkuru fiha, ve ma yemkurune illa bi enfusihim ve ma yeş'urun."},
  {id: 913, sure_no: 124, ayet_no: 124, sure_adi: "Enam", text: "وَاِذَا جَٓاءَتْهُمْ اٰيَةٌ قَالُوا لَنْ نُؤْمِنَ حَتّٰى نُؤْتٰى مِثْلَ مَٓا اُو۫تِيَ رُسُلُ اللّٰهِۜ اَللّٰهُ اَعْلَمُ حَيْثُ يَجْعَلُ رِسَالَتَهُۜ سَيُص۪يبُ الَّذ۪ينَ اَجْرَمُوا صَغَارٌ عِنْدَ اللّٰهِ وَعَذَابٌ شَد۪يدٌ بِمَا كَانُوا يَمْكُرُونَ", translation: "Onlara bir ayet geldiği zaman, \"Allah'ın Resul'üne verilenin benzeri bize de verilmedikçe asla inanmayız.\" derler. Allah, Resul'lük görevini kime vereceğini en iyi bilendir. Suç işleyenlere, yaptıkları aldatmalar yüzünden, Allah katında bir aşağılanma ve şiddetli bir azap vardır.", transliteration: "Ve iza caethum ayetun kalu len nu'mine hatta nu'ta misle ma utiye rusulullah, allahu a'lemu haysu yec'alu risaleteh, seyusibullezine ecremu sagarun indallahi ve azabun şedidun bima kanu yemkurun."},
  {id: 914, sure_no: 125, ayet_no: 125, sure_adi: "Enam", text: "فَمَنْ يُرِدِ اللّٰهُ اَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْاِسْلَامِۚ وَمَنْ يُرِدْ اَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقاً حَرَجاً كَاَنَّمَا يَصَّعَّدُ فِي السَّمَٓاءِۜ   كَذٰلِكَ يَجْعَلُ اللّٰهُ الرِّجْسَ عَلَى الَّذ۪ينَ لَا يُؤْمِنُونَ", translation: "Allah, kim hidayeti hak ederse onun kalbini İslam'a açar; kime de sapkınlığı hak ederse göğe çıkmaya çalışıyormuşçasına kalbini daraltır, sıkar. Allah, iman etmeyenleri işte böyle sıkıntı içinde bırakır.", transliteration: "Fe men yuridillahu en yehdiyehu yeşrah sadrehu lil islam, ve men yurid en yudıllehu yec'al sadrehu dayyikan haracen, ke ennema yassa'adu fis semai, kezalike yec'alullahur ricse alallezine la yu'minun."},
  {id: 915, sure_no: 126, ayet_no: 126, sure_adi: "Enam", text: "وَهٰذَا صِرَاطُ رَبِّكَ مُسْتَق۪يماًۜ قَدْ فَصَّلْنَا الْاٰيَاتِ لِقَوْمٍ يَذَّكَّرُونَ", translation: "Rabb'inin dosdoğru yolu işte budur. Gerçekten öğüt alan bir halk için ayetleri ayrıntılı olarak açıkladık.", transliteration: "Ve haza sıratu rabbike mustekim, kad fassalnal ayati li kavmin yezzekkerun."},
  {id: 916, sure_no: 127, ayet_no: 127, sure_adi: "Enam", text: "لَهُمْ دَارُ السَّلَامِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ بِمَا كَانُوا يَعْمَلُونَ", translation: "Onlar için Rabb'leri yanında bir selam yurdu vardır. O, yaptıklarından dolayı onların velisidir.", transliteration: "Lehum darus selami inde rabbihim ve huve veliyyuhum bima kanu ya'melun."},
  {id: 917, sure_no: 128, ayet_no: 128, sure_adi: "Enam", text: "وَيَوْمَ يَحْشُرُهُمْ جَم۪يعاًۚ يَا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِنَ الْاِنْسِۚ وَقَالَ اَوْلِيَٓاؤُ۬هُمْ مِنَ الْاِنْسِ رَبَّـنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَبَلَغْنَٓا اَجَلَنَا الَّـذ۪ٓي اَجَّلْتَ لَنَاۜ قَالَ النَّارُ مَثْوٰيكُمْ خَالِد۪ينَ ف۪يهَٓا اِلَّا مَا شَٓاءَ اللّٰهُۜ اِنَّ رَبَّكَ حَك۪يمٌ عَل۪يمٌ", translation: "Hepsini bir araya topladığı gün: \"Ey cinn topluluğu! İnsten çoğaldınız! Onların insten evliyası da \"Ey Rabb'imiz! Biz, birbirimizden yararlandık. Ve bizim için belirlediğin sürenin sonuna ulaştık.\" derler. \"Ateş, varacağınız yerdir. Allah'ın dilemesi hariç orada devamlı kalacaksınız.\" der. Kuşkusuz Rabb'in En İyi Hüküm Veren'dir, Her Şeyi Bilen'dir.", transliteration: "Ve yevme yahşuruhum cemia, ya ma'şerel cinni kadisteksertum minel ins ve kale evliyauhum minel insi rabbenestemtea ba'duna biba'dın ve belagna ecelenellezi eccelte lena, kalen naru mesvakum halidine fiha illa ma şaallahu, inne rabbeke hakimun alim."},
  {id: 918, sure_no: 129, ayet_no: 129, sure_adi: "Enam", text: "وَكَذٰلِكَ نُوَلّ۪ي بَعْضَ الظَّالِم۪ينَ بَعْضاً بِمَا كَانُوا يَكْسِبُونَ۟", translation: "İşte böylece kazandıklarından dolayı, zalimlerin bir kısmını diğerlerinin peşine takarız.", transliteration: "Ve kezalike nuvelli ba'daz zalimine ba'dan bima kanu yeksibun."},
  {id: 919, sure_no: 130, ayet_no: 130, sure_adi: "Enam", text: "يَا مَعْشَرَ الْجِنِّ وَالْاِنْسِ اَلَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ اٰيَات۪ي وَيُنْذِرُونَكُمْ لِقَٓاءَ يَوْمِكُمْ هٰذَاۜ قَالُوا شَهِدْنَا عَلٰٓى اَنْفُسِنَا وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَشَهِدُوا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوا كَافِر۪ينَ", translation: "\"Ey cinn ve ins topluluğu! İçinizden, size ayetlerimi aktaran ve bugünle karşılaşacağınıza dair sizi uyaran Resuller gelmedi mi?\" \"Kendi aleyhimize tanığız\" derler. Dünya hayatı onları aldattı, kendilerinin Kafirler olduklarına tanıklık ettiler.", transliteration: "Ya ma'şerel cinni vel insi e lem ye'tikum rusulun minkum yakussune aleykum ayati ve yunzirunekum likae yevmikum haza, kalu şehidna ala enfusina ve garrethumul hayatud dunya ve şehidu ala enfusihim ennehum kanu kafirin."},
  {id: 920, sure_no: 131, ayet_no: 131, sure_adi: "Enam", text: "ذٰلِكَ اَنْ لَمْ يَكُنْ رَبُّكَ مُهْلِكَ الْقُرٰى بِظُلْمٍ وَاَهْلُهَا غَافِلُونَ", translation: "Gerçek şu ki: Rabb'in, gerçeklerden habersiz bir beldenin halkını haksız yere asla yok etmez.", transliteration: "Zalike en lem yekun rabbuke muhlikel kura bi zulmin ve ehluha gafilun."},
  {id: 921, sure_no: 132, ayet_no: 132, sure_adi: "Enam", text: "وَلِكُلٍّ دَرَجَاتٌ مِمَّا عَمِلُواۜ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُونَ", translation: "Herkesin yaptığı işe göre derecesi vardır. Rabb'in onların yaptıklarından habersiz değildir.", transliteration: "Ve li kullin derecatun mimma amilu, ve ma rabbukebi gafilin amma ya'melun."},
  {id: 922, sure_no: 133, ayet_no: 133, sure_adi: "Enam", text: "وَرَبُّكَ الْغَنِيُّ ذُوالرَّحْمَةِۜ اِنْ يَشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْ بَعْدِكُمْ مَا يَشَٓاءُ كَمَٓا اَنْشَاَكُمْ مِنْ ذُرِّيَّةِ قَوْمٍ اٰخَر۪ينَۜ", translation: "Rabb'in Hiçbir Şeye Muhtaç Olmayan'dır, Rahmeti Bol Olan'dır. Eğer dilerse sizi yok edip, yerinize dilediğini getirir. Tıpkı sizi başka toplumların soyundan var ettiği gibi.", transliteration: "Ve rabbukel ganiyyu zur rahmeh, in yeşe' yuzhibkum ve yestahlif min ba'dikum ma yeşau kema enşeekum min zurriyyeti kavmin aharin."},
  {id: 923, sure_no: 134, ayet_no: 134, sure_adi: "Enam", text: "اِنَّ مَا تُوعَدُونَ لَاٰتٍۙ وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ", translation: "Size yapılan uyarı kesinlikle yerine gelecektir. Siz bunu önleyemezsiniz.", transliteration: "İnne ma tuadune le atin ve ma entum bi mu'cizin."},
  {id: 924, sure_no: 135, ayet_no: 135, sure_adi: "Enam", text: "قُلْ يَا قَوْمِ اعْمَلُوا عَلٰى مَكَانَتِكُمْ اِنّ۪ي عَامِلٌۚ فَسَوْفَ تَعْلَمُونَۙ مَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ", translation: "De ki: \"Ey halkım! Elinizden geleni yapın. Ben de yapacağım. İleride göreceksiniz! Son yurt kimin olacak?\" Kuşkusuz zalimler kurtuluşa eremezler.", transliteration: "Kul ya kavmi'melu ala ma kanetikum inni amil, fe sevfe ta'lemune men tekunu lehu akıbetud dar, innehu la yuflihuz zalimun."},
  {id: 925, sure_no: 136, ayet_no: 136, sure_adi: "Enam", text: "وَجَعَلُوا لِلّٰهِ مِمَّا ذَرَاَ مِنَ الْحَرْثِ وَالْاَنْعَامِ نَص۪يباً فَقَالُوا هٰذَا لِلّٰهِ بِزَعْمِهِمْ وَهٰذَا لِشُرَكَٓائِنَاۚ فَمَا كَانَ لِشُرَكَٓائِهِمْ فَلَا يَصِلُ اِلَى اللّٰهِۚ وَمَا كَانَ لِلّٰهِ فَهُوَ يَصِلُ اِلٰى شُرَكَٓائِهِمْۜ سَٓاءَ مَا يَحْكُمُونَ", translation: "Allah'ın var ettiği ziraat ürünlerinden ve hayvanlardan, O'na bir pay ayırıp, zanlarınca: \"Bu Allah'a, bu da ortaklarımızadır.\" dediler. Ortakları için ayrılan, Allah'a ulaşmıyor, Allah için ayrılan, ortaklarına ulaşıyor. Ne kötü hüküm veriyorlar.", transliteration: "Ve cealu lillahi mimma zeree minel harsi vel en'ami nasibenfe kalu haza lillahi bi za'mihim ve haza li şurekaina, fe ma kane li şurekaihim fe la yasılu ilallahi ve ma kane lillahi fe huve yasilu ila şurekaihim, sae ma yahkumun."},
  {id: 926, sure_no: 137, ayet_no: 137, sure_adi: "Enam", text: "وَكَذٰلِكَ زَيَّنَ لِكَث۪يرٍ مِنَ الْمُشْرِك۪ينَ قَتْلَ اَوْلَادِهِمْ شُرَكَٓاؤُ۬هُمْ لِيُرْدُوهُمْ وَلِيَلْبِسُوا عَلَيْهِمْ د۪ينَهُمْۜ وَلَوْ شَٓاءَ اللّٰهُ مَا فَعَلُوهُ فَذَرْهُمْ وَمَا يَفْتَرُونَ", translation: "Böylece ortakları, Müşriklerden çoğuna evlatlarını öldürmeyi hoş gösterdiler. Hem onları mahvetmek hem de dinlerini karmakarışık etmek için. Eğer Allah dileseydi bunu yapamazlardı. Öyleyse onları uydurduklarıyla baş başa bırak.", transliteration: "Ve kezalike zeyyene li kesirin minel muşrikine katle evladihim şurekauhum li yurduhum ve li yelbisu aleyhim dinehum, ve lev şaellahu ma fealuhu fe zerhum ve ma yefterun."},
  {id: 927, sure_no: 138, ayet_no: 138, sure_adi: "Enam", text: "وَقَالُوا هٰذِه۪ٓ اَنْعَامٌ وَحَرْثٌ حِجْرٌۘ لَا يَطْعَمُهَٓا اِلَّا مَنْ نَشَٓاءُ بِزَعْمِهِمْ وَاَنْعَامٌ حُرِّمَتْ ظُهُورُهَا وَاَنْعَامٌ لَا يَذْكُرُونَ اسْمَ اللّٰهِ عَلَيْهَا افْتِرَٓاءً عَلَيْهِۜ سَيَجْز۪يهِمْ بِمَا كَانُوا يَفْتَرُونَ", translation: "Kendi zanlarınca: \"Bu davarlar ve ekinler dokunulmazdır. Onları dilediklerimizden başkası yiyemez. Şu davarların da sırtları haram edilmiştir.\" derler. O'na karşı iftira ederek bir kısım davarların da üzerine Allah'ın adını anmazlar. O, iftiraları yüzünden, Allah onları iftiraları ile cezalandıracaktır.", transliteration: "Ve kalu hazihi en'amun ve harsun hicrun la yat'amuha illa men neşau bi za'mihim ve en'amun hurrimet zuhuruha ve en'amun la yezkurunesmallahi aleyhaftiraen aleyh se yeczihim bima kanu yefterun."},
  {id: 928, sure_no: 139, ayet_no: 139, sure_adi: "Enam", text: "وَقَالُوا مَا ف۪ي بُطُونِ هٰذِهِ الْاَنْعَامِ خَالِصَةٌ لِذُكُورِنَا وَمُحَرَّمٌ عَلٰٓى اَزْوَاجِنَاۚ وَاِنْ يَكُنْ مَيْتَةً فَهُمْ ف۪يهِ شُرَكَٓاءُۜ سَيَجْز۪يهِمْ وَصْفَهُمْۜ اِنَّهُ حَك۪يمٌ عَل۪يمٌ", translation: "\"Bu davarların karınlarında bulunanlar yalnızca erkeklerimize ait olup, kadınlarımıza haram kılınmıştır. Eğer ölü doğarsa o zaman herkes onda ortaktır.\" dediler. Bu nitelendirmelerinin cezasını görecekler. O, En İyi Hüküm Veren'dir, Her Şeyi Bilen'dir.", transliteration: "Ve kalu ma fi butuni hazihil en'ami halisatun li zukurina ve muharremun ala ezvacina, ve in yekun meyteten fe hum fihi şurekau, se yeczihim vasfehum, innehu hakimun alim."},
  {id: 929, sure_no: 140, ayet_no: 140, sure_adi: "Enam", text: "قَدْ خَسِرَ الَّذ۪ينَ قَتَلُٓوا اَوْلَادَهُمْ سَفَهاً بِغَيْرِ  عِلْمٍ  وَحَرَّمُوا مَا رَزَقَهُمُ اللّٰهُ افْتِرَٓاءً عَلَى اللّٰهِۜ قَدْ ضَلُّوا وَمَا كَانُوا مُهْتَد۪ينَ۟", translation: "Bir bilgiye dayanmadan, akılsızca çocuklarını öldürenler ve Allah'ın rızık olarak verdiklerini, Allah adına iftira ederek haram sayanlar, gerçekten hüsrana uğramışlardır. Gerçekten onlar sapkınlaşmışlar ve doğru yolu bulamamışlardır.", transliteration: "Kad hasirellezine katelu evladehum sefehan bi gayri ilmin ve harremu ma rezekahumullahuftiraen alallah, kad dallu ve ma kanu muhtedin."},
  {id: 930, sure_no: 141, ayet_no: 141, sure_adi: "Enam", text: "وَهُوَ الَّـذ۪ٓي اَنْشَاَ جَنَّاتٍ مَعْرُوشَاتٍ وَغَيْرَ مَعْرُوشَاتٍ وَالنَّخْلَ وَالزَّرْعَ مُخْتَلِفاً اُكُلُهُ وَالزَّيْتُونَ وَالرُّمَّانَ مُتَشَابِهاً وَغَيْرَ مُتَشَابِهٍۜ  كُلُوا مِنْ ثَمَرِه۪ٓ اِذَٓا اَثْمَرَ وَاٰتُوا حَقَّهُ يَوْمَ حَصَادِه۪ۘ وَلَا تُسْرِفُواۜ اِنَّهُ لَا يُحِبُّ الْمُسْرِف۪ينَۙ", translation: "Asmalı ve asmasız bahçeleri, çeşit çeşit hurmaları; zirai ürünleri, birbirine benzeyen ve benzemeyen zeytin ve narları yetiştiren O'dur. Her biri meyve verdiği zaman, meyvesinden yiyin. Hasat zamanı da onun hakkını verin. İsraf etmeyin, kuşkusuz O, israf edenleri sevmez.", transliteration: "Ve huvellezi enşee cennatin ma'ruşatin ve gayre ma'ruşatin ven nahle vez zer'a muhtelifen ukuluhu vez zeytune ver rummane muteşabihen ve gayre muteşabih, kulu min semerihi iza esmere ve atu hakkahu yevme hasadihi ve la tusrifu, innehu la yuhibbul musrifin."},
  {id: 931, sure_no: 142, ayet_no: 142, sure_adi: "Enam", text: "وَمِنَ الْاَنْعَامِ حَمُولَةً وَفَرْشاًۜ كُلُوا مِمَّا رَزَقَكُمُ اللّٰهُ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌۙ", translation: "Ve hayvanlardan yük taşıyanlar ve serilecek olanlar/döşek yapılanlar var. Allah'ın rızık olarak verdiklerinden yiyin. Şeytanın adımlarını izlemeyin. O sizin için apaçık bir düşmandır.", transliteration: "Ve minel en'ami hamuleten ve ferşa, kulu mimma rezekakumullahu ve la tettebiu hutuvatiş şeytan,innehu lekum aduvvun mubin."},
  {id: 932, sure_no: 143, ayet_no: 143, sure_adi: "Enam", text: "ثَمَانِيَةَ اَزْوَاجٍۚ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِۜ قُلْ آٰلذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۜ نَبِّؤُ۫ن۪ي بِعِلْمٍ اِنْ كُنْتُمْ صَادِق۪ينَۙ", translation: "Sekiz eş; koyundan iki, keçiden iki. De ki: \"İki erkeği mi, yoksa iki dişiyi mi veya iki dişinin rahminde bulunanı mı haram etti? Eğer doğru söyleyenler iseniz bana bir bilgiye dayanarak haber verin.\"", transliteration: "Semaniyete ezvac, minad da'nisneyni ve minel ma'zisneyn, kul az zekereyni harreme emil unseyeyni emmeştemelet aleyhi erhamul unseyeyn, nebbiuni bi ilmin in kuntum sadıkin."},
  {id: 933, sure_no: 144, ayet_no: 144, sure_adi: "Enam", text: "وَمِنَ الْاِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِۜ قُلْ آٰلذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۜ اَمْ كُنْتُمْ شُهَدَٓاءَ اِذْ وَصّٰيكُمُ اللّٰهُ بِهٰذَاۚ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً لِيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ۟", translation: "Deveden iki, sığırdan iki. De ki: \"İki erkeği mi, yoksa iki dişiyi mi veya iki dişinin rahminde bulunanı mı haram etti? Yoksa Allah, size bunları tavsiye ettiği zaman siz orada mıydınız? Bir bilgiye dayanmadan, insanları saptırmak için Allah adına yalan uydurandan daha zalim kim olabilir? Allah, zalim olan bir halkı doğru yola iletmez.\"", transliteration: "Ve minel ibilisneyni ve minel bakarisneyn, kul az zekereyni harreme emil unseyeyni emmeştemelet aleyhi erhamul unseyeyn, em kuntum şuhedae iz vassakumullahu bi haza, fe men azlemu mimmeniftera alallahi keziben li yudillen nase bi gayri ilm, innallahe la yehdil kavmez zalimin."},
  {id: 934, sure_no: 145, ayet_no: 145, sure_adi: "Enam", text: "قُلْ لَٓا اَجِدُ ف۪ي مَٓا اُو۫حِيَ اِلَيَّ مُحَرَّماً عَلٰى طَاعِمٍ يَطْعَمُهُٓ اِلَّٓا اَنْ يَكُونَ مَيْتَةً اَوْ دَماً مَسْفُوحاً اَوْ لَحْمَ خِنْز۪يرٍ فَاِنَّهُ رِجْسٌ اَوْ فِسْقاً اُهِلَّ لِغَيْرِ اللّٰهِ بِه۪ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَاِنَّ رَبَّكَ غَفُورٌ رَح۪يمٌ", translation: "De ki: \"Bana vahyolunanda; leş, akıtılmış kan, pis olan domuz eti veya bir sapkınlık olarak Allah'tan başkası adına kesilmiş olanlar hariç, yiyecek kimse için haram kılınmış bir şey bulamıyorum. Kim mecbur kalırsa haddi aşmadan, zaruri ihtiyacı kadar bunlardan yiyebilir.\" Kuşkusuz Rabb'in Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Kul la ecidu fi ma uhiye ileyye muharremen ala taimin yat'amuhu illa en yekune meyteten ev demen mesfuhan ev lahme hinzirin fe innehu ricsun ev fıskan uhille li gayrillahi bih, fe menidturra gayre bagın ve la adin fe inne rabbeke gafurun rahim."},
  {id: 935, sure_no: 146, ayet_no: 146, sure_adi: "Enam", text: "وَعَلَى الَّذ۪ينَ هَادُوا حَرَّمْنَا كُلَّ ذ۪ي ظُفُرٍۚ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُومَهُمَٓا اِلَّا مَا حَمَلَتْ ظُهُورُهُمَٓا اَوِ الْحَوَايَٓا اَوْ مَا اخْتَلَطَ بِعَظْمٍۜ ذٰلِكَ جَزَيْنَاهُمْ بِبَغْيِهِمْۘ وَاِنَّا لَصَادِقُونَ", translation: "Yahudilere bütün tırnaklı hayvanları haram ettik; onlara, sığır ve koyunun iç yağlarını da haram ettik. Sırtlarında taşıdıkları, bağırsaklarına ve kemiklerine karışan yağlar hariç. Bu, azgınlıkları nedeniyle onlara verdiğimiz cezadır. Kuşkusuz Biz, doğru olanlarız.", transliteration: "Ve alellezine hadu harremna kulle zi zufur, ve minel bakari vel ganemi harremna aleyhim şuhumehuma illa ma hamelet zuhuruhuma evil havaya ev mahteleta bi azm, zalike cezeynahum bi bagyihim ve inna le sadikun."},
  {id: 936, sure_no: 147, ayet_no: 147, sure_adi: "Enam", text: "فَاِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُورَحْمَةٍ وَاسِعَةٍۚ وَلَا يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِم۪ينَ", translation: "Eğer seni yalanlarlarsa, de ki: \"Rabb'iniz geniş rahmet sahibidir, ne var ki O'nun azabı mücrim halktan geri çevrilemez.\"", transliteration: "Fe in kezzebuke fe kul rabbukum zu rahmetin vasi'ah, ve la yureddu be'suhu anil kavmil mucrimin."},
  {id: 937, sure_no: 148, ayet_no: 148, sure_adi: "Enam", text: "سَيَقُولُ الَّذ۪ينَ اَشْرَكُوا لَوْ شَٓاءَ اللّٰهُ مَٓا اَشْرَكْنَا وَلَٓا اٰبَٓاؤُ۬نَا وَلَا حَرَّمْنَا مِنْ شَيْءٍۜ كَذٰلِكَ كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْ حَتّٰى ذَاقُوا بَأْسَنَاۜ قُلْ هَلْ عِنْدَكُمْ مِنْ عِلْمٍ فَتُخْرِجُوهُ لَنَاۜ اِنْ تَتَّبِعُونَ اِلَّا الظَّنَّ وَاِنْ اَنْتُمْ اِلَّا تَخْرُصُونَ", translation: "Müşrik olanlar diyecekler ki: \"Eğer Allah dileseydi biz de Müşrik olmazdık, babalarımız da. Ve hiçbir şeyi de haram kılmazdık.\" Onlardan öncekiler de aynı şekilde yalanladılar da sonunda azabımızı tattılar. De ki: \"Yanınızda bir bilgi varsa onu bize gösterin. Siz, zandan başka bir şeye uymuyorsunuz ve ancak yalan yanlış atıp tutuyorsunuz.\"", transliteration: "Seyekulullezine eşreku lev şaallahu ma eşrekna ve la abauna ve la harremna min şey', kezalike kezzebellezine min kablihim hatta zaku be'sena, kul hel indekum min ilmin fe tuhricuhu lena, in tettebiune illez zanne ve in entumilla tahrusun."},
  {id: 938, sure_no: 149, ayet_no: 149, sure_adi: "Enam", text: "قُلْ فَلِلّٰهِ الْحُجَّةُ الْبَالِغَةُۚ فَلَوْ شَٓاءَ لَهَدٰيكُمْ اَجْمَع۪ينَ", translation: "De ki: \"Üstün hüccet Allah'ındır. Eğer Allah dileseydi elbette hepinizi doğru yola iletirdi.\"", transliteration: "Kul fe lillahil huccetul baligah, fe lev şae le hedakum ecmain."},
  {id: 939, sure_no: 150, ayet_no: 150, sure_adi: "Enam", text: "قُلْ هَلُمَّ شُهَدَٓاءَكُمُ الَّذ۪ينَ يَشْهَدُونَ اَنَّ اللّٰهَ حَرَّمَ هٰذَاۚ فَاِنْ شَهِدُوا فَلَا تَشْهَدْ مَعَهُمْۚ وَلَا تَتَّبِعْ اَهْوَٓاءَ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَالَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُونَ۟", translation: "De ki: \"Allah'ın bunu haram ettiğine dair tanıklık edecek tanıklarınızı getirin.\" Eğer onlar tanıklık ederlerse sen onlarla beraber tanıklık etme. Ayetlerimizi yalanlayanların ve ahirete inanmayanların tutku ve kuruntularına uyma. Onlar, Rabb'lerine eş koşuyorlar.", transliteration: "Kul helumme şuhedaekumullezine yeşhedune ennallahe harreme haza, fe in şehidu fe la teşhed meahum, ve la tettebi' ehvaellezine kezzebu bi ayatina vellezine la yu'minune bil ahireti ve hum bi rabbihim ya'dilun."},
  {id: 940, sure_no: 151, ayet_no: 151, sure_adi: "Enam", text: "قُلْ تَعَالَوْا اَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ اَلَّا تُشْرِكُوا بِه۪ شَيْـٔاًۜ وَبِالْوَالِدَيْنِ اِحْسَاناًۚ وَلَا تَقْتُلُٓوا اَوْلَادَكُمْ مِنْ اِمْلَاقٍۜ نَحْنُ نَرْزُقُكُمْ وَاِيَّاهُمْۚ وَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَۚ وَلَا تَقْتُلُوا النَّفْسَ الَّت۪ي حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۜ ذٰلِكُمْ وَصّٰيكُمْ بِه۪ لَعَلَّكُمْ تَعْقِلُونَ", translation: "De ki: \"Gelin, Rabb'inizin size haram kıldığı şeyleri bildireyim: O'na şirk koşmayın. Anaya babaya iyilik edin. Yoksulluk endişesi ile çocuklarınızı öldürmeyin.\" Sizin de onların da rızıklarını veren Allah'tır. \"Fuhşiyatın açığına da gizlisine de yaklaşmayın. Haklı bir gerekçe olmadıkça Allah'ın dokunulmaz kıldığı cana kıymayın. İşte O, size bunları öğütledi, umulur ki aklınızı kullanırsınız.\"", transliteration: "Kul tealev etlu ma harreme rabbukum aleykum ella tuşriku bihi şey'a, ve bil valideyni ihsana, ve la taktulu evladekum min imlak, nahnu nerzukukum ve iyyahum, ve la takrebul fevahışe ma zahere minha ve ma batan, ve la taktulun nefselleti harremallahu illa bil hakk, zalikum vassakum bihi leallekum ta'kılun."},
  {id: 941, sure_no: 152, ayet_no: 152, sure_adi: "Enam", text: "وَلَا تَقْرَبُوا مَالَ الْيَت۪يمِ اِلَّا بِالَّت۪ي هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهُۚ وَاَوْفُوا الْكَيْلَ وَالْم۪يزَانَ بِالْقِسْطِۚ لَا نُكَلِّفُ نَفْساً اِلَّا وُسْعَهَا وَاِذَا قُلْتُمْ فَاعْدِلُوا وَلَوْ كَانَ ذَا قُرْبٰىۚ وَبِعَهْدِ اللّٰهِ اَوْفُواۜ ذٰلِكُمْ وَصّٰيكُمْ بِه۪ لَعَلَّكُمْ تَذَكَّرُونَۙ", translation: "Olgunluk çağına erişinceye kadar, iyiliği için olmadıkça yetimin malına dokunmayın. Ölçü ve tartıyı hakkaniyetle yapın. Biz, gücünün yettiğinden fazlasını kişiye teklif etmeyiz. Yakın akrabanız da olsa konuştuğunuz zaman adaleti gözetin. Allah'a verdiğiniz sözü tutun. O, size bunları böylece öğütte bulundu, umulur ki öğüt alırsınız.", transliteration: "Ve la takrebu malel yetimi illa billeti hiye ahsenu hatta yebluga eşuddeh, ve evful keyle vel mizane bil kıst, la nukellifu nefsen illa vus'aha ve iza kultum fa'dilu ve lev kane za kurba, ve bi ahdillahi evfu, zalikum vassakum bihi leallekum tezekkerun."},
  {id: 942, sure_no: 153, ayet_no: 153, sure_adi: "Enam", text: "وَاَنَّ هٰذَا صِرَاط۪ي مُسْتَق۪يماً فَاتَّبِعُوهُۚ وَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَب۪يلِه۪ۜ ذٰلِكُمْ وَصّٰيكُمْ بِه۪ لَعَلَّكُمْ تَتَّقُونَ", translation: "Gerçekten bu, Ben'im dosdoğru yolumdur. Ona uyun. Başka yollara uymayın. Zira o yollar sizi, O'nun yolundan uzaklaştırır. O, size bunları öğütledi, umulur ki takvalı olursunuz.", transliteration: "Ve enne haza sırati mustekimen fettebiuh, ve la tettebius subule fe teferreka bikum an sebilih, zalikum vassakum bihi leallekum tettekun."},
  {id: 943, sure_no: 154, ayet_no: 154, sure_adi: "Enam", text: "ثُمَّ اٰتَيْنَا مُوسَى الْكِتَابَ تَمَاماً عَلَى الَّـذ۪ٓي اَحْسَنَ وَتَفْص۪يلاً لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ بِلِقَٓاءِ رَبِّهِمْ يُؤْمِنُونَ۟", translation: "Sonra Musa'ya, iyi olanlara tamamlayıcı olarak her şeyi açıklayan, rahmet olan ve doğru yolu gösteren Kitap'ı verdik. Umulur ki Rabb'lerine kavuşacaklarına inanırlar.", transliteration: "Summe ateyna musel kitabe tamamen alellezi ahsene ve tafsilen li kulli şey'in ve huden ve rahmeten leallehum bi likai rabbihim yu'minun."},
  {id: 944, sure_no: 155, ayet_no: 155, sure_adi: "Enam", text: "وَهٰذَا كِتَابٌ اَنْزَلْنَاهُ مُبَارَكٌ فَاتَّبِعُوهُ وَاتَّقُوا لَعَلَّكُمْ تُرْحَمُونَۙ", translation: "İşte bu, indirdiğimiz kutsal bir Kitap'tır. Ona uyun. Takvalı olun. Umulur ki bağışlanırsınız.", transliteration: "Ve haza kitabun enzelnahu mubarekun fettebiuhu vetteku leallekum turhamun."},
  {id: 945, sure_no: 156, ayet_no: 156, sure_adi: "Enam", text: "اَنْ تَقُولُٓوا اِنَّـمَٓا اُنْزِلَ الْكِتَابُ عَلٰى طَٓائِفَتَيْنِ مِنْ قَبْلِنَاۖ وَاِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغَافِل۪ينَۙ", translation: "\"Kitap, bizden önce yalnızca iki topluluğa indirildi, biz onların edindikleri bilgiden habersizdik.\" dememeniz için;", transliteration: "En tekulu innema unzilel kitabu ala taifeteyni min kablina ve in kunna an dirasetihim le gafilin."},
  {id: 946, sure_no: 157, ayet_no: 157, sure_adi: "Enam", text: "اَوْ تَقُولُوا لَوْ اَنَّٓا اُنْزِلَ عَلَيْنَا الْكِتَابُ لَكُنَّٓا اَهْدٰى مِنْهُمْۚ فَقَدْ جَٓاءَكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌۚ فَمَنْ اَظْلَمُ مِمَّنْ كَذَّبَ بِاٰيَاتِ اللّٰهِ وَصَدَفَ عَنْهَاۜ سَنَجْزِي الَّذ۪ينَ يَصْدِفُونَ عَنْ اٰيَاتِنَا سُٓوءَ الْعَذَابِ بِمَا كَانُوا يَصْدِفُونَ", translation: "Veya \"Eğer bize Kitap indirilseydi, biz onlardan daha iyi doğru yolda olurduk.\" dememeniz için, Rabb'inizden size apaçık bir beyyinat, bir hidayet ve bir rahmet geldi. Allah'ın ayetlerini yalanlayıp, ondan yüz çevirenden daha zalim kim olabilir? Ayetlerimizden yüz çevirenleri, yüz çevirmelerinden dolayı azabın en kötüsü ile cezalandıracağız.", transliteration: "Ev tekulu lev enna unzile aleynel kitabu le kunna ehda minhum, fe kad caekum beyyinetun min rabbikum ve huden ve rahmeh, fe men azlemu mimmen kezzebe bi ayatillahi ve sadefe anha, se neczillezine yasdifune an ayatina suel azabi bima kanu yasdifun."},
  {id: 947, sure_no: 158, ayet_no: 158, sure_adi: "Enam", text: "هَلْ يَنْظُرُونَ اِلَّٓا اَنْ تَأْتِيَهُمُ الْمَلٰٓئِكَةُ اَوْ يَأْتِيَ رَبُّكَ اَوْ يَأْتِيَ بَعْضُ اٰيَاتِ رَبِّكَۜ يَوْمَ يَأْت۪ي بَعْضُ اٰيَاتِ رَبِّكَ لَا يَنْفَعُ نَفْساً ا۪يمَانُهَا لَمْ تَكُنْ اٰمَنَتْ مِنْ قَبْلُ اَوْ كَسَبَتْ ف۪ٓي ا۪يمَانِهَا خَيْراًۜ قُلِ انْتَظِرُٓوا اِنَّا مُنْتَظِرُونَ", translation: "Onlar, kendilerine; meleklerin gelmesini, Rabb'inin gelmesini veya Rabb'inin bazı ayetlerinin gelmesini mi bekliyorlar? Rabb'inin ayetlerinden biri geldiği gün, daha önce iman etmemiş veya imanında bir hayır kazanmamış kimseye, imanı fayda sağlamaz. De ki: \"Bekleyin, kuşkusuz biz de beklemekteyiz.\"", transliteration: "Hel yanzurune illa en te'tiyehumul melaiketu ev ye'tiye rabbuke ev ye'tiye ba'du ayati rabbik, yevme ye'ti ba'du ayati rabbike la yenfeu nefsen imanuha lem tekun amenet min kablu ev kesebet fi imaniha hayra, kul intezıru inna muntezırun."},
  {id: 948, sure_no: 159, ayet_no: 159, sure_adi: "Enam", text: "اِنَّ الَّذ۪ينَ فَرَّقُوا د۪ينَهُمْ وَكَانُوا شِيَعاً لَسْتَ مِنْهُمْ ف۪ي شَيْءٍۜ اِنَّـمَٓا اَمْرُهُمْ اِلَى اللّٰهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوا يَفْعَلُونَ", translation: "Dinlerini parça parça edip, grup grup olanlar var ya, senin onlarla hiçbir ilişkin yoktur. Onların işi ancak Allah'a kalmıştır. Sonra O, onlara yaptıklarını bildirecektir.", transliteration: "İnnellezine ferreku dinehum ve kanu şiyean leste minhum fi şey', innema emruhum ilallahi summe yunebbiuhum bima kanu yef'alun."},
  {id: 949, sure_no: 160, ayet_no: 160, sure_adi: "Enam", text: "مَنْ جَٓاءَ بِالْحَسَنَةِ فَلَهُ عَشْرُ اَمْثَالِهَاۚ وَمَنْ جَٓاءَ بِالسَّيِّئَةِ فَلَا يُجْزٰٓى اِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُونَ", translation: "Kim bir iyilik ile gelirse ona on katı vardır, kim bir kötülük ile gelirse kötülüğü oranında karşılık bulur. Onların hiçbirine haksızlık yapılmaz.", transliteration: "Men cae bil haseneti fe lehu aşru emsaliha, ve men cae bis seyyieti fe la yucza illa misleha ve hum la yuzlemun."},
  {id: 950, sure_no: 161, ayet_no: 161, sure_adi: "Enam", text: "قُلْ اِنَّن۪ي هَدٰين۪ي رَبّ۪ٓي اِلٰى صِرَاطٍ مُسْتَق۪يمٍۚ د۪يناً قِيَماً مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۚ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ", translation: "De ki: \"Rabb'im, beni dosdoğru bir yola iletti: Dimdik ayakta duran bir dine, Hanif olan İbrahim'in inanç sistemine. O Müşriklerden değildi.\"", transliteration: "Kul inneni hedani rabbi ila sıratın mustekim dinen kıyamen millete ibrahime hanifa, ve ma kane minel muşrikin."},
  {id: 951, sure_no: 162, ayet_no: 162, sure_adi: "Enam", text: "قُلْ اِنَّ صَلَات۪ي وَنُسُك۪ي وَمَحْيَايَ وَمَمَات۪ي لِلّٰهِ رَبِّ الْعَالَم۪ينَۙ", translation: "De ki: \"Benim salatım, nusukum, hayatım ve ölümüm alemlerin Rabb'i olan Allah içindir.\"", transliteration: "Kul inne salati ve nusuki ve mahyaye ve memati lillahi rabbil alemin."},
  {id: 952, sure_no: 163, ayet_no: 163, sure_adi: "Enam", text: "لَا شَر۪يكَ لَهُۚ وَبِذٰلِكَ اُمِرْتُ وَاَنَا۬ اَوَّلُ الْمُسْلِم۪ينَ", translation: "\"O'nun hiçbir ortağı yoktur. Bana böyle buyruldu. Ben teslim olanların öncüsüyüm.\"", transliteration: "La şerike leh, ve bi zalike umirtu ve ene evvelul muslimin."},
  {id: 953, sure_no: 164, ayet_no: 164, sure_adi: "Enam", text: "قُلْ اَغَيْرَ اللّٰهِ اَبْغ۪ي رَباًّ وَهُوَ رَبُّ كُلِّ شَيْءٍۜ وَلَا تَكْسِبُ كُلُّ نَفْسٍ اِلَّا عَلَيْهَاۚ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۚ ثُمَّ اِلٰى رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ", translation: "De ki: \"Allah, her şeyin Rabb'iyken, ben O'ndan başka bir Rabb'mi arayayım?\" Herkesin kazandığı yalnız kendisine aittir. Hiç kimse bir başkasının yükünü yüklenmez. Sonunda dönüşünüz Rabb'inizedir. O, size ayrılığa düştüğünüz şeyleri haber verecektir.", transliteration: "Kul e gayrallahi ebgi rabben ve huve rabbu kulli şey', ve la teksibu kullu nefsin illa aleyh, ve la teziru vaziretun vizre uhra, summe ila rabbikum merciukum fe yunebbiukum bima kuntum fihi tahtelifun."},
  {id: 954, sure_no: 165, ayet_no: 165, sure_adi: "Enam", text: "وَهُوَ الَّذ۪ي جَعَلَكُمْ خَلَٓائِفَ الْاَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِيَبْلُوَكُمْ ف۪ي مَٓا اٰتٰيكُمْۜ اِنَّ رَبَّكَ سَر۪يعُ الْعِقَابِۘ وَاِنَّهُ لَغَفُورٌ رَح۪يمٌ", translation: "Sizi, yeryüzünün halifeleri yapan, size verdiği şeylerle denemek için bazınızı bazınıza derecelerle üstün kılan, O'dur. Kuşkusuz Rabb'in, cezası pek çabuk olandır. Ve gerçekten O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve huvellezi cealekum halaifelardı ve refea ba'dakum fevka ba'dın derecatin li yebluvekum fi ma atakum, inne rabbeke seriul ikabi ve innehu le gafurun rahim."},
  {id: 955, sure_no: 1, ayet_no: 1, sure_adi: "Araf", text: "الٓمٓصٓۜ", translation: "Elif, Lam, Mim, Sad.", transliteration: "Elif, lam, mim, sad"},
  {id: 956, sure_no: 2, ayet_no: 2, sure_adi: "Araf", text: "كِتَابٌ اُنْزِلَ اِلَيْكَ فَلَا يَكُنْ ف۪ي صَدْرِكَ حَرَجٌ مِنْهُ لِتُنْذِرَ بِه۪ وَذِكْرٰى لِلْمُؤْمِن۪ينَ", translation: "Bu, sana indirilen bir Kitap'tır. Onunla uyarıda bulunman ve Mü'minlere öğüt vermen konusunda göğsünde bir sıkıntı olmasın.", transliteration: "Kitabun unzile ileyke fe la yekun fi sadrike haracun minhu litunzire bihi ve zikra lil mu'minin."},
  {id: 957, sure_no: 3, ayet_no: 3, sure_adi: "Araf", text: "اِتَّبِعُوا مَٓا اُنْزِلَ اِلَيْكُمْ مِنْ رَبِّكُمْ وَلَا تَتَّبِعُوا مِنْ دُونِه۪ٓ اَوْلِيَٓاءَۜ  قَل۪يلاً مَا تَذَكَّرُونَ", translation: "Rabb'inizden size indirilene uyun. O'nun yanı sıra başka velilere uymayın. Ne kadar az öğüt tutuyorsunuz!", transliteration: "Ittebiu ma unzile ileykum min rabbikum ve la tettebiu min dunihi evliya, kalilen ma tezekkerun."},
  {id: 958, sure_no: 4, ayet_no: 4, sure_adi: "Araf", text: "وَكَمْ مِنْ قَرْيَةٍ اَهْلَكْنَاهَا فَجَٓاءَهَا بَأْسُنَا بَيَاتاً اَوْ هُمْ قَٓائِلُونَ", translation: "Biz, nice kentleri yok ettik. Gece uyurlarken veya gündüz uykusundayken onlara azabımız gelip çattı.", transliteration: "Ve kem min karyetin ehleknaha fe caeha be'suna beyaten ev hum kailun."},
  {id: 959, sure_no: 5, ayet_no: 5, sure_adi: "Araf", text: "فَمَا كَانَ دَعْوٰيهُمْ اِذْ جَٓاءَهُمْ بَأْسُنَٓا اِلَّٓا اَنْ قَالُٓوا اِنَّا كُنَّا ظَالِم۪ينَ", translation: "Azabımız onlara geldiğinde tek feryatları, \"Biz gerçekten zalimlermişiz.\" demelerinden başka bir şey olmadı.", transliteration: "Fe ma kane da'vahum iz caehum be'suna illa en kalu inna kunna zalimin."},
  {id: 960, sure_no: 6, ayet_no: 6, sure_adi: "Araf", text: "فَلَنَسْـَٔلَنَّ الَّذ۪ينَ اُرْسِلَ اِلَيْهِمْ وَلَنَسْـَٔلَنَّ الْمُرْسَل۪ينَۙ", translation: "Kendilerine elçi gönderilenlere de gönderilen elçilere de mutlaka soracağız.", transliteration: "Fe le nes'elennellezine ursile ileyhim ve le nes'elennel murselin."},
  {id: 961, sure_no: 7, ayet_no: 7, sure_adi: "Araf", text: "فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَمَا كُنَّا غَٓائِب۪ينَ", translation: "Kesin bir bilgi ile onlara yaptıklarını anlatacağız. Zira Biz yaptıkları hiçbir şeyden habersiz değiliz.", transliteration: "Fe le nekussanne aleyhim bi ilmin ve ma kunna gaibin."},
  {id: 962, sure_no: 8, ayet_no: 8, sure_adi: "Araf", text: "وَالْوَزْنُ يَوْمَئِذٍۨ الْحَقُّۚ فَمَنْ ثَقُلَتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "O Gün tartı tam doğrudur. Kimin tartısı ağır gelirse, işte onlar kurtuluşa erenlerdir.", transliteration: "Vel veznu yevme izinil hakk, fe men sekulet mevazinuhu fe ulaike humul muflihun."},
  {id: 963, sure_no: 9, ayet_no: 9, sure_adi: "Araf", text: "وَمَنْ خَفَّتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ بِمَا كَانُوا بِاٰيَاتِنَا يَظْلِمُونَ", translation: "Kimin de tartısı hafif gelirse, işte onlar, ayetlerimize haksızlık etmelerinden dolayı kendilerine yazık edenlerdir.", transliteration: "Ve men haffet mevazinuhu fe ulaikellezine hasiru enfusehum bima kanu biayatina yazlimun."},
  {id: 964, sure_no: 10, ayet_no: 10, sure_adi: "Araf", text: "وَلَقَدْ مَكَّنَّاكُمْ فِي الْاَرْضِ وَجَعَلْنَا لَكُمْ ف۪يهَا مَعَايِشَۜ قَل۪يلاً مَا تَشْكُرُونَ۟", translation: "Doğrusu Biz, sizi yeryüzüne yerleştirdik ve orada size geçimlik verdik. Ne kadar da az şükrediyorsunuz.", transliteration: "Ve lekad mekkennakum fil ardı ve cealna lekum fiha maayiş', kalilen ma teşkurun."},
  {id: 965, sure_no: 11, ayet_no: 11, sure_adi: "Araf", text: "وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ ثُمَّ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَۗ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ لَمْ يَكُنْ مِنَ السَّاجِد۪ينَ", translation: "Ant olsun ki sizi Biz yarattık. Sonra size şekil verdik. Sonra meleklere, \"Adem'e secde edin.\" dedik. İblis hariç hepsi secde ettiler. O, secde edenlerden olmadı.", transliteration: "Ve lekad halaknakum summe savvernakum summe kulna lil melaiketiscudu li ademe fe secedu illa iblis, lem yekun mines sacidin."},
  {id: 966, sure_no: 12, ayet_no: 12, sure_adi: "Araf", text: "قَالَ مَا مَنَعَكَ اَلَّا تَسْجُدَ اِذْ اَمَرْتُكَۜ قَالَ اَنَا۬ خَيْرٌ مِنْهُۚ خَلَقْتَن۪ي مِنْ نَارٍ وَخَلَقْتَهُ مِنْ ط۪ينٍ", translation: "\"Sana buyurduğum halde, seni secde etmekten alıkoyan nedir?\" dedi. \"Ben ondan hayırlıyım, beni ateşten onu çamurdan yarattın.\" dedi.", transliteration: "Kale ma meneake ella tescude iz emertuk, kale ene hayrun minh, halakteni min narin ve halaktehu min tin."},
  {id: 967, sure_no: 13, ayet_no: 13, sure_adi: "Araf", text: "قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُونُ لَكَ اَنْ تَتَكَبَّرَ ف۪يهَا فَاخْرُجْ اِنَّكَ مِنَ الصَّاغِر۪ينَ", translation: "\"Hemen oradan in; orada büyüklük taslamak haddin değil. Hemen oradan çık. Sen aşağılanmışlardansın!\" dedi.", transliteration: "Kale fehbit minha fe ma yekunu leke en tetekebbere fiha fahruc inneke mines sagirin."},
  {id: 968, sure_no: 14, ayet_no: 14, sure_adi: "Araf", text: "قَالَ اَنْظِرْن۪ٓي اِلٰى يَوْمِ يُبْعَثُونَ", translation: "\"Tekrar diriltilecekleri güne kadar beni gözle\" dedi.", transliteration: "Kale enzırni ila yevmi yub'asun."},
  {id: 969, sure_no: 15, ayet_no: 15, sure_adi: "Araf", text: "قَالَ اِنَّكَ مِنَ الْمُنْظَر۪ينَ", translation: "\"Sen gözlenenlerdensin.\" dedi.", transliteration: "Kale inneke minel munzarin."},
  {id: 970, sure_no: 16, ayet_no: 16, sure_adi: "Araf", text: "قَالَ فَبِمَٓا اَغْوَيْتَن۪ي لَاَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَق۪يمَۙ", translation: "\"Azdırmandan dolayı, onlar için senin dosdoğru yolunun üzerine oturacağım.\" dedi.", transliteration: "Kale fe bima agveyteni le ak'udenne lehum sıratekel mustekim."},
  {id: 971, sure_no: 17, ayet_no: 17, sure_adi: "Araf", text: "ثُمَّ لَاٰتِيَنَّهُمْ مِنْ بَيْنِ اَيْد۪يهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ اَيْمَانِهِمْ وَعَنْ شَمَٓائِلِهِمْۜ وَلَا تَجِدُ اَكْثَرَهُمْ شَاكِر۪ينَ", translation: "\"Sonra, ant olsun ki onlara önlerinden, arkalarından, sağlarından ve sollarından sokulacağım ve Sen onların çoğunu şükrediciler olarak bulamayacaksın.\"", transliteration: "Summe le atiyennehum min beyni eydihim ve min halfihim ve an eymanihim ve an şemailihim, ve la tecidu ekserehum şakirin."},
  {id: 972, sure_no: 18, ayet_no: 18, sure_adi: "Araf", text: "قَالَ اخْرُجْ مِنْهَا مَذْؤُ۫ماً مَدْحُوراًۜ لَمَنْ تَبِعَكَ مِنْهُمْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنْكُمْ اَجْمَع۪ينَ", translation: "\"Kovulmuş ve kınanmış olarak oradan çık. Onlardan kim sana uyarsa, Cehennem'i onlarla dolduracağım.\" dedi.", transliteration: "Kalehruc minha mez'umen medhura, le men tebiake minhum leemleenne cehenneme minkum ecmain."},
  {id: 973, sure_no: 19, ayet_no: 19, sure_adi: "Araf", text: "وَيَٓا اٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِم۪ينَ", translation: "Ey Adem! \"Sen ve eşin cennete yerleşin, dilediğiniz yerden yiyin, fakat şu ağaca yaklaşmayın. Yoksa zalimlerden olursunuz.\"", transliteration: "Ve ya ademuskun ente ve zevcukel cennete fe kula min haysu şi'tuma ve la takreba hazihiş şecerete fe tekuna minez zalimin."},
  {id: 974, sure_no: 20, ayet_no: 20, sure_adi: "Araf", text: "فَوَسْوَسَ لَهُمَا الشَّيْطَانُ لِيُبْدِيَ لَهُمَا مَا وُ۫رِيَ عَنْهُمَا مِنْ سَوْاٰتِهِمَا وَقَالَ مَا نَهٰيكُمَا رَبُّكُمَا عَنْ هٰذِهِ الشَّجَرَةِ اِلَّٓا اَنْ تَكُونَا مَلَكَيْنِ اَوْ تَكُونَا مِنَ الْخَالِد۪ينَ", translation: "Derken şeytan, kötülüklerini kendilerine göstermek için onlara fısıldadı. Rabb'iniz size bu ağacı melik olmayasınız veya kalıcılardan olmayasınız diye yasakladı.\" dedi.", transliteration: "Fe vesvese lehumuş şeytanu li yubdiye lehuma ma vuriye anhuma min sev'atihima ve kale ma nehakuma rabbukuma an hazihiş şecereti illa en tekuna melekeyni ev tekuna minel halidin."},
  {id: 975, sure_no: 21, ayet_no: 21, sure_adi: "Araf", text: "وَقَاسَمَـهُمَٓا اِنّ۪ي لَكُمَا لَمِنَ النَّاصِح۪ينَۙ", translation: "Ve ikisine: \"Ben gerçekten ikinizin de iyiliğini istemekteyim.\" diye yemin etti.", transliteration: "Ve kasemehuma inni lekuma le minen nasıhin."},
  {id: 976, sure_no: 22, ayet_no: 22, sure_adi: "Araf", text: "فَدَلّٰيهُمَا بِغُرُورٍۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِۜ وَنَادٰيهُمَا رَبُّهُمَٓا اَلَمْ اَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَاَقُلْ لَكُمَٓا اِنَّ الشَّيْطَانَ لَكُمَا عَدُوٌّ مُب۪ينٌ", translation: "Böylece ikisini aldatıp baştan çıkardı. O ağaçtan tadınca, çirkinlikleri açığa çıktı. Cennet yapraklarını üst üste koyup örtünmeye başladılar. Rabb'leri onlara: \"Ben sizi o ağaçtan men etmedim mi? Bu şeytan size apaçık bir düşmandır demedim mi?\" diye seslendi.", transliteration: "Fedellahuma bi gurur, fe lemma zakaş şecerete bedet lehuma sev'atuhuma ve tafika yahsıfani aleyhima min varakıl cenneh, ve nadahuma rabbuhuma e lem enhekuma an tilkumeş şecereti ve ekul lekuma inneş şeytane lekuma aduvvun mubin."},
  {id: 977, sure_no: 23, ayet_no: 23, sure_adi: "Araf", text: "قَالَا رَبَّـنَا ظَلَمْنَٓا اَنْفُسَنَا وَاِنْ لَمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ الْخَاسِر۪ينَ", translation: "\"Ey Rabb'imiz! Biz, kendimize zulmettik. Eğer bizi bağışlamaz ve merhamet etmezsen hüsrana uğrayanlardan oluruz.\" dediler.", transliteration: "Kala rabbena zalemna enfusena ve in lem tagfirlena ve terhamna le nekunenne minel hasirin."},
  {id: 978, sure_no: 24, ayet_no: 24, sure_adi: "Araf", text: "قَالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّۚ وَلَكُمْ فِي الْاَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ اِلٰى ح۪ينٍ", translation: "\"Bir kısmınız bir kısmınıza düşman olarak inin.\" dedi. Yeryüzünde, size belli bir süreye kadar yerleşme ve yararlanma imkanı vardır.\" dedi.", transliteration: "Kalehbitu ba'dukum li ba'dın aduvv, ve lekum fil'ardı mustekarrun ve metaun ila hin."},
  {id: 979, sure_no: 25, ayet_no: 25, sure_adi: "Araf", text: "قَالَ ف۪يهَا تَحْيَوْنَ وَف۪يهَا تَمُوتُونَ وَمِنْهَا تُخْرَجُونَ۟", translation: "De ki: \"Orada yaşayacaksınız, orada öleceksiniz ve oradan çıkarılacaksınız.\"", transliteration: "Kale fiha tahyevne ve fiha temutune ve minha tuhrecun."},
  {id: 980, sure_no: 26, ayet_no: 26, sure_adi: "Araf", text: "يَا بَن۪ٓي اٰدَمَ قَدْ اَنْزَلْنَا عَلَيْكُمْ لِبَاساً يُوَار۪ي سَوْاٰتِكُمْ وَر۪يشاً۠ وَلِبَاسُ التَّقْوٰى ذٰلِكَ خَيْرٌۜ ذٰلِكَ مِنْ اٰيَاتِ اللّٰهِ لَعَلَّهُمْ يَذَّكَّرُونَ", translation: "Ey Ademoğulları! Cesetlerinizi örtecek ve sizi süsleyecek libas indirdik. Takva libası ise daha hayırlıdır. İşte bu Allah'ın ayetlerindendir. Umulur ki düşünüp öğüt alırlar.", transliteration: "Ya beni ademe kad enzelna aleykum libasen yuvari sev'atikum ve rişa ve libasut takva zalike hayr, zalike min ayatillahi leallehum yezzekkerun."},
  {id: 981, sure_no: 27, ayet_no: 27, sure_adi: "Araf", text: "يَا بَن۪ٓي اٰدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطَانُ كَمَٓا اَخْرَجَ اَبَوَيْكُمْ مِنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْاٰتِهِمَاۜ اِنَّهُ يَرٰيكُمْ هُوَ وَقَب۪يلُهُ مِنْ حَيْثُ لَا تَرَوْنَهُمْۜ اِنَّا جَعَلْنَا الشَّيَاط۪ينَ اَوْلِيَٓاءَ لِلَّذ۪ينَ لَا يُؤْمِنُونَ", translation: "Ey ademoğulları! Şeytan kötülüklerini onlara göstermek için, libaslarını soyarak, ana babanızı cennetten çıkardığı gibi sizi de bir fitneye düşürmesin. O ve taraftarları, sizin onları göremeyeceğiniz yerden sizi görürler. Biz, şeytanları iman etmeyenlere evliya kıldık.", transliteration: "Ya beni ademe la yeftinennekumuş şeytanu kema ahrece ebeveykum minel cenneti yenziu anhuma libasehuma li yuriyehuma sev'atihima innehu yerakum huve ve kabiluhu min haysu la terevnehum inna cealneş şeyatine evliyae lillezine la yu'minun."},
  {id: 982, sure_no: 28, ayet_no: 28, sure_adi: "Araf", text: "وَاِذَا فَعَلُوا فَاحِشَةً قَالُوا وَجَدْنَا عَلَيْهَٓا اٰبَٓاءَنَا وَاللّٰهُ اَمَرَنَا بِهَاۜ قُلْ اِنَّ اللّٰهَ لَا يَأْمُرُ بِالْفَحْشَٓاءِۜ اَتَقُولُونَ عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "Onlar bir fuhuş yaptıkları zaman, \"Atalarımızdan böyle gördük ve Allah bize böyle emretti.\" derler. De ki: \"Allah fuhşu emretmez. Hakkında bilginiz olmayan şeyleri mi Allah'a yakıştırıyorsunuz?\"", transliteration: "Ve iza faalu fahişeten kalu vecedna aleyha abaena vallahu emerena biha kul innallahe la ye'muru bil fahşa, e tekulune alallahi ma la ta'lemun."},
  {id: 983, sure_no: 29, ayet_no: 29, sure_adi: "Araf", text: "قُلْ اَمَرَ رَبّ۪ي بِالْقِسْطِ۠ وَاَق۪يمُوا وُجُوهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَادْعُوهُ مُخْلِص۪ينَ لَهُ الدّ۪ينَۜ   كَمَا بَدَاَكُمْ تَعُودُونَۜ", translation: "De ki: \"Rabb'im bana haktan yana olmayı emretti.\" Her mescitte yüzlerinizi O'na doğrultun ve dini yalnızca O'na has kılarak dua edin. Sizi ilk yarattığı gibi yine O'na döneceksiniz.", transliteration: "Kul emere rabbi bil kıst ve ekimu vucuhekum inde kulli mescidin ved'uhu muhlisine lehud din, kema bedeekum teudun."},
  {id: 984, sure_no: 30, ayet_no: 30, sure_adi: "Araf", text: "فَر۪يقاً هَدٰى وَفَر۪يقاً حَقَّ عَلَيْهِمُ الضَّلَالَةُۜ اِنَّهُمُ اتَّخَذُوا الشَّيَاط۪ينَ اَوْلِيَٓاءَ مِنْ دُونِ اللّٰهِ وَيَحْسَبُونَ اَنَّهُمْ مُهْتَدُونَ", translation: "Bir kısmını doğru yola iletti. Bir kısmı da sapkın yolu hak etti. Zira onlar, Allah'ın yanı sıra şeytanları evliya edindiler. Ve kendilerini de doğru yolda olduklarını sanıyorlar!", transliteration: "Ferikan hada ve ferikan hakka aleyhimud dalaletu, innehumuttehazuş şeyatine evliyae min dunillahi ve yahsebune ennehum muhtedun."},
  {id: 985, sure_no: 31, ayet_no: 31, sure_adi: "Araf", text: "يَا بَن۪ٓي اٰدَمَ خُذُوا ز۪ينَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَكُلُوا وَاشْرَبُوا وَلَا تُسْرِفُواۚ اِنَّهُ لَا يُحِبُّ الْمُسْرِف۪ينَ۟", translation: "Ey Ademoğulları! Her secde edilen yerde ziynetlerinizi alın. Yiyin, için fakat israf etmeyin. Zira O, israf edenleri sevmez.", transliteration: "Ya beni ademe huzu zinetekum inde kulli mescidin ve kulu veşrebu ve la tusrifu, innehu la yuhıbbul musrifin."},
  {id: 986, sure_no: 32, ayet_no: 32, sure_adi: "Araf", text: "قُلْ مَنْ حَرَّمَ ز۪ينَةَ اللّٰهِ الَّت۪ٓي اَخْرَجَ لِعِبَادِه۪ وَالطَّيِّبَاتِ مِنَ الرِّزْقِۜ قُلْ هِيَ لِلَّذ۪ينَ اٰمَنُوا فِي الْحَيٰوةِ الدُّنْيَا خَالِصَةً يَوْمَ الْقِيٰمَةِۜ  كَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ", translation: "De ki: \"Allah'ın kulları için çıkardığı ziyneti ve temiz rızıkları kim haram etmiş?\" De ki: \"Bunlar dünya hayatında iman edenlerindir; kıyamet günü ise yalnız onlara aittir.\" Bilen bir topluma ayetleri böyle detaylı olarak açıklıyoruz.", transliteration: "Kul men harreme zinetallahilleti ahrece li ibadihi vet tayyibati miner rızk, kul hiye lillezine amenu fil hayatid dunya halisaten yevmel kıyameh, kezalike nufassılul ayati li kavmin ya'lemun."},
  {id: 987, sure_no: 33, ayet_no: 33, sure_adi: "Araf", text: "قُلْ اِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْاِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَاَنْ تُشْرِكُوا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ سُلْطَاناً وَاَنْ تَقُولُوا عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "De ki: \"Rabb'im şunları kesinlikle haram kılmıştır: Fuhuşların açık olanını ve gizli olanını, günahları, haksız yere saldırmayı, hakkında hiçbir belge indirmediği herhangi bir şeyi Allah'a ortak koşmayı ve Allah'a, bilmediğiniz şeyleri yakıştırmanızı.\"", transliteration: "Kul innema harreme rabbiyel fevahişe ma zahere minha ve ma batane vel isme vel bagye bi gayril hakkı ve en tuşriku billahi ma lem yunezzil bihi sultanen ve en tekulu alallahi ma la ta'lemun."},
  {id: 988, sure_no: 34, ayet_no: 34, sure_adi: "Araf", text: "وَلِكُلِّ اُمَّةٍ اَجَلٌۚ فَاِذَا جَٓاءَ اَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً وَلَا يَسْتَقْدِمُونَ", translation: "Her ümmetin bir eceli vardır. Ecelleri gelince ne bir an ertelenir ne de bir an öne alınır.", transliteration: "Ve li kulli ummetin ecel, fe iza cae eceluhum la yeste'hırune saaten ve la yestakdimun."},
  {id: 989, sure_no: 35, ayet_no: 35, sure_adi: "Araf", text: "يَا بَن۪ٓي اٰدَمَ اِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ اٰيَات۪يۙ فَمَنِ اتَّقٰى وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ", translation: "Ey Adem oğulları! Size, içinizden ayetlerimi anlatan elçiler geldiğinde, her kim takvalı davranır ve kendisini düzeltirse, onlara kaygı yoktur ve onlar üzülmeyecekler de.", transliteration: "Ya beni ademe imma ye'tiyennekum rusulun minkum yekussune aleykum ayati fe menitteka ve asleha fe la havfun aleyhim ve la hum yahzenun."},
  {id: 990, sure_no: 36, ayet_no: 36, sure_adi: "Araf", text: "وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَاسْتَكْبَرُوا عَنْهَٓا اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "Büyüklük taslayarak, ayetlerimizi yalanlayanlar, işte onlar ateş halkıdırlar. Onlar, orada sürekli kalıcıdırlar.", transliteration: "Vellezine kezzebu bi ayatina vestekberu anha ulaike ashabun nar, hum fiha halidun."},
  {id: 991, sure_no: 37, ayet_no: 37, sure_adi: "Araf", text: "فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ كَذَّبَ بِاٰيَاتِه۪ۜ  اُو۬لٰٓئِكَ يَنَالُهُمْ نَص۪يبُهُمْ مِنَ الْكِتَابِۜ حَتّٰٓى اِذَا جَٓاءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْۙ قَالُٓوا اَيْنَ مَا كُنْتُمْ تَدْعُونَ مِنْ دُونِ اللّٰهِۜ قَالُوا ضَلُّوا عَنَّا وَشَهِدُوا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوا كَافِر۪ينَ", translation: "Allah'a iftira eden veya O'nun ayetlerini yalanlayandan daha zalim kim olabilir? Kitap'taki nasipleri onlara erişecektir. Nihayet Resullerimiz, canlarını almak için onlara geldiğinde, \"Allah'tan başka yakardığınız ilahlar nerede?\" dediklerinde; onlar da: \"Onlar bizden uzaklaşıp gittiler.\" dediler. Kafir olduklarına dair kendi aleyhlerinde tanıklık ettiler.", transliteration: "Fe men azlemu mimmeniftera alallahi keziben ev kezzebe bi ayatih ulaike yenaluhum nasibuhum minel kitab, hatta iza caethum rusuluna yeteveffevnehum kalu eyne ma kuntum ted'une min dunillah kalu dallu anna ve şehidu ala enfusihim ennehum kanu kafirin."},
  {id: 992, sure_no: 38, ayet_no: 38, sure_adi: "Araf", text: "قَالَ ادْخُلُوا ف۪ٓي اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِنَ الْجِنِّ وَالْاِنْسِ فِي النَّارِۜ   كُلَّمَا دَخَلَتْ اُمَّةٌ لَعَنَتْ اُخْتَهَاۜ  حَتّٰٓى اِذَا ادَّارَكُوا ف۪يهَا جَم۪يعاًۙ قَالَتْ اُخْرٰيهُمْ لِاُو۫لٰيهُمْ رَبَّنَا هٰٓؤُ۬لَٓاءِ اَضَلُّونَا فَاٰتِهِمْ عَذَاباً ضِعْفاً مِنَ النَّارِۜ قَالَ لِكُلٍّ ضِعْفٌ وَلٰكِنْ لَا تَعْلَمُونَ", translation: "Buyurdu ki: \"Sizden önceki cinn ve insten ümmetler arasında siz de ateşe girin. Her ümmet girdikçe, yoldaşlarına lanet etti. Birbiri ardına hepsi orada toplandığı zaman; sonrakiler öncekiler için: \"Ey Rabb'imiz! Bunlar bizi saptırdılar, bunlara ateşten bir kat daha azap ver.\" \"Hepsi için bir kat fazla vardır, ama siz bilmezsiniz.\" dedi.", transliteration: "Kaledhulu fi umemin kad halet min kablikum minel cinni vel insi fin nar, kullema dehalet ummetun leanet uhteha, hatta izeddareku fiha cemian kalet uhrahum li ulahum rabbena haulai edalluna fe atihim azaben di'fen minen nar kale li kullin di'fun ve lakin la ta'lemun."},
  {id: 993, sure_no: 39, ayet_no: 39, sure_adi: "Araf", text: "وَقَالَتْ اُو۫لٰيهُمْ لِاُخْرٰيهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُونَ۟", translation: "Öncekiler de sonrakilere: \"Sizin, bizden iyi bir tarafınız yoktu. O halde kazandıklarınıza karşılık azabı tadın.\" dediler.", transliteration: "Ve kalet ulahum li uhrahum fe ma kane lekum aleyna min fadlin fe zukul azabe bima kuntum teksibun."},
  {id: 994, sure_no: 40, ayet_no: 40, sure_adi: "Araf", text: "اِنَّ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا لَا تُفَتَّحُ لَهُمْ اَبْوَابُ السَّمَٓاءِ وَلَا يَدْخُلُونَ الْجَنَّةَ حَتّٰى يَلِجَ الْجَمَلُ ف۪ي سَمِّ الْخِيَاطِۜ وَكَذٰلِكَ نَجْزِي الْمُجْرِم۪ينَ", translation: "Ayetlerimizi yalanlayan ve büyüklenenler var ya, onlara gök kapıları açılmayacak ve onlar deve iğnenin deliğinden geçmedikçe, Cennet'e giremeyeceklerdir. Mücrimleri böyle cezalandırırız.", transliteration: "İnnellezine kezzebu bi ayatina vestekberu anha la tufettehu lehum ebvabus semai ve la yedhulunel cennete hatta yelicel cemelu fi semmil hiyat ve kezalike neczil mucrimin."},
  {id: 995, sure_no: 41, ayet_no: 41, sure_adi: "Araf", text: "لَهُمْ مِنْ جَهَنَّمَ مِهَادٌ وَمِنْ فَوْقِهِمْ غَوَاشٍۜ وَكَذٰلِكَ نَجْزِي الظَّالِم۪ينَ", translation: "Onlar için Cehennem'den döşek ve örtüler vardır. İşte zalimleri böyle cezalandırırız.", transliteration: "Lehum min cehenneme mihadun ve min fevkıhim gavaş ve kezalike necziz zalimin."},
  {id: 996, sure_no: 42, ayet_no: 42, sure_adi: "Araf", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَا نُكَلِّفُ نَفْساً اِلَّا وُسْعَهَاۘ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "İman edip salihatı yapanlar - ki hiç kimseye gücünün yettiğinden başkasını yüklemeyiz- Onlar, orada sürekli kalacaklardır.", transliteration: "Vellezine amenu ve amilus salihati la nukellifu nefsen illa vus'aha ulaike ashabul cenneh, hum fiha halidun."},
  {id: 997, sure_no: 43, ayet_no: 43, sure_adi: "Araf", text: "وَنَزَعْنَا مَا ف۪ي صُدُورِهِمْ مِنْ غِلٍّ تَجْر۪ي مِنْ تَحْتِهِمُ الْاَنْهَارُۚ وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذ۪ي هَدٰينَا لِهٰذَا وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَٓا اَنْ هَدٰينَا اللّٰهُۚ  لَقَدْ جَٓاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۜ وَنُودُٓوا اَنْ تِلْكُمُ الْجَنَّةُ اُو۫رِثْتُمُوهَا بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Göğüslerinde tasadan ne varsa çıkarıp almışız. Yanı başlarında ırmaklar akmaktadır. Derler ki: \"Bizi buna ulaştıran Allah'a hamdolsun. Eğer Allah bizi doğru yola iletmeseydi biz kendiliğimizden doğru yolu bulamazdık. Ant olsun ki Rabb'imizin Resulleri gerçeği getirmişlerdir.\" Onlara: \"İşte yaptığınız işlere karşılık, hak ettiğiniz Cennet budur.\" diye seslenilir.", transliteration: "Ve neza'na ma fi sudurihim min gıllin tecri min tahtihimul enhar, ve kalul hamdu lillahillezi hedana li haza ve ma kunna li nehtediye levla en hedanallah, lekad caet rusulu rabbina bil hakk, ve nudu en tilkumul cennetu uristumuha bima kuntum ta'melun."},
  {id: 998, sure_no: 44, ayet_no: 44, sure_adi: "Araf", text: "وَنَادٰٓى اَصْحَابُ الْجَنَّةِ اَصْحَابَ النَّارِ اَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقاًّ فَهَلْ وَجَدْتُمْ مَا وَعَدَ رَبُّكُمْ حَقاًّۜ  قَالُوا نَعَمْۚ فَاَذَّنَ مُؤَذِّنٌ بَيْنَهُمْ اَنْ لَعْنَةُ اللّٰهِ عَلَى الظَّالِم۪ينَۙ", translation: "Cennet halkı, Cehennem halkına, \"Rabb'imizin bize söz verdiklerinin gerçek olduğunu gördük; siz de Rabb'inizin size söylediklerinin gerçek olduğunu gördünüz mü?\" diye seslenirler. \"Evet.\" derler. Aralarından bir çağırıcı, \"Allah'ın laneti zalimlerin üzerine olsun.\" diye bağırır.", transliteration: "Ve nada ashabul cenneti ashaben nari en kad vecedna ma vaadena rabbuna hakka fe hel vecedtum ma vaade rabbukum hakka kalu neam fe ezzene muezzinun beynehum en la'netullahi alez zalimin."},
  {id: 999, sure_no: 45, ayet_no: 45, sure_adi: "Araf", text: "اَلَّذ۪ينَ يَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَيَبْغُونَهَا عِوَجاًۚ وَهُمْ بِالْاٰخِرَةِ كَافِرُونَۜ", translation: "Onlar, Allah'ın yolundan alıkoyan ve onu eğri göstermek isteyen ve ahireti de inkar eden kimselerdi.", transliteration: "Ellezine yasuddune an sebilillahi ve yebguneha iveca ve hum bil ahireti kafirun."},
  {id: 1000, sure_no: 46, ayet_no: 46, sure_adi: "Araf", text: "وَبَيْنَـهُمَا حِجَابٌۚ وَعَلَى الْاَعْرَافِ رِجَالٌ يَعْرِفُونَ كُلاًّ بِس۪يمٰيهُمْۚ وَنَادَوْا اَصْحَابَ الْجَنَّةِ اَنْ سَلَامٌ عَلَيْكُمْ لَمْ يَدْخُلُوهَا وَهُمْ يَطْمَعُونَ", translation: "İki taraf arasında bir hicap vardır. Ve A'raf' üzerinde de hepsini simalarından tanıyan kimseler vardır. Henüz Cennet'e girmemiş olan, fakat girmeyi uman Cennet halkına: \"Size selam olsun.\" diye seslendiler.", transliteration: "Ve beynehuma hicab ve alel a'rafi ricalun ya'rifune kullen bi simahum ve nadev ashabel cenneti en selamun aleykum lem yedhuluha ve hum yatmeun."},
  {id: 1001, sure_no: 47, ayet_no: 47, sure_adi: "Araf", text: "وَاِذَا صُرِفَتْ اَبْصَارُهُمْ تِلْقَٓاءَ اَصْحَابِ النَّارِۙ قَالُوا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظَّالِم۪ينَ۟", translation: "Bakışları ateş halkı tarafına döndürülünce de \"Ey Rabb'imiz! Bizi zalim halkla beraber bulundurma.\" derler.", transliteration: "Ve iza surifet ebsaruhum tilkae ashabin nari kalu rabbena la tec'alna mealkavmiz zalimin."},
  {id: 1002, sure_no: 48, ayet_no: 48, sure_adi: "Araf", text: "وَنَادٰٓى اَصْحَابُ الْاَعْرَافِ رِجَالاً يَعْرِفُونَهُمْ بِس۪يمٰيهُمْ قَالُوا مَٓا اَغْنٰى عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُونَ", translation: "A'raf halkı, yüzlerinden tanıdıkları kimselere de: \"Çokluğunuz da tasladığınız büyüklük de size bir yarar sağlamadı.\" dediler.", transliteration: "Ve nada ashabul'a'rafi ricalen ya'rifunehum bi simahum kalu ma agna ankum cem'ukum ve ma kuntum testekbirun."},
  {id: 1003, sure_no: 49, ayet_no: 49, sure_adi: "Araf", text: "اَهٰٓؤُ۬لَٓاءِ الَّذ۪ينَ اَقْسَمْتُمْ لَا يَنَالُهُمُ اللّٰهُ بِرَحْمَةٍۜ اُدْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَٓا اَنْتُمْ تَحْزَنُونَ", translation: "Cehennemliklere; \"Allah, hiçbir rahmete erdirmeyecek diye yemin ettiğiniz kimseler bunlar mıydı?\" Onlara: \"Girin Cennet'e, artık size korku yoktur. Üzülecek de değilsiniz.\" denir.", transliteration: "E haulaillezine aksemtum la yenaluhumullahu bi rahmeh udhulul cennete la havfun aleykum ve la entum tahzenun."},
  {id: 1004, sure_no: 50, ayet_no: 50, sure_adi: "Araf", text: "وَنَادٰٓى اَصْحَابُ النَّارِ اَصْحَابَ الْجَنَّةِ اَنْ اَف۪يضُوا عَلَيْنَا مِنَ الْمَٓاءِ اَوْ مِمَّا رَزَقَكُمُ اللّٰهُۜ قَالُٓوا اِنَّ اللّٰهَ حَرَّمَهُمَا عَلَى الْكَافِر۪ينَۙ", translation: "Ateş halkı, Cennet halkına, \"Suyunuzdan veya Allah'ın rızık olarak verdiği şeylerden biraz da bize verin.\" diye feryat ederler. Onlar, \"Allah, bu ikisini kafirlere haram kılmıştır.\" derler.", transliteration: "Ve nada ashabun nari ashabel cenneti en efidu aleyna minel mai ev mimma rezekakumullah, kalu innallahe harremehuma alel kafirin."},
  {id: 1005, sure_no: 51, ayet_no: 51, sure_adi: "Araf", text: "اَلَّذ۪ينَ اتَّخَذُوا د۪ينَهُمْ لَهْواً وَلَعِباً وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَاۚ فَالْيَوْمَ نَنْسٰيهُمْ كَمَا نَسُوا لِقَٓاءَ يَوْمِهِمْ هٰذَاۙ وَمَا كَانُوا بِاٰيَاتِنَا يَجْحَدُونَ", translation: "Onlar ki, dinlerini bir oyun ve eğlence yerine koydular. Dünya hayatı onları aldattı. Onlar, karşılaşacakları bugünü unuttukları ve ayetlerimizi bile bile inkar ettikleri gibi, biz de onları unuturuz.", transliteration: "Ellezinettehazu dinehum lehven ve leiben ve garrethumul hayatud dunya, felyevme nensahum kema nesu likae yevmihim haza ve ma kanu bi ayatina yechadun."},
  {id: 1006, sure_no: 52, ayet_no: 52, sure_adi: "Araf", text: "وَلَقَدْ جِئْنَاهُمْ بِكِتَابٍ فَصَّلْنَاهُ عَلٰى عِلْمٍ هُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ", translation: "Gerçekten Biz, onlara, iman etmek isteyen bir halk için, bilgiye göre açıkladığımız; yol gösterici ve rahmet olan bir Kitap getirdik.", transliteration: "Ve lekad ci'nahum bi kitabin fassalnahu ala ilmin huden ve rahmeten li kavmin yu'minun."},
  {id: 1007, sure_no: 53, ayet_no: 53, sure_adi: "Araf", text: "هَلْ يَنْظُرُونَ اِلَّا تَأْو۪يلَهُۜ يَوْمَ يَأْت۪ي تَأْو۪يلُهُ يَقُولُ الَّذ۪ينَ نَسُوهُ مِنْ قَبْلُ قَدْ جَٓاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۚ فَهَلْ لَنَا مِنْ شُفَعَٓاءَ فَيَشْفَعُوا لَـنَٓا اَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذ۪ي كُنَّا نَعْمَلُۜ قَدْ خَسِرُٓوا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ۟", translation: "Onun verdiği haberin gerçekleşmesini mi bekliyorlar? Onun haberinin gerçekleştiği gün, daha önce onu unutmuş olanlar diyecekler ki: \"Gerçekten Rabb'imizin Resulleri gerçeği getirmişler. Acaba bir şefaatçi var mıdır ki bize şefaatte bulunsun veya geri döndürülsek de yaptıklarımızdan başkasını yapsak?\" Gerçekten onlar kendilerine yazık etmişlerdir. Uydurdukları şeyler kendilerinden uzaklaşıp kaybolmuştur.", transliteration: "Hel yanzurune illa te'vileh, yevme ye'ti te'viluhu yekulullezine nesuhu min kablu kad caet rusulu rabbina bil hakk, fe hel lena min şufeae fe yeşfeu lena ev nureddu fe na'mele gayrellezi kunna na'mel, kad hasiru enfusehum ve dalle anhum ma kanu yefterun."},
  {id: 1008, sure_no: 54, ayet_no: 54, sure_adi: "Araf", text: "اِنَّ رَبَّكُمُ اللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُغْشِي الَّيْلَ النَّهَارَ يَطْلُبُهُ حَث۪يثاًۙ وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومَ مُسَخَّرَاتٍ بِاَمْرِه۪ۜ اَلَا لَهُ الْخَلْقُ وَالْاَمْرُۜ تَبَارَكَ اللّٰهُ رَبُّ الْعَالَم۪ينَ", translation: "Rabb'iniz; gökleri ve yeri altı günde yaratan, sonra arş üstüne isteva eden; geceyi, durmadan takip eden gündüze katan; Güneş'i, Ay'ı ve yıldızları emrine tabi kılan Allah'tır. Dikkat edin! Yaratmak da emretmek de yalnız O'na özgüdür. Alemlerin Rabb'i olan Allah, Şanı Çok Yücedir.", transliteration: "İnne rabbekumullahullezi halakas semavati vel arda fi sitteti eyyamin summesteva alel arşı, yugşil leylen nehare yatlubuhu hasisen veş şemse vel kamere ven nucume musahharatin bi emrih, e la lehul halku vel emr, tebarekallahu rabbulalemin."},
  {id: 1009, sure_no: 55, ayet_no: 55, sure_adi: "Araf", text: "اُدْعُوا رَبَّكُمْ تَضَرُّعاً وَخُفْيَةًۜ اِنَّهُ لَا يُحِبُّ الْمُعْتَد۪ينَۚ", translation: "Rabb'inize \"tederruan\" ve \"hufyeten\" dua edin. Kuşkusuz O, haddi aşanları sevmez.", transliteration: "Ud'u rabbekum tedarruan ve hufyeh, innehu la yuhıbbul mu'tedin."},
  {id: 1010, sure_no: 56, ayet_no: 56, sure_adi: "Araf", text: "وَلَا تُفْسِدُوا فِي الْاَرْضِ بَعْدَ اِصْلَاحِهَا وَادْعُوهُ خَوْفاً وَطَمَعاًۜ اِنَّ رَحْمَتَ اللّٰهِ قَر۪يبٌ مِنَ الْمُحْسِن۪ينَ", translation: "Ve düzeltildikten sonra, yeryüzünde bozgunculuk yapmayın. O'na endişe ve ümit ile dua edin. Kuşkusuz Allah'ın rahmeti muhsin olanlara yakındır.", transliteration: "Ve la tufsidu fil ardı ba'de ıslahıha ved'uhu havfen ve tamaa inne rahmetallahi karibun minel muhsinin."},
  {id: 1011, sure_no: 57, ayet_no: 57, sure_adi: "Araf", text: "وَهُوَ الَّذ۪ي يُرْسِلُ الرِّيَاحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِه۪ۜ حَتّٰٓى اِذَٓا اَقَلَّتْ سَحَاباً ثِقَالاً سُقْنَاهُ لِبَلَدٍ مَيِّتٍ فَاَنْزَلْنَا بِهِ الْمَٓاءَ فَاَخْرَجْنَا بِه۪ مِنْ كُلِّ الثَّمَرَاتِۜ   كَذٰلِكَ نُخْرِجُ الْمَوْتٰى لَعَلَّكُمْ تَذَكَّرُونَ", translation: "Rahmetinin önünde rüzgarları müjdeci olarak gönderen O'dur. Rüzgarlar, ağır bulutları yüklenince, onları ölü bir beldeye sevk ederiz. Böylece onunla indirdiğimiz suyla, orada her türlü ürünü çıkarırız. İşte Biz ölüleri de böyle diriltip çıkarırız. Umulur ki düşünüp öğüt alırsınız.", transliteration: "Ve huvellezi yursilur riyaha buşren beyne yedey rahmetih, hatta iza ekallet sehaben sikale suknahu li beledin meyyitin fe enzelna bihil mae fe ahrecna bihimin kullissemerat, kezalikenuhricul mevta leallekum tezekkerun."},
  {id: 1012, sure_no: 58, ayet_no: 58, sure_adi: "Araf", text: "وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهُ بِاِذْنِ رَبِّه۪ۚ وَالَّذ۪ي خَبُثَ لَا يَخْرُجُ اِلَّا نَكِداًۜ  كَذٰلِكَ نُصَرِّفُ الْاٰيَاتِ لِقَوْمٍ يَشْكُرُونَ۟", translation: "Toprağı iyi olan beldenin bitkisi, Rabb'inin izni ile verimli olur. Toprağı kötü olan beldenin ürünü verimsiz olur. Şükreden bir kavim için ayetleri böyle ayrıntılı olarak açıklıyoruz.", transliteration: "Vel beledut tayyibu yahrucu nebatuhu bi izni rabbih, vellezi habuse la yahrucu illa nekida, kezalike nusarriful ayati li kavmin yeşkurun."},
  {id: 1013, sure_no: 59, ayet_no: 59, sure_adi: "Araf", text: "لَقَدْ اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ فَقَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظ۪يمٍ", translation: "Ant olsun ki Biz, Nuh'u halkına gönderdik. \"Ey halkım! Allah'a kulluk edin. Sizin için O'ndan başka ilah yoktur; kuşkusuz ben, sizin için o büyük günün azabından korkuyorum.\" dedi.", transliteration: "Lekad erselna nuhan ila kavmihi fe kale ya kavmi'budullahe ma lekum min ilahin gayruh, inni ehafu aleykum azabe yevmin azim."},
  {id: 1014, sure_no: 60, ayet_no: 60, sure_adi: "Araf", text: "قَالَ الْمَلَأُ مِنْ قَوْمِه۪ٓ اِنَّا لَنَرٰيكَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Halkının meleleri: \"Biz, seni kesin bir sapkınlık içinde görüyoruz.\" dediler.", transliteration: "Kalel meleu min kavmihi inna le nerake fi dalalin mubin."},
  {id: 1015, sure_no: 61, ayet_no: 61, sure_adi: "Araf", text: "قَالَ يَا قَوْمِ لَيْسَ ب۪ي ضَلَالَةٌ وَلٰكِنّ۪ي رَسُولٌ مِنْ رَبِّ الْعَالَم۪ينَ", translation: "\"Ey halkım, bende bir sapkınlık yok. Ben ancak alemlerin Rabb'inden bir elçiyim.\"", transliteration: "Kale ya kavmi leyse bi dalaletun ve lakinni resulun min rabbil alemin."},
  {id: 1016, sure_no: 62, ayet_no: 62, sure_adi: "Araf", text: "اُبَلِّغُكُمْ رِسَالَاتِ رَبّ۪ي وَاَنْصَحُ لَكُمْ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "\"Rabb'imin gönderdiklerini size tebliğ ediyorum. Ve size öğüt veriyorum. Allah tarafından, sizin bilmediklerinizi biliyorum.\"", transliteration: "Ubelligukum risalati rabbi ve ensahu lekum ve a'lemu minallahi ma la ta'lemun."},
  {id: 1017, sure_no: 63, ayet_no: 63, sure_adi: "Araf", text: "اَوَعَجِبْتُمْ اَنْ جَٓاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلٰى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوا وَلَعَلَّكُمْ تُرْحَمُونَ", translation: "\"Takvalı olmanızı sağlamak ve böylece merhamet olunmanız için; içinizden sizi uyaracak bir adama, Rabb'inizden size bir zikir gelmesine mi şaştınız?\"", transliteration: "E ve acibtum en caekum zikrun min rabbikum ala raculin minkum li yunzirekum ve li tetteku ve leallekum turhamun."},
  {id: 1018, sure_no: 64, ayet_no: 64, sure_adi: "Araf", text: "فَكَذَّبُوهُ فَاَنْجَيْنَاهُ وَالَّذ۪ينَ مَعَهُ فِي الْفُلْكِ وَاَغْرَقْنَا الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۜ اِنَّهُمْ كَانُوا قَوْماً عَم۪ينَ۟", translation: "Derken, halkı onu yalanladı. Biz de onu ve onunla beraber gemide bulunanları kurtardık. Ayetlerimizi yalanlayanları da boğduk. Onlar gerçeği görmeyen bir kavimdi.", transliteration: "Fe kezzebuhu fe enceynahu vellezine meahu fil fulki ve agraknellezine kezzebu bi ayatina, innehum kanu kavmen amin."},
  {id: 1019, sure_no: 65, ayet_no: 65, sure_adi: "Araf", text: "وَاِلٰى عَادٍ اَخَاهُمْ هُوداًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اَفَلَا تَتَّقُونَ", translation: "Ad halkına da kardeşleri Hud'u gönderdik. \"Ey halkım! Allah'a kulluk edin ve sizin için O'ndan başka ilah yoktur. Yine de takva sahibi olmayacak mısınız?\" dedi.", transliteration: "Ve ila adin ehahum huda, kale ya kavmi'budullahe ma lekum min ilahin gayruh, e fe la tettekun."},
  {id: 1020, sure_no: 66, ayet_no: 66, sure_adi: "Araf", text: "قَالَ الْمَلَأُ الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ٓ اِنَّا لَنَرٰيكَ ف۪ي سَفَاهَةٍ وَاِنَّا لَنَظُنُّكَ مِنَ الْكَاذِب۪ينَ", translation: "Halkının Kafir meleleri: \"Gerçekten biz seni beyinsizlerden görüyoruz ve gerçekten seni yalancılardan sanıyoruz.\" dediler.", transliteration: "Kalelmeleullezine keferu min kavmihi inna le nerake fi sefahetin ve inna le nezunnuke minel kazibin."},
  {id: 1021, sure_no: 67, ayet_no: 67, sure_adi: "Araf", text: "قَالَ يَا قَوْمِ لَيْسَ ب۪ي سَفَاهَةٌ وَلٰكِنّ۪ي رَسُولٌ مِنْ رَبِّ الْعَالَم۪ينَ", translation: "\"Ey halkım! Ben beyinsizlerden değilim. Ben ancak Alemlerin Rabb'inden bir elçiyim.\" dedi.", transliteration: "Kale ya kavmi leyse bi sefahetun ve lakinni resulun min rabbil alemin."},
  {id: 1022, sure_no: 68, ayet_no: 68, sure_adi: "Araf", text: "اُبَلِّغُكُمْ رِسَالَاتِ رَبّ۪ي وَاَنَا۬ لَكُمْ نَاصِحٌ اَم۪ينٌ", translation: "\"Ben, size Rabb'imin mesajlarını iletiyorum. Ben, sizin için güvenilir bir öğütçüyüm.\"", transliteration: "Ubelligukum risalati rabbi ve ene lekum nasıhun emin."},
  {id: 1023, sure_no: 69, ayet_no: 69, sure_adi: "Araf", text: "اَوَعَجِبْتُمْ اَنْ جَٓاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلٰى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْۜ  وَاذْكُرُٓوا اِذْ جَعَلَكُمْ خُلَـفَٓاءَ مِنْ بَعْدِ قَوْمِ نُوحٍ وَزَادَكُمْ فِي الْخَلْقِ بَصْۣـطَةًۚ فَاذْكُرُٓوا اٰلَٓاءَ اللّٰهِ لَعَلَّكُمْ تُفْلِحُونَ", translation: "\"Sizi uyarması için, içinizden bir kimse aracılığıyla Rabb'inizden size bir zikir gelmesine hayret mi ettiniz? Nuh toplumundan sonra sizi onların yerine geçirdiğini hatırlayın. Ve yaradılışça sizi onlardan güçlü kıldı. O halde Allah'ın nimetlerini hatırlayın. Umulur ki kurtuluşa erersiniz.\"", transliteration: "E ve acibtum en caekum zikrun min rabbikum ala raculin minkum li yunzirekum, vezkuru iz cealekum hulefae min ba'di kavmi nuhın ve zadekum fil halkı bastaten, fezkuru alaallahi leallekum tuflihun."},
  {id: 1024, sure_no: 70, ayet_no: 70, sure_adi: "Araf", text: "قَالُٓوا اَجِئْتَنَا لِنَعْبُدَ اللّٰهَ وَحْدَهُ وَنَذَرَ مَا كَانَ يَعْبُدُ اٰبَٓاؤُ۬نَاۚ فَأْتِنَا بِمَا تَعِدُنَٓا اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "\"Sen, bize, tek olan Allah'a kulluk etmemiz ve atalarımızın kulluk ettiklerini bırakmamız için mi geldin? Eğer doğru sözlülerdensen, haydi bizi tehdit ettiğin şeyi getir!\" dediler.", transliteration: "Kalu e ci'tena li na'budallahe vahdehu ve nezere ma kane ya'budu abauna, fe'tina bi ma teiduna in kunte mines sadıkin."},
  {id: 1025, sure_no: 71, ayet_no: 71, sure_adi: "Araf", text: "قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِنْ رَبِّكُمْ رِجْسٌ وَغَضَبٌۜ اَتُجَادِلُونَن۪ي ف۪ٓي اَسْمَٓاءٍ سَمَّيْتُمُوهَٓا اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ مَا نَزَّلَ اللّٰهُ بِهَا مِنْ سُلْطَانٍۜ فَانْتَظِرُٓوا اِنّ۪ي مَعَكُمْ مِنَ الْمُنْتَظِر۪ينَ", translation: "\"Rabb'inizin azabı ve öfkesi, hakkınızda kesinleşti. Haklarında Allah'ın hiçbir yetki belgesi indirmediği, sizin ve atalarınızın taktığı isimler hakkında benimle tartışıyor musunuz? Bekleyin öyleyse! Kuşkusuz ben de sizinle beraber bekleyenlerdenim.\" dedi.", transliteration: "Kale kad vakaa aleykum min rabbikum ricsun ve gadabun, e tucadiluneni fi esmain semmeytumuha entum ve abaukum ma nezzelallahu biha min sultanin, fentezıru inni meakum minel muntezırin."},
  {id: 1026, sure_no: 72, ayet_no: 72, sure_adi: "Araf", text: "فَاَنْجَيْنَاهُ وَالَّذ۪ينَ مَعَهُ بِرَحْمَةٍ مِنَّا وَقَطَعْنَا دَابِرَ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَمَا كَانُوا مُؤْمِن۪ينَ۟", translation: "Onu ve beraberinde olanları bizden bir rahmetle kurtardık. Ayetlerimizi yalanlayıp inanmayanların ise kökünü kestik.", transliteration: "Fe enceynahu vellezine meahu bi rahmetin minna ve kata'na dabirellezine kezzebu bi ayatina ve ma kanu mu'minin."},
  {id: 1027, sure_no: 73, ayet_no: 73, sure_adi: "Araf", text: "وَاِلٰى ثَمُودَ اَخَاهُمْ صَـالِـحاًۢ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ قَدْ جَٓاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْۜ هٰذِه۪ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوهَا تَأْكُلْ ف۪ٓي اَرْضِ اللّٰهِ وَلَا تَمَسُّوهَا بِسُٓوءٍ فَيَأْخُذَكُمْ عَذَابٌ اَل۪يمٌ", translation: "Semud halkına da kardeşleri Salih'i gönderdik: \"Ey halkım: Allah'a kulluk edin. Sizin için O'ndan başka ilah yoktur. Rabb'inizden size bir beyyinat gelmiştir. İşte şu Allah'ın dişi devesi, sizin için bir ayettir. Bırakın onu, Allah'ın arzında otlasın. Kötü bir amaçla ona yaklaşmayın. Yoksa sizi yakın bir azab yakalar.\" dedi.", transliteration: "Ve ila semude ehahum salihan kale ya kavmi'budullahe ma lekum min ilahin gayruhu, kad caetkum beyyinetun min rabbikum hazihi nakatullahi lekum ayeten fe zeruha te'kul fi ardıllahi ve la temessuha bi suin fe ye'huzekum azabun elim."},
  {id: 1028, sure_no: 74, ayet_no: 74, sure_adi: "Araf", text: "وَاذْ‌كُرُٓوا اِذْ جَعَلَكُمْ خُلَـفَٓاءَ مِنْ بَعْدِ عَادٍ وَبَوَّاَكُمْ فِي الْاَرْضِ تَتَّخِذُونَ مِنْ سُهُولِهَا قُصُوراً وَتَنْحِتُونَ الْجِبَالَ بُيُوتاًۚ  فَاذْكُـرُٓوا اٰلَٓاءَ اللّٰهِ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ", translation: "\"Hatırlayın! Ad'dan sonra sizi halifeler yapıp yeryüzüne yerleştirdi. O'nun ovalarında köşkler yapıyor, dağlarını yontup evler yapıyorsunuz. Allah'ın nimetlerini düşünün de yeryüzünde bozgunculuk yapıp karışıklık çıkarmayın.\"", transliteration: "Vezkuru iz cealekum hulefae min ba'di adin ve bevveekum fil ardı tettehızune min suhuliha kusuren ve tenhitunel cibale buyuten fezkuru alaallahi ve la ta'sev fil ardı mufsidin."},
  {id: 1029, sure_no: 75, ayet_no: 75, sure_adi: "Araf", text: "قَالَ الْمَلَأُ الَّذ۪ينَ اسْتَكْبَرُوا مِنْ قَوْمِه۪ لِلَّذ۪ينَ اسْتُضْعِفُوا لِمَنْ اٰمَنَ مِنْهُمْ اَتَعْلَمُونَ اَنَّ صَالِحاً مُرْسَلٌ مِنْ رَبِّه۪ۜ قَالُٓوا اِنَّا بِمَٓا اُرْسِلَ بِه۪ مُؤْمِنُونَ", translation: "Halkının büyüklük taslayan meleleri, içlerinden iman eden mustezaf'lara: \"Siz, Salih'in, Rabb'i tarafından gönderilmiş olduğundan emin misiniz?\" dediler. Onlar da: \"Biz, onunla gönderilen her şeye iman edenlerdeniz.\" dediler.", transliteration: "Kalel meleullezinestekberu min kavmihi lillezinestud'ıfu li men amene minhum e ta'lemune enne salihan murselun min rabbihi kalu inna bima ursile bihi mu'minun."},
  {id: 1030, sure_no: 76, ayet_no: 76, sure_adi: "Araf", text: "قَالَ الَّذ۪ينَ اسْتَكْبَرُٓوا اِنَّا بِالَّـذ۪ٓي اٰمَنْتُمْ بِه۪ كَافِرُونَ", translation: "Büyüklük taslayan kimseler de dediler ki: \"Biz de sizin iman ettiğiniz şeyi kesin olarak küfredenleriz.\"", transliteration: "Kalellezinestekberu inna billezi amentum bihi kafirun."},
  {id: 1031, sure_no: 77, ayet_no: 77, sure_adi: "Araf", text: "فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ وَقَالُوا يَا صَالِحُ ائْتِنَا بِمَا تَعِدُنَٓا اِنْ كُنْتَ مِنَ الْمُرْسَل۪ينَ", translation: "Derken dişi deveyi kestiler ve böylece Rabb'inin emrine karşı geldiler ve \"Ey Salih! Sen gerçekten gönderilenlerdensen, haydi bizi tehdit ettiğin şeyleri getir.\" dediler.", transliteration: "Fe akarun nakate ve atev an emri rabbihim ve kalu ya salihu'tina bima teiduna in kunte minel murselin."},
  {id: 1032, sure_no: 78, ayet_no: 78, sure_adi: "Araf", text: "فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوا ف۪ي دَارِهِمْ جَاثِم۪ينَ", translation: "Bunun üzerine şiddetli bir sarsıntı onları yakaladı ve yurtlarında dizüstü çöke kaldılar.", transliteration: "Fe ehazethumur recfetu fe asbahu fi darihim casimin."},
  {id: 1033, sure_no: 79, ayet_no: 79, sure_adi: "Araf", text: "فَتَوَلّٰى عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَةَ رَبّ۪ي وَنَصَحْتُ لَـكُمْ وَلٰكِنْ لَا تُحِبُّونَ النَّاصِح۪ينَ", translation: "O da onlardan yüz çevirip: \"Ey halkım, ben size Rabb'imin mesajını ilettim ve size öğüt verdim, ancak siz öğüt verenleri sevmiyorsunuz.\" dedi.", transliteration: "Fe tevella anhum ve kale ya kavmi lekad eblagtukum risalete rabbi ve nesahtu lekum ve lakin la tuhıbbunen nasıhin."},
  {id: 1034, sure_no: 80, ayet_no: 80, sure_adi: "Araf", text: "وَلُوطاً اِذْ قَالَ لِقَوْمِه۪ٓ اَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِنَ الْعَالَم۪ينَ", translation: "Hani Lut da halkına şöyle demişti: \"Ey halkım! Sizden önce alemlerden hiç kimsenin yapmadığı bir fahişeliği mi yapıyorsunuz?\"", transliteration: "Ve lutan iz kale li kavmihi e te'tunel fahışete ma sebekakum biha min ehadin minel alemin."},
  {id: 1035, sure_no: 81, ayet_no: 81, sure_adi: "Araf", text: "اِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَٓاءِۜ بَلْ اَنْتُمْ قَوْمٌ مُسْرِفُونَ", translation: "\"Kadınları bırakıp şehvetle erkeklere yaklaşıyorsunuz. Hayır! Siz gerçekten müsrif bir halksınız.\"", transliteration: "İnnekum le te'tuner ricale şehveten min dunin nisai, bel entum kavmun musrifun."},
  {id: 1036, sure_no: 82, ayet_no: 82, sure_adi: "Araf", text: "وَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُٓوا اَخْرِجُوهُمْ مِنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَتَطَهَّرُونَ", translation: "Halkının cevabı yalnızca şu oldu: \"Çıkarın onları yurtlarınızdan, zira o insanlar, kendilerini çok temiz görüyorlar.\"", transliteration: "Ve ma kane cevabe kavmihi illa en kalu ahricuhum min karyetikum, innehum unasun yetetahherun."},
  {id: 1037, sure_no: 83, ayet_no: 83, sure_adi: "Araf", text: "فَاَنْجَيْنَاهُ وَاَهْلَهُٓ اِلَّا امْرَاَتَهُۘ  كَانَتْ مِنَ الْغَابِر۪ينَ", translation: "Bunun üzerine onu ve yanında yer alanları kurtardık. Karısı hariç; o, geride kalanlardan oldu.", transliteration: "Fe enceynahu ve ehlehu illemreetehu kanet minel gabirin."},
  {id: 1038, sure_no: 84, ayet_no: 84, sure_adi: "Araf", text: "وَاَمْطَرْنَا عَلَيْهِمْ مَطَراًۜ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِم۪ينَ۟", translation: "Üzerlerine bir yağmur yağdırdık. Bak, mücrimlerin sonu nasıl oldu!", transliteration: "Ve emtarna aleyhim matara, fenzur keyfe kane akıbetul mucrimin."},
  {id: 1039, sure_no: 85, ayet_no: 85, sure_adi: "Araf", text: "وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْباًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ قَدْ جَٓاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ فَاَوْفُوا الْكَيْلَ وَالْم۪يزَانَ وَلَا تَبْخَسُوا النَّاسَ اَشْيَٓاءَهُمْ وَلَا تُفْسِدُوا فِي الْاَرْضِ بَعْدَ اِصْلَاحِهَاۜ ذٰلِكُمْ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ", translation: "Medyen'e de kardeşleri Şuayb'ı. Dedi ki: \"Ey halkım! Allah'a kulluk edin. Sizin için O'ndan başka ilah yoktur. Rabb'inizden size bir beyyinat gelmiştir. Ölçüyü ve tartıyı tam yapın. İnsanlara mallarını eksik vermeyin. Düzeltildikten sonra yeryüzünde bozgunculuk yapmayın; eğer Mü'minler iseniz bu, sizin için hayırlı olandır.\"", transliteration: "Ve ila medyene ehahum şuayba kale ya kavmi'budullahe ma lekum min ilahin gayruhu kad caetkum beyyinetun min rabbikum fe evful keyle vel mizane ve la tebhasun nase eşyaehum ve la tufsidu fil ardı ba'de ıslahıha zalikum hayrun lekum in kuntum mu'minin."},
  {id: 1040, sure_no: 86, ayet_no: 86, sure_adi: "Araf", text: "وَلَا تَقْعُدُوا بِكُلِّ صِرَاطٍ تُوعِدُونَ وَتَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ مَنْ اٰمَنَ بِه۪ وَتَبْغُونَهَا عِوَجاًۚ وَاذْكُرُٓوا اِذْ كُنْتُمْ قَل۪يلاً فَكَثَّرَكُمْۖ وَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِد۪ينَ", translation: "\"İman edenleri tehdit ederek Allah'ın yolundan çevirmek ve o yolu eğri göstermeye çalışmak için her yolun başına oturmayın. Azken sizi nasıl çoğalttığımızı bir düşünün. Bakın! Bozgunculuk yapanların sonu nasıl oldu!\"", transliteration: "Ve la tak'udu bikulli sıratın tu'ıdune ve tasuddune an sebilillahi men amene bihi ve tebguneha ivecen vezkuru iz kuntum kalilen fe kesserekum vanzuru keyfe kane akıbetul mufsidin."},
  {id: 1041, sure_no: 87, ayet_no: 87, sure_adi: "Araf", text: "وَاِنْ كَانَ طَٓائِفَةٌ مِنْكُمْ اٰمَنُوا بِالَّـذ۪ٓي اُرْسِلْتُ بِه۪ وَطَٓائِفَةٌ لَمْ يُؤْمِنُوا فَاصْبِرُوا حَتّٰى يَحْكُمَ اللّٰهُ بَيْنَنَاۚ وَهُوَ خَيْرُ الْحَاكِم۪ينَ", translation: "\"Mademki sizin bir kısmınız benimle gönderilene iman etmiş, bir kısmınız da iman etmemişse; o halde Allah aramızda hükmünü verinceye kadar sabredin. O, hüküm verenlerin en hayırlısıdır.\"", transliteration: "Ve in kane taifetun minkum amenu billezi ursiltu bihi ve taifetun lem yu'minu fasbiru hatta yahkumallahu beynena, ve huve hayrul hakimin."},
  {id: 1042, sure_no: 88, ayet_no: 88, sure_adi: "Araf", text: "قَالَ الْمَلَأُ الَّذ۪ينَ اسْتَكْبَرُوا مِنْ قَوْمِه۪ لَنُخْرِجَنَّكَ يَا شُعَيْبُ وَالَّذ۪ينَ اٰمَنُوا مَعَكَ مِنْ قَرْيَتِنَٓا اَوْ لَتَعُودُنَّ ف۪ي مِلَّتِنَاۜ قَالَ اَوَلَوْ كُنَّا كَارِه۪ينَ", translation: "Halkının büyüklük taslayan meleleri dediler ki: \"Ey Şu'ayb! Seni ve seninle birlikte iman edenleri ya yurdumuzdan çıkaracağız ya da bizim milletimize döneceksiniz.\" O da: \"Kerih görsek de mi?\" dedi.", transliteration: "Kalel meleullezinestekberu min kavmihi le nuhricenneke ya şuaybu vellezine amenu meake min karyetina ev le teudunne fi milletina, kale e ve lev kunna karihin."},
  {id: 1043, sure_no: 89, ayet_no: 89, sure_adi: "Araf", text: "قَدِ افْتَرَيْنَا عَلَى اللّٰهِ كَذِباً اِنْ عُدْنَا ف۪ي مِلَّتِكُمْ بَعْدَ اِذْ نَجّٰينَا اللّٰهُ مِنْهَاۜ وَمَا يَكُونُ لَـنَٓا اَنْ نَعُودَ ف۪يهَٓا اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُ رَبُّنَاۜ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْماًۜ عَلَى اللّٰهِ تَوَكَّلْنَاۜ رَبَّـنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَاَنْتَ خَيْرُ الْفَاتِح۪ينَ", translation: "\"O, bizi ondan kurtardıktan sonra, eğer tekrar sizin milletinize dönersek, Allah'a karşı yalan yere iftira etmiş oluruz. Rabb'imiz Allah dilemedikçe bizim ona dönmemiz olacak şey değildir. Rabb'imizin bilgisi her şeyi kuşatmıştır. Biz, yalnızca Allah'a tevekkül ettik. Ey Rabb'imiz! Bizimle halkımız arasında gerçeği ortaya çıkar. Sen, gerçeği ortaya çıkaranların en hayırlısısın.\"", transliteration: "Kadiftereyna alallahi keziben in udna fi milletikum ba'de iz necceynallahu minha, ve ma yekunu lena en neude fiha illa en yeşaallahu rabbuna, vesia rabbuna kulle şey'in ilmen, alallahi tevekkelna, rabbeneftah beynena ve beyne kavmina bil hakkı ve ente hayrul fatihin."},
  {id: 1044, sure_no: 90, ayet_no: 90, sure_adi: "Araf", text: "وَقَالَ الْمَلَأُ الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ لَئِنِ اتَّبَعْتُمْ شُعَيْباً اِنَّكُمْ اِذاً لَخَاسِرُونَ", translation: "Halkından Kafir meleler: \"Eğer Şu'ayb'e uyarsanız kaybedenlerden olursunuz.\" dediler.", transliteration: "Ve kalel meleullezine keferu min kavmihi le initteba'tum şuayben innekum izen le hasirun."},
  {id: 1045, sure_no: 91, ayet_no: 91, sure_adi: "Araf", text: "فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوا ف۪ي دَارِهِمْ جَاثِم۪ينَۚۛ", translation: "Derken şiddetli bir sarsıntı onları yakaladı ve yurtlarında dizüstü çöke kaldılar.", transliteration: "Fe ehazethumur recfetu fe asbehu fi darihim casimin."},
  {id: 1046, sure_no: 92, ayet_no: 92, sure_adi: "Araf", text: "اَلَّذ۪ينَ كَذَّبُوا شُعَيْباً كَاَنْ لَمْ يَغْنَوْا ف۪يهَاۚۛ اَلَّذ۪ينَ كَذَّبُوا شُعَيْباً كَانُوا هُمُ الْخَاسِر۪ينَ", translation: "Şu'ayb'ı yalanlayanlar, sanki orada hiç yaşamamışlar gibi yok oldular. Asıl kaybedenler Şu'ayb'ı yalanlayanlar oldu.", transliteration: "Ellezine kezzebu şuayben ke en lem yagnev fiha, ellezine kezzebu şuayben kanu humul hasirin."},
  {id: 1047, sure_no: 93, ayet_no: 93, sure_adi: "Araf", text: "فَتَوَلّٰى عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَاتِ رَبّ۪ي وَنَصَحْتُ لَكُمْۚ فَكَيْفَ اٰسٰى عَلٰى قَوْمٍ كَافِر۪ينَ۟", translation: "Onlardan yüz çevirdi ve \"Ey halkım.\" dedi: \"Ben, size Rabb'imin mesajını tebliğ ettim ve size öğüt verdim. Artık kafir bir topluma nasıl olur da üzülebilirim?\"", transliteration: "Fe tevella anhum ve kale ya kavmi lekad eblagtukum risalati rabbi ve nesahtu lekum, fe keyfe asa ala kavmin kafirin."},
  {id: 1048, sure_no: 94, ayet_no: 94, sure_adi: "Araf", text: "وَمَٓا اَرْسَلْنَا ف۪ي قَرْيَةٍ مِنْ نَبِيٍّ اِلَّٓا اَخَذْنَٓا اَهْلَهَا بِالْبَأْسَٓاءِ وَالضَّرَّٓاءِ لَعَلَّهُمْ يَضَّرَّعُونَ", translation: "Hangi beldeye bir Nebi gönderdiysek, o beldenin halkını darra'i etsin diye sıkıntı ve zorlukla yakaladık.", transliteration: "Ve ma erselna fi karyetin min nebiyyin illa ehazna ehleha bil be'sai ved darrai leallehum yaddarraun."},
  {id: 1049, sure_no: 95, ayet_no: 95, sure_adi: "Araf", text: "ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتّٰى عَفَوْا وَقَالُوا قَدْ مَسَّ اٰبَٓاءَنَا الضَّرَّٓاءُ وَالسَّرَّٓاءُ فَاَخَذْنَاهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ", translation: "Sonra kötülüğü iyiliğe çevirdik. Nihayet imkanları artınca: \"Atalarımızın da sıkıntılı ve bolluk günleri olmuştu.\" dediler. Bu yüzden, Biz de onları ansızın yakalayıverdik.", transliteration: "Summe beddelna mekanes seyyietil hasenete hatta afev ve kalu kad messe abaenad darrau ves serrau fe ehaznahum bagteten ve hum la yeş'urun."},
  {id: 1050, sure_no: 96, ayet_no: 96, sure_adi: "Araf", text: "وَلَوْ اَنَّ اَهْلَ الْقُرٰٓى اٰمَنُوا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكَاتٍ مِنَ السَّمَٓاءِ وَالْاَرْضِ وَلٰكِنْ كَذَّبُوا فَاَخَذْنَاهُمْ بِمَا كَانُوا يَكْسِبُونَ", translation: "Eğer beldelerin halkı iman edip, takva sahibi olsalardı, muhakkak üzerlerine göğün ve yerin bereketini açardık. Ancak onlar yalanladılar, Biz de yaptıklarına karşılık onları kıskıvrak yakaladık.", transliteration: "Ve lev enne ehlel kura amenu vettekav le fetahna aleyhim berekatin mines semai vel ardı ve lakin kezzebu fe ehaznahum bima kanu yeksibun."},
  {id: 1051, sure_no: 97, ayet_no: 97, sure_adi: "Araf", text: "اَفَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَأْتِيَهُمْ بَأْسُنَا بَيَاتاً وَهُمْ نَٓائِمُونَۜ", translation: "O beldelerin halkı, geceleyin uyurlarken kendilerine azabımızın gelmeyeceğinden emin mi oldular?", transliteration: "E fe emine ehlul kura en ye'tiyehum be'suna beyaten ve hum naimun."},
  {id: 1052, sure_no: 98, ayet_no: 98, sure_adi: "Araf", text: "اَوَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَأْتِيَهُمْ بَأْسُنَا ضُحًى وَهُمْ يَلْعَبُونَ", translation: "Ya da o beldelerin halkı, gündüzün serin vaktinde dünya işleriyle oyalanırlarken azabımızın onlara gelmeyeceğinden emin mi oldular?", transliteration: "E ve emine ehlul kura en ye'tiyehum be'suna duhan ve hum yel'abun ."},
  {id: 1053, sure_no: 99, ayet_no: 99, sure_adi: "Araf", text: "اَفَاَمِنُوا مَكْرَ اللّٰهِۚ فَلَا يَأْمَنُ مَكْرَ اللّٰهِ اِلَّا الْقَوْمُ الْخَاسِرُونَ۟", translation: "Allah'ın planından emin mi oldular? Kaybedenlerden başkası Allah'ın planından emin olamaz.", transliteration: "E fe eminu mekrallahi, fe la ye'menu mekrallahi illel kavmul hasirun."},
  {id: 1054, sure_no: 100, ayet_no: 100, sure_adi: "Araf", text: "اَوَلَمْ يَهْدِ لِلَّذ۪ينَ يَرِثُونَ الْاَرْضَ مِنْ بَعْدِ اَهْلِهَٓا اَنْ لَوْ نَشَٓاءُ اَصَبْنَاهُمْ بِذُنُوبِهِمْۚ وَنَطْبَعُ عَلٰى قُلُوبِهِمْ فَهُمْ لَا يَسْمَعُونَ", translation: "Önceki halklardan sonra yeryüzüne mirasçı olanların, doğru yolu bulmaları gerekmez miydi? Eğer biz dileseydik onları da suçlarından dolayı cezalandırırdık. Kalplerini mühürlerdik de duymaz olurlardı.", transliteration: "E ve lem yehdi lillezine yerisunel arda min ba'di ehliha en lev neşau esabnahum bi zunubihim, ve natbeu ala kulubihim fe hum la yesme'un."},
  {id: 1055, sure_no: 101, ayet_no: 101, sure_adi: "Araf", text: "تِلْكَ الْقُرٰى نَقُصُّ عَلَيْكَ مِنْ اَنْـبَٓائِهَاۚ وَلَقَدْ جَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِۚ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا مِنْ قَبْلُۜ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوبِ الْكَافِر۪ينَ", translation: "İşte o beldeler ki sana bazı haberlerini anlatıyoruz. Resulleri onlara beyyinat getirmişlerdi. Ancak onlar, daha önce yalanlamış oldukları şeye inanmak istemediler. Allah Kafirlerin kalplerini işte böyle mühürler.", transliteration: "Tilkel kura nakussu aleyke min enbaiha ve lekad caethum rusuluhum bil beyyinati fe ma kanu liyu'minu bi ma kezzebu min kablu kezalike yatbaullahu ala kulubil kafirin ."},
  {id: 1056, sure_no: 102, ayet_no: 102, sure_adi: "Araf", text: "وَمَا وَجَدْنَا لِاَكْثَرِهِمْ مِنْ عَهْدٍۚ وَاِنْ وَجَدْنَٓا اَكْثَرَهُمْ لَفَاسِق۪ينَ", translation: "Onların çoğunda, sözlerine bağlılık bulmadık. Ama onların çoğunu fasık olarak bulduk.", transliteration: "Ve ma vecedna li ekserihim min ahdin, ve in vecedna ekserehum le fasikin."},
  {id: 1057, sure_no: 103, ayet_no: 103, sure_adi: "Araf", text: "ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسٰى بِاٰيَاتِنَٓا اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ فَظَلَمُوا بِهَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِد۪ينَ", translation: "Sonra onların ardından Musa'yı ayetlerimizle Firavun'a ve onun melelerine gönderdik. Onlar, ona zulmettiler. Bak bozguncuların sonu nasıl oldu!", transliteration: "Summe beasna min ba'dihim musa bi ayatina ila fir'avne ve melaihi fe zalemu biha, fanzur keyfe kane akıbetul mufsidin."},
  {id: 1058, sure_no: 104, ayet_no: 104, sure_adi: "Araf", text: "وَقَالَ مُوسٰى يَا فِرْعَوْنُ اِنّ۪ي رَسُولٌ مِنْ رَبِّ الْعَالَم۪ينَۚ", translation: "Musa dedi ki: \"Ey Firavun! Ben gerçekten Alemlerin Rabb'i tarafından gönderilmiş bir Resul'üm.\"", transliteration: "Ve kale musa ya fir'avnu inni resulun min rabbil alemin."},
  {id: 1059, sure_no: 105, ayet_no: 105, sure_adi: "Araf", text: "حَق۪يقٌ عَلٰٓى اَنْ لَٓا اَقُولَ عَلَى اللّٰهِ اِلَّا الْحَقَّۜ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِنْ رَبِّكُمْ فَاَرْسِلْ مَعِيَ بَن۪ٓي اِسْرَٓائ۪لَۜ", translation: "\"Allah adına, hakikatten başkasını söylememek benim üzerime borçtur. Size, Rabb'inizden açık beyyinelerle geldim. Öyleyse İsrailoğullarını benimle gönder.\"", transliteration: "Hakikun ala en la ekule alallahi illel hakk, kad ci'tukum bi beyyinetin min rabbikum fe ersil maiye beni israil."},
  {id: 1060, sure_no: 106, ayet_no: 106, sure_adi: "Araf", text: "قَالَ اِنْ كُنْتَ جِئْتَ بِاٰيَةٍ فَأْتِ بِهَٓا اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "Firavun: \"Eğer gerçekten bir ayet getirdiysen ve doğru söyleyenlerdensen onu göster bakalım.\" dedi.", transliteration: "Kale in kunte ci'te bi ayetin fe'ti biha in kunte mines sadikin."},
  {id: 1061, sure_no: 107, ayet_no: 107, sure_adi: "Araf", text: "فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُب۪ينٌۚ", translation: "Bunun üzerine, Musa asasını yere bıraktı, asa sahici büyük bir yılan oldu.", transliteration: "Fe elka asahu fe iza hiye su'banun mubin."},
  {id: 1062, sure_no: 108, ayet_no: 108, sure_adi: "Araf", text: "وَنَزَعَ يَدَهُ فَاِذَا هِيَ بَيْضَٓاءُ لِلنَّاظِر۪ينَ۟", translation: "Ve elini çıkardığı zaman, eli bakanlar için bembeyaz parlayıverdi.", transliteration: "Ve neze'a yedehu fe iza hiye beydau lin nazırin."},
  {id: 1063, sure_no: 109, ayet_no: 109, sure_adi: "Araf", text: "قَالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ اِنَّ هٰذَا لَسَاحِرٌ عَل۪يمٌۙ", translation: "Firavun halkının meleleri: \"Bu gerçekten bilgin bir büyücüdür.\" dediler.", transliteration: "Kalel meleu min kavmi fir'avne inne haza le sahırun alim."},
  {id: 1064, sure_no: 110, ayet_no: 110, sure_adi: "Araf", text: "يُر۪يدُ اَنْ يُخْرِجَكُمْ مِنْ اَرْضِكُمْۚ فَمَاذَا تَأْمُرُونَ", translation: "\"Sizi yurdunuzdan çıkarmak istiyor. Ne buyuruyorsunuz?\"", transliteration: "Yuridu en yuhricekum min ardıkum, fe maza te'murun."},
  {id: 1065, sure_no: 111, ayet_no: 111, sure_adi: "Araf", text: "قَالُٓوا اَرْجِهْ وَاَخَاهُ وَاَرْسِلْ فِي الْمَدَٓائِنِ حَاشِر۪ينَۙ", translation: "\"Onu ve kardeşini alıkoy. Şehirlere de toplayıcılar gönder.\" dediler.", transliteration: "Kalu ercih ve ehahu ve ersil fil medaini haşirin."},
  {id: 1066, sure_no: 112, ayet_no: 112, sure_adi: "Araf", text: "يَأْتُوكَ بِكُلِّ سَاحِرٍ عَل۪يمٍ", translation: "\"Bütün bilgin sihirbazları sana getirsinler.\"", transliteration: "Ye'tuke bi kulli sahırin alim."},
  {id: 1067, sure_no: 113, ayet_no: 113, sure_adi: "Araf", text: "وَجَٓاءَ السَّحَرَةُ فِرْعَوْنَ قَالُٓوا اِنَّ لَنَا لَاَجْراً اِنْ كُنَّا نَحْنُ الْغَالِب۪ينَ", translation: "Sihirbazlar, Firavun'a geldiler: \"Eğer galip gelirsek bize bir ödül var değil mi?\" dediler.", transliteration: "Ve caes seharatu fir'avne kalu inne lena le ecren in kunna nahnul galibin."},
  {id: 1068, sure_no: 114, ayet_no: 114, sure_adi: "Araf", text: "قَالَ نَعَمْ وَاِنَّكُمْ لَمِنَ الْمُقَرَّب۪ينَ", translation: "Evet, \"Sizler yakınlaştırılanlardan olacaksınız.\" dedi.", transliteration: "Kale ne'am ve innekum le minel mukarrebin."},
  {id: 1069, sure_no: 115, ayet_no: 115, sure_adi: "Araf", text: "قَالُوا يَا مُوسٰٓى اِمَّٓا اَنْ تُلْقِيَ وَاِمَّٓا اَنْ نَكُونَ نَحْنُ الْمُلْق۪ينَ", translation: "\"Ey Musa! Önce sen mi atacaksın, yoksa atanlar biz mi olalım?\" dediler.", transliteration: "Kalu ya musa imma en tulkiye ve imma en nekune nahnul mulkin."},
  {id: 1070, sure_no: 116, ayet_no: 116, sure_adi: "Araf", text: "قَالَ اَلْقُواۚ فَلَمَّٓا اَلْقَوْا سَحَرُٓوا اَعْيُنَ النَّاسِ وَاسْتَرْهَبُوهُمْ وَجَٓاؤُ۫ بِسِحْرٍ عَظ۪يمٍ", translation: "\"Siz atın\" dedi. Attıkları zaman, büyük bir büyü yaparak insanların gözlerini büyüleyip, onlara korku verdiler.", transliteration: "Kale elku fe lemma elkav seharu a'yunen nasi vesterhebuhum ve cau bi sihrin azim."},
  {id: 1071, sure_no: 117, ayet_no: 117, sure_adi: "Araf", text: "وَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنْ اَلْقِ عَصَاكَۚ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَۚ", translation: "Biz de Musa'ya: \"Asanı at.\" diye vahyettik. O, onların uydurdukları şeyleri yutuverdi.", transliteration: "Ve evhayna ila musa en elkı asake, fe iza hiye telkafu ma ye'fikun."},
  {id: 1072, sure_no: 118, ayet_no: 118, sure_adi: "Araf", text: "فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوا يَعْمَلُونَۚ", translation: "Böylece, hakikat ortaya çıktı ve onların bütün yaptıkları batıl oldu.", transliteration: "Fe vakaal hakku ve batale ma kanu ya'melun."},
  {id: 1073, sure_no: 119, ayet_no: 119, sure_adi: "Araf", text: "فَغُلِبُوا هُنَالِكَ وَانْقَلَبُوا صَاغِر۪ينَۚ", translation: "Orada yenik düştüler. Küçük düşüp, iddialarından vazgeçtiler.", transliteration: "Fe gulibu hunalike venkalebu sagırin."},
  {id: 1074, sure_no: 120, ayet_no: 120, sure_adi: "Araf", text: "وَاُلْقِيَ السَّحَرَةُ سَاجِد۪ينَۚ", translation: "Ve sihirbazlar secdeye kapandılar.", transliteration: "Ve ulkıyes seharatu sacidin."},
  {id: 1075, sure_no: 121, ayet_no: 121, sure_adi: "Araf", text: "قَالُٓوا اٰمَنَّا بِرَبِّ الْعَالَم۪ينَۙ", translation: "\"Alemlerin Rabb'ine iman ettik.\" dediler.", transliteration: "Kalu amenna bi rabbil alemin."},
  {id: 1076, sure_no: 122, ayet_no: 122, sure_adi: "Araf", text: "رَبِّ مُوسٰى وَهٰرُونَ", translation: "\"Musa ve Harun'un Rabb'ine.\"", transliteration: "Rabbi musa ve harun."},
  {id: 1077, sure_no: 123, ayet_no: 123, sure_adi: "Araf", text: "قَالَ فِرْعَوْنُ اٰمَنْتُمْ بِه۪ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّ هٰذَا لَمَكْرٌ مَكَرْتُمُوهُ فِي الْمَد۪ينَةِ لِتُخْرِجُوا مِنْهَٓا اَهْلَهَاۚ فَسَوْفَ تَعْلَمُونَ", translation: "Firavun: \"Ben size izin vermeden mi ona inandınız?\" dedi. \"Doğrusu bu, halkı şehirden çıkarmak için kurduğunuz bir tuzaktır. Ama yakında göreceksiniz!\"", transliteration: "Kale fir'avnu amentum bihi kable en azene lekum, inne haza le mekrun mekertumuhu fil medineti li tuhricu minha ehleha, fe sevfe ta'lemun."},
  {id: 1078, sure_no: 124, ayet_no: 124, sure_adi: "Araf", text: "لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِنْ خِلَافٍ ثُمَّ لَاُصَلِّبَنَّكُمْ اَجْمَع۪ينَ", translation: "\"Mutlaka ellerinizi ve ayaklarınızı çaprazlama keseceğim ve sonra da hepinizi asacağım.\"", transliteration: "Le ukattıanne eydiyekum ve erculekum min hilafin summe le usallibennekum ecmain."},
  {id: 1079, sure_no: 125, ayet_no: 125, sure_adi: "Araf", text: "قَالُٓوا اِنَّٓا اِلٰى رَبِّنَا مُنْقَلِبُونَۚ", translation: "\"Biz zaten Rabb'imize döneceğiz.\" dediler.", transliteration: "Kalu inna ila rabbina munkalibun."},
  {id: 1080, sure_no: 126, ayet_no: 126, sure_adi: "Araf", text: "وَمَا تَنْقِمُ مِنَّٓا اِلَّٓا اَنْ اٰمَنَّا بِاٰيَاتِ رَبِّنَا لَمَّا جَٓاءَتْنَاۜ رَبَّـنَٓا اَفْرِغْ عَلَيْنَا صَبْراً وَتَوَفَّـنَا مُسْلِم۪ينَ۟", translation: "\"Rabb'imizin bize gelen ayetlerine iman etmemizden dolayı, bizden intikam almak istiyorsun. Ey Rabbimiz! Bizi sabırlı kıl ve canımızı teslim olanlar olarak al.\"", transliteration: "Ve ma tenkımu minna illa en amenna bi ayati rabbina lemma caetna, rabbena efrıg aleyna sabren ve teveffena muslimin."},
  {id: 1081, sure_no: 127, ayet_no: 127, sure_adi: "Araf", text: "وَقَالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ اَتَذَرُ مُوسٰى وَقَوْمَهُ لِيُفْسِدُوا فِي الْاَرْضِ وَيَذَرَكَ وَاٰلِهَتَكَۜ قَالَ سَنُقَتِّلُ اَبْنَٓاءَهُمْ وَنَسْتَحْـي۪ نِسَٓاءَهُمْۚ وَاِنَّا فَوْقَهُمْ قَاهِرُونَ", translation: "Firavun halkının meleleri: \"Musa'yı ve yanında yer alanları, yeryüzünde bozgunculuk yapsınlar, seni ve ilahlarını terk etsinler diye mi bırakacaksın?\" dediler. O da: \"Oğullarını öldürüp, kadınlarını sağ bırakacağız. Kuşkusuz, biz onların üzerinde kahredicileriz.\" dedi.", transliteration: "Ve kalel meleu min kavmi fir'avne e tezeru musa ve kavmehu li yufsidu fìl ardı ve yezereke ve aliheteke, kale senukattilu ebnaehum ve nestahyi nisaehum ve inna fevkahum kahirun."},
  {id: 1082, sure_no: 128, ayet_no: 128, sure_adi: "Araf", text: "قَالَ مُوسٰى لِقَوْمِهِ اسْتَع۪ينُوا بِاللّٰهِ وَاصْبِرُواۚ اِنَّ الْاَرْضَ لِلّٰهِۚ يُورِثُهَا مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَالْعَاقِبَةُ لِلْمُتَّق۪ينَ", translation: "Musa, halkına: \"Allah'tan yardım dileyin ve sabredin. Kuşkusuz yeryüzü Allah'ındır. Dilediği kulunu ona mirasçı kılar. Mutlu son takva sahibi olanlar içindir.\" dedi.", transliteration: "Kale musa li kavmihisteinu billahi vasbiru, innel arda lillahi yurisuha men yeşau min ibadih, vel akıbetu lil muttekin."},
  {id: 1083, sure_no: 129, ayet_no: 129, sure_adi: "Araf", text: "قَالُٓوا اُو۫ذ۪ينَا مِنْ قَبْلِ اَنْ تَأْتِيَنَا وَمِنْ بَعْدِ مَا جِئْتَنَاۜ قَالَ عَسٰى رَبُّكُمْ اَنْ يُهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِي الْاَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُونَ۟", translation: "\"Sen bize gelmeden önce de, bize geldikten sonra da bize eziyet edildi.\" dediler. O da: \"Umulur ki Rabb'iniz düşmanlarınızı yok eder ve sizi yeryüzüne halifeler kılar da nasıl davranacağınıza bakar.\" dedi.", transliteration: "Kalu uzina min kabli en te'tiyena ve min ba'di ma ci'tena, kale asa rabbukum en yuhlike aduvvekum ve yestahlifekum fil ardı fe yanzure keyfe ta'melun."},
  {id: 1084, sure_no: 130, ayet_no: 130, sure_adi: "Araf", text: "وَلَقَدْ اَخَذْنَٓا اٰلَ فِرْعَوْنَ بِالسِّن۪ينَ وَنَقْصٍ مِنَ الثَّمَرَاتِ لَعَلَّهُمْ يَذَّكَّرُونَ", translation: "Ant olsun ki belki ders alırlar diye, Firavuncuları yıllarca kuraklık ve ürün kıtlığıyla cezalandırdık.", transliteration: "Ve lekad ehazna ale fir'avne bis sinine ve naksın mines semerati leallehum yezzekkerun."},
  {id: 1085, sure_no: 131, ayet_no: 131, sure_adi: "Araf", text: "فَاِذَا جَٓاءَتْهُمُ الْحَسَنَةُ قَالُوا لَنَا هٰذِه۪ۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَطَّيَّرُوا بِمُوسٰى وَمَنْ مَعَهُۜ اَلَٓا اِنَّمَا طَٓائِرُهُمْ عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "Onlara bir iyilik geldiği zaman, \"Bu bizdendir.\" derler. Bir kötülük isabet ettiği zaman da Musa ve onunla birlikte olanları uğursuz sayarlardı. İyi bilin ki onların uğursuzluğu ancak Allah katındadır. Ancak çokları bunu bilmez.", transliteration: "Fe iza caethumul hasenetu kalu lena hazih, ve in tusibhum seyyietun yettayyeru bi musa ve men meah, e la innema tairuhum indallahi ve lakinne ekserehum la ya'lemun."},
  {id: 1086, sure_no: 132, ayet_no: 132, sure_adi: "Araf", text: "وَقَالُوا مَهْمَا تَأْتِنَا بِه۪ مِنْ اٰيَةٍ لِتَسْحَرَنَا بِهَاۙ فَمَا نَحْنُ لَكَ بِمُؤْمِن۪ينَ", translation: "\"Bizi büyülemek için ne kadar ayet getirirsen getir, biz sana asla inanacak değiliz.\" dediler.", transliteration: "Ve kalu mehma te'tina bihi min ayetin li tesharena biha fe ma nahnu leke bi mu'minin."},
  {id: 1087, sure_no: 133, ayet_no: 133, sure_adi: "Araf", text: "فَاَرْسَلْنَا عَلَيْهِمُ الطُّوفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ اٰيَاتٍ مُفَصَّلَاتٍ فَاسْتَكْبَرُوا وَكَانُوا قَوْماً مُجْرِم۪ينَ", translation: "Bunun üzerine, Biz de ayrı ayrı ayetler olarak onlara tufan, çekirge, haşarat, kurbağa ve kan gönderdik. Yine de büyüklük taslayıp mücrim bir toplum olmaya devam ettiler.", transliteration: "Fe erselna aleyhimut tufane vel cerade vel kummele ved dafadia ved deme ayatin mufassalatin festekberu ve kanu kavmen mucrimin."},
  {id: 1088, sure_no: 134, ayet_no: 134, sure_adi: "Araf", text: "وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوا يَا مُوسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ لَئِنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَن۪ٓي اِسْرَٓائ۪لَۚ", translation: "Başlarına azap gelince: \"Ey Musa! Sana verilen söze dayanarak Rabb'ine dua et, eğer bizden bu azabı uzaklaştırırsan sana inanacağız ve İsrailoğulları'nın seninle birlikte gitmesine izin vereceğiz.\" dediler.", transliteration: "Ve lemma vakaa aleyhimur riczu kalu ya mused'u lena rabbeke bi ma ahide indek, le in keşefte anner ricze le nu'minenne leke ve le nursilenne meake beni israil."},
  {id: 1089, sure_no: 135, ayet_no: 135, sure_adi: "Araf", text: "فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ اِلٰٓى اَجَلٍ هُمْ بَالِغُوهُ اِذَا هُمْ يَنْكُثُونَ", translation: "Biz onlardan geçirecekleri bir süreye kadar azabı kaldırınca da hemen sözlerinden dönüverdiler.", transliteration: "Fe lemma keşefna anhumur ricze ila ecelin hum baliguhu iza hum yenkusun."},
  {id: 1090, sure_no: 136, ayet_no: 136, sure_adi: "Araf", text: "فَانْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنَاهُمْ فِي الْيَمِّ بِاَنَّهُمْ كَذَّبُوا بِاٰيَاتِنَا وَكَانُوا عَنْهَا غَافِل۪ينَ", translation: "Bu yüzden, Biz de onlara hak ettikleri cezayı verdik, onları denizde boğduk. Çünkü onlar ayetlerimizi yalanladılar. Onları umursamaz olmuşlardı.", transliteration: "Fentekamna minhum fe agraknahum fil yemmi biennehum kezzebu bi ayatina ve kanu anha gafilin."},
  {id: 1091, sure_no: 137, ayet_no: 137, sure_adi: "Araf", text: "وَاَوْرَثْنَا الْقَوْمَ الَّذ۪ينَ كَانُوا يُسْتَضْعَفُونَ مَشَارِقَ الْاَرْضِ وَمَغَارِبَهَا الَّت۪ي بَارَكْنَا ف۪يهَاۜ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنٰى عَلٰى بَن۪ٓي اِسْرَٓائ۪لَ بِمَا صَبَرُواۜ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهُ وَمَا كَانُوا يَعْرِشُونَ", translation: "Mus'tezaf olan halkı da bereketlendirdiğimiz arzın doğusuna ve batısına mirasçı kıldık. Sabretmelerine karşılık Rabb'inin İsrailoğullarına takdir ettiği hüküm gerçekleşti. Firavun ve halkının yapıp yükselttikleri yapıları harap ettik.", transliteration: "Ve evresnel kavmellezine kanu yustad'afune meşarikal ardı ve megaribehelleti barekna fiha, ve temmet kelimetu rabbikel husna ala beni israile bi ma saberu, ve demmerna ma kane yasnau fir'avnu ve kavmuhu ve ma kanu ya'rişun."},
  {id: 1092, sure_no: 138, ayet_no: 138, sure_adi: "Araf", text: "وَجَاوَزْنَا بِبَن۪ٓي اِسْرَٓائ۪لَ الْبَحْرَ فَاَتَوْا عَلٰى قَوْمٍ يَعْكُفُونَ عَلٰٓى اَصْنَامٍ لَهُمْۚ قَالُوا يَا مُوسَى اجْعَلْ لَـنَٓا اِلٰهاً كَمَا لَهُمْ اٰلِهَةٌۜ قَالَ اِنَّكُمْ قَوْمٌ تَجْهَلُونَ", translation: "İsrailoğullarını denizden geçirdik. Kendilerine özgü putlara tapan bir halka rastladılar. Ey Musa! \"Bize de bunların ilahları gibi bir ilah yap.\" dediler. \"Siz gerçekten cahil bir kavimsiniz.\" dedi.", transliteration: "Ve cavezna bi beni israilel bahre fe etev ala kavmin ya'kufune ala asnamin lehum, kalu ya musac'al lena ilahen ke ma lehum alihetun, kale innekum kavmun techelun."},
  {id: 1093, sure_no: 139, ayet_no: 139, sure_adi: "Araf", text: "اِنَّ هٰٓؤُ۬لَٓاءِ مُتَبَّرٌ مَا هُمْ ف۪يهِ وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ", translation: "\"Bunların içinde bulundukları yok olacaktır ve yaptıkları şey de batıldır.\"", transliteration: "İnne haulai mutebberun ma hum fihi ve batılun ma kanu ya'melun."},
  {id: 1094, sure_no: 140, ayet_no: 140, sure_adi: "Araf", text: "قَالَ اَغَيْرَ اللّٰهِ اَبْغ۪يكُمْ اِلٰهاً وَهُوَ فَضَّلَكُمْ عَلَى الْعَالَم۪ينَ", translation: "\"Sizi alemlere üstün kıldığı halde, size Allah'tan başka bir ilah mı arayayım?\" dedi.", transliteration: "Kale e gayrallahi ebgikum ilahen ve huve faddalekum alel alemin."},
  {id: 1095, sure_no: 141, ayet_no: 141, sure_adi: "Araf", text: "وَاِذْ اَنْجَيْنَاكُمْ مِنْ اٰلِ فِرْعَوْنَ يَسُومُونَكُمْ سُٓوءَ الْعَذَابِۚ يُقَتِّلُونَ اَبْنَٓاءَكُمْ وَيَسْتَحْيُونَ نِسَٓاءَكُمْۜ وَف۪ي ذٰلِكُمْ بَلَٓاءٌ مِنْ رَبِّكُمْ عَظ۪يمٌ۟", translation: "Hani Biz, size azapların en kötüsünü yapan; oğullarınızı öldürüp, kadınlarınızı sağ bırakan Firavun toplumundan sizi kurtarmıştık. Bunda, sizin için Rabb'inizden büyük bir sınav vardı.\"", transliteration: "Ve iz enceynakum min ali fir'avne yesumunekum suel azab, yukattilune ebnaekum ve yestahyune nisaekum ve fi zalikum belaun min rabbikum azim."},
  {id: 1096, sure_no: 142, ayet_no: 142, sure_adi: "Araf", text: "وَوٰعَدْنَا مُوسٰى ثَلٰث۪ينَ لَيْلَةً وَاَتْمَمْنَاهَا بِعَشْرٍ فَتَمَّ م۪يقَاتُ رَبِّه۪ٓ اَرْبَع۪ينَ لَيْلَةًۚ وَقَالَ مُوسٰى لِاَخ۪يهِ هٰرُونَ اخْلُفْن۪ي ف۪ي قَوْم۪ي وَاَصْلِحْ وَلَا تَتَّبِـعْ سَب۪يلَ الْمُفْسِد۪ينَ", translation: "Musa'ya, otuz gece süre verdik, sonra buna on gece daha ekledik. Böylece Rabb'inin belirlediği vakit kırk geceye tamamlandı. Musa, kardeşi Harun'a, \"Benim halkım içinde benim yerime geç, onları ıslah et ve bozguncuların yoluna uyma!\" dedi.", transliteration: "Ve vaadna musa selasine leyleten ve etmemnaha bi aşrin fe temme mikatu rabbihi erbaine leyleh, ve kale musa li ahihi harunahlufni fi kavmi ve aslıh ve la tettebi' sebilel mufsidin."},
  {id: 1097, sure_no: 143, ayet_no: 143, sure_adi: "Araf", text: "وَلَمَّا جَٓاءَ مُوسٰى لِم۪يقَاتِنَا وَكَلَّمَهُ رَبُّهُۙ قَالَ رَبِّ اَرِن۪ٓي اَنْظُرْ اِلَيْكَۜ قَالَ لَنْ تَرٰين۪ي وَلٰكِنِ انْظُرْ اِلَى الْجَبَلِ فَاِنِ اسْتَقَرَّ مَكَانَهُ فَسَوْفَ تَرٰين۪يۚ فَلَمَّا تَجَلّٰى رَبُّهُ لِلْجَبَلِ جَعَلَهُ دَكاًّ وَخَرَّ مُوسٰى صَعِقاًۚ فَلَمَّٓا اَفَاقَ قَالَ سُبْحَانَكَ تُبْتُ اِلَيْكَ وَاَنَا۬ اَوَّلُ الْمُؤْمِن۪ينَ", translation: "Ve Musa, belirlediğimiz yere gelip de Rabb'i onunla konuşunca: \"Bana görün de Sana bakayım!\" dedi. \"Sen Beni göremezsin, fakat şu dağa bak, eğer o yerinde durursa sen de Beni göreceksin.\" buyurdu. Rabb'i dağa tecelli edince onu darmadağın etti ve Musa baygın düştü. Kendine gelince: \"Sen münezzehsin. Tevbe ettim Sana. Ben Mü'minlerin ilkiyim.\" dedi.", transliteration: "Ve lemma cae musa li mikatina ve kellemehu rabbuhu kale rabbi erini enzur ileyk, kale len terani ve lakininzur ilel cebeli fe inistekarre mekanehu fe sevfe terani fe lemma tecella rabbuhu lil cebeli cealehu dekkan ve harra musa saıkan, fe lemma efaka kale subhaneke tubtu ileyke ve ene evvelul mu'minin."},
  {id: 1098, sure_no: 144, ayet_no: 144, sure_adi: "Araf", text: "قَالَ يَا مُوسٰٓى اِنِّي اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسَالَات۪ي وَبِكَلَام۪يۘ فَخُذْ مَٓا اٰتَيْتُكَ وَكُنْ مِنَ الشَّاكِر۪ينَ", translation: "\"Ey Musa! Gönderdiklerimle ve Kelamımla seni insanlar arasında üstün kıldım. Sana verdiğimi al ve şükredenlerden ol!\" dedi.", transliteration: "Kale ya musa innistafeytuke alen nasi bi risalati ve bi kelami fe huz ma ateytuke ve kun mineş şakirin."},
  {id: 1099, sure_no: 145, ayet_no: 145, sure_adi: "Araf", text: "وَكَتَبْنَا لَهُ فِي الْاَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَوْعِظَةً وَتَفْص۪يلاً لِكُلِّ شَيْءٍۚ فَخُذْهَا بِقُوَّةٍ وَأْمُرْ قَوْمَكَ يَأْخُذُوا بِاَحْسَنِهَاۜ سَاُر۪يكُمْ دَارَ الْفَاسِق۪ينَ", translation: "Ona verdiğimiz levhalarda öğüt olmak üzere her şeyi açık bir şekilde yazdık. \"Onu kuvvetle tut ve halkına da onu en iyi şekilde tutmalarını buyur. Yakında size fasıkların yurdunu göstereceğim.\"", transliteration: "Ve ketebna lehu fil elvahı min kulli şey'in mev'ızaten ve tafsilen li kulli şey'in fe huzha bi kuvvetin ve'mur kavmeke ye'huzu bi ahseniha seurikum darel fasikin."},
  {id: 1100, sure_no: 146, ayet_no: 146, sure_adi: "Araf", text: "سَاَصْرِفُ عَنْ اٰيَاتِيَ الَّذ۪ينَ يَتَكَبَّرُونَ فِي الْاَرْضِ بِغَيْرِ الْحَقِّۜ وَاِنْ يَرَوْا كُلَّ اٰيَةٍ لَا يُؤْمِنُوا بِهَاۚ وَاِنْ يَرَوْا سَب۪يلَ الرُّشْدِ لَا يَتَّخِذُوهُ سَب۪يلاًۚ وَاِنْ يَرَوْا سَب۪يلَ الْغَيِّ يَتَّخِذُوهُ سَب۪يلاًۜ ذٰلِكَ بِاَنَّهُمْ كَذَّبُوا بِاٰيَاتِنَا وَكَانُوا عَنْهَا غَافِل۪ينَ", translation: "Yeryüzünde, haksız yere büyüklük taslayanları, ayetlerimden uzaklaştıracağım. Onlar, bütün ayetleri görseler de yine ona inanmazlar. Rüşd yolunu görseler de onu yol tutmazlar; ama azgınlık yolunu görseler onu kendilerine yol tutarlar. Bu, ayetlerimizi yalanlamalarından ve ondan gafil bulunmalarındandır.", transliteration: "Seasrifu an ayatiyellezine yetekebberune fil ardı bi gayril hakkı ve in yerev kulle ayetin la yu'minu biha ve in yerev sebiler ruşdi la yettehızuhu sebilen ve in yerev sebilel gayyi yettehızuhu sebil, zalike bi ennehum kezzebu bi ayatina ve kanu anha gafilin."},
  {id: 1101, sure_no: 147, ayet_no: 147, sure_adi: "Araf", text: "وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَلِقَٓاءِ الْاٰخِرَةِ حَبِطَتْ اَعْمَالُهُمْۜ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوا يَعْمَلُونَ۟", translation: "Ayetlerimizi ve ahiretteki karşılaşmayı yalanlayan kimselerin amelleri boşa gitmiştir. Onlar, başkasıyla değil, ancak kendi yaptıkları ile cezalandırılacaklardır.", transliteration: "Vellezine kezzebu bi ayatina ve likail ahireti habitat a'maluhum, hel yuczevne illa ma kanu ya'melun."},
  {id: 1102, sure_no: 148, ayet_no: 148, sure_adi: "Araf", text: "وَاتَّخَذَ قَوْمُ مُوسٰى مِنْ بَعْدِه۪ مِنْ حُلِيِّهِمْ عِجْلاً جَسَداً لَهُ خُوَارٌۜ  اَلَمْ يَرَوْا اَنَّهُ لَا يُكَلِّمُهُمْ وَلَا يَهْد۪يهِمْ سَب۪يلاًۢ اِتَّخَذُوهُ وَكَانُوا ظَالِم۪ينَ", translation: "Musa'nın halkı, onun arkasından, böğürmesi olan, süs eşyalarından yapılmış bir buzağı heykelini benimsediler. Onun kendileriyle konuşamadığını ve yol gösteremediğini görmediler mi ki onu benimsediler? Onu benimsemekle zalimlerden oldular.", transliteration: "Vettehaze kavmu musa min ba'dihi min huliyyihim iclen ceseden lehu huvar, e lem yerev ennehu la yukellimuhum ve la yehdihim sebilen ittehazuhu ve kanu zalimin."},
  {id: 1103, sure_no: 149, ayet_no: 149, sure_adi: "Araf", text: "وَلَمَّا سُقِطَ ف۪ٓي اَيْد۪يهِمْ وَرَاَوْا اَنَّهُمْ قَدْ ضَلُّواۙ قَالُوا لَئِنْ لَمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُونَنَّ مِنَ الْخَاسِر۪ينَ", translation: "Ne zaman ki akılları başlarına gelip gerçekten sapmış olduklarını görünce: \"Eğer Rabb'imiz bize acımaz ve bizi bağışlamazsa mutlaka hüsrana uğrayanlardan oluruz.\" dediler.", transliteration: "Ve lemma sukıta fi eydihim ve reev ennehum kad dallu kalu le in lem yerhamna rabbuna ve yağfir lena le nekunenne minel hasirin."},
  {id: 1104, sure_no: 150, ayet_no: 150, sure_adi: "Araf", text: "وَلَمَّا رَجَعَ مُوسٰٓى اِلٰى قَوْمِه۪ غَضْبَانَ اَسِفاًۙ قَالَ بِئْسَمَا خَلَفْتُمُون۪ي مِنْ بَعْد۪يۚ اَعَجِلْتُمْ اَمْرَ رَبِّكُمْۚ وَاَلْقَى الْاَلْوَاحَ وَاَخَذَ بِرَأْسِ اَخ۪يهِ يَجُرُّهُٓ اِلَيْهِۜ قَالَ ابْنَ اُمَّ اِنَّ الْقَوْمَ اسْتَضْعَفُون۪ي وَكَادُوا يَقْتُلُونَن۪يۘ فَلَا تُشْمِتْ بِيَ الْاَعْدَٓاءَ وَلَا تَجْعَلْن۪ي مَعَ الْقَوْمِ الظَّالِم۪ينَ", translation: "Musa, halkına döndüğünde, öfke ve üzüntü içinde onlara: \"Benim yokluğumda ne kötü işler yapmışsınız! Rabb'inizin emrini çabuklaştırdınız mı?\" dedi. Levhaları bırakıp, kardeşinin başını tutup kendine çekti. \"Ey annemin oğlu! Gerçekten bu halk beni zayıf buldu, neredeyse beni öldüreceklerdi; sen de düşmanları benimle sevindirme, beni bu zalim kimselerle bir tutma.\" dedi.", transliteration: "Ve lemma recea musa ila kavmihi gadbane esifen kale bi'sema haleftumuni min ba'di, e aciltum emre rabbikum, ve elkal elvaha ve ehaze bi re'si ahihi yecurruhu ileyh, kalebne umme innel kavmestad'afuni ve kadu yaktuluneni fe la tuşmit biyel a'dae ve la tec'alni meal kavmiz zalimin."},
  {id: 1105, sure_no: 151, ayet_no: 151, sure_adi: "Araf", text: "قَالَ رَبِّ اغْفِرْ ل۪ي وَلِاَخ۪ي وَاَدْخِلْنَا ف۪ي رَحْمَتِكَۘ وَاَنْتَ اَرْحَمُ الرَّاحِم۪ينَ۟", translation: "\"Rabb'im! Beni ve kardeşimi bağışla! Bizi rahmetine dahil et. Sen merhametlilerin en merhametlisisin.\" dedi.", transliteration: "Kale rabbıgfirli ve li ahi ve edhilna fi rahmetike ve ente erhamur rahımin."},
  {id: 1106, sure_no: 152, ayet_no: 152, sure_adi: "Araf", text: "اِنَّ الَّذ۪ينَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِنْ رَبِّهِمْ وَذِلَّةٌ فِي الْحَيٰوةِ الدُّنْيَاۜ وَكَذٰلِكَ نَجْزِي الْمُفْتَر۪ينَ", translation: "Buzağıyı benimseyenlere Rabb'lerinden bir öfke erişecektir ve dünya hayatında da zillete uğrayacaklardır. İşte iftiracıları böyle cezalandırırız.", transliteration: "İnnellezinettehazul ıcle seyenaluhum gadabun min rabbihim ve zilletun fil hayatid dunya, ve kezalike neczil mufterin."},
  {id: 1107, sure_no: 153, ayet_no: 153, sure_adi: "Araf", text: "وَالَّذ۪ينَ عَمِلُوا السَّيِّـَٔاتِ ثُمَّ تَابُوا مِنْ بَعْدِهَا وَاٰمَنُواۘ اِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَح۪يمٌ", translation: "Kötülük yapıp, arkasından tevbe edip, İman Edenler için kuşkusuz Rabb'in bundan sonra Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Vellezine amilus seyyiati summe tabu min ba'diha ve amenu inne rabbeke min ba'diha le gafurun rahim."},
  {id: 1108, sure_no: 154, ayet_no: 154, sure_adi: "Araf", text: "وَلَمَّا سَكَتَ عَنْ مُوسَى الْغَضَبُ اَخَذَ الْاَلْوَاحَۚ وَف۪ي نُسْخَتِهَا هُدًى وَرَحْمَةٌ لِلَّذ۪ينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَ", translation: "Öfkesi geçince Musa levhaları aldı, onlardaki yazıda Rabb'leri için korkanlara bir yol gösterme ve bir rahmet vardı.", transliteration: "Ve lemma sekete an musel gadabu ehazel elvah, ve fi nushatiha huden ve rahmetun lillezine hum li rabbihim yerhebun."},
  {id: 1109, sure_no: 155, ayet_no: 155, sure_adi: "Araf", text: "وَاخْتَارَ مُوسٰى قَوْمَهُ سَبْع۪ينَ رَجُلاً لِم۪يقَاتِنَاۚ فَلَمَّٓا اَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ اَهْلَكْتَهُمْ مِنْ قَبْلُ وَاِيَّايَۜ اَتُهْلِكُنَا بِمَا فَعَلَ السُّفَـهَٓاءُ مِنَّاۚ اِنْ هِيَ اِلَّا فِتْنَتُكَۜ تُضِلُّ بِهَا مَنْ تَشَٓاءُ وَتَهْد۪ي مَنْ تَشَٓاءُۜ  اَنْتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الْغَافِر۪ينَ", translation: "Musa, belirlediğimiz buluşma için adamlarından yetmiş kişi seçti. Kendilerini sarsıntı tutunca: \"Ey Rabb'im! Dileseydin bunları da beni de daha önce yok ederdin. İçimizdeki birtakım beyinsizler yüzünden bizi yok mu edeceksin? Bu, ancak senin bir fitnendir. Onunla hak edeni sapkınlıkta bırakırsın hak edene de doğru yolu gösterirsin. Sen bizim velimizsin. Bizi bağışla. Bize merhamet et. Sen bağışlayanların en hayırlısısın.\" dedi.", transliteration: "Vahtara musa kavmehu seb'ine raculen li mikatina, fe lemma ehazet humur recfetu kale rabbi lev şi'te ehlektehum min kablu ve iyyaye, e tuhlikuna bi ma feales sufehau minna, in hiye illa fitnetuk, tudıllu biha men teşau ve tehdi men teşau ente veliyyuna fagfirlena verhamna ve ente hayrul gafirin."},
  {id: 1110, sure_no: 156, ayet_no: 156, sure_adi: "Araf", text: "وَاكْتُبْ لَنَا ف۪ي هٰذِهِ الدُّنْيَا حَسَنَةً وَفِي الْاٰخِرَةِ اِنَّا هُدْنَٓا اِلَيْكَۜ قَالَ عَذَاب۪ٓي اُص۪يبُ بِه۪ مَنْ اَشَٓاءُۚ وَرَحْمَت۪ي وَسِعَتْ كُلَّ شَيْءٍۜ فَسَاَكْتُبُهَا لِلَّذ۪ينَ يَتَّقُونَ وَيُؤْتُونَ الزَّكٰوةَ وَالَّذ۪ينَ هُمْ بِاٰيَاتِنَا يُؤْمِنُونَۚ", translation: "Bize, bu dünyada da ahiret'te de iyilik yaz. Biz Sana yöneldik. Allah: \"Azabıma hak edeni uğratırım, rahmetim ise her şeyi kuşatmıştır. Onu, takva sahibi olanlara, zekatı yapanlara ve ayetlerimize inananlara yazacağım.\" buyurdu.", transliteration: "Vektub lena fi hazihid dunya haseneten ve fil ahıreti inna hudna ileyk, kale azabi usibu bihi men eşau ve rahmeti vesiat kulle şey', fe se ektubuha lillezine yettekune ve yu'tunez zekate vellezine hum bi ayatina yu'minun."},
  {id: 1111, sure_no: 157, ayet_no: 157, sure_adi: "Araf", text: "اَلَّذ۪ينَ يَتَّبِعُونَ الرَّسُولَ النَّبِيَّ الْاُمِّيَّ الَّذ۪ي يَجِدُونَهُ مَكْتُوباً عِنْدَهُمْ فِي التَّوْرٰيةِ وَالْاِنْج۪يلِۘ يَأْمُرُهُمْ بِالْمَعْرُوفِ وَيَنْهٰيهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبَاتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبَٓائِثَ وَيَضَعُ عَنْهُمْ اِصْرَهُمْ وَالْاَغْلَالَ الَّت۪ي كَانَتْ عَلَيْهِمْۜ فَالَّذ۪ينَ اٰمَنُوا بِه۪ وَعَزَّرُوهُ وَنَصَرُوهُ وَاتَّبَعُوا النُّورَ الَّـذ۪ٓي اُنْزِلَ مَعَهُٓۙ اُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ۟", translation: "Onlar ki, yanlarındaki Tevrat ve İncil'de yazılı buldukları ummi Nebi Resule, tabi olurlar. O ki, onlara ma'ruf olanı yapar ve tavsiye eder ve onları münker olandan alıkoyar ve temiz şeyleri helal, pis şeyleri haram kılar, zahmet ve sıkıntı veren şeyleri onlardan kaldırır, onlardan bağları çözer, ona iman eden, ona saygı gösterenler ve ona yardım edenler ve ona indirilen nura tabi olanlar işte kurtuluşa erenler bunlardır.", transliteration: "Ellezine yettebiuner resulen nebiyyel ummiyyellezi yecidunehu mektuben indehum fit tevrati vel incili ye'muruhum bil ma'rufi ve yenhahum anil munkeri ve yuhıllu lehumut tayyibati ve yuharrimu aleyhimul habaise ve yedau anhum ısrahum vel aglalelleti kanet aleyhim, fellezine amenu bihi ve azzeruhu ve nasaruhu vettebeun nurellezi unzile meahu ulaike humul muflihun."},
  {id: 1112, sure_no: 158, ayet_no: 158, sure_adi: "Araf", text: "قُلْ يَٓا اَيُّهَا النَّاسُ اِنّ۪ي رَسُولُ اللّٰهِ اِلَيْكُمْ جَم۪يعاًۨ الَّذ۪ي لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۚ لَٓا اِلٰهَ اِلَّا هُوَ يُحْـي۪ وَيُم۪يتُۖ فَاٰمِنُوا بِاللّٰهِ وَرَسُولِهِ النَّبِيِّ الْاُمِّيِّ الَّذ۪ي يُؤْمِنُ بِاللّٰهِ وَكَلِمَاتِه۪ وَاتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ", translation: "De ki: \"Ey insanlar! Ben, göklerin ve yerin sahibi olan, kendisinden başka ilah olmayan, yaşatan ve öldüren Allah'ın, size, hepinize gönderdiği bir Resul'üm. Gelin, Allah'a ve O'nun sözlerine iman eden Ummi Nebi Resulüne iman edin ve ona uyun ki böylece doğru yolu bulasınız.\"", transliteration: "Kul ya eyyuhen nasu inni resulullahi ileykum cemianillezi lehu mulkus semavati vel ard, la ilahe illa huve yuhyi ve yumit, fe aminu billahi ve resulihin nebiyyil ummiyyillezi yu'minu billahi ve kelimatihi vettebiuhu leallekum tehtedun."},
  {id: 1113, sure_no: 159, ayet_no: 159, sure_adi: "Araf", text: "وَمِنْ قَوْمِ مُوسٰٓى اُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِه۪ يَعْدِلُونَ", translation: "Musa'nın halkından da hakka ileten ve onunla adaletli davranan bir topluluk vardı.", transliteration: "Ve min kavmi musa ummetun yehdune bil hakkı ve bihi ya'dilun."},
  {id: 1114, sure_no: 160, ayet_no: 160, sure_adi: "Araf", text: "وَقَطَّعْنَاهُمُ اثْنَتَيْ عَشْرَةَ اَسْبَاطاً اُمَماًۜ وَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اِذِ اسْتَسْقٰيهُ قَوْمُهُٓ اَنِ اضْرِبْ بِعَصَاكَ الْحَجَرَۚ فَانْبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْناًۜ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَشْرَبَهُمْۜ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوٰىۜ  كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْۜ وَمَا ظَلَمُونَا وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Biz, onları oymaklar halinde on iki topluluğa ayırdık. Halkı ondan su isteyince, Musa'ya, \"Asanı taşa vur!\" diye vahyettik. Ondan on iki pınar fışkırdı. Her topluluk su alacağı kaynağı bildi. Üzerlerine buluttan gölgelik yaptık, onlara menn ve bıldırcın bağışladık. \"Size rızık olarak verdiklerimizin temiz olanlarından yiyin.\" Onlar, bize zulmetmediler, fakat kendilerine zulmediyorlardı.", transliteration: "Ve katta'nahumusnetey aşrete esbatan umema, ve evhayna ila musa izisteskahu kavmuhu enıdrıb bi asakel hacer, fenbeceset minhusneta aşrete ayna, kad alime kullu unasin meşrebehum, ve zallelna aleyhimul gamame ve enzelna aleyhimul menne ves selva, kulu min tayyibati ma rezaknakum, ve ma zalemuna ve lakin kanu enfusehum yazlimun."},
  {id: 1115, sure_no: 161, ayet_no: 161, sure_adi: "Araf", text: "وَاِذْ ق۪يلَ لَهُمُ اسْكُنُوا هٰذِهِ الْقَرْيَةَ وَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ وَقُولُوا حِطَّةٌ وَادْخُلُوا الْبَابَ سُجَّداً نَغْفِرْ لَكُمْ خَط۪ٓيـَٔاتِكُمْۜ سَنَز۪يدُ الْمُحْسِن۪ينَ", translation: "Onlara, \"Şu beldeye yerleşin ve orada dilediğiniz şeylerden yiyin. Af dilediğinizi söyleyin ve teslim olmuş/kabullenmiş olarak kapısından girin.\" denilmişti. Biz, \"Yanlışlarınızı bağışlayalım ve iyilik edenlere fazlasıyla verelim.\"", transliteration: "Ve iz kile lehumuskunu hazihil karyete ve kulu minha haysu şi'tum ve kulu hıttatun vedhulul babe succeden nagfir lekum hatiatikum, senezidul muhsinin."},
  {id: 1116, sure_no: 162, ayet_no: 162, sure_adi: "Araf", text: "فَبَدَّلَ الَّذ۪ينَ ظَلَمُوا مِنْهُمْ قَوْلاً غَيْرَ الَّذ۪ي ق۪يلَ لَهُمْ فَاَرْسَلْنَا عَلَيْهِمْ رِجْزاً مِنَ السَّمَٓاءِ بِمَا كَانُوا يَظْلِمُونَ۟", translation: "Ancak onlardan haksızlık yapanlar, kendilerine söylenen sözü başka bir söze çevirdiler. Biz de haksızlık yapmaları yüzünden, üzerlerine gökten bir azap gönderdik.", transliteration: "Fe beddelellezine zalemu minhum kavlen gayrellezi kile lehum fe erselna aleyhim riczen mines semai bi ma kanu yazlimun."},
  {id: 1117, sure_no: 163, ayet_no: 163, sure_adi: "Araf", text: "وَسْـَٔلْهُمْ عَنِ الْقَرْيَةِ الَّت۪ي كَانَتْ حَاضِرَةَ الْبَحْرِۢ اِذْ يَعْدُونَ فِي السَّبْتِ اِذْ تَأْت۪يهِمْ ح۪يتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعاً وَيَوْمَ لَا يَسْبِتُونَۙ لَا تَأْت۪يهِمْۚ كَذٰلِكَ نَبْلُوهُمْ بِمَا كَانُوا يَفْسُقُونَ", translation: "Onlara, deniz kıyısındaki kasabanın durumunu sor. Hani onlar Cumartesi Günü Yasasını çiğneyerek haddi aşıyorlardı. Balıklar Cumartesi günü ortaya çıkıyor, diğer günler ise gelmiyorlardı. fasıklıkları nedeniyle kendilerini bu şekilde sınıyorduk.", transliteration: "Ves'elhum anil karyetilleti kanet hadıratel bahri iz ya'dune fis sebti iz te'tihim hitanuhum yevme sebtihim şurre'an ve yevme la yesbitune la te'tihim, kezalike nebluhum bi ma kanu yefsukun."},
  {id: 1118, sure_no: 164, ayet_no: 164, sure_adi: "Araf", text: "وَاِذْ قَالَتْ اُمَّةٌ مِنْهُمْ لِمَ تَعِظُونَ قَوْماًۙۨ اللّٰهُ مُهْلِكُهُمْ اَوْ مُعَذِّبُهُمْ عَذَاباً شَد۪يداًۜ قَالُوا مَعْذِرَةً اِلٰى رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُونَ", translation: "Onlardan bir topluluk dedi ki: \"Allah'ın yok edeceği veya şiddetli bir azap ile cezalandıracağı bir halka ne diye öğüt veriyorsunuz?\" Dediler ki: \"Rabb'inize mazeret beyan etmek için ve bir de belki takva sahibi olurlar.\"", transliteration: "Ve iz kalet ummetun minhum lime teizune kavmenillahu muhlikuhum ev muazzibuhum azaben şedida, kalu ma'zireten ila rabbikum ve leallehum yettekun."},
  {id: 1119, sure_no: 165, ayet_no: 165, sure_adi: "Araf", text: "فَلَمَّا نَسُوا مَا ذُكِّرُوا بِه۪ٓ اَنْجَيْنَا الَّذ۪ينَ يَنْهَوْنَ عَنِ السُّٓوءِ وَاَخَذْنَا الَّذ۪ينَ ظَلَمُوا بِعَذَابٍ بَـ۪ٔيسٍ بِمَا كَانُوا يَفْسُقُونَ", translation: "Ne zaman ki onlar, yapılan öğüdü umursamadılar, Biz de kötülükten alıkoymaya çalışanları kurtardık, zulmedenleri, fasıklık yapmaları nedeniyle çetin bir azapla cezalandırdık.", transliteration: "Fe lemma nesu ma zukkiru bihi enceynellezine yenhevne anis sui ve ahaznellezine zalemu bi azabin beisin bi ma kanu yefsukun."},
  {id: 1120, sure_no: 166, ayet_no: 166, sure_adi: "Araf", text: "فَلَمَّا عَتَوْا عَنْ مَا نُهُوا عَنْهُ قُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِـ۪ٔينَ", translation: "Yasaklandıkları şeyleri yapmakta ısrar edince, onlara: \"Düşkün maymunlar olun.\" dedik.", transliteration: "Fe lemma atev an ma nuhu anhu kulna lehum kunu kıredeten hasiin."},
  {id: 1121, sure_no: 167, ayet_no: 167, sure_adi: "Araf", text: "وَاِذْ تَاَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ يَسُومُهُمْ سُٓوءَ الْعَذَابِۜ اِنَّ رَبَّكَ لَسَر۪يعُ الْعِقَابِۚ وَاِنَّهُ لَغَفُورٌ رَح۪يمٌ", translation: "Hani Rabb'in; onların, üzerine Kıyamet Günü'ne kadar kötü azaba uğratacak kimseleri mutlaka göndereceğini bildirmişti. Rabb'in çabuk ceza verendir. Kuşkusuz O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve iz teezzene rabbuke le yeb'asenne aleyhim ila yevmil kıyameti men yesumuhum suel azab, inne rabbeke le seriul ıkabi ve innehu le gafurun rahim."},
  {id: 1122, sure_no: 168, ayet_no: 168, sure_adi: "Araf", text: "وَقَطَّعْنَاهُمْ فِي الْاَرْضِ اُمَماًۚ مِنْهُمُ الصَّالِحُونَ وَمِنْهُمْ دُونَ ذٰلِكَۘ وَبَلَوْنَاهُمْ بِالْحَسَنَاتِ وَالسَّيِّـَٔاتِ لَعَلَّهُمْ يَرْجِعُونَ", translation: "Onları, yeryüzünde topluluklara ayırdık. Onlardan, salih olanlar da vardı olmayanlar da. Belki dönerler diye onları iyiliklerle ve kötülüklerle sınadık.", transliteration: "Ve katta'nahum fil ardı umema, minhumus salihune ve minhum dune zalike ve belevnahum bil hasenati ves seyyiati leallehum yerciun."},
  {id: 1123, sure_no: 169, ayet_no: 169, sure_adi: "Araf", text: "فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتَابَ يَأْخُذُونَ عَرَضَ هٰذَا الْاَدْنٰى وَيَقُولُونَ سَيُغْفَرُ لَنَاۚ وَاِنْ يَأْتِهِمْ عَرَضٌ مِثْلُهُ يَأْخُذُوهُۜ اَلَمْ يُؤْخَذْ عَلَيْهِمْ م۪يثَاقُ الْكِتَابِ اَنْ لَا يَقُولُوا عَلَى اللّٰهِ اِلَّا الْحَقَّ وَدَرَسُوا مَا ف۪يهِۜ وَالدَّارُ الْاٰخِرَةُ خَيْرٌ لِلَّذ۪ينَ يَتَّقُونَۜ اَفَلَا تَعْقِلُونَ", translation: "Onların yerine Kitap'a mirasçı olanlar, nasıl olsa bağışlanacağız diyerek dünyanın geçici menfaatlerini tercih ettiler; kendilerine buna benzer şeyler gelse, onu da tercih ederler. Onlardan, Kitap'a bağlı kalacaklarına ve Allah hakkında ancak hakkı söyleyeceklerine dair söz alınmamış mıydı? Oysaki onlar, o Kitap'ta olanı okumuşlardı. Ahiret yurdu takva sahibi olanlar için daha hayırlıdır. Hala akletmeyecek misiniz?", transliteration: "Fe halefe min ba'dihim halfun verisul kitabe ye'huzune arada hazel edna ve yekulune se yugferu lena ve in ye'tihim aradun misluhu ye'huzuh, e lem yu'haz aleyhim misakul kitabi en la yekulu alallahi illel hakka ve deresu ma fih, ved darul ahıretu hayrun lillezine yettekun, e fe la ta'kılun."},
  {id: 1124, sure_no: 170, ayet_no: 170, sure_adi: "Araf", text: "وَالَّذ۪ينَ يُمَسِّكُونَ بِالْكِتَابِ وَاَقَامُوا الصَّلٰوةَۜ اِنَّا لَا نُض۪يعُ اَجْرَ الْمُصْلِح۪ينَ", translation: "Kitap'a sımsıkı sarılıp, salatı ikame edenlere gelince; kuşkusuz Biz salih olanların emeklerini zayi etmeyiz.", transliteration: "Vellezine yumessikune bil kitabi ve ekamus salate inna la nudiu ecrel muslihin."},
  {id: 1125, sure_no: 171, ayet_no: 171, sure_adi: "Araf", text: "وَاِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَاَنَّهُ ظُلَّةٌ وَظَنُّٓوا اَنَّهُ وَاقِعٌ بِهِمْۚ خُذُوا مَٓا اٰتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا ف۪يهِ لَعَلَّكُمْ تَتَّقُونَ۟", translation: "Hani! Biz, dağı gölgelik gibi üzerlerine kaldırmıştık da onlar da üzerlerine düşecek sanmışlardı. \"Size verdiğimize sımsıkı sarılın, içindeki öğüdü tutun ki takva sahibi olabilesiniz.\"", transliteration: "Ve iz netaknel cebele fevkahum ke ennehu zulletun ve zannu ennehu vakıun bihim, huzu ma ateynakum bi kuvvetin vezkuru ma fihi leallekum tettekun."},
  {id: 1126, sure_no: 172, ayet_no: 172, sure_adi: "Araf", text: "وَاِذْ اَخَذَ رَبُّكَ مِنْ بَن۪ٓي اٰدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَاَشْهَدَهُمْ عَلٰٓى اَنْفُسِهِمْۚ اَلَسْتُ بِرَبِّكُمْۜ  قَالُوا بَلٰىۚۛ شَهِدْنَاۚۛ اَنْ تَقُولُوا يَوْمَ الْقِيٰمَةِ اِنَّا كُنَّا عَنْ هٰذَا غَافِل۪ينَۙ", translation: "Kıyamet günü, \"Biz bundan habersizdik.\" demeyesiniz diye. Rabb'in, ademoğullarının sırtlarından soylarını çıkardı. Ve onları kendilerine tanık yaptı. \"Ben sizin Rabb'iniz değil miyim?\" dedi. \"Evet, Rabb'imizsin, Tanıklık ediyoruz.\" dediler.", transliteration: "Ve iz ehaze rabbuke min beni ademe min zuhurihim zurriyyetehum ve eşhedehum ala enfusihim, e lestu birabbikum, kalu bela, şehidna, en tekulu yevmel kıyameti inna kunna an haza gafilin."},
  {id: 1127, sure_no: 173, ayet_no: 173, sure_adi: "Araf", text: "اَوْ تَقُولُٓوا اِنَّـمَٓا اَشْرَكَ اٰبَٓاؤُ۬نَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِنْ بَعْدِهِمْۚ اَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُونَ", translation: "Veya \"Biz, bizden önce şirk koşan atalarımızın ardından gelen bir nesiliz, batılla amel edenlerin yaptıkları yüzünden bizi mi yok edeceksin?\" demeyesiniz diye.", transliteration: "Ev tekulu innema eşreke abauna min kablu ve kunna zurriyyeten min ba'dihim, e fe tuhlikuna bima fealel mubtilun."},
  {id: 1128, sure_no: 174, ayet_no: 174, sure_adi: "Araf", text: "وَكَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ وَلَعَلَّهُمْ يَرْجِعُونَ", translation: "İşte, doğruyu bulsunlar diye ayetlerimizi böyle ayrıntılı olarak açıklıyoruz.", transliteration: "Ve kezalike nufassılul ayati ve leallehum yerci'un."},
  {id: 1129, sure_no: 175, ayet_no: 175, sure_adi: "Araf", text: "وَاتْلُ عَلَيْهِمْ نَبَاَ الَّـذ۪ٓي اٰتَيْنَاهُ اٰيَاتِنَا فَانْسَلَخَ مِنْهَا فَاَتْبَعَهُ الشَّيْطَانُ فَكَانَ مِنَ الْغَاو۪ينَ", translation: "Onlara, o kimsenin haberini de oku, ki ona ayetlerimizi vermiştik de onlardan sıyrılıp ayrıldı. Şeytan da onu kendine tabi kıldı. Böylece azgınlardan oldu.", transliteration: "Vetlu aleyhim nebeellezi ateynahu ayatina fenseleha minha fe etbeahuş şeytanu fe kane minel gavin."},
  {id: 1130, sure_no: 176, ayet_no: 176, sure_adi: "Araf", text: "وَلَوْ شِئْنَا لَرَفَعْنَاهُ بِهَا وَلٰكِنَّهُٓ  اَخْلَدَ اِلَى الْاَرْضِ وَاتَّـبَعَ هَوٰيهُۚ فَمَثَلُهُ  كَمَثَلِ الْكَلْبِۚ اِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ اَوْ تَتْرُكْهُ يَلْهَثْۜ ذٰلِكَ مَثَلُ الْقَوْمِ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۚ فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ", translation: "Dileseydik onu bununla yükseltirdik. Fakat o yere saplandı, hevasına uydu. Onun durumu, üzerine varsan da dilini sarkıtıp soluyan, varmasan da dilini sarkıtıp soluyan köpeğin durumu gibidir. İşte ayetlerimizi yalanlayan halkın durumu böyledir. Sen bu kıssayı anlat, belki öğüt alırlar.", transliteration: "Ve lev şi'na le refa'nahu biha ve lakinnehu ahlede ilel ardı vettebea hevah, fe meseluhu ke meselil kelb, in tahmil aleyhi yelhes ev tetrukhu yelhes, zalike meselul kavmillezine kezzebu bi ayatina, faksusil kasasa leallehum yetefekkerun."},
  {id: 1131, sure_no: 177, ayet_no: 177, sure_adi: "Araf", text: "سَٓاءَ مَثَلاًۨ الْقَوْمُ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا وَاَنْفُسَهُمْ كَانُوا يَظْلِمُونَ", translation: "Ayetlerimizi yalanlayan ve böylece kendilerine zulmeden halkın durumu ne kötüdür.", transliteration: "Sae meselennil kavmullezine kezzebu bi ayatina ve enfusehum kanu yazlimun."},
  {id: 1132, sure_no: 178, ayet_no: 178, sure_adi: "Araf", text: "مَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَد۪يۚ وَمَنْ يُضْلِلْ فَاُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ", translation: "Allah, kime yol gösterirse, odur yolu bulan. Kimi de saptırırsa1, işte onlar hüsrana uğrayanlardır.", transliteration: "Men yehdillahu fehuvel muhtedi ve men yudlil fe ulaike humul hasirun."},
  {id: 1133, sure_no: 179, ayet_no: 179, sure_adi: "Araf", text: "وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَث۪يراً مِنَ الْجِنِّ وَالْاِنْسِۘ  لَهُمْ قُلُوبٌ لَا يَفْقَهُونَ بِهَاۘ وَلَهُمْ اَعْيُنٌ لَا يُبْصِرُونَ بِهَاۘ وَلَهُمْ اٰذَانٌ لَا يَسْمَعُونَ بِهَاۜ  اُو۬لٰٓئِكَ كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّۜ اُو۬لٰٓئِكَ هُمُ الْغَافِلُونَ", translation: "Gerçek şu ki, cinnden ve insten çoğalttıklarımızın çoğu Cehennem'liktir. Ki onların kalpleri vardır onunla kavramazlar, gözleri vardır onunla görmezler, kulakları vardır onunla işitmezler. İşte bunlar hayvanlar gibidirler, hatta daha da bilinçsizdirler. İşte gafil olanlar bunlardır.", transliteration: "Ve lekad zere'na li cehenneme kesiren minel cinni vel insi lehum kulubun la yefkahune biha ve lehum a'yunun la yubsırune biha ve lehum azanun la yesmeune biha, ulaike kel en'ami bel hum edallu, ulaike humul gafilun."},
  {id: 1134, sure_no: 180, ayet_no: 180, sure_adi: "Araf", text: "وَلِلّٰهِ الْاَسْمَٓاءُ الْحُسْنٰى فَادْعُوهُ بِهَاۖ وَذَرُوا الَّذ۪ينَ يُلْحِدُونَ ف۪ٓي اَسْمَٓائِه۪ۜ سَيُجْزَوْنَ مَا كَانُوا يَعْمَلُونَ", translation: "En iyi isimler Allah'ındır. Öyleyse O'nu, onlarla çağırın. Ona yakışmayan isimlerle çağıran kimseleri bırakın. Onlar, yaptıklarının cezasını görecekler.", transliteration: "Ve lillahil esmaul husna fed'uhu biha ve zerullezine yulhıdune fi esmaih, se yuczevne ma kanu ya'melun."},
  {id: 1135, sure_no: 181, ayet_no: 181, sure_adi: "Araf", text: "وَمِمَّنْ خَلَقْنَٓا اُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِه۪ يَعْدِلُونَ۟", translation: "Yarattıklarımız arasında öyle bir topluluk da var ki, hakka iletirler ve onunla adil olurlar.", transliteration: "Ve mimmen halakna ummetun yehdune bil hakkı ve bihi ya'dilun."},
  {id: 1136, sure_no: 182, ayet_no: 182, sure_adi: "Araf", text: "وَالَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَا سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لَا يَعْلَمُونَۚ", translation: "Ayetlerimizi yalanlayanları, hiç bilemeyecekleri bir şekilde adım adım yok olmaya yaklaştırırız.", transliteration: "Vellezine kezzebu bi ayatina se nestedricuhum min haysu la ya'lemun."},
  {id: 1137, sure_no: 183, ayet_no: 183, sure_adi: "Araf", text: "وَاُمْل۪ي لَهُمْۜ اِنَّ كَيْد۪ي مَت۪ينٌ", translation: "Onlara süre veriyorum, ancak planım çok sağlamdır.", transliteration: "Ve umli lehum, inne keydi metin"},
  {id: 1138, sure_no: 184, ayet_no: 184, sure_adi: "Araf", text: "اَوَلَمْ يَتَفَكَّرُوا مَا بِصَاحِبِهِمْ مِنْ جِنَّةٍۜ اِنْ هُوَ اِلَّا نَذ۪يرٌ مُب۪ينٌ", translation: "Arkadaşlarında, hiçbir delilik olmadığını düşünmüyorlar mı? O, ancak apaçık bir uyarıcıdır.", transliteration: "E ve lem yetefekkeru ma bi sahıbihim min cinneh, in huve illa nezirun mubin."},
  {id: 1139, sure_no: 185, ayet_no: 185, sure_adi: "Araf", text: "اَوَلَمْ يَنْظُرُوا ف۪ي مَلَكُوتِ السَّمٰوَاتِ وَالْاَرْضِ وَمَا خَلَقَ اللّٰهُ مِنْ شَيْءٍۙ وَاَنْ عَسٰٓى اَنْ يَكُونَ قَدِ اقْتَرَبَ اَجَلُهُمْۚ فَبِاَيِّ حَد۪يثٍ بَعْدَهُ يُؤْمِنُونَ", translation: "Göklerin ve yerin melekutuna, Allah'ın yaratmış olduğu şeylere, sürelerinin yaklaşmış olabileceği ihtimaline hiç bakmazlar mı? Bundan sonra artık hangi hadise iman edecekler?", transliteration: "E ve lem yanzuru fi melekutis semavati vel ardı ve ma halakallahu min şey'in ve en asa en yekune kadıkterebe eceluhum, fe bi eyyi hadisin ba'dehu yu'minun."},
  {id: 1140, sure_no: 186, ayet_no: 186, sure_adi: "Araf", text: "مَنْ يُضْلِلِ اللّٰهُ فَلَا هَادِيَ لَهُۜ وَيَذَرُهُمْ ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ", translation: "Allah, kimi saptırırsa ona doğru yolu gösterecek yoktur. Onları, azgınlıkları içinde bocalar durumda bırakır.", transliteration: "Men yudlilillahu fe la hadiye leh, ve yezeruhum fi tugyanihim ya'mehun."},
  {id: 1141, sure_no: 187, ayet_no: 187, sure_adi: "Araf", text: "يَسْـَٔلُونَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰيهَاۜ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ رَبّ۪يۚ لَا يُجَلّ۪يهَا لِوَقْتِهَٓا اِلَّا هُوَۜ ثَقُلَتْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ لَا تَأْت۪يكُمْ اِلَّا بَغْتَةًۜ يَسْـَٔلُونَكَ كَاَنَّكَ حَفِيٌّ عَنْهَاۜ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ", translation: "Sana Sa'at'ten soruyorlar. Ne zaman gerçekleşecek diye. De ki: \"Onun bilgisi sadece Rabb'imin yanındadır. Onun vaktini O'ndan başkası açıklayamaz. O göklere de, yere de ağır gelecektir. O size ansızın gelecek.\" Sanki sen biliyormuşsun gibi onu sana soruyorlar. De ki: \"Onun bilgisi sadece Allah'ın yanındadır.\" Ancak insanların çoğu bu gerçeği bilmez.", transliteration: "Yes'eluneke anis sa'ati eyyane mursaha, kul innema ilmuha inde rabbi, la yucelliha li vaktiha illa huv, sekulet fis semavati vel ard, la te'tikum illa bagtete, yes'eluneke ke enneke hafiyyun anha, kul innema ilmuha indallahi ve lakinne ekseren nasi la ya'lemun."},
  {id: 1142, sure_no: 188, ayet_no: 188, sure_adi: "Araf", text: "قُلْ لَٓا اَمْلِكُ لِنَفْس۪ي نَفْعاً وَلَا ضَراًّ اِلَّا مَا شَٓاءَ اللّٰهُۜ وَلَوْ كُنْتُ اَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِۚ وَمَا مَسَّنِيَ السُّٓوءُ اِنْ اَنَا۬ اِلَّا نَذ۪يرٌ وَبَش۪يرٌ لِقَوْمٍ يُؤْمِنُونَ۟", translation: "De ki: \"Allah dilemedikçe, kendime bir yarar sağlamak ya da kendimden bir zararı uzaklaştırmak benim elimde değil. Eğer gaybı bilseydim, elbette daha çok yararıma olan şeyi yapardım ve bana bir kötülük de dokunmazdı. Ben, iman eden bir halk için sadece bir uyarıcı ve haber verenim.\"", transliteration: "Kul la emliku li nefsi nef'an ve la darran illa maşaallah, ve lev kuntu a'lemul gaybe lesteksertu minel hayri ve ma messeniyes suu in ene illa nezirun ve beşirun li kavmin yu'minun."},
  {id: 1143, sure_no: 189, ayet_no: 189, sure_adi: "Araf", text: "هُوَ الَّذ۪ي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ وَجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ اِلَيْهَاۚ فَلَمَّا تَغَشّٰيهَا حَمَلَتْ حَمْلاً خَف۪يفاً فَمَرَّتْ بِه۪ۚ فَلَمَّٓا اَثْقَلَتْ دَعَوَا اللّٰهَ رَبَّهُمَا لَئِنْ اٰتَيْتَنَا صَالِحاً لَنَكُونَنَّ مِنَ الشَّاكِر۪ينَ", translation: "O, sizi bir tek nefisten yarattı ve kendisi ile sükunet bulsun diye ondan eşini var etti. Eşini sarıp örtünce, eşi hafif bir yük yüklendi. Bir müddet böyle geçti. Yükü ağırlaşınca her ikisi de Rabb'leri olan Allah'a: \"Eğer bize Salih bir evlat verirsen elbette Sana şükredenlerden olacağız.\" diye dua ettiler.", transliteration: "Huvellezi halakakum min nefsin vahıdetin ve ceale minha zevceha li yeskune ileyha, fe lemma tegaşşaha hamelet hamlen hafifen fe merret bihi, fe lemma eskalet deavallahe rabbehuma lein ateytena salihan le nekunenne mineş şakirin."},
  {id: 1144, sure_no: 190, ayet_no: 190, sure_adi: "Araf", text: "فَلَمَّٓا اٰتٰيهُمَا صَالِحاً جَعَلَا لَهُ شُرَكَٓاءَ ف۪يمَٓا اٰتٰيهُمَاۚ فَتَعَالَى اللّٰهُ عَمَّا يُشْرِكُونَ", translation: "Fakat onlara, salih bir evlat verince; kendilerine verilen şeyde ona ortaklar koşmaya başladılar. Allah, onların ortak koştukları şeyden yücedir.", transliteration: "Fe lemma atahuma salihan ceala lehu şurakae fima atahuma, fe tealallahu amma yuşrikun."},
  {id: 1145, sure_no: 191, ayet_no: 191, sure_adi: "Araf", text: "اَيُشْرِكُونَ مَا لَا يَخْلُقُ شَيْـٔاً وَهُمْ يُخْلَقُونَۘ", translation: "Hiçbir şey yaratamayan, aksine kendisi de yaratılmış olan şeyleri mi ortak koşuyorlar?", transliteration: "E yuşrikune ma la yahluku şey'en ve hum yuhlekun."},
  {id: 1146, sure_no: 192, ayet_no: 192, sure_adi: "Araf", text: "وَلَا يَسْتَط۪يعُونَ لَهُمْ نَصْراً وَلَٓا اَنْفُسَهُمْ يَنْصُرُونَ", translation: "Oysa onlar, ne onlara yardım edebilirler ne de kendilerine yardım edebilirler.", transliteration: "Ve la yestetiune lehum nasran ve la enfusehum yansurun."},
  {id: 1147, sure_no: 193, ayet_no: 193, sure_adi: "Araf", text: "وَاِنْ تَدْعُوهُمْ اِلَى الْهُدٰى لَا يَتَّبِعُوكُمْۜ سَوَٓاءٌ عَلَيْكُمْ اَدَعَوْتُمُوهُمْ اَمْ اَنْتُمْ صَامِتُونَ", translation: "Onları gerçeği kabul etmeye davet etseniz, size tabi olmazlar. Onları davet etseniz de etmeseniz de size karşı tutumları değişmez.", transliteration: "Ve in ted'uhum ilel huda la yettebiukum, sevaun aleykum e deavtumuhum em entum samitun."},
  {id: 1148, sure_no: 194, ayet_no: 194, sure_adi: "Araf", text: "اِنَّ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ عِبَادٌ اَمْثَالُكُمْ فَادْعُوهُمْ فَلْيَسْتَج۪يبُوا لَكُمْ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Allah'tan başka dua ettikleriniz, sizin gibi kullardır. Eğer doğru sözlü kimselerseniz, haydi onlara dua edin de size karşılık versinler.", transliteration: "İnnellezine ted'une min dunillahi ıbadun emsalukum fed'uhum felyestecibu lekum in kuntum sadıkin."},
  {id: 1149, sure_no: 195, ayet_no: 195, sure_adi: "Araf", text: "اَلَهُمْ اَرْجُلٌ يَمْشُونَ بِهَاۘ اَمْ لَهُمْ اَيْدٍ يَبْطِشُونَ بِهَاۘ اَمْ لَهُمْ اَعْيُنٌ يُبْصِرُونَ بِهَاۘ اَمْ لَهُمْ اٰذَانٌ يَسْمَعُونَ بِهَاۜ قُلِ ادْعُوا شُرَكَٓاءَكُمْ ثُمَّ ك۪يدُونِ فَلَا تُنْظِرُونِ", translation: "Onların, kendileriyle yürüyecek ayakları mı var? Veya tutacak elleri mi var? Veya görecek gözleri mi var? Ya da işitecek kulakları mı var? De ki: \"Haydi çağırın ortaklarınızı, sonra göz açtırmaksızın bana tuzak kurun.\"", transliteration: "E lehum erculun yemşune biha, em lehum eydin yabtışune biha, em lehum a'yunun yubsırune biha, em lehum azanun yesmeune biha, kulid'u şurekaekum summe kiduni fe la tunzırun."},
  {id: 1150, sure_no: 196, ayet_no: 196, sure_adi: "Araf", text: "اِنَّ وَلِـِّيَ اللّٰهُ الَّذ۪ي نَزَّلَ الْكِتَابَۘ وَهُوَ يَتَوَلَّى الصَّالِح۪ينَ", translation: "\"Kuşkusuz benim velim, Kitap'ı indiren Allah'tır. O, salih olanları koruyup gözetir.\"", transliteration: "İnne veliyyiyallahullezi nezzelel kitabe ve huve yetevelles salihin."},
  {id: 1151, sure_no: 197, ayet_no: 197, sure_adi: "Araf", text: "وَالَّذ۪ينَ تَدْعُونَ مِنْ دُونِه۪ لَا يَسْتَط۪يعُونَ نَصْرَكُمْ وَلَٓا اَنْفُسَهُمْ يَنْصُرُونَ", translation: "\"O'ndan başka dua ettiklerinizin, ne size yardım etmeye güçleri yeter, ne de kendilerine yardım etmeye.\"", transliteration: "Vellezine ted'une min dunihi la yestetiune nasrakum ve la enfusehum yensurun."},
  {id: 1152, sure_no: 198, ayet_no: 198, sure_adi: "Araf", text: "وَاِنْ تَدْعُوهُمْ اِلَى الْهُدٰى لَا يَسْمَعُواۜ وَتَرٰيهُمْ يَنْظُرُونَ اِلَيْكَ وَهُمْ لَا يُبْصِرُونَ", translation: "Eğer onları doğru yola çağırsanız sizi duymazlar. Onların, sana baktıklarını sanırsın, oysa onlar görmezler.", transliteration: "Ve in ted'uhum ilel huda la yesme'u, ve terahum yenzurune ileyke ve hum la yubsırun."},
  {id: 1153, sure_no: 199, ayet_no: 199, sure_adi: "Araf", text: "خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَاَعْرِضْ عَنِ الْجَاهِل۪ينَ", translation: "Affedici ol, urf ile buyur, cahillerden yüz çevir.", transliteration: "Huzil afve ve'mur bil urfi ve a'rıd anil cahilin."},
  {id: 1154, sure_no: 200, ayet_no: 200, sure_adi: "Araf", text: "وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِۜ اِنَّهُ سَم۪يعٌ عَل۪يمٌ", translation: "Ne zaman şeytan kötü bir dürtüş ile seni dürtülecek olursa hemen Allah'a sığın. Kuşkusuz O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve imma yenzeganneke mineş şeytani nezgun festeiz billah, innehu semiun alim."},
  {id: 1155, sure_no: 201, ayet_no: 201, sure_adi: "Araf", text: "اِنَّ الَّذ۪ينَ اتَّقَوْا اِذَا مَسَّهُمْ طَٓائِفٌ مِنَ الشَّيْطَانِ تَذَكَّرُوا فَاِذَا هُمْ مُبْصِرُونَۚ", translation: "Takva sahipleri, şeytan onları kuşattığı zaman, düşünüp hemen gerçeği kavrarlar.", transliteration: "İnnellezinettekav iza messehum taifun mineş şeytani tezekkeru fe izahum mubsırun."},
  {id: 1156, sure_no: 202, ayet_no: 202, sure_adi: "Araf", text: "وَاِخْوَانُهُمْ يَمُدُّونَهُمْ فِي الْغَيِّ ثُمَّ لَا يُقْصِرُونَ", translation: "Şeytanlar, kendilerine kardeş olanları azgınlığa sürüklerler, sonra da yakalarını bırakmazlar.", transliteration: "Ve ihvanuhum yemuddunehum fil gayyi summe la yuksirun."},
  {id: 1157, sure_no: 203, ayet_no: 203, sure_adi: "Araf", text: "وَاِذَا لَمْ تَأْتِهِمْ بِاٰيَةٍ قَالُوا لَوْلَا اجْتَبَيْتَهَاۜ قُلْ اِنَّـمَٓا اَتَّبِـعُ مَا يُوحٰٓى اِلَيَّ مِنْ رَبّ۪يۚ هٰذَا بَصَٓائِرُ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌ لِقَوْمٍ يُؤْمِنُونَ", translation: "Onlara bir ayet getirmediğin zaman, \"Derleyip uydursaydın ya.\" derler. De ki: \"Ben ancak Rabb'imden bana vahyedilene uyuyorum. Bu, iman eden bir halk için Rabb'inizden gelen bir basiret, bir yol gösterici ve bir rahmettir.\"", transliteration: "Ve iza lem te'tihim biayetin kalu lev lectebeyteha, kul innema ettebiu ma yuha ileyye min rabbi haza besairu min rabbikum ve huden ve rahmetun li kavmin yu'minun ."},
  {id: 1158, sure_no: 204, ayet_no: 204, sure_adi: "Araf", text: "وَاِذَا قُرِئَ الْقُرْاٰنُ فَاسْتَمِعُوا لَهُ وَاَنْصِتُوا لَعَلَّكُمْ تُرْحَمُونَ", translation: "Kur'an okunduğu zaman hemen susup onu dinleyin ki size rahmet edilsin.", transliteration: "Ve iza kuriel kur'anu festemiu lehu ve ensıtu leallekum turhamun."},
  {id: 1159, sure_no: 205, ayet_no: 205, sure_adi: "Araf", text: "وَاذْكُرْ رَبَّكَ ف۪ي نَفْسِكَ تَضَرُّعاً وَخ۪يفَةً وَدُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْاٰصَالِ وَلَا تَكُنْ مِنَ الْغَافِل۪ينَ", translation: "Nefsinde tedarruan ve çekinerek, yüksek olmayan bir sesle sabah akşam Rabb'ini an. Umursamazlardan olma!", transliteration: "Vezkur rabbeke fi nefsike tedarruan ve hifeten ve dunel cehri minel kavli bil guduvvi vel asali ve la tekun minel gafilin."},
  {id: 1160, sure_no: 206, ayet_no: 206, sure_adi: "Araf", text: "اِنَّ الَّذ۪ينَ عِنْدَ رَبِّكَ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِه۪ وَيُسَبِّحُونَهُ وَلَهُ يَسْجُدُونَ", translation: "Rabb'ine yakın olanlar, O'na kulluk etmekten asla kibirlenmezler; O'nu tesbih ederler ve O'na secde ederler.", transliteration: "İnnellezine inde rabbike la yestekbirune an ibadetihi ve yusebbihunehu ve lehu yescudun."},
  {id: 1161, sure_no: 1, ayet_no: 1, sure_adi: "Enfal", text: "يَسْـَٔلُونَكَ عَنِ الْاَنْفَالِۜ قُلِ الْاَنْفَالُ لِلّٰهِ وَالرَّسُولِۚ فَاتَّقُوا اللّٰهَ وَاَصْلِحُوا ذَاتَ بَيْنِكُمْۖ وَاَط۪يعُوا اللّٰهَ وَرَسُولَهُٓ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Sana enfalı soruyorlar. De ki: \"Enfal, Allah ve Resul'ü içindir. Eğer Mü'min iseniz Allah'a karşı takva sahibi olun, birbirinizin arasını düzeltin, Allah'a ve Resul'üne itaat edin.", transliteration: "Yes'eluneke anil enfal, kulil enfalu lillahi ver resul, fettekullahe ve aslihu zate beynikum ve etiullahe ve resulehu in kuntum mu'minin."},
  {id: 1162, sure_no: 2, ayet_no: 2, sure_adi: "Enfal", text: "اِنَّمَا الْمُؤْمِنُونَ الَّذ۪ينَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوبُهُمْ وَاِذَا تُلِيَتْ عَلَيْهِمْ اٰيَاتُهُ زَادَتْهُمْ ا۪يمَاناً وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَۚ", translation: "Gerçekten Mü'minler o kimselerdir ki: Allah anıldığı zaman kalpleri ürperir, onlara Allah'ın ayetleri okunduğunda bu imanlarını artırır ve yalnızca Rabb'lerine tevekkül ederler.", transliteration: "İnnemel mu'minunellezine iza zukirallahu vecilet kulubuhum ve iza tuliyet aleyhim ayatuhu zadethum imanen ve ala rabbihim yetevekkelun."},
  {id: 1163, sure_no: 3, ayet_no: 3, sure_adi: "Enfal", text: "اَلَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَۜ", translation: "Onlar, salatı ikame eden ve kendilerine rızık olarak verdiğimiz şeylerden infak edenlerdir.", transliteration: "Ellezine yukimunes salate ve mimma razaknahum yunfikun."},
  {id: 1164, sure_no: 4, ayet_no: 4, sure_adi: "Enfal", text: "اُو۬لٰٓئِكَ هُمُ الْمُؤْمِنُونَ حَـقاًّۜ لَهُمْ دَرَجَاتٌ عِنْدَ رَبِّهِمْ وَمَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌۚ", translation: "İşte onlar gerçek Mü'minlerdir. Onlar için Rabb'leri katında dereceler, bağışlanma ve kerim bir rızık vardır.", transliteration: "Ulaike humul mu'minune hakka, lehum derecatun inde rabbihim ve magfiretun ve rızkun kerim."},
  {id: 1165, sure_no: 5, ayet_no: 5, sure_adi: "Enfal", text: "كَمَٓا اَخْرَجَكَ رَبُّكَ مِنْ بَيْتِكَ بِالْحَقِّۖ وَاِنَّ فَر۪يقاً مِنَ الْمُؤْمِن۪ينَ لَكَارِهُونَۙ", translation: "Keza, Rabb'in Hakk uğruna seni evinden çıkardı. Oysa Mü'minlerden bir kısmı bundan hoşlanmamıştı.", transliteration: "Kema ahreceke rabbuke min beytike bil hakkı ve inne ferikan minel mu'minine le karihun."},
  {id: 1166, sure_no: 6, ayet_no: 6, sure_adi: "Enfal", text: "يُجَادِلُونَكَ فِي الْحَقِّ بَعْدَ مَا تَبَيَّنَ كَاَنَّمَا يُسَاقُونَ اِلَى الْمَوْتِ وَهُمْ يَنْظُرُونَۜ", translation: "Hakk açığa çıktıktan sonra bile, sanki göz göre göre ölüme sürükleniyorlarmış gibi, seninle tartışıyorlardı.", transliteration: "Yucadiluneke fil hakkı ba'de ma tebeyyene ke ennema yusakune ilel mevti ve hum yanzurun."},
  {id: 1167, sure_no: 7, ayet_no: 7, sure_adi: "Enfal", text: "وَاِذْ يَعِدُكُمُ اللّٰهُ اِحْدَى الطَّٓائِفَتَيْنِ اَنَّهَا لَكُمْ وَتَوَدُّونَ اَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُونُ لَكُمْ وَيُر۪يدُ اللّٰهُ اَنْ يُحِقَّ الْحَقَّ بِكَلِمَاتِه۪ وَيَقْطَعَ دَابِرَ الْكَافِر۪ينَۙ", translation: "Allah, iki topluluktan birinin sizin olacağını vaat ediyordu. Siz ise kuvveti bulunmayanı istiyordunuz. Oysa Allah da kelimeleriyle Hakk'ı gerçekleştirmek ve Kafirlerin kökünün kesilmesini istiyordu.", transliteration: "Ve iz yaıdukumullahu ihdet taifeteyni enneha lekum, ve teveddune enne gayre zatiş şevketi tekunu lekum, ve yuridullahu en yuhıkkal hakka bi kelimatihi ve yaktaa dabirel kafirin."},
  {id: 1168, sure_no: 8, ayet_no: 8, sure_adi: "Enfal", text: "لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُونَۚ", translation: "Bu, suçluların hoşuna gitmese de Hakk'ın ortaya çıkarılması ve Batıl'ın yok olması içindi.", transliteration: "Li yuhıkkal hakka ve yubtılel batıle ve lev kerihel mucrimun."},
  {id: 1169, sure_no: 9, ayet_no: 9, sure_adi: "Enfal", text: "اِذْ تَسْتَغ۪يثُونَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ اَنّ۪ي مُمِدُّكُمْ بِاَلْفٍ مِنَ الْمَلٰٓئِكَةِ مُرْدِف۪ينَ", translation: "Hani siz, Rabb'inizden yardım istiyordunuz. O da ardı ardına bin melekle yardım edeceğim diye, isteğinize karşılık vermişti.", transliteration: "İz testegisune rabbekum festecabe lekum enni mumiddukum bi elfin minel melaiketi murdifin."},
  {id: 1170, sure_no: 10, ayet_no: 10, sure_adi: "Enfal", text: "وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى وَلِتَطْمَئِنَّ بِه۪ قُلُوبُكُمْۚ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ۟", translation: "Allah bunu, ancak bir müjde olsun ve onunla kalpleriniz yatışsın diye yaptı. Yardım, ancak Allah'tandır. Kuşkusuz Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve ma cealehullahu illa buşra ve li tatmainne bihi kulubukum ve men nasru illa min indillah, innallahe azizun hakim."},
  {id: 1171, sure_no: 11, ayet_no: 11, sure_adi: "Enfal", text: "اِذْ يُغَشّ۪يكُمُ النُّعَاسَ اَمَنَةً مِنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِنَ السَّمَٓاءِ مَٓاءً لِيُطَهِّرَكُمْ بِه۪ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطَانِ وَلِيَرْبِطَ عَلٰى قُلُوبِكُمْ وَيُثَبِّتَ بِهِ الْاَقْدَامَۜ", translation: "Hani O, size Kendi katından bir emniyet olmak üzere bir uyku sardırıyordu. Sizi arındırmak, sizden şeytanın pisliğini gidermek, kalplerinizi yatıştırmak ve ayaklarınızı yere sabit kılmak için gökten üzerinize bir su indiriyordu.", transliteration: "İz yugaşşikumun nuase emeneten minhu ve yunezzilu aleykum mines semai maen li yutahhirekum bihi ve yuzhibe ankum riczeş şeytani ve li yerbıta ala kulubikum ve yusebbite bihil akdam."},
  {id: 1172, sure_no: 12, ayet_no: 12, sure_adi: "Enfal", text: "اِذْ يُوح۪ي رَبُّكَ اِلَى الْمَلٰٓئِكَةِ اَنّ۪ي مَعَكُمْ فَثَبِّتُوا الَّذ۪ينَ اٰمَنُواۜ سَاُلْق۪ي ف۪ي قُلُوبِ الَّذ۪ينَ كَفَرُوا الرُّعْبَ فَاضْرِبُوا فَوْقَ الْاَعْنَاقِ وَاضْرِبُوا مِنْهُمْ كُلَّ بَنَانٍۜ", translation: "Hani, Rabb'in meleklere, sizinle beraberim, diye vahyediyordu; \"İman Edenleri pekiştirin. Kafirlerin kalplerine korku salacağım. Haydi! Vurun onların boyunlarına, vurun onların parmaklarına.\"", transliteration: "İz yuhi rabbuke ilel melaiketi enni meakum fe sebbitullezine amenu, seulki fi kulubillezine keferur ru'be fadribu fevkal a'nakı vadribu minhum kulle benan."},
  {id: 1173, sure_no: 13, ayet_no: 13, sure_adi: "Enfal", text: "ذٰلِكَ بِاَنَّهُمْ شَٓاقُّوا اللّٰهَ وَرَسُولَهُۚ وَمَنْ يُشَاقِقِ اللّٰهَ وَرَسُولَهُ فَاِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ", translation: "Böyle! Çünkü onlar, Allah'a ve Resul'üne karşı geldiler. Kim Allah'a ve Resul'üne karşı gelirse, bilsin ki, Allah'ın Azabı Çok Çetindir.", transliteration: "Zalike bi ennehum şakkullahe ve resuluh, ve men yuşakıkıllahe ve resulehu fe innallahe şedidul ikab."},
  {id: 1174, sure_no: 14, ayet_no: 14, sure_adi: "Enfal", text: "ذٰلِكُمْ فَذُوقُوهُ وَاَنَّ لِلْكَافِر۪ينَ عَذَابَ النَّارِ", translation: "Şimdi onu tadın. Kafirler için yalnızca ateşin azabı vardır.", transliteration: "Zalikum fe zukuhu ve enne lil kafirine azaben nar."},
  {id: 1175, sure_no: 15, ayet_no: 15, sure_adi: "Enfal", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا لَق۪يتُمُ الَّذ۪ينَ كَفَرُوا زَحْفاً فَلَا تُوَلُّوهُمُ الْاَدْبَارَۚ", translation: "Ey İman Edenler! Kafirlerin orduları ile karşılaştığınız zaman, onlara arkanızı dönmeyin.", transliteration: "Ya eyyuhellezine amenu iza lekitumullezine keferu zahfen fe la tuvelluhumul edbar."},
  {id: 1176, sure_no: 16, ayet_no: 16, sure_adi: "Enfal", text: "وَمَنْ يُوَلِّهِمْ يَوْمَئِذٍ دُبُرَهُٓ اِلَّا مُتَحَرِّفاً لِقِتَالٍ اَوْ مُتَحَيِّزاً اِلٰى فِئَةٍ فَقَدْ بَٓاءَ بِغَضَبٍ مِنَ اللّٰهِ وَمَأْوٰيهُ جَهَنَّمُۜ وَبِئْسَ الْمَص۪يرُ", translation: "Kim böyle bir günde, savaşmak için bir cepheye çekilmek amacıyla veya başka bir birliğe katılmanın dışında, düşmana arkasını dönerse, Allah'ın gazabına uğramış olur. Onun varacağı yer Cehennem'dir. O, ne kötü bir dönüş yeridir.", transliteration: "Ve men yuvellihim yevmeizin duburehu illa muteharrifen li kıtalin ev mutehayyizen ila fietin fe kad bae bi gadabin minallahi ve me'vahu cehennem, ve bi'sel masir."},
  {id: 1177, sure_no: 17, ayet_no: 17, sure_adi: "Enfal", text: "فَلَمْ تَقْتُلُوهُمْ وَلٰكِنَّ اللّٰهَ قَتَلَهُمْۖ وَمَا رَمَيْتَ اِذْ رَمَيْتَ وَلٰكِنَّ اللّٰهَ رَمٰىۚ وَلِيُبْلِيَ الْمُؤْمِن۪ينَ مِنْهُ بَلَٓاءً حَسَناًۜ اِنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌ", translation: "Onları siz öldürmediniz, fakat Allah öldürdü. Attığın zaman da sen atmadın, fakat Allah attı. Allah, bunu Mü'minleri iyi bir sınavla sınava tabi tutmak için yaptı. Kuşkusuz Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Fe lem taktuluhum ve lakinnallahe katelehum, ve ma remeyte iz remeyte ve lakinnallahe rema, ve li yubliyel mu'minine minhu belaen hasena, innallahe semiun alim."},
  {id: 1178, sure_no: 18, ayet_no: 18, sure_adi: "Enfal", text: "ذٰلِكُمْ وَاَنَّ اللّٰهَ مُوهِنُ كَيْدِ الْكَافِر۪ينَ", translation: "İşte size böyle yardım etti. Allah, Kafirlerin planlarını bozandır.", transliteration: "Zalikum ve ennallahe muhinu keydil kafirin."},
  {id: 1179, sure_no: 19, ayet_no: 19, sure_adi: "Enfal", text: "اِنْ تَسْتَفْتِحُوا فَقَدْ جَٓاءَكُمُ الْفَتْحُۚ وَاِنْ تَنْتَهُوا فَهُوَ خَيْرٌ لَكُمْۚ وَاِنْ تَعُودُوا نَعُدْۚ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْـٔاً وَلَوْ كَـثُرَتْۙ وَاَنَّ اللّٰهَ مَعَ الْمُؤْمِن۪ينَ۟", translation: "Eğer fetih istiyorsanız, işte size fetih geldi. Eğer son verirseniz, o sizin için daha hayırlıdır. Eğer dönerseniz, Biz de döneriz. Topluluğunuz çok olsa da kesinlikle size bir fayda vermez. Kuşkusuz Allah, Mü'minlerle beraberdir.", transliteration: "İn testeftihu fe kad caekumul feth, ve in tentehu fe huve hayrun lekum, ve in teudu naud, ve len tugniye ankum fietukum şey'en ve lev kesuret ve ennallahe meal mu'minin."},
  {id: 1180, sure_no: 20, ayet_no: 20, sure_adi: "Enfal", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَط۪يعُوا اللّٰهَ وَرَسُولَهُ وَلَا تَوَلَّوْا عَنْهُ وَاَنْتُمْ تَسْمَعُونَۚ", translation: "Ey İman Edenler! Allah'a ve Resul'üne itaat edin. İşitip dururken ondan yüz çevirmeyin!", transliteration: "Ya eyyuhellezine amenu etiullahe ve resulehu ve la tevellev anhu ve entum tesmeun."},
  {id: 1181, sure_no: 21, ayet_no: 21, sure_adi: "Enfal", text: "وَلَا تَكُونُوا كَالَّذ۪ينَ قَالُوا سَمِعْنَا وَهُمْ لَا يَسْمَعُونَ", translation: "İşitmedikleri halde, işittik diyen kimseler gibi olmayın!", transliteration: "Ve la tekunu kellezine kalu semi'na ve hum la yesmeun."},
  {id: 1182, sure_no: 22, ayet_no: 22, sure_adi: "Enfal", text: "اِنَّ شَرَّ الدَّوَٓابِّ عِنْدَ اللّٰهِ الصُّمُّ الْبُكْمُ الَّذ۪ينَ لَا يَعْقِلُونَ", translation: "Allah katında, yeryüzündeki canlıların en değersizi, aklını kullanmayan sağırlar ve dilsizlerdir.", transliteration: "İnne şerred devabbi indallahis summul bukmullezine la ya'kılun."},
  {id: 1183, sure_no: 23, ayet_no: 23, sure_adi: "Enfal", text: "وَلَوْ عَلِمَ اللّٰهُ ف۪يهِمْ خَيْراً لَاَسْمَعَهُمْۜ وَلَوْ اَسْمَعَهُمْ لَتَوَلَّوْا وَهُمْ مُعْرِضُونَ", translation: "Eğer Allah, onlarda bir hayır olduğunu görseydi onlara işittirirdi. Onlara işittirseydi de aldırmayarak yine dönerlerdi.", transliteration: "Ve lev alimallahu fi him hayren le esmeahum, ve lev esmeahum le tevellev ve hum mu'ridune."},
  {id: 1184, sure_no: 24, ayet_no: 24, sure_adi: "Enfal", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اسْتَج۪يبُوا لِلّٰهِ وَلِلرَّسُولِ اِذَا دَعَاكُمْ لِمَا يُحْي۪يكُمْۚ وَاعْلَمُٓوا اَنَّ اللّٰهَ يَحُولُ بَيْنَ الْمَرْءِ وَقَلْبِه۪ وَاَنَّـهُٓ اِلَيْهِ تُحْشَرُونَ", translation: "Ey İman Edenler! Sizi, size hayat verecek şeylere çağırdığı zaman, Allah'a ve Resul'üne icabet edin. Bilin ki Allah, kişi ile kalbinin arasına girer. Kuşkusuz hepiniz O'na dönüp toplanacaksınız.", transliteration: "Ya eyyuhellezine amenustecibu lillahi ve lir resuli iza deakum lima yuhyikum, va'lemu ennallahe yehulu beynel mer'i ve kalbihi ve ennehu ileyhi tuhşerun."},
  {id: 1185, sure_no: 25, ayet_no: 25, sure_adi: "Enfal", text: "وَاتَّقُوا فِتْنَةً لَا تُص۪يبَنَّ الَّذ۪ينَ ظَلَمُوا مِنْكُمْ خَٓاصَّةًۚ وَاعْلَمُٓوا اَنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ", translation: "Yalnızca aranızdaki haksızlık edenlerin başlarına gelmekle sınırlı kalmayacak fitneye karşı takva sahibi olun. Unutmayın ki Allah'ın azabı çok çetindir.", transliteration: "Vetteku fitneten la tusibennellezine zalemu minkum hassah, va'lemu ennallahe şedidul ikab."},
  {id: 1186, sure_no: 26, ayet_no: 26, sure_adi: "Enfal", text: "وَاذْكُـرُٓوا اِذْ اَنْتُمْ قَل۪يلٌ مُسْتَضْعَفُونَ فِي الْاَرْضِ تَخَافُونَ اَنْ يَتَخَطَّفَكُمُ النَّاسُ فَاٰوٰيكُمْ وَاَيَّدَكُمْ بِنَصْرِه۪ وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Ve hatırlayın; bir zamanlar yeryüzünde sayıca azdınız ve mus'tezaf kimselerdiniz. İnsanların sizi alıp götürmesinden korkuyordunuz. O, sizi barındırdı ve yardımıyla kuvvetlendirdi. Temiz şeylerden size rızık verdi. O halde şükretmelisiniz.", transliteration: "Vezkuru iz entum kalilun mustad'afune fil ardı tehafune en yetehattafekumun nasu fe avakum ve eyyedekum bi nasrihi ve rezekakum minet tayyibati leallekum teşkurun."},
  {id: 1187, sure_no: 27, ayet_no: 27, sure_adi: "Enfal", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَخُونُوا اللّٰهَ وَالرَّسُولَ وَتَخُونُٓوا اَمَانَاتِكُمْ وَاَنْتُمْ تَعْلَمُونَ", translation: "Ey İman Edenler! Allah'a ve Resul'üne ihanet etmeyin. Yoksa bile bile emanetlerinize ihanet etmiş olursunuz.", transliteration: "Ya eyyuhellezine amenu la tehunullahe ver resule ve tehunu emanatikum ve entum ta'lemun."},
  {id: 1188, sure_no: 28, ayet_no: 28, sure_adi: "Enfal", text: "وَاعْلَمُٓوا اَنَّـمَٓا اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌۙ وَاَنَّ اللّٰهَ عِنْدَهُٓ اَجْرٌ عَظ۪يمٌ۟", translation: "Bilin ki mallarınız ve evlatlarınız ancak birer fitnedir. Kuşkusuz Allah'ın yanında çok büyük ödül vardır.", transliteration: "Va'lemu ennema emvalukum ve evladukum fitnetun ve ennallahe indehu ecrun azim."},
  {id: 1189, sure_no: 29, ayet_no: 29, sure_adi: "Enfal", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ تَتَّقُوا اللّٰهَ يَجْعَلْ لَكُمْ فُرْقَاناً وَيُكَفِّرْ عَنْكُمْ سَيِّـَٔاتِكُمْ وَيَغْفِرْ لَكُمْۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ", translation: "Ey İman Edenler! Eğer Allah için takvalı olursanız; O, size bir Furkan verir, kötülüklerinize kafirlik eder ve sizi bağışlar. Allah, Büyük Fazl Sahibidir.", transliteration: "Ya eyyuhellezine amenu in tettekullahe yec'al lekum furkanen ve yukeffir ankum seyyiatikum ve yagfir lekum, vallahu zul fadlil azim."},
  {id: 1190, sure_no: 30, ayet_no: 30, sure_adi: "Enfal", text: "وَاِذْ يَمْكُرُ بِكَ الَّذ۪ينَ كَفَرُوا لِيُثْبِتُوكَ اَوْ يَقْتُلُوكَ اَوْ يُخْرِجُوكَۜ وَيَمْكُرُونَ وَيَمْكُرُ اللّٰهُۜ وَاللّٰهُ خَيْرُ الْمَاكِر۪ينَ", translation: "Kafirler seni tutuklamak, öldürmek veya sürgün etmek için plan yapıyorlardı. Onlar plan yaparlarken, Allah da plan yapıyordu. Allah, plan yapanların en hayırlısıdır.", transliteration: "Ve iz yemkuru bikellezine keferu li yusbituke ev yaktuluke ev yuhricuk ve yemkurune ve yemkurullah, vallahu hayrul makirin."},
  {id: 1191, sure_no: 31, ayet_no: 31, sure_adi: "Enfal", text: "وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا قَالُوا قَدْ سَمِعْنَا لَوْ نَشَٓاءُ لَقُلْنَا مِثْلَ هٰذَٓاۙ اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ", translation: "Onlara, ayetlerimiz okunduğu zaman, \"Evet, biz bunu duyduk, dilersek biz de onun benzerini söyleriz; bu, evvelkilerin masallarından başka bir şey değildir.\" dediler.", transliteration: "Ve iza tutla aleyhim ayatuna kalu kad semi'na lev neşau le kulna misle haza in haza illa esatirul evvelin."},
  {id: 1192, sure_no: 32, ayet_no: 32, sure_adi: "Enfal", text: "وَاِذْ قَالُوا اللّٰهُمَّ اِنْ كَانَ هٰذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَاَمْطِرْ عَلَيْنَا حِجَارَةً مِنَ السَّمَٓاءِ اَوِ ائْتِنَا بِعَذَابٍ اَل۪يمٍ", translation: "Bir de dediler ki: \"Allah'ım! Eğer bu Senin tarafından gelen bir gerçekse, gökten üzerimize taş yağdır veya bize can yakıcı bir azap ver.\"", transliteration: "Ve iz kalullahumme in kane haza huvel hakka min indike fe emtir aleyna hıcareten mines semai evi'tina bi azabin elim."},
  {id: 1193, sure_no: 33, ayet_no: 33, sure_adi: "Enfal", text: "وَمَا كَانَ اللّٰهُ لِيُعَذِّبَهُمْ وَاَنْتَ ف۪يهِمْۜ وَمَا كَانَ اللّٰهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُونَ", translation: "Sen onların içindeyken, Allah, onlara azap etmez. Onlar, bağışlanma diledikleri sürece de Allah onlara azap edecek değildir.", transliteration: "Ve ma kanallahu li yuazzibehum ve ente fihim, ve ma kanallahu muazzibehum ve hum yestagfirun."},
  {id: 1194, sure_no: 34, ayet_no: 34, sure_adi: "Enfal", text: "وَمَا لَهُمْ اَلَّا يُعَذِّبَهُمُ اللّٰهُ وَهُمْ يَصُدُّونَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُٓوا اَوْلِيَٓاءَهُۜ اِنْ اَوْلِيَٓاؤُ۬هُٓ اِلَّا الْمُتَّقُونَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "Onlar, Mescid-i Haram'a girmeye engel oldukları halde ve onun velileri olmadıkları halde Allah neden onlara azap etmesin? Oysa oranın evliyası muttakilerdir. Fakat onların çoğu bilmezler.", transliteration: "Ve ma lehum ella yuazzibehumullahu ve hum yasuddune anil mescidil harami ve ma kanu evliyaehu, in evliyauhu illel muttekune ve lakinne ekserehum la ya'lemun."},
  {id: 1195, sure_no: 35, ayet_no: 35, sure_adi: "Enfal", text: "وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ اِلَّا مُكَٓاءً وَتَصْدِيَةًۜ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ", translation: "Onların, Beyt'in yanındaki salatları, ıslık çalmaktan ve el çırpmaktan başka bir şey değildir. Öyleyse küfrünüzden dolayı azabı tadın.", transliteration: "Ve ma kane salatuhum indel beyti illa mukaen ve tasdiyeh, fe zukul azabe bima kuntum tekfurun."},
  {id: 1196, sure_no: 36, ayet_no: 36, sure_adi: "Enfal", text: "اِنَّ الَّذ۪ينَ كَفَرُوا يُنْفِقُونَ اَمْوَالَهُمْ لِيَصُدُّوا عَنْ سَب۪يلِ اللّٰهِۜ فَسَيُنْفِقُونَهَا ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُونَۜ وَالَّذ۪ينَ كَفَرُٓوا اِلٰى جَهَنَّمَ يُحْشَرُونَۙ", translation: "Kafirler, Allah yolundan alıkoymak için mallarını harcarlar ve harcayacaklar da. Sonra, bu kendilerine pişmanlık olacak ve sonra mağlup olacaklar. Kafirler Cehennem'de toplanacaklardır.", transliteration: "İnnellezine keferu yunfikune emvalehum li yesuddu an sebilillah, fe seyunfikuneha summe tekunu aleyhim hasreten summe yuglebun, vellezine keferu ila cehenneme yuhşerun."},
  {id: 1197, sure_no: 37, ayet_no: 37, sure_adi: "Enfal", text: "لِيَم۪يزَ اللّٰهُ الْخَب۪يثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَب۪يثَ بَعْضَهُ عَلٰى بَعْضٍ فَيَرْكُمَهُ جَم۪يعاً فَيَجْعَلَهُ ف۪ي جَهَنَّمَۜ اُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ۟", translation: "Ki Allah, pis olanı temiz olandan ayırsın, pis olanları birbirinin üzerine koyup, hepsini bir araya getirsin, sonra hepsini Cehennem'e doldursun. İşte onlar hüsrana uğrayanlardır.", transliteration: "Li yemizallahul habise minet tayyibi ve yec'alel habise ba'dahu ala ba'dın fe yerkumehu cemian fe yec'alehu fi cehennem, ulaike humul hasirun."},
  {id: 1198, sure_no: 38, ayet_no: 38, sure_adi: "Enfal", text: "قُلْ لِلَّذ۪ينَ كَفَرُٓوا اِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ مَا قَدْ سَلَفَۚ وَاِنْ يَعُودُوا فَقَدْ مَضَتْ سُنَّتُ الْاَوَّل۪ينَ", translation: "Kafirlere de ki: Eğer vazgeçerlerse geçmişte yaptıkları bağışlanır. Eğer tekrar dönerlerse, evvelkilerin başına gelenler ortadadır.", transliteration: "Kul lillezine keferu in yentehu yugfer lehum ma kad selef, ve in yeudu fe kad madat sunnetul evvelin."},
  {id: 1199, sure_no: 39, ayet_no: 39, sure_adi: "Enfal", text: "وَقَاتِلُوهُمْ حَتّٰى لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدّ۪ينُ كُلُّهُ لِلّٰهِۚ فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ بِمَا يَعْمَلُونَ بَص۪يرٌ", translation: "Fitne kalmayıp, din tamamıyla Allah'ın oluncaya kadar onlarla savaşın. Eğer vazgeçerlerse kuşkusuz Allah onların ne yaptığını görmektedir.", transliteration: "Ve katiluhum hatta la tekune fitnetun ve yekuned dinu kulluhu lillahi, fe inintehev fe innallahe bima ya'melune basir."},
  {id: 1200, sure_no: 40, ayet_no: 40, sure_adi: "Enfal", text: "وَاِنْ تَوَلَّوْا فَاعْلَمُٓوا اَنَّ اللّٰهَ مَوْلٰيكُمْۜ نِعْمَ الْمَوْلٰى وَنِعْمَ النَّص۪يرُ", translation: "Eğer yüz çevirirlerse, artık bilin ki Allah sizin mevlanızdır. O, ne güzel mevla, ne güzel yardımcıdır.", transliteration: "Ve in tevellev fa'lemu ennallahe mevlakum, ni'mel mevla ve ni'men nasir."},
  {id: 1201, sure_no: 41, ayet_no: 41, sure_adi: "Enfal", text: "وَاعْلَمُٓوا اَنَّمَا غَنِمْتُمْ مِنْ شَيْءٍ فَاَنَّ لِلّٰهِ خُمُسَهُ وَلِلرَّسُولِ وَلِذِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَابْنِ السَّب۪يلِۙ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ وَمَٓا اَنْزَلْنَا عَلٰى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعَانِۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Eğer Allah'a, Hakk ile Batıl'ın birbirinden ayrıldığı gün; iki ordunun karşı karşıya geldiği günde, kulumuza indirdiğimize iman ediyorsanız, bilin ki: Ganimet olarak ele geçirdiklerinizin beşte biri Allah'ın ve Resulü'nün, yakınların, yetimlerin, düşkünlerin, yol oğlunundur. Allah, Her Şeye Güç Yetirendir.", transliteration: "Va'lemu ennema ganimtum min şey'in fe enne lillahi humusehu ve lir resuli ve li zil kurba vel yetama vel mesakini vebnis sebili in kuntum amentum billahi ve ma enzelna ala abdina yevmel furkani yevmettekal cem'an, vallahu ala kulli şey'in kadir."},
  {id: 1202, sure_no: 42, ayet_no: 42, sure_adi: "Enfal", text: "اِذْ اَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوٰى وَالرَّكْبُ اَسْفَلَ مِنْكُمْۜ وَلَوْ تَوَاعَدْتُمْ لَاخْتَلَفْتُمْ فِي الْم۪يعَادِۙ وَلٰكِنْ لِيَقْضِيَ اللّٰهُ اَمْراً كَانَ مَفْعُولاًۙ لِيَهْلِكَ مَنْ هَلَكَ عَنْ بَيِّنَةٍ وَيَحْيٰى مَنْ حَيَّ عَنْ بَيِّنَةٍۜ وَاِنَّ اللّٰهَ لَسَم۪يعٌ عَل۪يمٌۙ", translation: "Sizin vadinin bir ucunda, onların da öteki ucunda ve kervanın da sizden aşağıda olduğu o gün, eğer bilinen bir yerde buluşmak hususunda sözleşmiş olsaydınız dahi, anlaşmazlığa düşerdiniz. Ama Allah, gerçekleştirilmesi gereken bir işi yaptı; yok olan, apaçık bir kanıtla yok olsun, yaşayan da apaçık bir kanıtla yaşasın diye. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "İz entum bil udvetid dunya ve hum bil udvetil kusva verrekbu esfele minkum, ve lev tevaadtum lahteleftum fil miadi ve lakin li yakdiyallahu emren kane mef'ulen li yehlike men heleke an beyyinetin ve yahya men hayye an beyyineh, ve innallahe le semi'un alim."},
  {id: 1203, sure_no: 43, ayet_no: 43, sure_adi: "Enfal", text: "اِذْ يُر۪يكَهُمُ اللّٰهُ ف۪ي مَنَامِكَ قَل۪يلاًۜ وَلَوْ اَرٰيكَهُمْ كَث۪يراً لَفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِي الْاَمْرِ وَلٰكِنَّ اللّٰهَ سَلَّمَۜ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Hani Allah, onları sana uykunda az göstermişti. Eğer O, onları sana çok gösterseydi, tedirgin olup ne yapacağınız hususunda anlaşmazlığa düşerdiniz. Fakat Allah, sizi bu tehlikeden korudu. Zira O, göğüslerde olanları en iyi bilendir.", transliteration: "İz yurikehumullahu fi menamike kalilen, ve lev erakehum kesiren le feşiltum ve le tenaza'tum fil emri ve lakinnallahe sellem, innehu alimun bi zatis sudur."},
  {id: 1204, sure_no: 44, ayet_no: 44, sure_adi: "Enfal", text: "وَاِذْ يُر۪يكُمُوهُمْ اِذِ الْتَقَيْتُمْ ف۪ٓي اَعْيُنِكُمْ قَل۪يلاً وَيُقَلِّلُكُمْ ف۪ٓي اَعْيُنِهِمْ لِيَقْضِيَ اللّٰهُ اَمْراً كَانَ مَفْعُولاًۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ۟", translation: "Karşı karşıya geldiğinizde, Allah onları sizin gözünüzde, sizi de onların gözünde sayıca azmış gibi gösteriyordu ki takdir edilen işi yerine getirsin. Bütün işler yalnızca Allah'a döndürülür.", transliteration: "Ve iz yurikumuhum iziltekaytum fi a'yunikum kalilen ve yukallilukum fi a'yunihim li yakdıyallahu emren kane mef'ula, ve ilallahi turceul umur."},
  {id: 1205, sure_no: 45, ayet_no: 45, sure_adi: "Enfal", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا لَق۪يتُمْ فِئَةً فَاثْبُتُوا وَاذْكُرُوا اللّٰهَ كَث۪يراً لَعَلَّكُمْ تُفْلِحُونَۚ", translation: "Ey İman Edenler! Bir toplulukla karşılaştığınız zaman, kararlı ve dirençli olun ve Allah'ı çok anın ki kurtuluşa eresiniz.", transliteration: "Ya eyyuhellezine amenu iza lekitum fieten fesbutu vezkurullahe kesiren leallekum tuflihun."},
  {id: 1206, sure_no: 46, ayet_no: 46, sure_adi: "Enfal", text: "وَاَط۪يعُوا اللّٰهَ وَرَسُولَهُ وَلَا تَنَازَعُوا فَتَفْشَلُوا وَتَذْهَبَ ر۪يحُكُمْ وَاصْبِرُواۜ اِنَّ اللّٰهَ مَعَ الصَّابِر۪ينَۚ", translation: "Allah'a ve Resul'üne itaat edin. Birbirinizle çekişmeyin. Sonra zaafa düşüp zayıflarsınız. Sabredin. Kuşkusuz Allah, sabredenlerle beraberdir.", transliteration: "Ve etiullahe ve resulehu ve la tenazeu fe tefşelu ve tezhebe rihukum vasbiru, innallahe meas sabirin."},
  {id: 1207, sure_no: 47, ayet_no: 47, sure_adi: "Enfal", text: "وَلَا تَكُونُوا كَالَّذ۪ينَ خَرَجُوا مِنْ دِيَارِهِمْ بَطَراً وَرِئَٓاءَ النَّاسِ وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِۜ وَاللّٰهُ بِمَا يَعْمَلُونَ مُح۪يطٌ", translation: "Çalım satarak, insanlara gösteriş yaparak ve Allah'ın yolundan alıkoyarak yurtlarından çıkanlar gibi olmayın. Allah, onların yaptıklarını çepeçevre kuşatmıştır.", transliteration: "Ve la tekunu kellezine harecu min diyarihim bataran ve riaen nasi ve yasuddune an sebilillah, vallahu bima ya'melune muhit."},
  {id: 1208, sure_no: 48, ayet_no: 48, sure_adi: "Enfal", text: "وَاِذْ زَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَاِنّ۪ي جَارٌ لَكُمْۚ فَلَمَّا تَرَٓاءَتِ الْفِئَتَانِ نَكَصَ عَلٰى عَقِبَيْهِ وَقَالَ اِنّ۪ي بَر۪ٓيءٌ  مِنْكُمْ اِنّ۪ٓي اَرٰى مَا لَا تَرَوْنَ اِنّ۪ٓي اَخَافُ اللّٰهَۜ وَاللّٰهُ شَد۪يدُ الْعِقَابِ۟", translation: "Hani, şeytan onlara yaptıkları işi süslü gösterip: \"Bugün, sizi hiçbir insan topluluğu yenemez, ben de muhakkak sizin yardımcınızım.\" dedi. Fakat iki ordu karşılaşınca geri döndü ve \"Ben sizden uzağım, ben sizin görmediğinizi görmekteyim, ben Allah'tan korkarım. Zira Allah'ın azabı çok çetindir.\" dedi.", transliteration: "Ve iz zeyyene lehumuş şeytanu a'malehum ve kale la galibe lekumul yevme minen nasi ve inni carun lekum, fe lemma teraetil fietani nekesa ala akıbeyhi ve kale inni beriun minkum inni era ma la terevne inni ehafullah, vallahu şedidul ıkab."},
  {id: 1209, sure_no: 49, ayet_no: 49, sure_adi: "Enfal", text: "اِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ غَرَّ هٰٓؤُ۬لَٓاءِ د۪ينُهُمْۜ وَمَنْ يَتَوَكَّلْ عَلَى اللّٰهِ فَاِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ", translation: "Münafıklar, o kalplerinde hastalık bulunanlar: \"Bunları, dinleri aldatmış.\" diyorlardı. Oysa kim Allah'a tevekkül ederse Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "İz yekulul munafikune vellezine fi kulubihim maradun garrehaulai dinuhum, ve men yetevekkel alallahi fe innallahe azizun hakim."},
  {id: 1210, sure_no: 50, ayet_no: 50, sure_adi: "Enfal", text: "وَلَوْ تَرٰٓى اِذْ يَتَوَفَّى الَّذ۪ينَ كَفَرُواۙ الْمَلٰٓئِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَاَدْبَارَهُمْۚ وَذُوقُوا عَذَابَ الْحَر۪يقِ", translation: "Şayet görüp tanıklık etseydin eğer! Melekler, Kafirlerin canlarını alırken, yüzlerine ve sırtlarına vuruyorlar ve \"Kavurucu azabı tadın.\" diyorlar.", transliteration: "Ve lev tera iz yeteveffellezine keferul melaiketu yadrıbune vucuhehum ve edbarehum, ve zuku azabel harik."},
  {id: 1211, sure_no: 51, ayet_no: 51, sure_adi: "Enfal", text: "ذٰلِكَ بِمَا قَدَّمَتْ اَيْد۪يكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِلْعَب۪يدِۙ", translation: "İşte bu, kendi ellerinizle yaptıklarınızın karşılığıdır, yoksa Allah kullarına asla haksızlık etmez.", transliteration: "Zalike bima kaddemet eydikum ve ennallahe leyse bi zallamin lil abid."},
  {id: 1212, sure_no: 52, ayet_no: 52, sure_adi: "Enfal", text: "كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَفَرُوا بِاٰيَاتِ اللّٰهِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوبِهِمْۜ اِنَّ اللّٰهَ قَوِيٌّ شَد۪يدُ الْعِقَابِ", translation: "Tıpkı Firavuncuların ve onlardan öncekilerin durumu gibi. Allah'ın ayetlerini yalanlamışlardı. Allah da onları, suçları yüzünden yakalamıştı. Allah, Mutlak Güç Sahibi'dir, Cezalandırması Çok Şiddetli Olan'dır.", transliteration: "Ke de'bi ali fir'avne vellezine min kablihim, keferu bi ayatillahi fe ehazehumullahu bi zunubihim, innallahe kaviyyun şedidul ıkab."},
  {id: 1213, sure_no: 53, ayet_no: 53, sure_adi: "Enfal", text: "ذٰلِكَ بِاَنَّ اللّٰهَ لَمْ يَكُ مُغَيِّراً نِعْمَةً اَنْعَمَهَا عَلٰى قَوْمٍ حَتّٰى يُغَيِّرُوا مَا بِاَنْفُسِهِمْۙ وَاَنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌۙ", translation: "Çünkü bir halk kendisini değiştirmedikçe, Allah onlara verdiği nimeti değiştirecek değildir. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Zalike biennallahe lem yeku mugayyiren ni'meten en'ameha ala kavmin hatta yugayyiru ma bi enfusihim ve ennallahe semiun alim."},
  {id: 1214, sure_no: 54, ayet_no: 54, sure_adi: "Enfal", text: "كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَذَّبُوا بِاٰيَاتِ رَبِّهِمْۚ فَاَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَاَغْرَقْـنَٓا اٰلَ فِرْعَوْنَۚ وَكُلٌّ كَانُوا ظَالِم۪ينَ", translation: "Tıpkı Firavuncuların ve onlardan öncekilerin durumu gibi. Rabb'lerinin ayetlerini yalanlamışlardı da Biz de suçlarından dolayı onları mahvetmiş ve Firavuncuları suda boğmuştuk. Hepsi de zalim kimselerdi.", transliteration: "Ke de'bi ali fir'avne vellezine min kablihim, kezzebu biayati rabbihim, fe ehleknahum bi zunubihim ve agrakna ale fir'avn, ve kullun kanu zalimin."},
  {id: 1215, sure_no: 55, ayet_no: 55, sure_adi: "Enfal", text: "اِنَّ شَرَّ الدَّوَٓابِّ عِنْدَ اللّٰهِ الَّذ۪ينَ كَفَرُوا فَهُمْ لَا يُؤْمِنُونَۚ", translation: "Allah katında canlıların en şerlisi, Kafirlerdir. Artık onlar inanmazlar;", transliteration: "İnne şerred devabbi indallahillezine keferu fe hum la yu'minun."},
  {id: 1216, sure_no: 56, ayet_no: 56, sure_adi: "Enfal", text: "اَلَّذ۪ينَ عَاهَدْتَ مِنْهُمْ ثُمَّ يَنْقُضُونَ عَهْدَهُمْ ف۪ي كُلِّ مَرَّةٍ وَهُمْ لَا يَتَّقُونَ", translation: "Onlar, kendileriyle antlaşma yaptığın halde, her defasında hiç çekinmeden antlaşmalarını bozan kimselerdir.", transliteration: "Ellezine ahedte minhum summe yenkudune ahdehum fi kulli merretin ve hum la yettekun."},
  {id: 1217, sure_no: 57, ayet_no: 57, sure_adi: "Enfal", text: "فَاِمَّا تَثْقَفَنَّهُمْ فِي الْحَرْبِ فَشَرِّدْ بِهِمْ مَنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَ", translation: "Eğer savaşta onları yakalarsan, geride kalanlara ders olacak şekilde onları darmadağın et. Belki akıllarını başlarına alırlar.", transliteration: "Fe imma teskafennehum fil harbi feşerrid bihim men halfehum leallehum yezzekkerun."},
  {id: 1218, sure_no: 58, ayet_no: 58, sure_adi: "Enfal", text: "وَاِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْبِذْ اِلَيْهِمْ عَلٰى سَوَٓاءٍۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْخَٓائِن۪ينَ۟", translation: "Eğer bir halkın ihanetinden endişe edersen, onlarla aynı şekilde anlaşmayı boz. Kuşkusuz Allah, hainlik yapanları sevmez.", transliteration: "Ve imma tehafenne min kavmin hiyaneten fenbiz ileyhim ala sevain, innallahe la yuhıbbul hainin."},
  {id: 1219, sure_no: 59, ayet_no: 59, sure_adi: "Enfal", text: "وَلَا يَحْسَبَنَّ الَّذ۪ينَ كَفَرُوا سَبَقُواۜ اِنَّهُمْ لَا يُعْجِزُونَ", translation: "Kafirler kendilerinden vazgeçildiğini sanmasınlar. Onlar, Bizi aciz bırakamazlar.", transliteration: "Ve la yahsebennellezine keferu sebeku, innehum la yu'cizun."},
  {id: 1220, sure_no: 60, ayet_no: 60, sure_adi: "Enfal", text: "وَاَعِدُّوا لَهُمْ مَا اسْتَطَعْتُمْ مِنْ قُوَّةٍ وَمِنْ رِبَاطِ الْخَيْلِ تُرْهِبُونَ بِه۪ عَدُوَّ اللّٰهِ وَعَدُوَّكُمْ وَاٰخَر۪ينَ مِنْ دُونِهِمْۚ لَا تَعْلَمُونَهُمْۚ اَللّٰهُ يَعْلَمُهُمْۜ وَمَا تُنْفِقُوا مِنْ شَيْءٍ ف۪ي سَب۪يلِ اللّٰهِ يُوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُونَ", translation: "Siz de onlara karşı gücünüzün yettiği kadar güç ve savaş atları hazırlayın ki, bununla Allah'ın ve sizin düşmanınızı ve onların dışındaki sizin bilmeyip, Allah'ın bildiği düşmanları korkutasınız. Allah yolunda her ne harcarsanız, size tam olarak ödenir. Ve hiçbir haksızlığa uğratılmazsınız.", transliteration: "Ve eıddu lehum mesteta'tum min kuvvetin ve min rıbatil hayli turhibune bihi aduvvallahi ve aduvvekum ve aharine min dunihim, la ta'lemunehum, allahu ya'lemuhum, ve ma tunfiku min şey'in fi sebilillahi yuveffe ileykum ve entum la tuzlemun."},
  {id: 1221, sure_no: 61, ayet_no: 61, sure_adi: "Enfal", text: "وَاِنْ جَنَحُوا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللّٰهِۜ اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Eğer onlar barışa yanaşırlarsa, sen de yanaş ve Allah'a tevekkül et. Kuşkusuz O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve in cenehu lis selmi fecnah leha ve tevekkel alallah, innehu huves semiul alim."},
  {id: 1222, sure_no: 62, ayet_no: 62, sure_adi: "Enfal", text: "وَاِنْ يُر۪يدُٓوا اَنْ يَخْدَعُوكَ فَاِنَّ حَسْبَكَ اللّٰهُۜ هُوَ الَّـذ۪ٓي اَيَّدَكَ بِنَصْرِه۪ وَبِالْمُؤْمِن۪ينَۙ", translation: "Eğer sana hile yapmak isterlerse, şunu bil ki Allah sana yeter. O, seni yardımı ile ve Mü'minlerle destekledi.", transliteration: "Ve in yuridu en yahdeuke feinne hasbekallah, huvellezi eyyedeke bi nasrihi ve bilmu'minin."},
  {id: 1223, sure_no: 63, ayet_no: 63, sure_adi: "Enfal", text: "وَاَلَّفَ بَيْنَ قُلُوبِهِمْۜ لَوْ اَنْفَقْتَ مَا فِي الْاَرْضِ جَم۪يعاً مَٓا اَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَلٰكِنَّ اللّٰهَ اَلَّفَ بَيْنَهُمْۜ اِنَّهُ عَز۪يزٌ حَك۪يمٌ", translation: "Ve onların kalplerini uzlaştırdı. Eğer yeryüzündeki her şeyi infak etseydin yine de onların kalplerini kaynaştıramazdın. Fakat Allah, onları kaynaştırdı. Kuşkusuz O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve ellefe beyne kulubihim, lev enfakte ma fil ardı cemian ma ellefte beyne kulubihim ve lakinnallahe ellefe beynehum, innehu azizun hakim."},
  {id: 1224, sure_no: 64, ayet_no: 64, sure_adi: "Enfal", text: "يَٓا اَيُّهَا النَّبِيُّ حَسْبُكَ اللّٰهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِن۪ينَ۟", translation: "Ey Nebi! Allah, sana ve sana uyan Mü'minlere yeter.", transliteration: "Ya eyyuhennebiyyu hasbukallahu ve menittebeake minel mu'minin."},
  {id: 1225, sure_no: 65, ayet_no: 65, sure_adi: "Enfal", text: "يَٓا اَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِن۪ينَ عَلَى الْقِتَالِۜ اِنْ يَكُنْ مِنْكُمْ عِشْرُونَ صَابِرُونَ يَغْلِبُوا مِائَتَيْنِۚ وَاِنْ يَكُنْ مِنْكُمْ مِائَةٌ يَغْلِبُٓوا اَلْفاً مِنَ الَّذ۪ينَ كَفَرُوا بِاَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ", translation: "Ey Nebi! Mü'minleri savaşmaya cesaretlendir. Eğer sizden sabreden yirmi kişi bulunursa, iki yüz kişiye galip gelir. Eğer sizden yüz kişi bulunursa, Kafirlerden bin kişiye galip gelir. Çünkü onlar gerçekten anlamaz bir halktır.", transliteration: "Ya eyyuhen nebiyyu harridıl mu'minine alel kıtal, in yekun minkum işrune sabirune yaglibu mieteyn, ve in yekun minkum mietun yaglibu elfen minellezine keferu bi ennehum kavmun la yefkahun."},
  {id: 1226, sure_no: 66, ayet_no: 66, sure_adi: "Enfal", text: "اَلْـٰٔنَ خَفَّفَ اللّٰهُ عَنْكُمْ وَعَلِمَ اَنَّ ف۪يكُمْ ضَعْفاًۜ فَاِنْ يَكُنْ مِنْكُمْ مِائَةٌ صَابِرَةٌ يَغْلِبُوا مِائَتَيْنِۚ وَاِنْ يَكُنْ مِنْكُمْ اَلْفٌ يَغْلِبُٓوا اَلْفَيْنِ بِاِذْنِ اللّٰهِۜ وَاللّٰهُ مَعَ الصَّابِر۪ينَ", translation: "Şimdi Allah, yükünüzü hafifletti. Çünkü sizde bir zayıflık olduğunu bilmektedir. Yine de, eğer sizden sabırlı yüz kişi bulunursa iki yüz kişiye galip gelir. Eğer sizden bin kişi olursa Allah'ın izni ile iki bin kişiye galip gelir. Allah, sabredenlerle beraberdir.", transliteration: "El'ane haffefallahu ankum ve alime enne fikum da'fa, fe in yekun minkum mietun sabiretun yaglibu mieteyn, ve in yekun minkum elfun yaglibu elfeyni bi iznillah, vallahu meas sabirin."},
  {id: 1227, sure_no: 67, ayet_no: 67, sure_adi: "Enfal", text: "مَا كَانَ لِنَبِيٍّ اَنْ يَكُونَ لَـهُٓ اَسْرٰى حَتّٰى يُثْخِنَ فِي الْاَرْضِۜ تُر۪يدُونَ عَرَضَ الدُّنْيَاۗ وَاللّٰهُ يُر۪يدُ الْاٰخِرَةَۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ", translation: "Hiçbir Neb'iye, yeryüzünde düşmana üstünlük sağlayıncaya kadar, esir almak yaraşmaz. Siz geçici dünya malını istiyorsunuz, oysa Allah ahireti istiyor. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ma kane li nebiyyin en yekune lehu esra hatta yushıne fil ard, turidune aradad dunya, vallahu yuridul ahıreh, vallahu azizun hakim."},
  {id: 1228, sure_no: 68, ayet_no: 68, sure_adi: "Enfal", text: "لَوْلَا كِتَابٌ مِنَ اللّٰهِ سَبَقَ لَمَسَّكُمْ ف۪يمَٓا اَخَذْتُمْ عَذَابٌ عَظ۪يمٌ", translation: "Eğer Allah tarafından önceden bir Kitap olmasaydı, aldığınız şeyden dolayı size mutlaka büyük bir azap dokunurdu.", transliteration: "Lev la kitabun minallahi sebeka le messekum fima ehaztum azabun azim."},
  {id: 1229, sure_no: 69, ayet_no: 69, sure_adi: "Enfal", text: "فَكُلُوا مِمَّا غَنِمْتُمْ حَلَالاً طَيِّباًۘ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟", translation: "Artık elde ettiğiniz ganimetten, helal ve temiz olarak yiyin ve Allah için takvalı olun. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Fe kulu mimma ganimtum halalen tayyiben vettekullah, innallahe gafurun rahim."},
  {id: 1230, sure_no: 70, ayet_no: 70, sure_adi: "Enfal", text: "يَٓا اَيُّهَا النَّبِيُّ قُلْ لِمَنْ ف۪ٓي اَيْد۪يكُمْ مِنَ الْاَسْرٰٓىۙ اِنْ يَعْلَمِ اللّٰهُ ف۪ي قُلُوبِكُمْ خَيْراً يُؤْتِكُمْ خَيْراً مِمَّٓا اُخِذَ مِنْكُمْ وَيَغْفِرْ لَكُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "Ey Nebi! Elinizdeki esirlere de ki: \"Eğer Allah kalplerinizde bir hayır olduğunu bilirse, size, sizden alınandan daha hayırlısını verir ve sizi bağışlar.\" Allah'ın Rahmeti Bol'dur, Kesintisiz'dir.", transliteration: "Ya eyyuhen nebiyyu kul li men fi eydikum minel esra in ya'lemillahu fi kulubikum hayren yu'tikum hayren mimma uhıze minkum ve yagfirlekum, vallahu gafurun rahim."},
  {id: 1231, sure_no: 71, ayet_no: 71, sure_adi: "Enfal", text: "وَاِنْ يُر۪يدُوا خِيَانَتَكَ فَقَدْ خَانُوا اللّٰهَ مِنْ قَبْلُ فَاَمْكَنَ مِنْهُمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Eğer sana ihanet etmek isterlerse; daha önce Allah'a da ihanet etmişlerdi. Allah, onlara karşı sana imkan verdi. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve in yuridu hıyaneteke fe kad hanullahe min kablu fe emkene minhum, vallahu alimun hakim."},
  {id: 1232, sure_no: 72, ayet_no: 72, sure_adi: "Enfal", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَهَاجَرُوا وَجَاهَدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ ف۪ي سَب۪يلِ اللّٰهِ وَالَّذ۪ينَ اٰوَوْا وَنَصَرُٓوا اُو۬لٰٓئِكَ بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۜ وَالَّذ۪ينَ اٰمَنُوا وَلَمْ يُهَاجِرُوا مَا لَكُمْ مِنْ وَلَايَتِهِمْ مِنْ شَيْءٍ حَتّٰى يُهَاجِرُواۚ وَاِنِ اسْتَنْصَرُوكُمْ فِي الدّ۪ينِ فَعَلَيْكُمُ النَّصْرُ اِلَّا عَلٰى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ م۪يثَاقٌۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "İman edip, hicret edenler, malları ve canları ile Allah yolunda cihat edenler, barındırıp yardım edenler; işte onlar birbirlerinin velileridirler. İman edip de hicret etmeyenlere gelince, hicret edinceye kadar onların velayetinden size bir şey yoktur. Eğer onlar din hususunda sizden yardım isterlerse, onlara yardım etmek üzerinize borçtur. Ancak aranızda anlaşma bulunan bir halkın aleyhine olursa, o bu hükmün dışındadır. Allah, yaptığınız her şeyi görmektedir.", transliteration: "İnnellezine amenu ve haceru ve cahedu bi emvalihim ve enfusihim fi sebilillahi vellezine avev ve nasaru ulaike ba'duhum evliyau ba'd, vellezine amenu ve lem yuhaciru ma lekum min velayetihim min şey'in hatta yuhaciru, ve inistensarukum fid dini fe aleykumun nasru illa ala kavmin beynekum ve beynehum misak, vallahu bima ta'melune basir."},
  {id: 1233, sure_no: 73, ayet_no: 73, sure_adi: "Enfal", text: "وَالَّذ۪ينَ كَفَرُوا بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۜ اِلَّا تَفْعَلُوهُ تَكُنْ فِتْنَةٌ فِي الْاَرْضِ وَفَسَادٌ كَب۪يرٌۜ", translation: "Kafirler birbirlerinin evliyasıdır. Eğer siz de bunu yapmazsanız, yeryüzünde fitne ve büyük kargaşa olur.", transliteration: "Vellezine keferu ba'duhum evliyau ba'd, illa tef'aluhu tekun fitnetun fil ardı ve fesadun kebir."},
  {id: 1234, sure_no: 74, ayet_no: 74, sure_adi: "Enfal", text: "وَالَّذ۪ينَ اٰمَنُوا وَهَاجَرُوا وَجَاهَدُوا ف۪ي سَب۪يلِ اللّٰهِ وَالَّذ۪ينَ اٰوَوْا وَنَصَرُٓوا اُو۬لٰٓئِكَ هُمُ الْمُؤْمِنُونَ حَقاًّۜ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ", translation: "İman edip hicret edenler, Allah yolunda cihat edenler; barındıran ve yardım edenler işte onlar gerçek Mü'minlerdir. Onlar için bağışlanma ve kerim bir rızık vardır.", transliteration: "Vellezine amenu ve haceru ve cahedu fi sebilillahi vellezine avev ve nasaru ulaike humul mu'minune hakka, lehum magfiretun ve rizkun kerim."},
  {id: 1235, sure_no: 75, ayet_no: 75, sure_adi: "Enfal", text: "وَالَّذ۪ينَ اٰمَنُوا مِنْ بَعْدُ وَهَاجَرُوا وَجَاهَدُوا مَعَكُمْ فَاُو۬لٰٓئِكَ مِنْكُمْۜ وَاُو۬لُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ ف۪ي كِتَابِ اللّٰهِۜ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Sonradan iman edip de hicret edenler ve sizinle birlikte cihat edenler sizdendir. Allah'ın Kitap'ına göre rahim sahipleri önceliklidir. Kuşkusuz Allah, Her Şeyi Bilen'dir.", transliteration: "Vellezine amenu min ba'du ve haceru ve cahedu meakum fe ulaike minkum, ve ulul erhami ba'duhum evla biba'dın fi kitabillah, innallahe bi kulli şey'in alim."},
  {id: 1236, sure_no: 1, ayet_no: 1, sure_adi: "Tevbe", text: "بَرَٓاءَةٌ مِنَ اللّٰهِ وَرَسُولِه۪ٓ اِلَى الَّذ۪ينَ عَاهَدْتُمْ مِنَ الْمُشْرِك۪ينَۜ", translation: "Antlaşma yapılan Müşrik kimselere Allah ve Resul'ünden bir beraettir:", transliteration: "Beraetun minallahi ve resulihi ilellezine ahedtum minel muşrikin ."},
  {id: 1237, sure_no: 2, ayet_no: 2, sure_adi: "Tevbe", text: "فَس۪يحُوا فِي الْاَرْضِ اَرْبَعَةَ اَشْهُرٍ وَاعْلَمُٓوا اَنَّكُمْ غَيْرُ مُعْجِزِي اللّٰهِۙ وَاَنَّ اللّٰهَ مُخْزِي الْكَافِر۪ينَ", translation: "Yeryüzünde dört ay daha dolaşın ve bilin ki Allah'ı aciz bırakamazsınız. Allah, Kafirleri rezil edecektir.", transliteration: "Fesihu fil ardı erbeate eşhurin va'lemu ennekum gayru mu'cizillahi ve ennallahe muhzil kafirin."},
  {id: 1238, sure_no: 3, ayet_no: 3, sure_adi: "Tevbe", text: "وَاَذَانٌ مِنَ اللّٰهِ وَرَسُولِه۪ٓ اِلَى النَّاسِ يَوْمَ الْحَجِّ الْاَكْبَرِ اَنَّ اللّٰهَ بَر۪ٓيءٌ مِنَ الْمُشْرِك۪ينَۙ وَرَسُولُهُۜ  فَاِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَكُمْۚ وَاِنْ تَوَلَّيْتُمْ فَاعْلَمُٓوا اَنَّكُمْ غَيْرُ مُعْجِزِي اللّٰهِۜ وَبَشِّرِ الَّذ۪ينَ كَفَرُوا بِعَذَابٍ اَل۪يمٍۙ", translation: "Bu Allah ve Resul'ünden; Hacc-ı Ekber gününde, insanlara bir duyurudur. Allah ve Resul'ü Müşriklerden uzaktır. Ey Müşrikler! Eğer tevbe ederseniz, bu sizin için hayırlı olandır. Eğer yüz çevirirseniz, iyi bilin ki Allah'ı aciz bırakacak değilsiniz. Kafirleri can yakıcı bir azaptan haberdar et.", transliteration: "Ve ezanun minallahi ve resulihi ilen nasi yevmel haccıl ekberi ennallahe beriun minel muşrikine ve resuluh , fe in tubtum fe huve hayrun lekum, ve in tevelleytum fa'lemu ennekum gayru mu'cizillah , ve beşşirillezine keferu bi azabin elim."},
  {id: 1239, sure_no: 4, ayet_no: 4, sure_adi: "Tevbe", text: "اِلَّا الَّذ۪ينَ عَاهَدْتُمْ مِنَ الْمُشْرِك۪ينَ ثُمَّ لَمْ يَنْقُصُوكُمْ شَيْـٔاً وَلَمْ يُظَاهِرُوا عَلَيْكُمْ اَحَداً فَاَتِمُّٓوا اِلَيْهِمْ عَهْدَهُمْ اِلٰى مُدَّتِهِمْۜ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّق۪ينَ", translation: "Ancak, kendileriyle yaptığınız antlaşmanın hükümlerine eksiksiz uyan ve size karşı başkalarıyla iş birliğinde bulunmayan Müşrikler müstesna. Onlarla antlaşmanın süresini tamamlayın. Allah, takva sahiplerini sever.", transliteration: "İllellezine ahedtum minel muşrikine summe lem yankusukum şey'en ve lem yuzahiru aleykum ehaden fe etimmu ileyhim ahdehum ila muddetihim, innallahe yuhıbbul muttekin ."},
  {id: 1240, sure_no: 5, ayet_no: 5, sure_adi: "Tevbe", text: "فَاِذَا انْسَلَخَ الْاَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِك۪ينَ حَيْثُ وَجَدْتُمُوهُمْ وَخُذُوهُمْ وَاحْصُرُوهُمْ وَاقْعُدُوا لَهُمْ كُلَّ مَرْصَدٍۚ فَاِنْ تَابُوا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَخَلُّوا سَب۪يلَهُمْۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Haram aylar çıktığı zaman, artık \"o Müşrikleri\" nerede bulursanız öldürün, onları yakalayıp hapsedin, bütün geçit yerlerinde onları gözetleyin. Eğer tevbe edip, salatı ikame eder, zekatı yaparlarsa diledikleri yolu seçsinler. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Fe izenselehal eşhurul hurumu faktulul muşrikine haysu vecedtumuhum ve huzuhum vahsuruhum vak'udu lehum kulle marsad , fe in tabu ve ekamus salate ve atuz zekate fe hallu sebilehum, innallahe gafurun rahim."},
  {id: 1241, sure_no: 6, ayet_no: 6, sure_adi: "Tevbe", text: "وَاِنْ اَحَدٌ مِنَ الْمُشْرِك۪ينَ اسْتَجَارَكَ فَاَجِرْهُ حَتّٰى يَسْمَعَ كَلَامَ اللّٰهِ ثُمَّ اَبْلِغْهُ مَأْمَنَهُۜ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَا يَعْلَمُونَ۟", translation: "Eğer Müşriklerden biri senden korunma isterse, ona bu korumayı sağla ki Allah'ın sözlerini öğrenip anlama imkanı bulabilsin. Sonra da onu güven içinde olacağı yere ulaştır. Zira bunlar, gerçeği bilmeyen bir halktır.", transliteration: "Ve in ehadun minel muşrikinestecareke fe ecirhu hatta yesmea kelamallahi summe eblighu me'meneh, zalike bi ennehum kavmun la ya'lemun."},
  {id: 1242, sure_no: 7, ayet_no: 7, sure_adi: "Tevbe", text: "كَيْفَ يَكُونُ لِلْمُشْرِك۪ينَ عَهْدٌ عِنْدَ اللّٰهِ وَعِنْدَ رَسُولِه۪ٓ اِلَّا الَّذ۪ينَ عَاهَدْتُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِۚ فَمَا اسْتَقَامُوا لَكُمْ فَاسْتَق۪يمُوا لَهُمْۜ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّق۪ينَ", translation: "Yaptıkları antlaşmayı bozan Müşriklerin, Allah ve Resul'ünün yanında nasıl itibarları olabilir ki? Kendileriyle Mescid-i Haram yanında sözleşme yaptıklarınızın durumu başkadır. Onlar, size karşı sözlerinde durdukları sürece siz de sözünüzde durun. Allah, takva sahibi olanları sever.", transliteration: "Keyfe yekunu lil muşrikine ahdun ındallahi ve ınde resulihi illellezine ahedtum ındel mescidil haram, fe mestekamu lekum festekimu lehum, innallahe yuhıbbul muttekin."},
  {id: 1243, sure_no: 8, ayet_no: 8, sure_adi: "Tevbe", text: "كَيْفَ وَاِنْ يَظْهَرُوا عَلَيْكُمْ لَا يَرْقُبُوا ف۪يكُمْ اِلاًّ وَلَا ذِمَّةًۜ يُرْضُونَكُمْ بِاَفْوَاهِهِمْ وَتَأْبٰى قُلُوبُهُمْۚ وَاَكْثَرُهُمْ فَاسِقُونَۚ", translation: "Nasıl olabilir ki? Onlar, size galip gelmiş olsalar, hakkınızda ne bir antlaşma ne de bir yükümlülük gözetmezler. Kalben istemedikleri halde, dilleriyle sizi hoşnut etmeye çalışırlar. Onların çoğu fasıktırlar.", transliteration: "Keyfe ve in yazheru aleykum la yerkubu fikum illen ve la zimmeh , yurdunekum bi efvahihim ve te'ba kulubuhum, ve ekseruhum fasikun."},
  {id: 1244, sure_no: 9, ayet_no: 9, sure_adi: "Tevbe", text: "اِشْتَرَوْا بِاٰيَاتِ اللّٰهِ ثَمَناً قَل۪يلاً فَصَدُّوا عَنْ سَب۪يلِه۪ۜ اِنَّهُمْ سَٓاءَ مَا كَانُوا يَعْمَلُونَ", translation: "Allah'ın ayetlerini az bir bedel karşılığında satıp, O'nun yolundan alıkoydular. Gerçekten onların yaptıkları çok kötüdür!", transliteration: "İşterev bi ayatillahi semenen kalilen fe saddu an sebilih,innehum sae ma kanu ya'melun."},
  {id: 1245, sure_no: 10, ayet_no: 10, sure_adi: "Tevbe", text: "لَا يَرْقُبُونَ ف۪ي مُؤْمِنٍ اِلاًّ وَلَا ذِمَّةًۜ  وَاُو۬لٰٓئِكَ هُمُ الْمُعْتَدُونَ", translation: "Onlar, bir Mü'min hakkında ne bir antlaşma ne de bir yükümlülük gözetirler. İşte saldırganlar, onlardır.", transliteration: "La yerkubune fi mu'minin illen ve la zimmeh, ve ulaike humul mu'tedun."},
  {id: 1246, sure_no: 11, ayet_no: 11, sure_adi: "Tevbe", text: "فَاِنْ تَابُوا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَاِخْوَانُكُمْ فِي الدّ۪ينِۜ وَنُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ", translation: "Eğer tevbe eder, \"salatı ikame eder ve zekatı yaparlarsa,\" onlar artık dinde sizin kardeşlerinizdir. Biz, ayetleri, bilen bir kavim için böyle ayrıntılı bir şekilde açıklıyoruz.", transliteration: "Fe in tabu ve ekamus salate ve atuz zekate fe ıhvanukum fid din , ve nufassılul ayati li kavmin ya'lemun."},
  {id: 1247, sure_no: 12, ayet_no: 12, sure_adi: "Tevbe", text: "وَاِنْ نَكَثُٓوا اَيْمَانَهُمْ مِنْ بَعْدِ عَهْدِهِمْ وَطَعَنُوا ف۪ي د۪ينِكُمْ فَقَاتِلُٓوا اَئِمَّةَ الْكُفْرِۙ اِنَّهُمْ لَٓا اَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُونَ", translation: "Eğer onlar, antlaşmalarından sonra yeminlerini bozup dininize dil uzatırlarsa, küfrün öncüleri ile savaşın. Çünkü onların yeminleri yok hükmündedir. Umulur ki yaptıklarından vazgeçerler.", transliteration: "Ve in nekesu eymanehum min ba'di ahdihim ve ta'anu fi dinikum fe katilu eimmetel kufri innehum la eymane lehum leallehum yentehun."},
  {id: 1248, sure_no: 13, ayet_no: 13, sure_adi: "Tevbe", text: "اَلَا تُقَاتِلُونَ قَوْماً نَكَثُٓوا اَيْمَانَهُمْ وَهَمُّوا بِاِخْرَاجِ الرَّسُولِ وَهُمْ بَدَؤُ۫كُمْ اَوَّلَ مَرَّةٍۜ اَتَخْشَوْنَهُمْۚ فَاللّٰهُ اَحَقُّ اَنْ تَخْشَوْهُ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Antlarını bozup, Resul'ü yurdundan çıkarmaya karar veren ve size karşı saldırıya ilk geçen bir halkla savaşmaz mısınız? Yoksa onlara huşu mu duyuyorsunuz? Eğer gerçekten iman edenler iseniz, Allah'a daha çok huşu duymanız gerektiğini bilmelisiniz.", transliteration: "E la tukatilune kavmen nekesu eymanehum ve hemmu bi ihracir resuli ve hum bedeukum evvele merreh, e tahşevnehum, fallahu ehakku en tahşevhu in kuntum mu'minin."},
  {id: 1249, sure_no: 14, ayet_no: 14, sure_adi: "Tevbe", text: "قَاتِلُوهُمْ يُعَذِّبْهُمُ اللّٰهُ بِاَيْد۪يكُمْ وَيُخْزِهِمْ وَيَنْصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُورَ قَوْمٍ مُؤْمِن۪ينَۙ", translation: "Onlarla savaşın ki Allah sizin ellerinizle onlara azap etsin, onları rezil etsin, onlara karşı size yardım etsin. İman Eden halkın göğüslerine şifa versin.", transliteration: "Katiluhum yuazzibhumullahu bi eydikum ve yuhzihim ve yansurkum aleyhim ve yeşfi sudure kavmin mu'minin."},
  {id: 1250, sure_no: 15, ayet_no: 15, sure_adi: "Tevbe", text: "وَيُذْهِبْ غَيْظَ قُلُوبِهِمْۜ وَيَتُوبُ اللّٰهُ عَلٰى مَنْ يَشَٓاءُۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Ve onların kalplerindeki öfkeyi gidersin. Allah, hak edene tevbe nasip eder. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve yuzhib gayza kulubihim, ve yetubullahu ala men yeşa'u, vallahu alimun hakim."},
  {id: 1251, sure_no: 16, ayet_no: 16, sure_adi: "Tevbe", text: "اَمْ حَسِبْتُمْ اَنْ تُتْرَكُوا وَلَمَّا يَعْلَمِ اللّٰهُ الَّذ۪ينَ جَاهَدُوا مِنْكُمْ وَلَمْ يَتَّخِذُوا مِنْ دُونِ اللّٰهِ وَلَا رَسُولِه۪ وَلَا الْمُؤْمِن۪ينَ وَل۪يجَةًۜ وَاللّٰهُ خَب۪يرٌ بِمَا تَعْمَلُونَ۟", translation: "Yoksa siz, Allah'ın içinizden cihat edip, Allah'tan, Resul'ünden ve Mü'minlerden başkasını sırdaş edinmeyenleri ortaya çıkarmadan, kendi halinize bırakılacağınızı mı sandınız? Allah, yaptığınız her şeyden haberdardır.", transliteration: "Em hasibtum en tutreku ve lemma ya'lemillahullezine cahedu minkum ve lem yettehızu min dunillahi ve la resulihi ve lel mu'minine ve liceh, vallahu habirun bi ma ta'melun."},
  {id: 1252, sure_no: 17, ayet_no: 17, sure_adi: "Tevbe", text: "مَا كَانَ لِلْمُشْرِك۪ينَ اَنْ يَعْمُرُوا مَسَاجِدَ اللّٰهِ شَاهِد۪ينَ عَلٰٓى اَنْفُسِهِمْ بِالْكُفْرِۜ اُو۬لٰٓئِكَ حَبِطَتْ اَعْمَالُهُمْۚ وَفِي النَّارِ هُمْ خَالِدُونَ", translation: "Kafir olduklarını bildikleri halde, Müşriklerin, Allah'ın mescitlerini imar etmeye hakları yoktur. Onların bütün yaptıkları boşa gitmiştir. Onlar, ateşte sürekli kalacaklardır.", transliteration: "Ma kane lil muşrikine en ya'muru mesacidallahi şahidine ala enfusihim bil kufr, ulaike habitat a'maluhum ve fin nari hum halidun ."},
  {id: 1253, sure_no: 18, ayet_no: 18, sure_adi: "Tevbe", text: "اِنَّمَا يَعْمُرُ مَسَاجِدَ اللّٰهِ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ وَلَمْ يَخْشَ اِلَّا اللّٰهَ فَعَسٰٓى اُو۬لٰٓئِكَ اَنْ يَكُونُوا مِنَ الْمُهْتَد۪ينَ", translation: "Allah'ın mescitlerini, ancak Allah 'a ve ahiret gününe iman edip, salatı ikame eden, zekatı yapan ve Allah'tan başkasından korkmayan kimseler imar edebilirler. Onların doğru yolu bulanlardan olmaları umulur.", transliteration: "İnnema ya'muru mesacidallahi men amene billahi vel yevmil ahıri ve ekames salate ve atez zekate ve lem yahşe illallahe fe asa ulaike en yekunu minel muhtedin."},
  {id: 1254, sure_no: 19, ayet_no: 19, sure_adi: "Tevbe", text: "اَجَعَلْتُمْ سِقَايَةَ الْحَٓاجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَجَاهَدَ ف۪ي سَب۪يلِ اللّٰهِۜ لَا يَسْتَوُ۫نَ عِنْدَ اللّٰهِۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَۢ", translation: "Siz, hacılara su dağıtmayı ve Mescid-i Haram'ı imar etmeyi; Allah'a ve ahiret gününe iman edip, Allah yolunda cihat edenlerle bir mi tuttunuz? Bunlar, Allah'ın yanında bir değildir. Allah, zalim olan halkı doğru yola iletmez.", transliteration: "E cealtum sikayetel hacci ve ımaretel mescidil harami ke men amene billahi vel yevmil ahıri ve cahede fi sebilillah, la yestevune ındallah, vallahu la yehdil kavmez zalimin."},
  {id: 1255, sure_no: 20, ayet_no: 20, sure_adi: "Tevbe", text: "اَلَّذ۪ينَ اٰمَنُوا وَهَاجَرُوا وَجَاهَدُوا ف۪ي سَب۪يلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۙ اَعْظَمُ دَرَجَةً عِنْدَ اللّٰهِۜ وَاُو۬لٰٓئِكَ هُمُ الْفَٓائِزُونَ", translation: "İman edip, hicret eden ve Allah yolunda mallarıyla canlarıyla cihat edenlerin, Allah katında dereceleri daha üstündür. İşte onlar kazançlı olanlardır.", transliteration: "Ellezine amenu ve haceru ve cahedu fi sebilillahi bi emvalihim ve enfusihim a'zamu dereceten ındallah ve ulaike humul faizun ."},
  {id: 1256, sure_no: 21, ayet_no: 21, sure_adi: "Tevbe", text: "يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِنْهُ وَرِضْوَانٍ وَجَنَّاتٍ لَهُمْ ف۪يهَا نَع۪يمٌ مُق۪يمٌۙ", translation: "Rabb'leri, onları, Kendi rahmeti, hoşnutluğu ve içinde tükenmeyen nimetler bulunan Cennetler ile müjdeliyor.", transliteration: "Yubeşşiruhum rabbuhum bi rahmetin minhu ve rıdvanin ve cennatin lehum fiha naimun mukim."},
  {id: 1257, sure_no: 22, ayet_no: 22, sure_adi: "Tevbe", text: "خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ اِنَّ اللّٰهَ عِنْدَهُٓ اَجْرٌ عَظ۪يمٌ", translation: "Onlar, orada sürekli kalıcıdırlar. Kuşkusuz büyük ödül Allah'ın katındadır.", transliteration: "Halidine fiha ebeda, innallahe indehu ecrun azim ."},
  {id: 1258, sure_no: 23, ayet_no: 23, sure_adi: "Tevbe", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُٓوا اٰبَٓاءَكُمْ وَاِخْوَانَكُمْ اَوْلِيَٓاءَ اِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْا۪يمَانِۜ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ", translation: "Ey İman Edenler! Eğer küfrü imana tercih ediyorlarsa, babalarınızı ve kardeşlerinizi evliya edinmeyin. Sizden kim onlara dönerse, işte onlar zalimlerin ta kendileridir.", transliteration: "Ya eyyuhellezine amenu la tettehızu abaekum ve ihvanekum evliyae inistehabbul kufre alel imani, ve men yetevellehum minkum fe ulaike humuz zalimun."},
  {id: 1259, sure_no: 24, ayet_no: 24, sure_adi: "Tevbe", text: "قُلْ اِنْ كَانَ اٰبَٓاؤُ۬كُمْ وَاَبْنَٓاؤُ۬كُمْ وَاِخْوَانُكُمْ وَاَزْوَاجُكُمْ وَعَش۪يرَتُكُمْ وَاَمْوَالٌۨ اقْتَرَفْتُمُوهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسَاكِنُ تَرْضَوْنَـهَٓا اَحَبَّ اِلَيْكُمْ مِنَ اللّٰهِ وَرَسُولِه۪ وَجِهَادٍ ف۪ي سَب۪يلِه۪ فَتَرَبَّصُوا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِه۪ۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ۟", translation: "De ki: \"Eğer babalarınız, çocuklarınız, kardeşleriniz, eşleriniz, sülaleniz; kazandığınız mallarınız, kötüye gitmesinden korktuğunuz ticaretiniz, hoşunuza giden evleriniz, size; Allah'tan, O'nun Resul'ünden ve O'nun yolunda cihad etmekten daha sevimli geliyorsa, o halde Allah'ın hükmünün gerçekleşmesini bekleyin.\" Allah, fasık olan halkı hidayete iletmez.", transliteration: "Kul in kane abaukum ve ebnaukum ve ıhvanukum ve ezvacukum ve aşiretukum ve emvalunıktereftumuha ve ticaretun tahşevne kesadeha ve mesakinu terdavneha ehabbe ileykum minallahi ve resulihi ve cihadin fi sebilihi fe terabbesu hatta ye' tiyallahu bi emrih, vallahu la yehdil kavmel fasikin."},
  {id: 1260, sure_no: 25, ayet_no: 25, sure_adi: "Tevbe", text: "لَقَدْ نَصَرَكُمُ اللّٰهُ ف۪ي مَوَاطِنَ كَث۪يرَةٍۙ وَيَوْمَ حُنَيْنٍۙ اِذْ اَعْجَبَتْكُمْ كَـثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْـٔاً وَضَاقَتْ عَلَيْكُمُ الْاَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُدْبِر۪ينَۚ", translation: "Ant olsun ki Allah, size birçok yerde ve Huneyn gününde yardım etmişti. Hani çokluğunuz sizi böbürlendirmişti de size bir faydası olmamıştı. Yeryüzü bütün genişliğine rağmen size dar gelmişti. Sonra da gerisin geri dönüp gitmiştiniz.", transliteration: "Lekad nasarakumullahu fi mevatıne kesiretin ve yevme huneynin iz a'cebetkum kesretukum fe lem tugni ankum şey'en ve dakat aleykumul ardu bi ma rehubet summe velleytum mudbirine."},
  {id: 1261, sure_no: 26, ayet_no: 26, sure_adi: "Tevbe", text: "ثُمَّ اَنْزَلَ اللّٰهُ سَك۪ينَتَهُ عَلٰى رَسُولِه۪ وَعَلَى الْمُؤْمِن۪ينَ وَاَنْزَلَ جُنُوداً لَمْ تَرَوْهَا وَعَذَّبَ الَّذ۪ينَ كَفَرُواۜ وَذٰلِكَ جَزَٓاءُ الْكَافِر۪ينَ", translation: "Sonra Allah, Resul'ünün ve İman Edenlerin üzerine dinginlik ve güven indirdi. Görmediğiniz ordular indirdi ve Kafirlere azap verdi. İşte budur Kafirlerin cezası.", transliteration: "Summe enzelallahu sekinetehu ala resuli-hi ve alel mu'minine ve enzele cunuden lem terev-ha ve azzebellezine keferu ve zalike cezaul kafirin."},
  {id: 1262, sure_no: 27, ayet_no: 27, sure_adi: "Tevbe", text: "ثُمَّ يَتُوبُ اللّٰهُ مِنْ بَعْدِ ذٰلِكَ عَلٰى مَنْ يَشَٓاءُۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "Sonra Allah, bunun ardından hak edenin tevbesini kabul eder. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Summe yetubullahu min ba'di zalike ala men yeşa'u, vallahu gafurun rahim."},
  {id: 1263, sure_no: 28, ayet_no: 28, sure_adi: "Tevbe", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هٰذَاۚ وَاِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْن۪يكُمُ اللّٰهُ مِنْ فَضْلِه۪ٓ اِنْ شَٓاءَۜ اِنَّ اللّٰهَ عَل۪يمٌ حَك۪يمٌ", translation: "Ey İman Edenler! Bu Müşrikler ancak necistir. Öyleyse bu yıldan sonra Mescid-i Haram'a yaklaşmasınlar. Eğer yoksulluktan korkarsanız, Allah dilerse sizi kendi lütfu ile yakında zengin yapar. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ya eyyuhellezine amenu innemal muşrikune necesun fe la yakrebul mescidel harame ba'de amihim haza ve in hıftum ayleten fe sevfe yugnikumullahu min fadlihi in şae, innallahe alimun hakim."},
  {id: 1264, sure_no: 29, ayet_no: 29, sure_adi: "Tevbe", text: "قَاتِلُوا الَّذ۪ينَ لَا يُؤْمِنُونَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ اللّٰهُ وَرَسُولُهُ وَلَا يَد۪ينُونَ د۪ينَ الْحَقِّ مِنَ الَّذ۪ينَ اُو۫تُوا الْكِتَابَ حَتّٰى يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَهُمْ صَاغِرُونَ۟", translation: "Kendilerine Kitap verilenlerden, Allah'a ve Ahiret Günü'ne inanmayan; Allah'ın ve Resul'ünün haram kıldığını haram saymayan ve Hak dini, din edinmeyen kimselerle, üstünlüğünüzü kabul ettirinceye, kendi elleriyle size o cizyeyi verinceye kadar savaşın.", transliteration: "Katilullezine la yu'minune billahi ve la bil yevmil ahıri ve la yuharrimune ma harremallahu ve resuluhu ve la yedinune dinel hakkı minellezine utul kitabe hatta yu'tul cizyete an yedin ve hum sagirun."},
  {id: 1265, sure_no: 30, ayet_no: 30, sure_adi: "Tevbe", text: "وَقَالَتِ الْيَهُودُ عُزَيْرٌۨ ابْنُ اللّٰهِ وَقَالَتِ النَّصَارَى الْمَس۪يحُ ابْنُ اللّٰهِۜ ذٰلِكَ قَوْلُهُمْ بِاَفْوَاهِهِمْۚ يُضَاهِؤُ۫نَ قَوْلَ الَّذ۪ينَ كَفَرُوا مِنْ قَبْلُۜ قَاتَلَهُمُ اللّٰهُۘ اَنّٰى يُؤْفَكُونَ", translation: "Yahudiler: \"Uzeyr Allah'ın oğludur.\" dediler; Nesara da: \"Mesih Allah'ın oğludur.\" dediler. Bu, onların dillerine doladıkları sözlerdir ki daha önce küfredenlerin sözlerine benzetiyorlar. Allah, kahretsin onları! Nasıl da saptırıyorlar.", transliteration: "Ve kaletil yahudu uzeyrunibnullahi ve kaletin nasarel mesihubnullah zalike kavluhum bi efvahihim yudahiune kavlellezine keferu min kabl katelehumullah enna yu'fekun."},
  {id: 1266, sure_no: 31, ayet_no: 31, sure_adi: "Tevbe", text: "اِتَّخَذُٓوا اَحْبَارَهُمْ وَرُهْبَانَهُمْ اَرْبَاباً مِنْ دُونِ اللّٰهِ وَالْمَس۪يحَ ابْنَ مَرْيَمَۚ وَمَٓا اُمِرُٓوا اِلَّا لِيَعْبُدُٓوا اِلٰهاً وَاحِداًۚ لَٓا اِلٰهَ اِلَّا هُوَۜ سُبْحَانَهُ عَمَّا يُشْرِكُونَ", translation: "Onlar, Allah'ın yanı sıra hahamlarını, rahiplerini ve Meryem Oğlu Mesih'i rabbler edindiler. Oysa bunlar, bir tek olan İlah'a kulluk etmekle emrolunmuşlardı. Ondan başka hiçbir ilah yoktur. O, bunların ortak koştuklarından münezzehtir.", transliteration: "İttehazu ahbarehum ve ruhbanehum erbaben min dunillahi vel mesihabne meryem, ve ma umiru illa li ya'budu ilahen vahida ,la ilahe illa huve, subhanehu amma yuşrikun."},
  {id: 1267, sure_no: 32, ayet_no: 32, sure_adi: "Tevbe", text: "يُر۪يدُونَ اَنْ يُطْفِؤُ۫ا نُورَ اللّٰهِ بِاَفْوَاهِهِمْ وَيَأْبَى اللّٰهُ اِلَّٓا اَنْ يُتِمَّ نُورَهُ وَلَوْ كَرِهَ الْكَافِرُونَ", translation: "Allah'ın nurunu ağızlarıyla söndürmek istiyorlar. Ancak, Kafirler hoşlanmasalar da Allah kesinlikle nurunu tamamlamaktan başka bir şey istemez.", transliteration: "Yuridune en yutfiu nurallahi bi efvahihim ve ye'ballahu illa en yutimme nurehu ve lev kerihel kafirun."},
  {id: 1268, sure_no: 33, ayet_no: 33, sure_adi: "Tevbe", text: "هُوَ الَّـذ۪ٓي اَرْسَلَ رَسُولَهُ بِالْهُدٰى وَد۪ينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدّ۪ينِ كُلِّه۪ۙ وَلَوْ كَرِهَ الْمُشْرِكُونَ", translation: "Müşrikler hoşlanmasalar da dinini her dinin üstünde açık olarak ortaya koymak üzere Resul'ünü doğru yol ve gerçek din ile gönderdi.", transliteration: "Huvellezi ersele resulehu bil huda ve dinil hakkı li yuzhirehu aled dini kullihi ve lev kerihel muşrikun."},
  {id: 1269, sure_no: 34, ayet_no: 34, sure_adi: "Tevbe", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنَّ كَث۪يراً مِنَ الْاَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُونَ اَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِۜ وَالَّذ۪ينَ يَكْنِزُونَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنْفِقُونَهَا ف۪ي سَب۪يلِ اللّٰهِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَل۪يمٍۙ", translation: "Ey İman Edenler! Doğrusu, ahbar ve ruhban kimselerin çoğu, insanların mallarını haksız şekilde yerler. İnsanları Allah'ın yolundan çevirirler. Altın ve gümüşü biriktirip de onları Allah yolunda harcamayanlar var ya işte onları can yakıcı bir azaptan haberdar et.", transliteration: "Ya eyyuhellezine amenu inne kesiren minel ahbari ver ruhbani le ye'kulune emvalen nasi bil batıli ve yasuddune an sebilillah, vellezine yeknizunez zehebe vel fıddate ve la yunfikuneha fi sebilillahi fe beşşirhum bi azabin elim."},
  {id: 1270, sure_no: 35, ayet_no: 35, sure_adi: "Tevbe", text: "يَوْمَ يُحْمٰى عَلَيْهَا ف۪ي نَارِ جَهَنَّمَ فَتُكْوٰى بِهَا جِبَاهُهُمْ وَجُنُوبُهُمْ وَظُهُورُهُمْۜ هٰذَا مَا كَنَزْتُمْ لِاَنْفُسِكُمْ فَذُوقُوا مَا كُنْتُمْ تَكْنِزُونَ", translation: "O Gün, Cehennem ateşinde kızdırılıp; onlarla alınları, yanları ve sırtları dağlanacak, \"İşte bunlardır, kendiniz için biriktirdiğiniz şeyler, tadın, biriktirdiğiniz şeylerin azabını.\" denecek.", transliteration: "Yevme yuhma aleyha fi nari cehenneme fe tukva biha cibahuhum ve cunubuhum ve zuhuruhum, haza ma keneztum li enfusikum fe zuku ma kuntum teknizun."},
  {id: 1271, sure_no: 36, ayet_no: 36, sure_adi: "Tevbe", text: "اِنَّ عِدَّةَ الشُّهُورِ عِنْدَ اللّٰهِ اثْنَا عَشَرَ شَهْراً ف۪ي كِتَابِ اللّٰهِ يَوْمَ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ مِنْهَٓا اَرْبَعَةٌ حُرُمٌۜ ذٰلِكَ الدّ۪ينُ الْقَيِّمُ فَلَا تَظْلِمُوا ف۪يهِنَّ اَنْفُسَكُمْ وَقَاتِلُوا الْمُشْرِك۪ينَ كَٓافَّةً كَمَا يُقَاتِلُونَكُمْ كَٓافَّةًۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ مَعَ الْمُتَّق۪ينَ", translation: "Gökleri ve yeri yarattığı zaman koyduğu yasasında, Allah'ın yanında ayların sayısı on ikidir. Bunların dördü Haram Aylar'dır. İşte kayyum olan budur. Bu aylarda, kendinize haksızlık yapmayın. Ve Müşrikler nasıl sizinle topyekun savaşıyorlarsa siz de onlarla topyekun savaşın. Biliniz ki, Allah, muttakilerle beraberdir.", transliteration: "İnne iddeteş şuhuri indallahisna aşere şehren fi kitabillahi yevme halakas semavati vel arda minha erbeatun huruma zaliked dinul kayyimu fe la tazlimu fihinne enfusekum ve katilul muşrikine kaffeten kema yukatilunekum kaffeh, va'lemu ennallahe meal muttekin."},
  {id: 1272, sure_no: 37, ayet_no: 37, sure_adi: "Tevbe", text: "اِنَّمَا النَّس۪ٓيءُ زِيَادَةٌ فِي الْكُفْرِ يُضَلُّ بِهِ الَّذ۪ينَ كَفَرُوا يُحِلُّونَهُ عَاماً وَيُحَرِّمُونَهُ عَاماً لِيُوَاطِؤُ۫ا عِدَّةَ مَا حَرَّمَ اللّٰهُ فَيُحِلُّوا مَا حَرَّمَ اللّٰهُۜ زُيِّنَ لَهُمْ سُٓوءُ اَعْمَالِهِمْۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ۟", translation: "Haram ayların yerlerini değiştirip ertelemek, küfürde ileri gitmektir ki Kafirler onunla saptırılır. Onlar, Allah'ın haram kıldığı aylara denk getirmek için bu ertelemeyi bir yıl helal sayarlarken bir yıl sonra haram sayarlar. Böylece Allah'ın haram kıldığını helal saymış oluyorlar. Böylece yaptıkları kötü şeyler, onlara güzel gösterildi. Allah Kafir halkı doğru yola iletmez.", transliteration: "İnnemen nesiu ziyadetun fil kufri yudallu bihillezine keferu yuhillunehu amen ve yuharrimunehu amen li yuvatiu iddete ma harremallahu fe yuhillu ma harremallah, zuyyine lehum suu a'malihim, vallahu la yehdil kavmel kafirin."},
  {id: 1273, sure_no: 38, ayet_no: 38, sure_adi: "Tevbe", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا مَا لَكُمْ اِذَا ق۪يلَ لَكُمُ انْفِرُوا ف۪ي سَب۪يلِ اللّٰهِ اثَّاقَلْتُمْ اِلَى الْاَرْضِۜ اَرَض۪يتُمْ بِالْحَيٰوةِ الدُّنْيَا مِنَ الْاٰخِرَةِۚ فَمَا مَتَاعُ الْحَيٰوةِ الدُّنْيَا فِي الْاٰخِرَةِ اِلَّا قَل۪يلٌ", translation: "Ey İman Edenler! Size ne oluyor ki, Allah yolunda seferber olun denildiği zaman, işi ağırdan aldınız! Yoksa hiret hayatı yerine dünya hayatını mı tercih ettiniz. Ama ahiret hayatının yanında dünya hayatının değeri pek azdır.", transliteration: "Ya eyyuhellezine amenu ma lekum iza kile lekumunfiru fi sebilillahissakaltum ilel ard, e raditum bil hayatid dunya minel ahireh, fe ma metaul hayatid dunya fil ahireti illa kalil."},
  {id: 1274, sure_no: 39, ayet_no: 39, sure_adi: "Tevbe", text: "اِلَّا تَنْفِرُوا يُعَذِّبْكُمْ عَذَاباً اَل۪يماً وَيَسْتَبْدِلْ قَوْماً غَيْرَكُمْ وَلَا تَضُرُّوهُ شَيْـٔاًۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Eğer seferber olmazsanız, sizi can yakıcı bir azapla cezalandırır ve yerinize başka bir halkı getirir. Siz O'na hiçbir şeyle zarar veremezsiniz. Allah, Her Şeye Güç Yetiren'dir.", transliteration: "İlla tenfiru yuazzibkum azaben elimen ve yestebdil kavmen gayrekum ve la tedurruhu şey'a , vallahu ala kulli şey'in kadir."},
  {id: 1275, sure_no: 40, ayet_no: 40, sure_adi: "Tevbe", text: "اِلَّا تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللّٰهُ اِذْ اَخْرَجَهُ الَّذ۪ينَ كَفَرُوا ثَانِيَ اثْنَيْنِ اِذْ هُمَا فِي الْغَارِ اِذْ يَقُولُ لِصَاحِبِه۪ لَا تَحْزَنْ اِنَّ اللّٰهَ مَعَنَاۚ فَاَنْزَلَ اللّٰهُ سَك۪ينَتَهُ عَلَيْهِ وَاَيَّدَهُ بِجُنُودٍ لَمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذ۪ينَ كَفَرُوا السُّفْلٰىۜ وَكَلِمَةُ اللّٰهِ هِيَ الْعُلْيَاۜ وَاللّٰهُ عَز۪يزٌ حَك۪يمٌ", translation: "Eğer siz ona yardım etmezseniz, iyi bilin ki Allah ona yardım etmişti. Hani Kafirler onu çıkardıklarında iki kişiden ikincisiydi. İkisi mağaradayken, o, arkadaşına: \"Üzülme, kuşkusuz Allah bizimle beraberdir.\" demişti. Bunun üzerine Allah, üzerlerine dinginlik ve güven indirmişti. Onu, sizin görmediğiniz güçlerle desteklemişti. Ve küfredenlerin sözünü alçaltmıştı. Allah'ın sözü ise en yüce olandır. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "İlla tensuruhu fe kad nasarahullahu iz ahrecehullezine keferu saniyesneyni iz huma fil gari iz yekulu li sahibihi la tahzen innallahe meana, fe enzelallahu sekinetehu aleyhi ve eyyedehu bicunudin lem terevha ve ceale kelimetellezine keferus sufla, ve kelimetullahi hiyel ulya vallahu azizun hakim ."},
  {id: 1276, sure_no: 41, ayet_no: 41, sure_adi: "Tevbe", text: "اِنْفِرُوا خِفَافاً وَثِقَالاً وَجَاهِدُوا بِاَمْوَالِكُمْ وَاَنْفُسِكُمْ ف۪ي سَب۪يلِ اللّٰهِۜ ذٰلِكُمْ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "Ağır ve hafif her türlü savaş araç gereçleriyle seferber olun. Mallarınızla ve canlarınızla Allah yolunda cihad edin. Eğer bilirseniz, bu sizin için daha hayırlıdır.", transliteration: "İnfiru hıfafen ve sikalen ve cahidu bi emvalikum ve enfusikum fi sebilillah, zalikum hayrun lekum in kuntum ta'lemun."},
  {id: 1277, sure_no: 42, ayet_no: 42, sure_adi: "Tevbe", text: "لَوْ كَانَ عَرَضاً قَر۪يباً وَسَفَراً قَاصِداً لَاتَّـبَعُوكَ وَلٰكِنْ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُۜ وَسَيَحْلِفُونَ بِاللّٰهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْۚ يُهْلِكُونَ اَنْفُسَهُمْۚ وَاللّٰهُ يَعْلَمُ اِنَّهُمْ لَكَاذِبُونَ۟", translation: "Eğer kolay bir kazanç ve sıradan bir \"sefer\" olsaydı, arkandan gelirlerdi. Ancak bu zorlu yolculuk, onlara uzak geldi. \"Eğer gücümüz yetseydi, biz de sizinle çıkardık.\" diye Allah'a yemin edecekler. Kendilerini yok olmaya sürüklüyorlar. Kuşkusuz Allah, onların yalancı olduklarını en iyi bilendir.", transliteration: "Lev kane aradan kariben ve seferen kasıden lettebeuke ve lakin beudet aleyhimuş şukkah, ve seyahlifune billahi levisteta'na leharecna meakum, yuhlikune enfusehum, vallahu ya'lemu innehum le kazibun."},
  {id: 1278, sure_no: 43, ayet_no: 43, sure_adi: "Tevbe", text: "عَفَا اللّٰهُ عَنْكَۚ لِمَ اَذِنْتَ لَهُمْ حَتّٰى يَتَبَيَّنَ لَكَ الَّذ۪ينَ صَدَقُوا وَتَعْلَمَ الْكَاذِب۪ينَ", translation: "Allah seni affetsin! Doğru söyleyenler belli olmadan, yalancılar bilinmeden, onlara niçin izin verdin?", transliteration: "Afallahu ank, lime ezinte lehum hatta yetebeyyene lekellezine sadaku ve ta'lemel kazibin."},
  {id: 1279, sure_no: 44, ayet_no: 44, sure_adi: "Tevbe", text: "لَا يَسْتَأْذِنُكَ الَّذ۪ينَ يُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ اَنْ يُجَاهِدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۜ وَاللّٰهُ عَل۪يمٌ بِالْمُتَّق۪ينَ", translation: "Allah'a ve ahiret gününe iman edenler, mallarıyla ve canlarıyla cihad etmemek için senden izin istemezler. Allah, muttakileri en iyi bilendir.", transliteration: "La yeste'zinukellezine yu'minune billahi vel yevmil ahiri en yucahidu bi emvalihim ve enfusihim,vallahu alimun bil muttekin."},
  {id: 1280, sure_no: 45, ayet_no: 45, sure_adi: "Tevbe", text: "اِنَّمَا يَسْتَأْذِنُكَ الَّذ۪ينَ لَا يُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَارْتَابَتْ قُلُوبُهُمْ فَهُمْ ف۪ي رَيْبِهِمْ يَتَرَدَّدُونَ", translation: "Doğrusu, senden, ancak Allah'a ve ahiret gününe iman etmeyenler ve kalpleri kuşkuya düşüp, kuşku içinde bocalayanlar sefere çıkmamak için izin isterler.", transliteration: "İnnema yeste'zinulkellezine la yu'minune billahi vel yevmil ahiri vertabet kulubuhum fe hum fi reybihim yetereddedun."},
  {id: 1281, sure_no: 46, ayet_no: 46, sure_adi: "Tevbe", text: "وَلَوْ اَرَادُوا الْخُرُوجَ لَاَعَدُّوا لَهُ عُدَّةً وَلٰكِنْ كَرِهَ اللّٰهُ انْبِعَاثَهُمْ فَثَبَّطَهُمْ وَق۪يلَ اقْعُدُوا مَعَ الْقَاعِد۪ينَ", translation: "Eğer çıkmak isteselerdi, elbette bunun için hazırlık yaparlardı. Ancak Allah, onların davranışlarını kötü gördü de kendilerini alıkoydu ve onlara: \"Oturanlarla beraber siz de oturun.\" denildi.", transliteration: "Ve lev eradul huruce le eaddu lehu uddeten ve lakin kerihallahunbiasehum fe sebbetahum ve kilak'udu meal kaidin ."},
  {id: 1282, sure_no: 47, ayet_no: 47, sure_adi: "Tevbe", text: "لَوْ خَرَجُوا ف۪يكُمْ مَا زَادُوكُمْ اِلَّا خَبَالاً وَلَا۬اَوْضَعُوا خِلَالَكُمْ يَبْغُونَكُمُ الْفِتْنَةَۚ وَف۪يكُمْ سَمَّاعُونَ لَهُمْۜ وَاللّٰهُ عَل۪يمٌ بِالظَّالِم۪ينَ", translation: "Eğer sizinle çıksalardı, bozgunculuktan başka bir şey yapmazlardı; sizi fitneye düşürmek için koşuştururlardı. İçinizde, onlara kulak verecekler de olurdu. Kuşkusuz Allah, zalimleri en iyi bilendir.", transliteration: "Lev harecu fikum ma zadukum illa habalen ve la evdau hılalekum yebgunekumul fitneh, ve fikum semmaune lehum, vallahu alimun biz zalimin."},
  {id: 1283, sure_no: 48, ayet_no: 48, sure_adi: "Tevbe", text: "لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوا لَكَ الْاُمُورَ حَتّٰى جَٓاءَ الْحَقُّ وَظَهَرَ اَمْرُ اللّٰهِ وَهُمْ كَارِهُونَ", translation: "Daha önce de fitne çıkarmak istemişler ve sana karşı türlü işler çevirmişlerdi. Nihayet, Hakk geldi ve onlar istemedikleri halde, Allah'ın emri gerçekleşti.", transliteration: "Lekadibtegul fitnete min kablu ve kallebu lekel umure hatta cael hakku ve zahere emrullahi ve hum karihun."},
  {id: 1284, sure_no: 49, ayet_no: 49, sure_adi: "Tevbe", text: "وَمِنْهُمْ مَنْ يَقُولُ ائْذَنْ ل۪ي وَلَا تَفْتِنّ۪يۜ اَلَا فِي الْفِتْنَةِ سَقَطُواۜ وَاِنَّ جَهَنَّمَ لَمُح۪يطَةٌ بِالْكَافِر۪ينَ", translation: "Onlardan kimi de: \"Bana izin ver, beni fitneye düşürme.\" der. İyi bilmiş ol ki, onlar, fitneye düşmüşlerdir. Cehennem onları kuşatacaktır.", transliteration: "Ve minhum men yekulu'zen li ve la teftinni, e la fil fitneti sekatu, ve inne cehenneme le muhitatun bil kafirin."},
  {id: 1285, sure_no: 50, ayet_no: 50, sure_adi: "Tevbe", text: "اِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْۚ وَاِنْ تُصِبْكَ مُص۪يبَةٌ يَقُولُوا قَدْ اَخَذْنَٓا اَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَهُمْ فَرِحُونَ", translation: "Eğer sana bir iyilik isabet ederse, bu onları üzer. Fakat sana bir kötülük dokunursa, \"Biz daha önceden önlemimizi almıştık.\" derler ve sevinç içinde arkalarına dönüp giderler.", transliteration: "İn tusıbke hasenetun tesu'hum, ve in tusıbke musibetun yekulu kad ehazna emrena min kablu ve yetevellev ve hum ferihun."},
  {id: 1286, sure_no: 51, ayet_no: 51, sure_adi: "Tevbe", text: "قُلْ لَنْ يُص۪يبَنَٓا اِلَّا مَا كَتَبَ اللّٰهُ لَنَاۚ هُوَ مَوْلٰينَاۚ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ", translation: "De ki: \"Allah'ın bizim için yazdığından başkası bize erişmez. O, bizim Mevla'mızdır. Öyleyse, Mü'minler, yalnız Allah'a tevekkül etsinler.", transliteration: "Kul len yusibena illa ma keteballahu lena, huve mevlana, ve alallahi fel yetevekkelil mu'minun."},
  {id: 1287, sure_no: 52, ayet_no: 52, sure_adi: "Tevbe", text: "قُلْ هَلْ تَرَبَّصُونَ بِنَٓا اِلَّٓا اِحْدَى الْحُسْنَيَيْنِۜ وَنَحْنُ نَتَرَبَّصُ بِكُمْ اَنْ يُص۪يبَكُمُ اللّٰهُ بِعَذَابٍ مِنْ عِنْدِه۪ٓ اَوْ بِاَيْد۪ينَاۘ فَتَرَبَّصُٓوا اِنَّا مَعَكُمْ مُتَرَبِّصُونَ", translation: "De ki: \"Bize iki güzellikten birinin dışında başka bir şeyin gelmesini mi bekliyorsunuz? Oysa biz, Allah'ın kendi katından veya bizim elimizle size bir azap gelmesini bekliyoruz. Öyleyse bekleyin. Doğrusu biz de sizinle beraber bekleyenlerdeniz.\"", transliteration: "Kul hel terabbesune bina illa ıhdel husneyeyn ve nahnu neterabbesu bikum en yusibekumullahu bi azabin min indihi ev bi eydina, fe terabbasu inna meakum muterabbisun."},
  {id: 1288, sure_no: 53, ayet_no: 53, sure_adi: "Tevbe", text: "قُلْ اَنْفِقُوا طَوْعاً اَوْ كَرْهاً لَنْ يُتَقَبَّلَ مِنْكُمْۜ اِنَّكُمْ كُنْتُمْ قَوْماً فَاسِق۪ينَ", translation: "De ki: \"İster istekli, ister isteksiz infak edin; sizden asla kabul edilmeyecektir. Çünkü siz fasık bir toplum oldunuz.\"", transliteration: "Kul enfiku tav'an ev kerhen len yutekabbele minkum, innekum kuntum kavmen fasikin."},
  {id: 1289, sure_no: 54, ayet_no: 54, sure_adi: "Tevbe", text: "وَمَا مَنَعَهُمْ اَنْ تُقْبَلَ مِنْهُمْ نَفَقَاتُهُمْ اِلَّٓا اَنَّهُمْ كَفَرُوا بِاللّٰهِ وَبِرَسُولِه۪ وَلَا يَأْتُونَ الصَّلٰوةَ اِلَّا وَهُمْ كُسَالٰى وَلَا يُنْفِقُونَ اِلَّا وَهُمْ كَارِهُونَ", translation: "İnfaklarının kabul edilmesine engel şey, onların, Allah'a ve Resul'üne karşı küfretmeleri, salata üşene üşene gelmeleri ve istemeyerek infak etmeleridir.", transliteration: "Ve ma meneahum en tukbele minhum nefekatuhum illa ennehum keferu billahi ve bi resulihi ve la ye'tunes salate illa ve humkusala ve la yunfikune illa ve hum karihun."},
  {id: 1290, sure_no: 55, ayet_no: 55, sure_adi: "Tevbe", text: "فَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَلَٓا اَوْلَادُهُمْۜ اِنَّمَا يُر۪يدُ اللّٰهُ لِيُعَذِّبَهُمْ بِهَا فِي الْحَيٰوةِ الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كَافِرُونَ", translation: "Öyleyse, onların malları ve çocukları seni imrendirmesin. Doğrusu, Allah, bunlarla, onlara dünya hayatında azap etmeyi ve canlarının kafir olarak çıkmasını istiyor.", transliteration: "Fe la tu'cibke emvaluhum ve la evladuhum, innema yuridullahu li yuazzibehum biha fil hayatid dunya ve tezheka enfusuhum ve hum kafirun."},
  {id: 1291, sure_no: 56, ayet_no: 56, sure_adi: "Tevbe", text: "وَيَحْلِفُونَ بِاللّٰهِ اِنَّهُمْ لَمِنْكُمْۜ وَمَا هُمْ مِنْكُمْ وَلٰكِنَّهُمْ قَوْمٌ يَفْرَقُونَ", translation: "Sizden olduklarına dair Allah'a yemin ederler. Oysa onlar, sizden değiller. Onlar, ayrılık çıkaran bir topluluktur.", transliteration: "Ve yahlifune billahi innehum le minkum, ve ma hum minkum ve lakinnehum kavmun yefrekun."},
  {id: 1292, sure_no: 57, ayet_no: 57, sure_adi: "Tevbe", text: "لَوْ يَجِدُونَ مَلْجَـٔاً اَوْ مَغَارَاتٍ اَوْ مُدَّخَلاً لَوَلَّوْا اِلَيْهِ وَهُمْ يَجْمَحُونَ", translation: "Eğer sığınılacak bir yer veya bir mağara veya girilecek bir delik bulsalardı, panik içinde oraya koşarlardı.", transliteration: "Lev yecidune melce'en ev magaratin ev muddehalen le vellev ileyhi ve hum yecmehun."},
  {id: 1293, sure_no: 58, ayet_no: 58, sure_adi: "Tevbe", text: "وَمِنْهُمْ مَنْ يَلْمِزُكَ فِي الصَّدَقَاتِۚ فَاِنْ اُعْطُوا مِنْهَا رَضُوا وَاِنْ لَمْ يُعْطَوْا مِنْهَٓا اِذَا هُمْ يَسْخَطُونَ", translation: "İçlerinden kimileri de sadakalar hakkında sana dil uzatır. Eğer kendilerine pay verilirse hoşlanırlar, verilmeyince de hemen kızarlar.", transliteration: "Ve minhum men yelmizuke fis sadakat, fe in u'tu minha radu ve in lem yu'tav minha iza hum yeshatun."},
  {id: 1294, sure_no: 59, ayet_no: 59, sure_adi: "Tevbe", text: "وَلَوْ اَنَّهُمْ رَضُوا مَٓا اٰتٰيهُمُ اللّٰهُ وَرَسُولُهُ وَقَالُوا حَسْبُنَا اللّٰهُ سَيُؤْت۪ينَا اللّٰهُ مِنْ فَضْلِه۪ وَرَسُولُهُٓۙ اِنَّٓا اِلَى اللّٰهِ رَاغِبُونَ۟", translation: "Ne olurdu! Onlar, Allah'ın ve Resul'ünün verdiklerine razı olsalar ve: \"Allah'ın lütfu bize yeter, Allah bize lütfundan yine verir, Resul'ü de. Bizim isteğimiz yalnızca Allah'ın rızasıdır.\" deselerdi.", transliteration: "Ve lev ennehum radu ma atahumullahu ve resuluhu ve kalu hasbunallahu se yu'tinallahu min fadlihi ve resuluhu inna ilallahi ragıbun."},
  {id: 1295, sure_no: 60, ayet_no: 60, sure_adi: "Tevbe", text: "اِنَّمَا الصَّدَقَاتُ لِلْفُقَـرَٓاءِ وَالْمَسَاك۪ينِ وَالْعَامِل۪ينَ عَلَيْهَا وَالْمُؤَ۬لَّفَةِ قُلُوبُهُمْ وَفِي الرِّقَابِ وَالْغَارِم۪ينَ وَف۪ي سَب۪يلِ اللّٰهِ وَابْنِ السَّب۪يلِۜ فَر۪يضَةً مِنَ اللّٰهِۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Sadakalar, Allah'tan bir farz olarak; ancak yoksullara, düşkünlere, bununla ilgili görevlilere, kalpleri kazanılacak kimselere, rikab olanlara, borçlulara, Allah yoluna ve yol oğluna aittir. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "İnnemas sadakatu lil fukarai vel mesakini vel amiline aleyha vel muellefeti kulubuhum ve fir rikabi vel garimine ve fi sebilillahi vebnissebil, faridaten minallah, vallahu alimun hakim."},
  {id: 1296, sure_no: 61, ayet_no: 61, sure_adi: "Tevbe", text: "وَمِنْهُمُ الَّذ۪ينَ يُؤْذُونَ النَّبِيَّ وَيَقُولُونَ هُوَ اُذُنٌۜ قُلْ اُذُنُ خَيْرٍ لَكُمْ يُؤْمِنُ بِاللّٰهِ وَيُؤْمِنُ لِلْمُؤْمِن۪ينَ وَرَحْمَةٌ لِلَّذ۪ينَ اٰمَنُوا مِنْكُمْۜ وَالَّذ۪ينَ يُؤْذُونَ رَسُولَ اللّٰهِ لَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Onlardan bir kısmı, \"O, bir kulaktır.\" diyerek, Nebi'yi incitiyorlar. De ki: \"O, sizin için hayır kulağıdır.\" O, Allah'a inanır ve Mü'minlere inanır. İman Edenler için bir rahmettir. Allah'ın Resul'ünü incitenler için can yakıcı bir azap vardır.", transliteration: "Ve minhumullezine yu'zunen nebiyye ve yekulune huve uzun, kul uzunu hayrin lekum yu'minu billahi ve yu'minu lil mu'minine ve rahmetun lillezine amenu minkum, vellezine yu'zune resulallahi lehum azabun elim."},
  {id: 1297, sure_no: 62, ayet_no: 62, sure_adi: "Tevbe", text: "يَحْلِفُونَ بِاللّٰهِ لَكُمْ لِيُرْضُوكُمْۚ وَاللّٰهُ وَرَسُولُـهُٓ اَحَقُّ اَنْ يُرْضُوهُ اِنْ كَانُوا مُؤْمِن۪ينَ", translation: "Sizi hoşnut etmek için, Allah'a yemin ediyorlar. Oysaki gerçekten inanıyorlarsa, Allah ve Resul'ü hoşnut edilmeye daha layıktır.", transliteration: "Yahlifune billahi lekum li yurdukum, vallahu ve resuluhu ehakku en yurduhu in kanu mu'minin."},
  {id: 1298, sure_no: 63, ayet_no: 63, sure_adi: "Tevbe", text: "اَلَمْ يَعْلَمُٓوا اَنَّهُ مَنْ يُحَادِدِ اللّٰهَ وَرَسُولَهُ فَاَنَّ لَهُ نَارَ جَهَنَّمَ خَالِداً ف۪يهَاۜ ذٰلِكَ الْخِزْيُ الْعَظ۪يمُ", translation: "Bilmediler mi ki: Kim Allah'a ve Resul'üne karşı haddi aşarsa, onun için, içinde sürekli kalacağı Cehennem ateşi vardır. İşte bu, büyük rezilliktir.", transliteration: "E lem ya'lemu ennehu men yuhadidillahe ve resulehu fe enne lehu nare cehenneme haliden fiha, zalikel hızyul azim."},
  {id: 1299, sure_no: 64, ayet_no: 64, sure_adi: "Tevbe", text: "يَحْذَرُ الْمُنَافِقُونَ اَنْ تُنَزَّلَ عَلَيْهِمْ سُورَةٌ تُنَبِّئُهُمْ بِمَا ف۪ي قُلُوبِهِمْۜ قُلِ اسْتَهْزِؤُ۫اۚ اِنَّ اللّٰهَ مُخْرِجٌ مَا تَحْذَرُونَ", translation: "Münafıklar, kendileri hakkında kalplerinde olanı haber verecek bir surenin indirilmesinden çekiniyorlar. De ki: \"Alay edin bakalım! Kuşkusuz Allah, çekindiğiniz şeyi açığa çıkaracaktır.\"", transliteration: "Yahzerul munafikune en tunezzele aleyhim suretun tunebbiuhum bi ma fi kulubihim, kulistehziu, innallahe muhricun ma tahzerun."},
  {id: 1300, sure_no: 65, ayet_no: 65, sure_adi: "Tevbe", text: "وَلَئِنْ سَاَلْتَهُمْ لَيَقُولُنَّ اِنَّمَا كُنَّا نَخُوضُ وَنَلْعَبُۜ قُلْ اَبِاللّٰهِ وَاٰيَاتِه۪ وَرَسُولِه۪ كُنْتُمْ تَسْتَهْزِؤُ۫نَ", translation: "Eğer onlara soracak olsan, \"Biz sadece lafa dalmış şakalaşıyorduk.\" derler. De ki: \"Allah ile mi, O'nun ayetleri ve Resul'ü ile mi alay ediyordunuz?\"", transliteration: "Ve lein se'eltehum le yekulunne innema kunna nahudu ve nel'ab, kul e billahi ve ayatihi ve resulihi kuntum testehziun ."},
  {id: 1301, sure_no: 66, ayet_no: 66, sure_adi: "Tevbe", text: "لَا تَعْتَذِرُوا قَدْ كَفَرْتُمْ بَعْدَ ا۪يمَانِكُمْۜ اِنْ نَعْفُ عَنْ طَٓائِفَةٍ مِنْكُمْ نُعَذِّبْ طَٓائِفَةً بِاَنَّهُمْ كَانُوا مُجْرِم۪ينَ۟", translation: "Hiç özür dilemeyin! Ant olsun siz iman ettikten sonra kafirlik ettiniz. Sizden bir kısmınızı affetsek bile, suç işlemelerinden dolayı bir kısmınıza da azap edeceğiz.", transliteration: "La ta'teziru kad kefertum ba'de imanikum, in na'fu an taifetin minkum nuazzib taifeten bi ennehum kanu mucrimin."},
  {id: 1302, sure_no: 67, ayet_no: 67, sure_adi: "Tevbe", text: "اَلْمُنَافِقُونَ وَالْمُنَافِقَاتُ بَعْضُهُمْ مِنْ بَعْضٍۢ يَأْمُرُونَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوفِ وَيَقْبِضُونَ اَيْدِيَهُمْۜ نَسُوا اللّٰهَ فَنَسِيَهُمْۜ اِنَّ الْمُنَافِق۪ينَ هُمُ الْفَاسِقُونَ", translation: "Münafık erkekler ve Münafık kadınlar birbirlerindendirler; kötülüğü buyururlar, iyilikten alıkoyarlar. Ellerini sıkı tutarlar. Onlar, Allah'ı unuttular, Allah da onları unuttu. Münafıklar, fasıkların ta kendileridir.", transliteration: "El munafikune vel munafikatu ba'duhum min ba'd, ye'murune bil munkeri ve yenhevne anil ma'rufi ve yakbidune eydiyehum nesullahe fe nesiyehum innel munafıkine humul fasikun."},
  {id: 1303, sure_no: 68, ayet_no: 68, sure_adi: "Tevbe", text: "وَعَدَ اللّٰهُ الْمُنَافِق۪ينَ وَالْمُنَافِقَاتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَاۜ هِيَ حَسْبُهُمْۚ وَلَعَنَهُمُ اللّٰهُۚ وَلَهُمْ عَذَابٌ مُق۪يمٌۙ", translation: "Allah, Münafık erkeklere ve Münafık kadınlara ve Kafirlere, Cehennem ateşini vadetmiştir. Orada sürekli kalıcıdırlar. Bu, onlara yeter. Allah, onları lanetlemiştir ve onlar için kalıcı bir azap vardır.", transliteration: "Vaadallahul munafikine vel munafikati vel kuffare nare cehenneme halidine fiha hiye hasbuhum, ve leanehumullah ve lehum azabun mukim ."},
  {id: 1304, sure_no: 69, ayet_no: 69, sure_adi: "Tevbe", text: "كَالَّذ۪ينَ مِنْ قَبْلِكُمْ كَانُٓوا اَشَدَّ مِنْكُمْ قُوَّةً وَاَكْثَرَ اَمْوَالاً وَاَوْلَاداًۜ فَاسْتَمْتَعُوا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذ۪ينَ مِنْ قَبْلِكُمْ بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذ۪ي خَاضُواۜ اُو۬لٰٓئِكَ حَبِطَتْ اَعْمَالُهُمْ فِي الدُّنْيَا وَالْاٰخِرَةِۚ وَاُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ", translation: "Tıpkı sizden öncekiler gibisiniz. Onlar; kuvvetçe sizden daha güçlü, mal ve evlat bakımından sizden daha çoktular. Onlar, payları kadar bundan yararlandılar. Sizden öncekilerin paylarınca yararlandıkları gibi siz de payınızca yararlandınız. Ve onların daldığı gibi siz de daldınız. İşte onların yaptıkları dünyada da ahirette de boşa gitmiştir. Onlar hüsrana uğrayanlardır.", transliteration: "Kellezine min kablikum kanu eşedde minkum kuvveten ve eksere emvalen ve evlada, festemteu bi halakihim, festemta'tum bi halakikum kemastemteallezine min kablikum bi halakihim ve hudtum kellezi hadu, ulaike habitat a'maluhum fid dunya vel ahıreh, ve ulaike humul hasirun ."},
  {id: 1305, sure_no: 70, ayet_no: 70, sure_adi: "Tevbe", text: "اَلَمْ يَأْتِهِمْ نَبَاُ الَّذ۪ينَ مِنْ قَبْلِهِمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَقَوْمِ اِبْرٰه۪يمَ وَاَصْحَابِ مَدْيَنَ وَالْمُؤْتَفِكَاتِۜ اَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِۚ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Onlara, kendilerinden öncekilerin; Nuh, Ad ve Semud halklarının, İbrahim halkının ve Medyen sahiplerinin ve mutefikelerin haberi gelmedi mi? Resulleri onlara açık kanıtlar getirmişti. Allah, onlara haksızlık etmiş değildi. Fakat onlar kendi kendilerine haksızlık yapıyorlardı.", transliteration: "E lem ye'tihim nebeullezine min kablihim kavmi nuhin ve adn ve semude ve kavmi ibrahime ve ashabi medyene vel mu'tefikat, etethum rusuluhum bil beyyinat, fe ma kanallahu li yazlimehum ve lakin kanu enfusehum yazlimun."},
  {id: 1306, sure_no: 71, ayet_no: 71, sure_adi: "Tevbe", text: "وَالْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۢ يَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَيُط۪يعُونَ اللّٰهَ وَرَسُولَهُۜ اُو۬لٰٓئِكَ سَيَرْحَمُهُمُ اللّٰهُۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ", translation: "Mü'min erkekler ve Mü'min kadınlar birbirlerinin velileridirler. Ma'ruf olanı yaparlar, münkerden sakındırırlar. Salatı ikame ederler, zekatı yaparlar. Allah'a ve Resul'üne itaat ederler. İşte bunlara, Allah rahmet edecektir. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Vel mu'minune vel mu'minatu ba'duhum evliyau ba'd, ye'murune bil ma'rufi ve yenhevne anil munkeri ve yukimunas salate ve yu'tunez zekate ve yutiunallahe ve resuleh, ulaike se yerhamuhumullah, innallahe azizun hakim."},
  {id: 1307, sure_no: 72, ayet_no: 72, sure_adi: "Tevbe", text: "وَعَدَ اللّٰهُ الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا وَمَسَاكِنَ طَيِّبَةً ف۪ي جَنَّاتِ عَدْنٍۜ وَرِضْوَانٌ مِنَ اللّٰهِ اَكْبَرُۜ ذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُ۟", translation: "Allah, Mü'min erkeklere ve Mü'min kadınlara, içinden ırmaklar akan, içinde sürekli kalacakları Cennetler ve Adn Cennetleri'nde temiz yerleşim yerleri söz verdi. Allah'ın razı olması ise daha büyüktür. İşte büyük başarı budur.", transliteration: "Vaadallahul mu'minine vel mu'minati cennatin tecri min tahtihel enharu halidine fiha ve mesakine tayyibeten fi cennati adn, ve rıdvanun minallahi ekber, zalike huvel fevzul azim."},
  {id: 1308, sure_no: 73, ayet_no: 73, sure_adi: "Tevbe", text: "يَٓا اَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِق۪ينَ وَاغْلُظْ عَلَيْهِمْۜ وَمَأْوٰيهُمْ جَهَنَّمُۜ وَبِئْسَ الْمَص۪يرُ", translation: "Ey Nebi! Kafirlerle ve Münafıklarla cihat et. Onlara karşı kararlılıkla mücadele et. Onların varacakları yer Cehennem'dir. O, ne kötü varış yeridir.", transliteration: "Ya eyyuhen nebiyyu cahidil kuffare vel munafikine vagluz aleyhim, ve me'vahum cehennem, ve bi'sel masir."},
  {id: 1309, sure_no: 74, ayet_no: 74, sure_adi: "Tevbe", text: "يَحْلِفُونَ بِاللّٰهِ مَا قَالُواۜ وَلَقَدْ قَالُوا كَلِمَةَ الْكُفْرِ وَكَفَرُوا بَعْدَ اِسْلَامِهِمْ وَهَمُّوا بِمَا لَمْ يَنَالُواۚ وَمَا نَقَمُٓوا اِلَّٓا اَنْ اَغْنٰيهُمُ اللّٰهُ وَرَسُولُهُ مِنْ فَضْلِه۪ۚ فَاِنْ يَتُوبُوا يَكُ خَيْراً لَهُمْۚ وَاِنْ يَتَوَلَّوْا يُعَذِّبْهُمُ اللّٰهُ عَذَاباً اَل۪يماً فِي الدُّنْيَا وَالْاٰخِرَةِۚ وَمَا لَهُمْ فِي الْاَرْضِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ", translation: "Münafıklar söylemediklerine dair Allah'a yemin ediyorlar. Ant olsun küfür sözünü söylediler. İslam olduktan sonra Kafir oldular. Elde edemeyecekleri bir şeye yöneldiler. Öç almaya kalkışmaları da ancak Allah ve Resulü'nün kendi fazlından onları zengin etmiş olmasındandır. Eğer tevbe ederlerse, haklarında hayırlı olur. Şayet yüz çevirirlerse, Allah onları dünyada ve ahirette can yakıcı azaba uğratır. Ve onlar için yeryüzünde ne bir veli ne bir yardımcı vardır.", transliteration: "Yahlifune billahi ma kalu, ve lekad kalu kelimetel kufri ve keferu ba'de islamihim ve hemmu bi ma lem yenalu, ve ma nekamu illa en egnahumullahu ve resuluhu min fadlih, fe in yetubu yeku hayren lehum, ve in yetevellev yuazzibhumullahu azaben elimen fid dunya vel ahıreh, ve ma lehum fil ardı min veliyyin ve la nasir."},
  {id: 1310, sure_no: 75, ayet_no: 75, sure_adi: "Tevbe", text: "وَمِنْهُمْ مَنْ عَاهَدَ اللّٰهَ لَئِنْ اٰتٰينَا مِنْ فَضْلِه۪ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ الصَّالِح۪ينَ", translation: "Onlardan kimi de \"Eğer lütfundan bize verirse, ant olsun sadaka vereceğiz ve ant olsun salihlerden olacağız.\" diye Allah'a söz vermişlerdi.", transliteration: "Ve minhum men ahedallahe le in atana min fadlihi Le nessaddekanne ve le nekunenne mines salihin."},
  {id: 1311, sure_no: 76, ayet_no: 76, sure_adi: "Tevbe", text: "فَلَمَّٓا اٰتٰيهُمْ مِنْ فَضْلِه۪ بَخِلُوا بِه۪ وَتَوَلَّوْا وَهُمْ مُعْرِضُونَ", translation: "Allah, onlara lütfundan verince, onlar cimrilik edip yüz çevirdiler. Zaten onlar dönektirler.", transliteration: "Fe lemma atahum min fadlihi bahılu bihi ve tevellev ve hum mu'ridun."},
  {id: 1312, sure_no: 77, ayet_no: 77, sure_adi: "Tevbe", text: "فَاَعْقَبَهُمْ نِفَاقاً ف۪ي قُلُوبِهِمْ اِلٰى يَوْمِ يَلْقَوْنَهُ بِمَٓا اَخْلَفُوا اللّٰهَ مَا وَعَدُوهُ وَبِمَا كَانُوا يَكْذِبُونَ", translation: "Allah'a verdikleri sözü tutmamaları ve yalan söylemeleri nedeniyle, Kendisi ile karşılaşacakları güne kadar onların kalplerine nifak soktu.", transliteration: "Fe a'kabehum nifakan fi kulubihim ila yevmi yelkavnehu bi ma ahlefullahe ma vaaduhu ve bi ma kanu yekzibun."},
  {id: 1313, sure_no: 78, ayet_no: 78, sure_adi: "Tevbe", text: "اَلَمْ يَعْلَمُٓوا اَنَّ اللّٰهَ يَعْلَمُ سِرَّهُمْ وَنَجْوٰيهُمْ وَاَنَّ اللّٰهَ عَلَّامُ الْغُيُوبِۚ", translation: "Bilmiyorlar mı ki, Allah onların sırlarını da fısıldaşmalarını da bilmektedir. Allah, gaybı bilendir.", transliteration: "E lem ya'lemu ennallahe ya'lemu sırrehum ve necvahum ve ennallahe allamul guyub."},
  {id: 1314, sure_no: 79, ayet_no: 79, sure_adi: "Tevbe", text: "اَلَّذ۪ينَ يَلْمِزُونَ الْمُطَّوِّع۪ينَ مِنَ الْمُؤْمِن۪ينَ فِي الصَّدَقَاتِ وَالَّذ۪ينَ لَا يَجِدُونَ اِلَّا جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْۜ سَخِرَ اللّٰهُ مِنْهُمْۘ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Sadakalar konusunda gönülden davranan Mü'minlere dil uzatanlar ve güçleri oranında verebilenleri alaya alanlar var ya, Allah da onları alaya alacaktır. Onlar için can yakıcı bir azap vardır.", transliteration: "Ellezine yelmizunel muttavviine minel mu'minine fis sadakati vellezine la yecidune illa cuhdehum fe yesharune minhum sehirallahu minhum, ve lehum azabun elim."},
  {id: 1315, sure_no: 80, ayet_no: 80, sure_adi: "Tevbe", text: "اِسْتَغْفِرْ لَهُمْ اَوْ لَا تَسْتَغْفِرْ لَهُمْۜ اِنْ تَسْتَغْفِرْ لَهُمْ سَبْع۪ينَ مَرَّةً فَلَنْ يَغْفِرَ اللّٰهُ لَهُمْۜ ذٰلِكَ بِاَنَّهُمْ كَفَرُوا بِاللّٰهِ وَرَسُولِه۪ۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ۟", translation: "Onlar için ister bağışlanma dile ister dileme. Onlar için yetmiş defa bağışlanma dilesen de yine Allah onları bağışlamayacaktır. Bu, onların Allah'ı ve Resul'ünü inkar etmelerindendir. Allah, fasık olan halkı doğru yola iletmez.", transliteration: "İstagfir lehum ev la testagfir lehum, in testagfir lehum seb'ine merreten fe len yagfirallahu lehum, zalike bi ennehum keferu billahi ve resulih, vallahu la yehdil kavmel fasikin."},
  {id: 1316, sure_no: 81, ayet_no: 81, sure_adi: "Tevbe", text: "فَرِحَ الْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلَافَ رَسُولِ اللّٰهِ وَكَرِهُٓوا اَنْ يُجَاهِدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ ف۪ي سَب۪يلِ اللّٰهِ وَقَالُوا لَا تَنْفِرُوا فِي الْحَرِّۜ قُلْ نَارُ جَهَنَّمَ اَشَدُّ حَراًّۜ لَوْ كَانُوا يَفْقَهُونَ", translation: "Allah'ın Resul'üne muhalefet ederek geride kalanlar, oturup kalmalarına sevindiler. Mallarıyla, canlarıyla cihad etmekten hoşlanmadılar. Bir de \"Bu sıcakta savaşa çıkmayın.\" dediler. De ki: \"Cehennem ateşi daha sıcaktır.\" Keşke anlasalardı!", transliteration: "Ferihal muhallefune bi mak'adihim hılafe resulillahi ve kerihuen yucahidu bi emvalihim ve enfusihim fi sebilillahi ve kalu la tenfiru fil harr, kul naru cehennemeeşeddu harra, lev kanu yefkahun."},
  {id: 1317, sure_no: 82, ayet_no: 82, sure_adi: "Tevbe", text: "فَلْيَضْحَكُوا قَل۪يلاً وَلْيَبْكُوا كَث۪يراًۚ جَزَٓاءً بِمَا كَانُوا يَكْسِبُونَ", translation: "Öyleyse yaptıklarına karşılık, az gülüp, çok ağlasınlar.", transliteration: "Fel yadhaku kalilen vel yebku kesira, cezaen bi ma kanu yeksibun."},
  {id: 1318, sure_no: 83, ayet_no: 83, sure_adi: "Tevbe", text: "فَاِنْ رَجَعَكَ اللّٰهُ اِلٰى طَٓائِفَةٍ مِنْهُمْ فَاسْتَأْذَنُوكَ لِلْخُرُوجِ فَقُلْ لَنْ تَخْرُجُوا مَعِيَ اَبَداً وَلَنْ تُقَاتِلُوا مَعِيَ عَدُواًّۜ اِنَّكُمْ رَض۪يتُمْ بِالْقُعُودِ اَوَّلَ مَرَّةٍ فَاقْعُدُوا مَعَ الْخَالِف۪ينَ", translation: "Eğer Allah, seni onlardan bir grupla karşılaştırırsa, onlar da seninle savaşa çıkmak için izin isterlerse; de ki: \"Benimle asla çıkamayacaksınız. Artık benimle hiçbir düşmanla savaşamayacaksınız. Çünkü siz daha önce oturup kalmayı tercih ettiniz. Artık, geride kalanlarla oturun!\"", transliteration: "Fe in receakallahu ila taifetin minhum feste'zenuke lil huruci fe kul len tahrucu maiye ebeden ve len tukatilu maiye aduvv, innekumraditum bil ku'udi evvele merretin fak'udu meal halifin."},
  {id: 1319, sure_no: 84, ayet_no: 84, sure_adi: "Tevbe", text: "وَلَا تُصَلِّ عَلٰٓى اَحَدٍ مِنْهُمْ مَاتَ اَبَداً وَلَا تَقُمْ عَلٰى قَبْرِه۪ۜ اِنَّهُمْ كَفَرُوا بِاللّٰهِ وَرَسُولِه۪ وَمَاتُوا وَهُمْ فَاسِقُونَ", translation: "Onlardan ölen hiçbir kimseye, asla salat etme ve kabirlerinin başında da durma. Çünkü onlar, Allah ve Resul'ünü küfrettiler. Ve onlar fasık olarak öldüler.", transliteration: "Ve la tusalli ala ehadin minhum mate ebeden ve la tekum ala kabrih, innehum keferu billahi ve resulihi ve matu ve hum fasikun ."},
  {id: 1320, sure_no: 85, ayet_no: 85, sure_adi: "Tevbe", text: "وَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَاَوْلَادُهُمْۜ اِنَّمَا يُر۪يدُ اللّٰهُ اَنْ يُعَذِّبَهُمْ بِهَا فِي الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كَافِرُونَ", translation: "Öyleyse, onların malları ve çocukları seni imrendirmesin. Doğrusu, Allah, bunlarla, onlara dünya hayatında azap etmeyi ve canlarının Kafir olarak çıkmasını istiyor.", transliteration: "Ve La tu'cibke emvaluhum ve evladuhum, innema yuridullahu en yuazzibehum biha fid dunya ve tezheka enfusuhum ve hum kafirun."},
  {id: 1321, sure_no: 86, ayet_no: 86, sure_adi: "Tevbe", text: "وَاِذَٓا اُنْزِلَتْ سُورَةٌ اَنْ اٰمِنُوا بِاللّٰهِ وَجَاهِدُوا مَعَ رَسُولِهِ اسْتَأْذَنَكَ اُو۬لُوا الطَّوْلِ مِنْهُمْ وَقَالُوا ذَرْنَا نَكُنْ مَعَ الْقَاعِد۪ينَ", translation: "\"Allah'a iman edin, Resul'ü ile birlikte cihad edin.\" diye bir sure indirildiği zaman, onlardan servet sahibi olanlar, senden izin istediler: \"Bırak bizi oturanlarla beraber oturalım.\" dediler.", transliteration: "Ve iza unzilet suretun en aminu billahi ve cahidu mearesulihiste'zeneke ulut tavli minhum ve kaluzerna nekun meal ka'ıdin."},
  {id: 1322, sure_no: 87, ayet_no: 87, sure_adi: "Tevbe", text: "رَضُوا بِاَنْ يَكُونُوا مَعَ الْخَوَالِفِ وَطُبِـعَ عَلٰى قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ", translation: "Onlar, geride kalanlarla beraber olmayı tercih ettiler. Kalpleri mühürlendi, artık onlar anlamazlar.", transliteration: "Radu bi en yekunu meal havalifi ve tubia ala kulubihim fe hum la yefkahun."},
  {id: 1323, sure_no: 88, ayet_no: 88, sure_adi: "Tevbe", text: "لٰكِنِ الرَّسُولُ وَالَّذ۪ينَ اٰمَنُوا مَعَهُ جَاهَدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۜ وَاُو۬لٰٓئِكَ لَهُمُ الْخَيْرَاتُۘ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "Fakat Resul ve onunla birlikte iman edenler, mallarıyla, canlarıyla cihad ettiler. İşte onlar, bütün hayırlar onlarındır. İşte kurtuluşa erenler onlardır.", transliteration: "Lakinir resulu vellezine amenu meahu cahedu bi emvalihim ve enfusihim, ve ulaike lehumul hayratu ve ulaike humul muflihun."},
  {id: 1324, sure_no: 89, ayet_no: 89, sure_adi: "Tevbe", text: "اَعَدَّ اللّٰهُ لَهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ ذٰلِكَ الْفَوْزُ الْعَظ۪يمُ۟", translation: "Allah, onlar için içinden ırmaklar akan, içinde süresiz kalacakları Cennetler hazırlamıştır. İşte büyük başarı budur.", transliteration: "Eaddallahu lehum cennatin tecri min tahtihel enharu halidine fiha, zalikel fevzul azim."},
  {id: 1325, sure_no: 90, ayet_no: 90, sure_adi: "Tevbe", text: "وَجَٓاءَ الْمُعَذِّرُونَ مِنَ الْاَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذ۪ينَ كَذَبُوا اللّٰهَ وَرَسُولَهُۜ سَيُص۪يبُ الَّذ۪ينَ كَفَرُوا مِنْهُمْ عَذَابٌ اَل۪يمٌ", translation: "Özür beyan eden Bedevi Araplar, kendilerine izin verilmesi için geldiler. Allah'a ve Resul'üne yalan söyleyenler ise oturup kaldılar. Onlardan Kafir olanlara yakında can yakıcı bir azap dokunacaktır.", transliteration: "Ve cael muazzirune minel a'rabi li yu'zene lehum ve ka'adellezine kezebullahe ve resuleh, se yusibullezine keferu minhum azabun elim."},
  {id: 1326, sure_no: 91, ayet_no: 91, sure_adi: "Tevbe", text: "لَيْسَ عَلَى الضُّعَفَٓاءِ وَلَا عَلَى الْمَرْضٰى وَلَا عَلَى الَّذ۪ينَ لَا يَجِدُونَ مَا يُنْفِقُونَ حَرَجٌ اِذَا نَصَحُوا لِلّٰهِ وَرَسُولِه۪ۜ مَا عَلَى الْمُحْسِن۪ينَ مِنْ سَب۪يلٍۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌۙ", translation: "Zayıflara, hastalara ve harcayacakları bir şeyi olmayanlara, Allah ve Resul'üne bağlı kaldıkları sürece bir sorumluluk yoktur. İyilik edenlerin aleyhine de bir yol yoktur. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Leyse alad duafai ve la alel merda ve la alellezine la yecidune ma yunfikune haracun iza nasahu lillahi ve resulih, ma alel muhsinine min sebil, vallahu gafurun rahim."},
  {id: 1327, sure_no: 92, ayet_no: 92, sure_adi: "Tevbe", text: "وَلَا عَلَى الَّذ۪ينَ اِذَا مَٓا اَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَٓا اَجِدُ مَٓا اَحْمِلُكُمْ عَلَيْهِۖ تَوَلَّوْا وَاَعْيُنُهُمْ تَف۪يضُ مِنَ الدَّمْعِ حَزَناً اَلَّا يَجِدُوا مَا يُنْفِقُونَۜ", translation: "Bir de sana, kendilerine binek sağlaman için başvurduklarında, \"Sizi bindirecek bir şey bulamıyorum.\" dediğin zaman, harcayacakları bir şeyleri olmadığı için üzüntüden gözlerinden yaşlar akıtarak geri dönüp gidenlere de bir sorumluluk yoktur.", transliteration: "Ve la alellezine iza ma etevke li tahmilehum kulte la ecidu maahmilukum aleyhi tevellev ve a'yunuhum tefidu mined dem'i hazenen ella yecidu ma yunfikun."},
  {id: 1328, sure_no: 93, ayet_no: 93, sure_adi: "Tevbe", text: "اِنَّمَا السَّب۪يلُ عَلَى الَّذ۪ينَ يَسْتَأْذِنُونَكَ وَهُمْ اَغْنِيَٓاءُۚ رَضُوا بِاَنْ يَكُونُوا مَعَ الْخَوَالِفِۙ وَطَبَعَ اللّٰهُ عَلٰى قُلُوبِهِمْ فَهُمْ لَا يَعْلَمُونَ", translation: "Ancak imkanları olduğu halde senden izin isteyenler kınanmalıdır. Onlar, geride kalanlarla beraber olmayı istediler. Allah da onların kalplerini mühürledi. Bu yüzden artık onlar yaptıkları yanlışı idrak etmezler.", transliteration: "İnnemes sebilu alellezine yeste'zinuneke ve hum agniya', radu bi en yekunu meal havalifi ve tabeallahu ala kulubihim fe hum la ya'lemun."},
  {id: 1329, sure_no: 94, ayet_no: 94, sure_adi: "Tevbe", text: "يَعْتَذِرُونَ اِلَيْكُمْ اِذَا رَجَعْتُمْ اِلَيْهِمْۜ قُلْ لَا تَعْتَذِرُوا لَنْ نُؤْمِنَ لَكُمْ قَدْ نَبَّاَنَا اللّٰهُ مِنْ اَخْبَارِكُمْۜ وَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُولُهُ ثُمَّ تُرَدُّونَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Onlara döndüğünüz zaman size mazeret ileri sürerler. De ki: \"Mazeret ileri sürmeyin, size asla inanmayacağız. Allah, durumunuzdan bizi haberdar etti. Allah ve Resul'ü yapacaklarınızı görecektir. Sonra, görüneni ve görünmeyeni bilene döndürüleceksiniz. O, bütün yaptıklarınızı size bildirecektir.\"", transliteration: "Ya'tezirune ileykum iza reca'tum ileyhim, kul la ta'teziru len nu'mine lekum kad nebbe enallahu min ahbarikum, ve se yerallahu amelekum ve resuluhu summe tureddune ila alimil gaybi veş şehadeti fe yunebbiukum bi ma kuntum ta'melun."},
  {id: 1330, sure_no: 95, ayet_no: 95, sure_adi: "Tevbe", text: "سَيَحْلِفُونَ بِاللّٰهِ لَكُمْ اِذَا انْقَلَبْتُمْ اِلَيْهِمْ لِتُعْرِضُوا عَنْهُمْۜ فَاَعْرِضُوا عَنْهُمْۜ اِنَّهُمْ رِجْسٌۘ وَمَأْوٰيهُمْ جَهَنَّمُۚ جَزَٓاءً بِمَا كَانُوا يَكْسِبُونَ", translation: "Onlara döndüğünüz zaman, kendilerinden vazgeçmeniz için Allah'a yemin edecekler. Onlardan yüz çevirin. Çünkü onlar murdardır. Yaptıklarının karşılığı olarak varacakları yer Cehennem'dir.", transliteration: "Se yahlifune billahi lekum izenkalebtum ileyhim li tu'ridu anhum, fe a'rıdu anhum, innehum ricsun ve me'vahum cehennem , cezaen bi ma kanu yeksibun."},
  {id: 1331, sure_no: 96, ayet_no: 96, sure_adi: "Tevbe", text: "يَحْلِفُونَ لَكُمْ لِتَرْضَوْا عَنْهُمْۚ فَاِنْ تَرْضَوْا عَنْهُمْ فَاِنَّ اللّٰهَ لَا يَرْضٰى عَنِ الْقَوْمِ الْفَاسِق۪ينَ", translation: "Kendilerini hoş göresiniz diye, size yemin ederler. Siz, onlardan razı olsanız da bilesiniz ki Allah fasık olan toplumdan asla razı olmaz.", transliteration: "Yahlifune lekum li terdav anhum, fe in terdav anhum fe innallahe la yerda anil kavmil fasikin."},
  {id: 1332, sure_no: 97, ayet_no: 97, sure_adi: "Tevbe", text: "اَلْاَعْرَابُ اَشَدُّ كُفْراً وَنِفَاقاً وَاَجْدَرُ اَلَّا يَعْلَمُوا حُدُودَ مَٓا اَنْزَلَ اللّٰهُ عَلٰى رَسُولِه۪ۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Bedevi Araplar, küfür ve nifakta daha katıdırlar. Allah'ın Resul'üne indirdiği sınırları tanımamaya daha yatkındırlar. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "El a'rabu eşeddu kufren ve nifakan ve ecderu ella ya'lemu hududema enzelallahu ala resulih, vallahu alimun hakim."},
  {id: 1333, sure_no: 98, ayet_no: 98, sure_adi: "Tevbe", text: "وَمِنَ الْاَعْرَابِ مَنْ يَتَّخِذُ مَا يُنْفِقُ مَغْرَماً وَيَتَرَبَّصُ بِكُمُ الدَّوَٓائِرَۜ عَلَيْهِمْ دَٓائِرَةُ السَّوْءِۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ", translation: "Bedevi Araplar'dan kimisi de yaptığı infakı zarar sayar ve devrin değişmesini ve sizin başınıza kötü devirlerin gelmesini beklerler. Kötü devirler kendi başlarına gelsin. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve minel a'rabi men yettehızu ma yunfiku magremen ve yeterabbesu bi kumud devair, aleyhim dairetussev' , vallahu semiun alim."},
  {id: 1334, sure_no: 99, ayet_no: 99, sure_adi: "Tevbe", text: "وَمِنَ الْاَعْرَابِ مَنْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبَاتٍ عِنْدَ اللّٰهِ وَصَلَوَاتِ الرَّسُولِۜ اَلَٓا اِنَّهَا قُرْبَةٌ لَهُمْۜ سَيُدْخِلُهُمُ اللّٰهُ ف۪ي رَحْمَتِه۪ۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ۟", translation: "Bedevi Araplardan kimisi de Allah'a ve ahiret gününe inanır. İnfak ettiğini Allah katında yakınlığa ve Resul'ün selavatına vesile sayar. Gerçekten o, kendileri için yakınlık vesilesidir. Allah, onları rahmetine alacak. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve minel a'rabi men yu'minu billahi vel yevmil ahıri ve yettehızu ma yunfiku kurubatin indallahi ve salavatir resul, e la inneha kurbetunlehum, se yudhıluhumullahu fi rahmetih, innallahe gafurun rahim."},
  {id: 1335, sure_no: 100, ayet_no: 100, sure_adi: "Tevbe", text: "وَالسَّابِقُونَ الْاَوَّلُونَ مِنَ الْمُهَاجِر۪ينَ وَالْاَنْصَارِ وَالَّذ۪ينَ اتَّبَعُوهُمْ بِاِحْسَانٍۙ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوا عَنْهُ وَاَعَدَّ لَهُمْ جَنَّاتٍ تَجْر۪ي تَحْتَهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ ذٰلِكَ الْفَوْزُ الْعَظ۪يمُ", translation: "Muhacir ve Ensar'dan, öne geçenlerden ve iyilikte onları izleyenlerden Allah razı olmuştur. Onlar da O'ndan razı olmuştur. Onlara, içinde aralıksız ve sürekli kalacakları, içinden ırmaklar akan Cennetler hazırlamıştır. İşte büyük başarı budur.", transliteration: "Ves sabikunel evvelune minel muhacirine vel ensari vellezinettebeuhum bi ıhsanin radıyallahu anhum ve radu anhu ve eadde lehum cennatin tecri tahtehel enharu halidine fiha ebeda, zalikel fevzul azim."},
  {id: 1336, sure_no: 101, ayet_no: 101, sure_adi: "Tevbe", text: "وَمِمَّنْ حَوْلَكُمْ مِنَ الْاَعْرَابِ مُنَافِقُونَۜ وَمِنْ اَهْلِ الْمَد۪ينَةِ مَرَدُوا عَلَى النِّفَاقِ لَا تَعْلَمُهُمْۜ نَحْنُ نَعْلَمُهُمْۜ سَنُعَذِّبُهُمْ مَرَّتَيْنِ ثُمَّ يُرَدُّونَ اِلٰى عَذَابٍ عَظ۪يمٍۚ", translation: "Çevrenizdeki Bedevi Araplardan Münafık olanlar vardır. Ve Medine halkından da nifakta ileri gidenler vardır. Sen onları bilemezsin. Biz onları biliriz. Onlara iki defa azap edeceğiz. Sonra onlar, daha büyük azaba uğratılacaklardır.", transliteration: "Ve mimmen havlekum minel a'rabi munafikun, ve min ehlil medineti meredu alen nifakı la ta'lemuhum, nahnu na'lemuhum, se nuazzibuhum merreteyni summe yureddune ila azabin azim."},
  {id: 1337, sure_no: 102, ayet_no: 102, sure_adi: "Tevbe", text: "وَاٰخَرُونَ اعْتَرَفُوا بِذُنُوبِهِمْ خَلَطُوا عَمَلاً صَالِحاً وَاٰخَرَ سَيِّئاًۜ عَسَى اللّٰهُ اَنْ يَتُوبَ عَلَيْهِمْۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Diğer bir kısmı da suçlarını itiraf ettiler. Onlar, iyi bir ameli kötü bir amelle karıştırmışlardı. Belki Allah, onların tevbesini kabul eder. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve aharuna'terefu bi zunubihim haletu amelen salihan ve ahare seyyia, asallahu en yetube aleyhim, innallahe gafurun rahim."},
  {id: 1338, sure_no: 103, ayet_no: 103, sure_adi: "Tevbe", text: "خُذْ مِنْ اَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكّ۪يهِمْ بِهَا وَصَلِّ عَلَيْهِمْۜ اِنَّ صَلٰوتَكَ سَكَنٌ لَهُمْۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ", translation: "Onların mallarından sadaka al; bununla onları temizleyip arındırırsın. Ve onlara salli ol, kuşkusuz senin salatın onlara dinginlik verir. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Huz min emvalihim sadakaten tutahhiruhum ve tuzekkihim biha ve salli aleyhim, inne salateke sekenun lehum, vallahu semiun alim."},
  {id: 1339, sure_no: 104, ayet_no: 104, sure_adi: "Tevbe", text: "اَلَمْ يَعْلَمُٓوا اَنَّ اللّٰهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِه۪ وَيَأْخُذُ الصَّدَقَاتِ وَاَنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّح۪يمُ", translation: "Bilmediler mi ki kullarından tevbeyi kabul eden ve sadakaları alan Allah'tır. Kuşkusuz Allah, Tevbeleri Kabul Eden'dir, Rahmeti Kesintisiz'dir.", transliteration: "E lem ya'lemu ennallahe huve yakbelut tevbete an ibadihi ve ye'huzus sadakati ve ennallahe huvet tevvabur rahim."},
  {id: 1340, sure_no: 105, ayet_no: 105, sure_adi: "Tevbe", text: "وَقُلِ اعْمَلُوا فَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُولُهُ وَالْمُؤْمِنُونَۜ وَسَتُرَدُّونَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَۚ", translation: "De ki: \"Ne yaparsanız yapın. Yaptıklarınızı Allah, O'nun Resul'ü ve Mü'minler görecekler. Sonra, görüneni de görünmeyeni de Bilen'e döndürüleceksiniz. O, size yaptıklarınızı haber verecektir.\"", transliteration: "Ve kuli'melu fe se yerallahu amelekum ve resuluhu vel mu'minun, ve se tureddune ila alimil gaybi veş şehadeti fe yunebbiukum bi ma kuntum ta'melun."},
  {id: 1341, sure_no: 106, ayet_no: 106, sure_adi: "Tevbe", text: "وَاٰخَرُونَ مُرْجَوْنَ لِاَمْرِ اللّٰهِ اِمَّا يُعَذِّبُهُمْ وَاِمَّا يَتُوبُ عَلَيْهِمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Geri kalan bir bölümün işi de Allah'ın yargısına kalmıştır; onlara ya azap eder veya tevbelerini kabul eder. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve aharune murcevne li emrillahi imma yuazzibuhum ve imma yetubu aleyhim, vallahu alimun hakim."},
  {id: 1342, sure_no: 107, ayet_no: 107, sure_adi: "Tevbe", text: "وَالَّذ۪ينَ اتَّخَذُوا مَسْجِداً ضِرَاراً وَكُفْراً وَتَفْر۪يقاً بَيْنَ الْمُؤْمِن۪ينَ وَاِرْصَاداً لِمَنْ حَارَبَ اللّٰهَ وَرَسُولَهُ مِنْ قَبْلُۜ وَلَيَحْلِفُنَّ اِنْ اَرَدْنَٓا اِلَّا الْحُسْنٰىۜ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكَاذِبُونَ", translation: "Zarar vermek, inkar etmek, nifak çıkarmak, Allah ve Resul'üne karşı daha önce savaşanlara gözcülük yapmak üzere bir mescit yapan kimseler: \"Biz yalnızca iyilik yapmak istedik.\" diye yemin ederler. Oysaki Allah, onların yalan söylediklerine tanıktır.", transliteration: "Vellezinettehazu mesciden dıraren ve kufren ve tefrikan beynel mu'minine ve irsaden li men hareballahe ve resulehu min kabl, ve le yahlifunne in eredna illelhusna, vallahu yeşhedu innehum le kazibun."},
  {id: 1343, sure_no: 108, ayet_no: 108, sure_adi: "Tevbe", text: "لَا تَقُمْ ف۪يهِ اَبَداًۜ لَمَسْجِدٌ اُسِّسَ عَلَى التَّقْوٰى مِنْ اَوَّلِ يَوْمٍ اَحَقُّ اَنْ تَقُومَ ف۪يهِۜ ف۪يهِ رِجَالٌ يُحِبُّونَ اَنْ يَتَطَهَّرُواۜ وَاللّٰهُ يُحِبُّ الْمُطَّهِّر۪ينَ", translation: "Orada asla durma, ilk yapıldığında takva üzere yapılan mescit, içinde bulunmaya daha layıktır. Orada arınmayı seven kişiler vardır. Allah, arınmak isteyenleri sever.", transliteration: "La tekum fihi ebeda, le mescidun ussise alet takva min evveli yevmin ehakku en tekume fih, fihi ricalun yuhıbbune en yetetahheru, vallahu yuhıbbul muttahhirin."},
  {id: 1344, sure_no: 109, ayet_no: 109, sure_adi: "Tevbe", text: "اَفَمَنْ اَسَّسَ بُنْيَانَهُ عَلٰى تَقْوٰى مِنَ اللّٰهِ وَرِضْوَانٍ خَيْرٌ اَمْ مَنْ اَسَّسَ بُنْيَانَهُ عَلٰى شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِه۪ ف۪ي نَارِ جَهَنَّمَۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ", translation: "Binasını takva üzere, Allah rızası için kuran kimse mi, yoksa binasını uçurumun kenarına kurup da onunla birlikte Cehennem ateşinin içine yuvarlanan kimse mi hayırlı olandır? Allah, zalim halkı doğru yola iletmez.", transliteration: "E fe men essese bunyanehu ala takva minallahi ve rıdvanin hayrun em men essese bunyanehu ala şefa curufin harin fenhare bihi fi nari cehennem, vallahu la yehdil kavmez zalimin."},
  {id: 1345, sure_no: 110, ayet_no: 110, sure_adi: "Tevbe", text: "لَا يَزَالُ بُنْيَانُهُمُ الَّذ۪ي بَنَوْا ر۪يبَةً ف۪ي قُلُوبِهِمْ اِلَّٓا اَنْ تَقَطَّعَ قُلُوبُهُمْۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ۟", translation: "Onların kalpleri parçalanmadıkça, kurdukları yapı, kalplerinde bir kuşku olarak sürüp gidecektir. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "La yezalu bunyanuhumullezi benev ribeten fi kulubihim illa en tekattaa kulubuhum, vallahu alimun hakim."},
  {id: 1346, sure_no: 111, ayet_no: 111, sure_adi: "Tevbe", text: "اِنَّ اللّٰهَ اشْتَرٰى مِنَ الْمُؤْمِن۪ينَ اَنْفُسَهُمْ وَاَمْوَالَهُمْ بِاَنَّ لَهُمُ الْجَنَّةَۜ يُقَاتِلُونَ ف۪ي سَب۪يلِ اللّٰهِ فَيَقْتُلُونَ وَيُقْتَلُونَ وَعْداً عَلَيْهِ حَقاًّ فِي التَّوْرٰيةِ وَالْاِنْج۪يلِ وَالْقُرْاٰنِۜ وَمَنْ اَوْفٰى بِعَهْدِه۪ مِنَ اللّٰهِ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذ۪ي بَايَعْتُمْ بِه۪ۜ وَذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُ", translation: "Allah, kendi yolunda savaşarak ölen ve öldüren Mü'minlerin; canlarını ve mallarını Cennet karşılığında satın almıştır. Bu, Tevrat'ta, İncil'de ve Kur'an'da gerçek olan bir söz vermedir. Allah'tan daha iyi sözünde duran kim olabilir? O halde, O'nunla yapmış olduğunuz bu alışverişten dolayı sevinin. İşte büyük başarı budur.", transliteration: "İnnallaheştera minel mu'minine enfusehum ve emvalehum bi enne lehumul cenneh, yukatilune fi sebilillahi fe yaktulune ve yuktelune va'den aleyhi hakkan fit tevrati vel incili vel kur'an, ve men evfa bi ahdihi minallahi, festebşiru bi bey'ıkumullezi baya'tum bih , ve zalike huvel fevzul azim."},
  {id: 1347, sure_no: 112, ayet_no: 112, sure_adi: "Tevbe", text: "اَلتَّٓائِبُونَ الْعَابِدُونَ الْحَامِدُونَ السَّٓائِحُونَ الرَّاكِعُونَ السَّاجِدُونَ الْاٰمِرُونَ بِالْمَعْرُوفِ وَالنَّاهُونَ عَنِ الْمُنْكَرِ وَالْحَافِظُونَ لِحُدُودِ اللّٰهِۜ  وَبَشِّرِ الْمُؤْمِن۪ينَ", translation: "Tevbe edenler, kulluk edenler, hamd edenler, seyahat edenler, ruku edenler, secde edenler, mar'uf olanı yapıp, münker olana engel olanlar; Allah'ın hudutlarını koruyanlardır. Mü'minleri müjdele.", transliteration: "Ettaibunel abidunel hamidunes saihuner rakiunes sacidunel amirune bil ma'rufi ven nahune anil munkeri vel hafizune li hududillah , ve beşşiril mu'minin ."},
  {id: 1348, sure_no: 113, ayet_no: 113, sure_adi: "Tevbe", text: "مَا كَانَ لِلنَّبِيِّ وَالَّذ۪ينَ اٰمَنُٓوا اَنْ يَسْتَغْفِرُوا لِلْمُشْرِك۪ينَ وَلَوْ كَانُٓوا اُو۬ل۪ي قُرْبٰى مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمْ اَنَّهُمْ اَصْحَابُ الْجَح۪يمِ", translation: "Nebi ve Mü'minlere; Cehennem'lik oldukları açıkça belli olduktan sonra, yakınları da olsa, Müşriklere bağışlanma dilemeleri yaraşmaz.", transliteration: "Ma kane lin nebiyyi vellezine amenu en yestagfiru lil muşrikine ve lev kanu uli kurba min ba'di ma tebeyyene lehum ennehum ashabul cahim."},
  {id: 1349, sure_no: 114, ayet_no: 114, sure_adi: "Tevbe", text: "وَمَا كَانَ اسْتِغْفَارُ اِبْرٰه۪يمَ لِاَب۪يهِ اِلَّا عَنْ مَوْعِدَةٍ وَعَدَهَٓا اِيَّاهُۚ فَلَمَّا تَبَيَّنَ لَـهُٓ اَنَّهُ عَدُوٌّ لِلّٰهِ تَبَرَّاَ مِنْهُۜ اِنَّ اِبْرٰه۪يمَ لَاَوَّاهٌ حَل۪يمٌ", translation: "İbrahim'in babası için bağışlanma dilemesinin sebebi, ona söz vermiş olmasıydı. Ama onun, Allah'a düşman olduğu kendisine açıkça belli olunca, ondan uzaklaştı. Kuşkusuz İbrahim, çok ince ruhlu ve yumuşak huylu birisiydi.", transliteration: "Ve ma kanestigfaru ibrahime li ebihi illa an mev'ıdetin vaadeha iyyah, fe lemma tebeyyene lehu ennehuaduvvun lillahi teberre'e minh, inne ibrahime le evvahun halim."},
  {id: 1350, sure_no: 115, ayet_no: 115, sure_adi: "Tevbe", text: "وَمَا كَانَ اللّٰهُ لِيُضِلَّ قَوْماً بَعْدَ اِذْ هَدٰيهُمْ حَتّٰى يُبَيِّنَ لَهُمْ مَا يَتَّقُونَۜ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Allah, bir halkı doğru yola ilettikten sonra, sakınıp korunacakları şeyleri kendilerine açıklamadıkça onları sapıtmış saymaz. Allah, Her Şeyi En İyi Bilendir.", transliteration: "Ve ma kanallahu li yudılle kavmen ba'de iz hedahum hatta yubeyyine lehum ma yettekun, innallahe bi kulli şey'in alim."},
  {id: 1351, sure_no: 116, ayet_no: 116, sure_adi: "Tevbe", text: "اِنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ يُحْـي۪ وَيُم۪يتُۜ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ", translation: "Göklerin ve yerin mülkü Allah'ındır. Yaşatan ve öldüren O'dur. Sizin, Allah'tan başka bir veli ve bir yardımcınız yoktur.", transliteration: "İnnallahe lehu mulkus semavati vel ard, yuhyi ve yumit, ve ma lekum min dunillahi min veliyyin ve la nasir."},
  {id: 1352, sure_no: 117, ayet_no: 117, sure_adi: "Tevbe", text: "لَقَدْ تَابَ اللّٰهُ عَلَى النَّبِيِّ وَالْمُهَاجِر۪ينَ وَالْاَنْصَارِ الَّذ۪ينَ اتَّبَعُوهُ ف۪ي سَاعَةِ الْعُسْرَةِ مِنْ بَعْدِ مَا كَادَ يَز۪يغُ قُلُوبُ فَر۪يقٍ مِنْهُمْ ثُمَّ تَابَ عَلَيْهِمْۜ اِنَّهُ بِهِمْ رَؤُ۫فٌ رَح۪يمٌۙ", translation: "Ant olsun ki, Allah, Nebi'nin ve zor şartlarda ona destek olan Muhacir ve Ensar'ın tevbelerini kabul etti. İçlerinden bir kısmının kalpleri kaymak üzereyken yine de onların tevbelerini kabul etti. Çünkü O, onlara karşı Çok Şefkatli'dir, Rahmeti Kesintisiz'dir.", transliteration: "Lekad taballahu alen nebiyyi vel muhacirine vel ensarillezinet tebeuhu fi saatil usreti min ba'di ma kade yezigu kulubu ferikın minhum summe tabe aleyhim, innehu bihim raufun rahim."},
  {id: 1353, sure_no: 118, ayet_no: 118, sure_adi: "Tevbe", text: "وَعَلَى الثَّلٰثَةِ الَّذ۪ينَ خُلِّفُواۜ حَتّٰٓى اِذَا ضَاقَتْ عَلَيْهِمُ الْاَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ اَنْفُسُهُمْ وَظَنُّٓوا اَنْ لَا مَلْجَأَ مِنَ اللّٰهِ اِلَّٓا اِلَيْهِۜ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوبُواۜ اِنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّح۪يمُ۟", translation: "Ve geri bırakılan üç kişinin tevbesini de kabul etti. Öyle ki, bütün genişliğine rağmen, yeryüzü onlara dar gelmişti. Canları sıkıldıkça sıkılmıştı. Ve Allah'tan başka sığınılacak kimse olmadığını anladılar. Sonra Allah, tevbeye yöneldikleri için, tevbelerini kabul etti. Kuşkusuz Allah, Tevbeleri Kabul Eden'dir, Rahmeti Kesintisiz'dir.", transliteration: "Ve ales selasetillezine hullifu, hatta iza dakat aleyhimul ardu bima rehubet ve dakat aleyhim enfusuhum ve zannu en la melcee minallahi illa ileyh, summe tabe aleyhim li yetubu, innallahe huvet tevvabur rahim."},
  {id: 1354, sure_no: 119, ayet_no: 119, sure_adi: "Tevbe", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَكُونُوا مَعَ الصَّادِق۪ينَ", translation: "Ey İman Edenler! Allah'a karşı takva sahibi olun ve sadıklarla birlikte olun.", transliteration: "Ya eyyuhellezine amenuttekullahe ve kunu meas sadikin ."},
  {id: 1355, sure_no: 120, ayet_no: 120, sure_adi: "Tevbe", text: "مَا كَانَ لِاَهْلِ الْمَد۪ينَةِ وَمَنْ حَوْلَهُمْ مِنَ الْاَعْرَابِ اَنْ يَتَخَلَّفُوا عَنْ رَسُولِ اللّٰهِ وَلَا يَرْغَبُوا بِاَنْفُسِهِمْ عَنْ نَفْسِه۪ۜ ذٰلِكَ بِاَنَّهُمْ لَا يُص۪يبُهُمْ ظَمَاٌ وَلَا نَصَبٌ وَلَا مَخْمَصَةٌ ف۪ي سَب۪يلِ اللّٰهِ وَلَا يَطَؤُ۫نَ مَوْطِئاً يَغ۪يظُ الْكُفَّارَ وَلَا يَنَالُونَ مِنْ عَدُوٍّ نَيْلاً اِلَّا كُتِبَ لَهُمْ بِه۪ عَمَلٌ صَالِحٌۜ اِنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُحْسِن۪ينَۙ", translation: "Ne Medine halkının ne de etrafındaki Bedevi Arapların, Allah'ın Resul'ünden geri kalmaları ve onun canından önce kendi canlarının kaygısına düşmeleri olacak şeydir. Çünkü Allah yolunda katlanacakları susuzluk, yorgunluk, açlık ve Kafirleri kızdıracak bir yeri zapt etmeleri ve düşmana karşı elde ettikleri başarı, kendilerine salih bir amel olarak yazılacaktır. Zira Allah, muhsin olanların kazanımlarını yok etmez.", transliteration: "Ma kane li ehlil medineti ve men havlehum minel a'rabi en yetehallefu an resulillahi ve la yergabu bi enfusihim an nefsih, zalike bi ennehum la yusibuhum zameun ve la nasabun ve la mahmesatun fi sebilillahi ve la yetaune mevtıan yagizul kuffare ve la yenalune min aduvvin neylen illa kutibe lehum bihi amelun salih, innallahe la yudiu ecrel muhsinin."},
  {id: 1356, sure_no: 121, ayet_no: 121, sure_adi: "Tevbe", text: "وَلَا يُنْفِقُونَ نَفَقَةً صَغ۪يرَةً وَلَا كَب۪يرَةً وَلَا يَقْطَعُونَ وَادِياً اِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا كَانُوا يَعْمَلُونَ", translation: "Büyük veya küçük, yaptıkları her yardım ve güçlük içinde aştıkları her vadi, kendilerine yazılacaktır. Allah, yaptıklarından daha iyisiyle kendilerini ödüllendirecektir.", transliteration: "Ve la yunfikune nefakaten sagireten ve la kebireten ve la yaktaune vadien illa kutibe lehum li yecziyehumullahu ahsene ma kanu ya'melun."},
  {id: 1357, sure_no: 122, ayet_no: 122, sure_adi: "Tevbe", text: "وَمَا كَانَ الْمُؤْمِنُونَ لِيَنْفِرُوا كَٓافَّةًۜ فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طَٓائِفَةٌ لِيَتَفَقَّهُوا فِي الدّ۪ينِ وَلِيُنْذِرُوا قَوْمَهُمْ اِذَا رَجَعُٓوا اِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ۟", translation: "Mü'minlerin toptan seferber olmaları uygun değildir. Dinde bilgi edinmek için her grubun içinden birtakım kimseler gitmeli ve döndüklerinde halklarını bilgilendirmelidirler. Umulur ki onlar sakınırlar.", transliteration: "Ve ma kanel mu'minune li yenfiru kaffeh, fe lev la nefere min kulli firkatin minhum taifetun li yetefekkahu fid dini ve li yunziru kavmehum iza receu ileyhim leallehum yahzerun."},
  {id: 1358, sure_no: 123, ayet_no: 123, sure_adi: "Tevbe", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا قَاتِلُوا الَّذ۪ينَ يَلُونَكُمْ مِنَ الْكُفَّارِ وَلْيَجِدُوا ف۪يكُمْ غِلْظَةًۜ وَاعْلَمُٓوا اَنَّ اللّٰهَ مَعَ الْمُتَّق۪ينَ", translation: "Ey İman Edenler! Size yakın olan Kafirlerle savaşın. Ve onlar, sizde kararlılık görsünler. Bilin ki Allah, takva sahibi olanlarla beraberdir.", transliteration: "Ya eyyuhellezine amenu katilullezine yelunekum minel kuffari vel yecidu fikum gilzah, va'lemu ennallahe meal muttekin."},
  {id: 1359, sure_no: 124, ayet_no: 124, sure_adi: "Tevbe", text: "وَاِذَا مَٓا اُنْزِلَتْ سُورَةٌ فَمِنْهُمْ مَنْ يَقُولُ اَيُّـكُمْ زَادَتْهُ هٰذِه۪ٓ ا۪يمَاناًۚ فَاَمَّا الَّذ۪ينَ اٰمَنُوا فَزَادَتْهُمْ ا۪يمَاناً وَهُمْ يَسْتَبْشِرُونَ", translation: "Ne zaman bir sure indirilse, onlardan bazıları: \"Bu hanginizin imanını arttırdı?\" derler. Bu, İman Edenler'in imanını arttırdı. Onlar, müjdelenmelerine sevinirler.", transliteration: "Ve iza ma unzilet suretun fe minhum men yekulu eyyukum zadethu hazihi imana, fe emmellezine amenu fe zadethum imanen ve hum yestebşirun."},
  {id: 1360, sure_no: 125, ayet_no: 125, sure_adi: "Tevbe", text: "وَاَمَّا الَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ فَزَادَتْهُمْ رِجْساً اِلٰى رِجْسِهِمْ وَمَاتُوا وَهُمْ كَافِرُونَ", translation: "Kalplerinde hastalık olanlara gelince, bu onların kötülüklerine kötülük katmıştır. Ve onlar Kafir olarak ölürler.", transliteration: "Ve emmellezine fi kulubihim maradun fe zadethum ricsen ila ricsihim ve matu ve hum kafirun."},
  {id: 1361, sure_no: 126, ayet_no: 126, sure_adi: "Tevbe", text: "اَوَلَا يَرَوْنَ اَنَّهُمْ يُفْتَنُونَ ف۪ي كُلِّ عَامٍ مَرَّةً اَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمْ يَذَّكَّرُونَ", translation: "Kendilerinin yılda bir iki defa sınandıklarını görmüyorlar mı? Yine de tevbe edip öğüt almıyorlar!", transliteration: "E ve la yerevne ennehum yuftenune fi kulli amin merreten ev merreteyni summe la yetubune ve la hum yezzekkerun."},
  {id: 1362, sure_no: 127, ayet_no: 127, sure_adi: "Tevbe", text: "وَاِذَا مَٓا اُنْزِلَتْ سُورَةٌ نَظَرَ بَعْضُهُمْ اِلٰى بَعْضٍۜ هَلْ يَرٰيكُمْ مِنْ اَحَدٍ ثُمَّ انْصَرَفُواۜ صَرَفَ اللّٰهُ قُلُوبَهُمْ بِاَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ", translation: "Bir sure indirildiği zaman: \"Sizi gören var mı?\" diye birbirlerine bakar, sonra da dönüp giderler. Allah, onların kalplerini çevirmiştir. Çünkü onlar, düşünmeyen bir halktır.", transliteration: "Ve iza ma unzilet suretun nazara ba'duhum ila ba'd, hel yerakum min ehadin summensarafu, sarafallahu kulubehum bi ennehum kavmun la yefkahun."},
  {id: 1363, sure_no: 128, ayet_no: 128, sure_adi: "Tevbe", text: "لَقَدْ جَٓاءَكُمْ رَسُولٌ مِنْ اَنْفُسِكُمْ عَز۪يزٌۘ عَلَيْهِ مَا عَنِتُّمْ حَر۪يصٌ عَلَيْكُمْ بِالْمُؤْمِن۪ينَ رَؤُ۫فٌ رَح۪يمٌ", translation: "Ant olsun ki, size kendinizden bir Resul geldi. Sıkıntıya düşmeniz ona ağır gelir; o, size düşkün, Mü'minlere şefkatli ve merhametlidir.", transliteration: "Lekad caekum resulun min enfusikum aziz, aleyhi ma anittum harisun aleykum bil mu'minine raufun rahim."},
  {id: 1364, sure_no: 129, ayet_no: 129, sure_adi: "Tevbe", text: "فَاِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللّٰهُۘ  لَٓا اِلٰهَ اِلَّا هُوَۜ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظ۪يمِ", translation: "Eğer yüz çevirirlerse de ki: \"Allah bana yeter. O'ndan başka ilah yoktur. Ben O'na tevekkül ettim. O, Büyük Arş'ın Rabb'idir.\"", transliteration: "Fe in tevellev fe kul hasbiyallah, la ilahe illa huv, aleyhi tevekkeltu ve huve rabbul arşil azim."},
  {id: 1365, sure_no: 1, ayet_no: 1, sure_adi: "Yunus", text: "الٓـرٰ۠ تِلْكَ اٰيَاتُ الْكِتَابِ الْحَك۪يمِ", translation: "Elif, Lam, Ra. İşte bunlar Hakim Kitap'ın ayetleridir.", transliteration: "Elif lam ra, tilke ayatul kitabil hakim."},
  {id: 1366, sure_no: 2, ayet_no: 2, sure_adi: "Yunus", text: "اَكَانَ لِلنَّاسِ عَجَباً اَنْ اَوْحَيْنَٓا اِلٰى رَجُلٍ مِنْهُمْ اَنْ اَنْذِرِ النَّاسَ وَبَشِّرِ الَّذ۪ينَ اٰمَنُٓوا اَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْۜ قَالَ الْكَافِرُونَ اِنَّ هٰذَا لَسَاحِرٌ مُب۪ينٌ", translation: "İçlerinden birine: \"İnsanları uyar ve İman Edenlere Rabb'leri katında gerçek üstünlük makamı olduğunu haber ver!\" diye vahyetmemiz, şaşılacak bir şey mi ki Kafirler: \"Bu apaçık bir büyücüdür.\" dediler?", transliteration: "E kane linnasi aceben en evhayna ila reculin minhum en enzirin nase ve beşşirillezine amenu enne lehum kademe sıdkın inde rabbihim, kalel kafirune inne haza le sahırun mubin."},
  {id: 1367, sure_no: 3, ayet_no: 3, sure_adi: "Yunus", text: "اِنَّ رَبَّكُمُ اللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُدَبِّرُ الْاَمْرَۜ مَا مِنْ شَف۪يعٍ اِلَّا مِنْ بَعْدِ اِذْنِه۪ۜ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ فَاعْبُدُوهُۜ اَفَلَا تَذَكَّرُونَ", translation: "Rabb'iniz, o Allah'tır ki, gökleri ve yeri altı günde yarattı; sonra arşa isteva etti. Bütün işlere yasalarını koydu. O'nun izni olmadan hiçbir şefaatçi olamaz. İşte bu Rabb'iniz olan Allah'tır. Öyleyse O'na kulluk edin. Hala düşünüp öğüt almaz mısınız?", transliteration: "İnne rabbekumullahullezi halakas semavati vel arda fi sitteti eyyamin summesteva alel arşi yudebbirul emr, ma min şefiin illa min ba'di iznih, zalikumullahu rabbukum fa'buduh, e fe la tezekkerun."},
  {id: 1368, sure_no: 4, ayet_no: 4, sure_adi: "Yunus", text: "اِلَيْهِ مَرْجِعُكُمْ جَم۪يعاًۜ وَعْدَ اللّٰهِ حَقاًّۜ اِنَّهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ لِيَجْزِيَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ بِالْقِسْطِۜ وَالَّذ۪ينَ كَفَرُوا لَهُمْ شَرَابٌ مِنْ حَم۪يمٍ وَعَذَابٌ اَل۪يمٌ بِمَا كَانُوا يَكْفُرُونَ", translation: "Hepinizin dönüşü O'nadır. Allah'ın sözü haktır. O, mahlukatı önce yaratır, sonra iman edip salihatı yapanlara adaletle karşılık vermek için tekrar yaratır. Kafirler ise küfürlerinden dolayı, kaynar sudan içecek ve onlara can yakıcı bir azap vardır.", transliteration: "İleyhi merciukum cemia, va'dallahi hakka, innehu yebdeul halka summe yuiduhu li yecziyellezine amenu ve amilus salihati bil kıst, vellezine keferu lehum şerabun min hamimin ve azabun elimun bima kanu yekfurun."},
  {id: 1369, sure_no: 5, ayet_no: 5, sure_adi: "Yunus", text: "هُوَ الَّذ۪ي جَعَلَ الشَّمْسَ ضِيَٓاءً وَالْقَمَرَ نُوراً وَقَدَّرَهُ مَنَازِلَ لِتَعْلَمُوا عَدَدَ السِّن۪ينَ وَالْحِسَابَۜ مَا خَلَقَ اللّٰهُ ذٰلِكَ اِلَّا بِالْحَقِّۜ يُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْلَمُونَ", translation: "Güneş'i bir ziya, Ay'ı bir nur yapan ve yılların sayısını ve hesabını bilmeniz için menziller belirleyen O'dur. Allah, bunları ancak hakk ile yaratmıştır. O, bilen bir halk için ayetlerini ayrıntılı olarak açıklamaktadır.", transliteration: "Huvellezi cealeş şemse dıyaen vel kamere nuren ve kadderehu menazile li ta'lemu adedes sinine vel hisab, ma halakallahu zalike illa bil hakk, yufassılul ayati li kavmin ya'lemun."},
  {id: 1370, sure_no: 6, ayet_no: 6, sure_adi: "Yunus", text: "اِنَّ فِي اخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللّٰهُ فِي السَّمٰوَاتِ وَالْاَرْضِ لَاٰيَاتٍ لِقَوْمٍ يَتَّقُونَ", translation: "Gece ile gündüzün birbiri ardınca gelmesinde, Allah'ın yarattığı göklerde ve yerde, takva sahibi bir halk için nice ayetler vardır.", transliteration: "İnne fihtilafil leyli ven nehari ve ma halakallahu fis semavati vel ardı le ayatin li kavmin yettekun."},
  {id: 1371, sure_no: 7, ayet_no: 7, sure_adi: "Yunus", text: "اِنَّ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا وَرَضُوا بِالْحَيٰوةِ الدُّنْيَا وَاطْمَاَنُّوا بِهَا وَالَّذ۪ينَ هُمْ عَنْ اٰيَاتِنَا غَافِلُونَۙ", translation: "Bizimle karşılaşacaklarını ummayanlar ve dünya hayatından hoşnut olup onunla tatmin olanlar ve ayetlerimizi görmezden gelenler…", transliteration: "İnnellezine la yercune likaena ve radu bil hayatid dunya vatme'ennu biha vellezine hum an ayatina gafilun."},
  {id: 1372, sure_no: 8, ayet_no: 8, sure_adi: "Yunus", text: "اُو۬لٰٓئِكَ مَأْوٰيهُمُ النَّارُ بِمَا كَانُوا يَكْسِبُونَ", translation: "İşte onların, yaptıklarına karşılık olarak, varacakları yer ateştir.", transliteration: "Ulaike me'vahumun naru bima kanu yeksibun."},
  {id: 1373, sure_no: 9, ayet_no: 9, sure_adi: "Yunus", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ يَهْد۪يهِمْ رَبُّهُمْ بِا۪يمَانِهِمْۚ تَجْر۪ي مِنْ تَحْتِهِمُ الْاَنْهَارُ ف۪ي جَنَّاتِ النَّع۪يمِ", translation: "İman edip, salihatı yapanlara gelince, imanlarından dolayı Rabb'leri onları doğru yola iletir ve içlerinden ırmaklar akan Na'im Cennetlerine koyar.", transliteration: "İnnellezine amenu ve amilus salihati yehdihim rabbuhum bi imanihim, tecri min tahtihimul enharu fi cennatin naim."},
  {id: 1374, sure_no: 10, ayet_no: 10, sure_adi: "Yunus", text: "دَعْوٰيهُمْ ف۪يهَا سُبْحَانَكَ اللّٰهُمَّ وَتَحِيَّتُهُمْ ف۪يهَا سَلَامٌۚ وَاٰخِرُ دَعْوٰيهُمْ اَنِ الْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَ۟", translation: "Onların, oradaki duası: \"Ey Allah'ım! Seni tesbih ederiz.\" Onların temennileri, \"Selam\"dır. Dualarının sonu ise: \"Alemlerin Rabb'i olan Allah'a hamdolsun.\" dur.", transliteration: "Da'vahum fiha subhanekellahumme ve tehiyyetuhum fiha selam, ve ahıru da'vahum enil hamdulillahi rabbil alemin."},
  {id: 1375, sure_no: 11, ayet_no: 11, sure_adi: "Yunus", text: "وَلَوْ يُعَجِّلُ اللّٰهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ اِلَيْهِمْ اَجَلُهُمْۜ فَنَذَرُ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ", translation: "İnsanların, hayrı acele istedikleri gibi, Allah da şerri onlara acele verseydi, muhakkak süreleri bitirilirdi. Ama Biz, Bizimle karşılaşmayı ummayanları, kendi hallerine bırakırız. Azgınlıkları içinde bocalar dururlar.", transliteration: "Ve lev yuaccilullahu lin nasiş şerresti'calehum bil hayri le kudiye ileyhim eceluhum, fe nezerullezine la yercune likaena fi tugyanihim ya'mehun."},
  {id: 1376, sure_no: 12, ayet_no: 12, sure_adi: "Yunus", text: "وَاِذَا مَسَّ الْاِنْسَانَ الضُّرُّ دَعَانَا لِجَنْبِه۪ٓ اَوْ قَاعِداً اَوْ قَٓائِماًۚ فَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهُ مَرَّ كَاَنْ لَمْ يَدْعُنَٓا اِلٰى ضُرٍّ مَسَّهُۜ كَذٰلِكَ زُيِّنَ لِلْمُسْرِف۪ينَ مَا كَانُوا يَعْمَلُونَ", translation: "İnsana bir sıkıntı dokununca, yatarken, otururken veya ayaktayken bize dua eder; fakat Biz onun sıkıntısını giderince de karşılaştığı sıkıntıdan ötürü sanki Bize hiç dua etmemiş gibi davranmaya devam eder. İşte Müsriflere, yaptıkları şey böyle cazip gösterilmiştir.", transliteration: "Ve iza messel insaned durru deana li cenbihi ev kaiden ev kaima, fe lemma keşefna anhu durrehu merre ke'en lem yed'una ila durrin messeh, kezalike zuyyine lil musrifine ma kanu ya'melun."},
  {id: 1377, sure_no: 13, ayet_no: 13, sure_adi: "Yunus", text: "وَلَقَدْ اَهْلَكْنَا الْقُرُونَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُواۙ وَجَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَمَا كَانُوا لِيُؤْمِنُواۜ كَذٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِم۪ينَ", translation: "Ant olsun Biz, sizden önce nice nesilleri, Resulleri kendilerine kanıt içeren; açıklayıcı, açığa çıkarıcı bilgi getirdiği halde iman etmeyip, haksızlık yaptıkları için yok ettik. İşte suçlu halkları böyle cezalandırırız.", transliteration: "Ve lekad ehleknel kurune min kablikum lemma zalemu ve caethum rusuluhum bil beyyinati ve ma kanu li yu'minu, kezalike neczil kavmel mucrimin."},
  {id: 1378, sure_no: 14, ayet_no: 14, sure_adi: "Yunus", text: "ثُمَّ جَعَلْنَاكُمْ خَلَٓائِفَ فِي الْاَرْضِ مِنْ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُونَ", translation: "Sonra, onların arkasından, yeryüzünde sizi halifeler yaptık ki bakalım nasıl davranacaksınız.", transliteration: "Summe cealnakum halaife fil ardı min ba'dihim li nanzure keyfe ta'melun."},
  {id: 1379, sure_no: 15, ayet_no: 15, sure_adi: "Yunus", text: "وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍۙ قَالَ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا ائْتِ بِقُرْاٰنٍ غَيْرِ هٰذَٓا اَوْ بَدِّلْهُۜ قُلْ مَا يَكُونُ ل۪ٓي اَنْ اُبَدِّلَهُ مِنْ تِلْقَٓائِ۬ نَفْس۪يۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوحٰٓى اِلَيَّۚ اِنّ۪ٓي اَخَافُ اِنْ عَصَيْتُ رَبّ۪ي عَذَابَ يَوْمٍ عَظ۪يمٍ", translation: "Bizimle karşılaşmayı ummayanlara, ayetlerimiz açık açık okunduğu zaman, onlar: \"Ya bundan başka bir Kur'an getir veya onu değiştir.\" dediler. De ki: \"Onu kendiliğimden değiştirmem olacak şey değildir. Ben, ancak bana vahyolunana uyarım. Eğer Rabb'ime karşı gelirsem, büyük günün azabından korkarım.\"", transliteration: "Ve iza tutla aleyhim ayatuna beyyinatin kalellezine la yercune likaena'ti bi kur'anin gayri haza ev beddilh, kul ma yekunu li en ubeddilehu min tilkai nefsi, in ettebiu illa ma yuha ileyy, inni ehafu in asaytu rabbi azabe yevmin azim."},
  {id: 1380, sure_no: 16, ayet_no: 16, sure_adi: "Yunus", text: "قُلْ لَوْ شَٓاءَ اللّٰهُ مَا تَلَوْتُهُ عَلَيْكُمْ وَلَٓا اَدْرٰيكُمْ بِه۪ۘ فَقَدْ لَبِثْتُ ف۪يكُمْ عُمُراً مِنْ قَبْلِه۪ۜ اَفَلَا تَعْقِلُونَ", translation: "De ki: \"Eğer Allah dileseydi onu size okumazdım. Onu size hiç bildirmezdim. İçinizde bir ömür kaldım. Hala aklınızı kullanmayacak mısınız?\"", transliteration: "Kul lev saallahu ma televtuhu aleykum ve la edrakum bihi, fe kad lebistu fikum umuren min kablih, e fe la ta'kilun."},
  {id: 1381, sure_no: 17, ayet_no: 17, sure_adi: "Yunus", text: "فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ كَذَّبَ بِاٰيَاتِه۪ۜ اِنَّهُ لَا يُفْلِحُ الْمُجْرِمُونَ", translation: "Uydurduğu yalanı Allah'a dayandıran veya O'nun ayetlerini yalan sayandan daha zalim kim olabilir? O, mücrimleri kurtuluşa erdirmez.", transliteration: "Fe men azlemu mimmeniftera alallahi keziben ev kezzebe bi ayatih, innehu la yuflihul mucrimun."},
  {id: 1382, sure_no: 18, ayet_no: 18, sure_adi: "Yunus", text: "وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ وَيَقُولُونَ هٰٓؤُ۬لَٓاءِ شُفَعَٓاؤُ۬نَا عِنْدَ اللّٰهِۜ قُلْ اَتُنَبِّؤُ۫نَ اللّٰهَ بِمَا لَا يَعْلَمُ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يُشْرِكُونَ", translation: "Onlar, Allah'ın yanı sıra bir de kendilerine ne bir zarar ne bir yarar sağlamayan şeylere kulluk ediyorlar ve \"Bunlar, Allah'ın katında bizim şefaatçilerimizdir.\" diyorlar. De ki: \"Allah'a, göklerde ve yerde kendisinin bilmediği bir şeyi mi haber veriyorsunuz?\" Allah, onların ortak koştuklarından Münezzeh'tir ve Çok Yüce'dir.", transliteration: "Ve ya'budune min dunillahi ma la yedurruhum ve la yenfeuhum ve yekulune haulai şufeauna indallah, kul e tunebbiunallahe bima la ya'lemu fis semavati ve la fil ard, subhanehu ve teala amma yuşrikun."},
  {id: 1383, sure_no: 19, ayet_no: 19, sure_adi: "Yunus", text: "وَمَا كَانَ النَّاسُ اِلَّٓا اُمَّةً وَاحِدَةً فَاخْتَلَفُواۜ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ ف۪يمَا ف۪يهِ يَخْتَلِفُونَ", translation: "İnsanlar, sadece tek bir ümmetti. Sonra anlaşmazlığa düştüler. Şayet Rabb'inin katında önceden verilmiş bir hüküm olmasaydı, anlaşmazlığa düştükleri konularda hemen karar verilirdi.", transliteration: "Ve ma kanen nasu illa ummeten vahideten fahtelefu, ve lev la kelimetun sebekat min rabbike le kudiye beynehum fima fihi yahtelifun."},
  {id: 1384, sure_no: 20, ayet_no: 20, sure_adi: "Yunus", text: "وَيَقُولُونَ لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۚ فَقُلْ اِنَّمَا الْغَيْبُ لِلّٰهِ فَانْتَظِرُواۚ اِنّ۪ي مَعَكُمْ مِنَ الْمُنْتَظِر۪ينَ۟", translation: "\"Ona Rabb'inden bir ayet indirilmeli değil mi!\" diyorlar. De ki: \"Gayb yalnızca Allah'a aittir. Artık bekleyin, ben de sizinle beraber bekleyenlerdenim.\"", transliteration: "Ve yekulune lev la unzile aleyhi ayetun min rabbih, fe kul innemel gaybu lillahi fenteziru, inni meakum minel muntazirin."},
  {id: 1385, sure_no: 21, ayet_no: 21, sure_adi: "Yunus", text: "وَاِذَٓا اَذَقْنَا النَّاسَ رَحْمَةً مِنْ بَعْدِ ضَرَّٓاءَ مَسَّتْهُمْ اِذَا لَهُمْ مَكْرٌ ف۪ٓي اٰيَاتِنَاۜ قُلِ اللّٰهُ اَسْرَعُ مَكْراًۜ اِنَّ رُسُلَنَا يَكْتُبُونَ مَا تَمْكُرُونَ", translation: "İnsanlara dokunan bir sıkıntıdan sonra, bir rahmet tattırdığımız zaman, ayetlerimize karşı hemen plan yapmaya kalkışırlar. De ki: \"Plan yapmada Allah daha seridir.\" Elçilerimiz, yaptığınız tüm planları kaydetmektedirler.", transliteration: "Ve iza ezaknen nase rahmeten min ba'di darrae messethum iza lehum mekrun fi ayatina, kulillahu esrau mekra, inne rusulena yektubune ma temkurun."},
  {id: 1386, sure_no: 22, ayet_no: 22, sure_adi: "Yunus", text: "هُوَ الَّذ۪ي يُسَيِّرُكُمْ فِي الْبَرِّ وَالْبَحْرِۜ حَتّٰٓى اِذَا كُنْتُمْ فِي الْفُلْكِۚ وَجَرَيْنَ بِهِمْ بِر۪يحٍ طَيِّبَةٍ وَفَرِحُوا بِهَا جَٓاءَتْهَا ر۪يحٌ عَاصِفٌ وَجَٓاءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَظَنُّٓوا اَنَّهُمْ اُح۪يطَ بِهِمْۙ دَعَوُا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَۚ لَئِنْ اَنْجَيْتَنَا مِنْ هٰذِه۪ لَنَكُونَنَّ مِنَ الشَّاكِر۪ينَ", translation: "Sizi karada ve denizde yürüten O'dur. Öyle ki siz gemideyken ve güzel bir rüzgarla akıp giderken, yolcuların da bununla sevindikleri bir sırada, birden şiddetli bir kasırga gelip çatar ve her yönden dalgaların onları sarıp kuşattığı anda, dini Allah'a has kılarak: \"Ant olsun, eğer bizi kurtarırsan mutlaka şükredenlerden olacağız.\" diye dua ederler.", transliteration: "Huvellezi yuseyyirukum fil berri vel bahr, hatta iza kuntum fil fulk, ve cereyne bihim bi rihin tayyibetin ve ferihu biha caetha rihun asifun ve caehumul mevcu min kulli mekanin ve zannu ennehum uhita bihim deavullahe muhlisine lehud din, le in enceytena min hazihi le nekunenne mineş şakirin."},
  {id: 1387, sure_no: 23, ayet_no: 23, sure_adi: "Yunus", text: "فَلَمَّٓا اَنْجٰيهُمْ اِذَا هُمْ يَبْغُونَ فِي الْاَرْضِ بِغَيْرِ الْحَقِّۜ  يَٓا اَيُّهَا النَّاسُ اِنَّمَا بَغْيُكُمْ عَلٰٓى اَنْفُسِكُمْۙ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Ama onları kurtarınca, yeryüzünde haksız yere taşkınlık yaparlar. Ey insanlar! Sizin azgınlığınız ancak kendiniz içindir. Dünya hayatı bir geçimliktir. Sonunda bize döneceksiniz. Yaptıklarınızı size haber veririz.", transliteration: "Fe lemma encahum iza hum yebgune fil ardı bi gayril hakk, ya eyyuhen nasu innema bagyukum ala enfusikum metaal hayatid dunya summe ileyna merciukum fe nunebbiukum bima kuntum ta'melun."},
  {id: 1388, sure_no: 24, ayet_no: 24, sure_adi: "Yunus", text: "اِنَّمَا مَثَلُ الْحَيٰوةِ الدُّنْيَا كَمَٓاءٍ اَنْزَلْنَاهُ مِنَ السَّمَٓاءِ فَاخْتَلَطَ بِه۪ نَبَاتُ الْاَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْاَنْعَامُۜ حَتّٰٓى اِذَٓا اَخَذَتِ الْاَرْضُ زُخْرُفَهَا وَازَّيَّـنَتْ وَظَنَّ اَهْلُهَٓا اَنَّهُمْ قَادِرُونَ عَلَيْهَٓاۙ اَتٰيهَٓا اَمْرُنَا لَيْلاً اَوْ نَهَاراً فَجَعَلْنَاهَا حَص۪يداً كَاَنْ لَمْ تَغْنَ بِالْاَمْسِۜ  كَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَتَفَكَّرُونَ", translation: "Dünya hayatı, gökten indirdiğimiz yağmurla hayat bulup yeşeren, insanların ve hayvanların yararlandıkları yeryüzü bitkileri gibidir. Öyle ki yeryüzü bütün süslerini ve güzelliğini kuşandığı ve sahipleri de onu elde edecek güce sahip olduklarını sandıkları bir sırada; geceleyin veya güpegündüz ona emrimiz geliverir de bunları sanki dün yerinde hiç yokmuş gibi kökünden yolunmuş bir hale getiririz. İşte düşünen bir halk için ayetlerimizi böyle detaylı olarak açıklıyoruz.", transliteration: "İnnema meselul hayatid dunya ke main enzelnahu mines semai fahteleta bihi nebatul ardı mimma ye'kulun nasu vel en'am, hatta iza ehazetil ardu zuhrufeha vezzeyyenet ve zanne ehluha ennehum kadirune aleyha etaha emruna leylen ev neharen fe cealnaha hasiden ke en lem tagne bil ems, kezalike nufassilul ayati li kavmin yetefekkerun."},
  {id: 1389, sure_no: 25, ayet_no: 25, sure_adi: "Yunus", text: "وَاللّٰهُ يَدْعُٓوا اِلٰى دَارِ السَّلَامِۜ وَيَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Allah, selam diyarına çağırır. Hak edeni dosdoğru yola iletir.", transliteration: "Vallahu yed'u ila daris selam, ve yehdi men yeşau ila sıratin mustekim."},
  {id: 1390, sure_no: 26, ayet_no: 26, sure_adi: "Yunus", text: "لِلَّذ۪ينَ اَحْسَنُوا الْحُسْنٰى وَزِيَادَةٌۜ وَلَا يَرْهَقُ وُجُوهَهُمْ قَتَرٌ وَلَا ذِلَّةٌۜ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "İyi ve güzel davrananlar için daha güzeli ve fazlası var. Onların, yüzleri ne kararır ne de hor görülmekten kızarır. Onlar, Cennet halkıdır. Orada sürekli kalıcıdırlar.", transliteration: "Lillezine ahsenul husna ve ziyadeh, ve la yerheku vucuhehum katerun ve la zilleh, ulaike ashabul cenneh, hum fiha halidun."},
  {id: 1391, sure_no: 27, ayet_no: 27, sure_adi: "Yunus", text: "وَالَّذ۪ينَ كَسَبُوا السَّيِّـَٔاتِ جَزَٓاءُ سَيِّئَةٍ بِمِثْلِهَاۙ وَتَرْهَقُهُمْ ذِلَّةٌۜ مَا لَهُمْ مِنَ اللّٰهِ مِنْ عَاصِمٍۚ  كَاَنَّـمَٓا اُغْشِيَتْ وُجُوهُهُمْ قِطَعاً مِنَ الَّيْلِ مُظْلِماًۜ اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "Kötülük yapanların cezaları, yaptıkları kötülük kadardır. Onları her yönden zillet kaplayacaktır. Onları Allah'ın cezasından kurtaracak hiç kimse yoktur. Yüzleri geceden daha kara bir parçayla örtülmüş gibidir. İşte onlar ateş halkıdır. Orada sürekli kalacaklardır.", transliteration: "Vellezine kesebus seyyiati cezau seyyietin bi misliha ve terhekuhum zilleh, ma lehum minallahi min asim, ke ennema ugsiyet vucuhuhum kita'an minel leyli muzlima, ulaike ashabun nar, hum fiha halidun."},
  {id: 1392, sure_no: 28, ayet_no: 28, sure_adi: "Yunus", text: "وَيَوْمَ نَحْشُرُهُمْ جَم۪يعاً ثُمَّ نَقُولُ لِلَّذ۪ينَ اَشْرَكُوا مَكَانَكُمْ اَنْتُمْ وَشُرَكَٓاؤُ۬كُمْۚ فَزَيَّلْنَا بَيْنَهُمْ وَقَالَ شُرَكَٓاؤُ۬هُمْ مَا كُنْتُمْ اِيَّانَا تَعْبُدُونَ", translation: "O Gün, onların hepsini toplarız. Sonra şirk koşanlara, \"Siz ve şirk koştuklarınız, olduğunuz yerde durun.\" diyeceğiz. Artık onları birbirinden ayıracağız. Şirk koşulanlar, şöyle diyecekler: \"Siz, bize kulluk etmiyordunuz.\"", transliteration: "Ve yevme nahsuruhum cemian summe nekulu lillezine eşreku mekanekum entum ve şurekaukum, fe zeyyelna beynehum, ve kale şurekauhum ma kuntum iyyana ta'budun."},
  {id: 1393, sure_no: 29, ayet_no: 29, sure_adi: "Yunus", text: "فَكَفٰى بِاللّٰهِ شَه۪يداً بَيْنَنَا وَبَيْنَكُمْ اِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغَافِل۪ينَ", translation: "\"Sizinle bizim aramızda tanık olarak Allah yeter. Doğrusu, sizin bize kulluğunuzdan haberimiz yoktu.\"", transliteration: "Fe kefa billahi şehiden beynena ve beynekum in kunna an ibadetikum le gafilin."},
  {id: 1394, sure_no: 30, ayet_no: 30, sure_adi: "Yunus", text: "هُنَالِكَ تَبْلُوا كُلُّ نَفْسٍ مَٓا اَسْلَفَتْ وَرُدُّٓوا اِلَى اللّٰهِ مَوْلٰيهُمُ الْحَقِّ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ۟", translation: "İşte orada, herkes ne yapmışsa onu bulur. Gerçek mevlaları olan Allah'a döndürülmüş olarak. Uydurdukları şeyler ise kendilerinden uzaklaşıp kaybolurlar.", transliteration: "Hunalike teblu kullu nefsin ma eslefet ve ruddu ilallahi mevlahumul hakkı ve dalle anhum ma kanu yefterun."},
  {id: 1395, sure_no: 31, ayet_no: 31, sure_adi: "Yunus", text: "قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِ اَمَّنْ يَمْلِكُ السَّمْعَ وَالْاَبْصَارَ وَمَنْ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُدَبِّرُ الْاَمْرَۜ فَسَيَقُولُونَ اللّٰهُۚ فَقُلْ اَفَلَا تَتَّقُونَ", translation: "De ki: \"Sizleri gökten ve yerden rızıklandıran kimdir? İşitme ve görme gücünü veren kimdir? Ölüden diriyi, diriden ölüyü çıkaran kimdir? Bütün işleri düzenleyen kimdir?\" Diyecekler ki: \"Allah'tır.\" O halde, \"O'na karşı takvalı olmayacak mısınız?\" de.", transliteration: "Kul men yerzukukum mines semai vel ardı emmen yemlikus sem'a vel ebsare ve men yuhricul hayye minel meyyiti ve yuhricul meyyite minel hayyi ve men yudebbirul emr, fe se yekulunallah, fe kul e fe la tettekun."},
  {id: 1396, sure_no: 32, ayet_no: 32, sure_adi: "Yunus", text: "فَذٰلِكُمُ اللّٰهُ رَبُّكُمُ الْحَقُّۚ فَمَاذَا بَعْدَ الْحَقِّ اِلَّا الضَّلَالُۚ فَاَنّٰى تُصْرَفُونَ", translation: "İşte bu gerçek Rabb'iniz Allah'tır. Hakikatten sonra sapkınlıktan başka ne vardır? Öyleyse nasıl çevriliyorsunuz?", transliteration: "Fe zalikumullahu rabbukumul hakk, fe maza ba'del hakkı illed dalal, fe enna tusrafun."},
  {id: 1397, sure_no: 33, ayet_no: 33, sure_adi: "Yunus", text: "كَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذ۪ينَ فَسَقُٓوا اَنَّهُمْ لَا يُؤْمِنُونَ", translation: "Böylece, Rabb'inin fasıklar hakkındaki kelimesi gerçekleşmiş oldu. Onlar iman etmezler.", transliteration: "Kezalike hakkat kelimetu rabbike alellezine feseku ennehum la yu'minun."},
  {id: 1398, sure_no: 34, ayet_no: 34, sure_adi: "Yunus", text: "قُلْ هَلْ مِنْ شُرَكَٓائِكُمْ مَنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُۜ قُلِ اللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ فَاَنّٰى تُؤْفَكُونَ", translation: "De ki: \"Ortak koştuklarınızdan ilk kez yaratıp sonra tekrar diriltecek var mıdır?\" De ki: \"Allah, ilk olarak yaratır, sonra diriltir. Nasıl da çevriliyorsunuz?\"", transliteration: "Kul hel min şurekaikum men yebdeul halka summe yu'iduh, kulillahu yebdeul halka summe yu'iduhu fe enna tu'fekun."},
  {id: 1399, sure_no: 35, ayet_no: 35, sure_adi: "Yunus", text: "قُلْ هَلْ مِنْ شُرَكَٓائِكُمْ مَنْ يَهْد۪ٓي اِلَى الْحَقِّۜ قُلِ اللّٰهُ يَهْد۪ي لِلْحَقِّۜ اَفَمَنْ يَهْد۪ٓي اِلَى الْحَقِّ اَحَقُّ اَنْ يُتَّبَعَ اَمَّنْ لَا يَهِدّ۪ٓي اِلَّٓا اَنْ يُهْدٰىۚ فَمَا لَكُمْ۠    كَيْفَ تَحْكُمُونَ", translation: "De ki: \"Ortak koştuklarınızdan, gerçeğe iletebilecek kimse var mı?\" De ki: \"Allah gerçeğe iletir. Gerçeğe ileten mi, yoksa kendisi iletilmedikçe doğru yolu bulamayan mı uyulmaya daha layıktır? Ne oluyor size, nasıl karar veriyorsunuz?\"", transliteration: "Kul hel min şurekaikum men yehdi ilel hakk, kulillahu yehdi lil hakk, e fe men yehdi ilel hakkı ehakku en yuttebea em men la yehiddi illa en yuhda, fe ma lekum, keyfe tahkumun."},
  {id: 1400, sure_no: 36, ayet_no: 36, sure_adi: "Yunus", text: "وَمَا يَتَّبِعُ اَكْثَرُهُمْ اِلَّا ظَناًّۜ اِنَّ الظَّنَّ لَا يُغْن۪ي مِنَ الْحَقِّ شَيْـٔاًۜ اِنَّ اللّٰهَ عَل۪يمٌ بِمَا يَفْعَلُونَ", translation: "Onların çoğu, ancak zanna uyarlar. Kuşkusuz zan hakikatin yerini tutamaz. Allah, onların ne yaptıklarını çok iyi bilendir.", transliteration: "Ve ma yettebiu ekseruhum illa zanna, innez zanne la yugni minel hakkı şey'a, innallahe alimun bima yef'alun."},
  {id: 1401, sure_no: 37, ayet_no: 37, sure_adi: "Yunus", text: "وَمَا كَانَ هٰذَا الْقُرْاٰنُ اَنْ يُفْتَرٰى مِنْ دُونِ اللّٰهِ وَلٰكِنْ تَصْد۪يقَ الَّذ۪ي بَيْنَ يَدَيْهِ وَتَفْص۪يلَ الْكِتَابِ لَا رَيْبَ ف۪يهِ مِنْ رَبِّ الْعَالَم۪ينَ۠", translation: "Bu Kur'an, Allah'tandır; başkası tarafından uydurulmuş değildir. Aynı zamanda kendinden önceki Kitapları tasdik eder ve Kitap'ı ayrıntılı olarak açıklar. Alemlerin Rabb'inden olduğu mutlak bir gerçektir.", transliteration: "Ve ma kane hazel kur'anu en yuftera min dunillahi ve lakin tasdikallezi beyne yedeyhi ve tafsilel kitabi la reybe fihi min rabbil alemin."},
  {id: 1402, sure_no: 38, ayet_no: 38, sure_adi: "Yunus", text: "اَمْ يَقُولُونَ افْتَرٰيهُۜ قُلْ فَأْتُوا بِسُورَةٍ مِثْلِه۪ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللّٰهِ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Yoksa \"Onu uydurdu.\" mu diyorlar? De ki: \"Allah'tan başka kim varsa çağırın da onun benzeri bir sure meydana getirin; eğer doğru söyleyenlerdenseniz.\"", transliteration: "Em yekulunefterah, kul fe'tu bi suretin mislihi ved'u menisteta'tum min dunillahi in kuntum sadikin."},
  {id: 1403, sure_no: 39, ayet_no: 39, sure_adi: "Yunus", text: "بَلْ كَذَّبُوا بِمَا لَمْ يُح۪يطُوا بِعِلْمِه۪ وَلَمَّا يَأْتِهِمْ تَأْو۪يلُهُۜ كَذٰلِكَ كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِم۪ينَ", translation: "Hayır! Onlar, bilgisini kavrayamadıkları ve kendilerine asıl anlamı açıklanmamış bir şeyi yalanladılar. Onlardan öncekiler de böyle yalanlamışlardı. Zalimlerin sonunun nasıl olduğuna bak!", transliteration: "Bel kezzebu bima lem yuhitu bi ilmihi ve lemma ye'tihim te'viluh, kezalike kezzebellezine min kablihim fanzur keyfe kane akibetuz zalimin."},
  {id: 1404, sure_no: 40, ayet_no: 40, sure_adi: "Yunus", text: "وَمِنْهُمْ مَنْ يُؤْمِنُ بِه۪ وَمِنْهُمْ مَنْ لَا يُؤْمِنُ بِه۪ۜ وَرَبُّكَ اَعْلَمُ بِالْمُفْسِد۪ينَ۟", translation: "Onlardan kimi ona iman eder, kimi de ona iman etmez. Rabb'in bozgunculuk edenleri en iyi bilendir.", transliteration: "Ve minhum men yu'minu bihi ve minhum men la yu'minu bih, ve rabbuke a'lemu bil mufsidin."},
  {id: 1405, sure_no: 41, ayet_no: 41, sure_adi: "Yunus", text: "وَاِنْ كَذَّبُوكَ فَقُلْ ل۪ي عَمَل۪ي وَلَكُمْ عَمَلُكُمْۚ اَنْتُمْ بَر۪ٓيؤُ۫نَ مِمَّٓا اَعْمَلُ وَاَنَا۬ بَر۪ٓيءٌ مِمَّا تَعْمَلُونَ", translation: "Eğer seni yalanlarlarsa, de ki: \"Benim yaptıklarım bana, sizin yaptıklarınız da sizedir. Siz benim yaptıklarımdan sorumlu değilsiniz ben de sizin yaptıklarınızdan sorumlu değilim.\"", transliteration: "Ve in kezzebuke fe kul li ameli ve lekum amelukum, entum beriune mimma a'melu ve ene beriun mimma ta'melun."},
  {id: 1406, sure_no: 42, ayet_no: 42, sure_adi: "Yunus", text: "وَمِنْهُمْ مَنْ يَسْتَمِعُونَ اِلَيْكَۜ اَفَاَنْتَ تُسْمِـعُ الصُّمَّ وَلَوْ كَانُوا لَا يَعْقِلُونَ", translation: "Onlardan seni dinleyenler de var. Ancak sağırlara duyurabilir misin? Üstelik akıllarını da kullanmıyorlarsa!", transliteration: "Ve minhum men yestemiune ileyk, e fe ente tusmius summe ve lev kanu la ya'kilun."},
  {id: 1407, sure_no: 43, ayet_no: 43, sure_adi: "Yunus", text: "وَمِنْهُمْ مَنْ يَنْظُرُ اِلَيْكَۜ اَفَاَنْتَ تَهْدِي الْعُمْيَ وَلَوْ كَانُوا لَا يُبْصِرُونَ", translation: "Onlardan sana bakanlar da var. Fakat körlere sen mi doğru yolu göstereceksin? Eğer basiretli değillerse!", transliteration: "Ve minhum men yanzuru ileyk, e fe ente tehdil umye ve lev kanu la yubsırun."},
  {id: 1408, sure_no: 44, ayet_no: 44, sure_adi: "Yunus", text: "اِنَّ اللّٰهَ لَا يَظْلِمُ النَّاسَ شَيْـٔاً وَلٰكِنَّ النَّاسَ اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Allah, insanlara haksızlık yapmaz. Ancak insanlar kendi kendilerine haksızlık yaparlar.", transliteration: "İnnallahe la yazlimun nase şey'en ve lakinnen nase enfusehum yazlimun."},
  {id: 1409, sure_no: 45, ayet_no: 45, sure_adi: "Yunus", text: "وَيَوْمَ يَحْشُرُهُمْ كَاَنْ لَمْ يَلْبَثُٓوا اِلَّا سَاعَةً مِنَ النَّهَارِ يَتَعَارَفُونَ بَيْنَهُمْۜ قَدْ خَسِرَ الَّذ۪ينَ كَذَّبُوا بِلِقَٓاءِ اللّٰهِ وَمَا كَانُوا مُهْتَد۪ينَ", translation: "Onları toplayacağımız gün; dünyada, ancak birbirleri ile tanışabilecekleri kadar kısa olan, gündüzün bir saati kadar yaşamış gibi gelecek kendilerine. Allah'a kavuşmayı yalanlayıp, doğru yola yönelmemiş olanlar, gerçekten hüsrana uğramışlardır.", transliteration: "Ve yevme yahşuruhum keen lem yelbesu illa saaten minen nehari yete arefune beynehum, kad hasirellezine kezzebu bi likaillahi ve ma kanu muhtedin."},
  {id: 1410, sure_no: 46, ayet_no: 46, sure_adi: "Yunus", text: "وَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذ۪ي نَعِدُهُمْ اَوْ نَتَوَفَّـيَنَّكَ فَاِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللّٰهُ شَه۪يدٌ عَلٰى مَا يَفْعَلُونَ", translation: "Onlara yaptığımız uyarının bir kısmını sana göstersek de göstermeden vefat ettirsek de onların dönüşü Bizedir. Allah, onların ne yaptıklarına tanıktır.", transliteration: "Ve imma nuriyenneke ba'dellezi naıduhum ev neteveffeyenneke fe ileyna merciuhum summallahu şehidun ala ma yef'alun."},
  {id: 1411, sure_no: 47, ayet_no: 47, sure_adi: "Yunus", text: "وَلِكُلِّ اُمَّةٍ رَسُولٌۚ فَاِذَا جَٓاءَ رَسُولُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَ", translation: "Her ümmetin bir resulü vardır. Resulleri geldiği zaman aralarında hakkaniyetle hükmedilir. Asla haksızlığa uğratılmazlar.", transliteration: "Ve likulli ummetin resul, feiza cae resuluhum kudıye beynehum bil kıstı ve hum la yuzlamun."},
  {id: 1412, sure_no: 48, ayet_no: 48, sure_adi: "Yunus", text: "وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Eğer doğru söylüyorsanız, yaptığınız bu uyarı ne zaman gerçekleşecek?\" diyorlar.", transliteration: "Ve yekulune meta hazel va'du in kuntum sadıkin."},
  {id: 1413, sure_no: 49, ayet_no: 49, sure_adi: "Yunus", text: "قُلْ لَٓا اَمْلِكُ لِنَفْس۪ي ضَراًّ وَلَا نَفْعاً اِلَّا مَا شَٓاءَ اللّٰهُۜ لِكُلِّ اُمَّةٍ اَجَلٌۜ اِذَا جَٓاءَ اَجَلُهُمْ فَلَا يَسْتَأْخِرُونَ سَاعَةً وَلَا يَسْتَقْدِمُونَ", translation: "De ki: \"Ben, kendime dahi Allah'ın dilediğinden başka ne bir yarar sağlama ne de bir zarar verme gücüne sahibim.\" Her ümmetin bir süresi vardır. Süreleri gelince ne bir saat öne alınırlar ne de geriye bırakılırlar.", transliteration: "Kul la emliku li nefsi darran ve la nef'an illa ma şaallah, li kulli ummetin ecel, iza cae eceluhum fe la yeste'hırune saaten ve la yestakdimun."},
  {id: 1414, sure_no: 50, ayet_no: 50, sure_adi: "Yunus", text: "قُلْ اَرَاَيْتُمْ اِنْ اَتٰيكُمْ عَذَابُهُ بَيَاتاً اَوْ نَهَاراً مَاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ", translation: "De ki: \"Söyleyin bakalım! O'nun azabı ister gece uykuda ister gündüz gelsin. Suçlular neden bunu acele istiyorlar?\"", transliteration: "Kul ereeytum in etakum azabuhu beyaten ev neharen maza yesta'cilu minhul mucrimun."},
  {id: 1415, sure_no: 51, ayet_no: 51, sure_adi: "Yunus", text: "اَثُمَّ اِذَا مَا وَقَعَ اٰمَنْتُمْ بِه۪ۜ آٰلْـٰٔنَ وَقَدْ كُنْتُمْ بِه۪ تَسْتَعْجِلُونَ", translation: "Gerçekleşmesi için acele istediğiniz azaba gerçekleşince mi inanacaksınız yoksa şimdi mi?\"", transliteration: "E summe iza ma vakaa amentum bih, al'ane ve kad kuntum bihi testa'cilun."},
  {id: 1416, sure_no: 52, ayet_no: 52, sure_adi: "Yunus", text: "ثُمَّ ق۪يلَ لِلَّذ۪ينَ ظَلَمُوا ذُوقُوا عَذَابَ الْخُلْدِۚ هَلْ تُجْزَوْنَ اِلَّا بِمَا كُنْتُمْ تَكْسِبُونَ", translation: "Sonra zulmedenlere, \"Süresiz azabı tadın.\" denir. \"Kazandığınızdan başka bir karşılık mı bekliyordunuz?\"", transliteration: "Summe kile lillezine zalemu zuku azabel huld, hel tuczevne illa bima kuntum teksibun."},
  {id: 1417, sure_no: 53, ayet_no: 53, sure_adi: "Yunus", text: "وَيَسْتَنْبِـؤُ۫نَكَ اَحَقٌّ هُوَۜ قُلْ ا۪ي وَرَبّ۪ٓي اِنَّهُ لَحَقٌّ وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ۟", translation: "Sana \"Bu gerçek midir?\" diye soruyorlar. De ki: \"Rabb'ime yemin ederim ki o gerçektir. Ve siz onu engelleyemezsiniz.\"", transliteration: "Ve yestenbiuneke ehakkun hu, kul i ve rabbi innehu le hakkun ve ma entum bi mu'cizin."},
  {id: 1418, sure_no: 54, ayet_no: 54, sure_adi: "Yunus", text: "وَلَوْ اَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِي الْاَرْضِ لَافْتَدَتْ بِه۪ۜ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۚ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَ", translation: "Zulmedenler, yeryüzündekilerin tamamı kendilerinin olsaydı, kurtulmak için onları fidye olarak verirlerdi. Azabı gördükleri zaman içten bir pişmanlık duyarlar. Ancak aralarında hakkaniyetle hüküm verilir ve haksızlığa uğratılmazlar.", transliteration: "Ve lev enne li kulli nefsin zalemet ma fil ardı leftedet bih, ve eserrun nedamete lemma reevul azab, ve kudıye beynehum bil kıstı ve hum la yuzlemun."},
  {id: 1419, sure_no: 55, ayet_no: 55, sure_adi: "Yunus", text: "اَلَٓا اِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ اَلَٓا اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "İyi bilin ki, göklerde ve yerde ne varsa hepsi Allah'ındır. Yine iyi bilin ki, Allah'ın uyarısı gerçektir. Fakat onların çoğu bu gerçeği anlamıyor.", transliteration: "E la inne lillahi ma fis semavati vel ard, e la inne va'dallahi hakkun ve lakinne ekserehum la ya'lemun."},
  {id: 1420, sure_no: 56, ayet_no: 56, sure_adi: "Yunus", text: "هُوَ يُحْـي۪ وَيُم۪يتُ وَاِلَيْهِ تُرْجَعُونَ", translation: "Dirilten de öldüren de O'dur. Ve O'na döndürüleceksiniz.", transliteration: "Huve yuhyi ve yumitu ve ileyhi turceun."},
  {id: 1421, sure_no: 57, ayet_no: 57, sure_adi: "Yunus", text: "يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَتْكُمْ مَوْعِظَةٌ مِنْ رَبِّكُمْ وَشِفَٓاءٌ لِمَا فِي الصُّدُورِ وَهُدًى وَرَحْمَةٌ لِلْمُؤْمِن۪ينَ", translation: "Ey insanlar! Rabb'inizden size bir öğüt; göğüslerde olana bir şifa, Mü'minler için bir hidayet ve rahmet gelmiştir.", transliteration: "Ya eyyuhen nasu kad caetkum mev'ızatun min rabbikum ve şifaun lima fis suduri ve huden ve rahmetun lil mu'minin."},
  {id: 1422, sure_no: 58, ayet_no: 58, sure_adi: "Yunus", text: "قُلْ بِفَضْلِ اللّٰهِ وَبِرَحْمَتِه۪ فَبِذٰلِكَ فَلْيَفْرَحُواۜ هُوَ خَيْرٌ مِمَّا يَجْمَعُونَ", translation: "De ki: \"Allah'ın fazlı ve rahmetiyle gönül huzuruna kavuşsunlar. Bu onların toplayıp biriktirdiklerinden daha hayırlıdır.\"", transliteration: "Kul bi fadlillahi ve bi rahmetihi fe bi zalike felyefrehu, huve hayrun mimma yecmeun."},
  {id: 1423, sure_no: 59, ayet_no: 59, sure_adi: "Yunus", text: "قُلْ اَرَاَيْتُمْ مَٓا اَنْزَلَ اللّٰهُ لَكُمْ مِنْ رِزْقٍ فَجَعَلْتُمْ مِنْهُ حَرَاماً وَحَلَالاًۜ قُلْ آٰللّٰهُ اَذِنَ لَكُمْ اَمْ عَلَى اللّٰهِ تَفْتَرُونَ", translation: "De ki: \"Allah'ın size rızık olarak indirdiklerinin bir kısmını helal, bir kısmını haram yaptığınızı görmüyor musunuz?\" De ki: \"Allah mı size izin verdi, yoksa Allah'a iftira mı ediyorsunuz?\"", transliteration: "Kul e reeytum ma enzelallahu lekum min rızkın fe cealtum minhu haramen ve halala, kul allahu ezine lekum em alallahi tefterun."},
  {id: 1424, sure_no: 60, ayet_no: 60, sure_adi: "Yunus", text: "وَمَا ظَنُّ الَّذ۪ينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ يَوْمَ الْقِيٰمَةِۜ اِنَّ اللّٰهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُونَ۟", translation: "Allah'a karşı yalan uyduranlar, kıyamet gününü ne sanıyorlar? Doğrusu Allah, insanlara karşı bol bağış sahibidir. Ancak onların çoğu yine de şükretmezler.", transliteration: "Ve ma zannullezine yefterune alallahil kezibe yevmel kıyameh, innallahe le zu fadlın alen nasi ve lakinne ekserehum la yeşkurun."},
  {id: 1425, sure_no: 61, ayet_no: 61, sure_adi: "Yunus", text: "وَمَا تَكُونُ ف۪ي شَأْنٍ وَمَا تَتْلُوا مِنْهُ مِنْ قُرْاٰنٍ وَلَا تَعْمَلُونَ مِنْ عَمَلٍ اِلَّا كُنَّا عَلَيْكُمْ شُهُوداً اِذْ تُف۪يضُونَ ف۪يهِۜ وَمَا يَعْزُبُ عَنْ رَبِّكَ مِنْ مِثْقَالِ ذَرَّةٍ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِ وَلَٓا اَصْغَرَ مِنْ ذٰلِكَ وَلَٓا اَكْبَرَ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍ", translation: "Ne durumda olursanız olun ve Kur'an'dan onun hakkında ne okursanız okuyun; hangi işle uğraşıyorsanız uğraşın, unutmayın ki Biz mutlaka yaptıklarınıza tanığız. Göklerde ve yerde zerre ağırlığınca hiçbir şey Rabb'inizden gizli kalamaz. Ne bundan daha küçüğü ne de daha büyüğü yoktur ki, hepsi apaçık bir Kitap'ta olmasın.", transliteration: "Ve ma tekunu fi şe'nin ve ma tetlu minhu min kur'anin ve la ta'melune min amelin illa kunna aleykum şuhuden iz tufidune, fih ve ma ya'zubu an rabbike min miskali zerretin fil ardı ve la fis semai ve la asgare min zalike ve la ekbere illa fi kitabin mubin."},
  {id: 1426, sure_no: 62, ayet_no: 62, sure_adi: "Yunus", text: "اَلَٓا اِنَّ اَوْلِيَٓاءَ اللّٰهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۚ", translation: "İyi bilin ki, Allah'ın velileri için bir korku yoktur, onlar asla üzülmeyeceklerdir.", transliteration: "E la inne evliya allahi la havfun aleyhim ve la hum yahzenun."},
  {id: 1427, sure_no: 63, ayet_no: 63, sure_adi: "Yunus", text: "اَلَّذ۪ينَ اٰمَنُوا وَكَانُوا يَتَّقُونَۜ", translation: "Onlar, iman eden ve takva sahibi olan kimselerdir.", transliteration: "Ellezine amenu ve kanu yettekun."},
  {id: 1428, sure_no: 64, ayet_no: 64, sure_adi: "Yunus", text: "لَهُمُ الْبُشْرٰى فِي الْحَيٰوةِ الدُّنْيَا وَفِي الْاٰخِرَةِۜ لَا تَبْد۪يلَ لِكَلِمَاتِ اللّٰهِۜ ذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُۜ", translation: "Onlar için dünya hayatında da ahirette de müjdeler vardır. Allah'ın kelimelerinde asla bir değişiklik olmaz. İşte en büyük başarı budur.", transliteration: "Lehumul buşra fil hayatid dunya ve fil ahıreh, la tebdile li kelimatillah, zalike huvel fevzul azim."},
  {id: 1429, sure_no: 65, ayet_no: 65, sure_adi: "Yunus", text: "وَلَا يَحْزُنْكَ قَوْلُهُمْۢ اِنَّ الْعِزَّةَ لِلّٰهِ جَم۪يعاًۜ  هُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Onların sözleri seni üzmesin. İzzet bütünüyle Allah'a aittir. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve la yahzunke kavluhum, innel izzete lillahi cemia, huves semiul alim."},
  {id: 1430, sure_no: 66, ayet_no: 66, sure_adi: "Yunus", text: "اَلَٓا اِنَّ لِلّٰهِ مَنْ فِي السَّمٰوَاتِ وَمَنْ فِي الْاَرْضِۜ وَمَا يَتَّبِعُ الَّذ۪ينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ شُرَكَٓاءَۜ اِنْ يَتَّبِعُونَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُونَ", translation: "İyi bilin ki göklerde ve yerde kim varsa Allah'ındır. Oysaki Allah'ın yanı sıra başkasına tabi olanlar neye tabi oluyorlar? Onlar, ancak zanna tabi olmuş oluyorlar. Ve onlar ancak saçmalıyorlar.", transliteration: "E la inne lillahi men fis semavati ve men fil ard, ve ma yettebiullezine yed'une min dunillahi şureka, in yettebiune illez zanne ve in hum illa yahrusun."},
  {id: 1431, sure_no: 67, ayet_no: 67, sure_adi: "Yunus", text: "هُوَ الَّذ۪ي جَعَلَ لَـكُمُ الَّيْلَ لِتَسْكُنُوا ف۪يهِ وَالنَّهَارَ مُبْصِراًۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَسْمَعُونَ", translation: "Sükunet bulasınız diye geceyi karanlık ve gündüzü de aydınlık yapan O'dur. Bunda dinleyen bir halk için ayetler vardır.", transliteration: "Huvellezi ceale lekumul leyle li teskunu fihi ven nehare mubsıra, inne fi zalike leayatin li kavmin yesmeun."},
  {id: 1432, sure_no: 68, ayet_no: 68, sure_adi: "Yunus", text: "قَالُوا اتَّخَذَ اللّٰهُ وَلَداً سُبْحَانَهُۜ هُوَ الْغَنِيُّۜ لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ اِنْ عِنْدَ‌كُمْ مِنْ سُلْطَانٍ بِهٰذَاۜ  اَتَقُولُونَ عَلَى اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "\"Allah çocuk edindi.\" dediler. O, bundan münezzehtir. O'nun hiçbir şeye ihtiyacı yoktur. Göklerde ve yerde ne varsa O'nundur. Sizin bu konuda hiçbir sultanınız yoktur. Allah hakkında bilmediğiniz şeyi söylüyorsunuz!", transliteration: "Kaluttehazallahu veleden subhaneh, huvel ganiy, lehu ma fis semavati ve ma fil ard, in indekum min sultanin bi haza, e tekulune alallahi ma la ta'lemun."},
  {id: 1433, sure_no: 69, ayet_no: 69, sure_adi: "Yunus", text: "قُلْ اِنَّ الَّذ۪ينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُونَۜ", translation: "De ki: \"Allah hakkında yalan uyduranlar kurtuluşa eremezler.\"", transliteration: "Kul innellezine yefterune alallahil kezibe la yuflihun."},
  {id: 1434, sure_no: 70, ayet_no: 70, sure_adi: "Yunus", text: "مَتَاعٌ فِي الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذ۪يقُهُمُ الْعَذَابَ الشَّد۪يدَ بِمَا كَانُوا يَكْفُرُونَ۟", translation: "Dünyada geçici bir yararlanma vardır. Sonra dönüşleri Bize'dir. Sonra da küfr etmelerinden dolayı onlara şiddetli azabı tattırırız.", transliteration: "Metaun fid dunya summe ileyna merciuhum summe nuzikuhumul azabeş şedide bima kanu yekfurun."},
  {id: 1435, sure_no: 71, ayet_no: 71, sure_adi: "Yunus", text: "وَاتْلُ عَلَيْهِمْ نَبَاَ نُوحٍۢ اِذْ قَالَ لِقَوْمِه۪ يَا قَوْمِ اِنْ كَانَ كَبُرَ عَلَيْكُمْ مَقَام۪ي وَتَذْك۪ير۪ي بِاٰيَاتِ اللّٰهِ فَعَلَى اللّٰهِ تَوَكَّلْتُ فَاَجْمِعُٓوا اَمْرَكُمْ وَشُرَكَٓاءَكُمْ ثُمَّ لَا يَكُنْ اَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُٓوا اِلَيَّ وَلَا تُنْظِرُونِ", translation: "Onlara, Nuh'un haberini anlat. Hani o halkına: \"Ey halkım! Eğer aranızda durmam ve Allah'ın ayetlerini hatırlatmam size ağır geliyorsa, bilin ki ben yalnızca Allah'a güveniyorum. Öyleyse yapacağınızı yapmak için şirk koştuklarınızla toplanıp karar verin. Sonra ne yapacaksanız yapın. Sonra bana fırsat vermeden aldığınız kararı hemen uygulayın!\" demişti.", transliteration: "Vetlu aleyhim nebe'e nuh, iz kale li kavmihi ya kavmi in kane kebure aleykum makami ve tezkiri bi ayatillahi fe alallahi tevekkeltu fe ecmiu emrekum ve şurekaekum summe la yekun emrukum aleykum gummeten summakdu ileyye ve la tunzirun."},
  {id: 1436, sure_no: 72, ayet_no: 72, sure_adi: "Yunus", text: "فَاِنْ تَوَلَّيْتُمْ فَمَا سَاَلْتُكُمْ مِنْ اَجْرٍۜ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِۙ وَاُمِرْتُ اَنْ اَكُونَ مِنَ الْمُسْلِم۪ينَ", translation: "\"Eğer yüz çevirirseniz, siz bilirsiniz. Yaptığım işe karşılık sizden bir ücret istemiş değilim. Benim ücretim ancak Allah'a aittir. Ben, müslimlerden olmakla emrolundum.\"", transliteration: "Fe in tevelleytum fe ma se'eltukum min ecr, in ecriye illa alallahi ve umirtu en ekune minel muslimin."},
  {id: 1437, sure_no: 73, ayet_no: 73, sure_adi: "Yunus", text: "فَكَذَّبُوهُ فَنَجَّيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ وَجَعَلْنَاهُمْ خَلَٓائِفَ وَاَغْرَقْنَا الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَر۪ينَ", translation: "Onu yalanladılar. Biz de onu ve onunla beraber gemide bulunanları kurtardık. Ve onları yeryüzünün halifeleri yaptık. Ayetlerimizi yalanlayanları da boğduk. Bak! Uyarılanların sonu nice oldu!", transliteration: "Fe kezzebuhu fe necceynahu ve men meahu fil fulki ve cealnahum halaife ve agraknellezine kezzebu bi ayatina, fanzur keyfe kane akıbetul munzerin."},
  {id: 1438, sure_no: 74, ayet_no: 74, sure_adi: "Yunus", text: "ثُمَّ بَعَثْنَا مِنْ بَعْدِه۪ رُسُلاً اِلٰى قَوْمِهِمْ فَجَٓاؤُ۫هُمْ بِالْبَيِّنَاتِ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا بِه۪ مِنْ قَبْلُۜ كَذٰلِكَ نَطْبَعُ عَلٰى قُلُوبِ الْمُعْتَد۪ينَ", translation: "Sonra onun arkasından, Resulleri halklarına gönderdik. Onlar, halklarına kanıt içeren açık belgelerle geldiler. Daha önce yalanladıkları şeylere iman etmek istemediler. İşte haddi aşanların kalplerini mühürleriz.", transliteration: "Summe beasna min ba'dihi rusulen ila kavmihim fe cauhum bil beyyinati fe ma kanu li yu'minu bima kezzebu bihi min kabl, kezalike natbeu ala kulubil mugtedin."},
  {id: 1439, sure_no: 75, ayet_no: 75, sure_adi: "Yunus", text: "ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسٰى وَهٰرُونَ اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ بِاٰيَاتِنَا فَاسْتَكْبَرُوا وَكَانُوا قَوْماً مُجْرِم۪ينَ", translation: "Sonra onların arkasından Musa ve Harun'u ayetlerimizle Firavun ve melelerine gönderdik. Ancak onlar büyüklendiler, suçlu bir halk oldular.", transliteration: "Summe beasna min ba'dihim musa ve harune ila fir'avne ve melaihi bi ayatina festekberu ve kanu kavmen mucrimin."},
  {id: 1440, sure_no: 76, ayet_no: 76, sure_adi: "Yunus", text: "فَلَمَّا جَٓاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُٓوا اِنَّ هٰذَا لَسِحْرٌ مُب۪ينٌ", translation: "Onlara, Tarafımızdan hakikat geldiği zaman, \"Kuşkusuz bu apaçık bir sihirdir.\" dediler.", transliteration: "Fe lemma caehumul hakku min indina kalu inne haza le sıhrun mubin."},
  {id: 1441, sure_no: 77, ayet_no: 77, sure_adi: "Yunus", text: "قَالَ مُوسٰٓى اَتَقُولُونَ لِلْحَقِّ لَمَّا جَٓاءَكُمْۜ اَسِحْرٌ هٰذَاۜ وَلَا يُفْلِحُ السَّاحِرُونَ", translation: "Musa: \"Size gelen hakikat için, \"bu sihirdir\" mi diyorsunuz? Oysa sihirbazlar iflah olmazlar.\" dedi.", transliteration: "Kale musa e tekulune lil hakkı lemma caekum, e sıhrun haza, ve la yuflihus sahırun."},
  {id: 1442, sure_no: 78, ayet_no: 78, sure_adi: "Yunus", text: "قَالُٓوا اَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ اٰبَٓاءَنَا وَتَكُونَ لَكُمَا الْكِبْرِيَٓاءُ فِي الْاَرْضِۜ وَمَا نَحْنُ لَكُمَا بِمُؤْمِن۪ينَ", translation: "Dediler ki: \"Sen; bizi, atalarımızı üzerinde bulduğumuz yoldan çevirmek ve yeryüzünde gücün ikinizin eline geçmesini sağlamak için mi geldin? Biz, size Mü'min olacak değiliz.", transliteration: "Kalu e ci'tena li telfitena amma vecedna aleyhi abaena ve tekune lekumel kibriyau fil ard, ve ma nahnu lekuma bi mu'minin."},
  {id: 1443, sure_no: 79, ayet_no: 79, sure_adi: "Yunus", text: "وَقَالَ فِرْعَوْنُ ائْتُون۪ي بِكُلِّ سَاحِرٍ عَل۪يمٍ", translation: "Firavun: \"Bütün bilgin sihirbazları bana getirin!\" dedi.", transliteration: "Ve kale fir'avnu'tuni bi kulli sahırin alim."},
  {id: 1444, sure_no: 80, ayet_no: 80, sure_adi: "Yunus", text: "فَلَمَّا جَٓاءَ السَّحَرَةُ قَالَ لَهُمْ مُوسٰٓى اَلْقُوا مَٓا اَنْتُمْ مُلْقُونَ", translation: "Sihirbazlar gelince, Musa onlara: \"Atacaklarınızı atın.\" dedi.", transliteration: "Fe lemma caes seharetu kale lehum musa elku ma entum mulkun."},
  {id: 1445, sure_no: 81, ayet_no: 81, sure_adi: "Yunus", text: "فَلَمَّٓا اَلْقَوْا قَالَ مُوسٰى مَا جِئْتُمْ بِهِ السِّحْرُۜ اِنَّ اللّٰهَ سَيُبْطِلُهُۜ اِنَّ اللّٰهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِد۪ينَ", translation: "Onlar, atacaklarını atınca Musa: \"Sizin bu yaptığınız sihirdir. Allah onu boşa çıkaracaktır. Allah, bozguncuların işini düzeltmez.\" dedi.", transliteration: "Fe lemma elkav kale musa ma ci'tum bihis sihr, innallahe se yubtiluh, innallahe la yuslihu amelel mufsidin."},
  {id: 1446, sure_no: 82, ayet_no: 82, sure_adi: "Yunus", text: "وَيُحِقُّ اللّٰهُ الْحَقَّ بِكَلِمَاتِه۪ وَلَوْ كَرِهَ الْمُجْرِمُونَ۟", translation: "Suçlular hoşlanmasalar da Allah, kelimeleriyle gerçeği ortaya çıkaracaktır.", transliteration: "Ve yuhikkullahul hakka bi kelimatihi ve lev kerihel mucrimun."},
  {id: 1447, sure_no: 83, ayet_no: 83, sure_adi: "Yunus", text: "فَمَٓا اٰمَنَ لِمُوسٰٓى اِلَّا ذُرِّيَّةٌ مِنْ قَوْمِه۪ عَلٰى خَوْفٍ مِنْ فِرْعَوْنَ وَمَلَا۬ئِهِمْ اَنْ يَفْتِنَهُمْۜ وَاِنَّ فِرْعَوْنَ لَعَالٍ فِي الْاَرْضِۚ وَاِنَّهُ لَمِنَ الْمُسْرِف۪ينَ", translation: "Firavun ve melelerin, kendilerine kötülük yapacaklarından korktukları için, Musa'ya halkından az sayıda gençten başka iman eden olmadı. Zira Firavun, o yerde çok büyüklenmişti. O, çok aşırı gidenlerdendi.", transliteration: "Fe ma amene li musa illa zurriyyetun min kavmihi ala havfin min fir'avne ve melaihim en yeftinehum, ve inne fir'avne lealin fil ard ve innehu le minel musrifin."},
  {id: 1448, sure_no: 84, ayet_no: 84, sure_adi: "Yunus", text: "وَقَالَ مُوسٰى يَا قَوْمِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ فَعَلَيْهِ تَوَكَّلُٓوا اِنْ كُنْتُمْ مُسْلِم۪ينَ", translation: "Musa dedi ki: \"Ey halkım! Eğer Allah'a iman etmişseniz ve O'na teslim olmuşsanız, o halde O'na tevekkül edin.\"", transliteration: "Ve kale musa ya kavmi in kuntum amentum billahi fe aleyhi tevekkelu in kuntum muslimin."},
  {id: 1449, sure_no: 85, ayet_no: 85, sure_adi: "Yunus", text: "فَقَالُوا عَلَى اللّٰهِ تَوَكَّلْنَاۚ رَبَّـنَا لَا تَجْعَلْنَا فِتْنَةً لِلْقَوْمِ الظَّالِم۪ينَۙ", translation: "Dediler ki: \"Biz, Allah'a tevekkül ettik. Ey Rabb'imiz! Bizi zalim halk için bir fitne yapma.\"", transliteration: "Fe kalu alallahi tevekkelna, rabbena la tec'alna fitneten lil kavmiz zalimin."},
  {id: 1450, sure_no: 86, ayet_no: 86, sure_adi: "Yunus", text: "وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكَافِر۪ينَ", translation: "Rahmetinle bizi Kafir halktan kurtar.", transliteration: "Ve neccina bi rahmetike minel kavmil kafirin."},
  {id: 1451, sure_no: 87, ayet_no: 87, sure_adi: "Yunus", text: "وَاَوْحَيْنَٓا اِلٰى مُوسٰى وَاَخ۪يهِ اَنْ تَبَوَّاٰ لِقَوْمِكُمَا بِمِصْرَ بُيُوتاً وَاجْعَلُوا بُيُوتَكُمْ قِبْلَةً وَاَق۪يمُوا الصَّلٰوةَۜ وَبَشِّرِ الْمُؤْمِن۪ينَ", translation: "Musa ve kardeşine vahyettik: \"Halkınız için Mısır'da evler hazırlayın. Evlerinizi kıble yapın ve salatı ikame edin. Mü'minleri müjdele.\"", transliteration: "Ve evhayna ila musa ve ahihi en tebevvea li kavmikuma bi mısra buyuten vec'alu buyutekum kıbleten ve akimus salah, ve beşşiril mu'minin."},
  {id: 1452, sure_no: 88, ayet_no: 88, sure_adi: "Yunus", text: "وَقَالَ مُوسٰى رَبَّـنَٓا اِنَّكَ اٰتَيْتَ فِرْعَوْنَ وَمَلَاَهُ ز۪ينَةً وَاَمْوَالاً فِي الْحَيٰوةِ الدُّنْيَاۙ رَبَّـنَا لِيُضِلُّوا عَنْ سَب۪يلِكَۚ رَبَّـنَا اطْمِسْ عَلٰٓى اَمْوَالِهِمْ وَاشْدُدْ عَلٰى قُلُوبِهِمْ فَلَا يُؤْمِنُوا حَتّٰى يَرَوُا الْعَذَابَ الْاَل۪يمَ", translation: "Musa: \"Ey Rabb'imiz! Firavun ve melelerine dünya hayatında ziynet ve mallar verdin. Ey Rabb'imiz! Onlar ise bunu Sen'in yolundan saptırmak için kullanıyorlar. Rabb'imiz! Mallarını yok et, kalplerine sıkıntı ver. Zira onlar can yakıcı azabı görmedikçe iman etmezler.\" dedi.", transliteration: "Ve kale musa rabbena inneke ateyte fir'avne ve melahu zineten ve emvalen fil hayatid dunya rabbena li yudıllu an sebilik, rabbenatmis ala emvalihim veşdud ala kulubihim fe la yu'minu hatta yerevul azabel elim."},
  {id: 1453, sure_no: 89, ayet_no: 89, sure_adi: "Yunus", text: "قَالَ قَدْ اُج۪يبَتْ دَعْوَتُكُمَا فَاسْتَق۪يمَا وَلَا تَتَّبِعَٓانِّ سَب۪يلَ الَّذ۪ينَ لَا يَعْلَمُونَ", translation: "Allah: \"İkinizin duası kabul olunmuştur. Siz, doğru yolunuza devam edin. Bilmeyenlerin yoluna uymayın.\" dedi.", transliteration: "Kale kad ucibet da'vetukuma festekima ve la tettebi anni sebilellezine la ya'lemun."},
  {id: 1454, sure_no: 90, ayet_no: 90, sure_adi: "Yunus", text: "وَجَاوَزْنَا بِبَن۪ٓي اِسْرَٓائ۪لَ الْبَحْرَ فَاَتْبَعَهُمْ فِرْعَوْنُ وَجُنُودُهُ بَغْياً وَعَدْواًۜ حَتّٰٓى اِذَٓا اَدْرَكَهُ الْغَرَقُۙ قَالَ اٰمَنْتُ اَنَّهُ لَٓا اِلٰهَ اِلَّا الَّـذ۪ٓي اٰمَنَتْ بِه۪ بَنُٓوا اِسْرَٓائ۪لَ وَاَنَا۬ مِنَ الْمُسْلِم۪ينَ", translation: "İsrailoğullarını denizden geçirdik. Firavun ve ordusu, azgınlık ve düşmanlıkla onların peşlerine düştüler. Boğulmak üzereyken: \"İsrailoğullarının iman ettiği ilahtan başka ilah olmadığına iman ettim ve ben de müslimlerdenim.\" dedi.", transliteration: "Ve cavezna bi beni israilel bahre fe etbeahum fir'avnu ve cunuduhu bagyen ve adva, hatta iza edrekehul gareku kale amentu ennehu la ilahe illellezi amenet bihi benu israile ve ene minel muslimin."},
  {id: 1455, sure_no: 91, ayet_no: 91, sure_adi: "Yunus", text: "آٰلْـٰٔنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِد۪ينَ", translation: "\"Şimdi mi! Oysa daha önce isyan etmiş ve bozgunculardan olmuştun.\"", transliteration: "Al'ane ve kad asayte kablu ve kunte minel mufsidin."},
  {id: 1456, sure_no: 92, ayet_no: 92, sure_adi: "Yunus", text: "فَالْيَوْمَ نُنَجّ۪يكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ اٰيَةًۜ وَاِنَّ كَث۪يراً مِنَ النَّاسِ عَنْ اٰيَاتِنَا لَغَافِلُونَ۟", translation: "\"Kendinden sonrakilere bir ayet olman için, bugün senin cansız bedenini kurtaracağız.\" Gerçekte ise insanların çoğu ayetlerimizi umursamıyorlar.", transliteration: "Fel yevme nuneccike bi bedenike li tekune limen halfeke ayeh, ve inne kesiren minen nasi an ayatina le gafilun."},
  {id: 1457, sure_no: 93, ayet_no: 93, sure_adi: "Yunus", text: "وَلَقَدْ بَوَّأْنَا بَن۪ٓي اِسْرَٓائ۪لَ مُبَوَّاَ صِدْقٍ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِۚ فَمَا اخْتَلَفُوا حَتّٰى جَٓاءَهُمُ الْعِلْمُۜ اِنَّ رَبَّكَ يَقْض۪ي بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ", translation: "Gerçekten Biz, İsrailoğullarını güvenli bir yere yerleştirdik. Onları temiz ve hoş nimetlerle rızıklandırdık. Kendilerine ilim gelinceye dek ihtilafa düşmediler. Rabb'in, aralarında ihtilaf ettikleri şeyler hakkında kıyamet günü hükmünü verecektir.", transliteration: "Ve lekad bevve'na beni israile mubevvee sıdkın ve razaknahum minet tayyibat, femahtelefu hatta caehumul ilm, inne rabbeke yakdi beynehum yevmel kıyameti fi ma kanu fihi yahtelifun."},
  {id: 1458, sure_no: 94, ayet_no: 94, sure_adi: "Yunus", text: "فَاِنْ كُنْتَ ف۪ي شَكٍّ مِمَّٓا اَنْزَلْـنَٓا اِلَيْكَ فَسْـَٔلِ الَّذ۪ينَ يَقْرَؤُ۫نَ الْكِتَابَ مِنْ قَبْلِكَۚ لَقَدْ جَٓاءَكَ الْحَقُّ مِنْ رَبِّكَ فَلَا تَكُونَنَّ مِنَ الْمُمْتَر۪ينَۙ", translation: "Eğer sana indirdiğimizden şüphe ediyorsan, senden önce Kitap'ı duyuranlara sor! Ant olsun ki Rabb'inden sana gelen hakikattir. Sakın kuşku duyanlardan olma.", transliteration: "Fe in kunte fi şekkin mimma enzelna ileyke fes'elillezine yakreunel kitabe min kablik, lekad caekel hakku min rabbike fe la tekunenne minel mumterin."},
  {id: 1459, sure_no: 95, ayet_no: 95, sure_adi: "Yunus", text: "وَلَا تَكُونَنَّ مِنَ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِ اللّٰهِ فَتَكُونَ مِنَ الْخَاسِر۪ينَ", translation: "Allah'ın ayetlerini yalanlayanlardan olma. Yoksa hüsrana uğrayanlardan olursun.", transliteration: "Ve la tekunenne minellezine kezzebu bi ayatillahi fe tekune minel hasirin."},
  {id: 1460, sure_no: 96, ayet_no: 96, sure_adi: "Yunus", text: "اِنَّ الَّذ۪ينَ حَقَّتْ عَلَيْهِمْ كَلِمَتُ رَبِّكَ لَا يُؤْمِنُونَۙ", translation: "Rabb'inin haklarında kelimeleri gerçekleşecek olanlar, iman etmezler.", transliteration: "İnnellezine hakkat aleyhim kelimetu rabbike la yu'minun."},
  {id: 1461, sure_no: 97, ayet_no: 97, sure_adi: "Yunus", text: "وَلَوْ جَٓاءَتْهُمْ  كُلُّ اٰيَةٍ حَتّٰى يَرَوُا الْعَذَابَ الْاَل۪يمَ", translation: "Onlara, her türlü ayet gelse bile yine de can yakıcı azabı görmedikçe iman etmezler.", transliteration: "Ve lev caethum kullu ayetin hatta yerevul azabel elim."},
  {id: 1462, sure_no: 98, ayet_no: 98, sure_adi: "Yunus", text: "فَلَوْلَا كَانَتْ قَرْيَةٌ اٰمَنَتْ فَنَفَعَهَٓا ا۪يمَانُهَٓا اِلَّا قَوْمَ يُونُسَۜ لَمَّٓا اٰمَنُوا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِي الْحَيٰوةِ الدُّنْيَا وَمَتَّعْنَاهُمْ اِلٰى ح۪ينٍ", translation: "Keşke azap gelmeden önce iman edip de imanları kendilerine fayda vermiş bir kent olsaydı. Yalnız Yunus'un halkı iman edince, dünya hayatında onlardan rezillik azabını kaldırdık. Ve onları belli bir süre daha yararlandırdık.", transliteration: "Fe lev la kanet karyetun amenet fe nefeaha imanuha, illa kavme yunus, lemma amenu keşefna anhum azabel hızyi fil hayatid dunya ve metta'nahum ila hin."},
  {id: 1463, sure_no: 99, ayet_no: 99, sure_adi: "Yunus", text: "وَلَوْ شَٓاءَ رَبُّكَ لَاٰمَنَ مَنْ فِي الْاَرْضِ كُلُّهُمْ جَم۪يعاًۜ اَفَاَنْتَ تُكْرِهُ النَّاسَ حَتّٰى يَكُونُوا مُؤْمِن۪ينَ", translation: "Oysa Rabb'in dileseydi, yeryüzündekilerin tamamı iman ederdi! Bunu bildiğin halde, insanları zorla mı Mü'min yapacaksın?", transliteration: "Ve lev şae rabbuke le amene men fil ardı kulluhum cemia, e fe ente tukrihun nase hatta yekunu mu'minin."},
  {id: 1464, sure_no: 100, ayet_no: 100, sure_adi: "Yunus", text: "وَمَا كَانَ لِنَفْسٍ اَنْ تُؤْمِنَ اِلَّا بِاِذْنِ اللّٰهِۜ وَيَجْعَلُ الرِّجْسَ عَلَى الَّذ۪ينَ لَا يَعْقِلُونَ", translation: "Allah'ın bilgisi dışında hiç kimsenin iman etmesi mümkün değildir. Ve aklını kullanmayanları rics3 ile karşı karşıya bırakır.", transliteration: "Ve ma kane li nefsin en tu'mine illa bi iznillah, ve yec'alur ricse alellezine la ya'kılun."},
  {id: 1465, sure_no: 101, ayet_no: 101, sure_adi: "Yunus", text: "قُلِ انْظُرُوا مَاذَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَمَا تُغْنِي الْاٰيَاتُ وَالنُّذُرُ عَنْ قَوْمٍ لَا يُؤْمِنُونَ", translation: "De ki: \"Göklerde ve yerde olanlara bir bakın!\" Ancak o apaçık ayetler ve uyarılar iman etmeyecek bir halka yarar sağlamaz.", transliteration: "Kulinzuru maza fis semavati vel ard, ve ma tugnil ayatu ven nuzuru an kavmin la yu'minun."},
  {id: 1466, sure_no: 102, ayet_no: 102, sure_adi: "Yunus", text: "فَهَلْ يَنْتَظِرُونَ اِلَّا مِثْلَ اَيَّامِ الَّذ۪ينَ خَلَوْا مِنْ قَبْلِهِمْۜ قُلْ فَانْتَظِرُٓوا اِنّ۪ي مَعَكُمْ مِنَ الْمُنْتَظِر۪ينَ", translation: "Kendilerinden öncekilerin başından geçen felaketlerin bir benzerini mi bekliyorlar? De ki: \"Bekleyin, ben de sizinle beraber bekliyorum.\"", transliteration: "Fe hel yentezırune illa misle eyyamillezine halev min kablihim, kul fentezıru inni meakum minel muntezirin."},
  {id: 1467, sure_no: 103, ayet_no: 103, sure_adi: "Yunus", text: "ثُمَّ نُنَجّ۪ي رُسُلَنَا وَالَّذ۪ينَ اٰمَنُوا كَذٰلِكَۚ حَقاًّ عَلَيْنَا نُنْجِ الْمُؤْمِن۪ينَ۟", translation: "Sonra Resullerimizi ve İman Edenleri kurtarırız. İman Edenleri kurtarmak üzerimize bir haktır.", transliteration: "Summe nunecci rusulena vellezine amenu kezalik, hakkan aleyna nuncil mu'minin."},
  {id: 1468, sure_no: 104, ayet_no: 104, sure_adi: "Yunus", text: "قُلْ يَٓا اَيُّهَا النَّاسُ اِنْ كُنْتُمْ ف۪ي شَكٍّ مِنْ د۪ين۪ي فَلَٓا اَعْبُدُ الَّذ۪ينَ تَعْبُدُونَ مِنْ دُونِ اللّٰهِ وَلٰكِنْ اَعْبُدُ اللّٰهَ الَّذ۪ي يَتَوَفّٰيكُمْۚ وَاُمِرْتُ اَنْ اَكُونَ مِنَ الْمُؤْمِن۪ينَۙ", translation: "De ki: \"Ey insanlar! Eğer benim dinimden kuşku duyuyorsanız, ben, sizin Allah'ın yanı sıra kulluk ettiklerinize kulluk etmem. Ben, ancak sizin canınızı alacak olan Allah'a kulluk ederim. Ve bana iman edenlerden olmam buyruldu.\"", transliteration: "Kul ya eyyuhen nasu in kuntum fi şekkin min dini,fe la a'budullezine ta'budune min dunillahi, ve lakin a'budullahellezi yeteveffakum, ve umirtu en ekune minel mu'minin."},
  {id: 1469, sure_no: 105, ayet_no: 105, sure_adi: "Yunus", text: "وَاَنْ اَقِمْ وَجْهَكَ لِلدّ۪ينِ حَن۪يفاًۚ وَلَا تَكُونَنَّ مِنَ الْمُشْرِك۪ينَ", translation: "Allah'ı birleyici olarak dine yönel. Sakın Müşriklerden olma.", transliteration: "Ve en ekim vecheke lid dini hanifa, ve la tekunenne minel muşrikin."},
  {id: 1470, sure_no: 106, ayet_no: 106, sure_adi: "Yunus", text: "وَلَا تَدْعُ مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُكَ وَلَا يَضُرُّكَۚ فَاِنْ فَعَلْتَ فَاِنَّكَ اِذاً مِنَ الظَّالِم۪ينَ", translation: "Allah'ı bırakıp da sana fayda da zarar da veremeyecek olanlara yönelme. Böyle yaparsan zalimlerden olursun.", transliteration: "Ve la ted'u min dunillahi ma la yenfeuke ve la yadurruk, fe in fealte fe inneke izen minez zalimin."},
  {id: 1471, sure_no: 107, ayet_no: 107, sure_adi: "Yunus", text: "وَاِنْ يَمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهُٓ اِلَّا هُوَۚ وَاِنْ يُرِدْكَ بِخَيْرٍ فَلَا رَٓادَّ لِفَضْلِه۪ۜ يُص۪يبُ بِه۪ مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَهُوَ الْغَفُورُ الرَّح۪يمُ", translation: "Eğer Allah, sana bir sıkıntı verirse onu Kendisinden başka giderebilecek yoktur. Eğer senin için bir hayır dilerse, O'nun fazlını geri çevirebilecek yoktur. O, onu kullarından hak edene nasip eder. O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve in yemseskallahu bidurrin fe la kaşife lehu illa hu, ve in yuridke bi hayrin fe la radde li fadlih, yusibu bihi men yeşau min ibadih, ve huvel gafurur rahim."},
  {id: 1472, sure_no: 108, ayet_no: 108, sure_adi: "Yunus", text: "قُلْ يَٓا اَيُّهَا النَّاسُ قَدْ جَٓاءَكُمُ الْحَقُّ مِنْ رَبِّكُمْۚ  فَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَد۪ي لِنَفْسِه۪ۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۚ وَمَٓا اَنَا۬ عَلَيْكُمْ بِوَك۪يلٍۜ", translation: "De ki: \"Ey insanlar, işte Rabb'inizden size Hakk geldi. Doğru yolu kabul eden, kendisi için kabul etmiş olur. Sapkınlık eden de kendi aleyhine sapmış olur. Ve ben sizin vekiliniz değilim.\"", transliteration: "Kul ya eyyuhen nasu kad caekumul hakku min rabbikum, fe men ihteda fe innema yehtedi li nefsih, ve men dalle fe innema yadıllu aleyha, ve ma ene aleykum bi vekil."},
  {id: 1473, sure_no: 109, ayet_no: 109, sure_adi: "Yunus", text: "وَاتَّبِعْ مَا يُوحٰٓى اِلَيْكَ وَاصْبِرْ حَتّٰى يَحْكُمَ اللّٰهُۚ وَهُوَ خَيْرُ الْحَاكِم۪ينَ", translation: "Ve sana vahyolunana uy. Allah, hükmünü verene kadar sabret. Ve O, Hüküm Verenlerin En Hayırlısıdır.", transliteration: "Vettebi' ma yuha ileyke vasbir hatta yahkumallah, ve huve hayrul hakimin."},
  {id: 1474, sure_no: 1, ayet_no: 1, sure_adi: "Hud", text: "الٓـرٰ۠   كِتَابٌ اُحْكِمَتْ اٰيَاتُهُ ثُمَّ فُصِّلَتْ مِنْ لَدُنْ حَك۪يمٍ خَب۪يرٍۙ", translation: "Elif, Lam, Ra. Bu, Yasa Sahibi, Her Şeyden Haberdar Olan tarafından, ayetleri sağlamlaştırılmış ve ayrıntılı olarak açıklanmış bir Kitap'tır.", transliteration: "Elif lam ra kitabun uhkimet ayatuhu summe fussılet min ledun hakimin habir."},
  {id: 1475, sure_no: 2, ayet_no: 2, sure_adi: "Hud", text: "اَلَّا تَعْبُدُٓوا اِلَّا اللّٰهَۜ اِنَّن۪ي لَكُمْ مِنْهُ نَذ۪يرٌ وَبَش۪يرٌۙ", translation: "Ki Allah'tan başkasına kulluk yapmayasınız. Kuşkusuz ben, O'nun tarafından size gönderilmiş bir uyarıcı ve haber vericiyim.", transliteration: "Ella ta'budu illallah, inneni lekum minhu nezirun ve beşir."},
  {id: 1476, sure_no: 3, ayet_no: 3, sure_adi: "Hud", text: "وَاَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُٓوا اِلَيْهِ يُمَتِّعْكُمْ مَتَاعاً حَسَناً اِلٰٓى اَجَلٍ مُسَمًّى وَيُؤْتِ كُلَّ ذ۪ي فَضْلٍ فَضْلَهُۜ وَاِنْ تَوَلَّوْا فَاِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَب۪يرٍ", translation: "Ve Rabb'inizden bağışlanma dileyin, sonra O'na tevbe edin ki belirli bir süreye kadar sizi iyi nimetlerden yararlandırsın. Fazilet sahibine, faziletinin karşılığını versin. Eğer yüz çevirirseniz, ben sizin için büyük günün azabından korkarım.", transliteration: "Ve enistagfiru rabbekum summe tubu ileyhi yumetti'kum metaan hasenen ila ecelin musemmen ve yu'ti kulle zi fadlin fadleh, ve in tevellev fe inni ehafu aleykum azabe yevmin kebir."},
  {id: 1477, sure_no: 4, ayet_no: 4, sure_adi: "Hud", text: "اِلَى اللّٰهِ مَرْجِعُكُمْۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Dönüşünüz yalnızca Allah'adır. O'nun Gücü Her Şeye Yeter.", transliteration: "İlallahi merciukum, ve huve ala kulli şey'in kadir."},
  {id: 1478, sure_no: 5, ayet_no: 5, sure_adi: "Hud", text: "اَلَٓا اِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُوا مِنْهُۜ  اَلَا ح۪ينَ يَسْتَغْشُونَ ثِيَابَهُمْۙ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَۚ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "İyi bilin ki! Onlar, O'ndan düşmanlıklarını gizlemek için iki büklüm olurlar. Yine iyi bilin ki! Onlar, örtülerine büründükleri zaman, O, neyi gizleyip neyi açığa çıkardıklarını bilir. O, göğüslerde olanı en iyi bilendir.", transliteration: "E la innehum yesnune sudurehum li yestahfu minh, e la hine yestagşune siyabehum ya'lemu ma yusirrune ve ma yu'linun, innehu alimun bi zatis sudur."},
  {id: 1479, sure_no: 6, ayet_no: 6, sure_adi: "Hud", text: "وَمَا مِنْ دَٓابَّةٍ فِي الْاَرْضِ اِلَّا عَلَى اللّٰهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَاۜ كُلٌّ ف۪ي كِتَابٍ مُب۪ينٍ", translation: "Yeryüzünde yaşayan hiçbir canlı yoktur ki, rızkı Allah'a ait olmasın. O, onun karar kıldığı yeri ve geçici durduğu yeri bilir. Bunların tamamı apaçık bir Kitap'tadır.", transliteration: "Ve ma min dabbetin fil ardı illa alallahi rızkuha ve ya'lemu mustekarreha ve mustevdeaha, kullun fi kitabin mubin."},
  {id: 1480, sure_no: 7, ayet_no: 7, sure_adi: "Hud", text: "وَهُوَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ ف۪ي سِتَّةِ اَيَّامٍ وَكَانَ عَرْشُهُ عَلَى الْمَٓاءِ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلاًۜ وَلَئِنْ قُلْتَ اِنَّكُمْ مَبْعُوثُونَ مِنْ بَعْدِ الْمَوْتِ لَيَقُولَنَّ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ", translation: "Gökleri ve yeri altı günde yaratan O'dur. Daha önce arşı su üzerindeydi. Sizi yaratması, hanginizin iyi şeyler yapacağını sınav yapmak içindir. Eğer Kafirlere: \"Gerçekten siz, öldükten sonra diriltilecekseniz.\" desen, \"Bu ancak apaçık bir büyüdür.\" diyecekler.", transliteration: "Ve huvellezi halakas semavati vel arda fi sitteti eyyamin ve kane arşuhu alel mai li yebluvekum eyyukum ahsenu amela, ve le in kulte innekum meb'usune min ba'dil mevti le yekulennellezine keferu in haza illa sihrun mubin."},
  {id: 1481, sure_no: 8, ayet_no: 8, sure_adi: "Hud", text: "وَلَئِنْ اَخَّرْنَا عَنْهُمُ الْعَذَابَ اِلٰٓى اُمَّةٍ مَعْدُودَةٍ لَيَقُولُنَّ مَا يَحْبِسُهُۜ اَلَا يَوْمَ يَأْت۪يهِمْ لَيْسَ مَصْرُوفاً عَنْهُمْ وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟", translation: "Eğer, bir ümmet için azabı onlardan belli bir süreye kadar erteleyecek olursak, \"Bunu engelleyen şey nedir ki?\" derler. Bilesiniz ki, onlara azap geldiği gün, artık geri çevrilmez. Alaya aldıkları azap onları kuşatır.", transliteration: "Ve le in ahharna anhumul azabe ila ummetin ma'dudetin le yekulunne ma yahbisuh, e la yevme ye'tihim leyse masrufen anhum ve haka bi him ma kanu bihi yestehziun."},
  {id: 1482, sure_no: 9, ayet_no: 9, sure_adi: "Hud", text: "وَلَئِنْ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنَاهَا مِنْهُۚ اِنَّهُ لَيَؤُ۫سٌ كَفُورٌ", translation: "Eğer insana, tarafımızdan bir rahmet tattırsak, sonra da onu geri alsak, ümitsizliğe kapılır ve küfreder.", transliteration: "Ve le in ezaknal insane minna rahmeten summe neza'naha minh, innehu le yeusun kefur."},
  {id: 1483, sure_no: 10, ayet_no: 10, sure_adi: "Hud", text: "وَلَئِنْ اَذَقْنَاهُ نَعْمَٓاءَ بَعْدَ ضَرَّٓاءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ السَّيِّـَٔاتُ عَنّ۪يۜ  اِنَّهُ لَفَرِحٌ فَخُورٌۙ", translation: "Eğer, kendisine dokunan bir zarardan sonra, ona bir nimet tattırsak, \"Kötülüklerden kurtuldum.\" diye böbürlenir ve şımarmaya başlar.", transliteration: "Ve le in ezaknahu na'mae ba'de darrae messethu le yekulenne zehebes seyyiatu anni, innehu le ferihun fahur."},
  {id: 1484, sure_no: 11, ayet_no: 11, sure_adi: "Hud", text: "اِلَّا الَّذ۪ينَ صَبَرُوا وَعَمِلُوا الصَّالِحَاتِۜ  اُو۬لٰٓئِكَ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ كَب۪يرٌ", translation: "Ancak sabredenler ve salihatı yapanlar hariç. İşte onlar için bağışlanma ve büyük bir ödül vardır.", transliteration: "İllellezine saberu ve amilus salihat, ulaike lehum magfiretun ve ecrun kebir."},
  {id: 1485, sure_no: 12, ayet_no: 12, sure_adi: "Hud", text: "فَلَعَلَّكَ تَارِكٌ بَعْضَ مَا يُوحٰٓى اِلَيْكَ وَضَٓائِقٌ بِه۪ صَدْرُكَ اَنْ يَقُولُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ كَنْزٌ اَوْ جَٓاءَ مَعَهُ مَلَكٌۜ اِنَّـمَٓا اَنْتَ نَذ۪يرٌۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ وَك۪يلٌۜ", translation: "O halde sen, \"Ona bir hazine indirilmeli veya onunla bir melek gelmeli değil miydi?\" dedikleri için göğsün daralıp; sana vahyolunanın bir kısmını tebliğ etmekten vaz mı geçeceksin? Sen yalnızca bir uyarıcısın. Allah ise her şeye vekildir.", transliteration: "Fe lealleke tarikun ba'da ma yuha ileyke ve daikun bihi sadruke en yekulu lev la unzile aleyhi kenzun ev cae meahu melek, innema ente nezir, vallahu ala kulli şey'in vekil."},
  {id: 1486, sure_no: 13, ayet_no: 13, sure_adi: "Hud", text: "اَمْ يَقُولُونَ افْتَرٰيهُۜ  قُلْ فَأْتُوا بِعَشْرِ سُوَرٍ مِثْلِه۪ مُفْتَرَيَاتٍ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللّٰهِ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Yoksa \"Onu uydurdu.\" mu diyorlar? De ki: \"Eğer, doğru söylüyorsanız haydi onun benzeri on sure getirin. Allah'tan başka çağırabileceklerinizi de çağırın.\"", transliteration: "Em yekulunefterah, kul fe'tu bi aşri suverin mislihi muftereyatin ved'u menisteta'tum min dunillahi in kuntum sadikin."},
  {id: 1487, sure_no: 14, ayet_no: 14, sure_adi: "Hud", text: "فَاِلَّمْ يَسْتَج۪يبُوا لَكُمْ فَاعْلَمُٓوا اَنَّـمَٓا اُنْزِلَ بِعِلْمِ اللّٰهِ وَاَنْ لَٓا اِلٰهَ اِلَّا هُوَۚ فَهَلْ اَنْتُمْ مُسْلِمُونَ", translation: "Eğer, size cevap veremezlerse; iyi bilin ki, bu ancak Allah'ın ilmiyle indirilmiştir. O'ndan başka ilah yoktur. Hala müslim olmayacak mısınız?", transliteration: "Fe illem yestecibu lekum fa'lemu ennema unzile bi ilmillahi ve en la ilahe illa hu, fe hel entum muslimun."},
  {id: 1488, sure_no: 15, ayet_no: 15, sure_adi: "Hud", text: "مَنْ كَانَ يُر۪يدُ الْحَيٰوةَ الدُّنْيَا وَز۪ينَتَهَا نُوَفِّ اِلَيْهِمْ اَعْمَالَهُمْ ف۪يهَا وَهُمْ ف۪يهَا لَا يُبْخَسُونَ", translation: "Kim sadece dünya hayatını ve onun ziynetini isterse, onlara yaptıklarının karşılığını eksiksiz veririz. Bu hususta onlara hiçbir haksızlık yapılmaz.", transliteration: "Men kane yuridul hayated dunya ve zineteha nuveffi ileyhim a'malehum fiha ve hum fiha la yubhasun."},
  {id: 1489, sure_no: 16, ayet_no: 16, sure_adi: "Hud", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ لَيْسَ لَهُمْ فِي الْاٰخِرَةِ اِلَّا النَّارُۘ وَحَبِطَ مَا صَنَعُوا ف۪يهَا وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ", translation: "İşte bu kimselere, ahirette ateşten başka bir şey yoktur. Burada yaptıkları şeyler boşa gitmiştir. Zaten yaptıkları bütün işleri geçersizdir.", transliteration: "Ulaikellezine leyse lehum fil ahıreti illen nar ve habita ma sanau fiha ve batılun ma kanu ya'melun."},
  {id: 1490, sure_no: 17, ayet_no: 17, sure_adi: "Hud", text: "اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِنْ رَبِّه۪ وَيَتْلُوهُ شَاهِدٌ مِنْهُ وَمِنْ قَبْلِه۪ كِتَابُ مُوسٰٓى اِمَاماً وَرَحْمَةًۜ  اُو۬لٰٓئِكَ يُؤْمِنُونَ بِه۪ۜ وَمَنْ يَكْفُرْ بِه۪ مِنَ الْاَحْزَابِ فَالنَّارُ مَوْعِدُهُۚ فَلَا تَكُ ف۪ي مِرْيَةٍ مِنْهُ اِنَّهُ الْحَقُّ مِنْ رَبِّكَ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ", translation: "Rabb'inden, kanıt içeren bir bilgi üzerinde olan kimse ile böyle olmayan kimse bir olur mu? Bunu Rabb'inden bir tanık ve bir de ondan önce rehber ve rahmet olarak Musa'nın Kitap'ı desteklemektedir. İşte bunlar, ona iman ederler. Hangi grup onu küfr ederse, varacağı yer ateştir. Ondan kuşkun olmasın. Kuşkusuz o Rabb'inden bir gerçektir. Fakat insanların çoğu iman etmezler.", transliteration: "E fe men kane ala beyyinetin min rabbihi ve yetluhu şahidun minhu ve min kablihi kitabu musa imamen ve rahmeh, ulaike yu'minune bih, ve men yekfur bihi minel ahzabi fen naru mev'ıduh, fe la teku fi miryetin minhu innehul hakku min rabbike ve lakinne ekseren nasi la yu'minun."},
  {id: 1491, sure_no: 18, ayet_no: 18, sure_adi: "Hud", text: "وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباًۜ اُو۬لٰٓئِكَ يُعْرَضُونَ عَلٰى رَبِّهِمْ وَيَقُولُ الْاَشْهَادُ هٰٓؤُ۬لَٓاءِ الَّذ۪ينَ كَذَبُوا عَلٰى رَبِّهِمْۚ اَلَا لَعْنَةُ اللّٰهِ عَلَى الظَّالِم۪ينَۙ", translation: "Uydurduğu yalanı Allah'a dayandırandan daha zalim kim olabilir? Onlar, Rabb'lerinin huzuruna çıkarılacaklar ve tanıklar da: \"Rabb'lerine karşı yalan uyduranlar işte bunlardır.\" diyeceklerdir. İyi bilin ki Allah'ın laneti zalimlerin üzerinedir.", transliteration: "Ve men ezlemu mimmeniftera alallahi keziba, ulaike yu'radune ala rabbihim ve yekulul eşhadu haulaillezine kezebu ala rabbihim, e la la'netullahi alaz zalimin."},
  {id: 1492, sure_no: 19, ayet_no: 19, sure_adi: "Hud", text: "اَلَّذ۪ينَ يَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَيَبْغُونَهَا عِوَجاًۜ وَهُمْ بِالْاٰخِرَةِ هُمْ كَافِرُونَ", translation: "O kimseler, Allah'ın yolundan alıkoyarlar. Ve o yolu eğri göstermeye çalışırlar. Ve onlar, ahiret hayatını yok sayarlar.", transliteration: "Ellezine yasuddune an sebilillahi ve yebguneha iveca, ve hum bil ahıreti hum kafirun."},
  {id: 1493, sure_no: 20, ayet_no: 20, sure_adi: "Hud", text: "اُو۬لٰٓئِكَ لَمْ يَكُونُوا مُعْجِز۪ينَ فِي الْاَرْضِ وَمَا كَانَ لَهُمْ مِنْ دُونِ اللّٰهِ مِنْ اَوْلِيَٓاءَۢ يُضَاعَفُ لَهُمُ الْعَذَابُۜ مَا كَانُوا يَسْتَط۪يعُونَ السَّمْعَ وَمَا كَانُوا يُبْصِرُونَ", translation: "Onlar, yeryüzünde Allah'ı aciz bırakabilecek değillerdir. Kendilerini, Allah'a karşı koruyacak bir yardımcı da bulamayacaklardır. Onların azapları kat kat olacaktır. Onlar, gerçeği duymaya ve görmeye tahammül edemiyorlardı.", transliteration: "Ulaike lem yekunu mu'cizine fil ardı ve ma kane lehum min dunillahi min evliya, yudaafu lehumul azab, ma kanu yestetiunes sem'a ve ma kanu yubsirun."},
  {id: 1494, sure_no: 21, ayet_no: 21, sure_adi: "Hud", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ", translation: "İşte onlar, kendilerine yazık eden kimselerdir. Uydurdukları şeyler de kendilerini terk edip kaybolmuştur.", transliteration: "Ulaikellezine hasiru enfusehum ve dalle anhum ma kanu yefterun."},
  {id: 1495, sure_no: 22, ayet_no: 22, sure_adi: "Hud", text: "لَا جَرَمَ اَنَّهُمْ فِي الْاٰخِرَةِ هُمُ الْاَخْسَرُونَ", translation: "Onlar ahirette en büyük kayba uğrayanlardır.", transliteration: "La cereme ennehum fil ahıreti humul ahserun."},
  {id: 1496, sure_no: 23, ayet_no: 23, sure_adi: "Hud", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَاَخْبَتُٓوا اِلٰى رَبِّهِمْۙ اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "İman edip, salihatı yapanlar, Rabb'lerine içtenlikle bağlı olanlar; Cennet ehlidirler. Onlar, orada sürekli kalacaklardır.", transliteration: "İnnellezine amenu ve amilus salihati ve ahbetu ila rabbihim ulaike ashabul cenneh, hum fiha halidun."},
  {id: 1497, sure_no: 24, ayet_no: 24, sure_adi: "Hud", text: "مَثَلُ الْفَر۪يقَيْنِ كَالْاَعْمٰى وَالْاَصَمِّ وَالْبَص۪يرِ وَالسَّم۪يعِۜ هَلْ يَسْتَوِيَانِ مَثَلاًۜ اَفَلَا تَذَكَّرُونَ۟", translation: "Bu iki grubun durumu; kör ve sağır ile gören ve duyanın durumu gibidir. Bunlar hiç bir olur mu? Niçin düşünüp öğüt almıyorsunuz?", transliteration: "Meselul ferikayni kel a'ma vel esammi vel basiri ves semi' hel yesteviyani mesela e fe la tezekkerun."},
  {id: 1498, sure_no: 25, ayet_no: 25, sure_adi: "Hud", text: "وَلَقَدْ اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ۘ اِنّ۪ي لَكُمْ نَذ۪يرٌ مُب۪ينٌۙ", translation: "Ant olsun ki! Biz, Nuh'u kendi halkına göndermiştik. \"Ben sizin için apaçık bir uyarıcıyım;\"", transliteration: "Ve lekad erselna nuhan ila kavmihi inni lekum nezirun mubin."},
  {id: 1499, sure_no: 26, ayet_no: 26, sure_adi: "Hud", text: "اَنْ لَا تَعْبُدُٓوا اِلَّا اللّٰهَۜ اِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ اَل۪يمٍ", translation: "\"Allah'tan başkasına kulluk etmeyin. Ben, sizin için can yakıcı bir günün azabından korkuyorum.\"", transliteration: "En la ta'budu illallah, inni ehafu aleykum azabe yevmin elim."},
  {id: 1500, sure_no: 27, ayet_no: 27, sure_adi: "Hud", text: "فَقَالَ الْمَلَأُ الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ مَا نَرٰيكَ اِلَّا بَشَراً مِثْلَنَا وَمَا نَرٰيكَ اتَّبَعَكَ اِلَّا الَّذ۪ينَ هُمْ اَرَاذِلُنَا بَادِيَ الرَّأْيِۚ وَمَا نَرٰى لَكُمْ عَلَيْنَا مِنْ فَضْلٍ بَلْ نَظُنُّكُمْ كَاذِب۪ينَ", translation: "Bunun üzerine halkının kafir meleleri: \"Biz, seni kendimiz gibi bir beşer olarak görüyoruz. Görüyoruz ki, sana tabi olanlar, bizim toplumun en zayıf ve sefil olanlarıdır. Sizin, bize karşı bir üstünlüğünüzü görmüyoruz. Bilakis sizin yalancılardan olduğunuzu düşünüyoruz.\" dediler.", transliteration: "Fe kalel meleullezine keferu min kavmihi ma nerake illa beşeren mislena ve ma nerakettebeake illellezine hum eraziluna badiyer re'y, ve ma nera lekum aleyna min fadlin bel nezunnukum kazibin."},
  {id: 1501, sure_no: 28, ayet_no: 28, sure_adi: "Hud", text: "قَالَ يَا قَوْمِ اَرَاَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَاٰتٰين۪ي رَحْمَةً مِنْ عِنْدِه۪ فَعُمِّيَتْ عَلَيْكُمْۜ اَنُلْزِمُكُمُوهَا وَاَنْتُمْ لَهَا كَارِهُونَ", translation: "Dedi ki: \"Ey halkım! Bakın! Ya ben Rabb'imden açık bir kanıt üzerinde isem ve O'nun katından bana bir rahmet verilmişse ve siz de bunu görmüyorsanız; istemediğiniz halde, onu size zorla kabul ettirebilir miyim?\"", transliteration: "Kale ya kavmi e reeytum in kuntu ala beyyinetin min rabbi ve atani rahmeten min indihi fe ummiyet aleykum, e nulzimukumuha ve entum leha karihun."},
  {id: 1502, sure_no: 29, ayet_no: 29, sure_adi: "Hud", text: "وَيَا قَوْمِ لَٓا اَسْـَٔلُكُمْ عَلَيْهِ مَالاًۜ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ وَمَٓا اَنَا۬ بِطَارِدِ الَّذ۪ينَ اٰمَنُواۜ اِنَّهُمْ مُلَاقُوا رَبِّهِمْ وَلٰكِنّ۪ٓي اَرٰيكُمْ قَوْماً تَجْهَلُونَ", translation: "\"Ey halkım! Buna karşı sizden herhangi bir ücret istemiyorum. Benim ücretim, yalnızca Allah'a aittir. Siz, hor görüyorsunuz diye, iman edenleri yanımdan kovacak değilim. Onlar, Rabb'lerine kavuşacaklardır. Fakat ben, sizi cahillik eden bir halk olarak görüyorum.\"", transliteration: "Ve ya kavmi la es'elukum aleyhi mala, in ecriye illa alallahi ve ma ene bi taridillezine amenu, innehum mulaku rabbihim ve lakinni erakum kavmen techelun."},
  {id: 1503, sure_no: 30, ayet_no: 30, sure_adi: "Hud", text: "وَيَا قَوْمِ مَنْ يَنْصُرُن۪ي مِنَ اللّٰهِ اِنْ طَرَدْتُهُمْۜ اَفَلَا تَذَكَّرُونَ", translation: "\"Ey halkım! Eğer onları yanımdan kovacak olursam, Allah'a karşı bana kim yardımcı olacak? Hiç düşünmüyor musunuz?\"", transliteration: "Ve ya kavmi men yansuruni minallahi in taredtuhum, e fe la tezekkerun."},
  {id: 1504, sure_no: 31, ayet_no: 31, sure_adi: "Hud", text: "وَلَٓا اَقُولُ لَكُمْ عِنْد۪ي خَزَٓائِنُ اللّٰهِ وَلَٓا اَعْلَمُ الْغَيْبَ وَلَٓا اَقُولُ اِنّ۪ي مَلَكٌ وَلَٓا اَقُولُ لِلَّذ۪ينَ تَزْدَر۪ٓي اَعْيُنُكُمْ لَنْ يُؤْتِيَهُمُ اللّٰهُ خَيْراًۜ اَللّٰهُ اَعْلَمُ بِمَا ف۪ٓي اَنْفُسِهِمْۚ اِنّ۪ٓي اِذاً لَمِنَ الظَّالِم۪ينَ", translation: "\"Ben size, \"Allah'ın hazineleri benim yanımdadır.\" demiyorum. Ben gaybı da bilmem. Yine \"Ben meleğim.\" de demiyorum. Hor gördüğünüz kimseler için, \"Allah, onlara hiçbir hayır vermeyecektir.\" de demiyorum. İçlerinde olanı Allah daha iyi bilir. Böyle bir şey yaptığım takdirde zalimlerden olurum.\"", transliteration: "Ve la ekulu lekum indi hazainullahi ve la a'lemul gaybe ve la ekulu inni melekun ve la ekulu lillezine tezderi a'yunukum len yu'tiyehumullahu hayra, allahu a'lemu bima fi enfusihim, inni izen le minez zalimin."},
  {id: 1505, sure_no: 32, ayet_no: 32, sure_adi: "Hud", text: "قَالُوا يَا نُوحُ قَدْ جَادَلْتَنَا فَاَكْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَٓا اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "Dediler ki: \"Ey Nuh! Bizimle mücadele ettin. Üstelik bu mücadelede çok ileri gittin. Eğer doğru söyleyenlerden isen, bizi tehdit ettiğin şeyi getir de görelim!\"", transliteration: "Kalu ya nuhu kad cadeltena fe ekserte cidalena fe'tina bi ma teiduna in kunte mines sadikin."},
  {id: 1506, sure_no: 33, ayet_no: 33, sure_adi: "Hud", text: "قَالَ اِنَّمَا يَأْت۪يكُمْ بِهِ اللّٰهُ اِنْ شَٓاءَ وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ", translation: "Dedi ki: \"Onu size dilediği takdirde ancak Allah getirir. Ve siz, O'nu asla aciz bırakamazsınız.\"", transliteration: "Kale innema ye'tikum bihillahu in şae ve ma entum bi mu'cizin."},
  {id: 1507, sure_no: 34, ayet_no: 34, sure_adi: "Hud", text: "وَلَا يَنْفَعُكُمْ نُصْح۪ٓي اِنْ اَرَدْتُ اَنْ اَنْصَحَ لَكُمْ اِنْ كَانَ اللّٰهُ يُر۪يدُ اَنْ يُغْوِيَكُمْۜ هُوَ رَبُّكُمْ وَاِلَيْهِ تُرْجَعُونَۜ", translation: "\"Eğer Allah sizi azdırmayı dilerse, size öğüt versem de öğüdüm yarar sağlamaz. Rabb'iniz O'dur, O'na döndürüleceksiniz.\"", transliteration: "Ve la yenfeukum nushi in eredtu en ensaha lekum in kanallahu yuridu en yugviyekum, huve rabbukum ve ileyhi turceun."},
  {id: 1508, sure_no: 35, ayet_no: 35, sure_adi: "Hud", text: "اَمْ يَقُولُونَ افْتَرٰيهُۜ قُلْ اِنِ افْتَرَيْتُهُ فَعَلَيَّ اِجْرَام۪ي وَاَنَا۬ بَر۪ٓيءٌ مِمَّا تُجْرِمُونَ۟", translation: "Yoksa \"Onu kendisi uydurdu\" mu diyorlar? De ki: \"Eğer uydurduysam, suçu bana aittir. Ben de sizin işlediğiniz suçlardan uzağım.\"", transliteration: "Em yekulunefterah, kul iniftereytuhu fe aleyye icrami ve ene beriun mimma tucrimun."},
  {id: 1509, sure_no: 36, ayet_no: 36, sure_adi: "Hud", text: "وَاُو۫حِيَ اِلٰى نُوحٍ اَنَّهُ لَنْ يُؤْمِنَ مِنْ قَوْمِكَ اِلَّا مَنْ قَدْ اٰمَنَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَفْعَلُونَۚ", translation: "Nuh'a vahyedildi: \"Şu ana kadar iman etmiş olanların dışında, senin halkından kesinlikle kimse inanmayacak. Onların yaptıklarından dolayı tasalanma;\"", transliteration: "Ve uhiye ila nuhın ennehu len yu'mine min kavmike illa men kad amene fe la tebteis bi ma kanu yef'alun."},
  {id: 1510, sure_no: 37, ayet_no: 37, sure_adi: "Hud", text: "وَاصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْن۪ي فِي الَّذ۪ينَ ظَلَمُواۚ اِنَّهُمْ مُغْرَقُونَ", translation: "\"Bizim gözetimimiz altında ve vahyimizle gemiyi yap. Zalimler hakkında benden bir istekte bulunma. Onlar boğulacaklardır.\"", transliteration: "Vasnaıl fulke bi a'yunina ve vahyina ve la tuhatıbni fillezine zalemu, innehum mugrekun."},
  {id: 1511, sure_no: 38, ayet_no: 38, sure_adi: "Hud", text: "وَيَصْنَعُ الْفُلْكَ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَاٌ مِنْ قَوْمِه۪ سَخِرُوا مِنْهُۜ قَالَ اِنْ تَسْخَرُوا مِنَّا فَاِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُونَۜ", translation: "Gemiyi yapıyordu. Halkının meleleri, onun yanından ne zaman geçseler, onunla alay ediyorlardı. Dedi ki: \"Alay edin bakalım! Görürsünüz, sizin bizimle alay ettiğiniz gibi biz de sizinle alay edeceğiz.\"", transliteration: "Ve yasneul fulke ve kullema merre aleyhi meleun min kavmihi sehıru minh, kale in tesharu minna fe inna nesharu minkum kema tesharun."},
  {id: 1512, sure_no: 39, ayet_no: 39, sure_adi: "Hud", text: "فَسَوْفَ تَعْلَمُونَۙ مَنْ يَأْت۪يهِ عَذَابٌ يُخْز۪يهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُق۪يمٌ", translation: "\"Alçaltıcı azabın kime geleceğini ve kalıcı azaba kimin uğrayacağını yakında bileceksiniz.\"", transliteration: "Fe sevfe ta'lemune men ye'tihi azabun yuhzihi ve yehıllu aleyhi azabun mukim."},
  {id: 1513, sure_no: 40, ayet_no: 40, sure_adi: "Hud", text: "حَتّٰٓى اِذَا جَٓاءَ اَمْرُنَا وَفَارَ التَّنُّورُۙ قُلْنَا احْمِلْ ف۪يهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ اٰمَنَۜ وَمَٓا اٰمَنَ مَعَهُٓ اِلَّا قَل۪يلٌ", translation: "Buyruğumuz gelip tandır kaynamaya başladığı zaman, Biz dedik ki: \"Her cinsten birer çift ve aleyhlerinde hüküm verilmiş olanlar hariç aileni ve İman Edenleri ona yükle.\" Zaten onunla birlikte çok az kimse iman etmişti.", transliteration: "Hatta iza cae emruna ve faret tennuru kulnahmil fiha min kullin zevceynisneyni ve ehleke illa men sebeka aleyhil kavlu ve men amen, ve ma amene meahu illa kalil."},
  {id: 1514, sure_no: 41, ayet_no: 41, sure_adi: "Hud", text: "وَقَالَ ارْكَبُوا ف۪يهَا بِسْمِ اللّٰهِ مَجْرٰۭۙيهَا وَمُرْسٰيهَاۜ اِنَّ رَبّ۪ي لَغَفُورٌ رَح۪يمٌ", translation: "\"Haydi, binin.\" dedi. \"Onun gitmesi de durması da Allah'ın adıyladır. Elbette Rabb'im Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.\"", transliteration: "Ve kalerkebu fiha bismillahi mecraha ve mursaha, inne rabbi le gafurun rahim."},
  {id: 1515, sure_no: 42, ayet_no: 42, sure_adi: "Hud", text: "وَهِيَ تَجْر۪ي بِهِمْ ف۪ي مَوْجٍ كَالْجِبَالِ وَنَادٰى نُوحٌۨ ابْنَهُ وَكَانَ ف۪ي مَعْزِلٍ يَا بُنَيَّ ارْكَبْۭۗ مَعَنَا وَلَا تَكُنْ مَعَ الْكَافِر۪ينَ", translation: "Gemi dağlar gibi dalgalar arasında akıp gidiyordu. Nuh, bir kenarda bekleyen oğluna:\" Yavrum bizimle beraber sen de bin; Kafirlerle beraber kalma.\" diye seslendi.", transliteration: "Ve hiye tecri bihim fi mevcin kel cibali ve nada nuhunibnehu ve kane fi ma'zilin ya buneyyerkeb meana ve la tekun meal kafirin."},
  {id: 1516, sure_no: 43, ayet_no: 43, sure_adi: "Hud", text: "قَالَ سَاٰو۪ٓي اِلٰى جَبَلٍ يَعْصِمُن۪ي مِنَ الْمَٓاءِۜ قَالَ لَا عَاصِمَ الْيَوْمَ مِنْ اَمْرِ اللّٰهِ اِلَّا مَنْ رَحِمَۚ وَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَق۪ينَ", translation: "O: \"Dağa sığınırım, beni sudan kurtarır.\" deyince; \"Bugün Allah'ın rahmetine erişenden başkasını, Allah'ın emrinden koruyacak hiçbir şey yoktur.\" dedi. Aralarına dalga girdi ve o da boğulanlardan oldu.", transliteration: "Kale seavi ila cebelin ya'sımuni minel ma' kale la asımel yevme min emrillahi illa men rahim, ve hale beynehumal mevcu fe kane minel mugrakin."},
  {id: 1517, sure_no: 44, ayet_no: 44, sure_adi: "Hud", text: "وَق۪يلَ يَٓا اَرْضُ ابْلَع۪ي مَٓاءَكِ وَيَا سَمَٓاءُ اَقْلِع۪ي وَغ۪يضَ الْمَٓاءُ وَقُضِيَ الْاَمْرُ وَاسْتَوَتْ عَلَى الْجُودِيِّ وَق۪يلَ بُعْداً لِلْقَوْمِ الظَّالِم۪ينَ", translation: "\"Ey yer, suyunu çek ve ey gök, suyunu kes.\" denildi. Su çekildi, iş bitirildi. Gemi Cudi'ye oturdu. \"Zalimler topluluğu Allah'ın rahmetinden uzak olsun.\" denildi.", transliteration: "Ve kile ya ardublei maeki ve ya semau aklii ve gidal mau ve kudıyel emru vestevet alal cudiyyi ve kile bu'den lil kavmiz zalimin."},
  {id: 1518, sure_no: 45, ayet_no: 45, sure_adi: "Hud", text: "وَنَادٰى نُوحٌ رَبَّهُ فَقَالَ رَبِّ اِنَّ ابْن۪ي مِنْ اَهْل۪ي وَاِنَّ وَعْدَكَ الْحَقُّ وَاَنْتَ اَحْكَمُ الْحَاكِم۪ينَ", translation: "Nuh, Rabb'ine seslendi: \"Ey Rabb'im! Oğlum benim ehlimdendir. Senin uyarın elbette gerçektir. Sen, hakimlerin hakimisin.\" dedi.", transliteration: "Ve nada nuhun rabbehu fe kale rabbi innebni min ehli ve inne va'dekel hakku ve ente ahkemul hakimin."},
  {id: 1519, sure_no: 46, ayet_no: 46, sure_adi: "Hud", text: "قَالَ يَا نُوحُ اِنَّهُ لَيْسَ مِنْ اَهْلِكَۚ اِنَّهُ عَمَلٌ غَيْرُ صَالِحٍۗ فَلَا تَسْـَٔلْنِ مَا لَيْسَ لَكَ بِه۪ عِلْمٌۜ اِنّ۪ٓي اَعِظُكَ اَنْ تَكُونَ مِنَ الْجَاهِل۪ينَ", translation: "Dedi ki: \"Ey Nuh! O senin ehlinden değildir. Zira onun yaptığı doğru olmayan bir işti. Öyleyse hakkında bilgin olmayan şeyi Benden isteme. Cahillerden olmaktan seni sakındırıyorum.\"", transliteration: "Kale ya nuhu innehu leyse min ehlik, innehu amelun gayru salih, fe la tes'elni ma leyse leke bihi ilm, inni eızuke en tekune minel cahilin."},
  {id: 1520, sure_no: 47, ayet_no: 47, sure_adi: "Hud", text: "قَالَ رَبِّ اِنّ۪ٓي اَعُوذُ بِكَ اَنْ اَسْـَٔلَكَ مَا لَيْسَ ل۪ي بِه۪ عِلْمٌۜ وَاِلَّا تَغْفِرْ ل۪ي وَتَرْحَمْن۪ٓي اَكُنْ مِنَ الْخَاسِر۪ينَ", translation: "\"Rabb'im! Bilmediğim bir şeyi Sen'den istemekten Sana sığınırım. Eğer beni bağışlamaz ve merhamet etmezsen hüsrana uğrayanlardan olurum.\" dedi.", transliteration: "Kale rabbi inni euzu bike en es'eleke ma leyse li bihi ilm, ve illa tagfirli ve terhamni ekun minel hasirin."},
  {id: 1521, sure_no: 48, ayet_no: 48, sure_adi: "Hud", text: "ق۪يلَ يَا نُوحُ اهْبِطْ بِسَلَامٍ مِنَّا وَبَرَكَاتٍ عَلَيْكَ وَعَلٰٓى اُمَمٍ مِمَّنْ مَعَكَۜ وَاُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِنَّا عَذَابٌ اَل۪يمٌ", translation: "Dendi ki: \"Ey Nuh! Katımızdan esenlikle in. Sana ve sana tabi olan topluluklara bereketler olsun. Ama öyle toplumlar da olacak ki, onları dünya nimetlerinden yararlandıracağız, sonra onlara Bizden can yakıcı bir azap dokunacaktır.\"", transliteration: "Kile ya nuhuhbıt bi selamin minna ve berekatin aleyke ve ala umemin mimmen meak, ve umemun se numettiuhum summe yemessuhum minna azabun elim."},
  {id: 1522, sure_no: 49, ayet_no: 49, sure_adi: "Hud", text: "تِلْكَ مِنْ اَنْـبَٓاءِ الْغَيْبِ نُوح۪يهَٓا اِلَيْكَۚ مَا كُنْتَ تَعْلَمُهَٓا اَنْتَ وَلَا قَوْمُكَ مِنْ قَبْلِ هٰذَاۜۛ فَاصْبِرْۜۛ اِنَّ الْعَاقِبَةَ لِلْمُتَّق۪ينَ۟", translation: "İşte bunlar, sana vahyettiğimiz gayb haberlerindendir. Bunları ne sen ne de halkın daha önce bilmiyordunuz. O halde sabret. Sonuç, takva sahiplerinindir.", transliteration: "Tilke min enbail gaybi nuhiha ileyk, ma kunte ta'lemuha ente ve la kavmuke min kabli haza, fasbır, innel akıbete lil muttekin."},
  {id: 1523, sure_no: 50, ayet_no: 50, sure_adi: "Hud", text: "وَاِلٰى عَادٍ اَخَاهُمْ هُوداًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ اِنْ اَنْتُمْ اِلَّا مُفْتَرُونَ", translation: "Ad halkına kardeşleri Hud'u gönderdik. Dedi ki: \"Ey halkım! Yalnızca Allah'a kulluk edin. Sizin ondan başka bir ilahınız yoktur; siz bu tutumunuzla iftira etmekten başka bir şey yapmıyorsunuz.\"", transliteration: "Ve ila adin ehahum huda, kale ya kavmi'budullahe ma lekum min ilahin gayruh, in entum illa mufterun."},
  {id: 1524, sure_no: 51, ayet_no: 51, sure_adi: "Hud", text: "يَا قَوْمِ لَٓا اَسْـَٔلُكُمْ عَلَيْهِ اَجْراًۜ اِنْ اَجْرِيَ اِلَّا عَلَى الَّذ۪ي فَطَرَن۪يۜ اَفَلَا تَعْقِلُونَ", translation: "\"Ey halkım! Bunun için sizden bir ücret istemiyorum. Benim ücretim, yalnızca bana bu benliği verene aittir. Aklınızı kullanmayacak mısınız?\"", transliteration: "Ya kavmi la es'elukum aleyhi ecra, in ecriye illa alellezi fetarani, e fe la ta'kılun."},
  {id: 1525, sure_no: 52, ayet_no: 52, sure_adi: "Hud", text: "وَيَا قَوْمِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُٓوا اِلَيْهِ يُرْسِلِ السَّمَٓاءَ عَلَيْكُمْ مِدْرَاراً وَيَزِدْكُمْ قُوَّةً اِلٰى قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِم۪ينَ", translation: "\"Ey halkım! Rabb'inizden bağışlanma dileyin. Sonra O'na tevbe edin, gökten üzerinize bol bol rahmet göndersin, gücünüze güç katsın. Mücrimler olarak yüz çevirmeyin.\"", transliteration: "Ve ya kavmistagfiru rabbekum summe tubu ileyhi yursilis semae aleykum midraran ve yezidkum kuvveten ila kuvvetikum ve la tetevellev mucrimin."},
  {id: 1526, sure_no: 53, ayet_no: 53, sure_adi: "Hud", text: "قَالُوا يَا هُودُ مَا جِئْتَنَا بِبَيِّنَةٍ وَمَا نَحْنُ بِتَارِك۪ٓي اٰلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِن۪ينَ", translation: "Dediler ki: \"Ey Hud! Bize kanıt içeren bir bilgi getirmedin; biz, senin sözünle ilahlarımızı terk edecek değiliz. Ve biz sana inanacak da değiliz.\"", transliteration: "Kalu ya hudu ma ci'tena bibeyyinetin ve ma nahnu bi tariki alihetina an kavlike ve ma nahnu leke bi muminin."},
  {id: 1527, sure_no: 54, ayet_no: 54, sure_adi: "Hud", text: "اِنْ نَقُولُ اِلَّا اعْتَرٰيكَ بَعْضُ اٰلِهَتِنَا بِسُٓوءٍۜ قَالَ اِنّ۪ٓي اُشْهِدُ اللّٰهَ وَاشْهَدُٓوا اَنّ۪ي بَر۪ٓيءٌ مِمَّا تُشْرِكُونَۙ", translation: "\"İlahlarımızdan biri seni fena çarpmış, demekten başka bir sözümüz yok.\" dediler. Hud da: \"Ben Allah'ı tanık tutuyorum. Siz de tanık olun ki ben Müşriklerden değilim.\" dedi.", transliteration: "İn nekulu illa'terake ba'du alihetina bi su', kale inni uşhidullahe veşhedu enni beriun mimma tuşrikune."},
  {id: 1528, sure_no: 55, ayet_no: 55, sure_adi: "Hud", text: "مِنْ دُونِه۪ فَك۪يدُون۪ي جَم۪يعاً ثُمَّ لَا تُنْظِرُونِ", translation: "\"Haydi, hiç zaman kaybetmeden bana istediğiniz tuzağı kurun.\"", transliteration: "Min dunihi fe kiduni cemian summe la tunzırun."},
  {id: 1529, sure_no: 56, ayet_no: 56, sure_adi: "Hud", text: "اِنّ۪ي تَوَكَّلْتُ عَلَى اللّٰهِ رَبّ۪ي وَرَبِّكُمْۜ مَا مِنْ دَٓابَّةٍ اِلَّا هُوَ اٰخِذٌ بِنَاصِيَتِهَاۜ اِنَّ رَبّ۪ي عَلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "\"Ben, benim de Rabb'im sizin de Rabb'iniz olan Allah'a tevekkül ettim. Hiçbir canlı yoktur ki O'nun buyruğunda/kontrolünde olmasın. Kuşkusuz Rabb'im dosdoğru bir yol üzerindedir.\"", transliteration: "İnni tevekkeltu alallahi rabbi ve rabbikum, ma min dabbetin illa huve ahızun bi nasıyetiha, inne rabbi ala sıratın mustekim."},
  {id: 1530, sure_no: 57, ayet_no: 57, sure_adi: "Hud", text: "فَاِنْ تَوَلَّوْا فَقَدْ اَبْلَغْتُكُمْ مَٓا اُرْسِلْتُ بِه۪ٓ اِلَيْكُمْۜ وَيَسْتَخْلِفُ رَبّ۪ي قَوْماً غَيْرَكُمْۚ وَلَا تَضُرُّونَهُ شَيْـٔاًۜ اِنَّ رَبّ۪ي عَلٰى كُلِّ شَيْءٍ حَف۪يظٌ", translation: "\"Eğer yüz çevirirseniz, bilin ki size iletmek üzere gönderildiğim mesajı size ilettim. Rabb'im, yerinize sizden başka bir halk getirebilir ve siz O'na hiçbir şekilde engel olamazsınız. Rabb'im, her şeyi koruyup gözetendir.\"", transliteration: "Fe in tevellev fe kad eblagtukum ma ursiltu bihi ileykum, ve yestahlifu rabbi kavmen gayrekum, ve la tedurrunehu şey'a, inne rabbi ala kulli şey'in hafiz."},
  {id: 1531, sure_no: 58, ayet_no: 58, sure_adi: "Hud", text: "وَلَمَّا جَٓاءَ اَمْرُنَا نَجَّيْنَا هُوداً وَالَّذ۪ينَ اٰمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّاۚ وَنَجَّيْنَاهُمْ مِنْ عَذَابٍ غَل۪يظٍ", translation: "Buyruğumuz gerçekleşince, Hud'u ve beraberindeki İman Edenleri Bizden bir rahmetle kurtardık. Onları şiddeti çok ağır bir azaptan kurtardık.", transliteration: "Ve lemma cae emruna necceyna huden vellezine amenu meahu bi rahmetin minna, ve necceynahum min azabin galiz."},
  {id: 1532, sure_no: 59, ayet_no: 59, sure_adi: "Hud", text: "وَتِلْكَ عَادٌ جَحَدُوا بِاٰيَاتِ رَبِّهِمْ وَعَصَوْا رُسُلَهُ وَاتَّبَعُٓوا اَمْرَ كُلِّ جَبَّارٍ عَن۪يدٍ", translation: "İşte Ad halkı Rabb'lerinin ayetlerini inkar ettiler ve Resullerine isyan ettiler. İnatçı zorbanın her buyruğuna uydular.", transliteration: "Ve tilke adun cehadu bi ayati rabbihim ve asav rusulehu vettebeu emre kulli cebbarin anid."},
  {id: 1533, sure_no: 60, ayet_no: 60, sure_adi: "Hud", text: "وَاُتْبِعُوا ف۪ي هٰذِهِ الدُّنْيَا لَعْنَةً وَيَوْمَ الْقِيٰمَةِۜ  اَلَٓا اِنَّ عَاداً كَفَرُوا رَبَّهُمْۜ اَلَا بُعْداً لِعَادٍ قَوْمِ هُودٍ۟", translation: "Bu dünyada da kıyamet gününde de lanete tabi tutuldular. İyi bilin ki gerçekten Ad halkı Rabb'lerine nankörlük ettiler. İşte böyle yok olup gitti Hud halkı Ad.", transliteration: "Ve utbiu fi hazihid dunya la'neten ve yevmel kıyameh, e la inne aden keferu rabbehum, e la bu'den li adin kavmi hud."},
  {id: 1534, sure_no: 61, ayet_no: 61, sure_adi: "Hud", text: "وَاِلٰى ثَمُودَ اَخَاهُمْ صَالِحاًۢ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ هُوَ اَنْشَاَكُمْ مِنَ الْاَرْضِ وَاسْتَعْمَرَكُمْ ف۪يهَا فَاسْتَغْفِرُوهُ ثُمَّ تُوبُٓوا اِلَيْهِۜ اِنَّ رَبّ۪ي قَر۪يبٌ مُج۪يبٌ", translation: "Semud halkına da kardeşleri Salih'i gönderdik. Ey halkım: \"Allah'a kulluk edin. Sizin O'ndan başka ilahınız yoktur.\" dedi. \"Sizi yer yüzünde meydana getiren ve sizi orayı imar etmekle görevli kılan O'dur. Öyleyse O'ndan bağışlanma dileyin. Sonra O'na tevbe ile yönelin. Rabb'im, Çok Yakın'dır, İsteklere Cevap Veren'dir.\"", transliteration: "Ve ila semude ehahum saliha, kale ya kavmi'budullahe ma lekum min ilahin gayruh, huve enşeekum minel ardı vesta'merekum fiha festagfiruhu summe tubu ileyh, inne rabbi karibun mucib."},
  {id: 1535, sure_no: 62, ayet_no: 62, sure_adi: "Hud", text: "قَالُوا يَا صَالِحُ قَدْ كُنْتَ ف۪ينَا مَرْجُواًّ قَبْلَ هٰذَٓا اَتَنْهٰينَٓا اَنْ نَعْبُدَ مَا يَعْبُدُ اٰبَٓاؤُ۬نَا وَاِنَّنَا لَف۪ي شَكٍّ مِمَّا تَدْعُونَٓا اِلَيْهِ مُر۪يبٍ", translation: "\"Ey Salih! Sen, bundan önce aramızda ümit beslenen biriydin. Şimdi atalarımızın kulluk ettiklerine kulluk etmekten bizi vazgeçirmek mi istiyorsun? Ve biz, gerçekten de senin bizi çağırdığın şey hakkında ikilem içindeyiz, kaygılanıyoruz.\" dediler.", transliteration: "Kalu ya salihu kad kunte fina mercuvven kable haza e tenhana en na'bude ma ya'budu abauna ve innena le fi şekkin mimma ted'una ileyhi murib."},
  {id: 1536, sure_no: 63, ayet_no: 63, sure_adi: "Hud", text: "قَالَ يَا قَوْمِ اَرَاَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَاٰتٰين۪ي مِنْهُ رَحْمَةً فَمَنْ يَنْصُرُن۪ي مِنَ اللّٰهِ اِنْ عَصَيْتُهُ فَمَا تَز۪يدُونَن۪ي غَيْرَ تَخْس۪يرٍ", translation: "\"Ey halkım, söyleyin bakalım, ya Rabb'imin Kendinden verdiği bir rahmetle, kanıt içeren açık bir bilgi üzerindeysem! Ona asi olduğum takdirde, Allah'ın vereceği cezaya karşı bana kim yardım edebilir? Bana zarar vermiş olmaktan başka bir şey yapmış olmazsınız.\" dedi.", transliteration: "Kale ya kavmi e reeytum in kuntu ala beyyinetin min rabbi ve atani minhu rahmeten fe men yansuruni minallahi in asaytuhu fe ma teziduneni gayre tahsir."},
  {id: 1537, sure_no: 64, ayet_no: 64, sure_adi: "Hud", text: "وَيَا قَوْمِ هٰذِه۪ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوهَا تَأْكُلْ ف۪ٓي اَرْضِ اللّٰهِ وَلَا تَمَسُّوهَا بِسُٓوءٍ فَيَأْخُذَكُمْ عَذَابٌ قَر۪يبٌ", translation: "\"Ey halkım! İşte şu Allah'ın dişi devesi, sizin için bir ayettir. Bırakın onu, Allah'ın arzında otlasın. Kötü bir amaçla ona yaklaşmayın. Yoksa sizi yakın bir azap yakalar.\"", transliteration: "Ve ya kavmi hazihi nakatullahi lekum ayeten fe zeruha te'kul fi ardıllahi ve la temessuha bi suin fe ye'huzekum azabun karib."},
  {id: 1538, sure_no: 65, ayet_no: 65, sure_adi: "Hud", text: "فَعَقَرُوهَا فَقَالَ تَمَتَّعُوا ف۪ي دَارِكُمْ ثَلٰثَةَ اَيَّامٍۜ ذٰلِكَ وَعْدٌ غَيْرُ مَكْذُوبٍ", translation: "Yine de onu kestiler. \"Yurdunuzda üç gün yaşayın. Bu yalan olmayan bir uyarıdır.\" dedi.", transliteration: "Fe akaruha fe kale temetteu fi darikum selasete eyyam, zalike va'dun gayru mekzub."},
  {id: 1539, sure_no: 66, ayet_no: 66, sure_adi: "Hud", text: "فَلَمَّا جَٓاءَ اَمْرُنَا نَجَّيْنَا صَالِحاً وَالَّذ۪ينَ اٰمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَمِنْ خِزْيِ يَوْمِئِذٍۜ اِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَز۪يزُ", translation: "Nihayet haklarındaki yargımız gerçekleşerek, Salih'i ve beraberinde iman etmiş olanları tarafımızdan bir rahmetle o günün zilletinden kurtardık. Rabb'in, Çok Güçlü'dür, Mutlak Üstün Olan'dır.", transliteration: "Fe lemma cae emruna necceyna salihan vellezine amenu meahu bi rahmetin minna ve min hizyi yevmi iz, inne rabbeke huvel kaviyyul aziz."},
  {id: 1540, sure_no: 67, ayet_no: 67, sure_adi: "Hud", text: "وَاَخَذَ الَّذ۪ينَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوا ف۪ي دِيَارِهِمْ جَاثِم۪ينَۙ", translation: "Zulmedenleri bir çığlık yakaladı. Yurtlarında dizüstü çökmüş olarak sabahladılar.", transliteration: "Ve ehazellezine zalemus sayhatu fe asbahu fi diyarihim casimin."},
  {id: 1541, sure_no: 68, ayet_no: 68, sure_adi: "Hud", text: "كَاَنْ لَمْ يَغْنَوْا ف۪يهَاۜ اَلَٓا اِنَّ ثَمُودَا۬ كَفَرُوا رَبَّهُمْۜ اَلَا بُعْداً لِثَمُودَ۟", translation: "Sanki orada hiç yaşamamışlardı. İyi bilin ki Semud halkı Rabb'lerine nankörlük ettiler. İyi bilin ki Semud halkı yok olup gitti.", transliteration: "Ke en lem yagnev fiha, e la inne semude keferu rabbehum, e la bu'den li semud."},
  {id: 1542, sure_no: 69, ayet_no: 69, sure_adi: "Hud", text: "وَلَقَدْ جَٓاءَتْ رُسُلُنَٓا اِبْرٰه۪يمَ بِالْبُشْرٰى قَالُوا سَلَاماًۜ قَالَ سَلَامٌۚ فَمَا لَبِثَ اَنْ جَٓاءَ بِعِجْلٍ حَن۪يذٍ", translation: "Ant olsun elçilerimiz İbrahim'e haber vermek için gelip, \"Selam!\" dediler. \"Selam!\" dedi ve hemen gidip kızarmış buzağı eti getirdi.", transliteration: "Ve lekad caet rusuluna ibrahime bil buşra kalu selama, kale selamun fe ma lebise en cae bi iclin haniz."},
  {id: 1543, sure_no: 70, ayet_no: 70, sure_adi: "Hud", text: "فَلَمَّا رَآٰ اَيْدِيَهُمْ لَا تَصِلُ اِلَيْهِ نَكِرَهُمْ وَاَوْجَسَ مِنْهُمْ خ۪يفَةًۜ قَالُوا لَا تَخَفْ اِنَّٓا اُرْسِلْـنَٓا اِلٰى قَوْمِ لُوطٍۜ", translation: "Ona ellerini uzatmadıklarını görünce, onlardan kuşkulanıp korkuya kapıldı. \"Korkma! Biz Lut halkına gönderildik.\" dediler.", transliteration: "Fe lemma rea eydiyehum la tesilu ileyhi nekirehum ve evcese minhum hifeh, kalu la tehaf inna ursilna ila kavmi lut."},
  {id: 1544, sure_no: 71, ayet_no: 71, sure_adi: "Hud", text: "وَامْرَاَتُهُ قَٓائِمَةٌ فَضَحِكَتْ فَبَشَّرْنَاهَا بِاِسْحٰقَۙ وَمِنْ وَرَٓاءِ اِسْحٰقَ يَعْقُوبَ", translation: "Ayakta duran hanımı bunun üzerine gülümsedi. Biz de ona İshak'ı müjdeledik ve İshak'ın arkasından da Ya'kub'u.", transliteration: "Vemreetuhu kaimetun fe dahıket fe beşşernaha bi ishaka ve min verai ishaka ya'kub."},
  {id: 1545, sure_no: 72, ayet_no: 72, sure_adi: "Hud", text: "قَالَتْ يَا وَيْلَتٰٓى ءَاَلِدُ وَاَنَا۬ عَجُوزٌ وَهٰذَا بَعْل۪ي شَيْخاًۜ اِنَّ هٰذَا لَشَيْءٌ عَج۪يبٌ", translation: "\"Vay başıma gelene! Ben bir koca karı, kocam da bir ihtiyar olduğumuz halde, doğuracak mıyım? Doğrusu bu çok hayret edilecek bir şey!\" dedi.", transliteration: "Kalet ya veyleta e elidu ve ene ecuzun ve haza ba'li şeyha, inne haza le şey'un acib."},
  {id: 1546, sure_no: 73, ayet_no: 73, sure_adi: "Hud", text: "قَالُٓوا اَتَعْجَب۪ينَ مِنْ اَمْرِ اللّٰهِ رَحْمَتُ اللّٰهِ وَبَرَكَاتُهُ عَلَيْكُمْ اَهْلَ الْبَيْتِۜ اِنَّهُ حَم۪يدٌ مَج۪يدٌ", translation: "\"Allah'ın takdirine hayret mi ediyorsun!\" dediler. \"Allah'ın rahmeti ve bereketleri sizin üzerinizedir ey ev halkı. O, Övgüye Değer Yegane Varlık'tır, İyiliği Bol Olan'dır.\"", transliteration: "Kalu e ta'cebine min emrillahi rahmetullahi ve berekatuhu aleykum ehlel beyt, innehu hamidun mecid."},
  {id: 1547, sure_no: 74, ayet_no: 74, sure_adi: "Hud", text: "فَلَمَّا ذَهَبَ عَنْ اِبْرٰه۪يمَ الرَّوْعُ وَجَٓاءَتْهُ الْبُشْرٰى يُجَادِلُنَا ف۪ي قَوْمِ لُوطٍۜ", translation: "İbrahim'in korkusu geçip, bu müjde kendisine verilince, Lut'un halkı hakkında bizimle tartışmaya başladı.", transliteration: "Fe lemma zehebe an ibrahimer rev'u ve caethul buşra yucadiluna fi kavmi lut."},
  {id: 1548, sure_no: 75, ayet_no: 75, sure_adi: "Hud", text: "اِنَّ اِبْرٰه۪يمَ لَحَل۪يمٌ اَوَّاهٌ مُن۪يبٌ", translation: "Çünkü İbrahim, çok yumuşak huylu, yufka yürekli ve Allah'a gönülden yönelen bir kimseydi.", transliteration: "İnne ibrahime le halimun evvahun munib."},
  {id: 1549, sure_no: 76, ayet_no: 76, sure_adi: "Hud", text: "يَٓا اِبْرٰه۪يمُ اَعْرِضْ عَنْ هٰذَاۚ اِنَّهُ قَدْ جَٓاءَ اَمْرُ رَبِّكَۚ وَاِنَّهُمْ اٰت۪يهِمْ عَذَابٌ غَيْرُ مَرْدُودٍ", translation: "\"Ey İbrahim! Bundan vazgeç. Çünkü Rabb'inin takdiri kesindir. Onlara gelecek azap engellenemez.\"", transliteration: "Ya ibrahimu a'rid an haza, innehu kad cae emru rabbik, ve innehum atihim azabun gayru merdud."},
  {id: 1550, sure_no: 77, ayet_no: 77, sure_adi: "Hud", text: "وَلَمَّا جَٓاءَتْ رُسُلُنَا لُوطاً س۪ٓيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعاً وَقَالَ هٰذَا يَوْمٌ عَص۪يبٌ", translation: "Elçilerimiz Lut'a vardıklarında, onlar yüzünden fenalaştı. Onlara karşı eli ayağı birbirine dolaştı. \"Bu çok zorlu bir gün.\" dedi.", transliteration: "Ve lemma caet resuluna lutan sie bihim ve daka bihim zer'an ve kale haza yevmun asib."},
  {id: 1551, sure_no: 78, ayet_no: 78, sure_adi: "Hud", text: "وَجَٓاءَهُ قَوْمُهُ يُهْرَعُونَ اِلَيْهِ وَمِنْ قَبْلُ كَانُوا يَعْمَلُونَ السَّيِّـَٔاتِۜ قَالَ يَا قَوْمِ هٰٓؤُ۬لَٓاءِ بَنَات۪ي هُنَّ اَطْهَرُ لَكُمْ فَاتَّقُوا اللّٰهَ وَلَا تُخْزُونِ ف۪ي ضَيْف۪يۜ اَلَيْسَ مِنْكُمْ رَجُلٌ رَش۪يدٌ", translation: "Halkı koşa koşa ona geldiler. Onlar, önceden de kötü işler yapıyorlardı. \"Ey halkım! İşte şunlar kızlarımdır, onlar sizin için temiz olandır. Allah için takvalı olun, misafirlerime karşı beni rezil etmeyin, içinizde hiç aklı başında kimse yok mu?\" dedi.", transliteration: "Ve caehu kavmuhu yuhreune ileyhi ve min kablu kanu ya'melunes seyyiat, kale ya kavmi haulai benati hunne etharu lekum, fettekullahe ve la tuhzuni fi dayfi, e leyse minkum raculun reşid."},
  {id: 1552, sure_no: 79, ayet_no: 79, sure_adi: "Hud", text: "قَالُوا لَقَدْ عَلِمْتَ مَا لَنَا ف۪ي بَنَاتِكَ مِنْ حَقٍّۚ وَاِنَّكَ لَتَعْلَمُ مَا نُر۪يدُ", translation: "\"Senin kızlarında bizim hakkımızın olmadığını biliyorsun. Ant olsun ki ne istediğimizi çok iyi biliyorsun.\" dediler.", transliteration: "Kalu lekad alimte ma lena fi benatike min hakk, ve inneke le ta'lemu ma nurid."},
  {id: 1553, sure_no: 80, ayet_no: 80, sure_adi: "Hud", text: "قَالَ لَوْ اَنَّ ل۪ي بِكُمْ قُوَّةً اَوْ اٰو۪ٓي اِلٰى رُكْنٍ شَد۪يدٍ", translation: "\"Keşke size karşı bir gücüm olsaydı veya sağlam bir kaleye sığınabilseydim.\" dedi.", transliteration: "Kale lev enne li bikum kuvveten ev avi ila ruknin şedid."},
  {id: 1554, sure_no: 81, ayet_no: 81, sure_adi: "Hud", text: "قَالُوا يَا لُوطُ اِنَّا رُسُلُ رَبِّكَ لَنْ يَصِلُٓوا اِلَيْكَ فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِنَ الَّيْلِ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ اِلَّا امْرَاَتَكَۜ اِنَّهُ مُص۪يبُهَا مَٓا اَصَابَهُمْۜ اِنَّ مَوْعِدَهُمُ الصُّبْحُۜ اَلَيْسَ الصُّبْحُ بِقَر۪يبٍ", translation: "\"Ey Lut! Biz, Rabb'inin elçileriyiz.\" dediler. \"Onlar, sana dokunamazlar. Ailenle birlikte gecenin bir bölümünde hemen yola çık. Hanımın hariç, hiç kimse arkada kalmasın. Doğrusu onların başına gelecek olan musibet onun da başına gelecektir. Onlara belirlenen vakit sabahtır. Sabah da yakın değil mi?\"", transliteration: "Kalu ya lutu inna rusulu rabbike len yasilu ileyke fe esri bi ehlike bi kıt'ın minel leyli ve la yeltefit minkum ehadun illemreetek, innehu musibuha ma esabehum, inne mev'ıdehumus subh, e leyses subhu bi karib."},
  {id: 1555, sure_no: 82, ayet_no: 82, sure_adi: "Hud", text: "فَلَمَّا جَٓاءَ اَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهَا حِجَارَةً مِنْ سِجّ۪يلٍۙ مَنْضُودٍۙ", translation: "Takdirimizle yaşadıkları yeri alt üst ettik. Üzerlerine pişmiş çamurdan yığınlarca taş yağdırdık.", transliteration: "Fe lemma cae emruna cealna aliyeha safileha ve emtarna aleyha hicareten min siccilin mendud."},
  {id: 1556, sure_no: 83, ayet_no: 83, sure_adi: "Hud", text: "مُسَوَّمَةً عِنْدَ رَبِّكَۜ وَمَا هِيَ مِنَ الظَّالِم۪ينَ بِبَع۪يدٍ۟", translation: "Bu taşlar, Rabb'inin katında işaretlenmiştir. Bunlar, zalimlerden uzak değildir.", transliteration: "Musevvemeten inde rabbik, ve ma hiye minez zalimine bi baid."},
  {id: 1557, sure_no: 84, ayet_no: 84, sure_adi: "Hud", text: "وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْباًۜ قَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ  وَلَا تَنْقُصُوا الْمِكْيَالَ وَالْم۪يزَانَ اِنّ۪ٓي اَرٰيكُمْ بِخَيْرٍ وَاِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُح۪يطٍ", translation: "Medyen halkına da kardeşleri Şu'ayb'ı gönderdik. \"Ey halkım! Allah'a kulluk edin. Sizin için O'ndan başka ilah yoktur. Ölçüyü ve tartıyı eksik yapmayın. Ben, sizi bir hayır içinde görüyorum. Ve sizin için kuşatıcı bir günün azabından korkuyorum.\" dedi.", transliteration: "Ve ila medyene ehahum şuayba, kale ya kavmi'budullahe ma lekum min ilahin gayruh, ve la tenkusul mikyale vel mizane inni erakum bi hayrin ve inni ehafu aleykum azabe yevmin muhit."},
  {id: 1558, sure_no: 85, ayet_no: 85, sure_adi: "Hud", text: "وَيَا قَوْمِ اَوْفُوا الْمِكْيَالَ وَالْم۪يزَانَ بِالْقِسْطِ وَلَا تَبْخَسُوا النَّاسَ اَشْيَٓاءَهُمْ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ", translation: "\"Ey kavmim! Ölçüyü ve tartıyı hakkaniyetle yapın. Kimsenin malını eksik vermeyin. Bozguncular olarak yeryüzünde fesat çıkarmayın.\"", transliteration: "Ve ya kavmi evful mikyale vel mizane bil kıstı ve la tebhasun nase eşyaehum ve la ta'sev fil ardı mufsidin."},
  {id: 1559, sure_no: 86, ayet_no: 86, sure_adi: "Hud", text: "بَقِيَّتُ اللّٰهِ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ وَمَٓا اَنَا۬ عَلَيْكُمْ بِحَف۪يظٍ", translation: "\"Eğer Mü'minler iseniz, Allah'ın bıraktığı sizin için hayırlı olandır. Yoksa ben, gözetleyiciniz değilim.\"", transliteration: "Bakıyyetullahi hayrun lekum in kuntum mu'minin, ve ma ene aleykum bi hafiz."},
  {id: 1560, sure_no: 87, ayet_no: 87, sure_adi: "Hud", text: "قَالُوا يَا شُعَيْبُ اَصَلٰوتُكَ تَأْمُرُكَ اَنْ نَتْرُكَ مَا يَعْبُدُ اٰبَٓاؤُ۬نَٓا اَوْ اَنْ نَفْعَلَ ف۪ٓي اَمْوَالِنَا مَا نَشٰٓؤُ۬اۜ اِنَّكَ لَاَنْتَ الْحَل۪يمُ الرَّش۪يدُ", translation: "Dediler ki: \"Ey Şu'ayb! Atalarımızın kulluk ettiklerini bırakmamızı; mallarımızı, istediğimiz gibi kullanmaktan vazgeçmemizi senin salatın mı buyuruyor? Oysaki sen yumuşak huylu, aklı başında bir adamsın.\"", transliteration: "Kalu ya şuaybu e salatuke te'muruke en netruke ma ya'budu abauna ev en nef'ale fi emvalina ma neşa', inneke le entel halimur reşid."},
  {id: 1561, sure_no: 88, ayet_no: 88, sure_adi: "Hud", text: "قَالَ يَا قَوْمِ اَرَاَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِنْ رَبّ۪ي وَرَزَقَن۪ي مِنْهُ رِزْقاً حَسَناًۜ وَمَٓا اُر۪يدُ اَنْ اُخَالِفَكُمْ اِلٰى مَٓا اَنْهٰيكُمْ عَنْهُۜ اِنْ اُر۪يدُ اِلَّا الْاِصْلَاحَ مَا اسْتَطَعْتُۜ وَمَا تَوْف۪يق۪ٓي اِلَّا بِاللّٰهِۜ  عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ اُن۪يبُ", translation: "\"Ey halkım! Bana söyleyin! \"Ya ben Rabb'imden kanıt içeren apaçık bir bilgiye sahipsem; kendinden bana iyi bir rızık vermişse! Vazgeçmenizi istediğim şeyleri, kendim yapmak istemiyorum. Sadece gücümün yettiği kadarıyla düzeltmek istiyorum. Başarım ancak Allah'tandır. Yalnız O'na tevekkül ettim ve yalnız O'na yöneldim.\" dedi.", transliteration: "Kale ya kavmi e reeytum in kuntu ala beyyinetin min rabbi ve rezekani minhu rızkan hasena, ve ma uridu en uhalifekum ila ma enhakum anh, in uridu illel ıslaha mesteta'tu, ve ma tevfiki illa billah, aleyhi tevekkeltu ve ileyhi unib."},
  {id: 1562, sure_no: 89, ayet_no: 89, sure_adi: "Hud", text: "وَيَا قَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاق۪ٓي اَنْ يُص۪يبَكُمْ مِثْلُ مَٓا اَصَابَ قَوْمَ نُوحٍ اَوْ قَوْمَ هُودٍ اَوْ قَوْمَ صَالِحٍۜ وَمَا قَوْمُ لُوطٍ مِنْكُمْ بِبَع۪يدٍ", translation: "\"Ey halkım! Bana karşı gelmeniz; Nuh halkının, Hud halkının, Salih halkının başına gelen şeyleri sizin başınıza da getirmesin? Üstelik Lut halkı sizden pek uzak da değil.\"", transliteration: "Ve ya kavmi la yecrimennekum şikaki en yusibekum mislu ma esabe kavme nuhin ev kavme hudin ev kavme salih, ve ma kavmu lutin minkum bi baid."},
  {id: 1563, sure_no: 90, ayet_no: 90, sure_adi: "Hud", text: "وَاسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُٓوا اِلَيْهِۜ اِنَّ رَبّ۪ي رَح۪يمٌ وَدُودٌ", translation: "\"Rabb'inizden bağışlanma dileyin. Sonra O'na tevbe edin! Rabb'imin, Rahmeti Kesintisiz'dir, Çok Seven'dir.\"", transliteration: "Vestagfiru rabbekum summe tubu ileyh, inne rabbi rahimun vedud."},
  {id: 1564, sure_no: 91, ayet_no: 91, sure_adi: "Hud", text: "قَالُوا يَا شُعَيْبُ مَا نَفْقَهُ۬ كَث۪يراً مِمَّا تَقُولُ وَاِنَّا لَنَرٰيكَ ف۪ينَا ضَع۪يفاًۚ وَلَوْلَا رَهْطُكَ لَرَجَمْنَاكَۘ وَمَٓا اَنْتَ عَلَيْنَا بِعَز۪يزٍ", translation: "\"Ey Şu'ayb! Dediklerinin çoğunu anlamıyoruz, biz, seni içimizde güçsüz görüyoruz. Kabilen olmasaydı seni taşlardık. Esasen sen değerli biri de değilsin.\" dediler.", transliteration: "Kalu ya Şuaybu ma nefkahu kesiren mimma tekulu ve inna le nerake fina daifa, ve lev la rehtuke le recemnake ve ma ente aleyna bi aziz."},
  {id: 1565, sure_no: 92, ayet_no: 92, sure_adi: "Hud", text: "قَالَ يَا قَوْمِ اَرَهْط۪ٓي اَعَزُّ عَلَيْكُمْ مِنَ اللّٰهِۜ وَاتَّخَذْتُمُوهُ وَرَٓاءَكُمْ ظِهْرِياًّۜ اِنَّ رَبّ۪ي بِمَا تَعْمَلُونَ مُح۪يطٌ", translation: "\"Ey halkım! Size göre kabilem Allah'tan daha mı değerli ki O'na sırt çevirdiniz. Kuşkusuz Rabb'im yaptıklarınızı kuşatmıştır.\"", transliteration: "Kale ya kavmi e rahti eazzu aleykum minallah, vettehaztumuhu veraekum zıhriyya, inne rabbi bi ma ta'melune muhit."},
  {id: 1566, sure_no: 93, ayet_no: 93, sure_adi: "Hud", text: "وَيَا قَوْمِ اعْمَلُوا عَلٰى مَكَانَتِكُمْ اِنّ۪ي عَامِلٌۜ سَوْفَ تَعْلَمُونَۙ مَنْ يَأْت۪يهِ عَذَابٌ يُخْز۪يهِ وَمَنْ هُوَ كَاذِبٌۜ وَارْتَقِبُٓوا اِنّ۪ي مَعَكُمْ رَق۪يبٌ", translation: "\"Ey halkım! Elinizden geleni yapın. Doğrusu ben de yapacağım. Yakında bileceksiniz, alçaltıcı azabın kime geleceğini ve kimin yalancı olduğunu. Gözetleyin, doğrusu ben de sizinle birlikte gözetliyorum.\"", transliteration: "Ve ya kavmi'melu ala mekanetikum inni amil, sevfe ta'lemune men ye'tihi azabun yuhzihi ve men huve kazib, vertekibu inni meakum rakib."},
  {id: 1567, sure_no: 94, ayet_no: 94, sure_adi: "Hud", text: "وَلَمَّا جَٓاءَ اَمْرُنَا نَجَّيْنَا شُعَيْباً وَالَّذ۪ينَ اٰمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَاَخَذَتِ الَّذ۪ينَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوا ف۪ي دِيَارِهِمْ جَاثِم۪ينَۙ", translation: "Hükmümüz gerçekleşince Şu'ayb'ı ve onunla birlikte iman etmiş olanları, Tarafımızdan bir rahmetle kurtardık. Zulmedenleri de dehşete düşürücü bir ses yakaladı. Yurtlarında dizüstü çökmüş olarak sabahladılar.", transliteration: "Ve lemma cae emruna necceyna ?uayben vellezine amenu meahu bi rahmetin minna ve ehazetillezine zalemussayhatu fe asbahu fi diyarihim casimin."},
  {id: 1568, sure_no: 95, ayet_no: 95, sure_adi: "Hud", text: "كَاَنْ لَمْ يَغْنَوْا ف۪يهَاۜ اَلَا بُعْداً لِمَدْيَنَ كَمَا بَعِدَتْ ثَمُودُ۟", translation: "Sanki orada hiç yaşamamışlardı. Tıpkı Semud gibi, Medyen de yok olup gitti.", transliteration: "Ke'en lem yagnev fiha, e la bu'den li medyene kema baıdet semud."},
  {id: 1569, sure_no: 96, ayet_no: 96, sure_adi: "Hud", text: "وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيَاتِنَا وَسُلْطَانٍ مُب۪ينٍۙ", translation: "Ant olsun Musa'yı ayetlerimizle ve apaçık bir yetkiyle gönderdik;", transliteration: "Ve le kad erselna musa bi ayatina ve sultanin mubin."},
  {id: 1570, sure_no: 97, ayet_no: 97, sure_adi: "Hud", text: "اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ فَاتَّـبَعُٓوا اَمْرَ فِرْعَوْنَۚ وَمَٓا اَمْرُ فِرْعَوْنَ بِرَش۪يدٍ", translation: "Firavun ve melelerine. Firavun'un buyruklarına uydular. Oysa Firavun'un buyrukları hiç de akıllıca değildi;", transliteration: "İla fir'avne ve melaihi fettebeu emre fir'avn, ve ma emru fir'avne bi reşid."},
  {id: 1571, sure_no: 98, ayet_no: 98, sure_adi: "Hud", text: "يَقْدُمُ قَوْمَهُ يَوْمَ الْقِيٰمَةِ فَاَوْرَدَهُمُ النَّارَۜ وَبِئْسَ الْوِرْدُ الْمَوْرُودُ", translation: "Kıyamet Günü, halkının önüne düşecek ve onları ateşe götürecektir. Varacakları yer, ne kötü bir yerdir.", transliteration: "Yakdumu kavmehu yevmel kıyameti fe evredehumun nar, ve bi'sel virdul mevrud."},
  {id: 1572, sure_no: 99, ayet_no: 99, sure_adi: "Hud", text: "وَاُتْبِعُوا ف۪ي هٰذِه۪ لَعْنَةً وَيَوْمَ الْقِيٰمَةِۜ بِئْسَ الرِّفْدُ الْمَرْفُودُ", translation: "Bu dünyada da Kıyamet Günü'nde de lanete uğratıldılar. Paylarına düşen ne kötü bir paydır.", transliteration: "Ve utbiu fi hazihi la'neten ve yevmel kıyameh, bi'ser rifdul merfud."},
  {id: 1573, sure_no: 100, ayet_no: 100, sure_adi: "Hud", text: "ذٰلِكَ مِنْ اَنْـبَٓاءِ الْقُرٰى نَقُصُّهُ عَلَيْكَ مِنْهَا قَٓائِمٌ وَحَص۪يدٌ", translation: "Bunlar, o kentlere ait sana bildirdiğimiz haberlerdir. O kentlerden, yerinde duran da var, biçilmiş ekin gibi olan da.", transliteration: "Zalike min enbail kura nekussuhu aleyke minha kaimun ve hasid."},
  {id: 1574, sure_no: 101, ayet_no: 101, sure_adi: "Hud", text: "وَمَا ظَلَمْنَاهُمْ وَلٰكِنْ ظَلَمُٓوا اَنْفُسَهُمْ فَـمَٓا اَغْنَتْ عَنْهُمْ اٰلِهَتُهُمُ الَّت۪ي يَدْعُونَ مِنْ دُونِ اللّٰهِ مِنْ شَيْءٍ لَمَّا جَٓاءَ اَمْرُ رَبِّكَۜ وَمَا زَادُوهُمْ غَيْرَ تَتْب۪يبٍ", translation: "Biz onlara haksızlık yapmadık; onlar kendi kendilerine haksızlık yaptılar. Rabb'inin buyruğu gerçekleşince, Allah'ı bırakıp da kulluk ettikleri ilahları kendilerine hiçbir yarar sağlamadı. Yalnızca kayıplarını artırdılar.", transliteration: "Ve ma zalemnahum ve lakin zalemu enfusehum fe ma agnet anhum alihetuhumulleti yed'une min dunillahi min şey'in lemma cae emru rabbik, ve ma zaduhum gayre tetbib."},
  {id: 1575, sure_no: 102, ayet_no: 102, sure_adi: "Hud", text: "وَكَذٰلِكَ اَخْذُ رَبِّكَ اِذَٓا اَخَذَ الْقُرٰى وَهِيَ ظَالِمَةٌۜ اِنَّ اَخْذَهُٓ اَل۪يمٌ شَد۪يدٌ", translation: "İşte Rabb'in, zulmeden kentleri cezalandırdığı zaman, böyle cezalandırır. O'nun cezası çok acı verici ve çok şiddetlidir.", transliteration: "Ve kezalike ahzu rabbike iza ehazel kura ve hiye zalimeh, inne ahzehu elimun şedid."},
  {id: 1576, sure_no: 103, ayet_no: 103, sure_adi: "Hud", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِمَنْ خَافَ عَذَابَ الْاٰخِرَةِۜ ذٰلِكَ يَوْمٌ مَجْمُوعٌۙ لَهُ النَّاسُ وَذٰلِكَ يَوْمٌ مَشْهُودٌ", translation: "Ahiret azabından korkanlar için, bunda bir ayet vardır. O Gün, bütün insanların toplanacağı gündür. O mutlaka görülecek bir gündür.", transliteration: "İnne fi zalike le ayeten li men hafe azabel ahıreh, zalike yevmun mecmuun lehun nasu ve zalike yevmun meşhud."},
  {id: 1577, sure_no: 104, ayet_no: 104, sure_adi: "Hud", text: "وَمَا نُؤَخِّرُهُٓ اِلَّا لِاَجَلٍ مَعْدُودٍۜ", translation: "Onu geciktirmemiz belli bir süreye kadardır.", transliteration: "Ve ma nuahhıruhu illa li ecelin ma'dud."},
  {id: 1578, sure_no: 105, ayet_no: 105, sure_adi: "Hud", text: "يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ اِلَّا بِـاِذْنِه۪ۚ فَمِنْهُمْ شَقِيٌّ وَسَع۪يدٌ", translation: "O Gün gelince, O'nun izni olmadan hiç kimse konuşamaz. Onlardan kimi mutsuz, kimi de mutludur.", transliteration: "Yevme ye'ti la tekellemu nefsun illa bi iznih, fe minhum şakıyyun ve said."},
  {id: 1579, sure_no: 106, ayet_no: 106, sure_adi: "Hud", text: "فَاَمَّا الَّذ۪ينَ شَقُوا فَفِي النَّارِ لَهُمْ ف۪يهَا زَف۪يرٌ وَشَه۪يقٌۙ", translation: "Mutsuz olanlar ateştedir. Onlar, orada hıçkırırlar, inleyip dururlar.", transliteration: "Fe emmellezine şeku fe fin nari lehum fiha zefirun ve şehik."},
  {id: 1580, sure_no: 107, ayet_no: 107, sure_adi: "Hud", text: "خَالِد۪ينَ ف۪يهَا مَا دَامَتِ السَّمٰوَاتُ وَالْاَرْضُ اِلَّا مَا شَٓاءَ رَبُّكَۜ اِنَّ رَبَّكَ فَعَّالٌ لِمَا يُر۪يدُ", translation: "Rabb'in, aksini dilemedikçe, gökler ve yer durdukça, orada sürekli kalacaklardır. Kuşkusuz Rabb'in, dilediğini yapandır.", transliteration: "Halidine fiha ma dametis semavatu vel'ardu illa ma şae rabbuk, inne rabbeke fe'alun lima yurid."},
  {id: 1581, sure_no: 108, ayet_no: 108, sure_adi: "Hud", text: "وَاَمَّا الَّذ۪ينَ سُعِدُوا فَفِي الْجَنَّةِ خَالِد۪ينَ ف۪يهَا مَا دَامَتِ السَّمٰوَاتُ وَالْاَرْضُ اِلَّا مَا شَٓاءَ رَبُّكَۜ عَطَٓاءً غَيْرَ مَجْذُوذٍ", translation: "Mutlu olanlar ise, Cennet'tedirler. Rabb'in aksini dilemedikçe, gökler ve yer durdukça, orada sürekli kalacaklardır. Bu kesintisiz bir iyiliktir.", transliteration: "Ve emmellezine suidu fe fil cenneti halidine fiha ma dametis semavatu vel ardu illa ma şae rabbuk, ataen gayre meczuz."},
  {id: 1582, sure_no: 109, ayet_no: 109, sure_adi: "Hud", text: "فَلَا تَكُ ف۪ي مِرْيَةٍ مِمَّا يَعْبُدُ هٰٓؤُ۬لَٓاءِۜ مَا يَعْبُدُونَ اِلَّا كَمَا يَعْبُدُ اٰبَٓاؤُ۬هُمْ مِنْ قَبْلُۜ وَاِنَّا لَمُوَفُّوهُمْ نَص۪يبَهُمْ غَيْرَ مَنْقُوصٍ۟", translation: "Onların kulluk ettikleri hakkında hiç kuşkun olmasın. Daha önce atalarının kulluk ettikleri gibi kulluk ediyorlar. Elbette paylarına düşeni eksiksiz olarak vereceğiz.", transliteration: "Fe la teku fi miryetin mimma ya'budu haula', ma ya'budune illa kema ya'budu abauhum min kabl, ve inna le muveffuhum nasibehum gayre menkus."},
  {id: 1583, sure_no: 110, ayet_no: 110, sure_adi: "Hud", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ ف۪يهِۜ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْۜ وَاِنَّهُمْ لَف۪ي شَكٍّ مِنْهُ مُر۪يبٍ", translation: "Ant olsun! Musa'ya Kitap'ı verdik de onda anlaşmazlığa düştüler. Rabb'in tarafından önceden takdir edilmiş bir karar olmasaydı, aralarında hemen hüküm verilirdi. Onlar da bunun hakkında ikilem ve güvensizlik içindedirler.", transliteration: "Ve lekad ateyna musel kitabe fahtulife fih, ve lev la kelimetun sebekat min rabbike le kudiye beynehum, ve innehum le fi şekkin minhu murib."},
  {id: 1584, sure_no: 111, ayet_no: 111, sure_adi: "Hud", text: "وَاِنَّ كُلاًّ لَمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ اَعْمَالَهُمْۜ اِنَّهُ بِمَا يَعْمَلُونَ خَب۪يرٌ", translation: "Rabb'in onların yaptığı her şeyin karşılığını tam olarak verecektir. O, onların yaptıklarından haberdardır.", transliteration: "Ve inne kullen lemma le yuveffiyennehum rabbuke a'malehum, innehu bima ya'melune habir."},
  {id: 1585, sure_no: 112, ayet_no: 112, sure_adi: "Hud", text: "فَاسْتَقِمْ كَمَٓا اُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلَا تَطْغَوْاۜ اِنَّهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Sen ve tevbe edip seninle birlikte olanlar, emrolunduğunuz gibi dosdoğru olun. Aşırı gitmeyin. O, yaptığınız her şeyi görmektedir.", transliteration: "Festekim kema umirte ve men tabe meake ve la tatgav, innehu bi ma ta'melune basir."},
  {id: 1586, sure_no: 113, ayet_no: 113, sure_adi: "Hud", text: "وَلَا تَرْكَـنُٓوا اِلَى الَّذ۪ينَ ظَلَمُوا فَتَمَسَّكُمُ النَّارُۙ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ اَوْلِيَٓاءَ ثُمَّ لَا تُنْصَرُونَ", translation: "Zulmedenlere eğilim göstermeyin. Sonra size de ateş dokunur. Allah'ın yanı sıra başka evliyanız da yoktur. Sonra yardım da göremezsiniz.", transliteration: "Ve la terkenu ilellezine zalemu fe temessekumun naru ve ma lekum min dunillahi min evliyae summe la tunsarun."},
  {id: 1587, sure_no: 114, ayet_no: 114, sure_adi: "Hud", text: "وَاَقِمِ الصَّلٰوةَ طَرَفَيِ النَّهَارِ وَزُلَفاً مِنَ الَّيْلِۜ اِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّـَٔاتِۜ ذٰلِكَ ذِكْرٰى لِلذَّاكِر۪ينَۚ", translation: "Gündüzün iki tarafında ve gecenin yakınlarında salatı ikame et. Çünkü iyilikler kötülükleri giderir. Bu, öğüt alanlar için bir öğüttür.", transliteration: "Ve ekımis salate tarafeyin nehari ve zulefen minel leyl, innel hasenati yuzhibnes seyyiat, zalike zikra liz zakirin."},
  {id: 1588, sure_no: 115, ayet_no: 115, sure_adi: "Hud", text: "وَاصْبِرْ فَاِنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُحْسِن۪ينَ", translation: "Sabret! Kuşkusuz Allah, iyilerin iyiliklerini asla karşılıksız bırakmaz.", transliteration: "Vasbir fe innallahe la yudiu ecrel muhsinin."},
  {id: 1589, sure_no: 116, ayet_no: 116, sure_adi: "Hud", text: "فَلَوْلَا كَانَ مِنَ الْقُرُونِ مِنْ قَبْلِكُمْ اُو۬لُوا بَقِيَّةٍ يَنْهَوْنَ عَنِ الْفَسَادِ فِي الْاَرْضِ اِلَّا قَل۪يلاً مِمَّنْ اَنْجَيْنَا مِنْهُمْۚ  وَاتَّبَعَ الَّذ۪ينَ ظَلَمُوا مَٓا اُتْرِفُوا ف۪يهِ وَكَانُوا مُجْرِم۪ينَ", translation: "Keşke sizden önceki nesillerde yeryüzünde fesatlığa karşı çıkan faziletli kimseler olmuş olsaydı. Onlardan ancak çok azını kurtuluşa erdirdik. Zulmedenler ise içinde bulundukları refaha dalıp böylece mücrim kimseler oldular.", transliteration: "Fe lev la kane minel kuruni min kablikum ulu bakıyyetin yenhevne anil fesadi fil ardı illa kalilen mimmen enceyna minhum, vettebeallezine zalemu ma utrifu fihi ve kanu mucrimin."},
  {id: 1590, sure_no: 117, ayet_no: 117, sure_adi: "Hud", text: "وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرٰى بِظُلْمٍ وَاَهْلُهَا مُصْلِحُونَ", translation: "Yoksa senin Rabb'in, o memleketleri, halkı düzelticiler oldukları halde, haksızlıkla yok edecek değildi!", transliteration: "Ve ma kane rabbuke li yuhlikel kura bi zulmin ve ehluha muslihun."},
  {id: 1591, sure_no: 118, ayet_no: 118, sure_adi: "Hud", text: "وَلَوْ شَٓاءَ رَبُّكَ لَجَعَلَ النَّاسَ اُمَّةً وَاحِدَةً وَلَا يَزَالُونَ مُخْتَلِف۪ينَۙ", translation: "Rabb'in dileseydi, bütün insanları tek bir ümmet yapardı. İhtilaf edenler olarak sürüp gitmezlerdi.", transliteration: "Ve lev şae rabbuke le cealen nase ummeten vahideten ve la yezalune muhtelifin."},
  {id: 1592, sure_no: 119, ayet_no: 119, sure_adi: "Hud", text: "اِلَّا مَنْ رَحِمَ رَبُّكَۜ وَلِذٰلِكَ خَلَقَهُمْۜ وَتَمَّتْ كَلِمَةُ رَبِّكَ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَع۪ينَ", translation: "Ancak Rabb'inin rahmet ettikleri hariç. Bunun için yarattı onları. Kesinlikle Rabb'inin takdir ettiği, \"Cehennem'i cinlerden ve insanlardan dolduracağım\" hükmü gerçekleşecektir.", transliteration: "İlla men rahime rabbuk, ve li zalike halakahum, ve temmet kelimetu rabbike le emleenne cehenneme minel cinneti ven nasi ecmain."},
  {id: 1593, sure_no: 120, ayet_no: 120, sure_adi: "Hud", text: "وَكُلاًّ نَقُصُّ عَلَيْكَ مِنْ اَنْبَٓاءِ الرُّسُلِ مَا نُثَبِّتُ بِه۪ فُؤٰادَكَۚ وَجَٓاءَكَ ف۪ي هٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَذِكْرٰى لِلْمُؤْمِن۪ينَ", translation: "Elçilerin haberlerinden, kalbini kuvvetlendirecek her şeyi sana anlatıyoruz. Bu hususta sana Hakk, Mü'minlere de öğüt ve hatırlatma geldi.", transliteration: "Ve kullen nakussu aleyke min enbair rusuli ma nusebbitu bihi fuadek ve caeke fi hazihil hakku ve mev'ızatun ve zikra lil muminin."},
  {id: 1594, sure_no: 121, ayet_no: 121, sure_adi: "Hud", text: "وَقُلْ لِلَّذ۪ينَ لَا يُؤْمِنُونَ اعْمَلُوا عَلٰى مَكَانَتِكُمْۜ اِنَّا عَامِلُونَۙ", translation: "İnanmayanlara de ki: \"Elinizden ne geliyorsa yapın. Biz de yapmaktayız.\"", transliteration: "Ve kul lillezine la yu'minuna'melu ala mekanetikum, inna amilun."},
  {id: 1595, sure_no: 122, ayet_no: 122, sure_adi: "Hud", text: "وَانْتَظِرُواۚ اِنَّا مُنْتَظِرُونَ", translation: "\"Bekleyin! Biz de bekliyoruz.\"", transliteration: "Ventazır, inna muntazırun."},
  {id: 1596, sure_no: 123, ayet_no: 123, sure_adi: "Hud", text: "وَلِلّٰهِ غَيْبُ السَّمٰوَاتِ وَالْاَرْضِ وَاِلَيْهِ يُرْجَعُ الْاَمْرُ كُلُّهُ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِۜ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ", translation: "Göklerin ve yerin gaybı Allah'a aittir. Bütün işler O'na döndürülür. Öyleyse yalnızca O'na kulluk et ve yalnızca O'na tevekkül et. Rabb'in, yaptıklarınızdan habersiz değildir.", transliteration: "Ve lillahi gaybus semavati vel ardı ve ileyhi yurceul emru kulluhu fa'budhu ve tevekkel aleyh, ve ma rabbuke bi gafilin amma ta'melun."},
  {id: 1597, sure_no: 1, ayet_no: 1, sure_adi: "Yusuf", text: "الٓـرٰ۠ تِلْكَ اٰيَاتُ الْـكِتَابِ الْمُب۪ينِ۠", translation: "Elif. Lam. Ra. Bunlar açık ve anlaşılır Kitap'ın ayetleridir.", transliteration: "Elif lam ra tilke ayatul kitabil mubin."},
  {id: 1598, sure_no: 2, ayet_no: 2, sure_adi: "Yusuf", text: "اِنَّٓا اَنْزَلْنَاهُ قُرْءٰناً عَرَبِياًّ لَعَلَّـكُمْ تَعْقِلُونَ", translation: "Akledip anlamanız için onu Arapça bir kur'an olarak gönderdik.", transliteration: "İnna enzelnahu kur'anen arabiyyen le allekum ta'kılun."},
  {id: 1599, sure_no: 3, ayet_no: 3, sure_adi: "Yusuf", text: "نَحْنُ نَقُصُّ عَلَيْكَ اَحْسَنَ الْقَصَصِ بِمَٓا اَوْحَيْنَٓا اِلَيْكَ هٰذَا الْقُرْاٰنَۗ وَاِنْ كُنْتَ مِنْ قَبْلِه۪ لَمِنَ الْغَافِل۪ينَ", translation: "Biz, Sana bu Kur'an'ı iletmekle, daha önce hakkında bilgi sahibi olmadığın kıssaları en doğru şekilde bildirmiş oluyoruz.", transliteration: "Nahnu nakussu aleyke ahsenel kasası bima evhayna ileyke hazel kur'ane ve in kunte min kablihi le minel gafilin."},
  {id: 1600, sure_no: 4, ayet_no: 4, sure_adi: "Yusuf", text: "اِذْ قَالَ يُوسُفُ لِاَب۪يهِ يَٓا اَبَتِ اِنّ۪ي رَاَيْتُ اَحَدَ عَشَرَ كَوْكَباً وَالشَّمْسَ وَالْقَمَرَ رَاَيْتُهُمْ ل۪ي سَاجِد۪ينَ", translation: "Hani bir zamanlar Yusuf, babasına: \"Ey babacığım! Ben on bir yıldızla, Güneş'in ve Ay'ın bana secde ettiklerini gördüm.\" demişti.", transliteration: "İz kale yusufu li ebihi ya ebeti inni re eytu ehade aşere kevkeben veş şemse vel kamere re eytuhum li sacidin."},
  {id: 1601, sure_no: 5, ayet_no: 5, sure_adi: "Yusuf", text: "قَالَ يَا بُنَيَّ لَا تَقْصُصْ رُءْيَاكَ عَلٰٓى اِخْوَتِكَ فَيَك۪يدُوا لَكَ كَيْداًۜ اِنَّ الشَّيْطَانَ لِلْاِنْسَانِ عَدُوٌّ مُب۪ينٌ", translation: "Dedi ki: \"Ey yavrum! Rüyanı kardeşlerine anlatma; sonra sana tuzak kurarlar. Şeytan, insanın açık bir düşmanıdır.\"", transliteration: "Kale ya buneyye la taksus ru'yake ala ihvetike fe yekidu leke keyda, inneş şeytane lil insani aduvvun mubin."},
  {id: 1602, sure_no: 6, ayet_no: 6, sure_adi: "Yusuf", text: "وَكَذٰلِكَ يَجْتَب۪يكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْو۪يلِ الْاَحَاد۪يثِ وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ وَعَلٰٓى اٰلِ يَعْقُوبَ كَمَٓا اَتَمَّهَا عَلٰٓى اَبَوَيْكَ مِنْ قَبْلُ اِبْرٰه۪يمَ وَاِسْحٰقَۜ اِنَّ رَبَّكَ عَل۪يمٌ حَك۪يمٌ۟", translation: "Böylece Rabb'in seni seçecek ve sana olayların yorumlanmasının bilgisini verecek. Tıpkı daha önce ataların İbrahim ve İshak'a nimetlerini tamamladığı gibi sana ve Ya'kub soyuna da tamamlayacaktır. Rabb'in Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve kezalike yectebike rabbuke ve yu allimuke min te'vilil ehadisi, ve yutimmu ni'metehu aleyke ve ala ali ya'kube kema etemmeha ala ebeveyke min kablu ibrahime ve ishak, inne rabbeke alimun hakim."},
  {id: 1603, sure_no: 7, ayet_no: 7, sure_adi: "Yusuf", text: "لَقَدْ كَانَ ف۪ي يُوسُفَ وَاِخْوَتِه۪ٓ اٰيَاتٌ لِلسَّٓائِل۪ينَ", translation: "Doğrusu, Yusuf ve kardeşlerinin olayında ders almak isteyenler için nice ayetler vardır.", transliteration: "Le kad kane fi yusufe ve ihvetihi ayatun lis sailin."},
  {id: 1604, sure_no: 8, ayet_no: 8, sure_adi: "Yusuf", text: "اِذْ قَالُوا لَيُوسُفُ وَاَخُوهُ اَحَبُّ اِلٰٓى اَب۪ينَا مِنَّا وَنَحْنُ عُصْبَةٌۜ اِنَّ اَبَانَا لَف۪ي ضَلَالٍ مُب۪ينٍۚ", translation: "Hani: \"Çok sayıda kardeş olduğumuz halde, babamız, Yusuf ve kardeşini bizden daha çok seviyor. Doğrusu babamız apaçık bir yanılgı içindedir.\" demişlerdi.", transliteration: "İz kalu le yusufu ve ehuhu ehabbu ila ebina minna ve nahnu usbeh, inne ebana le fi dalalin mubin."},
  {id: 1605, sure_no: 9, ayet_no: 9, sure_adi: "Yusuf", text: "اُقْتُلُوا يُوسُفَ اَوِ اطْرَحُوهُ اَرْضاً يَخْلُ لَكُمْ وَجْهُ اَب۪يكُمْ وَتَكُونُوا مِنْ بَعْدِه۪ قَوْماً صَالِح۪ينَ", translation: "\"Yusuf'u öldürün veya onu bilinmez bir yere atın ki babanızın ilgisi yalnızca size yönelsin. Ve bunun ardından iyi bir topluluk olursunuz.\"", transliteration: "Uktulu yusufe evitrahuhu ardan yahlu lekum vechu ebikum ve tekunu min ba'dihi kavmen salihin."},
  {id: 1606, sure_no: 10, ayet_no: 10, sure_adi: "Yusuf", text: "قَالَ قَٓائِلٌ مِنْهُمْ لَا تَقْتُلُوا يُوسُفَ وَاَلْقُوهُ ف۪ي غَيَابَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ اِنْ كُنْتُمْ فَاعِل۪ينَ", translation: "İçlerinden, sözü geçerli olanı: \"Yusuf'u öldürmeyin, eğer bir şey yapacaksanız onu bir kuyunun dibine bırakın, kervanlardan biri onu alsın.\"", transliteration: "Kale kailun minhum la taktulu yusufe ve elkuhu fi gayabetil cubbi yel-tekithu ba'dus seyyareti in kuntum failin."},
  {id: 1607, sure_no: 11, ayet_no: 11, sure_adi: "Yusuf", text: "قَالُوا يَٓا اَبَانَا مَا لَكَ لَا تَأْمَنَّۭۖا عَلٰى يُوسُفَ وَاِنَّا لَهُ لَنَاصِحُونَ", translation: "\"Ey babamız! Yusuf hakkında neden bize güvenmiyorsun? Oysaki biz onun iyiliğini isteyenleriz.\" dediler.", transliteration: "Kalu ya ebana ma leke la te'menna ala yusufe ve inna lehu lenasıhun."},
  {id: 1608, sure_no: 12, ayet_no: 12, sure_adi: "Yusuf", text: "اَرْسِلْهُ مَعَنَا غَداً يَرْتَعْ وَيَلْعَبْ وَاِنَّا لَهُ لَحَافِظُونَ", translation: "\"Onu, yarın bizimle beraber gönder. Dilediğince gezip eğlensin. Biz ona sahip çıkarız.\"", transliteration: "Ersilhu ma ana gaden yerta' ve yel'ab ve inna lehu lehafizun."},
  {id: 1609, sure_no: 13, ayet_no: 13, sure_adi: "Yusuf", text: "قَالَ اِنّ۪ي لَيَحْزُنُن۪ٓي اَنْ تَذْهَبُوا بِه۪ وَاَخَافُ اَنْ يَأْكُلَهُ الذِّئْبُ وَاَنْتُمْ عَنْهُ غَافِلُونَ", translation: "\"Onu götürmeniz beni tasalandırır.\" dedi. \"Sizin ondan habersiz olduğunuz bir sırada kurdun onu yemesinden korkarım.\"", transliteration: "Kale inni le yahzununi en tezhebu bihi ve ehafu en ye'kulehuz zi'bu ve entum anhu gafilun."},
  {id: 1610, sure_no: 14, ayet_no: 14, sure_adi: "Yusuf", text: "قَالُوا لَئِنْ اَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ اِنَّٓا اِذاً لَخَاسِرُونَ", translation: "\"Ant olsun ki, biz bu kadar kişiyken yine de kurt onu yerse, o takdirde bize yazıklar olsun.\" dediler.", transliteration: "Kalu le in ekelehuz zi'bu ve nahnu usbetun inna izen lehasirun."},
  {id: 1611, sure_no: 15, ayet_no: 15, sure_adi: "Yusuf", text: "فَلَمَّا ذَهَبُوا بِه۪ وَاَجْمَعُٓوا اَنْ يَجْعَلُوهُ ف۪ي غَيَابَتِ الْجُبِّۚ وَاَوْحَيْنَٓا اِلَيْهِ لَتُنَبِّئَنَّهُمْ بِاَمْرِهِمْ هٰذَا وَهُمْ لَا يَشْعُرُونَ", translation: "O'nu alıp götürdüler. Hep birlikte onu kuyunun dibine bırakmaya karar verdiler. O sırada Yusuf'a: \"Ant olsun onların bu yaptıklarını, bir gün gelecek yüzlerine vuracaksın.\" diye vahyettik.", transliteration: "Fe lemma zehebu bihi ve ecmeu en yec'aluhu fi gayabetil cubb, ve evhayna ileyhi le tunebbiennehum bi emrihim haza ve hum la yeş'urun."},
  {id: 1612, sure_no: 16, ayet_no: 16, sure_adi: "Yusuf", text: "وَجَٓاؤُٓ۫ اَبَاهُمْ عِشَٓاءً يَبْكُونَۜ", translation: "Ve akşam olunca, ağlayarak babalarına geldiler.", transliteration: "Ve cau ebahum işaen yebkun."},
  {id: 1613, sure_no: 17, ayet_no: 17, sure_adi: "Yusuf", text: "قَالُوا يَٓا اَبَانَٓا اِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوسُفَ عِنْدَ مَتَاعِنَا فَاَكَلَهُ الذِّئْبُۚ وَمَٓا اَنْتَ بِمُؤْمِنٍ لَنَا وَلَوْ كُنَّا صَادِق۪ينَ", translation: "\"Ey babamız! Yarışmaya gittik. Yusuf'u da eşyamızın yanına bırakmıştık. Kurt onu yemiş. Sen bize inanmayacaksın ama doğru söylüyoruz.\" dediler.", transliteration: "Kalu ya ebana inna zehebna nestebiku ve terekna yusufe inde metaına fe ekelehuz zi'bu, ve ma ente bi mu'minin lena ve lev kunna sadikin."},
  {id: 1614, sure_no: 18, ayet_no: 18, sure_adi: "Yusuf", text: "وَجَٓاؤُ۫ عَلٰى قَم۪يصِه۪ بِدَمٍ كَذِبٍۜ قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْراًۜ فَصَبْرٌ جَم۪يلٌۜ وَاللّٰهُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُونَ", translation: "Yalandan kan sürülmüş gömleğini getirdiler. Dedi ki: \"Nefisleriniz size bir iş yaptırdı. Anlattığınız şeye karşı yapabileceğim şey, güzelce sabredip, Allah'tan yardım istemektir.\"", transliteration: "Ve cau ala kamisıhi bi demin kezib, kale bel sevvelet lekum enfusukum emra, fe sabrun cemil, vallahul musteanu ala ma tesıfun."},
  {id: 1615, sure_no: 19, ayet_no: 19, sure_adi: "Yusuf", text: "وَجَٓاءَتْ سَيَّارَةٌ فَاَرْسَلُوا وَارِدَهُمْ فَاَدْلٰى دَلْوَهُۜ قَالَ يَا بُشْرٰى هٰذَا غُلَامٌۜ وَاَسَرُّوهُ بِضَاعَةًۜ وَاللّٰهُ عَل۪يمٌ بِمَا يَعْمَلُونَ", translation: "Ve bir kervan geldi. Sucularını gönderdiler. Kovasını sarkıtan, \"Müjde! Burada bir çocuk var.\" dedi. Satmak için onu yanlarına aldılar. Allah, yapmak istedikleri şeyi çok iyi biliyordu.", transliteration: "Ve caet seyyaretun fe erselu varidehum fe adla delveh, kale ya buşra haza gulam, ve eserruhu bidaah, vallahu alimun bi ma ya'melun."},
  {id: 1616, sure_no: 20, ayet_no: 20, sure_adi: "Yusuf", text: "وَشَرَوْهُ بِثَمَنٍ بَخْسٍ دَرَاهِمَ مَعْدُودَةٍۚ وَكَانُوا ف۪يهِ مِنَ الزَّاهِد۪ينَ۟", translation: "Onu ucuz bir fiyata, birkaç dirheme sattılar. Ona önem vermemişlerdi.", transliteration: "Ve şerevhu bi semenin bahsin derahime ma'dudeh, ve kanu fihi minez zahidin."},
  {id: 1617, sure_no: 21, ayet_no: 21, sure_adi: "Yusuf", text: "وَقَالَ الَّذِي اشْتَرٰيهُ مِنْ مِصْرَ لِامْرَاَتِه۪ٓ اَكْرِم۪ي مَثْوٰيهُ عَسٰٓى اَنْ يَنْفَعَنَٓا اَوْ نَتَّخِذَهُ وَلَداًۜ وَكَذٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْاَرْضِۘ وَلِنُعَلِّمَهُ مِنْ تَأْو۪يلِ الْاَحَاد۪يثِۜ وَاللّٰهُ غَالِبٌ عَلٰٓى اَمْرِه۪ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ", translation: "Onu satın alan Mısırlı, hanımına: \"Ona iyi bak, bize faydası olabilir, belki de onu evlat ediniriz.\" dedi. Böylece Yusuf'u oraya yerleştirdik ki ona kimi hadislerin yorumunu öğretelim. Allah, takdir ettiğini yapandır. Ancak insanların çoğu bunu bilmezler.", transliteration: "Ve kalellezişterahu min mısra limre'etihi ekrimi mesvahu asa en yenfeana ev nettehizehu veleda, ve kezalike mekkenna li yusufe fil ardı ve li nuallimehu min te'vilil ehadis, vallahu galibun ala emrihi ve lakinne ekseren nasi la ya'lemun."},
  {id: 1618, sure_no: 22, ayet_no: 22, sure_adi: "Yusuf", text: "وَلَمَّا بَلَغَ اَشُدَّهُٓ اٰتَيْنَاهُ حُكْماً وَعِلْماًۜ وَكَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "Olgunluk çağına erişince ona hükmetme yeteneği ve bilgi verdik. İşte Biz, iyi davrananları böyle ödüllendiririz.", transliteration: "Ve lemma belega eşuddehu ateynahu hukmen ve ilma, ve kezalike neczil muhsinin."},
  {id: 1619, sure_no: 23, ayet_no: 23, sure_adi: "Yusuf", text: "وَرَاوَدَتْهُ الَّت۪ي هُوَ ف۪ي بَيْتِهَا عَنْ نَفْسِه۪ وَغَلَّقَتِ الْاَبْوَابَ وَقَالَتْ هَيْتَ لَكَۜ قَالَ مَعَاذَ اللّٰهِ اِنَّهُ رَبّ۪ٓي اَحْسَنَ مَثْوَايَۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ", translation: "Evinde kaldığı kadın, ona sahip olmak istedi. Ve kapıları sıkıca kapatıp onu yanına çağırdı. Yusuf: \"Allah'a sığınırım. Rabbim bana çok iyi davrandı. Beni çok güzel bir konuma sahip kıldı. Haksızlık yapanlar kurtuluşa ermezler.\" dedi.", transliteration: "Ve ravedethulleti huve fi beytiha an nefsihi ve ğallekatil ebvabe ve kalet heyte lek, kale ma azallahi innehu rabbi ahsene mesvay, innehu la yuflihuz zalimun."},
  {id: 1620, sure_no: 24, ayet_no: 24, sure_adi: "Yusuf", text: "وَلَقَدْ هَمَّتْ بِه۪ۗ وَهَمَّ بِهَاۚ لَوْلَٓا اَنْ رَاٰ بُرْهَانَ رَبِّه۪ۜ  كَذٰلِكَ لِنَصْرِفَ عَنْهُ السُّٓوءَ وَالْفَحْشَٓاءَۜ اِنَّهُ مِنْ عِبَادِنَا الْمُخْلَص۪ينَ", translation: "Gerçekten kadın Yusuf'a ilgi duydu. Yusuf da kadına ilgi duydu. Ne var ki Rabb'inin burhanı sayesinde kadına uymadı. Böylece ondan kötülüğü ve fuhşu çevirdik. Kuşkusuz o Bizim muhles kullarımızdandı.", transliteration: "Ve le kad hemmet bihi ve hemme biha, levla en rea burhane rabbih, kezalike li nasrife anhus sue vel fahşa, innehu min ibadinel muhlesin."},
  {id: 1621, sure_no: 25, ayet_no: 25, sure_adi: "Yusuf", text: "وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَم۪يصَهُ مِنْ دُبُرٍ وَاَلْفَيَا سَيِّدَهَا لَدَا الْبَابِۜ قَالَتْ مَا جَزَٓاءُ مَنْ اَرَادَ بِاَهْلِكَ سُٓوءاً اِلَّٓا اَنْ يُسْجَنَ اَوْ عَذَابٌ اَل۪يمٌ", translation: "İkisi de kapıya doğru koştu. Kadın Yusuf'un gömleğini arka tarafından yırttı. Kapıda kadının kocası ile karşılaştılar. Kadın: \"Ailene kötülük yapmak isteyen kimsenin zindana atılması veya acı bir azapla cezalandırılması gerekmez mi?\" dedi.", transliteration: "Vestebekal babe ve kaddet kamisahu min duburin ve elfeya seyyideha ledel bab, kalet ma cezau men erade bi ehlike suen illa en yuscene ev azabun elim."},
  {id: 1622, sure_no: 26, ayet_no: 26, sure_adi: "Yusuf", text: "قَالَ هِيَ رَاوَدَتْن۪ي عَنْ نَفْس۪ي وَشَهِدَ شَاهِدٌ مِنْ اَهْلِهَاۚ اِنْ كَانَ قَم۪يصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكَاذِب۪ينَ", translation: "Yusuf: \"Kendisi bana sahip olmak istedi.\" dedi. Kadının ailesinden bir tanık durumu açığa çıkarmak için şöyle öneride bulundu: \"Eğer gömleği ön taraftan yırtılmışsa, kadın doğru, Yusuf yalan söylemektedir.\"", transliteration: "Kale hiye ravedetni an nefsi ve şehide şahidun min ehliha, in kane kamisuhu kudde min kubulin fe sadekat ve huve minel kazibin."},
  {id: 1623, sure_no: 27, ayet_no: 27, sure_adi: "Yusuf", text: "وَاِنْ كَانَ قَم۪يصُهُ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصَّادِق۪ينَ", translation: "\"Eğer gömlek arkadan yırtılmışsa, kadın yalan, O doğru söylemektedir.\"", transliteration: "Ve in kane kamisuhu kudde min duburin fe kezebet ve huve mines sadikin."},
  {id: 1624, sure_no: 28, ayet_no: 28, sure_adi: "Yusuf", text: "فَلَمَّا رَاٰ قَم۪يصَهُ قُدَّ مِنْ دُبُرٍ قَالَ اِنَّهُ مِنْ كَيْدِكُنَّۜ اِنَّ كَيْدَكُنَّ عَظ۪يمٌ", translation: "Gömleğin arkadan yırtılmış olduğunu görünce: \"Bu sizin oyununuzdur, gerçekten bu çok büyük bir oyundur.\" dedi.", transliteration: "Fe lemma rea kamisahu kudde min duburin kale innehu min keydikun, inne keydekunne azim."},
  {id: 1625, sure_no: 29, ayet_no: 29, sure_adi: "Yusuf", text: "يُوسُفُ اَعْرِضْ عَنْ هٰذَا وَاسْتَغْفِر۪ي لِذَنْبِكِۚ اِنَّكِ كُنْتِ مِنَ الْخَاطِـ۪ٔينَ۟", translation: "\"Yusuf! Bundan kimseye söz etme. Sen de suçundan dolayı bağışlanma dile. Yanlış yapan sensin!\" dedi.", transliteration: "Yusufu a'rıd an haza vestagfiri li zenbik, inneki kunti minel hatıin."},
  {id: 1626, sure_no: 30, ayet_no: 30, sure_adi: "Yusuf", text: "وَقَالَ نِسْوَةٌ فِي الْمَد۪ينَةِ امْرَاَتُ الْعَز۪يزِ تُرَاوِدُ فَتٰيهَا عَنْ نَفْسِه۪ۚ قَدْ شَغَفَهَا حُباًّۜ اِنَّا لَنَرٰيهَا ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Ve şehirdeki birtakım kadınlar: \"Aziz'in hanımı, genç hizmetlisiyle birlikte olmak istemiş; delikanlının sevgisi yüreğine işlemiş. Onu açıkça sapıtmış görüyoruz.\" diye dedikodu yaptılar.", transliteration: "Ve kale nisvetun fil medinetimre'etul azizi turavidu fetaha an nefsih, kad şegafeha hubba, inna le neraha fi dalalin mubin."},
  {id: 1627, sure_no: 31, ayet_no: 31, sure_adi: "Yusuf", text: "فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ اَرْسَلَتْ اِلَيْهِنَّ وَاَعْتَدَتْ لَهُنَّ مُتَّكَـٔاً وَاٰتَتْ كُلَّ وَاحِدَةٍ مِنْهُنَّ سِكّ۪يناً وَقَالَتِ اخْرُجْ عَلَيْهِنَّۚ فَلَمَّا رَاَيْنَهُٓ اَكْبَرْنَهُ وَقَطَّعْنَ اَيْدِيَهُنَّ وَقُلْنَ حَاشَ لِلّٰهِ مَا هٰذَا بَشَراًۜ اِنْ هٰذَٓا اِلَّا مَلَكٌ كَر۪يمٌ", translation: "Kadın dedikoduları işitince, onları davet etti, onlar için güzel bir ortam ve ziyafet hazırlayarak her birine birer bıçak verdi. Yusuf'a \"Kadınların karşılarına çık.\" dedi. Kadınlar, gördükleri karşısında adeta büyülendiler; şaşkınlıkla ellerini kestiler. \"Aman Allah'ım! Bu bir beşer değil, bu ancak şerefli bir melektir.\" dediler.", transliteration: "Fe lemma semiat bi mekrihinne erselet ileyhinne ve a'tedet lehunne mutteke'en ve atet kulle vahidetin minhunne sikkinen ve kaletihruc aleyhinn, fe lemma re'eynehu ekbernehu ve katta'ne eydiyehunne ve kulne haşe lillahi ma haza beşera,in haza illa melekun kerim."},
  {id: 1628, sure_no: 32, ayet_no: 32, sure_adi: "Yusuf", text: "قَالَتْ فَذٰلِكُنَّ الَّذ۪ي لُمْتُنَّن۪ي ف۪يهِۜ وَلَقَدْ رَاوَدْتُهُ عَنْ نَفْسِه۪ فَاسْتَعْصَمَۜ وَلَئِنْ لَمْ يَفْعَلْ مَٓا اٰمُرُهُ لَيُسْجَنَنَّ وَلَيَكُوناً مِنَ الصَّاغِر۪ينَ", translation: "Kadın: \"İşte! Bu gördüğünüz, beni, kendisi hakkında kınadığınız kimsedir. Ant olsun ki, onun benim olmasını istedim, ancak o reddetti. Ama ondan istediğim şeyi yapmazsa, zindana atılacak ve kesinlikle burnu yere sürtülenlerden olacaktır.\" dedi.", transliteration: "Kalet fe zalikunnellezi lumtunneni fih, ve lekad ravedtuhu an nefsihi festa'sam, ve lein lem yef'al ma amuruhu le yuscenenne ve leyekunen mines sagırin."},
  {id: 1629, sure_no: 33, ayet_no: 33, sure_adi: "Yusuf", text: "قَالَ رَبِّ السِّجْنُ اَحَبُّ اِلَيَّ مِمَّا يَدْعُونَن۪ٓي اِلَيْهِۚ وَاِلَّا تَصْرِفْ عَنّ۪ي كَيْدَهُنَّ اَصْبُ اِلَيْهِنَّ وَاَكُنْ مِنَ الْجَاهِل۪ينَ", translation: "\"Ey Rabb'im! Zindan bana, bunların istedikleri şeyi yapmaktan daha sevimlidir. Eğer onların tuzaklarını benden savmazsan, onlara kanıp cahillerden olurum.\" dedi.", transliteration: "Kale rabbis sicnu ehabbu ileyye mimma yed'uneni ileyh, ve illa tasrif anni keydehunne asbu ileyhinne ve ekun minel cahilin."},
  {id: 1630, sure_no: 34, ayet_no: 34, sure_adi: "Yusuf", text: "فَاسْتَجَابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّۜ اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Bunun üzerine Rabb'i dileğini kabul etti ve tuzaklarını ondan savdı. O' Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Festecabe lehu rabbuhu fe sarefe anhu keydehunn, innehu huves semiul alim."},
  {id: 1631, sure_no: 35, ayet_no: 35, sure_adi: "Yusuf", text: "ثُمَّ بَدَا لَهُمْ مِنْ بَعْدِ مَا رَاَوُا الْاٰيَاتِ لَيَسْجُنُنَّهُ حَتّٰى ح۪ينٍ۟", translation: "Suçsuzluğuna dair kanıtlara rağmen yine de onu belli bir süre zindana atmaya karar verdiler.", transliteration: "Summe bedalehum min ba'di ma raevul ayati le yescununnehu hatta hin."},
  {id: 1632, sure_no: 36, ayet_no: 36, sure_adi: "Yusuf", text: "وَدَخَلَ مَعَهُ السِّجْنَ فَتَيَانِۜ قَالَ اَحَدُهُمَٓا اِنّ۪ٓي اَرٰين۪ٓي اَعْصِرُ خَمْراًۚ وَقَالَ الْاٰخَرُ اِنّ۪ٓي اَرٰين۪ٓي اَحْمِلُ فَوْقَ رَأْس۪ي خُبْزاً تَأْكُلُ الطَّيْرُ مِنْهُۜ نَبِّئْنَا بِتَأْو۪يلِه۪ۚ اِنَّا نَرٰيكَ مِنَ الْمُحْسِن۪ينَ", translation: "Onunla birlikte iki genç daha zindana atıldı. Onlardan biri: \"Ben, rüyamda kendimi sarhoş edici şey yaparken gördüm.\" dedi. Diğeri: \"Ben de başımın üstünde ekmek taşıyordum, kuşların da onu yediklerini gördüm. Bize bunu yorumla. Doğrusu senin iyilik eden kimselerden olduğunu görüyoruz.\" dedi.", transliteration: "Ve dehale meahus sicne feteyan, kale ehaduhuma inni erani a'sıru hamra, ve kalel aharu inni erani ahmilu fevka re'si hubzen te'kulut tayru minh, nebbi'na bi te'vilih, inna nerake minel muhsinin."},
  {id: 1633, sure_no: 37, ayet_no: 37, sure_adi: "Yusuf", text: "قَالَ لَا يَأْت۪يكُمَا طَعَامٌ تُرْزَقَانِه۪ٓ اِلَّا نَبَّأْتُكُمَا بِتَأْو۪يلِه۪ قَبْلَ اَنْ يَأْتِيَكُمَاۜ ذٰلِكُمَا مِمَّا عَلَّمَن۪ي رَبّ۪يۜ اِنّ۪ي تَرَكْتُ مِلَّةَ قَوْمٍ لَا يُؤْمِنُونَ بِاللّٰهِ وَهُمْ بِالْاٰخِرَةِ هُمْ كَافِرُونَۙ", translation: "\"Yiyeceğiniz yemek daha gelmeden, ikinizin de rüyasını yorumlayacağım. Rüya yorumu yapmak Rabb'imin bana öğrettiği şeylerdendir. Doğrusu ben, Allah'a iman etmeyen ve ahiret gününü yalanlayan bir halkın milletini terk ettim.\" dedi.", transliteration: "Kale la ye'tikuma taamun turzekanihi illa nebbe'tukuma bi te'vilihi kable en ye'tiyekuma, zalikuma mimma allemeni rabbi, inni terektu millete kavmin la yu'minune billahi ve hum bil ahiretihum kafirun."},
  {id: 1634, sure_no: 38, ayet_no: 38, sure_adi: "Yusuf", text: "وَاتَّبَعْتُ مِلَّةَ اٰبَٓاء۪ٓي اِبْرٰه۪يمَ وَاِسْحٰقَ وَيَعْقُوبَۜ مَا كَانَ لَـنَٓا اَنْ نُشْرِكَ بِاللّٰهِ مِنْ شَيْءٍۜ ذٰلِكَ مِنْ فَضْلِ اللّٰهِ عَلَيْنَا وَعَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُونَ", translation: "\"Atalarım İbrahim, İshak ve Ya'kub'un milletini seçtim. Allah'a, herhangi bir şeyi ortak koşmak bize yakışmaz. Bu, Allah'ın bize ve insanlara olan lütfudur. Ne var ki, insanların çoğu yine de şükretmiyorlar!\"", transliteration: "Vetteba'tu millete abai ibrahime ve ishaka ve ya'kub, ma kane lena en nuşrike billahi min şey, zalike min fadlillahi aleyna ve alen nasi ve lakinne ekseren nasi la yeşkurun."},
  {id: 1635, sure_no: 39, ayet_no: 39, sure_adi: "Yusuf", text: "يَا صَاحِبَيِ السِّجْنِ ءَاَرْبَابٌ مُتَفَرِّقُونَ خَيْرٌ اَمِ اللّٰهُ الْوَاحِدُ الْقَهَّارُۜ", translation: "\"Ey zindan arkadaşlarım! Ayrı ayrı rabler mi daha hayırlıdır, yoksa Kahhar olan tek Allah mı?\"", transliteration: "Ya sahibeyis sicni e erbabun muteferrikune hayrun emillahul vahıdul kahhar."},
  {id: 1636, sure_no: 40, ayet_no: 40, sure_adi: "Yusuf", text: "مَا تَعْبُدُونَ مِنْ دُونِه۪ٓ اِلَّٓا اَسْمَٓاءً سَمَّيْتُمُوهَٓا اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ مَٓا اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطَانٍۜ اِنِ الْحُكْمُ اِلَّا لِلّٰهِۜ اَمَرَ اَلَّا تَعْبُدُٓوا اِلَّٓا اِيَّاهُۜ ذٰلِكَ الدّ۪ينُ الْقَيِّمُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ", translation: "\"Sizin, O'nun yanı sıra kulluk ettiğiniz şeyler, ancak sizin ve atalarınızın onlara yakıştırdığı isimlerden başka bir şey değildir. Allah, onlar için hiçbir yetkilendirmede bulunmadı. Hüküm yalnızca Allah'ındır; kendisinden başkasına kulluk etmemenizi emretmiştir. Dosdoğru din yalnızca budur. Ancak insanların çoğu bu gerçeği bilmezler.\"", transliteration: "Ma ta'budune min dunihi illa esmaen semmeytumuha entum ve abaukum ma enzelallahu biha min sultan, inil hukmu illa lillah, emere ella ta'budu illa iyyah, zaliked dinul kayyimu ve lakinne ekseren nasi la ya'lemun."},
  {id: 1637, sure_no: 41, ayet_no: 41, sure_adi: "Yusuf", text: "يَا صَاحِبَيِ السِّجْنِ اَمَّٓا اَحَدُكُمَا فَيَسْق۪ي رَبَّهُ خَمْراًۚ وَاَمَّا الْاٰخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَأْسِه۪ۜ قُضِيَ الْاَمْرُ الَّذ۪ي ف۪يهِ تَسْتَفْتِيَانِۜ", translation: "\"Ey zindan arkadaşlarım! Biriniz rabbine yine içki sunacak, biriniz ise asılacak ve kuşlar onun başından yiyecek. Bana sorduğunuz rüyanın gerçekleşecek yorumu budur.\"", transliteration: "Ya sahıbeyis sicni emma ehadukuma fe yeski rabbehu hamra, ve emmel aharu fe yuslebu fe te'kulut tayru min re'sih, kudiyel emrullezi fihi testeftiyan."},
  {id: 1638, sure_no: 42, ayet_no: 42, sure_adi: "Yusuf", text: "وَقَالَ لِلَّذ۪ي ظَنَّ اَنَّهُ نَاجٍ مِنْهُمَا اذْكُرْن۪ي عِنْدَ رَبِّكَۘ فَاَنْسٰيهُ الشَّيْطَانُ ذِكْرَ رَبِّه۪ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِن۪ينَۜ ۟", translation: "O ikisinden, kurtulacağını umduğu kimseye dedi ki: \"Rabb'inin yanında beni an.\" Ne var ki şeytan, ona rabbine Yusuf'dan söz etmeyi unutturdu. O da nice yıllar zindanda kaldı.", transliteration: "Ve kale lillezi zanne ennehu nacin minhumazkurni inde rabbike fe ensahuş şeytanu zikre rabbihi fe lebise fis sicni bid'a sinin."},
  {id: 1639, sure_no: 43, ayet_no: 43, sure_adi: "Yusuf", text: "وَقَالَ الْمَلِكُ اِنّ۪ٓي اَرٰى سَبْعَ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعَ سُنْبُلَاتٍ خُضْرٍ وَاُخَرَ يَابِسَاتٍۜ يَٓا اَيُّهَا الْمَلَأُ اَفْتُون۪ي ف۪ي رُءْيَايَ اِنْ كُنْتُمْ لِلرُّءْيَا تَعْبُرُونَ", translation: "Derken Hükümdar dedi ki: \"Doğrusu ben rüyamda yedi besili sığırı görüyorum; bunları yedi zayıf sığır yiyor. Ve yedi yeşil ve kuru başak görüyorum. Ey meleler! Eğer rüya tabir etmeyi biliyorsanız rüyamı yorumlayın.\"", transliteration: "Ve kalel meliku inni era seb'a bakaratin simanin ye'kuluhunne seb'un icafun ve seb'a sunbulatin hudrin ve uhara yabisat , ya eyyuhel meleu eftuni fi ru'yaye in kuntum lir ru'ya ta'burun."},
  {id: 1640, sure_no: 44, ayet_no: 44, sure_adi: "Yusuf", text: "قَالُٓوا اَضْغَاثُ اَحْلَامٍۚ وَمَا نَحْنُ بِتَأْو۪يلِ الْاَحْلَامِ بِعَالِم۪ينَ", translation: "\"Bunlar, karışık rüyalardır. Biz böyle rüyaların yorumunu bilenlerden değiliz.\" dediler.", transliteration: "Kalu adgasu ahlam, ve ma nahnu bi te'vilil ahlami bi alimin."},
  {id: 1641, sure_no: 45, ayet_no: 45, sure_adi: "Yusuf", text: "وَقَالَ الَّذ۪ي نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ اُمَّةٍ اَنَا۬ اُنَبِّئُكُمْ بِتَأْو۪يلِه۪ فَاَرْسِلُونِ", translation: "Ancak o zaman o iki kişiden kurtulmuş olanı, aradan geçen bunca zamandan sonra hatırladı. \"Beni hemen gönderin, rüyanın yorumunu ben size söyleyeceğim.\"", transliteration: "Ve kalellezi neca minhuma veddekere ba'de ummetin ene unebbiukum bi te'vilihi fe ersilun."},
  {id: 1642, sure_no: 46, ayet_no: 46, sure_adi: "Yusuf", text: "يُوسُفُ اَيُّهَا الصِّدّ۪يقُ اَفْتِنَا ف۪ي سَبْعِ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعِ سُنْبُلَاتٍ خُضْرٍ وَاُخَرَ يَابِسَاتٍۙ لَعَلّ۪ٓي اَرْجِعُ اِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُونَ", translation: "\"Ey doğru sözlü Yusuf! Yedi besili sığırı yiyen, yedi zayıf sığır ile yedi yeşil başak ve kuru başağın ne anlama geldiğini bize açıkla. Döndüğümde insanlara anlatayım da böylece onlar senin değerini öğrenmiş olurlar.\"", transliteration: "Yusufu eyyuhes sıddiku eftina fi seb'ı bakaratin simanin ye'kuluhunne seb'un icafun ve seb'ı sunbulatin hudrin ve uhare yabisatin, lealli erciu ilen nasi leallehum ya'lemun."},
  {id: 1643, sure_no: 47, ayet_no: 47, sure_adi: "Yusuf", text: "قَالَ تَزْرَعُونَ سَبْعَ سِن۪ينَ دَاَباًۚ فَمَا حَصَدْتُمْ فَذَرُوهُ ف۪ي سُنْبُلِه۪ٓ اِلَّا قَل۪يلاً مِمَّا تَأْكُلُونَ", translation: "Dedi ki: \"Yedi yıl bildik şekilde ekip biçin. Ancak biçtiğinizden, yiyeceğiniz kadarını ayırdıktan sonra kalanı biriktirin.\"", transliteration: "Kale tezreune seb'a sinine de'eba, fe ma hasadtum fe zeruhu fi sunbulihi illa kalilen mimma te'kulun."},
  {id: 1644, sure_no: 48, ayet_no: 48, sure_adi: "Yusuf", text: "ثُمَّ يَأْت۪ي مِنْ بَعْدِ ذٰلِكَ سَبْعٌ شِدَادٌ يَأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ اِلَّا قَل۪يلاً مِمَّا تُحْصِنُونَ", translation: "\"Sonra yedi yıllık bir kıtlık dönemi gelecek, önceden biriktirdiğinizin az bir miktarı hariç tamamı yenip bitirilecek.\"", transliteration: "Summe ye'ti min ba'di zalike seb'un şidadun ye'kulne ma kaddemtum lehunne illa kalilen mimma tuhsinun."},
  {id: 1645, sure_no: 49, ayet_no: 49, sure_adi: "Yusuf", text: "ثُمَّ يَأْت۪ي مِنْ بَعْدِ ذٰلِكَ عَامٌ ف۪يهِ يُغَاثُ النَّاسُ وَف۪يهِ يَعْصِرُونَ۟", translation: "\"Bu sıkıntının ardından, bol yağmurlu bir yıl gelecek. Ve insanlar onda sıkıp sağacaklar.\"", transliteration: "Summe ye'ti min ba'di zalike amun fihi yugasun nasu ve fihi ya'sırun."},
  {id: 1646, sure_no: 50, ayet_no: 50, sure_adi: "Yusuf", text: "وَقَالَ الْمَلِكُ ائْتُون۪ي بِه۪ۚ فَلَمَّا جَٓاءَهُ الرَّسُولُ قَالَ ارْجِعْ اِلٰى رَبِّكَ فَسْـَٔلْهُ مَا بَالُ النِّسْوَةِ الّٰت۪ي قَطَّعْنَ اَيْدِيَهُنَّۜ اِنَّ رَبّ۪ي بِكَيْدِهِنَّ عَل۪يمٌ", translation: "Hükümdar: \"Onu bana getirin.\" dedi. Hükümdarın elçisi ona gelince, rabbine dön ve ona, ellerini kesen kadınların durumunu sor. Kuşkusuz Rabb'im onların hilesini bilendir.\"", transliteration: "Ve kalel meliku'tuni bih, fe lemma caehur resulu kalerci' ila rabbike fes'elhu ma balun nisvetillati katta'ne eydiyehunn, inne rabbi bi keydihinne alim."},
  {id: 1647, sure_no: 51, ayet_no: 51, sure_adi: "Yusuf", text: "قَالَ مَا خَطْبُكُنَّ اِذْ رَاوَدْتُنَّ يُوسُفَ عَنْ نَفْسِه۪ۜ قُلْنَ حَاشَ لِلّٰهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُٓوءٍۜ قَالَتِ امْرَاَتُ الْعَز۪يزِ الْـٰٔنَ حَصْحَصَ الْحَقُّۘ اَنَا۬ رَاوَدْتُهُ عَنْ نَفْسِه۪ وَاِنَّهُ لَمِنَ الصَّادِق۪ينَ", translation: "Hükümdar: \"Yusuf'tan murat almak istediğinizde ondan nasıl bir karşılık aldınız?\" dedi. Kadınlar: \"Haşa! Allah için onun bir kötülüğünü bilmiyoruz.\" dediler. Aziz'in hanımı: \"Şimdi gerçek ortaya çıktı, ona ben sahip olmaya kalkıştım, o kesinlikle doğru söyleyenlerdendir.\" dedi.", transliteration: "Kale ma hatbukunne iz ravedtunne yusufe an nefsih, kulne haşe lillahi ma alimna aleyhi min su', kaletimre'etul azizil ane hashasal hakku ene ravedtuhu an nefsihi ve innehu le mines sadikin."},
  {id: 1648, sure_no: 52, ayet_no: 52, sure_adi: "Yusuf", text: "ذٰلِكَ لِيَعْلَمَ اَنّ۪ي لَمْ اَخُنْهُ بِالْغَيْبِ وَاَنَّ اللّٰهَ لَا يَهْد۪ي كَيْدَ الْخَٓائِن۪ينَ", translation: "\"Bu, gıyabında ona ihanet etmediğimi ve Allah'ın hainlerin planlarını başarılı kılmadığını bilmesi içindir.\"", transliteration: "Zalike li ya'leme enni lem ehunhu bil gaybi ve ennallahe la yehdi keydel hainin."},
  {id: 1649, sure_no: 53, ayet_no: 53, sure_adi: "Yusuf", text: "وَمَٓا اُبَرِّئُ نَفْس۪يۚ اِنَّ النَّفْسَ لَاَمَّارَةٌ بِالسُّٓوءِ اِلَّا مَا رَحِمَ رَبّ۪يۜ اِنَّ رَبّ۪ي غَفُورٌ رَح۪يمٌ", translation: "\"Ben kendimi temize çıkarmam. Rabb'imin rahmet ettiği kimse hariç nefis her zaman kötülüğü ister. Rabb'im Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.\"", transliteration: "Ve ma uberriu nefsi, innen nefse le emmaretun bis suı illa ma rahime rabbi, inne rabbi gafurun rahim."},
  {id: 1650, sure_no: 54, ayet_no: 54, sure_adi: "Yusuf", text: "وَقَالَ الْمَلِكُ ائْتُون۪ي بِه۪ٓ اَسْتَخْلِصْهُ لِنَفْس۪يۚ فَلَمَّا كَلَّمَهُ قَالَ اِنَّكَ الْيَوْمَ لَدَيْنَا مَك۪ينٌ اَم۪ينٌ", translation: "Ve Hükümdar: \"Onu bana getirin. Onu, yanımda seçkin bir kimse yapayım.\" dedi. Onunla görüştüğünde de: \"Sen bundan böyle yanımızda itibarlısın ve güvendesin.\" dedi.", transliteration: "Ve kalel meliku'tuni bihi estahlishu li nefsi, fe lemma kellemehu kale innekel yevme ledeyna mekinun emin."},
  {id: 1651, sure_no: 55, ayet_no: 55, sure_adi: "Yusuf", text: "قَالَ اجْعَلْن۪ي عَلٰى خَزَٓائِنِ الْاَرْضِۚ اِنّ۪ي حَف۪يظٌ عَل۪يمٌ", translation: "Yusuf: \"Beni ülkenin hazinelerinin başına tayin et. Zira ben onları korurum ve iyi bilirim.\" dedi.", transliteration: "Kalec'alni ala hazainil ard, inni hafizun alim."},
  {id: 1652, sure_no: 56, ayet_no: 56, sure_adi: "Yusuf", text: "وَكَذٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْاَرْضِۚ يَتَبَوَّاُ مِنْهَا حَيْثُ يَشَٓاءُۜ نُص۪يبُ بِرَحْمَتِنَا مَنْ نَشَٓاءُ وَلَا نُض۪يعُ اَجْرَ الْمُحْسِن۪ينَ", translation: "Ve böylece Yusuf'u yetki sahibi yaptık, istediği yerde mekan tutma imkanı verdik. Rahmetimizi istediğimize veririz. İyilik edenlerin yaptıklarını karşılıksız bırakmayız.", transliteration: "Ve kezalike mekkenna li yusufe fil ard, yetebevveu minha haysu yeşa', nusibu bi rahmetina men neşau ve la nudiu ecrel muhsinin."},
  {id: 1653, sure_no: 57, ayet_no: 57, sure_adi: "Yusuf", text: "وَلَاَجْرُ الْاٰخِرَةِ خَيْرٌ لِلَّذ۪ينَ اٰمَنُوا وَكَانُوا يَتَّقُونَ۟", translation: "Ancak iman eden ve takva sahibi olan kimseler için ahiretteki ödül hayırlı olandır.", transliteration: "Ve le ecrul ahıreti hayrun lillezine amenu ve kanu yettekun."},
  {id: 1654, sure_no: 58, ayet_no: 58, sure_adi: "Yusuf", text: "وَجَٓاءَ اِخْوَةُ يُوسُفَ فَدَخَلُوا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهُ مُنْكِرُونَ", translation: "Nihayet Yusuf'un kardeşleri geldi ve onun huzuruna çıktılar. Yusuf onları hemen tanıdı. Ama onlar onu tanıyamadılar.", transliteration: "Ve cae ihvetu yusufe fe dehalu aleyhi fe arefehum ve hum lehu munkirun."},
  {id: 1655, sure_no: 59, ayet_no: 59, sure_adi: "Yusuf", text: "وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُون۪ي بِاَخٍ لَكُمْ مِنْ اَب۪يكُمْۚ اَلَا تَرَوْنَ اَنّ۪ٓي اُو۫فِي الْكَيْلَ وَاَنَا۬ خَيْرُ الْمُنْزِل۪ينَ", translation: "Yüklerini yüklettikten sonra, kendilerine: \"Baba bir kardeşinizi de bana getirin. Görüyorsunuz ki ihtiyacınızı fazlasıyla veriyorum ve ben iyi bir misafirperverim.\" dedi.", transliteration: "Ve lemma cehhezehum bi cehazihim kale'tuni bi ahin lekum min ebikum, e la terevne enni ufil keyle ve ene hayrul munzilin."},
  {id: 1656, sure_no: 60, ayet_no: 60, sure_adi: "Yusuf", text: "فَاِنْ لَمْ تَأْتُون۪ي بِه۪ فَلَا كَيْلَ لَكُمْ عِنْد۪ي وَلَا تَقْرَبُونِ", translation: "\"Buna rağmen eğer o kardeşinizi bana getirmezseniz, size hiçbir şey vermem ve asla yanıma da yaklaşmayın.\"", transliteration: "Fe in lem te'tuni bihi fe la keyle lekum indi ve la takrebun."},
  {id: 1657, sure_no: 61, ayet_no: 61, sure_adi: "Yusuf", text: "قَالُوا سَنُرَاوِدُ عَنْهُ اَبَاهُ وَاِنَّا لَفَاعِلُونَ", translation: "\"Babasını ikna etmeye çalışacağız. Bunu mutlaka yapacağız.\" dediler.", transliteration: "Kalu senuravidu anhu ebahu ve inna le fa'ilun."},
  {id: 1658, sure_no: 62, ayet_no: 62, sure_adi: "Yusuf", text: "وَقَالَ لِفِتْيَانِهِ اجْعَلُوا بِضَاعَتَهُمْ ف۪ي رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُونَـهَٓا اِذَا انْقَلَـبُٓوا اِلٰٓى اَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَ", translation: "Yusuf adamlarına: \"Verdiklerini, yüklerinin içine koyun. Umulur ki ailelerine döndüklerinde bunu görür ve geri gelirler.\" dedi.", transliteration: "Ve kale li fityanihic'alu bidaatehum fi rihalihim leallehum ya'rifuneha izenkalebu ila ehlihim leallehum yerci'un."},
  {id: 1659, sure_no: 63, ayet_no: 63, sure_adi: "Yusuf", text: "فَلَمَّا رَجَعُٓوا اِلٰٓى اَب۪يهِمْ قَالُوا يَٓا اَبَانَا مُنِـعَ مِنَّا الْكَيْلُ فَاَرْسِلْ مَعَنَٓا اَخَانَا نَكْتَلْ وَاِنَّا لَهُ لَحَافِظُونَ", translation: "Babalarının yanına vardıklarında: \"Ey babamız! Kardeşimizi bizimle birlikte gönder ki tahıl alalım. Yoksa bize tahıl verilmeyecek. Onu mutlaka koruruz.\" dediler.", transliteration: "Fe lemma receu ila ebihim kalu ya ebana munia minnel keylu fe ersil meana ehana nektel ve inna lehu le hafizun."},
  {id: 1660, sure_no: 64, ayet_no: 64, sure_adi: "Yusuf", text: "قَالَ هَلْ اٰمَنُكُمْ عَلَيْهِ اِلَّا كَمَٓا اَمِنْتُكُمْ عَلٰٓى اَخ۪يهِ مِنْ قَبْلُۜ فَاللّٰهُ خَيْرٌ حَافِظاًۖ وَهُوَ اَرْحَمُ الرَّاحِم۪ينَ", translation: "\"Size güvenir miyim hiç? Bundan önce kardeşiniz hakkında da size güvenmiştim! Ama en iyi koruyucu Allah'tır ve O, merhamet edenlerin en merhametlisidir.\" dedi.", transliteration: "Kale hel amenukum aleyhi illa kema emintukum ala ahihi min kabl, fallahu hayrun hafiza ve huve erhamur rahimin."},
  {id: 1661, sure_no: 65, ayet_no: 65, sure_adi: "Yusuf", text: "وَلَمَّا فَتَحُوا مَتَاعَهُمْ وَجَدُوا بِضَاعَتَهُمْ رُدَّتْ اِلَيْهِمْۜ قَالُوا يَٓا اَبَانَا مَا نَبْغ۪يۜ هٰذِه۪ بِضَاعَتُنَا رُدَّتْ اِلَيْنَاۚ وَنَم۪يرُ اَهْلَنَا وَنَحْفَظُ اَخَانَا وَنَزْدَادُ كَيْلَ بَع۪يرٍۜ ذٰلِكَ كَيْلٌ يَس۪يرٌ", translation: "Tahıl yüklerini açtıklarında verdikleri bedelin kendilerine geri verilmiş olduğunu gördüler. \"Ey babamız! Bak sermayemiz bize geri verilmiş. Daha ne istiyoruz! Onunla da kendimize tahıl getiririz. Kardeşimizi de koruruz. Fazladan bir deve yükü daha alırız. Zaten bu bize yetmez.\" dediler.", transliteration: "Ve lemma fetehu metaahum vecedu bidaatehum ruddet ileyhim, kalu ya ebana ma nebgi, hazihi bidaatuna ruddet ileyna, ve nemiru ehlena ve nahfazu ehana ve nezdadu keyle beir , zalike keylun yesir."},
  {id: 1662, sure_no: 66, ayet_no: 66, sure_adi: "Yusuf", text: "قَالَ لَنْ اُرْسِلَهُ مَعَكُمْ حَتّٰى تُؤْتُونِ مَوْثِقاً مِنَ اللّٰهِ لَتَأْتُنَّن۪ي بِه۪ٓ اِلَّٓا اَنْ يُحَاطَ بِكُمْۚ فَلَمَّٓا اٰتَوْهُ مَوْثِقَهُمْ قَالَ اللّٰهُ عَلٰى مَا نَقُولُ وَك۪يلٌ", translation: "\"Saldırıya uğrayıp çaresiz kalmadıkça, kesinlikle onu bana getireceğinize dair Allah adına sağlam bir söz vermedikçe, onu sizinle asla göndermem.\" dedi. Onlar, söz verince: \"Allah söylediklerimize vekildir.\" dedi.", transliteration: "Kale len ursilehu meakum hatta tu'tuni mevsikan minallahi le te'tunneni bihi illa en yuhata bikum, fe lemma atevhu mevsikahum kalallahu ala ma nekulu vekil."},
  {id: 1663, sure_no: 67, ayet_no: 67, sure_adi: "Yusuf", text: "وَقَالَ يَا بَنِيَّ لَا تَدْخُلُوا مِنْ بَابٍ وَاحِدٍ وَادْخُلُوا مِنْ اَبْوَابٍ مُتَفَرِّقَةٍۜ وَمَٓا اُغْن۪ي عَنْكُمْ مِنَ اللّٰهِ مِنْ شَيْءٍۜ اِنِ الْحُكْمُ اِلَّا لِلّٰهِۜ عَلَيْهِ تَوَكَّلْتُۚ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ", translation: "Ve \"Ey oğullarım! Aynı kapıdan girmeyin, her biriniz ayrı bir kapıdan girin. Allah'ın takdirine karşı size bir faydam olmaz. Hüküm yalnızca Allah'ındır. Ben O'na tevekkül ettim. Tevekkül edecek olanlar O'na tevekkül etsinler.\" dedi.", transliteration: "Ve kale ya beniyye la tedhulu min babin vahidin vedhulu min ebvabin muteferrikah, ve ma ugni ankum minallahi min şey inil hukmu illa lillah, aleyhi tevekkeltu ve aleyhi fel yetevekkelil mutevekkilun."},
  {id: 1664, sure_no: 68, ayet_no: 68, sure_adi: "Yusuf", text: "وَلَمَّا دَخَلُوا مِنْ حَيْثُ اَمَرَهُمْ اَبُوهُمْۜ مَا كَانَ يُغْن۪ي عَنْهُمْ مِنَ اللّٰهِ مِنْ شَيْءٍ اِلَّا حَاجَةً ف۪ي نَفْسِ يَعْقُوبَ قَضٰيهَاۜ وَاِنَّهُ لَذُو عِلْمٍ لِمَا عَلَّمْنَاهُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ۟", translation: "Babalarının istediği şekilde girdiler. Ya'kub, eğer Allah, haklarında bir şey takdir etmiş olsaydı, içinden gelen bu dileğin onlara fayda vermeyeceğini bilmekteydi. O, kendisine öğrettiğimiz için ilim sahibiydi. Ancak insanların çoğu bilmezler.", transliteration: "Ve lemma dehalu min haysu emerehum ebuhum, ma kane yugni anhum minallahi min şey'in illa haceten fi nefsi ya'kube kadaha, ve innehu le zu ilmin lima allemnahu ve lakinne ekseren nasi la ya'lemun."},
  {id: 1665, sure_no: 69, ayet_no: 69, sure_adi: "Yusuf", text: "وَلَمَّا دَخَلُوا عَلٰى يُوسُفَ اٰوٰٓى اِلَيْهِ اَخَاهُ قَالَ اِنّ۪ٓي اَنَا۬ اَخُوكَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَعْمَلُونَ", translation: "Kardeşleri Yusuf'un yanına girince, o, öz kardeşini yanına alarak, \"Ben senin öz kardeşinim, onların yapmış oldukları şeylerden dolayı artık üzülme.\" dedi.", transliteration: "Ve lemma dehalu ala yusufe ava ileyhi ehahu, kale inni ene ehuke fe la tebteis bima kanu ya'melun."},
  {id: 1666, sure_no: 70, ayet_no: 70, sure_adi: "Yusuf", text: "فَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ ف۪ي رَحْلِ اَخ۪يهِ ثُمَّ اَذَّنَ مُؤَذِّنٌ اَيَّتُهَا الْع۪يرُ اِنَّكُمْ لَسَارِقُونَ", translation: "Yüklerini yükletirken su kabını öz kardeşinin yükü içine koydurttu. Sonra bir çağırıcı: \"Ey kervancılar! Siz hırsız kimselersiniz.\" diye seslendi.", transliteration: "Fe lemma cehhezehum bi cehazihim ceales sikayete fi rahli ahihi, summe ezzene muezzinun eyyetuhel iru innekum le sarikun."},
  {id: 1667, sure_no: 71, ayet_no: 71, sure_adi: "Yusuf", text: "قَالُوا وَاَقْبَلُوا عَلَيْهِمْ مَاذَا تَفْقِدُونَ", translation: "Döndüler ve çağırıcıya, \"Ne kaybettiniz?\" dediler.", transliteration: "Kalu ve akbelu aleyhim maza tefkidun."},
  {id: 1668, sure_no: 72, ayet_no: 72, sure_adi: "Yusuf", text: "قَالُوا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَٓاءَ بِه۪ حِمْلُ بَع۪يرٍ وَاَنَا۬ بِه۪ زَع۪يمٌ", translation: "\"Hükümdarın su kabını arıyoruz. Onu bulana bir deve yükü ödül var.\" dediler. Çağırıcı: \"Ben buna garanti veriyorum.\" dedi.", transliteration: "Kalu nefkıdu suvaalmeliki ve li men cae bihi hımlu beirin ve ene bihi za'im."},
  {id: 1669, sure_no: 73, ayet_no: 73, sure_adi: "Yusuf", text: "قَالُوا تَاللّٰهِ لَقَدْ عَلِمْتُمْ مَا جِئْنَا لِنُفْسِدَ فِي الْاَرْضِ وَمَا كُنَّا سَارِق۪ينَ", translation: "Yusuf'un kardeşleri: \"Vallahi buraya bozgunculuk yapmaya gelmedik; hırsız olmadığımızı da kesin olarak biliyorsunuz.\" dediler.", transliteration: "Kalu tallahi lekad alimtum ma ci'na li nufside fil ardı ve ma kunna sarikin."},
  {id: 1670, sure_no: 74, ayet_no: 74, sure_adi: "Yusuf", text: "قَالُوا فَمَا جَزَٓاؤُ۬هُٓ اِنْ كُنْتُمْ كَاذِب۪ينَ", translation: "\"Peki! Eğer yalan söylüyorsanız bunun cezasının ne olduğunu biliyor musunuz?\" dediler.", transliteration: "Kalu fe ma cezauhu in kuntum kazibin."},
  {id: 1671, sure_no: 75, ayet_no: 75, sure_adi: "Yusuf", text: "قَالُوا جَزَٓاؤُ۬هُ مَنْ وُجِدَ ف۪ي رَحْلِه۪ فَهُوَ جَزَٓاؤُ۬هُۜ  كَذٰلِكَ نَجْزِي الظَّالِم۪ينَ", translation: "\"Su kabı kimin yükünde bulunursa, bunun cezası, yük sahibinin kendisidir. Biz haksızları böyle cezalandırırız.\" dediler.", transliteration: "Kalu cezauhu men vucide fi rahlihi fe huve cezauh, kezalike necziz zalimin."},
  {id: 1672, sure_no: 76, ayet_no: 76, sure_adi: "Yusuf", text: "فَبَدَاَ بِاَوْعِيَتِهِمْ قَبْلَ وِعَٓاءِ اَخ۪يهِ ثُمَّ اسْتَخْرَجَهَا مِنْ وِعَٓاءِ اَخ۪يهِۜ كَذٰلِكَ كِدْنَا لِيُوسُفَۜ مَا كَانَ لِيَأْخُذَ اَخَاهُ ف۪ي د۪ينِ الْمَلِكِ اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُۜ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَٓاءُۜ وَفَوْقَ كُلِّ ذ۪ي عِلْمٍ عَل۪يمٌ", translation: "Bunun üzerine, kardeşinin yükünden önce diğerlerinin yükünü aramaya başladı. Sonra su kabını öz kardeşinin yükünden bulup çıkardı. İşte Yusuf için böyle bir tedbir aldık. Allah, böyle dilemeseydi hükümdarın dinine göre kardeşini alıkoyamayacaktı. Dilediğimizi derecelerle yükseltiriz. Her bilenin üstünde bir bilen vardır.", transliteration: "Fe bedee bi ev'ıyetihim kable viai ahihi, summestahreceha min viai ahih, kezalike kidna li yusuf, ma kane li ye'huze ehahu fi dinil meliki, illa en yeşaallah, nerfeu derecatin men neşa', ve fevka kulli zi ilmin alim."},
  {id: 1673, sure_no: 77, ayet_no: 77, sure_adi: "Yusuf", text: "قَالُٓوا اِنْ يَسْرِقْ فَقَدْ سَرَقَ اَخٌ لَهُ مِنْ قَبْلُۚ فَاَسَرَّهَا يُوسُفُ ف۪ي نَفْسِه۪ وَلَمْ يُبْدِهَا لَهُمْ قَالَ اَنْتُمْ شَرٌّ مَكَاناًۚ وَاللّٰهُ اَعْلَمُ بِمَا تَصِفُونَ", translation: "\"Eğer o çalmışsa, zaten daha önce kardeşi de çalmıştı.\" dediler. Yusuf, içinden geçeni onlara açmadı. Kendi kendine, \"Asıl kötü olan sizlersiniz ve anlattığınız şeyin iç yüzünü Allah biliyor.\" dedi.", transliteration: "Kalu in yesrık fe kad sereka ehun lehu min kabl, fe eserreha yusufu fi nefsihi ve lem yubdiha lehum kale entum şerrun mekana, vallahu a'lemu bima tesifun."},
  {id: 1674, sure_no: 78, ayet_no: 78, sure_adi: "Yusuf", text: "قَالُوا يَٓا اَيُّهَا الْعَز۪يزُ اِنَّ لَـهُٓ اَباً شَيْخاً كَب۪يراً فَخُذْ اَحَدَنَا مَكَانَهُۚ اِنَّا نَرٰيكَ مِنَ الْمُحْسِن۪ينَ", translation: "Kardeşleri: \"Ey soylu Aziz! Emin ol ki, bunun çok yaşlı bir babası var. Onun yerine bizden birini alıkoy. Senin iyilik edenlerden olduğunu görüyoruz.\" dediler.", transliteration: "Kalu ya eyyuhel azizu inne lehu eben şeyhan kebiren fe huz ehadena mekaneh, inna nerake minel muhsinin."},
  {id: 1675, sure_no: 79, ayet_no: 79, sure_adi: "Yusuf", text: "قَالَ مَعَاذَ اللّٰهِ اَنْ نَأْخُذَ اِلَّا مَنْ وَجَدْنَا مَتَاعَنَا عِنْدَهُٓۙ اِنَّٓا اِذاً لَظَالِمُونَ۟", translation: "\"Eşyamızı yanında bulduğumuz kimseden başkasını alıkoymaktan Allah'a sığınırız. Yoksa haksızlık etmiş oluruz.\" dedi.", transliteration: "Kale maazallahi en ne'huze illa men vecedna metaana indehu inna izen le zalimun."},
  {id: 1676, sure_no: 80, ayet_no: 80, sure_adi: "Yusuf", text: "فَلَمَّا اسْتَيْـَٔسُوا مِنْهُ خَلَصُوا نَجِياًّۜ قَالَ كَب۪يرُهُمْ اَلَمْ تَعْلَمُٓوا اَنَّ اَبَاكُمْ قَدْ اَخَذَ عَلَيْكُمْ مَوْثِقاً مِنَ اللّٰهِ وَمِنْ قَبْلُ مَا فَرَّطْتُمْ ف۪ي يُوسُفَۚ فَلَنْ اَبْرَحَ الْاَرْضَ حَتّٰى يَأْذَنَ ل۪ٓي اَب۪ٓي اَوْ يَحْكُمَ اللّٰهُ ل۪يۚ وَهُوَ خَيْرُ الْحَاكِم۪ينَ", translation: "Ondan ümitlerini kesince, kendi aralarında konuşmaya başladılar. Büyükleri: \"Babanızın Allah adına sizden söz aldığını ve daha önce Yusuf konusunda da sınırı aşmış olduğunuzu bilmez misiniz? Babam izin verinceye veya Allah hakkımda hüküm verinceye kadar buradan ayrılmam. O, hüküm verenlerin en iyisidir.\" dedi.", transliteration: "Fe lemmestey'esu minhu halesu neciyya, kale kebiruhum e lem ta'lemu enne ebakum kad ehaze aleykum mevsikan minallahi ve min kablu ma ferrattum fi yusuf, fe len ebrahal arda hatta ye'zene li ebi ev yahkumallahu li ve huve hayrul hakimin."},
  {id: 1677, sure_no: 81, ayet_no: 81, sure_adi: "Yusuf", text: "اِرْجِعُٓوا اِلٰٓى اَب۪يكُمْ فَقُولُوا يَٓا اَبَانَٓا اِنَّ ابْنَكَ سَرَقَۚ وَمَا شَهِدْنَٓا اِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حَافِظ۪ينَ", translation: "\"Siz dönün! Babanıza deyin ki: Ey babamız! İnan ki oğlun hırsızlık yaptı. Biz bildiğimizden başka bir şeye tanıklık etmedik; işin iç yüzünü de bilmiyoruz.\"", transliteration: "Irciu ila ebikum fe kulu ya ebana innebneke serak, ve ma şehidna illa bima alimna ve ma kunna lil gaybi hafizin."},
  {id: 1678, sure_no: 82, ayet_no: 82, sure_adi: "Yusuf", text: "وَسْـَٔلِ الْقَرْيَةَ الَّت۪ي كُنَّا ف۪يهَا وَالْع۪يرَ الَّت۪ٓي اَقْبَلْنَا ف۪يهَاۜ وَاِنَّا لَصَادِقُونَ", translation: "\"Gittiğimiz kasabanın halkına ve beraberinde olduğumuz kervana da sor. Kuşkusuz biz doğru söylüyoruz.\"", transliteration: "Ves'elil karyetelleti kunna fiha vel irelleti akbelna fiha, ve inna le sadikun."},
  {id: 1679, sure_no: 83, ayet_no: 83, sure_adi: "Yusuf", text: "قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْراًۜ فَصَبْرٌ جَم۪يلٌۜ عَسَى اللّٰهُ اَنْ يَأْتِيَن۪ي بِهِمْ جَم۪يعاًۜ اِنَّهُ هُوَ الْعَل۪يمُ الْحَك۪يمُ", translation: "\"Hayır! Nefsiniz bu işte sizi aldatmış. Artık bana düşen güzelce sabretmektir. Umarım ki Allah hepsini birden bana kavuşturur. Gerçek şu ki O, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.\"", transliteration: "Kale bel sevvelet lekum enfusukum emra, fe sabrun cemil, asallahu en ye'tiyeni bihim cemi'a, innehu huvel alimul hakim."},
  {id: 1680, sure_no: 84, ayet_no: 84, sure_adi: "Yusuf", text: "وَتَوَلّٰى عَنْهُمْ وَقَالَ يَٓا اَسَفٰى عَلٰى يُوسُفَ وَابْيَضَّتْ عَيْنَاهُ مِنَ الْحُزْنِ فَهُوَ كَظ۪يمٌ", translation: "Onlardan yüz çevirdi. Ve \"Vah! Vah! Yusuf'um!\" dedi. Üzüntüden gözleri ağardı. Yutkundukça yutkunuyordu.", transliteration: "Ve tevella anhum ve kale ya esefa ala yusufe vebyaddat aynahu minel huzni fe huve kezim."},
  {id: 1681, sure_no: 85, ayet_no: 85, sure_adi: "Yusuf", text: "قَالُوا تَاللّٰهِ تَفْتَؤُ۬ا تَذْكُرُ يُوسُفَ حَتّٰى تَكُونَ حَرَضاً اَوْ تَكُونَ مِنَ الْهَالِك۪ينَ", translation: "\"Yusuf'u anmaya devam edersen, vallahi sonunda üzüntünden ya hasta olacaksın veya öleceksin.\" dediler.", transliteration: "Kalu tallahi tefteu tezkuru yusufe hatta tekune haradan ev tekune minel halikin."},
  {id: 1682, sure_no: 86, ayet_no: 86, sure_adi: "Yusuf", text: "قَالَ اِنَّـمَٓا اَشْكُوا بَثّ۪ي وَحُزْن۪ٓي اِلَى اللّٰهِ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "\"Gönlümden geçenleri ve kederimi yalnızca Allah'a arz ediyorum. Ve Allah tarafından bilmeyeceğiniz şeyleri bilirim.\" dedi.", transliteration: "Kale innema eşku bessi ve huzni ilallahi ve a'lemu inallahi ma la ta'lemun."},
  {id: 1683, sure_no: 87, ayet_no: 87, sure_adi: "Yusuf", text: "يَا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِنْ يُوسُفَ وَاَخ۪يهِ وَلَا تَايْـَٔسُوا مِنْ رَوْحِ اللّٰهِۜ اِنَّهُ لَا يَايْـَٔسُ مِنْ رَوْحِ اللّٰهِ اِلَّا الْقَوْمُ الْكَافِرُونَ", translation: "\"Ey oğullarım! Gidin Yusuf'u ve kardeşini araştırın. Allah'ın rahmetinden umut kesmeyin; çünkü kafir bir halktan başkası O'nun rahmetinden umudunu kesmez.\"", transliteration: "Ya beniyyezhebu fe tehassesu min yusufe ve ehihi ve la te'yesu min revhillah, innehu la ye'yesu min revhillahi illel kavmul kafirun."},
  {id: 1684, sure_no: 88, ayet_no: 88, sure_adi: "Yusuf", text: "فَلَمَّا دَخَلُوا عَلَيْهِ قَالُوا يَٓا اَيُّهَا الْعَز۪يزُ مَسَّنَا وَاَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُزْجٰيةٍ فَاَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَاۜ اِنَّ اللّٰهَ يَجْزِي الْمُتَصَدِّق۪ينَ", translation: "Sonra onun yanına girdiklerinde: \"Ey saygıdeğer Aziz! Bize ve ailemize darlık dokundu. Az bir sermaye ile geldik. Bize tam ölçek ver ve bize tasaddukta bulun. Kuşkusuz, Allah tasadduk edenlerin ödülünü verir.\" dediler.", transliteration: "Fe lemma dehalu aleyhi kalu ya eyyuhel azizu messena ve ehlened durru ve ci'na bi bidaatin muzcatin fe evfi lenel keyle ve tesaddak aleyna, innallahe yeczil mutesaddikin."},
  {id: 1685, sure_no: 89, ayet_no: 89, sure_adi: "Yusuf", text: "قَالَ هَلْ عَلِمْتُمْ مَا فَعَلْتُمْ بِيُوسُفَ وَاَخ۪يهِ اِذْ اَنْتُمْ جَاهِلُونَ", translation: "\"Yusuf'a ve kardeşine cahilce ne yaptığınızı biliyor musunuz?\" dedi.", transliteration: "Kale hel alimtum ma fealtum bi yusufe ve ahihi iz entum cahilun."},
  {id: 1686, sure_no: 90, ayet_no: 90, sure_adi: "Yusuf", text: "قَالُٓوا ءَاِنَّكَ لَاَنْتَ يُوسُفُۜ قَالَ اَنَا۬ يُوسُفُ وَهٰذَٓا اَخ۪يۘ قَدْ مَنَّ اللّٰهُ عَلَيْنَاۜ اِنَّهُ مَنْ يَتَّقِ وَيَصْبِرْ فَاِنَّ اللّٰهَ لَا يُض۪يعُ اَجْرَ الْمُحْسِن۪ينَ", translation: "\"Yoksa sen Yusuf musun?\" dediler. \"Ben Yusuf'um, bu da kardeşim.\" dedi. \"Doğrusu, Allah bizi nimetlendirdi. Çünkü kim takvalı olur ve sabrederse, o zaman Allah, kesinlikle iyilerin yaptığını karşılıksız bırakmaz.\"", transliteration: "Kalu e inneke le ente yusuf, kale ene yusufu ve haza ahi kad mennallahu aleyna, innehu men yettekı ve yasbir fe innallahe la yudi'u ecrel muhsinin."},
  {id: 1687, sure_no: 91, ayet_no: 91, sure_adi: "Yusuf", text: "قَالُوا تَاللّٰهِ لَقَدْ اٰثَرَكَ اللّٰهُ عَلَيْنَا وَاِنْ كُنَّا لَخَاطِـ۪ٔينَ", translation: "Dediler ki: \"Allah'a yemin olsun ki, Allah, seni bize üstün kıldı. Ve biz gerçekten yanlış yapmışız.\"", transliteration: "Kalu tallahi lekad aserekellahu aleyna ve in kunna le hatıin."},
  {id: 1688, sure_no: 92, ayet_no: 92, sure_adi: "Yusuf", text: "قَالَ لَا تَثْر۪يبَ عَلَيْكُمُ الْيَوْمَۜ يَغْفِرُ اللّٰهُ لَكُمْۘ وَهُوَ اَرْحَمُ الرَّاحِم۪ينَ", translation: "Yusuf: \"Bugün, size bir kınama yoktur. Allah, sizi bağışlayıcılığıyla bağışlasın. O, merhametlilerin en merhametlisidir.\" dedi.", transliteration: "Kale la tesribe aleykumul yevm, yagfirullahu lekum ve huve erhamur rahimin."},
  {id: 1689, sure_no: 93, ayet_no: 93, sure_adi: "Yusuf", text: "اِذْهَبُوا بِقَم۪يص۪ي هٰذَا فَاَلْقُوهُ عَلٰى وَجْهِ اَب۪ي يَأْتِ بَص۪يراًۚ وَأْتُون۪ي بِاَهْلِكُمْ اَجْمَع۪ينَ۟", translation: "\"Şu gömleğimi götürün; babamın yüzüne koyun. Görmeye başlayacaktır. Ailenizin tamamını bana getirin.\" dedi.", transliteration: "İzhebu bikamisi haza fe elkuhu ala vechi ebi ye'ti basira, ve'tuni bi ehlikum ecma'in."},
  {id: 1690, sure_no: 94, ayet_no: 94, sure_adi: "Yusuf", text: "وَلَمَّا فَصَلَتِ الْع۪يرُ قَالَ اَبُوهُمْ اِنّ۪ي لَاَجِدُ ر۪يحَ يُوسُفَ لَوْلَٓا اَنْ تُفَنِّدُونِ", translation: "Kafile ayrıldığında babaları: \"Eğer bunamış demezseniz, doğrusu ben gerçekten Yusuf'un kokusunu hissediyorum.\" dedi.", transliteration: "Ve lemma fasalatil'iru kale ebuhum inni le ecidu riha yusufe lev la en tufennidun."},
  {id: 1691, sure_no: 95, ayet_no: 95, sure_adi: "Yusuf", text: "قَالُوا تَاللّٰهِ اِنَّكَ لَف۪ي ضَلَالِكَ الْقَد۪يمِ", translation: "\"Allah'a yemin olsun ki, sen hala eski şaşkınlığına devam ediyorsun.\" dediler.", transliteration: "Kalu tallahi inneke le fi dalalikel kadim."},
  {id: 1692, sure_no: 96, ayet_no: 96, sure_adi: "Yusuf", text: "فَلَمَّٓا اَنْ جَٓاءَ الْبَش۪يرُ اَلْقٰيهُ عَلٰى وَجْهِه۪ فَارْتَدَّ بَص۪يراًۚ قَالَ اَلَمْ اَقُلْ لَكُمْ اِنّ۪ٓي اَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُونَ", translation: "Haberci geldiği zaman, onu yüzüne koyunca gözü hemen görmeye başladı. \"Ben size demedim mi, ben Allah'tan sizin bilmediğiniz şeyleri bilirim.\" dedi.", transliteration: "Fe lemma en cael beşiru elkahu ala vechihi fertedde basira, kale e lem ekul lekum inni a'lemu minallahi ma la ta'lemun."},
  {id: 1693, sure_no: 97, ayet_no: 97, sure_adi: "Yusuf", text: "قَالُوا يَٓا اَبَانَا اسْتَغْفِرْ لَنَا ذُنُوبَنَٓا اِنَّا كُنَّا خَاطِـ۪ٔينَ", translation: "\"Ey babamız! Suçlarımız için bağışlama dile. Gerçekten bizler yanlış yaptık.\" dediler.", transliteration: "Kalu ya ebanestagfir lena zunubena inna kunna hatıin."},
  {id: 1694, sure_no: 98, ayet_no: 98, sure_adi: "Yusuf", text: "قَالَ سَوْفَ اَسْتَغْفِرُ لَكُمْ رَبّ۪يۜ اِنَّهُ هُوَ الْغَفُورُ الرَّح۪يمُ", translation: "\"Sizin için Rabb'imden bağışlanma dileyeceğim. Kuşkusuz O; Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.\"", transliteration: "Kale sevfe estagfiru lekum rabbi, innehu huvel gafurur rahim."},
  {id: 1695, sure_no: 99, ayet_no: 99, sure_adi: "Yusuf", text: "فَلَمَّا دَخَلُوا عَلٰى يُوسُفَ اٰوٰٓى اِلَيْهِ اَبَوَيْهِ وَقَالَ ادْخُلُوا مِصْرَ اِنْ شَٓاءَ اللّٰهُ اٰمِن۪ينَۜ", translation: "Ne zaman ki Yusuf'un yanına girdiler, anne ve babasını kucakladı ve \"Allah'ın izni ile güven içinde Mısır'a girin.\" dedi.", transliteration: "Fe lemma dehalu ala yusufe ava ileyhi ebeveyhi ve kaledhulu mısra in şaallahu aminin."},
  {id: 1696, sure_no: 100, ayet_no: 100, sure_adi: "Yusuf", text: "وَرَفَعَ اَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوا لَهُ سُجَّداًۚ وَقَالَ يَٓا اَبَتِ هٰذَا تَأْو۪يلُ رُءْيَايَ مِنْ قَبْلُۘ قَدْ جَعَلَهَا رَبّ۪ي حَقاًّۜ وَقَدْ اَحْسَنَ ب۪ٓي اِذْ اَخْرَجَن۪ي مِنَ السِّجْنِ وَجَٓاءَ بِكُمْ مِنَ الْبَدْوِ مِنْ بَعْدِ اَنْ نَزَغَ الشَّيْطَانُ بَيْن۪ي وَبَيْنَ اِخْوَت۪يۜ اِنَّ رَبّ۪ي لَط۪يفٌ لِمَا يَشَٓاءُۜ اِنَّهُ هُوَ الْعَل۪يمُ الْحَك۪يمُ", translation: "Yusuf, anne ve babasını makamına çıkardı. Hepsi onun makamına saygı ile eğildiler. Yusuf: \"Ey babacığım! Bu durum, daha önceki rüyamın gerçekleşmesidir. Rabb'im onu gerçek kıldı. Ve gerçekten bana ihsanda bulundu. Çünkü beni zindandan çıkardı. Şeytan benimle kardeşlerimin arasını açtıktan sonra, O sizi çölden getirdi. Benim Rabb'im dilediğine lütuf sahibidir. Gerçek şu ki: O, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.\"", transliteration: "Ve refea ebeveyhi alel arşı ve harru lehu succeda, ve kale ya ebeti haza te'vilu ru'yaye min kablu kad cealeha rabbi hakka, ve kad ahsene bi iz ahreceni mines sicni ve cae bikum minel bedvi min ba'di en nezegaş şeytanu beyni ve beyne ıhveti, inne rabbi latifun lima yeşa' innehu huvel alimul hakim."},
  {id: 1697, sure_no: 101, ayet_no: 101, sure_adi: "Yusuf", text: "رَبِّ قَدْ اٰتَيْتَن۪ي مِنَ الْمُلْكِ وَعَلَّمْتَن۪ي مِنْ تَأْو۪يلِ الْاَحَاد۪يثِۚ فَاطِرَ السَّمٰوَاتِ وَالْاَرْضِ اَنْتَ وَلِيّ۪  فِي الدُّنْيَا وَالْاٰخِرَةِۚ تَوَفَّن۪ي مُسْلِماً وَاَلْحِقْن۪ي بِالصَّالِح۪ينَ", translation: "\"Rabb'im! Bana yöneticilik verdin ve bana hadislerin yorumunu öğrettin. Ey göklere ve yere yasalarını koyan! Sen, dünyada da ahirette de velimsin; canımı müslim olarak al ve beni salihlerin5 arasına kat.\"", transliteration: "Rabbi kad ateyteni minel mulki ve allemteni min te'vilil ehadis, fatıras semavati vel ardı ente veliyyi fid dunya Vel ahıreh, teveffeni muslimen ve elhıkni bis salihin."},
  {id: 1698, sure_no: 102, ayet_no: 102, sure_adi: "Yusuf", text: "ذٰلِكَ مِنْ اَنْبَٓاءِ الْغَيْبِ نُوح۪يهِ اِلَيْكَۚ وَمَا كُنْتَ لَدَيْهِمْ اِذْ اَجْمَعُٓوا اَمْرَهُمْ وَهُمْ يَمْكُرُونَ", translation: "İşte bu sana vahiyle bildirdiğimiz; gaybi haberlerdendir. Yusuf'un kardeşleri bir araya gelip, tuzak kurmak için plan yaparlarken sen yanlarında değildin.", transliteration: "Zalike min enbail gaybi nuhihi ileyk, ve ma kunte ledeyhim iz ecmau emrehum ve hum yemkurun."},
  {id: 1699, sure_no: 103, ayet_no: 103, sure_adi: "Yusuf", text: "وَمَٓا اَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِن۪ينَ", translation: "Ama ne kadar çok istesen de insanların çoğu iman edecek değildir.", transliteration: "Ve ma ekserun nasi ve lev haraste bi mu'minin."},
  {id: 1700, sure_no: 104, ayet_no: 104, sure_adi: "Yusuf", text: "وَمَا تَسْـَٔلُهُمْ عَلَيْهِ مِنْ اَجْرٍۜ اِنْ هُوَ اِلَّا ذِكْرٌ لِلْعَالَم۪ينَ۟", translation: "Ve sen buna karşılık onlardan bir ücret istemiyorsun. O, bütün bir insanlık için ancak bir zikirdir.", transliteration: "Ve ma tes'eluhum aleyhi min ecr, in huve illa zikrun lil alemin."},
  {id: 1701, sure_no: 105, ayet_no: 105, sure_adi: "Yusuf", text: "وَكَاَيِّنْ مِنْ اٰيَةٍ فِي السَّمٰوَاتِ وَالْاَرْضِ يَمُرُّونَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُونَ", translation: "Göklerde ve yerde nice ayetler var, ancak onlar yine de bunları umursamamaktan, görmezden gelirler.", transliteration: "Ve keeyyin min ayetin fis semavati vel ardı yemurrune aleyha ve hum anha mu'ridun."},
  {id: 1702, sure_no: 106, ayet_no: 106, sure_adi: "Yusuf", text: "وَمَا يُؤْمِنُ اَكْثَرُهُمْ بِاللّٰهِ اِلَّا وَهُمْ مُشْرِكُونَ", translation: "Onların çoğu, şirk koşmadan Allah'a iman etmezler.", transliteration: "Ve ma yu'minu ekseruhum billahi illa ve hum muşrikun."},
  {id: 1703, sure_no: 107, ayet_no: 107, sure_adi: "Yusuf", text: "اَفَاَمِنُٓوا اَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِنْ عَذَابِ اللّٰهِ اَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ", translation: "Yoksa onlar, hiç beklemedikleri bir anda Allah'ın her şeyi kaplayan azabının gelmesinden veya o Sa'at'in onlara, farkında olmadan ansızın gelmesinden güvende midirler?", transliteration: "E fe eminu en te'tiyehum gaşiyetun min azabillahi ev te'tiyehumus saatu bagteten ve hum la yeş'urun."},
  {id: 1704, sure_no: 108, ayet_no: 108, sure_adi: "Yusuf", text: "قُلْ هٰذِه۪ سَب۪يل۪ٓي اَدْعُٓوا اِلَى اللّٰهِ عَلٰى بَص۪يرَةٍ اَنَا۬ وَمَنِ اتَّبَعَن۪يۜ وَسُبْحَانَ اللّٰهِ وَمَٓا اَنَا۬ مِنَ الْمُشْرِك۪ينَ", translation: "De ki: \"Benim yolum, basiret üzere Allah'a davet etme yoludur. Ve bana uyanların yolu da. Allah'ı tenzih ederim. Ve ben, Müşriklerden değilim.\"", transliteration: "Kul hazihi sebili ed'u ilallahi ala basiretin ene ve menittebeani, ve subhanallahi ve ma ene minel muşrikin."},
  {id: 1705, sure_no: 109, ayet_no: 109, sure_adi: "Yusuf", text: "وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالاً نُوح۪ٓي اِلَيْهِمْ مِنْ اَهْلِ الْقُرٰىۜ اَفَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ وَلَدَارُ الْاٰخِرَةِ خَيْرٌ لِلَّذ۪ينَ اتَّقَوْاۜ اَفَلَا تَعْقِلُونَ", translation: "Senden önce gönderdiğimiz ve kendilerine vahyettiğimiz kimseler de şehirlerde yaşayanlardan başkası değildi. Hem onlar, yeryüzünde gezip dolaşmazlar mı? Baksalar ya! Kendilerinden öncekilerin sonlarının nasıl olduğuna! Takva sahipleri için ahiret yurdu daha hayırlıdır. Hala aklınızı kullanmayacak mısınız?", transliteration: "Ve ma erselna min kablike illa ricalen nuhi ileyhim min ehlil kura, e fe lem yesiru fil ardı fe yanzuru keyfe kane akıbetullezine min kablihim, ve le darul ahıreti hayrun lillezinettekav, e fe la ta'kılun."},
  {id: 1706, sure_no: 110, ayet_no: 110, sure_adi: "Yusuf", text: "حَتّٰٓى اِذَا اسْتَيْـَٔسَ الرُّسُلُ وَظَنُّٓوا اَنَّهُمْ قَدْ كُذِبُوا جَٓاءَهُمْ نَصْرُنَاۙ فَنُجِّيَ مَنْ نَشَٓاءُۜ وَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِم۪ينَ", translation: "Ne zaman ki resuller, yalanlanmalarının bitmeyeceği kanaatine varıp ümitlerini iyice yitirince, onlara yardımımız ulaştı. Sonra da dilediklerimiz kurtarıldı. Azabımız, suçlu halklardan geri çevrilmez.", transliteration: "Hatta izestey'eser rusulu ve zannu ennehum kad kuzibu caehum nasruna fe nucciye men neşa', ve la yureddu be'suna anil kavmil mucrimin."},
  {id: 1707, sure_no: 111, ayet_no: 111, sure_adi: "Yusuf", text: "لَقَدْ كَانَ ف۪ي قَصَصِهِمْ عِبْرَةٌ لِاُو۬لِي الْاَلْبَابِۜ مَا كَانَ حَد۪يثاً يُفْتَرٰى وَلٰكِنْ تَصْد۪يقَ الَّذ۪ي بَيْنَ يَدَيْهِ وَتَفْص۪يلَ كُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ", translation: "Ant olsun ki; onların kıssalarında sağlıklı düşünen temiz akıl sahipleri için ibretler, alınacak dersler vardır. Bu uydurulan bir hadis değildir. Bilakis, kendinden öncekilerini onaylayan ve her şeyi ayrıntılı olarak açıklayan ve aynı zamanda iman eden halklar için bir yol gösterici ve bir rahmettir.", transliteration: "Lekad kane fi kasasıhim ibretun li ulil elbab, ma kane hadisen yuftera ve lakin tasdikallezi beyne yedeyhi ve tafsile kulli şey'in ve huden ve rahmeten li kavmin yu'minun."},
  {id: 1708, sure_no: 1, ayet_no: 1, sure_adi: "Rad", text: "الٓمٓرٰ ۠تِلْكَ اٰيَاتُ الْكِتَابِۜ وَالَّـذ۪ٓي اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ الْحَقُّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ", translation: "Elif, Lam, Mim, Ra. Bunlar Kitap'ın ayetleridir. O, sana Rabb'inden indirilen gerçeğin ta kendisidir. Ne var ki insanların çoğu ona inanmazlar.", transliteration: "Elif lam mim ra tilke ayatul kitab, vellezi unzile ileyke min rabbikel hakku ve lakinne ekseren nasi la yu'minun."},
  {id: 1709, sure_no: 2, ayet_no: 2, sure_adi: "Rad", text: "اَللّٰهُ الَّذ۪ي رَفَعَ السَّمٰوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۜ كُلٌّ يَجْر۪ي لِاَجَلٍ مُسَمًّىۜ يُدَبِّرُ الْاَمْرَ يُفَصِّلُ الْاٰيَاتِ لَعَلَّكُمْ بِلِقَٓاءِ رَبِّكُمْ تُوقِنُونَ", translation: "Bir dayanak olmaksızın -görmekte olduğunuz gibi- gökleri yükselten Allah'tır. Sonra, mutlak gücüyle Güneş'i ve Ay'ı koyduğu yasalara bağlı kıldı. Hepsi, adı konmuş bir ecele doğru akıp gitmektedir. İşleri düzenleyip idare eden O'dur. Ayetleri ayrıntılı olarak açıklamaktadır ki, belki Rabb'inize kavuşacağınıza kesin olarak iman edersiniz.", transliteration: "Allahullezi refeas semavati bi gayri amedin terevneha summesteva alel arşı ve sehhareş şemse vel kamer, kullun yecri li ecelin musemma, yudebbirul emre yufassılul ayati leallekum bi likai rabbikum tukınun."},
  {id: 1710, sure_no: 3, ayet_no: 3, sure_adi: "Rad", text: "وَهُوَ الَّذ۪ي مَدَّ الْاَرْضَ وَجَعَلَ ف۪يهَا رَوَاسِيَ وَاَنْهَاراًۜ وَمِنْ كُلِّ الثَّمَرَاتِ جَعَلَ ف۪يهَا زَوْجَيْنِ اثْنَيْنِ يُغْشِي الَّيْلَ النَّهَارَۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ", translation: "Ve O ki, yeryüzünü uzattı ve orada ağır baskılar ve nehirler yaptı. Orada meyvelerin hepsinden ikili eşler yaptı. Gündüzü geceyle örttü. Bunda tefekkür eden bir halk için ayetler vardır.", transliteration: "Ve huvellezi meddel arda ve ceale fiha revasiye ve enhara, ve min kullis semerati ceale fiha zevceynisneyni yugşil leylen nehar, inne fi zalike le ayatin li kavmin yetefekkerun."},
  {id: 1711, sure_no: 4, ayet_no: 4, sure_adi: "Rad", text: "وَفِي الْاَرْضِ قِطَعٌ مُتَجَاوِرَاتٌ وَجَنَّاتٌ مِنْ اَعْنَابٍ وَزَرْعٌ وَنَخ۪يلٌ صِنْوَانٌ وَغَيْرُ صِنْوَانٍ يُسْقٰى بِمَٓاءٍ وَاحِدٍ۠ وَنُفَضِّلُ بَعْضَهَا عَلٰى بَعْضٍ فِي الْاُكُلِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَ", translation: "Yeryüzünde aynı su ile sulanan ve birbirine bitişik toprak parçalarında, üzümlerden bahçeler, ekinler, çatallı çatalsız hurma ağaçları vardır. Ve Biz, onları yenmesinde farklı farklı kılıyoruz. Aklını kullanan bir toplum için bunda ayetler vardır.", transliteration: "Ve fil ardı kıtaun mutecaviratun ve cennatun min a'nabin ve zer'un ve nahilun sınvanun ve gayru sınvanin yuska bi main vahid, ve nufaddılu ba'deha ala ba'dın fil ukul, inne fi zalike le ayatin li kavmin ya'kılun."},
  {id: 1712, sure_no: 5, ayet_no: 5, sure_adi: "Rad", text: "وَاِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ ءَاِذَا كُنَّا تُرَاباً ءَاِنَّا لَف۪ي خَلْقٍ جَد۪يدٍۜ اُو۬لٰٓئِكَ الَّذ۪ينَ كَفَرُوا بِرَبِّهِمْۚ وَاُو۬لٰٓئِكَ الْاَغْلَالُ ف۪ٓي اَعْنَاقِهِمْۚ وَاُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "Eğer şaşırıyorsan, asıl şaşılacak şey, onların, \"Biz toprak olduğumuz zaman mı, gerçekten biz bir kez daha mı yaratılacağız?\" sözleridir. İşte onlar, Rabb'lerini küfreden kimselerdir. İşte onlar, boyunlarında halkalar olanlardır. Ve işte onlar, ateş ehlidir. Onlar, orada sürekli kalacaklardır.", transliteration: "Ve in ta'ceb fe acebun kavluhum e iza kunna turaben e inna le fi halkın cedid, ulaikellezine keferu bi rabbihim, ve ulaikel aglalu fi a'nakıhim, ve ulaike ashabun nar, hum fiha halidun."},
  {id: 1713, sure_no: 6, ayet_no: 6, sure_adi: "Rad", text: "وَيَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلَاتُۜ وَاِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِلنَّاسِ عَلٰى ظُلْمِهِمْۚ وَاِنَّ رَبَّكَ لَشَد۪يدُ الْعِقَابِ", translation: "Ve senden, iyilikten önce kötülüğü çabuklaştırmanı istiyorlar. Oysaki kendilerinden önce nice ibret alınacak cezalar yaşandı. Rabb'in, haksızlıklarına rağmen insanlar için gerçekten bağışlayıcı olandır. Ve elbette ki; Rabb'in, Azabı da Çok Şiddetli Olan'dır.", transliteration: "Ve yesta'ciluneke bis seyyieti kablel haseneti ve kad halet min kablihimul mesulat, ve inne rabbeke lezu magfiretin lin nasi ala zulmihim, ve inne rabbeke le şedidul ıkab."},
  {id: 1714, sure_no: 7, ayet_no: 7, sure_adi: "Rad", text: "وَيَقُولُ الَّذ۪ينَ كَفَرُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۜ اِنَّـمَٓا اَنْتَ مُنْذِرٌ وَلِكُلِّ قَوْمٍ هَادٍ۟", translation: "Kafirler, \"Ona Rabb'inden bir ayet indirilmeli değil miydi?\" diyorlar. Sen, yalnızca bir uyarıcısın. Ve her halkın bir yol göstericisi vardır.", transliteration: "Ve yekulullezine keferu lev la unzile aleyhi ayetun min rabbih, innema ente munzirun ve li kulli kavmin had."},
  {id: 1715, sure_no: 8, ayet_no: 8, sure_adi: "Rad", text: "اَللّٰهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ اُنْثٰى وَمَا تَغ۪يضُ الْاَرْحَامُ وَمَا تَزْدَادُۜ وَكُلُّ شَيْءٍ عِنْدَهُ بِمِقْدَارٍ", translation: "Allah, her dişinin neye gebe olduğunu, rahimlerin neyi azalttığını ve neyi artırdığını bilir. O'nun katında her şey koyduğu düzene göre işlemektedir.", transliteration: "Allahu ya'lemu ma tahmilu kullu unsa ve ma tegidul erhamu ve ma tezdad, ve kullu şey'in indehu bi mıkdar."},
  {id: 1716, sure_no: 9, ayet_no: 9, sure_adi: "Rad", text: "عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَب۪يرُ الْمُتَعَالِ", translation: "Görüneni ve görünemeyeni bilendir. O, Mutlak Büyük'tür ve Her Şeyden Yüce'dir.", transliteration: "Alimul gaybi veş şehadetil kebirul muteal."},
  {id: 1717, sure_no: 10, ayet_no: 10, sure_adi: "Rad", text: "سَوَٓاءٌ مِنْكُمْ مَنْ اَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِه۪ وَمَنْ هُوَ مُسْتَخْفٍ بِالَّيْلِ وَسَارِبٌ بِالنَّهَارِ", translation: "Sizden, düşüncesini açıklayan da gizleyen de bir şeyi gece yapan da gündüz yapan da aynıdır.", transliteration: "Sevaun minkum men eserrel kavle ve men cehere bihi ve men huve mustahfin bil leyli ve saribun bin nehar."},
  {id: 1718, sure_no: 11, ayet_no: 11, sure_adi: "Rad", text: "لَهُ مُعَقِّبَاتٌ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِه۪ يَحْفَظُونَهُ مِنْ اَمْرِ اللّٰهِۜ اِنَّ اللّٰهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتّٰى يُغَيِّرُوا مَا بِاَنْفُسِهِمْۜ وَاِذَٓا اَرَادَ اللّٰهُ بِقَوْمٍ سُٓوءاً فَلَا مَرَدَّ لَهُۚ وَمَا لَهُمْ مِنْ دُونِه۪ مِنْ وَالٍ", translation: "Onu her şeyi ile izleyenler vardır. Allah'ın işinden olarak onu gözetlerler. Kuşkusuz bir halk kendi durumunu değiştirmedikçe, Allah onların durumunu değiştirmez. Ve Allah, kötü bir halka ceza vermeyi istediğinde hiçbir güç bunu engelleyemez. Onlar için O'ndan başka bir veli yoktur.", transliteration: "Lehu muakkibatun min beyni yedeyhi ve min halfihi yahfezunehu min emrillah, innallahe la yugayyiru ma bi kavmin hatta yugayyiru ma bi enfusihim, ve iza eradallahu bi kavmin suen fe la meredde leh, ve ma lehum min dunihi min val."},
  {id: 1719, sure_no: 12, ayet_no: 12, sure_adi: "Rad", text: "هُوَ الَّذ۪ي يُر۪يكُمُ الْبَرْقَ خَوْفاً وَطَمَعاً وَيُنْشِئُ السَّحَابَ الثِّقَالَۚ", translation: "O, korkutan şimşeği gösteren ve umut olarak yağmur yüklü bulutları meydana getirendir.", transliteration: "Huveellezi yurikumul berka havfen ve tamean ve yunşius sehabes sikal."},
  {id: 1720, sure_no: 13, ayet_no: 13, sure_adi: "Rad", text: "وَيُسَبِّحُ الرَّعْدُ بِحَمْدِه۪ وَالْمَلٰٓئِكَةُ مِنْ خ۪يفَتِه۪ۚ وَيُرْسِلُ الصَّوَاعِقَ فَيُص۪يبُ بِهَا مَنْ يَشَٓاءُ وَهُمْ يُجَادِلُونَ فِي اللّٰهِۚ وَهُوَ شَد۪يدُ الْمِحَالِۜ", translation: "Gök gürültüsü övgüsüyle, melekler de ürpertisiyle O'nu tesbih ederler. O, yıldırımlar gönderir ve onunla dilediğini çarpar. Onlar ise Allah hakkında tartışıp duruyorlar. Ve O, karşı konulamaz pek çetin bir güç sahibidir.", transliteration: "Ve yusebbihur ra'du bi hamdihi vel melaiketu min hifetih, ve yursilus savaıka fe yusibu biha men ye?au ve hum yucadilune fillah, ve huve ?edidul mihal."},
  {id: 1721, sure_no: 14, ayet_no: 14, sure_adi: "Rad", text: "لَهُ دَعْوَةُ الْحَقِّۜ وَالَّذ۪ينَ يَدْعُونَ مِنْ دُونِه۪ لَا يَسْتَج۪يبُونَ لَهُمْ بِشَيْءٍ اِلَّا كَبَاسِطِ كَفَّيْهِ اِلَى الْمَٓاءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِه۪ۜ وَمَا دُعَٓاءُ الْكَافِر۪ينَ اِلَّا ف۪ي ضَلَالٍ", translation: "Gerçek olan dua ancak O'na yapılandır. Onların, O'nun yanı sıra istekte bulundukları varlıklar, hiçbir şekilde onlara karşılık veremezler. Onlar, elleri suya ulaşmadığı halde, ağızlarına su kendiliğinden gelsin diye iki avucunu açanlar gibidir. Kafirlerin duası sapkıncadır.", transliteration: "Lehu da'vetul hakk, vellezine yed'une min dunihi la yestecibune lehum bi şey'in illa kebasitı keffeyhi ilel mai li yebluga fahu ve ma huve bi baligıh, ve ma duaul kafirine illa fi dalal."},
  {id: 1722, sure_no: 15, ayet_no: 15, sure_adi: "Rad", text: "وَلِلّٰهِ يَسْجُدُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ طَوْعاً وَكَرْهاً وَظِلَالُهُمْ بِالْغُدُوِّ وَالْاٰصَالِ", translation: "Göklerde ve yerde olanlar ve gölgeleri, isteyerek veya istemeyerek, sabah akşam Allah'a secde ederler.", transliteration: "Ve lillahi yescudu men fis semavati vel ardı tav'an ve kerhen ve zilaluhum bil guduvvi vel asal."},
  {id: 1723, sure_no: 16, ayet_no: 16, sure_adi: "Rad", text: "قُلْ مَنْ رَبُّ السَّمٰوَاتِ وَالْاَرْضِۜ  قُلِ اللّٰهُۜ  قُلْ اَفَاتَّخَذْتُمْ مِنْ دُونِه۪ٓ اَوْلِيَٓاءَ لَا يَمْلِكُونَ لِاَنْفُسِهِمْ نَفْعاً وَلَا ضَراًّۜ قُلْ هَلْ يَسْتَوِي الْاَعْمٰى وَالْبَص۪يرُۙ  اَمْ هَلْ تَسْتَوِي الظُّلُمَاتُ وَالنُّورُۚ  اَمْ جَعَلُوا لِلّٰهِ شُرَكَٓاءَ خَلَقُوا كَخَلْقِه۪ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْۜ  قُلِ اللّٰهُ خَالِقُ كُلِّ شَيْءٍ وَهُوَ الْوَاحِدُ الْقَهَّارُ", translation: "De ki: \"Göklerin ve yerin Rabb'i kimdir?\" De ki: \"Allah'tır! O'nun yanı sıra, kendileri için yarar da zarar da sağlamaya güç yetiremeyenleri veliler mi edindiniz?\" De ki: \"Hiç gören ile kör bir olur mu? Ya da karanlıkla aydınlık bir midir?\" Yoksa Allah'a, O'nun yaratması gibi yaratması olan ortaklar mı buldular da bu yaratma, kendilerince birbirine mi benzeşti? De ki: \"Allah her şeyin yaratıcısıdır. O, Eşsiz ve Benzersiz Bir Olan'dır, Varlığın Üzerinde Mutlak Egemen'dir.\"", transliteration: "Kul men rabbus semavati vel ard, kulillah, kul e fettehaztum min dunihi evliyae la yemlikune li enfusihim nef'an ve la darra, kul hel yestevil a'ma vel basiru em hel testeviz zulumatu ven nur, em cealu lillahi şurekae halaku ke halkıhi fe teşabehel halku aleyhim, kulillahu haliku kulli şey'in ve huvel vahidul kahhar."},
  {id: 1724, sure_no: 17, ayet_no: 17, sure_adi: "Rad", text: "اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَسَالَتْ اَوْدِيَةٌ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَداً رَابِياًۜ وَمِمَّا يُوقِدُونَ عَلَيْهِ فِي النَّارِ ابْتِغَٓاءَ حِلْيَةٍ اَوْ مَتَاعٍ زَبَدٌ مِثْلُهُۜ  كَذٰلِكَ يَضْرِبُ اللّٰهُ الْحَقَّ وَالْبَاطِلَۜ فَاَمَّا الزَّبَدُ فَيَذْهَبُ جُفَٓاءًۚ وَاَمَّا مَا يَنْفَعُ النَّاسَ فَيَمْكُثُ فِي الْاَرْضِۜ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْاَمْثَالَۜ", translation: "Gökten su indirdi. Dereler kendi ölçüsünce çağlayıp aktı. Akıntı, üste çıkan köpüğü taşıyıp götürür. Buna benzer bir köpük de değerli maden elde etmek için veya faydalanmak için ateşte eritilen madenlerin üzerinde de oluşur. İşte Allah Hakk ve Batıl'a böyle örnek verir. Köpük yok olup gider. İnsana fayda veren şey ise kalıcı olur. İşte Allah böyle örnekler verir.", transliteration: "Enzele mines semai maen fe salet evdiyetun bi kaderiha fahtemeles seylu zebeden rabiya, ve mimma yukıdune aleyhi fin naribtigae hılyetin ev metaın zebedun misluh, kezalike yadribullahul hakka vel batıl, fe emmez zebedu fe yezhebu cufa', ve emma ma yenfaun nase fe yemkusufil ard, kezalike yadrıbullahul emsal."},
  {id: 1725, sure_no: 18, ayet_no: 18, sure_adi: "Rad", text: "لِلَّذ۪ينَ اسْتَجَابُوا لِرَبِّهِمُ الْحُسْنٰىۜ وَالَّذ۪ينَ لَمْ يَسْتَج۪يبُوا لَهُ لَوْ اَنَّ لَهُمْ مَا فِي الْاَرْضِ جَم۪يعاً وَمِثْلَهُ مَعَهُ لَافْتَدَوْا بِه۪ۜ اُو۬لٰٓئِكَ لَهُمْ سُٓوءُ الْحِسَابِۙ وَمَأْوٰيهُمْ جَهَنَّمُۜ وَبِئْسَ الْمِهَادُ۟", translation: "Rabb'lerinin çağrısına uyanlar için, en iyi karşılık vardır. O'na uymayanlar ise, yeryüzünde bulunanların tamamı ve bir o kadarı daha kendilerinin olsa, kurtulmak için hepsini karşılık olarak verirlerdi. Hesabın kötüsü onlar içindir. Varacakları yer Cehennem'dir. O ne kötü bir yataktır.", transliteration: "Lillezinestecabu li rabbihimul husna, vellezine lem yestecibu lehu lev enne lehum ma fil ardı cemian ve mislehu meahu leftedev bih, ulaike lehum suul hısabi ve me'vahum cehennem, ve bi'sel mihad."},
  {id: 1726, sure_no: 19, ayet_no: 19, sure_adi: "Rad", text: "اَفَمَنْ يَعْلَمُ اَنَّـمَٓا اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ الْحَقُّ كَمَنْ هُوَ اَعْمٰىۜ اِنَّمَا يَتَذَكَّرُ اُو۬لُوا الْاَلْبَابِۙ", translation: "Rabb'inden sana indirilenin kesin doğru olduğunu bilen kimse, kör kimse gibi midir? Ancak sağlıklı düşünen akıl sahipleri öğüt alıp gerçeği kavrarlar.", transliteration: "E fe men ya'lemu ennema unzile ileyke min rabbikel hakku ke men huve a'ma, innema yetezekkeru ulul elbab."},
  {id: 1727, sure_no: 20, ayet_no: 20, sure_adi: "Rad", text: "اَلَّذ۪ينَ يُوفُونَ بِعَهْدِ اللّٰهِ وَلَا يَنْقُضُونَ الْم۪يثَاقَۙ", translation: "Onlar, Allah'la olan ahdi yerine getirirler, misakı bozmazlar.", transliteration: "Ellezine yufune bi ahdillahi ve la yenkudunel misak."},
  {id: 1728, sure_no: 21, ayet_no: 21, sure_adi: "Rad", text: "وَالَّذ۪ينَ يَصِلُونَ مَٓا اَمَرَ اللّٰهُ بِه۪ٓ اَنْ يُوصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُونَ سُٓوءَ الْحِسَابِۜ", translation: "Ve o kimseler, Allah'ın bağlı kalınmasını emrettiği şeye bağlı kalırlar, Rabb'lerini saygı ile yüceltirler, hesabın kötü olmasından korkarlar.", transliteration: "Vellezine yasılune ma emerallahu bihi en yusale ve yahşevne rabbehum ve yehafune suel hisab."},
  {id: 1729, sure_no: 22, ayet_no: 22, sure_adi: "Rad", text: "وَالَّذ۪ينَ صَبَرُوا ابْتِغَٓاءَ وَجْهِ رَبِّهِمْ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِراًّ وَعَلَانِيَةً وَيَدْرَؤُ۫نَ بِالْحَسَنَةِ السَّيِّئَةَ اُو۬لٰٓئِكَ لَهُمْ عُقْبَى الدَّارِۙ", translation: "Ve o kimseler, sabırla Rabb'lerine yönelirler ve salatı ikame ederler, kendilerine verdiğimiz rızıktan gizli ve açık infak ederler, kötülüğü iyilikle savarlar; dünya yurdunun sonucu onlar içindir.", transliteration: "Vellezine saberubtigae vechi rabbihim ve ekamus salate ve enfeku mimma rezaknahum sirren ve alaniyeten ve yedreune bil hasenetis seyyiete ulaike lehum ukbed dar."},
  {id: 1730, sure_no: 23, ayet_no: 23, sure_adi: "Rad", text: "جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا وَمَنْ صَلَحَ مِنْ اٰبَٓائِهِمْ وَاَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ وَالْمَلٰٓئِكَةُ يَدْخُلُونَ عَلَيْهِمْ مِنْ كُلِّ بَابٍۚ", translation: "Adn Cennetleri onlarındır. Oraya, anne ve babalarından, eşlerinden ve soylarından salih olanlar gireceklerdir. Melekler de her kapıdan yanlarına girecekler ve şöyle diyecekler:", transliteration: "Cennatu adnin yedhuluneha ve men salaha min abaihim ve ezvacihim ve zurriyyatihim vel melaiketu yedhulune aleyhim min kulli bab."},
  {id: 1731, sure_no: 24, ayet_no: 24, sure_adi: "Rad", text: "سَلَامٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِۜ", translation: "\"Sabretmeniz nedeniyle size selam olsun. Dünya yurdunun sonu ne güzeldir.\"", transliteration: "Selamun aleykum bi ma sabertum fe ni'me ukbed dar."},
  {id: 1732, sure_no: 25, ayet_no: 25, sure_adi: "Rad", text: "وَالَّذ۪ينَ يَنْقُضُونَ عَهْدَ اللّٰهِ مِنْ بَعْدِ م۪يثَاقِه۪ وَيَقْطَعُونَ مَٓا اَمَرَ اللّٰهُ بِه۪ٓ اَنْ يُوصَلَ وَيُفْسِدُونَ فِي الْاَرْضِۙ اُو۬لٰٓئِكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُٓوءُ الدَّارِ", translation: "Misaklerinden sonra Allah'ın ahdini  bozanlar; Allah'ın bağlı kalınmasını istediği şeyle bağlarını koparanlar ve yeryüzünde bozgunculuk yapanlara lanet vardır ve dünya yurdunun kötü sonu onlarındır.", transliteration: "Vellezine yankudune ahdallahi min ba'di misakıhi ve yaktaune ma emerallahu bihi en yusale ve yufsidune fil ardı ulaike lehumul la'netu ve lehum suud dar."},
  {id: 1733, sure_no: 26, ayet_no: 26, sure_adi: "Rad", text: "اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ وَفَرِحُوا بِالْحَيٰوةِ الدُّنْيَاۜ وَمَا الْحَيٰوةُ الدُّنْيَا فِي الْاٰخِرَةِ اِلَّا مَتَاعٌ۟", translation: "Allah, rızkı dilediğine genişletir de ölçülendirir de. Dünya hayatı ile şımardılar. Oysa dünya, ahiret hayatı yanında bir metadan başka bir şey değildir.", transliteration: "Allahu yebsutur rızka li men yeşau ve yakdir, ve ferihu bil hayatid dunya, ve mal hayatud dunya fil ahıreti illa meta'u."},
  {id: 1734, sure_no: 27, ayet_no: 27, sure_adi: "Rad", text: "وَيَقُولُ الَّذ۪ينَ كَفَرُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَةٌ مِنْ رَبِّه۪ۜ قُلْ اِنَّ اللّٰهَ يُضِلُّ مَنْ يَشَٓاءُ وَيَهْد۪ٓي اِلَيْهِ مَنْ اَنَابَۚ", translation: "Kafirler: \"Rabb'inden ona bir ayet indirilseydi ya!\" diyorlar. De ki: \"Allah, hak edeni saptırır, kendisine yönelen kimseyi de doğru yola iletir.\"", transliteration: "Ve yekulullezine keferu lev la unzile aleyhi ayetun min rabbih, kul innallahe yudillu men yeşau ve yehdi ileyhi men enab."},
  {id: 1735, sure_no: 28, ayet_no: 28, sure_adi: "Rad", text: "اَلَّذ۪ينَ اٰمَنُوا وَتَطْمَئِنُّ قُلُوبُهُمْ بِذِكْرِ اللّٰهِۜ اَلَا بِذِكْرِ اللّٰهِ تَطْمَئِنُّ الْقُلُوبُۜ", translation: "Onlar, İman Edenler ve kalpleri  Allah'ın zikri ile tatmin olanlardır. Kalpler, ancak Allah'ın zikri ile tatmin olur.", transliteration: "Ellezine amenu ve tatmainnu kulubuhum bi zikrillah e la bi zikrillahi tatmainnul kulub."},
  {id: 1736, sure_no: 29, ayet_no: 29, sure_adi: "Rad", text: "اَلَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ طُوبٰى لَهُمْ وَحُسْنُ مَاٰبٍ", translation: "İman Eden ve salihatı yapanlara ne mutlu. Dönüş yerinin iyisi onlarındır.", transliteration: "Ellezine amenu ve amilus salihati tuba lehum ve husnu meab."},
  {id: 1737, sure_no: 30, ayet_no: 30, sure_adi: "Rad", text: "كَذٰلِكَ اَرْسَلْنَاكَ ف۪ٓي اُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَٓا اُمَمٌ لِتَتْلُوَ۬ا عَلَيْهِمُ الَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ وَهُمْ يَكْفُرُونَ بِالرَّحْمٰنِۜ قُلْ هُوَ رَبّ۪ي لَٓا اِلٰهَ اِلَّا هُوَۚ عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ مَتَابِ", translation: "Kendilerinden önce nice toplumların gelip geçtiği bir topluma seni gönderdik ki, sana vahyettiğimizi onlara okuyup duyurasın. Onlar, Rahman'a nankörlük ediyorlar. De ki: \"O benim Rabb'imdir; Ondan başka ilah yoktur. Ben O'na tevekkül ettim, tevbe O'nadır.\"", transliteration: "Kezalike erselnake fi ummetin kad halet min kabliha umemun li tetluve aleyhimullezi evhayna ileyke ve hum yekfurune bir rahman, kul huve rabbi la ilahe illa hu, aleyhi tevekkeltu ve ileyhi metab."},
  {id: 1738, sure_no: 31, ayet_no: 31, sure_adi: "Rad", text: "وَلَوْ اَنَّ قُرْاٰناً سُيِّرَتْ بِهِ الْجِبَالُ اَوْ قُطِّعَتْ بِهِ الْاَرْضُ اَوْ كُلِّمَ بِهِ الْمَوْتٰىۜ بَلْ لِلّٰهِ الْاَمْرُ جَم۪يعاًۜ اَفَلَمْ يَايْـَٔسِ الَّذ۪ينَ اٰمَنُٓوا اَنْ لَوْ يَشَٓاءُ اللّٰهُ لَهَدَى النَّاسَ جَم۪يعاًۜ وَلَا يَزَالُ الَّذ۪ينَ كَفَرُوا تُص۪يبُهُمْ بِمَا صَنَعُوا قَارِعَةٌ اَوْ تَحُلُّ قَر۪يباً مِنْ دَارِهِمْ حَتّٰى يَأْتِيَ وَعْدُ اللّٰهِۜ اِنَّ اللّٰهَ لَا يُخْلِفُ الْم۪يعَادَ۟", translation: "Kur'an'la dağlar yürütülseydi veya onunla yeryüzü yarılıp parçalansaydı veya onunla ölüler konuşturulsaydı yine de bir şey değişmezdi. Hayır! Bütün işler Allah'a aittir. İman Edenler hala anlamadılar mı ki Allah dileyecek olsa bütün insanları doğru yola iletir. Yaltaklanmalarından dolayı Kafirler bela ile karşı karşıya kalıp duracaktır. Veya evlerinin yanı başına inecek. Allah'ın vadi gelinceye kadar bu böyle sürüp gidecektir. Kuşkusuz Allah sözünden dönmez.", transliteration: "Ve lev enne kur'anen suyyiret bihil cibalu ev kuttıat bihil ardu ev kullime bihil mevta, bel lillahil emru cemia, e fe lem ye'yesillezine amenu en lev yeşaullahu le heden nase cemia,ve la yezalullezine keferu tusibuhum bi ma sanau kariatun ev tehullu kariben min darihim hatta ye'tiye va'dullah, innallahe la yuhliful miad."},
  {id: 1739, sure_no: 32, ayet_no: 32, sure_adi: "Rad", text: "وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَاَمْلَيْتُ لِلَّذ۪ينَ كَفَرُوا ثُمَّ اَخَذْتُهُمْ۠ فَكَيْفَ كَانَ عِقَابِ", translation: "Ant olsun senden önceki Resullerle de alay edildi. Kafirlere süre tanıdım. Sonra zamanı gelince onları yakalayıverdim. Benim cezam nasıl olurmuş gördüler.", transliteration: "Ve lekadistuhzie bi rusulin min kablike fe emleytu lillezine keferu summe ehaztuhum, fe keyfe kane ıkab."},
  {id: 1740, sure_no: 33, ayet_no: 33, sure_adi: "Rad", text: "اَفَمَنْ هُوَ قَٓائِمٌ عَلٰى كُلِّ نَفْسٍ بِمَا كَسَبَتْۚ وَجَعَلُوا لِلّٰهِ شُرَكَٓاءَۜ قُلْ سَمُّوهُمْۜ اَمْ تُنَبِّؤُ۫نَهُ بِمَا لَا يَعْلَمُ فِي الْاَرْضِ اَمْ بِظَاهِرٍ مِنَ الْقَوْلِۜ بَلْ زُيِّنَ لِلَّذ۪ينَ كَفَرُوا مَكْرُهُمْ وَصُدُّوا عَنِ السَّب۪يلِۜ وَمَنْ يُضْلِلِ اللّٰهُ فَمَا لَهُ مِنْ هَادٍ", translation: "Peki, herkesin ne yaptığını gözeten O değil mi? Onlar yine de ilahlarını Allah'a ortaklar koştular. De ki: \"Onları istediğiniz isimle isimlendirin bakalım. Yoksa siz, O'na yeryüzünde bilmediği bir şeyi mi haber veriyorsunuz? Yoksa boş sözlere mi aldanıyorsunuz?\" Aslında Kafirlere planları güzel gösterildi de doğru yoldan alıkonuldular. Allah kimi saptırırsa artık ona yol gösterecek kimse olamaz.", transliteration: "E fe men huve kaimun ala kulli nefsin bi ma kesebet, ve cealu lillahi şureka', kul semmuhum, em tunebbiunehu bi ma la ya'lemu fil ardı em bi zahirin minel kavl, bel zuyyine lillezine keferu mekruhum ve suddu anis sebil, ve men yudlilillahu fe ma lehu min had."},
  {id: 1741, sure_no: 34, ayet_no: 34, sure_adi: "Rad", text: "لَهُمْ عَذَابٌ فِي الْحَيٰوةِ الدُّنْيَا وَلَعَذَابُ الْاٰخِرَةِ اَشَقُّۚ وَمَا لَهُمْ مِنَ اللّٰهِ مِنْ وَاقٍ", translation: "Onlara dünya hayatında bir azap vardır. Ahiret azabı ise daha zordur. Onları Allah'ın azabından koruyabilecek bir koruyucuları da yoktur.", transliteration: "Lehum azabun fil hayatid dunya ve le azabul ahıreti eşakk, ve ma lehum minallahi min vak."},
  {id: 1742, sure_no: 35, ayet_no: 35, sure_adi: "Rad", text: "مَثَلُ الْجَنَّةِ الَّت۪ي وُعِدَ الْمُتَّقُونَۜ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ اُكُلُهَا دَٓائِمٌ وَظِلُّهَاۜ تِلْكَ عُقْبَى الَّذ۪ينَ اتَّقَوْاۗ وَعُقْبَى الْكَافِر۪ينَ النَّارُ", translation: "Takva sahiplerine söz verilen Cennet'in içinden ırmaklar akmaktadır, meyvesi ve gölgesi süreklidir. İşte bu takva sahiplerinin sonudur. Kafirlerin sonu ise ateştir.", transliteration: "Meselul cennetilleti vuidel muttekun, tecri min tahtihel enhar, ukuluha daimun ve zilluha, tilke ukbellezinettekav ve ukbel kafirinen nar."},
  {id: 1743, sure_no: 36, ayet_no: 36, sure_adi: "Rad", text: "وَالَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يَفْرَحُونَ بِمَٓا اُنْزِلَ اِلَيْكَ وَمِنَ الْاَحْزَابِ مَنْ يُنْكِرُ بَعْضَهُۜ  قُلْ اِنَّـمَٓا اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ وَلَٓا اُشْرِكَ بِه۪ۜ اِلَيْهِ اَدْعُوا وَاِلَيْهِ مَاٰبِ", translation: "Kendilerine Kitap verdiğimiz kimseler, sana indirilen ile sevinirler. Kabilelerden onun bir kısmını küfreden gruplar vardır. De ki: \"Ben yalnızca Allah'a kulluk etmekle ve O'na şirk koşmamakla emrolundum. Ben yalnızca O'na çağırıyorum ve dönüşüm yalnızca O'nadır.\"", transliteration: "Vellezine ateynahumul kitabe yefrehune bima unzile ileyke ve minel ahzabi men yunkiru ba'dah, kul innema umirtu en a'budallahe ve la uşrike bih, ileyhi ed'u ve ileyhi meab."},
  {id: 1744, sure_no: 37, ayet_no: 37, sure_adi: "Rad", text: "وَكَذٰلِكَ اَنْزَلْنَاهُ حُكْماً عَرَبِياًّۜ وَلَئِنِ اتَّبَعْتَ اَهْوَٓاءَهُمْ بَعْدَ مَا جَٓاءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَلِيٍّ وَلَا وَاقٍ۟", translation: "İşte böylece Biz onu Arapça bir hüküm olarak indirdik. Eğer sana gelen ilimden sonra onların hevalarına uyarsan, seni Allah'tan koruyacak bir veli, bir koruyucu bulamazsın.", transliteration: "Ve kezalike enzelnahu hukmen arabiyya, ve le initteba'te ehvaehum ba'de ma caeke minel ilmi ma leke minallahi min veliyyin ve la vak."},
  {id: 1745, sure_no: 38, ayet_no: 38, sure_adi: "Rad", text: "وَلَقَدْ اَرْسَلْنَا رُسُلاً مِنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ اَزْوَاجاً وَذُرِّيَّةًۜ وَمَا كَانَ لِرَسُولٍ اَنْ يَأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِۜ لِكُلِّ اَجَلٍ كِتَابٌ", translation: "Ant olsun ki senden önce de Resuller gönderdik. Onlara da eşler ve çocuklar verdik. Bir Resul için Allah'ın izni olmadan bir ayet getirmesi mümkün değildir. Her ecelin bir kitabı vardır.", transliteration: "Ve lekad erselna rusulen min kablike ve cealna lehum ezvacen ve zurriyyeh, ve ma kane li resulin en ye'tiye bi ayetin illa bi iznillah, li kulli ecelin kitab."},
  {id: 1746, sure_no: 39, ayet_no: 39, sure_adi: "Rad", text: "يَمْحُوا اللّٰهُ مَا يَشَٓاءُ وَيُثْبِتُۚ وَعِنْدَهُٓ اُمُّ الْكِتَابِ", translation: "Allah dilediğini yok eder, dilediğine de dokunmaz. Ana Kitap O'nun yanındadır.", transliteration: "Yemhullahu ma yeşau ve yusbit, ve indehu ummul kitab."},
  {id: 1747, sure_no: 40, ayet_no: 40, sure_adi: "Rad", text: "وَاِنْ مَا نُرِيَنَّكَ بَعْضَ الَّذ۪ي نَعِدُهُمْ اَوْ نَتَوَفَّـيَنَّكَ فَاِنَّمَا عَلَيْكَ الْبَلَاغُ وَعَلَيْنَا الْحِسَابُ", translation: "İster onlara yaptığımız uyarının bir kısmını sana gösterelim, ister senin canını alalım; sana düşen sadece tebliğ yapmaktır. Hesap görmek Bize aittir.", transliteration: "Ve in ma nuriyenneke ba'dallezi neiduhum ev neteveffeyenneke fe innema aleykel belagu ve aleynel hisab."},
  {id: 1748, sure_no: 41, ayet_no: 41, sure_adi: "Rad", text: "اَوَلَمْ يَرَوْا اَنَّا نَأْتِي الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۜ وَاللّٰهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِه۪ۜ وَهُوَ سَر۪يعُ الْحِسَابِ", translation: "Yeryüzüne gelip onu çevresinden nasıl eksilttiğimizi görmüyorlar mı? Karar veren Allah'tır ve O'nun verdiği kararı bozacak hiç kimse yoktur. O, hesabı çabuk görendir.", transliteration: "E ve lem yerev enna ne'til arda nenkusuha min etrafiha, vallahu yahkumu la muakkıbe li hukmih, ve huve seriul hısab."},
  {id: 1749, sure_no: 42, ayet_no: 42, sure_adi: "Rad", text: "وَقَدْ مَكَرَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَلِلّٰهِ الْمَكْرُ جَم۪يعاًۜ يَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍۜ وَسَيَعْلَمُ الْكُفَّارُ لِمَنْ عُقْبَى الدَّارِ", translation: "Onlardan öncekiler de planlar yapmışlardı. Oysa Allah'ın planı bütün planları geçersiz kılar. Zira O, herkesin ne yaptığını bilir. Kafirler, bu yurdun sonunun kimin olduğunu bileceklerdir.", transliteration: "Ve kad mekerellezine min kablihim fe lillahil mekru cemia,ya'lemu ma teksibu kullu nefs, ve se ya'lemul kuffaru li men ukbed dar."},
  {id: 1750, sure_no: 43, ayet_no: 43, sure_adi: "Rad", text: "وَيَقُولُ الَّذ۪ينَ كَفَرُوا لَسْتَ مُرْسَلاًۜ قُلْ كَفٰى بِاللّٰهِ شَه۪يداً بَيْن۪ي وَبَيْنَكُمْۙ وَمَنْ عِنْدَهُ عِلْمُ الْكِتَابِ", translation: "Kafirler, \"Sen gönderilmiş değilsin.\" diyorlar. De ki: \"Benimle sizin aranızda en iyi tanık olarak Allah ve yanlarında Kitap'ın bilgisi olanlar yeter.\"", transliteration: "Ve yekulullezine keferu leste mursela, kul kefa billahi şehiden beyni ve beynekum ve men indehu ilmul kitab."},
  {id: 1751, sure_no: 1, ayet_no: 1, sure_adi: "İbrahim", text: "الٓـرٰ۠ كِتَابٌ اَنْزَلْنَاهُ اِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمَاتِ اِلَى النُّورِ بِـاِذْنِ رَبِّهِمْ اِلٰى صِرَاطِ الْعَز۪يزِ الْحَم۪يدِۙ", translation: "Elif, Lam, Ra. Bu; insanları, Rabb'lerinin izni ile karanlıklardan aydınlığa çıkarman; onları Mutlak Üstün Olan'ın, Övgüye Değer Yegane Varlık Olan'ın yoluna iletmen için sana indirdiğimiz bir Kitap'tır.", transliteration: "Elif lam ra kitabun enzelnahu ileyke li tuhricen nase minez zulumati ilen nuri bi izni rabbihim ila sıratıl azizil hamid."},
  {id: 1752, sure_no: 2, ayet_no: 2, sure_adi: "İbrahim", text: "اَللّٰهِ الَّذ۪ي لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَوَيْلٌ لِلْكَافِر۪ينَ مِنْ عَذَابٍ شَد۪يدٍۙ", translation: "Allah ki; göklerde ve yerde olan her şey O'nundur. Görecekleri şiddetli azaptan dolayı, Kafirlerin vay haline!", transliteration: "Allahillezi lehu ma fis semavati ve ma fil ard, ve veylun lil kafirine min azabin şedid."},
  {id: 1753, sure_no: 3, ayet_no: 3, sure_adi: "İbrahim", text: "اَلَّذ۪ينَ يَسْتَحِبُّونَ الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِ وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَيَبْغُونَهَا عِوَجاًۜ اُو۬لٰٓئِكَ ف۪ي ضَلَالٍ بَع۪يدٍ", translation: "Onlar, dünya hayatını ahiret hayatına tercih ederler. Allah'ın yolundan alıkoyarlar ve onda eğrilik ararlar. İşte onlar, derin bir sapkınlık içindedirler.", transliteration: "Ellezine yestehıbbunel hayated dunya alel ahıreti ve yasuddune an sebilillahi ve yebguneha iveca, ulaike fi dalalin baid."},
  {id: 1754, sure_no: 4, ayet_no: 4, sure_adi: "İbrahim", text: "وَمَٓا اَرْسَلْنَا مِنْ رَسُولٍ اِلَّا بِلِسَانِ قَوْمِه۪ لِيُبَيِّنَ لَهُمْۜ فَيُضِلُّ اللّٰهُ مَنْ يَشَٓاءُ وَيَهْد۪ي مَنْ يَشَٓاءُۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Biz, mesajımızı anlaşılır olarak iletebilmesi için hiçbir Resulü kendi halkının dilinden başka bir dille göndermedik. Allah, artık hak eden kimseyi saptırır, hak eden kimseyi de doğru yola iletir. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve ma erselna min resulin illa bi lisani kavmihi li yubeyyine lehum, fe yudillullahu men yeşau ve yehdi men yeşa', ve huvel azizul hakim."},
  {id: 1755, sure_no: 5, ayet_no: 5, sure_adi: "İbrahim", text: "وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيَاتِنَٓا اَنْ اَخْرِجْ قَوْمَكَ مِنَ الظُّلُمَاتِ اِلَى النُّورِ وَذَكِّرْهُمْ بِاَيَّامِ اللّٰهِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ", translation: "Ant olsun ki; Musa'yı, \"Halkını karanlıklardan aydınlığa çıkar, onlara Allah'ın günlerini hatırlat.\" diye ayetlerimizle gönderdik. Kesinlikle bunda sabreden ve şükreden kimseler için nice ayetler vardır.", transliteration: "Ve le kad erselna musa bi ayatina en ahric kavmeke minez zulumati ilen nuri, ve zekkirhum bi eyyamillah, inne fi zalike le ayatin li kulli sabbarin şekur."},
  {id: 1756, sure_no: 6, ayet_no: 6, sure_adi: "İbrahim", text: "وَاِذْ قَالَ مُوسٰى لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ اَنْجٰيكُمْ مِنْ اٰلِ فِرْعَوْنَ يَسُومُونَكُمْ سُٓوءَ الْعَذَابِ وَيُذَبِّحُونَ اَبْنَٓاءَكُمْ وَيَسْتَحْيُونَ نِسَٓاءَكُمْۜ وَف۪ي ذٰلِكُمْ بَلَٓاءٌ مِنْ رَبِّكُمْ عَظ۪يمٌ۟", translation: "Hani! Musa halkına demişti ki: \"Allah'ın üzerinizdeki nimetini hatırlayın. Hani O, sizi; oğullarınızı boğazlayıp, kadınlarınızı sağ bırakarak azabın en kötüsü ile cezalandıran Firavunculardan kurtarmıştı. Bunda, Rabb'inizden çok büyük bir sınav vardı.\"", transliteration: "Ve iz kale musa li kavmihizkuru ni'metallahi aleykum iz encakum min ali fir'avne yesumunekum suel azabi ve yuzebbihune ebnaekum ve yestahyune nisaekum, ve fi zalikum belaun min rabbikum azim."},
  {id: 1757, sure_no: 7, ayet_no: 7, sure_adi: "İbrahim", text: "وَاِذْ تَاَذَّنَ رَبُّكُمْ لَئِنْ شَكَرْتُمْ لَاَز۪يدَنَّكُمْ وَلَئِنْ كَفَرْتُمْ اِنَّ عَذَاب۪ي لَشَد۪يدٌ", translation: "Hani Rabb'iniz: \"Eğer şükrederseniz mutlaka size nimetlerimi artırırım, eğer nankörlük ederseniz bilin ki azabım çok şiddetlidir!\" diye bildirmişti.", transliteration: "Ve iz te'ezzene rabbukum le in şekertum le ezidennekum ve le in kefertum inne azabi le şedid."},
  {id: 1758, sure_no: 8, ayet_no: 8, sure_adi: "İbrahim", text: "وَقَالَ مُوسٰٓى اِنْ تَكْفُرُٓوا اَنْتُمْ وَمَنْ فِي الْاَرْضِ جَم۪يعاًۙ فَاِنَّ اللّٰهَ لَغَنِيٌّ حَم۪يدٌ", translation: "Musa dedi ki: \"Eğer siz ve yeryüzünde bulunanların tamamı nankörlük etseniz bile, iyi bilin ki Allah, Hiçbir Şeye Muhtaç Olmayan'dır, Övgüye Değer Yegane Varlık'tır.\"", transliteration: "Ve kale musa in tekfuru entum ve men fil ardı cemi'an fe innallahe le ganiyyun hamid."},
  {id: 1759, sure_no: 9, ayet_no: 9, sure_adi: "İbrahim", text: "اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذ۪ينَ مِنْ قَبْلِكُمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَۜۛ وَالَّذ۪ينَ مِنْ بَعْدِهِمْۜۛ لَا يَعْلَمُهُمْ اِلَّا اللّٰهُۜ جَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَرَدُّٓوا اَيْدِيَهُمْ ف۪ٓي اَفْوَاهِهِمْ وَقَالُٓوا اِنَّا كَفَرْنَا بِمَٓا اُرْسِلْتُمْ بِه۪ وَاِنَّا لَف۪ي شَكٍّ مِمَّا تَدْعُونَـنَٓا اِلَيْهِ مُر۪يبٍ", translation: "Sizden öncekilerin; Nuh, Ad, Semud halklarının ve onlardan sonra gelenlerin haberleri size gelmedi mi? Allah'tan başkası onları bilmez. Resulleri onlara beyyinelerle geldiği halde onlar zorla susturmaya çalışarak: \"Biz, kendisiyle gönderildiğiniz şeyi küfrediyoruz; bizi çağırdığınız şey konusunda kaygı verici ikilem içindeyiz.\" dediler.", transliteration: "E lem ye'tikum nebeullezine min kablikum kavmi nuhın ve adin ve semud, vellezine min ba'dihim, la ya'lemuhum illallah, caethum rusuluhum bil beyyinati fe reddu eydiyehum fi efvahihim ve kalu inna keferna bi ma ursiltum bihi ve inna le fi şekkin mimma ted'unena ileyhi murib."},
  {id: 1760, sure_no: 10, ayet_no: 10, sure_adi: "İbrahim", text: "قَالَتْ رُسُلُهُمْ اَفِي اللّٰهِ شَكٌّ فَاطِرِ السَّمٰوَاتِ وَالْاَرْضِۜ يَدْعُوكُمْ لِيَغْفِرَ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرَكُمْ اِلٰٓى اَجَلٍ مُسَمًّىۜ قَالُٓوا اِنْ اَنْتُمْ اِلَّا بَشَرٌ مِثْلُنَاۜ تُر۪يدُونَ اَنْ تَصُدُّونَا عَمَّا كَانَ يَعْبُدُ اٰبَٓاؤُ۬نَا فَأْتُونَا بِسُلْطَانٍ مُب۪ينٍ", translation: "Resulleri dedi ki: \"Göklere ve yere belli bir fıtrat veren, sizi, suçlarınızı bağışlamak için çağıran ve belirlenmiş bir ecele kadar sizi erteleyen Allah hakkında mı kuşkudasınız?\" Dediler: \"Siz de ancak bizim gibi sadece bir beşersiniz. Atalarımızın kulluk ettiklerinden bizi çevirmek istiyorsunuz. Öyleyse açık bir sultan getirin!\"", transliteration: "Kalet rusuluhum e fillahi şekkun fatırıs semavati vel ard, yed'ukum li yagfire lekum min zunubikum ve yuahhırekum ila ecelin musemma, kalu in entum illa beşerun misluna, turidune en tesudduna amma kane ya'budu abauna fe'tuna bi sultanin mubin."},
  {id: 1761, sure_no: 11, ayet_no: 11, sure_adi: "İbrahim", text: "قَالَتْ لَهُمْ رُسُلُهُمْ اِنْ نَحْنُ اِلَّا بَشَرٌ مِثْلُكُمْ وَلٰكِنَّ اللّٰهَ يَمُنُّ عَلٰى مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ۜ وَمَا كَانَ لَـنَٓا اَنْ نَأْتِيَكُمْ بِسُلْطَانٍ اِلَّا بِاِذْنِ اللّٰهِۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ", translation: "Resulleri onlara dediler ki: \"Biz de ancak sizin gibi beşeriz. Ancak Allah, kullarından dilediği kimseye iyilikte bulunur. Allah'ın izni olmaksızın bizim size bir sultan getirmemiz olacak şey değildir. Mü'minler yalnızca Allah'a tevekkül etsinler.\"", transliteration: "Kalet lehum rusuluhum in nahnu illa beşerun mislukum ve lakinnallahe yemunnu ala men yeşau min ibadih, ve ma kane lena en ne'tiyekum bi sultanin illa bi iznillah, ve alallahi fel yetevekkelil mu'minun."},
  {id: 1762, sure_no: 12, ayet_no: 12, sure_adi: "İbrahim", text: "وَمَا لَـنَٓا اَلَّا نَتَوَكَّلَ عَلَى اللّٰهِ وَقَدْ هَدٰينَا سُبُلَنَاۜ وَلَنَصْبِرَنَّ عَلٰى مَٓا اٰذَيْتُمُونَاۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ۟", translation: "\"Bize ne oluyor ki Allah'a tevekkül etmeyelim? Üstelik bize yollarımızı göstermişken. Elbette biz, bize yaşattığınız sıkıntılara sabredeceğiz. Tevekkül edenler de yalnızca Allah'a tevekkül etsinler.\"", transliteration: "Ve ma lena ella netevekkele alallahi ve kad hedana subulena, ve le nasbirenne ala ma azeytumuna, ve alallahi fel yetevekkelil mutevekkilun ."},
  {id: 1763, sure_no: 13, ayet_no: 13, sure_adi: "İbrahim", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِنْ اَرْضِنَٓا اَوْ لَتَعُودُنَّ ف۪ي مِلَّتِنَاۜ فَاَوْحٰٓى اِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظَّالِم۪ينَۙ", translation: "Kafirler, Resullerine: \"Sizi mutlaka yurdumuzdan süreceğiz veya kesinlikle bizim milletimize dönmek zorundasınız!\" dediler. Bunun üzerine Rabb'leri onlara: \"Zalimleri mutlaka yok edeceğiz.\" diye vahyetti.", transliteration: "Ve kalellezine keferu li rusulihim le nuhricennekum min ardına ev le teudunne fi milletina, fe evha ileyhim rabbuhum le nuhlikennez zalimin."},
  {id: 1764, sure_no: 14, ayet_no: 14, sure_adi: "İbrahim", text: "وَلَنُسْكِنَنَّكُمُ الْاَرْضَ مِنْ بَعْدِهِمْۜ ذٰلِكَ لِمَنْ خَافَ مَقَام۪ي وَخَافَ وَع۪يدِ", translation: "\"Ve onlardan sonra sizi mutlaka yeryüzünde yerleştireceğiz. Bu, makamımı ve uyarımı dikkate alan kimseler içindir.\"", transliteration: "Ve le nuskinennekumul arda min ba'dihim, zalike li men hafe makami ve hafe vaid."},
  {id: 1765, sure_no: 15, ayet_no: 15, sure_adi: "İbrahim", text: "وَاسْتَفْتَحُوا وَخَابَ كُلُّ جَبَّارٍ عَن۪يدٍۙ", translation: "Fetih istediler. Bile bile haktan yüz çeviren zorbaların hepsi kaybetti.", transliteration: "Vesteftehu ve habe kullu cebbarin anid."},
  {id: 1766, sure_no: 16, ayet_no: 16, sure_adi: "İbrahim", text: "مِنْ وَرَٓائِه۪ جَهَنَّمُ وَيُسْقٰى مِنْ مَٓاءٍ صَد۪يدٍۙ", translation: "Onun ardından da Cehennem vardır ve irinli sudan içirilecektir.", transliteration: "Min veraihi cehennemu ve yuska min main sadid."},
  {id: 1767, sure_no: 17, ayet_no: 17, sure_adi: "İbrahim", text: "يَتَجَرَّعُهُ وَلَا يَكَادُ يُس۪يغُهُ وَيَأْت۪يهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَمَا هُوَ بِمَيِّتٍۜ وَمِنْ وَرَٓائِه۪ عَذَابٌ غَل۪يظٌ", translation: "Onu yutmaya çalışacak fakat boğazından geçiremeyecekler. Her yanından kendilerine ölüm geldiği halde yine de ölemeyecekler. Ardından da daha ağır bir azap tadacaklar.", transliteration: "Yetecerreuhu ve la yekadu yusiguhu ve ye'tihil mevtu min kulli mekanin ve ma huve bi meyyit, ve min veraihi azabun galiz."},
  {id: 1768, sure_no: 18, ayet_no: 18, sure_adi: "İbrahim", text: "مَثَلُ الَّذ۪ينَ كَفَرُوا بِرَبِّهِمْ اَعْمَالُهُمْ كَرَمَادٍۨ اشْتَدَّتْ بِهِ الرّ۪يحُ ف۪ي يَوْمٍ عَاصِفٍۜ لَا يَقْدِرُونَ مِمَّا كَسَبُوا عَلٰى شَيْءٍۜ ذٰلِكَ هُوَ الضَّلَالُ الْبَع۪يدُ", translation: "Rabb'lerine nankörlük edenlerin yaptıkları, fırtınalı bir günde rüzgarın şiddetle savurduğu bir kül gibidir. Kazandıklarından hiçbir şey ellerinde kalmaz. İşte bu derin bir sapkınlıktır.", transliteration: "Meselullezine keferu bi rabbihim a'maluhum ke remadinişteddet bihir rihu fi yevmin asıf, la yakdirune mimma kesebu ala şey', zalike huved dalalul baid."},
  {id: 1769, sure_no: 19, ayet_no: 19, sure_adi: "İbrahim", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ اِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَد۪يدٍۙ", translation: "Allah'ın gökleri ve yeri Hakk ile yarattığını görmüyor musun? Dilerse sizi yok eder, yeni bir nesil yaratır;", transliteration: "E lem tere ennallahe halakas semavati vel arda bil hakk, in yeşa' yuzhibkum ve ye'ti bi halkın cedid."},
  {id: 1770, sure_no: 20, ayet_no: 20, sure_adi: "İbrahim", text: "وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَز۪يزٍ", translation: "Bu Allah'a göre asla büyük bir iş değildir.", transliteration: "Ve ma zalike alallahi bi aziz."},
  {id: 1771, sure_no: 21, ayet_no: 21, sure_adi: "İbrahim", text: "وَبَرَزُوا لِلّٰهِ جَم۪يعاً فَقَالَ الضُّعَفٰٓؤُ۬ا لِلَّذ۪ينَ اسْتَكْـبَرُٓوا اِنَّا كُنَّا لَكُمْ تَبَعاً فَهَلْ اَنْتُمْ مُغْنُونَ عَنَّا مِنْ عَذَابِ اللّٰهِ مِنْ شَيْءٍۜ قَالُوا لَوْ هَدٰينَا اللّٰهُ لَهَدَيْنَاكُمْۜ سَوَٓاءٌ عَلَيْنَٓا اَجَزِعْنَٓا اَمْ صَبَرْنَا مَا لَنَا مِنْ مَح۪يصٍ۟", translation: "Hepsi, Allah'ın huzuruna çıkacaklar. Güçsüz olan kimseler, büyüklük taslayanlara: \"Gerçekten biz size uyan kimselerdik. Şimdi siz, Allah'ın azabından herhangi bir şeyi bizden savabilir misiniz?\" diyecekler. Onlar: \"Eğer Allah bize bir yol gösterseydi biz de kesinlikle size yol göstericiler olurduk. Sabretsek de sabretmesek de bizim için birdir. Bizim için kaçacak bir yer yoktur.\" dediler.", transliteration: "Ve berezu lillahi cemian fe kaled duafau lillezinestekberu inna kunna lekum tebean fe hel entum mugnune anna min azabillahi min şey', kalu lev hedanallahu le hedeynakum, sevaun aleyna ecezi'na em saberna ma lena min mahis."},
  {id: 1772, sure_no: 22, ayet_no: 22, sure_adi: "İbrahim", text: "وَقَالَ الشَّيْطَانُ لَمَّا قُضِيَ الْاَمْرُ اِنَّ اللّٰهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُكُمْ فَاَخْلَفْتُكُمْۜ وَمَا كَانَ لِيَ عَلَيْكُمْ مِنْ سُلْطَانٍ اِلَّٓا اَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ ل۪يۚ فَلَا تَلُومُون۪ي وَلُومُٓوا اَنْفُسَكُمْۜ مَٓا اَنَا۬ بِمُصْرِخِكُمْ وَمَٓا اَنْتُمْ بِمُصْرِخِيَّۜ اِنّ۪ي كَفَرْتُ بِمَٓا اَشْرَكْتُمُونِ مِنْ قَبْلُۜ اِنَّ الظَّالِم۪ينَ لَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Ne zaman ki hüküm gerçekleşti, şeytan onlara: \"Şüphesiz ki Allah'ın vaktiyle yaptığı uyarıların hepsi gerçekleşti. Ben de size vadettim. Benim verdiğim sözler ise boş çıktı. Zaten benim size karşı zorlayıcı bir gücüm yoktu. Ben size sadece çağrıda bulundum, siz de kendiliğinizden çağrıma uydunuz. O halde beni kınamayın, kendinizi kınayın. Ne ben sizi kurtarabilirim ne de siz beni kurtarabilirsiniz. Gerçekten ben, daha önce beni Allah'a ortak koşmanızı da yok saymıştım.\" dedi. Zalimlerin hakkı acı bir azaptır.", transliteration: "Ve kaleş şeytanu lemma kudıyel emru innallahe veadekum va'del hakkı ve veadtukum fe ahleftukum, ve ma kane liye aleykum min sultanin illa en deavtukum festecebtum li, fe la telumuni ve lumu enfusekum, ma ene bi musrihikum ve ma entum bi musrıhıyy, inni kefertu bi ma eşrektumuni min kabl, innaz zalimine lehum azabun elim."},
  {id: 1773, sure_no: 23, ayet_no: 23, sure_adi: "İbrahim", text: "وَاُدْخِلَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا بِاِذْنِ رَبِّهِمْۜ تَحِيَّتُهُمْ ف۪يهَا سَلَامٌ", translation: "İman eden ve salihatı yapanlar, Rabb'lerinin izni ile içinde sürekli kalmak üzere içlerinden ırmaklar akan Cennetlere konulurlar. Oradaki yaşam temennileri \"selam\"dır.", transliteration: "Ve udhilellezine amenu ve amilus salihati cennatin tecri min tahtihel enharu halidine fiha bi izni rabbihim, tehıyyetuhum fiha selam."},
  {id: 1774, sure_no: 24, ayet_no: 24, sure_adi: "İbrahim", text: "اَلَمْ تَرَ كَيْفَ ضَرَبَ اللّٰهُ مَثَلاً كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ اَصْلُهَا ثَابِتٌ وَفَرْعُهَا فِي السَّمَٓاءِۙ", translation: "Görmüyor musun Allah nasıl örnek verdi? Hoş bir söz, bakımlı bir ağaç gibidir. Onun kökü sabittir. Ve onun dalları göktedir.", transliteration: "E lem tere keyfe daraballahu meselen kelimeten tayyibeten ke şeceretin tayyibetin asluha sabitun ve fer'uha fis sema."},
  {id: 1775, sure_no: 25, ayet_no: 25, sure_adi: "İbrahim", text: "تُؤْت۪ٓي اُكُلَهَا كُلَّ ح۪ينٍ بِاِذْنِ رَبِّهَاۜ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ", translation: "O, Rabb'inin izni ile her zaman ürün verir. Allah, öğüt almaları için insanlara böyle örnekler verir.", transliteration: "Tu'ti ukuleha kulle hinin bi izni rabbiha, ve yadrıbullahul emsale lin nasi leallehum yetezekkerun."},
  {id: 1776, sure_no: 26, ayet_no: 26, sure_adi: "İbrahim", text: "وَمَثَلُ كَلِمَةٍ خَب۪يثَةٍ كَشَجَرَةٍ خَب۪يثَةٍۨ اجْتُثَّتْ مِنْ فَوْقِ الْاَرْضِ مَا لَهَا مِنْ قَرَارٍ", translation: "Kötü bir söz de kökü yerden sökülmüş, ayakta duramayan kötü bir ağaç gibidir.", transliteration: "Ve meselu kelimetin habisetin ke şeceretin habisetinictusset min fevkıl ardı ma leha min karar."},
  {id: 1777, sure_no: 27, ayet_no: 27, sure_adi: "İbrahim", text: "يُثَبِّتُ اللّٰهُ الَّذ۪ينَ اٰمَنُوا بِالْقَوْلِ الثَّابِتِ فِي الْحَيٰوةِ الدُّنْيَا وَفِي الْاٰخِرَةِۚ وَيُضِلُّ اللّٰهُ الظَّالِم۪ينَ وَيَفْعَلُ اللّٰهُ مَا يَشَٓاءُ۟", translation: "Allah, iman edenleri dünya hayatında da ahirette de sapasağlam ayakta tutar. Allah, zalim olanları saptırır. Allah dilediğini yapar.", transliteration: "Yusebbitullahullezine amenu bil kavlis sabiti fil hayatid dunya ve fil ahıreh, ve yudıllullahuz zalimine ve yef'alullahu ma yeşa'."},
  {id: 1778, sure_no: 28, ayet_no: 28, sure_adi: "İbrahim", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ بَدَّلُوا نِعْمَتَ اللّٰهِ كُفْراً وَاَحَلُّوا قَوْمَهُمْ دَارَ الْبَوَارِۙ", translation: "Allah'ın nimetini Küfre çevirenleri ve böylece kendi toplumlarını yok olma yurduna sürükleyenleri görüyorsun değil mi?", transliteration: "E lem tere ilellezine beddelu ni'metallahi kufren ve ehallu kavmehum darel bevar."},
  {id: 1779, sure_no: 29, ayet_no: 29, sure_adi: "İbrahim", text: "جَهَنَّمَۚ يَصْلَوْنَهَاۜ وَبِئْسَ الْقَرَارُ", translation: "Gidecekleri yer Cehennem'dir; kalacakları yer ne kötüdür!", transliteration: "Cehennem, yaslevneha, ve bi'sel karar."},
  {id: 1780, sure_no: 30, ayet_no: 30, sure_adi: "İbrahim", text: "وَجَعَلُوا لِلّٰهِ اَنْدَاداً لِيُضِلُّوا عَنْ سَب۪يلِه۪ۜ قُلْ تَمَتَّعُوا فَاِنَّ مَص۪يرَكُمْ اِلَى النَّارِ", translation: "O'nun yolundan saptırmak için Allah'a birtakım eşler koştular. De ki: \"Yararlanın bakalım! Gideceğiniz yer ateştir.\"", transliteration: "Ve cealu lillahi endaden li yudıllu an sebilih, kul temetteu fe inne masirekum ilen nar."},
  {id: 1781, sure_no: 31, ayet_no: 31, sure_adi: "İbrahim", text: "قُلْ لِعِبَادِيَ الَّذ۪ينَ اٰمَنُوا يُق۪يمُوا الصَّلٰوةَ وَيُنْفِقُوا مِمَّا رَزَقْنَاهُمْ سِراًّ وَعَلَانِيَةً مِنْ قَبْلِ اَنْ يَأْتِيَ يَوْمٌ لَا بَيْعٌ ف۪يهِ وَلَا خِلَالٌ", translation: "İman eden kullarıma söyle: \"İçinde alışverişin ve dostluğun olmadığı o gün gelmeden önce, salatı ikame etsinler, kendilerine verdiğimiz rızıktan gizli açık infak etsinler.\"", transliteration: "Kul li ibadiyellezine amenu yukimus salate ve yunfiku mimma razaknahum sirren ve alaniyeten min kabli en ye'tiye yevmun la bey'un fihi ve la hilal."},
  {id: 1782, sure_no: 32, ayet_no: 32, sure_adi: "İbrahim", text: "اَللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَاَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَخْرَجَ بِه۪ مِنَ الثَّمَرَاتِ رِزْقاً لَكُمْۚ وَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِي الْبَحْرِ بِاَمْرِه۪ۚ وَسَخَّرَ لَكُمُ الْاَنْهَارَۚ", translation: "O Allah ki, gökleri ve yeri yarattı, gökten su indirdi ve böylece onunla ürünleri size rızık olarak yetiştirdi; koyduğu yasalarla denizlerde yüzüp giden gemileri hizmetinize verdi ve ırmakları yararınıza sundu.", transliteration: "Allahullezi halakas semavati vel arda ve enzele mines semai maen fe ahrece bihi mines semerati rızkan lekum, ve sehhare lekumul fulke li tecriye fil bahri bi emrih, ve sehhare lekumul enhar."},
  {id: 1783, sure_no: 33, ayet_no: 33, sure_adi: "İbrahim", text: "وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَٓائِبَيْنِۚ وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۚ", translation: "Düzenli bir hareket içinde olan Güneş'i ve Ay'ı hizmetinize verdi. Geceyi ve gündüzü de hizmetinize verdi.", transliteration: "Ve sehhare lekumuş şemse vel kamere daibeyn, ve sehhare lekumul leyle ven nehar."},
  {id: 1784, sure_no: 34, ayet_no: 34, sure_adi: "İbrahim", text: "وَاٰتٰيكُمْ مِنْ كُلِّ مَا سَاَلْتُمُوهُۜ وَاِنْ تَعُدُّوا نِعْمَتَ اللّٰهِ لَا تُحْصُوهَاۜ اِنَّ الْاِنْسَانَ لَظَلُومٌ كَفَّارٌ۟", translation: "Ondan istediğiniz her şeyden size verdi. Eğer Allah'ın nimetini sayacak olsanız sayamazsınız. Doğrusu insan, çok zalimdir, çok kafirdir.", transliteration: "Ve atakum min kulli ma se'eltumuh, ve in teuddu ni'metallahi la tuhsuha,innel insane le zalumun keffar."},
  {id: 1785, sure_no: 35, ayet_no: 35, sure_adi: "İbrahim", text: "وَاِذْ قَالَ اِبْرٰه۪يمُ رَبِّ اجْعَلْ هٰذَا الْبَلَدَ اٰمِناً وَاجْنُبْن۪ي وَبَنِيَّ اَنْ نَعْبُدَ الْاَصْنَامَۜ", translation: "Bir zamanlar İbrahim şöyle demişti: \"Rabb'im! Bu şehri güvenli kıl. Beni ve çocuklarımı putlara kulluk yapmaktan uzak tut.\"", transliteration: "Ve iz kale ibrahimu rabbic'al hazel belede aminen vecnubni ve beniyye en na'budel asnam."},
  {id: 1786, sure_no: 36, ayet_no: 36, sure_adi: "İbrahim", text: "رَبِّ اِنَّهُنَّ اَضْلَلْنَ كَث۪يراً مِنَ النَّاسِۚ فَمَنْ تَبِعَن۪ي فَاِنَّهُ مِنّ۪يۚ وَمَنْ عَصَان۪ي فَاِنَّكَ غَفُورٌ رَح۪يمٌ", translation: "\"Rabb'im! Doğrusu onlar, insanlardan çoğunu saptırdılar. Kim, bana uyarsa, o bendendir. Ve bana uymayana gelince: Kuşkusuz Sen, Çok Bağışlayıcı'sın, Rahmeti Kesintisiz Olan'sın.\"", transliteration: "Rabbi innehunne adlelne kesiren minen nas, fe men tebiani fe innehu minni, ve men asani fe inneke gafurun rahim."},
  {id: 1787, sure_no: 37, ayet_no: 37, sure_adi: "İbrahim", text: "رَبَّـنَٓا اِنّ۪ٓي اَسْكَنْتُ مِنْ ذُرِّيَّت۪ي بِوَادٍ غَيْرِ ذ۪ي زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِۙ رَبَّـنَا لِيُق۪يمُوا الصَّلٰوةَ فَاجْعَلْ اَفْـِٔدَةً مِنَ النَّاسِ تَهْو۪ٓي اِلَيْهِمْ وَارْزُقْهُمْ مِنَ الثَّمَرَاتِ لَعَلَّهُمْ يَشْكُرُونَ", translation: "\"Rabb'imiz! Gerçekten ben, neslimden bir kısmını sahipsiz, ekine elverişli olmayan vadiye; Beyt-i Haram'ın yanına yerleştirdim; Rabb'imiz! Salatı ikame etsinler. İnsanlardan bir kısmının gönlünü onlara yönelt. Ve onları kimi ürünlerle rızıklandır. Umulur ki onlar şükrederler.\"", transliteration: "Rabbena inni eskentu min zurriyyeti bi vadin gayri zi zer'ın inde beytilkel muharremi rabbena li yukimus salate fec'al ef'ideten minen nasi tehvi ileyhim verzukhum mines semerati leallehum yeşkurun."},
  {id: 1788, sure_no: 38, ayet_no: 38, sure_adi: "İbrahim", text: "رَبَّـنَٓا اِنَّكَ تَعْلَمُ مَا نُخْف۪ي وَمَا نُعْلِنُۜ وَمَا يَخْفٰى عَلَى اللّٰهِ مِنْ شَيْءٍ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِ", translation: "\"Rabb'imiz! Sen, gizlediğimiz ve açığa vurduğumuz her şeyi bilirsin. Yerde ve gökte hiçbir şey Allah'a gizli kalmaz.\"", transliteration: "Rabbena inneke ta'lemu ma nuhfi ve ma nu'lin, ve ma yahfa alallahi min şey'infil ardı ve la fis sema."},
  {id: 1789, sure_no: 39, ayet_no: 39, sure_adi: "İbrahim", text: "اَلْحَمْدُ لِلّٰهِ الَّذ۪ي وَهَبَ ل۪ي عَلَى الْكِبَرِ اِسْمٰع۪يلَ وَاِسْحٰقَۜ اِنَّ رَبّ۪ي لَسَم۪يعُ الدُّعَٓاءِ", translation: "\"Hamd, yaşlılığımda bana İsmail'i ve İshak'ı bağışlayan Allah'a aittir. Kuşkusuz, benim Rabb'im duayı işitendir.\"", transliteration: "Elhamdulillahillezi vehebe li alel kiberi ismaile ve ishak, inne rabbi le semiud dua."},
  {id: 1790, sure_no: 40, ayet_no: 40, sure_adi: "İbrahim", text: "رَبِّ اجْعَلْن۪ي مُق۪يمَ الصَّلٰوةِ وَمِنْ ذُرِّيَّت۪يۗ رَبَّـنَا وَتَقَبَّلْ دُعَٓاءِ", translation: "\"Rabbim! Beni ve soyumu salatı ikame eden kıl. Rabb'imiz isteğimi kabul et.\"", transliteration: "Rabbic'alni mukimas salati ve min zurriyyeti rabbena ve tekabbel dua."},
  {id: 1791, sure_no: 41, ayet_no: 41, sure_adi: "İbrahim", text: "رَبَّـنَا اغْفِرْ ل۪ي وَلِوَالِدَيَّ وَلِلْمُؤْمِن۪ينَ يَوْمَ يَقُومُ الْحِسَابُ۟", translation: "\"Rabb'imiz! Hesabın görüleceği gün, beni, annemi, babamı ve Mü'minleri affet.\"", transliteration: "Rabbenagfirli ve li valideyye ve lil mu'minine yevme yekumul hisab."},
  {id: 1792, sure_no: 42, ayet_no: 42, sure_adi: "İbrahim", text: "وَلَا تَحْسَبَنَّ اللّٰهَ غَافِلاً عَمَّا يَعْمَلُ الظَّالِمُونَۜ اِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ ف۪يهِ الْاَبْصَارُۙ", translation: "Sakın zalimlerin yaptıkları şeylerden Allah'ı gafil sanma. O hesaplaşmayı gözlerin dehşetten donup kalacağı güne erteliyor.", transliteration: "Ve la tahsebennallahe gafilen amma ya'meluz zalimun, innema yuahhıruhum li yevmin teşhasu fihil ebsar."},
  {id: 1793, sure_no: 43, ayet_no: 43, sure_adi: "İbrahim", text: "مُهْطِع۪ينَ مُقْنِع۪ي رُؤُ۫سِهِمْ لَا يَرْتَدُّ اِلَيْهِمْ طَرْفُهُمْۚ وَاَفْـِٔدَتُهُمْ هَوَٓاءٌۜ", translation: "Başlarını yukarı dikerek, bakışları donup kalmış bir şekilde umutsuzca koşarlar.", transliteration: "Muhtıine muknii ruusihim la yerteddu ileyhim tarfuhum, ve ef'idetuhum heva'."},
  {id: 1794, sure_no: 44, ayet_no: 44, sure_adi: "İbrahim", text: "وَاَنْذِرِ النَّاسَ يَوْمَ يَأْت۪يهِمُ الْعَذَابُۙ فَيَقُولُ الَّذ۪ينَ ظَلَمُوا رَبَّـنَٓا اَخِّرْنَٓا اِلٰٓى اَجَلٍ قَر۪يبٍۙ نُجِبْ دَعْوَتَكَ وَنَتَّبِـعِ الرُّسُلَۜ اَوَلَمْ تَكُونُٓوا اَقْسَمْتُمْ مِنْ قَبْلُ مَا لَكُمْ مِنْ زَوَالٍۙ", translation: "Azabın kendilerine geleceği gün ile insanları uyar. O zaman kendilerine haksızlık yapanlar: \"Rabb'imiz! Bize biraz daha süre ver de Senin çağrını kabul edelim ve Resullere uyalım.\" diyecekler. Allah da: \"Daha önce sizin için bir tükenişin, inişe geçişin olmadığına yemin edenler sizler değil miydiniz?\" diyecek.", transliteration: "Ve enzirin nase yevme ye'tihimul azabu fe yekulullezine zalemu rabbena ahhırna ila ecelin karibin nucib da'veteke ve nettebiır rusul, e ve lem tekunu aksemtum min kablu ma lekum min zeval."},
  {id: 1795, sure_no: 45, ayet_no: 45, sure_adi: "İbrahim", text: "وَسَكَنْتُمْ ف۪ي مَسَاكِنِ الَّذ۪ينَ ظَلَمُٓوا اَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْاَمْثَالَ", translation: "Ve siz, kendilerine zulmeden halkların yurtlarında yaşadınız. Onlara neler yaptığımız sizlere belli olmuştu, örneklerle açıklamıştık.", transliteration: "Ve sekentum fi mesakinillezine zalemu enfusehum ve tebeyyene lekum keyfe fealna bihimve darabna lekumul emsal."},
  {id: 1796, sure_no: 46, ayet_no: 46, sure_adi: "İbrahim", text: "وَقَدْ مَكَرُوا مَكْرَهُمْ وَعِنْدَ اللّٰهِ مَكْرُهُمْۜ وَاِنْ كَانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ الْجِبَالُ", translation: "Onlar, her türlü planı yapmışlardı. Oysaki dağları yerinden oynatacak derecede güçlü olduğunu sandıkları planları Allah'ın denetimi altındadır.", transliteration: "Ve kad mekeru mekrehum ve indallahi mekruhum, ve in kane mekruhum li tezule minhul cibal."},
  {id: 1797, sure_no: 47, ayet_no: 47, sure_adi: "İbrahim", text: "فَلَا تَحْسَبَنَّ اللّٰهَ مُخْلِفَ وَعْدِه۪ رُسُلَهُۜ اِنَّ اللّٰهَ عَز۪يزٌ ذُوانْتِقَامٍۜ", translation: "Öyleyse sakın Allah'ın Resullerine verdiği sözden cayacağını sanma! Allah, Mutlak Üstün Olan'dır, Hesap Sorucu'dur.", transliteration: "Fe la tahsebennallahe muhlife va'dihi rusuleh, innallahe azizun zuntikam."},
  {id: 1798, sure_no: 48, ayet_no: 48, sure_adi: "İbrahim", text: "يَوْمَ تُبَدَّلُ الْاَرْضُ غَيْرَ الْاَرْضِ وَالسَّمٰوَاتُ وَبَرَزُوا لِلّٰهِ الْوَاحِدِ الْقَهَّارِ", translation: "O Gün yeryüzü ve gökler başka bir hale dönüştürülür. Ve onlar, bir ve gücüne karşı konulamaz Allah'ın huzuruna çıkarlar.", transliteration: "Yevme tubeddelul ardu gayrel ardı ves semavatu ve berezu lillahil vahıdil kahhar."},
  {id: 1799, sure_no: 49, ayet_no: 49, sure_adi: "İbrahim", text: "وَتَرَى الْمُجْرِم۪ينَ يَوْمَئِذٍ مُقَرَّن۪ينَ فِي الْاَصْفَادِۚ", translation: "O Gün; suçluları, birbirlerine yaklaştırılarak zincirlere vurulmuş görürsün.", transliteration: "Ve terel mucrimine yevme izin mukarrenine fil asfad."},
  {id: 1800, sure_no: 50, ayet_no: 50, sure_adi: "İbrahim", text: "سَرَاب۪يلُهُمْ مِنْ قَطِرَانٍ وَتَغْشٰى وُجُوهَهُمُ النَّارُۙ", translation: "Onların giysileri katrandandır ve yüzlerini ateş sarmıştır.", transliteration: "Serabiluhum min katıranin ve tagşa vucuhehumun nar."},
  {id: 1801, sure_no: 51, ayet_no: 51, sure_adi: "İbrahim", text: "لِيَجْزِيَ اللّٰهُ كُلَّ نَفْسٍ مَا كَسَبَتْۜ اِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ", translation: "Allah, herkese kazandığına göre karşılık verecektir. Kuşkusuz Allah, hesabı çabuk görendir.", transliteration: "Li yecziyallahu kulle nefsin ma kesebet, innallahe seriul hısab."},
  {id: 1802, sure_no: 52, ayet_no: 52, sure_adi: "İbrahim", text: "هٰذَا بَلَاغٌ لِلنَّاسِ وَلِيُنْذَرُوا بِه۪ وَلِيَعْلَمُٓوا اَنَّمَا هُوَ اِلٰهٌ وَاحِدٌ وَلِيَذَّكَّرَ اُو۬لُوا الْاَلْبَابِ", translation: "Bu Kur'an, kendisiyle uyarılmaları, Allah'ın bir tek ilah olduğunun bilinmesi ve sağlıklı düşünen akıl sahiplerinin öğüt almaları için insanlara bir mesajdır.", transliteration: "Haza belagun lin nasi ve li yunzeru bihi ve li ya'lemu ennema huve ilahun vahidun ve li yezzekkere ulul elbab."},
  {id: 1803, sure_no: 1, ayet_no: 1, sure_adi: "Hicr", text: "الٓـرٰ۠ تِلْكَ اٰيَاتُ الْكِتَابِ وَقُرْاٰنٍ مُب۪ينٍ", translation: "Elif, Lam, Ra. İşte bunlar, Kitap'ın ve Kur'an-ı Mubin'in ayetleridir.", transliteration: "Elif lam ra tilke ayatul kitabi ve kur'anin mubin."},
  {id: 1804, sure_no: 2, ayet_no: 2, sure_adi: "Hicr", text: "رُبَمَا يَوَدُّ الَّذ۪ينَ كَفَرُوا لَوْ كَانُوا مُسْلِم۪ينَ", translation: "Gün gelecek, Kafirler: \"Keşke Müslim olsaydık.\" diye yakınacaklar.", transliteration: "Rubema yeveddullezine keferu lev kanu muslimin."},
  {id: 1805, sure_no: 3, ayet_no: 3, sure_adi: "Hicr", text: "ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا وَيُلْهِهِمُ الْاَمَلُ فَسَوْفَ يَعْلَمُونَ", translation: "Bırak onları; yiyip, içip faydalansınlar; beklentileri onları oyalasın. Fakat yakında gerçeği anlayacaklar.", transliteration: "Zerhum ye'kulu ve yetemetteu ve yulhihimul emelu fe sevfe ya'lemun."},
  {id: 1806, sure_no: 4, ayet_no: 4, sure_adi: "Hicr", text: "وَمَٓا اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا وَلَهَا كِتَابٌ مَعْلُومٌ", translation: "Biz, hiçbir kenti, bilinen bir kitabı olmaksızın helak etmedik.", transliteration: "Ve ma ehlekna min karyetin illa ve leha kitabun ma'lum."},
  {id: 1807, sure_no: 5, ayet_no: 5, sure_adi: "Hicr", text: "مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُونَ", translation: "Hiçbir toplum, ecelini öne alamaz ve geciktiremez.", transliteration: "Ma tesbiku min ummetin eceleha ve ma yeste'hırun."},
  {id: 1808, sure_no: 6, ayet_no: 6, sure_adi: "Hicr", text: "وَقَالُوا يَٓا اَيُّهَا الَّذ۪ي نُزِّلَ عَلَيْهِ الذِّكْرُ اِنَّكَ لَمَجْنُونٌۜ", translation: "\"Ey! Kendisine Zikir indirildiğini söyleyen, sen gerçekten mecnunsun!\" dediler.", transliteration: "Ve kalu ya eyyuhellezi nuzzile aleyhiz zikru inneke le mecnun."},
  {id: 1809, sure_no: 7, ayet_no: 7, sure_adi: "Hicr", text: "لَوْ مَا تَأْت۪ينَا بِالْمَلٰٓئِكَةِ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "\"Eğer doğru söylüyorsan, bize melekleri getir de görelim!\"", transliteration: "Lev ma te'tina bil melaiketi in kunte minas sadıkin."},
  {id: 1810, sure_no: 8, ayet_no: 8, sure_adi: "Hicr", text: "مَا نُنَزِّلُ الْمَلٰٓئِكَةَ اِلَّا بِالْحَقِّ وَمَا كَانُٓوا اِذاً مُنْظَر۪ينَ", translation: "Biz, melekleri ancak Hakk ile indiririz. O zaman da işleri bitirilmiş olur.", transliteration: "Ma nunezzilul melaikete illa bil hakkı ve ma kanu izen munzarin."},
  {id: 1811, sure_no: 9, ayet_no: 9, sure_adi: "Hicr", text: "اِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَاِنَّا لَهُ لَحَافِظُونَ", translation: "Zikri Biz indirdik ve kesinlikle onun koruyucusu da Biziz.", transliteration: "İnna nahnu nezzelnez zikre ve inna lehu le hafizun."},
  {id: 1812, sure_no: 10, ayet_no: 10, sure_adi: "Hicr", text: "وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ ف۪ي شِيَعِ الْاَوَّل۪ينَ", translation: "Ve ant olsun ki senden önceki geçmiş topluluklara da gönderdik.", transliteration: "Ve le kad erselna min kablike fi şiyaıl evvelin."},
  {id: 1813, sure_no: 11, ayet_no: 11, sure_adi: "Hicr", text: "وَمَا يَأْت۪يهِمْ مِنْ رَسُولٍ اِلَّا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Kendilerine gelen resullerden alay etmedikleri olmadı.", transliteration: "Ve ma ye'tihim min resulin illa kanu bihi yestehziun."},
  {id: 1814, sure_no: 12, ayet_no: 12, sure_adi: "Hicr", text: "كَذٰلِكَ نَسْلُكُهُ ف۪ي قُلُوبِ الْمُجْرِم۪ينَۙ", translation: "Böylece onu mücrimlerin kalplerine sokarız.", transliteration: "Kezalike neslukuhu fi kulubil mucrimin."},
  {id: 1815, sure_no: 13, ayet_no: 13, sure_adi: "Hicr", text: "لَا يُؤْمِنُونَ بِه۪ وَقَدْ خَلَتْ سُنَّةُ الْاَوَّل۪ينَ", translation: "Kendilerinden öncekilerin sünneti geçtiği halde ona inanmazlar.", transliteration: "La yu'minune bihi ve kad halet sunnetul evvelin."},
  {id: 1816, sure_no: 14, ayet_no: 14, sure_adi: "Hicr", text: "وَلَوْ فَتَحْنَا عَلَيْهِمْ بَاباً مِنَ السَّمَٓاءِ فَظَلُّوا ف۪يهِ يَعْرُجُونَۙ", translation: "Gökten bir kapı açsak da oraya çıksalar bile.", transliteration: "Ve lev fetahna aleyhim baben mines semai fe zallu fihi ya'rucun."},
  {id: 1817, sure_no: 15, ayet_no: 15, sure_adi: "Hicr", text: "لَقَالُٓوا اِنَّمَا سُكِّرَتْ اَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَسْحُورُونَ۟", translation: "\"Herhalde gözlerimiz döndürüldü, doğrusu büyülenmiş bir topluluğuz.\" derlerdi.", transliteration: "Le kalu innema sukkiret ebsaruna bel nahnu kavmun meshurun."},
  {id: 1818, sure_no: 16, ayet_no: 16, sure_adi: "Hicr", text: "وَلَقَدْ جَعَلْنَا فِي السَّمَٓاءِ بُرُوجاً وَزَيَّنَّاهَا لِلنَّاظِر۪ينَۙ", translation: "Ant olsun, Biz, semada burçlar yaptık ve bakanlar için onu donattık.", transliteration: "Ve le kad cealna fis semai burucen ve zeyyennaha lin nazırin."},
  {id: 1819, sure_no: 17, ayet_no: 17, sure_adi: "Hicr", text: "وَحَفِظْنَاهَا مِنْ كُلِّ شَيْطَانٍ رَج۪يمٍۙ", translation: "Onu her racim şeytandan koruduk.", transliteration: "Ve hafıznaha min kulli şeytanin recim."},
  {id: 1820, sure_no: 18, ayet_no: 18, sure_adi: "Hicr", text: "اِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَاَتْبَعَهُ شِهَابٌ مُب۪ينٌ", translation: "Ancak, kulak hırsızlığı yapan olursa, onu parlak bir alev kovalar.", transliteration: "İlla menisterakas sem'a fe etbeahu şihabun mubin."},
  {id: 1821, sure_no: 19, ayet_no: 19, sure_adi: "Hicr", text: "وَالْاَرْضَ مَدَدْنَاهَا وَاَلْقَيْنَا ف۪يهَا رَوَاسِيَ وَاَنْبَتْنَا ف۪يهَا مِنْ كُلِّ شَيْءٍ مَوْزُونٍ", translation: "Yeryüzünü yaydık ve oraya ağır baskılar yerleştirdik. Ve orada her türlü bitkiyi bir ölçüye göre yetiştirdik.", transliteration: "Vel arda medednaha ve elkayna fiha revasiye ve enbetna fiha min kulli şey'in mevzun."},
  {id: 1822, sure_no: 20, ayet_no: 20, sure_adi: "Hicr", text: "وَجَعَلْنَا لَكُمْ ف۪يهَا مَعَايِشَ وَمَنْ لَسْتُمْ لَهُ بِرَازِق۪ينَ", translation: "Sizin için de geçimlerini sizin sağlamadıklarınız için de orada, geçim kaynakları meydana getirdik.", transliteration: "Ve cealna lekum fiha meayişe ve men lestum lehu bi razıkin."},
  {id: 1823, sure_no: 21, ayet_no: 21, sure_adi: "Hicr", text: "وَاِنْ مِنْ شَيْءٍ اِلَّا عِنْدَنَا خَزَٓائِنُهُۘ وَمَا نُنَزِّلُـهُٓ اِلَّا بِقَدَرٍ مَعْلُومٍ", translation: "Hazineleri Bizim yanımızda olmayan hiçbir şey yoktur. Ve Biz, bilinen bir kaderi olmaksızın onu indirmeyiz.", transliteration: "Ve in min şey'in illa indena hazainuhu ve ma nunezziluhu illa bi kaderin ma'lum."},
  {id: 1824, sure_no: 22, ayet_no: 22, sure_adi: "Hicr", text: "وَاَرْسَلْنَا الرِّيَاحَ لَوَاقِـحَ فَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً فَاَسْقَيْنَاكُمُوهُۚ وَمَٓا اَنْتُمْ لَهُ بِخَازِن۪ينَ", translation: "Biz, rüzgarları aşılayıcı olarak gönderdik. Gökten su indirdik ve sizi onunla suya kavuşturduk. Onun hazinelerini oluşturan siz değilsiniz.", transliteration: "Ve erselner riyaha levakıha fe enzelna mines semai maen fe eskaynakumuh, ve ma entum lehu bi hazinin."},
  {id: 1825, sure_no: 23, ayet_no: 23, sure_adi: "Hicr", text: "وَاِنَّا لَنَحْنُ نُحْـي۪ وَنُم۪يتُ وَنَحْنُ الْوَارِثُونَ", translation: "Sadece Biz diriltir ve Biz öldürürüz. Ve varis olan da Biziz.", transliteration: "Ve inna le nahnu nuhyi ve numitu ve nahnul varisun."},
  {id: 1826, sure_no: 24, ayet_no: 24, sure_adi: "Hicr", text: "وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِم۪ينَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِر۪ينَ", translation: "Ant olsun ki, sizden öncekileri biliyoruz. Ve ant olsun ki sizden sonrakileri de biliyoruz.", transliteration: "Ve le kad alimnel mustakdimine minkum ve le kad alimnel muste'hırin."},
  {id: 1827, sure_no: 25, ayet_no: 25, sure_adi: "Hicr", text: "وَاِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْۜ اِنَّهُ حَك۪يمٌ عَل۪يمٌ۟", translation: "Rabb'in, bizzat onları haşreder. Kuşkusuz O, En İyi Hüküm Veren'dir, Her Şeyi Bilen'dir.", transliteration: "Ve inne rabbeke huve yahşuruhum, innehu hakimun alim."},
  {id: 1828, sure_no: 26, ayet_no: 26, sure_adi: "Hicr", text: "وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ صَلْصَالٍ مِنْ حَمَأٍ مَسْنُونٍۚ", translation: "Ant olsun ki; Biz, insanı salsalinden, dönüşüme uğramış bir balçıktan yarattık.", transliteration: "Ve le kad halaknel insane min salsalin min hamein mesnun."},
  {id: 1829, sure_no: 27, ayet_no: 27, sure_adi: "Hicr", text: "وَالْجَٓانَّ خَلَقْنَاهُ مِنْ قَبْلُ مِنْ نَارِ السَّمُومِ", translation: "Cinni daha önce \"Semum'un ateşinden\" yarattık.", transliteration: "Vel canne halaknahu min kablu min naris semum."},
  {id: 1830, sure_no: 28, ayet_no: 28, sure_adi: "Hicr", text: "وَاِذْ قَالَ رَبُّكَ لِلْمَلٰٓئِكَةِ اِنّ۪ي خَالِقٌ بَشَراً مِنْ صَلْصَالٍ مِنْ حَمَأٍ مَسْنُونٍ", translation: "Hani Rabb'in meleklere: \"Ben salsalinden, dönüşüme uğramış bir balçıktan, bir beşer yaratacağım.\" demişti.", transliteration: "Ve iz kale rabbuke lil melaiketi inni halikun beşeren min salsalin min hamein mesnun."},
  {id: 1831, sure_no: 29, ayet_no: 29, sure_adi: "Hicr", text: "فَاِذَا سَوَّيْتُهُ وَنَفَخْتُ ف۪يهِ مِنْ رُوح۪ي فَقَعُوا لَهُ سَاجِد۪ينَ", translation: "\"Onu biçimlendirip ve ona ruhumdan üflediğimde, hemen ona secde edin!\"", transliteration: "Fe iza sevveytuhu ve nefahtu fihi min ruhi fekau lehu sacidin."},
  {id: 1832, sure_no: 30, ayet_no: 30, sure_adi: "Hicr", text: "فَسَجَدَ الْمَلٰٓئِكَةُ كُلُّهُمْ اَجْمَعُونَۙ", translation: "Bunun üzerine bütün melekler ona secde ettiler.", transliteration: "Fe secedel melaiketu kulluhum ecmaun."},
  {id: 1833, sure_no: 31, ayet_no: 31, sure_adi: "Hicr", text: "اِلَّٓا اِبْل۪يسَۜ اَبٰٓى اَنْ يَكُونَ مَعَ السَّاجِد۪ينَ", translation: "İblis hariç. O, secde edenlerle birlikte olmaktan kaçındı.", transliteration: "İlla iblis, eba en yekune meas sacidin."},
  {id: 1834, sure_no: 32, ayet_no: 32, sure_adi: "Hicr", text: "قَالَ يَٓا اِبْل۪يسُ مَا لَكَ اَلَّا تَكُونَ مَعَ السَّاجِد۪ينَ", translation: "\"Ey iblis! Neden secde edenlerle birlikte olmadın?\" dedi.", transliteration: "Kale ya iblisu ma leke ella tekune meas sacidin."},
  {id: 1835, sure_no: 33, ayet_no: 33, sure_adi: "Hicr", text: "قَالَ لَمْ اَكُنْ لِاَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِنْ صَلْصَالٍ مِنْ حَمَأٍ مَسْنُونٍ", translation: "İblis: \"Ben; salsalinden, dönüşüme uğramış bir balçıktan yarattığın bir beşere, secde etmem.\" Dedi.", transliteration: "Kale lem ekun li escude li beşerin halaktehu min salsalin min hamein mesnun."},
  {id: 1836, sure_no: 34, ayet_no: 34, sure_adi: "Hicr", text: "قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَج۪يمٌ", translation: "Allah: \"Çık oradan! Sen kesinlikle kovulmuş birisin.\" dedi.", transliteration: "Kale fahruc minha fe inneke recim."},
  {id: 1837, sure_no: 35, ayet_no: 35, sure_adi: "Hicr", text: "وَاِنَّ عَلَيْكَ اللَّعْنَةَ اِلٰى يَوْمِ الدّ۪ينِ", translation: "\"Lanet, Din Günü'ne kadar senin üzerindedir.\" dedi.", transliteration: "Ve inne aleykel la'nete ila yevmid din."},
  {id: 1838, sure_no: 36, ayet_no: 36, sure_adi: "Hicr", text: "قَالَ رَبِّ فَاَنْظِرْن۪ٓي اِلٰى يَوْمِ يُبْعَثُونَ", translation: "İblis: \"Rabbim! Öyleyse yeniden diriltilecekleri güne kadar, bana süre tanı.\" dedi.", transliteration: "Kale rabbi fe enzırni ila yevmi yub'asun."},
  {id: 1839, sure_no: 37, ayet_no: 37, sure_adi: "Hicr", text: "قَالَ فَاِنَّكَ مِنَ الْمُنْظَر۪ينَۙ", translation: "Allah: \"Sen, süre verilenlerdensin;\"", transliteration: "Kale fe inneke minel munzarin."},
  {id: 1840, sure_no: 38, ayet_no: 38, sure_adi: "Hicr", text: "اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُومِ", translation: "\"Bilinen zamanın gününe kadar.\" dedi.", transliteration: "İla yevmil vaktil ma'lum."},
  {id: 1841, sure_no: 39, ayet_no: 39, sure_adi: "Hicr", text: "قَالَ رَبِّ بِمَٓا اَغْوَيْتَن۪ي لَاُزَيِّنَنَّ لَهُمْ فِي الْاَرْضِ وَلَاُغْوِيَنَّهُمْ اَجْمَع۪ينَۙ", translation: "İblis: \"Rabb'im! Beni azdırmandan dolayı, ben de yeryüzündeki her şeyi cazip göstererek, kesinlikle onların hepsini azdıracağım.\"", transliteration: "Kale rabbi bi ma agveyteni le uzeyyinenne lehum fil ardı ve le ugviyennehum ecmein."},
  {id: 1842, sure_no: 40, ayet_no: 40, sure_adi: "Hicr", text: "اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَص۪ينَ", translation: "\"Ancak onlardan muhles kulların hariç.\"", transliteration: "İlla ıbadeke minhumul muhlasin."},
  {id: 1843, sure_no: 41, ayet_no: 41, sure_adi: "Hicr", text: "قَالَ هٰذَا صِرَاطٌ عَلَيَّ مُسْتَق۪يمٌ", translation: "Allah: \"Bu, Bana varan dosdoğru yoldur.\" dedi.", transliteration: "Kale haza sıratun aleyye mustekim."},
  {id: 1844, sure_no: 42, ayet_no: 42, sure_adi: "Hicr", text: "اِنَّ عِبَاد۪ي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ اِلَّا مَنِ اتَّبَعَكَ مِنَ الْغَاو۪ينَ", translation: "Sana uyan azgınlar hariç, kullarım üzerinde hiçbir yaptırım gücün yoktur.", transliteration: "İnne ıbadi leyse leke aleyhim sultanun illa menittebeake minel gavin."},
  {id: 1845, sure_no: 43, ayet_no: 43, sure_adi: "Hicr", text: "وَاِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ اَجْمَع۪ينَۙ", translation: "Onların tamamının buluşma yeri Cehennem'dir.", transliteration: "Ve inne cehenneme le mev'ıduhum ecmain."},
  {id: 1846, sure_no: 44, ayet_no: 44, sure_adi: "Hicr", text: "لَهَا سَبْعَةُ اَبْوَابٍۜ لِكُلِّ بَابٍ مِنْهُمْ جُزْءٌ مَقْسُومٌ۟", translation: "Onun yedi kapısı vardır. Her kapıya onlardan bir grup ayrılmıştır.", transliteration: "Leha seb'atu ebvab, likulli babin minhum cuz'un maksum."},
  {id: 1847, sure_no: 45, ayet_no: 45, sure_adi: "Hicr", text: "اِنَّ الْمُتَّق۪ينَ ف۪ي جَنَّاتٍ وَعُيُونٍۜ", translation: "Takva sahipleri, Cennetlerde ve pınarların başlarındadırlar.", transliteration: "İnnel muttekine fi cennatin ve uyun."},
  {id: 1848, sure_no: 46, ayet_no: 46, sure_adi: "Hicr", text: "اُدْخُلُوهَا بِسَلَامٍ اٰمِن۪ينَ", translation: "Onlara: \"Güven ve esenlik içinde oraya girin.\" denecek.", transliteration: "Udhuluha bi selamin aminin."},
  {id: 1849, sure_no: 47, ayet_no: 47, sure_adi: "Hicr", text: "وَنَزَعْنَا مَا ف۪ي صُدُورِهِمْ مِنْ غِلٍّ اِخْوَاناً عَلٰى سُرُرٍ مُتَقَابِل۪ينَ", translation: "Ve onların göğüslerindeki kötü duyguların tamamını yok ettik. Onlar, kardeşler olarak, tahtlar üzerinde karşı karşıya otururlar.", transliteration: "Ve neza'na ma fi sudurihim min gıllin ıhvanen ala sururin mutekabilin."},
  {id: 1850, sure_no: 48, ayet_no: 48, sure_adi: "Hicr", text: "لَا يَمَسُّهُمْ ف۪يهَا نَصَبٌ وَمَا هُمْ مِنْهَا بِمُخْرَج۪ينَ", translation: "Orada, kendilerine hiçbir yorgunluk dokunmaz ve oradan hiç çıkarılmayacaklardır.", transliteration: "La yemessuhum fiha nasabun ve ma hum minha bi muhrecin."},
  {id: 1851, sure_no: 49, ayet_no: 49, sure_adi: "Hicr", text: "نَبِّئْ عِبَاد۪ٓي اَنّ۪ٓي اَنَا الْغَفُورُ الرَّح۪يمُۙ", translation: "Kullarıma haber ver: \"Ben, Çok Bağışlayıcıyım, Kesintisiz Rahmet Sahibiyim.\"", transliteration: "Nebbi' ibadi enni enel gafurur rahim."},
  {id: 1852, sure_no: 50, ayet_no: 50, sure_adi: "Hicr", text: "وَاَنَّ عَذَاب۪ي هُوَ الْعَذَابُ الْاَل۪يمُ", translation: "Fakat azabım, elem verici bir azaptır.", transliteration: "Ve enne azabi huvel azabul elim."},
  {id: 1853, sure_no: 51, ayet_no: 51, sure_adi: "Hicr", text: "وَنَبِّئْهُمْ عَنْ ضَيْفِ اِبْرٰه۪يمَۢ", translation: "Onlara İbrahim'in misafirlerinden haber ver.", transliteration: "Ve nebbi'hum an dayfi ibrahim."},
  {id: 1854, sure_no: 52, ayet_no: 52, sure_adi: "Hicr", text: "اِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلَاماًۜ قَالَ اِنَّا مِنْكُمْ وَجِلُونَ", translation: "İbrahim'in yanına girdiklerinde, \"Selam.\" dediler. İbrahim: \"Doğrusu, sizden korkuyoruz.\" dedi.", transliteration: "İz dehalu aleyhi fe kalu selama, kale inna minkum vecilun."},
  {id: 1855, sure_no: 53, ayet_no: 53, sure_adi: "Hicr", text: "قَالُوا لَا تَوْجَلْ اِنَّا نُبَشِّرُكَ بِغُلَامٍ عَل۪يمٍ", translation: "Onlar: \"Korkma! Biz, sana alim bir oğul müjdeliyoruz.\" dediler.", transliteration: "Kalu la tevcel inna nubeşşiruke bi gulamin alim."},
  {id: 1856, sure_no: 54, ayet_no: 54, sure_adi: "Hicr", text: "قَالَ اَبَشَّرْتُمُون۪ي عَلٰٓى اَنْ مَسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُونَ", translation: "İbrahim: \"Ben yaşlı bir beşer olduğum halde mi, beni müjdeliyorsunuz? Neye dayanarak bu müjdeyi veriyorsunuz?\"", transliteration: "Kale e beşşertumuni ala en messeniyel kiberu fe bime tubeşşirun."},
  {id: 1857, sure_no: 55, ayet_no: 55, sure_adi: "Hicr", text: "قَالُوا بَشَّرْنَاكَ بِالْحَقِّ فَلَا تَكُنْ مِنَ الْقَانِط۪ينَ", translation: "Onlar: \"Seni, hakk ile müjdeliyoruz. Asla ümidini kesenlerden olma.\" dediler.", transliteration: "Kalu beşşernake bil hakkı fe la tekun minel kanıtin."},
  {id: 1858, sure_no: 56, ayet_no: 56, sure_adi: "Hicr", text: "قَالَ وَمَنْ يَقْنَطُ مِنْ رَحْمَةِ رَبِّه۪ٓ اِلَّا الضَّٓالُّونَ", translation: "İbrahim: \"Rabb'inin rahmetinden, sapkınlardan başka kim ümidini keser?\" dedi.", transliteration: "Kale ve men yaknetu min rahmeti rabbihi illad dallun."},
  {id: 1859, sure_no: 57, ayet_no: 57, sure_adi: "Hicr", text: "قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُونَ", translation: "İbrahim: \"Ey elçiler! Sizin niyetiniz ne?\" dedi.", transliteration: "Kale fe ma hatbukum eyyuhel murselun."},
  {id: 1860, sure_no: 58, ayet_no: 58, sure_adi: "Hicr", text: "قَالُٓوا اِنَّٓا اُرْسِلْـنَٓا اِلٰى قَوْمٍ مُجْرِم۪ينَۙ", translation: "\"Biz, suç işleyen bir halka gönderildik.\" dediler.", transliteration: "Kalu inna ursilna ila kavmin mucrimin."},
  {id: 1861, sure_no: 59, ayet_no: 59, sure_adi: "Hicr", text: "اِلَّٓا اٰلَ لُوطٍۜ اِنَّا لَمُنَجُّوهُمْ اَجْمَع۪ينَۙ", translation: "\"Ancak Lut ailesinin tamamını kurtaracağız.\"", transliteration: "İlla ale lut, inna le muneccuhum ecma'in."},
  {id: 1862, sure_no: 60, ayet_no: 60, sure_adi: "Hicr", text: "اِلَّا امْرَاَتَهُ قَدَّرْنَٓاۙ اِنَّهَا لَمِنَ الْغَابِر۪ينَ۟", translation: "\"Onun karısı hariç. Onun, mutlaka geride kalanlardan olmasını takdir ettik.\"", transliteration: "İllemre'etehu kadderna inneha le minel gabirin."},
  {id: 1863, sure_no: 61, ayet_no: 61, sure_adi: "Hicr", text: "فَلَمَّا جَٓاءَ اٰلَ لُوطٍۨ الْمُرْسَلُونَۙ", translation: "Elçiler, Lut'un ailesine geldiklerinde,", transliteration: "Fe lemma cae ale lutınil murselun."},
  {id: 1864, sure_no: 62, ayet_no: 62, sure_adi: "Hicr", text: "قَالَ اِنَّكُمْ قَوْمٌ مُنْكَرُونَ", translation: "Lut: \"Doğrusu çekinilecek kimselersiniz.\" dedi.", transliteration: "Kale innekum kavmun munkerun."},
  {id: 1865, sure_no: 63, ayet_no: 63, sure_adi: "Hicr", text: "قَالُوا بَلْ جِئْنَاكَ بِمَا كَانُوا ف۪يهِ يَمْتَرُونَ", translation: "Elçiler: \"Hayır! Biz sana hakkında kuşku duyulan azabın haberini getirdik;\"", transliteration: "Kalu bel ci'nake bi ma kanu fihi yemterun."},
  {id: 1866, sure_no: 64, ayet_no: 64, sure_adi: "Hicr", text: "وَاَتَيْنَاكَ بِالْحَقِّ وَاِنَّا لَصَادِقُونَ", translation: "\"Doğru söyleyenleriz! Biz sana Hakk'ı getirdik.\"", transliteration: "Ve eteynake bil hakkı ve inna le sadikun."},
  {id: 1867, sure_no: 65, ayet_no: 65, sure_adi: "Hicr", text: "فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِنَ الَّيْلِ وَاتَّبِـعْ اَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ وَامْضُوا حَيْثُ تُؤْمَرُونَ", translation: "\"Hemen gecenin bir bölümünde, aileni yola çıkar, arkalarından onları takip et. Sağa sola takılıp oyalanmadan, bir an önce emrolunduğunuz yere doğru gidin.\"", transliteration: "Fe esri bi ehlike bi kıt'ın minel leyli vettebı' edbarehum ve la yeltefit minkum ehadun vamdu haysu tu'merun."},
  {id: 1868, sure_no: 66, ayet_no: 66, sure_adi: "Hicr", text: "وَقَضَيْنَٓا اِلَيْهِ ذٰلِكَ الْاَمْرَ اَنَّ دَابِرَ هٰٓؤُ۬لَٓاءِ مَقْطُوعٌ مُصْبِح۪ينَ", translation: "Sabaha girerlerken, onların kökünün kesileceği kararımızı, ona bildirdik.", transliteration: "Ve kadayna ileyhi zalikel emre enne dabire haulai maktuun musbihin."},
  {id: 1869, sure_no: 67, ayet_no: 67, sure_adi: "Hicr", text: "وَجَٓاءَ اَهْلُ الْمَد۪ينَةِ يَسْتَبْشِرُونَ", translation: "Şehir halkı sevinerek geldiler.", transliteration: "Ve cae ehlul medineti yestebşirun."},
  {id: 1870, sure_no: 68, ayet_no: 68, sure_adi: "Hicr", text: "قَالَ اِنَّ هٰٓؤُ۬لَٓاءِ ضَيْف۪ي فَلَا تَفْضَحُونِۙ", translation: "Lut: \"Bunlar benim misafirlerim. Sakın beni mahcup etmeyin;\"", transliteration: "Kale inne haulai dayfi fe la tefdahun."},
  {id: 1871, sure_no: 69, ayet_no: 69, sure_adi: "Hicr", text: "وَاتَّقُوا اللّٰهَ وَلَا تُخْزُونِ", translation: "\"Allah'a karşı takva sahibi olun. Beni rezil etmeyin.\" dedi.", transliteration: "Vettekullahe ve la tuhzun."},
  {id: 1872, sure_no: 70, ayet_no: 70, sure_adi: "Hicr", text: "قَالُٓوا اَوَلَمْ نَنْهَكَ عَنِ الْعَالَم۪ينَ", translation: "Şehir halkı: \"Seni, başkalarının işine karışmaktan men etmedik mi?\" dediler.", transliteration: "Kalu e ve lem nenheke anil alemin."},
  {id: 1873, sure_no: 71, ayet_no: 71, sure_adi: "Hicr", text: "قَالَ هٰٓؤُ۬لَٓاءِ بَنَات۪ٓي اِنْ كُنْتُمْ فَاعِل۪ينَۜ", translation: "Lut. \"Eğer bir şey yapacaksanız işte kızlarım.\" dedi.", transliteration: "Kale haulai benati in kuntum failin."},
  {id: 1874, sure_no: 72, ayet_no: 72, sure_adi: "Hicr", text: "لَعَمْرُكَ اِنَّهُمْ لَف۪ي سَكْرَتِهِمْ يَعْمَهُونَ", translation: "Ömrüne ant olsun ki, onlar sarhoşlukları içinde bocalayıp duruyorlardı.", transliteration: "Le amruke innehum le fi sekretihim ya'mehun."},
  {id: 1875, sure_no: 73, ayet_no: 73, sure_adi: "Hicr", text: "فَاَخَذَتْهُمُ الصَّيْحَةُ مُشْرِق۪ينَۙ", translation: "Tan yeri ağarırken korkunç bir ses onları yakaladı.", transliteration: "Fe ehazethumus sayhatu muşrikin."},
  {id: 1876, sure_no: 74, ayet_no: 74, sure_adi: "Hicr", text: "فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِنْ سِجّ۪يلٍۜ", translation: "Böylece şehri altüst ettik ve üzerlerine balçıktan pişirilmiş taşlar yağdırdık.", transliteration: "Fe cealna aliyeha safileha ve emterna aleyhim hıcareten min siccil."},
  {id: 1877, sure_no: 75, ayet_no: 75, sure_adi: "Hicr", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِلْمُتَوَسِّم۪ينَ", translation: "Bunda, derin kavrayış yetisi olanlar için kesinlikle ayetler vardır.", transliteration: "İnne fi zalike le ayatin lil mutevessimin ."},
  {id: 1878, sure_no: 76, ayet_no: 76, sure_adi: "Hicr", text: "وَاِنَّهَا لَبِسَب۪يلٍ مُق۪يمٍ", translation: "O, bir yol üzerinde durmaktadır.", transliteration: "Ve inneha le bi sebilin mukim."},
  {id: 1879, sure_no: 77, ayet_no: 77, sure_adi: "Hicr", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِلْمُؤْمِن۪ينَۜ", translation: "Bunda, iman edenler için bir ayet vardır.", transliteration: "İnne fi zalike le ayeten lil mu'minin."},
  {id: 1880, sure_no: 78, ayet_no: 78, sure_adi: "Hicr", text: "وَاِنْ كَانَ اَصْحَابُ الْاَيْكَةِ لَظَالِم۪ينَۙ", translation: "Eykeliler gerçekten zalim kimselerdi.", transliteration: "Ve in kane ashabul eyketi le zalimin ."},
  {id: 1881, sure_no: 79, ayet_no: 79, sure_adi: "Hicr", text: "فَانْتَقَمْنَا مِنْهُمْۢ وَاِنَّهُمَا لَبِاِمَامٍ مُب۪ينٍۜ۟", translation: "Onlardan intikam aldık. İkisi de kesinlikle açık bir rehberdir.", transliteration: "Fentekamna minhum, ve innehuma le bi imamin mubin."},
  {id: 1882, sure_no: 80, ayet_no: 80, sure_adi: "Hicr", text: "وَلَقَدْ كَذَّبَ اَصْحَابُ الْحِجْرِ الْمُرْسَل۪ينَۙ", translation: "Ant olsun ki Hicr halkı da gönderilen elçileri yalanladı.", transliteration: "Ve le kad kezzebe ashabul hıcril murselin."},
  {id: 1883, sure_no: 81, ayet_no: 81, sure_adi: "Hicr", text: "وَاٰتَيْنَاهُمْ اٰيَاتِنَا فَكَانُوا عَنْهَا مُعْرِض۪ينَۙ", translation: "Onlara ayetlerimizi verdik, fakat ondan yüz çevirdiler.", transliteration: "Ve ateynahum ayatina fe kanu anha mu'rıdin."},
  {id: 1884, sure_no: 82, ayet_no: 82, sure_adi: "Hicr", text: "وَكَانُوا يَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتاً اٰمِن۪ينَ", translation: "Güya dağlardan güvenli evler yontuyorlardı.", transliteration: "Ve kanu yanhıtune minel cibali buyuten aminin."},
  {id: 1885, sure_no: 83, ayet_no: 83, sure_adi: "Hicr", text: "فَاَخَذَتْهُمُ الصَّيْحَةُ مُصْبِح۪ينَۙ", translation: "Derken, sabaha karşı korkunç bir ses onları yakaladı.", transliteration: "Fe ehazethumus sayhatu musbıhin."},
  {id: 1886, sure_no: 84, ayet_no: 84, sure_adi: "Hicr", text: "فَمَٓا اَغْنٰى عَنْهُمْ مَا كَانُوا يَكْسِبُونَۜ", translation: "Sahip oldukları şeylerin, kendilerine hiçbir yararı olmadı.", transliteration: "Fe ma agna anhum ma kanu yeksibun."},
  {id: 1887, sure_no: 85, ayet_no: 85, sure_adi: "Hicr", text: "وَمَا خَلَقْنَا السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَٓا اِلَّا بِالْحَقِّۜ وَاِنَّ السَّاعَةَ لَاٰتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَم۪يلَ", translation: "Biz; gökleri, yeri ve ikisinin arasındakilerini Hakk ile yarattık. Elbette ki o Sa'at mutlaka gelecektir. O halde, sen onlara aldırış etme ve güzelce yüz çevir.", transliteration: "Ve ma halaknes semavati vel arda ve ma beynehuma illa bil hakk, ve innes saate le atiyetun fasfehıs safhal cemil."},
  {id: 1888, sure_no: 86, ayet_no: 86, sure_adi: "Hicr", text: "اِنَّ رَبَّكَ هُوَ الْخَلَّاقُ الْعَل۪يمُ", translation: "Rabb'in Her Şeyi Yaratan'dır ve Her Şeyi Bilen'dir.", transliteration: "İnne rabbeke huvel hallakul alim."},
  {id: 1889, sure_no: 87, ayet_no: 87, sure_adi: "Hicr", text: "وَلَقَدْ اٰتَيْنَاكَ سَبْعاً مِنَ الْمَثَان۪ي وَالْقُرْاٰنَ الْعَظ۪يمَ", translation: "Ant olsun ki sana seb'an mine'l- mesani ve yüce Kur'an'ı verdik.", transliteration: "Ve le kad ateynake seb'an minel mesani vel kur'anel azim."},
  {id: 1890, sure_no: 88, ayet_no: 88, sure_adi: "Hicr", text: "لَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِه۪ٓ اَزْوَاجاً مِنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِن۪ينَ", translation: "Onlardan bazılarına, kat kat vererek, kendilerini yararlandırdığımız şeylere imrenme. Onlar için üzülme. Sen, Mü'minlere kol kanat ger.", transliteration: "La temuddenne ayneyke ila ma metta'na bihi ezvacen minhum ve la tahzen aleyhim vahfıd cenahake lil mu'minin."},
  {id: 1891, sure_no: 89, ayet_no: 89, sure_adi: "Hicr", text: "وَقُلْ اِنّ۪ٓي اَنَا النَّذ۪يرُ الْمُب۪ينُۚ", translation: "De ki: \"Ben ancak apaçık bir uyarıcıyım.\"", transliteration: "Ve kul inni enen nezirul mubin."},
  {id: 1892, sure_no: 90, ayet_no: 90, sure_adi: "Hicr", text: "كَمَٓا اَنْزَلْنَا عَلَى الْمُقْتَسِم۪ينَۙ", translation: "Muktesimlere indirdiğimiz şey gibi.", transliteration: "Ke ma enzelna alel muktesimin."},
  {id: 1893, sure_no: 91, ayet_no: 91, sure_adi: "Hicr", text: "اَلَّذ۪ينَ جَعَلُوا الْقُرْاٰنَ عِض۪ينَ", translation: "Onlar şimdi de Kur'an'ı parça parça yaptılar.", transliteration: "Ellezine cealul kur'ane ıdin."},
  {id: 1894, sure_no: 92, ayet_no: 92, sure_adi: "Hicr", text: "فَوَرَبِّكَ لَنَسْـَٔلَنَّهُمْ اَجْمَع۪ينَۙ", translation: "Rabb'ine ant olsun ki kesinlikle onların hepsine soracağız.", transliteration: "Fe ve rabbike le nes'elennehum ecmain."},
  {id: 1895, sure_no: 93, ayet_no: 93, sure_adi: "Hicr", text: "عَمَّا كَانُوا يَعْمَلُونَ", translation: "Yaptıkları şeylerden.", transliteration: "Amma kanu ya'melun."},
  {id: 1896, sure_no: 94, ayet_no: 94, sure_adi: "Hicr", text: "فَاصْدَعْ بِمَا تُؤْمَرُ وَاَعْرِضْ عَنِ الْمُشْرِك۪ينَ", translation: "Emrolunduğun şeyi açıkça söyle ve Müşriklere aldırma.", transliteration: "Fasda' bi ma tu'meru ve a'rıd anil muşrikin."},
  {id: 1897, sure_no: 95, ayet_no: 95, sure_adi: "Hicr", text: "اِنَّا كَفَيْنَاكَ الْمُسْتَهْزِء۪ينَۙ", translation: "Alay edenlere karşı, Biz, sana yeteriz.", transliteration: "İnna kefeynakel mustehziin."},
  {id: 1898, sure_no: 96, ayet_no: 96, sure_adi: "Hicr", text: "اَلَّذ۪ينَ يَجْعَلُونَ مَعَ اللّٰهِ اِلٰهاً اٰخَرَۚ فَسَوْفَ يَعْلَمُونَ", translation: "Allah'ın yanı sıra başka ilah edinenler, yakında gerçeği anlayacaklar!", transliteration: "Ellezine yec'alune meallahi ilahen ahar, fe sevfe ya'lemun."},
  {id: 1899, sure_no: 97, ayet_no: 97, sure_adi: "Hicr", text: "وَلَقَدْ نَعْلَمُ اَنَّكَ يَض۪يقُ صَدْرُكَ بِمَا يَقُولُونَۙ", translation: "Ant olsun; Biz, onların söylediklerinden dolayı senin göğsünün daraldığını biliyoruz.", transliteration: "Ve le kad na'lemu enneke yadiku sadruke bi ma yekulun."},
  {id: 1900, sure_no: 98, ayet_no: 98, sure_adi: "Hicr", text: "فَسَبِّـحْ بِحَمْدِ رَبِّكَ وَكُنْ مِنَ السَّاجِد۪ينَۙ", translation: "Rabb'ini hamd ile tesbih et ve secde edenlerden ol.", transliteration: "Fe sebbih bi hamdi rabbike ve kun mines sacidin."},
  {id: 1901, sure_no: 99, ayet_no: 99, sure_adi: "Hicr", text: "وَاعْبُدْ رَبَّكَ حَتّٰى يَأْتِيَكَ الْيَق۪ينُ", translation: "Sana yakin gelinceye kadar, Rabb'ine kulluk et!", transliteration: "Va'bud rabbeke hatta ye'tiyekel yakin."},
  {id: 1902, sure_no: 1, ayet_no: 1, sure_adi: "Nahl", text: "اَتٰٓى اَمْرُ اللّٰهِ فَلَا تَسْتَعْجِلُوهُۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يُشْرِكُونَ", translation: "Allah'ın emri geldi. Artık onda acele etmeyin. O, onların şirk koştukları şeylerden münezzehtir ve yücedir.", transliteration: "Eta emrullahi fe la testa'ciluh, subhanehu ve teala amma yuşrikun."},
  {id: 1903, sure_no: 2, ayet_no: 2, sure_adi: "Nahl", text: "يُنَزِّلُ الْمَلٰٓئِكَةَ بِالرُّوحِ مِنْ اَمْرِه۪ عَلٰى مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ٓ اَنْ اَنْذِرُٓوا اَنَّهُ لَٓا اِلٰهَ اِلَّٓا اَنَا۬ فَاتَّقُونِ", translation: "\"Benden başka ilah yoktur, öyleyse bana karşı takva sahibi olun.\" uyarısında bulunmaları için kullarından dilediğine emrinden ruh ile melekleri indirir.", transliteration: "Yunezzilul melaikete bir ruhi min emrihi ala men yeşau min ibadihi en enziru ennehu la ilahe illa ene fettekun."},
  {id: 1904, sure_no: 3, ayet_no: 3, sure_adi: "Nahl", text: "خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ تَعَالٰى عَمَّا يُشْرِكُونَ", translation: "Gökleri ve yeryüzünü hakikat ile yarattı. O, onların şirk koştuklarından yücedir.", transliteration: "Halakas semavati vel arda bil hakk, teala amma yuşrikun."},
  {id: 1905, sure_no: 4, ayet_no: 4, sure_adi: "Nahl", text: "خَلَقَ الْاِنْسَانَ مِنْ نُطْفَةٍ فَاِذَا هُوَ خَص۪يمٌ مُب۪ينٌ", translation: "İnsanı bir nutfeden yarattı. Böyleyken bir de bakarsın, o apaçık bir düşmandır.", transliteration: "Halakal insane min nutfetin fe iza huve hasimun mubin."},
  {id: 1906, sure_no: 5, ayet_no: 5, sure_adi: "Nahl", text: "وَالْاَنْعَامَ خَلَقَهَاۚ لَكُمْ ف۪يهَا دِفْءٌ وَمَنَافِعُ وَمِنْهَا تَأْكُلُونَۖ", translation: "Hayvanları, sizin için O yarattı. Isınma ve birçok yararları yanında, onlardan yiyecekler de elde edersiniz.", transliteration: "Vel en'ame halakaha, lekum fiha dif'un ve menafiu ve minha te'kulun."},
  {id: 1907, sure_no: 6, ayet_no: 6, sure_adi: "Nahl", text: "وَلَكُمْ ف۪يهَا جَمَالٌ ح۪ينَ تُر۪يحُونَ وَح۪ينَ تَسْرَحُونَۖ", translation: "Akşamları yayılmaktan gelirlerken de sabahları yayılıma gönderirken de onlarda sizin için bir güzellik vardır.", transliteration: "Ve lekum fiha cemalun hine turihune ve hine tesrehun."},
  {id: 1908, sure_no: 7, ayet_no: 7, sure_adi: "Nahl", text: "وَتَحْمِلُ اَثْقَالَكُمْ اِلٰى بَلَدٍ لَمْ تَكُونُوا بَالِغ۪يهِ اِلَّا بِشِقِّ الْاَنْفُسِۜ اِنَّ رَبَّكُمْ لَرَؤُ۫فٌ رَح۪يمٌۙ", translation: "Zorlukla ulaşabileceğiniz yerlere yüklerinizi taşırlar. Kuşkusuz, Rabb'iniz Çok Şefkatli'dir, Rahmeti Kesintisiz'dir.", transliteration: "Ve tahmilu eskalekum ila beledin lem tekunu balıgihi illa bi şıkkıl enfus, inne rabbekum le raufun rahim."},
  {id: 1909, sure_no: 8, ayet_no: 8, sure_adi: "Nahl", text: "وَالْخَيْلَ وَالْبِغَالَ وَالْحَم۪يرَ لِتَرْكَبُوهَا وَز۪ينَةًۜ وَيَخْلُقُ مَا لَا تَعْلَمُونَ", translation: "Sizin için binek ve süs hayvanı olarak atları, katırları ve eşekleri yarattı. Ve O, bilmediğiniz şeyleri yaratmaktadır.", transliteration: "Vel hayle vel bigale vel hamire li terkebuha ve zineh, ve yahluku ma la ta'lemun."},
  {id: 1910, sure_no: 9, ayet_no: 9, sure_adi: "Nahl", text: "وَعَلَى اللّٰهِ قَصْدُ السَّب۪يلِ وَمِنْهَا جَٓائِرٌۜ وَلَوْ شَٓاءَ لَهَدٰيكُمْ اَجْمَع۪ينَ۟", translation: "Doğru yolu göstermek Allah'a aittir. Yolun eğrisi de vardır. Eğer O dileseydi, hepinizi hidayete erdirirdi.", transliteration: "Ve alallahi kasdus sebili ve minha cair, ve lev şae le hedakum ecmain."},
  {id: 1911, sure_no: 10, ayet_no: 10, sure_adi: "Nahl", text: "هُوَ الَّـذ۪ٓي اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً لَكُمْ مِنْهُ شَرَابٌ وَمِنْهُ شَجَرٌ ف۪يهِ تُس۪يمُونَ", translation: "Gökten su indiren O'dur. İçme sularınız O'ndandır. Hayvanlarınızı otlattığınız bitkiler de O'ndandır.", transliteration: "Huvellezi enzele mines semai maen lekum minhu şarabun ve minhu şecerun fihi tusimun."},
  {id: 1912, sure_no: 11, ayet_no: 11, sure_adi: "Nahl", text: "يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخ۪يلَ وَالْاَعْنَابَ وَمِنْ كُلِّ الثَّمَرَاتِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَتَفَكَّرُونَ", translation: "O su ile sizin için ekinler, zeytinler, hurmalar, üzümler ve her türlü meyveden bitiriyor. Kuşkusuz, düşünen bir halk için elbette bir ayet vardır.", transliteration: "Yunbitu lekum bihiz zer'a vez zeytune ven nahile vel a'nabe ve min kullis semerat, inne fi zalike le ayeten li kavmin yetefekkerun."},
  {id: 1913, sure_no: 12, ayet_no: 12, sure_adi: "Nahl", text: "وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۙ وَالشَّمْسَ وَالْقَمَرَۜ وَالنُّجُومُ مُسَخَّرَاتٌ بِاَمْرِه۪ۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَۙ", translation: "O, geceyi ve gündüzü, Güneş'i ve Ay'ı yararlanmanıza sundu. Yıldızlar da O'nun buyruğu ile boyun eğdirilmiştir. Bunda aklını kullanan bir halk için ayetler vardır.", transliteration: "Ve sehhara lekumul leyle ven nehare veş şemse vel kamer, ven nucumu musahharatun bi emrih, inne fi zalike le ayatin li kavmin ya'kılun."},
  {id: 1914, sure_no: 13, ayet_no: 13, sure_adi: "Nahl", text: "وَمَا ذَرَاَ لَكُمْ فِي الْاَرْضِ مُخْتَلِفاً اَلْوَانُهُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَذَّكَّرُونَ", translation: "Yeryüzünde, rengarenk şeyleri, sizin için üretip çoğalttı. Bunda öğüt alan bir halk için elbette ayet vardır.", transliteration: "Ve ma zerae lekum fil ardı muhtelifen elvanuh, inne fi zalike le ayeten li kavmin yezzekkerun."},
  {id: 1915, sure_no: 14, ayet_no: 14, sure_adi: "Nahl", text: "وَهُوَ الَّذ۪ي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْماً طَرِياًّ وَتَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَهَاۚ وَتَرَى الْفُلْكَ مَوَاخِرَ ف۪يهِ وَلِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ", translation: "Taze et yemeniz ve takındığınız süs eşyası çıkarmanız için denizi yararlanmanıza sunan O'dur. Lütfundan rızık aramanız için, onun içinde suları yararak giden gemiler görürsün. Umulur ki şükredersiniz.", transliteration: "Ve huvellezi sehharel bahre li te'kulu minhu lahmen tariyyen ve testahricu minhu hilyeten telbesuneha, ve terel fulke mevahira fihi ve li tebtegu min fadlihi ve leallekum teşkurun."},
  {id: 1916, sure_no: 15, ayet_no: 15, sure_adi: "Nahl", text: "وَاَلْقٰى فِي الْاَرْضِ رَوَاسِيَ اَنْ تَم۪يدَ بِكُمْ وَاَنْهَاراً وَسُبُلاً لَعَلَّكُمْ تَهْتَدُونَۙ", translation: "O, sizi sarsar diye yeryüzüne ağır baskı koydu. Yolunuzu bulmanız için nehirler ve yollardan izler bıraktı;", transliteration: "Ve elka fil ardı revasiye en temide bikum ve enharen ve subulen leallekum tehtedun."},
  {id: 1917, sure_no: 16, ayet_no: 16, sure_adi: "Nahl", text: "وَعَلَامَاتٍۜ وَبِالنَّجْمِ هُمْ يَهْتَدُونَ", translation: "Ve işaretler. Onlar, yıldızla yol bulurlar.", transliteration: "Ve alamat, ve bin necmi hum yehtedun."},
  {id: 1918, sure_no: 17, ayet_no: 17, sure_adi: "Nahl", text: "اَفَمَنْ يَخْلُقُ كَمَنْ لَا يَخْلُقُۜ اَفَلَا تَذَكَّرُونَ", translation: "O halde yaratan ile yaratmayan bir midir? Hala tezekkür etmez misiniz?", transliteration: "E fe men yahluku ke men la yahluk, e fe la tezekkerun."},
  {id: 1919, sure_no: 18, ayet_no: 18, sure_adi: "Nahl", text: "وَاِنْ تَعُدُّوا نِعْمَةَ اللّٰهِ لَا تُحْصُوهَاۜ اِنَّ اللّٰهَ لَغَفُورٌ رَح۪يمٌ", translation: "Eğer Allah'ın nimetlerini sayacak olsanız, onu hesaplayamazsınız. Kuşkusuz, Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve in teuddu ni'metallahi la tuhsuha, innallahe le gafurun rahim."},
  {id: 1920, sure_no: 19, ayet_no: 19, sure_adi: "Nahl", text: "وَاللّٰهُ يَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ", translation: "Allah, gizlediğiniz şeyleri de açığa vurduğunuz şeyleri de bilir.", transliteration: "Vallahu ya'lemu ma tusirrune ve ma tu'linun."},
  {id: 1921, sure_no: 20, ayet_no: 20, sure_adi: "Nahl", text: "وَالَّذ۪ينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ لَا يَخْلُقُونَ شَيْـٔاً وَهُمْ يُخْلَقُونَۜ", translation: "Onların, Allah'ın yanı sıra dua1 ettikleri, bir şey yaratamazlar. Kendileri yaratılmışlardır.", transliteration: "Vellezine yed'une min dunillahi la yahlukune şey'en ve hum yuhlekun."},
  {id: 1922, sure_no: 21, ayet_no: 21, sure_adi: "Nahl", text: "اَمْوَاتٌ غَيْرُ اَحْيَٓاءٍۚ وَمَا يَشْعُرُونَۙ اَيَّانَ يُبْعَثُونَ۟", translation: "Diri değil, ölüdürler. Diriltilecekleri zamandan habersizdirler.", transliteration: "Emvatun gayru ahya', ve ma yeş'urune eyyane yub'asun."},
  {id: 1923, sure_no: 22, ayet_no: 22, sure_adi: "Nahl", text: "اِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ فَالَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ قُلُوبُهُمْ مُنْكِرَةٌ وَهُمْ مُسْتَكْبِرُونَ", translation: "Sizin ilahınız, bir tek ilahtır. Buna rağmen, ahirete iman etmeyenlerin kalpleri, bunu kabul etmez. Ve onlar büyüklenen kimselerdir.", transliteration: "İlahukum ilahun vahid, fellezine la yu'minune bil ahirati kulubuhum munkiretun ve hum mustekbirun."},
  {id: 1924, sure_no: 23, ayet_no: 23, sure_adi: "Nahl", text: "لَا جَرَمَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَۜ اِنَّهُ لَا يُحِبُّ الْمُسْتَكْبِر۪ينَ", translation: "Allah'ın, onların gizlediklerini de açığa vurduklarını da bildiğinden kuşku yok. Gerçek şu ki O, büyüklük taslayanları sevmez.", transliteration: "La cereme ennallahe ya'lemu ma yusirrune ve ma yu'linun, innehu la yuhıbbul mustekbirin."},
  {id: 1925, sure_no: 24, ayet_no: 24, sure_adi: "Nahl", text: "وَاِذَا ق۪يلَ لَهُمْ مَاذَٓا اَنْزَلَ رَبُّكُمْۙ قَالُٓوا اَسَاط۪يرُ الْاَوَّل۪ينَۙ", translation: "Onlara, \"Rabb'inizin indirdiği şey nedir?\" diye sorulduğu zaman, \"Öncekilerin masalları!\" derler.", transliteration: "Ve iza kile lehum ma za enzele rabbukum kalu esatirul evvelin."},
  {id: 1926, sure_no: 25, ayet_no: 25, sure_adi: "Nahl", text: "لِيَحْمِلُٓوا اَوْزَارَهُمْ كَامِلَةً يَوْمَ الْقِيٰمَةِۙ وَمِنْ اَوْزَارِ الَّذ۪ينَ يُضِلُّونَهُمْ بِغَيْرِ عِلْمٍۜ اَلَا سَٓاءَ مَا يَزِرُونَ۟", translation: "Kıyamet Günü, kendi yüklerinin tamamını ve saptırdıkları cahillerin yüklerinden bir kısmını, yüklenmiş olurlar. Dikkat edin! Yüklendikleri şey ne kötüdür!", transliteration: "Liyahmilu evzarehum kamileten yevmel kıyameti ve min evzarillezine yudıllunehum bi gayri ilm, e la sae ma yezirun."},
  {id: 1927, sure_no: 26, ayet_no: 26, sure_adi: "Nahl", text: "قَدْ مَكَرَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَاَتَى اللّٰهُ بُنْيَانَهُمْ مِنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَاَتٰيهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ", translation: "Onlardan öncekiler de düzen kurmuşlardı. Allah, onların yapılarını temelden yıktı, çatıları da tepelerine çöktü. Onlara, bu azap, hesaba katmadıkları yerden geldi.", transliteration: "Kad mekerellezine min kablihim fe etallahu bunyanehum minel kavaıdi fe harre aleyhimus sakfu min fevkıhim ve etahumul azabu min haysu la yeş'urun."},
  {id: 1928, sure_no: 27, ayet_no: 27, sure_adi: "Nahl", text: "ثُمَّ يَوْمَ الْقِيٰمَةِ يُخْز۪يهِمْ وَيَقُولُ اَيْنَ شُرَكَٓاءِيَ الَّذ۪ينَ كُنْتُمْ تُشَٓاقُّونَ ف۪يهِمْۜ قَالَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ اِنَّ الْخِزْيَ الْيَوْمَ وَالسُّٓوءَ عَلَى الْكَافِر۪ينَۙ", translation: "Sonra Kıyamet Günü, onları rezil edecek. Ve \"Hani uğrunda ayrılığa düştüğünüz ortaklarım nerede?\" diyecek. Kendilerine ilim verilenler, \"Rezillik ve kötülük, bugün Kafirlerin üzerinedir.\" diyecek.", transliteration: "Summe yevmel kıyameti yuhzihim ve yekulu eyne şurekaiyellezine kuntum tuşakkune fihim, kalellezine utul ilme innel hızyel yevme ves sue alel kafirin."},
  {id: 1929, sure_no: 28, ayet_no: 28, sure_adi: "Nahl", text: "اَلَّذ۪ينَ تَتَوَفّٰيهُمُ الْمَلٰٓئِكَةُ ظَالِم۪ٓي اَنْفُسِهِمْۖ فَاَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُٓوءٍۜ بَلٰٓى اِنَّ اللّٰهَ عَل۪يمٌ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Melekler, kendilerine haksızlık yapanların canlarını alacakları zaman, onlar, teslimiyet içinde: \"Biz, kötü bir iş yapmadık.\" dediler. Hayır! Kuşkusuz, Allah, yapmış olduğunuz şeyleri çok iyi bilendir.", transliteration: "Ellezine teteveffahumul melaiketu zalimi enfusihim fe elkavus seleme ma kunna na'melu min su', bela innallahe alimun bima kuntum ta'melun."},
  {id: 1930, sure_no: 29, ayet_no: 29, sure_adi: "Nahl", text: "فَادْخُلُٓوا اَبْوَابَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَاۜ فَلَبِئْسَ مَثْوَى الْمُتَكَبِّر۪ينَ", translation: "O halde, içinde ebedi kalıcılar olarak Cehennem'in kapılarından girin! Büyüklük taslayanlar için ne kötü bir yerdir orası.", transliteration: "Fedhulu ebvabe cehenneme halidine fiha fe lebi'se mesvel mutekebbirin."},
  {id: 1931, sure_no: 30, ayet_no: 30, sure_adi: "Nahl", text: "وَق۪يلَ لِلَّذ۪ينَ اتَّقَوْا مَاذَٓا اَنْزَلَ رَبُّكُمْۜ قَالُوا خَيْراًۜ لِلَّذ۪ينَ اَحْسَنُوا ف۪ي هٰذِهِ الدُّنْيَا حَسَنَةٌۜ وَلَدَارُ الْاٰخِرَةِ خَيْرٌۜ وَلَنِعْمَ دَارُ الْمُتَّق۪ينَۙ", translation: "Ve takva sahiplerine: \"Rabb'inizin indirdiği şey nedir?\" denildi. \"İyilik.\" dediler. Bu dünyada, iyilik yapanlar için iyilik vardır. Ve elbette ahiret yurdu daha hayırlıdır. Takva sahiplerinin yurdu ne güzeldir.", transliteration: "Ve kile lillezinettekav ma za enzele rabbukum, kalu hayra, lillezine ahsenu fi hazihid dunya haseneh, ve le darul ahıreti hayr, ve le ni'me darul muttekin."},
  {id: 1932, sure_no: 31, ayet_no: 31, sure_adi: "Nahl", text: "جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ لَهُمْ ف۪يهَا مَا يَشَٓاؤُ۫نَۜ كَذٰلِكَ يَجْزِي اللّٰهُ الْمُتَّق۪ينَۙ", translation: "İçinden ırmaklar akan Adn Cennetlerine girerler. Orada, onlar için diledikleri şeyler var. İşte Allah, takva sahiplerini böyle ödüllendirir.", transliteration: "Cennatu adnin yedhuluneha tecri min tahtihel enharu lehum fiha ma yeşaun, kezalike yeczillahul muttekin."},
  {id: 1933, sure_no: 32, ayet_no: 32, sure_adi: "Nahl", text: "اَلَّذ۪ينَ تَتَوَفّٰيهُمُ الْمَلٰٓئِكَةُ طَيِّب۪ينَۙ يَقُولُونَ سَلَامٌ عَلَيْكُمُۙ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Melekler, onların canlarını tayyib şekilde alırlar. \"Selam size. Yapmış olduğunuz iyi şeylere karşılık girin Cennete.\" derler.", transliteration: "Ellezine teteveffahumul melaiketu tayyibine yekulune selamun aleykumudhulul cennete bima kuntum ta'melun."},
  {id: 1934, sure_no: 33, ayet_no: 33, sure_adi: "Nahl", text: "هَلْ يَنْظُرُونَ اِلَّٓا اَنْ تَأْتِيَهُمُ الْمَلٰٓئِكَةُ اَوْ يَأْتِيَ اَمْرُ رَبِّكَۜ كَذٰلِكَ فَعَلَ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Kendilerine meleklerin gelmesinden veya Rabb'inin emrinin gelmesinden başka bir şey mi bekliyorlar? Onlardan öncekiler de böyle yapmışlardı. Allah onlara haksızlık yapmadı. Fakat onlar kendilerine haksızlık yapıyorlardı.", transliteration: "Hel yanzurune illa en te'tiyehumul melaiketu ev ye'tiye emru rabbik, kezalike fe alellezine min kablihim, ve ma zalemehumullahu ve lakin kanu enfusehum yazlimun."},
  {id: 1935, sure_no: 34, ayet_no: 34, sure_adi: "Nahl", text: "فَاَصَابَهُمْ سَيِّـَٔاتُ مَا عَمِلُوا وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟", translation: "Böylece yaptıklarının kötülükleri onlara isabet etti. Alay ettikleri şey kendilerini kuşattı.", transliteration: "Fe esabehum seyyiatu ma amilu ve haka bihim ma kanu bihi yestehziun."},
  {id: 1936, sure_no: 35, ayet_no: 35, sure_adi: "Nahl", text: "وَقَالَ الَّذ۪ينَ اَشْرَكُوا لَوْ شَٓاءَ اللّٰهُ مَا عَبَدْنَا مِنْ دُونِه۪ مِنْ شَيْءٍ نَحْنُ وَلَٓا اٰبَٓاؤُ۬نَا وَلَا حَرَّمْنَا مِنْ دُونِه۪ مِنْ شَيْءٍۜ كَذٰلِكَ فَعَلَ الَّذ۪ينَ مِنْ قَبْلِهِمْۚ فَهَلْ عَلَى الرُّسُلِ اِلَّا الْبَلَاغُ الْمُب۪ينُ", translation: "Şirk koşanlar, \"Eğer Allah dileseydi biz onun yanı sıra başkasına kul olmazdık. Babalarımız da olmazdı. Ne biz ne de babalarımız O'nun haram kıldığından başka hiçbir şeyi haram kılmazdık.\" dediler. Onlardan öncekiler de böyle yaptılar. Bu durumda Resullerin üzerine düşen, vahyi apaçık bir şekilde tebliğden başkası değil.", transliteration: "Ve kalellezine eşreku lev şaallahu ma abedna min dunihi min şey'in nahnu ve la abauna ve la harremna min dunihi min şey', kezalike fe alellezine min kablihim, fe hel aler rusuli illel belagul mubin."},
  {id: 1937, sure_no: 36, ayet_no: 36, sure_adi: "Nahl", text: "وَلَقَدْ بَعَثْنَا ف۪ي كُلِّ اُمَّةٍ رَسُولاً اَنِ اعْبُدُوا اللّٰهَ وَاجْتَنِبُوا الطَّاغُوتَۚ فَمِنْهُمْ مَنْ هَدَى اللّٰهُ وَمِنْهُمْ مَنْ حَقَّتْ عَلَيْهِ الضَّلَالَةُۜ فَس۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّب۪ينَ", translation: "Ant olsun ki, Biz, her ümmete, Allah'a kulluk etmeleri ve tağuttan uzak durmaları için bir resul gönderdik. Allah onlardan kimini doğru yola iletti, kimine de sapkınlık hak oldu. Şimdi yeryüzünde gezin de yalanlayanların sonunun nasıl olduğuna bakın.", transliteration: "Ve le kad beasna fi kulli ummetin resulen eni'budullahe vectenibut tagut, fe minhum men hedallahu ve minhum men hakkat aleyhid dalaleh, fe siru fil ardı fanzuru keyfe kane akıbetul mukezzibin."},
  {id: 1938, sure_no: 37, ayet_no: 37, sure_adi: "Nahl", text: "اِنْ تَحْرِصْ عَلٰى هُدٰيهُمْ فَاِنَّ اللّٰهَ لَا يَهْد۪ي مَنْ يُضِلُّ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ", translation: "Sen, onların, doğru yola ermelerini ne kadar çok istersen iste; sapkınlıkta kararlı olanları Allah doğru yola iletmez. Onlar için bir yardımcı da bulunmaz.", transliteration: "İn tahris ala hudahum fe innallahe la yehdi men yudıllu ve ma lehum min nasırin."},
  {id: 1939, sure_no: 38, ayet_no: 38, sure_adi: "Nahl", text: "وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ  لَا يَبْعَثُ اللّٰهُ مَنْ يَمُوتُۜ بَلٰى وَعْداً عَلَيْهِ حَقاًّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَۙ", translation: "\"Allah, ölen bir kimseyi diriltmez.\" diye olanca güçleriyle Allah'a yemin ettiler. Hayır! Allah'ın ölüleri diriltmesi kesin bir sözdür. Ancak insanların çoğu bu gerçeği kavramazlar.", transliteration: "Ve aksemu billahi cehde eymanihim la yeb'asullahu men yemut, bela va'den aleyhi hakkan ve lakinne ekseren nasi la ya'lemun."},
  {id: 1940, sure_no: 39, ayet_no: 39, sure_adi: "Nahl", text: "لِيُبَيِّنَ لَهُمُ الَّذ۪ي يَخْتَلِفُونَ ف۪يهِ وَلِيَعْلَمَ الَّذ۪ينَ كَفَرُٓوا اَنَّهُمْ كَانُوا كَاذِب۪ينَ", translation: "Karşı çıktıkları şeyin onlara açıklanması ve Kafirlerin yalancılar olduklarını bilmeleri için diriltileceklerdir.", transliteration: "Li yubeyyine lehumullezi yahtelifune fihi ve li ya'lemellezine keferu ennehum kanu kazibin."},
  {id: 1941, sure_no: 40, ayet_no: 40, sure_adi: "Nahl", text: "اِنَّمَا قَوْلُنَا لِشَيْءٍ اِذَٓا اَرَدْنَاهُ اَنْ نَقُولَ لَهُ كُنْ فَيَكُونُ۟", translation: "Bir şeyin olmasını istediğimiz zaman, sözümüz ona sadece, \"Ol.\" demektir. O da olur.", transliteration: "İnnema kavluna li şey'in iza erednahu en nekule lehu kun fe yekun."},
  {id: 1942, sure_no: 41, ayet_no: 41, sure_adi: "Nahl", text: "وَالَّذ۪ينَ هَاجَرُوا فِي اللّٰهِ مِنْ بَعْدِ مَا ظُلِمُوا لَنُبَوِّئَنَّهُمْ فِي الدُّنْيَا حَسَنَةًۜ وَلَاَجْرُ الْاٰخِرَةِ اَكْبَرُۢ لَوْ كَانُوا يَعْلَمُونَۙ", translation: "Zulme uğramalarından sonra, Allah yolunda hicret eden kimseleri, kesinlikle dünyada iyi bir yere yerleştiririz. Ahiret ödülü ise daha büyüktür. Keşke hicretten geri kalanlar bunu bilselerdi!", transliteration: "Vellezine haceru fillahi min ba'di ma zulimu li nubevvi ennehum fid dunya haseneh, ve le ecrul ahıreti ekber, lev kanu ya'lemun."},
  {id: 1943, sure_no: 42, ayet_no: 42, sure_adi: "Nahl", text: "اَلَّذ۪ينَ صَبَرُوا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَ", translation: "Onlar, sabreden kimselerdir. Rabb'lerine, tevekkül edenlerdir.", transliteration: "Ellezine saberu ve ala rabbihim yetevekkelun."},
  {id: 1944, sure_no: 43, ayet_no: 43, sure_adi: "Nahl", text: "وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالاً نُوح۪ٓي اِلَيْهِمْ فَسْـَٔلُٓوا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُونَۙ", translation: "Senden önce de vahyimizi iletmede elçi olarak insandan başkasını görevlendirmedik. Eğer bilmiyorsanız, zikir ehline sorun.", transliteration: "Ve ma erselna min kablike illa ricalen nuhi ileyhim fes'elu ehlez zikri in kuntum la ta'lemun."},
  {id: 1945, sure_no: 44, ayet_no: 44, sure_adi: "Nahl", text: "بِالْبَيِّنَاتِ وَالزُّبُرِۜ وَاَنْزَلْـنَٓا اِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ اِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ", translation: "Onları beyyinatla zeburlarla gönderdik. Sana da zikri indirdik. İnsanlara, kendilerine indirileni beyan edesin. Ki böylece düşünüp öğüt alırlar.", transliteration: "Bil beyyinati vez zubur, ve enzelna ileykez zikre li tubeyyine lin nasi ma nuzzile ileyhim ve leallehum yetefekkerun."},
  {id: 1946, sure_no: 45, ayet_no: 45, sure_adi: "Nahl", text: "اَفَاَمِنَ الَّذ۪ينَ مَكَرُوا السَّيِّـَٔاتِ اَنْ يَخْسِفَ اللّٰهُ بِهِمُ الْاَرْضَ اَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَۙ", translation: "Sana sinsice kötü tuzaklar kuranlar, Allah'ın, kendilerini yerin dibine geçirmeyeceğinden veya ummadıkları bir yerden azabın gelmeyeceğinden emin midirler?", transliteration: "E fe eminellezine mekeru seyyiati en yahsifallahu bihimul arda ev ye'tiyehumul azabu min haysu la yeş'urun."},
  {id: 1947, sure_no: 46, ayet_no: 46, sure_adi: "Nahl", text: "اَوْ يَأْخُذَهُمْ ف۪ي تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِز۪ينَۙ", translation: "Veya gezinip dururlarken, onları ansızın yakalamasından. Onlar, bunu engelleyemezler de.", transliteration: "Ev ye'huzehum fi tekallubihim fe ma hum bi mu'cizin."},
  {id: 1948, sure_no: 47, ayet_no: 47, sure_adi: "Nahl", text: "اَوْ يَأْخُذَهُمْ عَلٰى تَخَوُّفٍۜ فَاِنَّ رَبَّكُمْ لَرَؤُ۫فٌ رَح۪يمٌ", translation: "Veya kendilerini, korku üzerinde yakalamayacağından. Buna rağmen, doğrusu Rabb'iniz, Çok Şefkatli'dir, Rahmeti Kesintisiz'dir.", transliteration: "Ev ye'huzehum ala tehavvuf, fe inne rabbekum le raufun rahim."},
  {id: 1949, sure_no: 48, ayet_no: 48, sure_adi: "Nahl", text: "اَوَلَمْ يَرَوْا اِلٰى مَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ يَتَفَيَّؤُ۬ا ظِلَالُهُ عَنِ الْيَم۪ينِ وَالشَّمَٓائِلِ سُجَّداً لِلّٰهِ وَهُمْ دَاخِرُونَ", translation: "Onlar, Allah'ın yarattığı şeylerden, herhangi bir şeye bakmazlar mı? Gölgelerinin; secde ederek, saygıyla sağa sola dönüp O'nun yasalarına nasıl uyduklarını görmüyorlar mı?", transliteration: "E ve lem yerev ila ma halakallahu min şey'in yetefeyyeu zilaluhu anil yemini veş şemaili succeden lillahi ve hum dahırun."},
  {id: 1950, sure_no: 49, ayet_no: 49, sure_adi: "Nahl", text: "وَلِلّٰهِ يَسْجُدُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ مِنْ دَٓابَّةٍ وَالْمَلٰٓئِكَةُ وَهُمْ لَا يَسْتَكْبِرُونَ", translation: "Göklerde ve yerde bulunan hareket halindeki varlıkların tamamı ve melekler büyüklenmeden, Allah'a secde ederler.", transliteration: "Ve lillahi yescudu ma fis semavati ve ma fil ardı min dabbetin vel melaiketu ve hum la yestekbirun."},
  {id: 1951, sure_no: 50, ayet_no: 50, sure_adi: "Nahl", text: "يَخَافُونَ رَبَّهُمْ مِنْ فَوْقِهِمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ۟", translation: "Kendilerine egemen olan Rabb'lerine karşı gelmekten korkarlar ve emrolundukları şeyleri yaparlar.", transliteration: "Yehafune rabbehum min fevkıhim ve yef'alune ma yu'merun."},
  {id: 1952, sure_no: 51, ayet_no: 51, sure_adi: "Nahl", text: "وَقَالَ اللّٰهُ لَا تَتَّخِذُٓوا اِلٰهَيْنِ اثْنَيْنِۚ اِنَّمَا هُوَ اِلٰهٌ وَاحِدٌۚ فَاِيَّايَ فَارْهَبُونِ", translation: "Allah: \"İki ilah edinmeyin! O, ancak tek bir ilahtır. O halde, yalnızca Bana rehbet duyun.\"", transliteration: "Ve kalallahu la tettehızu ilaheynisneyn, innema huve ilahun vahıd, fe iyyaye ferhebun."},
  {id: 1953, sure_no: 52, ayet_no: 52, sure_adi: "Nahl", text: "وَلَهُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِ وَلَهُ الدّ۪ينُ وَاصِباًۜ اَفَغَيْرَ اللّٰهِ تَتَّقُونَ", translation: "Göklerde ve yerde ne varsa, yalnız O'nundur. Din de yalnızca O'nundur. Böyleyken, Allah'tan başkasına mı takvalı oluyorsunuz?", transliteration: "Ve lehu ma fis semavati vel ardı ve lehud dinu vasıba, e fe gayrallahi tettekun."},
  {id: 1954, sure_no: 53, ayet_no: 53, sure_adi: "Nahl", text: "وَمَا بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللّٰهِ ثُمَّ اِذَا مَسَّكُمُ الضُّرُّ فَاِلَيْهِ تَجْـَٔرُونَۚ", translation: "Sahip olduğunuz nimetlerin tamamı, Allah'tandır. Sonra bir sıkıntıya uğradığınızda, yalnız O'na yalvarırsınız.", transliteration: "Ve ma bikum min ni'metin fe minallahi summe iza messekumud durru fe ileyhi tec'erun."},
  {id: 1955, sure_no: 54, ayet_no: 54, sure_adi: "Nahl", text: "ثُمَّ اِذَا كَشَفَ الضُّرَّ عَنْكُمْ اِذَا فَر۪يقٌ مِنْكُمْ بِرَبِّهِمْ يُشْرِكُونَۙ", translation: "Sonra O, sizden sıkıntıyı giderince, bir kısmınız hemen Rabb'lerine şirk koşmaya başlar.", transliteration: "Summe iza keşefad durra ankum iza ferikun minkum bi rabbihim yuşrikun."},
  {id: 1956, sure_no: 55, ayet_no: 55, sure_adi: "Nahl", text: "لِيَكْفُرُوا بِمَٓا اٰتَيْنَاهُمْۜ فَتَمَتَّعُوا۠ فَسَوْفَ تَعْلَمُونَ", translation: "Kendilerine verdiklerimize nankörlük ediyorlar. Şimdilik faydalanın bakalım! Yakında göreceksiniz!", transliteration: "Li yekfuru bima ateynahum, fe temetteu, fesevfe ta'lemun."},
  {id: 1957, sure_no: 56, ayet_no: 56, sure_adi: "Nahl", text: "وَيَجْعَلُونَ لِمَا لَا يَعْلَمُونَ نَص۪يباً مِمَّا رَزَقْنَاهُمْۜ تَاللّٰهِ لَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَفْتَرُونَ", translation: "Onlar, kendilerini rızıklandırdığımız şeylerden bilinçsizce pay ayırıyorlar. Allah'a yemin olsun ki, uydurduğunuz bu şeylerden kesinlikle hesaba çekileceksiniz.", transliteration: "Ve yec'alune li ma la ya'lemune nasiben mimma razaknahum, tallahi le tus'elunne amma kuntum tefterun."},
  {id: 1958, sure_no: 57, ayet_no: 57, sure_adi: "Nahl", text: "وَيَجْعَلُونَ لِلّٰهِ الْبَنَاتِ سُبْحَانَهُۙ وَلَهُمْ مَا يَشْتَهُونَ", translation: "Kız çocuklarını Allah'a yakıştırıyorlar. O, bundan münezzehtir. Hoşlandıklarını da kendilerine ayırıyorlar.", transliteration: "Ve yec'alune lillahil benati subhanehu ve lehum ma yeştehun."},
  {id: 1959, sure_no: 58, ayet_no: 58, sure_adi: "Nahl", text: "وَاِذَا بُشِّرَ اَحَدُهُمْ بِالْاُنْثٰى ظَلَّ وَجْهُهُ مُسْوَداًّ وَهُوَ كَظ۪يمٌۚ", translation: "Onlardan birisine, çocuğunun kız olduğu haberi verildiği zaman, kızgınlığından yüzü kapkara kesilir.", transliteration: "Ve iza buşşire ehaduhum bil unsa zalle vechuhu musvedden ve huve kezim."},
  {id: 1960, sure_no: 59, ayet_no: 59, sure_adi: "Nahl", text: "يَتَوَارٰى مِنَ الْقَوْمِ مِنْ سُٓوءِ مَا بُشِّرَ بِه۪ۜ اَيُمْسِكُهُ عَلٰى هُونٍ اَمْ يَدُسُّهُ فِي التُّرَابِۜ اَلَا سَٓاءَ مَا يَحْكُمُونَ", translation: "Aldığı haberin kötülüğünden, insanların yüzüne bakamaz olur! Bu zillete karşı onu yanında mı tutsun, yoksa toprağa mı gömsün! Dikkat edin, verdikleri hüküm ne kötüdür!", transliteration: "Yetevara minel kavmi min sui ma buşşire bih, e yumsikuhu ala hunin em yedussuhu fit turab, e la sae ma yahkumun."},
  {id: 1961, sure_no: 60, ayet_no: 60, sure_adi: "Nahl", text: "لِلَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ مَثَلُ السَّوْءِۚ وَلِلّٰهِ الْمَثَلُ الْاَعْلٰىۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ۟", translation: "Kötü misaller ahirete inanmayanlara aittir. En yüce misaller Allah içindir. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Lillezine la yu'minune bil ahıreti meselus sev', ve lillahil meselul a'la, ve huvel azizul hakim."},
  {id: 1962, sure_no: 61, ayet_no: 61, sure_adi: "Nahl", text: "وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِظُلْمِهِمْ مَا تَرَكَ عَلَيْهَا مِنْ دَٓابَّةٍ وَلٰكِنْ يُؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُسَمًّىۚ فَاِذَا جَٓاءَ اَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً وَلَا يَسْتَقْدِمُونَ", translation: "Eğer Allah insanları, haksızlıkları nedeniyle cezalandıracak olsaydı, yeryüzünde hiçbir canlı bırakmazdı. Ancak onları adı konulmuş bir süreye kadar erteler. Süreleri dolduğu zaman ne bir saat ertelenir ne de öne alınır.", transliteration: "Ve lev yuahızullahun nase bi zulmihim ma tereke aleyha min dabbetin ve lakin yuahhıruhum ila ecelin musemma, fe iza cae eceluhum la yeste'hırune saaten ve la yestakdimun."},
  {id: 1963, sure_no: 62, ayet_no: 62, sure_adi: "Nahl", text: "وَيَجْعَلُونَ لِلّٰهِ مَا يَكْرَهُونَ وَتَصِفُ اَلْسِنَتُهُمُ الْكَذِبَ اَنَّ لَهُمُ الْحُسْنٰىۜ لَا جَرَمَ اَنَّ لَهُمُ النَّارَ وَاَنَّهُمْ مُفْرَطُونَ", translation: "Onlar, hoşlanmadıkları şeyleri Allah'a ait kılarlar. Dilleri en iyinin kendilerine ait olduğu yalanını söyler durur. Ateş onlara aittir. Onlar ifratta olanlardır.", transliteration: "Ve yec'alune lillahi ma yekrehune ve tesıfu elsinetuhumul kezibe enne lehumul husna, la cereme enne lehumun nare ve ennehum mufretun."},
  {id: 1964, sure_no: 63, ayet_no: 63, sure_adi: "Nahl", text: "تَاللّٰهِ لَقَدْ اَرْسَلْـنَٓا اِلٰٓى اُمَمٍ مِنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Allah'a ant olsun ki, Biz, kesinlikle senden önceki toplumlara da elçiler gönderdik. Şeytan, onlara yaptıklarını süslü gösterdi. Şeytan, o gün de bugün de onların velisidir. Onlar için acı veren bir azap vardır.", transliteration: "Tallahi lekad erselna ila umemin min kablike fe zeyyene lehumuş şeytanu a'malehum fe huve veliyyuhumul yevme ve lehum azabun elim."},
  {id: 1965, sure_no: 64, ayet_no: 64, sure_adi: "Nahl", text: "وَمَٓا اَنْزَلْنَا عَلَيْكَ الْكِتَابَ اِلَّا لِتُبَيِّنَ لَهُمُ الَّذِي اخْتَلَفُوا ف۪يهِۙ وَهُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ", translation: "Biz, sana Kitap'ı, hakkında ayrılığa düştükleri şeyleri, kendilerine beyan etmen ve iman eden bir halk için yol gösterici ve rahmet olmasından başka bir şey için indirmedik.", transliteration: "Ve ma enzelna aleykel kitabe illa li tubeyyine lehumullezihtelefu fihi ve huden ve rahmeten li kavmin yu'minun."},
  {id: 1966, sure_no: 65, ayet_no: 65, sure_adi: "Nahl", text: "وَاللّٰهُ اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَسْمَعُونَ۟", translation: "Allah, gökyüzünden suyu indirerek, ölümünden sonra yeryüzüne hayat verdi. Bunda dinleyen bir halk için kesinlikle bir ayet vardır.", transliteration: "Vallahu enzele mines semai maen fe ahya bihil arda ba'de mevtiha, inne fi zalike le ayeten li kavmin yesmeun."},
  {id: 1967, sure_no: 66, ayet_no: 66, sure_adi: "Nahl", text: "وَاِنَّ لَكُمْ فِي الْاَنْعَامِ لَعِبْرَةًۜ  نُسْق۪يكُمْ مِمَّا ف۪ي بُطُونِه۪ مِنْ بَيْنِ فَرْثٍ وَدَمٍ لَبَناً خَالِصاً سَٓائِغاً لِلشَّارِب۪ينَ", translation: "Hayvanlarda da sizin için bir ibret vardır. Size, onların karınlarındaki fışkı ile kan arasından süzülüp gelen içimi lezzetli, halis bir süt içiriyoruz.", transliteration: "Ve inne lekum fil en'ami le ibreh, nuskikum mimma fi butunihi min beyni fersin ve demin lebenen halisen saigan liş şaribin."},
  {id: 1968, sure_no: 67, ayet_no: 67, sure_adi: "Nahl", text: "وَمِنْ ثَمَرَاتِ النَّخ۪يلِ وَالْاَعْنَابِ تَتَّخِذُونَ مِنْهُ سَكَراً وَرِزْقاً حَسَناًۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَعْقِلُونَ", translation: "Üzüm ve hurma meyvelerinden sarhoşluk veren içecek ve faydalı besinler elde edersiniz. Aklını kullanan bir halk için, bunda kesinlikle bir ayet vardır.", transliteration: "Ve min semeratin nahili vel a'nabi tettehizune minhu sekeren ve rızkan hasena, inne fi zalike le ayeten li kavmin ya'kılun."},
  {id: 1969, sure_no: 68, ayet_no: 68, sure_adi: "Nahl", text: "وَاَوْحٰى رَبُّكَ اِلَى النَّحْلِ اَنِ اتَّخِذ۪ي مِنَ الْجِبَالِ بُيُوتاً وَمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُونَۙ", translation: "Rabb'in, bal arısına; dağlarda, ağaçlarda ve hazırladıkları şeylerde yuva edinmesini vahyetti.", transliteration: "Ve evha rabbuke ilen nahli enittehızi minel cibali buyuten ve mineş şeceri ve mimma ya'rişun."},
  {id: 1970, sure_no: 69, ayet_no: 69, sure_adi: "Nahl", text: "ثُمَّ كُل۪ي مِنْ كُلِّ الثَّمَرَاتِ فَاسْلُك۪ي سُبُلَ رَبِّكِ ذُلُلاًۜ يَخْرُجُ مِنْ بُطُونِهَا شَرَابٌ مُخْتَلِفٌ اَلْوَانُهُ ف۪يهِ شِفَٓاءٌ لِلنَّاسِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَتَفَكَّرُونَ", translation: "\"Sonra, her çeşit bitkiden ye. Rabb'inin emre amade kılınmış yollarında dolaş.\" Onun karınlarından, çeşitli renklerde şarap çıkar. Onda, insanlar için şifa vardır. Bunda düşünen bir toplum için kesinlikle bir ayet vardır.", transliteration: "Summe kuli min kullis semerati fesluki subule rabbiki zulula, yahrucu min butuniha şarabun muhtelifun elvanuhu fihi şifaun lin nas, inne fi zalike le ayeten li kavmin yetefekkerun."},
  {id: 1971, sure_no: 70, ayet_no: 70, sure_adi: "Nahl", text: "وَاللّٰهُ خَلَقَكُمْ ثُمَّ يَتَوَفّٰيكُمْ وَمِنْكُمْ مَنْ يُرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْـٔاًۜ اِنَّ اللّٰهَ عَل۪يمٌ قَد۪يرٌ۟", translation: "Sizi, Allah yarattı, sonra da sizi vefat ettirecek. Sizden kiminiz de bilir bir haldeyken, hiçbir şey bilmeyen ihtiyar bir bunak oluncaya dek yaşatılır. Allah, Her Şeyi Bilen'dir, Her Şeye Gücü Yeten'dir.", transliteration: "Vallahu halakakum summe yeteveffakum ve minkum men yureddu ila erzelil umuri li keyla ya'leme ba'de ilmin şey'a, innallahe alimun kadir."},
  {id: 1972, sure_no: 71, ayet_no: 71, sure_adi: "Nahl", text: "وَاللّٰهُ فَضَّلَ بَعْضَكُمْ عَلٰى بَعْضٍ فِي الرِّزْقِۚ فَمَا الَّذ۪ينَ فُضِّلُوا بِرَٓادّ۪ي رِزْقِهِمْ عَلٰى مَا مَلَكَتْ اَيْمَانُهُمْ فَهُمْ ف۪يهِ سَوَٓاءٌۜ  اَفَبِنِعْمَةِ اللّٰهِ يَجْحَدُونَ", translation: "Allah, rızık konusunda kiminizi kiminize üstün kıldı. Üstün kılınanlar; rızıklarını yeminle hak sahibi oldukları kimselere aktarıyorlar da onlar, onda eşit oluyorlar mı? O halde, Allah'ın nimetini mi inkar ediyorlar?", transliteration: "Vallahu faddale ba'dakum ala ba'dın fir rızk, femellezine fuddılu bi raddi rızkıhim ala ma meleket eymanehum fe hum fihi seva', e fe bi ni'metillahi yechadun."},
  {id: 1973, sure_no: 72, ayet_no: 72, sure_adi: "Nahl", text: "وَاللّٰهُ جَعَلَ لَكُمْ مِنْ اَنْفُسِكُمْ اَزْوَاجاً وَجَعَلَ لَكُمْ مِنْ اَزْوَاجِكُمْ بَن۪ينَ وَحَفَدَةً وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِۜ اَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَتِ اللّٰهِ هُمْ يَكْفُرُونَۙ", translation: "Allah sizin için, kendinizden eşler var etti ve eşlerden de çocuklar ve torunlar var etti. Temiz şeylerle rızıklandırdı. Hala Batıl'a mı inanıyorlar? Allah'ın nimetine nankörlük mü ediyorlar?", transliteration: "Vallahu ceale lekum min enfusikum ezvacen ve ceale lekum min ezvacikum benine ve hafedeten ve rezakakum minet tayyibat, e fe bil batıli yu'minune ve bi ni'metillahi hum yekfurun."},
  {id: 1974, sure_no: 73, ayet_no: 73, sure_adi: "Nahl", text: "وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقاً مِنَ السَّمٰوَاتِ وَالْاَرْضِ شَيْـٔاً وَلَا يَسْتَط۪يعُونَۚ", translation: "Onlar, Allah'ın yanı sıra, göklerden ve yerden kendilerine hiçbir rızık veremeyen ve hiçbir şeye güçleri yetmeyen şeylere kulluk ediyorlar.", transliteration: "Ve ya'budune min dunillahi ma la yemliku lehum rızkan mines semavati vel ardı şey'en ve la yestetiun."},
  {id: 1975, sure_no: 74, ayet_no: 74, sure_adi: "Nahl", text: "فَلَا تَضْرِبُوا لِلّٰهِ الْاَمْثَالَۜ اِنَّ اللّٰهَ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ", translation: "Artık Allah'a benzerler uydurmayın. Kuşkusuz, Allah bilir siz bilmezsiniz.", transliteration: "Fe la tadribu lillahil emsal, innallahe ya'lemu ve entum la ta'lemun."},
  {id: 1976, sure_no: 75, ayet_no: 75, sure_adi: "Nahl", text: "ضَرَبَ اللّٰهُ مَثَلاً عَبْداً مَمْلُوكاً لَا يَقْدِرُ عَلٰى شَيْءٍ وَمَنْ رَزَقْنَاهُ مِنَّا رِزْقاً حَسَناً فَهُوَ يُنْفِقُ مِنْهُ سِراًّ وَجَهْراًۜ هَلْ يَسْتَوُ۫نَۜ اَلْحَمْدُ لِلّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَ", translation: "Allah, hiçbir şeye gücü yetmeyen ve başkasının malı olmuş bir abd ile tarafımızdan kendisine iyi bir rızık verip de ondan gizli ve açık olarak yardımda bulunan bir kimseyi örnek verir. Bunlar, hiç bir olurlar mı? Hamd, Allah'a mahsustur. Ne var ki onların çoğu bilmezler.", transliteration: "Daraballahu meselen abden memluken la yakdiru ala şey'in ve men razaknahu minna rızkan hasenen fe huve yunfiku minhu sırren ve cehra, hel yestevun, elhamdulillah, bel ekseruhum la ya'lemun."},
  {id: 1977, sure_no: 76, ayet_no: 76, sure_adi: "Nahl", text: "وَضَرَبَ اللّٰهُ مَثَلاً رَجُلَيْنِ اَحَدُهُمَٓا اَبْكَمُ لَا يَقْدِرُ عَلٰى شَيْءٍ وَهُوَ كَلٌّ عَلٰى مَوْلٰيهُۙ اَيْنَمَا يُوَجِّهْهُ لَا يَأْتِ بِخَيْرٍۜ هَلْ يَسْتَو۪ي هُوَۙ وَمَنْ يَأْمُرُ بِالْعَدْلِۙ وَهُوَ عَلٰى صِرَاطٍ مُسْتَق۪يمٍ۟", translation: "Allah, iki adamı da örnek verdi: Bunlardan biri dilsiz ve hiçbir şeye gücü yetmez; mevlasına bir yüktür. Onu nereye gönderirse göndersin, bir iş beceremez. Bu adamla, adaleti emreden ve dosdoğru yolda olan eşit olur mu?", transliteration: "Ve daraballahu meselen raculeyni ehaduhuma ebkemu la yakdiru ala şey'in ve huve kellun ala mevlahu eynema yuveccihhu la ye'ti bi hayr, hel yestevi huve ve men ye'muru bil adli ve huve ala sıratın mustakim."},
  {id: 1978, sure_no: 77, ayet_no: 77, sure_adi: "Nahl", text: "وَلِلّٰهِ غَيْبُ السَّمٰوَاتِ وَالْاَرْضِۜ وَمَٓا اَمْرُ السَّاعَةِ اِلَّا كَلَمْحِ الْبَصَرِ اَوْ هُوَ اَقْرَبُۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Göklerin ve yerin gaybı, yalnızca Allah'a aittir. Sa'at'in emri de yalnızca göz açıp kapamak veya ondan daha az bir zamanda olacaktır. Kuşkusuz, Allah Her Şeye Kadir'dir.", transliteration: "Ve lillahi gaybus semavati vel ard, ve ma emrus saati illa kelemhıl basari ev huve akreb, innallahe ala kulli şey'in kadir."},
  {id: 1979, sure_no: 78, ayet_no: 78, sure_adi: "Nahl", text: "وَاللّٰهُ اَخْرَجَكُمْ مِنْ بُطُونِ اُمَّهَاتِكُمْ لَا تَعْلَمُونَ شَيْـٔاًۙ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٔدَةَۙ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Ve Allah, sizi hiçbir şey bilmez halde, annelerinizin karnından çıkardı. Size işitme yetisi, görme yetisi ve anlama yetisi verdi. Umulur ki şükredersiniz.", transliteration: "Vallahu ahrecekum min butuni ummehatikum la ta'lemune şey'en ve ceale lekumus sem'a vel ebsare vel ef'idete leallekum teşkurun."},
  {id: 1980, sure_no: 79, ayet_no: 79, sure_adi: "Nahl", text: "اَلَمْ يَرَوْا اِلَى الطَّيْرِ مُسَخَّرَاتٍ ف۪ي جَوِّ السَّمَٓاءِۜ مَا يُمْسِكُهُنَّ اِلَّا اللّٰهُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ", translation: "Gökyüzünün boşluğunda ilahi yasa gereği, uçuşan kuşları görmüyorlar mı? Onları, Allah'tan başkası o boşlukta tutamaz. Bunda inanan bir halk için ayetler vardır.", transliteration: "E lem yerev ilet tayri musahharatin fi cevvis sema, ma yumsikuhunne illallah, inne fi zalike le ayatin li kavmin yu'minun."},
  {id: 1981, sure_no: 80, ayet_no: 80, sure_adi: "Nahl", text: "وَاللّٰهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَناً وَجَعَلَ لَكُمْ مِنْ جُلُودِ الْاَنْعَامِ بُيُوتاً تَسْتَخِفُّونَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ اِقَامَتِكُمْۙ وَمِنْ اَصْوَافِهَا وَاَوْبَارِهَا وَاَشْعَارِهَٓا اَثَاثاً وَمَتَاعاً اِلٰى ح۪ينٍ", translation: "Allah, evlerinizden sizin için bir huzur ve dinginlik yeri yaptı. Ve sizin için, hayvanların derilerinden yolculuğunuz sırasında ve konaklamanızda taşınabilir çadırdan evler ve yünlerinden, tüylerinden, kıllarından ev eşyası ve geçim aracı yaptı.", transliteration: "Vallahu ceale lekum min buyutikum sekenen ve ceale lekum min culudil en'ami buyuten testehıffuneha yevme za'nikum ve yevme ikametikum ve min asvafiha ve evbariha ve eş'ariha esasen ve metaan ila hin."},
  {id: 1982, sure_no: 81, ayet_no: 81, sure_adi: "Nahl", text: "وَاللّٰهُ جَعَلَ لَكُمْ مِمَّا خَلَقَ ظِلَالاً وَجَعَلَ لَكُمْ مِنَ الْجِبَالِ اَكْنَاناً وَجَعَلَ لَكُمْ سَرَاب۪يلَ تَق۪يكُمُ الْحَرَّ وَسَرَاب۪يلَ تَق۪يكُمْ بَأْسَكُمْۜ كَذٰلِكَ يُتِمُّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُونَ", translation: "Allah, sizin için, yarattığı şeylerden gölgeler yaptı. Sizin için dağlardan sığınaklar, sizi sıcaktan koruyacak giysiler ve şiddetli darbelerden koruyacak giysiler yaptı. Sizin üzerinizdeki nimetini işte böyle tamamlıyor. Umulur ki; böylece teslim olursunuz.", transliteration: "Vallahu ceale lekum mimma halaka zılalen ve ceale lekum minel cibali eknanen ve ceale lekum serabile tekikumul harra ve serabile tekikum be'sekum, kezalike yutimmu ni'metehu aleykum leallekum tuslimun."},
  {id: 1983, sure_no: 82, ayet_no: 82, sure_adi: "Nahl", text: "فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلَاغُ الْمُب۪ينُ", translation: "Eğer yüz çevirirlerse, sana düşen sadece açık bir şekilde tebliğ etmektir.", transliteration: "Fe in tevellev fe innema aleykel belagul mubin."},
  {id: 1984, sure_no: 83, ayet_no: 83, sure_adi: "Nahl", text: "يَعْرِفُونَ نِعْمَتَ اللّٰهِ ثُمَّ يُنْكِرُونَهَا وَاَكْثَرُهُمُ الْكَافِرُونَ۟", translation: "Onlar, bu nimetleri Allah'ın verdiğini biliyorlar. Sonra da onu görmezden geliyorlar. Onların çoğu Kafir kimselerdir.", transliteration: "Ya'rifune ni'metallahi summe yunkiruneha ve ekseruhumul kafirun."},
  {id: 1985, sure_no: 84, ayet_no: 84, sure_adi: "Nahl", text: "وَيَوْمَ نَبْعَثُ مِنْ كُلِّ اُمَّةٍ شَه۪يداً ثُمَّ لَا يُؤْذَنُ لِلَّذ۪ينَ كَفَرُوا وَلَا هُمْ يُسْتَعْتَبُونَ", translation: "Her ümmetten bir tanık getirdiğimiz gün, artık Kafirlere izin verilmez. Onlardan özür dilemeleri de istenmez.", transliteration: "Ve yevme neb'asu min kulli ummetin şehiden summe la yu'zenu lillezinekeferu ve la hum yusta'tebun."},
  {id: 1986, sure_no: 85, ayet_no: 85, sure_adi: "Nahl", text: "وَاِذَا رَاَ الَّذ۪ينَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنْظَرُونَ", translation: "Zulmeden kimseler, azapla karşı karşıya kaldıklarında, artık onlardan azap hafifletilmez. Ve onlara fırsat da verilmez.", transliteration: "Ve iza raellezine zalemul azabe fe la yuhaffefuanhum ve la hum yunzarun."},
  {id: 1987, sure_no: 86, ayet_no: 86, sure_adi: "Nahl", text: "وَاِذَا رَاَ الَّذ۪ينَ اَشْرَكُوا شُرَكَٓاءَهُمْ قَالُوا رَبَّـنَا هٰٓؤُ۬لَٓاءِ شُرَكَٓاؤُ۬نَا الَّذ۪ينَ كُنَّا نَدْعُوا مِنْ دُونِكَۚ فَاَلْقَوْا اِلَيْهِمُ الْقَوْلَ اِنَّكُمْ لَكَاذِبُونَۚ", translation: "Şirk koşanlar, şirk koştuklarını gördüklerinde: \"Rabb'imiz! İşte bunlar, Senin yanın sıra istekte bulunduğumuz, yakardığımız ortaklarımız.\" diyecekler. Şirk koşulanlar da: \"Siz, kesinlikle yalan söyleyenlersiniz.\" diyerek, onları yalanlarlar.", transliteration: "Ve iza raellezine eşreku şurekaehum kalu rabbena haulai şurekaunellezine kunna ned'u min dunik, fe elkav ileyhimul kavle innekum le kazibun."},
  {id: 1988, sure_no: 87, ayet_no: 87, sure_adi: "Nahl", text: "وَاَلْقَوْا اِلَى اللّٰهِ يَوْمَئِذٍۨ السَّلَمَ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ", translation: "O Gün, Allah'a teslim olurlar. Uydurdukları şeyler, yüzüstü bırakarak onlardan uzaklaşırlar.", transliteration: "Ve elkav ilallahi yevme izinis seleme ve dalle anhum ma kanu yefterun."},
  {id: 1989, sure_no: 88, ayet_no: 88, sure_adi: "Nahl", text: "اَلَّذ۪ينَ كَفَرُوا وَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ زِدْنَاهُمْ عَذَاباً فَوْقَ الْعَذَابِ بِمَا كَانُوا يُفْسِدُونَ", translation: "Allah'ın yolundan alıkoyan Kafirlere, yaptıkları bozgunculuk nedeniyle azap üstüne azap katarız.", transliteration: "Ellezine keferu ve saddu an sebilillahi zidnahum azaben fevkal azabi bima kanu yufsidun."},
  {id: 1990, sure_no: 89, ayet_no: 89, sure_adi: "Nahl", text: "وَيَوْمَ نَبْعَثُ ف۪ي كُلِّ اُمَّةٍ شَه۪يداً عَلَيْهِمْ مِنْ اَنْفُسِهِمْ وَجِئْنَا بِكَ شَه۪يداً عَلٰى هٰٓؤُ۬لَٓاءِۜ وَنَزَّلْنَا عَلَيْكَ الْكِتَابَ تِبْيَاناً لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً وَبُشْرٰى لِلْمُسْلِم۪ينَ۟", translation: "Biz, O Gün, her ümmet için kendi aralarından üzerlerine bir tanık getireceğiz. Seni de teslim olanlar için yol gösterici, rahmet ve haber verici olarak sana indirdiğimiz Kitap ile her şeyin açıklandığına dair bunlara tanıklık yapman için getireceğiz.", transliteration: "Ve yevme neb'asu fi kulli ummetin şehiden aleyhim min enfusihim ve ci'nabike şehiden ala haula, ve nezzelna aleykel kitabe tibyanen likulli şey'in ve huden ve rahmeten ve buşra lil muslimin."},
  {id: 1991, sure_no: 90, ayet_no: 90, sure_adi: "Nahl", text: "اِنَّ اللّٰهَ يَأْمُرُ بِالْعَدْلِ وَالْاِحْسَانِ وَا۪يتَٓائِ ذِي الْقُرْبٰى وَيَنْهٰى عَنِ الْفَحْشَٓاءِ وَالْمُنْكَرِ وَالْبَغْيِۚ  يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ", translation: "Allah; adaleti, ihsanı ve yakınlarınızda olanlara yardım etmeyi, buyurmakta; fahşadan, münkerden ve beğyiden men etmektedir. İyice anlayıp tutmanız için size öğüt veriyor.", transliteration: "İnnallahe ye'muru bil adli vel ihsani ve itai zil kurba ve yenha anil fahşai vel munkeri vel bagy, yeizukum leallekum tezekkerun."},
  {id: 1992, sure_no: 91, ayet_no: 91, sure_adi: "Nahl", text: "وَاَوْفُوا بِعَهْدِ اللّٰهِ اِذَا عَاهَدْتُمْ وَلَا تَنْقُضُوا الْاَيْمَانَ بَعْدَ تَوْك۪يدِهَا وَقَدْ جَعَلْتُمُ اللّٰهَ عَلَيْكُمْ كَف۪يلاًۜ اِنَّ اللّٰهَ يَعْلَمُ مَا تَفْعَلُونَ", translation: "Söz verdiğiniz zaman, verdiğiniz sözü Allah için tutun. Allah'ı kendinize kefil kılarak, pekiştirdikten sonra yeminlerinizi bozmayın. Allah ne yaptığınızı bilir.", transliteration: "Ve evfu bi ahdillahi iza ahedtum ve la tenkudul eymane ba'de tevkidiha ve kad cealtumullahe aleykum kefila, innallahe ya'lemu ma tef'alun."},
  {id: 1993, sure_no: 92, ayet_no: 92, sure_adi: "Nahl", text: "وَلَا تَكُونُوا كَالَّت۪ي نَقَضَتْ غَزْلَهَا مِنْ بَعْدِ قُوَّةٍ اَنْكَاثاًۜ تَتَّخِذُونَ اَيْمَانَكُمْ دَخَلاً بَيْنَكُمْ اَنْ تَكُونَ اُمَّةٌ هِيَ اَرْبٰى مِنْ اُمَّةٍۜ اِنَّمَا يَبْلُوكُمُ اللّٰهُ بِه۪ۜ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيٰمَةِ مَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ", translation: "İçinizden bir topluluğun başka bir topluluktan daha ribalı olmasından etkilenerek, yeminlerinizi aldatma amacıyla; ipliğini sağlamca eğirdikten sonra, onu geri çözüp bozan kadın gibi olmayın. Allah, sizi bununla sınıyor. Hakkında anlaşmazlığa düştüğünüz şeyler, kıyamet günü size açıklanacaktır.", transliteration: "Ve la tekunu kelleti nekadat gazleha min ba'di kuvvetin enkasa, tettehızune eymanekum dehalen beynekum en tekune ummetun hiye erba min ummeh, innema yeblukumullahu bih, ve le yubeyyinenne lekum yevmel kıyameti ma kuntum fihi tahtelifun."},
  {id: 1994, sure_no: 93, ayet_no: 93, sure_adi: "Nahl", text: "وَلَوْ شَٓاءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَاحِدَةً وَلٰكِنْ يُضِلُّ مَنْ يَشَٓاءُ وَيَهْد۪ي مَنْ يَشَٓاءُۜ وَلَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَعْمَلُونَ", translation: "Allah, dileseydi sizi tek bir ümmet yapardı. Fakat Allah hak edeni3 saptırır, hak edeni de doğru yola iletir. Siz, yaptığınız her şeyden sorumlu tutulacaksınız.", transliteration: "Ve lev şaallahu le cealekum ummeten vahideten ve lakin yudıllu men yeşau ve yehdi men yeşa', ve le tus'elunne amma kuntum ta'melun."},
  {id: 1995, sure_no: 94, ayet_no: 94, sure_adi: "Nahl", text: "وَلَا تَتَّخِذُٓوا اَيْمَانَكُمْ دَخَلاً بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بَعْدَ ثُبُوتِهَا وَتَذُوقُوا السُّٓوءَ بِمَا صَدَدْتُمْ عَنْ سَب۪يلِ اللّٰهِۚ وَلَكُمْ عَذَابٌ عَظ۪يمٌ", translation: "Yeminlerinizi, aranızda aldatma ve bozgunculuğa araç yapmayın. Yoksa yere sağlam bastıktan sonra ayak kayar. Allah'ın yoluna engel olduğunuzdan dolayı kötülükle karşı karşıya kalırsınız. Ve büyük bir azaba uğrarsınız.", transliteration: "Ve la tettehızu eymanekum dehalen beynekum fe tezille kademun ba'de subutiha ve tezukus sue bima sadedtum an sebilillah, ve lekum azabun azim."},
  {id: 1996, sure_no: 95, ayet_no: 95, sure_adi: "Nahl", text: "وَلَا تَشْتَرُوا بِعَهْدِ اللّٰهِ ثَمَناً قَل۪يلاًۜ اِنَّمَا عِنْدَ اللّٰهِ هُوَ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "Allah'ın ahdini, küçük bir çıkara değiştirmeyin. Şayet bilirseniz, Allah'ın yanındaki ödülünüz daha iyidir.", transliteration: "Ve la teşteru bi ahdillahi semenen kalila, innema indallahi huve hayrun lekum in kuntum ta'lemun."},
  {id: 1997, sure_no: 96, ayet_no: 96, sure_adi: "Nahl", text: "مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللّٰهِ بَاقٍۜ وَلَنَجْزِيَنَّ الَّذ۪ينَ صَبَرُٓوا اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوا يَعْمَلُونَ", translation: "Sizin yanınızda olan şeyler tükenir, Allah'ın yanında olan şeyler ise tükenmez. Sabredenlere, ödüllerini yaptıkları şeylerin karşılığı olarak en iyi şekilde vereceğiz.", transliteration: "Ma ındekum yenfedu ve ma ındallahi bak, ve le necziyennellezine saberu ecrehum bi ahseni ma kanu ya'melun."},
  {id: 1998, sure_no: 97, ayet_no: 97, sure_adi: "Nahl", text: "مَنْ عَمِلَ صَالِحاً مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَيٰوةً طَيِّبَةًۚ وَلَنَجْزِيَنَّهُمْ اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوا يَعْمَلُونَ", translation: "Erkek ve kadın, Mü'min olarak kim salihatı yaparsa, ona hoş, temiz bir hayat yaşatırız. Kesinlikle yaptıklarının karşılığını daha iyisiyle veririz.", transliteration: "Men amile salihan min zekerin ev unsa ve huve mu'minun fe le nuhyiyennehu hayaten tayyibeh, ve le necziyennehum ecrehum bi ahseni ma kanu ya'melun."},
  {id: 1999, sure_no: 98, ayet_no: 98, sure_adi: "Nahl", text: "فَاِذَا قَرَأْتَ الْقُرْاٰنَ فَاسْتَعِذْ بِاللّٰهِ مِنَ الشَّيْطَانِ الرَّج۪يمِ", translation: "Kur'an okuduğun zaman, racim şeytandan Allah'a sığın.", transliteration: "Fe iza kare'tel kur'ane festeız billahi mineş şeytanir racim."},
  {id: 2000, sure_no: 99, ayet_no: 99, sure_adi: "Nahl", text: "اِنَّهُ لَيْسَ لَهُ سُلْطَانٌ عَلَى الَّذ۪ينَ اٰمَنُوا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَ", translation: "Kuşkusuz, iman etmiş ve Rabb'lerine tevekkül eden kimseler üzerinde, onun bir sultanlığı yoktur.", transliteration: "İnnehu leyse lehu sultanun alellezine amenu ve ala rabbihim yetevekkelun."},
  {id: 2001, sure_no: 100, ayet_no: 100, sure_adi: "Nahl", text: "اِنَّمَا سُلْطَانُهُ عَلَى الَّذ۪ينَ يَتَوَلَّوْنَهُ وَالَّذ۪ينَ هُمْ بِه۪ مُشْرِكُونَ۟", translation: "Onun sultanlığı, ancak kendisini veli edinen ve Allah'a ortak koşan kimseler için söz konusudur.", transliteration: "İnnema sultanuhu alellezine yetevellevnehu vellezine hum bihi müşrikun."},
  {id: 2002, sure_no: 101, ayet_no: 101, sure_adi: "Nahl", text: "وَاِذَا بَدَّلْـنَٓا اٰيَةً مَكَانَ اٰيَةٍۙ وَاللّٰهُ اَعْلَمُ بِمَا يُنَزِّلُ قَالُٓوا اِنَّـمَٓا اَنْتَ مُفْتَرٍۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَ", translation: "Biz, bir ayeti, başka bir ayetle değiştirdiğimiz zaman: \"Allah ne indirdiğini bilirken, sen kesinlikle uyduruyorsun.\" derler. Hayır, onların çoğu gerçeği bilmiyor.", transliteration: "Ve iza beddelna ayeten mekane ayetin vallahu a'lemu bima yunezzilu kalu innema ente mufter, bel ekseruhum la ya'lemun."},
  {id: 2003, sure_no: 102, ayet_no: 102, sure_adi: "Nahl", text: "قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِنْ رَبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذ۪ينَ اٰمَنُوا وَهُدًى وَبُشْرٰى لِلْمُسْلِم۪ينَ", translation: "De ki: \"İman Edenlerin; imanlarını pekiştirmek, Müslimlere kılavuz ve müjde olmak üzere, Rabb'inden, Hakk ile o Kudus'un Ruhu çokça indi.\"", transliteration: "Kul nezzelehu ruhul kudusi min rabbike bil hakkı li yusebbitellezine amenu ve huden ve buşra lil muslimin."},
  {id: 2004, sure_no: 103, ayet_no: 103, sure_adi: "Nahl", text: "وَلَقَدْ نَعْلَمُ اَنَّهُمْ يَقُولُونَ اِنَّمَا يُعَلِّمُهُ بَشَرٌۜ لِسَانُ الَّذ۪ي يُلْحِدُونَ اِلَيْهِ اَعْجَمِيٌّ وَهٰذَا لِسَانٌ عَرَبِيٌّ مُب۪ينٌ", translation: "Ant olsun ki Biz, onların: \"Ona ancak bir beşer öğretiyor.\" dediklerini biliyoruz. Kastettikleri kimsenin dili yabancıdır. Bu ise apaçık bir Arapça'dır.", transliteration: "Ve lekad na'lemu ennehum yekulune innema yuallimuhu beşer, lisanullezi yulhıdune ileyhi a'cemiyyun ve haza lisanun arabiyyun mubin."},
  {id: 2005, sure_no: 104, ayet_no: 104, sure_adi: "Nahl", text: "اِنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِاٰيَاتِ اللّٰهِۙ لَا يَهْد۪يهِمُ اللّٰهُ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Allah, Allah'ın ayetlerine iman etmeyenleri doğru yola iletmez. Ve onlar için çok acı bir azap vardır.", transliteration: "İnnellezine la yu'minune bi ayatillahi la yehdihimullahu ve lehum azabun elim."},
  {id: 2006, sure_no: 105, ayet_no: 105, sure_adi: "Nahl", text: "اِنَّمَا يَفْتَرِي الْكَذِبَ الَّذ۪ينَ لَا يُؤْمِنُونَ بِاٰيَاتِ اللّٰهِۚ وَاُو۬لٰٓئِكَ هُمُ الْكَاذِبُونَ", translation: "Ancak, Allah'ın ayetlerine iman etmeyenler yalan uydururlar. Zaten onlar, yalancıların ta kendileridir.", transliteration: "İnnema yefteril kezibellezine la yu'minune bi ayatillahi ve ulaike humul kazibun."},
  {id: 2007, sure_no: 106, ayet_no: 106, sure_adi: "Nahl", text: "مَنْ كَفَرَ بِاللّٰهِ مِنْ بَعْدِ ا۪يمَانِه۪ٓ اِلَّا مَنْ اُكْرِهَ وَقَلْبُهُ مُطْمَئِنٌّ بِالْا۪يمَانِ وَلٰكِنْ مَنْ شَرَحَ بِالْكُفْرِ صَدْراً فَعَلَيْهِمْ غَضَبٌ مِنَ اللّٰهِۚ وَلَهُمْ عَذَابٌ عَظ۪يمٌ", translation: "Kalbi iman ile yatışmış olduğu halde, -baskı ile inkara zorlanan kimse hariç- kim imanından sonra Allah'ı küfrederse ve kim küfre göğüs açarsa, bilsin ki Allah'ın gazabı onların üzerinedir. Bunlar için büyük bir azap vardır.", transliteration: "Men kefere billahi min ba'di imanihi illa men ukrihe ve kalbuhu mutmainnun bil imani ve lakin men şereha bil kufri sadran fe aleyhim gadabun minallah, ve lehum azabun azim."},
  {id: 2008, sure_no: 107, ayet_no: 107, sure_adi: "Nahl", text: "ذٰلِكَ بِاَنَّهُمُ اسْتَحَبُّوا الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِۙ وَاَنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الْكَافِر۪ينَ", translation: "Bunun nedeni, onların dünya hayatını sevip onu ahirete tercih etmeleridir. Allah, Kafir halkı doğru yola iletmez.", transliteration: "Zalike bi ennehumustehebbul hayated dunya alel ahıreti ve ennallahe la yehdil kavmel kafirin."},
  {id: 2009, sure_no: 108, ayet_no: 108, sure_adi: "Nahl", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ طَبَعَ اللّٰهُ عَلٰى قُلُوبِهِمْ وَسَمْعِهِمْ وَاَبْصَارِهِمْۚ وَاُو۬لٰٓئِكَ هُمُ الْغَافِلُونَ", translation: "İşte onlar, Allah'ın kalplerini, kulaklarını ve gözlerini mühürlediği kimselerdir. Onlar, gafil olanların ta kendileridir.", transliteration: "Ulaikellezine tabeallahu ala kulubihim ve sem'ihim ve ebsarihim, ve ulaike humul gafilun."},
  {id: 2010, sure_no: 109, ayet_no: 109, sure_adi: "Nahl", text: "لَا جَرَمَ اَنَّهُمْ فِي الْاٰخِرَةِ هُمُ الْخَاسِرُونَ", translation: "Kuşkusuz onlar, ahirette hüsranda olanlardır.", transliteration: "La cereme ennehum fil ahıreti humul hasirun."},
  {id: 2011, sure_no: 110, ayet_no: 110, sure_adi: "Nahl", text: "ثُمَّ اِنَّ رَبَّكَ لِلَّذ۪ينَ هَاجَرُوا مِنْ بَعْدِ مَا فُتِنُوا ثُمَّ جَاهَدُوا وَصَبَرُٓواۙ اِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَح۪يمٌ۟", translation: "Sonra, Rabb'in, zulme uğrayıp hicret etme zorunda kalan, ardından da cihat edip, sabredenlerin yanındadır. Rabb'in, onlara karşı Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Summe inne rabbeke lillezine haceru min ba'di ma futinu summe cahedu ve saberu inne rabbeke min ba'diha le gafurun rahim."},
  {id: 2012, sure_no: 111, ayet_no: 111, sure_adi: "Nahl", text: "يَوْمَ تَأْت۪ي كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَفْسِهَا وَتُوَفّٰى كُلُّ نَفْسٍ مَا عَمِلَتْ وَهُمْ لَا يُظْلَمُونَ", translation: "O Gün gelir ve herkes kendi canını kurtarmak için çabalar. Herkese, yaptığı şeylerin karşılığı tastamam ödenir. Onlara asla haksızlık edilmez.", transliteration: "Yevme te'ti kullu nefsin tucadilu an nefsiha ve tuveffa kullu nefsin ma amilet ve hum la yuzlemun."},
  {id: 2013, sure_no: 112, ayet_no: 112, sure_adi: "Nahl", text: "وَضَرَبَ اللّٰهُ مَثَلاً قَرْيَةً كَانَتْ اٰمِنَةً مُطْمَئِنَّةً يَأْت۪يهَا رِزْقُهَا رَغَداً مِنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِاَنْعُمِ اللّٰهِ فَاَذَاقَهَا اللّٰهُ لِبَاسَ الْجُوعِ وَالْخَوْفِ بِمَا كَانُوا يَصْنَعُونَ", translation: "Allah, güvenli ve her yönden tatmin olmuş bir kenti örnek verdi. Oraya, her yerden bol bol rızık geliyordu. Ne var ki, Allah'ın nimetlerine nankörlük ettiler. Bunun üzerine Allah da onlara kaypaklıkları nedeniyle açlık ve korku elbisesini tattırdı.", transliteration: "Ve daraballahu meselen karyeten kanet amineten mutmainneten ye'tiha rızkuha ragaden min kulli mekanin fe keferet bi en'umillahi fe ezakahallahu libasel cui vel havfi bima kanu yasnaun."},
  {id: 2014, sure_no: 113, ayet_no: 113, sure_adi: "Nahl", text: "وَلَقَدْ جَٓاءَهُمْ رَسُولٌ مِنْهُمْ فَكَذَّبُوهُ فَاَخَذَهُمُ الْعَذَابُ وَهُمْ ظَالِمُونَ", translation: "Ant olsun ki, onlara, içlerinden bir Resul geldi. Fakat onu yalanladılar. Bunun üzerine onları azap yakaladı. Onlar zalimlerdir.", transliteration: "Ve lekad caehum resulun minhum fe kezzebuhu fe ehazehumul azabu ve hum zalimun."},
  {id: 2015, sure_no: 114, ayet_no: 114, sure_adi: "Nahl", text: "فَكُلُوا مِمَّا رَزَقَكُمُ اللّٰهُ حَلَالاً طَيِّباًۖ وَاشْكُرُوا نِعْمَتَ اللّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُونَ", translation: "Eğer Allah'a kulluk ediyorsanız, Allah'ın nimetine şükredin. Allah'ın size verdiği rızıklardan helal ve temiz olarak yiyin.", transliteration: "Fe kulu mimma razakakumullahu halalen tayyiben veşkuru ni'metallahi in kuntum iyyahu ta'budun."},
  {id: 2016, sure_no: 115, ayet_no: 115, sure_adi: "Nahl", text: "اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْز۪يرِ وَمَٓا اُهِلَّ لِغَيْرِ اللّٰهِ بِه۪ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Allah, size haram kıldı: ölmüş hayvan etini, kanı, domuz etini ve Allah'tan başkası adına kesilenleri Ancak kim mecbur kalırsa haddi aşmadığı ve hakkı çiğnemediği takdirde yiyebilir. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İnnema harreme aleykumul meytete veddeme ve lahmel hınziri ve ma uhılle li gayrillahi bih, fe menıdturra gayre bagın ve la adin fe innallahe gafurun rahim."},
  {id: 2017, sure_no: 116, ayet_no: 116, sure_adi: "Nahl", text: "وَلَا تَقُولُوا لِمَا تَصِفُ اَلْسِنَتُكُمُ الْكَذِبَ هٰذَا حَلَالٌ وَهٰذَا حَرَامٌ لِتَفْتَرُوا عَلَى اللّٰهِ الْكَذِبَۜ اِنَّ الَّذ۪ينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُونَۜ", translation: "Kendi yalanlarınızı, Allah'a dayandırarak, dilinize geldiği gibi yalan yanlış, \"Şu helaldir, şu haramdır.\" demeyin. Uydurduğu yalanı Allah'a dayandıranlar, kurtuluşa eremezler.", transliteration: "Ve la tekulu lima tesıfu elsinetukumul kezibe haza halalun ve haza haramun li tefteru alallahil kezib, innellezine yefterune alallahil kezibe la yuflihun."},
  {id: 2018, sure_no: 117, ayet_no: 117, sure_adi: "Nahl", text: "مَتَاعٌ قَل۪يلٌۖ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Bu yaptıkları az bir yararlanmadır. Onlara acı bir azap vardır.", transliteration: "Metaun kalilun ve lehum azabun elim."},
  {id: 2019, sure_no: 118, ayet_no: 118, sure_adi: "Nahl", text: "وَعَلَى الَّذ۪ينَ هَادُوا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُۚ وَمَا ظَلَمْنَاهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Sana anlattıklarımızı daha önce Yahudilere de haram kılmıştık. Biz, onlara haksızlık yapmadık. Fakat onlar kendi kendilerine haksızlık ediyorlardı.", transliteration: "Ve alellezine hadu harremna ma kasasna aleyke min kabl, ve ma zalemnahum ve lakin kanu enfusehum yazlimun."},
  {id: 2020, sure_no: 119, ayet_no: 119, sure_adi: "Nahl", text: "ثُمَّ اِنَّ رَبَّكَ لِلَّذ۪ينَ عَمِلُوا السُّٓوءَ بِجَهَالَةٍ ثُمَّ تَابُوا مِنْ بَعْدِ ذٰلِكَ وَاَصْلَحُٓواۙ اِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَح۪يمٌ۟", translation: "Sonra, Rabb'in, cahillikle kötülük yapan ve sonra bunun ardından tevbe edip kendisini düzelten kimseleri bağışlar. Çünkü Rabb'in Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Summe inne rabbeke lillezine amilus sue bi cehaletin summe tabu min ba'di zalike ve aslahu inne rabbeke min ba'diha le gafurun rahim."},
  {id: 2021, sure_no: 120, ayet_no: 120, sure_adi: "Nahl", text: "اِنَّ اِبْرٰه۪يمَ كَانَ اُمَّةً قَانِتاً لِلّٰهِ حَن۪يفاًۜ وَلَمْ يَكُ مِنَ الْمُشْرِك۪ينَۙ", translation: "İbrahim, hanif olarak Allah'a yönelen bir ümmetti. Ve Müşriklerden değildi.", transliteration: "İnne ibrahime kane ummeten kaniten lillahi hanifa ve lem yeku minel muşrikin."},
  {id: 2022, sure_no: 121, ayet_no: 121, sure_adi: "Nahl", text: "شَاكِراً لِاَنْعُمِهِۜ اِجْتَبٰيهُ وَهَدٰيهُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Allah'ın nimetlerine şükrediciydi. Onu seçti ve dosdoğru yola iletti.", transliteration: "Şakiren li en'umih, ictebahu ve hudahu ila sıratın mustekim."},
  {id: 2023, sure_no: 122, ayet_no: 122, sure_adi: "Nahl", text: "وَاٰتَيْنَاهُ فِي الدُّنْيَا حَسَنَةًۜ وَاِنَّهُ فِي الْاٰخِرَةِ لَمِنَ الصَّالِح۪ينَۜ", translation: "Ona dünyada iyilikler verdik ve kuşkusuz o, ahirette de salihlerdendir.", transliteration: "Ve ateynahu fid dunya haseneh, ve innehu fil ahıreti le mines salihin."},
  {id: 2024, sure_no: 123, ayet_no: 123, sure_adi: "Nahl", text: "ثُمَّ اَوْحَيْنَٓا اِلَيْكَ اَنِ اتَّبِـعْ مِلَّةَ اِبْرٰه۪يمَ حَن۪يفاًۜ وَمَا كَانَ مِنَ الْمُشْرِك۪ينَ", translation: "Sonra sana: \"Hanif olarak İbrahim'in milletine uy, o Müşriklerden değildi.\" diye vahyettik.", transliteration: "Summe evhayna ileyke enittebi' millete ibrahime hanifa, ve ma kane minel muşrikin."},
  {id: 2025, sure_no: 124, ayet_no: 124, sure_adi: "Nahl", text: "اِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذ۪ينَ اخْتَلَفُوا ف۪يهِۜ وَاِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ", translation: "Sebt yalnızca ihtilafa düşenler üzerine kılındı. Kuşkusuz, senin Rabb'in ihtilaf edip durdukları şeyler hakkında Kıyamet Günü, aralarında kesinlikle hüküm verecektir.", transliteration: "İnnema cuiles sebtu alellezinahtelefu fih, ve inne rabbeke le yahkumu beynehum yevmel kıyameti fima kanu fihi yahtelifun."},
  {id: 2026, sure_no: 125, ayet_no: 125, sure_adi: "Nahl", text: "اُدْعُ اِلٰى سَب۪يلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ وَجَادِلْهُمْ بِالَّت۪ي هِيَ اَحْسَنُۜ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَب۪يلِه۪ وَهُوَ اَعْلَمُ بِالْمُهْتَد۪ينَ", translation: "Rabb'inin yoluna, hikmetle ve iyiliği öğütleyerek çağır. Onlarla en iyi şekilde mücadele et. Kuşkusuz Rabb'in, kimin Kendi yolundan saptığını çok iyi bilir; doğru yolda olanları da en iyi O bilir.", transliteration: "Ud'u ila sebili rabbike bil hikmeti vel mev'ızatil haseneti ve cadilhum billeti hiye ahsen, inne rabbeke huve a'lemu bi men dalle an sebilihi ve huve a'lemu bil muhtedin."},
  {id: 2027, sure_no: 126, ayet_no: 126, sure_adi: "Nahl", text: "وَاِنْ عَاقَبْتُمْ فَعَاقِبُوا بِمِثْلِ مَا عُوقِبْتُمْ بِه۪ۜ وَلَئِنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِلصَّابِر۪ينَ", translation: "Eğer siz, sert karşılık vermek zorunda kalırsanız, o zaman onların size verdiği karşılığa orantılı olarak karşılık verin. Ama sabrederseniz, elbette böyle bir davranış sabredenler için daha hayırlıdır.", transliteration: "Ve in akabtum fe akıbu bi misli ma ukıbtum bih, ve le in sabertum le huve hayrun lis sabirin."},
  {id: 2028, sure_no: 127, ayet_no: 127, sure_adi: "Nahl", text: "وَاصْبِرْ وَمَا صَبْرُكَ اِلَّا بِاللّٰهِ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ ف۪ي ضَيْقٍ مِمَّا يَمْكُرُونَ", translation: "Sabret! Senin sabrın yalnızca Allah iledir. Onlar için üzülme. Onların kurdukları tuzaklar nedeniyle darlanma.", transliteration: "Vasbır ve ma sabruke illa billahi ve la tahzen aleyhim ve la teku fi daykın mimma yemkurun."},
  {id: 2029, sure_no: 128, ayet_no: 128, sure_adi: "Nahl", text: "اِنَّ اللّٰهَ مَعَ الَّذ۪ينَ اتَّقَوْا وَالَّذ۪ينَ هُمْ مُحْسِنُونَ", translation: "Kuşkusuz, Allah, takvalı olanlarla ve iyi davranan kimselerle beraberdir.", transliteration: "İnnallahe meallezinettekav vellezine hum muhsinun."},
  {id: 2030, sure_no: 1, ayet_no: 1, sure_adi: "İsra", text: "سُبْحَانَ الَّـذ۪ٓي اَسْرٰى بِعَبْدِه۪ لَيْلاً مِنَ الْمَسْجِدِ الْحَرَامِ اِلَى الْمَسْجِدِ الْاَقْصَا الَّذ۪ي بَارَكْنَا حَوْلَهُ لِنُرِيَهُ مِنْ اٰيَاتِنَاۜ اِنَّهُ هُوَ السَّم۪يعُ الْبَص۪يرُ", translation: "O subhandır. Kulunu, Mescid-i Haram'dan çevresini mübarek kıldığımız Mescid-i Aksa'ya bir gece yürüttü. Ona ayetlerimizi göstermek için. Kuşkusuz O, Her Şeyi Duyan'dır, Her Şeyi Gören'dir.", transliteration: "Subhanellezi esra bi abdihi leylen minel mescidil harami ilel mescidil aksallezi barekna havlehu li nuriyehu min ayatina, innehu huves semiul basir."},
  {id: 2031, sure_no: 2, ayet_no: 2, sure_adi: "İsra", text: "وَاٰتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَاهُ هُدًى لِبَن۪ٓي اِسْرَٓائ۪لَ اَلَّا تَتَّخِذُوا مِنْ دُون۪ي وَك۪يلاًۜ", translation: "Musa'ya, İsrailoğulları'na, Ben'den başka bir vekil edinmeyin diye, doğru yola ileten o Kitap'ı verdik.", transliteration: "Ve ateyna musel kitabe ve cealnahu huden li beni israile ella tettehızu min duni vekila."},
  {id: 2032, sure_no: 3, ayet_no: 3, sure_adi: "İsra", text: "ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوحٍۜ اِنَّهُ كَانَ عَبْداً شَكُوراً", translation: "Ey Nuh ile birlikte taşıdığımız kimselerin soyundan olanlar! Gerçek şu ki, o çok şükreden bir kuldu.", transliteration: "Zurriyyete men hamelna mea nuh, innehu kane abden şekura."},
  {id: 2033, sure_no: 4, ayet_no: 4, sure_adi: "İsra", text: "وَقَضَيْنَٓا اِلٰى بَن۪ٓي اِسْرَٓائ۪لَ فِي الْكِتَابِ لَتُفْسِدُنَّ فِي الْاَرْضِ مَرَّتَيْنِ وَلَتَعْـلُنَّ عُـلُواًّ كَب۪يراً", translation: "İsrailoğulları'na Kitap'ta yargıda bulunduk: \"Siz kesinlikle iki kez yeryüzünde fesat çıkaracaksınız ve kibirlenip böbürleneceksiniz.\"", transliteration: "Ve kadayna ila beni israile fil kitabi le tufsidunne fil ardı merreteyni ve le ta'lunne uluvven kebira."},
  {id: 2034, sure_no: 5, ayet_no: 5, sure_adi: "İsra", text: "فَاِذَا جَٓاءَ وَعْدُ اُو۫لٰيهُمَا بَعَثْنَا عَلَيْكُمْ عِبَاداً لَنَٓا اُو۬ل۪ي بَأْسٍ شَد۪يدٍ فَجَاسُوا خِلَالَ الدِّيَارِۜ وَكَانَ وَعْداً مَفْعُولاً", translation: "Birincisinin zamanı gelince çok güçlü kullarımızı üzerinize gönderdik. Yurtlarının içlerine kadar girdiler, işgal ettiler. Böylece yapılan uyarı gerçekleşmiş oldu.", transliteration: "Fe iza cae va'du ulahuma beasna aleykum ibaden lena ulibe'sin şedidin fe casu hılaled diyar, ve kane va'den mef'ula."},
  {id: 2035, sure_no: 6, ayet_no: 6, sure_adi: "İsra", text: "ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَاَمْدَدْنَاكُمْ بِاَمْوَالٍ وَبَن۪ينَ وَجَعَلْنَاكُمْ اَكْثَرَ نَف۪يراً", translation: "Sonra sizi, onlara karşı tekrar üstün kıldık. Size mallarla ve evlatlarla yardım ettik. Ve sayınızı çokça artırdık.", transliteration: "Summe rededna lekumul kerrete aleyhim ve emdednakum bi emvalin ve benine ve cealnakum eksere nefira."},
  {id: 2036, sure_no: 7, ayet_no: 7, sure_adi: "İsra", text: "اِنْ اَحْسَنْتُمْ اَحْسَنْتُمْ لِاَنْفُسِكُمْ وَاِنْ اَسَأْتُمْ فَلَهَاۜ فَاِذَا جَٓاءَ وَعْدُ الْاٰخِرَةِ لِيَسُٓؤُ۫ا وُجُوهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوهُ اَوَّلَ مَرَّةٍ وَلِيُتَبِّرُوا مَا عَلَوْا تَتْب۪يراً", translation: "Eğer iyilik yaparsanız, kendinize iyilik yapmış olursunuz. Eğer kötü olursanız, o da kendiniz içindir. Diğer bozgunculuğunuzun cezalandırma zamanı geldiğinde, sizi kötü duruma düşürmek için, ilk kez girdikleri gibi mescide girsinler. Ve yücelttiğiniz şeyleri darmadağın edip mahvetsinler.", transliteration: "İn ahsentum ahsentum li enfusikum ve in ese'tum fe leha, fe iza cae va'dul ahıreti li yesuu vucuhekum ve li yedhulul mescide kema dehaluhu evvele merretin ve li yutebbiru ma alev tetbira."},
  {id: 2037, sure_no: 8, ayet_no: 8, sure_adi: "İsra", text: "عَسٰى رَبُّكُمْ اَنْ يَرْحَمَكُمْۚ وَاِنْ عُدْتُمْ عُدْنَاۢ وَجَعَلْنَا جَهَنَّمَ لِلْكَافِر۪ينَ حَص۪يراً", translation: "Umulur ki Rabb'iniz size merhamet eder. Eğer siz dönerseniz, Biz de döneriz. Ve Biz Cehennem'i Kafirler için kuşatıcı kıldık.", transliteration: "Asa rabbukum en yerhamekum, ve in udtum udna, ve cealna cehenneme lil kafirine hasira."},
  {id: 2038, sure_no: 9, ayet_no: 9, sure_adi: "İsra", text: "اِنَّ هٰذَا الْقُرْاٰنَ يَهْد۪ي لِلَّت۪ي هِيَ اَقْوَمُ وَيُبَشِّرُ الْمُؤْمِن۪ينَ الَّذ۪ينَ يَعْمَلُونَ الصَّالِحَاتِ اَنَّ لَهُمْ اَجْراً كَب۪يراًۙ", translation: "Kuşkusuz bu Kur'an, en doğru yolu gösterir. Salihatı yapan Mü'minlere kesinlikle büyük bir ödül olduğunu müjdeler.", transliteration: "İnne hazel kur'ane yehdi lilleti hiye akvemu ve yubeşşirul mu'mininellezine ya'melunes salihati enne lehum ecren kebira."},
  {id: 2039, sure_no: 10, ayet_no: 10, sure_adi: "İsra", text: "وَاَنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ اَعْتَدْنَا لَهُمْ عَذَاباً اَل۪يماً۟", translation: "Ahirete iman etmeyenlere can yakıcı bir azap hazırlandığını haber verir.", transliteration: "Ve ennellezine la yu'minune bil ahıreti a'tedna lehum azaben elima."},
  {id: 2040, sure_no: 11, ayet_no: 11, sure_adi: "İsra", text: "وَيَدْعُ الْاِنْسَانُ بِالشَّرِّ دُعَٓاءَهُ بِالْخَيْرِۜ وَكَانَ الْاِنْسَانُ عَجُولاً", translation: "İnsan hayra dua eder gibi, şerre dua ediyor. İnsan çok acelecidir.", transliteration: "Ve yed'ul insanu biş şerri duaehu bil hayr, ve kanel insanu acula."},
  {id: 2041, sure_no: 12, ayet_no: 12, sure_adi: "İsra", text: "وَجَعَلْنَا الَّيْلَ وَالنَّهَارَ اٰيَتَيْنِ فَمَحَوْنَٓا اٰيَةَ الَّيْلِ وَجَعَلْـنَٓا اٰيَةَ النَّهَارِ مُبْصِرَةً لِتَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْ وَلِتَعْلَمُوا عَدَدَ السِّن۪ينَ وَالْحِسَابَۜ وَكُلَّ شَيْءٍ فَصَّلْنَاهُ تَفْص۪يلاً", translation: "Biz geceyi ve gündüzü iki ayet yaptık. Sonra geceyi karanlık, gündüzü aydınlık yaptık ki Rabb'inizin bahşettiği nimetleri çalışıp kazanasınız ve yılların sayısını ve hesabını yapma imkanı bulasınız. Biz, her şeyi ayrıntılı olarak açıkladık.", transliteration: "Ve cealnel leyle ven nehare ayeteyni fe mehavna ayetel leyli ve cealna ayeten nehari mubsıraten li tebtegu fadlen min rabbikum ve li ta'lemu adedes sinine vel hisab, ve kulle şey'in fassalnahu tafsila."},
  {id: 2042, sure_no: 13, ayet_no: 13, sure_adi: "İsra", text: "وَكُلَّ اِنْسَانٍ اَلْزَمْنَاهُ طَٓائِرَهُ ف۪ي عُنُقِه۪ۜ وَنُخْرِجُ لَهُ يَوْمَ الْقِيٰمَةِ كِتَاباً يَلْقٰيهُ مَنْشُوراً", translation: "Her insanın kuşunu kendi boynuna doladık. Kıyamet Günü, yaptıklarının tamamını gösteren kitabı önüne koyarız.", transliteration: "Ve kulle insanin elzemnahu tairehu fi unukıh, ve nuhricu lehu yevmel kıyameti kitaben yelkahu menşura."},
  {id: 2043, sure_no: 14, ayet_no: 14, sure_adi: "İsra", text: "اِقْرَأْ كِتَابَكَۜ كَفٰى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَس۪يباًۜ", translation: "Kitabını oku. Bugün hesap görücü olarak sen kendine yetersin.", transliteration: "Ikra' kitabek, kefa bi nefsikel yevme aleyke hasiba."},
  {id: 2044, sure_no: 15, ayet_no: 15, sure_adi: "İsra", text: "مَنِ اهْتَدٰى فَاِنَّمَا يَهْتَد۪ي لِنَفْسِه۪ۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۜ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۜ وَمَا كُنَّا مُعَذِّب۪ينَ حَتّٰى نَبْعَثَ رَسُولاً", translation: "Kim doğru yolda olursa, ancak kendi iyiliği için doğru yolda olur. Kim de saparsa ancak kendi kötülüğü için sapmış olur. Ve hiçbir yük taşıyıcı, başkasının yükünü yüklenmez. Ve Biz, bir Resul göndermedikçe azap edecek değiliz.", transliteration: "Menihteda fe innema yehtedi li nefsih, ve men dalle fe innema yadıllu aleyha, ve la teziru vaziretun vizre uhra, ve ma kunna muazzibine hatta neb'ase resula."},
  {id: 2045, sure_no: 16, ayet_no: 16, sure_adi: "İsra", text: "وَاِذَٓا اَرَدْنَٓا اَنْ نُهْلِكَ قَرْيَةً اَمَرْنَا مُتْرَف۪يهَا فَفَسَقُوا ف۪يهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنَاهَا تَدْم۪يراً", translation: "Biz bir beldeyi yok etmek istersek, varlık ve güç sahibi ileri gelenlerine uyarımızı yaparız, buna rağmen bozgunculuk yaparlarsa böylece söz hak olur. Ve onu helak ederek yok ederiz.", transliteration: "Ve iza eredna en nuhlike karyeten emerna mutrafiha fe feseku fiha fe hakka aleyhel kavlu fe demmernaha tedmira."},
  {id: 2046, sure_no: 17, ayet_no: 17, sure_adi: "İsra", text: "وَكَمْ اَهْلَكْنَا مِنَ الْقُرُونِ مِنْ بَعْدِ نُوحٍۜ وَكَفٰى بِرَبِّكَ بِذُنُوبِ عِبَادِه۪ خَب۪يراً بَص۪يراً", translation: "Nuh'tan sonra böyle nice nesilleri yok ettik. Kullarının suçlarını görmede ve haberdar olmada tek başına Rabb'in yeter.", transliteration: "Ve kem ehlekna minel kuruni min ba'di nuh ve kefa bi rabbike bi zunubi ıbadihi habiren basira."},
  {id: 2047, sure_no: 18, ayet_no: 18, sure_adi: "İsra", text: "مَنْ كَانَ يُر۪يدُ الْعَاجِلَةَ عَجَّلْنَا لَهُ ف۪يهَا مَا نَشَٓاءُ لِمَنْ نُر۪يدُ ثُمَّ جَعَلْنَا لَهُ جَهَنَّمَۚ يَصْلٰيهَا مَذْمُوماً مَدْحُوراً", translation: "Kim aceleyi isterse, hak eden kimseye dilediğimiz şeyi çabuklaştırırız. Sonra onun için Cehennem'i mekan yaparız. Kınanmış ve kovulmuş olarak oraya girer.", transliteration: "Men kane yuridul acilete accelna lehu fiha ma neşau li men nuridu summe cealna lehu cehennem, yaslaha mezmumen medhura."},
  {id: 2048, sure_no: 19, ayet_no: 19, sure_adi: "İsra", text: "وَمَنْ اَرَادَ الْاٰخِرَةَ وَسَعٰى لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَاُو۬لٰٓئِكَ كَانَ سَعْيُهُمْ مَشْكُوراً", translation: "Kim de ahireti isterse ve Mü'min olarak onun gerektirdiği şekilde çalışırsa, işte onların çalışmaları meşkurdur.", transliteration: "Ve men eradel ahırete ve saa leha sa'yeha ve huve mu'minun fe ulaike kane sa'yuhum meşkura."},
  {id: 2049, sure_no: 20, ayet_no: 20, sure_adi: "İsra", text: "كُلاًّ نُمِدُّ هٰٓؤُ۬لَٓاءِ وَهٰٓؤُ۬لَٓاءِ مِنْ عَطَٓاءِ رَبِّكَۜ وَمَا كَانَ عَطَٓاءُ رَبِّكَ مَحْظُوراً", translation: "Biz, bu dünyayı isteyene de ahireti isteyene de veririz. Bu, Rabb'inin atalarındandır. Rabb'inin ataları kısıtlanmış değildir.", transliteration: "Kullen numiddu haulai ve haulai min atai rabbik, ve ma kane atau rabbike mahzura."},
  {id: 2050, sure_no: 21, ayet_no: 21, sure_adi: "İsra", text: "اُنْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۜ وَلَلْاٰخِرَةُ اَكْبَرُ دَرَجَاتٍ وَاَكْبَرُ تَفْض۪يلاً", translation: "Bak! Onların bir kısmını bir kısmından daha fazla imkan sahibi kıldık. Elbette ahiret, dereceler ve imkan bakımından da daha büyüktür.", transliteration: "Unzur keyfe faddalna ba'dahum ala ba'd, ve lel ahıretu ekberu derecatin ve ekberu tafdila."},
  {id: 2051, sure_no: 22, ayet_no: 22, sure_adi: "İsra", text: "لَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ فَتَقْعُدَ مَذْمُوماً مَخْذُولاً۟", translation: "Allah ile birlikte başka bir ilah edinme. Yoksa kınanmış ve hor görülmüş olarak kalırsın.", transliteration: "La tec'al meallahi ilahen ahare fe tak'ude mezmumen mahzula."},
  {id: 2052, sure_no: 23, ayet_no: 23, sure_adi: "İsra", text: "وَقَضٰى رَبُّكَ اَلَّا تَعْبُدُٓوا اِلَّٓا اِيَّاهُ وَبِالْوَالِدَيْنِ اِحْسَاناًۜ اِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ اَحَدُهُمَٓا اَوْ كِلَاهُمَا فَلَا تَقُلْ لَهُمَٓا اُفٍّ وَلَا تَنْهَرْهُمَا وَقُلْ لَهُمَا قَوْلاً كَر۪يماً", translation: "Rabb'in, Kendisinden başkasına kul olmamanızı, anne ve babaya iyi davranmanızı kaza etti. Eğer ikisinden birisi veya her ikisi senin yanında yaşlanırlarsa sakın onlara \"öf\" deme, onlara kaba davranma. Ve ikisine de kerim şekilde konuş.", transliteration: "Ve kada rabbuke ella ta'budu illa iyyahu ve bil valideyni ihsana, imma yebluganne indekel kibere ehaduhuma ev kila huma fe la tekul lehuma uffin ve la tenher huma ve kul lehuma kavlen kerima."},
  {id: 2053, sure_no: 24, ayet_no: 24, sure_adi: "İsra", text: "وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَبِّ ارْحَمْهُمَا كَمَا رَبَّيَان۪ي صَغ۪يراًۜ", translation: "Ve merhametle, alçakgönüllüce onlara kol kanat ger. Ve de ki: \"Rabb'im, onların beni büyütürken gösterdikleri merhamet gibi, onlara merhamet et.\"", transliteration: "Vahfıd lehuma cenahaz zulli miner rahmeti ve kul rabbirhamhuma kema rabbeyani sagira."},
  {id: 2054, sure_no: 25, ayet_no: 25, sure_adi: "İsra", text: "رَبُّكُمْ اَعْلَمُ بِمَا ف۪ي نُفُوسِكُمْۜ اِنْ تَكُونُوا صَالِح۪ينَ فَاِنَّهُ كَانَ لِلْاَوَّاب۪ينَ غَفُوراً", translation: "Rabb'iniz niyetinizi çok iyi bilir. Eğer salihler olursanız, o zaman kuşkusuz O, Kendisine yönelenleri bağışlayıcıdır.", transliteration: "Rabbukum a'lemu bi ma fi nufusikum, in tekunu salihine fe innehu kane lil evvabine gafura."},
  {id: 2055, sure_no: 26, ayet_no: 26, sure_adi: "İsra", text: "وَاٰتِ ذَا الْقُرْبٰى حَقَّهُ وَالْمِسْك۪ينَ وَابْنَ السَّب۪يلِ وَلَا تُبَذِّرْ تَبْذ۪يراً", translation: "Yakınlık sahibine,1 düşkünlere ve yol oğluna yardım yap! Savurganlık yaparak saçıp savurma.", transliteration: "Ve ati zel kurba hakkahu vel miskine vebnes sebili ve la tubezzir tebzira."},
  {id: 2056, sure_no: 27, ayet_no: 27, sure_adi: "İsra", text: "اِنَّ الْمُبَذِّر۪ينَ كَانُٓوا اِخْوَانَ الشَّيَاط۪ينِۜ وَكَانَ الشَّيْطَانُ لِرَبِّه۪ كَفُوراً", translation: "Saçıp savuranlar, şeytanların kardeşleridir. Şeytan ise Rabb'ine karşı çok nankördür.", transliteration: "İnnel mubezzirine kanu ihvaneş şeyatin, ve kaneş şeytanu li rabbihi kefura."},
  {id: 2057, sure_no: 28, ayet_no: 28, sure_adi: "İsra", text: "وَاِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَٓاءَ رَحْمَةٍ مِنْ رَبِّكَ تَرْجُوهَا فَقُلْ لَهُمْ قَوْلاً مَيْسُوراً", translation: "Rabb'inden, ümit ettiğin rahmeti isterken, onlardan yüz çevirirsen bari yumuşak davranarak gönüllerini al.", transliteration: "Ve imma tu'ridanne anhumubtigae rahmetin min rabbike tercuha fe kul lehum kavlen meysura."},
  {id: 2058, sure_no: 29, ayet_no: 29, sure_adi: "İsra", text: "وَلَا تَجْعَلْ يَدَكَ مَغْلُولَةً اِلٰى عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوماً مَحْسُوراً", translation: "Elini bağlayıp boynuna asma. Onu büsbütün de açma. Aksi halde kınanırsın ve yaptığına pişman olur kalırsın.", transliteration: "Ve la tec'al yedeke magluleten ila unukıke ve la tebsutha kullel bastı fe tak'ude melumen mahsura."},
  {id: 2059, sure_no: 30, ayet_no: 30, sure_adi: "İsra", text: "اِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ اِنَّهُ كَانَ بِعِبَادِه۪ خَب۪يراً بَص۪يراً۟", translation: "Rabb'in, dilediği kimsenin rızkını genişletir ve bir ölçüye göre verir. O, kullarından Haberdar Olan'dır, Her Şeyi Gören'dir.", transliteration: "İnne rabbeke yebsutur rızka li men yeşau ve yakdir, innehu kane bi ibadihi habiran basira."},
  {id: 2060, sure_no: 31, ayet_no: 31, sure_adi: "İsra", text: "وَلَا تَقْتُلُٓوا اَوْلَادَكُمْ خَشْيَةَ اِمْلَاقٍۜ نَحْنُ نَرْزُقُهُمْ وَاِيَّاكُمْۜ اِنَّ قَتْلَهُمْ كَانَ خِطْـٔاً كَب۪يراً", translation: "Yoksulluk endişesi ile çocuklarınızı öldürmeyin. Onları ve sizi sadece Biz rızıklandırırız. Onların öldürülmesi büyük bir yanlıştır.", transliteration: "Ve la taktulu evladekum haşyete imlak, nahnu nerzukuhum ve iyyakum, inne katlehum kane hıt'en kebira."},
  {id: 2061, sure_no: 32, ayet_no: 32, sure_adi: "İsra", text: "وَلَا تَقْرَبُوا الزِّنٰٓى اِنَّهُ كَانَ فَاحِشَةًۜ وَسَٓاءَ سَب۪يلاً", translation: "Zinaya yaklaşmayın. Kuşkusuz o bir fuhuştur ve kötü bir yoldur.", transliteration: "Ve la takrebuz zina innehu kane fahışeh, ve sae sebila."},
  {id: 2062, sure_no: 33, ayet_no: 33, sure_adi: "İsra", text: "وَلَا تَقْتُلُوا النَّفْسَ الَّت۪ي حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۜ وَمَنْ قُتِلَ مَظْلُوماً فَقَدْ جَعَلْنَا لِوَلِيِّه۪ سُلْطَاناً فَلَا يُسْرِفْ فِي الْقَتْلِۜ اِنَّهُ كَانَ مَنْصُوراً", translation: "Allah'ın haram kıldığı bir canı haksız yere öldürmeyin. Kim haksız yere öldürülürse, Biz onun velisini sultan kıldık. O da öldürmede haddi aşmasın. O yardım olunmuştur.", transliteration: "Ve la taktulun nefselleti harremallahu illa bil hakk, ve men kutile mazlumen fe kad cealna li veliyyihi sultanen fe la yusrif fil katl, innehu kane mensura."},
  {id: 2063, sure_no: 34, ayet_no: 34, sure_adi: "İsra", text: "وَلَا تَقْرَبُوا مَالَ الْيَت۪يمِ اِلَّا بِالَّت۪ي هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهُۖ وَاَوْفُوا بِالْعَهْدِۚ اِنَّ الْعَهْدَ كَانَ مَسْؤُ۫لاً", translation: "Olgunluk çağına erişinceye kadar, iyiliği için olmadıkça yetimin malına dokunmayın. Ve verdiğiniz sözleri yerine getirin. Verilen söz insanı sorumlu yapar.", transliteration: "Ve la takrebu malel yetimi illa billeti hiye ahsenu hatta yebluga eşuddeh, ve evfu bil ahd, innel ahde kane mes'ula."},
  {id: 2064, sure_no: 35, ayet_no: 35, sure_adi: "İsra", text: "وَاَوْفُوا الْكَيْلَ اِذَا كِلْتُمْ وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَق۪يمِۜ ذٰلِكَ خَيْرٌ وَاَحْسَنُ تَأْو۪يلاً", translation: "Ölçtüğünüz zaman tam ölçün ve doğru terazi ile tartın. Hayırlı ve iyi olan budur.", transliteration: "Ve evful keyle iza kiltum vezinu bil kıstasil mustekim, zalike hayrun ve ahsenu te'vila."},
  {id: 2065, sure_no: 36, ayet_no: 36, sure_adi: "İsra", text: "وَلَا تَقْفُ مَا لَيْسَ لَكَ بِه۪ عِلْمٌۜ اِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤٰادَ كُلُّ اُو۬لٰٓئِكَ كَانَ عَنْهُ مَسْؤُ۫لاً", translation: "Hakkında bilgi sahibi olmadığın şeyin peşine düşme! Kuşkusuz kulak, göz ve fuad bunların hepsi ondan sorumludur.", transliteration: "Ve la takfu ma leyse leke bihi ilm, innes sem'a vel basara vel fuade kullu ulaike kane anhu mes'ula."},
  {id: 2066, sure_no: 37, ayet_no: 37, sure_adi: "İsra", text: "وَلَا تَمْشِ فِي الْاَرْضِ مَرَحاًۚ اِنَّكَ لَنْ تَخْرِقَ الْاَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُولاً", translation: "Yeryüzünde büyüklenerek yürüme! Sen asla yeri yaramazsın ve boyca dağlara erişemezsin.", transliteration: "Ve la temşi fil ardı mereha, inneke len tahrikal arda ve len teblugal cibale tula."},
  {id: 2067, sure_no: 38, ayet_no: 38, sure_adi: "İsra", text: "كُلُّ ذٰلِكَ كَانَ سَيِّئُهُ عِنْدَ رَبِّكَ مَكْرُوهاً", translation: "Bütün bunlar, Rabb'inin yanında hoş görülmeyen kötü şeylerdir.", transliteration: "Kullu zalike kane seyyiuhu inde rabbike mekruha."},
  {id: 2068, sure_no: 39, ayet_no: 39, sure_adi: "İsra", text: "ذٰلِكَ مِمَّٓا اَوْحٰٓى اِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِۜ وَلَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ فَتُلْقٰى ف۪ي جَهَنَّمَ مَلُوماً مَدْحُوراً", translation: "İşte bunlar, senin Rabb'inin, sana Hikmet'ten vahyettiği şeylerdendir. Allah ile birlikte başka ilah edinme. Yoksa kınanmış ve kovulmuş olarak Cehennem'e atılırsın.", transliteration: "Zalike mimma evha ileyke rabbuke minel hikmeh, ve la tec'al meallahi ilahen ahare fe tulka fi cehenneme melumen medhura."},
  {id: 2069, sure_no: 40, ayet_no: 40, sure_adi: "İsra", text: "اَفَاَصْفٰيكُمْ رَبُّكُمْ بِالْبَن۪ينَ وَاتَّخَذَ مِنَ الْمَلٰٓئِكَةِ اِنَاثاًۜ اِنَّكُمْ لَتَقُولُونَ قَوْلاً عَظ۪يماً۟", translation: "Rabb'iniz, oğulları size ayırdı da kendisi meleklerden kızlar mı edindi? Siz gerçekten çok büyük laf ediyorsunuz.", transliteration: "E fe asfakum rabbukum bil benine vettehaze minel melaiketi inasa, innekum le tekulune kavlen azima."},
  {id: 2070, sure_no: 41, ayet_no: 41, sure_adi: "İsra", text: "وَلَقَدْ صَرَّفْنَا ف۪ي هٰذَا الْقُرْاٰنِ لِيَذَّكَّرُواۜ وَمَا يَز۪يدُهُمْ اِلَّا نُفُوراً", translation: "Ant olsun ki Biz, bu Kur'an'da öğüt alsınlar diye her türlü açıklamayı yaptık. Oysaki bu onların yalnızca nefretlerini arttırdı.", transliteration: "Ve lekad sarrafna fi hazel kur'ani li yezzekkeru, ve ma yeziduhum illa nufura."},
  {id: 2071, sure_no: 42, ayet_no: 42, sure_adi: "İsra", text: "قُلْ لَوْ كَانَ مَعَهُٓ اٰلِهَةٌ  كَمَا يَقُولُونَ اِذاً لَابْتَغَوْا اِلٰى ذِي الْعَرْشِ سَب۪يلاً", translation: "De ki: \"Eğer dedikleri gibi, O'nunla beraber başka ilahlar olsaydı, o zaman onlar da mutlaka arşın sahibine bir yol ararlardı.\"", transliteration: "Kul lev kane meahu alihetun kema yekulune izen lebtegav ila zil arşı sebila."},
  {id: 2072, sure_no: 43, ayet_no: 43, sure_adi: "İsra", text: "سُبْحَانَهُ وَتَعَالٰى عَمَّا يَقُولُونَ عُلُواًّ كَب۪يراً", translation: "Allah, onların iddialarından Münezzeh'tir ve Yücedir.", transliteration: "Subhanehu ve teala amma yekulune uluvven kebira."},
  {id: 2073, sure_no: 44, ayet_no: 44, sure_adi: "İsra", text: "تُسَبِّحُ لَهُ السَّمٰوَاتُ السَّبْعُ وَالْاَرْضُ وَمَنْ ف۪يهِنَّۜ وَاِنْ مِنْ شَيْءٍ اِلَّا يُسَبِّحُ بِحَمْدِه۪ وَلٰكِنْ لَا تَفْقَهُونَ تَسْب۪يحَهُمْۜ اِنَّهُ كَانَ حَل۪يماً غَفُوراً", translation: "Yedi gök, yeryüzü ve içindekiler, O'nu tesbih ederler. Onu hamd ile tesbih etmeyen hiçbir şey yoktur. Fakat siz, onların tesbihlerini kavrayamazsınız. Kuşkusuz O, Çok Hoşgörülü'dür, Çok Bağışlayıcı'dır.", transliteration: "Tusebbihu lehus semavatus seb'u vel ardu ve men fihinn, ve in min şey'in illa yusebbihu bi hamdihi ve lakin la tefkahune tesbihahum, innehu kane halimen gafura."},
  {id: 2074, sure_no: 45, ayet_no: 45, sure_adi: "İsra", text: "وَاِذَا قَرَأْتَ الْقُرْاٰنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ حِجَاباً مَسْتُوراًۙ", translation: "Kur'an okuduğun zaman, seninle ahirete iman etmeyenler arasına görünmez bir perde çekeriz.", transliteration: "Ve iza kara'tel kur'ane cealna beyneke ve beynellezine la yu'minune bil ahıreti hicaben mestura."},
  {id: 2075, sure_no: 46, ayet_no: 46, sure_adi: "İsra", text: "وَجَعَلْنَا عَلٰى قُلُوبِهِمْ اَكِنَّةً اَنْ يَفْقَهُوهُ وَف۪ٓي اٰذَانِهِمْ وَقْراًۜ وَاِذَا ذَكَرْتَ رَبَّكَ فِي الْقُرْاٰنِ وَحْدَهُ وَلَّوْا عَلٰٓى اَدْبَارِهِمْ نُفُوراً", translation: "Kur'an'ı anlamalarına engel olsun diye, kalplerine perde, kulaklarına bir ağırlık koyduk. Sen, Kur'an'da Rabb'inin tekliğini andığın zaman, nefretle arkalarını dönüp kaçarlar.", transliteration: "Ve cealna ala kulubihim ekinneten en yefkahuhu ve fi azanihim vakra, ve iza zekerte rabbeke fil kur'ani vahdehu vellev ala edbarihim nufura."},
  {id: 2076, sure_no: 47, ayet_no: 47, sure_adi: "İsra", text: "نَحْنُ اَعْلَمُ بِمَا يَسْتَمِعُونَ بِه۪ٓ اِذْ يَسْتَمِعُونَ اِلَيْكَ وَاِذْ هُمْ نَجْوٰٓى اِذْ يَقُولُ الظَّالِمُونَ اِنْ تَتَّبِعُونَ اِلَّا رَجُلاً مَسْحُوراً", translation: "Biz onların seni ne amaçla dinlediklerini ve kendi aralarında fısıldaştıklarında da o zalimlerin: \"Siz büyülenmiş bir adamdan başkasına uymuyorsunuz.\" dediklerini çok iyi biliyoruz.", transliteration: "Nahnu a'lemu bima yestemiune bihi iz yestemiune ileyke ve iz hum necva iz yekuluz zalimune in tettebiune illa raculen meshura."},
  {id: 2077, sure_no: 48, ayet_no: 48, sure_adi: "İsra", text: "اُنْظُرْ كَيْفَ ضَرَبُوا لَكَ الْاَمْثَالَ فَضَلُّوا فَلَا يَسْتَط۪يعُونَ سَب۪يلاً", translation: "Seni neye benzettiklerine bir bak! Bu yüzden sapkınlaştılar. Artık, bir daha doğru yolu bulamazlar.", transliteration: "Unzur keyfe darabu lekel emsale fe dallu fe la yestetiune sebila."},
  {id: 2078, sure_no: 49, ayet_no: 49, sure_adi: "İsra", text: "وَقَالُٓوا ءَاِذَا كُنَّا عِظَاماً وَرُفَاتاً ءَاِنَّا لَمَبْعُوثُونَ خَلْقاً جَد۪يداً", translation: "\"Biz kemik yığını ve toz toprak olduktan sonra, gerçekten de yeni bir yaratılışla mı diriltileceğiz?\" dediler.", transliteration: "Ve kalu e iza kunna izamen ve rufaten e inna le meb'usune halkan cedida."},
  {id: 2079, sure_no: 50, ayet_no: 50, sure_adi: "İsra", text: "قُلْ كُونُوا حِجَارَةً اَوْ حَد۪يداًۙ", translation: "De ki: \"İster taş ister demir olun;\"", transliteration: "Kul kunu hicareten ev hadida."},
  {id: 2080, sure_no: 51, ayet_no: 51, sure_adi: "İsra", text: "اَوْ خَلْقاً مِمَّا يَكْبُرُ ف۪ي صُدُورِكُمْۚ فَسَيَقُولُونَ مَنْ يُع۪يدُنَاۜ قُلِ الَّذ۪ي فَطَرَكُمْ اَوَّلَ مَرَّةٍۚ فَسَيُنْغِضُونَ اِلَيْكَ رُؤُ۫سَهُمْ وَيَقُولُونَ مَتٰى هُوَۜ قُلْ عَسٰٓى اَنْ يَكُونَ قَر۪يباً", translation: "\"Veya göğüslerinizde büyüyen herhangi bir varlık.\" Sonra da \"Bizi kim geri çevirecek?\" diyecekler. De ki: \"Size ilk defa fıtrat belirlemiş olan.\" Alaylı alaylı başlarını sallayarak: \"Ne zamandır o?\" diyecekler. De ki: \"Belki de pek yakın bir zamanda.\"", transliteration: "Ev halkan mimma yekburu fi sudurikum, fe se yekulune men yuidun, kulillezi fetarakum evvele merreh, fe se yungıdune ileyke ruusehum ve yekulune meta huv, kul asa en yekune kariba."},
  {id: 2081, sure_no: 52, ayet_no: 52, sure_adi: "İsra", text: "يَوْمَ يَدْعُوكُمْ فَتَسْتَج۪يبُونَ بِحَمْدِه۪ وَتَظُنُّونَ اِنْ لَبِثْتُمْ اِلَّا قَل۪يلاً۟", translation: "\"Sizi çağıracağı gün, O'nu överek çağrıya uyacaksınız ve dünyada çok kısa bir süre kaldığınızı sanacaksınız.\"", transliteration: "Yevme yed'ukum fe testecibune bi hamdihi ve tezunnune in lebistum illa kalila."},
  {id: 2082, sure_no: 53, ayet_no: 53, sure_adi: "İsra", text: "وَقُلْ لِعِبَاد۪ي يَقُولُوا الَّت۪ي هِيَ اَحْسَنُۜ اِنَّ الشَّيْطَانَ يَنْزَغُ بَيْنَهُمْۜ اِنَّ الشَّيْطَانَ كَانَ لِلْاِنْسَانِ عَدُواًّ مُب۪يناً", translation: "Kullarıma de ki: \"Sözün en iyi olanını söylesinler!\" Şeytan, onların aralarını bozar. Şeytan, insan için apaçık bir düşmandır.", transliteration: "Ve kul li ibadi yekululleti hiye ahsen, inneş şeytane yenzegu beynehum, inneş şeytane kane lil insani aduvven mubina."},
  {id: 2083, sure_no: 54, ayet_no: 54, sure_adi: "İsra", text: "رَبُّكُمْ اَعْلَمُ بِكُمْۜ اِنْ يَشَأْ يَرْحَمْكُمْ اَوْ اِنْ يَشَأْ يُعَذِّبْكُمْۜ وَمَٓا اَرْسَلْنَاكَ عَلَيْهِمْ وَك۪يلاً", translation: "Rabb'iniz sizi daha iyi bilir. Hak edene merhamet eder veya hak edene azap eder. Seni onlara vekil olarak göndermedik.", transliteration: "Rabbukum a'lemu bikum, in yeşa' yerhamkum ev in yeşa' yuazzibkum, ve ma erselnake aleyhim vekila."},
  {id: 2084, sure_no: 55, ayet_no: 55, sure_adi: "İsra", text: "وَرَبُّكَ اَعْلَمُ بِمَنْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيّ۪نَ عَلٰى بَعْضٍ وَاٰتَيْنَا دَاوُ۫دَ زَبُوراً", translation: "Göklerde ve yeryüzünde kimlerin olduğunu Rabb'in daha iyi bilir. Ant olsun ki Biz Nebilerin kimini kiminden, kimi nitelikleriyle üstün kıldık. Davud'a Zebur'u verdik.", transliteration: "Ve rabbuke a'lemu bi men fis semavati vel ard, ve lekad faddalna ba'dan nebiyyine ala ba'dın ve ateyna davude zebura."},
  {id: 2085, sure_no: 56, ayet_no: 56, sure_adi: "İsra", text: "قُلِ ادْعُوا الَّذ۪ينَ زَعَمْتُمْ مِنْ دُونِه۪ فَلَا يَمْلِكُونَ كَشْفَ الضُّرِّ عَنْكُمْ وَلَا تَحْو۪يلاً", translation: "De ki: \"O'nun yanı sıra, zanda bulunduklarınızı çağırın.\" Oysa onlar, sizden bir sıkıntıyı yok etme veya onu değiştirmeye güç yetiremezler.", transliteration: "Kulid'ullezine zeamtum min dunihi fe la yemlikune keşfed durri ankum ve la tahvila."},
  {id: 2086, sure_no: 57, ayet_no: 57, sure_adi: "İsra", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ يَدْعُونَ يَبْتَغُونَ اِلٰى رَبِّهِمُ الْوَس۪يلَةَ اَيُّهُمْ اَقْرَبُ وَيَرْجُونَ رَحْمَتَهُ وَيَخَافُونَ عَذَابَهُۜ اِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُوراً", translation: "İşte onların, o yöneldikleri de Rabb'lerine daha yakın olmak için vesile arayan, O'nun rahmetini uman ve O'nun azabından korkan kimselerdir. Gerçekten Rabb'inin azabı korkunçtur.", transliteration: "Ulaikellezine yed'une yebtegune ila rabbihimul vesilete eyyuhum akrebu ve yercune rahmetehu ve yehafune azabeh, inne azabe rabbike kane mahzura."},
  {id: 2087, sure_no: 58, ayet_no: 58, sure_adi: "İsra", text: "وَاِنْ مِنْ قَرْيَةٍ اِلَّا نَحْنُ مُهْلِكُوهَا قَبْلَ يَوْمِ الْقِيٰمَةِ اَوْ مُعَذِّبُوهَا عَذَاباً شَد۪يداًۜ كَانَ ذٰلِكَ فِي الْكِتَابِ مَسْطُوراً", translation: "Hiçbir belde yoktur ki, kıyamet gününden önce biz onu yok etmeyelim veya şiddetli bir azap ile azaplandırmayalım. Bu Kitap'ta kayıtlıdır.", transliteration: "Ve in min karyetin illa nahnu muhlikuha kable yevmil kıyameti ev muazzibuha azaben şedida, kane zalike fil kitabi mestura."},
  {id: 2088, sure_no: 59, ayet_no: 59, sure_adi: "İsra", text: "وَمَا مَنَعَنَٓا اَنْ نُرْسِلَ بِالْاٰيَاتِ اِلَّٓا اَنْ كَذَّبَ بِهَا الْاَوَّلُونَۜ وَاٰتَيْنَا ثَمُودَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوا بِهَاۜ وَمَا نُرْسِلُ بِالْاٰيَاتِ اِلَّا تَخْو۪يفاً", translation: "Bizi ayet göndermekten alıkoyan şey, öncekilerin onu yalanlamış olmalarıdır. Semud halkına göz göre göre o dişi deveyi verdik. Onunla kendilerine zulmettiler. Ve Biz, ayetleri uyarmaktan başka bir şey için göndermeyiz.", transliteration: "Ve ma meneana en nursile bil ayati illa en kezzebe bihel evvelun, ve ateyna semuden nakate mubsıraten fe zalemu biha, ve ma nursilu bil ayati illa tahvifa."},
  {id: 2089, sure_no: 60, ayet_no: 60, sure_adi: "İsra", text: "وَاِذْ قُلْنَا لَكَ اِنَّ رَبَّكَ اَحَاطَ بِالنَّاسِۜ وَمَا جَعَلْنَا الرُّءْيَا الَّت۪ٓي اَرَيْنَاكَ اِلَّا فِتْنَةً لِلنَّاسِ وَالشَّجَرَةَ الْمَلْعُونَةَ فِي الْقُرْاٰنِۜ وَنُخَوِّفُهُمْۙ فَمَا يَز۪يدُهُمْ اِلَّا طُغْيَاناً كَب۪يراً۟", translation: "Hani bir zaman sana: \"Rabb'in insanları kuşatmıştır.\" demiştik. Sana gösterdiğimiz o rüyeti ve Kur'an'da lanet edilen ağacı, sadece insanlar için fitne kıldık. Biz onları uyarıyoruz. Fakat bu onların aşırı azgınlıklarını daha da artırmaktan başka bir şeye yaramıyor.", transliteration: "Ve iz kulna leke inne rabbeke ehata bin nas, ve ma cealner ru'yalleti ereynake illa fitneten lin nasi veş şeceretel mel'unete fil kur'an, ve nuhavvifuhum fe ma yeziduhum illa tugyanen kebira."},
  {id: 2090, sure_no: 61, ayet_no: 61, sure_adi: "İsra", text: "وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ قَالَ ءَاَسْجُدُ لِمَنْ خَلَقْتَ ط۪يناًۚ", translation: "Hani Biz meleklere, \"Adem'e secde edin.\" dediğimiz zaman, iblis hariç hepsi secde etti. İblis, \"Ben, çamurdan yarattığın kimseye mi secde edeceğim?\" dedi.", transliteration: "Ve iz kulna lil melaiketiscudu li ademe fe secedu illa iblis, kale e escudu li men halakte tina."},
  {id: 2091, sure_no: 62, ayet_no: 62, sure_adi: "İsra", text: "قَالَ اَرَاَيْتَكَ هٰذَا الَّذ۪ي كَرَّمْتَ عَلَيَّۘ  لَئِنْ اَخَّرْتَنِ اِلٰى يَوْمِ الْقِيٰمَةِ لَاَحْتَنِكَنَّ ذُرِّيَّتَهُٓ اِلَّا قَل۪يلاً", translation: "Dedi ki: \"Bu mu benden mükerrem kıldığın varlık? Eğer bana Kıyamet Günü'ne kadar müsaade edersen, pek azı hariç, onun soyunu kesinlikle buyruğum altına alacağım.\"", transliteration: "Kale e raeyteke hazellezi kerremte aley, le in ahharteni ila yevmil kıyameti le ahtenikenne zurriyyetehu illa kalila."},
  {id: 2092, sure_no: 63, ayet_no: 63, sure_adi: "İsra", text: "قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَاِنَّ جَهَنَّمَ جَزَٓاؤُ۬كُمْ جَزَٓاءً مَوْفُوراً", translation: "Allah, \"Git! Onlardan kim sana uyarsa, bilin ki bunun karşılığı tam bir karşılık olarak Cehennem'dir.\" dedi.", transliteration: "Kalezheb fe men tebiake minhum fe inne cehenneme cezaukum cezaen mevfura."},
  {id: 2093, sure_no: 64, ayet_no: 64, sure_adi: "İsra", text: "وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَاَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِي الْاَمْوَالِ وَالْاَوْلَادِ وَعِدْهُمْۜ وَمَا يَعِدُهُمُ الشَّيْطَانُ اِلَّا غُرُوراً", translation: "\"Onlardan, gücünün yettiğini sesinle ayart. Atlıların ve yayalarınla onları yaygara ile yönlendir. Mallarında ve çocuklarında onlara ortak ol. Ve onlara vaatlerde bulun. Şeytanın vadettikleri gururdan başka bir şey değildir.\"", transliteration: "Vestefziz menisteta'te minhum bi savtike ve eclib aleyhim bi haylike ve recilike ve şarikhum fil emvali vel evladi vaıdhum, ve ma yaiduhumuş şeytanu illa gurura."},
  {id: 2094, sure_no: 65, ayet_no: 65, sure_adi: "İsra", text: "اِنَّ عِبَاد۪ي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌۜ وَكَفٰى بِرَبِّكَ وَك۪يلاً", translation: "\"Bana yönelmiş kullarım üzerinde, senin bir sultanın yoktur. Vekil olarak Rabb'in yeter.\"", transliteration: "İnne ibadi leyse leke aleyhim sultan, ve kefa bi rabbike vekila."},
  {id: 2095, sure_no: 66, ayet_no: 66, sure_adi: "İsra", text: "رَبُّكُمُ الَّذ۪ي يُزْج۪ي لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِنْ فَضْلِه۪ۜ اِنَّهُ كَانَ بِكُمْ رَح۪يماً", translation: "Rabb'iniz, lütfundan arayasınız diye sizin için denizde gemileri yürütendir. O'nun size olan rahmeti kesintisizdir.", transliteration: "Rabbukumullezi yuzci lekumul fulke fil bahri li tebtegu min fadlih, innehu kane bi kum rahima."},
  {id: 2096, sure_no: 67, ayet_no: 67, sure_adi: "İsra", text: "وَاِذَا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ اِلَّٓا اِيَّاهُۚ فَلَمَّا نَجّٰيكُمْ اِلَى الْبَرِّ اَعْرَضْتُمْۜ وَكَانَ الْاِنْسَانُ كَفُوراً", translation: "Denizde bir sıkıntı size dokunduğu zaman, O'ndan başka yalvardıklarınız kaybolup gider. Fakat sizi karaya çıkarınca, yüz çevirirsiniz. İnsan çok nankördür.", transliteration: "Ve iza messekumud durru fil bahri dalle men ted'une illa iyyah, fe lemma neccakum ilel berri a'radtum, ve kanel insanu kefura."},
  {id: 2097, sure_no: 68, ayet_no: 68, sure_adi: "İsra", text: "اَفَاَمِنْتُمْ اَنْ يَخْسِفَ بِكُمْ جَانِبَ الْبَرِّ اَوْ يُرْسِلَ عَلَيْكُمْ حَاصِباً ثُمَّ لَا تَجِدُوا لَكُمْ وَك۪يلاًۙ", translation: "Peki, sizi karada yerin dibine geçirmesinden veya üzerinize bir kasırga göndermesinden güvende misiniz? Sonra kendinize bir vekil de bulamazsınız.", transliteration: "E fe emintum en yahsife bikum canibel berri ev yursile aleykum hasiben summe la tecidu lekum vekila."},
  {id: 2098, sure_no: 69, ayet_no: 69, sure_adi: "İsra", text: "اَمْ اَمِنْتُمْ اَنْ يُع۪يدَكُمْ ف۪يهِ تَارَةً اُخْرٰى فَيُرْسِلَ عَلَيْكُمْ قَاصِفاً مِنَ الرّ۪يحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْۙ ثُمَّ لَا تَجِدُوا لَكُمْ عَلَيْنَا بِه۪ تَب۪يعاً", translation: "Ya da sizi tekrar oraya döndürüp de böylece üzerinize kasırgalar göndermeyeceğinden ve ettiğiniz nankörlük nedeniyle sizi boğmayacağından emin mi oldunuz? Sonra bu yaptığınızdan dolayı Bize karşı size yardım edecek birini bulamazsınız.", transliteration: "Em emintum en yuidekum fihi tareten uhra fe yursile aleykum kasıfen miner rihı fe yugrikakum bima kefertum summe la tecidu lekum aleyna bihi tebia."},
  {id: 2099, sure_no: 70, ayet_no: 70, sure_adi: "İsra", text: "وَلَقَدْ كَرَّمْنَا بَن۪ٓي اٰدَمَ وَحَمَلْنَاهُمْ فِي الْبَرِّ وَالْبَحْرِ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلٰى كَث۪يرٍ مِمَّنْ خَلَقْنَا تَفْض۪يلاً۟", translation: "Ant olsun ki insanoğlunu kerem sahibi kıldık. Onları karada ve denizde taşıdık. Ve onları temiz şeylerle rızıklandırdık. Onları, yarattıklarımızın birçoğuna üstün kıldık.", transliteration: "Ve lekad kerremna beni ademe ve hamelnahum fil berri vel bahri ve razaknahum minet tayyibati ve faddalnahum ala kesirin mimmen halakna tafdila."},
  {id: 2100, sure_no: 71, ayet_no: 71, sure_adi: "İsra", text: "يَوْمَ نَدْعُوا كُلَّ اُنَاسٍ بِاِمَامِهِمْۚ فَمَنْ اُو۫تِيَ كِتَابَهُ بِيَم۪ينِه۪ فَاُو۬لٰٓئِكَ يَقْرَؤُ۫نَ كِتَابَهُمْ وَلَا يُظْلَمُونَ فَت۪يلاً", translation: "O Gün, bütün insanları imamlarıyla çağırırız. O Gün kimin kitabı sağından verilirse, işte onlar kendi kitaplarını okurlar ve kıl kadar haksızlığa uğratılmazlar.", transliteration: "Yevme ned'u kulle unasin bi imamihim, fe men utiye kitabehu bi yeminihi fe ulaike yakreune kitabehum ve la yuzlemune fetila."},
  {id: 2101, sure_no: 72, ayet_no: 72, sure_adi: "İsra", text: "وَمَنْ كَانَ ف۪ي هٰذِه۪ٓ اَعْمٰى فَهُوَ فِي الْاٰخِرَةِ اَعْمٰى وَاَضَلُّ سَب۪يلاً", translation: "Dünyada kör olan, ahirette de kör olacaktır. Yol bulma bakımından körden daha şaşkın olacaktır.", transliteration: "Ve men kane fi hazihi a'ma fe huve fil ahıreti a'ma ve edallu sebila."},
  {id: 2102, sure_no: 73, ayet_no: 73, sure_adi: "İsra", text: "وَاِنْ كَادُوا لَيَفْتِنُونَكَ عَنِ الَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهُۗ وَاِذاً لَاتَّخَذُوكَ خَل۪يلاً", translation: "Onlar, fitneyle neredeyse seni, sana vahyettiğimizden ayırarak, ondan başkasını Bize karşı uyduran bir iftiracı konumuna düşüreceklerdi. O zaman seni halil edinirlerdi.", transliteration: "Ve in kadu le yeftinuneke anillezi evhayna ileyke li tefteriye aleyna gayreh ve izen lettehazuke halila."},
  {id: 2103, sure_no: 74, ayet_no: 74, sure_adi: "İsra", text: "وَلَوْلَٓا اَنْ ثَبَّتْنَاكَ لَقَدْ كِدْتَ تَرْكَنُ اِلَيْهِمْ شَيْـٔاً قَل۪يلاًۗ", translation: "Seni dirençli kılmasaydık, ant olsun ki sen, neredeyse az da olsa onlara eğilim gösterecektin.", transliteration: "Ve lev la en sebbetnake lekad kidte terkenu ileyhim şey'en kalila."},
  {id: 2104, sure_no: 75, ayet_no: 75, sure_adi: "İsra", text: "اِذاً لَاَذَقْنَاكَ ضِعْفَ الْحَيٰوةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَص۪يراً", translation: "O durumda sana, hayatın da ölümün de azabını tattırırdık. Sonra Bize karşı bir yardımcı da bulamazdın.", transliteration: "İzen le ezaknake di'fal hayati ve di'fal memati summe la tecidu leke aleyna nasira."},
  {id: 2105, sure_no: 76, ayet_no: 76, sure_adi: "İsra", text: "وَاِنْ كَادُوا لَيَسْتَفِزُّونَكَ مِنَ الْاَرْضِ لِيُخْرِجُوكَ مِنْهَا وَاِذاً لَا يَلْبَثُونَ خِلَافَكَ اِلَّا قَل۪يلاً", translation: "Neredeyse seni bulunduğun yerden çıkman için tedirgin etmeyi başaracaklardı. Eğer öyle olsaydı, onlar da senin ardından ancak az bir süre kalabilirlerdi.", transliteration: "Ve in kadu le yestefizzuneke minel ardı li yuhricuke minha ve izen la yelbesune hilafeke illa kalila."},
  {id: 2106, sure_no: 77, ayet_no: 77, sure_adi: "İsra", text: "سُنَّةَ مَنْ قَدْ اَرْسَلْنَا قَبْلَكَ مِنْ رُسُلِنَا وَلَا تَجِدُ لِسُنَّتِنَا تَحْو۪يلاً۟", translation: "Senden önce de gönderdiğimiz Resul'lerimize uyguladığımız sünnetimiz budur. Bizim sünnetimizde bir değişiklik bulamazsın.", transliteration: "Sunnete men kad erselna kableke min rusulina ve la tecidu li sunnetina tahvila."},
  {id: 2107, sure_no: 78, ayet_no: 78, sure_adi: "İsra", text: "اَقِمِ الصَّلٰوةَ لِدُلُوكِ الشَّمْسِ اِلٰى غَسَقِ الَّيْلِ وَقُرْاٰنَ الْفَجْرِۜ اِنَّ قُرْاٰنَ الْفَجْرِ كَانَ مَشْهُوداً", translation: "Güneş'in batmasından gecenin karanlığı bastırıncaya kadar salatı ikame et. Ve fecrin kuranı; kuşkusuz fecrin kuranı tanıklıdır.", transliteration: "Ekımis salate li dulukiş şemsi ila gasakıl leyli ve kur'anel fecr, inne kur'anel fecri kane meşhuda."},
  {id: 2108, sure_no: 79, ayet_no: 79, sure_adi: "İsra", text: "وَمِنَ الَّيْلِ فَـتَهَجَّدْ بِه۪ نَافِلَةً لَكَۗ عَسٰٓى اَنْ يَبْعَثَكَ رَبُّكَ مَقَاماً مَحْمُوداً", translation: "Sana özgü nafile olarak gecenin bir kısmında onunla fetehecced et. Umulur ki Rabb'in seni makamı mahmuda ulaştırır.", transliteration: "Ve minel leyli fe tehecced bihi nafileten lek, asa en yeb'aseke rabbuke makamen mahmuda."},
  {id: 2109, sure_no: 80, ayet_no: 80, sure_adi: "İsra", text: "وَقُلْ رَبِّ اَدْخِلْن۪ي مُدْخَلَ صِدْقٍ وَاَخْرِجْن۪ي مُخْرَجَ صِدْقٍ وَاجْعَلْ ل۪ي مِنْ لَدُنْكَ سُلْطَاناً نَص۪يراً", translation: "De ki: \"Rabb'im! Beni doğru bir girişle girdir ve beni doğru bir çıkış ile çıkar. Ve bana katından bir sultan ver.\"", transliteration: "Ve kul rabbi edhılni mudhale sıdkın ve ahricni muhrece sıdkın vec'al li min ledunke sultanen nasira."},
  {id: 2110, sure_no: 81, ayet_no: 81, sure_adi: "İsra", text: "وَقُلْ جَٓاءَ الْحَقُّ وَزَهَقَ الْبَاطِلُۜ اِنَّ الْبَاطِلَ كَانَ زَهُوقاً", translation: "De ki: \"Hakk geldi, Batıl yok oldu.\" Kuşkusuz ki Batıl yok olmaya mahkumdur.", transliteration: "Ve kul cael hakku ve zehekal batıl, innel batıle kane zehuka."},
  {id: 2111, sure_no: 82, ayet_no: 82, sure_adi: "İsra", text: "وَنُنَزِّلُ مِنَ الْقُرْاٰنِ مَا هُوَ شِفَٓاءٌ وَرَحْمَةٌ لِلْمُؤْمِن۪ينَۙ وَلَا يَز۪يدُ الظَّالِم۪ينَ اِلَّا خَسَاراً", translation: "Kur'an'dan indirdiğimiz şeyler, Mü'minler için şifadır, rahmettir. Zalimlerin ise yalnızca hüsranını arttırır.", transliteration: "Ve nunezzilu minel kur'ani ma huve şifaun ve rahmetun lil mu'minine ve la yeziduz zalimine illa hasara."},
  {id: 2112, sure_no: 83, ayet_no: 83, sure_adi: "İsra", text: "وَاِذَٓا اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِه۪ۚ وَاِذَا مَسَّهُ الشَّرُّ كَانَ يَؤُ۫ساً", translation: "Ve kimi insana nimet verdiğimiz zaman, yüz çevirerek uzaklaşır. Ve ona bir kötülük dokunduğunda umutsuzluğa düşer.", transliteration: "Ve iza en'amna alel insani a'rada ve neabi canibih, ve iza messehuş şerru kane yeusa."},
  {id: 2113, sure_no: 84, ayet_no: 84, sure_adi: "İsra", text: "قُلْ كُلٌّ يَعْمَلُ عَلٰى شَاكِلَتِه۪ۜ فَرَبُّكُمْ اَعْلَمُ بِمَنْ هُوَ اَهْدٰى سَب۪يلاً۟", translation: "De ki: \"Herkes kendi karakterine göre davranır. Rabb'in, kimin doğru yolu seçtiğini iyi bilir.\"", transliteration: "Kul kullun ya'melu ala şakiletih, fe rabbukum a'lemu bi men huve ehda sebila."},
  {id: 2114, sure_no: 85, ayet_no: 85, sure_adi: "İsra", text: "وَيَسْـَٔلُونَكَ عَنِ الرُّوحِۜ قُلِ الرُّوحُ مِنْ اَمْرِ رَبّ۪ي وَمَٓا اُو۫ت۪يتُمْ مِنَ الْعِلْمِ اِلَّا قَل۪يلاً", translation: "Sana ruhtan soruyorlar. De ki: \"Ruh, Rabb'imin işindendir. Ve size ilimden çok az verilmiştir.\"", transliteration: "Ve yes'eluneke anir ruh, kulir ruhu min emri rabbi ve ma utitum minel ilmi illa kalila."},
  {id: 2115, sure_no: 86, ayet_no: 86, sure_adi: "İsra", text: "وَلَئِنْ شِئْنَا لَنَذْهَبَنَّ بِالَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِه۪ عَلَيْنَا وَك۪يلاًۙ", translation: "Eğer dilersek, sana vahyettiklerimizi ortadan kaldırırız; sonra Bize karşı kendine bir vekil bulamazsın.", transliteration: "Ve lein şi'na le nezhebenne billezi evhayna ileyke summe la tecidu leke bihi aleyna vekila."},
  {id: 2116, sure_no: 87, ayet_no: 87, sure_adi: "İsra", text: "اِلَّا رَحْمَةً مِنْ رَبِّكَۜ اِنَّ فَضْلَهُ  كَانَ عَلَيْكَ كَب۪يراً", translation: "Ancak bu, Rabb'inden bir rahmettir. O'nun, senin üzerindeki lütfu çok büyüktür.", transliteration: "İlla rahmeten min rabbik, inne fadlehu kane aleyke kebira."},
  {id: 2117, sure_no: 88, ayet_no: 88, sure_adi: "İsra", text: "قُلْ لَئِنِ اجْتَمَعَتِ الْاِنْسُ وَالْجِنُّ عَلٰٓى اَنْ يَأْتُوا بِمِثْلِ هٰذَا الْقُرْاٰنِ لَا يَأْتُونَ بِمِثْلِه۪ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَه۪يراً", translation: "De ki: \"Eğer ins ve cinn bu Kur'an'ın benzerini getirmek için bir araya gelseler ve birbirlerine yardımcı olsalar yine de onun benzerini ortaya koyamazlar.\"", transliteration: "Kul leinictemeatil insu vel cinnu ala en ye'tu bi misli hazel kur'ani la ye'tune bi mislihi ve lev kane ba'duhum li ba'dın zahira."},
  {id: 2118, sure_no: 89, ayet_no: 89, sure_adi: "İsra", text: "وَلَقَدْ صَرَّفْنَا لِلنَّاسِ ف۪ي هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۘ فَاَبٰٓى اَكْثَرُ النَّاسِ اِلَّا كُفُوراً", translation: "Ant olsun Biz, bu Kur'an'da insanlara her türlü örneği verdik. Buna rağmen insanların çoğu yine de kafirlikte diretti.", transliteration: "Ve lekad sarrafna lin nasi fi hazel kur'ani min kulli meselin fe eba ekserun nasi illa kufura."},
  {id: 2119, sure_no: 90, ayet_no: 90, sure_adi: "İsra", text: "وَقَالُوا لَنْ نُؤْمِنَ لَكَ حَتّٰى تَفْجُرَ لَنَا مِنَ الْاَرْضِ يَنْبُوعاًۙ", translation: "\"Yerden bizim için bir pınar fışkırtmadıkça sana asla inanmayacağız.\" dediler.", transliteration: "Ve kalu len nu'mine leke hatta tefcure lena minel ardı yenbua."},
  {id: 2120, sure_no: 91, ayet_no: 91, sure_adi: "İsra", text: "اَوْ تَكُونَ لَكَ جَنَّةٌ مِنْ نَخ۪يلٍ وَعِنَبٍ فَتُفَجِّرَ الْاَنْهَارَ خِلَالَهَا تَفْج۪يراًۙ", translation: "\"Veya senin hurma ve üzüm bağlarından bir bahçen olmalı ve aralarından gürül gürül nehirler akıtmalısın.\"", transliteration: "Ev tekune leke cennetun min nahilin ve inebin fe tufeccirel enhare hılaleha tefcira."},
  {id: 2121, sure_no: 92, ayet_no: 92, sure_adi: "İsra", text: "اَوْ تُسْقِطَ السَّمَٓاءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفاً اَوْ تَأْتِيَ بِاللّٰهِ وَالْمَلٰٓئِكَةِ قَب۪يلاًۙ", translation: "\"Veya iddia ettiğin gibi, göğü paramparça edip üzerimize yıkmalısın. Veya Allah'ı ve melekleri karşımıza çıkarmalısın.\"", transliteration: "Ev tuskıtas semae kema zeamte aleyna kisefen ev te'tiye billahi vel melaiketi kabila."},
  {id: 2122, sure_no: 93, ayet_no: 93, sure_adi: "İsra", text: "اَوْ يَكُونَ لَكَ بَيْتٌ مِنْ زُخْرُفٍ اَوْ تَرْقٰى فِي السَّمَٓاءِۜ وَلَنْ نُؤْمِنَ لِرُقِيِّكَ حَتّٰى تُنَزِّلَ عَلَيْنَا كِتَاباً نَقْرَؤُ۬هُۜ قُلْ سُبْحَانَ رَبّ۪ي هَلْ كُنْتُ اِلَّا بَشَراً رَسُولاً۟", translation: "\"Veya altından bir evin olmalı, ya da göğe yükselmelisin. Çıksan dahi, bize oradan okuyacağımız bir kitap indirmedikçe senin yükselişine asla inanmayız. De ki: \"Benim Rabb'im noksanlıklardan arıdır. Ben, bir beşer ve resulden başka bir şey miyim ki?\"", transliteration: "Ev yekune leke beytun min zuhrufin ev terka fis sema, ve len nu'mine li rukıyyike hatta tunezzile aleyna kitaben nakreuh, kul subhane rabbi hel kuntu illa beşeren resula."},
  {id: 2123, sure_no: 94, ayet_no: 94, sure_adi: "İsra", text: "وَمَا مَنَعَ النَّاسَ اَنْ يُؤْمِنُٓوا اِذْ جَٓاءَهُمُ الْهُدٰٓى اِلَّٓا اَنْ قَالُٓوا اَبَعَثَ اللّٰهُ بَشَراً رَسُولاً", translation: "İnsanlara, doğru yola ileten rehber gelince, onları iman etmekten alıkoyan şey, \"Allah, ölümlü bir beşeri mi resul gönderdi?\" şeklindeki anlayışlarıdır.", transliteration: "Ve ma menean nase en yu'minu iz cae humul huda illa en kalu e beasallahu beşeren resula."},
  {id: 2124, sure_no: 95, ayet_no: 95, sure_adi: "İsra", text: "قُلْ لَوْ كَانَ فِي الْاَرْضِ مَلٰٓئِكَةٌ يَمْشُونَ مُطْمَئِنّ۪ينَ لَنَزَّلْنَا عَلَيْهِمْ مِنَ السَّمَٓاءِ مَلَكاً رَسُولاً", translation: "De ki: \"Eğer yeryüzünün sakinleri melekler olsaydı, elbette onlara gökten resul olarak bir melek gönderirdik.\"", transliteration: "Kul lev kane fil ardı melaiketun yemşune mutmainnine le nezzelna aleyhim mines semai meleken resula."},
  {id: 2125, sure_no: 96, ayet_no: 96, sure_adi: "İsra", text: "قُلْ كَفٰى بِاللّٰهِ شَه۪يداً بَيْن۪ي وَبَيْنَكُمْۜ اِنَّهُ كَانَ بِعِبَادِه۪ خَب۪يراً بَص۪يراً", translation: "De ki: \"Benimle sizin aranızda şahit olarak Allah yeter. Kuşkusuz O, kulları için Her Şeyden Haberdar Olan'dır, Her Şeyi Gören'dir.\"", transliteration: "Kul kefa billahi şehiden beyni ve beynekum, innehu kane bi ıbadihi habiren basira."},
  {id: 2126, sure_no: 97, ayet_no: 97, sure_adi: "İsra", text: "وَمَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُمْ اَوْلِيَٓاءَ مِنْ دُونِه۪ۜ وَنَحْشُرُهُمْ يَوْمَ الْقِيٰمَةِ عَلٰى وُجُوهِهِمْ عُمْياً وَبُكْماً وَصُماًّۜ مَأْوٰيهُمْ جَهَنَّمُۜ  كُلَّمَا خَبَتْ زِدْنَاهُمْ سَع۪يراً", translation: "Allah, kime hidayet etmişse, işte o doğru yolu bulmuştur. Kimi de saptırırsa, onun için, O'ndan başka veliler bulamazsın. Kıyamet Günü, onları; kör, sağır ve dilsiz olarak yüzüstü sürünür durumda mahşer yerine toplarız. Onların varacakları yer Cehennem'dir. O ne zaman dinse, onlara ateşi artırırız.", transliteration: "Ve men yehdillahu fe huvel muhted, ve men yudlil fe len tecide lehum evliyae min dunih, ve nahşuruhum yevmel kıyameti ala vucuhihim umyen ve bukmen ve summa, me'vahum cehennem, kullema habet zidnahum saira."},
  {id: 2127, sure_no: 98, ayet_no: 98, sure_adi: "İsra", text: "ذٰلِكَ جَزَٓاؤُ۬هُمْ بِاَنَّهُمْ كَفَرُوا بِاٰيَاتِنَا وَقَالُٓوا ءَاِذَا كُنَّا عِظَاماً وَرُفَاتاً ءَاِنَّا لَمَبْعُوثُونَ خَلْقاً جَد۪يداً", translation: "Onların, cezalandırılmalarının nedeni, ayetlerimizi inkar etmelerinden ve \"Biz bir yığın kemik ve ufalanmış toz haline geldikten sonra mı yeni bir yaratılışla diriltilecekmişiz?\" diye alay etmeleridir.", transliteration: "Zalike cezauhum bi ennehum keferu bi ayatina ve kalu e iza kunna izamen ve rufaten e inna le meb'usune halkan cedida."},
  {id: 2128, sure_no: 99, ayet_no: 99, sure_adi: "İsra", text: "اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ قَادِرٌ عَلٰٓى اَنْ يَخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ اَجَلاً لَا رَيْبَ ف۪يهِۜ فَاَبَى الظَّالِمُونَ اِلَّا كُفُوراً", translation: "Gökleri ve yeri yaratan Allah'ın, tıpkı bunlar gibi, yeni yaratmalara da gücünün yeteceğini görmüyorlar mı? Kendileri için bir gün sona ereceği kesin olan bir süre tayin etmiştir. Zalimlerin yaptıkları Küfr'den başka bir şey değildir.", transliteration: "E ve lem yerev ennallahellezi halakas semavati vel arda kadirun ala en yahluka mislehum ve ceale lehum ecelen la reybe fih, fe ebaz zalimune illa kufura."},
  {id: 2129, sure_no: 100, ayet_no: 100, sure_adi: "İsra", text: "قُلْ لَوْ اَنْتُمْ تَمْلِكُونَ خَزَٓائِنَ رَحْمَةِ رَبّ۪ٓي اِذاً لَاَمْسَكْتُمْ خَشْيَةَ الْاِنْفَاقِۜ وَكَانَ الْاِنْسَانُ قَتُوراً۟", translation: "De ki: \"Eğer siz, Rabb'imin rahmet hazinelerine sahip olsaydınız, tükenir endişesi ile kimseye bir şey vermezdiniz. Kimi insan çok cimridir.\"", transliteration: "Kul lev entum temlikune hazaine rahmeti rabbi izen le emsektum haşyetel infak, ve kanel insanu katura."},
  {id: 2130, sure_no: 101, ayet_no: 101, sure_adi: "İsra", text: "وَلَقَدْ اٰتَيْنَا مُوسٰى تِسْعَ اٰيَاتٍ بَيِّنَاتٍ فَسْـَٔلْ بَن۪ٓي اِسْرَٓائ۪لَ اِذْ جَٓاءَهُمْ فَقَالَ لَهُ فِرْعَوْنُ اِنّ۪ي لَاَظُنُّكَ يَا مُوسٰى مَسْحُوراً", translation: "Ant olsun ki Biz Musa'ya apaçık dokuz ayet verdik. İsrailoğulları'na sor. Onlara geldiğinde, Firavun ona, \"Ey Musa! Ben kesinlikle büyülenmiş olduğunu görüyorum.\" demişti.", transliteration: "Ve lekad ateyna musa tis'a ayatin beyyinatin fes'el beni israile iz caehum fe kale lehu fir'avnu inni le ezunnuke ya musa meshura."},
  {id: 2131, sure_no: 102, ayet_no: 102, sure_adi: "İsra", text: "قَالَ لَقَدْ عَلِمْتَ مَٓا اَنْزَلَ هٰٓؤُ۬لَٓاءِ اِلَّا رَبُّ السَّمٰوَاتِ وَالْاَرْضِ بَصَٓائِرَۚ وَاِنّ۪ي لَاَظُنُّكَ يَا فِرْعَوْنُ مَثْبُوراً", translation: "Musa: \"Bunları uyarıcı, aydınlatıcı olarak göklerin ve yerin Rabb'inden başkasının indirmediğini sen bildin. Ey Firavun! Ben de senin mahvolduğunu görüyorum\". dedi.", transliteration: "Kale lekad alimte ma enzele haulai illa rabbus semavati vel ardı basair, ve inni le ezunnuke ya fir'avnu mesbura."},
  {id: 2132, sure_no: 103, ayet_no: 103, sure_adi: "İsra", text: "فَاَرَادَ اَنْ يَسْتَفِزَّهُمْ مِنَ الْاَرْضِ فَاَغْرَقْنَاهُ وَمَنْ مَعَهُ جَم۪يعاًۙ", translation: "Bunun üzerine yeryüzünü onlara dar etti. Biz, onu ve beraberinde olanların tamamını suda boğduk.", transliteration: "Fe erade en yestefizzehum minel ardı fe agraknahu ve men meahu cemia."},
  {id: 2133, sure_no: 104, ayet_no: 104, sure_adi: "İsra", text: "وَقُلْنَا مِنْ بَعْدِه۪ لِبَن۪ٓي اِسْرَٓائ۪لَ اسْكُنُوا الْاَرْضَ فَاِذَا جَٓاءَ وَعْدُ الْاٰخِرَةِ جِئْنَا بِكُمْ لَف۪يفاًۜ", translation: "Arkasından İsrailoğulları'na, \"güven içinde o yerde yerleşin! Sonra ahiret için verilen süre dolunca hepinizi toplayıp bir araya getireceğiz.\" dedik.", transliteration: "Ve kulna min ba'dihi li beni israileskunul arda fe iza cae va'dul ahıreti ci'na bikum lefifa."},
  {id: 2134, sure_no: 105, ayet_no: 105, sure_adi: "İsra", text: "وَبِالْحَقِّ اَنْزَلْنَاهُ وَبِالْحَقِّ نَزَلَۜ وَمَٓا اَرْسَلْنَاكَ اِلَّا مُبَشِّراً وَنَذ۪يراًۢ", translation: "Onu hakk ile indirdik. Ve hakk ile indi. Seni, haber verici ve uyarıcı olmandan başka bir şey için göndermedik.", transliteration: "Ve bil hakkı enzelnahu ve bil hakkı nezel, ve ma erselnake illa mubeşşiren ve nezira."},
  {id: 2135, sure_no: 106, ayet_no: 106, sure_adi: "İsra", text: "وَقُرْاٰناً فَرَقْنَاهُ لِتَقْرَاَهُ۫ عَلَى النَّاسِ عَلٰى مُكْثٍ وَنَزَّلْنَاهُ تَنْز۪يلاً", translation: "Kur'an'ı; onu, zamana yayarak insanlara duyurman için bölümler halinde birbiri ardınca indirdik.", transliteration: "Ve kur'anen faraknahu li takreehu alen nasi ala muksin ve nezzelnahu tenzila."},
  {id: 2136, sure_no: 107, ayet_no: 107, sure_adi: "İsra", text: "قُلْ اٰمِنُوا بِه۪ٓ اَوْ لَا تُؤْمِنُواۜ اِنَّ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ مِنْ قَبْلِه۪ٓ اِذَا يُتْلٰى عَلَيْهِمْ يَخِرُّونَ لِلْاَذْقَانِ سُجَّداًۙ", translation: "De ki: \"Ona ister iman edin ister iman etmeyin; daha önce kendilerine ilim verilenler, o, onlara okunduğu zamanlar, çeneleri üzerine secdeye kapanırlar.\"", transliteration: "Kul aminu bihi ev la tu'minu, innellezine utul ilme min kablihi iza yutla aleyhim yahırrune lil ezkani succeda."},
  {id: 2137, sure_no: 108, ayet_no: 108, sure_adi: "İsra", text: "وَيَقُولُونَ سُبْحَانَ رَبِّنَٓا اِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُولاً", translation: "Ve derler ki: \"Rabb'imiz her şeyden yücedir. Rabb'imizin uyarısı kesinlikle gerçekleşecektir.", transliteration: "Ve yekulune subhane rabbina in kane va'du rabbina le mef'ula."},
  {id: 2138, sure_no: 109, ayet_no: 109, sure_adi: "İsra", text: "وَيَخِرُّونَ لِلْاَذْقَانِ يَبْكُونَ وَيَز۪يدُهُمْ خُشُوعاً", translation: "Onlar, ağlayarak çeneleri üzerine kapanırlar. Bu onların huşularını artırır.", transliteration: "Ve yahırrune lil ezkani yebkune ve yeziduhum huşua."},
  {id: 2139, sure_no: 110, ayet_no: 110, sure_adi: "İsra", text: "قُلِ ادْعُوا اللّٰهَ اَوِ ادْعُوا الرَّحْمٰنَۜ  اَياًّ مَا تَدْعُوا فَلَهُ الْاَسْمَٓاءُ الْحُسْنٰىۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذٰلِكَ سَب۪يلاً", translation: "De ki: \"İster Allah diye çağırın ister Rahman diye çağırın. Hangisiyle çağırırsanız çağırın en iyi isimler O'nundur.\" Salatında ne fazla yükselt ne de fazla kıs. Bu ikisi arasında bir yol tut.", transliteration: "Kulid'ullahe evid'ur rahman, eyyen ma ted'u fe lehul esmaul husna, ve la techer bi salatike ve la tuhafit biha vebtegı beyne zalike sebila."},
  {id: 2140, sure_no: 111, ayet_no: 111, sure_adi: "İsra", text: "وَقُلِ الْحَمْدُ لِلّٰهِ الَّذ۪ي لَمْ يَتَّخِذْ وَلَداً وَلَمْ يَكُنْ لَهُ شَر۪يكٌ فِي الْمُلْكِ وَلَمْ يَكُنْ لَهُ وَلِيٌّ مِنَ الذُّلِّ وَكَبِّرْهُ تَكْب۪يراً", translation: "Ve de ki: \"Hamd, çocuk edinmeyen Allah'a özgüdür. O'nun mülkte ortağı yoktur. O'nun acizlikten dolayı bir veliye de ihtiyacı yoktur.\" O'nu tam bir yüceltme ile yücelt.", transliteration: "Ve kulil hamdu lillahillezi lem yettehız veleden ve lem yekun lehu şerikun fil mulki ve lem yekun lehu veliyyun minez zulli ve kebbirhu tekbira."},
  {id: 2141, sure_no: 1, ayet_no: 1, sure_adi: "Kehf", text: "اَلْحَمْدُ لِلّٰهِ الَّـذ۪ٓي اَنْزَلَ عَلٰى عَبْدِهِ الْـكِتَابَ وَلَمْ يَجْعَلْ لَهُ عِـوَجا۔ًۜ", translation: "Hamd, kendisinde hiçbir çelişki olmayan Kitap'ı indiren Allah'a aittir.", transliteration: "El hamdulillahillezi enzele ala abdihil kitabe ve lem yec'al lehu ıveca."},
  {id: 2142, sure_no: 2, ayet_no: 2, sure_adi: "Kehf", text: "قَيِّماً لِيُنْذِرَ بَأْساً شَد۪يداً مِنْ لَدُنْـهُ وَيُبَشِّرَ الْمُؤْمِن۪ينَ الَّذ۪ينَ يَعْمَلُونَ الصَّالِحَاتِ اَنَّ لَهُمْ اَجْراً حَسَناًۙ", translation: "Dosdoğru bir gözetici olarak; şiddetli azaba karşı uyarmak, erdemli ve doğru davranışlarda bulunan Mü'minlere, en iyi karşılığın onların olduğunu müjdelemek için katından indirdi.", transliteration: "Kayyimen li yunzire be'sen şediden min ledunhu ve yubeşşirel mu'mininellezine ya'melunes salihati enne lehum ecren hasena."},
  {id: 2143, sure_no: 3, ayet_no: 3, sure_adi: "Kehf", text: "مَاكِث۪ينَ ف۪يهِ اَبَداًۙ", translation: "Onlar, orada ebediyen kalıcıdırlar.", transliteration: "Makisine fihi ebeda."},
  {id: 2144, sure_no: 4, ayet_no: 4, sure_adi: "Kehf", text: "وَيُنْذِرَ الَّذ۪ينَ قَالُوا اتَّخَذَ اللّٰهُ وَلَداًۗ", translation: "Ve \"Allah, çocuk edindi.\" diyenleri uyarmak için.", transliteration: "Ve yunzirellezine kaluttehazellahu veleda."},
  {id: 2145, sure_no: 5, ayet_no: 5, sure_adi: "Kehf", text: "مَا لَهُمْ بِه۪ مِنْ عِلْمٍ وَلَا لِاٰبَٓائِهِمْۜ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ اَفْوَاهِهِمْۜ اِنْ يَقُولُونَ اِلَّا كَذِباً", translation: "Buna dair kendilerinin de atalarının da hiçbir bilgileri yoktur. Çok büyük söz ediyorlar. Yalandan başka bir şey söylemiyorlar.", transliteration: "Ma lehum bihi min ilmin ve la li abaihim, keburet kelimeten tahrucu min efvahihim, in yekulune illa keziba."},
  {id: 2146, sure_no: 6, ayet_no: 6, sure_adi: "Kehf", text: "فَلَعَلَّكَ بَاخِـعٌ نَفْسَكَ عَلٰٓى اٰثَارِهِمْ اِنْ لَمْ يُؤْمِنُوا بِهٰذَا الْحَد۪يثِ اَسَفاً", translation: "Onlar, bu söze inanmıyorlar diye, onların durumlarına üzüntünden neredeyse kendini harap edeceksin!", transliteration: "Fe lealleke bahiun nefseke ala asarihim in lem yu'minu bi hazel hadisi esefa."},
  {id: 2147, sure_no: 7, ayet_no: 7, sure_adi: "Kehf", text: "اِنَّا جَعَلْنَا مَا عَلَى الْاَرْضِ ز۪ينَةً لَهَا لِنَبْلُوَهُمْ اَيُّهُمْ اَحْسَنُ عَمَلاً", translation: "Biz, hangisinin daha iyi şeyler yapacağına sınav olsun diye, yeryüzünde olan şeyleri ilgi çekici yaptık.", transliteration: "İnna cealna ma alel ardı zineten leha li nebluvehum eyyuhum ahsenu amela."},
  {id: 2148, sure_no: 8, ayet_no: 8, sure_adi: "Kehf", text: "وَاِنَّا لَجَاعِلُونَ مَا عَلَيْهَا صَع۪يداً جُرُزاًۜ", translation: "Yeryüzünde olan güzellikleri kupkuru toprak haline getireceğiz.", transliteration: "Ve inna le cailune ma aleyha saiden curuza."},
  {id: 2149, sure_no: 9, ayet_no: 9, sure_adi: "Kehf", text: "اَمْ حَسِبْتَ اَنَّ اَصْحَابَ الْكَهْفِ وَالرَّق۪يمِ كَانُوا مِنْ اٰيَاتِنَا عَجَباً", translation: "Yoksa sen, Kehf ve Rakim Ashabı'nın Bizim en şaşılacak ayetlerimizden olduklarını mı sandın?", transliteration: "Em hasibte enne ashabel kehfi ver rakimi kanu min ayatina acaba."},
  {id: 2150, sure_no: 10, ayet_no: 10, sure_adi: "Kehf", text: "اِذْ اَوَى الْفِتْيَةُ اِلَى الْكَهْفِ فَقَالُوا رَبَّنَٓا اٰتِنَا مِنْ لَدُنْكَ رَحْمَةً وَهَيِّئْ لَنَا مِنْ اَمْرِنَا رَشَداً", translation: "Gençler, mağaraya sığındıkları zaman şöyle demişlerdi: \"Rabb'imiz, bize kendi katından bir rahmet ver. İşimizde doğru olanı yapma bilinci lütfet.\"", transliteration: "İz evel fityetu ilel kehfi fe kalu rabbena atina min ledunke rahmeten ve heyyi' lena min emrina reşeda."},
  {id: 2151, sure_no: 11, ayet_no: 11, sure_adi: "Kehf", text: "فَضَرَبْنَا عَلٰٓى اٰذَانِهِمْ فِي الْكَهْفِ سِن۪ينَ عَدَداًۙ", translation: "Böylece onları yıllar yılı mağarada dış dünyadan habersiz yaptık.", transliteration: "Fe darabna ala azanihim fil kehfi sinine adeda."},
  {id: 2152, sure_no: 12, ayet_no: 12, sure_adi: "Kehf", text: "ثُمَّ بَعَثْنَاهُمْ لِنَعْلَمَ اَيُّ الْحِزْبَيْنِ اَحْصٰى لِمَا لَبِثُٓوا اَمَداً۟", translation: "onra iki gruptan hangisinin kaldıkları süreyi daha iyi hesapladığını ortaya çıkarmak için, onları tekrar uyandırdık.", transliteration: "Summe beasnahum li na'leme eyyul hızbeyni ahsa lima lebisu emeda."},
  {id: 2153, sure_no: 13, ayet_no: 13, sure_adi: "Kehf", text: "نَحْنُ نَقُصُّ عَلَيْكَ نَبَاَهُمْ بِالْحَقِّۜ اِنَّهُمْ فِتْيَةٌ اٰمَنُوا بِرَبِّهِمْ وَزِدْنَاهُمْ هُدًىۗ", translation: "Biz, sana onların hikayesini bütün gerçekliği ile anlatıyoruz. Onlar Rabb'lerine iman eden gençlerdi. Biz de onlara hidayeti arttırdık.", transliteration: "Nahnu nakussu aleyke nebeehum bil hakk, innehum fityetun amenu bi rabbihim ve zidnahum huda."},
  {id: 2154, sure_no: 14, ayet_no: 14, sure_adi: "Kehf", text: "وَرَبَطْنَا عَلٰى قُلُوبِهِمْ اِذْ قَامُوا فَقَالُوا رَبُّنَا رَبُّ السَّمٰوَاتِ وَالْاَرْضِ لَنْ نَدْعُوَ۬ا مِنْ دُونِه۪ٓ اِلٰهاً لَقَدْ قُلْـنَٓا اِذاً شَطَطاً", translation: "Onların kalplerini pekiştirdik. Kıyam ederek: \"Bizim Rabb'imiz, göklerin ve yeryüzünün Rabb'idir\" dediler. \"Ondan başkasını asla ilah diye çağırmayız. Yoksa kesinlikle saçmalamış oluruz.\"", transliteration: "Ve rabatna ala kulubihim iz kamu fe kalu rabbuna rabbus semavati vel ardı len ned'uve min dunihi ilahen lekad kulna izen şetata."},
  {id: 2155, sure_no: 15, ayet_no: 15, sure_adi: "Kehf", text: "هٰٓؤُ۬لَٓاءِ قَوْمُنَا اتَّخَذُوا مِنْ دُونِه۪ٓ اٰلِهَةًۜ  لَوْلَا يَأْتُونَ عَلَيْهِمْ بِسُلْطَانٍ بَيِّنٍۜ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباًۜ", translation: "İşte bunlar, O'nun yanı sıra ilahlar edinenler bizim halkımızdır. Onların ilahlığını gösteren açık bir yetki belgesi getirselerdi ya! Allah'a karşı yalan uydurandan daha zalim kim olabilir?\"", transliteration: "Haulai kavmunettehazu min dunihi aliheh, lev la ye'tune aleyhim bi sultanin beyyin, fe men azlemu mimmeniftera alallahi keziba."},
  {id: 2156, sure_no: 16, ayet_no: 16, sure_adi: "Kehf", text: "وَاِذِ اعْتَزَلْتُمُوهُمْ وَمَا يَعْبُدُونَ اِلَّا اللّٰهَ فَأْوُٓ۫ا اِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِنْ رَحْمَتِه۪ وَيُهَيِّئْ لَكُمْ مِنْ اَمْرِكُمْ مِرْفَقاً", translation: "\"Mademki onlardan ve Allah'tan başka kulluk ettikleri şeylerden ayrılmayı tercih ettiniz, o halde mağaraya sığının ki, Rabb'iniz size rahmetini açsın ve size destek olarak işlerinizi kolaylaştırsın.\"", transliteration: "Ve izi'tezeltumuhum ve ma ya'budune illallahe fe'vu ilel kehfi yenşur lekum rabbukum min rahmetihi ve yuheyyi' lekum min emrikum mirfeka."},
  {id: 2157, sure_no: 17, ayet_no: 17, sure_adi: "Kehf", text: "وَتَرَى الشَّمْسَ اِذَا طَلَعَتْ تَزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَم۪ينِ وَاِذَا غَرَبَتْ تَقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ ف۪ي فَجْوَةٍ مِنْهُۜ ذٰلِكَ مِنْ اٰيَاتِ اللّٰهِۜ مَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِياًّ مُرْشِداً۟", translation: "Onlar, mağaranın geniş bir dehlizinde bulunurlarken, Güneş'in doğduğu zaman, mağaralarından sağa tarafa yöneldiğini, battığı zaman da sol tarafa doğru onları makaslayıp geçtiğini görürsün. Bu Allah'ın ayetlerindendir. Allah, kimi doğru yola iletirse, işte o doğru yolu bulmuştur. Kimi de sapkınlıkta bırakırsa, artık onun için yol gösteren bir veli bulamazsın.", transliteration: "Ve tereş şemse iza taleat tezaveru an kehfihim zatel yemini ve iza garabet takrıduhum zateş şimali ve hum fi fecvetin minh, zalike min ayatillah, men yehdillahu fe huvel muhted, ve men yudlil fe len tecide lehu veliyyen murşida."},
  {id: 2158, sure_no: 18, ayet_no: 18, sure_adi: "Kehf", text: "وَتَحْسَبُهُمْ اَيْقَاظاً وَهُمْ رُقُودٌۗ وَنُقَلِّبُهُمْ ذَاتَ الْيَم۪ينِ وَذَاتَ الشِّمَالِۗ وَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَص۪يدِۜ لَوِ اطَّـلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَاراً وَلَمُلِئْتَ مِنْهُمْ رُعْباً", translation: "Uykuda oldukları halde sen onları uyanık sanırsın. Ve biz onları sağ yanlarına ve sol yanlarına çeviririz. Köpekleri de avluda ön ayaklarını öne doğru uzatmıştı. Eğer sen onlarla karşılaşsaydın, gerisin geri kaçardın. Ve korkudan ürperirdin.", transliteration: "Ve tahsebuhum eykazan ve hum rukud, ve nukallibuhum zatel yemini ve zateş şimal, ve kelbuhum basitun ziraayhi bil vasid, levittala'te aleyhim le velleyte minhum firaren ve le muli'te minhum ru'ba."},
  {id: 2159, sure_no: 19, ayet_no: 19, sure_adi: "Kehf", text: "وَكَذٰلِكَ بَعَثْنَاهُمْ لِيَتَسَٓاءَلُوا بَيْنَهُمْۜ قَالَ قَٓائِلٌ مِنْهُمْ كَمْ لَبِثْتُمْۜ قَالُوا لَبِثْنَا يَوْماً اَوْ بَعْضَ يَوْمٍۜ قَالُوا رَبُّكُمْ اَعْلَمُ بِمَا لَبِثْتُمْ فَابْعَثُٓوا اَحَدَكُمْ بِوَرِقِكُمْ هٰذِه۪ٓ اِلَى الْمَد۪ينَةِ فَلْيَنْظُرْ اَيُّهَٓا اَزْكٰى طَعَاماً فَلْيَأْتِكُمْ بِرِزْقٍ مِنْهُ وَلْيَتَلَطَّفْ وَلَا يُشْعِرَنَّ بِكُمْ اَحَداً", translation: "Onları uyandırdık ve böylece birbirlerine sormaya başladılar. Onlardan birisi şu soruyu sordu: \"Ne kadar kaldınız?\" \"Bir gün veya günün bir bölümü kadar.\" dediler. Kimisi de: \"Ne kadar kaldığınızı Rabb'iniz bilir.\" dedi. \"Sizden birisini, gümüş paranızla şehre gönderin. Hangi yiyeceği seviyorsanız ondan yiyecek getirsin. Ve sizi kimseye sezdirmemeye dikkat etsin.\"", transliteration: "Ve kezalike beasnahum li yetesaelu beynehum, kale kailun minhum kem lebistum, kalu lebisna yevmen ev ba'da yevm, kalu rabbukum a'lemu bi ma lebistum feb'asu ehadekum bi verıkıkum hazihi ilel medineti fel yanzur eyyuha ezka taamen fel ye'tikum bi rızkın minhu vel yetelattaf ve la yuş'ırenne bikum ehada."},
  {id: 2160, sure_no: 20, ayet_no: 20, sure_adi: "Kehf", text: "اِنَّهُمْ اِنْ يَظْهَرُوا عَلَيْكُمْ يَرْجُمُوكُمْ اَوْ يُع۪يدُوكُمْ ف۪ي مِلَّتِهِمْ وَلَنْ تُفْلِحُٓوا اِذاً اَبَداً", translation: "\"Onlar sizi fark ederlerse, sizi taşa tutarlar veya kendi milletlerine döndürürler. O zaman, kesinlikle ebediyen kurtuluşa eremezsiniz.\"", transliteration: "İnnehum in yazheru aleykum yercumukum ev yuidukum fi milletihim ve len tuflihu izen ebeda."},
  {id: 2161, sure_no: 21, ayet_no: 21, sure_adi: "Kehf", text: "وَكَذٰلِكَ اَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُٓوا اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَاَنَّ السَّاعَةَ لَا رَيْبَ ف۪يهَاۚ اِذْ يَتَنَازَعُونَ بَيْنَهُمْ اَمْرَهُمْ فَقَالُوا ابْنُوا عَلَيْهِمْ بُنْيَاناًۜ رَبُّهُمْ اَعْلَمُ بِهِمْۜ قَالَ الَّذ۪ينَ غَلَبُوا عَلٰٓى اَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَسْجِداً", translation: "Böylece, Allah'ın verdiği sözün gerçek olduğunu, o Sa'at'in kesin olduğunu bilmeleri için onları haberdar ettik. O zaman, onların durumu hakkında aralarında tartışıyorlardı. \"Onların üzerlerine bir bina yapın; onların Rabb'leri, onları daha iyi bilir.\" dediler. Düşünceleri kabul edilenler: \"Onların üzerlerine bir mescid yapalım.\" dediler.", transliteration: "Ve kezalike a'serna aleyhim li ya'lemu enne va'dallahi hakkun ve ennes saate la reybe fiha, iz yetenazeune beynehum emrehum fe kalubnu aleyhim bunyana, rabbuhum a'lemu bihim, kalellezine galebu ala emrihim le nettehızenne aleyhim mescida."},
  {id: 2162, sure_no: 22, ayet_no: 22, sure_adi: "Kehf", text: "سَيَقُولُونَ ثَلٰثَةٌ رَابِعُهُمْ كَلْبُهُمْۚ وَيَقُولُونَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْماً بِالْغَيْبِۚ وَيَقُولُونَ سَبْعَةٌ وَثَامِنُهُمْ كَلْبُهُمْۜ قُلْ رَبّ۪ٓي اَعْلَمُ بِعِدَّتِهِمْ مَا يَعْلَمُهُمْ اِلَّا قَل۪يلٌ۠ فَلَا تُمَارِ ف۪يهِمْ اِلَّا مِرَٓاءً ظَاهِراًۖ وَلَا تَسْتَفْتِ ف۪يهِمْ مِنْهُمْ اَحَداً۟", translation: "\"Onlar üç kişidir, dördüncüsü köpekleridir.\" diyecekler. Gayba taş atar gibi \"Beş kişidir, altıncısı köpekleridir.\" diyecekler, \"Yedi kişidir, sekizincisi köpekleridir.\" diyecekler. De ki: \"Onların sayılarını Rabb'im bilir. Onları pek az kimseden başkası bilmez.\" Onlar hakkında, Kur'an'ın verdiği bilgi dışında onlarla tartışma. Onlar hakkında tartışan hiç kimseden de bir açıklama isteme.", transliteration: "Se yekulune selasetun rabiuhum kelbuhum, ve yekulune hamsetun sadisuhum kelbuhum recmen bil gayb, ve yekulune seb'atun ve saminuhum kelbuhum, kul rabbi a'lemu bi ıddetihim ma ya'lemuhum illa kalil, fe la tumari fihim illa miraen zahira, ve la testefti fihim minhum ehada."},
  {id: 2163, sure_no: 23, ayet_no: 23, sure_adi: "Kehf", text: "وَلَا تَقُولَنَّ لِشَايْءٍ اِنّ۪ي فَاعِلٌ ذٰلِكَ غَداًۙ", translation: "Hiçbir şey için, \"Ben, yarın bunu kesinlikle yapacağım.\" deme.", transliteration: "Ve la tekulenne li şey'in inni faılun zalike gada."},
  {id: 2164, sure_no: 24, ayet_no: 24, sure_adi: "Kehf", text: "اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُۘ وَاذْكُرْ رَبَّكَ اِذَا نَس۪يتَ وَقُلْ عَسٰٓى اَنْ يَهْدِيَنِ رَبّ۪ي لِاَقْرَبَ مِنْ هٰذَا رَشَداً", translation: "Ancak, \"Allah'ın dilemesiyle yapacağım.\" de. Ve unuttuğun zaman Rabb'ini an. Ve \"Umulur ki Rabb'im beni en doğru olanı yapmaya yöneltir.\" de.", transliteration: "İlla en yeşaallahu vezkur rabbeke iza nesite ve kul asa en yehdiyeni rabbi li akrabe min haza reşeda."},
  {id: 2165, sure_no: 25, ayet_no: 25, sure_adi: "Kehf", text: "وَلَبِثُوا ف۪ي كَـهْفِهِمْ ثَلٰثَ مِائَةٍ سِن۪ينَ وَازْدَادُوا تِسْعاً", translation: "Onlar, mağaralarında, üç yüz yıl kaldılar ve dokuz yıl ilave ettiler.", transliteration: "Ve lebisu fi kehfihim selase mietin sinine vezdadu tis'a."},
  {id: 2166, sure_no: 26, ayet_no: 26, sure_adi: "Kehf", text: "قُلِ اللّٰهُ اَعْلَمُ بِمَا لَبِثُواۚ لَهُ غَيْبُ السَّمٰوَاتِ وَالْاَرْضِۜ اَبْصِرْ بِه۪ وَاَسْمِـعْۜ مَا لَهُمْ مِنْ دُونِه۪ مِنْ وَلِيٍّۘ وَلَا يُشْرِكُ ف۪ي حُكْمِه۪ٓ اَحَداً", translation: "De ki: \"Onların ne kadar kaldıklarını en iyi Allah bilir.\" Göklerin ve yerin bilinmezi yalnızca O'na aittir. En iyi gören ve en iyi işiten O'dur! Onlar için O'ndan başka veli yoktur. O, hükümranlığına2 kimseyi ortak etmez.", transliteration: "Kulillahu a'lemu bima lebisu, lehu gaybus semavati vel ard, ebsır bihi ve esmı', ma lehum min dunihi min veliyyin ve la yuşriku fi hukmihi ehada."},
  {id: 2167, sure_no: 27, ayet_no: 27, sure_adi: "Kehf", text: "وَاتْلُ مَٓا اُو۫حِيَ اِلَيْكَ مِنْ كِتَابِ رَبِّكَۚ لَا مُبَدِّلَ لِكَلِمَاتِه۪ وَلَنْ تَجِدَ مِنْ دُونِه۪ مُلْتَحَداً", translation: "Ve sen, Rabb'inin Kitap'ından sana vahyedileni oku! O'nun kelimelerini değiştirecek yoktur. O'ndan başka sığınılacak da bulamazsın.", transliteration: "Vetlu ma uhıye ileyke min kitabi rabbik, la mubeddile li kelimatihi ve len tecide min dunihi multehada."},
  {id: 2168, sure_no: 28, ayet_no: 28, sure_adi: "Kehf", text: "وَاصْبِرْ نَفْسَكَ مَعَ الَّذ۪ينَ يَدْعُونَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُر۪يدُونَ وَجْهَهُ وَلَا تَعْدُ عَيْنَاكَ عَنْهُمْۚ تُر۪يدُ ز۪ينَةَ الْحَيٰوةِ الدُّنْيَا وَلَا تُطِـعْ مَنْ اَغْفَلْنَا قَلْبَهُ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوٰيهُ وَكَانَ اَمْرُهُ فُرُطاً", translation: "Sabah akşam O'nun yoluna yönelerek, Rabb'ine çağrıda bulunanlarla beraber olmada sabırlı ol. Dünya hayatının çekiciliğine kanarak gözlerini onlardan ayırma. Kalbini zikrimizden gafil kıldığımız, tutkularına uymuş, işi aşırılık olan kimseye boyun eğme.", transliteration: "Vasbır nefseke meallezine yed'une rabbehum bil gadati vel aşiyyi yuridune vechehu ve la ta'du aynake anhum, turidu zinetel hayatid dunya ve la tutı' men agfelna kalbehu an zikrina vettebea hevahu ve kane emruhu furuta."},
  {id: 2169, sure_no: 29, ayet_no: 29, sure_adi: "Kehf", text: "وَقُلِ الْحَقُّ مِنْ رَبِّكُمْ فَمَنْ شَٓاءَ فَلْيُؤْمِنْ وَمَنْ شَٓاءَ فَلْيَكْفُرْۙ اِنَّٓا اَعْتَدْنَا لِلظَّالِم۪ينَ نَاراًۙ اَحَاطَ بِهِمْ سُرَادِقُهَاۜ وَاِنْ يَسْتَغ۪يثُوا يُغَاثُوا بِمَٓاءٍ كَالْمُهْلِ يَشْوِي الْوُجُوهَۜ بِئْسَ الشَّرَابُۜ وَسَٓاءَتْ مُرْتَفَقاً", translation: "De ki: \"Hakk Rabb'inizdendir. O halde dileyen iman etsin, dileyen küfretsin.\" Kuşkusuz Biz, zalimler için bir ateş hazırladık. Çadır gibi onları kuşatan. Eğer yardım isterlerse, erimiş maden gibi yüzleri haşlayan bir su yağdırılır. O ne kötü bir içecektir! Ve ne kötü bir barınma yeridir.", transliteration: "Ve kulil hakku min rabbikum fe men şae fel yu'min ve men şae fel yekfur inna a'tedna liz zalimine naren ehata bihim suradikuha, ve in yestegisu yugasu bi main kel muhli yeşvil vucuh, bi'seş şerab ve saet murtefeka."},
  {id: 2170, sure_no: 30, ayet_no: 30, sure_adi: "Kehf", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ اِنَّا لَا نُض۪يعُ اَجْرَ مَنْ اَحْسَنَ عَمَلاًۚ", translation: "Gerçek şu ki; iman edip salihatı yapanlara gelince, Biz, iyi bir iş yapan hiç kimsenin yaptığını karşılıksız bırakmayız.", transliteration: "İnnellezine amenu ve amilus salihati inna la nudiu ecre men ahsene amela."},
  {id: 2171, sure_no: 31, ayet_no: 31, sure_adi: "Kehf", text: "اُو۬لٰٓئِكَ لَهُمْ جَنَّاتُ عَدْنٍ تَجْر۪ي مِنْ تَحْتِهِمُ الْاَنْهَارُ يُحَلَّوْنَ ف۪يهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَيَلْبَسُونَ ثِيَاباً خُضْراً مِنْ سُنْدُسٍ وَاِسْتَبْرَقٍ مُتَّكِـ۪ٔينَ ف۪يهَا عَلَى الْاَرَٓائِكِۜ نِعْمَ الثَّوَابُۜ وَحَسُنَتْ مُرْتَفَقاً۟", translation: "İşte onlara Adn Cennetleri vardır. Onların içinden nehirler akar. Orada, altından bileziklerle süslenirler. İnce ve kalın ipekten yeşil elbiseler giyerler. Orada tahtlar üzerine yaslanırlar. Ne güzel bir karşılık ve ne iyi bir ağırlanma yeri!", transliteration: "Ulaike lehum cennatu adnin tecri min tahtihimul enharu yuhallevne fiha min esavire min zehebin ve yelbesune siyaben hudren min sundusin ve istebrekın muttekiine fiha alel eraik, ni'mes sevab, ve hasunet murtefeka."},
  {id: 2172, sure_no: 32, ayet_no: 32, sure_adi: "Kehf", text: "وَاضْرِبْ لَهُمْ مَثَلاً رَجُلَيْنِ جَعَلْنَا لِاَحَدِهِمَا جَنَّتَيْنِ مِنْ اَعْنَابٍ وَحَفَفْنَاهُمَا بِنَخْلٍ وَجَعَلْنَا بَيْنَهُمَا زَرْعاًۜ", translation: "Onlara iki adamın durumunu örnek ver. Bunlardan birine her türlü üzümden iki bahçe yaptık. Ve bu iki bahçenin çevresini hurmalıklarla donattık. Aralarında da ekinlikler bitirdik.", transliteration: "Vadrıb lehum meselen raculeyni cealna li ehadihima cenneteyni min a'nabin ve hafefnahuma bi nahlin ve cealna beynehuma zer'a."},
  {id: 2173, sure_no: 33, ayet_no: 33, sure_adi: "Kehf", text: "كِلْتَا الْجَنَّتَيْنِ اٰتَتْ اُكُلَهَا وَلَمْ تَظْلِمْ مِنْهُ شَيْـٔاًۙ وَفَجَّرْنَا خِلَالَهُمَا نَهَراًۙ", translation: "İki bahçenin ikisi de hiçbir şey eksik bırakmadan meyvelerini verdi. Ve aralarında bir nehir akıttık.", transliteration: "Kiltel cenneteyni atet ukuleha ve lem tazlim minhu şey'en ve feccerna hılalehuma nehera."},
  {id: 2174, sure_no: 34, ayet_no: 34, sure_adi: "Kehf", text: "وَكَانَ لَهُ ثَمَرٌۚ فَقَالَ لِصَاحِبِه۪ وَهُوَ يُحَاوِرُهُٓ اَنَا۬ اَكْثَرُ مِنْكَ مَالاً وَاَعَزُّ نَفَراً", translation: "Ve onun serveti oldu. Arkadaşı ile konuşurken: \"Ben malca senden daha zenginim, insan sayısınca da senden daha güçlüyüm.\" dedi.", transliteration: "Ve kane lehu semer, fe kale li sahıbihi ve huve yuhaviruhu ene ekseru minke malen ve eazzu nefera."},
  {id: 2175, sure_no: 35, ayet_no: 35, sure_adi: "Kehf", text: "وَدَخَلَ جَنَّتَهُ وَهُوَ ظَالِمٌ لِنَفْسِه۪ۚ قَالَ مَٓا اَظُنُّ اَنْ تَب۪يدَ هٰذِه۪ٓ اَبَداًۙ", translation: "Ve o kendisine yazık ederek bahçesine girdi: \"Ben, bunun hiçbir zaman yok olacağını sanmıyorum.\" dedi.", transliteration: "Ve dehale cennetehu ve huve zalimun li nefsih, kale ma ezunnu en tebide hazihi ebeda."},
  {id: 2176, sure_no: 36, ayet_no: 36, sure_adi: "Kehf", text: "وَمَٓا اَظُنُّ السَّاعَةَ قَٓائِمَةًۙ وَلَئِنْ رُدِدْتُ اِلٰى رَبّ۪ي لَاَجِدَنَّ خَيْراً مِنْهَا مُنْقَلَباً", translation: "\"Kıyametin kopacağını sanmıyorum. Eğer böyle bir şey olur da Rabb'ime döndürülürsem, mutlaka orada bundan daha hayırlısını bulurum.\"", transliteration: "Ve ma ezunnus saate kaimeten ve le in rudidtu ila rabbi le ecidenne hayren minha munkaleba."},
  {id: 2177, sure_no: 37, ayet_no: 37, sure_adi: "Kehf", text: "قَالَ لَهُ صَاحِبُهُ وَهُوَ يُحَاوِرُهُٓ  اَكَفَرْتَ بِالَّذ۪ي خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ سَوّٰيكَ رَجُلاًۜ", translation: "Tartışmaya girdiği arkadaşı ona: \"Seni topraktan, sonra bir nutfeden yaratan, sonra da seni insan şekline sokana nankörlük mü ediyorsun?\"", transliteration: "Kale lehu sahıbuhu ve huve yuhaviruhu e keferte billezi halakake min turabin summe min nutfetin summe sevvake racula."},
  {id: 2178, sure_no: 38, ayet_no: 38, sure_adi: "Kehf", text: "لٰكِنَّا۬ هُوَ اللّٰهُ رَبّ۪ي وَلَٓا اُشْرِكُ بِرَبّ۪ٓي اَحَداً", translation: "\"Oysa benim Rabb'im Allah'tır. Ve ben Rabb'ime kimseyi ortak koşmam.\"", transliteration: "Lakinne huvallahu rabbi ve la uşriku bi rabbi ehada."},
  {id: 2179, sure_no: 39, ayet_no: 39, sure_adi: "Kehf", text: "وَلَوْلَٓا اِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَٓاءَ اللّٰهُۙ لَا قُوَّةَ اِلَّا بِاللّٰهِۚ اِنْ تَرَنِ اَنَا۬ اَقَلَّ مِنْكَ مَالاً وَوَلَداًۚ", translation: "\"Her ne kadar beni mal-mülk ve evlat bakımından eksik görüyorsan da bahçene girdiğin zaman: \"Allah ne dilerse o olur, Allah'tan başka hiçbir güç yoktur, deseydin ya!\"", transliteration: "Ve lev la iz dehalte cenneteke kulte ma şaallahu la kuvvete illa billah, in tereni ene ekalle minke malen ve veleda."},
  {id: 2180, sure_no: 40, ayet_no: 40, sure_adi: "Kehf", text: "فَعَسٰى رَبّ۪ٓي اَنْ يُؤْتِيَنِ خَيْراً مِنْ جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَاناً مِنَ السَّمَٓاءِ فَتُصْبِحَ صَع۪يداً زَلَقاًۙ", translation: "\"Belki Rabb'im, bana senin bahçenden daha hayırlısını verir. Ve seninkinin üzerine de gökten felaketler gönderir de verimsiz, kupkuru bir toprak olur.\"", transliteration: "Fe asa rabbi en yu'tiyeni hayran min cennetike ve yursile aleyha husbanen mines semai fe tusbiha saiden zeleka."},
  {id: 2181, sure_no: 41, ayet_no: 41, sure_adi: "Kehf", text: "اَوْ يُصْبِحَ مَٓاؤُ۬هَا غَوْراً فَلَنْ تَسْتَط۪يعَ لَهُ طَلَباً", translation: "\"Veya onun suyu yerin dibine çekilir de artık onu çıkarmaya asla gücün yetmez.\"", transliteration: "Ev yusbiha mauha gavren fe len testetia lehu taleba."},
  {id: 2182, sure_no: 42, ayet_no: 42, sure_adi: "Kehf", text: "وَاُح۪يطَ بِثَمَرِه۪ فَاَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلٰى مَٓا اَنْفَقَ ف۪يهَا وَهِيَ خَاوِيَةٌ عَلٰى عُرُوشِهَا وَيَقُولُ يَا لَيْتَن۪ي لَمْ اُشْرِكْ بِرَبّ۪ٓي اَحَداً", translation: "Onun ürünleri kuşatılıp bitirildi. Ve çardakları üzerine yıkılmıştı. Yaptığı harcamalara üzülerek ellerini ovuştururken, \"Keşke ben Rabb'ime hiçbir şeyi ortak koşmasaydım.\" diyordu.", transliteration: "Ve uhita bi semerihi fe asbeha yukallibu keffeyhi ala ma enfeka fiha ve hiye haviyetun ala uruşiha ve yekulu ya leyteni lem uşrik bi rabbi ehada."},
  {id: 2183, sure_no: 43, ayet_no: 43, sure_adi: "Kehf", text: "وَلَمْ تَكُنْ لَهُ فِئَةٌ يَنْصُرُونَهُ مِنْ دُونِ اللّٰهِ وَمَا كَانَ مُنْتَصِراًۜ", translation: "Allah'tan başka kendisine yardım edecek kimseler olmadı. Ve kendi kendisini de koruyamadı.", transliteration: "Ve lem tekun lehu fietun yansurunehu min dunillahi ve ma kane muntesira."},
  {id: 2184, sure_no: 44, ayet_no: 44, sure_adi: "Kehf", text: "هُنَالِكَ الْوَلَايَةُ لِلّٰهِ الْحَقِّۜ هُوَ خَيْرٌ ثَوَاباً وَخَيْرٌ عُقْباً۟", translation: "İşte bu durumda velilik, gerçek olarak yalnızca Allah'a aittir. O, ödüllendirme bakımından da en hayırlı olandır. Sonuç bakımından da en hayırlı olandır.", transliteration: "Hunalikel velayetu lillahil hakk, huve hayrun sevaben ve hayrun ukba."},
  {id: 2185, sure_no: 45, ayet_no: 45, sure_adi: "Kehf", text: "وَاضْرِبْ لَهُمْ مَثَلَ الْحَيٰوةِ الدُّنْيَا كَمَٓاءٍ اَنْزَلْنَاهُ مِنَ السَّمَٓاءِ فَاخْتَلَطَ بِه۪ نَبَاتُ الْاَرْضِ فَاَصْبَحَ هَش۪يماً تَذْرُوهُ الرِّيَاحُۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُقْتَدِراً", translation: "Dünya hayatının neye benzediğine dair şu örneği ver: o, gökten indirdiğimiz su ile yeryüzünde yeşeren ve daha sonra çer çöp olup rüzgarla sağa sola savrulan bitki gibidir. Allah'ın Her Şeye Gücü Yeter.", transliteration: "Vadrıb lehum meselel hayatid dunya ke main enzelnahu mines semai fahteleta bihi nebatul ardı fe asbeha heşimen tezruhur riyah, ve kanallahu ala kulli şey'in muktedira."},
  {id: 2186, sure_no: 46, ayet_no: 46, sure_adi: "Kehf", text: "اَلْمَالُ وَالْبَنُونَ ز۪ينَةُ الْحَيٰوةِ الدُّنْيَاۚ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَاباً وَخَيْرٌ اَمَلاً", translation: "Mal ve çocuklar, dünya hayatının süsüdür. Kalıcı olan iyi işler ise Rabb'inin yanında daha değerlidir. Ümit bağlama yönünden de daha değerlidir.", transliteration: "El malu vel benune zinetul hayatid dunya, vel bakıyatus salihatu hayrun inde rabbike sevaben ve hayrun emela."},
  {id: 2187, sure_no: 47, ayet_no: 47, sure_adi: "Kehf", text: "وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْاَرْضَ بَارِزَةًۙ وَحَشَرْنَاهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ اَحَداًۚ", translation: "O Gün dağları yürüteceğiz. Ve yeryüzünü dümdüz görürsün. Hiç kimseyi bırakmaksızın onların tamamını mahşerde toplarız.", transliteration: "Ve yevme nuseyyirul cibale ve terel arda barizeten ve haşernahum fe lem nugadir minhum ehada."},
  {id: 2188, sure_no: 48, ayet_no: 48, sure_adi: "Kehf", text: "وَعُرِضُوا عَلٰى رَبِّكَ صَفاًّۜ لَقَدْ جِئْتُمُونَا كَمَا خَلَقْنَاكُمْ اَوَّلَ مَرَّةٍۘ بَلْ زَعَمْتُمْ اَلَّنْ نَجْعَلَ لَكُمْ مَوْعِداً", translation: "Saf saf Rabb'ine arz olunacaklar. \"Ant olsun ki siz, ilk yarattığımız gibi Bize geldiniz. Hayır, size yaptığımız uyarıları, gerçekleştirmeyeceğimizi sanıyordunuz.\"", transliteration: "Ve uridu ala rabbike saffa, lekad ci'tumuna kema halaknakum evvele merreh, bel zeamtum ellen nec'ale lekum mev'ıda."},
  {id: 2189, sure_no: 49, ayet_no: 49, sure_adi: "Kehf", text: "وَوُضِعَ الْكِتَابُ فَتَرَى الْمُجْرِم۪ينَ مُشْفِق۪ينَ مِمَّا ف۪يهِ وَيَقُولُونَ يَا وَيْلَتَنَا مَالِ هٰذَا الْكِتَابِ لَا يُغَادِرُ صَغ۪يرَةً وَلَا كَب۪يرَةً اِلَّٓا اَحْصٰيهَاۚ وَوَجَدُوا مَا عَمِلُوا حَاضِراًۜ وَلَا يَظْلِمُ رَبُّكَ اَحَداً۟", translation: "Ve kitap ortaya kondu. O zaman suçluların onda olanlardan ürperdiklerini görürsün. \"Eyvah bize! Bu nasıl bir kitap ki, büyük-küçük saymadık hiçbir şey bırakmamış.\" derler. Yaptıkları her şeyi hazır buldular. Senin Rabb'in, hiç kimseye haksızlık yapmaz.", transliteration: "Ve vudıal kitabu fe terel mucrimine muşfikine mimma fihi ve yekulune ya veyletena mali hazel kitabi la yugadiru sagireten ve la kebireten illa ahsaha, ve vecedu ma amilu hadıra, ve la yazlimu rabbuke ehada."},
  {id: 2190, sure_no: 50, ayet_no: 50, sure_adi: "Kehf", text: "وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ اَمْرِ رَبِّه۪ۜ  اَفَتَتَّخِذُونَهُ وَذُرِّيَّتَهُٓ اَوْلِيَٓاءَ مِنْ دُون۪ي وَهُمْ لَكُمْ عَدُوٌّۜ بِئْسَ لِلظَّالِم۪ينَ بَدَلاً", translation: "Hani! Biz, meleklere, \"Adem'e secde edin.\" demiştik. İblis hariç hepsi secde etmişti. O cinlerdendi. Böylece Rabb'inin sözünden dışarı çıktı. Benim yanım sıra, onu ve soyunu evliya mı ediniyorsunuz? Oysa onlar sizin düşmanınızdır. Zalimler için ne kötü bir tercihtir bu!", transliteration: "Ve iz kulna lil melaiketiscudu li ademe fe secedu illa iblis, kane minel cinni fe feseka an emri rabbih, e fe tettehızunehu ve zurriyyetehu evliyae min duni ve hum lekum aduvv, bi'se liz zalimine bedela."},
  {id: 2191, sure_no: 51, ayet_no: 51, sure_adi: "Kehf", text: "مَٓا اَشْهَدْتُهُمْ خَلْقَ السَّمٰوَاتِ وَالْاَرْضِ وَلَا خَلْقَ اَنْفُسِهِمْۖ وَمَا كُنْتُ مُتَّخِذَ الْمُضِلّ۪ينَ عَضُداً", translation: "Ben onları göklerin ve yeryüzünün yaratılışına ve kendilerinin yaratılışına tanık tutmadım. Ve Ben saptıranları yardımcı edinmedim.", transliteration: "Ma eşhedtuhum halkas semavati vel ardı ve la halka enfusihim ve ma kuntu muttehızel mudılline aduda."},
  {id: 2192, sure_no: 52, ayet_no: 52, sure_adi: "Kehf", text: "وَيَوْمَ يَقُولُ نَادُوا شُرَكَٓاءِيَ الَّذ۪ينَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَج۪يبُوا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَوْبِقاً", translation: "O Gün Allah, \"Benim ortaklarım olarak sandıklarınızı haydi çağırın.\" der. Sonra onları çağırdılar fakat onlar, kendilerine cevap vermedi. Ve Biz onların aralarına aşılmaz bir engel koyduk.", transliteration: "Ve yevme yekulu nadu şurekaiyellezine zeamtum fe deavhum fe lem yestecibu lehum ve cealna beynehum mevbika."},
  {id: 2193, sure_no: 53, ayet_no: 53, sure_adi: "Kehf", text: "وَرَاَ الْمُجْرِمُونَ النَّارَ فَظَنُّٓوا اَنَّهُمْ مُوَاقِعُوهَا وَلَمْ يَجِدُوا عَنْهَا مَصْرِفاً۟", translation: "Mücrimler ateşi görünce, ona düşeceklerini anlarlar. Ancak ondan bir kaçış yolu bulamazlar.", transliteration: "Ve reel mucrimunen nare fe zannu ennehum muvakıuha ve lem yecidu anha masrifa."},
  {id: 2194, sure_no: 54, ayet_no: 54, sure_adi: "Kehf", text: "وَلَقَدْ صَرَّفْنَا ف۪ي هٰذَا الْقُرْاٰنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍۜ وَكَانَ الْاِنْسَانُ اَكْثَرَ شَيْءٍ جَدَلاً", translation: "Ant olsun ki Biz, bu Kur'an'da her türlü örneği farklı farklı açıklamalarla verdik. Ne var ki insan bilir bilmez her şeye karşı çıkmayı çok sevmektedir.", transliteration: "Ve lekad sarrafna fi hazel kur'ani lin nasi min kulli mesel, ve kanel insanu eksere şey'in cedela."},
  {id: 2195, sure_no: 55, ayet_no: 55, sure_adi: "Kehf", text: "وَمَا مَنَعَ النَّاسَ اَنْ يُؤْمِنُٓوا اِذْ جَٓاءَهُمُ الْهُدٰى وَيَسْتَغْفِرُوا رَبَّهُمْ اِلَّٓا اَنْ تَأْتِيَهُمْ سُنَّةُ الْاَوَّل۪ينَ اَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلاً", translation: "Kendilerine doğru yolu gösterici geldiği zaman; insanları, Rabb'lerinin bağışlamasını dilemekten ve iman etmekten alıkoyan şey, kendilerinden öncekilerinin başına gelenlerin, kendi başlarına da gelmesini ya da azapla karşı karşıya gelmeyi beklemeleridir.", transliteration: "Ve ma menean nase en yu'minu iz cae humul huda ve yestagfiru rabbehum illa en te'tiyehum sunnetul evveline ev ye'tiyehumul azabu kubula."},
  {id: 2196, sure_no: 56, ayet_no: 56, sure_adi: "Kehf", text: "وَمَا نُرْسِلُ الْمُرْسَل۪ينَ اِلَّا مُبَشِّر۪ينَ وَمُنْذِر۪ينَۚ وَيُجَادِلُ الَّذ۪ينَ كَفَرُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ وَاتَّخَذُٓوا اٰيَات۪ي وَمَٓا اُنْذِرُوا هُزُواً", translation: "Biz, Resulleri ancak haberdar ediciler ve uyarıcılar olarak göndeririz. Kafirler ise Hakk'ı Batıl ile ortadan kaldırmak için mücadele ediyorlar. Ve onlar, ayetlerimizi ve uyarıldıkları şeyleri alaya alıyorlar.", transliteration: "Ve ma nursilul murseline illa mubeşşirine ve munzirin, ve yucadilullezine keferu bil batılı li yudhıdu bihil hakka vettehazu ayati ve ma unziru huzuva."},
  {id: 2197, sure_no: 57, ayet_no: 57, sure_adi: "Kehf", text: "وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيَاتِ رَبِّه۪ فَاَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُۜ اِنَّا جَعَلْنَا عَلٰى قُلُوبِهِمْ اَكِنَّةً اَنْ يَفْقَهُوهُ وَف۪ٓي اٰذَانِهِمْ وَقْراًۜ وَاِنْ تَدْعُهُمْ اِلَى الْهُدٰى فَلَنْ يَهْتَدُٓوا اِذاً اَبَداً", translation: "Rabb'inin ayetleriyle öğüt verildiği zaman onu dikkate almayan ve yapıp ettiklerini önemsemeyen kimseden daha zalim kim vardır? Biz, böylelerinin kalplerinin üzerine, gerçeği düşünüp kavramayı engelleyen bir örtü, kulaklarına da ağırlık koyduk. Sen onları doğruya yöneltmeye çalışsan da artık asla doğru yola dönmezler.", transliteration: "Ve men azlemu mimmen zukkire bi ayati rabbihi fe a'rada anha ve nesiye ma kaddemet yedah, inna cealna ala kulubihim ekinneten en yefkahuhu ve fi azanihim vakra ve in ted'uhum ilel huda fe len yehtedu izen ebeda."},
  {id: 2198, sure_no: 58, ayet_no: 58, sure_adi: "Kehf", text: "وَرَبُّكَ الْغَفُورُ ذُوالرَّحْمَةِۜ لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوا لَعَجَّلَ لَهُمُ الْعَذَابَۜ بَلْ لَهُمْ مَوْعِدٌ لَنْ يَجِدُوا مِنْ دُونِه۪ مَوْئِلاً", translation: "Bununla beraber, rahmet sahibi Rabb'in çok bağışlayıcıdır. Eğer O, yaptıkları yüzünden onları hemen hesaba çekseydi, kesinlikle onlara azabı hemen verirdi. Aksine onlar için belirlenmiş bir zaman vardır. Onlar, O'ndan başka sığınılacak bir yer asla bulamazlar.", transliteration: "Ve rabbukel gafuru zur rahmeh, lev yuahızuhum bi ma kesebu le accele lehumul azab, bel lehum mev'ıdun len yecidu min dunihi mev'ila."},
  {id: 2199, sure_no: 59, ayet_no: 59, sure_adi: "Kehf", text: "وَتِلْكَ الْقُرٰٓى اَهْلَكْنَاهُمْ لَمَّا ظَلَمُوا وَجَعَلْنَا لِمَهْلِكِهِمْ مَوْعِداً۟", translation: "İşte zulmettikleri zaman yok ettiğimiz beldeler. Biz onların yok edilmeleri için de bir zaman belirlemiştik.", transliteration: "Ve tilkel kura ehleknahum lemma zalemu ve cealna li mehlikihim mev'ıda."},
  {id: 2200, sure_no: 60, ayet_no: 60, sure_adi: "Kehf", text: "وَاِذْ قَالَ مُوسٰى لِفَتٰيهُ لَٓا اَبْرَحُ حَتّٰٓى اَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ اَوْ اَمْضِيَ حُقُباً", translation: "Bir zamanlar Musa, genç arkadaşına: \"İki denizin birleştiği yere ulaşıncaya kadar yoluma devam edeceğim veya uzun zaman gitmeye devam edeceğim.\" demişti.", transliteration: "Ve iz kale musa li fetahu la ebrehu hatta ebluga mecmeal bahreyni ev emdıye hukuba."},
  {id: 2201, sure_no: 61, ayet_no: 61, sure_adi: "Kehf", text: "فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوتَهُمَا فَاتَّخَذَ سَب۪يلَهُ فِي الْبَحْرِ سَرَباً", translation: "İkisinin arasının birleştiği yere ulaştıklarında hutlarını unuttular. O da denizin derinliklerine doğru kendi yolunu tuttu.", transliteration: "Fe lemma belega mecmea beynihima nesiya hutehuma fettehaze sebilehu fil bahri sereba."},
  {id: 2202, sure_no: 62, ayet_no: 62, sure_adi: "Kehf", text: "فَلَمَّا جَاوَزَا قَالَ لِفَتٰيهُ اٰتِنَا غَدَٓاءَنَاۘ لَقَدْ لَق۪ينَا مِنْ سَفَرِنَا هٰذَا نَصَباً", translation: "Epey bir mesafe aldıktan sonra, genç arkadaşına, \"Yolculuğumuz nedeniyle iyice yorulduk, haydi sabah yemeğimizi getir.\" dedi.", transliteration: "Fe lemma caveza kale li fetahu atina gadaena lekad lekina min seferina haza nasaba."},
  {id: 2203, sure_no: 63, ayet_no: 63, sure_adi: "Kehf", text: "قَالَ اَرَاَيْتَ اِذْ اَوَيْنَٓا اِلَى الصَّخْرَةِ فَاِنّ۪ي نَس۪يتُ الْحُوتَۘ وَمَٓا اَنْسَان۪يهُ اِلَّا الشَّيْطَانُ اَنْ اَذْكُرَهُۚ وَاتَّخَذَ سَب۪يلَهُ فِي الْبَحْرِۗ عَجَباً", translation: "\"Tüh! Şu işe bak! O kayanın yanında durduğumuzda ben hutu unuttum; şeytan hatırlamama engel oldu. O da şaşılacak bir şekilde denizde kendisine yol tuttu.\"", transliteration: "Kale eraeyte iz eveyna ilas sahrati fe inni nesitul hut, ve ma ensanihu illeş şeytanu en ezkureh, vettehaze sebilehu fil bahri aceba."},
  {id: 2204, sure_no: 64, ayet_no: 64, sure_adi: "Kehf", text: "قَالَ ذٰلِكَ مَا كُنَّا نَبْغِۗ فَارْتَدَّا عَلٰٓى اٰثَارِهِمَا قَصَصاًۙ", translation: "\"Hemen oraya dönmeliyiz\" dedi. Ve izlerini takip ederek geri döndüler.", transliteration: "Kale zalike ma kunna nebgı fertedda ala asarihima kasasa."},
  {id: 2205, sure_no: 65, ayet_no: 65, sure_adi: "Kehf", text: "فَوَجَدَا عَبْداً مِنْ عِبَادِنَٓا اٰتَيْنَاهُ رَحْمَةً مِنْ عِنْدِنَا وَعَلَّمْنَاهُ مِنْ لَدُنَّا عِلْماً", translation: "Derken katımızdan, kendisine bir rahmet verdiğimiz ve tarafımızdan bir ilim öğrettiğimiz Bizim kullarımızdan bir kul buldular.", transliteration: "Fe veceda abden min ibadina ateynahu rahmeten min indina ve allemnahu min ledunna ilma."},
  {id: 2206, sure_no: 66, ayet_no: 66, sure_adi: "Kehf", text: "قَالَ لَهُ مُوسٰى هَلْ اَتَّبِعُكَ عَلٰٓى اَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْداً", translation: "Musa ona: \"Sana öğretilen doğru yoldan gitme bilgisini bana da öğretmen için, sana tabi olabilir miyim?\" dedi.", transliteration: "Kale lehu musa hel ettebiuke ala en tuallimeni mimma ullimte ruşda."},
  {id: 2207, sure_no: 67, ayet_no: 67, sure_adi: "Kehf", text: "قَالَ اِنَّكَ لَنْ تَسْتَط۪يعَ مَعِيَ صَبْراً", translation: "\"Sen, benimle beraber olmada sabretmeye asla güç yetiremezsin.\"", transliteration: "Kale inneke len testetia maiye sabra."},
  {id: 2208, sure_no: 68, ayet_no: 68, sure_adi: "Kehf", text: "وَكَيْفَ تَصْبِرُ عَلٰى مَا لَمْ تُحِطْ بِه۪ خُبْراً", translation: "\"Haberdar edilerek iç yüzünü kavrayamadığın şeye nasıl sabredebilirsin?\"", transliteration: "Ve keyfe tesbiru ala ma lem tuhıt bihi hubra."},
  {id: 2209, sure_no: 69, ayet_no: 69, sure_adi: "Kehf", text: "قَالَ سَتَجِدُن۪ٓي اِنْ شَٓاءَ اللّٰهُ صَابِراً وَلَٓا اَعْص۪ي لَكَ اَمْراً", translation: "\"İnşaallah beni sabırlı bulacaksın ve senin hiçbir işine karşı çıkmayacağım.\" dedi.", transliteration: "Kale se teciduni inşaallahu sabiren ve la a'si leke emra."},
  {id: 2210, sure_no: 70, ayet_no: 70, sure_adi: "Kehf", text: "قَالَ فَاِنِ اتَّبَعْتَن۪ي فَلَا تَسْـَٔلْن۪ي عَنْ شَيْءٍ حَتّٰٓى اُحْدِثَ لَكَ مِنْهُ ذِكْراً۟", translation: "\"O halde, eğer bana uyacaksan, hakkında bir açıklama yapıncaya kadar bana hiçbir şey hakkında soru sorma.\" dedi.", transliteration: "Kale fe initteba'teni fe la tes'elni an şey'in hatta uhdise leke minhu zikra."},
  {id: 2211, sure_no: 71, ayet_no: 71, sure_adi: "Kehf", text: "فَانْطَلَقَا۠ حَتّٰٓى اِذَا رَكِبَا فِي السَّف۪ينَةِ خَرَقَهَاۜ قَالَ اَخَرَقْتَهَا لِتُغْرِقَ اَهْلَهَاۚ لَقَدْ جِئْتَ شَيْـٔاً اِمْراً", translation: "Bunun üzerine yürüdüler. Nihayet bir gemiye bindiklerinde onu deldi. Musa: \"İçindekilerini boğmak için mi onu deldin? Gerçekten sen şaşılacak bir iş yaptın.\" dedi.", transliteration: "Fentalaka, hatta iza rakiba fis sefineti harakaha kale e harakteha li tugrika ehleha, lekad ci'te şey'en imra."},
  {id: 2212, sure_no: 72, ayet_no: 72, sure_adi: "Kehf", text: "قَالَ اَلَمْ اَقُلْ اِنَّكَ لَنْ تَسْتَط۪يعَ مَعِيَ صَبْراً", translation: "\"Gerçek şu ki sen benimle birlikte olmada sabretmeye asla güç yetiremezsin dememiş miydim?\"", transliteration: "Kale e lem ekul inneke len testetia maiye sabra."},
  {id: 2213, sure_no: 73, ayet_no: 73, sure_adi: "Kehf", text: "قَالَ لَا تُؤَاخِذْن۪ي بِمَا نَس۪يتُ وَلَا تُرْهِقْن۪ي مِنْ اَمْر۪ي عُسْراً", translation: "\"Unutmuş olmamdan dolayı beni kınama ve bu işimden dolayı güçlük çıkarma.\" dedi.", transliteration: "Kale la tuahızni bima nesitu ve la turhıkni min emri usra."},
  {id: 2214, sure_no: 74, ayet_no: 74, sure_adi: "Kehf", text: "فَانْطَلَقَا۠ حَتّٰٓى اِذَا لَقِيَا غُلَاماً فَقَتَلَهُۙ قَالَ اَقَتَلْتَ نَفْساً زَكِيَّةً بِغَيْرِ نَفْسٍۜ لَقَدْ جِئْتَ شَيْـٔاً نُكْراً", translation: "Tekrar yola koyuldular. Nihayet bir delikanlıya rastladılar. Onu öldürdü. \"Bir cana karşılık olmaksızın zekiyye bir canı öldürdün mü? Gerçekten dehşet verici bir şey yaptın!\"", transliteration: "Fentaleka, hatta iza lekıya gulamen fe katelehu kale e katelte nefsen zekiyyeten bi gayri nefs, lekad ci'te şey'en nukra."},
  {id: 2215, sure_no: 75, ayet_no: 75, sure_adi: "Kehf", text: "قَالَ اَلَمْ اَقُلْ لَكَ اِنَّكَ لَنْ تَسْتَط۪يعَ مَعِيَ صَبْراً", translation: "Sana, \"Sen, kesinlikle benimle birlikte olmaya sabretmeye asla güç yetiremezsin, demedim mi?\" dedi.", transliteration: "Kale e lem ekul leke inneke len testetia maıye sabra."},
  {id: 2216, sure_no: 76, ayet_no: 76, sure_adi: "Kehf", text: "قَالَ اِنْ سَاَلْتُكَ عَنْ شَيْءٍ بَعْدَهَا فَلَا تُصَاحِبْن۪يۚ قَدْ بَلَغْتَ مِنْ لَدُنّ۪ي عُذْراً", translation: "\"Eğer bir daha sana bir şey sorarsam, artık benimle arkadaşlık etmezsin! Tarafımdan yeterince özür dilemede bulundum.\"", transliteration: "Kale in seeltuke an şey'in ba'deha fe la tusahıbni, kad belagte min ledunni uzra."},
  {id: 2217, sure_no: 77, ayet_no: 77, sure_adi: "Kehf", text: "فَانْطَلَقَا۠ حَتّٰٓى اِذَٓا اَتَيَٓا اَهْلَ قَرْيَةٍۨ اسْتَطْعَمَٓا اَهْلَهَا فَاَبَوْا اَنْ يُضَيِّفُوهُمَا فَوَجَدَا ف۪يهَا جِدَاراً يُر۪يدُ اَنْ يَنْقَضَّ فَاَقَامَهُۜ قَالَ لَوْ شِئْتَ لَتَّخَذْتَ عَلَيْهِ اَجْراً", translation: "Yeniden yola koyuldular. Bir kasabaya varınca, karşılaştıkları halktan yiyecek istediler. Ne var ki onlar, kendilerini misafir etmekten kaçındılar. Derken orada yıkılmak üzere olan bir duvar buldular. Hemen onu düzeltti. Musa: \"Eğer isteseydin elbette bunun için bir ücret alırdın.\" dedi.", transliteration: "Fentaleka, hatta iza eteya ehle karyetin istat'ama ehleha fe ebev en yudayyifuhuma fe veceda fiha cidaren yuridu en yenkadda fe ekameh, kale lev şi'te lettehazte aleyhi ecra."},
  {id: 2218, sure_no: 78, ayet_no: 78, sure_adi: "Kehf", text: "قَالَ هٰذَا فِرَاقُ بَيْن۪ي وَبَيْنِكَۚ سَاُنَبِّئُكَ بِتَأْو۪يلِ مَا لَمْ تَسْتَطِـعْ عَلَيْهِ صَبْراً", translation: "\"İşte bu yollarımızın ayrılma vaktidir. Sabırlı olmaya güç yetiremediğin şeylerin içyüzünü sana açıklayacağım.\" dedi.", transliteration: "Kale haza firaku beyni ve beynik, se unebbiuke bi te'vili ma lem testetı' aleyhi sabra."},
  {id: 2219, sure_no: 79, ayet_no: 79, sure_adi: "Kehf", text: "اَمَّا السَّف۪ينَةُ فَكَانَتْ لِمَسَاك۪ينَ يَعْمَلُونَ فِي الْبَحْرِ فَاَرَدْتُ اَنْ اَع۪يبَهَا وَكَانَ وَرَٓاءَهُمْ مَلِكٌ يَأْخُذُ كُلَّ سَف۪ينَةٍ غَصْباً", translation: "\"O gemi, geçimini denizden sağlayan yoksul kimselere aitti. Gemiyi hasarlı göstermek istedim, zira onların ilerisinde bütün gemileri gasp eden bir hükümdar vardı.\"", transliteration: "Emmes sefinetu fe kanet li mesakine ya'melune fil bahri fe eradtu en eibeha ve kane veraehum melikun ye'huzu kulle sefinetin gasba."},
  {id: 2220, sure_no: 80, ayet_no: 80, sure_adi: "Kehf", text: "وَاَمَّا الْغُلَامُ فَكَانَ اَبَوَاهُ مُؤْمِنَيْنِ فَخَش۪ينَٓا اَنْ يُرْهِقَهُمَا طُغْيَاناً وَكُفْراًۚ", translation: "\"Delikanlıya gelince, anne ve babası Mü'min kimselerdi. Onları azgınlığa ve Küfre sürüklemesine rızamız olmadı.\"", transliteration: "Ve emmel gulamu fe kane ebevahu mu'mineyni fe haşina en yurhikahuma tugyanen ve kufra."},
  {id: 2221, sure_no: 81, ayet_no: 81, sure_adi: "Kehf", text: "فَاَرَدْنَٓا اَنْ يُبْدِلَهُمَا رَبُّهُمَا خَيْراً مِنْهُ زَكٰوةً وَاَقْرَبَ رُحْماً", translation: "\"Böylece onların Rabb'lerinin, onu, ondan daha hayırlı, daha zekat ve merhamete daha yakın olanla değiştirmesini istedik.\"", transliteration: "Fe eredna en yubdilehuma rabbuhuma hayren minhu zekaten ve akrebe ruhma."},
  {id: 2222, sure_no: 82, ayet_no: 82, sure_adi: "Kehf", text: "وَاَمَّا الْجِدَارُ فَكَانَ لِغُلَامَيْنِ يَت۪يمَيْنِ فِي الْمَد۪ينَةِ وَكَانَ تَحْتَهُ كَنْزٌ لَهُمَا وَكَانَ اَبُوهُمَا صَالِحاًۚ فَاَرَادَ رَبُّكَ اَنْ يَبْلُغَٓا اَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَاۗ رَحْمَةً مِنْ رَبِّكَۚ وَمَا فَعَلْتُهُ عَنْ اَمْر۪يۜ ذٰلِكَ تَأْو۪يلُ مَا لَمْ تَسْطِـعْ عَلَيْهِ صَبْراًۜ۟", translation: "\"Duvar ise o şehirde iki yetim gence aitti. Ve onun altında, onlara ait bir servet vardı. Babaları iyi bir kimseydi. İşte onun için Rabb'in, onların erginlik dönemine erişmesini ve - Rabb'lerinden bir rahmet olarak- serveti çıkarmalarını istedi. Ve ben onu kendiliğimden bir iş olarak yapmadım. İşte senin sabretmeye güç yetiremediğin şeylerin açıklaması budur.\"", transliteration: "Ve emmel cidaru fe kane li gulameyni yetimeyni fil medineti ve kane tahtehu kenzun lehuma ve kane ebuhuma saliha, fe erade rabbuke en yebluga eşuddehuma ve yestahrica kenzehuma rahmeten min rabbik ve ma fealtuhu an emri, zalike te'vilu ma lem testı' aleyhi sabra."},
  {id: 2223, sure_no: 83, ayet_no: 83, sure_adi: "Kehf", text: "وَيَسْـَٔلُونَكَ عَنْ ذِي الْقَرْنَيْنِۜ قُلْ سَاَتْلُوا عَلَيْكُمْ مِنْهُ ذِكْراًۜ", translation: "Sana, Zu'l-Karneyn'den soruyorlar. De ki: \"Size ondan bir konu anlatacağım.\"", transliteration: "Ve yes'eluneke an zil karneyn, kul se etlu aleykum minhu zikra."},
  {id: 2224, sure_no: 84, ayet_no: 84, sure_adi: "Kehf", text: "اِنَّا مَكَّنَّا لَهُ فِي الْاَرْضِ وَاٰتَيْنَاهُ مِنْ كُلِّ شَيْءٍ سَبَباًۙ", translation: "Doğrusu Biz, onu yeryüzünde güçlendirdik ve ona her şeyden bir sebep verdik.", transliteration: "İnna mekkenna lehu fil ardı ve ateynahu min kulli şey'in sebeba."},
  {id: 2225, sure_no: 85, ayet_no: 85, sure_adi: "Kehf", text: "فَاَتْبَعَ سَبَباً", translation: "Böylece bir sebebe tabi oldu.", transliteration: "Fe etbea sebeba."},
  {id: 2226, sure_no: 86, ayet_no: 86, sure_adi: "Kehf", text: "حَتّٰٓى اِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ ف۪ي عَيْنٍ حَمِئَةٍ وَوَجَدَ عِنْدَهَا قَوْماًۜ قُلْنَا يَا ذَا الْقَرْنَيْنِ اِمَّٓا اَنْ تُعَذِّبَ وَاِمَّٓا اَنْ تَتَّخِذَ ف۪يهِمْ حُسْناً", translation: "Nihayet o, Güneş'in battığı yere vardığı zaman, onu koyu bir suda batıyor buldu. Bir de bunun yanında bir halkla karşılaştı. \"Ey Zu'l-Karneyn! Dilersen onları cezalandırırsın, dilersen onlara iyilik edersin.\" dedik.", transliteration: "Hatta iza belega magribeş şemsi vecedeha tagrubu fi aynin hamietin ve vecede indeha kavma, kulna ya zel karneyni imma en tuazzibe ve imma en tettehıze fihim husna."},
  {id: 2227, sure_no: 87, ayet_no: 87, sure_adi: "Kehf", text: "قَالَ اَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهُ ثُمَّ يُرَدُّ اِلٰى رَبِّه۪ فَيُعَذِّبُهُ عَذَاباً نُكْراً", translation: "Zu'l-Karneyn: \"Kim zulmederse ona azap edeceğiz. Sonra Rabb'ine döndürülür. Böylece ona daha şiddetli bir azapla azap edilir.\" dedi.", transliteration: "Kale emma men zaleme fe sevfe nuazzibuhu summe yureddu ila rabbihi fe yuazzibuhu azaben nukra."},
  {id: 2228, sure_no: 88, ayet_no: 88, sure_adi: "Kehf", text: "وَاَمَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحاً فَلَهُ جَزَٓاءًۨ الْحُسْنٰىۚ وَسَنَقُولُ لَهُ مِنْ اَمْرِنَا يُسْراًۜ", translation: "\"Kim iman eder ve salihatı yaparsa, en iyi karşılık onundur. Biz, ona her türlü kolaylığı göstereceğiz.\"", transliteration: "Ve emma men amene ve amile salihan fe lehu cezaenil husna ve se nekulu lehu min emrina yusra."},
  {id: 2229, sure_no: 89, ayet_no: 89, sure_adi: "Kehf", text: "ثُمَّ اَتْـبَعَ سَبَباً", translation: "Sonra bir sebebe tabi oldu.", transliteration: "Summe etbea sebeba."},
  {id: 2230, sure_no: 90, ayet_no: 90, sure_adi: "Kehf", text: "حَتّٰٓى اِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلٰى قَوْمٍ لَمْ نَجْعَلْ لَهُمْ مِنْ دُونِهَا سِتْراًۙ", translation: "Nihayet Güneş'in doğduğu yere vardığı zaman, onu, kendilerini Güneş'e karşı koruyacak bir örtü yapmadığımız bir halkın üzerine doğarken buldu.", transliteration: "Hatta iza belega matlıaş şemsi vecedeha tatluu ala kavmin lem nec'al lehum min duniha sitra."},
  {id: 2231, sure_no: 91, ayet_no: 91, sure_adi: "Kehf", text: "كَذٰلِكَۜ وَقَدْ اَحَطْنَا بِمَا لَدَيْهِ خُبْراً", translation: "İşte böyle! Biz, onun yanında olan şeyleri bilgimizle kuşatmıştık.", transliteration: "Kezalik, ve kad ehatna bima ledeyhi hubra."},
  {id: 2232, sure_no: 92, ayet_no: 92, sure_adi: "Kehf", text: "ثُمَّ اَتْبَعَ سَبَباً", translation: "Sonra bir sebebe tabi oldu.", transliteration: "Summe etbea sebeba."},
  {id: 2233, sure_no: 93, ayet_no: 93, sure_adi: "Kehf", text: "حَتّٰٓى اِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُونِهِمَا قَوْماًۙ لَا يَكَادُونَ يَفْقَهُونَ قَوْلاً", translation: "Nihayet iki set arasına ulaştığı zaman, onların yanı başında neredeyse hiç söz anlamayan bir halkla karşılaştı.", transliteration: "Hatta iza belega beynes seddeyni vecede min dunihima kavmen la yekadune yefkahune kavla."},
  {id: 2234, sure_no: 94, ayet_no: 94, sure_adi: "Kehf", text: "قَالُوا يَا ذَا الْقَرْنَيْنِ اِنَّ يَأْجُوجَ وَمَأْجُوجَ مُفْسِدُونَ فِي الْاَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجاً عَلٰٓى اَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَداًّ", translation: "\"Ey Zu'l-Karneyn! Şüphesiz Ye'cuc ve Me'cuc yeryüzünde fesat çıkaranlardır. Onun için, onlarla bizim aramıza ücreti mukabilinde bir set yap, olmaz mı?\" dediler.", transliteration: "Kalu ya zel karneyni inne ye'cuce ve me'cuce mufsidune fil ardı fe hel nec'alu leke harcen ala en tec'ale beynena ve beynehum sedda."},
  {id: 2235, sure_no: 95, ayet_no: 95, sure_adi: "Kehf", text: "قَالَ مَا مَكَّنّ۪ي ف۪يهِ رَبّ۪ي خَيْرٌ فَاَع۪ينُون۪ي بِقُوَّةٍ اَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْماًۙ", translation: "\"Rabb'imin, beni içinde bulundurduğu imkanlar daha hayırlıdır.\" dedi. \"Şimdi bana güç verin de sizinle onların arasına çok sağlam bir engel yapayım.\" dedi.", transliteration: "Kale ma mekkenni fihi rabbi hayrun fe einuni bi kuvvetin ec'al beynekum ve beynehum redma."},
  {id: 2236, sure_no: 96, ayet_no: 96, sure_adi: "Kehf", text: "اٰتُون۪ي زُبَرَ الْحَد۪يدِۜ حَتّٰٓى اِذَا سَاوٰى بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُواۜ حَتّٰٓى اِذَا جَعَلَهُ نَاراًۙ قَالَ اٰتُون۪ٓي اُفْرِ غْ عَلَيْهِ قِطْراًۜ", translation: "\"Bana demir parçaları getirin. İki dağın arası eşit seviyeye gelinceye kadar körükleyin.\" dedi. Onu bir ateş haline getirince, \"Bana erimiş bakır getirin, onun üzerine dökeceğim.\" dedi.", transliteration: "Atuni zuberel hadid, hatta iza sava beynes sadafeyni kalenfuhu, hatta iza cealehu naren kale atuni ufrig aleyhi kıtra."},
  {id: 2237, sure_no: 97, ayet_no: 97, sure_adi: "Kehf", text: "فَمَا اسْطَاعُٓوا اَنْ يَظْهَرُوهُ وَمَا اسْتَطَاعُوا لَهُ نَقْباً", translation: "Artık onu aşmaya ve yarıp geçmeye güç yetiremediler.", transliteration: "Femestau en yazheruhu ve mestetau lehu nakba."},
  {id: 2238, sure_no: 98, ayet_no: 98, sure_adi: "Kehf", text: "قَالَ هٰذَا رَحْمَةٌ مِنْ رَبّ۪يۚ فَاِذَا جَٓاءَ وَعْدُ رَبّ۪ي جَعَلَهُ دَكَّٓاءَۚ وَكَانَ وَعْدُ رَبّ۪ي حَقاًّۜ", translation: "\"Bu Rabb'imden bir rahmettir. Ama Rabb'imin uyarısı gerçekleştiği zaman, onu yerle bir eder. Ve Rabb'imin uyarısı gerçektir.\" dedi.", transliteration: "Kale haza rahmetun min rabbi, fe iza cae va'du rabbi cealehu dekka', ve kane va'du rabbi hakka."},
  {id: 2239, sure_no: 99, ayet_no: 99, sure_adi: "Kehf", text: "وَتَرَكْنَا بَعْضَهُمْ يَوْمَئِذٍ يَمُوجُ ف۪ي بَعْضٍ وَنُفِخَ فِي الصُّورِ فَجَمَعْنَاهُمْ جَمْعاًۙ", translation: "O Gün onları terk ederiz. Dalga dalga birbirlerine karışırlar. Ve Sur'a üfürülür. Hepsini bir araya toplarız.", transliteration: "Ve terakna ba'dahum yevmeizin yemucu fi ba'dın ve nufiha fis suri fe cema'nahum cem'a."},
  {id: 2240, sure_no: 100, ayet_no: 100, sure_adi: "Kehf", text: "وَعَرَضْنَا جَهَنَّمَ يَوْمَئِذٍ لِلْكَافِر۪ينَ عَرْضاًۙ", translation: "O Gün Cehennem'i Kafirlere sunarız, tam bir sunuşla.", transliteration: "Ve aradna cehenneme yevmeizin lil kafirine arda."},
  {id: 2241, sure_no: 101, ayet_no: 101, sure_adi: "Kehf", text: "اَلَّذ۪ينَ كَانَتْ اَعْيُنُهُمْ ف۪ي غِطَٓاءٍ عَنْ ذِكْر۪ي وَكَانُوا لَا يَسْتَط۪يعُونَ سَمْعاً۟", translation: "Ki onlar, bizim zikrimize karşı gözleri kapalı ve işitmeye tahammülü olmayanlardır.", transliteration: "Ellezine kanet a'yunuhum fi gıtain an zikri ve kanu la yestetiune sem'a."},
  {id: 2242, sure_no: 102, ayet_no: 102, sure_adi: "Kehf", text: "اَفَحَسِبَ الَّذ۪ينَ كَفَرُٓوا اَنْ يَتَّخِذُوا عِبَاد۪ي مِنْ دُون۪ٓي اَوْلِيَٓاءَۜ اِنَّٓا اَعْتَدْنَا جَهَنَّمَ لِلْكَافِر۪ينَ نُزُلاً", translation: "Yoksa Kafirler, Benim yanım sıra evliya edinebileceklerini mi sandılar? Biz, Cehennem'i Kafirlere bir ikram olarak hazırladık.", transliteration: "E fe hasibellezine keferu en yettehızu ibadi min duni evliya', inna a'tedna cehenneme lil kafirine nuzula."},
  {id: 2243, sure_no: 103, ayet_no: 103, sure_adi: "Kehf", text: "قُلْ هَلْ نُنَبِّئُكُمْ بِالْاَخْسَر۪ينَ اَعْمَالاًۜ", translation: "De ki: \"Size, yaptıklarından dolayı en çok kayba uğrayanları haber verelim mi?\"", transliteration: "Kul hel nunebbiukum bil ahserine a'mala."},
  {id: 2244, sure_no: 104, ayet_no: 104, sure_adi: "Kehf", text: "اَلَّذ۪ينَ ضَلَّ سَعْيُهُمْ فِي الْحَيٰوةِ الدُّنْيَا وَهُمْ يَحْسَبُونَ اَنَّهُمْ يُحْسِنُونَ صُنْعاً", translation: "\"Onlar, dünya hayatında iyi işler yaptıklarını sanırlarken, yaptıkları boşa gitmiş olan kimselerdir.\"", transliteration: "Ellezine dalle sa'yuhum fil hayatid dunya ve hum yahsebune ennehum yuhsinune sun'a."},
  {id: 2245, sure_no: 105, ayet_no: 105, sure_adi: "Kehf", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ كَفَرُوا بِاٰيَاتِ رَبِّهِمْ وَلِقَٓائِه۪ فَحَبِطَتْ اَعْمَالُهُمْ فَلَا نُق۪يمُ لَهُمْ يَوْمَ الْقِيٰمَةِ وَزْناً", translation: "İşte onlar, Rabb'lerinin ayetlerini ve O'na kavuşmayı inkar edenlerdir. Bu nedenle onların bütün yaptıkları boşa gitmiştir. Artık kıyamet günü onlara hiçbir değer vermeyiz.", transliteration: "Ulaikellezine keferu bi ayati rabbihim ve likaihi fe habitat a'maluhum fe la nukimu lehum yevmel kıyameti vezna."},
  {id: 2246, sure_no: 106, ayet_no: 106, sure_adi: "Kehf", text: "ذٰلِكَ جَزَٓاؤُ۬هُمْ جَهَنَّمُ بِمَا كَفَرُوا وَاتَّخَذُٓوا اٰيَات۪ي وَرُسُل۪ي هُزُواً", translation: "Küfretmeleri, ayetlerimi ve Resullerimi alaya almaları nedeniyle onların cezaları Cehennem'dir.", transliteration: "Zalike cezauhum cehennemu bima keferu vettehazu ayati ve rusuli huzuva."},
  {id: 2247, sure_no: 107, ayet_no: 107, sure_adi: "Kehf", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَانَتْ لَهُمْ جَنَّاتُ الْفِرْدَوْسِ نُزُلاًۙ", translation: "İman edip, salihatı yapanların ikramı Firdevs Cennetleridir.", transliteration: "İnnellezine amenu ve amilus salihati kanet lehum cennatul firdevsi nuzula."},
  {id: 2248, sure_no: 108, ayet_no: 108, sure_adi: "Kehf", text: "خَالِد۪ينَ ف۪يهَا لَا يَبْغُونَ عَنْهَا حِوَلاً", translation: "Orada devamlı kalırlar. Asla ayrılmak istemezler.", transliteration: "Halidine fiha la yebgune anha hıvela."},
  {id: 2249, sure_no: 109, ayet_no: 109, sure_adi: "Kehf", text: "قُلْ لَوْ كَانَ الْبَحْرُ مِدَاداً لِكَلِمَاتِ رَبّ۪ي لَنَفِدَ الْبَحْرُ قَبْلَ اَنْ تَنْفَدَ كَلِمَاتُ رَبّ۪ي وَلَوْ جِئْنَا بِمِثْلِه۪ مَدَداً", translation: "De ki: \"Rabb'imin kelimeleri için denizler ve bir o kadar daha deniz mürekkep olsa; Rabb'imin kelimeleri bitmeden denizler biterdi.", transliteration: "Kul lev kanel bahru midaden li kelimati rabbi le nefidel bahru kable en tenfede kelimatu rabbi ve lev ci'na bi mislihi mededa."},
  {id: 2250, sure_no: 110, ayet_no: 110, sure_adi: "Kehf", text: "قُلْ اِنَّـمَٓا اَنَا۬ بَشَرٌ مِثْلُكُمْ يُوحٰٓى اِلَيَّ اَنَّـمَٓا اِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ فَمَنْ كَانَ يَرْجُوا لِقَٓاءَ رَبِّه۪ فَلْيَعْمَلْ عَمَلاً صَالِحاً وَلَا يُشْرِكْ بِعِبَادَةِ رَبِّه۪ٓ اَحَداً", translation: "De ki: \"Ben de ancak sizin gibi bir beşerim. Sizden farkım; bana, ilahınızın ancak tek ilah olduğu vahyedilmiş olmasıdır. Onun için her kim Rabb'ine kavuşmayı umuyorsa, salihatı yapsın ve Rabb'ine kullukta hiç kimseyi ortak koşmasın.\"", transliteration: "Kul innema ene beşerun mislukum yuha ileyye ennema ilahukum ilahun vahid, fe men kane yercu likae rabbihi fel ya'mel amelen salihan ve la yuşrik bi ıbadeti rabbihi ehada."},
  {id: 2251, sure_no: 1, ayet_no: 1, sure_adi: "Meryem", text: "كٓـهٰيٰعٓصٓۜ", translation: "Kaf, Ha, Ya, Ayn, Sad.", transliteration: "Kaf, ha, ya, ayn, sad."},
  {id: 2252, sure_no: 2, ayet_no: 2, sure_adi: "Meryem", text: "ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهُ زَكَرِيَّاۚ", translation: "Rabb'inin, kulu Zekeriya'ya rahmetinin zikridir.", transliteration: "Zikru rahmeti rabbike abdehu zekeriyya."},
  {id: 2253, sure_no: 3, ayet_no: 3, sure_adi: "Meryem", text: "اِذْ نَادٰى رَبَّهُ نِدَٓاءً خَفِياًّ", translation: "O, içten bir seslenişle Rabb'ine seslenmişti.", transliteration: "İz nada rabbehu nidaen hafiyya."},
  {id: 2254, sure_no: 4, ayet_no: 4, sure_adi: "Meryem", text: "قَالَ رَبِّ اِنّ۪ي وَهَنَ الْعَظْمُ مِنّ۪ي وَاشْتَعَلَ الرَّأْسُ شَيْباً وَلَمْ اَكُنْ بِدُعَٓائِكَ رَبِّ شَقِياًّ", translation: "\"Rabb'im! Gerçekten ben iyice güçten kesildim. Saçım başım ağardı. Ve Rabb'im, Sana ettiğim dualarım hiç karşılıksız kalmadı.\"", transliteration: "Kale rabbi inni ve henel azmu minni veştealer re'su şeyben ve lem ekun bi duaike rabbi şakıyya."},
  {id: 2255, sure_no: 5, ayet_no: 5, sure_adi: "Meryem", text: "وَاِنّ۪ي خِفْتُ الْمَوَالِيَ مِنْ وَرَٓاء۪ي وَكَانَتِ امْرَاَت۪ي عَاقِراً فَهَبْ ل۪ي مِنْ لَدُنْكَ وَلِياًّۚ", translation: "\"Gerçekten, benden sonra yerime geçeceklerden çok endişeliyim. Hanımım da kısır. Onun için bana katından bir veli bağışla.\"", transliteration: "Ve inni hıftul mevaliye min verai ve kanetimreeti akıran feheb li min ledunke veliyya."},
  {id: 2256, sure_no: 6, ayet_no: 6, sure_adi: "Meryem", text: "يَرِثُن۪ي وَيَرِثُ مِنْ اٰلِ يَعْقُوبَۗ وَاجْعَلْهُ رَبِّ رَضِياًّ", translation: "\"Bana ve Ya'kub soyuna mirasçı olsun. Rabb'im, onu hoşnut olunan kıl.\"", transliteration: "Yerisuni ve yerisu min ali ya'kube vec'alhu rabbi radıyya."},
  {id: 2257, sure_no: 7, ayet_no: 7, sure_adi: "Meryem", text: "يَا زَكَرِيَّٓا اِنَّـا نُـبَشِّرُكَ بِغُـلَامٍۨ اسْـمُهُ يَحْيٰىۙ لَمْ نَجْعَلْ لَهُ مِنْ قَبْلُ سَمِياًّ", translation: "\"Ey Zekeriya! Biz, sana ismi Yahya olan ve daha önce dengi görülmemiş bir oğul müjdeliyoruz.\"", transliteration: "Ya zekeriyya inna nubeşşiruke bi gulaminismuhu yahya lem nec'al lehu min kablu semiyya."},
  {id: 2258, sure_no: 8, ayet_no: 8, sure_adi: "Meryem", text: "قَالَ رَبِّ اَنّٰى يَكُونُ ل۪ي غُلَامٌ وَكَانَتِ امْرَاَت۪ي عَاقِراً وَقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِياًّ", translation: "\"Rabb'im! Hanımım kısır, ben de ayakta duramayacak kadar yaşlanmışken, benim nasıl bir oğlum olabilir?\" dedi.", transliteration: "Kale rabbi enna yekunu li gulamun ve kanetimreeti akıran ve kad belagtu minel kiberi ıtiyya."},
  {id: 2259, sure_no: 9, ayet_no: 9, sure_adi: "Meryem", text: "قَالَ كَذٰلِكَۚ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْـٔاً", translation: "aklısın, ne var ki senin Rabb'in böyle olmasını buyurdu.\" Ve \"Bu Benim için kolaydır. Bundan önce de seni, sen hiçbir şey değilken yarattım.\" dedi.", transliteration: "Kale kezalik, kale rabbuke huve aleyye heyyinun ve kad halaktuke min kablu ve lem teku şey'a."},
  {id: 2260, sure_no: 10, ayet_no: 10, sure_adi: "Meryem", text: "قَالَ رَبِّ اجْعَلْ ل۪ٓي اٰيَةًۜ  قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَ لَيَالٍ سَوِياًّ", translation: "\"Rabb'im! Bana bir ayet ver!\" dedi. \"Senin ayetin, sapasağlam olduğun halde aralıksız üç gece insanlarla konuşmayacak olmandır.\" buyurdu.", transliteration: "Kale rabbic'al li ayeh, kale ayetuke ella tukellimen nase selase leyalin seviyya."},
  {id: 2261, sure_no: 11, ayet_no: 11, sure_adi: "Meryem", text: "فَخَرَجَ عَلٰى قَوْمِه۪ مِنَ الْمِحْرَابِ فَاَوْحٰٓى اِلَيْهِمْ اَنْ سَبِّحُوا بُكْرَةً وَعَشِياًّ", translation: "Bunun üzerine mihraptan halkının karşısına çıktı. Onlara, sabah akşam tesbih etmelerini vahyetti.", transliteration: "Fe harece ala kavmihi minel mihrabi fe evha ileyhim en sebbihu bukreten ve aşiyya."},
  {id: 2262, sure_no: 12, ayet_no: 12, sure_adi: "Meryem", text: "يَا يَحْيٰى خُذِ الْكِتَابَ بِقُوَّةٍۜ وَاٰتَيْنَاهُ الْحُكْمَ صَبِياًّۙ", translation: "\"Ey Yahya! Kitap'ı kuvvetlice tut.\" O henüz çocukken ona hikmet verdik.", transliteration: "Ya yahya huzil kitabe bi kuvveh, ve ateynahul hukme sabiyya."},
  {id: 2263, sure_no: 13, ayet_no: 13, sure_adi: "Meryem", text: "وَحَنَاناً مِنْ لَدُنَّا وَزَكٰوةًۜ وَكَانَ تَقِياًّۙ", translation: "Tarafımızdan yumuşak kalplilik ve zekat verdik. Ve o, takva sahibi oldu.", transliteration: "Ve hananen min ledunna ve zekah, ve kane tekıyya."},
  {id: 2264, sure_no: 14, ayet_no: 14, sure_adi: "Meryem", text: "وَبَراًّ بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّاراً عَصِياًّ", translation: "Anne ve babasına karşı birr sahibiydi. Karşı çıkan ve asilik eden biri değildi.", transliteration: "Ve berren bi valideyhi ve lem yekun cebbaren asıyya."},
  {id: 2265, sure_no: 15, ayet_no: 15, sure_adi: "Meryem", text: "وَسَلَامٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوتُ وَيَوْمَ يُبْعَثُ حَياًّ۟", translation: "Doğduğu gün, öleceği gün ve canlı olarak yeniden diriltileceği gün onun üzerine selam olsun.", transliteration: "Ve selamun aleyhi yevme vulide ve yevme yemutu ve yevme yub'asu hayya."},
  {id: 2266, sure_no: 16, ayet_no: 16, sure_adi: "Meryem", text: "وَاذْكُرْ فِي الْكِتَابِ مَرْيَمَۢ اِذِ انْتَبَذَتْ مِنْ اَهْلِهَا مَكَاناً شَرْقِياًّۙ", translation: "Kitap'ta Meryem'i de an! Hani o, ailesinden ayrılarak, doğu tarafında bir yere çekilmişti.", transliteration: "Vezkur fil kitabı meryem, izintebezet min ehliha mekanen şarkıyya."},
  {id: 2267, sure_no: 17, ayet_no: 17, sure_adi: "Meryem", text: "فَاتَّخَذَتْ مِنْ دُونِهِمْ حِجَاباً فَاَرْسَلْـنَٓا اِلَيْهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَراً سَوِياًّ", translation: "Sonra ailesi ile arasına bir perde çekti. O zaman, ona ruhumuzu gönderdik. Ona normal bir beşer yapısında temessül etti.", transliteration: "Fettehazet min dunihim hicaben fe erselna ileyha ruhana fe temessele leha beşeren seviyya."},
  {id: 2268, sure_no: 18, ayet_no: 18, sure_adi: "Meryem", text: "قَالَتْ اِنّ۪ٓي اَعُوذُ بِالرَّحْمٰنِ مِنْكَ اِنْ كُنْتَ تَقِياًّ", translation: "\"Senden Rahman'a sığınırım. Eğer takva sahibi isen.\" dedi.", transliteration: "Kalet inni euzu bir rahmani minke in kunte tekıyya."},
  {id: 2269, sure_no: 19, ayet_no: 19, sure_adi: "Meryem", text: "قَالَ اِنَّـمَٓا اَنَا۬ رَسُولُ رَبِّكِۗ لِاَهَبَ لَكِ غُلَاماً زَكِياًّ", translation: "\"Ben sadece \"Rabb'inin elçisiyim.\" dedi. \"Sana tertemiz bir oğul armağan etmek için geldim.\"", transliteration: "Kale innema ene resulu rabbiki li ehebe leki gulamen zekiyya."},
  {id: 2270, sure_no: 20, ayet_no: 20, sure_adi: "Meryem", text: "قَالَتْ اَنّٰى يَكُونُ ل۪ي غُلَامٌ وَلَمْ يَمْسَسْن۪ي بَشَرٌ وَلَمْ اَكُ بَغِياًّ", translation: "\"Benim nasıl bir oğlum olabilir? Bana kesinlikle bir beşer dokunmamışken. Ve ben kesinlikle iffetsiz değilim.\" dedi.", transliteration: "Kalet enna yekunu li gulamun ve lem yemsesni beşerun ve lem eku bagıyya."},
  {id: 2271, sure_no: 21, ayet_no: 21, sure_adi: "Meryem", text: "قَالَ كَذٰلِكِۚ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌۚ وَلِنَجْعَلَـهُٓ اٰيَةً لِلنَّاسِ وَرَحْمَةً مِنَّاۚ وَكَانَ اَمْراً مَقْضِياًّ", translation: "\"İşte böyle.\" dedi. Rabb'in: \"O Bana kolaydır. Onu, insanlara bir ayet ve Bizden bir rahmet kılacağız.\" dedi.\" Bunun böyle olması karara bağlanmıştır.\"", transliteration: "Kale kezalik, kale rabbuki huve aleyye heyyin, ve li nec'alehu ayeten lin nasi ve rahmeten minna, ve kane emren makdıyya."},
  {id: 2272, sure_no: 22, ayet_no: 22, sure_adi: "Meryem", text: "فَحَمَلَتْهُ فَانْتَبَذَتْ بِه۪ مَكَاناً قَصِياًّ", translation: "Ona hamile kaldı ve gözden uzak bir yere çekildi.", transliteration: "Fe hamelethu fentebezet bihi mekanen kasıyya."},
  {id: 2273, sure_no: 23, ayet_no: 23, sure_adi: "Meryem", text: "فَاَجَٓاءَهَا الْمَخَاضُ اِلٰى جِذْعِ النَّخْلَةِۚ قَالَتْ يَا لَيْتَن۪ي مِتُّ قَبْلَ هٰذَا وَكُنْتُ نَسْياً مَنْسِياًّ", translation: "Sonra doğum sancısı onu bir hurma ağacının gövdesine sığınmaya mecbur etti. \"Keşke bundan önce ölseydim de unutulup gitseydim.\" dedi.", transliteration: "Fe ecae hel mehadu ila ciz'ın nahleh, kalet ya leyteni mittu kable haza ve kuntu nesyen mensiyya."},
  {id: 2274, sure_no: 24, ayet_no: 24, sure_adi: "Meryem", text: "فَنَادٰيهَا مِنْ تَحْتِهَٓا اَلَّا تَحْزَن۪ي قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِياًّ", translation: "Sonra altından ona \"Üzülme.\" diye bir ses geldi: \"Rabb'in, senin alt tarafında olanı şerefli kılmıştır.\"", transliteration: "Fe nadaha min tahtiha ella tahzeni kad ceale rabbuki tahteki seriyya."},
  {id: 2275, sure_no: 25, ayet_no: 25, sure_adi: "Meryem", text: "وَهُزّ۪ٓي اِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسَاقِطْ عَلَيْكِ رُطَباً جَنِياًّۘ", translation: "\"Hurma ağacını silkele. Üzerine olmuş taze hurmalar dökülsün.\"", transliteration: "Ve huzzi ileyki bi ciz'ın nahleti tusakıt aleyki rutaben ceniyya."},
  {id: 2276, sure_no: 26, ayet_no: 26, sure_adi: "Meryem", text: "فَكُل۪ي وَاشْرَب۪ي وَقَرّ۪ي عَيْناًۚ فَاِمَّا تَرَيِنَّ مِنَ الْبَشَرِ اَحَداًۙ فَقُول۪ٓي اِنّ۪ي نَذَرْتُ لِلرَّحْمٰنِ صَوْماً فَلَنْ اُكَلِّمَ الْيَوْمَ اِنْسِياًّۚ", translation: "\"Artık ye ve iç. Gözün aydın olsun! Eğer beşerlerden biri ile karşılaşırsan; ben Rahman'a savm adadım, bu nedenle bugün hiç kimse ile konuşmayacağım de.\"", transliteration: "Fe kuli veşrabi ve karri ayna, fe imma terayinne minel beşeri ehaden fe kuli inni nezertu lir rahmani savmen fe len ukellimel yevme insiyya."},
  {id: 2277, sure_no: 27, ayet_no: 27, sure_adi: "Meryem", text: "فَاَتَتْ بِه۪ قَوْمَهَا تَحْمِلُهُۜ قَالُوا يَا مَرْيَمُ لَقَدْ جِئْتِ شَيْـٔاً فَرِياًّ", translation: "Sonra onu kucaklayarak halka getirdi. Dediler ki: \"Ey Meryem! Doğrusu sen olmayacak bir şey yaptın.\"", transliteration: "Fe etet bihi kavmeha tahmiluh, kalu ya meryemu lekad ci'ti şey'en feriyya."},
  {id: 2278, sure_no: 28, ayet_no: 28, sure_adi: "Meryem", text: "يَٓا اُخْتَ هٰرُونَ مَا كَانَ اَبُوكِ امْرَاَ سَوْءٍ وَمَا كَانَتْ اُمُّكِ بَغِياًّۚ", translation: "\"Ey Harun'un kız kardeşi! Doğrusu senin baban kötü bir adam değildi. Annen de iffetsiz bir kadın değildi.\"", transliteration: "Ya uhte harune ma kane ebukimrae sev'in ve ma kanet ummuki begıyya."},
  {id: 2279, sure_no: 29, ayet_no: 29, sure_adi: "Meryem", text: "فَاَشَارَتْ اِلَيْهِ۠ قَالُوا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِي الْمَهْدِ صَبِياًّ", translation: "Bunun üzerine, Meryem çocuğu işaret etti. Onlar: \"Biz beşikteki bir çocukla nasıl konuşuruz?\" dediler.", transliteration: "Fe eşaret ileyh, kalu keyfe nukellimu men kane fil mehdi sabiyya."},
  {id: 2280, sure_no: 30, ayet_no: 30, sure_adi: "Meryem", text: "قَالَ اِنّ۪ي عَبْدُ اللّٰهِ۠ اٰتَانِيَ الْكِتَابَ وَجَعَلَن۪ي نَبِياًّۙ", translation: "Kuşkusuz ki ben Allah'ın kuluyum. Bana Kitap verdi ve beni Nebi yaptı.\" dedi.", transliteration: "Kale inni abdullah, ataniyel kitabe ve cealeni nebiyya."},
  {id: 2281, sure_no: 31, ayet_no: 31, sure_adi: "Meryem", text: "وَجَعَلَن۪ي مُبَارَكاً اَيْنَ مَا كُنْتُۖ وَاَوْصَان۪ي بِالصَّلٰوةِ وَالزَّكٰوةِ مَا دُمْتُ حَياًّۖ", translation: "\"Ve beni bulunduğum her yerde mübarek kıldı. Ve yaşadığım sürece bana salatı ve zekatı tavsiye etti.\"", transliteration: "Ve cealeni mubareken eyne ma kuntu ve evsani bis salati vez zekati ma dumtu hayya."},
  {id: 2282, sure_no: 32, ayet_no: 32, sure_adi: "Meryem", text: "وَبَراًّ بِوَالِدَت۪يۘ وَلَمْ يَجْعَلْن۪ي جَبَّاراً شَقِياًّ", translation: "Ve anneme karşı birr yaptı. Ve beni bir zorba, bir isyankar yapmadı.\"", transliteration: "Ve berren bi valideti ve lem yec'alni cebbaren şakıyya."},
  {id: 2283, sure_no: 33, ayet_no: 33, sure_adi: "Meryem", text: "وَالسَّلَامُ عَلَيَّ يَوْمَ وُلِدْتُ وَيَوْمَ اَمُوتُ وَيَوْمَ اُبْعَثُ حَياًّ", translation: "\"Doğurulduğum gün, öleceğim gün ve canlı olarak diriltileceğim gün, selam benim üzerimedir.\"", transliteration: "Ves selamu aleyye yevme vulidtu ve yevme emutu ve yevme ub'asu hayya."},
  {id: 2284, sure_no: 34, ayet_no: 34, sure_adi: "Meryem", text: "ذٰلِكَ ع۪يسَى ابْنُ مَرْيَمَۚ قَوْلَ الْحَقِّ الَّذ۪ي ف۪يهِ يَمْتَرُونَ", translation: "İşte hakkında tartıştıkları Meryem Oğlu İsa hakkında söylenecek gerçek söz budur.", transliteration: "Zalike isebnu meryem, kavlel hakkıllezi fihi yemterun."},
  {id: 2285, sure_no: 35, ayet_no: 35, sure_adi: "Meryem", text: "مَا كَانَ لِلّٰهِ اَنْ يَتَّخِذَ مِنْ وَلَدٍۙ سُبْحَانَهُۜ اِذَا قَضٰٓى اَمْراً فَاِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُۜ", translation: "Allah'ın çocuk edinmesi olacak şey değildir. O, her şeyden münezzehtir. Allah bir şeyin olmasına karar verdiği zaman ona \"Ol.\" der, o da olur.", transliteration: "Ma kane lillahi en yettehıze min veledin subhaneh, iza kada emren fe innema yekulu lehu kun fe yekun."},
  {id: 2286, sure_no: 36, ayet_no: 36, sure_adi: "Meryem", text: "وَاِنَّ اللّٰهَ رَبّ۪ي وَرَبُّكُمْ فَاعْبُدُوهُۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ", translation: "Allah, benim de Rabb'im sizin de Rabb'inizdir. O halde, O'na kulluk edin. İşte bu dosdoğru yoldur.", transliteration: "Ve innallahe rabbi ve rabbukum fa'buduh, haza sıratun mustekim."},
  {id: 2287, sure_no: 37, ayet_no: 37, sure_adi: "Meryem", text: "فَاخْتَلَفَ الْاَحْزَابُ مِنْ بَيْنِهِمْۚ فَوَيْلٌ لِلَّذ۪ينَ كَفَرُوا مِنْ مَشْهَدِ يَوْمٍ عَظ۪يمٍ", translation: "Bundan sonra gruplar kendi aralarında çekişmeye başladılar. Büyük günde bütün gerçekler ortaya çıktığı zaman, o Kafirlerin vay haline.", transliteration: "Fahtelefel ahzabu min beynihim, fe veylun lillezine keferu min meşhedi yevmin azim."},
  {id: 2288, sure_no: 38, ayet_no: 38, sure_adi: "Meryem", text: "اَسْمِعْ بِهِمْ وَاَبْصِرْۙ يَوْمَ يَأْتُونَنَاۚ لٰكِنِ الظَّالِمُونَ الْيَوْمَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Bize gelecekleri gün, onlara gerçekler işittirilir ve gösterilir. Ne var ki zalimler, bugün apaçık bir şaşkınlık içindeler.", transliteration: "Esmi' bihim ve ebsır yevme ye'tunena lakiniz zalimunel yevme fi dalalin mubin."},
  {id: 2289, sure_no: 39, ayet_no: 39, sure_adi: "Meryem", text: "وَاَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ اِذْ قُضِيَ الْاَمْرُۚ وَهُمْ ف۪ي غَفْلَةٍ وَهُمْ لَا يُؤْمِنُونَ", translation: "Gaflet içinde olup, iman etmeyenleri, emrin yerine getirileceği pişmanlık günüyle uyar.", transliteration: "Ve enzirhum yevmel hasreti iz kudıyel emr, ve hum fi gafletin ve hum la yu'minun."},
  {id: 2290, sure_no: 40, ayet_no: 40, sure_adi: "Meryem", text: "اِنَّا نَحْنُ نَرِثُ الْاَرْضَ وَمَنْ عَلَيْهَا وَاِلَيْنَا يُرْجَعُونَ۟", translation: "Yeryüzüne ve onun üzerindeki her şeye Biz varis olacağız. Ve onlar, yalnızca Bize döndürülecekler.", transliteration: "İnna nahnu nerisul arda ve men aleyha ve ileyna yurceun."},
  {id: 2291, sure_no: 41, ayet_no: 41, sure_adi: "Meryem", text: "وَاذْكُرْ فِي الْكِتَابِ اِبْرٰه۪يمَۜ اِنَّهُ كَانَ صِدّ۪يقاً نَبِياًّ", translation: "Kitap'ta İbrahim'i de an. O, sadık bir Nebi'ydi.", transliteration: "Vezkur fil kitabi ibrahim, innehu kane sıddikan nebiyya."},
  {id: 2292, sure_no: 42, ayet_no: 42, sure_adi: "Meryem", text: "اِذْ قَالَ لِاَب۪يهِ يَٓا اَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْن۪ي عَنْكَ شَيْـٔاً", translation: "Babasına: \"Ey babacığım! İşitmeyen, görmeyen ve sana hiçbir yararı olmayan şeylere niçin kulluk ediyorsun?\" demişti.", transliteration: "İz kale li ebihi, ya ebeti lime ta'budu ma la yesmau ve la yubsıru ve la yugni anke şey'a."},
  {id: 2293, sure_no: 43, ayet_no: 43, sure_adi: "Meryem", text: "يَٓا اَبَتِ اِنّ۪ي قَدْ جَٓاءَن۪ي مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْن۪ٓي اَهْدِكَ صِرَاطاً سَوِياًّ", translation: "\"Ey babacığım! Sana gelmeyen bir ilim bana geldi. O halde bana uy. Ki seni dosdoğru olan yola ileteyim.\"", transliteration: "Ya ebeti inni kad caeni minel ilmi ma lem ye'tike fettebi'ni ehdike sıratan seviyya."},
  {id: 2294, sure_no: 44, ayet_no: 44, sure_adi: "Meryem", text: "يَٓا اَبَتِ لَا تَعْبُدِ الشَّيْطَانَۜ اِنَّ الشَّيْطَانَ كَانَ لِلرَّحْمٰنِ عَصِياًّ", translation: "\"Ey babacığım! Şeytana kulluk etme. Şeytan Rahman'a isyan eden biridir.\"", transliteration: "Ya ebeti la ta'budiş şeytan, inneş şeytane kane lir rahmani asıyya."},
  {id: 2295, sure_no: 45, ayet_no: 45, sure_adi: "Meryem", text: "يَٓا اَبَتِ اِنّ۪ٓي اَخَافُ اَنْ يَمَسَّكَ عَذَابٌ مِنَ الرَّحْمٰنِ فَتَكُونَ لِلشَّيْطَانِ وَلِياًّ", translation: "\"Ey babacığım! Ben, Rahman'dan sana bir azap dokunur da bu durumda şeytan için bir veli olursun diye korkuyorum.\"", transliteration: "Ya ebeti inni ehafu en yemesseke azabun miner rahmani fe tekune liş şeytani veliyya."},
  {id: 2296, sure_no: 46, ayet_no: 46, sure_adi: "Meryem", text: "قَالَ اَرَاغِبٌ اَنْتَ عَنْ اٰلِهَت۪ي يَٓا اِبْرٰه۪يمُۚ  لَئِنْ لَمْ تَنْتَهِ۬ لَاَرْجُمَنَّكَ وَاهْجُرْن۪ي مَلِياًّ", translation: "\"Ey İbrahim! Sen, benim ilahlarıma değer vermiyor musun? Eğer vazgeçmezsen kesinlikle seni taşlarım. Şimdi uzun bir süre gözüme görünme.\" dedi.", transliteration: "Kale e ragıbun ente an aliheti ya ibrahim, lein lem tentehi le ercumenneke vehcurni meliyya."},
  {id: 2297, sure_no: 47, ayet_no: 47, sure_adi: "Meryem", text: "قَالَ سَلَامٌ عَلَيْكَۚ سَاَسْتَغْفِرُ لَكَ رَبّ۪يۜ اِنَّهُ  كَانَ ب۪ي حَفِياًّ", translation: "\"Sana selam olsun. Seni affetmesi için Rabb'imden bağışlanma dileyeceğim. Kuşkusuz O, bana çok lütufkardır.\"", transliteration: "Kale selamun aleyk, se estagfiru leke rabbi, innehu kane bi hafiyya."},
  {id: 2298, sure_no: 48, ayet_no: 48, sure_adi: "Meryem", text: "وَاَعْتَزِلُكُمْ وَمَا تَدْعُونَ مِنْ دُونِ اللّٰهِ وَاَدْعُوا رَبّ۪يۘ عَسٰٓى اَلَّٓا اَكُونَ بِدُعَٓاءِ رَبّ۪ي شَقِياًّ", translation: "\"Sizden ve Allah'ın yanı sıra kulluk ettiğiniz şeylerden uzak durup, yalnızca Rabb'ime dua edeceğim. Umulur ki, Rabb'ime ettiğim dualar sayesinde mahrum olmam.\"", transliteration: "Ve a'tezilukum ve ma ted'une min dunillahi ve ed'u rabbi, asa ella ekune bi duai rabbi şakıyya."},
  {id: 2299, sure_no: 49, ayet_no: 49, sure_adi: "Meryem", text: "فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللّٰهِۙ وَهَبْنَا لَـهُٓ اِسْحٰقَ وَيَعْقُوبَۜ وَكُلاًّ جَعَلْنَا نَبِياًّ", translation: "Onlardan ve onların Allah'ın yanı sıra kulluk ettiklerinden ayrılınca, Biz, ona İshak'ı ve torunu Ya'kub'u bağışladık. Hepsini Nebi yaptık.", transliteration: "Fe lemma'tezelehum ve ma ya'budune min dunillahi vehebna lehu ishaka ve ya'kub ve kullen cealna nebiyya."},
  {id: 2300, sure_no: 50, ayet_no: 50, sure_adi: "Meryem", text: "وَوَهَبْنَا لَهُمْ مِنْ رَحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِياًّ۟", translation: "Ve rahmetimizden onlara lütfettik. Onların doğrulukla anılmalarını sağladık.", transliteration: "Ve vehebna lehum min rahmetina ve cealna lehum lisane sıdkın aliyya."},
  {id: 2301, sure_no: 51, ayet_no: 51, sure_adi: "Meryem", text: "وَاذْكُرْ فِي الْكِتَابِ مُوسٰىۘ اِنَّهُ كَانَ مُخْلَصاً وَكَانَ رَسُولاً نَبِياًّ", translation: "Kitap'ta Musa'yı da an. O, muhles bir Resul, bir Nebi'ydi.", transliteration: "Vezkur fil kitabi musa, innehu kane muhlesan ve kane resulen nebiyya."},
  {id: 2302, sure_no: 52, ayet_no: 52, sure_adi: "Meryem", text: "وَنَادَيْنَاهُ مِنْ جَانِبِ الطُّورِ الْاَيْمَنِ وَقَرَّبْنَاهُ نَجِياًّ", translation: "Ve Tur'un sağ tarafından ona seslendik. Onu, özel konuşmak için yaklaştırdık.", transliteration: "Ve nadeynahu min canibit turil eymeni ve karrebnahu neciyya."},
  {id: 2303, sure_no: 53, ayet_no: 53, sure_adi: "Meryem", text: "وَوَهَبْنَا لَهُ مِنْ رَحْمَتِنَٓا اَخَاهُ هٰرُونَ نَبِياًّ", translation: "Ona rahmetimizden bir Nebi olarak kardeşi Harun'u armağan ettik.", transliteration: "Ve vehebna lehu min rahmetina ehahu harune nebiyya."},
  {id: 2304, sure_no: 54, ayet_no: 54, sure_adi: "Meryem", text: "وَاذْكُرْ فِي الْكِتَابِ اِسْمٰع۪يلَۘ اِنَّهُ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُولاً نَبِياًّۚ", translation: "Kitap'ta İsmail'i de an. O, sözüne sadık bir Resul, bir Nebi'ydi.", transliteration: "Vezkur fil kitabi ismaile innehu kane sadıkal va'di ve kane resulen nebiyya."},
  {id: 2305, sure_no: 55, ayet_no: 55, sure_adi: "Meryem", text: "وَكَانَ يَأْمُرُ اَهْلَهُ بِالصَّلٰوةِ وَالزَّكٰوةِۖ وَكَانَ عِنْدَ رَبِّه۪ مَرْضِياًّ", translation: "Ve o kendisi ile birlikte olanlara salatı ve zekatı buyuruyordu. Ve o Rabb'inin yanında kendisinden hoşnut olunmuşlardandı.", transliteration: "Ve kane ye'muru ehlehu bis salati vez zekati ve kane inde rabbihi mardıyya."},
  {id: 2306, sure_no: 56, ayet_no: 56, sure_adi: "Meryem", text: "وَاذْكُرْ فِي الْكِتَابِ اِدْر۪يسَۘ اِنَّهُ كَانَ صِدّ۪يقاً نَبِياًّۗ", translation: "Kitap'ta İdris'i de an. O, çok sadık bir Nebi'ydi.", transliteration: "Vezkur fil kitabi idrise innehu kane sıddikan nebiyya."},
  {id: 2307, sure_no: 57, ayet_no: 57, sure_adi: "Meryem", text: "وَرَفَعْنَاهُ مَكَاناً عَلِياًّ", translation: "Onu yüce bir mekana yükselttik.", transliteration: "Ve refa'nahu mekanen aliyya."},
  {id: 2308, sure_no: 58, ayet_no: 58, sure_adi: "Meryem", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِنَ النَّبِيّ۪نَ مِنْ ذُرِّيَّةِ اٰدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوحٍۘ وَمِنْ ذُرِّيَّةِ اِبْرٰه۪يمَ وَاِسْرَٓائ۪لَ وَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَاۜ اِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُ الرَّحْمٰنِ خَرُّوا سُجَّداً وَبُكِياًّ", translation: "İşte bunlar, Allah'ın nimetlendirdiği Nebilerdendi. Adem'in neslinden ve Nuh ile birlikte taşıdıklarımızdan ve İbrahim ve İsrail'in soyundan ve doğru yola ilettiğimiz ve seçtiklerimizdendir. Onlara Rahman'ın ayetleri okunduğu zaman ağlayarak secdeye kapanırlardı.", transliteration: "Ulaikellezine en'amallahu aleyhim minen nebiyyine min zurriyyeti ademe ve mimmen hamelna mea nuhin ve min zurriyyeti ibrahime ve israile ve mimmen hedeyna vectebeyna, iza tutla aleyhim ayatur rahmani harru succeden ve bukiyya."},
  {id: 2309, sure_no: 59, ayet_no: 59, sure_adi: "Meryem", text: "فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ اَضَاعُوا الصَّلٰوةَ وَاتَّبَعُوا الشَّهَوَاتِ فَسَوْفَ يَلْقَوْنَ غَياًّۙ", translation: "Bundan sonra arkalarından gelen sonraki nesil, salatı zayi ettiler ve şehvetlerine uydular. Yakında kötülükleri kendilerine dönecektir.", transliteration: "Fe halefe min ba'dihim halfun edaus salate vettebeuş şehevati fe sevfe yelkavne gayya."},
  {id: 2310, sure_no: 60, ayet_no: 60, sure_adi: "Meryem", text: "اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحاً فَاُو۬لٰٓئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ شَيْـٔاًۙ", translation: "Ancak tevbe edip, iman eden ve salihatı yapanlar hariç. İşte onlar Cennet'e girecekler ve onlara hiçbir şekilde haksızlık yapılmayacaktır.", transliteration: "İlla men tabe ve amene ve amile salihan fe ulaike yedhulunel cennete ve la yuzlemune şey'a."},
  {id: 2311, sure_no: 61, ayet_no: 61, sure_adi: "Meryem", text: "جَنَّاتِ عَدْنٍۨ  الَّت۪ي وَعَدَ الرَّحْمٰنُ عِبَادَهُ بِالْغَيْبِۜ اِنَّهُ كَانَ وَعْدُهُ مَأْتِياًّ", translation: "Rahman, kullarına gıyaben Adn Cennetleri söz verdi. Kuşkusuz O'nun sözü gerçekleşecektir.", transliteration: "Cennati adninilleti vaader rahmanu ibadehu bil gayb, innehu kane va'duhu me'tiyya."},
  {id: 2312, sure_no: 62, ayet_no: 62, sure_adi: "Meryem", text: "لَا يَسْمَعُونَ ف۪يهَا لَغْواً اِلَّا سَلَاماًۜ وَلَهُمْ رِزْقُهُمْ ف۪يهَا بُكْرَةً وَعَشِياًّ", translation: "Onlar, orada boş söz işitmezler. Ancak \"selam\" işitirler. Ve orada, onların sabah akşam rızıkları vardır.", transliteration: "La yesmeune fiha lagven illa selama, ve lehum rızkuhum fiha bukreten ve aşiyya."},
  {id: 2313, sure_no: 63, ayet_no: 63, sure_adi: "Meryem", text: "تِلْكَ الْجَنَّةُ الَّت۪ي نُورِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِياًّ", translation: "İşte bu, kullarımızdan takva sahibi olanlara miras olarak vereceğimiz Cennet'tir.", transliteration: "Tilkel cennetulleti nurisu min ibadina men kane takıyya."},
  {id: 2314, sure_no: 64, ayet_no: 64, sure_adi: "Meryem", text: "وَمَا نَتَنَزَّلُ اِلَّا بِاَمْرِ رَبِّكَۚ لَهُ مَا بَيْنَ اَيْد۪ينَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذٰلِكَۚ وَمَا كَانَ رَبُّكَ نَسِياًّۚ", translation: "Biz, Rabb'inin emri olmaksızın inmeyiz. Önümüzdeki, arkamızdaki ve bunların arasında olan her şey O'na aittir. Rabb'in unutkan değildir.", transliteration: "Ve ma netenezzelu illa bi emri rabbik, lehu ma beyne eydina ve ma halfena ve ma beyne zalik, ve ma kane rabbuke nesiyya."},
  {id: 2315, sure_no: 65, ayet_no: 65, sure_adi: "Meryem", text: "رَبُّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِه۪ۜ هَلْ تَعْلَمُ لَهُ سَمِياًّ۟", translation: "Göklerin, yerin ve ikisinin arasındakilerin Rabb'idir. Öyle ise yalnızca O'na kul ol ve kulluğunda sabırlı ol. İsmi O'nunla anılmaya değer bir başkasını biliyor musun?", transliteration: "Rabbus semavati vel ardı ve ma beynehuma fa'budhu vastabir li ibadetih, hel ta'lemu lehu semiyya."},
  {id: 2316, sure_no: 66, ayet_no: 66, sure_adi: "Meryem", text: "وَيَقُولُ الْاِنْسَانُ ءَاِذَا مَا مِتُّ لَسَوْفَ اُخْرَجُ حَياًّ", translation: "İnsan: \"Öldükten sonra gerçekten yeniden diriltilecek miyim?\" diyor.", transliteration: "Ve yekulul insanu e iza ma mittu le sevfe uhracu hayya."},
  {id: 2317, sure_no: 67, ayet_no: 67, sure_adi: "Meryem", text: "اَوَلَا يَذْكُرُ الْاِنْسَانُ اَنَّا خَلَقْنَاهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْـٔاً", translation: "Oysaki daha önce hiçbir şey değilken, kendisini yoktan var ettiğimizi düşünmüyor mu?", transliteration: "E ve la yezkurul insanu enna halaknahu min kablu ve lem yeku şey'a."},
  {id: 2318, sure_no: 68, ayet_no: 68, sure_adi: "Meryem", text: "فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيَاط۪ينَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِياًّۚ", translation: "Rabb'ine ant olsun ki, onları ve şeytanları kesinlikle bir araya toplayacağız. Sonra onları Cehennem'in kenarında diz üstü çökmüş olarak hazır tutacağız.", transliteration: "Fe ve rabbike le nahşurennehum veş şeyatine summe le nuhdırannehum havle cehenneme cisiyya."},
  {id: 2319, sure_no: 69, ayet_no: 69, sure_adi: "Meryem", text: "ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ ش۪يعَةٍ اَيُّهُمْ اَشَدُّ عَلَى الرَّحْمٰنِ عِتِياًّۚ", translation: "Sonra, her topluluktan, Rahman'a karşı kim başkaldırmışsa onları mutlaka ortaya çıkaracağız.", transliteration: "Summe le nenzianne min kulli şiatin eyyuhum eşeddu aler rahmani ıtiyya."},
  {id: 2320, sure_no: 70, ayet_no: 70, sure_adi: "Meryem", text: "ثُمَّ لَنَحْنُ اَعْلَمُ بِالَّذ۪ينَ هُمْ اَوْلٰى بِهَا صِلِياًّ", translation: "Sonra Biz, onu kimlerin hak ettiğini elbette daha iyi biliriz.", transliteration: "Summe le nahnu a'lemu billezine hum evla biha sıliyya."},
  {id: 2321, sure_no: 71, ayet_no: 71, sure_adi: "Meryem", text: "وَاِنْ مِنْكُمْ اِلَّا وَارِدُهَاۚ كَانَ عَلٰى رَبِّكَ حَتْماً مَقْضِياًّۚ", translation: "Sizden oraya gelmeyecek hiç kimse yoktur. Bu Rabb'inin üzerine aldığı kesinleşmiş bir yargıdır.", transliteration: "Ve in minkum illa variduha, kane ala rabbike hatmen makdıyya."},
  {id: 2322, sure_no: 72, ayet_no: 72, sure_adi: "Meryem", text: "ثُمَّ نُنَجِّي الَّذ۪ينَ اتَّقَوْا وَنَذَرُ الظَّالِم۪ينَ ف۪يهَا جِثِياًّ", translation: "Sonra Biz takva sahiplerini kurtuluşa erdireceğiz. Ve zalimleri, orada diz üstü çökmüş halde bırakacağız.", transliteration: "Summe nuneccillezinettekav ve nezeruz zalimine fiha cisiyya."},
  {id: 2323, sure_no: 73, ayet_no: 73, sure_adi: "Meryem", text: "وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ قَالَ الَّذ۪ينَ كَفَرُوا لِلَّذ۪ينَ اٰمَنُٓواۙ اَيُّ الْفَر۪يقَيْنِ خَيْرٌ مَقَاماً وَاَحْسَنُ نَدِياًّ", translation: "Açıklayıcı kanıt içeren ayetlerimiz onlara okunduğu zaman, Kafirler, İman Edenler'e; \"İki gruptan hangisi konumu itibariyle daha hayırlı, çevresi bakımından daha itibarlıdır?\" dediler.", transliteration: "Ve iza tutla aleyhim ayatuna beyyinatin kalellezine keferu lillezine amenu eyyul ferikayni hayrun makamen ve ahsenu nediyya."},
  {id: 2324, sure_no: 74, ayet_no: 74, sure_adi: "Meryem", text: "وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ اَحْسَنُ اَثَاثاً وَرِءْياً", translation: "Oysa onlardan önce, mal ve gösterişçe daha iyi olan nice nesilleri yok ettik.", transliteration: "Ve kem ehlekna kablehum min karnin hum ahsenu esasen ve ri'ya."},
  {id: 2325, sure_no: 75, ayet_no: 75, sure_adi: "Meryem", text: "قُلْ مَنْ كَانَ فِي الضَّلَالَةِ فَلْيَمْدُدْ لَهُ الرَّحْمٰنُ مَداًّۚ حَتّٰٓى اِذَا رَاَوْا مَا يُوعَدُونَ اِمَّا الْعَذَابَ وَاِمَّا السَّاعَةَۜ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَكَاناً وَاَضْعَفُ جُنْداً", translation: "De ki: \"Kim sapkınlıkta ise, Rahman, ona vaat edilen şeyi; azabı veya Sa'at'i görecekleri zamana kadar, zamanı uzatarak süre tanır. Böylece kimin yerce daha kötü ve taraftarca daha zayıf olduğunu yakında bilecekler.\"", transliteration: "Kul men kane fid dalaleti fel yemdud lehur rahmanu medda, hatta iza raev ma yuadune immel azabe ve immes saah, fe se ya'lemune men huve şerrun mekanen ve ad'afu cunda."},
  {id: 2326, sure_no: 76, ayet_no: 76, sure_adi: "Meryem", text: "وَيَز۪يدُ اللّٰهُ الَّذ۪ينَ اهْتَدَوْا هُدًىۜ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَاباً وَخَيْرٌ مَرَداًّ", translation: "Allah, doğru yola yönelenleri, doğru yola iletir. Yapılmış iyi ve yararlı işler Rabb'inin yanında hem karşılık bakımından hem de sonuç bakımından hayırlı olandır.", transliteration: "Ve yezidullahullezinehtedev huda, vel bakıyatus salihatu hayrun inde rabbike sevaben ve hayrun meredda."},
  {id: 2327, sure_no: 77, ayet_no: 77, sure_adi: "Meryem", text: "اَفَرَاَيْتَ الَّذ۪ي كَفَرَ بِاٰيَاتِنَا وَقَالَ لَاُو۫تَيَنَّ مَالاً وَوَلَداًۜ", translation: "Ayetlerimizi inkar edip, yine de \"Bana kesinlikle mal ve evlat verilecek.\" diyen kimseyi görüyor musun?", transliteration: "E fe raeytellezi kefere bi ayatina ve kale le uteyenne malen ve veleda."},
  {id: 2328, sure_no: 78, ayet_no: 78, sure_adi: "Meryem", text: "اَطَّـلَعَ الْغَيْبَ اَمِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْداًۙ", translation: "O, gaybı mı biliyor? Yoksa Rahman'dan bir söz mü aldı?", transliteration: "Ettalaal gaybe emittehaze inder rahmani ahda."},
  {id: 2329, sure_no: 79, ayet_no: 79, sure_adi: "Meryem", text: "كَلَّاۜ سَنَكْتُبُ مَا يَقُولُ وَنَمُدُّ لَهُ مِنَ الْعَذَابِ مَداًّۙ", translation: "Hayır! Onun söylediklerini yazacağız. Ve ona azabı uzattıkça uzatacağız.", transliteration: "Kella, se nektubu ma yekulu ve nemuddu lehu minel azabi medda."},
  {id: 2330, sure_no: 80, ayet_no: 80, sure_adi: "Meryem", text: "وَنَرِثُهُ مَا يَقُولُ وَيَأْت۪ينَا فَرْداً", translation: "Ve onun söylediği şeylere Biz mirasçı olacağız. Ve o Bize tek başına gelecek.", transliteration: "Ve nerisuhu ma yekulu ve ye'tina ferda."},
  {id: 2331, sure_no: 81, ayet_no: 81, sure_adi: "Meryem", text: "وَاتَّخَذُوا مِنْ دُونِ اللّٰهِ اٰلِهَةً لِيَكُونُوا لَهُمْ عِزاًّۙ", translation: "Ve onlar, kendileri için bir izzet olsun diye Allah'tan başka ilahlar edindiler.", transliteration: "Vettehazu min dunillahi aliheten li yekunu lehum ızza."},
  {id: 2332, sure_no: 82, ayet_no: 82, sure_adi: "Meryem", text: "كَلَّاۜ سَيَكْفُرُونَ بِعِبَادَتِهِمْ وَيَكُونُونَ عَلَيْهِمْ ضِداًّ۟", translation: "Hayır! İlah edinilenler, onların kulluklarını inkar edecekler ve onlara düşman kesilecekler.", transliteration: "Kella, se yekfurune bi ibadetihim ve yekunune aleyhim dıdda."},
  {id: 2333, sure_no: 83, ayet_no: 83, sure_adi: "Meryem", text: "اَلَمْ تَرَ اَنَّٓا اَرْسَلْنَا الشَّيَاط۪ينَ عَلَى الْكَافِر۪ينَ تَؤُزُّهُمْ اَزاًّۙ", translation: "Görmüyor musun? Biz, Kafirlerin üzerine, onları tahrik ederek kışkırtan şeytanları saldık.", transliteration: "E lem tere enna erselneş şeyatine alel kafirine teuzzuhum ezza."},
  {id: 2334, sure_no: 84, ayet_no: 84, sure_adi: "Meryem", text: "فَلَا تَعْجَلْ عَلَيْهِمْۜ اِنَّمَا نَعُدُّ لَهُمْ عَداًّۚ", translation: "Öyleyse onlar için acele etme. Onların günlerinin hesabını yapıyoruz.", transliteration: "Fe la ta'cel aleyhim, innema neuddu lehum adda."},
  {id: 2335, sure_no: 85, ayet_no: 85, sure_adi: "Meryem", text: "يَوْمَ نَحْشُرُ الْمُتَّق۪ينَ اِلَى الرَّحْمٰنِ وَفْداًۙ", translation: "O gün takva sahiplerini Rahman'ın huzurunda konuk olarak toplayacağız.", transliteration: "Yevme nahşurul muttekine iler rahmani vefda."},
  {id: 2336, sure_no: 86, ayet_no: 86, sure_adi: "Meryem", text: "وَنَسُوقُ الْمُجْرِم۪ينَ اِلٰى جَهَنَّمَ وِرْداًۢ", translation: "Mücrimleri de susamış olarak Cehennem'e süreceğiz.", transliteration: "Ve nesukul mucrimine ila cehenneme virda."},
  {id: 2337, sure_no: 87, ayet_no: 87, sure_adi: "Meryem", text: "لَا يَمْلِكُونَ الشَّفَاعَةَ اِلَّا مَنِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْداًۢ", translation: "Rahman'ın yanında bir \"ahd\" edinmiş olan kimse hariç, bir şefaate sahip olamayacaklar.", transliteration: "La yemlikuneş şefaate illa menittehaze inder rahmani ahda."},
  {id: 2338, sure_no: 88, ayet_no: 88, sure_adi: "Meryem", text: "وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَداًۜ", translation: "\"Rahman bir çocuk edindi.\" dediler.", transliteration: "Ve kaluttehazer rahmanu veleda."},
  {id: 2339, sure_no: 89, ayet_no: 89, sure_adi: "Meryem", text: "لَقَدْ جِئْتُمْ شَيْـٔاً اِداًّۙ", translation: "Ant olsun ki, siz çok kötü bir iddiada bulundunuz.", transliteration: "Lekad ci'tum şey'en idda."},
  {id: 2340, sure_no: 90, ayet_no: 90, sure_adi: "Meryem", text: "تَكَادُ السَّمٰوَاتُ يَتَفَطَّرْنَ مِنْهُ وَتَنْشَقُّ الْاَرْضُ وَتَخِرُّ الْجِبَالُ هَداًّۙ", translation: "Neredeyse gökler çatlayacak, yer yarılacak ve dağlar gürültü ile devrilecekti.", transliteration: "Tekadus semavatu yetefattarne minhu ve tenşakkul ardu ve tehırrul cibalu hedda."},
  {id: 2341, sure_no: 91, ayet_no: 91, sure_adi: "Meryem", text: "اَنْ دَعَوْا لِلرَّحْمٰنِ وَلَداًۚ", translation: "Rahman'a bir çocuk isnat ettiler diye.", transliteration: "En deav lir rahmani veleda."},
  {id: 2342, sure_no: 92, ayet_no: 92, sure_adi: "Meryem", text: "وَمَا يَنْبَغ۪ي لِلرَّحْمٰنِ اَنْ يَتَّخِذَ وَلَداًۜ", translation: "Oysa Rahman'a çocuk edinmek yaraşmaz.", transliteration: "Ve ma yenbagi lir rahmani en yettehıze veleda."},
  {id: 2343, sure_no: 93, ayet_no: 93, sure_adi: "Meryem", text: "اِنْ كُلُّ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ اِلَّٓا اٰتِي الرَّحْمٰنِ عَبْداًۜ", translation: "Göklerde ve yerde bulunan herkes Rahman'a yalnızca kul olarak gelecek.", transliteration: "İn kullu men fis semavati vel ardı illa atir rahmani abda."},
  {id: 2344, sure_no: 94, ayet_no: 94, sure_adi: "Meryem", text: "لَقَدْ اَحْصٰيهُمْ وَعَدَّهُمْ عَداًّۜ", translation: "Ant olsun ki, onların tamamını kuşatmış ve bir bir tespit etmiştir.", transliteration: "Lekad ahsahum ve addehum adda."},
  {id: 2345, sure_no: 95, ayet_no: 95, sure_adi: "Meryem", text: "وَكُلُّهُمْ اٰت۪يهِ يَوْمَ الْقِيٰمَةِ فَرْداً", translation: "Onların hepsi, Kıyamet günü O'na tek başına gelecektir.", transliteration: "Ve kulluhum atihi yevmel kıyameti ferda."},
  {id: 2346, sure_no: 96, ayet_no: 96, sure_adi: "Meryem", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَيَجْعَلُ لَهُمُ الرَّحْمٰنُ وُداًّ", translation: "İman eden ve salihatı yapanları, Rahman sevilenler kılacaktır.", transliteration: "İnnellezine amenu ve amilus salihati se yec'alu lehumur rahmanu vudda."},
  {id: 2347, sure_no: 97, ayet_no: 97, sure_adi: "Meryem", text: "فَاِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّق۪ينَ وَتُنْذِرَ بِه۪ قَوْماً لُداًّ", translation: "Böylece Biz onu, kendisi ile takva sahiplerini müjdelemen ve inat eden bir halkı uyarabilmen için senin dilinde kolaylaştırdık.", transliteration: "Fe innema yessernahu bi lisanike li tubeşşire bihil muttekine ve tunzire bihi kavmen ludda."},
  {id: 2348, sure_no: 98, ayet_no: 98, sure_adi: "Meryem", text: "وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍۜ هَلْ تُحِسُّ مِنْهُمْ مِنْ اَحَدٍ اَوْ تَسْمَعُ لَهُمْ رِكْزاً", translation: "Onlardan önce nice nesilleri yok ettik. Onlardan herhangi birilerinin varlığını hissediyor musun? Veya onlardan en küçük bir ses duyabiliyor musun?", transliteration: "Ve kem ehlekna kablehum min karn, hel tuhıssu minhum min ehadin ev tesmeu lehum rikza."},
  {id: 2349, sure_no: 1, ayet_no: 1, sure_adi: "Taha", text: "طٰهٰۜ", translation: "Ta. Ha.", transliteration: "Ta, ha."},
  {id: 2350, sure_no: 2, ayet_no: 2, sure_adi: "Taha", text: "مَٓا اَنْزَلْنَا عَلَيْكَ الْقُرْاٰنَ لِتَشْقٰىۙ", translation: "Biz, bu Kur'an'ı sana güçlük çekesin diye indirmedik.", transliteration: "Ma enzelna aleykel kur'ane li teşka."},
  {id: 2351, sure_no: 3, ayet_no: 3, sure_adi: "Taha", text: "اِلَّا تَذْكِرَةً لِمَنْ يَخْشٰىۙ", translation: "Ancak huşu duyan kimselere öğüt olsun diye indirdik.", transliteration: "İlla tezkireten li men yahşa."},
  {id: 2352, sure_no: 4, ayet_no: 4, sure_adi: "Taha", text: "تَنْز۪يلاً مِمَّنْ خَلَقَ الْاَرْضَ وَالسَّمٰوَاتِ الْعُلٰىۜ", translation: "Yeri ve yüce gökleri yaratan tarafından indirilmiştir.", transliteration: "Tenzilen mimmen halakal arda ves semavatil ula."},
  {id: 2353, sure_no: 5, ayet_no: 5, sure_adi: "Taha", text: "اَلرَّحْمٰنُ عَلَى الْعَرْشِ اسْتَوٰى", translation: "Rahman, arş üzerine isteva etmiştir.", transliteration: "Er rahmanu alel arşisteva."},
  {id: 2354, sure_no: 6, ayet_no: 6, sure_adi: "Taha", text: "لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرٰى", translation: "Göklerde ve yerde ne varsa ve bu ikisinin arasında ve nemli toprağın altında ne varsa O'nundur.", transliteration: "Lehu ma fis semavati ve ma fil ardı ve ma beynehuma ve ma tahtes sera."},
  {id: 2355, sure_no: 7, ayet_no: 7, sure_adi: "Taha", text: "وَاِنْ تَجْهَرْ بِالْقَوْلِ فَاِنَّهُ يَعْلَمُ السِّرَّ وَاَخْفٰى", translation: "Sözü açıkça söylesen de söylemesen de bil ki O, gizliyi de saklıyı da bilir.", transliteration: "Ve in techer bil kavli fe innehu ya'lemus sirre ve ahfa."},
  {id: 2356, sure_no: 8, ayet_no: 8, sure_adi: "Taha", text: "اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۜ  لَهُ الْاَسْمَٓاءُ الْحُسْنٰى", translation: "Allah, kendisinden başka ilah olmayandır. En \"iyi isimler\" yalnızca O'nundur.", transliteration: "Allahu la ilahe illa huve, lehul esmaul husna."},
  {id: 2357, sure_no: 9, ayet_no: 9, sure_adi: "Taha", text: "وَهَلْ اَتٰيكَ حَد۪يثُ مُوسٰىۢ", translation: "Sana Musa'nın hadisi ulaştı mı?", transliteration: "Ve hel etake hadisu musa."},
  {id: 2358, sure_no: 10, ayet_no: 10, sure_adi: "Taha", text: "اِذْ رَاٰ نَاراً فَقَالَ لِاَهْلِهِ امْكُـثُٓوا اِنّ۪ٓي اٰنَسْتُ نَاراً لَعَلّ۪ٓي اٰت۪يكُمْ مِنْهَا بِقَبَسٍ اَوْ اَجِدُ عَلَى النَّارِ هُدًى", translation: "Hani o bir ateş görmüş ve yanındakilere: \"Ben bir ateş gördüm. Bekleyin! Belki ondan size bir kor getiririm veya ateşin yanında bir yol gösteren bulurum.\" demişti.", transliteration: "İz rea naren fe kale li ehlihimkusu inni anestu naren lealli atikum minha bi kabesin ev ecidu alen nari huda."},
  {id: 2359, sure_no: 11, ayet_no: 11, sure_adi: "Taha", text: "فَلَمَّٓا اَتٰيهَا نُودِيَ يَا مُوسٰى", translation: "Oraya varınca, \"Ey Musa!\" diye seslenildi.", transliteration: "Fe lemma etaha nudiye ya musa."},
  {id: 2360, sure_no: 12, ayet_no: 12, sure_adi: "Taha", text: "اِنّ۪ٓي اَنَا۬ رَبُّكَ فَاخْلَعْ نَعْلَيْكَۚ اِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًىۜ", translation: "\"Ben senin Rabb'inim. Şimdi ayakkabılarını çıkar. Sen temizlenmiş vadide, Tuva'dasın.\"", transliteration: "İnni ene rabbuke fehla' na'leyk, inneke bil vadil mukaddesi tuva."},
  {id: 2361, sure_no: 13, ayet_no: 13, sure_adi: "Taha", text: "وَاَنَا اخْتَرْتُكَ فَاسْتَمِـعْ لِمَا يُوحٰى", translation: "\"Ben seni seçtim. Öyleyse vahyolunanı dinle.\"", transliteration: "Ve enahtertuke festemi' li ma yuha."},
  {id: 2362, sure_no: 14, ayet_no: 14, sure_adi: "Taha", text: "اِنَّـن۪ٓي اَنَا اللّٰهُ لَٓا اِلٰهَ اِلَّٓا اَنَا۬ فَاعْبُدْن۪يۙ وَاَقِمِ الصَّلٰوةَ لِذِكْر۪ي", translation: "\"Ben Allah'ım. Benden başka ilah yoktur. Öyleyse yalnızca Bana kulluk et. Ve öğüdüm için salatı ikame et.\"", transliteration: "İnneni enallahu la ilahe illa ene fa'budni ve ekımis salate li zikri."},
  {id: 2363, sure_no: 15, ayet_no: 15, sure_adi: "Taha", text: "اِنَّ السَّاعَةَ اٰتِيَةٌ اَكَادُ اُخْف۪يهَا لِتُجْزٰى كُلُّ نَفْسٍ بِمَا تَسْعٰى", translation: "\"Zamanını bildirmemiş olsam da herkesin yaptığının karşılığını görmesi için kuşkusuz ki o Sa'at gelecektir.\"", transliteration: "İnnes saate atiyetun ekadu uhfiha li tucza kullu nefsin bima tes'a."},
  {id: 2364, sure_no: 16, ayet_no: 16, sure_adi: "Taha", text: "فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَا يُؤْمِنُ بِهَا وَاتَّـبَعَ هَوٰيهُ فَتَرْدٰى", translation: "\"Öyleyse ona iman etmeyen, tutku ve kuruntusuna uyan kimse, seni ondan alıkoymasın. Yoksa helak olursun.\"", transliteration: "Fe la yesuddenneke anha men la yu'minu biha vettebea hevahu fe terda."},
  {id: 2365, sure_no: 17, ayet_no: 17, sure_adi: "Taha", text: "وَمَا تِلْكَ بِيَم۪ينِكَ يَا مُوسٰى", translation: "\"Sağ elindeki nedir ey Musa?\"", transliteration: "Ve ma tilke bi yeminike ya musa."},
  {id: 2366, sure_no: 18, ayet_no: 18, sure_adi: "Taha", text: "قَالَ هِيَ عَصَايَۚ اَتَوَكَّـؤُ۬ا عَلَيْهَا وَاَهُشُّ بِهَا عَلٰى غَنَم۪ي وَلِيَ ف۪يهَا مَاٰرِبُ اُخْرٰى", translation: "\"O benim asamdır, ben ona dayanırım. Ve onunla koyunlarıma yaprak silkelerim. Onda benim için başka yararlar da var.\" dedi.", transliteration: "Kale hiye asay, etevekkeu aleyha ve ehuşşu biha ala ganemi ve liye fiha mearibu uhra."},
  {id: 2367, sure_no: 19, ayet_no: 19, sure_adi: "Taha", text: "قَالَ اَلْقِهَا يَا مُوسٰى", translation: "\"Ey Musa, onu yere at.\" dedi.", transliteration: "Kale elkıha ya musa."},
  {id: 2368, sure_no: 20, ayet_no: 20, sure_adi: "Taha", text: "فَاَلْقٰيهَا فَاِذَا هِيَ حَيَّةٌ تَسْعٰى", translation: "Musa onu atınca bir de ne görsün! Hızla hareket eden bir canlı olmuştu.", transliteration: "Fe elkaha fe iza hiye hayyetun tes'a."},
  {id: 2369, sure_no: 21, ayet_no: 21, sure_adi: "Taha", text: "قَالَ خُذْهَا وَلَا تَخَفْ۠ سَنُع۪يدُهَا س۪يرَتَهَا الْاُو۫لٰى", translation: "Allah: \"Onu tut, korkma! Onu ilk haline döndüreceğiz.\" dedi.", transliteration: "Kale huzha ve la tehaf se nuiduha siretehel ula."},
  {id: 2370, sure_no: 22, ayet_no: 22, sure_adi: "Taha", text: "وَاضْمُمْ يَدَكَ اِلٰى جَنَاحِكَ تَخْرُجْ بَيْضَٓاءَ مِنْ غَيْرِ سُٓوءٍ اٰيَةً اُخْرٰىۙ", translation: "Allah: \"Elini koynuna sok. Başka bir ayet olarak, kusursuz bir beyazlıkta çıksın.\"", transliteration: "Vadmum yedeke ila cenahıke tahruc beydae min gayri suin ayeten uhra."},
  {id: 2371, sure_no: 23, ayet_no: 23, sure_adi: "Taha", text: "لِنُرِيَكَ مِنْ اٰيَاتِنَا الْكُبْرٰىۚ", translation: "\"Bununla daha büyük ayetlerimizden bazılarını sana gösterelim.\"", transliteration: "Li nuriyeke min ayatinel kubra."},
  {id: 2372, sure_no: 24, ayet_no: 24, sure_adi: "Taha", text: "اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهُ طَغٰى۟", translation: "\"Firavuna git! Çünkü o azgınlaştı.\"", transliteration: "İzheb ila fir'avne innehu taga."},
  {id: 2373, sure_no: 25, ayet_no: 25, sure_adi: "Taha", text: "قَالَ رَبِّ اشْرَحْ ل۪ي صَدْر۪يۙ", translation: "\"Rabb'im! Göğsüme genişlik ver.\" dedi.", transliteration: "Kale rabbişrah li sadri."},
  {id: 2374, sure_no: 26, ayet_no: 26, sure_adi: "Taha", text: "وَيَسِّرْ ل۪ٓي اَمْر۪يۙ", translation: "\"Ve işimi kolaylaştır.\"", transliteration: "Ve yessir li emri."},
  {id: 2375, sure_no: 27, ayet_no: 27, sure_adi: "Taha", text: "وَاحْلُلْ عُقْدَةً مِنْ لِسَان۪يۙ", translation: "\"Dilimden düğümü çöz.\"", transliteration: "Vahlul ukdeten min lisani."},
  {id: 2376, sure_no: 28, ayet_no: 28, sure_adi: "Taha", text: "يَفْقَهُوا قَوْل۪يۖ", translation: "\"Ki söyleyeceklerimi anlasınlar.\"", transliteration: "Yefkahu kavli."},
  {id: 2377, sure_no: 29, ayet_no: 29, sure_adi: "Taha", text: "وَاجْعَلْ ل۪ي وَز۪يراً مِنْ اَهْل۪يۙ", translation: "\"Bana, yakınlarımdan bir yardımcı ver.\"", transliteration: "Vec'al li veziren min ehli."},
  {id: 2378, sure_no: 30, ayet_no: 30, sure_adi: "Taha", text: "هٰرُونَ اَخ۪يۚ", translation: "\"Kardeşim Harun'u.\"", transliteration: "Harune ahi."},
  {id: 2379, sure_no: 31, ayet_no: 31, sure_adi: "Taha", text: "اُشْدُدْ بِه۪ٓ اَزْر۪يۙ", translation: "\"Onunla gücüme güç kat.\"", transliteration: "Uşdud bihi ezri."},
  {id: 2380, sure_no: 32, ayet_no: 32, sure_adi: "Taha", text: "وَاَشْرِكْهُ ف۪ٓي اَمْر۪يۙ", translation: "\"Onu görevime ortak et.\"", transliteration: "Ve eşrikhu fi emri."},
  {id: 2381, sure_no: 33, ayet_no: 33, sure_adi: "Taha", text: "كَيْ نُسَبِّحَكَ كَث۪يراًۙ", translation: "\"Ki Seni çok tesbih edelim.\"", transliteration: "Key nusebbihake kesira."},
  {id: 2382, sure_no: 34, ayet_no: 34, sure_adi: "Taha", text: "وَنَذْكُرَكَ كَث۪يراًۜ", translation: "\"Seni çokça öğütleyelim.\"", transliteration: "Ve nezkureke kesira."},
  {id: 2383, sure_no: 35, ayet_no: 35, sure_adi: "Taha", text: "اِنَّكَ كُنْتَ بِنَا بَص۪يراً", translation: "\"Sen, bizim her halimizi görmektesin.\"", transliteration: "İnneke kunte bina basira."},
  {id: 2384, sure_no: 36, ayet_no: 36, sure_adi: "Taha", text: "قَالَ قَدْ اُو۫ت۪يتَ سُؤْلَكَ يَا مُوسٰى", translation: "\"Ey Musa! İstediğin sana verildi.\" dedi.", transliteration: "Kale kad utite su'leke ya musa."},
  {id: 2385, sure_no: 37, ayet_no: 37, sure_adi: "Taha", text: "وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً اُخْرٰىۙ", translation: "\"Gerçek şu ki, Biz sana daha önce de lütufta bulunmuştuk.\"", transliteration: "Ve lekad menenna aleyke merreten uhra."},
  {id: 2386, sure_no: 38, ayet_no: 38, sure_adi: "Taha", text: "اِذْ اَوْحَيْنَٓا اِلٰٓى اُمِّكَ مَا يُوحٰىۙ", translation: "\"Hani annene vahyedilmesi gerekeni vahyetmiştik.\"", transliteration: "İz evhayna ila ummike ma yuha."},
  {id: 2387, sure_no: 39, ayet_no: 39, sure_adi: "Taha", text: "اَنِ اقْذِف۪يهِ فِي التَّابُوتِ فَاقْذِف۪يهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ ل۪ي وَعَدُوٌّ لَهُۜ وَاَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنّ۪يۚ وَلِتُصْنَعَ عَلٰى عَيْن۪يۢ", translation: "\"Onu sandığa koy da suya bırak. Su da onu kıyıya bıraksın. Benim ve onun düşmanı, onu alsın.\" Ve \"korumam altında yetiştirilmen için seni sevimli biri yaptım.\"", transliteration: "Enıkzifihi fit tabuti fakzifihi fil yemmi felyulkıhil yemmu bis sahıli ye'huzhu aduvvun li ve aduvvun leh, ve elkaytu aleyke mehabbeten minni ve li tusnea ala ayni."},
  {id: 2388, sure_no: 40, ayet_no: 40, sure_adi: "Taha", text: "اِذْ تَمْش۪ٓي اُخْتُكَ فَتَقُولُ هَلْ اَدُلُّكُمْ عَلٰى مَنْ يَكْفُلُهُۜ فَرَجَعْنَاكَ اِلٰٓى اُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَۜ وَقَتَلْتَ نَفْساً فَنَجَّيْنَاكَ مِنَ الْغَمِّ وَفَتَنَّاكَ فُتُوناً۠ فَلَبِثْتَ سِن۪ينَ ف۪ٓي اَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلٰى قَدَرٍ يَا مُوسٰى", translation: "\"Hani kız kardeşin gidip, \"Size, ona bakacak birisini bulmanızda yardımcı olayım mı?\" demişti. Böylece gözü aydın olsun ve üzülmesin diye seni annene geri döndürdük. Ve birisini öldürmüştün de seni sıkıntıdan kurtarmıştık. Ve seni çeşitli sınavlarla sınav yaptık. Sonra yıllarca Medyen halkı içinde kaldın. Sonra takdirimiz gereği şimdi buradasın ey Musa!\"", transliteration: "İz temşi uhtuke fe tekulu hel edullukum ala men yekfuluh, fe reca'nake ila ummike key takarre aynuha ve la tahzen, ve katelte nefsen fe necceynake minel gammi ve fetennake futuna, fe lebiste sinine fi ehli medyene summe ci'te ala kaderin ya musa."},
  {id: 2389, sure_no: 41, ayet_no: 41, sure_adi: "Taha", text: "وَاصْطَنَعْتُكَ لِنَفْس۪يۚ", translation: "\"Seni Kendim için yetiştirdim.\"", transliteration: "Vastana'tuke li nefsi."},
  {id: 2390, sure_no: 42, ayet_no: 42, sure_adi: "Taha", text: "اِذْهَبْ اَنْتَ وَاَخُوكَ بِاٰيَات۪ي وَلَا تَنِيَا ف۪ي ذِكْر۪يۚ", translation: "\"Sen ve kardeşin, ayetlerimle gidin. Öğütlerimi duyurmada gevşeklik göstermeyin.\"", transliteration: "İzheb ente ve ehuke bi ayati ve la teniya fi zikri."},
  {id: 2391, sure_no: 43, ayet_no: 43, sure_adi: "Taha", text: "اِذْهَبَٓا اِلٰى فِرْعَوْنَ اِنَّهُ طَغٰىۚ", translation: "\"İkiniz de Firavun'a gidin. Kuşkusuz o azgınlaştı.\"", transliteration: "İzheba ila fir'avne innehu taga."},
  {id: 2392, sure_no: 44, ayet_no: 44, sure_adi: "Taha", text: "فَقُولَا لَهُ قَوْلاً لَيِّناً لَعَلَّهُ يَتَذَكَّرُ اَوْ يَخْشٰى", translation: "\"Ona yumuşak söz söyleyin. Umulur ki öğüt alır veya huşu duyar.\"", transliteration: "Fe kula lehu kavlen leyyinen leallehu yetezekkeru ev yahşa."},
  {id: 2393, sure_no: 45, ayet_no: 45, sure_adi: "Taha", text: "قَالَا رَبَّـنَٓا اِنَّـنَا نَخَافُ اَنْ يَفْرُطَ عَلَيْنَٓا اَوْ اَنْ يَطْغٰى", translation: "\"Rabb'imiz! Onun bize karşı aşırı gitmesinden veya azgınlık yapmasından çekiniyoruz.\" dediler.", transliteration: "Kala rabbena innena nehafu en yefruta aleyna ev en yatga."},
  {id: 2394, sure_no: 46, ayet_no: 46, sure_adi: "Taha", text: "قَالَ لَا تَخَافَٓا اِنَّن۪ي مَعَكُمَٓا اَسْمَعُ وَاَرٰى", translation: "\"Korkmayın! Kuşkusuz Ben sizinle birlikteyim. İşitir ve görürüm.\" dedi.", transliteration: "Kale la tehafa inneni meakuma esmau ve era."},
  {id: 2395, sure_no: 47, ayet_no: 47, sure_adi: "Taha", text: "فَأْتِيَاهُ فَقُولَٓا اِنَّا رَسُولَا رَبِّكَ فَاَرْسِلْ مَعَنَا بَن۪ٓي اِسْرَٓائ۪لَ وَلَا تُعَذِّبْهُمْۜ قَدْ جِئْنَاكَ بِاٰيَةٍ مِنْ رَبِّكَۜ وَالسَّلَامُ عَلٰى مَنِ اتَّـبَعَ الْهُدٰى", translation: "\"Hemen ona gidin: \"Kuşkusuz ki biz Rabb'inin iki Resul'üyüz. Artık İsrailoğulları'nı bizimle gönder ve onlara azap etme. Doğrusu biz sana bir ayet ile geldik. Selam doğru yola uyanlaradır.\"", transliteration: "Fe'tiyahu fe kula inna resula rabbike fe ersil meana beni israile ve la tuazzibhum, kad ci'nake bi ayetin min rabbik, ves selamu ala menittebeal huda."},
  {id: 2396, sure_no: 48, ayet_no: 48, sure_adi: "Taha", text: "اِنَّا قَدْ اُو۫حِيَ اِلَيْنَٓا اَنَّ الْعَذَابَ عَلٰى مَنْ كَذَّبَ وَتَوَلّٰى", translation: "\"Bize, yalanlayıp aldırmayanların azaba uğrayacakları vahyolundu.\"", transliteration: "İnna kad uhıye ileyna ennel azabe ala men kezzebe ve tevella."},
  {id: 2397, sure_no: 49, ayet_no: 49, sure_adi: "Taha", text: "قَالَ فَمَنْ رَبُّكُمَا يَا مُوسٰى", translation: "Firavun: \"Ey Musa! Rabb'iniz kimdir?\" dedi.", transliteration: "Kale fe men rabbukuma ya musa."},
  {id: 2398, sure_no: 50, ayet_no: 50, sure_adi: "Taha", text: "قَالَ رَبُّنَا الَّـذ۪ٓي اَعْطٰى كُلَّ شَيْءٍ خَلْقَهُ ثُمَّ هَدٰى", translation: "Musa: \"Bizim Rabb'imiz her şeye yaradılışını veren, sonra da yol gösterendir.\" dedi.", transliteration: "Kale rabbunellezi a'ta kulle şey'in halkahu summe heda."},
  {id: 2399, sure_no: 51, ayet_no: 51, sure_adi: "Taha", text: "قَالَ فَمَا بَالُ الْقُرُونِ الْاُو۫لٰى", translation: "Firavun: \"Öyleyse ilk nesillerin durumu ne olacak?\" dedi.", transliteration: "Kale fe ma balul kurunil ula."},
  {id: 2400, sure_no: 52, ayet_no: 52, sure_adi: "Taha", text: "قَالَ عِلْمُهَا عِنْدَ رَبّ۪ي ف۪ي كِتَابٍۚ لَا يَضِلُّ رَبّ۪ي وَلَا يَنْسٰىۘ", translation: "\"Onun bilgisi Rabb'imin yanında yazılıdır. Rabb'im yanlış yapmaz ve unutmaz.\" dedi.", transliteration: "Kale ilmuha inde rabbi fi kitab, la yadıllu rabbi ve la yensa."},
  {id: 2401, sure_no: 53, ayet_no: 53, sure_adi: "Taha", text: "اَلَّذ۪ي جَعَلَ لَكُمُ الْاَرْضَ مَهْداً وَسَلَكَ لَكُمْ ف۪يهَا سُبُلاً وَاَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۜ فَاَخْرَجْنَا بِه۪ٓ اَزْوَاجاً مِنْ نَبَاتٍ شَتّٰى", translation: "Yeryüzünü sizin için bir beşik yapan, orada sizin için yollar açan ve gökten su indiren O'dur. Onunla her türlü bitkiden çiftler yetiştirdik.", transliteration: "Ellezi ceale lekumul arda mehden ve seleke lekum fiha subulen ve enzele mines semai maa, fe ahrecna bihi ezvacen min nebatin şetta."},
  {id: 2402, sure_no: 54, ayet_no: 54, sure_adi: "Taha", text: "كُلُوا وَارْعَوْا اَنْعَامَكُمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِاُو۬لِي النُّهٰى۟", translation: "Yiyin ve hayvanlarınızı otlatın. Bunda, akıl sahipleri için ayetler vardır.", transliteration: "Kulu ver'av en'amekum, inne fi zalike le ayatin li ulin nuha."},
  {id: 2403, sure_no: 55, ayet_no: 55, sure_adi: "Taha", text: "مِنْهَا خَلَقْنَاكُمْ وَف۪يهَا نُع۪يدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً اُخْرٰى", translation: "Sizi ondan yarattık. Ve sizi ona döndüreceğiz. Ve sizi oradan bir kere daha çıkaracağız.", transliteration: "Minha halaknakum ve fiha nuidukum ve minha nuhricukum tareten uhra."},
  {id: 2404, sure_no: 56, ayet_no: 56, sure_adi: "Taha", text: "وَلَقَدْ اَرَيْنَاهُ اٰيَاتِنَا كُلَّهَا فَكَذَّبَ وَاَبٰى", translation: "Ant olsun ki; ayetlerimizin hepsini ona gösterdik. Buna rağmen yalanlamakta diretti.", transliteration: "Ve lekad ereynahu ayatina kulleha fe kezzebe ve eba."},
  {id: 2405, sure_no: 57, ayet_no: 57, sure_adi: "Taha", text: "قَالَ اَجِئْتَنَا لِتُخْرِجَنَا مِنْ اَرْضِنَا بِسِحْرِكَ يَا مُوسٰى", translation: "\"Ey Musa! Sen sihrinle bizi yurdumuzdan çıkarmak için mi geldin?\" dedi.", transliteration: "Kale e ci'tena li tuhricena min ardına bi sihrike ya musa."},
  {id: 2406, sure_no: 58, ayet_no: 58, sure_adi: "Taha", text: "فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِثْلِه۪ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِداً لَا نُخْلِفُهُ نَحْنُ وَلَٓا اَنْتَ مَكَاناً سُوًى", translation: "\"O halde biz de sana, senin sihrine benzer bir sihirle karşılık vereceğiz. Şimdi üzerinde birlikte anlaşacağımız uygun bir yer ve zaman belirle.\"", transliteration: "Fe le ne'tiyenneke bi sıhrin mislihi fec'al beynena ve beyneke mev'ıden la nuhlifuhu nahnu ve la ente mekanen suva."},
  {id: 2407, sure_no: 59, ayet_no: 59, sure_adi: "Taha", text: "قَالَ مَوْعِدُكُمْ يَوْمُ الزّ۪ينَةِ وَاَنْ يُحْشَرَ النَّاسُ ضُحًى", translation: "\"Sizinle buluşma zamanı, Ziynet Günü insanların toplanma zamanı olan kuşluk vakti olsun.\" dedi.", transliteration: "Kale mev'ıdukum yevmuz zineti ve en yuhşeren nasu duha."},
  {id: 2408, sure_no: 60, ayet_no: 60, sure_adi: "Taha", text: "فَتَوَلّٰى فِرْعَوْنُ فَجَمَعَ كَيْدَهُ ثُمَّ اَتٰى", translation: "Bunun üzerine Firavun dönüp gitti ve yapacağı hileleri hazırlattıktan sonra belirlenen yere geldi.", transliteration: "Fe tevella fir'avnu fe cemea keydehu summe eta."},
  {id: 2409, sure_no: 61, ayet_no: 61, sure_adi: "Taha", text: "قَالَ لَهُمْ مُوسٰى وَيْلَكُمْ لَا تَفْتَرُوا عَلَى اللّٰهِ كَذِباً فَيُسْحِتَكُمْ بِعَذَابٍۚ وَقَدْ خَابَ مَنِ افْتَرٰى", translation: "Musa onlara: \"Yazıklar olsun size! Yalanlarınızla Allah'a iftira etmeyin. Yoksa sizi azapla yok eder. İftira edenler, gerçekten yok olmuştur.\"", transliteration: "Kale lehum musa veylekum la tefteru alallahi keziben fe yushıtekum bi azab, ve kad habe meniftera."},
  {id: 2410, sure_no: 62, ayet_no: 62, sure_adi: "Taha", text: "فَتَنَازَعُٓوا اَمْرَهُمْ بَيْنَهُمْ وَاَسَرُّوا النَّجْوٰى", translation: "Firavunun adamları fısıltı ile aralarında ne yapacaklarını tartıştılar.", transliteration: "Fe tenazeu emrehum beynehum ve eserrun necva."},
  {id: 2411, sure_no: 63, ayet_no: 63, sure_adi: "Taha", text: "قَالُٓوا اِنْ هٰذَانِ لَسَاحِرَانِ يُر۪يدَانِ اَنْ يُخْرِجَاكُمْ مِنْ اَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَر۪يقَتِكُمُ الْمُثْلٰى", translation: "\"Bu ikisi kesinlikle sihirbazdır. Sihirleriyle sizi yerinizden çıkarmak, örnek yolunuzu değiştirmek istiyorlar.\"", transliteration: "Kalu in hazani le sahirani yuridani en yuhricakum min ardıkum bi sihrihima ve yezheba bi tarikatikumul musla."},
  {id: 2412, sure_no: 64, ayet_no: 64, sure_adi: "Taha", text: "فَاَجْمِعُوا كَيْدَكُمْ ثُمَّ ائْتُوا صَفاًّۚ وَقَدْ اَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلٰى", translation: "\"Şu halde bütün oyunlarınızı birleştirin, sonra bir düzen içinde ortaya koyun. Bugün üstün gelen kesinlikle başarıyı elde etmiş olur.\"", transliteration: "Fe ecmiu keydekum summe'tu saffa, ve kad eflehal yevme menista'la."},
  {id: 2413, sure_no: 65, ayet_no: 65, sure_adi: "Taha", text: "قَالُوا يَا مُوسٰٓى اِمَّٓا اَنْ تُلْقِيَ وَاِمَّٓا اَنْ نَكُونَ اَوَّلَ مَنْ اَلْقٰى", translation: "Sihirbazlar, \"Ey Musa! Önce sen mi atacaksın, yoksa biz mi atalım?\" dediler.", transliteration: "Kalu ya musa imma en tulkıye ve imma en nekune evvele men elka."},
  {id: 2414, sure_no: 66, ayet_no: 66, sure_adi: "Taha", text: "قَالَ بَلْ اَلْقُواۚ فَاِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ اِلَيْهِ مِنْ سِحْرِهِمْ اَنَّهَا تَسْعٰى", translation: "Musa, \"Buyurun, önce siz atın.\" dedi. Yaptıkları büyüden dolayı attıkları sopa ve ip hızla hareket ediyormuş gibi göründü.", transliteration: "Kale bel elku, fe iza hıbaluhum ve ısıyyuhum yuhayyelu ileyhi min sıhrihim enneha tes'a."},
  {id: 2415, sure_no: 67, ayet_no: 67, sure_adi: "Taha", text: "فَاَوْجَسَ ف۪ي نَفْسِه۪ خ۪يفَةً مُوسٰى", translation: "Bunun üzerine Musa'nın içine korku düştü.", transliteration: "Fe evcese fi nefsihi hifeten musa."},
  {id: 2416, sure_no: 68, ayet_no: 68, sure_adi: "Taha", text: "قُلْنَا لَا تَخَفْ اِنَّكَ اَنْتَ الْاَعْلٰى", translation: "\"Korkma! Kuşkusuz ki sen üstün geleceksin.\" dedik.", transliteration: "Kulna la tehaf inneke entel a'la."},
  {id: 2417, sure_no: 69, ayet_no: 69, sure_adi: "Taha", text: "وَاَلْقِ مَا ف۪ي يَم۪ينِكَ تَلْقَفْ مَا صَنَعُواۜ اِنَّمَا صَنَعُوا كَيْدُ سَاحِرٍۜ وَلَا يُفْلِحُ السَّاحِرُ حَيْثُ اَتٰى", translation: "\"Sağ elinde olanı at, onların yaptığı şeyleri yutacak. Onların yaptıkları sihirbaz hilesinden başka bir şey değil. Ve sihirbazlar ne yaparlarsa yapsınlar kurtuluşa eremezler.\"", transliteration: "Ve elkı ma fi yeminike telkaf ma sanau, innema sanau keydu sahır, ve la yuflihus sahıru haysu eta."},
  {id: 2418, sure_no: 70, ayet_no: 70, sure_adi: "Taha", text: "فَاُلْقِيَ السَّحَرَةُ سُجَّداً قَالُٓوا اٰمَنَّا بِرَبِّ هٰرُونَ وَمُوسٰى", translation: "Bunun üzerine sihirbazlar, secde edenler olarak atıldılar. Biz: \"Harun'un ve Musa'nın Rabb'ine iman ettik.\" dediler.", transliteration: "Fe ulkıyes seharatu succeden kalu amenna bi rabbi harune ve musa."},
  {id: 2419, sure_no: 71, ayet_no: 71, sure_adi: "Taha", text: "قَالَ اٰمَنْتُمْ لَهُ قَبْلَ اَنْ اٰذَنَ لَكُمْۜ  اِنَّهُ لَكَب۪يرُكُمُ الَّذ۪ي عَلَّمَكُمُ السِّحْرَۚ فَلَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِنْ خِلَافٍ وَلَاُصَلِّبَنَّكُمْ ف۪ي جُذُوعِ النَّخْلِۘ وَلَتَعْلَمُنَّ اَيُّـنَٓا اَشَدُّ عَذَاباً وَاَبْقٰى", translation: "Firavun, \"Ben izin vermeden ona iman mı ettiniz? Kuşkusuz o, size sihir öğreten hocanızdır. Kesinlikle ellerinizi ve ayaklarınızı çaprazlama keseceğim ve sizi hurma ağacının gövdesine asacağım. Ve böylece hangimizin azabının daha şiddetli ve kalıcı olduğunu mutlaka öğreneceksiniz.\" dedi.", transliteration: "Kale amentum lehu kable en azene lekum, innehu le kebirukumullezi allemekumus sihr, fe le ukattıanne eydiyekum ve erculekum min hilafin ve le usallibennekum fi cuzuın nahli ve le ta'lemunne eyyuna eşeddu azaben ve ebka."},
  {id: 2420, sure_no: 72, ayet_no: 72, sure_adi: "Taha", text: "قَالُوا لَنْ نُؤْثِرَكَ عَلٰى مَا جَٓاءَنَا مِنَ الْبَيِّنَاتِ وَالَّذ۪ي فَطَرَنَا فَاقْضِ مَٓا اَنْتَ قَاضٍۜ اِنَّمَا تَقْض۪ي هٰذِهِ الْحَيٰوةَ الدُّنْيَاۜ", translation: "\"Bizim açımızdan, ortaya konan bu kanıtlayıcı bilgiden sonra, yaradılışımızın esaslarını belirleyen yaratıcıya karşı seni asla tercih etmeyiz. İstediğin şeyi yap. Senin hükmün ancak bu dünya hayatında geçer.\" dediler.", transliteration: "Kalu len nu'sireke ala ma caena minel beyyinati vellezi fatarana fakdi ma ente kad, innema takdi hazihil hayated dunya."},
  {id: 2421, sure_no: 73, ayet_no: 73, sure_adi: "Taha", text: "اِنَّٓا اٰمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطَايَانَا وَمَٓا اَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِۜ وَاللّٰهُ خَيْرٌ وَاَبْقٰى", translation: "\"Biz, yanlışlarımız ve bize sihirle zorla yaptırdığın kötü şeyler için Rabb'imizin bizi bağışlayacağına inanıyoruz. Allah hayırlı ve kalıcı olandır.\"", transliteration: "İnna amenna bi rabbina li yagfire lena hatayana ve ma ekrehtena aleyhi mines sihr, vallahu hayrun ve ebka."},
  {id: 2422, sure_no: 74, ayet_no: 74, sure_adi: "Taha", text: "اِنَّهُ مَنْ يَأْتِ رَبَّهُ مُجْرِماً فَاِنَّ لَهُ جَهَنَّمَۜ   لَا يَمُوتُ ف۪يهَا وَلَا يَحْيٰى", translation: "Kim Rabb'ine mücrim olarak gelirse, onun yeri Cehennem'dir. Orada ne ölür ne de yaşar.", transliteration: "İnnehu men ye'ti rabbehu mucrimen fe inne lehu cehennem, la yemutu fiha ve la yahya."},
  {id: 2423, sure_no: 75, ayet_no: 75, sure_adi: "Taha", text: "وَمَنْ يَأْتِه۪ مُؤْمِناً قَدْ عَمِلَ الصَّالِحَاتِ فَاُو۬لٰٓئِكَ لَهُمُ الدَّرَجَاتُ الْعُلٰىۙ", translation: "Ve kim O'na bir mümin olarak salihatı yapmış şekilde gelirse, işte böyle kimseler için yüksek makamlar vardır.", transliteration: "Ve men ye'tihi mu'minen kad amiles salihati fe ulaike lehumud derecatul ula."},
  {id: 2424, sure_no: 76, ayet_no: 76, sure_adi: "Taha", text: "جَنَّاتُ عَدْنٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ وَذٰلِكَ جَزٰٓؤُ۬ا مَنْ تَزَكّٰى۟", translation: "İçinden ırmaklar akan Adn Cennetleri'nde sürekli kalacaklar. İşte bu zekat yapanlara verilecek karşılıktır.", transliteration: "Cennatu adnin tecri min tahtihel enharu halidine fiha ve zalike cezau men tezekka."},
  {id: 2425, sure_no: 77, ayet_no: 77, sure_adi: "Taha", text: "وَلَقَدْ اَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنْ اَسْرِ بِعِبَاد۪ي فَاضْرِبْ لَهُمْ طَر۪يقاً فِي الْبَحْرِ يَبَساًۚ لَا تَخَافُ دَرَكاً وَلَا تَخْشٰى", translation: "Ant olsun ki, Musa'ya: \"Kullarımla geceleyin yola çık; onlara denizde kuru bir yol aç. Arkanızdan yetişecekler diye korkma, Allah'a olan bağlılığını kaybetme!\" diye vahyettik.", transliteration: "Ve lekad evhayna ila musa en esri bi ibadi fadrib lehum tarikan fil bahri yebesa, la tehafu dereken ve la tahşa."},
  {id: 2426, sure_no: 78, ayet_no: 78, sure_adi: "Taha", text: "فَاَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِه۪ فَغَشِيَهُمْ مِنَ الْيَمِّ مَا غَشِيَهُمْۜ", translation: "Firavun hemen ordusu ile onların ardına düştü. Derken sudan üzerlerine kapanan kapandı.", transliteration: "Fe etbeahum fir'avnu bi cunudihi fe gaşiyehum minel yemmi ma gaşiyehum."},
  {id: 2427, sure_no: 79, ayet_no: 79, sure_adi: "Taha", text: "وَاَضَلَّ فِرْعَوْنُ قَوْمَهُ وَمَا هَدٰى", translation: "Firavun halkını sapkınlıkta bıraktı; onları doğru yola yöneltmedi.", transliteration: "Ve edalle fir'avnu kavmehu ve ma heda."},
  {id: 2428, sure_no: 80, ayet_no: 80, sure_adi: "Taha", text: "يَا بَن۪ٓي اِسْرَٓائ۪لَ قَدْ اَنْجَيْنَاكُمْ مِنْ عَدُوِّكُمْ وَوٰعَدْنَاكُمْ جَانِبَ الطُّورِ الْاَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى", translation: "Ey İsrailoğulları! Sizi, düşmanınızdan kurtarmış ve Tur'un sağ yanında sizden söz almıştık. Ve size menn ve bıldırcın bağışlamıştık.", transliteration: "Ya beni israile kad enceynakum min aduvvikum ve vaadnakum canibet turil eymene ve nezzelna aleykumul menne ves selva."},
  {id: 2429, sure_no: 81, ayet_no: 81, sure_adi: "Taha", text: "كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَلَا تَطْغَوْا ف۪يهِ فَيَحِلَّ عَلَيْكُمْ غَضَب۪يۚ وَمَنْ يَحْلِلْ عَلَيْهِ غَضَب۪ي فَقَدْ هَوٰى", translation: "Size rızık olarak verdiğimiz temiz şeyleri yiyin. Bu konuda aşırılık yapmayın. Yoksa gazabıma uğrarsınız. Kim gazabıma uğrarsa, kesinlikle o tepetaklak olmuştur.", transliteration: "Kulu min tayyibati ma rezaknakum ve la tatgav fihi fe yahılle aleykum gadabi ve men yahlil aleyhi gadabi fe kad heva."},
  {id: 2430, sure_no: 82, ayet_no: 82, sure_adi: "Taha", text: "وَاِنّ۪ي لَغَفَّارٌ لِمَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحاً ثُمَّ اهْتَدٰى", translation: "Ben, tevbe eden, iman edip salihatı yapan, doğru yolda olan kimseyi kesinlikle bağışlarım.", transliteration: "Ve inni le gaffarun li men tabe ve amene ve amile salihan summehteda."},
  {id: 2431, sure_no: 83, ayet_no: 83, sure_adi: "Taha", text: "وَمَٓا اَعْجَلَكَ عَنْ قَوْمِكَ يَا مُوسٰى", translation: "Ey Musa, seni halkından aceleyle ayrılmaya sevk eden şey nedir?\"", transliteration: "Ve ma a'celeke an kavmike ya musa."},
  {id: 2432, sure_no: 84, ayet_no: 84, sure_adi: "Taha", text: "قَالَ هُمْ اُو۬لَٓاءِ عَلٰٓى اَثَر۪ي وَعَجِلْتُ اِلَيْكَ رَبِّ لِتَرْضٰى", translation: "Musa: \"Onlar, benim yolumdalar. Acele etmemin nedeni, Sen'i hoşnut etmektir.\" dedi.", transliteration: "Kale hum ulai ala eseri ve aciltu ileyke rabbi li terda."},
  {id: 2433, sure_no: 85, ayet_no: 85, sure_adi: "Taha", text: "قَالَ فَاِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْ بَعْدِكَ وَاَضَلَّهُمُ السَّامِرِيُّ", translation: "Allah: \"Biz, halkını sınadık. Senden sonra Samiri onları saptırdı.\"", transliteration: "Kale fe inna kad fetenna kavmeke min ba'dike ve edallehumus samiriyy."},
  {id: 2434, sure_no: 86, ayet_no: 86, sure_adi: "Taha", text: "فَرَجَعَ مُوسٰٓى اِلٰى قَوْمِه۪ غَضْبَانَ اَسِفاًۚ قَالَ يَا قَوْمِ اَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْداً حَسَناًۜ اَفَطَالَ عَلَيْكُمُ الْعَهْدُ اَمْ اَرَدْتُمْ اَنْ يَحِلَّ عَلَيْكُمْ غَضَبٌ مِنْ رَبِّكُمْ فَاَخْلَفْتُمْ مَوْعِد۪ي", translation: "Bunun üzerine Musa, halkına öfkeli ve üzüntülü bir şekilde geri döndü: \"Ey halkım, Rabb'iniz size iyi bir söz vermedi mi? Şimdi bu süre size uzun mu geldi? Yoksa Rabb'inizin gazabının üzerinize inmesini mi istediniz de bana verdiğiniz sözden döndünüz?\" dedi.", transliteration: "Fe recea musa ila kavmihi gadbane esifa, kale ya kavmi e lem yaıdkum rabbukum va'den hasena, e fe tale aleykumul ahdu em eredtum en yahılle aleykum gadabun min rabbikum fe ahleftum mev'ıdi."},
  {id: 2435, sure_no: 87, ayet_no: 87, sure_adi: "Taha", text: "قَالُوا مَٓا اَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلٰكِنَّا حُمِّلْـنَٓا اَوْزَاراً مِنْ ز۪ينَةِ الْقَوْمِ فَقَذَفْنَاهَا فَكَذٰلِكَ اَلْقَى السَّامِرِيُّۙ", translation: "\"Senin sözünden kendi isteğimizle çıkmadık. Fakat biz halkın ziynetlerinden yüklenmiştik. Sonra onları kaldırıp attık. Aynı şekilde Samiri de attı.\"", transliteration: "Kalu ma ahlefna mev'ıdeke bi melkina ve lakinna hummilna evzaren min zinetil kavmi fe kazefnaha fe kezalike elkas samiriyy."},
  {id: 2436, sure_no: 88, ayet_no: 88, sure_adi: "Taha", text: "فَاَخْرَجَ لَهُمْ عِجْلاً جَسَداً لَهُ خُوَارٌ فَقَالُوا هٰذَٓا اِلٰهُكُمْ وَاِلٰهُ مُوسٰى فَنَسِيَۜ", translation: "Böylece onlara böğürmesi olan bir boğa cesedi çıkardı. Ve onlara: \"Bu sizin de ilahınız Musa'nın da. Ancak Musa onu unuttu.\" dediler.", transliteration: "Fe ahrece lehum ıclen ceseden lehu huvarun fe kalu haza ilahukum ve ilahu musa fe nesiy."},
  {id: 2437, sure_no: 89, ayet_no: 89, sure_adi: "Taha", text: "اَفَلَا يَرَوْنَ اَلَّا يَرْجِعُ اِلَيْهِمْ قَوْلاًۙ وَلَا يَمْلِكُ لَهُمْ ضَراًّ وَلَا نَفْعاً۟", translation: "Onlar, kendilerine bir cevap veremediğini, yarar veya zarar verecek bir gücünün olmadığını görmüyorlar mı?", transliteration: "E fe la yerevne ella yerciu ileyhim kavlen ve la yemliku lehum darren ve la nef'a."},
  {id: 2438, sure_no: 90, ayet_no: 90, sure_adi: "Taha", text: "وَلَقَدْ قَالَ لَهُمْ هٰرُونُ مِنْ قَبْلُ يَا قَوْمِ اِنَّمَا فُتِنْتُمْ بِه۪ۚ وَاِنَّ رَبَّكُمُ الرَّحْمٰنُ فَاتَّبِعُون۪ي وَاَط۪يعُٓوا اَمْر۪ي", translation: "Ant olsun ki, Harun, daha önce onlara: \"Ey halkım! Kuşku yok ki siz bununla sınava çekildiniz. Kuşkusuz sizin Rabb'iniz Rahman'dır. Gelin bana uyun ve buyruklarıma tabi olun.\" demişti.", transliteration: "Ve lekad kale lehum harunu min kablu ya kavmi innema futintum bih ve inne rabbekumur rahmanu fettebiuni ve etiu emri."},
  {id: 2439, sure_no: 91, ayet_no: 91, sure_adi: "Taha", text: "قَالُوا لَنْ نَبْرَحَ عَلَيْهِ عَاكِف۪ينَ حَتّٰى يَرْجِعَ اِلَيْنَا مُوسٰى", translation: "Halk, \"Musa bize dönüp gelinceye kadar, kendimizi buzağıya adamaktan asla vazgeçmeyeceğiz.\" dedi.", transliteration: "Kalu len nebreha aleyhi akifine hatta yercia ileyna musa."},
  {id: 2440, sure_no: 92, ayet_no: 92, sure_adi: "Taha", text: "قَالَ يَا هٰرُونُ مَا مَنَعَكَ اِذْ رَاَيْتَهُمْ ضَلُّواۙ", translation: "Musa: \"Ey Harun! Bunların sapkınlıklarını gördüğün halde, onları uyarmaktan seni alıkoyan ne oldu?\"", transliteration: "Kale ya harunu ma meneake iz reeytehum dallu."},
  {id: 2441, sure_no: 93, ayet_no: 93, sure_adi: "Taha", text: "اَلَّا تَتَّبِعَنِۜ اَفَعَصَيْتَ اَمْر۪ي", translation: "\"Niçin bana uymadın? Yoksa buyruklarıma karşı mı çıktın?\"", transliteration: "Ella tettebian, e fe asayte emri."},
  {id: 2442, sure_no: 94, ayet_no: 94, sure_adi: "Taha", text: "قَالَ يَبْنَؤُ۬مَّ لَا تَأْخُذْ بِلِحْيَت۪ي وَلَا بِرَأْس۪يۚ اِنّ۪ي خَش۪يتُ اَنْ تَقُولَ فَرَّقْتَ بَيْنَ بَن۪ٓي اِسْرَٓائ۪لَ وَلَمْ تَرْقُبْ قَوْل۪ي", translation: "Harun: \"Ey annemin oğlu! Sakalımı ve saçımı tutma! Gerçek şu ki: ben senin, \"İsrailoğulları arasında ayrılık çıkardın, sözümü dinlemedin.\" demenden huşu ettim. dedi.", transliteration: "Kale yebneumme la te'huz bi lıhyeti ve la bi re'si, inni haşitu en tekule ferrakte beyne beni israile ve lem terkub kavli."},
  {id: 2443, sure_no: 95, ayet_no: 95, sure_adi: "Taha", text: "قَالَ فَمَا خَطْبُكَ يَا سَامِرِيُّ", translation: "Musa: \"Ey Samiri! Sen ne yapmak istiyorsun?\"", transliteration: "Kale fe ma hatbuke ya samiriyy."},
  {id: 2444, sure_no: 96, ayet_no: 96, sure_adi: "Taha", text: "قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوا بِه۪ فَقَبَضْتُ قَبْضَةً مِنْ اَثَرِ الرَّسُولِ فَنَبَذْتُهَا وَكَذٰلِكَ سَوَّلَتْ ل۪ي نَفْس۪ي", translation: "Samiri: \"Ben, onların anlamadıkları şeyi anladım. Resulün öğretisinden az bir şey almıştım işte onu bıraktım. Bunu, bana nefsim hoş gösterdi.\" dedi.", transliteration: "Kale basurtu bi ma lem yabsuru bihi fe kabadtu kabdaten min eserir resuli fe nebeztuha ve kezalike sevvelet li nefsi."},
  {id: 2445, sure_no: 97, ayet_no: 97, sure_adi: "Taha", text: "قَالَ فَاذْهَبْ فَاِنَّ لَكَ فِي الْحَيٰوةِ اَنْ تَقُولَ لَا مِسَاسَۖ وَاِنَّ لَكَ مَوْعِداً لَنْ تُخْلَفَهُۚ  وَانْظُرْ اِلٰٓى اِلٰهِكَ الَّذ۪ي ظَلْتَ عَلَيْهِ عَاكِفاًۜ لَنُحَرِّقَنَّهُ ثُمَّ لَنَنْسِفَنَّهُ فِي الْيَمِّ نَسْفاً", translation: "Musa: \"Çek git karşımdan! Artık sen, hayatın boyunca toplumdan dışlanacaksın. Ayrıca senin için, asla kurtulamayacağın bir buluşma günü var. Bir de kendini adadığın ilahına bak! Onu kesinlikle yakacağız. Sonra da savurup suya saçacağız.\" dedi.", transliteration: "Kale fezheb fe inne leke fil hayati en tekule la misase ve inne leke mev'ıden len tuhlefeh, vanzur ila ilahikellezi zalte aleyhi akifa, le nuharrikannehu summe le nensifennehu fil yemmi nesfa."},
  {id: 2446, sure_no: 98, ayet_no: 98, sure_adi: "Taha", text: "اِنَّـمَٓا اِلٰهُكُمُ اللّٰهُ الَّذ۪ي لَٓا اِلٰهَ اِلَّا هُوَۜ وَسِعَ كُلَّ شَيْءٍ عِلْماً", translation: "\"Sizin ilahınız yalnızca Allah'tır. O'ndan başka ilah yoktur. Kuşkusuz, O'nun ilmi her şeyi kuşatmıştır.\"", transliteration: "İnnema ilahukumullahullezi la ilahe illa huv, vesia kulle şey'in ilma."},
  {id: 2447, sure_no: 99, ayet_no: 99, sure_adi: "Taha", text: "كَذٰلِكَ نَقُصُّ عَلَيْكَ مِنْ اَنْـبَٓاءِ مَا قَدْ سَبَقَۚ وَقَدْ اٰتَيْنَاكَ مِنْ لَدُنَّا ذِكْراًۚ", translation: "İşte böylece geçmişlerin haberlerinden sana anlatıyoruz. Gerçek şu ki sana katımızdan bir zikir verdik.", transliteration: "Kezalike nakussu aleyke min enbai ma kad sebak, ve kad ateynake min ledunna zikra."},
  {id: 2448, sure_no: 100, ayet_no: 100, sure_adi: "Taha", text: "مَنْ اَعْرَضَ عَنْهُ فَاِنَّهُ يَحْمِلُ يَوْمَ الْقِيٰمَةِ وِزْراًۙ", translation: "Kim ondan yüz çevirirse, o kıyamet günü için ağır bir yük yüklenir.", transliteration: "Men a'rada anhu fe innehu yahmilu yevmel kıyameti vizra."},
  {id: 2449, sure_no: 101, ayet_no: 101, sure_adi: "Taha", text: "خَالِد۪ينَ ف۪يهِۜ وَسَٓاءَ لَهُمْ يَوْمَ الْقِيٰمَةِ حِمْلاًۙ", translation: "Onlar, orada sürekli kalacaklardır. Ve yüklendikleri yük, Kıyamet gününde onlar için ne kötüdür.", transliteration: "Halidine fih, ve sae lehum yevmel kıyameti hımla."},
  {id: 2450, sure_no: 102, ayet_no: 102, sure_adi: "Taha", text: "يَوْمَ يُنْفَخُ فِي الصُّورِ وَنَحْشُرُ الْمُجْرِم۪ينَ يَوْمَئِذٍ زُرْقاًۚ", translation: "O gün, Sur'a üfürülür. O gün, mücrimleri şaşkınlıktan gözleri donuklaşmış olarak toplayacağız.", transliteration: "Yevme yunfehu fis suri ve nahşurul mucrimine yevme izin zurka."},
  {id: 2451, sure_no: 103, ayet_no: 103, sure_adi: "Taha", text: "يَتَخَافَتُونَ بَيْنَهُمْ اِنْ لَبِثْتُمْ اِلَّا عَشْراً", translation: "\"Dünya hayatınız sadece on gün sürdü değil mi?\" diye aralarında fısıldaşırlar.", transliteration: "Yetehafetune beynehum in lebistum illa aşra."},
  {id: 2452, sure_no: 104, ayet_no: 104, sure_adi: "Taha", text: "نَحْنُ اَعْلَمُ بِمَا يَقُولُونَ اِذْ يَقُولُ اَمْثَلُهُمْ طَر۪يقَةً اِنْ لَبِثْتُمْ اِلَّا يَوْماً۟", translation: "Biz, aralarında ne konuştuklarını çok iyi biliriz. Onlardan isabetli olanı, \"Olsa olsa bir gün kaldınız.\" diyecek.", transliteration: "Nahnu a'lemu bima yekulune iz yekulu emseluhum tarikaten in lebistum illa yevma."},
  {id: 2453, sure_no: 105, ayet_no: 105, sure_adi: "Taha", text: "وَيَسْـَٔلُونَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبّ۪ي نَسْفاًۙ", translation: "Sana dağlardan soruyorlar. De ki: \"Rabb'im onları ufalayıp savuracak.\"", transliteration: "Ve yes'eluneke anil cibali fe kul yensifuha rabbi nesfa."},
  {id: 2454, sure_no: 106, ayet_no: 106, sure_adi: "Taha", text: "فَيَذَرُهَا قَاعاً صَفْصَفاًۙ", translation: "\"Onları dümdüz araziye çevirecektir.\"", transliteration: "Fe yezeruha kaan safsafa."},
  {id: 2455, sure_no: 107, ayet_no: 107, sure_adi: "Taha", text: "لَا تَرٰى ف۪يهَا عِوَجاً وَلَٓا اَمْتاً", translation: "Orada ne bir çukur ne de bir tümsek göremezsin.", transliteration: "La tera fiha ivecen ve la emta."},
  {id: 2456, sure_no: 108, ayet_no: 108, sure_adi: "Taha", text: "يَوْمَئِذٍ يَتَّبِعُونَ الدَّاعِيَ لَا عِوَجَ لَهُۚ وَخَشَعَتِ الْاَصْوَاتُ لِلرَّحْمٰنِ فَلَا تَسْمَعُ اِلَّا هَمْساً", translation: "O Gün, hiçbir tarafa sapmadan, davetçiye uyarlar. Rahman'a karşı sesler kısılmıştır. Fısıltıdan başka bir şey işitemezsin.", transliteration: "Yevme izin yettebiuned daıye la ivece leh, ve haşeatil asvatu lir rahmani fe la tesmeu illa hemsa."},
  {id: 2457, sure_no: 109, ayet_no: 109, sure_adi: "Taha", text: "يَوْمَئِذٍ لَا تَنْفَعُ الشَّفَاعَةُ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَرَضِيَ لَهُ قَوْلاً", translation: "O Gün, şefaat fayda vermez. Rahman'ın kendisine izin verdiği ve sözünden hoşnut olduğu kimse hariç.", transliteration: "Yevme izin la tenfauş şefaatu illa men ezine lehur rahmanu ve radıye lehu kavla."},
  {id: 2458, sure_no: 110, ayet_no: 110, sure_adi: "Taha", text: "يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْ وَلَا يُح۪يطُونَ بِه۪ عِلْماً", translation: "Onların önlerindekini ve arkalarındakini bilir. Ama onlar, O'nu bilgice kavrayamazlar.", transliteration: "Ya'lemu ma beyne eydihim ve ma halfehum ve la yuhitune bihi ılma."},
  {id: 2459, sure_no: 111, ayet_no: 111, sure_adi: "Taha", text: "وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِۜ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْماً", translation: "Yüzler, Hayy ve Kayyum olanın önünde eğilmiştir. Zulüm taşıyıcılar perişan olmuştur.", transliteration: "Ve anetil vucuhu lil hayyil kayyum, ve kad habe men hamele zulma."},
  {id: 2460, sure_no: 112, ayet_no: 112, sure_adi: "Taha", text: "وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا يَخَافُ ظُلْماً وَلَا هَضْماً", translation: "İman Eden biri olarak salihatı yapan kimse ise haksızlığa uğramaktan da hakkının yeneceğinden de korkmaz.", transliteration: "Ve men ya'mel mines salihati ve huve mu'minun fe la yehafu zulmen ve la hadma."},
  {id: 2461, sure_no: 113, ayet_no: 113, sure_adi: "Taha", text: "وَكَذٰلِكَ اَنْزَلْنَاهُ قُرْاٰناً عَرَبِياًّ وَصَرَّفْنَا ف۪يهِ مِنَ الْوَع۪يدِ لَعَلَّهُمْ يَتَّقُونَ اَوْ يُحْدِثُ لَهُمْ ذِكْراً", translation: "Sana onu Arapça bir Kur'an olarak indirdik. O'nda, uyarıları teker teker bildirdik. Böylece takva sahibi olurlar veya onlar için öğüt veren bir söz olur.", transliteration: "Ve kezalike enzelnahu kur'anen arabiyyen ve sarrafna fihi minel vaidi leallehum yettekune ev yuhdisu lehum zikra."},
  {id: 2462, sure_no: 114, ayet_no: 114, sure_adi: "Taha", text: "فَتَعَالَى اللّٰهُ الْمَلِكُ الْحَقُّۚ وَلَا تَعْجَلْ بِالْقُرْاٰنِ مِنْ قَبْلِ اَنْ يُقْضٰٓى اِلَيْكَ وَحْيُهُۘ وَقُلْ رَبِّ زِدْن۪ي عِلْماً", translation: "Her şeyin gerçek egemeni olan Allah, yücedir. Kur'an'ın sana vahyedilmesi bitirilmeden önce acele etme. \"Rabb'im bana bilgiyi arttır.\" de.", transliteration: "Fe tealallahul melikul hak, ve la ta'cel bil kur'ani min kabli en yukda ileyke vahyuhu ve kul rabbi zidni ılma."},
  {id: 2463, sure_no: 115, ayet_no: 115, sure_adi: "Taha", text: "وَلَقَدْ عَهِدْنَٓا اِلٰٓى اٰدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهُ عَزْماً۟", translation: "Ant olsun ki Biz, bundan önce Adem'e ahit verdik, ancak o unuttu. Onu azimli bulmadık.", transliteration: "Ve lekad ahidna ila ademe min kablu fe nesiye ve lem necid lehu azma."},
  {id: 2464, sure_no: 116, ayet_no: 116, sure_adi: "Taha", text: "وَاِذْ قُلْنَا لِلْمَلٰٓئِكَةِ اسْجُدُوا لِاٰدَمَ فَسَجَدُٓوا اِلَّٓا اِبْل۪يسَۜ اَبٰى", translation: "Hani! Meleklere: \"Adem'e secde edin.\" demiştik. Hemen secde etmişlerdi, İblis ise bundan kaçınmıştı.", transliteration: "Ve iz kulna lil melaiketiscudu li ademe fe secedu illa iblis, eba."},
  {id: 2465, sure_no: 117, ayet_no: 117, sure_adi: "Taha", text: "فَقُلْنَا يَٓا اٰدَمُ اِنَّ هٰذَا عَدُوٌّ لَكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقٰى", translation: "Bunun üzerine Biz de: \"Ey Adem! Kuşkusuz bu sana ve eşine düşmandır. Sakın sizi cennetten çıkarmasın. Sonra mutsuz olursun.\" dedik.", transliteration: "Fe kulna ya ademu inne haza aduvvun leke ve li zevcike fe la yuhricennekuma minel cenneti fe teşka."},
  {id: 2466, sure_no: 118, ayet_no: 118, sure_adi: "Taha", text: "اِنَّ لَكَ اَلَّا تَجُوعَ ف۪يهَا وَلَا تَعْرٰىۙ", translation: "\"Senin için orada acıkmak ve açıkta kalmak yoktur.\"", transliteration: "İnne leke ella tecua fiha ve la ta'ra."},
  {id: 2467, sure_no: 119, ayet_no: 119, sure_adi: "Taha", text: "وَاَنَّكَ لَا تَظْمَؤُ۬ا ف۪يهَا وَلَا تَضْحٰى", translation: "\"Orada susuz kalmazsın ve sıcaktan zarar görmezsin.\"", transliteration: "Ve enneke la tazmeu fiha ve la tadha."},
  {id: 2468, sure_no: 120, ayet_no: 120, sure_adi: "Taha", text: "فَوَسْوَسَ اِلَيْهِ الشَّيْطَانُ قَالَ يَٓا اٰدَمُ هَلْ اَدُلُّكَ عَلٰى شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَا يَبْلٰى", translation: "Sonra şeytan ona vesvese verdi. Dedi ki: \"Ey Adem! Sana ebedilik ağacı ve yok olmayacak bir egemenlik için yol göstereyim mi?\"", transliteration: "Fe vesvese ileyhiş şeytanu kale ya ademu hel edulluke ala şeceretil huldi ve mulkin la yebla."},
  {id: 2469, sure_no: 121, ayet_no: 121, sure_adi: "Taha", text: "فَاَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِۘ وَعَصٰٓى اٰدَمُ رَبَّهُ فَغَوٰىۖ", translation: "Bunun üzerine ikisi de ondan yediler. Hemen çirkinlikleri kendilerine görünüverdi. Cennet yaprağından örtmeye başladılar. Adem, Rabb'ine asilik edip yanıldı.", transliteration: "Fe ekela minha fe bedet lehuma sev'atuhuma ve tafıka yahsıfani aleyhima min varakıl cenneti ve asa ademu rabbehu fe gava."},
  {id: 2470, sure_no: 122, ayet_no: 122, sure_adi: "Taha", text: "ثُمَّ اجْتَبٰيهُ رَبُّهُ فَتَابَ عَلَيْهِ وَهَدٰى", translation: "Sonra Rabbi onu seçti, tevbesini kabul etti. Ve ona doğru yolu gösterdi.", transliteration: "Summectebahu rabbuhu fe tabe aleyhi ve heda."},
  {id: 2471, sure_no: 123, ayet_no: 123, sure_adi: "Taha", text: "قَالَ اهْبِطَا مِنْهَا جَم۪يعاً بَعْضُكُمْ لِبَعْضٍ عَدُوٌّۚ فَاِمَّا يَأْتِيَنَّكُمْ مِنّ۪ي هُدًى فَمَنِ اتَّـبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشْقٰى", translation: "Dedi ki: \"Hepiniz oradan ayrılın.\" Bir kısmınız bir kısmınıza düşmansınız. Benden size bir yol gösterici geldiği zaman, kim yol göstericime uyarsa; işte o sapkınlığa düşmez ve mutsuz olmaz.\"", transliteration: "Kalehbita minha cemian ba'dukum li ba'dın aduvv, fe imma ye'tiyennekum minni huden fe menittebea hudaye fe la yadıllu ve la yeşka."},
  {id: 2472, sure_no: 124, ayet_no: 124, sure_adi: "Taha", text: "وَمَنْ اَعْرَضَ عَنْ ذِكْر۪ي فَاِنَّ لَهُ مَع۪يشَةً ضَنْكاً وَنَحْشُرُهُ يَوْمَ الْقِيٰمَةِ اَعْمٰى", translation: "Kim Ben'im zikrimden yüz çevirirse, bilmelidir ki onun için bunalımlı bir yaşam vardır. Kıyamet Günü de onu kör olarak haşrederiz.", transliteration: "Ve men a'rada an zikri fe inne lehu maişeten danken ve nahşuruhu yevmel kıyameti a'ma."},
  {id: 2473, sure_no: 125, ayet_no: 125, sure_adi: "Taha", text: "قَالَ رَبِّ لِمَ حَشَرْتَـن۪ٓي اَعْمٰى وَقَدْ كُنْتُ بَص۪يراً", translation: "\"Rabb'im, beni neden kör olarak haşrettin? Oysa dünyada iken gören biriydim.\" der.", transliteration: "Kale rabbi lime haşerteni a'ma ve kad kuntu basira."},
  {id: 2474, sure_no: 126, ayet_no: 126, sure_adi: "Taha", text: "قَالَ كَذٰلِكَ اَتَتْكَ اٰيَاتُنَا فَـنَس۪يتَهَاۚ وَكَذٰلِكَ الْيَوْمَ تُنْسٰى", translation: "\"Bu böyledir, ayetlerimiz sana geldi ancak onları dikkate almadın; bugün de sen terk edileceksin.\" dedi.", transliteration: "Kale kezalike etetke ayatuna fe nesiteha, ve kezalikel yevme tunsa."},
  {id: 2475, sure_no: 127, ayet_no: 127, sure_adi: "Taha", text: "وَكَذٰلِكَ نَجْز۪ي مَنْ اَسْرَفَ وَلَمْ يُؤْمِنْ بِاٰيَاتِ رَبِّه۪ۜ وَلَعَذَابُ الْاٰخِرَةِ اَشَدُّ وَاَبْقٰى", translation: "Haddi aşanları ve Rabb'inin ayetlerine iman etmeyenleri işte böyle cezalandırırız. Ve ahiret azabı daha şiddetli ve daha kalıcıdır.", transliteration: "Ve kezalike neczi men esrefe ve lem yu'min bi ayati rabbih, ve le azabul ahıreti eşeddu ve ebka."},
  {id: 2476, sure_no: 128, ayet_no: 128, sure_adi: "Taha", text: "اَفَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ يَمْشُونَ ف۪ي مَسَاكِنِهِمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِاُو۬لِي النُّهٰى۟", translation: "Şimdi yurtlarında gezip dolaştıkları nesilleri yok etmiş olmamız, onlar için yol gösterici olmadı mı? Kuşku yok ki bunda kötülükten alıkoyan bir akıl için nice kanıtlar vardır.", transliteration: "E fe lem yehdi lehum kem ehlekna kablehum minel kuruni yemşune fi mesakinihim, inne fi zalike le ayatin li ulin nuha."},
  {id: 2477, sure_no: 129, ayet_no: 129, sure_adi: "Taha", text: "وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَكَانَ لِزَاماً وَاَجَلٌ مُسَمًّىۜ", translation: "Ve eğer Rabb'inden daha önce kararlaştırılmış bir hüküm ve adı konmuş bir süre olmasaydı elbette ceza kaçınılmaz olurdu.", transliteration: "Ve lev la kelimetun sebekat min rabbike le kane lizamen ve ecelun musemma."},
  {id: 2478, sure_no: 130, ayet_no: 130, sure_adi: "Taha", text: "فَاصْبِرْ عَلٰى مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ غُرُوبِهَاۚ وَمِنْ اٰنَٓائِ الَّيْلِ فَسَبِّـحْ وَاَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضٰى", translation: "O halde söylenen şeylere sabret! Ve Rabb'ini, Güneş'in doğmasından önce ve batmasından önce ve gecenin bir kısmında ve gündüzün taraflarında hamd ile tesbih et. Umulur ki böylece O'nun hoşnutluğunu kazanırsın.", transliteration: "Fasbir ala ma yekulune ve sebbih bi hamdi rabbike kable tuluış şemsi ve kable gurubiha, ve min anail leyli fe sebbih ve etrafen nehari lealleke terda."},
  {id: 2479, sure_no: 131, ayet_no: 131, sure_adi: "Taha", text: "وَلَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِه۪ٓ اَزْوَاجاً مِنْهُمْ زَهْرَةَ الْحَيٰوةِ الدُّنْيَا لِنَفْتِنَهُمْ ف۪يهِۜ وَرِزْقُ رَبِّكَ خَيْرٌ وَاَبْقٰى", translation: "Onlardan bazı zümrelere, kendilerini sınamak için, dünya hayatının süsü olarak verdiğimiz şeylere imrenme. Rabb'inin rızkı daha hayırlıdır ve kalıcıdır.", transliteration: "Ve la temuddenne ayneyke ila ma metta'na bihi ezvacen minhum zehretel hayatid dunya li neftinehum fih, ve rızku rabbike hayrun ve ebka."},
  {id: 2480, sure_no: 132, ayet_no: 132, sure_adi: "Taha", text: "وَأْمُرْ اَهْلَكَ بِالصَّلٰوةِ وَاصْطَبِرْ عَلَيْهَاۜ لَا نَسْـَٔلُكَ رِزْقاًۜ نَحْنُ نَرْزُقُكَۜ وَالْعَاقِبَةُ لِلتَّقْوٰى", translation: "Ehline salatı buyur. Kendin de onun üzerinde sabırlı ol. Senden rızık istemiyoruz. Seni, Biz rızıklandırıyoruz. Sonuç takva içindir.", transliteration: "Ve'mur ehleke bis salati vastabir aleyha, la nes'eluke rızka, nahnu nerzukuk, vel akıbetu lit takva."},
  {id: 2481, sure_no: 133, ayet_no: 133, sure_adi: "Taha", text: "وَقَالُوا لَوْلَا يَأْت۪ينَا بِاٰيَةٍ مِنْ رَبِّه۪ۜ اَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِي الصُّحُفِ الْاُو۫لٰى", translation: "\"Rabb'inden bize bir ayet getirse ya!\" dediler. Onlara, önceki suhuflarda yer alan kanıt içeren açıklayıcı bilgiler ulaşmadı mı?", transliteration: "Ve kalu lev la ye'tina bi ayetin min rabbih, e ve lem te'tihim beyyinetu ma fis suhufil ula."},
  {id: 2482, sure_no: 134, ayet_no: 134, sure_adi: "Taha", text: "وَلَوْ اَنَّٓا اَهْلَكْنَاهُمْ بِعَذَابٍ مِنْ قَبْلِه۪ لَقَالُوا رَبَّنَا لَوْلَٓا اَرْسَلْتَ اِلَيْنَا رَسُولاً فَنَتَّبِعَ اٰيَاتِكَ مِنْ قَبْلِ اَنْ نَذِلَّ وَنَخْزٰى", translation: "Ve eğer Biz, ondan önce bir azap ile onları yok etseydik, \"Ey Rabb'imiz! Bize bir Resul gönderseydin de hor ve rezil olmadan önce Sen'in ayetlerine uysaydık ya!\" diyeceklerdi.", transliteration: "Ve lev enna ehleknahum bi azabin min kablihi le kalu rabbena lev la erselte ileyna resulen fe nettebia ayatike min kabli en nezille ve nahza."},
  {id: 2483, sure_no: 135, ayet_no: 135, sure_adi: "Taha", text: "قُلْ كُلٌّ مُتَرَبِّصٌ فَتَرَبَّصُواۚ فَسَتَعْلَمُونَ مَنْ اَصْحَابُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدٰى", translation: "De ki: \"Herkes beklemektedir. O halde siz de bekleyin. Kuşku yok ki kimin düzgün yolda olduğunu, kimin doğru yolu bulduğunu yakında bileceksiniz.\"", transliteration: "Kul kullun muterebbisun fe terabbesu, fe se ta'lemune men ashabus sıratıs seviyyi ve menihteda."},
  {id: 2484, sure_no: 1, ayet_no: 1, sure_adi: "Enbiya", text: "اِقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ ف۪ي غَفْلَةٍ مُعْرِضُونَۚ", translation: "İnsanlar için hesap vakti yaklaştı. Ne var ki onlar gaflet içinde aldırmıyorlar.", transliteration: "Ikterebe lin nasi hisabuhum ve hum fi gafletin mu'ridun."},
  {id: 2485, sure_no: 2, ayet_no: 2, sure_adi: "Enbiya", text: "مَا يَأْت۪يهِمْ مِنْ ذِكْرٍ مِنْ رَبِّهِمْ مُحْدَثٍ اِلَّا اسْتَمَعُوهُ وَهُمْ يَلْعَبُونَۙ", translation: "Rabb'lerinden, kendilerine gelen söze bürünmüş her yeni öğüdü, sadece alay ederek dinlerler.", transliteration: "Ma ye'tihim min zikrin min rabbihim muhdesin illestemeuhu ve hum yel'abun."},
  {id: 2486, sure_no: 3, ayet_no: 3, sure_adi: "Enbiya", text: "لَاهِيَةً قُلُوبُهُمْۜ وَاَسَرُّوا النَّجْوٰىۗ اَلَّذ۪ينَ ظَلَمُواۗ هَلْ هٰذَٓا اِلَّا بَشَرٌ مِثْلُكُمْۚ اَفَتَأْتُونَ السِّحْرَ وَاَنْتُمْ تُبْصِرُونَ", translation: "O haksızlık yapanlar, akıllarınca önemsemeyerek ve kendi aralarında fısıldayarak: \"Bu, sizin gibi bir beşerden başka bir şey midir? Yoksa siz, göz göre göre büyüye mi kapılacaksınız?\" diyorlar.", transliteration: "Lahiyeten kulubuhum ve eserrun necvellezine zalemu hel haza illa beşerun mislukum, e fe te'tunes sihre ve entum tubsırun."},
  {id: 2487, sure_no: 4, ayet_no: 4, sure_adi: "Enbiya", text: "قَالَ رَبّ۪ي يَعْلَمُ الْقَوْلَ فِي السَّمَٓاءِ وَالْاَرْضِۘ وَهُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "\"Rabb'im, gökte ve yerde olan her sözü bilir. O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.\" dedi.", transliteration: "Kale rabbi ya'lemul kavle fis semai vel ardı ve huves semiul alim."},
  {id: 2488, sure_no: 5, ayet_no: 5, sure_adi: "Enbiya", text: "بَلْ قَالُٓوا اَضْغَاثُ اَحْلَامٍ بَلِ افْتَرٰيهُ بَلْ هُوَ شَاعِرٌۚ فَلْيَأْتِنَا بِاٰيَةٍ كَمَٓا اُرْسِلَ الْاَوَّلُونَ", translation: "\"Hayır, bunlar karmakarışık düşlerdir. Hayır, belki onu uydurdu. Hayır, o bir şairdir. Öyleyse öncekilere gönderildiği gibi bize bir ayet getirsin.\" dediler.", transliteration: "Bel kalu adgasu ahlamin belifterahu bel huve şaır, fel ye'tina bi ayetin kema ursilel evvelun."},
  {id: 2489, sure_no: 6, ayet_no: 6, sure_adi: "Enbiya", text: "مَٓا اٰمَنَتْ قَبْلَهُمْ مِنْ قَرْيَةٍ اَهْلَكْنَاهَاۚ اَفَهُمْ يُؤْمِنُونَ", translation: "Onlardan önce yok ettiğimiz hiçbir kent iman etmemişti. Şimdi bunlar mı iman edecekler?", transliteration: "Ma amenet kablehum min karyetin ehleknaha, e fe hum yu'minun."},
  {id: 2490, sure_no: 7, ayet_no: 7, sure_adi: "Enbiya", text: "وَمَٓا اَرْسَلْنَا قَبْلَكَ اِلَّا رِجَالاً نُوح۪ٓي اِلَيْهِمْ فَسْـَٔلُٓوا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُونَ", translation: "Senden önce de vahyimizi iletmede elçi olarak insandan başkasını görevlendirmedik. Eğer bilmiyorsanız, zikir ehline sorun.", transliteration: "Ve ma erselna kableke illa ricalen nuhi ileyhim fes'elu ehlez zikri in kuntum la ta'lemun."},
  {id: 2491, sure_no: 8, ayet_no: 8, sure_adi: "Enbiya", text: "وَمَا جَعَلْنَاهُمْ جَسَداً لَا يَأْكُلُونَ الطَّعَامَ وَمَا  كَانُوا خَالِد۪ينَ", translation: "Biz, onları yemeye, içmeye ihtiyaç duymayan varlıklar yapmadık. Ve onlar ölümsüz de değillerdi.", transliteration: "Ve ma cealnahum ceseden la ye'kulunet taame ve ma kanu halidin."},
  {id: 2492, sure_no: 9, ayet_no: 9, sure_adi: "Enbiya", text: "ثُمَّ صَدَقْنَاهُمُ الْوَعْدَ فَاَنْجَيْنَاهُمْ وَمَنْ نَشَٓاءُ وَاَهْلَكْنَا الْمُسْرِف۪ينَ", translation: "Sonra onlara verdiğimiz söze bağlı kaldık. Onları ve hak eden kimseleri kurtardık. Müsrifleri ise yok ettik.", transliteration: "Summe sadaknahumul va'de fe enceynahum ve men neşau ve ehleknel musrifin."},
  {id: 2493, sure_no: 10, ayet_no: 10, sure_adi: "Enbiya", text: "لَقَدْ اَنْزَلْـنَٓا اِلَيْكُمْ كِتَاباً ف۪يهِ ذِكْرُكُمْۜ اَفَلَا تَعْقِلُونَ۟", translation: "Ant olsun ki, gerçekten içinde sizin öğüdünüz olan bir kitap indirdik. Hala aklınızı kullanmayacak mısınız?", transliteration: "Lekad enzelna ileykum kitaben fihi zikrukum, e fe la ta'kılun."},
  {id: 2494, sure_no: 11, ayet_no: 11, sure_adi: "Enbiya", text: "وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَاَنْشَأْنَا بَعْدَهَا قَوْماً اٰخَر۪ينَ", translation: "Ve Biz, zalim olan kentlerden nicelerini kırıp geçirdik. Onlardan sonra başka bir halk getirdik.", transliteration: "Ve kem kasamna min karyetin kanet zalimeten ve enşe'na ba'deha kavmen aharin."},
  {id: 2495, sure_no: 12, ayet_no: 12, sure_adi: "Enbiya", text: "فَلَمَّٓا اَحَسُّوا بَأْسَنَٓا اِذَا هُمْ مِنْهَا يَرْكُضُونَۜ", translation: "Öyle ki onlar azabımızı hissettikleri zaman, ondan kaçmaya çalışıyorlardı.", transliteration: "Fe lemma ehassu be'sena iza hum minha yerkudun."},
  {id: 2496, sure_no: 13, ayet_no: 13, sure_adi: "Enbiya", text: "لَا تَرْكُضُوا وَارْجِعُٓوا اِلٰى مَٓا اُتْرِفْتُمْ ف۪يهِ وَمَسَاكِنِكُمْ لَعَلَّكُمْ تُسْـَٔلُونَ", translation: "Boşuna kaçmayın, refaha kavuşturulduğunuz şeylere ve evlerinize geri dönün; çünkü hesaba çekileceksiniz.", transliteration: "La terkudu verciu ila ma utriftum fihi ve mesakinikum leallekum tus'elun."},
  {id: 2497, sure_no: 14, ayet_no: 14, sure_adi: "Enbiya", text: "قَالُوا يَا وَيْلَنَٓا اِنَّا كُنَّا ظَالِم۪ينَ", translation: "\"Yazıklar olsun bize! Biz zalimlermişiz.\" dediler.", transliteration: "Kalu ya veylena inna kunna zalimin."},
  {id: 2498, sure_no: 15, ayet_no: 15, sure_adi: "Enbiya", text: "فَمَا زَالَتْ تِلْكَ دَعْوٰيهُمْ حَتّٰى جَعَلْنَاهُمْ حَص۪يداً خَامِد۪ينَ", translation: "Biçilmiş ekin gibi pörsüyüp, cansız kalıncaya kadar bu çağrıları son bulmadı.", transliteration: "Fe ma zalet tilke da'vahum hatta cealnahum hasiden hamidin."},
  {id: 2499, sure_no: 16, ayet_no: 16, sure_adi: "Enbiya", text: "وَمَا خَلَقْنَا السَّمَٓاءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا لَاعِب۪ينَ", translation: "Biz; yeri, göğü ve ikisinin arasındakileri eğlence olsun diye yaratmadık.", transliteration: "Ve ma halaknes semae vel arda ve ma beynehuma laıbin."},
  {id: 2500, sure_no: 17, ayet_no: 17, sure_adi: "Enbiya", text: "لَوْ اَرَدْنَٓا اَنْ نَتَّخِذَ لَهْواً لَاتَّخَذْنَاهُ مِنْ لَدُنَّاۗ اِنْ كُنَّا فَاعِل۪ينَ", translation: "Eğer eğlence edinmek isteseydik, elbette onu kendi şanımıza uygun yapardık. Eğer yapacak olsak böyle yapardık.", transliteration: "Lev eredna en nettehıze lehven lettehaznahu min ledunna in kunna fa'ılin."},
  {id: 2501, sure_no: 18, ayet_no: 18, sure_adi: "Enbiya", text: "بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهُ فَاِذَا هُوَ زَاهِقٌۜ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُونَ", translation: "Aksine, Biz; Hakk'ı, Batıl'a karşı ortaya koyarız da onu mahveder. Böylece Batıl yok olur. Nitelediğiniz şeylerden dolayı size yazıklar olsun.", transliteration: "Bel nakzifu bil hakkı alel batıli fe yedmeguhu fe iza huve zahik, ve lekumul veylu mimma tasıfun."},
  {id: 2502, sure_no: 19, ayet_no: 19, sure_adi: "Enbiya", text: "وَلَهُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَمَنْ عِنْدَهُ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِه۪ وَلَا يَسْتَحْسِرُونَۚ", translation: "Göklerde ve yerde ne varsa O'nundur. O'nun yanında bulunanlar O'na kulluk etmekten büyüklenmez ve usanmazlar;", transliteration: "Ve lehu men fis semavati vel ard, ve men indehu la yestekbirune an ıbadetihi ve la yestahsirun."},
  {id: 2503, sure_no: 20, ayet_no: 20, sure_adi: "Enbiya", text: "يُسَبِّحُونَ الَّيْلَ وَالنَّهَارَ لَا يَفْتُرُونَ", translation: "Gece, gündüz ara vermeksizin tesbih ederler.", transliteration: "Yusebbihunel leyle ven nehare la yefturun."},
  {id: 2504, sure_no: 21, ayet_no: 21, sure_adi: "Enbiya", text: "اَمِ اتَّخَذُٓوا اٰلِهَةً مِنَ الْاَرْضِ هُمْ يُنْشِرُونَ", translation: "Yoksa yeryüzünde birtakım ilahlar edindiler de ölüleri onlar mı diriltecek?", transliteration: "Emittehazu aliheten minel ardı hum yunşirun."},
  {id: 2505, sure_no: 22, ayet_no: 22, sure_adi: "Enbiya", text: "لَوْ كَانَ ف۪يهِمَٓا اٰلِهَةٌ اِلَّا اللّٰهُ لَفَسَدَتَاۚ فَسُبْحَانَ اللّٰهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ", translation: "Eğer ikisinde de Allah'tan başka ilahlar olsaydı, ikisi de kesinlikle fesada uğrardı. Arşın Rabb'i olan Allah, onların niteledikleri şeylerden münezzehtir.", transliteration: "Lev kane fihima alihetun illallahu le fesedeta, fe subhanallahi rabbil arşi amma yasıfun."},
  {id: 2506, sure_no: 23, ayet_no: 23, sure_adi: "Enbiya", text: "لَا يُسْـَٔلُ عَمَّا يَفْعَلُ وَهُمْ يُسْـَٔلُونَ", translation: "O, yaptıklarından dolayı sorgulanamaz. Ama onlar yaptıklarının hesabını vereceklerdir.", transliteration: "La yus'elu amma yef'alu ve hum yus'elun."},
  {id: 2507, sure_no: 24, ayet_no: 24, sure_adi: "Enbiya", text: "اَمِ اتَّخَذُوا مِنْ دُونِه۪ٓ اٰلِهَةًۜ قُلْ هَاتُوا بُرْهَانَكُمْۚ هٰذَا ذِكْرُ مَنْ مَعِيَ وَذِكْرُ مَنْ قَبْل۪يۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَۙ الْحَقَّ فَهُمْ مُعْرِضُونَ", translation: "Yoksa O'nun yanı sıra başka ilahlar mı edindiler? De ki: \"Burhanınızı getirin. Bu benimle beraber olanların ve benden öncekilerin öğüdüdür.\" Fakat onların çoğu gerçeği bilmezler ve onlar, ondan yüz çevirenlerdir.", transliteration: "Emittehazu min dunihi aliheh, kul hatu burhanekum, haza zikru men maiye ve zikru men kabli, bel ekseruhum la ya'lemunel hakka fehum mu'ridun."},
  {id: 2508, sure_no: 25, ayet_no: 25, sure_adi: "Enbiya", text: "وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ اِلَّا نُوح۪ٓي اِلَيْهِ اَنَّهُ لَٓا اِلٰهَ اِلَّٓا اَنَا۬ فَاعْبُدُونِ", translation: "Senden önce hiçbir Resul göndermedik ki, kendisine, \"Benden başka ilah yoktur. Onun için Bana kulluk edin.\" diye vahyetmiş olmayalım.", transliteration: "Ve ma erselna min kablike min resulin illa nuhi ileyhi ennehu la ilahe illa ene fa'budun."},
  {id: 2509, sure_no: 26, ayet_no: 26, sure_adi: "Enbiya", text: "وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَداً سُبْحَانَهُۜ بَلْ عِبَادٌ مُكْرَمُونَۙ", translation: "Ve onlar: \"Rahman çocuk edindi.\" dediler. O, bundan münezzehtir. Onlar, ikram olunmuş kullardır.", transliteration: "Ve kaluttehazer rahmanu veleden subhaneh, bel ıbadun mukremun."},
  {id: 2510, sure_no: 27, ayet_no: 27, sure_adi: "Enbiya", text: "لَا يَسْبِقُونَهُ بِالْقَوْلِ وَهُمْ بِاَمْرِه۪ يَعْمَلُونَ", translation: "Onlar, O'nun sözüne aykırı hareket edemezler. Onlar, yalnızca O'nun buyruğuna uyarlar.", transliteration: "La yesbikunehu bil kavli ve hum bi emrihi ya'melun."},
  {id: 2511, sure_no: 28, ayet_no: 28, sure_adi: "Enbiya", text: "يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُونَۙ اِلَّا لِمَنِ ارْتَضٰى وَهُمْ مِنْ خَشْيَتِه۪ مُشْفِقُونَ", translation: "Allah, onların bütün yapıp ettiklerini bilir. Ve onlar, rızasına ermiş olanlardan başkasına şefaat edemezler. Onlar, O'na duydukları derin saygıdan titrerler.", transliteration: "Ya'lemu ma beyne eydihim ve ma halfehum ve la yeşfeune illa li menirteda ve hum min haşyetihi muşfikun."},
  {id: 2512, sure_no: 29, ayet_no: 29, sure_adi: "Enbiya", text: "وَمَنْ يَقُلْ مِنْهُمْ اِنّ۪ٓي اِلٰهٌ مِنْ دُونِه۪ فَذٰلِكَ نَجْز۪يهِ جَهَنَّمَۜ  كَذٰلِكَ نَجْزِي الظَّالِم۪ينَ۟", translation: "Onlardan kim, \"O'nun yanı sıra ben de ilahım.\" derse, işte o zaman onu Cehennem'le cezalandırırız. Biz, zalimleri böyle cezalandırırız.", transliteration: "Ve men yekul minhum inni ilahun min dunihi fe zalike neczihi cehennem, kezalike necziz zalimin."},
  {id: 2513, sure_no: 30, ayet_no: 30, sure_adi: "Enbiya", text: "اَوَلَمْ يَرَ الَّذ۪ينَ كَفَرُٓوا اَنَّ السَّمٰوَاتِ وَالْاَرْضَ كَانَتَا رَتْقاً فَفَتَقْنَاهُمَاۜ وَجَعَلْنَا مِنَ الْمَٓاءِ كُلَّ شَيْءٍ حَيٍّۜ اَفَلَا يُؤْمِنُونَ", translation: "Kafirler; görmüyorlar mı gökler ve yer bitişikti? Biz onları ayırdık. Suyu hayat kaynağı kıldık. Buna rağmen hala inanmıyorlar mı?", transliteration: "E ve lem yerellezine keferu ennes semavati vel arda kaneta retkan fe fetaknahuma, ve cealna minel mai kulle şey'in hayy, e fe la yu'minun."},
  {id: 2514, sure_no: 31, ayet_no: 31, sure_adi: "Enbiya", text: "وَجَعَلْنَا فِي الْاَرْضِ رَوَاسِيَ اَنْ تَم۪يدَ بِهِمْ وَجَعَلْنَا ف۪يهَا فِجَاجاً سُبُلاً لَعَلَّهُمْ يَهْتَدُونَ", translation: "Ve Biz, onları sarsmasın diye yeryüzüne ağır baskılar yerleştirdik. Orada yol bulmaları için geçitler yaptık.", transliteration: "Ve cealna fil ardı revasiye en temide bihim ve cealna fiha ficacen subulen leallehum yehtedun."},
  {id: 2515, sure_no: 32, ayet_no: 32, sure_adi: "Enbiya", text: "وَجَعَلْنَا السَّمَٓاءَ سَقْفاً مَحْفُوظاًۚ وَهُمْ عَنْ اٰيَاتِهَا مُعْرِضُونَ", translation: "Gökyüzünü korunmuş bir tavan yaptık. Onlar hala ayetlerimden yüz çeviriyorlar.", transliteration: "Ve cealnes semae sakfen mahfuza, ve hum an ayatiha mu'ridun."},
  {id: 2516, sure_no: 33, ayet_no: 33, sure_adi: "Enbiya", text: "وَهُوَ الَّذ۪ي خَلَقَ الَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَۜ  كُلٌّ ف۪ي فَلَكٍ يَسْبَحُونَ", translation: "Geceyi ve gündüzü, Güneş'i ve Ay'ı yaratan O'dur. Onların her biri kendi yörüngesinde yüzmektedir.", transliteration: "Ve huvellezi halakal leyle ven nehare veş şemse vel kamer, kullun fi felekin yesbehun."},
  {id: 2517, sure_no: 34, ayet_no: 34, sure_adi: "Enbiya", text: "وَمَا جَعَلْنَا لِبَشَرٍ مِنْ قَبْلِكَ الْخُلْدَۜ اَفَا۬ئِنْ مِتَّ فَهُمُ الْخَالِدُونَ", translation: "Biz, senden önce de hiçbir beşeri ölümsüz kılmadık. Sen öleceksin de onlar süresiz mi yaşayacaklar?", transliteration: "Ve ma cealna li beşerin min kablikel huld, e fe in mitte fe humul halidun."},
  {id: 2518, sure_no: 35, ayet_no: 35, sure_adi: "Enbiya", text: "كُلُّ نَفْسٍ ذَٓائِقَةُ الْمَوْتِۜ وَنَبْلُوكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةًۜ وَاِلَيْنَا تُرْجَعُونَ", translation: "Her nefs ölümü tadıcıdır. Sizi sınav olarak, iyilik ve kötülükle sınarız. Ve siz, yalnız Bize döndürüleceksiniz.", transliteration: "Kullu nefsin zaikatul mevt, ve neblukum biş şerri vel hayri fitneh, ve ileyna turceun."},
  {id: 2519, sure_no: 36, ayet_no: 36, sure_adi: "Enbiya", text: "وَاِذَا رَاٰكَ الَّذ۪ينَ كَفَرُٓوا اِنْ يَتَّخِذُونَكَ اِلَّا هُزُواًۜ اَهٰذَا الَّذ۪ي يَذْكُرُ اٰلِهَتَكُمْۚ وَهُمْ بِذِكْرِ الرَّحْمٰنِ هُمْ كَافِرُونَ", translation: "Ve Kafirler seni gördükleri zaman: \"İlahlarınızı zikreden bu mu?\" diye alaya alıyorlar. Onlar, Rahman'ın zikrini küfredenlerdir.", transliteration: "Ve iza reakellezine keferu in yettehızuneke illa huzuva, e hazellezi yezkuru alihetekum, ve hum bi zikrir rahmani hum kafirun."},
  {id: 2520, sure_no: 37, ayet_no: 37, sure_adi: "Enbiya", text: "خُلِقَ الْاِنْسَانُ مِنْ عَجَلٍۜ سَاُر۪يكُمْ اٰيَات۪ي فَلَا تَسْتَعْجِلُونِ", translation: "İnsan aceleden yaratılmıştır. Size, günü gelince ayetlerimi göstereceğim. Acele etmemi istemeyin.", transliteration: "Hulikal insanu min acel, seurikum ayati fe la testa'cilun."},
  {id: 2521, sure_no: 38, ayet_no: 38, sure_adi: "Enbiya", text: "وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Eğer doğru söyleyenlerdenseniz bu uyarı ne zaman gerçekleşecek?\" diyorlar.", transliteration: "Ve yekulune meta hazel va'du in kuntum sadıkin."},
  {id: 2522, sure_no: 39, ayet_no: 39, sure_adi: "Enbiya", text: "لَوْ يَعْلَمُ الَّذ۪ينَ كَفَرُوا ح۪ينَ لَا يَكُفُّونَ عَنْ وُجُوهِهِمُ النَّارَ وَلَا عَنْ ظُهُورِهِمْ وَلَا هُمْ يُنْصَرُونَ", translation: "Kafirler, kendilerini her yönden saracak ateşi savamayacakları ve yardımsız kalacakları zamanı ah bir bilselerdi!", transliteration: "Lev ya'lemullezine keferu hine la yekuffune an vucuhihimun nare ve la an zuhurihim ve la hum yunsarun."},
  {id: 2523, sure_no: 40, ayet_no: 40, sure_adi: "Enbiya", text: "بَلْ تَأْت۪يهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَط۪يعُونَ رَدَّهَا وَلَا هُمْ يُنْظَرُونَ", translation: "Aslında, onlara ansızın gelecek ve onları şaşkına çevirecek. Artık onu geri çevirmeye güçleri yetmeyecek ve onlara süre de verilmeyecek.", transliteration: "Bel te'tihim bagteten fe tebhetuhum fe la yesteti'une reddeha ve la hum yunzarun."},
  {id: 2524, sure_no: 41, ayet_no: 41, sure_adi: "Enbiya", text: "وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذ۪ينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟", translation: "Gerçek şu ki senden önce de birçok Resul'le alay edildi. Sonra alay ettikleri şey, alay edenleri kuşattı.", transliteration: "Ve lekadistuhzie bi rusulin min kablike fe haka billezine sehıru minhum ma kanu bihi yestehziun."},
  {id: 2525, sure_no: 42, ayet_no: 42, sure_adi: "Enbiya", text: "قُلْ مَنْ يَكْلَؤُ۬كُمْ بِالَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمٰنِۜ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُعْرِضُونَ", translation: "De ki: \"Gece ya da gündüz Rahman'dan sizi kim koruyabilir?\" Doğrusu onlar, Rabb'lerinin öğüdünden yüz çevirenlerdir.", transliteration: "Kul men yekleukum bil leyli ven nehari miner rahman, bel hum an zikri rabbihim mu'ridun."},
  {id: 2526, sure_no: 43, ayet_no: 43, sure_adi: "Enbiya", text: "اَمْ لَهُمْ اٰلِهَةٌ تَمْنَعُهُمْ مِنْ دُونِنَاۜ لَا يَسْتَط۪يعُونَ نَصْرَ اَنْفُسِهِمْ وَلَا هُمْ مِنَّا يُصْحَبُونَ", translation: "Yoksa onların, onları Bize karşı koruyacak ilahları mı var? Onlar, kendilerine dahi yardıma güç yetiremezler. Ve onlara Bizim tarafımızdan sahip çıkılmaz.", transliteration: "Em lehum alihetun temneuhum min dunina, la yestetiune nasre enfusihim ve la hum minna yushabun."},
  {id: 2527, sure_no: 44, ayet_no: 44, sure_adi: "Enbiya", text: "بَلْ مَتَّعْنَا هٰٓؤُ۬لَٓاءِ وَاٰبَٓاءَهُمْ حَتّٰى طَالَ عَلَيْهِمُ الْعُمُرُۜ اَفَلَا يَرَوْنَ اَنَّا نَأْتِي الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۜ اَفَهُمُ الْغَالِبُونَ", translation: "Oysaki onlara uzun gelen bir ömür boyunca, onları ve atalarını geçindirdik. Yeryüzüne müdahale edip onun her tarafında birçok şeyi nasıl yok ettiğimizi görmüyorlar mı? O halde galip olanlar onlar mı?", transliteration: "Bel metta'na haulai ve abaehum hatta tale aleyhimul umur, e fe la yerevne enna ne'til arda nenkusuha min etrafiha, e fehumul galibun."},
  {id: 2528, sure_no: 45, ayet_no: 45, sure_adi: "Enbiya", text: "قُلْ اِنَّـمَٓا اُنْذِرُكُمْ بِالْوَحْيِۘ وَلَا يَسْمَعُ الصُّمُّ الدُّعَٓاءَ اِذَا مَا يُنْذَرُونَ", translation: "De ki: \"Ben sizi ancak vahiy ile uyarıyorum.\" Ne var ki sağırlar uyarılsalar da çağrıyı duymazlar.", transliteration: "Kul innema unzirukum bil vahyi ve la yesmeus summud duae iza ma yunzerun."},
  {id: 2529, sure_no: 46, ayet_no: 46, sure_adi: "Enbiya", text: "وَلَئِنْ مَسَّتْهُمْ نَفْحَةٌ مِنْ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَا وَيْلَنَٓا اِنَّا كُنَّا ظَالِم۪ينَ", translation: "Eğer onlara, Rabb'inin azabından bir nebze dokunsa, \"Eyvah bize! Biz kendimize haksızlık edenleriz.\" diyecekler.", transliteration: "Ve le in messethum nefhatun min azabi rabbike le yekulunne ya veylena inna kunna zalimin."},
  {id: 2530, sure_no: 47, ayet_no: 47, sure_adi: "Enbiya", text: "وَنَضَعُ الْمَوَاز۪ينَ الْقِسْطَ لِيَوْمِ الْقِيٰمَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْـٔاًۜ وَاِنْ كَانَ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ اَتَيْنَا بِهَاۜ وَكَفٰى بِنَا حَاسِب۪ينَ", translation: "Kıyamet günü hak edileni eksiksiz belirleyen tartıları kurarız. Hiç kimse, hiçbir biçimde haksızlığa uğratılmaz. Hardal tanesi kadar da olsa her şeyi hesaba katarız. Hesap gören olarak Biz yeteriz.", transliteration: "Ve nedaul mevazinel kısta li yevmil kıyameti fe la tuzlemu nefsun şey'a ve in kane miskale habbetin min hardelin eteyna biha, ve kefa bina hasibin."},
  {id: 2531, sure_no: 48, ayet_no: 48, sure_adi: "Enbiya", text: "وَلَقَدْ اٰتَيْنَا مُوسٰى وَهٰرُونَ الْفُرْقَانَ وَضِيَٓاءً وَذِكْراً لِلْمُتَّق۪ينَۙ", translation: "Ant olsun ki, Musa ve Harun'a takva sahipleri için bir ışık olan, bir öğüt olan Furkan'ı verdik.", transliteration: "Ve lekad ateyna musa ve harunel furkane ve dıyaen ve zikren lil muttekin."},
  {id: 2532, sure_no: 49, ayet_no: 49, sure_adi: "Enbiya", text: "اَلَّذ۪ينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِنَ السَّاعَةِ مُشْفِقُونَ", translation: "Onlar, Rabb'lerini görmedikleri halde, O'na içten derin bir saygı duyarlar. Ve onlar, kıyamet gününün hesabından endişe ederler.", transliteration: "Ellezine yahşevne rabbehum bil gaybi ve hum mines saati muşfikun."},
  {id: 2533, sure_no: 50, ayet_no: 50, sure_adi: "Enbiya", text: "وَهٰذَا ذِكْرٌ مُبَارَكٌ اَنْزَلْنَاهُۜ  اَفَاَنْتُمْ لَهُ مُنْكِرُونَ۟", translation: "Bu, indirdiğimiz çok değerli bir öğüttür. Böyleyken, siz onu inkar mı edeceksiniz?", transliteration: "Ve haza zikrun mubarekun enzelnah, e fe entum lehu munkirun."},
  {id: 2534, sure_no: 51, ayet_no: 51, sure_adi: "Enbiya", text: "وَلَقَدْ اٰتَيْنَٓا اِبْرٰه۪يمَ رُشْدَهُ مِنْ قَبْلُ وَكُنَّا بِه۪ عَالِم۪ينَۚ", translation: "Ant olsun İbrahim'e rüşdünü vermiştik. Biz, onu tanıyorduk.", transliteration: "Ve lekad ateyna ibrahime ruşdehu min kablu ve kunna bihi alimin."},
  {id: 2535, sure_no: 52, ayet_no: 52, sure_adi: "Enbiya", text: "اِذْ قَالَ لِاَب۪يهِ وَقَوْمِه۪ مَا هٰذِهِ التَّمَاث۪يلُ الَّت۪ٓي اَنْتُمْ لَهَا عَاكِفُونَ", translation: "Hani o, babasına ve halkına: \"Kendinizi adadığınız bu heykeller nedir?\" dedi.", transliteration: "İz kale li ebihi ve kavmihi ma hazihit temasilulleti entum leha akifun."},
  {id: 2536, sure_no: 53, ayet_no: 53, sure_adi: "Enbiya", text: "قَالُوا وَجَدْنَٓا اٰبَٓاءَنَا لَهَا عَابِد۪ينَ", translation: "\"Biz, atalarımızı bunlara kulluk edenler olarak bulduk.\" dediler.", transliteration: "Kalu vecedna abaena leha abidin."},
  {id: 2537, sure_no: 54, ayet_no: 54, sure_adi: "Enbiya", text: "قَالَ لَقَدْ كُنْتُمْ اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "\"Gerçek şu ki siz de atalarınız da açık bir sapkınlık içindesiniz.\" dedi.", transliteration: "Kale lekad kuntum entum ve abaukum fi dalalin mubin."},
  {id: 2538, sure_no: 55, ayet_no: 55, sure_adi: "Enbiya", text: "قَالُٓوا اَجِئْتَنَا بِالْحَقِّ اَمْ اَنْتَ مِنَ اللَّاعِب۪ينَ", translation: "\"Gerçeği mi getirdin, yoksa bizimle oyun mu oynuyorsun?\" dediler.", transliteration: "Kalu e ci'tena bil hakkı em ente minel laıbin."},
  {id: 2539, sure_no: 56, ayet_no: 56, sure_adi: "Enbiya", text: "قَالَ بَلْ رَبُّكُمْ رَبُّ السَّمٰوَاتِ وَالْاَرْضِ الَّذ۪ي فَطَرَهُنَّۘ وَاَنَا۬ عَلٰى ذٰلِكُمْ مِنَ الشَّاهِد۪ينَ", translation: "\"Hayır! Sizin Rabb'iniz göklerin ve yerin Rabb'idir. Onlara işleyiş yasalarını O koymuştur. Bunun böyle olduğundan kesinlikle eminim.\"", transliteration: "Kale bel rabbukum rabbus semavati vel ardıllezi fatarahunne ve ene ala zalikum mineş şahidin."},
  {id: 2540, sure_no: 57, ayet_no: 57, sure_adi: "Enbiya", text: "وَتَاللّٰهِ لَاَك۪يدَنَّ اَصْنَامَكُمْ بَعْدَ اَنْ تُوَلُّوا مُدْبِر۪ينَ", translation: "\"Allah'a yemin ederim ki, siz buradan ayrıldıktan sonra, putlarınız hakkında tasarladığımı gerçekleştireceğim.\"", transliteration: "Ve tallahi le ekidenne asnamekum ba'de en tuvellu mudbirin."},
  {id: 2541, sure_no: 58, ayet_no: 58, sure_adi: "Enbiya", text: "فَجَعَلَهُمْ جُذَاذاً اِلَّا كَب۪يراً لَهُمْ لَعَلَّهُمْ اِلَيْهِ يَرْجِعُونَ", translation: "Sonra da kendisine sorsunlar diye, büyük olanı hariç diğerlerini parça parça etti.", transliteration: "Fe cealehum cuzazen illa kebiren lehum leallehum ileyhi yerciun."},
  {id: 2542, sure_no: 59, ayet_no: 59, sure_adi: "Enbiya", text: "قَالُوا مَنْ فَعَلَ هٰذَا بِاٰلِهَتِنَٓا اِنَّهُ لَمِنَ الظَّالِم۪ينَ", translation: "\"Bizim ilahlarımıza bunu kim yaptı? Kuşku yok ki o, gerçekten zalimlerdendir.\" dediler.", transliteration: "Kalu men feale haza bi alihetina innehu le minez zalimin."},
  {id: 2543, sure_no: 60, ayet_no: 60, sure_adi: "Enbiya", text: "قَالُوا سَمِعْنَا فَتًى يَذْكُرُهُمْ يُقَالُ لَـهُٓ اِبْرٰه۪يمُۜ", translation: "\"İbrahim adında bir gencin onlardan söz ettiğini duyduk.\" dediler.", transliteration: "Kalu semi'na feten yezkuruhum yukalu lehu ibrahim."},
  {id: 2544, sure_no: 61, ayet_no: 61, sure_adi: "Enbiya", text: "قَالُوا فَأْتُوا بِه۪ عَلٰٓى اَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُونَ", translation: "\"O halde onu insanların huzuruna getirin. Belki tanıklık eden çıkar\" dediler.", transliteration: "Kalu fe'tu bihi ala a'yunin nasi leallehum yeşhedun."},
  {id: 2545, sure_no: 62, ayet_no: 62, sure_adi: "Enbiya", text: "قَالُٓوا ءَاَنْتَ فَعَلْتَ هٰذَا بِاٰلِهَتِنَا يَٓا اِبْرٰه۪يمُۜ", translation: "\"Ey İbrahim! İlahlarımıza bunu sen mi yaptın?\" dediler.", transliteration: "Kalu e ente fealte haza bi alihetina ya ibrahim."},
  {id: 2546, sure_no: 63, ayet_no: 63, sure_adi: "Enbiya", text: "قَالَ بَلْ فَعَلَهُۗ كَب۪يرُهُمْ هٰذَا فَسْـَٔلُوهُمْ اِنْ كَانُوا يَنْطِقُونَ", translation: "\"Hayır, onu şu büyükleri yapmıştır. Eğer konuşabilirlerse haydi onlara sorun!\" dedi.", transliteration: "Kale bel fealehu kebiruhum haza fes'eluhum in kanu yentıkun."},
  {id: 2547, sure_no: 64, ayet_no: 64, sure_adi: "Enbiya", text: "فَرَجَعُٓوا اِلٰٓى اَنْفُسِهِمْ فَقَالُٓوا اِنَّكُمْ اَنْتُمُ الظَّالِمُونَۙ", translation: "Bunun üzerine birbirlerine dönüp, \"Haksızlık ediyoruz.\" dediler.", transliteration: "Fe receu ila enfusihim fe kalu innekum entumuz zalimun."},
  {id: 2548, sure_no: 65, ayet_no: 65, sure_adi: "Enbiya", text: "ثُمَّ نُكِسُوا عَلٰى رُؤُ۫سِهِمْۚ لَقَدْ عَلِمْتَ مَا هٰٓؤُ۬لَٓاءِ يَنْطِقُونَ", translation: "Sonra çok geçmeden yine eski kafalarına döndürüldüler: \"Gerçek şu ki sen bunların konuşmadıklarını biliyorsun!\" dediler.", transliteration: "Summe nukisu ala ruusihim, lekad alimte ma haulai yentıkun."},
  {id: 2549, sure_no: 66, ayet_no: 66, sure_adi: "Enbiya", text: "قَالَ اَفَتَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُكُمْ شَيْـٔاً وَلَا يَضُرُّكُمْۜ", translation: "İbrahim: \"Allah'ı bırakıp da size bir faydası da bir zararı da olmayan şeylere mi kulluk ediyorsunuz?\" dedi.", transliteration: "Kale e fe ta'budune min dunillahi ma la yenfeukum şey'en ve la yadurrukum."},
  {id: 2550, sure_no: 67, ayet_no: 67, sure_adi: "Enbiya", text: "اُفٍّ لَكُمْ وَلِمَا تَعْبُدُونَ مِنْ دُونِ اللّٰهِۜ اَفَلَا تَعْقِلُونَ", translation: "\"Yazıklar olsun size ve Allah'ın yanı sıra kulluk ettiğiniz şeylere. Siz hala aklınızı kullanmayacak mısınız?\"", transliteration: "Uffin lekum ve li ma ta'budune min dunillah, e fe la ta'kılun."},
  {id: 2551, sure_no: 68, ayet_no: 68, sure_adi: "Enbiya", text: "قَالُوا حَرِّقُوهُ وَانْصُرُٓوا اٰلِهَتَكُمْ اِنْ كُنْتُمْ فَاعِل۪ينَ", translation: "Halk: \"Eğer yapabilirseniz, onu yakın! İlahlarınızın intikamını alın!\" dediler.", transliteration: "Kalu harrikuhu vansuru alihetekum in kuntum faılin."},
  {id: 2552, sure_no: 69, ayet_no: 69, sure_adi: "Enbiya", text: "قُلْنَا يَا نَارُ كُون۪ي بَرْداً وَسَلَاماً عَلٰٓى اِبْرٰه۪يمَۙ", translation: "\"Ey ateş! İbrahim'e serin ve esenlik ol.\" dedik.", transliteration: "Kulna ya naru kuni berden ve selamen ala ibrahim."},
  {id: 2553, sure_no: 70, ayet_no: 70, sure_adi: "Enbiya", text: "وَاَرَادُوا بِه۪ كَيْداً فَجَعَلْنَاهُمُ الْاَخْسَر۪ينَۚ", translation: "Ona tuzak kurmak istediler. Fakat onları hüsrana uğrattık.", transliteration: "Ve eradu bihi keyden fe cealna humul ahserin."},
  {id: 2554, sure_no: 71, ayet_no: 71, sure_adi: "Enbiya", text: "وَنَجَّيْنَاهُ وَلُوطاً اِلَى الْاَرْضِ الَّت۪ي بَارَكْنَا ف۪يهَا لِلْعَالَم۪ينَ", translation: "Onu ve Lut'u kurtarıp, alemler için bereketli kıldığımız yurda ulaştırdık.", transliteration: "Ve necceynahu ve lutan ilel ardılleti barakna fiha lil alemin."},
  {id: 2555, sure_no: 72, ayet_no: 72, sure_adi: "Enbiya", text: "وَوَهَبْنَا لَـهُٓ اِسْحٰقَۜ وَيَعْقُوبَ نَافِلَةًۜ وَكُلاًّ جَعَلْنَا صَالِح۪ينَ", translation: "Ona İshak'ı ve ayrıca Ya'kub'u armağan ettik. Ve hepsini iyi kimseler yaptık.", transliteration: "Ve vehebna lehu ishak, ve ya'kube nafileh, ve kullen cealna salihin."},
  {id: 2556, sure_no: 73, ayet_no: 73, sure_adi: "Enbiya", text: "وَجَعَلْنَاهُمْ اَئِمَّةً يَهْدُونَ بِاَمْرِنَا وَاَوْحَيْنَٓا اِلَيْهِمْ فِعْلَ الْخَيْرَاتِ وَاِقَامَ الصَّلٰوةِ وَا۪يتَٓاءَ الزَّكٰوةِۚ وَكَانُوا لَنَا عَابِد۪ينَۙ", translation: "Onları, buyruklarımızla doğru yolu gösteren önderler kıldık. Onlara hayırlar yapmayı, salatı ikame etmeyi, zekatı yapmayı vahyettik. Ve onlar yalnızca bize kulluk eden kimselerdi.", transliteration: "Ve cealnahum eimmeten yehdune bi emrina ve evhayna ileyhim fi'lel hayrati ve ikames salati ve itaez zekah, ve kanu lena abidin."},
  {id: 2557, sure_no: 74, ayet_no: 74, sure_adi: "Enbiya", text: "وَلُوطاً اٰتَيْنَاهُ حُكْماً وَعِلْماً وَنَجَّيْنَاهُ مِنَ الْقَرْيَةِ الَّت۪ي كَانَتْ تَعْمَلُ الْخَبَٓائِثَۜ اِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَاسِق۪ينَۙ", translation: "Biz, Lut'a bir hüküm, bir ilim verdik. Onu çirkin işler yapan kentten kurtardık. Onlar fasık olan kötü bir halktı.", transliteration: "Ve lutan ateynahu hukmen ve ılmen ve necceynahu minel karyetilleti kanet ta'melul habais, innehum kanu kavme sev'in fasikin."},
  {id: 2558, sure_no: 75, ayet_no: 75, sure_adi: "Enbiya", text: "وَاَدْخَلْنَاهُ ف۪ي رَحْمَتِنَاۜ اِنَّهُ مِنَ الصَّالِح۪ينَ۟", translation: "Onu rahmetimize dahil ettik. O salihlerdendi.", transliteration: "Ve edhalnahu fi rahmetina, innehu mines salihin."},
  {id: 2559, sure_no: 76, ayet_no: 76, sure_adi: "Enbiya", text: "وَنُوحاً اِذْ نَادٰى مِنْ قَبْلُ فَاسْتَجَبْنَا لَهُ فَنَجَّيْنَاهُ وَاَهْلَهُ مِنَ الْكَرْبِ الْعَظ۪يمِۚ", translation: "Ve Nuh'u da hani o daha önce bize çağrıda bulunmuştu. Biz de çağrısına karşılık verdik. Onu ve ehlini büyük bir felaketten kurtardık.", transliteration: "Ve nuhan iz nada min kablu festecebna lehu fe necceynahu ve ehlehu minel kerbil azim."},
  {id: 2560, sure_no: 77, ayet_no: 77, sure_adi: "Enbiya", text: "وَنَصَرْنَاهُ مِنَ الْقَوْمِ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۜ اِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَاَغْرَقْنَاهُمْ اَجْمَع۪ينَ", translation: "Ayetlerimizi yalanlayan bir halka karşı ona yardım ettik. Onlar kötü bir halktı. Bundan dolayı Biz de hepsini boğduk.", transliteration: "Ve nasarnahu minel kavmillezine kezzebu bi ayatina, innehum kanu kavme sev'in fe agraknahum ecmain."},
  {id: 2561, sure_no: 78, ayet_no: 78, sure_adi: "Enbiya", text: "وَدَاوُ۫دَ وَسُلَيْمٰنَ اِذْ يَحْكُمَانِ فِي الْحَرْثِ اِذْ نَفَشَتْ ف۪يهِ غَنَمُ الْقَوْمِۚ وَكُنَّا لِحُكْمِهِمْ شَاهِد۪ينَۙ", translation: "Hani Davud ve Süleyman, halkın koyunlarının yayıldıkları ekin hakkında hüküm veriyorlardı. Biz, onların hükümlerine tanıktık.", transliteration: "Ve davude ve suleymane iz yahkumani fil harsi iz nefeşet fihi ganemul kavm, ve kunna li hukmihim şahidin."},
  {id: 2562, sure_no: 79, ayet_no: 79, sure_adi: "Enbiya", text: "فَفَهَّمْنَاهَا سُلَيْمٰنَۚ وَكُلاًّ اٰتَيْنَا حُكْماً وَعِلْماًۘ وَسَخَّرْنَا مَعَ دَاوُ۫دَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَۜ وَكُنَّا فَاعِل۪ينَ", translation: "Biz, bunu Süleyman'a iyice kavrattık. Her ikisine de hüküm ve ilim verdik. Davud'la beraber tesbih etsinler diye dağları ve kuşları buyruk altına aldık. Ve Biz, yapanlarız.", transliteration: "Fe fehhemnaha suleyman, ve kullen ateyna hukmen ve ılmen ve sehharna mea davudel cibale yusebbihne vet tayr, ve kunna faılin."},
  {id: 2563, sure_no: 80, ayet_no: 80, sure_adi: "Enbiya", text: "وَعَلَّمْنَاهُ صَنْعَةَ لَبُوسٍ لَكُمْ لِتُحْصِنَكُمْ مِنْ بَأْسِكُمْۚ فَهَلْ اَنْتُمْ شَاكِرُونَ", translation: "Ona; sizi, sizin şiddetinizden koruyacak elbise yapma sanatını öğrettik. Öyleyse şükredenler misiniz?", transliteration: "Ve allemnahu san'ate lebusin lekum li tuhsınekum min be'sikum, fe hel entum şakirun."},
  {id: 2564, sure_no: 81, ayet_no: 81, sure_adi: "Enbiya", text: "وَلِسُلَيْمٰنَ الرّ۪يحَ عَاصِفَةً تَجْر۪ي بِاَمْرِه۪ٓ اِلَى الْاَرْضِ الَّت۪ي بَارَكْنَا ف۪يهَاۜ وَكُنَّا بِكُلِّ شَيْءٍ عَالِم۪ينَ", translation: "e şiddetli esen rüzgar Süleyman içindi. Onun isteğine uygun şekilde bereketli yerlere doğru eser giderdi. Biz, Her Şeyi Bilenleriz.", transliteration: "Ve li suleymaner riha asıfeten tecri bi emrihi ilel ardılleti barekna fiha ve kunna bi kulli şey'in alimin."},
  {id: 2565, sure_no: 82, ayet_no: 82, sure_adi: "Enbiya", text: "وَمِنَ الشَّيَاط۪ينِ مَنْ يَغُوصُونَ لَهُ وَيَعْمَلُونَ عَمَلاً دُونَ ذٰلِكَۚ وَكُنَّا لَهُمْ حَافِظ۪ينَۙ", translation: "Ve şeytanlardan kendisi için dalgıçlık eden ve bundan başka iş yapanlar vardı. Onları gözetim altında tutuyorduk.", transliteration: "Ve mineş şeyatini men yegusune lehu ve ya'melune amelen dune zalik, ve kunna lehum hafızin."},
  {id: 2566, sure_no: 83, ayet_no: 83, sure_adi: "Enbiya", text: "وَاَيُّوبَ اِذْ نَادٰى رَبَّهُٓ اَنّ۪ي مَسَّنِيَ الضُّرُّ وَاَنْتَ اَرْحَمُ الرَّاحِم۪ينَۚ", translation: "Eyyub'u da an! Hani o: \"Bana bir dert dokundu. Ve Sen, merhamet edenlerin en merhametlisisin.\" diye Rabb'ine seslenmişti.", transliteration: "Ve eyyube iz nada rabbehu enni messeniyed durru ve ente erhamur rahimin."},
  {id: 2567, sure_no: 84, ayet_no: 84, sure_adi: "Enbiya", text: "فَاسْتَجَبْنَا لَهُ فَكَشَفْنَا مَا بِه۪ مِنْ ضُرٍّ وَاٰتَيْنَاهُ اَهْلَهُ وَمِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنْ عِنْدِنَا وَذِكْرٰى لِلْعَابِد۪ينَ", translation: "Biz de onun çağrısına karşılık verdik. Ve derdini yok ettik. Ve katımızdan bir rahmet ve kulluk edenlere bir öğüt olmak üzere, kendisine ehlini ve onlarla birlikte bir mislini daha verdik.", transliteration: "Festecebna lehu fe keşefna ma bihi min durrin ve ateynahu ehlehu ve mislehum meahum rahmeten min ındina ve zikra lil abidin."},
  {id: 2568, sure_no: 85, ayet_no: 85, sure_adi: "Enbiya", text: "وَاِسْمٰع۪يلَ وَاِدْر۪يسَ وَذَا الْكِفْلِۜ  كُلٌّ مِنَ الصَّابِر۪ينَۚ", translation: "İsmail'i, İdris'i ve Zu'l-Kifl'i de an. Hepsi sabredenlerdendi.", transliteration: "Ve ismaile ve idrise ve zelkifl, kullun mines sabirin."},
  {id: 2569, sure_no: 86, ayet_no: 86, sure_adi: "Enbiya", text: "وَاَدْخَلْنَاهُمْ ف۪ي رَحْمَتِنَاۜ اِنَّهُمْ مِنَ الصَّالِح۪ينَ", translation: "Onları rahmetimizle kuşattık. Onlar salih kimselerdi.", transliteration: "Ve edhalnahum fi rahmetina, innehum mines salihin."},
  {id: 2570, sure_no: 87, ayet_no: 87, sure_adi: "Enbiya", text: "وَذَا النُّونِ اِذْ ذَهَبَ مُغَاضِباً فَظَنَّ اَنْ لَنْ نَقْدِرَ عَلَيْهِ فَنَادٰى فِي الظُّلُمَاتِ اَنْ لَٓا اِلٰهَ اِلَّٓا اَنْتَ سُبْحَانَكَۗ اِنّ۪ي كُنْتُ مِنَ الظَّالِم۪ينَۚ", translation: "Ve Zu'n-Nun'u da an! Hani öfkelenerek gitmişti de kendisini sıkıntıda bırakmayacağımızı sanmıştı. Sonra karanlıklar içinde, \"Senden başka ilah yoktur! Sen yüceler yücesisin. Ben haksızlık yaptım!\" diye seslenmişti.", transliteration: "Ve zennuni iz zehebe mugadıben fe zanne en len nakdire aleyhi fe nada fiz zulumati en la ilahe illa ente subhaneke inni kuntu minez zalimin."},
  {id: 2571, sure_no: 88, ayet_no: 88, sure_adi: "Enbiya", text: "فَاسْتَجَبْنَا لَهُۙ وَنَجَّيْنَاهُ مِنَ الْغَمِّۜ وَكَذٰلِكَ نُنْجِي الْمُؤْمِن۪ينَ", translation: "Bunun üzerine çağrısına karşılık verdik ve onu sıkıntıdan kurtardık. İşte inananları böyle kurtarırız.", transliteration: "Festecebna lehu ve necceynahu minel gamm, ve kezalike nuncil mu'minin."},
  {id: 2572, sure_no: 89, ayet_no: 89, sure_adi: "Enbiya", text: "وَزَكَرِيَّٓا اِذْ نَادٰى رَبَّهُ رَبِّ لَا تَذَرْن۪ي فَرْداً وَاَنْتَ خَيْرُ الْوَارِث۪ينَۚ", translation: "Ve Zekeriyya'yı da an! Rabb'ine: \"Rabb'im! Beni tek başıma bırakma, Sen varislerin en hayırlısısın.\" diye seslenmişti.", transliteration: "Ve zekeriyya iz nada rabbehu rabbi la tezerni ferden ve ente hayrul varisin."},
  {id: 2573, sure_no: 90, ayet_no: 90, sure_adi: "Enbiya", text: "فَاسْتَجَبْنَا لَهُۘ وَوَهَبْنَا لَهُ يَحْيٰى وَاَصْلَحْنَا لَهُ زَوْجَهُۜ اِنَّهُمْ كَانُوا يُسَارِعُونَ فِي الْخَيْرَاتِ وَيَدْعُونَنَا رَغَباً وَرَهَباًۜ وَكَانُوا لَنَا خَاشِع۪ينَ", translation: "Bunun üzerine çağrısına karşılık verdik. Ve kendisine Yahya'yı armağan ettik. Ve onun için eşini ıslah ettik. Onlar hayırlarda yarışıyor, umarak ve endişe ederek Bize yalvarıyorlardı. Ve Bize karşı içtenlikle saygı duyuyorlardı.", transliteration: "Festecebna leh, ve vehebna lehu yahya ve aslahna lehu zevceh, innehum kanu yusariune fil hayrati ve yed'unena regaben ve reheba, ve kanu lena haşiin."},
  {id: 2574, sure_no: 91, ayet_no: 91, sure_adi: "Enbiya", text: "وَالَّت۪ٓي اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا ف۪يهَا مِنْ رُوحِنَا وَجَعَلْنَاهَا وَابْنَهَٓا اٰيَةً لِلْعَالَم۪ينَ", translation: "Ve namusunu koruyanı da an. Ona ruhumuzdan üfledik. Ve kendisini ve oğlunu alemler için bir ayet kıldık.", transliteration: "Velleti ahsanet ferceha fe nefahna fiha min ruhina ve cealnaha vebneha ayeten lil alemin."},
  {id: 2575, sure_no: 92, ayet_no: 92, sure_adi: "Enbiya", text: "اِنَّ هٰذِه۪ٓ اُمَّتُكُمْ اُمَّةً وَاحِدَةًۘ وَاَنَا۬ رَبُّكُمْ فَاعْبُدُونِ", translation: "Bu sizin ümmetiniz tek bir ümmettir. Çünkü hepinizin Rabb'i Ben'im. O halde yalnızca Bana kulluk edin!", transliteration: "İnne hazihi ummetukum ummeten vahıdeten ve ene rabbukum fa'budun."},
  {id: 2576, sure_no: 93, ayet_no: 93, sure_adi: "Enbiya", text: "وَتَقَطَّعُٓوا اَمْرَهُمْ بَيْنَهُمْۜ كُلٌّ اِلَيْنَا رَاجِعُونَ۟", translation: "Emirlerini aralarında bölük pörçük ettiler. Oysa hepsi yalnızca Bize dönecekler.", transliteration: "Ve tekattau emrehum beynehum, kullun ileyna raciun."},
  {id: 2577, sure_no: 94, ayet_no: 94, sure_adi: "Enbiya", text: "فَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِه۪ۚ وَاِنَّا لَهُ كَاتِبُونَ", translation: "Fakat kim iman etmiş olarak salihat yaparsa, onun çabası için kafirlik yapılmaz. Biz, bütün çabalarını yazmaktayız.", transliteration: "Fe men ya'mel mines salihati ve huve mu'minun fe la kufrane li sa'yih, ve inna lehu katibun."},
  {id: 2578, sure_no: 95, ayet_no: 95, sure_adi: "Enbiya", text: "وَحَرَامٌ عَلٰى قَرْيَةٍ اَهْلَكْنَاهَٓا اَنَّهُمْ لَا يَرْجِعُونَ", translation: "Yok ettiğimiz bir kent halkının geri dönmesi haramdır. Onlar bir daha geri dönemezler.", transliteration: "Ve haramun ala karyetin ehleknaha ennehum la yerciun."},
  {id: 2579, sure_no: 96, ayet_no: 96, sure_adi: "Enbiya", text: "حَتّٰٓى اِذَا فُتِحَتْ يَأْجُوجُ وَمَأْجُوجُ وَهُمْ مِنْ كُلِّ حَدَبٍ يَنْسِلُونَ", translation: "Hatta Ye'cuc ve Me'cuc yaygınlaşıp her tarafı kuşatma altına alıncaya kadar.", transliteration: "Hatta iza futihat ye'cucu ve me'cucu ve hum min kulli hadebin yensilun."},
  {id: 2580, sure_no: 97, ayet_no: 97, sure_adi: "Enbiya", text: "وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَاِذَا هِيَ شَاخِصَةٌ اَبْصَارُ الَّذ۪ينَ كَفَرُواۜ يَا وَيْلَنَا قَدْ كُنَّا ف۪ي غَفْلَةٍ مِنْ هٰذَا بَلْ كُنَّا ظَالِم۪ينَ", translation: "Uyarıldıkları gerçekle yüz yüze geldiklerinde, Kafirlerin gözleri korku ile büyür. \"Eyvah bizlere! Gerçekten biz aldanış içindeymişiz. Aslında biz, kendimize haksızlık etmişiz.\"", transliteration: "Vakterabel va'dul hakku fe iza hiye şahısatun ebsarullezine keferu, ya veylena kad kunna fi gafletin min haza bel kunna zalimin."},
  {id: 2581, sure_no: 98, ayet_no: 98, sure_adi: "Enbiya", text: "اِنَّكُمْ وَمَا تَعْبُدُونَ مِنْ دُونِ اللّٰهِ حَصَبُ جَهَنَّمَۜ اَنْتُمْ لَهَا وَارِدُونَ", translation: "Siz ve Allah'ın yanı sıra kulluk ettikleriniz, Cehennem'in odunusunuz. Siz oraya gireceksiniz.", transliteration: "İnnekum ve ma ta'budune min dunillahi hasabu cehennem, entum leha varidun."},
  {id: 2582, sure_no: 99, ayet_no: 99, sure_adi: "Enbiya", text: "لَوْ كَانَ هٰٓؤُ۬لَٓاءِ اٰلِهَةً مَا وَرَدُوهَاۜ وَكُلٌّ ف۪يهَا خَالِدُونَ", translation: "Eğer onlar gerçekten ilah olsalardı, Cehennem'e girmezlerdi. Oysa hepsi orada sürekli kalacaklardır.", transliteration: "Lev kane haulai aliheten ma veraduha, ve kullun fiha halidun."},
  {id: 2583, sure_no: 100, ayet_no: 100, sure_adi: "Enbiya", text: "لَهُمْ ف۪يهَا زَف۪يرٌ وَهُمْ ف۪يهَا لَا يَسْمَعُونَ", translation: "Onlar için orada bir inleme vardır ve onlar orada kendi inlemelerinden başka bir şey işitmezler.", transliteration: "Lehum fiha zefirun ve hum fiha la yesmeun."},
  {id: 2584, sure_no: 101, ayet_no: 101, sure_adi: "Enbiya", text: "اِنَّ الَّذ۪ينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنٰٓىۙ اُو۬لٰٓئِكَ عَنْهَا مُبْعَدُونَۙ", translation: "Bizden kendilerine iyilik ulaşanlar, işte onlar, ondan uzaklaştırılanlardır.", transliteration: "İnnellezine sebekat lehum minnel husna ulaike anha mub'adun."},
  {id: 2585, sure_no: 102, ayet_no: 102, sure_adi: "Enbiya", text: "لَا يَسْمَعُونَ حَس۪يسَهَاۚ وَهُمْ ف۪ي مَا اشْتَهَتْ اَنْفُسُهُمْ خَالِدُونَۚ", translation: "Onlar, Cehennem'in uğultusunu bile duymazlar. Ve onlar canlarının istediği şeylerin içinde sürekli kalacaklardır.", transliteration: "La yesme'une hasiseha, ve hum fi meştehet enfusuhum halidun."},
  {id: 2586, sure_no: 103, ayet_no: 103, sure_adi: "Enbiya", text: "لَا يَحْزُنُهُمُ الْفَزَعُ الْاَكْبَرُ وَتَتَلَقّٰيهُمُ الْمَلٰٓئِكَةُۜ هٰذَا يَوْمُكُمُ الَّذ۪ي كُنْتُمْ تُوعَدُونَ", translation: "O en büyük dehşet, onları kaygılandırmayacak. Ve melekler, \"İşte bu, size söz verilen gününüzdür.\" diye onları karşılayacaklar.", transliteration: "La yahzunuhumul fezeul ekberu ve tetelakkahumul melaikeh, haza yevmukumullezi kuntum tuadun."},
  {id: 2587, sure_no: 104, ayet_no: 104, sure_adi: "Enbiya", text: "يَوْمَ نَطْوِي السَّمَٓاءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِۜ كَمَا بَدَأْنَٓا اَوَّلَ خَلْقٍ نُع۪يدُهُۜ وَعْداً عَلَيْنَاۜ اِنَّا كُنَّا فَاعِل۪ينَ", translation: "O gün, kitap sayfalarını dürer gibi göğü düreriz. Onu ilk yarattığımız gibi yeniden yaratacağız. Bu Bizim katımızdan verilmiş bir sözdür. Kuşkusuz sözümüzü yerine getiririz.", transliteration: "Yevme natvis semae ke tayyis sicilli lil kutub, kema bede'na evvele halkın nuiduh, va'den aleyna, inna kunna faılin."},
  {id: 2588, sure_no: 105, ayet_no: 105, sure_adi: "Enbiya", text: "وَلَقَدْ كَتَبْنَا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ اَنَّ الْاَرْضَ يَرِثُهَا عِبَادِيَ الصَّالِحُونَ", translation: "Ve ant olsun ki Biz, zikirden sonra zeburda, arza salih kullarımızın varis olacağını yazdık.", transliteration: "Ve lekad ketebna fiz zeburi min ba'diz zikri ennel arda yerisuha ıbadiyes salihun."},
  {id: 2589, sure_no: 106, ayet_no: 106, sure_adi: "Enbiya", text: "اِنَّ ف۪ي هٰذَا لَبَلَاغاً لِقَوْمٍ عَابِد۪ينَۜ", translation: "Bunda kulluk eden bir toplum için açık bir duyuru vardır.", transliteration: "İnne fi haza le belagan li kavmin abidin."},
  {id: 2590, sure_no: 107, ayet_no: 107, sure_adi: "Enbiya", text: "وَمَٓا اَرْسَلْنَاكَ اِلَّا رَحْمَةً لِلْعَالَم۪ينَ", translation: "Biz, seni bütün insanlık için sadece bir rahmet olarak gönderdik.", transliteration: "Ve ma erselnake illa rahmeten lil alemin."},
  {id: 2591, sure_no: 108, ayet_no: 108, sure_adi: "Enbiya", text: "قُلْ اِنَّمَا يُوحٰٓى اِلَيَّ اَنَّـمَٓا اِلٰهُكُمْ اِلٰهٌ وَاحِدٌۚ فَهَلْ اَنْتُمْ مُسْلِمُونَ", translation: "De ki: \"Bana, sizin ilahınızın ancak tek bir ilah olduğu vahyolundu. Artık O'na teslim olacak mısınız?\"", transliteration: "Kul innema yuha ileyye ennema ilahukum ilahun vahid, fe hel entum muslimun."},
  {id: 2592, sure_no: 109, ayet_no: 109, sure_adi: "Enbiya", text: "فَاِنْ تَوَلَّوْا فَقُلْ اٰذَنْتُكُمْ عَلٰى سَوَٓاءٍۜ وَاِنْ اَدْر۪ٓي اَقَر۪يبٌ اَمْ بَع۪يدٌ مَا تُوعَدُونَ", translation: "Buna rağmen yüz çevirirlerse, o zaman de ki: \"Herkese eşit olarak duyurdum. Söz verilen şey yakın mı uzak mı orasını bilmiyorum!\"", transliteration: "Fe in tevellev fe kul azentukum ala seva', ve in edri e karibun em baidun ma tuadun."},
  {id: 2593, sure_no: 110, ayet_no: 110, sure_adi: "Enbiya", text: "اِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُونَ", translation: "\"O, açıkça söylenen sözleri de bilir, gizlediklerinizi de bilir.\"", transliteration: "İnnehu ya'lemul cehre minel kavli ve ya'lemu ma tektumun."},
  {id: 2594, sure_no: 111, ayet_no: 111, sure_adi: "Enbiya", text: "وَاِنْ اَدْر۪ي لَعَلَّهُ فِتْنَةٌ لَكُمْ وَمَتَاعٌ اِلٰى ح۪ينٍ", translation: "\"Bilemem, belki o bir sınav ve sizin için belli bir zamana kadar yararlanmadır.\"", transliteration: "Ve in edri leallehu fitnetun lekum ve metaun ila hin."},
  {id: 2595, sure_no: 112, ayet_no: 112, sure_adi: "Enbiya", text: "قَالَ رَبِّ احْكُمْ بِالْحَقِّۜ وَرَبُّنَا الرَّحْمٰنُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُونَ", translation: "Dedi ki: \"Rabb'im! Aramızda Hakk ile hükmet. Ve Rabb'imiz, yakıştırmalarınıza karşı yardım istenecek Rahman'dır.\"", transliteration: "Kale rabbıhkum bil hakk, ve rabbuner rahmanul musteanu ala ma tasıfun."},
  {id: 2596, sure_no: 1, ayet_no: 1, sure_adi: "Hac", text: "يَٓا اَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْۚ اِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظ۪يمٌ", translation: "Ey İnsanlar! Rabb'inize karşı takva sahibi olun. Kuşkusuz, o Sa'at'in şiddetli sarsıntısı çok büyük bir şeydir.", transliteration: "Ya eyyuhen nasutteku rabbekum, inne zelzeletes saati şey'un azim."},
  {id: 2597, sure_no: 2, ayet_no: 2, sure_adi: "Hac", text: "يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّٓا اَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكَارٰى وَمَا هُمْ بِسُكَارٰى وَلٰكِنَّ عَذَابَ اللّٰهِ شَد۪يدٌ", translation: "Onu gördüğünüz gün, her emzikli kadın, emzirdiğini unutur. Her hamile kadın, bebeğini düşürür. Ve İnsanları sarhoş olmadıkları halde, sarhoş gibi görürsün. Zira Allah'ın azabı çok şiddetlidir.", transliteration: "Yevme teravneha tezhelu kullu murdıatin amma erdaat ve tedau kullu zati hamlin hamleha ve teren nase sukara ve ma hum bi sukara ve lakinne azaballahi şedid."},
  {id: 2598, sure_no: 3, ayet_no: 3, sure_adi: "Hac", text: "وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَيَتَّبِعُ كُلَّ شَيْطَانٍ مَر۪يدٍۙ", translation: "Ve İnsanlardan öyle kimseler vardır ki; hiçbir bilgisi olmadığı halde, Allah hakkında ileri geri konuşurlar ve bütün azgın şeytanların peşinden giderler.", transliteration: "Ve minen nasi men yucadilu fillahi bi gayri ilmin ve yettebiu kulle şeytanin merid."},
  {id: 2599, sure_no: 4, ayet_no: 4, sure_adi: "Hac", text: "كُتِبَ عَلَيْهِ اَنَّهُ مَنْ تَوَلَّاهُ فَاَنَّهُ يُضِلُّهُ وَيَهْد۪يهِ اِلٰى عَذَابِ السَّع۪يرِ", translation: "Onun hakkında yazıldı: ona uyan kesinlikle bilmelidir ki, şeytan onu saptırır. Ve onu Sair'in azabına iletir.", transliteration: "Kutibe aleyhi ennehu men tevellahu fe ennehu yudılluhu ve yehdihi ila azabis sair."},
  {id: 2600, sure_no: 5, ayet_no: 5, sure_adi: "Hac", text: "يَٓا اَيُّهَا النَّاسُ اِنْ كُنْتُمْ ف۪ي رَيْبٍ مِنَ الْبَعْثِ فَاِنَّا خَلَقْنَاكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُضْغَةٍ مُخَلَّقَةٍ وَغَيْرِ مُخَلَّقَةٍ لِنُبَيِّنَ لَكُمْۜ وَنُقِرُّ فِي الْاَرْحَامِ مَا نَشَٓاءُ اِلٰٓى اَجَلٍ مُسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلاً ثُمَّ لِتَبْلُغُٓوا اَشُدَّكُمْۚ وَمِنْكُمْ مَنْ يُتَوَفّٰى وَمِنْكُمْ مَنْ يُرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْلَا يَعْلَمَ مِنْ بَعْدِ عِلْمٍ شَيْـٔاًۜ وَتَرَى الْاَرْضَ هَامِدَةً فَاِذَٓا اَنْزَلْنَا عَلَيْهَا الْمَٓاءَ اهْتَزَّتْ وَرَبَتْ وَاَنْبَتَتْ مِنْ كُلِّ زَوْجٍ بَه۪يجٍ", translation: "Ey İnsanlar! Eğer öldükten sonra yeniden dirilmekten kuşkunuz varsa; bilin ki Biz, sizi topraktan, sonra nutfeden, sonra bir alakadan, sonra yapısı belli belirsiz mudğadan yarattık. Ne olduğunuzu bilin diye size açıklıyoruz. Ve Biz, dilediğimizi belli bir süreye kadar rahimlerde tutarız. Sonra sizi bir bebek olarak çıkarırız. Sonra kiminiz ergenlik çağına ulaşır. Ve sizden bir kısmınız vefat ettirilir. Kiminiz de ömrünün en kötü dönemine erişir; bir şey bilmez yaşlı bir bunak haline gelir. Yeryüzünü kurumuş ölmüş görürsün, ama üzerine su indirdiğimiz zaman yeniden hareketlenir, kabarır ve her bitkiden göz alıcı çiftler bitirir.", transliteration: "Ya eyyuhen nasu in kuntum fi raybin minel ba'si fe inna halaknakum min turabin summe min nutfetin summe min alakatin summe min mudgatin muhallekatin ve gayri muhallekatin li nubeyyine lekum, ve nukırru fil erhami ma neşau ila ecelin musemmen summe nuhricukum tıflen summe li teblugu eşuddekum ve minkum men yuteveffa ve minkum men yuraddu ila erzelil umuri li keyla ya'leme min ba'di ilmin şey'a, ve terel arda hamideten fe iza enzelna aleyhel maehtezzet ve rabet ve enbetet min kulli zevcin behic."},
  {id: 2601, sure_no: 6, ayet_no: 6, sure_adi: "Hac", text: "ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّهُ يُحْـيِ الْمَوْتٰى وَاَنَّهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌۙ", translation: "İşte Hakk olan Allah budur. O, ölüleri diriltir. Ve O, Her Şeye Gücü Yeten'dir.", transliteration: "Zalike bi ennallahe huvel hakku ve ennehu yuhyil mevta ve ennehu ala kulli şey'in kadir."},
  {id: 2602, sure_no: 7, ayet_no: 7, sure_adi: "Hac", text: "وَاَنَّ السَّاعَةَ اٰتِيَةٌ لَا رَيْبَ ف۪يهَاۙ وَاَنَّ اللّٰهَ يَبْعَثُ مَنْ فِي الْقُبُورِ", translation: "Kuşkusuz o Sa'at gelecektir. Onun geleceği kesindir. Ve Allah, kabirlerde olanları diriltecektir.", transliteration: "Ve ennes saate atiyetun la raybe fiha ve ennallahe yeb'asu men fil kubur."},
  {id: 2603, sure_no: 8, ayet_no: 8, sure_adi: "Hac", text: "وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُن۪يرٍۙ", translation: "İnsanlardan bazıları, bir bilgiye, bir yol göstericiye ve aydınlatıcı bir kitaba dayanmaksızın Allah hakkında tartışıyorlar.", transliteration: "Ve minen nasi men yucadilu fillahi bi gayri ilmin ve la huden ve la kitabin munir."},
  {id: 2604, sure_no: 9, ayet_no: 9, sure_adi: "Hac", text: "ثَانِيَ عِطْفِه۪ لِيُضِلَّ عَنْ سَب۪يلِ اللّٰهِۜ لَهُ فِي الدُّنْيَا خِزْيٌ وَنُذ۪يقُهُ يَوْمَ الْقِيٰمَةِ عَذَابَ الْحَر۪يقِ", translation: "Allah'ın yolundan saptırmak için kıvırıp durur. Onun için dünyada rezillik vardır. Ve ona Kıyamet günü yakıcı ateşin azabını tattıracağız.", transliteration: "Saniye ıtfihi li yudılle an sebilillah, lehu fid dunya hızyun ve nuzikuhu yevmel kıyameti azabel harik."},
  {id: 2605, sure_no: 10, ayet_no: 10, sure_adi: "Hac", text: "ذٰلِكَ بِمَا قَدَّمَتْ يَدَاكَ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِلْعَب۪يدِ۟", translation: "İşte bu kendi tercihinle yaptığın şeylerden dolayıdır. Kuşkusuz Allah, kullarına haksızlık edici değildir.", transliteration: "Zalike bima kaddemet yedake ve ennallahe leyse bi zallamin lil abid."},
  {id: 2606, sure_no: 11, ayet_no: 11, sure_adi: "Hac", text: "وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللّٰهَ عَلٰى حَرْفٍۚ فَاِنْ اَصَابَهُ خَيْرٌۨ اطْمَاَنَّ بِه۪ۚ وَاِنْ اَصَابَتْهُ فِتْنَةٌۨ انْقَلَبَ عَلٰى وَجْهِه۪۠ خَسِرَ الدُّنْيَا وَالْاٰخِرَةَۜ ذٰلِكَ هُوَ الْخُسْرَانُ الْمُب۪ينُ", translation: "İnsanlardan bazıları da iş olsun diye Allah'a kulluk eder. Onun yararına bir şey olursa onunla mutlu olur, eğer bir fitneyle karşı karşıya kalırsa hemen yönünü değiştirir. O dünyada da ahirette de kaybedendir. İşte o apaçık bir kayıptır.", transliteration: "Ve minen nasi men ya'budullahe ala harf, fe in asabehu hayrunıtmeenne bih, ve in asabethu fitnetuninkalebe ala vechihi, hasired dunya vel ahıreh, zalike huvel husranul mubin."},
  {id: 2607, sure_no: 12, ayet_no: 12, sure_adi: "Hac", text: "يَدْعُوا مِنْ دُونِ اللّٰهِ مَا لَا يَضُرُّهُ وَمَا لَا يَنْفَعُهُۜ ذٰلِكَ هُوَ الضَّلَالُ الْبَع۪يدُ", translation: "Allah'ın yanı sıra kendilerine zarar da fayda da veremeyecek olan şeylere dua ediyorlar. İşte bu derin bir sapkınlıktır.", transliteration: "Yed'u min dunillahi ma la yedurruhu ve ma la yenfeuh, zalike huved dalalul baid."},
  {id: 2608, sure_no: 13, ayet_no: 13, sure_adi: "Hac", text: "يَدْعُوا لَمَنْ ضَرُّهُٓ اَقْرَبُ مِنْ نَفْعِه۪ۜ لَبِئْسَ الْمَوْلٰى وَلَبِئْسَ الْعَش۪يرُ", translation: "Gerçekten de zararı yararından daha yakın olana dua ediyorlar. O ne kötü mevla ne kötü yoldaştır!", transliteration: "Yed'u le men darruhu akrabu min nef'ıh, le bi'sel mevla ve le bi'sel aşir."},
  {id: 2609, sure_no: 14, ayet_no: 14, sure_adi: "Hac", text: "اِنَّ اللّٰهَ يُدْخِلُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ اِنَّ اللّٰهَ يَفْعَلُ مَا يُر۪يدُ", translation: "Allah, iman eden ve salihatı yapanları altından ırmaklar akan Cennetlere koyacak. Kuşkusuz Allah, Dilediği Şeyi Yapar.", transliteration: "İnnallahe yudhılullezine amenu ve amilus salihati cennatin tecri min tahtihel enhar, innallahe yef'alu ma yurid."},
  {id: 2610, sure_no: 15, ayet_no: 15, sure_adi: "Hac", text: "مَنْ كَانَ يَظُنُّ اَنْ لَنْ يَنْصُرَهُ اللّٰهُ فِي الدُّنْيَا وَالْاٰخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ اِلَى السَّمَٓاءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُ مَا يَغ۪يظُ", translation: "Kim Allah'ın, ona dünyada ve ahirette kesinlikle yardım etmeyeceğini zannediyorsa, o zaman semaya bir araç uzatsın da sonra onu kessin de baksın bakalım bu planı kendisini kızdıran şeyi giderecek mi?", transliteration: "Men kane yezunnu en len yensurehullahu fid dunya vel ahıreti felyemdud bi sebebin iles semai summel yakta' felyenzur hel yuzhibennekeyduhu ma yagiz."},
  {id: 2611, sure_no: 16, ayet_no: 16, sure_adi: "Hac", text: "وَكَذٰلِكَ اَنْزَلْنَاهُ اٰيَاتٍ بَيِّنَاتٍۙ وَاَنَّ اللّٰهَ يَهْد۪ي مَنْ يُر۪يدُ", translation: "Onu apaçık ayetler olarak indirdik. Allah, hak edene doğru yolu gösterir.", transliteration: "Ve kezalike enzelnahu ayatin beyyinatin ve ennallahe yehdi men yurid."},
  {id: 2612, sure_no: 17, ayet_no: 17, sure_adi: "Hac", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَالَّذ۪ينَ هَادُوا وَالصَّابِـ۪ٔينَ وَالنَّصَارٰى وَالْمَجُوسَ وَالَّذ۪ينَ اَشْرَكُواۗ اِنَّ اللّٰهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ", translation: "Allah; İman Edenler, Yahudiler, Sabiiler, Nesara, Mecusiler ve Müşrik olanların Kıyamet günü aralarını ayıracaktır. Allah, her şeye tanıktır.", transliteration: "İnnellezine amenu vellezine hadu ves sabiine ven nasara vel mecuse vellezine eşreku innallahe yafsılu beynehum yevmel kıyameh, innallahe ala kulli şey'in şehid."},
  {id: 2613, sure_no: 18, ayet_no: 18, sure_adi: "Hac", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ يَسْجُدُ لَهُ مَنْ فِي السَّمٰوَاتِ وَمَنْ فِي الْاَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُومُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَٓابُّ وَكَث۪يرٌ مِنَ النَّاسِۜ وَكَث۪يرٌ حَقَّ عَلَيْهِ الْعَذَابُۜ وَمَنْ يُهِنِ اللّٰهُ فَمَا لَهُ مِنْ مُكْرِمٍۜ اِنَّ اللّٰهَ يَفْعَلُ مَا يَشَٓاءُ", translation: "Göklerde ve yerde ne varsa; Güneş'in, Ay'ın ve yıldızların, dağların, ağaçların, hayvanların ve İnsanların birçoğunun Allah'a secde ettiklerini görmüyor musun? Birçoğunun da üzerine azap hak oldu. Ve Allah, kimi küçük düşürürse artık onun için bir yüceltici yoktur. Kuşkusuz Allah, dilediğini yapar.", transliteration: "E lem tera ennallahe yescudu lehu men fis semavati ve men fil ardı veş şemsu vel kameru ven nucumu vel cibalu veş şeceru ved devabbu ve kesirun minen nas, ve kesirun hakka aleyhil azab, ve men yuhinillahu fe ma lehu min mukrim, innallahe yef'alu ma yeşa'."},
  {id: 2614, sure_no: 19, ayet_no: 19, sure_adi: "Hac", text: "هٰذَانِ خَصْمَانِ اخْتَصَمُوا ف۪ي رَبِّهِمْۘ فَالَّذ۪ينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيَابٌ مِنْ نَارٍۜ يُصَبُّ مِنْ فَوْقِ رُؤُ۫سِهِمُ الْحَم۪يمُۚ", translation: "Bu iki karşıt taraf, Rabb'leri hakkında mücadele eden, birbirlerine iki düşmandır. Bunlardan Kafirler için ateşten biçilmiş elbiseler vardır. Onların başlarının üstünden kaynar sular dökülecek!", transliteration: "Hazani hasmanihtesamu fi rabbihim fellezine keferu kuttıat lehum siyabun min nar, yusabbu min fevkı ruusihumul hamim."},
  {id: 2615, sure_no: 20, ayet_no: 20, sure_adi: "Hac", text: "يُصْهَرُ بِه۪ مَا ف۪ي بُطُونِهِمْ وَالْجُلُودُۜ", translation: "Bununla iç organları ve derileri eritilecek.", transliteration: "Yusheru bihi ma fi butunihim vel culud."},
  {id: 2616, sure_no: 21, ayet_no: 21, sure_adi: "Hac", text: "وَلَهُمْ مَقَامِعُ مِنْ حَد۪يدٍ", translation: "Ve onlar için demirden topuzlar vardır.", transliteration: "Ve lehum makamıu min hadid."},
  {id: 2617, sure_no: 22, ayet_no: 22, sure_adi: "Hac", text: "كُلَّمَٓا اَرَادُٓوا اَنْ يَخْرُجُوا مِنْهَا مِنْ غَمٍّ اُع۪يدُوا ف۪يهَا وَذُوقُوا عَذَابَ الْحَر۪يقِ۟", translation: "Azaptan kurtulmak için ne zaman oradan çıkmak isteyecek olurlarsa, yakıcı azabı tatmaları için oraya geri döndürülürler.", transliteration: "Kullema eradu en yahrucu minha min gammin uidu fiha ve zuku azabel harik."},
  {id: 2618, sure_no: 23, ayet_no: 23, sure_adi: "Hac", text: "اِنَّ اللّٰهَ يُدْخِلُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ يُحَلَّوْنَ ف۪يهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤً۬اۜ وَلِبَاسُهُمْ ف۪يهَا حَر۪يرٌ", translation: "Allah, iman eden ve salihatı yapanları, içinden ırmaklar akan Cennetlere koyacak. Onlar, orada altından bilezikler ve inciler ile süslenirler. Elbiseleri ipektendir.", transliteration: "İnnallahe yudhılullezine amenu ve amilus salihati cennatin tecri min tahtihel enharu yuhallevne fiha min esavira min zehebin ve lu'lua, ve libasuhum fiha harir."},
  {id: 2619, sure_no: 24, ayet_no: 24, sure_adi: "Hac", text: "وَهُدُٓوا اِلَى الطَّيِّبِ مِنَ الْقَوْلِۗ وَهُدُٓوا اِلٰى صِرَاطِ الْحَم۪يدِ", translation: "Sözün temiz-hoş olanına yönlendirildiler. Hamid'in yoluna yönlendirildiler.", transliteration: "Ve hudu ilet tayyibi minel kavli ve hudu ila sıratıl hamid."},
  {id: 2620, sure_no: 25, ayet_no: 25, sure_adi: "Hac", text: "اِنَّ الَّذ۪ينَ كَفَرُوا وَيَصُدُّونَ عَنْ سَب۪يلِ اللّٰهِ وَالْمَسْجِدِ الْحَرَامِ الَّذ۪ي جَعَلْنَاهُ لِلنَّاسِ سَوَٓاءًۨ الْعَاكِفُ ف۪يهِ وَالْبَادِۜ وَمَنْ يُرِدْ ف۪يهِ بِـاِلْحَادٍ بِظُـلْمٍ نُذِقْهُ مِنْ عَذَابٍ اَل۪يمٍ۟", translation: "Kafir olanlar ve Allah'ın yolundan ve Mescid-i Haram'dan engelleyenler -bilsinler ki- onu, orada yerleşik olan ve dışarıdan gelen bütün İnsanların eşitçe adanmaları için yaptık. Kim orada haksızlıkla sapkınlık yaparsa, ona acı azaptan tattıracağız.", transliteration: "İnnellezine keferu ve yasuddune an sebilillahi vel mescidil haramillezi cealnahu lin nasi sevaenil akıfu fihi vel bad, ve men yurid fihi bi ilhadin bi zulmin nuzıkhu min azabin elim."},
  {id: 2621, sure_no: 26, ayet_no: 26, sure_adi: "Hac", text: "وَاِذْ بَوَّأْنَا لِاِبْرٰه۪يمَ مَكَانَ الْبَيْتِ اَنْ لَا تُشْرِكْ ب۪ي شَيْـٔاً وَطَهِّرْ بَيْتِيَ لِلطَّٓائِف۪ينَ وَالْقَٓائِم۪ينَ وَالرُّكَّعِ السُّجُودِ", translation: "Bir zamanlar İbrahim'e evin yerini göstererek; \"Bana hiçbir şeyi ortak koşma. Evimi tavaf edenler, kaim olanlar, ruku edenler, secde edenler için arındır.\" demiştik.", transliteration: "Ve iz bevve'na li ibrahime mekanel beyti en la tuşrik bi şey'en ve tahhir beytiye lit taifine vel kaimine ver rukkais sucud."},
  {id: 2622, sure_no: 27, ayet_no: 27, sure_adi: "Hac", text: "وَاَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجَالاً وَعَلٰى كُلِّ ضَامِرٍ يَأْت۪ينَ مِنْ كُلِّ فَجٍّ عَم۪يقٍۙ", translation: "İnsanları Hacca çağır. Gerek yaya gerek her türlü binek üzerinde, uzak-yakın her yerden sana gelsinler.", transliteration: "Ve ezzin fin nasi bil hacci ye'tuke ricalen ve ala kulli damirin ye'tine min kulli feccin amik."},
  {id: 2623, sure_no: 28, ayet_no: 28, sure_adi: "Hac", text: "لِيَشْهَدُوا مَنَافِـعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللّٰهِ ف۪ٓي اَيَّامٍ مَعْلُومَاتٍ عَلٰى مَا رَزَقَهُمْ مِنْ بَه۪يمَةِ الْاَنْعَامِۚ فَكُلُوا مِنْهَا وَاَطْعِمُوا الْـبَٓائِسَ الْفَق۪يرَۘ", translation: "Bunun kendilerine sağlayacağı yararlara tanık olsunlar. Ve kendilerine rızık olarak verilen hayvanlar üzerine belli günlerde Allah'ın adını ansınlar. Böylece onlardan yiyin, muhtaç ve yoksul olanları doyurun.", transliteration: "Li yeşhedu menafia lehum ve yezkurusmallahi fi eyyamin ma'lumatin ala ma rezakahum min behimetil en'am, fe kulu minha ve at'ımul baisel fakir."},
  {id: 2624, sure_no: 29, ayet_no: 29, sure_adi: "Hac", text: "ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَلْيُوفُوا نُذُورَهُمْ وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَت۪يقِ", translation: "Sonra kirlerini gidersinler. Adaklarını yerine getirsinler. Ve Beyt-i Atik'i tavaf etsinler.", transliteration: "Summel yakdu tefesehum vel yufu nuzurahum vel yettavvefu bil beytil atik."},
  {id: 2625, sure_no: 30, ayet_no: 30, sure_adi: "Hac", text: "ذٰلِكَۗ وَمَنْ يُعَظِّمْ حُرُمَاتِ اللّٰهِ فَهُوَ خَيْرٌ لَهُ عِنْدَ رَبِّه۪ۜ وَاُحِلَّتْ لَكُمُ الْاَنْعَامُ اِلَّا مَا يُتْلٰى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْاَوْثَانِ وَاجْتَنِبُوا قَوْلَ الزُّورِۙ", translation: "İşte böyle. Kim Rabb'inin yasaklarına uymada gerekli özeni gösterirse, bu onun için daha hayırlıdır. Size okunanlar hariç, diğer hayvanlar helal kılındı. Artık putların pisliğinden kaçının. Yalan sözden kaçının.", transliteration: "Zalike ve men yuazzım hurumatillahi fe huve hayrun lehu inde rabbih, ve uhıllet lekumul en'amu illa ma yutla aleykum fectenibur ricse minel evsani vectenibu kavlez zur."},
  {id: 2626, sure_no: 31, ayet_no: 31, sure_adi: "Hac", text: "حُنَفَٓاءَ لِلّٰهِ غَيْرَ مُشْرِك۪ينَ بِه۪ۜ وَمَنْ يُشْرِكْ بِاللّٰهِ فَكَاَنَّمَا خَرَّ مِنَ السَّمَٓاءِ فَتَخْطَفُهُ الطَّيْرُ اَوْ تَهْو۪ي بِهِ الرّ۪يحُ ف۪ي مَكَانٍ سَح۪يقٍ", translation: "Hanifler, onunla Allah'a şirk koşmayanlardır. Allah'a şirk koşan kimse, sanki gökten düşen ve kuşun kaptığı veya rüzgarın uzak yerlere sürüklediği kimse gibidir.", transliteration: "Hunefae lillahi gayre muşrikine bih, ve men yuşrik billahi fe ke ennema harre mines semai fe tahtafuhut tayru ev tehvi bihir rihu fi mekanin sahik."},
  {id: 2627, sure_no: 32, ayet_no: 32, sure_adi: "Hac", text: "ذٰلِكَۗ وَمَنْ يُعَظِّمْ شَعَٓائِرَ اللّٰهِ فَاِنَّهَا مِنْ تَقْوَى الْقُلُوبِ", translation: "İşte böyle! Kim Allah'ın şiarlarına saygı gösterirse, kuşkusuz bu saygı kalplerin takvasındandır.", transliteration: "Zalike ve men yuazzım şeairallahi fe inneha min takval kulub."},
  {id: 2628, sure_no: 33, ayet_no: 33, sure_adi: "Hac", text: "لَكُمْ ف۪يهَا مَنَافِـعُ اِلٰٓى اَجَلٍ مُسَمًّى ثُمَّ مَحِلُّـهَٓا اِلَى الْبَيْتِ الْعَت۪يقِ۟", translation: "Sizin için onlarda belli bir süreye kadar faydalar vardır. Sonra bunların mahalli Beyt-i Atik'edir.", transliteration: "Lekum fiha menafiu ila ecelin musemmen summe mahılluha ilel beytil atik."},
  {id: 2629, sure_no: 34, ayet_no: 34, sure_adi: "Hac", text: "وَلِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكاً لِيَذْكُرُوا اسْمَ اللّٰهِ عَلٰى مَا رَزَقَهُمْ مِنْ بَه۪يمَةِ الْاَنْعَامِۜ فَاِلٰهُكُمْ اِلٰهٌ وَاحِدٌ فَلَـهُٓ اَسْلِمُواۜ وَبَشِّرِ الْمُخْبِت۪ينَۙ", translation: "Rızık olarak verilen hayvanların üzerine Allah'ın adını anmaları için bütün ümmetlere mensek tayin ettik. Sizin ilahınız bir tek ilahtır. Artık yalnızca ona teslim olun. Samimi İnsanları haberdar et.", transliteration: "Ve li kulli ummetin cealna menseken li yezkurusmallahi ala ma razakahum min behimetil en'am, fe ilahukum ilahun vahıdun fe lehu eslimu ve beşşiril muhbitin."},
  {id: 2630, sure_no: 35, ayet_no: 35, sure_adi: "Hac", text: "اَلَّذ۪ينَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوبُهُمْ وَالصَّابِر۪ينَ عَلٰى مَٓا اَصَابَهُمْ وَالْمُق۪يمِي الصَّلٰوةِۙ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ", translation: "Onlar ki Allah anıldığı zaman kalpleri ürperir, başlarına gelene sabrederler, salatı ikame ederler, kendilerini rızıklandırdığımız şeylerden infak ederler.", transliteration: "Ellezine iza zukirallahu vecilet kulubuhum vas sabirine ala ma esabehum vel mukimis salati ve mimma razaknahum yunfikun."},
  {id: 2631, sure_no: 36, ayet_no: 36, sure_adi: "Hac", text: "وَالْبُدْنَ جَعَلْنَاهَا لَكُمْ مِنْ شَعَٓائِرِ اللّٰهِ لَكُمْ ف۪يهَا خَيْرٌۗ فَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهَا صَوَٓافَّۚ فَاِذَا وَجَبَتْ جُنُوبُهَا فَكُلُوا مِنْهَا وَاَطْعِمُوا الْقَانِـعَ وَالْمُعْتَرَّۜ  كَذٰلِكَ سَخَّرْنَاهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ", translation: "Büyükbaş hayvanları da sizin için Allah'ın şiarlarından kıldık. Sizin için onlarda hayır vardır. Ayakları bağlı halde keserlerken üzerlerine Allah'ın adını anın. Yanları üzerine düşünce onları yiyin. İsteyene de istemeyene de yedirin. Böylece onları yararlanmanıza sunduk. Umulur ki şükredersiniz.", transliteration: "Vel budne cealnaha lekum min şeairillahi lekum fiha hayr, fezkurusmallahi aleyha savaff, fe iza vecebet cunubuha fe kulu minha ve at'ımul kania vel mu'terr, kezalike sahharnaha lekum leallekum teşkurun."},
  {id: 2632, sure_no: 37, ayet_no: 37, sure_adi: "Hac", text: "لَنْ يَنَالَ اللّٰهَ لُحُومُهَا وَلَا دِمَٓاؤُ۬هَا وَلٰكِنْ يَنَالُهُ التَّقْوٰى مِنْكُمْۜ  كَذٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰيكُمْۜ وَبَشِّرِ الْمُحْسِن۪ينَ", translation: "Onların ne etleri ne de kanları asla Allah'a ulaşmaz. Ona ulaşacak olan sizin takvanızdır. Sizi doğru yola ilettiğinden dolayı Allah'ı yüceltmeniz için onları sizin yararınıza sundu. İyi olan kimseleri müjdele.", transliteration: "Len yenalellahe luhumuha ve la dimauha ve lakin yenaluhut takva minkum, kezalike sahharaha lekum li tukebbirullahe ala ma hedakum, ve beşşiril muhsinin."},
  {id: 2633, sure_no: 38, ayet_no: 38, sure_adi: "Hac", text: "اِنَّ اللّٰهَ يُدَافِـعُ عَنِ الَّذ۪ينَ اٰمَنُواۜ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُورٍ۟", translation: "Allah, iman edenleri savunur. Allah, hain nankörleri sevmez.", transliteration: "İnnallahe yudafiu anillezine amenu, innallahe la yuhıbbu kulle havvanin kefur."},
  {id: 2634, sure_no: 39, ayet_no: 39, sure_adi: "Hac", text: "اُذِنَ لِلَّذ۪ينَ يُقَاتَلُونَ بِاَنَّهُمْ ظُلِمُواۜ وَاِنَّ اللّٰهَ عَلٰى نَصْرِهِمْ لَقَد۪يرٌۙ", translation: "Kendileriyle savaşılan kimselerin, zulme uğramaları nedeniyle, savaşmalarına izin verildi. Kuşkusuz Allah, onlara yardım etmeye kadirdir.", transliteration: "Uzine lillezine yukatelune bi ennehum zulim, ve innallahe ala nasrihim le kadir."},
  {id: 2635, sure_no: 40, ayet_no: 40, sure_adi: "Hac", text: "اَلَّذ۪ينَ اُخْرِجُوا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ اِلَّٓا اَنْ يَقُولُوا رَبُّنَا اللّٰهُۜ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَهُدِّمَتْ صَوَامِــعُ وَبِيَعٌ وَصَلَوَاتٌ وَمَسَاجِدُ يُذْكَرُ ف۪يهَا اسْمُ اللّٰهِ كَث۪يراًۜ وَلَيَنْصُرَنَّ اللّٰهُ مَنْ يَنْصُرُهُۜ اِنَّ اللّٰهَ لَقَوِيٌّ عَز۪يزٌ", translation: "Onlar, sadece \"Rabb'imiz Allah'tır.\" dedikleri için haksız yere yurtlarından çıkarıldılar. Eğer Allah, İnsanların bazılarını bazılarıyla savmasaydı, içinde Allah'ın isminin çokça anıldığı sevamiu, biyeun, selavat, mescitler mutlaka yıkılırdı. Allah, kendisine yardım edene mutlaka yardım eder. Kuşkusuz Allah, Mutlak Güç Sahibi'dir, Mutlak Üstün Olan'dır.", transliteration: "Ellezine uhricu min diyarihim bi gayri hakkın illa en yekulu rabbunallah, ve lev la def'ullahin nase ba'dahum bi ba'dın lehuddimet savamıu ve biyaun ve salavatun ve mesacidu yuzkeru fihesmullahi kesira, ve le yansurennallahu men yansuruh, innallahe le kaviyyun aziz."},
  {id: 2636, sure_no: 41, ayet_no: 41, sure_adi: "Hac", text: "اَلَّذ۪ينَ اِنْ مَكَّنَّاهُمْ فِي الْاَرْضِ اَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ وَاَمَرُوا بِالْمَعْرُوفِ وَنَهَوْا عَنِ الْمُنْكَرِۜ وَلِلّٰهِ عَاقِبَةُ الْاُمُورِ", translation: "Eğer yeryüzünde onları egemen kılarsak, salatı ikame ederler, zekatı yaparlar, ma'ruf olanı yapar, münkerden sakındırırlar. Bütün işlerin sonucu Allah'a dönecektir.", transliteration: "Ellezine in mekkennahum fil ardı ekamus salate ve atevuz zekate ve emeru bil ma'rufi ve nehev anil munker, ve lillahi akıbetul umur."},
  {id: 2637, sure_no: 42, ayet_no: 42, sure_adi: "Hac", text: "وَاِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَثَمُودُۙ", translation: "Eğer seni yalanlıyorlarsa, bil ki onlardan önce; Nuh, Ad ve Semud halkları da Nebilerini yalanlamışlardı.", transliteration: "Ve in yukezzibuke fe kad kezzebet kablehum kavmu nuhın ve adun ve semud."},
  {id: 2638, sure_no: 43, ayet_no: 43, sure_adi: "Hac", text: "وَقَوْمُ اِبْرٰه۪يمَ وَقَوْمُ لُوطٍۙ", translation: "İbrahim ve Lut halkları da yalanlamışlardı.", transliteration: "Ve kavmu ibrahime ve kavmu lut."},
  {id: 2639, sure_no: 44, ayet_no: 44, sure_adi: "Hac", text: "وَاَصْحَابُ مَدْيَنَۚ وَكُذِّبَ مُوسٰى فَاَمْلَيْتُ لِلْكَافِر۪ينَ ثُمَّ اَخَذْتُهُمْۚ فَكَيْفَ كَانَ نَك۪يرِ", translation: "Ve Medyen sahipleri de. Musa da yalanlandı. Fakat Kafirlere süre tanıdım. Sonra da onları yakaladım. Benim inkarım nasılmış gördüler.", transliteration: "Ve ashabu medyen, ve kuzzibe musa fe emleytu lil kafirine summe ehaztuhum, fe keyfe kane nekir."},
  {id: 2640, sure_no: 45, ayet_no: 45, sure_adi: "Hac", text: "فَكَاَيِّنْ مِنْ قَرْيَةٍ اَهْلَكْنَاهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلٰى عُرُوشِهَا وَبِئْرٍ مُعَطَّـلَةٍ وَقَصْرٍ مَش۪يدٍ", translation: "Böylece zulüm yapmalarından dolayı nice kentleri yok ettik. Çatıları çökmüş, duvarları yıkılmış; sahipsiz kalmış kuyular ve görkemli saraylar vardır.", transliteration: "Fe ke eyyin min karyetin ehleknaha ve hiye zalimetun fe hiye haviyetun ala uruşiha ve bi'rin muattalatin ve kasrın meşid."},
  {id: 2641, sure_no: 46, ayet_no: 46, sure_adi: "Hac", text: "اَفَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِهَٓا اَوْ اٰذَانٌ يَسْمَعُونَ بِهَاۚ فَاِنَّهَا لَا تَعْمَى الْاَبْصَارُ وَلٰكِنْ تَعْمَى الْقُلُوبُ الَّت۪ي فِي الصُّدُورِ", translation: "Onlar, yeryüzünde dolaşmıyorlar mı ki kendilerinin, kendisi ile akıl edecekleri kalpleri veya kendisi ile işitecek kulakları olsun. Gerçek şu ki, kör olan gözler değildir, kör olan göğüslerde olan kalplerdir.", transliteration: "E fe lem yesiru fil ardı fe tekune lehum kulubun ya'kılune biha ev azanunyesmeune biha, fe inneha la ta'mal ebsaru ve lakin ta'mal kulubulleti fis sudur."},
  {id: 2642, sure_no: 47, ayet_no: 47, sure_adi: "Hac", text: "وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ وَلَنْ يُخْلِفَ اللّٰهُ وَعْدَهُۜ وَاِنَّ يَوْماً عِنْدَ رَبِّكَ كَاَلْفِ سَنَةٍ مِمَّا تَعُدُّونَ", translation: "Ve senden azabı çabuklaştırmanı istiyorlar. Oysa Allah, sözünden asla dönmez. Kuşkusuz Rabb'ine göre bir gün, sizin saydıklarınızdan bin yıl gibidir.", transliteration: "Ve yesta'ciluneke bil azabi ve len yuhlifallahu va'deh, ve inne yevmen inde rabbike ke elfi senetin mimma teuddun."},
  {id: 2643, sure_no: 48, ayet_no: 48, sure_adi: "Hac", text: "وَكَاَيِّنْ مِنْ قَرْيَةٍ اَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ اَخَذْتُهَاۚ وَاِلَيَّ الْمَص۪يرُ۟", translation: "Zulmedip duran nice kentler vardı ki onlara süre vermiştim. Sonra onları yakaladım. Ve dönüş Bana'dır.", transliteration: "Ve ke eyyin min karyetin emleytu leha ve hiye zalimetun summe ehaztuha, ve ileyyel masir."},
  {id: 2644, sure_no: 49, ayet_no: 49, sure_adi: "Hac", text: "قُلْ يَٓا اَيُّهَا النَّاسُ اِنَّـمَٓا اَنَا۬ لَكُمْ نَذ۪يرٌ مُب۪ينٌۚ", translation: "De ki: \"Ey İnsanlar! Ben sizin için yalnızca apaçık bir uyarıcıyım.\"", transliteration: "Kul ya eyyuhen nasu innema ene lekum nezirun mubin."},
  {id: 2645, sure_no: 50, ayet_no: 50, sure_adi: "Hac", text: "فَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ", translation: "İman edip salihatı yapanlar için bağışlanma ve bol rızık vardır.", transliteration: "Fellezine amenu ve amilus salihati lehum magfiretun ve rızkun kerim."},
  {id: 2646, sure_no: 51, ayet_no: 51, sure_adi: "Hac", text: "وَالَّذ۪ينَ سَعَوْا ف۪ٓي اٰيَاتِنَا مُعَاجِز۪ينَ اُو۬لٰٓئِكَ اَصْحَابُ الْجَح۪يمِ", translation: "Ayetlerimizi geçersiz bırakma yarışında olanlar, işte onlar Cehennem ehlidir.", transliteration: "Vellezine seav fi ayatina muacizine ulaike ashabul cehim."},
  {id: 2647, sure_no: 52, ayet_no: 52, sure_adi: "Hac", text: "وَمَٓا اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَلَا نَبِيٍّ اِلَّٓا اِذَا تَمَنّٰٓى اَلْقَى الشَّيْطَانُ ف۪ٓي اُمْنِيَّتِه۪ۚ فَيَنْسَخُ اللّٰهُ مَا يُلْقِي الشَّيْطَانُ ثُمَّ يُحْكِمُ اللّٰهُ اٰيَاتِه۪ۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌۙ", translation: "Senden önce gönderdiğimiz her Resul ve Nebi, bir şey dilediği zaman, şeytan onun bu dileğine bir şeyler katmak istedi. Fakat Allah, şeytanın kattığı şeyleri yok eder. Sonra Allah, kendi ayetlerini sağlamlaştırır. Ve Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve ma erselna min kablike min resulin ve la nebiyyin illa iza temenna elkaş şeytanu fi umniyyetih, fe yensehullahu ma yulkış şeytanu summe yuhkimullahu ayatih, vallahu alimun hakim."},
  {id: 2648, sure_no: 53, ayet_no: 53, sure_adi: "Hac", text: "لِيَجْعَلَ مَا يُلْقِي الشَّيْطَانُ فِتْنَةً لِلَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ وَالْقَاسِيَةِ قُلُوبُهُمْۜ وَاِنَّ الظَّالِم۪ينَ لَف۪ي شِقَاقٍ بَع۪يدٍۙ", translation: "Kalplerinde hastalık olan ve kalpleri kararıp katılaşmış olanlara, şeytanın kattığı şeyi, sınav vesilesi kıldık. Zalimler derin bir yanılgı içindedirler.", transliteration: "Li yec'ale ma yulkış şeytanu fitneten lillezine fi kulubihim maradun vel kasiyeti kulubuhum, ve innez zalimine le fi şikakın baid."},
  {id: 2649, sure_no: 54, ayet_no: 54, sure_adi: "Hac", text: "وَلِيَعْلَمَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ اَنَّهُ الْحَقُّ مِنْ رَبِّكَ فَيُؤْمِنُوا بِه۪ فَتُخْبِتَ لَهُ قُلُوبُهُمْۜ وَاِنَّ اللّٰهَ لَهَادِ الَّذ۪ينَ اٰمَنُٓوا اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "İlim verilen kimselerin, onun Rabb'inden gelen bir gerçek olduğunu bilmeleri, ona iman etmeleri ve kalplerinin tatmin olması içindir. Allah, İman Edenler'e dosdoğru yolu gösterendir.", transliteration: "Ve li ya'lemellezine utul ılme ennehul hakku min rabbike fe yu'minu bihi fe tuhbite lehu kulubuhum, ve innallahe le hadillezine amenu ila sıratın mustakim."},
  {id: 2650, sure_no: 55, ayet_no: 55, sure_adi: "Hac", text: "وَلَا يَزَالُ الَّذ۪ينَ كَفَرُوا ف۪ي مِرْيَةٍ مِنْهُ حَتّٰى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً اَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَق۪يمٍ", translation: "Kafirler de Sa'at ansızın gelinceye veya ardından başka bir gün olmayan günün azabı gelinceye kadar ondan kuşku duymaya devam edeceklerdir.", transliteration: "Ve la yezalullezine keferu fi miryetin minhu hatta te'tiyehumus saatu bagteten ev ye'tiyehum azabu yevmin akim."},
  {id: 2651, sure_no: 56, ayet_no: 56, sure_adi: "Hac", text: "اَلْمُلْكُ يَوْمَئِذٍ لِلّٰهِۜ يَحْكُمُ بَيْنَهُمْۜ فَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ ف۪ي جَنَّاتِ النَّع۪يمِ", translation: "Egemenlik, O Gün tamamen Allah'a aittir, İnsanlar arasında yalnızca O hüküm verir. Artık iman eden ve salihatı yapanlar, Nimet Cennetlerindedirler.", transliteration: "El mulku yevme izin lillah, yahkumu beynehum, fellezine amenu ve amilus salihati fi cennatin naim."},
  {id: 2652, sure_no: 57, ayet_no: 57, sure_adi: "Hac", text: "وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَا فَاُو۬لٰٓئِكَ لَهُمْ عَذَابٌ مُه۪ينٌ", translation: "Kafir olan ve ayetlerimizi yalanlayan kimseler için alçaltıcı bir azap vardır.", transliteration: "Vellezine keferu ve kezzebu bi ayatina fe ulaike lehum azabun muhin."},
  {id: 2653, sure_no: 58, ayet_no: 58, sure_adi: "Hac", text: "وَالَّذ۪ينَ هَاجَرُوا ف۪ي سَب۪يلِ اللّٰهِ ثُمَّ قُتِلُٓوا اَوْ مَاتُوا لَيَرْزُقَنَّهُمُ اللّٰهُ رِزْقاً حَسَناًۜ وَاِنَّ اللّٰهَ لَهُوَ خَيْرُ الرَّازِق۪ينَ", translation: "Allah yolunda hicret edip de sonra öldürülmüş ve ölmüş olanları, Allah kesinlikle iyi bir rızıkla rızıklandıracaktır. Kuşkusuz Allah, rızık verenlerin en hayırlısıdır.", transliteration: "Vellezine haceru fi sebilillahi summe kutilu ev matu le yerzukannehumullahu rızkan hasena, ve innallahe le huve hayrur razikin."},
  {id: 2654, sure_no: 59, ayet_no: 59, sure_adi: "Hac", text: "لَيُدْخِلَنَّهُمْ مُدْخَلاً يَرْضَوْنَهُۜ وَاِنَّ اللّٰهَ لَعَل۪يمٌ حَل۪يمٌ", translation: "Onları kesinlikle hoşnut olacakları bir yere yerleştirecektir. Allah, Her Şeyi Bilen'dir, Çok Şefkatli'dir.", transliteration: "Le yudhılennehum mudhalen yerdavneh, ve innallahe le alimun halim."},
  {id: 2655, sure_no: 60, ayet_no: 60, sure_adi: "Hac", text: "ذٰلِكَۚ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوقِبَ بِه۪ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللّٰهُۜ اِنَّ اللّٰهَ لَعَفُوٌّ غَفُورٌ", translation: "Bu böyledir! Kim kendisine yapılan haksız bir saldırıya aynı oranda karşılık verirse, sonra yine saldırıya uğrarsa Allah ona yardım eder. Allah, Çok Affedici'dir, Çok Bağışlayıcı'dır.", transliteration: "Zalik, ve men akabe bi misli ma ukıbe bihi summe bugıye aleyhi le yansurennehullah, innallahe le afuvvun gafur."},
  {id: 2656, sure_no: 61, ayet_no: 61, sure_adi: "Hac", text: "ذٰلِكَ بِاَنَّ اللّٰهَ يُولِجُ الَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الَّيْلِ وَاَنَّ اللّٰهَ سَم۪يعٌ بَص۪يرٌ", translation: "Bu böyledir! Allah, geceyi gündüze, gündüzü de geceye döndürür. Allah, Her Şeyi Duyan'dır, Her Şeyi Gören'dir.", transliteration: "Zalike bi ennallahe yulicul leyle fin nehari ve yulicun nehare fil leyli ve ennallahe semiun basir."},
  {id: 2657, sure_no: 62, ayet_no: 62, sure_adi: "Hac", text: "ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُونَ مِنْ دُونِه۪ هُوَ الْبَاطِلُ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَب۪يرُ", translation: "İşte böyledir! Allah Hakk'ın ta kendisidir. O'ndan başka yöneldikleriniz ise Batıl'dır. Allah, Çok Yüce'dir, Çok Büyük'tür.", transliteration: "Zalike bi ennallahe huvel hakku ve enne ma yed'une min dunihi huvel batılu ve ennallahe huvel aliyyul kebir."},
  {id: 2658, sure_no: 63, ayet_no: 63, sure_adi: "Hac", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۘ فَتُصْبِـحُ الْاَرْضُ مُخْضَرَّةًۜ اِنَّ اللّٰهَ لَط۪يفٌ خَب۪يرٌۚ", translation: "Allah'ın gökten indirdiği su ile yeryüzünü nasıl yeşerttiğini görmüyor musun? Allah, Bütün Ayrıntıları Bilen'dir, Her Şeyden Haberdar'dır.", transliteration: "E lem tere ennallahe enzele mines semai maen fe tusbihul ardu muhdarreh, innallahe latifun habir."},
  {id: 2659, sure_no: 64, ayet_no: 64, sure_adi: "Hac", text: "لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاِنَّ اللّٰهَ لَهُوَ الْغَنِيُّ الْحَم۪يدُ۟", translation: "Göklerde ve yeryüzünde olan her şey O'nundur. Kuşkusuz Allah, Hiçbir Şeye Muhtaç Olmayan'dır, Övgüye Değer Yegane Varlık'tır.", transliteration: "Lehu ma fis semavati ve ma fil ard, ve innallahe le huvel ganiyyul hamid."},
  {id: 2660, sure_no: 65, ayet_no: 65, sure_adi: "Hac", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَا فِي الْاَرْضِ وَالْفُلْكَ تَجْر۪ي فِي الْبَحْرِ بِاَمْرِه۪ۜ وَيُمْسِكُ السَّمَٓاءَ اَنْ تَقَعَ عَلَى الْاَرْضِ اِلَّا بِاِذْنِه۪ۜ اِنَّ اللّٰهَ بِالنَّاسِ لَرَؤُ۫فٌ رَح۪يمٌ", translation: "Allah'ın, yeryüzünde olanları emrinize amade kıldığını görmedin mi? Ve gemiler denizde O'nun emri ile akıp gider. Yeryüzüne düşmesin diye göğü iradesiyle O tutuyor. Kuşkusuz Allah, İnsanlara Çok Şefkatli'dir. Rahmeti Kesintisiz'dir.", transliteration: "E lem tere ennallahe sahhara lekum ma fil ardı vel fulke tecri fil bahri bi emrih, ve yumsikus semae en tekaa alel ardı illa bi iznih, innallahe bin nasi le raufun rahim."},
  {id: 2661, sure_no: 66, ayet_no: 66, sure_adi: "Hac", text: "وَهُوَ الَّـذ۪ٓي اَحْيَاكُمْۘ ثُمَّ يُم۪يتُكُمْ ثُمَّ يُحْي۪يكُمْۜ اِنَّ الْاِنْسَانَ لَكَفُورٌ", translation: "Ve size hayat veren, sonra sizi öldürecek olan, sonra da sizi diriltecek olan O'dur. İnsan gerçekten çok nankördür.", transliteration: "Ve huvellezi ahyakum summe yumitukum summe yuhyikum, innel insane le kefur."},
  {id: 2662, sure_no: 67, ayet_no: 67, sure_adi: "Hac", text: "لِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكاً هُمْ نَاسِكُوهُ فَلَا يُنَازِعُنَّكَ فِي الْاَمْرِ وَادْعُ اِلٰى رَبِّكَۜ اِنَّكَ لَعَلٰى هُدًى مُسْتَق۪يمٍ", translation: "Her ümmet için bir mensek belirledik. Herkes kendi mensekini sürdürür. O halde bu işte seninle çekişmesinler. Sen, Rabb'ine çağır. Kuşkusuz ki sen dosdoğru bir yol üzerindesin.", transliteration: "Li kulli ummetin cealna menseken hum nasikuhu fe la yunaziunneke fil emri ved'u ila rabbik, inneke le ala huden mustekim."},
  {id: 2663, sure_no: 68, ayet_no: 68, sure_adi: "Hac", text: "وَاِنْ جَادَلُوكَ فَقُلِ اللّٰهُ اَعْلَمُ بِمَا تَعْمَلُونَ", translation: "Eğer seninle tartışırlarsa, o zaman: \"Allah yapmakta olduğunuz şeyleri en iyi bilendir.\" de.", transliteration: "Ve in cadeluke fe kulillahu a'lemu bima ta'melun."},
  {id: 2664, sure_no: 69, ayet_no: 69, sure_adi: "Hac", text: "اَللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كُنْتُمْ ف۪يهِ تَخْتَلِفُونَ", translation: "Allah, Kıyamet Günü üzerinde görüş ayrılığına düştüğünüz konularda aranızda hüküm verecektir.", transliteration: "Allahu yahkumu beynekum yevmel kıyameti fima kuntum fihi tahtelifun."},
  {id: 2665, sure_no: 70, ayet_no: 70, sure_adi: "Hac", text: "اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ يَعْلَمُ مَا فِي السَّمَٓاءِ وَالْاَرْضِۜ اِنَّ ذٰلِكَ ف۪ي كِتَابٍۜ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌ", translation: "Allah'ın gökte ve yerde olan şeyleri kesinlikle bildiğini bilmez misin? Bu bir Kitap'tadır. Bu Allah'a kolaydır.", transliteration: "E lem ta'lem ennallahe ya'lemu ma fis semai vel ard, inne zalike fi kitab, inne zalike alallahi yesir."},
  {id: 2666, sure_no: 71, ayet_no: 71, sure_adi: "Hac", text: "وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَمْ يُنَزِّلْ بِه۪ سُلْطَاناً وَمَا لَيْسَ لَهُمْ بِه۪ عِلْمٌۜ وَمَا لِلظَّالِم۪ينَ مِنْ نَص۪يرٍ", translation: "Onlar; Allah'ın yanı sıra O'nun kendileri hakkında bir sultan indirmediği ve hakkında bilgi sahibi olmadıkları şeylere kulluk ediyorlar. Zalimler için hiçbir yardım edici yoktur.", transliteration: "Ve ya'budune min dunillahi ma lem yunezzil bihi sultanen ve ma leyse lehum bihi ılm, ve ma liz zalimine min nasir."},
  {id: 2667, sure_no: 72, ayet_no: 72, sure_adi: "Hac", text: "وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ تَعْرِفُ ف۪ي وُجُوهِ الَّذ۪ينَ كَفَرُوا الْمُنْكَرَۜ يَكَادُونَ يَسْطُونَ بِالَّذ۪ينَ يَتْلُونَ عَلَيْهِمْ اٰيَاتِنَاۜ قُلْ اَفَاُنَبِّئُكُمْ بِشَرٍّ مِنْ ذٰلِكُمْۜ اَلنَّارُۜ وَعَدَهَا اللّٰهُ الَّذ۪ينَ كَفَرُواۜ وَبِئْسَ الْمَص۪يرُ۟", translation: "Onlara apaçık ayetlerimiz okunduğu zaman, o Kafirlerin yüzlerindeki hoşnutsuzluğu görürsün. Nerede ise kendilerine ayetlerimizi okuyanlara saldıracaklar. De ki: \"Size bundan daha kötüsünü haber vereyim mi? Ateş! Allah'ın Kafirlere sözüdür. O, ne kötü bir varış yeridir.\"", transliteration: "Ve iza tutla aleyhim ayatuna beyyinatin ta'rifu fi vucuhillezine keferul munker, yekadune yestune billezine yetlune aleyhim ayatina, kul e fe unebbiukum bi şerrin min zalikum, en nar, vaadehallahullezine keferu, ve bi'sel masir."},
  {id: 2668, sure_no: 73, ayet_no: 73, sure_adi: "Hac", text: "يَٓا اَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوا لَهُۜ اِنَّ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ لَنْ يَخْلُقُوا ذُبَاباً وَلَوِ اجْتَمَعُوا لَهُۜ وَاِنْ يَسْلُبْهُمُ الذُّبَابُ شَيْـٔاً لَا يَسْتَنْقِذُوهُ مِنْهُۜ ضَعُفَ الطَّالِبُ وَالْمَطْلُوبُ", translation: "Ey İnsanlar! Verilen örneği dikkatle dinleyin: Allah'ın yanı sıra kulluk ettikleriniz bir araya gelseler, kesinlikle bir sineği bile yaratamazlar. Değil yaratmak, sinek onlardan bir şey kapsa, onu bile kurtaramazlar. İsteyen de kendisinden istenen de ne kadar acizdir.", transliteration: "Ya eyyuhen nasu duribe meselun festemiu leh, innellezine ted'une min dunillahi len yahluku zubaben ve levictemeu leh, ve in yeslubhumuz zubabu şey'en la yestenkızuhu minh, daufat talibu vel matlub."},
  {id: 2669, sure_no: 74, ayet_no: 74, sure_adi: "Hac", text: "مَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِه۪ۜ اِنَّ اللّٰهَ لَقَوِيٌّ عَز۪يزٌ", translation: "Allah'ı gereği gibi takdir edemediler! Kuşkusuz Allah, Mutlak Güç Sahibi'dir, Mutlak Üstün Olan'dır.", transliteration: "Ma kaderullahe hakka kadrih, innallahe le kaviyyun aziz."},
  {id: 2670, sure_no: 75, ayet_no: 75, sure_adi: "Hac", text: "اَللّٰهُ يَصْطَف۪ي مِنَ الْمَلٰٓئِكَةِ رُسُلاً وَمِنَ النَّاسِۜ اِنَّ اللّٰهَ سَم۪يعٌ بَص۪يرٌۚ", translation: "Allah; meleklerden de resuller seçer, İnsanlardan da. Kuşkusuz, Allah, Her Şeyden Haberdar Olan'dır, Her Şeyi Gören'dir.", transliteration: "Allahu yastafi minel melaiketi rusulen ve minen nas, innallahe semiun basir."},
  {id: 2671, sure_no: 76, ayet_no: 76, sure_adi: "Hac", text: "يَعْلَمُ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ", translation: "Onların geçmişte yaptıklarını da gelecekte yapacaklarını da bilir. Ve işler yalnızca Allah'a döndürülür.", transliteration: "Ya'lemu ma beyne eydihim ve ma halfehum, ve ilallahi turceul umur."},
  {id: 2672, sure_no: 77, ayet_no: 77, sure_adi: "Hac", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا ارْكَعُوا وَاسْجُدُوا وَاعْبُدُوا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَۚ", translation: "Ey İman Edenler! Ruku edin, secde edin, Rabb'inize kulluk edin, hayır yapın ki kurtuluşa erebilesiniz.", transliteration: "Ya eyyuhellezine amenurkeu vescudu va'budu rabbekum vef'alul hayre leallekum tuflihun."},
  {id: 2673, sure_no: 78, ayet_no: 78, sure_adi: "Hac", text: "وَجَاهِدُوا فِي اللّٰهِ حَقَّ جِهَادِه۪ۜ هُوَ اجْتَبٰيكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِي الدّ۪ينِ مِنْ حَرَجٍۜ مِلَّةَ اَب۪يكُمْ اِبْرٰه۪يمَۜ هُوَ سَمّٰيكُمُ الْمُسْلِم۪ينَ مِنْ قَبْلُ وَف۪ي هٰذَا لِيَكُونَ الرَّسُولُ شَه۪يداً عَلَيْكُمْ وَتَكُونُوا شُهَدَٓاءَ عَلَى النَّاسِۚ فَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاعْتَصِمُوا بِاللّٰهِۜ هُوَ مَوْلٰيكُمْۚ فَنِعْمَ الْمَوْلٰى وَنِعْمَ النَّص۪يرُ", translation: "Allah yolunda gerektiği gibi cihad edin. O sizi seçti. Dinde size bir zorluk yüklemedi. Bu atanız İbrahim'in milleti. O, daha önce de şimdi de sizi Müslimler olarak isimlendirdi. Resul, size tanık olsun, siz de diğer İnsanlara. Öyleyse salatı ikame edin, zekatı yapın ve Allah'a sımsıkı bağlanın. O, sizin mevlanızdır. Ne güzel Mevla ne güzel yardımcıdır.", transliteration: "Ve cahidu fillahi hakka cihadih, huvectebakum ve ma ceale aleykum fid dini min harac, millete ebikum ibrahim, huve semmakumul muslimine min kablu ve fi haza li yekuner resulu şehiden aleykum ve tekunu şuhedae alen nas, fe ekimus salate ve atuz zekate va'tesımu billah, huve mevlakum, fe ni'mel mevla ve ni'men nasir."},
  {id: 2674, sure_no: 1, ayet_no: 1, sure_adi: "Müminun", text: "قَدْ اَفْلَحَ الْمُؤْمِنُونَۙ", translation: "Mü'minler gerçekten kurtuluşa ermişlerdir.", transliteration: "Kad eflehal mu'minun."},
  {id: 2675, sure_no: 2, ayet_no: 2, sure_adi: "Müminun", text: "اَلَّذ۪ينَ هُمْ ف۪ي صَلَاتِهِمْ خَاشِعُونَۙ", translation: "Onlar, salatlarında huşu içinde olan kimselerdir.", transliteration: "Ellezine hum fi salatihim haşiun."},
  {id: 2676, sure_no: 3, ayet_no: 3, sure_adi: "Müminun", text: "وَالَّذ۪ينَ هُمْ عَنِ اللَّغْوِ مُعْرِضُونَۙ", translation: "Onlar boş şeylerden yüz çevirirler.", transliteration: "Vellezine hum anil lagvi mu'ridun."},
  {id: 2677, sure_no: 4, ayet_no: 4, sure_adi: "Müminun", text: "وَالَّذ۪ينَ هُمْ لِلزَّكٰوةِ فَاعِلُونَۙ", translation: "Ve onlar, zekat için çalışanlardır.", transliteration: "Vellezine hum liz zekati failun."},
  {id: 2678, sure_no: 5, ayet_no: 5, sure_adi: "Müminun", text: "وَالَّذ۪ينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَۙ", translation: "Ve onlar, ırzlarını korurlar.", transliteration: "Vellezine hum li furucihim hafizun."},
  {id: 2679, sure_no: 6, ayet_no: 6, sure_adi: "Müminun", text: "اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوم۪ينَۚ", translation: "Eşleri veya yeminle sahip oldukları hariç. Bundan dolayı kınanmazlar.", transliteration: "İlla ala ezvacihim ev ma meleket eymanuhum fe innehum gayru melumin."},
  {id: 2680, sure_no: 7, ayet_no: 7, sure_adi: "Müminun", text: "فَمَنِ ابْتَغٰى وَرَٓاءَ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْعَادُونَۚ", translation: "Kim bunun ötesinde bir şey isterse, işte onlar haddi aşanlardır.", transliteration: "Fe menibtega verae zalike fe ulaike humul adun."},
  {id: 2681, sure_no: 8, ayet_no: 8, sure_adi: "Müminun", text: "وَالَّذ۪ينَ هُمْ لِاَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَۙ", translation: "Onlar, kendilerine verilen emanetler için sözlerine bağlı kalan kimselerdir.", transliteration: "Vellezine hum li emanatihim ve ahdihim raun."},
  {id: 2682, sure_no: 9, ayet_no: 9, sure_adi: "Müminun", text: "وَالَّذ۪ينَ هُمْ عَلٰى صَلَوَاتِهِمْ يُحَافِظُونَۢ", translation: "Onlar, salatlarını koruyan kimselerdir.", transliteration: "Vellezine hum ala salavatihim yuhafızun."},
  {id: 2683, sure_no: 10, ayet_no: 10, sure_adi: "Müminun", text: "اُو۬لٰٓئِكَ هُمُ الْوَارِثُونَۙ", translation: "İşte onlar varis olanlardır.", transliteration: "Ulaike humul varisun."},
  {id: 2684, sure_no: 11, ayet_no: 11, sure_adi: "Müminun", text: "اَلَّذ۪ينَ يَرِثُونَ الْفِرْدَوْسَۜ هُمْ ف۪يهَا خَالِدُونَ", translation: "Onlar, Firdevs'e varis olacaklar ve orada sürekli kalacaklardır.", transliteration: "Ellezine yerisunel firdevs, hum fiha halidun."},
  {id: 2685, sure_no: 12, ayet_no: 12, sure_adi: "Müminun", text: "وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ سُلَالَةٍ مِنْ ط۪ينٍۚ", translation: "Ant olsun ki, İnsanı çamurun özünden yarattık.", transliteration: "Ve lekad halaknal insane min sulaletin min tin."},
  {id: 2686, sure_no: 13, ayet_no: 13, sure_adi: "Müminun", text: "ثُمَّ جَعَلْنَاهُ نُطْفَةً ف۪ي قَرَارٍ مَك۪ينٍۖ", translation: "Sonra onu güvenli ve sağlam bir mekanda bir nutfe kıldık.", transliteration: "Summe cealnahu nutfeten fi kararin mekin."},
  {id: 2687, sure_no: 14, ayet_no: 14, sure_adi: "Müminun", text: "ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظَاماً فَكَسَوْنَا الْعِظَامَ لَحْماًۗ ثُمَّ اَنْشَأْنَاهُ خَلْقاً اٰخَرَۜ فَتَبَارَكَ اللّٰهُ اَحْسَنُ الْخَالِق۪ينَۜ", translation: "Sonra nutfeyi bir alaka olarak yarattık. Alakayı da mudğa olarak yarattık. Mudğadan da kemikleri yarattık. Kemiklere de et giydirdik. Sonra onu başka bir yaratılışla şekillendirdik. Yaratıcıların en iyisi olan Allah ne yücedir.", transliteration: "Summe halaknen nutfete alakaten fe halaknel alakate mudgaten fe halaknel mudgate ızamen fe kesevnel izame lahmen summe enşe'nahu halkan ahar, fe tebarekallahu ahsenul halikin."},
  {id: 2688, sure_no: 15, ayet_no: 15, sure_adi: "Müminun", text: "ثُمَّ اِنَّكُمْ بَعْدَ ذٰلِكَ لَمَيِّتُونَۜ", translation: "Sonra kuşku yok ki bunun ardından öleceksiniz.", transliteration: "Summe innekum ba'de zalike le meyyitun."},
  {id: 2689, sure_no: 16, ayet_no: 16, sure_adi: "Müminun", text: "ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ تُبْعَثُونَ", translation: "Sonra kuşkusuz siz kıyamet günü diriltileceksiniz.", transliteration: "Summe innekum yevmel kıyameti tub'asun."},
  {id: 2690, sure_no: 17, ayet_no: 17, sure_adi: "Müminun", text: "وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَٓائِقَۗ وَمَا كُنَّا عَنِ الْخَلْقِ غَافِل۪ينَ", translation: "Ant olsun ki üzerinizde yolların yedisini yarattık. Ve Biz yarattıklarımızdan habersiz değiliz.", transliteration: "Ve lekad halakna fevkakum seb'a taraika ve ma kunna anil halkı gafilin."},
  {id: 2691, sure_no: 18, ayet_no: 18, sure_adi: "Müminun", text: "وَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً بِقَدَرٍ فَاَسْكَنَّاهُ فِي الْاَرْضِۗ وَاِنَّا عَلٰى ذَهَابٍ بِه۪ لَقَادِرُونَۚ", translation: "Gökten kararınca su indirdik. Ve onu yeryüzünde yerleştirdik. Kuşkusuz Biz, onu gidermeye de gücü yetenleriz.", transliteration: "Ve enzelna mines semai maen bi kaderin fe eskennahu fil ardı ve inna ala zehabin bihi le kadirun."},
  {id: 2692, sure_no: 19, ayet_no: 19, sure_adi: "Müminun", text: "فَاَنْشَأْنَا لَكُمْ بِه۪ جَنَّاتٍ مِنْ نَخ۪يلٍ وَاَعْنَابٍۢ لَكُمْ ف۪يهَا فَوَاكِهُ۬ كَث۪يرَةٌ وَمِنْهَا تَأْكُلُونَۙ", translation: "Böylece onunla, sizin için hurma ve üzüm cennetleri meydana getirdik. Keza, oralarda yemekte olduğunuz pek çok meyve çeşitleri vardır.", transliteration: "Fe enşe'na lekum bihi cennatin min nahilin ve a'nab, lekum fiha fevakihu kesiretun ve minha te'kulun."},
  {id: 2693, sure_no: 20, ayet_no: 20, sure_adi: "Müminun", text: "وَشَجَرَةً تَخْرُجُ مِنْ طُورِ سَيْنَٓاءَ تَنْبُتُ بِالدُّهْنِ وَصِبْغٍ لِلْاٰكِل۪ينَ", translation: "Tur-i Seynae'da, yağ elde edilen ve aynı zamanda katık olarak yenen ağaç yetiştirdik.", transliteration: "Ve şecereten tahrucu min turi seynae tenbutu bid duhni ve sıbgın lil akilin."},
  {id: 2694, sure_no: 21, ayet_no: 21, sure_adi: "Müminun", text: "وَاِنَّ لَكُمْ فِي الْاَنْعَامِ لَعِبْرَةًۜ نُسْق۪يكُمْ مِمَّا ف۪ي بُطُونِهَا وَلَكُمْ ف۪يهَا مَنَافِعُ كَث۪يرَةٌ وَمِنْهَا تَأْكُلُونَۙ", translation: "Hayvanlardan da sizin için alınacak dersler vardır. Onların karınlarındaki şeyden size içiriyoruz. Ve onlarda sizin için pek çok yararlar vardır. Ve onlardan yersiniz.", transliteration: "Ve inne lekum fil en'ami le ibreh, nuskikum mimma fi butuniha ve lekum fiha menafiu kesiretun ve minha te'kulun."},
  {id: 2695, sure_no: 22, ayet_no: 22, sure_adi: "Müminun", text: "وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَ۟", translation: "Onları ve gemileri taşıma aracı olarak kullanırsınız.", transliteration: "Ve aleyha ve alel fulki tuhmelun."},
  {id: 2696, sure_no: 23, ayet_no: 23, sure_adi: "Müminun", text: "وَلَقَدْ اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ فَقَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ  اَفَلَا تَتَّقُونَ", translation: "Ant olsun ki Biz, Nuh'u halkına gönderdik. \"Ey halkım! Allah'a kulluk edin. Sizin için O'ndan başka ilah yoktur. Hala takva sahibi olmayacak mısınız?\" dedi.", transliteration: "Ve lekad erselna nuhan ila kavmihi fe kale ya kavmi' budullahe ma lekum min ilahin gayruh, e fe la tettekun."},
  {id: 2697, sure_no: 24, ayet_no: 24, sure_adi: "Müminun", text: "فَقَالَ الْمَلَؤُا الَّذ۪ينَ كَفَرُوا مِنْ قَوْمِه۪ مَا هٰذَٓا اِلَّا بَشَرٌ مِثْلُكُمْۙ يُر۪يدُ اَنْ يَتَفَضَّلَ عَلَيْكُمْۜ وَلَوْ شَٓاءَ اللّٰهُ لَاَنْزَلَ مَلٰٓئِكَةًۚ مَا سَمِعْنَا بِهٰذَا ف۪ٓي اٰبَٓائِنَا الْاَوَّل۪ينَۚ", translation: "Bunun üzerine halkından Kafir meleler: \"Bu, sizin gibi bir beşerden başka bir şey değildir. Size karşı üstünlük kurmak istiyor. Eğer Allah isteseydi mutlaka melekler indirirdi. Geçmiş atalarımızdan da böyle bir şey duymadık.\" dediler.", transliteration: "Fe kalel meleullezine keferu min kavmihi ma haza illa beşerun mıslukum yuridu en yetefaddale aleykum, ve lev şaallahu le enzele melaikeh, ma semi'na bi haza fi abainel evvelin."},
  {id: 2698, sure_no: 25, ayet_no: 25, sure_adi: "Müminun", text: "اِنْ هُوَ اِلَّا رَجُلٌ بِه۪ جِنَّةٌ فَتَرَبَّصُوا بِه۪ حَتّٰى ح۪ينٍ", translation: "\"O ancak cinlenmiş bir adamdır. O halde kesinlikle bir süre bekleyin.\"", transliteration: "İn huve illa raculun bihi cinnetun fe terabbasu bihi hatta hin."},
  {id: 2699, sure_no: 26, ayet_no: 26, sure_adi: "Müminun", text: "قَالَ رَبِّ انْصُرْن۪ي بِمَا كَذَّبُونِ", translation: "Nuh: \"Rabb'im! Yalanlamalarına karşı bana yardım et!\" dedi.", transliteration: "Kale rabbinsurni bima kezzebun."},
  {id: 2700, sure_no: 27, ayet_no: 27, sure_adi: "Müminun", text: "فَاَوْحَيْنَٓا اِلَيْهِ اَنِ اصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا فَاِذَا جَٓاءَ اَمْرُنَا وَفَارَ التَّنُّورُۙ فَاسْلُكْ ف۪يهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْۚ وَلَا تُخَاطِبْن۪ي فِي الَّذ۪ينَ ظَلَمُواۚ اِنَّهُمْ مُغْرَقُونَ", translation: "Bunun üzerine Biz, ona: \"Gözetimimiz altında vahyimiz ile bildirdiğimiz gibi gemi yap.\" diye vahyettik. \"Böylece emrimiz gereği Tennur kaynadığı zaman hemen ona her cinsten eşler olarak iki adet ve ehlini bindir. Onlardan, haklarında önceden hüküm verilenler hariç. Ve zulmedenler hakkında Ben'den bir dilekte bulunma. Onlar boğulacak olanlardır.\"", transliteration: "Fe evhayna ileyhi enısnaıl fulke bi a'yunina ve vahyina fe iza cae emruna ve faret tennuru fesluk fiha min kullin zevceynisneyni ve ehleke illa men sebeka aleyhil kavlu minhum, ve la tuhatıbni fillezine zalemu, innehum mugrakun."},
  {id: 2701, sure_no: 28, ayet_no: 28, sure_adi: "Müminun", text: "فَاِذَا اسْتَوَيْتَ اَنْتَ وَمَنْ مَعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلّٰهِ الَّذ۪ي نَجّٰينَا مِنَ الْقَوْمِ الظَّالِم۪ينَ", translation: "Sen ve seninle birlikte olanlar gemiye bindiğiniz zaman: \"Zalim halktan bizi kurtaran Allah'a hamdolsun.\" de.", transliteration: "Fe izesteveyte ente ve men meake alel fulki fe kulil hamdu lillahillezi neccana minel kavmiz zalimin."},
  {id: 2702, sure_no: 29, ayet_no: 29, sure_adi: "Müminun", text: "وَقُلْ رَبِّ اَنْزِلْن۪ي مُنْزَلاً مُبَارَكاً وَاَنْتَ خَيْرُ الْمُنْزِل۪ينَ", translation: "Ve de ki: \"Rabbim! Beni kutlu, bereketli bir yere indir. Ve Sen doğru yere yerleştireceklerin en hayırlısısın.\"", transliteration: "Ve kul rabbi enzilni munzelen mubareken ve ente hayrul munzilin."},
  {id: 2703, sure_no: 30, ayet_no: 30, sure_adi: "Müminun", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ وَاِنْ كُنَّا لَمُبْتَل۪ينَ", translation: "Bunda kesinlikle ayetler vardır. Ve Biz kesinlikle sınayanlarız.", transliteration: "İnne fi zalike le ayatin ve in kunna le mubtelin."},
  {id: 2704, sure_no: 31, ayet_no: 31, sure_adi: "Müminun", text: "ثُمَّ اَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْناً اٰخَر۪ينَۚ", translation: "Sonra onların ardından başka bir nesil ortaya çıkardık.", transliteration: "Summe enşe'na min ba'dihim karnen aharin."},
  {id: 2705, sure_no: 32, ayet_no: 32, sure_adi: "Müminun", text: "فَاَرْسَلْنَا ف۪يهِمْ رَسُولاً مِنْهُمْ اَنِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِنْ اِلٰهٍ غَيْرُهُۜ  اَفَلَا تَتَّقُونَ۟", translation: "Onlara, kendi içlerinden, \"Allah'a kulluk edin, sizin O'ndan başka ilahınız yoktur, hala takva sahibi olmayacak mısınız?\" diyen bir Resul gönderdik.", transliteration: "Fe erselna fihim resulen minhum eni'budullahe ma lekum min ilahin gayruh, e fe la tettekun."},
  {id: 2706, sure_no: 33, ayet_no: 33, sure_adi: "Müminun", text: "وَقَالَ الْمَلَأُ مِنْ قَوْمِهِ الَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِلِقَٓاءِ الْاٰخِرَةِ وَاَتْرَفْنَاهُمْ فِي الْحَيٰوةِ الدُّنْيَاۙ مَا هٰذَٓا اِلَّا بَشَرٌ مِثْلُكُمْۙ يَأْكُلُ مِمَّا تَأْكُلُونَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُونَ", translation: "Dünya hayatında refaha kavuşturduğumuz, Kafirlik eden ve ahirete kavuşmayı yalanlayan kavminin meleleri: \"Bu da sizin gibi bir beşerdir. Sizin yediğinizden yiyor, sizin içtiğinizden içiyor.\" dediler.", transliteration: "Ve kalel meleu min kavmihillezine keferu ve kezzebu bi likail ahıreti ve etrafnahum fil hayatid dunya ma haza illa beşerun mislukum ye'kulu mimma te'kulune minhu yeşrebu mimma teşrabun."},
  {id: 2707, sure_no: 34, ayet_no: 34, sure_adi: "Müminun", text: "وَلَئِنْ اَطَعْتُمْ بَشَراً مِثْلَكُمْ اِنَّكُمْ اِذاً لَخَاسِرُونَ", translation: "\"Eğer siz, sizden hiçbir farkı olmayan böyle bir beşere uyacak olursanız, kesinlikle hüsrana uğrarsınız.", transliteration: "Ve lein eta'tum beşeren mislekum innekum izen le hasirun."},
  {id: 2708, sure_no: 35, ayet_no: 35, sure_adi: "Müminun", text: "اَيَعِدُكُمْ اَنَّكُمْ اِذَا مِتُّمْ وَكُنْتُمْ تُرَاباً وَعِظَاماً اَنَّكُمْ مُخْرَجُونَۖ", translation: "\"Sizi; ölüp, toprak ve kemik haline geldikten sonra, yeniden diriltileceğinizle mi uyarıyor?\"", transliteration: "E yaıdukum ennekum iza mittum ve kuntum turaben ve izamen ennekum muhracun."},
  {id: 2709, sure_no: 36, ayet_no: 36, sure_adi: "Müminun", text: "هَيْهَاتَ هَيْهَاتَ لِمَا تُوعَدُونَۖ", translation: "\"Yazık, yazık! Uyarıldığınız şey, hiç olacak şey mi?\"", transliteration: "Heyhate heyhate lima tuadun."},
  {id: 2710, sure_no: 37, ayet_no: 37, sure_adi: "Müminun", text: "اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوث۪ينَۖ", translation: "\"Hayat, yalnızca bu dünyadan ibarettir. Yaşarız ve ölürüz. Yeniden diriltilecek değiliz.\"", transliteration: "İn hiye illa hayatuned dunya nemutu ve nahya ve ma nahnu bi meb'usin."},
  {id: 2711, sure_no: 38, ayet_no: 38, sure_adi: "Müminun", text: "اِنْ هُوَ اِلَّا رَجُلٌۨ افْـتَرٰى عَلَى اللّٰهِ كَذِباً وَمَا نَحْنُ لَهُ بِمُؤْمِن۪ينَ", translation: "\"O, uydurduğu yalanı Allah'a dayandıran bir kimsedir. Ona inanacak değiliz.\"", transliteration: "İn huve illa raculuniftera alallahi keziben ve ma nahnu lehu bi mu'minin."},
  {id: 2712, sure_no: 39, ayet_no: 39, sure_adi: "Müminun", text: "قَالَ رَبِّ انْصُرْن۪ي بِمَا كَذَّبُونِ", translation: "\"Rabbim! Yalanlamaları nedeniyle bana yardım et.\" dedi.", transliteration: "Kale rabbinsurni bima kezzebun."},
  {id: 2713, sure_no: 40, ayet_no: 40, sure_adi: "Müminun", text: "قَالَ عَمَّا قَل۪يلٍ لَيُصْبِحُنَّ نَادِم۪ينَۚ", translation: "Allah: \"Pek yakında kesinlikle pişman olacaklar.\" dedi.", transliteration: "Kale amma kalilin le yusbihunne nadimin."},
  {id: 2714, sure_no: 41, ayet_no: 41, sure_adi: "Müminun", text: "فَاَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنَاهُمْ غُـثَٓاءًۚ فَبُعْداً لِلْقَوْمِ الظَّالِم۪ينَ", translation: "Derken, kaçınılmaz bir gerçek olarak sayha onları yakalayıverdi. Böylece onları çerçöp yaptık. Zalim halk yok oldu.", transliteration: "Fe ehazethumus sayhatu bil hakkı fe cealnahum gusaen, fe bu'den lil kavmiz zalimin."},
  {id: 2715, sure_no: 42, ayet_no: 42, sure_adi: "Müminun", text: "ثُمَّ اَنْشَأْنَا مِنْ بَعْدِهِمْ قُرُوناً اٰخَر۪ينَۜ", translation: "Sonra, onların ardından başka nesiller getirdik.", transliteration: "Summe enşe'na min ba'dihim kurunen aharin."},
  {id: 2716, sure_no: 43, ayet_no: 43, sure_adi: "Müminun", text: "مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُونَۜ", translation: "Hiçbir ümmet ecelini öne alamaz ve erteleyemez.", transliteration: "Ma tesbiku min ummetin eceleha ve ma yeste'hırun."},
  {id: 2717, sure_no: 44, ayet_no: 44, sure_adi: "Müminun", text: "ثُمَّ اَرْسَلْنَا رُسُلَنَا تَتْرَاۜ  كُلَّمَا جَٓاءَ اُمَّةً رَسُولُهَا كَذَّبُوهُ فَاَتْبَعْنَا بَعْضَهُمْ بَعْضاً وَجَعَلْنَاهُمْ اَحَاد۪يثَۚ فَبُعْداً لِقَوْمٍ لَا يُؤْمِنُونَ", translation: "Sonra ardı ardına elçilerimizi gönderdik. Her ümmet, kendilerine gelen Resulleri yalanladı. Biz de onları ardı sıra devirdik. Ve onları halkın dilinde hadis konusu yaptık. İman etmeyen halk uzak olsun.", transliteration: "Summe erselna rusulena tetra, kullema cae ummeten resuluha kezzebuhu fe etba'na ba'dahum ba'dan ve cealnahum ehadis, fe bu'den li kavmin la yu'minun."},
  {id: 2718, sure_no: 45, ayet_no: 45, sure_adi: "Müminun", text: "ثُمَّ اَرْسَلْنَا مُوسٰى وَاَخَاهُ هٰرُونَ بِاٰيَاتِنَا وَسُلْطَانٍ مُب۪ينٍۙ", translation: "Sonra da Musa ve kardeşi Harun'u ayetlerimizle ve apaçık bir görevle gönderdik;", transliteration: "Summe erselna musa ve ehahu harune bi ayatina ve sultanin mubin."},
  {id: 2719, sure_no: 46, ayet_no: 46, sure_adi: "Müminun", text: "اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ فَاسْتَكْـبَرُوا وَكَانُوا قَوْماً عَال۪ينَۚ", translation: "Firavun ve melelerine. Ancak onlar kibirlendiler. Büyüklük taslayan bir halk oldular.", transliteration: "İla fir'avne ve meleihi festekberu ve kanu kavmen alin."},
  {id: 2720, sure_no: 47, ayet_no: 47, sure_adi: "Müminun", text: "فَقَالُٓوا اَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عَابِدُونَۚ", translation: "Sonra da: \"Bizden farkı olmayan, üstelik halkı da bize kulluk eden bu iki beşere mi inanacağız?\" dediler.", transliteration: "Fe kalu e nu'minu li beşereyni mislina ve kavmuhuma lena abidun."},
  {id: 2721, sure_no: 48, ayet_no: 48, sure_adi: "Müminun", text: "فَكَذَّبُوهُمَا فَكَانُوا مِنَ الْمُهْلَك۪ينَ", translation: "Onları yalanladılar. Ve helak edilenlerden oldular.", transliteration: "Fe kezzebuhuma fe kanu minel muhlekin."},
  {id: 2722, sure_no: 49, ayet_no: 49, sure_adi: "Müminun", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ لَعَلَّهُمْ يَهْتَدُونَ", translation: "Ve ant olsun, onlar doğru yolu bulsunlar diye Musa'ya Kitap'ı verdik.", transliteration: "Ve lekad ateyna musel kitabe leallehum yehtedun."},
  {id: 2723, sure_no: 50, ayet_no: 50, sure_adi: "Müminun", text: "وَجَعَلْنَا ابْنَ مَرْيَمَ وَاُمَّهُٓ اٰيَةً وَاٰوَيْنَاهُمَٓا اِلٰى رَبْوَةٍ ذَاتِ قَرَارٍ وَمَع۪ينٍ۟", translation: "Meryem Oğlu'nu ve annesini bir ayet kıldık. Ve ikisini, suyu olan yerleşime uygun bir tepeye yerleştirdik.", transliteration: "Ve cealnebne meryeme ve ummehu ayeten ve aveynahuma ila rabvetin zati kararin ve main."},
  {id: 2724, sure_no: 51, ayet_no: 51, sure_adi: "Müminun", text: "يَٓا اَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّبَاتِ وَاعْمَلُوا صَالِحاًۜ اِنّ۪ي بِمَا تَعْمَلُونَ عَل۪يمٌۜ", translation: "Ey Resuller! Helal ve temiz şeylerden yiyin. Ve salihatı yapın. Kuşkusuz yaptığınız şeyleri bilirim.", transliteration: "Ya eyyuher rusulu kulu minet tayyibati va'melu saliha, inni bima ta'melune alim."},
  {id: 2725, sure_no: 52, ayet_no: 52, sure_adi: "Müminun", text: "وَاِنَّ هٰذِه۪ٓ  اُمَّتُكُمْ اُمَّةً وَاحِدَةً وَاَنَا۬ رَبُّكُمْ فَاتَّقُونِ", translation: "Bu sizin ümmetiniz, tek bir ümmettir. Ben de sizin Rabb'inizim. O halde bana karşı takva sahibi olun.", transliteration: "Ve inne hazihi ummetukum ummeten vahıdeten ve ene rabbukum fettekun."},
  {id: 2726, sure_no: 53, ayet_no: 53, sure_adi: "Müminun", text: "فَـتَقَطَّـعُٓوا اَمْرَهُمْ بَيْنَهُمْ زُبُراًۜ  كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ", translation: "Sonra işlerini aralarında parça parça ettiler. Her bir grup kendine olanla yetinmektedir.", transliteration: "Fe tekattau emrehum beynehum zubura, kullu hızbin bima ledeyhim ferihun."},
  {id: 2727, sure_no: 54, ayet_no: 54, sure_adi: "Müminun", text: "فَذَرْهُمْ ف۪ي غَمْرَتِهِمْ حَتّٰى ح۪ينٍ", translation: "Artık onları belli bir süreye kadar aymazlıkları ile baş başa bırak!", transliteration: "Fe zerhum fi gamratihim hatta hin."},
  {id: 2728, sure_no: 55, ayet_no: 55, sure_adi: "Müminun", text: "اَيَحْسَبُونَ اَنَّمَا نُمِدُّهُمْ بِه۪ مِنْ مَالٍ وَبَن۪ينَۙ", translation: "Mal ve oğullarla onlara iyilik yaptığımızı mı sanıyorlar?", transliteration: "E yahsebune ennema numidduhum bihi min malin ve benin."},
  {id: 2729, sure_no: 56, ayet_no: 56, sure_adi: "Müminun", text: "نُسَارِعُ لَهُمْ فِي الْخَيْرَاتِۜ بَلْ لَا يَشْعُرُونَ", translation: "Hayırlarını çabuklaştırdığımızı mı sanıyorlar? Hayır, gerçeği kavrayacak bilinçte değiller.", transliteration: "Nusariu lehum fil hayrat bel la yeş'urun."},
  {id: 2730, sure_no: 57, ayet_no: 57, sure_adi: "Müminun", text: "اِنَّ الَّذ۪ينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَۙ", translation: "Rabb'lerine karşı derin saygı duyan kimseler, O'nu incitmekten çekinirler.", transliteration: "İnnellezine hum min haşyeti rabbihim muşfikun."},
  {id: 2731, sure_no: 58, ayet_no: 58, sure_adi: "Müminun", text: "وَالَّذ۪ينَ هُمْ بِاٰيَاتِ رَبِّهِمْ يُؤْمِنُونَۙ", translation: "Onlar, Rabb'lerinin ayetlerine inanırlar.", transliteration: "Vellezine hum bi ayati rabbihim yu'minun."},
  {id: 2732, sure_no: 59, ayet_no: 59, sure_adi: "Müminun", text: "وَالَّذ۪ينَ هُمْ بِرَبِّهِمْ لَا يُشْرِكُونَۙ", translation: "Onlar, Rabb'lerine şirk koşmazlar.", transliteration: "Vellezine hum bi rabbihim la yuşrikun."},
  {id: 2733, sure_no: 60, ayet_no: 60, sure_adi: "Müminun", text: "وَالَّذ۪ينَ يُؤْتُونَ مَٓا اٰتَوْا وَقُلُوبُهُمْ وَجِلَةٌ اَنَّهُمْ اِلٰى رَبِّهِمْ رَاجِعُونَۙ", translation: "Rabb'lerine döneceklerinin bilinciyle, derin bir saygı içinde vermeleri gerekenleri verirler.", transliteration: "Vellezine yu'tune ma atev ve kulubuhum veciletun ennehum ila rabbihim raciun."},
  {id: 2734, sure_no: 61, ayet_no: 61, sure_adi: "Müminun", text: "اُو۬لٰٓئِكَ يُسَارِعُونَ فِي الْخَيْرَاتِ وَهُمْ لَهَا سَابِقُونَ", translation: "İşte onlar, hayırlarda yarışırlar ve onda öncülük ederler.", transliteration: "Ulaike yusariune fil hayrati ve hum leha sabikun."},
  {id: 2735, sure_no: 62, ayet_no: 62, sure_adi: "Müminun", text: "وَلَا نُكَلِّفُ نَفْساً اِلَّا وُسْعَهَا وَلَدَيْنَا كِتَابٌ يَنْطِقُ بِالْحَقِّ وَهُمْ لَا يُظْلَمُونَ", translation: "Hiç kimseyi gücünün yettiğinden fazlasıyla sorumlu tutmayız. Nezdimizde gerçeği söyleyen bir kitap vardır. Onlar haksızlığa uğratılmazlar.", transliteration: "Ve la nukellifu nefsen illa vus'aha ve ledeyna kitabun yantıku bil hakkı ve hum la yuzlemun."},
  {id: 2736, sure_no: 63, ayet_no: 63, sure_adi: "Müminun", text: "بَلْ قُلُوبُهُمْ ف۪ي غَمْرَةٍ مِنْ هٰذَا وَلَهُمْ اَعْمَالٌ مِنْ دُونِ ذٰلِكَ هُمْ لَهَا عَامِلُونَ", translation: "Fakat onların kalpleri bundan gaflet içindedir. Onlar, başka işlerle uğraşıp durmaktadırlar.", transliteration: "Bel kulubuhum fi gamratin min haza ve lehum a'malun min duni zalike hum leha amilun."},
  {id: 2737, sure_no: 64, ayet_no: 64, sure_adi: "Müminun", text: "حَتّٰٓى اِذَٓا اَخَذْنَا مُتْرَف۪يهِمْ بِالْعَذَابِ اِذَا هُمْ يَجْـَٔرُونَۜ", translation: "Nihayet varlıklılarını azapla yakaladığımızda, hemen feryat etmeye başlarlar.", transliteration: "Hatta iza ehazna mutrafihim bil azabi iza hum yec'erun."},
  {id: 2738, sure_no: 65, ayet_no: 65, sure_adi: "Müminun", text: "لَا تَجْـَٔرُوا الْيَوْمَ اِنَّكُمْ مِنَّا لَا تُنْصَرُونَ", translation: "Bugün boşuna feryat etmeyin. Kesinlikle Bize karşı size yardım olunmaz.", transliteration: "La tec'erul yevme innekum minna la tunsarun."},
  {id: 2739, sure_no: 66, ayet_no: 66, sure_adi: "Müminun", text: "قَدْ كَانَتْ اٰيَات۪ي تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ عَلٰٓى اَعْقَابِكُمْ تَنْكِصُونَۙ", translation: "Vaktinde ayetlerimiz size okunduğunda siz onları hiçe sayıyordunuz.", transliteration: "Kad kanet ayati tutla aleykum fe kuntum ala a'kabikum tenkisun."},
  {id: 2740, sure_no: 67, ayet_no: 67, sure_adi: "Müminun", text: "مُسْتَكْبِر۪ينَ بِه۪ۗ سَامِراً تَهْجُرُونَ", translation: "Ayetlerime karşı büyüklük taslayarak, geceleri toplanıp saçma sapan değerlendirmeler yapıyordunuz.", transliteration: "Mustekbirine bihi samiran tehcurun."},
  {id: 2741, sure_no: 68, ayet_no: 68, sure_adi: "Müminun", text: "اَفَلَمْ يَدَّبَّرُوا الْقَوْلَ اَمْ جَٓاءَهُمْ مَا لَمْ يَأْتِ اٰبَٓاءَهُمُ الْاَوَّل۪ينَۘ", translation: "Onlar, hala sözün üzerinde düşünmüyorlar mı? Yoksa onlara, atalarına gelmemiş bir şey mi geldi?", transliteration: "E fe lem yeddebberul kavle em caehum ma lem ye'ti abaehumul evvelin."},
  {id: 2742, sure_no: 69, ayet_no: 69, sure_adi: "Müminun", text: "اَمْ لَمْ يَعْرِفُوا رَسُولَهُمْ فَهُمْ لَهُ مُنْكِرُونَۘ", translation: "Yoksa Resul'lerinin nasıl birisi olduğunu bilmiyorlar da onun için mi onu reddediyorlar?", transliteration: "Em lem ya'rifu resulehum fe hum lehu munkirun."},
  {id: 2743, sure_no: 70, ayet_no: 70, sure_adi: "Müminun", text: "اَمْ يَقُولُونَ بِه۪ جِنَّةٌۜ  بَلْ جَٓاءَهُمْ بِالْحَقِّ وَاَكْثَرُهُمْ لِلْحَقِّ كَارِهُونَ", translation: "Yoksa onda bir delilik olduğunu mu söylüyorlar? Hayır, onlara, Hakk ile geldi. Ne var ki onların çoğu Hakk'tan hoşlanmıyorlar.", transliteration: "Em yekulune bihi cinneh, bel caehum bil hakkı ve ekseruhum lil hakkı karihun."},
  {id: 2744, sure_no: 71, ayet_no: 71, sure_adi: "Müminun", text: "وَلَوِ اتَّبَعَ الْحَقُّ اَهْوَٓاءَهُمْ لَفَسَدَتِ السَّمٰوَاتُ وَالْاَرْضُ وَمَنْ ف۪يهِنَّۜ بَلْ اَتَيْنَاهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُعْرِضُونَۜ", translation: "Eğer Hakk onların hevalarına göre belirlenseydi gökler, yer ve onların içindekiler bozguna uğrardı. Hayır, faydalarına olacak zikirlerini getirdik. Ne var ki onlar faydalarına olan zikirden yüz çevirenlerdir.", transliteration: "Ve levittebeal hakku ehvaehum le fesedetis semavatu vel ardu ve men fi hinn, bel eteynahum bi zikrihim fe hum an zikrihim mu'ridun."},
  {id: 2745, sure_no: 72, ayet_no: 72, sure_adi: "Müminun", text: "اَمْ تَسْـَٔلُهُمْ خَرْجاً فَخَرَاجُ رَبِّكَ خَيْرٌۗ وَهُوَ خَيْرُ الرَّازِق۪ينَ", translation: "Yoksa onlardan bir karşılık mı bekliyorsun? Rabb'inin vereceği karşılık daha hayırlıdır. O rızıklandıranların en hayırlısıdır.", transliteration: "Em tes'eluhum harcen fe haracu rabbike hayrun ve huve hayrur razikin."},
  {id: 2746, sure_no: 73, ayet_no: 73, sure_adi: "Müminun", text: "وَاِنَّكَ لَتَدْعُوهُمْ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Kuşkusuz sen onları dosdoğru bir yola çağırıyorsun.", transliteration: "Ve inneke le ted'uhum ila sıratın mustakim."},
  {id: 2747, sure_no: 74, ayet_no: 74, sure_adi: "Müminun", text: "وَاِنَّ  الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُونَ", translation: "Kuşkusuz ahirete inanmayanlar, bu doğru yoldan sapanlardır.", transliteration: "Ve innellezine la yu'minune bil ahıreti anis sıratı le nakibun."},
  {id: 2748, sure_no: 75, ayet_no: 75, sure_adi: "Müminun", text: "وَلَوْ رَحِمْنَاهُمْ وَكَشَفْنَا مَا بِهِمْ مِنْ ضُرٍّ لَلَجُّوا ف۪ي طُغْيَانِهِمْ يَعْمَهُونَ", translation: "Onlara, rahmet edip sıkıntılarını gidersek, yine de azgınlıklarına devam ederler.", transliteration: "Ve lev rahımnahum ve keşefna ma bihim min durrin le leccu fi tugyanihim ya'mehun."},
  {id: 2749, sure_no: 76, ayet_no: 76, sure_adi: "Müminun", text: "وَلَقَدْ اَخَذْنَاهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوا لِرَبِّهِمْ وَمَا يَتَضَرَّعُونَ", translation: "Ant olsun, onları azap ile yakaladık. Ancak Rabbleri için uslanmadılar ve tedarruda bulunmadılar.", transliteration: "Ve lekad ehaznahum bil azabi fe mestekanu li rabbihim ve ma yetedarreun."},
  {id: 2750, sure_no: 77, ayet_no: 77, sure_adi: "Müminun", text: "حَتّٰٓى اِذَا فَتَحْنَا عَلَيْهِمْ بَاباً ذَا عَذَابٍ شَد۪يدٍ اِذَا هُمْ ف۪يهِ مُبْلِسُونَ۟", translation: "Ne var ki üzerlerine şiddetli bir azap kapısı açtığımız zaman, bütün ümitleri boşa çıkacaktır.", transliteration: "Hatta iza fetahna aleyhim baben za azabin şedidin iza hum fihi mublisun."},
  {id: 2751, sure_no: 78, ayet_no: 78, sure_adi: "Müminun", text: "وَهُوَ الَّـذ۪ٓي اَنْشَاَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٔدَةَۜ قَل۪يلاً مَا تَشْكُرُونَ", translation: "Size; işitme, görme ve ef'ide veren O'dur. Ne kadar az şükrediyorsunuz?", transliteration: "Ve huvellezi enşee lekumus sem'a vel ebsara vel ef'ideh, kalilen ma teşkurun."},
  {id: 2752, sure_no: 79, ayet_no: 79, sure_adi: "Müminun", text: "وَهُوَ الَّذ۪ي ذَرَاَكُمْ فِي الْاَرْضِ وَاِلَيْهِ تُحْشَرُونَ", translation: "Sizi yeryüzünde çoğaltan O'dur. Ve O'nun huzurunda toplanacaksınız.", transliteration: "Ve huvellezi zereekum fil ardı ve ileyhi tuhşerun."},
  {id: 2753, sure_no: 80, ayet_no: 80, sure_adi: "Müminun", text: "وَهُوَ الَّذ۪ي يُحْـي۪ وَيُم۪يتُ وَلَهُ اخْتِلَافُ الَّيْلِ وَالنَّهَارِۜ اَفَلَا تَعْقِلُونَ", translation: "Hayat veren de öldüren de O'dur. Gece ve gündüzün oluşması O'nun yasalarına göredir. Siz hala aklınızı kullanmayacak mısınız?", transliteration: "Ve huvellezi yuhyi ve yumitu ve lehuhtilaful leyli ven nehar, e fe la ta'kılun."},
  {id: 2754, sure_no: 81, ayet_no: 81, sure_adi: "Müminun", text: "بَلْ قَالُوا مِثْلَ مَا قَالَ الْاَوَّلُونَ", translation: "Ancak onlar öncekilerin dediklerinin aynısını dediler.", transliteration: "Bel kalu misle ma kalel evvelun."},
  {id: 2755, sure_no: 82, ayet_no: 82, sure_adi: "Müminun", text: "قَالُٓوا ءَاِذَا مِتْنَا وَكُنَّا تُرَاباً وَعِظَاماً ءَاِنَّا لَمَبْعُوثُونَ", translation: "Dediler ki: \"Ölüp de toprak ve kemik yığını olduktan sonra mı diriltileceğiz?\"", transliteration: "Kalu e iza mitna ve kunna turaben ve izamen e inna le meb'usun."},
  {id: 2756, sure_no: 83, ayet_no: 83, sure_adi: "Müminun", text: "لَقَدْ وُعِدْنَا نَحْنُ وَاٰبَٓاؤُ۬نَا هٰذَا مِنْ قَبْلُ اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ", translation: "\"Ant olsun ki bize yapılan bu uyarı, daha önce atalarımıza da yapılmıştı. Bu eskilerin masallarından başka bir şey değildir.\"", transliteration: "Lekad vuıdna nahnu ve abauna haza min kablu in haza illa esatirul evvelin."},
  {id: 2757, sure_no: 84, ayet_no: 84, sure_adi: "Müminun", text: "قُلْ لِمَنِ الْاَرْضُ وَمَنْ ف۪يهَٓا اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "De ki: \"Söyleyin bakalım, yeryüzü ve onda bulunanlar kime aittir?\"", transliteration: "Kul li menil ardu ve men fiha in kuntum ta'lemun."},
  {id: 2758, sure_no: 85, ayet_no: 85, sure_adi: "Müminun", text: "سَيَقُولُونَ لِلّٰهِۜ قُلْ اَفَلَا تَذَكَّرُونَ", translation: "\"Allah'a aittir.\" diyecekler. De ki: \"Hala öğüt almayacak mısınız?\"", transliteration: "Seyekulune lillah, kul e fe la tezekkerun."},
  {id: 2759, sure_no: 86, ayet_no: 86, sure_adi: "Müminun", text: "قُلْ مَنْ رَبُّ السَّمٰوَاتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظ۪يمِ", translation: "De ki: \"Yedi göğün ve muazzam arşın Rabb'i kimdir?\"", transliteration: "Kul men rabbus semavatis seb'ı ve rabbul arşil azim."},
  {id: 2760, sure_no: 87, ayet_no: 87, sure_adi: "Müminun", text: "سَيَقُولُونَ لِلّٰهِۜ قُلْ اَفَلَا تَتَّقُونَ", translation: "\"Allah'tır.\" diyecekler. De ki: \"O halde takva sahibi olmayacak mısınız?\"", transliteration: "Seyekulune lillah, kul e fe la tettekun."},
  {id: 2761, sure_no: 88, ayet_no: 88, sure_adi: "Müminun", text: "قُلْ مَنْ بِيَدِه۪ مَلَكُوتُ كُلِّ شَيْءٍ وَهُوَ يُج۪يرُ وَلَا يُجَارُ عَلَيْهِ اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "Sor bakalım: \"Evrenin egemenliğine sahip olan, koruyup gözeten ve kendisine karşı kimsenin korunamayacağı kimdir? Biliyorsanız söyleyin!\"", transliteration: "Kul men bi yedihi melekutu kulli şey'in ve huve yuciru ve la yucaru aleyhi in kuntum ta'lemun."},
  {id: 2762, sure_no: 89, ayet_no: 89, sure_adi: "Müminun", text: "سَيَقُولُونَ لِلّٰهِۜ قُلْ فَاَنّٰى تُسْحَرُونَ", translation: "\"Allah'tır.\" diyecekler. De ki: \"Öyleyse nasıl oluyor da aldanıyorsunuz?\"", transliteration: "Seyekulune lillah, kul fe enna tusharun."},
  {id: 2763, sure_no: 90, ayet_no: 90, sure_adi: "Müminun", text: "بَلْ اَتَيْنَاهُمْ بِالْحَقِّ وَاِنَّهُمْ لَكَاذِبُونَ", translation: "Oysa onlara Hakk'ı sunuyoruz. Onlar ise kesinlikle yalanlamaktadırlar.", transliteration: "Bel eteynahum bil hakkı ve innehum le kazibun."},
  {id: 2764, sure_no: 91, ayet_no: 91, sure_adi: "Müminun", text: "مَا اتَّخَذَ اللّٰهُ مِنْ وَلَدٍ وَمَا كَانَ مَعَهُ مِنْ اِلٰهٍ اِذاً لَذَهَبَ كُلُّ اِلٰهٍ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلٰى بَعْضٍۜ سُبْحَانَ اللّٰهِ عَمَّا يَصِفُونَۙ", translation: "Allah, çocuk edinmemiştir. Ve O'nun yanı sıra bir ilah daha yoktur. Eğer olsaydı her ilah kendi yarattığı ile birlikte hareket eder ve kimisi kimisine üstün olurdu. Allah, onların niteledikleri şeylerden münezzehtir.", transliteration: "Mettehazallahu min veledin ve ma kane meahu min ilahin izen le zehebe kullu ilahin bima halaka ve le ala ba'duhum ala ba'd, subhanallahi amma yasıfun."},
  {id: 2765, sure_no: 92, ayet_no: 92, sure_adi: "Müminun", text: "عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعَالٰى عَمَّا يُشْرِكُونَ۟", translation: "O, gizliyi de açığı da bilir. Onların şirk koştuklarından çok yücedir.", transliteration: "Alimil gaybi veş şehadeti fe teala amma yuşrikun."},
  {id: 2766, sure_no: 93, ayet_no: 93, sure_adi: "Müminun", text: "قُلْ رَبِّ اِمَّا تُرِيَنّ۪ي مَا يُوعَدُونَۙ", translation: "De ki: \"Rabb'im! Eğer onlara uyarısı yapılan azabı bana göstereceksen;\"", transliteration: "Kul rabbi imma turiyenni ma yuadun."},
  {id: 2767, sure_no: 94, ayet_no: 94, sure_adi: "Müminun", text: "رَبِّ فَلَا تَجْعَلْن۪ي فِي الْقَوْمِ الظَّالِم۪ينَ", translation: "\"Rabb'im! Beni, o zalim halk içinde bırakma.\"", transliteration: "Rabbi fe la tec'alni fil kavmiz zalimin."},
  {id: 2768, sure_no: 95, ayet_no: 95, sure_adi: "Müminun", text: "وَاِنَّا عَلٰٓى اَنْ نُرِيَكَ مَا نَعِدُهُمْ لَقَادِرُونَ", translation: "Kuşkusuz onları uyardığımız şeylere seni tanık yapmaya gücümüz yeter.", transliteration: "Ve inna ala en nuriyeke ma neıduhum le kadirun."},
  {id: 2769, sure_no: 96, ayet_no: 96, sure_adi: "Müminun", text: "اِدْفَعْ بِالَّت۪ي هِيَ اَحْسَنُ السَّيِّئَةَۜ نَحْنُ اَعْلَمُ بِمَا يَصِفُونَ", translation: "Kötülüğü en iyi şekilde sav. Biz, yakıştırmakta oldukları şeyleri çok iyi biliyoruz.", transliteration: "İdfa' billeti hiye ahsenus seyyieh, nahnu a'lemu bi ma yasıfun."},
  {id: 2770, sure_no: 97, ayet_no: 97, sure_adi: "Müminun", text: "وَقُلْ رَبِّ اَعُوذُ بِكَ مِنْ هَمَزَاتِ الشَّيَاط۪ينِۙ", translation: "De ki: \"Rabb'im! Şeytanların etkilemelerinden Sana sığınırım.\"", transliteration: "Ve kul rabbi euzu bike min hemezatiş şeyatin."},
  {id: 2771, sure_no: 98, ayet_no: 98, sure_adi: "Müminun", text: "وَاَعُوذُ بِكَ رَبِّ اَنْ يَحْضُرُونِ", translation: "\"Rabbim! Benimle yakınlık kurmalarından Sana sığınırım.\"", transliteration: "Ve euzu bike rabbi en yahdurun."},
  {id: 2772, sure_no: 99, ayet_no: 99, sure_adi: "Müminun", text: "حَتّٰٓى اِذَا جَٓاءَ اَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُونِۙ", translation: "Onlardan birine ölüm geldiği zaman, \"Rabb'im beni hayata geri döndür.\" der.", transliteration: "Hatta iza cae ehadehumul mevtu kale rabbirciun."},
  {id: 2773, sure_no: 100, ayet_no: 100, sure_adi: "Müminun", text: "لَعَلّ۪ٓي اَعْمَلُ صَالِحاً ف۪يمَا تَرَكْتُ كَلَّاۜ اِنَّهَا كَلِمَةٌ هُوَ قَٓائِلُهَاۜ وَمِنْ وَرَٓائِهِمْ بَرْزَخٌ اِلٰى يَوْمِ يُبْعَثُونَ", translation: "Böylece ihmal ettiğim salih işleri yaparım. Hayır! Kuşkusuz onun söylediği kesinlikle boş bir sözden ibarettir. Onların tekrar diriltilecekleri güne kadar arkalarında bir berzah vardır.", transliteration: "Lealli a'melu salihan fima terektu kella, inneha kelimetun huve kailuha, ve min veraihim berzahun ila yevmi yub'asun."},
  {id: 2774, sure_no: 101, ayet_no: 101, sure_adi: "Müminun", text: "فَاِذَا نُفِـخَ فِي الصُّورِ فَلَٓا اَنْسَابَ بَيْنَهُمْ يَوْمَئِذٍ وَلَا يَتَسَٓاءَلُونَ", translation: "Sura üfürüldüğü gün, artık ailenin, akrabanın bir yararı yoktur. Birbirlerinden soramazlar!", transliteration: "Fe iza nufiha fis suri fe la ensabe beynehum yevme izin ve la yetesaelun."},
  {id: 2775, sure_no: 102, ayet_no: 102, sure_adi: "Müminun", text: "فَمَنْ ثَقُلَتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "Kimlerin tartısı ağır gelirse, işte onlar kurtuluşa erenlerdir.", transliteration: "Fe men sekulet mevazinuhu fe ulaike humul muflihun."},
  {id: 2776, sure_no: 103, ayet_no: 103, sure_adi: "Müminun", text: "وَمَنْ خَفَّتْ مَوَاز۪ينُهُ فَاُو۬لٰٓئِكَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ ف۪ي جَهَنَّمَ خَالِدُونَۚ", translation: "Ve kimin tartısı hafif gelirse, işte onlar kendilerine yazık edenlerdir; Cehennem'de sürekli kalıcıdırlar.", transliteration: "Ve men haffet mevazinuhu fe ulaikellezine hasiru enfusehum fi cehenneme halidun."},
  {id: 2777, sure_no: 104, ayet_no: 104, sure_adi: "Müminun", text: "تَلْفَحُ وُجُوهَهُمُ النَّارُ وَهُمْ ف۪يهَا كَالِحُونَ", translation: "Ateş yüzlerini yalar ve onlar, orada acıyla somurtup kalırlar.", transliteration: "Telfehu vucuhehumun naru ve hum fiha kalihun."},
  {id: 2778, sure_no: 105, ayet_no: 105, sure_adi: "Müminun", text: "اَلَمْ تَكُنْ اٰيَات۪ي تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُونَ", translation: "\"Ayetlerim size okunduğunda; onları yalanlayanlar siz değil miydiniz?\"", transliteration: "E lem tekun ayati tutla aleykum fe kuntum biha tukezzibun."},
  {id: 2779, sure_no: 106, ayet_no: 106, sure_adi: "Müminun", text: "قَالُوا رَبَّـنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْماً ضَٓالّ۪ينَ", translation: "Dediler ki: \"Rabb'imiz! Azgınlığımıza yenilen sapkın bir halktık.\"", transliteration: "Kalu rabbena galebet aleyna şıkvetuna ve kunna kavmen dallin."},
  {id: 2780, sure_no: 107, ayet_no: 107, sure_adi: "Müminun", text: "رَبَّـنَٓا اَخْرِجْنَا مِنْهَا فَاِنْ عُدْنَا فَاِنَّا ظَالِمُونَ", translation: "\"Rabb'imiz! Bizi buradan çıkar. Eğer bir daha aynısını yaparsak zalim olduğumuz kesinleşmiş olur.\"", transliteration: "Rabbena ahricna minha fe in udna fe inna zalimun."},
  {id: 2781, sure_no: 108, ayet_no: 108, sure_adi: "Müminun", text: "قَالَ اخْسَؤُ۫ا ف۪يهَا وَلَا تُكَلِّمُونِ", translation: "Dedi ki: \"Sinin orada! Ben'den boşuna bir şey istemeyin!\"", transliteration: "Kalahseu fiha ve la tukellimun."},
  {id: 2782, sure_no: 109, ayet_no: 109, sure_adi: "Müminun", text: "اِنَّهُ  كَانَ فَر۪يقٌ مِنْ عِبَاد۪ي يَقُولُونَ رَبَّـنَٓا اٰمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الرَّاحِم۪ينَۚ", translation: "Gerçek şu ki, kimi kullarım: \"Rabb'imiz! Biz iman ettik; bizi bağışla, bize merhamet et, merhametlilerin en iyisi sensin.\" diyorlardı.", transliteration: "İnnehu kane ferikun min ibadi yekulune rabbena amenna fagfir lena verhamna ve ente hayrur rahımin."},
  {id: 2783, sure_no: 110, ayet_no: 110, sure_adi: "Müminun", text: "فَاتَّخَذْتُمُوهُمْ سِخْرِياًّ حَتّٰٓى اَنْسَوْكُمْ ذِكْر۪ي وَكُنْتُمْ مِنْهُمْ تَضْحَكُونَ", translation: "\"Siz ise onları alaya aldınız; öyle ki Benim öğütlerimi kulak ardı ettiniz. Onların haline gülüyordunuz.\"", transliteration: "Fettehaztumuhum sıhriyyen hatta ensevkum zikri ve kuntum minhum tadhakun."},
  {id: 2784, sure_no: 111, ayet_no: 111, sure_adi: "Müminun", text: "اِنّ۪ي جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُٓواۙ اَنَّهُمْ هُمُ الْفَٓائِزُونَ", translation: "\"Gerçek şu ki, bugün onlara sabretmelerinin karşılığını verdim. Onlar kazançlı çıkanlardır.\"", transliteration: "İnni cezeytuhumul yevme bima saberu ennehum humul faizun."},
  {id: 2785, sure_no: 112, ayet_no: 112, sure_adi: "Müminun", text: "قَالَ كَمْ لَبِثْتُمْ فِي الْاَرْضِ عَدَدَ سِن۪ينَ", translation: "Allah: \"Yeryüzünde kaç yıl kaldınız?\" dedi.", transliteration: "Kale kem lebistum fil ardı adede sinin."},
  {id: 2786, sure_no: 113, ayet_no: 113, sure_adi: "Müminun", text: "قَالُوا لَبِثْنَا يَوْماً اَوْ بَعْضَ يَوْمٍ فَسْـَٔلِ الْعَٓادّ۪ينَ", translation: "\"Bir gün veya günün bir kısmı kadar kaldık. Hesabını tutanlara sor!\" dediler.", transliteration: "Kalu lebisna yevmen ev ba'da yevmin fes'elil addin."},
  {id: 2787, sure_no: 114, ayet_no: 114, sure_adi: "Müminun", text: "قَالَ اِنْ لَبِثْتُمْ اِلَّا قَل۪يلاً لَوْ اَنَّكُمْ كُنْتُمْ تَعْلَمُونَ", translation: "\"Sadece az bir süre kaldınız. Keşke o zaman bunu kavramış olsaydınız.\" dedi.", transliteration: "Kale in lebistum illa kalilen lev ennekum kuntum ta'lemun."},
  {id: 2788, sure_no: 115, ayet_no: 115, sure_adi: "Müminun", text: "اَفَحَسِبْتُمْ اَنَّمَا خَلَقْنَاكُمْ عَبَثاً وَاَنَّكُمْ اِلَيْنَا لَا تُرْجَعُونَ", translation: "\"Yoksa sizi boş yere yarattığımızı ve Bize döndürülmeyeceğinizi mi sandınız?\"", transliteration: "E fe hasibtum ennema halaknakum abesen ve ennekum ileyna la turceun."},
  {id: 2789, sure_no: 116, ayet_no: 116, sure_adi: "Müminun", text: "فَتَعَالَى اللّٰهُ الْمَلِكُ الْحَقُّۚ   لَٓا اِلٰهَ اِلَّا هُوَۚ رَبُّ الْعَرْشِ الْكَر۪يمِ", translation: "\"Şunu bilin ki, gerçek egemenlik sahibi olan Allah, çok yücedir. O'ndan başka ilah yoktur. O şerefli arşın Rabb'idir.\"", transliteration: "Fe tealallahul melikul hakk, la ilahe illa hu, rabbul arşil kerim."},
  {id: 2790, sure_no: 117, ayet_no: 117, sure_adi: "Müminun", text: "وَمَنْ يَدْعُ مَعَ اللّٰهِ اِلٰهاً اٰخَرَۙ   لَا بُرْهَانَ لَهُ بِه۪ۙ فَاِنَّمَا حِسَابُهُ عِنْدَ رَبِّه۪ۜ اِنَّهُ لَا يُفْلِحُ الْكَافِرُونَ", translation: "Her kim, hakkında hiçbir burhan olmadığı halde, Allah'ın yanı sıra başka bir ilahtan istekte bulunursa, bilsin ki onun hesabı yalnızca Rabb'ine aittir. Kuşku yok ki Kafirler kurtuluşa eremezler.", transliteration: "Ve men yed'u maallahi ilahen ahare la burhane lehu bihi fe innema hısabuhu inde rabbih, innehu la yuflihul kafirun."},
  {id: 2791, sure_no: 118, ayet_no: 118, sure_adi: "Müminun", text: "وَقُلْ رَبِّ اغْفِرْ وَارْحَمْ وَاَنْتَ خَيْرُ الرَّاحِم۪ينَ", translation: "Ve de ki: \"Rabb'im! Bağışla ve merhamet et. Sen merhametlilerin en hayırlısısın.\"", transliteration: "Ve kul rabbigfir verham ve ente hayrur rahımin."},
  {id: 2792, sure_no: 1, ayet_no: 1, sure_adi: "Nur", text: "سُورَةٌ اَنْزَلْنَاهَا وَفَرَضْنَاهَا وَاَنْزَلْنَا ف۪يهَٓا اٰيَاتٍ بَيِّنَاتٍ لَعَلَّكُمْ تَذَكَّرُونَ", translation: "Bu indirip, farz kıldığımız bir suredir. Onda beyyinat1 ayetler ortaya koyduk. Umulur ki öğüt alırsınız.", transliteration: "Suratun enzelnaha ve faradnaha ve enzelna fiha ayatin beyyinatin leallekum tezekkerun."},
  {id: 2793, sure_no: 2, ayet_no: 2, sure_adi: "Nur", text: "اَلزَّانِيَةُ وَالزَّان۪ي فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا مِائَةَ جَلْدَةٍۖ وَلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ ف۪ي د۪ينِ اللّٰهِ اِنْ كُنْتُمْ تُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۚ وَلْيَشْهَدْ عَذَابَهُمَا طَٓائِفَةٌ مِنَ الْمُؤْمِن۪ينَ", translation: "Zina yapan kadın ve zina yapan erkeğin her birine yüzer sopa vurun. Allah'a ve ahiret gününe iman ediyorsanız, onlara karşı duyduğunuz acıma duygusu sizi Allah'ın bu yasasını uygulamaktan alıkoymasın. Mü'minlerden bir grup da buna tanık olsun.", transliteration: "Ez zaniyetu vez zani feclidu kulle vahıdin min huma miete celdetin ve la te'huzkum bi hima ra'fetun fi dinillahi in kuntum tu'minune billahi vel yevmil ahır, vel yeşhed azabehuma taifetun minel mu'minin."},
  {id: 2794, sure_no: 3, ayet_no: 3, sure_adi: "Nur", text: "اَلزَّان۪ي لَا يَنْكِحُ اِلَّا زَانِيَةً اَوْ مُشْرِكَةًۘ وَالزَّانِيَةُ لَا يَنْكِحُهَٓا اِلَّا زَانٍ اَوْ مُشْرِكٌۚ وَحُرِّمَ ذٰلِكَ عَلَى الْمُؤْمِن۪ينَ", translation: "Zina yapan erkek, zina yapan veya Müşrik olan kadından başkası ile evlenmiyor. Zina yapan kadınla zina yapan veya Müşrik olan bir erkekten başkası evlenmiyor. Bu uygulama Mü'minlere haram kılındı.", transliteration: "Ez zani la yenkihu illa zaniyeten ev muşriketen vez zaniyetu la yenkihuha illa zanin ev muşrik, ve hurrime zalike alel mu'minin."},
  {id: 2795, sure_no: 4, ayet_no: 4, sure_adi: "Nur", text: "وَالَّذ۪ينَ يَرْمُونَ الْمُحْصَنَاتِ ثُمَّ لَمْ يَأْتُوا بِاَرْبَعَةِ شُهَدَٓاءَ فَاجْلِدُوهُمْ ثَمَان۪ينَ جَلْدَةً وَلَا تَقْبَلُوا لَهُمْ شَهَادَةً اَبَداًۚ وَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَۙ", translation: "Ve muhsanat kadınlara iftira edip de sonra dört tanık getiremeyen kimselere seksen sopa vurun. Ve onların tanıklığını ebediyen kabul etmeyin. Onlar fasıktırlar.", transliteration: "Vellezine yermunel muhsanati summe lem ye'tu bi erbeati şuhedae fecliduhum semanine celdeten ve la takbelu lehum şehadeten ebeda, ve ulaike humul fasikun."},
  {id: 2796, sure_no: 5, ayet_no: 5, sure_adi: "Nur", text: "اِلَّا الَّذ۪ينَ تَابُوا مِنْ بَعْدِ ذٰلِكَ وَاَصْلَحُواۚ فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Fakat bundan sonra tevbe eden ve kendilerini düzelten kimseler hariç. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İllellezine tabu min ba'di zalike ve aslehu, fe innallahe gafurun rahim."},
  {id: 2797, sure_no: 6, ayet_no: 6, sure_adi: "Nur", text: "وَالَّذ۪ينَ يَرْمُونَ اَزْوَاجَهُمْ وَلَمْ يَكُنْ لَهُمْ شُهَدَٓاءُ اِلَّٓا اَنْفُسُهُمْ فَشَهَادَةُ اَحَدِهِمْ اَرْبَعُ شَهَادَاتٍ بِاللّٰهِۙ اِنَّهُ لَمِنَ الصَّادِق۪ينَ", translation: "Eşlerine iftira atanlar, kendilerinden başka tanıkları yoksa o zaman onların her birinin tanıklığı, kendisinin doğru söyleyenlerden olduğuna dair dört defa Allah'ı tanık tutmasıdır.", transliteration: "Vellezine yermune ezvacehum ve lem yekun lehum şuhedau illa enfusuhum fe şehadetu ehadihim erbeu şehadatin billahi innehu le mines sadıkin."},
  {id: 2798, sure_no: 7, ayet_no: 7, sure_adi: "Nur", text: "وَالْخَامِسَةُ اَنَّ لَعْنَتَ اللّٰهِ عَلَيْهِ اِنْ  كَانَ مِنَ الْكَاذِب۪ينَ", translation: "Beşincide de eğer yalan söyleyenlerden ise, Allah'ın lanetinin kendisi üzerine olmasını dilemesidir.", transliteration: "Vel hamisetu enne la'netallahi aleyhi in kane minel kazibin."},
  {id: 2799, sure_no: 8, ayet_no: 8, sure_adi: "Nur", text: "وَيَدْرَؤُ۬ا عَنْهَا الْعَذَابَ اَنْ تَشْهَدَ اَرْبَعَ شَهَادَاتٍ بِاللّٰهِۙ اِنَّهُ لَمِنَ الْكَاذِب۪ينَۙ", translation: "Kadının, dört kez \"Billahi o kesinlikle yalancıdır.\" şeklinde sözüne Allah'ı tanık etmesi ondan cezayı kaldırır.", transliteration: "Ve yedrau anhel azabe en teşhede erbea şehadatin billahi innehu le minel kazibin."},
  {id: 2800, sure_no: 9, ayet_no: 9, sure_adi: "Nur", text: "وَالْخَامِسَةَ اَنَّ غَضَبَ اللّٰهِ عَلَيْهَٓا اِنْ  كَانَ مِنَ الصَّادِق۪ينَ", translation: "Ve beşinci kez eğer o doğru söyleyenlerden ise Allah'ın gazabının kendisinin üzerine olmasını diler.", transliteration: "Vel hamisete enne gadaballahi aleyha in kane mines sadikin."},
  {id: 2801, sure_no: 10, ayet_no: 10, sure_adi: "Nur", text: "وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ وَاَنَّ اللّٰهَ تَـوَّابٌ حَك۪يمٌ۟", translation: "Ya üzerinizde, Allah'ın lütuf ve rahmeti olmasaydı! Allah, tevbeleri Kabul Eden'dir, En İyi Hüküm Verendir.", transliteration: "Ve lev la fadlullahi aleykum ve rahmetuhu ve ennellahe tevvabun hakim."},
  {id: 2802, sure_no: 11, ayet_no: 11, sure_adi: "Nur", text: "اِنَّ الَّذ۪ينَ جَٓاؤُ۫ بِالْاِفْكِ عُصْبَةٌ مِنْكُمْۜ  لَا تَحْسَبُوهُ شَراًّ لَكُمْۜ بَلْ هُوَ خَيْرٌ لَكُمْۜ لِكُلِّ امْرِئٍ مِنْهُمْ مَا اكْتَسَبَ مِنَ الْاِثْمِۚ وَالَّذ۪ي تَوَلّٰى كِبْرَهُ مِنْهُمْ لَهُ عَذَابٌ عَظ۪يمٌ", translation: "Bu ifk'i yapanlar içinizden birileridir. Sizin için bunun bir şer olduğunu sanmayın. Aksine bu sizin için bir hayırdır. Onlardan her birine günahtan bir pay vardır. İçlerinden önderlik yapıp bunu organize eden kimse için de çok büyük bir azap vardır.", transliteration: "İnnellezine cau bil ifki usbetun minkum, la tahsebuhu şerren lekum, bel huve hayrun lekum, li kullimriin minhum mektesebe minel ism, vellezi tevella kibrehu minhum lehu azabun azim."},
  {id: 2803, sure_no: 12, ayet_no: 12, sure_adi: "Nur", text: "لَوْلَٓا اِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بِاَنْفُسِهِمْ خَيْراًۙ وَقَالُوا هٰذَٓا اِفْكٌ مُب۪ينٌ", translation: "Mü'min erkeklerin ve Mü'min kadınların onu işittikleri zaman, iyi zan ile \"Bu apaçık iftiradır.\" demeleri gerekmez miydi?", transliteration: "Lev la iz semi'tumuhu zannel mu'minune vel mu'minatu bi enfusihim hayran ve kalu haza ifkun mubin."},
  {id: 2804, sure_no: 13, ayet_no: 13, sure_adi: "Nur", text: "لَوْلَا جَٓاؤُ۫ عَلَيْهِ بِاَرْبَعَةِ شُهَدَٓاءَۚ  فَاِذْ لَمْ يَأْتُوا بِالشُّهَدَٓاءِ فَاُو۬لٰٓئِكَ عِنْدَ اللّٰهِ هُمُ الْكَاذِبُونَ", translation: "İddialarına dört tanık getirmeleri gerekmez miydi? Tanık getiremediklerine göre onlar Allah'ın yanında yalancıdırlar.", transliteration: "Lev la cau aleyhi bi erbeati şuheda, fe iz lem ye'tu biş şuhedai fe ulaike indellahi humul kazibun."},
  {id: 2805, sure_no: 14, ayet_no: 14, sure_adi: "Nur", text: "وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ فِي الدُّنْيَا وَالْاٰخِرَةِ لَمَسَّكُمْ ف۪ي مَٓا اَفَضْتُمْ ف۪يهِ عَذَابٌ عَظ۪يمٌۚ", translation: "Eğer dünyada ve ahirette Allah'ın lütuf ve rahmeti sizin üzerinize olmasaydı, içine düştüğünüz şeyden dolayı size kesinlikle büyük bir azap dokunurdu.", transliteration: "Ve lev la fadlullahi aleykum ve rahmetuhu fid dunya vel ahırati le messekum fi ma efadtum fihi azabun azim."},
  {id: 2806, sure_no: 15, ayet_no: 15, sure_adi: "Nur", text: "اِذْ تَلَقَّوْنَهُ بِاَلْسِنَتِكُمْ وَتَقُولُونَ بِاَفْوَاهِكُمْ مَا لَيْسَ لَكُمْ بِه۪ عِلْمٌ وَتَحْسَبُونَهُ هَيِّناًۗ وَهُوَ عِنْدَ اللّٰهِ عَظ۪يمٌ", translation: "Hani siz onu dillerinize dolayıp, kendisi hakkında bilgi sahibi olmadığınız şeyi aranızda yayıyordunuz. Ve bunun önemsiz olduğunu sanıyordunuz. Oysaki bunun Allah'ın yanında önemi çok büyüktür.", transliteration: "İz telakkavnehu bi elsinetikum ve tekulune bi efvahikum ma leyse lekum bihi ilmun ve tahsebunehu heyyinen ve huve indallahi azim."},
  {id: 2807, sure_no: 16, ayet_no: 16, sure_adi: "Nur", text: "وَلَوْلَٓا اِذْ سَمِعْتُمُوهُ قُلْتُمْ مَا يَكُونُ لَـنَٓا اَنْ نَتَكَلَّمَ بِهٰذَاۗ سُبْحَانَكَ هٰذَا بُهْتَانٌ عَظ۪يمٌ", translation: "Ve onu duyduğunuz zaman: \"Bunu konuşmamız bize yakışmaz. Seni tenzih ederiz! Bu büyük bir iftiradır.\" demeniz gerekmez miydi?", transliteration: "Ve lev la iz semi'tumuhu kultum ma yekunu lena en netekelleme bi haza subhaneke haza buhtanun azim."},
  {id: 2808, sure_no: 17, ayet_no: 17, sure_adi: "Nur", text: "يَعِظُكُمُ اللّٰهُ اَنْ تَعُودُوا لِمِثْلِه۪ٓ اَبَداً اِنْ كُنْتُمْ مُؤْمِن۪ينَۚ", translation: "Allah size öğüt veriyor: Eğer iman etmiş kimselerseniz, böyle bir şeyi yapmayı ebediyen yasaklıyor.", transliteration: "Yeızukumullahu en teudu li mislihi ebeden in kuntum mu'minin."},
  {id: 2809, sure_no: 18, ayet_no: 18, sure_adi: "Nur", text: "وَيُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Allah, size ayetlerini beyan ediyor. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve yubeyyinullahu lekumul ayat, vallahu alimun hakim."},
  {id: 2810, sure_no: 19, ayet_no: 19, sure_adi: "Nur", text: "اِنَّ الَّذ۪ينَ يُحِبُّونَ اَنْ تَش۪يعَ الْفَاحِشَةُ فِي الَّذ۪ينَ اٰمَنُوا لَهُمْ عَذَابٌ اَل۪يمٌۙ فِي الدُّنْيَا وَالْاٰخِرَةِۜ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُونَ", translation: "İman Edenler arasında, fahişenin yayılmasından hoşlananlara dünya ve ahirette acı bir azap vardır. Allah bilir, siz bilmezsiniz.", transliteration: "İnnellezine yuhıbbune en teşial fahışetu fillezine amenu lehum azabun elimun fid dunya vel ahırah, vallahu ya'lemu ve entum la ta'lemun."},
  {id: 2811, sure_no: 20, ayet_no: 20, sure_adi: "Nur", text: "وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ وَاَنَّ اللّٰهَ رَؤُ۫فٌ رَح۪يمٌ۟", translation: "Eğer Allah'ın lütuf ve rahmeti üzerinizde olmasaydı! Allah, Çok Şefkatli'dir. Rahmeti Kesintisiz'dir.", transliteration: "Ve lev la fadlullahi aleykum ve rahmetuhu ve ennallahe raufun rahim."},
  {id: 2812, sure_no: 21, ayet_no: 21, sure_adi: "Nur", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِۜ وَمَنْ يَتَّبِـعْ خُطُوَاتِ الشَّيْطَانِ فَاِنَّهُ يَأْمُرُ بِالْفَحْشَٓاءِ وَالْمُنْكَرِۜ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهُ مَا زَكٰى مِنْكُمْ مِنْ اَحَدٍ اَبَداًۙ وَلٰكِنَّ اللّٰهَ يُزَكّ۪ي مَنْ يَشَٓاءُۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ", translation: "Ey İman Edenler! Şeytanın adımlarını takip etmeyin. Kim şeytanın adımlarını izlerse şunu bilsin ki şeytan, her türlü aşırılığı ve her türlü çirkinliği telkin eder. Ve eğer Allah'ın üzerinizdeki lütuf ve rahmeti olmasaydı sizden hiç kimse arınmayı asla başaramazdı. Fakat Allah hak edeni arındırır. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ya eyyuhellezine amenu la tettebiu hutuvatiş şeytan, ve men yettebi' hutuvatiş şeytani fe innehu ye'muru bil fahşai vel munker ve lev la fadlullahi aleykum ve rahmetuhu ma zeka minkum min ehadin ebeden ve lakinnallahe yuzekki men yeşau, vallahu semi'un alim."},
  {id: 2813, sure_no: 22, ayet_no: 22, sure_adi: "Nur", text: "وَلَا يَأْتَلِ اُو۬لُوا الْفَضْلِ مِنْكُمْ وَالسَّعَةِ اَنْ يُؤْتُٓوا اُو۬لِي الْقُرْبٰى وَالْمَسَاك۪ينَ وَالْمُهَاجِر۪ينَ ف۪ي سَب۪يلِ اللّٰهِۖ وَلْيَعْفُوا وَلْيَصْفَحُواۜ اَلَا تُحِبُّونَ اَنْ يَغْفِرَ اللّٰهُ لَكُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "Sizden fazilet ve varlık sahibi olanlar, yakınlarına, miskinlere, Allah yolunda hicret edenlere yardım etmeme konusunda yemin etmesinler. Ve artık bağışlayıp hoş görsünler. Allah'ın sizi bağışlamasından mutlu olmaz mısınız? Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve la ye'teli ulul fadlı minkum ves seati en yu'tu ulil kurba vel mesakine vel muhacirine fi sebilillah, vel ya'fu vel yasfehu, e la tuhıbbune en yagfirallahu lekum, vallahu gafurun rahim."},
  {id: 2814, sure_no: 23, ayet_no: 23, sure_adi: "Nur", text: "اِنَّ الَّذ۪ينَ يَرْمُونَ الْمُحْصَنَاتِ الْغَافِلَاتِ الْمُؤْمِنَاتِ لُعِنُوا فِي الدُّنْيَا وَالْاٰخِرَةِۖ وَلَهُمْ عَذَابٌ عَظ۪يمٌۙ", translation: "Hiçbir şeyden haberi olmadığı halde muhsanat Mü'min kadınlara iftira atan kimseler dünya ve ahirette lanetlenmişlerdir. Ve onlar için büyük bir azap vardır.", transliteration: "İnnellezine yermunel muhsanatil gafilatil mu'minati luınu fid dunya vel ahırati ve lehum azabun azim."},
  {id: 2815, sure_no: 24, ayet_no: 24, sure_adi: "Nur", text: "يَوْمَ تَشْهَدُ عَلَيْهِمْ اَلْسِنَتُهُمْ وَاَيْد۪يهِمْ وَاَرْجُلُهُمْ بِمَا كَانُوا يَعْمَلُونَ", translation: "O Gün dilleri, elleri ve ayakları yaptıklarına tanıklık edecektir.", transliteration: "Yevme teşhedu aleyhim elsinetuhum ve eydihim ve erculuhum bima kanu ya'melun."},
  {id: 2816, sure_no: 25, ayet_no: 25, sure_adi: "Nur", text: "يَوْمَئِذٍ يُوَفّ۪يهِمُ اللّٰهُ د۪ينَهُمُ الْحَقَّ وَيَعْلَمُونَ اَنَّ اللّٰهَ هُوَ الْحَقُّ الْمُب۪ينُ", translation: "O Gün Allah, onların hesaplarını hakkıyla görecektir. Onlar da Allah'ın Hakk'ın ta kendisi olduğunu apaçık bileceklerdir.", transliteration: "Yevme izin yuveffihimullahu dinehumul hakka ve ya'lemune ennallahe huvel hakkul mubin."},
  {id: 2817, sure_no: 26, ayet_no: 26, sure_adi: "Nur", text: "اَلْخَب۪يثَاتُ لِلْخَب۪يث۪ينَ وَالْخَب۪يثُونَ لِلْخَب۪يثَاتِۚ وَالطَّيِّبَاتُ لِلطَّيِّب۪ينَ وَالطَّيِّبُونَ لِلطَّيِّبَاتِۚ اُو۬لٰٓئِكَ مُبَرَّؤُ۫نَ مِمَّا يَقُولُونَۜ  لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ۟", translation: "Habis kadınlar habis erkeklere, habis erkekler habis kadınlara yakışır. Tayyib kadınlar tayyib erkeklere, tayyib erkekler de tayyib kadınlara yakışır. Bunlar, söylenenlerden uzak olanlardır. Onlar için bağışlanma ve cömertçe rızık vardır.", transliteration: "El habisatu lil habisine vel habisune lil habisat, vet tayyibatu lit tayyibine vet tayyibune lit tayyibat, ulaike muberraune mimma yekulun, lehum magfiretun ve rızkun kerim."},
  {id: 2818, sure_no: 27, ayet_no: 27, sure_adi: "Nur", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَدْخُلُوا بُيُوتاً غَيْرَ بُيُوتِكُمْ حَتّٰى تَسْتَأْنِسُوا وَتُسَلِّمُوا عَلٰٓى اَهْلِهَاۜ ذٰلِكُمْ خَيْرٌ لَكُمْ لَعَلَّكُمْ تَذَكَّرُونَ", translation: "Ey İman Edenler! Kendi evinizden başka evlere, sahiplerinin sizi istekle karşılayacaklarından emin olmadan ve selam vermeden girmeyin. Bu sizin için daha hayırlıdır. Umulur ki öğüt alırsınız.", transliteration: "Ya eyyuhellezine amenu la tedhulu buyuten gayra buyutikum hatta teste'nisu ve tusellimu ala ehliha, zalikum hayrun lekum leallekum tezekkerun."},
  {id: 2819, sure_no: 28, ayet_no: 28, sure_adi: "Nur", text: "فَاِنْ لَمْ تَجِدُوا ف۪يهَٓا اَحَداً فَلَا تَدْخُلُوهَا حَتّٰى يُؤْذَنَ لَكُمْۚ وَاِنْ ق۪يلَ لَكُمُ ارْجِعُوا فَارْجِعُوا هُوَ اَزْكٰى لَكُمْۜ وَاللّٰهُ بِمَا تَعْمَلُونَ عَل۪يمٌ", translation: "Eğer orada kimseyi bulamazsanız, size izin verilinceye kadar oraya girmeyin. Eğer geri dönmeniz istenirse hemen geri dönün. Bu sizin için daha nezihtir. Allah yaptığınız şeyi hangi amaçla yaptığınızdan haberdardır.", transliteration: "Fe in lem tecidu fiha ehaden fe la tedhuluha hatta yu'zene lekum ve in kile lekumurciu ferciu huve ezka lekum, vallahu bima ta'melune alim."},
  {id: 2820, sure_no: 29, ayet_no: 29, sure_adi: "Nur", text: "لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَدْخُلُوا بُيُوتاً غَيْرَ مَسْكُونَةٍ ف۪يهَا مَتَاعٌ لَكُمْۜ وَاللّٰهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ", translation: "Oturulmayan ama yararlanmak için kullanılan yerlere girmenizde bir sakınca yoktur. Allah, sizin açığa vurduğunuz şeyleri de gizlediğiniz şeyleri de bilir.", transliteration: "Leyse aleykum cunahun en tedhulu buyuten gayre meskunetin fiha metaun lekum, vallahu ya'lemu ma tubdune ve ma tektumun."},
  {id: 2821, sure_no: 30, ayet_no: 30, sure_adi: "Nur", text: "قُلْ لِلْمُؤْمِن۪ينَ يَغُضُّوا مِنْ اَبْصَارِهِمْ وَيَحْفَظُوا فُرُوجَهُمْۜ ذٰلِكَ اَزْكٰى لَهُمْۜ اِنَّ اللّٰهَ خَب۪يرٌ بِمَا يَصْنَعُونَ", translation: "Mü'min erkeklere söyle, bakışlarından bir kısmını sakınsınlar ve ırzlarını korusunlar. Bu onlar için daha arınmışcadır. Allah, davranışlarından haberdardır.", transliteration: "Kul lil mu'minine yaguddu min ebsarihim ve yahfezu furucehum, zalike ezka lehum, innellahe habirun bima yasneun."},
  {id: 2822, sure_no: 31, ayet_no: 31, sure_adi: "Nur", text: "وَقُلْ لِلْمُؤْمِنَاتِ يَغْضُضْنَ مِنْ اَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلَا يُبْد۪ينَ ز۪ينَتَهُنَّ اِلَّا مَا ظَهَرَ مِنْهَا وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلٰى جُيُوبِهِنَّۖ وَلَا يُبْد۪ينَ ز۪ينَتَهُنَّ اِلَّا لِبُعُولَتِهِنَّ اَوْ اٰبَٓائِهِنَّ اَوْ اٰبَٓاءِ بُعُولَتِهِنَّ اَوْ اَبْنَٓائِهِنَّ اَوْ اَبْنَٓاءِ بُعُولَتِهِنَّ اَوْ اِخْوَانِهِنَّ اَوْ بَن۪ٓي اِخْوَانِهِنَّ اَوْ بَن۪ٓي اَخَوَاتِهِنَّ اَوْ نِسَٓائِهِنَّ اَوْ مَا مَلَكَتْ اَيْمَانُهُنَّ اَوِ التَّابِع۪ينَ غَيْرِ اُو۬لِي الْاِرْبَةِ مِنَ الرِّجَالِ اَوِ الطِّفْلِ الَّذ۪ينَ لَمْ يَظْهَرُوا عَلٰى عَوْرَاتِ النِّسَٓاءِۖ وَلَا يَضْرِبْنَ بِاَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْف۪ينَ مِنْ ز۪ينَتِهِنَّۜ وَتُوبُٓوا اِلَى اللّٰهِ جَم۪يعاً اَيُّهَ الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ", translation: "Ve de Mü'min kadınlara söyle, bakışlarından bir kısmını sakınsınlar, ırzlarını korusunlar. Doğal olarak görünmesi gerekli olanlar dışında, ziynetlerini açığa vurmasınlar. Örtüleri ile göğüslerini örtsünler. Ziynetlerini; kocaları, babaları, kocalarının babaları, oğulları, kocalarının oğulları, erkek kardeşleri, erkek kardeşlerinin oğulları, kız kardeşlerinin oğulları, kadınların tamamı, yeminle sahip oldukları, kadına ihtiyaç duymayan erkek hizmetliler, kadınların avret yerlerinin henüz farkında olamayan çocuklar hariç, açığa vurmasınlar. Korudukları ziynetleri bilinsin diye, ayaklarını vurmasınlar. Ey Mü'minler! Hepiniz topluca Allah'a tevbe edin. Umulur ki kurtuluşa erersiniz.", transliteration: "Ve kul lil mu'minati yagdudne min ebsarihinne ve yahfazne furucehunne, ve la yubdine zinetehunneilla ma zahera minha, vel yadribne bi humurihinne ala cuyubihinne, ve la yubdine zinetehunne illa li buuletihinne ev abaihinne ev abai buuletihinne ev ebnaihinne ev ebnai buuletihinne ev ıhvanihinne ev beni ıhvanihinne ev beni ehavatihinne ev nisaihinne ev ma meleket eymanuhunne evit tabiine gayri ulil irbeti miner ricali evit tıflillezine lem yazharu ala avratin nisai, ve la yadribne bi erculihinne li yu'leme ma yuhfine min zinetihinn, ve tubu ilallahi cemian eyyuhel mu'minune leallekum tuflihun."},
  {id: 2823, sure_no: 32, ayet_no: 32, sure_adi: "Nur", text: "وَاَنْكِحُوا الْاَيَامٰى مِنْكُمْ وَالصَّالِح۪ينَ مِنْ عِبَادِكُمْ وَاِمَٓائِكُمْۜ اِنْ يَكُونُوا فُقَـرَٓاءَ يُغْنِهِمُ اللّٰهُ مِنْ فَضْلِه۪ۜ  وَاللّٰهُ وَاسِعٌ عَل۪يمٌ", translation: "Aranızdaki bekarları, kölelerinizden ve cariyelerinizden salih olanları nikahlayın. Eğer bunların durumları iyi değilse, Allah onlara lütufta bulunur. Allah, Yardımı Çok Kapsamlı Olan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve enkihul eyama minkum ves salihine min ibadikum ve imaikum, in yekunu fukarae yugnihimullahu min fadlih, vallahu vasiun alim."},
  {id: 2824, sure_no: 33, ayet_no: 33, sure_adi: "Nur", text: "وَلْيَسْتَعْفِفِ الَّذ۪ينَ لَا يَجِدُونَ نِكَاحاً حَتّٰى يُغْنِيَهُمُ اللّٰهُ مِنْ فَضْلِه۪ۜ وَالَّذ۪ينَ يَبْتَغُونَ الْكِتَابَ مِمَّا مَلَكَتْ اَيْمَانُكُمْ فَكَاتِبُوهُمْ اِنْ عَلِمْتُمْ ف۪يهِمْ خَيْراًۗ وَاٰتُوهُمْ مِنْ مَالِ اللّٰهِ الَّـذ۪ٓي اٰتٰيكُمْۜ وَلَا تُكْرِهُوا فَتَيَاتِكُمْ عَلَى الْبِغَٓاءِ اِنْ اَرَدْنَ تَحَصُّناً لِتَبْتَغُوا عَرَضَ الْحَيٰوةِ الدُّنْيَاۜ وَمَنْ يُكْرِهْهُنَّ فَاِنَّ اللّٰهَ مِنْ بَعْدِ اِكْرَاهِهِنَّ غَفُورٌ رَح۪يمٌ", translation: "Nikaha imkan bulamayanlar iffetlerini korusunlar. Allah lütfedip kendilerine bir imkan verinceye dek. Yeminle hak sahibi olduklarınızdan mukatebe yapmak isteyenlerle hemen antlaşma yapın. Eğer iyi niyetli olduklarını biliyorsanız. Ve Allah'ın size verdiği mallardan onlara verin. İffetli kalmak isteyen genç kadınları taşkınlığa zorlamayın; basit dünya hayatının geçici çıkarı için. Kim onları zorlarsa, bilsin ki Allah, onların zorlanmalarından dolayı onlara karşı Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Velyesta'fifillezine la yecidune nikahan hatta yugniyehumullahu min fadlih, vellezine yebtegunel kitabe mimma meleket eymanukum fe katibuhum in alimtum fihim hayren, ve atuhum min malillahillezi atakum, ve la tukrihu feteyatikum alel bigai in eradne tehassunen li tebtegu aradal hayatid dunya ve men yukrıhhunne fe innellahe min ba'di ikrahihinne gafurun rahim."},
  {id: 2825, sure_no: 34, ayet_no: 34, sure_adi: "Nur", text: "وَلَقَدْ اَنْزَلْـنَٓا اِلَيْكُمْ اٰيَاتٍ مُبَيِّنَاتٍ وَمَثَلاً مِنَ الَّذ۪ينَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِلْمُتَّق۪ينَ۟", translation: "Ve ant olsun ki size gerçeği apaçık gösteren ayetler, sizden önce yaşamış olan kuşaklardan örnekler ve takva sahipleri için bir va'az indirdik.", transliteration: "Ve lekad enzelna ileykum ayatin mubeyyinatin ve meselen minellezine halev min kablikum ve mev'izaten lil muttekin."},
  {id: 2826, sure_no: 35, ayet_no: 35, sure_adi: "Nur", text: "اَللّٰهُ نُورُ السَّمٰوَاتِ وَالْاَرْضِۜ مَثَلُ نُورِه۪ كَمِشْكٰوةٍ ف۪يهَا مِصْبَاحٌۜ  اَلْمِصْبَاحُ ف۪ي زُجَاجَةٍۜ اَلزُّجَاجَةُ  كَاَنَّهَا كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبَارَكَةٍ زَيْتُونَةٍ لَا شَرْقِيَّةٍ وَلَا غَرْبِيَّةٍۙ يَكَادُ زَيْتُهَا يُض۪ٓيءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌۜ  نُورٌ عَلٰى نُورٍۜ يَهْدِي اللّٰهُ لِنُورِه۪ مَنْ يَشَٓاءُۜ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌۙ", translation: "Allah, göklerin ve yeryüzünün aydınlığıdır. O'nun aydınlığı, içinde ışık bulunan kandil yuvası gibidir. O kandil, bir fanus içindedir. O fanus, inciden bir yıldız gibidir. Doğuya da batıya da ait olmayan mübarek bir ağacın yağından yakılır. Onun yağı neredeyse kendisine ateş dokunmasa bile ışık verir. Aydınlık üstüne aydınlıktır. Allah, hak eden kimseyi aydınlığına iletir. Allah, insanlara örnekler verir. Allah, her şeyi bütün ayrıntılarıyla bilendir.", transliteration: "Allahu nurus semavati vel ard, meselu nurihi ke mişkatin fiha mısbah, el mısbahu fi zucaceh, ez zucacetu ke enneha kevkebun durriyyun, yukadu min şeceratin mubaraketin zeytunetin la şarkiyetin ve la garbiyyetin, yekadu zeytuha yudiu ve lev lem temseshu nar, nurun ala nur, yehdillahu li nurihi men yeşau, ve yadribullahul emsale lin nas, vallahu bi kulli şey'in alim."},
  {id: 2827, sure_no: 36, ayet_no: 36, sure_adi: "Nur", text: "ف۪ي بُيُوتٍ اَذِنَ اللّٰهُ اَنْ تُرْفَعَ وَيُذْكَرَ ف۪يهَا اسْمُهُۙ يُسَبِّحُ لَهُ ف۪يهَا بِالْغُدُوِّ وَالْاٰصَالِۙ", translation: "(Allah'ın aydınlığı/ışığı) Allah'ın İsmi'nin yüceltilmesine ve öğütlerinin dinlenmesine izin verdiği evlerde vardır. Orada sabah akşam O'nu tesbih ederler.", transliteration: "Fi buyutin ezinallahu en turfea ve yuzkere fihesmuhu yusebbihu lehu fiha bil guduvvi vel asal."},
  {id: 2828, sure_no: 37, ayet_no: 37, sure_adi: "Nur", text: "رِجَالٌۙ  لَا تُلْه۪يهِمْ تِجَارَةٌ وَلَا بَيْعٌ عَنْ ذِكْرِ اللّٰهِ وَاِقَامِ الصَّلٰوةِ وَا۪يتَٓاءِ الزَّكٰوةِۙ يَخَافُونَ يَوْماً تَتَقَلَّبُ ف۪يهِ الْقُلُوبُ وَالْاَبْصَارُۙ", translation: "Öyle kimseler vardır ki ne mal ne de alışveriş onları Allah'ın buyruklarına uymaktan, salatı ikame etmekten ve zekatı yapmaktan alıkoyar. Onlar, kalplerin ve gözlerin altüst olacağı günden korkarlar.", transliteration: "Ricalun la tulhihim ticaratun ve la bey'un an zikrillahi ve ikamis salati ve itaiz zekati yehafune yevmen tetekallebu fihil kulubu vel ebsar."},
  {id: 2829, sure_no: 38, ayet_no: 38, sure_adi: "Nur", text: "لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا عَمِلُوا وَيَز۪يدَهُمْ مِنْ فَضْلِه۪ۜ وَاللّٰهُ يَرْزُقُ مَنْ يَشَٓاءُ بِغَيْرِ حِسَابٍ", translation: "Yaptıklarına karşılık Allah onlara en iyi karşılığı verecek ve lütfundan daha fazlasını da verecektir. Ve Allah hak edeni hesapsız rızıklandırır.", transliteration: "Li yecziyehumullahu ahsene ma amilu ve yezidehum min fadlih, vallahu yerzuku men yeşau bi gayri hisab."},
  {id: 2830, sure_no: 39, ayet_no: 39, sure_adi: "Nur", text: "وَالَّذ۪ينَ كَفَرُٓوا اَعْمَالُهُمْ كَسَرَابٍ بِق۪يعَةٍ يَحْسَبُهُ الظَّمْاٰنُ مَٓاءًۜ حَتّٰٓى اِذَا جَٓاءَهُ لَمْ يَجِدْهُ شَيْـٔاً وَوَجَدَ اللّٰهَ عِنْدَهُ فَوَفّٰيهُ حِسَابَهُۜ وَاللّٰهُ سَر۪يعُ الْحِسَابِۙ", translation: "Kafirlerin yaptıkları, çöldeki serap gibidir. Susayan onu su zanneder. Ancak yanına vardığında hiçbir şey bulamaz. Orada Allah'ı bulur ve O da yaptıklarının hesabını eksiksiz görür. Allah hesabı çabuk görendir.", transliteration: "Vellezine keferu a'maluhum ke serabin bi kiatin yahsebuhuz zam'anu mae, hatta iza caehu lem yecidhu şey'en ve vecedallahe indehu fe veffahu hisabeh, vallahu seriul hısab."},
  {id: 2831, sure_no: 40, ayet_no: 40, sure_adi: "Nur", text: "اَوْ كَظُلُمَاتٍ ف۪ي بَحْرٍ لُجِّيٍّ يَغْشٰيهُ مَوْجٌ مِنْ فَوْقِه۪ مَوْجٌ مِنْ فَوْقِه۪ سَحَابٌۜ ظُلُمَاتٌ بَعْضُهَا فَوْقَ بَعْضٍۜ اِذَٓا اَخْرَجَ يَدَهُ لَمْ يَكَدْ يَرٰيهَاۜ وَمَنْ لَمْ يَجْعَلِ اللّٰهُ لَهُ نُوراً فَمَا لَهُ مِنْ نُورٍ۟", translation: "Veya üzerinde bulutlar olan, üst üste dalgaların kuşattığı derin denizlerdeki karanlığa benzer. Elini göremeyecek kadar, karanlık üstüne karanlık. Allah'ın aydınlığıyla aydınlanmamış bir kimse için başka aydınlık yoktur.", transliteration: "Ev ke zulumatin fi bahrin lucciyyin yagşahu mevcun min fevkıhi mevcun min fevkıhi sehab, zulumatun ba'duha fevka ba'd, iza ahrace yedehu lem yeked yeraha ve men lem yec'alillahu lehu nuren fe ma lehu min nur."},
  {id: 2832, sure_no: 41, ayet_no: 41, sure_adi: "Nur", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ يُسَبِّـحُ لَهُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ وَالطَّيْرُ صَٓافَّاتٍۜ كُلٌّ قَدْ عَلِمَ صَلَاتَهُ وَتَسْب۪يحَهُۜ وَاللّٰهُ عَل۪يمٌ بِمَا يَفْعَلُونَ", translation: "Göklerde ve yeryüzünde bulunanların, sürü sürü uçanların, Allah'ı nasıl tesbih ettiklerini görmüyor musun? Kuşkusuz hepsi salatını ve tesbihini bilmektedir. Ve Allah, onların yaptıklarını en iyi bilendir.", transliteration: "E lem tera ennallahe yusebbihu lehu men fis semavati vel ardı vet tayru saffat, kullun kad alime salatehu ve tesbihah, vallahu alimun bima yef'alun."},
  {id: 2833, sure_no: 42, ayet_no: 42, sure_adi: "Nur", text: "وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۚ وَاِلَى اللّٰهِ الْمَص۪يرُ", translation: "Göklerin ve yeryüzünün egemenliği yalnızca Allah'a aittir. Sonuçta dönüş Allah'adır.", transliteration: "Ve lillahi mulkus semavati vel ard, ve ilallahil masir."},
  {id: 2834, sure_no: 43, ayet_no: 43, sure_adi: "Nur", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ يُزْج۪ي سَحَاباً ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكَاماً فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِه۪ۚ وَيُنَزِّلُ مِنَ السَّمَٓاءِ مِنْ جِبَالٍ ف۪يهَا مِنْ بَرَدٍ فَيُص۪يبُ بِه۪ مَنْ يَشَٓاءُ وَيَصْرِفُهُ عَنْ مَنْ يَشَٓاءُۜ  يَكَادُ سَنَا بَرْقِه۪ يَذْهَبُ بِالْاَبْصَارِۜ", translation: "Allah'ın, bulutları sürüklediğini, sonra aralarını birleştirdiğini, sonra da onları küme haline getirdiğini görmüyor musun? Böylece aralarından yağmur çıktığını görürsün. Ve gökten içinde dolu bulunan dağ gibi kümeleri getiriyor. Neredeyse parıltısı gözlerinizi alan şimşeği dilediğine isabet ettiriyor, dilediğinden de onu uzak tutuyor.", transliteration: "E lem tera ennallahe yuzci sehaben summe yuellifu beynehu summe yec'aluhu rukamen fe teral vedka yahrucu min hılalih, ve yunezzilu mines semai min cibalin fiha min beredin fe yusibu bihi men yeşau ve yasrifuhu an men yeşau, yekadu sena berkıhi yezhebu bil ebsar."},
  {id: 2835, sure_no: 44, ayet_no: 44, sure_adi: "Nur", text: "يُقَلِّبُ اللّٰهُ الَّيْلَ وَالنَّهَارَۜ اِنَّ ف۪ي ذٰلِكَ لَعِبْرَةً لِاُو۬لِي الْاَبْصَارِ", translation: "Allah, geceyi ve gündüzü sürekli değiştirir. Kuşku yok ki, elbette bunda basiret sahipleri için ibretler vardır.", transliteration: "Yukallibullahul leyle ven nehar, inne fi zalike le ibreten li ulil ebsar."},
  {id: 2836, sure_no: 45, ayet_no: 45, sure_adi: "Nur", text: "وَاللّٰهُ خَلَقَ كُلَّ دَٓابَّةٍ مِنْ مَٓاءٍۚ فَمِنْهُمْ مَنْ يَمْش۪ي عَلٰى بَطْنِه۪ۚ وَمِنْهُمْ مَنْ يَمْش۪ي عَلٰى رِجْلَيْنِۚ وَمِنْهُمْ مَنْ يَمْش۪ي عَلٰٓى اَرْبَعٍۜ يَخْلُقُ اللّٰهُ مَا يَشَٓاءُۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Allah, hareket eden her canlıyı sudan yarattı. Onların kimisi karnı, kimisi iki ayağı, kimisi de dört ayağı üzerinde yürür. Allah, dilediğini yaratır. Kuşkusuz, Allah'ın Her Şeye Gücü Yeter.", transliteration: "Vallahu halaka kulle dabbetin min main, fe minhum men yemşi ala batnih ve minhum men yemşi ala ricleyn ve minhum men yemşi ala erba', yahlukullahu ma yeşau, innellahe ala kulli şey'in kadir."},
  {id: 2837, sure_no: 46, ayet_no: 46, sure_adi: "Nur", text: "لَقَدْ اَنْزَلْـنَٓا اٰيَاتٍ مُبَيِّنَاتٍۜ وَاللّٰهُ يَهْد۪ي مَنْ يَشَٓاءُ اِلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Ant olsun ki apaçık ayetler indirdik. Allah, hak eden kimseyi dosdoğru yola iletir.", transliteration: "Le kad enzelna ayatin mubeyyinat, vallahu yehdi men yeşau ila sıratın mustakim."},
  {id: 2838, sure_no: 47, ayet_no: 47, sure_adi: "Nur", text: "وَيَقُولُونَ اٰمَنَّا بِاللّٰهِ وَبِالرَّسُولِ وَاَطَعْنَا ثُمَّ يَتَوَلّٰى فَر۪يقٌ مِنْهُمْ مِنْ بَعْدِ ذٰلِكَۜ وَمَٓا اُو۬لٰٓئِكَ بِالْمُؤْمِن۪ينَ", translation: "\"Allah'a ve Resul'e iman ettik ve tabi olduk.\" diyorlar. Sonra da onların bir kısmı yan çiziyor. Bunlar, iman etmiş değillerdir.", transliteration: "Ve yekulune amenna billahi ve bir resuli ve ata'na summe yetevella ferikun minhum min ba'di zalik ve ma ulaike bil mu'minin."},
  {id: 2839, sure_no: 48, ayet_no: 48, sure_adi: "Nur", text: "وَاِذَا دُعُٓوا اِلَى اللّٰهِ وَرَسُولِه۪ لِيَحْكُمَ بَيْنَهُمْ اِذَا فَر۪يقٌ مِنْهُمْ مُعْرِضُونَ", translation: "Aralarında hüküm vermesi için Allah'a ve Resul'üne çağrıldıkları zaman, onların bir kısmı bundan kaçınır.", transliteration: "Ve iza duu ilallahi ve resulihi li yahkume beynehum iza ferikun minhum mu'ridun."},
  {id: 2840, sure_no: 49, ayet_no: 49, sure_adi: "Nur", text: "وَاِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُٓوا اِلَيْهِ مُذْعِن۪ينَۜ", translation: "Ama verilen hüküm lehlerine olursa seve seve kabul ederler.", transliteration: "Ve in yekun lehumul hakku ye'tu ileyhi muz'ınin."},
  {id: 2841, sure_no: 50, ayet_no: 50, sure_adi: "Nur", text: "اَف۪ي قُلُوبِهِمْ مَرَضٌ اَمِ ارْتَابُٓوا اَمْ يَخَافُونَ اَنْ يَح۪يفَ اللّٰهُ عَلَيْهِمْ وَرَسُولُهُۜ بَلْ اُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ۟", translation: "Onların kalplerinde bir hastalık mı var? Yoksa kuşku mu duyuyorlar? Yoksa Allah ve Resul'ünün kendilerine haksızlık edeceğinden mi korkuyorlar? Hayır! Onlar, zalimlerin ta kendileridir!", transliteration: "E fi kulubihim maradun emirtabu em yehafune en yehifallahu aleyhim ve resuluh, bel ulaike humuz zalimun."},
  {id: 2842, sure_no: 51, ayet_no: 51, sure_adi: "Nur", text: "اِنَّمَا كَانَ قَوْلَ الْمُؤْمِن۪ينَ اِذَا دُعُٓوا اِلَى اللّٰهِ وَرَسُولِه۪ لِيَحْكُمَ بَيْنَهُمْ اَنْ يَقُولُوا سَمِعْنَا وَاَطَعْنَاۜ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "Aralarında hüküm vermesi için Allah'a ve Resul'üne çağrıldıkları zaman İman Edenler'in sözü ancak, \"İşittik ve itaat ettik.\" demeleri oldu. İşte onlar kurtuluşa erenlerdir.", transliteration: "İnnema kane kavlel mu'minine iza duu ilallahi ve resulihi li yahkume beynehum en yekulu semi'na ve ata'na ve ulaike humul muflihun."},
  {id: 2843, sure_no: 52, ayet_no: 52, sure_adi: "Nur", text: "وَمَنْ يُطِـعِ اللّٰهَ وَرَسُولَهُ وَيَخْشَ اللّٰهَ وَيَتَّقْهِ فَاُو۬لٰٓئِكَ هُمُ الْفَٓائِزُونَ", translation: "Kim Allah'a ve Resul'üne itaat eder, Allah'a huşu duyar ve O'na takvalı olursa işte onlar kazançlı çıkacak olanlardır.", transliteration: "Ve men yutıillahe ve resulehu ve yahşallahe ve yettakhi fe ulaike humul faizun."},
  {id: 2844, sure_no: 53, ayet_no: 53, sure_adi: "Nur", text: "وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَئِنْ اَمَرْتَهُمْ لَيَخْرُجُنَّۜ قُلْ لَا تُقْسِمُواۚ طَاعَةٌ مَعْرُوفَةٌۜ اِنَّ اللّٰهَ خَب۪يرٌ بِمَا تَعْمَلُونَ", translation: "Münafıklar, kendilerinden istediğin takdirde kesinlikle savaşa çıkacaklarına dair en kuvvetli yeminleri ile Allah'a yemin ettiler. De ki: \"Yemin etmeyin. Bağlılık ma'ruftur.\" Kuşkusuz Allah, yaptıklarınızdan haberdardır.", transliteration: "Ve aksemu billahi cehde eymanihim le in emertehum le yahrucunn, kul la tuksimu, taatun ma'rufeh, innellahe habirun bima ta'melun."},
  {id: 2845, sure_no: 54, ayet_no: 54, sure_adi: "Nur", text: "قُلْ اَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَۚ فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَا حُمِّلْتُمْۜ وَاِنْ تُط۪يعُوهُ تَهْتَدُواۜ وَمَا عَلَى الرَّسُولِ اِلَّا الْبَلَاغُ الْمُب۪ينُ", translation: "De ki: \"Allah'a itaat edin. Resul'e itaat edin.\" Eğer itaat etmezseniz, ona düşen yalnızca görevini yapmaktır. Size düşen ise sorumlu tutulduğunuz şeye uymaktır. Eğer ona itaat ederseniz, doğru yolu bulursunuz. Resul'ün üzerinde, mesajı açıkça iletmekten başka bir sorumluluk yoktur.", transliteration: "Kul atiullahe ve atiur resul, fe in tevellev fe innema aleyhi ma hummile ve aleykum ma hummiltum, ve in tutiuhu tehtedu, ve ma aler resuli illel belagul mubin."},
  {id: 2846, sure_no: 55, ayet_no: 55, sure_adi: "Nur", text: "وَعَدَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا مِنْكُمْ وَعَمِلُوا الصَّالِحَاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الْاَرْضِ كَمَا اسْتَخْلَفَ الَّذ۪ينَ مِنْ قَبْلِهِمْۖ وَلَيُمَكِّنَنَّ لَهُمْ د۪ينَهُمُ الَّذِي ارْتَضٰى لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِنْ بَعْدِ خَوْفِهِمْ اَمْناًۜ يَعْبُدُونَن۪ي لَا يُشْرِكُونَ ب۪ي شَيْـٔاًۜ وَمَنْ كَفَرَ بَعْدَ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَ", translation: "Allah, sizden iman eden ve salihatı yapanlara, tıpkı kendilerinden öncekileri halife kıldığı gibi, kendilerini de yeryüzünde halife kılacağını, onlar için beğenip seçtiği dini mutlaka sağlamlaştıracağını ve endişelerinden sonra onları mutlaka güvene kavuşturacağını vaat etti. Onlar, Bana kulluk ederler ve hiçbir şeyi ortak koşmazlar. Bundan sonra da kim küfrederse, işte onlar yoldan çıkanların ta kendileridir.", transliteration: "Vaadallahullezine amenu minkum ve amilus salihati leyestahlifennehum fil ardı kemestahlefellezine min kablihim, ve leyumekkinenne lehum dinehumullezirteda lehum ve le yubeddilennehum min ba'di havfihim emna, ya'buduneni la yuşrikune bi şey'a, ve men kefere ba'de zalike fe ulaike humul fasikun."},
  {id: 2847, sure_no: 56, ayet_no: 56, sure_adi: "Nur", text: "وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَط۪يعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ", translation: "Salatı ikame edin, zekatı yapın. Ve Resul'e itaat edin. Umulur ki merhamet edilirsiniz.", transliteration: "Ve ekimus salate ve atuz zekate ve atiur resule leallekum turhamun."},
  {id: 2848, sure_no: 57, ayet_no: 57, sure_adi: "Nur", text: "لَا تَحْسَبَنَّ الَّذ۪ينَ كَفَرُوا مُعْجِز۪ينَ فِي الْاَرْضِۚ وَمَأْوٰيهُمُ النَّارُۜ وَلَبِئْسَ الْمَص۪يرُ۟", translation: "Sakın Kafirleri yeryüzünde aciz bırakıcı zannetme. Onların varacağı yer ateştir. Ne kötü bir varış yeridir.", transliteration: "La tahsebennellezine keferu mu'cizine fil ard, ve me'vahumun nar, ve le bi'sel masir."},
  {id: 2849, sure_no: 58, ayet_no: 58, sure_adi: "Nur", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لِيَسْتَأْذِنْكُمُ الَّذ۪ينَ مَلَكَتْ اَيْمَانُكُمْ وَالَّذ۪ينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلٰثَ مَرَّاتٍۜ مِنْ قَبْلِ صَلٰوةِ الْفَجْرِ وَح۪ينَ تَضَعُونَ ثِيَابَكُمْ مِنَ الظَّه۪يرَةِ وَمِنْ بَعْدِ صَلٰوةِ الْعِشَٓاءِ۠ ثَلٰثُ عَوْرَاتٍ لَكُمْۜ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌ بَعْدَهُنَّۜ طَوَّافُونَ عَلَيْكُمْ بَعْضُكُمْ عَلٰى بَعْضٍۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Ey İman Edenler! Antlaşma yoluyla sahip olduğunuz kimseler, sizden erginlik yaşına gelmemiş olanlar; şu üç vakitte, yanınıza girmek için sizden izin istesinler; sabah salatından önce, gün ortasında elbiselerinizi çıkardığınızda, akşam salatından sonra. Bu üç vakit \"avret\" vaktidir. Bunlar dışında birbirinizin yanına girip çıkmanızda siz ve onlar için bir sakınca yoktur. İşte Allah, size ayetleri böyle açıklıyor. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ya eyyuhellezine amenu li yeste'zinkumullezine meleket eymanukum vellezine lem yeblugul hulume minkum selase merrat, min kabli salatil fecri, ve hinetedaune siyabekum minez zahirat, ve min ba'di salatil ışai, selasu avratin lekum, leyse aleykum ve la aleyhim cunahun ba'de hunn, tavvafune aleykum ba'dukum ala ba'd, kezalike yubeyyinullahu lekumul ayat, vallahu alimun hakim."},
  {id: 2850, sure_no: 59, ayet_no: 59, sure_adi: "Nur", text: "وَاِذَا بَلَغَ الْاَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيَاتِه۪ۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Ve çocuklarınız, ergenlik çağına geldiği zaman, onlardan öncekilerin izin istedikleri gibi izin istesinler. İşte böylece Allah size ayetlerini açıklıyor. Ve Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve iza belegal etfalu minkumul hulume felyeste'zinu kemeste'zenellezine min kablihim, kezalike yubeyyinullahu lekum ayatih, vallahu alimun hakim."},
  {id: 2851, sure_no: 60, ayet_no: 60, sure_adi: "Nur", text: "وَالْقَوَاعِدُ مِنَ النِّسَٓاءِ الّٰت۪ي لَا يَرْجُونَ نِكَاحاً فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ اَنْ يَضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجَاتٍ بِز۪ينَةٍۜ وَاَنْ يَسْتَعْفِفْنَ خَيْرٌ لَهُنَّۜ وَاللّٰهُ سَم۪يعٌ عَل۪يمٌ", translation: "Evlenme arzusu kalmamış yaşlı kadınların, cinsiyetlerini teşhir etme amacı gütmeden dış giysilerini çıkarmalarında bir sakınca yoktur. Sakınmaları daha hayırlıdır. Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Vel kavaıdu minen nisaillati la yercune nikahan fe leyse aleyhinne cunahun en yeda'ne siyabehunne gayra muteberricatin bi zineh, ve en yesta'fifne hayrun lehunn, vallahu semiun alim."},
  {id: 2852, sure_no: 61, ayet_no: 61, sure_adi: "Nur", text: "لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَلَا عَلَى الْاَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَر۪يضِ حَرَجٌ وَلَا عَلٰٓى اَنْفُسِكُمْ اَنْ تَأْكُلُوا مِنْ بُيُوتِكُمْ اَوْ بُيُوتِ اٰبَٓائِكُمْ اَوْ بُيُوتِ اُمَّهَاتِكُمْ اَوْ بُيُوتِ اِخْوَانِكُمْ اَوْ بُيُوتِ اَخَوَاتِكُمْ اَوْ بُيُوتِ اَعْمَامِكُمْ اَوْ بُيُوتِ عَمَّاتِكُمْ اَوْ بُيُوتِ اَخْوَالِكُمْ اَوْ بُيُوتِ خَالَاتِكُمْ اَوْ مَا مَلَكْتُمْ مَفَاتِحَهُٓ اَوْ صَد۪يقِكُمْۜ لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَأْكُلُوا جَم۪يعاً اَوْ اَشْتَاتاًۜ فَاِذَا دَخَلْتُمْ بُيُوتاً فَسَلِّمُوا عَلٰٓى اَنْفُسِكُمْ تَحِيَّةً مِنْ عِنْدِ اللّٰهِ مُبَارَكَةً طَيِّبَةًۜ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيَاتِ لَعَلَّكُمْ تَعْقِلُونَ۟", translation: "Köre bir sınırlama yoktur. Sakat olana bir sınırlama yoktur. Hasta olana bir sınırlama yoktur. ve size de evlerinizde veya babalarınızın evlerinde veya annelerinizin evlerinde veya erkek kardeşlerinizin evlerinde veya kız kardeşlerinizin evlerinde veya amcalarınızın evlerinde veya halalarınızın evlerinde veya dayılarınızın evlerinde veya teyzelerinizin evlerinde veya anahtarlarına sahip olduğunuz yerlerde veya arkadaşlarınızın evlerinde yemek yemenizde bir sakınca yoktur. Toplu olarak veya ayrı ayrı olarak yemenizde de bir sakınca yoktur. Evlere girdiğiniz zaman Allah tarafından kutlu ve temiz bir selam ile selam verin. İşte Allah, size ayetlerini böylece açıklıyor. Umulur ki böylece aklınızı kullanırsınız.", transliteration: "Leyse alel a'ma haracun ve la alel a'raci haracun ve la alel maridı haracun ve la ala enfusikum en te'kulu min buyutikum ev buyuti abaikum ev buyuti ummehatikum ev buyuti ihvanikum ev buyuti ehavatikum ev buyuti a'mamikum ev buyuti ammatikum ev buyuti ahvalikum ev buyuti halatikum ev ma melektum mefatihahu ev sadikıkum, leyse aleykum cunahun en te'kulu cemian ev eştata, fe iza dahaltum buyuten fe sellimu ala enfusikum tehıyyeten min indillahi mubareketen tayyibeh, kezalike yubeyyinullahu lekumul ayati leallekum ta'kılun."},
  {id: 2853, sure_no: 62, ayet_no: 62, sure_adi: "Nur", text: "اِنَّمَا الْمُؤْمِنُونَ الَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَرَسُولِه۪ وَاِذَا كَانُوا مَعَهُ عَلٰٓى اَمْرٍ جَامِعٍ لَمْ يَذْهَبُوا حَتّٰى يَسْتَأْذِنُوهُۜ اِنَّ الَّذ۪ينَ يَسْتَأْذِنُونَكَ اُو۬لٰٓئِكَ الَّذ۪ينَ يُؤْمِنُونَ بِاللّٰهِ وَرَسُولِه۪ۚ فَاِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِمَنْ شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللّٰهَۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Ancak Allah'a ve Resul'üne içtenlikle iman etmiş Mü'minler, toplumu ilgilendiren bir iş için onunla bir araya geldikleri zaman, ondan izin almadıkça gitmezler. Senden izin isteyen kimseler, işte onlar Allah'a ve O'nun Resul'üne iman edenlerdir. Öyle ise, bazı işleri için senden izin istedikleri zaman onlardan dilediğin kimseye izin ver; onlar için Allah'tan bağışlanma dile. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İnnelmel mu'minunellezine amenu billahi ve resulihi ve iza kanu meahu ala emrin camiın lem yezhebu hatta yeste'zinuh, innellezine yeste'zinuneke ulaikellezine yu'minune billahi ve resulih, fe izeste'zenuke li ba'dı şe'nihim fe'zen li men şi'te minhum vestağfir lehumullah, innallahe gafurun rahim."},
  {id: 2854, sure_no: 63, ayet_no: 63, sure_adi: "Nur", text: "لَا تَجْعَلُوا دُعَٓاءَ الرَّسُولِ بَيْنَكُمْ  كَدُعَٓاءِ بَعْضِكُمْ بَعْضاًۜ قَدْ يَعْلَمُ اللّٰهُ الَّذ۪ينَ يَتَسَلَّلُونَ مِنْكُمْ لِوَاذاًۚ فَلْيَحْذَرِ الَّذ۪ينَ يُخَالِفُونَ عَنْ اَمْرِه۪ٓ اَنْ تُص۪يبَهُمْ فِتْنَةٌ اَوْ يُص۪يبَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Resul'ün çağrısını, birbirinize yaptığınız çağrıyla denk tutmayın. İçinizden birbirinin arkasına saklanarak sıvışmak isteyenleri Allah biliyor. Bu sebeple onun buyurmalarına karşı gelenler, kendilerine bir fitnenin veya çok acıklı bir azabın isabet etmesinden sakınsınlar.", transliteration: "La tec'alu duaer resuli beynekum ke duai ba'dıkum ba'da, kad ya'lemullahullezine yetesellelune minkum livaza, fel yahzerillezine yuhalifune an emrihi en tusibehum fitnetun ev yusibehum azabun elim."},
  {id: 2855, sure_no: 64, ayet_no: 64, sure_adi: "Nur", text: "اَلَٓا اِنَّ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ قَدْ يَعْلَمُ مَٓا اَنْتُمْ عَلَيْهِۜ وَيَوْمَ يُرْجَعُونَ اِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُواۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Unutmayın! Göklerde ve yerde olan her şey Allah'ındır. O sizin ne üzerinde olduğunuzu kesinlikle bilir. O'na döndürüldükleri gün, onlara yaptıklarını haber verecektir. Allah, Her Şeyi En İyi Bilen'dir.", transliteration: "E la inne lillahi ma fis semavati vel ard, kad ya'lemu ma entum aleyh, ve yevme yurceune ileyhi fe yunebbiuhum bi ma amilu, vallahu bi kulli şey'in alim."},
  {id: 2856, sure_no: 1, ayet_no: 1, sure_adi: "Furkan", text: "تَبَارَكَ الَّذ۪ي نَزَّلَ الْفُرْقَانَ عَلٰى عَبْدِه۪ لِيَكُونَ لِلْعَالَم۪ينَ نَذ۪يراًۙ", translation: "Alemlere uyarıcı olması için, kuluna Furkan'ı indiren ne mübarektir!", transliteration: "Tebarekellezi nezzelel furkane ala abdihi li yekune lil alemine nezira."},
  {id: 2857, sure_no: 2, ayet_no: 2, sure_adi: "Furkan", text: "اَلَّذ۪ي لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَلَمْ يَتَّخِذْ وَلَداً وَلَمْ يَكُنْ لَهُ شَر۪يكٌ فِي الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهُ تَقْد۪يراً", translation: "O ki, göklerin ve yerin egemenliği yalnızca O'na aittir. Ve O, çocuk edinmemiştir. Egemenlikte O'nun ortağı yoktur. Her şeyi yaratan, işleyiş ve varoluş yasalarını belirleyen O'dur.", transliteration: "Ellezi lehu mulkus semavati vel ardı ve lem yettehız veleden ve lem yekun lehu şerikun fil mulki ve halaka kulle şey'in fe kadderahu takdira."},
  {id: 2858, sure_no: 3, ayet_no: 3, sure_adi: "Furkan", text: "وَاتَّخَذُوا مِنْ دُونِه۪ٓ اٰلِهَةً لَا يَخْلُقُونَ شَيْـٔاً وَهُمْ يُخْلَقُونَ وَلَا يَمْلِكُونَ لِاَنْفُسِهِمْ ضَراًّ وَلَا نَفْعاً وَلَا يَمْلِكُونَ مَوْتاً وَلَا حَيٰوةً وَلَا نُشُوراً", translation: "O'nun yanı sıra hiçbir şeyi yaratamayan, kendileri yaratılmış olan, kendilerine yarar sağlama ve kendilerinden zararı savma güçleri olmayan; ölüme, hayata ve ölümden sonra diriltmeye güçleri yetmeyenleri ilahlar edindiler.", transliteration: "Vettehazu min dunihi aliheten la yahlukune şey'en ve hum yuhlekune ve la yemlikune li enfusihim darran ve la nef'an ve la yemlikune mevten ve la hayaten ve la nuşura."},
  {id: 2859, sure_no: 4, ayet_no: 4, sure_adi: "Furkan", text: "وَقَالَ الَّذ۪ينَ كَفَرُٓوا اِنْ هٰذَٓا اِلَّٓا اِفْكٌۨ افْتَرٰيهُ وَاَعَانَهُ عَلَيْهِ قَوْمٌ اٰخَرُونَۚۛ فَقَدْ جَٓاؤُ۫ ظُلْماً وَزُوراًۚۛ", translation: "Kafirler: \"Bu sadece onun uydurduğu bir yalandır. Ona bu konuda diğer toplumlar da yardım etti.\" dediler. Böylece haksızca iftira ettiler.", transliteration: "Ve kalellezine keferu in haza illa ifkunifterahu ve eanehu aleyhi kavmun aharun, fe kad cau zulmen ve zura."},
  {id: 2860, sure_no: 5, ayet_no: 5, sure_adi: "Furkan", text: "وَقَالُٓوا اَسَاط۪يرُ الْاَوَّل۪ينَ اكْتَتَبَهَا فَهِيَ تُمْلٰى عَلَيْهِ بُكْرَةً وَاَص۪يلاً", translation: "Ve \"O, onun yazdırdığı ve sabah akşam ona dikte ettikleri evvelkilerin masallarıdır.\" dediler.", transliteration: "Ve kalu esatirul evvelinektetebeha fe hiye tumla aleyhi bukreten ve asila."},
  {id: 2861, sure_no: 6, ayet_no: 6, sure_adi: "Furkan", text: "قُلْ اَنْزَلَهُ الَّذ۪ي يَعْلَمُ السِّرَّ فِي السَّمٰوَاتِ وَالْاَرْضِۜ اِنَّهُ كَانَ غَفُوراً رَح۪يماً", translation: "De ki: \"Onu, göklerin ve yerin sırrını Bilen indirdi. Kuşkusuz O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.\"", transliteration: "Kul enzelehullezi ya'lemus sırre fis semavati vel ard, innehu kane gafuran rahima."},
  {id: 2862, sure_no: 7, ayet_no: 7, sure_adi: "Furkan", text: "وَقَالُوا مَالِ هٰذَا الرَّسُولِ يَأْكُلُ الطَّعَامَ وَيَمْش۪ي فِي الْاَسْوَاقِۜ  لَوْلَٓا اُنْزِلَ اِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذ۪يراًۙ", translation: "Dediler ki: \"Bu nasıl bir Resul ki, yiyor-içiyor, çarşı-pazar dolaşıyor. Ona bir melek indirilseydi de onunla birlikte uyarıcı olsaydı ya!\"", transliteration: "Ve kalu mali hazer resuli ye'kulit taame ve yemşi fil esvak, lev la unzile ileyhi melekun fe yekune meahu nezira."},
  {id: 2863, sure_no: 8, ayet_no: 8, sure_adi: "Furkan", text: "اَوْ يُلْقٰٓى اِلَيْهِ كَنْزٌ اَوْ تَكُونُ لَهُ جَنَّةٌ يَأْكُلُ مِنْهَاۜ وَقَالَ الظَّالِمُونَ اِنْ تَتَّبِعُونَ اِلَّا رَجُلاً مَسْحُوراً", translation: "\"Veya ona bir hazine verilseydi veya kendisinden yiyeceği bir bahçesi olsaydı ya!\" Bu zalimler: \"Siz yalnızca büyülenmiş bir adama uyuyorsunuz.\" dediler.", transliteration: "Ev yulka ileyhi kenzun ev tekunu lehu cennetunye'kulu minha, ve kalez zalimune in tettebiune illa raculen meshura."},
  {id: 2864, sure_no: 9, ayet_no: 9, sure_adi: "Furkan", text: "اُنْظُرْ كَيْفَ ضَرَبُوا لَكَ الْاَمْثَالَ فَضَلُّوا فَلَا يَسْتَط۪يعُونَ سَب۪يلاً۟", translation: "Senin için nasıl örnekler verdiklerine bir bak. Böylece saptılar, artık bir çıkış yolu bulamazlar.", transliteration: "Unzur keyfe darabu lekel emsale fe dallu fe la yestetiune sebila."},
  {id: 2865, sure_no: 10, ayet_no: 10, sure_adi: "Furkan", text: "تَبَارَكَ الَّـذ۪ٓي اِنْ شَٓاءَ جَعَلَ لَكَ خَيْراً مِنْ ذٰلِكَ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۙ وَيَجْعَلْ لَكَ قُصُوراً", translation: "O, öyle mübarektir ki, dilerse sana bundan daha hayırlı olan, içinden ırmaklar akan bahçeler verir. Köşkler de verir.", transliteration: "Tebarekellezi in şae ceale leke hayren min zalike cennatin tecri min tahtihel enharu ve yec'al leke kusura."},
  {id: 2866, sure_no: 11, ayet_no: 11, sure_adi: "Furkan", text: "بَلْ كَذَّبُوا بِالسَّاعَةِ وَاَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَع۪يراًۚ", translation: "Hayır! Onlar Sa'at'i1 yalanladılar. Ve Biz, o Sa'at'i yalanlayanlara alevli ateş hazırladık.", transliteration: "Bel kezzebu bis saati ve a'tedna li men kezzebe bis saati saira."},
  {id: 2867, sure_no: 12, ayet_no: 12, sure_adi: "Furkan", text: "اِذَا رَاَتْهُمْ مِنْ مَكَانٍ بَع۪يدٍ سَمِعُوا لَهَا تَغَيُّظاً وَزَف۪يراً", translation: "Cehennem onları uzak bir yerden gördüğü zaman, onun öfkelenmesini ve uğultusunu işittiler.", transliteration: "İza raethum min mekanin baidin semiu leha tegayyuzan ve zefira."},
  {id: 2868, sure_no: 13, ayet_no: 13, sure_adi: "Furkan", text: "وَاِذَٓا اُلْقُوا مِنْهَا مَكَاناً ضَيِّقاً مُقَرَّن۪ينَ دَعَوْا هُنَالِكَ ثُبُوراًۜ", translation: "Çaresizlik içinde, dar bir yere atıldıkları zaman orada yok olmak için yakaracaklar.", transliteration: "Ve iza ulku minha mekanen dayyıkan mukarrenine deav hunalike subura."},
  {id: 2869, sure_no: 14, ayet_no: 14, sure_adi: "Furkan", text: "لَا تَدْعُوا الْيَوْمَ ثُبُوراً وَاحِداً وَادْعُوا ثُبُوراً كَث۪يراً", translation: "Bugün bir kez değil, defalarca yok olmayı isteyin.", transliteration: "La ted'ul yevme suburan vahıden ved'u suburan kesira."},
  {id: 2870, sure_no: 15, ayet_no: 15, sure_adi: "Furkan", text: "قُلْ اَذٰلِكَ خَيْرٌ اَمْ جَنَّةُ الْخُلْدِ الَّت۪ي وُعِدَ الْمُتَّقُونَۜ كَانَتْ لَهُمْ جَزَٓاءً وَمَص۪يراً", translation: "De ki: \"Bu mu daha hayırlıdır, yoksa takva sahipleri için bir ödül olan, dönüş yeri olarak söz verilen süresiz Cennet mi?\"", transliteration: "Kul e zalike hayrun em cennetul huldilleti vuidel muttekun, kanet lehum cezaen ve masira."},
  {id: 2871, sure_no: 16, ayet_no: 16, sure_adi: "Furkan", text: "لَهُمْ ف۪يهَا مَا يَشَٓاؤُ۫نَ خَالِد۪ينَۜ  كَانَ عَلٰى رَبِّكَ وَعْداً مَسْؤُ۫لاً", translation: "Onlar için orada diledikleri her şey süresiz olarak vardır. Bu Rabb'inin yerine getirmeyi üstlendiği bir sözdür.", transliteration: "Lehum fiha ma yeşaune halidin, kane ala rabbike va'den mes'ula."},
  {id: 2872, sure_no: 17, ayet_no: 17, sure_adi: "Furkan", text: "وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللّٰهِ فَيَقُولُ ءَاَنْتُمْ اَضْلَلْتُمْ عِبَاد۪ي هٰٓؤُ۬لَٓاءِ اَمْ هُمْ ضَلُّوا السَّب۪يلَۜ", translation: "Ve O Gün, onları ve Allah'ın yanı sıra kulluk ettikleri şeyleri toplar, sonra da onlara: \"Kullarımı siz mi saptırdınız yoksa kendileri mi yoldan çıktılar?\" der.", transliteration: "Ve yevme yahşuruhum ve ma ya'budune min dunillahi fe yekulu e entum adleltum ibadi haulai em hum dallus sebil."},
  {id: 2873, sure_no: 18, ayet_no: 18, sure_adi: "Furkan", text: "قَالُوا سُبْحَانَكَ مَا كَانَ يَنْبَغ۪ي لَـنَٓا اَنْ نَتَّخِذَ مِنْ دُونِكَ مِنْ اَوْلِيَٓاءَ وَلٰكِنْ مَتَّعْتَهُمْ وَاٰبَٓاءَهُمْ حَتّٰى نَسُوا الذِّكْرَۚ وَكَانُوا قَوْماً بُوراً", translation: "Dediler ki: \"Seni tenzih ederiz, Senden başka veliler edinmek bize yakışmaz. Ancak Sen, onları ve atalarını bolca nimetlendirdin, nihayet onlar öğüdüne uymayı boş verdiler. Ve helak olmayı hak eden bir halk oldular.\"", transliteration: "Kalu subhaneke ma kane yenbegi lena en nettehıze min dunike min evliyae ve lakin metta'tehum ve abaehum hatta nesuz zikre, ve kanu kavmen bura."},
  {id: 2874, sure_no: 19, ayet_no: 19, sure_adi: "Furkan", text: "فَقَدْ كَذَّبُوكُمْ بِمَا تَقُولُونَۙ فَمَا تَسْتَط۪يعُونَ صَرْفاً وَلَا نَصْراًۚ وَمَنْ يَظْلِمْ مِنْكُمْ نُذِقْهُ عَذَاباً كَب۪يراً", translation: "İşte onlar söylediklerinizden dolayı sizi yalanladılar. Artık uzaklaştırmaya ve yardım almaya güç yetiremezsiniz. Ve sizden kim haksızlık etmişse ona büyük azabı tattıracağız.", transliteration: "Fe kad kezzebukum bima tekulune fe ma testetiune sarfan ve la nasra, ve men yazlım minkum nuzıkhu azaben kebira."},
  {id: 2875, sure_no: 20, ayet_no: 20, sure_adi: "Furkan", text: "وَمَٓا اَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَل۪ينَ اِلَّٓا اِنَّهُمْ لَيَأْكُلُونَ الطَّعَامَ وَيَمْشُونَ فِي الْاَسْوَاقِۜ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةًۜ اَتَصْبِرُونَۚ وَكَانَ رَبُّكَ بَص۪يراً۟", translation: "Kesinlikle Biz, senden önce de yemek yiyen ve çarşılarda dolaşan Resulerden farklı Resul göndermedik. Ve sizin bir kısmınızı bir kısmınız için sınav kıldık. Bakalım sabrediyor musunuz? Rabb'in Her Şeyi Gören'dir.", transliteration: "Ve ma erselna kableke minel murseline illa innehum le ye'kulunet taame ve yemşune fil esvakı ve cealna ba'dakum li ba'dın fitneten, e tasbirun, ve kane rabbuke basira."},
  {id: 2876, sure_no: 21, ayet_no: 21, sure_adi: "Furkan", text: "وَقَالَ الَّذ۪ينَ لَا يَرْجُونَ لِقَٓاءَنَا لَوْلَٓا اُنْزِلَ عَلَيْنَا الْمَلٰٓئِكَةُ اَوْ نَرٰى رَبَّـنَاۜ لَقَدِ اسْتَكْبَرُوا ف۪ٓي اَنْفُسِهِمْ وَعَتَوْ عُتُواًّ كَب۪يراً", translation: "Bizimle karşılaşacaklarına ihtimal vermeyenler: \"Bize meleklerin indirilmesi veya Rabb'imizi görmemiz gerekmez miydi?\" dediler. Ant olsun ki onlar kendi kendilerine büyüklendiler ve büyük bir taşkınlıkla hadlerini aştılar.", transliteration: "Ve kalellezine la yercune likaena lev la unzile aleynel melaiketu ev nera rabbena, lekad istekberu fi enfusihim ve atev utuvven kebira."},
  {id: 2877, sure_no: 22, ayet_no: 22, sure_adi: "Furkan", text: "يَوْمَ يَرَوْنَ الْمَلٰٓئِكَةَ لَا بُشْرٰى يَوْمَئِذٍ لِلْمُجْرِم۪ينَ وَيَقُولُونَ حِجْراً مَحْجُوراً", translation: "Suçları kendilerini kuşatmış olanlar, melekleri görecekleri gün, onlar için hiç de sevindirici olmayacak, melekler onlara: \"Boşuna beklemeyin size sevindirici haber imkansız.\" diyecekler.", transliteration: "Yevme yerevnel melaikete la buşra yevme izin lil mucrimine ve yekulune hicran mahcura."},
  {id: 2878, sure_no: 23, ayet_no: 23, sure_adi: "Furkan", text: "وَقَدِمْنَٓا اِلٰى مَا عَمِلُوا مِنْ عَمَلٍ فَجَعَلْنَاهُ هَبَٓاءً مَنْثُوراً", translation: "Ve onların yaptıklarının tamamını savrulmuş toz zerresi gibi boşa çıkaracağız.", transliteration: "Ve kadimna ila ma amilu min amelin fe cealnahu hebaen mensura."},
  {id: 2879, sure_no: 24, ayet_no: 24, sure_adi: "Furkan", text: "اَصْحَابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُسْتَقَراًّ وَاَحْسَنُ مَق۪يلاً", translation: "O Gün, Cennet ehli en yararlı yerde eğlenecek ve en iyi yerde dinlenecek.", transliteration: "Ashabul cenneti yevme izin hayrun mustekarran ve ahsenu makila."},
  {id: 2880, sure_no: 25, ayet_no: 25, sure_adi: "Furkan", text: "وَيَوْمَ تَشَقَّقُ السَّمَٓاءُ بِالْغَمَامِ وَنُزِّلَ الْمَلٰٓئِكَةُ تَنْز۪يلاً", translation: "O Gün, gök beyaz bulutlar şeklinde parçalanacak ve melekler ardı sıra indirilecek.", transliteration: "Ve yevme teşakkakus semau bil gamami ve nuzzilel melaiketu tenzila."},
  {id: 2881, sure_no: 26, ayet_no: 26, sure_adi: "Furkan", text: "اَلْمُلْكُ يَوْمَئِذٍۨ الْحَقُّ لِلرَّحْمٰنِۜ وَكَانَ يَوْماً عَلَى الْكَافِر۪ينَ عَس۪يراً", translation: "O Gün egemenlik yalnızca Rahman'a aittir. O gün, Kafirler için zor bir gündür.", transliteration: "El mulku yevmeizinil hakku lir rahman, ve kane yevmen alel kafirine asira."},
  {id: 2882, sure_no: 27, ayet_no: 27, sure_adi: "Furkan", text: "وَيَوْمَ يَعَضُّ الظَّالِمُ عَلٰى يَدَيْهِ يَقُولُ يَا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَب۪يلاً", translation: "O Gün, zalim kimse, ellerini ısırarak: \"Eyvah, keşke Resul'ün tuttuğu yolu tutmuş olsaydım.\" diyecek.", transliteration: "Ve yevme yeadduz zalimu ala yedeyhi yekulu ya leytenittehaztu mear resuli sebila."},
  {id: 2883, sure_no: 28, ayet_no: 28, sure_adi: "Furkan", text: "يَا وَيْلَتٰى لَيْتَن۪ي لَمْ اَتَّخِذْ فُلَاناً خَل۪يلاً", translation: "\"Eyvah! Keşke falancayı dost edinmeseydim!\"", transliteration: "Ya veyleta leyteni lem ettehız fulanen halila."},
  {id: 2884, sure_no: 29, ayet_no: 29, sure_adi: "Furkan", text: "لَقَدْ اَضَلَّن۪ي عَنِ الذِّكْرِ بَعْدَ اِذْ جَٓاءَن۪يۜ وَكَانَ الشَّيْطَانُ لِلْاِنْسَانِ خَذُولاً", translation: "\"Ant olsun ki bana öğüt geldiği halde, beni o sapıttı.\" Şeytan, insanı yalnız ve yardımsız bırakır!", transliteration: "Lekad edalleni aniz zikri ba'de iz caeni, ve kaneş şeytanu lil insani hazula."},
  {id: 2885, sure_no: 30, ayet_no: 30, sure_adi: "Furkan", text: "وَقَالَ الرَّسُولُ يَا رَبِّ اِنَّ قَوْمِي اتَّخَذُوا هٰذَا الْقُرْاٰنَ مَهْجُوراً", translation: "Resul: \"Ey Rabb'im, halkım bu Kur'an'ı mehcur tuttu.\" dedi.", transliteration: "Ve kaler resulu ya rabbi inne kavmittehazu hazel kur'ane mehcura."},
  {id: 2886, sure_no: 31, ayet_no: 31, sure_adi: "Furkan", text: "وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُواًّ مِنَ الْمُجْرِم۪ينَۜ وَكَفٰى بِرَبِّكَ هَادِياً وَنَص۪يراً", translation: "Bu hep böyle olmuştur. Biz her Nebi'ye, mücrimlerden bir düşman kıldık. Yol gösterici ve yardım edici olarak Rabb'in sana yeter.", transliteration: "Ve kezalike cealna li kulli nebiyyin aduvven minel mucrimin, ve kefa bi rabbike hadiyen ve nasira."},
  {id: 2887, sure_no: 32, ayet_no: 32, sure_adi: "Furkan", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْاٰنُ جُمْلَةً وَاحِدَةًۚ كَذٰلِكَ لِنُثَبِّتَ بِه۪ فُؤٰادَكَ وَرَتَّلْنَاهُ تَرْت۪يلاً", translation: "Kafirler: \"Kur'an ona bir defada ve topluca indirilmeli değil miydi?\" dediler. Oysaki bu, onu kalbine iyice yerleştirelim diyedir. Onu düzenli bir şekilde pekiştire pekiştire indirdik.", transliteration: "Ve kalellezine keferu lev la nuzzile aleyhil kur'anu, cumleten vahideh, kezalike li nusebbite bihi fuadeke ve rettelnahu tertila."},
  {id: 2888, sure_no: 33, ayet_no: 33, sure_adi: "Furkan", text: "وَلَا يَأْتُونَكَ بِمَثَلٍ اِلَّا جِئْنَاكَ بِالْحَقِّ وَاَحْسَنَ تَفْس۪يراًۜ", translation: "Onların sana verecekleri her örneğe karşılık, biz sana Hakk'ı en iyi şekilde açıklarız.", transliteration: "Ve la ye'tuneke bi meselin illa ci'nake bil hakkı ve ahsene tefsira."},
  {id: 2889, sure_no: 34, ayet_no: 34, sure_adi: "Furkan", text: "اَلَّذ۪ينَ يُحْشَرُونَ عَلٰى وُجُوهِهِمْ اِلٰى جَهَنَّمَۙ اُو۬لٰٓئِكَ شَرٌّ مَكَاناً وَاَضَلُّ سَب۪يلاً۟", translation: "Yüzüstü Cehennem'e toplanacak olanlar; işte onlar, yerce çok kötü ve yolca çok sapkınlardır.", transliteration: "Ellezine yuhşerune ala vucuhihim ila cehenneme ulaike şerrun mekanen ve edallu sebila."},
  {id: 2890, sure_no: 35, ayet_no: 35, sure_adi: "Furkan", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَا مَعَهُٓ اَخَاهُ هٰرُونَ وَز۪يراًۚ", translation: "Ant olsun ki Musa'ya Kitap'ı verdik. Ve kardeşi Harun'u da ona yardımcı yaptık.", transliteration: "Ve lekad ateyna musel kitabe ve cealna meahu ehahu harune vezira."},
  {id: 2891, sure_no: 36, ayet_no: 36, sure_adi: "Furkan", text: "فَقُلْنَا اذْهَبَٓا اِلَى الْقَوْمِ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِنَاۜ فَدَمَّرْنَاهُمْ تَدْم۪يراًۜ", translation: "Sonra da \"Ayetlerimizi yalanlayan o halka gidin.\" dedik. Sonunda da onları helak ederek yok ettik.", transliteration: "Fe kulnazheba ilel kavmillezine kezzebu bi ayatina, fe demmernahum tedmira."},
  {id: 2892, sure_no: 37, ayet_no: 37, sure_adi: "Furkan", text: "وَقَوْمَ نُوحٍ لَمَّا كَذَّبُوا الرُّسُلَ اَغْرَقْنَاهُمْ وَجَعَلْنَاهُمْ لِلنَّاسِ اٰيَةًۜ وَاَعْتَدْنَا لِلظَّالِم۪ينَ عَـذَاباً اَل۪يماًۚ", translation: "Nuh'un halkı, Resuller'i yalanladıklarında suda boğduk ve onları insanlar için bir ayet yaptık. Ve zalimler için acı veren bir azap hazırladık.", transliteration: "Ve kavme nuhın lemma kezzebur rusule agraknahum ve cealnahum lin nasi ayeh, ve a'tedna liz zalimine azaben elima."},
  {id: 2893, sure_no: 38, ayet_no: 38, sure_adi: "Furkan", text: "وَعَـاداً وَثَمُودَا۬ وَاَصْحَابَ الرَّسِّ وَقُرُوناً بَيْنَ ذٰلِكَ كَث۪يراً", translation: "Ad'ı, Semud'u, Ress yandaşlarını ve bunlar arasında yaşayan daha birçok nesilleri de;", transliteration: "Ve aden ve semuda ve ashaber ressi ve kurunen beyne zalike kesira."},
  {id: 2894, sure_no: 39, ayet_no: 39, sure_adi: "Furkan", text: "وَكُلاًّ ضَرَبْنَا لَهُ الْاَمْثَالَۘ وَكُلاًّ تَبَّرْنَا تَتْب۪يراً", translation: "Ve onların hepsini örneklerle uyardık ve hepsini mahvederek helak ettik.", transliteration: "Ve kullen darabna lehul emsale ve kullen tebberna tetbira."},
  {id: 2895, sure_no: 40, ayet_no: 40, sure_adi: "Furkan", text: "وَلَقَدْ اَتَوْا عَلَى الْقَرْيَةِ الَّت۪ٓي اُمْطِرَتْ مَطَرَ السَّوْءِۜ اَفَلَمْ يَكُونُوا يَرَوْنَهَاۚ بَلْ كَانُوا لَا يَرْجُونَ نُشُوراً", translation: "Ve ant olsun ki onlar, felaket yağmuru yağdırılmış olan beldeye vardılar. Peki, onu da mı görmediler? Aksine onlar, öldükten sonra dirilmeyi beklemiyorlar.", transliteration: "Ve lekad atev alel karyetilleti umtırat mataras sev', e fe lem yekunu yerevneha, bel kanu la yercune nuşura."},
  {id: 2896, sure_no: 41, ayet_no: 41, sure_adi: "Furkan", text: "وَاِذَا رَاَوْكَ اِنْ يَتَّخِذُونَكَ اِلَّا هُزُواًۜ اَهٰذَا الَّذ۪ي بَعَثَ اللّٰهُ رَسُولاً", translation: "Seni gördükleri zaman: \"Allah'ın Resul olarak gönderdiği bu mu?\" diye alay konusu ediniyorlar.", transliteration: "Ve iza reavke in yettehızuneke illa huzuva, e hazellezi beasallahu resula."},
  {id: 2897, sure_no: 42, ayet_no: 42, sure_adi: "Furkan", text: "اِنْ كَادَ لَيُضِلُّنَا عَنْ اٰلِهَتِنَا لَوْلَٓا اَنْ صَبَرْنَا عَلَيْهَاۜ وَسَوْفَ يَعْلَمُونَ ح۪ينَ يَرَوْنَ الْعَذَابَ مَنْ اَضَلُّ سَب۪يلاً", translation: "\"Şayet ilahlarımıza bağlılıkta kararlı olmasaydık, neredeyse bizi saptırıyordu.\" diyorlar. Azabı gördükleri zaman kimin sapkın olduğunu bileceklerdir.", transliteration: "İn kade le yudılluna an alihetina lev la en saberna aleyha, ve sevfe ya'lemune hine yerevnel azabe men edallu sebila."},
  {id: 2898, sure_no: 43, ayet_no: 43, sure_adi: "Furkan", text: "اَرَاَيْتَ مَنِ اتَّخَذَ اِلٰهَهُ هَوٰيهُۜ اَفَاَنْتَ تَكُونُ عَلَيْهِ وَك۪يلاًۙ", translation: "Hevasını ilah edinen kimseyi görüyorsun! Onun sorumluluğunu sen üstlenebilir misin?", transliteration: "E raeyte menittehaze ilahehu hevah, e fe ente tekunu aleyhi vekila."},
  {id: 2899, sure_no: 44, ayet_no: 44, sure_adi: "Furkan", text: "اَمْ تَحْسَبُ اَنَّ اَكْثَرَهُمْ يَسْمَعُونَ اَوْ يَعْقِلُونَۜ اِنْ هُمْ اِلَّا كَالْاَنْـعَامِ بَلْ هُمْ اَضَلُّ سَب۪يلاً۟", translation: "Yoksa onların çoğunun gerçeğe kulak verdiğini veya akıllarını kullandıklarını mı sanıyorsun? Onlar tıpkı hayvanlar gibidir. Hatta doğru yolu seçmede hayvanlardan daha yetersizdirler.", transliteration: "Em tahsebu enne ekserehum yesmeune ev ya'kılun, in hum illa kel en'ami bel hum edallu sebila."},
  {id: 2900, sure_no: 45, ayet_no: 45, sure_adi: "Furkan", text: "اَلَمْ تَرَ اِلٰى رَبِّكَ كَيْفَ مَدَّ الظِّلَّۚ وَلَوْ شَٓاءَ لَجَعَلَهُ سَاكِناًۚ ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَل۪يلاًۙ", translation: "Rabb'inin gölgeyi nasıl uzattığını görmez misin? Dileseydi onu sabit kılardı. Sonra da Güneş'i ona yol gösterici yaptık.", transliteration: "E lem tere ila rabbike keyfe meddez zıll, ve lev şae le cealehu sakina, summe cealneş şemse aleyhi delila."},
  {id: 2901, sure_no: 46, ayet_no: 46, sure_adi: "Furkan", text: "ثُمَّ قَبَضْنَاهُ اِلَيْنَا قَبْضاً يَس۪يراً", translation: "Sonra da onu kolayca kendimize çektik.", transliteration: "Summe kabadnahu ileyna kabdan yesira."},
  {id: 2902, sure_no: 47, ayet_no: 47, sure_adi: "Furkan", text: "وَهُوَ الَّذ۪ي جَعَلَ لَكُمُ الَّيْلَ لِبَاساً وَالنَّوْمَ سُبَاتاً وَجَعَلَ النَّهَارَ نُشُوراً", translation: "Sizin için geceyi örtü, uykuyu dinlenme ve gündüzü çalışma zamanı kıldık.", transliteration: "Ve huvellezi ceale lekumul leyle libasen ven nevme subaten ve cealen nehare nuşura."},
  {id: 2903, sure_no: 48, ayet_no: 48, sure_adi: "Furkan", text: "وَهُوَ الَّـذ۪ٓي اَرْسَلَ الرِّيَاحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِه۪ۚ وَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً طَهُوراًۙ", translation: "Rahmetinin önünde rüzgarı haberci olarak gönderen O'dur. Biz, gökten tertemiz bir su indirdik.", transliteration: "Ve huvellezi erseler riyaha buşren beyne yedey rahmetih, ve enzelna mines semai maen tahura."},
  {id: 2904, sure_no: 49, ayet_no: 49, sure_adi: "Furkan", text: "لِنُحْيِيَ بِه۪ بَلْدَةً مَيْتاً وَنُسْقِيَهُ مِمَّا خَلَقْنَٓا اَنْعَاماً وَاَنَاسِيَّ كَث۪يراً", translation: "Ki o yağmurla ölü bir beldeyi canlandırmak, yaratmış olduğumuz hayvanları ve insanları suya kavuşturmak içindir.", transliteration: "Li nuhyiye bihi beldeten meyten ve nuskıyehu mimma halakna en'amen ve enasiyye kesira."},
  {id: 2905, sure_no: 50, ayet_no: 50, sure_adi: "Furkan", text: "وَلَقَدْ صَرَّفْنَاهُ بَيْنَهُمْ لِيَذَّكَّرُواۘ  فَاَبٰٓى اَكْثَرُ النَّاسِ اِلَّا كُفُوراً", translation: "Ant olsun ki Biz, öğüt almaları için onu, onların aralarında paylaştırdık. Ancak insanların çoğu nankörlük etmekte direttiler.", transliteration: "Ve lekad sarrafnahu beynehum li yezzekkeru fe eba ekserun nasi illa kufura."},
  {id: 2906, sure_no: 51, ayet_no: 51, sure_adi: "Furkan", text: "وَلَوْ شِئْنَا لَبَعَثْنَا ف۪ي كُلِّ قَرْيَةٍ نَذ۪يراًۘ", translation: "Eğer dileseydik, elbette her beldeden bir uyarıcı çıkarırdık.", transliteration: "Ve lev şi'na le beasna fi kulli karyetin nezira."},
  {id: 2907, sure_no: 52, ayet_no: 52, sure_adi: "Furkan", text: "فَلَا تُطِعِ الْكَافِر۪ينَ وَجَاهِدْهُمْ بِه۪ جِهَاداً كَب۪يراً", translation: "Öyleyse Kafirlere boyun eğme. Onunla2 onlara karşı var gücünle büyük bir mücadeleye giriş.", transliteration: "Fe la tutııl kafirine ve cahidhum bihi cihaden kebira."},
  {id: 2908, sure_no: 53, ayet_no: 53, sure_adi: "Furkan", text: "وَهُوَ الَّذ۪ي مَرَجَ الْبَحْرَيْنِ هٰذَا عَذْبٌ فُرَاتٌ وَهٰذَا مِلْحٌ اُجَاجٌۚ وَجَعَلَ بَيْنَهُمَا بَرْزَخاً وَحِجْراً مَحْجُوراً", translation: "Ve iki denizi serbest bırakan O'dur; biri lezzetli ve tatlı, diğeri tuzlu ve acı. Aralarına bir perde koydu. Birbirlerine karışmalarını engelledi.", transliteration: "Ve huvellezi meracel bahreyni haza azbun furatun ve haza milhun ucac, ve ceale beynehuma, berzehan ve hıcran mahcura."},
  {id: 2909, sure_no: 54, ayet_no: 54, sure_adi: "Furkan", text: "وَهُوَ الَّذ۪ي خَلَقَ مِنَ الْمَٓاءِ بَشَراً فَجَعَلَهُ نَسَباً وَصِهْراًۜ وَكَانَ رَبُّكَ قَد۪يراً", translation: "O, beşeri sudan yarattı. Sonra onu, soy sop ve akraba sahibi kıldı. Senin Rabb'in her şeye güç yetirendir.", transliteration: "Ve huvellezi halaka minel mai beşeren fe cealehu neseben ve sıhra, ve kane rabbuke kadira."},
  {id: 2910, sure_no: 55, ayet_no: 55, sure_adi: "Furkan", text: "وَيَعْبُدُونَ مِنْ دُونِ اللّٰهِ مَا لَا يَنْفَعُهُمْ وَلَا يَضُرُّهُمْۜ وَكَانَ الْكَافِرُ عَلٰى رَبِّه۪ ظَه۪يراً", translation: "Allah'ın yanı sıra kendilerine bir yararı da zararı da olmayan şeylere kulluk ediyorlar. Kafir, Rabb'ine karşı olanın destekçisidir.", transliteration: "Ve ya'budune min dunillahi ma la yenfeuhum ve la yadurruhum, ve kanel kafiru ala rabbihi zahira."},
  {id: 2911, sure_no: 56, ayet_no: 56, sure_adi: "Furkan", text: "وَمَٓا اَرْسَلْنَاكَ اِلَّا مُبَشِّراً وَنَذ۪يراً", translation: "Biz, seni haberci ve uyarıcı olmanın dışında başka bir şey için göndermedik.", transliteration: "Ve ma erselnake illa mubeşşiren ve nezira."},
  {id: 2912, sure_no: 57, ayet_no: 57, sure_adi: "Furkan", text: "قُلْ مَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِلَّا مَنْ شَٓاءَ اَنْ يَتَّخِذَ اِلٰى رَبِّه۪ سَب۪يلاً", translation: "De ki: \"Yaptığım çağrı için; sizden, Rabb'inizin yolunu seçmenizi istemekten başka bir ücret istemiyorum.\"", transliteration: "Kul ma es'elukum aleyhi min ecrin illa men şae en yettehıze ila rabbihi sebila."},
  {id: 2913, sure_no: 58, ayet_no: 58, sure_adi: "Furkan", text: "وَتَوَكَّلْ عَلَى الْحَيِّ الَّذ۪ي لَا يَمُوتُ وَسَبِّـحْ بِحَمْدِه۪ۜ وَكَفٰى بِه۪ بِذُنُوبِ عِبَادِه۪ خَب۪يراًۚۛ", translation: "Ve sen, Ölümsüz Olan'a güvenip dayan. Ve O'nu övgü ile yücelt. O'nun kullarının suçlarından haberdar olması sana yeter.", transliteration: "Ve tevekkel alel hayyillezi la yemutu ve sebbih bi hamdih, ve kefa bihi bi zunubi ibadihi habira."},
  {id: 2914, sure_no: 59, ayet_no: 59, sure_adi: "Furkan", text: "اَلَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚۛ اَلرَّحْمٰنُ فَسْـَٔلْ بِه۪ خَب۪يراً", translation: "O; gökleri, yeri ve ikisinin arasındakileri altı günde yarattı. Sonra arşa isteva etti. O, Rahman'dır. Öyleyse Her Şeyden Haberdar Olan'dan iste.", transliteration: "Ellezi halakas semavati vel arda ve ma beynehuma fi sitteti eyyamin summesteva alel arşir rahmanu fes'el bihi habira."},
  {id: 2915, sure_no: 60, ayet_no: 60, sure_adi: "Furkan", text: "وَاِذَا ق۪يلَ لَهُمُ اسْجُدُوا لِلرَّحْمٰنِ قَالُوا وَمَا الرَّحْمٰنُۗ اَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُوراً۟", translation: "Ve onlara, \"Rahman'a secde edin.\" dendiği zaman, \"Rahman da neymiş? Senin bize buyurduğun her şeye secde mi edeceğiz?\" dediler. Bu çağrın onların nefretlerini arttırdı.", transliteration: "Ve iza kile lehumuscudu lir rahmani kalu ve mer rahmanu e nescudu li ma te'muruna ve zadehum nufura."},
  {id: 2916, sure_no: 61, ayet_no: 61, sure_adi: "Furkan", text: "تَبَارَكَ الَّذ۪ي جَعَلَ فِي السَّمَٓاءِ بُرُوجاً وَجَعَلَ ف۪يهَا سِرَاجاً وَقَمَراً مُن۪يراً", translation: "Gökte burçlar yapan ve orada bir çerağ ve aydınlatan Ay'ı yapan ne mübarektir.", transliteration: "Tebarekellezi ceale fis semai burucen ve ceale fiha siracen ve kameren munira."},
  {id: 2917, sure_no: 62, ayet_no: 62, sure_adi: "Furkan", text: "وَهُوَ الَّذ۪ي جَعَلَ الَّيْلَ وَالنَّهَارَ خِلْفَةً لِمَنْ اَرَادَ اَنْ يَذَّكَّرَ اَوْ اَرَادَ شُكُوراً", translation: "Ve O, öğüt almak veya şükretmek isteyenler için gece ile gündüzü art arda getirendir.", transliteration: "Ve huvellezi cealel leyle ven nehare hılfeten li men erade en yezzekkere ev erade şukura."},
  {id: 2918, sure_no: 63, ayet_no: 63, sure_adi: "Furkan", text: "وَعِبَادُ الرَّحْمٰنِ الَّذ۪ينَ يَمْشُونَ عَلَى الْاَرْضِ هَوْناً وَاِذَا خَاطَبَهُمُ الْجَاهِلُونَ قَالُوا سَلَاماً", translation: "Rahman'ın kulları yeryüzünde alçak gönüllülükle yürürler. Cahiller, onlara laf attıkları zaman, \"Selam.\" derler.", transliteration: "Ve ibadur rahmanillezine yemşune alel ardı hevnen ve iza hatabehumul cahilune kalu selama."},
  {id: 2919, sure_no: 64, ayet_no: 64, sure_adi: "Furkan", text: "وَالَّذ۪ينَ يَب۪يتُونَ لِرَبِّهِمْ سُجَّداً وَقِيَاماً", translation: "Onlar, Rabb'lerinin buyruklarına tabi olmada ve yerine getirmede önlem alırlar ve özenli davranırlar.", transliteration: "Vellezine yebitune li rabbihim succeden ve kıyama."},
  {id: 2920, sure_no: 65, ayet_no: 65, sure_adi: "Furkan", text: "وَالَّذ۪ينَ يَقُولُونَ رَبَّـنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَۗ اِنَّ عَذَابَهَا كَانَ غَرَاماًۗ", translation: "Onlar: \"Rabbimiz Cehennem azabını bizden uzaklaştır. Kuşkusuz onun azabı sürekli bir yok oluştur.\" derler.", transliteration: "Vellezine yekulune rabbenasrif anna azabe cehenneme inne azabeha kane garama."},
  {id: 2921, sure_no: 66, ayet_no: 66, sure_adi: "Furkan", text: "اِنَّهَا سَٓاءَتْ مُسْتَقَراًّ وَمُقَاماً", translation: "Kuşkusuz o, kötü bir konaklama yeri ve konaktır.", transliteration: "İnneha saet mustekarren ve mukama."},
  {id: 2922, sure_no: 67, ayet_no: 67, sure_adi: "Furkan", text: "وَالَّذ۪ينَ اِذَٓا اَنْفَقُوا لَمْ يُسْرِفُوا وَلَمْ يَقْتُرُوا وَكَانَ بَيْنَ ذٰلِكَ قَوَاماً", translation: "Onlar, infak ettikleri zaman israf da cimrilik de etmezler. Bu ikisi arasında bir denge kurarlar.", transliteration: "Vellezine iza enfeku lem yusrifu ve lem yakturu ve kane beyne zalike kavama."},
  {id: 2923, sure_no: 68, ayet_no: 68, sure_adi: "Furkan", text: "وَالَّذ۪ينَ لَا يَدْعُونَ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ وَلَا يَقْتُلُونَ النَّفْسَ الَّت۪ي حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّ وَلَا يَزْنُونَۚ وَمَنْ يَفْعَلْ ذٰلِكَ يَلْقَ اَثَاماًۙ", translation: "Onlar, Allah'la birlikte başka bir ilaha dua etmezler. Allah'ın haram kıldığı canı geçerli bir neden olmadıkça öldürmezler. Zina yapmazlar. Kim bunları yaparsa günah işlemiş olur.", transliteration: "Vellezine la yed'une meallahi ilahen ahara ve la yaktulunen nefselleti harremallahu illa bil hakkı ve la yeznun, ve men yef'al zalike yelka esama."},
  {id: 2924, sure_no: 69, ayet_no: 69, sure_adi: "Furkan", text: "يُضَاعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيٰمَةِ وَيَخْلُدْ ف۪يه۪۫ مُهَاناًۗ", translation: "Kıyamet Günü onun azabı katlanır ve orada sürekli horlanmış olarak kalır.", transliteration: "Yudaaf lehul azabu yevmel kıyameti ve yahlud fihi muhana."},
  {id: 2925, sure_no: 70, ayet_no: 70, sure_adi: "Furkan", text: "اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ عَمَلاً صَالِحاً فَاُو۬لٰٓئِكَ يُبَدِّلُ اللّٰهُ سَيِّـَٔاتِهِمْ حَسَنَاتٍۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً", translation: "Ancak tevbe eden, iman edip salihat yapanlar hariç. Allah onların kötülüklerini iyiliklere çevirir. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İlla men tabe ve amene ve amile amelen salihan fe ulaike yubeddilullahu seyyiatihim hasenat, ve kanallahu gafuren rahima."},
  {id: 2926, sure_no: 71, ayet_no: 71, sure_adi: "Furkan", text: "وَمَنْ تَابَ وَعَمِلَ صَالِحاً فَاِنَّهُ يَتُوبُ اِلَى اللّٰهِ مَتَاباً", translation: "Kim tevbe eder ve salihatı yaparsa, kuşkusuz o, bağışlanmış olarak Allah'a döner.", transliteration: "Ve men tabe ve amile salihan fe innehu yetubu ilallahi metaba."},
  {id: 2927, sure_no: 72, ayet_no: 72, sure_adi: "Furkan", text: "وَالَّذ۪ينَ لَا يَشْهَدُونَ الزُّورَۙ وَاِذَا مَرُّوا بِاللَّغْوِ مَرُّوا كِرَاماً", translation: "Onlar, yalan yere tanıklık etmezler. Boş ve anlamsız bir şeyle karşılaştıkları zaman, onurlu ve erdemlice oradan uzaklaşırlar.", transliteration: "Vellezine la yeşhedunez zura ve iza merru bil lagvi merru kirama."},
  {id: 2928, sure_no: 73, ayet_no: 73, sure_adi: "Furkan", text: "وَالَّذ۪ينَ اِذَا ذُكِّرُوا بِاٰيَاتِ رَبِّهِمْ لَمْ يَخِرُّوا عَلَـيْـهَا صُـماًّ وَعُمْيَـاناً", translation: "Onlara, Rabblerinin ayetleri hatırlatıldığı zaman, onlara karşı kör ve sağırmış gibi görmezden gelmezler.", transliteration: "Vellezine iza zukkiru bi ayati rabbihim lem yahırru aleyha summen ve umyanen."},
  {id: 2929, sure_no: 74, ayet_no: 74, sure_adi: "Furkan", text: "وَالَّذ۪ينَ يَقُولُونَ رَبَّـنَا هَبْ لَنَا مِنْ اَزْوَاجِنَا وَذُرِّيَّاتِنَا قُرَّةَ اَعْيُنٍ وَاجْعَلْنَا لِلْمُتَّق۪ينَ اِمَاماً", translation: "Ve onlar, \"Rabb'imiz! Eşlerimizden ve soyumuzdan bize göz aydınlığı bağışla. Ve bizi takva sahiplerine önder yap!\" derler.", transliteration: "Vellezine yekulune rabbena heb lena min ezvacina ve zurriyyatina kurrete a'yunin vec'alna lil muttekine imama."},
  {id: 2930, sure_no: 75, ayet_no: 75, sure_adi: "Furkan", text: "اُو۬لٰٓئِكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوا وَيُلَقَّوْنَ ف۪يهَا تَحِيَّةً وَسَلَاماًۙ", translation: "İşte onlar, sabretmelerine karşılık yüksek makamlarla ödüllendirilecekler. Orada saygınlık ve esenlik dilekleriyle karşılanacaklardır.", transliteration: "Ulaike yuczevnel gurfete bi ma saberu ve yulekkavne fiha tahiyyeten ve selama."},
  {id: 2931, sure_no: 76, ayet_no: 76, sure_adi: "Furkan", text: "خَالِد۪ينَ ف۪يهَاۜ حَسُنَتْ مُسْتَقَراًّ وَمُقَاماً", translation: "Sürekli orada kalacaklardır. Orası ne güzel bir konaklama yeri ve konaktır.", transliteration: "Halidine fiha, hasunet mustekarren ve mukama."},
  {id: 2932, sure_no: 77, ayet_no: 77, sure_adi: "Furkan", text: "قُلْ مَا يَعْبَؤُ۬ا بِكُمْ رَبّ۪ي لَوْلَا دُعَٓاؤُ۬كُمْۚ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُونُ لِزَاماً", translation: "De ki: \"Başkasına yalvarmanız olmazsa Rabb'im sizi ne yapsın?\" Oysaki siz yalanladınız. Bunun karşılığını yakında göreceksiniz.", transliteration: "Kul ma ya'beu bikum rabbi lev la duaukum, fe kad kezzebtum fe sevfe yekunu lizama."},
  {id: 2933, sure_no: 1, ayet_no: 1, sure_adi: "Şuara", text: "طٰسٓمٓۜ", translation: "Ta, Sin, Mim.", transliteration: "Ta, sin, mim."},
  {id: 2934, sure_no: 2, ayet_no: 2, sure_adi: "Şuara", text: "تِلْكَ اٰيَاتُ الْكِتَابِ الْمُب۪ينِ", translation: "Bunlar gerçeği apaçık ortaya koyan Kitap'ın ayetleridir.", transliteration: "Tilke ayatul kitabil mubin."},
  {id: 2935, sure_no: 3, ayet_no: 3, sure_adi: "Şuara", text: "لَعَلَّكَ بَاخِعٌ نَفْسَكَ اَلَّا يَكُونُوا مُؤْمِن۪ينَ", translation: "İman etmiyorlar diye, adeta kendini helak edeceksin.", transliteration: "Lealleke bahıun nefseke ella yekunu mu'minin."},
  {id: 2936, sure_no: 4, ayet_no: 4, sure_adi: "Şuara", text: "اِنْ نَشَأْ نُنَزِّلْ عَلَيْهِمْ مِنَ السَّمَٓاءِ اٰيَةً فَظَلَّتْ اَعْنَاقُهُمْ لَهَا خَاضِع۪ينَ", translation: "Eğer dileseydik gökten öyle bir ayet indirirdik ki hepsi ona boyun eğmek zorunda kalırdı.", transliteration: "İn neşe' nunezzil aleyhim mines semai ayeten fe zallet a'nakuhum leha hadıin."},
  {id: 2937, sure_no: 5, ayet_no: 5, sure_adi: "Şuara", text: "وَمَا يَأْت۪يهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمٰنِ مُحْدَثٍ اِلَّا كَانُوا عَنْهُ مُعْرِض۪ينَ", translation: "Rahman'dan kendilerine gelen söze bürünmüş her yeni öğütten yüz çevirdiler.", transliteration: "Ve ma ye'tihim min zikrin miner rahmani muhdesin illa kanu anhu mu'ridin."},
  {id: 2938, sure_no: 6, ayet_no: 6, sure_adi: "Şuara", text: "فَقَدْ كَذَّبُوا فَسَيَأْت۪يهِمْ اَنْبٰٓؤُ۬ا مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Sonra da kesin olarak yalanladılar. Fakat alay ettikleri şeyin haberleri yakında onlara gelecek.", transliteration: "Fe kad kezzebu fe seye'tihim enbau ma kanu bihi yestehziun."},
  {id: 2939, sure_no: 7, ayet_no: 7, sure_adi: "Şuara", text: "اَوَلَمْ يَرَوْا اِلَى الْاَرْضِ كَمْ اَنْبَتْنَا ف۪يهَا مِنْ كُلِّ زَوْجٍ كَر۪يمٍ", translation: "Onlar, yeryüzünü görmüyorlar mı? Her tür bitkiden çiftler olarak bol bol yetiştirdik.", transliteration: "E ve lem yerev ilel ardı kem enbetna fiha min kulli zevcin kerim."},
  {id: 2940, sure_no: 8, ayet_no: 8, sure_adi: "Şuara", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ  وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Bunda kesinlikle bir ayet vardır. Ancak onların çoğu inanmadı.", transliteration: "İnne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 2941, sure_no: 9, ayet_no: 9, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Rabb'in, Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 2942, sure_no: 10, ayet_no: 10, sure_adi: "Şuara", text: "وَاِذْ نَادٰى رَبُّكَ مُوسٰٓى اَنِ ائْتِ الْقَوْمَ الظَّالِم۪ينَۙ", translation: "Bir zamanlar Rabb'in, zalim halka gitmesi için Musa'ya seslenmişti.", transliteration: "Ve iz nada rabbuke musa eni'til kavmez zalimin."},
  {id: 2943, sure_no: 11, ayet_no: 11, sure_adi: "Şuara", text: "قَوْمَ فِرْعَوْنَۜ  اَلَا يَتَّقُونَ", translation: "\"Firavun halkı takva sahibi olmayacak mı?\"", transliteration: "Kavme fir'avn, e la yettekun."},
  {id: 2944, sure_no: 12, ayet_no: 12, sure_adi: "Şuara", text: "قَالَ رَبِّ اِنّ۪ٓي اَخَافُ اَنْ يُكَذِّبُونِۜ", translation: "Musa, \"Rabb'im! Beni yalanlamalarından korkuyorum.\" dedi.", transliteration: "Kale rabbi inni ehafu en yukezzibun."},
  {id: 2945, sure_no: 13, ayet_no: 13, sure_adi: "Şuara", text: "وَيَض۪يقُ صَدْر۪ي وَلَا يَنْطَلِقُ لِسَان۪ي فَاَرْسِلْ اِلٰى هٰرُونَ", translation: "\"Göğsüm daralır, dilim dönmez; onun için Harun'u gönder.\"", transliteration: "Ve yadiku sadri ve la yentaliku lisani fe ersil ila harun."},
  {id: 2946, sure_no: 14, ayet_no: 14, sure_adi: "Şuara", text: "وَلَهُمْ عَلَيَّ ذَنْبٌ فَاَخَافُ اَنْ يَقْتُلُونِۚ", translation: "\"Onlara göre ben suçluyum. Bu yüzden beni öldürmelerinden korkuyorum.\"", transliteration: "Ve lehum aleyye zenbun fe ehafu en yaktulun."},
  {id: 2947, sure_no: 15, ayet_no: 15, sure_adi: "Şuara", text: "قَالَ كَلَّاۚ فَاذْهَبَا بِاٰيَاتِنَٓا اِنَّا مَعَكُمْ مُسْتَمِعُونَ", translation: "\"Hayır, haydi ikiniz ayetlerimizle gidin! Kuşkusuz Biz, sizinle beraber işitenleriz.\" dedi.", transliteration: "Kale kella, fezheba bi ayatina inna meakum mustemiun."},
  {id: 2948, sure_no: 16, ayet_no: 16, sure_adi: "Şuara", text: "فَأْتِيَا فِرْعَوْنَ فَقُولَٓا اِنَّا رَسُولُ رَبِّ الْعَالَم۪ينَۙ", translation: "Haydi! Firavun'a gidin ve ona: \"Biz, alemlerin Rabb'inin Resulleriyiz.\" deyin.", transliteration: "Fe'tiya fir'avne fe kula inna resulu rabbil alemin."},
  {id: 2949, sure_no: 17, ayet_no: 17, sure_adi: "Şuara", text: "اَنْ اَرْسِلْ مَعَنَا بَن۪ٓي اِسْرَٓائ۪لَۜ", translation: "\"İsrailoğulları'nı bizimle beraber gönder!\"", transliteration: "En ersil meana beni israil."},
  {id: 2950, sure_no: 18, ayet_no: 18, sure_adi: "Şuara", text: "قَالَ اَلَمْ نُرَبِّكَ ف۪ينَا وَل۪يداً وَلَبِثْتَ ف۪ينَا مِنْ عُمُرِكَ سِن۪ينَ", translation: "Firavun: \"Çocukken, seni içimizde himaye edip yetiştirmedik mi? Yıllarca yanımızda kalmadın mı?\"", transliteration: "Kale e lem nurabbike fina veliden ve lebiste fina min umurike sinin."},
  {id: 2951, sure_no: 19, ayet_no: 19, sure_adi: "Şuara", text: "وَفَعَلْتَ فَعْلَتَكَ الَّت۪ي فَعَلْتَ وَاَنْتَ مِنَ الْكَافِر۪ينَ", translation: "\"Sen ise yapacağını yaptın. Sen kafirlerdensin.\"", transliteration: "Ve fealte fa'letekelleti fealte ve ente minel kafirin."},
  {id: 2952, sure_no: 20, ayet_no: 20, sure_adi: "Şuara", text: "قَالَ فَعَلْتُـهَٓا اِذاً وَاَنَا۬ مِنَ الضَّٓالّ۪ينَۜ", translation: "Musa: \"Ben o işi şaşkınlıkla yaptım.\" dedi.", transliteration: "Kale fealtuha izen ve ene mined dallin."},
  {id: 2953, sure_no: 21, ayet_no: 21, sure_adi: "Şuara", text: "فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ ل۪ي رَبّ۪ي حُكْماً وَجَعَلَن۪ي مِنَ الْمُرْسَل۪ينَ", translation: "\"Sizden korktuğum için de hemen kaçtım. Sonra Rabb'im bana hikmet bağışladı ve beni Resullerden kıldı.\"", transliteration: "Fe ferartu minkum lemma hıftukum fe vehebe li rabbi hukmen ve cealeni minel murselin."},
  {id: 2954, sure_no: 22, ayet_no: 22, sure_adi: "Şuara", text: "وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ اَنْ عَبَّدْتَ بَن۪ٓي اِسْرَٓائ۪لَۜ", translation: "İsrailoğulları'nı kendine kul edinmiş olmayı bir nimetmiş gibi başıma kakıyorsun.\" dedi.", transliteration: "Ve tilke ni'metun temunnuha aleyye en abbedte beni israil."},
  {id: 2955, sure_no: 23, ayet_no: 23, sure_adi: "Şuara", text: "قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعَالَم۪ينَ", translation: "Firavun: \"Alemlerin Rabb''i de nedir?\" dedi.", transliteration: "Kale fir'avnu ve ma rabbul alemin."},
  {id: 2956, sure_no: 24, ayet_no: 24, sure_adi: "Şuara", text: "قَالَ رَبُّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۜ اِنْ كُنْتُمْ مُوقِن۪ينَ", translation: "Musa: \"Eğer bütün gerçekliği ile doğruyu bilmek istiyorsanız, bilesiniz ki O, göklerin, yerin ve bu ikisi arasında bulunanların Rabb'idir.\" dedi.", transliteration: "Kale rabbus semavati vel ardı ve ma beynehuma, in kuntum mukınin."},
  {id: 2957, sure_no: 25, ayet_no: 25, sure_adi: "Şuara", text: "قَالَ لِمَنْ حَوْلَـهُٓ  اَلَا تَسْتَمِعُونَ", translation: "Firavun, etrafındakilere: \"Duymuyor musunuz?\" dedi.", transliteration: "Kale li men havlehu e la testemiun."},
  {id: 2958, sure_no: 26, ayet_no: 26, sure_adi: "Şuara", text: "قَالَ رَبُّكُمْ وَرَبُّ اٰبَٓائِكُمُ الْاَوَّل۪ينَ", translation: "Musa: \"Sizin de Rabb'iniz, sizden önceki atalarınızın da Rabb'idir\" dedi.", transliteration: "Kale rabbukum ve rabbu abaikumul evvelin."},
  {id: 2959, sure_no: 27, ayet_no: 27, sure_adi: "Şuara", text: "قَالَ اِنَّ رَسُولَكُمُ الَّـذ۪ٓي اُرْسِلَ اِلَيْكُمْ لَمَجْنُونٌ", translation: "Firavun: \"Size gönderilmiş olan Resul'ünüz, gerçekten mecnundur.\" dedi.", transliteration: "Kale inne resulekumullezi ursile ileykum le mecnun."},
  {id: 2960, sure_no: 28, ayet_no: 28, sure_adi: "Şuara", text: "قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَاۜ اِنْ كُنْتُمْ تَعْقِلُونَ", translation: "Musa: \"Eğer aklınızı kullanırsanız anlayacaksınız ki O, doğunun, batının ve ikisinin arasındakilerin Rabb'idir.\" dedi.", transliteration: "Kale rabbul meşrıkı vel magribi ve ma beynehuma, in kuntum ta'kılun."},
  {id: 2961, sure_no: 29, ayet_no: 29, sure_adi: "Şuara", text: "قَالَ لَئِنِ اتَّخَذْتَ اِلٰهاً غَيْر۪ي لَاَجْعَلَنَّكَ مِنَ الْمَسْجُون۪ينَ", translation: "Firavun: \"Benden başka bir ilah edinirsen, kesinlikle seni zindana atarım.\" dedi.", transliteration: "Kale leinittehazte ilahen gayri le ec'alenneke minel mescunin."},
  {id: 2962, sure_no: 30, ayet_no: 30, sure_adi: "Şuara", text: "قَالَ اَوَلَوْ جِئْتُكَ بِشَيْءٍ مُب۪ينٍ", translation: "Musa: \"Sana apaçık bir şey getirsem de mi?\" dedi.", transliteration: "Kale e ve lev ci'tuke bi şey'in mubin."},
  {id: 2963, sure_no: 31, ayet_no: 31, sure_adi: "Şuara", text: "قَالَ فَأْتِ بِه۪ٓ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "Firavun: \"Öyleyse haydi getir onu. Eğer doğru söyleyenlerdensen.\" dedi.", transliteration: "Kale fe'ti bihi in kunte mines sadikin."},
  {id: 2964, sure_no: 32, ayet_no: 32, sure_adi: "Şuara", text: "فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُب۪ينٌۚ", translation: "Bunun üzerine Musa, asasını bırakıverdi; bir de ne görsünler, apaçık büyük bir yılan.", transliteration: "Fe elka asahu fe iza hiye su'banun mubin."},
  {id: 2965, sure_no: 33, ayet_no: 33, sure_adi: "Şuara", text: "وَنَزَعَ يَدَهُ فَاِذَا هِيَ بَيْضَٓاءُ لِلنَّاظِر۪ينَ۟", translation: "Ve elini çıkardı. Bakanlar ne görsün; beyaz bir el.", transliteration: "Ve nezea yedehu fe iza hiye beydau lin nazırin."},
  {id: 2966, sure_no: 34, ayet_no: 34, sure_adi: "Şuara", text: "قَالَ لِلْمَلَأِ حَوْلَـهُٓ اِنَّ هٰذَا لَسَاحِرٌ عَل۪يمٌۙ", translation: "Firavun, yanındaki melelere: \"Bu gerçekten çok bilgili bir sihirbazdır.\" dedi.", transliteration: "Kale lil melei havlehu inne haza le sahırun alim."},
  {id: 2967, sure_no: 35, ayet_no: 35, sure_adi: "Şuara", text: "يُر۪يدُ اَنْ يُخْرِجَكُمْ مِنْ اَرْضِكُمْ بِسِحْرِه۪ۗ فَمَاذَا تَأْمُرُونَ", translation: "\"Sizi sihri ile yurdunuzdan çıkarmak istiyor. Ne yapmamı istiyorsunuz?\" dedi.", transliteration: "Yuridu en yuhricekum min ardıkum bi sıhrihi fe maza te'murun."},
  {id: 2968, sure_no: 36, ayet_no: 36, sure_adi: "Şuara", text: "قَالُٓوا اَرْجِهْ وَاَخَاهُ وَابْعَثْ فِي الْمَدَٓائِنِ حَاشِر۪ينَۙ", translation: "\"Onu ve kardeşini alıkoy. Şehirlere sihirbazları toplamaya birilerini gönder.\" dediler.", transliteration: "Kalu ercih ve ehahu veb'as fil medaini haşirin."},
  {id: 2969, sure_no: 37, ayet_no: 37, sure_adi: "Şuara", text: "يَأْتُوكَ بِكُلِّ سَحَّارٍ عَل۪يمٍ", translation: "\"Bütün bilgili sihirbazları sana getirsinler.\"", transliteration: "Ye'tuke bi kulli sehharin alim."},
  {id: 2970, sure_no: 38, ayet_no: 38, sure_adi: "Şuara", text: "فَجُمِعَ السَّحَرَةُ لِم۪يقَاتِ يَوْمٍ مَعْلُومٍۙ", translation: "Böylece sihirbazlar, belirlenen bir günün tayin edilen vaktinde bir araya getirildiler.", transliteration: "Fe cumias seharatu li mikati yevmin ma'lum."},
  {id: 2971, sure_no: 39, ayet_no: 39, sure_adi: "Şuara", text: "وَق۪يلَ لِلنَّاسِ هَلْ اَنْتُمْ مُجْتَمِعُونَۙ", translation: "İnsanlara: \"Siz de toplananlar olun.\" denildi.", transliteration: "Ve kile lin nasi hel entum muctemiun."},
  {id: 2972, sure_no: 40, ayet_no: 40, sure_adi: "Şuara", text: "لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ اِنْ كَانُوا هُمُ الْغَالِب۪ينَ", translation: "\"Umarız sihirbazlar galip gelir; o zaman biz de onlara tabi oluruz.\"", transliteration: "Leallena nettebius seharate in kanu humul galibin."},
  {id: 2973, sure_no: 41, ayet_no: 41, sure_adi: "Şuara", text: "فَلَمَّا جَٓاءَ السَّحَرَةُ قَالُوا لِفِرْعَوْنَ اَئِنَّ لَنَا لَاَجْراً اِنْ كُنَّا نَحْنُ الْغَالِب۪ينَ", translation: "Sihirbazlar Firavun'a geldiklerinde: \"Eğer galip gelirsek bize bir ödül var mı?\" dediler.", transliteration: "Fe lemma caes seharatu kalu li fir'avne e inne lena le ecran in kunna nahnul galibin."},
  {id: 2974, sure_no: 42, ayet_no: 42, sure_adi: "Şuara", text: "قَالَ نَعَمْ وَاِنَّكُمْ اِذاً لَمِنَ الْمُقَرَّب۪ينَ", translation: "\"Evet; o zaman sizi kesinlikle himayeme alırım.\" dedi.", transliteration: "Kale neam ve innekum izen le minel mukarrabin."},
  {id: 2975, sure_no: 43, ayet_no: 43, sure_adi: "Şuara", text: "قَالَ لَهُمْ مُوسٰٓى اَلْقُوا مَٓا اَنْتُمْ مُلْقُونَ", translation: "Musa onlara: \"Atacağınız şeyi atın.\" dedi.", transliteration: "Kale lehum musa elku ma entum mulkun."},
  {id: 2976, sure_no: 44, ayet_no: 44, sure_adi: "Şuara", text: "فَاَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوا بِعِزَّةِ فِرْعَوْنَ اِنَّا لَنَحْنُ الْغَالِبُونَ", translation: "Bunun üzerine iplerini ve asalarını attılar. \"Firavun'un onuru adına elbette galip gelecekler bizler olacağız.\" dediler.", transliteration: "Fe elkav hıbalehum ve ısıyyehum ve kalu bi izzeti fir'avne inna le nahnul galibun."},
  {id: 2977, sure_no: 45, ayet_no: 45, sure_adi: "Şuara", text: "فَاَلْقٰى مُوسٰى عَصَاهُ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَۚ", translation: "Sonra Musa asasını attı. Birde ne görsünler! Uydurdukları şeyleri yutuyor.", transliteration: "Fe elka musa asahu fe iza hiye telkafu ma ye'fikun."},
  {id: 2978, sure_no: 46, ayet_no: 46, sure_adi: "Şuara", text: "فَاُلْقِيَ السَّحَرَةُ سَاجِد۪ينَۙ", translation: "Sihirbazlar hemen teslimiyetlerini ilan ettiler.", transliteration: "Fe ulkıyes seharatu sacidin."},
  {id: 2979, sure_no: 47, ayet_no: 47, sure_adi: "Şuara", text: "قَالُٓوا اٰمَنَّا بِرَبِّ الْعَالَم۪ينَۙ", translation: "\"Alemlerin Rabb'ine inandık.\" dediler.", transliteration: "Kalu amenna bi rabbil alemin."},
  {id: 2980, sure_no: 48, ayet_no: 48, sure_adi: "Şuara", text: "رَبِّ مُوسٰى وَهٰرُونَ", translation: "\"Musa ve Harun'un Rabb'ine.\"", transliteration: "Rabbi musa ve harun."},
  {id: 2981, sure_no: 49, ayet_no: 49, sure_adi: "Şuara", text: "قَالَ اٰمَنْتُمْ لَهُ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّهُ لَكَب۪يرُكُمُ الَّذ۪ي عَلَّمَكُمُ السِّحْرَۚ  فَلَسَوْفَ تَعْلَمُونَۜ  لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِنْ خِلَافٍ وَلَاُصَلِّبَنَّكُمْ اَجْمَع۪ينَ", translation: "Firavun, \"Ben izin vermeden O'na iman mı ettiniz? Kuşkusuz o, size sihir öğreten hocanızdır. Şunu kesin olarak bilin ki ellerinizi ve ayaklarınızı çaprazlama kestireceğim ve kesinlikle hepinizi astıracağım!\" dedi.", transliteration: "Kale amentum lehu kable en azene lekum, innehu le kebirukumullezi allemekumus sıhr, fe le sevfe ta'lemun, le ukattıanne eydiyekum ve erculekum min hılafin ve le usallibennekum ecmain."},
  {id: 2982, sure_no: 50, ayet_no: 50, sure_adi: "Şuara", text: "قَالُوا لَا ضَيْرَۘ اِنَّٓا اِلٰى رَبِّنَا مُنْقَلِبُونَۚ", translation: "\"Önemli değil. Nasıl olsa Rabb'imize döneceğiz.\" dediler.", transliteration: "Kalu la dayra inna ila rabbina munkalibun."},
  {id: 2983, sure_no: 51, ayet_no: 51, sure_adi: "Şuara", text: "اِنَّا نَطْمَعُ اَنْ يَغْفِرَ لَنَا رَبُّنَا خَطَايَانَٓا اَنْ كُنَّٓا اَوَّلَ الْمُؤْمِن۪ينَۜ۟", translation: "\"İlk Mü'minler olduğumuz için, Rabb'imizin yanlışlarımızı bağışlayacağını ümit ediyoruz.\" dediler.", transliteration: "İnna natmeu en yagfira lena rabbuna hatayana en kunna evvelel mu'minin."},
  {id: 2984, sure_no: 52, ayet_no: 52, sure_adi: "Şuara", text: "وَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنْ اَسْرِ بِعِبَاد۪ٓي اِنَّكُمْ مُتَّبَعُونَ", translation: "Musa'ya: \"Kullarım ile gece yola çık. Kesinlikle siz takip edileceksiniz.\" diye vahyettik.", transliteration: "Ve evhayna ila musa en esri bi ıbadi innekum muttebeun."},
  {id: 2985, sure_no: 53, ayet_no: 53, sure_adi: "Şuara", text: "فَاَرْسَلَ فِرْعَوْنُ فِي الْمَدَٓائِنِ حَاشِر۪ينَۚ", translation: "Derken Firavun, şehirlere toplayıcılar gönderdi.", transliteration: "Fe ersele fir'avnu fil medaini haşirin."},
  {id: 2986, sure_no: 54, ayet_no: 54, sure_adi: "Şuara", text: "اِنَّ هٰٓؤُ۬لَٓاءِ لَشِرْذِمَةٌ قَل۪يلُونَۙ", translation: "\"Bunlar önemsiz bir topluluktur.\"", transliteration: "İnne haulai le şirzimetun kalilun."},
  {id: 2987, sure_no: 55, ayet_no: 55, sure_adi: "Şuara", text: "وَاِنَّهُمْ لَنَا لَـغَٓائِظُونَۙ", translation: "\"Ve bize karşı kin ve nefret duyuyorlar.\"", transliteration: "Ve innehum lena le gaizun."},
  {id: 2988, sure_no: 56, ayet_no: 56, sure_adi: "Şuara", text: "وَاِنَّا لَجَم۪يعٌ حَاذِرُونَۜ", translation: "\"Biz ise hazırlıklı bir topluluğuz.\"", transliteration: "Ve inna le cemiun hazirun."},
  {id: 2989, sure_no: 57, ayet_no: 57, sure_adi: "Şuara", text: "فَاَخْرَجْنَاهُمْ مِنْ جَنَّاتٍ وَعُيُونٍۙ", translation: "Derken onları cennetlerden ve çeşmelerden çıkardık.", transliteration: "Fe ahracnahum min cennatin ve uyun."},
  {id: 2990, sure_no: 58, ayet_no: 58, sure_adi: "Şuara", text: "وَكُنُوزٍ وَمَقَامٍ كَر۪يمٍۙ", translation: "Ve hazinelerden ve kerim makamlardan.", transliteration: "Ve kunuzin ve makamin kerim."},
  {id: 2991, sure_no: 59, ayet_no: 59, sure_adi: "Şuara", text: "كَذٰلِكَۜ وَاَوْرَثْنَاهَا بَن۪ٓي اِسْرَٓائ۪لَۚ", translation: "İşte böylece onlara İsrailoğulları'nı varis kıldık.", transliteration: "Kezalik, ve evresnaha beni israil."},
  {id: 2992, sure_no: 60, ayet_no: 60, sure_adi: "Şuara", text: "فَاَتْبَعُوهُمْ مُشْرِق۪ينَ", translation: "Gün doğarken onların ardına düştüler.", transliteration: "Fe etbeuhum muşrikin."},
  {id: 2993, sure_no: 61, ayet_no: 61, sure_adi: "Şuara", text: "فَلَمَّا تَـرَٓاءَ الْجَمْعَانِ قَالَ اَصْحَابُ مُوسٰٓى اِنَّا لَمُدْرَكُونَۚ", translation: "İki topluluk birbirini görünce, Musa'nın taraftarları, \"İşte şimdi yakalandık.\" dediler.", transliteration: "Fe lemma terael cem'ani kale ashabu musa inna le mudrakun."},
  {id: 2994, sure_no: 62, ayet_no: 62, sure_adi: "Şuara", text: "قَالَ كَلَّاۚ اِنَّ مَعِيَ رَبّ۪ي سَيَهْد۪ينِ", translation: "Musa: \"Hayır, endişelenmeyin! Kuşkusuz Rabb'im benimledir. Bana yol gösterecektir.\" dedi.", transliteration: "Kale kella, inne maiye rabbi seyehdin."},
  {id: 2995, sure_no: 63, ayet_no: 63, sure_adi: "Şuara", text: "فَاَوْحَيْنَٓا اِلٰى مُوسٰٓى اَنِ اضْرِبْ بِعَصَاكَ الْبَحْرَۜ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظ۪يمِۚ", translation: "Musa'ya, \"Asanı denize vur.\" diye vahyettik. Deniz hemen yarıldı. Her bir parçası büyük, yüksek bir dağ gibiydi.", transliteration: "Fe evhayna ila musa enıdrib bi asakel bahr, fenfeleka fe kane kullu firkın ket tavdil azim."},
  {id: 2996, sure_no: 64, ayet_no: 64, sure_adi: "Şuara", text: "وَاَزْلَفْنَا ثَمَّ الْاٰخَر۪ينَۚ", translation: "Diğerlerini de oraya yaklaştırdık.", transliteration: "Ve ezlefna semmel aharin."},
  {id: 2997, sure_no: 65, ayet_no: 65, sure_adi: "Şuara", text: "وَاَنْجَيْنَا مُوسٰى وَمَنْ مَعَهُٓ اَجْمَع۪ينَۚ", translation: "Musa ve beraberinde olanların hepsini kurtardık.", transliteration: "Ve enceyna musa ve men meahu ecmain."},
  {id: 2998, sure_no: 66, ayet_no: 66, sure_adi: "Şuara", text: "ثُمَّ اَغْرَقْنَا الْاٰخَر۪ينَۜ", translation: "Sonra da diğerlerini boğduk.", transliteration: "Summe agraknel aharin."},
  {id: 2999, sure_no: 67, ayet_no: 67, sure_adi: "Şuara", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Kuşkusuz bunda bir ayet vardır. Buna rağmen onların çokları inanmamaktadır.", transliteration: "İnne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 3000, sure_no: 68, ayet_no: 68, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Kuşkusuz Rabb'in, Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 3001, sure_no: 69, ayet_no: 69, sure_adi: "Şuara", text: "وَاتْلُ عَلَيْهِمْ نَبَاَ اِبْرٰه۪يمَۢ", translation: "Onlara İbrahim'in haberini anlat.", transliteration: "Vetlu aleyhim nebee ibrahim."},
  {id: 3002, sure_no: 70, ayet_no: 70, sure_adi: "Şuara", text: "اِذْ قَالَ لِاَب۪يهِ وَقَوْمِه۪ مَا تَعْبُدُونَ", translation: "Babasına ve halkına; \"Siz neye kulluk ediyorsunuz?\" demişti.", transliteration: "İz kale li ebihi ve kavmihi ma ta'budun."},
  {id: 3003, sure_no: 71, ayet_no: 71, sure_adi: "Şuara", text: "قَالُوا نَعْبُدُ اَصْنَاماً فَنَظَلُّ لَهَا عَاكِف۪ينَ", translation: "\"Putlara kulluk ediyoruz. Kendimizi onlara adamaktan vazgeçmeyeceğiz.\" dediler.", transliteration: "Kalu na'budu asnamen fe nezallu leha akifin."},
  {id: 3004, sure_no: 72, ayet_no: 72, sure_adi: "Şuara", text: "قَالَ هَلْ يَسْمَعُونَكُمْ اِذْ تَدْعُونَۙ", translation: "İbrahim: \"Onlara dua ettiğiniz zaman sizi işitiyorlar mı?\" dedi.", transliteration: "Kale hel yesmeunekum iz ted'un."},
  {id: 3005, sure_no: 73, ayet_no: 73, sure_adi: "Şuara", text: "اَوْ يَنْفَعُونَكُمْ اَوْ يَضُرُّونَ", translation: "\"Veya size bir yararları ya da zararları oluyor mu?\"", transliteration: "Ev yenfeunekum ev yedurrun."},
  {id: 3006, sure_no: 74, ayet_no: 74, sure_adi: "Şuara", text: "قَالُوا بَلْ وَجَدْنَٓا اٰبَٓاءَنَا كَذٰلِكَ يَفْعَلُونَ", translation: "Onlar: \"Ama atalarımızı böyle yaparlarken bulduk.\" dediler.", transliteration: "Kalu bel vecedna abaena kezalike yef'alun."},
  {id: 3007, sure_no: 75, ayet_no: 75, sure_adi: "Şuara", text: "قَالَ اَفَرَاَيْتُمْ مَا كُنْتُمْ تَعْبُدُونَۙ", translation: "İbrahim: \"Peki neye kulluk ettiğinizi hiç düşünüyor musunuz?\" dedi.", transliteration: "Kale e fe raeytum ma kuntum ta'budun."},
  {id: 3008, sure_no: 76, ayet_no: 76, sure_adi: "Şuara", text: "اَنْتُمْ وَاٰبَٓاؤُ۬كُمُ الْاَقْدَمُونَ", translation: "\"Siz ve geçmiş atalarınız.\"", transliteration: "Entum ve abaukumul akdemun."},
  {id: 3009, sure_no: 77, ayet_no: 77, sure_adi: "Şuara", text: "فَاِنَّهُمْ عَدُوٌّ ل۪ٓي اِلَّا رَبَّ الْعَالَم۪ينَۙ", translation: "\"Yalnızca alemlerin Rabb'i (dostumdur). O'nun dışındakilerin hepsi benim düşmanımdır.\"", transliteration: "Fe innehum aduvvun li illa rabbel alemin."},
  {id: 3010, sure_no: 78, ayet_no: 78, sure_adi: "Şuara", text: "اَلَّذ۪ي خَلَقَن۪ي فَهُوَ يَهْد۪ينِۙ", translation: "\"Beni yaratan ve yol gösteren O'dur.\"", transliteration: "Ellezi halakani fe huve yehdin."},
  {id: 3011, sure_no: 79, ayet_no: 79, sure_adi: "Şuara", text: "وَالَّذ۪ي هُوَ يُطْعِمُن۪ي وَيَسْق۪ينِۙ", translation: "\"O'dur beni yediren ve içiren.\"", transliteration: "Vellezi huve yut'ımuni ve yeskin."},
  {id: 3012, sure_no: 80, ayet_no: 80, sure_adi: "Şuara", text: "وَاِذَا مَرِضْتُ فَهُوَ يَشْف۪ينِۖ", translation: "\"Hastalandığım zaman, O'dur bana şifa veren.\"", transliteration: "Ve iza maridtu fe huve yeşfin."},
  {id: 3013, sure_no: 81, ayet_no: 81, sure_adi: "Şuara", text: "وَالَّذ۪ي يُم۪يتُن۪ي ثُمَّ يُحْي۪ينِۙ", translation: "\"Beni öldürecek ve tekrar diriltecek olan O'dur.\"", transliteration: "Vellezi yumituni summe yuhyin."},
  {id: 3014, sure_no: 82, ayet_no: 82, sure_adi: "Şuara", text: "وَالَّـذ۪ٓي اَطْمَعُ اَنْ يَغْفِرَ ل۪ي خَط۪ٓيـَٔت۪ي يَوْمَ الدّ۪ينِۜ", translation: "\"Ve Din Günü yanlışımı bağışlayacağını umduğum O'dur.\"", transliteration: "Vellezi atmeu en yagfira li hatieti yevmed din."},
  {id: 3015, sure_no: 83, ayet_no: 83, sure_adi: "Şuara", text: "رَبِّ هَبْ ل۪ي حُكْماً وَاَلْحِقْن۪ي بِالصَّالِح۪ينَۙ", translation: "\"Rabb'im bana karşılıksız hüküm ver ve beni salihlerin arasına dahil et.\"", transliteration: "Rabbi heb li hukmen ve elhıkni bis salihin."},
  {id: 3016, sure_no: 84, ayet_no: 84, sure_adi: "Şuara", text: "وَاجْعَلْ ل۪ي لِسَانَ صِدْقٍ فِي الْاٰخِر۪ينَۙ", translation: "\"Ve sonrakiler arasında güzel bir ün ile anılmamı nasip et.\"", transliteration: "Vec'al li lisane sıdkın fil ahırin."},
  {id: 3017, sure_no: 85, ayet_no: 85, sure_adi: "Şuara", text: "وَاجْعَلْن۪ي مِنْ وَرَثَةِ جَنَّةِ النَّع۪يمِۙ", translation: "\"Ve beni nimeti bol Cennet'in mirasçılarından kıl.\"", transliteration: "Vec'alni min veraseti cennetin naim."},
  {id: 3018, sure_no: 86, ayet_no: 86, sure_adi: "Şuara", text: "وَاغْفِرْ  لِاَب۪ٓي اِنَّهُ  كَانَ مِنَ الضَّٓالّ۪ينَۙ", translation: "\"Babamı bağışla, o sapkınlardandır.\"", transliteration: "Vagfir li ebi innehu kane mined dallin."},
  {id: 3019, sure_no: 87, ayet_no: 87, sure_adi: "Şuara", text: "وَلَا تُخْزِن۪ي يَوْمَ يُبْعَثُونَۙ", translation: "\"Yeniden dirilme gününde beni utandırma.\"", transliteration: "Ve la tuhzini yevme yub'asun."},
  {id: 3020, sure_no: 88, ayet_no: 88, sure_adi: "Şuara", text: "يَوْمَ لَا يَنْفَعُ مَالٌ وَلَا بَنُونَۙ", translation: "\"Evladın ve malın yarar sağlamadığı gün.\"", transliteration: "Yevme la yenfau malun ve la benun."},
  {id: 3021, sure_no: 89, ayet_no: 89, sure_adi: "Şuara", text: "اِلَّا مَنْ اَتَى اللّٰهَ بِقَلْبٍ سَل۪يمٍۜ", translation: "\"Allah'a selim bir kalple gelenler hariç.\"", transliteration: "İlla men etallahe bi kalbin selim."},
  {id: 3022, sure_no: 90, ayet_no: 90, sure_adi: "Şuara", text: "وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّق۪ينَۙ", translation: "Ve Cennet, takva sahipleri için yaklaştırılır.", transliteration: "Ve uzlifetil cennetu lil muttekin."},
  {id: 3023, sure_no: 91, ayet_no: 91, sure_adi: "Şuara", text: "وَبُرِّزَتِ الْجَح۪يمُ لِلْغَاو۪ينَۙ", translation: "Ve Cehennem azgınların karşısına çıkarılır.", transliteration: "Ve burrizetil cahimu lil gavin."},
  {id: 3024, sure_no: 92, ayet_no: 92, sure_adi: "Şuara", text: "وَق۪يلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تَعْبُدُونَۙ", translation: "Ve onlara: \"Kulluk ettikleriniz nerede?\" denilir.", transliteration: "Ve kile lehum eyne ma kuntum ta'budun."},
  {id: 3025, sure_no: 93, ayet_no: 93, sure_adi: "Şuara", text: "مِنْ دُونِ اللّٰهِۜ هَلْ يَنْصُرُونَكُمْ اَوْ يَنْتَصِرُونَۜ", translation: "\"Allah'tan başka size yardım edebilecek var mı? Veya kendilerine bir yararları olabilir mi?\"", transliteration: "Min dunillah, hel yensurunekum ev yentesırun."},
  {id: 3026, sure_no: 94, ayet_no: 94, sure_adi: "Şuara", text: "فَكُبْكِبُوا ف۪يهَا هُمْ وَالْغَاوُ۫نَۙ", translation: "Arkasından onlar ve azgınlar onun içine tepetaklak atılacaklar.", transliteration: "Fe kubkıbu fiha hum vel gavun."},
  {id: 3027, sure_no: 95, ayet_no: 95, sure_adi: "Şuara", text: "وَجُنُودُ اِبْل۪يسَ اَجْمَعُونَۜ", translation: "Ve İblisin bütün askerleri.", transliteration: "Ve cunudu iblise ecmeun."},
  {id: 3028, sure_no: 96, ayet_no: 96, sure_adi: "Şuara", text: "قَالُوا وَهُمْ ف۪يهَا يَخْتَصِمُونَۙ", translation: "Onlar, orada birbirleriyle çekişerek diyecekler ki:", transliteration: "Kalu ve hum fiha yahtesımun."},
  {id: 3029, sure_no: 97, ayet_no: 97, sure_adi: "Şuara", text: "تَاللّٰهِ اِنْ كُنَّا لَف۪ي ضَلَالٍ مُب۪ينٍۙ", translation: "\"Allah'a yemin olsun ki, biz apaçık bir sapkınlık içindeymişiz.\"", transliteration: "Tallahi in kunna le fi dalalin mubin."},
  {id: 3030, sure_no: 98, ayet_no: 98, sure_adi: "Şuara", text: "اِذْ نُسَوّ۪يكُمْ بِرَبِّ الْعَالَم۪ينَ", translation: "\"Çünkü sizi Alemlerin Rabb'i ile bir tutuyorduk.\"", transliteration: "İz nusevvikum bi rabbil alemin."},
  {id: 3031, sure_no: 99, ayet_no: 99, sure_adi: "Şuara", text: "وَمَٓا اَضَلَّـنَٓا اِلَّا الْمُجْرِمُونَ", translation: "\"Ve bizi hep o mücrimler saptırdı.\"", transliteration: "Ve ma edallena illel mucrimun."},
  {id: 3032, sure_no: 100, ayet_no: 100, sure_adi: "Şuara", text: "فَمَا لَنَا مِنْ شَافِع۪ينَۙ", translation: "\"Artık şefaat edecek kimsemiz de yok.\"", transliteration: "Fe ma lena min şafiin."},
  {id: 3033, sure_no: 101, ayet_no: 101, sure_adi: "Şuara", text: "وَلَا صَد۪يقٍ حَم۪يمٍ", translation: "\"Gerçek bir dostumuz da.\"", transliteration: "Ve la sadikın hamim."},
  {id: 3034, sure_no: 102, ayet_no: 102, sure_adi: "Şuara", text: "فَلَوْ اَنَّ لَنَا كَرَّةً فَنَكُونَ مِنَ الْمُؤْمِن۪ينَ", translation: "\"Keşke bizim için geri dönüş olsaydı da biz de iman edenlerden olsaydık.\"", transliteration: "Fe lev enne lena kerraten fe nekune minel mu'minin."},
  {id: 3035, sure_no: 103, ayet_no: 103, sure_adi: "Şuara", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Bunda bir ayet vardır. Buna rağmen onların çoğu inanmamaktadırlar.", transliteration: "İnne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 3036, sure_no: 104, ayet_no: 104, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Kuşkusuz senin Rabb'in Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 3037, sure_no: 105, ayet_no: 105, sure_adi: "Şuara", text: "كَذَّبَتْ قَوْمُ نُوحٍۨ الْمُرْسَل۪ينَۚ", translation: "Nuh halkı gönderilmişleri yalanladı.", transliteration: "Kezzebet kavmu nuhınil murselin."},
  {id: 3038, sure_no: 106, ayet_no: 106, sure_adi: "Şuara", text: "اِذْ قَالَ لَهُمْ اَخُوهُمْ نُوحٌ اَلَا تَتَّقُونَۚ", translation: "Kardeşleri Nuh onlara: \"Takva sahibi olmayacak mısınız?\" demişti.", transliteration: "İz kale lehum ehuhum nuhun e la tettekun."},
  {id: 3039, sure_no: 107, ayet_no: 107, sure_adi: "Şuara", text: "اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ", translation: "\"Sizin için güvenilir bir Resul'üm.\"", transliteration: "İnni lekum resulun emin."},
  {id: 3040, sure_no: 108, ayet_no: 108, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ", translation: "\"Öyleyse Allah için takva sahibi olun ve bana itaat edin.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3041, sure_no: 109, ayet_no: 109, sure_adi: "Şuara", text: "وَمَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعَالَم۪ينَۚ", translation: "\"Buna karşılık sizden bir ücret istemiyorum. Benim ücretim yalnızca alemlerin Rabb'ine aittir.\"", transliteration: "Ve ma es'elukum aleyhi min ecr, in ecriye illa ala rabbil alemin."},
  {id: 3042, sure_no: 110, ayet_no: 110, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۜ", translation: "\"Öyleyse Allah için takva sahibi olun ve bana tabi olun.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3043, sure_no: 111, ayet_no: 111, sure_adi: "Şuara", text: "قَالُٓوا اَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْاَرْذَلُونَۜ", translation: "\"Toplumun en yoksun kesimi sana uymaktayken, biz kendimizi onlarla bir tutup sana inanır mıyız?\" dediler.", transliteration: "Kalu e nu'minu leke vettebeakel erzelun."},
  {id: 3044, sure_no: 112, ayet_no: 112, sure_adi: "Şuara", text: "قَالَ وَمَا عِلْم۪ي بِمَا كَانُوا يَعْمَلُونَۚ", translation: "\"Onların tercihlerini belirleyici ben değilim.\" dedi.", transliteration: "Kale ve ma ilmi bima kanu ya'melun."},
  {id: 3045, sure_no: 113, ayet_no: 113, sure_adi: "Şuara", text: "اِنْ حِسَابُهُمْ اِلَّا عَلٰى رَبّ۪ي لَوْ تَشْعُرُونَۚ", translation: "\"Onların hesabı ancak Rabb'ime aittir. Eğer düşünürseniz gerçeği anlarsınız.\"", transliteration: "İn hısabuhum illa ala rabbi lev teş'urun."},
  {id: 3046, sure_no: 114, ayet_no: 114, sure_adi: "Şuara", text: "وَمَٓا اَنَا۬ بِطَارِدِ الْمُؤْمِن۪ينَۚ", translation: "\"İnananları kovacak değilim.\"", transliteration: "Ve ma ene bi taridil mu'minin."},
  {id: 3047, sure_no: 115, ayet_no: 115, sure_adi: "Şuara", text: "اِنْ اَنَا۬ اِلَّا نَذ۪يرٌ مُب۪ينٌۜ", translation: "\"Ben, yalnızca apaçık bir uyarıcıyım.\"", transliteration: "İn ene illa nezirun mubin."},
  {id: 3048, sure_no: 116, ayet_no: 116, sure_adi: "Şuara", text: "قَالُوا لَئِنْ لَمْ تَنْتَهِ۬ يَا نُوحُ لَتَكُونَنَّ مِنَ الْمَرْجُوم۪ينَۜ", translation: "\"Ey Nuh! Eğer kesin olarak vazgeçmezsen, iyi bil ki, kesinlikle taşlananlardan olursun!\" dediler.", transliteration: "Kalu le in lem tentehi ya nuhule tekunenne minel mercumin."},
  {id: 3049, sure_no: 117, ayet_no: 117, sure_adi: "Şuara", text: "قَالَ رَبِّ اِنَّ قَوْم۪ي كَذَّبُونِۚ", translation: "\"Rabb'im! Halkım beni yalanladı.\" dedi.", transliteration: "Kale rabbi inne kavmi kezzebun."},
  {id: 3050, sure_no: 118, ayet_no: 118, sure_adi: "Şuara", text: "فَافْتَحْ بَيْن۪ي وَبَيْنَهُمْ فَتْحاً وَنَجِّن۪ي وَمَنْ مَعِيَ مِنَ الْمُؤْمِن۪ينَ", translation: "\"Artık benim onlarla işimi tamamen bitir. Beni ve benimle beraber iman edenleri kurtar.\" dedi.", transliteration: "Feftah beyni ve beynehum fethan ve neccini ve men maiye minel mu'minin."},
  {id: 3051, sure_no: 119, ayet_no: 119, sure_adi: "Şuara", text: "فَاَنْجَيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ الْمَشْحُونِۚ", translation: "Bunun üzerine onu ve onunla birlikte olanları dolan gemide kurtardık.", transliteration: "Fe enceynahu ve men meahu fil fulkil meşhun."},
  {id: 3052, sure_no: 120, ayet_no: 120, sure_adi: "Şuara", text: "ثُمَّ اَغْرَقْنَا بَعْدُ الْبَاق۪ينَۜ", translation: "Sonra geride kalanları boğduk.", transliteration: "Summe agrakna ba'dul bakin."},
  {id: 3053, sure_no: 121, ayet_no: 121, sure_adi: "Şuara", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Bunda kesinlikle bir ayet vardır. Yine de onların çoğu inananlardan olmadı.", transliteration: "İnne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 3054, sure_no: 122, ayet_no: 122, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Rabb'in, elbette Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 3055, sure_no: 123, ayet_no: 123, sure_adi: "Şuara", text: "كَذَّبَتْ عَادٌۨ الْمُرْسَل۪ينَۚ", translation: "Ad halkı gönderilmişleri yalanladı.", transliteration: "Kezzebet adunil murselin."},
  {id: 3056, sure_no: 124, ayet_no: 124, sure_adi: "Şuara", text: "اِذْ قَالَ لَهُمْ اَخُوهُمْ هُودٌ اَلَا تَتَّقُونَۚ", translation: "Kardeşleri Hud onlara: \"Takva sahibi olmayacak mısınız?\" demişti.", transliteration: "İz kale lehum ehuhum hudun e la tettekun."},
  {id: 3057, sure_no: 125, ayet_no: 125, sure_adi: "Şuara", text: "اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ", translation: "\"Sizin için güvenilir bir Resul'üm.\"", transliteration: "İnni lekum resulun emin."},
  {id: 3058, sure_no: 126, ayet_no: 126, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ", translation: "\"Öyleyse Allah için takva sahibi olun ve bana tabi olun.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3059, sure_no: 127, ayet_no: 127, sure_adi: "Şuara", text: "وَمَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ  اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعَالَم۪ينَۜ", translation: "\"Buna karşılık sizden bir ücret istemiyorum. Benim ücretim yalnızca alemlerin Rabb'ine aittir.\"", transliteration: "Ve ma es'elukum aleyhi min ecr, in ecriye illa ala rabbil alemin."},
  {id: 3060, sure_no: 128, ayet_no: 128, sure_adi: "Şuara", text: "اَتَبْنُونَ بِكُلِّ ر۪يعٍ اٰيَةً تَعْبَثُونَۙ", translation: "\"Her yüksek tepeye ayet bina ederek mi oyalanıyorsunuz?\"", transliteration: "E tebnune bi kulli riın ayeten ta'besun."},
  {id: 3061, sure_no: 129, ayet_no: 129, sure_adi: "Şuara", text: "وَتَتَّخِذُونَ مَصَانِـعَ لَعَلَّكُمْ تَخْلُدُونَۚ", translation: "\"Hiç ölmeyeceğinizi sanarak sağlam yapılar ediniyorsunuz.\"", transliteration: "Ve tettehızune mesania leallekum tahludun."},
  {id: 3062, sure_no: 130, ayet_no: 130, sure_adi: "Şuara", text: "وَاِذَا بَطَشْتُمْ بَطَشْتُمْ جَبَّار۪ينَۚ", translation: "\"Elinize imkan geçtiği zaman, onu zorbaca kullanıyorsunuz.\"", transliteration: "Ve iza betaştum betaştum cebbarin."},
  {id: 3063, sure_no: 131, ayet_no: 131, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ", translation: "\"Gelin Allah'a karşı takva sahibi olun. Ve bana itaat edin.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3064, sure_no: 132, ayet_no: 132, sure_adi: "Şuara", text: "وَاتَّقُوا الَّـذ۪ٓي اَمَدَّكُمْ بِمَا تَعْلَمُونَۚ", translation: "\"Bildiğiniz şeylerle size yardım edene karşı takva sahibi olun.\"", transliteration: "Vettekullezi emeddekum bima ta'lemun."},
  {id: 3065, sure_no: 133, ayet_no: 133, sure_adi: "Şuara", text: "اَمَدَّكُمْ بِاَنْعَامٍ وَبَن۪ينَۙ", translation: "\"Size davarlarla, evlatlarla yardım etti.\"", transliteration: "Emeddekum bi en'amin ve benin."},
  {id: 3066, sure_no: 134, ayet_no: 134, sure_adi: "Şuara", text: "وَجَنَّاتٍ وَعُيُونٍۚ", translation: "\"Ve bahçelerle ve pınarlarla.\"", transliteration: "Ve cennatin ve uyun."},
  {id: 3067, sure_no: 135, ayet_no: 135, sure_adi: "Şuara", text: "اِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظ۪يمٍۜ", translation: "\"Ben sizin için büyük bir günün azabından korkuyorum.\"", transliteration: "İnni ehafu aleykum azabe yevmin azim."},
  {id: 3068, sure_no: 136, ayet_no: 136, sure_adi: "Şuara", text: "قَالُوا سَوَٓاءٌ عَلَيْنَٓا اَوَعَظْتَ اَمْ لَمْ تَكُنْ مِنَ الْوَاعِظ۪ينَۙ", translation: "\"Bize öğüt versen de veya öğüt verenlerden olmasan da bizim için birdir.\" dediler.", transliteration: "Kalu sevaun aleyna e vaazte em lem tekun minel vaızin."},
  {id: 3069, sure_no: 137, ayet_no: 137, sure_adi: "Şuara", text: "اِنْ هٰذَٓا اِلَّا خُلُقُ الْاَوَّل۪ينَۙ", translation: "\"Bu öncekilerin uydurmasından başka bir şey değildir.\"", transliteration: "İn haza illa hulukul evvelin."},
  {id: 3070, sure_no: 138, ayet_no: 138, sure_adi: "Şuara", text: "وَمَا نَحْنُ بِمُعَذَّب۪ينَۚ", translation: "\"Biz, azaba uğratılacak da değiliz.\"", transliteration: "Ve ma nahnu bi muazzebin."},
  {id: 3071, sure_no: 139, ayet_no: 139, sure_adi: "Şuara", text: "فَكَذَّبُوهُ فَاَهْلَكْنَاهُمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Onu yalanladılar. Bunun üzerine onları helak ettik. Bunda bir ayet vardır. Buna rağmen İnsanların pek çoğu inanmamaktadırlar.", transliteration: "Fe kezzebuhu fe ehleknahum, inne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 3072, sure_no: 140, ayet_no: 140, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Rabb'in; Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 3073, sure_no: 141, ayet_no: 141, sure_adi: "Şuara", text: "كَذَّبَتْ ثَمُودُ الْمُرْسَل۪ينَۚ", translation: "Semud halkı gönderilmişleri yalanladı.", transliteration: "Kezzebet semudul murselin."},
  {id: 3074, sure_no: 142, ayet_no: 142, sure_adi: "Şuara", text: "اِذْ قَالَ لَهُمْ اَخُوهُمْ صَالِحٌ اَلَا تَتَّقُونَۚ", translation: "Hani! Kardeşleri Salih onlara: \"Takva sahibi olmayacak mısınız?\" demişti.", transliteration: "İz kale lehum ehuhum salihun e la tettekun."},
  {id: 3075, sure_no: 143, ayet_no: 143, sure_adi: "Şuara", text: "اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ", translation: "\"Sizin için güvenilir bir Resul'üm.\"", transliteration: "İnni lekum resulun emin."},
  {id: 3076, sure_no: 144, ayet_no: 144, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ", translation: "\"Allah için takva sahibi olun ve bana itaat edin.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3077, sure_no: 145, ayet_no: 145, sure_adi: "Şuara", text: "وَمَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعَالَم۪ينَۜ", translation: "\"Buna karşılık sizden bir ücret istemiyorum. Benim ücretim yalnızca alemlerin Rabb'ine aittir.\"", transliteration: "Ve ma es'elukum aleyhi min ecr, in ecriye illa ala rabbil alemin."},
  {id: 3078, sure_no: 146, ayet_no: 146, sure_adi: "Şuara", text: "اَتُتْرَكُونَ ف۪ي مَا هٰهُنَٓا اٰمِن۪ينَۙ", translation: "\"Siz burada; bulunduğunuz yerde hep güven içinde bırakılacak mısınız?\"", transliteration: "E tutrakune fi ma hahuna aminin."},
  {id: 3079, sure_no: 147, ayet_no: 147, sure_adi: "Şuara", text: "ف۪ي جَنَّاتٍ وَعُيُونٍۙ", translation: "\"Cennetlerde ve çeşme başlarında.\"", transliteration: "Fi cennatin ve uyun."},
  {id: 3080, sure_no: 148, ayet_no: 148, sure_adi: "Şuara", text: "وَزُرُوعٍ وَنَخْلٍ طَلْعُهَا هَض۪يمٌۚ", translation: "\"Ekinliklerde ve salkımlı hurmalıklarda.\"", transliteration: "Ve zuruın ve nahlin tal'uha hedim."},
  {id: 3081, sure_no: 149, ayet_no: 149, sure_adi: "Şuara", text: "وَتَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتاً فَارِه۪ينَۚ", translation: "\"Ve dağlardan ustalıkla yonttuğunuz evlerde.\"", transliteration: "Ve tenhıtune minel cibali buyuten farihin."},
  {id: 3082, sure_no: 150, ayet_no: 150, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ", translation: "\"Allah için takvalı olun ve bana itaat edin.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3083, sure_no: 151, ayet_no: 151, sure_adi: "Şuara", text: "وَلَا تُط۪يعُٓوا اَمْرَ الْمُسْرِف۪ينَۙ", translation: "\"Ve müsriflerin buyruklarına uymayın.\"", transliteration: "Ve la tutiu emral musrifin."},
  {id: 3084, sure_no: 152, ayet_no: 152, sure_adi: "Şuara", text: "اَلَّذ۪ينَ يُفْسِدُونَ فِي الْاَرْضِ وَلَا يُصْلِحُونَ", translation: "\"Onlar, yeryüzünde bozgunculuk yapıyorlar, düzeltmiyorlar.\"", transliteration: "Ellezine yufsidune fil ardı ve la yuslihun."},
  {id: 3085, sure_no: 153, ayet_no: 153, sure_adi: "Şuara", text: "قَالُٓوا اِنَّـمَٓا اَنْتَ مِنَ الْمُسَحَّر۪ينَۚ", translation: "\"Sen kesinlikle büyülenmişlerdensin.\" dediler.", transliteration: "Kalu innema ente minel musahharin."},
  {id: 3086, sure_no: 154, ayet_no: 154, sure_adi: "Şuara", text: "مَٓا اَنْتَ اِلَّا بَشَرٌ مِثْلُنَاۚ فَأْتِ بِاٰيَةٍ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "\"Sen de ancak bizim gibi bir beşersin. Eğer doğru söyleyenlerden isen bize bir ayetgetir.\"", transliteration: "Ma ente illa beşerun misluna, fe'ti bi ayetin in kunte mines sadikin."},
  {id: 3087, sure_no: 155, ayet_no: 155, sure_adi: "Şuara", text: "قَالَ هٰذِه۪ نَاقَةٌ لَهَا شِرْبٌ وَلَكُمْ شِرْبُ يَوْمٍ مَعْلُومٍۚ", translation: "\"İşte bu dişi deve. Su içme hakkı; belirli bir gün onun ve belirli bir gün sizindir.\" dedi.", transliteration: "Kale hazihi nakatun leha şirbun ve lekum şirbu yevmin ma'lum."},
  {id: 3088, sure_no: 156, ayet_no: 156, sure_adi: "Şuara", text: "وَلَا تَمَسُّوهَا بِسُٓوءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظ۪يمٍ", translation: "\"Sakın ona bir kötülük dokundurmayın. Yoksa büyük günün azabı sizi yakalar.\"", transliteration: "Ve la temessuha bi suin fe ye'huzekum azabu yevmin azim."},
  {id: 3089, sure_no: 157, ayet_no: 157, sure_adi: "Şuara", text: "فَعَقَرُوهَا فَاَصْبَحُوا نَادِم۪ينَۙ", translation: "Derken onu boğazladılar. Sonra da pişman oldular.", transliteration: "Fe akaruha fe asbahu nadimin."},
  {id: 3090, sure_no: 158, ayet_no: 158, sure_adi: "Şuara", text: "فَاَخَذَهُمُ الْعَذَابُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Fakat azap onları yakaladı. Kuşkusuz bunda bir ayet vardır. Yine de İnsanların çoğu iman etmediler.", transliteration: "Fe ehazehumul azab, inne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 3091, sure_no: 159, ayet_no: 159, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Rabb'in, Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 3092, sure_no: 160, ayet_no: 160, sure_adi: "Şuara", text: "كَذَّبَتْ قَوْمُ لُوطٍۨ الْمُرْسَل۪ينَۚ", translation: "Lut halkı gönderilmişleri yalanladı.", transliteration: "Kezzebet kavmu lutınil murselin."},
  {id: 3093, sure_no: 161, ayet_no: 161, sure_adi: "Şuara", text: "اِذْ قَالَ لَهُمْ اَخُوهُمْ لُوطٌ اَلَا تَتَّقُونَۚ", translation: "Kardeşleri Lut onlara: \"Takva sahibi olmayacak mısınız?\" demişti.", transliteration: "İz kale lehum ehuhum lutun e la tettekun."},
  {id: 3094, sure_no: 162, ayet_no: 162, sure_adi: "Şuara", text: "اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ", translation: "\"Sizin için güvenilir bir Resul'üm.\"", transliteration: "İnni lekum resulun emin."},
  {id: 3095, sure_no: 163, ayet_no: 163, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ", translation: "\"Allah için takva sahibi olun ve bana itaat edin.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3096, sure_no: 164, ayet_no: 164, sure_adi: "Şuara", text: "وَمَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعَالَم۪ينَۜ", translation: "\"Buna karşılık sizden bir ücret istemiyorum. Benim ücretim yalnızca alemlerin Rabb'ine aittir.\"", transliteration: "Ve ma es'elukum aleyhi min ecr, in ecriye illa ala rabbil alemin."},
  {id: 3097, sure_no: 165, ayet_no: 165, sure_adi: "Şuara", text: "اَتَأْتُونَ الذُّكْرَانَ مِنَ الْعَالَم۪ينَۙ", translation: "\"İnsanlardan erkeklerle mi ilişkiye giriyorsunuz?\"", transliteration: "E te'tunez zukrane minel alemin."},
  {id: 3098, sure_no: 166, ayet_no: 166, sure_adi: "Şuara", text: "وَتَذَرُونَ مَا خَلَقَ لَكُمْ رَبُّكُمْ مِنْ اَزْوَاجِكُمْۜ بَلْ اَنْتُمْ قَوْمٌ عَادُونَ", translation: "\"Rabb'inizin sizin için yarattığı eşlerinizi bırakıyorsunuz. Hayır! Siz azgın bir halksınız.\"", transliteration: "Ve tezerune ma halaka lekum rabbukum min ezvacikum, bel entum kavmun adun."},
  {id: 3099, sure_no: 167, ayet_no: 167, sure_adi: "Şuara", text: "قَالُوا لَئِنْ لَمْ تَنْتَهِ۬ يَا لُوطُ لَتَكُونَنَّ مِنَ الْمُخْرَج۪ينَ", translation: "\"Ey Lut! Eğer kesin olarak vazgeçmezsen, kesinlikle sürülenlerden olacaksın.\" dediler.", transliteration: "Kalu le in lem tentehi ya lutu le tekunenne minel muhracin."},
  {id: 3100, sure_no: 168, ayet_no: 168, sure_adi: "Şuara", text: "قَالَ اِنّ۪ي لِعَمَلِكُمْ مِنَ الْقَال۪ينَۜ", translation: "Lut: \"Sizi, yaptıklarınızdan dolayı kınıyorum.\" dedi.", transliteration: "Kale inni li amelikum minel kalin."},
  {id: 3101, sure_no: 169, ayet_no: 169, sure_adi: "Şuara", text: "رَبِّ نَجِّن۪ي وَاَهْل۪ي مِمَّا يَعْمَلُونَ", translation: "\"Rabb'im beni ve ehlimi bunların yaptıklarından kurtar.\"", transliteration: "Rabbi neccini ve ehli mimma ya'melun."},
  {id: 3102, sure_no: 170, ayet_no: 170, sure_adi: "Şuara", text: "فَنَجَّيْنَاهُ وَاَهْلَـهُٓ  اَجْمَع۪ينَۙ", translation: "Bunun üzerine onu ve ehlinin tamamını kurtardık.", transliteration: "Fe necceynahu ve ehlehu ecmain."},
  {id: 3103, sure_no: 171, ayet_no: 171, sure_adi: "Şuara", text: "اِلَّا عَجُوزاً فِي الْغَابِر۪ينَۚ", translation: "Geride kalanlardan aciz bir kadın hariç.", transliteration: "İlla acuzen fil gabirin."},
  {id: 3104, sure_no: 172, ayet_no: 172, sure_adi: "Şuara", text: "ثُمَّ دَمَّرْنَا الْاٰخَر۪ينَۚ", translation: "Sonra diğerlerini dumura uğrattık.", transliteration: "Summe demmernel aharin."},
  {id: 3105, sure_no: 173, ayet_no: 173, sure_adi: "Şuara", text: "وَاَمْطَرْنَا عَلَيْهِمْ مَطَراًۚ فَسَٓاءَ مَطَرُ الْمُنْذَر۪ينَ", translation: "Ve üzerlerine taştan yağmur yağdırdık. Uyarılanların yağmuru ne kötüdür!", transliteration: "Ve emtarna aleyhim matara, fe sae matarul munzerin."},
  {id: 3106, sure_no: 174, ayet_no: 174, sure_adi: "Şuara", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Kuşkusuz bunda bir ayet vardır. Ama onların çoğu iman edenlerden olmadı.", transliteration: "İnne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 3107, sure_no: 175, ayet_no: 175, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Senin Rabb'in, Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 3108, sure_no: 176, ayet_no: 176, sure_adi: "Şuara", text: "كَذَّبَ اَصْحَابُ لْـَٔيْكَةِ الْمُرْسَل۪ينَۚ", translation: "Eyke halkı gönderilmişleri yalanladı.", transliteration: "Kezzebe ashabul eyketil murselin."},
  {id: 3109, sure_no: 177, ayet_no: 177, sure_adi: "Şuara", text: "اِذْ قَالَ لَهُمْ شُعَيْبٌ اَلَا تَتَّقُونَۚ", translation: "Hani! Şu'ayb onlara: \"Takva sahibi olmayacak mısınız?\" demişti.", transliteration: "İz kale lehum şuaybun e la tettekun."},
  {id: 3110, sure_no: 178, ayet_no: 178, sure_adi: "Şuara", text: "اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ", translation: "\"Sizin için güvenilir bir Resul'üm.\"", transliteration: "İnni lekum resulun emin."},
  {id: 3111, sure_no: 179, ayet_no: 179, sure_adi: "Şuara", text: "فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِۚ", translation: "\"Allah için takva sahibi olun ve bana itaat edin.\"", transliteration: "Fettekullahe ve etiun."},
  {id: 3112, sure_no: 180, ayet_no: 180, sure_adi: "Şuara", text: "وَمَٓا اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعَالَم۪ينَۜ", translation: "\"Buna karşılık sizden bir ücret istemiyorum. Benim ücretim yalnızca alemlerin Rabb'ine aittir.\"", transliteration: "Ve ma es'elukum aleyhi min ecr, in ecriye illa ala rabbil alemin."},
  {id: 3113, sure_no: 181, ayet_no: 181, sure_adi: "Şuara", text: "اَوْفُوا الْكَيْلَ وَلَا تَكُونُوا مِنَ الْمُخْسِر۪ينَۚ", translation: "\"Ölçüde tam olun. Hak yiyenlerden olmayın.\"", transliteration: "Evful keyle ve la tekunu minel muhsirin."},
  {id: 3114, sure_no: 182, ayet_no: 182, sure_adi: "Şuara", text: "وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَق۪يمِۚ", translation: "\"Doğru terazi ile tartın.\"", transliteration: "Vezinu bil kıstasil mustekim."},
  {id: 3115, sure_no: 183, ayet_no: 183, sure_adi: "Şuara", text: "وَلَا تَبْخَسُوا النَّاسَ اَشْيَٓاءَهُمْ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَۚ", translation: "\"İnsanların mallarını değerinden düşürmeyin. Bozgunculuk yaparak yeryüzünde karışıklık çıkarmayın.\"", transliteration: "Ve la tebhasun nase eşyaehum ve la ta'sev fil ardı mufsidin."},
  {id: 3116, sure_no: 184, ayet_no: 184, sure_adi: "Şuara", text: "وَاتَّقُوا الَّذ۪ي خَلَقَكُمْ وَالْجِبِلَّةَ الْاَوَّل۪ينَۜ", translation: "\"Sizi ve sizden önceki nesilleri yaratana karşı takva sahibi olun.\"", transliteration: "Vettekullezi halakakum vel cibilletel evvelin."},
  {id: 3117, sure_no: 185, ayet_no: 185, sure_adi: "Şuara", text: "قَالُٓوا اِنَّـمَٓا اَنْتَ مِنَ الْمُسَحَّر۪ينَۙ", translation: "\"Sen iyice büyülenmişlerdensin.\" dediler.", transliteration: "Kalu innema ente minel musahharin."},
  {id: 3118, sure_no: 186, ayet_no: 186, sure_adi: "Şuara", text: "وَمَٓا اَنْتَ اِلَّا بَشَرٌ مِثْلُنَا وَاِنْ نَظُنُّكَ لَمِنَ الْكَاذِب۪ينَۚ", translation: "\"Sen de bizim gibi bir beşerden başka bir şey değilsin. Kesinlikle yalancı olduğunu düşünüyoruz.\"", transliteration: "Ve ma ente illa beşerun misluna ve in nazunnuke le minel kazibin."},
  {id: 3119, sure_no: 187, ayet_no: 187, sure_adi: "Şuara", text: "فَاَسْقِطْ عَلَيْنَا كِسَفاً مِنَ السَّمَٓاءِ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَۜ", translation: "\"Eğer doğru söylüyorsan, haydi gökten üzerimize parçalar düşür.\"", transliteration: "Fe eskıt aleyna kisefen mines semai in kunte mines sadıkin."},
  {id: 3120, sure_no: 188, ayet_no: 188, sure_adi: "Şuara", text: "قَالَ رَبّ۪ٓي اَعْلَمُ بِمَا تَعْمَلُونَ", translation: "Şu'ayb: \"Rabb'im yaptıklarınızı daha iyi bilir.\" dedi.", transliteration: "Kale rabbi a'lemu bi ma ta'melun."},
  {id: 3121, sure_no: 189, ayet_no: 189, sure_adi: "Şuara", text: "فَكَذَّبُوهُ فَاَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِۜ اِنَّهُ كَانَ عَذَابَ يَوْمٍ عَظ۪يمٍ", translation: "Ne var ki onu yalanladılar. Bunun üzerine gölge gününün azabı onları yakaladı. Kuşkusuz o, büyük günün azabıydı.", transliteration: "Fe kezzebuhu fe ehazehum azabu yevmiz zulleh, innehu kane azabe yevmin azim."},
  {id: 3122, sure_no: 190, ayet_no: 190, sure_adi: "Şuara", text: "اِنَّ ف۪ي ذٰلِكَ لَاٰيَةًۜ وَمَا كَانَ اَكْثَرُهُمْ مُؤْمِن۪ينَ", translation: "Kuşkusuz bunda bir ayet vardır. Ama onların çoğu iman edenlerden olmadı.", transliteration: "İnne fi zalike le ayeh, ve ma kane ekseruhum mu'minin."},
  {id: 3123, sure_no: 191, ayet_no: 191, sure_adi: "Şuara", text: "وَاِنَّ رَبَّكَ لَهُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Rabb'in, Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve inne rabbeke le huvel azizur rahim."},
  {id: 3124, sure_no: 192, ayet_no: 192, sure_adi: "Şuara", text: "وَاِنَّهُ لَتَنْز۪يلُ رَبِّ الْعَالَم۪ينَۜ", translation: "O, kesinlikle Alemlerin Rabb'inin indirmesidir.", transliteration: "Ve innehu le tenzilu rabbil alemin."},
  {id: 3125, sure_no: 193, ayet_no: 193, sure_adi: "Şuara", text: "نَزَلَ بِهِ الرُّوحُ الْاَم۪ينُۙ", translation: "Onunla er-Ruh'ul Emin indi.", transliteration: "Nezele bihir ruhul emin."},
  {id: 3126, sure_no: 194, ayet_no: 194, sure_adi: "Şuara", text: "عَلٰى قَلْبِكَ لِتَكُونَ مِنَ الْمُنْذِر۪ينَۙ", translation: "Senin kalbine. Uyarıcılardan olman için.", transliteration: "Ala kalbike li tekune minel munzirin."},
  {id: 3127, sure_no: 195, ayet_no: 195, sure_adi: "Şuara", text: "بِلِسَانٍ عَرَبِيٍّ مُب۪ينٍۜ", translation: "Apaçık Arapça bir lisan ile.", transliteration: "Bi lisanin arabiyyin mubin."},
  {id: 3128, sure_no: 196, ayet_no: 196, sure_adi: "Şuara", text: "وَاِنَّهُ لَف۪ي زُبُرِ الْاَوَّل۪ينَ", translation: "Kuşkusuz o kesinlikle öncekilerin Zeburlarında da vardır.", transliteration: "Ve innehu lefi zuburil evvelin."},
  {id: 3129, sure_no: 197, ayet_no: 197, sure_adi: "Şuara", text: "اَوَلَمْ يَكُنْ لَهُمْ اٰيَةً اَنْ يَعْلَمَهُ عُلَمٰٓؤُ۬ا بَن۪ٓي اِسْرَٓائ۪لَۜ", translation: "Ve İsrailoğulları bilginlerinin onu bilmesi, onlar için bir ayet değil mi?", transliteration: "E ve lem yekun lehum ayeten en ya'lemehu ulemau beni israil."},
  {id: 3130, sure_no: 198, ayet_no: 198, sure_adi: "Şuara", text: "وَلَوْ نَزَّلْنَاهُ عَلٰى بَعْضِ الْاَعْجَم۪ينَۙ", translation: "Eğer onu yabancılardan birisine indirseydik;", transliteration: "Ve lev nezzelnahu ala ba'dıl a'cemin."},
  {id: 3131, sure_no: 199, ayet_no: 199, sure_adi: "Şuara", text: "فَقَرَاَهُ عَلَيْهِمْ مَا كَانُوا بِه۪ مُؤْمِن۪ينَۜ", translation: "O da bunu onlara okusaydı, onlar yine de inanmayacaklardı.", transliteration: "Fe karaehu aleyhim ma kanu bihi mu'minin."},
  {id: 3132, sure_no: 200, ayet_no: 200, sure_adi: "Şuara", text: "كَذٰلِكَ سَلَكْنَاهُ ف۪ي قُلُوبِ الْمُجْرِم۪ينَۜ", translation: "Böylece onu mücrimlerin kalplerine soktuk.", transliteration: "Kezalike seleknahu fi kulubil mucrimin."},
  {id: 3133, sure_no: 201, ayet_no: 201, sure_adi: "Şuara", text: "لَا يُؤْمِنُونَ بِه۪ حَتّٰى يَرَوُا الْعَذَابَ الْاَل۪يمَۙ", translation: "Acıklı azabı görmedikçe ona inanmazlar.", transliteration: "La yu'minune bihi hatta yeravul azabel elim."},
  {id: 3134, sure_no: 202, ayet_no: 202, sure_adi: "Şuara", text: "فَيَأْتِيَهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَۙ", translation: "İşte bu onlara, onlar farkında olmadan, ansızın gelecektir.", transliteration: "Fe ye'tiyehum bagteten ve hum la yeş'urun."},
  {id: 3135, sure_no: 203, ayet_no: 203, sure_adi: "Şuara", text: "فَيَقُولُوا هَلْ نَحْنُ مُنْظَرُونَۜ", translation: "O zaman; \"Bize birazcık olsun süre verilir mi acaba?\" diyecekler.", transliteration: "Fe yekulu hel nahnu munzarun."},
  {id: 3136, sure_no: 204, ayet_no: 204, sure_adi: "Şuara", text: "اَفَبِعَذَابِنَا يَسْتَعْجِلُونَ", translation: "Yoksa azabımızı çabuklaştırmak mı istiyorlar?", transliteration: "E fe bi azabina yesta'cilun."},
  {id: 3137, sure_no: 205, ayet_no: 205, sure_adi: "Şuara", text: "اَفَرَاَيْتَ اِنْ مَتَّعْنَاهُمْ سِن۪ينَۙ", translation: "Gördün mü? Onlara yıllarca yararlanmalarına fırsat versek,", transliteration: "E fe raeyte in metta'nahum sinin."},
  {id: 3138, sure_no: 206, ayet_no: 206, sure_adi: "Şuara", text: "ثُمَّ جَٓاءَهُمْ مَا كَانُوا يُوعَدُونَۙ", translation: "Sonra da onlara yapılan uyarı gerçekleşse,", transliteration: "Summe caehum ma kanu yuadun."},
  {id: 3139, sure_no: 207, ayet_no: 207, sure_adi: "Şuara", text: "مَٓا اَغْنٰى عَنْهُمْ مَا كَانُوا يُمَتَّعُونَۜ", translation: "Yararlandıkları şeyler onlara hiçbir yarar sağlamaz.", transliteration: "Ma agna anhum ma kanu yumetteun."},
  {id: 3140, sure_no: 208, ayet_no: 208, sure_adi: "Şuara", text: "وَمَٓا اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا لَهَا مُنْذِرُونَۗۛ", translation: "Uyarıcıları olmayan hiçbir kenti yok etmedik.", transliteration: "Ve ma ehlekna min karyetin illa leha munzirun."},
  {id: 3141, sure_no: 209, ayet_no: 209, sure_adi: "Şuara", text: "ذِكْرٰى۠ۛ وَمَا كُنَّا ظَالِم۪ينَ", translation: "Bu öğüttür. Biz, haksızlık yapanlardan olmadık.", transliteration: "Zikra, ve ma kunna zalimin."},
  {id: 3142, sure_no: 210, ayet_no: 210, sure_adi: "Şuara", text: "وَمَا تَنَزَّلَتْ بِهِ الشَّيَاط۪ينُ", translation: "Onu, şeytanlar indirmedi.", transliteration: "Ve ma tenezzelet bihiş şeyatin."},
  {id: 3143, sure_no: 211, ayet_no: 211, sure_adi: "Şuara", text: "وَمَا يَنْبَغ۪ي لَهُمْ وَمَا يَسْتَط۪يعُونَۜ", translation: "Bu onların harcı değil, hem buna güçleri de yetmez.", transliteration: "Ve ma yenbagi lehum ve ma yestetiun."},
  {id: 3144, sure_no: 212, ayet_no: 212, sure_adi: "Şuara", text: "اِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَۜ", translation: "Çünkü onlar, işitmekten kesin olarak uzak tutulmuş olanlardır.", transliteration: "İnnehum anis sem'i le ma'zulun."},
  {id: 3145, sure_no: 213, ayet_no: 213, sure_adi: "Şuara", text: "فَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ فَتَكُونَ مِنَ الْمُعَذَّب۪ينَۚ", translation: "O halde Allah ile beraber başka bir ilaha yönelme. Yoksa azap edilenlerden olursun.", transliteration: "Fe la ted'u meallahi ilahen ahara fe tekune minel muazzebin."},
  {id: 3146, sure_no: 214, ayet_no: 214, sure_adi: "Şuara", text: "وَاَنْذِرْ عَش۪يرَتَكَ الْاَقْرَب۪ينَۙ", translation: "Önce yakın akrabalarını uyar.", transliteration: "Ve enzir aşiretekel akrebin."},
  {id: 3147, sure_no: 215, ayet_no: 215, sure_adi: "Şuara", text: "وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّـبَعَكَ مِنَ الْمُؤْمِن۪ينَۚ", translation: "Ve sana uyan İman Edenler'e sahip çık.", transliteration: "Vahfıd cenahake li menittebeake minel mu'minin."},
  {id: 3148, sure_no: 216, ayet_no: 216, sure_adi: "Şuara", text: "فَاِنْ عَصَوْكَ فَقُلْ اِنّ۪ي بَر۪ٓيءٌ مِمَّا تَعْمَلُونَۚ", translation: "Eğer sana karşı çıkarlarsa, \"Ben sizin yaptıklarınızdan uzağım.\" de.", transliteration: "Fe in asavke fe kul inni beriun mimma ta'melun."},
  {id: 3149, sure_no: 217, ayet_no: 217, sure_adi: "Şuara", text: "وَتَوَكَّلْ عَلَى الْعَز۪يزِ الرَّح۪يمِۙ", translation: "Mutlak Üstün Olan'a, Rahmeti Kesintisiz Olan'a tevekkül et.", transliteration: "Ve tevekkel alel azizir rahim."},
  {id: 3150, sure_no: 218, ayet_no: 218, sure_adi: "Şuara", text: "اَلَّذ۪ي يَرٰيكَ ح۪ينَ تَقُومُۙ", translation: "Kıyam ettiğin zaman O seni görür.", transliteration: "Ellezi yerake hine tekum."},
  {id: 3151, sure_no: 219, ayet_no: 219, sure_adi: "Şuara", text: "وَتَقَلُّبَكَ فِي السَّاجِد۪ينَ", translation: "Secde edenler içindeki hareketini de.", transliteration: "Ve tekallubeke fis sacidin."},
  {id: 3152, sure_no: 220, ayet_no: 220, sure_adi: "Şuara", text: "اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "İnnehu huves semiul alim."},
  {id: 3153, sure_no: 221, ayet_no: 221, sure_adi: "Şuara", text: "هَلْ اُنَبِّئُكُمْ عَلٰى مَنْ تَنَزَّلُ الشَّيَاط۪ينُۜ", translation: "Şeytanların kimlere indiğini size haber vereyim mi?", transliteration: "Hel unebbiukum ala men tenezzeluş şeyatin."},
  {id: 3154, sure_no: 222, ayet_no: 222, sure_adi: "Şuara", text: "تَنَزَّلُ عَلٰى كُلِّ اَفَّاكٍ اَث۪يمٍۙ", translation: "Bütün iftiracı günahkarlara inerler.", transliteration: "Tenezzelu ala kulli effakin esim."},
  {id: 3155, sure_no: 223, ayet_no: 223, sure_adi: "Şuara", text: "يُلْقُونَ السَّمْعَ وَاَكْثَرُهُمْ كَاذِبُونَۜ", translation: "Onlar, kulak verirler ve onların çoğu yalancıdırlar.", transliteration: "Yulkunes sem'a ve ekseruhum kazibun."},
  {id: 3156, sure_no: 224, ayet_no: 224, sure_adi: "Şuara", text: "وَالشُّعَرَٓاءُ يَتَّبِعُهُمُ الْغَاوُ۫نَۜ", translation: "Ve o şairler; onlara azgınlar tabi olur.", transliteration: "Veş şuarau yettebiuhumul gavun."},
  {id: 3157, sure_no: 225, ayet_no: 225, sure_adi: "Şuara", text: "اَلَمْ تَرَ اَنَّهُمْ ف۪ي كُلِّ وَادٍ يَه۪يمُونَۙ", translation: "Onların her vadide nasıl şaşkın şaşkın dolaştıklarını görmüyor musun?", transliteration: "E lem tera ennehum fi kulli vadin yehimun."},
  {id: 3158, sure_no: 226, ayet_no: 226, sure_adi: "Şuara", text: "وَاَنَّهُمْ يَقُولُونَ مَا لَا يَفْعَلُونَۙ", translation: "Onlar yapmayacakları şeyleri söylerler.", transliteration: "Ve ennehum yekulune ma la yef'alun."},
  {id: 3159, sure_no: 227, ayet_no: 227, sure_adi: "Şuara", text: "اِلَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَذَكَرُوا اللّٰهَ كَث۪يراً وَانْتَصَرُوا مِنْ بَعْدِ مَا ظُلِمُواۜ وَسَيَعْلَمُ الَّذ۪ينَ ظَلَمُٓوا اَيَّ مُنْقَلَبٍ يَنْقَلِبُونَ", translation: "Ancak iman edenler, salihatı yapanlar, her zaman Allah'ın öğüdünü dinleyenler ve haksızlığa uğratıldıklarında kendilerini savunanlar hariç. Zulmedenler, nasıl bir alt üst oluşla, alt üst olacaklarını yakında bileceklerdir.", transliteration: "İllellezine amenu ve amilus salihati ve zekerullahe kesiran ventesaru min ba'di ma zulimu, ve se ya'lemullezine zalemu eyye munkalebin yenkalibun."},
  {id: 3160, sure_no: 1, ayet_no: 1, sure_adi: "Neml", text: "طٰسٓ۠ تِلْكَ اٰيَاتُ الْقُرْاٰنِ وَكِتَابٍ مُب۪ينٍۙ", translation: "Ta, Sin. İşte bunlar Kur'an'ın, gerçeği apaçık ortaya koyan Kitap'ın ayetleridir.", transliteration: "Ta sin, tilke ayatul kur'ani ve kitabin mubin."},
  {id: 3161, sure_no: 2, ayet_no: 2, sure_adi: "Neml", text: "هُدًى وَبُشْرٰى لِلْمُؤْمِن۪ينَۙ", translation: "İman Edenler için doğru yola iletici ve haber vericidir.", transliteration: "Huden ve buşra lil mu'minin."},
  {id: 3162, sure_no: 3, ayet_no: 3, sure_adi: "Neml", text: "اَلَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوقِنُونَ", translation: "Onlar, salatı ikame ederler, zekatı yaparlar.Onlar, ahiret'e kesin olarak inanırlar.", transliteration: "Ellezine yukimunes salate ve yu'tunez zekate ve hum bil ahıreti hum yukınun."},
  {id: 3163, sure_no: 4, ayet_no: 4, sure_adi: "Neml", text: "اِنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ زَيَّنَّا لَهُمْ اَعْمَالَهُمْ فَهُمْ يَعْمَهُونَۜ", translation: "Hesap gününe iman etmeyenlere, yaptıklarını sevimli gösterdik. Bu yüzden şaşkınlık içinde bocalayıp dururlar.", transliteration: "İnnellezine la yu'minune bil ahireti zeyyenna lehum a'malehum fe hum ya'mehun."},
  {id: 3164, sure_no: 5, ayet_no: 5, sure_adi: "Neml", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ لَهُمْ سُٓوءُ الْعَذَابِ وَهُمْ فِي الْاٰخِرَةِ هُمُ الْاَخْسَرُونَ", translation: "Onlar için azabın kötüsü vardır. Onlar, Hesap gününde en çok ziyana uğrayacak kimselerdir.", transliteration: "Ulaikellezine lehum suul azabi ve hum fil ahıreti humul ahserun."},
  {id: 3165, sure_no: 6, ayet_no: 6, sure_adi: "Neml", text: "وَاِنَّكَ لَتُلَقَّى الْقُرْاٰنَ مِنْ لَدُنْ حَك۪يمٍ عَل۪يمٍ", translation: "Kuşkusuz Sen En İyi Hüküm Veren ve Her Şeyi Bilen tarafından Kur'an verilensin.", transliteration: "Ve inneke le tulekkal kur'ane minledun hakimin alim."},
  {id: 3166, sure_no: 7, ayet_no: 7, sure_adi: "Neml", text: "اِذْ قَالَ مُوسٰى لِاَهْلِه۪ٓ اِنّ۪ٓي اٰنَسْتُ نَاراًۜ سَاٰت۪يكُمْ مِنْهَا بِخَبَرٍ اَوْ اٰت۪يكُمْ بِشِهَابٍ قَبَسٍ لَعَلَّكُمْ تَصْطَلُونَ", translation: "Hani Musa, yakınlarına: \"Bir ateş fark ettim. Size ondan bir haber veya ısınmanız için kor halinde bir parça ateş getireceğim.\" demişti.", transliteration: "İz kale musa li ehlihi inni anestu nara, se atikum minha bi haberin ev atikum bi şihabin kabesin leallekum tastalun."},
  {id: 3167, sure_no: 8, ayet_no: 8, sure_adi: "Neml", text: "فَلَمَّا جَٓاءَهَا نُودِيَ اَنْ بُورِكَ مَنْ فِي النَّارِ وَمَنْ حَوْلَهَاۜ وَسُبْحَانَ اللّٰهِ رَبِّ الْعَالَم۪ينَ", translation: "Oraya gittiği zaman ona seslenildi: \"Ateşin içinde ve etrafında olanlar kutlu kılındı. Ve alemlerin Rabb'i olan Allah, Subhan'dır.\"", transliteration: "Fe lemma caeha nudiye en burike men fin nari ve men havleha, ve subhanallahi rabbil alemin."},
  {id: 3168, sure_no: 9, ayet_no: 9, sure_adi: "Neml", text: "يَا مُوسٰٓى اِنَّـهُٓ  اَنَا اللّٰهُ الْعَز۪يزُ الْحَك۪يمُۙ", translation: "\"Ey Musa! Ben, Mutlak Üstün Olan ve En İyi Hüküm Veren Allah'ım!\"", transliteration: "Ya musa innehu enallahul azizul hakim."},
  {id: 3169, sure_no: 10, ayet_no: 10, sure_adi: "Neml", text: "وَاَلْقِ عَصَاكَۜ فَلَمَّا رَاٰهَا تَهْتَزُّ  كَاَنَّهَا جَٓانٌّ وَلّٰى مُدْبِراً وَلَمْ يُعَقِّبْۜ يَا مُوسٰى لَا تَخَفْ اِنّ۪ي لَا يَخَافُ لَدَيَّ الْمُرْسَلُونَۗ", translation: "\"Asanı bırak!\" Musa, asasını bırakmasıyla birlikte onun yılan gibi hareket ettiğini görünce, geri dönüp arkasına bakmadan kaçtı. \"Ey Musa! Korkma, gerçekten Ben'im. Ben'im huzurumda Resuller korkmaz.\"", transliteration: "Ve elkı asak, fe lemma reaha tehtezzu ke enneha cannun vella mudbiren ve lem yuakkıb, ya musa la tehaf inni la yehafu ledeyyel murselun."},
  {id: 3170, sure_no: 11, ayet_no: 11, sure_adi: "Neml", text: "اِلَّا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْناً بَعْدَ سُٓوءٍ فَاِنّ۪ي غَفُورٌ رَح۪يمٌ", translation: "\"Kim haksızlık yapar, sonra da yaptığı kötülüğü iyiliğe çevirirse, bilsin ki Ben Çok Bağışlayıcı'yım ve Rahmeti Kesintisiz Olan'ım.\"", transliteration: "İlla men zaleme summe beddele husnen ba'de suin fe inni gafurun rahim."},
  {id: 3171, sure_no: 12, ayet_no: 12, sure_adi: "Neml", text: "وَاَدْخِلْ يَدَكَ ف۪ي جَيْبِكَ تَخْرُجْ بَيْضَٓاءَ مِنْ غَيْرِ سُٓوءٍ ف۪ي تِسْعِ اٰيَاتٍ اِلٰى فِرْعَوْنَ وَقَوْمِه۪ۜ اِنَّهُمْ كَانُوا قَوْماً فَاسِق۪ينَ", translation: "\"Ve elini koynuna sok. Kötülük olmaksızın onu bembeyaz olarak çıkar. Firavun ve halkına dokuz ayet ile git. Çünkü onlar fasık bir halk oldular.\"", transliteration: "Ve edhıl yedeke fi ceybike tahruc beydae min gayri suin fi tis'ı ayatin ila fir'avne ve kavmih, innehum kanu kavmen fasikin."},
  {id: 3172, sure_no: 13, ayet_no: 13, sure_adi: "Neml", text: "فَلَمَّا جَٓاءَتْهُمْ اٰيَاتُنَا مُبْصِرَةً قَالُوا هٰذَا سِحْرٌ مُب۪ينٌۚ", translation: "Onlara, gerçeği ortaya koyan ayetlerimiz gelince, \"Bu apaçık sihirdir.\" dediler.", transliteration: "Fe lemma caethum ayatuna mubsıraten kalu haza sihrun mubin."},
  {id: 3173, sure_no: 14, ayet_no: 14, sure_adi: "Neml", text: "وَجَحَدُوا بِهَا وَاسْتَيْقَنَتْهَٓا اَنْفُسُهُمْ ظُلْماً وَعُلُواًّۜ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِد۪ينَ۟", translation: "Doğruluğundan emin oldukları halde, haksızca ve büyüklenerek mücadele ettiler. Ama bozguncuların sonlarının nasıl olduğuna bir bak!", transliteration: "Ve cehadu biha vesteykanetha enfusuhum zulmen ve uluvva, fenzur keyfe kane akıbetul mufsidin."},
  {id: 3174, sure_no: 15, ayet_no: 15, sure_adi: "Neml", text: "وَلَقَدْ اٰتَيْنَا دَاوُ۫دَ وَسُلَيْمٰنَ عِلْماًۚ وَقَالَا الْحَمْدُ لِلّٰهِ الَّذ۪ي فَضَّلَنَا عَلٰى كَث۪يرٍ مِنْ عِبَادِهِ الْمُؤْمِن۪ينَ", translation: "Ant olsun ki Davud'a ve Süleyman'a bir ilim verdik. İkisi de: \"Tüm övgüler, bizi, iman eden kullarının birçoğuna üstün kılan Allah'adır.\" dediler.", transliteration: "Ve lekad ateyna davude ve suleymane ilma, ve kalal hamdu lillahillezi faddalena ala kesirin min ibadihil mu'minin."},
  {id: 3175, sure_no: 16, ayet_no: 16, sure_adi: "Neml", text: "وَوَرِثَ سُلَيْمٰنُ دَاوُ۫دَ وَقَالَ يَٓا اَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَاُو۫ت۪ينَا مِنْ كُلِّ شَيْءٍۜ اِنَّ هٰذَا لَهُوَ الْفَضْلُ الْمُب۪ينُ", translation: "Süleyman, Davud'a mirasçı oldu. Süleyman: \"Ey İnsanlar! Bize kuşdili öğretildi. Bize her şeyden verildi. Bu apaçık ilahi bir armağandır.\" dedi.", transliteration: "Ve varise suleymanu davude ve kale ya eyyuhen nasu ullimna mentıkat tayrı, ve utina min kulli şey', inne haza le huvel fadlul mubin."},
  {id: 3176, sure_no: 17, ayet_no: 17, sure_adi: "Neml", text: "وَحُشِرَ لِسُلَيْمٰنَ جُنُودُهُ مِنَ الْجِنِّ وَالْاِنْسِ وَالطَّيْرِ فَهُمْ يُوزَعُونَ", translation: "Süleyman için, ins ve cinn ve kuşlardan ordular toplandı. Sonra da sevk edildiler.", transliteration: "Ve huşire li suleymane cunuduhu minel cinni vel insi vet tayrı fe hum yuzeun."},
  {id: 3177, sure_no: 18, ayet_no: 18, sure_adi: "Neml", text: "حَتّٰٓى اِذَٓا اَتَوْا عَلٰى وَادِ النَّمْلِۙ قَالَتْ نَمْلَةٌ يَٓا اَيُّهَا النَّمْلُ ادْخُلُوا مَسَاكِنَكُمْۚ لَا يَحْطِمَنَّكُمْ سُلَيْمٰنُ وَجُنُودُهُۙ وَهُمْ لَا يَشْعُرُونَ", translation: "Nihayet Neml Vadisi'ne geldikleri zaman, dişi bir neml: \"Ey nemller! Meskenlerinize girin, Süleyman ve orduları istemeyerek sizi kırıp geçirmesin.\" dedi.", transliteration: "Hatta iza etev ala vadin nemli kalet nemletun ya eyyuhen nemludhulu mesakinekum, la yahtımennekum suleymanu ve cunuduhu ve hum la yeş'urun."},
  {id: 3178, sure_no: 19, ayet_no: 19, sure_adi: "Neml", text: "فَتَبَسَّمَ ضَاحِكاً مِنْ قَوْلِهَا وَقَالَ رَبِّ اَوْزِعْن۪ٓي اَنْ اَشْكُرَ نِعْمَتَكَ الَّت۪ٓي اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحاً تَرْضٰيهُ وَاَدْخِلْن۪ي بِرَحْمَتِكَ ف۪ي عِبَادِكَ الصَّالِح۪ينَ", translation: "Bunun üzerine Süleyman onun sözüne gülümseyerek tebessüm etti. Ve \"Ey Rabb'im! Bana, anne ve babama bağışladığın nimetlerin karşılığında, şükretmede ve hoşnut olacağın işler yapmada beni başarılı kıl. Ve rahmetinle beni salih kullarının arasına kat.\" dedi.", transliteration: "Fe tebesseme dahıken min kavliha ve kale rabbi evzı'ni en eşkure ni'metekelleti en'amte aleyye ve ala valideyye ve en a'mele salihan terdahu ve edhılni bi rahmetike fi ibadikes salihin."},
  {id: 3179, sure_no: 20, ayet_no: 20, sure_adi: "Neml", text: "وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَٓا اَرَى الْهُدْهُدَۘ اَمْ كَانَ مِنَ الْغَٓائِب۪ينَ", translation: "Süleyman kuş topluluğunu yokladı. Sonra: \"Hudhud'u niçin göremiyorum? Yoksa kayıplara mı karıştı?\" dedi.", transliteration: "Ve tefekkadat tayra fe kale maliye la eral hudhude em kane minel gaibin."},
  {id: 3180, sure_no: 21, ayet_no: 21, sure_adi: "Neml", text: "لَاُعَذِّبَنَّهُ عَذَاباً شَد۪يداً اَوْ لَا۬اَذْبَحَنَّهُٓ اَوْ لَيَأْتِيَنّ۪ي بِسُلْطَانٍ مُب۪ينٍ", translation: "\"Kesinlikle ona şiddetli bir ceza vereceğim. Ya da onu boğazlayacağım. Ya da bana mazereti ile ilgili açık bir belge getirecek.\"", transliteration: "Le uazzibennehu azaben şediden ev le ezbehannehu ev le ye'tiyenni bi sultanin mubin."},
  {id: 3181, sure_no: 22, ayet_no: 22, sure_adi: "Neml", text: "فَمَكَثَ غَيْرَ بَع۪يدٍ فَقَالَ اَحَطْتُ بِمَا لَمْ تُحِطْ بِه۪ وَجِئْتُكَ مِنْ سَبَأٍ بِنَبَأٍ يَق۪ينٍ", translation: "Çok geçmeden geldi. Ve: \"Senin kuşatamadığın bir şeyi kuşattım. Sana Sebe'den kesin doğru olan bir haber getirdim.\" dedi.", transliteration: "Fe mekese gayre baidin fe kale ehattu bi ma lem tuhıt bihi ve ci'tuke min sebein bi nebein yakin."},
  {id: 3182, sure_no: 23, ayet_no: 23, sure_adi: "Neml", text: "اِنّ۪ي وَجَدْتُ امْرَاَةً تَمْلِكُهُمْ وَاُو۫تِيَتْ مِنْ كُلِّ شَيْءٍ وَلَهَا عَرْشٌ عَظ۪يمٌ", translation: "\"Onlara yöneticilik yapan bir kadın buldum. Ona her şeyden verilmiş. Çok görkemli bir tahtı var.\"", transliteration: "İnni vecedtumreeten temlikuhum ve utiyet min kulli şey'in ve leha arşun azim."},
  {id: 3183, sure_no: 24, ayet_no: 24, sure_adi: "Neml", text: "وَجَدْتُهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِنْ دُونِ اللّٰهِ وَزَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّب۪يلِ فَهُمْ لَا يَهْتَدُونَۙ", translation: "\"Kendisini de toplumunu da Allah'ın yanı sıra Güneş'e secde ederken buldum. Şeytan onlara yaptıklarını güzel göstererek onları doğru yoldan alıkoymuş, bundan dolayı da doğru yolu bulamıyorlar.\"", transliteration: "Vecedtuha ve kavmeha yescudune liş şemsi min dunillahi ve zeyyene lehümuş şeytanu a'malehum fe saddehum anis sebili fe hum la yehtedun."},
  {id: 3184, sure_no: 25, ayet_no: 25, sure_adi: "Neml", text: "اَلَّا يَسْجُدُوا لِلّٰهِ الَّذ۪ي يُخْرِجُ الْخَبْءَ فِي السَّمٰوَاتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُخْفُونَ وَمَا تُعْلِنُونَ", translation: "\"Göklerde ve yerde saklı olanı çıkaran, sizin sakladığınızı da açıkladığınızı da bilen Allah'a secde etmeleri gerekmez mi?\"", transliteration: "Ella yescudu lillahillezi yuhriculhab'e fis semavati vel ardı ve ya'lemu ma tuhfune ve ma tu'linun."},
  {id: 3185, sure_no: 26, ayet_no: 26, sure_adi: "Neml", text: "اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَ رَبُّ الْعَرْشِ الْعَظ۪يمِ", translation: "\"Allah ki, büyük arşın sahibidir. O'ndan başka ilah yoktur.\"", transliteration: "Allahu la ilahe illa huve rabbul arşil azim."},
  {id: 3186, sure_no: 27, ayet_no: 27, sure_adi: "Neml", text: "قَالَ سَنَنْظُرُ اَصَدَقْتَ اَمْ كُنْتَ مِنَ الْكَاذِب۪ينَ", translation: "Süleyman, Hudhud'a: \"Bakacağız! Doğru mu söyledin yoksa yalan mı!\" dedi.", transliteration: "Kale se nenzuru e sadakte em kunte minel kazibin."},
  {id: 3187, sure_no: 28, ayet_no: 28, sure_adi: "Neml", text: "اِذْهَبْ بِكِتَاب۪ي هٰذَا فَاَلْقِهْ اِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُونَ", translation: "\"Bu mektubumu götür, onlara ilet. Sonra bir kenara çekilip ne tepki vereceklerini gözle.\"", transliteration: "İzheb bi kitabi haza fe elkıh ileyhim summe tevelle anhum fenzur maza yerciun."},
  {id: 3188, sure_no: 29, ayet_no: 29, sure_adi: "Neml", text: "قَالَتْ يَٓا اَيُّهَا الْمَلَؤُ۬ا اِنّ۪ٓي اُلْقِيَ اِلَيَّ كِتَابٌ كَر۪يمٌ", translation: "Sebe Melike'si: \"Ey meleler! Bana çok şerefli bir mektup bırakıldı.\" dedi.", transliteration: "Kalet ya eyyuhel meleu inni ulkıye ileyye kitabun kerim."},
  {id: 3189, sure_no: 30, ayet_no: 30, sure_adi: "Neml", text: "اِنَّهُ مِنْ سُلَيْمٰنَ وَاِنَّهُ بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّح۪يمِۙ", translation: "\"O, Süleyman'ın mektubudur. Rahmeti Bol ve Kesintisiz olan Allah'ın adıyla başlıyor.\"", transliteration: "İnnehu min suleymane ve innehu bismillahir rahmanir rahim."},
  {id: 3190, sure_no: 31, ayet_no: 31, sure_adi: "Neml", text: "اَلَّا تَعْلُوا عَلَيَّ وَأْتُون۪ي مُسْلِم۪ينَ۟", translation: "\"Sakın bana karşı büyüklük taslamayın. Teslimiyet içinde bana gelin!\" diyor.", transliteration: "Ella ta'lu aleyye ve'tuni muslimin."},
  {id: 3191, sure_no: 32, ayet_no: 32, sure_adi: "Neml", text: "قَالَتْ يَٓا اَيُّهَا الْمَلَؤُ۬ا اَفْتُون۪ي ف۪ٓي اَمْر۪يۚ مَا كُنْتُ قَاطِعَةً اَمْراً حَتّٰى تَشْهَدُونِ", translation: "\"Ey meleler! Bu istekle ilgili bana görüşlerinizi bildirin. Siz, olmadan ben kesin bir karar verecek değilim.\" dedi.", transliteration: "Kalet ya eyyuhel meleu eftuni fi emri, ma kuntu katıaten emren hatta teşhedun."},
  {id: 3192, sure_no: 33, ayet_no: 33, sure_adi: "Neml", text: "قَالُوا نَحْنُ اُو۬لُوا قُوَّةٍ وَاُو۬لُوا بَأْسٍ شَد۪يدٍ وَالْاَمْرُ اِلَيْكِ فَانْظُر۪ي مَاذَا تَأْمُر۪ينَ", translation: "\"Biz güçlüyüz ve çok büyük savaş gücüne sahibiz. Buyruk senindir. Nasıl istiyorsan öyle olsun.\" dediler.", transliteration: "Kalu nahnu ulu kuvvetin ve ulu be'sin şedidin vel emru ileyki fenzuri maza te'murin."},
  {id: 3193, sure_no: 34, ayet_no: 34, sure_adi: "Neml", text: "قَالَتْ اِنَّ الْمُلُوكَ اِذَا دَخَلُوا قَرْيَةً اَفْسَدُوهَا وَجَعَلُٓوا اَعِزَّةَ اَهْلِهَٓا اَذِلَّةًۚ وَكَذٰلِكَ يَفْعَلُونَ", translation: "Sebe Melike'si: \"Hükümdarlar bir ülkeye girdikleri zaman, orayı bozguna uğratırlar. Oranın halkından izzet sahibi olanları zillete düşürürler. Onlar da böyle yapacaklar.\" dedi.", transliteration: "Kalet innel muluke iza dehalu karyeten efseduha ve cealu eizzete ehliha ezilleh, ve kezalike yef'alun."},
  {id: 3194, sure_no: 35, ayet_no: 35, sure_adi: "Neml", text: "وَاِنّ۪ي مُرْسِلَةٌ اِلَيْهِمْ بِهَدِيَّةٍ فَنَاظِرَةٌ بِمَ يَرْجِعُ الْمُرْسَلُونَ", translation: "\"Onlara hediyeler göndereyim. Bakalım elçiler nasıl bir cevapla dönecekler?\"", transliteration: "Ve inni mursiletun ileyhim bi hediyyetin fe nazıratun bime yerciul murselun."},
  {id: 3195, sure_no: 36, ayet_no: 36, sure_adi: "Neml", text: "فَلَمَّا جَٓاءَ سُلَيْمٰنَ قَالَ اَتُمِدُّونَنِ بِمَالٍۘ فَمَٓا اٰتٰينِ‌يَ اللّٰهُ خَيْرٌ مِمَّٓا اٰتٰيكُمْۚ بَلْ اَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُونَ", translation: "Elçi Süleyman'a geldiğinde, Süleyman: \"Bana mal ile yardım etmek mi istiyorsunuz? Oysa Allah'ın bana verdiği, size verdiğinden daha hayırlıdır. Böyle hediyelere ancak sizin gibiler sevinir.\" dedi.", transliteration: "Fe lemma cae suleymane kale e tumidduneni bi malin fe ma ataniyallahu hayrun mimma atakum, bel entum bi hediyyetikum tefrahun."},
  {id: 3196, sure_no: 37, ayet_no: 37, sure_adi: "Neml", text: "اِرْجِعْ اِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُودٍ لَا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِنْهَٓا اَذِلَّةً وَهُمْ صَاغِرُونَ", translation: "\"Onlara geri dön. Karşı koyamayacakları bir ordu ile gelir, onları yurtlarından hor ve aşağılanmış olarak çıkarırım.\"", transliteration: "İrcı' ileyhim fe le ne'tiyennehum bi cunudin la kıbele lehum biha ve le nuhricennehum minha ezilleten ve hum sagırun."},
  {id: 3197, sure_no: 38, ayet_no: 38, sure_adi: "Neml", text: "قَالَ يَٓا اَيُّهَا الْمَلَؤُ۬ا اَيُّكُمْ يَأْت۪ين۪ي بِعَرْشِهَا قَبْلَ اَنْ يَأْتُون۪ي مُسْلِم۪ينَ", translation: "Süleyman: \"Ey meleler! Onlar teslimiyet içinde bana gelmeden önce hanginiz onun tahtını bana getirebilir?\" dedi.", transliteration: "Kale ya eyyuhel meleu eyyekum ye'tini bi arşiha kable en ye'tuni muslimin."},
  {id: 3198, sure_no: 39, ayet_no: 39, sure_adi: "Neml", text: "قَالَ عِفْر۪يتٌ مِنَ الْجِنِّ اَنَا۬ اٰت۪يكَ بِه۪ قَبْلَ اَنْ تَقُومَ مِنْ مَقَامِكَۚ وَاِنّ۪ي عَلَيْهِ لَقَوِيٌّ اَم۪ينٌ", translation: "Cinnlerden bir ifrit: \"Sen makamından kalkmadan önce onu sana getiririm. Bunu gerçekleştirebileceğimden eminim.\" dedi.", transliteration: "Kale ıfritun minel cinni ene atike bihi kable en tekume min makamik ve inni aleyhi le kaviyyun emin."},
  {id: 3199, sure_no: 40, ayet_no: 40, sure_adi: "Neml", text: "قَالَ الَّذ۪ي عِنْدَهُ عِلْمٌ مِنَ الْكِتَابِ اَنَا۬ اٰت۪يكَ بِه۪ قَبْلَ اَنْ يَرْتَدَّ اِلَيْكَ طَرْفُكَۜ فَلَمَّا رَاٰهُ مُسْتَقِراًّ عِنْدَهُ قَالَ هٰذَا مِنْ فَضْلِ رَبّ۪ي۠ لِيَبْلُوَن۪ٓي ءَاَشْكُرُ اَمْ اَكْفُرُۜ وَمَنْ شَكَرَ فَاِنَّمَا يَشْكُرُ لِنَفْسِه۪ۚ وَمَنْ كَفَرَ فَاِنَّ رَبّ۪ي غَنِيٌّ كَر۪يمٌ", translation: "Kitaptan yanında bir bilgi bulunan kimse: \"Onu bakışın sana dönmeden getiririm.\" dedi. Süleyman, onun yanı başında durduğunu görünce: \"Bu Rabb'imin lütfundandır; şükür mü edeceğim yoksa nankörlük mü edeceğim diye beni sınamak içindir.\" dedi. \"Ve kim şükrederse kendisi için şükretmiş olur, kim de nankörlük ederse, bilsin ki Rabb'im Hiçbir Şeye Muhtaç Olmayan'dır, Cömert'tir. \"", transliteration: "Kalellezi indehu ilmun minel kitabi ene atike bihi kable en yertedde ileyke tarfuk, fe lemma reahu mustekırran indehu kale haza min fadlı rabbi, li yebluveni e eşkur em ekfur, ve men şekere fe innema yeşkuru li nefsih ve men kefere fe inne rabbi ganiyyun kerim."},
  {id: 3200, sure_no: 41, ayet_no: 41, sure_adi: "Neml", text: "قَالَ نَكِّرُوا لَهَا عَرْشَهَا نَنْظُرْ اَتَهْتَد۪ٓي اَمْ تَكُونُ مِنَ الَّذ۪ينَ لَا يَهْتَدُونَ", translation: "Süleyman: \"Onun tahtının şeklini değiştirin. Bakalım doğruyu bulacak mı yoksa doğruyu bulamayanlardan mı olacak?\" dedi.", transliteration: "Kale nekkiru leha arşeha nenzur e tehtedi em tekunu minellezine la yehtedun."},
  {id: 3201, sure_no: 42, ayet_no: 42, sure_adi: "Neml", text: "فَلَمَّا جَٓاءَتْ ق۪يلَ اَهٰكَذَا عَرْشُكِۜ قَالَتْ كَاَنَّهُ هُوَۚ وَاُو۫ت۪ينَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِم۪ينَ", translation: "Melike geldiğinde ona: \"Senin tahtın böyle miydi?\" denildi. \"Sanki onun gibi.\" dedi. Ve \"Bize daha önce bilgi verildi ve biz teslimiyet gösterdik!\"", transliteration: "Fe lemma caet kile e hakeza arşuk, kalet ke ennehu huve ve utinel ilme min kabliha ve kunna muslimin."},
  {id: 3202, sure_no: 43, ayet_no: 43, sure_adi: "Neml", text: "وَصَدَّهَا مَا كَانَتْ تَعْبُدُ مِنْ دُونِ اللّٰهِۜ اِنَّهَا كَانَتْ مِنْ قَوْمٍ كَافِر۪ينَ", translation: "Allah'ın yanı sıra kulluk ettiği şeyler, onu alıkoymuştu. Çünkü o Kafir bir halktandı.", transliteration: "Ve saddeha ma kanet ta'budu min dunillah, inneha kanet min kavmin kafirin."},
  {id: 3203, sure_no: 44, ayet_no: 44, sure_adi: "Neml", text: "ق۪يلَ لَهَا ادْخُلِي الصَّرْحَۚ فَلَمَّا رَاَتْهُ حَسِبَتْهُ لُجَّةً وَكَشَفَتْ عَنْ سَاقَيْهَاۜ قَالَ اِنَّهُ صَرْحٌ مُمَرَّدٌ مِنْ قَوَار۪يرَۜ قَالَتْ رَبِّ اِنّ۪ي ظَلَمْتُ نَفْس۪ي وَاَسْلَمْتُ مَعَ سُلَيْمٰنَ لِلّٰهِ رَبِّ الْعَالَم۪ينَ۟", translation: "Ona, \"Köşke gir!\" denildi. Köşkü görünce, onu derin su sanarak bacaklarını sıvadı. Süleyman, \"Bu billurdan döşenmiş şeffaf bir zemindir.\" dedi. Melike, \"Rabb'im, ben kendime haksızlık ettim. Süleyman ile birlikte alemlerin Rabb'i olan Allah'a teslim oldum.\" dedi.", transliteration: "Kile lehadhulis sarh, fe lemma raethu hasibethu lucceten ve keşefet an sakayha, kale innehu sarhun mumerradun min kavarir, kalet rabbi inni zalemtu nefsi ve eslemtu mea suleymane lillahi rabbil alemin."},
  {id: 3204, sure_no: 45, ayet_no: 45, sure_adi: "Neml", text: "وَلَقَدْ اَرْسَلْـنَٓا اِلٰى ثَمُودَ اَخَـاهُمْ صَـالِحاً اَنِ اعْبُـدُوا اللّٰهَ فَاِذَا هُمْ فَر۪يقَانِ يَخْتَصِمُونَ", translation: "Ant olsun ki Semud halkına da \"Allah'a kulluk edin.\" diye kardeşleri Salih'i gönderdik. Ne var ki birbirleri ile çekişen iki grup oldular.", transliteration: "Ve lekad erselna ila semude ehahum salihan eni'budullahe fe izahum ferikani yahtesımun."},
  {id: 3205, sure_no: 46, ayet_no: 46, sure_adi: "Neml", text: "قَالَ يَا قَوْمِ لِمَ تَسْتَعْجِلُونَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِۚ لَوْلَا تَسْتَغْفِرُونَ اللّٰهَ لَعَلَّكُمْ تُرْحَمُونَ", translation: "Salih: \"Ey halkım! Neden iyilik dururken, kötülüğün bir an önce olmasını istiyorsunuz? Allah'tan bağışlanma dileseniz olmaz mı? Belki bağışlanırsınız!\" dedi.", transliteration: "Kale ya kavmi lime testa'cilune bis seyyieti kablel haseneh, lev la testagfirunallahe leallekum turhamun."},
  {id: 3206, sure_no: 47, ayet_no: 47, sure_adi: "Neml", text: "قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَعَكَۜ قَالَ طَٓائِرُكُمْ عِنْدَ اللّٰهِ بَلْ اَنْتُمْ قَوْمٌ تُفْتَنُونَ", translation: "\"Sen ve seninle beraber olanlar bize uğursuzluk getirdiniz.\" dediler. Salih: \"Sizin uğursuzluğunuz Allah'ın takdirindedir. Belki sınav olunmakta olan bir toplumsunuz!\" dedi.", transliteration: "Kalut tayyerna bike ve bi men meak, kale tairukum indallahi bel entum kavmun tuftenun."},
  {id: 3207, sure_no: 48, ayet_no: 48, sure_adi: "Neml", text: "وَكَانَ فِي الْمَد۪ينَةِ تِسْعَةُ رَهْطٍ يُفْسِدُونَ فِي الْاَرْضِ وَلَا يُصْلِحُونَ", translation: "Kentte bozgunculuk yapan dokuzlu bir çete vardı. Yeryüzünde bozgunculuk yapıyor, düzeltmeye yanaşmıyorlardı.", transliteration: "Ve kane fil medineti tis'atu rahtın yufsidune fil ardı ve la yuslihun."},
  {id: 3208, sure_no: 49, ayet_no: 49, sure_adi: "Neml", text: "قَالُوا تَقَاسَمُوا بِاللّٰهِ لَنُبَيِّتَنَّهُ وَاَهْلَهُ ثُمَّ لَنَقُولَنَّ لِوَلِيِّه۪ مَا شَهِدْنَا مَهْلِكَ اَهْلِه۪ وَاِنَّا لَصَادِقُونَ", translation: "Allah'a yemin ederek dediler ki: \"Gece ona ve ailesine baskın yapalım. Sonra da onun velisine ailesinin yok edilmesiyle bir ilgimiz yok, biz kesinlikle doğru söyleyenleriz diyelim.\"", transliteration: "Kalu tekasemu billahi le nubeyyitennehu ve ehlehu summe le nekulenne li veliyyihi ma şehidna mehlike ehlihi ve inna le sadikun."},
  {id: 3209, sure_no: 50, ayet_no: 50, sure_adi: "Neml", text: "وَمَكَرُوا مَكْراً وَمَكَرْنَا مَكْراً وَهُمْ لَا يَشْعُرُونَ", translation: "Plan yaptılar. Biz de planlarını boşa çıkardık. Fakat farkına varamadılar.", transliteration: "Ve mekeru mekran ve mekerna mekran ve hum la yeş'urun."},
  {id: 3210, sure_no: 51, ayet_no: 51, sure_adi: "Neml", text: "فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْۙ اَنَّا دَمَّرْنَاهُمْ وَقَوْمَهُمْ اَجْمَع۪ينَ", translation: "Onların planlarının sonucunun nasıl olduğuna bak; onları ve halklarını yerle bir ettik.", transliteration: "Fenzur keyfe kane akıbetu mekrihim enna demmernahum ve kavmehum ecmein."},
  {id: 3211, sure_no: 52, ayet_no: 52, sure_adi: "Neml", text: "فَتِلْكَ بُيُوتُهُمْ خَاوِيَةً بِمَا ظَلَمُواۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِقَوْمٍ يَعْلَمُونَ", translation: "İşte, haksızlıkları nedeniyle harabe olan evleri! Bilen bir toplum için bunda kesinlikle bir ayet vardır.", transliteration: "Fe tilke buyutuhum haviyeten bima zalemu, inne fi zalike le ayeten li kavmin ya'lemun."},
  {id: 3212, sure_no: 53, ayet_no: 53, sure_adi: "Neml", text: "وَاَنْجَيْنَا الَّذ۪ينَ اٰمَنُوا وَكَانُوا يَتَّقُونَ", translation: "İman edip takva sahibi olanları ise kurtardık.", transliteration: "Ve enceynellezine amenu ve kanu yettekun."},
  {id: 3213, sure_no: 54, ayet_no: 54, sure_adi: "Neml", text: "وَلُوطاً اِذْ قَالَ لِقَوْمِه۪ٓ اَتَأْتُونَ الْفَاحِشَةَ وَاَنْتُمْ تُبْصِرُونَ", translation: "Lut, halkına şöyle demişti: \"Göz göre göre fahişelik mi yapıyorsunuz?\"", transliteration: "Ve lutan iz kale li kavmihi ete'tunel fahışete ve entum tubsırun."},
  {id: 3214, sure_no: 55, ayet_no: 55, sure_adi: "Neml", text: "اَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَٓاءِۜ بَلْ اَنْتُمْ قَوْمٌ تَجْهَلُونَ", translation: "\"Kadınları bırakıp şehvetle erkeklere mi yanaşıyorsunuz? Hayır! Siz gerçekten cahil bir toplumsunuz.\"", transliteration: "E innekum le te'tuner ricale şehveten min dunin nisai, bel entum kavmun techelun."},
  {id: 3215, sure_no: 56, ayet_no: 56, sure_adi: "Neml", text: "فَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُٓوا اَخْرِجُٓوا اٰلَ لُوطٍ مِنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَتَطَهَّرُونَ", translation: "Fakat halkının cevabı: \"Lut ailesini kasabamızdan çıkarın; çünkü onlar temiz kalmak isteyen kimselermiş!\" demekten başka bir şey olmadı.", transliteration: "Fe ma kane cevabe kavmihi illa en kalu ahricu ale lutın min karyetikum innehum unasun yetetahherun."},
  {id: 3216, sure_no: 57, ayet_no: 57, sure_adi: "Neml", text: "فَاَنْجَيْنَاهُ وَاَهْلَـهُٓ اِلَّا امْرَاَتَهُۘ قَدَّرْنَاهَا مِنَ الْغَابِر۪ينَ", translation: "Bunun üzerine onu ve taraftarlarını kurtardık; karısı hariç. Onun geride kalanlardan olmasını uygun gördük.", transliteration: "Fe enceynahu ve ehlehu illemreetehu kaddernaha minel gabirin."},
  {id: 3217, sure_no: 58, ayet_no: 58, sure_adi: "Neml", text: "وَاَمْطَرْنَا عَلَيْهِمْ مَطَراًۚ فَسَٓاءَ مَطَرُ الْمُنْذَر۪ينَ۟", translation: "Ve onların üzerlerine taştan yağmur yağdırdık. Uyarılanların yağmuru; çok kötü oldu.", transliteration: "Ve emtarna aleyhim matara, fe sae matarul munzerin."},
  {id: 3218, sure_no: 59, ayet_no: 59, sure_adi: "Neml", text: "قُلِ الْحَمْدُ لِلّٰهِ وَسَلَامٌ عَلٰى عِبَادِهِ الَّذ۪ينَ اصْطَفٰىۜ آٰللّٰهُ خَيْرٌ اَمَّا يُشْرِكُونَۜ", translation: "De ki: \"Allah'a hamd olsun.\" Selam onun seçtiği kullarının üzerinedir. Allah mı daha hayırlıdır yoksa ona şirk koştukları mı?", transliteration: "Kulil hamdu lillahi ve selamun ala ibadihillezinastafa, allahu hayrun emma yuşrikun."},
  {id: 3219, sure_no: 60, ayet_no: 60, sure_adi: "Neml", text: "اَمَّنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَاَنْزَلَ لَكُمْ مِنَ السَّمَٓاءِ مَٓاءًۚ فَاَنْبَتْنَا بِه۪ حَدَٓائِقَ ذَاتَ بَهْجَةٍۚ مَا كَانَ لَكُمْ اَنْ تُنْبِتُوا شَجَرَهَاۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ بَلْ هُمْ قَوْمٌ يَعْدِلُونَۜ", translation: "Veya gökleri ve yeri yaratan ve sizin için gökten su indiren mi? Biz onunla, bir ağacını dahi yetiştiremeyeceğiniz güzel bahçeler yetiştirdik. Allah ile birlikte başka bir ilah mı var? Hayır, onlar sapkınlık ediyorlar.", transliteration: "Emmen halakas semavati vel arda ve enzele lekum mines semai ma', fe enbetna bihi hadaika zate behceh, ma kane lekum en tunbitu şecereha, e ilahun meallah, bel hum kavmun ya'dilun."},
  {id: 3220, sure_no: 61, ayet_no: 61, sure_adi: "Neml", text: "اَمَّنْ جَعَلَ الْاَرْضَ قَرَاراً وَجَعَلَ خِلَالَـهَٓا اَنْهَاراً وَجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزاًۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَۜ", translation: "Yoksa yeryüzünü yerleşme yeri yapan ve orda nehirler akıtan, orada ağır baskılar koyan ve iki deniz arasına engel koyan mı? Allah ile beraber bir ilah mı? Hayır, onların çoğu bilmiyorlar.", transliteration: "Emmen cealel arda kararen ve ceale hılaleha enharen ve ceale leha revasiye ve ceale beynel bahreyni haciza, e ilahun meallah, bel ekseruhum la ya'lemun."},
  {id: 3221, sure_no: 62, ayet_no: 62, sure_adi: "Neml", text: "اَمَّنْ يُج۪يبُ الْمُضْطَرَّ اِذَا دَعَاهُ وَيَكْشِفُ السُّٓوءَ وَيَجْعَلُكُمْ خُلَـفَٓاءَ الْاَرْضِۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ قَل۪يلاً مَا تَذَكَّرُونَۜ", translation: "Yoksa darda kalanın çağrısına karşılık veren, kötülüğü gideren ve sizi yeryüzünün halifeleri yapan mı? Allah ile birlikte başka bir ilah mı? Ne kadar az öğüt alıyorsunuz?", transliteration: "Emmen yucibul mudtarra iza deahu ve yekşifus sue ve yec'alukum hulefael ard, e ilahun meallah, kalilen ma tezekkerun."},
  {id: 3222, sure_no: 63, ayet_no: 63, sure_adi: "Neml", text: "اَمَّنْ يَهْد۪يكُمْ ف۪ي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُرْسِلُ الرِّيَاحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِه۪ۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ تَعَالَى اللّٰهُ عَمَّا يُشْرِكُونَۜ", translation: "Yoksa karanın ve denizin karanlıklarında size yol gösteren mi? Rahmetinin önünde müjdeci olarak rüzgarları gönderen mi? Allah ile birlikte bir ilah mı? Allah, onların şirk koştuklarından çok yücedir.", transliteration: "Emmen yehdikum fi zulumatil berri vel bahri ve men yursilur riyaha buşren beyne yedey rahmetih, e ilahun meallah, tealallahu amma yuşrikun."},
  {id: 3223, sure_no: 64, ayet_no: 64, sure_adi: "Neml", text: "اَمَّنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ وَمَنْ يَرْزُقُكُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِۜ ءَاِلٰهٌ مَعَ اللّٰهِۜ قُلْ هَاتُوا بُرْهَانَكُمْ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Yoksa ilk kez yaratan, sonra da yaratmayı tekrarlayacak olan, sizi gökten ve yerden rızıklandıran mı? Allah ile birlikte başka bir ilah mı? De ki: \"Eğer doğru söyleyenlerdenseniz burhanınızı getirin.\"", transliteration: "Emmen yebdeul halka summe yuiduhu ve men yerzukukum mines semai vel ard, e ilahun meallah, kul hatu burhanekum in kuntum sadikin."},
  {id: 3224, sure_no: 65, ayet_no: 65, sure_adi: "Neml", text: "قُلْ لَا يَعْلَمُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِ الْغَيْبَ اِلَّا اللّٰهُۜ وَمَا يَشْعُرُونَ اَيَّانَ يُبْعَثُونَ", translation: "De ki: \"Göklerde ve yerde gaybı Allah'tan başka kimse bilemez. Onlar ne zaman diriltileceklerinin bilincinde olamazlar.\"", transliteration: "Kul la ya'lemu men fis semavati vel ardıl gaybe illallah ve ma yeş'urune eyyane yub'asun."},
  {id: 3225, sure_no: 66, ayet_no: 66, sure_adi: "Neml", text: "بَلِ ادَّارَكَ عِلْمُهُمْ فِي الْاٰخِرَةِ۠ بَلْ هُمْ ف۪ي شَكٍّ مِنْهَا۠ بَلْ هُمْ مِنْهَا عَمُونَ۟", translation: "Aslında onlar ahiret hakkında yeterince bilgilendirildiler. Fakat hala şüphe içindeler. Doğrusu bundan yana kördürler.", transliteration: "Beliddareke ilmuhum fil ahıreh, bel hum fi şekkin minha, bel hum minha amun."},
  {id: 3226, sure_no: 67, ayet_no: 67, sure_adi: "Neml", text: "وَقَالَ الَّذ۪ينَ كَفَرُٓوا ءَاِذَا كُنَّا تُرَاباً وَاٰبَٓاؤُ۬نَٓا اَئِنَّا لَمُخْرَجُونَ", translation: "Kafirler: \"Biz ve atalarımız toprak olduktan sonra, gerçekten de diriltilip çıkartılacak mıyız?\" dediler.", transliteration: "Ve kalellezine keferu e iza kunna turaben ve abauna e inna le muhracun."},
  {id: 3227, sure_no: 68, ayet_no: 68, sure_adi: "Neml", text: "لَقَدْ وُعِدْنَا هٰذَا نَحْنُ وَاٰبَٓاؤُ۬نَا مِنْ قَبْلُۙ اِنْ هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ", translation: "\"Ant olsun, bu bize söylenen daha önce atalarımıza da söylenmişti. Ancak bunlar evvelkilerin masallarından başka bir şey değildir.\" dediler.", transliteration: "Lekad vuıdna haza nahnu ve abauna min kablu in haza illa esatirul evvelin."},
  {id: 3228, sure_no: 69, ayet_no: 69, sure_adi: "Neml", text: "قُلْ س۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِم۪ينَ", translation: "De ki: \"Yeryüzünde gezip dolaşın, mücrimlerin sonları nasıl olmuş bir görün?\"", transliteration: "Kul siru fil ardı fenzuru keyfe kane akibetul mucrimin."},
  {id: 3229, sure_no: 70, ayet_no: 70, sure_adi: "Neml", text: "وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُنْ ف۪ي ضَيْقٍ مِمَّا يَمْكُرُونَ", translation: "Onlar için üzülme! Yaptıkları planlardan dolayı canını sıkma.", transliteration: "Ve la tahzen aleyhim ve la tekun fi daykın mimma yemkurun."},
  {id: 3230, sure_no: 71, ayet_no: 71, sure_adi: "Neml", text: "وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Eğer doğru söyleyenlerdenseniz, bu uyarı ne zaman gerçekleşecek?\" diyorlar.", transliteration: "Ve yekulune meta hazel va'du in kuntum sadıkin."},
  {id: 3231, sure_no: 72, ayet_no: 72, sure_adi: "Neml", text: "قُلْ عَسٰٓى اَنْ يَكُونَ رَدِفَ لَكُمْ بَعْضُ الَّذ۪ي تَسْتَعْجِلُونَ", translation: "De ki: \"Çarçabuk istediğiniz azabın bir kısmı belki de size yaklaşmıştır.\"", transliteration: "Kul asa en yekune radife lekum ba'dullezi testa'cilun."},
  {id: 3232, sure_no: 73, ayet_no: 73, sure_adi: "Neml", text: "وَاِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُونَ", translation: "Senin Rabb'in İnsanlara karşı lütuf sahibidir. Ne var ki onların çoğu şükretmiyorlar.", transliteration: "Ve inne rabbeke le zu fadlın alen nasi ve lakinne ekserehum la yeşkurun."},
  {id: 3233, sure_no: 74, ayet_no: 74, sure_adi: "Neml", text: "وَاِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ", translation: "Rabb'in, onların kalplerinde gizlediklerini de açıkladıklarını da kesin olarak bilmektedir.", transliteration: "Ve inne rabbeke le ya'lemu ma tukinnu suduruhum ve ma yu'linun."},
  {id: 3234, sure_no: 75, ayet_no: 75, sure_adi: "Neml", text: "وَمَا مِنْ غَٓائِبَةٍ فِي السَّمَٓاءِ وَالْاَرْضِ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍ", translation: "Gökte ve yerde gayb olan hiçbir şey yoktur ki apaçık bir Kitap'ta olmasın.", transliteration: "Ve ma min gaibetin fis semai vel ardı illa fi kitabin mubin."},
  {id: 3235, sure_no: 76, ayet_no: 76, sure_adi: "Neml", text: "اِنَّ هٰذَا الْقُرْاٰنَ يَقُصُّ عَلٰى بَن۪ٓي اِسْرَٓائ۪لَ اَكْثَرَ الَّذ۪ي هُمْ ف۪يهِ يَخْتَلِفُونَ", translation: "Bu Kur'an; İsrailoğulları'na, hakkında ayrılığa düştükleri pek çok şeyi açıklıyor.", transliteration: "İnne hazel kur'ane yakussu ala beni israile ekserellezi hum fihi yahtelifun."},
  {id: 3236, sure_no: 77, ayet_no: 77, sure_adi: "Neml", text: "وَاِنَّهُ لَهُدًى وَرَحْمَةٌ لِلْمُؤْمِن۪ينَ", translation: "O, Mü'minler için yol gösterici ve rahmettir.", transliteration: "Ve innehu le huden ve rahmetun lil mu'minin."},
  {id: 3237, sure_no: 78, ayet_no: 78, sure_adi: "Neml", text: "اِنَّ رَبَّكَ يَقْض۪ي بَيْنَهُمْ بِحُكْمِه۪ۚ وَهُوَ الْعَز۪يزُ الْعَل۪يمُۚ", translation: "Rabb'in, onların arasında hükmünü verecek. O, Mutlak Üstün Olan'dır, Her Şeyi Bilen'dir.", transliteration: "İnne rabbeke yakdi beynehum bi hukmihi, ve huvel azizul alim."},
  {id: 3238, sure_no: 79, ayet_no: 79, sure_adi: "Neml", text: "فَتَوَكَّلْ عَلَى اللّٰهِۜ اِنَّكَ عَلَى الْحَقِّ الْمُب۪ينِ", translation: "Öyleyse Allah'a tevekkül et. Kuşkusuz sen, apaçık gerçek üzerindesin.", transliteration: "Fe tevekkel alallah, inneke alel hakkıl mubin."},
  {id: 3239, sure_no: 80, ayet_no: 80, sure_adi: "Neml", text: "اِنَّكَ لَا تُسْمِــعُ الْمَوْتٰى وَلَا تُسْمِــعُ الصُّمَّ الدُّعَٓاءَ اِذَا وَلَّوْا مُدْبِر۪ينَ", translation: "Sen, ölülere duyuramazsın. Mesajı aldırmayan sağırlara da duyuramazsın.", transliteration: "İnneke la tusmiul mevta ve la tusmius summed duae iza vellev mudbirin."},
  {id: 3240, sure_no: 81, ayet_no: 81, sure_adi: "Neml", text: "وَمَٓا اَنْتَ بِهَادِي الْعُمْيِ عَنْ ضَلَالَتِهِمْۜ اِنْ تُسْمِــعُ اِلَّا مَنْ يُؤْمِنُ بِاٰيَاتِنَا فَهُمْ مُسْلِمُونَ", translation: "Sen körleri, sapkınlıktan çevirip doğru yola iletemezsin; sen ancak ayetlerimize iman edip teslim olanlara gerçeği duyurabilirsin.", transliteration: "Ve ma ente bi hadil umyi an dalaletihim, in tusmiu illa men yu'minu bi ayatina fe hum muslimun."},
  {id: 3241, sure_no: 82, ayet_no: 82, sure_adi: "Neml", text: "وَاِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ اَخْرَجْنَا لَهُمْ دَٓابَّةً مِنَ الْاَرْضِ تُكَلِّمُهُمْۙ اَنَّ النَّاسَ كَانُوا بِاٰيَاتِنَا لَا يُوقِنُونَ۟", translation: "Üzerlerine söz gerçekleştiği zaman, onlara yerden bir dabbe çıkarırız. Kuşkusuz o, onlara, İnsanların ayetlerimize inanmadıklarını söyler.", transliteration: "Ve iza vakaal kavlu aleyhim ahracna lehum dabbeten minel ardı tukellimuhum ennen nase kanu bi ayatina la yukınun."},
  {id: 3242, sure_no: 83, ayet_no: 83, sure_adi: "Neml", text: "وَيَوْمَ نَحْشُرُ مِنْ كُلِّ اُمَّةٍ فَوْجاً مِمَّنْ يُكَذِّبُ بِاٰيَاتِنَا فَهُمْ يُوزَعُونَ", translation: "O gün bütün toplumlardan, ayetlerimizi yalanlayanları gruplar halinde toplayacağız. Artık onlar sımsıkı tutuklanıp sevk edilirler.", transliteration: "Ve yevme nahşuru min kulli ummetin fevcen mimmen yukezzibu bi ayatina fe hum yuzeun."},
  {id: 3243, sure_no: 84, ayet_no: 84, sure_adi: "Neml", text: "حَتّٰٓى اِذَا جَٓاؤُ۫ قَالَ اَكَذَّبْتُمْ بِاٰيَات۪ي وَلَمْ تُح۪يطُوا بِهَا عِلْماً اَمَّاذَا كُنْتُمْ تَعْمَلُونَ", translation: "Ve geldikleri zaman: \"Onu bilgi ile kavramadınız da mı ayetlerimi yalanladınız? Yoksa başka bir neden mi var?\" dedi.", transliteration: "Hatta iza cau kale e kezzebtum bi ayati ve lem tuhitu biha ılmen em maza kuntum ta'melun."},
  {id: 3244, sure_no: 85, ayet_no: 85, sure_adi: "Neml", text: "وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوا فَهُمْ لَا يَنْطِقُونَ", translation: "Ve haksızlıkları nedeniyle üzerlerine söz gerçekleşmiş oldu. Artık onlar konuşamazlar.", transliteration: "Ve vakaal kavlu aleyhim bima zalemu fe hum la yentıkun."},
  {id: 3245, sure_no: 86, ayet_no: 86, sure_adi: "Neml", text: "اَلَمْ يَرَوْا اَنَّا جَعَلْنَا الَّيْلَ لِيَسْكُنُوا ف۪يهِ وَالنَّهَارَ مُبْصِراًۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ", translation: "Dinginlik için geceyi nasıl karanlık, iş görmeleri için gündüzü de nasıl aydınlık yaptığımızı görmüyorlar mı? Kuşkusuz bunda iman eden toplum için ayetler vardır.", transliteration: "E lem yerev enna cealnel leyle li yeskunu fihi ven nehara mubsıra, inne fi zalike le ayatin li kavmin yu'minun."},
  {id: 3246, sure_no: 87, ayet_no: 87, sure_adi: "Neml", text: "وَيَوْمَ يُنْفَخُ فِي الصُّورِ فَفَزِ  عَ مَنْ فِي السَّمٰوَاتِ وَمَنْ فِي الْاَرْضِ اِلَّا مَنْ شَٓاءَ اللّٰهُۜ وَكُلٌّ اَتَوْهُ دَاخِر۪ينَ", translation: "Sur'a üflendiği gün, Allah'ın dilediği kimseler hariç, göklerde ve yerde olanlar dehşete kapılacak ve hepsi aşağılanmış olarak geleceklerdir.", transliteration: "Ve yevme yunfehu fis suri fe fezia men fis semavati ve men fil ardı illa men şaallah, ve kullun etevhu dahırin."},
  {id: 3247, sure_no: 88, ayet_no: 88, sure_adi: "Neml", text: "وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَهِيَ تَمُرُّ مَرَّ السَّحَابِۜ صُنْعَ اللّٰهِ الَّـذ۪ٓي اَتْقَنَ كُلَّ شَيْءٍۜ اِنَّهُ خَب۪يرٌ بِمَا تَفْعَلُونَ", translation: "Dağı görürsün, onu hareketsiz sanırsın. Oysaki o bulut gibi hareket eder. Her şeyi mükemmel yapan Allah'ın işidir bu. Kuşkusuz O, yaptığınız her şeyden haberdardır.", transliteration: "Ve terel cibale tahsebuha camideten ve hiye temurru merres sehab, sun'allahillezi etkane kulle şey', innehu habirun bima tef'alun."},
  {id: 3248, sure_no: 89, ayet_no: 89, sure_adi: "Neml", text: "مَنْ جَٓاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَاۚ وَهُمْ مِنْ فَزَعٍ يَوْمَئِذٍ اٰمِنُونَ", translation: "Kim iyi şeylerle geldiyse, onun için getirdiğinden daha iyisi vardır. Onlar, O Gün dehşetten güvende olanlardır.", transliteration: "Men cae bil haseneti fe lehu hayrun minha, ve hum min fezein yevmeizin aminun."},
  {id: 3249, sure_no: 90, ayet_no: 90, sure_adi: "Neml", text: "وَمَنْ جَٓاءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِي النَّارِۜ هَلْ تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُونَ", translation: "Ve kim kötü şeylerle gelirse, onlar da yüzüstü ateşe atılır. Yaptıklarınızın karşılığından başka bir şeyle mi cezalandırılıyorsunuz?", transliteration: "Ve men cae bis seyyieti fe kubbet vucuhuhum fin nar, hel tuczevne illa ma kuntum ta'melun."},
  {id: 3250, sure_no: 91, ayet_no: 91, sure_adi: "Neml", text: "اِنَّـمَٓا اُمِرْتُ اَنْ اَعْبُدَ رَبَّ هٰذِهِ الْبَلْدَةِ الَّذ۪ي حَرَّمَهَا وَلَهُ كُلُّ شَيْءٍۘ وَاُمِرْتُ اَنْ اَكُونَ مِنَ الْمُسْلِم۪ينَۙ", translation: "Ben, sadece bu beldeye saygınlık veren Rabb'e kul olmakla emrolundum! Her şey O'nundur. Ve ben Müslim olmakla emrolundum!", transliteration: "İnnema umirtu en a'bude rabbe hazihil beldetillezi harremeha ve lehu kullu şey'in ve umırtu en ekune minel muslimin."},
  {id: 3251, sure_no: 92, ayet_no: 92, sure_adi: "Neml", text: "وَاَنْ اَتْلُوَا الْقُرْاٰنَۚ فَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَد۪ي لِنَفْسِه۪ۚ وَمَنْ ضَلَّ فَقُلْ اِنَّـمَٓا اَنَا۬ مِنَ الْمُنْذِر۪ينَ", translation: "Ve Kur'an okumakla. Her kim doğru yola yönelirse, kendisi için yönelmiş olur. Ve sapkın yolu seçenlere: \"Ben yalnızca bir uyarıcıyım.\" de.", transliteration: "Ve en etluvel kur'an, fe menihteda fe innema yehtedi li nefsih, ve men dalle fe kul innema ene minel munzirin."},
  {id: 3252, sure_no: 93, ayet_no: 93, sure_adi: "Neml", text: "وَقُلِ الْحَمْدُ لِلّٰهِ سَيُر۪يكُمْ اٰيَاتِه۪ فَتَعْرِفُونَهَاۜ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ", translation: "Ve de ki: \"Hamd Allah'adır. Size ayetlerini gösterecek, siz de onları tanıyacaksınız.\" Senin Rabb'in, yaptıklarınızdan habersiz değildir.", transliteration: "Ve kulil hamdu lillahi seyurikum ayatihi fe ta'rifuneha, ve ma rabbuke bi gafilin amma ta'melun."},
  {id: 3253, sure_no: 1, ayet_no: 1, sure_adi: "Kasas", text: "طٰسٓمٓۜ", translation: "Ta. Sin. Mim.", transliteration: "Ta sin mim."},
  {id: 3254, sure_no: 2, ayet_no: 2, sure_adi: "Kasas", text: "تِلْكَ اٰيَاتُ الْكِتَابِ الْمُب۪ينِ", translation: "İşte bunlar gerçeği apaçık ortaya koyan Kitap'ın ayetleridir.", transliteration: "Tilke ayatul kitabil mubin."},
  {id: 3255, sure_no: 3, ayet_no: 3, sure_adi: "Kasas", text: "نَتْلُوا عَلَيْكَ مِنْ نَبَأِ مُوسٰى وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُؤْمِنُونَ", translation: "Biz, sana iman eden bir halk için ders alsınlar diye Musa ve Firavun'un haberinden bir kısım gerçeği anlatacağız.", transliteration: "Netlu aleyke min nebei musa ve fir'avne bil hakkı li kavmin yu'minun."},
  {id: 3256, sure_no: 4, ayet_no: 4, sure_adi: "Kasas", text: "اِنَّ فِرْعَوْنَ عَلَا فِي الْاَرْضِ وَجَعَلَ اَهْلَهَا شِيَعاً يَسْتَضْعِفُ طَٓائِفَةً مِنْهُمْ يُذَبِّـحُ اَبْنَٓاءَهُمْ وَيَسْتَحْـي۪ نِسَٓاءَهُمْۜ اِنَّهُ كَانَ مِنَ الْمُفْسِد۪ينَ", translation: "Gerçek şu ki: Firavun, büyüklenerek halkını sınıflara ayırdı. Onlardan bir sınıfı güçsüz düşürerek ezmek istiyor; erkek çocuklarını boğazlatıyor ve kadınlarını sağ bırakıyordu. Kuşkusuz o, bozgunculardandı.", transliteration: "İnne fir'avne ala fil ardı ve ceale ehleha şiyean yestad'ıfu taifeten minhum yuzebbihu ebnaehum ve yestahyi nisaehum, innehu kane minel mufsidin."},
  {id: 3257, sure_no: 5, ayet_no: 5, sure_adi: "Kasas", text: "وَنُر۪يدُ اَنْ نَمُنَّ عَلَى الَّذ۪ينَ اسْتُضْعِفُوا فِي الْاَرْضِ وَنَجْعَلَهُمْ اَئِمَّةً وَنَجْعَلَهُمُ الْوَارِث۪ينَۙ", translation: "Biz ise, yeryüzünde ezilenlere iyilik yapmak ve onları önderler kılmak ve varisler yapmak istiyoruz.", transliteration: "Ve nuridu en nemunne alellezinestud'ıfu fil ardı ve nec'alehum eimmeten ve nec'alehumul varisin."},
  {id: 3258, sure_no: 6, ayet_no: 6, sure_adi: "Kasas", text: "وَنُمَكِّنَ لَهُمْ فِي الْاَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا مِنْهُمْ مَا كَانُوا يَحْذَرُونَ", translation: "Ve onları yeryüzünde iktidar yapalım, Firavun'a, Haman'a ve ikisinin ordusuna, onlardan çekindikleri şeyleri gösterelim.", transliteration: "Ve numekkine lehum fil ardı ve nuriye fir'avne ve hamane ve cunudehuma minhum ma kanu yahzerun."},
  {id: 3259, sure_no: 7, ayet_no: 7, sure_adi: "Kasas", text: "وَاَوْحَيْنَٓا اِلٰٓى اُمِّ مُوسٰٓى اَنْ اَرْضِع۪يهِۚ فَاِذَا خِفْتِ عَلَيْهِ فَاَلْق۪يهِ فِي الْيَمِّ وَلَا تَخَاف۪ي وَلَا تَحْزَن۪يۚ اِنَّا رَٓادُّوهُ اِلَيْكِ وَجَاعِلُوهُ مِنَ الْمُرْسَل۪ينَ", translation: "Musa'nın annesine vahyettik: \"Onu emzir! Eğer onun için korkarsan, onu suya bırak. Ve korkma, üzülme. Biz, onu sana döndüreceğiz. Ve onu Resullerden yapacağız.\"", transliteration: "Ve evhayna ila ummi musa en erdıih, fe iza hıfti aleyhi fe elkihi fil yemmi ve la tehafi ve la tahzeni, inna radduhu ileyki ve caıluhu minel murselin."},
  {id: 3260, sure_no: 8, ayet_no: 8, sure_adi: "Kasas", text: "فَالْتَقَطَهُٓ اٰلُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُواًّ وَحَزَناًۜ اِنَّ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا كَانُوا خَاطِـ۪ٔينَ", translation: "Derken Firavun ailesi, kendileri için bir düşman ve başlarına dert olacak olan onu, buluntu olarak aldı. Firavun, Haman ve askerleri yanlış yaptılar.", transliteration: "Feltekatahu alu fir'avne li yekune lehum aduvven ve hazena, inne fir'avne ve hamane ve cunudehuma kanu hatıin."},
  {id: 3261, sure_no: 9, ayet_no: 9, sure_adi: "Kasas", text: "وَقَالَتِ امْرَاَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ ل۪ي وَلَكَۜ لَا تَقْتُلُوهُۗ عَسٰٓى اَنْ يَنْفَعَنَٓا اَوْ نَتَّخِذَهُ وَلَداً وَهُمْ لَا يَشْعُرُونَ", translation: "Ve Firavun'un karısı: \"Bana ve sana göz aydınlığı olsun. Onu öldürmeyin, belki bize yararı olur. Veya onu evlat ediniriz.\" dedi. Onlar, olacakların ayırdında değillerdi.", transliteration: "Ve kaletimraetu fir'avne kurretu aynin li ve lek, la taktuluhu asa en yenfeana ev nettehızehu veleden ve hum la yeş'urun."},
  {id: 3262, sure_no: 10, ayet_no: 10, sure_adi: "Kasas", text: "وَاَصْبَحَ فُؤٰادُ اُمِّ مُوسٰى فَارِغاًۜ اِنْ كَادَتْ لَتُبْد۪ي بِه۪ لَوْلَٓا اَنْ رَبَطْنَا عَلٰى قَلْبِهَا لِتَكُونَ مِنَ الْمُؤْمِن۪ينَ", translation: "Musa'nın annesi gönlü boş olarak sabahladı. Eğer Biz sözümüze güvenenlerden olması için gönlünü pekiştirmeseydik az kalsın durumu açığa vuracaktı.", transliteration: "Ve asbaha fuadu ummi musa fariga, in kadet le tubdi bihi lev la en rabatna ala kalbiha li tekune minel mu'minin."},
  {id: 3263, sure_no: 11, ayet_no: 11, sure_adi: "Kasas", text: "وَقَالَتْ لِاُخْتِه۪ قُصّ۪يهِۘ فَبَصُرَتْ بِه۪ عَنْ جُنُبٍ وَهُمْ لَا يَشْعُرُونَۙ", translation: "Kız kardeşine: \"Onu izlemeye al.\" dedi. O da onlar farkında olmadan onu uzaktan izledi.", transliteration: "Ve kalet li uhtihi kussihi fe besurat bihi an cunubin ve hum la yeş'urun."},
  {id: 3264, sure_no: 12, ayet_no: 12, sure_adi: "Kasas", text: "وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ اَدُلُّكُمْ عَلٰٓى اَهْلِ بَيْتٍ يَكْفُلُونَهُ لَكُمْ وَهُمْ لَهُ نَاصِحُونَ", translation: "Biz, onun, sütannelerinin sütünü emmemesini sağladık. Bunun üzerine: \"Size, onun bakımını üstlenecek ve onu iyi yetiştirecek bir aile göstereyim mi?\" dedi.", transliteration: "Ve harremna aleyhil meradıa min kablu fe kalet hel edullukum ala ehli beytin yekfulunehu lekum ve hum lehu nasıhun."},
  {id: 3265, sure_no: 13, ayet_no: 13, sure_adi: "Kasas", text: "فَرَدَدْنَاهُ اِلٰٓى اُمِّه۪ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ۟", translation: "Derken gözü aydın olsun, üzülmesin diye onu annesine kavuşturduk. Ve Allah'ın sözünün gerçek olduğunu fakat onların çoğunun bunu anlamadığını bilsin.", transliteration: "Fe redednahu ila ummihi key tekarra aynuha ve la tahzene ve li ta'leme enne va'dallahi hakkun ve lakinne ekserehum la ya'lemun."},
  {id: 3266, sure_no: 14, ayet_no: 14, sure_adi: "Kasas", text: "وَلَمَّا بَلَغَ اَشُدَّهُ وَاسْتَوٰٓى اٰتَيْنَاهُ حُكْماً وَعِلْماًۜ وَكَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "Ve güçlü çağına erişip olgunlaşınca, ona hikmet ve ilim verdik. İyileri işte böyle ödüllendiririz.", transliteration: "Ve lemma belega eşuddehu vesteva ateynahu hukmen ve ilma, ve kezalike neczil muhsinin."},
  {id: 3267, sure_no: 15, ayet_no: 15, sure_adi: "Kasas", text: "وَدَخَلَ الْمَد۪ينَةَ عَلٰى ح۪ينِ غَفْلَةٍ مِنْ اَهْلِهَا فَوَجَدَ ف۪يهَا رَجُلَيْنِ يَقْتَتِلَانِۘ هٰذَا مِنْ ش۪يعَتِه۪ وَهٰذَا مِنْ عَدُوِّه۪ۚ فَاسْتَغَاثَهُ الَّذ۪ي مِنْ ش۪يعَتِه۪ عَلَى الَّذ۪ي مِنْ عَدُوِّه۪ۙ فَوَكَزَهُ مُوسٰى فَقَضٰى عَلَيْهِۘ قَالَ هٰذَا مِنْ عَمَلِ الشَّيْطَانِۜ اِنَّهُ عَدُوٌّ مُضِلٌّ مُب۪ينٌ", translation: "Musa, şehir halkının habersiz olduğu bir sırada şehre girdi. Orada biri kendi tarafından, diğeri de düşman olan taraftan kavga eden iki kişi buldu. Kendi tarafından olan, diğerine karşı ondan yardım istedi. Bunun üzerine Musa onu yumrukladı ve ölümüne neden oldu. Musa: \"Bu şeytanın işindendir. Kuşkusuz o, saptırıcı apaçık bir düşmandır.\" dedi.", transliteration: "Ve dehalel medinete ala hini gafletin min ehliha fe vecede fiha raculeyni yaktetilani haza min şiatihi ve haza min aduvvih, festegasehullezi min şiatihi alellezi min aduvvihi, fe vekezehu musa fe kada aleyhi kale haza min ameliş şeytan, innehu aduvvun mudillun mubin."},
  {id: 3268, sure_no: 16, ayet_no: 16, sure_adi: "Kasas", text: "قَالَ رَبِّ اِنّ۪ي ظَلَمْتُ نَفْس۪ي فَاغْفِرْ ل۪ي فَغَفَرَ لَهُۜ اِنَّهُ هُوَ الْغَفُورُ الرَّح۪يمُ", translation: "\"Rabb'im! Doğrusu ben kendime haksızlık yaptım. Artık beni bağışla.\" dedi. Böylece onu bağışladı. Gerçekten O; Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Kale rabbi inni zalemtu nefsi fagfirli fe gafera leh, innehu huvel gafurur rahim."},
  {id: 3269, sure_no: 17, ayet_no: 17, sure_adi: "Kasas", text: "قَالَ رَبِّ بِمَٓا اَنْعَمْتَ عَلَيَّ فَلَنْ اَكُونَ ظَه۪يراً لِلْمُجْرِم۪ينَ", translation: "\"Rabb'im! Bana verdiğin nimet sayesinde, bundan böyle asla mücrimlere arka çıkmayacağım.\" dedi.", transliteration: "Kale rabbi bima en'amte aleyye fe len ekune zahiren lil mucrimin."},
  {id: 3270, sure_no: 18, ayet_no: 18, sure_adi: "Kasas", text: "فَاَصْبَحَ فِي الْمَد۪ينَةِ خَٓائِفاً يَتَرَقَّبُ فَاِذَا الَّذِي اسْتَنْصَرَهُ بِالْاَمْسِ يَسْتَصْرِخُهُۜ قَالَ لَهُ مُوسٰٓى اِنَّكَ لَغَوِيٌّ مُب۪ينٌ", translation: "Korku içinde etrafı kollayarak şehirde sabahladı. Bir de baktı ki dün yardım isteyen kişi yine yardım istiyor. Musa: \"Sen apaçık bir azgınsın!\" dedi.", transliteration: "Fe asbaha fil medineti haifen yeterakkabu fe izellezistensarahu bil emsi yestasrihuh, kale lehu musa inneke le gaviyyun mubin."},
  {id: 3271, sure_no: 19, ayet_no: 19, sure_adi: "Kasas", text: "فَلَمَّٓا اَنْ اَرَادَ اَنْ يَبْطِشَ بِالَّذ۪ي هُوَ عَدُوٌّ لَهُمَاۙ قَالَ يَا مُوسٰٓى اَتُر۪يدُ اَنْ تَقْتُلَن۪ي كَمَا قَتَلْتَ نَفْساً بِالْاَمْسِۗ اِنْ تُر۪يدُ اِلَّٓا اَنْ تَكُونَ جَبَّاراً فِي الْاَرْضِ وَمَا تُر۪يدُ اَنْ تَكُونَ مِنَ الْمُصْلِح۪ينَ", translation: "İkisinin de düşmanı olan adamı yakalamak istediğinde: \"Ey Musa! Dün öldürdüğün kimse gibi beni de mi öldürmek istiyorsun? Sen yalnızca yeryüzünde bir zorba olmak istiyorsun, arayı düzelticilerden olmak istemiyorsun.\" dedi.", transliteration: "Fe lemma en erade en yabtışe billezi huve aduvvun lehuma kale ya musa e turidu en taktuleni kema katelte nefsen bil emsi in turidu illa en tekune cebbaren fil ardı ve ma turidu en tekune minel muslihin."},
  {id: 3272, sure_no: 20, ayet_no: 20, sure_adi: "Kasas", text: "وَجَٓاءَ رَجُلٌ مِنْ اَقْصَا الْمَد۪ينَةِ يَسْعٰىۘ قَالَ يَا مُوسٰٓى اِنَّ الْمَلَاَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَاخْرُجْ اِنّ۪ي لَكَ مِنَ النَّاصِح۪ينَ", translation: "Şehrin diğer tarafından bir adam koşarak geldi. \"Ey Musa! Meleler senin öldürülmen konusunda görüşme yapıyorlar. Derhal kaybol. Seni iyiliğin için uyarıyorum.\" dedi.", transliteration: "Ve cae raculun min aksal medineti yes'a kale ya musa innel melee ye'temirune bike li yaktuluke fahruc inni leke minen nasıhin."},
  {id: 3273, sure_no: 21, ayet_no: 21, sure_adi: "Kasas", text: "فَخَرَجَ مِنْهَا خَٓائِفاً يَتَرَقَّبُۘ قَالَ رَبِّ نَجِّن۪ي مِنَ الْقَوْمِ الظَّالِم۪ينَ۟", translation: "Bunun üzerine korku içinde etrafı kollayarak oradan ayrıldı: \"Rabb'im! Beni bu zalim halktan kurtar.\" dedi.", transliteration: "Fe harece minha haifen yeterakkabu, kale rabbi neccini minel kavmiz zalimin."},
  {id: 3274, sure_no: 22, ayet_no: 22, sure_adi: "Kasas", text: "وَلَمَّا تَوَجَّهَ تِلْقَٓاءَ مَدْيَنَ قَالَ عَسٰى رَبّ۪ٓي اَنْ يَهْدِيَن۪ي سَوَٓاءَ السَّب۪يلِ", translation: "Medyen tarafına yöneldiğinde: \"Umarım Rabb'im bana bir çıkış yolu gösterir.\" dedi.", transliteration: "Ve lemma teveccehe tilkae medyene kale asa rabbi en yehdiyeni sevaes sebil."},
  {id: 3275, sure_no: 23, ayet_no: 23, sure_adi: "Kasas", text: "وَلَمَّا وَرَدَ مَٓاءَ مَدْيَنَ وَجَدَ عَلَيْهِ اُمَّةً مِنَ النَّاسِ يَسْقُونَۘ وَوَجَدَ مِنْ دُونِهِمُ امْرَاَتَيْنِ تَذُودَانِۚ قَالَ مَا خَطْبُكُمَاۜ قَالَتَا لَا نَسْق۪ي حَتّٰى يُصْدِرَ الرِّعَٓاءُ وَاَبُونَا شَيْخٌ كَب۪يرٌ", translation: "Medyen suyuna vardığında, orada hayvanlarını sulamakta olan birçok İnsan gördü. Bir de sürülerini sulamayı gözetleyen iki kadın vardı. Onlara: \"Neden böyle duruyorsunuz?\" dedi. \"Çobanlar sulayıp çekilmeden biz sulayamayız. Babamız çok ihtiyardır.\" dediler.", transliteration: "Ve lemma verede mae medyene vecede aleyhi ummeten minen nasi yeskun, ve vecede min dunihimumreeteyni tezudan, kale ma hatbukuma, kaleta la neski hatta yusdirar riau ve ebuna şeyhun kebir."},
  {id: 3276, sure_no: 24, ayet_no: 24, sure_adi: "Kasas", text: "فَسَقٰى لَهُمَا ثُمَّ تَوَلّٰٓى اِلَى الظِّلِّ فَقَالَ رَبِّ اِنّ۪ي لِمَٓا اَنْزَلْتَ اِلَيَّ مِنْ خَيْرٍ فَق۪يرٌ", translation: "Bunun üzerine ikisi için hayvanları suladı. Sonra gölgeye çekildi. \"Rabb'im! Ben, bana hayır olarak bağışlayacağın her şeye muhtacım.\" dedi.", transliteration: "Fe seka lehuma summe tevella ilez zılli fe kale rabbi inni lima enzelte ileyye min hayrin fakir."},
  {id: 3277, sure_no: 25, ayet_no: 25, sure_adi: "Kasas", text: "فَجَٓاءَتْهُ اِحْدٰيهُمَا تَمْش۪ي عَلَى اسْتِحْيَٓاءٍۘ قَالَتْ اِنَّ اَب۪ي يَدْعُوكَ لِيَجْزِيَكَ اَجْرَ مَا سَقَيْتَ لَنَاۜ فَلَمَّا جَٓاءَهُ وَقَصَّ عَلَيْهِ الْقَصَصَۙ قَالَ لَا تَخَفْ۠ نَجَوْتَ مِنَ الْقَوْمِ الظَّالِم۪ينَ", translation: "Derken, iki kızdan biri utana utana Musa'ya geldi: \"Babam, bizim yerimize sulamanın karşılığını vermek için seni çağırıyor.\" dedi. Musa, yanına vararak başından geçenleri anlattı. \"Korkma! Zalimlerden kurtuldun.\" dedi.", transliteration: "Fe caethu ıhdahuma temşi alestihyain, kalet inne ebi yed'uke li yecziyeke ecra ma sekayte lena, fe lemma caehu ve kassa aleyhil kasasa kale la tehaf, necevte minel kavmiz zalimin."},
  {id: 3278, sure_no: 26, ayet_no: 26, sure_adi: "Kasas", text: "قَالَتْ اِحْدٰيهُمَا يَٓا اَبَتِ اسْتَأْجِرْهُۘ اِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْاَم۪ينُ", translation: "İki kızından biri: \"Ey babacığım! Onu ücretle tut. Gerçekten o, ücretle tutacaklarının en iyisi, güçlüsü ve güvenilirdir.\" dedi.", transliteration: "Kalet ıhdahuma ya ebetiste'cirhu inne hayra meniste'certel kaviyyul emin."},
  {id: 3279, sure_no: 27, ayet_no: 27, sure_adi: "Kasas", text: "قَالَ اِنّ۪ٓي اُر۪يدُ اَنْ اُنْكِحَكَ اِحْدَى ابْنَتَيَّ هَاتَيْنِ عَلٰٓى اَنْ تَأْجُرَن۪ي ثَمَانِيَ حِجَجٍۚ فَاِنْ اَتْمَمْتَ عَشْراً فَمِنْ عِنْدِكَۚ وَمَٓا اُر۪يدُ اَنْ اَشُقَّ عَلَيْكَۜ سَتَجِدُن۪ٓي اِنْ شَٓاءَ اللّٰهُ مِنَ الصَّالِح۪ينَ", translation: "Bu iki kızımdan birini, bana sekiz hacc boyunca çalışmana karşılık seninle evlendirmek istiyorum. Eğer on yıla tamamlarsan sen bilirsin. Sana rahatsızlık vermek istemem. İnşaallah beni salihlerden bulacaksın.\" dedi.", transliteration: "Kale inni uridu en unkihake ihdebneteyye hateyni ala en te'cureni semaniye hıcec, fe in etmemte aşran fe min indik, ve ma uridu en eşukka aleyk, seteciduni in şaallahu mines salihin."},
  {id: 3280, sure_no: 28, ayet_no: 28, sure_adi: "Kasas", text: "قَالَ ذٰلِكَ بَيْن۪ي وَبَيْنَكَۜ اَيَّمَا الْاَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّۜ وَاللّٰهُ عَلٰى مَا نَقُولُ وَك۪يلٌ۟", translation: "\"Bu ikimizin arasındadır. Bu iki süreden hangisini tamamlarsam tamamlıyayım bana kırgınlık olmasın. Konuştuklarımıza Allah tanıktır.\" dedi.", transliteration: "Kale zalike beyni ve beynek, eyyemel eceleyni kadaytu fe la udvane aleyy, vallahu ala ma nekulu vekil."},
  {id: 3281, sure_no: 29, ayet_no: 29, sure_adi: "Kasas", text: "فَلَمَّا قَضٰى مُوسَى الْاَجَلَ وَسَارَ بِاَهْلِه۪ٓ اٰنَسَ مِنْ جَانِبِ الطُّورِ نَاراًۚ قَالَ لِاَهْلِهِ امْكُثُٓوا اِنّ۪ٓي اٰنَسْتُ نَاراً لَعَلّ۪ٓي اٰت۪يكُمْ مِنْهَا بِخَبَرٍ اَوْ جَذْوَةٍ مِنَ النَّارِ لَعَلَّكُمْ تَصْطَلُونَ", translation: "Musa, süresini tamamlayınca, ailesi ile yola çıktı. Tur tarafında bir ateş fark etti. Ailesine: \"Bekleyin. Ben bir ateş gördüm. Belki size ondan bir haber veya ısınmanız için bir ateş getiririm.\" dedi.", transliteration: "Fe lemma kada musel ecele ve sare bi ehlihi anese min canibit turi nara, kale li ehlihimkusu inni anestu naren lealli atikum minha bi haberin ev cezvetin minen nari leallekum testalun."},
  {id: 3282, sure_no: 30, ayet_no: 30, sure_adi: "Kasas", text: "فَلَمَّٓا اَتٰيهَا نُودِيَ مِنْ شَاطِئِ الْوَادِ الْاَيْمَنِ فِي الْبُقْعَةِ الْمُبَارَكَةِ مِنَ الشَّجَرَةِ اَنْ يَا مُوسٰٓى اِنّ۪ٓي اَنَا اللّٰهُ رَبُّ الْعَالَم۪ينَۙ", translation: "Oraya vardığında, kutlu yerdeki vadinin sağ kıyısındaki bir ağaçtan kendisine seslenildi: \"Ey Musa! Ben, alemlerin Rabb'i Allah'ım.\"", transliteration: "Fe lemma etaha nudiye min şatııl vadil eymeni fil buk'atil mubareketi mineş şecerati en ya musa inni enallahu rabbul alemin."},
  {id: 3283, sure_no: 31, ayet_no: 31, sure_adi: "Kasas", text: "وَاَنْ اَلْقِ عَصَاكَۜ فَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَٓانٌّ وَلّٰى مُدْبِراً وَلَمْ يُعَقِّبْۜ يَا مُوسٰٓى اَقْبِلْ وَلَا تَخَفْ۠ اِنَّكَ مِنَ الْاٰمِن۪ينَ", translation: "\"Asanı yere bırak!\" Onun yılan gibi hareket ettiğini görünce, dönüp arkasına bakmadan kaçtı. \"Ey Musa, dön! Korkma, güvende olanlardansın.\"", transliteration: "Ve en elkı asak, fe lemma reaha tehtezzu keenneha cannun vella mudbiren ve lem yuakkıb, ya musa akbil ve la tehaf, inneke minel aminin."},
  {id: 3284, sure_no: 32, ayet_no: 32, sure_adi: "Kasas", text: "اُسْلُكْ يَدَكَ ف۪ي جَيْبِكَ تَخْرُجْ بَيْضَٓاءَ مِنْ غَيْرِ سُٓوءٍۘ وَاضْمُمْ اِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ فَذَانِكَ بُرْهَانَانِ مِنْ رَبِّكَ اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ۜ اِنَّهُمْ كَانُوا قَوْماً فَاسِق۪ينَ", translation: "\"Elini koynuna sok, kusursuz beyaz olarak çıkar. Telaşlanma, kollarını kendine çek. Bu ikisi, senin Rabb'inden, Firavun ve onun melelerine iki burhandır. Kuşkusuz ki onlar, sapkın bir kavimdir.\"", transliteration: "Usluk yedeke fi ceybike tahruc beydae min gayri su, vadmum ileyke cenahake miner rehbi fe zanike burhanani min rabbike ila fir'avne ve melaih, innehum kanu kavmen fasikin."},
  {id: 3285, sure_no: 33, ayet_no: 33, sure_adi: "Kasas", text: "قَالَ رَبِّ اِنّ۪ي قَتَلْتُ مِنْهُمْ نَفْساً فَاَخَافُ اَنْ يَقْتُلُونِ", translation: "\"Rabb'im! Onlardan birisini öldürdüm. Onların da beni öldürmelerinden korkuyorum.\" dedi.", transliteration: "Kale rabbi inni kateltu minhum nefsen fe ehafu en yaktulun."},
  {id: 3286, sure_no: 34, ayet_no: 34, sure_adi: "Kasas", text: "وَاَخ۪ي هٰرُونُ هُوَ اَفْصَحُ مِنّ۪ي لِسَاناً فَاَرْسِلْهُ مَعِيَ رِدْءاً يُصَدِّقُن۪يۘ اِنّ۪ٓي اَخَافُ اَنْ يُكَذِّبُونِ", translation: "\"Kardeşim Harun'un konuşması benden daha düzgündür. Onu da beni doğrulayan bir yardımcı olarak, benimle birlikte gönder. Doğrusu beni yalanlamalarından korkuyorum.\"", transliteration: "Ve ahi harunu huve efsahu minni lisanen fe ersilhu maiye rid'en yusaddıkuni, inni ehafu en yukezzibun."},
  {id: 3287, sure_no: 35, ayet_no: 35, sure_adi: "Kasas", text: "قَالَ سَنَشُدُّ عَضُدَكَ بِاَخ۪يكَ وَنَجْعَلُ لَكُمَا سُلْطَاناً فَلَا يَصِلُونَ اِلَيْكُمَا بِاٰيَاتِنَاۚ اَنْتُمَا وَمَنِ اتَّـبَعَكُمَا الْغَالِبُونَ", translation: "\"Senin gücünü kardeşinle arttıracağız. İkinizi de sultan kılacağız. Onlar, ayetlerimizden dolayı size karşı koyamayacaklar. Siz ikiniz ve sizi izleyenler kazanacaksınız.\" dedi.", transliteration: "Kale se neşuddu adudeke bi ahike ve nec'alu lekuma sultanen fe la yasılune ileykuma bi ayatina, entuma ve menittebeakumel galibun."},
  {id: 3288, sure_no: 36, ayet_no: 36, sure_adi: "Kasas", text: "فَلَمَّا جَٓاءَهُمْ مُوسٰى بِاٰيَاتِنَا بَيِّنَاتٍ قَالُوا مَا هٰذَٓا اِلَّا سِحْرٌ مُفْتَرًى وَمَا سَمِعْنَا بِهٰذَا ف۪ٓي اٰبَٓائِنَا الْاَوَّل۪ينَ", translation: "Musa onlara apaçık ayetlerimizi iletince: \"Bu, uydurulmuş bir sihirden başka bir şey değildir. Biz önceki atalarımızdan böyle bir şey duymadık.\" dediler.", transliteration: "Fe lemma caehum musa bi ayatina beyyinatin kalu ma haza illa sihrun mufteren ve ma semi'na bi haza fi abainel evvelin."},
  {id: 3289, sure_no: 37, ayet_no: 37, sure_adi: "Kasas", text: "وَقَالَ مُوسٰى رَبّ۪ٓي اَعْلَمُ بِمَنْ جَٓاءَ بِالْهُدٰى مِنْ عِنْدِه۪ وَمَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِۜ اِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ", translation: "Musa: \"Rabb'im, kimin kendi katından doğru yolu göstermek için geldiğini ve hayırlı sonucun kimin olacağını en iyi bilendir. Kuşkusuz zalimler kurtuluşa eremezler.\" dedi.", transliteration: "Ve kale musa rabbi a'lemu bi men cae bil huda min indihi ve men tekunu lehu akıbetud dar, innehu la yuflihuz zalimun."},
  {id: 3290, sure_no: 38, ayet_no: 38, sure_adi: "Kasas", text: "وَقَالَ فِرْعَوْنُ يَٓا اَيُّهَا الْمَلَأُ مَا عَلِمْتُ لَكُمْ مِنْ اِلٰهٍ غَيْر۪يۚ فَاَوْقِدْ ل۪ي يَا هَامَانُ عَلَى الطّ۪ينِ فَاجْعَلْ ل۪ي صَرْحاً لَعَلّ۪ٓي اَطَّلِعُ اِلٰٓى اِلٰهِ مُوسٰىۙ وَاِنّ۪ي لَاَظُنُّهُ مِنَ الْكَاذِب۪ينَ", translation: "Firavun: \"Ey halkımın meleleri! Ben, sizin için benden başka ilah bilmiyorum. Ey Haman, benim için çamur üzerine hemen bir ateş yak; bana yüksek bir kule yap. Belki Musa'nın ilahı ile karşılaşırım. Onun yalancılardan olduğunu zannediyorum.\" dedi.", transliteration: "Ve kale fir'avnu ya eyyuhel meleu ma alimtu lekum min ilahin gayri, fe evkıd li ya hamanu alet tini fec'al li sarhan lealli attaliu ila ilahi musa ve inni le ezunnuhu minel kazibin."},
  {id: 3291, sure_no: 39, ayet_no: 39, sure_adi: "Kasas", text: "وَاسْتَكْبَرَ هُوَ وَجُنُودُهُ فِي الْاَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّٓوا اَنَّهُمْ اِلَيْنَا لَا يُرْجَعُونَ", translation: "O ve ordusu yeryüzünde haksız yere büyüklendiler ve gerçekten de Biz'e döndürülmeyeceklerini sandılar.", transliteration: "Vestekbere huve ve cunuduhu fil ardı bi gayril hakkı ve zannu ennehum ileyna la yurceun."},
  {id: 3292, sure_no: 40, ayet_no: 40, sure_adi: "Kasas", text: "فَاَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِم۪ينَ", translation: "Sonra onu ve askerlerini yakalayıp suya gömdük. Bak bakalım, zalimlerin sonunun nasıl olduğuna!", transliteration: "Fe ehaznahu ve cunudehu fe nebeznahum fil yemm, fanzur keyfe kane akıbetuz zalimin."},
  {id: 3293, sure_no: 41, ayet_no: 41, sure_adi: "Kasas", text: "وَجَعَلْنَاهُمْ اَئِمَّةً يَدْعُونَ اِلَى النَّارِۚ وَيَوْمَ الْقِيٰمَةِ لَا يُنْصَرُونَ", translation: "Onları, ateşe çağıran önderler yaptık. Ve kıyamet günü onlara yardım edilmez.", transliteration: "Ve cealnahum eimmeten yed'une ilen nar, ve yevmel kıyameti la yunsarun."},
  {id: 3294, sure_no: 42, ayet_no: 42, sure_adi: "Kasas", text: "وَاَتْبَعْنَاهُمْ ف۪ي هٰذِهِ الدُّنْيَا لَعْنَةًۚ وَيَوْمَ الْقِيٰمَةِ هُمْ مِنَ الْمَقْبُوح۪ينَ۟", translation: "Bu dünyada arkalarına bir lanet taktık. Kıyamet günü ise onlar çirkinleştirilmiş olanlardandır.", transliteration: "Ve etba'nahum fi hazihid dunya la'neh ve yevmel kıyameti hum minel makbuhin."},
  {id: 3295, sure_no: 43, ayet_no: 43, sure_adi: "Kasas", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ مِنْ بَعْدِ مَٓا اَهْلَكْنَا الْقُرُونَ الْاُو۫لٰى بَصَٓائِرَ لِلنَّاسِ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ يَتَذَكَّرُونَ", translation: "Ant olsun ki önceki nesilleri yok ettikten sonra; Musa'ya, öğüt alırlar diye, basiretler, doğru yolu gösteren ve rahmet olarak Kitap'ı verdik.", transliteration: "Ve lekad ateyna musel kitabe min ba'di ma ehleknel kurunel ula besaire lin nasi ve huden ve rahmeten leallehum yetezekkerun."},
  {id: 3296, sure_no: 44, ayet_no: 44, sure_adi: "Kasas", text: "وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ اِذْ قَضَيْنَٓا اِلٰى مُوسَى الْاَمْرَ وَمَا كُنْتَ مِنَ الشَّاهِد۪ينَۙ", translation: "Sen, Musa'ya o emri yerine getirdiğimizde batı tarafında değildin. Ve sen, tanık olanlardan da değildin.", transliteration: "Ve ma kunte bi canibil garbiyyi iz kadayna ila musel emre ve ma kunte mineş şahidin."},
  {id: 3297, sure_no: 45, ayet_no: 45, sure_adi: "Kasas", text: "وَلٰكِنَّٓا اَنْشَأْنَا قُرُوناً فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُۚ وَمَا كُنْتَ ثَاوِياً ف۪ٓي اَهْلِ مَدْيَنَ تَتْلُوا عَلَيْهِمْ اٰيَاتِنَاۙ وَلٰكِنَّا كُنَّا مُرْسِل۪ينَ", translation: "Üstelik nice nesiller inşa ettik. Üzerlerinden ömürler geçti. Medyen Halkı arasında ayetlerimizi okuyan da sen değildin. Fakat seni Resul olarak gönderen Biziz.", transliteration: "Ve lakinna enşe'na kurunen fe tetavele aleyhimul umur, ve ma kunte saviyen fi ehli medyene tetlu aleyhim ayatina, ve lakinna kunna mursilin."},
  {id: 3298, sure_no: 46, ayet_no: 46, sure_adi: "Kasas", text: "وَمَا كُنْتَ بِجَانِبِ الطُّورِ اِذْ نَادَيْنَا وَلٰكِنْ رَحْمَةً مِنْ رَبِّكَ لِتُنْذِرَ قَوْماً مَٓا اَتٰيهُمْ مِنْ نَذ۪يرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ", translation: "Ve seslendiğimiz zaman, Tur'un yanında da değildin. Fakat senden önce, kendilerine bir uyarıcı gelmemiş olan halkı uyarman için, seni Rabb'inden bir rahmet olarak gönderdik. Umulur ki öğüt alırlar.", transliteration: "Ve ma kunte bi canibit turi iz nadeyna, ve lakin rahmeten min rabbike li tunzire kavmen ma etahum min nezirin min kablike leallehum yetezekkerun."},
  {id: 3299, sure_no: 47, ayet_no: 47, sure_adi: "Kasas", text: "وَلَوْلَٓا اَنْ تُص۪يبَهُمْ مُص۪يبَةٌ بِمَا قَدَّمَتْ اَيْد۪يهِمْ فَيَقُولُوا رَبَّـنَا لَوْلَٓا اَرْسَلْتَ اِلَيْنَا رَسُولاً فَنَتَّبِعَ اٰيَاتِكَ وَنَكُونَ مِنَ الْمُؤْمِن۪ينَ", translation: "Eğer elleriyle sundukları nedeniyle onlara bir bela isabet ederse: \"Rabb'imiz! Keşke bize bir Resul gönderseydin böylece biz, Sen'in ayetlerine tabi olur ve iman edenlerden olurduk.\" diyemesinler diye.", transliteration: "Ve lev la en tusibehum musibetun bima kaddemet eydihim fe yekulu rabbena lev la erselte ileyna resulen fe nettebia ayatike ve nekune minel mu'minin."},
  {id: 3300, sure_no: 48, ayet_no: 48, sure_adi: "Kasas", text: "فَلَمَّا جَٓاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوا لَوْلَٓا اُو۫تِيَ مِثْلَ مَٓا اُو۫تِيَ مُوسٰىۜ اَوَلَمْ يَكْفُرُوا بِمَٓا اُو۫تِيَ مُوسٰى مِنْ قَبْلُۚ قَالُوا سِحْرَانِ تَظَاهَرَا۠ وَقَالُٓوا اِنَّا بِكُلٍّ كَافِرُونَ", translation: "Ne var ki onlara Hakk gelince: \"Musa'ya verilen gibi ona da verilseydi olmaz mıydı?\" dediler. Oysaki Musa'ya verilenleri de daha önce inkar etmediler mi? \"Bunlar, birbirine destek olan iki sihirbaz.\" dediler. Biz hepsini inkar edenleriz.\" dediler.", transliteration: "Fe lemma caehumul hakku min indina kalu lev la utiye misle ma utıye musa, e ve lem yekfuru bima utiye musa min kabl, kalu sihrani tezaher, ve kalu inna bi kullin kafirun."},
  {id: 3301, sure_no: 49, ayet_no: 49, sure_adi: "Kasas", text: "قُلْ فَأْتُوا بِكِتَابٍ مِنْ عِنْدِ اللّٰهِ هُوَ اَهْدٰى مِنْهُمَٓا اَتَّبِعْهُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "De ki: \"Eğer doğru söyleyenlerdenseniz, Allah'ın katından, o ikisinden daha doğru yola ileten bir kitap getirin de ben de ona uyayım.\"", transliteration: "Kul fe'tu bi kitabin min indillahi huve ehda min huma ettebi' hu in kuntum sadikin."},
  {id: 3302, sure_no: 50, ayet_no: 50, sure_adi: "Kasas", text: "فَاِنْ لَمْ يَسْتَج۪يبُوا لَكَ فَاعْلَمْ اَنَّمَا يَتَّبِعُونَ اَهْوَٓاءَهُمْۜ وَمَنْ اَضَلُّ مِمَّنِ اتَّبَعَ هَوٰيهُ بِغَيْرِ هُدًى مِنَ اللّٰهِۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ", translation: "Buna rağmen eğer sana uymazlarsa, bil ki onlar hevalarına uymaktadırlar. Allah'tan bir yol gösterici olmaksızın kendi hevasına uyandan daha sapkın kim olabilir? Kuşkusuz Allah, zalim bir toplumu doğru yola iletmez.", transliteration: "Fe in lem yestecibu leke fa'lem ennema yettebiune ehvaehum, ve men edallu mimmenittebea hevahu bi gayri huden minallah, innallahe la yehdil kavmez zalimin."},
  {id: 3303, sure_no: 51, ayet_no: 51, sure_adi: "Kasas", text: "وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَۜ‌۟", translation: "Ant olsun! Öğüt alırlar diye, onlara Söz'ü ardı ardına yolladık.", transliteration: "Ve lekad vassalna lehumul kavle leallehum yetezekkerun."},
  {id: 3304, sure_no: 52, ayet_no: 52, sure_adi: "Kasas", text: "اَلَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ مِنْ قَبْلِه۪ هُمْ بِه۪ يُؤْمِنُونَ", translation: "Ondan önce kendilerine Kitap verdiklerimiz, O'na inanırlar.", transliteration: "Ellezine ateynahumul kitabe min kablihi hum bihi yu'minun."},
  {id: 3305, sure_no: 53, ayet_no: 53, sure_adi: "Kasas", text: "وَاِذَا يُتْلٰى عَلَيْهِمْ قَالُٓوا اٰمَنَّا بِه۪ٓ اِنَّهُ الْحَقُّ مِنْ رَبِّنَٓا اِنَّا كُنَّا مِنْ قَبْلِه۪ مُسْلِم۪ينَ", translation: "Onlara okunduğu zaman: \"Ona inandık. Kuşkusuz o, Rabb'imizden gelen Hakk'tır. Biz ondan önce de teslim olanlardık.\" dediler.", transliteration: "Ve iza yutla aleyhim kalu amenna bihi innehul hakku min rabbina inna kunna min kablihi muslimin."},
  {id: 3306, sure_no: 54, ayet_no: 54, sure_adi: "Kasas", text: "اُو۬لٰٓئِكَ يُؤْتَوْنَ اَجْرَهُمْ مَرَّتَيْنِ بِمَا صَبَرُوا وَيَدْرَؤُ۫نَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ", translation: "İşte onlara sabrettiklerinden dolayı, ödülleri iki kat verilecektir. Onlar, kötülüğü iyilik ile savarlar. Kendilerine verdiğimiz rızıktan infak ederler.", transliteration: "Ulaike yu'tevne ecrehum merreteyni bima saberu ve yedraune bil hasenetis seyyiete ve mimma razaknahum yunfikun."},
  {id: 3307, sure_no: 55, ayet_no: 55, sure_adi: "Kasas", text: "وَاِذَا سَمِعُوا اللَّغْوَ اَعْرَضُوا عَنْهُ وَقَالُوا لَـنَٓا اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۘ سَلَامٌ عَلَيْكُمْۘ لَا نَبْتَغِي الْجَاهِل۪ينَ", translation: "Onlar, kötü ve çirkin bir söz duydukları zaman, ondan yüz çevirirler. Ve: \"Bizim yaptıklarımız bize, sizin yaptıklarınız da size aittir. Selam üzerinize olsun. Bizim cahillerle işimiz olmaz.\" derler.", transliteration: "Ve iza semiullagve a'radu anhu, ve kalu lena a'maluna ve lekum a'malukum selamun aleykum la nebtegil cahilin."},
  {id: 3308, sure_no: 56, ayet_no: 56, sure_adi: "Kasas", text: "اِنَّكَ لَا تَهْد۪ي مَنْ اَحْبَبْتَ وَلٰكِنَّ اللّٰهَ يَهْد۪ي مَنْ يَشَٓاءُۚ وَهُوَ اَعْلَمُ بِالْمُهْتَد۪ينَ", translation: "Sen, sevdiğin kimseyi doğru yola iletemezsin; ama Allah hak edeni doğru yola iletir. O, doğru yolu seçenleri iyi bilir.", transliteration: "İnneke la tehdi men ahbebte ve lakinnallahe yehdi men yeşa', ve huve a'lemu bil muhtedin."},
  {id: 3309, sure_no: 57, ayet_no: 57, sure_adi: "Kasas", text: "وَقَالُٓوا اِنْ نَتَّبِـعِ الْهُدٰى مَعَكَ نُتَخَطَّفْ مِنْ اَرْضِنَاۜ اَوَلَمْ نُمَكِّنْ لَهُمْ حَرَماً اٰمِناً يُجْبٰٓى اِلَيْهِ ثَمَرَاتُ كُلِّ شَيْءٍ رِزْقاً مِنْ لَدُنَّا وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "\"Eğer seninle beraber doğru yoldan gidersek, yerimizden kovuluruz.\" dediler. Katımızdan bir rızık olarak her türlü ürünün kendilerine getirildiği, saygı duyulan kutlu yere güven içinde yerleştirmedik mi? Ne var ki onların çoğu bilmiyorlar.", transliteration: "Ve kalu in nettebiıl huda meake nutehattaf min ardına, e ve lem numekkin lehum haremen aminen yucba ileyhi semeratu kulli şey'in rızkan min ledunna ve lakinne ekserehum la ya'lemun."},
  {id: 3310, sure_no: 58, ayet_no: 58, sure_adi: "Kasas", text: "وَكَمْ اَهْلَكْنَا مِنْ قَرْيَةٍ بَطِرَتْ مَع۪يشَتَهَاۚ فَتِلْكَ مَسَاكِنُهُمْ لَمْ تُسْكَنْ مِنْ بَعْدِهِمْ اِلَّا قَل۪يلاًۜ وَكُنَّا نَحْنُ الْوَارِث۪ينَ", translation: "Şımararak, geçindikleri şeylere şükretmeyen nice kenti yıkıma uğrattık. İşte bunlar, onların yerleşim yerleri! Kendilerinden sonra pek az kullanılan evleri. Şimdi onların hepsi bize kaldı.", transliteration: "Ve kem ehlekna min karyetin batırat maişeteha, fe tilke mesakinuhum lem tusken min ba'dihim illa kalila, ve kunna nahnul varisin."},
  {id: 3311, sure_no: 59, ayet_no: 59, sure_adi: "Kasas", text: "وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرٰى حَتّٰى يَبْعَثَ ف۪ٓي اُمِّهَا رَسُولاً يَتْلُوا عَلَيْهِمْ اٰيَاتِنَاۚ وَمَا كُنَّا مُهْلِكِي الْقُرٰٓى اِلَّا وَاَهْلُهَا ظَالِمُونَ", translation: "Rabb'in, ülkelerin ana merkezlerine kendilerine ayetlerimizi okuyan bir Resul göndermedikçe, yıkıma uğratıcı olmadı. Biz, halkı zulmetmedikçe, kentleri yıkıma uğratıcı değiliz.", transliteration: "Ve ma kane rabbuke muhlikel kura hatta yeb'ase fi ummiha resulen yetlu aleyhim ayatina, ve ma kunna muhlikil kura illa ve ehluha zalimun."},
  {id: 3312, sure_no: 60, ayet_no: 60, sure_adi: "Kasas", text: "وَمَٓا اُو۫ت۪يتُمْ مِنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا وَز۪ينَتُهَاۚ وَمَا عِنْدَ اللّٰهِ خَيْرٌ وَاَبْقٰىۜ اَفَلَا تَعْقِلُونَ۟", translation: "Size verilen her şey, aslında dünya hayatının geçimliğidir ve ziynetidir. Allah'ın katında olanlar ise daha hayırlı ve daha kalıcıdır. Hala aklınızı kullanmayacak mısınız?", transliteration: "Ve ma utitum min şey'in fe metaul hayatid dunya ve zinetuha ve ma indallahi hayrun ve ebka, e fe la ta'kılun."},
  {id: 3313, sure_no: 61, ayet_no: 61, sure_adi: "Kasas", text: "اَفَمَنْ وَعَدْنَاهُ وَعْداً حَسَناً فَهُوَ لَاق۪يهِ كَمَنْ مَتَّعْنَاهُ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيٰمَةِ مِنَ الْمُحْضَر۪ينَ", translation: "Öyleyse, kendisine iyi bir söz verdiğimiz ve mutlaka ona kavuşacak olan kimse, dünya hayatının geçimliği ile geçimlerini sağladığımız, sonra kıyamet günü hazır bulundurulacak olan kimse gibi midir?", transliteration: "E fe men vaadnahu va'den hasenen fe huve lakihi ke men metta'nahu metaal hayatid dunya summe huve yevmel kıyameti minel muhdarin."},
  {id: 3314, sure_no: 62, ayet_no: 62, sure_adi: "Kasas", text: "وَيَوْمَ يُنَاد۪يهِمْ فَيَقُولُ اَيْنَ شُرَكَٓاءِيَ الَّذ۪ينَ كُنْتُمْ تَزْعُمُونَ", translation: "O Gün, onlara seslenilir: \"Benim ortaklarım olduğunu ileri sürdükleriniz hani nerede?\"", transliteration: "Ve yevme yunadihim fe yekulu eyne şurekaiyellezine kuntum tez'umun."},
  {id: 3315, sure_no: 63, ayet_no: 63, sure_adi: "Kasas", text: "قَالَ الَّذ۪ينَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هٰٓؤُ۬لَٓاءِ الَّذ۪ينَ اَغْوَيْنَاۚ اَغْوَيْنَاهُمْ كَمَا غَوَيْنَاۚ تَبَرَّأْنَٓا اِلَيْكَۘ مَا كَانُٓوا اِيَّانَا يَعْبُدُونَ", translation: "Haklarında sözün gerçekleştiği kimseler: \"Rabb'imiz! İşte bunlar azdırdığımız kimselerdir. Kendimiz azdığımız gibi, onları da azdırdık. Uzak olduğumuzu Sana arz ederiz. Zaten onlar, bize kulluk etmiyorlardı.\" dediler.", transliteration: "Kalellezine hakka aleyhimul kavlu rabbena haulaillezine agveyna, agveynahum kema gaveyna, teberre'na ileyke ma kanu iyyana ya'budun."},
  {id: 3316, sure_no: 64, ayet_no: 64, sure_adi: "Kasas", text: "وَق۪يلَ ادْعُوا شُرَكَٓاءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَج۪يبُوا لَهُمْ وَرَاَوُا الْعَذَابَۚ لَوْ اَنَّهُمْ كَانُوا يَهْتَدُونَ", translation: "Ve onlara: \"Ortaklarınızı çağırın.\" denildi. Onlar da çağırdılar. Ancak cevap alamadılar ve azapla karşı karşıya kaldılar. Keşke zamanında doğru yolu seçselerdi.", transliteration: "Ve kiled'u şurekaekum fe deavhum fe lem yestecibu lehum ve reavul azab, lev ennehum kanu yehtedun."},
  {id: 3317, sure_no: 65, ayet_no: 65, sure_adi: "Kasas", text: "وَيَوْمَ يُنَاد۪يهِمْ فَيَقُولُ مَاذَٓا اَجَبْتُمُ الْمُرْسَل۪ينَ", translation: "Ve o gün Allah, onlara seslenecek: \"Gönderilen Resullere ne cevap verdiniz?\"", transliteration: "Ve yevme yunadihim fe yekulu maza ecebtumul murselin."},
  {id: 3318, sure_no: 66, ayet_no: 66, sure_adi: "Kasas", text: "فَعَمِيَتْ عَلَيْهِمُ الْاَنْـبَٓاءُ يَوْمَئِذٍ فَهُمْ لَا يَتَسَٓاءَلُونَ", translation: "O Gün, bütün özür yolları onlara kapanır. Artık onlar sorulmazlar.", transliteration: "Fe amiyet aleyhimul enbau yevme izin fe hum la yetesaelun."},
  {id: 3319, sure_no: 67, ayet_no: 67, sure_adi: "Kasas", text: "فَاَمَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحاً فَعَسٰٓى اَنْ يَكُونَ مِنَ الْمُفْلِح۪ينَ", translation: "Fakat tevbe eden, iman eden ve salihatı yapan kimse, kurtuluşa erenlerden olacağını umabilir.", transliteration: "Fe emma men tabe ve amene ve amile salihan fe asa en yekune minel muflihin."},
  {id: 3320, sure_no: 68, ayet_no: 68, sure_adi: "Kasas", text: "وَرَبُّكَ يَخْلُقُ مَا يَشَٓاءُ وَيَخْتَارُۜ مَا كَانَ لَهُمُ الْخِيَرَةُۜ سُبْحَانَ اللّٰهِ وَتَعَالٰى عَمَّا يُشْرِكُونَ", translation: "Ve Rabb'in, dilediği şeyi yaratır ve onlar için hayırlı olanı seçer. Seçim onların değildir. Allah, onların ortak koştuklarından münezzehtir ve yüceler yücesidir.", transliteration: "Ve rabbuke yahluku ma yeşau ve yahtar, ma kane lehumul hıyarat, subhanallahi ve teala amma yuşrikun."},
  {id: 3321, sure_no: 69, ayet_no: 69, sure_adi: "Kasas", text: "وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ", translation: "Ve Rabb'in, onların sinelerinde gizledikleri şeyleri de açığa vurdukları şeyleri de bilir.", transliteration: "Ve rabbuke ya'lemu ma tukinnu suduruhum ve ma yu'linun."},
  {id: 3322, sure_no: 70, ayet_no: 70, sure_adi: "Kasas", text: "وَهُوَ اللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۜ  لَهُ الْحَمْدُ فِي الْاُو۫لٰى وَالْاٰخِرَةِۘ وَلَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُونَ", translation: "O, Kendisinden başka ilah olmayan Allah'tır. Her işin başında da sonunda da tüm övgüler onun içindir. Ve karar O'na aittir. Ve O'na döndürüleceksiniz.", transliteration: "Ve huvallahu la ilahe illa huve, lehul hamdu fil ula vel ahırati ve lehul hukmu ve ileyhi turceun."},
  {id: 3323, sure_no: 71, ayet_no: 71, sure_adi: "Kasas", text: "قُلْ اَرَاَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ الَّيْلَ سَرْمَداً اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْت۪يكُمْ بِضِيَٓاءٍۜ اَفَلَا تَسْمَعُونَ", translation: "Baksanıza! Eğer Allah, üzerinize geceyi, kıyamet gününe kadar aralıksız devam ettirse, Allah'tan başka size aydınlığı verecek olan ilah kimdir? Hala gerçeğe kulak vermeyecek misiniz?", transliteration: "Kul e reeytum in cealallahu aleykumul leyle sermeden ila yevmil kıyameti men ilahun gayrullahi ye'tikum bi dıya', e fe la tesme'un."},
  {id: 3324, sure_no: 72, ayet_no: 72, sure_adi: "Kasas", text: "قُلْ اَرَاَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ النَّهَارَ سَرْمَداً اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْت۪يكُمْ بِلَيْلٍ تَسْكُنُونَ ف۪يهِۜ اَفَلَا تُبْصِرُونَ", translation: "Baksanıza! Eğer Allah, üzerinize gündüzü kıyamet gününe kadar aralıksız devam ettirse, Allah'tan başka size içinde dinlendiğiniz geceyi getirecek ilah kimdir? Hala gerçeği görmeyecek misiniz?", transliteration: "Kul e reeytum in cealallahu aleykumun nehare sermeden ila yevmil kıyameti men ilahun gayrullahi ye'tikum bi leylin teskunune fih, e fe la tubsırun."},
  {id: 3325, sure_no: 73, ayet_no: 73, sure_adi: "Kasas", text: "وَمِنْ رَحْمَتِه۪ جَعَلَ لَكُمُ الَّيْلَ وَالنَّهَارَ لِتَسْكُنُوا ف۪يهِ وَلِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ", translation: "Gece ve gündüzün olması O'nun rahmetindendir; dinlenmeniz için ve lütfundan rızkınızı temin etmeniz için geceyi ve gündüzü düzenledi. Umulur ki şükredersiniz.", transliteration: "Ve min rahmetihi ceale lekumul leyle ven nehare li teskunu fihi ve li tebtegu min fadlihi ve leallekum teşkurun."},
  {id: 3326, sure_no: 74, ayet_no: 74, sure_adi: "Kasas", text: "وَيَوْمَ يُنَاد۪يهِمْ فَيَقُولُ اَيْنَ شُرَكَٓاءِيَ الَّذ۪ينَ كُنْتُمْ تَزْعُمُونَ", translation: "O Gün onlara seslenecek: \"Ortaklarım olduğunu iddia ettikleriniz hani nerede?\" diye soracak.", transliteration: "Ve yevme yunadihim fe yekulu eyne şurekaiyellezine kuntum tez'umun."},
  {id: 3327, sure_no: 75, ayet_no: 75, sure_adi: "Kasas", text: "وَنَزَعْنَا مِنْ كُلِّ اُمَّةٍ شَه۪يداً فَقُلْنَا هَاتُوا بُرْهَانَكُمْ فَعَلِمُٓوا اَنَّ الْحَقَّ لِلّٰهِ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ۟", translation: "Her ümmetten bir tanık çıkarırız. Sonra da: \"Burhanlarınızı getirin.\" deriz. Böylece hakikatin Allah'a ait olduğunu anlarlar. Uydurdukları şeyler, onlardan ayrılıp kaybolurlar.", transliteration: "Ve neza'na min kulli ummetin şehiden fe kulna hatu burhanekum fe alimu ennel hakka lillahi ve dalle anhum ma kanu yefterun."},
  {id: 3328, sure_no: 76, ayet_no: 76, sure_adi: "Kasas", text: "اِنَّ قَارُونَ كَانَ مِنْ قَوْمِ مُوسٰى فَبَغٰى عَلَيْهِمْۖ وَاٰتَيْنَاهُ مِنَ الْكُنُوزِ مَٓا اِنَّ مَفَاتِحَهُ لَتَنُٓوأُ بِالْعُصْبَةِ اُو۬لِي الْقُوَّةِۗ اِذْ قَالَ لَهُ قَوْمُهُ لَا تَفْرَحْ اِنَّ اللّٰهَ لَا يُحِبُّ الْفَرِح۪ينَ", translation: "Karun, Musa'nın halkından birisiydi. Halkına karşı azgınlaştı. Ona öyle hazineler vermiştik ki, onların anahtarlarını güçlü bir topluluk zor taşıyordu. Halkı ona: \"Şımarma! Allah şımaranları sevmez.\" demişti.", transliteration: "İnne karune kane min kavmi musa, fe bega aleyhim, ve ateynahu minel kunuzi ma inne mefatihahu le tenuu bil usbeti ulil kuvveh, iz kale lehu kavmuhu la tefrah innallahe la yuhıbbul ferihin."},
  {id: 3329, sure_no: 77, ayet_no: 77, sure_adi: "Kasas", text: "وَابْتَغِ ف۪يمَٓا اٰتٰيكَ اللّٰهُ الدَّارَ الْاٰخِرَةَ وَلَا تَنْسَ نَص۪يبَكَ مِنَ الدُّنْيَا وَاَحْسِنْ كَمَٓا اَحْسَنَ اللّٰهُ اِلَيْكَ وَلَا تَبْغِ الْفَسَادَ فِي الْاَرْضِۜ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُفْسِد۪ينَ", translation: "\"Allah'ın sana verdikleri ile ahiret yurdunu elde etmeye çalış. Ve dünyadan da nasibini unutma. Allah'ın sana iyilikte bulunduğu gibi, sen de İnsanlara iyilikte bulun. Ve yeryüzünde bozgunculuk yapma. Allah, bozguncuları sevmez.\"", transliteration: "Vebtegı fima atakellahud darel ahırete ve la tense nasibekemined dunya ve ahsin kema ahsenallahu ileyke ve la tebgıl fesade fil ard, innallahe la yuhıbbul mufsidin."},
  {id: 3330, sure_no: 78, ayet_no: 78, sure_adi: "Kasas", text: "قَالَ اِنَّـمَٓا اُو۫ت۪يتُهُ عَلٰى عِلْمٍ عِنْد۪يۜ اَوَلَمْ يَعْلَمْ اَنَّ اللّٰهَ قَدْ اَهْلَكَ مِنْ قَبْلِه۪ مِنَ الْقُرُونِ مَنْ هُوَ اَشَدُّ مِنْهُ قُوَّةً وَاَكْثَرُ جَمْعاًۜ وَلَا يُسْـَٔلُ عَنْ ذُنُوبِهِمُ الْمُجْرِمُونَ", translation: "Karun: \"Bu serveti bilgili olmam sayesinde elde ettim.\" dedi. Allah'ın daha önce ondan daha güçlü olan ve ondan daha çok taraftarı ve birikimi olan kuşakları yıkıma uğratmış olduğunu bilmiyor mu? Mücrimlere suçlarından sorulmaz.", transliteration: "Kale innema utituhu ala ilmin indi, e ve lem ya'lem ennellahe kad ehleke min kablihi minel kuruni men huve eşeddu minhu kuvveten ve ekseru cem'a, ve la yus'elu an zunubihimul mucrimun."},
  {id: 3331, sure_no: 79, ayet_no: 79, sure_adi: "Kasas", text: "فَخَرَجَ عَلٰى قَوْمِه۪ ف۪ي ز۪ينَتِه۪ۜ قَالَ الَّذ۪ينَ يُر۪يدُونَ الْحَيٰوةَ الدُّنْيَا يَا لَيْتَ لَنَا مِثْلَ مَٓا اُو۫تِيَ قَارُونُۙ اِنَّهُ لَذُو حَظٍّ عَظ۪يمٍ", translation: "Karun, ziynetli bir şekilde halkının karşısına çıktı. Dünya hayatına düşkünlüğü olanlar: \"Keşke bizim de Karun gibi varlığımız olsaydı! Gerçekten o, çok kısmetli biri.\" diye iç geçirdiler.", transliteration: "Fe harece ala kavmihi fi zinetih, kalellezine yuridunel hayated dunya ya leyte lena misle ma utiye karunu innehu le zu hazzın azim."},
  {id: 3332, sure_no: 80, ayet_no: 80, sure_adi: "Kasas", text: "وَقَالَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللّٰهِ خَيْرٌ لِمَنْ اٰمَنَ وَعَمِلَ صَالِحاًۚ وَلَا يُلَقّٰيهَٓا اِلَّا الصَّابِرُونَ", translation: "İlim verilmiş olanlar: \"Size yazıklar olsun! İman eden ve salihatı yapan kimselere Allah'ın vereceği ödül daha hayırlıdır. Buna sabredenlerden başkası kavuşamaz.\" dediler.", transliteration: "Ve kalellezine utul ilme veylekum sevabullahi hayrun li men amene ve amile saliha ve la yulekkaha illes sabirun."},
  {id: 3333, sure_no: 81, ayet_no: 81, sure_adi: "Kasas", text: "فَخَسَفْنَا بِه۪ وَبِدَارِهِ الْاَرْضَ فَمَا كَانَ لَهُ مِنْ فِئَةٍ يَنْصُرُونَهُ مِنْ دُونِ اللّٰهِۗ وَمَا كَانَ مِنَ الْمُنْتَصِر۪ينَ", translation: "Sonra, onu ve yurdunu yerin dibine geçirdik. Allah'a karşı kendisine yardım edecek bir taraftar da olmadı. Yardım edilenlerden de olmadı.", transliteration: "Fe hasefna bihi ve bidarihil arda fe ma kane lehu min fietin yensurunehu min dunillahi ve ma kane minel muntasırin."},
  {id: 3334, sure_no: 82, ayet_no: 82, sure_adi: "Kasas", text: "وَاَصْبَحَ الَّذ۪ينَ تَمَنَّوْا مَكَانَهُ بِالْاَمْسِ يَقُولُونَ وَيْكَاَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ مِنْ عِبَادِه۪ وَيَقْدِرُۚ لَوْلَٓا اَنْ مَنَّ اللّٰهُ عَلَيْنَا لَخَسَفَ بِنَاۜ وَيْكَاَنَّهُ لَا يُفْلِحُ الْكَافِرُونَ۟", translation: "Dün, onun yerinde olmayı isteyenler; bugün, \"Demek ki, kullarından dilediğine rızkı genişleten ve ölçülendiren Allah'mış. Eğer Allah bize lütfetmiş olmasaydı, bizi de yerin dibine geçirirdi. Demek ki Kafirler kurtuluşa eremezler.\" dediler.", transliteration: "Ve asbehallezine temennev mekanehu bil emsi yekulune vey keennellahe yebsutur rızka li men yeşau min ıbadihi ve yakdir, lev la en mennallahu aleyna le hasefe bina, vey keennehu la yuflihul kafirun."},
  {id: 3335, sure_no: 83, ayet_no: 83, sure_adi: "Kasas", text: "تِلْكَ الدَّارُ الْاٰخِرَةُ نَجْعَلُهَا لِلَّذ۪ينَ لَا يُر۪يدُونَ عُلُواًّ فِي الْاَرْضِ وَلَا فَسَاداًۜ وَالْعَاقِبَةُ لِلْمُتَّق۪ينَ", translation: "İşte ahiret yurdu! Onu, yeryüzünde büyüklük taslamayan ve bozgunculuk yapmayan kimseler için ayırdık. Gelecek takva sahiplerinindir.", transliteration: "Tilked darul ahıretu nec'aluha lillezine la yuridune uluvven fil ardı ve la fesada, vel akıbetu lil muttekin."},
  {id: 3336, sure_no: 84, ayet_no: 84, sure_adi: "Kasas", text: "مَنْ جَٓاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَاۚ وَمَنْ جَٓاءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذ۪ينَ عَمِلُوا السَّيِّـَٔاتِ اِلَّا مَا كَانُوا يَعْمَلُونَ", translation: "Kim bir iyilik ile gelirse; ona, ondan daha hayırlısı vardır. Kim de bir kötülük ile gelirse; yaptığı kötülük kadar cezalandırılır.", transliteration: "Men cae bil haseneti fe lehu hayrun minha ve men cae bis seyyieti fe la yuczellezine amilus seyyiati illa ma kanu ya'melun."},
  {id: 3337, sure_no: 85, ayet_no: 85, sure_adi: "Kasas", text: "اِنَّ الَّذ۪ي فَرَضَ عَلَيْكَ الْقُرْاٰنَ لَـرَٓادُّكَ اِلٰى مَعَادٍۜ   قُلْ رَبّ۪ٓي اَعْلَمُ مَنْ جَٓاءَ بِالْهُدٰى وَمَنْ هُوَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Sana Kur'an'ı farz kılan, elbette seni dönülecek yere döndürecektir. De ki: \"Kimin doğru yolda olduğunu ve kimin sapkın olduğunu Rabb'im bilir.\"", transliteration: "İnnellezi farada aleykel kur'ane le radduke ila mead, kul rabbi a'lemu men cae bil huda ve men huve fi dalalin mubin."},
  {id: 3338, sure_no: 86, ayet_no: 86, sure_adi: "Kasas", text: "وَمَا كُنْتَ تَرْجُٓوا اَنْ يُلْقٰٓى اِلَيْكَ الْكِتَابُ اِلَّا رَحْمَةً مِنْ رَبِّكَ فَلَا تَكُونَنَّ ظَه۪يراً لِلْكَافِر۪ينَۘ", translation: "Sen, Kitap'ın sana iletileceğini beklemiyordun. O, ancak Rabb'inden bir rahmet olarak verildi. Öyleyse sakın Kafirlere destek olma.", transliteration: "Ve ma kunte tercu en yulka ileykel kitabu illa rahmeten min rabbike fe la tekunenne zahiren lil kafirin."},
  {id: 3339, sure_no: 87, ayet_no: 87, sure_adi: "Kasas", text: "وَلَا يَصُدُّنَّكَ عَنْ اٰيَاتِ اللّٰهِ بَعْدَ اِذْ اُنْزِلَتْ اِلَيْكَ وَادْعُ اِلٰى رَبِّكَ وَلَا تَكُونَنَّ مِنَ الْمُشْرِك۪ينَۚ", translation: "Sana indirilen, Allah'ın ayetleridir. Sakın seni ondan alıkoymasınlar. Sen, İnsanları Rabb'ine çağır. Müşriklerden olma.", transliteration: "Ve la yasuddunneke an ayatillahi ba'de iz unzılet ileyke ved'u ila rabbike ve la tekunenne minel muşrikin."},
  {id: 3340, sure_no: 88, ayet_no: 88, sure_adi: "Kasas", text: "وَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهاً اٰخَرَۢ  لَٓا اِلٰهَ اِلَّا هُوَ۠   كُلُّ شَيْءٍ هَالِكٌ اِلَّا وَجْهَهُۜ  لَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُونَ", translation: "Allah ile birlikte başka ilahtan istekte bulunma. O'ndan başka ilah yoktur. O'nun yüzü hariç her şey yok olucudur. Hüküm O'nundur. Ve O'na döndürüleceksiniz.", transliteration: "Ve la ted'u meallahi ilahen ahar, la ilahe illa hu, kullu şey'in halikun illa vecheh, lehul hukmu ve ileyhi turceun."},
  {id: 3341, sure_no: 1, ayet_no: 1, sure_adi: "Ankebut", text: "الٓمٓ ۠", translation: "Elif. Lam. Mim.", transliteration: "Elif lam mim."},
  {id: 3342, sure_no: 2, ayet_no: 2, sure_adi: "Ankebut", text: "اَحَسِبَ النَّاسُ اَنْ يُتْرَكُٓوا اَنْ يَقُولُٓوا اٰمَنَّا وَهُمْ لَا يُفْتَنُونَ", translation: "İnsanlar, iman ettik demekle fitnelendirilmeden bırakılacaklarını mı sanıyorlar.", transliteration: "E hasiben nasu en yutreku en yekulu amenna ve hum la yuftenun."},
  {id: 3343, sure_no: 3, ayet_no: 3, sure_adi: "Ankebut", text: "وَلَقَدْ فَتَنَّا الَّذ۪ينَ مِنْ قَبْلِهِمْ فَلَيَعْلَمَنَّ اللّٰهُ الَّذ۪ينَ صَدَقُوا وَلَيَعْلَمَنَّ الْكَاذِب۪ينَ", translation: "Ant olsun ki onlardan öncekilerini de fitnelendirmiştik. Böylece Allah, kimin samimi kimin de yalancı olduğunu ortaya çıkarmaktadır.", transliteration: "Ve lekad fetennellezine min kablihim fe le ya'lemennellahullezine sadaku ve le ya'lemenel kazibin."},
  {id: 3344, sure_no: 4, ayet_no: 4, sure_adi: "Ankebut", text: "اَمْ حَسِبَ الَّذ۪ينَ يَعْمَلُونَ السَّيِّـَٔاتِ اَنْ يَسْبِقُونَاۜ سَٓاءَ مَا يَحْكُمُونَ", translation: "Yoksa kötülük yapanlar, Biz'den kurtulacaklarını mı sanıyorlar? Ne kötü bir yargıda bulunuyorlar!", transliteration: "Em hasibellezine ya'melunes seyyiati en yesbikuna, sae ma yahkumun."},
  {id: 3345, sure_no: 5, ayet_no: 5, sure_adi: "Ankebut", text: "مَنْ كَانَ يَرْجُوا لِقَٓاءَ اللّٰهِ فَاِنَّ اَجَلَ اللّٰهِ لَاٰتٍۜ وَهُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Allah'a kavuşacaklarını umanlar bilsinler ki, Allah'ın belirlediği zaman mutlaka gelecektir. O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Men kane yercu likaallahi fe inne ecelallahi leat, ve huves semiul alim."},
  {id: 3346, sure_no: 6, ayet_no: 6, sure_adi: "Ankebut", text: "وَمَنْ جَاهَدَ فَاِنَّمَا يُجَاهِدُ لِنَفْسِه۪ۜ اِنَّ اللّٰهَ لَغَنِيٌّ عَنِ الْعَالَم۪ينَ", translation: "Kim cihad ederse, ancak kendisi için cihad etmiş olur. Allah, kesinlikle hiç kimseye, hiçbir şeye muhtaç değildir.", transliteration: "Ve men cahede fe innema yucahidu li nefsih, innallahe le ganiyyun anil alemin."},
  {id: 3347, sure_no: 7, ayet_no: 7, sure_adi: "Ankebut", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَنَجْزِيَنَّهُمْ اَحْسَنَ الَّذ۪ي كَانُوا يَعْمَلُونَ", translation: "İman eden ve salihatı yapanların kötülüklerine mutlaka küfrederiz. Ve kesinlikle onlara yaptıklarından daha iyisi ile karşılık vereceğiz.", transliteration: "Vellezine amenu ve amilus salihati le nukeffiranne anhum seyyiatihim ve le necziyennehum ahsenellezi kanu ya'melun."},
  {id: 3348, sure_no: 8, ayet_no: 8, sure_adi: "Ankebut", text: "وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ حُسْناًۜ وَاِنْ جَاهَدَاكَ لِتُشْرِكَ ب۪ي مَا لَيْسَ لَكَ بِه۪ عِلْمٌ فَلَا تُطِعْهُمَاۜ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Biz, insana anne ve babasına iyi davranmasını tavsiye ettik. Eğer onlar, hakkında bilgin olmayan bir şey ile Bana şirk koşman için seninle cihad ederlerse, onları dinleme. Dönüşünüz ancak Bana'dır. O zaman yapmış olduklarınızı size haber vereceğim.", transliteration: "Ve vassaynel insane bi valideyhi husna, ve in cahedake li tuşrike bi ma leyse leke bihi ilmun fe la tutı'huma, ileyye merciukum fe unebbiukum bima kuntum ta'melun."},
  {id: 3349, sure_no: 9, ayet_no: 9, sure_adi: "Ankebut", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُدْخِلَنَّهُمْ فِي الصَّالِح۪ينَ", translation: "İman eden ve salihatı yapanları da kesinlikle salihlerin arasına katacağız.", transliteration: "Vellezine amenu ve amilus salihati le nudhılennehum fis salihin."},
  {id: 3350, sure_no: 10, ayet_no: 10, sure_adi: "Ankebut", text: "وَمِنَ النَّاسِ مَنْ يَقُولُ اٰمَنَّا بِاللّٰهِ فَاِذَٓا اُو۫ذِيَ فِي اللّٰهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللّٰهِۜ وَلَئِنْ جَٓاءَ نَصْرٌ مِنْ رَبِّكَ لَيَقُولُنَّ اِنَّا كُنَّا مَعَكُمْۜ اَوَلَيْسَ اللّٰهُ بِاَعْلَمَ بِمَا ف۪ي صُدُورِ الْعَالَم۪ينَ", translation: "Allah'a iman ettiğini söyleyen kimi insanlar vardır ki, Allah yolunda bir eziyet gördükleri zaman, insanların fitnesini Allah'ın azabı ile bir tutarlar. Eğer Rabb'lerinden bir yardım gelecek olsa, kesinlikle, \"Kuşku yok ki biz sizinle beraberdik.\" diyeceklerdir. Oysaki Allah, herkesin göğsünde olanı en iyi bilen değil midir?", transliteration: "Ve minen nasi men yekulu amenna billahi fe iza uziye fillahi ceale fitneten nasi ke azabillah, ve le in cae nasrun min rabbike le yekulunne inna kunna meakum, e ve leysallahu bi a'leme bi ma fi suduril alemin."},
  {id: 3351, sure_no: 11, ayet_no: 11, sure_adi: "Ankebut", text: "وَلَيَعْلَمَنَّ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَلَيَعْلَمَنَّ الْمُنَافِق۪ينَ", translation: "Allah, elbette iman edenleri bilir ve elbette kimlerin de Münafık olduğunu da bilir.", transliteration: "Ve le ya'lemennallahullezine amenu ve le ya'lemennel munafikin."},
  {id: 3352, sure_no: 12, ayet_no: 12, sure_adi: "Ankebut", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا لِلَّذ۪ينَ اٰمَنُوا اتَّبِعُوا سَب۪يلَنَا وَلْنَحْمِلْ خَطَايَاكُمْۜ وَمَا هُمْ بِحَامِل۪ينَ مِنْ خَطَايَاهُمْ مِنْ شَيْءٍۜ اِنَّهُمْ لَكَاذِبُونَ", translation: "Kafirler: İman Edenler'e: \"Bizim yolumuza uyun, sizin yanlışlarınızı biz üstlenelim.\" dediler. Oysa onlar, başkalarının hatalarından hiçbir şey üstlenemezler. Kesinlikle onlar yalancıdırlar.", transliteration: "Ve kalellezine keferu lillezine amenuttebiu sebilena velnahmil hatayakum, ve ma hum bi hamiline min hatayahum min şey', innehum le kazibun."},
  {id: 3353, sure_no: 13, ayet_no: 13, sure_adi: "Ankebut", text: "وَلَيَحْمِلُنَّ اَثْقَالَهُمْ وَاَثْقَالاً مَعَ اَثْقَالِهِمْۘ وَلَيُسْـَٔلُنَّ يَوْمَ الْقِيٰمَةِ عَمَّا كَانُوا يَفْتَرُونَ۟", translation: "Onlar hem kendi yüklerini hem kendi yükleri ile birlikte başka yükleri taşıyacaklar. Kıyamet günü, kesinlikle uydurdukları şeylerden hesaba çekilecekler.", transliteration: "Ve le yahmilunne eskalehum ve eskalen mea eskalihim ve le yus'elunne yevmel kıyameti amma kanu yefterun."},
  {id: 3354, sure_no: 14, ayet_no: 14, sure_adi: "Ankebut", text: "وَلَقَدْ اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ فَلَبِثَ ف۪يهِمْ اَلْفَ سَنَةٍ اِلَّا خَمْس۪ينَ عَاماًۜ فَاَخَذَهُمُ الطُّوفَانُ وَهُمْ ظَالِمُونَ", translation: "Ant olsun ki Nuh'u halkına gönderdik. Onların içinde bin aydan elli yıl eksik kaldı. Sonunda haksızlık edenleri tufan aldı.", transliteration: "Ve lekad erselna nuhan ila kavmihi, fe lebise fihim elfe senetin illa hamsine ama, fe ehazehumut tufanu ve hum zalimun."},
  {id: 3355, sure_no: 15, ayet_no: 15, sure_adi: "Ankebut", text: "فَاَنْجَيْنَاهُ وَاَصْحَابَ السَّف۪ينَةِ وَجَعَلْنَاهَٓا اٰيَةً لِلْعَالَم۪ينَ", translation: "Böylece onu ve gemideki arkadaşlarını kurtardık. Onu cümle aleme bir ayet yaptık.", transliteration: "Fe enceynahu ve ashabes sefineti ve cealna haayeten lil alemin."},
  {id: 3356, sure_no: 16, ayet_no: 16, sure_adi: "Ankebut", text: "وَاِبْرٰه۪يمَ اِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللّٰهَ وَاتَّقُوهُۜ ذٰلِكُمْ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "İbrahim, halka: \"Allah'a kul olun ve O'na karşı takva sahibi olun. Eğer bilirseniz bu sizin için daha hayırlıdır.\" demişti.", transliteration: "Ve ibrahime iz kale li kavmihi'budullahe vettekuh, zalikum hayrun lekum in kuntum ta'lemun."},
  {id: 3357, sure_no: 17, ayet_no: 17, sure_adi: "Ankebut", text: "اِنَّمَا تَعْبُدُونَ مِنْ دُونِ اللّٰهِ اَوْثَاناً وَتَخْلُقُونَ اِفْكاًۜ اِنَّ الَّذ۪ينَ تَعْبُدُونَ مِنْ دُونِ اللّٰهِ لَا يَمْلِكُونَ لَكُمْ رِزْقاً فَابْتَغُوا عِنْدَ اللّٰهِ الرِّزْقَ وَاعْبُدُوهُ وَاشْكُرُوا لَهُۜ اِلَيْهِ تُرْجَعُونَ", translation: "\"Siz ise, Allah'tan başka, birtakım putlara kulluk ediyorsunuz ve tasarladığınız putlarla iftira ediyorsunuz. Şunu iyi bilin ki, Allah'tan başka kulluk ettikleriniz, size bir rızık vermeye güç yetiremezler. Öyleyse, rızkı Allah'tan isteyin. Ve yalnızca O'na kulluk edin. O'na şükredin. Eninde sonunda O'na döndürüleceksiniz.\"", transliteration: "İnnema ta'budune min dunillahi evsanen ve tahlukune ifka, innellezine ta'budune min dunillahi la yemlikune lekum rızkan, febtegu indallahir rızka va'buduhu veşkuru leh, ileyhi turceun."},
  {id: 3358, sure_no: 18, ayet_no: 18, sure_adi: "Ankebut", text: "وَاِنْ تُكَذِّبُوا فَقَدْ كَذَّبَ اُمَمٌ مِنْ قَبْلِكُمْۜ وَمَا عَلَى الرَّسُولِ اِلَّا الْبَلَاغُ الْمُب۪ينُ", translation: "\"Eğer yalanlarsanız, şunu bilin ki, sizden öncekiler de yalanlamışlardı. Resule düşen de açıkça çağrı yapmaktan başka bir şey değildir.\"", transliteration: "Ve in tukezzibu fe kad kezzebe umemun min kablikum, ve ma aler resuli illel belagul mubin."},
  {id: 3359, sure_no: 19, ayet_no: 19, sure_adi: "Ankebut", text: "اَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللّٰهُ الْخَلْقَ ثُمَّ يُع۪يدُهُۜ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌ", translation: "Onlar, Allah'ın yaratmayı nasıl başlattığını hiç düşünmüyorlar mı? Sonra onu tekrarlayacak. Kuşkusuz bu, Allah'a çok kolaydır.", transliteration: "E ve lem yerev keyfe yubdiullahul halka, summe yuiduh , inne zalike alallahi yesir."},
  {id: 3360, sure_no: 20, ayet_no: 20, sure_adi: "Ankebut", text: "قُلْ س۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ بَدَاَ الْخَلْقَ ثُمَّ اللّٰهُ يُنْشِئُ النَّشْاَةَ الْاٰخِرَةَۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌۚ", translation: "De ki: \"Yeryüzünde gezip dolaşın da ilk yaratışın nasıl olduğuna bakın. Sonra Allah, son inşa etmeyi de aynı şekilde yapacaktır. Allah, Her Şeye Güç Yetiren'dir.\"", transliteration: "Kul siru fil ardı fanzuru keyfe bedeel halka, summallahu yunşiun neş'etel ahıreh, innallahe ala kulli şey'in kadir."},
  {id: 3361, sure_no: 21, ayet_no: 21, sure_adi: "Ankebut", text: "يُعَذِّبُ مَنْ يَشَٓاءُ وَيَرْحَمُ مَنْ يَشَٓاءُۚ وَاِلَيْهِ تُقْلَبُونَ", translation: "O, Hak eden kimseye azap eder, hak eden kimseye de rahmet eder. Ve O'na döndürüleceksiniz.", transliteration: "Yuazzibu men yeşau ve yerhamu men yeşa', ve ileyhi tuklebun."},
  {id: 3362, sure_no: 22, ayet_no: 22, sure_adi: "Ankebut", text: "وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ فِي الْاَرْضِ وَلَا فِي السَّمَٓاءِۘ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ۟", translation: "Siz, yerde ve gökte aciz bırakacak değilsiniz. Sizin Allah'ın yanı sıra başka veliniz ve yardımcınız yoktur.", transliteration: "Ve ma entum bi mu'cizine fil ardı ve la fis semai ve ma lekum min dunillahi min veliyyin ve la nasir."},
  {id: 3363, sure_no: 23, ayet_no: 23, sure_adi: "Ankebut", text: "وَالَّذ۪ينَ كَفَرُوا بِاٰيَاتِ اللّٰهِ وَلِقَٓائِه۪ٓ اُو۬لٰٓئِكَ يَـئِسُوا مِنْ رَحْمَت۪ي وَاُو۬لٰٓئِكَ لَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Allah'ın ayetlerini ve O'nunla buluşma gerçeğini yok sayanlar; işte onlar, Ben'im rahmetimden ümitlerini kesmişlerdir. Onlar için acıklı bir azap vardır.", transliteration: "Vellezine keferu bi ayatillahi ve likaihi ulaike yeisu min rahmeti ve ulaike lehum azabun elim."},
  {id: 3364, sure_no: 24, ayet_no: 24, sure_adi: "Ankebut", text: "فَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُوا اقْتُلُوهُ اَوْ حَرِّقُوهُ فَاَنْجٰيهُ اللّٰهُ مِنَ النَّارِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ", translation: "Sonra onun halkının cevabı: \"Onu öldürün veya yakın!\" demeleri oldu. Bunun üzerine Allah, onu ateşten kurtardı. Bunda iman edecek bir halk için kesinlikle ayetler vardır.", transliteration: "Fe ma kane cevabe kavmihi illa en kaluktuluhu ev harrýkuhu fe encahullahu minen nar, inne fi zalike le ayatin li kavmin yu'minun."},
  {id: 3365, sure_no: 25, ayet_no: 25, sure_adi: "Ankebut", text: "وَقَالَ اِنَّمَا اتَّخَذْتُمْ مِنْ دُونِ اللّٰهِ اَوْثَاناًۙ مَوَدَّةَ بَيْنِكُمْ فِي الْحَيٰوةِ الدُّنْيَاۚ ثُمَّ يَوْمَ الْقِيٰمَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَيَلْعَنُ بَعْضُكُمْ بَعْضاًۘ وَمَأْوٰيكُمُ النَّارُ  وَمَا لَكُمْ مِنْ نَاصِر۪ينَۗ", translation: "İbrahim: \"Siz, dünya hayatında Allah'ın yanı sıra putları aranızda sevgi vesilesi edindiniz. Sonra Kıyamet Günü, bir kısmınız bir kısmınızı yalanlayacak ve bir kısmınız bir kısmınızı lanetleyeceksiniz. Varacağınız yer ateştir. Ve sizin için bir yardımcı yoktur.\" dedi.", transliteration: "Ve kale innemettehaztum min dunillahi evsanen meveddete beynikum fil hayatid dunya, summe yevmel kıyameti yekfuru ba'dukum bi ba'dın ve yel'anu ba'dukum ba'dan ve me'vakumun naru ve ma lekum min nasırin."},
  {id: 3366, sure_no: 26, ayet_no: 26, sure_adi: "Ankebut", text: "فَاٰمَنَ لَهُ لُوطٌۢ وَقَالَ اِنّ۪ي مُهَاجِرٌ اِلٰى رَبّ۪يۜ اِنَّهُ هُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Bunun üzerine Lut, O'na inandı. Ve dedi ki: \"Ben, Rabb'im için hicret ediyorum. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.\"", transliteration: "Fe amene lehu lut  ve kale inni muhacirun ila rabbi, innehu huvel azizul hakim."},
  {id: 3367, sure_no: 27, ayet_no: 27, sure_adi: "Ankebut", text: "وَوَهَبْنَا لَـهُٓ اِسْحٰقَ وَيَعْقُوبَ وَجَعَلْنَا ف۪ي ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتَابَ وَاٰتَيْنَاهُ اَجْرَهُ فِي الدُّنْيَاۚ وَاِنَّهُ فِي الْاٰخِرَةِ لَمِنَ الصَّالِح۪ينَ", translation: "Biz O'na İshak'ı ve Ya'kub'u hibe ettik. Onun soyundan gelenlere Nebi'lik ve Kitap verdik. Böylece O'na dünyada ödülünü verdik. O, ahirette de elbette salihlerden olacaktır.", transliteration: "Ve vehebna lehu ishaka ve ya'kube ve cealna fi zurriyyetihin nubuvvete vel kitabe, ve ateynahu ecrehu fid dunya, ve innehu fil ahıreti le mines salihin."},
  {id: 3368, sure_no: 28, ayet_no: 28, sure_adi: "Ankebut", text: "وَلُوطاً اِذْ قَالَ لِقَوْمِه۪ٓ اِنَّكُمْ لَتَأْتُونَ الْفَاحِشَةَۘ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِنَ الْعَالَم۪ينَ", translation: "Hani Lut halkına: \"Siz, bu alemde sizden önce yaşamış olanların hiçbirinin yapmadığı bir fahişeliğe yöneliyorsunuz.\" demişti.", transliteration: "Ve lutan iz kale li kavmihi innekum le te'tunel fahışete ma sebekakum biha min ehadin minel alemin."},
  {id: 3369, sure_no: 29, ayet_no: 29, sure_adi: "Ankebut", text: "اَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ وَتَقْطَعُونَ السَّب۪يلَ وَتَأْتُونَ ف۪ي نَاد۪يكُمُ الْمُنْكَرَۜ فَمَا كَانَ جَوَابَ قَوْمِه۪ٓ اِلَّٓا اَنْ قَالُوا ائْتِنَا بِعَذَابِ اللّٰهِ اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "\"Gerçekten siz; erkeklere yönelecek, yanlış yolu seçecek ve bir araya gelerek çirkinlik yapacak mısınız?\" Halkının yanıtı: \"Eğer doğru söyleyenlerden isen Allah'ın azabını bize getir.\" demeleri oldu.", transliteration: "E innekum le te'tuner ricale ve taktaunes sebile ve te'tune fi nadikumulmunker, fe ma kane cevabe kavmihi illa en kalu'tina bi azabillahi in kunte mines sadikin."},
  {id: 3370, sure_no: 30, ayet_no: 30, sure_adi: "Ankebut", text: "قَالَ رَبِّ انْصُرْن۪ي عَلَى الْقَوْمِ الْمُفْسِد۪ينَ۟", translation: "Lut: \"Rabb'im! Bozgunculuk yapan halka karşı bana yardım et.\" dedi.", transliteration: "Kale rabbinsurni alel kavmil mufsidin."},
  {id: 3371, sure_no: 31, ayet_no: 31, sure_adi: "Ankebut", text: "وَلَمَّا جَٓاءَتْ رُسُلُـنَٓا اِبْرٰه۪يمَ بِالْبُشْرٰىۙ قَالُٓوا اِنَّا مُهْلِكُٓوا اَهْلِ هٰذِهِ الْقَرْيَةِۚ اِنَّ اَهْلَهَا كَانُوا ظَالِم۪ينَۚ", translation: "Resullerimiz İbrahim'e müjdeyi getirdiklerinde: \"Biz bu beldenin halkını helak edeceğiz.\" dediler. \"Zira bu beldenin halkı zalim oldular.\"", transliteration: "Ve lemma caet rusuluna ibrahime bil buşra, kalu inna muhliku ehli hazihil karyeh, inne ehleha kanu zalimin."},
  {id: 3372, sure_no: 32, ayet_no: 32, sure_adi: "Ankebut", text: "قَالَ اِنَّ ف۪يهَا لُـوطاًۜ قَالُوا نَحْنُ اَعْلَمُ بِمَنْ ف۪يهَاۘ لَنُنَجِّيَنَّهُ وَاَهْلَـهُٓ اِلَّا امْرَاَتَهُۘ كَانَتْ مِنَ الْغَابِر۪ينَ", translation: "İbrahim: \"Fakat orada Lut var!\" dedi. \"Biz, orada kimin olduğunu daha iyi biliriz. Geride kalanlarla beraber olacak olan karısı hariç, onu ve ehlini mutlaka kurtaracağız.\" dediler.", transliteration: "Kale inne fiha luta, kalu nahnu a'lemu bi men fiha le nunecciyennehu ve ehlehu illemreetehu kanet minel gabirin."},
  {id: 3373, sure_no: 33, ayet_no: 33, sure_adi: "Ankebut", text: "وَلَمَّٓا اَنْ جَٓاءَتْ رُسُلُنَا لُـوطاً س۪ٓيءَ بِهِمْ وَضَـاقَ بِهِمْ ذَرْعاً وَقَالُوا لَا تَخَفْ وَلَا تَحْزَنْ۠ اِنَّا مُنَجُّوكَ وَاَهْلَكَ اِلَّا امْرَاَتَكَ كَانَتْ مِنَ الْغَابِر۪ينَ", translation: "Elçilerimiz Lut'a vardıkları zaman, onların gelmelerinden dolayı telaşlandı, sarkıntılık yapacaklarını düşünerek içi daraldı. Elçiler: \"Korkma ve üzülme; Biz -geride kalanlarla beraber olacak olan karın hariç- seni ve ehlini mutlaka kurtaracağız.\" dediler.", transliteration: "Ve lemma en caet rusuluna lutan sie bihim ve daka bihim zer'an, ve kalu la tehaf ve la tahzen, inna muneccuke ve ehleke illemreeteke kanet minel gabirin."},
  {id: 3374, sure_no: 34, ayet_no: 34, sure_adi: "Ankebut", text: "اِنَّا مُنْزِلُونَ عَلٰٓى اَهْلِ هٰذِهِ الْقَرْيَةِ رِجْزاً مِنَ السَّمَٓاءِ بِمَا كَانُوا يَفْسُقُونَ", translation: "Biz, bu belde halkının üzerine, yoldan çıkmaları nedeniyle gökten bir azap indireceğiz.", transliteration: "İnna munzilune ala ehli hazihil karyeti riczen mines semai bima kanu yefsukun."},
  {id: 3375, sure_no: 35, ayet_no: 35, sure_adi: "Ankebut", text: "وَلَقَدْ تَرَكْنَا مِنْهَٓا اٰيَةً بَيِّنَةً لِقَوْمٍ يَعْقِلُونَ", translation: "Ant olsun ki Biz, aklını kullanan bir halk için ondan apaçık bir ayet bıraktık.", transliteration: "Ve lekad terekna minha ayeten beyyineten li kavmin ya'kılun."},
  {id: 3376, sure_no: 36, ayet_no: 36, sure_adi: "Ankebut", text: "وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْباًۙ فَقَالَ يَا قَوْمِ اعْبُدُوا اللّٰهَ وَارْجُوا الْيَوْمَ الْاٰخِرَ وَلَا تَعْثَوْا فِي الْاَرْضِ مُفْسِد۪ينَ", translation: "Medyen halkına da kardeşleri Şu'ayb'ı gönderdik. Şu'ayb: \"Ey halkım! Allah'a kulluk edin. Ahiret gününü umut edin. Yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın!\" dedi.", transliteration: "Ve ila medyene ehahum şuayben fe kale ya kavmi'budullahe vercul yevmel ahıre ve la ta'sev fil ardı mufsidin."},
  {id: 3377, sure_no: 37, ayet_no: 37, sure_adi: "Ankebut", text: "فَكَذَّبُوهُ فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوا ف۪ي دَارِهِمْ جَاثِم۪ينَۘ", translation: "Fakat onu yalanladılar. Bu nedenle onları şiddetli bir sarsıntı yakaladı ve yurtlarında dizüstü çöke kaldılar.", transliteration: "Fe kezzebuhu fe ehazethumur recfetu fe asbehu fi darihim casimin."},
  {id: 3378, sure_no: 38, ayet_no: 38, sure_adi: "Ankebut", text: "وَعَاداً وَثَمُودَا۬ وَقَدْ تَبَيَّنَ لَكُمْ مِنْ مَسَاكِنِهِمْ۠ وَزَيَّنَ لَهُمُ الشَّيْطَانُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّب۪يلِ وَكَانُوا مُسْتَبْصِر۪ينَۙ", translation: "Ad ve Semud'un sonları da yurtlarının durumundan size belli olmaktadır. Şeytan yaptıklarını güzel göstererek onların yanlış yolu seçmelerine sebep oldu. Oysaki doğruyu görebilirlerdi.", transliteration: "Ve aden ve semude ve kad tebeyyene lekum min mesakinihim, ve zeyyene lehumuş şeytanu a'malehum fe saddehum anis sebili ve kanu mustebsırin."},
  {id: 3379, sure_no: 39, ayet_no: 39, sure_adi: "Ankebut", text: "وَقَارُونَ وَفِرْعَوْنَ وَهَامَانَ وَلَقَدْ جَٓاءَهُمْ مُوسٰى بِالْبَيِّنَاتِ فَاسْتَكْبَرُوا فِي الْاَرْضِ وَمَا كَانُوا سَابِق۪ينَۚ", translation: "Karun, Firavun ve Haman'a; Musa kanıt içeren, açıklayıcı bilgiyle geldi. Ne var ki onlar yeryüzünde büyüklük tasladılar. Onlar kurtulanlardan olmadılar.", transliteration: "Ve karune ve fir'avne ve hamane ve lekad caehum musa bil beyyinati festekberu fil ardı ve ma kanu sabikin."},
  {id: 3380, sure_no: 40, ayet_no: 40, sure_adi: "Ankebut", text: "فَكُلاًّ اَخَذْنَا بِذَنْبِه۪ۚ فَمِنْهُمْ مَنْ اَرْسَلْنَا عَـلَيْهِ حَـاصِباًۚ وَمِنْهُمْ مَنْ اَخَذَتْهُ الصَّيْحَةُۚ وَمِنْهُمْ مَنْ خَسَفْنَا بِهِ الْاَرْضَۚ وَمِنْهُمْ مَنْ اَغْرَقْنَاۚ وَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَ", translation: "Bunun üzerine suçları nedeniyle hepsini cezalandırdık. Bir kısmının üzerine taşlar savuran rüzgarlar gönderdik. Kimisini de korkunç bir ses yakaladı. Kimisini de yerin dibine geçirdik. Kimisini de boğduk. Böyle yapmakla, Allah onlara haksızlık yapmadı. Fakat onlar kendi kendilerine haksızlık yaptılar.", transliteration: "Fe kullen ehazna bi zenbih, fe minhum men erselna aleyhi hasıba, ve minhum men ehazethussayhah, ve minhum men hasefnabihil ard, ve minhum men agrakna, ve ma kanallahu li yazlimehum ve lakin kanu enfusehum yazlimun."},
  {id: 3381, sure_no: 41, ayet_no: 41, sure_adi: "Ankebut", text: "مَثَلُ الَّذ۪ينَ اتَّخَذُوا مِنْ دُونِ اللّٰهِ اَوْلِيَٓاءَ كَمَثَلِ الْعَنْكَبُوتِۚ اِتَّخَذَتْ بَيْتاًۜ وَاِنَّ اَوْهَنَ الْبُيُوتِ لَبَيْتُ الْعَنْكَبُوتِۢ لَوْ كَانُوا يَعْلَمُونَ", translation: "Allah'ın yanı sıra veliler edinenlerin durumu, kendisine dişi örümceğin evini ev edinenin durumu gibidir. Kuşkusuz evlerin en dayanıksızı dişi örümceğin evidir. Keşke bunu kavrayabilselerdi.", transliteration: "Meselullezinettehazu min dunillahi evliyae ke meselil ankebut, ittehazet beyta ve inne evhenel buyuti le beytul ankebut, lev kanu ya'lemun."},
  {id: 3382, sure_no: 42, ayet_no: 42, sure_adi: "Ankebut", text: "اِنَّ اللّٰهَ يَعْلَمُ مَا يَدْعُونَ مِنْ دُونِه۪ مِنْ شَيْءٍۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Kuşkusuz Allah, onların Kendisinin yanı sıra yöneldikleri şeylerin ne olduğunu bilir. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "İnnallahe ya'lemu ma yed'une min dunihi min şey', ve huvel azizul hakim."},
  {id: 3383, sure_no: 43, ayet_no: 43, sure_adi: "Ankebut", text: "وَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِۚ وَمَا يَعْقِلُـهَٓا اِلَّا الْعَالِمُونَ", translation: "Bu örnekleri insanlara veriyoruz. Onu alimlerden başkası kavrayamaz.", transliteration: "Ve tilkel emsalu nadribuha lin nas ve ma ya'kıluha illel alimun."},
  {id: 3384, sure_no: 44, ayet_no: 44, sure_adi: "Ankebut", text: "خَلَقَ اللّٰهُ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِلْمُؤْمِن۪ينَ۟", translation: "Allah, gökleri ve yeri hakk ile yarattı. Bunda İman Edenler için kesinlikle bir ayet vardır.", transliteration: "Halakallahus semavati vel arda bil hakk, inne fi zalike le ayeten lil mu'minin."},
  {id: 3385, sure_no: 45, ayet_no: 45, sure_adi: "Ankebut", text: "اُتْلُ مَٓا اُو۫حِيَ اِلَيْكَ مِنَ الْكِتَابِ وَاَقِمِ الصَّلٰوةَۜ اِنَّ الصَّلٰوةَ تَنْهٰى عَنِ الْفَحْشَٓاءِ وَالْمُنْكَرِۜ وَلَذِكْرُ اللّٰهِ  اَكْبَرُۜ  وَاللّٰهُ  يَعْلَمُ  مَا  تَصْنَعُونَ", translation: "Kitap'tan sana vahyolunan şeyi oku. Salatı ikame et. Salat, fahşadan ve münkerden alıkoyar. Kesinlikle Allah'ın zikri daha büyüktür. Allah, yaptığınız şeyleri bilir.", transliteration: "Utlu ma uhıye ileyke minel kitabi ve ekımıs salat, innes salate tenha anil fahşai vel munker, ve le zikrullahi ekber, vallahu ya'lemu ma tasneun."},
  {id: 3386, sure_no: 46, ayet_no: 46, sure_adi: "Ankebut", text: "وَلَا تُجَادِلُٓوا اَهْلَ الْكِتَابِ اِلَّا بِالَّت۪ي هِيَ اَحْسَنُۗ اِلَّا الَّذ۪ينَ ظَلَمُوا مِنْهُمْ وَقُولُٓوا اٰمَنَّا بِالَّـذ۪ٓي اُنْزِلَ اِلَيْنَا وَاُنْزِلَ اِلَيْكُمْ وَاِلٰهُنَا وَاِلٰهُكُمْ وَاحِدٌ وَنَحْنُ لَهُ مُسْلِمُونَ", translation: "Haksızlık edenleri hariç, Kitap Ehli ile ancak en iyi şekilde mücadele edin. Ve deyin ki: \"Biz, bize indirilene de size indirilene de iman ettik. Bizim ve sizin ilahınız birdir. Biz de O'na teslim olanlarız.\"", transliteration: "Ve la tucadilu ehlel kitabi illa billeti hiye ahsenu illellezine zalemu minhum ve kulu amenna billezi unzile ileyna ve unzile ileykum ve ilahuna ve ilahukum vahıdun ve nahnu lehu muslimun."},
  {id: 3387, sure_no: 47, ayet_no: 47, sure_adi: "Ankebut", text: "وَكَذٰلِكَ اَنْزَلْـنَٓا اِلَيْكَ الْكِتَابَۜ فَالَّذ۪ينَ اٰتَيْنَاهُمُ الْكِتَابَ يُؤْمِنُونَ بِه۪ۚ وَمِنْ هٰٓؤُ۬لَٓاءِ مَنْ يُؤْمِنُ بِه۪ۜ وَمَا يَجْحَدُ بِاٰيَاتِنَٓا اِلَّا الْكَافِرُونَ", translation: "İşte böylece sana Kitap'ı indirdik. Kendilerine Kitap ulaştırdıklarımız, ona iman ederler. Şu kimselerden de O'na iman edecekler vardır. Kafirlerden başkası ayetlerimizi inkar etmez.", transliteration: "Ve kezalike enzelna ileykel kitab, fellezine ateyna humul kitabe yu'minune bih, ve min haulai men yu'minu bih, ve ma yechadu bi ayatina illel kafirun."},
  {id: 3388, sure_no: 48, ayet_no: 48, sure_adi: "Ankebut", text: "وَمَا كُنْتَ تَتْلُوا مِنْ قَبْلِه۪ مِنْ كِتَابٍ وَلَا تَخُطُّهُ بِيَم۪ينِكَ اِذاً لَارْتَابَ الْمُبْطِلُونَ", translation: "Sen, daha önce herhangi bir kitaptan okuyor ve onu elinle yazıyor değildin. Öyle olsaydı, mesajını geçersiz yapmak isteyenler kesinlikle kuşkulanırlardı.", transliteration: "Ve ma kunte tetlu min kablihi min kitabin ve la tehuttuhu bi yeminike izen lertabel mubtılun."},
  {id: 3389, sure_no: 49, ayet_no: 49, sure_adi: "Ankebut", text: "بَلْ هُوَ اٰيَاتٌ بَيِّنَاتٌ ف۪ي صُدُورِ الَّذ۪ينَ اُو۫تُوا الْعِلْمَۜ وَمَا يَجْحَدُ بِاٰيَاتِنَٓا اِلَّا الظَّالِمُونَ", translation: "Hayır! O, kendilerine ilim verilenlerin gönüllerinde yer eden apaçık ayetlerdir. Bizim ayetlerimizi zalimlerden başkası yalanlamaz.", transliteration: "Bel huve ayatun beyyinatun fi sudurillezine utul ilm, ve ma yechadu bi ayatina illez zalimun."},
  {id: 3390, sure_no: 50, ayet_no: 50, sure_adi: "Ankebut", text: "وَقَالُوا لَوْلَٓا اُنْزِلَ عَلَيْهِ اٰيَاتٌ مِنْ رَبِّه۪ۜ قُلْ اِنَّمَا الْاٰيَاتُ عِنْدَ اللّٰهِۜ وَاِنَّـمَٓا اَنَا۬ نَذ۪يرٌ مُب۪ينٌ", translation: "\"Ona Rabb'inden ayetler indirilmeli değil miydi?\" dediler. De ki: \"Ayetler ancak Allah'ın yanındadır. Ben ancak apaçık bir uyarıcıyım.\"", transliteration: "Ve kalu lev la unzile aleyhi ayatun min rabbih, kul innemel ayatu indallah, ve innema ene nezirun mubin."},
  {id: 3391, sure_no: 51, ayet_no: 51, sure_adi: "Ankebut", text: "اَوَلَمْ يَكْفِهِمْ اَنَّٓا اَنْزَلْنَا عَلَيْكَ الْكِتَابَ يُتْلٰى عَلَيْهِمْۜ اِنَّ ف۪ي ذٰلِكَ لَرَحْمَةً وَذِكْرٰى لِقَوْمٍ يُؤْمِنُونَ۟", translation: "Kendilerine okunan Kitap'ı sana indirmemiz onlara yetmedi mi? Kuşkusuz bunda iman eden bir toplum için bir rahmet ve zikir vardır.", transliteration: "E ve lem yekfihim enna enzelna aleykel kitabe yutla aleyhim, inne fi zalike le rahmeten ve zikra li kavmin yu'minun."},
  {id: 3392, sure_no: 52, ayet_no: 52, sure_adi: "Ankebut", text: "قُلْ كَفٰى بِاللّٰهِ بَيْن۪ي وَبَيْنَكُمْ شَه۪يداًۚ يَعْلَمُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ وَالَّذ۪ينَ اٰمَنُوا بِالْبَاطِلِ وَكَفَرُوا بِاللّٰهِۙ اُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ", translation: "De ki: \"Benimle sizin aranızda tanık olarak Allah yeter. O, göklerde ve yerde olanları bilir.\" Batıl'a iman edenler ve Allah'ı yalanlayan kimseler, işte onlar hüsranda olanlardır.", transliteration: "Kul kefa billahi beyni ve beynekum şehida, ya'lemu ma fis semavati vel ard, vellezine amenu bil batılı ve keferu billahi ulaike humul hasirun."},
  {id: 3393, sure_no: 53, ayet_no: 53, sure_adi: "Ankebut", text: "وَيَسْتَعْجِلُونَكَ بِالْعَذَابِۜ وَلَوْلَٓا اَجَلٌ مُسَمًّى لَجَٓاءَهُمُ الْعَذَابُۜ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ", translation: "Senden azabı hemen getirmeni istiyorlar. Eğer belirlenmiş bir zaman olmasaydı, azap onlara elbette gelmişti. Ve o, hiç farkında olmadıkları bir sırada ansızın gelecek.", transliteration: "Ve yesta'ciluneke bil azab, ve lev la ecelun musemmen le caehumul azab, ve le ye'tiyennehum bagteten ve hum la yeş'urun."},
  {id: 3394, sure_no: 54, ayet_no: 54, sure_adi: "Ankebut", text: "يَسْتَعْجِلُونَكَ بِالْعَذَابِۜ وَاِنَّ جَهَنَّمَ لَمُح۪يطَةٌ بِالْكَافِر۪ينَۙ", translation: "Senden azabı hemen getirmeni istiyorlar. Oysaki Cehennem kesinlikle Kafirleri çepeçevre kuşatacaktır.", transliteration: "Yesta'ciluneke bil azab, ve inne cehenneme le muhitatun bil kafirin."},
  {id: 3395, sure_no: 55, ayet_no: 55, sure_adi: "Ankebut", text: "يَوْمَ يَغْشٰيهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْ وَيَقُولُ ذُوقُوا مَا كُنْتُمْ تَعْمَلُونَ", translation: "O gün, azap üstlerinden ve ayaklarının altından onları kuşatacak. Ve \"Yapmış olduğunuz şeylerin cezasını tadın!\" der.", transliteration: "Yevme yagşahumul azabu min fevkıhim ve min tahti erculihim ve yekulu zuku ma kuntum ta'melun."},
  {id: 3396, sure_no: 56, ayet_no: 56, sure_adi: "Ankebut", text: "يَا عِبَادِيَ الَّذ۪ينَ اٰمَنُٓوا اِنَّ اَرْض۪ي وَاسِعَةٌ فَاِيَّايَ فَاعْبُدُونِ", translation: "Ey İman Eden kullarım! Yeryüzüm geniştir. Öyleyse yalnız Bana kulluk edin.", transliteration: "Ya ıbadıyellezine amenu inne ardi vasiatun fe iyyaye fa'budun."},
  {id: 3397, sure_no: 57, ayet_no: 57, sure_adi: "Ankebut", text: "كُلُّ نَفْسٍ ذَٓائِقَةُ الْمَوْتِ ثُمَّ اِلَيْنَا تُرْجَعُونَ", translation: "Her nefs ölümü tadıcıdır. Sonra Biz'e döndürüleceksiniz.", transliteration: "Kullu nefsin zaikatul mevti summe ileyna turceun."},
  {id: 3398, sure_no: 58, ayet_no: 58, sure_adi: "Ankebut", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُبَوِّئَنَّهُمْ مِنَ الْجَنَّةِ غُرَفاً تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ نِعْمَ اَجْرُ الْعَامِل۪ينَۗ", translation: "İman edenleri ve salihatı yapanları, içinde sürekli kalacakları Cennet'te; altından ırmaklar akan köşklere yerleştireceğiz. İyi işler yapanların ödülü ne güzeldir!", transliteration: "Vellezine amenu ve amilus salihati le nubevviennehum minel cenneti gurafan tecrimin tahtihel enharu halidine fiha, ni'me ecrul amilin."},
  {id: 3399, sure_no: 59, ayet_no: 59, sure_adi: "Ankebut", text: "اَلَّذ۪ينَ صَبَرُوا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَ", translation: "Onlar, sabreden ve Rabb'lerine tevekkül edenlerdir.", transliteration: "Ellezine saberu ve ala rabbihim yetevekkelun."},
  {id: 3400, sure_no: 60, ayet_no: 60, sure_adi: "Ankebut", text: "وَكَاَيِّنْ مِنْ دَٓابَّةٍ لَا تَحْمِلُ رِزْقَهَاۗ اَللّٰهُ يَرْزُقُهَا وَاِيَّاكُمْۘ وَهُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Rızıklarını temin edemeyen nice dabbe vardır. Onlara da size de Allah rızık verir. O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve keeyyin min dabbetin la tahmilu rızkaha allahu yerzukuha ve iyyakum ve huves semiul alim."},
  {id: 3401, sure_no: 61, ayet_no: 61, sure_adi: "Ankebut", text: "وَلَئِنْ سَاَلْتَهُمْ مَنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُولُنَّ اللّٰهُۚ فَاَنّٰى يُؤْفَكُونَ", translation: "Gerçek şu ki, onlara: \"Gökleri ve yeri kim yarattı, Güneş'i ve Ay'ı yararınıza sunan kimdir?\" diye sorsan, kesinlikle \"Allah.\" diyecekler. O halde nasıl başka varlıklara yöneliyorlar!", transliteration: "Ve le in seeltehum men halakas semavati vel arda ve sehhareş şemse vel kamere le yekulunnallah, fe enna yu'fekun."},
  {id: 3402, sure_no: 62, ayet_no: 62, sure_adi: "Ankebut", text: "اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ مِنْ عِبَادِه۪ وَيَقْدِرُ لَهُۜ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Allah, kullarından dilediğine rızkı genişletir ve onun için takdir eder. Kuşkusuz Allah, Her Şeyi En İyi Bilen'dir.", transliteration: "Allahu yebsutur rızka li men yeşau min ibadihi ve yakdiru leh, innallahe bi kulli şey'in alim."},
  {id: 3403, sure_no: 63, ayet_no: 63, sure_adi: "Ankebut", text: "وَلَئِنْ سَاَلْتَهُمْ مَنْ نَزَّلَ مِنَ السَّمَٓاءِ مَٓاءً فَاَحْيَا بِهِ الْاَرْضَ مِنْ بَعْدِ مَوْتِهَا لَيَقُولُنَّ اللّٰهُۜ  قُلِ الْحَمْدُ لِلّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْقِلُونَ۟", translation: "Eğer onlara: \"Gökten su indirip de onunla yeryüzünü ölümünden sonra dirilten kimdir?\" diye sorsan, kesinlikle \"Allah\" diyecekler. De ki: \"Hamd Allah'a özgüdür.\" Fakat onların çoğu akıllarını kullanmıyorlar.", transliteration: "Ve le in seeltehum men nezzele mines semai maen fe ahya bihil arda min ba'di mevtiha le yekulunnallah, kulil hamdu lillah, bel ekseruhum la ya'kılun."},
  {id: 3404, sure_no: 64, ayet_no: 64, sure_adi: "Ankebut", text: "وَمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَٓا اِلَّا لَهْوٌ وَلَعِبٌۜ وَاِنَّ الدَّارَ الْاٰخِرَةَ لَهِيَ الْحَيَوَانُۢ لَوْ كَانُوا يَعْلَمُونَ", translation: "Bu dünya hayatı bir oyun ve eğlenceden başka bir şey değildir. Ahiret Yurdu, gerçek hayattır. Keşke, bunun farkında olsalardı!", transliteration: "Ve ma hazihil hayatud dunya illa lehvun ve laib, ve inned darel ahırete le hiyel hayevan, lev kanu ya'lemun."},
  {id: 3405, sure_no: 65, ayet_no: 65, sure_adi: "Ankebut", text: "فَاِذَا رَكِبُوا فِي الْفُلْكِ دَعَوُا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَۚ فَلَمَّا نَجّٰيهُمْ اِلَى الْبَرِّ اِذَا هُمْ يُشْرِكُونَۙ", translation: "Gemiye bindikleri zaman, dini yalnız Allah'a özgü kılarak O'na dua ederler. Fakat onları karaya çıkarıp kurtardığımız zaman, hemen şirk koşarlar.", transliteration: "Fe iza rakibu fil fulki deavullahe muhlisine lehud din, fe lemma neccahum ilel berri iza hum yuşrikun."},
  {id: 3406, sure_no: 66, ayet_no: 66, sure_adi: "Ankebut", text: "لِيَكْفُرُوا بِمَٓا اٰتَيْنَاهُمْۙ وَلِيَتَمَتَّعُوا۠ فَسَوْفَ يَعْلَمُونَ", translation: "Onlar, kendilerine verdiğimiz nimetlere nankörlük etsinler ve gönüllerince yaşasınlar! Yakında neyin ne olduğunu görecekler!", transliteration: "Li yekfuru bima ateynahum ve li yetemettau, fe sevfe ya'lemun."},
  {id: 3407, sure_no: 67, ayet_no: 67, sure_adi: "Ankebut", text: "اَوَلَمْ يَرَوْا اَنَّا جَعَلْنَا حَرَماً اٰمِناً وَيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْۜ اَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَةِ اللّٰهِ يَكْفُرُونَ", translation: "Çevresindeki insanlar can güvenliği içinde değilken, orayı güvenli harem ve emin bir yer kıldığımızı görmediler mi? Hala Batıl'a mı inanıyorlar ve Allah'ın nimetine nankörlük mü ediyorlar?", transliteration: "E ve lem yerev enna cealna haramen aminen ve yutehattafun nasu min havlihim, e fe bil batılı yu'minune ve bi ni'metillahi yekfurun."},
  {id: 3408, sure_no: 68, ayet_no: 68, sure_adi: "Ankebut", text: "وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِباً اَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَٓاءَهُۜ اَلَيْسَ ف۪ي جَهَنَّمَ مَثْوًى لِلْكَافِر۪ينَ", translation: "Uydurduğu yalanı Allah'a isnat eden iftiracıdan veya kendisine gelen hakikati yalanlayandan daha zalim kim vardır? Kafirler için Cehennem'de yer mi yok?", transliteration: "Ve men azlemu mimmeniftera alallahi keziben ev kezzebe bil hakkı lemma caeh, e leyse fi cehenneme mesven lil kafirin."},
  {id: 3409, sure_no: 69, ayet_no: 69, sure_adi: "Ankebut", text: "وَالَّذ۪ينَ جَاهَدُوا ف۪ينَا لَنَهْدِيَنَّهُمْ سُبُلَنَاۜ وَاِنَّ اللّٰهَ لَمَعَ الْمُحْسِن۪ينَ", translation: "Biz, Bizim yolumuzda cihad edenleri elbette yollarımıza iletiriz. Kuşkusuz Allah, muhsinlerle beraberdir.", transliteration: "Vellezine cahedu fina le nehdiyennehum subulena ve innallahe le meal muhsinin."},
  {id: 3410, sure_no: 1, ayet_no: 1, sure_adi: "Rum", text: "الٓمٓ۠", translation: "Elif. Lam. Mim.", transliteration: "Elif lam mim."},
  {id: 3411, sure_no: 2, ayet_no: 2, sure_adi: "Rum", text: "غُلِبَتِ الرُّومُۙ", translation: "Rum'a galip gelindi.", transliteration: "Gulibetir rum."},
  {id: 3412, sure_no: 3, ayet_no: 3, sure_adi: "Rum", text: "ف۪ٓي اَدْنَى الْاَرْضِ وَهُمْ مِنْ بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَۙ", translation: "En yakın bir yerde. Bu yenilgilerinden sonra pek yakında galip geleceklerdir.", transliteration: "Fi ednel ardı ve hum min ba'di galebihim se yaglibun."},
  {id: 3413, sure_no: 4, ayet_no: 4, sure_adi: "Rum", text: "ف۪ي بِضْعِ سِن۪ينَۜ لِلّٰهِ الْاَمْرُ مِنْ قَبْلُ وَمِنْ بَعْدُۜ وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَۙ", translation: "Birkaç yıl içinde. Önce de sonra da her buyruk Allah'ındır. Ve o gün İman Edenler sevineceklerdir.", transliteration: "Fi bıd'ı sinin, lillahil emru min kablu ve min ba'd, ve yevme izin yefrahul mu'minun."},
  {id: 3414, sure_no: 5, ayet_no: 5, sure_adi: "Rum", text: "بِنَصْرِ اللّٰهِۜ يَنْصُرُ مَنْ يَشَٓاءُۜ وَهُوَ الْعَز۪يزُ الرَّح۪يمُ", translation: "Hak eden kimseye yardım eden Allah'ın yardımıyla. O, Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Bi nasrillah, yansuru men yeşa', ve huvel azizur rahim."},
  {id: 3415, sure_no: 6, ayet_no: 6, sure_adi: "Rum", text: "وَعْدَ اللّٰهِۜ لَا يُخْلِفُ اللّٰهُ وَعْدَهُ وَلٰكِنَّ اَكْثَرَ  النَّاسِ لَا يَعْلَمُونَ", translation: "Bu, Allah'ın sözüdür. Allah verdiği sözden dönmez. Fakat insanların çoğu bunun bilincinde değildirler.", transliteration: "Va'dallah, la yuhlifullahu va'dehu ve lakinne ekseren nasi la ya'lemun."},
  {id: 3416, sure_no: 7, ayet_no: 7, sure_adi: "Rum", text: "يَعْلَمُونَ ظَاهِراً مِنَ الْحَيٰوةِ الدُّنْيَاۚ وَهُمْ عَنِ الْاٰخِرَةِ هُمْ غَافِلُونَ", translation: "Onlar, dünya hayatının görünen yüzünü bilirler. Onlar, ahiret hayatının bilincinde değildirler.", transliteration: "Ya'lemune zahiren minel hayatid dunya, ve hum anil ahıreti hum gafilun."},
  {id: 3417, sure_no: 8, ayet_no: 8, sure_adi: "Rum", text: "اَوَلَمْ يَتَفَكَّرُوا ف۪ٓي اَنْفُسِهِمْ۠ مَا خَلَقَ اللّٰهُ السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَٓا اِلَّا بِالْحَقِّ وَاَجَلٍ مُسَمًّىۜ وَاِنَّ كَث۪يراً مِنَ النَّاسِ بِلِقَٓائِ۬ رَبِّهِمْ لَكَافِرُونَ", translation: "Onlar, muhasebe yapıp hiç düşünmüyorlar mı? Allah; gökleri, yeri ve bu ikisinin arasındaki şeyleri ancak Hakk ile ve belli bir süre için yarattı. İnsanların çoğu Rabb'lerinin huzuruna çıkacaklarını küfrediyorlar.", transliteration: "E ve lem yetefekkeru fi enfusihim, ma halakallahus semavati vel arda ve ma beynehuma illa bil hakkı ve ecelin musemma ve inne kesiran minen nasi bi likai rabbihim le kafirun."},
  {id: 3418, sure_no: 9, ayet_no: 9, sure_adi: "Rum", text: "اَوَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَانُٓوا اَشَدَّ مِنْهُمْ قُوَّةً وَاَثَارُوا الْاَرْضَ وَعَمَرُوهَٓا اَكْثَرَ مِمَّا عَمَرُوهَا وَجَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِۜ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُٓوا اَنْفُسَهُمْ يَظْلِمُونَۜ", translation: "Yeryüzünde dolaşıp, kendilerinden öncekilerinin sonları nasıl olmuş, bir bakmadılar mı? Onlar, kendilerinden daha güçlüydüler; yeryüzünü alt üst etmişlerdi; onu, kendilerinin imar ettiklerinden daha çok imar etmişlerdi. Resulleri, onlara nice açık belgelerle gelmişti. Allah onlara haksızlık yapmamış, onlar kendi kendilerine haksızlık yapmışlardı.", transliteration: "E ve lem yesiru fil ardı fe yenzuru keyfe kane akıbetullezine min kablihim, kanu eşedde minhum kuvveten, ve esarul arda ve ameruha eksera mimma ameruha ve caethum rusuluhum bil beyyinat, fe ma kanallahu li yazlimehum ve lakin kanu enfusehum yazlimun."},
  {id: 3419, sure_no: 10, ayet_no: 10, sure_adi: "Rum", text: "ثُمَّ  كَانَ عَاقِبَةَ الَّذ۪ينَ اَسَٓاؤُا السُّٓوآٰى اَنْ كَذَّبُوا بِاٰيَاتِ اللّٰهِ وَكَانُوا بِهَا يَسْتَهْزِؤُ۫نَ۟", translation: "Sonra, Allah'ın ayetlerini yalanlayıp, alaya alarak kötülük yapanların sonları çok kötü oldu.", transliteration: "Summe kane akıbetellezine esaus sua en kezzebu bi ayatillahi ve kanu biha yestehziun."},
  {id: 3420, sure_no: 11, ayet_no: 11, sure_adi: "Rum", text: "اَللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ ثُمَّ اِلَيْهِ تُرْجَعُونَ", translation: "Allah, önce yaratmaya başlar, sonra onu iade eder. Sonra da O'na döndürülürsünüz.", transliteration: "Allahu yebdeul halka summe yuiduhu summe ileyhi turceun."},
  {id: 3421, sure_no: 12, ayet_no: 12, sure_adi: "Rum", text: "وَيَوْمَ تَقُومُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُونَ", translation: "Sa'at'in gerçekleştiği O Gün, mücrimler umutlarını kaybederler.", transliteration: "Ve yevme tekumus saatu yublisul mucrimun."},
  {id: 3422, sure_no: 13, ayet_no: 13, sure_adi: "Rum", text: "وَلَمْ يَكُنْ لَهُمْ مِنْ شُرَكَٓائِهِمْ شُفَعٰٓؤُ۬ا وَكَانُوا بِشُرَكَٓائِهِمْ كَافِر۪ينَ", translation: "Ortak koştukları da onlara şefaatçi olmayacaktır. Ortaklarını da yok sayacaklar.", transliteration: "Ve lem yekun lehum min şurekaihim şufeau ve kanu bi şurekaihim kafirin."},
  {id: 3423, sure_no: 14, ayet_no: 14, sure_adi: "Rum", text: "وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَتَفَرَّقُونَ", translation: "Sa'at'in gerçekleştiği gün; O Gün, onlar birbirinden ayrılırlar.", transliteration: "Ve yevme tekumus saatu yevmeizin yeteferrakun."},
  {id: 3424, sure_no: 15, ayet_no: 15, sure_adi: "Rum", text: "فَاَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ ف۪ي رَوْضَةٍ يُحْبَرُونَ", translation: "Fakat iman edenler ve salihatı yapanlar, onlar güzel bir bahçede mutluluk içinde yaşayacaklardır.", transliteration: "Fe emmellezine amenu ve amilus salihati fe hum fi ravdatin yuhberun."},
  {id: 3425, sure_no: 16, ayet_no: 16, sure_adi: "Rum", text: "وَاَمَّا الَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَا وَلِقَٓائِ الْاٰخِرَةِ فَاُو۬لٰٓئِكَ فِي الْعَذَابِ مُحْضَرُونَ", translation: "Kafir olup ayetlerimizi ve ahiret kavuşmasını yalanlayan kimselere gelince; onlar, azap içinde hazır bulundurulurlar.", transliteration: "Ve emmellezine keferu ve kezzebu bi ayatina ve likail ahıreti fe ulaike fil azabi muhdarun."},
  {id: 3426, sure_no: 17, ayet_no: 17, sure_adi: "Rum", text: "فَسُبْحَانَ اللّٰهِ ح۪ينَ تُمْسُونَ وَح۪ينَ تُصْبِحُونَ", translation: "O halde akşama kavuştuğunuz zaman ve sabaha kavuştuğunuz zaman tesbih Allah'ındır.", transliteration: "Fe subhanallahi hine tumsune ve hine tusbıhun."},
  {id: 3427, sure_no: 18, ayet_no: 18, sure_adi: "Rum", text: "وَلَهُ الْحَمْدُ فِي السَّمٰوَاتِ وَالْاَرْضِ وَعَشِياًّ وَح۪ينَ تُظْهِرُونَ", translation: "Karanlık ve aydınlık zamanlarda, göklerde ve yerde hamd, O'na aittir.", transliteration: "Ve lehul hamdu fis semavati vel ardı ve aşiyyen ve hine tuzhırun."},
  {id: 3428, sure_no: 19, ayet_no: 19, sure_adi: "Rum", text: "يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْـيِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ وَكَذٰلِكَ تُخْرَجُونَ۟", translation: "O, ölüden diriyi, diriden de ölüyü çıkarır. Ve ölmüş yeryüzüne hayat verir. İşte, tıpkı bunun gibi siz de diriltileceksiniz.", transliteration: "Yuhricul hayye minel meyyiti ve yuhricul meyyite minel hayyi ve yuhyil arda ba'de mevtiha, ve kezalike tuhrecun."},
  {id: 3429, sure_no: 20, ayet_no: 20, sure_adi: "Rum", text: "وَمِنْ اٰيَاتِه۪ٓ اَنْ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ اِذَٓا اَنْتُمْ بَشَرٌ تَنْتَشِرُونَ", translation: "Sizi topraktan yaratması, O'nun ayetlerindendir. Ardından da yeryüzüne dağılan bir beşer oldunuz.", transliteration: "Ve min ayatihi en halakakum min turabin summe iza entum beşerun tenteşirun."},
  {id: 3430, sure_no: 21, ayet_no: 21, sure_adi: "Rum", text: "وَمِنْ اٰيَاتِه۪ٓ اَنْ خَلَقَ لَكُمْ مِنْ اَنْفُسِكُمْ اَزْوَاجاً لِتَسْكُـنُٓوا اِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَوَدَّةً وَرَحْمَةًۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ", translation: "O'nun ayetlerinden biri de sizin için kendi cinsinizden eşler yaratmasıdır. Siz, onunla dinginleşir huzur bulursunuz. Birbirinize karşı, aranızda sevgi ve rahmet oluşturdu. Düşünen bir toplum için bunda nice ayetler vardır.", transliteration: "Ve min ayatihi en halaka lekum min enfusikum ezvacen li teskunu ileyha ve ceale beynekum meveddeten ve rahmeh, inne fi zalike le ayatin li kavmin yetefekkerun."},
  {id: 3431, sure_no: 22, ayet_no: 22, sure_adi: "Rum", text: "وَمِنْ اٰيَاتِه۪ خَلْقُ السَّمٰوَاتِ وَالْاَرْضِ وَاخْتِلَافُ اَلْسِنَتِكُمْ وَاَلْوَانِكُمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِلْعَالِم۪ينَ", translation: "Göklerin ve yerin yaratılışı, dillerinizin ve renklerinizin farklı oluşu O'nun ayetlerindendir Bunda bilenler için ayetler vardır.", transliteration: "Ve min ayatihi halkus semavati vel ardı vahtilafu elsinetikum ve elvanikum, inne fi zalike le ayatin lil alimin."},
  {id: 3432, sure_no: 23, ayet_no: 23, sure_adi: "Rum", text: "وَمِنْ اٰيَاتِه۪ مَنَامُكُمْ بِالَّيْلِ وَالنَّهَارِ وَابْتِغَٓاؤُ۬كُمْ مِنْ فَضْلِه۪ۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَسْمَعُونَ", translation: "Yine gece uyumanız, gündüz O'nun fazlından rızık aramanız O'nun ayetlerindendir. Bunda, anlayan bir halk için ayetler vardır.", transliteration: "Ve min ayatihi menamukum bil leyli ven nehari vebtigaukum min fadlih, inne fi zalike le ayatin li kavmin yesmeun."},
  {id: 3433, sure_no: 24, ayet_no: 24, sure_adi: "Rum", text: "وَمِنْ اٰيَاتِه۪ يُر۪يكُمُ الْبَرْقَ خَوْفاً وَطَمَعاً وَيُنَزِّلُ مِنَ السَّمَٓاءِ مَٓاءً فَيُحْـي۪ بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَعْقِلُونَ", translation: "Size, bir yandan korku veren bir yandan da yağmurun belirtisi olarak umut veren şimşeği göstermesi ve gökten su indirerek, ölmüş yeryüzüne hayat vermesi O'nun ayetlerindendir. Bunda aklını kullanan bir kavim için kesinlikle ayetler vardır.", transliteration: "Ve min ayatihi yurikumul berka havfen ve tamaan, ve yunezzilu mines semai maen fe yuhyi bihil arda ba'de mevtiha, inne fi zalike le ayatin li kavmin ya'kılun."},
  {id: 3434, sure_no: 25, ayet_no: 25, sure_adi: "Rum", text: "وَمِنْ اٰيَاتِه۪ٓ اَنْ تَقُومَ السَّمَٓاءُ وَالْاَرْضُ بِاَمْرِه۪ۜ ثُمَّ اِذَا دَعَاكُمْ دَعْوَةً مِنَ الْاَرْضِ اِذَٓا اَنْتُمْ تَخْرُجُونَ", translation: "Göklerin ve yerin, buyruğu ile durması, O'nun ayetlerindendir. Sonra bir tek çağırışla çağırdığı zaman yerden derhal çıkacaksınız.", transliteration: "Ve min ayatihi en tekumes semau vel ardu bi emrih, summe iza deakum da'veten minel ardı iza entum tahrucun."},
  {id: 3435, sure_no: 26, ayet_no: 26, sure_adi: "Rum", text: "وَلَهُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ كُلٌّ لَهُ قَانِتُونَ", translation: "Göklerde ve yerde olanların tamamı O'nundur. Hepsi O'nun yasalarına bağlıdır.", transliteration: "Ve lehu men fis semavati vel ard, kullun lehu kanitun."},
  {id: 3436, sure_no: 27, ayet_no: 27, sure_adi: "Rum", text: "وَهُوَ الَّذ۪ي يَبْدَؤُا الْخَلْقَ ثُمَّ يُع۪يدُهُ وَهُوَ اَهْوَنُ عَلَيْهِۜ وَلَهُ الْمَثَلُ الْاَعْلٰى فِي السَّمٰوَاتِ وَالْاَرْضِۚ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ۟", translation: "Yaratmayı ilk kez başlatan, sonra onu tekrar eden O'dur. Bu, O'nun için çok kolaydır. Göklerde ve yerde en yüce nitelikler O'nundur. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve huvellezi yebdeul halka summe yuiduhu, ve huve ehvenu aleyh, ve lehul meselul a'la fis semavati vel ard, ve huvel azizul hakim."},
  {id: 3437, sure_no: 28, ayet_no: 28, sure_adi: "Rum", text: "ضَرَبَ لَكُمْ مَثَلاً مِنْ اَنْفُسِكُمْۜ هَلْ لَكُمْ مِنْ مَا مَلَكَتْ اَيْمَانُكُمْ مِنْ شُرَكَٓاءَ ف۪ي مَا رَزَقْنَاكُمْ فَاَنْتُمْ ف۪يهِ سَوَٓاءٌ تَخَافُونَهُمْ كَخ۪يفَتِكُمْ اَنْفُسَكُمْۜ كَذٰلِكَ نُفَصِّلُ الْاٰيَاتِ لِقَوْمٍ يَعْقِلُونَ", translation: "Kendinizden, size bir örnek vermektedir: \"Size verdiğimiz mallarda yeminle hak sahibi olduğunuz kimselerden ortaklarınız var mı? Siz ve onlar bu mallarda eşit misiniz? Onları, birbirinizi saydığınız gibi sayar mısınız? Aklını kullanan bir kavim için ayetleri işte böyle açıklıyoruz.\"", transliteration: ": Darabe lekum meselen min enfusikum, hel lekum min ma meleket eymanukum min şurekae fi ma rezaknakum fe entum fihi sevaun tehafunehum ke hifetikum enfusekum, kezalike nufassılul ayati li kavmin ya'kılun."},
  {id: 3438, sure_no: 29, ayet_no: 29, sure_adi: "Rum", text: "بَلِ اتَّبَعَ الَّذ۪ينَ ظَلَمُٓوا اَهْوَٓاءَهُمْ بِغَيْرِ عِلْمٍۚ فَمَنْ يَهْد۪ي مَنْ اَضَلَّ اللّٰهُۜ وَمَا لَهُمْ مِنْ نَاصِر۪ينَ", translation: "Hayır, zalimler körü körüne hevalarına uydular. Bundan sonra Allah'ın şaşırttığını kim doğru yola iletebilir? Ve onların yardımcıları da yoktur.", transliteration: "Belittebeallezine zalemu ehvaehum bi gayri ilm, fe men yehdi men edallallah, ve ma lehum min nasırin."},
  {id: 3439, sure_no: 30, ayet_no: 30, sure_adi: "Rum", text: "فَاَقِمْ وَجْهَكَ لِلدّ۪ينِ حَن۪يفاًۜ فِطْرَتَ اللّٰهِ الَّت۪ي فَطَرَ النَّاسَ عَلَيْهَاۜ لَا تَبْد۪يلَ لِخَلْقِ اللّٰهِۜ ذٰلِكَ الدّ۪ينُ الْقَيِّمُۗ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَۗ", translation: "O halde hanif olarak dine yüzünü ikame et. İnsanları, üzerinde yaratmış olduğu Allah'ın fıtratına. Allah'ın yaratmasında değişme olmaz. Kayyum olan din budur. Fakat insanların çoğu bilmiyorlar.", transliteration: "Fe ekim vecheke lid dini hanifa, fıtratallahilleti fataran nase aleyha, la tebdile li halkıllah, zaliked dinul kayyimu ve lakinne ekseren nasi la ya'lemun."},
  {id: 3440, sure_no: 31, ayet_no: 31, sure_adi: "Rum", text: "مُن۪يب۪ينَ اِلَيْهِ وَاتَّقُوهُ وَاَق۪يمُوا الصَّلٰوةَ وَلَا تَكُونُوا مِنَ الْمُشْرِك۪ينَۙ", translation: "O'na yönelin. O'na karşı takva sahibi olun. Salatı ikame edin. Müşriklerden olmayın.", transliteration: "Munibine ileyhi vettekuhu ve ekimus salate ve la tekunu minel muşrikin."},
  {id: 3441, sure_no: 32, ayet_no: 32, sure_adi: "Rum", text: "مِنَ الَّذ۪ينَ فَرَّقُوا د۪ينَهُمْ وَكَانُوا شِيَعاًۜ كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ", translation: "Dinlerini parçalara bölen, gruplara ayrılan ve her grubun kendi yanındakiyle böbürlendiği kimselerden olmayın.", transliteration: "Minellezine ferraku dinehum ve kanu şiyea, kullu hızbin bima ledeyhim ferihun."},
  {id: 3442, sure_no: 33, ayet_no: 33, sure_adi: "Rum", text: "وَاِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُن۪يب۪ينَ اِلَيْهِ ثُمَّ اِذَٓا اَذَاقَهُمْ مِنْهُ رَحْمَةً اِذَا فَر۪يقٌ مِنْهُمْ بِرَبِّهِمْ يُشْرِكُونَۙ", translation: "İnsanların başlarına bir sıkıntı gelince, Rabb'lerine yönelerek O'na dua ederler. Sonra, onlara kendinden bir rahmet tattırınca, onlardan bir kısmı Rabb'lerine şirk koşarlar.", transliteration: "Ve iza messen nase durrun deav rabbehum munibine ileyhi summe iza ezakahum minhu rahmeten iza ferikun minhum bi rabbihim yuşrikun."},
  {id: 3443, sure_no: 34, ayet_no: 34, sure_adi: "Rum", text: "لِيَكْفُرُوا بِمَٓا اٰتَيْنَاهُمْۜ فَتَمَتَّعُوا۠ فَسَوْفَ تَعْلَمُونَ", translation: "Kendilerine verdiğimiz şeylere nankörlük ederler. İstediğiniz gibi yaşayın bakalım! İleride gerçeği göreceksiniz.", transliteration: "Li yekfuru bima ateynahum, fe temetteu fe sevfe ta'lemun."},
  {id: 3444, sure_no: 35, ayet_no: 35, sure_adi: "Rum", text: "اَمْ اَنْزَلْنَا عَلَيْهِمْ سُلْطَاناً فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا بِه۪ يُشْرِكُونَ", translation: "Yoksa onlara bir sultan gönderdik de o, onlara şirk koşmalarını mı söylüyor?", transliteration: "Em enzelna aleyhim sultanen fe huve yetekellemu bima kanu bihi yuşrikun."},
  {id: 3445, sure_no: 36, ayet_no: 36, sure_adi: "Rum", text: "وَاِذَٓا اَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوا بِهَاۜ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ اَيْد۪يهِمْ اِذَا هُمْ يَقْنَطُونَ", translation: "İnsanlara bir rahmet tattırdığımız zaman, onunla şımarırlar. Kendi yaptıklarından dolayı başlarına bir kötülük gelirse, o zaman hemen umutsuzluğa kapılırlar.", transliteration: "Ve iza ezaknen nase rahmeten ferihu biha, ve in tusıbhum seyyietun bima kaddemet eydihim iza hum yaknetun."},
  {id: 3446, sure_no: 37, ayet_no: 37, sure_adi: "Rum", text: "اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ", translation: "Allah'ın dilediği kimse için rızkı genişlettiğini ve ölçülendirdiğini bilmediler mi? Bunda iman eden bir halk için ayetler vardır.", transliteration: "EE ve lem yerev ennellahe yebsutur rızka li men yeşau ve yakdir, inne fi zalike le ayatin li kavmin yu'minun."},
  {id: 3447, sure_no: 38, ayet_no: 38, sure_adi: "Rum", text: "فَاٰتِ ذَا الْقُرْبٰى حَقَّهُ وَالْمِسْك۪ينَ وَابْنَ السَّب۪يلِۜ ذٰلِكَ خَيْرٌ لِلَّذ۪ينَ يُر۪يدُونَ وَجْهَ اللّٰهِۘ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "Öyleyse; yakınlara, miskine ve yol oğluna hakkını ver. Bu, Allah'ın yüzünü dileyenler için daha hayırlıdır. İşte onlar kurtuluşa erenlerdir.", transliteration: "Fe ati zel kurba hakkahu vel miskine vebnes sebil, zalike hayrun lillezine yuridune vechallahi ve ulaike humul muflihun."},
  {id: 3448, sure_no: 39, ayet_no: 39, sure_adi: "Rum", text: "وَمَٓا اٰتَيْتُمْ مِنْ رِباً لِيَرْبُوَ۬ا ف۪ٓي اَمْوَالِ النَّاسِ فَلَا يَرْبُوا عِنْدَ اللّٰهِۚ وَمَٓا اٰتَيْتُمْ مِنْ زَكٰوةٍ تُر۪يدُونَ وَجْهَ اللّٰهِ فَاُو۬لٰٓئِكَ هُمُ الْمُضْعِفُونَ", translation: "İnsanların mallarında artsın diye ribadan verdiğiniz, Allah'ın katında artmaz. Allah'ın yüzünü dileyerek zekattan verenler, işte bunu yapanlar kat kat arttıranlardır.", transliteration: "Ve ma ateytum min riben li yerbuve fi emvalin nasi fe la yerbu indallah, ve ma ateytum min zekatin turidune vechallahi fe ulaike humul mud'ıfun."},
  {id: 3449, sure_no: 40, ayet_no: 40, sure_adi: "Rum", text: "اَللّٰهُ الَّذ۪ي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُم۪يتُكُمْ ثُمَّ يُحْي۪يكُمْۜ هَلْ مِنْ شُرَكَٓائِكُمْ مَنْ يَفْعَلُ مِنْ ذٰلِكُمْ مِنْ شَيْءٍۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يُشْرِكُونَ۟", translation: "O Allah ki, sizi yaratan, sonra sizi yaşatan, sonra sizi öldüren, sonra sizi diriltendir. Ortak koştuklarınızdan bunları yapacak olan var mı? Allah, onların şirk koştuklarından münezzehtir, yüceler yücesidir.", transliteration: "Allahullezi halakakum summe rezekakum summe yumitukum summe yuhyikum, hel min şurekaikum men yef'alu min zalikum min şey', subhanehu ve teala amma yuşrikun."},
  {id: 3450, sure_no: 41, ayet_no: 41, sure_adi: "Rum", text: "ظَهَرَ الْفَسَادُ فِي الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ اَيْدِي النَّاسِ لِيُذ۪يقَهُمْ بَعْضَ الَّذ۪ي عَمِلُوا لَعَلَّهُمْ يَرْجِعُونَ", translation: "İnsanların kendi yaptıklarından dolayı karada ve denizde fesat ortaya çıktı. Belki akıllarını başlarına alırlar diye yaptıklarının bir kısmı onlara tattırıldı.", transliteration: "Zaharel fesadu fil berri vel bahri bima kesebet eydin nasi, li yuzikahum ba'dallezi amilu leallehum yerciun."},
  {id: 3451, sure_no: 42, ayet_no: 42, sure_adi: "Rum", text: "قُلْ س۪يرُوا فِي الْاَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلُۜ كَانَ اَكْثَرُهُمْ مُشْرِك۪ينَ", translation: "De ki: \"Yeryüzünde gezin de daha öncekilerin sonlarının nasıl olduğuna bakın. Onların çoğu Müşriklerdendi.\"", transliteration: "Kul siru fil ardı fenzuru keyfe kane akıbetullezine min kabl, kane ekseruhum muşrikin."},
  {id: 3452, sure_no: 43, ayet_no: 43, sure_adi: "Rum", text: "فَاَقِمْ وَجْهَكَ لِلدّ۪ينِ الْقَيِّمِ مِنْ قَبْلِ اَنْ يَأْتِيَ يَوْمٌ لَا مَرَدَّ لَهُ مِنَ اللّٰهِ يَوْمَئِذٍ يَصَّدَّعُونَ", translation: "Öyleyse Allah tarafından geri döndürülmesi olmayan gün gelmeden önce, yönünü, koruyup güvene alan dine çevir, ona sıkı sıkıya bağlan. O Gün onlar bölük bölük ayrılırlar.", transliteration: "Fe ekim vecheke lid dinil kayyimi min kabli en ye'tiye yevmun la meredde lehu minallahi yevmeizin yassaddeun."},
  {id: 3453, sure_no: 44, ayet_no: 44, sure_adi: "Rum", text: "مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُۚ وَمَنْ عَمِلَ صَالِحاً فَلِاَنْفُسِهِمْ يَمْهَدُونَۙ", translation: "Kim küfrederse, küfrü kendi aleyhinedir. Kim salihatı yaparsa, onlar kendileri için iyi bir gelecek hazırlamış olurlar.", transliteration: "Men kefere fe aleyhi kufruh, ve men amile salihan fe li enfusihim yemhedun."},
  {id: 3454, sure_no: 45, ayet_no: 45, sure_adi: "Rum", text: "لِيَجْزِيَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْ فَضْلِه۪ۜ اِنَّهُ لَا يُحِبُّ الْكَافِر۪ينَ", translation: "İman Eden ve salihatı yapanlara lütfundan karşılık verir. O, Kafirleri sevmez.", transliteration: "Li yecziyellezine amenu ve amilus salihati min fadlih, innehu la yuhıbbul kafirin."},
  {id: 3455, sure_no: 46, ayet_no: 46, sure_adi: "Rum", text: "وَمِنْ اٰيَاتِه۪ٓ اَنْ يُرْسِلَ الرِّيَاحَ مُبَشِّرَاتٍ وَلِيُذ۪يقَكُمْ مِنْ رَحْمَتِه۪ وَلِتَجْرِيَ الْفُلْكُ بِاَمْرِه۪ وَلِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ", translation: "Rüzgarları müjdeci olarak göndererek, rahmetinden size tattırması, buyruğu ile gemilerin akıp gitmesi ve lütfundan rızık istemeniz, O'nun ayetlerindendir. Umulur ki şükredenlerden olursunuz.", transliteration: "Ve min ayatihi en yursiler riyaha mubeşşiratin ve li yuzikakum min rahmetihi ve li tecriyel fulku bi emrihi ve li tebtegu min fadlihi ve leallekum teşkurun."},
  {id: 3456, sure_no: 47, ayet_no: 47, sure_adi: "Rum", text: "وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ رُسُلاً اِلٰى قَوْمِهِمْ فَجَٓاؤُ۫هُمْ بِالْبَيِّنَاتِ فَانْتَقَمْنَا مِنَ الَّذ۪ينَ اَجْرَمُواۜ وَكَانَ حَقاًّ عَلَيْنَا نَصْرُ الْمُؤْمِن۪ينَ", translation: "Ant olsun ki, senden önce, birçok Resul'ü halklarına gönderdik. Onlara beyyinat getirdiler. Ardından suçlulardan intikam aldık. Mü'min kimselere yardım etmek Biz'e hak oldu.", transliteration: "Ve lekad erselna min kablike rusulen ila kavmihim fe cauhum bil beyyinati fentekamna minellezine ecramu, ve kane hakkan aleyna nasrul mu'minin."},
  {id: 3457, sure_no: 48, ayet_no: 48, sure_adi: "Rum", text: "اَللّٰهُ الَّذ۪ي يُرْسِلُ الرِّيَاحَ فَتُث۪يرُ سَحَاباً فَيَبْسُطُهُ فِي السَّمَٓاءِ كَيْفَ يَشَٓاءُ وَيَجْعَلُهُ  كِسَفاً فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِه۪ۚ فَاِذَٓا اَصَابَ بِه۪ مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ٓ اِذَا هُمْ يَسْتَبْشِرُونَ", translation: "Allah, rüzgarı göndererek bulutları hareket ettirir. Sonra onu gökyüzünde dilediği gibi yayıp kütleler haline getirir. Sonra onun arasından yağmurun çıktığını görürsün. Böylece kullarından dilediğine onu isabet ettirdiği zaman onlar sevinirler.", transliteration: "Allahullezi yursilur riyaha fe tusiru sehaben fe yebsutuhu fis semai keyfe yeşau ve yec'aluhu kisefen fe terel vedka yahrucu min hılalih, fe iza esabe bihi men yeşau min ibadihi iza hum yestebşirun."},
  {id: 3458, sure_no: 49, ayet_no: 49, sure_adi: "Rum", text: "وَاِنْ كَانُوا مِنْ قَبْلِ اَنْ يُنَزَّلَ عَلَيْهِمْ مِنْ قَبْلِه۪ لَمُبْلِس۪ينَ", translation: "Oysa onlar, yağmurun onlara indirilmesinden önce gerçekten ümitlerini kesenlerdi.", transliteration: "Ve in kanu min kabli en yunezzele aleyhim min kablihi le mublisin."},
  {id: 3459, sure_no: 50, ayet_no: 50, sure_adi: "Rum", text: "فَانْظُرْ اِلٰٓى اٰثَارِ رَحْمَتِ اللّٰهِ كَيْفَ يُحْـيِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ اِنَّ ذٰلِكَ لَمُحْـيِ الْمَوْتٰىۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Allah'ın rahmetinin eserlerine bir bak! Ölümünden sonra yeryüzünü nasıl diriltiyor! Ölüleri de kesinlikle diriltecek O'dur. O, Her Şeye Güç Yetiren'dir.", transliteration: "Fenzur ila asari rahmetillahi keyfe yuhyil arda ba'de mevtiha, inne zalike le muhyil mevta, ve huve ala kulli şey'in kadir."},
  {id: 3460, sure_no: 51, ayet_no: 51, sure_adi: "Rum", text: "وَلَئِنْ اَرْسَلْنَا ر۪يحاً فَرَاَوْهُ مُصْفَراًّ لَظَلُّوا مِنْ بَعْدِه۪ يَكْفُرُونَ", translation: "Eğer rüzgar göndersek, ekini ve bitkileri sararmış görseler, nankörlük etmeye başlarlar.", transliteration: "Ve le in erselna rihan fe raevhu musfarran le zallu min ba'dihi yekfurun."},
  {id: 3461, sure_no: 52, ayet_no: 52, sure_adi: "Rum", text: "فَاِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَٓاءَ اِذَا وَلَّوْا مُدْبِر۪ينَ", translation: "Şu bir gerçek ki, sen ölülere duyuramazsın. Arkasını dönüp giden sağırlara da çağrıyı duyuramazsın.", transliteration: "Fe inneke la tusmiul mevta ve la tusmius summed duae iza vellev mudbirin."},
  {id: 3462, sure_no: 53, ayet_no: 53, sure_adi: "Rum", text: "وَمَٓا اَنْـتَ بِهَادِ الْعُمْيِ عَنْ ضَلَالَتِهِمْۜ اِنْ تُسْمِعُ اِلَّا مَنْ يُؤْمِنُ بِاٰيَاتِنَا فَهُمْ مُسْلِمُونَ۟", translation: "Sen, körleri sapkınlıklarından kurtarıp doğru yola iletemezsin. Sen, ayetlerimizi ancak iman edeceklere duyurabilirsin; zira onlar teslim olmuş kimselerdir.", transliteration: "Ve ma ente bi hadil umyi an dalaletihim, in tusmiu illa men yu'minu bi ayatina fe hum muslimun."},
  {id: 3463, sure_no: 54, ayet_no: 54, sure_adi: "Rum", text: "اَللّٰهُ الَّذ۪ي خَلَقَكُمْ مِنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْ بَعْدِ قُوَّةٍ ضَعْفاً وَشَيْبَةًۜ يَخْلُقُ مَا يَشَٓاءُۚ وَهُوَ الْعَل۪يمُ الْقَد۪يرُ", translation: "Sizi önce zayıf yaratıp, ardından size güç veren sonra bu gücün ardından sizi zayıf ve yaşlı kılan Allah'tır. O, dilediği şeyi yaratır. Ve O, Her Şeyi Bilen'dir, Her Şeye Gücü Yeten'dir.", transliteration: "Allahullezi halakakum min da'fin summe ceale min ba'di da'fin kuvveten summe ceale min ba'di kuvvetin da'fen ve şeybeh, yahluku ma yeşau, ve huvel alimul kadir."},
  {id: 3464, sure_no: 55, ayet_no: 55, sure_adi: "Rum", text: "وَيَوْمَ تَقُومُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُونَۙ مَا لَبِثُوا غَيْرَ سَاعَةٍۜ  كَذٰلِكَ كَانُوا يُؤْفَكُونَ", translation: "Kıyamet'in koptuğu gün, mücrimler dünyada bir saatten fazla kalmadıklarına yemin ederler. Onlar işte böyle döndürülüyorlardı.", transliteration: "Ve yevme tekumus saatu yuksimul mucrimune ma lebisu gayra saah, kezalike kanu yu'fekun."},
  {id: 3465, sure_no: 56, ayet_no: 56, sure_adi: "Rum", text: "وَقَالَ الَّذ۪ينَ اُو۫تُوا الْعِلْمَ وَالْا۪يمَانَ لَقَدْ لَبِثْتُمْ ف۪ي كِتَابِ اللّٰهِ اِلٰى يَوْمِ الْبَعْثِۘ فَهٰذَا يَوْمُ الْبَعْثِ وَلٰكِنَّكُمْ كُنْتُمْ لَا تَعْلَمُونَ", translation: "Kendilerine bilgi ve iman verilenler: \"Ant olsun ki, siz Allah'ın yasasındaki diriliş gününe kadar kaldınız. İşte bu dirilme günüdür. Ne var ki siz bu gerçeği algılayamadınız.", transliteration: "Ve kalellezine utul ilme vel imane lekad lebistum fi kitabillahi ila yevmil ba'si fe haza yevmul ba'si ve lakinnekum kuntum la ta'lemun."},
  {id: 3466, sure_no: 57, ayet_no: 57, sure_adi: "Rum", text: "فَيَوْمَئِذٍ لَا يَنْفَعُ الَّذ۪ينَ ظَلَمُوا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُونَ", translation: "Artık O Gün zalimlere mazeretleri yarar sağlamaz. Onlardan mazeret de istenmeyecek.", transliteration: "Fe yevmeizin la yenfeullezine zalemu ma'ziratuhum ve la hum yusta'tebun."},
  {id: 3467, sure_no: 58, ayet_no: 58, sure_adi: "Rum", text: "وَلَقَدْ ضَرَبْنَا لِلنَّاسِ ف۪ي هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۜ وَلَئِنْ جِئْتَهُمْ بِاٰيَةٍ لَيَقُولَنَّ الَّذ۪ينَ كَفَرُٓوا اِنْ اَنْتُمْ اِلَّا مُبْطِلُونَ", translation: "Ant olsun ki, insanlar için bu Kur'an'da her türlü örneği vererek gerçekleri anlattık. Sen onlara bir ayet de getirsen, Kafirler kesinlikle: \"Siz, sadece, asılsız iddialarda bulunan kimselersiniz.\" derler.", transliteration: "Ve lekad darebna lin nasi fi hazel kur'ani min kulli mesel, ve le in ci'tehum bi ayetin le yekulennellezine keferu in entum illa mubtılun."},
  {id: 3468, sure_no: 59, ayet_no: 59, sure_adi: "Rum", text: "كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوبِ الَّذ۪ينَ لَا يَعْلَمُونَ", translation: "İşte Allah, bilmeyen kimselerin kalplerinin üzerini böyle mühürler.", transliteration: "Kezalike yatbaullahu ala kulubillezine la ya'lemun."},
  {id: 3469, sure_no: 60, ayet_no: 60, sure_adi: "Rum", text: "فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَلَا يَسْتَخِفَّنَّكَ الَّذ۪ينَ لَا يُوقِنُونَ", translation: "Öyleyse sabret, kuşkusuz Allah'ın verdiği söz kesindir. Sakın ha! İman etmeyenler seni gevşetmesinler.", transliteration: "Fasbir inne va'dallahi hakkun ve la yestahıffennekellezine la yukınun."},
  {id: 3470, sure_no: 1, ayet_no: 1, sure_adi: "Lokman", text: "الٓمٓ۠", translation: "Elif. Lam. Mim.", transliteration: "Elif lam mim."},
  {id: 3471, sure_no: 2, ayet_no: 2, sure_adi: "Lokman", text: "تِلْكَ اٰيَاتُ الْكِتَابِ الْحَك۪يمِۙ", translation: "İşte bunlar, hakim olan Kitap'ın ayetleridir.", transliteration: "Tilke ayatul kitabil hakim."},
  {id: 3472, sure_no: 3, ayet_no: 3, sure_adi: "Lokman", text: "هُدًى وَرَحْمَةً لِلْمُحْسِن۪ينَۙ", translation: "İyiler için yol gösterici ve rahmettir.", transliteration: "Huden ve rahmeten lil muhsinin."},
  {id: 3473, sure_no: 4, ayet_no: 4, sure_adi: "Lokman", text: "اَلَّذ۪ينَ يُق۪يمُونَ الصَّلٰوةَ وَيُؤْتُونَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوقِنُونَۜ", translation: "Onlar, salatı ikame ederler ve zekatı yaparlar. Onlar Ahiret'e kesin olarak iman ederler.", transliteration: "Ellezine yukimunes salate ve yu'tunez zekate ve hum bil ahıreti hum yukinun."},
  {id: 3474, sure_no: 5, ayet_no: 5, sure_adi: "Lokman", text: "اُو۬لٰٓئِكَ عَلٰى هُدًى مِنْ رَبِّهِمْ وَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "İşte onlar, Rabb'leri tarafından doğru yol üzerindedirler. Onlar kurtuluşa erenlerdir.", transliteration: "Ulaike ala huden min rabbihim ve ulaike humul muflihun."},
  {id: 3475, sure_no: 6, ayet_no: 6, sure_adi: "Lokman", text: "وَمِنَ النَّاسِ مَنْ يَشْتَر۪ي لَهْوَ الْحَد۪يثِ لِيُضِلَّ عَنْ سَب۪يلِ اللّٰهِ بِغَيْرِ عِلْمٍۙ وَيَتَّخِذَهَا هُزُواًۜ اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ مُه۪ينٌ", translation: "İnsanlardan, bilgisi olmadığı halde; Allah'ın yolundan saptırmak ve onu alay konusu edinmek için gerçeği boş hadislerle değiştirenler vardır. İşte onlar için aşağılayıcı bir azap vardır.", transliteration: "Ve minen nasi men yeşteri lehvel hadisi li yudılle an sebilillahi bi gayri ilmin ve yettehızeha huzuva, ulaike lehum azabun muhin."},
  {id: 3476, sure_no: 7, ayet_no: 7, sure_adi: "Lokman", text: "وَاِذَا تُتْلٰى عَلَيْهِ اٰيَاتُنَا وَلّٰى مُسْتَكْبِراً كَاَنْ لَمْ يَسْمَعْهَا كَاَنَّ ف۪ٓي اُذُنَيْهِ وَقْراًۚ فَبَشِّرْهُ بِعَذَابٍ اَل۪يمٍ", translation: "Ona ayetlerimiz okunduğu zaman sanki onları duymamış, sanki kulakları sağırmış gibi, küstahça umursamazlıktan gelir. Öyleyse onu, çok acı veren bir azaptan haberdar et.", transliteration: "Ve iza tutla aleyhi ayatuna vella mustekbiren ke en lem yesma'ha ke enne fi uzuneyhi vakra, fe beşşirhu bi azabin elim."},
  {id: 3477, sure_no: 8, ayet_no: 8, sure_adi: "Lokman", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتُ النَّع۪يمِۙ", translation: "İman eden ve salihatı yapanlar için, Naim Cennet'ler vardır.", transliteration: "İnnellezine amenu ve amilus salihati lehum cennatun na'im."},
  {id: 3478, sure_no: 9, ayet_no: 9, sure_adi: "Lokman", text: "خَالِد۪ينَ ف۪يهَاۜ وَعْدَ اللّٰهِ حَقاًّۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Orada sürekli kalırlar. Allah'ın vaadi gerçektir. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Halidine fiha, va'dallahi hakka, ve huvel azizul hakim."},
  {id: 3479, sure_no: 10, ayet_no: 10, sure_adi: "Lokman", text: "خَلَقَ السَّمٰوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَاَلْقٰى فِي الْاَرْضِ رَوَاسِيَ اَنْ تَم۪يدَ بِكُمْ وَبَثَّ ف۪يهَا مِنْ كُلِّ دَٓابَّةٍۜ وَاَنْزَلْنَا مِنَ السَّمَٓاءِ مَٓاءً فَاَنْبَتْنَا ف۪يهَا مِنْ كُلِّ زَوْجٍ كَر۪يمٍ", translation: "Gördüğünüz gibi gökleri direksiz yarattı. Yeryüzüne de sizi sarsmaması için ağır baskılar yerleştirdi. Orada her türlü canlıyı yaydı. Gökten su indirip, orada her kerim çiftten bitki bitirdi.", transliteration: "Halakas semavati bi gayri amedin terevneha ve elka fil ardı revasiye en temide bikum ve besse fiha min kulli dabbeh, ve enzelna mines semai maen fe enbetna fiha min kulli zevcin kerim."},
  {id: 3480, sure_no: 11, ayet_no: 11, sure_adi: "Lokman", text: "هٰذَا خَلْقُ اللّٰهِ فَاَرُون۪ي مَاذَا خَلَقَ الَّذ۪ينَ مِنْ دُونِه۪ۜ بَلِ الظَّالِمُونَ ف۪ي ضَلَالٍ مُب۪ينٍ۟", translation: "Bu Allah'ın yaratmasıdır. Haydi, bana O'ndan başkasının ne yarattığını gösterin. Hayır! Zalimler apaçık bir sapkınlık içindedirler.", transliteration: "Haza halkullahi fe eruni maza halakallezine min dunih, beliz zalimune fi dalalin mubin."},
  {id: 3481, sure_no: 12, ayet_no: 12, sure_adi: "Lokman", text: "وَلَقَدْ اٰتَيْنَا لُقْمٰنَ الْحِكْمَةَ اَنِ اشْكُرْ لِلّٰهِۜ وَمَنْ يَشْكُرْ فَاِنَّمَا يَشْكُرُ لِنَفْسِه۪ۚ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ حَم۪يدٌ", translation: "Ant olsun ki, \"Allah'a şükret.\" diye, Lokman'a hikmet verdik. Kim şükrederse kendisi için şükreder. Kim de nankörlük ederse bilsin ki Allah kimsenin şükrüne muhtaç değildir. Bütün övgüler O'nadır.", transliteration: "Ve lekad ateyna lukmanel hikmete enişkur lillah, ve men yeşkur fe innema yeşkuru li nefsih, ve men kefere fe innellahe ganiyyun hamid."},
  {id: 3482, sure_no: 13, ayet_no: 13, sure_adi: "Lokman", text: "وَاِذْ قَالَ لُقْمٰنُ لِابْنِه۪ وَهُوَ يَعِظُهُ يَا بُنَيَّ لَا تُشْرِكْ بِاللّٰهِۜ اِنَّ الشِّرْكَ لَظُلْمٌ عَظ۪يمٌ", translation: "Vaktiyle Lokman oğluna öğüt vererek: \"Ey yavrucuğum! Allah'a şirk koşma, kuşkusuz şirk koşmak çok büyük bir zulümdür.\" demişti.", transliteration: "Ve iz kale lukmanu libnihi ve huve yaızuhu ya buneyye la tuşrik billah, inneş şirke le zulmun azim."},
  {id: 3483, sure_no: 14, ayet_no: 14, sure_adi: "Lokman", text: "وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِۚ حَمَلَتْهُ اُمُّهُ وَهْناً عَلٰى وَهْنٍ وَفِصَالُهُ ف۪ي عَامَيْنِ اَنِ اشْكُرْ ل۪ي وَلِوَالِدَيْكَۜ اِلَيَّ الْمَص۪يرُ", translation: "İnsana, anne ve babasını vasiyet ettik. Onu, annesi zorluk üzerine zorluk içinde taşıdı. İki yıl boyunca ona süt verdi. Bana, anne ve babana şükret! Dönüş Bana'dır.", transliteration: "Ve vassaynel insane bi valideyh, hamelethu ummuhu vehnen ala vehnin ve fisaluhu fi ameyni enişkurli ve li valideyk, ileyyel masir."},
  {id: 3484, sure_no: 15, ayet_no: 15, sure_adi: "Lokman", text: "وَاِنْ جَاهَدَاكَ عَلٰٓى اَنْ تُشْرِكَ ب۪ي مَا لَيْسَ لَكَ بِه۪ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِي الدُّنْيَا مَعْرُوفاًۘ وَاتَّبِعْ سَب۪يلَ مَنْ اَنَابَ اِلَيَّۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Eğer o ikisi, hakkında bilgin olmayan bir şeyi Bana ortak koşman için seninle cihad ederlerse, o zaman sakın onlara itaat etme. Dünyada onlarla güzel geçin. Bana yönelen kimselerin yoluna uy. Sonra dönüşünüz Bana'dır. O zaman yaptığınız şeyleri size haber vereceğim.", transliteration: "Ve in cahedake ala en tuşrike bi ma leyse leke bihi ilmun fe la tutı'huma ve sahibhuma fid dunya magrufen vettebi' sebile men enabe ileyy, summe ileyye merciukum fe unebbiukum bi ma kuntum ta'melun."},
  {id: 3485, sure_no: 16, ayet_no: 16, sure_adi: "Lokman", text: "يَا بُنَيَّ اِنَّـهَٓا اِنْ تَكُ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ فَتَكُنْ ف۪ي صَخْرَةٍ اَوْ فِي السَّمٰوَاتِ اَوْ فِي الْاَرْضِ يَأْتِ بِهَا اللّٰهُۜ اِنَّ اللّٰهَ لَط۪يفٌ خَب۪يرٌ", translation: "\"Ey oğulcuğum! Yaptıkların bir hardal tanesi kadar olup da bir kayanın içinde veya gökyüzünde veya yer altında bile olsa, Allah onu meydana çıkarır. Kuşkusuz Allah, Bütün Ayrıntıları Bilen'dir, Her Şeyden Haberdar'dır.\"", transliteration: "Ya buneyye inneha in teku miskale habbetin min hardalin fe tekun fi sahretin ev fis semavati ev fil ardı ye'ti bihallah, innellahe latifun habir."},
  {id: 3486, sure_no: 17, ayet_no: 17, sure_adi: "Lokman", text: "يَا بُنَيَّ اَقِمِ الصَّلٰوةَ وَأْمُرْ بِالْمَعْرُوفِ وَانْهَ عَنِ الْمُنْكَرِ وَاصْبِرْ عَلٰى مَٓا اَصَابَكَۜ اِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُورِۚ", translation: "\"Ey oğulcuğum! Salatı ikame et, ma'ruf olanı yap, münker olandan sakındır. Karşılaştığın zorluklara sabret. Bunlar kararlılık göstermen gereken şeylerdir.\"", transliteration: "Ya buneyye ekımıs salate ve'mur bil ma'rufi venhe anil munkeri vasbir ala ma esabek, inne zalike min azmil umur."},
  {id: 3487, sure_no: 18, ayet_no: 18, sure_adi: "Lokman", text: "وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِي الْاَرْضِ مَرَحاًۜ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍۚ", translation: "\"Kibirli bir tavırla insanları küçümseyerek onlardan yüz çevirme ve yeryüzünde böbürlenerek yürüme. Kuşkusuz Allah, kendisini beğenen ve övünüp duranları sevmez.\"", transliteration: "Ve la tusa'ir haddeke lin nasi ve la temşi fil ardı meraha innellahe la yuhıbbu kulle muhtalin fehur."},
  {id: 3488, sure_no: 19, ayet_no: 19, sure_adi: "Lokman", text: "وَاقْصِدْ ف۪ي مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَۜ اِنَّ اَنْكَرَ الْاَصْوَاتِ لَصَوْتُ الْحَم۪يرِ۟", translation: "\"Yürüyüşünde ölçülü ol; sesini alçalt. Seslerin en çirkini, eşeklerin sesidir.\"", transliteration: "Vaksid fi meşyike vagdud min savtik, inne enkerel asvati le savtul hamir."},
  {id: 3489, sure_no: 20, ayet_no: 20, sure_adi: "Lokman", text: "اَلَمْ تَرَوْا اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَاَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظَاهِرَةً وَبَاطِنَةًۜ وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُن۪يرٍ", translation: "Allah'ın, göklerde ve yerde olan her şeyi sizin hizmetinize sunduğunu, görünen görünmeyen nimetleri sizin için bollaştırdığını görmüyor musunuz? Yine de kimi insanlar; bir bilgiye, bir yol göstericiye ve aydınlatıcı bir kitaba dayanmaksızın Allah hakkında tartışıyorlar.", transliteration: "EE lem terev ennellahe sehhare lekum ma fis semavati ve ma fil ardı ve esbega aleykum niamehu zahireten ve batıneh, ve minen nasi men yucadilu fillahi bi gayri ilmin ve la huden ve la kitabin munir."},
  {id: 3490, sure_no: 21, ayet_no: 21, sure_adi: "Lokman", text: "وَاِذَا ق۪يلَ لَهُمُ اتَّبِعُوا مَٓا اَنْزَلَ اللّٰهُ قَالُوا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ اٰبَٓاءَنَاۜ اَوَلَوْ كَانَ الشَّيْطَانُ يَدْعُوهُمْ اِلٰى عَذَابِ السَّع۪يرِ", translation: "Onlara, \"Allah'ın indirdiği şeye uyun.\" dendiği zaman: \"Hayır! Biz atalarımızı üzerinde bulduğumuz şeye uyarız.\" dediler. Peki! Ya şeytan onları alevli ateşin azabına çağırmış olsa da mı?", transliteration: "Ve iza kile lehumuttebiu ma enzelallahu kalu bel nettebiu ma vecedna aleyhi abaena, e ve lev kaneş şeytanu yed'uhum ila azabis sair."},
  {id: 3491, sure_no: 22, ayet_no: 22, sure_adi: "Lokman", text: "وَمَنْ يُسْلِمْ وَجْهَهُٓ اِلَى اللّٰهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۜ وَاِلَى اللّٰهِ عَاقِبَةُ الْاُمُورِ", translation: "Kim muhsin olarak, tüm varlığı ile Allah'a yönelirse, işte o, gerçekten en sağlam tutamağa tutunmuştur. İşlerin sonucu yalnızca Allah'a aittir.", transliteration: "Ve men yuslim vechehu ilallahi ve huve muhsinun fe kadistemseke bil urvetil vuska, ve ilallahi akibetul umur."},
  {id: 3492, sure_no: 23, ayet_no: 23, sure_adi: "Lokman", text: "وَمَنْ كَفَرَ فَلَا يَحْزُنْكَ كُفْرُهُ ۜ اِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُواۜ اِنَّ اللّٰهَ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Kim de Kafirlik ederse, artık onun Küfrü seni üzmesin. Onların dönüşü yalnızca Biz'edir. Yaptıkları şeyleri haber vereceğiz. Kuşkusuz Allah, sinelerde olanı en iyi bilendir.", transliteration: "Ve men kefere fe la yahzunke kufruh, ileyna merciuhum fe nunebbiuhum bi ma amil, innallahe alimun bi zatis sudur."},
  {id: 3493, sure_no: 24, ayet_no: 24, sure_adi: "Lokman", text: "نُمَتِّعُهُمْ قَل۪يلاً ثُمَّ نَضْطَرُّهُمْ اِلٰى عَذَابٍ غَل۪يظٍ", translation: "Onları biraz yararlandırırız. Sonra ağır bir azaba maruz bırakırız.", transliteration: "Numettiuhum kalilen summe nadtarruhum ila azabin galiz."},
  {id: 3494, sure_no: 25, ayet_no: 25, sure_adi: "Lokman", text: "وَلَئِنْ سَاَلْتَهُمْ مَنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ لَيَقُولُنَّ اللّٰهُۜ  قُلِ الْحَمْدُ لِلّٰهِۜ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَ", translation: "Eğer onlara: \"Gökleri ve yeri kim yarattı?\" diye sorsan, kesinlikle \"Allah.\" diyecekler. De ki: \"Bütün övgüler Allah'a layıktır.\" Hayır, çokları bunu idrak etmezler.", transliteration: "Ve le in seeltehum men halakas semavati vel arda le yekulunnellah, kulil hamdulillah, bel ekseruhum la ya'lemun."},
  {id: 3495, sure_no: 26, ayet_no: 26, sure_adi: "Lokman", text: "لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۜ اِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَم۪يدُ", translation: "Göklerde ve yerde olanlar, Allah'ındır. Allah, Hiçbir Şeye Muhtaç Olmayan'dır, Övgüye Değer Yegane Varlık'tır.", transliteration: "Lillahi ma fis semavati vel ard, innallahe huvel ganiyyul hamid."},
  {id: 3496, sure_no: 27, ayet_no: 27, sure_adi: "Lokman", text: "وَلَوْ اَنَّ مَا فِي الْاَرْضِ مِنْ شَجَرَةٍ اَقْلَامٌ وَالْبَحْرُ يَمُدُّهُ مِنْ بَعْدِه۪ سَبْعَةُ اَبْحُرٍ مَا نَفِدَتْ كَلِمَاتُ اللّٰهِۜ اِنَّ اللّٰهَ عَز۪يزٌ حَك۪يمٌ", translation: "Eğer yeryüzünde bulunan ağaçlar kalem olsaydı; deniz ve yedi deniz daha ilave edilseydi, Allah'ın kelimeleri tükenmezdi. Kuşkusuz Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir", transliteration: "Ve lev enne ma fil ardı min şeceretin aklamun vel bahru yemudduhu min ba'dihi seb'atu ebhurin ma nefidet kelimatullah, innellahe azizun hakim."},
  {id: 3497, sure_no: 28, ayet_no: 28, sure_adi: "Lokman", text: "مَا خَلْقُكُمْ وَلَا بَعْثُكُمْ اِلَّا كَنَفْسٍ وَاحِدَةٍۜ اِنَّ اللّٰهَ سَم۪يعٌ بَص۪يرٌ", translation: "Sizin yaratılmanız ve ölümden sonra diriltilmeniz Allah için bir kişiyi yaratmak ve diriltmek kadar kolaydır. Kuşkusuz Allah, Her Şeyi Duyan, Her Şeyi Gören'dir.", transliteration: "Ma halkukum ve la ba'sukum illa ke nefsin vahıdeh, innallahe semiun basir."},
  {id: 3498, sure_no: 29, ayet_no: 29, sure_adi: "Lokman", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ يُولِجُ الَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۘ   كُلٌّ يَجْر۪ٓي اِلٰٓى اَجَلٍ مُسَمًّى وَاَنَّ اللّٰهَ بِمَا تَعْمَلُونَ خَب۪يرٌ", translation: "Allah'ın; geceyi gündüze, gündüzü de geceye çevirdiğini görmüyor musun? Güneş'i ve Ay'ı buyruk altına aldı. Her biri belirlenmiş bir süreye kadar hareketini sürdürüyor. Kuşkusuz Allah, yaptıklarınızdan haberdardır.", transliteration: "E lem tere ennallahe yulicul leyle fin nehari ve yulicun nehare fil leyli, ve sehhareş şemse vel kamere kullun yecri ila ecelin musemmen ve ennallahe bi ma ta'melune habir."},
  {id: 3499, sure_no: 30, ayet_no: 30, sure_adi: "Lokman", text: "ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُونَ مِنْ دُونِهِ الْبَاطِلُۙ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَب۪يرُ۟", translation: "Çünkü Allah gerçekliğin ta kendisidir. Ve onların, O'nun yanı sıra yakardıkları ise kesinlikle gerçek dışıdır. Kuşkusuz Allah, Çok Yüce'dir, Çok Büyük'tür.", transliteration: "Zalike bi ennellahe huvel hakku ve enne ma yed'une min dunihil batılu ve ennallahe huvel aliyyul kebir."},
  {id: 3500, sure_no: 31, ayet_no: 31, sure_adi: "Lokman", text: "اَلَمْ تَرَ اَنَّ الْفُلْكَ تَجْر۪ي فِي الْبَحْرِ بِنِعْمَتِ اللّٰهِ لِيُرِيَكُمْ مِنْ اٰيَاتِه۪ۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ", translation: "Allah'ın nimeti ile denizde yüzen gemileri görmüyor musunuz? Allah, bununla size ayetlerinden göstermektedir. Kuşkusuz bunda sabreden ve şükredenler için dersler vardır.", transliteration: "E lem tere ennel fulke tecri fil bahri bi ni'metillahi li yuriyekum min ayatih inne fi zalike le ayatin li kulli sabbarin şekur."},
  {id: 3501, sure_no: 32, ayet_no: 32, sure_adi: "Lokman", text: "وَاِذَا غَشِيَهُمْ مَوْجٌ كَالظُّلَلِ دَعَوُا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَۚ فَلَمَّا نَجّٰيهُمْ اِلَى الْبَرِّ فَمِنْهُمْ مُقْتَصِدٌۜوَمَا يَجْحَدُ بِاٰيَاتِنَٓا اِلَّا كُلُّ خَتَّارٍ كَفُورٍ", translation: "Ve karanlık gölgeler gibi dalgalar onları sardığı zaman, dini O'na has kılarak Allah'a yalvarırlar. Ama onları karaya çıkarıp kurtardığı zaman, onlardan bir kısmı orta bir yol tutar. Ve Biz'im ayetlerimize ancak, tam hain ve tam nankör olanlardan başkası bile bile ilgisiz kalmaz.", transliteration: "Ve iza gaşiyehum mevcun kez zuleli deavullahe muhlisine lehud din, fe lemma neccahum ilel berri fe minhum muktesıd, ve ma yechadu bi ayatina illa kullu hattarin kefur."},
  {id: 3502, sure_no: 33, ayet_no: 33, sure_adi: "Lokman", text: "يَٓا اَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ وَاخْشَوْا يَوْماً لَا يَجْز۪ي وَالِدٌ عَنْ وَلَدِه۪ۘ وَلَا مَوْلُودٌ هُوَ جَازٍ عَنْ وَالِدِه۪ شَيْـٔاًۜ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَا۠ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُورُ", translation: "Ey insanlar! Rabb'inize takvalı olun. Ve babanın çocuğuna hiçbir yarar sağlayamadığı, çocuğun da babasına hiçbir şey ile yarar sağlayamadığı günden sakının. Allah'ın sözü gerçektir. Öyleyse, dünya hayatı sizi aldatmasın! Sakın aldatıcı sizi Allah ile aldatmasın.", transliteration: "Ya eyyuhen nasutteku rabbekum vahşev yevmen la yeczi validun an veledihi ve la mevludun huve cazin an validihi şey'a inne va'dallahi hakkun fe la tegurrennekumul hayatud dunya, ve la yagurrennekum billahil garur."},
  {id: 3503, sure_no: 34, ayet_no: 34, sure_adi: "Lokman", text: "اِنَّ اللّٰهَ عِنْدَهُ عِلْمُ السَّاعَةِۚ وَيُنَزِّلُ الْغَيْثَۚ وَيَعْلَمُ مَا فِي الْاَرْحَامِۜ وَمَا تَدْر۪ي نَفْسٌ مَاذَا تَكْسِبُ غَداًۜ وَمَا تَدْر۪ي نَفْسٌ بِاَيِّ اَرْضٍ تَمُو تُ  ۜ  اِنَّ اللّٰهَ عَل۪يمٌ خَب۪يرٌ", translation: "Sa'at'in bilgisi, Allah'ın yanındadır. Ve O, yağmuru yağdırır, rahimde olanı bilir. Kimse yarın ne kazanacağını bilemez. Kimse nerede öleceğini de bilemez. Kuşkusuz Allah, Her Şeyi Bilen'dir, Her Şeyden Haberdar'dır.", transliteration: "İnnallahe indehu ilmus saah, ve yunezzilul gays, ve ya'lemu ma fil erham, ve ma tedri nefsun maza teksibu gada, ve ma tedri nefsun bi eyyi ardın temut, innallahe alimun habir."},
  {id: 3504, sure_no: 1, ayet_no: 1, sure_adi: "Secde", text: "الٓـمٓ۠", translation: "Elif. Lam. Mim.", transliteration: "Elif lam mim."},
  {id: 3505, sure_no: 2, ayet_no: 2, sure_adi: "Secde", text: "تَنْز۪يلُ الْكِتَابِ لَا رَيْبَ ف۪يهِ مِنْ رَبِّ الْعَالَم۪ينَۜ", translation: "Mutlak doğru olan bu Kitap'ın indirilişi, alemlerin Rabb'indendir.", transliteration: "Tenzilul kitabi la reybe fihi min rabbil alemin."},
  {id: 3506, sure_no: 3, ayet_no: 3, sure_adi: "Secde", text: "اَمْ يَقُولُونَ افْتَرٰيهُۚ بَلْ هُوَ الْحَقُّ مِنْ رَبِّكَ لِتُنْذِرَ قَوْماً مَٓا اَتٰيهُمْ مِنْ نَذ۪يرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ", translation: "Yoksa \"Onu kendisi uydurdu\" mu diyorlar? Bilakis! O, Rabb'inden bir gerçektir. Senden önce kendilerine bir uyarıcı gelmemiş olan halkı uyarman içindir. Umulur ki böylece onlar, doğru yolu seçerler.", transliteration: "Em yekulunefterah, bel huvel hakku min rabbike li tunzire kavmen ma etahum min nezirin min kablike leallehum yehtedun."},
  {id: 3507, sure_no: 4, ayet_no: 4, sure_adi: "Secde", text: "اَللّٰهُ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۜ مَا لَكُمْ مِنْ دُونِه۪ مِنْ وَلِيٍّ وَلَا شَف۪يعٍۜ اَفَلَا تَتَذَكَّرُونَ", translation: "O Allah ki; gökleri, yeri ve ikisinin arasındakileri altı günde yarattı. Sonra arşa isteva etti. Sizin O'ndan başka veliniz ve şefaatçiniz yoktur. Hala öğüt almaz mısınız?", transliteration: "Allahullezi halakas semavati vel arda ve ma beynehuma fi sitteti eyyamin summesteva alel arş, ma lekum min dunihi min veliyyin ve la şefii, e fe la tetezekkerun."},
  {id: 3508, sure_no: 5, ayet_no: 5, sure_adi: "Secde", text: "يُدَبِّرُ الْاَمْرَ مِنَ السَّمَٓاءِ اِلَى الْاَرْضِ ثُمَّ يَعْرُجُ اِلَيْهِ ف۪ي يَوْمٍ كَانَ مِقْدَارُهُٓ اَلْفَ سَنَةٍ مِمَّا تَعُدُّونَ", translation: "Gökten yeryüzüne kadar işleri düzenler. Sonra miktarı sizin hesabınıza göre bin yıl süren bir gün içinde işler O'na yükselir.", transliteration: "Yudebbirul emre mines semai ilel ardı summe ya'rucu ileyhi fi yevmin kane mıkdaruhu elfe senetin mimma teuddun."},
  {id: 3509, sure_no: 6, ayet_no: 6, sure_adi: "Secde", text: "ذٰلِكَ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَز۪يزُ الرَّح۪يمُۙ", translation: "İşte O, görünmeyeni ve görüneni bilen Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "Zalike alimul gaybi veş şehadetil azizur rahim."},
  {id: 3510, sure_no: 7, ayet_no: 7, sure_adi: "Secde", text: "اَلَّـذ۪ٓي اَحْسَنَ كُلَّ شَيْءٍ خَلَقَهُ وَبَدَاَ خَلْقَ الْاِنْسَانِ مِنْ ط۪ينٍۚ", translation: "O ki yarattığı her şeyi en iyi şekilde yaratmıştır. İnsanı yaratmaya çamurdan başladı.", transliteration: "Ellezi ahsene kulle şey'in halakahu ve bedee halkal insani min tin."},
  {id: 3511, sure_no: 8, ayet_no: 8, sure_adi: "Secde", text: "ثُمَّ جَعَلَ نَسْلَهُ مِنْ سُلَالَةٍ مِنْ مَٓاءٍ مَه۪ينٍۚ", translation: "Sonra onun soyunu bir özden, basit bir sudan, devam ettirdi.", transliteration: "Summe ceale neslehu min sulaletin min main mehin."},
  {id: 3512, sure_no: 9, ayet_no: 9, sure_adi: "Secde", text: "ثُمَّ سَوّٰيهُ وَنَفَخَ ف۪يهِ مِنْ رُوحِه۪ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٔدَةَۜ قَل۪يلاً مَا تَشْكُرُونَ", translation: "Sonra onu düzenledi ve ona Ruhundan üfledi. Size duyma yetisi, görme yetisi ve fuad verdi. Ne kadar az şükrediyorsunuz!", transliteration: "Summe sevvahu ve nefeha fihi min ruhihi ve ceale lekumus sem'a vel ebsare vel ef'ideh, kalilen ma teşkurun."},
  {id: 3513, sure_no: 10, ayet_no: 10, sure_adi: "Secde", text: "وَقَالُٓوا ءَاِذَا ضَلَلْنَا فِي الْاَرْضِ ءَاِنَّا لَف۪ي خَلْقٍ جَد۪يدٍۜ بَلْ هُمْ بِلِقَٓاءِ رَبِّهِمْ كَافِرُونَ", translation: "Onlar: \"Toprakta yok olup kaybolduktan sonra yeniden mi yaratılacağız?\" dediler. Hayır; onlar, Rabb'lerine kavuşmayı yalanlıyorlar.", transliteration: "Ve kalu e iza dalelna fil ardı e inna le fi halkın cedid, bel hum bi likai rabbihim kafirun."},
  {id: 3514, sure_no: 11, ayet_no: 11, sure_adi: "Secde", text: "قُلْ يَتَوَفّٰيكُمْ مَلَكُ الْمَوْتِ الَّذ۪ي وُكِّلَ بِكُمْ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُونَ۟", translation: "De ki: \"Size vekil kılınan ölüm meleği, sizi vefat ettirecek. Sonra Rabb'inize döndürüleceksiniz.\"", transliteration: "Kul yeteveffakum melekul mevtillezi vukkile bikum summe ila rabbikum turceun."},
  {id: 3515, sure_no: 12, ayet_no: 12, sure_adi: "Secde", text: "وَلَوْ تَرٰٓى اِذِ الْمُجْرِمُونَ نَاكِسُوا رُؤُ۫سِهِمْ عِنْدَ رَبِّهِمْۜ رَبَّـنَٓا اَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَـالِحاً اِنَّا مُوقِنُونَ", translation: "Mücrimleri, Rabb'lerinin huzurunda başları öne eğilmiş olarak: \"Rabb'imiz! Gördük ve dinledik, şimdi bizi dünyaya geri gönder de salihatı yapalım. Biz, artık kesin bir şekilde iman ettik.\" derlerken bir görsen!", transliteration: "Ve lev tera izil mucrimune nakısu ruusihim inde rabbihim, rabbena ebsarna ve semi'na ferci'na na'mel salihan inna mukinun."},
  {id: 3516, sure_no: 13, ayet_no: 13, sure_adi: "Secde", text: "وَلَوْ شِئْنَا لَاٰتَيْنَا كُلَّ نَفْسٍ هُدٰيهَا وَلٰكِنْ حَقَّ الْقَوْلُ مِنّ۪ي لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَع۪ينَ", translation: "Eğer dileseydik, herkese elbette hidayetini verirdik. Fakat Ben'den söz hak oldu: \"Cehennem'i tamamen cinn ve insanlardan dolduracağım.\"", transliteration: "Ve lev şi'na le ateyna kulle nefsin hudaha ve lakin hakkal kavlu minni le emleenne cehenneme minel cinneti ven nasi ecmain."},
  {id: 3517, sure_no: 14, ayet_no: 14, sure_adi: "Secde", text: "فَذُوقُوا بِمَا نَس۪يتُمْ لِقَٓاءَ يَوْمِكُمْ هٰذَاۚ اِنَّا نَس۪ينَاكُمْ وَذُوقُوا عَذَابَ الْخُلْدِ بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Öyleyse bu gününüzle karşılaşmayı unuttuğunuz için tadın azabı. Kuşkusuz Biz de sizi unuttuk. Yapmış olduklarınıza karşılık, sürekli olan azabı tadın.", transliteration: "Fe zuku bi ma nesitum likae yevmikum haza, inna nesinakum ve zuku azabel huldi bi ma kuntum ta'melun."},
  {id: 3518, sure_no: 15, ayet_no: 15, sure_adi: "Secde", text: "اِنَّمَا يُؤْمِنُ بِاٰيَاتِنَا الَّذ۪ينَ اِذَا ذُكِّرُوا بِهَا خَرُّوا سُجَّداً وَسَبَّحُوا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُونَ", translation: "Bizim ayetlerimize iman edenler; kendilerine öğüt verildiği zaman saygı gösterirler ve Rabb'lerini övgü ile yüceltirler. Onlar asla büyüklük taslamazlar.", transliteration: "İnnema yu'minu bi ayatinellezine iza zukkiru biha harru succeden ve sebbehu bi hamdi rabbihim ve hum la yestekbirun."},
  {id: 3519, sure_no: 16, ayet_no: 16, sure_adi: "Secde", text: "تَتَجَافٰى جُنُوبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفاً وَطَمَعاًۘ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ", translation: "Onlar, yataklarından kalkarak korku ve ümit içinde Rabb'lerine dua ederler. Kendilerine verdiğimiz rızıklardan infak ederler.", transliteration: "Tetecafa cunubuhum anil medacıi yed'une rabbehum havfen ve tamaan ve mimma razaknahum yunfikun."},
  {id: 3520, sure_no: 17, ayet_no: 17, sure_adi: "Secde", text: "فَلَا تَعْلَمُ نَفْسٌ مَٓا اُخْفِيَ لَهُمْ مِنْ قُرَّةِ اَعْيُنٍۚ جَزَٓاءً بِمَا كَانُوا يَعْمَلُونَ", translation: "Hiç kimse, yaptıkları iyi şeylerin karşılığı olarak, kendisini ne tür bir mutluluğun beklediğini bilmez.", transliteration: "Fe la ta'lemu nefsun ma uhfiye lehum min kurreti a'yun, cezaen bi ma kanu ya'melun."},
  {id: 3521, sure_no: 18, ayet_no: 18, sure_adi: "Secde", text: "اَفَمَنْ كَانَ مُؤْمِناً كَمَنْ كَانَ فَاسِقاًۜ لَا يَسْتَوُ۫نَ", translation: "Öyleyse, Mü'min kimse \"fasık\" olan kimse gibi midir? Elbette bunlar bir olmazlar.", transliteration: "E fe men kane mu'minen kemen kane fasika, la yestevun."},
  {id: 3522, sure_no: 19, ayet_no: 19, sure_adi: "Secde", text: "اَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ جَنَّاتُ الْمَأْوٰىۘ نُزُلاً بِمَا كَانُوا يَعْمَلُونَ", translation: "İman eden ve salihatı yapanlar, işte onlar için, yapmış olduklarından dolayı konaklama yeri olarak Me'va Cennetleri var.", transliteration: "Emmellezine amenu ve amilus salihati fe lehum cennatul me'va nuzulen bi ma kanu ya'melun."},
  {id: 3523, sure_no: 20, ayet_no: 20, sure_adi: "Secde", text: "وَاَمَّا الَّذ۪ينَ فَسَقُوا فَمَأْوٰيهُمُ النَّارُۜ  كُلَّمَٓا اَرَادُٓوا اَنْ يَخْرُجُوا مِنْهَٓا اُع۪يدُوا ف۪يهَا وَق۪يلَ لَهُمْ ذُوقُوا عَذَابَ النَّارِ الَّذ۪ي كُنْتُمْ بِه۪ تُكَذِّبُونَ", translation: "Fasıklara gelince, onların barınağı ateştir. Her çıkmak istediklerinde, oraya yeniden iade edilirler. Ve onlara: \"Yalanladığınız ateşin azabını tadın!\" denir.", transliteration: "Ve emmellezine feseku fe me'vahumun nar, kulle ma eradu en yahrucu minha uidu fiha, ve kile lehum zuku azaben narillezi kuntum bihi tukezzibun."},
  {id: 3524, sure_no: 21, ayet_no: 21, sure_adi: "Secde", text: "وَلَنُذ۪يقَنَّهُمْ مِنَ الْعَذَابِ الْاَدْنٰى دُونَ الْعَذَابِ الْاَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ", translation: "Onlara, büyük azaptan önce, daha yakın olan azaptan tattıracağız. Belki dönerler.", transliteration: "Ve le nuzikannehum minel azabil edna dunel azabil ekberi leallehum yerciun."},
  {id: 3525, sure_no: 22, ayet_no: 22, sure_adi: "Secde", text: "وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيَاتِ رَبِّه۪ ثُمَّ اَعْرَضَ عَنْهَاۜ اِنَّا مِنَ الْمُجْرِم۪ينَ مُنْتَقِمُونَ۟", translation: "Rabb'inin ayetleri ile öğütlendiği halde, ondan yüz çeviren kimseden daha zalim kim vardır? Kuşkusuz Biz, mücrimlere hak ettikleri cezayı vereceğiz.", transliteration: "Ve men azlemu mimmen zukkire bi ayati rabbihi summe a'rada anha, inna minel mucrimine muntekimun."},
  {id: 3526, sure_no: 23, ayet_no: 23, sure_adi: "Secde", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ فَلَا تَكُنْ ف۪ي مِرْيَةٍ مِنْ لِقَٓائِه۪ وَجَعَلْنَاهُ هُدًى لِبَن۪ٓي اِسْرَٓائ۪لَۚ", translation: "Ant olsun ki Musa'ya Kitap verdik. Sakın ona kavuşmaktan kuşku içinde olma. Onu İsrailoğulları için yol gösterici yaptık.", transliteration: "Ve lekad ateyna musel kitabe fe la tekun fi miryetin min likaihi ve cealnahu huden li beni israil."},
  {id: 3527, sure_no: 24, ayet_no: 24, sure_adi: "Secde", text: "وَجَعَلْنَا مِنْهُمْ اَئِمَّةً يَهْدُونَ بِاَمْرِنَا لَمَّا صَبَرُواۜ وَكَانُوا بِاٰيَاتِنَا يُوقِنُونَ", translation: "Sabrettikleri ve ayetlerimize tam bir bağlılık gösterdikleri için, onlardan buyruğumuzla doğru yola ileten önderler çıkardık.", transliteration: "Ve cealna minhum eimmeten yehdune bi emrina lemma saberu ve kanu bi ayatina yukınun."},
  {id: 3528, sure_no: 25, ayet_no: 25, sure_adi: "Secde", text: "اِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ", translation: "Senin Rabb'in; kıyamet günü, anlaşmazlığa düştükleri şeylerde onların arasını ayırır.", transliteration: "İnne rabbeke huve yafsilu beynehum yevmel kıyameti fima kanu fihi yahtelifun."},
  {id: 3529, sure_no: 26, ayet_no: 26, sure_adi: "Secde", text: "اَوَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِنَ الْقُرُونِ يَمْشُونَ ف۪ي مَسَاكِنِهِمْۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍۜ اَفَلَا يَسْمَعُونَ", translation: "Yaşadıkları yerlerde, daha önce gezip dolaşmış olan nice nesilleri yok etmiş olmamız, onları doğru yola iletmeye yetmedi mi? Kuşkusuz bunda nice ayetler vardır. Hala söz dinlemeyecekler mi?", transliteration: "E ve lem yehdi lehum kem ehlekna min kablihim minel kuruni yemşune fi mesakinihim, inne fi zalike le ayat, e fe la yesmeun."},
  {id: 3530, sure_no: 27, ayet_no: 27, sure_adi: "Secde", text: "اَوَلَمْ يَرَوْا اَنَّا نَسُوقُ الْمَٓاءَ اِلَى الْاَرْضِ الْجُرُزِ فَنُخْرِجُ بِه۪ زَرْعاً تَأْكُلُ مِنْهُ اَنْعَامُهُمْ وَاَنْفُسُهُمْۜ اَفَلَا يُبْصِرُونَ", translation: "Çorak araziye suyu sevk edip, onunla hayvanlarının ve kendilerinin yedikleri bitkileri çıkardığımızı görmüyorlar mı? Bundan ibret almaları gerekmez mi?", transliteration: "E ve lem yerev enna nesukul mae ilel ardıl curuzi fe nuhricu bihi zar'an te'kulu minhu en'amuhum ve enfusuhum e fe la yubsirun."},
  {id: 3531, sure_no: 28, ayet_no: 28, sure_adi: "Secde", text: "وَيَقُولُونَ مَتٰى هٰذَا الْفَتْحُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Eğer doğru kimselerseniz, bu fetih ne zaman?\" diyorlar.", transliteration: "Ve yekulune meta hazel fethu in kuntum sadikin."},
  {id: 3532, sure_no: 29, ayet_no: 29, sure_adi: "Secde", text: "قُلْ يَوْمَ الْفَتْحِ لَا يَنْفَعُ الَّذ۪ينَ كَفَرُٓوا ا۪يمَانُهُمْ وَلَا هُمْ يُنْظَرُونَ", translation: "De ki: \"Kafirlerin fetih günü inanmaları kendilerine bir yarar sağlamaz. Ve onlara süre de tanınmaz.\"", transliteration: "Kul yevmel fethi la yenfeullezine keferu imanuhum ve la hum yunzarun."},
  {id: 3533, sure_no: 30, ayet_no: 30, sure_adi: "Secde", text: "فَاَعْرِضْ عَنْهُمْ وَانْتَظِرْ اِنَّهُمْ مُنْتَظِرُونَ", translation: "Artık onları kendi hallerine bırak ve olacakları bekle. Doğrusu onlar da bekleyenlerdir.", transliteration: "Fe a'rıd anhum ventezır innehum muntezırun."},
  {id: 3534, sure_no: 1, ayet_no: 1, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا النَّبِيُّ اتَّقِ اللّٰهَ وَلَا تُطِعِ الْكَافِر۪ينَ وَالْمُنَافِق۪ينَۜ اِنَّ اللّٰهَ كَانَ عَل۪يماً حَك۪يماًۙ", translation: "Ey Nebi! Allah'a takvalı ol. Kafirlere ve Münafıklara uyma. Kuşkusuz Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ya eyyuhen nebiyyuttekillahe ve la tutıil kafirine vel munafikin, innallahe kane alimen hakima."},
  {id: 3535, sure_no: 2, ayet_no: 2, sure_adi: "Ahzab", text: "وَاتَّبِعْ مَا يُوحٰٓى اِلَيْكَ مِنْ رَبِّكَۜ اِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُونَ خَب۪يراًۙ", translation: "Rabb'inden sana vahyedilen neyse yalnızca ona uy. Kuşkusuz Allah, yaptığınız şeylerden haberdardır.", transliteration: "Vettebi' ma yuha ileyke min rabbik, innallahe kane bima ta'melune habira."},
  {id: 3536, sure_no: 3, ayet_no: 3, sure_adi: "Ahzab", text: "وَتَوَكَّلْ عَلَى اللّٰهِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً", translation: "Allah'a tevekkül et. Vekil olarak Allah yeter.", transliteration: "Ve tevekkel alallah ve kefa billahi vekila."},
  {id: 3537, sure_no: 4, ayet_no: 4, sure_adi: "Ahzab", text: "مَا جَعَلَ اللّٰهُ لِرَجُلٍ مِنْ قَلْبَيْنِ ف۪ي جَوْفِه۪ۚ وَمَا جَعَلَ اَزْوَاجَكُمُ الّٰٓئ۪ تُظَاهِرُونَ مِنْهُنَّ اُمَّهَاتِكُمْۚ وَمَا جَعَلَ اَدْعِيَٓاءَكُمْ اَبْنَٓاءَكُمْۜ ذٰلِكُمْ قَوْلُكُمْ بِاَفْوَاهِكُمْۜ وَاللّٰهُ يَقُولُ الْحَقَّ وَهُوَ يَهْدِي السَّب۪يلَ", translation: "Allah, hiç kimsenin bedenine iki kalp yerleştirmedi. Zihar yaptığınız eşlerinizi, size anne yapmadı. Ve himayeniz altına aldıklarınızı öz evladınız kılmadı. Bunlar sizin söylediğiniz boş sözlerdir. Allah gerçeği söyler. Ve doğru yola O iletir.", transliteration: "Ma cealallahu li raculin min kalbeyni fi cevfih, ve ma ceale ezvacekumullai tuzahırune min hunne ummehatikum, ve ma ceale ed'ıyaekum ebnaekum, zalikum kavlukum bi efvahikum, vallahu yekulul hakka ve huve yehdis sebil."},
  {id: 3538, sure_no: 5, ayet_no: 5, sure_adi: "Ahzab", text: "اُدْعُوهُمْ لِاٰبَٓائِهِمْ هُوَ اَقْسَطُ عِنْدَ اللّٰهِۚ فَاِنْ لَمْ تَعْلَمُٓوا اٰبَٓاءَهُمْ فَاِخْوَانُكُمْ فِي الدّ۪ينِ وَمَوَال۪يكُمْۜ وَلَيْسَ عَلَيْكُمْ جُنَاحٌ ف۪يمَٓا اَخْطَأْتُمْ بِه۪ۙ وَلٰكِنْ مَا تَعَمَّدَتْ قُلُوبُكُمْۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً", translation: "Onları babalarına nispetle çağırın. Bu Allah'ın yanında daha hakkaniyetlidir. Eğer babalarını bilmiyorsanız, artık onlar dinde kardeşleriniz ve sorumluluklarını üstlendiğiniz kimselerdir. Bilinçli olarak yaptığınız şeyler dışında, yanlışlıkla yaptıklarınızda sizin için bir günah yoktur. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ud'uhum li abaihim huve aksatu indallah, fe in lem ta'lemu abaehum fe ıhvanukum fid dini ve mevalikum, ve leyse aleykum cunahun fima ahta'tum bihi ve lakin ma taammedet kulubukum, ve kanallahu gafuren rahima."},
  {id: 3539, sure_no: 6, ayet_no: 6, sure_adi: "Ahzab", text: "اَلنَّبِيُّ اَوْلٰى بِالْمُؤْمِن۪ينَ مِنْ اَنْفُسِهِمْ وَاَزْوَاجُهُٓ اُمَّهَاتُهُمْۜ وَاُو۬لُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ ف۪ي كِتَابِ اللّٰهِ مِنَ الْمُؤْمِن۪ينَ وَالْمُهَاجِر۪ينَ اِلَّٓا اَنْ تَفْعَلُٓوا اِلٰٓى اَوْلِيَٓائِكُمْ مَعْرُوفاًۜ كَانَ ذٰلِكَ فِي الْكِتَابِ مَسْطُوراً", translation: "Nebi, İman Edenler için kendi canlarından daha yakındır. O'nun eşleri onların anneleridir. Aralarında aile bağı olanlar; Allah'ın Kitap'ına göre birbirlerine, diğer Mü'minler'den ve muhacirlerden daha yakındırlar. Ancak velilerinize yapacağınız iyilik hariç. İşte bunlar Kitap'ta kayıtlıdır.", transliteration: "En nebiyyu evla bil mu'minine min enfusihim ve ezvacuhu ummehatuhum, ve ulul erhami ba'duhum evla bi ba'dın fi kitabillahi minel mu'minine vel muhacirine illa en tef'alu ila evliyaikum ma'rufa, kane zalike fil kitabi mestura."},
  {id: 3540, sure_no: 7, ayet_no: 7, sure_adi: "Ahzab", text: "وَاِذْ اَخَذْنَا مِنَ النَّبِيّ۪نَ م۪يثَاقَهُمْ وَمِنْكَ وَمِنْ نُوحٍ وَاِبْرٰه۪يمَ وَمُوسٰى وَع۪يسَى ابْنِ مَرْيَمَۖ وَاَخَذْنَا مِنْهُمْ م۪يثَاقاً غَل۪يظاًۙ", translation: "Hani Biz, Nebilerden kesin söz almıştık. Ve senden, Nuh'tan, İbrahim'den, Musa'dan ve Meryem Oğlu İsa'dan, hepsinden sağlam söz aldık.", transliteration: "Ve iz ehazna minen nebiyyine misakahum ve minke ve min nuhın ve ibrahime ve musa ve isebni meryeme ve ehazna minhum misakan galiza."},
  {id: 3541, sure_no: 8, ayet_no: 8, sure_adi: "Ahzab", text: "لِيَسْـَٔلَ الصَّادِق۪ينَ عَنْ صِدْقِهِمْۚ وَاَعَدَّ لِلْكَافِر۪ينَ عَذَاباً اَل۪يماً۟", translation: "Ki, doğru kimselere, doğruluklarını sorsun. Ve Kafirlere de acı verecek bir azap hazırladı.", transliteration: "Li yes'eles sadikine an sıdkıhim, ve eadde lil kafirine azaben elima."},
  {id: 3542, sure_no: 9, ayet_no: 9, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَٓاءَتْكُمْ جُنُودٌ فَاَرْسَلْنَا عَلَيْهِمْ ر۪يحاً وَجُنُوداً لَمْ تَرَوْهَاۜ وَكَانَ اللّٰهُ بِمَا تَعْمَلُونَ بَص۪يراًۚ", translation: "Ey İman Edenler! Allah'ın size olan nimetlerini hatırlayın! Hani, üzerinize ordular gelmişti de Biz, onların üzerine rüzgar ve göremediğiniz ordular göndermiştik. Allah, yaptıklarınızı görendir.", transliteration: "Ya eyyuhellezine amenuzkuru ni'metallahi aleykum iz caetkum cunudun fe erselna aleyhim rihan ve cunuden lem terevha, ve kanallahu bima ta'melune basira."},
  {id: 3543, sure_no: 10, ayet_no: 10, sure_adi: "Ahzab", text: "اِذْ جَٓاؤُ۫كُمْ مِنْ فَوْقِكُمْ وَمِنْ اَسْفَلَ مِنْكُمْ وَاِذْ زَاغَتِ الْاَبْصَارُ وَبَلَغَتِ الْقُلُوبُ الْحَنَاجِرَ وَتَظُنُّونَ بِاللّٰهِ الظُّنُونَا", translation: "Hani onlar, alttan ve üstten, her yönden size saldırmışlardı. Gözler kaymış, yürekler ağızlara gelmişti. Ve siz, Allah hakkında zan üzerine zanda bulunuyordunuz.", transliteration: "İz caukum min fevkıkum ve min esfele minkum ve iz zagatil ebsaru ve belegatil kulubul hanacire ve tezunnune billahiz zununa."},
  {id: 3544, sure_no: 11, ayet_no: 11, sure_adi: "Ahzab", text: "هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُونَ وَزُلْزِلُوا زِلْزَالاً شَد۪يداً", translation: "Orada, İman Edenler sınava tabi oldular. Şiddetli bir sarsıntı ile sarsıldılar.", transliteration: "Hunalikebtuliyel mu'minune ve zulzilu zilzalen şedida."},
  {id: 3545, sure_no: 12, ayet_no: 12, sure_adi: "Ahzab", text: "وَاِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ مَا وَعَدَنَا اللّٰهُ وَرَسُولُـهُٓ اِلَّا غُرُوراً", translation: "O zaman Münafıklar ve kalplerinde hastalık olanlar, \"Allah ve Resulü bize boş vaatte bulunmuş.\" diyorlardı.", transliteration: "Ve iz yekulul munafikune vellezine fi kulubihim maradun ma vaadenallahu ve resuluhu illa gurura."},
  {id: 3546, sure_no: 13, ayet_no: 13, sure_adi: "Ahzab", text: "وَاِذْ قَالَتْ طَٓائِفَةٌ مِنْهُمْ يَٓا اَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُواۚ وَيَسْتَأْذِنُ فَر۪يقٌ مِنْهُمُ النَّبِيَّ يَقُولُونَ اِنَّ بُيُوتَنَا عَوْرَةٌ وَمَا هِيَ بِعَوْرَةٍۜ اِنْ يُر۪يدُونَ اِلَّا فِرَاراً", translation: "Onlardan bir topluluk: \"Ey Yesrib halkı, sizin için burada duracak bir yer yok, hemen dönün! Yine onlardan bir grup da: \"Evlerimiz gerçekten korumasızdır.\" diyerek Nebi'den izin istiyorlardı. Oysaki evleri korumasız değildi. Onlar, savaştan kaçmak için bahane arıyorlardı.", transliteration: "Ve iz kalet taifetun minhum ya ehle yesribe la mukame lekum ferciu, ve yeste'zinu ferikun minhumun nebiyye yekulune inne buyutena avretun ve ma hiye bi avreh, in yuridune illa firara."},
  {id: 3547, sure_no: 14, ayet_no: 14, sure_adi: "Ahzab", text: "وَلَوْ دُخِلَتْ عَلَيْهِمْ مِنْ اَقْطَارِهَا ثُمَّ سُئِلُوا الْفِتْنَةَ لَاٰتَوْهَا وَمَا تَلَبَّثُوا بِهَٓا اِلَّا يَس۪يراً", translation: "Fakat onların üzerlerine, her bir taraftan girilip kuşatılsalardı ve sonra da fitne çıkarmaları istenseydi, tereddüt etmeksizin bunu hemen yerine getirirlerdi.", transliteration: "Ve lev duhılet aleyhim min aktariha summe suilul fitnete le atevha ve ma telebbesu biha illa yesira."},
  {id: 3548, sure_no: 15, ayet_no: 15, sure_adi: "Ahzab", text: "وَلَقَدْ كَانُوا عَاهَدُوا اللّٰهَ مِنْ قَبْلُ لَا يُوَلُّونَ الْاَدْبَارَۜ وَكَانَ عَهْدُ اللّٰهِ مَسْؤُ۫لاً", translation: "Oysaki onlar, daha önce arkalarını dönüp kaçmayacaklarına dair Allah'a söz vermişlerdi. Allah'a verilen söz sorumluluktur.", transliteration: "Ve lekad kanu ahedullahe min kablu la yuvellunel edbar, ve kane ahdullahi mes'ula."},
  {id: 3549, sure_no: 16, ayet_no: 16, sure_adi: "Ahzab", text: "قُلْ لَنْ يَنْفَعَكُمُ الْفِرَارُ اِنْ فَرَرْتُمْ مِنَ الْمَوْتِ اَوِ الْقَتْلِ وَاِذاً لَا تُمَتَّعُونَ اِلَّا قَل۪يلاً", translation: "De ki: \"Eğer ölümden veya öldürülmekten kaçıyorsanız, kaçmak asla size yarar sağlamaz. Öyle olsa bile çok az faydalanırsınız.\"", transliteration: "Kul len yenfeakumul firaru in ferertum minel mevti evil katli ve izen la tumetteune illa kalila."},
  {id: 3550, sure_no: 17, ayet_no: 17, sure_adi: "Ahzab", text: "قُلْ مَنْ ذَا الَّذ۪ي يَعْصِمُكُمْ مِنَ اللّٰهِ اِنْ اَرَادَ بِكُمْ سُٓوءاً اَوْ اَرَادَ بِكُمْ رَحْمَةًۜ وَلَا يَجِدُونَ لَهُمْ مِنْ دُونِ اللّٰهِ وَلِياًّ وَلَا نَص۪يراً", translation: "De ki: \"Eğer Allah başınıza bir kötülük getirmeyi dilese sizi kim koruyabilir? Veya size bir rahmet dilese…\" Onlar, kendileri için Allah'tan başka bir veli de bir yardımcı da bulamazlar.", transliteration: "Kul men zellezi ya'sımukum minallahi in erade bikum suen ev eradebikum rahmeh, ve la yecidune lehum min dunillahi veliyyen ve la nasira."},
  {id: 3551, sure_no: 18, ayet_no: 18, sure_adi: "Ahzab", text: "قَدْ يَعْلَمُ اللّٰهُ الْمُعَوِّق۪ينَ مِنْكُمْ وَالْقَٓائِل۪ينَ لِاِخْوَانِهِمْ هَلُمَّ اِلَيْنَاۚ وَلَا يَأْتُونَ الْبَأْسَ اِلَّا قَل۪يلاًۙ", translation: "Allah, sizden engel olmaya çalışanları ve kardeşlerine: \"Bize gelin.\" diyenleri kesinlikle biliyor. Onların pek azı hariç savaşa gelmezler.", transliteration: "Kad ya'lemullahul muavvikine minkum vel kailine li ıhvanihim helumme ileyna, ve la ye'tunel be'se illa kalila."},
  {id: 3552, sure_no: 19, ayet_no: 19, sure_adi: "Ahzab", text: "اَشِحَّةً عَلَيْكُمْۚ فَاِذَا جَٓاءَ الْخَوْفُ رَاَيْتَهُمْ يَنْظُرُونَ اِلَيْكَ تَدُورُ اَعْيُنُهُمْ كَالَّذ۪ي يُغْشٰى عَلَيْهِ مِنَ الْمَوْتِۚ فَاِذَا ذَهَبَ الْخَوْفُ سَلَقُوكُمْ بِاَلْسِنَةٍ حِدَادٍ اَشِحَّةً عَلَى الْخَيْرِۜ اُو۬لٰٓئِكَ لَمْ يُؤْمِنُوا فَاَحْبَطَ اللّٰهُ اَعْمَالَهُمْۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يراً", translation: "Size karşı çok isteksizdirler. Fakat korku gelince, ölümden dolayı baygınlık çökmüş kimse gibi gözleri dönmüş olarak sana baktıklarını görürsün. Sonra korkuyu savınca, hayra karşı kıskançlıkla sivri dilleriyle sizi incitirler. İşte onlar Mü'min değiller. Allah yaptıklarını boşa çıkardı. Bu Allah'a kolaydır.", transliteration: "Eşıhhaten aleykum fe iza cael havfu reeytehum yenzurune ileyke teduru a'yunuhum kellezi yugşa aleyhi minel mevt, fe iza zehebel havfu selekukum bi elsinetin hıdadin eşıhhaten alel hayr, ulaike lem yu'minu fe ahbetallahu a'malehum, ve kane zalike alallahi yesira."},
  {id: 3553, sure_no: 20, ayet_no: 20, sure_adi: "Ahzab", text: "يَحْسَبُونَ الْاَحْزَابَ لَمْ يَذْهَبُواۚ وَاِنْ يَأْتِ الْاَحْزَابُ يَوَدُّوا لَوْ اَنَّهُمْ بَادُونَ فِي الْاَعْرَابِ يَسْـَٔلُونَ عَنْ اَنْبَٓائِكُمْۜ وَلَوْ كَانُوا ف۪يكُمْ مَا قَاتَلُٓوا اِلَّا قَل۪يلاً۟", translation: "Onlar, düşman birliklerinin gitmediğini sanıyorlardı. Eğer birlikler gelseler, çölde yaşayan Arapların arasına karışıp, ne halde bulunduğunuzu sormak isterler. Eğer sizin aranızda olsalardı, pek azı hariç, savaşmazlardı.", transliteration: "Yahsebunel ahzabe lem yezhebu, ve in ye'til ahzabu yeveddu lev ennehum badune fil a'rabi yes'elune an enbaikum, ve lev kanu fikum ma katelu illa kalila."},
  {id: 3554, sure_no: 21, ayet_no: 21, sure_adi: "Ahzab", text: "لَقَدْ كَانَ لَكُمْ ف۪ي رَسُولِ اللّٰهِ اُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُوا اللّٰهَ وَالْيَوْمَ الْاٰخِرَ وَذَكَرَ اللّٰهَ كَث۪يراًۜ", translation: "Ant olsun ki, sizden Allah'a ve ahiret gününe kavuşmayı uman ve Allah'ı çokça zikredenler için, Allah'ın Resul'ünde iyi bir örnek vardır.", transliteration: "Lekad kane lekum fi resulillahi usvetun hasenetun limen kane yercullahe vel yevmel ahıre ve zekerallahe kesira."},
  {id: 3555, sure_no: 22, ayet_no: 22, sure_adi: "Ahzab", text: "وَلَمَّا رَاَ الْمُؤْمِنُونَ الْاَحْزَابَۙ قَالُوا هٰذَا مَا وَعَدَنَا اللّٰهُ وَرَسُولُهُ وَصَدَقَ اللّٰهُ وَرَسُولُهُۘ وَمَا زَادَهُمْ اِلَّٓا ا۪يمَاناً وَتَسْل۪يماًۜ", translation: "Mü'minler, düşman birliklerini gördükleri zaman: \"İşte bu, Allah'ın ve Resul'ünün bize söz verdiği şeydir. Allah ve O'nun Resul'ü doğru söyledi.\" Bu, onların yalnızca iman ve teslimiyetlerini arttırdı.", transliteration: "Ve lemma real mu'minunel ahzabe kalu haza ma vaadenallahu ve resuluhu ve sadakallahu ve resuluhu ve ma zadehum illa imanen ve teslima."},
  {id: 3556, sure_no: 23, ayet_no: 23, sure_adi: "Ahzab", text: "مِنَ الْمُؤْمِن۪ينَ رِجَالٌ صَدَقُوا مَا عَاهَدُوا اللّٰهَ عَلَيْهِۚ فَمِنْهُمْ مَنْ قَضٰى نَحْبَهُ وَمِنْهُمْ مَنْ يَنْتَظِرُۘ وَمَا بَدَّلُوا تَبْد۪يلاًۙ", translation: "Mü'minlerden öyle erler var ki; Allah'a, üzerine söz verdikleri şeylere bağlılık gösterdiler. Böylece onlardan bir kısmı verdiği sözü yerine getirdi, bir kısmı da beklemektedir. Verdikleri sözden asla dönmediler.", transliteration: "Minel mu'minine ricalun sadaku ma ahedullahe aleyh, fe minhum men kada nahbehu ve minhum men yentezıru ve ma beddelu tebdila."},
  {id: 3557, sure_no: 24, ayet_no: 24, sure_adi: "Ahzab", text: "لِيَجْزِيَ اللّٰهُ الصَّادِق۪ينَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنَافِق۪ينَ اِنْ شَٓاءَ اَوْ يَتُوبَ عَلَيْهِمْۜ اِنَّ اللّٰهَ كَانَ غَفُوراً رَح۪يماًۚ", translation: "Allah, doğru kimseleri, doğruluklarından dolayı ödüllendirecek, Münafıkları, dilerse cezalandıracak veya tevbelerini kabul edecektir. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Li yecziyallahus sadıkine bi sıdkıhım ve yuazzibel munafıkine in şae ev yetube aleyhim, innallahe kane gafuren rahima."},
  {id: 3558, sure_no: 25, ayet_no: 25, sure_adi: "Ahzab", text: "وَرَدَّ اللّٰهُ الَّذ۪ينَ كَفَرُوا بِغَيْظِهِمْ لَمْ يَنَالُوا خَيْراًۜ وَكَفَى اللّٰهُ الْمُؤْمِن۪ينَ الْقِتَالَۜ وَكَانَ اللّٰهُ قَوِياًّ عَز۪يزاًۚ", translation: "Kafirleri bir yarar elde etmeden kinleriyle geri çevirdi. Ve Allah, Mü'minlere savaşta yeterli geldi. Allah, Mutlak Güç Sahibi'dir, Mutlak Üstün Olan'dır.", transliteration: "Ve reddallahullezine keferu bi gayzıhim lem yenalu hayra, ve kefallahul mu'mininel kıtal, ve kanallahu kaviyyen aziza."},
  {id: 3559, sure_no: 26, ayet_no: 26, sure_adi: "Ahzab", text: "وَاَنْزَلَ الَّذ۪ينَ ظَاهَرُوهُمْ مِنْ اَهْلِ الْكِتَابِ مِنْ صَيَاص۪يهِمْ وَقَذَفَ ف۪ي قُلُوبِهِمُ الرُّعْبَ فَر۪يقاً تَقْتُلُونَ وَتَأْسِرُونَ فَر۪يقاًۚ", translation: "Allah, Kitap Ehli'nden düşmanlara yardım edenleri kalelerinden indirdi. Ve yüreklerine korku saldı. Bir kısmını öldürüyor, bir kısmını esir alıyordunuz.", transliteration: "Ve enzelellezine zaheruhum min ehlil kitabi min sayasihım ve kazefe fi kulubihimur ru'be feriykan taktulune ve te'sirune ferika."},
  {id: 3560, sure_no: 27, ayet_no: 27, sure_adi: "Ahzab", text: "وَاَوْرَثَكُمْ اَرْضَهُمْ وَدِيَارَهُمْ وَاَمْوَالَهُمْ وَاَرْضاً لَمْ تَطَؤُ۫هَاۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يراً۟", translation: "Sizi, onların yerlerine, yurtlarına, mallarına ve hiç ayak basmadığınız yerlere mirasçı yaptı. Allah, Her Şeye Güç Yetiren'dir.", transliteration: "Ve evresekum ardahum ve diyarehum ve emvalehum ve ardan lem tetauha, ve kanallahu ala kulli şey'in kadira."},
  {id: 3561, sure_no: 28, ayet_no: 28, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا النَّبِيُّ قُلْ لِاَزْوَاجِكَ اِنْ كُنْتُنَّ تُرِدْنَ الْحَيٰوةَ الدُّنْيَا وَز۪ينَتَهَا فَتَعَالَيْنَ اُمَتِّعْكُنَّ وَاُسَرِّحْكُنَّ سَرَاحاً جَم۪يلاً", translation: "Ey Nebi! Eşlerine de ki: \"Eğer dünya hayatını ve onun ziynetini istiyorsanız, gelin sizi yararlandırayım. Ve sizi güzellikle boşayayım.\"", transliteration: "Ya eyyuhen nebiyyu kul li ezvacike in kuntunne turidnel hayated dunya ve ziyneteha fe tealeyne umetti'kunne ve userrihkunne serahan cemila."},
  {id: 3562, sure_no: 29, ayet_no: 29, sure_adi: "Ahzab", text: "وَاِنْ كُنْتُنَّ تُرِدْنَ اللّٰهَ وَرَسُولَهُ وَالدَّارَ الْاٰخِرَةَ فَاِنَّ اللّٰهَ اَعَدَّ لِلْمُحْسِنَاتِ مِنْكُنَّ اَجْراً عَظ۪يماً", translation: "\"Eğer Allah ve Resul'ünü ve ahiret yurdunu istiyorsanız, kuşkusuz Allah, sizden muhsin olan kadınlar için büyük bir ecir hazırlamıştır.\"", transliteration: "Ve in kuntunne turidnallahe ve resulehu veddarel'ahırete fe innallahe eadde lil muhsinati minkunne ecren azima."},
  {id: 3563, sure_no: 30, ayet_no: 30, sure_adi: "Ahzab", text: "يَا نِسَٓاءَ النَّبِيِّ مَنْ يَأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُبَيِّنَةٍ يُضَاعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِۜ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَس۪يراً", translation: "Ey Nebi'nin hanımları! Sizden kim açık bir fuhuş yaparsa, onun azabı iki kat arttırılır. Ve bu Allah'a göre pek kolaydır.", transliteration: "Ya nisaen nebiyyi men ye'ti min kunne bi fahışetin mubeyyinetin yuda'af lehel'azabu dı'feyn, ve kane zalike alallahi yesira."},
  {id: 3564, sure_no: 31, ayet_no: 31, sure_adi: "Ahzab", text: "وَمَنْ يَقْنُتْ مِنْكُنَّ لِلّٰهِ وَرَسُولِه۪ وَتَعْمَلْ صَالِحاً نُؤْتِهَٓا اَجْرَهَا مَرَّتَيْنِۙ وَاَعْتَدْنَا لَهَا رِزْقاً كَر۪يماً", translation: "Ve sizden kim Allah ve Resul'üne içtenlikle uyar ve salih olanı yaparsa ona da iki kat ödül veririz. Ayrıca ona cömertçe bir rızık hazırlamışızdır.", transliteration: "Ve men yaknut min kunne lillahi ve resulihi ve ta'mel salihan nu'tiha ecreha merreteyni ve a'tedna leha rızkan kerima."},
  {id: 3565, sure_no: 32, ayet_no: 32, sure_adi: "Ahzab", text: "يَا نِسَٓاءَ النَّبِيِّ لَسْتُنَّ  كَاَحَدٍ مِنَ النِّسَٓاءِ اِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذ۪ي ف۪ي قَلْبِه۪ مَرَضٌ وَقُلْنَ قَوْلاً مَعْرُوفاًۚ", translation: "Ey Nebi'nin hanımları! Siz diğer kadınlardan biri değilsiniz. Eğer takvalıysanız, yabancı erkeklerle edalı bir üslupla konuşmayın. Zira kalbinde kötülük olan bir kimse buna yanlış anlam yükleyebilir. Sözün maruf olanını söyleyin.", transliteration: "Ya nisaen nebiyyi lestunne ke ehadin minen nisai inittekaytunne fe la tahda'ne bil kavli fe yatmaallezi fi kalbihi maradun ve kulne kavlen ma'rufa."},
  {id: 3566, sure_no: 33, ayet_no: 33, sure_adi: "Ahzab", text: "وَقَرْنَ ف۪ي بُيُوتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْاُو۫لٰى وَاَقِمْنَ الصَّلٰوةَ وَاٰت۪ينَ الزَّكٰوةَ وَاَطِعْنَ اللّٰهَ وَرَسُولَهُۜ اِنَّمَا يُر۪يدُ اللّٰهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ اَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْه۪يراًۚ", translation: "Evlerinizde vakarlı olun. Cahiliye dönemindeki gösteriş gibi gösteriş yapmayın. Salatı ikame edin, zekatı yapın. Allah'a ve Resul'üne itaat edin. Ey Nebi'nin ailesi! Allah sizden her türlü kirliliği giderip sizi tertemiz kılmak istiyor.", transliteration: "Ve karne fi buyutikunne ve la teberrecne teberrucel cahiliyyetil ula ve ekımnes salate ve atinez zekate ve atı'nallahe ve resuleh, innema yuridullahu li yuzhibe ankumur ricse ehlel beyti ve yutahhirekum tathira."},
  {id: 3567, sure_no: 34, ayet_no: 34, sure_adi: "Ahzab", text: "وَاذْكُرْنَ مَا يُتْلٰى ف۪ي بُيُوتِكُنَّ مِنْ اٰيَاتِ اللّٰهِ وَالْحِكْمَةِۜ اِنَّ اللّٰهَ كَانَ لَط۪يفاً خَب۪يراً۟", translation: "Evlerinizde okunmakta olan Allah'ın ayetlerini ve hikmetini aklınızdan çıkarmayın. Kuşkusuz Allah, Bütün Ayrıntıları Bilen'dir, Her Şeyden Haberdar'dır.", transliteration: "Vezkurne ma yutla fi buyutikunne min ayatillahi vel hikmeh, innallahe kane latifen habira."},
  {id: 3568, sure_no: 35, ayet_no: 35, sure_adi: "Ahzab", text: "اِنَّ الْمُسْلِم۪ينَ وَالْمُسْلِمَاتِ وَالْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ وَالْقَانِت۪ينَ وَالْقَانِتَاتِ وَالصَّادِق۪ينَ وَالصَّادِقَاتِ وَالصَّابِر۪ينَ وَالصَّابِرَاتِ وَالْخَاشِع۪ينَ وَالْخَاشِعَاتِ وَالْمُتَصَدِّق۪ينَ وَالْمُتَصَدِّقَاتِ وَالصَّٓائِم۪ينَ وَالصَّٓائِمَاتِ وَالْحَافِظ۪ينَ فُرُوجَهُمْ وَالْحَافِظَاتِ وَالذَّاكِر۪ينَ اللّٰهَ كَث۪يراً وَالذَّاكِرَاتِ اَعَدَّ اللّٰهُ لَهُمْ مَغْفِرَةً وَاَجْراً عَظ۪يماً", translation: "Müslim erkekler ve Müslim kadınlar, Mü'min erkekler ve Mü'min kadınlar, adanmış erkekler ve adanmış kadınlar, sadık erkekler ve sadık kadınlar, sabreden erkekler ve sabreden kadınlar, huşulu erkekler ve huşulu kadınlar, sadaka veren erkekler ve sadaka veren kadınlar, saim olan erkekler ve saim olan kadınlar, ırzlarını koruyan erkekler ve ırzlarını koruyan kadınlar ve Allah'ı çok zikreden erkekler ve Allah'ı çok zikreden kadınlar; Allah, onlar için bağışlanma ve büyük bir ödül hazırlamıştır.", transliteration: "İnnel muslimine vel muslimati vel mu'minine vel mu'minati vel kanitine vel kanitati ves sadikine ves sadikati ves sabirine ves sabirati vel haşiine vel haşiati vel mutesaddikine vel mutesaddikati ves saimine ves saimati vel hafızine furucehum vel hafızati vez zakirinallahe kesiren vez zakirati eaddallahu lehum magfireten ve ecren azima."},
  {id: 3569, sure_no: 36, ayet_no: 36, sure_adi: "Ahzab", text: "وَمَا كَانَ لِمُؤْمِنٍ وَلَا مُؤْمِنَةٍ اِذَا قَضَى اللّٰهُ وَرَسُولُهُٓ  اَمْراً اَنْ يَكُونَ لَهُمُ الْخِيَرَةُ مِنْ اَمْرِهِمْۜ وَمَنْ يَعْصِ اللّٰهَ وَرَسُولَهُ فَقَدْ ضَلَّ ضَلَالاً مُب۪يناً", translation: "Allah ve Resul'ü bir konuda karar verdiği zaman, hiçbir İman Eden erkek ve İman Eden kadın için, o konuda tercih hakkı yoktur. Kim Allah ve Resul'üne asilik ederse o, açık bir sapkınlıkla sapmış olur.", transliteration: "Ve ma kane li mu'minin ve la mu'minetin iza kadallahu ve resuluhu emren en yekune lehumul hıyeretu min emrihim, ve men ya'sıllahe ve resulehu fe kad dalle dalalen mubina."},
  {id: 3570, sure_no: 37, ayet_no: 37, sure_adi: "Ahzab", text: "وَاِذْ تَقُولُ لِلَّـذ۪ٓي اَنْعَمَ اللّٰهُ عَلَيْهِ وَاَنْعَمْتَ عَلَيْهِ اَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللّٰهَ وَتُخْف۪ي ف۪ي نَفْسِكَ مَا اللّٰهُ مُبْد۪يهِ وَتَخْشَى النَّاسَۚ وَاللّٰهُ اَحَقُّ اَنْ تَخْشٰيهُۜ فَلَمَّا قَضٰى زَيْدٌ مِنْهَا وَطَراً زَوَّجْنَاكَهَا لِكَيْ لَا يَكُونَ عَلَى الْمُؤْمِن۪ينَ حَرَجٌ ف۪ٓي اَزْوَاجِ اَدْعِيَٓائِهِمْ اِذَا قَضَوْا مِنْهُنَّ وَطَراًۜ وَكَانَ اَمْرُ اللّٰهِ مَفْعُولاً", translation: "Allah'ın kendisine nimet verdiği ve senin de nimet verdiğin kimseye diyordun ki: \"Eşini yanında tut. Allah'a karşı takvalı ol. Allah'ın açığa çıkaracağı şeyi içinde gizliyorsun. İnsanlara huşu duyuyorsun; oysaki huşu duyman gereken Allah'tır.\" Sonra Zeyd ondan tamamıyla ayrılınca, Biz onu sana eş yaptık ki böylece himaye edilenlerin boşadıkları kadınlarla evlenmelerinde İman Edenlerin üzerinde bir güçlük olmasın. İşte Allah'ın emri böylece yerine gelmiş oldu.", transliteration: "Ve iz tekulu lillezi en'amallahu aleyhi ve en'amte aleyhi emsik aleyke zevceke vettekıllah ve tuhfi fi nefsike mallahu mubdihi ve tahşen nas, vallahu ehakku en tahşah, fe lemma kada zeydun minha vetaran zevvecna keha likey la yekune alel mu'minine haracun fi ezvaci ed'ıyaihim iza kadav min hunne vetara, ve kane emrullahi mef'ula."},
  {id: 3571, sure_no: 38, ayet_no: 38, sure_adi: "Ahzab", text: "مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ ف۪يمَا فَرَضَ اللّٰهُ لَهُۜ سُنَّةَ اللّٰهِ فِي الَّذ۪ينَ خَلَوْا مِنْ قَبْلُۜ وَكَانَ اَمْرُ اللّٰهِ قَدَراً مَقْدُوراًۙ", translation: "Nebi, Allah'ın farz kılması gereği olarak bir şeyi yapmasından dolayı suçlanamaz. Bu, daha önceki toplumlarda da geçerli olan Allah'ın yasasıdır. Allah'ın emri, takdir edilmiş bir kaderdir.", transliteration: "Ma kane alen nebiyyi min harecin fima faradallahu leh, sunnetallahi fillezine halev min kabl, ve kane emrullahi kaderen makdura."},
  {id: 3572, sure_no: 39, ayet_no: 39, sure_adi: "Ahzab", text: "اَلَّذ۪ينَ يُبَلِّغُونَ رِسَالَاتِ اللّٰهِ وَيَخْشَوْنَهُ وَلَا يَخْشَوْنَ اَحَداً اِلَّا اللّٰهَۜ وَكَفٰى بِاللّٰهِ حَس۪يباً", translation: "Onlar, Allah'ın mesajını iletirler ve O'na huşu duyarlar ve Allah'tan başka hiç kimseye huşu duymazlar. Allah, hesap görücü olarak yeter.", transliteration: "Ellezine yubelligune risalatillahi ve yahşevnehu ve la yahşevne ehaden illallah, ve kefa billahi hasiba."},
  {id: 3573, sure_no: 40, ayet_no: 40, sure_adi: "Ahzab", text: "مَا كَانَ مُحَمَّدٌ اَبَٓا اَحَدٍ مِنْ رِجَالِكُمْ وَلٰكِنْ رَسُولَ اللّٰهِ وَخَاتَمَ النَّبِيّ۪نَۜ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَل۪يماً۟", translation: "Muhammed içinizden hiç birinizin babası değildir; fakat Allah'ın Resul'ü ve Nebilerin sonuncusudur. Allah, her şeyi en iyi bilendir.", transliteration: "Ma kane muhammedun eba ehadin min ricalikum, ve lakin resulallahi ve hatemen nebiyyin, ve kanallahu bi kulli şey'in alima."},
  {id: 3574, sure_no: 41, ayet_no: 41, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اذْكُرُوا اللّٰهَ ذِكْراً كَث۪يراًۙ", translation: "Ey iman edenler! Çok anışla Allah'ı anın.", transliteration: "Ya eyyuhellezine amenuzkurullahe zikren kesira."},
  {id: 3575, sure_no: 42, ayet_no: 42, sure_adi: "Ahzab", text: "وَسَبِّحُوهُ بُكْرَةً وَاَص۪يلاً", translation: "O'nu günün başında sonunda tesbih edin.", transliteration: "Ve sebbihuhu bukreten ve asila."},
  {id: 3576, sure_no: 43, ayet_no: 43, sure_adi: "Ahzab", text: "هُوَ الَّذ۪ي يُصَلّ۪ي عَلَيْكُمْ وَمَلٰٓئِكَتُهُ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ اِلَى النُّورِۜ وَكَانَ بِالْمُؤْمِن۪ينَ رَح۪يماً", translation: "Allah ve melekleri, sizi karanlıklardan aydınlığa çıkarmak için size salat etmektedir. O, Mü'minlere karşı çok merhametlidir.", transliteration: "Huvellezi yusalli aleykum ve melaiketuhu li yuhricekum minez zulumati ilen nur, ve kane bil mu'minine rahima."},
  {id: 3577, sure_no: 44, ayet_no: 44, sure_adi: "Ahzab", text: "تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُ سَلَامٌۚ وَاَعَدَّ لَهُمْ اَجْراً كَر۪يماً", translation: "O'na kavuşacakları gün, \"selam\" ile karşılanırlar. Onlar için cömertçe ödül hazırlanmıştır.", transliteration: "Tehiyyetuhum yevme yelkavnehu selam, ve eadde lehum ecren kerima."},
  {id: 3578, sure_no: 45, ayet_no: 45, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا النَّبِيُّ اِنَّٓا اَرْسَلْنَاكَ شَاهِداً وَمُبَشِّراً وَنَذ۪يراًۙ", translation: "Ey Nebi! Biz seni tanık, haber veren ve uyarıcı olarak gönderdik.", transliteration: "Ya eyyuhen nebiyyu inna erselnake şahiden ve mubeşşiren ve nezira."},
  {id: 3579, sure_no: 46, ayet_no: 46, sure_adi: "Ahzab", text: "وَدَاعِياً اِلَى اللّٰهِ بِـاِذْنِه۪ وَسِرَاجاً مُن۪يراً", translation: "O'nun izni ile Allah'a çağıran ve aydınlatan bir kandil olarak.", transliteration: "Ve daiyen ilallahi bi iznihi ve siracen munira."},
  {id: 3580, sure_no: 47, ayet_no: 47, sure_adi: "Ahzab", text: "وَبَشِّرِ الْمُؤْمِن۪ينَ بِاَنَّ لَهُمْ مِنَ اللّٰهِ فَضْلاً كَب۪يراً", translation: "Mü'minleri müjdele! Onlar için Allah'tan büyük bir lütuf vardır.", transliteration: "Ve beşşiril mu'minine bi enne lehum minallahi fadlen kebira."},
  {id: 3581, sure_no: 48, ayet_no: 48, sure_adi: "Ahzab", text: "وَلَا تُطِـعِ الْكَافِر۪ينَ وَالْمُنَافِق۪ينَ وَدَعْ اَذٰيهُمْ وَتَوَكَّلْ عَلَى اللّٰهِۜ وَكَفٰى بِاللّٰهِ وَك۪يلاً", translation: "Kafirlere ve Münafıklara boyun eğme, onların baskılarına aldırma. Allah'a tevekkül et. Vekil olarak Allah yeter.", transliteration: "Ve la tutııl kafirine vel munafikine veda'ezahum ve tevekkel alallah, ve kefa billahi vekila."},
  {id: 3582, sure_no: 49, ayet_no: 49, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا نَكَحْتُمُ الْمُؤْمِنَاتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّونَهَاۚ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحاً جَم۪يلاً", translation: "Ey iman edenler! Mü'min kadınlarla evlenip, sonra onlara dokunmadan boşarsanız, onların iddet süresince beklemelerini isteme hakkınız yoktur. Böyle bir durumda onları hemen yararlandırın ve güzellikle ayrılın.", transliteration: "Ya eyyuhellezine amenu iza nekahtumul mu'minati summe tallaktumuhunne min kabli en temessuhunne fe ma lekum aleyhinne min iddetin ta'tedduneha, fe mettiuhunne ve serrihuhunne serahan cemila."},
  {id: 3583, sure_no: 50, ayet_no: 50, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا النَّبِيُّ اِنَّٓا اَحْلَلْنَا لَكَ اَزْوَاجَكَ الّٰـت۪ٓي اٰتَيْتَ اُجُورَهُنَّ وَمَا مَلَكَتْ يَم۪ينُكَ مِمَّٓا اَفَٓاءَ اللّٰهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّاتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَالَاتِكَ الّٰت۪ي هَاجَرْنَ مَعَكَۘ وَامْرَاَةً مُؤْمِنَةً اِنْ وَهَبَتْ نَفْسَهَا لِلنَّبِيِّ اِنْ اَرَادَ النَّبِيُّ اَنْ يَسْتَنْكِحَهَاۗ خَالِصَةً لَكَ مِنْ دُونِ الْمُؤْمِن۪ينَۜ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ ف۪ٓي اَزْوَاجِهِمْ وَمَا مَلَكَتْ اَيْمَانُهُمْ لِكَيْلَا يَكُونَ عَلَيْكَ حَرَجٌۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً", translation: "Ey Nebi! Biz, ecirlerini verdiğin eşlerini ve savaşlarda Allah'ın fey olarak sana verdiği antlaşma yolu ile hak sahibi olduklarını, sana helal kıldık. Seninle birlikte hicret eden amcanın kızları, halalarının kızları, dayının kızları, teyzelerinin kızları ve kendisini Nebi'ye hibe edip de Nebi'nin de evlenmeyi uygun gördüğü Mü'min kadını -ki bu yalnızca sana özgüdür- sana helal kıldık. Onlara zevceleri ve antlaşma yolu ile hak sahibi oldukları konusunda neyi farz kıldığımızı biliriz. Bu durum senin için bir güçlük olmasın diyedir. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyyuhen nebiyyu inna ahlelna leke ezvacekelleti ateyte ucurehunne ve ma meleket yeminuke mimma efaallahu aleyke ve benati ammike ve benati ammatike ve benati halike ve benati halatikellati hacerne meak, vemreeten mu'mineten in vehebet nefseha lin nebiyyi in eraden nebiyyu en yestenkihaha halisaten leke min dunil mu'minin, kad alimna ma faradna aleyhim fi ezvacihim ve ma meleket eymanuhum li keyla yekune aleyke harac, ve kanallahu gafuran rahima."},
  {id: 3584, sure_no: 51, ayet_no: 51, sure_adi: "Ahzab", text: "تُرْج۪ي مَنْ تَشَٓاءُ مِنْهُنَّ وَتُــْٔـو۪ٓي اِلَيْكَ مَنْ تَشَٓاءُۜ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَۜ  ذٰلِكَ اَدْنٰٓى اَنْ تَقَرَّ اَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَٓا اٰتَيْتَهُنَّ كُلُّهُنَّۜ وَاللّٰهُ يَعْلَمُ مَا ف۪ي قُلُوبِكُمْۜ وَكَانَ اللّٰهُ عَل۪يماً حَل۪يماً", translation: "Onlardan dilediğini ertelersin, dilediğini yanına alırsın. Bıraktıklarından, istediğini yanına almanda senin için bir sakınca yoktur. En uygun olanı budur. Bu, onların gözlerinin aydın olması ve hüzünlenmemeleri ve kendilerine verdiğinle hoşnut olmaları için en uygun olanıdır. Allah, kalplerinizde olanı bilmektedir. Allah, Her Şeyi Bilen'dir, Çok Şefkatli'dir.", transliteration: "Turci men teşau minhunne ve tu'vi ileyke men teşau, ve menibtegayte mimmen azelte fe la cunaha aleyk, zalike edna en tekarre a'yunuhunne ve la yahzenne ve yerdayne bima ateytehunne kulluhunn, vallahu ya'lemu ma fi kulubikum ve kanallahu alimen halima."},
  {id: 3585, sure_no: 52, ayet_no: 52, sure_adi: "Ahzab", text: "لَا يَحِلُّ لَكَ النِّسَٓاءُ مِنْ بَعْدُ وَلَٓا اَنْ تَبَدَّلَ بِهِنَّ مِنْ اَزْوَاجٍ وَلَوْ اَعْجَبَكَ حُسْنُهُنَّ اِلَّا مَا مَلَكَتْ يَم۪ينُكَۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ رَق۪يـباً۟", translation: "Bundan böyle başka kadınlarla evlenmen, eşlerini boşayıp yerine iyi olması hoşuna gitse bile başka kadın alman sana helal olmaz. Ancak antlaşma yolu ile hak sahibi olduğun hariç. Allah, her şeyi gözetleyip denetleyendir.", transliteration: "La yahıllu leken nisau min ba'du ve la en tebeddele bihinne min ezvacin ve lev a'cebeke husnuhunne illa ma meleket yeminuk, ve kanallahu ala kulli şey'in rakiba."},
  {id: 3586, sure_no: 53, ayet_no: 53, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَدْخُلُوا بُيُوتَ النَّبِيِّ اِلَّٓا اَنْ يُؤْذَنَ لَكُمْ اِلٰى طَعَامٍ غَيْرَ نَاظِر۪ينَ اِنٰيهُۙ وَلٰكِنْ اِذَا دُع۪يتُمْ فَادْخُلُوا فَاِذَا طَعِمْتُمْ فَانْتَشِرُوا وَلَا مُسْتَأْنِس۪ينَ لِحَد۪يثٍۜ اِنَّ ذٰلِكُمْ كَانَ يُؤْذِي النَّبِيَّ فَيَسْتَحْـي۪ مِنْكُمْۘ وَاللّٰهُ لَا يَسْتَحْـي۪ مِنَ الْحَقِّۜ وَاِذَا سَاَلْتُمُوهُنَّ مَتَاعاً فَسْـَٔلُوهُنَّ مِنْ وَرَٓاءِ حِجَابٍۜ ذٰلِكُمْ اَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّۜ وَمَا كَانَ لَكُمْ اَنْ تُؤْذُوا رَسُولَ اللّٰهِ وَلَٓا اَنْ تَنْكِحُٓوا اَزْوَاجَهُ مِنْ بَعْدِه۪ٓ اَبَداًۜ اِنَّ ذٰلِكُمْ كَانَ عِنْـدَ اللّٰهِ عَظ۪يـماً", translation: "Ey İman Edenler! Nebi'nin evlerine, yemeğe izin verilmiş olmasının dışında vakitli vakitsiz izinsiz girmeyin. Ama çağrılmışsanız o başka. Yemeği yiyince de hadise dalıp oyalanmayın, hemen dağılın. Doğrusu bu haliniz Nebi'yi rahatsız ediyor, o sizi kırmamak için bir şey demiyor. Allah ise gerçeği açıklamaktan çekinmez. Onlardan bir şey isteyeceğiniz zaman da hicap arkasından isteyin. Bu sizin kalpleriniz için de onların kalpleri için de daha uygundur. Allah'ın Resul'üne rahatsızlık vermeniz doğru olmaz. Kendisinden sonra eşleriyle asla evlenemezsiniz. Kuşkusuz bu Allah'ın yanında büyük şeydir.", transliteration: "Ya eyyuhellezine amenu la tedhulu buyuten nebiyyi illa en yu'zene lekum ila taamin gayre nazırine inahu ve lakin iza duitum fedhulu fe iza taimtum fenteşiru ve la muste'nisine li hadis, inne zalikum kane yu'zin nebiyye fe yestahyi minkum vallahu la yestahyi minel hakk, ve iza seeltumuhunne metaan fes'eluhunne min verai hıcab, zalikum atharu li kulubikum ve kulubihinn, ve ma kane lekum en tu'zu resulallahi ve la en tenkihu ezvacehu min ba'dihi ebeda, inne zalikum kane indallahi azima."},
  {id: 3587, sure_no: 54, ayet_no: 54, sure_adi: "Ahzab", text: "اِنْ تُبْدُوا شَيْـٔاً اَوْ تُخْفُوهُ فَاِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَل۪يماً", translation: "Bir şeyi açığa vursanız da gizleseniz de şunu bilin ki; kuşkusuz Allah, Her Şeyi En İyi Bilen'dir.", transliteration: "İn tubdu şey'en ev tuhfuhu fe innallahe kane bi kulli şey'in alima."},
  {id: 3588, sure_no: 55, ayet_no: 55, sure_adi: "Ahzab", text: "لَا جُنَاحَ عَلَيْهِنَّ ف۪ٓي اٰبَٓائِهِنَّ وَلَٓا اَبْنَٓائِهِنَّ وَلَٓا اِخْوَانِهِنَّ وَلَٓا اَبْنَٓاءِ اِخْوَانِهِنَّ وَلَٓا اَبْنَٓاءِ اَخَوَاتِهِنَّ وَلَا نِسَٓائِهِنَّ وَلَا مَا مَلَكَتْ اَيْمَانُهُنَّۚ وَاتَّق۪ينَ اللّٰهَۜ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَه۪يداً", translation: "Onlara; Babaları, oğulları, kardeşleri, erkek kardeşlerinin oğulları, kız kardeşlerinin oğulları, hemcinsleri ve antlaşma ile hak sahibi oldukları ile serbestçe görüşmelerinde bir sakınca yoktur. Allah'a karşı takva sahibi olun. Kuşkusuz Allah, Her Şeye Tanık'tır.", transliteration: "La cunaha aleyhinne fi abaihinne ve la ebnaihinne ve la ihvanihinne ve la ebnai ihvanihinne ve la ebnai ehavatihinne ve la nisaihinne ve la ma meleket eymanuhun, vettekinallah, innallahe kane ala kulli şey'in şehida."},
  {id: 3589, sure_no: 56, ayet_no: 56, sure_adi: "Ahzab", text: "اِنَّ اللّٰهَ وَمَلٰٓئِكَتَهُ يُصَلُّونَ عَلَى النَّبِيِّۜ يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا صَلُّوا عَلَيْهِ وَسَلِّمُوا تَسْل۪يماً", translation: "Kuşkusuz Allah ve melekleri, Nebi'ye salat ederler. Ey İman Edenler! Siz de O'na salat edin. Tam bir bağlılıkla selam edin.", transliteration: "İnnallahe ve melaiketehu yusallune alen nebiyyi, ya eyyuhellezine amenu sallu aleyhi ve sellimu teslima."},
  {id: 3590, sure_no: 57, ayet_no: 57, sure_adi: "Ahzab", text: "اِنَّ الَّذ۪ينَ يُؤْذُونَ اللّٰهَ وَرَسُولَهُ لَعَنَهُمُ اللّٰهُ فِي الدُّنْيَا وَالْاٰخِرَةِ وَاَعَدَّ لَهُمْ عَذَاباً مُه۪يناً", translation: "Allah ve Resul'üne eziyet edenlere, Allah, dünya ve ahirette lanet etti. Ve onlar için alçaltıcı bir azap hazırladı.", transliteration: "İnnellezine yu'zunallahe ve resulehu leanehumullahu fid dunya vel ahıreti ve eadde lehum azaben muhina."},
  {id: 3591, sure_no: 58, ayet_no: 58, sure_adi: "Ahzab", text: "وَالَّذ۪ينَ يُؤْذُونَ الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ بِغَيْرِ مَا اكْتَسَبُوا فَقَدِ احْتَمَلُوا بُهْتَاناً وَاِثْماً مُب۪يناً۟", translation: "Mü'min erkeklere ve Mü'min kadınlara yapmadıkları bir şey nedeniyle eziyet eden kimseler, bu durumda iftira ve apaçık günah yüklenmiş oldular.", transliteration: "Vellezine yu'zunel mu'minine vel mu'minati bi gayri mektesebu fe kadihtemelu buhtanen ve ismen mubina."},
  {id: 3592, sure_no: 59, ayet_no: 59, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا النَّبِيُّ قُلْ لِاَزْوَاجِكَ وَبَنَاتِكَ وَنِسَٓاءِ الْمُؤْمِن۪ينَ يُدْن۪ينَ عَلَيْهِنَّ مِنْ جَلَاب۪يبِهِنَّۜ ذٰلِكَ اَدْنٰٓى اَنْ يُعْرَفْنَ فَلَا يُؤْذَيْنَۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً", translation: "Ey Nebi! Eşlerine, kızlarına ve Mü'minlerin kadınlarına söyle, cilbablarını üzerlerine salsınlar. Bu, salma onların bilinmeleri ve eziyet edilmemeleri için daha uygundur. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyyuhen nebiyyu kul li ezvacike ve benatike ve nisail mu'minine yudnine aleyhinne min celabibihinn, zalike edna en yu'refne fe la yu'zeyn ve kanallahu gafuren rahima."},
  {id: 3593, sure_no: 60, ayet_no: 60, sure_adi: "Ahzab", text: "لَئِنْ لَمْ يَنْتَهِ الْمُنَافِقُونَ وَالَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ وَالْمُرْجِفُونَ فِي الْمَد۪ينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُونَكَ ف۪يهَٓا اِلَّا قَل۪يلاًۚۛ", translation: "Eğer, münafıklar, kalplerinde hastalık bulunanlar, şehirde yalan ve kötü haber yayanlar bu davranışlarına son vermezlerse, ant olsun ki, seni onlara musallat ederiz de seninle bir arada daha fazla kalamazlar.", transliteration: "Le in lem yentehil munafikune vellezine fi kulubihim maradun vel murcifune fil medineti le nugriyenneke bihim summe la yucaviruneke fiha illa kalila."},
  {id: 3594, sure_no: 61, ayet_no: 61, sure_adi: "Ahzab", text: "مَلْعُون۪ينَۚۛ اَيْنَ مَا ثُقِفُٓوا اُخِذُوا وَقُتِّلُوا تَقْت۪يلاً", translation: "Lanetlenmiş kimseler olarak, nerede rastlansalar yakalanıp, kıyasıya öldürülürler.", transliteration: "Mel'unin, eyne ma sukıfu uhızu ve kuttılu taktila."},
  {id: 3595, sure_no: 62, ayet_no: 62, sure_adi: "Ahzab", text: "سُنَّةَ اللّٰهِ فِي الَّذ۪ينَ خَلَوْا مِنْ قَبْلُۚ وَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْد۪يلاً", translation: "Gelmiş geçmiş olanların tamamı hakkında Allah'ın sünneti budur. Ve Allah'ın sünnetinde asla bir değişiklik bulamazsın.", transliteration: "Sunnetallahi fillezine halev min kabl, ve len tecide li sunnetillahi tebdila."},
  {id: 3596, sure_no: 63, ayet_no: 63, sure_adi: "Ahzab", text: "يَسْـَٔلُكَ النَّاسُ عَنِ السَّاعَةِۜ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِۜ وَمَا يُدْر۪يكَ لَعَلَّ السَّاعَةَ تَكُونُ قَر۪يباً", translation: "İnsanlar sana Sa'at'ten soruyorlar. De ki: \"Onun bilgisi yalnızca Allah'ın yanındadır.\" Ne bilirsin belki de o Sa'at yaklaşmıştır.", transliteration: "Yes'eluken nasu anis saah, kul innema ilmuha indallah, ve ma yudrike lealles saate tekunu kariba."},
  {id: 3597, sure_no: 64, ayet_no: 64, sure_adi: "Ahzab", text: "اِنَّ اللّٰهَ لَعَنَ الْكَافِر۪ينَ وَاَعَدَّ لَهُمْ سَع۪يراًۙ", translation: "Allah, Kafirlere lanet etmiştir. Onlar için alevli ateşi hazırlamıştır.", transliteration: "İnnallahe leanel kafirine ve eadde lehum saira."},
  {id: 3598, sure_no: 65, ayet_no: 65, sure_adi: "Ahzab", text: "خَالِد۪ينَ ف۪يهَٓا اَبَداًۚ لَا يَجِدُونَ وَلِياًّ وَلَا نَص۪يراًۚ", translation: "Orada sürekli kalıcıdırlar. Orada bir koruyucu ve yardımcı bulamazlar.", transliteration: "Halidine fiha ebeda, la yecidune veliyyen ve la nasira."},
  {id: 3599, sure_no: 66, ayet_no: 66, sure_adi: "Ahzab", text: "يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِي النَّارِ يَقُولُونَ يَا لَيْتَنَٓا اَطَعْنَا اللّٰهَ وَاَطَعْنَا الرَّسُولَا", translation: "Yüzlerinin ateşin içinde bir taraftan bir tarafa çevrileceği gün: \"Keşke biz, Allah'a ve Resul'e itaat etseydik.\" derler.", transliteration: "Yevme tukallebu vucuhuhum fin nari yekulune ya leytena eta'nallahe ve eta'ner resula."},
  {id: 3600, sure_no: 67, ayet_no: 67, sure_adi: "Ahzab", text: "وَقَالُوا رَبَّنَٓا اِنَّٓا اَطَعْنَا سَادَتَنَا وَكُـبَرَٓاءَنَا فَاَضَلُّونَا السَّب۪يلَا", translation: "\"Ey Rabb'imiz! Biz büyüklerimize ve beylerimize uyduk, onlar da bizi yanlış yola saptırdılar.\" derler.", transliteration: "Ve kalu rabbena inna ata'na sadetena ve kuberaena fe edallunes sebil."},
  {id: 3601, sure_no: 68, ayet_no: 68, sure_adi: "Ahzab", text: "رَبَّنَٓا اٰتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْناً كَب۪يراً۟", translation: "\"Rabb'imiz! Onlara iki kat azap ver ve onları büyük bir lanete uğrat.\"", transliteration: "Rabbena atihim dı'feyni minel azabi vel anhum la'nen kebira."},
  {id: 3602, sure_no: 69, ayet_no: 69, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَكُونُوا كَالَّذ۪ينَ اٰذَوْا مُوسٰى فَبَرَّاَهُ اللّٰهُ مِمَّا قَالُواۜ وَكَانَ عِنْدَ اللّٰهِ وَج۪يهاً", translation: "Ey İman Edenler! Musa'ya eziyet eden kimseler gibi olmayın! Allah; O'nu, onların söylediklerinden temize çıkardı. O, Allah'ın yanında yüzü ak olan biriydi.", transliteration: "Ya eyyuhellezine amenu la tekunu kellezine azev musa fe berreehullahu mimma kalu, ve kane indallahi veciha."},
  {id: 3603, sure_no: 70, ayet_no: 70, sure_adi: "Ahzab", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَقُولُوا قَوْلاً سَد۪يداًۙ", translation: "Ey İman Edenler! Allah'a takvalı davranın ve daima doğruyu söyleyin;", transliteration: "Ya eyyuhellezine amenuttekullahe ve kulu kavlen sedida."},
  {id: 3604, sure_no: 71, ayet_no: 71, sure_adi: "Ahzab", text: "يُصْلِحْ لَكُمْ اَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْۜ وَمَنْ يُطِـعِ اللّٰهَ وَرَسُولَهُ فَقَدْ فَازَ فَوْزاً عَظ۪يماً", translation: "Ki yaptıklarınızı sizin için düzeltsin, suçlarınızı bağışlasın. Her kim Allah'a ve Resul'üne itaat ederse en büyük kurtuluşla kurtuluşa ermiş olur.", transliteration: "Yuslıh lekum a'malekum ve yagfir lekum zunubekum, ve men yutıillahe ve resulehu fe kad faze fevzen azima."},
  {id: 3605, sure_no: 72, ayet_no: 72, sure_adi: "Ahzab", text: "اِنَّا عَرَضْنَا الْاَمَانَةَ عَلَى السَّمٰوَاتِ وَالْاَرْضِ وَالْجِبَالِ فَاَبَيْنَ اَنْ يَحْمِلْنَهَا وَاَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْاِنْسَانُۜ اِنَّهُ كَانَ ظَلُوماً جَهُولاًۙ", translation: "Biz, emaneti göklere, yere ve dağlara sunduk. Onu yüklenmeye yanaşmadılar. Ondan korktular. Onu insan yüklendi. O, çok zalim ve çok cahildir.", transliteration: "İnna aradnel emanete ales semavati vel ardı vel cibali fe ebeyne en yahmilneha ve eşfakne minha ve hamelehal insan, innehu kane zalumen cehula."},
  {id: 3606, sure_no: 73, ayet_no: 73, sure_adi: "Ahzab", text: "لِيُعَذِّبَ اللّٰهُ الْمُنَافِق۪ينَ وَالْمُنَافِقَاتِ وَالْمُشْرِك۪ينَ وَالْمُشْرِكَاتِ وَيَتُوبَ اللّٰهُ عَلَى الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً", translation: "Allah, münafık erkeklere ve kadınlara; Müşrik erkeklere ve kadınlara azap eder, Mü'min erkeklerin ve kadınların tevbelerini kabul eder Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Li yuazziballahul munafikine vel munafikati vel muşrikine vel muşrikati ve yetuballahu alel mu'minine vel mu'minat, ve kanallahu gafuren rahima."},
  {id: 3607, sure_no: 1, ayet_no: 1, sure_adi: "Sebe", text: "اَلْحَمْدُ لِلّٰهِ الَّذ۪ي لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ وَلَهُ الْحَمْدُ فِي الْاٰخِرَةِۜ  وَهُوَ الْحَك۪يمُ الْخَب۪يرُ", translation: "Hamd, göklerde ve yerde olan şeyler kendisine ait olan Allah içindir. Hamd, Ahiret'te de O'na aittir. O, En İyi Hüküm Veren'dir, Her Şeyden Haberdar'dır.", transliteration: "El hamdu lillahillezi lehu ma fis semavati ve ma fil ardı ve lehul hamdu fil ahireh, ve huvel hakimul habir."},
  {id: 3608, sure_no: 2, ayet_no: 2, sure_adi: "Sebe", text: "يَعْلَمُ مَا يَلِجُ فِي الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَٓاءِ وَمَا يَعْرُجُ ف۪يهَاۜ وَهُوَ الرَّح۪يمُ الْغَفُورُ", translation: "O, yere gireni ve ondan çıkanı, gökten ineni ve ona yükseleni bilir. O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya'lemu ma yelicu fil ardı ve ma yahrucu minha ve ma yenzilu mines semai ve ma yarucu fiha, ve huver rahimul gafur."},
  {id: 3609, sure_no: 3, ayet_no: 3, sure_adi: "Sebe", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا لَا تَأْت۪ينَا السَّاعَةُۜ قُلْ بَلٰى وَرَبّ۪ي لَتَأْتِيَنَّكُمْ عَالِمِ الْغَيْبِۚ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِ وَلَٓا اَصْغَرُ مِنْ ذٰلِكَ وَلَٓا اَكْبَرُ اِلَّا ف۪ي كِتَابٍ مُب۪ينٍۙ", translation: "Kafirler: \"Bize o Sa'at gelmeyecek.\" dediler. De ki: \"Bilakis! Gaybı bilen Rabb'ime ant olsun ki, o kesinlikle size gelecektir. Göklerde olsun, yerde olsun zerre kadar da olsa hiçbir şey O'ndan gizli kalmaz. En küçüğünden en büyüğüne kadar her şey Kitab-ı Mubin'dedir.", transliteration: "Ve kalellezine keferu la te'tines saah, kul bela ve rabbi le te'tiyennekum alimil gayb, la ya'zubu anhu miskalu zerretin fis semavati ve la fil ardı ve la asgaru min zalike ve la ekberu illa fi kitabin mubin."},
  {id: 3610, sure_no: 4, ayet_no: 4, sure_adi: "Sebe", text: "لِيَجْزِيَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِۜ اُو۬لٰٓئِكَ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَر۪يمٌ", translation: "O Gün; iman edip, salihatı yapanları ödüllendirecektir. Onlar için bağışlanma ve cömertçe rızık vardır.", transliteration: "Li yecziyellezine amenu ve amilus salihat, ulaike lehum magfiretun ve rızkun kerim."},
  {id: 3611, sure_no: 5, ayet_no: 5, sure_adi: "Sebe", text: "وَالَّذ۪ينَ سَعَوْ ف۪ٓي اٰيَاتِنَا مُعَاجِز۪ينَ اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ مِنْ رِجْزٍ اَل۪يمٌۗ", translation: "Ayetlerimizi etkisiz bırakmak için çalışanlar; işte onlar, onlar için elem verici kötü bir azap vardır.", transliteration: "Vellezine seav fi ayatina muacizine ulaike lehum azabun min riczin elim."},
  {id: 3612, sure_no: 6, ayet_no: 6, sure_adi: "Sebe", text: "وَيَرَى الَّذ۪ينَ اُو۫تُوا الْعِلْمَ الَّـذ۪ٓي اُنْزِلَ اِلَيْكَ مِنْ رَبِّكَ هُوَ الْحَقَّۙ وَيَهْد۪ٓي اِلٰى صِرَاطِ الْعَز۪يزِ الْحَم۪يدِ", translation: "Kendilerine ilim verilmiş olanlar, Rabb'inden sana indirilenin yegane gerçek olduğunu ve onun; Aziz'in, Hamid'in yoluna ilettiğini görüyorlar.", transliteration: "Ve yerellezine utul ılmellezi unzile ileyke min rabbike huvel hakka ve yehdi ila sıratıl azizil hamid."},
  {id: 3613, sure_no: 7, ayet_no: 7, sure_adi: "Sebe", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا هَلْ نَدُلُّكُمْ عَلٰى رَجُلٍ يُنَبِّئُكُمْ اِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍۙ اِنَّكُمْ لَف۪ي خَلْقٍ جَد۪يدٍۚ", translation: "Kafirler alayımsı bir şekilde: \"Çürüyüp, lime lime parçalandığınız zaman, sizin yeniden dirileceğinizi haber veren bir adamı size gösterelim mi?\" dediler.", transliteration: "Ve kalellezine keferu hel nedullukum ala raculin yunebbiukum iza muzzıktum kulle mumezzekın innekum le fi halkın cedid."},
  {id: 3614, sure_no: 8, ayet_no: 8, sure_adi: "Sebe", text: "اَفْتَرٰى عَلَى اللّٰهِ كَذِباً اَمْ بِه۪ جِنَّةٌۜ  بَلِ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ فِي الْعَذَابِ وَالضَّلَالِ الْبَع۪يدِ", translation: "\"Bu adam ya uydurduklarını Allah'a dayandırıyor ya da cinnet geçiriyor!\" Hayır, aslında inanmayanlar, çaresizlik ve derin bir sapkınlık içindedirler.", transliteration: "Eftera alallahi keziben em bihi cinneh, belillezine la yuminune bil ahireti fil azabi ved dalalil baid."},
  {id: 3615, sure_no: 9, ayet_no: 9, sure_adi: "Sebe", text: "اَفَلَمْ يَرَوْا اِلٰى مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِۜ اِنْ نَشَأْ نَخْسِفْ بِهِمُ الْاَرْضَ اَوْ نُسْقِطْ عَلَيْهِمْ كِسَفاً مِنَ السَّمَٓاءِۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَةً لِكُلِّ عَبْدٍ مُن۪يبٍ۟", translation: "Onlar, göklerde ve yerde, önlerinde ve arkalarında olanları görmediler mi? Dilersek onları yere geçiririz. Veya gökten üzerlerine parçalar düşürürüz. Bunlarda yönelen her kul için ayet vardır.", transliteration: "E fe lem yerev ila ma beyne eydihim ve ma halfehum mines semai vel ard, in neşe'nahsif bihimul arda ev nuskıt aleyhim kisefen mines sema, inne fi zalike le ayeten li kulli abdin munib."},
  {id: 3616, sure_no: 10, ayet_no: 10, sure_adi: "Sebe", text: "وَلَقَدْ اٰتَيْنَا دَاوُ۫دَ مِنَّا فَضْلاًۜ يَا جِبَالُ اَوِّب۪ي مَعَهُ وَالطَّيْرَۚ وَاَلَنَّا لَهُ الْحَد۪يدَۙ", translation: "Ant olsun ki, katımızdan Davud'a bir fazilet verdik. Ey dağlar ve kuşlar! Onunla beraber övgüyü tekrarlayın. Ve onun için demiri yumuşattık.", transliteration: "Ve lekad ateyna davude minna fadla, ya cibalu evvibi meahu vet tayr, ve elenna lehul hadid."},
  {id: 3617, sure_no: 11, ayet_no: 11, sure_adi: "Sebe", text: "اَنِ اعْمَلْ سَابِغَاتٍ وَقَدِّرْ فِي السَّرْدِ وَاعْمَلُوا صَالِحاًۜ اِنّ۪ي بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Bedeni örten zırhlar yap. Bağlantılarını güzel şekilde tasarla. Salihatı yapın. Ben, yaptıklarınızı Bütün Gerçeğiyle Gören'im.", transliteration: "Enimel sabigatin ve kaddir fis serdi va'melu saliha, inni bima tamelune basir."},
  {id: 3618, sure_no: 12, ayet_no: 12, sure_adi: "Sebe", text: "وَلِسُلَيْمٰنَ الرّ۪يحَ غُدُوُّهَا شَهْرٌ وَرَوَاحُهَا شَهْرٌۚ وَاَسَلْنَا لَهُ عَيْنَ الْقِطْرِۜ وَمِنَ الْجِنِّ مَنْ يَعْمَلُ بَيْنَ يَدَيْهِ بِاِذْنِ رَبِّه۪ۜ وَمَنْ يَزِغْ مِنْهُمْ عَنْ اَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّع۪يرِ", translation: "Sabahleyin bir aylık yol gitmeyi, akşamleyin bir aylık yoldan geri dönmeyi sağlayan rüzgar, Süleyman içindi. Ve erimiş bakırı kaynağından ona akıttık. Ve cinnlerden, Rabb'inin izni ile onun elinin altında çalışanlar vardı. Onlardan kim emrimizden çıkacak olsa, ona alevli ateşin azabından tattırdık.", transliteration: "Ve li suleymaner riha guduvvuha şehrun ve revahuha şehr, ve eselna lehu aynel kıtr, ve minel cinni men ya'melu beyne yedeyhi bi izni rabbih, ve men yezıg minhum an emrina nuzıkhu min azabis sair."},
  {id: 3619, sure_no: 13, ayet_no: 13, sure_adi: "Sebe", text: "يَعْمَلُونَ لَهُ مَا يَشَٓاءُ مِنْ مَحَار۪يبَ وَتَمَاث۪يلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُورٍ رَاسِيَاتٍۜ اِعْمَلُٓوا اٰلَ دَاوُ۫دَ شُكْراًۜ وَقَل۪يلٌ مِنْ عِبَادِيَ الشَّكُورُ", translation: "Ona dilediği gibi mabetler, şekil verilmiş eşyalar, havuz büyüklüğünde çanaklar ve sabit ağır kazanlar yapıyorlardı. Ey Davud'u izleyenler! Şükür için çalışın. Ama kullarım içinde şükreden çok azdır.", transliteration: "Ya'melune lehu ma yeşau min meharibe ve temasile ve cifanin kel cevabi ve kudurin rasiyat, i'melu ale davude şukra, ve kalilun min ibadiyeş şekur."},
  {id: 3620, sure_no: 14, ayet_no: 14, sure_adi: "Sebe", text: "فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلٰى مَوْتِه۪ٓ اِلَّا دَٓابَّةُ الْاَرْضِ تَأْكُلُ مِنْسَاَتَهُۚ فَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ اَنْ لَوْ كَانُوا يَعْلَمُونَ الْغَيْبَ مَا لَبِثُوا فِي الْعَذَابِ الْمُه۪ينِ", translation: "Onun ölümünü gerçekleştirdiğimiz zaman; ölümünü, \"minsee\"yi kemiren \"deabbetulard\"dan başka bir şey ortaya çıkarmadı. Yere kapandığında ortaya çıktı ki; cinnler o gaybı bilselerdi, o alçaltıcı azap içinde kalmazlardı.", transliteration: "Fe lemma kadayna aleyhil mevte ma dellehum ala mevtihi illa dabbetul ardı te'kulu minseeteh, fe lemma harre tebeyyenetil cinnu en lev kanu ya'lemunel gaybe ma lebisu fil azabil muhin."},
  {id: 3621, sure_no: 15, ayet_no: 15, sure_adi: "Sebe", text: "لَقَدْ كَانَ لِسَبَأٍ ف۪ي مَسْكَنِهِمْ اٰيَةٌۚ جَنَّتَانِ عَنْ يَم۪ينٍ وَشِمَالٍۜ كُلُوا مِنْ رِزْقِ رَبِّكُمْ وَاشْكُرُوا لَهُۜ بَلْدَةٌ طَيِّبَةٌ وَرَبٌّ غَفُورٌ", translation: "Ant olsun ki, Sebelilerin yaşadıkları yerde bir ayet vardı: Sağda ve solda iki cennet! \"Rabb'inizin rızkından yiyin ve O'na şükredin. Temiz bir belde ve çok bağışlayıcı bir Rabb!\"", transliteration: "Lekad kane li sebein fi meskenihim ayeh, cennetani an yeminin ve şimal, kulu min rızkı rabbikum veşkuru leh, beldetun tayyibetun ve rabbun gafur."},
  {id: 3622, sure_no: 16, ayet_no: 16, sure_adi: "Sebe", text: "فَاَعْرَضُوا فَاَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنَاهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ اُكُلٍ خَمْطٍ وَاَثْلٍ وَشَيْءٍ مِنْ سِدْرٍ قَل۪يلٍ", translation: "Onlarsa yüz çevirdiler. Biz de üzerlerine su seddinin suyunu salıverdik. Ve iki cennetlerini, acı meyveli ağaçlara, meyvesiz ağaçlara ve az miktarda sedir ağacı bulunan iki cennete çevirdik.", transliteration: "Fe a'radu fe erselna aleyhim seylel arimi ve beddelna-hum bi cenneteyhim cenneteyni zevatey ukulin hamtın ve eslin ve şeyin min sidrin kalil."},
  {id: 3623, sure_no: 17, ayet_no: 17, sure_adi: "Sebe", text: "ذٰلِكَ جَزَيْنَاهُمْ بِمَا كَفَرُواۜ وَهَلْ نُجَاز۪ٓي اِلَّا الْكَفُورَ", translation: "Bu, kafirlik etmeleri nedeniyle, onları cezalandırmamızdır. Biz, yalnızca kafirleri cezalandırırız.", transliteration: "Zalike cezeynahum bima keferu, ve hel nucazi illel kefur."},
  {id: 3624, sure_no: 18, ayet_no: 18, sure_adi: "Sebe", text: "وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّت۪ي بَارَكْنَا ف۪يهَا قُرًى ظَاهِرَةً وَقَدَّرْنَا ف۪يهَا السَّيْرَۜ س۪يرُوا ف۪يهَا لَيَالِيَ وَاَيَّاماً اٰمِن۪ينَ", translation: "Onlarla bereketli kıldığımız kentler arasında birbirlerine yakın kasabalar yaptık. Bunlar arasında yolculuk yapılacak yollar düzenledik. \"Buralarda geceleri ve gündüzleri güvenli olarak yolculuk yapın dedik.", transliteration: "Ve cealna beynehum ve beynel kurelleti barekna fiha kuren zahireten ve kadderna fihes seyr, siru fiha leyaliye ve eyyamen aminin."},
  {id: 3625, sure_no: 19, ayet_no: 19, sure_adi: "Sebe", text: "فَقَالُوا رَبَّنَا بَاعِدْ بَيْنَ اَسْفَارِنَا وَظَلَمُٓوا اَنْفُسَهُمْ فَجَعَلْنَاهُمْ اَحَاد۪يثَ وَمَزَّقْنَاهُمْ كُلَّ مُمَزَّقٍۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ", translation: "Fakat onlar: \"Rabb'imiz! Yolculuk mesafelerimizi uzat.\" dediler ve kendilerine zulmettiler. Biz de onları dillerde dolaşan hadis yaptık. Ve onları tamamen parça parça dağıttık. Kuşkusuz bunda çok sabredenlerin ve çok şükredenlerin tamamı için kesinlikle ayetler vardır.", transliteration: "Fe kalu rabbena baidbeyne esfarina ve zalemu enfusehum fe cealnahum ehadise ve mezzaknahum kulle mumezzak, inne fi zalike le ayatin li kulli sabbarin şekur."},
  {id: 3626, sure_no: 20, ayet_no: 20, sure_adi: "Sebe", text: "وَلَقَدْ صَدَّقَ عَلَيْهِمْ اِبْل۪يسُ ظَنَّهُ فَاتَّبَعُوهُ اِلَّا فَر۪يقاً مِنَ الْمُؤْمِن۪ينَ", translation: "Ant olsun ki, İblis onlar hakkındaki beklentisini gerçekleştirdi. İman Edenlerden oluşan bir topluluk hariç, hepsi ona uydular.", transliteration: "Ve lekad saddaka aleyhim iblisu zannehu fettebeuhu illa ferikan minel muminin."},
  {id: 3627, sure_no: 21, ayet_no: 21, sure_adi: "Sebe", text: "وَمَا كَانَ لَهُ عَلَيْهِمْ مِنْ سُلْطَانٍ اِلَّا لِنَعْلَمَ مَنْ يُؤْمِنُ بِالْاٰخِرَةِ مِمَّنْ هُوَ مِنْهَا ف۪ي شَكٍّۜ وَرَبُّكَ عَلٰى كُلِّ شَيْءٍ حَف۪يظٌ۟", translation: "Aslında İblisin onlar üzerinde bir sultanlığı yoktu. Fakat Biz, ahirete iman edenle ondan kuşku duyanı ayırt edeceğiz. Senin Rabb'in Her Şeyi Gözetip Koruyan'dır.", transliteration: "Ve ma kane lehu aleyhim min sultanin illa li na'leme men yu'minu bil ahireti mimmen huve minha fi şekk, ve rabbuke ala kulli şeyin hafiz."},
  {id: 3628, sure_no: 22, ayet_no: 22, sure_adi: "Sebe", text: "قُلِ ادْعُوا الَّذ۪ينَ زَعَمْتُمْ مِنْ دُونِ اللّٰهِۚ لَا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِ وَمَا لَهُمْ ف۪يهِمَا مِنْ شِرْكٍ وَمَا لَهُ مِنْهُمْ مِنْ ظَه۪يرٍ", translation: "De ki: \"Allah'ın yanı sıra değer verdiklerinize yakarın! Onlar, göklerde ve yerde zerre kadar bir şeye sahip değildirler. Onların, göklerin ve yerin yaratılmalarında bir payları yoktur. Ve Allah'ın, onların yardımına ihtiyacı da yoktur.", transliteration: "Kulid'ullezine zeamtum min dunillah, la yemlikune miskale zerretin fis semavati ve la fil ardı ve ma lehum fihima min şirkin ve ma lehu minhum min zahir."},
  {id: 3629, sure_no: 23, ayet_no: 23, sure_adi: "Sebe", text: "وَلَا تَنْفَعُ الشَّفَاعَةُ عِنْدَهُٓ اِلَّا لِمَنْ اَذِنَ لَهُۜ حَتّٰٓى اِذَا فُزِّعَ عَنْ قُلُوبِهِمْ قَالُوا مَاذَاۙ قَالَ رَبُّكُمْۜ قَالُوا الْحَقَّۚ وَهُوَ الْعَلِيُّ الْكَب۪يرُ", translation: "O'nun yanında şefaat, yalnızca izin verdiği kimseye fayda verir. Kalplerindeki korku giderilince: \"Rabb'imiz ne buyurdu?\" derler. \"Gerçeği.\" derler. Ve O, Çok Yüce'dir, Çok Büyük'tür.", transliteration: "Ve la tenfeuş şefaatu indehu illa li men ezine leh, hatta iza fuzzia an kulubihim kalu maza kale rabbukum, kalul hakk, ve huvel aliyyul kebir."},
  {id: 3630, sure_no: 24, ayet_no: 24, sure_adi: "Sebe", text: "قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمٰوَاتِ وَالْاَرْضِۜ قُلِ اللّٰهُۙ وَاِنَّٓا اَوْ اِيَّاكُمْ لَعَلٰى هُدًى اَوْ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "De ki: \"Göklerden ve yerden sizi rızıklandıran kimdir?\" De ki: \"Allah! Ya siz ya da biz doğru yoldayız veya ya biz ya da siz açıkça bir sapkınlık içindeyiz.\"", transliteration: "Kul men yerzukukum mines semavati vel ard, kulillahu ve inna ev iyyakum le ala huden ev fi dalalin mubin."},
  {id: 3631, sure_no: 25, ayet_no: 25, sure_adi: "Sebe", text: "قُلْ لَا تُسْـَٔلُونَ عَمَّٓا اَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُونَ", translation: "De ki: \"Bizim yaptığımız cürümden siz sorumlu tutulmazsınız. Ve biz de sizin yaptıklarınızdan sorumlu tutulmayız.\"", transliteration: "Kul la tus'elune amma ecremna ve la nus'elu amma ta'melun."},
  {id: 3632, sure_no: 26, ayet_no: 26, sure_adi: "Sebe", text: "قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّۜ وَهُوَ الْفَتَّاحُ الْعَل۪يمُ", translation: "De ki: \"Rabb'imiz hepimizi bir araya toplayacak sonra da aramızda adaletle yargıda bulunacaktır. O; Mutlak Adil Olan Yargıç'tır, Her Şeyi Bilen'dir.\"", transliteration: "Kul yecmeu beynena rabbuna summe yeftehu beynena bil hakk, ve huvel fettahul alim."},
  {id: 3633, sure_no: 27, ayet_no: 27, sure_adi: "Sebe", text: "قُلْ اَرُونِيَ الَّذ۪ينَ اَلْحَقْتُمْ بِه۪ شُرَكَٓاءَ كَلَّاۜ بَلْ هُوَ اللّٰهُ الْعَز۪يزُ الْحَك۪يمُ", translation: "De ki: \"O'na ortak kabul ettiklerinizi bana gösterin! Asla böyle bir şey olamaz. O, Allah ki; Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.\"", transliteration: "Kul eruniyellezine elhaktum bihi şurekae kella, bel huvallahul azizul hakim."},
  {id: 3634, sure_no: 28, ayet_no: 28, sure_adi: "Sebe", text: "وَمَٓا اَرْسَلْنَاكَ اِلَّا كَٓافَّةً لِلنَّاسِ بَش۪يراً وَنَذ۪يراً وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ", translation: "Biz, seni bütün insanlığa yalnızca haber verici ve uyarıcı olarak gönderdik. Ne var ki insanların çoğu bu gerçeği anlamıyorlar.", transliteration: "Ve ma erselnake illa kaffeten lin nasi beşiren ve neziren ve lakinne ekseren nasi la ya'lemun."},
  {id: 3635, sure_no: 29, ayet_no: 29, sure_adi: "Sebe", text: "وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Eğer doğru söylüyorsanız, bizi tehdit ettiğiniz uyarı ne zaman?\" diyorlar.", transliteration: "Ve yekulune meta hazel va'du in kuntum sadikin."},
  {id: 3636, sure_no: 30, ayet_no: 30, sure_adi: "Sebe", text: "قُلْ لَكُمْ م۪يعَادُ يَوْمٍ لَا تَسْتَأْخِرُونَ عَنْهُ سَاعَةً وَلَا تَسْتَقْدِمُونَ۟", translation: "De ki: \"Size yapılan uyarının bir zamanı vardır. Ondan, bir saat bile geri de kalmazsınız ileri de geçemezsiniz.\"", transliteration: "Kul lekum miadu yevmin la teste'hirune anhu saaten ve la testakdimun."},
  {id: 3637, sure_no: 31, ayet_no: 31, sure_adi: "Sebe", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا لَنْ نُؤْمِنَ بِهٰذَا الْقُرْاٰنِ وَلَا بِالَّذ۪ي بَيْنَ يَدَيْهِۜ وَلَوْ تَرٰٓى اِذِ الظَّالِمُونَ مَوْقُوفُونَ عِنْدَ رَبِّهِمْۚ يَرْجِعُ بَعْضُهُمْ اِلٰى بَعْضٍۨ الْقَوْلَۚ يَقُولُ الَّذ۪ينَ اسْتُضْعِفُوا لِلَّذ۪ينَ اسْتَكْبَرُوا  لَوْلَٓا اَنْتُمْ لَكُنَّا مُؤْمِن۪ينَ", translation: "Kafirler, \"Biz ne bu Kur'an'a ne de ondan önce gelene asla inanmayacağız.\" dediler. Sen bu zalimleri, Rabb'leri huzuruna çıkarıldıklarında nasıl birbirlerine sataştıklarını bir görsen! Güçsüzler, büyüklük taslayan kimselere, \"Eğer siz olmasaydınız, biz kesinlikle inananlardan olurduk.\" derler.", transliteration: "Ve kalellezine keferu len nu'mine bi hazel kur'ani ve la billezi beyne yedeyh, ve lev tera iziz zalimune mevkufune inde rabbihim, yerciu ba'duhum ila ba'dınil kavl, yekulullezinestud'ifu lillezinestekberu lev la entum le kunna muminin."},
  {id: 3638, sure_no: 32, ayet_no: 32, sure_adi: "Sebe", text: "قَالَ الَّذ۪ينَ اسْتَكْبَرُوا لِلَّذ۪ينَ اسْتُضْعِفُٓوا اَنَحْنُ صَدَدْنَاكُمْ عَنِ الْهُدٰى بَعْدَ اِذْ جَٓاءَكُمْ بَلْ كُنْتُمْ مُجْرِم۪ينَ", translation: "Büyüklük taslayanlar, mus'tezaflara: \"Size doğru yol gösterildiğinde, sizi o yoldan biz mi alıkoyduk? Hayır! Siz zaten suçlu kimselerdiniz.\" dediler.", transliteration: "Kalellezinestekberu lillezinestud'ifu e nahnu sadednakum anil huda ba'de iz caekum bel kuntum mucrimin."},
  {id: 3639, sure_no: 33, ayet_no: 33, sure_adi: "Sebe", text: "وَقَالَ الَّذ۪ينَ اسْتُضْعِفُوا لِلَّذ۪ينَ اسْتَكْبَرُوا بَلْ مَكْرُ الَّيْلِ وَالنَّهَارِ اِذْ تَأْمُرُونَـنَٓا اَنْ نَكْفُرَ بِاللّٰهِ وَنَجْعَلَ لَهُٓ  اَنْدَاداًۜ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۜ وَجَعَلْنَا الْاَغْلَالَ ف۪ٓي اَعْنَاقِ الَّذ۪ينَ كَفَرُواۜ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوا يَعْمَلُونَ", translation: "Mus'tezaflar, büyüklük taslayanlara: \"Hayır! Gece gündüz kurduğunuz tuzaklarla bize, Allah'ı yalanlamamızı, O'na birtakım eşler koşmamızı öneriyordunuz.\" dediler. Azabı gördükleri zaman, için için pişman olacaklar. Kafirlerin boyunlarına demir halkalar geçiririz. Onlar, yalnızca yapmış oldukları şeylerin karşılığını görecekler.", transliteration: "Ve kalellezinestud'ifu lillezinestekberu bel mekrul leyli ven nehari iz te'murunena en nekfure billahi ve nec'ale lehu endada, ve eserrun nedamete lemma raevul azab, ve cealnel aglale fi a'nakıllezine keferu, hel yuczevne illa ma kanu ya'melun."},
  {id: 3640, sure_no: 34, ayet_no: 34, sure_adi: "Sebe", text: "وَمَٓا اَرْسَلْنَا ف۪ي قَرْيَةٍ مِنْ نَذ۪يرٍ اِلَّا قَالَ مُتْرَفُوهَٓاۙ اِنَّا بِمَٓا اُرْسِلْتُمْ بِه۪ كَافِرُونَ", translation: "Biz, hangi beldeye bir uyarıcı gönderdiysek, oranın mutref olanları: \"Biz, sizin getirdiğiniz şeyleri küfrediyoruz.\" dediler.", transliteration: "Ve ma erselna fi karyetin min nezirin illa kale mutrefuha inna bima ursiltum bihi kafirun."},
  {id: 3641, sure_no: 35, ayet_no: 35, sure_adi: "Sebe", text: "وَقَالُوا نَحْنُ اَكْثَرُ اَمْوَالاً وَاَوْلَاداًۙ وَمَا نَحْنُ بِمُعَذَّب۪ينَ", translation: "\"Biz, varlık ve evlat olarak çok daha fazlayız. Ve biz, azap görecek de değiliz.\" dediler.", transliteration: "Ve kalu nahnu ekseru emvalen ve evladen ve ma nahnu bi muazzebin."},
  {id: 3642, sure_no: 36, ayet_no: 36, sure_adi: "Sebe", text: "قُلْ اِنَّ رَبّ۪ي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ۟", translation: "De ki: \"Benim Rabb'im, dilediği kimseye rızkı genişletir ve takdir eder. Fakat insanların çoğu idrak etmezler.\"", transliteration: "Kul inne rabbi yebsutur rızka limen yeşau ve yakdiru ve lakinne ekseren nasi la ya'lemun."},
  {id: 3643, sure_no: 37, ayet_no: 37, sure_adi: "Sebe", text: "وَمَٓا اَمْوَالُكُمْ وَلَٓا اَوْلَادُكُمْ بِالَّت۪ي تُقَرِّبُكُمْ عِنْدَنَا زُلْفٰٓى اِلَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحاًۘ فَاُو۬لٰٓئِكَ لَهُمْ جَزَٓاءُ الضِّعْفِ بِمَا عَمِلُوا وَهُمْ فِي الْغُرُفَاتِ اٰمِنُونَ", translation: "Sizi, Bize yaklaştıracak olan, mallarınız ve evlatlarınız değildir. İman edenler ve salihatı yapanlar, işte onlara yaptıklarına karşılık kat kat ödül vardır. Ve onlar, yüksek makamlarda güven içindedirler.", transliteration: "Ve ma emvalukum ve la evladukum billeti tukarribukum indena zulfa illa men amene ve amile salihan fe ulaike lehum cezaud dı'fi bima amilu ve hum fil gurufati aminun."},
  {id: 3644, sure_no: 38, ayet_no: 38, sure_adi: "Sebe", text: "وَالَّذ۪ينَ يَسْعَوْنَ ف۪ٓي اٰيَاتِنَا مُعَاجِز۪ينَ اُو۬لٰٓئِكَ فِي الْعَذَابِ مُحْضَرُونَ", translation: "Ayetlerimizi etkisiz kılmak için uğraşanlar, işte onlar azapla karşı karşıya kalacak olanlardır.", transliteration: "Vellezine yes'avne fi ayatina muacizine ulaike fil azabi muhdarun."},
  {id: 3645, sure_no: 39, ayet_no: 39, sure_adi: "Sebe", text: "قُلْ اِنَّ رَبّ۪ي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ مِنْ عِبَادِه۪ وَيَقْدِرُ لَهُۜ وَمَٓا اَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُۚ وَهُوَ خَيْرُ الرَّازِق۪ينَ", translation: "De ki: \"Benim Rabb'im, kullarından dilediği kimse için rızkı genişletir ve takdir eder. Bir şey infak ettiğiniz zaman; O, onun karşılığını verir. Ve O, rızık verenlerin en hayırlısıdır.", transliteration: "Kul inne rabbi yebsutur rızka li men yeşau min ibadihi ve yakdiru leh, ve ma enfaktum min şeyin fe huve yuhlifuh, ve huve hayrur razikin."},
  {id: 3646, sure_no: 40, ayet_no: 40, sure_adi: "Sebe", text: "وَيَوْمَ يَحْشُرُهُمْ جَم۪يعاً ثُمَّ يَقُولُ لِلْمَلٰٓئِكَةِ اَهٰٓؤُ۬لَٓاءِ اِيَّاكُمْ  كَانُوا يَعْبُدُونَ", translation: "Ve O Gün O, onların hepsini bir araya toplayacak. Sonra meleklere: \"Şunlar, size mi kulluk ediyorlardı?\" diye soracak.", transliteration: "Ve yevme yahşuruhum cemian summe yekulu lil melaiketi e haulai iyyakum kanu ya'budun."},
  {id: 3647, sure_no: 41, ayet_no: 41, sure_adi: "Sebe", text: "قَالُوا سُبْحَانَكَ اَنْتَ وَلِيُّنَا مِنْ دُونِهِمْۚ بَلْ كَانُوا يَعْبُدُونَ الْجِنَّۚ اَكْثَرُهُمْ بِهِمْ مُؤْمِنُونَ", translation: "Onlar: \"Seni tenzih ederiz. Bizim velimiz onlar değil, Sen'sin. Bilakis, onlar, cinnlere kulluk ediyorlardı. Çoğu onlara inanıyorlardı.\" derler.", transliteration: "Kalu subhaneke ente veliyyuna min dunihim, bel kanu ya'budunel cinn, ekseruhum bihim mu'minun."},
  {id: 3648, sure_no: 42, ayet_no: 42, sure_adi: "Sebe", text: "فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَفْعاً وَلَا ضَراًّۜ وَنَقُولُ لِلَّذ۪ينَ ظَلَمُوا ذُوقُوا عَذَابَ النَّارِ الَّت۪ي كُنْتُمْ بِهَا تُكَذِّبُونَ", translation: "Artık bugün birbirinize ne yarar ne de zarar vermeye gücünüz yeter. Zulmedenlere: \"Yalanlamış olduğunuz ateşin azabını tadın.\" diyeceğiz.", transliteration: "Fel yevme la yemliku ba'dukum li ba'dın nef'an ve la darra, ve nekulu lillezine zalemu zuku azaben narilleti kuntum biha tukezzibun."},
  {id: 3649, sure_no: 43, ayet_no: 43, sure_adi: "Sebe", text: "وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ قَالُوا مَا هٰذَٓا اِلَّا رَجُلٌ يُر۪يدُ اَنْ يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ اٰبَٓاؤُ۬كُمْۚ وَقَالُوا مَا هٰذَٓا اِلَّٓا اِفْكٌ مُفْتَرًىۜ وَقَالَ الَّذ۪ينَ كَفَرُوا لِلْحَقِّ لَمَّا جَٓاءَهُمْۙ اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌ", translation: "Onlara apaçık ayetlerimiz okunduğu zaman: \"Bu, ancak, atalarınızın tapmakta olduğu şeylerden sizi vazgeçirmek isteyen bir adamdan başkası değildir.\" dediler. Ve dediler ki: \"Bu, uydurulmuş bir iftiradan başka bir şey değildir.\" Kafirler, kendilerine gerçek gelince: \"Bu, ancak apaçık bir büyüdür.\" dediler.", transliteration: "Ve iza tutla aleyhim ayatuna beyyinatin kalu ma haza illa raculun yuridu en yasuddekum amma kane ya'budu abaukum, ve kalu ma haza illa ifkun muftera ve kalellezine keferu lil hakkı lemma caehum in haza illa sihrun mubin."},
  {id: 3650, sure_no: 44, ayet_no: 44, sure_adi: "Sebe", text: "وَمَٓا اٰتَيْنَاهُمْ مِنْ كُتُبٍ يَدْرُسُونَهَا وَمَٓا اَرْسَلْنَٓا اِلَيْهِمْ قَبْلَكَ مِنْ نَذ۪يرٍۜ", translation: "Biz, onlara bilgilenecekleri kitaplardan vermedik. Ve senden önce onlara bir uyarıcı da göndermedik.", transliteration: "Ve ma ateynahum min kutubin yedrusuneha ve ma erselna ileyhim kableke min nezir."},
  {id: 3651, sure_no: 45, ayet_no: 45, sure_adi: "Sebe", text: "وَكَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْۙ وَمَا بَلَغُوا مِعْشَارَ مَٓا اٰتَيْنَاهُمْ فَكَذَّبُوا رُسُل۪ي۠ فَكَيْفَ كَانَ نَك۪يرِ۟", translation: "Kendilerinden öncekiler de yalanlamışlardı. Ve onlara verdiğimiz nimetlerin onda birine bile sahip değillerdi. Buna rağmen Resul'lerimizi yalanladılar. Fakat Beni yok sayma neymiş gördüler!", transliteration: "Ve kezzebellezine min kablihim ve ma belegu mi'şare ma ateynahum fe kezzebu rusuli, fe keyfe kane nekir."},
  {id: 3652, sure_no: 46, ayet_no: 46, sure_adi: "Sebe", text: "قُلْ اِنَّـمَٓا اَعِظُـكُمْ بِوَاحِدَةٍۚ اَنْ تَقُومُوا لِلّٰهِ مَثْنٰى وَفُرَادٰى ثُمَّ تَتَفَكَّرُوا۠ مَا بِصَاحِبِكُمْ مِنْ جِنَّةٍۜ اِنْ هُوَ اِلَّا نَذ۪يرٌ لَكُمْ بَيْنَ يَدَيْ عَذَابٍ شَد۪يدٍ", translation: "De ki: \"Size yalnızca bir tek şey öğütlüyorum. Allah için ikişer ikişer ve teker teker bir araya gelin. Sonra iyice düşünün.\" Sizin arkadaşınızda bir delilik yoktur. O, şiddetli bir azabın öncesinde bir uyarıcıdır.", transliteration: "Kul innema eızukum bi vahideh, en tekumu lillahi mesna ve furada summe tetefekkeru, ma bi sahıbikum min cinneh, in huve illa nezirun lekum beyne yedey azabin şedid."},
  {id: 3653, sure_no: 47, ayet_no: 47, sure_adi: "Sebe", text: "قُلْ مَا سَاَلْتُكُمْ مِنْ اَجْرٍ فَهُوَ لَكُمْۜ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ", translation: "De ki: \"Sizden bir karşılık beklemiyorum. Sizden bir beklentim yok. Benim ücretim, yalnızca Allah'a aittir. Ve O, Her Şeye Tanık'tır.\"", transliteration: "Kul ma seeltukum min ecrin fe huve lekum, in ecriye illa alallah, ve huve ala kulli şeyin şehid."},
  {id: 3654, sure_no: 48, ayet_no: 48, sure_adi: "Sebe", text: "قُلْ اِنَّ رَبّ۪ي يَقْذِفُ بِالْحَقِّۚ عَلَّامُ الْغُيُوبِ", translation: "De ki \"Benim Rabb'im gerçeği ortaya koyar. O, bütün gaybı en iyi bilendir.\"", transliteration: "Kul inne rabbi yakzifu bil hakk, allamul guyub."},
  {id: 3655, sure_no: 49, ayet_no: 49, sure_adi: "Sebe", text: "قُلْ جَٓاءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُع۪يدُ", translation: "De ki: \"Hakk geldi. Artık, Batıl ortaya bir şey koyamaz da geri getiremez de.\"", transliteration: "Kul cael hakku ve ma yubdiul batılu ve ma yuid."},
  {id: 3656, sure_no: 50, ayet_no: 50, sure_adi: "Sebe", text: "قُلْ اِنْ ضَلَلْتُ فَاِنَّـمَٓا اَضِلُّ عَلٰى نَفْس۪يۚ وَاِنِ اهْتَدَيْتُ فَبِمَا يُوح۪ٓي اِلَيَّ رَبّ۪يۜ اِنَّهُ سَم۪يعٌ قَر۪يبٌ", translation: "De ki: \"Eğer yanlış yola sapmışsam sadece kendi zararıma sapmış olurum. Eğer doğru yoldaysam, bilesiniz ki Rabb'imin bana verdiği vahiy sayesindedir. O, Her Şeyi Duyan'dır, En Yakın Olan'dır.", transliteration: "Kul in dalaltu fe innema edıllu ala nefsi, ve in ihtedeytu fe bima yuhi ileyye rabbi, innehu semiun karib."},
  {id: 3657, sure_no: 51, ayet_no: 51, sure_adi: "Sebe", text: "وَلَوْ تَرٰٓى اِذْ فَزِعُوا فَلَا فَوْتَ وَاُخِذُوا مِنْ مَكَانٍ قَر۪يبٍۙ", translation: "Onları, korkuya kapıldıkları zaman bir görsen. Artık kurtuluş yoktur. Yakın bir yerden yakalandılar.", transliteration: "Ve lev tera iz feziu fe la fevte ve uhızu min mekanin karib."},
  {id: 3658, sure_no: 52, ayet_no: 52, sure_adi: "Sebe", text: "وَقَالُٓوا اٰمَنَّا بِه۪ۚ وَاَنّٰى لَهُمُ التَّنَاوُشُ مِنْ مَكَانٍ بَع۪يدٍۚ", translation: "\"O'na iman ettik.\" dediler. Bunu uzak bir yerden nasıl elde edebilirler?", transliteration: "Ve kalu amenna bih, ve enna lehumut tenavuşu min mekanin baid."},
  {id: 3659, sure_no: 53, ayet_no: 53, sure_adi: "Sebe", text: "وَقَدْ كَفَرُوا بِه۪ مِنْ قَبْلُۚ وَيَقْذِفُونَ بِالْغَيْبِ مِنْ مَكَانٍ بَع۪يدٍ", translation: "Oysaki daha önce onu kesin olarak yalanlamışlardı. Uzak bir yerden gayb hakkında atıp tutuyorlardı.", transliteration: "Ve kad keferu bihi min kabl, ve yakzifune bil gaybi min mekanin baid."},
  {id: 3660, sure_no: 54, ayet_no: 54, sure_adi: "Sebe", text: "وَح۪يلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِاَشْيَاعِهِمْ مِنْ قَبْلُۜ اِنَّهُمْ كَانُوا ف۪ي شَكٍّ مُر۪يبٍ", translation: "Tıpkı bundan önce benzerlerine yapıldığı gibi; artık canlarının arzu ettiğini yapamayacaklar. Onlar kaygı verici ikilem içindeydiler.", transliteration: "Ve hile beynehum ve beyne ma yeştehune kema fuile bi eşyaihim min kabl, innehum kanu fi şekkin murib."},
  {id: 3661, sure_no: 1, ayet_no: 1, sure_adi: "Fatır", text: "اَلْحَمْدُ لِلّٰهِ فَاطِرِ السَّمٰوَاتِ وَالْاَرْضِ جَاعِلِ الْمَلٰٓئِكَةِ رُسُلاً اُو۬ل۪ٓي اَجْنِحَةٍ مَثْنٰى وَثُلٰثَ وَرُبَاعَۜ يَز۪يدُ فِي الْخَلْقِ مَا يَشَٓاءُۜ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Hamd göklere ve yere yaradılış yasalarını koyan, melekleri ikişer, üçer, dörder kanatlı resuller yapan Allah'a özgüdür. O, yaratmada dilediğini arttırır. Allah, Her Şeye Gücü Yeten'dir.", transliteration: "Elhamdu lillahi fatırıs semavati vel ardı cailil melaiketi rusulen uli ecnihatin mesna ve sulase ve rubaa, yezidu fil halkı ma yeşau, innallahe ala kulli şey'in kadir."},
  {id: 3662, sure_no: 2, ayet_no: 2, sure_adi: "Fatır", text: "مَا يَفْتَحِ اللّٰهُ لِلنَّاسِ مِنْ رَحْمَةٍ فَلَا مُمْسِكَ لَهَاۚ وَمَا يُمْسِكْۙ فَلَا مُرْسِلَ لَهُ مِنْ بَعْدِه۪ۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Allah, insanlar için rahmetinden neyi açarsa artık onu tutacak biri olamaz. Her neyi tutarsa da artık O'ndan sonra onu gönderecek de yoktur. Ve O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ma yeftehillahu lin nasi min rahmetin fe la mumsike leha, ve ma yumsik fe la mursile lehu min ba'dih, ve huvel azizul hakim."},
  {id: 3663, sure_no: 3, ayet_no: 3, sure_adi: "Fatır", text: "يَٓا اَيُّهَا النَّاسُ اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْۜ هَلْ مِنْ خَالِقٍ غَيْرُ اللّٰهِ يَرْزُقُكُمْ مِنَ السَّمَٓاءِ وَالْاَرْضِۜ  لَٓا اِلٰهَ اِلَّا هُوَۘ   فَاَنّٰى تُؤْفَكُونَ", translation: "Ey insanlar! Allah'ın üzerinizdeki nimetini hatırlayın. Allah'tan başka gökten ve yerden size rızık veren bir yaratıcı var mı? O'ndan başka ilah yoktur. O halde ne diye döndürülüyorsunuz?", transliteration: "Ya eyyuhen nasuzkuru ni'metallahi aleykum, hel min halikın gayrullahi yerzukukum mines semai vel ard, la ilahe illa huve fe enna tu'fekun."},
  {id: 3664, sure_no: 4, ayet_no: 4, sure_adi: "Fatır", text: "وَاِنْ يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ", translation: "Eğer seni yalanlıyorlarsa, bil ki senden önceki Resuller de yalanlanmışlardı. Bütün işler Allah'a döndürülür.", transliteration: "Ve in yukezzibuke fe kad kuzzibet rusulun min kablik, ve ilallahi turceul umur."},
  {id: 3665, sure_no: 5, ayet_no: 5, sure_adi: "Fatır", text: "يَٓا اَيُّهَا النَّاسُ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَا۠ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُورُ", translation: "Ey insanlar! Allah'ın sözü gerçektir. Öyleyse, dünya hayatı sizi aldatmasın! Sakın aldatıcı sizi Allah ile aldatmasın.", transliteration: "Ya eyyuhen nasu inne va'dallahi hakkun fe la tegurrennekumul hayatud dunya, ve la yegurrennekum billahil garur."},
  {id: 3666, sure_no: 6, ayet_no: 6, sure_adi: "Fatır", text: "اِنَّ الشَّيْطَانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُواًّۜ اِنَّمَا يَدْعُوا حِزْبَهُ لِيَكُونُوا مِنْ اَصْحَابِ السَّع۪يرِۜ", translation: "Kuşkusuz şeytan sizin düşmanınızdır. Öyleyse siz de onu düşman edinin. O kendisine uyanları alevli ateşin halkından olmaya çağırır.", transliteration: "İnneş şeytane lekum aduvvun fettehızuhu aduvva, innema yed'u hızbehu li yekunu min ashabis seir."},
  {id: 3667, sure_no: 7, ayet_no: 7, sure_adi: "Fatır", text: "اَلَّذ۪ينَ كَفَرُوا لَهُمْ عَذَابٌ شَد۪يدٌۜ وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ كَب۪يرٌ۟", translation: "Kafirler için şiddetli bir azap vardır. İman eden ve salihatı yapanlar için bağışlanma ve büyük bir ödül vardır.", transliteration: "Ellezine keferu lehum azabun şedid, vellezine amenu ve amilus salihati lehum magfiretun ve ecrun kebir."},
  {id: 3668, sure_no: 8, ayet_no: 8, sure_adi: "Fatır", text: "اَفَمَنْ زُيِّنَ لَهُ سُٓوءُ عَمَلِه۪ فَرَاٰهُ حَسَناًۜ فَاِنَّ اللّٰهَ يُضِلُّ مَنْ يَشَٓاءُ وَيَهْد۪ي مَنْ يَشَٓاءُۘ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَاتٍۜ اِنَّ اللّٰهَ عَل۪يمٌ بِمَا يَصْنَعُونَ", translation: "Yaptığı kötü işleri kendisine yakıştıran ve onu güzel gören kimseye ne demeli? Allah, Hak edeni saptırır, hak edeni de doğru yola iletir. Dolayısıyla onlar için üzülüp kendini harap etme. Allah, onların yapmakta olduklarını en iyi bilendir.", transliteration: "E fe men zuyyine lehu suu amelihi fe reahu hasena, fe innallahe yudıllu men yeşau ve yehdi men yeşau, fe la tezheb nefsuke aleyhim haserat, innallahe alimun bima yesneun."},
  {id: 3669, sure_no: 9, ayet_no: 9, sure_adi: "Fatır", text: "وَاللّٰهُ الَّـذ۪ٓي اَرْسَلَ الرِّيَاحَ فَتُث۪يرُ سَحَاباً فَسُقْنَاهُ اِلٰى بَلَدٍ مَيِّتٍ فَاَحْيَيْنَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ  كَذٰلِكَ النُّشُورُ", translation: "Allah, rüzgarları göndererek bulutları harekete geçirendir. Sonra onu ölü bir beldeye yönelterek, onunla, ölü yeryüzüne hayat veririz. İşte yeniden diriliş de böyledir.", transliteration: "Vallahullezi, erseler riyaha fe tusiru sehaben fe suknahu ila beledin meyyitin fe ahyeyna bihil arda ba'de mevtiha, kezaliken nuşur."},
  {id: 3670, sure_no: 10, ayet_no: 10, sure_adi: "Fatır", text: "مَنْ كَانَ يُر۪يدُ الْعِزَّةَ فَلِلّٰهِ الْعِزَّةُ جَم۪يعاًۜ اِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِـحُ يَرْفَعُهُۜ وَالَّذ۪ينَ يَمْكُرُونَ السَّيِّـَٔاتِ لَهُمْ عَذَابٌ شَد۪يدٌۜ وَمَكْرُ اُو۬لٰٓئِكَ هُوَ يَبُورُ", translation: "Kim izzet istiyorsa, bilsin ki izzet tamamen Allah'a aittir. Temiz sözler O'na yükselir. Düzgün iş onu yükseltir. Kötülük planı yapanlar için, şiddetli bir azap vardır. Kurdukları düzenler boşa gidecektir.", transliteration: "Men kane yuridul izzete fe lillahil izzetu cemia, ileyhi yes'adul kelimut tayyibu vel amelus salihu yerfeuh, vellezine yemkurunes seyyiati lehum azabun şedid, ve mekru ulaike huve yebur."},
  {id: 3671, sure_no: 11, ayet_no: 11, sure_adi: "Fatır", text: "وَاللّٰهُ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ جَعَلَكُمْ اَزْوَاجاًۜ وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِه۪ۜ وَمَا يُعَمَّرُ مِنْ مُعَمَّرٍ وَلَا يُنْقَصُ مِنْ عُمُرِه۪ٓ  اِلَّا ف۪ي كِتَابٍۜ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌ", translation: "Allah; sizi topraktan, sonra nutfeden yarattı. Sonra sizi çiftler kıldı. Hiçbir dişi, O'nun bilgisi olmadan ne hamile kalabilir ne de doğurabilir. Bir kimseye ömür verilmesi; ömrünün uzaması veya kısalması, hepsi bir Kitap'tadır. Kuşkusuz bu Allah'a kolaydır.", transliteration: "Vallahu halakakum min turabin summe min nutfetin summe cealekum ezvaca, ve ma tahmilu min unsa ve la tedau illa bi ilmih, ve ma yuammeru min muammerin ve la yunkasu min umurihi illa fi kitab, inne zalike alallahi yesir."},
  {id: 3672, sure_no: 12, ayet_no: 12, sure_adi: "Fatır", text: "وَمَا يَسْتَوِي الْبَحْرَانِۗ هٰذَا عَذْبٌ فُرَاتٌ سَٓائِـغٌ شَرَابُهُ وَهٰذَا مِلْحٌ اُجَاجٌۜ وَمِنْ كُلٍّ تَأْكُلُونَ لَحْماً طَرِياًّ وَتَسْتَخْرِجُونَ حِلْيَةً تَلْبَسُونَهَاۚ وَتَرَى الْفُلْكَ ف۪يهِ مَوَاخِرَ لِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَ", translation: "İki deniz bir değildir. Biri lezzetlidir, tatlıdır. Susuzluğu gideren içimi rahat olandır. Diğeri tuzlu ve acıdır. Her ikisinden de taze et yersiniz. Ve her ikisinden de kullandığınız süs eşyası çıkarırsınız. O'nun lütfundan rızık aramanız için, onu yararak giden gemileri görürsün. Umulur ki şükredersiniz.", transliteration: "Ve ma yestevil bahrani haza azbun furatun saigun şerabuhu ve haza milhun ucac, ve min kullin te'kulune lahmen tariyyen ve testahricune hilyeten telbesuneha, ve terel fulke fihi mevahire li tebtegu min fadlihi ve leallekum teşkurun."},
  {id: 3673, sure_no: 13, ayet_no: 13, sure_adi: "Fatır", text: "يُولِجُ الَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الَّيْلِۙ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۘ كُلٌّ يَجْر۪ي لِاَجَلٍ مُسَمًّىۜ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۜ وَالَّذ۪ينَ تَدْعُونَ مِنْ دُونِه۪ مَا يَمْلِكُونَ مِنْ قِطْم۪يرٍۜ", translation: "O, geceyi gündüze, gündüzü de geceye çevirir. Güneş'i ve Ay'ı buyruk altına aldı. Her biri belirlenmiş bir süreye kadar hareketini sürdürüyor. İşte bu, Allah'tır, Rabb'inizdir. Egemenlik O'nundur. O'nun yanı sıra çağırdığınız kimseler, bir çekirdeğin zarına bile sahip değiller.", transliteration: "Yulicul leyle fin nehari ve yulicun nehare fil leyli ve sehhareş şemse vel kamere kullun yecri li ecelin musemma, zalikumullahu rabbukum lehul mulk, vellezine ted'une min dunihi ma yemlikune min kıtmir."},
  {id: 3674, sure_no: 14, ayet_no: 14, sure_adi: "Fatır", text: "اِنْ تَدْعُوهُمْ لَا يَسْمَعُوا دُعَٓاءَكُمْۚ وَلَوْ سَمِعُوا مَا اسْتَجَابُوا لَكُمْۜ وَيَوْمَ الْقِيٰمَةِ يَكْفُرُونَ بِشِرْكِكُمْۜ وَلَا يُنَبِّئُكَ مِثْلُ خَب۪يرٍ۟", translation: "Eğer onları çağırırsanız, çağrınızı işitmezler. İşitmiş olsalar bile size cevap veremezler. Kıyamet günü, kendilerine yakıştırdığınız şirki reddederler. Sana, Her Şeyden Haberdar Olan'ın, haber verdiği gibi hiç kimse haber veremez.", transliteration: "İn ted'uhum la yesmeu duaekum, ve lev semiu mestecabu lekum, ve yevmel kıyameti yekfurune bi şirkikum, ve la yunebbiuke mislu habir."},
  {id: 3675, sure_no: 15, ayet_no: 15, sure_adi: "Fatır", text: "يَٓا اَيُّهَا النَّاسُ اَنْتُمُ الْفُقَـرَٓاءُ اِلَى اللّٰهِۚ وَاللّٰهُ هُوَ الْغَنِيُّ الْحَم۪يدُ", translation: "Ey insanlar! Allah'a muhtaç olanlar sizlersiniz. Allah ise hiçbir şeye muhtaç değildir. Övgüye layık olandır.", transliteration: "Ya eyyuhen nasu entumul fukarau ilallahi, vallahu huvel ganiyyul hamid."},
  {id: 3676, sure_no: 16, ayet_no: 16, sure_adi: "Fatır", text: "اِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَد۪يدٍۚ", translation: "Eğer dilerse sizi yok eder ve yeni bir halk getirir.", transliteration: "İn yeşe' yuzhibkum ve ye'ti bi halkın cedid."},
  {id: 3677, sure_no: 17, ayet_no: 17, sure_adi: "Fatır", text: "وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَز۪يزٍ", translation: "Bu, Allah için asla güç bir şey değildir.", transliteration: "Ve ma zalike alallahi bi aziz."},
  {id: 3678, sure_no: 18, ayet_no: 18, sure_adi: "Fatır", text: "وَلَا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۜ وَاِنْ تَدْعُ مُثْقَلَةٌ اِلٰى حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَلَوْ كَانَ ذَا قُرْبٰىۜ اِنَّمَا تُنْذِرُ الَّذ۪ينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَاَقَامُوا الصَّلٰوةَۜ وَمَنْ تَزَكّٰى فَاِنَّمَا يَتَزَكّٰى لِنَفْسِه۪ۜ وَاِلَى اللّٰهِ الْمَص۪يرُ", translation: "Yük taşıyan birisi, bir başkasının yükünü taşımaz. Yükü ağır olan kimse, bir başkasını yardıma çağırsa, çağırdığı yakını da olsa ona yardım etmez. Sen, ancak görmedikleri halde Rabb'ine içtenlikle saygı duyan ve salatı ikame edenleri uyarırsın. Her kim arınırsa kendisi için arınmış olur. Dönüş Allah'adır.", transliteration: "Ve la teziru vaziretun vizre uhra, ve in ted'u muskaletun ila himliha la yuhmel minhu şey'un ve lev kane za kurba, innema tunzirullezine yahşevne rabbehum bil gaybi ve ekamus salah, ve men tezekka fe innema yetezekka li nefsih, ve ilallahil masir."},
  {id: 3679, sure_no: 19, ayet_no: 19, sure_adi: "Fatır", text: "وَمَا يَسْتَوِي الْاَعْمٰى وَالْبَص۪يرُۙ", translation: "Körle gören bir değildir.", transliteration: "Ve ma yestevil a'ma vel basir."},
  {id: 3680, sure_no: 20, ayet_no: 20, sure_adi: "Fatır", text: "وَلَا الظُّلُمَاتُ وَلَا النُّورُۙ", translation: "Karanlık ve aydınlık bir değildir.", transliteration: "Ve lez zulumatu ve len nur."},
  {id: 3681, sure_no: 21, ayet_no: 21, sure_adi: "Fatır", text: "وَلَا الظِّلُّ وَلَا الْحَرُورُۚ", translation: "Serinlik ve sıcaklık bir değildir.", transliteration: "Ve lez zıllu ve lel harur."},
  {id: 3682, sure_no: 22, ayet_no: 22, sure_adi: "Fatır", text: "وَمَا يَسْتَوِي الْاَحْيَٓاءُ وَلَا الْاَمْوَاتُۜ اِنَّ اللّٰهَ يُسْمِــعُ مَنْ يَشَٓاءُۚ وَمَٓا اَنْتَ بِمُسْمِــعٍ مَنْ فِي الْقُبُورِ", translation: "Dirilerle ölüler bir değildir. Kuşkusuz ki Allah, hak edene duyurur. Sen mezarlarda olanlara asla duyuramazsın.", transliteration: "Ve ma yestevil ahyau ve lel emvat, innallahe yusmiu men yeşau, ve ma ente bi musmiin men fil kubur."},
  {id: 3683, sure_no: 23, ayet_no: 23, sure_adi: "Fatır", text: "اِنْ اَنْتَ اِلَّا نَذ۪يرٌ", translation: "Sen yalnızca bir uyarıcısın.", transliteration: "İn ente illa nezir."},
  {id: 3684, sure_no: 24, ayet_no: 24, sure_adi: "Fatır", text: "اِنَّٓا اَرْسَلْنَاكَ بِالْحَقِّ بَش۪يراً وَنَذ۪يراًۜ وَاِنْ مِنْ اُمَّةٍ اِلَّا خَلَا ف۪يهَا نَذ۪يرٌ", translation: "Kuşkusuz Biz seni, gerçek ile bir haber verici ve uyarıcı olarak gönderdik. Her ümmet içinde bir uyarıcı geçmiştir.", transliteration: "İnna erselnake bil hakkı beşiren ve nezira, ve in min ummetin illa hala fiha nezir."},
  {id: 3685, sure_no: 25, ayet_no: 25, sure_adi: "Fatır", text: "وَاِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْۚ جَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَبِالزُّبُرِ وَبِالْكِتَابِ الْمُن۪يرِ", translation: "Eğer seni yalanlıyorlarsa, bil ki onlardan öncekiler de yalanlamışlardı. Onların Resulleri, onlara kanıt içeren bilgiyle, sahifelerle ve aydınlatıcı kitaplarla gelmişlerdi.", transliteration: "Ve in yukezzibuke fe kad kezzebellezine min kablihim, caethum rusuluhum bil beyyinati ve biz zuburi ve bil kitabil munir."},
  {id: 3686, sure_no: 26, ayet_no: 26, sure_adi: "Fatır", text: "ثُمَّ اَخَذْتُ الَّذ۪ينَ كَفَرُوا فَكَيْفَ كَانَ نَك۪يرِ۟", translation: "Sonra Kafirleri yakaladım. Benim inkarım nasıl oldu?", transliteration: "Summe ehaztullezine keferu fe keyfe kane nekir."},
  {id: 3687, sure_no: 27, ayet_no: 27, sure_adi: "Fatır", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءًۚ فَاَخْرَجْنَا بِه۪ ثَمَرَاتٍ مُخْتَلِفاً اَلْوَانُهَاۜ وَمِنَ الْجِبَالِ جُدَدٌ ب۪يضٌ وَحُمْرٌ مُخْتَلِفٌ اَلْوَانُهَا وَغَرَاب۪يبُ سُودٌ", translation: "Allah'ın gökten su indirdiğini görmüyor musun? Onunla rengarenk ürünler çıkardık. Ve dağlarda da beyazlı, kırmızılı ve çeşitli renklerde, siyah ve simsiyah katmanlar oluşturduk.", transliteration: "E lem tere ennallahe enzele mines semai maen, fe ahrecna bihi semeratin muhtelifen elvanuha, ve minel cibali cudedun bidun ve humrun muhtelifun elvanuha ve garabibu sud."},
  {id: 3688, sure_no: 28, ayet_no: 28, sure_adi: "Fatır", text: "وَمِنَ النَّاسِ وَالدَّوَٓابِّ وَالْاَنْعَامِ مُخْتَلِفٌ اَلْوَانُهُ كَذٰلِكَۜ اِنَّمَا يَخْشَى اللّٰهَ مِنْ عِبَادِهِ الْعُلَمٰٓؤُ۬اۜ اِنَّ اللّٰهَ عَز۪يزٌ غَفُورٌ", translation: "İnsanlardan, hayvanlardan ve diğer canlılardan da çeşitli renkte olanları vardır. Allah'ın kullarından yalnız \"ilim sahibi olanlar\" haşyet ederler. Kuşkusuz ki Allah, Mutlak Üstün Olan'dır, Çok Bağışlayıcı'dır.", transliteration: "Ve minen nasi ved devabbi vel en'ami muhtelifun elvanuhu kezalik, innema yahşallahe min ibadihil ulemau, innallahe azizun gafur."},
  {id: 3689, sure_no: 29, ayet_no: 29, sure_adi: "Fatır", text: "اِنَّ الَّذ۪ينَ يَتْلُونَ كِتَابَ اللّٰهِ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِراًّ وَعَلَانِيَةً يَرْجُونَ تِجَارَةً لَنْ تَبُورَۙ", translation: "Kuşkusuz Allah'ın Kitap'ını okuyanlar, salatı ikame edenler ve rızıklandırdığımız şeylerden gizli ve açık infak edenler asla kesilmeyecek bir kazanç umabilirler.", transliteration: "İnnellezine yetlune kitaballahi ve ekamus salate ve enfeku mimma rezaknahum sirren ve alaniyeten yercune ticareten len tebur."},
  {id: 3690, sure_no: 30, ayet_no: 30, sure_adi: "Fatır", text: "لِيُوَفِّيَهُمْ اُجُورَهُمْ وَيَز۪يدَهُمْ مِنْ فَضْلِه۪ۜ اِنَّهُ غَفُورٌ شَكُورٌ", translation: "Onlara, yaptıklarının karşılığını lütfundan arttırarak fazlasıyla verir. Kuşkusuz ki O, Çok Bağışlayıcı'dır, Yapılan Şeyin Karşılığını Veren'dir.", transliteration: "Li yuveffiyehum ucurehum ve yezidehum min fadlih, innehu gafurun şekur."},
  {id: 3691, sure_no: 31, ayet_no: 31, sure_adi: "Fatır", text: "وَالَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ مِنَ الْكِتَابِ هُوَ الْحَقُّ مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِۜ اِنَّ اللّٰهَ بِعِبَادِه۪ لَخَب۪يرٌ بَص۪يرٌ", translation: "Sana Kitap'tan vahyettiğimiz, kendisinden öncekileri tasdik edici olarak gerçektir. Kuşkusuz Allah, Kullarından Haberdar Olan'dır, Her Şeyi Gören'dir.", transliteration: "Vellezi evhayna ileyke minel kitabi huvel hakku musaddikan lima beyne yedeyh, innallahe bi ibadihi le habirun basir."},
  {id: 3692, sure_no: 32, ayet_no: 32, sure_adi: "Fatır", text: "ثُمَّ اَوْرَثْنَا الْكِتَابَ الَّذ۪ينَ اصْطَفَيْنَا مِنْ عِبَادِنَاۚ فَمِنْهُمْ ظَالِمٌ لِنَفْسِه۪ۚ وَمِنْهُمْ مُقْتَصِدٌۚ وَمِنْهُمْ سَابِقٌ بِالْخَيْرَاتِ بِاِذْنِ اللّٰهِۜ ذٰلِكَ هُوَ الْفَضْلُ الْكَب۪يرُۜ", translation: "Sonra kullarımızdan seçtiğimiz kimselere Kitap'ı miras bıraktık. Onlardan bir kısmı kendilerine zulmederler, onlardan bir kısmı ortalama bir yol tutarlar, onlardan bir kısmı da Allah'ın izniyle hayırlarda önde giderler. İşte büyük fazilet budur.", transliteration: "Summe evresnel kitabellezinastafeyna min ibadina, fe minhum zalimun li nefsih, ve minhum muktesid, ve minhum sabikun bil hayrati bi iznillah, zalike huvel fadlul kebir."},
  {id: 3693, sure_no: 33, ayet_no: 33, sure_adi: "Fatır", text: "جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا يُحَلَّوْنَ ف۪يهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤً۬اۚ وَلِبَاسُهُمْ ف۪يهَا حَر۪يرٌ", translation: "Onların girecekleri yer Adn Cennetleridir. Orada, altından bilezik ve incilerle süslenecekler. Giysileri ise ipektendir.", transliteration: "Cennatu adnin yedhuluneha yuhallevne fiha min esavire min zehebin ve lu'lua, ve libasuhum fiha harir."},
  {id: 3694, sure_no: 34, ayet_no: 34, sure_adi: "Fatır", text: "وَقَالُوا الْحَمْدُ لِلّٰهِ الَّـذ۪ٓي اَذْهَبَ عَنَّا الْحَزَنَۜ اِنَّ رَبَّـنَا لَغَفُورٌ شَكُورٌۙ", translation: "\"Bizden hüznü gideren Allah'a hamdolsun. Rabb'imiz gerçekten Çok Bağışlayıcı'dır, Yapılan Şeyin Karşılığını Veren'dir.\" derler.", transliteration: "Ve kalul hamdu lillahillezi ezhebe annel hazen, inne rabbena le gafurun şekur."},
  {id: 3695, sure_no: 35, ayet_no: 35, sure_adi: "Fatır", text: "اَلَّـذ۪ٓي اَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِه۪ۚ لَا يَمَسُّنَا ف۪يهَا نَصَبٌ وَلَا يَمَسُّنَا ف۪يهَا لُغُوبٌ", translation: "\"O ki, bizi lütfundan kalınacak bir yurda yerleştirdi. Orada bize bir yorgunluk dokunmayacak ve orada bize bir usanç gelmeyecek.\"", transliteration: "Ellezi ehallena darel mukameti min fadlih, la yemessuna fiha nasabun ve la yemessuna fiha lugub."},
  {id: 3696, sure_no: 36, ayet_no: 36, sure_adi: "Fatır", text: "وَالَّذ۪ينَ كَفَرُوا لَهُمْ نَارُ جَهَنَّمَۚ لَا يُقْضٰى عَلَيْهِمْ فَيَمُوتُوا وَلَا يُخَفَّفُ عَنْهُمْ مِنْ عَذَابِهَاۜ  كَذٰلِكَ نَجْز۪ي كُلَّ كَفُورٍۚ", translation: "Kafirlere gelince, onlar için Cehennem ateşi vardır. Ölmelerine karar verilmez ki ölüp kurtulsunlar. Onların azaplarından da hafifletilmez. İşte bütün Kafirleri böyle cezalandırırız.", transliteration: "Vellezine keferu lehum naru cehennem, la yukda aleyhim fe yemutu ve la yuhaffefu anhum min azabiha, kezalike neczi kulle kefur."},
  {id: 3697, sure_no: 37, ayet_no: 37, sure_adi: "Fatır", text: "وَهُمْ يَصْطَرِخُونَ ف۪يهَاۚ رَبَّنَٓا اَخْرِجْنَا نَعْمَلْ صَالِحاً غَيْرَ الَّذ۪ي كُنَّا نَعْمَلُۜ اَوَلَمْ نُعَمِّرْكُمْ مَا يَتَذَكَّرُ ف۪يهِ مَنْ تَذَكَّرَ وَجَٓاءَكُمُ النَّذ۪يرُۜ فَذُوقُوا فَمَا لِلظَّالِم۪ينَ مِنْ نَص۪يرٍ۟", translation: "Onlar, orada yardım için bağırıp çağırırlar: \"Rabb'imiz! Bizi çıkar, daha önce yaptığımızdan başka, düzgün amel yapalım.\" Size dünyada öğüt dinleyecek kimsenin, öğüt dinlemesine yetecek kadar bir ömür vermedik mi? Size uyarıcı gelmedi mi? O halde tadın! Artık zalimler için bir yardımcı yoktur.", transliteration: "Ve hum yastarihune fiha, rabbena ahricna na'mel salihan gayrellezi kunna na'mel, e ve lem nuammirkum ma yetezekkeru fihi men tezekkere ve caekumun nezir, fe zuku fe ma liz zalimine min nasir."},
  {id: 3698, sure_no: 38, ayet_no: 38, sure_adi: "Fatır", text: "اِنَّ اللّٰهَ عَالِمُ غَيْبِ السَّمٰوَاتِ وَالْاَرْضِۜ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Kuşkusuz Allah, göklerin ve yerin gaybını bilendir. Kuşkusuz O, göğüslerde olanı en iyi bilendir.", transliteration: "İnnallahe alimu gaybis semavati vel ard, innehu alimun bi zatis sudur."},
  {id: 3699, sure_no: 39, ayet_no: 39, sure_adi: "Fatır", text: "هُوَ الَّذ۪ي جَعَلَكُمْ خَلَٓائِفَ فِي الْاَرْضِۜ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُۜ وَلَا يَز۪يدُ الْكَافِر۪ينَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ اِلَّا مَقْتاًۚ وَلَا يَز۪يدُ الْكَافِر۪ينَ كُفْرُهُمْ اِلَّا خَسَاراً", translation: "Sizi yeryüzünde halifeler yapan O'dur. Kim gerçeğe kafirlik ederse, küfretmesi kendi zararınadır. Kafirlerin küfürleri, kendileri için Rabb'lerinin yanında gazabı artırmaktan başka bir şeye yaramaz. Kafirlerin, küfrü sadece kendilerinin zararını artırır.", transliteration: "Huvellezi cealekum halaife fil ard, fe men kefere fe aleyhi kufruh, ve la yezidul kafirine kufruhum inde rabbihim illa makta, ve la yezidul kafirine kufruhum illa hasara."},
  {id: 3700, sure_no: 40, ayet_no: 40, sure_adi: "Fatır", text: "قُلْ اَرَاَيْتُمْ شُرَكَٓاءَكُمُ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِۜ اَرُون۪ي مَاذَا خَلَقُوا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِي السَّمٰوَاتِۚ اَمْ اٰتَيْنَاهُمْ كِتَاباً فَهُمْ عَلٰى بَيِّنَتٍ مِنْهُۚ بَلْ اِنْ يَعِدُ الظَّالِمُونَ بَعْضُهُمْ بَعْضاً اِلَّا غُرُوراً", translation: "De ki: \"Allah'ın yanı sıra yakardıklarınıza bir bakın bakalım! Onların, yeryüzünde yarattıkları bir şey varsa bana gösterin! Yoksa onların göklerde bir ortakları mı var? Ya da onlara bir kitap verdik de onlar, ondan bir beyyine üzerinde midirler? Hayır, hayır! Zalimlerin birbirlerine verdikleri sözler, aldatmacadan başka bir şey değildir.", transliteration: "Kul ereeytum şurekaekumullezine ted'une min dunillah, eruni maza halaku minel ardı em lehum şirkun fis semavat, em ateynahum kitaben fe hum ala beyyinetin minh, bel in yaıduz zalimune ba'duhum ba'dan illa gurura."},
  {id: 3701, sure_no: 41, ayet_no: 41, sure_adi: "Fatır", text: "اِنَّ اللّٰهَ يُمْسِكُ السَّمٰوَاتِ وَالْاَرْضَ اَنْ تَزُولَاۚ وَلَئِنْ زَالَتَٓا اِنْ اَمْسَكَهُمَا مِنْ اَحَدٍ مِنْ بَعْدِه۪ۜ اِنَّهُ  كَانَ حَل۪يماً غَفُوراً", translation: "Gökleri ve yeri koyduğu yasalarla yok olmaktan koruyan Allah'tır. Ant olsun ki eğer onlar yok olurlarsa, o ikisini O'ndan başka tutacak yoktur. Kuşkusuz O, Çok Şefkatli'dir. Çok Bağışlayıcı'dır.", transliteration: "İnnallahe yumsikus semavati vel arda en tezula, ve le in zaleta in emsekehuma min ehadin min ba'dih, innehu kane halimen gafura."},
  {id: 3702, sure_no: 42, ayet_no: 42, sure_adi: "Fatır", text: "وَاَقْسَمُوا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَئِنْ جَٓاءَهُمْ نَذ۪يرٌ لَيَكُونُنَّ اَهْدٰى مِنْ اِحْدَى الْاُمَمِۚ فَلَمَّا جَٓاءَهُمْ نَذ۪يرٌ مَا زَادَهُمْ اِلَّا نُفُوراًۙ", translation: "Eğer kendilerine bir uyarıcı gelirse, diğer toplumlardan kesinlikle daha doğru yolda olacaklarına dair var güçleriyle Allah'a yeminler etmişlerdi. Fakat onlara uyarıcı geldiğinde bu onların yalnızca nefretlerini artırdı.", transliteration: "Ve aksemu billahi cehde eymanihim le in caehum nezirun le yekununne ehda min ihdel umem, fe lemma caehum nezirun ma zadehum illa nufura."},
  {id: 3703, sure_no: 43, ayet_no: 43, sure_adi: "Fatır", text: "اِسْتِكْبَاراً فِي الْاَرْضِ وَمَكْرَ السَّيِّئِۜ وَلَا يَح۪يقُ الْمَكْرُ السَّيِّئُ اِلَّا بِاَهْلِه۪ۜ فَهَلْ يَنْظُرُونَ اِلَّا سُنَّتَ الْاَوَّل۪ينَۚ فَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَبْد۪يلاًۚ وَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَحْو۪يلاً", translation: "Yeryüzünde büyüklendiler ve kötü planlar yaptılar. Oysa kötü planlar, sahibinden başkasını kuşatmaz. Öyleyse onlar, öncekilerin sünnetinden başkasını mı gözlüyorlar? Halbuki Allah'ın sünnetinde asla bir değişiklik bulamazsın. Allah'ın sünnetinde asla bir sapma bulamazsın.", transliteration: "İstikbaren fil ardı ve mekres seyyii, ve la yahikul mekrus seyyiu illa bi ehlih, fe hel yenzurune illa sunnetel evvelin, fe len tecide li sunnetillahi tebdila, ve len tecide li sunnetillahi tahvila."},
  {id: 3704, sure_no: 44, ayet_no: 44, sure_adi: "Fatır", text: "اَوَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْ وَكَانُٓوا اَشَدَّ مِنْهُمْ قُوَّةًۜ وَمَا كَانَ اللّٰهُ لِيُعْجِزَهُ مِنْ شَيْءٍ فِي السَّمٰوَاتِ وَلَا فِي الْاَرْضِۜ اِنَّهُ كَانَ عَل۪يماً قَد۪يراً", translation: "Yeryüzünde dolaşıp, kendilerinden öncekilerinin sonları nasıl olmuş, bir bakmadılar mı? Oysaki onlar, güç olarak kendilerinden daha güçlüydüler. Göklerde ve yerde Allah'ı aciz bırakacak hiçbir şey yoktur. O, Her Şeyi Bilen'dir, Her Şeye Gücü Yeten'dir.", transliteration: "E ve lem yesiru fil ardı fe yenzuru keyfe kane akıbetullezine min kablihim ve kanu eşedde minhum kuvveh, ve ma kanallahu li yu'cizehu min şey'in fis semavati ve la fil ard, innehu kane alimen kadira."},
  {id: 3705, sure_no: 45, ayet_no: 45, sure_adi: "Fatır", text: "وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِمَا كَسَبُوا مَا تَرَكَ عَلٰى ظَهْرِهَا مِنْ دَٓابَّةٍ وَلٰكِنْ يُؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُسَمًّىۚ فَاِذَا جَٓاءَ اَجَلُهُمْ فَاِنَّ اللّٰهَ كَانَ بِعِبَادِه۪ بَص۪يراً", translation: "Eğer Allah, insanların yaptıklarının hesabını hemen görecek olsaydı, yeryüzünde hiçbir canlı bırakmazdı. Fakat O, onlara belli bir zamana kadar süre vermektedir. Belirlenen süre dolduğu zaman da kuşkusuz Allah kendi kullarını en iyi görendir.", transliteration: "Ve lev yuahızullahun nase bima kesebu ma tereke ala zahriha min dabbetin, ve lakin yuahhıruhum ila ecelin musemma, fe iza cae eceluhum fe innallahe kane bi ibadihi basira."},
  {id: 3706, sure_no: 1, ayet_no: 1, sure_adi: "Yasin", text: "يٰسٓۜ", translation: "Ya-Sin.", transliteration: "Ya sin."},
  {id: 3707, sure_no: 2, ayet_no: 2, sure_adi: "Yasin", text: "وَالْقُرْاٰنِ الْحَك۪يمِۙ", translation: "Hakim Kur'an'a ant olsun.", transliteration: "Vel kur'anil hakim."},
  {id: 3708, sure_no: 3, ayet_no: 3, sure_adi: "Yasin", text: "اِنَّكَ لَمِنَ الْمُرْسَل۪ينَۙ", translation: "Kuşkusuz sen, gönderilmiş elçilerdensin.", transliteration: "İnneke leminel murselin."},
  {id: 3709, sure_no: 4, ayet_no: 4, sure_adi: "Yasin", text: "عَلٰى صِرَاطٍ مُسْتَق۪يمٍۜ", translation: "Dosdoğru bir yoldasın;", transliteration: "Ala sıratın mustekim."},
  {id: 3710, sure_no: 5, ayet_no: 5, sure_adi: "Yasin", text: "تَنْز۪يلَ الْعَز۪يزِ الرَّح۪يمِۙ", translation: "Bu Kur'an, Mutlak Üstün ve Rahmeti Kesintisiz olan tarafından,", transliteration: "Tenzilel azizir rahim."},
  {id: 3711, sure_no: 6, ayet_no: 6, sure_adi: "Yasin", text: "لِتُنْذِرَ قَوْماً مَٓا اُنْذِرَ اٰبَٓاؤُ۬هُمْ فَهُمْ غَافِلُونَ", translation: "Ataları uyarıldığı halde yine de gaflet içinde olan bir halkı uyarman için indirilmiştir.", transliteration: "Li tunzire kavmen ma unzire abauhum fe hum gafilun."},
  {id: 3712, sure_no: 7, ayet_no: 7, sure_adi: "Yasin", text: "لَقَدْ حَقَّ الْقَوْلُ عَلٰٓى اَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُونَ", translation: "Ant olsun ki, onların çoğu üzerine söz hakk oldu. Artık onlar iman etmezler.", transliteration: "Lekad hakkal kavlu ala ekserihim fe hum la yu'minun."},
  {id: 3713, sure_no: 8, ayet_no: 8, sure_adi: "Yasin", text: "اِنَّا جَعَلْنَا ف۪ٓي اَعْنَاقِهِمْ اَغْلَالاً فَهِيَ اِلَى الْاَذْقَانِ فَهُمْ مُقْمَحُونَ", translation: "Biz, onların boyunlarına, çenelerine kadar dayanan demir halkalar geçirdik. Bu nedenle başları sürekli yukarıda kalkık olanlardır.", transliteration: "İnna cealna fi a'nakıhim aglalen fe hiye ilel ezkani fe hum mukmehun."},
  {id: 3714, sure_no: 9, ayet_no: 9, sure_adi: "Yasin", text: "وَجَعَلْنَا مِنْ بَيْنِ اَيْد۪يهِمْ سَداًّ وَمِنْ خَلْفِهِمْ سَداًّ فَاَغْشَيْنَاهُمْ فَهُمْ لَا يُبْصِرُونَ", translation: "Önlerine ve arkalarına birer set çektik. Böylece onları perdeledik. Artık gerçeği görmezler.", transliteration: "Ve cealna min beyni eydihim sedden ve min halfihim sedden fe agşeynahum fe hum la yubsırun."},
  {id: 3715, sure_no: 10, ayet_no: 10, sure_adi: "Yasin", text: "وَسَوَٓاءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُونَ", translation: "Uyarsan da uyarmasan da onlar için birdir. Onlar iman etmezler.", transliteration: "Ve sevaun aleyhim e enzertehum em lem tunzirhum la yu'minun."},
  {id: 3716, sure_no: 11, ayet_no: 11, sure_adi: "Yasin", text: "اِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمٰنَ بِالْغَيْبِۚ فَبَشِّرْهُ بِمَغْفِرَةٍ وَاَجْرٍ كَر۪يمٍ", translation: "Sen ancak Zikir'e uyan ve görmediği halde Rahman'a haşyet duyan kimseyi uyarabilirsin. İşte böylesine bağışlanma ve çok şerefli bir ödülü haber ver.", transliteration: "İnnema tunziru menittebeaz zikre ve haşiyer rahmane bil gayb, fe beşşirhu bi magfiretin ve ecrin kerim."},
  {id: 3717, sure_no: 12, ayet_no: 12, sure_adi: "Yasin", text: "اِنَّا نَحْنُ نُحْـيِ الْمَوْتٰى وَنَكْتُبُ مَا قَدَّمُوا وَاٰثَارَهُمْۜ وَكُلَّ شَيْءٍ اَحْصَيْنَاهُ ف۪ٓي اِمَامٍ مُب۪ينٍ۟", translation: "Kuşkusuz ölüleri Biz diriltiriz Biz. Önceden yapıp gönderdiklerini ve geride bıraktıklarını yazarız. Biz her şeyi bir \"imam-ı mubin\"de kayıt altına almışızdır.", transliteration: "İnna nahnu nuhyil mevta ve nektubu ma kaddemu ve asarehum ve kulle şey'in ahsaynahu fi imamin mubin."},
  {id: 3718, sure_no: 13, ayet_no: 13, sure_adi: "Yasin", text: "وَاضْرِبْ لَهُمْ مَثَلاً اَصْحَابَ الْقَرْيَةِۢ اِذْ جَٓاءَهَا الْمُرْسَلُونَۚ", translation: "Onlara, o kentin halkını örnek ver. Hani, onlara Resuller gelmişti.", transliteration: "Vadrıb lehum meselen ashabel karyeh, iz cae hel murselun."},
  {id: 3719, sure_no: 14, ayet_no: 14, sure_adi: "Yasin", text: "اِذْ اَرْسَلْـنَٓا اِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُٓوا اِنَّٓا اِلَيْكُمْ مُرْسَلُونَ", translation: "Onlara iki Resul göndermiştik. Fakat ikisini de yalanladılar. Bunun üzerine üçüncü ile destekledik. Dediler ki: \"Biz, size gönderilmiş Resulleriz.\"", transliteration: "İz erselna ileyhimusneyni fe kezzebuhuma fe azzezna bi salisin fe kalu inna ileykum murselun."},
  {id: 3720, sure_no: 15, ayet_no: 15, sure_adi: "Yasin", text: "قَالُوا مَٓا اَنْتُمْ اِلَّا بَشَرٌ مِثْلُنَاۙ وَمَٓا اَنْزَلَ الرَّحْمٰنُ مِنْ شَيْءٍۙ اِنْ اَنْتُمْ اِلَّا تَكْذِبُونَ", translation: "Onlar: \"Siz de bizim gibi birer beşersiniz. Rahman herhangi bir şey indirmedi. Siz sadece yalan söylüyorsunuz.\" dediler.", transliteration: "Kalu ma entum illa beşerun misluna ve ma enzeler rahmanu min şey'in in entum illa tekzibun."},
  {id: 3721, sure_no: 16, ayet_no: 16, sure_adi: "Yasin", text: "قَالُوا رَبُّنَا يَعْلَمُ اِنَّٓا اِلَيْكُمْ لَمُرْسَلُونَ", translation: "\"Rabb'imiz biliyor ki, biz gerçekten size gönderilmiş Resulleriz.\" dediler.", transliteration: "Kalu rabbuna ya'lemu inna ileykum le murselun."},
  {id: 3722, sure_no: 17, ayet_no: 17, sure_adi: "Yasin", text: "وَمَا عَلَيْنَٓا اِلَّا الْبَلَاغُ الْمُب۪ينُ", translation: "\"Bize düşen yalnızca açıkça iletmektir.\"", transliteration: "Ve ma aleyna illel belagul mubin."},
  {id: 3723, sure_no: 18, ayet_no: 18, sure_adi: "Yasin", text: "قَالُٓوا اِنَّا تَطَيَّرْنَا بِكُمْۚ لَئِنْ لَمْ تَنْتَهُوا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُمْ مِنَّا عَذَابٌ اَل۪يمٌ", translation: "\"Siz bize kesinlikle uğursuzluk getirdiniz. Eğer vazgeçmezseniz, kesinlikle sizi taşlarız. Ve bizden size çok acıklı bir azap dokunur.\" dediler.", transliteration: "Kalu inna tetayyerna bi kum, le in lem tentehu le nercumennekum ve le yemessennekum minna azabun elim."},
  {id: 3724, sure_no: 19, ayet_no: 19, sure_adi: "Yasin", text: "قَالُوا طَٓائِرُكُمْ مَعَكُمْۜ اَئِنْ ذُكِّرْتُمْۜ بَلْ اَنْتُمْ قَوْمٌ مُسْرِفُونَ", translation: "\"Uğursuzluğunuz kendinizdendir. Size öğüt verildi diye mi? Hayır! Siz müsrif bir halksınız.\" dediler.", transliteration: "Kalu tairikum meakum, e in zukkirtum, bel entum kavmun musrifun."},
  {id: 3725, sure_no: 20, ayet_no: 20, sure_adi: "Yasin", text: "وَجَٓاءَ مِنْ اَقْصَا الْمَد۪ينَةِ رَجُلٌ يَسْعٰى قَالَ يَا قَوْمِ اتَّبِعُوا الْمُرْسَل۪ينَۙ", translation: "Şehrin en uzak yerinden bir adam koşarak geldi: \"Ey halkım, gönderilmiş olan Resullere uyun!\" dedi.", transliteration: "Ve cae min aksal medineti raculun yes'a kale ya kavmittebiul murselin."},
  {id: 3726, sure_no: 21, ayet_no: 21, sure_adi: "Yasin", text: "اِتَّبِعُوا مَنْ لَا يَسْـَٔلُكُمْ اَجْراً وَهُمْ مُهْتَدُونَ", translation: "\"Sizden herhangi bir karşılık beklemeyen bu kimselere uyun. Onlar doğru yolda olanlardır.\"", transliteration: "İttebiu men la yes'elukum ecren ve hum muhtedun."},
  {id: 3727, sure_no: 22, ayet_no: 22, sure_adi: "Yasin", text: "وَمَا لِيَ لَٓا اَعْبُدُ الَّذ۪ي فَطَرَن۪ي وَاِلَيْهِ تُرْجَعُونَ", translation: "\"Ben, niçin benim fıtratımı belirleyene kulluk etmeyeyim? Siz de O'na döndürüleceksiniz.\"", transliteration: "Ve ma liye la a'budullezi fatarani ve ileyhi turceun."},
  {id: 3728, sure_no: 23, ayet_no: 23, sure_adi: "Yasin", text: "ءَاَتَّخِذُ مِنْ دُونِه۪ٓ اٰلِهَةً اِنْ يُرِدْنِ الرَّحْمٰنُ بِضُرٍّ لَا تُغْنِ عَنّ۪ي شَفَاعَتُهُمْ شَيْـٔاً وَلَا يُنْقِذُونِۚ", translation: "\"Ben, O'nun yanı sıra ilahlar edinir miyim? Eğer Rahman, bana bir zarar dilerse, onların şefaatinin bana hiçbir yararı olmaz. Onlar beni kurtaramazlar.\"", transliteration: "E ettehızu min dunihi aliheten in yuridnir rahmanu bi durrin la tugni anni şefaatuhum şey'en ve la yunkızun."},
  {id: 3729, sure_no: 24, ayet_no: 24, sure_adi: "Yasin", text: "اِنّ۪ٓي اِذاً لَف۪ي ضَلَالٍ مُب۪ينٍ", translation: "\"Öyle olsaydı ben, kesinlikle apaçık bir sapkınlık içinde olurdum.\"", transliteration: "İnni izen le fi dalalin mubin."},
  {id: 3730, sure_no: 25, ayet_no: 25, sure_adi: "Yasin", text: "اِنّ۪ٓي اٰمَنْتُ بِرَبِّكُمْ فَاسْمَعُونِۜ", translation: "\"Ben, sizin de Rabb'iniz olana inandım. Beni dinleyin!\"", transliteration: "İnni amentu bi rabbikum fesmeun."},
  {id: 3731, sure_no: 26, ayet_no: 26, sure_adi: "Yasin", text: "ق۪يلَ ادْخُلِ الْجَنَّةَۜ قَالَ يَا لَيْتَ قَوْم۪ي يَعْلَمُونَۙ", translation: "Ona, \"Cennete gir!\" denildi. \"Keşke halkım bilseydi;\"", transliteration: "Kiled hulil cenneh, kale ya leyte kavmi ya'lemun."},
  {id: 3732, sure_no: 27, ayet_no: 27, sure_adi: "Yasin", text: "بِمَا غَفَرَ ل۪ي رَبّ۪ي وَجَعَلَن۪ي مِنَ الْمُكْرَم۪ينَ", translation: "\"Rabb'imin beni bağışladığını ve ikram edilenlerden yaptığını.\"", transliteration: "Bima gafere li rabbi ve cealeni minel mukremin."},
  {id: 3733, sure_no: 28, ayet_no: 28, sure_adi: "Yasin", text: "وَمَٓا اَنْزَلْنَا عَلٰى قَوْمِه۪ مِنْ بَعْدِه۪ مِنْ جُنْدٍ مِنَ السَّمَٓاءِ وَمَا كُنَّا مُنْزِل۪ينَ", translation: "Ondan sonra, halkının üzerine gökten hiçbir ordu indirmedik, indirecek de değildik!", transliteration: "Ve ma enzelna ala kavmihi min ba'dihi min cundin mines semai ve ma kunna munzilin."},
  {id: 3734, sure_no: 29, ayet_no: 29, sure_adi: "Yasin", text: "اِنْ كَانَتْ اِلَّا صَيْحَةً وَاحِدَةً فَاِذَا هُمْ خَامِدُونَ", translation: "Sadece bir sayha! O zaman onlar sönenler oldular!", transliteration: "İn kanet illa sayhaten vahıdetenfe iza hum hamidun."},
  {id: 3735, sure_no: 30, ayet_no: 30, sure_adi: "Yasin", text: "يَا حَسْرَةً عَلَى الْعِبَادِۚ مَا يَأْت۪يهِمْ مِنْ رَسُولٍ اِلَّا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Yazıklar olsun o kullara! Kendilerine gelen her Resul'le alay ettiler.", transliteration: "Ya hasreten alel ıbad, ma ye'tihim min resulin illa kanu bihi yestehziun."},
  {id: 3736, sure_no: 31, ayet_no: 31, sure_adi: "Yasin", text: "اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ اَنَّهُمْ اِلَيْهِمْ لَا يَرْجِعُونَ", translation: "Kendilerinden önce nice nesilleri yok ettiğimizi ve bir daha geri dönemediklerini düşünmezler mi?", transliteration: "E lem yerev kem ehlekna kablehum minel kuruni ennehum ileyhim la yerciun."},
  {id: 3737, sure_no: 32, ayet_no: 32, sure_adi: "Yasin", text: "وَاِنْ كُلٌّ لَمَّا جَم۪يعٌ لَدَيْنَا مُحْضَرُونَ۟", translation: "Onların hepsi de toplanıp kesinlikle karşımızda hazır bulundurulacaklardır.", transliteration: "Ve in kullun lemma cemiun ledeyna muhdarun."},
  {id: 3738, sure_no: 33, ayet_no: 33, sure_adi: "Yasin", text: "وَاٰيَةٌ لَهُمُ الْاَرْضُ الْمَيْتَةُۚ اَحْيَيْنَاهَا وَاَخْرَجْنَا مِنْهَا حَباًّ فَمِنْهُ يَأْكُلُونَ", translation: "Ölü toprak, onlara bir ayettir. Onu canlandırdık ve ondan ürünler çıkardık. Böylece ondan yerler.", transliteration: "Ve ayetun lehumul ardul meyteh, ahyeynaha ve ahrecna minha habben fe minhu ye'kulun."},
  {id: 3739, sure_no: 34, ayet_no: 34, sure_adi: "Yasin", text: "وَجَعَلْنَا ف۪يهَا جَنَّاتٍ مِنْ نَخ۪يلٍ وَاَعْنَابٍ وَفَجَّرْنَا ف۪يهَا مِنَ الْعُيُونِۙ", translation: "Orada hurma ve üzüm bahçeleri yaptık. Ve orada pınarlar akıttık.", transliteration: "Ve cealna fiha cennatin min nahilin ve a'nabin ve feccerna fiha minel uyun."},
  {id: 3740, sure_no: 35, ayet_no: 35, sure_adi: "Yasin", text: "لِيَأْكُلُوا مِنْ ثَمَرِه۪ۙ وَمَا عَمِلَتْهُ اَيْد۪يهِمْۜ اَفَلَا يَشْكُرُونَ", translation: "Onun ürünlerinden ve elleriyle yaptıklarından yesinler diye. Hala şükretmeyecekler mi?", transliteration: "Li ye'kulu min semerihi ve ma amilethu eydihim, e fe la yeşkurun."},
  {id: 3741, sure_no: 36, ayet_no: 36, sure_adi: "Yasin", text: "سُبْحَانَ الَّذ۪ي خَلَقَ الْاَزْوَاجَ كُلَّهَا مِمَّا تُنْبِتُ الْاَرْضُ وَمِنْ اَنْفُسِهِمْ وَمِمَّا لَا يَعْلَمُونَ", translation: "Yerin bitirdiklerinden, kendilerinden ve bilemeyecekleri şeylerden çiftler yaratan, O, Sübhan'dır.", transliteration: "Subhanellezi halakal ezvace kulleha mimma tunbitulardu ve min enfusihim ve mimma la ya'lemun."},
  {id: 3742, sure_no: 37, ayet_no: 37, sure_adi: "Yasin", text: "وَاٰيَةٌ لَهُمُ الَّيْلُۚ نَسْلَخُ مِنْهُ النَّهَارَ فَاِذَا هُمْ مُظْلِمُونَۙ", translation: "Gece de onlar için bir ayettir. Ondan gündüzü çekip alırız da onlar karanlıkta kalırlar.", transliteration: "Ve ayetun lehumul leyl, neslehu minhun nehare fe iza hum muzlimun."},
  {id: 3743, sure_no: 38, ayet_no: 38, sure_adi: "Yasin", text: "وَالشَّمْسُ تَجْر۪ي لِمُسْتَقَرٍّ لَهَاۜ ذٰلِكَ تَقْد۪يرُ الْعَز۪يزِ الْعَل۪يمِۜ", translation: "Güneş, kendisi için karar kılınan yörüngesinde akar gider. İşte bu Mutlak Üstün Olan'ın, Her Şeyi Bilen'in yasasıdır.", transliteration: "Veş şemsu tecri li mustekarrin leha, zalike takdirul azizil alim."},
  {id: 3744, sure_no: 39, ayet_no: 39, sure_adi: "Yasin", text: "وَالْقَمَرَ قَدَّرْنَاهُ مَنَازِلَ حَتّٰى عَادَ كَالْعُرْجُونِ الْقَد۪يمِ", translation: "Ay'a da menziller takdir ettik. Sonunda kuru bir hurma dalına döner.", transliteration: "Vel kamere kaddernahu menazile hatta adekel urcunil kadim."},
  {id: 3745, sure_no: 40, ayet_no: 40, sure_adi: "Yasin", text: "لَا الشَّمْسُ يَنْبَغ۪ي لَـهَٓا اَنْ تُدْرِكَ الْقَمَرَ وَلَا الَّيْلُ سَابِقُ النَّهَارِۜ وَكُلٌّ ف۪ي فَلَكٍ يَسْبَحُونَ", translation: "Ne Güneş Ay'a erişebilir ve ne de gecenin gündüzü geçmesi mümkün olabilir. Hepsi de bir yörüngede hareket ederler.", transliteration: "Leş şemsu yenbegi leha en tudrikel kamere ve lel leylu sabikun nehar, ve kullun fi felekin yesbehun."},
  {id: 3746, sure_no: 41, ayet_no: 41, sure_adi: "Yasin", text: "وَاٰيَةٌ لَهُمْ اَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِي الْفُلْكِ الْمَشْحُونِۙ", translation: "Onların soyunu dolu gemilerde taşımamız onlar için bir ayettir.", transliteration: "Ve ayetun lehum enna hamelna zurriyyetehum fil fulkil meşhun."},
  {id: 3747, sure_no: 42, ayet_no: 42, sure_adi: "Yasin", text: "وَخَلَقْنَا لَهُمْ مِنْ مِثْلِه۪ مَا يَرْكَبُونَ", translation: "Onlar için, onun gibi binecekleri şeyler yarattık.", transliteration: "Ve halakna lehum min mislihi ma yerkebun."},
  {id: 3748, sure_no: 43, ayet_no: 43, sure_adi: "Yasin", text: "وَاِنْ نَشَأْ نُغْرِقْهُمْ فَلَا صَر۪يخَ لَهُمْ وَلَا هُمْ يُنْقَذُونَۙ", translation: "Dilersek onları batırırız. Ne onlara yardım eden bulunur ne de onlar kurtulabilir.", transliteration: "Ve in neşe' nugrıkhum fe la sariha lehum ve la hum yunkazun."},
  {id: 3749, sure_no: 44, ayet_no: 44, sure_adi: "Yasin", text: "اِلَّا رَحْمَةً مِنَّا وَمَتَاعاً اِلٰى ح۪ينٍ", translation: "Bizden bir merhamet ve belli bir zamana kadar yararlandırmamız hariçtir.", transliteration: "İlla rahmeten minna ve metaan ila hin."},
  {id: 3750, sure_no: 45, ayet_no: 45, sure_adi: "Yasin", text: "وَاِذَا ق۪يلَ لَهُمُ اتَّقُوا مَا بَيْنَ اَيْد۪يكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ", translation: "Onlara: \"Sahip olduğunuz ve olacağınız şeylerde takva sahibi olun. Umulur ki böylece merhamet olunursunuz.\" denildiği zaman;", transliteration: "Ve iza kile lehumutteku ma beyne eydikum ve ma halfekum leallekum turhamun."},
  {id: 3751, sure_no: 46, ayet_no: 46, sure_adi: "Yasin", text: "وَمَا تَأْت۪يهِمْ مِنْ اٰيَةٍ مِنْ اٰيَاتِ رَبِّهِمْ اِلَّا كَانُوا عَنْهَا مُعْرِض۪ينَ", translation: "Onlar, Rabb'lerinin ayetlerinden hangi ayet gelirse gelsin ondan yüz çevirenler oldular.", transliteration: "Ve ma te'tihim min ayetin min ayati rabbihim illa kanu anha mu'ridin."},
  {id: 3752, sure_no: 47, ayet_no: 47, sure_adi: "Yasin", text: "وَاِذَا ق۪يلَ لَهُمْ اَنْفِقُوا مِمَّا رَزَقَكُمُ اللّٰهُۙ قَالَ الَّذ۪ينَ كَفَرُوا لِلَّذ۪ينَ اٰمَنُٓوا اَنُطْعِمُ مَنْ لَوْ يَشَٓاءُ اللّٰهُ اَطْعَمَهُۗ اِنْ اَنْتُمْ اِلَّا ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Onlara: \"Allah'ın sizi rızıklandırdığı şeylerden ihtiyaç sahiplerine yardım edin dendiği zaman, Kafirler, İman Edenler'e: \"Allah'ın dileseydi doyuracağı kimseyi biz mi doyuracağız? Siz ancak apaçık bir sapkınlık içindesiniz.\" dediler.", transliteration: "Ve iza kile lehum enfiku mimma rezakakumullahu kalellezine keferu lillezine amenu e nut'imu men lev yeşaullahu at'ameh, in entum illa fi dalalin mubin."},
  {id: 3753, sure_no: 48, ayet_no: 48, sure_adi: "Yasin", text: "وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Madem doğru söyleyenlerseniz, bu vaad ne zaman?\" derler.", transliteration: "Ve yekulune meta hazel va'du in kuntum sadikin."},
  {id: 3754, sure_no: 49, ayet_no: 49, sure_adi: "Yasin", text: "مَا يَنْظُرُونَ اِلَّا صَيْحَةً وَاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُونَ", translation: "Onlar birbirleri ile çekişirlerken, onları yakalayacak tek bir çığlıktan başkasını gözlemiyorlar.", transliteration: "Ma yenzurune illa sayhaten vahıdeten te'huzuhum ve hum yahıssımun."},
  {id: 3755, sure_no: 50, ayet_no: 50, sure_adi: "Yasin", text: "فَلَا يَسْتَط۪يعُونَ تَوْصِيَةً وَلَٓا اِلٰٓى اَهْلِهِمْ يَرْجِعُونَ۟", translation: "Artık vasiyette bulunmaya da ailelerine dönmeye de güçleri yetmez.", transliteration: "Fe la yestetiune tavsiyeten ve la ila ehlihim yerciun."},
  {id: 3756, sure_no: 51, ayet_no: 51, sure_adi: "Yasin", text: "وَنُفِـخَ فِي الصُّورِ فَاِذَا هُمْ مِنَ الْاَجْدَاثِ اِلٰى رَبِّهِمْ يَنْسِلُونَ", translation: "Sur'a üflenmiştir! İşte o zaman onlar, kabirlerinden Rabb'lerine akın ederler.", transliteration: "Ve nufiha fis suri fe iza hum minel ecdasi ila rabbihim yensilun."},
  {id: 3757, sure_no: 52, ayet_no: 52, sure_adi: "Yasin", text: "قَالُوا يَا وَيْلَنَا مَنْ بَعَثَنَا مِنْ مَرْقَدِنَ۔اۢ هٰذَا مَا وَعَدَ الرَّحْمٰنُ وَصَدَقَ الْمُرْسَلُونَ", translation: "\"Eyvah bize! Yattığımız yerden bizi kim kaldırdı? Bu Rahman'ın uyardığı şeydir. Resuller doğru söylemişler.\" dediler.", transliteration: "Kalu ya veylena men beasena min merkadina, haza ma vaader rahmanuve sadakal murselun."},
  {id: 3758, sure_no: 53, ayet_no: 53, sure_adi: "Yasin", text: "اِنْ كَانَتْ اِلَّا صَيْحَةً وَاحِدَةً فَاِذَا هُمْ جَم۪يعٌ لَدَيْنَا مُحْضَرُونَ", translation: "Sadece tek bir sayha! İşte o zaman onların tamamı huzurumuza getirilirler.", transliteration: "İn kanet illa sayhaten vahıdeten fe iza hum cemiun ledeyna muhdarun."},
  {id: 3759, sure_no: 54, ayet_no: 54, sure_adi: "Yasin", text: "فَالْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْـٔاً وَلَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُونَ", translation: "Artık bugün kişi, hiçbir haksızlık görmeyecek. Sadece yaptıklarınızın karşılığını göreceksiniz.", transliteration: "Fel yevme la tuzlemu nefsun şey'en ve la tuczevne illa ma kuntum ta'melun."},
  {id: 3760, sure_no: 55, ayet_no: 55, sure_adi: "Yasin", text: "اِنَّ اَصْحَابَ الْجَنَّةِ الْيَوْمَ ف۪ي شُغُلٍ فَاكِهُونَۚ", translation: "Kuşkusuz Cennet ehli bugün keyifli bir uğraş içindedir.", transliteration: "İnne ashabel cennetil yevme fi şugulin fakihun."},
  {id: 3761, sure_no: 56, ayet_no: 56, sure_adi: "Yasin", text: "هُمْ وَاَزْوَاجُهُمْ ف۪ي ظِلَالٍ عَلَى الْاَرَٓائِكِ مُتَّكِؤُ۫نَ", translation: "Onlar ve eşleri gölgeliklerde tahtlar üzerine kurulmuşlardır.", transliteration: "Hum ve ezvacuhum fi zılalin alel eraiki muttekiun."},
  {id: 3762, sure_no: 57, ayet_no: 57, sure_adi: "Yasin", text: "لَهُمْ ف۪يهَا فَاكِهَةٌ وَلَهُمْ مَا يَدَّعُونَۚ", translation: "Orada onlar için meyve ve gönüllerinin çektiği her şey vardır.", transliteration: "Lehum fiha fakihetun ve lehum ma yeddeun."},
  {id: 3763, sure_no: 58, ayet_no: 58, sure_adi: "Yasin", text: "سَلَامٌ قَوْلاً مِنْ رَبٍّ رَح۪يمٍ", translation: "Rahmeti kesintisiz Rabb'den söz selamdır.", transliteration: "Selamun kavlen min rabbin rahim."},
  {id: 3764, sure_no: 59, ayet_no: 59, sure_adi: "Yasin", text: "وَامْتَازُوا الْيَوْمَ اَيُّهَا الْمُجْرِمُونَ", translation: "Ey mücrimler! Bugün ayrılın!", transliteration: "Vemtazul yevme eyyuhel mucrimun."},
  {id: 3765, sure_no: 60, ayet_no: 60, sure_adi: "Yasin", text: "اَلَمْ اَعْهَدْ اِلَيْكُمْ يَا بَن۪ٓي اٰدَمَ اَنْ لَا تَعْبُدُوا الشَّيْطَانَۚ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌۙ", translation: "Ey Ademoğulları! Ben, size \"Şeytana kulluk etmeyin, o sizin için apaçık düşmandır, diye uyarıda bulunmadım mı?\"", transliteration: "E lem a'had ileykum ya beni ademe en la ta'buduş şeytan, innehu lekum aduvvun mubin."},
  {id: 3766, sure_no: 61, ayet_no: 61, sure_adi: "Yasin", text: "وَاَنِ اعْبُدُون۪يۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ", translation: "Bana kulluk edin. Dosdoğru yol budur.", transliteration: "Ve eni'buduni, haza sıratun mustekim."},
  {id: 3767, sure_no: 62, ayet_no: 62, sure_adi: "Yasin", text: "وَلَقَدْ اَضَلَّ مِنْكُمْ جِبِلاًّ كَث۪يراًۜ اَفَلَمْ تَكُونُوا تَعْقِلُونَ", translation: "Ant olsun ki sizden birçoklarını saptırdı. Sizde bunu anlayacak akıl yok muydu?", transliteration: "Ve lekad edalle minkum cibillen kesira, e fe lem tekunu ta'kılun."},
  {id: 3768, sure_no: 63, ayet_no: 63, sure_adi: "Yasin", text: "هٰذِه۪ جَهَنَّمُ الَّت۪ي كُنْتُمْ تُوعَدُونَ", translation: "İşte, uyarılmış olduğunuz Cehennem budur.", transliteration: "Hazihi cehennemulleti kuntum tuadun."},
  {id: 3769, sure_no: 64, ayet_no: 64, sure_adi: "Yasin", text: "اِصْلَوْهَا الْيَوْمَ بِمَا كُنْتُمْ تَكْفُرُونَ", translation: "Kafir olduğunuz için bugün oraya girin!", transliteration: "Islevhel yevme bima kuntum tekfurun."},
  {id: 3770, sure_no: 65, ayet_no: 65, sure_adi: "Yasin", text: "اَلْيَوْمَ نَخْتِمُ عَلٰٓى اَفْوَاهِهِمْ وَتُكَلِّمُنَٓا اَيْد۪يهِمْ وَتَشْهَدُ اَرْجُلُهُمْ بِمَا كَانُوا يَكْسِبُونَ", translation: "Bugün onların ağızlarını kapatırız. Bize elleri konuşur, ayakları da kazandıkları şeylere tanıklık eder.", transliteration: "El yevme nahtimu ala efvahihim ve tukellimuna eydihim ve teşhedu erculuhum bima kanu yeksibun."},
  {id: 3771, sure_no: 66, ayet_no: 66, sure_adi: "Yasin", text: "وَلَوْ نَشَٓاءُ لَطَمَسْنَا عَلٰٓى اَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَاَنّٰى يُبْصِرُونَ", translation: "Eğer dileseydik, elbette gözlerini kör ederdik de yol bulmak için koşuşturup dururlardı. Yollarını nasıl bulacaklardı ki?", transliteration: "Ve lev neşau le tamesna ala a'yunihim festebekus sırata fe enna yubsırun."},
  {id: 3772, sure_no: 67, ayet_no: 67, sure_adi: "Yasin", text: "وَلَوْ نَشَٓاءُ لَمَسَخْنَاهُمْ عَلٰى مَكَانَتِهِمْ فَمَا اسْتَطَاعُوا مُضِياًّ وَلَا يَرْجِعُونَ۟", translation: "Eğer dileseydik, oldukları yerde sabit bir şekle dönüştürürdük, ileri gitmeye de geri dönmeye de güç yetiremezlerdi.", transliteration: "Ve lev neşau le mesahnahum ala mekanetihim fe mastetau mudiyyen ve la yerciun."},
  {id: 3773, sure_no: 68, ayet_no: 68, sure_adi: "Yasin", text: "وَمَنْ نُعَمِّرْهُ نُنَكِّسْهُ فِي الْخَلْقِۜ اَفَلَا يَعْقِلُونَ", translation: "Kimin ömrünü uzatırsak, zamanla yaratılış olarak onu tersine çeviririz. Buna rağmen hala akıllarını kullanmayacaklar mı?", transliteration: "Ve men nuammirhu nunekkishu fil halk, e fe la ya'kılun."},
  {id: 3774, sure_no: 69, ayet_no: 69, sure_adi: "Yasin", text: "وَمَا عَلَّمْنَاهُ الشِّعْرَ وَمَا يَنْبَغ۪ي لَهُۜ اِنْ هُوَ اِلَّا ذِكْرٌ وَقُرْاٰنٌ مُب۪ينٌۙ", translation: "Biz ona şiir öğretmedik. Ve bu, ona yakışmaz da. O, yalnızca bir Öğüt ve apaçık Kur'an'dır.", transliteration: "Ve ma allemnahuş şi're ve ma yenbagi leh, in huve illa zikrun ve kur'anun mubin."},
  {id: 3775, sure_no: 70, ayet_no: 70, sure_adi: "Yasin", text: "لِيُنْذِرَ مَنْ كَانَ حَياًّ وَيَحِقَّ الْقَوْلُ عَلَى الْكَافِر۪ينَ", translation: "O, diri olanları uyarmak ve Kafirlerin üzerine Söz'ün hak olması içindir.", transliteration: "Li yunzire men kane hayyen ve yehıkkal kavlu alel kafirin."},
  {id: 3776, sure_no: 71, ayet_no: 71, sure_adi: "Yasin", text: "اَوَلَمْ يَرَوْا اَنَّا خَلَقْنَا لَهُمْ مِمَّا عَمِلَتْ اَيْد۪ينَٓا اَنْعَاماً فَهُمْ لَهَا مَالِكُونَ", translation: "Ellerimizin yaptıklarından, onlara sahip oldukları hayvanlar yarattığımızı görmüyorlar mı?", transliteration: "E ve lem yerev enna halakna lehum mimma amilet eydina en'amen fe hum leha malikun."},
  {id: 3777, sure_no: 72, ayet_no: 72, sure_adi: "Yasin", text: "وَذَلَّلْنَاهَا لَهُمْ فَمِنْهَا رَكُوبُهُمْ وَمِنْهَا يَأْكُلُونَ", translation: "Bu hayvanları yararlanmalarına sunduk. Kimine biniyor, kimini de yiyorlar.", transliteration: "Ve zellelnaha lehum fe minha rakubuhum ve minha ye'kulun."},
  {id: 3778, sure_no: 73, ayet_no: 73, sure_adi: "Yasin", text: "وَلَهُمْ ف۪يهَا مَنَافِـعُ وَمَشَارِبُۜ اَفَلَا يَشْكُرُونَ", translation: "Bu hayvanlarda onlar için yararlar ve içecek şeyler vardır. Hala şükretmeyecekler mi?", transliteration: "Ve lehum fiha menafiu ve meşarib, e fe la yeşkurun."},
  {id: 3779, sure_no: 74, ayet_no: 74, sure_adi: "Yasin", text: "وَاتَّخَذُوا مِنْ دُونِ اللّٰهِ اٰلِهَةً لَعَلَّهُمْ يُنْصَرُونَۜ", translation: "Onlar, Allah'ın yanı sıra, kendilerinden yardım umdukları ilahlar edindiler.", transliteration: "Vettehazu min dunillahi aliheten leallehum yunsarun."},
  {id: 3780, sure_no: 75, ayet_no: 75, sure_adi: "Yasin", text: "لَا يَسْتَط۪يعُونَ نَصْرَهُمْۙ وَهُمْ لَهُمْ جُنْدٌ مُحْضَرُونَ", translation: "Oysa bu ilahlar onlara yardım etmeye güç yetiremezler. Ne var ki kendileri onlar için hazır askerlerdir.", transliteration: "La yestetiune nasrahum ve hum lehum cundun muhdarun."},
  {id: 3781, sure_no: 76, ayet_no: 76, sure_adi: "Yasin", text: "فَلَا يَحْزُنْكَ قَوْلُهُمْۢ اِنَّا نَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ", translation: "Artık onların sözleri seni üzmesin. Biz, onların gizlediklerini de açığa vurduklarını da biliyoruz.", transliteration: "Fe la yahzunke kavluhum, inna na'lemu ma yusirrune ve ma yu'linun."},
  {id: 3782, sure_no: 77, ayet_no: 77, sure_adi: "Yasin", text: "اَوَلَمْ يَرَ الْاِنْسَانُ اَنَّا خَلَقْنَاهُ مِنْ نُطْفَةٍ فَاِذَا هُوَ خَص۪يمٌ مُب۪ينٌ", translation: "İnsan, onu bir nutfeden yarattığımızı düşünmüyor mu? Şimdi de Bize apaçık düşman oldu.", transliteration: "E ve lem yerel insanu enna halaknahu min nutfetin fe iza huve hasimun mubin."},
  {id: 3783, sure_no: 78, ayet_no: 78, sure_adi: "Yasin", text: "وَضَرَبَ لَنَا مَثَلاً وَنَسِيَ خَلْقَهُۜ قَالَ مَنْ يُحْـيِ الْعِظَامَ وَهِيَ رَم۪يمٌ", translation: "Nasıl yaratıldığını dikkate almayarak, bir de Bize örnek veriyor: \"Kemiklerimiz çürüyüp gitmişken, kim onlara can verecek?\" diyor.", transliteration: "Ve darebe lena meselen ve nesiye halkah, kale men yuhyil izame ve hiye remim."},
  {id: 3784, sure_no: 79, ayet_no: 79, sure_adi: "Yasin", text: "قُلْ يُحْي۪يهَا الَّـذ۪ٓي اَنْشَاَهَٓا اَوَّلَ مَرَّةٍۜ وَهُوَ بِكُلِّ خَلْقٍ عَل۪يمٌۙ", translation: "De ki: \"Onu ilk defa inşa eden, ona hayat verecek. Ve O, yaratmayı çok iyi bilendir.\"", transliteration: "Kul yuhyihellezi enşeeha evvele merreh, ve huve bi kulli halkın alim."},
  {id: 3785, sure_no: 80, ayet_no: 80, sure_adi: "Yasin", text: "اَلَّذ۪ي جَعَلَ لَكُمْ مِنَ الشَّجَرِ الْاَخْضَرِ نَاراً فَاِذَٓا اَنْتُمْ مِنْهُ تُوقِدُونَ", translation: "Size yemyeşil ağaçtan ateş çıkaran O'dur. Siz, ondan yakıp duruyorsunuz.", transliteration: "Ellezi ceale lekum mineş şeceril ahdarinaren fe iza entum minhu tukıdun."},
  {id: 3786, sure_no: 81, ayet_no: 81, sure_adi: "Yasin", text: "اَوَلَيْسَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِقَادِرٍ عَلٰٓى اَنْ يَخْلُقَ مِثْلَهُمْۜ بَلٰى وَهُوَ الْخَلَّاقُ الْعَل۪يمُ", translation: "Gökleri ve yeri yaratanın, onların benzerlerini de yaratmaya gücü yetmez mi? Evet O, Yegane Yaratıcı'dır, Her Şeyi Bilen'dir.", transliteration: "E ve leysellezi halakas semavati vel arda bi kadirin ala en yahluka mislehum, bela ve huvel hallakul alim."},
  {id: 3787, sure_no: 82, ayet_no: 82, sure_adi: "Yasin", text: "اِنَّـمَٓا اَمْرُهُٓ اِذَٓا اَرَادَ شَيْـٔاً اَنْ يَقُولَ لَهُ كُنْ فَيَكُونُ", translation: "O, bir şey yaratmak istediğinde, ona: \"Ol.\" der. O da hemen oluverir.", transliteration: "İnnema emruhu iza erade şey'en en yekule lehu kun fe yekun."},
  {id: 3788, sure_no: 83, ayet_no: 83, sure_adi: "Yasin", text: "فَسُبْحَانَ الَّذ۪ي بِيَدِه۪ مَلَكُوتُ كُلِّ شَيْءٍ وَاِلَيْهِ تُرْجَعُونَ", translation: "O, çok yüce ve çok üstündür. Her şeyin mülkü ve egemenliği O'nun elindedir. Ve O'na döndürüleceksiniz.", transliteration: "Fe subhanellezi bi yedihi melekutu kulli şey'in ve ileyhi turceun."},
  {id: 3789, sure_no: 1, ayet_no: 1, sure_adi: "Saffat", text: "وَالصَّٓافَّاتِ صَفاًّۙ", translation: "Saf bağlayarak, saflar halinde dizilenlere ant olsun.", transliteration: "Ves saffati saffa."},
  {id: 3790, sure_no: 2, ayet_no: 2, sure_adi: "Saffat", text: "فَالزَّاجِرَاتِ زَجْراًۙ", translation: "Haykırıp sürenlere,", transliteration: "Fez zacirati zecra."},
  {id: 3791, sure_no: 3, ayet_no: 3, sure_adi: "Saffat", text: "فَالتَّالِيَاتِ ذِكْراًۙ", translation: "Öğüt okuyanlara,", transliteration: "Fet taliyati zikra."},
  {id: 3792, sure_no: 4, ayet_no: 4, sure_adi: "Saffat", text: "اِنَّ اِلٰهَكُمْ لَوَاحِدٌۜ", translation: "Kuşkusuz ilahınız elbette Bir Tek'tir.", transliteration: "İnne ilahekum le vahıd."},
  {id: 3793, sure_no: 5, ayet_no: 5, sure_adi: "Saffat", text: "رَبُّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ الْمَشَارِقِۜ", translation: "Göklerin, yerin ve ikisinin arasında olanların Rabb'idir. Ve doğuların Rabb'idir.", transliteration: "Rabbus semavati vel ardı ve ma beynehuma ve rabbul meşarık."},
  {id: 3794, sure_no: 6, ayet_no: 6, sure_adi: "Saffat", text: "اِنَّا زَيَّنَّا السَّمَٓاءَ الدُّنْيَا بِز۪ينَةٍۨ الْـكَوَاكِبِۙ", translation: "Biz, dünya semasını bir ziynetle, yıldızlarla bezedik.", transliteration: "İnna zeyyennes semaed dunya bi ziynetinil kevakib."},
  {id: 3795, sure_no: 7, ayet_no: 7, sure_adi: "Saffat", text: "وَحِفْظاً مِنْ كُلِّ شَيْطَانٍ مَارِدٍۚ", translation: "Bütün marid şeytanlardan koruduk.", transliteration: "Ve hıfzan min kulli şeytanin marid."},
  {id: 3796, sure_no: 8, ayet_no: 8, sure_adi: "Saffat", text: "لَا يَسَّمَّعُونَ اِلَى الْمَلَأِ الْاَعْلٰى وَيُقْذَفُونَ مِنْ كُلِّ جَانِبٍۗ", translation: "Mele-i A'la'ya kulak verip dinleyemezler; her taraftan kovulurlar.", transliteration: "La yessemmeune ilel meleil a'la ve yukzefune minkulli canib."},
  {id: 3797, sure_no: 9, ayet_no: 9, sure_adi: "Saffat", text: "دُحُوراً وَلَهُمْ عَذَابٌ وَاصِبٌۙ", translation: "Kovulmuş olarak, onlar için kesintisiz azap vardır.", transliteration: "Duhuran ve lehum azabun vasib."},
  {id: 3798, sure_no: 10, ayet_no: 10, sure_adi: "Saffat", text: "اِلَّا مَنْ خَطِفَ الْخَطْفَةَ فَاَتْبَعَهُ شِهَابٌ ثَاقِبٌ", translation: "Ancak oradan bir söz kapan olursa, kayıp giden parlak bir alev ona yetişir ve onu yakar.", transliteration: "İlla men hatıfel hatfete fe etbeahu şihabun sakib."},
  {id: 3799, sure_no: 11, ayet_no: 11, sure_adi: "Saffat", text: "فَاسْتَفْتِهِمْ اَهُمْ اَشَدُّ خَلْقاً اَمْ مَنْ خَلَقْنَاۜ اِنَّا خَلَقْنَاهُمْ مِنْ ط۪ينٍ لَازِبٍ", translation: "Şimdi onlara sor: \"Onları yaratmak mı daha zor, yoksa diğer yarattıklarımızı mı?\" Oysa kendilerini yapışkan bir çamurdan yarattık.", transliteration: "Festeftihim e hum eşeddu halkan em men halakna, inna halaknahum min tinin lazib."},
  {id: 3800, sure_no: 12, ayet_no: 12, sure_adi: "Saffat", text: "بَلْ عَجِبْتَ وَيَسْخَرُونَۖ", translation: "Evet, sen hayran kaldın, onlar ise alay ediyorlar.", transliteration: "Bel acibte ve yesharun."},
  {id: 3801, sure_no: 13, ayet_no: 13, sure_adi: "Saffat", text: "وَاِذَا ذُكِّرُوا لَا يَذْكُرُونَۖ", translation: "Kendilerine öğüt verildiği zaman, öğüdü dikkate almıyorlar.", transliteration: "Ve iza zukkiru la yezkurun."},
  {id: 3802, sure_no: 14, ayet_no: 14, sure_adi: "Saffat", text: "وَاِذَا رَاَوْا اٰيَةً يَسْتَسْخِرُونَۖ", translation: "Ve bir ayet gördükleri zaman eğlenceye alıyorlar.", transliteration: "Ve iza raev ayeten yesteshırun."},
  {id: 3803, sure_no: 15, ayet_no: 15, sure_adi: "Saffat", text: "وَقَالُٓوا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُب۪ينٌۚ", translation: "\"Bu apaçık bir büyüden başka bir şey değildir.\" diyorlar.", transliteration: "Ve kalu in haza illa sihrun mubin."},
  {id: 3804, sure_no: 16, ayet_no: 16, sure_adi: "Saffat", text: "ءَاِذَا مِتْنَا وَكُنَّا تُرَاباً وَعِظَاماً ءَاِنَّا لَمَبْعُوثُونَۙ", translation: "\"Öldüğümüz; toprak ve kemik olduğumuz zaman, yeniden diriltileceğiz öyle mi?\"", transliteration: "E iza mitna ve kunna turaben ve izamen e inna le meb'usun."},
  {id: 3805, sure_no: 17, ayet_no: 17, sure_adi: "Saffat", text: "اَوَاٰبَٓاؤُ۬نَا الْاَوَّلُونَۜ", translation: "\"Yok olup gitmiş atalarımız da mı?\"", transliteration: "E ve abaunel evvelun."},
  {id: 3806, sure_no: 18, ayet_no: 18, sure_adi: "Saffat", text: "قُلْ نَعَمْ وَاَنْتُمْ دَاخِرُونَۚ", translation: "De ki: \"Evet, aşağılanmış olarak.\"", transliteration: "Kul neam ve entum dahırun."},
  {id: 3807, sure_no: 19, ayet_no: 19, sure_adi: "Saffat", text: "فَاِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ فَاِذَا هُمْ يَنْظُرُونَ", translation: "Artık o tek bir haykırıştır. O zaman neyin ne olduğunu görecekler.", transliteration: "Fe innema hiye zecretun vahıdetun fe iza hum yenzurun."},
  {id: 3808, sure_no: 20, ayet_no: 20, sure_adi: "Saffat", text: "وَقَالُوا يَا وَيْلَنَا هٰذَا يَوْمُ الدّ۪ينِ", translation: "\"Eyvah bizlere! İşte bu Din Günü'dür\" derler.", transliteration: "Ve kalu ya veylena haza yevmud din."},
  {id: 3809, sure_no: 21, ayet_no: 21, sure_adi: "Saffat", text: "هٰذَا يَوْمُ الْفَصْلِ الَّذ۪ي كُنْتُمْ بِه۪ تُكَذِّبُونَ۟", translation: "Bu, yalanladığınız Fasıl Günü'dür.", transliteration: "Haza yevmul faslillezi kuntum bihi tukezzibun."},
  {id: 3810, sure_no: 22, ayet_no: 22, sure_adi: "Saffat", text: "اُحْشُرُوا الَّذ۪ينَ ظَلَمُوا وَاَزْوَاجَهُمْ وَمَا كَانُوا يَعْبُدُونَۙ", translation: "Toplayın o zulmedenleri, eşlerini ve onların kulluk ettikleri şeyleri;", transliteration: "Uhşurullezine zalemu ve ezvacehum ve ma kanu ya'budun."},
  {id: 3811, sure_no: 23, ayet_no: 23, sure_adi: "Saffat", text: "مِنْ دُونِ اللّٰهِ فَاهْدُوهُمْ اِلٰى صِرَاطِ الْجَح۪يمِۙ", translation: "Allah'ın yanı sıra. Artık onlara Cehennem yolunu gösterin.", transliteration: "Min dunillahi fehduhum ila sıratıl cahim."},
  {id: 3812, sure_no: 24, ayet_no: 24, sure_adi: "Saffat", text: "وَقِفُوهُمْ اِنَّهُمْ مَسْؤُ۫لُونَۙ", translation: "Onları durdurun! Kuşkusuz onlar sorumludurlar.", transliteration: "Vakıfuhum innehum mes'ulun."},
  {id: 3813, sure_no: 25, ayet_no: 25, sure_adi: "Saffat", text: "مَا لَـكُمْ لَا تَنَاصَرُونَ", translation: "Size ne oldu da dünyadaki gibi yardımlaşmıyorsunuz?", transliteration: "Ma lekum la tenasarun."},
  {id: 3814, sure_no: 26, ayet_no: 26, sure_adi: "Saffat", text: "بَلْ هُمُ الْيَوْمَ مُسْتَسْلِمُونَ", translation: "Hayır! Onlar O Gün teslim olmuşlardır.", transliteration: "Bel humul yevme musteslimun."},
  {id: 3815, sure_no: 27, ayet_no: 27, sure_adi: "Saffat", text: "وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَتَسَٓاءَلُونَ", translation: "Karşılıklı olarak birbirlerini suçluyorlar.", transliteration: "Ve akbele ba'duhum ala ba'dın yetesaelun."},
  {id: 3816, sure_no: 28, ayet_no: 28, sure_adi: "Saffat", text: "قَالُٓوا اِنَّكُمْ كُنْتُمْ تَأْتُونَنَا عَنِ الْيَم۪ينِ", translation: "\"Gerçek şu ki siz bize, hep sağ taraftan geliyordunuz.\" derler.", transliteration: "Kalu innekum kuntum te'tunena anil yemin."},
  {id: 3817, sure_no: 29, ayet_no: 29, sure_adi: "Saffat", text: "قَالُوا بَلْ لَمْ تَكُونُوا مُؤْمِن۪ينَۚ", translation: "\"Hayır, siz zaten iman eden kimseler değildiniz.\" derler.", transliteration: "Kalu bel lem tekunu mu'minin."},
  {id: 3818, sure_no: 30, ayet_no: 30, sure_adi: "Saffat", text: "وَمَا كَانَ لَنَا عَلَيْكُمْ مِنْ سُلْطَانٍۚ بَلْ كُنْتُمْ قَوْماً طَاغ۪ينَ", translation: "\"Bizim sizin üzerinizde yetkimiz yoktu. Bilakis, siz azmış bir halktınız.\"", transliteration: "Ve ma kane lena aleykum min sultan, bel kuntum kavmen tagin."},
  {id: 3819, sure_no: 31, ayet_no: 31, sure_adi: "Saffat", text: "فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَاۗ اِنَّا لَذَٓائِقُونَ", translation: "\"Artık Rabb'imizin Söz'ü üzerimize hak oldu. Kuşkusuz biz, azabı tadacak olanlarız.\"", transliteration: "Fe hakka aleyna kavlu rabbina inna le zaıkun."},
  {id: 3820, sure_no: 32, ayet_no: 32, sure_adi: "Saffat", text: "فَاَغْوَيْنَا‌كُمْ اِنَّا كُنَّا غَاو۪ينَ", translation: "\"Biz, sizi azdırdık, çünkü biz azgındık.\"", transliteration: "Fe agveynakum inna kunna gavin."},
  {id: 3821, sure_no: 33, ayet_no: 33, sure_adi: "Saffat", text: "فَاِنَّهُمْ يَوْمَئِذٍ فِي الْعَذَابِ مُشْتَرِكُونَ", translation: "Onlar, O gün azapta ortaktırlar.", transliteration: "Fe innehum yevme izin fil azabi muşterikun."},
  {id: 3822, sure_no: 34, ayet_no: 34, sure_adi: "Saffat", text: "اِنَّا كَذٰلِكَ نَفْعَلُ بِالْمُجْرِم۪ينَ", translation: "Mücrimleri böyle cezalandırırız.", transliteration: "İnna kezalike nef'alu bil mucrimin."},
  {id: 3823, sure_no: 35, ayet_no: 35, sure_adi: "Saffat", text: "اِنَّهُمْ كَانُٓوا اِذَا ق۪يلَ لَهُمْ لَٓا اِلٰهَ اِلَّا اللّٰهُ يَسْتَكْبِرُونَۙ", translation: "Onlar, kendilerine: \"Allah'tan başka ilah yoktur.\" denildiği zaman büyüklük taslayanlardı.", transliteration: "İnnehum kanu iza kile lehum la ilahe illallahu yestekbirun."},
  {id: 3824, sure_no: 36, ayet_no: 36, sure_adi: "Saffat", text: "وَيَقُولُونَ اَئِنَّا لَتَارِكُٓوا اٰلِهَتِنَا لِشَاعِرٍ مَجْنُونٍۜ", translation: "\"Mecnun bir şair için ilahlarımızı terk edenler mi olacağız?\" derlerdi.", transliteration: "Ve yekulune e inna le tariku alihetina li şairin mecnun."},
  {id: 3825, sure_no: 37, ayet_no: 37, sure_adi: "Saffat", text: "بَلْ جَٓاءَ بِالْحَقِّ وَصَدَّقَ الْمُرْسَل۪ينَ", translation: "Bilakis, o, Hakkı getirdi ve gönderilmiş Resulleri doğruladı.", transliteration: "Bel cae bil hakkı ve saddakal murselin."},
  {id: 3826, sure_no: 38, ayet_no: 38, sure_adi: "Saffat", text: "اِنَّكُمْ لَذَٓائِقُوا الْعَذَابِ الْاَل۪يمِۚ", translation: "Siz, kesinlikle elim azabı tadacak olanlarsınız.", transliteration: "İnnekum le zaikul azabil elim."},
  {id: 3827, sure_no: 39, ayet_no: 39, sure_adi: "Saffat", text: "وَمَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُونَۙ", translation: "Sadece yapmış olduğunuz şeyin karşılığını alacaksınız.", transliteration: "Ve ma tuczevne illa ma kuntum ta'melun."},
  {id: 3828, sure_no: 40, ayet_no: 40, sure_adi: "Saffat", text: "اِلَّا عِبَادَ اللّٰهِ الْمُخْلَص۪ينَ", translation: "Allah'ın muhles kulları hariç.", transliteration: "İlla ibadallahil muhlesin."},
  {id: 3829, sure_no: 41, ayet_no: 41, sure_adi: "Saffat", text: "اُو۬لٰٓئِكَ لَهُمْ رِزْقٌ مَعْلُومٌۙ", translation: "Onlar için bilinen bir rızık vardır.", transliteration: "Ulaike lehum rizkun ma'lum."},
  {id: 3830, sure_no: 42, ayet_no: 42, sure_adi: "Saffat", text: "فَوَا‌كِهُۚ وَهُمْ مُكْرَمُونَۙ", translation: "Onlara meyveler ikram edilecek.", transliteration: "Fevakih, ve hum mukremun."},
  {id: 3831, sure_no: 43, ayet_no: 43, sure_adi: "Saffat", text: "ف۪ي جَنَّاتِ النَّع۪يمِۙ", translation: "Naim Cennetleri'nde.", transliteration: "Fi cennatin naim."},
  {id: 3832, sure_no: 44, ayet_no: 44, sure_adi: "Saffat", text: "عَلٰى سُرُرٍ مُتَقَابِل۪ينَ", translation: "Karşılıklı tahtlar üzerinde.", transliteration: "Ala sururin mutekabilin."},
  {id: 3833, sure_no: 45, ayet_no: 45, sure_adi: "Saffat", text: "يُطَافُ عَلَيْهِمْ بِكَأْسٍ مِنْ مَع۪ينٍۙ", translation: "Etraflarında kaynaklardan doldurulmuş kaseler dolaştırılır.", transliteration: "Yutafu aleyhim bi ke'sin min main."},
  {id: 3834, sure_no: 46, ayet_no: 46, sure_adi: "Saffat", text: "بَيْضَٓاءَ لَذَّةٍ لِلشَّارِب۪ينَۚ", translation: "Berrak, içenlere lezzet veren.", transliteration: "Beydae lezzetin liş şaribin."},
  {id: 3835, sure_no: 47, ayet_no: 47, sure_adi: "Saffat", text: "لَا ف۪يهَا غَوْلٌ وَلَا هُمْ عَنْهَا يُنْزَفُونَ", translation: "İçinde kötü etkisi olan bir şey yoktur. Ve ondan onların akılları karışmaz.", transliteration: "La fiha gavlun ve la hum anha yunzefun."},
  {id: 3836, sure_no: 48, ayet_no: 48, sure_adi: "Saffat", text: "وَعِنْدَهُمْ قَاصِرَاتُ الطَّرْفِ ع۪ينٌۙ", translation: "Yanlarında bakışlarını koruyanlar vardır.", transliteration: "Ve indehum kasıratut tarfı in."},
  {id: 3837, sure_no: 49, ayet_no: 49, sure_adi: "Saffat", text: "كَاَنَّهُنَّ بَيْضٌ مَكْنُونٌ", translation: "Onlar, iyi korunmuş yumurta gibidir.", transliteration: "Ke enne hunne beydun meknun."},
  {id: 3838, sure_no: 50, ayet_no: 50, sure_adi: "Saffat", text: "فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَتَسَٓاءَلُونَ", translation: "Birbirleriyle karşılıklı sohbet ediyorlar.", transliteration: "Fe akbele ba'duhum ala ba'dın yetesaelun."},
  {id: 3839, sure_no: 51, ayet_no: 51, sure_adi: "Saffat", text: "قَالَ قَٓائِلٌ مِنْهُمْ اِنّ۪ي كَانَ ل۪ي قَر۪ينٌۙ", translation: "İçlerinden biri dedi ki: \"Benim yakın bir arkadaşım vardı.\"", transliteration: "Kale kailun minhum inni kane li karin."},
  {id: 3840, sure_no: 52, ayet_no: 52, sure_adi: "Saffat", text: "يَقُولُ اَئِنَّكَ لَمِنَ الْمُصَدِّق۪ينَ", translation: "Diyordu ki: \"Sen gerçekten ahireti doğrulayanlardan mısın?\"", transliteration: "Yekulu e inneke le minel musaddikin."},
  {id: 3841, sure_no: 53, ayet_no: 53, sure_adi: "Saffat", text: "ءَاِذَا مِتْنَا وَكُنَّا تُرَاباً وَعِظَاماً ءَاِنَّا لَمَد۪ينُونَ", translation: "\"Öldüğümüz, toprak ve kemik olduğumuz zaman, gerçekten cezalandırılacak mıyız?\"", transliteration: "E iza mitna ve kunna turaben ve izamen e inna le medinun."},
  {id: 3842, sure_no: 54, ayet_no: 54, sure_adi: "Saffat", text: "قَالَ هَلْ اَنْتُمْ مُطَّلِعُونَ", translation: "\"Siz yakından bilenler misiniz?\" derdi.", transliteration: "Kale hel entum muttaliun."},
  {id: 3843, sure_no: 55, ayet_no: 55, sure_adi: "Saffat", text: "فَاطَّـلَعَ فَرَاٰهُ ف۪ي سَوَٓاءِ الْجَح۪يمِ", translation: "Derken yakından tanık oldu. Onu Cehennem'in ortasında gördü.", transliteration: "Fettalea fe reahu fi sevail cahim."},
  {id: 3844, sure_no: 56, ayet_no: 56, sure_adi: "Saffat", text: "قَالَ تَاللّٰهِ اِنْ كِدْتَ لَتُرْد۪ينِۙ", translation: "\"Vallahi az kalsın beni de mahvedecektin.\" dedi.", transliteration: "Kale tallahi in kidte le turdin."},
  {id: 3845, sure_no: 57, ayet_no: 57, sure_adi: "Saffat", text: "وَلَوْلَا نِعْمَةُ رَبّ۪ي لَكُنْتُ مِنَ الْمُحْضَر۪ينَ", translation: "\"Eğer Rabb'imin nimeti olmasaydı, ben de Cehennem'e atılanlardan olurdum.\"", transliteration: "Ve lev la ni'metu rabbi le kuntu minel muhdarin."},
  {id: 3846, sure_no: 58, ayet_no: 58, sure_adi: "Saffat", text: "اَفَمَا نَحْنُ بِمَيِّت۪ينَۙ", translation: "\"Biz artık bir daha ölmeyeceğiz, öyle değil mi?\"", transliteration: "E fe ma nahnu bi meyyitin."},
  {id: 3847, sure_no: 59, ayet_no: 59, sure_adi: "Saffat", text: "اِلَّا مَوْتَتَنَا الْاُو۫لٰى وَمَا نَحْنُ بِمُعَذَّب۪ينَ", translation: "\"İlk ölümümüzden başka ölüm görmeyecek, azaba uğratılacaklar da olmayacağız.\"", transliteration: "İlla mevtetenel ula ve ma nahnu bi muazzebin."},
  {id: 3848, sure_no: 60, ayet_no: 60, sure_adi: "Saffat", text: "اِنَّ هٰذَا لَهُوَ الْفَوْزُ الْعَظ۪يمُ", translation: "Bu gerçekten en büyük başarıdır.", transliteration: "İnne haza le huvel fevzul azim."},
  {id: 3849, sure_no: 61, ayet_no: 61, sure_adi: "Saffat", text: "لِمِثْلِ هٰذَا فَلْيَعْمَلِ الْعَامِلُونَ", translation: "Çalışanlar, bunun gibi şeyler için çalışsın.", transliteration: "Li misli haza fel ya'melil amilun."},
  {id: 3850, sure_no: 62, ayet_no: 62, sure_adi: "Saffat", text: "اَذٰلِكَ خَيْرٌ نُزُلاً اَمْ شَجَرَةُ الزَّقُّومِ", translation: "İkram olarak bu mu daha iyi, yoksa zakkum ağacı mı?", transliteration: "E zalike hayrun nuzulen em şeceretuz zakkum."},
  {id: 3851, sure_no: 63, ayet_no: 63, sure_adi: "Saffat", text: "اِنَّا جَعَلْنَاهَا فِتْنَةً لِلظَّالِم۪ينَ", translation: "Biz, onu zalimler için bir fitne yaptık.", transliteration: "İnna cealnaha fitneten liz zalimin."},
  {id: 3852, sure_no: 64, ayet_no: 64, sure_adi: "Saffat", text: "اِنَّهَا شَجَرَةٌ تَخْرُجُ ف۪ٓي اَصْلِ الْجَح۪يمِۙ", translation: "O, Cehennem'in dibinde çıkan bir ağaçtır.", transliteration: "İnneha şeceretun tahrucu fi aslil cahim."},
  {id: 3853, sure_no: 65, ayet_no: 65, sure_adi: "Saffat", text: "طَلْعُهَا كَاَنَّهُ رُؤُ۫سُ الشَّيَاط۪ينِ", translation: "Tomurcukları şeytanların başları gibidir.", transliteration: "Tal'uha ke ennehu ruusuş şeyatin."},
  {id: 3854, sure_no: 66, ayet_no: 66, sure_adi: "Saffat", text: "فَاِنَّهُمْ لَاٰكِلُونَ مِنْهَا فَمَالِؤُ۫نَ مِنْهَا الْبُطُونَۜ", translation: "Onlar, ondan yiyecekler ve karınlarını onunla dolduracaklardır.", transliteration: "Fe innehum le akilune minha fe maliune min hel butun."},
  {id: 3855, sure_no: 67, ayet_no: 67, sure_adi: "Saffat", text: "ثُمَّ اِنَّ لَهُمْ عَلَيْهَا لَشَوْباً مِنْ حَم۪يمٍۚ", translation: "Sonra da onun üstüne kaynar su karıştırılmış bir içecek vardır.", transliteration: "Summe inne lehum aleyha le şevben min hamim."},
  {id: 3856, sure_no: 68, ayet_no: 68, sure_adi: "Saffat", text: "ثُمَّ اِنَّ مَرْجِعَهُمْ لَاِلَى الْجَح۪يمِ", translation: "Sonra dönecekleri yer, kesinlikle Cehennem'dir.", transliteration: "Summe inne merciahum le ilel cahim."},
  {id: 3857, sure_no: 69, ayet_no: 69, sure_adi: "Saffat", text: "اِنَّهُمْ اَلْفَوْا اٰبَٓاءَهُمْ ضَٓالّ۪ينَۙ", translation: "Onlar, atalarını sapkın bir halde buldular.", transliteration: "İnnehum elfev abaehum dalline."},
  {id: 3858, sure_no: 70, ayet_no: 70, sure_adi: "Saffat", text: "فَهُمْ عَلٰٓى اٰثَارِهِمْ يُهْرَعُونَ", translation: "Kendileri de onların izleri üzerinde koşturdular.", transliteration: "Fe hum ala asarihim yuhreun."},
  {id: 3859, sure_no: 71, ayet_no: 71, sure_adi: "Saffat", text: "وَلَقَدْ ضَلَّ قَبْلَهُمْ اَكْثَرُ الْاَوَّل۪ينَۙ", translation: "Ant olsun ki onlardan öncekilerin çoğu sapkındı.", transliteration: "Ve lekad dalle kablehum ekserul evvelin."},
  {id: 3860, sure_no: 72, ayet_no: 72, sure_adi: "Saffat", text: "وَلَقَدْ اَرْسَلْنَا ف۪يهِمْ مُنْذِر۪ينَ", translation: "Ant olsun ki onlara içlerinden uyarıcılar gönderdik.", transliteration: "Ve lekad erselna fi him munzirin."},
  {id: 3861, sure_no: 73, ayet_no: 73, sure_adi: "Saffat", text: "فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَر۪ينَۙ", translation: "Uyarılanların sonlarının nasıl olduğuna bir bak!", transliteration: "Fanzur keyfe kane akibetul munzerin."},
  {id: 3862, sure_no: 74, ayet_no: 74, sure_adi: "Saffat", text: "اِلَّا عِبَادَ اللّٰهِ الْمُخْلَص۪ينَ۟", translation: "Ancak Allah'ın muhles kulları hariç.", transliteration: "İlla ibadallahil muhlasin."},
  {id: 3863, sure_no: 75, ayet_no: 75, sure_adi: "Saffat", text: "وَلَقَدْ نَادٰينَا نُوحٌ فَلَنِعْمَ الْمُج۪يبُونَۚ", translation: "Ant olsun ki Nuh, Bize dua etmişti. Biz, ne güzel karşılık vermiştik!", transliteration: "Ve lekad nadana nuhun fe le ni'mel mucibun."},
  {id: 3864, sure_no: 76, ayet_no: 76, sure_adi: "Saffat", text: "وَنَجَّيْنَاهُ وَاَهْلَهُ مِنَ الْكَرْبِ الْعَظ۪يمِۘ", translation: "Onu ve ehlini büyük sıkıntıdan kurtardık.", transliteration: "Ve necceynahu ve ehlehu minel kerbil azim."},
  {id: 3865, sure_no: 77, ayet_no: 77, sure_adi: "Saffat", text: "وَجَعَلْنَا ذُرِّيَّتَهُ هُمُ الْبَاق۪ينَۘ", translation: "Onun soyunu kalıcı kıldık.", transliteration: "Ve cealna zurriyyetehu humul bakin."},
  {id: 3866, sure_no: 78, ayet_no: 78, sure_adi: "Saffat", text: "وَتَرَكْنَا عَلَيْهِ فِي الْاٰخِر۪ينَۘ", translation: "Sonradan gelenler içinde onu destekleyiciler bıraktık.", transliteration: "Ve terekna aleyhi fil ahirin."},
  {id: 3867, sure_no: 79, ayet_no: 79, sure_adi: "Saffat", text: "سَلَامٌ عَلٰى نُوحٍ فِي الْعَالَم۪ينَ", translation: "Alemler içinde, Nuh'a selam olsun.", transliteration: "Selamun ala nuhın fil alemin."},
  {id: 3868, sure_no: 80, ayet_no: 80, sure_adi: "Saffat", text: "اِنَّا كَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "İyilere, işte böyle karşılık veririz.", transliteration: "İnna kezalike neczil muhsinin."},
  {id: 3869, sure_no: 81, ayet_no: 81, sure_adi: "Saffat", text: "اِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِن۪ينَ", translation: "O, Bizim Mü'min kullarımızdandı.", transliteration: "İnnehu min ibadinel mu'minin."},
  {id: 3870, sure_no: 82, ayet_no: 82, sure_adi: "Saffat", text: "ثُمَّ اَغْرَقْنَا الْاٰخَر۪ينَ", translation: "Sonra diğerlerini boğduk.", transliteration: "Summe agraknel aharin."},
  {id: 3871, sure_no: 83, ayet_no: 83, sure_adi: "Saffat", text: "وَاِنَّ مِنْ ش۪يعَتِه۪ لَاِبْرٰه۪يمَۢ", translation: "İbrahim, onun yolunu izleyenlerdendi.", transliteration: "Ve inne min şiatihi le ibrahim."},
  {id: 3872, sure_no: 84, ayet_no: 84, sure_adi: "Saffat", text: "اِذْ جَٓاءَ رَبَّهُ بِقَلْبٍ سَل۪يمٍ", translation: "O, Rabb'ine selim bir kalp ile gelmişti.", transliteration: "İz cae rabbehu bi kalbin selim."},
  {id: 3873, sure_no: 85, ayet_no: 85, sure_adi: "Saffat", text: "اِذْ قَالَ لِاَب۪يهِ وَقَوْمِه۪ مَاذَا تَعْبُدُونَۚ", translation: "Babasına ve halka: \"Nelere kulluk ediyorsunuz?\" demişti.", transliteration: "İz kale li ebihi ve kavmihi maza ta'budun."},
  {id: 3874, sure_no: 86, ayet_no: 86, sure_adi: "Saffat", text: "اَئِفْكاً اٰلِهَةً دُونَ اللّٰهِ تُر۪يدُونَۜ", translation: "\"Allah'ı bırakarak uydurma ilahlar mı istiyorsunuz?\"", transliteration: "E ifken aliheten dunallahi turidun."},
  {id: 3875, sure_no: 87, ayet_no: 87, sure_adi: "Saffat", text: "فَمَا ظَنُّكُمْ بِرَبِّ الْعَالَم۪ينَ", translation: "\"Alemlerin Rabb'i hakkında nasıl bir anlayışa sahipsiniz?\"", transliteration: "Fe ma zannukum bi rabbil alemin."},
  {id: 3876, sure_no: 88, ayet_no: 88, sure_adi: "Saffat", text: "فَنَظَرَ نَظْرَةً فِي النُّجُومِۙ", translation: "Yıldızlara bir nazarla nazar etti.", transliteration: "Fe nazara nazraten fin nucum."},
  {id: 3877, sure_no: 89, ayet_no: 89, sure_adi: "Saffat", text: "فَقَالَ اِنّ۪ي سَق۪يمٌ", translation: "\"Ben gerçekten hastayım!\" dedi.", transliteration: "Fe kale inni sakim."},
  {id: 3878, sure_no: 90, ayet_no: 90, sure_adi: "Saffat", text: "فَتَوَلَّوْا عَنْهُ مُدْبِر۪ينَ", translation: "Bunun üzerine ondan yüz çevirerek, arkalarını dönüp gittiler.", transliteration: "Fe tevellev anhu mudbirin."},
  {id: 3879, sure_no: 91, ayet_no: 91, sure_adi: "Saffat", text: "فَرَاغَ اِلٰٓى اٰلِهَتِهِمْ فَقَالَ اَلَا تَأْكُلُونَۚ", translation: "Onların ilahlarına yanaşarak: \"Yemez misiniz?\" dedi.", transliteration: "Feraga ila alihetihim fe kale e la te'kulun."},
  {id: 3880, sure_no: 92, ayet_no: 92, sure_adi: "Saffat", text: "مَا لَكُمْ لَا تَنْطِقُونَ", translation: "\"Neyiniz var? Neden konuşmuyorsunuz?\"", transliteration: "Ma lekum la tentıkun."},
  {id: 3881, sure_no: 93, ayet_no: 93, sure_adi: "Saffat", text: "فَرَاغَ عَلَيْهِمْ ضَرْباً بِالْيَم۪ينِ", translation: "Üzerlerine yürüyüp sağıyla vurdu.", transliteration: "Feraga aleyhim darben bil yemin."},
  {id: 3882, sure_no: 94, ayet_no: 94, sure_adi: "Saffat", text: "فَاَقْبَلُٓوا اِلَيْهِ يَزِفُّونَ", translation: "Bunun üzerine koşuşturarak ona yöneldiler.", transliteration: "Fe akbelu ileyhi yeziffun."},
  {id: 3883, sure_no: 95, ayet_no: 95, sure_adi: "Saffat", text: "قَالَ اَتَعْبُدُونَ مَا تَنْحِتُونَۙ", translation: "\"Siz yonttuğunuz şeylere mi kulluk ediyorsunuz?\"", transliteration: "Kale e ta'budune ma tenhıtun."},
  {id: 3884, sure_no: 96, ayet_no: 96, sure_adi: "Saffat", text: "وَاللّٰهُ خَلَقَكُمْ وَمَا تَعْمَلُونَ", translation: "\"Sizi de yaptığınız şeyleri de Allah yarattı.\"", transliteration: "Vallahu halakakum ve ma ta'melun."},
  {id: 3885, sure_no: 97, ayet_no: 97, sure_adi: "Saffat", text: "قَالُوا ابْنُوا لَهُ بُنْيَاناً فَاَلْقُوهُ فِي الْجَح۪يمِ", translation: "\"Onun için bir yer hazırlayın, sonra da onu cehenneme atın.\" dediler.", transliteration: "Kalubnu lehu bunyanen fe elkuhu fil cahim."},
  {id: 3886, sure_no: 98, ayet_no: 98, sure_adi: "Saffat", text: "فَاَرَادُوا بِه۪ كَيْداً فَجَعَلْنَاهُمُ الْاَسْفَل۪ينَ", translation: "Ona tuzak kurmak istediler. Tuzaklarını boşa çıkarıp onları aşağılık bir duruma düşürdük.", transliteration: "Fe eradu bihi keyden fe cealna humul esfelin."},
  {id: 3887, sure_no: 99, ayet_no: 99, sure_adi: "Saffat", text: "وَقَالَ اِنّ۪ي ذَاهِبٌ اِلٰى رَبّ۪ي سَيَهْد۪ينِ", translation: "İbrahim: \"Ben, Rabb'ime gidiyorum, O, bana yol gösterecek.\" dedi.", transliteration: "Ve kale inni zahibun ila rabbi seyehdin."},
  {id: 3888, sure_no: 100, ayet_no: 100, sure_adi: "Saffat", text: "رَبِّ هَبْ ل۪ي مِنَ الصَّالِح۪ينَ", translation: "\"Rabb'im! Bana salihlerden bağışla.\"", transliteration: "Rabbi heb li mines salihin."},
  {id: 3889, sure_no: 101, ayet_no: 101, sure_adi: "Saffat", text: "فَبَشَّرْنَاهُ بِغُلَامٍ حَل۪يمٍ", translation: "Bunun üzerine onu yumuşak huylu bir oğulla müjdeledik.", transliteration: "Fe beşşernahu bi gulamin halim."},
  {id: 3890, sure_no: 102, ayet_no: 102, sure_adi: "Saffat", text: "فَلَمَّا بَلَغَ مَعَهُ السَّعْيَ قَالَ يَا بُنَيَّ اِنّ۪ٓي اَرٰى فِي الْمَنَامِ اَنّ۪ٓي اَذْبَحُكَ فَانْظُرْ مَاذَا تَرٰىۜ قَالَ يَٓا اَبَتِ افْعَلْ مَا تُؤْمَرُۘ سَتَجِدُن۪ٓي اِنْ شَٓاءَ اللّٰهُ مِنَ الصَّابِر۪ينَ", translation: "Çocuk babasıyla birlikte iş tutacak çağa eriştiği zaman, babası: \"Ey oğulcuğum! Ben, uykumda seni boğazladığımı görüyorum. Bir düşün bakalım, sen ne dersin?\" dedi. Çocuk: \"Ey babacığım! Sana buyurulanı yap. İnşallah beni sabredenlerden bulacaksın.\" dedi.", transliteration: "Fe lemma belega meahus sa'ye kale ya buneyye inni era fil menami enni ezbehuke fanzur maza tera, kale ya ebetif'al ma tu'meru seteciduni inşaallahu mines sabirin."},
  {id: 3891, sure_no: 103, ayet_no: 103, sure_adi: "Saffat", text: "فَلَمَّٓا اَسْلَمَا وَتَلَّهُ لِلْجَب۪ينِۚ", translation: "Sonra, ikisi de teslim olup, yanı üzere getirdi,", transliteration: "Fe lemma eslema ve tellehu lil cebin."},
  {id: 3892, sure_no: 104, ayet_no: 104, sure_adi: "Saffat", text: "وَنَادَيْنَاهُ اَنْ يَٓا اِبْرٰه۪يمُۙ", translation: "\"Ey İbrahim!\" diye ona seslendik.", transliteration: "Ve nadeynahu en ya ibrahim."},
  {id: 3893, sure_no: 105, ayet_no: 105, sure_adi: "Saffat", text: "قَدْ صَدَّقْتَ الرُّءْيَاۚ اِنَّا كَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "Sen kesinlikle o rüyayı doğruladın. Biz, Muhsinlere işte böyle karşılık veririz.", transliteration: "Kad saddakter ru'ya, inna kezalike neczil muhsinin."},
  {id: 3894, sure_no: 106, ayet_no: 106, sure_adi: "Saffat", text: "اِنَّ هٰذَا لَهُوَ الْبَلٰٓؤُا الْمُب۪ينُ", translation: "Bu, kesin olarak apaçık bir beladır.", transliteration: "İnne haza le huvel belaul mubin."},
  {id: 3895, sure_no: 107, ayet_no: 107, sure_adi: "Saffat", text: "وَفَدَيْنَاهُ بِذِبْحٍ عَظ۪يمٍ", translation: "Ona fidye olarak büyük bir kurbanlık verdik.", transliteration: "Ve fedeynahu bi zibhın azim."},
  {id: 3896, sure_no: 108, ayet_no: 108, sure_adi: "Saffat", text: "وَتَرَكْنَا عَلَيْهِ فِي الْاٰخِر۪ينَ", translation: "Gelecek nesiller arasında ona iyi bir ün bıraktık.", transliteration: "Ve terekna aleyhi fil ahirin."},
  {id: 3897, sure_no: 109, ayet_no: 109, sure_adi: "Saffat", text: "سَلَامٌ عَلٰٓى اِبْرٰه۪يمَ", translation: "İbrahim'e selam olsun.", transliteration: "Selamun ala ibrahim."},
  {id: 3898, sure_no: 110, ayet_no: 110, sure_adi: "Saffat", text: "كَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "İşte Biz, iyilere böyle karşılık veririz.", transliteration: "Kezalike neczil muhsinin."},
  {id: 3899, sure_no: 111, ayet_no: 111, sure_adi: "Saffat", text: "اِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِن۪ينَ", translation: "O, Bizim Mü'min kullarımızdandı.", transliteration: "İnnehu min ibadinel mu'minin."},
  {id: 3900, sure_no: 112, ayet_no: 112, sure_adi: "Saffat", text: "وَبَشَّرْنَاهُ بِاِسْحٰقَ نَبِياًّ مِنَ الصَّالِح۪ينَ", translation: "Biz, onu salihlerden bir nebi olan İshak ile müjdeledik.", transliteration: "Ve beşşernahu bi ishaka nebiyyen mines salihin."},
  {id: 3901, sure_no: 113, ayet_no: 113, sure_adi: "Saffat", text: "وَبَارَكْنَا عَلَيْهِ وَعَلٰٓى اِسْحٰقَۜ وَمِنْ ذُرِّيَّتِهِمَا مُحْسِنٌ وَظَالِمٌ لِنَفْسِه۪ مُب۪ينٌ۟", translation: "Ona ve İshak'a bereket verdik. Her ikisinin soyundan muhsin olan da kendisine açıkça zulmeden de vardır.", transliteration: "Ve barekna aleyhi ve ala ishak, ve min zurriyyetihima muhsinun ve zalimun li nefsihi mubin."},
  {id: 3902, sure_no: 114, ayet_no: 114, sure_adi: "Saffat", text: "وَلَقَدْ مَنَنَّا عَلٰى مُوسٰى وَهٰرُونَۚ", translation: "Ant olsun ki Musa ve Harun'a nimetler verdik.", transliteration: "Ve lekad menenna ala musa ve harun."},
  {id: 3903, sure_no: 115, ayet_no: 115, sure_adi: "Saffat", text: "وَنَجَّيْنَاهُمَا وَقَوْمَهُمَا مِنَ الْكَرْبِ الْعَظ۪يمِۚ", translation: "O ikisini ve onlarla birlikte hareket eden halkı büyük sıkıntıdan kurtardık.", transliteration: "Ve necceyna huma ve kavme huma minel kerbil azim."},
  {id: 3904, sure_no: 116, ayet_no: 116, sure_adi: "Saffat", text: "وَنَصَرْنَاهُمْ فَكَانُوا هُمُ الْغَالِب۪ينَۚ", translation: "Onlara yardım ettik. Galip gelenler onlar oldular.", transliteration: "Ve nasarnahum fe kanu humul galibin."},
  {id: 3905, sure_no: 117, ayet_no: 117, sure_adi: "Saffat", text: "وَاٰتَيْنَاهُمَا الْكِتَابَ الْمُسْتَب۪ينَۚ", translation: "Onlara gerçekleri açıklayan kitap verdik.", transliteration: "Ve ateyna humel kitabel mustebin."},
  {id: 3906, sure_no: 118, ayet_no: 118, sure_adi: "Saffat", text: "وَهَدَيْنَاهُمَا الصِّرَاطَ الْمُسْتَق۪يمَۚ", translation: "Onları dosdoğru yola ilettik.", transliteration: "Ve hedeyna humes sıratal mustekim."},
  {id: 3907, sure_no: 119, ayet_no: 119, sure_adi: "Saffat", text: "وَتَرَكْنَا عَلَيْهِمَا فِي الْاٰخِر۪ينَ", translation: "Gelecek nesiller arasında ona iyi bir ün bıraktık.", transliteration: "Ve terekna aleyhima fil ahirin."},
  {id: 3908, sure_no: 120, ayet_no: 120, sure_adi: "Saffat", text: "سَلَامٌ عَلٰى مُوسٰى وَهٰرُونَ", translation: "Musa ve Harun'a selam olsun.", transliteration: "Selamun ala musa ve harun."},
  {id: 3909, sure_no: 121, ayet_no: 121, sure_adi: "Saffat", text: "اِنَّا كَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "Biz, muhsinleri böyle ödüllendiririz.", transliteration: "İnna kezalike neczil muhsinin."},
  {id: 3910, sure_no: 122, ayet_no: 122, sure_adi: "Saffat", text: "اِنَّهُمَا مِنْ عِبَادِنَا الْمُؤْمِن۪ينَ", translation: "İkisi de Bizim Mü'min kullarımızdandı.", transliteration: "İnne huma min ibadinel mu'minin."},
  {id: 3911, sure_no: 123, ayet_no: 123, sure_adi: "Saffat", text: "وَاِنَّ اِلْيَاسَ لَمِنَ الْمُرْسَل۪ينَۜ", translation: "İlyas da kesinlikle gönderilenlerdendir.", transliteration: "Ve inne ilyase le minel murselin."},
  {id: 3912, sure_no: 124, ayet_no: 124, sure_adi: "Saffat", text: "اِذْ قَالَ لِقَوْمِه۪ٓ اَلَا تَتَّقُونَ", translation: "Hani o, halkına: \"Siz, takva sahibi olmayacak mısınız?\" demişti.", transliteration: "İz kale li kavmihi e la tettekun."},
  {id: 3913, sure_no: 125, ayet_no: 125, sure_adi: "Saffat", text: "اَتَدْعُونَ بَعْلاً وَتَذَرُونَ اَحْسَنَ الْخَالِق۪ينَۙ", translation: "\"Yaratanların en iyisini bırakıp da Ba'le mi yöneliyorsunuz?\"", transliteration: "Eted'une ba'len ve tezerune ahsenel halikin."},
  {id: 3914, sure_no: 126, ayet_no: 126, sure_adi: "Saffat", text: "اَللّٰهَ رَبَّكُمْ وَرَبَّ اٰبَٓائِكُمُ الْاَوَّل۪ينَ", translation: "\"Allah, sizin ve atalarınızın Rabb'idir.\"", transliteration: "Allahe rabbekum ve rabbe abaikumul evvelin."},
  {id: 3915, sure_no: 127, ayet_no: 127, sure_adi: "Saffat", text: "فَكَذَّبُوهُ فَاِنَّهُمْ لَمُحْضَرُونَۙ", translation: "Ancak onu yalanladılar. Kuşkusuz onlar hazır bulundurulacak olanlardır.", transliteration: "Fe kezzebuhu fe inne hum le muhdarun."},
  {id: 3916, sure_no: 128, ayet_no: 128, sure_adi: "Saffat", text: "اِلَّا عِبَادَ اللّٰهِ الْمُخْلَص۪ينَ", translation: "Allah'ın muhles kulları hariç.", transliteration: "İlla ibadallahil muhlasin."},
  {id: 3917, sure_no: 129, ayet_no: 129, sure_adi: "Saffat", text: "وَتَرَكْنَا عَلَيْهِ فِي الْاٰخِر۪ينَ", translation: "Gelecek nesiller arasında ona iyi bir ün bıraktık.", transliteration: "Ve terekna aleyhi fil ahirin."},
  {id: 3918, sure_no: 130, ayet_no: 130, sure_adi: "Saffat", text: "سَلَامٌ عَلٰٓى اِلْ‌يَاس۪ينَ", translation: "İlyas'a selam olsun.", transliteration: "Selamun ala ilyasin."},
  {id: 3919, sure_no: 131, ayet_no: 131, sure_adi: "Saffat", text: "اِنَّا كَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "İşte Biz, iyilere böyle karşılık veririz.", transliteration: "İnna kezalike neczil muhsinin."},
  {id: 3920, sure_no: 132, ayet_no: 132, sure_adi: "Saffat", text: "اِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِن۪ينَ", translation: "O, Bizim Mü'min kullarımızdandı.", transliteration: "İnnehu min ibadinel mu'minin."},
  {id: 3921, sure_no: 133, ayet_no: 133, sure_adi: "Saffat", text: "وَاِنَّ لُوطاً لَمِنَ الْمُرْسَل۪ينَۜ", translation: "Lut da kesinlikle gönderilenlerdendir.", transliteration: "Ve inne lutan le minel murselin."},
  {id: 3922, sure_no: 134, ayet_no: 134, sure_adi: "Saffat", text: "اِذْ نَجَّيْنَاهُ وَاَهْلَـهُٓ اَجْمَع۪ينَۙ", translation: "Hani onu ve yanında yer alanların tamamını kurtarmıştık.", transliteration: "İz necceynahu ve ehlehu ecmain."},
  {id: 3923, sure_no: 135, ayet_no: 135, sure_adi: "Saffat", text: "اِلَّا عَجُوزاً فِي الْغَابِر۪ينَ", translation: "Ancak geride kalan acuze bir kadın hariç.", transliteration: "İlla acuzen fil gabirin."},
  {id: 3924, sure_no: 136, ayet_no: 136, sure_adi: "Saffat", text: "ثُمَّ دَمَّرْنَا الْاٰخَر۪ينَ", translation: "Sonra diğerlerini dumura uğrattık.", transliteration: "Summe demmernel aharin."},
  {id: 3925, sure_no: 137, ayet_no: 137, sure_adi: "Saffat", text: "وَاِنَّكُمْ لَتَمُرُّونَ عَلَيْهِمْ مُصْبِح۪ينَۙ", translation: "Siz, gündüz onların yanlarından geçip gidiyorsunuz.", transliteration: "Ve innekum le temurrune aleyhim musbihin."},
  {id: 3926, sure_no: 138, ayet_no: 138, sure_adi: "Saffat", text: "وَبِالَّيْلِۜ اَفَلَا تَعْقِلُونَ۟", translation: "Ve geceleyin de. Hala aklınızı kullanmayacak mısınız?", transliteration: "Ve bil leyl, e fe la ta'kılun."},
  {id: 3927, sure_no: 139, ayet_no: 139, sure_adi: "Saffat", text: "وَاِنَّ يُونُسَ لَمِنَ الْمُرْسَل۪ينَۜ", translation: "Yunus da kesinlikle gönderilenlerdendir.", transliteration: "Ve inne yunuse le minel murselin."},
  {id: 3928, sure_no: 140, ayet_no: 140, sure_adi: "Saffat", text: "اِذْ اَبَقَ اِلَى الْفُلْكِ الْمَشْحُونِۙ", translation: "Hani o, dolu gemiye kaçmıştı.", transliteration: "İz ebeka ilel fulkil meşhun."},
  {id: 3929, sure_no: 141, ayet_no: 141, sure_adi: "Saffat", text: "فَسَاهَمَ فَكَانَ مِنَ الْمُدْحَض۪ينَۚ", translation: "Sonra kura çekti ve kaybedenlerden oldu.", transliteration: "Fe saheme fe kane minel mudhadin."},
  {id: 3930, sure_no: 142, ayet_no: 142, sure_adi: "Saffat", text: "فَالْتَقَمَهُ الْحُوتُ وَهُوَ مُل۪يمٌ", translation: "O kendisini suçlayıp dururken, hut onu yuttu.", transliteration: "Feltekamehul hutu ve huve mulim."},
  {id: 3931, sure_no: 143, ayet_no: 143, sure_adi: "Saffat", text: "فَلَوْلَٓا اَنَّهُ كَانَ مِنَ الْمُسَبِّح۪ينَۙ", translation: "Eğer o gerçekten tesbih edenlerden olmasaydı;", transliteration: "Fe lev la ennehu kane minel musebbihin."},
  {id: 3932, sure_no: 144, ayet_no: 144, sure_adi: "Saffat", text: "لَلَبِثَ ف۪ي بَطْنِه۪ٓ اِلٰى يَوْمِ يُبْعَثُونَ", translation: "Diriltilecekleri güne kadar balığın karnında kalırdı.", transliteration: "Le lebise fi batnihi ila yevmi yub'asun."},
  {id: 3933, sure_no: 145, ayet_no: 145, sure_adi: "Saffat", text: "فَنَبَذْنَاهُ بِالْعَرَٓاءِ وَهُوَ سَق۪يمٌۚ", translation: "Sonunda, onu bitkin bir halde ıssız bir yere attık.", transliteration: "Fe nebeznahu bil arai ve huve sakim."},
  {id: 3934, sure_no: 146, ayet_no: 146, sure_adi: "Saffat", text: "وَاَنْبَتْنَا عَلَيْهِ شَجَرَةً مِنْ يَقْط۪ينٍۚ", translation: "Onun üzerine geniş yapraklılardan bir ağaç bitirdik.", transliteration: "Ve enbetna aleyhi şecereten min yaktin."},
  {id: 3935, sure_no: 147, ayet_no: 147, sure_adi: "Saffat", text: "وَاَرْسَلْنَاهُ اِلٰى مِائَةِ اَلْفٍ اَوْ يَز۪يدُونَۚ", translation: "Onu, nüfusu yüz binden fazla bir halka Resul olarak gönderdik.", transliteration: "Ve erselnahu ila mieti elfin ev yezidun."},
  {id: 3936, sure_no: 148, ayet_no: 148, sure_adi: "Saffat", text: "فَاٰمَنُوا فَمَتَّعْنَاهُمْ اِلٰى ح۪ينٍۜ", translation: "Bu sefer iman ettiler. Biz de onları belli bir süre yararlandırdık.", transliteration: "Fe amenu fe metta'nahum ila hin."},
  {id: 3937, sure_no: 149, ayet_no: 149, sure_adi: "Saffat", text: "فَاسْتَفْتِهِمْ اَلِرَبِّكَ الْبَنَاتُ وَلَهُمُ الْبَنُونَۙ", translation: "Haydi, onlara sor: \"Kız çocuklar Rabb'inin de oğlan çocuklar onların mı?\"", transliteration: "Festeftihim e li rabbikel benatu ve lehumul benun."},
  {id: 3938, sure_no: 150, ayet_no: 150, sure_adi: "Saffat", text: "اَمْ خَلَقْنَا الْمَلٰٓئِكَةَ اِنَاثاً وَهُمْ شَاهِدُونَ", translation: "Yoksa Biz melekleri dişi olarak yarattık da onlar buna tanık mı oldular?", transliteration: "Em halaknel melaikete inasen ve hum şahidun."},
  {id: 3939, sure_no: 151, ayet_no: 151, sure_adi: "Saffat", text: "اَلَٓا اِنَّهُمْ مِنْ اِفْكِهِمْ لَيَقُولُونَۙ", translation: "Dikkat edin! Doğrusu uydurdukları iftiralarından dolayı;", transliteration: "E la innehum min ifkihim le yekulun."},
  {id: 3940, sure_no: 152, ayet_no: 152, sure_adi: "Saffat", text: "وَلَدَ اللّٰهُۙ وَاِنَّهُمْ لَكَاذِبُونَ", translation: "\"Allah'ın çocuğu oldu.\" diyorlar. Onlar, kesinlikle yalancıdırlar.", transliteration: "Veledallahu ve innehum le kazibun."},
  {id: 3941, sure_no: 153, ayet_no: 153, sure_adi: "Saffat", text: "اَصْطَفَى الْبَنَاتِ عَلَى الْبَن۪ينَۜ", translation: "Allah; kızları, oğullara tercih etmiş öyle mi?", transliteration: "Astafel benati alel benin."},
  {id: 3942, sure_no: 154, ayet_no: 154, sure_adi: "Saffat", text: "مَا لَـكُمْ۠  كَيْفَ تَحْكُمُونَ", translation: "Size ne oluyor? Nasıl böyle bir hüküm veriyorsunuz?", transliteration: "Ma lekum, keyfe tahkumun."},
  {id: 3943, sure_no: 155, ayet_no: 155, sure_adi: "Saffat", text: "اَفَلَا تَذَكَّرُونَۚ", translation: "Hiç mi öğüt almıyorsunuz?", transliteration: "E fe la tezekkerun."},
  {id: 3944, sure_no: 156, ayet_no: 156, sure_adi: "Saffat", text: "اَمْ لَـكُمْ سُلْطَانٌ مُب۪ينٌۙ", translation: "Yoksa elinizde açık bir belgeniz mi var?", transliteration: "Em lekum sultanun mubin."},
  {id: 3945, sure_no: 157, ayet_no: 157, sure_adi: "Saffat", text: "فَأْتُوا بِكِتَابِكُمْ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Eğer doğru söyleyenlerdenseniz, o halde kitabınızı getirin.", transliteration: "Fe'tu bi kitabikum in kuntum sadikin."},
  {id: 3946, sure_no: 158, ayet_no: 158, sure_adi: "Saffat", text: "وَجَعَلُوا بَيْنَهُ وَبَيْنَ الْجِنَّةِ نَسَباًۜ وَلَقَدْ عَلِمَتِ الْجِنَّةُ اِنَّهُمْ لَمُحْضَرُونَۙ", translation: "Allah ile cinnler arasında soy bağı uydurdular. Ant olsun ki cinnler de kesinlikle onların hazır bulundurulacaklarını bilmektedirler.", transliteration: "Ve cealu beynehu ve beynel cinneti neseba, ve lekad alimetil cinnetu innehum le muhdarun."},
  {id: 3947, sure_no: 159, ayet_no: 159, sure_adi: "Saffat", text: "سُبْحَانَ اللّٰهِ عَمَّا يَصِفُونَۙ", translation: "Allah, onların niteledikleri şeyden münezzehtir.", transliteration: "Subhanallahi amma yasifun."},
  {id: 3948, sure_no: 160, ayet_no: 160, sure_adi: "Saffat", text: "اِلَّا عِبَادَ اللّٰهِ الْمُخْلَص۪ينَ", translation: "Allah'ın muhles kulları hariç.", transliteration: "İlla ibadallahil muhlasin."},
  {id: 3949, sure_no: 161, ayet_no: 161, sure_adi: "Saffat", text: "فَاِنَّكُمْ وَمَا تَعْبُدُونَۙ", translation: "Artık siz ve kulluk yaptıklarınız,", transliteration: "Fe innekum ve ma ta'budun."},
  {id: 3950, sure_no: 162, ayet_no: 162, sure_adi: "Saffat", text: "مَٓا اَنْتُمْ عَلَيْهِ بِفَاتِن۪ينَۙ", translation: "O'na karşı fitneye düşüremezsiniz.", transliteration: "Ma entum aleyhi bi fatinin."},
  {id: 3951, sure_no: 163, ayet_no: 163, sure_adi: "Saffat", text: "اِلَّا مَنْ هُوَ صَالِ الْجَح۪يمِ", translation: "Cehennem'e girecekler hariç.", transliteration: "İlla men huve salil cahim."},
  {id: 3952, sure_no: 164, ayet_no: 164, sure_adi: "Saffat", text: "وَمَا مِنَّٓا اِلَّا لَهُ مَقَامٌ مَعْلُومٌ", translation: "\"Bizden her birimizin belli bir makamı vardır.\"", transliteration: "Ve ma minna illa lehu makamun ma'lum."},
  {id: 3953, sure_no: 165, ayet_no: 165, sure_adi: "Saffat", text: "وَاِنَّا لَنَحْنُ الصَّٓافُّونَۚ", translation: "\"Biziz biz, saf saf duranlar.\"", transliteration: "Ve inna le nahnus saffun."},
  {id: 3954, sure_no: 166, ayet_no: 166, sure_adi: "Saffat", text: "وَاِنَّا لَنَحْنُ الْمُسَبِّحُونَ", translation: "\"Biziz biz, tesbih edenler.\"", transliteration: "Ve inna le nahnul musebbihun."},
  {id: 3955, sure_no: 167, ayet_no: 167, sure_adi: "Saffat", text: "وَاِنْ كَانُوا لَيَقُولُونَۙ", translation: "Eğer diyecek olurlarsa:", transliteration: "Ve in kanu le yekulun."},
  {id: 3956, sure_no: 168, ayet_no: 168, sure_adi: "Saffat", text: "لَوْ اَنَّ عِنْدَنَا ذِكْراً مِنَ الْاَوَّل۪ينَۙ", translation: "\"Yanımızda öncekilere verilen öğüt gibi bir öğüt olsaydı.\"", transliteration: "Lev enne indena zikren minel evvelin."},
  {id: 3957, sure_no: 169, ayet_no: 169, sure_adi: "Saffat", text: "لَـكُنَّا عِبَادَ اللّٰهِ الْمُخْلَص۪ينَ", translation: "\"Biz de Allah'ın muhles kullarından olurduk.\"", transliteration: "Le kunna ibadallahil muhlasin."},
  {id: 3958, sure_no: 170, ayet_no: 170, sure_adi: "Saffat", text: "فَـكَفَرُوا بِه۪ۚ فَسَوْفَ يَعْلَمُونَ", translation: "Fakat onu yalanladılar. Artık yakında bilecekler.", transliteration: "Fe keferu bih, fe sevfe ya'lemun."},
  {id: 3959, sure_no: 171, ayet_no: 171, sure_adi: "Saffat", text: "وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا الْمُرْسَل۪ينَۚ", translation: "Ant olsun ki gönderilen kullarımıza şu sözü verdik:", transliteration: "Ve lekad sebekat kelimetuna li ibadinel murselin."},
  {id: 3960, sure_no: 172, ayet_no: 172, sure_adi: "Saffat", text: "اِنَّهُمْ لَهُمُ الْمَنْصُورُونَۖ", translation: "Onlar, kesinlikle yardım edilecek olanlardır.", transliteration: "İnnehum le humul mensurun."},
  {id: 3961, sure_no: 173, ayet_no: 173, sure_adi: "Saffat", text: "وَاِنَّ جُنْدَنَا لَهُمُ الْغَالِبُونَ", translation: "Bizim ordumuz onlara mutlaka galip gelecektir.", transliteration: "Ve inne cundena le humul galibun."},
  {id: 3962, sure_no: 174, ayet_no: 174, sure_adi: "Saffat", text: "فَتَوَلَّ عَنْهُمْ حَتّٰى ح۪ينٍۙ", translation: "Artık bir süre onlardan yüz çevir.", transliteration: "Fe tevelle anhum hatta hin."},
  {id: 3963, sure_no: 175, ayet_no: 175, sure_adi: "Saffat", text: "وَاَبْصِرْهُمْ فَسَوْفَ يُبْصِرُونَ", translation: "Onları gözle! Yakında onlar da görecekler.", transliteration: "Ve ebsirhum fe sevfe yubsirun."},
  {id: 3964, sure_no: 176, ayet_no: 176, sure_adi: "Saffat", text: "اَفَبِعَذَابِنَا يَسْتَعْجِلُونَ", translation: "Yoksa azabımızı acele mi istiyorlar?", transliteration: "E fe bi azabina yesta'cilun."},
  {id: 3965, sure_no: 177, ayet_no: 177, sure_adi: "Saffat", text: "فَاِذَا نَزَلَ بِسَاحَتِهِمْ فَسَٓاءَ صَبَاحُ الْمُنْذَر۪ينَ", translation: "Fakat onların bulundukları yere indiği zaman, uyarılanların sabahı ne kötüdür!", transliteration: "Fe iza nezele bisahatihim fe sae sabahul munzerin."},
  {id: 3966, sure_no: 178, ayet_no: 178, sure_adi: "Saffat", text: "وَتَوَلَّ عَنْهُمْ حَتّٰى ح۪ينٍۙ", translation: "Artık bir süre onlardan yüz çevir.", transliteration: "Ve tevelle anhum hatta hin."},
  {id: 3967, sure_no: 179, ayet_no: 179, sure_adi: "Saffat", text: "وَاَبْصِرْ فَسَوْفَ يُبْصِرُونَ", translation: "Onları gözle! Yakında onlar da görecekler.", transliteration: "Ve ebsir fe sevfe yubsirun."},
  {id: 3968, sure_no: 180, ayet_no: 180, sure_adi: "Saffat", text: "سُبْحَانَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُونَۚ", translation: "İzzetin Rabb'i olan Rabb'in, onların nitelediği şeylerden münezzehtir.", transliteration: "Subhane rabbike rabbil izzeti amma yasifun."},
  {id: 3969, sure_no: 181, ayet_no: 181, sure_adi: "Saffat", text: "وَسَلَامٌ عَلَى الْمُرْسَل۪ينَۚ", translation: "Gönderilenlere selam olsun.", transliteration: "Ve selamun alel murselin."},
  {id: 3970, sure_no: 182, ayet_no: 182, sure_adi: "Saffat", text: "وَالْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَ", translation: "Hamd, alemlerin Rabb'i Allah'adır.", transliteration: "Vel hamdu lillahi rabbil alemin."},
  {id: 3971, sure_no: 1, ayet_no: 1, sure_adi: "Sad", text: "صٓ وَالْقُرْاٰنِ ذِي الذِّكْرِۜ", translation: "Sad. Zikir sahibi Kur'an'a ant olsun.", transliteration: "Sad, vel kur'ani ziz zikr."},
  {id: 3972, sure_no: 2, ayet_no: 2, sure_adi: "Sad", text: "بَلِ الَّذ۪ينَ كَفَرُوا ف۪ي عِزَّةٍ وَشِقَاقٍ", translation: "Doğrusu Kafirler büyüklenmelerine yediremediklerinden muhalefet ediyorlar.", transliteration: "Belillezine keferu fi ızzetin ve şikak."},
  {id: 3973, sure_no: 3, ayet_no: 3, sure_adi: "Sad", text: "كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ فَنَادَوْا وَلَاتَ ح۪ينَ مَنَاصٍ", translation: "Onlardan önce nice kuşakları yok ettik. O zaman feryat ettiler, ama artık kurtuluş vakti geçmişti.", transliteration: "Kem ehlekna min kablihim min karnin fe nadev ve late hine menas."},
  {id: 3974, sure_no: 4, ayet_no: 4, sure_adi: "Sad", text: "وَعَجِبُٓوا اَنْ جَٓاءَهُمْ مُنْذِرٌ مِنْهُمْۘ وَقَالَ الْكَافِرُونَ هٰذَا سَاحِرٌ كَذَّابٌۚ", translation: "İçlerinden, kendilerine bir uyarıcı gelmesine şaşırdılar. Kafirler: \"Bu, yalancı bir büyücüdür.\" dediler.", transliteration: "Ve acibu en caehum munzirun minhum ve kalel kafirune haza sahırun kezzab."},
  {id: 3975, sure_no: 5, ayet_no: 5, sure_adi: "Sad", text: "اَجَعَلَ الْاٰلِهَةَ اِلٰهاً وَاحِداًۚ اِنَّ هٰذَا لَشَيْءٌ عُجَابٌ", translation: "\"O, bunca ilahı, bir tek ilah mı kılıyor? Kuşkusuz bu şaşılacak bir şeydir.\"", transliteration: "E cealel alihete ilahen vahıda, inne haza le şey'un ucab."},
  {id: 3976, sure_no: 6, ayet_no: 6, sure_adi: "Sad", text: "وَانْطَلَقَ الْمَلَأُ مِنْهُمْ اَنِ امْشُوا وَاصْبِرُوا عَلٰٓى اٰلِهَتِكُمْۚ اِنَّ هٰذَا لَشَيْءٌ يُرَادُۚ", translation: "Onlardan, meleler harekete geçerek: \"Bildiğiniz yoldan gitmeye devam edin, ilahlarınızı bırakmayın. Kesinlikle sizden beklenen budur.\" dediler.", transliteration: "Ventalekal meleu minhum enimşu vasbiru ala alihetikum inne haza le şey'un yurad."},
  {id: 3977, sure_no: 7, ayet_no: 7, sure_adi: "Sad", text: "مَا سَمِعْنَا بِهٰذَا فِي الْمِلَّةِ الْاٰخِرَةِۚ  اِنْ هٰذَٓا اِلَّا اخْتِلَاقٌۚ", translation: "\"Biz, bunu son inanç sistemlerinde de duymadık. Bu yalnızca bir uydurmadır.\"", transliteration: "Ma semi'na bi haza fil milletil ahıreh, in haza illahtilak."},
  {id: 3978, sure_no: 8, ayet_no: 8, sure_adi: "Sad", text: "ءَاُنْزِلَ عَلَيْهِ الذِّكْرُ مِنْ بَيْنِنَاۜ بَلْ هُمْ ف۪ي شَكٍّ مِنْ ذِكْر۪يۚ بَلْ لَمَّا يَذُوقُوا عَذَابِۜ", translation: "\"Zikir, aramızda ona mı indirildi?\" Oysaki onlar Benim zikrimden kuşku içindedirler. Hayır, onlar azabımı henüz tatmadılar.", transliteration: "E unzile aleyhiz zikru min beynina, bel hum fi şekkin min zikri, bel lemma yezuku azab."},
  {id: 3979, sure_no: 9, ayet_no: 9, sure_adi: "Sad", text: "اَمْ عِنْدَهُمْ خَزَٓائِنُ رَحْمَةِ رَبِّكَ الْعَز۪يزِ الْوَهَّابِۚ", translation: "Yoksa Çok Yüce ve Çok Bağışta Bulunan Rabb'inin rahmet hazineleri onların yanında mı?", transliteration: "Em indehum hazainu rahmeti rabbikel azizil vehhab."},
  {id: 3980, sure_no: 10, ayet_no: 10, sure_adi: "Sad", text: "اَمْ لَهُمْ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا۠ فَلْيَرْتَقُوا فِي الْاَسْبَابِ", translation: "Ya da göklerin, yerin ve ikisi arasındakilerin mülkü onlara mı ait? O halde sebepler bulsunlar da yükselsinler!", transliteration: "Em lehum mulkus semavati vel ardı ve ma beynehuma, felyerteku fil esbab."},
  {id: 3981, sure_no: 11, ayet_no: 11, sure_adi: "Sad", text: "جُنْدٌ مَا هُنَالِكَ مَهْزُومٌ مِنَ الْاَحْزَابِ", translation: "Onlar, burada bozguna uğramış gruplardan meydana gelmiş bir ordudur.", transliteration: "Cundun ma hunalike mehzumun minel ahzab."},
  {id: 3982, sure_no: 12, ayet_no: 12, sure_adi: "Sad", text: "كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَفِرْعَوْنُ ذُوالْاَوْتَادِۙ", translation: "Onlardan önce Nuh toplumu, Ad, kazıklar sahibi Firavun da yalanlamıştı.", transliteration: "Kezzebet kablehum kavmu nuhın ve adun ve fir'avnu zul evtadi."},
  {id: 3983, sure_no: 13, ayet_no: 13, sure_adi: "Sad", text: "وَثَمُودُ وَقَوْمُ لُوطٍ وَاَصْحَابُ لْـَٔيْكَةِۜ  اُو۬لٰٓئِكَ الْاَحْزَابُ", translation: "Ve Semud, Lut'un halkı ve Eyke halkı; işte onlar da işbirlikçi gruplardır.", transliteration: "Ve semudu ve kavmu lutın ve ashabul eykeh, ulaikel ahzab."},
  {id: 3984, sure_no: 14, ayet_no: 14, sure_adi: "Sad", text: "اِنْ كُلٌّ اِلَّا كَذَّبَ الرُّسُلَ فَحَقَّ عِقَابِ۟", translation: "Onların hepsi de Resulleri yalanladı. Bu nedenle azabımı hak ettiler.", transliteration: "İn kullun illa kezzeber rusule fe hakka ıkab."},
  {id: 3985, sure_no: 15, ayet_no: 15, sure_adi: "Sad", text: "وَمَا يَنْظُرُ هٰٓؤُ۬لَٓاءِ اِلَّا صَيْحَةً وَاحِدَةً مَا لَهَا مِنْ فَوَاقٍ", translation: "Bunlar geri dönüşü olmayan bir çığlıktan başkasını beklemiyorlar.", transliteration: "Ve ma yanzuru haulai illa sayhaten vahıdeten ma leha min fevak."},
  {id: 3986, sure_no: 16, ayet_no: 16, sure_adi: "Sad", text: "وَقَالُوا رَبَّنَا عَجِّلْ لَنَا قِطَّنَا قَبْلَ يَوْمِ الْحِسَابِ", translation: "Ve: \"Rabb''imiz, Hesap Günü'nden önce azaptan payımıza düşeni hemen ver.\" dediler.", transliteration: "Ve kalu rabbena accil lena kıttana kable yevmil hisab."},
  {id: 3987, sure_no: 17, ayet_no: 17, sure_adi: "Sad", text: "اِصْبِرْ عَلٰى مَا يَقُولُونَ وَاذْكُرْ عَبْدَنَا دَاوُ۫دَ ذَا الْاَيْدِۚ اِنَّـهُٓ  اَوَّابٌ", translation: "Onların dediklerine sabret, güçlerin sahibi kulumuz Davud'u düşün. O, her durumda Allah'a yönelirdi.", transliteration: "Isbır ala ma yekulune vezkur abdena davude zel eyd, innehu evvab."},
  {id: 3988, sure_no: 18, ayet_no: 18, sure_adi: "Sad", text: "اِنَّا سَخَّرْنَا الْجِبَالَ مَعَهُ يُسَبِّحْنَ بِالْعَشِيِّ وَالْاِشْرَاقِۙ", translation: "Dağları boyun eğdirdik. Akşamdan gündoğumuna onunla birlikte tesbih ederlerdi.", transliteration: "İnna sahharnel cibale meahu yusebbıhne bil aşiyyi vel işrak."},
  {id: 3989, sure_no: 19, ayet_no: 19, sure_adi: "Sad", text: "وَالطَّيْرَ مَحْشُورَةًۜ   كُلٌّ لَـهُٓ  اَوَّابٌ", translation: "Kuşların tamamı toplu halde ona yönelmişlerdi.", transliteration: "Vet tayre mahşureh, kullun lehu evvab."},
  {id: 3990, sure_no: 20, ayet_no: 20, sure_adi: "Sad", text: "وَشَدَدْنَا مُلْكَهُ وَاٰتَيْنَاهُ الْحِكْمَةَ وَفَصْلَ الْخِطَابِ", translation: "Onun gücünü pekiştirdik. Ona Hikmet'i ve fesle-l hitabı verdik.", transliteration: "Ve şededna mulkehu ve ateynahul hikmete ve faslel hıtab."},
  {id: 3991, sure_no: 21, ayet_no: 21, sure_adi: "Sad", text: "وَهَلْ اَتٰيكَ نَـبَؤُا الْخَصْمِۢ  اِذْ تَسَوَّرُوا الْمِحْرَابَۙ", translation: "Birbirleriyle davalıların haberi sana geldi mi? Duvarı aşarak mihraba gelmişlerdi!", transliteration: "Ve hel etake nebeul hasm, iz tesevverul mihrab."},
  {id: 3992, sure_no: 22, ayet_no: 22, sure_adi: "Sad", text: "اِذْ دَخَلُوا عَلٰى دَاوُ۫دَ فَفَزِعَ مِنْهُمْ قَالُوا لَا تَخَفْۚ خَصْمَانِ بَغٰى بَعْضُنَا عَلٰى بَعْضٍ فَاحْكُمْ بَيْنَنَا بِالْحَقِّ وَلَا تُشْطِطْ وَاهْدِنَٓا اِلٰى سَوَٓاءِ الصِّرَاطِ", translation: "Davud'un yanına girdiklerinde o, onlardan korktu. \"Korkma! İki davacıyız. Birimiz ötekine haksızlık etti. Şimdi sen, hakkımızda hakk ile hüküm ver. Haksızlık etme. Bize makul olan yolu göster.\" dediler.", transliteration: "İz dehalu ala davude fe fezia minhum kalu la tehaf, hasmani bega ba'duna ala ba'dın fahkum beynena bil hakkı ve la tuştıt vehdina ila sevais sırat."},
  {id: 3993, sure_no: 23, ayet_no: 23, sure_adi: "Sad", text: "اِنَّ هٰذَٓا اَخ۪ي لَهُ تِسْعٌ وَتِسْعُونَ نَعْجَةً وَلِيَ نَعْجَةٌ وَاحِدَةٌ فَقَالَ اَكْفِلْن۪يهَا وَعَزَّن۪ي فِي الْخِطَابِ", translation: "\"Bu benim kardeşim. Onun doksan dokuz koyunu var ve benim bir tek koyunum var. Buna rağmen onu da kendisine vermemi istedi ve tartışmamızda bana üstünlük sağladı.\"", transliteration: "İnne haza ahi lehu tis'un ve tis'une na'ceten ve liye na'cetun vahidetun fe kale ekfilniha ve azzeni fil hıtab."},
  {id: 3994, sure_no: 24, ayet_no: 24, sure_adi: "Sad", text: "قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ اِلٰى نِعَاجِه۪ۜ وَاِنَّ كَث۪يراً مِنَ الْخُلَطَٓاءِ لَيَبْغ۪ي بَعْضُهُمْ عَلٰى بَعْضٍ اِلَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَقَل۪يلٌ مَا هُمْۜ وَظَنَّ دَاوُ۫دُ اَنَّمَا فَتَنَّاهُ فَاسْتَغْفَرَ رَبَّهُ وَخَرَّ رَا‌كِعاً وَاَنَابَ", translation: "\"Gerçekten, senin koyununu kendi koyunlarına katmayı istemekle doğrusu sana haksızlık etmiştir. Ortakların çoğu, birbirlerine haksızlık ediyorlar. Ancak iman edenler ve salihatı yapanlar haksızlık etmezler. Ancak onlar da ne kadar azdır!\" dedi. Davud, kendisini fitnelendirdiğimizi iyice anladı. Hemen Rabb'inden bağışlanma diledi, ruku ederek, tam bir teslimiyetle Rabb'ine yöneldi.", transliteration: "Kale lekad zalemeke bi suali na'cetike ila niacih, ve inne kesiren minel huletai le yebgi ba'duhum ala ba'dın illellezine amenu ve amilus salihati ve kalilun ma hum, ve zanne davudu ennema fetennahu festagfere rabbehu ve harre rakian ve enab."},
  {id: 3995, sure_no: 25, ayet_no: 25, sure_adi: "Sad", text: "فَغَفَرْنَا لَهُ ذٰلِكَۜ وَاِنَّ لَهُ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ", translation: "Böylece onu bundan dolayı bağışladık. Onun yanımızda yüksek konumu ve iyi bir sığınağı vardır.", transliteration: "Fe gaferna lehu zalik, ve inne lehu indena le zulfa ve husne meab."},
  {id: 3996, sure_no: 26, ayet_no: 26, sure_adi: "Sad", text: "يَا دَاوُ۫دُ اِنَّا جَعَلْنَاكَ خَل۪يفَةً فِي الْاَرْضِ فَاحْكُمْ بَيْنَ النَّاسِ بِالْحَقِّ وَلَا تَتَّبِعِ الْهَوٰى فَيُضِلَّكَ عَنْ سَب۪يلِ اللّٰهِۜ  اِنَّ الَّذ۪ينَ يَضِلُّونَ عَنْ سَب۪يلِ اللّٰهِ لَهُمْ عَذَابٌ شَد۪يدٌ بِمَا نَسُوا يَوْمَ الْحِسَابِ۟", translation: "Ey Davud! Biz seni yeryüzünde bir halife yaptık. İnsanlar arasında Hakk ile hükmet. Hevaya uyma. Aksi halde heva seni Allah'ın yolundan saptırır. Allah'ın yolundan sapanlar, Hesap Günü'nü göz ardı etmiş olduklarından, kendileri için çok şiddetli bir azap vardır.", transliteration: "Ya davudu inna cealnake halifeten fil ardı fahkum beynen nasi bil hakkı ve la tettebiil heva fe yudılleke an sebilillah, innellezine yadıllune an sebilillahi lehum azabun şedidun bi ma nesu yevmel hisab."},
  {id: 3997, sure_no: 27, ayet_no: 27, sure_adi: "Sad", text: "وَمَا خَلَقْنَا السَّمَٓاءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا بَاطِلاًۜ ذٰلِكَ ظَنُّ الَّذ۪ينَ كَفَرُواۚ فَوَيْلٌ لِلَّذ۪ينَ كَفَرُوا مِنَ النَّارِۜ", translation: "Biz, göğü, yeri ve ikisi arasında olanları boşuna yaratmadık. Bu, kafirlerin görüşüdür. Kendilerini ateşe atan, kafirlerin vay haline.", transliteration: "Ve ma halaknes semae vel arda ve ma beynehuma batıla, zalike zannullezine keferu, fe veylun lillezine keferu minen nar."},
  {id: 3998, sure_no: 28, ayet_no: 28, sure_adi: "Sad", text: "اَمْ نَجْعَلُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَالْمُفْسِد۪ينَ فِي الْاَرْضِۘ اَمْ نَجْعَلُ الْمُتَّق۪ينَ كَالْفُجَّارِ", translation: "İman eden ve salihatı yapanları, yeryüzünde bozgunculuk yapanlarla bir tutar mıyız? Ya da takva sahiplerini facirlerle bir tutar mıyız?", transliteration: "Em nec'alullezine amenu ve amilus salihati kel mufsidine fil ardı em nec'alul muttekine kel fuccar."},
  {id: 3999, sure_no: 29, ayet_no: 29, sure_adi: "Sad", text: "كِتَابٌ اَنْزَلْنَاهُ اِلَيْكَ مُبَارَكٌ لِيَدَّبَّرُٓوا اٰيَاتِه۪ وَلِيَتَذَكَّرَ اُو۬لُوا الْاَلْبَابِ", translation: "Bu; akıl sahiplerinin, ayetlerini düşünüp öğüt almaları için, sana indirdiğimiz kutlu bir Kitap'tır.", transliteration: "Kitabun enzelnahu ileyke mubarekun li yeddebberu ayatihi ve li yetezekkere ulul elbab."},
  {id: 4000, sure_no: 30, ayet_no: 30, sure_adi: "Sad", text: "وَوَهَبْنَا لِدَاوُ۫دَ سُلَيْمٰنَۜ نِعْمَ الْعَبْدُۜ اِنَّهُٓ  اَوَّابٌۜ", translation: "Davud'a Süleyman'ı armağan ettik. Ne güzel bir kuldu. O, her zaman Allah'a yönelendi.", transliteration: "Ve vehebna li davude suleyman, ni'mel abd, innehu evvab."},
  {id: 4001, sure_no: 31, ayet_no: 31, sure_adi: "Sad", text: "اِذْ عُرِضَ عَلَيْهِ بِالْعَشِيِّ الصَّافِنَاتُ الْجِيَادُۙ", translation: "Bir zaman kendisine, akşamüstü iyi cins safkan atlar sunulmuştu.", transliteration: "İz urıda aleyhi bil aşiyyis safinatul ciyad."},
  {id: 4002, sure_no: 32, ayet_no: 32, sure_adi: "Sad", text: "فَقَالَ اِنّ۪ٓي اَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبّ۪يۚ حَتّٰى تَوَارَتْ بِالْحِجَابِ۠", translation: "\"Doğrusu ben Rabb'imin öğüdünden dolayı hayra muhabbeti sevdim.\" dedi. Derken gözden kayboldular.", transliteration: "Fe kale inni ahbebtu hubbel hayri an zikri rabbi, hatta tevaret bil hıcab."},
  {id: 4003, sure_no: 33, ayet_no: 33, sure_adi: "Sad", text: "رُدُّوهَا عَلَيَّۜ فَطَفِقَ مَسْحاً بِالسُّوقِ وَالْاَعْنَاقِ", translation: "\"Onları bana geri getirin.\" Ardından bacaklarını ve boyunlarını mesh etmeye başladı.", transliteration: "Rudduha aleyy, fe tafika meshan bis sukı vel a'nak."},
  {id: 4004, sure_no: 34, ayet_no: 34, sure_adi: "Sad", text: "وَلَقَدْ فَتَنَّا سُلَيْمٰنَ وَاَلْقَيْنَا عَلٰى كُرْسِيِّه۪ جَسَداً ثُمَّ اَنَابَ", translation: "Ant olsun ki Süleyman'ı fitnelendirdik. Kürsüsünün üzerine bir ceset bıraktık. Sonra o Bize yöneldi.", transliteration: "Ve lekad fetenna suleymane ve elkayna ala kursiyyihi ceseden summe enab."},
  {id: 4005, sure_no: 35, ayet_no: 35, sure_adi: "Sad", text: "قَالَ رَبِّ اغْفِرْ ل۪ي وَهَبْ ل۪ي مُلْكاً لَا يَنْبَغ۪ي لِاَحَدٍ مِنْ بَعْد۪يۚ اِنَّكَ اَنْتَ الْوَهَّابُ", translation: "\"Ey Rabb'im! Beni bağışla. Bana, benden sonra hiç kimsenin sahip olamayacağı bir mülk bağışla. Kuşkusuz ki Sen, Bol Bol Bağışlayıcı'sın.\" dedi.", transliteration: "Kale rabbigfir li veheb li mulken la yenbagi li ehadin min ba'di, inneke entel vehhab."},
  {id: 4006, sure_no: 36, ayet_no: 36, sure_adi: "Sad", text: "فَسَخَّرْنَا لَهُ الرّ۪يحَ تَجْر۪ي بِاَمْرِه۪ رُخَٓاءً حَيْثُ اَصَابَۙ", translation: "Bunun üzerine rüzgarı onun emrine verdik. Onun emri ile dilediği yere yumuşak bir esinti ile akıp gidiyordu.", transliteration: "Fe sehharna lehur riha tecri bi emrihi ruhaen haysu esab."},
  {id: 4007, sure_no: 37, ayet_no: 37, sure_adi: "Sad", text: "وَالشَّيَاط۪ينَ كُلَّ بَنَّٓاءٍ وَغَوَّاصٍۙ", translation: "Ve şeytanları. Her türlü yapı ustasını ve dalgıçları.", transliteration: "Veş şeyatine kulle bennain ve gavvasın."},
  {id: 4008, sure_no: 38, ayet_no: 38, sure_adi: "Sad", text: "وَاٰخَر۪ينَ مُقَرَّن۪ينَ فِي الْاَصْفَادِ", translation: "Ve zincirlerle bağlanmış olan diğerlerini de.", transliteration: "Ve aharine mukarrenine fil asfad."},
  {id: 4009, sure_no: 39, ayet_no: 39, sure_adi: "Sad", text: "هٰذَا عَطَٓاؤُ۬نَا فَامْنُنْ اَوْ اَمْسِكْ بِغَيْرِ حِسَابٍ", translation: "Bu Bizim verdiklerimizdir. Artık hesabı sana kalmış, dilediğine ver veya verme.", transliteration: "Haza atauna femnun ev emsik bi gayri hisab."},
  {id: 4010, sure_no: 40, ayet_no: 40, sure_adi: "Sad", text: "وَاِنَّ لَهُ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ۟", translation: "Onun yanımızda iyi bir makamı ve iyi bir geleceği vardır.", transliteration: "Ve inne lehu ındena le zulfa ve husne meab."},
  {id: 4011, sure_no: 41, ayet_no: 41, sure_adi: "Sad", text: "وَاذْكُرْ عَبْدَنَٓا اَيُّوبَۢ اِذْ نَادٰى رَبَّهُٓ اَنّ۪ي مَسَّنِيَ الشَّيْطَانُ بِنُصْبٍ وَعَذَابٍۜ", translation: "Kulumuz Eyyub'u da hatırla. Bir zamanlar Rabb'ine seslenmişti: \"Şeytan bana dert ve azap dokundurdu.\"", transliteration: "Vezkur abdena eyyub, iz nada rabbehu enni messeniyeş şeytanu bi nusbin ve azab."},
  {id: 4012, sure_no: 42, ayet_no: 42, sure_adi: "Sad", text: "اُرْكُضْ بِرِجْلِكَۚ هٰذَا مُغْتَسَلٌ بَارِدٌ وَشَرَابٌ", translation: "\"Ey Eyyub! Ayağın ile topukla! İşte bu hem yıkanılacak hem de içilecek soğuk su.\"", transliteration: "Urkud biriclik, haza mugteselun baridun ve şerab."},
  {id: 4013, sure_no: 43, ayet_no: 43, sure_adi: "Sad", text: "وَوَهَبْنَا لَهُٓ   اَهْلَهُ وَمِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنَّا وَذِكْرٰى لِاُو۬لِي الْاَلْبَابِ", translation: "Katımızdan bir rahmet ve akıl sahipleri için bir ibret olarak, ona yanında yer alanları ve onlarla birlikte bir o kadarını daha bahşettik.", transliteration: "Ve vehebna lehu ehlehu ve mislehum meahum rahmeten minna ve zikra li ulil elbab."},
  {id: 4014, sure_no: 44, ayet_no: 44, sure_adi: "Sad", text: "وَخُذْ بِيَدِكَ ضِغْثاً فَاضْرِبْ بِه۪ وَلَا تَحْنَثْۜ اِنَّا وَجَدْنَاهُ صَابِراًۜ نِعْمَ الْعَبْدُۜ اِنَّهُٓ  اَوَّابٌ", translation: "Ve eline bir deste al, onunla yola koyul ve hanis olma. Biz, onu sabredici bulduk. O ne iyi kuldu! O, her zaman Allah'a yöneldi.", transliteration: "Ve huz bi yedike dıgsen fadrıb bihi ve la tahnes, inna vecednahu sabira, ni'mel abd, innehu evvab."},
  {id: 4015, sure_no: 45, ayet_no: 45, sure_adi: "Sad", text: "وَاذْكُرْ عِبَادَنَٓا اِبْرٰه۪يمَ وَاِسْحٰقَ وَيَعْقُوبَ اُو۬لِي الْاَيْد۪ي وَالْاَبْصَارِ", translation: "Güç ve basiret sahibi olan kullarımız İbrahim'i, İshak'ı ve Yakub'u da an.", transliteration: "Vezkur ıbadena ibrahime ve ishaka ve ya'kube ulil eydi vel ebsar."},
  {id: 4016, sure_no: 46, ayet_no: 46, sure_adi: "Sad", text: "اِنَّٓا اَخْلَصْنَاهُمْ بِخَالِصَةٍ ذِكْرَى الدَّارِۚ", translation: "Biz, onları sürekli ahiret yurdu düşüncesiyle arınmış, samimiyet sahibi kimseler yaptık.", transliteration: "İnna ahlasnahum bi halisatin zikred dar."},
  {id: 4017, sure_no: 47, ayet_no: 47, sure_adi: "Sad", text: "وَاِنَّهُمْ عِنْدَنَا لَمِنَ الْمُصْطَفَيْنَ الْاَخْيَارِ", translation: "Onlar, yanımızda seçkin ve hayırlı kimselerdendir.", transliteration: "Ve innehum ındena le minel mustafeynel ahyar."},
  {id: 4018, sure_no: 48, ayet_no: 48, sure_adi: "Sad", text: "وَاذْكُرْ اِسْمٰع۪يلَ وَالْيَسَعَ وَذَا الْـكِفْلِۜ وَكُلٌّ مِنَ الْاَخْيَارِۜ", translation: "İsmail'i, Elyesa'yı ve Zu'l-Kifl'i de an. Hepsi de hayırlı kimselerdendir.", transliteration: "Vezkur ismaile velyesea ve zel kifl, ve kullun minel ahyar."},
  {id: 4019, sure_no: 49, ayet_no: 49, sure_adi: "Sad", text: "هٰذَا ذِكْرٌۜ وَاِنَّ لِلْمُتَّق۪ينَ لَحُسْنَ مَاٰبٍۙ", translation: "Bu bir öğüttür. Kuşkusuz takva sahipleri için iyi bir gelecek vardır.", transliteration: "Haza zikr, ve inne lil muttekine le husne meab."},
  {id: 4020, sure_no: 50, ayet_no: 50, sure_adi: "Sad", text: "جَنَّاتِ عَدْنٍ مُفَتَّحَةً لَهُمُ الْاَبْوَابُۚ", translation: "Adn Cennetlerinin kapıları onlara açıktır.", transliteration: "Cennati adnin mufettehaten le humul ebvab."},
  {id: 4021, sure_no: 51, ayet_no: 51, sure_adi: "Sad", text: "مُتَّكِـ۪ٔينَ ف۪يهَا يَدْعُونَ ف۪يهَا بِفَاكِهَةٍ كَث۪يرَةٍ وَشَرَابٍ", translation: "Orada keyiflerince oturmuş olarak onlara pek çok meyve ve içecek sunulur.", transliteration: "Muttekine fiha yed'une fiha bi fakihetin kesiretin ve şerab."},
  {id: 4022, sure_no: 52, ayet_no: 52, sure_adi: "Sad", text: "وَعِنْدَهُمْ قَاصِرَاتُ الطَّرْفِ اَتْرَابٌ", translation: "Ve yanlarında, bakışlarını koruyan yaşıtlar vardır.", transliteration: "Ve ındehum kasıratut tarfi etrab."},
  {id: 4023, sure_no: 53, ayet_no: 53, sure_adi: "Sad", text: "هٰذَا مَا تُوعَدُونَ لِيَوْمِ الْحِسَابِ", translation: "İşte bu, Hesap Günü için size söz verilenlerdir.", transliteration: "Haza ma tuadune li yevmil hisab."},
  {id: 4024, sure_no: 54, ayet_no: 54, sure_adi: "Sad", text: "اِنَّ هٰذَا لَرِزْقُنَا مَا لَهُ مِنْ نَفَادٍۚ", translation: "Bu, bitmez tükenmez rızkımızdır.", transliteration: "İnne haza le rızkuna ma lehu min nefad."},
  {id: 4025, sure_no: 55, ayet_no: 55, sure_adi: "Sad", text: "هٰذَاۜ وَاِنَّ لِلطَّاغ۪ينَ لَشَرَّ مَاٰبٍۙ", translation: "İyilerin durumu budur. Azgınlar için ise kötü bir gelecek vardır.", transliteration: "Haza, ve inne lit tagıyne le şerre meab."},
  {id: 4026, sure_no: 56, ayet_no: 56, sure_adi: "Sad", text: "جَهَنَّمَۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمِهَادُ", translation: "Varacakları yer Cehennem'dir. Orası ne kötü bir yataktır.", transliteration: "Cehennem, yaslevneha, fe bi'sel mihad."},
  {id: 4027, sure_no: 57, ayet_no: 57, sure_adi: "Sad", text: "هٰذَاۙ فَلْيَذُوقُوهُ حَم۪يمٌ وَغَسَّاقٌۙ", translation: "İşte kaynar ve kokuşmuş su; tatsınlar bakalım!", transliteration: "Haza fel yezukuhu hamiymun ve gassak."},
  {id: 4028, sure_no: 58, ayet_no: 58, sure_adi: "Sad", text: "وَاٰخَرُ مِنْ شَكْلِه۪ٓ  اَزْوَاجٌۜ", translation: "Ve aynı türden çeşit çeşit azaplar.", transliteration: "Ve aharu min şeklihi ezvac."},
  {id: 4029, sure_no: 59, ayet_no: 59, sure_adi: "Sad", text: "هٰذَا فَوْجٌ مُقْتَحِمٌ مَعَكُمْۚ لَا مَرْحَباً بِهِمْۜ  اِنَّهُمْ صَالُوا النَّارِ", translation: "İşte bunlar da sizinle birlikte azaba katlanacak olan bir gruptur. Onlara rahatlık yoktur. Onlar, ateşe girecek olanlardır.", transliteration: "Haza fevcun muktehımun meakum, la merhaben bihim, innehum salun nar."},
  {id: 4030, sure_no: 60, ayet_no: 60, sure_adi: "Sad", text: "قَالُوا بَلْ اَنْتُمْ۠  لَا مَرْحَباً بِكُمْۜ  اَنْتُمْ قَدَّمْتُمُوهُ لَنَاۚ فَبِئْسَ الْقَرَارُ", translation: "Diğerleri ise: \"Hayır! Asıl size rahatlık yok. Ona uğramamızın sebebi sizsiniz. O ne kötü bir konaklama yeridir!\" dediler.", transliteration: "Kalu bel entum, la merhaben bikum, entum kaddemtumuhu lena, febi'sel karar."},
  {id: 4031, sure_no: 61, ayet_no: 61, sure_adi: "Sad", text: "قَالُوا رَبَّنَا مَنْ قَدَّمَ لَنَا هٰذَا فَزِدْهُ عَذَاباً ضِعْفاً فِي النَّارِ", translation: "\"Rabb'imiz! Buna kim sebep olduysa onun ateşteki azabını kat kat arttır!\" dediler.", transliteration: "Kalu rabbena men kaddeme lena haza fe zidhu azaben dı'fen fin nar."},
  {id: 4032, sure_no: 62, ayet_no: 62, sure_adi: "Sad", text: "وَقَالُوا مَا لَنَا لَا نَرٰى رِجَالاً كُنَّا نَعُدُّهُمْ مِنَ الْاَشْرَارِۜ", translation: "\"Biz, neden kötülerden olarak gördüğümüz adamları görmüyoruz?\" derler.", transliteration: "Ve kalu ma lena la nera ricalen kunna neudduhum minel eşrar."},
  {id: 4033, sure_no: 63, ayet_no: 63, sure_adi: "Sad", text: "اَتَّخَذْنَاهُمْ سِخْرِياًّ اَمْ زَاغَتْ عَنْهُمُ الْاَبْصَارُ", translation: "Hani kendilerini alaya almıştık! Yoksa buradalar da biz mi görmüyoruz?", transliteration: "Ettehaznahum sıhriyyen em zagat anhumul ebsar."},
  {id: 4034, sure_no: 64, ayet_no: 64, sure_adi: "Sad", text: "اِنَّ ذٰلِكَ لَحَقٌّ تَخَاصُمُ اَهْلِ النَّارِ۟", translation: "Ateş halkının birbirleriyle bu çekişmeleri kesinlikle gerçektir.", transliteration: "İnne zalike le hakkun tehasumu ehlin nar."},
  {id: 4035, sure_no: 65, ayet_no: 65, sure_adi: "Sad", text: "قُلْ اِنَّـمَٓا اَنَا۬ مُنْذِرٌۗ وَمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ الْوَاحِدُ الْقَهَّارُۚ", translation: "De ki: \"Ben yalnızca bir uyarıcıyım. Tek ve kahredici olan Allah'tan başka ilah yoktur.\"", transliteration: "Kul innema ene munzirun ve ma min ilahin ilallahul vahıdul kahhar."},
  {id: 4036, sure_no: 66, ayet_no: 66, sure_adi: "Sad", text: "رَبُّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الْعَز۪يزُ الْغَفَّارُ", translation: "Göklerin, yerin ve ikisinin arasında olan şeylerin Rabb'i Mutlak Üstün Olan'dır, Çok Bağışlayıcı'dır.", transliteration: "Rabbus semavati vel ardı ve ma beynehumel azizul gaffar."},
  {id: 4037, sure_no: 67, ayet_no: 67, sure_adi: "Sad", text: "قُلْ هُوَ نَبَؤٌا عَظ۪يمٌۙ", translation: "De ki: \"O, çok büyük bir haberdir.\"", transliteration: "Kul huve nebeun azimun."},
  {id: 4038, sure_no: 68, ayet_no: 68, sure_adi: "Sad", text: "اَنْتُمْ عَنْهُ مُعْرِضُونَ", translation: "\"Siz, ondan yüz çeviriyorsunuz.\"", transliteration: "Entum anhu mu'ridun."},
  {id: 4039, sure_no: 69, ayet_no: 69, sure_adi: "Sad", text: "مَا كَانَ لِيَ مِنْ عِلْمٍ بِالْمَلَأِ الْاَعْلٰٓى اِذْ يَخْتَصِمُونَ", translation: "Onlar tartışırlarken, benim Mele-i A'la'ya dair bir bilgim yoktu.", transliteration: "Ma kane liye min ilmin bil meleil a'la iz yahtesımun."},
  {id: 4040, sure_no: 70, ayet_no: 70, sure_adi: "Sad", text: "اِنْ يُوحٰٓى اِلَيَّ اِلَّٓا اَنَّمَٓا اَنَا۬ نَذ۪يرٌ مُب۪ينٌ", translation: "Bana, yalnızca apaçık bir uyarıcı olduğum için vahyediliyor.\"", transliteration: "İn yuha ileyye illa ennema ene nezirun mubin."},
  {id: 4041, sure_no: 71, ayet_no: 71, sure_adi: "Sad", text: "اِذْ قَالَ رَبُّكَ لِلْمَلٰٓئِكَةِ اِنّ۪ي خَالِقٌ بَشَراً مِنْ ط۪ينٍ", translation: "Hani Rabb'in meleklere: \"Ben çamurdan bir beşer yaratacağım.\" demişti.", transliteration: "İz kale rabbuke lil melaiketi inni halikun beşeren min tin."},
  {id: 4042, sure_no: 72, ayet_no: 72, sure_adi: "Sad", text: "فَاِذَا سَوَّيْتُهُ وَنَفَخْتُ ف۪يهِ مِنْ رُوح۪ي فَقَعُوا لَهُ سَاجِد۪ينَ", translation: "Onu biçimlendirip, ruhumdan üflediğim zaman derhal ona secdeye kapanın!", transliteration: "Fe iza sevveytuhu ve nefahtu fihi min ruhi fe kau lehu sacidin."},
  {id: 4043, sure_no: 73, ayet_no: 73, sure_adi: "Sad", text: "فَسَجَدَ الْمَلٰٓئِكَةُ  كُلُّهُمْ اَجْمَعُونَۙ", translation: "Bunun üzerine meleklerin tamamı, hep birlikte secde ettiler.", transliteration: "Fe secedel melaiketu kulluhum ecmaun."},
  {id: 4044, sure_no: 74, ayet_no: 74, sure_adi: "Sad", text: "اِلَّٓا اِبْل۪يسَۜ اِسْتَكْـبَرَ وَكَانَ مِنَ الْكَافِر۪ينَ", translation: "Ancak iblis etmedi. O kibirlendi ve zaten o kafirlerdendi.", transliteration: "İlla iblis, istekbere ve kane minel kafirin."},
  {id: 4045, sure_no: 75, ayet_no: 75, sure_adi: "Sad", text: "قَالَ يَٓا اِبْل۪يسُ مَا مَنَعَكَ اَنْ تَسْجُدَ لِمَا خَلَقْتُ بِيَدَيَّۜ اَسْتَكْـبَرْتَ اَمْ كُنْتَ مِنَ الْعَال۪ينَ", translation: "\"Ey İblis! İki elimle yarattığım şeye secde etmekten seni alıkoyan nedir? Büyüklük mü taslıyorsun, yoksa kendini çok mu üstün görüyorsun?\" dedi.", transliteration: "Kale ya iblisu ma meneake en tescude lima halaktu bi yedeyy, estekberte em kunte minel alin."},
  {id: 4046, sure_no: 76, ayet_no: 76, sure_adi: "Sad", text: "قَالَ اَنَا۬ خَيْرٌ مِنْهُۜ خَلَقْتَن۪ي مِنْ نَارٍ وَخَلَقْتَهُ مِنْ ط۪ينٍ", translation: "\"Ben ondan hayırlıyım. Beni ateşten yarattın, onu çamurdan yarattın.\" dedi.", transliteration: "Kale ene hayrun minh, halakteni min narin ve halaktehu min tin."},
  {id: 4047, sure_no: 77, ayet_no: 77, sure_adi: "Sad", text: "قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَج۪يمٌۚ", translation: "\"Oradan çık! Sen kesinlikle racimsin.\" dedi.", transliteration: "Kale fahruc minha fe inneke recim."},
  {id: 4048, sure_no: 78, ayet_no: 78, sure_adi: "Sad", text: "وَاِنَّ عَلَيْكَ لَعْنَت۪ٓي اِلٰى يَوْمِ الدّ۪ينِ", translation: "Lanetim, Din Günü'ne kadar senin üzerindedir.", transliteration: "Ve inne aleyke la'neti ila yevmid din."},
  {id: 4049, sure_no: 79, ayet_no: 79, sure_adi: "Sad", text: "قَالَ رَبِّ فَاَنْظِرْن۪ٓي اِلٰى يَوْمِ يُبْعَثُونَ", translation: "İblis: \"Rabb'im! O halde yeniden dirilme gününe kadar bana süre ver.\" dedi.", transliteration: "Kale rabbi fe enzırni ila yevmi yub'asun."},
  {id: 4050, sure_no: 80, ayet_no: 80, sure_adi: "Sad", text: "قَالَ فَاِنَّكَ مِنَ الْمُنْظَر۪ينَۙ", translation: "Allah, \"Peki süre verilenlerdensin.\" dedi.", transliteration: "Kale fe inneke minel munzarin."},
  {id: 4051, sure_no: 81, ayet_no: 81, sure_adi: "Sad", text: "اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُومِ", translation: "Zamanı bilinen güne kadar.", transliteration: "İla yevmil vaktil ma'lum."},
  {id: 4052, sure_no: 82, ayet_no: 82, sure_adi: "Sad", text: "قَالَ فَبِعِزَّتِكَ لَاُغْوِيَنَّهُمْ اَجْمَع۪ينَۙ", translation: "İblis: \"Öyleyse, izzetine ant olsun ki onların hepsini azdıracağım.\" dedi.", transliteration: "Kale fe bi izzetike le ugviyennehum ecmain."},
  {id: 4053, sure_no: 83, ayet_no: 83, sure_adi: "Sad", text: "اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَص۪ينَ", translation: "\"Ancak içlerinden muhles kulların hariç.\"", transliteration: "İlla ibadeke minhumul muhlasin."},
  {id: 4054, sure_no: 84, ayet_no: 84, sure_adi: "Sad", text: "قَالَ فَالْحَقُّۘ وَالْحَقَّ اَقُولُۚ", translation: "\"Gerçek budur. Ben, gerçeği söylerim.\" dedi.", transliteration: "Kale fel hakku vel hakka ekul."},
  {id: 4055, sure_no: 85, ayet_no: 85, sure_adi: "Sad", text: "لَاَمْلَـَٔنَّ جَهَنَّمَ مِنْكَ وَمِمَّنْ تَبِعَكَ مِنْهُمْ اَجْمَع۪ينَ", translation: "Allah: \"Ant olsun ki Cehennem'i senden ve sana uyanlardan dolduracağım.\" dedi.", transliteration: "Le emleenne cehenneme minke ve mimmen tebiake minhum ecmain."},
  {id: 4056, sure_no: 86, ayet_no: 86, sure_adi: "Sad", text: "قُلْ مَٓا اَسْـَٔلُـكُمْ عَلَيْهِ مِنْ اَجْرٍ وَمَٓا اَنَا۬ مِنَ الْمُتَكَلِّف۪ينَ", translation: "De ki: \"Sizden yaptığım bu işe karşı bir ücret istemiyorum. Ve ben kendiliğimden bir sorumluluk getirenlerden değilim.\"", transliteration: "Kul ma es'elukum aleyhi min ecrin ve ma ene minel mutekellifin."},
  {id: 4057, sure_no: 87, ayet_no: 87, sure_adi: "Sad", text: "اِنْ هُوَ اِلَّا ذِكْرٌ لِلْعَالَم۪ينَ", translation: "O, ancak bütün insanlık için bir zikirdir.", transliteration: "İn huve illa zikrun lil alemin."},
  {id: 4058, sure_no: 88, ayet_no: 88, sure_adi: "Sad", text: "وَلَتَعْلَمُنَّ نَبَاَهُ بَعْدَ ح۪ينٍ", translation: "Onun haberini bir zaman sonra kesinlikle bileceksiniz.", transliteration: "Ve le talemunne nebeehu ba'de hin."},
  {id: 4059, sure_no: 1, ayet_no: 1, sure_adi: "Zümer", text: "تَنْز۪يلُ الْكِتَابِ مِنَ اللّٰهِ الْعَز۪يزِ الْحَك۪يمِ", translation: "Kitap'ın indirilmesi, Mutlak Üstün Olan, En İyi Hüküm Veren Allah tarafındandır.", transliteration: "Tenzilul kitabi minallahil azizil hakim."},
  {id: 4060, sure_no: 2, ayet_no: 2, sure_adi: "Zümer", text: "اِنَّٓا اَنْزَلْـنَٓا اِلَيْكَ الْكِتَابَ بِالْحَقِّ فَاعْبُدِ اللّٰهَ مُخْلِصاً لَهُ الدّ۪ينَۜ", translation: "Biz, bu Kitap'ı sana Hakk ile indirdik. Öyleyse dini yalnızca O'na halis kılarak Allah'a kulluk et.", transliteration: "İnna enzelna ileykel kitabe bil hakkı fa'budillahe muhlisan lehud din."},
  {id: 4061, sure_no: 3, ayet_no: 3, sure_adi: "Zümer", text: "اَلَا لِلّٰهِ الدّ۪ينُ الْخَالِصُۜ وَالَّذ۪ينَ اتَّخَذُوا مِنْ دُونِه۪ٓ اَوْلِيَٓاءَۢ مَا نَعْبُدُهُمْ اِلَّا لِيُقَرِّبُونَٓا اِلَى اللّٰهِ زُلْفٰىۜ اِنَّ اللّٰهَ يَحْكُمُ بَيْنَهُمْ ف۪ي مَا هُمْ ف۪يهِ يَخْتَلِفُونَۜ اِنَّ اللّٰهَ لَا يَهْد۪ي مَنْ هُوَ كَاذِبٌ كَفَّارٌ", translation: "Dikkat edin! Halis din yalnızca Allah'a aittir. O'nun yanı sıra veliler edinenler: \"Onlara, bizi Allah'a daha yakın bir seviyeye yaklaştırsınlar diye kulluk ediyoruz.\" diyorlar. Allah, hakkında tartıştıkları şey için hükmünü verecektir. Allah, yalancı ve azılı nankörleri doğru yola iletmez.", transliteration: "E la lillahid dinul halis, vellezinettehazu min dunihi evliya, ma na'buduhum illa li yukarribuna ilallahi zulfa, innallahe yahkumu beynehum fi ma hum fihi yahtelifun, innallahe la yehdi men huve kazibun keffar."},
  {id: 4062, sure_no: 4, ayet_no: 4, sure_adi: "Zümer", text: "لَوْ اَرَادَ اللّٰهُ اَنْ يَتَّخِذَ وَلَداً لَاصْطَفٰى مِمَّا يَخْلُقُ مَا يَشَٓاءُۙ سُبْحَانَهُۜ هُوَ اللّٰهُ الْوَاحِدُ الْقَهَّارُ", translation: "Eğer Allah bir çocuk edinmek isteseydi, yaratacağından dilediğini seçerdi. O, bundan münezzehtir. O Allah, Eşsiz ve Benzersiz Tek Olan'dır, Varlığın Üzerinde Mutlak Egemen'dir.", transliteration: "Lev eradallahu en yettehıze veleden lastafa mimma yahluku ma yeşau subhaneh, huvallahul vahıdul kahhar."},
  {id: 4063, sure_no: 5, ayet_no: 5, sure_adi: "Zümer", text: "خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّۚ يُكَوِّرُ الَّيْلَ عَلَى النَّهَارِ وَيُكَوِّرُ النَّهَارَ عَلَى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۜ  كُلٌّ يَجْر۪ي لِاَجَلٍ مُسَمًّىۜ اَلَا هُوَ الْعَز۪يزُ الْغَفَّارُ", translation: "Gökleri ve yeri Hakk ile yarattı. Geceyi gündüze, gündüzü geceye çevirir. Güneş'i ve Ay'ı buyruk altına almıştır. Hepsi belirlenmiş bir zamana akıp gitmektedir. İyi bilin ki O, Mutlak Üstün Olan'dır, Çok Bağışlayıcı'dır.", transliteration: "Halakas semavati vel arda bil hakk, yukevvirul leyle alen nehari ve yukevvirun nehare alel leyli ve sehhareş şemse vel kamer, kullun yecri li ecelin musemma, e la huvel azizul gaffar."},
  {id: 4064, sure_no: 6, ayet_no: 6, sure_adi: "Zümer", text: "خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَاَنْزَلَ لَكُمْ مِنَ الْاَنْعَامِ ثَمَانِيَةَ اَزْوَاجٍۜ يَخْلُقُكُمْ ف۪ي بُطُونِ اُمَّهَاتِكُمْ خَلْقاً مِنْ بَعْدِ خَلْقٍ ف۪ي ظُلُمَاتٍ ثَلٰثٍۜ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۜ لَٓا اِلٰهَ اِلَّا هُوَۚ فَاَنّٰى تُصْرَفُونَ", translation: "Sizi tek bir nefisten yarattı. Sonra ondan eşini meydana getirdi. Sizin için hayvanlardan sekiz eş bağışladı. Sizi annelerinizin karnında üç karanlık içinde, yaratılıştan sonra bir yaratılış ile yaratıyor. İşte bu sizin Rabb'iniz Allah'tır. Mülk, O'nundur. O'ndan başka ilah yoktur. Öyleyse nasıl oluyor da başka şeylere yöneliyorsunuz?", transliteration: "Halakakum min nefsin vahıdetin summe ceale minha zevceha ve enzele lekum minel en'ami semaniyete ezvac, yahlukukum fi butuni ummehatikum halkan min ba'di halkın fi zulumatin selas, zalikumullahu rabbukum lehul mulk, la ilahe illa huve, fe enna tusrafun."},
  {id: 4065, sure_no: 7, ayet_no: 7, sure_adi: "Zümer", text: "اِنْ تَكْفُرُوا فَاِنَّ اللّٰهَ غَنِيٌّ عَنْكُمْ وَلَا يَرْضٰى لِعِبَادِهِ الْكُفْرَۚ وَاِنْ تَشْكُرُوا يَرْضَهُ۬ لَكُمْۜ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۜ ثُمَّ اِلٰى رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَۜ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Eğer Küfrederseniz; bilin ki Allah'ın size hiçbir ihtiyacı yoktur. O, kullarının Küfründen hoşnut olmaz. Eğer şükrederseniz ondan hoşnut olur. Hiç kimse bir başkasının yükünü yüklenmez. Sonunda dönüşünüz Rabb'inizedir. Böylece yapmış olduklarınızı size haber verecektir. Kuşkusuz O, sinelerde olanı en iyi bilendir.", transliteration: "İn tekfuru fe innallahe ganiyyun ankum, ve la yerda li ıbadihil kufr, ve in teşkuru yerdahu lekum, ve la teziru vaziretun vizra uhra, summe ila rabbikum merciukum fe yunebbiukum bima kuntum ta'melun, innehu alimun bi zatis sudur."},
  {id: 4066, sure_no: 8, ayet_no: 8, sure_adi: "Zümer", text: "وَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَا رَبَّهُ مُن۪يباً اِلَيْهِ ثُمَّ اِذَا خَوَّلَهُ نِعْمَةً مِنْهُ نَسِيَ مَا كَانَ يَدْعُٓوا اِلَيْهِ مِنْ قَبْلُ وَجَعَلَ لِلّٰهِ اَنْدَاداً لِيُضِلَّ عَنْ سَب۪يلِه۪ۜ قُلْ تَمَتَّعْ بِكُفْرِكَ قَل۪يلاًۗ اِنَّكَ مِنْ اَصْحَابِ النَّارِ", translation: "İnsana bir sıkıntı dokunduğu zaman, Rabb'ine yönelerek bütün benliğiyle O'na dua eder. Sonra kendisine bir nimet lütfettiği zaman, daha önce O'na yöneldiği halini unutur. O'nun yolundan saptırmak için Allah'a ortaklar koşar. De ki: \"Nankörlüğünle biraz daha yararlan. Kuşkusuz ki sen ateşin halkındansın.\"", transliteration: "Ve iza messel insane durrun dea rabbehu muniben ileyhi summe iza havvelehu ni'meten minhu nesiye ma kane yed'u ileyhi min kablu ve ceale lillahi endaden li yudılle an sebilih, kul temetta' bi kufrike kalilen inneke min ashabin nar."},
  {id: 4067, sure_no: 9, ayet_no: 9, sure_adi: "Zümer", text: "اَمَّنْ هُوَ قَانِتٌ اٰنَٓاءَ الَّيْلِ سَاجِداً وَقَٓائِماً يَحْذَرُ الْاٰخِرَةَ وَيَرْجُوا رَحْمَةَ رَبِّه۪ۜ قُلْ هَلْ يَسْتَوِي الَّذ۪ينَ يَعْلَمُونَ وَالَّذ۪ينَ لَا يَعْلَمُونَۜ اِنَّمَا يَتَذَكَّرُ اُو۬لُوا الْاَلْبَابِ۟", translation: "Gece saatlerinde secde ederek, kıyam durarak itaatkar olan, ahireti hesaba katan ve Rabb'inin rahmetini uman kimse, bu nankörlerle bir tutulur mu hiç? De ki: \"Hiç bilenlerle bilmeyenler bir olur mu?\" Kesinlikle yalnızca temiz akıl sahipleri öğüt alırlar.", transliteration: "Em men huve kanitun anael leyli saciden ve kaimen yahzerul ahırete ve yercu rahmete rabbih, kul hel yestevillezine ya'lemune vellezine la ya'lemun, innema yetezekkeru ulul elbab."},
  {id: 4068, sure_no: 10, ayet_no: 10, sure_adi: "Zümer", text: "قُلْ يَا عِبَادِ الَّذ۪ينَ اٰمَنُوا اتَّقُوا رَبَّكُمْۜ لِلَّذ۪ينَ اَحْسَنُوا ف۪ي هٰذِهِ الدُّنْيَا حَسَنَةٌۜ وَاَرْضُ اللّٰهِ وَاسِعَةٌۜ اِنَّمَا يُوَفَّى الصَّابِرُونَ اَجْرَهُمْ بِغَيْرِ حِسَابٍ", translation: "De ki: \"Ey iman eden kullar! Rabb'inize karşı takvalı olun. Bu dünyada, iyi olanlar için iyilik vardır. Allah'ın arzı geniştir. Ancak sabredenlere ödülleri hesapsız ödenir.", transliteration: "Kul ya ıbadıllezine amenutteku rabbekum, lillezine ahsenu fi hazihid dunya haseneh, ve ardullahi vasiah innema yuveffas sabirune ecrehum bi gayri hisab."},
  {id: 4069, sure_no: 11, ayet_no: 11, sure_adi: "Zümer", text: "قُلْ اِنّ۪ٓي اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ مُخْلِصاً لَهُ الدّ۪ينَۙ", translation: "De ki: \"Ben, dini yalnızca Kendisine özgü kılarak, Allah'a kulluk etmekle emrolundum.\"", transliteration: "Kul inni umirtu en a'budallahe muhlisan lehud din."},
  {id: 4070, sure_no: 12, ayet_no: 12, sure_adi: "Zümer", text: "وَاُمِرْتُ لِاَنْ اَكُونَ اَوَّلَ الْمُسْلِم۪ينَ", translation: "\"Ve bana teslim olanların ilki olmam buyruldu.\"", transliteration: "Ve umirtu li en ekune evvelel muslimin."},
  {id: 4071, sure_no: 13, ayet_no: 13, sure_adi: "Zümer", text: "قُلْ اِنّ۪ٓي اَخَافُ اِنْ عَصَيْتُ رَبّ۪ي عَذَابَ يَوْمٍ عَظ۪يمٍ", translation: "De ki: \"Eğer Rabb'ime karşı gelirsem, büyük günün azabından korkarım.\"", transliteration: "Kul inni ehafu in asaytu rabbi azabe yevmin azim."},
  {id: 4072, sure_no: 14, ayet_no: 14, sure_adi: "Zümer", text: "قُلِ اللّٰهَ اَعْبُدُ مُخْلِصاً لَهُ د۪ين۪يۙ", translation: "De ki: \"Ben, dinimi Allah'a özgü kılarak O'na kulluk ederim.\"", transliteration: "Kulillahe a'budu muhlisan lehu dini."},
  {id: 4073, sure_no: 15, ayet_no: 15, sure_adi: "Zümer", text: "فَاعْبُدُوا مَا شِئْتُمْ مِنْ دُونِه۪ۜ  قُلْ اِنَّ الْخَاسِر۪ينَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ وَاَهْل۪يهِمْ يَوْمَ الْقِيٰمَةِۜ  اَلَا ذٰلِكَ هُوَ الْخُسْرَانُ الْمُب۪ينُ", translation: "\"Siz de O'nun yanı sıra istediğinize kulluk edin.\" De ki: \"Asıl kaybedenler, kıyamet gününde kendilerini ve yakınlarını kayba uğratacak olanlardır.\" Dikkat edin! İşte apaçık kayıp budur.", transliteration: "Fa'budu ma şi'tum min dunih, kul innel hasirine ellezine hasiru enfusehum ve ehlihim yevmel kıyameh e la zalike huvel husranul mubin."},
  {id: 4074, sure_no: 16, ayet_no: 16, sure_adi: "Zümer", text: "لَهُمْ مِنْ فَوْقِهِمْ ظُلَلٌ مِنَ النَّارِ وَمِنْ تَحْتِهِمْ ظُلَلٌۜ ذٰلِكَ يُخَوِّفُ اللّٰهُ بِه۪ عِبَادَهُۜ يَا عِبَادِ فَاتَّقُونِ", translation: "Onların üstlerinde de altlarında da ateşten katmanlar vardır. İşte Allah'ın, kullarını uyarıp sakınmalarını istediği şey budur. Ey kullarım, Bana karşı takvalı olun!", transliteration: "Lehum min fevkıhim zulelun minen nari ve min tahtihim zulel, zalike yuhavvifullahu bihi ıbadeh, ya ıbadi fettekun."},
  {id: 4075, sure_no: 17, ayet_no: 17, sure_adi: "Zümer", text: "وَالَّذ۪ينَ اجْتَنَبُوا الطَّاغُوتَ اَنْ يَعْبُدُوهَا وَاَنَابُٓوا اِلَى اللّٰهِ لَهُمُ الْبُشْرٰىۚ فَبَشِّرْ عِبَادِۙ", translation: "Tağuta kulluk etmekten kaçınan ve Allah'a yönelenlere müjdeler olsun. O halde bu kullarımı müjdele.", transliteration: "Vellezinectenebut tagute en ya'buduha ve enabu ilallahi lehumul buşra, fe beşşir ıbad."},
  {id: 4076, sure_no: 18, ayet_no: 18, sure_adi: "Zümer", text: "اَلَّذ۪ينَ يَسْتَمِعُونَ الْقَوْلَ فَيَتَّبِعُونَ اَحْسَنَهُۜ  اُو۬لٰٓئِكَ الَّذ۪ينَ هَدٰيهُمُ اللّٰهُ وَاُو۬لٰٓئِكَ هُمْ اُو۬لُوا الْاَلْبَابِ", translation: "Sözü dinleyip, en iyisine uyarlar. İşte onlar, Allah'ın doğru yola ilettiği kimselerdir. İşte onlar sağlıklı düşünen akıl sahipleridir.", transliteration: "Ellezine yestemiunel kavle fe yettebiune ahseneh, ulaikellezine hedahumullahu ve ulaike hum ulul elbab."},
  {id: 4077, sure_no: 19, ayet_no: 19, sure_adi: "Zümer", text: "اَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ الْعَذَابِۜ اَفَاَنْتَ تُنْقِذُ مَنْ فِي النَّارِۚ", translation: "Hakkında azap kararı gerçekleşmiş olana gelince; ateşte olanı sen mi kurtaracaksın?", transliteration: "E fe men hakka aleyhi kelimetul azab, e fe ente tunkızu men fin nar."},
  {id: 4078, sure_no: 20, ayet_no: 20, sure_adi: "Zümer", text: "لٰكِنِ الَّذ۪ينَ اتَّقَوْا رَبَّهُمْ لَهُمْ غُرَفٌ مِنْ فَوْقِهَا غُرَفٌ مَبْنِيَّةٌۙ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ وَعْدَ اللّٰهِۜ لَا يُخْلِفُ اللّٰهُ الْم۪يعَادَ", translation: "Fakat Rabb'lerine takvalı olanlar için kat kat bina edilmiş, önlerinde nehirler akan köşkler vardır. Bu Allah'ın verdiği sözdür. Allah verdiği sözden dönmez.", transliteration: "Lakinillezinettekav rabbehum lehum gurefun min fevkıha gurefun mebniyyetun tecri min tahtihel enhar, va'dallah, la yuhlifullahul miad."},
  {id: 4079, sure_no: 21, ayet_no: 21, sure_adi: "Zümer", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَٓاءِ مَٓاءً فَسَلَكَهُ يَنَاب۪يعَ فِي الْاَرْضِ ثُمَّ يُخْرِجُ بِه۪ زَرْعاً مُخْتَلِفاً اَلْوَانُهُ ثُمَّ يَه۪يجُ فَـتَرٰيهُ مُصْفَراًّ ثُمَّ يَجْعَلُهُ حُطَاماًۜ اِنَّ ف۪ي ذٰلِكَ لَذِكْرٰى لِاُو۬لِي الْاَلْبَابِ۟", translation: "Allah'ın, gökten su indirdiğini ve onu su kaynakları şeklinde yeryüzünde akıttığını, sonra onunla rengarenk ekinler çıkardığını, daha sonra onların sararıp kuruduklarını; kuru bir çöpe döndüğünü görmedin mi? Kuşkusuz bunda sağlıklı düşünen akıl sahipleri için bir öğüt vardır.", transliteration: "E lem tere ennallahe enzele mines semai maen fe selekehu yenabia fil ardı summe yuhricu bihi zer'an muhtelifen elvanuhu summe yehicu fe terahu musferran summe yec'aluhu hutama, inne fi zalike le zikra li ulil elbab."},
  {id: 4080, sure_no: 22, ayet_no: 22, sure_adi: "Zümer", text: "اَفَمَنْ شَرَحَ اللّٰهُ صَدْرَهُ لِلْاِسْلَامِ فَهُوَ عَلٰى نُورٍ مِنْ رَبِّه۪ۜ فَوَيْلٌ لِلْقَاسِيَةِ قُلُوبُهُمْ مِنْ ذِكْرِ اللّٰهِۜ اُو۬لٰٓئِكَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Allah'ın, göğsünü İslam'a açtığı kimse, Rabb'inden bir ışık üzerinde olmaz mı? Öyleyse Allah'ın öğütlerine karşı kalpleri katılaşmış olanlara yazıklar olsun! İşte onlar, apaçık bir sapkınlık içindedirler.", transliteration: "E fe men şerehallahu sadrehu lil islami fe huve ala nurin min rabbih, fe veylun lil kasiyeti kulubuhum min zikrillah, ulaike fi dalalin mubin."},
  {id: 4081, sure_no: 23, ayet_no: 23, sure_adi: "Zümer", text: "اَللّٰهُ نَزَّلَ اَحْسَنَ الْحَد۪يثِ كِتَاباً مُتَشَابِهاً مَثَانِيَۗ  تَقْشَعِرُّ مِنْهُ جُلُودُ الَّذ۪ينَ يَخْشَوْنَ رَبَّهُمْۚ ثُمَّ تَل۪ينُ جُلُودُهُمْ وَقُلُوبُهُمْ اِلٰى ذِكْرِ اللّٰهِۜ ذٰلِكَ هُدَى اللّٰهِ يَهْد۪ي بِه۪ مَنْ يَشَٓاءُۜ وَمَنْ يُضْلِلِ اللّٰهُ فَمَا لَهُ مِنْ هَادٍ", translation: "Allah, hadisin en iyisini; muteşabih, ikişerli bir kitap halinde indirdi. Ondan, Rabb'lerine karşı içtenlikli saygısı olanların tüyleri ürperir. Ardından da benlikleri ve kalpleri Allah'ın öğüdü ile huzura kavuşur. İşte bu, Allah'ın doğru yolu gösteren kılavuzudur. Onunla hak edeni doğru yola iletir; Allah, kimi sapkınlıkta bırakırsa, artık ona doğru yolu gösteren bulunamaz.", transliteration: "Allahu nezzele ahsenel hadisi kitaben muteşabihen mesaniye takşaırru minhu culudullezine yahşevne rabbehum, summe telinu culuduhum ve kulubuhum ila zikrillah, zalike hudallahi yehdi bihi men yeşau, ve men yudlilillahu fe ma lehu min had."},
  {id: 4082, sure_no: 24, ayet_no: 24, sure_adi: "Zümer", text: "اَفَمَنْ يَتَّق۪ي بِوَجْهِه۪ سُٓوءَ الْعَذَابِ يَوْمَ الْقِيٰمَةِۜ وَق۪يلَ لِلظَّالِم۪ينَ ذُوقُوا مَا كُنْتُمْ تَكْسِبُونَ", translation: "O halde kıyamet günü, kendisini kötü azaptan kim koruyabilir? Zalimlere: \"Kazandığınızın karşılığını tadın!\" denir.", transliteration: "E fe men yetteki bi vechihi suel azabi yevmel kıyame, ve kıyle liz zalimine zuku ma kuntum teksibun."},
  {id: 4083, sure_no: 25, ayet_no: 25, sure_adi: "Zümer", text: "كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَاَتٰيهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ", translation: "Onlardan öncekiler de yalanlamışlardı da kendilerine hiç beklemedikleri yerden azap geldi.", transliteration: "Kezzebellezine min kablihim fe etahumul azabu min haysu la yeş'urun."},
  {id: 4084, sure_no: 26, ayet_no: 26, sure_adi: "Zümer", text: "فَاَذَاقَهُمُ اللّٰهُ الْخِزْيَ فِي الْحَيٰوةِ الدُّنْيَاۚ وَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُۢ لَوْ كَانُوا يَعْلَمُونَ", translation: "Allah, onlara dünya hayatında zilleti tattırdı. Ahiret azabı elbette daha büyüktür. Keşke bilmiş olsalardı.", transliteration: "Fe ezakahumullahul hızye fil hayatid dunya, ve le azabul ahıreti ekber, lev kanu ya'lemun."},
  {id: 4085, sure_no: 27, ayet_no: 27, sure_adi: "Zümer", text: "وَلَقَدْ ضَرَبْنَا لِلنَّاسِ ف۪ي هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍ لَعَلَّهُمْ يَتَذَكَّرُونَۚ", translation: "Ant olsun ki Kur'an'da insanlar için her konudan örnekler verdik. Umulur ki böylece öğüt alırlar.", transliteration: "Ve lekad darebna lin nasi fi hazel kur'ani min kulli meselin leallehum yetezekkerun."},
  {id: 4086, sure_no: 28, ayet_no: 28, sure_adi: "Zümer", text: "قُرْاٰناً عَرَبِياًّ غَيْرَ ذ۪ي عِوَجٍ لَعَلَّهُمْ يَتَّقُونَ", translation: "Kusursuz, Arapça bir Kur'an'dır. Umulur ki takva sahibi olurlar.", transliteration: "Kur'anen arabiyyen gayre zi ivecin leallehum yettekun."},
  {id: 4087, sure_no: 29, ayet_no: 29, sure_adi: "Zümer", text: "ضَرَبَ اللّٰهُ مَثَلاً رَجُلاً ف۪يهِ شُرَكَٓاءُ مُتَشَاكِسُونَ وَرَجُلاً سَلَماً لِرَجُلٍۜ هَلْ يَسْتَوِيَانِ مَثَلاًۜ اَلْحَمْدُ لِلّٰهِۚ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُونَ", translation: "Allah, aralarında anlaşamayan birkaç ortağa bağlı bir kimse ile tek bir kişiye bağlı bir kimseyi örnek verdi: Bu iki kimsenin durumu hiçbir olur mu? Övgü yalnızca Allah'a özgüdür. Ama çokları bilmiyorlar.", transliteration: "Daraballahu meselen raculen fihi şurekau muteşakisune ve raculen selemen li racul, hel yesteviyani mesel, el hamdulillah, bel ekseruhum la ya'lemun."},
  {id: 4088, sure_no: 30, ayet_no: 30, sure_adi: "Zümer", text: "اِنَّكَ مَيِّتٌ وَاِنَّهُمْ مَيِّتُونَۘ", translation: "Kuşkusuz sen ölümlüsün, kuşkusuz onlar da ölümlüdürler.", transliteration: "İnneke meyyitun ve innehum meyyitun."},
  {id: 4089, sure_no: 31, ayet_no: 31, sure_adi: "Zümer", text: "ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ عِنْدَ رَبِّكُمْ تَخْتَصِمُونَ۟", translation: "Sonra kıyamet günü hepiniz Rabb'inizin huzurunda davalaşacaksınız.", transliteration: "Summe innekum yevmel kıyameti ınde rabbikum tahtasımun."},
  {id: 4090, sure_no: 32, ayet_no: 32, sure_adi: "Zümer", text: "فَمَنْ اَظْلَمُ مِمَّنْ كَذَبَ عَلَى اللّٰهِ وَكَذَّبَ بِالصِّدْقِ اِذْ جَٓاءَهُۜ اَلَيْسَ ف۪ي جَهَنَّمَ مَثْوًى لِلْكَافِر۪ينَ", translation: "Öyleyse, Allah adına yalan uyduran ve kendisine gelen doğruyu yalanlayandan daha zalim kim olabilir? Kafirlerin yeri Cehennem'de değil mi?", transliteration: "Fe men azlemu mimmen kezzebe alallahi ve kezzebe bis sıdkı iz caeh, e leyse fi cehenneme mesven lil kafirin."},
  {id: 4091, sure_no: 33, ayet_no: 33, sure_adi: "Zümer", text: "وَالَّذ۪ي جَٓاءَ بِالصِّدْقِ وَصَدَّقَ بِه۪ٓ اُو۬لٰٓئِكَ هُمُ الْمُتَّقُونَ", translation: "Doğruyu getirenler ve onu doğrulayanlar, işte onlar takva sahibidirler.", transliteration: "Vellezi cae bis sıdkı ve saddeka bihi ulaike humul muttekun."},
  {id: 4092, sure_no: 34, ayet_no: 34, sure_adi: "Zümer", text: "لَهُمْ مَا يَشَٓاؤُ۫نَ عِنْدَ رَبِّهِمْۜ ذٰلِكَ جَزٰٓؤُا الْمُحْسِن۪ينَۚ", translation: "Onlar için Rabb'leri yanında diledikleri her şey vardır. İşte budur muhsin olanların karşılığı.", transliteration: "Lehum ma yeşaune inde rabbihim, zalike cezaul muhsinin."},
  {id: 4093, sure_no: 35, ayet_no: 35, sure_adi: "Zümer", text: "لِيُكَفِّرَ اللّٰهُ عَنْهُمْ اَسْوَاَ الَّذ۪ي عَمِلُوا وَيَجْزِيَهُمْ اَجْرَهُمْ بِاَحْسَنِ الَّذ۪ي كَانُوا يَعْمَلُونَ", translation: "Allah, onların yaptıklarının en kötülerine kafir olacak; yaptıklarının karşılığında, en iyisiyle karşılık verecek.", transliteration: "Li yukeffirallahu anhum esveellezi amilu ve yecziyehum ecrehum bi ahsenillezi kanu ya'melun."},
  {id: 4094, sure_no: 36, ayet_no: 36, sure_adi: "Zümer", text: "اَلَيْسَ اللّٰهُ بِكَافٍ عَبْدَهُۜ وَيُخَوِّفُونَكَ بِالَّذ۪ينَ مِنْ دُونِه۪ۜ وَمَنْ يُضْلِلِ اللّٰهُ فَمَا لَهُ مِنْ هَادٍۚ", translation: "Allah, kuluna yeterli değil mi? Onlar seni O'ndan başkası ile korkutmaya çalışıyorlar. Allah, kimi sapkınlıkta bırakırsa, onu doğruya iletecek yoktur.", transliteration: "E leysallahu bi kafin abdeh, ve yuhavvifuneke billezine min dunih, ve men yudlilillahu fe ma lehu min had."},
  {id: 4095, sure_no: 37, ayet_no: 37, sure_adi: "Zümer", text: "وَمَنْ يَهْدِ اللّٰهُ فَمَا لَهُ مِنْ مُضِلٍّۜ اَلَيْسَ اللّٰهُ بِعَز۪يزٍ ذِي انْتِقَامٍ", translation: "Allah, kimi doğru yola iletirse, onu saptırabilecek yoktur. Allah, Mutlak Üstün Olan; intikam sahibi olan değil midir?", transliteration: "Ve men yehdillahu fe ma lehu min mudıll, e leysallahu bi azizin zintikam."},
  {id: 4096, sure_no: 38, ayet_no: 38, sure_adi: "Zümer", text: "وَلَئِنْ سَاَلْتَهُمْ مَنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ لَيَقُولُنَّ اللّٰهُۜ قُلْ اَفَرَاَيْتُمْ مَا تَدْعُونَ مِنْ دُونِ اللّٰهِ اِنْ اَرَادَنِيَ اللّٰهُ بِضُرٍّ هَلْ هُنَّ كَاشِفَاتُ ضُرِّه۪ٓ اَوْ اَرَادَن۪ي بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكَاتُ رَحْمَتِه۪ۜ قُلْ حَسْبِيَ اللّٰهُۜ عَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُونَ", translation: "Onlara: \"Gökleri ve yeri kim yarattı?\" diye sorsan, \"Elbette Allah.\" Diyecekler. De ki: \"O halde Allah'ın yanı sıra yöneldiğiniz ilahlar, eğer Allah bana bir sıkıntı vermek istese, o sıkıntıya engel olabilirler mi? Veya Allah bana bir rahmet dilese, buna engel olabilirler mi?\" De ki: \"Allah bana yeter!\" De ki: \"Sığınıp güvenmek isteyenler, yalnızca O'na sığınıp güvensinler.\"", transliteration: "Ve le in seeltehum men halakas semavati vel arda le yekulunnallah, kul e fe raeytum ma ted'une min dunillahi in eradeniyallahu bi durrin hel hunne kaşifatu durrihi ev eradeni bi rahmetin hel hunne mumsikatu rahmetih, kul hasbiyallah, aleyhi yetevekkelul mutevekkılun."},
  {id: 4097, sure_no: 39, ayet_no: 39, sure_adi: "Zümer", text: "قُلْ يَا قَوْمِ اعْمَلُوا عَلٰى مَكَانَتِكُمْ اِنّ۪ي عَامِلٌۚ فَسَوْفَ تَعْلَمُونَۙ", translation: "De ki: \"Ey halkım! Elinizden geleni yapın! Ben de bana düşeni yapacağım. Yakında anlayacaksınız!\"", transliteration: "Kul ya kavmi'melu ala mekanetikum inni amil, fe sevfe ta'lemune."},
  {id: 4098, sure_no: 40, ayet_no: 40, sure_adi: "Zümer", text: "مَنْ يَأْت۪يهِ عَذَابٌ يُخْز۪يهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُق۪يمٌ", translation: "\"Alçaltıcı azabın ve kalıcı azabın kime geleceğini.\"", transliteration: "Men ye'tihi azabun yuhzihi ve yahıllu aleyhi azabun mukim."},
  {id: 4099, sure_no: 41, ayet_no: 41, sure_adi: "Zümer", text: "اِنَّٓا اَنْزَلْنَا عَلَيْكَ الْكِتَابَ لِلنَّاسِ بِالْحَقِّۚ فَمَنِ اهْتَدٰى فَلِنَفْسِه۪ۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۚ وَمَٓا اَنْتَ عَلَيْهِمْ بِوَك۪يلٍ۟", translation: "Biz, sana bu Kitap'ı insanlar için \"hakk\" ile indirdik. Artık kim doğru yolu seçerse bu kendi yararınadır. Kim de sapkınlığı seçerse kendi zararına sapmış olur. Sen onların üzerine vekil değilsin.", transliteration: "İnna enzelna aleykel kitabe lin nasi bil hakkı, fe men ihteda fe li nefsih, ve men dalle fe innema yadıllu aleyha, ve ma ente aleyhim bi vekil."},
  {id: 4100, sure_no: 42, ayet_no: 42, sure_adi: "Zümer", text: "اَللّٰهُ يَتَوَفَّى الْاَنْفُسَ ح۪ينَ مَوْتِهَا وَالَّت۪ي لَمْ تَمُتْ ف۪ي مَنَامِهَاۚ فَيُمْسِكُ الَّت۪ي قَضٰى عَلَيْهَا الْمَوْتَ وَيُرْسِلُ الْاُخْرٰٓى اِلٰٓى اَجَلٍ مُسَمًّىۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ", translation: "Allah, öleceklerin ölümleri anında canlarını alır; ölmeyecek olanların da uykularında. Ölümüne hükmettiğini yanında tutar, diğerlerini belirlenmiş bir süreye kadar salıverir. Bunda düşünen bir halk için alınacak pek çok ders vardır.", transliteration: "Allahu yeteveffel enfuse hine mevtiha velleti lem temut fi menamiha, fe yumsikulleti kada aleyhel mevte ve yursilul uhra ila ecelin musemma, inne fi zalike le ayatin li kavmin yetefekkerun."},
  {id: 4101, sure_no: 43, ayet_no: 43, sure_adi: "Zümer", text: "اَمِ اتَّخَذُوا مِنْ دُونِ اللّٰهِ شُفَعَٓاءَۜ  قُلْ اَوَلَوْ كَانُوا لَا يَمْلِكُونَ شَيْـٔاً وَلَا يَعْقِلُونَ", translation: "Yoksa Allah'ın yanı sıra şefaatçiler mi edindiler? De ki: \"Onlar hiçbir şeye sahip olmasalar ve akıl etmeseler de mi?\"", transliteration: "Emittehazu min dunillahi şufeae, kul e ve lev kanu la yemlikune şey'en ve la ya'kılun."},
  {id: 4102, sure_no: 44, ayet_no: 44, sure_adi: "Zümer", text: "قُلْ لِلّٰهِ الشَّفَاعَةُ جَم۪يعاًۜ لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ ثُمَّ اِلَيْهِ تُرْجَعُونَ", translation: "De ki: \"Şefaat etme tamamıyla Allah'ın elindedir. Göklerin ve yerin egemenliği yalnızca O'na aittir. Sonra O'na döndürüleceksiniz.\"", transliteration: "Kul lillahiş şefaatu cemia, lehu mulkus semavati vel ard, summe ileyhi turceun."},
  {id: 4103, sure_no: 45, ayet_no: 45, sure_adi: "Zümer", text: "وَاِذَا ذُكِرَ اللّٰهُ وَحْدَهُ اشْمَاَزَّتْ قُلُوبُ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِۚ وَاِذَا ذُكِرَ الَّذ۪ينَ مِنْ دُونِه۪ٓ اِذَا هُمْ يَسْتَبْشِرُونَ", translation: "Allah, \"tek başına\" anıldığı zaman, ahirete iman etmeyenlerin kalpleri burkulur. O'nun yanı sıra başkası anıldığı zaman sevinip mutluluk duyarlar.", transliteration: "Ve iza zukirallahu vahdehuşmeezzet kulubullezine la yu'minune bil ahıreh, ve iza zukirellezine min dunihi iza hum yestebşirun."},
  {id: 4104, sure_no: 46, ayet_no: 46, sure_adi: "Zümer", text: "قُلِ اللّٰهُمَّ فَاطِرَ السَّمٰوَاتِ وَالْاَرْضِ عَالِمَ الْغَيْبِ وَالشَّهَادَةِ اَنْتَ تَحْكُمُ بَيْنَ عِبَادِكَ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ", translation: "De ki: \"Göklere ve yere işleyiş yasalarını koyan, gaybı ve görüneni bilen Allah'ım! Kullarının aralarında çekiştikleri şeyler hakkında hüküm verecek olan Sen'sin.\"", transliteration: "Kulillahumme fatıras semavati vel ardı alimel gaybi veş şehadeti ente tahkumu beyne ıbadike fi ma kanu fihi yahtelifun."},
  {id: 4105, sure_no: 47, ayet_no: 47, sure_adi: "Zümer", text: "وَلَوْ اَنَّ لِلَّذ۪ينَ ظَلَمُوا مَا فِي الْاَرْضِ جَم۪يعاً وَمِثْلَهُ مَعَهُ لَافْتَدَوْا بِه۪ مِنْ سُٓوءِ الْعَذَابِ يَوْمَ الْقِيٰمَةِۜ وَبَدَا لَهُمْ مِنَ اللّٰهِ مَا لَمْ يَكُونُوا يَحْتَسِبُونَ", translation: "Eğer yeryüzünde olanların tamamı ve bir o kadarı daha, o zulmedenlerin olsaydı, kıyamet günü azabın kötülüğünden kurtulmak için kesinlikle onu fidye olarak verirlerdi. Hiç hesaba katmadıkları şeyler, Allah tarafından karşılarına çıkarılacaktır.", transliteration: "Ve lev enne lillezine zalemu ma fil ardı cemian ve mislehu meahu leftedev bihi min suil azabi yevmel kıyameh, ve beda lehum minallahi ma lem yekunu yahtesibun."},
  {id: 4106, sure_no: 48, ayet_no: 48, sure_adi: "Zümer", text: "وَبَدَا لَهُمْ سَيِّـَٔاتُ مَا كَسَبُوا وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Ve kazandıkları kötülükler açığa çıktı. Kendisi ile alay ettikleri şey onları kuşattı.", transliteration: "Ve beda lehum seyyiatu ma kesebu ve haka bihim ma kanu bihi yestehziun."},
  {id: 4107, sure_no: 49, ayet_no: 49, sure_adi: "Zümer", text: "فَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَانَاۘ ثُمَّ اِذَا خَوَّلْنَاهُ نِعْمَةً مِنَّاۙ قَالَ اِنَّـمَٓا اُو۫ت۪يتُهُ عَلٰى عِلْمٍۜ بَلْ هِيَ فِتْنَةٌ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "İnsanın başı derde girdiği zaman Bize yönelir. Sonra ona tarafımızdan bir yardım bahşettiğimizde: \"Bu bana bilgimden/yeteneğimden dolayı verilmiştir.\" der. Hayır! O bir fitnedir. Ne var ki onların çoğu bilmezler.", transliteration: "Fe iza messel insane durrun deana, summe iza havvelnahu ni'meten minna kale innema utituhu ala ilm, bel hiye fitnetun ve lakinne ekserehum la ya'lemun."},
  {id: 4108, sure_no: 50, ayet_no: 50, sure_adi: "Zümer", text: "قَدْ قَالَهَا الَّذ۪ينَ مِنْ قَبْلِهِمْ فَمَٓا اَغْنٰى عَنْهُمْ مَا كَانُوا يَكْسِبُونَ", translation: "Gerçekten, onlardan öncekiler de böyle söylemişti. Fakat kazanmış oldukları şeyler onlara bir yarar sağlamadı.", transliteration: "Kad kalehellezine min kablihim fe ma agna anhum ma kanu yeksibun."},
  {id: 4109, sure_no: 51, ayet_no: 51, sure_adi: "Zümer", text: "فَاَصَابَهُمْ سَيِّـَٔاتُ مَا كَسَبُواۜ وَالَّذ۪ينَ ظَلَمُوا مِنْ هٰٓؤُ۬لَٓاءِ سَيُص۪يبُهُمْ سَيِّـَٔاتُ مَا كَسَبُواۙ وَمَا هُمْ بِمُعْجِز۪ينَ", translation: "Kazandıkları şeylerin kötülükleri kendilerine isabet etti. Bunlardan zulmeden kimselerin de kazandıkları şeylerin kötülükleri onlara yakında isabet edecektir. Ve onlar azabı önleyecek güce sahip değiller.", transliteration: "Fe esabehum seyyiatu ma kesebu, vellezine zalemu min haulai se yusibuhum seyyiatu ma kesebu ve ma hum bi mu'cizin."},
  {id: 4110, sure_no: 52, ayet_no: 52, sure_adi: "Zümer", text: "اَوَلَمْ يَعْلَمُٓوا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يُؤْمِنُونَ۟", translation: "Allah'ın dilediğine rızkı genişlettiğini ve ölçülendirdiğini bilmediler mi? Bunda iman edecek bir toplum için ayetler vardır.", transliteration: "E ve lem ya'lemu ennallahe yebsutur rızka li men yeşau ve yakdir, inne fi zalike le ayatin li kavmin yu'minun."},
  {id: 4111, sure_no: 53, ayet_no: 53, sure_adi: "Zümer", text: "قُلْ يَا عِبَادِيَ الَّذ۪ينَ اَسْرَفُوا عَلٰٓى اَنْفُسِهِمْ لَا تَقْنَطُوا مِنْ رَحْمَةِ اللّٰهِۜ اِنَّ اللّٰهَ يَغْفِرُ الذُّنُوبَ جَم۪يعاًۜ اِنَّهُ هُوَ الْغَفُورُ الرَّح۪يمُ", translation: "De ki: \"Ey kendilerine karşı israf eden kullarım! Allah'ın rahmetinden ümit kesmeyin. Allah, suçların hepsini bağışlar. O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.\"", transliteration: "Kul ya ıbadiyellezine esrefu ala enfusihim la taknetu min rahmetillah, innallahe yagfiruz zunube cemia, innehu huvel gafurur rahim."},
  {id: 4112, sure_no: 54, ayet_no: 54, sure_adi: "Zümer", text: "وَاَن۪يبُٓوا اِلٰى رَبِّكُمْ وَاَسْلِمُوا لَهُ مِنْ قَبْلِ اَنْ يَأْتِيَكُمُ الْعَذَابُ ثُمَّ لَا تُنْصَرُونَ", translation: "Rabb'inize yönelin! Ve size azap gelmeden önce O'na teslim olun. Sonra yardım olunmazsınız.", transliteration: "Ve enibu ila rabbikum ve eslimu lehu min kabli en ye'tiyekumul azabu summe la tunsarun."},
  {id: 4113, sure_no: 55, ayet_no: 55, sure_adi: "Zümer", text: "وَاتَّبِعُٓوا اَحْسَنَ مَٓا اُنْزِلَ اِلَيْكُمْ مِنْ رَبِّكُمْ مِنْ قَبْلِ اَنْ يَأْتِيَكُمُ الْعَذَابُ بَغْتَةً وَاَنْتُمْ لَا تَشْعُرُونَۙ", translation: "Hiç beklemediğiniz bir anda, size ansızın azap gelmeden önce, Rabb'inizden size indirilen en iyi olana uyun.", transliteration: "Vettebiu ahsene ma unzile ileykum min rabbikum min kabli en ye'tiyekumul azabu bagteten ve entum la teş'urun."},
  {id: 4114, sure_no: 56, ayet_no: 56, sure_adi: "Zümer", text: "اَنْ تَقُولَ نَفْسٌ يَا حَسْرَتٰى عَلٰى مَا فَرَّطْتُ ف۪ي جَنْبِ اللّٰهِ وَاِنْ كُنْتُ لَمِنَ السَّاخِر۪ينَۙ", translation: "Sonunda: \"Allah'a karşı aşırı gittiğimden dolayı yazıklar olsun bana! Doğrusu ben alay edenlerdendim.\" dememeniz için;", transliteration: "En tekule nefsun ya hasreta ala ma ferrattu fi cenbillahi ve in kuntu le mines sahirin."},
  {id: 4115, sure_no: 57, ayet_no: 57, sure_adi: "Zümer", text: "اَوْ تَقُولَ لَوْ اَنَّ اللّٰهَ هَدٰين۪ي لَكُنْتُ مِنَ الْمُتَّق۪ينَۙ", translation: "Veya \"Allah bana doğru yolu gösterseydi, takva sahibi olurdum.\" dememeniz için;", transliteration: "Ev tekule lev ennallahe hedani le kuntu minel muttekin."},
  {id: 4116, sure_no: 58, ayet_no: 58, sure_adi: "Zümer", text: "اَوْ تَقُولَ ح۪ينَ تَرَى الْعَذَابَ لَوْ اَنَّ ل۪ي كَرَّةً فَاَكُونَ مِنَ الْمُحْسِن۪ينَ", translation: "Veya azabı gördüğü zaman, \"Keşke bir kez daha dünyaya dönme imkanım olsaydı, o zaman muhsinlerden olurdum.\" dememeniz için.", transliteration: "Ev tekule hine terel azabe lev enne li kerreten fe ekune minel muhsinin."},
  {id: 4117, sure_no: 59, ayet_no: 59, sure_adi: "Zümer", text: "بَلٰى قَدْ جَٓاءَتْكَ اٰيَات۪ي فَكَذَّبْتَ بِهَا وَاسْتَكْبَرْتَ وَكُنْتَ مِنَ الْكَافِر۪ينَ", translation: "Hayır, sana ayetlerim gelmişti de onları yalanlamıştın, büyüklük taslamış ve kafirlerden olmuştun.", transliteration: "Bela kad caetke ayati fe kezzebte biha vestekberte ve kunte minel kafirin."},
  {id: 4118, sure_no: 60, ayet_no: 60, sure_adi: "Zümer", text: "وَيَوْمَ الْقِيٰمَةِ تَرَى الَّذ۪ينَ كَذَبُوا عَلَى اللّٰهِ وُجُوهُهُمْ مُسْوَدَّةٌۜ اَلَيْسَ ف۪ي جَهَنَّمَ مَثْوًى لِلْمُتَكَبِّر۪ينَ", translation: "Kıyamet Günü, Allah hakkında yalan söyleyenlerin yüzlerini kararmış görürsün. Büyüklük taslayanların yeri Cehennem'de değil mi?", transliteration: "Ve yevmel kıyameti terellezine kezebu alallahi vucuhuhum musveddeh, e leysefi cehenneme mesven lil mutekebbirin."},
  {id: 4119, sure_no: 61, ayet_no: 61, sure_adi: "Zümer", text: "وَيُنَجِّي اللّٰهُ الَّذ۪ينَ اتَّقَوْا بِمَفَازَتِهِمْۘ لَا يَمَسُّهُمُ السُّٓوءُ وَلَا هُمْ يَحْزَنُونَ", translation: "Allah, takva sahiplerini, hak ettikleri kurtuluşa erdirecektir. Onlara kötülük dokunmaz. Onlar üzülmezler de.", transliteration: "Ve yuneccillahullezinettekav bi mefazetihim la yemessuhumus suu ve la hum yahzenun."},
  {id: 4120, sure_no: 62, ayet_no: 62, sure_adi: "Zümer", text: "اَللّٰهُ خَالِقُ كُلِّ شَيْءٍۘ وَهُوَ عَلٰى كُلِّ شَيْءٍ وَك۪يلٌ", translation: "Allah, her şeyin yaratıcısıdır. O, Her Şeye Vekil'dir.", transliteration: "Allahu haliku kulli şey'in ve huve ala kulli şey'in vekil."},
  {id: 4121, sure_no: 63, ayet_no: 63, sure_adi: "Zümer", text: "لَهُ مَقَال۪يدُ السَّمٰوَاتِ وَالْاَرْضِۜ وَالَّذ۪ينَ كَفَرُوا بِاٰيَاتِ اللّٰهِ اُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ۟", translation: "Göklerin ve yerin kilidi O'na aittir. Allah'ın ayetlerini yalanlayanlar; işte onlar, hüsrana uğrayanlardır.", transliteration: "Lehu makalidus semavati vel ard, vellezine keferu bi ayatillahi ulaike humul hasirun."},
  {id: 4122, sure_no: 64, ayet_no: 64, sure_adi: "Zümer", text: "قُلْ اَفَغَيْرَ اللّٰهِ تَأْمُرُٓونّ۪ٓي اَعْبُدُ اَيُّهَا الْجَاهِلُونَ", translation: "De ki: \"Ey cahiller! Buna rağmen, Allah'tan başkasına kulluk etmemi mi istiyorsunuz?\"", transliteration: "Kul e fe gayrallahi te'murunni a'budu eyyuhel cahilun."},
  {id: 4123, sure_no: 65, ayet_no: 65, sure_adi: "Zümer", text: "وَلَقَدْ اُو۫حِيَ اِلَيْكَ وَاِلَى الَّذ۪ينَ مِنْ قَبْلِكَۚ لَئِنْ اَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ الْخَاسِر۪ينَ", translation: "Ant olsun ki, sana ve senden öncekilere şöyle vahyolundu: \"Eğer Allah'a şirk koşarsan şunu iyi bil ki bütün yaptıkların boşa gider ve hüsrana uğrayanlardan olursun.\"", transliteration: "Ve lekad uhıye ileyke ve ilellezine min kablik, le in eşrekte le yahbetanne ameluke ve le tekunenne minel hasirin."},
  {id: 4124, sure_no: 66, ayet_no: 66, sure_adi: "Zümer", text: "بَلِ اللّٰهَ فَاعْبُدْ وَكُنْ مِنَ الشَّاكِر۪ينَ", translation: "\"O halde yalnız Allah'a kulluk et ve şükredenlerden ol!\"", transliteration: "Belillahe fa'bud ve kun mineş şakirin."},
  {id: 4125, sure_no: 67, ayet_no: 67, sure_adi: "Zümer", text: "وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِه۪ۗ وَالْاَرْضُ جَم۪يعاً قَبْضَتُهُ يَوْمَ الْقِيٰمَةِ وَالسَّمٰوَاتُ مَطْوِيَّاتٌ بِيَم۪ينِه۪ۜ سُبْحَانَهُ وَتَعَالٰى عَمَّا يُشْرِكُونَ", translation: "Allah'ı hakkıyla takdir etmediler. Yeryüzü, kıyamet günü tamamıyla O'nun avucundadır. Gökler de O'nun sağ eliyle dürülmüş olacaktır. O, onların ortak koştuklarından münezzehtir ve çok yücedir.", transliteration: "Ve ma kaderullahe hakka kadrihi vel ardu cemian kabdatuhu yevmel kıyameti ves semavatu matviyyatun bi yeminih, subhanehu ve te'ala amma yuşrikun."},
  {id: 4126, sure_no: 68, ayet_no: 68, sure_adi: "Zümer", text: "وَنُفِـخَ فِي الصُّورِ فَصَعِقَ مَنْ فِي السَّمٰوَاتِ وَمَنْ فِي الْاَرْضِ اِلَّا مَنْ شَٓاءَ اللّٰهُۚ ثُمَّ نُفِـخَ ف۪يهِ اُخْرٰى فَاِذَا هُمْ قِيَامٌ يَنْظُرُونَ", translation: "Sur'a üflenmiş, Allah'ın dilediği hariç göklerde ve yerde olanlar çarpılıp yıkılıvermiştir. Sonra ona bir kez daha üfürüldüğü zaman onlar kalkarak bakınırlar.", transliteration: "Ve nufiha fis suri fe saıka men fis semavati ve men fil ardı illa men şaallah, summe nufiha fihi uhra fe izahum kıyamun yanzurun."},
  {id: 4127, sure_no: 69, ayet_no: 69, sure_adi: "Zümer", text: "وَاَشْرَقَتِ الْاَرْضُ بِنُورِ رَبِّهَا وَوُضِعَ الْكِتَابُ وَج۪ٓيءَ بِالنَّبِيّ۪نَ وَالشُّهَدَٓاءِ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَهُمْ لَا يُظْلَمُونَ", translation: "Yer Rabb'inin aydınlığı ile aydınlanır, kitap ortaya konur, Nebiler ve tanıklar getirilir; aralarında hakk ile karar verilir. Ve onlar asla haksızlığa uğramazlar.", transliteration: "Ve eşrekatil ardu bi nuri rabbiha ve vudıal kitabu ve cie bin nebiyyine veş şuhedai ve kudıye beynehum bil hakkı ve hum la yuzlemun."},
  {id: 4128, sure_no: 70, ayet_no: 70, sure_adi: "Zümer", text: "وَوُفِّيَتْ كُلُّ نَفْسٍ مَا عَمِلَتْ وَهُوَ اَعْلَمُ بِمَا يَفْعَلُونَ۟", translation: "Herkese yaptığının karşılığı tam olarak verilir. O, onların yaptıklarını en iyi bilendir.", transliteration: "Ve vuffiyet kullu nefsin ma amilet ve huve a'lemu bima yef'alun."},
  {id: 4129, sure_no: 71, ayet_no: 71, sure_adi: "Zümer", text: "وَس۪يقَ الَّذ۪ينَ كَفَرُٓوا اِلٰى جَهَنَّمَ زُمَراًۜ حَتّٰٓى اِذَا جَٓاؤُ۫هَا فُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُـهَٓا اَلَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَتْلُونَ عَلَيْكُمْ اٰيَاتِ رَبِّكُمْ وَيُنْذِرُونَكُمْ لِقَٓاءَ يَوْمِكُمْ هٰذَاۜ قَالُوا بَلٰى وَلٰكِنْ حَقَّتْ كَلِمَةُ الْعَذَابِ عَلَى الْكَافِر۪ينَ", translation: "Kafirler bölük bölük Cehennem'e sürülürler. Oraya vardıklarında, kapıları açılır. Cehennem'in bekçileri onlara: \"İçinizden size Rabb'inizin ayetlerini okuyan, sizi bu gününüzle karşılaşacağınıza dair uyaran Resuller gelmedi mi?\" derler. Onlar: \"Evet geldi.\" derler. Fakat azap sözü Kafirlerin üzerine gerçekleşti.", transliteration: "Vesikallezine keferu ila cehenneme zumera, hatta iza cauha futihat ebvabuha, ve kale lehum hazenetuha e lem ye'tikum rusulun minkum yetlune aleykum ayati rabbikum ve yunzirunekum likae yevmikum haza, kalu bela ve lakin hakkat kelimetul azabi alel kafirin."},
  {id: 4130, sure_no: 72, ayet_no: 72, sure_adi: "Zümer", text: "ق۪يلَ ادْخُلُٓوا اَبْوَابَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَاۚ فَبِئْسَ مَثْوَى الْمُتَكَبِّر۪ينَ", translation: "\"İçinde sürekli kalmak üzere Cehennem'in kapılarından girin!\" denir. Büyüklük taslayanların kalacakları yer ne kötüdür.", transliteration: "Kiledhulu ebvabe cehenneme halidine fiha, fe bi'se mesvel mutekebbirin."},
  {id: 4131, sure_no: 73, ayet_no: 73, sure_adi: "Zümer", text: "وَس۪يقَ الَّذ۪ينَ اتَّقَوْا رَبَّهُمْ اِلَى الْجَنَّةِ زُمَراًۜ حَتّٰٓى اِذَا جَٓاؤُ۫هَا وَفُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلَامٌ عَلَيْكُمْ طِبْتُمْ فَادْخُلُوهَا خَالِد۪ينَ", translation: "Rabb'lerine karşı takva sahibi olanlar grup grup Cennet'e sevk edilirler. Oraya vardıklarında, onun kapıları açılır. Onun görevlileri, onlara: \"Selam sizlere, siz aklandınız! Sürekli kalmak üzere ona girin.\" derler.", transliteration: "Vesikallezinettekav rabbehum ilel cenneti zumera, hatta iza cauha ve futihat ebvabuha ve kale lehum hazenetuha selamun aleykum tıbtum fedhuluha halidin."},
  {id: 4132, sure_no: 74, ayet_no: 74, sure_adi: "Zümer", text: "وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذ۪ي صَدَقَنَا وَعْدَهُ وَاَوْرَثَنَا الْاَرْضَ نَتَبَوَّاُ مِنَ الْجَنَّةِ حَيْثُ نَشَٓاءُۚ فَنِعْمَ اَجْرُ الْعَامِل۪ينَ", translation: "Onlar da: \"Hamd, bize verdiği sözü yerine getiren Allah'a özgüdür. Bizi bu yere varis kıldı. Cennet'te istediğimiz yerde kalabiliyoruz. Çalışanların ödülü ne güzel.\" derler.", transliteration: "Ve kalul hamdu lillahillezi sadakana va'dehu ve evresenel arda netebevveu minel cenneti haysu neşa, fe ni'me ecrul amilin."},
  {id: 4133, sure_no: 75, ayet_no: 75, sure_adi: "Zümer", text: "وَتَرَى الْمَلٰٓئِكَةَ حَٓافّ۪ينَ مِنْ حَوْلِ الْعَرْشِ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْۚ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَق۪يلَ الْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَ", translation: "Melekleri, arşın çevresini kuşatmış olarak, Rabb'lerini övgü ile yücelttiklerini görürsün. Artık onların aralarında Hakk ile hüküm verilmiştir. \"Alemlerin Rabb'ine hamdolsun.\" denir.", transliteration: "Ve terel melaikete haffine min havlil arşı yusebbihune bi hamdi rabbihim, ve kudıye beynehum bil hakkı ve kıylel hamdu lillahi rabbil alemin."},
  {id: 4134, sure_no: 1, ayet_no: 1, sure_adi: "Mümin", text: "حٰمٓۜ", translation: "Ha, Mim.", transliteration: "Ha mim."},
  {id: 4135, sure_no: 2, ayet_no: 2, sure_adi: "Mümin", text: "تَنْز۪يلُ الْكِتَابِ مِنَ اللّٰهِ الْعَز۪يزِ الْعَل۪يمِۙ", translation: "Bu Kitap'ın indirilmesi, Mutlak Üstün Olan, Her Şeyi Bilen Allah tarafındandır.", transliteration: "Tenzilul kitabi minallahil azizil alim."},
  {id: 4136, sure_no: 3, ayet_no: 3, sure_adi: "Mümin", text: "غَافِرِ الذَّنْبِ وَقَابِلِ التَّوْبِ شَد۪يدِ الْعِقَابِ ذِي الطَّوْلِۜ لَٓا اِلٰهَ اِلَّا هُوَۜ اِلَيْهِ الْمَص۪يرُ", translation: "O, suçu bağışlayan, tevbeyi kabul eden, cezası şiddetli, sınırsız güç sahibi olandır. O'ndan başka ilah yoktur. Dönüş, yalnızca O'nadır.", transliteration: "Gafiriz zenbi ve kabilit tevbi şedidil ikabi zit tavl, la ilahe illa huve, ileyhil masir."},
  {id: 4137, sure_no: 4, ayet_no: 4, sure_adi: "Mümin", text: "مَا يُجَادِلُ ف۪ٓي اٰيَاتِ اللّٰهِ اِلَّا الَّذ۪ينَ كَفَرُوا فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِي الْبِلَادِ", translation: "Kafirlerden başkası Allah'ın ayetleri hakkında tartışmaz. Onların beldelerde diledikleri gibi gezip tozmaları seni aldatmasın.", transliteration: "Ma yucadilu fi ayatillahi illellezine keferu fe la yagrurke tekallubuhum fil bilad."},
  {id: 4138, sure_no: 5, ayet_no: 5, sure_adi: "Mümin", text: "كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَالْاَحْزَابُ مِنْ بَعْدِهِمْۖ وَهَمَّتْ كُلُّ اُمَّةٍ بِرَسُولِهِمْ لِيَأْخُذُوهُ وَجَادَلُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَاَخَذْتُهُمْ۠ فَكَيْفَ كَانَ عِقَابِ", translation: "Onlardan önce Nuh halkı ve onlardan sonraki topluluklar da yalanladılar. Her ümmet, kendi Resullerini yalanlamaya yöneldi. Hakk'ı, Batıl'la yok etmek için mücadele ettiler. Ben de onları yakalayıverdim. Akabinde cezalandırmam nasılmış gördüler!", transliteration: "Kezzebet kablehum kavmu nuhın vel ahzabu min ba'dıhım ve hemmet kullu ummetin bi resulihim li ye'huzuhu ve cadelu bil batılı li yudhıdu bihil hakka fe ehaztuhum, fe keyfe kane ıkab."},
  {id: 4139, sure_no: 6, ayet_no: 6, sure_adi: "Mümin", text: "وَكَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذ۪ينَ كَفَرُٓوا اَنَّهُمْ اَصْحَابُ النَّارِۢ", translation: "İşte böylece Kafirlerin üzerine, Rabb'inin, \"Kuşkusuz ki onlar Cehennem halkıdır.\" kelimesi hak oldu.", transliteration: "Ve kezalike hakkat kelimetu rabbike alellezine keferu ennehum ashabun nar."},
  {id: 4140, sure_no: 7, ayet_no: 7, sure_adi: "Mümin", text: "اَلَّذ۪ينَ يَحْمِلُونَ الْعَرْشَ وَمَنْ حَوْلَهُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُونَ بِه۪ وَيَسْتَغْفِرُونَ لِلَّذ۪ينَ اٰمَنُواۚ رَبَّـنَا وَسِعْتَ كُلَّ شَيْءٍ رَحْمَةً وَعِلْماً فَاغْفِرْ لِلَّذ۪ينَ تَابُوا وَاتَّبَعُوا سَب۪يلَكَ وَقِهِمْ عَذَابَ الْجَح۪يمِ", translation: "Arş'ı yüklenenler ve çevresinde bulunanlar, Rabb'lerini övgü ile yüceltirler. Ve O'na iman ederler. İman Edenler için bağışlanma dilerler: \"Rabb'imiz! Sen, rahmet ve bilgice her şeyi kuşattın. Tevbe edip senin dosdoğru yoluna uyan kimseleri bağışla. Onları Cehennem azabından koru.\"", transliteration: "Ellezine yahmilunel arşa ve men havlehu yusebbihune bi hamdi rabbihim ve yu'minune bihi ve yestagfirune lillezine amenu, rabbena vesi'te kulle şey'in rahmeten ve ilmen fagfir lillezine tabu vettebeu sebileke vekıhim azabel cahim."},
  {id: 4141, sure_no: 8, ayet_no: 8, sure_adi: "Mümin", text: "رَبَّنَا وَاَدْخِلْهُمْ جَنَّاتِ عَدْنٍۨ الَّت۪ي وَعَدْتَهُمْ وَمَنْ صَلَحَ مِنْ اٰبَٓائِهِمْ وَاَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْۜ اِنَّكَ اَنْتَ الْعَز۪يزُ الْحَك۪يمُۚ", translation: "\"Rabb'imiz! Onları; onların atalarından, eşlerinden ve soylarından salih kimseleri, kendilerine söz verdiğin Adn Cennetlerine koy. Sen, Mutlak Üstün Olan'sın, En İyi Hüküm Veren'sin.\"", transliteration: "Rabbena ve edhilhum cennati adninilleti vaadtehum ve men salaha min abaihim ve ezvacihim ve zurriyyatihim inneke entel azizul hakim."},
  {id: 4142, sure_no: 9, ayet_no: 9, sure_adi: "Mümin", text: "وَقِهِمُ السَّيِّـَٔاتِۜ  وَمَنْ تَقِ السَّيِّـَٔاتِ يَوْمَئِذٍ فَقَدْ رَحِمْتَهُۜ وَذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُ۟", translation: "\"Onları kötülüklerden koru. Ve Sen O Gün kimi kötülüklerden korursan, ona rahmet etmiş olursun. İşte bu, büyük kurtuluştur.\"", transliteration: "Vekıhimus seyyiat, ve men tekıs seyyiati yevme izin fe kad rahimteh ve zalike huvel fevzul azim."},
  {id: 4143, sure_no: 10, ayet_no: 10, sure_adi: "Mümin", text: "اِنَّ الَّذ۪ينَ كَفَرُوا يُنَادَوْنَ لَمَقْتُ اللّٰهِ اَكْبَرُ مِنْ مَقْتِكُمْ اَنْفُسَكُمْ اِذْ تُدْعَوْنَ اِلَى الْا۪يمَانِ فَتَكْفُرُونَ", translation: "Kafirlere seslenilir: \"Elbette ki Allah'ın kızgınlığı, kendinize duymuş olduğunuz kızgınlıktan daha büyüktür. Zira siz inanmaya çağrıldığınız zaman, kafirlik ediyordunuz.\"", transliteration: "İnnellezine keferu yunadevne le maktullahi ekberu min maktikum enfusekum iz tud'avne ilel imani fe tekfurun."},
  {id: 4144, sure_no: 11, ayet_no: 11, sure_adi: "Mümin", text: "قَالُوا رَبَّنَٓا اَمَتَّنَا اثْنَتَيْنِ وَاَحْيَيْتَنَا اثْنَتَيْنِ فَاعْتَرَفْنَا بِذُنُوبِنَا فَهَلْ اِلٰى خُرُوجٍ مِنْ سَب۪يلٍ", translation: "Dediler ki: \"Rabb'imiz! Bizi iki kez öldürdün, iki kez dirilttin. Artık suçlarımızı itiraf ettik. Şimdi bu durumdan kurtulmanın bir yolu var mı?\"", transliteration: "Kalu rabbena emettenesneteyni ve ahyeytenesneteyni fa'terefna bi zunubina fe hel ila hurucin min sebil."},
  {id: 4145, sure_no: 12, ayet_no: 12, sure_adi: "Mümin", text: "ذٰلِكُمْ بِاَنَّـهُٓ اِذَا دُعِيَ اللّٰهُ وَحْدَهُ كَفَرْتُمْۚ وَاِنْ يُشْرَكْ بِه۪ تُؤْمِنُواۜ فَالْحُكْمُ لِلّٰهِ الْعَلِيِّ الْكَب۪يرِ", translation: "Bu, sizin bir tek Allah'a çağrıldığınız zaman küfretmeniz nedeniyledir. O'na şirk koşulunca iman ediyordunuz. Artık karar yüce ve büyük olan Allah'ındır.", transliteration: "Zalikum bi ennehu iza duiyallahu vahdehu kefertum, ve in yuşrek bihi tu'minu, fel hukmu lillahil aliyyil kebir."},
  {id: 4146, sure_no: 13, ayet_no: 13, sure_adi: "Mümin", text: "هُوَ الَّذ۪ي يُر۪يكُمْ اٰيَاتِه۪ وَيُنَزِّلُ لَكُمْ مِنَ السَّمَٓاءِ رِزْقاًۜ وَمَا يَتَذَكَّرُ اِلَّا مَنْ يُن۪يبُ", translation: "O, size ayetlerini gösteren, sizin için gökten rızık indirendir. Ancak, gönülden yönelenler öğüt alırlar.", transliteration: "Huvellezi yurikum ayatihi ve yunezzilu lekum mines semai rızka, ve ma yetezekkeru illa men yunib."},
  {id: 4147, sure_no: 14, ayet_no: 14, sure_adi: "Mümin", text: "فَادْعُوا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَ وَلَوْ كَرِهَ الْكَافِرُونَ", translation: "Öyleyse, Kafirler hoşlanmasa da dini yalnızca kendisine özgü kılarak Allah'a dua edin.", transliteration: "Fed'ullahe muhlisine lehud dine ve lev kerihel kafirun."},
  {id: 4148, sure_no: 15, ayet_no: 15, sure_adi: "Mümin", text: "رَف۪يعُ الدَّرَجَاتِ ذُوالْعَرْشِۚ يُلْقِي الرُّوحَ مِنْ اَمْرِه۪ عَلٰى مَنْ يَشَٓاءُ مِنْ عِبَادِه۪ لِيُنْذِرَ يَوْمَ التَّلَاقِۙ", translation: "O, dereceleri yükseltendir, Arş'ın sahibidir. O, \"Buluşma Günü\" hakkında insanları uyarmak için Kendi buyruğundan olan ruhu kullarından dilediğine ulaştırır.", transliteration: "Refiud derecati zul arş, yulkır ruha min emrihi ala men yeşau min ıbadihi li yunzire yevmet telak."},
  {id: 4149, sure_no: 16, ayet_no: 16, sure_adi: "Mümin", text: "يَوْمَ هُمْ بَارِزُونَۚ لَا يَخْفٰى عَلَى اللّٰهِ مِنْهُمْ شَيْءٌۜ لِمَنِ الْمُلْكُ الْيَوْمَۜ لِلّٰهِ الْوَاحِدِ الْقَهَّارِ", translation: "O Gün, onlar ortaya çıkarlar. Onların hiçbir şeyi Allah'a gizli kalmaz. O Gün, mülk kimindir? Tek ve Kahhar Olan Allah'ındır.", transliteration: "Yevme hum barizun la yahfa alallahi min hum şey'un, li menil mulkul yevm, lillahil vahidil kahhar."},
  {id: 4150, sure_no: 17, ayet_no: 17, sure_adi: "Mümin", text: "اَلْيَوْمَ تُجْزٰى كُلُّ نَفْسٍ بِمَا كَسَبَتْۜ لَا ظُلْمَ الْيَوْمَۜ اِنَّ اللّٰهَ سَر۪يعُ الْحِسَابِ", translation: "Bugün her kişi kazandıklarıyla hesaba çekilecektir. Bugün, haksızlık yoktur. Kuşkusuz Allah hesabı çabuk görendir.", transliteration: "El yevme tucza kullu nefsin bima kesebet, la zulmel yevm, innallahe seriul hisab."},
  {id: 4151, sure_no: 18, ayet_no: 18, sure_adi: "Mümin", text: "وَاَنْذِرْهُمْ يَوْمَ الْاٰزِفَةِ اِذِ الْقُلُوبُ لَدَى الْحَنَاجِرِ كَاظِم۪ينَۜ مَا لِلظَّالِم۪ينَ مِنْ حَم۪يمٍ وَلَا شَف۪يعٍ يُطَاعُۜ", translation: "Yaklaşan gün hakkında onları uyar. O Gün korkudan yürekler ağızlara gelir. Zalimler için ne samimi bir dost ne de sözü dinlenir bir şefaatçi vardır.", transliteration: "Ve enzirhum yevmel azifeti izil kulubu ledel hanaciri kazımin, ma liz zalimine min hamimin ve la şefiin yutau."},
  {id: 4152, sure_no: 19, ayet_no: 19, sure_adi: "Mümin", text: "يَعْلَمُ خَٓائِنَةَ الْاَعْيُنِ وَمَا تُخْفِي الصُّدُورُ", translation: "O, bakışlardaki art niyeti ve göğüslerin gizlediğini bilir.", transliteration: "Ya'lemu hainetel a'yuni ve ma tuhfis sudur."},
  {id: 4153, sure_no: 20, ayet_no: 20, sure_adi: "Mümin", text: "وَاللّٰهُ يَقْض۪ي بِالْحَقِّۜ وَالَّذ۪ينَ يَدْعُونَ مِنْ دُونِه۪ لَا يَقْضُونَ بِشَيْءٍۜ اِنَّ اللّٰهَ هُوَ السَّم۪يعُ الْبَص۪يرُ۟", translation: "Allah, Hakkı gerçekleştirir. Allah'ın yanı sıra yöneldikleri kimseler ise hiçbir şeyi gerçekleştiremezler. Kuşkusuz Allah, Her Şeyi Duyan, Her Şeyi Gören'dir.", transliteration: "Vallahu yakdi bil hakk, vellezine yed'une min dunihi la yakdune bi şey'in, innallahe huves semiul basir."},
  {id: 4154, sure_no: 21, ayet_no: 21, sure_adi: "Mümin", text: "اَوَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ كَانُوا مِنْ قَبْلِهِمْۜ كَانُوا هُمْ اَشَدَّ مِنْهُمْ قُوَّةً وَاٰثَاراً فِي الْاَرْضِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوبِهِمْ وَمَا كَانَ لَهُمْ مِنَ اللّٰهِ مِنْ وَاقٍ", translation: "Onlar, yeryüzünde gezinip, kendilerinden önce gelip geçmiş olanların sonlarının nasıl olduğuna bakmazlar mı? Onlar, yeryüzünde güç olarak ve bıraktıkları eserler bakımından daha üstündüler. Yine de Allah onları, suçları nedeniyle cezalandırdı. Kendilerini Allah'tan koruyan kimse olmadı.", transliteration: "E ve lem yesiru fil ardı fe yenzuru keyfe kane akibetullezine kanu min kablihim, kanu hum eşedde min hum kuvveten ve asaran fil ardı fe ehazehumullahu bi zunubihim ve ma kane lehum minallahi min vak."},
  {id: 4155, sure_no: 22, ayet_no: 22, sure_adi: "Mümin", text: "ذٰلِكَ بِاَنَّهُمْ كَانَتْ تَأْت۪يهِمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَكَفَرُوا فَاَخَذَهُمُ اللّٰهُۜ اِنَّهُ قَوِيٌّ شَد۪يدُ الْعِقَابِ", translation: "Bu, Resuller kendilerine kanıt içeren belgelerle geldikleri halde onları yalanlamaları nedeniyledir. Bunun üzerine Allah onları cezalandırdı. O, Mutlak Güç Sahibi'dir, Cezalandırması Çok Şiddetli Olan'dır.", transliteration: "Zalike bi ennehum kanet te'tihim rusuluhum bil beyyinati fe keferu fe ehazehumullah, innehu kaviyyun şedidul ikab."},
  {id: 4156, sure_no: 23, ayet_no: 23, sure_adi: "Mümin", text: "وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيَاتِنَا وَسُلْطَانٍ مُب۪ينٍۙ", translation: "Ant olsun ki, Musa'yı ayetlerimizle ve apaçık bir yetkiyle gönderdik:", transliteration: "Ve lekad erselna musa bi ayatina ve sultanin mubin."},
  {id: 4157, sure_no: 24, ayet_no: 24, sure_adi: "Mümin", text: "اِلٰى فِرْعَوْنَ وَهَامَانَ وَقَارُونَ فَقَالُوا سَاحِرٌ كَذَّابٌ", translation: "Firavun'a, Haman'a ve Karun'a. Fakat onlar: \"Bu yalancı bir sihirbazdır.\" dediler.", transliteration: "İla fir'avne ve hamane ve karune fe kalu sahirun kezzab."},
  {id: 4158, sure_no: 25, ayet_no: 25, sure_adi: "Mümin", text: "فَلَمَّا جَٓاءَهُمْ بِالْحَقِّ مِنْ عِنْدِنَا قَالُوا اقْتُلُٓوا اَبْنَٓاءَ الَّذ۪ينَ اٰمَنُوا مَعَهُ وَاسْتَحْيُوا نِسَٓاءَهُمْۜ وَمَا كَيْدُ الْكَافِر۪ينَ اِلَّا ف۪ي ضَلَالٍ", translation: "Böylece katımızdan Hakk ile geldiği zaman onlar: \"Onunla birlikte iman eden kimselerin oğullarını öldürün; kadınlarını ise sağ bırakın.\" dediler. Kafirlerin düzeni, sapkınlığa düşmekten başka bir şey değildir.", transliteration: "Fe lemma caehum bil hakkı min indina kaluktulu ebnaellezine amenu meahu vestahyu nisaehum, ve ma keydul kafirine illa fi dalal."},
  {id: 4159, sure_no: 26, ayet_no: 26, sure_adi: "Mümin", text: "وَقَالَ فِرْعَوْنُ ذَرُون۪ٓي اَقْتُلْ مُوسٰى وَلْيَدْعُ رَبَّهُۚ اِنّ۪ٓي اَخَافُ اَنْ يُبَدِّلَ د۪ينَكُمْ اَوْ اَنْ يُظْهِرَ فِي الْاَرْضِ الْفَسَادَ", translation: "Firavun: \"Bırakın beni, Musa'yı öldüreyim. O istediği kadar Rabb'ini yardıma çağırsın. Ben, onun sizin dininizi değiştirmesinden veya yeryüzünde fesat çıkarmasından korkuyorum.\" dedi.", transliteration: "Ve kale fir'avnu zeruni aktul musa vel yed'u rabbeh, inni ehafu en yubeddile dinekum ev en yuzhire fil ardıl fesad."},
  {id: 4160, sure_no: 27, ayet_no: 27, sure_adi: "Mümin", text: "وَقَالَ مُوسٰٓى اِنّ۪ي عُذْتُ بِرَبّ۪ي وَرَبِّكُمْ مِنْ كُلِّ مُتَكَبِّرٍ لَا يُؤْمِنُ بِيَوْمِ الْحِسَابِ۟", translation: "Musa: \"Kuşkusuz ben, hesap gününe iman etmeyen bütün kibirlilerden, benim de Rabb'im, sizin de Rabb'iniz olan Allah'a sığınırım.\" dedi.", transliteration: "Ve kale musa inni uztu bi rabbi ve rabbikum min kulli mutekebbirin la yu'minu bi yevmil hisab."},
  {id: 4161, sure_no: 28, ayet_no: 28, sure_adi: "Mümin", text: "وَقَالَ رَجُلٌ مُؤْمِنٌۗ مِنْ اٰلِ فِرْعَوْنَ يَكْتُمُ ا۪يمَانَهُٓ اَتَقْتُلُونَ رَجُلاً اَنْ يَقُولَ رَبِّيَ اللّٰهُ وَقَدْ جَٓاءَكُمْ بِالْبَيِّنَاتِ مِنْ رَبِّكُمْۜ وَاِنْ يَكُ كَاذِباً فَعَلَيْهِ كَذِبُهُۚ وَاِنْ يَكُ صَادِقاً يُصِبْكُمْ بَعْضُ الَّذ۪ي يَعِدُكُمْۜ اِنَّ اللّٰهَ لَا يَهْد۪ي مَنْ هُوَ مُسْرِفٌ كَذَّابٌ", translation: "Firavun taraftarlarından imanını gizleyen bir kimse, şöyle dedi: \"Rabb'im Allah'tır dediği için mi bir adamı öldüreceksiniz? Oysaki o, size Rabb'inizden açık kanıtlarla geldi. Eğer yalancı biriyse yalanı kendi aleyhinedir. Eğer doğru söylüyorsa uyardığı şeylerin bir kısmı size isabet edecektir. Allah, aşırı giden yalancı bir kimseyi doğru yola iletmez.\"", transliteration: "Ve kale raculun mu'minun min ali fir'avne yektumu imanehu e taktulune raculen en yekule rabbiyallahu ve kad caekum bil beyyinati min rabbikum, ve in yeku kaziben fe aleyhi kezibuh, ve in yeku sadikan yusibkum ba'dullezi yeidukum, innallahe la yehdi men huve musrifun kezzab."},
  {id: 4162, sure_no: 29, ayet_no: 29, sure_adi: "Mümin", text: "يَا قَوْمِ لَكُمُ الْمُلْكُ الْيَوْمَ ظَاهِر۪ينَ فِي الْاَرْضِۘ فَمَنْ يَنْصُرُنَا مِنْ بَأْسِ اللّٰهِ اِنْ جَٓاءَنَاۜ قَالَ فِرْعَوْنُ مَٓا اُر۪يكُمْ اِلَّا مَٓا اَرٰى وَمَٓا اَهْد۪يكُمْ اِلَّا سَب۪يلَ الرَّشَادِ", translation: "\"Ey halkım! Bugün yeryüzünde gücü elinizde bulunduranlar olarak mülk sizindir. Şayet gelecek olursa Allah'ın azabından bizi kim kurtarabilir?\" Firavun: \"Ben size, gördüğüm şeyden başkasını göstermiyorum. Ve ben, sizi reşadın yolundan başkasına iletemem.\" dedi.", transliteration: "Ya kavmi lekumul mulkul yevme zahirine fil ardı fe men yensuruna min be'sillahi in caena, kale fir'avnu ma urikum illa ma era ve ma ehdikum illa sebiler reşad."},
  {id: 4163, sure_no: 30, ayet_no: 30, sure_adi: "Mümin", text: "وَقَالَ الَّـذ۪ٓي اٰمَنَ يَا قَوْمِ اِنّ۪ٓي اَخَافُ عَلَيْكُمْ مِثْلَ يَوْمِ الْاَحْزَابِۙ", translation: "İman etmiş olan kimse: \"Ey halkım! Ben daha önce birçok toplumun başına gelenin sizin başınıza da gelmesinden korkuyorum.\"", transliteration: "Ve kalellezi amene ya kavmi inni ehafu aleykum misle yevmil ahzab."},
  {id: 4164, sure_no: 31, ayet_no: 31, sure_adi: "Mümin", text: "مِثْلَ دَأْبِ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَالَّذ۪ينَ مِنْ بَعْدِهِمْۜ وَمَا اللّٰهُ يُر۪يدُ ظُلْماً لِلْعِبَادِ", translation: "\"Nuh, Ad, Semud ve onlardan sonraki toplumların durumu gibi. Allah, kullarına haksızlık edici değildir.\"", transliteration: "Misle de'bi kavmi nuhın ve adin ve semude vellezine min ba'dihim, ve mallahu yuridu zulmen lil ibad."},
  {id: 4165, sure_no: 32, ayet_no: 32, sure_adi: "Mümin", text: "وَيَا قَوْمِ اِنّ۪ٓي اَخَافُ عَلَيْكُمْ يَوْمَ التَّنَادِۙ", translation: "\"Ey halkım! Ben sizin için o feryat gününden korkuyorum.\"", transliteration: "Ve ya kavmi inni ehafu aleykum yevmet tenad."},
  {id: 4166, sure_no: 33, ayet_no: 33, sure_adi: "Mümin", text: "يَوْمَ تُوَلُّونَ مُدْبِر۪ينَۚ مَا لَكُمْ مِنَ اللّٰهِ مِنْ عَاصِمٍۚ وَمَنْ يُضْلِلِ اللّٰهُ فَمَا لَهُ مِنْ هَادٍ", translation: "Arkanıza bakmadan kaçacağınız gün, sizi Allah'ın azabından koruyabilecek kimse yoktur. Allah, kimi sapkınlıkta bırakırsa ona doğru yolu gösterecek yoktur.", transliteration: "Yevme tuvellune mudbirin, ma lekum minallahi min asım ve men yudlilillahu fe ma lehu min had."},
  {id: 4167, sure_no: 34, ayet_no: 34, sure_adi: "Mümin", text: "وَلَقَدْ جَٓاءَكُمْ يُوسُفُ مِنْ قَبْلُ بِالْبَيِّنَاتِ فَمَا زِلْتُمْ ف۪ي شَكٍّ مِمَّا جَٓاءَكُمْ بِه۪ۜ حَتّٰٓى اِذَا هَلَكَ قُلْتُمْ لَنْ يَبْعَثَ اللّٰهُ مِنْ بَعْدِه۪ رَسُولاًۜ كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ هُوَ مُسْرِفٌ مُرْتَابٌۚ", translation: "Ant olsun ki daha önce Yusuf size açık kanıt içeren belgelerle gelmişti. Ancak o zaman onun size getirdiği şeyler hakkında kuşkulanıp durmuştunuz. Yusuf ölünce de: \"Bundan sonra Allah asla Resul göndermez.\" dediniz. Allah, haddi aşan, güvenmeyen kimseyi işte böyle saptırır.", transliteration: "Ve lekad caekum yusufu min kablu bil beyyinati fe ma ziltum fi şekkin mimma caekum bih, hatta iza heleke kultum len yeb'asallahu min ba'dihi resula, kezalike yudıllullahu men huve musrifun murtab."},
  {id: 4168, sure_no: 35, ayet_no: 35, sure_adi: "Mümin", text: "اَلَّذ۪ينَ يُجَادِلُونَ ف۪ٓي اٰيَاتِ اللّٰهِ بِغَيْرِ سُلْطَانٍ اَتٰيهُمْۜ كَبُرَ مَقْتاً عِنْدَ اللّٰهِ وَعِنْدَ الَّذ۪ينَ اٰمَنُواۜ  كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ", translation: "O kimseler, kendilerine görevli kılınmış, yetki verilmiş bir kimse gelmediği halde, Allah'ın ayetleri hakkında tartışırlar. Bu da Allah'ın yanında da insanların yanında da büyük bir kızgınlığa neden olur. İşte böyle! Allah, her büyüklük taslayanın kalbini mühürler.", transliteration: "Ellezine yucadilune fi ayatillahi bi gayri sultanin etahum, kebure makten indallahi ve indellezine amenu, kezalike yatbaullahu ala kulli kalbi mutekebbirin cebbar."},
  {id: 4169, sure_no: 36, ayet_no: 36, sure_adi: "Mümin", text: "وَقَالَ فِرْعَوْنُ يَا هَامَانُ ابْنِ ل۪ي صَرْحاً لَعَلّ۪ٓي اَبْلُغُ الْاَسْبَابَۙ", translation: "Firavun: \"Ey Haman! Benim için yüksek bir kule yap. Belki böylece o sebeplere ulaşırım;\"", transliteration: "Ve kale fir'avnu ya hamanubni li sarhan lealli eblugul esbab."},
  {id: 4170, sure_no: 37, ayet_no: 37, sure_adi: "Mümin", text: "اَسْبَابَ السَّمٰوَاتِ فَاَطَّلِعَ اِلٰٓى اِلٰهِ مُوسٰى وَاِنّ۪ي لَاَظُنُّهُ كَاذِباًۜ وَكَذٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُٓوءُ عَمَلِه۪ وَصُدَّ عَنِ السَّب۪يلِۜ وَمَا كَيْدُ فِرْعَوْنَ اِلَّا ف۪ي تَبَابٍ۟", translation: "\"Göklerin sebeplerine. Böylece Musa'nın ilahını görürüm. Çünkü ben onun yalancı olduğunu sanıyorum.\" dedi. Ve işte böylece Firavun'a, yaptığı kötü iş iyi gösterildi ve doğru yoldan çıkarıldı. Firavunun planı, hüsrandan başka bir şeye yaramadı.", transliteration: "Esbabes semavati fe attalia ila ilahi musa ve inni le ezunnuhu kaziba, ve kezalike zuyyine li fir'avne suu amelihi ve sudde anis sebil, ve ma keydu fir'avne illa fi tebab."},
  {id: 4171, sure_no: 38, ayet_no: 38, sure_adi: "Mümin", text: "وَقَالَ الَّـذ۪ٓي اٰمَنَ يَا قَوْمِ اتَّبِعُونِ اَهْدِكُمْ سَب۪يلَ الرَّشَادِۚ", translation: "İman etmiş olan kimse: \"Ey halkım! Bana uyun ki sizi reşadın yoluna ileteyim.\"", transliteration: "Ve kalellezi amene ya kavmittebiuni ehdikum sebiler reşad."},
  {id: 4172, sure_no: 39, ayet_no: 39, sure_adi: "Mümin", text: "يَا قَوْمِ اِنَّمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَا مَتَاعٌۘ وَاِنَّ الْاٰخِرَةَ هِيَ دَارُ الْقَرَارِ", translation: "\"Ey halkım! Bu dünya hayatı geçici bir yararlanmadır. Ahiret hayatı ise kesinlikle devamlı kalınacak yerdir.\"", transliteration: "Ya kavmi innema hazihil hayatud dunya metaun ve innel ahirete hiye darul karar."},
  {id: 4173, sure_no: 40, ayet_no: 40, sure_adi: "Mümin", text: "مَنْ عَمِلَ سَيِّئَةً فَلَا يُجْزٰٓى اِلَّا مِثْلَهَاۚ وَمَنْ عَمِلَ صَالِحاً مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُو۬لٰٓئِكَ يَدْخُلُونَ الْجَنَّةَ يُرْزَقُونَ ف۪يهَا بِغَيْرِ حِسَابٍ", translation: "\"Kim bir kötülük yaparsa, ona yaptığının karşılığından fazlasıyla karşılık verilmez. Erkek veya kadın, her kim Mü'min olarak salih olanı yaparsa, işte onlar hesapsız şekilde rızıklanmak üzere Cennet'e girerler.\"", transliteration: "Men amile seyyieten fe la yucza illa misleha, ve men amile salihan min zekerin ev unsa ve huve mu'minun fe ulaike yedhulunel cennete yurzekune fiha bi gayri hisab."},
  {id: 4174, sure_no: 41, ayet_no: 41, sure_adi: "Mümin", text: "وَيَا قَوْمِ مَا ل۪ٓي اَدْعُوكُمْ اِلَى النَّجٰوةِ وَتَدْعُونَن۪ٓي اِلَى النَّارِۜ", translation: "\"Ey halkım! Ne gariptir ki siz beni ateşe, ben ise sizi kurtuluşa çağırıyorum!\"", transliteration: "Ve ya kavmi ma li ed'ukum ilen necati ve ted'uneni ilen nar."},
  {id: 4175, sure_no: 42, ayet_no: 42, sure_adi: "Mümin", text: "تَدْعُونَن۪ي لِاَكْفُرَ بِاللّٰهِ وَاُشْرِكَ بِه۪ مَا لَيْسَ ل۪ي بِه۪ عِلْمٌۘ وَاَنَا۬ اَدْعُوكُمْ اِلَى الْعَز۪يزِ الْغَفَّارِ", translation: "\"Siz beni Allah'ı yalanlamaya ve hakkında bilgim olmayan şeyleri O'na ortak koşmaya çağırıyorsunuz. Ben ise sizi Mutlak Üstün ve Çok Bağışlayıcı Olan'a çağırıyorum.\"", transliteration: "Ted'uneni li ekfure billahi ve uşrike bihi ma leyse li bihi ilmun ve ene ed'ukum ilel azizil gaffar."},
  {id: 4176, sure_no: 43, ayet_no: 43, sure_adi: "Mümin", text: "لَا جَرَمَ اَنَّمَا تَدْعُونَن۪ٓي اِلَيْهِ لَيْسَ لَهُ دَعْوَةٌ فِي الدُّنْيَا وَلَا فِي الْاٰخِرَةِ وَاَنَّ مَرَدَّنَٓا اِلَى اللّٰهِ وَاَنَّ الْمُسْرِف۪ينَ هُمْ اَصْحَابُ النَّارِ", translation: "\"Şu bir gerçek ki, sizin beni kendisine çağırdığınız şey, dünyada da ahirette de kendisine çağıranlara cevap verme gücü olmayan şeydir. Kuşkusuz dönüşümüz Allah'adır. Haddi aşanlar Cehennem'liktirler.\"", transliteration: "La cereme ennema ted'uneni ileyhi leyse lehu da'vetun fid dunya ve la fil ahireti ve enne mereddena ilallahi ve ennel musrifine hum ashabun nar."},
  {id: 4177, sure_no: 44, ayet_no: 44, sure_adi: "Mümin", text: "فَسَتَذْكُرُونَ مَٓا اَقُولُ لَكُمْۜ وَاُفَوِّضُ اَمْر۪ٓي اِلَى اللّٰهِۜ اِنَّ اللّٰهَ بَص۪يرٌ بِالْعِبَادِ", translation: "\"Sizin için söylediklerimin doğruluğunu yakında anlayacaksınız. Ben işimi Allah'a havale ediyorum. Allah, kullarının yaptığı her şeyi görmektedir.\"", transliteration: "Fe se tezkurune ma ekulu lekum, ve ufevvidu emri ilallah, innallahe basirun bil ibad."},
  {id: 4178, sure_no: 45, ayet_no: 45, sure_adi: "Mümin", text: "فَوَقٰيهُ اللّٰهُ سَيِّـَٔاتِ مَا مَكَرُوا وَحَاقَ بِاٰلِ فِرْعَوْنَ سُٓوءُ الْعَذَابِۚ", translation: "Sonra Allah onu, onların yaptıkları planların kötülüklerinden korudu. Firavuncuları ise azabın kötüsü kuşattı.", transliteration: "Fe vekahullahu seyyiati ma mekeru ve haka bi ali fir'avne suul azab."},
  {id: 4179, sure_no: 46, ayet_no: 46, sure_adi: "Mümin", text: "اَلنَّارُ يُعْرَضُونَ عَلَيْهَا غُدُواًّ وَعَشِياًّۚ وَيَوْمَ تَقُومُ السَّاعَةُ۠ اَدْخِلُٓوا اٰلَ فِرْعَوْنَ اَشَدَّ الْعَذَابِ", translation: "Ateş! Sabah akşam ona arz olunurlar. O Sa'at'in gerçekleşeceği gün: \"Firavuncuları azabın şiddetlisine sokun!\"", transliteration: "En naru yu'radune aleyha guduvven ve aşiyya ve yevme tekumus saah, edhılu ale firavne eşeddel azab."},
  {id: 4180, sure_no: 47, ayet_no: 47, sure_adi: "Mümin", text: "وَاِذْ يَتَحَٓاجُّونَ فِي النَّارِ فَيَقُولُ الضُّعَفٰٓؤُ۬ا لِلَّذ۪ينَ اسْتَكْـبَرُٓوا اِنَّا كُنَّا لَكُمْ تَبَعاً فَهَلْ اَنْتُمْ مُغْنُونَ عَنَّا نَص۪يباً مِنَ النَّارِ", translation: "Ateşin içinde birbirlerini suçlarlar; güçsüz olanlar, büyüklük taslayanlara: \"Biz size uyduk, şimdi siz ateşin bir kısmını bizden savabilir misiniz?\" derler.", transliteration: "Ve iz yetehaccune fin nari fe yekulud duafau lillezinestekberu inna kunna lekum tebean fe hel entum mugnune anna nasiben minen nar."},
  {id: 4181, sure_no: 48, ayet_no: 48, sure_adi: "Mümin", text: "قَالَ الَّذ۪ينَ اسْتَكْبَرُٓوا اِنَّا كُلٌّ ف۪يهَٓا اِنَّ اللّٰهَ قَدْ حَكَمَ بَيْنَ الْعِبَادِ", translation: "Büyüklük taslayanlar: \"Hepimiz onun içindeyiz. Artık Allah, kulları arasında hükmünü verdi.\" derler.", transliteration: "Kalellezi nestekberu inna kullun fiha innallahe kad hakeme beynel ibad."},
  {id: 4182, sure_no: 49, ayet_no: 49, sure_adi: "Mümin", text: "وَقَالَ الَّذ۪ينَ فِي النَّارِ لِخَزَنَةِ جَهَنَّمَ ادْعُوا رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْماً مِنَ الْعَذَابِ", translation: "Ateşte olanlar, Cehennem görevlilerine: \"Rabb'inize dua edin de bir gün de olsa azabı bizden hafifletsin.\" derler.", transliteration: "Ve kalellezine fin nari li hazeneti cehennemed'u rabbekum yuhaffif anna yevmen minel azab."},
  {id: 4183, sure_no: 50, ayet_no: 50, sure_adi: "Mümin", text: "قَالُٓوا اَوَلَمْ تَكُ تَأْت۪يكُمْ رُسُلُكُمْ بِالْبَيِّنَاتِۜ قَالُوا بَلٰىۜ قَالُوا فَادْعُواۚ وَمَا دُعٰٓـؤُا الْكَافِر۪ينَ اِلَّا ف۪ي ضَلَالٍ۟", translation: "Görevliler: \"Resulleriniz, size kanıt içeren bilgilerle gelmediler mi?\" derler. Onlar: \"Evet, geldiler.\" derler. Görevliler: \"O halde kendiniz yalvarıp yakarın; Kafirlerin duası ancak boş ve anlamsızdır.\" derler.", transliteration: "Kalu e ve lem teku te'tikum rusulukum bil beyyinat, kalu bela, kalu fed'u, ve ma duaul kafirine illa fi dalal."},
  {id: 4184, sure_no: 51, ayet_no: 51, sure_adi: "Mümin", text: "اِنَّا لَنَنْصُرُ رُسُلَنَا وَالَّذ۪ينَ اٰمَنُوا فِي الْحَيٰوةِ الدُّنْيَا وَيَوْمَ يَقُومُ الْاَشْهَادُۙ", translation: "Biz, Resullerimize ve iman edenlere dünya hayatında ve tanıkların tanıklık edecekleri günde kesinlikle yardım ederiz.", transliteration: "İnna le nensuru rusulena vellezine amenu fil hayatid dunya ve yevme yekumul eşhad."},
  {id: 4185, sure_no: 52, ayet_no: 52, sure_adi: "Mümin", text: "يَوْمَ لَا يَنْفَعُ الظَّالِم۪ينَ مَعْذِرَتُهُمْ وَلَهُمُ اللَّعْنَةُ وَلَهُمْ سُٓوءُ الدَّارِ", translation: "O Gün zalimlere, mazeretleri yarar sağlamaz. Onlara lanet okunacak ve yurtların en kötüsü onlar içindir.", transliteration: "Yevme la yenfeuz zalimine ma'ziretuhum ve lehumulla'netu ve lehum suud dar."},
  {id: 4186, sure_no: 53, ayet_no: 53, sure_adi: "Mümin", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْهُدٰى وَاَوْرَثْنَا بَن۪ٓي اِسْرَٓائ۪ـلَ الْكِتَابَۙ", translation: "Ant olsun ki Musa'ya hidayet verdik. İsrailoğulları'na Kitap'ı miras bıraktık;", transliteration: "Ve lekad ateyna musel huda ve evresna beni israilel kitab."},
  {id: 4187, sure_no: 54, ayet_no: 54, sure_adi: "Mümin", text: "هُدًى وَذِكْرٰى لِاُو۬لِي الْاَلْبَابِ", translation: "Temiz akıl sahipleri için doğru yolu gösterici ve öğüt olarak.", transliteration: "Huden ve zikra li ulil elbab."},
  {id: 4188, sure_no: 55, ayet_no: 55, sure_adi: "Mümin", text: "فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَاسْتَغْفِرْ لِذَنْبِكَ وَسَبِّـحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْاِبْكَارِ", translation: "O halde sabret. Kuşkusuz Allah'ın verdiği söz gerçektir. Suçların için bağışlanma dile. Akşam sabah Rabb'ini hamd ile tesbih et.", transliteration: "Fasbir inne va'dallahi hakkun vestagfir li zenbike ve sebbih bi hamdi rabbike bil aşiyyi vel ibkar."},
  {id: 4189, sure_no: 56, ayet_no: 56, sure_adi: "Mümin", text: "اِنَّ الَّذ۪ينَ يُجَادِلُونَ ف۪ٓي اٰيَاتِ اللّٰهِ بِغَيْرِ سُلْطَانٍ اَتٰيهُمْۙ اِنْ ف۪ي صُدُورِهِمْ اِلَّا كِبْرٌ مَا هُمْ بِبَالِغ۪يهِۚ فَاسْتَعِذْ بِاللّٰهِۜ اِنَّهُ هُوَ السَّم۪يعُ الْبَص۪يرُ", translation: "Yetkili kılınmadıkları halde Allah'ın ayetleri hakkında ileri geri konuşanların kalplerinde, hiçbir zaman tatmin edemeyecekleri bir büyüklenme tutkusu vardır. Sen, Allah'a sığın. O, Her Şeyi Duyan, Her Şeyi Gören'dir.", transliteration: "İnnellezine yucadilune fi ayatillahi bi gayri sultanin etahum in fi sudurihim illa kibrun ma hum bi baligih, festeiz billah, innehu huves semiul basir."},
  {id: 4190, sure_no: 57, ayet_no: 57, sure_adi: "Mümin", text: "لَخَلْقُ السَّمٰوَاتِ وَالْاَرْضِ اَكْبَرُ مِنْ خَلْقِ النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ", translation: "Elbette ki göklerin ve yerin yaratılması, insanların yaratılmasından daha büyük bir şeydir. Ama insanların çoğu bu gerçeği kavramıyorlar.", transliteration: "Le halkus semavati vel ardı ekberu min halkın nasi ve lakinne ekseren nasi la ya'lemun."},
  {id: 4191, sure_no: 58, ayet_no: 58, sure_adi: "Mümin", text: "وَمَا يَسْتَوِي الْاَعْمٰى وَالْبَص۪يرُ وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَلَا الْمُس۪ٓيءُۜ قَل۪يلاً مَا تَـتَذَكَّرُونَ", translation: "Kör ile gören bir değildir. İman edip salihatı yapanlar ile kötülük yapanlar da bir değildir. Ne kadar az düşünüyorsunuz?", transliteration: "Ve ma yestevil a'ma vel basiru vellezine amenu ve amilus salihati ve lel musiu, kalilen ma tetezekkerun."},
  {id: 4192, sure_no: 59, ayet_no: 59, sure_adi: "Mümin", text: "اِنَّ السَّاعَةَ لَاٰتِيَةٌ لَا رَيْبَ ف۪يهَا وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ", translation: "Kuşkusuz ki o Sa'at mutlaka gelecektir. Onun geleceği kesinlikle doğrudur. Fakat insanların çoğu buna inanmazlar.", transliteration: "İnnes saate le atiyetun la reybe fiha ve lakinne ekseren nasi la yu'minun."},
  {id: 4193, sure_no: 60, ayet_no: 60, sure_adi: "Mümin", text: "وَقَالَ رَبُّكُمُ ادْعُون۪ٓي اَسْتَجِبْ لَكُمْۜ اِنَّ الَّذ۪ينَ يَسْتَكْبِرُونَ عَنْ عِبَادَت۪ي سَيَدْخُلُونَ جَهَنَّمَ دَاخِر۪ينَ۟", translation: "Rabb'iniz: \"Bana dua edin ki size karşılık vereyim. Bana kulluk etmeye büyüklenenler, horlanmış olarak Cehennem'e gireceklerdir.\" dedi.", transliteration: "Ve kale rabbukumud'uni estecib lekum, innellezine yestekbirune an ibadeti se yedhulune cehenneme dahırin."},
  {id: 4194, sure_no: 61, ayet_no: 61, sure_adi: "Mümin", text: "اَللّٰهُ الَّذ۪ي جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوا ف۪يهِ وَالنَّهَارَ مُبْصِراًۜ اِنَّ اللّٰهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُونَ", translation: "Allah, dinginlik bulmanız için geceyi karanlık, gündüzü de aydınlık yapandır. Allah, insanlara karşı lütuf sahibidir. Ancak insanların çoğu şükretmezler.", transliteration: "Allahullezi ceale lekumul leyle li teskunu fihi ven nehare mubsıra, innallahe le zu fadlin alen nasi ve lakinne ekseren nasi la yeşkurun."},
  {id: 4195, sure_no: 62, ayet_no: 62, sure_adi: "Mümin", text: "ذٰلِكُمُ اللّٰهُ رَبُّكُمْ خَالِقُ كُلِّ شَيْءٍۢ  لَٓا اِلٰهَ اِلَّا هُوَۘ فَاَنّٰى تُؤْفَكُونَ", translation: "İşte Rabbiniz olan Allah budur. O, her şeyin yaratıcısıdır. O'ndan başka ilah yoktur. O halde nasıl oluyor da başka şeylere yöneliyorsunuz?", transliteration: "Zalikumullahu rabbukum haliku kulli şey'in la ilahe illa huve fe enna tu'fekun."},
  {id: 4196, sure_no: 63, ayet_no: 63, sure_adi: "Mümin", text: "كَذٰلِكَ يُؤْفَكُ الَّذ۪ينَ كَانُوا بِاٰيَاتِ اللّٰهِ يَجْحَدُونَ", translation: "Allah'ın ayetlerini bile bile reddedenler, işte böyle haktan uzaklaşmışlardı.", transliteration: "Kezalike yu'fekullezine kanu bi ayatillahi yechadun."},
  {id: 4197, sure_no: 64, ayet_no: 64, sure_adi: "Mümin", text: "اَللّٰهُ الَّذ۪ي جَعَلَ لَكُمُ الْاَرْضَ قَرَاراً وَالسَّمَٓاءَ بِنَٓاءً وَصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْ وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِۜ ذٰلِكُمُ اللّٰهُ رَبُّكُمْۚ فَـتَبَارَكَ اللّٰهُ رَبُّ الْعَالَم۪ينَ", translation: "Sizin için yeryüzünü yerleşim alanı, gökyüzünü de üzerinize bir tavan gibi yapan Allah'tır. O, size en iyi şekille şekil veren ve sizi temiz şeylerden rızıklandırandır. İşte Rabb'iniz olan Allah odur. Alemlerin Rabb'i olan Allah, ne Mübarek'tir.", transliteration: "Allahullezi ceale lekumul arda kararen ves semae binaen ve savverekum fe ahsene suverekum ve razakakum minet tayyibat, zalikumullahu rabbukum, fe tebarekallahu rabbul alemin."},
  {id: 4198, sure_no: 65, ayet_no: 65, sure_adi: "Mümin", text: "هُوَ الْحَيُّ لَٓا اِلٰهَ اِلَّا هُوَ فَادْعُوهُ مُخْلِص۪ينَ لَهُ الدّ۪ينَۜ اَلْحَمْدُ لِلّٰهِ رَبِّ الْعَالَم۪ينَ", translation: "O, diridir. Ondan başka ilah yoktur. O halde dini yalnızca O'na has kılarak O'na dua edin. Hamd yalnızca alemlerin Rabb'i olan Allah'a özgüdür.", transliteration: "Huvel hayyu la ilahe illa huve fed'uhu muhlisine lehud din, el hamdu lillahi rabbil alemin."},
  {id: 4199, sure_no: 66, ayet_no: 66, sure_adi: "Mümin", text: "قُلْ اِنّ۪ي نُه۪يتُ اَنْ اَعْبُدَ الَّذ۪ينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ لَمَّا جَٓاءَنِيَ الْبَيِّنَاتُ مِنْ رَبّ۪ي وَاُمِرْتُ اَنْ اُسْلِمَ لِرَبِّ الْعَالَم۪ينَ", translation: "De ki: \"Bana, Rabb'imden gelen açık kanıtlar içeren bilgilerle; sizin, Allah'tan başka yöneldiklerinize kulluk etmekten kesinlikle men edildim ve ben alemlerin Rabb'ine teslim olmakla emrolundum.\"", transliteration: "Kul inni nuhitu en a'budellezine ted'une min dunillahi lemma caeniyel beyyinatu min rabbi ve umirtu en uslime li rabbil alemin."},
  {id: 4200, sure_no: 67, ayet_no: 67, sure_adi: "Mümin", text: "هُوَ الَّذ۪ي خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلاً ثُمَّ لِتَبْلُغُٓوا اَشُدَّكُمْ ثُمَّ لِتَكُونُوا شُيُوخاًۚ وَمِنْكُمْ مَنْ يُتَوَفّٰى مِنْ قَبْلُ وَلِتَبْلُغُٓوا اَجَلاً مُسَمًّى وَلَعَلَّكُمْ تَعْقِلُونَ", translation: "O, sizi topraktan yaratandır. Sonra bir nutfeden, sonra bir alakadan. Sonra sizi bir bebek olarak dünyaya getiriyor. Nihayet olgunluk çağına ulaşır, derken yaşlanırsınız. Kiminiz bu süreci tamamlamadan vefat ettirilir. Kiminiz de belirlenen süreye kadar yaşarsınız. Umulur ki aklınızı kullanırsınız.", transliteration: "Huvellezi halakakum min turabin summe min nutfetin summe min alakatin summe yuhricukum tıflen summe li teblugu eşuddekum summe li tekunu şuyuha, ve minkum men yuteveffa min kablu ve li teblugu ecelen musemmen ve leallekum ta'kılun."},
  {id: 4201, sure_no: 68, ayet_no: 68, sure_adi: "Mümin", text: "هُوَ الَّذ۪ي يُحْـي۪ وَيُم۪يتُۚ فَاِذَا قَضٰٓى اَمْراً فَاِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ۟", translation: "Hayat veren de öldüren de O'dur. O, bir işin olmasını istediği zaman ona sadece \"Ol.\" der, o da olur.", transliteration: "Huvellezi yuhyi ve yumit, fe iza kada emren fe innema yekulu lehu kun fe yekun."},
  {id: 4202, sure_no: 69, ayet_no: 69, sure_adi: "Mümin", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ يُجَادِلُونَ ف۪ٓي اٰيَاتِ اللّٰهِۜ اَنّٰى يُصْرَفُونَۚۛ", translation: "Allah'ın ayetleri hakkında tartışanları görmedin mi? Nasıl da saptırılıyorlar!", transliteration: "E lem tere ilellezine yucadilune fi ayatillah, enna yusrafun."},
  {id: 4203, sure_no: 70, ayet_no: 70, sure_adi: "Mümin", text: "اَلَّذ۪ينَ كَذَّبُوا بِالْكِتَابِ وَبِمَٓا اَرْسَلْنَا بِه۪ رُسُلَنَا۠ۛ فَسَوْفَ يَعْلَمُونَۙ", translation: "Bu Kitap'ı ve Resullerimizle gönderdiklerimizi yalanlayanlar elbette yakında gerçeği anlayacaklar.", transliteration: "Ellezine kezzebu bil kitabi ve bima erselna bihi rusulena, fe sevfe ya'lemun."},
  {id: 4204, sure_no: 71, ayet_no: 71, sure_adi: "Mümin", text: "اِذِ الْاَغْلَالُ ف۪ٓي اَعْنَاقِهِمْ وَالسَّلَاسِلُۜ يُسْحَبُونَۙ", translation: "O vakit boyunlarında halkalar ve zincirler olduğu halde sürüklenecekler.", transliteration: "İzil aglalu fi a'nakıhim ves selasil, yushabun."},
  {id: 4205, sure_no: 72, ayet_no: 72, sure_adi: "Mümin", text: "فِي الْحَم۪يمِ ثُمَّ فِي النَّارِ يُسْجَرُونَۚ", translation: "Kaynar suda sonra ateşte yakılacaklar.", transliteration: "Fil hamimi summe fin nari yuscerun."},
  {id: 4206, sure_no: 73, ayet_no: 73, sure_adi: "Mümin", text: "ثُمَّ ق۪يلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تُشْرِكُونَۙ", translation: "Sonra onlara: \"Şirk koştuklarınız nerede?\" denir;", transliteration: "Summe kile lehum eyne ma kuntum tuşrikun."},
  {id: 4207, sure_no: 74, ayet_no: 74, sure_adi: "Mümin", text: "مِنْ دُونِ اللّٰهِۜ قَالُوا ضَلُّوا عَنَّا بَلْ لَمْ نَكُنْ نَدْعُوا مِنْ قَبْلُ شَيْـٔاًۜ كَذٰلِكَ يُضِلُّ اللّٰهُ الْكَافِر۪ينَ", translation: "Allah'tan başka. \"Onlar, bizi ortada bırakıp kayboldular. Demek ki daha önce yakardıklarımız bir hiçmiş.\" derler. İşte Allah, Kafirleri böyle sapkınlıkta bırakır.", transliteration: "Min dunillah, kalu dallu anna bel lem nekun ned'u min kablu şey'a, kezalike yudıllullahul kafirin."},
  {id: 4208, sure_no: 75, ayet_no: 75, sure_adi: "Mümin", text: "ذٰلِكُمْ بِمَا كُنْتُمْ تَفْرَحُونَ فِي الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَمْرَحُونَۚ", translation: "İşte bu, yeryüzünde hak hukuk tanımaksızın şımarıp azmanız nedeniyledir.", transliteration: "Zalikum bima kuntum tefrehune fil ardı bi gayril hakkı ve bima kuntum temrehun."},
  {id: 4209, sure_no: 76, ayet_no: 76, sure_adi: "Mümin", text: "اُدْخُلُٓوا اَبْوَابَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَاۚ فَبِئْسَ مَثْوَى الْمُتَكَبِّر۪ينَ", translation: "Orada sürekli kalmak üzere Cehennem'in kapılarından girin. İşte, büyüklenenlerin yeri ne kötüdür!", transliteration: "Udhulu ebvabe cehenneme halidine fiha, fe bi'se mesvel mutekebbirin."},
  {id: 4210, sure_no: 77, ayet_no: 77, sure_adi: "Mümin", text: "فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّۚ فَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذ۪ي نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا يُرْجَعُونَ", translation: "O halde sabret. Kuşkusuz Allah'ın yaptığı uyarı gerçektir. Onlara yaptığımız uyarılardan bir kısmını sana göstersek de göstermeden seni vefat ettirsek de sonunda onlar yalnızca Bize döndürüleceklerdir.", transliteration: "Fasbir inne va'dallahi hakk, fe imma nuriyenneke ba'dallezi neıduhum ev neteveffeyenneke fe ileyna yurceun."},
  {id: 4211, sure_no: 78, ayet_no: 78, sure_adi: "Mümin", text: "وَلَقَدْ اَرْسَلْنَا رُسُلاً مِنْ قَبْلِكَ مِنْهُمْ مَنْ قَصَصْنَا عَلَيْكَ وَمِنْهُمْ مَنْ لَمْ نَقْصُصْ عَلَيْكَۜ وَمَا كَانَ لِرَسُولٍ اَنْ يَأْتِيَ بِاٰيَةٍ اِلَّا بِـاِذْنِ اللّٰهِۚ فَاِذَا جَٓاءَ اَمْرُ اللّٰهِ قُضِيَ بِالْحَقِّ وَخَسِرَ هُنَالِكَ الْمُبْطِلُونَ۟", translation: "Ant olsun ki, senden önce de birçok Resul gönderdik. Onlardan bir kısmını sana anlattık, bir kısmını ise anlatmadık. Hiçbir Resul, Allah'ın izni olmaksızın bir ayet getiremez. Allah'ın buyruğu geldiği zaman hak yerini bulur. Batıl'dan yana olanlar hüsrana uğrarlar.", transliteration: "Ve lekad erselna rusulen min kablike minhum men kasasna aleyke ve minhum men lem naksus aleyk, ve ma kane li resulin en ye'tiye bi ayetin illa bi iznillah, fe iza cae emrullahi kudıye bil hakkı ve hasire hunalikel mubtılun."},
  {id: 4212, sure_no: 79, ayet_no: 79, sure_adi: "Mümin", text: "اَللّٰهُ الَّذ۪ي جَعَلَ لَكُمُ الْاَنْعَامَ لِتَرْكَبُوا مِنْهَا وَمِنْهَا تَأْكُلُونَۘ", translation: "Binmeniz ve yemeniz için hayvanları yararlanmanıza sunan Allah'tır.", transliteration: "Allahullezi ceale lekumul en'ame li terkebu minha ve minha te'kulun."},
  {id: 4213, sure_no: 80, ayet_no: 80, sure_adi: "Mümin", text: "وَلَكُمْ ف۪يهَا مَنَافِـعُ وَلِتَبْلُغُوا عَلَيْهَا حَاجَةً ف۪ي صُدُورِكُمْ وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَۜ", translation: "Ve sizin için onlarda daha nice yararlar var. Onlarla arzu ettiğiniz yere ulaşırsınız. Onların ve gemilerin üzerinde taşınırsınız.", transliteration: "Ve lekum fiha menafiu ve li teblugu aleyha haceten fi sudurikum ve aleyha ve alel fulki tuhmelun."},
  {id: 4214, sure_no: 81, ayet_no: 81, sure_adi: "Mümin", text: "وَيُر۪يكُمْ اٰيَاتِه۪ۗ فَاَيَّ اٰيَاتِ اللّٰهِ تُنْكِرُونَ", translation: "Allah size ayetlerini gösteriyor. O halde Allah'ın ayetlerinden hangisini inkar ediyorsunuz?", transliteration: "Ve yurikum ayatihi fe eyye ayatillahi tunkirun."},
  {id: 4215, sure_no: 82, ayet_no: 82, sure_adi: "Mümin", text: "اَفَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ كَانُٓوا اَكْثَرَ مِنْهُمْ وَاَشَدَّ قُوَّةً وَاٰثَاراً فِي الْاَرْضِ فَمَٓا اَغْنٰى عَنْهُمْ مَا كَانُوا يَكْسِبُونَ", translation: "Hem onlar, yeryüzünde gezip dolaşmazlar mı? Baksalar ya! Kendilerinden öncekilerin sonlarının nasıl olduğuna! Onlar, güç ve yeryüzündeki eserleri bakımından daha üstündüler. Öyle olduğu halde sahip oldukları şeyler kendilerine yarar sağlamadı.", transliteration: "E fe lem yesiru fil ardı fe yenzuru keyfe kane akıbetullezine min kablihim, kanu eksere minhum ve eşedde kuvveten ve asaren fil ardı fema agna anhum ma kanu yeksibun."},
  {id: 4216, sure_no: 83, ayet_no: 83, sure_adi: "Mümin", text: "فَلَمَّا جَٓاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَرِحُوا بِمَا عِنْدَهُمْ مِنَ الْعِلْمِ وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Resulleri kendilerine kanıt içeren açıklayıcı bilgilerle geldiği zaman, sahip oldukları bilgiye güvenerek şımardılar ve kendisi ile alay ettikleri şey onları kuşattı.", transliteration: "Fe lemma caethum rusuluhum bil beyyinati ferihu bima indehum minel ilmi ve haka bihim ma kanu bihi yestehziun."},
  {id: 4217, sure_no: 84, ayet_no: 84, sure_adi: "Mümin", text: "فَلَمَّا رَاَوْا بَأْسَنَا قَالُٓوا اٰمَنَّا بِاللّٰهِ وَحْدَهُ وَكَفَرْنَا بِمَا كُنَّا بِه۪ مُشْرِك۪ينَ", translation: "Bizim cezamızı gördükleri zaman: \"Allah'ın tekliğine iman ettik ve O'na şirk koştuklarımızı küfrettik.\" dediler.", transliteration: "Fe lemma reev be'sena kalu amenna billahi vahdehu ve keferna bima kunna bihi muşrikin."},
  {id: 4218, sure_no: 85, ayet_no: 85, sure_adi: "Mümin", text: "فَلَمْ يَكُ يَنْفَعُهُمْ ا۪يمَانُهُمْ لَمَّا رَاَوْا بَأْسَنَاۜ سُنَّتَ اللّٰهِ الَّت۪ي قَدْ خَلَتْ ف۪ي عِبَادِه۪ۚ وَخَسِرَ هُنَالِكَ الْكَافِرُونَ", translation: "Cezamızı görünce iman etmeleri onlara bir fayda vermedi. Allah'ın kulları hakkındaki geçmişten bugüne uyguladığı yasası budur. Kafirler orada zarara uğradılar.", transliteration: "Fe lem yeku yenfeuhum imanuhum lemma reev be'sena, sunnetallahilleti kad halet fi ibadih, ve hasire hunalikel kafirun."},
  {id: 4219, sure_no: 1, ayet_no: 1, sure_adi: "Fussilet", text: "حٰمٓۜ", translation: "Ha, Mim.", transliteration: "Ha mim."},
  {id: 4220, sure_no: 2, ayet_no: 2, sure_adi: "Fussilet", text: "تَنْز۪يلٌ مِنَ الرَّحْمٰنِ الرَّح۪يمِۚ", translation: "Rahmeti Bol ve Kesintisiz olan tarafından indirilmiştir.", transliteration: "Tenzilun miner rahmanir rahim."},
  {id: 4221, sure_no: 3, ayet_no: 3, sure_adi: "Fussilet", text: "كِتَابٌ فُصِّلَتْ اٰيَاتُهُ قُرْاٰناً عَرَبِياًّ لِقَوْمٍ يَعْلَمُونَۙ", translation: "Bilen bir halk için, ayetleri detaylandırılmış, Arapça kur'an bir Kitap'tır.", transliteration: "Kitabun fussilet ayatuhu kur'anen arabiyyen li kavmin ya'lemun."},
  {id: 4222, sure_no: 4, ayet_no: 4, sure_adi: "Fussilet", text: "بَش۪يراً وَنَذ۪يراًۚ فَاَعْرَضَ اَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُونَ", translation: "Haber verici ve uyarıcı olarak. Fakat insanların çoğu ondan yüz çevirmekte ve onu dinlememektedirler.", transliteration: "Beşiren ve nezira, fe a'rada ekseruhum fehum la yesmeun."},
  {id: 4223, sure_no: 5, ayet_no: 5, sure_adi: "Fussilet", text: "وَقَالُوا قُلُوبُنَا ف۪ٓي اَكِنَّةٍ مِمَّا تَدْعُونَٓا اِلَيْهِ وَف۪ٓي اٰذَانِنَا وَقْرٌ وَمِنْ بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَاعْمَلْ اِنَّـنَا عَامِلُونَ", translation: "\"Bizi kendisine çağırdığın şeye karşı, kalplerimiz örtülü, kulaklarımızda bir ağırlık ve bizimle senin aranda bir perde vardır. Artık sen, bildiğini yap, biz de bildiğimizi yapacağız.\" dediler.", transliteration: "Ve kalu kulubuna fi ekinnetin mimma ted'una ileyhi ve fi azanina vakrun ve min beynina ve beynike hicabun fa'mel innena amilun."},
  {id: 4224, sure_no: 6, ayet_no: 6, sure_adi: "Fussilet", text: "قُلْ اِنَّـمَٓا اَنَا۬ بَشَرٌ مِثْلُكُمْ يُوحٰٓى اِلَيَّ اَنَّـمَٓا اِلٰهُـكُمْ اِلٰهٌ وَاحِدٌ فَاسْتَق۪يمُٓوا اِلَيْهِ وَاسْتَغْفِرُوهُۜ وَوَيْلٌ لِلْمُشْرِك۪ينَۙ", translation: "De ki: \"Ben de sizin gibi bir beşerim. Bana vahyolunuyor ki, sizin ilahınız, bir tek ilahtır. Öyleyse doğrudan O'na yönelin ve yalnızca O'ndan bağışlanma dileyin. Müşriklerin vay haline!\"", transliteration: "Kul innema ene beşerun mislukum yuha ileyye ennema ilahukum ilahun vahidun festekimu ileyhi vestagfiruh, ve veylun lil muşrikin."},
  {id: 4225, sure_no: 7, ayet_no: 7, sure_adi: "Fussilet", text: "اَلَّذ۪ينَ لَا يُؤْتُونَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ كَافِرُونَ", translation: "Onlar zekat yapmazlar. Ahiret'i inkar ederler.", transliteration: "Ellezine la yu'tunez zekate ve hum bil ahireti hum kafirun."},
  {id: 4226, sure_no: 8, ayet_no: 8, sure_adi: "Fussilet", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُونٍ۟", translation: "İman eden ve salihatı yapanlar için minnet altında bırakmayan bir ödül vardır.", transliteration: "İnnellezine amenu ve amilus salihati lehum ecrun gayru memnun."},
  {id: 4227, sure_no: 9, ayet_no: 9, sure_adi: "Fussilet", text: "قُلْ اَئِنَّكُمْ لَتَكْفُرُونَ بِالَّذ۪ي خَلَقَ الْاَرْضَ ف۪ي يَوْمَيْنِ وَتَجْعَلُونَ لَـهُٓ اَنْدَاداًۜ ذٰلِكَ رَبُّ الْعَالَم۪ينَۚ", translation: "De ki: \"Gerçekten de yeri iki günde yaratana mı nankörlük ediyorsunuz? O'na denk olanlar mı görüyorsunuz? O, alemlerin Rabb'idir.\"", transliteration: "Kul e innekum le tekfurune billezi halakal arda fi yevmeyni ve tec'alune lehu endada, zalike rabbul alemin."},
  {id: 4228, sure_no: 10, ayet_no: 10, sure_adi: "Fussilet", text: "وَجَعَلَ ف۪يهَا رَوَاسِيَ مِنْ فَوْقِهَا وَبَارَكَ ف۪يهَا وَقَدَّرَ ف۪يهَٓا اَقْوَاتَهَا ف۪ٓي اَرْبَعَةِ اَيَّامٍۜ سَوَٓاءً لِلسَّٓائِل۪ينَ", translation: "Orada; onun üzerinde ağır baskılar oluşturdu. Ve orayı bereketli kıldı. Orada rızkını temin etmek isteyenler için, rızıkları, fark gözetmeden dört gün içinde takdir etti.", transliteration: "Ve ceale fiha revasiye min fevkıha ve bareke fiha ve kaddere fiha akvateha fi erbeati eyyam, sevaen lis sailin."},
  {id: 4229, sure_no: 11, ayet_no: 11, sure_adi: "Fussilet", text: "ثُمَّ اسْتَوٰٓى اِلَى السَّمَٓاءِ وَهِيَ دُخَانٌ فَقَالَ لَهَا وَلِلْاَرْضِ ائْتِيَا طَوْعاً اَوْ كَرْهاًۜ قَالَـتَٓا اَتَيْنَا طَٓائِع۪ينَ", translation: "Sonra duman halinde bulunan göğe yöneldi. Ona ve yeryüzüne: \"İsteyerek veya istemeyerek gelin!\" dedi. İkisi de: \"İsteyerek geldik.\" dediler.", transliteration: "Summesteva iles semai ve hiye duhanun fe kale leha ve lil ardı'tiya tav'an ev kerha, kaleta eteyna taiin."},
  {id: 4230, sure_no: 12, ayet_no: 12, sure_adi: "Fussilet", text: "فَقَضٰيهُنَّ سَبْعَ سَمٰوَاتٍ ف۪ي يَوْمَيْنِ وَاَوْحٰى ف۪ي كُلِّ سَمَٓاءٍ اَمْرَهَاۜ وَزَيَّنَّا السَّمَٓاءَ الدُّنْيَا بِمَصَاب۪يحَۗ وَحِفْظاًۜ ذٰلِكَ تَقْد۪يرُ الْعَز۪يزِ الْعَل۪يمِ", translation: "Böylece onları iki günde yedi gök olarak tamamladı. Ve her göğe işini vahyetti. Ve dünya göğünü kandillerle süsleyip koruduk. İşte bu, Mutlak Üstün Olan ve Her Şeyi Bilen'in takdiridir.", transliteration: "Fe kadahunne seb'a semavatin fi yevmeyni ve evha fi kulli semain emreha ve zeyyennes semaed dunya bi mesabiha ve hıfza, zalike takdirul azizil alim."},
  {id: 4231, sure_no: 13, ayet_no: 13, sure_adi: "Fussilet", text: "فَاِنْ اَعْرَضُوا فَقُلْ اَنْذَرْتُكُمْ صَاعِقَةً مِثْلَ صَاعِقَةِ عَادٍ وَثَمُودَۜ", translation: "Eğer hala yüz çevirirlerse, onlara de ki: \"Ad ve Semud'un yıldırımı gibi bir yıldırıma karşı sizi uyardım.\"", transliteration: "Fe in a'radu fe kul enzertukum saıkaten misle saıkati adin ve semud."},
  {id: 4232, sure_no: 14, ayet_no: 14, sure_adi: "Fussilet", text: "اِذْ جَٓاءَتْهُمُ الرُّسُلُ مِنْ بَيْنِ اَيْد۪يهِمْ وَمِنْ خَلْفِهِمْ اَلَّا تَعْبُدُٓوا اِلَّا اللّٰهَۜ   قَالُوا لَوْ شَٓاءَ رَبُّنَا لَاَنْزَلَ مَلٰٓئِكَةً فَاِنَّا بِمَٓا اُرْسِلْتُمْ بِه۪ كَافِرُونَ", translation: "Hani! Onlara, onlardan önceki ve sonrakilere, \"Allah'tan başkasına kulluk etmeyin.\" diye Resuller gelmişti. Onlar, \"Eğer Rabb'imiz isteseydi kesinlikle melekler indirirdi. O halde biz kendisiyle gönderilmiş olduğunuz şeylere kafirlik ediyoruz.\" demişlerdi.", transliteration: "İz caethumur rusulu min beyni eydihim ve min halfihim ella ta'budu illallah, kalu lev şae rabbuna le enzele melaiketen fe inna bima ursiltum bihi kafirun."},
  {id: 4233, sure_no: 15, ayet_no: 15, sure_adi: "Fussilet", text: "فَاَمَّا عَادٌ فَاسْتَكْبَرُوا فِي الْاَرْضِ بِغَيْرِ الْحَقِّ وَقَالُوا مَنْ اَشَدُّ مِنَّا قُوَّةًۜ  اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذ۪ي خَلَقَهُمْ هُوَ اَشَدُّ مِنْهُمْ قُوَّةًۜ وَكَانُوا بِاٰيَاتِنَا يَجْحَدُونَ", translation: "Ad'a gelince, onlar yeryüzünde haksız yere büyüklük tasladılar: \"Bizden daha güçlü kim olabilir?\" dediler. Kendilerini yaratan Allah'ın, kendilerinden daha güçlü olduğunu görmediler mi? Ayetlerimizi bile bile reddediyorlardı.", transliteration: "Fe emma adun festekberu fil ardı bi gayril hakkı ve kalu men eşeddu minna kuvveh, e ve lem yerev ennellahellezi halakahum huve eşeddu minhum kuvveh ve kanu bi ayatina yechadun."},
  {id: 4234, sure_no: 16, ayet_no: 16, sure_adi: "Fussilet", text: "فَاَرْسَلْنَا عَلَيْهِمْ ر۪يحاً صَرْصَراً ف۪ٓي اَيَّامٍ نَحِسَاتٍ لِنُذ۪يقَهُمْ عَذَابَ الْخِزْيِ فِي الْحَيٰوةِ الدُّنْيَاۜ وَلَعَذَابُ الْاٰخِرَةِ اَخْزٰى وَهُمْ لَا يُنْصَرُونَ", translation: "Bunun üzerine, dünya hayatında zillet azabını onlara tattırmamız için, o bahtsız günlerde dondurucu bir rüzgar gönderdik. Ahiret azabı ise elbette daha rezil edicidir. Ve onlara yardım da olunmaz.", transliteration: "Fe erselna aleyhim rihan sarsaran fi eyyamin nahisatin li nuzikahum azabel hizyi fil hayatid dunya, ve le azabul ahireti ahza ve hum la yunsarun."},
  {id: 4235, sure_no: 17, ayet_no: 17, sure_adi: "Fussilet", text: "وَاَمَّا ثَمُودُ فَهَدَيْنَاهُمْ فَاسْتَحَبُّوا الْعَمٰى عَلَى الْهُدٰى فَاَخَذَتْهُمْ صَاعِقَةُ الْعَذَابِ الْهُونِ بِمَا كَانُوا يَكْسِبُونَۚ", translation: "Semud halkına gelince, onlara doğru yolu göstermiştik. Fakat onlar körlüğü doğru yola tercih ettiler. Bunun üzerine, yaptıkları şeyler nedeniyle, alçaltıcı azabın yıldırımı onları yakaladı.", transliteration: "Ve emma semudu fe hedeynahum festehabbul ama alel huda fe ehazethum saıkatul azabil huni bima kanu yeksibun."},
  {id: 4236, sure_no: 18, ayet_no: 18, sure_adi: "Fussilet", text: "وَنَجَّيْنَا الَّذ۪ينَ اٰمَنُوا وَكَانُوا يَتَّقُونَ۟", translation: "Biz, iman eden ve takvalı davranan kimseleri kurtardık.", transliteration: "Ve necceynellezine amenu ve kanu yettekun."},
  {id: 4237, sure_no: 19, ayet_no: 19, sure_adi: "Fussilet", text: "وَيَوْمَ يُحْشَرُ اَعْدَٓاءُ اللّٰهِ اِلَى النَّارِ فَهُمْ يُوزَعُونَ", translation: "O Gün, Allah'ın düşmanları bir araya getirilip topluca ateşe sürülecekler.", transliteration: "Ve yevme yuhşeru a'daullahi ilen nari fe hum yuzeun."},
  {id: 4238, sure_no: 20, ayet_no: 20, sure_adi: "Fussilet", text: "حَتّٰٓى اِذَا مَا جَٓاؤُ۫هَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَاَبْصَارُهُمْ وَجُلُودُهُمْ بِمَا كَانُوا يَعْمَلُونَ", translation: "Nihayet oraya geldikleri zaman, yapmış oldukları şeylere; işitme, görme duyuları ve bedenleri kendi aleyhlerine tanıklık ederler.", transliteration: "Hatta iza ma cauha şehide aleyhim sem'uhum ve ebsaruhum ve culuduhum bima kanu ya'melun."},
  {id: 4239, sure_no: 21, ayet_no: 21, sure_adi: "Fussilet", text: "وَقَالُوا لِجُلُودِهِمْ لِمَ شَهِدْتُمْ عَلَيْنَاۜ قَالُٓوا اَنْطَقَنَا اللّٰهُ الَّـذ۪ٓي اَنْطَقَ كُلَّ شَيْءٍ وَهُوَ خَلَقَكُمْ اَوَّلَ مَرَّةٍ وَاِلَيْهِ تُرْجَعُونَ", translation: "Bedenlerine, \"Niçin aleyhimize tanıklık ettiniz?\" dediler. Bedenleri de: \"Bizi, her şeyi konuşturan Allah konuşturdu. Sizi ilk defa O yarattı ve O'na döndürülüyorsunuz.\"", transliteration: "Ve kalu li culudihim lime şehidtum aleyna, kalu entakanallahullezi entaka kulle şey'in ve huve halakakum evvele merretin ve ileyhi turceun."},
  {id: 4240, sure_no: 22, ayet_no: 22, sure_adi: "Fussilet", text: "وَمَا كُنْتُمْ تَسْتَتِرُونَ اَنْ يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَٓا اَبْصَارُكُمْ وَلَا جُلُودُكُمْ وَلٰكِنْ ظَنَنْتُمْ اَنَّ اللّٰهَ لَا يَعْلَمُ كَث۪يراً مِمَّا تَعْمَلُونَ", translation: "\"İşitme ve görme duyunuz ve bedeniniz aleyhinize tanıklık eder diye sakınmıyordunuz. Yapmakta olduğunuz birçok şeyi Allah'ın bilmediğini zannediyordunuz.\"", transliteration: "Ve ma kuntum testetirune en yeşhede aleykum sem'ukum ve la ebsarukum ve la culudukum ve lakin zanentum ennellahe la ya'lemu kesiren mimma ta'melun."},
  {id: 4241, sure_no: 23, ayet_no: 23, sure_adi: "Fussilet", text: "وَذٰلِكُمْ ظَنُّكُمُ الَّذ۪ي ظَنَنْتُمْ بِرَبِّكُمْ اَرْدٰيكُمْ فَاَصْبَحْتُمْ مِنَ الْخَاسِر۪ينَ", translation: "\"İşte Rabb'iniz hakkındaki bu zannınız, sizi helake sürükledi. Böylece hüsrana uğrayanlardan oldunuz.\"", transliteration: "Ve zalikum zannukumullezi zanentum bi rabbikum erdakum fe asbahtum minel hasirin."},
  {id: 4242, sure_no: 24, ayet_no: 24, sure_adi: "Fussilet", text: "فَاِنْ يَصْبِرُوا فَالنَّارُ مَثْوًى لَهُمْۚ وَاِنْ يَسْتَعْتِبُوا فَمَا هُمْ مِنَ الْمُعْتَب۪ينَ", translation: "Artık dayanabilirlerse, onlar için konaklama yeri ateştir. Eğer özür beyan edip af isteseler de onlar affedilecek olanlardan değillerdir.", transliteration: "Fe in yasbiru fen naru mesven lehum ve in yesta'tibu fe ma hum minel mu'tebin."},
  {id: 4243, sure_no: 25, ayet_no: 25, sure_adi: "Fussilet", text: "وَقَيَّضْنَا لَهُمْ قُرَنَٓاءَ فَزَيَّنُوا لَهُمْ مَا بَيْنَ اَيْد۪يهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ الْقَوْلُ ف۪ٓي اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِنَ الْجِنِّ وَالْاِنْسِۚ اِنَّهُمْ كَانُوا خَاسِر۪ينَ۟", translation: "Biz, onlara birtakım yandaşlar musallat ettik. Yaptıklarını ve yapacaklarını onlara süslü gösterdiler. Cinn ve insten, kendilerinden önce gelip geçmiş topluluklarda yürürlükte olan \"söz\" üzerlerine gerçekleşti. Onlar hüsrana uğrayan kimselerdir.", transliteration: "Ve kayyadna lehum kurenae fe zeyyenu lehum ma beyne eydihim ve ma halfehum ve hakka aleyhimul kavlu fi umemin kad halet min kablihim minel cinni vel ins, innehum kanu hasirin."},
  {id: 4244, sure_no: 26, ayet_no: 26, sure_adi: "Fussilet", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا لَا تَسْمَعُوا لِهٰذَا الْقُرْاٰنِ وَالْغَوْا ف۪يهِ لَعَلَّكُمْ تَغْلِبُونَ", translation: "Kafirler: \"Bu Kur'an'ı dinlemeyin, gürültü edin, belki üstün gelirsiniz.\" dediler.", transliteration: "Ve kalellezine keferu la tesmeu li hazel kur'ani velgav fihi leallekum taglibun."},
  {id: 4245, sure_no: 27, ayet_no: 27, sure_adi: "Fussilet", text: "فَلَنُذ۪يقَنَّ الَّذ۪ينَ كَفَرُوا عَذَاباً شَد۪يداً وَلَنَجْزِيَنَّهُمْ اَسْوَاَ الَّذ۪ي كَانُوا يَعْمَلُونَ", translation: "Böylesi Kafirlere şiddetli bir azap tattıracağız. Ve onları yaptıklarının en kötüsü ile kesinlikle cezalandıracağız.", transliteration: "Fe le nuzikannellezine keferu azaben şediden ve le necziyennehum esveellezi kanu ya'melun."},
  {id: 4246, sure_no: 28, ayet_no: 28, sure_adi: "Fussilet", text: "ذٰلِكَ جَزَٓاءُ اَعْدَٓاءِ اللّٰهِ النَّارُۚ لَهُمْ ف۪يهَا دَارُ الْخُلْدِۜ جَزَٓاءً بِمَا كَانُوا بِاٰيَاتِنَا يَجْحَدُونَ", translation: "İşte böyle! Allah'ın düşmanlarının cezası ateştir. Ayetlerimizi bilerek reddetmeleri nedeniyle ceza olarak, onlar için orada ebedilik yurdu vardır.", transliteration: "Zalike cezau a'daillahin nar, lehum fiha darul huld, cezaen bimakanu bi ayatina yechadun."},
  {id: 4247, sure_no: 29, ayet_no: 29, sure_adi: "Fussilet", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا رَبَّـنَٓا اَرِنَا الَّذَيْنِ اَضَلَّانَا مِنَ الْجِنِّ وَالْاِنْسِ نَجْعَلْهُمَا تَحْتَ اَقْدَامِنَا لِيَكُونَا مِنَ الْاَسْفَل۪ينَ", translation: "Kafirler: \"Rabb'imiz! Cinn ve insten bizi saptıranları bize göster. Aşağılanmışlardan olmaları için onları ayaklarımızın altına alalım.\" dediler.", transliteration: "Ve kalellezine keferu rabbena erinellezeyni edallana minel cinni vel insi nec'al huma tahte akdamina li yekuna minel esfelin."},
  {id: 4248, sure_no: 30, ayet_no: 30, sure_adi: "Fussilet", text: "اِنَّ الَّذ۪ينَ قَالُوا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوا تَـتَنَزَّلُ عَلَيْهِمُ الْمَلٰٓئِكَةُ اَلَّا تَخَافُوا وَلَا تَحْزَنُوا وَاَبْشِرُوا بِالْجَنَّةِ الَّت۪ي كُنْتُمْ تُوعَدُونَ", translation: "\"Rabb'imiz Allah'tır.\" deyip, dosdoğru olanlara gelince, onlara melekler gelerek: \"Korkmayın ve üzülmeyin. Söz verildiğiniz Cennet'le sevinin!\" derler.", transliteration: "İnnellezine kalu rabbunallahu summestekamu tetenezzelu aleyhimul melaiketu ella tehafu ve la tahzenu ve ebşiru bil cennetilleti kuntum tuadun."},
  {id: 4249, sure_no: 31, ayet_no: 31, sure_adi: "Fussilet", text: "نَحْنُ اَوْلِيَٓاؤُ۬كُمْ فِي الْحَيٰوةِ الدُّنْيَا وَفِي الْاٰخِرَةِۚ وَلَكُمْ ف۪يهَا مَا تَشْتَـه۪ٓي اَنْفُسُكُمْ وَلَكُمْ ف۪يهَا مَا تَدَّعُونَۜ", translation: "\"Biz, dünya hayatında ve ahirette sizin evliyanızız. Orada canlarınızın istediği her şey vardır ve istediğiniz her şey emrinizdedir.\"", transliteration: "Nahnu evliyaukum fil hayatid dunya ve fil ahireh, ve lekum fiha ma teştehi enfusukum ve lekum fiha ma teddeun."},
  {id: 4250, sure_no: 32, ayet_no: 32, sure_adi: "Fussilet", text: "نُزُلاً مِنْ غَفُورٍ رَح۪يمٍ۟", translation: "\"Çok Bağışlayıcı ve Rahmeti Kesintisiz olan tarafından bir ikram olarak.\"", transliteration: "Nuzulen min gafurin rahim."},
  {id: 4251, sure_no: 33, ayet_no: 33, sure_adi: "Fussilet", text: "وَمَنْ اَحْسَنُ قَوْلاً مِمَّنْ دَعَٓا اِلَى اللّٰهِ وَعَمِلَ صَالِحاً وَقَالَ اِنَّن۪ي مِنَ الْمُسْلِم۪ينَ", translation: "Allah'a çağıran, salihatı yapan ve \"Ben Müslüm olanlardanım.\" diyen kimseden daha iyi sözlü kim vardır?", transliteration: "Ve men ahsenu kavlen mimmen dea ilallahi ve amile salihan ve kale inneni minel muslimin."},
  {id: 4252, sure_no: 34, ayet_no: 34, sure_adi: "Fussilet", text: "وَلَا تَسْتَوِي الْحَسَنَةُ وَلَا السَّيِّئَةُۜ اِدْفَعْ بِالَّت۪ي هِيَ اَحْسَنُ فَاِذَا الَّذ۪ي بَيْنَكَ وَبَيْنَهُ عَدَاوَةٌ  كَاَنَّهُ وَلِيٌّ حَم۪يمٌ", translation: "İyilikle kötülük bir değildir. Kötülüğü iyilikle sav. Bir de bakmışsın ki seninle arasında düşmanlık olan kişi, candan velin oluvermiş.", transliteration: "Ve la testevil hasenetu ve les seyyieh, idfa' billeti hiye ahsenu fe izellezi beyneke ve beynehu adavetun ke ennehu veliyyun hamim."},
  {id: 4253, sure_no: 35, ayet_no: 35, sure_adi: "Fussilet", text: "وَمَا يُلَقّٰيهَٓا اِلَّا الَّذ۪ينَ صَبَرُواۚ وَمَا يُلَقّٰيهَٓا اِلَّا ذُوحَظٍّ عَظ۪يمٍ", translation: "Buna ancak sabredenler kavuşturulur ve buna büyük haz sahipleri kavuşturulur.", transliteration: "Ve ma yulakkaha illellezine saberu, ve ma yulakkaha illa zu hazzın azim."},
  {id: 4254, sure_no: 36, ayet_no: 36, sure_adi: "Fussilet", text: "وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِۜ اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُ", translation: "Ne zaman şeytan kötü bir dürtüş ile seni dürtülecek olursa hemen Allah'a sığın. Kuşkusuz O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ve imma yenzeganneke mineş şeytani nezgun festeız billah, innehu huves semiul alim."},
  {id: 4255, sure_no: 37, ayet_no: 37, sure_adi: "Fussilet", text: "وَمِنْ اٰيَاتِهِ الَّيْلُ وَالنَّهَارُ وَالشَّمْسُ وَالْقَمَرُۜ لَا تَسْجُدُوا لِلشَّمْسِ وَلَا لِلْقَمَرِ وَاسْجُدُوا لِلّٰهِ الَّذ۪ي خَلَقَهُنَّ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُونَ", translation: "Gece ve gündüz; Güneş ve Ay O'nun ayetlerindendir. Güneş'e ve Ay'a secde etmeyin. Eğer yalnızca O'na kulluk ediyorsanız, onları yaratmış olana secde edin.", transliteration: "Ve min ayatihil leylu ven neharu veş şemsu vel kamer, la tescudu liş şemsi ve la lil kameri vescudu lillahillezi halakahunne in kuntum iyyahu ta'budun."},
  {id: 4256, sure_no: 38, ayet_no: 38, sure_adi: "Fussilet", text: "فَاِنِ اسْتَكْبَرُوا فَالَّذ۪ينَ عِنْدَ رَبِّكَ يُسَبِّحُونَ لَهُ بِالَّيْلِ وَالنَّهَارِ وَهُمْ لَا يَسْـَٔمُونَ", translation: "Eğer kibirlenirlerse, bilsinler ki, Rabb'inin katında bulunanlar, gece gündüz, O'nu tesbih ederler ve onlar hiç usanmazlar.", transliteration: "Fe inistekberu fellezine inde rabbike yusebbihune lehu bil leyli ven nehari ve hum la yes'emun."},
  {id: 4257, sure_no: 39, ayet_no: 39, sure_adi: "Fussilet", text: "وَمِنْ اٰيَاتِه۪ٓ اَنَّكَ تَرَى الْاَرْضَ خَاشِعَةً فَاِذَٓا اَنْزَلْنَا عَلَيْهَا الْمَٓاءَ اهْتَزَّتْ وَرَبَتْۜ اِنَّ الَّـذ۪ٓي اَحْيَاهَا لَمُحْـيِ الْمَوْتٰىۜ اِنَّهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Kupkuru olarak gördüğün yeryüzünün, sonradan üzerine su indirdiğimiz zaman harekete geçmesi ve nemalanıp kabarması da O'nun ayetlerindendir. Ona hayat veren, elbette ki ölüleri de diriltir. O, Her Şeye Gücü Yeten'dir.", transliteration: "Ve min ayatihi enneke terel arda haşiaten fe iza enzelna aleyhel maehtezzet ve rebet, innellezi ahyaha le muhyil mevta, innehu ala kulli şey'in kadir."},
  {id: 4258, sure_no: 40, ayet_no: 40, sure_adi: "Fussilet", text: "اِنَّ الَّذ۪ينَ يُلْحِدُونَ ف۪ٓي اٰيَاتِنَا لَا يَخْفَوْنَ عَلَيْنَاۜ  اَفَمَنْ يُلْقٰى فِي النَّارِ خَيْرٌ اَمْ مَنْ يَأْت۪ٓي اٰمِناً يَوْمَ الْقِيٰمَةِۜ اِعْمَلُوا مَا شِئْتُمْۙ اِنَّهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Ayetlerimizde saptırma yapanlar, Bize gizli kalmazlar. O halde kıyamet günü ateşe atılan kimse mi yoksa Bize güven içinde gelenler mi hayırlıdır? Dilediğinizi yapın. Kuşkusuz O, yaptığınız şeyleri en iyi görendir.", transliteration: "İnnellezine yulhıdune fi ayatina la yahfevne aleyna, e fe men yulka fin nari hayrun em men ye'ti aminen yevmel kıyameh, i'melu ma şi'tum innehu bima ta'melune basir."},
  {id: 4259, sure_no: 41, ayet_no: 41, sure_adi: "Fussilet", text: "اِنَّ الَّذ۪ينَ كَفَرُوا بِالذِّكْرِ لَمَّا جَٓاءَهُمْۚ وَاِنَّهُ لَكِتَابٌ عَز۪يزٌۙ", translation: "Onlar, kendilerine zikir geldiği zaman onu Küfrettiler. Kuşkusuz O, yüce bir Kitap'tır.", transliteration: "İnnellezine keferu biz zikri lemma caehum, ve innehu le kitabun aziz."},
  {id: 4260, sure_no: 42, ayet_no: 42, sure_adi: "Fussilet", text: "لَا يَأْت۪يهِ الْبَاطِلُ مِنْ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِه۪ۜ تَنْز۪يلٌ مِنْ حَك۪يمٍ حَم۪يدٍ", translation: "Ona önünden ve arkasından batıl yaklaşamaz. En İyi Hüküm Veren, Övgüye Değer Yegane Varlık tarafından indirilmiştir.", transliteration: "La ye'tihil batılu min beyni yedeyhi ve la min halfih, tenzilun min hakimin hamid."},
  {id: 4261, sure_no: 43, ayet_no: 43, sure_adi: "Fussilet", text: "مَا يُقَالُ لَكَ اِلَّا مَا قَدْ ق۪يلَ لِلرُّسُلِ مِنْ قَبْلِكَۜ اِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ وَذُو عِقَابٍ اَل۪يمٍ", translation: "Sana, senden önceki Resullere söylenmiş olandan başka bir şey söylenmiyor. Senin Rabb'in, kesinlikle bağışlamanın ve acı veren azabın sahibidir.", transliteration: "Ma yukalu leke illa ma kad kile lir rusuli min kablik, inne rabbeke le zu magfiretin ve zu ikabin elim."},
  {id: 4262, sure_no: 44, ayet_no: 44, sure_adi: "Fussilet", text: "وَلَوْ جَعَلْنَاهُ قُرْاٰناً اَعْجَمِياًّ لَقَالُوا لَوْلَا فُصِّلَتْ اٰيَاتُهُۜ  ءَاَۭۘعْجَمِيٌّ وَعَرَبِيٌّۜ قُلْ هُوَ لِلَّذ۪ينَ اٰمَنُوا هُدًى وَشِفَٓاءٌۜ وَالَّذ۪ينَ لَا يُؤْمِنُونَ ف۪ٓي اٰذَانِهِمْ وَقْرٌ وَهُوَ عَلَيْهِمْ عَمًىۜ اُو۬لٰٓئِكَ يُنَادَوْنَ مِنْ مَكَانٍ بَع۪يدٍ۟", translation: "Biz, onu yabancı bir dille \"kur'an\" yapsaydık, mutlaka: \"O'nun ayetleri açıklanmalı değil miydi?\" derlerdi. Yabancı dilde bir kur'ana Arap muhatap, hiç olur mu? De ki: \"O, iman edenler için bir yol gösterici ve bir şifadır.\" Ve inanmayanlara gelince, onların kulaklarında bir ağırlık vardır. Ve O, onlara kapalıdır. Onlara sanki uzak bir yerden seslenilmektedir.", transliteration: "Ve lev cealnahu kur'anen a'cemiyyen le kalu lev la fussilet ayatuh, e a'cemiyyun ve arabiy, kul huve lillezine amenu huden ve şifaun, vellezine la yu'minune fi azanihim vakrun ve huve aleyhim ama, ulaike yunadevne min mekanin baid."},
  {id: 4263, sure_no: 45, ayet_no: 45, sure_adi: "Fussilet", text: "وَلَقَدْ اٰتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ ف۪يهِۜ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْۜ وَاِنَّهُمْ لَف۪ي شَكٍّ مِنْهُ مُر۪يبٍ", translation: "Ant olsun ki Biz, Musa'ya Kitap'ı vermiştik de onda görüş ayrılığına düştüler. Eğer Rabb'inin önceden verilmiş bir kararı olmasaydı, aralarında hemen hüküm verilir, işleri bitirilirdi. Onlar, Kur'an hakkında tam bir ikilem ve kaygı içindeler.", transliteration: "Ve lekad ateyna musel kitabe fahtulife fih, ve lev la kelimetun sebekat min rabbike le kudıye beynehum, ve innehum lefi şekkin minhu murib."},
  {id: 4264, sure_no: 46, ayet_no: 46, sure_adi: "Fussilet", text: "مَنْ عَمِلَ صَالِحاً فَلِنَفْسِه۪ وَمَنْ اَسَٓاءَ فَعَلَيْهَاۜ وَمَا رَبُّكَ بِظَلَّامٍ لِلْعَب۪يدِ", translation: "Kim salihatı yaparsa kendisi için yapmış olur. Kim de bir kötülük yaparsa, kötülüğü kendisi içindir. Senin Rabb'in kullarına asla haksızlık yapmaz.", transliteration: "Men amile salihan fe li nefsihi ve men esae fe aleyha, ve ma rabbuke bi zallamin lil abid."},
  {id: 4265, sure_no: 47, ayet_no: 47, sure_adi: "Fussilet", text: "اِلَيْهِ يُرَدُّ عِلْمُ السَّاعَةِۜ وَمَا تَخْرُجُ مِنْ ثَمَرَاتٍ مِنْ اَكْمَامِهَا وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِه۪ۜ وَيَوْمَ يُنَاد۪يهِمْ اَيْنَ شُرَكَٓاء۪يۙ قَالُٓوا اٰذَنَّاكَۙ مَا مِنَّا مِنْ شَه۪يدٍۚ", translation: "O Sa'at'ın bilgisi yalnızca O'na havale olunur. O'nun bilgisi dışında hiçbir ürün kabuğundan çıkmaz. Hiçbir dişi hamile kalmaz ve doğum yapmaz. Onlara: \"Bana ortak koştuklarınız nerede?\" diye seslenileceği gün, onlar: \"Bizden bir tanık olmadığını Sana bildiririz.\" derler.", transliteration: "İleyhi yureddu ilmus saah, ve ma tahrucu min semeratinmin ekmamiha ve ma tahmilu min unsa ve la tedau illa bi ilmih, ve yevme yunadihim eyne şurekai kalu azennake ma minna min şehid."},
  {id: 4266, sure_no: 48, ayet_no: 48, sure_adi: "Fussilet", text: "وَضَلَّ عَنْهُمْ مَا كَانُوا يَدْعُونَ مِنْ قَبْلُ وَظَنُّوا مَا لَهُمْ مِنْ مَح۪يصٍ", translation: "Ve daha önce tapındıkları şeyler, onlardan uzaklaşıp gittiler. Onlar, kendileri için kaçıp kurtulacak yer olmadığını anladılar.", transliteration: "Ve dalle anhum ma kanu yed'une min kablu ve zannu ma lehum min mahis."},
  {id: 4267, sure_no: 49, ayet_no: 49, sure_adi: "Fussilet", text: "لَا يَسْـَٔمُ الْاِنْسَانُ مِنْ دُعَٓاءِ الْخَيْرِۘ وَاِنْ مَسَّهُ الشَّرُّ فَيَؤُ۫سٌ قَنُوطٌ", translation: "İnsan iyilik istemekten bıkıp usanmaz. Eğer kendisine bir kötülük dokunursa, hemen karamsarlığa kapılır ve ümitsiz olur.", transliteration: "La yes'emul insanu min duail hayri ve in messehuş şerru fe yeusun kanut."},
  {id: 4268, sure_no: 50, ayet_no: 50, sure_adi: "Fussilet", text: "وَلَئِنْ اَذَقْنَاهُ رَحْمَةً مِنَّا مِنْ بَعْدِ ضَرَّٓاءَ مَسَّتْهُ لَيَقُولَنَّ هٰذَا ل۪يۙ وَمَٓا اَظُنُّ السَّاعَةَ قَٓائِمَةًۙ وَلَئِنْ رُجِعْتُ اِلٰى رَبّ۪ٓي اِنَّ ل۪ي عِنْدَهُ لَلْحُسْنٰىۚ فَلَنُنَبِّئَنَّ الَّذ۪ينَ كَفَرُوا بِمَا عَمِلُواۘ وَلَنُذ۪يقَنَّهُمْ مِنْ عَذَابٍ غَل۪يظٍ", translation: "Fakat kendisine dokunan sıkıntıdan sonra, ona Tarafımızdan bir rahmet tattırsak: \"Bu benim hakkımdır. Ve Sa'at'ın geleceğini de sanmıyorum. Gelse bile, Rabb'imin huzuruna çıkarılacak olursam, O'nun yanında kesinlikle benim için en iyisi vardır.\" der. O zaman, Kafirlere yaptıklarını kesinlikle bildireceğiz ve onlara kesinlikle ağır bir cezadan tattıracağız.", transliteration: "Ve le in ezaknahu rahmeten minna min ba'di darrae messethu le yekulenne haza li ve ma ezunnus saate kaimeten ve le in ruci'tu ila rabbi inne li indehu lel husna, fe le nunebbiennellezine keferu bima amilu ve le nuzikannehum min azabin galiz."},
  {id: 4269, sure_no: 51, ayet_no: 51, sure_adi: "Fussilet", text: "وَاِذَٓا اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِه۪ۚ وَاِذَا مَسَّهُ الشَّرُّ فَذُو دُعَٓاءٍ عَر۪يضٍ", translation: "İnsana nimet verdiğimiz zaman, yüz çevirip yan çizer. Kendisine bir kötülük dokunduğu zaman da bol bol dua ederek yardım ister.", transliteration: "Ve iza en'amna alel insani a'rada ve nea bi canibih, ve iza messehuş şerru fe zu duain arid."},
  {id: 4270, sure_no: 52, ayet_no: 52, sure_adi: "Fussilet", text: "قُلْ اَرَاَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ ثُمَّ كَفَرْتُمْ بِه۪ مَنْ اَضَلُّ مِمَّنْ هُوَ ف۪ي شِقَاقٍ بَع۪يدٍ", translation: "De ki: \"Gördünüz mü? Eğer Kur'an Allah katından olduğu halde, siz onu inkar ediyor ve tanımıyorsanız; bu durumda, haktan uzak kalandan daha sapkın kim olabilir?\"", transliteration: "Kul e reeytum in kane min indillahi summe kefertum bihi men edallu mimmen huve fi şikakın baid."},
  {id: 4271, sure_no: 53, ayet_no: 53, sure_adi: "Fussilet", text: "سَنُر۪يهِمْ اٰيَاتِنَا فِي الْاٰفَاقِ وَف۪ٓي اَنْفُسِهِمْ حَتّٰى يَتَبَـيَّنَ لَهُمْ اَنَّهُ الْحَقُّۜ اَوَلَمْ يَكْفِ بِرَبِّكَ اَنَّهُ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ", translation: "Ayetlerimizi afakta ve enfüste onlara göstereceğiz. Onun gerçek olduğu açıkça belli olsun diye. Rabb'inin her şeye tanık olması yeterli değil mi?", transliteration: "Se nurihim ayatina fil afakı ve fi enfusihim hatta yetebeyyene lehum ennehul hakk, e ve lem yekfi bi rabbike ennehu ala kulli şey'in şehid."},
  {id: 4272, sure_no: 54, ayet_no: 54, sure_adi: "Fussilet", text: "اَلَٓا اِنَّهُمْ ف۪ي مِرْيَةٍ مِنْ لِقَٓاءِ رَبِّهِمْۜ  اَلَٓا اِنَّهُ بِكُلِّ شَيْءٍ مُح۪يطٌ", translation: "İyi bilin ki onlar, Rabb'lerine kavuşacaklarından kuşku duyuyorlar. İyi bilin ki O, Her Şeyi Kuşatan'dır.", transliteration: "E la innehum fi miryetin min likai rabbihim, e la innehu bi kulli şey'in muhit."},
  {id: 4273, sure_no: 1, ayet_no: 1, sure_adi: "Şura", text: "حٰمٓ", translation: "Ha, Mim.", transliteration: "Ha mim."},
  {id: 4274, sure_no: 2, ayet_no: 2, sure_adi: "Şura", text: "عٓسٓقٓ۠", translation: "Ayn, Sin, Kaf.", transliteration: "Ayn sin kaf."},
  {id: 4275, sure_no: 3, ayet_no: 3, sure_adi: "Şura", text: "كَذٰلِكَ يُوح۪ٓي اِلَيْكَ وَاِلَى الَّذ۪ينَ مِنْ قَبْلِكَۙ اللّٰهُ الْعَز۪يزُ الْحَك۪يمُ", translation: "Mutlak Üstün Olan, En İyi Hüküm Veren Allah, sana ve senden öncekilere işte böyle vahyeder.", transliteration: "Kezalike yuhi ileyke ve ilellezine min kablikellahul azizul hakim."},
  {id: 4276, sure_no: 4, ayet_no: 4, sure_adi: "Şura", text: "لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَهُوَ الْعَلِيُّ الْعَظ۪يمُ", translation: "Göklerde ve yerde olan her şey O'nundur. O, Çok Yüce'dir, Çok Ulu'dur.", transliteration: "Lehu ma fis semavati ve ma fil ard, ve huvel aliyyul azim."},
  {id: 4277, sure_no: 5, ayet_no: 5, sure_adi: "Şura", text: "تَكَادُ السَّمٰوَاتُ يَتَفَطَّرْنَ مِنْ فَوْقِهِنَّ وَالْمَلٰٓئِكَةُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُونَ لِمَنْ فِي الْاَرْضِۜ اَلَٓا اِنَّ اللّٰهَ هُوَ الْغَفُورُ الرَّح۪يمُ", translation: "Gökler neredeyse üstlerinden çatlayacak. Melekler ise Rabb'lerini övgü ile yüceltiyor, yeryüzünde bulunanlar için bağışlanma diliyorlar. İyi bilin ki Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Tekadus semavatu yetefattarne min fevkıhinne vel melaiketu yusebbihune bi hamdi rabbihim ve yestagfirune li men fil ard, e la innellahe huvel gafurur rahim."},
  {id: 4278, sure_no: 6, ayet_no: 6, sure_adi: "Şura", text: "وَالَّذ۪ينَ اتَّخَذُوا مِنْ دُونِه۪ٓ اَوْلِيَٓاءَ اللّٰهُ حَف۪يظٌ عَلَيْهِمْۘ وَمَٓا اَنْتَ عَلَيْهِمْ بِوَك۪يلٍ", translation: "Onlar, O'nun yanı sıra veliler edindiler. Allah, onları görüp gözetlemektedir. Sen onlara vekil değilsin.", transliteration: "Vellezinettehazu min dunihi evliyallahu hafizun aleyhim ve ma ente aleyhim bi vekil."},
  {id: 4279, sure_no: 7, ayet_no: 7, sure_adi: "Şura", text: "وَكَذٰلِكَ اَوْحَيْنَٓا اِلَيْكَ قُرْاٰناً عَرَبِياًّ لِتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَا وَتُنْذِرَ يَوْمَ الْجَمْعِ لَا رَيْبَ ف۪يهِۜ فَر۪يقٌ فِي الْجَنَّةِ وَفَر۪يقٌ فِي السَّع۪يرِ", translation: "Kentlerin anası ve civarındakileri, geleceği kesin olan Toplanma Günü ile uyarman için sana Arapça bir Kur'an vahyettik. Onların bir kısmı Cennet'tedir, bir kısmı da alevli ateştedir.", transliteration: "Ve kezalike evhayna ileyke kur'anen arabiyyen li tunzire ummel kura ve men havleha ve tunzire yevmel cem'i la reybe fih, ferikun fil cenneti ve ferikun fis sair."},
  {id: 4280, sure_no: 8, ayet_no: 8, sure_adi: "Şura", text: "وَلَوْ شَٓاءَ اللّٰهُ لَجَعَلَهُمْ اُمَّةً وَاحِدَةً وَلٰكِنْ يُدْخِلُ مَنْ يَشَٓاءُ ف۪ي رَحْمَتِه۪ۜ وَالظَّالِمُونَ مَا لَهُمْ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ", translation: "Eğer Allah dileseydi kesinlikle onları bir tek ümmet yapardı. Ne var ki O hak eden kimseyi rahmetinin içine koyar. Zalimlere gelince, onların velisi ve yardımcısı yoktur.", transliteration: "Ve lev şaallahu le cealehum ummeten vahıdeten ve lakin yudhilu men yeşau fi rahmetih, vez zalimune ma lehum min veliyyin ve la nasir."},
  {id: 4281, sure_no: 9, ayet_no: 9, sure_adi: "Şura", text: "اَمِ اتَّخَذُوا مِنْ دُونِه۪ٓ اَوْلِيَٓاءَۚ  فَاللّٰهُ هُوَ الْوَلِيُّ وَهُوَ يُحْـيِ الْمَوْتٰىۘ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ۟", translation: "Yoksa O'ndan başka veliler mi edindiler? Oysa ki veli yalnızca O'dur. O, ölüleri diriltir ve O, Her Şeye Gücü Yeten'dir.", transliteration: "Emittehazu min dunihi evliyae, fallahu huvel veliyyu ve huve yuhyil mevta ve huve ala kulli şey'in kadir."},
  {id: 4282, sure_no: 10, ayet_no: 10, sure_adi: "Şura", text: "وَمَا اخْتَلَفْتُمْ ف۪يهِ مِنْ شَيْءٍ فَحُكْمُهُٓ اِلَى اللّٰهِۜ ذٰلِكُمُ اللّٰهُ رَبّ۪ي عَلَيْهِ تَوَكَّلْتُۗ وَاِلَيْهِ اُن۪يبُ", translation: "Bir şey hakkında anlaşmazlığa düşerseniz, artık onun hükmü Allah'a aittir. İşte O Allah, benim Rabb'imdir. O'na tevekkül ettim ve O'na yöneldim.", transliteration: "Ve mahteleftum fihi min şey'in fe hukmuhu ilallah, zalikumullahu rabbi aleyhi tevekkeltu ve ileyhi unib."},
  {id: 4283, sure_no: 11, ayet_no: 11, sure_adi: "Şura", text: "فَاطِرُ السَّمٰوَاتِ وَالْاَرْضِۜ جَعَلَ لَكُمْ مِنْ اَنْفُسِكُمْ اَزْوَاجاً وَمِنَ الْاَنْعَامِ اَزْوَاجاًۚ يَذْرَؤُ۬كُمْ ف۪يهِۜ لَيْسَ كَمِثْلِه۪ شَيْءٌۚ وَهُوَ السَّم۪يعُ الْبَص۪يرُ", translation: "Göklerin ve yerin fıtratını belirleyendir. Size, kendinizden çiftler, hayvanlardan çiftler yapandır. Orada sizi bir düzen içinde üretiyor. O'na benzer hiçbir şey yoktur. O, Her Şeyi Duyan, Her Şeyi Gören'dir.", transliteration: "Fatırus semavati vel ard, ceale lekum min enfusikum ezvacen ve minel en'ami ezvaca, yezreukum fih, leyse ke mislihi şey'un, ve huves semiul basir."},
  {id: 4284, sure_no: 12, ayet_no: 12, sure_adi: "Şura", text: "لَهُ مَقَال۪يدُ السَّمٰوَاتِ وَالْاَرْضِۚ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَٓاءُ وَيَقْدِرُۜ اِنَّهُ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Göklerin ve yeryüzünün kilitleri yalnızca O'nundur. O, dilediği kimse için rızkı genişletir ve takdir eder. O, Her Şeyi En İyi Bilen'dir.", transliteration: "Lehu mekalidus semavati vel ard, yebsutur rızka li men yeşau ve yakdir, innehu bi kulli şey'in alim."},
  {id: 4285, sure_no: 13, ayet_no: 13, sure_adi: "Şura", text: "شَرَعَ لَكُمْ مِنَ الدّ۪ينِ مَا وَصّٰى بِه۪ نُوحاً وَالَّـذ۪ٓي اَوْحَيْنَٓا اِلَيْكَ وَمَا وَصَّيْنَا بِه۪ٓ اِبْرٰه۪يمَ وَمُوسٰى وَع۪يسٰٓى اَنْ اَق۪يمُوا الدّ۪ينَ وَلَا تَتَفَرَّقُوا ف۪يهِۜ   كَبُرَ عَلَى الْمُشْرِك۪ينَ مَا تَدْعُوهُمْ اِلَيْهِۜ اَللّٰهُ يَجْتَب۪ٓي اِلَيْهِ مَنْ يَشَٓاءُ وَيَهْد۪ٓي اِلَيْهِ مَنْ يُن۪يبُ", translation: "O, size dinden Nuh'a tavsiye ettiğini, sana vahyettiğimizi; İbrahim'e, Musa'ya ve İsa'ya, tavsiye buyurduğumuzu şeriat yaptı. \"Dini ayakta tutun ve onda ayrılığa düşmeyin.\" Senin kendilerini davet ettiğin şey, Müşriklere ağır geldi. Allah, hak eden kimseyi kendisine seçer ve kendisine yönelen kimseye doğru yolu gösterir.", transliteration: "Şerea lekum mined dini ma vassa bihi nuhan vellezi evhayna ileyke ve ma vassayna bihi ibrahime ve musa ve isa, en ekimud dine ve la teteferreku fih, kebure alel muşrikine ma ted'uhum ileyh, allahu yectebi ileyhi men yeşau ve yehdi ileyhi men yunib."},
  {id: 4286, sure_no: 14, ayet_no: 14, sure_adi: "Şura", text: "وَمَا تَفَرَّقُٓوا اِلَّا مِنْ بَعْدِ مَا جَٓاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْۜ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ اِلٰٓى اَجَلٍ مُسَمًّى لَقُضِيَ بَيْنَهُمْۜ وَاِنَّ الَّذ۪ينَ اُو۫رِثُوا الْكِتَابَ مِنْ بَعْدِهِمْ لَف۪ي شَكٍّ مِنْهُ مُر۪يبٍ", translation: "Onların, kendilerine ilim geldikten sonra anlaşmazlığa düşmelerinin nedeni kıskançlık ve ihtiraslarıdır. Eğer Rabb'inden, \"belirlenmiş bir süreye kadar\" sözü verilmemiş olunsaydı, aralarında hemen hüküm gerçekleştirilirdi. Onların ardından Kitap'a mirasçı olanlar, tam bir ikilem ve kaygı içindedirler.", transliteration: "Ve ma teferreku illa min ba'di ma caehumul ilmu bagyen beynehum, ve lev la kelimetun sebekat min rabbike ila ecelin musemmen le kudıye beynehum, ve innellezine urisul kitabe min ba'dihim le fi şekkin minhu murib."},
  {id: 4287, sure_no: 15, ayet_no: 15, sure_adi: "Şura", text: "فَلِذٰلِكَ فَادْعُۚ وَاسْتَقِمْ كَمَٓا اُمِرْتَۚ وَلَا تَتَّبِعْ اَهْوَٓاءَهُمْۚ وَقُلْ اٰمَنْتُ بِمَٓا اَنْزَلَ اللّٰهُ مِنْ كِتَابٍۚ وَاُمِرْتُ لِاَعْدِلَ بَيْنَكُمْۜ اَللّٰهُ رَبُّنَا وَرَبُّكُمْۜ لَـنَٓا اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۜ لَا حُجَّةَ بَيْنَنَا وَبَيْنَكُمْۜ اَللّٰهُ يَجْمَعُ بَيْنَنَاۚ وَاِلَيْهِ الْمَص۪يرُۜ", translation: "İşte bunun için, artık sen onlara çağrıda bulun. Buyrulduğun gibi dosdoğru ol. Onların hevalarına uyma. Onlara de ki: \"Allah'ın Kitap'tan indirdiği şeye iman ettim.\" Ve bana aranızda adaleti gerçekleştirmem buyruldu. Allah, bizim de Rabb'imizdir, sizin de Rabb'inizdir. Bizim yaptıklarımız bize, sizin yaptıklarınız da sizedir. Öyleyse aramızda çekişmeye gerek yoktur. Nasıl olsa Allah aramızı bulacak. Dönüş yalnızca O'nadır.\"", transliteration: "Fe li zalike fed'u vestekım kema umirt, ve la tettebi' ehvaehum, ve kul amentu bi ma enzelallahu min kitab, ve umirtu li a'dile beynekum, allahu rabbuna ve rabbukum, lena a'maluna ve lekum a'malukum, la huccete beynena ve beynekum, allahu yecmeubeynena, ve ileyhil masir."},
  {id: 4288, sure_no: 16, ayet_no: 16, sure_adi: "Şura", text: "وَالَّذ۪ينَ يُحَٓاجُّونَ فِي اللّٰهِ مِنْ بَعْدِ مَا اسْتُج۪يبَ لَهُ حُجَّتُهُمْ دَاحِضَةٌ عِنْدَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌ وَلَهُمْ عَذَابٌ شَد۪يدٌ", translation: "Allah'a olan çağrıya olumlu yanıt verdikten sonra, O'nun hakkında tartışanların ileri sürdükleri kanıtlar, Rabb'lerinin yanında geçersizdir. Onların üzerinde gazap vardır, şiddetli bir azap da onlar içindir.", transliteration: "Vellezine yuhaccune fillahi min ba'di mestucibe lehu huccetuhum dahıdatun inde rabbihim ve aleyhim gadabun ve lehum azabun şedid."},
  {id: 4289, sure_no: 17, ayet_no: 17, sure_adi: "Şura", text: "اَللّٰهُ الَّـذ۪ٓي اَنْزَلَ الْكِتَابَ بِالْحَقِّ وَالْم۪يزَانَۜ وَمَا يُدْر۪يكَ لَعَلَّ السَّاعَةَ قَر۪يبٌ", translation: "Allah, Kitap'ı ve mizanı hakk ile indirendir. Nereden bileceksin ki belki de o Sa'at çok yakındır!", transliteration: "Allahullezi enzelel kitabe bil hakkı vel mizan ve ma yudrike lealles saate karib."},
  {id: 4290, sure_no: 18, ayet_no: 18, sure_adi: "Şura", text: "يَسْتَعْجِلُ بِهَا الَّذ۪ينَ لَا يُؤْمِنُونَ بِهَاۚ وَالَّذ۪ينَ اٰمَنُوا مُشْفِقُونَ مِنْهَاۙ وَيَعْلَمُونَ اَنَّهَا الْحَقُّۜ اَلَٓا اِنَّ الَّذ۪ينَ يُمَارُونَ فِي السَّاعَةِ لَف۪ي ضَلَالٍ بَع۪يدٍ", translation: "İnanmayanlar, alayımsı bir şekilde, onun hemen gerçekleşmesini istiyorlar. İman Edenler ise tedbirlidirler. Onun gerçek olduğunu bilirler. Sa'at hakkında tartışanlar, derin bir sapkınlık içindedirler.", transliteration: "Yesta'cilu bihellezine la yu'minune biha, vellezine amenu muşfikune minha ve ya'lemune ennehel hakk, e la innellezine yumarune fis saati le fi dalalin baid."},
  {id: 4291, sure_no: 19, ayet_no: 19, sure_adi: "Şura", text: "اَللّٰهُ لَط۪يفٌ بِعِبَادِه۪ يَرْزُقُ مَنْ يَشَٓاءُۚ وَهُوَ الْقَوِيُّ الْعَز۪يزُ۟", translation: "Allah, kullarına karşı çok hoşnut edicidir. Dilediğini rızıklandırır. O, Mutlak Güç Sahibi'dir, Mutlak Üstün Olan'dır.", transliteration: "Allahu latifun bi ibadihi yerzuku men yeşau, ve huvel kaviyyul aziz."},
  {id: 4292, sure_no: 20, ayet_no: 20, sure_adi: "Şura", text: "مَنْ كَانَ يُر۪يدُ حَرْثَ الْاٰخِرَةِ نَزِدْ لَهُ ف۪ي حَرْثِه۪ۚ وَمَنْ كَانَ يُر۪يدُ حَرْثَ الدُّنْيَا نُؤْتِه۪ مِنْهَا وَمَا لَهُ فِي الْاٰخِرَةِ مِنْ نَص۪يبٍ", translation: "Kim ahiret ekinini isterse, Biz onun kazancını artırırız. Kim dünya ekinini isterse, ona da ondan artırırız. Ve onun için ahirette hiçbir nasip yoktur.", transliteration: "Men kane yuridu harsel ahireti nezid lehu fi harsih, ve men kane yuridu harsed dunya nu'tihi minha ve ma lehu fil ahireti min nasib."},
  {id: 4293, sure_no: 21, ayet_no: 21, sure_adi: "Şura", text: "اَمْ لَهُمْ شُرَكٰٓؤُ۬ا شَرَعُوا لَهُمْ مِنَ الدّ۪ينِ مَا لَمْ يَأْذَنْ بِهِ اللّٰهُۜ وَلَوْلَا كَلِمَةُ الْفَصْلِ لَقُضِيَ بَيْنَهُمْۜ وَاِنَّ الظَّالِم۪ينَ لَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Yoksa onların, Allah'ın dinde izin vermediği bir şeyi, kendilerine meşru kılan ortakları mı var? Eğer \"ayırma kelimesi\" olmasaydı kesinlikle aralarında hemen hüküm verilirdi. Kuşkusuz ki zalimler için acı bir azap vardır.", transliteration: "Em lehum şurekau şerau lehum mined dini ma lem ye'zen bihillah, ve lev la kelimetul faslı le kudiye beynehum, ve innez zalimine lehum azabun elim."},
  {id: 4294, sure_no: 22, ayet_no: 22, sure_adi: "Şura", text: "تَرَى الظَّالِم۪ينَ مُشْفِق۪ينَ مِمَّا كَسَبُوا وَهُوَ وَاقِـعٌ بِهِمْۜ وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ ف۪ي رَوْضَاتِ الْجَنَّاتِۚ لَهُمْ مَا يَشَٓاؤُ۫نَ عِنْدَ رَبِّهِمْۜ ذٰلِكَ هُوَ الْفَضْلُ الْكَب۪يرُ", translation: "Kazandıkları şeylerle karşı karşıya geldiklerinde, zalimlerin endişeye kapıldıklarını görürsün. İman edip salihatı yapanlar da Cennetler'in bahçelerindedirler. Onlar için Rabb'lerinin yanında istedikleri her şey vardır. İşte bu büyük ikramdır.", transliteration: "Terez zalimine muşfikine mimma kesebu ve huve vakıun bihim, vellezine amenu ve amilus salihati fi ravdatil cennat, lehum ma yeşaune inde rabbihim zalike huvel fadlul kebir."},
  {id: 4295, sure_no: 23, ayet_no: 23, sure_adi: "Şura", text: "ذٰلِكَ الَّذ۪ي يُبَشِّرُ اللّٰهُ عِبَادَهُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِۜ قُلْ لَٓا اَسْـَٔلُكُمْ عَلَيْهِ اَجْراً اِلَّا الْمَوَدَّةَ فِي الْقُرْبٰىۜ وَمَنْ يَقْتَرِفْ حَسَنَةً نَزِدْ لَهُ ف۪يهَا حُسْناًۜ اِنَّ اللّٰهَ غَفُورٌ شَكُورٌ", translation: "İşte bu, Allah'ın, iman edip salihatı yapan kullarına müjdelediği şeydir. De ki: \"Ben bu çağrıya karşılık \"yakınlıkta sevgiden\" başka sizden bir ücret istemiyorum.\" Her kim bir iyilik yaparsa, onun için iyilikleri artırırız. Allah, Çok Bağışlayıcı'dır, Çok Şükreden'dir.", transliteration: "Zalikellezi yubeşşirullahu ibadehullezine amenu ve amilus salihat, kul la es'elukum aleyhi ecren illel meveddete fil kurba ve men yakterif haseneten nezid lehu fiha husna, innellahe gafurun şekur."},
  {id: 4296, sure_no: 24, ayet_no: 24, sure_adi: "Şura", text: "اَمْ يَقُولُونَ افْتَرٰى عَلَى اللّٰهِ كَذِباًۚ فَاِنْ يَشَأِ اللّٰهُ يَخْتِمْ عَلٰى قَلْبِكَۜ وَيَمْحُ اللّٰهُ الْبَاطِلَ وَيُحِقُّ الْحَقَّ بِكَلِمَاتِه۪ۜ اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Onlar, senin için \"Allah'a karşı yalan uydurdu.\" diyorlar. Oysaki Allah dilerse senin kalbini de mühürler. Batıl'ı yok eder. Ve kelimeleriyle Hakk'ı gerçekleştirir. O, göğüslerde olanı çok iyi bilendir.", transliteration: "Em yekuluneftera alallahi keziba, fe in yeşeillahu yahtim ala kalbik, ve yemhullahul batıla ve yuhıkkul hakka bi kelimatih, innehu alimun bi zatis sudur."},
  {id: 4297, sure_no: 25, ayet_no: 25, sure_adi: "Şura", text: "وَهُوَ الَّذ۪ي يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِه۪ وَيَعْفُوا عَنِ السَّيِّـَٔاتِ وَيَعْلَمُ مَا تَفْعَلُونَۙ", translation: "Ve O, kullarının tevbesini kabul eder ve kötülüklerini bağışlar. Ve yaptığınız şeyleri bilir.", transliteration: "Ve huvellezi yakbelut tevbete an ibadihi ve ya'fu anis seyyiati ve ya'lemu ma tef'alun."},
  {id: 4298, sure_no: 26, ayet_no: 26, sure_adi: "Şura", text: "وَيَسْتَج۪يبُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَيَز۪يدُهُمْ مِنْ فَضْلِه۪ۜ وَالْكَافِرُونَ لَهُمْ عَذَابٌ شَد۪يدٌ", translation: "İman edip salihatı yapanların isteklerini kabul eder ve onlara lütfundan daha fazlasını verir. Kafirlere ise şiddetli bir azap vardır.", transliteration: "Ve yestecibullezine amenu ve amilus salihati ve yeziduhum min fadlih, vel kafirune lehum azabun şedid."},
  {id: 4299, sure_no: 27, ayet_no: 27, sure_adi: "Şura", text: "وَلَوْ بَسَطَ اللّٰهُ الرِّزْقَ لِعِبَادِه۪ لَبَغَوْا فِي الْاَرْضِ وَلٰكِنْ يُنَزِّلُ بِقَدَرٍ مَا يَشَٓاءُۜ اِنَّهُ بِعِبَادِه۪ خَب۪يرٌ بَص۪يرٌ", translation: "Eğer Allah, kullarına rızkı genişletseydi, kesinlikle yeryüzünde azarlardı. Fakat O, dilediği kadarını indirir. Kuşkusuz O, kullarının Her Şeyinden Haberdar Olan'dır, Her Şeyi Gören'dir.", transliteration: "Ve lev besetallahur rızka li ibadihi le begav fil ardı ve lakin yunezzilu bi kaderin ma yeşau, innehu bi ibadihi habirun basir."},
  {id: 4300, sure_no: 28, ayet_no: 28, sure_adi: "Şura", text: "وَهُوَ الَّذ۪ي يُنَزِّلُ الْغَيْثَ مِنْ بَعْدِ مَا قَنَطُوا وَيَنْشُرُ رَحْمَتَهُۜ  وَهُوَ الْوَلِيُّ الْحَم۪يدُ", translation: "O, insanlar ümitsizliğe düştükten sonra yağmuru yağdıran ve rahmetini yayandır. Ve O, Gerçek Gözetici'dir, Övgüye Değer Yegane Varlık'tır.", transliteration: "Ve huvellezi yunezzilul gayse min ba'di ma kanetu ve yenşuru rahmeteh, ve huvel veliyyul hamid."},
  {id: 4301, sure_no: 29, ayet_no: 29, sure_adi: "Şura", text: "وَمِنْ اٰيَاتِه۪ خَلْقُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَثَّ ف۪يهِمَا مِنْ دَٓابَّةٍۜ وَهُوَ عَلٰى جَمْعِهِمْ اِذَا يَشَٓاءُ قَد۪يرٌ۟", translation: "Göklerin ve yeryüzünün yaratılması ve o ikisinde her canlıdan çoğaltıp yayması, O'nun ayetlerindendir. O, dilediği zaman onları toplamaya gücü yetendir.", transliteration: "Ve min ayatihi halkus semavati vel ardı ve ma besse fihima min dabbeh, ve huve ala cem'ihim iza yeşau kadir."},
  {id: 4302, sure_no: 30, ayet_no: 30, sure_adi: "Şura", text: "وَمَٓا اَصَابَكُمْ مِنْ مُص۪يبَةٍ فَبِمَا كَسَبَتْ اَيْد۪يكُمْ وَيَعْفُوا عَنْ كَث۪يرٍۜ", translation: "Size isabet eden musibet kendi ellerinizle yaptığınız şeyler yüzündendir. O, çoğuna da engel oluyor.", transliteration: "Ve ma esabekum min musibetin fe bi ma kesebet eydikum ve ya'fu an kesir."},
  {id: 4303, sure_no: 31, ayet_no: 31, sure_adi: "Şura", text: "وَمَٓا اَنْتُمْ بِمُعْجِز۪ينَ فِي الْاَرْضِۚ وَمَا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَلَا نَص۪يرٍ", translation: "Siz, yeryüzünde aciz bırakabilecek olanlar değilsiniz. Sizin, Allah'tan başka bir veliniz ve yardımcınız yoktur.", transliteration: "Ve ma entum bi mu'cizine fil ard, ve ma lekum min dunillahi min veliyyin ve la nasir."},
  {id: 4304, sure_no: 32, ayet_no: 32, sure_adi: "Şura", text: "وَمِنْ اٰيَاتِهِ الْجَوَارِ فِي الْبَحْرِ كَالْاَعْلَامِۜ", translation: "Denizdeki dağ gibi akıp giden gemiler O'nun ayetlerindendir.", transliteration: "Ve min ayatihil cevari fil bahri kel a'lam."},
  {id: 4305, sure_no: 33, ayet_no: 33, sure_adi: "Şura", text: "اِنْ يَشَأْ يُسْكِنِ الرّ۪يحَ فَيَظْلَلْنَ رَوَاكِدَ عَلٰى ظَهْرِه۪ۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍۙ", translation: "Dilerse rüzgarı durdurur da denizin yüzünde kalakalırlar. Kuşkusuz bunda çok sabreden ve çok şükredenler için ayetler vardır.", transliteration: "İn yeşe' yuskinir riha fe yazlelne revakide ala zahrih, inne fi zalike le ayatin li kulli sabbarin şekur."},
  {id: 4306, sure_no: 34, ayet_no: 34, sure_adi: "Şura", text: "اَوْ يُوبِقْهُنَّ بِمَا كَسَبُوا وَيَعْفُ عَنْ كَث۪يرٍۘ", translation: "Veya yaptıkları şeylerden dolayı onları yok eder veya birçoğunu bağışlar.", transliteration: "Ev yubıkhunne bima kesebu ve ya'fu an kesir."},
  {id: 4307, sure_no: 35, ayet_no: 35, sure_adi: "Şura", text: "وَيَعْلَمَ الَّذ۪ينَ يُجَادِلُونَ ف۪ٓي اٰيَاتِنَاۜ مَا لَهُمْ مِنْ مَح۪يصٍ", translation: "Ayetlerimiz hakkında mücadele edenler, kendileri için sığınacak bir yer olmadığını bilsinler.", transliteration: "Ve ya'lemellezine yucadilune fi ayatina, ma lehum min mahis."},
  {id: 4308, sure_no: 36, ayet_no: 36, sure_adi: "Şura", text: "فَمَٓا اُو۫ت۪يتُمْ مِنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَاۚ وَمَا عِنْدَ اللّٰهِ خَيْرٌ وَاَبْقٰى لِلَّذ۪ينَ اٰمَنُوا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُونَۚ", translation: "Size verilen her şey, geçici dünya hayatının geçimliğidir. İman edip, Rabb'lerine tevekkül edenlere Allah katından verilecek nimetler daha hayırlıdır ve daha kalıcıdır.", transliteration: "Fe ma utitum min şey'in fe meta'ul hayatid dunya, ve ma ındallahi hayrun ve ebka lillezine amenu ve ala rabbihim yetevekkelun."},
  {id: 4309, sure_no: 37, ayet_no: 37, sure_adi: "Şura", text: "وَالَّذ۪ينَ يَجْتَنِبُونَ كَـبَٓائِرَ الْاِثْمِ وَالْفَوَاحِشَ وَاِذَا مَا غَضِبُوا هُمْ يَغْفِرُونَۚ", translation: "Onlar, büyük günahlardan ve fahşadan kaçınan ve öfkelendikleri zaman bağışlayan kimselerdir.", transliteration: "Vellezine yectenibune kebairel ismi vel fevahışe ve iza ma gadıbuhum yagfirun."},
  {id: 4310, sure_no: 38, ayet_no: 38, sure_adi: "Şura", text: "وَالَّذ۪ينَ اسْتَجَابُوا لِرَبِّهِمْ وَاَقَامُوا الصَّلٰوةَۖ وَاَمْرُهُمْ شُورٰى بَيْنَهُمْۖ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَۚ", translation: "Rabb'lerinin çağrısına uyarlar ve salatı ikame ederler. Onlar, işlerini birbirlerine şura ile yaparlar. Kendilerine verdiğimiz rızıktan infak ederler.", transliteration: "Vellezinestacabu li rabbihim ve ekamus salate ve emruhum şura beynehum ve mimma rezaknahum yunfikun."},
  {id: 4311, sure_no: 39, ayet_no: 39, sure_adi: "Şura", text: "وَالَّذ۪ينَ اِذَٓا اَصَابَهُمُ الْبَغْيُ هُمْ يَنْتَصِرُونَ", translation: "Bir haksızlığa, zulme uğradıkları zaman, yardımlaşırlar.", transliteration: "Vellezine iza esabehumul bagyuhum yentesırun."},
  {id: 4312, sure_no: 40, ayet_no: 40, sure_adi: "Şura", text: "وَجَزٰٓؤُ۬ا سَيِّئَةٍ سَيِّئَةٌ مِثْلُهَاۚ فَمَنْ عَفَا وَاَصْلَحَ فَاَجْرُهُ عَلَى اللّٰهِۜ اِنَّهُ لَا يُحِبُّ الظَّالِم۪ينَ", translation: "Bir kötülüğün karşılığı o kötülük ölçüsündedir. Ama kim bağışlar ve düzeltici olursa artık onun karşılığı Allah'a aittir. Kuşkusuz O, haksızlık yapanları sevmez.", transliteration: "Ve cezau seyyietin, seyyietun misluha, fe men afa ve asleha fe ecruhu alallah, innehu la yuhıbbuz zalimin."},
  {id: 4313, sure_no: 41, ayet_no: 41, sure_adi: "Şura", text: "وَلَمَنِ انْتَصَرَ بَعْدَ ظُلْمِه۪ فَاُو۬لٰٓئِكَ مَا عَلَيْهِمْ مِنْ سَب۪يلٍۜ", translation: "Kim de haksızlığa uğradıktan sonra hakkını alırsa, onun aleyhine bir yol tercih edilmez.", transliteration: "Ve le men intesare ba'de zulmihi fe ulaike ma aleyhim min sebil."},
  {id: 4314, sure_no: 42, ayet_no: 42, sure_adi: "Şura", text: "اِنَّمَا السَّب۪يلُ عَلَى الَّذ۪ينَ يَظْلِمُونَ النَّاسَ وَيَبْغُونَ فِي الْاَرْضِ بِغَيْرِ الْحَقِّۜ  اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Fakat insanlara haksızlık yapanların ve yeryüzünde haksız yere zorbalık yapanların aleyhlerine bir yol tercih edilir. İşte onlar, kendileri için acı bir azap olanlardır.", transliteration: "İnnemes sebilu alellezine yazlimunen nase ve yebgune fil ardı bi gayril hakk, ulaike lehum azabun elim."},
  {id: 4315, sure_no: 43, ayet_no: 43, sure_adi: "Şura", text: "وَلَمَنْ صَبَرَ وَغَفَرَ اِنَّ ذٰلِكَ لَمِنْ عَزْمِ الْاُمُورِ۟", translation: "Ancak kim sabreder ve bağışlarsa, büyüklük yapmış olur.", transliteration: "Ve le men sabere ve gafere inne zalike le min azmil umur."},
  {id: 4316, sure_no: 44, ayet_no: 44, sure_adi: "Şura", text: "وَمَنْ يُضْلِلِ اللّٰهُ فَمَا لَهُ مِنْ وَلِيٍّ مِنْ بَعْدِه۪ۜ وَتَرَى الظَّالِم۪ينَ لَمَّا رَاَوُا الْعَذَابَ يَقُولُونَ هَلْ اِلٰى مَرَدٍّ مِنْ سَب۪يلٍۚ", translation: "Allah, kimi sapkınlıkta bırakırsa, artık bunun ardından onun için bir veli bulunmaz. Haksızlık yapanların, azabı gördüklerinde: \"Acaba geri dönmenin bir yolu var mı?\" dediklerini göreceksin.", transliteration: "Ve men yudlilillahu fe ma lehu min veliyin min ba'dih, ve terez zalimine lemma reevul azabe yekulune hel ila mereddin min sebil."},
  {id: 4317, sure_no: 45, ayet_no: 45, sure_adi: "Şura", text: "وَتَرٰيهُمْ يُعْرَضُونَ عَلَيْهَا خَاشِع۪ينَ مِنَ الذُّلِّ يَنْظُرُونَ مِنْ طَرْفٍ خَفِيٍّۜ وَقَالَ الَّذ۪ينَ اٰمَنُٓوا اِنَّ الْخَاسِر۪ينَ الَّذ۪ينَ خَسِرُٓوا اَنْفُسَهُمْ وَاَهْل۪يهِمْ يَوْمَ الْقِيٰمَةِۜ اَلَٓا اِنَّ الظَّالِم۪ينَ ف۪ي عَذَابٍ مُق۪يمٍ", translation: "Onları, aşağılanmalarından dolayı başları öne eğilmiş, göz ucuyla çevrelerine bakarlarken ona sunulduklarını göreceksin. İman edenler: \"Zarara uğrayanlar, kendilerini ve taraftarlarını kiyamet günü zarara uğratmış olan kimselerdir.\" dediler. İyi bilin ki zalimler kalıcı bir azabın içindedirler.", transliteration: "Ve terahum yu'redune aleyha haşiineminez zulli yenzurune min tarfin hafiy, ve kalellezine amenu innel hasirinellezine hasiru enfusehum ve ehlihim yevmel kıyameh, e la innez zalimine fi azabin mukim."},
  {id: 4318, sure_no: 46, ayet_no: 46, sure_adi: "Şura", text: "وَمَا كَانَ لَهُمْ مِنْ اَوْلِيَٓاءَ يَنْصُرُونَهُمْ مِنْ دُونِ اللّٰهِۜ وَمَنْ يُضْلِلِ اللّٰهُ فَمَا لَهُ مِنْ سَب۪يلٍۜ", translation: "Onların, kendilerine yardım edecek Allah'tan başka velileri yoktur. Ve Allah kimi sapkınlıkta bırakırsa artık onun için bir kurtuluş yolu yoktur.", transliteration: "Ve ma kane lehum min evliyae yensurunehum min dunillah. Ve men yudlilillahu fe ma lehu min sebil."},
  {id: 4319, sure_no: 47, ayet_no: 47, sure_adi: "Şura", text: "اِسْتَج۪يبُوا لِرَبِّكُمْ مِنْ قَبْلِ اَنْ يَأْتِيَ يَوْمٌ لَا مَرَدَّ لَهُ مِنَ اللّٰهِۜ مَا لَكُمْ مِنْ مَلْجَاٍ يَوْمَئِذٍ وَمَا لَكُمْ مِنْ نَك۪يرٍ", translation: "Allah tarafından geri döndürülmeyecek olan günün gelmesinden önce, Rabb'inizin çağrısına olumlu yanıt verin. O Gün, sizin için bir sığınak yoktur. Sizin için kabul etmemek de yoktur.", transliteration: "İstecibu li rabbikum min kabli en ye'tiye yevmun la meredde lehu minallah, ma lekum min melcein yevme izin ve ma lekum min nekir."},
  {id: 4320, sure_no: 48, ayet_no: 48, sure_adi: "Şura", text: "فَاِنْ اَعْرَضُوا فَمَٓا اَرْسَلْنَاكَ عَلَيْهِمْ حَف۪يظاًۜ اِنْ عَلَيْكَ اِلَّا الْبَلَاغُۜ وَاِنَّٓا اِذَٓا اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً فَرِحَ بِهَاۜ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ اَيْد۪يهِمْ فَاِنَّ الْاِنْسَانَ كَفُورٌ", translation: "Buna rağmen eğer yüz çevirirlerse, Biz, seni onların üzerine bekçi olarak göndermedik. Senin üzerine düşen yalnızca çağrıda bulunmaktır. Biz, insana tarafımızdan bir rahmet tattırdığımız zaman, insan ona sevinir. Kendi yaptıklarından dolayı başına bir kötülük gelse, işte o zaman insan kafir kesilir.", transliteration: "Fe in a'redu fe ma erselnake aleyhim hafiza, in aleyke illel belagu, ve inna iza ezaknal insane minna rahmeten feriha biha, ve in tusibhum seyyietun bi ma kaddemet eydihim fe innel insane kefur."},
  {id: 4321, sure_no: 49, ayet_no: 49, sure_adi: "Şura", text: "لِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ يَخْلُقُ مَا يَشَٓاءُۜ يَهَبُ لِمَنْ يَشَٓاءُ اِنَاثاً وَيَهَبُ لِمَنْ يَشَٓاءُ الذُّكُورَۙ", translation: "Göklerin ve yeryüzünün egemenliği yalnız Allah'a aittir. O, dilediği şeyi yaratır. Dilediğine dişiler dilediğine de erkekler bahşeder.", transliteration: "Lillahi mulkus semavati vel ard, yahluku ma yeşau, yehebu li men yeşau inasen ve yehebu li men yeşauz zukur."},
  {id: 4322, sure_no: 50, ayet_no: 50, sure_adi: "Şura", text: "اَوْ يُزَوِّجُهُمْ ذُكْرَاناً وَاِنَاثاًۚ وَيَجْعَلُ مَنْ يَشَٓاءُ عَق۪يماًۜ اِنَّهُ عَل۪يمٌ قَد۪يرٌ", translation: "Veya hem erkekler hem dişiler; onları çift yapar. Ve dilediğini kısır yapar. Kuşkusuz O, Her Şeyi Bilen'dir, Her Şeye Gücü Yeten'dir.", transliteration: "Ev yuzevvicuhum zukranen ve inasa, ve yec'alu men yeşau akima, innehu alimun kadir."},
  {id: 4323, sure_no: 51, ayet_no: 51, sure_adi: "Şura", text: "وَمَا كَانَ لِبَشَرٍ اَنْ يُكَلِّمَهُ اللّٰهُ اِلَّا وَحْياً اَوْ مِنْ وَرَٓائِ۬ حِجَابٍ اَوْ يُرْسِلَ رَسُولاً فَيُوحِيَ بِـاِذْنِه۪ مَا يَشَٓاءُۜ اِنَّهُ عَلِيٌّ حَك۪يمٌ", translation: "Allah'ın, bir beşer ile konuşması söz konusu değildir. Ancak, vahiy ile veya bir perde arkasından veya bir resul göndererek, izni ile dilediğini vahyetmesi dışında. Kuşkusuz O, Çok Yüce'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve ma kane li beşerin en yukellimehullahu illa vahyen ev min verai hıcabin ev yursile resulen fe yuhıye bi iznihi ma yeşau, innehu aliyyun hakim."},
  {id: 4324, sure_no: 52, ayet_no: 52, sure_adi: "Şura", text: "وَكَذٰلِكَ اَوْحَيْنَٓا اِلَيْكَ رُوحاً مِنْ اَمْرِنَاۜ مَا كُنْتَ تَدْر۪ي مَا الْكِتَابُ وَلَا الْا۪يمَانُ وَلٰكِنْ جَعَلْنَاهُ نُوراً نَهْد۪ي بِه۪ مَنْ نَشَٓاءُ مِنْ عِبَادِنَاۜ وَاِنَّكَ لَتَهْد۪ٓي اِلٰى صِرَاطٍ مُسْتَق۪يمٍۙ", translation: "İşte böylece sana buyruğumuzdan bir ruh vahyettik. Sen kitap nedir, iman nedir bilmezdin. Fakat onu, kullarımızdan dilediğimiz kimseyi doğru yola ileteceğimiz bir ışık yaptık. Kuşkusuz sen, dosdoğru bir yola kılavuzluk etmektesin.", transliteration: "Ve kezalike evhayna ileyke ruhan min emrina, ma kunte tedri mel kitabu ve lel imanu ve lakin cealnahu nuren nehdi bihi men neşau min ibadina, ve inneke le tehdi ila sıratın mustekim."},
  {id: 4325, sure_no: 53, ayet_no: 53, sure_adi: "Şura", text: "صِرَاطِ اللّٰهِ الَّذ۪ي لَهُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ اَلَٓا اِلَى اللّٰهِ تَص۪يرُ الْاُمُورُ", translation: "Göklerde ve yerde olanların kendisine ait olduğu Allah'ın yoluna. İyi bilin ki bütün işler sonunda Allah'a döner.", transliteration: "Sıratıllahillezi lehu ma fis semavati ve ma fil ard, e la ilallahi tesirul umur."},
  {id: 4326, sure_no: 1, ayet_no: 1, sure_adi: "Zuhruf", text: "حٰمٓۜ", translation: "Ha, Mim.", transliteration: "Ha mim."},
  {id: 4327, sure_no: 2, ayet_no: 2, sure_adi: "Zuhruf", text: "وَالْكِتَابِ الْمُب۪ينِۙ", translation: "Apaçık Kitap'a ant olsun ki!", transliteration: "Vel kitabil mubini."},
  {id: 4328, sure_no: 3, ayet_no: 3, sure_adi: "Zuhruf", text: "اِنَّا جَعَلْنَاهُ قُرْءٰناً عَرَبِياًّ لَعَلَّكُمْ تَعْقِلُونَۚ", translation: "Biz onu düşünüp anlayasınız diye Arapça bir kuran yaptık.", transliteration: "İnna cealnahu kur'anen arabiyyen leallekum ta'kılun."},
  {id: 4329, sure_no: 4, ayet_no: 4, sure_adi: "Zuhruf", text: "وَاِنَّهُ ف۪ٓي اُمِّ الْكِتَابِ لَدَيْنَا لَعَلِيٌّ حَك۪يمٌۜ", translation: "Kur'an, nezdimizde Ummu'l Kitap'tadır. Gerçekten yücedir ve hakimdir.", transliteration: "Ve innehu fi ummil kitabi ledeyna le aliyyun hakim."},
  {id: 4330, sure_no: 5, ayet_no: 5, sure_adi: "Zuhruf", text: "اَفَنَضْرِبُ عَنْكُمُ الذِّكْرَ صَفْحاً اَنْ كُنْتُمْ قَوْماً مُسْرِف۪ينَ", translation: "Siz, israf eden bir halk oldunuz diye, size öğüt vermekten vaz mı geçelim?", transliteration: "E fe nadribu ankumuz zikre safhan en kuntum kavmen musrifin."},
  {id: 4331, sure_no: 6, ayet_no: 6, sure_adi: "Zuhruf", text: "وَكَمْ اَرْسَلْنَا مِنْ نَبِيٍّ فِي الْاَوَّل۪ينَ", translation: "Öncekiler için de nice Nebiler gönderdik.", transliteration: "Ve kem erselna min nebiyin fil evvelin."},
  {id: 4332, sure_no: 7, ayet_no: 7, sure_adi: "Zuhruf", text: "وَمَا يَأْت۪يهِمْ مِنْ نَبِيٍّ اِلَّا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Onlar, kendilerine gelen her Nebi'yi mutlaka alaya alıyorlardı.", transliteration: "Ve ma yetihim min nebiyin illa kanu bihi yestehziun."},
  {id: 4333, sure_no: 8, ayet_no: 8, sure_adi: "Zuhruf", text: "فَاَهْلَكْـنَٓا اَشَدَّ مِنْهُمْ بَطْشاً وَمَضٰى مَثَلُ الْاَوَّل۪ينَ", translation: "Biz, güç bakımından onlardan daha üstün olanları yok ettik. Geçmişe ait örnek haline geldiler.", transliteration: "Fe ehlekna eşedde minhum batşen ve meda meselul evvelin."},
  {id: 4334, sure_no: 9, ayet_no: 9, sure_adi: "Zuhruf", text: "وَلَئِنْ سَاَلْتَهُمْ مَنْ خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ الْعَز۪يزُ الْعَل۪يمُۙ", translation: "Müşriklere: \"Gökleri ve yeri kim yarattı?\" diye sorsan, kesinlikle: \"Onları Mutlak Üstün Olan, Her Şeyi Bilen yarattı.\" diyecekler.", transliteration: "Ve le in seeltehum men halakas semavati vel arda le yekulunne halakahunnel azizul alim."},
  {id: 4335, sure_no: 10, ayet_no: 10, sure_adi: "Zuhruf", text: "اَلَّذ۪ي جَعَلَ لَكُمُ الْاَرْضَ مَهْداً وَجَعَلَ لَكُمْ ف۪يهَا سُبُلاً لَعَلَّكُمْ تَهْتَدُونَۚ", translation: "O, yeryüzünü sizin için beşik yaptı. Orada sizin için yollar yaptı. Umulur ki hidayete erersiniz.", transliteration: "Ellezi cealekumul arda mehden ve cealelekum fiha subulen leallekum tehtedun."},
  {id: 4336, sure_no: 11, ayet_no: 11, sure_adi: "Zuhruf", text: "وَالَّذ۪ي نَزَّلَ مِنَ السَّمَٓاءِ مَٓاءً بِقَدَرٍۚ فَاَنْشَرْنَا بِه۪ بَلْدَةً مَيْتاًۚ كَذٰلِكَ تُخْرَجُونَ", translation: "O, bir ölçüye göre gökten su indirendir. Sonra, onunla ölü bir beldeyi canlandırdık. İşte siz de böyle yeniden diriltileceksiniz.", transliteration: "Vellezi nezzele mines semai maenbi kader, fe enşerna bihi beldetenmeyten, kezalike tuhrecun."},
  {id: 4337, sure_no: 12, ayet_no: 12, sure_adi: "Zuhruf", text: "وَالَّذ۪ي خَلَقَ الْاَزْوَاجَ كُلَّهَا وَجَعَلَ لَكُمْ مِنَ الْفُلْكِ وَالْاَنْعَامِ مَا تَرْكَبُونَۙ", translation: "Her şeyi çift yarattı. Gemilerden ve hayvanlardan binekler kıldı.", transliteration: "Vellezi halakal ezvace kullehave ceale lekum minel fulki vel enami ma terkebun."},
  {id: 4338, sure_no: 13, ayet_no: 13, sure_adi: "Zuhruf", text: "لِتَسْتَوُ۫ا عَلٰى ظُهُورِه۪ ثُمَّ تَذْكُرُوا نِعْمَةَ رَبِّكُمْ اِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا سُبْحَانَ الَّذ۪ي سَخَّرَ لَنَا هٰذَا وَمَا كُنَّا لَهُ مُقْرِن۪ينَۙ", translation: "Üzerlerine binip, onlardan yararlanınca, Rabb'inizin verdiği nimetleri anarak: \"Bunları, hizmetimize veren Allah ne yücedir; yoksa bizim bunlara gücümüz yetmezdi.\" deyin.", transliteration: "Li testevu ala zuhurihi summe tezkuru ni'mete rabbikum izesteveytum aleyhi, ve tekulu subhanellezi sehhare lena haza ve ma kunna lehu mukrinin."},
  {id: 4339, sure_no: 14, ayet_no: 14, sure_adi: "Zuhruf", text: "وَاِنَّٓا اِلٰى رَبِّنَا لَمُنْقَلِبُونَ", translation: "Kuşkusuz biz, sonunda Rabb'imize döneceğiz.", transliteration: "Ve inna ila rabbina le munkalibun."},
  {id: 4340, sure_no: 15, ayet_no: 15, sure_adi: "Zuhruf", text: "وَجَعَلُوا لَهُ مِنْ عِبَادِه۪ جُزْءاًۜ اِنَّ الْاِنْسَانَ لَكَفُورٌ مُب۪ينٌۜ", translation: "Bazı kullarını, O'nun bir parçası saydılar. Gerçekten, o insan, açıkça nankörlük etmektedir.", transliteration: "Ve cealu lehu min ibadihi cuz'a, innel insane le kefurun mubin."},
  {id: 4341, sure_no: 16, ayet_no: 16, sure_adi: "Zuhruf", text: "اَمِ اتَّخَذَ مِمَّا يَخْلُقُ بَنَاتٍ وَاَصْفٰيكُمْ بِالْبَن۪ينَ۟", translation: "Yoksa O, yarattıklarından kızları kendisine ayırıp, oğulları size mi bıraktı?", transliteration: "Emittehaze mimma yahluku benatin ve asfakum bil benin."},
  {id: 4342, sure_no: 17, ayet_no: 17, sure_adi: "Zuhruf", text: "وَاِذَا بُشِّرَ اَحَدُهُمْ بِمَا ضَرَبَ لِلرَّحْمٰنِ مَثَلاً ظَلَّ وَجْهُهُ مُسْوَداًّ وَهُوَ كَظ۪يمٌ", translation: "Onlardan biri, kendisine, Rahman'a layık gördüğü kız çocuğu haberi verildiği zaman, yüzü simsiyah kesilir, içini üzüntü kaplar.", transliteration: "Ve iza buşşire ehaduhum bi ma darabe lir rahmani meselen zalle vechuhu musvedden ve huve kezim."},
  {id: 4343, sure_no: 18, ayet_no: 18, sure_adi: "Zuhruf", text: "اَوَمَنْ يُنَشَّؤُ۬ا فِي الْحِلْيَةِ وَهُوَ فِي الْخِصَامِ غَيْرُ مُب۪ينٍ", translation: "Ve süs içinde büyütülmekten başka işe yaramayanı mı verdi diye hayıflanır!", transliteration: "E ve men yuneşşeu fil hılyeti ve huve fil hısami gayru mubin."},
  {id: 4344, sure_no: 19, ayet_no: 19, sure_adi: "Zuhruf", text: "وَجَعَلُوا الْمَلٰٓئِكَةَ الَّذ۪ينَ هُمْ عِبَادُ الرَّحْمٰنِ اِنَاثاًۜ اَشَهِدُوا خَلْقَهُمْۜ سَتُكْتَبُ شَهَادَتُهُمْ وَيُسْـَٔلُونَ", translation: "Onlar, Rahman'ın kulları olan melekleri dişi saydılar. Onların yaratılışlarına tanık mı oldular? Onların bu tanıklıkları yazılacak ve sorguya çekileceklerdir.", transliteration: "Ve cealul melaiketellezine hum ibadur rahmani inasa, e şehidu halkahum, setuktebu şehadetuhum ve yus'elun."},
  {id: 4345, sure_no: 20, ayet_no: 20, sure_adi: "Zuhruf", text: "وَقَالُوا لَوْ شَٓاءَ الرَّحْمٰنُ مَا عَبَدْنَاهُمْۜ مَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍۗ اِنْ هُمْ اِلَّا يَخْرُصُونَۜ", translation: "\"Eğer Rahman dileseydi, biz onlara kulluk etmezdik.\" dediler. Onların bu konuda hiçbir bilgileri yoktur. Onlar yalnızca saçmalıyorlar.", transliteration: "Ve kalu lev şaer rahmanu ma abednahum, ma lehum bi zalike min ilmin in hum illa yahrusun."},
  {id: 4346, sure_no: 21, ayet_no: 21, sure_adi: "Zuhruf", text: "اَمْ اٰتَيْنَاهُمْ كِتَاباً مِنْ قَبْلِه۪ فَهُمْ بِه۪ مُسْتَمْسِكُونَ", translation: "Yoksa ondan önce, onlara kitap verdik de onlar, ona mı dayanıyorlar?", transliteration: "Em ateynahum kitaben min kablihi fe hum bihi mustemsikun."},
  {id: 4347, sure_no: 22, ayet_no: 22, sure_adi: "Zuhruf", text: "بَلْ قَالُٓوا اِنَّا وَجَدْنَٓا اٰبَٓاءَنَا عَلٰٓى اُمَّةٍ وَاِنَّا عَلٰٓى اٰثَارِهِمْ مُهْتَدُونَ", translation: "Hayır! Dediler ki: \"Doğrusu, biz, atalarımızı bir ümmet üzerinde bulduk, biz de onların izinden gidiyoruz.", transliteration: "Bel kalu inna vecedna abaena ala ummetin ve inna ala asarihim muhtedun."},
  {id: 4348, sure_no: 23, ayet_no: 23, sure_adi: "Zuhruf", text: "وَكَذٰلِكَ مَٓا اَرْسَلْنَا مِنْ قَبْلِكَ ف۪ي قَرْيَةٍ مِنْ نَذ۪يرٍ اِلَّا قَالَ مُتْرَفُوهَٓاۙ اِنَّا وَجَدْنَٓا اٰبَٓاءَنَا عَلٰٓى اُمَّةٍ وَاِنَّا عَلٰٓى اٰثَارِهِمْ مُقْتَدُونَ", translation: "Tıpkı bunun gibi, senden önce de ne zaman bir beldeye uyarıcı gönderdiysek, oranın refah içinde olanları: \"Biz, atalarımızı bir ümmet üzerinde bulduk. Biz de kesinlikle onların izinden gidiyoruz.\" dediler.", transliteration: "Ve kezalike ma erselna min kablike fi karyetin min nezirin illa kale mutrefuha inna vecedna abaena ala ummetin ve inna ala asarihim muktedun."},
  {id: 4349, sure_no: 24, ayet_no: 24, sure_adi: "Zuhruf", text: "قَالَ اَوَلَوْ جِئْتُكُمْ بِاَهْدٰى مِمَّا وَجَدْتُمْ عَلَيْهِ اٰبَٓاءَكُمْۜ قَالُٓوا اِنَّا بِمَٓا اُرْسِلْتُمْ بِه۪ كَافِرُونَ", translation: "Size, \"Atalarınızı üzerinde bulduğunuz yoldan daha doğrusunu getirmiş olsam da mı?\" deyince, onlar: \"Biz, sizinle gönderilene kafirlik ediyoruz.\" dediler.", transliteration: "Kale e ve lev ci'tukum bi ehda mimma vecedtum aleyhi abaekum, kalu inna bi ma ursıltum bihi kafirun."},
  {id: 4350, sure_no: 25, ayet_no: 25, sure_adi: "Zuhruf", text: "فَانْتَقَمْنَا مِنْهُمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّب۪ينَ۟", translation: "Bunun üzerine onlara hak ettikleri cezayı verdik. Yalanlayanların sonlarının nasıl olduğuna bir bak!", transliteration: "Fentekamna minhum fanzur keyfe kane akıbetul mukezzibin."},
  {id: 4351, sure_no: 26, ayet_no: 26, sure_adi: "Zuhruf", text: "وَاِذْ قَالَ اِبْرٰه۪يمُ لِاَب۪يهِ وَقَوْمِه۪ٓ اِنَّن۪ي بَرَٓاءٌ مِمَّا تَعْبُدُونَۙ", translation: "Bir zamanlar İbrahim, babasına ve halkına: \"Ben sizin kulluk ettiklerinizden uzağım demişti.\"", transliteration: "Ve iz kale ibrahimu li ebihi ve kavmihi inneni beraun mimma ta'budun."},
  {id: 4352, sure_no: 27, ayet_no: 27, sure_adi: "Zuhruf", text: "اِلَّا الَّذ۪ي فَطَرَن۪ي فَاِنَّهُ سَيَهْد۪ينِ", translation: "\"Ancak fıtratımı belirleyen başka. Kuşkusuz O beni doğru yola iletecektir.\"", transliteration: "İllellezi fatarani fe innehu se yehdin."},
  {id: 4353, sure_no: 28, ayet_no: 28, sure_adi: "Zuhruf", text: "وَجَعَلَهَا كَلِمَةً بَاقِيَةً ف۪ي عَقِبِه۪ لَعَلَّهُمْ يَرْجِعُونَ", translation: "İbrahim, gerçeğe yönelmeleri umuduyla takdir edilmiş hükmü gelecek nesiller için de kalıcı bir ilke yaptı.", transliteration: "Ve cealeha kelimeten bakıyeten fi akıbihi leallehum yerciun."},
  {id: 4354, sure_no: 29, ayet_no: 29, sure_adi: "Zuhruf", text: "بَلْ مَتَّعْتُ هٰٓؤُ۬لَٓاءِ وَاٰبَٓاءَهُمْ حَتّٰى جَٓاءَهُمُ الْحَقُّ وَرَسُولٌ مُب۪ينٌ", translation: "Doğrusu bunları ve atalarını, kendilerine Hakk ve onu açıklayıcı bir Resul gelinceye kadar yararlandırıp yaşattım.", transliteration: "Bel metta'tu haulai ve abaehum hatta caehumul hakku ve resulun mubin."},
  {id: 4355, sure_no: 30, ayet_no: 30, sure_adi: "Zuhruf", text: "وَلَمَّا جَٓاءَهُمُ الْحَقُّ قَالُوا هٰذَا سِحْرٌ وَاِنَّا بِه۪ كَافِرُونَ", translation: "Onlara, Hakk geldiği zaman: \"Bu bir büyüdür. Biz onu yalanlayanlarız.\" dediler.", transliteration: "Ve lemma cae humul hakku kalu haza sihrun ve inna bihi kafirun."},
  {id: 4356, sure_no: 31, ayet_no: 31, sure_adi: "Zuhruf", text: "وَقَالُوا لَوْلَا نُزِّلَ هٰذَا الْقُرْاٰنُ عَلٰى رَجُلٍ مِنَ الْقَرْيَتَيْنِ عَظ۪يمٍ", translation: "\"Bu Kur'an'ın, iki şehrin birinden, bir büyük adama indirilmesi gerekmez miydi?\" dediler.", transliteration: "Ve kalu lev la nuzzile hazel kur'anu ala raculin minel karyeteyni azim."},
  {id: 4357, sure_no: 32, ayet_no: 32, sure_adi: "Zuhruf", text: "اَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَۜ نَحْنُ قَسَمْنَا بَيْنَهُمْ مَع۪يشَتَهُمْ فِي الْحَيٰوةِ الدُّنْيَا وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِيَتَّخِذَ بَعْضُهُمْ بَعْضاً سُخْرِياًّۜ وَرَحْمَتُ رَبِّكَ خَيْرٌ مِمَّا يَجْمَعُونَ", translation: "Rabb'inin rahmetini onlar mı dağıtıyorlar? Dünya hayatındaki geçimliklerini Biz paylaştırdık. Birbirlerine işlerini gördürsünler diye, onların bir kısmını bir kısmının üzerine derecelerle yükselttik. Rabb'inin rahmeti onların biriktirdiklerinden daha hayırlıdır.", transliteration: "E hum yaksimune rahmete rabbik, nahnu kasemna beynehum maişetehum fil hayatid dunyave refa'na ba'dahum fevka ba'dın derecatin li yettehıze ba'duhum ba'dan suhriyya, ve rahmetu rabbike hayrun mimma yecmaun."},
  {id: 4358, sure_no: 33, ayet_no: 33, sure_adi: "Zuhruf", text: "وَلَوْلَٓا اَنْ يَكُونَ النَّاسُ اُمَّةً وَاحِدَةً لَجَعَلْنَا لِمَنْ يَكْفُرُ بِالرَّحْمٰنِ لِبُيُوتِهِمْ سُقُفاً مِنْ فِضَّةٍ وَمَعَارِجَ عَلَيْهَا يَظْهَرُونَۙ", translation: "Şayet insanlar, tek bir ümmet haline gelmeyecek olsalardı, Rahman'ı küfreden kimselerin, evlerinin tavanlarını ve merdivenlerini gümüşten yapardık.", transliteration: "Ve lev la en yekunen nasu ummeten vahıdeten le cealna limen yekfuru bir rahmani li buyutihim sukufen min fıddatin ve mearice aleyha yazherune."},
  {id: 4359, sure_no: 34, ayet_no: 34, sure_adi: "Zuhruf", text: "وَلِبُيُوتِهِمْ اَبْوَاباً وَسُرُراً عَلَيْهَا يَتَّكِؤُ۫نَۙ", translation: "Ve evlerinin kapılarını ve üzerine oturup yaslandıkları koltuklarını da.", transliteration: "Ve li buyutihim ebvaben ve sururen aleyha yettekiun."},
  {id: 4360, sure_no: 35, ayet_no: 35, sure_adi: "Zuhruf", text: "وَزُخْرُفاًۜ وَاِنْ كُلُّ ذٰلِكَ لَمَّا مَتَاعُ الْحَيٰوةِ الدُّنْيَاۜ وَالْاٰخِرَةُ عِنْدَ رَبِّكَ لِلْمُتَّق۪ينَ۟", translation: "Ve altına boğardık. Bunların tamamı, dünya hayatının kazanımından başka bir şey değildir. Ahiret ise Rabb'inin yanında, yalnızca takva sahipleri içindir.", transliteration: "Ve zuhrufa, ve in kullu zalike lemma metaul hayatid dunya, vel ahiretu inde rabbike lil muttekin."},
  {id: 4361, sure_no: 36, ayet_no: 36, sure_adi: "Zuhruf", text: "وَمَنْ يَعْشُ عَنْ ذِكْرِ الرَّحْمٰنِ نُقَيِّضْ لَهُ شَيْطَاناً فَهُوَ لَهُ قَر۪ينٌ", translation: "Her kim Rahman'ın öğüdüne karşı duyarsız olursa, Biz ona bir şeytan salarız. Artık şeytan onun yakın arkadaşı olur.", transliteration: "Ve men ya'şu an zikrir rahmani nukayyıd lehu şeytanen fe huve lehu karin."},
  {id: 4362, sure_no: 37, ayet_no: 37, sure_adi: "Zuhruf", text: "وَاِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ السَّب۪يلِ وَيَحْسَبُونَ اَنَّهُمْ مُهْتَدُونَ", translation: "Şeytanlar, onları doğru yoldan saptırdıkları halde, onlar hala kendilerinin doğru yolda olduklarını sanırlar.", transliteration: "Ve innehum le yasuddunehum anis sebili ve yahsebune ennehum muhtedun."},
  {id: 4363, sure_no: 38, ayet_no: 38, sure_adi: "Zuhruf", text: "حَتّٰٓى اِذَا جَٓاءَنَا قَالَ يَا لَيْتَ بَيْن۪ي وَبَيْنَكَ بُعْدَ الْمَشْرِقَيْنِ فَبِئْسَ الْقَر۪ينُ", translation: "Nihayet o Bize geldiği zaman: \"Keşke seninle aramız iki doğu uzaklığı kadar uzak olsaydı.\" der. Öyleyse bu ne kötü bir arkadaşlıktır.", transliteration: "Hatta iza caena kale ya leyte beyni ve beyneke bu'del meşrikayni fe bi'sel karin."},
  {id: 4364, sure_no: 39, ayet_no: 39, sure_adi: "Zuhruf", text: "وَلَنْ يَنْفَعَكُمُ الْيَوْمَ اِذْ ظَلَمْتُمْ اَنَّكُمْ فِي الْعَذَابِ مُشْتَرِكُونَ", translation: "Bugün, pişmanlığınız kesinlikle size bir yarar sağlamaz. Kendinize haksızlık yaptınız. Siz azaba da ortaksınız.", transliteration: "Ve len yenfeakumul yevme iz zalemtum ennekum fil azabi muşterikun."},
  {id: 4365, sure_no: 40, ayet_no: 40, sure_adi: "Zuhruf", text: "اَفَاَنْتَ تُسْمِــعُ الصُّمَّ اَوْ تَهْدِي الْعُمْيَ وَمَنْ كَانَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "O halde sağıra sen mi işittireceksin? Veya köre ve apaçık sapkınlıkta olana doğru yolu gösterebilir misin?", transliteration: "E fe ente tusmius summe ev tehdil umye ve men kane fi dalalin mubin."},
  {id: 4366, sure_no: 41, ayet_no: 41, sure_adi: "Zuhruf", text: "فَاِمَّا نَذْهَبَنَّ بِكَ فَاِنَّا مِنْهُمْ مُنْتَقِمُونَۙ", translation: "Biz, seni bu dünyadan alıp götürsek bile, onlara hak ettikleri cezayı mutlaka vereceğiz.", transliteration: "Fe imma nezhebenne bike fe inna minhum muntekımun."},
  {id: 4367, sure_no: 42, ayet_no: 42, sure_adi: "Zuhruf", text: "اَوْ نُرِيَنَّكَ الَّذ۪ي وَعَدْنَاهُمْ فَاِنَّا عَلَيْهِمْ مُقْتَدِرُونَ", translation: "Veya onları uyardığımız azabı sana gösteririz. Elbette Biz'im onlara gücümüz yeter.", transliteration: "Ev nuriyennekellezi vaadnahum fe inna aleyhim muktedirun."},
  {id: 4368, sure_no: 43, ayet_no: 43, sure_adi: "Zuhruf", text: "فَاسْتَمْسِكْ بِالَّـذ۪ٓي اُو۫حِيَ اِلَيْكَۚ اِنَّكَ عَلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "Öyleyse sen, sana vahyedilene sarılmaya bak. Kuşkusuz sen dosdoğru bir yol üzerindesin.", transliteration: "Festemsik billezi uhıye ileyk, inneke ala sıratın mustekim."},
  {id: 4369, sure_no: 44, ayet_no: 44, sure_adi: "Zuhruf", text: "وَاِنَّهُ لَذِكْرٌ لَكَ وَلِقَوْمِكَۚ وَسَوْفَ تُسْـَٔلُونَ", translation: "Kuşkusuz o sana ve halkına bir öğüttür. Ondan sorulacaksınız.", transliteration: "Ve innehu le zikrun leke ve li kavmik, ve sevfe tus'elun."},
  {id: 4370, sure_no: 45, ayet_no: 45, sure_adi: "Zuhruf", text: "وَسْـَٔلْ مَنْ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رُسُلِنَاۗ اَجَعَلْنَا مِنْ دُونِ الرَّحْمٰنِ اٰلِهَةً يُعْبَدُونَ۟", translation: "Senden önce kendilerine Resul gönderdiklerimize sor. Biz, Rahman'dan başka kulluk edilecek ilahlar kılmış mıyız?", transliteration: "Ves'el men erselna min kablike min rusulina e cealna min dunir rahmani aliheten yu'bedun."},
  {id: 4371, sure_no: 46, ayet_no: 46, sure_adi: "Zuhruf", text: "وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيَاتِنَٓا اِلٰى فِرْعَوْنَ وَمَلَا۬ئِه۪ فَقَالَ اِنّ۪ي رَسُولُ رَبِّ الْعَالَم۪ينَ", translation: "Ant olsun ki Biz Musa'yı ayetlerimizle Firavun'a ve onun melelerine gönderdik: \"Ben alemlerin Rabb'inin Resul'üyüm.\" dedi.", transliteration: "Ve lekad erselna musa bi ayatina ila fir'avne ve melaihi fe kale inni resulu rabbil alemin."},
  {id: 4372, sure_no: 47, ayet_no: 47, sure_adi: "Zuhruf", text: "فَلَمَّا جَٓاءَهُمْ بِاٰيَاتِنَٓا اِذَا هُمْ مِنْهَا يَضْحَكُونَ", translation: "Fakat Musa onlara ayetlerimizle gelince, onlar hemen alay etmeye başladılar.", transliteration: "Fe lemma caehum bi ayatina izahum minha yadhakun."},
  {id: 4373, sure_no: 48, ayet_no: 48, sure_adi: "Zuhruf", text: "وَمَا نُر۪يهِمْ مِنْ اٰيَةٍ اِلَّا هِيَ اَكْبَرُ مِنْ اُخْتِهَاۘ وَاَخَذْنَاهُمْ بِالْعَذَابِ لَعَلَّهُمْ يَرْجِعُونَ", translation: "Onlara gösterdiğimiz her ayet, bir öncekinden daha büyüktü. Ders alırlar diye onlara kimi sıkıntılar yaşattık.", transliteration: "Ve ma nurihim min ayetin illa hiye ekberu min uhtiha ve ehaznahum bil azabi leallehum yerciun."},
  {id: 4374, sure_no: 49, ayet_no: 49, sure_adi: "Zuhruf", text: "وَقَالُوا يَٓا اَيُّهَ السَّاحِرُ ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَ اِنَّـنَا لَمُهْتَدُونَ", translation: "\"Ey sihirbaz! Rabb'inin sana verdiği sözün hatırına bizim için dua et. Kuşkusuz biz doğru yola uyarız.\" dediler.", transliteration: "Ve kalu ya eyyuhes sahırud'u lena rabbeke bima ahide ındeke innena le muhtedun."},
  {id: 4375, sure_no: 50, ayet_no: 50, sure_adi: "Zuhruf", text: "فَلَمَّا كَشَفْنَا عَنْهُمُ الْعَذَابَ اِذَا هُمْ يَنْكُثُونَ", translation: "Fakat onları azaptan kurtarınca da hemen sözlerinden döndüler.", transliteration: "Fe lemma keşefna an humul azabe iza hum yenkusun."},
  {id: 4376, sure_no: 51, ayet_no: 51, sure_adi: "Zuhruf", text: "وَنَادٰى فِرْعَوْنُ ف۪ي قَوْمِه۪ قَالَ يَا قَوْمِ اَلَيْسَ ل۪ي مُلْكُ مِصْرَ وَهٰذِهِ الْاَنْهَارُ تَجْر۪ي مِنْ تَحْت۪يۚ اَفَلَا تُبْصِرُونَۜ", translation: "Firavun, halkına seslendi: \"Ey halkım! Mısır'ın egemenliği ve ayaklarımın altından akıp giden şu nehirler benim değil mi? Görmüyor musunuz?\"", transliteration: "Ve nada fir'avnu fi kavmihi kale ya kavmi e leyse li mulku mısra ve hazihil enharu tecri min tahti, e fe la tubsirun."},
  {id: 4377, sure_no: 52, ayet_no: 52, sure_adi: "Zuhruf", text: "اَمْ اَنَا۬ خَيْرٌ مِنْ هٰذَا الَّذ۪ي هُوَ مَه۪ينٌ وَلَا يَكَادُ يُب۪ينُ", translation: "\"Yoksa ben, ne istediğini bilmeyen şu zavallıdan daha hayırlı değil miyim?\"", transliteration: "Em ene hayrun min hazellezi huve mehinun ve la yekadu yubin."},
  {id: 4378, sure_no: 53, ayet_no: 53, sure_adi: "Zuhruf", text: "فَلَوْلَٓا اُلْقِيَ عَلَيْهِ اَسْوِرَةٌ مِنْ ذَهَبٍ اَوْ جَٓاءَ مَعَهُ الْمَلٰٓئِكَةُ مُقْتَرِن۪ينَ", translation: "\"Öyleyse ona takılmış altından bilezikler olmalı veya yanında kendisine eşlik eden melekler gelmeli değil miydi?\"", transliteration: "Fe lev la ulkıye aleyhi esviretun min zehebin ev cae meahul melaiketu mukterinin."},
  {id: 4379, sure_no: 54, ayet_no: 54, sure_adi: "Zuhruf", text: "فَاسْتَخَفَّ قَوْمَهُ فَاَطَاعُوهُۜ اِنَّهُمْ كَانُوا قَوْماً فَاسِق۪ينَ", translation: "Firavun halkını etkisi altına aldı. Bunun üzerine halkı ona itaat etti. Onlar fasık bir toplum oldular.", transliteration: "Festehaffe kavmehu fe atauh, innehum kanu kavmen fasikin."},
  {id: 4380, sure_no: 55, ayet_no: 55, sure_adi: "Zuhruf", text: "فَلَمَّٓا اٰسَفُونَا انْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنَاهُمْ اَجْمَع۪ينَۙ", translation: "Böylece Bize asilik yaptılar. Biz de onları cezalandırdık. Topluca suda boğduk.", transliteration: "Fe lemma asefunentekamna minhum fe agraknahum ecmain."},
  {id: 4381, sure_no: 56, ayet_no: 56, sure_adi: "Zuhruf", text: "فَجَعَلْنَاهُمْ سَلَفاً وَمَثَلاً لِلْاٰخِر۪ينَ۟", translation: "Böylece onları gelecek nesiller için ibret verici bir örnek kıldık.", transliteration: "Fe cealnahum selefen ve meselen lil ahırin."},
  {id: 4382, sure_no: 57, ayet_no: 57, sure_adi: "Zuhruf", text: "وَلَمَّا ضُرِبَ ابْنُ مَرْيَمَ مَثَلاً اِذَا قَوْمُكَ مِنْهُ يَصِدُّونَ", translation: "Meryem Oğlu örnek olarak anlatılınca, halkın hemen yaygara yaptı;", transliteration: "Ve lemma duribebnu meryeme meselen iza kavmuke minhu yasıddun."},
  {id: 4383, sure_no: 58, ayet_no: 58, sure_adi: "Zuhruf", text: "وَقَالُٓوا ءَاٰلِهَتُنَا خَيْرٌ اَمْ هُوَۜ مَا ضَرَبُوهُ لَكَ اِلَّا جَدَلاًۜ بَلْ هُمْ قَوْمٌ خَصِمُونَ", translation: "Ve: \"Bizim ilahlarımız mı daha hayırlıdır, yoksa o mu?\" dediler. Bu örneği, yalnızca sana muhalefet etmek için verdiler. Doğrusu onlar çok düşmanca davranan bir halktır.", transliteration: "Ve kalu e alihetuna hayrun em huve, ma darebuhu leke illa cedela, bel hum kavmun hasımun."},
  {id: 4384, sure_no: 59, ayet_no: 59, sure_adi: "Zuhruf", text: "اِنْ هُوَ اِلَّا عَبْدٌ اَنْعَمْنَا عَلَيْهِ وَجَعَلْنَاهُ مَثَلاً لِبَن۪ٓي اِسْرَٓائ۪ـلَۜ", translation: "kendisine nimet verdiğimiz ve İsrailoğulları'na örnek kıldığımız bir kuldur.", transliteration: "İn huve illa abdun en'amna aleyhi ve cealnahu meselen li beni israil."},
  {id: 4385, sure_no: 60, ayet_no: 60, sure_adi: "Zuhruf", text: "وَلَوْ نَشَٓاءُ لَجَعَلْنَا مِنْكُمْ مَلٰٓئِكَةً فِي الْاَرْضِ يَخْلُفُونَ", translation: "Eğer isteseydik sizi yeryüzünde birbirinizin ardı sıra gelen melekler yapardık.", transliteration: "Ve lev neşau le cealna minkum melaiketen fil ardı yahlufun."},
  {id: 4386, sure_no: 61, ayet_no: 61, sure_adi: "Zuhruf", text: "وَاِنَّهُ لَعِلْمٌ لِلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَاتَّبِعُونِۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ", translation: "Kuşkusuz o, kesinlikle o Sa'at için bir bilgidir. Sakın ondan kuşku duymayın! Bana uyun. Bu, dosdoğru yoldur.", transliteration: "Ve innehu le ilmun lis saati, fe la temterunne biha vettebiuni, haza sıratun mustekim."},
  {id: 4387, sure_no: 62, ayet_no: 62, sure_adi: "Zuhruf", text: "وَلَا يَصُدَّنَّكُمُ الشَّيْطَانُۚ اِنَّهُ لَكُمْ عَدُوٌّ مُب۪ينٌ", translation: "Sakın şeytan sizi alıkoymasın. Kuşkusuz o, sizin için apaçık bir düşmandır.", transliteration: "Ve la yasuddennekumuş şeytan, innehu lekum aduvvun mubin."},
  {id: 4388, sure_no: 63, ayet_no: 63, sure_adi: "Zuhruf", text: "وَلَمَّا جَٓاءَ ع۪يسٰى بِالْبَيِّنَاتِ قَالَ قَدْ جِئْتُكُمْ بِالْحِكْمَةِ وَلِاُبَيِّنَ لَكُمْ بَعْضَ الَّذ۪ي تَخْتَلِفُونَ ف۪يهِۚ فَاتَّقُوا اللّٰهَ وَاَط۪يعُونِ", translation: "İsa, apaçık beyyinelerle geldiği zaman: \"Ben size Hikmet'i getirdim. Hakkında anlaşmazlığa düştüğünüz şeylerin bir kısmını size açıklamak için geldim. O halde Allah için takvalı olun ve bana itaat edin.\" dedi.", transliteration: "Ve lemma cae isa bil beyyinati kale kad ci'tukum bil hikmeti ve li ubeyyine lekum ba'dellezi tahtelifune fih, fettekullahe ve etiuni."},
  {id: 4389, sure_no: 64, ayet_no: 64, sure_adi: "Zuhruf", text: "اِنَّ اللّٰهَ هُوَ رَبّ۪ي وَرَبُّكُمْ فَاعْبُدُوهُۜ هٰذَا صِرَاطٌ مُسْتَق۪يمٌ", translation: "Allah, benim de sizin de Rabb'inizdir. Öyleyse O'na kulluk edin! Bu, dosdoğru yoldur.", transliteration: "İnnellahe huve rabbi ve rabbukum fa'buduh, haza sıratun mustekim."},
  {id: 4390, sure_no: 65, ayet_no: 65, sure_adi: "Zuhruf", text: "فَاخْتَلَفَ الْاَحْزَابُ مِنْ بَيْنِهِمْۚ فَوَيْلٌ لِلَّذ۪ينَ ظَلَمُوا مِنْ عَذَابِ يَوْمٍ اَل۪يمٍ", translation: "Sonra gruplar kendi aralarında anlaşmazlığa düştüler. Artık acı veren günün azabından dolayı haksızlık yapanların vay haline!", transliteration: "Fahtelefel ahzabu min beynihim, fe veylun lillezine zalemu min azabi yevmin elim."},
  {id: 4391, sure_no: 66, ayet_no: 66, sure_adi: "Zuhruf", text: "هَلْ يَنْظُرُونَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ", translation: "O Sa'at'ın, farkında değillerken, ansızın onlara gelmesinden başka bir şey mi bekliyorlar?", transliteration: "Hel yenzurune illes saate en te'tiyehum bagteten ve hum la yeş'urun."},
  {id: 4392, sure_no: 67, ayet_no: 67, sure_adi: "Zuhruf", text: "اَلْاَخِلَّٓاءُ يَوْمَئِذٍ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ اِلَّا الْمُتَّق۪ينَۜ‌۟", translation: "O Gün, muttakiler dışında, dostlar birbirlerine düşmandırlar.", transliteration: "El ehillau yevme izin ba'duhum li ba'din aduvvun illel muttekin."},
  {id: 4393, sure_no: 68, ayet_no: 68, sure_adi: "Zuhruf", text: "يَا عِبَادِ لَا خَوْفٌ عَلَيْكُمُ الْيَوْمَ وَلَٓا اَنْتُمْ تَحْزَنُونَۚ", translation: "\"Ey kullarım! Bugün size korku yoktur. Siz üzülmeyeceksiniz.\"", transliteration: "Ya ibadi la havfun aleykumul yevme ve la entum tahzenun."},
  {id: 4394, sure_no: 69, ayet_no: 69, sure_adi: "Zuhruf", text: "اَلَّذ۪ينَ اٰمَنُوا بِاٰيَاتِنَا وَكَانُوا مُسْلِم۪ينَۚ", translation: "\"Ayetlerimize iman edenler ve teslim olanlarsınız.\"", transliteration: "Ellezine amenu bi ayatina ve kanu muslimin."},
  {id: 4395, sure_no: 70, ayet_no: 70, sure_adi: "Zuhruf", text: "اُدْخُلُوا الْجَنَّةَ اَنْتُمْ وَاَزْوَاجُكُمْ تُحْبَرُونَ", translation: "Cennete girin. Siz ve eşleriniz en iyi şekilde ağırlanacaksınız.\"", transliteration: "Udhulul cennete entum ve ezvacukum tuhberun ."},
  {id: 4396, sure_no: 71, ayet_no: 71, sure_adi: "Zuhruf", text: "يُطَافُ عَلَيْهِمْ بِصِحَافٍ مِنْ ذَهَبٍ وَاَكْوَابٍۚ وَف۪يهَا مَا تَشْتَه۪يهِ الْاَنْفُسُ وَتَلَذُّ الْاَعْيُنُۚ وَاَنْتُمْ ف۪يهَا خَالِدُونَۚ", translation: "Onların etraflarında altından tepsiler ve bardaklarla dolaşılır. Orada, canların çektiği, gözlerin hoşlandığı her şey vardır. Ve siz orada sürekli kalacaksınız.", transliteration: "Yutafu aleyhim bi sıhafin min zehebin ve ekvab, ve fiha ma teştehihil enfusu ve telezzul a'yun, ve entum fiha halidun."},
  {id: 4397, sure_no: 72, ayet_no: 72, sure_adi: "Zuhruf", text: "وَتِلْكَ الْجَنَّةُ الَّت۪ٓي اُو۫رِثْتُمُوهَا بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "İşte bu, yaptıklarınıza karşılık, mirasçı kılındığınız Cennet'tir.", transliteration: "Ve tilkel cennetulleti uristumuha bi ma kuntum ta'melun."},
  {id: 4398, sure_no: 73, ayet_no: 73, sure_adi: "Zuhruf", text: "لَكُمْ ف۪يهَا فَاكِهَةٌ  كَث۪يرَةٌ مِنْهَا تَأْكُلُونَ", translation: "Sizin için orada yiyeceğiniz pek çok meyve vardır.", transliteration: "Lekum fiha fakihetun kesiretun minha te'kulun."},
  {id: 4399, sure_no: 74, ayet_no: 74, sure_adi: "Zuhruf", text: "اِنَّ الْمُجْرِم۪ينَ ف۪ي عَذَابِ جَهَنَّمَ خَالِدُونَۚ", translation: "Kuşkusuz mücrimler, Cehennem azabında sürekli kalacak olanlardır.", transliteration: "İnnel mucrimine fi azabi cehenneme halidun."},
  {id: 4400, sure_no: 75, ayet_no: 75, sure_adi: "Zuhruf", text: "لَا يُفَتَّرُ عَنْهُمْ وَهُمْ ف۪يهِ مُبْلِسُونَۚ", translation: "Onlardan azap hafifletilmez. Onlar, orada umutlarını kesmiş olanlardır.", transliteration: "La yufetteru anhum ve hum fihi mublisun."},
  {id: 4401, sure_no: 76, ayet_no: 76, sure_adi: "Zuhruf", text: "وَمَا ظَلَمْنَاهُمْ وَلٰكِنْ كَانُوا هُمُ الظَّالِم۪ينَ", translation: "Biz onlara haksızlık yapmadık. Fakat onlar kendi kendilerine haksızlık yaptılar.", transliteration: "Ve ma zalemnahum ve lakin kanu humuz zalimin."},
  {id: 4402, sure_no: 77, ayet_no: 77, sure_adi: "Zuhruf", text: "وَنَادَوْا يَا مَالِكُ لِيَقْضِ عَلَيْنَا رَبُّكَۜ قَالَ اِنَّكُمْ مَاكِثُونَ", translation: "\"Ey malik! Rabb'in bizim aleyhimize hüküm versin.\" diye seslenirler. \"Siz böyle kalacaksınız.\" dedi.", transliteration: "Ve nadev ya maliku li yakdi aleyna rabbuk, kale innekum makisun."},
  {id: 4403, sure_no: 78, ayet_no: 78, sure_adi: "Zuhruf", text: "لَقَدْ جِئْنَاكُمْ بِالْحَقِّ وَلٰكِنَّ اَكْثَرَكُمْ لِلْحَقِّ كَارِهُونَ", translation: "Ant olsun ki size Hakk'ı getirdik. Ancak çoğunuz Hakk'tan hoşlanmadınız.", transliteration: "Lekad ci'nakum bil hakkı ve lakinne ekserekum lil hakkı karihun."},
  {id: 4404, sure_no: 79, ayet_no: 79, sure_adi: "Zuhruf", text: "اَمْ اَبْرَمُٓوا اَمْراً فَاِنَّا مُبْرِمُونَۚ", translation: "Yoksa onlar kesin karar mı verdiler? Biz de kesin kararlıyız!", transliteration: "Em ebremu emren fe inna mubrimun."},
  {id: 4405, sure_no: 80, ayet_no: 80, sure_adi: "Zuhruf", text: "اَمْ يَحْسَبُونَ اَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوٰيهُمْۜ  بَلٰى وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُونَ", translation: "Yoksa onların sırlarını ve fısıltılarını duymadığımızı mı sanıyorlar? Hayır! Onların yanlarında bulunan elçilerimiz her şeyi kaydediyorlar.", transliteration: "Em yahsebune enna la nesmeu sırrehum ve necvahum, bela ve rusuluna ledeyhim yektubun."},
  {id: 4406, sure_no: 81, ayet_no: 81, sure_adi: "Zuhruf", text: "قُلْ اِنْ كَانَ لِلرَّحْمٰنِ وَلَدٌۗ فَاَنَا۬ اَوَّلُ الْعَابِد۪ينَ", translation: "De ki: \"Eğer Rahman'ın çocuğu olsaydı, ona ilk ben kulluk ederdim.\"", transliteration: "Kul in kane lir rahmani veledun fe ena evvelul abidin."},
  {id: 4407, sure_no: 82, ayet_no: 82, sure_adi: "Zuhruf", text: "سُبْحَانَ رَبِّ السَّمٰوَاتِ وَالْاَرْضِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ", translation: "Göklerin ve yerin Rabb'i; arşın Rabb'i onların niteledikleri şeylerden münezzehtir.", transliteration: "Subhane rabbis semavati vel ardı rabbil arşi amma yasıfun."},
  {id: 4408, sure_no: 83, ayet_no: 83, sure_adi: "Zuhruf", text: "فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتّٰى يُلَاقُوا يَوْمَهُمُ الَّذ۪ي يُوعَدُونَ", translation: "Artık onları kendi hallerine bırak! Uyarıldıkları güne kavuşuncaya değin boş şeylerle oyalanıp dursunlar.", transliteration: "Fe zerhum yahudu ve yel'abu hatta yulaku yevme humullezi yu'adun."},
  {id: 4409, sure_no: 84, ayet_no: 84, sure_adi: "Zuhruf", text: "وَهُوَ الَّذ۪ي فِي السَّمَٓاءِ اِلٰهٌ وَفِي الْاَرْضِ اِلٰهٌۜ وَهُوَ الْحَك۪يمُ الْعَل۪يمُ", translation: "Gökte de yerde de İlah olan O'dur. Ve O, En İyi Hüküm Veren'dir, Her Şeyi Bilen'dir.", transliteration: "Ve huvellezi fis semai ilahun ve fil ardı ilah, ve huvel hakimul alim."},
  {id: 4410, sure_no: 85, ayet_no: 85, sure_adi: "Zuhruf", text: "وَتَبَارَكَ الَّذ۪ي لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۚ وَعِنْدَهُ عِلْمُ السَّاعَةِۚ وَاِلَيْهِ تُرْجَعُونَ", translation: "Göklerin, yerin ve aralarındaki her şeyin mülkiyeti kendisinin olan Zat ne kutludur. O Sa'at'in bilgisi yalnızca O'nun yanındadır. O'na döndürüleceksiniz.", transliteration: "Ve tebarekellezi lehu mulkus semavati vel'ardı ve ma beynehuma, ve indehu ilmus saah, ve ileyhi turceun."},
  {id: 4411, sure_no: 86, ayet_no: 86, sure_adi: "Zuhruf", text: "وَلَا يَمْلِكُ الَّذ۪ينَ يَدْعُونَ مِنْ دُونِهِ الشَّفَاعَةَ اِلَّا مَنْ شَهِدَ بِالْحَقِّ وَهُمْ يَعْلَمُونَ", translation: "Onların, O'nun yanı sıra dua ettikleri kimseler şefaate güç yetiremezler. Bunu ancak gerçeğe tanıklık edenler kavrar.", transliteration: "Ve la yemlikullezine yed'une min dunihiş şefate illa men şehide bil hakkı ve hum ya'lemun."},
  {id: 4412, sure_no: 87, ayet_no: 87, sure_adi: "Zuhruf", text: "وَلَئِنْ سَاَلْتَهُمْ مَنْ خَلَقَهُمْ لَيَقُولُنَّ اللّٰهُ فَاَنّٰى يُؤْفَكُونَۙ", translation: "Onlara, kendilerini kimin yarattığını sorsan, kesinlikle \"Allah\" diyeceklerdir. Buna rağmen nasıl başka ilahlara yöneliyorlar.", transliteration: "Ve le in se'eltehum men halakahum le yekulunnallahu fe enna yu'fekun."},
  {id: 4413, sure_no: 88, ayet_no: 88, sure_adi: "Zuhruf", text: "وَق۪يلِه۪ يَا رَبِّ اِنَّ هٰٓؤُ۬لَٓاءِ قَوْمٌ لَا يُؤْمِنُونَۢ", translation: "Nebi'nin sözü: \"Ey Rabb'im! Bunlar, inanmayan bir halktır.\"", transliteration: "Ve kilihi ya rabbi inne haulai kavmun la yu'minun."},
  {id: 4414, sure_no: 89, ayet_no: 89, sure_adi: "Zuhruf", text: "فَاصْفَحْ عَنْهُمْ وَقُلْ سَلَامٌۜ فَسَوْفَ يَعْلَمُونَ", translation: "Onları kendi hallerine bırak: \"Selam olsun.\" de. Yakında gerçeği görecekler.", transliteration: "Fasfah anhum ve kul selam, fe sevfe ya'lemun."},
  {id: 4415, sure_no: 1, ayet_no: 1, sure_adi: "Duhan", text: "حٰمٓۜ", translation: "Ha, Mim.", transliteration: "Ha mim."},
  {id: 4416, sure_no: 2, ayet_no: 2, sure_adi: "Duhan", text: "وَالْكِتَابِ الْمُب۪ينِۙ", translation: "Apaçık olan Kitap'a ant olsun.", transliteration: "Vel kitabil mubin."},
  {id: 4417, sure_no: 3, ayet_no: 3, sure_adi: "Duhan", text: "اِنَّٓا اَنْزَلْنَاهُ ف۪ي لَيْلَةٍ مُبَارَكَةٍ اِنَّا كُنَّا مُنْذِر۪ينَ", translation: "Onu kutlu bir gecede indirdik. Kuşkusuz Biz, uyaranlarız.", transliteration: "İnna enzelnahu fi leyletin mubareketin inna kunna munzirin."},
  {id: 4418, sure_no: 4, ayet_no: 4, sure_adi: "Duhan", text: "ف۪يهَا يُفْرَقُ كُـلُّ اَمْرٍ حَـك۪يمٍۜ", translation: "Kararlaştırılmış işlerin hepsi, onda ayırt edilir.", transliteration: "Fiha yufreku kullu emrin hakim."},
  {id: 4419, sure_no: 5, ayet_no: 5, sure_adi: "Duhan", text: "اَمْراً مِنْ عِنْدِنَاۜ اِنَّا كُنَّا مُرْسِل۪ينَۚ", translation: "Katımızdan bir buyruk olarak. Kuşkusuz Biz Resul göndericiyiz.", transliteration: "Emren min indina inna kunna mursilin."},
  {id: 4420, sure_no: 6, ayet_no: 6, sure_adi: "Duhan", text: "رَحْمَةً مِنْ رَبِّكَۜ اِنَّهُ هُوَ السَّم۪يعُ الْعَل۪يمُۙ", translation: "Rabb'inden bir rahmet olarak. Kuşkusuz O, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Rahmeten min rabbik, innehu huves semiul alim."},
  {id: 4421, sure_no: 7, ayet_no: 7, sure_adi: "Duhan", text: "رَبِّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۢ اِنْ كُنْتُمْ مُوقِن۪ينَ", translation: "Eğer kesin olarak iman ediyorsanız bilin ki O göklerin, yerin ve ikisinin arasındakilerin Rabb'idir.", transliteration: "Rabbis semavati vel ardı ve ma beynehuma, in kuntum mukinin."},
  {id: 4422, sure_no: 8, ayet_no: 8, sure_adi: "Duhan", text: "لَٓا اِلٰهَ اِلَّا هُوَ يُحْـي۪ وَيُم۪يتُۜ رَبُّكُمْ وَرَبُّ اٰبَٓائِكُمُ الْاَوَّل۪ينَ", translation: "Ondan başka ilah yoktur. Diriltir ve öldürür. Sizin ve sizden önceki atalarınızın Rabb'idir.", transliteration: "La ilahe illa huve yuhyi ve yumit, rabbukumve rabbu abaikumul evvelin."},
  {id: 4423, sure_no: 9, ayet_no: 9, sure_adi: "Duhan", text: "بَلْ هُمْ ف۪ي شَكٍّ يَلْعَبُونَ", translation: "Hayır! Onlar, kuşku içinde eğlenip duruyorlar.", transliteration: "Bel hum fi şekkin yel'abun."},
  {id: 4424, sure_no: 10, ayet_no: 10, sure_adi: "Duhan", text: "فَارْتَقِبْ يَوْمَ تَأْتِي السَّمَٓاءُ بِدُخَانٍ مُب۪ينٍۙ", translation: "Artık göğün, apaçık bir duman getireceği günü bekle.", transliteration: "Fertekib yevme te'tis semau bi duhanin mubin."},
  {id: 4425, sure_no: 11, ayet_no: 11, sure_adi: "Duhan", text: "يَغْشَى النَّاسَۜ هٰذَا عَذَابٌ اَل۪يمٌ", translation: "İnsanları sarıp kuşatacak. İşte bu can yakıcı bir azaptır.", transliteration: "Yagşan nas, haza azabun elim."},
  {id: 4426, sure_no: 12, ayet_no: 12, sure_adi: "Duhan", text: "رَبَّـنَا اكْشِفْ عَنَّا الْعَذَابَ اِنَّا مُؤْمِنُونَ", translation: "\"Rabb'imiz! Azabı bizden kaldır. Kuşkusuz biz iman edenleriz.\"", transliteration: "Rabbenekşif annel azabe inna mu'minun."},
  {id: 4427, sure_no: 13, ayet_no: 13, sure_adi: "Duhan", text: "اَنّٰى لَهُمُ الذِّكْرٰى وَقَدْ جَٓاءَهُمْ رَسُولٌ مُب۪ينٌۙ", translation: "Nerede öğüt almak! Oysaki onlara apaçık bir Resul gelmişti.", transliteration: "Enna lehumuz zikra ve kad caehum resulun mubin."},
  {id: 4428, sure_no: 14, ayet_no: 14, sure_adi: "Duhan", text: "ثُمَّ تَوَلَّوْا عَنْهُ وَقَالُوا مُعَلَّمٌ مَجْنُونٌۢ", translation: "\"Öğretilmiş bir deli.\" dediler. Ve ondan yüz çevirdiler.", transliteration: "Summe tevellev anhu ve kalu muallemun mecnun."},
  {id: 4429, sure_no: 15, ayet_no: 15, sure_adi: "Duhan", text: "اِنَّا كَاشِفُوا الْعَذَابِ قَل۪يلاً اِنَّكُمْ عَٓائِدُونَۢ", translation: "Biz, azabı biraz kaldırsak, siz kesinlikle dönersiniz!", transliteration: "İnna kaşiful azabi kalilen innekum aidun."},
  {id: 4430, sure_no: 16, ayet_no: 16, sure_adi: "Duhan", text: "يَوْمَ نَبْطِشُ الْبَطْشَةَ الْكُبْرٰىۚ اِنَّا مُنْتَقِمُونَ", translation: "En büyük yakalayışla yakalayacağımız gün, kuşkusuz Biz, mutlaka hesap görenleriz.", transliteration: "Yevme nebtışul batşetel kubra inna muntekimun."},
  {id: 4431, sure_no: 17, ayet_no: 17, sure_adi: "Duhan", text: "وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَٓاءَهُمْ رَسُولٌ كَر۪يمٌۙ", translation: "Ant olsun Biz, onlardan önce Firavun halkını fitnelendirdik. Onlara çok şerefli bir Resul gelmişti.", transliteration: "Ve lekad fetenna kablehum kavme fir'avne ve caehum resulun kerim."},
  {id: 4432, sure_no: 18, ayet_no: 18, sure_adi: "Duhan", text: "اَنْ اَدُّٓوا اِلَيَّ عِبَادَ اللّٰهِۜ اِنّ۪ي لَكُمْ رَسُولٌ اَم۪ينٌۙ", translation: "\"Allah'ın kullarını bana verin. Ben sizin için güvenilir bir Resul'üm.\"", transliteration: "En eddu ileyye ibadallah, inni lekum resulun emin."},
  {id: 4433, sure_no: 19, ayet_no: 19, sure_adi: "Duhan", text: "وَاَنْ لَا تَعْلُوا عَلَى اللّٰهِۚ اِنّ۪ٓي اٰت۪يكُمْ بِسُلْطَانٍ مُب۪ينٍۚ", translation: "\"Allah'a karşı ululuk taslamayın. Kuşkusuz ben, size açıkça görevlendirilmiş biri olarak geliyorum.\"", transliteration: "Ve en la ta'lu alallah, inniatikum bi sultanin mubin."},
  {id: 4434, sure_no: 20, ayet_no: 20, sure_adi: "Duhan", text: "وَاِنّ۪ي عُذْتُ بِرَبّ۪ي وَرَبِّكُمْ اَنْ تَرْجُمُونِۘ", translation: "\"Ben, beni taşlamanızdan, sizin de Rabb'iniz olan Rabb'ime sığındım.\"", transliteration: "Ve inni uztu bi rabbi ve rabbikumen tercumuni."},
  {id: 4435, sure_no: 21, ayet_no: 21, sure_adi: "Duhan", text: "وَاِنْ لَمْ تُؤْمِنُوا ل۪ي فَاعْتَزِلُونِ", translation: "\"Eğer bana inanmıyorsanız, artık yolumdan çekilin.\"", transliteration: "Ve in lem tu'minu li fa'teziluni."},
  {id: 4436, sure_no: 22, ayet_no: 22, sure_adi: "Duhan", text: "فَدَعَا رَبَّهُٓ  اَنَّ هٰٓؤُ۬لَٓاءِ قَوْمٌ مُجْرِمُونَ", translation: "\"Bunlar, suç işleyen bir toplumdur.\" diye Rabb'ine yakardı.", transliteration: "Fe dea rabbehu enne haulai kavmun mucrimun."},
  {id: 4437, sure_no: 23, ayet_no: 23, sure_adi: "Duhan", text: "فَاَسْرِ بِعِبَاد۪ي لَيْلاً اِنَّكُمْ مُتَّبَعُونَۙ", translation: "\"Haydi, kullarımla geceleyin yola çık. Siz takip edileceksiniz!\"", transliteration: "Fe esri bi ibadi leylen innekum muttebeun."},
  {id: 4438, sure_no: 24, ayet_no: 24, sure_adi: "Duhan", text: "وَاتْرُكِ الْبَحْرَ رَهْواًۜ اِنَّهُمْ جُنْدٌ مُغْرَقُونَ", translation: "\"Denizi olduğu hal üzere bırak. Onlar, boğulacak olan bir ordudur.\"", transliteration: "Vetrukil bahre rehva, innehum cundun mugrekun."},
  {id: 4439, sure_no: 25, ayet_no: 25, sure_adi: "Duhan", text: "كَمْ تَرَكُوا مِنْ جَنَّاتٍ وَعُيُونٍۙ", translation: "Onlar, bahçelerden, pınarlardan nicelerini geride bıraktılar.", transliteration: "Kem tereku min cennatin ve uyun."},
  {id: 4440, sure_no: 26, ayet_no: 26, sure_adi: "Duhan", text: "وَزُرُوعٍ وَمَقَامٍ كَر۪يمٍۙ", translation: "Ve ekinler, itibarlı makamlar.", transliteration: "Ve zuruin ve makamin kerim."},
  {id: 4441, sure_no: 27, ayet_no: 27, sure_adi: "Duhan", text: "وَنَعْمَةٍ كَانُوا ف۪يهَا فَاكِه۪ينَۙ", translation: "Ve sefa sürdükleri nice nimetler.", transliteration: "Ve na'metin kanu fiha fakihin."},
  {id: 4442, sure_no: 28, ayet_no: 28, sure_adi: "Duhan", text: "كَذٰلِكَ۠ وَاَوْرَثْنَاهَا قَوْماً اٰخَر۪ينَ", translation: "Böylece, sonraki halkı onlara mirasçı kıldık.", transliteration: "Kezalik, ve evresnaha kavmen aharin."},
  {id: 4443, sure_no: 29, ayet_no: 29, sure_adi: "Duhan", text: "فَمَا بَكَتْ عَلَيْهِمُ السَّمَٓاءُ وَالْاَرْضُ وَمَا كَانُوا مُنْظَر۪ينَ۟", translation: "Onlara ne gök ağladı ne de yer. Ve onlara fırsat da verilmedi.", transliteration: "Fe ma beket aleyhimus semau vel ardu ve ma kanu munzarin."},
  {id: 4444, sure_no: 30, ayet_no: 30, sure_adi: "Duhan", text: "وَلَقَدْ نَجَّيْنَا بَن۪ٓي اِسْرَٓائ۪لَ مِنَ الْعَذَابِ الْمُه۪ينِۙ", translation: "Ant olsun ki İsrailoğulları'nı o alçaltıcı azaptan Biz kurtardık.", transliteration: "Ve lekad necceyna beni israile minel azabil muhin."},
  {id: 4445, sure_no: 31, ayet_no: 31, sure_adi: "Duhan", text: "مِنْ فِرْعَوْنَۜ اِنَّهُ كَانَ عَالِياً مِنَ الْمُسْرِف۪ينَ", translation: "Firavun'un zulmünden. Kuşkusuz o, müsriflerdendi.", transliteration: "Min fir'avn, innehu kane aliyen minel musrifin."},
  {id: 4446, sure_no: 32, ayet_no: 32, sure_adi: "Duhan", text: "وَلَقَدِ اخْتَرْنَاهُمْ عَلٰى عِلْمٍ عَلَى الْعَالَم۪ينَۚ", translation: "Ant olsun ki onları bilerek diğer alemler üzerine tercih ettik.", transliteration: "Ve lekadihternahum ala ilmin alel alemin."},
  {id: 4447, sure_no: 33, ayet_no: 33, sure_adi: "Duhan", text: "وَاٰتَيْنَاهُمْ مِنَ الْاٰيَاتِ مَا ف۪يهِ بَلٰٓؤٌا مُب۪ينٌ", translation: "Onlara, içinde apaçık bela olan ayetlerden verdik.", transliteration: "Ve ateynahum minel ayati ma fihi belaun mubin."},
  {id: 4448, sure_no: 34, ayet_no: 34, sure_adi: "Duhan", text: "اِنَّ هٰٓؤُ۬لَٓاءِ لَيَقُولُونَۙ", translation: "Bunlar kesinlikle diyorlar ki:", transliteration: "İnne haulai le yekulun."},
  {id: 4449, sure_no: 35, ayet_no: 35, sure_adi: "Duhan", text: "اِنْ هِيَ اِلَّا مَوْتَتُنَا الْاُو۫لٰى وَمَا نَحْنُ بِمُنْشَر۪ينَ", translation: "\"Biz yalnızca öleceğiz. Tekrar diriltilecek değiliz.\"", transliteration: "İn hiye illa mevtetunel ulave ma nahnu bi munşerin."},
  {id: 4450, sure_no: 36, ayet_no: 36, sure_adi: "Duhan", text: "فَأْتُوا بِاٰبَٓائِنَٓا اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Eğer doğru söylüyorsanız haydi, ölmüş atalarımızı bize geri getirin.\"", transliteration: "Fe'tu bi abaina in kuntum sadikin."},
  {id: 4451, sure_no: 37, ayet_no: 37, sure_adi: "Duhan", text: "اَهُمْ خَيْرٌ اَمْ قَوْمُ تُبَّعٍۙ وَالَّذ۪ينَ مِنْ قَبْلِهِمْۜ اَهْلَكْنَاهُمْۘ اِنَّهُمْ كَانُوا مُجْرِم۪ينَ", translation: "Bunlar mı daha hayırlıdır yoksa Tubba halkı mi? Ya da daha önceki kimseler mi? Biz onları yok ettik. Onlar mücrimlerdendiler.", transliteration: "E hum hayrun em kavmu tubbein vellezine min kablihim, ehleknahum innehum kanu mucrimin."},
  {id: 4452, sure_no: 38, ayet_no: 38, sure_adi: "Duhan", text: "وَمَا خَلَقْنَا السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا لَاعِب۪ينَ", translation: "Biz; gökleri, yeryüzünü ve ikisinin arasında bulunanları oyun olsun diye yaratmadık.", transliteration: "Ve ma halaknes semavati vel arda ve ma beynehuma laibin."},
  {id: 4453, sure_no: 39, ayet_no: 39, sure_adi: "Duhan", text: "مَا خَلَقْنَاهُمَٓا اِلَّا بِالْحَقِّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "İkisini de yalnızca Hakk olarak yarattık. Fakat onların çoğu bilmiyorlar.", transliteration: "Ma halaknahuma illa bil hakkı ve lakinne ekserehum la ya'lemun."},
  {id: 4454, sure_no: 40, ayet_no: 40, sure_adi: "Duhan", text: "اِنَّ يَوْمَ الْفَصْلِ م۪يقَاتُهُمْ اَجْمَع۪ينَۙ", translation: "Ayrışma Günü, onların hepsinin kararlaştırılmış bir araya gelme zamanıdır.", transliteration: "İnne yevmel faslı mikatuhum ecmain."},
  {id: 4455, sure_no: 41, ayet_no: 41, sure_adi: "Duhan", text: "يَوْمَ لَا يُغْن۪ي مَوْلًى عَنْ مَوْلًى شَيْـٔاً وَلَا هُمْ يُنْصَرُونَۙ", translation: "O Gün, hiçbir yakının, yakınına bir yararı olmaz. Onlar, yardım da olunmazlar.", transliteration: "Yevme la yugni mevlen an mevlen şey'en ve la hum yunsarun."},
  {id: 4456, sure_no: 42, ayet_no: 42, sure_adi: "Duhan", text: "اِلَّا مَنْ رَحِمَ اللّٰهُۜ اِنَّهُ هُوَ الْعَز۪يزُ الرَّح۪يمُ۟", translation: "Ancak Allah'ın rahmet ettiği kimseler hariç. Kuşkusuz O, Mutlak Üstün Olan'dır, Rahmeti Kesintisiz'dir.", transliteration: "İlla men rahimallah, innehu huvel azizur rahim."},
  {id: 4457, sure_no: 43, ayet_no: 43, sure_adi: "Duhan", text: "اِنَّ شَجَرَتَ الزَّقُّومِۙ", translation: "Zakkum ağacı,", transliteration: "İnne şeceretez zakkum."},
  {id: 4458, sure_no: 44, ayet_no: 44, sure_adi: "Duhan", text: "طَعَامُ الْاَث۪يمِۚۛ", translation: "Günahkarların yemeğidir.", transliteration: "Taamul esim."},
  {id: 4459, sure_no: 45, ayet_no: 45, sure_adi: "Duhan", text: "كَالْمُهْلِۚۛ يَغْل۪ي فِي الْبُطُونِۙ", translation: "Erimiş maden gibi karınlarında kaynar.", transliteration: "Kel muhl, yagli fil butun."},
  {id: 4460, sure_no: 46, ayet_no: 46, sure_adi: "Duhan", text: "كَغَلْيِ الْحَم۪يمِ", translation: "Kaynar suyun kaynaması gibi.", transliteration: "Ke galyil hamim."},
  {id: 4461, sure_no: 47, ayet_no: 47, sure_adi: "Duhan", text: "خُذُوهُ فَاعْتِلُوهُ اِلٰى سَوَٓاءِ الْجَح۪يمِۚ", translation: "\"Onu tutun! Cehennem'in ortasına sürükleyin.\"", transliteration: "Huzuhu fa'tiluhu ila sevail cahim."},
  {id: 4462, sure_no: 48, ayet_no: 48, sure_adi: "Duhan", text: "ثُمَّ صُبُّوا فَوْقَ رَأْسِه۪ مِنْ عَذَابِ الْحَم۪يمِۜ", translation: "\"Sonra başına azap olarak kaynar su dökün.\"", transliteration: "Summe subbu fevka re'sihi min azabil hamim."},
  {id: 4463, sure_no: 49, ayet_no: 49, sure_adi: "Duhan", text: "ذُقْۙ ۚ اِنَّكَ اَنْتَ الْعَز۪يزُ الْكَر۪يمُ", translation: "Tat bakalım! Hani sen gerçekten çok güçlü ve şerefli biriydin!", transliteration: "Zuk, inneke entel azizul kerim."},
  {id: 4464, sure_no: 50, ayet_no: 50, sure_adi: "Duhan", text: "اِنَّ هٰذَا مَا كُنْتُمْ بِه۪ تَمْتَرُونَ", translation: "Bu, sizin kendisinden kuşku duyduğunuz şeydir.", transliteration: "İnne haza ma kuntum bihi temterun."},
  {id: 4465, sure_no: 51, ayet_no: 51, sure_adi: "Duhan", text: "اِنَّ الْمُتَّق۪ينَ ف۪ي مَقَامٍ اَم۪ينٍۙ", translation: "Kuşkusuz takva sahipleri güvenli bir yerde olacaklar;", transliteration: "İnnel muttekine fi makamin emin."},
  {id: 4466, sure_no: 52, ayet_no: 52, sure_adi: "Duhan", text: "ف۪ي جَنَّاتٍ وَعُيُونٍۚ", translation: "Bahçelerde ve pınar başlarında.", transliteration: "Fi cennatin ve uyun."},
  {id: 4467, sure_no: 53, ayet_no: 53, sure_adi: "Duhan", text: "يَلْبَسُونَ مِنْ سُنْدُسٍ وَاِسْتَبْرَقٍ مُتَقَابِل۪ينَۚ", translation: "İpekten ve atlastan elbiseler giyerler, karşılıklı otururlar.", transliteration: "Yelbesune min sundusin ve istebrakın mutekabilin."},
  {id: 4468, sure_no: 54, ayet_no: 54, sure_adi: "Duhan", text: "كَذٰلِكَ۠ وَزَوَّجْنَاهُمْ بِحُورٍ ع۪ينٍۜ", translation: "İşte böyle. Ve onları güzel gözlü hurilerle eşleştirdik.", transliteration: "Kezalik, ve zevvecnahum bi hurin in."},
  {id: 4469, sure_no: 55, ayet_no: 55, sure_adi: "Duhan", text: "يَدْعُونَ ف۪يهَا بِكُلِّ فَاكِهَةٍ اٰمِن۪ينَۙ", translation: "Orada güven içinde her türlü meyveden isterler.", transliteration: "Yed'une fiha bi kulli fakihetin aminin."},
  {id: 4470, sure_no: 56, ayet_no: 56, sure_adi: "Duhan", text: "لَا يَذُوقُونَ ف۪يهَا الْمَوْتَ اِلَّا الْمَوْتَةَ الْاُو۫لٰىۚ وَوَقٰيهُمْ عَذَابَ الْجَح۪يمِۙ", translation: "Orada bir daha ölümü tatmazlar. Onlar Cehennem azabından korunmuştur.", transliteration: "La yezukune fihel mevte illel mevtetel ula, ve vekahum azabel cahim."},
  {id: 4471, sure_no: 57, ayet_no: 57, sure_adi: "Duhan", text: "فَضْلاً مِنْ رَبِّكَۜ ذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُ", translation: "Bunlar senin Rabb'inden bir armağandır. İşte bu, en büyük kurtuluştur.", transliteration: "Fadlen min rabbik, zalike huvel fevzul azim."},
  {id: 4472, sure_no: 58, ayet_no: 58, sure_adi: "Duhan", text: "فَاِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ", translation: "İşte Biz, onu senin dilinle kolaylaştırdık. Umulur ki onlar öğüt alırlar.", transliteration: "Fe innema yessernahu bi lisanike leallehum yetezekkerun."},
  {id: 4473, sure_no: 59, ayet_no: 59, sure_adi: "Duhan", text: "فَارْتَقِبْ اِنَّهُمْ مُرْتَقِبُونَ", translation: "Artık gözetle! Onlar da gözetleyenlerdir.", transliteration: "Fertekib innehum murtekıbun."},
  {id: 4474, sure_no: 1, ayet_no: 1, sure_adi: "Casiye", text: "حٰمٓۜ", translation: "Ha, Mim.", transliteration: "Ha mim."},
  {id: 4475, sure_no: 2, ayet_no: 2, sure_adi: "Casiye", text: "تَنْز۪يلُ الْـكِتَابِ مِنَ اللّٰهِ الْعَز۪يزِ الْحَك۪يمِ", translation: "Bu Kitap'ın indirilmesi, Mutlak Üstün Olan, En İyi Hüküm Veren Allah tarafındandır.", transliteration: "Tenzilul kitabi minallahil azizil hakim."},
  {id: 4476, sure_no: 3, ayet_no: 3, sure_adi: "Casiye", text: "اِنَّ فِي السَّمٰوَاتِ وَالْاَرْضِ لَاٰيَاتٍ لِلْمُؤْمِن۪ينَۜ", translation: "Kuşkusuz göklerde ve yeryüzünde İman Edenler için ayetler vardır.", transliteration: "İnne fis semavati vel ardı le ayatin lil mu'minin."},
  {id: 4477, sure_no: 4, ayet_no: 4, sure_adi: "Casiye", text: "وَف۪ي خَلْقِكُمْ وَمَا يَبُثُّ مِنْ دَٓابَّةٍ اٰيَاتٌ لِقَوْمٍ يُوقِنُونَۙ", translation: "Sizin yaratılışınızda ve üretip yaydığı canlı varlıklarda \"gerçek bilgi\" sahibi olan bir toplum için ayetler vardır.", transliteration: "Ve fi halkıkum ve ma yebussu min dabbetin ayatun li kavmin yukınun."},
  {id: 4478, sure_no: 5, ayet_no: 5, sure_adi: "Casiye", text: "وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَٓا اَنْزَلَ اللّٰهُ مِنَ السَّمَٓاءِ مِنْ رِزْقٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَتَصْر۪يفِ الرِّيَاحِ اٰيَاتٌ لِقَوْمٍ يَعْقِلُونَ", translation: "Gece ile gündüzün birbirini izlemesinde, Allah'ın rızık olarak gökten indirdiği şeyle ölümünden sonra yeryüzünü diriltmesinde ve rüzgarları yönlendirmesinde aklını kullanan bir toplum için ayetler vardır.", transliteration: "Vahtilafil leyli ven nehari ve ma enzelallahu mines semai min rızkın fe ahya bihil arda ba'de mevtiha ve tasrifir riyahı ayatun li kavmin ya'kılun."},
  {id: 4479, sure_no: 6, ayet_no: 6, sure_adi: "Casiye", text: "تِلْكَ اٰيَاتُ اللّٰهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّۚ فَبِاَيِّ حَد۪يثٍ بَعْدَ اللّٰهِ وَاٰيَاتِه۪ يُؤْمِنُونَ", translation: "İşte bunlar, Bizim sana Hakk olarak anlattığımız Allah'ın ayetleridir. O halde Allah'tan ve O'nun ayetlerinden sonra hangi hadise inanacaklar?", transliteration: "Tilke ayatullahi netluha aleyke bil hakk, fe bi eyyi hadisin ba'dallahi ve ayatihi yu'minun."},
  {id: 4480, sure_no: 7, ayet_no: 7, sure_adi: "Casiye", text: "وَيْلٌ لِكُلِّ اَفَّاكٍ اَث۪يمٍۙ", translation: "Bütün yalancı günahkarların vay haline!", transliteration: "Veylun li kulli effakin esim."},
  {id: 4481, sure_no: 8, ayet_no: 8, sure_adi: "Casiye", text: "يَسْمَعُ اٰيَاتِ اللّٰهِ تُتْلٰى عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِراً كَاَنْ لَمْ يَسْمَعْهَاۚ فَبَشِّرْهُ بِعَذَابٍ اَل۪يمٍ", translation: "Böyle kimseler, kendilerine okunan Allah'ın ayetlerini duyduktan sonra, büyüklük taslayarak sanki hiç duymamış gibi davranır. Artık onu can yakıcı bir azapla haberdar et.", transliteration: "Yesmeu ayatillahi tutla aleyhi summe yusırru mustekbiren ke en lem yesma'ha, fe beşşirhu bi azabin elim."},
  {id: 4482, sure_no: 9, ayet_no: 9, sure_adi: "Casiye", text: "وَاِذَا عَلِمَ مِنْ اٰيَاتِنَا شَيْـٔاًۨ اتَّخَذَهَا هُزُواًۜ  اُو۬لٰٓئِكَ لَهُمْ عَذَابٌ مُه۪ينٌۜ", translation: "Ayetlerimizden bir şey öğrendikleri zaman, onu alay konusu edinirler. İşte onlar için alçaltıcı bir azap vardır.", transliteration: "Ve iza alime min ayatina şey'enittehazeha huzuva, ulaike lehum azabun muhin."},
  {id: 4483, sure_no: 10, ayet_no: 10, sure_adi: "Casiye", text: "مِنْ وَرَٓائِهِمْ جَهَنَّمُۚ وَلَا يُغْن۪ي عَنْهُمْ مَا كَسَبُوا شَيْـٔاً وَلَا مَا اتَّخَذُوا مِنْ دُونِ اللّٰهِ اَوْلِيَٓاءَۚ وَلَهُمْ عَذَابٌ عَظ۪يمٌۜ", translation: "Cehennem peşlerindedir. Kazandıkları şeyler ve Allah'ın yanı sıra edindikleri veliler onlara bir yarar sağlamaz. Onlar için büyük bir azap vardır.", transliteration: "Min veraihim cehennem, ve la yugni anhum ma kesebu şey'en ve la mattehazu min dunillahi evliyae, ve lehum azabun azim."},
  {id: 4484, sure_no: 11, ayet_no: 11, sure_adi: "Casiye", text: "هٰذَا هُدًىۚ وَالَّذ۪ينَ كَفَرُوا بِاٰيَاتِ رَبِّهِمْ لَهُمْ عَذَابٌ مِنْ رِجْزٍ اَل۪يمٌ۟", translation: "Bu doğru yol göstericisidir. Rabb'lerinin ayetlerini yalanlayanlar için çok kötü bir azap vardır.", transliteration: "Haza huda, vellezine keferu bi ayati rabbihim lehum azabun min riczin elim."},
  {id: 4485, sure_no: 12, ayet_no: 12, sure_adi: "Casiye", text: "اَللّٰهُ الَّذ۪ي سَخَّرَ لَكُمُ الْبَحْرَ لِتَجْرِيَ الْفُلْكُ ف۪يهِ بِاَمْرِه۪ وَلِتَبْتَغُوا مِنْ فَضْلِه۪ وَلَعَلَّكُمْ تَشْكُرُونَۚ", translation: "Allah, koyduğu yasalar sayesinde, içinde gemileri yüzdürmeniz ve O'nun lütfundan, onda rızık aramanız için denizi hizmetinize sunandır. Umulur ki şükredersiniz.", transliteration: "Allahullezi sahhare lekumul bahre li tecriyel fulku fihi bi emrihi ve li tehtegu min fadlihi ve leallekum teşkurun."},
  {id: 4486, sure_no: 13, ayet_no: 13, sure_adi: "Casiye", text: "وَسَخَّرَ لَكُمْ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ جَم۪يعاً مِنْهُۜ اِنَّ ف۪ي ذٰلِكَ لَاٰيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ", translation: "Göklerde ve yeryüzünde bulunan her şeyi Kendi lütfundan sizin yararlanmanıza sunmuştur. Bunda düşünen bir toplum için ayetler vardır.", transliteration: "Ve sahhare lekum ma fis semavati ve ma fil ardı cemian minh, inne fi zalike le ayatin li kavmin yetefekkerun."},
  {id: 4487, sure_no: 14, ayet_no: 14, sure_adi: "Casiye", text: "قُلْ لِلَّذ۪ينَ اٰمَنُوا يَغْفِرُوا لِلَّذ۪ينَ لَا يَرْجُونَ اَيَّامَ اللّٰهِ لِيَجْزِيَ قَوْماً بِمَا كَانُوا يَكْسِبُونَ", translation: "İman Edenlere de ki: \"Allah'ın, kendilerini hesaba çekeceği günün geleceğini ummayanları bağışlasınlar. Her halka yaptıklarının karşılığı verilecektir.\"", transliteration: "Kul lillezine amenu yagfiru lillezine la yercune eyyamallahi li yecziye kavmen bi ma kanu yeksibun."},
  {id: 4488, sure_no: 15, ayet_no: 15, sure_adi: "Casiye", text: "مَنْ عَمِلَ صَالِحاً فَلِنَفْسِه۪ۚ وَمَنْ اَسَٓاءَ فَعَلَيْهَاۘ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُونَ", translation: "Her kim iyilik yaparsa kendisi için yapmış olur, kim de kötülük yaparsa kendisi için yapmış olur. Sonra Rabb'inize döndürüleceksiniz.", transliteration: "Men amile salihan fe li nefsih, ve men esae fe aleyha summe ila rabbikum turceun."},
  {id: 4489, sure_no: 16, ayet_no: 16, sure_adi: "Casiye", text: "وَلَقَدْ اٰتَيْنَا بَن۪ٓي اِسْرَٓائ۪ـلَ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلَى الْعَالَم۪ينَۚ", translation: "Ant olsun ki İsrailoğulları'na Kitap, Hüküm ve Nebi'lik verdik. Ve onları temiz rızıklarla rızıklandırdık. Ve onları alemlere tercih ederek ihsanda bulunduk.", transliteration: "Ve lekad ateyna beni israilel kitabe vel hukme ven nubuvvete ve rezaknahum minet tayyibati ve faddalnahum alel alemin."},
  {id: 4490, sure_no: 17, ayet_no: 17, sure_adi: "Casiye", text: "وَاٰتَيْنَاهُمْ بَيِّنَاتٍ مِنَ الْاَمْرِۚ فَمَا اخْتَلَفُٓوا اِلَّا مِنْ بَعْدِ مَا جَٓاءَهُمُ الْعِلْمُۙ بَغْياً بَيْنَهُمْۜ اِنَّ رَبَّكَ يَقْض۪ي بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ ف۪يمَا كَانُوا ف۪يهِ يَخْتَلِفُونَ", translation: "Onlara, buyruklardan, apaçık, açıklayıcı bilgiler verdik. Sonra onlar, kendilerine ilim geldikten sonra hırslarına kapılarak ayrılığa düştüler. Rabb'in, ayrılığa düştükleri konularda kıyamet günü hüküm verecektir.", transliteration: "Ve ateynahum beyyinatin minel emr, fe mahtelefu illa min ba'di ma caehumul ilmu bagyen beynehum, inne rabbeke yakdi beynehum yevmel kıyameti fi ma kanu fihi yahtelifun."},
  {id: 4491, sure_no: 18, ayet_no: 18, sure_adi: "Casiye", text: "ثُمَّ جَعَلْنَاكَ عَلٰى شَر۪يعَةٍ مِنَ الْاَمْرِ فَاتَّبِعْهَا وَلَا تَتَّبِـعْ اَهْوَٓاءَ الَّذ۪ينَ لَا يَعْلَمُونَ", translation: "Sonra da seni buyruklardan apaçık bir şeriat sahibi yaptık. Öyleyse ona uy! Bilmeyenlerin hevalarına uyma.", transliteration: "Summe cealnake ala şeriatin minel emri fettebi' ha ve la tettebi' ehvaellezine la ya'lemun."},
  {id: 4492, sure_no: 19, ayet_no: 19, sure_adi: "Casiye", text: "اِنَّهُمْ لَنْ يُغْنُوا عَنْكَ مِنَ اللّٰهِ شَيْـٔاًۜ وَاِنَّ الظَّالِم۪ينَ بَعْضُهُمْ اَوْلِيَٓاءُ بَعْضٍۚ وَاللّٰهُ وَلِيُّ الْمُتَّق۪ينَ", translation: "Onlar, Allah'tan gelecek olan hiçbir şeyi senden savamazlar. Zalimler, birbirlerinin velileridirler, Allah ise takva sahiplerinin velisidir.", transliteration: "İnnehum len yugnu anke minallahi şey'a, ve innez zalimine ba'duhum evliyau ba'd, vallahu veliyyul muttekin."},
  {id: 4493, sure_no: 20, ayet_no: 20, sure_adi: "Casiye", text: "هٰذَا بَصَٓائِرُ لِلنَّاسِ وَهُدًى وَرَحْمَةٌ لِقَوْمٍ يُوقِنُونَ", translation: "Bu Kur'an, insanlar için gerçeği görme, gerçekten iman eden halk için doğru yolu gösterme ve bir rahmettir.", transliteration: "Haza basairu lin nasi ve huden ve rahmetun li kavmin yukınun."},
  {id: 4494, sure_no: 21, ayet_no: 21, sure_adi: "Casiye", text: "اَمْ حَسِبَ الَّذ۪ينَ اجْتَرَحُوا السَّيِّـَٔاتِ اَنْ نَجْعَلَهُمْ كَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِۙ سَوَٓاءً مَحْيَاهُمْ وَمَمَاتُهُمْۜ سَٓاءَ مَا يَحْكُمُونَ۟", translation: "Yoksa Bizim, kötülük yapan kimselerle, İman edip, salihatı yapan kimseleri, hayatlarında ve ölümlerinde bir tutacağımızı mı sanıyorlar. Ne kötü yargıda bulunuyorlar!", transliteration: "Em hasibellezinecterahus seyyiati en nec'alehum kellezine amenu ve amilus salihati sevaen mahyahum ve mematuhum, sae ma yahkumun."},
  {id: 4495, sure_no: 22, ayet_no: 22, sure_adi: "Casiye", text: "وَخَلَقَ اللّٰهُ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّ وَلِتُجْزٰى كُلُّ نَفْسٍ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ", translation: "Allah, gökleri ve yeryüzünü Hakk ile yarattı. Herkes yaptığının karşılığını görecek ve hiç kimseye haksızlık edilmeyecektir.", transliteration: "Ve halakallahus semavati vel arda bil hakkı ve li tucza kullu nefsin bima kesebet ve hum la yuzlemun."},
  {id: 4496, sure_no: 23, ayet_no: 23, sure_adi: "Casiye", text: "اَفَرَاَيْتَ مَنِ اتَّخَذَ اِلٰهَهُ هَوٰيهُ وَاَضَلَّهُ اللّٰهُ عَلٰى عِلْمٍ وَخَتَمَ عَلٰى سَمْعِه۪ وَقَلْبِه۪ وَجَعَلَ عَلٰى بَصَرِه۪ غِشَاوَةًۜ  فَمَنْ يَهْد۪يهِ مِنْ بَعْدِ اللّٰهِۜ  اَفَلَا تَذَكَّرُونَ", translation: "Hevasını, ilahlaştıran kimseyi gördün mü? Allah, bir bilgiye dayalı olarak, onu sapkınlıkta bıraktı. Ve onun kulağını ve kalbini mühürledi. Gözlerine perde çekti. Artık Allah'tan başka kim onu doğru yola iletebilir? Öğüt almıyor musunuz?", transliteration: "E fe reeyte menittehaze ilahehu hevahu ve edallehullahu ala ilmin ve hateme ala sem'ihi ve kalbihi ve ceale ala basarihi gışaveh, fe men yehdihi min ba'dillah, e fe la tezekkerun."},
  {id: 4497, sure_no: 24, ayet_no: 24, sure_adi: "Casiye", text: "وَقَالُوا مَا هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا يُهْلِكُنَٓا اِلَّا الدَّهْرُۚ وَمَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍۚ  اِنْ هُمْ اِلَّا يَظُنُّونَ", translation: "Onlar, \"Hayat, ancak bu dünya hayatından ibarettir. Doğar ve yaşarız, bizi ancak zaman yok eder.\" dediler. Oysaki bu konuda gerçeğe dayalı hiçbir bilgileri yoktur. Onlar, yalnızca zanda bulunuyorlar.", transliteration: "Ve kalu ma hiye illa hayatuned dunya nemutu ve nahya ve ma yuhlikuna illed dehr, ve ma lehum bi zalike min ilm, in hum illa yezunnun."},
  {id: 4498, sure_no: 25, ayet_no: 25, sure_adi: "Casiye", text: "وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ مَا كَانَ حُجَّتَهُمْ اِلَّٓا اَنْ قَالُوا ائْتُوا بِاٰبَٓائِنَٓا اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Onlara, ayetlerimiz açık ve kanıtlayıcı olarak okunduğu zaman da hüccetleri, \"Eğer doğru söylüyorsanız, atalarımızı geri getirin.\" demekten başka bir şey olmadı.", transliteration: "Ve iza tutla aleyhim ayatuna beyyinatin ma kane huccetehum illa en kalu'tu bi abaina in kuntum sadıkin."},
  {id: 4499, sure_no: 26, ayet_no: 26, sure_adi: "Casiye", text: "قُلِ اللّٰهُ يُحْي۪يكُمْ ثُمَّ يُم۪يتُكُمْ ثُمَّ يَجْمَعُكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ ف۪يهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُونَ۟", translation: "De ki: \"Sizi yaşatan sonra öldüren Allah'tır. Sonra sizi, gerçekleşeceği kesin olan kıyamet günü bir araya toplayacaktır.\" Ancak insanların çoğu bu gerçeği kavramıyorlar.", transliteration: "Kulillahu yuhyikum summe yumitukum summe yecmeukum ila yevmil kıyameti la reybe fihi ve lakinne ekseren nasi la ya'lemun."},
  {id: 4500, sure_no: 27, ayet_no: 27, sure_adi: "Casiye", text: "وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَخْسَرُ الْمُبْطِلُونَ", translation: "Göklerin ve yeryüzünün mülkü Allah'a aittir. Sa'at'ın kopacağı O Gün, sapkın yolda olanlar hüsrana uğrayacaklardır.", transliteration: "Ve lillahi mulkus semavati vel ard, ve yevme tekumus saatu yevme izin yahserul mubtılun."},
  {id: 4501, sure_no: 28, ayet_no: 28, sure_adi: "Casiye", text: "وَتَرٰى كُلَّ اُمَّةٍ جَاثِيَةً۠   كُلُّ اُمَّةٍ تُدْعٰٓى اِلٰى كِتَابِهَاۜ اَلْيَوْمَ تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ", translation: "Her ümmeti toplanmış görürsün. Her ümmet, amel defteriyle yüzleşmeye çağrılır: \"Bugün, yaptıklarınızın karşılığı size verilecektir.\"", transliteration: "Ve tera kulle ummetin casiyeh, kullu ummetin tud'a ila kitabiha, el yevme tuczevne ma kuntum ta'melun."},
  {id: 4502, sure_no: 29, ayet_no: 29, sure_adi: "Casiye", text: "هٰذَا كِتَابُنَا يَنْطِقُ عَلَيْكُمْ بِالْحَقِّۜ اِنَّا كُنَّا نَسْتَنْسِخُ مَا كُنْتُمْ تَعْمَلُونَ", translation: "\"İşte bu, yüzünüze karşı gerçekleri ortaya koyan amel defterinizdir. Kuşkusuz Biz, bütün yaptıklarınızı yazdırdık.\"", transliteration: "Haza kitabuna yentıku aleykum bil hakk, inna kunna nestensihu ma kuntum ta'melun."},
  {id: 4503, sure_no: 30, ayet_no: 30, sure_adi: "Casiye", text: "فَاَمَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُدْخِلُهُمْ رَبُّهُمْ ف۪ي رَحْمَتِه۪ۜ  ذٰلِكَ هُوَ الْفَوْزُ الْمُب۪ينُ", translation: "İman eden ve salihatı yapan kimseleri Rabb'leri rahmeti ile kuşatacaktır. İşte bu, büyük kurtuluşun ta kendisidir.", transliteration: "Fe emmellezine amenu ve amilus salihati fe yudhıluhum rabbuhum fi rahmetih, zalike huvel fevzul mubin."},
  {id: 4504, sure_no: 31, ayet_no: 31, sure_adi: "Casiye", text: "وَاَمَّا الَّذ۪ينَ كَفَرُوا۠  اَفَلَمْ تَكُنْ اٰيَات۪ي تُتْلٰى عَلَيْكُمْ فَاسْتَكْـبَرْتُمْ وَكُنْتُمْ قَوْماً مُجْرِم۪ينَ", translation: "Kafirlere denir ki: \"Ayetlerim size okunduğu zaman, büyüklük taslayarak suç işleyen bir halk olmuştunuz değil mi?\"", transliteration: "Ve emmellezine keferu, e fe lem tekun ayati tutla aleykum festekbertum ve kuntum kavmen mucrimin."},
  {id: 4505, sure_no: 32, ayet_no: 32, sure_adi: "Casiye", text: "وَاِذَا ق۪يلَ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَالسَّاعَةُ لَا رَيْبَ ف۪يهَا قُلْتُمْ مَا نَدْر۪ي مَا السَّاعَةُۙ اِنْ نَظُنُّ اِلَّا ظَناًّ وَمَا نَحْنُ بِمُسْتَيْقِن۪ينَ", translation: "\"Allah'ın verdiği söz gerçektir ve Sa'at mutlaka gelecektir.\" dendiği zaman, siz: \"Biz, o Sa'at nedir bilmeyiz? Yalnızca bir varsayım olduğunu sanıyoruz, kesin bilgi sahibi değiliz.\" dediniz.", transliteration: "Ve iza kile inne va'dallahi hakkun ves saatu la reybe fiha kultum ma nedri mes saatu in nezunnu illa zannen ve ma nahnu bi musteykınin."},
  {id: 4506, sure_no: 33, ayet_no: 33, sure_adi: "Casiye", text: "وَبَدَا لَهُمْ سَيِّـَٔاتُ مَا عَمِلُوا وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ", translation: "Yaptıklarının kötülükleri apaçık ortaya kondu ve alay ettikleri şeyler, onları kuşattı.", transliteration: "Ve beda lehum seyyiatu ma amilu ve haka bihim ma kanu bihi yestehziun."},
  {id: 4507, sure_no: 34, ayet_no: 34, sure_adi: "Casiye", text: "وَق۪يلَ الْيَوْمَ نَنْسٰيكُمْ كَمَا نَس۪يتُمْ لِقَٓاءَ يَوْمِكُمْ هٰذَا وَمَأْوٰيكُمُ النَّارُ وَمَا لَكُمْ مِنْ نَاصِر۪ينَ", translation: "\"Bugüne kavuşmayı nasıl umursamadıysanız, Biz de bugün sizi umursamıyoruz. Kalacağınız yer ateştir. Ve sizin için bir yardımcı da yoktur.\" denir.", transliteration: "Ve kilel yevme nensakum kema nesitum likae yevmikum haza ve me'vakumun naru ve ma lekum min nasırin."},
  {id: 4508, sure_no: 35, ayet_no: 35, sure_adi: "Casiye", text: "ذٰلِكُمْ بِاَنَّكُمُ اتَّخَذْتُمْ اٰيَاتِ اللّٰهِ هُزُواً وَغَرَّتْكُمُ الْحَيٰوةُ الدُّنْيَاۚ فَالْيَوْمَ لَا يُخْرَجُونَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُونَ", translation: "İşte bu, Allah'ın ayetlerini alay konusu etmeniz nedeniyledir. Sizi, dünya hayatı aldattı. Artık bugün onlar, Cehennem'den çıkarılmazlar. Ve onlardan özür de kabul edilmez.", transliteration: "Zalikum bi ennekumuttehaztum ayatillahi huzuven ve garretkumul hayatud dunya, fel yevme la yuhrecune minha ve la hum yusta'tebun."},
  {id: 4509, sure_no: 36, ayet_no: 36, sure_adi: "Casiye", text: "فَلِلّٰهِ الْحَمْدُ رَبِّ السَّمٰوَاتِ وَرَبِّ الْاَرْضِ رَبِّ الْعَالَم۪ينَ", translation: "Öyleyse övgünün tamamı, göklerin ve yerin Rabb'i ve alemlerin Rabb'i Allah'a mahsustur.", transliteration: "Fe lillahil hamdu rabbis semavati ve rabbil ardı rabbil alemin."},
  {id: 4510, sure_no: 37, ayet_no: 37, sure_adi: "Casiye", text: "وَلَهُ الْكِبْرِيَٓاءُ فِي السَّمٰوَاتِ وَالْاَرْضِۖ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Göklerde ve yerde erişilmez büyüklük ve egemenlik yalnızca O'nundur. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve lehul kibriyau fis semavati vel ard, ve huvel azizul hakim."},
  {id: 4511, sure_no: 1, ayet_no: 1, sure_adi: "Ahkaf", text: "حٰمٓ", translation: "Ha, Mim.", transliteration: "Ha mim."},
  {id: 4512, sure_no: 2, ayet_no: 2, sure_adi: "Ahkaf", text: "تَنْز۪يلُ الْكِتَابِ مِنَ اللّٰهِ الْعَز۪يزِ الْحَك۪يمِ", translation: "Kitap'ın indirilmesi Mutlak Üstün Olan, En İyi Hüküm Veren Allah tarafındandır.", transliteration: "Tenzilul kitabi minallahil azizil hakim."},
  {id: 4513, sure_no: 3, ayet_no: 3, sure_adi: "Ahkaf", text: "مَا خَلَقْنَا السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَٓا اِلَّا بِالْحَقِّ وَاَجَلٍ مُسَمًّىۜ وَالَّذ۪ينَ كَفَرُوا عَمَّٓا اُنْذِرُوا مُعْرِضُونَ", translation: "Gökleri, yeryüzünü ve ikisi arasındakileri gerçek olarak, belirlenmiş bir süre için yarattık. Kafirler uyarıldıkları şeylerden yüz çevirmektedirler.", transliteration: "Ma halaknes semavati vel arda ve ma beyne huma illa bil hakkı ve ecelin musemma, vellezine keferu amma unziru mu'ridun."},
  {id: 4514, sure_no: 4, ayet_no: 4, sure_adi: "Ahkaf", text: "قُلْ اَرَاَيْتُمْ مَا تَدْعُونَ مِنْ دُونِ اللّٰهِ اَرُون۪ي مَاذَا خَلَقُوا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِي السَّمٰوَاتِۜ ا۪يتُون۪ي بِكِتَابٍ مِنْ قَبْلِ هٰذَٓا اَوْ اَثَارَةٍ مِنْ عِلْمٍ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "De ki: \"Allah'ın yanı sıra yakardıklarınıza bir bakın bakalım! Onların, yeryüzünde yarattıkları bir şey varsa bana gösterin! Yoksa onların göklerde bir ortakları mı var? Eğer doğru söylüyorsanız, bana, daha önce gelmiş bir kitap ya da bilgi kırıntısı bir eser getirin.", transliteration: "Kul ereeytum ma ted'une min dunillahi eruni maza halaku minel ardı em lehum şirkun fis semavat, ituni bi kitabin min kabli haza ev esaretin min ilmin in kuntum sadikin."},
  {id: 4515, sure_no: 5, ayet_no: 5, sure_adi: "Ahkaf", text: "وَمَنْ اَضَلُّ مِمَّنْ يَدْعُوا مِنْ دُونِ اللّٰهِ مَنْ لَا يَسْتَج۪يبُ لَـهُٓ اِلٰى يَوْمِ الْقِيٰمَةِ وَهُمْ عَنْ دُعَٓائِهِمْ غَافِلُونَ", translation: "Allah'ın yanı sıra, kıyamet gününe kadar kendisine cevap veremeyecek ve edilen dualardan haberleri olmayana yönelen kimseden daha sapkın kim olabilir?", transliteration: "Ve men edallu mimmen yed'u min dunillahi men la yestecibu lehu ila yevmil kıyameti ve hum an duaihim gafilun."},
  {id: 4516, sure_no: 6, ayet_no: 6, sure_adi: "Ahkaf", text: "وَاِذَا حُشِرَ النَّاسُ كَانُوا لَهُمْ اَعْدَٓاءً وَكَانُوا بِعِبَادَتِهِمْ كَافِر۪ينَ", translation: "İnsanlar bir araya getirildikleri zaman, yöneldikleri kimseler, kendilerine düşman kesilirler ve onların kendilerine kulluk ettiklerini inkar ederler.", transliteration: "Ve iza huşiren nasu kanu lehum a'daen ve kanu bi ibadetihim kafirin."},
  {id: 4517, sure_no: 7, ayet_no: 7, sure_adi: "Ahkaf", text: "وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنَاتٍ قَالَ الَّذ۪ينَ كَفَرُوا لِلْحَقِّ لَمَّا جَٓاءَهُمْۙ هٰذَا سِحْرٌ مُب۪ينٌۜ", translation: "Ayetlerimiz kendilerine açıklayıcı olarak okunduğu zaman, Kafirler; kendilerine gelen \"Hakk\" için, \"Bu apaçık bir büyüdür.\" dediler.", transliteration: "Ve iza tutla aleyhim ayatuna beyyinatin kalellezine keferu lil hakkı lemma caehum haza sihrun mubin."},
  {id: 4518, sure_no: 8, ayet_no: 8, sure_adi: "Ahkaf", text: "اَمْ يَقُولُونَ افْتَرٰيهُۜ  قُلْ اِنِ افْتَرَيْتُهُ فَلَا تَمْلِكُونَ ل۪ي مِنَ اللّٰهِ شَيْـٔاًۜ هُوَ اَعْلَمُ بِمَا تُف۪يضُونَ ف۪يهِۜ   كَفٰى بِه۪ شَه۪يداً بَيْن۪ي وَبَيْنَكُمْۜ وَهُوَ الْغَفُورُ الرَّح۪يمُ", translation: "Yoksa \"Onu uydurdu.\" mu diyorlar? De ki: \"Eğer onu uydurmuş olsaydım, Allah'tan bana gelecek şeye siz engel olamazdınız. O, sizin ona karşı nelere giriştiğinizi çok iyi biliyor. Benimle sizin aranızda tanık olarak O yeter. O, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.\"", transliteration: "Em yekulunefterah, kul iniftereytuhu fe la temlikune li minallahi şey'a, huve a'lemu bi ma tufidune fih, kefa bihi şehiden beyni ve beynekum ve huvel gafurur rahim."},
  {id: 4519, sure_no: 9, ayet_no: 9, sure_adi: "Ahkaf", text: "قُلْ مَا كُنْتُ بِدْعاً مِنَ الرُّسُلِ وَمَٓا اَدْر۪ي مَا يُفْعَلُ ب۪ي وَلَا بِكُمْۜ اِنْ اَتَّبِعُ اِلَّا مَا يُوحٰٓى اِلَيَّ وَمَٓا اَنَا۬ اِلَّا نَذ۪يرٌ مُب۪ينٌ", translation: "De ki: \"İlk Resul ben değilim. Bana ve size ne yapılacağını bilmiyorum. Ben, yalnızca bana vahyedilene uyuyorum. Ben, yalnızca apaçık bir uyarıcıyım.\"", transliteration: "Kul ma kuntu bid'an miner rusuli ve ma edri ma yuf'alu bi ve la bikum, in ettebiu illa ma yuha ileyye ve ma ene illa nezirun mubin."},
  {id: 4520, sure_no: 10, ayet_no: 10, sure_adi: "Ahkaf", text: "قُلْ اَرَاَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ وَكَفَرْتُمْ بِه۪ وَشَهِدَ شَاهِدٌ مِنْ بَن۪ٓي اِسْرَٓائ۪لَ عَلٰى مِثْلِه۪ فَاٰمَنَ وَاسْتَكْـبَرْتُمْۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ۟", translation: "De ki: \"Gördünüz mü? Kur'an gerçekten de Allah katındansa ve siz onu yalanladıysanız; İsrailoğulları'ndan bir tanık da bunun benzerine tanık olup da iman etmişse ve siz de büyüklük tasladıysanız… Kuşkusuz Allah, zalim bir halka doğru yolu göstermez.\"", transliteration: "Kul e reeytum in kane min indillahi ve kefertum bihi ve şehide şahidun min beni israile ala mislihi fe amene vestekbertum innallahe la yehdil kavmez zalimin."},
  {id: 4521, sure_no: 11, ayet_no: 11, sure_adi: "Ahkaf", text: "وَقَالَ الَّذ۪ينَ كَفَرُوا لِلَّذ۪ينَ اٰمَنُوا لَوْ كَانَ خَيْراً مَا سَبَقُونَٓا اِلَيْهِۜ وَاِذْ لَمْ يَهْتَدُوا بِه۪ فَسَيَقُولُونَ هٰذَٓا اِفْكٌ قَد۪يمٌ", translation: "Kafirler, İman Edenler için: \"Eğer Kur'an'da bir \"hayır\" olsaydı, onlar, bizden önce onu kabul etmiş olmazlardı.\" dediler. Kur'an'la doğru yolu bulmayı kendilerine yediremediklerinden, \"Bu eski bir uydurmadır.\" diyeceklerdir.", transliteration: "Ve kalellezine keferu lillezine amenulev kane hayren ma sebekuna ileyh, ve iz lem yehtedu bihi fe seyekulune haza ifkun kadim."},
  {id: 4522, sure_no: 12, ayet_no: 12, sure_adi: "Ahkaf", text: "وَمِنْ قَبْلِه۪ كِتَابُ مُوسٰٓى اِمَاماً وَرَحْمَةًۜ وَهٰذَا كِتَابٌ مُصَدِّقٌ لِسَاناً عَرَبِياًّ لِيُنْذِرَ الَّذ۪ينَ ظَلَمُواۗ وَبُشْرٰى لِلْمُحْسِن۪ينَ", translation: "Bundan önce önder ve rahmet olarak Musa'nın Kitap'ı vardı. Kur'an zulmedenleri uyarmak; iyi olan kimseleri müjdelemek için, dili Arapça olan, tasdik eden bir Kitap'tır.", transliteration: "Ve min kablihi kitabu musa imamen ve rahmeh ve haza kitabun musaddikun lisanen arabiyyen li yunzirellezine zalemu ve buşra lil muhsinin."},
  {id: 4523, sure_no: 13, ayet_no: 13, sure_adi: "Ahkaf", text: "اِنَّ الَّذ۪ينَ قَالُوا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۚ", translation: "\"Rabb'imiz Allah'tır.\" deyip, bu sözlerine bağlı kalanlar için hiçbir korku yoktur. Onlar üzülmeyeceklerdir.", transliteration: "İnnellezine kalu rabbunallahu summestekamu fe la havfun aleyhim ve la hum yahzenun."},
  {id: 4524, sure_no: 14, ayet_no: 14, sure_adi: "Ahkaf", text: "اُو۬لٰٓئِكَ اَصْحَابُ الْجَنَّةِ خَالِد۪ينَ ف۪يهَاۚ جَزَٓاءً بِمَا كَانُوا يَعْمَلُونَ", translation: "İşte onlar Cennetliktirler. Yaptıklarına karşılık, orada sürekli kalacaklardır.", transliteration: "Ulaike ashabul cenneti halidine fiha, cezaen bima kanu ya'melun."},
  {id: 4525, sure_no: 15, ayet_no: 15, sure_adi: "Ahkaf", text: "وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ اِحْسَاناًۜ حَمَلَتْهُ اُمُّهُ كُرْهاً وَوَضَعَتْهُ كُرْهاًۜ وَحَمْلُهُ وَفِصَالُهُ ثَلٰثُونَ شَهْراًۜ حَتّٰٓى اِذَا بَلَغَ اَشُدَّهُ وَبَلَغَ اَرْبَع۪ينَ سَنَةًۙ قَالَ رَبِّ اَوْزِعْن۪ٓي اَنْ اَشْكُرَ نِعْمَتَكَ الَّت۪ٓي اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحاً تَرْضٰيهُ وَاَصْلِحْ ل۪ي ف۪ي ذُرِّيَّت۪يۚ اِنّ۪ي تُبْتُ اِلَيْكَ وَاِنّ۪ي مِنَ الْمُسْلِم۪ينَ", translation: "Biz insana, anne ve babasına çok iyi davranmasını öğütledik. Annesi onu güçlükle taşıdı ve onu güçlükle bıraktı. Onun taşınması ve ayrılması otuz aydır. Nihayet olgunluk çağına ulaştığı ve kırk yaşını tamamladığında: \"Ey Rabb'im! Bana, anne ve babama bağışladığın nimetlerin karşılığında şükretmede ve hoşnut olacağın işler yapmada beni başarılı kıl. Ve soyuma da düzgün ve düzeltici olmayı nasip et. Kuşkusuz ben, Sana yöneldim. Ve kuşkusuz ben, Sana teslim olanlardanım.\" dedi.", transliteration: "Ve vassaynel insane bi valideyhi ihsana, hamelethu ummuhu kurhen ve vadaathu kurha, ve hamluhu ve fisaluhu selasune şehra, hatta iza belega eşuddehu ve belega erbaine seneten kale rabbi evzı'ni en eşkure ni'metekelleti en'amte aleyye ve ala valideyye ve en a'mele salihan terdahu ve aslıh li fi zurriyyeti, inni tubtu ileyke ve inni minel muslimin."},
  {id: 4526, sure_no: 16, ayet_no: 16, sure_adi: "Ahkaf", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ نَتَقَبَّلُ عَنْهُمْ اَحْسَنَ مَا عَمِلُوا وَنَتَجَاوَزُ عَنْ سَيِّـَٔاتِهِمْ ف۪ٓي اَصْحَابِ الْجَنَّةِۜ وَعْدَ الصِّدْقِ الَّذ۪ي كَانُوا يُوعَدُونَ", translation: "Onlar, yaptıklarının karşılığını en iyi şekilde verdiğimiz ve kötülüklerini görmezden geldiğimiz, Cennet ehli olan kimselerdir. Bu, kendilerine verilen doğru sözün gerçekleşmesidir.", transliteration: "Ulaikellezine netekabbelu anhum ahsene ma amilu ve netecavezu an seyyiatihim fi ashabil cenneh, va'des sıdkıllezi kanu yuadun."},
  {id: 4527, sure_no: 17, ayet_no: 17, sure_adi: "Ahkaf", text: "وَالَّذ۪ي قَالَ لِوَالِدَيْهِ اُفٍّ لَكُمَٓا اَتَعِدَانِن۪ٓي اَنْ اُخْرَجَ وَقَدْ خَلَتِ الْقُرُونُ مِنْ قَبْل۪ي وَهُمَا يَسْتَغ۪يثَانِ اللّٰهَ وَيْلَكَ اٰمِنْۗ اِنَّ وَعْدَ اللّٰهِ حَقٌّۚ فَيَقُولُ مَا هٰذَٓا اِلَّٓا اَسَاط۪يرُ الْاَوَّل۪ينَ", translation: "Anne ve babasına: \"Öf be, ikinizden de bıktım! Bunca insan gelip geçtiği halde, hala, beni öldükten sonra diriltileceğimle mi tehdit ediyorsunuz?\" dedi. Onlar, onun için Allah'a dua ederek: \"Yazık sana! Gel iman et. Kuşkusuz Allah'ın sözü haktır.\" dediler. Bunun üzerine o: \"Bu öncekilerin masallarından başka bir şey değildir.\" dedi.", transliteration: "Vellezi kale li valideyhi uffın lekuma e teidanini en uhrece ve kad haletil kurunu min kabli ve huma yestegisanillahe veyleke amin, inne va'dallahi hakk, fe yekulu ma haza illa esatirul evvelin."},
  {id: 4528, sure_no: 18, ayet_no: 18, sure_adi: "Ahkaf", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ حَقَّ عَلَيْهِمُ الْقَوْلُ ف۪ٓي اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِنَ الْجِنِّ وَالْاِنْسِۜ اِنَّهُمْ كَانُوا خَاسِر۪ينَ", translation: "İşte onlar ki, Cinn ve insten, kendilerinden önce gelip geçmiş topluluklarda yürürlükte olan \"söz\" üzerlerine gerçekleşti. Onlar hüsrana uğrayan kimselerdir.", transliteration: "Ulaikellezine hakka aleyhimul kavlu fi umemin kad halet min kablihim minel cinni vel ins, innehum kanu hasirin."},
  {id: 4529, sure_no: 19, ayet_no: 19, sure_adi: "Ahkaf", text: "وَلِكُلٍّ دَرَجَاتٌ مِمَّا عَمِلُواۚ وَلِيُوَفِّيَهُمْ اَعْمَالَهُمْ وَهُمْ لَا يُظْلَمُونَ", translation: "Herkesin yaptığına karşılık bir derecesi vardır. Hiç kimse haksızlığa uğratılmaksızın, yaptıklarının karşılığı tam olarak ödenir.", transliteration: "Ve li kullin derecatun mimma amilu, ve li yuveffiyehum a'malehum ve hum la yuzlemun."},
  {id: 4530, sure_no: 20, ayet_no: 20, sure_adi: "Ahkaf", text: "وَيَوْمَ يُعْرَضُ الَّذ۪ينَ كَفَرُوا عَلَى النَّارِۜ  اَذْهَبْتُمْ طَيِّبَاتِكُمْ ف۪ي حَيَاتِكُمُ الدُّنْيَا وَاسْتَمْتَعْتُمْ بِهَاۚ فَالْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنْتُمْ تَسْتَكْبِرُونَ فِي الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَفْسُقُونَ۟", translation: "Kafirler, ateşe arz edilirlerken, onlara: \"Dünya hayatınızda sahip olduğunuz tüm güzel şeyleri boşa harcadınız. Onlarla sefa sürdünüz. Yeryüzünde haksız yere büyüklük tasladığınız ve doğru yoldan saptığınız için, artık bugün alçaltıcı bir azapla cezalandırılacaksınız.\" denir.", transliteration: "Ve yevme yu'radullezine keferu alen nar, ezhebtum tayyibatikum fi hayatikumud dunya vestemta'tum biha fel yevme tuczevne azabel huni bi ma kuntum testekbirune fil ardı bi gayril hakkı ve bi ma kuntum tefsukun."},
  {id: 4531, sure_no: 21, ayet_no: 21, sure_adi: "Ahkaf", text: "وَاذْكُرْ اَخَا عَادٍۜ اِذْ اَنْذَرَ قَوْمَهُ بِالْاَحْقَافِ وَقَدْ خَلَتِ النُّذُرُ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِه۪ٓ اَلَّا تَعْبُدُٓوا اِلَّا اللّٰهَۜ اِنّ۪ٓي اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظ۪يمٍ", translation: "Ad'ın kardeşini an! Hani Ahkaf'taki halkını uyarmıştı. Ondan önce de ve sonra da \"Allah'tan başkasına kulluk etmeyin! Ben sizin için büyük bir günün azabından korkuyorum.\" diye uyaran nice uyarıcılar gelip geçmişti.", transliteration: "Vezkur eha ad, iz enzere kavmehu bil ahkafi ve kad haletin nuzuru min beyni yedeyhi ve min halfihi ella ta'budu illallah, inni ehafu aleykum azabe yevmin azim."},
  {id: 4532, sure_no: 22, ayet_no: 22, sure_adi: "Ahkaf", text: "قَالُٓوا اَجِئْتَنَا لِتَأْفِكَنَا عَنْ اٰلِهَتِنَاۚ فَأْتِنَا بِمَا تَعِدُنَٓا اِنْ كُنْتَ مِنَ الصَّادِق۪ينَ", translation: "\"Sen bizi ilahlarımızdan vazgeçirmek için mi geldin? Eğer doğru söyleyenlerden isen, haydi bizi tehdit ettiğin şeyi hemen getir.\" dediler.", transliteration: "Kalu eci'tena li te'fikena an alihetina, fe'tina bi ma teıduna in kunte mines sadikin."},
  {id: 4533, sure_no: 23, ayet_no: 23, sure_adi: "Ahkaf", text: "قَالَ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِۘ وَاُبَلِّغُكُمْ مَٓا اُرْسِلْتُ بِه۪ وَلٰكِنّ۪ٓي اَرٰيكُمْ قَوْماً تَجْـهَلُونَ", translation: "\"O bilgi, yalnızca Allah'ın yanındadır. Ben, size, benimle gönderileni bildiriyorum. Ne var ki sizi cahillik eden bir kavim olarak görüyorum.\" dedi.", transliteration: "Kale innemel ilmu indallahi ve ubelligukum ma ursiltu bihi ve lakinni erakum kavmen techelun."},
  {id: 4534, sure_no: 24, ayet_no: 24, sure_adi: "Ahkaf", text: "فَلَمَّا رَاَوْهُ عَارِضاً مُسْتَقْبِلَ اَوْدِيَتِهِمْۙ قَالُوا هٰذَا عَارِضٌ مُمْطِرُنَاۜ بَلْ هُوَ مَا اسْتَعْجَلْتُمْ بِه۪ۜ ر۪يحٌ ف۪يهَا عَذَابٌ اَل۪يمٌۙ", translation: "Onu, vadilerine doğru gelen yoğun bir bulut halinde gördüklerinde: \"Bu, bize yağmur getiren bir buluttur.\" dediler. \"Hayır! O, sizin hemen gelmesini istediğiniz şeydir; acıklı azabı getiren rüzgardır.\"", transliteration: "Fe lemma reevhu aridan mustakbile evdiyetihim kalu haza aridun mumtıruna, bel huve mesta'celtum bih, rihun fiha azabun elim."},
  {id: 4535, sure_no: 25, ayet_no: 25, sure_adi: "Ahkaf", text: "تُدَمِّرُ كُلَّ شَيْءٍ بِاَمْرِ رَبِّهَا فَاَصْبَحُوا لَا يُرٰٓى اِلَّا مَسَاكِنُهُمْۜ  كَذٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِم۪ينَ", translation: "\"Rabb'inin emri ile her şeyi yok edecek!\" Böylece, meskenlerinden başka hiçbir şey görünmez oldu. Biz, suçlu halkı böyle cezalandırırız.", transliteration: "Tudemmiru kulle şey'in bi emri rabbiha fe asbehu la yura illa mesakinuhum kezalike neczil kavmel mucrimin."},
  {id: 4536, sure_no: 26, ayet_no: 26, sure_adi: "Ahkaf", text: "وَلَقَدْ مَكَّنَّاهُمْ ف۪يمَٓا اِنْ مَكَّنَّاكُمْ ف۪يهِ وَجَعَلْنَا لَهُمْ سَمْعاً وَاَبْصَاراً وَاَفْـِٔدَةًۘ  فَمَٓا اَغْنٰى عَنْهُمْ سَمْعُهُمْ وَلَٓا اَبْصَارُهُمْ وَلَٓا اَفْـِٔدَتُهُمْ مِنْ شَيْءٍ اِذْ  كَانُوا يَجْحَدُونَ بِاٰيَاتِ اللّٰهِ وَحَاقَ بِهِمْ مَا كَانُوا بِه۪ يَسْتَهْزِؤُ۫نَ۟", translation: "Ant olsun ki Biz, onlara, size vermediğimiz nice imkanlar vermiştik. Onlara da kulaklar, gözler ve kalpler verdik. Ne var ki işitme ve görmeleri onlara bir yarar sağlamadı. Kalpleri de onlara bir yarar sağlamadı. Çünkü onlar, Allah'ın ayetlerini bile bile reddediyorlardı. Sonunda alaya aldıkları şey onları kuşattı.", transliteration: "Ve lekad mekkenna hum fi ma in mekkennakum fihi ve cealna lehum sem'an ve ebsaren ve ef'ideten fe ma agna anhum sem'uhum ve la ebsaruhum ve la ef'idetuhum min şey'in iz kanu yechadune bi ayatillahi ve haka bihim ma kanu bihi yestehziun."},
  {id: 4537, sure_no: 27, ayet_no: 27, sure_adi: "Ahkaf", text: "وَلَقَدْ اَهْلَكْنَا مَا حَوْلَكُمْ مِنَ الْقُرٰى وَصَرَّفْنَا الْاٰيَاتِ لَعَلَّهُمْ يَرْجِعُونَ", translation: "Ant olsun ki çevrenizdeki beldeleri yok ettik. Oysaki dönerler diye ayetlerimizi açıkça bildirmiştik.", transliteration: "Ve lekad ehlekna ma havlekum minel kura ve sarrafnel ayati leallehum yerciun."},
  {id: 4538, sure_no: 28, ayet_no: 28, sure_adi: "Ahkaf", text: "فَلَوْلَا نَصَرَهُمُ الَّذ۪ينَ اتَّخَذُوا مِنْ دُونِ اللّٰهِ قُرْبَاناً اٰلِهَةًۜ بَلْ ضَلُّوا عَنْهُمْۚ وَذٰلِكَ اِفْكُهُمْ وَمَا كَانُوا يَفْتَرُونَ", translation: "Allah'ın yanı sıra, kendilerini O'na yaklaştırsınlar diye edindikleri ilahlar, onlara yardım etseydi ya! Aksine onları yüzüstü bırakıp kayboldular. Bu; onların, uydurdukları yalandan ibaret bir şeydi.", transliteration: "Fe lev la nasare humullezinettehazu min dunillahi kurbanen aliheh, bel dallu anhum, ve zalike ifkuhum ve ma kanu yefterun."},
  {id: 4539, sure_no: 29, ayet_no: 29, sure_adi: "Ahkaf", text: "وَاِذْ صَرَفْنَٓا اِلَيْكَ نَفَراً مِنَ الْجِنِّ يَسْتَمِعُونَ الْقُرْاٰنَۚ  فَلَمَّا حَضَرُوهُ قَالُٓوا اَنْصِتُواۚ فَلَمَّا قُضِيَ وَلَّوْا اِلٰى قَوْمِهِمْ مُنْذِر۪ينَ", translation: "Bir grup cinni Kur'an'ı dinlemeleri için sana yönlendirmiştik. Onlar, gelip Kur'an'ı dinlemeye başladıklarında birbirlerine, \"sessiz olun, dinleyin.\" dediler. Sonra da dinlemeleri bitince kendi halklarını uyarmak için geri döndüler.", transliteration: "Ve iz sarefna ileyke neferen minel cinni yestemiunel kur'an, fe lemma hadaruhu kalu ensıtu, fe lemma kudıye vellev ila kavmihim munzirin."},
  {id: 4540, sure_no: 30, ayet_no: 30, sure_adi: "Ahkaf", text: "قَالُوا يَا قَوْمَنَٓا اِنَّا سَمِعْنَا كِتَاباً اُنْزِلَ مِنْ بَعْدِ مُوسٰى مُصَدِّقاً لِمَا بَيْنَ يَدَيْهِ يَهْد۪ٓي اِلَى الْحَقِّ وَاِلٰى طَر۪يقٍ مُسْتَق۪يمٍ", translation: "\"Ey halkımız! Kuşkusuz biz, Musa'dan sonra indirilen ve kendinden öncekilerini onaylayan; gerçeği ve dosdoğru yolu gösteren bir Kitap dinledik.\" dediler.", transliteration: "Kalu ya kavmena inna semi'na kitaben unzile min ba'di musa musaddikan li ma beyne yedeyhi yehdi ilel hakkı ve ila tarikın mustekim."},
  {id: 4541, sure_no: 31, ayet_no: 31, sure_adi: "Ahkaf", text: "يَا قَوْمَنَٓا اَج۪يبُوا دَاعِيَ اللّٰهِ وَاٰمِنُوا بِه۪ يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُجِرْكُمْ مِنْ عَذَابٍ اَل۪يمٍ", translation: "\"Ey halkımız! Allah'ın davetçisine uyun ve Allah'a iman edin ki suçlarınızı bağışlasın ve sizi acıklı bir azaptan korusun.\"", transliteration: "Ya kavmena ecibu daiyallahi ve aminu bihi yagfir lekum min zunubikum ve yucirkum min azabin elim."},
  {id: 4542, sure_no: 32, ayet_no: 32, sure_adi: "Ahkaf", text: "وَمَنْ لَا يُجِبْ دَاعِيَ اللّٰهِ فَلَيْسَ بِمُعْجِزٍ فِي الْاَرْضِ وَلَيْسَ لَهُ مِنْ دُونِه۪ٓ اَوْلِيَٓاءُۜ  اُو۬لٰٓئِكَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "Her kim, Allah'a çağıran kimsenin çağrısına uymazsa, bilsin ki Allah'ı yeryüzünde aciz bırakacak değildir. Ve onun Allah'tan başka velileri de yoktur. İşte onlar apaçık bir sapkınlık içindedirler.", transliteration: "Ve men la yucib daiyallahi fe leyse bi mu'cizin fil ardı ve leyse lehu min dunihi evliyau, ulaike fi dalalin mubin."},
  {id: 4543, sure_no: 33, ayet_no: 33, sure_adi: "Ahkaf", text: "اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ وَلَمْ يَعْيَ بِخَلْقِهِنَّ بِقَادِرٍ عَلٰٓى اَنْ يُحْيِيَ الْمَوْتٰىۜ بَلٰٓى اِنَّهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Onlar, gökleri ve yeri yaratan ve yaratmaktan yorulmamış olan Allah'ın, ölüleri de diriltebileceği gerçeğini anlamıyorlar mı? Evet! Kuşkusuz O, Her Şeye Güç Yetiren'dir.", transliteration: "E ve lem yerev ennallahellezi halakas semavati vel arda ve lem ya'ye bi halkıhinne bi kadirin ala en yuhyiyel mevta, bela innehu ala kulli şey'in kadir."},
  {id: 4544, sure_no: 34, ayet_no: 34, sure_adi: "Ahkaf", text: "وَيَوْمَ يُعْرَضُ الَّذ۪ينَ كَفَرُوا عَلَى النَّارِۜ  اَلَيْسَ هٰذَا بِالْحَقِّۜ قَالُوا بَلٰى وَرَبِّنَاۜ قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ", translation: "Kafirler, ateşle karşı karşıya kaldıkları gün, \"Bu, gerçek değil miymiş?\" dedi. Onlar: \"Rabb'imize ant olsun ki gerçekmiş.\" dediler. \"O halde kafirliğinizden dolayı azabı tadın.\" dedi.", transliteration: "Ve yevme yu'redullezine keferu alen nar,e leyse haza bil hakk, kalu bela ve rabbina, kale fe zukul azabe bi ma kuntum tekfurun."},
  {id: 4545, sure_no: 35, ayet_no: 35, sure_adi: "Ahkaf", text: "فَاصْبِرْ كَمَا صَبَرَ اُو۬لُوا الْعَزْمِ مِنَ الرُّسُلِ وَلَا تَسْتَعْجِلْ لَهُمْۜ  كَاَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوعَدُونَۙ لَمْ يَلْبَثُٓوا اِلَّا سَاعَةً مِنْ نَهَارٍۜ  بَلَاغٌۚ  فَهَلْ يُهْلَكُ اِلَّا الْقَوْمُ الْفَاسِقُونَ", translation: "O halde Resullerden kararlılık sahibi olanların sabrettikleri gibi sen de sabret! Onlar için aceleci olma. Onlar, kendilerine uyarısı yapılan şeyi gördükleri zaman, dünyada sanki gündüzün bir saati kadar kalmış gibi olurlar. Bu, bir bildirimdir. Artık doğru yoldan sapmış halktan başkası yok edilmez.", transliteration: "Fasbir kema sabere ulul azmi miner rusuli ve la testa'cil lehum, ke ennehum yevme yerevne ma yuadune lem yelbesu illa saaten min nehar, belag, fe hel yuhleku illel kavmul fasikun."},
  {id: 4546, sure_no: 1, ayet_no: 1, sure_adi: "Muhammed", text: "اَلَّذ۪ينَ كَـفَرُوا وَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ اَضَلَّ اَعْمَالَهُمْ", translation: "Kafir olanların ve Allah'ın yolundan alıkoyanların yaptıklarını Allah boşa çıkaracaktır.", transliteration: "Ellezine keferu ve saddu an sebilillahi edalle a'malehum."},
  {id: 4547, sure_no: 2, ayet_no: 2, sure_adi: "Muhammed", text: "وَالَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَاٰمَنُوا بِمَا نُزِّلَ عَلٰى مُحَمَّدٍ وَهُوَ الْحَقُّ مِنْ رَبِّهِمْۙ  كَفَّرَ عَنْهُمْ سَيِّـَٔاتِهِمْ وَاَصْلَحَ بَالَهُمْ", translation: "İman eden, salihatı yapanlar ve Rabb'leri tarafından Muhammed'e indirilen \"Hakk'a\" inanan kimselere gelince, Allah onların kötülüklerini örtüp durumlarını düzeltecektir.", transliteration: "Vellezine amenu ve amilus salihati ve amenu bi ma nuzzile ala muhammedin ve huvel hakku min rabbihim keffere anhum seyyiatihim ve asleha balehum."},
  {id: 4548, sure_no: 3, ayet_no: 3, sure_adi: "Muhammed", text: "ذٰلِكَ بِاَنَّ الَّذ۪ينَ كَفَرُوا اتَّبَعُوا الْبَاطِلَ وَاَنَّ الَّذ۪ينَ اٰمَنُوا اتَّبَعُوا الْحَقَّ مِنْ رَبِّهِمْۜ   كَذٰلِكَ يَضْرِبُ اللّٰهُ لِلنَّاسِ اَمْثَالَهُمْ", translation: "Bu, Kafirlerin, \"Batıl'a\" uymaları, İman Edenlerin ise Rabb'lerinden gelen \"Hakk'a\" uymaları nedeniyledir. İşte Allah, insanlara kendi durumlarını böyle örnek verir.", transliteration: "Zalike bi ennellezine keferuttebeul batıle ve ennellezine amenuttebeul hakka min rabbihim, kezalike yadribullahu lin nasi emsalehum."},
  {id: 4549, sure_no: 4, ayet_no: 4, sure_adi: "Muhammed", text: "فَاِذَا لَق۪يتُمُ الَّذ۪ينَ كَفَرُوا فَضَرْبَ الرِّقَابِۜ حَتّٰٓى اِذَٓا اَثْخَنْتُمُوهُمْ فَشُدُّوا الْوَثَاقَۙ فَاِمَّا مَناًّ بَعْدُ وَاِمَّا فِدَٓاءً حَتّٰى تَضَعَ الْحَرْبُ اَوْزَارَهَاۚۛ ذٰلِكَۜۛ وَلَوْ يَشَٓاءُ اللّٰهُ لَانْتَصَرَ مِنْهُمْۙ وَلٰكِنْ لِيَبْلُوَ۬ا بَعْضَكُمْ بِبَعْضٍۜ وَالَّذ۪ينَ قُتِلُوا ف۪ي سَب۪يلِ اللّٰهِ فَلَنْ يُضِلَّ اَعْمَالَهُمْ", translation: "Kafirlerle savaşa giriştiğiniz zaman, boyunlarını vurun. Güçlerini yok edince, bağı sıkıca bağlayın. Ondan sonra bir bağışlama olarak veya fidye karşılığında savaş sona erince onları serbest bırakın. Eğer Allah isteseydi onlardan savaşsız da intikam alırdı. Fakat böyle olması sizi birbirinizle denemek içindir. Allah yolunda öldürülenlere gelince, Allah onların yaptıklarını asla karşılıksız bırakmaz.", transliteration: "Fe iza lekitumullezine keferu fe darber rikab, hatta iza eshantumuhum fe şuddul vesak, fe imma mennen ba'du ve imma fidaen hatta tedaal harbu evzareha, zalik, ve lev yeşaullahu lentasara minhum ve lakin li yebluve ba'dakum bi ba'd, vellezine kutilu fi sebilillahi fe len yudille a'malehum."},
  {id: 4550, sure_no: 5, ayet_no: 5, sure_adi: "Muhammed", text: "سَيَهْد۪يهِمْ وَيُصْلِحُ بَالَهُمْۚ", translation: "Onlara doğru yolu gösterecek ve durumlarını düzeltecektir.", transliteration: "Seyehdihim ve yuslihu balehum."},
  {id: 4551, sure_no: 6, ayet_no: 6, sure_adi: "Muhammed", text: "وَيُدْخِلُهُمُ الْجَنَّةَ عَرَّفَهَا لَهُمْ", translation: "Onları, dünyada kendilerine tarif ettiği Cennet'e yerleştirecektir.", transliteration: "Ve yudhıluhumul cennete arrefeha lehum."},
  {id: 4552, sure_no: 7, ayet_no: 7, sure_adi: "Muhammed", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ تَنْصُرُوا اللّٰهَ يَنْصُرْكُمْ وَيُثَبِّتْ اَقْدَامَكُمْ", translation: "Ey İman Edenler! Eğer siz Allah'a yardım ederseniz, O da size yardım eder. Ayaklarınızı sabit kılar.", transliteration: "Ya eyyuhellezine amenu in tensurullahe yensurkum ve yusebbit akdamekum."},
  {id: 4553, sure_no: 8, ayet_no: 8, sure_adi: "Muhammed", text: "وَالَّذ۪ينَ كَفَرُوا فَتَعْساً لَهُمْ وَاَضَلَّ اَعْمَالَهُمْ", translation: "Kafirler ise yok olsunlar! Allah, onların yaptıklarını boşa çıkaracaktır.", transliteration: "Vellezine keferu fe tağsen lehumve edalle a'malehum."},
  {id: 4554, sure_no: 9, ayet_no: 9, sure_adi: "Muhammed", text: "ذٰلِكَ بِاَنَّهُمْ كَرِهُوا مَٓا اَنْزَلَ اللّٰهُ فَاَحْبَطَ اَعْمَالَهُمْ", translation: "Bu, onların Allah'ın indirdiği şeyi kötü görmelerinden dolayıdır. Bundan dolayı Allah, onların yaptıklarını boşa çıkarmıştır.", transliteration: "Zalike bi ennehum kerihu ma enzelallahu fe ahbeta a'malehum."},
  {id: 4555, sure_no: 10, ayet_no: 10, sure_adi: "Muhammed", text: "اَفَلَمْ يَس۪يرُوا فِي الْاَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذ۪ينَ مِنْ قَبْلِهِمْۜ دَمَّرَ اللّٰهُ عَلَيْهِمْۘ وَلِلْكَافِر۪ينَ اَمْثَالُهَا", translation: "Hem onlar, yeryüzünde gezip dolaşmazlar mı? Baksalar ya! Kendilerinden öncekilerin sonlarının nasıl olduğuna! Allah, onları yerle bir etti. Onun bir benzeri de Kafirler içindir.", transliteration: "E fe lem yesiru fil ardı fe yenzuru keyfe kane akıbetullezine min kablihim, demmerallahu aleyhim ve lil kafirine emsaluha."},
  {id: 4556, sure_no: 11, ayet_no: 11, sure_adi: "Muhammed", text: "ذٰلِكَ بِاَنَّ اللّٰهَ مَوْلَى الَّذ۪ينَ اٰمَنُوا وَاَنَّ الْكَافِر۪ينَ لَا مَوْلٰى لَهُمْ۟", translation: "Allah iman edenlerin mevlasıdır. Kafirlerin ise hiçbir mevlası yoktur.", transliteration: "Zalike bi ennallahe mevlellezine amenu ve ennel kafirine la mevla lehum."},
  {id: 4557, sure_no: 12, ayet_no: 12, sure_adi: "Muhammed", text: "اِنَّ اللّٰهَ يُدْخِلُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ وَالَّذ۪ينَ كَفَرُوا يَتَمَتَّعُونَ وَيَأْكُلُونَ كَمَا تَأْكُلُ الْاَنْعَامُ وَالنَّارُ مَثْوًى لَهُمْ", translation: "Allah, iman edip salihatı yapanları, içinden ırmaklar akan Cennetlere yerleştirir. Kafirler ise her şeyden yararlanıp, hayvanların yediği gibi yerler. Onların varacakları yer ateştir.", transliteration: "İnnallahe yudhılullezine amenu ve amilus salihati cennatin tecri min tahtihel enhar, vellezine keferu yetemetteune ve ye'kulune kema te'kulul en'amu ven naru mesven lehum."},
  {id: 4558, sure_no: 13, ayet_no: 13, sure_adi: "Muhammed", text: "وَكَاَيِّنْ مِنْ قَرْيَةٍ هِيَ اَشَدُّ قُوَّةً مِنْ قَرْيَتِكَ الَّت۪ٓي اَخْرَجَتْكَۚ اَهْلَكْنَاهُمْ فَلَا نَاصِرَ لَهُمْ", translation: "Güç bakımından, seni yurdundan çıkaranlardan daha güçlü nice kent halkını yok ettik. Onlara yardım edecek hiç kimse de olmadı.", transliteration: "Ve keeyyin min karyetin hiye eşeddu kuvveten min karyetikelleti ahrecetke, ehleknahum fe la nasıra lehum."},
  {id: 4559, sure_no: 14, ayet_no: 14, sure_adi: "Muhammed", text: "اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِنْ رَبِّه۪ كَمَنْ زُيِّنَ لَهُ سُٓوءُ عَمَلِه۪ وَاتَّبَعُٓوا اَهْوَٓاءَهُمْ", translation: "Rabbinden gelen kanıt içeren apaçık bilgiyi izleyen kimse, kötü işleri kendisine güzel gösterilen ve hevalarına uyan kimse ile bir olur mu?", transliteration: "E fe men kane ala beyyinetin min rabbihi ke men zuyyine lehu suu amelihi vettebeu ehvaehum."},
  {id: 4560, sure_no: 15, ayet_no: 15, sure_adi: "Muhammed", text: "مَثَلُ الْجَنَّةِ الَّت۪ي وُعِدَ الْمُتَّقُونَۜ ف۪يهَٓا اَنْهَارٌ مِنْ مَٓاءٍ غَيْرِ اٰسِنٍۚ وَاَنْهَارٌ مِنْ لَبَنٍ لَمْ يَتَغَيَّرْ طَعْمُهُۚ وَاَنْهَارٌ مِنْ خَمْرٍ لَذَّةٍ لِلشَّارِب۪ينَۚ وَاَنْهَارٌ مِنْ عَسَلٍ مُصَفًّىۜ  وَلَهُمْ ف۪يهَا مِنْ كُلِّ الثَّمَرَاتِ وَمَغْفِرَةٌ مِنْ رَبِّهِمْۜ   كَمَنْ هُوَ خَالِدٌ فِي النَّارِ وَسُقُوا مَٓاءً حَم۪يماً فَقَطَّعَ اَمْعَٓاءَهُمْ", translation: "Takva sahiplerine söz verilen Cennet, şöyledir: İçinde; kokusu ve tadı değişmeyen sudan nehirler, tadı bozulmayan sütten nehirler, içenlere zevk veren hamrdan nehirler ve saf baldan nehirler bulunur. Orada, onlar için her türlü meyve bulunur. Onlara, Rabb'lerinden bağışlanma vardır. Bu kimseler, ateşte devamlı kalacak olan ve sıcak kaynar su içirilen, bundan dolayı da bağırsakları parça parça olan kimselerle bir olur mu hiç?", transliteration: "Meselul cennetilleti vuidel muttekun, fiha enharun min main gayri asin, ve enharun min lebenin lem yetegayyer ta'muh, ve enharun min hamrin lezzetin liş şaribin, ve enharun min aselin musaffa, ve lehum fiha min kullis semerati ve magfiretun min rabbihim, ke men huve halidun fin nari ve suku maen hamimen fe kattaa em'aehum."},
  {id: 4561, sure_no: 16, ayet_no: 16, sure_adi: "Muhammed", text: "وَمِنْهُمْ مَنْ يَسْتَمِــعُ اِلَيْكَۚ حَتّٰٓى اِذَا خَرَجُوا مِنْ عِنْدِكَ قَالُوا لِلَّذ۪ينَ اُو۫تُوا الْعِلْمَ مَاذَا قَالَ اٰنِفاً۠ اُو۬لٰٓئِكَ الَّذ۪ينَ طَبَعَ اللّٰهُ عَلٰى قُلُوبِهِمْ وَاتَّبَعُٓوا اَهْوَٓاءَهُمْ", translation: "Münafıklardan kimisi de seni dinliyormuş gibi gözükür. Senin yanından ayrıldıktan sonra, kendilerine ilim verilenlere: \"O, biraz önce ne dedi?\" dediler. İşte onlar, Allah'ın kalplerini mühürledikleri ve hevalarına tabi olan kimselerdir.", transliteration: "Ve minhum men yestemiu ileyke, hatta iza harecu min indike kalu lillezine utul ilme maza kale anifa, ulaikellezine tabaallahu ala kulubihim vettebeu ehvaehum."},
  {id: 4562, sure_no: 17, ayet_no: 17, sure_adi: "Muhammed", text: "وَالَّذ۪ينَ اهْتَدَوْا زَادَهُمْ هُدًى وَاٰتٰيهُمْ تَقْوٰيهُمْ", translation: "Yöneltildikleri doğru yolda olanlara gelince, onlara doğru yolları pekiştirildi ve onlara takvalarını verdi.", transliteration: "Vellezinehtedev zadehum huden ve atahum takvahum."},
  {id: 4563, sure_no: 18, ayet_no: 18, sure_adi: "Muhammed", text: "فَهَلْ يَنْظُرُونَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةًۚ فَقَدْ جَٓاءَ اَشْرَاطُهَاۚ فَاَنّٰى لَهُمْ اِذَا جَٓاءَتْهُمْ ذِكْرٰيهُمْ", translation: "İlle de o Sa'at'in ansızın gelmesini mi bekliyorlar? Oysa onun belirtileri gelmiştir. O gelecek olduktan sonra, öğüdü dinlemek onlara ne yarar sağlar ki?", transliteration: "Fe hel yenzurune illes saate en te'tiyehum bagteh, fe kad cae eşratuha, fe enna lehum iza caethum zikrahum."},
  {id: 4564, sure_no: 19, ayet_no: 19, sure_adi: "Muhammed", text: "فَاعْلَمْ اَنَّهُ لَٓا اِلٰهَ اِلَّا اللّٰهُ وَاسْتَغْفِرْ لِذَنْبِكَ وَلِلْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِۜ وَاللّٰهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوٰيكُمْ۟", translation: "O halde, Allah'tan başka ilah olmadığını bil. Kendi suçların için, inanan erkekler ve kadınlar için bağışlanma dile. Allah, dönüp dolaşacağınız yeri ve varıp duracağınız yeri bilir.", transliteration: "Fa'lem ennehu la ilahe illallahu vestagfir li zenbike ve lil mu'minine vel mu'minat, valla hu ya'lemu mutekallebekum ve mesvakum."},
  {id: 4565, sure_no: 20, ayet_no: 20, sure_adi: "Muhammed", text: "وَيَقُولُ الَّذ۪ينَ اٰمَنُوا لَوْلَا نُزِّلَتْ سُورَةٌۚ فَاِذَٓا اُنْزِلَتْ سُورَةٌ مُحْكَمَةٌ وَذُكِرَ ف۪يهَا الْقِتَالُۙ رَاَيْتَ الَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ يَنْظُرُونَ اِلَيْكَ نَظَرَ الْمَغْشِيِّ عَلَيْهِ مِنَ الْمَوْتِۜ فَاَوْلٰى لَهُمْۚ", translation: "İman Edenler, \"Bir sure indirilmeli değil miydi\" derler. Sonra savaştan söz eden, hükmü kesin bir sure indirilince, kalpleri hastalıklı kimselerin, sana, sanki üzerlerine ölüm hali çökmüş kimseler gibi baygın baygın baktıklarını görürsün. Oysa onlar için uygun olanı;", transliteration: "Ve yekulullezine amenu lev la nuzzilet sureh, fe iza unzilet suretun muhkemetun ve zukire fi hel kıtalu re'eytellezine fi kulubihim maradun yanzurune ileyke nazaral magşiyyi aleyhi minel mevt, fe evla lehum."},
  {id: 4566, sure_no: 21, ayet_no: 21, sure_adi: "Muhammed", text: "طَاعَةٌ وَقَوْلٌ مَعْرُوفٌ۠ فَاِذَا عَزَمَ الْاَمْرُ۠ فَلَوْ صَدَقُوا اللّٰهَ لَكَانَ خَيْراً لَهُمْۚ", translation: "İtaat etmek ve ma'ruf söz söylemektir. Fakat iş kesinleşince Allah'a verdikleri sözde dursalardı elbette bu onlar için hayırlı olan olurdu.", transliteration: "Taatun ve kavlun ma'ruf, fe iza azemel emr, fe lev sadekullahe le kane hayran lehum."},
  {id: 4567, sure_no: 22, ayet_no: 22, sure_adi: "Muhammed", text: "فَهَلْ عَسَيْتُمْ اِنْ تَوَلَّيْتُمْ اَنْ تُفْسِدُوا فِي الْاَرْضِ وَتُقَطِّعُٓوا اَرْحَامَكُمْ", translation: "Yüz çevirmekle yeryüzünde fesadın çıkmasına ve rahmet bağlarının paramparça olmasına sebep olacağınızın farkında mısınız?", transliteration: "Fe hel aseytum in tevelleytum en tufsidu fil ardı ve tukattıu erhamekum."},
  {id: 4568, sure_no: 23, ayet_no: 23, sure_adi: "Muhammed", text: "اُو۬لٰٓئِكَ الَّذ۪ينَ لَعَنَهُمُ اللّٰهُ فَاَصَمَّهُمْ وَاَعْمٰٓى اَبْصَارَهُمْ", translation: "İşte onlar, Allah'ın lanetlediği kimselerdir. Böylece onların kulaklarını sağır, gözlerini kör yaptı.", transliteration: "Ulaikellezine leanehumullahu fe esammehum ve a'ma ebsarehum."},
  {id: 4569, sure_no: 24, ayet_no: 24, sure_adi: "Muhammed", text: "اَفَلَا يَتَدَبَّرُونَ الْقُرْاٰنَ اَمْ عَلٰى قُلُوبٍ اَقْفَالُهَا", translation: "Onlar, Kur'an üzerinde düşünmüyorlar mı? Yoksa kalpleri üzerinde kilitleri mi var?", transliteration: "E fe la yetedebberunel kur'ane em ala kulubin akfaluha."},
  {id: 4570, sure_no: 25, ayet_no: 25, sure_adi: "Muhammed", text: "اِنَّ الَّذ۪ينَ ارْتَدُّوا عَلٰٓى اَدْبَارِهِمْ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَىۙ الشَّيْطَانُ سَوَّلَ لَهُمْۜ وَاَمْلٰى لَهُمْ", translation: "Kendilerine doğru yol açıkça belli olduktan sonra, ona sırt dönenleri, şeytan aldattı ve onları boş ümitlere yöneltti.", transliteration: "İnnellezinerteddu ala edbarihim min ba'di ma tebeyyene lehumul hudeş şeytanu sevvele lehum ve emla lehum."},
  {id: 4571, sure_no: 26, ayet_no: 26, sure_adi: "Muhammed", text: "ذٰلِكَ بِاَنَّهُمْ قَالُوا لِلَّذ۪ينَ كَرِهُوا مَا نَزَّلَ اللّٰهُ سَنُط۪يعُكُمْ ف۪ي بَعْضِ الْاَمْرِۚ وَاللّٰهُ يَعْلَمُ اِسْرَارَهُمْ", translation: "Bu, onların, Allah'ın indirdiğini beğenmeyen kimselere: \"Bazı işlerde size uyacağız.\" demeleri nedeniyledir. Allah, onların gizlediklerini biliyor.", transliteration: "Zalike bi ennehum kalu lillezine kerihu ma nezzelallahu senutiukum fi ba'dil emr, vallahu ya'lemu israrehum."},
  {id: 4572, sure_no: 27, ayet_no: 27, sure_adi: "Muhammed", text: "فَكَيْفَ اِذَا تَوَفَّتْهُمُ الْمَلٰٓئِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَاَدْبَارَهُمْ", translation: "Melekler onları vefat ettirirken, yüzlerine ve arkalarına vurduklarında onların durumları ne olacak?", transliteration: "Fe keyfe iza teveffethumul melaiketu yadribune vucuhehum ve edbarehum."},
  {id: 4573, sure_no: 28, ayet_no: 28, sure_adi: "Muhammed", text: "ذٰلِكَ بِاَنَّهُمُ اتَّبَعُوا مَٓا اَسْخَطَ اللّٰهَ وَكَرِهُوا رِضْوَانَهُ فَاَحْبَطَ اَعْمَالَهُمْ۟", translation: "Bu, onların, Allah'ı öfkelendiren şeylere uymaları ve O'nun rızasını gereksiz görmelerindendir. Böylece onların yaptıkları işleri boşa çıkardı.", transliteration: "Zalike bi ennehumuttebeu ma eshatallahe ve kerihu rıdvanehu fe ahbeta a'malehum."},
  {id: 4574, sure_no: 29, ayet_no: 29, sure_adi: "Muhammed", text: "اَمْ حَسِبَ الَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ اَنْ لَنْ يُخْرِجَ اللّٰهُ اَضْغَانَهُمْ", translation: "Yoksa kalplerinde hastalık olanlar, Allah'ın, içlerindeki kini ortaya çıkarmayacağını mı sandılar?", transliteration: "Em hasibellezine fi kulubihim maradun en len yuhricallahu adganehum."},
  {id: 4575, sure_no: 30, ayet_no: 30, sure_adi: "Muhammed", text: "وَلَوْ نَشَٓاءُ لَاَرَيْنَاكَهُمْ فَلَعَرَفْتَهُمْ بِس۪يمٰيهُمْۜ وَلَتَعْرِفَنَّهُمْ ف۪ي لَحْنِ الْقَوْلِۜ وَاللّٰهُ يَعْلَمُ اَعْمَالَكُمْ", translation: "Eğer isteseydik onları sana tanıttırırdık. Sen de onları simalarından tanırdın. Yine de sen; onları, konuşma tarzlarından tanırsın. Allah, yaptıklarınızı bilir.", transliteration: "Ve lev neşau le ereynakehum fe le areftehum bi simahum ve le ta'rifennehum fi lahnil kavl, vallahu ya'lemu a'malekum."},
  {id: 4576, sure_no: 31, ayet_no: 31, sure_adi: "Muhammed", text: "وَلَنَبْلُوَنَّكُمْ حَتّٰى نَعْلَمَ الْمُجَاهِد۪ينَ مِنْكُمْ وَالصَّابِر۪ينَۙ وَنَبْلُوَ۬ا اَخْبَارَكُمْ", translation: "İçinizden cihad edenleri ve sabredenleri ortaya çıkarıncaya kadar sizi sınava tabi tutarız. Ve haberlerinizi de sınava tabi tutacağız.", transliteration: "Ve le nebluvennekum hatta na'lemel mucahidine minkum ves sabirine ve nebluve ahbarekum."},
  {id: 4577, sure_no: 32, ayet_no: 32, sure_adi: "Muhammed", text: "اِنَّ الَّذ۪ينَ كَفَرُوا وَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ وَشَٓاقُّوا الرَّسُولَ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدٰىۙ لَنْ يَضُرُّوا اللّٰهَ شَيْـٔاًۜ وَسَيُحْبِطُ اَعْمَالَهُمْ", translation: "Kafirler, Allah'ın yolundan alıkoyanlar, doğru yol açıkça belli olduktan sonra Resul'e karşı gelen kimseler, hiçbir şekilde ve asla Allah'a zarar veremezler. Allah, onların yaptıklarını boşa çıkaracaktır.", transliteration: "İnnellezine keferu ve saddu an sebilillahi ve şakkur resule min ba'di ma tebeyyene lehumul huda len yedurrullahe şey'a, ve seyuhbitu a'malehum."},
  {id: 4578, sure_no: 33, ayet_no: 33, sure_adi: "Muhammed", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَ وَلَا تُبْطِلُٓوا اَعْمَالَكُمْ", translation: "Ey iman edenler! Allah'a itaat edin ve Resul'e itaat edin. Yaptıklarınızı boşa çıkarmayın.", transliteration: "Ya eyyuhellezine amenu etiullahe ve etiur resule ve la tubtılu a'malekum."},
  {id: 4579, sure_no: 34, ayet_no: 34, sure_adi: "Muhammed", text: "اِنَّ الَّذ۪ينَ كَفَرُوا وَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ ثُمَّ مَاتُوا وَهُمْ كُفَّارٌ فَلَنْ يَغْفِرَ اللّٰهُ لَهُمْ", translation: "Kafir olanlar, Allah'ın yolundan alıkoyanlar; sonra da kafir olarak ölen kimseleri, Allah, asla bağışlamayacaktır.", transliteration: "İnnellezine keferu ve saddu an sebilillahi summe matu ve hum kuffarun fe len yagfirallahu lehum."},
  {id: 4580, sure_no: 35, ayet_no: 35, sure_adi: "Muhammed", text: "فَلَا تَهِنُوا وَتَدْعُٓوا اِلَى السَّلْمِۗ وَاَنْتُمُ الْاَعْلَوْنَۗ وَاللّٰهُ مَعَكُمْ وَلَنْ يَتِرَكُمْ اَعْمَالَكُمْ", translation: "O halde gevşemeyin ve siz üstünken barışa çağırmayın. Allah, sizinle beraberdir. Yaptıklarınızı asla boşa çıkarmayacaktır.", transliteration: "Fe la tehinu ve ted'u iles selmi ve entumul a'levne vallahu meakum ve len yetirekum a'malekum."},
  {id: 4581, sure_no: 36, ayet_no: 36, sure_adi: "Muhammed", text: "اِنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَلَهْوٌۜ وَاِنْ تُؤْمِنُوا وَتَتَّقُوا يُؤْتِكُمْ اُجُورَكُمْ وَلَا يَسْـَٔلْكُمْ اَمْوَالَكُمْ", translation: "Dünya hayatı yalnızca bir oyun ve eğlencedir. Eğer iman eder ve takva sahibi olursanız, size ödülleriniz verilir. Ve sizden mallarınızı istemez.", transliteration: "İnnemel hayatud dunya laibun ve lehv, ve in tu'minu ve tetteku yu'tikum ucurekum ve la yes'elkum emvalekum."},
  {id: 4582, sure_no: 37, ayet_no: 37, sure_adi: "Muhammed", text: "اِنْ يَسْـَٔلْكُمُوهَا فَيُحْفِكُمْ تَبْخَلُوا وَيُخْرِجْ اَضْغَانَكُمْ", translation: "Eğer sizden onu isteyip de sizi zorlasaydı, cimrilik ederdiniz. Böylece hoşnutsuzluğunuzu açığa çıkarırdı.", transliteration: "İn yes'elkumuha fe yuhfikum tebhalu ve yuhric adganekum."},
  {id: 4583, sure_no: 38, ayet_no: 38, sure_adi: "Muhammed", text: "هَٓا اَنْتُمْ هٰٓؤُ۬لَٓاءِ تُدْعَوْنَ لِتُنْفِقُوا ف۪ي سَب۪يلِ اللّٰهِۚ فَمِنْكُمْ مَنْ يَبْخَلُۚ وَمَنْ يَبْخَلْ فَاِنَّمَا يَبْخَلُ عَنْ نَفْسِه۪ۜ وَاللّٰهُ الْغَنِيُّ وَاَنْتُمُ الْفُقَـرَٓاءُۚ وَاِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْماً غَيْرَكُمْۙ ثُمَّ لَا يَكُونُٓوا اَمْثَالَكُمْ", translation: "İşte siz böylesiniz. Allah yolunda infak etmeye çağrılırsınız ancak sizden bir kısmınız cimrilik yapar. Kim cimrilik yaparsa, aslında kendi aleyhinde cimrilik yapmış olur. Allah zengindir, siz ise yoksulsunuz. Eğer yüz çevirirseniz, sizin yerinize başka bir halk getirir. Onlar sizin gibi olmazlar.", transliteration: "Ha entum haulai tud'avne li tunfiku fi sebilillah, fe minkum men yebhal, ve men yebhal fe innema yebhalu an nefsih, vallahul ganiyyu ve entumul fukarau, ve in tetevellev yestebdil kavmen gayrekum summe la yekunu emsalekum."},
  {id: 4584, sure_no: 1, ayet_no: 1, sure_adi: "Fetih", text: "اِنَّا فَتَحْنَا لَكَ فَتْحاً مُب۪يناًۙ", translation: "Biz, sana apaçık bir fethin yolunu açtık.", transliteration: "İnna fetahna leke fethan mubina."},
  {id: 4585, sure_no: 2, ayet_no: 2, sure_adi: "Fetih", text: "لِيَغْفِرَ لَكَ اللّٰهُ مَا تَقَدَّمَ مِنْ ذَنْبِكَ وَمَا تَاَخَّرَ وَيُتِمَّ نِعْمَتَهُ عَلَيْكَ وَيَهْدِيَكَ صِرَاطاً مُسْتَق۪يماًۙ", translation: "Allah, senin geçmiş ve sonraki suçlarını bağışlasın ve sana nimetini tamamlasın ve seni dosdoğru yola iletsin diye.", transliteration: "Li yagfire lekallahu ma tekaddeme min zenbike ve ma teahhare ve yutimme ni'metehu aleyke ve yehdiyeke sıratan mustekima."},
  {id: 4586, sure_no: 3, ayet_no: 3, sure_adi: "Fetih", text: "وَيَنْصُرَكَ اللّٰهُ نَصْراً عَز۪يزاً", translation: "Ve Allah, sana çok üstün bir zaferle yardım etsin diye.", transliteration: "Ve yansurekallahu nasran aziza."},
  {id: 4587, sure_no: 4, ayet_no: 4, sure_adi: "Fetih", text: "هُوَ الَّـذ۪ٓي اَنْزَلَ السَّك۪ينَةَ ف۪ي قُلُوبِ الْمُؤْمِن۪ينَ لِيَزْدَادُٓوا ا۪يمَاناً مَعَ ا۪يمَانِهِمْۜ وَلِلّٰهِ جُنُودُ السَّمٰوَاتِ وَالْاَرْضِۜ وَكَانَ اللّٰهُ عَل۪يماً حَك۪يماًۙ", translation: "İmanları artsın diye, İman Edenler'in kalbine sükunet indiren O'dur. Göklerin ve yerin güçleri Allah'ındır. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Huvellezi enzeles sekinete fi kulubil mu'minine li yezdadu imanen mea imanihim, ve lillahi cunudus semavati vel ard, ve kanallahu alimen hakima."},
  {id: 4588, sure_no: 5, ayet_no: 5, sure_adi: "Fetih", text: "لِيُدْخِلَ الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَا وَيُكَفِّرَ عَنْهُمْ سَيِّـَٔاتِهِمْۜ وَكَانَ ذٰلِكَ عِنْدَ اللّٰهِ فَوْزاً عَظ۪يماًۙ", translation: "Mü'min erkekleri ve Mü'min kadınları, içinde sürekli kalmak üzere, içinden nehirler akan Cennetlere koymak ve kötülüklerini örtmek içindir. İşte Allah'ın yanında büyük kurtuluş budur.", transliteration: "Li yudhilel mu'minine vel mu'minati cennatin tecri min tahtihel enharu halidine fiha ve yukeffire anhum seyyiatihim, ve kane zalike indallahi fevzen azima."},
  {id: 4589, sure_no: 6, ayet_no: 6, sure_adi: "Fetih", text: "وَيُعَذِّبَ الْمُنَافِق۪ينَ وَالْمُنَافِقَاتِ وَالْمُشْرِك۪ينَ وَالْمُشْرِكَاتِ الظَّٓانّ۪ينَ بِاللّٰهِ ظَنَّ السَّوْءِۜ عَلَيْهِمْ دَٓائِرَةُ السَّوْءِۚ وَغَضِبَ اللّٰهُ عَلَيْهِمْ وَلَعَنَهُمْ وَاَعَدَّ لَهُمْ جَهَنَّمَۜ وَسَٓاءَتْ مَص۪يراً", translation: "Allah; hakkında kötü zanda bulunan münafık erkeklere ve kadınlara, Müşrik erkek ve kadınlara azap etsin. Kötü zanları onların üzerlerine dönsün. Allah, onlara kızdı ve onları lanetledi. Ve onlar için Cehennem'i hazırladı. Ne kötü bir varış yeri.", transliteration: "Ve yuazzibel munafikine vel munafikati vel muşrikine vel muşrikatiz zannine billahi zannes sev'i aleyhim dairetus sev'i, ve gadiballahu aleyhim ve leanehum ve eadde lehum cehennem, ve saet masira."},
  {id: 4590, sure_no: 7, ayet_no: 7, sure_adi: "Fetih", text: "وَلِلّٰهِ جُنُودُ السَّمٰوَاتِ وَالْاَرْضِۜ وَكَانَ اللّٰهُ عَز۪يزاً حَك۪يماً", translation: "Göklerin ve yerin güçleri Allah'ındır. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve lillahi cunudus semavati vel ard, ve kanallahu azizen hakima."},
  {id: 4591, sure_no: 8, ayet_no: 8, sure_adi: "Fetih", text: "اِنَّٓا اَرْسَلْنَاكَ شَاهِداً وَمُبَشِّراً وَنَذ۪يراًۙ", translation: "Biz, seni bir tanık, haber verici ve uyarıcı olarak gönderdik.", transliteration: "İnna erselnake şahiden ve mubeşşiren ve nezira."},
  {id: 4592, sure_no: 9, ayet_no: 9, sure_adi: "Fetih", text: "لِتُؤْمِنُوا بِاللّٰهِ وَرَسُولِه۪ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُۜ وَتُسَبِّحُوهُ بُكْرَةً وَاَص۪يلاً", translation: "Allah'a ve Resul'üne iman etmeniz, O'na destek olmanız, O'na saygı göstermeniz ve O'nu sabah akşam tesbih etmeniz için.", transliteration: "Li tu'minu billahi ve resulihi ve tuazziruhu ve tuvakkıruh, ve tusebbihuhu bukreten ve asila."},
  {id: 4593, sure_no: 10, ayet_no: 10, sure_adi: "Fetih", text: "اِنَّ الَّذ۪ينَ يُبَايِعُونَكَ اِنَّمَا يُبَايِعُونَ اللّٰهَۜ يَدُ اللّٰهِ فَوْقَ اَيْد۪يهِمْۚ فَمَنْ نَكَثَ فَاِنَّمَا يَنْكُثُ عَلٰى نَفْسِه۪ۚ وَمَنْ اَوْفٰى بِمَا عَاهَدَ عَلَيْهُ اللّٰهَ فَسَيُؤْت۪يهِ اَجْراً عَظ۪يماً۟", translation: "Sana bi'at edenler, aslında Allah'a bi'at etmişlerdir. Allah'ın eli onların eli üzerindedir. Kim bağlılığını bozarsa ancak kendi aleyhine bozmuş olur. Kim de Allah'a verdiği söze bağlı kalırsa, Allah, ona büyük bir ödül verecektir.", transliteration: "İnnellezine yubayiuneke innema yubayiunallah, yedullahi fevka eydihim, fe men nekese fe innema yenkusu ala nefsih, ve men evfa bi ma ahede aleyhullahe fe se yu'tihi ecren azima."},
  {id: 4594, sure_no: 11, ayet_no: 11, sure_adi: "Fetih", text: "سَيَقُولُ لَكَ الْمُخَلَّفُونَ مِنَ الْاَعْرَابِ شَغَلَتْنَٓا اَمْوَالُنَا وَاَهْلُونَا فَاسْتَغْفِرْ لَنَاۚ يَقُولُونَ بِاَلْسِنَتِهِمْ مَا لَيْسَ ف۪ي قُلُوبِهِمْۜ قُلْ فَمَنْ يَمْلِكُ لَكُمْ مِنَ اللّٰهِ شَيْـٔاً اِنْ اَرَادَ بِكُمْ ضَراًّ اَوْ اَرَادَ بِكُمْ نَفْعاًۜ بَلْ كَانَ اللّٰهُ بِمَا تَعْمَلُونَ خَب۪يراً", translation: "Bedevi Araplardan geri kalanlar, sana: \"Mallarımız ve ailemiz bizim seninle gelmemize engel oldu. Haydi, Allah'tan bağışlanmamızı dile.\" diyecekler. Onlar, kalplerinde olmayanı dilleri ile söylüyorlar. De ki: \"Eğer Allah, size bir zarar veya yarar dilerse, buna kim engel olabilir?\" Bilakis, Allah, yaptıklarınızdan haberdardır.", transliteration: "Se yekulu lekel muhallefune minel a'rabi şegaletna emvaluna ve ehluna festagfir lena, yekulune bi elsinetihim ma leyse fi kulubihim, kul fe men yemliku lekum minallahi şey'en in erade bikum darren ev erade bikum nef'a, bel kanallahu bi ma ta'melune habira."},
  {id: 4595, sure_no: 12, ayet_no: 12, sure_adi: "Fetih", text: "بَلْ ظَنَنْتُمْ اَنْ لَنْ يَنْقَلِبَ الرَّسُولُ وَالْمُؤْمِنُونَ اِلٰٓى اَهْل۪يهِمْ اَبَداً وَزُيِّنَ ذٰلِكَ ف۪ي قُلُوبِكُمْ وَظَنَنْتُمْ ظَنَّ السَّوْءِۚ وَكُنْتُمْ قَوْماً بُوراً", translation: "Hayır! Siz, Resul'ün ve İman Edenlerin asla ailelerine dönemeyeceklerini sanmıştınız. Bu zan, kalplerinize güzel göründü. Kötü bir zanla, zanda bulundunuz. Siz, yok olmayı hak eden bir halk oldunuz.", transliteration: "Bel zanentum en len yenkaliber resulu vel mu'minune ila ehlihim ebeden ve zuyyine zalike fi kulubikum ve zanentum zannes sev'i ve kuntum kavmen bura."},
  {id: 4596, sure_no: 13, ayet_no: 13, sure_adi: "Fetih", text: "وَمَنْ لَمْ يُؤْمِنْ بِاللّٰهِ وَرَسُولِه۪ فَاِنَّٓا اَعْتَدْنَا لِلْكَافِر۪ينَ سَع۪يراً", translation: "Kim Allah'a ve Resul'üne iman etmezse, bilsin ki Kafirler için alevli bir ateş hazırladık.", transliteration: "Ve men lem yu'min billahi ve resulihi fe inna a'tedna lil kafirine saira."},
  {id: 4597, sure_no: 14, ayet_no: 14, sure_adi: "Fetih", text: "وَلِلّٰهِ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ يَغْفِرُ لِمَنْ يَشَٓاءُ وَيُعَذِّبُ مَنْ يَشَٓاءُۜ وَكَانَ اللّٰهُ غَفُوراً رَح۪يماً", translation: "Göklerin ve yerin egemenliği Allah'a aittir. O, hak edeni bağışlar, hak edene azap eder. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve lillahi mulkus semavati vel ard, yagfiru li men yeşau ve yuazzibu men yeşau, ve kanallahu gafuren rahima."},
  {id: 4598, sure_no: 15, ayet_no: 15, sure_adi: "Fetih", text: "سَيَقُولُ الْمُخَلَّفُونَ اِذَا انْطَلَقْتُمْ اِلٰى مَغَانِمَ لِتَأْخُذُوهَا ذَرُونَا نَتَّبِعْكُمْۚ يُر۪يدُونَ اَنْ يُبَدِّلُوا كَلَامَ اللّٰهِۜ قُلْ لَنْ تَتَّبِعُونَا كَذٰلِكُمْ قَالَ اللّٰهُ مِنْ قَبْلُۚ فَسَيَقُولُونَ بَلْ تَحْسُدُونَنَاۜ بَلْ كَانُوا لَا يَفْقَهُونَ اِلَّا قَل۪يلاً", translation: "Geri bırakılanlar, ganimetleri almak için gittiğinizde: \"Müsaade edin sizinle gelelim.\" diyecekler. Allah'ın sözünü değiştirmek istiyorlar. De ki: \"Siz, asla bizimle gelemezsiniz. Allah, bu hükmü daha önce buyurmuştu.\" O zaman da: \"Hayır, siz bizi kıskanıyorsunuz.\" diyecekler. Doğrusu onlar anlayışı kıt kimselerdir.", transliteration: "Se yekulul muhallefune izentalaktum ila meganime li te'huzuha zeruna nettebi'kum, yuridune en yubeddilu kelamallah, kul len tettebiuna kezalikum kalallahu min kabl, fe se yekulune bel tahsudunena, bel kanu la yefkahune illa kalila."},
  {id: 4599, sure_no: 16, ayet_no: 16, sure_adi: "Fetih", text: "قُلْ لِلْمُخَلَّف۪ينَ مِنَ الْاَعْرَابِ سَتُدْعَوْنَ اِلٰى قَوْمٍ اُو۬ل۪ي بَأْسٍ شَد۪يدٍ تُقَاتِلُونَهُمْ اَوْ يُسْلِمُونَۚ فَاِنْ تُط۪يعُوا يُؤْتِكُمُ اللّٰهُ اَجْراً حَسَناًۚ وَاِنْ تَتَوَلَّوْا كَمَا تَوَلَّيْتُمْ مِنْ قَبْلُ يُعَذِّبْكُمْ عَذَاباً اَل۪يماً", translation: "Bedevi Araplardan, geride kalmayı tercih edenlere de ki: \"Yakında çok güçlü bir halkla savaşmaya çağrılacaksınız ya onlarla savaşırsınız ya da onlar teslim olurlar. Bundan sonra, eğer bu çağrıya uyarsanız Allah size en iyi karşılığı verir. Ama daha önce yan çizdiğiniz gibi yine yan çizecek olursanız, size acıklı bir azapla, azap eder.\"", transliteration: "Kul lil muhallefine minel a'rabi setud'avne ila kavmin uli be'sin şedidin tukatilunehum ev yuslimun, fe in tutiu yu'tikumullahu ecren hasena, ve in tetevellev kema tevelleytum min kablu yuazzibkum azaben elima."},
  {id: 4600, sure_no: 17, ayet_no: 17, sure_adi: "Fetih", text: "لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَلَا عَلَى الْاَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَر۪يضِ حَرَجٌۜ وَمَنْ يُطِـعِ اللّٰهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۚ وَمَنْ يَتَوَلَّ يُعَذِّبْهُ عَذَاباً اَل۪يماً۟", translation: "Köre bir sınırlama yoktur. Sakat olana bir sınırlama yoktur. Hasta olana bir sınırlama yoktur. Kim Allah'a ve Resul'üne itaat ederse, Allah, onları içinden ırmaklar akan Cennetlere koyar. Kim de yan çizerse, ona acıklı bir azapla azap eder.", transliteration: "Leyse alel a'ma haracun ve la alel a'reci haracun ve la alel maridı harac, ve men yutııllahe ve resulehu yudhılhu cennatin tecri min tahtihel enhar, ve men yetevelle yuazzibhu azaben elima."},
  {id: 4601, sure_no: 18, ayet_no: 18, sure_adi: "Fetih", text: "لَقَدْ رَضِيَ اللّٰهُ عَنِ الْمُؤْمِن۪ينَ اِذْ يُبَايِعُونَكَ تَحْتَ الشَّجَرَةِ فَعَلِمَ مَا ف۪ي قُلُوبِهِمْ فَاَنْزَلَ السَّك۪ينَةَ عَلَيْهِمْ وَاَثَابَهُمْ فَتْحاً قَر۪يباًۙ", translation: "Ant olsun ki, o ağacın altında sana bi'at ettikleri zaman Allah, İman Edenler'den razı oldu. Onların kalplerinde olanı biliyordu. Onların üzerlerine huzur ve dinginlik indirdi. Onlara yakın bir fetih nasip etti.", transliteration: "Lekad radiyallahu anil mu'minine iz yubayiuneke tahteş şecereti fe alime ma fi kulubihim fe enzeles sekinete aleyhim ve esabehum fethan kariba."},
  {id: 4602, sure_no: 19, ayet_no: 19, sure_adi: "Fetih", text: "وَمَغَانِمَ كَث۪يرَةً يَأْخُذُونَهَاۜ وَكَانَ اللّٰهُ عَز۪يزاً حَك۪يماً", translation: "Onlara, elde edecekleri pek çok ganimet nasip etmiştir. Allah, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve meganime kesireten ye'huzuneha, ve kanallahu azizen hakima."},
  {id: 4603, sure_no: 20, ayet_no: 20, sure_adi: "Fetih", text: "وَعَدَكُمُ اللّٰهُ مَغَانِمَ كَث۪يرَةً تَأْخُذُونَهَا فَعَجَّلَ لَكُمْ هٰذِه۪ وَكَفَّ اَيْدِيَ النَّاسِ عَنْكُمْۚ وَلِتَكُونَ اٰيَةً لِلْمُؤْمِن۪ينَ وَيَهْدِيَكُمْ صِرَاطاً مُسْتَق۪يماًۙ", translation: "Allah size, alacağınız pek çok ganimet sözü verdi. Şimdilik size bunları bahşetti. İnsanların ellerini sizden çekti. İman Edenler'e ayet olsun ve sizi dosdoğru yola iletsin diye.", transliteration: "Vaadekumullahu meganime kesireten te'huzuneha fe accele lekum hazihi ve keffe eydiyen nasi ankum, ve li tekune ayeten lil mu'minine ve yehdiyekum sıratan mustekima."},
  {id: 4604, sure_no: 21, ayet_no: 21, sure_adi: "Fetih", text: "وَاُخْرٰى لَمْ تَقْدِرُوا عَلَيْهَا قَدْ اَحَاطَ اللّٰهُ بِهَاۜ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يراً", translation: "Ve henüz elde edemediğiniz ama Allah'ın kuşattığı başka şeyler de var. Allah, Her Şeye Güç Yetiren'dir.", transliteration: "Ve uhra lem takdiru aleyha kad ehatallahu biha, ve kanallahu ala kulli şey'in kadira."},
  {id: 4605, sure_no: 22, ayet_no: 22, sure_adi: "Fetih", text: "وَلَوْ قَاتَلَكُمُ الَّذ۪ينَ كَفَرُوا لَوَلَّوُا الْاَدْبَارَ ثُمَّ لَا يَجِدُونَ وَلِياًّ وَلَا نَص۪يراً", translation: "Eğer, Kafirler sizinle savaşsalardı, arkalarını dönüp kaçarlardı. Sonra da bir veli ve yardımcı bulamazlardı.", transliteration: "Ve lev katelekumullezine keferu le vellevul edbare summe la yecidune veliyyen ve la nasira."},
  {id: 4606, sure_no: 23, ayet_no: 23, sure_adi: "Fetih", text: "سُنَّةَ اللّٰهِ الَّت۪ي قَدْ خَلَتْ مِنْ قَبْلُۚ وَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْد۪يلاً", translation: "Bu Allah'ın öteden beri süregelen yasasıdır. Allah'ın yasasında asla bir değişiklik bulamazsın.", transliteration: "Sunnetellahilleti kad halet min kabl, ve len tecide li sunnetillahi tebdila."},
  {id: 4607, sure_no: 24, ayet_no: 24, sure_adi: "Fetih", text: "وَهُوَ الَّذ۪ي كَفَّ اَيْدِيَهُمْ عَنْكُمْ وَاَيْدِيَكُمْ عَنْهُمْ بِبَطْنِ مَكَّةَ مِنْ بَعْدِ اَنْ اَظْفَرَكُمْ عَلَيْهِمْۜ وَكَانَ اللّٰهُ بِمَا تَعْمَلُونَ بَص۪يراً", translation: "Sizi, Mekke'nin merkezinde onlara galip kıldıktan sonra, onların ellerini sizden ve sizin ellerinizi de onlardan çeken O'dur. Allah, yaptıklarınızı görendir.", transliteration: "Ve huvellezi keffe eydiyehum ankum ve eydiyekum anhum bi batni mekkete min ba'di en azferekum aleyhim ve kanallahu bi ma ta'melune basira."},
  {id: 4608, sure_no: 25, ayet_no: 25, sure_adi: "Fetih", text: "هُمُ الَّذ۪ينَ كَفَرُوا وَصَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ وَالْهَدْيَ مَعْكُوفاً اَنْ يَبْلُغَ مَحِلَّهُۜ وَلَوْلَا رِجَالٌ مُؤْمِنُونَ وَنِسَٓاءٌ مُؤْمِنَاتٌ لَمْ تَعْلَمُوهُمْ اَنْ تَطَؤُ۫هُمْ فَتُص۪يبَكُمْ مِنْهُمْ مَعَرَّةٌ بِغَيْرِ عِلْمٍۚ لِيُدْخِلَ اللّٰهُ ف۪ي رَحْمَتِه۪ مَنْ يَشَٓاءُۚ لَوْ تَزَيَّلُوا لَعَذَّبْنَا الَّذ۪ينَ كَفَرُوا مِنْهُمْ عَذَاباً اَل۪يماً", translation: "Onlar, Kafirlerdir. Sizi Mescid-i Haram'dan alıkoyan, ayrılmış kurbanların yerine ulaşmasını engelleyen kimselerdir. Eğer Mü'min erkeklerden ve kadınlardan olup da kendilerini henüz tanımadığınız için bilmeden onlara zarar vermeniz ve böylece vebale girmeniz söz konusu olmasaydı, onlarla savaşmanıza izin verilirdi. Allah, dilediğini rahmetine alır. Eğer birbirlerinden ayrılmış olsalardı elbette onlardan Kafir olanları acı veren bir azapla cezalandırırdık.", transliteration: "Humullezine keferu ve saddukum anil mescidil harami vel hedye ma'kufen en yebluga mahıllehu, ve lev la ricalun mu'minune ve nisaun mu'minatun lem ta'lemuhum en tetauhum fe tusibekum minhum maarratun bi gayri ilm, li yudhılallahu fi rahmetihi men yeşau, lev tezeyyelu le azzebnellezine keferu minhum azaben elima."},
  {id: 4609, sure_no: 26, ayet_no: 26, sure_adi: "Fetih", text: "اِذْ جَعَلَ الَّذ۪ينَ كَفَرُوا ف۪ي قُلُوبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجَاهِلِيَّةِ فَاَنْزَلَ اللّٰهُ سَك۪ينَتَهُ عَلٰى رَسُولِه۪ وَعَلَى الْمُؤْمِن۪ينَ وَاَلْزَمَهُمْ كَلِمَةَ التَّقْوٰى وَكَانُٓوا اَحَقَّ بِهَا وَاَهْلَهَاۜ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَل۪يماً۟", translation: "Kafirler, küçük görme taassubunu, cahiliye taassubunu kalplerinde taşıyorlardı. Allah da Resul'ünün ve Mü'minlerin üzerine dinginlik indirdi. Onları takva sözüne bağlı kıldı. Zaten onlar buna layık ve ehildiler. Allah, Her Şeyi En İyi Bilen'dir.", transliteration: "İz cealellezine keferu fi kulubihimul hamiyyete hamiyyetel cahiliyyeti fe enzelallahu sekinetehu ala resulihi ve alel mu'minine ve elzemehum kelimetet takva ve kanu e hakka biha ve ehleha ve kanallahu bi kulli şey'in alima."},
  {id: 4610, sure_no: 27, ayet_no: 27, sure_adi: "Fetih", text: "لَقَدْ صَدَقَ اللّٰهُ رَسُولَهُ الرُّءْيَا بِالْحَقِّۚ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرَامَ اِنْ شَٓاءَ اللّٰهُ اٰمِن۪ينَۙ مُحَلِّق۪ينَ رُؤُ۫سَكُمْ وَمُقَصِّر۪ينَۙ لَا تَخَافُونَۜ فَعَلِمَ مَا لَمْ تَعْلَمُوا فَجَعَلَ مِنْ دُونِ ذٰلِكَ فَتْحاً قَر۪يباً", translation: "Ant olsun ki Allah, Resul'ünün rüyasını hakk ile doğruladı. Allah dilerse, Mescid-i Haram'a başlarınız tıraş edilmiş ve saçlarınız kısaltılmış olarak korkmadan güven içinde gireceksiniz. Allah, sizin bilmediğiniz şeyleri biliyordu. Bundan başka size yakın bir fetih takdir etti.", transliteration: "Lekad sadakallahu resulehur ru'ya bil hakk, le tedhulunnel mescidel harame inşaallahu aminine muhallikine ruusekum ve mukassırine la tehafun, fe alime ma lem ta'lemu fe ceale min duni zalike fethan kariba."},
  {id: 4611, sure_no: 28, ayet_no: 28, sure_adi: "Fetih", text: "هُوَ الَّـذ۪ٓي اَرْسَلَ رَسُولَهُ بِالْهُدٰى وَد۪ينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدّ۪ينِ كُلِّه۪ۜ وَكَفٰى بِاللّٰهِ شَه۪يداًۜ", translation: "Gerçek dini açıkça ortaya koymak için Resul'ünü doğru yol göstericisi ve gerçek din ile gönderen O'dur. Tanık olarak Allah yeter.", transliteration: "Huvellezi ersele resulehu bil huda ve dinil hakkı li yuzhirehu aled dini kullih, ve kefa billahi şehida."},
  {id: 4612, sure_no: 29, ayet_no: 29, sure_adi: "Fetih", text: "مُحَمَّدٌ رَسُولُ اللّٰهِۜ وَالَّذ۪ينَ مَعَهُٓ  اَشِدَّٓاءُ عَلَى الْكُفَّارِ رُحَمَٓاءُ بَيْنَهُمْ تَرٰيهُمْ رُكَّعاً سُجَّداً يَبْتَغُونَ فَضْلاً مِنَ اللّٰهِ وَرِضْوَاناًۘ س۪يمَاهُمْ ف۪ي وُجُوهِهِمْ مِنْ اَثَرِ السُّجُودِۜ ذٰلِكَ مَثَلُهُمْ فِي التَّوْرٰيةِۚۛ وَمَثَلُهُمْ فِي الْاِنْج۪يلِ۠ۛ   كَزَرْعٍ اَخْرَجَ شَطْـَٔهُ۫ فَاٰزَرَهُ فَاسْتَغْلَظَ فَاسْتَوٰى عَلٰى سُوقِه۪ يُعْجِبُ الزُّرَّاعَ لِيَغ۪يظَ بِهِمُ الْكُفَّارَۜ وَعَدَ اللّٰهُ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْهُمْ مَغْفِرَةً وَاَجْراً عَظ۪يماً", translation: "Muhammed, Allah'ın Resul'üdür. Onunla beraber olanlar, Kafirlere karşı sert, birbirlerine çok merhametlidirler. Onları; ruku ederken, secde ederken ve Allah'tan bağışlanma ve hoşnutluk isterlerken görürsün. Onların belirtileri, yüzlerindeki secde izleridir. İşte bunlar, onların Tevrat'taki örnekleridir. İncil'deki örnekleri de filizini yarıp çıkaran, sonra onu güçlendirerek kalınlaşıp, gövdesi üzerinde yükselen ekin gibidir. Bu, ekincilerin hoşuna gider. Allah, Kafirlere onlarla üzüntü vermektedir. Allah, iman edip salihatı yapan kimselere bağışlanma ve büyük bir ödül sözü vermiştir.", transliteration: "Muhammedun resulullah, vellezine meahu eşiddau alel kuffari ruhamau beynehum terahum rukkean succeden yebtegune fadlen minallahi ve rıdvanen simahum fi vucuhihim min eseris sucud, zalike meseluhum fit tevrat, ve meseluhum fil incil, ke zer'in ahrece şat'ehu fe azerehu festagleza festeva ala sukıhi yu'cibuz zurraa, li yagiza bihimul kuffar, vaadallahullezine amenu ve amilus salihati minhum magfireten ve ecren azima."},
  {id: 4613, sure_no: 1, ayet_no: 1, sure_adi: "Hucurat", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُقَدِّمُوا بَيْنَ يَدَيِ اللّٰهِ وَرَسُولِه۪ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ سَم۪يعٌ عَل۪يمٌ", translation: "Ey İman Edenler! Allah'ın ve Resul'ünün iki eli arasında öne geçmeyin. Allah için takva sahibi olun. Kuşkusuz Allah, Her Şeyi Duyan'dır, Her Şeyi Bilen'dir.", transliteration: "Ya eyyuhellezine amenu la tukaddimu beyne yedeyillahi ve resulihi vettekullah, innallahe semiun alim."},
  {id: 4614, sure_no: 2, ayet_no: 2, sure_adi: "Hucurat", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَرْفَعُٓوا اَصْوَاتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَلَا تَجْهَرُوا لَهُ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ اَنْ تَحْبَطَ اَعْمَالُكُمْ وَاَنْتُمْ لَا تَشْعُرُونَ", translation: "Ey İman Edenler! Sesinizi, Nebi'nin sesinden daha fazla yükseltmeyin. Birbirinizle bağrışarak konuştuğunuz gibi, Nebi'yle yüksek sesle konuşmayın. Yoksa farkında olmadan yaptıklarınız boşa gider.", transliteration: "Ya eyyuhellezine amenu la terfeu asvatekum fevka savtin nebiyyi ve la techeru lehu bil kavli ke cehri ba'dıkum li ba'dın en tahbeta a'malukum ve entum la teş'urun."},
  {id: 4615, sure_no: 3, ayet_no: 3, sure_adi: "Hucurat", text: "اِنَّ الَّذ۪ينَ يَغُضُّونَ اَصْوَاتَهُمْ عِنْدَ رَسُولِ اللّٰهِ اُو۬لٰٓئِكَ الَّذ۪ينَ امْتَحَنَ اللّٰهُ قُلُوبَهُمْ لِلتَّقْوٰىۜ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ عَظ۪يمٌ", translation: "Allah'ın Resul'ünün yanında kısık sesle konuşanlar; işte onlar, Allah'ın takva için kalplerini sınav ettiği kimselerdir. Onlar için bağışlanma ve büyük ödül vardır.", transliteration: "İnnellezine yeguddune asvatehum inde resulillahi ulaikel lezinemtehanallahu kulubehum lit takva lehum magfiretun ve ecrun azim."},
  {id: 4616, sure_no: 4, ayet_no: 4, sure_adi: "Hucurat", text: "اِنَّ الَّذ۪ينَ يُنَادُونَكَ مِنْ وَرَٓاءِ الْحُجُرَاتِ اَكْثَرُهُمْ لَا يَعْقِلُونَ", translation: "Sana odaların dışından seslenenlerin çoğu, düşüncesiz kimselerdir.", transliteration: "İnnellezine yunaduneke min verail hucurati ekseruhum la ya'kılun."},
  {id: 4617, sure_no: 5, ayet_no: 5, sure_adi: "Hucurat", text: "وَلَوْ اَنَّهُمْ صَبَرُوا حَتّٰى تَخْرُجَ اِلَيْهِمْ لَكَانَ خَيْراً لَهُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "Eğer onlar, sen yanlarına çıkıncaya kadar sabredip bekleselerdi, elbette daha iyi olurdu. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ve lev ennehum saberu hatta tahruce ileyhim le kane hayren lehum, vallahu gafurun rahim."},
  {id: 4618, sure_no: 6, ayet_no: 6, sure_adi: "Hucurat", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنْ جَٓاءَكُمْ فَاسِقٌ بِنَبَأٍ۬ فَتَبَيَّنُٓوا اَنْ تُص۪يبُوا قَوْماً بِجَهَالَةٍ فَتُصْبِحُوا عَلٰى مَا فَعَلْتُمْ نَادِم۪ينَ", translation: "Ey İman Edenler! Eğer fasık bir kimse, size bir haber getirirse doğru olup olmadığını araştırın. Yoksa bir topluma cahilce kötülük edersiniz de sonra yaptığınız şeye pişman olursunuz.", transliteration: "Ya eyyuhellezine amenu in caekum fasikun bi nebein fe tebeyyenu en tusibu kavmen bi cehaletin fe tusbihu ala ma fealtum nadimin."},
  {id: 4619, sure_no: 7, ayet_no: 7, sure_adi: "Hucurat", text: "وَاعْلَمُٓوا اَنَّ ف۪يكُمْ رَسُولَ اللّٰهِۜ لَوْ يُط۪يعُكُمْ ف۪ي كَث۪يرٍ مِنَ الْاَمْرِ لَعَنِتُّمْ وَلٰكِنَّ اللّٰهَ حَبَّبَ اِلَيْكُمُ الْا۪يمَانَ وَزَيَّـنَهُ ف۪ي قُلُوبِكُمْ وَكَرَّهَ اِلَيْكُمُ الْكُفْرَ وَالْفُسُوقَ وَالْعِصْيَانَۜ  اُو۬لٰٓئِكَ هُمُ الرَّاشِدُونَۙ", translation: "Allah'ın Resul'ünün aranızda olduğunu unutmayın. Eğer birçok işte size uysaydı kesinlikle sıkıntıya düşerdiniz. Fakat Allah, size imanı sevdirdi ve kalplerinizi onunla güzelleştirdi; küfürden, fasıklıktan ve isyandan nefret ettirdi. İşte bunlardır seçimlerini doğru yapmış olanlar.", transliteration: "Va'lemu enne fikum resulallah, lev yutiukum fi kesirin minel emri le anittum ve lakinnallahe habbebe ileykumul imane ve zeyyenehu fi kulubikum, ve kerrehe ileykumul kufre vel fusuka vel isyan, ulaike humur raşidun."},
  {id: 4620, sure_no: 8, ayet_no: 8, sure_adi: "Hucurat", text: "فَضْلاً مِنَ اللّٰهِ وَنِعْمَةًۜ وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Bu, Allah'tan bir lütuf ve nimettir. Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Fadlen minallahi ve ni'meh, vallahu alimun hakim."},
  {id: 4621, sure_no: 9, ayet_no: 9, sure_adi: "Hucurat", text: "وَاِنْ طَٓائِفَتَانِ مِنَ الْمُؤْمِن۪ينَ اقْتَتَلُوا فَاَصْلِحُوا بَيْنَهُمَاۚ فَاِنْ بَغَتْ اِحْدٰيهُمَا عَلَى الْاُخْرٰى فَقَاتِلُوا الَّت۪ي تَبْغ۪ي حَتّٰى تَف۪ٓيءَ اِلٰٓى اَمْرِ اللّٰهِۚ فَاِنْ فَٓاءَتْ فَاَصْلِحُوا بَيْنَهُمَا بِالْعَدْلِ وَاَقْسِطُواۜ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِط۪ينَ", translation: "Eğer Mü'minlerden iki grup birbirleriyle vuruşurlarsa, hemen aralarını düzeltin. Eğer ikisinden biri, diğerine saldırırsa, saldırgan olanlarla, Allah'ın emirlerine uygun davranıncaya kadar savaşın. Eğer böyle davranmaktan vazgeçerlerse o zaman adaletle aralarını düzeltin ve hakkaniyetli olun. Allah, haktan yana olanları sever.", transliteration: "Ve in taifetani minel mu'mininektetelu fe aslihu beyne huma, fe in begat ihdahuma alel uhra fe katilulleti tebgi hatta tefie ila emrillah, fe in faet fe aslihu beynehuma bil adli ve aksitu, innallahe yuhıbbul muksitin."},
  {id: 4622, sure_no: 10, ayet_no: 10, sure_adi: "Hucurat", text: "اِنَّمَا الْمُؤْمِنُونَ اِخْوَةٌ فَاَصْلِحُوا بَيْنَ اَخَوَيْكُمْ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُرْحَمُونَ۟", translation: "İman Edenler ancak kardeştir. Öyleyse kardeşlerinizin arasını düzeltin. Allah için takva sahibi olun. Umulur ki böylece merhamet olunursunuz.", transliteration: "İnnemel mu'minune ihvetun fe aslihu beyne ehaveykum vettekullahe leallekum turhamun."},
  {id: 4623, sure_no: 11, ayet_no: 11, sure_adi: "Hucurat", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا يَسْخَرْ قَوْمٌ مِنْ قَوْمٍ عَسٰٓى اَنْ يَكُونُوا خَيْراً مِنْهُمْ وَلَا نِسَٓاءٌ مِنْ نِسَٓاءٍ عَسٰٓى اَنْ يَكُنَّ خَيْراً مِنْهُنَّۚ وَلَا تَلْمِزُٓوا اَنْفُسَكُمْ وَلَا تَنَابَزُوا بِالْاَلْقَابِۜ بِئْسَ الِاسْمُ الْفُسُوقُ بَعْدَ الْا۪يمَانِۚ وَمَنْ لَمْ يَتُبْ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ", translation: "Ey İman Edenler! Bir halk başka bir halkla alay etmesin. Belki alay edilenler, alay edenlerden daha hayırlıdırlar. Kadınlar da başka kadınlarla alay etmesinler. Belki alay edilen kadınlar, alay edenlerden daha iyidirler. Birbirinizde kusur aramayın. Birbirinize kötü lakaplar takmayın. İmandan sonra fasık olarak nitelendirilmek ne kötüdür. Kim tevbe etmezse işte onlar zalimdirler.", transliteration: "Ya eyyuhellezine amenu la yeshar kavmun min kavmin asa en yekunu hayren minhum ve la nisaun min nisain asa en yekunne hayren minhunn, ve la telmizu enfusekum ve la tenabezu bil elkab, bi'sel ismul fusuku ba'del iman, ve men lem yetub, fe ulaike humuz zalimun."},
  {id: 4624, sure_no: 12, ayet_no: 12, sure_adi: "Hucurat", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اجْتَنِبُوا كَث۪يراً مِنَ الظَّنِّۚ اِنَّ بَعْضَ الظَّنِّ اِثْمٌ وَلَا تَجَسَّسُوا وَلَا يَغْتَبْ بَعْضُكُمْ بَعْضاًۜ اَيُحِبُّ اَحَدُكُمْ اَنْ يَأْكُلَ لَحْمَ اَخ۪يهِ مَيْتاً فَكَرِهْتُمُوهُۜ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ تَـوَّابٌ رَح۪يمٌ", translation: "Ey İman Edenler! Zannın birçoğundan sakının. Kuşkusuz bazı zanlar günahtır. Birbirinizin kusurlarını araştırmayın. Bir kısmınız, bir kısmınızın gıybetini yapmasın. Hiç sizden biriniz, ölmüş kardeşinin etini yemekten hoşlanır mı? Elbette ondan tiksinirsiniz. Öyleyse Allah için takva sahibi olun. Kuşkusuz Allah, Tevbeleri Kabul Eden'dir, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyyyuhellezine amenuctenibu kesiran minez zanni, inne ba'daz zanni ismun, ve la tecessesu ve la yagteb ba'dukum ba'da, e yuhıbbu ehadukum en ye'kule lahme ahihi meyten fe kerihtumuh, vettekullah, innallahe tevvabun rahim."},
  {id: 4625, sure_no: 13, ayet_no: 13, sure_adi: "Hucurat", text: "يَٓا اَيُّهَا النَّاسُ اِنَّا خَلَقْنَاكُمْ مِنْ ذَكَرٍ وَاُنْثٰى وَجَعَلْنَاكُمْ شُعُوباً وَقَـبَٓائِلَ لِتَعَارَفُواۜ اِنَّ اَكْرَمَكُمْ عِنْدَ اللّٰهِ اَتْقٰيكُمْۜ اِنَّ اللّٰهَ عَل۪يمٌ خَب۪يرٌ", translation: "Ey insanlar! Sizi bir erkek ve bir dişiden yarattık. Birbirinizle tanışmanız için sizi kabilelere ve sülalelere ayırdık. Allah'ın yanında en kerim olanınız, en çok takva sahibi olanınızdır. Kuşkusuz Allah, Her Şeyi Bilen'dir, Her Şeyden Haberdar'dır.", transliteration: "Ya eyyuhen nasu inna halaknakum min zekerin ve unsa ve cealnakum şuuben ve kabaile li tearefu, inne ekremekum indallahi etkakum, innallahe alimun habir."},
  {id: 4626, sure_no: 14, ayet_no: 14, sure_adi: "Hucurat", text: "قَالَتِ الْاَعْرَابُ اٰمَنَّاۜ قُلْ لَمْ تُؤْمِنُوا وَلٰكِنْ قُولُٓوا اَسْلَمْنَا وَلَمَّا يَدْخُلِ الْا۪يمَانُ ف۪ي قُلُوبِكُمْۜ وَاِنْ تُط۪يعُوا اللّٰهَ وَرَسُولَهُ لَا يَلِتْكُمْ مِنْ اَعْمَالِكُمْ شَيْـٔاًۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Bedevi Araplar, \"iman ettik.\" dediler. De ki: \"Siz iman etmediniz, fakat teslim olduk, deyin. Çünkü henüz iman kalbinize girmedi. Eğer Allah'a ve Resul'üne itaat ederseniz, O, yaptıklarınızdan hiçbir şeyi eksiltmez.\" Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Kaletil a'rabu amenna, kul lem tu'minu ve lakin kulu eslemna ve lemma yedhulil imanu fi kulubikum, ve in tutiullahe ve resulehu la yelitkum min a'malikum şey'a, innallahe gafurun rahim."},
  {id: 4627, sure_no: 15, ayet_no: 15, sure_adi: "Hucurat", text: "اِنَّمَا الْمُؤْمِنُونَ الَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَرَسُولِه۪ ثُمَّ لَمْ يَرْتَابُوا وَجَاهَدُوا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ ف۪ي سَب۪يلِ اللّٰهِۜ اُو۬لٰٓئِكَ هُمُ الصَّادِقُونَ", translation: "Mü'minler, ancak Allah'a ve Resul'üne iman eden, sonra kesinlikle hiçbir kuşkuya yer vermeyen; malları ve canlarıyla Allah yolunda cihad eden kimselerdir. Onlar, özü ve sözü bir olanlardır.", transliteration: "İnnemel mu'minunellezine amenu billahi ve resulihi summe lem yertabu ve cahedu bi emvalihim ve enfusihim fi sebilillah, ulaike humus sadikun."},
  {id: 4628, sure_no: 16, ayet_no: 16, sure_adi: "Hucurat", text: "قُلْ اَتُعَلِّمُونَ اللّٰهَ بِد۪ينِكُمْ وَاللّٰهُ يَعْلَمُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "De ki: \"Dininizi Allah'a mı öğretiyorsunuz? Allah, göklerde olanları da yerde olanları da bilir. Allah, Her Şeyi En İyi Bilen'dir.\"", transliteration: "Kul etualli munallahe bi dinikum vallahu ya'lemu ma fis semavati ve ma fil ard, vallahu bi kulli şey'in alim."},
  {id: 4629, sure_no: 17, ayet_no: 17, sure_adi: "Hucurat", text: "يَمُنُّونَ عَلَيْكَ اَنْ اَسْلَمُواۜ قُلْ لَا تَمُنُّوا عَلَيَّ اِسْلَامَكُمْۚ بَلِ اللّٰهُ يَمُنُّ عَلَيْكُمْ اَنْ هَدٰيكُمْ لِلْا۪يمَانِ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Onlar, teslim olmalarını başına kakıyorlar. De ki: \"İslam'a boyun eğdiniz diye, benden minnet beklemeyin. Bilakis, eğer özü-sözü bir kimselerseniz, sizi imana erdirdiği için, siz Allah'a minnet duyun.\"", transliteration: "Yemunnune aleyke en eslemu kul la temunnu aleyye islamekum, belillahu yemunnu aleykum en hedakum lil imani in kuntum sadikin."},
  {id: 4630, sure_no: 18, ayet_no: 18, sure_adi: "Hucurat", text: "اِنَّ اللّٰهَ يَعْلَمُ غَيْبَ السَّمٰوَاتِ وَالْاَرْضِۜ وَاللّٰهُ بَص۪يرٌ بِمَا تَعْمَلُونَ", translation: "Allah, göklerin ve yerin gaybını bilir. Allah, yaptıklarınızı görendir.", transliteration: "İnnallahe ya'lemu gaybes semavati vel ard, vallahu basirun bima ta'melun."},
  {id: 4631, sure_no: 1, ayet_no: 1, sure_adi: "Kaf", text: "قٓ۠ وَالْقُرْاٰنِ الْمَج۪يدِۚ", translation: "Kaf. Mecid Kur'an'a ant olsun.", transliteration: "Kaf vel kur'anil mecid."},
  {id: 4632, sure_no: 2, ayet_no: 2, sure_adi: "Kaf", text: "بَلْ عَجِبُٓوا اَنْ جَٓاءَهُمْ مُنْذِرٌ مِنْهُمْ فَقَالَ الْـكَافِرُونَ هٰذَا شَيْءٌ عَج۪يبٌ", translation: "Ama kendilerinden bir uyarıcının onlara gelmesine şaşırdılar. Kafirler: \"Bu şaşılacak bir şey.\" dediler.", transliteration: "Bel acibu en caehum munzirun minhum fe kalel kafirune haza şey'un acibun."},
  {id: 4633, sure_no: 3, ayet_no: 3, sure_adi: "Kaf", text: "ءَاِذَا مِتْنَا وَكُنَّا تُرَاباًۚ ذٰلِكَ رَجْعٌ بَع۪يدٌ", translation: "\"Biz ölüp, toprak olduktan sonra mı diriltileceğiz? İşte bu mümkün olmayan bir dönüştür.\" dediler.", transliteration: "E iza mitna ve kunna turaba, zalike rec'un baidun."},
  {id: 4634, sure_no: 4, ayet_no: 4, sure_adi: "Kaf", text: "قَدْ عَلِمْنَا مَا تَنْقُصُ الْاَرْضُ مِنْهُمْۚ وَعِنْدَنَا كِتَابٌ حَف۪يظٌ", translation: "Biz, yerin onlardan neyi eksilttiğini elbette biliyoruz. Yanımızda her şeyi kayda alan bir Kitap vardır.", transliteration: "Kad alimna ma tenkusul ardu minhum, ve indena kitabun hafizun."},
  {id: 4635, sure_no: 5, ayet_no: 5, sure_adi: "Kaf", text: "بَلْ كَذَّبُوا بِالْحَقِّ لَمَّا جَٓاءَهُمْ فَهُمْ ف۪ٓي اَمْرٍ مَر۪يجٍ", translation: "Ne var ki onlar, kendilerine Hakk gelince onu yalanladılar. Bu yüzden karmakarışık bir durumdalar.", transliteration: "Bel kezzebu bil hakkı lemma caehum fe hum fi emrin mericin."},
  {id: 4636, sure_no: 6, ayet_no: 6, sure_adi: "Kaf", text: "اَفَلَمْ يَنْظُرُٓوا اِلَى السَّمَٓاءِ فَوْقَهُمْ كَيْفَ بَنَيْنَاهَا وَزَيَّنَّاهَا وَمَا لَهَا مِنْ فُرُوجٍ", translation: "Öyleyse üzerlerindeki göğe bakmıyorlar mı ki, onu hiçbir çatlağı olmaksızın nasıl bina etmişiz, nasıl süslemişiz!", transliteration: "E fe lem yanzuru iles semai fevkahum keyfe beneynaha ve zeyyennaha ve ma leha min furucin."},
  {id: 4637, sure_no: 7, ayet_no: 7, sure_adi: "Kaf", text: "وَالْاَرْضَ مَدَدْنَاهَا وَاَلْقَيْنَا ف۪يهَا رَوَاسِيَ وَاَنْبَتْنَا ف۪يهَا مِنْ كُلِّ زَوْجٍ بَه۪يجٍۙ", translation: "Ve yine yeryüzünü döşedik, oraya ağır baskılar yerleştirdik. Ve orada her çeşitten göz alıcı çiftler yetiştirdik.", transliteration: "Vel arda medednaha ve elkayna fiha revasiye ve enbetna fiha min kulli zevcin behicin."},
  {id: 4638, sure_no: 8, ayet_no: 8, sure_adi: "Kaf", text: "تَبْصِرَةً وَذِكْرٰى لِكُلِّ عَبْدٍ مُن۪يبٍ", translation: "Bunları, Allah'a yönelen kulların basiretini açmak ve öğüt olması için yaptık.", transliteration: "Tebsıraten ve zikra li kulli abdin munibin."},
  {id: 4639, sure_no: 9, ayet_no: 9, sure_adi: "Kaf", text: "وَنَزَّلْنَا مِنَ السَّمَٓاءِ مَٓاءً مُبَارَكاً فَاَنْبَتْنَا بِه۪ جَنَّاتٍ وَحَبَّ الْحَص۪يدِۙ", translation: "Gökten bereketli su indirdik. Onunla cennetler ve biçilecek ekinler yetiştirdik.", transliteration: "Ve nezzelna mines semai maen mubareken fe enbetna bihi cennatin ve habbel hasidi."},
  {id: 4640, sure_no: 10, ayet_no: 10, sure_adi: "Kaf", text: "وَالنَّخْلَ بَاسِقَاتٍ لَهَا طَلْعٌ نَض۪يدٌۙ", translation: "Ve salkımları üst üste yığılmış, uzun hurma ağaçları.", transliteration: "Ven nahle basikatin leha tal'un nadidun."},
  {id: 4641, sure_no: 11, ayet_no: 11, sure_adi: "Kaf", text: "رِزْقاً لِلْعِبَادِۙ وَاَحْيَيْنَا بِه۪ بَلْدَةً مَيْتاًۜ  كَذٰلِكَ الْخُرُوجُ", translation: "Kullar için rızık olarak. Onunla ölü bir beldeye hayat verdik. İşte yeniden diriliş de böyledir.", transliteration: "Rızkan lil ibadi ve ahyeyna bihi beldeten meyta, kezalikel hurucu."},
  {id: 4642, sure_no: 12, ayet_no: 12, sure_adi: "Kaf", text: "كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَاَصْحَابُ الرَّسِّ وَثَمُودُۙ", translation: "Onlardan önce Nuh halkı, Ress'liler ve Semud yalanlamıştı.", transliteration: "Kezzebet kablehum kavmu nuhın ve ashabur ressi ve semudu."},
  {id: 4643, sure_no: 13, ayet_no: 13, sure_adi: "Kaf", text: "وَعَادٌ وَفِرْعَوْنُ وَاِخْوَانُ لُوطٍۙ", translation: "Ve Ad, Firavun ve Lut'un kardeşleri de.", transliteration: "Ve adun ve fir'avnu ve ihvanu lutın."},
  {id: 4644, sure_no: 14, ayet_no: 14, sure_adi: "Kaf", text: "وَاَصْحَابُ الْاَيْكَةِ وَقَوْمُ تُبَّعٍۜ  كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَع۪يدِ", translation: "Ve Eykeliler ve Tubba halkı. Hepsi Resulleri yalanladılar. Böylece uyarım gerçekleşti.", transliteration: "Ve ashabul eyketi ve kavmu tubbain, kullun kezzeber rusule fe hakka vaidi."},
  {id: 4645, sure_no: 15, ayet_no: 15, sure_adi: "Kaf", text: "اَفَعَي۪ينَا بِالْخَلْقِ الْاَوَّلِۜ بَلْ هُمْ ف۪ي لَبْسٍ مِنْ خَلْقٍ جَد۪يدٍ۟", translation: "İlk yaratışta yetersiz kaldık mı ki yeniden yaratılıştan şüphe içindeler!", transliteration: "E fe ayina bil halkıl evvel, bel hum fi lebsin min halkın cedid."},
  {id: 4646, sure_no: 16, ayet_no: 16, sure_adi: "Kaf", text: "وَلَقَدْ خَلَقْنَا الْاِنْسَانَ وَنَعْلَمُ مَا تُوَسْوِسُ بِه۪ نَفْسُهُۚ وَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْ حَبْلِ الْوَر۪يدِ", translation: "Ant olsun ki insanı Biz yarattık. Nefsinin ona neler fısıldadığını biliriz. Ve Biz ona \"habl-i verid\"ten daha yakınız.", transliteration: "Ve lekad halaknel insane ve na'lemu ma tuvesvisu bihi nefsuh, ve nahnu akrebu ileyhi min hablil veridi."},
  {id: 4647, sure_no: 17, ayet_no: 17, sure_adi: "Kaf", text: "اِذْ يَتَلَقَّى الْمُتَلَقِّيَانِ عَنِ الْيَم۪ينِ وَعَنِ الشِّمَالِ قَع۪يدٌ", translation: "Sağında ve solunda yerleşmiş iki kaydedici, yaptıklarını kayda geçirmektedirler.", transliteration: "İz yetelakkal mutelakkiyani anil yemini ve aniş şimali kaidun."},
  {id: 4648, sure_no: 18, ayet_no: 18, sure_adi: "Kaf", text: "مَا يَلْفِظُ مِنْ قَوْلٍ اِلَّا لَدَيْهِ رَق۪يبٌ عَت۪يدٌ", translation: "Söylediği her söz, yanındaki hazır gözleyicilerce gözlenmektedir.", transliteration: "Ma yelfızu min kavlin illa ledeyhi rakibun atidun."},
  {id: 4649, sure_no: 19, ayet_no: 19, sure_adi: "Kaf", text: "وَجَٓاءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّۜ ذٰلِكَ مَا كُنْتَ مِنْهُ تَح۪يدُ", translation: "Ölümün sarhoşluğu kaçınılmaz bir gerçek olarak gelince, \"İşte senin kaçtığın şey budur!\"", transliteration: "Ve caet sekretul mevti bil hakk, zalike ma kunte minhu tehidu."},
  {id: 4650, sure_no: 20, ayet_no: 20, sure_adi: "Kaf", text: "وَنُفِـخَ فِي الصُّورِۜ ذٰلِكَ يَوْمُ الْوَع۪يدِ", translation: "Ve Sur'a üfürülür. \"İşte bu, vaktiyle uyarıldığınız gündür.\"", transliteration: "Ve nufiha fis sur, zalike yevmul vaidi."},
  {id: 4651, sure_no: 21, ayet_no: 21, sure_adi: "Kaf", text: "وَجَٓاءَتْ كُلُّ نَفْسٍ مَعَهَا سَٓائِقٌ وَشَه۪يدٌ", translation: "Herkes yanında bir sevk edici ve bir tanıkla birlikte gelir.", transliteration: "Ve caet kullu nefsin meaha saikun ve şehidun."},
  {id: 4652, sure_no: 22, ayet_no: 22, sure_adi: "Kaf", text: "لَقَدْ كُنْتَ ف۪ي غَفْلَةٍ مِنْ هٰذَا فَـكَشَفْنَا عَنْكَ غِطَٓاءَكَ فَبَصَرُكَ الْيَوْمَ حَد۪يدٌ", translation: "Ant olsun ki sen bugünün geleceğinden gaflet içindeydin. İşte senden perdeyi kaldırdık. Artık bugün gerçeği bütün açıklığıyla görüyorsun.", transliteration: "Lekad kunte fi gafletin min haza fe keşefna anke gıtaeke fe besarukel yevme hadidun."},
  {id: 4653, sure_no: 23, ayet_no: 23, sure_adi: "Kaf", text: "وَقَالَ قَر۪ينُهُ هٰذَا مَا لَدَيَّ عَت۪يدٌۜ", translation: "Onun yakını, \"İşte bu yanımda olan şey hazırdır.\" der.", transliteration: "Ve kale karinuhu haza ma ledeyye atid."},
  {id: 4654, sure_no: 24, ayet_no: 24, sure_adi: "Kaf", text: "اَلْقِيَا ف۪ي جَهَنَّمَ كُلَّ كَفَّارٍ عَن۪يدٍۙ", translation: "\"İnatçı, Kafirlerin tamamını Cehennem'e atın.\"", transliteration: "Elkıya fi cehenneme kulle keffarin anidin."},
  {id: 4655, sure_no: 25, ayet_no: 25, sure_adi: "Kaf", text: "مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ مُر۪يبٍۙ", translation: "Hayra engel olan, haddi aşan, güvensizlik içinde olan.", transliteration: "Mennaın lil hayri mu'tedin muribin."},
  {id: 4656, sure_no: 26, ayet_no: 26, sure_adi: "Kaf", text: "اَلَّذ۪ي جَعَلَ مَعَ اللّٰهِ اِلٰهاً اٰخَرَ فَاَلْقِيَاهُ فِي الْعَذَابِ الشَّد۪يدِ", translation: "O, Allah ile birlikte başka ilah edindi. Öyleyse, onu şiddetli azaba atın.", transliteration: "Ellezi ceale meallahi ilahen ahara fe elkıyahu fil azabiş şedidi."},
  {id: 4657, sure_no: 27, ayet_no: 27, sure_adi: "Kaf", text: "قَالَ قَر۪ينُهُ رَبَّنَا مَٓا اَطْغَيْتُهُ وَلٰكِنْ كَانَ ف۪ي ضَلَالٍ بَع۪يدٍ", translation: "Onun yakını: \"Rabb'imiz! Onu ben azdırmadım, fakat o derin bir sapkınlık içindeydi.\" der.", transliteration: "Kale karinuhu rabbena ma etgaytuhu ve lakin kane fi dalalin baidin."},
  {id: 4658, sure_no: 28, ayet_no: 28, sure_adi: "Kaf", text: "قَالَ لَا تَخْتَصِمُوا لَدَيَّ وَقَدْ قَدَّمْتُ اِلَيْكُمْ بِالْوَع۪يدِ", translation: "\"Huzurumda çekişmeyin! Size daha önce uyarımı yapmıştım.\" der.", transliteration: "Kale la tahtesımu ledeyye ve kad kaddemtu ileykum bil vaidi."},
  {id: 4659, sure_no: 29, ayet_no: 29, sure_adi: "Kaf", text: "مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ وَمَٓا اَنَا۬ بِظَلَّامٍ لِلْعَب۪يدِ۟", translation: "\"Katımda söz değiştirilmez. Ben kullara asla haksızlık eden değilim.\"", transliteration: "Ma yubeddelul kavlu ledeyye ve ma ene bi zallamin lil abid."},
  {id: 4660, sure_no: 30, ayet_no: 30, sure_adi: "Kaf", text: "يَوْمَ نَقُولُ لِجَهَنَّمَ هَلِ امْتَلَأْتِ وَتَقُولُ هَلْ مِنْ مَز۪يدٍ", translation: "O Gün, Cehennem'e, \"Doldun mu?\" deriz. O da \"Daha yok mu?\" der.", transliteration: "Yevme nekulu li cehenneme helimtele'ti ve tekulu hel min mezidin."},
  {id: 4661, sure_no: 31, ayet_no: 31, sure_adi: "Kaf", text: "وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّق۪ينَ غَيْرَ بَع۪يدٍ", translation: "Cennet, takva sahipleri için uzak değil, yaklaştırılmıştır.", transliteration: "Ve uzlifetil cennetu lil muttekine gayre baidin."},
  {id: 4662, sure_no: 32, ayet_no: 32, sure_adi: "Kaf", text: "هٰذَا مَا تُوعَدُونَ لِكُلِّ اَوَّابٍ حَف۪يظٍۚ", translation: "İşte size söz verilen şey budur. Yönelen ve koruyanların tamamı içindir.", transliteration: "Haza ma tuadune li kulli evvabin hafiz."},
  {id: 4663, sure_no: 33, ayet_no: 33, sure_adi: "Kaf", text: "مَنْ خَشِيَ الرَّحْمٰنَ بِالْغَيْبِ وَجَٓاءَ بِقَلْبٍ مُن۪يبٍ", translation: "Gaybda Allah'a huşu duyanların, gönülden bağlı olanların ödülüdür.", transliteration: "Men haşiyer rahmane bil gaybi ve cae bi kalbin munibin."},
  {id: 4664, sure_no: 34, ayet_no: 34, sure_adi: "Kaf", text: "اُدْخُلُوهَا بِسَلَامٍۜ ذٰلِكَ يَوْمُ الْخُلُودِ", translation: "Oraya esenlikle girin. İşte bu Süreklilik Günü'dür.", transliteration: "Udhuluha bi selam, zalike yevmul hulud."},
  {id: 4665, sure_no: 35, ayet_no: 35, sure_adi: "Kaf", text: "لَهُمْ مَا يَشَٓاؤُ۫نَ ف۪يهَا وَلَدَيْنَا مَز۪يدٌ", translation: "Onlar için, orada diledikleri her şey vardır. Yanımızda daha fazlası da vardır.", transliteration: "Lehum ma yeşaune fiha ve ledeyna mezidun."},
  {id: 4666, sure_no: 36, ayet_no: 36, sure_adi: "Kaf", text: "وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ اَشَدُّ مِنْهُمْ بَطْشاً فَنَقَّبُوا فِي الْبِلَادِۜ هَلْ مِنْ مَح۪يصٍ", translation: "Onlardan önce, kendilerinden daha güçlü nice nesilleri yok ettik. Yok olmaktan kurtulmak için belde belde dolaştılar. Böyleyken sığınacak bir yer buldular mı?", transliteration: "Ve kem ehlekna kablehum min karnin hum eşeddu minhum batşen fe nakkabu fil bilad, hel min mahisin."},
  {id: 4667, sure_no: 37, ayet_no: 37, sure_adi: "Kaf", text: "اِنَّ ف۪ي ذٰلِكَ لَذِكْرٰى لِمَنْ كَانَ لَهُ قَلْبٌ اَوْ اَلْقَى السَّمْعَ وَهُوَ شَه۪يدٌ", translation: "Kuşkusuz bunda kalbi olan ve can kulağıyla dinleyen ve tanık olan kimseler için kesinlikle alınacak öğüt vardır.", transliteration: "İnne fi zalike le zikra li men kane lehu kalbun ev elkas sem'a ve huve şehidun."},
  {id: 4668, sure_no: 38, ayet_no: 38, sure_adi: "Kaf", text: "وَلَقَدْ خَلَقْنَا السَّمٰوَاتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا ف۪ي سِتَّةِ اَيَّامٍۗ وَمَا مَسَّنَا مِنْ لُغُوبٍ", translation: "Ant olsun ki gökleri, yeri ve ikisinin arasındakileri bir yorgunluk olmaksızın altı günde yarattık.", transliteration: "Ve lekad halaknes semavati vel arda ve ma beynehuma fi sitteti eyyamin ve ma messena min lugub."},
  {id: 4669, sure_no: 39, ayet_no: 39, sure_adi: "Kaf", text: "فَاصْبِرْ عَلٰى مَا يَقُولُونَ وَسَبِّـحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ الْغُرُوبِۚ", translation: "Onların söyledikleri şeylere sabret. Güneş'in doğmasından önce ve batmasından önce Rabb'ini hamd ile tesbih et.", transliteration: "Fasbir ala ma yekulune ve sebbih bi hamdi rabbike kable tuluış şemsi ve kablel gurub."},
  {id: 4670, sure_no: 40, ayet_no: 40, sure_adi: "Kaf", text: "وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاَدْبَارَ السُّجُودِ", translation: "Geceleyin ve secdelerin ardından O'nu tesbih et.", transliteration: "Ve minel leyli fe sebbihhu ve edbares sucudi."},
  {id: 4671, sure_no: 41, ayet_no: 41, sure_adi: "Kaf", text: "وَاسْتَمِــعْ يَوْمَ يُنَادِ الْمُنَادِ مِنْ مَكَانٍ قَر۪يبٍۙ", translation: "Çağırıcının, yakın bir yerden çağıracağı güne kulak ver.", transliteration: "Vestemi' yevme yunadil munadi min mekanin karib."},
  {id: 4672, sure_no: 42, ayet_no: 42, sure_adi: "Kaf", text: "يَوْمَ يَسْمَعُونَ الصَّيْحَةَ بِالْحَقِّۜ ذٰلِكَ يَوْمُ الْخُرُوجِ", translation: "O Gün, gerçek olan o çığlığı duyarlar. İşte bu yeniden diriliş günüdür.", transliteration: "Yevme yesmeunes sayhate bil hakk, zalike yevmul huruci."},
  {id: 4673, sure_no: 43, ayet_no: 43, sure_adi: "Kaf", text: "اِنَّا نَحْنُ نُحْـي۪ وَنُم۪يتُ وَاِلَيْنَا الْمَص۪يرُۙ", translation: "Biz; ancak Biz hayat veririz ve öldürürüz. Ve dönüş Biz'edir.", transliteration: "İnna nahnu nuhyi ve numitu ve ileynel masiru."},
  {id: 4674, sure_no: 44, ayet_no: 44, sure_adi: "Kaf", text: "يَوْمَ تَشَقَّقُ الْاَرْضُ عَنْهُمْ سِرَاعاًۜ ذٰلِكَ حَشْرٌ عَلَيْنَا يَس۪يرٌ", translation: "O Gün yer yarılır, onlar topraktan çabukça ayrılırlar. İşte bu, Biz'im için kolay bir toplamadır.", transliteration: "Yevme teşakkakul ardu anhum siraa, zalike haşrun aleyna yesirun."},
  {id: 4675, sure_no: 45, ayet_no: 45, sure_adi: "Kaf", text: "نَحْنُ اَعْلَمُ بِمَا يَقُولُونَ وَمَٓا اَنْتَ عَلَيْهِمْ بِجَبَّارٍ فَذَكِّرْ بِالْقُرْاٰنِ مَنْ يَخَافُ وَع۪يدِ", translation: "Onların ne dediklerini en iyi Bilen'iz. Sen, onları zorla yola getirecek değilsin. O halde Ben'im uyarımdan korkan kimselere Kur'an'la öğüt ver.", transliteration: "Nahnu a'lemu bi ma yekulune ve ma ente aleyhim bi cebbarin fe zekkir bil kur'ani men yehafu vaidi."},
  {id: 4676, sure_no: 1, ayet_no: 1, sure_adi: "Zariyat", text: "وَالذَّارِيَاتِ ذَرْواًۙ", translation: "Esip savuranlara,", transliteration: "Vez zariyati zerven."},
  {id: 4677, sure_no: 2, ayet_no: 2, sure_adi: "Zariyat", text: "فَالْحَامِلَاتِ وِقْراًۙ", translation: "Ve yükü taşıyanlara,", transliteration: "Fel hamilati vıkren."},
  {id: 4678, sure_no: 3, ayet_no: 3, sure_adi: "Zariyat", text: "فَالْجَارِيَاتِ يُسْراًۙ", translation: "Sonra kolayca akıp gidenlere,", transliteration: "Fel cariyati yusren."},
  {id: 4679, sure_no: 4, ayet_no: 4, sure_adi: "Zariyat", text: "فَالْمُقَسِّمَاتِ اَمْراًۙ", translation: "Sonra işi paylaştıranlara ant olsun ki,", transliteration: "Fel mukassimati, emren."},
  {id: 4680, sure_no: 5, ayet_no: 5, sure_adi: "Zariyat", text: "اِنَّمَا تُوعَدُونَ لَصَادِقٌۙ", translation: "Uyarıldığınız şey kesinlikle gerçektir.", transliteration: "İnnema tuadune le sadikun."},
  {id: 4681, sure_no: 6, ayet_no: 6, sure_adi: "Zariyat", text: "وَاِنَّ الدّ۪ينَ لَوَاقِـعٌۜ", translation: "Kuşkusuz din kesinlikle gerçekleşecektir.", transliteration: "Ve inned dine le vakıu."},
  {id: 4682, sure_no: 7, ayet_no: 7, sure_adi: "Zariyat", text: "وَالسَّمَٓاءِ ذَاتِ الْحُبُكِۙ", translation: "Yollara sahip gökyüzüne ant olsun ki,", transliteration: "Ves semai zatil hubuki."},
  {id: 4683, sure_no: 8, ayet_no: 8, sure_adi: "Zariyat", text: "اِنَّكُمْ لَف۪ي قَوْلٍ مُخْتَلِفٍۙ", translation: "Kuşkusuz siz, söylediklerinizde çelişki içindesiniz.", transliteration: "İnnekum le fi kavlin muhtelifin."},
  {id: 4684, sure_no: 9, ayet_no: 9, sure_adi: "Zariyat", text: "يُؤْفَكُ عَنْهُ مَنْ اُفِكَۜ", translation: "Döndürülecek kimse, ondan çevrilir.", transliteration: "Yu'feku anhu men ufik."},
  {id: 4685, sure_no: 10, ayet_no: 10, sure_adi: "Zariyat", text: "قُتِلَ الْخَرَّاصُونَۙ", translation: "Kahrolsun yalancılar.", transliteration: "Kutilel harrasune."},
  {id: 4686, sure_no: 11, ayet_no: 11, sure_adi: "Zariyat", text: "اَلَّذ۪ينَ هُمْ ف۪ي غَمْرَةٍ سَاهُونَۙ", translation: "Onlar, cehalet içinde ne yaptığını bilmeyenlerdir.", transliteration: "Ellezine hum fi gamretin sahune."},
  {id: 4687, sure_no: 12, ayet_no: 12, sure_adi: "Zariyat", text: "يَسْـَٔلُونَ اَيَّانَ يَوْمُ الدّ۪ينِۜ", translation: "\"Din Günü ne zaman?\" diye sorarlar.", transliteration: "Yes'elune eyyane yevmud din."},
  {id: 4688, sure_no: 13, ayet_no: 13, sure_adi: "Zariyat", text: "يَوْمَ هُمْ عَلَى النَّارِ يُفْتَنُونَ", translation: "O gün onlar, ateşe atılacaklar.", transliteration: "Yevme hum alen nari yuftenune."},
  {id: 4689, sure_no: 14, ayet_no: 14, sure_adi: "Zariyat", text: "ذُوقُوا فِتْنَتَكُمْۜ هٰذَا الَّذ۪ي كُنْتُمْ بِه۪ تَسْتَعْجِلُونَ", translation: "Fitnenizi tadın. Bu, sizin acele istediğiniz şeydir.", transliteration: "Zuku fitnetekum, hazellezi kuntum bihi testa'cilun."},
  {id: 4690, sure_no: 15, ayet_no: 15, sure_adi: "Zariyat", text: "اِنَّ الْمُتَّق۪ينَ ف۪ي جَنَّاتٍ وَعُيُونٍۙ", translation: "Takva sahipleri ise cennetlerde ve pınarlardadırlar.", transliteration: "İnnel muttekine fi cennatin ve uyunin."},
  {id: 4691, sure_no: 16, ayet_no: 16, sure_adi: "Zariyat", text: "اٰخِذ۪ينَ مَٓا اٰتٰيهُمْ رَبُّهُمْۜ اِنَّهُمْ كَانُوا قَبْلَ ذٰلِكَ مُحْسِن۪ينَۜ", translation: "Rabb'lerinin kendilerine verdiğini alanlar, daha önce iyi olanlardır.", transliteration: "Ahizine ma atahum rabbuhum, innehum kanu kable zalike muhsinin."},
  {id: 4692, sure_no: 17, ayet_no: 17, sure_adi: "Zariyat", text: "كَانُوا قَل۪يلاً مِنَ الَّيْلِ مَا يَهْجَعُونَ", translation: "Geceleri pek az uyurlardı.", transliteration: "Kanu kalilen minel leyli ma yehceun."},
  {id: 4693, sure_no: 18, ayet_no: 18, sure_adi: "Zariyat", text: "وَبِالْاَسْحَارِ هُمْ يَسْتَغْفِرُونَ", translation: "Onlar seher vakitlerinde bağışlanma dilerlerdi.", transliteration: "Ve bil esharihum yestağfirune."},
  {id: 4694, sure_no: 19, ayet_no: 19, sure_adi: "Zariyat", text: "وَف۪ٓي اَمْوَالِهِمْ حَقٌّ لِلسَّٓائِلِ وَالْمَحْرُومِ", translation: "Mallarından –istesin, istemesin- ihtiyaç sahipleri için bir pay ayırırlardı.", transliteration: "Ve fi emvalihim hakkun lis saili vel mahrumi."},
  {id: 4695, sure_no: 20, ayet_no: 20, sure_adi: "Zariyat", text: "وَفِي الْاَرْضِ اٰيَاتٌ لِلْمُوقِن۪ينَۙ", translation: "Gerçeği kavrayanlar için yeryüzünde nice ayetler vardır.", transliteration: "Ve fil ardı ayatun lil mukınine."},
  {id: 4696, sure_no: 21, ayet_no: 21, sure_adi: "Zariyat", text: "وَف۪ٓي اَنْفُسِكُمْۜ  اَفَلَا تُبْصِرُونَ", translation: "Ve kendiniz de ayetsiniz. Hala görmüyor musunuz?", transliteration: "Ve fi enfusikum, e fe la tubsirun."},
  {id: 4697, sure_no: 22, ayet_no: 22, sure_adi: "Zariyat", text: "وَفِي السَّمَٓاءِ رِزْقُكُمْ وَمَا تُوعَدُونَ", translation: "Gökte rızkınız ve uyarıldığınız şeyler vardır.", transliteration: "Ve fis semai rızkukum ve ma tuadun."},
  {id: 4698, sure_no: 23, ayet_no: 23, sure_adi: "Zariyat", text: "فَوَرَبِّ السَّمَٓاءِ وَالْاَرْضِ اِنَّهُ لَحَقٌّ مِثْلَ مَٓا اَنَّكُمْ تَنْطِقُونَ۟", translation: "Göklerin ve yeryüzünün Rabb'ine ant olsun ki, sizin konuşmanız nasıl gerçekse, kesinlikle o da o kadar gerçektir.", transliteration: "Fe ve rabbis semai vel ardı innehu le hakkun misle ma ennekum tentıkun."},
  {id: 4699, sure_no: 24, ayet_no: 24, sure_adi: "Zariyat", text: "هَلْ اَتٰيكَ حَد۪يثُ ضَيْفِ اِبْرٰه۪يمَ الْمُكْرَم۪ينَۢ", translation: "İbrahim'ın saygın konuklarının hadisi sana geldi mi?", transliteration: "Hel etake hadisu dayfi ibrahimel mukremin."},
  {id: 4700, sure_no: 25, ayet_no: 25, sure_adi: "Zariyat", text: "اِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلَاماًۜ قَالَ سَلَامٌۚ قَوْمٌ مُنْكَرُونَ", translation: "Onun yanına geldiklerinde, \"Selam.\" dediler. \"Selam, tanınmayan topluluk.\" dedi.", transliteration: "İz dehalu aleyhi fe kalu selama, kale selam, kavmun munkerun."},
  {id: 4701, sure_no: 26, ayet_no: 26, sure_adi: "Zariyat", text: "فَرَاغَ اِلٰٓى اَهْلِه۪ فَجَٓاءَ بِعِجْلٍ سَم۪ينٍۙ", translation: "Habersizce ailesine gidip, hemen kızarmış buzağı eti getirdi.", transliteration: "Fe raga ila ehlihi fe cae bi iclin seminin."},
  {id: 4702, sure_no: 27, ayet_no: 27, sure_adi: "Zariyat", text: "فَقَرَّبَهُٓ اِلَيْهِمْ قَالَ اَلَا تَأْكُلُونَۘ", translation: "Onları buyur ederek: \"Yemez misiniz?\" dedi.", transliteration: "Fe karrebehu ileyhim kale e la te'kulun."},
  {id: 4703, sure_no: 28, ayet_no: 28, sure_adi: "Zariyat", text: "فَاَوْجَسَ مِنْهُمْ خ۪يفَةًۜ قَالُوا لَا تَخَفْۜ وَبَشَّرُوهُ بِغُلَامٍ عَل۪يمٍ", translation: "Durumlarından dolayı içine bir korku düştü. \"Korkma.\" dediler ve ona bilgin bir çocuk müjdelediler.", transliteration: "Fe evcese minhum hifeh, kalu la tehaf, ve beşşeruhu bi gulamin alim."},
  {id: 4704, sure_no: 29, ayet_no: 29, sure_adi: "Zariyat", text: "فَاَقْبَلَتِ امْرَاَتُهُ ف۪ي صَرَّةٍ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوزٌ عَق۪يمٌ", translation: "Bunun üzerine hanımı şaşkınlık içinde, yüzüne vurarak yüksek sesle: \"Ben kısır, ihtiyar bir kadınım.\" dedi.", transliteration: "Fe akbeletimreetuhu fi sarretin fe sakket vecheha ve kalet acuzun akimun."},
  {id: 4705, sure_no: 30, ayet_no: 30, sure_adi: "Zariyat", text: "قَالُوا كَذٰلِكِۙ قَالَ رَبُّكِۜ اِنَّهُ هُوَ الْحَك۪يمُ الْعَل۪يمُ", translation: "\"Senin Rabb'inin buyurduğu şey işte budur.\" dediler. O, En İyi Hüküm Veren'dir, Her Şeyi Bilen'dir.", transliteration: "Kalu kezaliki kale rabbuk, innehu huvel hakimul alimu."},
  {id: 4706, sure_no: 31, ayet_no: 31, sure_adi: "Zariyat", text: "قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُونَ", translation: "İbrahim: \"O halde ey elçiler! Sizin geliş amacınız nedir?\" dedi.", transliteration: "Kale fe ma hatbukum eyyuhel murselun."},
  {id: 4707, sure_no: 32, ayet_no: 32, sure_adi: "Zariyat", text: "قَالُٓوا اِنَّٓا اُرْسِلْـنَٓا اِلٰى قَوْمٍ مُجْرِم۪ينَۙ", translation: "\"Biz, suçlu bir topluma gönderildik.\" dediler.", transliteration: "Kalu inna ursilna ila kavmin mucrimine."},
  {id: 4708, sure_no: 33, ayet_no: 33, sure_adi: "Zariyat", text: "لِنُرْسِلَ عَلَيْهِمْ حِجَارَةً مِنْ ط۪ينٍۙ", translation: "Onların üzerlerine çamurdan pişirilmiş taşlar yağdırmak için.", transliteration: "Li nursile aleyhim hıcareten min tinin."},
  {id: 4709, sure_no: 34, ayet_no: 34, sure_adi: "Zariyat", text: "مُسَوَّمَةً عِنْدَ رَبِّكَ لِلْمُسْرِف۪ينَ", translation: "Onlar Rabb'inin katından, aşırı gidenler için olan taşlardır.", transliteration: "Musevvemeten inde rabbike lil musrifin."},
  {id: 4710, sure_no: 35, ayet_no: 35, sure_adi: "Zariyat", text: "فَاَخْرَجْنَا مَنْ كَانَ ف۪يهَا مِنَ الْمُؤْمِن۪ينَۚ", translation: "Orada bulunan İman Edenler'i çıkardık.", transliteration: "Fe ahrecna men kane fiha minel mu'minin."},
  {id: 4711, sure_no: 36, ayet_no: 36, sure_adi: "Zariyat", text: "فَمَا وَجَدْنَا ف۪يهَا غَيْرَ بَيْتٍ مِنَ الْمُسْلِم۪ينَۚ", translation: "Fakat orada, bir evden başkasında, Müslimlerden kimse bulamadık.", transliteration: "Fe ma vecedna fiha gayre beytin minel muslimin."},
  {id: 4712, sure_no: 37, ayet_no: 37, sure_adi: "Zariyat", text: "وَتَرَكْنَا ف۪يهَٓا اٰيَةً لِلَّذ۪ينَ يَخَافُونَ الْعَذَابَ الْاَل۪يمَۜ", translation: "Orada can yakan azaptan korkanlar için bir ayet bıraktık.", transliteration: "Ve terekna fiha ayeten lillezine yahafunel azabel elim."},
  {id: 4713, sure_no: 38, ayet_no: 38, sure_adi: "Zariyat", text: "وَف۪ي مُوسٰٓى اِذْ اَرْسَلْنَاهُ اِلٰى فِرْعَوْنَ بِسُلْطَانٍ مُب۪ينٍ", translation: "Musa'da da vardır. Onu Firavun'a apaçık bir sultanla göndermiştik.", transliteration: "Ve fi musa iz erselnahu ila fir'avne bi sultanin mubinin."},
  {id: 4714, sure_no: 39, ayet_no: 39, sure_adi: "Zariyat", text: "فَتَوَلّٰى بِرُكْنِه۪ وَقَالَ سَاحِرٌ اَوْ مَجْنُونٌ", translation: "Ancak Firavun yüz çevirdi. \"O bir sihirbaz veya delidir.\" dedi.", transliteration: "Fe tevella bi ruknihi ve kale sahırun ev mecnunun."},
  {id: 4715, sure_no: 40, ayet_no: 40, sure_adi: "Zariyat", text: "فَاَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّ وَهُوَ مُل۪يمٌۜ", translation: "Sonra onu ve ordularını yakaladık ve denize attık. Kendi kendini kınıyordu.", transliteration: "Fe ehaznahu ve cunudehu fe nebeznahum fil yemmi ve huve mulim."},
  {id: 4716, sure_no: 41, ayet_no: 41, sure_adi: "Zariyat", text: "وَف۪ي عَادٍ اِذْ اَرْسَلْنَا عَلَيْهِمُ الرّ۪يحَ الْعَق۪يمَۚ", translation: "Ad'da da vardır. Onların üzerine kasıp kavuran rüzgar göndermiştik.", transliteration: "Ve fi adin iz erselna aleyhimur rihal akim."},
  {id: 4717, sure_no: 42, ayet_no: 42, sure_adi: "Zariyat", text: "مَا تَذَرُ مِنْ شَيْءٍ اَتَتْ عَلَيْهِ اِلَّا جَعَلَتْهُ  كَالرَّم۪يمِۜ", translation: "Nereye uğradıysa orayı çürümüş çer çöpe çevirmişti.", transliteration: "Ma tezeru min şey'in etet aleyhi illa cealethu ker remim."},
  {id: 4718, sure_no: 43, ayet_no: 43, sure_adi: "Zariyat", text: "وَف۪ي ثَمُودَ اِذْ ق۪يلَ لَهُمْ تَمَتَّعُوا حَتّٰى ح۪ينٍ", translation: "Semud'da da vardır. Onlara: \"Belli bir süreye kadar yararlanın.\" denmişti.", transliteration: "Ve fi semude iz kile lehum temetteu hatta hinin."},
  {id: 4719, sure_no: 44, ayet_no: 44, sure_adi: "Zariyat", text: "فَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ فَاَخَذَتْهُمُ الصَّاعِقَةُ وَهُمْ يَنْظُرُونَ", translation: "Fakat Rabb'lerinin emrinden çıktılar. Bunun üzerine bakıp dururlarken yıldırım onları yakalayıverdi.", transliteration: "Fe atev an emri rabbihim fe ehazethumus saikatu ve hum yanzurun."},
  {id: 4720, sure_no: 45, ayet_no: 45, sure_adi: "Zariyat", text: "فَمَا اسْتَطَاعُوا مِنْ قِيَامٍ وَمَا كَانُوا مُنْتَصِر۪ينَۙ", translation: "Ayağa kalkmaya güçleri yetmedi. Yardım görenler de olmadılar.", transliteration: "Fe mestetau min kıyamin ve ma kanu muntesirine."},
  {id: 4721, sure_no: 46, ayet_no: 46, sure_adi: "Zariyat", text: "وَقَوْمَ نُوحٍ مِنْ قَبْلُۜ اِنَّهُمْ كَانُوا قَوْماً فَاسِق۪ينَ۟", translation: "Daha önce Nuh halkını da. Ki onlar fasık bir halktı.", transliteration: "Ve kavme nuhın min kabl, inne hum kanu kavmen fasıkin."},
  {id: 4722, sure_no: 47, ayet_no: 47, sure_adi: "Zariyat", text: "وَالسَّمَٓاءَ بَنَيْنَاهَا بِاَيْدٍ وَاِنَّا لَمُوسِعُونَ", translation: "Göğü ellerle bina ettik. Kuşkusuz genişletici olan elbette Biziz.", transliteration: "Ves semae beneynaha bi eydin ve inna le musiun."},
  {id: 4723, sure_no: 48, ayet_no: 48, sure_adi: "Zariyat", text: "وَالْاَرْضَ فَرَشْنَاهَا فَنِعْمَ الْمَاهِدُونَ", translation: "Yeryüzünü de Biz döşedik. Ne güzel döşeyiciyiz!", transliteration: "Vel arda fereşnaha fe ni'mel mahidun."},
  {id: 4724, sure_no: 49, ayet_no: 49, sure_adi: "Zariyat", text: "وَمِنْ كُلِّ شَيْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُونَ", translation: "Her şeyi çift yarattık. Umulur ki öğüt alırsınız.", transliteration: "Ve min kulli şey'in halakna zevceyni leallekum tezekkerun."},
  {id: 4725, sure_no: 50, ayet_no: 50, sure_adi: "Zariyat", text: "فَفِرُّٓوا اِلَى اللّٰهِۜ اِنّ۪ي لَكُمْ مِنْهُ نَذ۪يرٌ مُب۪ينٌۚ", translation: "\"O halde Allah'a sığının! Ben, sizin için O'nun tarafından gönderilmiş apaçık bir uyarıcıyım.\"", transliteration: "Fe firru ilallah, inni lekum minhu nezirun mubin."},
  {id: 4726, sure_no: 51, ayet_no: 51, sure_adi: "Zariyat", text: "وَلَا تَجْعَلُوا مَعَ اللّٰهِ اِلٰهاً اٰخَرَۜ اِنّ۪ي لَكُمْ مِنْهُ نَذ۪يرٌ مُب۪ينٌ", translation: "\"Allah'ın yanı sıra başka bir ilah tanımayın. Ben, sizin için O'nun tarafından gönderilmiş apaçık bir uyarıcıyım.\"", transliteration: "Ve la tec'alu meallahi ilahen ahar, inni lekum minhu nezirun mubin."},
  {id: 4727, sure_no: 52, ayet_no: 52, sure_adi: "Zariyat", text: "كَذٰلِكَ مَٓا اَتَى الَّذ۪ينَ مِنْ قَبْلِهِمْ مِنْ رَسُولٍ اِلَّا قَالُوا سَاحِرٌ اَوْ مَجْنُونٌ", translation: "Aynen bunlar gibi, bunlardan öncekiler de kendilerine gelen Resullere, \"Sihirbazdır veya mecnundur.\" dan başka bir şey demediler.", transliteration: "Kezalike ma etellezine min kablihim min resulin illa kalu sahırun ev mecnun."},
  {id: 4728, sure_no: 53, ayet_no: 53, sure_adi: "Zariyat", text: "اَتَوَاصَوْا بِه۪ۚ بَلْ هُمْ قَوْمٌ طَاغُونَ", translation: "Sanki böyle yapmayı sonrakilere vasiyet etmişler! Hayır, onlar azgın bir halktır.", transliteration: "E tevasav bih, bel hum kavmun tagun."},
  {id: 4729, sure_no: 54, ayet_no: 54, sure_adi: "Zariyat", text: "فَتَوَلَّ عَنْهُمْ فَمَٓا اَنْتَ بِمَلُومٍۘ", translation: "O halde onlardan yüz çevir. Artık kınanacak değilsin.", transliteration: "Fe tevelle anhum fe ma ente bi melum."},
  {id: 4730, sure_no: 55, ayet_no: 55, sure_adi: "Zariyat", text: "وَذَكِّرْ فَاِنَّ الذِّكْرٰى تَنْفَعُ الْمُؤْمِن۪ينَ", translation: "Öğüt ver; kuşkusuz ki öğüt Mü'minlere fayda verir.", transliteration: "Ve zekkir fe innez zikra tenfeul mu'minin."},
  {id: 4731, sure_no: 56, ayet_no: 56, sure_adi: "Zariyat", text: "وَمَا خَلَقْتُ الْجِنَّ وَالْاِنْسَ اِلَّا لِيَعْبُدُونِ", translation: "Ben, cinni ve insi yalnızca bana kulluk etsinler diye yarattım.", transliteration: "Ve ma halaktul cinne vel inse illa li ya'budun."},
  {id: 4732, sure_no: 57, ayet_no: 57, sure_adi: "Zariyat", text: "مَٓا اُر۪يدُ مِنْهُمْ مِنْ رِزْقٍ وَمَٓا اُر۪يدُ اَنْ يُطْعِمُونِ", translation: "Onlardan bir rızık da istemiyorum, Beni doyurmalarını da istemiyorum.", transliteration: "Ma uridu minhum min rızkın ve ma uridu en yut'imuni."},
  {id: 4733, sure_no: 58, ayet_no: 58, sure_adi: "Zariyat", text: "اِنَّ اللّٰهَ هُوَ الرَّزَّاقُ ذُوالْقُوَّةِ الْمَت۪ينُ", translation: "Kuşkusuz rızık veren, güçlü ve gücünde metin olan Allah'tır.", transliteration: "İnnallahe huver rezzaku zul kuvvetil metin."},
  {id: 4734, sure_no: 59, ayet_no: 59, sure_adi: "Zariyat", text: "فَاِنَّ لِلَّذ۪ينَ ظَلَمُوا ذَنُوباً مِثْلَ ذَنُوبِ اَصْحَابِهِمْ فَلَا يَسْتَعْجِلُونِ", translation: "Zulmedenlerin azaptan payı, arkadaşlarının payı gibidir. Artık acele etmesinler.", transliteration: "Fe inne lillezine zalemu zenuben misle zenubi ashabihim fe la yesta'ciluni."},
  {id: 4735, sure_no: 60, ayet_no: 60, sure_adi: "Zariyat", text: "فَوَيْلٌ لِلَّذ۪ينَ كَفَرُوا مِنْ يَوْمِهِمُ الَّذ۪ي يُوعَدُونَ", translation: "Kendilerine haber verilen azap günü geldiğinde, Kafirlerin vay haline.", transliteration: "Fe veylun lillezine keferu min yevmihimullezi yuadun."},
  {id: 4736, sure_no: 1, ayet_no: 1, sure_adi: "Tur", text: "وَالطُّورِۙ", translation: "Tur'a ant olsun;", transliteration: "Vet turi."},
  {id: 4737, sure_no: 2, ayet_no: 2, sure_adi: "Tur", text: "وَكِتَابٍ مَسْطُورٍۙ", translation: "Satır satır yazılmış Kitap'a,", transliteration: "Ve kitabin mesturin."},
  {id: 4738, sure_no: 3, ayet_no: 3, sure_adi: "Tur", text: "ف۪ي رَقٍّ مَنْشُورٍۙ", translation: "Üzerine yazı yazılmış, yayılmış ince deriye.", transliteration: "Fi rakkın menşurin."},
  {id: 4739, sure_no: 4, ayet_no: 4, sure_adi: "Tur", text: "وَالْبَيْتِ الْمَعْمُورِۙ", translation: "Beyt-i Mamur'a ant olsun.", transliteration: "Vel beytil ma'muri."},
  {id: 4740, sure_no: 5, ayet_no: 5, sure_adi: "Tur", text: "وَالسَّقْفِ الْمَرْفُوعِۙ", translation: "Yükseltilmiş tavana ant olsun", transliteration: "Ves sakfil merfui."},
  {id: 4741, sure_no: 6, ayet_no: 6, sure_adi: "Tur", text: "وَالْبَحْرِ الْمَسْجُورِۙ", translation: "Doldurulmuş denize ant olsun.", transliteration: "Vel bahril mescuri."},
  {id: 4742, sure_no: 7, ayet_no: 7, sure_adi: "Tur", text: "اِنَّ عَذَابَ رَبِّكَ لَوَاقِـعٌۙ", translation: "Rabb'inin azabı kesinlikle gerçekleşecektir.", transliteration: "İnne azabe rabbike le vakı'un."},
  {id: 4743, sure_no: 8, ayet_no: 8, sure_adi: "Tur", text: "مَا لَهُ مِنْ دَافِـعٍۙ", translation: "Onu savacak yoktur.", transliteration: "Ma lehu min dafiin."},
  {id: 4744, sure_no: 9, ayet_no: 9, sure_adi: "Tur", text: "يَوْمَ تَمُورُ السَّمَٓاءُ مَوْراًۙ", translation: "O Gün gök, sarsıldıkça sarsılır.", transliteration: "Yevme temurus semau mevren."},
  {id: 4745, sure_no: 10, ayet_no: 10, sure_adi: "Tur", text: "وَتَس۪يرُ الْجِبَالُ سَيْراًۜ", translation: "Dağlar yerlerinden oynar.", transliteration: "Ve tesirul cibalu seyra."},
  {id: 4746, sure_no: 11, ayet_no: 11, sure_adi: "Tur", text: "فَوَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَۙ", translation: "O Gün yalanlayanların vay hallerine.", transliteration: "Fe veylun yevme izin lil mukezzibine."},
  {id: 4747, sure_no: 12, ayet_no: 12, sure_adi: "Tur", text: "اَلَّذ۪ينَ هُمْ ف۪ي خَوْضٍ يَلْعَبُونَۢ", translation: "Onlar ki, gereksiz şeylere dalıp oyalanıyorlar.", transliteration: "Ellezine hum fi havdın yel'abun."},
  {id: 4748, sure_no: 13, ayet_no: 13, sure_adi: "Tur", text: "يَوْمَ يُدَعُّونَ اِلٰى نَارِ جَهَنَّمَ دَعاًّۜ", translation: "O Gün, Cehennem ateşine sürüklenirler.", transliteration: "Yevme yude'une ila nari cehenneme de'a."},
  {id: 4749, sure_no: 14, ayet_no: 14, sure_adi: "Tur", text: "هٰذِهِ النَّارُ الَّت۪ي كُنْتُمْ بِهَا تُكَذِّبُونَ", translation: "İşte bu, yalanladığınız ateştir!", transliteration: "Hazihin narulleti kuntum biha tukezzibun."},
  {id: 4750, sure_no: 15, ayet_no: 15, sure_adi: "Tur", text: "اَفَسِحْرٌ هٰذَٓا اَمْ اَنْتُمْ لَا تُبْصِرُونَ", translation: "Bu sihir miymiş? Yoksa siz mi görmüyormuşsunuz?", transliteration: "E fe sihrun haza em entum la tubsirun."},
  {id: 4751, sure_no: 16, ayet_no: 16, sure_adi: "Tur", text: "اِصْلَوْهَا فَاصْبِرُٓوا اَوْ لَا تَصْبِرُواۚ سَوَٓاءٌ عَلَيْكُمْۜ اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ", translation: "Oraya girin. Artık dayansanız da dayanmasanız da sizin için birdir. Yaptığınız şeylerin karşılığını görüyorsunuz.", transliteration: "Islevha fasbiru ev la tasbiru sevaun aleykum, innema tuczevne ma kuntum ta'melun."},
  {id: 4752, sure_no: 17, ayet_no: 17, sure_adi: "Tur", text: "اِنَّ الْمُتَّق۪ينَ ف۪ي جَنَّاتٍ وَنَع۪يمٍۙ", translation: "Takva sahipleri cennetlerde ve nimetler içindedirler;", transliteration: "İnnel muttekine fi cennatin ve naimin."},
  {id: 4753, sure_no: 18, ayet_no: 18, sure_adi: "Tur", text: "فَاكِه۪ينَ بِمَٓا اٰتٰيهُمْ رَبُّهُمْۚ وَوَقٰيهُمْ رَبُّهُمْ عَذَابَ الْجَح۪يمِ", translation: "Rabb'lerinin kendilerine verdiklerinden hoşnut olarak. Rabb'leri onları Cehennem ateşinden korumuştur.", transliteration: "Fakihine bi ma atahum rabbuhum, ve vekahum rabbuhum azabel cahim."},
  {id: 4754, sure_no: 19, ayet_no: 19, sure_adi: "Tur", text: "كُلُوا وَاشْرَبُوا هَن۪ٓيـٔاً بِمَا كُنْتُمْ تَعْمَلُونَۙ", translation: "Yaptıklarınızın karşılığı olarak, afiyetle yiyin ve için;", transliteration: "Kulu veşrebu henien bi ma kuntum ta'melune."},
  {id: 4755, sure_no: 20, ayet_no: 20, sure_adi: "Tur", text: "مُتَّكِـ۪ٔينَ عَلٰى سُرُرٍ مَصْفُوفَةٍۚ وَزَوَّجْنَاهُمْ بِحُورٍ ع۪ينٍ", translation: "Sıra sıra dizilmiş koltuklara yaslanarak. Biz, onları temiz, \"güzel bakışlı\" hurilerle eşleştirmişizdir.", transliteration: "Muttekiine ala sururin masfufeh, ve zevvecnahum bi hurin inin."},
  {id: 4756, sure_no: 21, ayet_no: 21, sure_adi: "Tur", text: "وَالَّذ۪ينَ اٰمَنُوا وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُمْ بِا۪يمَانٍ اَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَٓا اَلَتْنَاهُمْ مِنْ عَمَلِهِمْ مِنْ شَيْءٍۜ  كُلُّ امْرِئٍ بِمَا كَسَبَ رَه۪ينٌ", translation: "İman eden, soyları da iman ederek kendilerine tabi olan kimselerin, soylarını da kendilerine kattık. Ve onların yaptıklarından bir şey eksiltmedik. Herkes kendi yaptıklarının karşılığını alacak.", transliteration: "Vellezine amenu vettebeathum zurriyyetuhum bi imanin elhakna bihim zurriyyetehum ve ma eletnahum min amelihim min şey'in, kullumriin bi ma kesebe rehinun."},
  {id: 4757, sure_no: 22, ayet_no: 22, sure_adi: "Tur", text: "وَاَمْدَدْنَاهُمْ بِفَاكِهَةٍ وَلَحْمٍ مِمَّا يَشْتَهُونَ", translation: "Onlara; meyvelerden, etlerden ve canlarının çektiği şeylerden bol bol sunarız.", transliteration: "Ve emdednahum bi fakihetin ve lahmin mimma yeştehun."},
  {id: 4758, sure_no: 23, ayet_no: 23, sure_adi: "Tur", text: "يَتَنَازَعُونَ ف۪يهَا كَأْساً لَا لَغْوٌ ف۪يهَا وَلَا تَأْث۪يمٌ", translation: "Birbirlerine, saçmalamaya ve günaha yol açmayan kadehler sunacaklar.", transliteration: "Yetenazeune fiha ke'sen la lagvun fiha ve la te'simun."},
  {id: 4759, sure_no: 24, ayet_no: 24, sure_adi: "Tur", text: "وَيَطُوفُ عَلَيْهِمْ غِلْمَانٌ لَهُمْ كَاَنَّهُمْ لُؤْلُؤٌ۬ مَكْنُونٌ", translation: "Sedefleri içine gizlenmiş inci gibi gılmanlar, hizmet için çevrelerinde dolanırlar.", transliteration: "Ve yetufu aleyhim gılmanun lehum ke ennehum lu'luun meknunun."},
  {id: 4760, sure_no: 25, ayet_no: 25, sure_adi: "Tur", text: "وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَتَسَٓاءَلُونَ", translation: "Bir araya gelerek söyleşirler:", transliteration: "Ve akbele ba'duhum ala ba'dın yetesaelun."},
  {id: 4761, sure_no: 26, ayet_no: 26, sure_adi: "Tur", text: "قَالُٓوا اِنَّا كُنَّا قَبْلُ ف۪ٓي اَهْلِنَا مُشْفِق۪ينَ", translation: "\"Doğrusu biz, daha önce ailemizden dolayı korkuyorduk.\"", transliteration: "Kalu inna kunna kablu fi ehlina muşfikin."},
  {id: 4762, sure_no: 27, ayet_no: 27, sure_adi: "Tur", text: "فَمَنَّ اللّٰهُ عَلَيْنَا وَوَقٰينَا عَذَابَ السَّمُومِ", translation: "\"Şimdi, Allah, bizi nimetlendirdi ve bizi kavurucu ateşin azabından korudu.\"", transliteration: "Fe mennallahu aleyna ve vekana azabes semum."},
  {id: 4763, sure_no: 28, ayet_no: 28, sure_adi: "Tur", text: "اِنَّا كُنَّا مِنْ قَبْلُ نَدْعُوهُۜ اِنَّهُ هُوَ الْبَرُّ الرَّح۪يمُ۟", translation: "\"İyi ki daha önce yalnızca O'na yöneldik. Kuşkusuz ki O, İyilik Yapan'dır, Rahmeti Kesintisiz'dir.\"", transliteration: "İnna kunna min kablu ned'uh, innehu huvel berrur rahim."},
  {id: 4764, sure_no: 29, ayet_no: 29, sure_adi: "Tur", text: "فَذَكِّرْ فَمَٓا اَنْتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَلَا مَجْنُونٍۜ", translation: "O halde öğüt vermeye devam et. Rabb'inin nimeti sayesinde ne kahin ne de mecnunsun.", transliteration: "Fe zekkir fe ma ente bi ni'meti rabbike bi kahinin ve la mecnun."},
  {id: 4765, sure_no: 30, ayet_no: 30, sure_adi: "Tur", text: "اَمْ يَقُولُونَ شَاعِرٌ نَتَرَبَّصُ بِه۪ رَيْبَ الْمَنُونِ", translation: "Yoksa: \"O bir şairdir, ansızın zamanın felaketine uğramasını bekliyoruz.\" mu diyorlar?", transliteration: "Em yekulune şairun neterabbesu bihi reybel menuni."},
  {id: 4766, sure_no: 31, ayet_no: 31, sure_adi: "Tur", text: "قُلْ تَرَبَّصُوا فَاِنّ۪ي مَعَكُمْ مِنَ الْمُتَرَبِّص۪ينَۜ", translation: "De ki: \"Bekleyin! Ben de sizinle birlikte bekleyenlerdenim.\"", transliteration: "Kul terabbesu fe inni meakum minel muterabbisin."},
  {id: 4767, sure_no: 32, ayet_no: 32, sure_adi: "Tur", text: "اَمْ تَأْمُرُهُمْ اَحْلَامُهُمْ بِهٰذَٓا اَمْ هُمْ قَوْمٌ طَاغُونَۚ", translation: "Yoksa onların düşleri böyle mi istiyor? Yoksa onlar azgın bir halk mı?", transliteration: "Em te'muruhum ahlamuhum bi haza em hum kavmun tagun."},
  {id: 4768, sure_no: 33, ayet_no: 33, sure_adi: "Tur", text: "اَمْ يَقُولُونَ تَقَوَّلَهُۚ بَلْ لَا يُؤْمِنُونَۚ", translation: "Yoksa: \"Onu kendisi uydurup söyledi.\" mi diyorlar? İşin gerçeği, onlar inanmak istemiyorlar.", transliteration: "Em yekulune tekavveleh, bel la yu'minun."},
  {id: 4769, sure_no: 34, ayet_no: 34, sure_adi: "Tur", text: "فَلْيَأْتُوا بِحَد۪يثٍ مِثْلِه۪ٓ اِنْ كَانُوا صَادِق۪ينَۜ", translation: "Eğer söylediklerinde haklı kimselerse, onlar da onun benzeri bir hadis getirsinler!", transliteration: "Fel ye'tu bi hadisin mislihi in kanu sadikin."},
  {id: 4770, sure_no: 35, ayet_no: 35, sure_adi: "Tur", text: "اَمْ خُلِقُوا مِنْ غَيْرِ شَيْءٍ اَمْ هُمُ الْخَالِقُونَۜ", translation: "Yoksa onlar, bir yaratıcı olmaksızın mı yaratıldılar? Yoksa yaratanlar onlar mıdır?", transliteration: "Em huliku min gayri şey'in em humul halikun."},
  {id: 4771, sure_no: 36, ayet_no: 36, sure_adi: "Tur", text: "اَمْ خَلَقُوا السَّمٰوَاتِ وَالْاَرْضَۚ بَلْ لَا يُوقِنُونَۜ", translation: "Yoksa gökleri ve yeryüzünü onlar mı yarattı? Hayır, onlar gerçeğe ulaşmaya yanaşmıyorlar!", transliteration: "Em halakus semavati vel ard, bel la yukınun."},
  {id: 4772, sure_no: 37, ayet_no: 37, sure_adi: "Tur", text: "اَمْ عِنْدَهُمْ خَزَٓائِنُ رَبِّكَ اَمْ هُمُ الْمُصَيْطِرُونَۜ", translation: "Yoksa Rabb'inin hazineleri onların yanında mı? Veya kendilerinin her şeye güç yetirebileceklerini mi sanıyorlar?", transliteration: "Em indehum hazainu rabbike em humul musaytırun."},
  {id: 4773, sure_no: 38, ayet_no: 38, sure_adi: "Tur", text: "اَمْ لَهُمْ سُلَّمٌ يَسْتَمِعُونَ ف۪يهِۚ فَلْيَأْتِ مُسْتَمِعُهُمْ بِسُلْطَانٍ مُب۪ينٍۜ", translation: "Yoksa onların, orada dinleyecekleri bir merdivenleri mi var? Öyleyse dinleyenleri buna dair bir belge getirsinler!", transliteration: "Em lehum sullemun yestemiune fih, fel ye'ti mustemiuhum bi sultanin mubin."},
  {id: 4774, sure_no: 39, ayet_no: 39, sure_adi: "Tur", text: "اَمْ لَهُ الْبَنَاتُ وَلَـكُمُ الْبَنُونَۜ", translation: "Yoksa kızlar O'nun da oğullar sizin mi?", transliteration: "Em le hul benatu ve le kumul benun."},
  {id: 4775, sure_no: 40, ayet_no: 40, sure_adi: "Tur", text: "اَمْ تَسْـَٔلُهُمْ اَجْراً فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَۜ", translation: "Yoksa sen onlardan bir ücret istiyorsun da bu yüzden onlar ağır bir yük altına mı giriyorlar?", transliteration: "Em tes'eluhum ecren fe hum min magremin muskalun."},
  {id: 4776, sure_no: 41, ayet_no: 41, sure_adi: "Tur", text: "اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَۜ", translation: "Yoksa gayb onların yanında da onlar mı yazıyorlar?", transliteration: "Em indehumul gaybu fe hum yektubun."},
  {id: 4777, sure_no: 42, ayet_no: 42, sure_adi: "Tur", text: "اَمْ يُر۪يدُونَ كَيْداًۜ فَالَّذ۪ينَ كَفَرُوا هُمُ الْمَك۪يدُونَۜ", translation: "Yoksa bir tuzak mı kurmak istiyorlar? Ne var ki tuzağa düşecek olanlar Kafirlerdir.", transliteration: "Em yuridune keyda, fellezine keferu humul mekidun."},
  {id: 4778, sure_no: 43, ayet_no: 43, sure_adi: "Tur", text: "اَمْ لَهُمْ اِلٰهٌ غَيْرُ اللّٰهِۜ سُبْحَانَ اللّٰهِ عَمَّا يُشْرِكُونَ", translation: "Yoksa onlar için Allah'tan başka bir ilahları mı var? Allah, onların şirk koştuklarından münezzehtir.", transliteration: "Em lehum ilahun gayrullah, subhanallahi amma yuşrikun."},
  {id: 4779, sure_no: 44, ayet_no: 44, sure_adi: "Tur", text: "وَاِنْ يَرَوْا كِسْفاً مِنَ السَّمَٓاءِ سَاقِطاً يَقُولُوا سَحَابٌ مَرْكُومٌ", translation: "Gökten bir parçanın düştüğünü görseler, \"Üst üste yığılmış bulutlardır.\" derler!", transliteration: "Ve in yerev kisfen mines semai sakıtan yekulu sehabun merkum."},
  {id: 4780, sure_no: 45, ayet_no: 45, sure_adi: "Tur", text: "فَذَرْهُمْ حَتّٰى يُلَاقُوا يَوْمَهُمُ الَّذ۪ي ف۪يهِ يُصْعَقُونَۙ", translation: "Artık onları cezalandırılacakları güne kavuşuncaya kadar kendi hallerine bırak.", transliteration: "Fe zerhum hatta yulaku yevmehumullezi fihi yus'akune."},
  {id: 4781, sure_no: 46, ayet_no: 46, sure_adi: "Tur", text: "يَوْمَ لَا يُغْن۪ي عَنْهُمْ كَيْدُهُمْ شَيْـٔاً وَلَا هُمْ يُنْصَرُونَۜ", translation: "O Gün planları onlara hiçbir yarar sağlamaz. Ve onlar yardım da olunmazlar.", transliteration: "Yevme la yugni anhum keyduhum şey'en ve la hum yunsarun."},
  {id: 4782, sure_no: 47, ayet_no: 47, sure_adi: "Tur", text: "وَاِنَّ لِلَّذ۪ينَ ظَلَمُوا عَذَاباً دُونَ ذٰلِكَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُونَ", translation: "Zulmedenler için bundan başka bir azap daha vardır. Ne var ki onların çoğu, bunun bilincinde değiller.", transliteration: "Ve inne lillezine zalemu azaben dune zalike ve lakinne ekserehum la ya'lemun."},
  {id: 4783, sure_no: 48, ayet_no: 48, sure_adi: "Tur", text: "وَاصْبِرْ لِحُكْمِ رَبِّكَ فَاِنَّكَ بِاَعْيُنِنَا وَسَبِّـحْ بِحَمْدِ رَبِّكَ ح۪ينَ تَقُومُۙ", translation: "Rabb'inin hükmünü sabrederek bekle. Sen gözümüzün önündesin. Ve her kalkışında Rabb'ini hamd ile tesbih et.", transliteration: "Vasbir li hukmi rabbike fe inneke bi a'yunina, ve sebbih bi hamdi rabbike hine tekumu."},
  {id: 4784, sure_no: 49, ayet_no: 49, sure_adi: "Tur", text: "وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاِدْبَارَ النُّجُومِ", translation: "Gecenin bir kısmında ve yıldızların batışının ardından O'nu tesbih et.", transliteration: "Ve minel leyli fe sebbihhu ve idbaren nucumi."},
  {id: 4785, sure_no: 1, ayet_no: 1, sure_adi: "Necm", text: "وَالنَّجْمِ اِذَا هَوٰىۙ", translation: "Batan yıldıza ant olsun ki,", transliteration: "Ven necmi iza heva."},
  {id: 4786, sure_no: 2, ayet_no: 2, sure_adi: "Necm", text: "مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوٰىۚ", translation: "Arkadaşınız sapkın ve azgın değil.", transliteration: "Ma dalle sahıbukum ve ma gava."},
  {id: 4787, sure_no: 3, ayet_no: 3, sure_adi: "Necm", text: "وَمَا يَنْطِقُ عَنِ الْهَوٰىۜ", translation: "Ve o, hevasından konuşmaz.", transliteration: "Ve ma yentıku anil heva."},
  {id: 4788, sure_no: 4, ayet_no: 4, sure_adi: "Necm", text: "اِنْ هُوَ اِلَّا وَحْيٌ يُوحٰىۙ", translation: "Onun size söyledikleri, kendisine vahyedilen vahiyden başkası değildir.", transliteration: "İn huve illa vahyun yuha."},
  {id: 4789, sure_no: 5, ayet_no: 5, sure_adi: "Necm", text: "عَلَّمَهُ شَد۪يدُ الْقُوٰىۙ", translation: "Ona Üstün Güç Sahibi ve Kudretli Olan öğretti.", transliteration: "Allemehu şedidul kuva."},
  {id: 4790, sure_no: 6, ayet_no: 6, sure_adi: "Necm", text: "ذُومِرَّةٍۜ  فَاسْتَوٰىۙ", translation: "Üstün Akla Sahip, Egemenlik Kurmuş Olan.", transliteration: "Zu mirreh, festeva."},
  {id: 4791, sure_no: 7, ayet_no: 7, sure_adi: "Necm", text: "وَهُوَ بِالْاُفُقِ الْاَعْلٰىۜ", translation: "O, yüksek bir ufuktaydı.", transliteration: "Ve huve bil ufukil a'la."},
  {id: 4792, sure_no: 8, ayet_no: 8, sure_adi: "Necm", text: "ثُمَّ دَنَا فَتَدَلّٰىۙ", translation: "Sonra yaklaştı ve sarktı.", transliteration: "Summe dena fe tedella."},
  {id: 4793, sure_no: 9, ayet_no: 9, sure_adi: "Necm", text: "فَكَانَ قَابَ قَوْسَيْنِ اَوْ اَدْنٰىۚ", translation: "Böylece iki yay aralığı kadar, hatta daha yakın oldu.", transliteration: "Fe kane kabe kavseyni ev edna."},
  {id: 4794, sure_no: 10, ayet_no: 10, sure_adi: "Necm", text: "فَاَوْحٰٓى اِلٰى عَبْدِه۪ مَٓا اَوْحٰىۜ", translation: "Kuluna vahyedeceği şeyi vahyetti.", transliteration: "Fe evha ila abdihi ma evha."},
  {id: 4795, sure_no: 11, ayet_no: 11, sure_adi: "Necm", text: "مَا كَذَبَ الْفُؤٰ۬ادُ مَا رَاٰى", translation: "Gönlü, gördüğünü yalanlamadı.", transliteration: "Ma kezebel fuadu ma rea."},
  {id: 4796, sure_no: 12, ayet_no: 12, sure_adi: "Necm", text: "اَفَتُمَارُونَهُ عَلٰى مَا يَرٰى", translation: "Onun gördüğü şeyden kuşku mu duyuyorsunuz?", transliteration: "E fe tumar runehu ala ma yera."},
  {id: 4797, sure_no: 13, ayet_no: 13, sure_adi: "Necm", text: "وَلَقَدْ رَاٰهُ نَزْلَةً اُخْرٰىۙ", translation: "Ant olsun ki, onu başka bir inişinde de gördü.", transliteration: "Ve lekad reahu nezleten uhra."},
  {id: 4798, sure_no: 14, ayet_no: 14, sure_adi: "Necm", text: "عِنْدَ سِدْرَةِ الْمُنْتَهٰى", translation: "Sidretu'l Münteha'nın yanında.", transliteration: "İnde sidretil munteha."},
  {id: 4799, sure_no: 15, ayet_no: 15, sure_adi: "Necm", text: "عِنْدَهَا جَنَّةُ الْمَأْوٰىۜ", translation: "Onun yanında me'va cenneti vardır.", transliteration: "İndeha cennetul me'va."},
  {id: 4800, sure_no: 16, ayet_no: 16, sure_adi: "Necm", text: "اِذْ يَغْشَى السِّدْرَةَ مَا يَغْشٰىۙ", translation: "O zaman kaplayan şey, Sidre'yi kaplıyordu.", transliteration: "İz yagşes sidrete ma yagşa."},
  {id: 4801, sure_no: 17, ayet_no: 17, sure_adi: "Necm", text: "مَا زَاغَ الْبَصَرُ وَمَا طَغٰى", translation: "Bakış şaşmadı ve haddi aşmadı.", transliteration: "Ma zagal basaru ve ma tega."},
  {id: 4802, sure_no: 18, ayet_no: 18, sure_adi: "Necm", text: "لَقَدْ رَاٰى مِنْ اٰيَاتِ رَبِّهِ الْـكُبْرٰى", translation: "Ant olsun, Rabb'inin ayetlerinin en büyüğünü gördü.", transliteration: "Lekad rea min ayati rabbihil kubra."},
  {id: 4803, sure_no: 19, ayet_no: 19, sure_adi: "Necm", text: "اَفَرَاَيْتُمُ اللَّاتَ وَالْعُزّٰىۙ", translation: "Görüyor musunuz Lat'ı ve Uzza'yı?", transliteration: "E fe reeytumul late vel uzza."},
  {id: 4804, sure_no: 20, ayet_no: 20, sure_adi: "Necm", text: "وَمَنٰوةَ الثَّالِثَةَ الْاُخْرٰى", translation: "Diğer üçüncü Menat'ı?", transliteration: "Ve menates salisetel uhra."},
  {id: 4805, sure_no: 21, ayet_no: 21, sure_adi: "Necm", text: "اَلَـكُمُ الذَّكَرُ وَلَهُ الْاُنْثٰى", translation: "Erkek sizin, dişi O'nun mu?", transliteration: "E lekumuz zekeru ve lehul unsa."},
  {id: 4806, sure_no: 22, ayet_no: 22, sure_adi: "Necm", text: "تِلْكَ اِذاً قِسْمَةٌ ض۪يزٰى", translation: "O halde bu insafsız bir paylaştırmadır.", transliteration: "Tilke izen kısmetun diza."},
  {id: 4807, sure_no: 23, ayet_no: 23, sure_adi: "Necm", text: "اِنْ هِيَ اِلَّٓا اَسْمَٓاءٌ سَمَّيْتُمُوهَٓا اَنْتُمْ وَاٰبَٓاؤُ۬كُمْ مَٓا اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطَانٍۜ اِنْ يَتَّبِعُونَ اِلَّا الظَّنَّ وَمَا تَهْوَى الْاَنْفُسُۚ وَلَقَدْ جَٓاءَهُمْ مِنْ رَبِّهِمُ الْهُدٰىۜ", translation: "Bu isimler, sizin ve atalarınızın onlara yakıştırdığı isimlerden başka bir şey değildir. Allah, onlar için hiçbir yetkilendirmede bulunmadı. Ant olsun ki, onlara Rabb'lerinden doğru yolu gösterici geldiği halde, onlar yalnızca zanna ve benliklerinin hevasına uyuyorlar.", transliteration: "İn hiye illa esmaun semmeytumuha entum ve abaukum ma enzelallahu biha min sultan, in yettebiune illez zanne ve ma tehvel enfus, ve lekad caehum min rabbihimul huda."},
  {id: 4808, sure_no: 24, ayet_no: 24, sure_adi: "Necm", text: "اَمْ لِلْاِنْسَانِ مَا تَمَنّٰىۘ", translation: "Yoksa insan için yalnızca temenni ettiği şey mi var?", transliteration: "Em lil insani ma temenna."},
  {id: 4809, sure_no: 25, ayet_no: 25, sure_adi: "Necm", text: "فَلِلّٰهِ الْاٰخِرَةُ وَالْاُو۫لٰى۟", translation: "Ahiret de dünya da Allah'ındır.", transliteration: "Fe lillahil ahiretu vel ula."},
  {id: 4810, sure_no: 26, ayet_no: 26, sure_adi: "Necm", text: "وَكَمْ مِنْ مَلَكٍ فِي السَّمٰوَاتِ لَا تُغْن۪ي شَفَاعَتُهُمْ شَيْـٔاً اِلَّا مِنْ بَعْدِ اَنْ يَأْذَنَ اللّٰهُ لِمَنْ يَشَٓاءُ وَيَرْضٰى", translation: "Göklerde nice melekler var ki, Allah'ın dilediği ve hoşnut olduğu kimseler için izin vermesi dışında onların şefaatleri hiçbir yarar sağlamaz.", transliteration: "Ve kem min melekin fis semavati la tugni şefaatuhum şey'en illa min ba'di en ye'zenallahu limen yeşau ve yerda."},
  {id: 4811, sure_no: 27, ayet_no: 27, sure_adi: "Necm", text: "اِنَّ الَّذ۪ينَ لَا يُؤْمِنُونَ بِالْاٰخِرَةِ لَيُسَمُّونَ الْمَلٰٓئِكَةَ تَسْمِيَةَ الْاُنْثٰى", translation: "Kuşkusuz ahirete inanmayanlar, melekleri dişi varlıkların adları ile adlandırıyorlar.", transliteration: "İnnellezine la yu'minune bil ahireti le yusemmunel melaikete tesmiyetel unsa."},
  {id: 4812, sure_no: 28, ayet_no: 28, sure_adi: "Necm", text: "وَمَا لَهُمْ بِه۪ مِنْ عِلْمٍۜ  اِنْ يَتَّبِعُونَ اِلَّا الظَّنَّۚ وَاِنَّ الظَّنَّ لَا يُغْن۪ي مِنَ الْحَقِّ شَيْـٔاًۚ", translation: "Oysaki onların bu konuda hiçbir bilgileri yoktur. Onlar yalnızca zanna uyuyorlar. Oysaki zan, \"gerçekten\" yana hiçbir değer taşımaz.", transliteration: "Ve ma lehum bihi min ilm, in yettebiune illez zann, ve innez zanne la yugni minel hakkı şey'a."},
  {id: 4813, sure_no: 29, ayet_no: 29, sure_adi: "Necm", text: "فَاَعْرِضْ عَنْ مَنْ تَوَلّٰى عَنْ ذِكْرِنَا وَلَمْ يُرِدْ اِلَّا الْحَيٰوةَ الدُّنْيَاۜ", translation: "Dünya hayatından başka bir şey istemeyen, zikrimizden yüz çevirenlerden sen de yüz çevir.", transliteration: "Fe a'rıd an men tevella an zikrina ve lem yurid illel hayated dunya."},
  {id: 4814, sure_no: 30, ayet_no: 30, sure_adi: "Necm", text: "ذٰلِكَ مَبْلَغُهُمْ مِنَ الْعِلْمِۜ  اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَب۪يلِه۪ وَهُوَ اَعْلَمُ بِمَنِ اهْتَدٰى", translation: "Onların bilgi düzeyi bu kadardır. Rabb'in, kimin kendi yolundan sapmış olduğunu en iyi bilendir ve O, iletildiği doğru yolda olanı da en iyi bilendir.", transliteration: "Zalike mebleguhum minel ilm, inne rabbeke huve a'lemu bi men dalle an sebilihi ve huve a'lemu bi menihteda."},
  {id: 4815, sure_no: 31, ayet_no: 31, sure_adi: "Necm", text: "وَلِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۙ لِيَجْزِيَ الَّذ۪ينَ اَسَٓاؤُ۫ا بِمَا عَمِلُوا وَيَجْزِيَ الَّذ۪ينَ اَحْسَنُوا بِالْحُسْنٰىۚ", translation: "Göklerde ve yerde olan her şey Allah'ındır. Bu, kötülük yapanların cezalandırılması ve iyilik yapanların daha iyisiyle ödüllendirilmesi içindir.", transliteration: "Ve lillahi ma fis semavati ve ma fil ardı li yecziyellezine esau bima amilu ve yecziyellezine ahsenu bil husna."},
  {id: 4816, sure_no: 32, ayet_no: 32, sure_adi: "Necm", text: "اَلَّذ۪ينَ يَجْتَنِبُونَ كَـبَٓائِرَ الْاِثْمِ وَالْفَوَاحِشَ اِلَّا اللَّمَمَۜ اِنَّ رَبَّكَ وَاسِعُ الْمَغْفِرَةِۜ هُوَ اَعْلَمُ بِكُمْ اِذْ اَنْشَاَكُمْ مِنَ الْاَرْضِ وَاِذْ اَنْتُمْ اَجِنَّةٌ ف۪ي بُطُونِ اُمَّهَاتِكُمْۚ فَلَا تُزَكُّٓوا اَنْفُسَكُمْۜ هُوَ اَعْلَمُ بِمَنِ اتَّقٰى۟", translation: "Onlar, ufak-tefek hatalara düşmek hariç, büyük günahlardan ve aşırılıklardan kaçınırlar. Kuşkusuz Rabb'in, bağışlaması bol olandır. O, sizi topraktan inşa ederken de annelerinizin karnında cenin halindeyken de ne olduğunuzu en iyi bilendir. O halde kendinizi temize çıkarmayın. O, takva1 sahibi olan kimseyi en iyi bilendir.", transliteration: "Ellezine yectenibune kebairel ismi vel fevahışe illâ-llemem, inne rabbeke vasiul magfireh, huve a'lemu bikum iz enşeekum minel ardı ve iz entum e cinnetun fi butuni ummehatikum, fe la tuzekku enfusekum, huve a'lemu bi menitteka."},
  {id: 4817, sure_no: 33, ayet_no: 33, sure_adi: "Necm", text: "اَفَرَاَيْتَ الَّذ۪ي تَوَلّٰىۙ", translation: "Peki, yüz çevireni görüyor musun?", transliteration: "E fe re'eytellezi tevella."},
  {id: 4818, sure_no: 34, ayet_no: 34, sure_adi: "Necm", text: "وَاَعْطٰى قَل۪يلاً وَاَكْدٰى", translation: "Azıcık verip, inatla cimrilik edeni?", transliteration: "Ve a'ta kalilen ve ekda."},
  {id: 4819, sure_no: 35, ayet_no: 35, sure_adi: "Necm", text: "اَعِنْدَهُ عِلْمُ الْغَيْبِ فَهُوَ يَرٰى", translation: "Yoksa o gaybın bilgisine sahip de onu mu görüyor?", transliteration: "E indehu ilmul gaybi fe huve yera."},
  {id: 4820, sure_no: 36, ayet_no: 36, sure_adi: "Necm", text: "اَمْ لَمْ يُنَبَّأْ بِمَا ف۪ي صُحُفِ مُوسٰىۙ", translation: "Musa'nın sayfalarında olanlar haber verilmedi mi?", transliteration: "Em lem yunebbe' bima fi suhufi musa."},
  {id: 4821, sure_no: 37, ayet_no: 37, sure_adi: "Necm", text: "وَاِبْرٰه۪يمَ الَّذ۪ي وَفّٰىۙ", translation: "Ve de çok vefalı İbrahim'in.", transliteration: "Ve ibrahimellezi veffa."},
  {id: 4822, sure_no: 38, ayet_no: 38, sure_adi: "Necm", text: "اَلَّا تَزِرُ وَازِرَةٌ وِزْرَ اُخْرٰىۙ", translation: "Gerçek şu ki, hiçbir günahkar, başka bir günahkarın yükünü yüklenemez.", transliteration: "Ella teziru vaziretun vizre uhra."},
  {id: 4823, sure_no: 39, ayet_no: 39, sure_adi: "Necm", text: "وَاَنْ لَيْسَ لِلْاِنْسَانِ اِلَّا مَا سَعٰىۙ", translation: "Ve gerçek şu ki, insan için çalışmasından başka bir şey yoktur.", transliteration: "Ve en leyse lil insani illa ma sea."},
  {id: 4824, sure_no: 40, ayet_no: 40, sure_adi: "Necm", text: "وَاَنَّ سَعْيَهُ سَوْفَ يُرٰىۖ", translation: "Ve onun çalışması yakında görülecektir.", transliteration: "Ve enne sa'yehu sevfe yura."},
  {id: 4825, sure_no: 41, ayet_no: 41, sure_adi: "Necm", text: "ثُمَّ يُجْزٰيهُ الْجَزَٓاءَ الْاَوْفٰىۙ", translation: "Sonra karşılığı kendisine eksiksiz olarak verilecektir.", transliteration: "Summe yuczahul cezael evfa."},
  {id: 4826, sure_no: 42, ayet_no: 42, sure_adi: "Necm", text: "وَاَنَّ اِلٰى رَبِّكَ الْمُنْتَهٰىۙ", translation: "Sonunda dönüş yalnızca Rabb'inedir.", transliteration: "Ve enne ila rabbikel munteha."},
  {id: 4827, sure_no: 43, ayet_no: 43, sure_adi: "Necm", text: "وَاَنَّهُ هُوَ اَضْحَكَ وَاَبْكٰىۙ", translation: "Güldüren de ağlatan da O'dur.", transliteration: "Ve ennehu huve adhake ve ebka."},
  {id: 4828, sure_no: 44, ayet_no: 44, sure_adi: "Necm", text: "وَاَنَّهُ هُوَ اَمَاتَ وَاَحْيَاۙ", translation: "Öldüren de dirilten de O'dur.", transliteration: "Ve ennehu huve emate ve ahya."},
  {id: 4829, sure_no: 45, ayet_no: 45, sure_adi: "Necm", text: "وَاَنَّهُ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰىۙ", translation: "O, erkek ve dişi çiftler yarattı.", transliteration: "Ve ennehu halakaz zevceyniz zekere vel unsa."},
  {id: 4830, sure_no: 46, ayet_no: 46, sure_adi: "Necm", text: "مِنْ نُطْفَةٍ اِذَا تُمْنٰىۖ", translation: "Atıldığında, nutfeden.", transliteration: "Min nutfetin iza tumna."},
  {id: 4831, sure_no: 47, ayet_no: 47, sure_adi: "Necm", text: "وَاَنَّ عَلَيْهِ النَّشْاَةَ الْاُخْرٰىۙ", translation: "Ve kuşkusuz bundan sonraki diriltme de yalnızca O'na aittir.", transliteration: "Ve enne aleyhin neş'etel uhra."},
  {id: 4832, sure_no: 48, ayet_no: 48, sure_adi: "Necm", text: "وَاَنَّهُ هُوَ اَغْنٰى وَاَقْنٰىۙ", translation: "Zenginlik veren de O'dur, sınırlayan da.", transliteration: "Ve ennehu huve agna ve akna."},
  {id: 4833, sure_no: 49, ayet_no: 49, sure_adi: "Necm", text: "وَاَنَّهُ هُوَ رَبُّ الشِّعْرٰىۙ", translation: "Şi'ra'nın Rabb'i de O'dur.", transliteration: "Ve ennehu huve rabbuş şı'ra."},
  {id: 4834, sure_no: 50, ayet_no: 50, sure_adi: "Necm", text: "وَاَنَّـهُٓ اَهْلَكَ عَاداًۨ الْاُو۫لٰىۙ", translation: "Önceki Ad'ı yok etti.", transliteration: "Ve ennehu ehleke adenil ula."},
  {id: 4835, sure_no: 51, ayet_no: 51, sure_adi: "Necm", text: "وَثَمُودَا۬ فَمَٓا اَبْـقٰىۙ", translation: "Ve Semud'u da. Geride kimseyi bırakmadı.", transliteration: "Ve semude fema ebka."},
  {id: 4836, sure_no: 52, ayet_no: 52, sure_adi: "Necm", text: "وَقَوْمَ نُوحٍ مِنْ قَبْلُۜ اِنَّهُمْ كَانُوا هُمْ اَظْلَمَ وَاَطْغٰىۜ", translation: "Daha önce de Nuh'un halkını. Onlar, daha zalim ve daha azgın olanlardı.", transliteration: "Ve kavme nuhın min kabl, innehum kanu hum azleme ve atga."},
  {id: 4837, sure_no: 53, ayet_no: 53, sure_adi: "Necm", text: "وَالْمُؤْتَفِكَةَ اَهْوٰىۙ", translation: "Alt üst edilen beldeyi de yerin dibine geçirdi.", transliteration: "Vel mu'tefikete ehva."},
  {id: 4838, sure_no: 54, ayet_no: 54, sure_adi: "Necm", text: "فَغَشّٰيهَا مَا غَشّٰىۚ", translation: "Artık onu kaplayan şey kapladı.", transliteration: "Fe gaşşaha ma gaşşa."},
  {id: 4839, sure_no: 55, ayet_no: 55, sure_adi: "Necm", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكَ تَتَمَارٰى", translation: "O halde Rabb'inin hangi nimetinden kuşku duyuyorsun?", transliteration: "Fe bi eyyi alai rabbike tetemara."},
  {id: 4840, sure_no: 56, ayet_no: 56, sure_adi: "Necm", text: "هٰذَا نَذ۪يرٌ مِنَ النُّذُرِ الْاُو۫لٰى", translation: "Bu uyarıcı da ilk uyarıcılar gibi bir uyarıcıdır.", transliteration: "Haza nezirun minen nuzuril ula."},
  {id: 4841, sure_no: 57, ayet_no: 57, sure_adi: "Necm", text: "اَزِفَتِ الْاٰزِفَةُۚ", translation: "Yaklaşan, yaklaştı.", transliteration: "Ezifetil azifeh."},
  {id: 4842, sure_no: 58, ayet_no: 58, sure_adi: "Necm", text: "لَيْسَ لَهَا مِنْ دُونِ اللّٰهِ كَاشِفَةٌ", translation: "Onu, Allah'tan başkası çeviremez.", transliteration: "Leyse leha min dunillahi kaşifeh."},
  {id: 4843, sure_no: 59, ayet_no: 59, sure_adi: "Necm", text: "اَفَمِنْ هٰذَا الْحَد۪يثِ تَعْجَبُونَۙ", translation: "Bu hadislere mi şaşırıyorsunuz?", transliteration: "E fe min hazel hadisi ta'cebun."},
  {id: 4844, sure_no: 60, ayet_no: 60, sure_adi: "Necm", text: "وَتَضْحَكُونَ وَلَا تَبْكُونَۙ", translation: "Ağlanacak halinize gülüyorsunuz.", transliteration: "Ve tedhakune ve la tebkun."},
  {id: 4845, sure_no: 61, ayet_no: 61, sure_adi: "Necm", text: "وَاَنْتُمْ سَامِدُونَ", translation: "Ve siz gaflete dalanlarsınız!", transliteration: "Ve entum samidun."},
  {id: 4846, sure_no: 62, ayet_no: 62, sure_adi: "Necm", text: "فَاسْجُدُوا لِلّٰهِ وَاعْبُدُوا", translation: "Artık bu gafletten kurtulup, Allah'a secde edin ve O'na kul olun.", transliteration: "Fescudu lillahi va'budu."},
  {id: 4847, sure_no: 1, ayet_no: 1, sure_adi: "Kamer", text: "اِقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ", translation: "Sa'at yaklaştı ve Ay yarıldı.", transliteration: "İkterebetis saatu ven şakkal kamer."},
  {id: 4848, sure_no: 2, ayet_no: 2, sure_adi: "Kamer", text: "وَاِنْ يَرَوْا اٰيَةً يُعْرِضُوا وَيَقُولُوا سِحْرٌ مُسْتَمِرٌّ", translation: "Onlar, bir ayet görseler, hemen yüz çevirirler. Ve \"Bu süregelen bir büyüdür.\" derler.", transliteration: "Ve in yerev ayeten yu'ridu ve yekulu sihrun mustemirr."},
  {id: 4849, sure_no: 3, ayet_no: 3, sure_adi: "Kamer", text: "وَكَذَّبُوا وَاتَّبَعُٓوا اَهْوَٓاءَهُمْ وَكُلُّ اَمْرٍ مُسْتَقِرٌّ", translation: "Ve yalanladılar. Kendi tutkularına uydular. Oysa her şey kararlaştırılmıştır.", transliteration: "Ve kezzebu vettebeu ehvaehum ve kullu emrin mustekırr."},
  {id: 4850, sure_no: 4, ayet_no: 4, sure_adi: "Kamer", text: "وَلَقَدْ جَٓاءَهُمْ مِنَ الْاَنْـبَٓاءِ مَا ف۪يهِ مُزْدَجَرٌۙ", translation: "Ant olsun ki onlara, yanılgılarını giderecek nice haberler geldi.", transliteration: "Ve lekad caehum minel enbai ma fihi muzdecer."},
  {id: 4851, sure_no: 5, ayet_no: 5, sure_adi: "Kamer", text: "حِكْمَةٌ بَالِغَةٌ فَمَا تُغْنِ النُّذُرُۙ", translation: "Yüksek seviyede hikmetli haberler. Buna rağmen uyarıların bir yararı olmadı.", transliteration: "Hikmetun baligatun fe ma tugnin nuzur."},
  {id: 4852, sure_no: 6, ayet_no: 6, sure_adi: "Kamer", text: "فَتَوَلَّ عَنْهُمْۢ يَوْمَ يَدْعُ الدَّاعِ اِلٰى شَيْءٍ نُكُرٍۙ", translation: "O halde onlardan yüz çevir. O Gün çağırıcı onları hiç hoşlanmayacakları şeye çağıracak.", transliteration: "Fe tevelle anhum, yevme yed'ud dai ila şey'in nukur."},
  {id: 4853, sure_no: 7, ayet_no: 7, sure_adi: "Kamer", text: "خُشَّعاً اَبْصَارُهُمْ يَخْرُجُونَ مِنَ الْاَجْدَاثِ كَاَنَّهُمْ جَرَادٌ مُنْتَشِرٌۙ", translation: "Kabirlerinden baygın gözlerle çıkarlar. Etrafa dağılmış çekirgeler gibidirler.", transliteration: "Huşşe'an ebsaruhum yahrucune minel ecdasi keennehum ceradun munteşir."},
  {id: 4854, sure_no: 8, ayet_no: 8, sure_adi: "Kamer", text: "مُهْطِع۪ينَ اِلَى الدَّاعِۜ يَقُولُ الْـكَافِرُونَ هٰذَا يَوْمٌ عَسِرٌ", translation: "Çağırıcıya doğru koşan Kafirler: \"Bu, çok zor bir gün.\" diyecekler.", transliteration: "Muhtıine iled dai, yekulul kafirune haza yevmun asir."},
  {id: 4855, sure_no: 9, ayet_no: 9, sure_adi: "Kamer", text: "كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ فَـكَذَّبُوا عَبْدَنَا وَقَالُوا مَجْنُونٌ وَازْدُجِرَ", translation: "Onlardan önce Nuh'un halkı da yalanladı. Kulumuzu, \"O delinin biridir.\" diye yalanladılar. Kulumuz zorluk çıkarılarak engellendi.", transliteration: "Kezzebet kablehum kavmu nuhın fe kezzebu abdena ve kalu mecnunun vezducir."},
  {id: 4856, sure_no: 10, ayet_no: 10, sure_adi: "Kamer", text: "فَدَعَا رَبَّهُٓ  اَنّ۪ي مَغْلُوبٌ فَانْتَصِرْ", translation: "Sonunda Rabb'ine çağrıda bulundu: \"Doğrusu ben yenik düştüm, bana yardım et.\"", transliteration: "Fe dea rabbehu enni maglubun fentasır."},
  {id: 4857, sure_no: 11, ayet_no: 11, sure_adi: "Kamer", text: "فَفَتَحْنَٓا اَبْوَابَ السَّمَٓاءِ بِمَٓاءٍ مُنْهَمِرٍۘ", translation: "Biz de hemen göğün kapılarını gürül gürül boşalan su ile açtık.", transliteration: "Fe fetahna ebvabes semai bi main munhemir."},
  {id: 4858, sure_no: 12, ayet_no: 12, sure_adi: "Kamer", text: "وَفَجَّرْنَا الْاَرْضَ عُيُوناً فَالْتَقَى الْمَٓاءُ عَلٰٓى اَمْرٍ قَدْ قُدِرَۚ", translation: "Yeryüzünde de kaynakları fışkırttık. Böylece sular kararlaştırılan amaç için birleşti.", transliteration: "Ve feccernel arda uyunen feltekalmau ala emrin kad kudir."},
  {id: 4859, sure_no: 13, ayet_no: 13, sure_adi: "Kamer", text: "وَحَمَلْنَاهُ عَلٰى ذَاتِ اَلْوَاحٍ وَدُسُرٍۙ", translation: "Onu ağaç lifi ile birbirine bağlanmış tahtalar üzerinde taşıdık.", transliteration: "Ve hamelnahu ala zati elvahın ve dusur."},
  {id: 4860, sure_no: 14, ayet_no: 14, sure_adi: "Kamer", text: "تَجْر۪ي بِاَعْيُنِنَاۚ جَزَٓاءً لِمَنْ كَانَ كُفِرَ", translation: "Yalanlanan kimseye bir ödül olarak, gözetimimiz altında yüzüp gidiyordu.", transliteration: "Tecri bi a'yunina, cezaen li men kane kufir."},
  {id: 4861, sure_no: 15, ayet_no: 15, sure_adi: "Kamer", text: "وَلَقَدْ تَرَكْنَاهَٓا اٰيَةً فَهَلْ مِنْ مُدَّكِرٍ", translation: "Ant olsun ki onu bir ayet olarak bıraktık. İbret alan yok mudur?", transliteration: "Ve lekad tereknaha ayeten fe hel min muddekir."},
  {id: 4862, sure_no: 16, ayet_no: 16, sure_adi: "Kamer", text: "فَـكَيْفَ كَانَ عَذَاب۪ي وَنُذُرِ", translation: "Ama azabım ve uyarılarım nasıl oldu?", transliteration: "Fe keyfe kane azabi ve nuzur."},
  {id: 4863, sure_no: 17, ayet_no: 17, sure_adi: "Kamer", text: "وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ", translation: "Ant olsun Biz, Kur'an'ı öğüt alınsın diye kolaylaştırdık. Var mı öğüt alıp düşünen?", transliteration: "Ve lekad yessernel kur'ane liz zikri fe hel min muddekir."},
  {id: 4864, sure_no: 18, ayet_no: 18, sure_adi: "Kamer", text: "كَذَّبَتْ عَادٌ فَـكَيْفَ كَانَ عَذَاب۪ي وَنُذُرِ", translation: "Ad da yalanladı. Ama azabım ve uyarılarım nasıl oldu?", transliteration: "Kezzebet adun fe keyfe kane azabi ve nuzur."},
  {id: 4865, sure_no: 19, ayet_no: 19, sure_adi: "Kamer", text: "اِنَّٓا اَرْسَلْنَا عَلَيْهِمْ ر۪يحاً صَرْصَراً ف۪ي يَوْمِ نَحْسٍ مُسْتَمِرٍّۙ", translation: "Biz, onların üzerine felaketleri gün boyu süren çok sarsıcı rüzgarı gönderdik.", transliteration: "İnna erselna aleyhim rihan sarsaren fi yevmi nahsin mustemirr."},
  {id: 4866, sure_no: 20, ayet_no: 20, sure_adi: "Kamer", text: "تَنْزِ عُ النَّاسَۙ كَاَنَّهُمْ اَعْجَازُ نَخْلٍ مُنْقَعِرٍ", translation: "İnsanları, kökünden sökülmüş hurma kütükleri gibi savurup atıyordu.", transliteration: "Tenziun nase ke ennehum a'cazu nahlin munkair."},
  {id: 4867, sure_no: 21, ayet_no: 21, sure_adi: "Kamer", text: "فَـكَيْفَ كَانَ عَذَاب۪ي وَنُذُرِ", translation: "Ama azabım ve uyarılarım nasıl oldu?", transliteration: "Fe keyfe kane azabi ve nuzur."},
  {id: 4868, sure_no: 22, ayet_no: 22, sure_adi: "Kamer", text: "وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ۟", translation: "Ant olsun Biz, Kur'an'ı öğüt alınsın diye kolaylaştırdık. Var mı öğüt alıp düşünen?", transliteration: "Ve lekad yessernel kur'ane liz zikri fe hel min muddekir."},
  {id: 4869, sure_no: 23, ayet_no: 23, sure_adi: "Kamer", text: "كَذَّبَتْ ثَمُودُ بِالنُّذُرِ", translation: "Semud da uyarıcıları yalanladı.", transliteration: "Kezzebet semudu bin nuzur."},
  {id: 4870, sure_no: 24, ayet_no: 24, sure_adi: "Kamer", text: "فَقَالُٓوا اَبَشَراً مِنَّا وَاحِداً نَتَّبِعُهُٓۙ  اِنَّٓا اِذاً لَف۪ي ضَلَالٍ وَسُعُرٍ", translation: "Şöyle dediler: \"Bizden biri olan bir beşere mı biz ona mı uyacağız? Öyle yaparsak kesinlikle bir sapkınlık ve çılgınlık yapmış oluruz.", transliteration: "Fe kalu ebeşeren minna vahiden nettebiuhu inna izen lefi dalalin ve suur."},
  {id: 4871, sure_no: 25, ayet_no: 25, sure_adi: "Kamer", text: "ءَاُلْقِيَ الذِّكْرُ عَلَيْهِ مِنْ بَيْنِنَا بَلْ هُوَ كَذَّابٌ اَشِرٌ", translation: "Zikir. aramızdan bula bula onu mu buldu? Hayır! O küstah bir yalancıdır.", transliteration: "E ulkıyez zikru aleyhi min beynina bel huve kezzabun eşir."},
  {id: 4872, sure_no: 26, ayet_no: 26, sure_adi: "Kamer", text: "سَيَعْلَمُونَ غَداً مَنِ الْـكَذَّابُ الْاَشِرُ", translation: "Onlar, yarın küstah yalancının kim olduğunu anlayacaklar!", transliteration: "Se ya'lemune gaden menil kezzabul eşir."},
  {id: 4873, sure_no: 27, ayet_no: 27, sure_adi: "Kamer", text: "اِنَّا مُرْسِلُوا النَّاقَةِ فِتْنَةً لَهُمْ فَارْتَقِبْهُمْ وَاصْطَبِرْۘ", translation: "Biz, fitne olsun diye onlara dişi deveyi gönderdik. Artık onları gözetle ve sabret.", transliteration: "İnna mursilun nakati fitneten lehum fertekıbhum vestabir."},
  {id: 4874, sure_no: 28, ayet_no: 28, sure_adi: "Kamer", text: "وَنَبِّئْهُمْ اَنَّ الْمَٓاءَ قِسْمَةٌ بَيْنَهُمْۚ  كُلُّ شِرْبٍ مُحْتَضَرٌ", translation: "Onlara, suyun bölüşüldüğünü haber ver. Her içiş hazır kılınmıştır.", transliteration: "Ve nebbi'hum ennel mae kısmetun beynehum, kullu şirbin muhtedar."},
  {id: 4875, sure_no: 29, ayet_no: 29, sure_adi: "Kamer", text: "فَنَادَوْا صَاحِبَهُمْ فَتَعَاطٰى فَعَقَرَ", translation: "Derken arkadaşlarını çağırdılar, o da bıçağa sarıldı ve kesti.", transliteration: "Fe nadev sahıbehum fe teata fe akar."},
  {id: 4876, sure_no: 30, ayet_no: 30, sure_adi: "Kamer", text: "فَـكَيْفَ كَانَ عَذَاب۪ي وَنُذُرِ", translation: "Ama azabım ve uyarılarım nasıl oldu?", transliteration: "Fe keyfe kane azabi ve nuzur."},
  {id: 4877, sure_no: 31, ayet_no: 31, sure_adi: "Kamer", text: "اِنَّٓا اَرْسَلْنَا عَلَيْهِمْ صَيْحَةً وَاحِدَةً فَـكَانُوا كَـهَش۪يمِ الْمُحْتَظِرِ", translation: "Biz, onların üzerlerine tek bir sayha gönderdik. Böylece ağıldaki ufalanmış kuru ot gibi oldular.", transliteration: "İnna erselna aleyhim sayhaten vahıdeten fe kanu ke heşimil muhtezir."},
  {id: 4878, sure_no: 32, ayet_no: 32, sure_adi: "Kamer", text: "وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ", translation: "Ant olsun Biz, Kur'an'ı öğüt alınsın diye kolaylaştırdık. Var mı öğüt alıp düşünen?", transliteration: "Ve lekad yessernel kur'ane liz zikri fe hel min muddekir."},
  {id: 4879, sure_no: 33, ayet_no: 33, sure_adi: "Kamer", text: "كَذَّبَتْ قَوْمُ لُوطٍ بِالنُّذُرِ", translation: "Lut'un halkı da uyarıcılarını yalanladı.", transliteration: "Kezzebet kavmu lutın bin nuzur."},
  {id: 4880, sure_no: 34, ayet_no: 34, sure_adi: "Kamer", text: "اِنَّٓا اَرْسَلْنَا عَلَيْهِمْ حَاصِباً اِلَّٓا اٰلَ لُوطٍۜ  نَجَّيْنَاهُمْ بِسَحَرٍۙ", translation: "Biz de onların üzerlerine yok edici kasırga gönderdik. Lut'un yanında yer alanlar bunun dışında tutuldu. Seher vakti onları kurtardık.", transliteration: "İnna erselna aleyhim hasiben illa ale lut, necceynahum bi sehar."},
  {id: 4881, sure_no: 35, ayet_no: 35, sure_adi: "Kamer", text: "نِعْمَةً مِنْ عِنْدِنَاۜ   كَذٰلِكَ نَجْز۪ي مَنْ شَكَرَ", translation: "İşte şükredenleri katımızdan bir nimet olarak böyle ödüllendiririz.", transliteration: "Ni'meten min indina, kezalike neczi men şeker."},
  {id: 4882, sure_no: 36, ayet_no: 36, sure_adi: "Kamer", text: "وَلَقَدْ اَنْذَرَهُمْ بَطْشَتَنَا فَتَمَارَوْا بِالنُّذُرِ", translation: "Ant olsun ki yakalayıp tutuşumuza karşı onları uyarmıştı. Ne var ki onlar bu uyarıları kuşku ile karşıladılar.", transliteration: "Ve lekad enzerehum batşetena fe temarev bin nuzur."},
  {id: 4883, sure_no: 37, ayet_no: 37, sure_adi: "Kamer", text: "وَلَقَدْ رَاوَدُوهُ عَنْ ضَيْفِه۪ فَطَمَسْنَٓا اَعْيُنَهُمْ فَذُوقُوا عَذَاب۪ي وَنُذُرِ", translation: "Ant olsun ki onun misafirlerinden cinsel yönden yararlanmak istediler. Bunun üzerine gözlerini sildik. Azabımı ve uyarılarımı tadın.", transliteration: "Ve lekad raveduhu an dayfihi fe tamesna a'yunehum fe zuku azabi ve nuzur."},
  {id: 4884, sure_no: 38, ayet_no: 38, sure_adi: "Kamer", text: "وَلَقَدْ صَبَّحَهُمْ بُكْرَةً عَذَابٌ مُسْتَقِرٌّۚ", translation: "Ant olsun ki, onları sabahleyin kalıcı bir azap yakaladı.", transliteration: "Ve lekad sabbehahum bukreten azabun mustekırr."},
  {id: 4885, sure_no: 39, ayet_no: 39, sure_adi: "Kamer", text: "فَذُوقُوا عَذَاب۪ي وَنُذُرِ", translation: "Haydi! Tadın bakalım, azabımı ve uyarılarımı!", transliteration: "Fe zuku azabi ve nuzur."},
  {id: 4886, sure_no: 40, ayet_no: 40, sure_adi: "Kamer", text: "وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ۟", translation: "Ant olsun Biz, Kur'an'ı öğüt alınsın diye kolaylaştırdık. Var mı öğüt alıp düşünen?", transliteration: "Ve lekad yessernel kur'ane liz zikri fe hel min muddekir."},
  {id: 4887, sure_no: 41, ayet_no: 41, sure_adi: "Kamer", text: "وَلَقَدْ جَٓاءَ اٰلَ فِرْعَوْنَ النُّذُرُۚ", translation: "Ant olsun ki, Firavun ailesine de uyarıcılar gelmişti.", transliteration: "Ve lekad cae ale fir'avnen nuzur."},
  {id: 4888, sure_no: 42, ayet_no: 42, sure_adi: "Kamer", text: "كَذَّبُوا بِاٰيَاتِنَا كُلِّهَا فَاَخَذْنَاهُمْ اَخْذَ عَز۪يزٍ مُقْتَدِرٍ", translation: "Ayetlerimizin tamamını yalanladılar. Biz de onları gücümüze, üstünlüğümüze yaraşır bir yakalayışla yakalayıverdik.", transliteration: "Kezzebu bi ayatina kulliha fe ehaznahum ahze azizin muktedir."},
  {id: 4889, sure_no: 43, ayet_no: 43, sure_adi: "Kamer", text: "اَكُفَّارُكُمْ خَيْرٌ مِنْ اُو۬لٰٓئِكُمْ اَمْ لَـكُمْ بَرَٓاءَةٌ فِي الزُّبُرِۚ", translation: "Sizin kafirleriniz onlardan daha mı hayırlı; yoksa kitaplarda size dokunulmayacağına dair söz mü verildi?", transliteration: "E kuffarukum hayrun min ulaikum em lekum beraetun fiz zubur."},
  {id: 4890, sure_no: 44, ayet_no: 44, sure_adi: "Kamer", text: "اَمْ يَقُولُونَ نَحْنُ جَم۪يعٌ مُنْتَصِرٌ", translation: "Yoksa: \"Biz, güç birliği etmiş yenilmez bir toplumuz.\" mu diyorlar?", transliteration: "Em yekulune nahnu cemiun muntesir."},
  {id: 4891, sure_no: 45, ayet_no: 45, sure_adi: "Kamer", text: "سَيُهْزَمُ الْجَمْعُ وَيُوَلُّونَ الدُّبُرَ", translation: "Yakında o topluluk bozguna uğrayacak ve darmadağın olacaklar.", transliteration: "Se yuhzemul cem'u ve yuvelluned dubur."},
  {id: 4892, sure_no: 46, ayet_no: 46, sure_adi: "Kamer", text: "بَلِ السَّاعَةُ مَوْعِدُهُمْ وَالسَّاعَةُ اَدْهٰى وَاَمَرُّ", translation: "Hayır! Asıl onlara vaat edilen o Sa'at'tir. Ve o Sa'at, daha korkunç daha dehşetlidir.", transliteration: "Belis saatu mev'ıduhum ves sa'atu edha ve emerr."},
  {id: 4893, sure_no: 47, ayet_no: 47, sure_adi: "Kamer", text: "اِنَّ الْمُجْرِم۪ينَ ف۪ي ضَلَالٍ وَسُعُرٍۢ", translation: "Kuşkusuz, mücrimler sapkınlık ve çılgınlık içindedirler.", transliteration: "İnnel mucrimine fi dalalin ve suur."},
  {id: 4894, sure_no: 48, ayet_no: 48, sure_adi: "Kamer", text: "يَوْمَ يُسْحَبُونَ فِي النَّارِ عَلٰى وُجُوهِهِمْۜ ذُوقُوا مَسَّ سَقَرَ", translation: "O Gün yüz üstü sürülerek ateşe atılırlar. \"Cehennem ateşinin dokunuşunu tadın!\"", transliteration: "Yevme yushabune fin nari ala vucuhihim, zuku messe sekar."},
  {id: 4895, sure_no: 49, ayet_no: 49, sure_adi: "Kamer", text: "اِنَّا  كُلَّ شَيْءٍ خَلَقْنَاهُ بِقَدَرٍ", translation: "Biz, her şeyi bir yasayla yarattık.", transliteration: "İnna kulle şey'in halaknahu bi kader."},
  {id: 4896, sure_no: 50, ayet_no: 50, sure_adi: "Kamer", text: "وَمَٓا اَمْرُنَٓا اِلَّا وَاحِدَةٌ  كَلَمْحٍ بِالْبَصَرِ", translation: "Ve buyruğumuz bir tek buyruktur. Gözün bir anlık bakışı gibidir.", transliteration: "Ve ma emruna illa vahıdetun ke lemhın bil basar."},
  {id: 4897, sure_no: 51, ayet_no: 51, sure_adi: "Kamer", text: "وَلَقَدْ اَهْلَكْنَٓا اَشْيَاعَكُمْ فَهَلْ مِنْ مُدَّكِرٍ", translation: "Ant olsun ki size benzer nicelerini yok ettik. Buna rağmen düşünen mi var?", transliteration: "Ve lekad ehlekna eşyaakum fe hel min muddekir."},
  {id: 4898, sure_no: 52, ayet_no: 52, sure_adi: "Kamer", text: "وَكُلُّ شَيْءٍ فَعَلُوهُ فِي الزُّبُرِ", translation: "Onların yaptığı her şey kitaplardadır.", transliteration: "Ve kullu şey'in fe aluhu fiz zubur."},
  {id: 4899, sure_no: 53, ayet_no: 53, sure_adi: "Kamer", text: "وَكُلُّ صَغ۪يرٍ وَكَب۪يرٍ مُسْتَطَرٌ", translation: "Ve küçük büyük her şey yazılmıştır.", transliteration: "Ve kullu sagirin ve kebirin mustetar."},
  {id: 4900, sure_no: 54, ayet_no: 54, sure_adi: "Kamer", text: "اِنَّ الْمُتَّق۪ينَ ف۪ي جَنَّاتٍ وَنَهَرٍۙ", translation: "Takva sahipleri cennetlerde ve nehir kenarlarındadır.", transliteration: "İnnel muttekine fi cennatin ve neher."},
  {id: 4901, sure_no: 55, ayet_no: 55, sure_adi: "Kamer", text: "ف۪ي مَقْعَدِ صِدْقٍ عِنْدَ مَل۪يكٍ مُقْتَدِرٍ", translation: "Sonsuz Güç Sahibi yöneticinin katında, sadakat makamındadırlar.", transliteration: "Fi mak'adi sıdkın inde melikin muktedir."},
  {id: 4902, sure_no: 1, ayet_no: 1, sure_adi: "Rahman", text: "اَلرَّحْمٰنُۙ", translation: "Rahman.", transliteration: "Er rahman."},
  {id: 4903, sure_no: 2, ayet_no: 2, sure_adi: "Rahman", text: "عَلَّمَ الْقُرْاٰنَۜ", translation: "Kur'an'ı öğretti.", transliteration: "Allemel kur'an."},
  {id: 4904, sure_no: 3, ayet_no: 3, sure_adi: "Rahman", text: "خَلَقَ الْاِنْسَانَۙ", translation: "İnsanı yarattı.", transliteration: "Halakal insan."},
  {id: 4905, sure_no: 4, ayet_no: 4, sure_adi: "Rahman", text: "عَلَّمَهُ الْبَيَانَ", translation: "Ona beyanı öğretti.", transliteration: "Allemehul beyan."},
  {id: 4906, sure_no: 5, ayet_no: 5, sure_adi: "Rahman", text: "اَلشَّمْسُ وَالْقَمَرُ بِحُسْبَانٍۖ", translation: "Güneş ve Ay hesap iledir.", transliteration: "Eş şemsu vel kameru bi husban."},
  {id: 4907, sure_no: 6, ayet_no: 6, sure_adi: "Rahman", text: "وَالنَّجْمُ وَالشَّجَرُ يَسْجُدَانِ", translation: "Bitkiler ve ağaçlar, ikisi de secde ederler.", transliteration: "Ven necmu veş şeceru yescudan."},
  {id: 4908, sure_no: 7, ayet_no: 7, sure_adi: "Rahman", text: "وَالسَّمَٓاءَ رَفَعَهَا وَوَضَعَ الْم۪يزَانَۙ", translation: "Ve sema; onu yükseltti. Ve ölçüyü koydu.", transliteration: "Ves semae refeaha ve vedaal mizan."},
  {id: 4909, sure_no: 8, ayet_no: 8, sure_adi: "Rahman", text: "اَلَّا تَطْغَوْا فِي الْم۪يزَانِ", translation: "Ölçüyü çiğnemeyin.", transliteration: "Ella tatgav fil mizan."},
  {id: 4910, sure_no: 9, ayet_no: 9, sure_adi: "Rahman", text: "وَاَق۪يمُوا الْوَزْنَ بِالْقِسْطِ وَلَا تُخْسِرُوا الْم۪يزَانَ", translation: "Tartıyı hakkaniyetle ayakta tutun. Ölçüyü eksiltmeyin.", transliteration: "Ve ekimul vezne bil kıstı ve la tuhsırul mizan."},
  {id: 4911, sure_no: 10, ayet_no: 10, sure_adi: "Rahman", text: "وَالْاَرْضَ وَضَعَهَا لِلْاَنَامِۙ", translation: "Ve yeryüzü; onu yaratıklar için alçalttı.", transliteration: "Vel arda vedaaha lil enam."},
  {id: 4912, sure_no: 11, ayet_no: 11, sure_adi: "Rahman", text: "ف۪يهَا فَاكِهَةٌۖ وَالنَّخْلُ ذَاتُ الْاَكْمَامِ", translation: "Orada meyveler ve salkım salkım hurma ağaçları var.", transliteration: "Fiha fakihetun vennahlu zatul ekmam."},
  {id: 4913, sure_no: 12, ayet_no: 12, sure_adi: "Rahman", text: "وَالْحَبُّ ذُوالْعَصْفِ وَالرَّيْحَانُۚ", translation: "Yapraklı taneler ve güzel kokulu bitkiler var.", transliteration: "Vel habbu zul asfi ver reyhan."},
  {id: 4914, sure_no: 13, ayet_no: 13, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4915, sure_no: 14, ayet_no: 14, sure_adi: "Rahman", text: "خَلَقَ الْاِنْسَانَ مِنْ صَلْصَالٍ كَالْفَخَّارِۙ", translation: "İnsanı salsalinden, çömlek gibi pişmiş kuru çamurdan yarattı.", transliteration: "Halakal insane min salsalin kel fehhar."},
  {id: 4916, sure_no: 15, ayet_no: 15, sure_adi: "Rahman", text: "وَخَلَقَ الْجَٓانَّ مِنْ مَارِجٍ مِنْ نَارٍۚ", translation: "Ve cinleri dumanı olmayan ateşten yarattı.", transliteration: "Ve halakal canne min maricin min nar."},
  {id: 4917, sure_no: 16, ayet_no: 16, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4918, sure_no: 17, ayet_no: 17, sure_adi: "Rahman", text: "رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِۚ", translation: "İki doğunun ve iki batının Rabb'idir.", transliteration: "Rabbul meşrikayni ve rabbul magribeyn."},
  {id: 4919, sure_no: 18, ayet_no: 18, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4920, sure_no: 19, ayet_no: 19, sure_adi: "Rahman", text: "مَرَجَ الْبَحْرَيْنِ يَلْتَقِيَانِۙ", translation: "İki denizi birbirine kavuşmak üzere saldı.", transliteration: "Merecel bahreyni yeltekıyan."},
  {id: 4921, sure_no: 20, ayet_no: 20, sure_adi: "Rahman", text: "بَيْنَهُمَا بَرْزَخٌ لَا يَبْغِيَانِۚ", translation: "Aralarında bir engel var, birbirlerinin sınırını geçip karışmıyorlar.", transliteration: "Beynehuma berzehun la yebgıyan."},
  {id: 4922, sure_no: 21, ayet_no: 21, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4923, sure_no: 22, ayet_no: 22, sure_adi: "Rahman", text: "يَخْرُجُ مِنْهُمَا اللُّؤْلُؤُ۬ وَالْمَرْجَانُۚ", translation: "İkisinden de inci ve mercan çıkar.", transliteration: "Yahrucu min humel luluu vel mercan."},
  {id: 4924, sure_no: 23, ayet_no: 23, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4925, sure_no: 24, ayet_no: 24, sure_adi: "Rahman", text: "وَلَهُ الْجَوَارِ الْمُنْشَاٰتُ فِي الْبَحْرِ كَالْاَعْلَامِۚ", translation: "Denizde akıp giden, dağlar gibi yapılmış olan gemiler O'nundur.", transliteration: "Ve lehul cevaril munşeatu fil bahri kel alam."},
  {id: 4926, sure_no: 25, ayet_no: 25, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ۟", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4927, sure_no: 26, ayet_no: 26, sure_adi: "Rahman", text: "كُلُّ مَنْ عَلَيْهَا فَانٍۚ", translation: "Onun üzerindeki herkes yok olacaktır.", transliteration: "Kullu men aleyha fan."},
  {id: 4928, sure_no: 27, ayet_no: 27, sure_adi: "Rahman", text: "وَيَبْقٰى وَجْهُ رَبِّكَ ذُوالْجَلَالِ وَالْاِكْرَامِۚ", translation: "Kalıcı olan yalnız Celal ve İkram Sahibi Rabb'inin kendisidir.", transliteration: "Ve yebka vechu rabbike zul celali vel ikram."},
  {id: 4929, sure_no: 28, ayet_no: 28, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4930, sure_no: 29, ayet_no: 29, sure_adi: "Rahman", text: "يَسْـَٔلُهُ مَنْ فِي السَّمٰوَاتِ وَالْاَرْضِۜ كُلَّ يَوْمٍ هُوَ ف۪ي شَأْنٍۚ", translation: "Göklerde ve yerde olanlar, O'ndan isterler. O, her gün bir şan işindedir.", transliteration: "Yes' eluhu men fis semavati vel ard, kulle yevmin huve fi şe'nin."},
  {id: 4931, sure_no: 30, ayet_no: 30, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4932, sure_no: 31, ayet_no: 31, sure_adi: "Rahman", text: "سَنَفْرُغُ لَكُمْ اَيُّهَ الثَّقَلَانِۚ", translation: "Ey iki büyük grup! Yakında sizin hesabınıza bakacağız!", transliteration: "Se nefrugu lekum eyyuhes sekalan."},
  {id: 4933, sure_no: 32, ayet_no: 32, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4934, sure_no: 33, ayet_no: 33, sure_adi: "Rahman", text: "يَا مَعْشَرَ الْجِنِّ وَالْاِنْسِ اِنِ اسْتَطَعْتُمْ اَنْ تَنْفُذُوا مِنْ اَقْطَارِ السَّمٰوَاتِ وَالْاَرْضِ فَانْفُذُواۜ  لَا تَنْفُذُونَ اِلَّا بِسُلْطَانٍۚ", translation: "Ey cin ve ins toplulukları! Eğer göklerin ve yerin ötesine geçmeye güç yettirebilirseniz, haydi geçin. Ancak aşma yetkisi verilmeden geçemezsiniz.", transliteration: "Ya ma'şerel cinni vel insi inisteta'tum en tenfuzu min aktaris semavati vel ardı fenfuz, la tenfuzune illa bi sultan."},
  {id: 4935, sure_no: 34, ayet_no: 34, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4936, sure_no: 35, ayet_no: 35, sure_adi: "Rahman", text: "يُرْسَلُ عَلَيْكُمَا شُوَاظٌ مِنْ نَارٍ وَنُحَاسٌ فَلَا تَنْتَصِرَانِۚ", translation: "İkinizin üzerine ateşten bir alev ve duman gönderilir ve siz yardımlaşamazsınız.", transliteration: "Yurselu aleykuma şuvazun min narin ve nuhasun fe la tentesıran."},
  {id: 4937, sure_no: 36, ayet_no: 36, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4938, sure_no: 37, ayet_no: 37, sure_adi: "Rahman", text: "فَاِذَا انْشَقَّتِ السَّمَٓاءُ فَكَانَتْ وَرْدَةً كَالدِّهَانِۚ", translation: "Gök parçalanıp kırmızı gül renginde yağ eriyiğine dönüştüğü zaman;", transliteration: "Fe izen şakkatis semau fe kanet verdeten keddihan."},
  {id: 4939, sure_no: 38, ayet_no: 38, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4940, sure_no: 39, ayet_no: 39, sure_adi: "Rahman", text: "فَيَوْمَئِذٍ لَا يُسْـَٔلُ عَنْ ذَنْبِه۪ٓ اِنْسٌ وَلَا جَٓانٌّۚ", translation: "İşte O Gün, ins ve cine suçları sorulmaz.", transliteration: "Fe yevme izin la yus'elu an zenbihi insun ve la cann."},
  {id: 4941, sure_no: 40, ayet_no: 40, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4942, sure_no: 41, ayet_no: 41, sure_adi: "Rahman", text: "يُعْرَفُ الْمُجْرِمُونَ بِس۪يمٰيهُمْ فَيُؤْخَذُ بِالنَّوَاص۪ي وَالْاَقْدَامِۚ", translation: "Suçlular simalarından tanınır. Alınlarından ve ayaklarından yakalanırlar.", transliteration: "Yu'reful mucrımune bi simahum fe yu'hazu bin nevasi vel akdam."},
  {id: 4943, sure_no: 42, ayet_no: 42, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4944, sure_no: 43, ayet_no: 43, sure_adi: "Rahman", text: "هٰذِه۪ جَهَنَّمُ الَّت۪ي يُكَذِّبُ بِهَا الْمُجْرِمُونَۢ", translation: "İşte bu, suçluların yalanladığı Cehennem'dir.", transliteration: "Hazihi cehennemulleti yukezzibu bi hel mucrimun."},
  {id: 4945, sure_no: 44, ayet_no: 44, sure_adi: "Rahman", text: "يَطُوفُونَ بَيْنَهَا وَبَيْنَ حَم۪يمٍ اٰنٍۚ", translation: "Onunla kaynar su arasında dolaşır dururlar.", transliteration: "Yetufune beyneha ve beyne hamimin an."},
  {id: 4946, sure_no: 45, ayet_no: 45, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ۟", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4947, sure_no: 46, ayet_no: 46, sure_adi: "Rahman", text: "وَلِمَنْ خَافَ مَقَامَ رَبِّه۪ جَنَّتَانِۚ", translation: "Rabb'inin makamından korkanlar için iki Cennet vardır.", transliteration: "Ve li men hafe makame rabbihi cennetan."},
  {id: 4948, sure_no: 47, ayet_no: 47, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۙ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4949, sure_no: 48, ayet_no: 48, sure_adi: "Rahman", text: "ذَوَاتَٓا اَفْنَانٍۚ", translation: "İkisinin de çeşit çeşit ağaçları vardır.", transliteration: "Zevata efnan."},
  {id: 4950, sure_no: 49, ayet_no: 49, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4951, sure_no: 50, ayet_no: 50, sure_adi: "Rahman", text: "ف۪يهِمَا عَيْنَانِ تَجْرِيَانِۚ", translation: "İkisinde de akıp giden iki pınar vardır.", transliteration: "Fihi ma aynani tecriyan."},
  {id: 4952, sure_no: 51, ayet_no: 51, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4953, sure_no: 52, ayet_no: 52, sure_adi: "Rahman", text: "ف۪يهِمَا مِنْ كُلِّ فَاكِهَةٍ زَوْجَانِۚ", translation: "İkisinde de her meyveden çift çift vardır.", transliteration: "Fihi ma min kulli fatihetin zevcan."},
  {id: 4954, sure_no: 53, ayet_no: 53, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4955, sure_no: 54, ayet_no: 54, sure_adi: "Rahman", text: "مُتَّكِـ۪ٔينَ عَلٰى فُرُشٍ بَطَٓائِنُهَا مِنْ اِسْتَبْرَقٍۜ وَجَنَا الْجَنَّتَيْنِ دَانٍۚ", translation: "Astarları kalın ipekten minderlere yaslanırlar. İki Cennet'in meyvesi alınabilecek yakınlıktadır.", transliteration: "Muttekiine ala furuşin betainuha min istebrak, ve cenel cenneteyni dan."},
  {id: 4956, sure_no: 55, ayet_no: 55, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4957, sure_no: 56, ayet_no: 56, sure_adi: "Rahman", text: "ف۪يهِنَّ قَاصِرَاتُ الطَّرْفِۙ لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَٓانٌّۚ", translation: "Onlarda daha önce ins ve cinnin dokunmadığı, bakışlarını başkasından çekmiş eşler vardır.", transliteration: "Fihinne kasiratut tarfi lem yatmishunne insun kablehum ve la cann."},
  {id: 4958, sure_no: 57, ayet_no: 57, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۚ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4959, sure_no: 58, ayet_no: 58, sure_adi: "Rahman", text: "كَاَنَّهُنَّ الْيَاقُوتُ وَالْمَرْجَانُۚ", translation: "Onlar, adeta yakut ve mercan gibidirler.", transliteration: "Ke enne hunnel yakutu vel mercan."},
  {id: 4960, sure_no: 59, ayet_no: 59, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4961, sure_no: 60, ayet_no: 60, sure_adi: "Rahman", text: "هَلْ جَزَٓاءُ الْاِحْسَانِ اِلَّا الْاِحْسَانُۚ", translation: "İyiliğin karşılığı, iyilikten başka olabilir mi?", transliteration: "Hel cezaul ihsani illel ihsan."},
  {id: 4962, sure_no: 61, ayet_no: 61, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4963, sure_no: 62, ayet_no: 62, sure_adi: "Rahman", text: "وَمِنْ دُونِهِمَا جَنَّتَانِۚ", translation: "Bu ikisinden başka iki cennet daha var.", transliteration: "Ve min dunihima cennetan."},
  {id: 4964, sure_no: 63, ayet_no: 63, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۙ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4965, sure_no: 64, ayet_no: 64, sure_adi: "Rahman", text: "مُدْهَٓامَّتَانِۚ", translation: "İkisi de yemyeşildir.", transliteration: "Mud hammetan."},
  {id: 4966, sure_no: 65, ayet_no: 65, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۚ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4967, sure_no: 66, ayet_no: 66, sure_adi: "Rahman", text: "ف۪يهِمَا عَيْنَانِ نَضَّاخَتَانِۚ", translation: "İkisinde de coşkun iki su kaynağı vardır.", transliteration: "Fihi ma aynani neddahatan."},
  {id: 4968, sure_no: 67, ayet_no: 67, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۚ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4969, sure_no: 68, ayet_no: 68, sure_adi: "Rahman", text: "ف۪يهِمَا فَاكِهَةٌ وَنَخْلٌ وَرُمَّانٌۚ", translation: "İkisinde de meyve, hurma ve nar vardır.", transliteration: "Fihi ma fakihetun ve nahlun ve rumman."},
  {id: 4970, sure_no: 69, ayet_no: 69, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۚ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4971, sure_no: 70, ayet_no: 70, sure_adi: "Rahman", text: "ف۪يهِنَّ خَيْرَاتٌ حِسَانٌۚ", translation: "Onlarda hayırlar ve iyilikler vardır.", transliteration: "Fihinne hayratun hisan."},
  {id: 4972, sure_no: 71, ayet_no: 71, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۚ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4973, sure_no: 72, ayet_no: 72, sure_adi: "Rahman", text: "حُورٌ مَقْصُورَاتٌ فِي الْخِيَامِۚ", translation: "Çadırlarda korunaklı huriler vardır.", transliteration: "Hurun maksuratun fil hiyam."},
  {id: 4974, sure_no: 73, ayet_no: 73, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۚ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4975, sure_no: 74, ayet_no: 74, sure_adi: "Rahman", text: "لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَٓانٌّۚ", translation: "Onlara daha önce ins ve cin1 dokunmadı.", transliteration: "Lem yatmishunne insun kablehum ve la cann."},
  {id: 4976, sure_no: 75, ayet_no: 75, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِۚ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4977, sure_no: 76, ayet_no: 76, sure_adi: "Rahman", text: "مُتَّكِـ۪ٔينَ عَلٰى رَفْرَفٍ خُضْرٍ وَعَبْقَرِيٍّ حِسَانٍۚ", translation: "En iyi döşeklerin üzerinde yeşil yastıklara yaslanırlar.", transliteration: "Muttekiine ala refrefin hudrin ve abkariyyin hisan."},
  {id: 4978, sure_no: 77, ayet_no: 77, sure_adi: "Rahman", text: "فَبِاَيِّ اٰلَٓاءِ رَبِّكُمَا تُكَذِّبَانِ", translation: "O halde siz ikiniz, Rabb'inizin hangi nimetlerini yalanlayabilirsiniz?", transliteration: "Fe bi eyyi alai rabbikuma tukezziban."},
  {id: 4979, sure_no: 78, ayet_no: 78, sure_adi: "Rahman", text: "تَبَارَكَ اسْمُ رَبِّكَ ذِي الْجَلَالِ وَالْاِكْرَامِ", translation: "Celal ve İkram Sahibi Olan Rabb'inin ismi ne yücedir.", transliteration: "Tebarekesmu rabbike zil celali vel ikram."},
  {id: 4980, sure_no: 1, ayet_no: 1, sure_adi: "Vakıa", text: "اِذَا وَقَعَتِ الْوَاقِعَةُۙ", translation: "Olacak olan o müthiş olay gerçekleştiği zaman.", transliteration: "İza ve kaatil vakıah."},
  {id: 4981, sure_no: 2, ayet_no: 2, sure_adi: "Vakıa", text: "لَيْسَ لِوَقْعَتِهَا كَاذِبَةٌۢ", translation: "Onun gerçekleşmesini yalanlayan kimse kalmayacak.", transliteration: "Leyse li vak'atiha kazibeh."},
  {id: 4982, sure_no: 3, ayet_no: 3, sure_adi: "Vakıa", text: "خَافِضَةٌ رَافِعَةٌۙ", translation: "Alçaltıcıdır, yükselticidir.", transliteration: "Hafidatun rafiah."},
  {id: 4983, sure_no: 4, ayet_no: 4, sure_adi: "Vakıa", text: "اِذَا رُجَّتِ الْاَرْضُ رَجاًّۙ", translation: "Yer sarsıldıkça sarsıldığı zaman.", transliteration: "İza ruccetil ardu recca."},
  {id: 4984, sure_no: 5, ayet_no: 5, sure_adi: "Vakıa", text: "وَبُسَّتِ الْجِبَالُ بَساًّۙ", translation: "Dağlar parça parça olup,", transliteration: "Ve bussetil cibalu bessa."},
  {id: 4985, sure_no: 6, ayet_no: 6, sure_adi: "Vakıa", text: "فَكَانَتْ هَبَٓاءً مُنْبَثاًّۙ", translation: "Toz duman haline gelince,", transliteration: "Fe kanet hebaen mun bessa."},
  {id: 4986, sure_no: 7, ayet_no: 7, sure_adi: "Vakıa", text: "وَكُنْتُمْ اَزْوَاجاً ثَلٰثَةًۜ", translation: "Siz o zaman üç sınıfa ayrılacaksınız.", transliteration: "Ve kuntum ezvacen selaseh."},
  {id: 4987, sure_no: 8, ayet_no: 8, sure_adi: "Vakıa", text: "فَاَصْحَابُ الْمَيْمَنَةِ مَٓا اَصْحَابُ الْمَيْمَنَةِۜ", translation: "İşte ashabı meymene! Nedir ashabı meymene?", transliteration: "Fe ashabul meymeneti ma ashabul meymeneti."},
  {id: 4988, sure_no: 9, ayet_no: 9, sure_adi: "Vakıa", text: "وَاَصْحَابُ الْمَشْـَٔمَةِ مَٓا اَصْحَابُ الْمَشْـَٔمَةِۜ", translation: "Ashabı meş'eme! Nedir ashabı meş'eme?", transliteration: "Ve ashabul meş'emeti ma ashabul meş'emeti."},
  {id: 4989, sure_no: 10, ayet_no: 10, sure_adi: "Vakıa", text: "وَالسَّابِقُونَ السَّابِقُونَۙ", translation: "Ve sabikunlar da sabikunlardır.", transliteration: "Ves sabikunes sabikun."},
  {id: 4990, sure_no: 11, ayet_no: 11, sure_adi: "Vakıa", text: "اُو۬لٰٓئِكَ الْمُقَرَّبُونَۚ", translation: "İşte onlar, yaklaştırılanlardır.", transliteration: "Ulaikel mukarrebun."},
  {id: 4991, sure_no: 12, ayet_no: 12, sure_adi: "Vakıa", text: "ف۪ي جَنَّاتِ النَّع۪يمِ", translation: "Naim Cennetlerindedirler.", transliteration: "Fi cennatin naim."},
  {id: 4992, sure_no: 13, ayet_no: 13, sure_adi: "Vakıa", text: "ثُلَّةٌ مِنَ الْاَوَّل۪ينَۙ", translation: "Birçoğu öncekilerden.", transliteration: "Sulletun minel evvelin."},
  {id: 4993, sure_no: 14, ayet_no: 14, sure_adi: "Vakıa", text: "وَقَل۪يلٌ مِنَ الْاٰخِر۪ينَۜ", translation: "Birazı da sonrakilerden.", transliteration: "Ve kalilun minel ahirin."},
  {id: 4994, sure_no: 15, ayet_no: 15, sure_adi: "Vakıa", text: "عَلٰى سُرُرٍ مَوْضُونَةٍۙ", translation: "İşlemeli tahtlar üzerinde.", transliteration: "Ala sururin mevdunetin."},
  {id: 4995, sure_no: 16, ayet_no: 16, sure_adi: "Vakıa", text: "مُتَّكِـ۪ٔينَ عَلَيْهَا مُتَقَابِل۪ينَ", translation: "Onların üzerinde karşılıklı yaslanırlar.", transliteration: "Muttekiine aleyha mutekabilin."},
  {id: 4996, sure_no: 17, ayet_no: 17, sure_adi: "Vakıa", text: "يَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَۙ", translation: "Yaşamları sürekli kılınmış gençler çevrelerinde dolaşırlar.", transliteration: "Yetufu aleyhim vildanun muhalledun."},
  {id: 4997, sure_no: 18, ayet_no: 18, sure_adi: "Vakıa", text: "بِاَكْوَابٍ وَاَبَار۪يقَ وَكَأْسٍ مِنْ مَع۪ينٍۙ", translation: "Kaynağından doldurulmuş testiler, ibrikler ve billur kadehler ile.", transliteration: "Bi ekvabin ve ebarika ve ke'sin min main."},
  {id: 4998, sure_no: 19, ayet_no: 19, sure_adi: "Vakıa", text: "لَا يُصَدَّعُونَ عَنْهَا وَلَا يُنْزِفُونَۙ", translation: "Ondan; başları ağrımaz ve sarhoş olmazlar.", transliteration: "La yusaddeune anha ve la yunzifun."},
  {id: 4999, sure_no: 20, ayet_no: 20, sure_adi: "Vakıa", text: "وَفَاكِهَةٍ مِمَّا يَتَخَيَّرُونَۙ", translation: "Ve beğendikleri meyveler,", transliteration: "Ve fakihetin mimma yetehayyerun."},
  {id: 5000, sure_no: 21, ayet_no: 21, sure_adi: "Vakıa", text: "وَلَحْمِ طَيْرٍ مِمَّا يَشْتَهُونَۜ", translation: "Ve canlarının çektiği kuş etleri,", transliteration: "Ve lahmi tayrin mimma yeştehun."},
  {id: 5001, sure_no: 22, ayet_no: 22, sure_adi: "Vakıa", text: "وَحُورٌ ع۪ينٌۙ", translation: "Güzel bakışlı huriler.", transliteration: "Ve hurun inun."},
  {id: 5002, sure_no: 23, ayet_no: 23, sure_adi: "Vakıa", text: "كَاَمْثَالِ اللُّؤْلُؤِ۬ الْمَكْنُونِۚ", translation: "Korunmuş inci gibi.", transliteration: "Ke emsalil lu'luil meknun."},
  {id: 5003, sure_no: 24, ayet_no: 24, sure_adi: "Vakıa", text: "جَزَٓاءً بِمَا كَانُوا يَعْمَلُونَ", translation: "Yaptıkları iyi şeylere karşılık olarak.", transliteration: "Cezaen bi ma kanu ya'melun."},
  {id: 5004, sure_no: 25, ayet_no: 25, sure_adi: "Vakıa", text: "لَا يَسْمَعُونَ ف۪يهَا لَغْواً وَلَا تَأْث۪يماًۙ", translation: "Orada boş, anlamsız ve günaha sokan şeyler duymazlar.", transliteration: "La yesmeune fiha lagven ve la te'sima."},
  {id: 5005, sure_no: 26, ayet_no: 26, sure_adi: "Vakıa", text: "اِلَّا ق۪يلاً سَلَاماً سَلَاماً", translation: "Söz olarak yalnızca; selam, selam sözü söylenir.", transliteration: "İlla kilen selamen selama."},
  {id: 5006, sure_no: 27, ayet_no: 27, sure_adi: "Vakıa", text: "وَاَصْحَابُ الْيَم۪ينِ مَٓا اَصْحَابُ الْيَم۪ينِۜ", translation: "Sağın adamları! Nedir sağın adamları?", transliteration: "Ve ashabul yemini ma ashabul yemin."},
  {id: 5007, sure_no: 28, ayet_no: 28, sure_adi: "Vakıa", text: "ف۪ي سِدْرٍ مَخْضُودٍۙ", translation: "Dikensiz sedir ağaçları içindedirler.", transliteration: "Fi sidrin mahdud."},
  {id: 5008, sure_no: 29, ayet_no: 29, sure_adi: "Vakıa", text: "وَطَلْحٍ مَنْضُودٍۙ", translation: "Salkım salkım muz ağaçları,", transliteration: "Ve talhın mendud."},
  {id: 5009, sure_no: 30, ayet_no: 30, sure_adi: "Vakıa", text: "وَظِلٍّ مَمْدُودٍۙ", translation: "Uzamış gölgeler,", transliteration: "Ve zıllin memdud."},
  {id: 5010, sure_no: 31, ayet_no: 31, sure_adi: "Vakıa", text: "وَمَٓاءٍ مَسْكُوبٍۙ", translation: "Ve çağlayan sular,", transliteration: "Ve main meskub."},
  {id: 5011, sure_no: 32, ayet_no: 32, sure_adi: "Vakıa", text: "وَفَاكِهَةٍ كَث۪يرَةٍۙ", translation: "Her türden meyveler,", transliteration: "Ve fakihetin kesirah"},
  {id: 5012, sure_no: 33, ayet_no: 33, sure_adi: "Vakıa", text: "لَا مَقْطُوعَةٍ وَلَا مَمْنُوعَةٍۙ", translation: "Tükenmeyen ve yasaklanmayan,", transliteration: "La maktuatin ve la memnuah."},
  {id: 5013, sure_no: 34, ayet_no: 34, sure_adi: "Vakıa", text: "وَفُرُشٍ مَرْفُوعَةٍۜ", translation: "Kabartılmış döşeklerdedirler.", transliteration: "Ve furuşin merfuah."},
  {id: 5014, sure_no: 35, ayet_no: 35, sure_adi: "Vakıa", text: "اِنَّٓا اَنْشَأْنَاهُنَّ اِنْشَٓاءًۙ", translation: "Biz, onları yeni bir düzenleme ile düzenledik.", transliteration: "İnna enşe'na hunne inşaa."},
  {id: 5015, sure_no: 36, ayet_no: 36, sure_adi: "Vakıa", text: "فَجَعَلْنَاهُنَّ اَبْكَاراًۙ", translation: "Onları dokunulmamışlar yaptık.", transliteration: "Fe cealna hunne ebkaran."},
  {id: 5016, sure_no: 37, ayet_no: 37, sure_adi: "Vakıa", text: "عُـرُباً اَتْـرَاباًۙ", translation: "Sahibini yadırgamayan.", transliteration: "Uruben etraba."},
  {id: 5017, sure_no: 38, ayet_no: 38, sure_adi: "Vakıa", text: "لِاَصْحَـابِ الْيَم۪ينِۜ ۟", translation: "Sağın adamları için.", transliteration: "Li ashabil yemin."},
  {id: 5018, sure_no: 39, ayet_no: 39, sure_adi: "Vakıa", text: "ثُلَّةٌ مِنَ الْاَوَّل۪ينَۙ", translation: "Bir kısmı öncekilerdendir.", transliteration: "Sulletun minel evvelin."},
  {id: 5019, sure_no: 40, ayet_no: 40, sure_adi: "Vakıa", text: "وَثُلَّةٌ مِنَ الْاٰخِر۪ينَۜ", translation: "Bir kısmı sonrakilerdendir.", transliteration: "Ve sulletun minel ahırin."},
  {id: 5020, sure_no: 41, ayet_no: 41, sure_adi: "Vakıa", text: "وَاَصْحَابُ الشِّمَالِۙ مَٓا اَصْحَابُ الشِّمَالِۜ", translation: "Ve solun adamları! Nedir solun adamları?", transliteration: "Ve ashabuş şimali ma ashabuş şimal."},
  {id: 5021, sure_no: 42, ayet_no: 42, sure_adi: "Vakıa", text: "ف۪ي سَمُومٍ وَحَم۪يمٍۙ", translation: "Kavurucu bir azap ve kaynar su içindedirler.", transliteration: "Fi semumin ve hamim."},
  {id: 5022, sure_no: 43, ayet_no: 43, sure_adi: "Vakıa", text: "وَظِلٍّ مِنْ يَحْمُومٍۙ", translation: "Ve kara bir dumanın gölgesinde,", transliteration: "Ve zıllin min yahmum."},
  {id: 5023, sure_no: 44, ayet_no: 44, sure_adi: "Vakıa", text: "لَا بَارِدٍ وَلَا كَر۪يمٍ", translation: "Serin olmayan, faydası olamayan!", transliteration: "La baridin ve la kerim."},
  {id: 5024, sure_no: 45, ayet_no: 45, sure_adi: "Vakıa", text: "اِنَّهُمْ كَانُوا قَبْلَ ذٰلِكَ مُتْرَف۪ينَۚ", translation: "Çünkü onlar bundan önce varlık içinde zevklerine dalmışlardı.", transliteration: "İnnehum kanu kable zalike mutrefin."},
  {id: 5025, sure_no: 46, ayet_no: 46, sure_adi: "Vakıa", text: "وَكَانُوا يُصِرُّونَ عَلَى الْحِنْثِ الْعَظ۪يمِۚ", translation: "Büyük ihanette ısrar ediyorlardı.", transliteration: "Ve kanu yusirrune alel hınsil azim."},
  {id: 5026, sure_no: 47, ayet_no: 47, sure_adi: "Vakıa", text: "وَكَانُوا يَقُولُونَ اَئِذَا مِتْنَا وَكُنَّا تُرَاباً وَعِظَاماً ءَاِنَّا لَمَبْعُوثُونَۙ", translation: "Ve \"Biz ölüp, toprak ve kemik yığını olduktan sonra mı yeniden diriltileceğiz?\" diyorlardı.", transliteration: "Ve kanu yekulune e iza mitna ve kunna turaben ve iza men e inna le meb'usun."},
  {id: 5027, sure_no: 48, ayet_no: 48, sure_adi: "Vakıa", text: "اَوَاٰبَٓاؤُ۬نَا الْاَوَّلُونَ", translation: "\"Bizden önce ölmüş olan atalarımız da mı?\"", transliteration: "E ve abaunel evvelun."},
  {id: 5028, sure_no: 49, ayet_no: 49, sure_adi: "Vakıa", text: "قُلْ اِنَّ الْاَوَّل۪ينَ وَالْاٰخِر۪ينَۙ", translation: "De ki: \"Öncekiler de sonrakiler de diriltilecekler.\"", transliteration: "Kul innel evveline vel ahirin."},
  {id: 5029, sure_no: 50, ayet_no: 50, sure_adi: "Vakıa", text: "لَمَجْمُوعُونَ اِلٰى م۪يقَاتِ يَوْمٍ مَعْلُومٍ", translation: "Bilinen günün belli olan zamanında kesinlikle toplanmış olacaklar.", transliteration: "Le mecmuune ila mikati yevmin ma'lum."},
  {id: 5030, sure_no: 51, ayet_no: 51, sure_adi: "Vakıa", text: "ثُمَّ اِنَّكُمْ اَيُّهَا الضَّٓالُّونَ الْمُكَذِّبُونَۙ", translation: "Sonra siz, ey gerçekten sapkınlıkta olan yalancılar!", transliteration: "Summe innekum eyyuhed dallunel mukezzibun."},
  {id: 5031, sure_no: 52, ayet_no: 52, sure_adi: "Vakıa", text: "لَاٰكِلُونَ مِنْ شَجَرٍ مِنْ زَقُّومٍۙ", translation: "Kesinlikle zakkum ağacından yiyecek olanlarsınız!", transliteration: "Le akilune min şecerin min zakkumin."},
  {id: 5032, sure_no: 53, ayet_no: 53, sure_adi: "Vakıa", text: "فَمَالِـؤُ۫نَ مِنْهَا الْبُطُونَۚ", translation: "Karınlarınızı onunla dolduracaksınız!", transliteration: "Fe ma liune minhel butun."},
  {id: 5033, sure_no: 54, ayet_no: 54, sure_adi: "Vakıa", text: "فَشَارِبُونَ عَلَيْهِ مِنَ الْحَم۪يمِۚ", translation: "Sonra da onun üzerine kaynar sudan içeceksiniz.", transliteration: "Fe şaribune aleyhi minel hamim."},
  {id: 5034, sure_no: 55, ayet_no: 55, sure_adi: "Vakıa", text: "فَشَارِبُونَ شُرْبَ الْه۪يمِۜ", translation: "Hem de susuz kalmış develerin içişi gibi içeceksiniz.", transliteration: "Fe şaribune şurbel him."},
  {id: 5035, sure_no: 56, ayet_no: 56, sure_adi: "Vakıa", text: "هٰذَا نُزُلُهُمْ يَوْمَ الدّ۪ينِۜ", translation: "İşte bu, Din Günü'nde onların ziyafetleridir.", transliteration: "Haza nuzuluhum yevmed din."},
  {id: 5036, sure_no: 57, ayet_no: 57, sure_adi: "Vakıa", text: "نَحْنُ خَلَقْنَاكُمْ فَلَوْلَا تُصَدِّقُونَ۟", translation: "Sizi Biz yarattık. Bu gerçeği hala kabul etmeyecek misiniz?", transliteration: "Nahnu halaknakum fe lev la tusaddikun."},
  {id: 5037, sure_no: 58, ayet_no: 58, sure_adi: "Vakıa", text: "اَفَرَاَيْتُمْ مَا تُمْنُونَۜ", translation: "Öyleyse akıttığınız şeyin ne olduğunu hiç düşündünüz mü?", transliteration: "E fe reeytum ma tumnun."},
  {id: 5038, sure_no: 59, ayet_no: 59, sure_adi: "Vakıa", text: "ءَاَنْتُمْ تَخْلُقُونَهُٓ اَمْ نَحْنُ الْخَالِقُونَ", translation: "Onu siz mi yaratıyorsunuz, yoksa yaratan Biz miyiz?", transliteration: "E entum tahlukunehu em nahnul halikun."},
  {id: 5039, sure_no: 60, ayet_no: 60, sure_adi: "Vakıa", text: "نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوق۪ينَۙ", translation: "Aranızda ölümü Biz takdir ettik. Kimse Bizi engelleyemez.", transliteration: "Nahnu kadderna beynekumul mevte ve ma nahnu bi mes- bukin."},
  {id: 5040, sure_no: 61, ayet_no: 61, sure_adi: "Vakıa", text: "عَلٰٓى اَنْ نُبَدِّلَ اَمْثَالَكُمْ وَنُنْشِئَكُمْ ف۪ي مَا لَا تَعْلَمُونَ", translation: "Yapınızı değiştirip, sizi bilmediğiniz bir şekilde, yeniden yapma gücünden yoksun değiliz.", transliteration: "Ala en nubeddile emsalekum ve nunşiekum fi ma la ta'lemun."},
  {id: 5041, sure_no: 62, ayet_no: 62, sure_adi: "Vakıa", text: "وَلَقَدْ عَلِمْتُمُ النَّشْاَةَ الْاُو۫لٰى فَلَوْلَا تَذَكَّرُونَ", translation: "Ant olsun ki ilk meydana getirmeyi bildiniz. Düşünüp öğüt almanız gerekmez mi?", transliteration: "Ve lekad alimtumunneş etel ula fe lev la tezekkerun."},
  {id: 5042, sure_no: 63, ayet_no: 63, sure_adi: "Vakıa", text: "اَفَرَاَيْتُمْ مَا تَحْرُثُونَۜ", translation: "Ektiklerinizi görmüyor musunuz?", transliteration: "E fe reeytum ma tahrusun."},
  {id: 5043, sure_no: 64, ayet_no: 64, sure_adi: "Vakıa", text: "ءَاَنْتُمْ تَزْرَعُونَهُٓ  اَمْ نَحْنُ الزَّارِعُونَ", translation: "Onu siz mi yetiştiriyorsunuz, yoksa Biz mi?", transliteration: "E entum tezre unehu em nahnuz zariun."},
  {id: 5044, sure_no: 65, ayet_no: 65, sure_adi: "Vakıa", text: "لَوْ نَشَٓاءُ لَجَعَلْنَاهُ حُطَاماً فَظَلْتُمْ تَفَكَّهُونَ", translation: "Dileseydik kesinlikle onu çer-çöp yapardık da siz şaşırıp kalırdınız.", transliteration: "Lev neşau le cealnahu hutamen fe zaltum tefekkehun."},
  {id: 5045, sure_no: 66, ayet_no: 66, sure_adi: "Vakıa", text: "اِنَّا لَمُغْرَمُونَۙ", translation: "\"Gerçekten borçlandık;\"", transliteration: "İnna le mugremun."},
  {id: 5046, sure_no: 67, ayet_no: 67, sure_adi: "Vakıa", text: "بَلْ نَحْنُ مَحْرُومُونَ", translation: "\"Doğrusu, yoksun bırakıldık.\"", transliteration: "Bel nahnu mahrumun."},
  {id: 5047, sure_no: 68, ayet_no: 68, sure_adi: "Vakıa", text: "اَفَرَاَيْتُمُ الْمَٓاءَ الَّذ۪ي تَشْرَبُونَۜ", translation: "Peki, içtiğiniz suya hiç baktınız mı?", transliteration: "E fe reeytumul maellezi teşrebun."},
  {id: 5048, sure_no: 69, ayet_no: 69, sure_adi: "Vakıa", text: "ءَاَنْتُمْ اَنْزَلْتُمُوهُ مِنَ الْمُزْنِ اَمْ نَحْنُ الْمُنْزِلُونَ", translation: "Onu bulutlardan indiren siz misiniz, yoksa indiren Biz miyiz?", transliteration: "E entum enzeltumuhu minel muzni em nahnul munzilun."},
  {id: 5049, sure_no: 70, ayet_no: 70, sure_adi: "Vakıa", text: "لَوْ نَشَٓاءُ جَعَلْنَاهُ اُجَاجاً فَلَوْلَا تَشْكُرُونَ", translation: "Eğer dileseydik onu tuzlu yapardık. Buna şükretmeniz gerekmez mi?", transliteration: "Lev neşau cealnahu ucacen fe levla teşkurun."},
  {id: 5050, sure_no: 71, ayet_no: 71, sure_adi: "Vakıa", text: "اَفَرَاَيْتُمُ النَّارَ الَّت۪ي تُورُونَۜ", translation: "Yaktığınız o ateşe baktınız mı hiç?", transliteration: "E fe reeytumun narelleti turun."},
  {id: 5051, sure_no: 72, ayet_no: 72, sure_adi: "Vakıa", text: "ءَاَنْتُمْ اَنْشَأْتُمْ شَجَرَتَـهَٓا اَمْ نَحْنُ الْمُنْشِؤُ۫نَ", translation: "Onun ağacını siz mi yetiştirdiniz, yoksa yetiştiren Biz miyiz?", transliteration: "E entum enşe'tum şecereteha em nahnul munşiun."},
  {id: 5052, sure_no: 73, ayet_no: 73, sure_adi: "Vakıa", text: "نَحْنُ جَعَلْنَاهَا تَذْكِرَةً وَمَتَاعاً لِلْمُقْو۪ينَۚ", translation: "Biz, onu bir hatırlatma ve ihtiyaç sahipleri için bir yararlanma kaynağı yaptık.", transliteration: "Nahnu cealnaha tezkireten ve metaan lil mukvin."},
  {id: 5053, sure_no: 74, ayet_no: 74, sure_adi: "Vakıa", text: "فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظ۪يمِ۟", translation: "O halde Azim Rabb'inin adını tesbih et.", transliteration: "Fe sebbih bismi rabbikel azim."},
  {id: 5054, sure_no: 75, ayet_no: 75, sure_adi: "Vakıa", text: "فَلَٓا اُقْسِمُ بِمَوَاقِـعِ النُّجُومِۙ", translation: "Hayır, yıldızların yerleri üzerine yemin ederim;", transliteration: "Fe la uksimu bi mevakiin nucum."},
  {id: 5055, sure_no: 76, ayet_no: 76, sure_adi: "Vakıa", text: "وَاِنَّهُ لَقَسَمٌ لَوْ تَعْلَمُونَ عَظ۪يمٌۙ", translation: "Eğer bilirseniz bu yüce bir yemindir.", transliteration: "Ve innehu le kasemun lev ta'lemune azim."},
  {id: 5056, sure_no: 77, ayet_no: 77, sure_adi: "Vakıa", text: "اِنَّهُ لَقُرْاٰنٌ كَر۪يمٌۙ", translation: "Kuşkusuz o, kesinlikle Kerim bir Kur'an'dır.", transliteration: "İnnehu le kur'anun kerim."},
  {id: 5057, sure_no: 78, ayet_no: 78, sure_adi: "Vakıa", text: "ف۪ي كِتَابٍ مَكْنُونٍۙ", translation: "Korunmuş bir Kitap'tadır.", transliteration: "Fi kitabin meknun."},
  {id: 5058, sure_no: 79, ayet_no: 79, sure_adi: "Vakıa", text: "لَا يَمَسُّهُٓ اِلَّا الْمُطَهَّرُونَۜ", translation: "Ona arındırılmış olanlardan başkası dokunamaz.", transliteration: "La yemessuhu illel mutahherun."},
  {id: 5059, sure_no: 80, ayet_no: 80, sure_adi: "Vakıa", text: "تَنْز۪يلٌ مِنْ رَبِّ الْعَالَم۪ينَ", translation: "O, Alemlerin Rabb'inden indirilmedir.", transliteration: "Tenzilun min rabbil alemin."},
  {id: 5060, sure_no: 81, ayet_no: 81, sure_adi: "Vakıa", text: "اَفَبِهٰذَا الْحَد۪يثِ اَنْتُمْ مُدْهِنُونَۙ", translation: "Böyle bir hadisi mi hafife alıyorsunuz?", transliteration: "E fe bi hazel hadisi entum mudhinun."},
  {id: 5061, sure_no: 82, ayet_no: 82, sure_adi: "Vakıa", text: "وَتَجْعَلُونَ رِزْقَـكُمْ اَنَّكُمْ تُكَذِّبُونَ", translation: "Verilen rızıklara yalanlayarak mı karşılık veriyorsunuz?", transliteration: "Ve tec'alune rızkakum ennekum tukezzibun."},
  {id: 5062, sure_no: 83, ayet_no: 83, sure_adi: "Vakıa", text: "فَلَوْلَٓا اِذَا بَلَغَتِ الْحُلْقُومَۙ", translation: "Can boğaza dayandığı zaman;", transliteration: "Fe lev la iza belegatil hulkume."},
  {id: 5063, sure_no: 84, ayet_no: 84, sure_adi: "Vakıa", text: "وَاَنْتُمْ ح۪ينَئِذٍ تَنْظُرُونَۙ", translation: "O zaman bakınıp durursunuz.", transliteration: "Ve entum hine izin tenzurun."},
  {id: 5064, sure_no: 85, ayet_no: 85, sure_adi: "Vakıa", text: "وَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْكُمْ وَلٰـكِنْ لَا تُبْصِرُونَ", translation: "Biz ona sizden daha yakınız fakat siz göremezsiniz.", transliteration: "Ve nahnu akrabu ileyhi minkum ve lakin la tubsirun"},
  {id: 5065, sure_no: 86, ayet_no: 86, sure_adi: "Vakıa", text: "فَلَوْلَٓا اِنْ كُنْتُمْ غَيْرَ مَد۪ين۪ينَۙ", translation: "Mademki hesap sorulacak kimseler değilsiniz,", transliteration: "Fe lev la in kuntum gayre medinin."},
  {id: 5066, sure_no: 87, ayet_no: 87, sure_adi: "Vakıa", text: "تَرْجِعُونَـهَٓا اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "Eğer doğrulardansanız, onu geri çevirmeniz gerekmez mi?", transliteration: "Terciuneha in kuntum sadikin."},
  {id: 5067, sure_no: 88, ayet_no: 88, sure_adi: "Vakıa", text: "فَاَمَّٓا اِنْ كَانَ مِنَ الْمُقَرَّب۪ينَۙ", translation: "Fakat eğer o, yaklaştırılanlardan ise,", transliteration: "Fe emma in kane minel mukarrebine."},
  {id: 5068, sure_no: 89, ayet_no: 89, sure_adi: "Vakıa", text: "فَرَوْحٌ وَرَيْحَانٌ وَجَنَّتُ نَع۪يمٍ", translation: "O takdirde, rahat bir hayat, huzur, güzel kokulu rızık ve nimetlerle dolu bir Cennet vardır.", transliteration: "Fe revhun ve reyhanun ve cennetu naim."},
  {id: 5069, sure_no: 90, ayet_no: 90, sure_adi: "Vakıa", text: "وَاَمَّٓا اِنْ كَانَ مِنْ اَصْحَابِ الْيَم۪ينِۙ", translation: "Ve eğer o, sağın adamlarından1 ise,", transliteration: "Ve emma in kane min ashabil yemin."},
  {id: 5070, sure_no: 91, ayet_no: 91, sure_adi: "Vakıa", text: "فَسَلَامٌ لَكَ مِنْ اَصْحَابِ الْيَم۪ينِ", translation: "O zaman sağın adamlarından, \"Sana selam olsun.\"", transliteration: "Fe selamun leke min ashabil yemin."},
  {id: 5071, sure_no: 92, ayet_no: 92, sure_adi: "Vakıa", text: "وَاَمَّٓا اِنْ كَانَ مِنَ الْمُكَذِّب۪ينَ الضَّٓالّ۪ينَۙ", translation: "Ama sapkınlıkta olan yalanlayıcılardan ise,", transliteration: "Ve emma in kane minel mukezzibined dallin."},
  {id: 5072, sure_no: 93, ayet_no: 93, sure_adi: "Vakıa", text: "فَنُزُلٌ مِنْ حَم۪يمٍۙ", translation: "O zaman kaynar sudan bir ziyafet vardır!", transliteration: "Fe nuzulun min hamim."},
  {id: 5073, sure_no: 94, ayet_no: 94, sure_adi: "Vakıa", text: "وَتَصْلِيَةُ جَح۪يمٍۙ", translation: "Ve Cehennem'e atılma vardır.", transliteration: "Ve tasliyetu cahim."},
  {id: 5074, sure_no: 95, ayet_no: 95, sure_adi: "Vakıa", text: "اِنَّ هٰذَا لَهُوَ حَقُّ الْيَق۪ينِۚ", translation: "Kuşkusuz bu bildirdiklerimiz kesin olarak gerçektir.", transliteration: "İnne haza le huve hakkul yakin."},
  {id: 5075, sure_no: 96, ayet_no: 96, sure_adi: "Vakıa", text: "فَسَبِّـحْ بِاسْمِ رَبِّكَ الْعَظ۪يمِ", translation: "O halde Azim Rabb'inin adını tesbih et.", transliteration: "Fe sebbih bismi rabbikel azim."},
  {id: 5076, sure_no: 1, ayet_no: 1, sure_adi: "Hadid", text: "سَبَّحَ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۚ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Göklerde ve yerde olanlar, Allah'ı tesbih etmektedir. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Sebbeha lillahi ma fis semavati vel ard, ve huvel azizul hakim."},
  {id: 5077, sure_no: 2, ayet_no: 2, sure_adi: "Hadid", text: "لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۚ يُحْـي۪ وَيُم۪يتُۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Göklerin ve yeryüzünün mülkü yalnızca O'nundur. O, diriltir ve öldürür. O, Her Şeye Güç Yetiren'dir.", transliteration: "Lehu mulkus semavati vel ard, yuhyi ve yumit, ve huve ala kulli şey'in kadir."},
  {id: 5078, sure_no: 3, ayet_no: 3, sure_adi: "Hadid", text: "هُوَ الْاَوَّلُ وَالْاٰخِرُ وَالظَّاهِرُ وَالْبَاطِنُۚ وَهُوَ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "O, Evvel'dir ve Ahir'dir; Zahir'dir, Batın'dır. Ve O, Her Şeyi En İyi Bilen'dir.", transliteration: "Huvel evvelu vel ahiru vez zahiru vel batın, ve huve bi kulli şey'in alim."},
  {id: 5079, sure_no: 4, ayet_no: 4, sure_adi: "Hadid", text: "هُوَ الَّذ۪ي خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ ف۪ي سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۜ يَعْلَمُ مَا يَلِجُ فِي الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَٓاءِ وَمَا يَعْرُجُ ف۪يهَاۜ وَهُوَ مَعَكُمْ اَيْنَ مَا كُنْتُمْۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "O, gökleri ve yeri altı günde yarattı. Sonra arşa isteva etti. O, yere gireni ve ondan çıkanı, gökten ineni ve ona yükseleni bilir. Ve nerede olursanız olun, O, sizinle beraberdir. Allah, Yaptıklarınızı En İyi Gören'dir.", transliteration: "Huvellezi halakas semavati vel ardafisitteti eyyamin summesteva alel arş, a'lemu ma yelicu fil ardı ve ma yahrucu minha ve ma yenzilu mines semai ve ma ya'rucu fiha, ve huve meakum eyne ma kuntum, vallahu bi ma ta'melune basir."},
  {id: 5080, sure_no: 5, ayet_no: 5, sure_adi: "Hadid", text: "لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُورُ", translation: "Göklerin ve yeryüzünün mülkü O'nundur. Bütün işler yalnızca Allah'a döndürülür.", transliteration: "Lehu mulkus semavati vel ard, ve ilallahi turceul umur."},
  {id: 5081, sure_no: 6, ayet_no: 6, sure_adi: "Hadid", text: "يُولِجُ الَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الَّيْلِۜ وَهُوَ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Geceyi gündüze katar, gündüzü geceye katar. O, göğüslerin özünü en iyi bilendir.", transliteration: "Yulicul leyle fin nehari ve yulicun nehare fil leyl ve huve alimun bi zatis sudur."},
  {id: 5082, sure_no: 7, ayet_no: 7, sure_adi: "Hadid", text: "اٰمِنُوا بِاللّٰهِ وَرَسُولِه۪ وَاَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَف۪ينَ ف۪يهِۜ فَالَّذ۪ينَ اٰمَنُوا مِنْكُمْ وَاَنْفَقُوا لَهُمْ اَجْرٌ كَب۪يرٌ", translation: "Allah'a ve Resul'üne iman edin. Sizi sahip kıldığı şeylerden, ihtiyaç sahiplerine yardım edin. Bilin ki sizden iman edip, ihtiyaç sahiplerine yardım edenler için büyük ödül vardır.", transliteration: "Aminu billahi ve resulihi ve enfiku mimma cealekum mustahlefine fih, fellezine amenu minkum ve enfeku lehum ecrun kebir."},
  {id: 5083, sure_no: 8, ayet_no: 8, sure_adi: "Hadid", text: "وَمَا لَكُمْ لَا تُؤْمِنُونَ بِاللّٰهِۚ وَالرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا بِرَبِّكُمْ وَقَدْ اَخَذَ م۪يثَاقَكُمْ اِنْ كُنْتُمْ مُؤْمِن۪ينَ", translation: "Eğer gerçekten Mü'minseniz, size ne oluyor da Resul sizi Rabb'inize güvenmeye çağırdığı halde, Allah'a güvenmiyorsunuz? Oysa sizden kesin söz almıştı.", transliteration: "Ve ma lekum la tu'minune billah, ver resulu yed'ukum li tu'minu bi rabbikum ve kad e haze misakakum in kuntum mu'minin."},
  {id: 5084, sure_no: 9, ayet_no: 9, sure_adi: "Hadid", text: "هُوَ الَّذ۪ي يُنَزِّلُ عَلٰى عَبْدِه۪ٓ  اٰيَاتٍ بَيِّنَاتٍ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ اِلَى النُّورِۜ وَاِنَّ اللّٰهَ بِكُمْ لَرَؤُ۫فٌ رَح۪يمٌ", translation: "Sizi karanlıklardan aydınlığa çıkarmak için kuluna kanıt içeren açıklayıcı ayetleri indiren O'dur. Kuşkusuz Allah, Çok Şefkatli'dir, Rahmeti Kesintisiz'dir.", transliteration: "Huvellezi yunezzilu ala abdihi ayatin beyyinatin li yuhricekum minez zulumati ilen nur, ve innellahe bikum le raufun rahim."},
  {id: 5085, sure_no: 10, ayet_no: 10, sure_adi: "Hadid", text: "وَمَا لَكُمْ اَلَّا تُنْفِقُوا ف۪ي سَب۪يلِ اللّٰهِ وَلِلّٰهِ م۪يرَاثُ السَّمٰوَاتِ وَالْاَرْضِۜ لَا يَسْتَو۪ي مِنْكُمْ مَنْ اَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَۜ  اُو۬لٰٓئِكَ اَعْظَمُ دَرَجَةً مِنَ الَّذ۪ينَ اَنْفَقُوا مِنْ بَعْدُ وَقَاتَلُواۜ وَكُلاًّ وَعَدَ اللّٰهُ الْحُسْنٰىۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ۟", translation: "Size ne oluyor ki, göklerin ve yerin son sahipliği Allah'ın olduğu halde, Allah'ın yolunda harcamıyorsunuz? Sizden, fetihten önce Allah yolunda harcayan ve savaşanla, fetihten sonra harcayan ve savaşan bir değildir. Onlar daha üstün derece sahibidirler. Allah, hepsine \"en iyiyi\" söz verdi. Allah, Yaptıklarınızdan En İyi Haberdar Olandır.", transliteration: "Ve ma lekum ella tunfiku fi sebilillahi, ve lillahi mirasus semavati vel ard, la yestevi minkum men enfeka min kablil fethi ve katel, ulaike a'zamu dereceten minellezine enfeku min ba'du ve katelu ve kullen ve adallahul husna, vallahu bi ma ta'melune habir."},
  {id: 5086, sure_no: 11, ayet_no: 11, sure_adi: "Hadid", text: "مَنْ ذَا الَّذ۪ي يُقْرِضُ اللّٰهَ قَرْضاً حَسَناً فَيُضَاعِفَهُ لَهُ وَلَهُٓ  اَجْرٌ كَر۪يمٌۚ", translation: "Kim ki Allah'a iyi bir ödünç verirse, Allah, ona karşılığını katlayarak verir. Ve onun için şerefli bir karşılık vardır.", transliteration: "Men zellezi yukridullahe kardan hasenen fe yudaifehu lehu ve lehu ecrun kerim."},
  {id: 5087, sure_no: 12, ayet_no: 12, sure_adi: "Hadid", text: "يَوْمَ تَرَى الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ يَسْعٰى نُورُهُمْ بَيْنَ اَيْد۪يهِمْ وَبِاَيْمَانِهِمْ بُشْرٰيكُمُ الْيَوْمَ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ ذٰلِكَ هُوَ الْفَوْزُ الْعَظ۪يمُۚ", translation: "O Gün, Mü'min erkekleri ve Mü'min kadınları, nurları önlerinde ve sağlarında olduğu halde koşarlarken göreceksin. \"Bugün müjdeniz; içinde sürekli kalacağınız, içinden ırmaklar akan Cennetlerdir. İşte bu büyük kurtuluştur.\"", transliteration: "Yevme terel mu'minine vel mu'minati yes'a nuruhum beyne eydihim ve bi eymanihim buşrakumul yevme cennatun tecri min tahtihel enharu halidine fih, zalike huvel fevzul azim."},
  {id: 5088, sure_no: 13, ayet_no: 13, sure_adi: "Hadid", text: "يَوْمَ يَقُولُ الْمُنَافِقُونَ وَالْمُنَافِقَاتُ لِلَّذ۪ينَ اٰمَنُوا انْظُرُونَا نَقْتَبِسْ مِنْ نُورِكُمْ ق۪يلَ ارْجِعُوا وَرَٓاءَكُمْ فَالْتَمِسُوا نُوراًۜ فَضُرِبَ بَيْنَهُمْ بِسُورٍ لَهُ بَابٌۜ بَاطِنُهُ ف۪يهِ الرَّحْمَةُ وَظَاهِرُهُ مِنْ قِبَلِهِ الْعَذَابُۜ", translation: "O gün münafık erkekler ve münafık kadınlar, iman eden kimselere: \"Bize bakın da nurunuzdan biraz yararlanalım.\" derler. Onlara: \"Arkanıza dönün de oradan nur arayın.\" denir. O anda aralarına kapısı olan bir duvar çekilir; duvarın iç tarafı rahmet, dış tarafında ise azap vardır.", transliteration: "Yevme yekulul munafikune vel munafikatu lillezine amenunzuruna naktebis min nurikum, kilerci'u veraekum fel temisu nura, fe duribe beynehum bi surin lehu bab, batınuhu fihir rahmetu ve zahiruhu min kıbelihil azab."},
  {id: 5089, sure_no: 14, ayet_no: 14, sure_adi: "Hadid", text: "يُنَادُونَهُمْ اَلَمْ نَكُنْ مَعَكُمْۜ قَالُوا بَلٰى وَلٰكِنَّكُمْ فَـتَنْتُمْ اَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْاَمَانِيُّ حَتّٰى جَٓاءَ اَمْرُ اللّٰهِ وَغَرَّكُمْ بِاللّٰهِ الْغَرُورُ", translation: "İman Edenlere, \"Biz sizinle beraber değil miydik?\" diye seslenirler. İman Edenler, \"Evet, ama siz kendinizi fitneye düşürdünüz, bekleyip, kuşku duydunuz. Allah'ın emri gelinceye kadar tutku ve kuruntularınız sizi aldattı. Ve o ğarur da sizi Allah ile aldattı.\" dediler.", transliteration: "Yunadunehum e lem nekun meakum, kalu bela ve lakinnekum fe tentum enfusekum ve terebbastum vertebtum ve garret kumul emaniyyu hatta cae emrullahi ve garrekum billahil garur."},
  {id: 5090, sure_no: 15, ayet_no: 15, sure_adi: "Hadid", text: "فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَلَا مِنَ الَّذ۪ينَ كَفَرُواۜ مَأْوٰيكُمُ النَّارُۜ هِيَ مَوْلٰيكُمْۜ وَبِئْسَ الْمَص۪يرُ", translation: "Bugün artık sizden kurtuluş fidyesi kabul edilmez ve Kafirlerden de. Sizin varacağınız yer ateştir. Sizin mevlanız odur. Ne kötü varış yeridir o.", transliteration: "Fel yevme la yu'hazu minkum fid yetun ve la minellezine keferu, me'vakumun nar, hiye mevlakum, ve bi'sel masir."},
  {id: 5091, sure_no: 16, ayet_no: 16, sure_adi: "Hadid", text: "اَلَمْ يَأْنِ لِلَّذ۪ينَ اٰمَنُٓوا اَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللّٰهِ وَمَا نَزَلَ مِنَ الْحَقِّۙ وَلَا يَكُونُوا كَالَّذ۪ينَ اُو۫تُوا الْكِتَابَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْاَمَدُ فَقَسَتْ قُلُوبُهُمْۜ وَكَث۪يرٌ مِنْهُمْ فَاسِقُونَ", translation: "İman Edenler'in; Allah'ın öğütlerini dinleyip de Hakk'tan gelen şeyle kalplerinin yumuşaması zamanı gelmedi mi? Daha önce kendilerine Kitap verilip, üzerinden uzun zaman geçince kalpleri katılaşanlar gibi olmasınlar. Onların çoğu fasık kimselerdi.", transliteration: "E lem ye'ni lillezine amenu en tahşea kulubuhum li zikrillahi ve ma nezele minel hakkı ve la yekunu kellezine utul kitabe min kablu fe tale aleyhimul emedu fe kaset kulubuhum, ve kesirun minhum fasikun."},
  {id: 5092, sure_no: 17, ayet_no: 17, sure_adi: "Hadid", text: "اِعْلَمُٓوا اَنَّ اللّٰهَ يُحْـيِ الْاَرْضَ بَعْدَ مَوْتِهَاۜ  قَدْ بَيَّنَّا لَكُمُ الْاٰيَاتِ لَعَلَّكُمْ تَعْقِلُونَ", translation: "Bilin ki yeryüzünü ölümünden sonra dirilten Allah'tır. Aklınızı kullanırsınız diye ayetleri sizin için açıkça ortaya koyduk.", transliteration: "İ'lemu ennellahe yuhyil arda ba'de mevtiha, kad beyyenna lekumul ayati leallekum ta'kılun."},
  {id: 5093, sure_no: 18, ayet_no: 18, sure_adi: "Hadid", text: "اِنَّ الْمُصَّدِّق۪ينَ وَالْمُصَّدِّقَاتِ وَاَقْرَضُوا اللّٰهَ قَرْضاً حَسَناً يُضَاعَفُ لَهُمْ وَلَهُمْ اَجْرٌ كَر۪يمٌ", translation: "Sadaka veren erkeklerin, sadaka veren kadınların ve Allah'a iyi bir ödünç verenlerin, verdikleri katlanarak geri ödenir. Ve ayrıca onlar için çok şerefli bir ödül vardır.", transliteration: "İnnel mussaddikine vel mussaddikati ve akradullahe kardan hasenen yudaafu lehum ve lehum ecrun kerim."},
  {id: 5094, sure_no: 19, ayet_no: 19, sure_adi: "Hadid", text: "وَالَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَرُسُلِـه۪ٓ اُو۬لٰٓئِكَ هُمُ الصِّدّ۪يقُونَۗ وَالشُّهَدَٓاءُ عِنْدَ رَبِّهِمْۜ لَهُمْ اَجْرُهُمْ وَنُورُهُمْۜ وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ الْجَح۪يمِ۟", translation: "Allah'a ve Resuller'ine iman edenler; işte onlar Sıddıklardır. Ve Rabb'leri yanında şahitlerdir. Onların ödülleri ve nurları vardır. Küfreden ve ayetlerimizi yalanlayanlar, işte onlar Cehennem halkıdırlar.", transliteration: "Vellezine amenu billahi ve rusulihi ulaike humus sıddikune veş şuhedau inde rabbihim, lehum ecruhum ve nuruhum, vellezine keferu ve kezzebu bi ayatina ulaike ashabul cahim."},
  {id: 5095, sure_no: 20, ayet_no: 20, sure_adi: "Hadid", text: "اِعْلَمُٓوا اَنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ وَز۪ينَةٌ وَتَفَاخُرٌ بَيْنَكُمْ وَتَكَاثُرٌ فِي الْاَمْوَالِ وَالْاَوْلَادِۜ  كَمَثَلِ غَيْثٍ اَعْجَبَ الْكُفَّارَ نَبَاتُهُ ثُمَّ يَه۪يجُ فَتَرٰيهُ مُصْفَراًّ ثُمَّ يَكُونُ حُطَاماًۜ وَفِي الْاٰخِرَةِ عَذَابٌ شَد۪يدٌۙ وَمَغْفِرَةٌ مِنَ اللّٰهِ وَرِضْوَانٌۜ وَمَا الْحَيٰوةُ الدُّنْيَٓا اِلَّا مَتَاعُ الْغُرُورِ", translation: "Bilin ki dünya hayatı bir oyundur, bir oyalanmadır; bir ziynettir, aranızda bir övünmedir, mallarda ve çocuklarda çokluk yarışıdır. Onun durumu; bitirdiği bitkilerle kafirlerin hoşuna giden yağmur gibidir. Ardından o bitkileri kurur, onları sararmış görürsün. Sonra da çer çöp olurlar. Ahirette, şiddetli bir azap da Allah'ın hoşnutluğu ve bağışlaması da vardır. Dünya hayatı ise aldatıcı bir metadan başka bir şey değildir.", transliteration: "İ'lemu ennemel hayatud dunya leibun ve lehvun ve zinetun ve tefahurun beynekum ve tekasurun fil emvali vel evlad, ke meseli gaysin a'cebel kuffare nebatuhu summe yehicu fe terahu musferren summe yekunu hutama, ve fil ahıreti azabun şedidun ve magfiretun minallahi ve rıdvan, ve mel hayatud dunya illa metaul gurur."},
  {id: 5096, sure_no: 21, ayet_no: 21, sure_adi: "Hadid", text: "سَابِقُٓوا اِلٰى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَٓاءِ وَالْاَرْضِۙ اُعِدَّتْ لِلَّذ۪ينَ اٰمَنُوا بِاللّٰهِ وَرُسُلِه۪ۜ ذٰلِكَ فَضْلُ اللّٰهِ يُؤْت۪يهِ مَنْ يَشَٓاءُۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ", translation: "Rabb'inizin bağışlamasını, Allah'a ve Resuller'ine iman edenler için hazırlanmış, genişliği gökle yerin genişliği gibi olan Cennet'i kazanmak için yarışın. İşte bu, Allah'ın lütfudur. Onu hak edene verir. Ve Allah, büyük lütuf sahibidir.", transliteration: "Sabiku ila magfiretin min rabbikum ve cennetin arduha keardıs semai vel ardı uıddet lillezine amenu billahi ve rusulih, zalike fadlullahi yu'tihi men yeşau, vallahu zul fadlil azim."},
  {id: 5097, sure_no: 22, ayet_no: 22, sure_adi: "Hadid", text: "مَٓا اَصَابَ مِنْ مُص۪يبَةٍ فِي الْاَرْضِ وَلَا ف۪ٓي اَنْفُسِكُمْ اِلَّا ف۪ي كِتَابٍ مِنْ قَبْلِ اَنْ نَبْرَاَهَاۜ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌۚ", translation: "Ne yeryüzünde ne de kendinizde meydana gelen bir musibet yoktur ki Biz onu gerçekleştirmeden önce bir Kitap'ta yazılmamış olsun. Kuşkusuz bu Allah'a kolaydır.", transliteration: "Ma esabe min musibetin fil ardı ve la fi enfusikum illa fi kitabin min kabli en nebreeha, inne zalike alallahi yesir."},
  {id: 5098, sure_no: 23, ayet_no: 23, sure_adi: "Hadid", text: "لِكَيْلَا تَأْسَوْا عَلٰى مَا فَاتَكُمْ وَلَا تَفْرَحُوا بِمَٓا اٰتٰيكُمْۜ وَاللّٰهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍۙ", translation: "Kaybettiklerinize üzülmemeniz, Allah'ın verdiği şeylerle şımarmamanız içindir. Allah, kendisini beğenip böbürlenen hiç kimseyi sevmez.", transliteration: "Li keyla te'sev ala ma fatekum ve la tefrehu bi ma atakum, vallahu la yuhıbbu kulle muhtalin fehur."},
  {id: 5099, sure_no: 24, ayet_no: 24, sure_adi: "Hadid", text: "اَلَّذ۪ينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِۜ وَمَنْ يَتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَم۪يدُ", translation: "Böyleleri cimrilik ederler ve insanlara da cimrilik yapmalarını tavsiye ederler. Kim yüz çevirirse bilsin ki Allah, Hiçbir Şeye Muhtaç Olmayan'dır, Övgüye Değer Yegane Varlık'tır.", transliteration: "Ellezine yebhalune ve ye'murunen nase bil buhl, ve men yetevelle feinnellahe huvel ganiyyul hamid."},
  {id: 5100, sure_no: 25, ayet_no: 25, sure_adi: "Hadid", text: "لَقَدْ اَرْسَلْنَا رُسُلَنَا بِالْبَيِّنَاتِ وَاَنْزَلْنَا مَعَهُمُ الْكِتَابَ وَالْم۪يزَانَ لِيَقُومَ النَّاسُ بِالْقِسْطِۚ وَاَنْزَلْنَا الْحَد۪يدَ ف۪يهِ بَأْسٌ  شَد۪يدٌ  وَمَنَافِعُ  لِلنَّاسِ وَلِيَعْلَمَ اللّٰهُ  مَنْ  يَنْصُرُهُ وَرُسُلَهُ بِالْغَيْبِۜ اِنَّ اللّٰهَ قَوِيٌّ عَز۪يزٌ۟", translation: "Ant olsun ki Resullerimizi beyyinelerle gönderdik. Ve insanlar haktan yana olsunlar diye Resullerle birlikte Kitap'ı ve mizanı indirdik. Ayrıca kendisinde büyük bir güç ve insanlar için pek çok yararlar bulunan demiri bağışladık, Kendisine ve Resullerine, onların gıyabında destek olan kimseler açıkça bilinsin! Kuşkusuz Allah, Mutlak Güç Sahibi'dir, Mutlak Üstün Olan'dır.", transliteration: "Lekad erselna rusulena bil beyyinati ve enzelna meahumul kitabe vel mizane li yekumen nasu bil kıst, ve enzelnel hadide fihi be'sun şedidun ve menafiu lin nasi ve li ya'lemallahu men yensuruhu ve rusulehu bil gayb, innellahe kaviyyun aziz."},
  {id: 5101, sure_no: 26, ayet_no: 26, sure_adi: "Hadid", text: "وَلَقَدْ اَرْسَلْنَا نُوحاً وَاِبْرٰه۪يمَ وَجَعَلْنَا ف۪ي ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتَابَ فَمِنْهُمْ مُهْتَدٍۚ وَكَث۪يرٌ مِنْهُمْ فَاسِقُونَ", translation: "Ant olsun ki Nuh'u ve İbrahim'i gönderdik. Onların soyundan Nebiler kıldık ve onlara Kitap verdik. Gönderildikleri halktan bir kısmı doğru yolu bulurken, birçoğu da fasıktırlar.", transliteration: "Ve lekad erselna nuhan ve ibrahime ve cealna fi zurriyyetihimen nubuvvete vel kitabe fe minhum muhted, ve kesirun minhum fasikun."},
  {id: 5102, sure_no: 27, ayet_no: 27, sure_adi: "Hadid", text: "ثُمَّ قَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِع۪يسَى ابْنِ مَرْيَمَ وَاٰتَيْنَاهُ الْاِنْج۪يلَ وَجَعَلْنَا ف۪ي قُلُوبِ الَّذ۪ينَ اتَّبَعُوهُ رَأْفَةً  وَرَحْمَةًۜ  وَرَهْبَانِيَّةًۨ ابْتَدَعُوهَا مَا كَتَبْنَاهَا عَلَيْهِمْ اِلَّا ابْتِغَٓاءَ رِضْوَانِ اللّٰهِ فَمَا رَعَوْهَا حَقَّ رِعَايَـتِهَاۚ فَاٰتَيْنَا الَّذ۪ينَ اٰمَنُوا مِنْهُمْ اَجْرَهُمْۚ وَكَث۪يرٌ مِنْهُمْ فَاسِقُونَ", translation: "Sonra onların izleri üzerinde art arda Resullerimizi gönderdik. Ve Meryem Oğlu İsa'yı gönderdik ve ona İncil'i verdik. Ona uyan kimselerin kalplerine şefkat ve merhamet koyduk. Allah'ın rızasını kazanmak için uydurdukları ve fakat gereği gibi de uymadıkları ruhbanlık Bizim buyruğumuzdan kaynaklanmış değildir. Onlardan iman edenlere ödüllerini verdik. Ne var ki onların çoğu doğru yoldan çıkmış kimselerdi.", transliteration: "Summe kaffeyna ala asarihim bi rusulina ve kaffeyna bi'isebni meryeme ve ateynahul incile ve cealna fi kulubillezinet tebeuhu re'feten ve rahmeh, ve rahbaniyyetenibtedeuha ma ketebnaha aleyhim illebtigae rıdvanillahi fe ma reavha hakka riayetiha, fe ateynellezine amenu minhum ecrehum, ve kesirun minhum fasikun."},
  {id: 5103, sure_no: 28, ayet_no: 28, sure_adi: "Hadid", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَاٰمِنُوا بِرَسُولِه۪ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِه۪ وَيَجْعَلْ لَكُمْ نُوراً تَمْشُونَ بِه۪ وَيَغْفِرْ لَكُمْۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌۙ", translation: "Ey İman Edenler! Allah için takvalı olun. O'nun Resul'üne iman edin ki, size rahmetinden iki pay versin. Ve size aydınlığında yürüyeceğiniz bir ışık yapsın. Sizi bağışlasın. Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyyuhellezine amenut tekullahe ve aminu bi resulihi yu'tikum kifleyni min rahmetihi ve yec'al lekum nuren temşune bihi ve yagfir lekum, vallahu gafurun rahim."},
  {id: 5104, sure_no: 29, ayet_no: 29, sure_adi: "Hadid", text: "لِئَلَّا يَعْلَمَ اَهْلُ الْكِتَابِ اَلَّا يَقْدِرُونَ عَلٰى شَيْءٍ مِنْ فَضْلِ اللّٰهِ وَاَنَّ الْفَضْلَ بِيَدِ اللّٰهِ يُؤْت۪يهِ مَنْ يَشَٓاءُۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ", translation: "Kitap Ehli, Allah'ın lütfundan hiçbir şeye güçlerinin yetmeyeceğini; lütfun Allah'ın elinde olduğunu ve onu hak edene vereceğini bilmezlik etmesin! Allah, Büyük Lütuf Sahibi'dir.", transliteration: "Li ella ya'leme ehlul kitabi ella yakdirune ala şey'in min fadlillahi ve ennel fadle bi yedillahi yu'tihi men yeşau, vallahu zul fadlil azim."},
  {id: 5105, sure_no: 1, ayet_no: 1, sure_adi: "Mücadele", text: "قَدْ سَمِعَ اللّٰهُ قَوْلَ الَّت۪ي تُجَادِلُكَ ف۪ي زَوْجِهَا وَتَشْتَك۪ٓي اِلَى اللّٰهِۗ وَاللّٰهُ يَسْمَعُ تَحَاوُرَكُمَاۜ اِنَّ اللّٰهَ سَم۪يعٌ بَص۪يرٌ", translation: "Kocası hakkında seninle tartışan ve şikayette bulunan kadının sözünü Allah kesinlikle işitmiştir. Ve Allah, konuşmalarınızı işitir. Kuşkusuz ki Allah, Her Şeyi Duyan, Her Şeyi Gören'dir.", transliteration: "Kad semiallahu kavlelleti tucadiluke fi zevciha ve teşteki ilallahi vallahu yesmeu tehavurekuma, innellahe semi'un basir."},
  {id: 5106, sure_no: 2, ayet_no: 2, sure_adi: "Mücadele", text: "اَلَّذ۪ينَ يُظَاهِرُونَ مِنْكُمْ مِنْ نِسَٓائِهِمْ مَا هُنَّ اُمَّهَاتِهِمْۜ اِنْ اُمَّهَاتُهُمْ اِلَّا الّٰٓئ۪ وَلَدْنَهُمْۜ وَاِنَّهُمْ لَيَقُولُونَ مُنْكَراً مِنَ الْقَوْلِ وَزُوراًۜ وَاِنَّ اللّٰهَ لَعَفُوٌّ غَفُورٌ", translation: "Sizden hanımlarına zihar yapanlar bilsinler ki, hanımları anneleri değildir. Anneleri yalnızca kendilerini doğuranlardır. Doğrusu onlar, sözün çirkin ve asılsız olanını söylüyorlar. Kuşkusuz Allah, Çok Affedici'dir, Çok Bağışlayıcı'dır.", transliteration: "Ellezine yuzahirune minkum min nisaihim ma hunne ummehatihim, in ummehatuhum illellai velednehum, ve innehum le yekulune munkeren minel kavli ve zura, ve innellahe le afuvvun gafur."},
  {id: 5107, sure_no: 3, ayet_no: 3, sure_adi: "Mücadele", text: "وَالَّذ۪ينَ يُظَاهِرُونَ مِنْ نِسَٓائِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا فَـتَحْر۪يرُ رَقَـبَةٍ مِنْ قَبْلِ اَنْ يَتَمَٓاسَّاۜ ذٰلِكُمْ تُوعَظُونَ بِه۪ۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ", translation: "Hanımlarına zihar yapıp sonra da kararlarından dönenlerin, ilişkiye girmeden önce bir rekabeyi özgürlüğüne kavuşturması gerekir. İşte size, yapmanız gereken öğütleniyor. Allah, yaptığınız her şeyden haberdardır.", transliteration: "Vellezine yuzahirune min nisaihim summe yeudune li ma kalu fe tahriru rekabetin min kabli en yetemassa, zalikum tuazune bih, vallahu bi ma ta'melune habir."},
  {id: 5108, sure_no: 4, ayet_no: 4, sure_adi: "Mücadele", text: "فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ اَنْ يَتَمَٓاسَّاۚ فَمَنْ لَمْ يَسْتَطِعْ فَاِطْعَامُ سِتّ۪ينَ مِسْك۪يناًۜ ذٰلِكَ لِتُؤْمِنُوا بِاللّٰهِ وَرَسُولِه۪ۜ وَتِلْكَ حُدُودُ اللّٰهِۜ وَلِلْكَافِر۪ينَ عَذَابٌ اَل۪يمٌ", translation: "Kim bulamazsa, ilişkiye girmeden önce, aralıksız olarak iki ay oruç tutmalıdır. Oruç tutmaya gücü yetmeyenler, altmış yoksulu doyurmalıdır. Allah'a ve Resul'üne iman etmenizin gereği olarak böyle yapın. Bunlar, Allah'ın sınırlarıdır. Kafirler için çok acıklı bir azap vardır.", transliteration: "Fe men lem yecid fe siyamu şehreyni mutetabiayni min kabli en yetemassa, fe men lem yestetı' fe ıt'amu sittine miskina, zalike li tu'minu billahi ve resulih, ve tilke hududullah, ve lil kafirine azabun elim."},
  {id: 5109, sure_no: 5, ayet_no: 5, sure_adi: "Mücadele", text: "اِنَّ الَّذ۪ينَ يُحَٓادُّونَ اللّٰهَ وَرَسُولَهُ كُبِتُوا كَمَا كُبِتَ الَّذ۪ينَ مِنْ قَبْلِهِمْ وَقَدْ اَنْزَلْـنَٓا اٰيَاتٍ بَيِّنَاتٍۜ وَلِلْكَافِر۪ينَ عَذَابٌ مُه۪ينٌۚ", translation: "Kanıt içeren açıklayıcı ayetler indirmiş olduğumuz halde, Allah'a ve Resul'üne muhalefet edenler, kendilerinden öncekilerin alçaltıldığı gibi alçaltılacaklardır. Kafirler için alçaltıcı bir azap vardır.", transliteration: "İnnelleziyne yuhadunellahe ve resulehu kubitu kema kubitellezine min kablihim ve kad enzelna ayatin beyyinat, ve lil kafirine azabun muhin."},
  {id: 5110, sure_no: 6, ayet_no: 6, sure_adi: "Mücadele", text: "يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَم۪يعاً فَيُنَبِّئُهُمْ بِمَا عَمِلُواۜ  اَحْصٰيهُ اللّٰهُ وَنَسُوهُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌ۟", translation: "Allah, onları dirilteceği gün, kendilerine yaptıklarını haber verecektir. Allah, onların unuttuklarını tek tek sayacak. Allah, Her Şeye Tanıktır.", transliteration: "Yevme yeb'asu humullahu cemian fe yunebbiuhum bi ma amilu, ahsahullahu ve nesuh, vallahu ala kulli şey'in şehid."},
  {id: 5111, sure_no: 7, ayet_no: 7, sure_adi: "Mücadele", text: "اَلَمْ تَرَ اَنَّ اللّٰهَ يَعْلَمُ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۜ مَا يَكُونُ مِنْ نَجْوٰى ثَلٰثَةٍ اِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ اِلَّا هُوَ سَادِسُهُمْ وَلَٓا اَدْنٰى مِنْ ذٰلِكَ وَلَٓا اَكْثَرَ اِلَّا هُوَ مَعَهُمْ اَيْنَ مَا كَانُواۚ ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوا يَوْمَ الْقِيٰمَةِۜ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Allah'ın gökte ve yerde olan her şeyi bildiğinden haberin yok mu? Üç kişinin gizli konuştuğu yerde dördüncüleri kesinlikle O'dur. Beş kişinin, O altıncısıdır. Bundan az veya çok olsunlar ve nerede olurlarsa olsunlar O, kesinlikle onlarla beraberdir. Sonra Kıyamet Günü, yaptıkları şeyleri onlara haber verecektir. Kuşkusuz Allah, Her Şeyi En İyi Bilen'dir.", transliteration: "E lem tere ennellahe ya'lemu ma fis semavati ve ma fil ard, ma yekunu min necva selasetin illa huve rabiuhum ve la hamsetin illa huve sadisuhum ve la edna min zalike ve la eksere illa huve me'ahum eyne ma kanu, summe yunebbiuhum bi ma amilu yevmel kıyameh, innellahe bi kulli şey'in alim."},
  {id: 5112, sure_no: 8, ayet_no: 8, sure_adi: "Mücadele", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ نُهُوا عَنِ النَّجْوٰى ثُمَّ يَعُودُونَ لِمَا نُهُوا عَنْهُ وَيَتَنَاجَوْنَ بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِۘ وَاِذَا جَٓاؤُ۫كَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللّٰهُۙ وَيَقُولُونَ ف۪ٓي اَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللّٰهُ بِمَا نَقُولُۜ حَسْبُهُمْ جَهَنَّمُۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمَص۪يرُ", translation: "Gizlilik içinde konuşmaktan yasaklandıkları halde, bu yasağa uymayarak günah, düşmanlık ve Resul'e karşı gelmek amacıyla gizlilik içinde bir araya gelenlerden haberin var mı? Sana geldikleri zaman, Allah'ın seni selamlamadığı şeyle seni selamlarlar. Kendi aralarında da: \"Söylediğimiz şeylerden dolayı Allah bize azap etse ya!\" diyorlar. Onlara Cehennem yeter. Ona yaslanacaklar. Orası ne kötü varış yeridir.", transliteration: "E lem tere ilellezine nuhu aninnecva summe yeudune li ma nuhu anhu ve yetenacevne bil ismi vel udvani ve ma'siyetir resul, ve iza cauke hayyevke bi ma lem yuhayyike bihillahu, ve yekulune fi enfusihim lev la yuazzibunallahu bi ma nekul, hasbuhum cehennem, yaslevneha, febi'sel masir."},
  {id: 5113, sure_no: 9, ayet_no: 9, sure_adi: "Mücadele", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوٰىۜ وَاتَّقُوا اللّٰهَ الَّـذ۪ٓي اِلَيْهِ تُحْشَرُونَ", translation: "Ey İman Edenler! Bir araya geldiğiniz zaman, aranızda günah, düşmanlık ve Resul'e karşı gelmek amacıyla görüşme yapmayın. Görüşmelerinizi birr ve takva konusunda yapın. Huzurunda toplanacağınız Allah'a karşı takva sahibi olun.", transliteration: "Ya eyyuhellezine amenu iza tenaceytum fe la tetenacev bil ismi vel udvani ve ma'siyetir resuli ve tenacev bil birri vet takva, vettekullahellezi ileyhi tuhşerun."},
  {id: 5114, sure_no: 10, ayet_no: 10, sure_adi: "Mücadele", text: "اِنَّمَا النَّجْوٰى مِنَ الشَّيْطَانِ لِيَحْزُنَ الَّذ۪ينَ اٰمَنُوا وَلَيْسَ بِضَٓارِّهِمْ شَيْـٔاً اِلَّا بِـاِذْنِ اللّٰهِۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ", translation: "Kuşkusuz gizlilik içinde yapılan görüşmeler, İman Edenler'i üzmek için şeytancadır. Oysa şeytan, Allah'ın izni olmadıkça onlara bir sıkıntı verecek değildir. Öyleyse Mü'minler Allah'a tevekkül etsinler.", transliteration: "İnne men necva mineş şeytani li yahzunellezine amenu ve leyse bi darrihim şey'en illa bi iznillah, ve alallahi fel yetevekkelil mu'minun."},
  {id: 5115, sure_no: 11, ayet_no: 11, sure_adi: "Mücadele", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا ق۪يلَ لَكُمْ تَفَسَّحُوا فِي الْمَجَالِسِ فَافْسَحُوا يَفْسَحِ اللّٰهُ لَكُمْۚ وَاِذَا ق۪يلَ انْشُزُوا فَانْشُزُوا يَرْفَعِ اللّٰهُ الَّذ۪ينَ اٰمَنُوا مِنْكُمْۙ وَالَّذ۪ينَ اُو۫تُوا الْعِلْمَ دَرَجَاتٍۜ وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ", translation: "Ey İman Edenler! Meclislerde size: \"Yer açın!\" denildiği zaman, hemen yer açın. Allah da size yer açar. \"Dağılın!\" denildiği zaman da hemen dağılın! Allah, sizden iman edenlerin ve ilim verilmiş olanların derecelerini yükseltir. Allah, yaptığınız Her Şeyden Haberdardır.", transliteration: "Ya eyyuhellezine amenu iza kile lekum tefessehu fil mecalisi fefsehu yefsehıllahu lekum, ve iza kilenşuzu fenşuzu yerfeillahullezine amenu minkum vellezine utul ilme derecat, vallahu bi ma ta'melune habir."},
  {id: 5116, sure_no: 12, ayet_no: 12, sure_adi: "Mücadele", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا نَاجَيْتُمُ الرَّسُولَ فَقَدِّمُوا بَيْنَ يَدَيْ نَجْوٰيكُمْ صَدَقَةًۜ ذٰلِكَ خَيْرٌ لَـكُمْ وَاَطْهَرُۜ فَاِنْ لَمْ تَجِدُوا فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Ey İman Edenler! Resul'le gizli bir şey konuşacağınız zaman, gizli bir şey konuşmanızdan önce sadaka verin. Bu sizin için daha hayırlıdır ve daha temizdir. Ancak bir şey bulamazsanız, bilin ki Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyyuhellezine amenu iza naceytumur resule fe kaddimu beyne yedey necvakum sadakah, zalike hayrun lekum ve athar, fe in lem tecidu fe innellahe gafurun rahim."},
  {id: 5117, sure_no: 13, ayet_no: 13, sure_adi: "Mücadele", text: "ءَاَشْفَقْتُمْ اَنْ تُقَدِّمُوا بَيْنَ يَدَيْ نَجْوٰيكُمْ صَدَقَاتٍۜ فَاِذْ لَمْ تَفْعَلُوا وَتَابَ اللّٰهُ عَلَيْكُمْ فَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَط۪يعُوا اللّٰهَ وَرَسُولَهُۜ  وَاللّٰهُ خَب۪يرٌ بِمَا تَعْمَلُونَ۟", translation: "Gizli bir şey konuşmadan önce sadaka vermek ağır geldi değil mi? Bunu yapamayınca Allah, size tevbe etti. Artık salatı ikame edin, zekatı yapın; Allah'a ve Resul'üne itaat edin. Allah yaptıklarınızdan haberdardır.", transliteration: "E eşfaktum en tukaddimu beyne yedey necvakum sadekat, fe iz lem tef'alu ve taballahu aleykum, fe ekimus salate ve atuz zekate ve etiullahe ve resuleh, vallahu habirun bi ma ta'melun."},
  {id: 5118, sure_no: 14, ayet_no: 14, sure_adi: "Mücadele", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ تَوَلَّوْا قَوْماً غَضِبَ اللّٰهُ عَلَيْهِمْۜ مَا هُمْ مِنْكُمْ وَلَا مِنْهُمْۙ وَيَحْلِفُونَ عَلَى الْـكَذِبِ وَهُمْ يَعْلَمُونَ", translation: "Allah'ın kendilerine gazap ettiği bir halka dönenleri görüyor musun? Aslında onlar ne sizdendirler ne de onlardan. Bilerek yalan yere yemin ediyorlar.", transliteration: "E lem tere ilellezine tevellev kavmen gadıballahu aleyhim, ma hum minkum ve la minhum ve yahlifune alel kezibi ve hum ya'lemun."},
  {id: 5119, sure_no: 15, ayet_no: 15, sure_adi: "Mücadele", text: "اَعَدَّ اللّٰهُ لَهُمْ عَذَاباً شَد۪يداًۜ اِنَّهُمْ سَٓاءَ مَا كَانُوا يَعْمَلُونَ", translation: "Allah, onlara şiddetli bir azap hazırlamıştır. Onlar, çok kötü şeyler yapıyorlar.", transliteration: "E addallahu lehum azaben şedida, innehum sae ma kanu ya'melun."},
  {id: 5120, sure_no: 16, ayet_no: 16, sure_adi: "Mücadele", text: "اِتَّخَذُٓوا اَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَب۪يلِ اللّٰهِ فَلَهُمْ عَذَابٌ مُه۪ينٌ", translation: "Yeminlerini kullanarak insanları Allah'ın yolundan çevirdiler. Artık onlar için alçaltıcı bir azap vardır.", transliteration: "İttehazu eymanehum cunneten fe saddu an sebilillahi fe lehum azabun muhin."},
  {id: 5121, sure_no: 17, ayet_no: 17, sure_adi: "Mücadele", text: "لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَٓا اَوْلَادُهُمْ مِنَ اللّٰهِ شَيْـٔاًۜ اُو۬لٰٓئِكَ اَصْحَابُ النَّارِۚ هُمْ ف۪يهَا خَالِدُونَ", translation: "Onların malları ve evlatları, Allah'tan gelecek bir şeye karşı, onlara asla yarar sağlamaz. İşte onlar, ateşin halkıdırlar. Orada sürekli kalacak olanlardır.", transliteration: "Len tugniye anhum emvaluhum ve la evladuhum min allahi şey'a, ulaike ashabun nar, hum fiha halidun."},
  {id: 5122, sure_no: 18, ayet_no: 18, sure_adi: "Mücadele", text: "يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَم۪يعاً فَيَحْلِفُونَ لَهُ كَمَا يَحْلِفُونَ لَكُمْ وَيَحْسَبُونَ اَنَّهُمْ عَلٰى شَيْءٍۜ  اَلَٓا اِنَّهُمْ هُمُ الْكَاذِبُونَ", translation: "O gün Allah, onların tamamını yeniden diriltecek. O zaman, size yemin ettikleri gibi O'na da yemin edecekler. Böylece bir şey elde edeceklerini sanacaklar. Dikkat edin! Onlar, kesinlikle yalancıdırlar.", transliteration: "Yevme yeb'asuhumullahu cemian fe yahlifune lehu kema yahlifune lekum ve yahsebune ennehum ala şey'in, e la innehum humul kazibun."},
  {id: 5123, sure_no: 19, ayet_no: 19, sure_adi: "Mücadele", text: "اِسْتَحْوَذَ عَلَيْهِمُ الشَّيْطَانُ فَاَنْسٰيهُمْ ذِكْرَ اللّٰهِۜ  اُو۬لٰٓئِكَ حِزْبُ الشَّيْطَانِۜ اَلَٓا اِنَّ حِزْبَ الشَّيْطَانِ هُمُ الْخَاسِرُونَ", translation: "Şeytan onları kuşattı. Böylece Allah'ın öğüdünü onlara unutturdu. Onlar, şeytanın taraftarlarıdır. Dikkat edin! Şeytanın taraftarları kesinlikle kaybedenlerdir.", transliteration: "İstahveze aleyhimuş şeytanu fe ensahum zikrallah, ulaike hizbuş şeytan, ela inne hizbeşşeytani humul hasirun."},
  {id: 5124, sure_no: 20, ayet_no: 20, sure_adi: "Mücadele", text: "اِنَّ الَّذ۪ينَ يُحَٓادُّونَ اللّٰهَ وَرَسُولَهُٓ  اُو۬لٰٓئِكَ فِي الْاَذَلّ۪ينَ", translation: "Allah'a ve O'nun Resul'üne karşı haddi aşanlar, işte onlar zillet içindedirler.", transliteration: "İnnellezine yuhaddunallahe ve resulehu ulaike fil ezellin."},
  {id: 5125, sure_no: 21, ayet_no: 21, sure_adi: "Mücadele", text: "كَتَبَ اللّٰهُ لَاَغْلِبَنَّ اَنَا۬ وَرُسُل۪يۜ اِنَّ اللّٰهَ قَوِيٌّ عَز۪يزٌ", translation: "Allah: \"Ben ve Resul'lerim kesinlikle galip geleceğiz.\" diye hükmetmiştir. Kuşkusuz Allah; Mutlak Güç Sahibi'dir, Mutlak Üstün Olan'dır.", transliteration: "Keteballahu le aglibenne ene ve rusuli, innallahe kaviyyun aziz."},
  {id: 5126, sure_no: 22, ayet_no: 22, sure_adi: "Mücadele", text: "لَا تَجِدُ قَوْماً يُؤْمِنُونَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ يُوَٓادُّونَ مَنْ حَٓادَّ اللّٰهَ وَرَسُولَهُ وَلَوْ كَانُٓوا اٰبَٓاءَهُمْ اَوْ اَبْنَٓاءَهُمْ اَوْ اِخْوَانَهُمْ اَوْ عَش۪يرَتَهُمْۜ اُو۬لٰٓئِكَ كَتَبَ ف۪ي قُلُوبِهِمُ الْا۪يمَانَ وَاَيَّدَهُمْ بِرُوحٍ مِنْهُۜ وَيُدْخِلُهُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَاۜ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوا عَنْهُۜ اُو۬لٰٓئِكَ حِزْبُ اللّٰهِۜ اَلَٓا اِنَّ حِزْبَ اللّٰهِ هُمُ الْمُفْلِحُونَ", translation: "Allah'a ve ahiret gününe iman eden bir halkın, Allah'a ve O'nun Resul'üne karşı haddi aşanlara karşı sevgi duyduklarına tanık olamazsın; isterse bunlar, babaları, çocukları, kardeşleri veya akrabaları olsun. Onlar, Allah'ın kalplerine iman yazdığı ve kendilerini, kendinden bir ruh ile desteklediği kimselerdir. Allah, onları içinden ırmaklar akan Cennet'lere koyacaktır. Onlar, orada sürekli kalacak olanlardır. Allah, onlardan hoşnut oldu, onlar da O'ndan hoşnut oldular. İşte onlar, Allah'ın taraftarlarıdır. Dikkat edin! Doğrusu Allah'ın taraftarları kurtuluşa erenlerdir.", transliteration: "La tecidu kavmen yu'munune billahi vel yevmil ahiri yuvaddune men haddallahe ve resulehu ve lev kanu abaehum ve ebnaehum ve ihvanehum ev aşiretehum, ulaike ketebe fi kulubihimul imane ve eyyedehum bi ruhin minh, ve yudhıluhum cennatin tecri min tahtihel enharu halidine fiha, radıyallahu anhum ve radu anh, ulaike hizbullah, e la inne hizbullahi humul muflihun."},
  {id: 5127, sure_no: 1, ayet_no: 1, sure_adi: "Haşr", text: "سَبَّحَ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۚ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Göklerde ve yerde bulunanlar, Allah'ı tesbih etmektedir. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Sebbeha lillahi ma fis semavati ve ma fil ard, ve huvel azizul hakim."},
  {id: 5128, sure_no: 2, ayet_no: 2, sure_adi: "Haşr", text: "هُوَ الَّـذ۪ٓي اَخْرَجَ الَّذ۪ينَ كَفَرُوا مِنْ اَهْلِ الْكِتَابِ مِنْ دِيَارِهِمْ لِاَوَّلِ الْحَشْرِۜ مَا ظَنَنْتُمْ اَنْ يَخْرُجُوا وَظَنُّٓوا اَنَّهُمْ مَانِعَتُهُمْ حُصُونُهُمْ مِنَ اللّٰهِ فَاَتٰيهُمُ اللّٰهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا وَقَذَفَ ف۪ي قُلُوبِهِمُ الرُّعْبَ يُخْرِبُونَ بُيُوتَهُمْ بِاَيْد۪يهِمْ وَاَيْدِي الْمُؤْمِن۪ينَ فَاعْتَبِرُوا يَٓا اُو۬لِي الْاَبْصَارِ", translation: "Kitap Ehli'nden kafirleri sürgün için yurtlarından çıkaran O'dur. Siz, onların yurtlarından çıkacaklarını sanmamıştınız. Ve onlar da kalelerinin, kendilerini Allah'tan koruyacağına inandılar. Oysa Allah'ın buyruğu onlara hesaba katmadıkları yerden geldi. Allah, onların yüreklerine evlerinin kendi elleriyle ve iman edenlerin elleriyle harap edileceği korkusunu saldı. Ey basiret sahipleri! Artık ibret alın.", transliteration: "Huvellezi ahrecellezine keferu min ehlil kitabi min diyarihim li evvelil haşr, ma zanentum en yahrucu ve zannu ennehum maniatuhum husunuhum minallahi fe etahumullahu min haysu lem yahtesibu ve kazefe fi kulubihimur ru'be yuhribune buyutehum bi eydihim ve eydil mu'minine fa'tabiru ya ulil ebsar."},
  {id: 5129, sure_no: 3, ayet_no: 3, sure_adi: "Haşr", text: "وَلَوْلَٓا اَنْ كَتَبَ اللّٰهُ عَلَيْهِمُ الْجَلَٓاءَ لَعَذَّبَهُمْ فِي الدُّنْيَاۜ وَلَهُمْ فِي الْاٰخِرَةِ عَذَابُ النَّارِ", translation: "Eğer Allah, onlara sürgün takdirinde bulunmamış olsaydı, kesinlikle dünyada azap verirdi. Onlara, ahirette ateşin azabı vardır.", transliteration: "Ve lev la en keteballahu aleyhimul celae le azzebehum fid dunya, ve lehum fil ahıreti azabun nar."},
  {id: 5130, sure_no: 4, ayet_no: 4, sure_adi: "Haşr", text: "ذٰلِكَ بِاَنَّهُمْ شَٓاقُّوا اللّٰهَ وَرَسُولَهُۚ وَمَنْ يُشَٓاقِّ اللّٰهَ فَاِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِ", translation: "İşte bu, onların Allah'a ve O'nun Resul'üne muhalefet etmeleri nedeniyledir. Kim Allah'a muhalefet ederse bilsin ki Allah, cezası çok şiddetli olandır.", transliteration: "Zalike bi ennehum şa akkullahe ve resuleh, ve men yuşa akkıllahe fe innallahe şedidul ikab."},
  {id: 5131, sure_no: 5, ayet_no: 5, sure_adi: "Haşr", text: "مَا قَطَعْتُمْ مِنْ ل۪ينَةٍ اَوْ تَرَكْتُمُوهَا قَٓائِمَةً عَلٰٓى اُصُولِهَا فَبِاِذْنِ اللّٰهِ وَلِيُخْزِيَ الْفَاسِق۪ينَ", translation: "Taze hurmadan kestikleriniz veya kesmedikleriniz de ancak Allah'ın izniyledir. Bu, fasıkların perişan olmaları içindir.", transliteration: "Ma kata'tum min linetin ev terektumuha kaimeten ala usuliha fe bi iznillahi ve li yuhziyel fasikin."},
  {id: 5132, sure_no: 6, ayet_no: 6, sure_adi: "Haşr", text: "وَمَٓا اَفَٓاءَ اللّٰهُ عَلٰى رَسُولِه۪ مِنْهُمْ فَمَٓا اَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلٰكِنَّ اللّٰهَ يُسَلِّطُ رُسُلَهُ عَلٰى مَنْ يَشَٓاءُۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Allah'ın Resul'üne verdiği fey'e gelince, siz onlar için ne at koşturdunuz ne de deve! Fakat Allah; Resul'üne, dilediği kimselere karşı üstünlük verir. Allah, Her Şeye Güç Yetiren'dir.", transliteration: "Ve ma efa allahu ala resulihi minhum fe ma evceftum aleyhi min haylin ve la rikabin ve lakinnallahe yusallitu rusulehu ala men yeşau, vallahu ala kulli şey'in kadir."},
  {id: 5133, sure_no: 7, ayet_no: 7, sure_adi: "Haşr", text: "مَٓا اَفَٓاءَ اللّٰهُ عَلٰى رَسُولِه۪ مِنْ اَهْلِ الْقُرٰى فَلِلّٰهِ وَلِلرَّسُولِ وَلِذِي الْقُرْبٰى وَالْيَتَامٰى وَالْمَسَاك۪ينِ وَابْنِ السَّب۪يلِۙ  كَيْ لَا يَكُونَ دُولَةً بَيْنَ الْاَغْنِيَٓاءِ مِنْكُمْۜ وَمَٓا اٰتٰيكُمُ الرَّسُولُ فَخُذُوهُ وَمَا نَهٰيكُمْ عَنْهُ فَانْتَهُواۚ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ شَد۪يدُ الْعِقَابِۢ", translation: "Allah'ın, beldeler halkından, Resul'üne verdiği feyler; aranızda zenginliğe neden olan, elden ele dolaşan bir zenginlik olmasın diye; Allah, Resul, yakınlık sahipleri, yetimler, miskinler ve yol oğlu içindir. Resul size ne verdiyse onu alın. Sizi neden alıkoyduysa ondan vazgeçin. Allah'a karşı takva sahibi olun. Kuşkusuz Allah, Cezalandırması Çok Şiddetli Olan'dır.", transliteration: "Ma efa allahu ala resulihi min ehlil kura fe lillahi ve lir resuli ve lizil kurba vel yetama vel mesakini vebnis sebili key la yekune duleten beynel agniyai minkum, ve ma atakumur resulu fe huzuhu ve ma nehakum anhu fentehu, vettekullah, innallahe şedidul ikab."},
  {id: 5134, sure_no: 8, ayet_no: 8, sure_adi: "Haşr", text: "لِلْفُقَـرَٓاءِ الْمُهَاجِر۪ينَ الَّذ۪ينَ اُخْرِجُوا مِنْ دِيَارِهِمْ وَاَمْوَالِهِمْ يَبْتَغُونَ فَضْلاً مِنَ اللّٰهِ وَرِضْوَاناً وَيَنْصُرُونَ اللّٰهَ وَرَسُولَهُۜ  اُو۬لٰٓئِكَ هُمُ الصَّادِقُونَۚ", translation: "Bu mallarda; Allah'ın lütfu ve rızasını kazanmak arzusuyla, Allah'a ve Resul'üne yardım ettikleri için yurtlarından çıkarılan ve malları ellerinden alınan, hicret etmiş olan yoksulların da hakkı vardır. Çünkü onlar özü sözü bir olanlardır.", transliteration: "Lil fukarail muhacirinellezine uhricu min diyarihim ve emvalihim yebtegune fadlen minallahi ve rıdvanen ve yensurunallahe ve resuleh, ulaike humus sadikun."},
  {id: 5135, sure_no: 9, ayet_no: 9, sure_adi: "Haşr", text: "وَالَّذ۪ينَ تَبَوَّؤُ الدَّارَ وَالْا۪يمَانَ مِنْ قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ اِلَيْهِمْ وَلَا يَجِدُونَ ف۪ي صُدُورِهِمْ حَاجَةً مِمَّٓا اُو۫تُوا وَيُؤْثِرُونَ عَلٰٓى اَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌۜ وَمَنْ يُوقَ شُحَّ نَفْسِه۪ فَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَۚ", translation: "Onlardan önce Medine'yi yurt edinen ve kalplerine iman yerleşmiş olanlar, kendilerine hicret edenleri severler. Onlara verilen ganimetlerden dolayı, -kendileri muhtaç olsa bile- kalplerinde bir kaygı, kıskançlık duymazlar. Onları kendilerine tercih ederler. Kim, kendisini cimrilikten korursa, işte onlar kurtuluşa erenlerdir.", transliteration: "Vellezine tebevveud dare vel imane min kablihim yuhıbbune men hacere ileyhim ve la yecidune fi sudurihim haceten mimma utu ve yu'sirune ala enfusihim ve lev kane bihim hasasah, ve men yuka şuhha nefsihi fe ulaike humul muflihun."},
  {id: 5136, sure_no: 10, ayet_no: 10, sure_adi: "Haşr", text: "وَالَّذ۪ينَ جَٓاؤُ۫ مِنْ بَعْدِهِمْ يَقُولُونَ رَبَّـنَا اغْفِرْ لَنَا وَلِاِخْوَانِنَا الَّذ۪ينَ سَبَقُونَا بِالْا۪يمَانِ وَلَا تَجْعَلْ ف۪ي قُلُوبِنَا غِلاًّ لِلَّذ۪ينَ اٰمَنُوا رَبَّـنَٓا اِنَّكَ رَؤُ۫فٌ رَح۪يمٌ۟", translation: "Onlardan sonra gelenler: \"Rabb'imiz! Bizi ve bizden önce iman ile göç etmiş kardeşlerimizi bağışla. Kalplerimizde iman edenlere karşı kin bırakma. Rabb'imiz! Kuşkusuz Sen Çok Şefkatli, Rahmeti Kesintisizsin.\" derler.", transliteration: "Vellezine cau min ba'dihim yekulune rabbenagfir lena ve li ihvaninellezine sebekuna bil imani ve la tec'al fi kulubina gıllen lillezine amenu rabbena inneke raufun rahim."},
  {id: 5137, sure_no: 11, ayet_no: 11, sure_adi: "Haşr", text: "اَلَمْ تَرَ اِلَى الَّذ۪ينَ نَافَقُوا يَقُولُونَ لِاِخْوَانِهِمُ الَّذ۪ينَ كَفَرُوا مِنْ اَهْلِ الْكِتَابِ لَئِنْ اُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُط۪يعُ ف۪يكُمْ اَحَداً اَبَداًۚ وَاِنْ قُوتِلْتُمْ لَنَنْصُرَنَّكُمْۜ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكَاذِبُونَ", translation: "Nifak çıkaranları görmüyor musun? Kitap Ehli'nden Kafir yoldaşlarına: \"Eğer siz yurdunuzdan çıkarılacak olursanız, kesinlikle biz de sizinle birlikte çıkarız. Sizin aleyhinize olan konularda kimseye boyun eğmeyiz. Eğer sizinle savaşırlarsa, kesinlikle size yardım ederiz.\" derler. Allah, tanıklık eder ki onlar gerçekten yalancıdırlar.", transliteration: "E lem tere ilellezine nafeku yekulune li ihvanihimullezie keferu min ehlil kitabi le in uhrictum le nahrucenne me'akum ve la nutiu fi kum ehaden ebeden ve in kutiltum le nensurennekum, vallahu yeşhedu innehum le kazibun."},
  {id: 5138, sure_no: 12, ayet_no: 12, sure_adi: "Haşr", text: "لَئِنْ اُخْرِجُوا لَا يَخْرُجُونَ مَعَهُمْۚ وَلَئِنْ قُوتِلُوا لَا يَنْصُرُونَهُمْۚ وَلَئِنْ نَصَرُوهُمْ لَيُوَلُّنَّ الْاَدْبَارَ۠  ثُمَّ لَا يُنْصَرُونَ", translation: "Eğer yurtlarından çıkarılsalar, münafıklar kesinlikle onlarla birlikte çıkmazlar. Eğer savaşırlarsa, kesinlikle onlara yardım da etmezler. Yardım etseler bile, sıkışınca savaştan kaçarlar. Sonra onlara güvenenler de yardımsız kalırlar.", transliteration: "Le in uhricu la yahrucune me'ahum ve le in kutılu la yensurunehum ve le in nesaruhum le yuvellunnel edbar, summe la yunsarun."},
  {id: 5139, sure_no: 13, ayet_no: 13, sure_adi: "Haşr", text: "لَاَنْتُمْ اَشَدُّ رَهْبَةً ف۪ي صُدُورِهِمْ مِنَ اللّٰهِۜ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ", translation: "Onların yüreklerinde, Allah'a olan korkudan daha fazla size olan korku vardır. Bu onların, gerçeği kavramayan bir halk olmalarındandır.", transliteration: "Le entum eşeddu rehbeten fi sudurihim minallahi, zalike bi ennehum kavmun la yefkahun."},
  {id: 5140, sure_no: 14, ayet_no: 14, sure_adi: "Haşr", text: "لَا يُقَاتِلُونَكُمْ جَم۪يعاً اِلَّا ف۪ي قُرًى مُحَصَّنَةٍ اَوْ مِنْ وَرَٓاءِ جُدُرٍۜ بَأْسُهُمْ بَيْنَهُمْ شَد۪يدٌۜ تَحْسَبُهُمْ جَم۪يعاً وَقُلُوبُهُمْ شَتّٰىۜ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَۚ", translation: "Onlar, korunaklı şehirler içinde veya surlar arkasında olmadıkça, sizinle, toplu olarak karşı karşıya gelerek savaşmazlar. Kaldı ki kendi aralarında da derin ayrılıklar içindedirler. Sen, onların birlik olduklarını sanırsın, oysaki kalben farklı farklıdırlar. Bu, onların akıllarını doğru kullanmayan bir halk olmaları nedeniyledir.", transliteration: "La yukatilunekum cemian illa fi kuren muhassanetin ev min verai cudur, be'suhum beynehum şedid, tahsebuhum cemian ve kulubuhum şetta, zalike bi ennehum kavmun la ya'kılun."},
  {id: 5141, sure_no: 15, ayet_no: 15, sure_adi: "Haşr", text: "كَمَثَلِ الَّذ۪ينَ مِنْ قَبْلِهِمْ قَر۪يباً ذَاقُوا وَبَالَ اَمْرِهِمْۚ وَلَهُمْ عَذَابٌ اَل۪يمٌۚ", translation: "Onların durumu, kendilerinden yakın zaman öncesinde yaptıkları işlerin sonucunu tatmış olanların durumu gibidir. Ve onlar için acı bir azap vardır.", transliteration: "Kemeselillezine min kablihim kariben zaku ve bale emrihim ve lehum azabun elim."},
  {id: 5142, sure_no: 16, ayet_no: 16, sure_adi: "Haşr", text: "كَمَثَلِ الشَّيْطَانِ اِذْ قَالَ لِلْاِنْسَانِ اكْفُرْۚ فَلَمَّا كَفَرَ قَالَ اِنّ۪ي بَر۪ٓيءٌ مِنْكَ اِنّ۪ٓي اَخَافُ اللّٰهَ رَبَّ الْعَالَم۪ينَ", translation: "Onların durumu tıpkı şeytanın durumu gibidir. İnsana: \"Kafir ol.\" der. Ne var ki insan Kafir olunca da: \"Kesinlikle ben senden uzağım, ben alemlerin Rabb'i olan Allah'tan korkarım.\" der.", transliteration: "Ke meseliş şeytani iz kale lil insanikfur, fe lemma kefere kale inni beriun minke inni ehafullahe rabbel alemin."},
  {id: 5143, sure_no: 17, ayet_no: 17, sure_adi: "Haşr", text: "فَكَانَ عَاقِبَتَهُمَٓا اَنَّهُمَا فِي النَّارِ خَالِدَيْنِ ف۪يهَاۜ وَذٰلِكَ جَزٰٓؤُا الظَّالِم۪ينَ۟", translation: "Her ikisinin de sonu, ateşin içinde sonsuza dek kalmaktır. İşte zalimlerin cezası budur.", transliteration: "Fe kane akıbetehuma ennehuma fin nari halideyni fiha, ve zalike cezauz zalimin."},
  {id: 5144, sure_no: 18, ayet_no: 18, sure_adi: "Haşr", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا اتَّقُوا اللّٰهَ وَلْتَنْظُرْ نَفْسٌ مَا قَدَّمَتْ لِغَدٍۚ وَاتَّقُوا اللّٰهَۜ اِنَّ اللّٰهَ خَب۪يرٌ بِمَا تَعْمَلُونَ", translation: "Ey İman Edenler! Allah'a karşı takva sahibi olun! Herkes yarın için ne hazırladığına baksın! Allah'a karşı takva sahibi olun. Kuşkusuz ki Allah, yaptıklarınızdan haberdardır.", transliteration: "Ya eyyuhellezine amenuttekullahe vel tenzur nefsun ma kad demet ligad, vettekullah, innallahe habirun bi ma ta'melun."},
  {id: 5145, sure_no: 19, ayet_no: 19, sure_adi: "Haşr", text: "وَلَا تَكُونُوا كَالَّذ۪ينَ نَسُوا اللّٰهَ فَاَنْسٰيهُمْ اَنْفُسَهُمْۜ اُو۬لٰٓئِكَ هُمُ الْفَاسِقُونَ", translation: "Allah'ı umursamayan, böylece kendileri ile baş başa kalan kimseler gibi olmayın! İşte onlar fasıklardır.", transliteration: "Ve la tekunu kellezine nesullahe fe ensahum enfusehum, ulaike humul fasikun."},
  {id: 5146, sure_no: 20, ayet_no: 20, sure_adi: "Haşr", text: "لَا يَسْتَـو۪ٓي اَصْحَابُ النَّارِ وَاَصْحَابُ الْجَنَّةِۜ اَصْحَابُ الْجَنَّةِ هُمُ الْـفَٓائِزُونَ", translation: "Cehennemlikler ile Cennetlikler bir değildir. Cennetlikler, kurtuluşa eren kimselerdir.", transliteration: "La yestevi ashabun nari ve ashabul cenneh, ashabul cenneti humul faizun."},
  {id: 5147, sure_no: 21, ayet_no: 21, sure_adi: "Haşr", text: "لَوْ اَنْزَلْنَا هٰذَا الْقُرْاٰنَ عَلٰى جَبَلٍ لَرَاَيْتَهُ خَاشِعاً مُتَصَدِّعاً مِنْ خَشْيَةِ اللّٰهِۜ وَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ", translation: "Eğer Biz, bu Kur'an'ı dağa indirseydik, onu; huşu duyarak, Allah'a olan huşudan parça parça olmuş görürdün. Bu örnekleri insanlar için veriyoruz ki belki düşünüp gerçeği kavrarlar.", transliteration: "Lev enzelna hazel kur'ane ala cebelin le reeytehu haşian mutesaddian min haşyetillah, ve tilkel emsalu nadribuha lin nasi leallehum yetefekkerun."},
  {id: 5148, sure_no: 22, ayet_no: 22, sure_adi: "Haşr", text: "هُوَ اللّٰهُ الَّذ۪ي لَٓا اِلٰهَ اِلَّا هُوَۚ عَالِمُ الْغَيْبِ وَالشَّهَادَةِۚ هُوَ الرَّحْمٰنُ الرَّح۪يمُ", translation: "O, kendisinden başka ilah olmayan Allah'tır. Görünmeyeni ve görüneni bilendir. O, Rahmeti Bol ve Kesintisiz Olan'dır.", transliteration: "Huvallahullezi la ilahe illa huve, alimul gaybi veş şehadeh, huver rahmanur rahim."},
  {id: 5149, sure_no: 23, ayet_no: 23, sure_adi: "Haşr", text: "هُوَ اللّٰهُ الَّذ۪ي لَٓا اِلٰهَ اِلَّا هُوَۚ اَلْمَلِكُ الْقُدُّوسُ السَّلَامُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَز۪يزُ الْجَبَّارُ الْمُتَكَبِّرُۜ سُبْحَانَ اللّٰهِ عَمَّا يُشْرِكُونَ", translation: "O Allah ki: O'ndan başka ilah yoktur; Melik'tir, Kuddus'tür, Selam'dır, Mü'min'dir, Muheymin'dir, Aziz'dir, Cebbar'dır, Mutekebbir'dir. Allah, onların ortak koştukları şeylerden münezzehtir.", transliteration: "Huvallahullezi la ilahe illa huve, elmelikul kuddusus selamul mu'minul muheyminul azizul cebbarul mutekebbir, subhanallahi amma yuşrikun."},
  {id: 5150, sure_no: 24, ayet_no: 24, sure_adi: "Haşr", text: "هُوَ اللّٰهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ لَهُ الْاَسْمَٓاءُ الْحُسْنٰىۜ يُسَبِّـحُ لَهُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِۚ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "O Allah ki: Halik'dır, Bari'dir, Musavvir'dir. En iyi nitelikler O'na aittir. Göklerde ve yeryüzünde olanlar, O'nu tesbih ederler. O, Mutlak Üstün Olan'dır, En İyi Yasa Koyan'dır.", transliteration: "Huvallahul halikul bariul musavviru lehul esmaul husna, yusebbihu lehu ma fis semavati vel ard ve huvel azizul hakim."},
  {id: 5151, sure_no: 1, ayet_no: 1, sure_adi: "Mümtehine", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَّخِذُوا عَدُوّ۪ي وَعَدُوَّكُمْ اَوْلِيَٓاءَ تُلْقُونَ اِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوا بِمَا جَٓاءَكُمْ مِنَ الْحَقِّۚ يُخْرِجُونَ الرَّسُولَ وَاِيَّاكُمْ اَنْ تُؤْمِنُوا بِاللّٰهِ رَبِّكُمْۜ اِنْ كُنْتُمْ خَرَجْتُمْ جِهَاداً ف۪ي سَب۪يل۪ي وَابْتِغَٓاءَ مَرْضَات۪ي تُسِرُّونَ اِلَيْهِمْ بِالْمَوَدَّةِۗ وَاَنَا۬ اَعْلَمُ بِمَٓا اَخْفَيْتُمْ وَمَٓا اَعْلَنْتُمْۜ وَمَنْ يَفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَٓاءَ السَّب۪يلِ", translation: "Ey İman Edenler! Benim ve sizin düşmanlarınızı evliya edinmeyin. Onlar, Hakk'tan size geleni inkar ettikleri halde onlarla yakınlık kuruyorsunuz. Oysaki Rabb'iniz olan Allah'a iman etmenizden dolayı Resul'ü ve sizi yurdunuzdan çıkardılar. Eğer Benim yolumda mücadele etmek ve rızamı kazanmak için yola çıktıysanız, niçin onlara yakınlık kurup sır veriyorsunuz? Ben, sizin gizli ve açık bütün yaptıklarınızı bilirim. Sizden kim bunu yaparsa o, kesinlikle yolun ortasından sapmış olur.", transliteration: "Ya eyyuhellezine amenu la tettehızu aduvvi ve aduvvekum evliyae, tulkune ileyhim bil meveddeti ve kad keferu bi ma caekum minel hakk, yuhricuner resule ve iyyakum en tu'minu billahi rabbikum, in kuntum harectum cihaden fi sebili vebtigae merdati tusirrune ileyhim bil meveddeti ve ene a'lemu bi ma ahfeytum ve ma a'lentum, ve men yef'alhu minkum fe kad dalle sevaes sebil."},
  {id: 5152, sure_no: 2, ayet_no: 2, sure_adi: "Mümtehine", text: "اِنْ يَثْقَفُوكُمْ يَكُونُوا لَكُمْ اَعْدَٓاءً وَيَبْسُطُٓوا اِلَيْكُمْ اَيْدِيَهُمْ وَاَلْسِنَتَهُمْ بِالسُّٓوءِ وَوَدُّوا لَوْ تَكْفُرُونَۜ", translation: "Eğer ellerine fırsat geçirirlerse size düşman kesilirler. Elleriyle ve dilleriyle size her türlü kötülüğü yaparlar. Tekrar küfre dönmenizi temenni ederler.", transliteration: "İn yeskafukum yekunu lekum a'daen ve yebsutu ileykum eydiyehum ve elsinetehum bis sui ve veddu lev tekfurun."},
  {id: 5153, sure_no: 3, ayet_no: 3, sure_adi: "Mümtehine", text: "لَنْ تَنْفَعَكُمْ اَرْحَامُكُمْ وَلَٓا اَوْلَادُكُمْۚۛ يَوْمَ الْقِيٰمَةِۚۛ يَفْصِلُ بَيْنَكُمْۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Kıyamet günü akrabalarınız ve çocuklarınız size asla fayda sağlamazlar. Sizin aranızı ayıracaktır. Ve Allah, yaptıklarınızı En İyi Gören'dir.", transliteration: "Len tenfeakum erhamukum ve la evladukum, yevmel kıyameh yefsılu beynekum, vallahu bi ma ta'melune basir."},
  {id: 5154, sure_no: 4, ayet_no: 4, sure_adi: "Mümtehine", text: "قَدْ كَانَتْ لَكُمْ اُسْوَةٌ حَسَنَةٌ ف۪ٓي اِبْرٰه۪يمَ وَالَّذ۪ينَ مَعَهُۚ اِذْ قَالُوا لِقَوْمِهِمْ اِنَّا بُرَءٰٓؤُ۬ا مِنْكُمْ وَمِمَّا تَعْبُدُونَ مِنْ دُونِ اللّٰهِۘ   كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَٓاءُ اَبَداً حَتّٰى تُؤْمِنُوا بِاللّٰهِ وَحْدَهُٓ اِلَّا قَوْلَ اِبْرٰه۪يمَ لِاَب۪يهِ لَاَسْتَغْفِرَنَّ لَكَ وَمَٓا اَمْلِكُ لَكَ مِنَ اللّٰهِ مِنْ شَيْءٍۜ رَبَّـنَا عَلَيْكَ تَوَكَّلْنَا وَاِلَيْكَ اَنَبْنَا وَاِلَيْكَ الْمَص۪يرُ", translation: "Ancak İbrahim'in babasına: \"Allah'tan olacak olana gücüm yetmez, fakat senin için bağışlanma dileyeceğim\" sözü hariç. İbrahim'de ve onunla birlikte bulunanlarda sizin için iyi bir örnek vardır. Onlar halklarına şöyle demişlerdi: \"Biz, sizden ve sizin Allah'ın yanı sıra kulluk ettiğiniz şeylerden kesinlikle uzağız. Biz, sizi reddediyoruz. Siz, ilahın yalnızca Allah olduğuna iman edinceye kadar bizimle sizin aranızda düşmanlık ve kınama devam edecektir. Rabb'imiz! Yalnız Sana dayandık, yalnız Sana yöneldik. Ve dönüş yalnızca Sana'dır.\"", transliteration: "Kad kanet lekum usvetun hasenetun fi ibrahime vellezine meah, iz kalu li kavmihim inna bureau minkum ve mimma ta'budune min dunillahi keferna bikum, ve bedee beynena ve beynekumul adavetu vel bagdau ebeden hatta tu'minu billahi vahdehu, illa kavle ibrahime li ebihi le estagfirenne leke ve ma emliku leke minallahi min şey'İn, rabbena aleyke tevekkelna ve ileyke enebna ve ileykel masir."},
  {id: 5155, sure_no: 5, ayet_no: 5, sure_adi: "Mümtehine", text: "رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِلَّذ۪ينَ كَفَرُوا وَاغْفِرْ لَنَا رَبَّنَاۚ اِنَّكَ اَنْتَ الْعَز۪يزُ الْحَك۪يمُ", translation: "\"Rabb'imiz! Bizi Kafirler için sınav konusu yapma. Bizi bağışla Rabb'imiz! Kuşkusuz Sen, Mutlak Üstün Olan'sın, En Doğru Karar Veren'sin.\"", transliteration: "Rabbena la tec'alna fitneten lillezine keferu, vagfir lena rabbena, inneke entel azizul hakim."},
  {id: 5156, sure_no: 6, ayet_no: 6, sure_adi: "Mümtehine", text: "لَقَدْ  كَانَ لَـكُمْ ف۪يهِمْ اُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُوا اللّٰهَ وَالْيَوْمَ الْاٰخِرَۜ وَمَنْ يَتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَم۪يدُ۟", translation: "Ant olsun ki, onlar, sizin için ve ahiret gününü bekleyen kimseler için iyi bir örnektirler. Kim yüz çevirirse, bilsin ki kuşkusuz Allah; Hiçbir Şeye Muhtaç Olmayan'dır, Övgüye Değer Yegane Varlık'tır.", transliteration: "Lekad kane lekum fihim usvetun hasenetun li men kane yercullahe vel yevmel ahire ve men yetevelle fe innallahe huvel ganiyyul hamid."},
  {id: 5157, sure_no: 7, ayet_no: 7, sure_adi: "Mümtehine", text: "عَسَى اللّٰهُ اَنْ يَجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذ۪ينَ عَادَيْتُمْ مِنْهُمْ مَوَدَّةًۜ وَاللّٰهُ قَد۪يرٌۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "Belki Allah, gün gelir sizinle, onlardan düşmanlık beslediğiniz kimseler arasında bir sevgi oluşturur. Allah; Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Asallahu en yec'ale beynekum ve beynellezine adeytum minhum meveddeh, vallahu kadir, vallahu gafurun rahim."},
  {id: 5158, sure_no: 8, ayet_no: 8, sure_adi: "Mümtehine", text: "لَا يَنْهٰيكُمُ اللّٰهُ عَنِ الَّذ۪ينَ لَمْ يُقَاتِلُوكُمْ فِي الدّ۪ينِ وَلَمْ يُخْرِجُوكُمْ مِنْ دِيَارِكُمْ اَنْ تَـبَرُّوهُمْ وَتُقْسِطُٓوا اِلَيْهِمْۜ  اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِط۪ينَ", translation: "Allah; sizi, din konusunda sizinle savaşmayan ve sizi yurdunuzdan çıkarmayan kimselere iyilik yapmaktan, onlara karşı hak neyse onu yapmaktan alıkoymaz. Kuşkusuz ki Allah, haktan ve haklıdan yana olanları sever.", transliteration: "La yenhakumullahu anillezine lem yukatilukum fid dini ve lem yuhricukum min diyarikum en teberruhum ve tuksitu ileyhim, innallahe yuhıbbul muksitin."},
  {id: 5159, sure_no: 9, ayet_no: 9, sure_adi: "Mümtehine", text: "اِنَّمَا يَنْهٰيكُمُ اللّٰهُ عَنِ الَّذ۪ينَ قَاتَلُوكُمْ فِي الدّ۪ينِ وَاَخْرَجُوكُمْ مِنْ دِيَارِكُمْ وَظَاهَرُوا عَلٰٓى اِخْرَاجِكُمْ اَنْ تَوَلَّوْهُمْۚ وَمَنْ يَتَوَلَّهُمْ فَاُو۬لٰٓئِكَ هُمُ الظَّالِمُونَ", translation: "Ancak Allah, din konusunda sizinle savaşan, sizi yurtlarınızdan çıkaran ve çıkarılmanız için yardımlaşan kimselerle yakınlık kurmanızı yasaklamıştır. Kim onlarla yakınlık kurarsa, onlar zalimlerin ta kendileridir.", transliteration: "İnnema yenhakumullahu anillezine katelukum fid dini ve ahrecukum min diyarikum ve zaheru ala ıhracikum en tevellevhum, ve men yetevellehum fe ulaike humuz zalimun."},
  {id: 5160, sure_no: 10, ayet_no: 10, sure_adi: "Mümtehine", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا جَٓاءَكُمُ الْمُؤْمِنَاتُ مُهَاجِرَاتٍ فَامْتَحِنُوهُنَّۜ  اَللّٰهُ اَعْلَمُ بِا۪يمَانِهِنَّۚ  فَاِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَاتٍ فَلَا تَرْجِعُوهُنَّ اِلَى الْكُفَّارِۜ  لَا هُنَّ حِلٌّ لَهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّۜ وَاٰتُوهُمْ مَٓا اَنْفَقُواۜ وَلَا جُنَاحَ عَلَيْكُمْ اَنْ تَنْكِحُوهُنَّ اِذَٓا اٰتَيْتُمُوهُنَّ اُجُورَهُنَّۜ وَلَا تُمْسِكُوا بِعِصَمِ الْكَوَافِرِ وَسْـَٔلُوا مَٓا اَنْفَقْتُمْ وَلْيَسْـَٔلُوا مَٓا اَنْفَقُواۜ  ذٰلِكُمْ حُكْمُ اللّٰهِۜ  يَحْكُمُ بَيْنَكُمْۜ  وَاللّٰهُ عَل۪يمٌ حَك۪يمٌ", translation: "Ey İman Edenler! Sığınmak üzere size gelen Mü'min kadınların hicret nedenlerini sorgulayın. Allah, onların gerçekten iman edip etmediklerini çok iyi bilmektedir. Eğer gerçekten Mü'min olduklarını anlarsanız, onları Kafirlere geri göndermeyin. Onlar, Kafir erkeklere helal değildir. Kafir erkekler de onlara helal değildir. Kafirlerin bu kadınlara verdiklerini iade edin. Mehirlerini verdiğiniz takdirde onlarla nikahlanmanızda bir sakınca yoktur. Ve Kafir kadınları nikahınızda tutmayın. Ve ne infak ettiyseniz geri isteyin. Onlar da infak ettiklerini geri istesinler. İşte bu Allah'ın aranızda verdiği hükmüdür. Allah; Her Şeyi Bilen'dir, En Doğru Karar Veren'dir.", transliteration: "Ya eyyuhellezine amenu iza caekumul mu'minatu muhaciratin femtehınu hunn, allahu a'lemu bi imanihinn, fe in alimtimu hunne mu'minatin fe la terciu hunne ilel kuffar, la hunne hıllun lehum ve la hum yehıllune le hunn, ve atuhum ma enfeku, ve la cunaha aleykum en tenkıhu hunne iza ateytumu hunne ucurehunn, ve la tumsiku bi isamil kevafiri ves'elu ma enfaktum vel yes'elu ma enfeku, zalikum hukmullah, yahkumu beynekum, vallahu alimun hakim."},
  {id: 5161, sure_no: 11, ayet_no: 11, sure_adi: "Mümtehine", text: "وَاِنْ فَاتَكُمْ شَيْءٌ مِنْ اَزْوَاجِكُمْ اِلَى الْـكُفَّارِ فَعَاقَبْتُمْ فَاٰتُوا الَّذ۪ينَ ذَهَبَتْ اَزْوَاجُهُمْ مِثْلَ مَٓا اَنْفَقُواۜ وَاتَّقُوا اللّٰهَ الَّـذ۪ٓي اَنْتُمْ بِه۪ مُؤْمِنُونَ", translation: "Eğer eşlerinizden dolayı, bir şeyiniz Kafirlere geçtiyse, sonra da ödeme sırası size geldiğinde, o zaman eşleri gitmiş olanlara infak ettikleri kadar verin. Ve siz, kendisine iman ettiğiniz Allah'a karşı takva sahibi olun.", transliteration: "Ve in fatekum şey'un min ezvacikum ilel kuffari fe akabtum fe atullezine zehebet ezvacuhum misle ma enfeku, vettekullahellezi entum bihi mu'minun."},
  {id: 5162, sure_no: 12, ayet_no: 12, sure_adi: "Mümtehine", text: "يَٓا اَيُّهَا النَّبِيُّ اِذَا جَٓاءَكَ الْمُؤْمِنَاتُ يُبَايِعْنَكَ عَلٰٓى اَنْ لَا يُشْرِكْنَ بِاللّٰهِ شَيْـٔاً وَلَا يَسْرِقْنَ وَلَا يَزْن۪ينَ وَلَا يَقْتُلْنَ اَوْلَادَهُنَّ وَلَا يَأْت۪ينَ بِبُهْتَانٍ يَفْتَر۪ينَهُ بَيْنَ اَيْد۪يهِنَّ وَاَرْجُلِهِنَّ وَلَا يَعْص۪ينَكَ ف۪ي مَعْرُوفٍ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللّٰهَۜ اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Ey Nebi! İman Eden kadınlar; Allah'a hiçbir şeyi ortak koşmamak, hırsızlık yapmamak, zina yapmamak, çocuklarını öldürmemek, elleri ve ayakları arasında bir iftira uydurup getirmemek, maruf olan konularda sana asilik yapmamak koşuluyla sana bi'at ederlerse onların bi'atlarını kabul et ve onlar için Allah'tan bağışlanma dile. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyyuhen nebiyyu iza caekel mu'minatu yubayi'neke ala en la yuşrikne billahi şey'en ve la yesrikne ve la yeznine ve la yaktulne evladehunne ve la ye'tine bi buhtanin yefterinehu beyne eydihinne ve erculihinne ve la ya'sineke fi ma'rufin fe bayı'hunne vestagfirlehunnallah innallahe gafurun rahim."},
  {id: 5163, sure_no: 13, ayet_no: 13, sure_adi: "Mümtehine", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تَتَوَلَّوْا قَوْماً غَضِبَ اللّٰهُ عَلَيْهِمْ قَدْ يَـئِسُوا مِنَ الْاٰخِرَةِ كَمَا يَـئِسَ الْكُفَّارُ مِنْ اَصْحَابِ الْقُبُورِ", translation: "Ey İman Edenler! Allah'ın gazap ettiği halka dönmeyin. Kafirlerin mezarlık halkından ümitlerini kestikleri gibi onlar da ahiretten ümitlerini kesmişlerdir.", transliteration: "Ya eyyuhellezine amenu la tetevellev kavmen gadıballahu aleyhim kad yeisu minel ahireti kema yeisel kuffaru min ashabil kubur."},
  {id: 5164, sure_no: 1, ayet_no: 1, sure_adi: "Saff", text: "سَبَّحَ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۚ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Göklerde ve yerde olanlar, Allah'ı tesbih ederler. O, Mutlak Üstün Olan'dır ve En Doğru Hüküm Veren'dir.", transliteration: "Sebbeha lillahi ma fis semavati ve ma fil ard, ve huvel azizul hakim."},
  {id: 5165, sure_no: 2, ayet_no: 2, sure_adi: "Saff", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ", translation: "Ey İman Edenler! Yapmayacağınız şeyi niçin söylüyorsunuz?", transliteration: "Ya eyyuhellezine amenu lime tekulune ma la tef'alun."},
  {id: 5166, sure_no: 3, ayet_no: 3, sure_adi: "Saff", text: "كَبُرَ مَقْتاً عِنْدَ اللّٰهِ اَنْ تَقُولُوا مَا لَا تَفْعَلُونَ", translation: "Yapmayacağınız şeyleri söylemeniz, Allah'ın yanında en nefret edilen şeydir.", transliteration: "Kebure makten indallahi en tekulu ma la tef'alun."},
  {id: 5167, sure_no: 4, ayet_no: 4, sure_adi: "Saff", text: "اِنَّ اللّٰهَ يُحِبُّ الَّذ۪ينَ يُقَاتِلُونَ ف۪ي سَب۪يلِه۪ صَفاًّ كَاَنَّهُمْ بُنْيَانٌ مَرْصُوصٌ", translation: "Allah, kendi yolunda bir duvarın taşları gibi kenetlenip saf tutarak savaşanları sever.", transliteration: "İnnallahe yuhıbbullezine yukatilune fi sebilihi saffen ke ennehum bunyanun mersus."},
  {id: 5168, sure_no: 5, ayet_no: 5, sure_adi: "Saff", text: "وَاِذْ قَالَ مُوسٰى لِقَوْمِه۪ يَا قَوْمِ لِمَ تُؤْذُونَن۪ي وَقَدْ تَعْلَمُونَ اَنّ۪ي رَسُولُ اللّٰهِ اِلَيْكُمْۜ فَلَمَّا زَاغُٓوا اَزَاغَ اللّٰهُ قُلُوبَهُمْۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ", translation: "Hani Musa, halkına: \"Ey halkım! Benim, size Allah tarafından gönderilen bir Resul olduğumu bildiğiniz halde, niçin bana eziyet ediyorsunuz?\" demişti. Ne zaman ki onlar eğrildiler, Allah da onların kalplerini eğriltti. Allah, fasıklar topluluğuna doğru yolu göstermez.", transliteration: "Ve iz kale musa li kavmihi ya kavmi lime tu'zuneni ve kad ta'lemune enni resulullahi ileykum, fe lemma zagu ezagallahu kulubehum, vallahu la yehdil kavmel fasikin."},
  {id: 5169, sure_no: 6, ayet_no: 6, sure_adi: "Saff", text: "وَاِذْ قَالَ ع۪يسَى ابْنُ مَرْيَمَ يَا بَن۪ٓي اِسْرَٓائ۪لَ اِنّ۪ي رَسُولُ اللّٰهِ اِلَيْكُمْ مُصَدِّقاً لِمَا بَـيْنَ يَدَيَّ مِنَ التَّوْرٰيةِ وَمُبَشِّراً بِرَسُولٍ يَأْت۪ي مِنْ بَعْدِي اسْمُهُٓ اَحْمَدُۜ فَلَمَّا جَٓاءَهُمْ بِالْبَـيِّنَاتِ قَالُوا هٰذَا سِحْرٌ مُب۪ينٌ", translation: "Hani Meryem Oğlu İsa: \"Ey İsrailoğulları! Ben, elinizdeki Tevrat'ı doğrulayan ve benden sonra gelecek olan, adı ahmed olan bir Resulü müjdeleyen Allah'ın Resul'üyüm.\" demişti. Fakat onlara beyyineler getirince, onlar: \"Bu, apaçık bir büyüdür.\" dediler.", transliteration: "Ve iz kale isebnu meryeme ya beni israile inni resulullahi ileykum musaddikan li ma beyne yedeyye minet tevrati ve mubeşşiren bi resulin ye'ti min bagdismuhu ahmed, fe lemma caehum bil beyyinati kalu haza sihrun mubin."},
  {id: 5170, sure_no: 7, ayet_no: 7, sure_adi: "Saff", text: "وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ الْـكَذِبَ وَهُوَ يُدْعٰٓى اِلَى الْاِسْلَامِۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ", translation: "İslam'a çağrıldığı halde, uydurduğu yalanları Allah'a isnat ederek, iftira edenden daha zalim kim olabilir? Allah, zalim topluma doğru yolu göstermez.", transliteration: "Ve men azlemu mimmeniftera alallahil kezibe ve huve yud'a ilel islam, vallahu la yehdil kavmez zalimin."},
  {id: 5171, sure_no: 8, ayet_no: 8, sure_adi: "Saff", text: "يُر۪يدُونَ لِيُطْفِؤُ۫ا نُورَ اللّٰهِ بِاَفْوَاهِهِمْ وَاللّٰهُ مُتِمُّ نُورِه۪ وَلَوْ كَرِهَ الْـكَافِرُونَ", translation: "Onlar, ağızları ile Allah'ın nurunu söndürmek istiyorlar. Ancak, Kafirler'in hoşuna gitmese de Allah, nurunu tamamlayacaktır.", transliteration: "Yuridune li utfiu nurallahi bi efvahihim vallahu mutimmu nurihi ve lev kerihel kafirun."},
  {id: 5172, sure_no: 9, ayet_no: 9, sure_adi: "Saff", text: "هُوَ الَّـذ۪ٓي اَرْسَلَ رَسُولَهُ بِالْهُدٰى وَد۪ينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدّ۪ينِ كُلِّه۪ وَلَوْ كَرِهَ الْمُشْرِكُونَ۟", translation: "Müşriklerin hoşuna gitmese de Resul'ünü, dinin tamamını bildirmek üzere doğru yolu gösteren rehberle ve Hakk Din ile gönderen O'dur.", transliteration: "Huvellezi ersele resulehu bil huda ve dinil hakkı li yuzhirehu aled dini kullihi ve lev kerihel muşriku."},
  {id: 5173, sure_no: 10, ayet_no: 10, sure_adi: "Saff", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا هَلْ اَدُلُّكُمْ عَلٰى تِجَارَةٍ تُنْج۪يكُمْ مِنْ عَذَابٍ اَل۪يمٍ", translation: "Ey İman Edenler! Size, can yakıcı bir azaptan kurtaracak, kazançlı bir ticaretin yolunu göstereyim mi?", transliteration: "Ya eyyuhellezine amenu hel edullukum ala ticaretin tuncikum min azabin elim."},
  {id: 5174, sure_no: 11, ayet_no: 11, sure_adi: "Saff", text: "تُـؤْمِنُونَ بِاللّٰهِ وَرَسُولِه۪ وَتُجَاهِدُونَ ف۪ي سَب۪يلِ اللّٰهِ بِاَمْوَالِكُمْ وَاَنْفُسِكُمْۜ ذٰلِكُمْ خَيْرٌ لَـكُمْ اِنْ كُنْتُمْ تَعْلَمُونَۙ", translation: "Bu, Allah'a ve Resul'üne iman etmeniz; Allah yolunda, mallarınızla ve canlarınızla cihad etmenizdir. İşte bu, eğer bilirseniz sizin için hayırlı olandır.", transliteration: "Tu'minune billahi ve resulihi ve tucahidune fi sebilillahi bi emvalikum ve enfusikum, zalikum hayrun lekum in kuntum ta'lemun."},
  {id: 5175, sure_no: 12, ayet_no: 12, sure_adi: "Saff", text: "يَغْفِرْ لَـكُمْ ذُنُوبَكُمْ وَيُدْخِلْـكُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ وَمَسَا‌كِنَ طَيِّبَةً ف۪ي جَنَّاتِ عَدْنٍۜ  ذٰلِكَ الْفَوْزُ الْعَظ۪يمُۙ", translation: "Sizin suçlarınızı bağışlar ve sizi içinden nehirler akan Cennetlere koyar. Ve sizi, Adn Cennetler'inde, hoş meskenlere yerleştirir. İşte bu, büyük kurtuluştur.", transliteration: "Yagfir lekum zunubekum ve yudhılkum cennatin tecri min tahtihel enharu ve mesakine tayyibeten fi cennati adn, zalikel fevzul azim."},
  {id: 5176, sure_no: 13, ayet_no: 13, sure_adi: "Saff", text: "وَاُخْرٰى تُحِبُّونَهَاۜ نَصْرٌ مِنَ اللّٰهِ وَفَتْحٌ قَر۪يبٌۜ وَبَشِّرِ الْمُؤْمِن۪ينَ", translation: "Ayrıca seveceğiniz bir şey daha: Allah'tan yardım ve yakın bir fetih. Mü'minleri müjdele.", transliteration: "Ve uhra tuhıbbuneha, nasrun minallahi ve fethun karib, ve beşşiril mu'minin."},
  {id: 5177, sure_no: 14, ayet_no: 14, sure_adi: "Saff", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا كُونُٓوا اَنْصَارَ اللّٰهِ كَمَا قَالَ ع۪يسَى ابْنُ مَرْيَمَ لِلْحَوَارِيّ۪نَ مَنْ اَنْصَار۪ٓي اِلَى اللّٰهِۜ  قَالَ الْحَوَارِيُّونَ نَحْنُ اَنْصَارُ اللّٰهِ فَاٰمَنَتْ طَٓائِفَةٌ مِنْ بَن۪ٓي اِسْرَٓائ۪لَ وَكَـفَرَتْ طَٓائِفَةٌۚ فَاَيَّدْنَا الَّذ۪ينَ اٰمَنُوا عَلٰى عَدُوِّهِمْ فَاَصْبَحُوا ظَاهِر۪ينَ", translation: "Ey İman Edenler! Allah'ın yardımcıları olun! Meryem Oğlu İsa'nın havarilere: \"Allah yolunda benim yardımcılarım kimdir?\" dediği zaman, havarilerin: \"Allah yolunun yardımcıları biziz.\" dedikleri gibi. Sonuçta, İsrailoğulları'nın bir kısmı iman etti, bir kısmı da inanmadı. Biz de iman Edenler'i, düşmanlarına karşı destekledik. Böylece, onlar üstün geldiler.", transliteration: "Ya eyyuhellezine amenu kunu ensarallahi kema kale isebnu meryeme lil havariyyine men ensari ilallah, kalel havariyune nahnu ensarullah, fe amenet taifetun min beni israile ve keferet taifeh, fe eyyednellezine amenu ala aduvvihim fe asbehu zahirin."},
  {id: 5178, sure_no: 1, ayet_no: 1, sure_adi: "Cuma", text: "يُسَبِّـحُ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِ الْمَلِكِ الْقُدُّوسِ الْعَز۪يزِ الْحَك۪يمِ", translation: "Göklerde ve yerde bulunanların tamamı, Melik, Kuddus, Mutlak Üstün Olan, En İyi Hüküm Veren Allah'ı tesbih etmektedir.", transliteration: "Yusebbihu lillahi ma fis semavati ve ma fil ardıl melikil kuddusil azizil hakim."},
  {id: 5179, sure_no: 2, ayet_no: 2, sure_adi: "Cuma", text: "هُوَ الَّذ۪ي بَعَثَ فِي الْاُمِّيّ۪نَ رَسُولاً مِنْهُمْ يَتْلُوا عَلَيْهِمْ اٰيَاتِه۪ وَيُزَكّ۪يهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَۗ وَاِنْ كَانُوا مِنْ قَبْلُ لَف۪ي ضَلَالٍ مُب۪ينٍۙ", translation: "Ümmilere, kendilerinden olan; O'nun ayetlerini okuyan, onları arındıran, onlara Kitap'ı ve Hikmet'i öğreten bir Resul görevlendiren O'dur. Onlar, bundan önce apaçık bir sapkınlık içindeydiler.", transliteration: "Huvellezi bease fil ummiyyine resulen minhum yetlu aleyhim ayatihi ve yuzekkihim ve yuallimuhumul kitabe vel hikmeh, ve in kanu min kablu le fi dalalin mubin."},
  {id: 5180, sure_no: 3, ayet_no: 3, sure_adi: "Cuma", text: "وَاٰخَر۪ينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْۜ وَهُوَ الْعَز۪يزُ الْحَك۪يمُ", translation: "Onlardan, henüz kendilerine katılmamış olan diğerlerine de. O, Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Ve aharine minhum lemma yelhaku bi him, ve huvel azizul hakim."},
  {id: 5181, sure_no: 4, ayet_no: 4, sure_adi: "Cuma", text: "ذٰلِكَ فَضْلُ اللّٰهِ يُؤْت۪يهِ مَنْ يَشَٓاءُۜ وَاللّٰهُ ذُوالْفَضْلِ الْعَظ۪يمِ", translation: "İşte bu, Allah'ın hak eden kimselere verdiği lütfudur. Allah, Büyük Lütuf Sahibi'dir.", transliteration: "Zalike fadlullahi yu'tihi men yeşau, vallahu zul fadlil azim."},
  {id: 5182, sure_no: 5, ayet_no: 5, sure_adi: "Cuma", text: "مَثَلُ الَّذ۪ينَ حُمِّلُوا التَّوْرٰيةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ اَسْفَاراًۜ بِئْسَ مَثَلُ الْقَوْمِ الَّذ۪ينَ كَذَّبُوا بِاٰيَاتِ اللّٰهِۜ وَاللّٰهُ لَا يَهْدِي الْقَوْمَ الظَّالِم۪ينَ", translation: "Tevrat'a uymakla yükümlü kılınıp da yükümlülüğünü yerine getirmeyen kimselerin durumu, ciltlerle kitap taşıyan eşeklerin durumu gibidir. Allah'ın ayetlerini yalanlayan toplumun düştüğü durum ne kötüdür. Allah, zalim toplumu doğru yola iletmez.", transliteration: "Meselullezine hummilut tevrate summe lem yahmiluha ke meselil hımari yahmilu esfara, bi'se meselul kavmillezine kezzebu bi ayatillah, vallahu la yehdil kavmez zalimin."},
  {id: 5183, sure_no: 6, ayet_no: 6, sure_adi: "Cuma", text: "قُلْ يَٓا اَيُّهَا الَّذ۪ينَ هَادُٓوا اِنْ زَعَمْتُمْ اَنَّكُمْ اَوْلِيَٓاءُ لِلّٰهِ مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "De ki: \"Ey Yahudiler! Eğer diğer insanların değil de yalnızca kendinizin Allah'ın velileri olduğunuza inanıyorsanız ve bu iddianızda samimi iseniz, haydi hemen ölümü isteyin.\"", transliteration: "Kul ya eyyuhellezine hadu in zeamtum ennekum evliyau lillahi min dunin nasi fe temennevul mevte in kuntum sadikin."},
  {id: 5184, sure_no: 7, ayet_no: 7, sure_adi: "Cuma", text: "وَلَا يَتَمَنَّوْنَهُٓ اَبَداً بِمَا قَدَّمَتْ اَيْد۪يهِمْۜ وَاللّٰهُ عَل۪يمٌ بِالظَّالِم۪ينَ", translation: "Yaptıkları şeyler nedeniyle, hiçbir zaman ölümü temenni etmezler. Allah, haksızlık edenleri en iyi bilendir.", transliteration: "Ve la yetemennevnehu ebeden bi ma kaddemet eydihim, vallahu alimun biz zalimin."},
  {id: 5185, sure_no: 8, ayet_no: 8, sure_adi: "Cuma", text: "قُلْ اِنَّ الْمَوْتَ الَّذ۪ي تَفِرُّونَ مِنْهُ فَاِنَّهُ مُلَاق۪يكُمْ ثُمَّ تُرَدُّونَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ۟", translation: "De ki: Kendisinden kaçtığınız ölüm, kesinlikle karşınıza çıkacaktır. Sonra, görünmeyen ve görünen her şeyi bilene döndürüleceksiniz. O, size yaptıklarınızı haber verecektir.", transliteration: "Kul innel mevtellezi tefirrune minhu fe innehu mulakikum summe tureddune ila alimil gaybi veş şehadeti fe yunebbiukum bi ma kuntum ta'melun."},
  {id: 5186, sure_no: 9, ayet_no: 9, sure_adi: "Cuma", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِذَا نُودِيَ لِلصَّلٰوةِ مِنْ يَوْمِ الْجُمُعَةِ فَاسْعَوْا اِلٰى ذِكْرِ اللّٰهِ وَذَرُوا الْبَيْعَۜ ذٰلِكُمْ خَيْرٌ لَكُمْ اِنْ كُنْتُمْ تَعْلَمُونَ", translation: "Ey İman Edenler! Cuma günü salat için seslenildiği zaman, alışverişi bırakıp, hemen Allah'ın öğüdüne koşun. Eğer bilirseniz, bu, sizin için hayırlı olandır.", transliteration: "Ya eyyuhellezine amenu iza nudiye lis salati min yevmil cumuati fes'av ila zikrillahi ve zerul bey'a, zalikum hayrun lekum in kuntum ta'lemun."},
  {id: 5187, sure_no: 10, ayet_no: 10, sure_adi: "Cuma", text: "فَاِذَا قُضِيَتِ الصَّلٰوةُ فَانْتَشِرُوا فِي الْاَرْضِ وَابْتَغُوا مِنْ فَضْلِ اللّٰهِ وَاذْكُرُوا اللّٰهَ كَث۪يراً لَعَلَّكُمْ تُفْلِحُونَ", translation: "Salatı kaza ettikten sonra, hemen yeryüzüne dağılın ve Allah'ın lütfundan nasibinizi arayın. Allah'ın öğütlerini hiç unutmayın. Umulur ki, böylece kurtuluşa erersiniz.", transliteration: "Fe iza kudiyetıs salatu fenteşiru fil ardı vebtegu min fadlillahi vezkurullahe kesiren leallekum tuflihun."},
  {id: 5188, sure_no: 11, ayet_no: 11, sure_adi: "Cuma", text: "وَاِذَا رَاَوْا تِجَارَةً اَوْ لَهْواًۨ انْفَضُّٓوا اِلَيْهَا وَتَرَكُوكَ قَٓائِماًۜ قُلْ مَا عِنْدَ اللّٰهِ خَيْرٌ مِنَ اللَّهْوِ وَمِنَ التِّجَارَةِۜ وَاللّٰهُ خَيْرُ الرَّازِق۪ينَ", translation: "Bir kısım insanlar, ticaret ve eğlence görünce, seni bırakarak ona yönelip gittiler. De ki: \"Allah'ın katında olanlar, eğlenceden ve ticaretten daha hayırlıdır ve Allah, rızık verenlerin en hayırlısıdır.\"", transliteration: "Ve iza reev ticareten ev lehveninfaddu ileyha ve terekuke kaima, kul ma indallahi hayrun minel lehvi ve minet ticareh, vallahu hayrur razıkin."},
  {id: 5189, sure_no: 1, ayet_no: 1, sure_adi: "Münafikun", text: "اِذَا جَٓاءَكَ الْمُنَافِقُونَ قَالُوا نَشْهَدُ اِنَّكَ لَرَسُولُ اللّٰهِۢ وَاللّٰهُ يَعْلَمُ اِنَّكَ لَرَسُولُهُۜ وَاللّٰهُ يَشْهَدُ اِنَّ الْمُنَافِق۪ينَ لَـكَاذِبُونَۚ", translation: "Münafıklar sana geldiklerinde: \"Biz, tanıklık ederiz ki, kuşkusuz sen, Allah'ın Resul'üsün.\" dediler. Elbette ki Allah, senin, Kendisinin Resul'ü olduğunu biliyor. Fakat Allah tanıktır ki, Münafıklar, kesinlikle yalancıdırlar.", transliteration: "İza caekel munafikune kalu neşhedu inneke le resulullah, vallahu ya'lemu inneke le resuluh, vallahu yeşhedu innel munafikine le kazibun."},
  {id: 5190, sure_no: 2, ayet_no: 2, sure_adi: "Münafikun", text: "اِتَّخَذُٓوا اَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَب۪يلِ اللّٰهِۜ اِنَّهُمْ سَٓاءَ مَا كَانُوا يَعْمَلُونَ", translation: "Onlar, yeminlerini kalkan yapıyorlar. Böylece insanları, Allah'ın yolundan saptırıyorlar. Kuşkusuz, yaptıkları şey çok kötüdür.", transliteration: "İttehazu eymanehum cunneten fe saddu an sebilillah, innehum sae ma kanu ya'melun."},
  {id: 5191, sure_no: 3, ayet_no: 3, sure_adi: "Münafikun", text: "ذٰلِكَ بِاَنَّهُمْ اٰمَنُوا ثُمَّ كَفَرُوا فَطُبِعَ عَلٰى قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ", translation: "Bu şundandır: onlar önce iman ettiler sonra küfrettiler. Bu nedenle, onların kalpleri mühürlenmiştir. Artık onlar, gerçeği kavrayamazlar.", transliteration: "Zalike bi ennehum amenu summe keferu fe tubia ala kulubihim fe hum la yefkahun."},
  {id: 5192, sure_no: 4, ayet_no: 4, sure_adi: "Münafikun", text: "وَاِذَا رَاَيْتَهُمْ تُعْجِبُكَ اَجْسَامُهُمْۜ وَاِنْ يَقُولُوا تَسْمَعْ لِقَوْلِهِمْۜ   كَاَنَّهُمْ خُشُبٌ مُسَنَّدَةٌۜ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْۜ هُمُ الْعَدُوُّ فَاحْذَرْهُمْۜ  قَاتَلَهُمُ اللّٰهُۘ  اَنّٰى يُؤْفَـكُونَ", translation: "Onlara baktığında, dış görünüşleri itibariyle, seni hayran bırakırlar. Konuştuklarında, sözlerine itibar edersin. Aslında, elbise giydirilmiş kütükler gibidirler. Her bağırtıyı kendi aleyhlerine sanırlar. Onlar, düşmandırlar, onlara karşı dikkatli ol. Allah, onların canlarını alsın! Nasıl da döndürülüyorlar!", transliteration: "Ve iza reeytehum tu'cibuke ecsamuhum, ve in yekulu tesma', li kavlihim, ke ennehum huşubun musennedeh, yahsebune kulle sayhatin aleyhim, humul aduvvu fahzerhum, katelehumullahu enna yu'fekun."},
  {id: 5193, sure_no: 5, ayet_no: 5, sure_adi: "Münafikun", text: "وَاِذَا ق۪يلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُولُ اللّٰهِ لَـوَّوْا رُؤُ۫سَهُمْ وَرَاَيْتَهُمْ يَصُدُّونَ وَهُمْ مُسْتَكْبِرُونَ", translation: "Ve onlara, \"Gelin, Allah'ın Resul'ü sizin için bağışlanma dilesin.\" dendiği zaman; tersleyerek, büyüklük taslayıp, çekip gittiklerini görürsün.", transliteration: "Ve iza kile lehum tealev yestagfir lekum resulullahi levvev ruusehum ve reeytehum yesuddune ve hum mustekbirun."},
  {id: 5194, sure_no: 6, ayet_no: 6, sure_adi: "Münafikun", text: "سَوَٓاءٌ عَلَيْهِمْ اَسْتَغْفَرْتَ لَهُمْ اَمْ لَمْ تَسْتَغْفِرْ لَهُمْۜ لَنْ يَغْفِرَ اللّٰهُ لَهُمْۜ اِنَّ اللّٰهَ لَا يَهْدِي الْقَوْمَ الْفَاسِق۪ينَ", translation: "Zaten onlar için bağışlanma dilesen de dilemesen de fark etmez. Allah, onları asla bağışlamayacaktır. Çünkü Allah, böyle fasık halkı asla doğru yola iletmez.", transliteration: "Sevaun aleyhim estagferte le hum em lem testagfir lehum, len yagfirallahu lehum, innallahe la yehdil kavmel fasikin."},
  {id: 5195, sure_no: 7, ayet_no: 7, sure_adi: "Münafikun", text: "هُمُ الَّذ۪ينَ يَقُولُونَ لَا تُنْفِقُوا عَلٰى مَنْ عِنْدَ رَسُولِ اللّٰهِ حَتّٰى يَنْفَضُّواۜ وَلِلّٰهِ خَزَٓائِنُ السَّمٰوَاتِ وَالْاَرْضِ وَلٰكِنَّ الْمُنَافِق۪ينَ لَا يَفْقَهُونَ", translation: "Onlar, Allah'ın Resul'ünün yanında bulunanlara, \"Yardım etmeyin ki, dağılıp gitsinler.\" diyenlerdir. Oysa göklerin ve yeryüzünün bütün hazineleri Allah'ındır. Ancak, münafıklar, bu gerçeği kavrayamıyorlar.", transliteration: "Humullezine yekulune la tunfiku ala men inde resulillahi hatta yenfaddu, ve lillahi hazainus semavati vel ardı ve lakinnel munafikine la yefkahun."},
  {id: 5196, sure_no: 8, ayet_no: 8, sure_adi: "Münafikun", text: "يَقُولُونَ لَئِنْ رَجَعْنَٓا اِلَى الْمَد۪ينَةِ لَيُخْرِجَنَّ الْاَعَزُّ مِنْهَا الْاَذَلَّۜ وَلِلّٰهِ الْعِزَّةُ وَلِرَسُولِه۪ وَلِلْمُؤْمِن۪ينَ وَلٰكِنَّ الْمُنَافِق۪ينَ لَا يَعْلَمُونَ۟", translation: "\"Eğer Medine'ye dönecek olursak, mutlaka daha güçlü olan, güçsüz olanı, oradan sürüp çıkarır.\" diyorlar. Oysa izzet; Allah'ın, O'nun Resul'ünün ve inananlarındır. Fakat münafıklar bu gerçeği bilmiyorlar.", transliteration: "Yekulune le in reca'na ilel medineti le yuhricennel eazzu min hel ezell, ve lillahil izzetu ve li resulihi ve lil mu'minine ve lakinnel munafikine la ya'lemun."},
  {id: 5197, sure_no: 9, ayet_no: 9, sure_adi: "Münafikun", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا لَا تُلْهِكُمْ اَمْوَالُكُمْ وَلَٓا اَوْلَادُكُمْ عَنْ ذِكْرِ اللّٰهِۚ وَمَنْ يَفْعَلْ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْخَاسِرُونَ", translation: "Ey İman Edenler! Mallarınız ve çocuklarınız, sizi, Allah'ın öğütlerini dinlemekten alıkoymasın. Kimler bunu yaparsa, bilsinler ki asıl kaybedenler onlardır.", transliteration: "Ya eyyuhellezine amenu la tulhikum emvalukum ve la evladukum an zikrillah, ve men yef'al zalike fe ulaike humul hasirun."},
  {id: 5198, sure_no: 10, ayet_no: 10, sure_adi: "Münafikun", text: "وَاَنْفِقُوا مِمَّا رَزَقْنَاكُمْ مِنْ قَبْلِ اَنْ يَأْتِيَ اَحَدَكُمُ الْمَوْتُ فَيَقُولَ رَبِّ لَوْلَٓا اَخَّرْتَـن۪ٓي اِلٰٓى اَجَلٍ قَر۪يبٍۙ فَاَصَّدَّقَ وَاَكُنْ مِنَ الصَّالِح۪ينَ", translation: "Herhangi birinize ölüm gelip çatıp da \"Rabb'im! Ölümümü yakın bir zamana kadar ertelesen de böylece ben de sadaka versem ve iyilerden olsam.\" demezden önce, size rızık olarak verdiklerimizden karşılıksız yardımda bulunun.", transliteration: "Ve enfiku mimma rezaknakum min kabli en ye'tiye ehadekumul mevtu fe yekule rabbi lev la ahharteni ila ecelin karibin fe assaddeka ve ekun mines salihin."},
  {id: 5199, sure_no: 11, ayet_no: 11, sure_adi: "Münafikun", text: "وَلَنْ يُؤَخِّرَ اللّٰهُ نَفْساً اِذَا جَٓاءَ اَجَلُهَاۜ وَاللّٰهُ خَب۪يرٌ بِمَا تَعْمَلُونَ", translation: "Allah, süresinin sonu gelmiş hiç kimseyi asla ertelemez. Allah, yaptığınız her şeyden haberdardır.", transliteration: "Ve len yuahhırallahu nefsen iza cae eceluha, vallahu habirun bi ma ta'melun."},
  {id: 5200, sure_no: 1, ayet_no: 1, sure_adi: "Tegabun", text: "يُسَبِّحُ لِلّٰهِ مَا فِي السَّمٰوَاتِ وَمَا فِي الْاَرْضِۚ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُۘ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Göklerde ve yeryüzünde olan her şey Allah'ı tesbih eder. Mülk yalnızca O'nundur. Hamd O'nadır. O'nun gücü her şeye yeter.", transliteration: "Yusebbihu lillahi ma fis semavati ve ma fil ard, le hul mulku ve le hul hamdu ve huve ala kulli şey'in kadir."},
  {id: 5201, sure_no: 2, ayet_no: 2, sure_adi: "Tegabun", text: "هُوَ الَّذ۪ي خَلَقَكُمْ فَمِنْكُمْ  كَافِرٌ وَمِنْكُمْ مُؤْمِنٌۜ وَاللّٰهُ بِمَا تَعْمَلُونَ بَص۪يرٌ", translation: "Sizi yaratan O'dur. Böyleyken; iman edeniniz var, kafir olanınız var. Allah, yaptıklarınızı görmektedir.", transliteration: "Huvellezi halakakum fe minkum kafiru ve minkum mu'min, vallahu bima ta'melune basir."},
  {id: 5202, sure_no: 3, ayet_no: 3, sure_adi: "Tegabun", text: "خَلَقَ السَّمٰوَاتِ وَالْاَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْۚ وَاِلَيْهِ الْمَص۪يرُ", translation: "Gökleri ve yeri Hakk ile yarattı. Size, en iyi biçimle biçim verdi. Ve dönüş yalnızca O'nadır.", transliteration: "Halakas semavati vel arda bil hakkı ve savverekum fe ahsene suverekum ve ileyhil masir."},
  {id: 5203, sure_no: 4, ayet_no: 4, sure_adi: "Tegabun", text: "يَعْلَمُ مَا فِي السَّمٰوَاتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَۜ وَاللّٰهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Göklerde ve yerde olanları bilir. Gizlediklerinizi de açığa vurduğunuzu da bilir. Allah, göğüslerin özünü en iyi bilendir.", transliteration: "Ya'lemu ma fis semavati vel ardı ve ya'lemu ma tusirrune ve ma tu'linun, vallahu alimun bi zatis sudur."},
  {id: 5204, sure_no: 5, ayet_no: 5, sure_adi: "Tegabun", text: "اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذ۪ينَ كَـفَرُوا مِنْ قَبْلُۘ فَذَاقُوا وَبَالَ اَمْرِهِمْ وَلَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Kafirlerin yaptıkları şeylerin bedelini nasıl ödediklerinin haberi size gelmedi mi? Onlar için acı bir azap vardır.", transliteration: "E lem ye'tikum nebeullezine keferu min kablu fe zaku ve bale emrihim ve lehum azabun elim."},
  {id: 5205, sure_no: 6, ayet_no: 6, sure_adi: "Tegabun", text: "ذٰلِكَ بِاَنَّهُ كَانَتْ تَأْت۪يهِمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَقَالُٓوا اَبَشَرٌ يَهْدُونَنَاۘ  فَكَفَرُوا وَتَوَلَّوْا وَاسْتَغْنَى اللّٰهُۜ  وَاللّٰهُ غَنِيٌّ حَم۪يدٌ", translation: "İşte bu, Resulleri kendilerine açık kanıt içeren bilgilerle geldiklerinde: \"Bir beşer mi bizi doğru yola iletecek?\" diyerek, kabul etmeyerek yüz çevirmelerindendir. Allah'ın onların iman etmelerine ihtiyacı yoktur. Allah, Hiçbir Şeye Muhtaç Olmayan'dır, Övgüye Değer Yegane Varlık'tır.", transliteration: "Zalike bi ennehu kanet te'tihim rusuluhum bil beyyinati fe kalu e beşerun yehdunena fe keferu ve tevellev vestagnallah, vallahu ganiyyun hamid."},
  {id: 5206, sure_no: 7, ayet_no: 7, sure_adi: "Tegabun", text: "زَعَمَ الَّذ۪ينَ كَفَرُٓوا اَنْ لَنْ يُبْعَثُواۜ  قُلْ بَلٰى وَرَبّ۪ي لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْۜ وَذٰلِكَ عَلَى اللّٰهِ يَس۪يرٌ", translation: "Kafirler kesinlikle tekrar diriltilmeyeceklerini iddia ettiler. De ki: \"Rabb'ime ant olsun ki kesinlikle diriltileceksiniz. Sonra yaptıklarınızın tamamı size haber verilecektir. Ve bu, Allah için çok kolaydır.", transliteration: "Zeamellezine keferu en len yub'asu, kul bela ve rabbi le tub'asunne summe le tunebbeunne bima amiltum, ve zalike alallahi yesir."},
  {id: 5207, sure_no: 8, ayet_no: 8, sure_adi: "Tegabun", text: "فَاٰمِنُوا بِاللّٰهِ وَرَسُولِه۪ وَالنُّورِ الَّـذ۪ٓي اَنْزَلْنَاۜ  وَاللّٰهُ بِمَا تَعْمَلُونَ خَب۪يرٌ", translation: "O halde, Allah'a, Resul'üne ve indirdiğimiz nura inanın. Allah, yaptıklarınızdan haberdardır.", transliteration: "Fe amınu billahi ve resulihi ven nurillezi enzelna, vallahu bima ta'melune habir."},
  {id: 5208, sure_no: 9, ayet_no: 9, sure_adi: "Tegabun", text: "يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ذٰلِكَ يَوْمُ التَّغَابُنِۜ وَمَنْ يُؤْمِنْ بِاللّٰهِ وَيَعْمَلْ صَالِحاً يُكَفِّرْ عَنْهُ سَيِّـَٔاتِه۪ وَيُدْخِلْهُ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ ذٰلِكَ الْفَوْزُ الْعَظ۪يمُ", translation: "Toplanma günü için sizi bir araya toplayacağı gün, işte O Gün aldanma günüdür. Kim Allah'a iman eder ve salihatı yaparsa, Allah onun kötülüklerini örter ve onu içinde kesintisiz olarak, sürekli kalacağı, içinden nehirler akan Cennetlere koyar. İşte bu, büyük kurtuluştur.", transliteration: "Yevme yecmeukum li yevmil cem'i zalike yevmut tegabun, ve men yu'min billahi ve ya'mel salihan yukeffir anhu seyyiatihi ve yudhılhu cennatin tecri min tahtihel enharu halidine fiha ebeda, zalikel fevzul azim."},
  {id: 5209, sure_no: 10, ayet_no: 10, sure_adi: "Tegabun", text: "وَالَّذ۪ينَ كَفَرُوا وَكَذَّبُوا بِاٰيَاتِنَٓا اُو۬لٰٓئِكَ اَصْحَابُ النَّارِ خَالِد۪ينَ ف۪يهَاۜ وَبِئْسَ الْمَص۪يرُ۟", translation: "Kafirlik eden ve ayetlerimizi yalanlayanlar; işte onlar, ateş halkıdırlar ve orada sürekli kalacak olanlardır. O, ne kötü bir varış yeridir.", transliteration: "Vellezine keferu ve kezzebu bi ayatina ulaike ashabun nari halidine fiha ve bi'sel masir."},
  {id: 5210, sure_no: 11, ayet_no: 11, sure_adi: "Tegabun", text: "مَٓا اَصَابَ مِنْ مُص۪يبَةٍ اِلَّا بِاِذْنِ اللّٰهِۜ وَمَنْ يُؤْمِنْ بِاللّٰهِ يَهْدِ قَلْبَهُۜ وَاللّٰهُ بِكُلِّ شَيْءٍ عَل۪يمٌ", translation: "Allah'ın izni dışında hiçbir musibet isabet etmez. Kim Allah'a iman ederse, Allah, onun kalbini doğruya yöneltir. Allah, Her Şeyi En İyi Bilen'dir.", transliteration: "Ma esabe min musibetin illa bi iznillah, ve men yu'min billahi yehdi kalbeh, vallahu bikulli şey'in alim."},
  {id: 5211, sure_no: 12, ayet_no: 12, sure_adi: "Tegabun", text: "وَاَط۪يعُوا اللّٰهَ وَاَط۪يعُوا الرَّسُولَۚ فَاِنْ تَوَلَّيْتُمْ فَاِنَّمَا عَلٰى رَسُولِنَا الْبَلَاغُ الْمُب۪ينُ", translation: "Allah'a itaat edin, Resul'e itaat edin. Eğer yüz çevirirseniz, bilin ki Resul'ümüze düşen yalnızca açıkça tebliğ etmektir.", transliteration: "Ve etiullahe ve etiur resul, fe in tevelleytum fe innema ala resulinel belagul mubin."},
  {id: 5212, sure_no: 13, ayet_no: 13, sure_adi: "Tegabun", text: "اَللّٰهُ لَٓا اِلٰهَ اِلَّا هُوَۜ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ", translation: "Allah! Kendisinden başka ilah olmayandır. İman Edenler, yalnızca Allah'a tevekkül etsinler.", transliteration: "Allahu la ilahe illa huve, ve alallahi fel yetevekkelil mu'minun."},
  {id: 5213, sure_no: 14, ayet_no: 14, sure_adi: "Tegabun", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُٓوا اِنَّ مِنْ اَزْوَاجِكُمْ وَاَوْلَادِكُمْ عَدُواًّ لَكُمْ فَاحْذَرُوهُمْۚ وَاِنْ تَعْفُوا وَتَصْفَحُوا وَتَغْفِرُوا فَاِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Ey İman Edenler: Doğrusu, eşlerinizden ve çocuklarınızdan size düşmanlık edenler olabilir. Onlara karşı dikkatli olun. Eğer affeder, kusurlarını görmezden gelirseniz ve bağışlarsanız, bilin ki kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyhuhellezine amenu inne min ezvacikum ve evladikum aduvven lekum fahzeruhum, ve in ta'fu ve tasfehu ve tagfiru fe innallahe gafurun rahim."},
  {id: 5214, sure_no: 15, ayet_no: 15, sure_adi: "Tegabun", text: "اِنَّـمَٓا اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌۜ وَاللّٰهُ عِنْدَهُٓ اَجْرٌ عَظ۪يمٌ", translation: "Mallarınız ve çocuklarınız yalnızca bir fitnedir. Büyük ödül, Allah'ın yanındadır.", transliteration: "İnnema emvalukum ve evladukum fitneh, vallahu indehu ecrun azim."},
  {id: 5215, sure_no: 16, ayet_no: 16, sure_adi: "Tegabun", text: "فَاتَّقُوا اللّٰهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوا وَاَط۪يعُوا وَاَنْفِقُوا خَيْراً لِاَنْفُسِكُمْۜ وَمَنْ يُوقَ شُحَّ نَفْسِه۪ فَاُو۬لٰٓئِكَ هُمُ الْمُفْلِحُونَ", translation: "Gücünüz yettiğince Allah'a karşı takva sahibi olun. Dinleyin ve itaat edin. Kendi iyiliğiniz için infak edin. Kim benliğinin cimriliğinden korunursa, işte onlar kurtuluşa erenlerdir.", transliteration: "Fettekullahe mesteta'tum vesmeu ve etiu ve enfiku hayren li enfusikum, ve men yuka şuhha nefsihi fe ulaike humul muflihun."},
  {id: 5216, sure_no: 17, ayet_no: 17, sure_adi: "Tegabun", text: "اِنْ تُقْرِضُوا اللّٰهَ قَرْضاً حَسَناً يُضَاعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْۜ وَاللّٰهُ شَكُورٌ حَل۪يمٌۙ", translation: "Eğer Allah'a güzel bir ödünç verirseniz, onu size kat kat fazlasıyla geri öder ve sizi bağışlar. Allah; Çok Şükreden'dir, Çok Hoşgörülü'dür.", transliteration: "İn tukridullahe kardan hasenen yudaıfhu lekum ve yagfir lekum, vallahu şekurun halim."},
  {id: 5217, sure_no: 18, ayet_no: 18, sure_adi: "Tegabun", text: "عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَز۪يزُ الْحَك۪يمُ", translation: "Görünmeyen ve görünen her şeyi bilendir. Mutlak Üstün Olan'dır, En İyi Hüküm Veren'dir.", transliteration: "Alimul gaybi veş şehadetil azizul hakim."},
  {id: 5218, sure_no: 1, ayet_no: 1, sure_adi: "Talak", text: "يَٓا اَيُّهَا النَّبِيُّ اِذَا طَلَّقْتُمُ النِّسَٓاءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَاَحْصُوا الْعِدَّةَۚ وَاتَّقُوا اللّٰهَ رَبَّكُمْۚ لَا تُخْرِجُوهُنَّ مِنْ بُيُوتِهِنَّ وَلَا يَخْرُجْنَ اِلَّٓا اَنْ يَأْت۪ينَ بِفَاحِشَةٍ مُبَيِّنَةٍۜ وَتِلْكَ حُدُودُ اللّٰهِۜ وَمَنْ يَتَعَدَّ حُدُودَ اللّٰهِ فَقَدْ ظَلَمَ نَفْسَهُۜ  لَا تَدْر۪ي لَعَلَّ اللّٰهَ يُحْدِثُ بَعْدَ ذٰلِكَ اَمْراً", translation: "Ey Nebi! Kadınları boşayacağınız zaman, iddet sürelerini gözeterek boşayın ve iddetlerini sayın. Rabb'iniz Allah'a karşı takva sahibi olun. Açıkça fahşa yapmadıkça onları evlerinden çıkarmayın; onlar da çıkmasınlar. Bunlar, Allah'ın sınırlarıdır. Kim Allah'ın sınırlarını aşarsa, kesinlikle kendisine haksızlık etmiş olur. Bilemezsin, olur ki Allah bundan sonra yeni bir hadis ortaya çıkarır.", transliteration: "Ya eyyuhen nebiyyu iza tallaktumun nisae fe tallikuhunne li iddetihinne ve ahsul iddeh, vettekullahe rabbekum, la tuhricuhunne min buyutihinne ve la yahrucne illa en ye'tine bi fahişetin mubeyyineh, ve tilke hududullah, ve men yeteadde hududallahi fe kad zaleme nefseh, la tedri leallallahe yuhdısu ba'de zalike emra."},
  {id: 5219, sure_no: 2, ayet_no: 2, sure_adi: "Talak", text: "فَاِذَا بَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوهُنَّ بِمَعْرُوفٍ اَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَاَشْهِدُوا ذَوَيْ عَدْلٍ مِنْكُمْ وَاَق۪يمُوا الشَّهَادَةَ لِلّٰهِۜ  ذٰلِكُمْ يُوعَظُ بِه۪ مَنْ كَانَ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۜ وَمَنْ يَتَّقِ اللّٰهَ يَجْعَلْ لَهُ مَخْرَجاًۙ", translation: "Bekleme süreleri tamamlandığında onları ma'rufa uygun olarak tutun veya ma'rufa uygun olarak onlardan ayrılın. Ve sizden adalet sahibi iki kişi tanık olsun. Tanıklığı, Allah için yerine getirin. İşte Allah'a ve ahiret gününe iman edenlere verilen öğüt budur. Kim Allah için takva sahibi olursa, Allah, ona bir çıkış yolu nasip eder.", transliteration: "Fe iza belagne ecelehunne fe emsikuhunne bi ma'rufin evfarikuhunne bi ma'rufin ve eşhidu zevey adlin minkum ve ekimuş şehadete lillah, zalikum yuazu bihi men kane yu'minu billahi vel yevmil ahir, ve men yettekıllahe yec'al lehu mahreca."},
  {id: 5220, sure_no: 3, ayet_no: 3, sure_adi: "Talak", text: "وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُۜ وَمَنْ يَتَوَكَّلْ عَلَى اللّٰهِ فَهُوَ حَسْبُهُۜ اِنَّ اللّٰهَ بَالِغُ اَمْرِه۪ۜ قَدْ جَعَلَ اللّٰهُ لِكُلِّ شَيْءٍ قَدْراً", translation: "Ve Allah, ona ummadığı yerden rızık verir. Kim Allah'a tevekkül ederse, O, ona yeter. Kuşkusuz Allah, buyruğunu yerine getirir. Allah, her şey için bir ölçü belirlemiştir.", transliteration: "Ve yerzukhu min haysu la yahtesib, ve men yetevekkel alallahi fe huve hasbuh, innallahe baligu emrih, kad cealallahu li kulli şey'in kadra."},
  {id: 5221, sure_no: 4, ayet_no: 4, sure_adi: "Talak", text: "وَالّٰٓئ۪ يَـئِسْنَ مِنَ الْمَح۪يضِ مِنْ نِسَٓائِكُمْ اِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلٰثَةُ اَشْهُرٍۙ وَالّٰٓئ۪ لَمْ يَحِضْنَۜ  وَاُو۬لَاتُ الْاَحْمَالِ اَجَلُهُنَّ اَنْ يَضَعْنَ حَمْلَهُنَّۜ  وَمَنْ يَتَّقِ اللّٰهَ يَجْعَلْ لَهُ مِنْ اَمْرِه۪ يُسْراً", translation: "Kadınlarınızdan adet görmekten umudunu kesenler hakkında kuşku duyarsanız, o zaman onların iddetleri üç aydır. Ve adet görmeyenlerin de. Hamile kadınların bekleme süresi ise doğum yapıncaya kadardır. Kim Allah için takva sahibi olursa, Allah ona işinde kolaylık sağlar.", transliteration: "Vellai yeisne minel mahidı min nisaikum inirtebtum fe iddetuhunne selasetu eşhurin vellai lem yahıdn, ve ulatul ahmali eceluhunne en yada'ne hamlehunn, ve men yettekıllahe yec'al lehu min emrihi yusra."},
  {id: 5222, sure_no: 5, ayet_no: 5, sure_adi: "Talak", text: "ذٰلِكَ اَمْرُ اللّٰهِ اَنْزَلَهُٓ اِلَيْكُمْۜ وَمَنْ يَتَّقِ اللّٰهَ يُكَفِّرْ عَنْهُ سَيِّـَٔاتِه۪ وَيُعْظِمْ لَـهُٓ اَجْراً", translation: "İşte bu, Allah'ın size indirdiği buyruğudur. Kim Allah için takva sahibi olursa, onun kötülüklerine kafir olur ve ona yüce, üstün bir ödül verir.", transliteration: "Zalike emrullahi enzelehu ileykum, ve men yettekıllahe yukeffir anhu seyyiatihi ve yu'zım lehu ecra."},
  {id: 5223, sure_no: 6, ayet_no: 6, sure_adi: "Talak", text: "اَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِنْ وُجْدِكُمْ وَلَا تُضَٓارُّوهُنَّ لِتُضَيِّقُوا عَلَيْهِنَّۜ وَاِنْ كُنَّ اُو۬لَاتِ حَمْلٍ فَاَنْفِقُوا عَلَيْهِنَّ حَتّٰى يَضَعْنَ حَمْلَهُنَّۚ فَاِنْ اَرْضَعْنَ لَكُمْ فَاٰتُوهُنَّ اُجُورَهُنَّۚ وَأْتَمِرُوا بَيْنَكُمْ بِمَعْرُوفٍۚ وَاِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُٓ  اُخْرٰىۜ", translation: "Oturduğunuz yerin bir bölümünde gücünüz yettiğince onları oturtun. Onları sıkıntıya sokmak için, zarar verecek bir şey yapmayın. Eğer hamile iseler, doğum yapıncaya kadar onlara karşılıksız yardımda bulunun. Sonra emzirirlerse ücretlerini verin. Aranızda konuşarak en iyi ve uygun olacak bir şekilde anlaşın. Eğer bir zorlukla karşılaşırsanız o zaman emzirme işini başkasına yaptırın.", transliteration: "Eskinu hunne min haysu sekentum min vucdikum ve la tudarruhunne li tudayyiku aleyhinn, ve in kunne ulati hamlin fe enfiku aleyhinne hatta yeda'ne hamle hunn, fe in erda'ne lekum fe atuhunne ucure hunn, ve'temiru beynekum bi ma'ruf, ve in teasertum fe se turdıu lehu uhra."},
  {id: 5224, sure_no: 7, ayet_no: 7, sure_adi: "Talak", text: "لِيُنْفِقْ ذُوسَعَةٍ مِنْ سَعَتِه۪ۜ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهُ فَلْيُنْفِقْ مِمَّٓا اٰتٰيهُ اللّٰهُۜ  لَا يُكَلِّفُ اللّٰهُ نَفْساً اِلَّا مَٓا اٰتٰيهَاۜ سَيَجْعَلُ اللّٰهُ بَعْدَ عُسْرٍ يُسْراً۟", translation: "Varlık sahibi olanlar, varlıklarına göre karşılıksız yardım etsinler. Durumu müsait olmayan da Allah ne verdiyse ondan versin. Allah, bir kimseyi, kendisine verdiğinden fazlasıyla sorumlu tutmaz. Allah, zorluğun ardından bir kolaylık verecektir.", transliteration: "Li yunfık zu seatin min seatih, ve men kudire aleyhi rızkuhu fel yunfik mimma atahullah, la yukellifullahu nefsen illa ma ataha, seyec'alullahu ba'de usrin yusra."},
  {id: 5225, sure_no: 8, ayet_no: 8, sure_adi: "Talak", text: "وَكَاَيِّنْ مِنْ قَرْيَةٍ عَتَتْ عَنْ اَمْرِ رَبِّهَا وَرُسُلِه۪ فَحَاسَبْنَاهَا حِسَاباً شَد۪يداً وَعَذَّبْنَاهَا عَذَاباً نُكْراً", translation: "Nice kasaba halkı, Rabb'lerinin buyruğuna ve O'nun Resullerine başkaldırdılar da Biz de onları şiddetli bir hesaba çektik ve onları korkunç bir azapla cezalandırdık.", transliteration: "Ve keeyyin min karyetin atet an emri rabbiha ve rusulihi fe hasebnaha hisaben şediden ve azzebnaha azaben nukra."},
  {id: 5226, sure_no: 9, ayet_no: 9, sure_adi: "Talak", text: "فَذَاقَتْ وَبَالَ اَمْرِهَا وَكَانَ عَاقِبَةُ اَمْرِهَا خُسْراً", translation: "Böylece işinin vebalini tattı. İşinin sonu da hüsran oldu.", transliteration: "Fe zakat ve bale emriha ve kane akıbetu emriha husra."},
  {id: 5227, sure_no: 10, ayet_no: 10, sure_adi: "Talak", text: "اَعَدَّ اللّٰهُ لَهُمْ عَذَاباً شَد۪يداً فَاتَّقُوا اللّٰهَ يَٓا اُو۬لِي الْاَلْبَابِۚۛ اَلَّذ۪ينَ اٰمَنُواۚۛ قَدْ اَنْزَلَ اللّٰهُ اِلَيْكُمْ ذِكْراًۙ", translation: "Allah, onlara şiddetli bir azap hazırladı. Ey İman Eden sağlıklı düşünen akıl sahipleri, Allah için takva sahibi olun. Allah, size zikri indirmiştir.", transliteration: "E addallahu lehum azaben şediden fettekullahe ya ulil elbab, ellezine amenu, kad enzelallahu ileykum zikra."},
  {id: 5228, sure_no: 11, ayet_no: 11, sure_adi: "Talak", text: "رَسُولاً يَتْلُوا عَلَيْكُمْ اٰيَاتِ اللّٰهِ مُبَيِّنَاتٍ لِيُخْرِجَ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنَ الظُّلُمَاتِ اِلَى النُّورِۜ  وَمَنْ يُؤْمِنْ بِاللّٰهِ وَيَعْمَلْ صَالِحاً يُدْخِلْهُ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ قَدْ اَحْسَنَ اللّٰهُ لَهُ رِزْقاً", translation: "İman eden ve salihatı1 yapan kimseleri, karanlıklardan aydınlığa çıkarmak için, Allah'ın apaçık olan ayetlerini size okuyan bir Resul gönderdi. Kim, Allah'a iman eder ve salih olanı yaparsa, Allah onu, içinden ırmaklar akan ve içinde kesintisiz olarak sürekli kalacakları Cennetlere koyar. Allah, onu en iyi şekilde rızıklandıracaktır.", transliteration: "Resulen yetlu aleykum ayatillahi mubeyyinatin li yuhricellezine amenu ve amilus salihati minez zulumati ilen nur, ve men yu'min billahi ve ya'mel salihan yudhilhu cennatin tecri min tahtihel enharu halidine fiha ebeda, kad ahsenallahu lehu rızka."},
  {id: 5229, sure_no: 12, ayet_no: 12, sure_adi: "Talak", text: "اَللّٰهُ الَّذ۪ي خَلَقَ سَبْعَ سَمٰوَاتٍ وَمِنَ الْاَرْضِ مِثْلَهُنَّۜ يَتَنَزَّلُ الْاَمْرُ بَيْنَهُنَّ لِتَعْلَمُٓوا اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌۙ وَاَنَّ اللّٰهَ قَدْ اَحَاطَ بِكُلِّ شَيْءٍ عِلْماً", translation: "Allah, yedi göğü ve benzer şekilde yeryüzünü yaratandır. Allah'ın her şeye gücünün yettiğini, Allah'ın bilgisinin her şeyi kuşattığını bilesiniz diye, buyruğu onlar arasında iner durur.", transliteration: "Allahullezi halaka seb'a semavatin ve minel ardı mislehunn, yetenezzelul emru beynehunne li ta'lemu ennallahe ala kulli şey'in kadirun ve ennallahe kad ehata bi kulli şey'in ilma."},
  {id: 5230, sure_no: 1, ayet_no: 1, sure_adi: "Tahrim", text: "يَٓا اَيُّهَا النَّبِيُّ  لِمَ تُحَرِّمُ مَٓا اَحَلَّ اللّٰهُ لَكَۚ تَبْتَغ۪ي مَرْضَاتَ اَزْوَاجِكَۜ وَاللّٰهُ غَفُورٌ رَح۪يمٌ", translation: "Ey Nebi! Eşlerinin hoşnutluğunu gözeterek, Allah'ın helal kıldığı şeyi niçin kendine haram kılıyorsun? Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "Ya eyyuhen nebiyyu lime tuharrimu ma ehallallahu lek, tebtegi merdate ezvacik, vallahu gafurun rahim."},
  {id: 5231, sure_no: 2, ayet_no: 2, sure_adi: "Tahrim", text: "قَدْ فَرَضَ اللّٰهُ لَـكُمْ تَحِلَّةَ اَيْمَانِكُمْۚ وَاللّٰهُ مَوْلٰيكُمْۚ وَهُوَ الْعَل۪يمُ الْحَك۪يمُ", translation: "Allah, yeminlerinizi, kefaretini ödeyerek çözmeyi size farz kılmıştır. Allah, sizin mevlanızdır. O; Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Kad faradallahu lekum tehillete eymanikum, vallahu mevlakum, ve huvel alimul hakim."},
  {id: 5232, sure_no: 3, ayet_no: 3, sure_adi: "Tahrim", text: "وَاِذْ اَسَرَّ النَّبِيُّ اِلٰى بَعْضِ اَزْوَاجِه۪ حَد۪يثاًۚ فَلَمَّا نَبَّاَتْ بِه۪ وَاَظْهَرَهُ اللّٰهُ عَلَيْهِ عَرَّفَ بَعْضَهُ وَاَعْرَضَ عَنْ بَعْضٍۚ فَلَمَّا نَبَّاَهَا بِه۪ قَالَتْ مَنْ اَنْبَاَكَ هٰذَاۜ  قَالَ نَبَّاَنِيَ الْعَل۪يمُ الْخَب۪يرُ", translation: "Hani Nebi, eşlerinden birine aralarında kalmak üzere bir hadis söylemişti. Fakat o eşi, söylenen şeyi başkasına söyleyince, Allah Nebi'ye bunu izhar etti. Nebi de bir kısmını açıklamış ve bir kısmından da vazgeçmişti. Eşine, bundan haberdar olduğunu söyleyince, eşi: \"Bunu sana kim haber verdi?\" deyince, Nebi: \"Bana, Her Şeyi Bilen, Her Şeyden Haberdar Olan bildirdi.\" demişti.", transliteration: "Ve iz eserren nebiyyu ila ba'dı ezvacihi hadisa, fe lemma nebbeet bihi ve azherehullahu aleyhi arrefe ba'dahu ve a'rada an ba'd, fe lemma nebbeeha bihi kalet men enbeeke haza, kale nebbeeniyel alimul habir."},
  {id: 5233, sure_no: 4, ayet_no: 4, sure_adi: "Tahrim", text: "اِنْ تَتُوبَٓا اِلَى اللّٰهِ فَقَدْ صَغَتْ قُلُوبُكُمَاۚ وَاِنْ تَظَاهَرَا عَلَيْهِ فَاِنَّ اللّٰهَ هُوَ مَوْلٰيهُ وَجِبْر۪يلُ وَصَالِـحُ الْمُؤْمِن۪ينَۚ وَالْمَلٰٓئِكَةُ بَعْدَ ذٰلِكَ ظَه۪يرٌ", translation: "Siz ikiniz Allah'a tevbe etmelisiniz. Çünkü ikinizin de kalbi yanlış düşüncelere yöneldi. Eğer ona karşı dayanışmaya girerseniz hiç kuşkusuz Allah ona mevladır. Cibril ve iman edenlerin salihleri ve bunlardan başka melekler de onun yanındadırlar.", transliteration: "İn tetuba ilallahi fe kad sagat kulubukuma, ve in tezahera aleyhi fe innallahe huve mevlahu ve cibrilu ve salihul mu'minin, vel melaiketu ba'de zalike zahir."},
  {id: 5234, sure_no: 5, ayet_no: 5, sure_adi: "Tahrim", text: "عَسٰى رَبُّهُٓ اِنْ طَلَّقَكُنَّ اَنْ يُبْدِلَهُٓ  اَزْوَاجاً خَيْراً مِنْكُنَّ مُسْلِمَاتٍ مُؤْمِنَاتٍ قَانِتَاتٍ تَٓائِبَاتٍ عَابِدَاتٍ سَٓائِحَاتٍ ثَيِّبَاتٍ وَاَبْكَاراً", translation: "Eğer o sizi boşarsa, Rabb'inin ona sizden daha hayırlı olan; Allah'a teslim olan, iman eden, saygıda kusur etmeyen, tevbe eden, kulluk eden, seyahat eden dul ve bakire eşler vermesi umulur.", transliteration: "Asa rabbuhu in tallakakunne en yubdilehu ezvacen hayren min kunne muslimatin mu'minatin kanitatin taibatin abidatin saihatin seyyibatin ve ebkara."},
  {id: 5235, sure_no: 6, ayet_no: 6, sure_adi: "Tahrim", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا قُٓوا اَنْفُسَكُمْ وَاَهْل۪يكُمْ نَاراً وَقُودُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلٰٓئِكَةٌ غِلَاظٌ شِدَادٌ لَا يَعْصُونَ اللّٰهَ مَٓا اَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ", translation: "Ey İman Edenler! Kendinizi ve yakınlarınızı, yakıtı taşlar ve insanlar olan ateşten koruyun. Onun üzerinde, Allah'ın buyruklarına karşı gelmeyen ve buyrulan şeyi yapan çok güçlü ve acımasız melekler vardır.", transliteration: "Ya eyyuhellezine amenu ku enfusekum ve ehlikum naren vakuduhan nasu vel hicaretu aleyha melaiketun gılazun şidadun la ya'sunallahe ma emerehum ve yef'alune ma yu'merune."},
  {id: 5236, sure_no: 7, ayet_no: 7, sure_adi: "Tahrim", text: "يَٓا اَيُّهَا الَّذ۪ينَ كَفَرُوا لَا تَعْتَذِرُوا الْيَوْمَۜ  اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ۟", translation: "Ey Kafirler! Bugün özür dilemeyin! Siz, yalnızca yaptıklarınızın karşılığını görmektesiniz.", transliteration: "Ya eyyuhellezine keferu la ta'tezirul yevm, innema tuczevne ma kuntum ta'melun."},
  {id: 5237, sure_no: 8, ayet_no: 8, sure_adi: "Tahrim", text: "يَٓا اَيُّهَا الَّذ۪ينَ اٰمَنُوا تُوبُٓوا اِلَى اللّٰهِ تَوْبَةً نَصُوحاًۜ عَسٰى رَبُّكُمْ اَنْ يُكَفِّرَ عَنْكُمْ سَيِّـَٔاتِكُمْ وَيُدْخِلَكُمْ جَنَّاتٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۙ يَوْمَ لَا يُخْزِي اللّٰهُ النَّبِيَّ وَالَّذ۪ينَ اٰمَنُوا مَعَهُۚ نُورُهُمْ يَسْعٰى بَيْنَ اَيْد۪يهِمْ وَبِاَيْمَانِهِمْ يَقُولُونَ رَبَّـنَٓا اَتْمِمْ لَنَا نُورَنَا وَاغْفِرْ لَنَاۚ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌ", translation: "Ey İman Edenler! Öğüt veren bir tevbe ile Allah'a tevbe edin. Umulur ki Rabb'iniz, kötülüklerinizi örter ve sizi altından nehirler akan Cennetlere koyar. O Gün Allah, Nebileri ve onlarla beraber olanları asla üzmeyecektir. Onların ışıkları önlerini ve yanlarını aydınlatır. \"Rabb'imiz, bizim ışığımızı tamamla ve bizi bağışla. Kuşkusuz Sen, Her Şeye Güç Yetiren'sin.\" derler.", transliteration: "Ya eyyuhellezine amenu tubu ilallahi tevbeten nasuha, asa rabbukum en yukeffire ankum seyyiatikum ve yudhilekum cennatin tecri min tahtihel enharu, yevme la yuhzillahun nebiyye vellezine amenu meah, nuruhum yes'a beyne eydihim ve bi eymanihim yekulune rabbena etmim lena nurena vagfir lena, inneke ala kulli şey'in kadir."},
  {id: 5238, sure_no: 9, ayet_no: 9, sure_adi: "Tahrim", text: "يَٓا اَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِق۪ينَ وَاغْلُظْ عَلَيْهِمْۜ وَمَأْوٰيهُمْ جَهَنَّمُۜ وَبِئْسَ الْمَص۪يرُ", translation: "Ey Nebi! Kafirlere ve münafıklara karşı mücadele et. Ve onlara kararlılık göster. Onların varacağı yer Cehennem'dir. O ne kötü dönüş yeridir.", transliteration: "Ya eyyuhen nebiyyu cahidil kuffare vel munafikine vagluz aleyhim, ve me'vahum cehennem, ve bi'sel masir."},
  {id: 5239, sure_no: 10, ayet_no: 10, sure_adi: "Tahrim", text: "ضَرَبَ اللّٰهُ مَثَلاً لِلَّذ۪ينَ كَفَرُوا امْرَاَتَ نُوحٍ وَامْرَاَتَ لُوطٍۜ  كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللّٰهِ شَيْـٔاً وَق۪يلَ ادْخُلَا النَّارَ مَعَ الدَّاخِل۪ينَ", translation: "Allah, Kafirlere Nuh'un ve Lut'un hanımlarını örnek verdi. İkisi de iyi kullarımızdan iki kulumuzla evliydiler. Fakat onlara hainlik ettiler. Bu yüzden Allah'tan gelen şeye karşı, kocalarının onlara bir faydası olmadı. Onlara: \"İkiniz de girenlerle birlikte ateşe girin.\" denildi.", transliteration: "Dareballahu meselen lillezine keferumreete nuhın vemreete lut, kaneta tahte abdeyni min ibadina salihayni fe hanetahuma fe lem yugniya anhuma minallahi şey'en ve kiledhulen nare mead dahılin."},
  {id: 5240, sure_no: 11, ayet_no: 11, sure_adi: "Tahrim", text: "وَضَرَبَ اللّٰهُ مَثَلاً لِلَّذ۪ينَ اٰمَنُوا امْرَاَتَ فِرْعَوْنَۢ اِذْ قَالَتْ رَبِّ ابْنِ ل۪ي عِنْدَكَ بَيْتاً فِي الْجَنَّةِ وَنَجِّن۪ي مِنْ فِرْعَوْنَ وَعَمَلِه۪ وَنَجِّن۪ي مِنَ الْقَوْمِ الظَّالِم۪ينَۙ", translation: "Allah, iman eden kimselere, Firavun'un hanımını örnek verdi. Hani o, \"Rabb'im! Katında, benim için Cennet'te bir ev yap. Beni Firavun ve yaptıklarından kurtar. Ve zalimler topluluğundan beni kurtar.\" demişti.", transliteration: "Ve dareballahu meselen lillezine amenumreete fir'avn, iz kalet rabbibni li indeke beyten fil cenneti ve neccini min fir'avne ve amelihi ve neccini minel kavmiz zalimin."},
  {id: 5241, sure_no: 12, ayet_no: 12, sure_adi: "Tahrim", text: "وَمَرْيَمَ ابْنَتَ عِمْرٰنَ الَّت۪ٓي اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا ف۪يهِ مِنْ رُوحِنَا وَصَدَّقَتْ بِكَلِمَاتِ رَبِّهَا وَكُتُبِه۪ وَكَانَتْ مِنَ الْقَانِت۪ينَ", translation: "İmran kızı Meryem; ırzını korumuştu. Ona ruhumuzdan üfledik. O, Rabb'inin kelimelerini ve kitaplarını tasdik etti ve gönülden saygı gösterenlerden oldu.", transliteration: "Ve meryemebnete ımranelleti ahsanet ferceha fe nefahna fihi min ruhına ve saddekat bi kelimati rabbiha ve kutubihi ve kanet minel kanitin."},
  {id: 5242, sure_no: 1, ayet_no: 1, sure_adi: "Mülk", text: "تَبَارَكَ الَّذ۪ي بِيَدِهِ الْمُلْكُۘ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَد۪يرٌۙ", translation: "Mutlak egemenliği elinde bulunduran Allah, ne kutludur! O, Her Şeye Gücü Yeten'dir.", transliteration: "Tebarekellezi bi yedihil mulku ve huve ala kulli şey'in kadir."},
  {id: 5243, sure_no: 2, ayet_no: 2, sure_adi: "Mülk", text: "اَلَّذ۪ي خَلَقَ الْمَوْتَ وَالْحَيٰوةَ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلاًۜ وَهُوَ الْعَز۪يزُ الْغَفُورُۙ", translation: "Hanginizin daha iyi iş yapacağını sınamak için ölümü ve hayatı yaratan O'dur. O, Mutlak Üstün Olan'dır, Çok Bağışlayıcı'dır.", transliteration: "Ellezi halakal mevte vel hayate li yebluvekum eyyukum ahsenu amela, ve huvel azi zul gafur."},
  {id: 5244, sure_no: 3, ayet_no: 3, sure_adi: "Mülk", text: "اَلَّذ۪ي خَلَقَ سَبْعَ سَمٰوَاتٍ طِبَاقاًۜ مَا تَرٰى ف۪ي خَلْقِ الرَّحْمٰنِ مِنْ تَفَاوُتٍۜ فَارْجِعِ الْبَصَرَۙ هَلْ تَرٰى مِنْ فُطُورٍ", translation: "Gökleri yedi tabaka halinde yaratan O'dur. Rahman'ın yaratmasında bir düzensizlik göremezsin. Bak bakalım, işleyiş yasalarında bir uygunsuzluk görebilecek misin?", transliteration: "Ellezi halaka seb'a semavatin tibaka, ma tera fi halkır rahmani min tefavut, ferciıl basara hel tera min futur."},
  {id: 5245, sure_no: 4, ayet_no: 4, sure_adi: "Mülk", text: "ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ اِلَيْكَ الْبَصَرُ خَاسِئاً وَهُوَ حَس۪يرٌ", translation: "Sonra, tekrar tekrar bir daha bak! Gözlerin, bitkin ve aradığını bulamamış bir halde sana geri döner.", transliteration: "Summerciıl basara kerreteyni yenkalib ileykel basaru hasien ve huve hasir."},
  {id: 5246, sure_no: 5, ayet_no: 5, sure_adi: "Mülk", text: "وَلَقَدْ زَيَّنَّا السَّمَٓاءَ الدُّنْيَا بِمَصَاب۪يحَ وَجَعَلْنَاهَا رُجُوماً لِلشَّيَاط۪ينِ وَاَعْتَدْنَا لَهُمْ عَذَابَ السَّع۪يرِ", translation: "Ant olsun ki Biz, yakın gökyüzünü kandillerle süsledik. Onları, şeytanlar için asılsız şeyler söyleme malzemesi yaptık. Onlar için ateşin azabını hazırladık.", transliteration: "Ve lekad zeyyennes semaed dunya bi mesabiha ve cealnaha rucumen liş şeyatini ve a'tedna lehum azabes sair."},
  {id: 5247, sure_no: 6, ayet_no: 6, sure_adi: "Mülk", text: "وَلِلَّذ۪ينَ كَفَرُوا بِرَبِّهِمْ عَذَابُ جَهَنَّمَۜ  وَبِئْسَ الْمَص۪يرُ", translation: "Kafirler için Cehennem azabı vardır. Ve o, ne kötü varış yeridir.", transliteration: "Ve lillezine keferu bi rabbihim azabu cehennem, ve bi'sel masir."},
  {id: 5248, sure_no: 7, ayet_no: 7, sure_adi: "Mülk", text: "اِذَٓا اُلْقُوا ف۪يهَا سَمِعُوا لَهَا شَه۪يقاً وَهِيَ تَفُورُۙ", translation: "Oraya atıldıklarında, onun kaynayan korkunç sesini duyarlar", transliteration: "İza ulku fiha semiu leha şehikan ve hiye tefur."},
  {id: 5249, sure_no: 8, ayet_no: 8, sure_adi: "Mülk", text: "تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِۜ  كُلَّمَٓا اُلْقِيَ ف۪يهَا فَوْجٌ سَاَلَهُمْ خَزَنَتُهَٓا اَلَمْ يَأْتِكُمْ نَذ۪يرٌ", translation: "Neredeyse öfkesinden çatlayacak gibi olur. Oraya her grup atıldığında, onun bekçileri onlara: \"Size uyarıcı gelmedi mi?\" diye sorar.", transliteration: "Tekadu temeyyezu minel gayz, kullema ulkıye fiha fevcun seelehum hazenetuha e lem ye'tikum nezir."},
  {id: 5250, sure_no: 9, ayet_no: 9, sure_adi: "Mülk", text: "قَالُوا بَلٰى قَدْ جَٓاءَنَا نَذ۪يرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللّٰهُ مِنْ شَيْءٍۚ اِنْ اَنْتُمْ اِلَّا ف۪ي ضَلَالٍ كَب۪يرٍ", translation: "Onlar, \"Evet, bize uyarıcı geldi, fakat biz onu yalanladık. Allah, hiçbir şey indirmemiştir, siz büyük bir sapkınlık içindesiniz.\" dedik.", transliteration: "Kalu bela kad caena nezirun fe kezzebna ve kulna ma nezzelallahu min şey'in entum illa fi dalalin kebir."},
  {id: 5251, sure_no: 10, ayet_no: 10, sure_adi: "Mülk", text: "وَقَالُوا لَوْ كُنَّا نَسْمَعُ اَوْ نَعْقِلُ مَا كُنَّا ف۪ٓي اَصْحَابِ السَّع۪يرِ", translation: "\"Eğer dinlemiş veya düşünmüş olsaydık, şimdi alevli ateşin halkı içinde olmazdık.\" dediler.", transliteration: "Ve kalu lev kunna nesmeu ev na'kılu ma kunna fi ashabis sair."},
  {id: 5252, sure_no: 11, ayet_no: 11, sure_adi: "Mülk", text: "فَاعْتَرَفُوا بِذَنْبِهِمْۚ فَسُحْقاً لِاَصْحَابِ السَّع۪يرِ", translation: "Böylece suçlarını itiraf ettiler. Rahmetten uzak olsun Cehennemlikler.", transliteration: "Fa'terefu bi zenbihim, fe suhkan li ashabis sair."},
  {id: 5253, sure_no: 12, ayet_no: 12, sure_adi: "Mülk", text: "اِنَّ الَّذ۪ينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَغْفِرَةٌ وَاَجْرٌ كَب۪يرٌ", translation: "Rabb'lerine gizlide içtenlikle saygı duyanlara ise bağışlanma ve büyük ödül vardır.", transliteration: "İnnellezine yahşevne rabbehum bil gaybi lehum magfiretun ve ecrun kebir."},
  {id: 5254, sure_no: 13, ayet_no: 13, sure_adi: "Mülk", text: "وَاَسِرُّوا قَوْلَكُمْ اَوِ اجْهَرُوا بِه۪ۜ  اِنَّهُ عَل۪يمٌ بِذَاتِ الصُّدُورِ", translation: "Sözünüzü gizleseniz de açığa vursanız da fark etmez; O, göğüslerde olanı En İyi Bilen'dir.", transliteration: "Ve esirru kavlekum evicheru bih, innehu alimun bi zatis sudur."},
  {id: 5255, sure_no: 14, ayet_no: 14, sure_adi: "Mülk", text: "اَلَا يَعْلَمُ مَنْ خَلَقَۜ وَهُوَ اللَّط۪يفُ الْخَب۪يرُ۟", translation: "Yaratan, yarattığını bilmez mi? O, Bütün Ayrıntıları Bilen'dir, Her Şeyden Haberdar'dır.", transliteration: "Ela ya'lemu men halak, ve huvel latiful habir."},
  {id: 5256, sure_no: 15, ayet_no: 15, sure_adi: "Mülk", text: "هُوَ الَّذ۪ي جَعَلَ لَكُمُ الْاَرْضَ ذَلُولاً فَامْشُوا ف۪ي مَنَاكِبِهَا وَكُلُوا مِنْ رِزْقِه۪ۜ  وَاِلَيْهِ النُّشُورُ", translation: "Yeryüzünü size boyun eğdiren O'dur. Artık onun üzerinde dilediğinizce dolaşın ve O'nun rızkından yiyin. Sonunda dönüş O'nadır.", transliteration: "Huvellezi ceale lekumul arda zelulen femşu fi menakibiha ve kulu min rızkıh, ve ileyhin nuşur."},
  {id: 5257, sure_no: 16, ayet_no: 16, sure_adi: "Mülk", text: "ءَاَمِنْتُمْ مَنْ فِي السَّمَٓاءِ اَنْ يَخْسِفَ بِكُمُ الْاَرْضَ فَاِذَا هِيَ تَمُورُۙ", translation: "Gökte Olan'ın, sizi yerin dibine batırmayacağından emin mi oldunuz? Bir de bakmışsınız yer sarsılıyor!", transliteration: "E emintum men fis semai en yahsife bikumul arda fe iza hiye temur."},
  {id: 5258, sure_no: 17, ayet_no: 17, sure_adi: "Mülk", text: "اَمْ اَمِنْتُمْ مَنْ فِي السَّمَٓاءِ اَنْ يُرْسِلَ عَلَيْكُمْ حَاصِباًۜ فَسَتَعْلَمُونَ كَيْفَ نَذ۪يرِ", translation: "Ya da gökte olanın üzerinize taş yağdırmayacağından emin misiniz? O halde, uyarımın nasıl olduğunu yakında bileceksiniz.", transliteration: "Em emintum men fis semai en yursile aleykum hasıba fe se ta'lemune keyfe nezir."},
  {id: 5259, sure_no: 18, ayet_no: 18, sure_adi: "Mülk", text: "وَلَقَدْ كَذَّبَ الَّذ۪ينَ مِنْ قَبْلِهِمْ فَكَيْفَ كَانَ نَك۪يرِ", translation: "Ant olsun ki, onlardan öncekiler de yalanladılar. Yok saymam nasılmış gördüler.", transliteration: "Ve lekad kezzebellezine min kablihim fe keyfe kane nekir."},
  {id: 5260, sure_no: 19, ayet_no: 19, sure_adi: "Mülk", text: "اَوَلَمْ يَرَوْا اِلَى الطَّيْرِ فَوْقَهُمْ صَٓافَّاتٍ وَيَقْبِضْنَۜ مَا يُمْسِكُهُنَّ اِلَّا الرَّحْمٰنُۜ اِنَّهُ بِكُلِّ شَيْءٍ بَص۪يرٌ", translation: "Gökyüzünde sıra sıra süzülerek, kanat çırparak uçanları görmüyorlar mı? Onları havada Rahman'dan başkası tutmuyor. Kuşkusuz O, Her Şeyi Gören'dir.", transliteration: "E ve lem yerev ilet tayri fevkahum saffatin ve yakbıdn, ma yumsikuhunne iller rahman, innehu bi kulli şey'in basir."},
  {id: 5261, sure_no: 20, ayet_no: 20, sure_adi: "Mülk", text: "اَمَّنْ هٰذَا الَّذ۪ي هُوَ جُنْدٌ لَكُمْ يَنْصُرُكُمْ مِنْ دُونِ الرَّحْمٰنِۜ اِنِ الْكَافِرُونَ اِلَّا ف۪ي غُرُورٍۚ", translation: "Yoksa Rahman'dan başka, size yardım edecek ordunuz mu var? Kafirler yalnızca bir aldanış içindedirler.", transliteration: "Emmen hazellezi huve cundun lekum yensurukum min dunir rahman, inil kafirune illa fi gurur."},
  {id: 5262, sure_no: 21, ayet_no: 21, sure_adi: "Mülk", text: "اَمَّنْ هٰذَا الَّذ۪ي يَرْزُقُكُمْ اِنْ اَمْسَكَ رِزْقَهُۚ بَلْ لَجُّوا ف۪ي عُتُوٍّ وَنُفُورٍ", translation: "Veya O, rızkınızı kesse, size kim rızık verebilir? Bilakis, onlar haddi aşmada ve nefrette ısrar ettiler.", transliteration: "Emmen hazellezi yerzukukum in emseke rızkah, bel leccu fi utuvvin ve nufur."},
  {id: 5263, sure_no: 22, ayet_no: 22, sure_adi: "Mülk", text: "اَفَمَنْ يَمْش۪ي مُكِباًّ عَلٰى وَجْهِه۪ٓ  اَهْدٰٓى اَمَّنْ يَمْش۪ي سَوِياًّ عَلٰى صِرَاطٍ مُسْتَق۪يمٍ", translation: "O halde, yüzüstü sürünerek giden mi, yoksa dosdoğru yolda düzgün yürüyen mi hedefine varır?", transliteration: "E fe men yemşi mukibben ala vechihi ehda emmen yemşi seviyyen ala sıratın mustekim."},
  {id: 5264, sure_no: 23, ayet_no: 23, sure_adi: "Mülk", text: "قُلْ هُوَ الَّـذ۪ٓي اَنْشَاَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٔدَةَۜ قَل۪يلاً مَا تَشْكُرُونَ", translation: "De ki: \"Sizi biçimlendiren; size işitme, görme ve idrak etme gücü veren O'dur. Ne kadar az şükrediyorsunuz?", transliteration: "Kul huvellezi enşeekum ve ceale lekumus sem'a vel ebsare vel ef'ideh, kalilen ma teşkurun."},
  {id: 5265, sure_no: 24, ayet_no: 24, sure_adi: "Mülk", text: "قُلْ هُوَ الَّذ۪ي ذَرَاَكُمْ فِي الْاَرْضِ وَاِلَيْهِ تُحْشَرُونَ", translation: "De ki: \"Sizi, yeryüzünde çoğaltıp yayan O'dur. Ve O'na toplanıp götürüleceksiniz.\"", transliteration: "Kul huvellezi zereekum fil ardı ve ileyhi tuhşerun."},
  {id: 5266, sure_no: 25, ayet_no: 25, sure_adi: "Mülk", text: "وَيَقُولُونَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صَادِق۪ينَ", translation: "\"Eğer doğru söylüyorsanız, yaptığınız bu uyarı ne zaman gerçekleşecek?\" diyorlar.", transliteration: "Ve yekulune meta hazel va'du in kuntum sadikin."},
  {id: 5267, sure_no: 26, ayet_no: 26, sure_adi: "Mülk", text: "قُلْ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِۖ وَاِنَّمَٓا اَنَا۬ نَذ۪يرٌ مُب۪ينٌ", translation: "De ki: \"Bunu yalnızca Allah bilmektedir. Ben, sadece apaçık bir uyarıcıyım.\"", transliteration: "Kul innemel ilmu indallahi ve innema ene nezirun mubin."},
  {id: 5268, sure_no: 27, ayet_no: 27, sure_adi: "Mülk", text: "فَلَمَّا رَاَوْهُ زُلْفَةً س۪ٓيـَٔتْ وُجُوهُ الَّذ۪ينَ كَفَرُوا وَق۪يلَ هٰذَا الَّذ۪ي كُنْتُمْ بِه۪ تَدَّعُونَ", translation: "Onu yakından gördükleri zaman, Kafirlerin yüzleri kötüleşti. Onlara: \"İşte bu, sizin isteyip durduğunuz şey!\" denildi.", transliteration: "Fe lemma reevhu zulfeten siet vucuhullezine keferu ve kile hazellezi kuntum bihi teddeun."},
  {id: 5269, sure_no: 28, ayet_no: 28, sure_adi: "Mülk", text: "قُلْ اَرَاَيْتُمْ اِنْ اَهْلَكَنِيَ اللّٰهُ وَمَنْ مَعِيَ اَوْ رَحِمَنَاۙ فَمَنْ يُج۪يرُ الْكَافِر۪ينَ مِنْ عَذَابٍ اَل۪يمٍ", translation: "De ki: \"Bakın, Allah beni ve benimle birlikte olanları ister yok eder ister merhamet eder; peki, Kafirleri acıklı azaptan kim koruyacak?\"", transliteration: "Kul ereeytum in ehlekeniyallahu ve men maıye ev rahımena fe men yucirul kafirine min azabin elim."},
  {id: 5270, sure_no: 29, ayet_no: 29, sure_adi: "Mülk", text: "قُلْ هُوَ الرَّحْمٰنُ اٰمَنَّا بِه۪ وَعَلَيْهِ تَوَكَّلْنَاۚ فَسَتَعْلَمُونَ مَنْ هُوَ ف۪ي ضَلَالٍ مُب۪ينٍ", translation: "De ki: \"O, Rahmeti Kesintisiz Olan'dır. O'na iman ettik ve O'na tevekkül ettik. Artık kimin apaçık bir sapkınlık içinde olduğunu yakında bileceksiniz.\"", transliteration: "Kul huver rahmanu amenna bihi ve aleyhi tevekkelna, fe se ta'lemune men huve fi dalalin mubin."},
  {id: 5271, sure_no: 30, ayet_no: 30, sure_adi: "Mülk", text: "قُلْ اَرَاَيْتُمْ اِنْ اَصْبَحَ مَٓاؤُ۬كُمْ غَوْراً فَمَنْ يَأْت۪يكُمْ بِمَٓاءٍ مَع۪ينٍ", translation: "De ki: \"Düşünsenize, şayet suyunuz yeraltına çekilse, size akarsuyu kim getirebilir?\"", transliteration: "Kul e re'eytum in asbaha maukum gavren fe men ye'tikum bi main main."},
  {id: 5272, sure_no: 1, ayet_no: 1, sure_adi: "Kalem", text: "نٓ وَالْقَلَمِ وَمَا يَسْطُرُونَۙ", translation: "Nun. Kaleme ve satır satır yazdıklarına ant olsun ki,", transliteration: "Nun vel kalemi ve ma yesturun."},
  {id: 5273, sure_no: 2, ayet_no: 2, sure_adi: "Kalem", text: "مَٓا اَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍۚ", translation: "Rabb'inin nimeti ile sen mecnun değilsin.", transliteration: "Ma ente bi ni'meti rabbike bi mecnun."},
  {id: 5274, sure_no: 3, ayet_no: 3, sure_adi: "Kalem", text: "وَاِنَّ لَكَ لَاَجْراً غَيْرَ مَمْنُونٍۚ", translation: "Senin için minnet altında bırakmayan bir ödül vardır.", transliteration: "Ve inne leke le ecren gayre memnun."},
  {id: 5275, sure_no: 4, ayet_no: 4, sure_adi: "Kalem", text: "وَاِنَّكَ لَعَلٰى خُلُقٍ عَظ۪يمٍ", translation: "Sen, kesinlikle büyük bir ahlak üzerindesin.", transliteration: "Ve inneke le ala hulukın azim."},
  {id: 5276, sure_no: 5, ayet_no: 5, sure_adi: "Kalem", text: "فَسَتُبْصِرُ وَيُبْصِرُونَۙ", translation: "Yakında göreceksin, onlar da görecekler!", transliteration: "Fe se tubsıru ve yubsırun."},
  {id: 5277, sure_no: 6, ayet_no: 6, sure_adi: "Kalem", text: "بِاَيِّكُمُ الْمَفْتُونُ", translation: "Kimmiş meftun olan.", transliteration: "Bi eyyikumul meftun."},
  {id: 5278, sure_no: 7, ayet_no: 7, sure_adi: "Kalem", text: "اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَب۪يلِه۪ۖ وَهُوَ اَعْلَمُ بِالْمُهْتَد۪ينَ", translation: "Kuşkusuz Rabb'in, kimin Kendi yolundan saptığını çok iyi bilir; doğru yolda olanları da en iyi O bilir.", transliteration: "İnne rabbeke huve a'lemu bi men dalle an sebilihi ve huve a'lemu bil muhtedin."},
  {id: 5279, sure_no: 8, ayet_no: 8, sure_adi: "Kalem", text: "فَلَا تُطِعِ الْمُكَذِّب۪ينَ", translation: "O halde yalanlayanlara boyun eğme.", transliteration: "Fe la tutııl mukezzibin."},
  {id: 5280, sure_no: 9, ayet_no: 9, sure_adi: "Kalem", text: "وَدُّوا لَوْ تُدْهِنُ فَيُدْهِنُونَ", translation: "Onlar, uzlaşmacı olmanı ve böylece de seninle uzlaşmayı isterler.", transliteration: "Veddu lev tudhinu fe yudhinun."},
  {id: 5281, sure_no: 10, ayet_no: 10, sure_adi: "Kalem", text: "وَلَا تُطِـعْ كُلَّ حَلَّافٍ مَه۪ينٍۙ", translation: "Hiç durmadan yemin eden düzenbazlara boyun eğme.", transliteration: "Ve la tutı' kulle hallafin mehin."},
  {id: 5282, sure_no: 11, ayet_no: 11, sure_adi: "Kalem", text: "هَمَّازٍ مَشَّٓاءٍ بِنَم۪يمٍۙ", translation: "Devamlı kusur arayıp laf taşıyan iftiracılara,", transliteration: "Hemmazin meşşain bi nemim."},
  {id: 5283, sure_no: 12, ayet_no: 12, sure_adi: "Kalem", text: "مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ اَث۪يمٍۙ", translation: "Sürekli iyi şeyleri engelleyip haddi aşan günahkarlara,", transliteration: "Mennaın lil hayri mu'tedin esim."},
  {id: 5284, sure_no: 13, ayet_no: 13, sure_adi: "Kalem", text: "عُتُلٍّ بَعْدَ ذٰلِكَ زَن۪يمٍۙ", translation: "Zorba, kötü karakterli kimselere;", transliteration: "Utullin ba'de zalike zenim."},
  {id: 5285, sure_no: 14, ayet_no: 14, sure_adi: "Kalem", text: "اَنْ كَانَ ذَا مَالٍ وَبَن۪ينَۜ", translation: "Mal ve oğulları var diye.", transliteration: "En kane za malin ve benin."},
  {id: 5286, sure_no: 15, ayet_no: 15, sure_adi: "Kalem", text: "اِذَا تُتْلٰى عَلَيْهِ اٰيَاتُنَا قَالَ اَسَاط۪يرُ الْاَوَّل۪ينَ", translation: "Ona ayetlerimiz okunduğu zaman: \"Bunlar evvelkilerin masalları.\" der.", transliteration: "İza tutla aleyhi ayatuna kale esatirul evvelin."},
  {id: 5287, sure_no: 16, ayet_no: 16, sure_adi: "Kalem", text: "سَنَسِمُهُ عَلَى الْخُرْطُومِ", translation: "Yakında hortumunun üzerine damga basacağız.", transliteration: "Se nesimuhu alel hurtum."},
  {id: 5288, sure_no: 17, ayet_no: 17, sure_adi: "Kalem", text: "اِنَّا بَلَوْنَاهُمْ  كَمَا بَلَوْنَٓا اَصْحَابَ الْجَنَّةِۚ  اِذْ اَقْسَمُوا لَيَصْرِمُنَّهَا مُصْبِح۪ينَۙ", translation: "Kuşkusuz Biz onları belalandırdık. Tıpkı, bahçelerinin ürünlerini sabah erkenden toplayacaklarına dair sözleşen bahçe sahiplerini belalandırdığımız gibi.", transliteration: "İnna belevnahum ke ma belevna ashabel cenneh, iz aksemule yasri munneha musbihin."},
  {id: 5289, sure_no: 18, ayet_no: 18, sure_adi: "Kalem", text: "وَلَا يَسْتَثْنُونَ", translation: "Bir istisna da yapmıyorlardı.", transliteration: "Ve la yestesnun."},
  {id: 5290, sure_no: 19, ayet_no: 19, sure_adi: "Kalem", text: "فَطَافَ عَلَيْهَا طَٓائِفٌ مِنْ رَبِّكَ وَهُمْ نَٓائِمُونَ", translation: "Fakat onlar daha uyanmadan, Rabb'in tarafından bir dolaşan onun üzerinde dolaştı.", transliteration: "Fe tafe aleyha taifun min rabbike ve hum naimun."},
  {id: 5291, sure_no: 20, ayet_no: 20, sure_adi: "Kalem", text: "فَاَصْبَحَتْ كَالصَّر۪يمِ", translation: "Böylece, bahçeleri, üzerinde hiç ekin olmayan kara toprak gibi oldu.", transliteration: "Fe asbahat kes sarim."},
  {id: 5292, sure_no: 21, ayet_no: 21, sure_adi: "Kalem", text: "فَتَنَادَوْا مُصْبِح۪ينَۙ", translation: "Sabah olunca birbirlerine seslendiler.", transliteration: "Fe tenadev musbihin."},
  {id: 5293, sure_no: 22, ayet_no: 22, sure_adi: "Kalem", text: "اَنِ اغْدُوا عَلٰى حَرْثِكُمْ اِنْ كُنْتُمْ صَارِم۪ينَ", translation: "Eğer, ürününüzü toplayacaksanız, tarlanıza sabah erkenden gidin!", transliteration: "Enıgdu ala harsikum in kuntum sarımin."},
  {id: 5294, sure_no: 23, ayet_no: 23, sure_adi: "Kalem", text: "فَانْطَلَقُوا وَهُمْ يَتَخَافَتُونَۙ", translation: "Hemen, sessizce yola koyuldular.", transliteration: "Fentaleku ve hum yetehafetun."},
  {id: 5295, sure_no: 24, ayet_no: 24, sure_adi: "Kalem", text: "اَنْ لَا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِسْك۪ينٌ", translation: "\"Sakın ha! Bugün aranıza hiçbir ihtiyaç sahibi girmesin.\"", transliteration: "En la yedhulennehel yevme aleykum miskin."},
  {id: 5296, sure_no: 25, ayet_no: 25, sure_adi: "Kalem", text: "وَغَدَوْا عَلٰى حَرْدٍ قَادِر۪ينَ", translation: "İhtiyaç sahiplerini göz ardı ederek erkenden gittiler.", transliteration: "Ve gadev ala hardin kadirin."},
  {id: 5297, sure_no: 26, ayet_no: 26, sure_adi: "Kalem", text: "فَلَمَّا رَاَوْهَا قَالُٓوا اِنَّا لَضَٓالُّونَۙ", translation: "Fakat onu gördüklerinde: \"Herhalde yanlış yere geldik!\" dediler.", transliteration: "Fe lemma reevha kalu inna le dallun."},
  {id: 5298, sure_no: 27, ayet_no: 27, sure_adi: "Kalem", text: "بَلْ نَحْنُ مَحْرُومُونَ", translation: "\"Hayır! Biz, mahrum bırakılanlarız.\"", transliteration: "Bel nahnu mahrumun."},
  {id: 5299, sure_no: 28, ayet_no: 28, sure_adi: "Kalem", text: "قَالَ اَوْسَطُهُمْ اَلَمْ اَقُلْ لَـكُمْ لَوْلَا تُسَبِّحُونَ", translation: "En makul düşünenleri: \"Ben, size tesbih etmeliyiz dememiş miydim?\" dedi.", transliteration: "Kale evsatuhum e lem ekul lekum levla tusebbihun."},
  {id: 5300, sure_no: 29, ayet_no: 29, sure_adi: "Kalem", text: "قَالُوا سُبْحَانَ رَبِّنَٓا اِنَّا كُنَّا ظَالِم۪ينَ", translation: "Onlar: \"Rabb'imizi tesbih ederiz. Doğrusu bizler haksızlık edenlermişiz.\" dediler.", transliteration: "Kalu subhane rabbina inna kunna zalimin."},
  {id: 5301, sure_no: 30, ayet_no: 30, sure_adi: "Kalem", text: "فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَتَلَاوَمُونَ", translation: "Ardından birbirlerini suçlamaya başladılar.", transliteration: "Fe akbele ba'duhum ala ba'dın yetelavemun."},
  {id: 5302, sure_no: 31, ayet_no: 31, sure_adi: "Kalem", text: "قَالُوا يَا وَيْلَنَٓا اِنَّا كُنَّا طَاغ۪ينَ", translation: "\"Yazıklar olsun bize! Biz, gerçekten azgınlık eden kimselermişiz.\"", transliteration: "Kalu ya veylena inna kunna tagin."},
  {id: 5303, sure_no: 32, ayet_no: 32, sure_adi: "Kalem", text: "عَسٰى رَبُّنَٓا اَنْ يُبْدِلَنَا خَيْراً مِنْهَٓا اِنَّٓا اِلٰى رَبِّنَا رَاغِبُونَ", translation: "\"Umarız ki, Rabb'imiz bize onun yerine daha hayırlısını verir. Ümitle Rabb'imize yöneliyoruz.\"", transliteration: "Asa rabbuna en yubdilena hayren minha inna ila rabbina ragıbun."},
  {id: 5304, sure_no: 33, ayet_no: 33, sure_adi: "Kalem", text: "كَذٰلِكَ الْعَذَابُۜ وَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُۢ  لَوْ كَانُوا يَعْلَمُونَ۟", translation: "İşte azap böyledir. Ahiret azabı ise elbette daha büyüktür. Keşke bilenlerden olsalardı.", transliteration: "Kezalikel azab, ve le azabul ahıreti ekber, lev kanu ya'lemun."},
  {id: 5305, sure_no: 34, ayet_no: 34, sure_adi: "Kalem", text: "اِنَّ لِلْمُتَّق۪ينَ عِنْدَ رَبِّهِمْ جَنَّاتِ النَّع۪يمِ", translation: "Takva sahipleri için, Rabb'lerinin yanında, nimeti bol Cennetler vardır.", transliteration: "İnne lil muttekine ınde rabbihim cennatin naim."},
  {id: 5306, sure_no: 35, ayet_no: 35, sure_adi: "Kalem", text: "اَفَنَجْعَلُ الْمُسْلِم۪ينَ كَالْمُجْرِم۪ينَۜ", translation: "İşte böyle, hiç Allah'a teslim olanları, suçlularla bir tutar mıyız?", transliteration: "E fe necalul muslimine kel mucrimin."},
  {id: 5307, sure_no: 36, ayet_no: 36, sure_adi: "Kalem", text: "مَا لَـكُمْ۠   كَيْفَ تَحْكُمُونَۚ", translation: "Size ne oluyor? Nasıl hüküm veriyorsunuz?", transliteration: "Ma lekum, keyfe tahkumun."},
  {id: 5308, sure_no: 37, ayet_no: 37, sure_adi: "Kalem", text: "اَمْ لَـكُمْ  كِتَابٌ ف۪يهِ تَدْرُسُونَۙ", translation: "Yoksa bir kitabınız var da bu bilgileri oradan mı öğreniyorsunuz?", transliteration: "Em lekum kitabun fihi tedrusun."},
  {id: 5309, sure_no: 38, ayet_no: 38, sure_adi: "Kalem", text: "اِنَّ لَـكُمْ ف۪يهِ لَمَا تَخَيَّرُونَۚ", translation: "İçinde, \"Beğendiğiniz şeyler sizindir.\" yazan bir kitap.", transliteration: "İnne lekum fihi lema tehayyerun."},
  {id: 5310, sure_no: 39, ayet_no: 39, sure_adi: "Kalem", text: "اَمْ لَـكُمْ اَيْمَانٌ عَلَيْنَا بَالِغَةٌ اِلٰى يَوْمِ الْقِيٰمَةِۙ اِنَّ لَـكُمْ لَمَا تَحْكُمُونَۚ", translation: "Yoksa siz her ne hüküm verirseniz öyle olacak diye, kıyamet gününe kadar geçerli verilmiş bir sözümüz mü var?", transliteration: "Em lekum eymanun aleyna baligatun ila yevmil kıyameti inne lekum lema tahkumun."},
  {id: 5311, sure_no: 40, ayet_no: 40, sure_adi: "Kalem", text: "سَلْهُمْ اَيُّهُمْ بِذٰلِكَ زَع۪يمٌۚۛ", translation: "Onlara sor bakalım: \"Böyle bir şeye hangisi garanti verebilir?\"", transliteration: "Sel hum eyyuhum bi zalike zeim."},
  {id: 5312, sure_no: 41, ayet_no: 41, sure_adi: "Kalem", text: "اَمْ لَهُمْ شُرَكَٓاءُۚۛ  فَلْيَأْتُوا بِشُرَكَٓائِهِمْ  اِنْ  كَانُوا صَادِق۪ينَ", translation: "Yoksa ortakları mı var? Eğer doğru söylüyorlarsa ortaklarını getirsinler!", transliteration: "Em lehum şurekau, fel ye'tu bi şurekaihim in kanu sadikin."},
  {id: 5313, sure_no: 42, ayet_no: 42, sure_adi: "Kalem", text: "يَوْمَ يُكْشَفُ عَنْ سَاقٍ وَيُدْعَوْنَ اِلَى السُّجُودِ فَلَا يَسْتَط۪يعُونَۙ", translation: "Gerçeklerin açığa çıktığı gün secde etmeye çağrılacaklar, ancak buna güçleri yetmez.", transliteration: "Yevme yukşefu an sakın ve yud'avne iles sucudi fe la yestetiun."},
  {id: 5314, sure_no: 43, ayet_no: 43, sure_adi: "Kalem", text: "خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌۜ  وَقَدْ كَانُوا يُدْعَوْنَ اِلَى السُّجُودِ وَهُمْ سَالِمُونَ", translation: "O Gün, gözlerini umutsuzca endişe bürüyecek, yüzlerini aşağılanmışlık duygusu kaplayacaktır. Oysaki onlar, fırsat varken secdeye davet olunmuşlardı.", transliteration: "Haşiaten ebsaruhum terhekuhum zilleh, ve kad kanu yud'avne iles sucudi ve hum salimun."},
  {id: 5315, sure_no: 44, ayet_no: 44, sure_adi: "Kalem", text: "فَذَرْن۪ي وَمَنْ يُكَذِّبُ بِهٰذَا الْحَد۪يثِۜ سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لَا يَعْلَمُونَۙ", translation: "O halde bu hadisi yalanlayanları Bana bırak. Onları bilmedikleri bir yerden yavaş yavaş yaklaştıracağız.", transliteration: "Fe zerni ve men yukezzibu bi hazel hadis, se nestedricuhum min haysu la ya'lemun."},
  {id: 5316, sure_no: 45, ayet_no: 45, sure_adi: "Kalem", text: "وَاُمْل۪ي لَهُمْۜ  اِنَّ كَيْد۪ي مَت۪ينٌ", translation: "Onlara süre tanıyorum. Kuşkusuz, Benim planım çok sağlamdır.", transliteration: "Ve umli lehum, inne keydi metin."},
  {id: 5317, sure_no: 46, ayet_no: 46, sure_adi: "Kalem", text: "اَمْ تَسْـَٔلُهُمْ اَجْراً فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَۚ", translation: "Yoksa onlardan bir ücret istiyorsun da onlar ağır bir borca mı giriyorlar?", transliteration: "Em tes'eluhum ecren fe hum min magremin muskalun."},
  {id: 5318, sure_no: 47, ayet_no: 47, sure_adi: "Kalem", text: "اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ", translation: "Veya gaybın bilgisine sahipler de oradan mı yazıyorlar?", transliteration: "Em inde humul gaybu fehum yektubun."},
  {id: 5319, sure_no: 48, ayet_no: 48, sure_adi: "Kalem", text: "فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُنْ كَصَاحِبِ الْحُوتِۢ اِذْ نَادٰى وَهُوَ مَكْظُومٌۜ", translation: "Artık Rabb'inin hükmünü sabırla bekle. Balık sahibi gibi olma. Hani o çok üzüntülü ve hüzünlü olarak seslenmişti.", transliteration: "Fasbir li hukmi rabbike ve la tekun ke sahıbil hut, iz nada ve huve mekzum."},
  {id: 5320, sure_no: 49, ayet_no: 49, sure_adi: "Kalem", text: "لَوْلَٓا اَنْ تَدَارَكَهُ نِعْمَةٌ مِنْ رَبِّه۪ لَنُبِذَ بِالْعَرَٓاءِ وَهُوَ مَذْمُومٌ", translation: "Eğer Rabb'inden yeniden bir nimet ulaşmasaydı, kınanmış olarak boş bir yere atılmış olacaktı.", transliteration: "Levla en tedarekehu ni'metun min rabbihi le nubize bil arai ve huve mezmum."},
  {id: 5321, sure_no: 50, ayet_no: 50, sure_adi: "Kalem", text: "فَاجْتَبٰيهُ رَبُّهُ فَجَعَلَهُ مِنَ الصَّالِح۪ينَ", translation: "Fakat Rabb'i onu seçti ve iyilerden yaptı.", transliteration: "Fectebahu rabbuhu fe cealehu mines salihin."},
  {id: 5322, sure_no: 51, ayet_no: 51, sure_adi: "Kalem", text: "وَاِنْ يَكَادُ الَّذ۪ينَ كَفَرُوا لَيُزْلِقُونَكَ بِاَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُولُونَ اِنَّهُ لَمَجْنُونٌۢ", translation: "Kafirler, o öğüdü duydukları zaman, neredeyse gözleriyle seni devireceklerdi. \"Kuşkusuz o bir delidir.\" diyorlardı.", transliteration: "Ve in yekadullezine keferu le yuzlikuneke bi ebsarihim lemma semiuz zikra ve yekulune innehu le mecnun."},
  {id: 5323, sure_no: 52, ayet_no: 52, sure_adi: "Kalem", text: "وَمَا هُوَ اِلَّا ذِكْرٌ لِلْعَالَم۪ينَ", translation: "Oysaki o, bütün insanlık için öğütten başka bir şey değildir.", transliteration: "Ve ma huve illa zikrun lil alemin."},
  {id: 5324, sure_no: 1, ayet_no: 1, sure_adi: "Hâkka", text: "اَلْحَٓاقَّةُۙ", translation: "Gerçekleşeceği kesin olan!", transliteration: "El hakkah."},
  {id: 5325, sure_no: 2, ayet_no: 2, sure_adi: "Hâkka", text: "مَا الْحَٓاقَّةُۚ", translation: "Nedir gerçekleşeceği kesin olan?", transliteration: "Mel hakkah."},
  {id: 5326, sure_no: 3, ayet_no: 3, sure_adi: "Hâkka", text: "وَمَٓا اَدْرٰيكَ مَا الْحَٓاقَّةُۜ", translation: "Gerçekleşeceği kesin olanın ne olduğunu, nereden bileceksin?", transliteration: "Ve ma edrake mel hakkah."},
  {id: 5327, sure_no: 4, ayet_no: 4, sure_adi: "Hâkka", text: "كَذَّبَتْ ثَمُودُ وَعَادٌ بِالْقَارِعَةِ", translation: "Semud ve Ad halkı Karia'yı yalanladılar.", transliteration: "Kezzebet semudu ve adun bil kariah."},
  {id: 5328, sure_no: 5, ayet_no: 5, sure_adi: "Hâkka", text: "فَاَمَّا ثَمُودُ فَاُهْلِكُوا بِالطَّاغِيَةِ", translation: "Bu nedenle Semud halkı büyük bir yıkımla yok edildi.", transliteration: "Fe emma semudu fe uhliku bit tagıyeh."},
  {id: 5329, sure_no: 6, ayet_no: 6, sure_adi: "Hâkka", text: "وَاَمَّا عَادٌ فَاُهْلِكُوا بِر۪يحٍ صَرْصَرٍ عَاتِيَةٍۙ", translation: "Ad halkı ise kasıp kavuran, şiddetli bir fırtına ile yok edildi.", transliteration: "Ve emma adun fe uhliku bi rihın sarsarin atiyeh."},
  {id: 5330, sure_no: 7, ayet_no: 7, sure_adi: "Hâkka", text: "سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَثَمَانِيَةَ اَيَّامٍۙ حُسُوماً فَتَرَى الْقَوْمَ ف۪يهَا صَرْعٰىۙ كَاَنَّهُمْ اَعْجَازُ نَخْلٍ خَاوِيَةٍۚ", translation: "Onu, yedi gece ve sekiz gün; hiç ara vermeden üzerlerine musallat etti. Öyle ki, o halkı, orada kökünden sökülmüş hurma ağacı kütükleri gibi yere serilmiş görürsün.", transliteration: "Sehhareha aleyhim seb'a leyalin ve semaniyete eyyamin husumen fe terel kavme fiha sar'a ke ennehum a'cazu nahlin haviyeh."},
  {id: 5331, sure_no: 8, ayet_no: 8, sure_adi: "Hâkka", text: "فَهَلْ تَرٰى لَهُمْ مِنْ بَاقِيَةٍ", translation: "Artık onlara ait bir kalıntı görebiliyor musun?", transliteration: "Fe hel tera lehum min bakıyeh."},
  {id: 5332, sure_no: 9, ayet_no: 9, sure_adi: "Hâkka", text: "وَجَٓاءَ فِرْعَوْنُ وَمَنْ قَبْلَهُ وَالْمُؤْتَفِكَاتُ بِالْخَاطِئَةِۚ", translation: "Firavun, ondan öncekiler ve kentleri alt üst olanlar, o yanlış ile geldiler.", transliteration: "Ve cae fir'avnu ve men kablehu vel mu'tefikatu bil hatıeh."},
  {id: 5333, sure_no: 10, ayet_no: 10, sure_adi: "Hâkka", text: "فَعَصَوْا رَسُولَ رَبِّهِمْ فَاَخَذَهُمْ اَخْذَةً رَابِيَةً", translation: "Onlar, Rabb'lerinin Resul'üne karşı geldiler. Bunun üzerine onları şiddetli bir yakalayışla yakaladı.", transliteration: "Fe asav resule rabbihim fe ehazehum ahzeten rabiyeh."},
  {id: 5334, sure_no: 11, ayet_no: 11, sure_adi: "Hâkka", text: "اِنَّا لَمَّا طَغَا الْمَٓاءُ حَمَلْنَاكُمْ فِي الْجَارِيَةِۙ", translation: "Suyun taştığı günde, sizi o akıp gidende Biz taşıdık.", transliteration: "İnna lemma tagal mau hamelnakum fil cariyeh."},
  {id: 5335, sure_no: 12, ayet_no: 12, sure_adi: "Hâkka", text: "لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَتَعِيَهَٓا اُذُنٌ وَاعِيَةٌ", translation: "Onu sizin için bir öğüt yapalım, duyan kulaklar onu duysun diye.", transliteration: "Li nec'aleha lekum tezkireten ve teıyeha uzunun vaıyeh."},
  {id: 5336, sure_no: 13, ayet_no: 13, sure_adi: "Hâkka", text: "فَاِذَا نُفِخَ فِي الصُّورِ نَفْخَةٌ وَاحِدَةٌۙ", translation: "Artık Sur'a bir tek üfleyişle üflendiği zaman,", transliteration: "Fe iza nufiha fis suri nefhatun vahıdeh."},
  {id: 5337, sure_no: 14, ayet_no: 14, sure_adi: "Hâkka", text: "وَحُمِلَتِ الْاَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَاحِدَةً", translation: "Yeryüzü ve dağlar kaldırılıp, bir tek çarpışla parçalandığı zaman,", transliteration: "Ve humiletil ardu vel cibalu fe dukketa dekketen vahıdeh."},
  {id: 5338, sure_no: 15, ayet_no: 15, sure_adi: "Hâkka", text: "فَيَوْمَئِذٍ وَقَعَتِ الْوَاقِعَةُۙ", translation: "şte O Gün gerçekleşecek olan gerçekleşir.", transliteration: "Fe yevme izin vekaatil vakıah."},
  {id: 5339, sure_no: 16, ayet_no: 16, sure_adi: "Hâkka", text: "وَانْشَقَّتِ السَّمَٓاءُ فَهِيَ يَوْمَئِذٍ وَاهِيَةٌۙ", translation: "Gök yarılır. O Gün, o zayıftır artık.", transliteration: "Ven şakkatis semau fe hiye yevme izin vahiyeh."},
  {id: 5340, sure_no: 17, ayet_no: 17, sure_adi: "Hâkka", text: "وَالْمَلَكُ عَلٰٓى اَرْجَٓائِهَاۜ وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَانِيَةٌۜ", translation: "melek, onun çevresi üzerindedir. O Gün Rabb'inin Arş'ını yeni varlıklar taşırlar.", transliteration: "Vel meleku ala ercaiha, ve yahmilu arşe rabbike fevkahum yevme izin semaniyeh."},
  {id: 5341, sure_no: 18, ayet_no: 18, sure_adi: "Hâkka", text: "يَوْمَئِذٍ تُعْرَضُونَ لَا تَخْفٰى مِنْكُمْ خَافِيَةٌ", translation: "O Gün, arz olunacaksınız. Gizli saklı hiçbir şeyiniz kalmaz.", transliteration: "Yevme izin tu'radune la tahfa minkum hafiyeh."},
  {id: 5342, sure_no: 19, ayet_no: 19, sure_adi: "Hâkka", text: "فَاَمَّا مَنْ اُو۫تِيَ كِتَابَهُ بِيَم۪ينِه۪ فَيَقُولُ هَٓاؤُ۬مُ اقْرَؤُ۫ا كِتَابِيَهْۚ", translation: "Kitabı sağından verilen kimse: \"Alın, kitabımı okuyun.\" der.", transliteration: "Fe emma men utiye kitabehu bi yeminihi fe yekulu haumukreu kitabiyeh."},
  {id: 5343, sure_no: 20, ayet_no: 20, sure_adi: "Hâkka", text: "اِنّ۪ي ظَنَنْتُ اَنّ۪ي مُلَاقٍ حِسَابِيَهْۚ", translation: "Doğrusu ben, hesabımla karşılaşacağımı biliyordum.", transliteration: "İnni zanentu enniy mülakın hısabiyeh."},
  {id: 5344, sure_no: 21, ayet_no: 21, sure_adi: "Hâkka", text: "فَهُوَ ف۪ي ع۪يشَةٍ رَاضِيَةٍۙ", translation: "Artık o, hoşnut olacağı bir hayat yaşayacak.", transliteration: "Fe huve fi işetin radıyeh."},
  {id: 5345, sure_no: 22, ayet_no: 22, sure_adi: "Hâkka", text: "ف۪ي جَنَّةٍ عَالِيَةٍۙ", translation: "Mükemmel bir bahçede;", transliteration: "Fi cennetin aliyeh."},
  {id: 5346, sure_no: 23, ayet_no: 23, sure_adi: "Hâkka", text: "قُطُوفُهَا دَانِيَةٌ", translation: "Olgunlaşmış meyveleri dallarından sarkmış bir haldedir.", transliteration: "Kutufuha daniyeh."},
  {id: 5347, sure_no: 24, ayet_no: 24, sure_adi: "Hâkka", text: "كُلُوا وَاشْرَبُوا هَن۪ٓيـٔاً بِمَٓا اَسْلَفْتُمْ فِي الْاَيَّامِ الْخَالِيَةِ", translation: "Dünyada yapmış olduğunuz şeylerin karşılığı olarak, afiyetle yiyin ve için.", transliteration: "Kulu veşrebu henien bima esleftum fil eyyamil haliyeh."},
  {id: 5348, sure_no: 25, ayet_no: 25, sure_adi: "Hâkka", text: "وَاَمَّا مَنْ اُو۫تِيَ كِتَابَهُ بِشِمَالِه۪ فَيَقُولُ يَا لَيْتَن۪ي لَمْ اُو۫تَ كِتَابِيَهْۚ", translation: "Kitabı solundan verilen kimse ise: \"Keşke bana kitabım verilmeseydi.\" de;", transliteration: "Ve emma men utiye kitabehu bi şimalihi fe yekulu ya leyteni lem ute kitabiyeh."},
  {id: 5349, sure_no: 26, ayet_no: 26, sure_adi: "Hâkka", text: "وَلَمْ اَدْرِ مَا حِسَابِيَهْۚ", translation: "\"Hesabımın ne olduğunu hiç bilmeseydim.\" der.", transliteration: "Ve lem edri ma hısabiyeh."},
  {id: 5350, sure_no: 27, ayet_no: 27, sure_adi: "Hâkka", text: "يَا لَيْتَهَا كَانَتِ الْقَاضِيَةَۚ", translation: "\"Keşke o bitmiş olsaydı.\"", transliteration: "Ya leyteha kanetil kadiyeh."},
  {id: 5351, sure_no: 28, ayet_no: 28, sure_adi: "Hâkka", text: "مَٓا اَغْنٰى عَنّ۪ي مَالِيَهْۚ", translation: "\"Malım-gücüm bana hiçbir yarar sağlamadı.\"", transliteration: "Ma agna anni maliyeh."},
  {id: 5352, sure_no: 29, ayet_no: 29, sure_adi: "Hâkka", text: "هَلَكَ عَنّ۪ي سُلْطَانِيَهْۚ", translation: "\"Bütün saltanatım yok oldu.\"", transliteration: "Heleke anni sultaniyeh."},
  {id: 5353, sure_no: 30, ayet_no: 30, sure_adi: "Hâkka", text: "خُذُوهُ فَغُلُّوهُۙ", translation: "\"Onu tutun ve bağlayın.\"", transliteration: "Huzuhu fe gulluh."},
  {id: 5354, sure_no: 31, ayet_no: 31, sure_adi: "Hâkka", text: "ثُمَّ الْجَح۪يمَ صَلُّوهُۙ", translation: "\"Sonra onu Cehennem'e atın.\"", transliteration: "Summel cahime salluh."},
  {id: 5355, sure_no: 32, ayet_no: 32, sure_adi: "Hâkka", text: "ثُمَّ ف۪ي سِلْسِلَةٍ ذَرْعُهَا سَبْعُونَ ذِرَاعاً فَاسْلُكُوهُۜ", translation: "\"Sonra onu yetmiş arşın uzunluğunda bir zincire bağlayın.\"", transliteration: "Summe fi silsiletin zer'uha seb'une ziraan feslukuh."},
  {id: 5356, sure_no: 33, ayet_no: 33, sure_adi: "Hâkka", text: "اِنَّهُ كَانَ لَا يُؤْمِنُ بِاللّٰهِ الْعَظ۪يمِۙ", translation: "O, O Azim Allah'a inanmıyordu.", transliteration: "İnnehu kane la yu'minu billahil azim."},
  {id: 5357, sure_no: 34, ayet_no: 34, sure_adi: "Hâkka", text: "وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْك۪ينِۜ", translation: "Miskinin yedirilmesini teşvik etmiyordu.", transliteration: "Ve la yahuddu ala taamil miskin."},
  {id: 5358, sure_no: 35, ayet_no: 35, sure_adi: "Hâkka", text: "فَلَيْسَ لَهُ الْيَوْمَ هٰهُنَا حَم۪يمٌۙ", translation: "Artık bugün, onun burada hiçbir koruyucusu yoktur.", transliteration: "Fe leyse lehul yevme hahuna hamim."},
  {id: 5359, sure_no: 36, ayet_no: 36, sure_adi: "Hâkka", text: "وَلَا طَعَامٌ اِلَّا مِنْ غِسْل۪ينٍۙ", translation: "Ve gislinden başka bir yiyecekleri yoktur.", transliteration: "Ve la taamun illa min gıslin."},
  {id: 5360, sure_no: 37, ayet_no: 37, sure_adi: "Hâkka", text: "لَا يَأْكُلُهُٓ اِلَّا الْخَاطِؤُ۫نَ۟", translation: "Onu, yanlışlarında ısrar edenlerden başkası yemez.", transliteration: "La ye'kuluhu illel hatiun."},
  {id: 5361, sure_no: 38, ayet_no: 38, sure_adi: "Hâkka", text: "فَلَٓا اُقْسِمُ بِمَا تُبْصِرُونَۙ", translation: "Hayır! Gördüğünüz şeylere yemin ederim;", transliteration: "Fe la uksımu bima tubsırun."},
  {id: 5362, sure_no: 39, ayet_no: 39, sure_adi: "Hâkka", text: "وَمَا لَا تُبْصِرُونَۙ", translation: "Ve görmediklerinize.", transliteration: "Ve ma la tubsırun."},
  {id: 5363, sure_no: 40, ayet_no: 40, sure_adi: "Hâkka", text: "اِنَّهُ لَقَوْلُ رَسُولٍ كَر۪يمٍۚ", translation: "Kuşkusuz o, şerefli bir Resul'ün sözdür.", transliteration: "İnnehu le kavlu resulun kerimin."},
  {id: 5364, sure_no: 41, ayet_no: 41, sure_adi: "Hâkka", text: "وَمَا هُوَ بِقَوْلِ شَاعِرٍۜ  قَل۪يلاً مَا تُـؤْمِنُونَۙ", translation: "O, bir şair sözü değildir. Amma da inançsızsınız!", transliteration: "Ve ma huve bi kavli şairin, kalilin ma tu'minun."},
  {id: 5365, sure_no: 42, ayet_no: 42, sure_adi: "Hâkka", text: "وَلَا بِقَوْلِ كَاهِنٍۜ  قَل۪يلاً مَا تَذَكَّرُونَۜ", translation: "O, bir kahin sözü de değildir. Hiç öğüt dinlemiyorsunuz!", transliteration: "Ve la bi kavli kahin, kalilen ma tezekkerun."},
  {id: 5366, sure_no: 43, ayet_no: 43, sure_adi: "Hâkka", text: "تَنْز۪يلٌ مِنْ رَبِّ الْعَالَم۪ينَ", translation: "Alemlerin Rabb'inden indirilmedir.", transliteration: "Tenzilun min rabbil alemin."},
  {id: 5367, sure_no: 44, ayet_no: 44, sure_adi: "Hâkka", text: "وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْاَقَاو۪يلِۙ", translation: "Eğer Biz'im adımıza bazı sözler uydurmuş olsaydı;", transliteration: "Ve lev tekavvele aleyna ba'dal ekavil."},
  {id: 5368, sure_no: 45, ayet_no: 45, sure_adi: "Hâkka", text: "لَاَخَذْنَا مِنْهُ بِالْيَم۪ينِۙ", translation: "Elbette onun bütün gücünü alırdık.", transliteration: "Le ehazna minhu bil yemin."},
  {id: 5369, sure_no: 46, ayet_no: 46, sure_adi: "Hâkka", text: "ثُمَّ لَقَطَعْنَا مِنْهُ الْوَت۪ينَۘ", translation: "Sonra kesinlikle can damarını koparırdık.", transliteration: "Summe le kata'na minhul vetin."},
  {id: 5370, sure_no: 47, ayet_no: 47, sure_adi: "Hâkka", text: "فَمَا مِنْكُمْ مِنْ اَحَدٍ عَنْهُ حَاجِز۪ينَ", translation: "Hiçbiriniz buna engel olamazdınız.", transliteration: "Fe ma minkum min ehadin anhu hacizin."},
  {id: 5371, sure_no: 48, ayet_no: 48, sure_adi: "Hâkka", text: "وَاِنَّهُ لَتَذْكِرَةٌ لِلْمُتَّق۪ينَ", translation: "O, takva sahipleri için bir öğüttür.", transliteration: "Ve innehu le tezkiretun lil muttekin."},
  {id: 5372, sure_no: 49, ayet_no: 49, sure_adi: "Hâkka", text: "وَاِنَّا لَنَعْلَمُ اَنَّ مِنْكُمْ مُكَذِّب۪ينَ", translation: "Biz, sizden onu yalanlayıcıları çok iyi biliyoruz.", transliteration: "Ve inna le na'lemu enne minkum mukezzibin."},
  {id: 5373, sure_no: 50, ayet_no: 50, sure_adi: "Hâkka", text: "وَاِنَّهُ لَحَسْرَةٌ عَلَى الْـكَافِر۪ينَ", translation: "O, Kafirler için derin bir pişmanlıktır.", transliteration: "Ve innehu le hasretun alel kafirin."},
  {id: 5374, sure_no: 51, ayet_no: 51, sure_adi: "Hâkka", text: "وَاِنَّهُ لَحَقُّ الْيَق۪ينِ", translation: "O, kesinlikle gerçektir.", transliteration: "Ve innehu le hakk'ul yakin."},
  {id: 5375, sure_no: 52, ayet_no: 52, sure_adi: "Hâkka", text: "فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظ۪يمِ", translation: "O halde Azim Rabb'inin adını tesbih et.", transliteration: "Fe sebbıh bismi rabbikel azim."},
  {id: 5376, sure_no: 1, ayet_no: 1, sure_adi: "Mearic", text: "سَاَلَ سَٓائِلٌ بِعَذَابٍ وَاقِعٍۙ", translation: "İsteyen, gerçekleşecek olan azabı istedi.", transliteration: "Se ele sailun bi azabin vakı'n."},
  {id: 5377, sure_no: 2, ayet_no: 2, sure_adi: "Mearic", text: "لِلْـكَافِر۪ينَ لَيْسَ لَهُ دَافِعٌۙ", translation: "Kafirler için engellenemez olan azabı.", transliteration: "Lil kafirine leyse lehu dafi'."},
  {id: 5378, sure_no: 3, ayet_no: 3, sure_adi: "Mearic", text: "مِنَ اللّٰهِ ذِي الْمَعَارِجِۜ", translation: "Mearic sahibi Allah tarafından.", transliteration: "Minallahi zil mearic."},
  {id: 5379, sure_no: 4, ayet_no: 4, sure_adi: "Mearic", text: "تَعْرُجُ الْمَلٰٓئِكَةُ وَالرُّوحُ اِلَيْهِ ف۪ي يَوْمٍ كَانَ مِقْدَارُهُ خَمْس۪ينَ اَلْفَ سَنَةٍۚ", translation: "Melekler ve ruh, miktarı elli bin yıl olan bir günde O'na yükselir.", transliteration: "Ta'rucul melaiketu ver ruhu ileyhi fi yevmin kane mikdaruhu hamsine elfe seneh."},
  {id: 5380, sure_no: 5, ayet_no: 5, sure_adi: "Mearic", text: "فَاصْبِرْ صَبْراً جَم۪يلاً", translation: "O halde, güzel bir sabır ile sabret.", transliteration: "Fasbir sabren cemila."},
  {id: 5381, sure_no: 6, ayet_no: 6, sure_adi: "Mearic", text: "اِنَّهُمْ يَرَوْنَهُ بَع۪يداًۙ", translation: "Onlar onu uzak olarak görüyorlar.", transliteration: "İnnehum yerevnehu baida."},
  {id: 5382, sure_no: 7, ayet_no: 7, sure_adi: "Mearic", text: "وَنَرٰيهُ قَر۪يباًۜ", translation: "Biz, onu yakın görüyoruz.", transliteration: "Ve nerahu kariba."},
  {id: 5383, sure_no: 8, ayet_no: 8, sure_adi: "Mearic", text: "يَوْمَ تَكُونُ السَّمَٓاءُ كَالْمُهْلِۙ", translation: "O gün gökyüzü erimiş bir maden gibi olur.", transliteration: "Yevme tekunus semau kel muhl."},
  {id: 5384, sure_no: 9, ayet_no: 9, sure_adi: "Mearic", text: "وَتَكُونُ الْجِبَالُ كَالْعِهْنِۙ", translation: "Ve dağlar renkli yün gibi olur.", transliteration: "Ve tekunul cibalu kel ıhn."},
  {id: 5385, sure_no: 10, ayet_no: 10, sure_adi: "Mearic", text: "وَلَا يَسْـَٔلُ حَم۪يمٌ حَم۪يماًۚ", translation: "Ve hiçbir hamim, başka bir hamimi sormaz.", transliteration: "Ve la yes'elu hamimun hamima."},
  {id: 5386, sure_no: 11, ayet_no: 11, sure_adi: "Mearic", text: "يُبَصَّرُونَهُمْۜ  يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَد۪ي مِنْ عَذَابِ يَوْمِئِذٍ بِبَن۪يهِۙ", translation: "Onlar, birbirleriyle yüzleşecekler. Mücrim olan, O Gün azabından kurtulmak için mümkün olsa oğullarını fidye olarak verebilmeyi ister;", transliteration: "Yubassarunehum yeveddul mucrimu lev yeftedi min azabi yevmi izin bi benih."},
  {id: 5387, sure_no: 12, ayet_no: 12, sure_adi: "Mearic", text: "وَصَاحِبَتِه۪ وَاَخ۪يهِۙ", translation: "Eşini ve kardeşini,", transliteration: "Ve sahıbetihi ve ahih."},
  {id: 5388, sure_no: 13, ayet_no: 13, sure_adi: "Mearic", text: "وَفَص۪يلَتِهِ الَّت۪ي تُـْٔو۪يهِۙ", translation: "Kendisini barındıran soyunu-sopunu;", transliteration: "Ve fasiletihilleti tu'vih."},
  {id: 5389, sure_no: 14, ayet_no: 14, sure_adi: "Mearic", text: "وَمَنْ فِي الْاَرْضِ جَم۪يعاًۙ ثُمَّ يُنْج۪يهِۙ", translation: "Yeryüzünde kimi varsa tamamını verip kendisini kurtarmak ister.", transliteration: "Ve men fil ardı cemi'an summe yuncih."},
  {id: 5390, sure_no: 15, ayet_no: 15, sure_adi: "Mearic", text: "كَلَّاۜ اِنَّهَا لَظٰىۙ", translation: "Hayır! Hayır! O alev alev yanan ateştir;", transliteration: "Kella, inneha leza."},
  {id: 5391, sure_no: 16, ayet_no: 16, sure_adi: "Mearic", text: "نَزَّاعَةً لِلشَّوٰىۚ", translation: "Derileri kavurup soyan;", transliteration: "Nezzaaten liş şeva."},
  {id: 5392, sure_no: 17, ayet_no: 17, sure_adi: "Mearic", text: "تَدْعُوا مَنْ اَدْبَرَ وَتَوَلّٰىۙ", translation: "Haktan yüz çevirip, arkasını dönen kimseyi kendisine çağıran;", transliteration: "Ted'u men edbera ve tevella."},
  {id: 5393, sure_no: 18, ayet_no: 18, sure_adi: "Mearic", text: "وَجَمَعَ فَاَوْعٰى", translation: "Mal, mülk edinip, onu biriktireni.", transliteration: "Ve cemea fe ev'a."},
  {id: 5394, sure_no: 19, ayet_no: 19, sure_adi: "Mearic", text: "اِنَّ الْاِنْسَانَ خُلِقَ هَلُوعاًۙ", translation: "O insan hırslı yaratıldı.", transliteration: "İnnel insane hulika helua."},
  {id: 5395, sure_no: 20, ayet_no: 20, sure_adi: "Mearic", text: "اِذَا مَسَّهُ الشَّرُّ جَزُوعاًۙ", translation: "Başına kötü bir şey geldiğinde sızlanmaya başlar.", transliteration: "İza messehuş şerru cezua."},
  {id: 5396, sure_no: 21, ayet_no: 21, sure_adi: "Mearic", text: "وَاِذَا مَسَّهُ الْخَيْرُ مَنُوعاًۙ", translation: "Mal-mülk sahibi olunca da ondan kimseyi yararlandırmaz.", transliteration: "Ve iza messehul hayru menua."},
  {id: 5397, sure_no: 22, ayet_no: 22, sure_adi: "Mearic", text: "اِلَّا الْمُصَلّ۪ينَۙ", translation: "Ancak \"musallin\" olanlar hariç.", transliteration: "İllel musallin."},
  {id: 5398, sure_no: 23, ayet_no: 23, sure_adi: "Mearic", text: "اَلَّذ۪ينَ هُمْ عَلٰى صَلَاتِهِمْ دَٓائِمُونَۖ", translation: "Onlar, salatlarında devamlıdırlar.", transliteration: "Ellezine hum ala salatihim daimun."},
  {id: 5399, sure_no: 24, ayet_no: 24, sure_adi: "Mearic", text: "وَالَّذ۪ينَ ف۪ٓي اَمْوَالِهِمْ حَقٌّ مَعْلُومٌۙ", translation: "Onlar, mallarında belirli bir pay ayıranlardır;", transliteration: "Vellezine fi emvalihim hakkun ma'lum."},
  {id: 5400, sure_no: 25, ayet_no: 25, sure_adi: "Mearic", text: "لِلسَّٓائِلِ وَالْمَحْرُومِۖ", translation: "İsteyenler ve yoksun olanlar için.", transliteration: "Lis saili vel mahrum."},
  {id: 5401, sure_no: 26, ayet_no: 26, sure_adi: "Mearic", text: "وَالَّذ۪ينَ يُصَدِّقُونَ بِيَوْمِ الدّ۪ينِۖ", translation: "Onlar, Din Günü'nü tasdik ederler.", transliteration: "Vellezine yusaddikune bi yevmid din."},
  {id: 5402, sure_no: 27, ayet_no: 27, sure_adi: "Mearic", text: "وَالَّذ۪ينَ هُمْ مِنْ عَذَابِ رَبِّهِمْ مُشْفِقُونَۚ", translation: "Onlar, Rabb'lerinin azabından çekinirler.", transliteration: "Vellezine hum min azabi rabbihim muşfikun."},
  {id: 5403, sure_no: 28, ayet_no: 28, sure_adi: "Mearic", text: "اِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُونٍۚ", translation: "Rabb'lerinin azabından kimse emin olamaz.", transliteration: "İnne azabe rabbihim gayru me'mun."},
  {id: 5404, sure_no: 29, ayet_no: 29, sure_adi: "Mearic", text: "وَالَّذ۪ينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَۙ", translation: "Onlar, ferçlerini korurlar.", transliteration: "Vellezine hum li furucihim hafizun."},
  {id: 5405, sure_no: 30, ayet_no: 30, sure_adi: "Mearic", text: "اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوم۪ينَۚ", translation: "Eşleri veya antlaşma yolu ile sahip oldukları hariç. Şüphesiz onlar kınanmazlar.", transliteration: "İlla ala ezvacihim ev ma meleket eymanuhum fe innehum gayru melumin."},
  {id: 5406, sure_no: 31, ayet_no: 31, sure_adi: "Mearic", text: "فَمَنِ ابْتَغٰى وَرَٓاءَ ذٰلِكَ فَاُو۬لٰٓئِكَ هُمُ الْعَادُونَۚ", translation: "Bunun ötesinde bir şey isteyenler, haddi aşanlardır.", transliteration: "Fe menibtega verae zalike fe ulaike humul adun."},
  {id: 5407, sure_no: 32, ayet_no: 32, sure_adi: "Mearic", text: "وَالَّذ۪ينَ هُمْ لِاَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَۖ", translation: "Onlar, emanetlerini ve ahitlerini gözetirler.", transliteration: "Vellezine hum li emanatihim ve ahdihim raun."},
  {id: 5408, sure_no: 33, ayet_no: 33, sure_adi: "Mearic", text: "وَالَّذ۪ينَ هُمْ بِشَهَادَاتِهِمْ قَٓائِمُونَۖ", translation: "Onlar, tanıklıklarını dosdoğru yerine getirirler.", transliteration: "Vellezine hum bi şehadatihim kaimun."},
  {id: 5409, sure_no: 34, ayet_no: 34, sure_adi: "Mearic", text: "وَالَّذ۪ينَ هُمْ عَلٰى صَلَاتِهِمْ يُحَافِظُونَۜ", translation: "Onlar, salatlarını korurlar.", transliteration: "Vellezine hum ala salatihim yuhafizun."},
  {id: 5410, sure_no: 35, ayet_no: 35, sure_adi: "Mearic", text: "اُو۬لٰٓئِكَ ف۪ي جَنَّاتٍ مُكْرَمُونَۜ ۟", translation: "İşte onlar, Cennetlerde ağırlanacak olanlardır.", transliteration: "Ulaike fi cennatin mukremun."},
  {id: 5411, sure_no: 36, ayet_no: 36, sure_adi: "Mearic", text: "فَمَالِ الَّذ۪ينَ كَفَرُوا قِبَلَكَ مُهْطِع۪ينَۙ", translation: "Şimdi Kafirlere ne oluyor da sana doğru koşuşturuyorlar?", transliteration: "Fe ma lillezine keferu kıbeleke muhtıin."},
  {id: 5412, sure_no: 37, ayet_no: 37, sure_adi: "Mearic", text: "عَنِ الْيَم۪ينِۙ وَعَنِ الشِّمَالِ عِز۪ينَ", translation: "Her yönden, her yerden gruplar halinde.", transliteration: "Anil yemini ve aniş şimali ızin."},
  {id: 5413, sure_no: 38, ayet_no: 38, sure_adi: "Mearic", text: "اَيَطْمَعُ كُلُّ امْرِئٍ مِنْهُمْ اَنْ يُدْخَلَ جَنَّةَ نَع۪يمٍۙ", translation: "Onların her biri Nimet Cenneti'ne konulacağını mı umuyor?", transliteration: "E yatmeu kullumriin minhum en yudhale cennete naim."},
  {id: 5414, sure_no: 39, ayet_no: 39, sure_adi: "Mearic", text: "كَلَّاۜ  اِنَّا خَلَقْنَاهُمْ مِمَّا يَعْلَمُونَ", translation: "Hayır, boşuna ummasınlar! Biz, onları bildikleri şeyden yarattık.", transliteration: "Kella, inna halaknahum mimma ya'lemun."},
  {id: 5415, sure_no: 40, ayet_no: 40, sure_adi: "Mearic", text: "فَلَٓا اُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغَارِبِ اِنَّا لَقَادِرُونَۙ", translation: "Hayır, öyle değil! Doğuların ve batıların Rabb'ine yemin ederim ki kesinlikle Bizim her şeye gücümüz yeter;", transliteration: "Fe la uksimu bi rabbil meşarikı vel megaribi inna le kadirun."},
  {id: 5416, sure_no: 41, ayet_no: 41, sure_adi: "Mearic", text: "عَلٰٓى اَنْ نُبَدِّلَ خَيْراً مِنْهُمْۙ وَمَا نَحْنُ بِمَسْبُوق۪ينَ", translation: "Onların yerine, kendilerinden daha hayırlısını getirmeye kesinlikle güç yetirenleriz. Bunu yapmamıza hiçbir güç engel olamaz.", transliteration: "Ala en nubeddile hayren minhum ve ma nahnu bi mesbukin."},
  {id: 5417, sure_no: 42, ayet_no: 42, sure_adi: "Mearic", text: "فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتّٰى يُلَاقُوا يَوْمَهُمُ الَّذ۪ي يُوعَدُونَۙ", translation: "Artık onları kendi hallerine bırak. Uyarıldıkları günleri gelip çatıncaya kadar gaflet içinde oyalanıp dursunlar.", transliteration: "Fe zerhum yehudu ve yel'abu hatta yulaku yevme humullezi yuadun."},
  {id: 5418, sure_no: 43, ayet_no: 43, sure_adi: "Mearic", text: "يَوْمَ يَخْرُجُونَ مِنَ الْاَجْدَاثِ سِرَاعاً  كَاَنَّهُمْ اِلٰى نُصُبٍ يُوفِضُونَۙ", translation: "O Gün, onlar kabirlerinden fırlayıp çıkarlar. Sanki bir hedefe koşuyor gibi;", transliteration: "Yevme yahrucune minel ecdasi siraan ke ennehum ila nusubin yufidun."},
  {id: 5419, sure_no: 44, ayet_no: 44, sure_adi: "Mearic", text: "خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌۜ ذٰلِكَ الْيَوْمُ الَّذ۪ي كَانُوا يُوعَدُونَ", translation: "Gözlerinde korku, kendilerini zillet bürümüş halde. İşte bu, onların uyarıldıkları gündür.", transliteration: "Haşi'aten ebsaruhum terhekuhum zilleh, zalikel yevmullezi kanu yuadun."},
  {id: 5420, sure_no: 1, ayet_no: 1, sure_adi: "Nuh", text: "اِنَّٓا اَرْسَلْنَا نُوحاً اِلٰى قَوْمِه۪ٓ اَنْ اَنْذِرْ قَوْمَكَ مِنْ قَبْلِ اَنْ يَأْتِيَهُمْ عَذَابٌ اَل۪يمٌ", translation: "Biz Nuh'u halkına, kendilerine acı veren bir azap gelmezden önce onları uyarması için gönderdik.", transliteration: "İnna erselna nuhan ila kavmihi en enzir kavmeke min kabli en ye'tiyehum azabun elim."},
  {id: 5421, sure_no: 2, ayet_no: 2, sure_adi: "Nuh", text: "قَالَ يَا قَوْمِ اِنّ۪ي لَـكُمْ نَذ۪يرٌ مُب۪ينٌۙ", translation: "\"Ey halkım! Ben, sizin için apaçık bir uyarıcıyım.\" dedi.", transliteration: "Kale ya kavmi inni lekum nezirun mubin."},
  {id: 5422, sure_no: 3, ayet_no: 3, sure_adi: "Nuh", text: "اَنِ اعْبُدُوا اللّٰهَ وَاتَّقُوهُ وَاَط۪يعُونِۙ", translation: "\"Allah'a kulluk edin, O'nun için takva sahibi olun ve bana itaat edin.\"", transliteration: "Eni'budullahe vettekuhu ve etiun."},
  {id: 5423, sure_no: 4, ayet_no: 4, sure_adi: "Nuh", text: "يَغْفِرْ لَـكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ اِلٰٓى اَجَلٍ مُسَمًّىۜ اِنَّ اَجَلَ اللّٰهِ اِذَا جَٓاءَ لَا يُؤَخَّرُۢ  لَوْ كُنْتُمْ تَعْلَمُونَ", translation: "\"Ki Allah, suçlarınızı bağışlasın ve size belirlenmiş bir sürenin sonuna kadar zaman versin. Kuşkusuz Allah'ın belirlediği süre dolunca, ertelenmez. Keşke bilenlerden olsaydınız.\"", transliteration: "Yagfir lekum min zunubikum ve yuahhırkum ila ecelin musemma, inne ecelallahi iza cae la yuahhar, lev kuntum ta'lemun."},
  {id: 5424, sure_no: 5, ayet_no: 5, sure_adi: "Nuh", text: "قَالَ رَبِّ اِنّ۪ي دَعَوْتُ قَوْم۪ي لَيْلاً وَنَهَاراًۙ", translation: "\"Rabb'im! Ben, halkıma gece gündüz çağrıda bulundum.\" dedi.", transliteration: "Kale rabbi inni deavtu kavmi leylen ve nehara."},
  {id: 5425, sure_no: 6, ayet_no: 6, sure_adi: "Nuh", text: "فَلَمْ يَزِدْهُمْ دُعَٓاء۪ٓي اِلَّا فِرَاراً", translation: "\"Fakat benim çağrım, onların kaçışlarını daha da artırdı.\"", transliteration: "Fe lem yezidhum duai illa firara."},
  {id: 5426, sure_no: 7, ayet_no: 7, sure_adi: "Nuh", text: "وَاِنّ۪ي كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُٓوا اَصَابِعَهُمْ ف۪ٓي اٰذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَاَصَرُّوا وَاسْتَكْبَرُوا اسْتِكْبَاراًۚ", translation: "\"Öyle ki onları ne zaman Sen'in bağışlayıcılığına çağırdıysam parmaklarını kulaklarına tıkadılar, elbiselerine büründüler ısrarla kibirlendikçe kibirlendiler.\"", transliteration: "Ve inni kullema deavtuhum li tagfire lehum cealu esabiahum fi azanihim vestagşev siyabehum ve esarru vestekberustikbara."},
  {id: 5427, sure_no: 8, ayet_no: 8, sure_adi: "Nuh", text: "ثُمَّ اِنّ۪ي دَعَوْتُهُمْ جِهَاراًۙ", translation: "\"Sonra onlara açık açık her fırsatta çağrıda bulundum.\"", transliteration: "Summe inni deavtuhum cihara."},
  {id: 5428, sure_no: 9, ayet_no: 9, sure_adi: "Nuh", text: "ثُمَّ اِنّ۪ٓي اَعْلَنْتُ لَهُمْ وَاَسْرَرْتُ لَهُمْ اِسْرَاراًۙ", translation: "\"Sonra onlara; çağrımı toplu olarak da birebir görüşerek de ilettim.\"", transliteration: "Summe inni a'lentu lehum ve esrartu lehum israra."},
  {id: 5429, sure_no: 10, ayet_no: 10, sure_adi: "Nuh", text: "فَقُلْتُ اسْتَغْفِرُوا رَبَّكُمْ اِنَّهُ  كَانَ غَفَّاراًۙ", translation: "\"Rabb'inizden bağışlanma dileyin. Kuşkusuz O, çok bağışlayandır.\" dedim.", transliteration: "Fe kul tustagfıru rabbekum innehu kane gaffara."},
  {id: 5430, sure_no: 11, ayet_no: 11, sure_adi: "Nuh", text: "يُرْسِلِ السَّمَٓاءَ عَلَيْكُمْ مِدْرَاراًۙ", translation: "\"Gökten üzerinize bol yağmur göndersin.\"", transliteration: "Yursilis semae aleykum midrara."},
  {id: 5431, sure_no: 12, ayet_no: 12, sure_adi: "Nuh", text: "وَيُمْدِدْكُمْ بِاَمْوَالٍ وَبَن۪ينَ وَيَجْعَلْ لَكُمْ جَنَّاتٍ وَيَجْعَلْ لَكُمْ اَنْهَاراًۜ", translation: "\"Size mal mülk ve oğullarla yardım etsin. Ve sizin için bahçeler yapsın, nehirler akıtsın.\"", transliteration: "Ve yumdidkum biemvalin ve benine ve yec'al lekum cennatin ve yec'al lekum enhara."},
  {id: 5432, sure_no: 13, ayet_no: 13, sure_adi: "Nuh", text: "مَا لَكُمْ لَا تَرْجُونَ لِلّٰهِ وَقَاراًۚ", translation: "\"Size ne oluyor ki Allah'ın büyüklüğünü takdir etmiyorsunuz?\"", transliteration: "Ma lekum la tercune lillahi vekara."},
  {id: 5433, sure_no: 14, ayet_no: 14, sure_adi: "Nuh", text: "وَقَدْ خَلَقَكُمْ اَطْوَاراً", translation: "\"Oysa O, sizi çeşitli aşamalardan geçirerek yarattı.\"", transliteration: "Ve kad halakakum etvara."},
  {id: 5434, sure_no: 15, ayet_no: 15, sure_adi: "Nuh", text: "اَلَمْ تَرَوْا كَيْفَ خَلَقَ اللّٰهُ سَبْعَ سَمٰوَاتٍ طِبَاقاًۙ", translation: "\"Görmüyor musunuz Allah'ın yedi göğü nasıl tabakalar halinde yarattığını?\"", transliteration: "E lem terev keyfe halakallahu seb'a semavatin tıbaka."},
  {id: 5435, sure_no: 16, ayet_no: 16, sure_adi: "Nuh", text: "وَجَعَلَ الْقَمَرَ ف۪يهِنَّ نُوراً وَجَعَلَ الشَّمْسَ سِرَاجاً", translation: "\"Ve Ay'ı onların içinde bir nur, Güneş'i de kandil yaptı.\"", transliteration: "Ve cealel kamere fihinne nuren ve cealeş şemse siraca."},
  {id: 5436, sure_no: 17, ayet_no: 17, sure_adi: "Nuh", text: "وَاللّٰهُ اَنْبَتَكُمْ مِنَ الْاَرْضِ نَبَاتاًۙ", translation: "\"Allah, sizi yerden bir bitki gibi bitirdi.\"", transliteration: "Vallahu enbetekum minel ardı nebata."},
  {id: 5437, sure_no: 18, ayet_no: 18, sure_adi: "Nuh", text: "ثُمَّ يُع۪يدُكُمْ ف۪يهَا وَيُخْرِجُكُمْ اِخْرَاجاً", translation: "\"Sonra sizi toprağa döndürecek ve ardından bir çıkışla oradan çıkaracak.\"", transliteration: "Summe yuidukum fiha ve yuhricukum ihraca."},
  {id: 5438, sure_no: 19, ayet_no: 19, sure_adi: "Nuh", text: "وَاللّٰهُ جَعَلَ لَـكُمُ الْاَرْضَ بِسَاطاًۙ", translation: "\"Allah, yeryüzünü sizin için genişçe yaydı.\"", transliteration: "Vallahu ceale lekumul arda bisata."},
  {id: 5439, sure_no: 20, ayet_no: 20, sure_adi: "Nuh", text: "لِتَسْلُكُوا مِنْهَا سُبُلاً فِجَاجاً۟", translation: "\"Yolculuk etmeniz için orada geniş yollar yaptı.\"", transliteration: "Li tesluku minha subulen ficaca."},
  {id: 5440, sure_no: 21, ayet_no: 21, sure_adi: "Nuh", text: "قَالَ نُوحٌ رَبِّ اِنَّهُمْ عَصَوْن۪ي وَاتَّبَعُوا مَنْ لَمْ يَزِدْهُ مَالُهُ وَوَلَدُهُٓ  اِلَّا خَسَاراًۚ", translation: "Nuh: \"Rabb'im! Kuşkusuz onlar bana asi oldular. Malı ve evladı kendisine zarardan başka bir şey artırmayan kimseye uydular.\" dedi.", transliteration: "Kale nuhun rabbi innehum asavni vettebeu men lem yezidhu maluhu ve veleduhu illa hasara."},
  {id: 5441, sure_no: 22, ayet_no: 22, sure_adi: "Nuh", text: "وَمَكَرُوا مَكْراً كُبَّاراًۚ", translation: "\"Büyük tuzaklarla tuzak kurdular.\"", transliteration: "Ve mekeru mekren kubbara."},
  {id: 5442, sure_no: 23, ayet_no: 23, sure_adi: "Nuh", text: "وَقَالُوا لَا تَذَرُنَّ اٰلِهَتَكُمْ وَلَا تَذَرُنَّ وَداًّ وَلَا سُوَاعاًۙ وَلَا يَغُوثَ وَيَعُوقَ وَنَسْراًۚ", translation: "\"Sakın ilahlarınızı bırakmayın; sakın Vedd'i, Suva'yı, Yeğus'u, Yeuk'u ve Nesr'i bırakmayın.\" dediler.", transliteration: "Ve kalu la tezerunne alihetekum ve la tezerrunne vedden ve la suvaan ve la yeguse ve yeuka ve nesra."},
  {id: 5443, sure_no: 24, ayet_no: 24, sure_adi: "Nuh", text: "وَقَدْ اَضَلُّوا كَث۪يراًۚ وَلَا تَزِدِ الظَّالِم۪ينَ اِلَّا ضَلَالاً", translation: "\"Pek çoğunu saptırdılar. Sen de bu zalimlerin şaşkınlıklarından başka bir şeylerini artırma!\"", transliteration: "Ve kad edallu kesira, ve la tezidiz zalimine illa dalala."},
  {id: 5444, sure_no: 25, ayet_no: 25, sure_adi: "Nuh", text: "مِمَّا خَط۪ٓيـَٔاتِهِمْ اُغْرِقُوا فَاُدْخِلُوا نَاراً فَلَمْ يَجِدُوا لَهُمْ مِنْ دُونِ اللّٰهِ اَنْصَاراً", translation: "Onlar, yanlışları nedeniyle suda boğuldular. Sonra ateşe atıldılar. Kendilerine Allah'tan başka yardımcı bulamadılar.", transliteration: "Mimma hatiatihim ugriku fe udhılu naran fe lem yecıdu lehum min dunillahi ensara."},
  {id: 5445, sure_no: 26, ayet_no: 26, sure_adi: "Nuh", text: "وَقَالَ نُوحٌ رَبِّ لَا تَذَرْ عَلَى الْاَرْضِ مِنَ الْكَافِر۪ينَ دَيَّاراً", translation: "Nuh: \"Rabb'im! O Kafirlerden yeryüzünde hiç kimseyi bırakma.\" dedi.", transliteration: "Ve kale nuhun rabbi la tezer alel ardı minel kafirine deyyara."},
  {id: 5446, sure_no: 27, ayet_no: 27, sure_adi: "Nuh", text: "اِنَّكَ اِنْ تَذَرْهُمْ يُضِلُّوا عِبَادَكَ وَلَا يَلِدُٓوا اِلَّا فَاجِراً كَفَّاراً", translation: "\"Çünkü onları bırakınca, kullarını saptırıyorlar; yalnızca facir ve Kafir kimseler yetiştiriyorlar.\"", transliteration: "İnneke in tezerhum yudıllu ıbadeke ve la yelidu illa faciren keffara."},
  {id: 5447, sure_no: 28, ayet_no: 28, sure_adi: "Nuh", text: "رَبِّ اغْفِرْ ل۪ي وَلِوَالِدَيَّ وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِناً وَلِلْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِۜ وَلَا تَزِدِ الظَّالِم۪ينَ اِلَّا تَبَاراً", translation: "\"Rabb'im! Beni, annemi, babamı ve Mü'min olarak evime girenleri ve Mü'min erkekleri ve Mü'min kadınları bağışla. Zalimlerin yalnızca tükenişlerini arttır.\"", transliteration: "Rabbigfirli ve li valideyye ve li men dehale beytiye mu'minen ve lil mu'minine vel mu'minat ve la tezidiz zalimine illa tebara."},
  {id: 5448, sure_no: 1, ayet_no: 1, sure_adi: "Cin", text: "قُلْ اُو۫حِيَ اِلَيَّ اَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقَالُٓوا اِنَّا سَمِعْنَا قُرْاٰناً عَجَباًۙ", translation: "De ki: \"Bana, cinlerden bir topluluğun dinledikten sonra gidip; biz gerçekten hayranlık uyandıran bir kur'an dinledik, dedikleri, vahyolundu.\"", transliteration: "Kul uhıye ileyye ennehustemea neferun minel cinni fe kalu inna semi'na kur'anen aceba."},
  {id: 5449, sure_no: 2, ayet_no: 2, sure_adi: "Cin", text: "يَهْد۪ٓي اِلَى الرُّشْدِ فَاٰمَنَّا بِه۪ۜ وَلَنْ نُشْرِكَ بِرَبِّنَٓا اَحَداًۙ", translation: "\"Her konuda doğru yola iletiyor; ona iman ettik. Artık kesinlikle Rabb'imize hiçbir şeyi ortak koşmayacağız.\"", transliteration: "Yehdi iler ruşdi fe amenna bih, ve len nuşrike bi rabbina ehada."},
  {id: 5450, sure_no: 3, ayet_no: 3, sure_adi: "Cin", text: "وَاَنَّهُ تَعَالٰى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَلَا وَلَداًۙ", translation: "\"Rabb'imizin şanı çok yücedir. O, asla eş ve çocuk edinmemiştir.\"", transliteration: "Ve ennehu teala ceddu rabbina mettehaze sahıbeten ve la veleda."},
  {id: 5451, sure_no: 4, ayet_no: 4, sure_adi: "Cin", text: "وَاَنَّهُ  كَانَ يَقُولُ سَف۪يهُنَا عَلَى اللّٰهِ شَطَطاًۙ", translation: "\"Meğer bizim \"beyinsiz,\" Allah hakkında saçma şeyler söylüyormuş.\"", transliteration: "Ve ennehu kane yekulu sefihuna alallahi şetata."},
  {id: 5452, sure_no: 5, ayet_no: 5, sure_adi: "Cin", text: "وَاَنَّا ظَنَنَّٓا اَنْ لَنْ تَقُولَ الْاِنْسُ وَالْجِنُّ عَلَى اللّٰهِ كَذِباًۙ", translation: "\"Doğrusu biz insin ve cinnin Allah'a karşı asla yalan söylemeyeceklerine inanıyorduk.\"", transliteration: "Ve enna zanenna en len tekulel insu vel cinnu alallahi keziba."},
  {id: 5453, sure_no: 6, ayet_no: 6, sure_adi: "Cin", text: "وَاَنَّهُ كَانَ رِجَالٌ مِنَ الْاِنْسِ يَعُوذُونَ بِرِجَالٍ مِنَ الْجِنِّ فَزَادُوهُمْ رَهَقاًۙ", translation: "Gerçekten de insten bazı adamlar, cinden bazı adamlara sığınıyorlardı. Böylece onların azgınlıklarını, beyinsizliklerini artırıyorlardı.", transliteration: "Ve ennehu kane ricalun minel insi yeuzune bi ricalin minel cinni fe zaduhum reheka."},
  {id: 5454, sure_no: 7, ayet_no: 7, sure_adi: "Cin", text: "وَاَنَّهُمْ ظَنُّوا كَمَا ظَنَنْتُمْ اَنْ لَنْ يَبْعَثَ اللّٰهُ اَحَداًۙ", translation: "Gerçekten de onlar sizin sandığınız gibi, Allah'ın hiç kimseyi diriltmeyeceğini sanmışlardı.", transliteration: "Ve ennehum zannu kema zanentum en len yeb'asallahu ehada."},
  {id: 5455, sure_no: 8, ayet_no: 8, sure_adi: "Cin", text: "وَاَنَّا لَمَسْنَا السَّمَٓاءَ فَوَجَدْنَاهَا مُلِئَتْ حَرَساً شَد۪يداً وَشُهُباًۙ", translation: "\"Gerçekten de biz göğe erişmek istedik, fakat onun zorlu bekçiler ve ışınlarla doldurulmuş olduğunu gördük.\"", transliteration: "Ve enna le mesnes semae fe vecednaha muliet haresen şediden ve şuhuba."},
  {id: 5456, sure_no: 9, ayet_no: 9, sure_adi: "Cin", text: "وَاَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِۜ فَمَنْ يَسْتَمِعِ الْاٰنَ يَجِدْ لَهُ شِهَاباً رَصَداًۙ", translation: "\"Biz, dinlemek için oturma yerlerine otururduk. Fakat şimdi kim dinlemek isterse, kendisini gözetleyen bir ışın buluyor.\"", transliteration: "Ve enna kunna nak'udu minha mekaıde lis sem'i fe men yestemiıl ane yecid lehu şihaben rasada."},
  {id: 5457, sure_no: 10, ayet_no: 10, sure_adi: "Cin", text: "وَاَنَّا لَا نَدْر۪ٓي اَشَرٌّ اُر۪يدَ بِمَنْ فِي الْاَرْضِ اَمْ اَرَادَ بِهِمْ رَبُّهُمْ رَشَداًۙ", translation: "\"Gerçekten bilmiyoruz. Yeryüzünde olan kimselere kötülük mü istendi yoksa Rabb'leri onlara bir iyilik mi istedi bilmiyoruz.\"", transliteration: "Ve enna la nedri eşerrun uride bi men fil ardı em erade bi him rabbuhum reşeda."},
  {id: 5458, sure_no: 11, ayet_no: 11, sure_adi: "Cin", text: "وَاَنَّا مِنَّا الصَّالِحُونَ وَمِنَّا دُونَ ذٰلِكَۜ كُنَّا طَرَٓائِقَ قِدَداًۙ", translation: "\"Doğrusu bir kısmımız salihlerdeniz, bir kısmımız da bunun dışındadır. Biz, ayrı ayrı yollar tuttuk.\"", transliteration: "Ve enna minnes salihune ve minna dune zalik, kunna taraika kıdeda."},
  {id: 5459, sure_no: 12, ayet_no: 12, sure_adi: "Cin", text: "وَاَنَّا ظَنَنَّٓا اَنْ لَنْ نُعْجِزَ اللّٰهَ فِي الْاَرْضِ وَلَنْ نُعْجِزَهُ هَرَباًۙ", translation: "\"Doğrusu biz, yeryüzünde Allah'ı asla aciz bırakamayacağımızı; kaçmakla da O'ndan asla kurtulamayacağımızı anladık.\"", transliteration: "Ve enna zanenna en len nu'cizallahe fil ardı ve len nu'cizehu hereba."},
  {id: 5460, sure_no: 13, ayet_no: 13, sure_adi: "Cin", text: "وَاَنَّا لَمَّا سَمِعْنَا الْهُدٰٓى اٰمَنَّا بِه۪ۜ فَمَنْ يُؤْمِنْ بِرَبِّه۪ فَلَا يَخَافُ بَخْساً وَلَا رَهَقاًۙ", translation: "\"Yol gösteren rehberi dinlediğimizde ona inandık. Artık kim Rabb'ine iman ederse, bundan sonra hakkının verilmemesinden veya haksızlığa uğramaktan endişe etmez.\"", transliteration: "Ve enna lemma semi'nel huda amenna bih, fe men yu'min bi rabbihi fe la yehafu bahsen ve la reheka."},
  {id: 5461, sure_no: 14, ayet_no: 14, sure_adi: "Cin", text: "وَاَنَّا مِنَّا الْمُسْلِمُونَ وَمِنَّا الْقَاسِطُونَۜ  فَمَنْ اَسْلَمَ فَاُو۬لٰٓئِكَ تَحَرَّوْا رَشَداً", translation: "\"Bizden, Allah'a teslim olanlar da var, asilik edip kendilerine haksızlık yapanlar da. Allah'a teslim olanlar, gerçeği arayanlardır.\"", transliteration: "Ve enna minnel muslimune ve minnel kasitun, fe men esleme fe ulaike teharrev reşeda."},
  {id: 5462, sure_no: 15, ayet_no: 15, sure_adi: "Cin", text: "وَاَمَّا الْقَاسِطُونَ فَكَانُوا لِجَهَنَّمَ حَطَباًۙ", translation: "Asilik edip kendilerine haksızlık yapanlar, işte onlar Cehennem'e odun oldular.", transliteration: "Ve emmel kasitune fe kanu li cehenneme hataba."},
  {id: 5463, sure_no: 16, ayet_no: 16, sure_adi: "Cin", text: "وَاَنْ لَوِ اسْتَقَامُوا عَلَى الطَّر۪يقَةِ لَاَسْقَيْنَاهُمْ مَٓاءً غَدَقاًۙ", translation: "Eğer onlar, yolda dosdoğru gitselerdi, onları kesinlikle bol su ile sulardık ki;", transliteration: "Ve en levistekamu alet tarikati le eskaynahum maen gadeka."},
  {id: 5464, sure_no: 17, ayet_no: 17, sure_adi: "Cin", text: "لِنَفْتِنَهُمْ ف۪يهِۚ وَمَنْ يُعْرِضْ عَنْ ذِكْرِ رَبِّه۪ يَسْلُكْهُ عَذَاباً صَعَداًۙ", translation: "Onları bu nimetlerle sınardık. Kim Rabb'inin öğüdünden yüz çevirirse, O da onu çok şiddetli bir azaba uğratır.", transliteration: "Li neftinehum fih, ve men yu'rıd an zikri rabbihi yeslukhu azaben saada."},
  {id: 5465, sure_no: 18, ayet_no: 18, sure_adi: "Cin", text: "وَاَنَّ الْمَسَاجِدَ لِلّٰهِ فَلَا تَدْعُوا مَعَ اللّٰهِ اَحَداًۙ", translation: "Mescitler Allah içindir. O halde Allah'ın yanı sıra başka bir kimseye çağrıda bulunmayın.", transliteration: "Ve ennel mesacide lillahi fe la ted'u maallahi ehada."},
  {id: 5466, sure_no: 19, ayet_no: 19, sure_adi: "Cin", text: "وَاَنَّهُ لَمَّا قَامَ عَبْدُ اللّٰهِ يَدْعُوهُ كَادُوا يَكُونُونَ عَلَيْهِ لِبَداًۜ۟", translation: "O Allah'ın kulu, O'na çağrıda bulunmaya kalktığı zaman, neredeyse çevresinde keçe gibi oluyorlardı.", transliteration: "Ve ennehu lemma kame abdullahi yeduhu kadu yekunune aleyhi libeda."},
  {id: 5467, sure_no: 20, ayet_no: 20, sure_adi: "Cin", text: "قُلْ اِنَّمَٓا اَدْعُوا رَبّ۪ي وَلَٓا اُشْرِكُ بِه۪ٓ اَحَداً", translation: "De ki: \"Ben yalnızca Rabb'ime dua ederim ve hiçbir şeyi O'na şirk koşmam.\"", transliteration: "Kul innema ed'u rabbi ve la uşriku bihi ehada."},
  {id: 5468, sure_no: 21, ayet_no: 21, sure_adi: "Cin", text: "قُلْ اِنّ۪ي لَٓا اَمْلِكُ لَكُمْ ضَراًّ وَلَا رَشَداً", translation: "De ki: \"Ben, size bir zarar verme ve sizi irşad etme gücüne sahip değilim.\"", transliteration: "Kul inni la emliku lekum darren ve la reşeda."},
  {id: 5469, sure_no: 22, ayet_no: 22, sure_adi: "Cin", text: "قُلْ اِنّ۪ي لَنْ يُج۪يرَن۪ي مِنَ اللّٰهِ اَحَدٌ وَلَنْ اَجِدَ مِنْ دُونِه۪ مُلْتَحَداًۙ", translation: "De ki: \"Beni, Allah'a karşı hiç kimse koruyamaz ve ben asla O'ndan başka sığınacak bir yer bulamam.\"", transliteration: "Kul inni len yucireni minallahi ehadun ve len ecide min dunihi multehada."},
  {id: 5470, sure_no: 23, ayet_no: 23, sure_adi: "Cin", text: "اِلَّا بَلَاغاً مِنَ اللّٰهِ وَرِسَالَاتِه۪ۜ وَمَنْ يَعْصِ اللّٰهَ وَرَسُولَهُ فَاِنَّ لَهُ نَارَ جَهَنَّمَ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ", translation: "\"Bana düşen sadece Allah'tan aldığım vahyi size duyurmak ve gönderileni iletmektir. Kim Allah'a ve O'nun Resul'üne karşı gelirse, bilsin ki onun için kesintisiz olarak sürekli içinde kalacağı Cehennem ateşi vardır.\"", transliteration: "İlla belagan minallahi ve risalatih, ve men ya'sıllahe ve resulehu fe inne lehu nare cehenneme halidine fiha ebeda."},
  {id: 5471, sure_no: 24, ayet_no: 24, sure_adi: "Cin", text: "حَتّٰٓى اِذَا رَاَوْا مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ اَضْعَفُ نَاصِراً وَاَقَلُّ عَدَداً", translation: "Uyarıldıkları şeyi gördükleri zaman, kimin yardımcısının daha zayıf ve kimsesiz olduğunu bilecekler.", transliteration: "Hatta iza reev ma yuadune fe se ya'lemune men ad'afu nasıren ve ekallu adeda."},
  {id: 5472, sure_no: 25, ayet_no: 25, sure_adi: "Cin", text: "قُلْ اِنْ اَدْر۪ٓي اَقَر۪يبٌ مَا تُوعَدُونَ اَمْ يَجْعَلُ لَهُ رَبّ۪ٓي اَمَداً", translation: "De ki: \"Uyarıldığınız şey yakın mıdır, yoksa Rabb'im onun için uzun bir süre mi belirlemiştir, bilmiyorum.\"", transliteration: "Kul in edri e karibun ma tuadune em yec'alu lehu rabbi emeda."},
  {id: 5473, sure_no: 26, ayet_no: 26, sure_adi: "Cin", text: "عَالِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلٰى غَيْبِه۪ٓ اَحَداًۙ", translation: "O, gaybı bilendir. Ve O, gaybını hiç kimseye bildirmez;", transliteration: "Alimul gaybi fe la yuzhiru ala gaybihi ehada."},
  {id: 5474, sure_no: 27, ayet_no: 27, sure_adi: "Cin", text: "اِلَّا مَنِ ارْتَضٰى مِنْ رَسُولٍ فَاِنَّهُ يَسْلُكُ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِه۪ رَصَداًۙ", translation: "Resullerden razı oldukları hariç. O, onların her tarafından gözetleyiciler gönderir;", transliteration: "İlla menirteda min resulin fe innehu yesluku min beyni yedeyhi ve min halfihi rasada."},
  {id: 5475, sure_no: 28, ayet_no: 28, sure_adi: "Cin", text: "لِيَعْلَمَ اَنْ قَدْ اَبْلَغُوا رِسَالَاتِ رَبِّهِمْ وَاَحَاطَ بِمَا لَدَيْهِمْ وَاَحْصٰى كُلَّ شَيْءٍ عَدَداً", translation: "Rabb'lerinin mesajlarını tebliğ ettiklerini bilmek için. Yanlarında olanları kuşatmış ve her şeyi kayıt altına almıştır.", transliteration: "Li ya'leme en kad eblegu rısalati rabbihim ve ehata bima ledeyhim ve ahsa kulle şey'in adeda."},
  {id: 5476, sure_no: 1, ayet_no: 1, sure_adi: "Müzzemmil", text: "يَٓا اَيُّهَا الْمُزَّمِّلُۙ", translation: "Ey örtüsüne bürünen!", transliteration: "Ya eyyuhel muzzemmil."},
  {id: 5477, sure_no: 2, ayet_no: 2, sure_adi: "Müzzemmil", text: "قُمِ الَّيْلَ اِلَّا قَل۪يلاًۙ", translation: "Gecenin birazında kalk!", transliteration: "Kumil leyle illa kalila."},
  {id: 5478, sure_no: 3, ayet_no: 3, sure_adi: "Müzzemmil", text: "نِصْفَهُٓ  اَوِ انْقُصْ مِنْهُ قَل۪يلاًۙ", translation: "Gecenin yarısında veya ondan biraz önce.", transliteration: "Nısfehu evinkus minhu kalila."},
  {id: 5479, sure_no: 4, ayet_no: 4, sure_adi: "Müzzemmil", text: "اَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْاٰنَ تَرْت۪يلاًۜ", translation: "Veya gecenin yarısından biraz sonra. Ve daveti düzgün bir şekilde duyur.", transliteration: "Ey zid aleyhi ve rettilil kur'ane tertila."},
  {id: 5480, sure_no: 5, ayet_no: 5, sure_adi: "Müzzemmil", text: "اِنَّا سَنُلْق۪ي عَلَيْكَ قَوْلاً ثَق۪يلاًۜ", translation: "Biz, sana ağır bir söz bırakacağız.", transliteration: "İnna se nulki aleyke kavlen sekila."},
  {id: 5481, sure_no: 6, ayet_no: 6, sure_adi: "Müzzemmil", text: "اِنَّ نَاشِئَةَ الَّيْلِ هِيَ اَشَدُّ وَطْـٔاً وَاَقْوَمُ ق۪يلاًۜ", translation: "Kuşkusuz gece, bir oluşu gerçekleştirmek için söz bakımından daha etkilidir.", transliteration: "İnne naşietel leyli hiye eşeddu vat'en ve akvemu kila."},
  {id: 5482, sure_no: 7, ayet_no: 7, sure_adi: "Müzzemmil", text: "اِنَّ لَكَ فِي النَّهَارِ سَبْحاً طَو۪يلاًۜ", translation: "Gündüz, uzun süre uğraşacağın işler var.", transliteration: "İnne leke fin nehari sebhan tavila."},
  {id: 5483, sure_no: 8, ayet_no: 8, sure_adi: "Müzzemmil", text: "وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ اِلَيْهِ تَبْت۪يلاًۜ", translation: "Rabb'inin adını an ve bütün benliğinle yalnızca O'na yönel.", transliteration: "Vezkurisme rabbike ve tebettel ileyhi tebtila."},
  {id: 5484, sure_no: 9, ayet_no: 9, sure_adi: "Müzzemmil", text: "رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَٓا اِلٰهَ اِلَّا هُوَ فَاتَّخِذْهُ وَك۪يلاً", translation: "O, doğunun ve batının Rabb'idir. O'ndan başka ilah yoktur. Öyleyse yalnız O'nu vekil edin.", transliteration: "Rabbul meşrıkı vel magribi la ilahe illa huve fettehızhu vekila."},
  {id: 5485, sure_no: 10, ayet_no: 10, sure_adi: "Müzzemmil", text: "وَاصْبِرْ عَلٰى مَا يَقُولُونَ وَاهْجُرْهُمْ هَجْراً جَم۪يلاً", translation: "Onların söylediklerine sabret. Ve onlardan güzellikle uzak dur.", transliteration: "Vasbir ala ma yekulune vehcurhum hecren cemila."},
  {id: 5486, sure_no: 11, ayet_no: 11, sure_adi: "Müzzemmil", text: "وَذَرْن۪ي وَالْمُكَذِّب۪ينَ اُو۬لِي النَّعْمَةِ وَمَهِّلْهُمْ قَل۪يلاً", translation: "Varlık sahibi, yalanlayıcıları Bana bırak. Onlara biraz süre tanı.", transliteration: "Ve zerni vel mukezzibine ulin na'meti ve mehhilhum kalila."},
  {id: 5487, sure_no: 12, ayet_no: 12, sure_adi: "Müzzemmil", text: "اِنَّ لَدَيْنَٓا اَنْكَالاً وَجَح۪يماًۙ", translation: "Bizim yanımızda prangalar ve Cehennem var.", transliteration: "İnne ledeyna enkalen ve cahima."},
  {id: 5488, sure_no: 13, ayet_no: 13, sure_adi: "Müzzemmil", text: "وَطَعَاماً ذَا غُصَّةٍ وَعَذَاباً اَل۪يماً", translation: "Ve boğazı tıkayan yiyecek ve acı bir azap vardır.", transliteration: "Ve taamen za gussatin ve azaben elima."},
  {id: 5489, sure_no: 14, ayet_no: 14, sure_adi: "Müzzemmil", text: "يَوْمَ تَرْجُفُ الْاَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَث۪يباً مَه۪يلاً", translation: "O Gün yeryüzü ve dağlar şiddetle sarsılır ve dağlar dağılmış kum yığınları gibi olur.", transliteration: "Yevme tercuful ardu vel cibalu ve kanetil cibalu kesiben mehila."},
  {id: 5490, sure_no: 15, ayet_no: 15, sure_adi: "Müzzemmil", text: "اِنَّٓا اَرْسَلْنَٓا اِلَيْكُمْ رَسُولاً شَاهِداً عَلَيْكُمْ كَمَٓا اَرْسَلْنَٓا اِلٰى فِرْعَوْنَ رَسُولاًۜ", translation: "Size tanıklık edecek bir Resul gönderdik. Tıpkı Firavun'a da bir Resul gönderdiğimiz gibi.", transliteration: "İnna erselna ileykum resulen şahiden aleykum kema erselna ila fir'avne resula."},
  {id: 5491, sure_no: 16, ayet_no: 16, sure_adi: "Müzzemmil", text: "فَعَصٰى فِرْعَوْنُ الرَّسُولَ فَاَخَذْنَاهُ اَخْذاً وَب۪يلاً", translation: "Firavun Resul'e isyan etti. Bunun üzerine onu çok zorlu bir tutuşla tutuverdik.", transliteration: "Fe asa fir'avnur resule fe ehaznahu ahzen vebila."},
  {id: 5492, sure_no: 17, ayet_no: 17, sure_adi: "Müzzemmil", text: "فَـكَيْفَ تَتَّقُونَ اِنْ كَفَرْتُمْ يَوْماً يَجْعَلُ الْوِلْدَانَ ش۪يباًۗ", translation: "Eğer Kafirlerden olursanız, çocukların saçlarını ağartan o günden kendinizi nasıl koruyacaksınız?", transliteration: "Fe keyfe tettekune in kefertum yevmen yec'alul vildane şiba."},
  {id: 5493, sure_no: 18, ayet_no: 18, sure_adi: "Müzzemmil", text: "اَلسَّمَٓاءُ مُنْفَطِرٌ بِه۪ۜ   كَانَ وَعْدُهُ مَفْعُولاً", translation: "Gök, o günün şiddeti ile çatlayıp parçalanacak ve O'nun uyarısı gerçekleşecektir.", transliteration: "Es semau munfatırun bih, kane va'duhu mef'ula."},
  {id: 5494, sure_no: 19, ayet_no: 19, sure_adi: "Müzzemmil", text: "اِنَّ هٰذِه۪ تَذْكِرَةٌۚ  فَمَنْ شَٓاءَ اتَّخَذَ اِلٰى رَبِّه۪ سَب۪يلاً۟", translation: "Bu bir öğüttür. Dileyen Rabb'ine götüren bir yol edinir.", transliteration: "İnne hazihi tezkirah, fe men şaettehaze ila rabbihi sebila."},
  {id: 5495, sure_no: 20, ayet_no: 20, sure_adi: "Müzzemmil", text: "اِنَّ رَبَّكَ يَعْلَمُ اَنَّكَ تَقُومُ اَدْنٰى مِنْ ثُلُثَيِ الَّيْلِ وَنِصْفَهُ وَثُلُثَهُ وَطَٓائِفَةٌ مِنَ الَّذ۪ينَ مَعَكَۜ وَاللّٰهُ يُقَدِّرُ الَّيْلَ وَالنَّهَارَۜ عَلِمَ اَنْ لَنْ تُحْصُوهُ فَتَابَ عَلَيْكُمْ فَاقْرَؤُ۫ا مَا تَيَسَّرَ مِنَ الْقُرْاٰنِۜ عَلِمَ اَنْ سَيَكُونُ مِنْكُمْ مَرْضٰىۙ وَاٰخَرُونَ يَضْرِبُونَ فِي الْاَرْضِ يَبْتَغُونَ مِنْ فَضْلِ اللّٰهِۙ وَاٰخَرُونَ يُقَاتِلُونَ ف۪ي سَب۪يلِ اللّٰهِۘ فَاقْرَؤُ۫ا مَا تَيَسَّرَ مِنْهُۙ وَاَق۪يمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَقْرِضُوا اللّٰهَ قَرْضاً حَسَناًۜ وَمَا تُقَدِّمُوا لِاَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللّٰهِ هُوَ خَيْراً وَاَعْظَمَ اَجْراًۜ وَاسْتَغْفِرُوا اللّٰهَۜ  اِنَّ اللّٰهَ غَفُورٌ رَح۪يمٌ", translation: "Rabb'in, senin bazen gecenin üçte ikisinden daha az, bazen yarısı, bazen üçte bir kadar vakit geçirdiğini elbette biliyor. Seninle beraber bulunanlardan bir grubun da. Geceyi ve gündüzü takdir eden Allah'tır. Onu asla hesaplayamayacağınızı bildi de tevbenizi kabul etti. O halde İlahi mesajı gücünüz yettiğince insanlara ulaştırmaya çalışın. Ayrıca Allah, içinizden kimin hasta olduğunu bilmektedir. Kiminizin Allah'ın lütfundan rızkını aramak için yeryüzünde çalışmaya, kiminizin de kendisini Allah yolunda feda etmeye çıktığını bilmektedir. O halde gücünüz yettiğince onu nakletmeye, duyurmaya çalışın. Salatı ikame edin, zekatı yapın. İyi bir ödünçle Allah'a ödünç verin. Kendiniz için hayır olarak ne verirseniz, ödül olarak Allah'ın katında onu daha hayırlısıyla ve daha büyüğüyle bulursunuz. Allah'tan bağışlanma dileyin. Kuşkusuz Allah, Çok Bağışlayıcı'dır, Rahmeti Kesintisiz'dir.", transliteration: "İnne rabbeke ya'lemu enneke tekumu edna min suluseyil leyli ve nısfehu ve sulusehu ve taifetun minellezine meak, vallahu yukaddirul leyle ven nehar, alime en len tuhsuhu fe tabe aleykum, fakreu ma teyessere minel kur'an, alime en seyekunu minkum merda ve aharune yadribune fil'ardı yebtegune min fadlillahi ve aharune yukatilune fi sebilillahi fakreu ma teyessere minhu ve ekimus salate ve atuz zekate ve akridullahe kardan hasena, ve ma tukaddimu li enfusikum min hayrin teciduhu indallahi huve hayren ve a'zame ecra, vestagfirullah, innellahe gafurun rahim."},
  {id: 5496, sure_no: 1, ayet_no: 1, sure_adi: "Müddessir", text: "يَٓا اَيُّهَا الْمُدَّثِّرُۙ", translation: "Ey örtüye bürünmüş olan.", transliteration: "Ya eyyuhel muddessir."},
  {id: 5497, sure_no: 2, ayet_no: 2, sure_adi: "Müddessir", text: "قُمْ فَاَنْذِرْۙ", translation: "Kalk ve uyarmaya başla.", transliteration: "Kum fe enzir."},
  {id: 5498, sure_no: 3, ayet_no: 3, sure_adi: "Müddessir", text: "وَرَبَّكَ فَـكَبِّرْۙ", translation: "Rabb'ini tekbir et!", transliteration: "Ve rabbeke fe kebbir."},
  {id: 5499, sure_no: 4, ayet_no: 4, sure_adi: "Müddessir", text: "وَثِيَابَكَ فَطَهِّرْۙ", translation: "Ve örtünü hemen temizle.", transliteration: "Ve siyabeke fe tahhir."},
  {id: 5500, sure_no: 5, ayet_no: 5, sure_adi: "Müddessir", text: "وَالرُّجْزَ فَاهْجُرْۙ", translation: "Kirlilikten kaçın.", transliteration: "Verrucze fehcur."},
  {id: 5501, sure_no: 6, ayet_no: 6, sure_adi: "Müddessir", text: "وَلَا تَمْنُنْ تَسْتَكْثِرُۙ", translation: "Karşılık bekleyerek iyilik yapma.", transliteration: "Ve la temnun testeksir."},
  {id: 5502, sure_no: 7, ayet_no: 7, sure_adi: "Müddessir", text: "وَلِرَبِّكَ فَاصْبِرْۜ", translation: "Rabb'in için sabret.", transliteration: "Ve li rabbike fasbir."},
  {id: 5503, sure_no: 8, ayet_no: 8, sure_adi: "Müddessir", text: "فَاِذَا نُقِرَ فِي النَّاقُورِۙ", translation: "Nakur'a üflendiği zaman.", transliteration: "Fe iza nukıre fin naku."},
  {id: 5504, sure_no: 9, ayet_no: 9, sure_adi: "Müddessir", text: "فَذٰلِكَ يَوْمَئِذٍ يَوْمٌ عَس۪يرٌۙ", translation: "İşte O Gün, çok zorlu bir gündür;", transliteration: "Fe zalike yevme izin yevmun asi."},
  {id: 5505, sure_no: 10, ayet_no: 10, sure_adi: "Müddessir", text: "عَلَى الْـكَافِر۪ينَ غَيْرُ يَس۪يرٍ", translation: "Kafirler için kolay olmayan.", transliteration: "Alel kafirine gayru yesir."},
  {id: 5506, sure_no: 11, ayet_no: 11, sure_adi: "Müddessir", text: "ذَرْن۪ي وَمَنْ خَلَقْتُ وَح۪يداًۙ", translation: "Tek olarak yarattığım kişiyi Bana bırak.", transliteration: "Zerni ve men halaktu vahida."},
  {id: 5507, sure_no: 12, ayet_no: 12, sure_adi: "Müddessir", text: "وَجَعَلْتُ لَهُ مَالاً مَمْدُوداًۙ", translation: "Ona hesapsız mal verdim;", transliteration: "Ve ce'altu lehu malen memduda."},
  {id: 5508, sure_no: 13, ayet_no: 13, sure_adi: "Müddessir", text: "وَبَن۪ينَ شُهُوداًۙ", translation: "Ve her zaman yanında olan evlatlar.", transliteration: "Ve benine şuhuda."},
  {id: 5509, sure_no: 14, ayet_no: 14, sure_adi: "Müddessir", text: "وَمَهَّدْتُ لَهُ تَمْه۪يداًۙ", translation: "Ona, geniş imkanlar sağladım.", transliteration: "Ve mehhedtu lehu temhida."},
  {id: 5510, sure_no: 15, ayet_no: 15, sure_adi: "Müddessir", text: "ثُمَّ يَطْمَعُ اَنْ اَز۪يدَۗ", translation: "Sonra hırsla daha da artırmamı ister.", transliteration: "Summe yatmau en ezid."},
  {id: 5511, sure_no: 16, ayet_no: 16, sure_adi: "Müddessir", text: "كَلَّاۜ اِنَّهُ كَانَ لِاٰيَاتِنَا عَن۪يداًۜ", translation: "Hayır, asla! O Biz'im ayetlerimize karşı inat etti.", transliteration: "Kella, innehu kane li ayatina anida."},
  {id: 5512, sure_no: 17, ayet_no: 17, sure_adi: "Müddessir", text: "سَاُرْهِقُهُ صَعُوداًۜ", translation: "Onu sarp bir yokuşa sardıracağım.", transliteration: "Se urhikuhu sauda."},
  {id: 5513, sure_no: 18, ayet_no: 18, sure_adi: "Müddessir", text: "اِنَّهُ فَـكَّرَ وَقَدَّرَۙ", translation: "Düşündü ve karar verdi.", transliteration: "İnnehu fekkere ve kadder."},
  {id: 5514, sure_no: 19, ayet_no: 19, sure_adi: "Müddessir", text: "فَقُتِلَ كَيْفَ قَدَّرَۙ", translation: "O mahvoldu, nasıl bir karar verdi!", transliteration: "Fe kutile keyfe kadder."},
  {id: 5515, sure_no: 20, ayet_no: 20, sure_adi: "Müddessir", text: "ثُمَّ قُتِلَ كَيْفَ قَدَّرَۙ", translation: "Yine o mahvoldu. Nasıl bir karar verdi!", transliteration: "Summe kutile keyfe kadder."},
  {id: 5516, sure_no: 21, ayet_no: 21, sure_adi: "Müddessir", text: "ثُمَّ نَظَرَۙ", translation: "Sonra baktı!", transliteration: "Summe nazar."},
  {id: 5517, sure_no: 22, ayet_no: 22, sure_adi: "Müddessir", text: "ثُمَّ عَبَسَ وَبَسَرَۙ", translation: "Sonra kaşlarını çatıp surat astı!", transliteration: "Summe abese ve beser."},
  {id: 5518, sure_no: 23, ayet_no: 23, sure_adi: "Müddessir", text: "ثُمَّ اَدْبَرَ وَاسْتَكْبَرَۙ", translation: "Sonra kibirlendi ve arkasını döndü!", transliteration: "Summe edbere vestekber."},
  {id: 5519, sure_no: 24, ayet_no: 24, sure_adi: "Müddessir", text: "فَقَالَ اِنْ هٰذَٓا اِلَّا سِحْرٌ يُؤْثَرُۙ", translation: "\"Bu, rivayetle gelen bir büyüden başka bir şey değil.\" dedi.", transliteration: "Fe kale in haza illa sihrun yu'ser."},
  {id: 5520, sure_no: 25, ayet_no: 25, sure_adi: "Müddessir", text: "اِنْ هٰذَٓا اِلَّا قَوْلُ الْبَشَرِۜ", translation: "\"Bu, olsa olsa ancak bir beşer sözüdür!\"", transliteration: "İn haza illa kavlul beşer."},
  {id: 5521, sure_no: 26, ayet_no: 26, sure_adi: "Müddessir", text: "سَاُصْل۪يهِ سَقَرَ", translation: "Onu Sakar'a sokacağım.", transliteration: "Se uslihi sekar."},
  {id: 5522, sure_no: 27, ayet_no: 27, sure_adi: "Müddessir", text: "وَمَٓا اَدْرٰيكَ مَا سَقَرُۜ", translation: "Sakar'ın ne olduğunu sana bildiren nedir?", transliteration: "Ve ma edrake ma sekar."},
  {id: 5523, sure_no: 28, ayet_no: 28, sure_adi: "Müddessir", text: "لَا تُبْق۪ي وَلَا تَذَرُۚ", translation: "O bırakmaz ve terk etmez!", transliteration: "La tubki ve la tezer."},
  {id: 5524, sure_no: 29, ayet_no: 29, sure_adi: "Müddessir", text: "لَـوَّاحَةٌ لِلْبَشَرِۚ", translation: "Beşeri yakıp kavurucudur!", transliteration: "Levvahatun lil beşer."},
  {id: 5525, sure_no: 30, ayet_no: 30, sure_adi: "Müddessir", text: "عَلَيْهَا تِسْعَةَ عَشَرَۜ", translation: "Onun üzerinedir on dokuz!", transliteration: "Aleyha tis'ate aşer."},
  {id: 5526, sure_no: 31, ayet_no: 31, sure_adi: "Müddessir", text: "وَمَا جَعَلْنَٓا اَصْحَابَ النَّارِ اِلَّا مَلٰٓئِكَةًۖ وَمَا جَعَلْنَا عِدَّتَهُمْ اِلَّا فِتْنَةً لِلَّذ۪ينَ كَـفَرُواۙ لِيَسْتَيْقِنَ الَّذ۪ينَ اُو۫تُوا الْـكِتَابَ وَيَزْدَادَ الَّذ۪ينَ اٰمَنُٓوا ا۪يمَاناً وَلَا يَرْتَابَ الَّذ۪ينَ اُو۫تُوا الْـكِتَابَ وَالْمُؤْمِنُونَۙ وَلِيَقُولَ الَّذ۪ينَ ف۪ي قُلُوبِهِمْ مَرَضٌ وَالْـكَافِرُونَ مَاذَٓا اَرَادَ اللّٰهُ بِهٰذَا مَثَلاًۜ   كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ يَشَٓاءُ وَيَـهْد۪ي مَنْ يَشَٓاءُۜ وَمَا يَعْلَمُ جُنُودَ رَبِّكَ اِلَّا هُوَۜ  وَمَا هِيَ اِلَّا ذِكْرٰى لِلْبَشَرِ۟", translation: "Cehennem ashabını meleklerden başkasını yapmadık. Onların sayılarını, Kafirler için bir fitneden başka bir şey yapmadık. Kendilerine kitap verilenler; kesin olarak bilsinler, İman Edenler'in imanları artsın. Kendilerine kitap verilmiş iman sahipleri kuşku duymasınlar. Kalplerinde hastalık olanlarla, Kafirler de desinler ki: \"Allah, bu örnekle ne demek istiyor şimdi?\" İşte böyle, Allah, hak edeni dalalette bırakır, hak edeni doğru yolu gösterir. Rabb'inin ordularını, kendisinden başkası bilmez. Bu, beşer için zikirden başka bir şey değildir.", transliteration: "Ve ma cealna ashaben nari illa melaiketen ve ma cealna ıddetehum illa fitneten lillezine keferu li yesteykınellezine utul kitabe ve yezdadellezine amenu imanen ve la yertabellezine utul kitabe vel mu'minune, ve li yekulellezine fi kulubihim maradun vel kafirune maza eradallahu bi haza mesela, kezalike yudıllullahu men yeşau ve yehdi men yeşa, ve ma ya'lemu cunude rabbike illa hu, ve ma hiye illa zikra lil beşer."},
  {id: 5527, sure_no: 32, ayet_no: 32, sure_adi: "Müddessir", text: "كَلَّا وَالْقَمَرِۙ", translation: "Hayır! Anlayan kim? Ay'a ant olsun;", transliteration: "Kella vel kamer."},
  {id: 5528, sure_no: 33, ayet_no: 33, sure_adi: "Müddessir", text: "وَالَّيْلِ اِذْ اَدْبَرَۙ", translation: "Ve dönüp gelen geceye;", transliteration: "Vel leyli iz edber."},
  {id: 5529, sure_no: 34, ayet_no: 34, sure_adi: "Müddessir", text: "وَالصُّبْحِ اِذَٓا اَسْفَرَۙ", translation: "Ve aydınlatan sabaha.", transliteration: "Ves subhı iza esfer."},
  {id: 5530, sure_no: 35, ayet_no: 35, sure_adi: "Müddessir", text: "اِنَّهَا لَاِحْدَى الْـكُبَرِۙ", translation: "Kuşkusuz o en büyüklerden biridir.", transliteration: "İnneha le ıhdel kuber."},
  {id: 5531, sure_no: 36, ayet_no: 36, sure_adi: "Müddessir", text: "نَذ۪يراً لِلْبَشَرِۙ", translation: "Beşer için uyarıcıdır;", transliteration: "Neziren lil beşer."},
  {id: 5532, sure_no: 37, ayet_no: 37, sure_adi: "Müddessir", text: "لِمَنْ شَٓاءَ مِنْكُمْ اَنْ يَتَقَدَّمَ اَوْ يَتَاَخَّرَۜ", translation: "Sizden, öne geçmek isteyen için de geride kalmak isteyen için de.", transliteration: "Li men şae minkum en yetekaddeme ev yeteahhar."},
  {id: 5533, sure_no: 38, ayet_no: 38, sure_adi: "Müddessir", text: "كُلُّ نَفْسٍ بِمَا كَسَبَتْ رَه۪ينَةٌۙ", translation: "Herkes kazancının karşılığında bir rehinedir.", transliteration: "Kullu nefsin bima kesebet rehineh."},
  {id: 5534, sure_no: 39, ayet_no: 39, sure_adi: "Müddessir", text: "اِلَّٓا اَصْحَابَ الْيَم۪ينِۜۛ", translation: "Ancak \"Ashabı Yemin\" hariç.", transliteration: "İlla ashabel yemin."},
  {id: 5535, sure_no: 40, ayet_no: 40, sure_adi: "Müddessir", text: "ف۪ي جَنَّاتٍۜۛ يَتَسَٓاءَلُونَۙ", translation: "Cennetler içinde soracaklar;", transliteration: "Fi cennat, yetesaelun."},
  {id: 5536, sure_no: 41, ayet_no: 41, sure_adi: "Müddessir", text: "عَنِ الْمُجْرِم۪ينَۙ", translation: "Suçlulara,", transliteration: "Anil mucrimin."},
  {id: 5537, sure_no: 42, ayet_no: 42, sure_adi: "Müddessir", text: "مَا سَلَـكَكُمْ ف۪ي سَقَرَ", translation: "Sizi Sakar'a sürükleyen nedir?", transliteration: "Ma selekekum fi sekar."},
  {id: 5538, sure_no: 43, ayet_no: 43, sure_adi: "Müddessir", text: "قَالُوا لَمْ نَكُ مِنَ الْمُصَلّ۪ينَۙ", translation: "\"Musallin\" den olmadık.\" dediler.", transliteration: "Kalu lem neku minel musallin."},
  {id: 5539, sure_no: 44, ayet_no: 44, sure_adi: "Müddessir", text: "وَلَمْ نَكُ نُطْعِمُ الْمِسْك۪ينَۙ", translation: "\"Miskine yediren değildik.\"", transliteration: "Ve lem neku nut'ımul miskin."},
  {id: 5540, sure_no: 45, ayet_no: 45, sure_adi: "Müddessir", text: "وَكُنَّا نَخُوضُ مَعَ الْخَٓائِض۪ينَۙ", translation: "\"Batıl inançlara dalanlarla beraber biz de dalardık.\"", transliteration: "Ve kunna nehudu maal haidin."},
  {id: 5541, sure_no: 46, ayet_no: 46, sure_adi: "Müddessir", text: "وَكُنَّا نُكَذِّبُ بِيَوْمِ الدّ۪ينِۙ", translation: "\"Din Günü'nü yalanlardık.\"", transliteration: "Ve kunna nukezzibu bi yevmid din."},
  {id: 5542, sure_no: 47, ayet_no: 47, sure_adi: "Müddessir", text: "حَتّٰٓى اَتٰينَا الْيَق۪ينُۜ", translation: "\"Bize yakin gelene kadar.\"", transliteration: "Hatta etanel yakin."},
  {id: 5543, sure_no: 48, ayet_no: 48, sure_adi: "Müddessir", text: "فَمَا تَنْفَعُهُمْ شَفَاعَةُ الشَّافِع۪ينَۜ", translation: "Artık şefaatçilerin şefaati onlara yarar sağlamaz.", transliteration: "Fe ma tenfeuhum şefaatuş şafiin."},
  {id: 5544, sure_no: 49, ayet_no: 49, sure_adi: "Müddessir", text: "فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِض۪ينَۙ", translation: "Onlara ne oluyor ki zikirden yüz çeviriyorlar.", transliteration: "Fe ma lehum anit tezkireti mu'rıdin."},
  {id: 5545, sure_no: 50, ayet_no: 50, sure_adi: "Müddessir", text: "كَاَنَّهُمْ حُمُرٌ مُسْتَنْفِرَةٌۙ", translation: "Sanki ürkmüş yabani eşekler gibidirler;", transliteration: "Ke ennehum humurun mustenfireth."},
  {id: 5546, sure_no: 51, ayet_no: 51, sure_adi: "Müddessir", text: "فَرَّتْ مِنْ قَسْوَرَةٍۜ", translation: "Aslandan kaçan.", transliteration: "Ferret min kasvereh."},
  {id: 5547, sure_no: 52, ayet_no: 52, sure_adi: "Müddessir", text: "بَلْ يُر۪يدُ كُلُّ امْرِئٍ مِنْهُمْ اَنْ يُؤْتٰى صُحُفاً مُنَشَّرَةًۙ", translation: "Hayır! Onların tamamı, kendileri için yazılmış sahifeler gelmesini ister.", transliteration: "Bel yuridu kullumriin minhum en yu'ta suhufen muneşşereh ."},
  {id: 5548, sure_no: 53, ayet_no: 53, sure_adi: "Müddessir", text: "كَلَّاۜ  بَلْ لَا يَخَافُونَ الْاٰخِرَةَۜ", translation: "Hayır! Doğrusu onlar ahiretten korkmuyorlar.", transliteration: "Kella, bel la yuhafunel ahıreh."},
  {id: 5549, sure_no: 54, ayet_no: 54, sure_adi: "Müddessir", text: "كَلَّٓا اِنَّهُ تَذْكِرَةٌۚ", translation: "Hayır! Kuşkusuz, o bir öğüttür.", transliteration: "Kella innehu tezkireh."},
  {id: 5550, sure_no: 55, ayet_no: 55, sure_adi: "Müddessir", text: "فَمَنْ شَٓاءَ ذَكَرَهُۜ", translation: "Dileyen ondan öğüt alır.", transliteration: "Fe men şae zekereh."},
  {id: 5551, sure_no: 56, ayet_no: 56, sure_adi: "Müddessir", text: "وَمَا يَذْكُرُونَ اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُۜ  هُوَ اَهْلُ التَّقْوٰى وَاَهْلُ الْمَغْفِرَةِ", translation: "Allah dilemedikçe onlar öğüt almazlar. O, takvaya ehildir ve bağışlamaya ehildir.", transliteration: "Ve ma yezkurune illa en yeşaallah, huve ehlut takva ve ehlul magfireh."},
  {id: 5552, sure_no: 1, ayet_no: 1, sure_adi: "Kıyame", text: "لَٓا اُقْسِمُ بِيَوْمِ الْقِيٰمَةِۙ", translation: "Hayır, kıyamet gününe yemin ederim.", transliteration: "La uksimu bi yevmil kıyameh."},
  {id: 5553, sure_no: 2, ayet_no: 2, sure_adi: "Kıyame", text: "وَلَٓا اُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ", translation: "Hayır, çok kınayan benliğe yemin ederim.", transliteration: "Ve la uksimu bin nefsil levvameh."},
  {id: 5554, sure_no: 3, ayet_no: 3, sure_adi: "Kıyame", text: "اَيَحْسَبُ الْاِنْسَانُ اَلَّنْ نَجْمَعَ عِظَامَهُۜ", translation: "O insan, kemiklerini asla bir araya getiremeyeceğimizi mi sanıyor?", transliteration: "E yahsebul insanu ellen necmea ıza meh."},
  {id: 5555, sure_no: 4, ayet_no: 4, sure_adi: "Kıyame", text: "بَلٰى قَادِر۪ينَ عَلٰٓى اَنْ نُسَوِّيَ بَنَانَهُ", translation: "Hayır, hayır! Biz, onu parmak uçlarına varıncaya dek yeniden düzenlemeye gücü yetenleriz.", transliteration: "Bela kadirine ala en nusevviye bena neh."},
  {id: 5556, sure_no: 5, ayet_no: 5, sure_adi: "Kıyame", text: "بَلْ يُر۪يدُ الْاِنْسَانُ لِيَفْجُرَ اَمَامَهُۚ", translation: "Aslında o insan, ömrünü mücrim olarak geçirmeyi ister.", transliteration: "Bel yuridul insanu li yefcure emameh."},
  {id: 5557, sure_no: 6, ayet_no: 6, sure_adi: "Kıyame", text: "يَسْـَٔلُ اَيَّانَ يَوْمُ الْقِيٰمَةِۜ", translation: "\"Kıyamet günü ne zamanmış?\" diye sorar.", transliteration: "Yes'elu eyyane yevmul kıyameh."},
  {id: 5558, sure_no: 7, ayet_no: 7, sure_adi: "Kıyame", text: "فَاِذَا بَرِقَ الْبَصَرُۙ", translation: "Gözler, dehşetle kamaştığında.", transliteration: "Fe iza berikal basar."},
  {id: 5559, sure_no: 8, ayet_no: 8, sure_adi: "Kıyame", text: "وَخَسَفَ الْقَمَرُۙ", translation: "Ay, karardığında.", transliteration: "Ve hasefel kamer."},
  {id: 5560, sure_no: 9, ayet_no: 9, sure_adi: "Kıyame", text: "وَجُمِعَ الشَّمْسُ وَالْقَمَرُۙ", translation: "Güneş ve Ay bir araya getirildiği zaman.", transliteration: "Ve cumiaş şemsu vel kamer."},
  {id: 5561, sure_no: 10, ayet_no: 10, sure_adi: "Kıyame", text: "يَقُولُ الْاِنْسَانُ يَوْمَئِذٍ اَيْنَ الْمَفَرُّۚ", translation: "O Gün, o insan, \"Kaçacak yer var mı?\" diyecek.", transliteration: "Yekulul insanu yevme izin eynel meferr."},
  {id: 5562, sure_no: 11, ayet_no: 11, sure_adi: "Kıyame", text: "كَلَّا لَا وَزَرَۚ", translation: "Hayır! Kesinlikle sığınacak bir yer yoktur!", transliteration: "Kella la vezer."},
  {id: 5563, sure_no: 12, ayet_no: 12, sure_adi: "Kıyame", text: "اِلٰى رَبِّكَ يَوْمَئِذٍۨ الْمُسْتَقَرُّۜ", translation: "O Gün, sığınılacak tek yer senin Rabb'indir.", transliteration: "İla rabbike yevme izinil mustekar."},
  {id: 5564, sure_no: 13, ayet_no: 13, sure_adi: "Kıyame", text: "يُنَبَّؤُا الْاِنْسَانُ يَوْمَئِذٍ بِمَا قَدَّمَ وَاَخَّرَۜ", translation: "O Gün, o insan, yaptıklarından da yapması gerektiği halde yapmadıklarından da bir bir haberdar edilir.", transliteration: "Yunebbeul insanu yevme izin bima kaddeme ve ahhar."},
  {id: 5565, sure_no: 14, ayet_no: 14, sure_adi: "Kıyame", text: "بَلِ الْاِنْسَانُ عَلٰى نَفْسِه۪ بَص۪يرَةٌۙ", translation: "Hayır! Aslında o insan, kendi aleyhine tanıktır.", transliteration: "Belil insanu ala nefsihi basireth."},
  {id: 5566, sure_no: 15, ayet_no: 15, sure_adi: "Kıyame", text: "وَلَوْ اَلْقٰى مَعَاذ۪يرَهُۜ", translation: "Mazeretlerini ileri sürse bile.", transliteration: "Ve lev elka meazireh."},
  {id: 5567, sure_no: 16, ayet_no: 16, sure_adi: "Kıyame", text: "لَا تُحَرِّكْ بِه۪ لِسَانَكَ لِتَعْجَلَ بِه۪ۜ", translation: "Telaşla, geçiştirmeye çalışarak, dilini dolaştırıp durma!", transliteration: "La tuharrik bihi lisaneke li ta'cele bihi."},
  {id: 5568, sure_no: 17, ayet_no: 17, sure_adi: "Kıyame", text: "اِنَّ عَلَيْنَا جَمْعَهُ وَقُرْاٰنَهُۚ", translation: "Kuşkusuz, onun toplanması ve okunması Bize aittir.", transliteration: "İnne aleyna cem'ahu ve kur'anehu."},
  {id: 5569, sure_no: 18, ayet_no: 18, sure_adi: "Kıyame", text: "فَاِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْاٰنَهُۚ", translation: "O halde onu okuduğumuz zaman, onun okunuşuna tabi ol.", transliteration: "Fe iza kara'nahu fettebi'kur'anehu."},
  {id: 5570, sure_no: 19, ayet_no: 19, sure_adi: "Kıyame", text: "ثُمَّ اِنَّ عَلَيْنَا بَيَانَهُۜ", translation: "Sonra, onun beyanı yalnızca Biz'e aittir.", transliteration: "Summe inne aleyna beyanehu."},
  {id: 5571, sure_no: 20, ayet_no: 20, sure_adi: "Kıyame", text: "كَلَّا بَلْ تُحِبُّونَ الْعَاجِلَةَۙ", translation: "Hayır! İşin doğrusu, siz çabuk geçeni seviyordunuz.", transliteration: "Kella bel tuhıbbunel acileh."},
  {id: 5572, sure_no: 21, ayet_no: 21, sure_adi: "Kıyame", text: "وَتَذَرُونَ الْاٰخِرَةَۜ", translation: "\"Ahireti de umursamıyordunuz.\"", transliteration: "Ve tezerunel ahıreh."},
  {id: 5573, sure_no: 22, ayet_no: 22, sure_adi: "Kıyame", text: "وُجُوهٌ يَوْمَئِذٍ نَاضِرَةٌۙ", translation: "Birtakım yüzler O Gün ışıl ışıldır.", transliteration: "Vucuhun yevme izin nadıreh."},
  {id: 5574, sure_no: 23, ayet_no: 23, sure_adi: "Kıyame", text: "اِلٰى رَبِّهَا نَاظِرَةٌۚ", translation: "Rabb'ine bakar.", transliteration: "İla rabbiha nazıreh."},
  {id: 5575, sure_no: 24, ayet_no: 24, sure_adi: "Kıyame", text: "وَوُجُوهٌ يَوْمَئِذٍ بَاسِرَةٌۙ", translation: "Birtakım yüzler O Gün asıktır.", transliteration: "Ve vucuhun yevme izin basireth."},
  {id: 5576, sure_no: 25, ayet_no: 25, sure_adi: "Kıyame", text: "تَظُنُّ اَنْ يُفْعَلَ بِهَا فَاقِرَةٌۜ", translation: "Belini bükecek bir felaketle karşı karşıya kaldığını anlar.", transliteration: "Tezunnu en yuf'ale biha fakıreh."},
  {id: 5577, sure_no: 26, ayet_no: 26, sure_adi: "Kıyame", text: "كَلَّٓا اِذَا بَلَغَتِ التَّرَاقِيَۙ", translation: "Hayır! Kesinlikle düşündükleri gibi değil. Köprücük kemiklerine dayandığı zaman;", transliteration: "Kella iza belegatit terakıy."},
  {id: 5578, sure_no: 27, ayet_no: 27, sure_adi: "Kıyame", text: "وَق۪يلَ مَنْ۔ رَاقٍۙ", translation: "\"Kurtaracak olan kimdir?\" denir.", transliteration: "Ve kile men rak."},
  {id: 5579, sure_no: 28, ayet_no: 28, sure_adi: "Kıyame", text: "وَظَنَّ اَنَّهُ الْفِرَاقُۙ", translation: "Bunun ayrılık anı olduğunu anlar.", transliteration: "Ve zanne ennehul firak."},
  {id: 5580, sure_no: 29, ayet_no: 29, sure_adi: "Kıyame", text: "وَالْتَفَّتِ السَّاقُ بِالسَّاقِۙ", translation: "Ve ayakları birbirine dolaşır.", transliteration: "Velteffetis saku bis sak."},
  {id: 5581, sure_no: 30, ayet_no: 30, sure_adi: "Kıyame", text: "اِلٰى رَبِّكَ يَوْمَئِذٍۨ الْمَسَاقُۜ‌۟", translation: "O Gün gidiş yalnızca Rabb'inedir.", transliteration: "İla rabbike yevme izinil mesak."},
  {id: 5582, sure_no: 31, ayet_no: 31, sure_adi: "Kıyame", text: "فَلَا صَدَّقَ وَلَا صَلّٰىۙ", translation: "Fakat o ne onayladı ne de salla yaptı.", transliteration: "Fe la saddeka ve la salla."},
  {id: 5583, sure_no: 32, ayet_no: 32, sure_adi: "Kıyame", text: "وَلٰـكِنْ كَذَّبَ وَتَوَلّٰىۙ", translation: "Fakat yalanladı ve köstekledi.", transliteration: "Ve lakin kezzebe ve tevella."},
  {id: 5584, sure_no: 33, ayet_no: 33, sure_adi: "Kıyame", text: "ثُمَّ ذَهَبَ اِلٰٓى اَهْلِه۪ يَتَمَطّٰىۜ", translation: "Sonra böbürlenerek yandaşlarına gitti.", transliteration: "Summe zehebe ila ehlihi yetemetta."},
  {id: 5585, sure_no: 34, ayet_no: 34, sure_adi: "Kıyame", text: "اَوْلٰى لَكَ فَاَوْلٰىۙ", translation: "Yazık sana yazık, sen hak ettin!", transliteration: "Evla leke fe evla."},
  {id: 5586, sure_no: 35, ayet_no: 35, sure_adi: "Kıyame", text: "ثُمَّ اَوْلٰى لَكَ فَاَوْلٰىۜ", translation: "Evet, yazık sana yazık, sen hak ettin.", transliteration: "Summe evla leke fe evla."},
  {id: 5587, sure_no: 36, ayet_no: 36, sure_adi: "Kıyame", text: "اَيَحْسَبُ الْاِنْسَانُ اَنْ يُتْرَكَ سُدًىۜ", translation: "Yoksa o insan başıboş bırakılacağını mı sanıyor?", transliteration: "E yahsebul'insanu en yutreke suda."},
  {id: 5588, sure_no: 37, ayet_no: 37, sure_adi: "Kıyame", text: "اَلَمْ يَكُ نُطْفَةً مِنْ مَنِيٍّ يُمْنٰىۙ", translation: "Yoksa o, bir nutfeden, dökülen meniden ibaret değil miydi?", transliteration: "E lem yeku nutfeten min meni yin yumna."},
  {id: 5589, sure_no: 38, ayet_no: 38, sure_adi: "Kıyame", text: "ثُمَّ  كَانَ عَلَقَةً فَخَلَقَ فَسَوّٰىۙ", translation: "Sonra bir \"alaka1\" oldu. Derken onu yarattı ve ona biçim verdi.", transliteration: "Summe kane alakaten fe halaka fe sevva."},
  {id: 5590, sure_no: 39, ayet_no: 39, sure_adi: "Kıyame", text: "فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰىۜ", translation: "Ondan erkek ve dişi iki cinsi meydana getirdi.", transliteration: "Fe ceale minhuz zevceyniz zekere vel unsa."},
  {id: 5591, sure_no: 40, ayet_no: 40, sure_adi: "Kıyame", text: "اَلَيْسَ ذٰلِكَ بِقَادِرٍ عَلٰٓى اَنْ يُحْيِيَ الْمَوْتٰى", translation: "İşte bütün bunları yapanın, ölüleri diriltmeye gücü yetmez mi?", transliteration: "E leyse zalike bi kadirin ala en yuhyiyel mevta."},
  {id: 5592, sure_no: 1, ayet_no: 1, sure_adi: "İnsan", text: "هَلْ اَتٰى عَلَى الْاِنْسَانِ ح۪ينٌ مِنَ الدَّهْرِ لَمْ يَكُنْ شَيْـٔاً مَذْكُوراً", translation: "Anılmaya değer bir şey değilken, insanın üzerinden \"dehr\" den bir zaman gelip geçmedi mi?", transliteration: "Hel eta alel insani hinun mined dehri lem yekun şey'en mezkura."},
  {id: 5593, sure_no: 2, ayet_no: 2, sure_adi: "İnsan", text: "اِنَّا خَلَقْنَا الْاِنْسَانَ مِنْ نُطْفَةٍ  اَمْشَاجٍۗ  نَبْتَل۪يهِ فَجَعَلْنَاهُ سَم۪يعاً بَص۪يراً", translation: "İnsanı karışık bir nutfeden yarattık. Onu sınava tabi tutacağız; bu nedenle onu duyan ve gören bir varlık yaptık.", transliteration: "İnna halaknel insane min nutfetin emşacin nebtelihi fe cealnahu semian basira."},
  {id: 5594, sure_no: 3, ayet_no: 3, sure_adi: "İnsan", text: "اِنَّا هَدَيْنَاهُ السَّب۪يلَ اِمَّا شَا‌كِراً وَاِمَّا كَفُوراً", translation: "Ona doğru yolu gösterdik. Ama isterse şükreden, isterse nankörlük eden biri olur.", transliteration: "İnna hedeynahus sebile imma şakiren ve imma kefura."},
  {id: 5595, sure_no: 4, ayet_no: 4, sure_adi: "İnsan", text: "اِنَّٓا اَعْتَدْنَا لِلْـكَافِر۪ينَ سَلَاسِلَا۬ وَاَغْلَالاً وَسَع۪يراً", translation: "Kafirler için zincirler, kelepçeler ve alevli ateş hazırladık.", transliteration: "İnna a'tedna lil kafirine selasile ve ağlalen ve seira."},
  {id: 5596, sure_no: 5, ayet_no: 5, sure_adi: "İnsan", text: "اِنَّ الْاَبْرَارَ يَشْرَبُونَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُوراًۚ", translation: "İyi olanlar ise içinde kafur bulunan bir kadehten içerler.", transliteration: "İnnel ebrara yeşrebune min ke'sin kane mizacuha kafura."},
  {id: 5597, sure_no: 6, ayet_no: 6, sure_adi: "İnsan", text: "عَيْناً يَشْرَبُ بِهَا عِبَادُ اللّٰهِ يُفَجِّرُونَهَا تَفْج۪يراً", translation: "Allah'ın kullarının, içtikleri ve diledikleri kadar yararlandıkları bir kaynak.", transliteration: "Aynen yeşrebu biha ibadullahi yufecciruneha tefcira."},
  {id: 5598, sure_no: 7, ayet_no: 7, sure_adi: "İnsan", text: "يُوفُونَ بِالنَّذْرِ وَيَخَافُونَ يَوْماً كَانَ شَرُّهُ مُسْتَط۪يراً", translation: "Verdikleri sözü yerine getirirler, kötülüğü salgın bir günden korkarlar.", transliteration: "Yufune bin nezri ve yehafune yevmen kane şerruhu mustetira."},
  {id: 5599, sure_no: 8, ayet_no: 8, sure_adi: "İnsan", text: "وَيُطْعِمُونَ الطَّعَامَ عَلٰى حُبِّه۪ مِسْك۪يناً وَيَت۪يماً وَاَس۪يراً", translation: "İhtiyaçları olmasına rağmen yiyeceği; yoksula, öksüze ve tutsağa yedirirler.", transliteration: "Ve yut'imunet taame ala hubbihi miskinen ve yetimen ve esira"},
  {id: 5600, sure_no: 9, ayet_no: 9, sure_adi: "İnsan", text: "اِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللّٰهِ لَا نُر۪يدُ مِنْكُمْ جَزَٓاءً وَلَا شُكُوراً", translation: "Biz, sizi yalnızca Allah için doyuruyoruz. Sizden bir karşılık veya bir teşekkür beklemiyoruz.", transliteration: "İnnema nut'imukum li vechillahi la nuridu minkum cezaen ve la şukura."},
  {id: 5601, sure_no: 10, ayet_no: 10, sure_adi: "İnsan", text: "اِنَّا نَخَافُ مِنْ رَبِّنَا يَوْماً عَبُوساً قَمْطَر۪يراً", translation: "Biz; yüzlerin asık olduğu, belalı, zor günde Rabb'imizin azabından korkarız.", transliteration: "İnna nehafu min rabbina yevmen abusen kamtarira."},
  {id: 5602, sure_no: 11, ayet_no: 11, sure_adi: "İnsan", text: "فَوَقٰيهُمُ اللّٰهُ شَرَّ ذٰلِكَ الْيَوْمِ وَلَقّٰيهُمْ نَضْرَةً وَسُرُوراًۚ", translation: "Allah da bu nedenle onları, o günün kötülüğünden koruyacak. Ve onları, mutluluk ve sevince kavuşturacak.", transliteration: "Fe vekahumullahu şerra zalikel yevmi ve lakkahum nadreten ve surura."},
  {id: 5603, sure_no: 12, ayet_no: 12, sure_adi: "İnsan", text: "وَجَزٰيهُمْ بِمَا صَبَرُوا جَنَّةً وَحَر۪يراًۙ", translation: "Sabretmelerine karşılık onları cennet ve ipek ile ödüllendirecektir.", transliteration: "Ve cezahum bima saberu cenneten ve harira."},
  {id: 5604, sure_no: 13, ayet_no: 13, sure_adi: "İnsan", text: "مُتَّكِـ۪ٔينَ ف۪يهَا عَلَى الْاَرَٓائِكِۚ لَا يَرَوْنَ ف۪يهَا شَمْساً وَلَا زَمْهَر۪يراًۚ", translation: "Orada, tahtlara kurulacaklar. Orada ne şiddetli bir sıcak ne de dondurucu bir soğuk görecekler.", transliteration: "Muttekiine fiha alel eraik, la yeravne fiha şemsen ve la zemherira."},
  {id: 5605, sure_no: 14, ayet_no: 14, sure_adi: "İnsan", text: "وَدَانِيَةً عَلَيْهِمْ ظِلَالُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْل۪يلاً", translation: "Meyve ağaçlarının gölgeleri üzerlerine sarkacak, meyveleri yemeye hazır olarak yaklaştırılacak.", transliteration: "Ve daniyeten aleyhim zılaluha ve zullilet kutufuha tezlila."},
  {id: 5606, sure_no: 15, ayet_no: 15, sure_adi: "İnsan", text: "وَيُطَافُ عَلَيْهِمْ بِاٰنِيَةٍ مِنْ فِضَّةٍ وَاَكْوَابٍ كَانَتْ قَوَار۪يرَاۙ", translation: "Gümüşten kaplar ve billur kupalarla aralarında dolaşılır.", transliteration: "Ve yutafu aleyhim bi aniyetin min fıddatin ve ekvabin kanet kavarira."},
  {id: 5607, sure_no: 16, ayet_no: 16, sure_adi: "İnsan", text: "قَوَار۪يرَ مِنْ فِضَّةٍ قَدَّرُوهَا تَقْد۪يراً", translation: "Miktarını kendilerinin belirledikleri gümüşten billur kadehler,", transliteration: "Kavarira min fıddatin kadderuha takdira."},
  {id: 5608, sure_no: 17, ayet_no: 17, sure_adi: "İnsan", text: "وَيُسْقَوْنَ ف۪يهَا كَأْساً كَانَ مِزَاجُهَا زَنْجَب۪يلاًۚ", translation: "Ve orada, karışımı zencefil olan kadehler sunulur.", transliteration: "Ve yuskavne fiha ke'sen kane mizacuha zencebila."},
  {id: 5609, sure_no: 18, ayet_no: 18, sure_adi: "İnsan", text: "عَيْناً ف۪يهَا تُسَمّٰى سَلْسَب۪يلاً", translation: "Selsebil denilen bir pınardan.", transliteration: "Aynen fiha tusemma selsebila."},
  {id: 5610, sure_no: 19, ayet_no: 19, sure_adi: "İnsan", text: "وَيَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَۚ اِذَا رَاَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤ۬اً مَنْثُوراً", translation: "Ölümsüz gençler aralarında dolaşırlar. Onları gördüğünde, saçılmış inciler sanırsın.", transliteration: "Ve yetufu aleyhim vildanun muhalledun, iza reeytehum hasibtehum lu'luen mensura."},
  {id: 5611, sure_no: 20, ayet_no: 20, sure_adi: "İnsan", text: "وَاِذَا رَاَيْتَ ثَمَّ رَاَيْتَ نَع۪يماً وَمُلْـكاً كَب۪يراً", translation: "Nereye baksan orada nimetler, büyük bir mülk ve saltanat görürsün.", transliteration: "Ve iza reeyte semme reeyte naimen ve mulken kebira."},
  {id: 5612, sure_no: 21, ayet_no: 21, sure_adi: "İnsan", text: "عَالِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَاِسْتَبْرَقٌۘ وَحُلُّٓوا اَسَاوِرَ مِنْ فِضَّةٍۚ وَسَقٰيهُمْ رَبُّهُمْ شَرَاباً طَهُوراً", translation: "Üzerlerinde yeşil ince ipekten ve parlak atlastan elbiseler vardır. Gümüş bileziklerle süslenecekler. Rabb'leri onlara tertemiz içecekler sunacak.", transliteration: "Aliyehum siyabu sundusin hudrun ve istebrakun ve hullu esavira min fıddah, ve sekahum rabbuhum şaraben tahura."},
  {id: 5613, sure_no: 22, ayet_no: 22, sure_adi: "İnsan", text: "اِنَّ هٰذَا كَانَ لَـكُمْ جَزَٓاءً وَكَانَ سَعْيُكُمْ مَشْكُوراً۟", translation: "Bu sizin ödülünüzdür. Çabalarınız bu karşılığı hak etti.", transliteration: "İnne haza kane lekum cezaen ve kane sa'yukum meşkura."},
  {id: 5614, sure_no: 23, ayet_no: 23, sure_adi: "İnsan", text: "اِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْاٰنَ تَنْز۪يلاًۚ", translation: "Kuşkusuz Biz, Kur'an'ı sana parça parça indirdik.", transliteration: "İnna nahnu nezzelna aleykel kur'ane tenzila."},
  {id: 5615, sure_no: 24, ayet_no: 24, sure_adi: "İnsan", text: "فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ اٰثِماً اَوْ كَفُوراًۚ", translation: "Artık Rabb'inin hükmü için sabret. Onlardan, günahkarlara ve Kafirlere uyma.", transliteration: "Fasbir li hukmi rabbike ve la tutı'minhum asimen ev kefura."},
  {id: 5616, sure_no: 25, ayet_no: 25, sure_adi: "İnsan", text: "وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَاَص۪يلاًۚ", translation: "Sabah akşam Rabb'inin ismini an.", transliteration: "Vezkurisme rabbike bukreten ve asila."},
  {id: 5617, sure_no: 26, ayet_no: 26, sure_adi: "İnsan", text: "وَمِنَ الَّيْلِ فَاسْجُدْ لَهُ وَسَبِّحْهُ لَيْلاً طَو۪يلاً", translation: "Gecenin bir bölümünde O'na secde et. Ve gece uzunca O'nu tesbih et.", transliteration: "Ve minel leyli fescud lehu ve sebbihhu leylen tavila."},
  {id: 5618, sure_no: 27, ayet_no: 27, sure_adi: "İnsan", text: "اِنَّ هٰٓؤُ۬لَٓاءِ يُحِبُّونَ الْعَاجِلَةَ وَيَذَرُونَ وَرَٓاءَهُمْ يَوْماً ثَق۪يلاً", translation: "Onlar çabuk geçen dünyayı seviyorlar, zor günü umursamıyorlar.", transliteration: "İnne haulai yuhıbbunel acilete ve yezerune veraehum yevmen sekila."},
  {id: 5619, sure_no: 28, ayet_no: 28, sure_adi: "İnsan", text: "نَحْنُ خَلَقْنَاهُمْ وَشَدَدْنَٓا اَسْرَهُمْۚ وَاِذَا شِئْنَا بَدَّلْـنَٓا اَمْثَالَهُمْ تَبْد۪يلاً", translation: "Onları Biz yarattık. Yaratılışlarını sapasağlam yaptık. Dilersek onları yok eder, yerlerine benzerlerini getiririz.", transliteration: "Nahnu halaknahum ve şededna esrehum, ve iza şi'na beddelna emsalehum tebdila."},
  {id: 5620, sure_no: 29, ayet_no: 29, sure_adi: "İnsan", text: "اِنَّ هٰذِه۪ تَذْكِرَةٌۚ فَمَنْ شَٓاءَ اتَّخَذَ اِلٰى رَبِّه۪ سَب۪يلاً", translation: "Bu bir öğüttür. Artık dileyen onunla Rabb'ine varan bir yol edinir.", transliteration: "İnne hazihi tezkireh, fe men şaettehaze ila rabbihi sebila."},
  {id: 5621, sure_no: 30, ayet_no: 30, sure_adi: "İnsan", text: "وَمَا تَشَٓاؤُ۫نَ اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُۜ اِنَّ اللّٰهَ  كَانَ عَل۪يماً حَك۪يماًۗ", translation: "Allah dilemedikçe siz dileyemezsiniz. Kuşkusuz Allah, Her Şeyi Bilen'dir, En İyi Hüküm Veren'dir.", transliteration: "Ve ma teşaune illa en yeşaallah, innallahe kane alimen hakima."},
  {id: 5622, sure_no: 31, ayet_no: 31, sure_adi: "İnsan", text: "يُدْخِلُ مَنْ يَشَٓاءُ ف۪ي رَحْمَتِه۪ۜ وَالظَّالِم۪ينَ اَعَدَّ لَهُمْ عَذَاباً اَل۪يماً", translation: "Hak edeni rahmetine alır. Zalimler için ise acıklı bir azap hazırladı.", transliteration: "Yudhilu men yeşau fi rahmetih, vez zalimine eadde lehum azaben elima."},
  {id: 5623, sure_no: 1, ayet_no: 1, sure_adi: "Mürselat", text: "وَالْمُرْسَلَاتِ عُرْفاًۙ", translation: "Art arda gönderilenlere ant olsun.", transliteration: "Vel murselati urfa."},
  {id: 5624, sure_no: 2, ayet_no: 2, sure_adi: "Mürselat", text: "فَالْعَاصِفَاتِ عَصْفاًۙ", translation: "Ve şiddetle estikçe esenlere,", transliteration: "Fel asıfati asfa."},
  {id: 5625, sure_no: 3, ayet_no: 3, sure_adi: "Mürselat", text: "وَالنَّاشِرَاتِ نَشْراًۙ", translation: "Yaydıkça yayanlara,", transliteration: "Vennaşirati neşren."},
  {id: 5626, sure_no: 4, ayet_no: 4, sure_adi: "Mürselat", text: "فَالْفَارِقَاتِ فَرْقاًۙ", translation: "Ayırdıkça ayıranlara,", transliteration: "Fel farikati ferka."},
  {id: 5627, sure_no: 5, ayet_no: 5, sure_adi: "Mürselat", text: "فَالْمُلْقِيَاتِ ذِكْراًۙ", translation: "Ve de öğüdü ulaştıranlara,", transliteration: "Fel mulkıyati zikra."},
  {id: 5628, sure_no: 6, ayet_no: 6, sure_adi: "Mürselat", text: "عُذْراً اَوْ نُذْراًۙ", translation: "Özür veya uyarı için,", transliteration: "Uzren ev nuzra."},
  {id: 5629, sure_no: 7, ayet_no: 7, sure_adi: "Mürselat", text: "اِنَّمَا تُوعَدُونَ لَوَاقِعٌۜ", translation: "Uyarıldığınız şey kesinlikle gerçekleşecektir.", transliteration: "İnnema tuadune levakı'."},
  {id: 5630, sure_no: 8, ayet_no: 8, sure_adi: "Mürselat", text: "فَاِذَا النُّجُومُ طُمِسَتْۙ", translation: "Yıldızlar silindiğinde.", transliteration: "Fe izen nucumu tumiset."},
  {id: 5631, sure_no: 9, ayet_no: 9, sure_adi: "Mürselat", text: "وَاِذَا السَّمَٓاءُ فُرِجَتْۙ", translation: "Gök aralandığında.", transliteration: "Ve izes semau furicet."},
  {id: 5632, sure_no: 10, ayet_no: 10, sure_adi: "Mürselat", text: "وَاِذَا الْجِبَالُ نُسِفَتْۙ", translation: "Dağlar ufalanıp savrulduğunda;", transliteration: "Ve izel cibalu nusifet."},
  {id: 5633, sure_no: 11, ayet_no: 11, sure_adi: "Mürselat", text: "وَاِذَا الرُّسُلُ اُقِّتَتْۜ", translation: "O resullere vakit belirlendiğinde;", transliteration: "Ve izer rusulu ukkıtet."},
  {id: 5634, sure_no: 12, ayet_no: 12, sure_adi: "Mürselat", text: "لِاَيِّ يَوْمٍ اُجِّلَتْۜ", translation: "Bunlar hangi gün için ertelendi?", transliteration: "Li eyyi yevmin uccilet."},
  {id: 5635, sure_no: 13, ayet_no: 13, sure_adi: "Mürselat", text: "لِيَوْمِ الْفَصْلِۚ", translation: "Ayırt etme günü için.", transliteration: "Li yevmil fasl."},
  {id: 5636, sure_no: 14, ayet_no: 14, sure_adi: "Mürselat", text: "وَمَٓا اَدْرٰيكَ مَا يَوْمُ الْفَصْلِۜ", translation: "Ayırt etme gününün ne olduğunu biliyor musun?", transliteration: "Ve ma edrake ma yevmul fasl."},
  {id: 5637, sure_no: 15, ayet_no: 15, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5638, sure_no: 16, ayet_no: 16, sure_adi: "Mürselat", text: "اَلَمْ نُهْلِكِ الْاَوَّل۪ينَۜ", translation: "Öncekileri yok etmedik mi?", transliteration: "E lem nuhlikil evvelin."},
  {id: 5639, sure_no: 17, ayet_no: 17, sure_adi: "Mürselat", text: "ثُمَّ نُتْبِعُهُمُ الْاٰخِر۪ينَ", translation: "Sonra arkadan gelenleri de onlara katarız.", transliteration: "Summe nutbiuhumul ahırin."},
  {id: 5640, sure_no: 18, ayet_no: 18, sure_adi: "Mürselat", text: "كَذٰلِكَ نَفْعَلُ بِالْمُجْرِم۪ينَ", translation: "İşte suçlulara böyle yaparız.", transliteration: "Kezalike nef'alu bil mucrimin."},
  {id: 5641, sure_no: 19, ayet_no: 19, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5642, sure_no: 20, ayet_no: 20, sure_adi: "Mürselat", text: "اَلَمْ نَخْلُقْكُمْ مِنْ مَٓاءٍ مَه۪ينٍۙ", translation: "Sizi basit bir sudan yaratmadık mı?", transliteration: "E lem nahlukkum min main mehin."},
  {id: 5643, sure_no: 21, ayet_no: 21, sure_adi: "Mürselat", text: "فَجَعَلْنَاهُ ف۪ي قَرَارٍ مَك۪ينٍۙ", translation: "Sonra onu korunaklı bir yere yerleştirdik.", transliteration: "Fe cealnahu fi kararin mekin."},
  {id: 5644, sure_no: 22, ayet_no: 22, sure_adi: "Mürselat", text: "اِلٰى قَدَرٍ مَعْلُومٍۙ", translation: "Belirlenen bir kadere kadar.", transliteration: "İla kaderin ma'lum."},
  {id: 5645, sure_no: 23, ayet_no: 23, sure_adi: "Mürselat", text: "فَقَدَرْنَاۗ فَنِعْمَ الْقَادِرُونَ", translation: "Sonra takdir ettik, ne güzel takdir edenleriz.", transliteration: "Fe kaderna fe ni'mel kadirun."},
  {id: 5646, sure_no: 24, ayet_no: 24, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5647, sure_no: 25, ayet_no: 25, sure_adi: "Mürselat", text: "اَلَمْ نَجْعَلِ الْاَرْضَ كِفَاتاًۙ", translation: "Yeryüzünü toplanma yeri yapmadık mı?", transliteration: "E lem nec'alil arda kifata."},
  {id: 5648, sure_no: 26, ayet_no: 26, sure_adi: "Mürselat", text: "اَحْيَٓاءً وَاَمْوَاتاًۙ", translation: "Diriler ve ölüler için.", transliteration: "Ahyaen ve emvata."},
  {id: 5649, sure_no: 27, ayet_no: 27, sure_adi: "Mürselat", text: "وَجَعَلْنَا ف۪يهَا رَوَاسِيَ شَامِخَاتٍ وَاَسْقَيْنَاكُمْ مَٓاءً فُرَاتاًۜ", translation: "Orada oturaklı ağır baskılar yaptık. Size tatlı su içirdik.", transliteration: "Ve cealna fiha revasiye şamihatin ve eskaynakum maen furata."},
  {id: 5650, sure_no: 28, ayet_no: 28, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5651, sure_no: 29, ayet_no: 29, sure_adi: "Mürselat", text: "اِنْطَلِقُٓوا اِلٰى مَا كُنْتُمْ بِه۪ تُكَذِّبُونَۚ", translation: "Haydi, kendisini yalanlamış olduğunuz şeye gidin bakalım!", transliteration: "İntaliku ila ma kuntum bihi tukezzibun."},
  {id: 5652, sure_no: 30, ayet_no: 30, sure_adi: "Mürselat", text: "اِنْطَلِقُٓوا اِلٰى ظِلٍّ ذ۪ي ثَلٰثِ شُعَبٍۙ", translation: "Üç çatal sahibi gölgeye gidin;", transliteration: "İntaliku ila zıllin zi selasi şuab."},
  {id: 5653, sure_no: 31, ayet_no: 31, sure_adi: "Mürselat", text: "لَا ظَل۪يلٍ وَلَا يُغْن۪ي مِنَ اللَّهَبِۜ", translation: "Gölge yapmayan ve alevden de korumayan.", transliteration: "La zalilin ve la yugni minel leheb."},
  {id: 5654, sure_no: 32, ayet_no: 32, sure_adi: "Mürselat", text: "اِنَّهَا تَرْم۪ي بِشَرَرٍ كَالْقَصْرِۚ", translation: "O, kocaman kütükler gibi kıvılcımlar saçar.", transliteration: "İnneha termi bi şerarin kel kasr."},
  {id: 5655, sure_no: 33, ayet_no: 33, sure_adi: "Mürselat", text: "كَاَنَّهُ جِمَالَتٌ صُفْرٌۜ", translation: "Sanki o sarı deve sürüsüdür.", transliteration: "Ke ennehu cimaletun sufr."},
  {id: 5656, sure_no: 34, ayet_no: 34, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5657, sure_no: 35, ayet_no: 35, sure_adi: "Mürselat", text: "هٰذَا يَوْمُ لَا يَنْطِقُونَۙ", translation: "Bu, konuşamayacakları gündür.", transliteration: "Haza yevmu la yentıkun."},
  {id: 5658, sure_no: 36, ayet_no: 36, sure_adi: "Mürselat", text: "وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ", translation: "Onlara izin verilmez ki, özür dilesinler.", transliteration: "Ve la yu'zenu lehum fe ya'tezirun."},
  {id: 5659, sure_no: 37, ayet_no: 37, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5660, sure_no: 38, ayet_no: 38, sure_adi: "Mürselat", text: "هٰذَا يَوْمُ الْفَصْلِۚ جَمَعْنَاكُمْ وَالْاَوَّل۪ينَ", translation: "Bu, sizi ve öncekileri topladığımız, ayırma günüdür.", transliteration: "Haza yevmul fasl, cema'nakum vel evvelin."},
  {id: 5661, sure_no: 39, ayet_no: 39, sure_adi: "Mürselat", text: "فَاِنْ كَانَ لَكُمْ كَيْدٌ فَك۪يدُونِ", translation: "Haydi! Eğer kurtulmak için bir planınız varsa, Bana karşı hemen planınızı uygulayın!", transliteration: "Fe in kane lekum keydun fe kidun."},
  {id: 5662, sure_no: 40, ayet_no: 40, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ۟", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5663, sure_no: 41, ayet_no: 41, sure_adi: "Mürselat", text: "اِنَّ الْمُتَّق۪ينَ ف۪ي ظِلَالٍ وَعُيُونٍۙ", translation: "Takva sahipleri gölgelerde ve pınar başlarındadırlar.", transliteration: "İnnel muttekine fi zılalin ve uyun."},
  {id: 5664, sure_no: 42, ayet_no: 42, sure_adi: "Mürselat", text: "وَفَوَاكِهَ مِمَّا يَشْتَهُونَۜ", translation: "Ve canlarının çektiği meyveler.", transliteration: "Ve fevakihe mimma yeştehun."},
  {id: 5665, sure_no: 43, ayet_no: 43, sure_adi: "Mürselat", text: "كُلُوا وَاشْرَبُوا هَن۪ٓيـٔاً بِمَا كُنْتُمْ تَعْمَلُونَ", translation: "Yaptıklarınıza karşılık afiyetle yiyin ve için.", transliteration: "Kulu veşrebu henien bima kuntum ta'melun."},
  {id: 5666, sure_no: 44, ayet_no: 44, sure_adi: "Mürselat", text: "اِنَّا كَذٰلِكَ نَجْزِي الْمُحْسِن۪ينَ", translation: "İyi olanları işte böyle ödüllendiririz.", transliteration: "İnna kezalike neczil muhsinin."},
  {id: 5667, sure_no: 45, ayet_no: 45, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5668, sure_no: 46, ayet_no: 46, sure_adi: "Mürselat", text: "كُلُوا وَتَمَتَّعُوا قَل۪يلاً اِنَّكُمْ مُجْرِمُونَ", translation: "Yiyin ve yararlanın biraz. Siz suçlularsınız.", transliteration: "Kulu ve temetteu kalilen innekum mucrimun."},
  {id: 5669, sure_no: 47, ayet_no: 47, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5670, sure_no: 48, ayet_no: 48, sure_adi: "Mürselat", text: "وَاِذَا ق۪يلَ لَهُمُ ارْكَعُوا لَا يَرْكَعُونَ", translation: "Onlara, \"Ruku edin.\" denildiği zaman ruku etmezler.", transliteration: "Ve iza kile lehumurkeu la yerkeun."},
  {id: 5671, sure_no: 49, ayet_no: 49, sure_adi: "Mürselat", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5672, sure_no: 50, ayet_no: 50, sure_adi: "Mürselat", text: "فَبِاَيِّ حَد۪يثٍ بَعْدَهُ يُؤْمِنُونَ", translation: "Artık bundan başka hangi hadise inanacaklar?", transliteration: "Fe bi eyyi hadisin ba'dehu yu'minun."},
  {id: 5673, sure_no: 1, ayet_no: 1, sure_adi: "Nebe", text: "عَمَّ يَتَسَٓاءَلُونَۚ", translation: "Birbirlerine neyi soruyorlar?", transliteration: "Amme yetesaelun."},
  {id: 5674, sure_no: 2, ayet_no: 2, sure_adi: "Nebe", text: "عَنِ النَّبَأِ الْعَظ۪يمِۙ", translation: "Büyük haberden mi?", transliteration: "Anin nebeil azim."},
  {id: 5675, sure_no: 3, ayet_no: 3, sure_adi: "Nebe", text: "اَلَّذ۪ي هُمْ ف۪يهِ مُخْتَلِفُونَۜ", translation: "Onlar, onun hakkında anlaşmazlık içindeler.", transliteration: "Ellezi hum fihi muhtelifun."},
  {id: 5676, sure_no: 4, ayet_no: 4, sure_adi: "Nebe", text: "كَلَّا سَيَعْلَمُونَۙ", translation: "Hayır, yakında bilecekler.", transliteration: "Kella se ya'lemun."},
  {id: 5677, sure_no: 5, ayet_no: 5, sure_adi: "Nebe", text: "ثُمَّ كَلَّا سَيَعْلَمُونَ", translation: "Yine hayır, yakında bilecekler.", transliteration: "Summe kella se ya'lemun."},
  {id: 5678, sure_no: 6, ayet_no: 6, sure_adi: "Nebe", text: "اَلَمْ نَجْعَلِ الْاَرْضَ مِهَاداًۙ", translation: "Yeryüzünü bir döşek yapmadık mı?", transliteration: "E lem nec'alil arda mihada."},
  {id: 5679, sure_no: 7, ayet_no: 7, sure_adi: "Nebe", text: "وَالْجِبَالَ اَوْتَاداًۖ", translation: "Ve dağları da birer kazık?", transliteration: "Vel cibale evtada."},
  {id: 5680, sure_no: 8, ayet_no: 8, sure_adi: "Nebe", text: "وَخَلَقْنَاكُمْ اَزْوَاجاًۙ", translation: "Sizi çiftler halinde yarattık.", transliteration: "Ve halaknakum ezvaca."},
  {id: 5681, sure_no: 9, ayet_no: 9, sure_adi: "Nebe", text: "وَجَعَلْنَا نَوْمَكُمْ سُبَاتاًۙ", translation: "Uykunuzu dinlenme zamanı yaptık.", transliteration: "Ve cealna nevmekum subata."},
  {id: 5682, sure_no: 10, ayet_no: 10, sure_adi: "Nebe", text: "وَجَعَلْنَا الَّيْلَ لِبَاساًۙ", translation: "Ve geceyi bir elbise yaptık.", transliteration: "Ve cealnel leyle libasa."},
  {id: 5683, sure_no: 11, ayet_no: 11, sure_adi: "Nebe", text: "وَجَعَلْنَا النَّهَارَ مَعَاشاًۖ", translation: "Gündüzü geçim zamanı yaptık.", transliteration: "Ve cealnen nehare meaşa."},
  {id: 5684, sure_no: 12, ayet_no: 12, sure_adi: "Nebe", text: "وَبَنَيْنَا فَوْقَـكُمْ سَبْعاً شِدَاداًۙ", translation: "Üstünüze yedi sağlamı bina ettik.", transliteration: "Ve beneyna fevkakum seb'an şidada."},
  {id: 5685, sure_no: 13, ayet_no: 13, sure_adi: "Nebe", text: "وَجَعَلْنَا سِرَاجاً وَهَّاجاًۖ", translation: "Ve ışık saçan bir kandil.", transliteration: "Ve cealna siracen vehhaca."},
  {id: 5686, sure_no: 14, ayet_no: 14, sure_adi: "Nebe", text: "وَاَنْزَلْنَا مِنَ الْمُعْصِرَاتِ مَٓاءً ثَجَّاجاًۙ", translation: "Ve sıkıştırılmış bulutlardan şırıl şırıl akan su indirdik.", transliteration: "Ve enzelna minel mu'sırati maen seccaca."},
  {id: 5687, sure_no: 15, ayet_no: 15, sure_adi: "Nebe", text: "لِنُخْرِجَ بِه۪ حَباًّ وَنَبَاتاًۙ", translation: "Onunla tahıl ve bitkiler yetiştirelim diye;", transliteration: "Li nuhrice bihi habben ve nebata."},
  {id: 5688, sure_no: 16, ayet_no: 16, sure_adi: "Nebe", text: "وَجَنَّاتٍ اَلْفَافاًۜ", translation: "İç içe girmiş bağlar bahçeler.", transliteration: "Ve cennatin elfafa."},
  {id: 5689, sure_no: 17, ayet_no: 17, sure_adi: "Nebe", text: "اِنَّ يَوْمَ الْفَصْلِ كَانَ م۪يقَاتاًۙ", translation: "Ayırma Günü belirlenmiş bir vakittir.", transliteration: "İnne yevmel faslı kane mikata."},
  {id: 5690, sure_no: 18, ayet_no: 18, sure_adi: "Nebe", text: "يَوْمَ يُنْفَخُ فِي الصُّورِ فَتَأْتُونَ اَفْوَاجاًۙ", translation: "O gün Sur'a üflenir, bölükler halinde gelirsiniz.", transliteration: "Yevme yunfehu fis suri fe te'tune efvaca."},
  {id: 5691, sure_no: 19, ayet_no: 19, sure_adi: "Nebe", text: "وَفُتِحَتِ السَّمَٓاءُ فَـكَانَتْ اَبْوَاباًۙ", translation: "Gökyüzü açılmış ve kapı kapı olmuştur.", transliteration: "Ve futihatis semau fe kanet ebvaba."},
  {id: 5692, sure_no: 20, ayet_no: 20, sure_adi: "Nebe", text: "وَسُيِّرَتِ الْجِبَالُ فَـكَانَتْ سَرَاباًۜ", translation: "Dağlar yürütülmüş, bir serap olmuştur.", transliteration: "Ve suyyiretil cibalu fe kanet seraba."},
  {id: 5693, sure_no: 21, ayet_no: 21, sure_adi: "Nebe", text: "اِنَّ جَهَنَّمَ كَانَتْ مِرْصَاداًۙ", translation: "Cehennem gözetleme yeri oldu;", transliteration: "İnne cehenneme kanet mirsada."},
  {id: 5694, sure_no: 22, ayet_no: 22, sure_adi: "Nebe", text: "لِلطَّاغ۪ينَ مَاٰباًۙ", translation: "Haddi aşanlar için varacakları yer olarak.", transliteration: "Lit tagine meaba."},
  {id: 5695, sure_no: 23, ayet_no: 23, sure_adi: "Nebe", text: "لَابِث۪ينَ ف۪يهَٓا اَحْقَاباًۚ", translation: "Orada \"ahkaben\" kalacaklardır.", transliteration: "Labisine fiha ahkaba."},
  {id: 5696, sure_no: 24, ayet_no: 24, sure_adi: "Nebe", text: "لَا يَذُوقُونَ ف۪يهَا بَرْداً وَلَا شَرَاباًۙ", translation: "Orada ne bir serinlik tadacaklar ne de içecek bir şey.", transliteration: "La yezukune fiha berden ve la şeraba."},
  {id: 5697, sure_no: 25, ayet_no: 25, sure_adi: "Nebe", text: "اِلَّا حَم۪يماً وَغَسَّاقاًۙ", translation: "Yalnızca kaynar ve kokuşmuş su tadacaklar.", transliteration: "İlla hamimen ve gassaka."},
  {id: 5698, sure_no: 26, ayet_no: 26, sure_adi: "Nebe", text: "جَزَٓاءً وِفَاقاً", translation: "Yaptıklarına uygun bir karşılık olarak.", transliteration: "Cezaen vifaka."},
  {id: 5699, sure_no: 27, ayet_no: 27, sure_adi: "Nebe", text: "اِنَّهُمْ كَانُوا لَا يَرْجُونَ حِسَاباًۙ", translation: "Onlar, hesap görüleceğini ummuyorlardı.", transliteration: "İnnehum kanu la yercune hısaba."},
  {id: 5700, sure_no: 28, ayet_no: 28, sure_adi: "Nebe", text: "وَكَذَّبُوا بِاٰيَاتِنَا كِذَّاباًۜ", translation: "Ayetlerimizi yalanladıkça yalanladılar.", transliteration: "Ve kezzebu bi ayatina kizzaba."},
  {id: 5701, sure_no: 29, ayet_no: 29, sure_adi: "Nebe", text: "وَكُلَّ شَيْءٍ اَحْصَيْنَاهُ كِتَاباً", translation: "Oysa Biz, her şeyi yazıp kaydettik.", transliteration: "Ve kulle şey'in ahsaynahu kitaba."},
  {id: 5702, sure_no: 30, ayet_no: 30, sure_adi: "Nebe", text: "فَذُوقُوا فَلَنْ نَز۪يدَكُمْ اِلَّا عَذَاباً۟", translation: "Şimdi azabı tadın bakalım! Artık size azaptan başka bir şey artırmayacağız.", transliteration: "Fe zuku felen nezidekum illa azaba."},
  {id: 5703, sure_no: 31, ayet_no: 31, sure_adi: "Nebe", text: "اِنَّ لِلْمُتَّق۪ينَ مَفَازاًۙ", translation: "Takva sahipleri için ödül vardır;", transliteration: "İnne lil muttekine mefaza."},
  {id: 5704, sure_no: 32, ayet_no: 32, sure_adi: "Nebe", text: "حَدَٓائِقَ وَاَعْنَاباًۙ", translation: "Bağlar ve bahçeler,", transliteration: "Hadaika ve a'naba."},
  {id: 5705, sure_no: 33, ayet_no: 33, sure_adi: "Nebe", text: "وَكَوَاعِبَ اَتْرَاباًۙ", translation: "Görkemli, dengi dengine,(eşler)", transliteration: "Ve kevaıbe etraba."},
  {id: 5706, sure_no: 34, ayet_no: 34, sure_adi: "Nebe", text: "وَكَأْساً دِهَاقاًۜ", translation: "Ve dolu kaseler.", transliteration: "Ve ke'sen dihaka."},
  {id: 5707, sure_no: 35, ayet_no: 35, sure_adi: "Nebe", text: "لَا يَسْمَعُونَ ف۪يهَا لَغْواً وَلَا كِذَّاباًۚ", translation: "Orada yalan da boş söz de duymazlar.", transliteration: "La yes'meune fiha lagven ve la kizzaba."},
  {id: 5708, sure_no: 36, ayet_no: 36, sure_adi: "Nebe", text: "جَزَٓاءً مِنْ رَبِّكَ عَطَٓاءً حِسَاباًۙ", translation: "Bunlar, Rabb'inden hesaba karşılık verilen ödüldür.", transliteration: "Cezaen min rabbike ataen hısaba."},
  {id: 5709, sure_no: 37, ayet_no: 37, sure_adi: "Nebe", text: "رَبِّ السَّمٰوَاتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۙ الرَّحْمٰنِ لَا يَمْلِكُونَ مِنْهُ خِطَاباًۙ", translation: "O, göklerin, yerin ve ikisinin arasında bulunanların Rabb'idir, Rahman'dır. Ondan bir hitaba, hiç kimse güç yetiremez.", transliteration: "Rabbis semavati vel ardı ve ma beynehumer rahmani la yemlikune minhu hitaba."},
  {id: 5710, sure_no: 38, ayet_no: 38, sure_adi: "Nebe", text: "يَوْمَ يَقُومُ الرُّوحُ وَالْمَلٰٓئِكَةُ صَفاًّۜ  لَا يَتَكَلَّمُونَ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَقَالَ صَوَاباً", translation: "O gün, o ruh ve o melekler saflar halinde hazır bulunur. Rahman'ın izin verdiklerinden başkası konuşamaz. İzin verilen de doğruyu söyler.", transliteration: "Yevme yekumur ruhu vel melaiketu saffa, la yetekellemune illa men ezine lehur rahmanu ve kale sevaba."},
  {id: 5711, sure_no: 39, ayet_no: 39, sure_adi: "Nebe", text: "ذٰلِكَ الْيَوْمُ الْحَقُّۚ فَمَنْ شَٓاءَ اتَّخَذَ اِلٰى رَبِّه۪ مَاٰباً", translation: "İşte bu Hakk gündür. Dileyen Rabb'ine varan bir yol edinir.", transliteration: "Zalikel yevmul hakk, femen şaettehaze ila rabbihi meaba."},
  {id: 5712, sure_no: 40, ayet_no: 40, sure_adi: "Nebe", text: "اِنَّٓا اَنْذَرْنَا‌كُمْ عَذَاباً قَر۪يباًۚ يَوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُولُ الْـكَافِرُ يَا لَيْتَن۪ي كُنْتُ تُرَاباً", translation: "Sizi yakın bir azapla uyardık. O gün kişi ellerinin yaptıklarıyla karşı karşıya gelecek ve Kafir kimse: \"Keşke toprak olsaydım.\" diyecek.", transliteration: "İnna enzernakum azaben kariba, yevme yenzurul mer'u ma kaddemet yedahu ve yekulul kafiru ya leyteni kuntu turaba."},
  {id: 5713, sure_no: 1, ayet_no: 1, sure_adi: "Naziat", text: "وَالنَّازِعَاتِ غَرْقاًۙ", translation: "Ant olsun şiddetle çekip alanlara.", transliteration: "Ven naziati garka."},
  {id: 5714, sure_no: 2, ayet_no: 2, sure_adi: "Naziat", text: "وَالنَّاشِطَاتِ نَشْطاًۙ", translation: "Ant olsun kolayca çekip alanlara.", transliteration: "Ven naşitati neşta."},
  {id: 5715, sure_no: 3, ayet_no: 3, sure_adi: "Naziat", text: "وَالسَّابِحَاتِ سَبْحاًۙ", translation: "Ant olsun yüzdükçe yüzenlere.", transliteration: "Ves sabihati sebha."},
  {id: 5716, sure_no: 4, ayet_no: 4, sure_adi: "Naziat", text: "فَالسَّابِقَاتِ سَبْقاًۙ", translation: "Ve de yarıştıkça yarışanlara.", transliteration: "Fes sabikati sebka."},
  {id: 5717, sure_no: 5, ayet_no: 5, sure_adi: "Naziat", text: "فَالْمُدَبِّرَاتِ اَمْراًۢ", translation: "Ve de işi yerine getirenlere.", transliteration: "Fel mudebbirati emra."},
  {id: 5718, sure_no: 6, ayet_no: 6, sure_adi: "Naziat", text: "يَوْمَ تَرْجُفُ الرَّاجِفَةُۙ", translation: "O Gün sarsan sarsacak.", transliteration: "Yevme tercufur racifeh."},
  {id: 5719, sure_no: 7, ayet_no: 7, sure_adi: "Naziat", text: "تَتْبَعُهَا الرَّادِفَةُۜ", translation: "Arkasından gelen onu izleyecek.", transliteration: "Tetbeuher radifeh."},
  {id: 5720, sure_no: 8, ayet_no: 8, sure_adi: "Naziat", text: "قُلُوبٌ يَوْمَئِذٍ وَاجِفَةٌۙ", translation: "O Gün kalpler endişe ile çarpar.", transliteration: "Kulubun yevmeizin vacifeh."},
  {id: 5721, sure_no: 9, ayet_no: 9, sure_adi: "Naziat", text: "اَبْصَارُهَا خَاشِعَةٌۢ", translation: "Bakışları korku içindedir.", transliteration: "Ebsaruha haşiah."},
  {id: 5722, sure_no: 10, ayet_no: 10, sure_adi: "Naziat", text: "يَقُولُونَ ءَاِنَّا لَمَرْدُودُونَ فِي الْحَافِرَةِۜ", translation: "\"Biz, tekrar eski halimize mi döndürülecekmişiz?\" diyorlar;", transliteration: "Yekulune e inna le merdudune fil hafireh."},
  {id: 5723, sure_no: 11, ayet_no: 11, sure_adi: "Naziat", text: "ءَاِذَا كُنَّا عِظَاماً نَخِرَةًۜ", translation: "Çürümüş, dağılmış kemikler olmuşken?", transliteration: "E iza kunna izamen nahıreh."},
  {id: 5724, sure_no: 12, ayet_no: 12, sure_adi: "Naziat", text: "قَالُوا تِلْكَ اِذاً كَرَّةٌ خَاسِرَةٌۢ", translation: "\"Öyleyse, bu hüsranlı bir dönüştür.\" dediler.", transliteration: "Kalu tilke izen kerretun hasireh."},
  {id: 5725, sure_no: 13, ayet_no: 13, sure_adi: "Naziat", text: "فَاِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌۙ", translation: "Oysaki o, tek bir haykırıştır!", transliteration: "Fe innema hiye zecretun vahıdeh."},
  {id: 5726, sure_no: 14, ayet_no: 14, sure_adi: "Naziat", text: "فَاِذَا هُمْ بِالسَّاهِرَةِۜ", translation: "Bir de bakmışsın meydandalar.", transliteration: "Fe iza hum bis sahireh."},
  {id: 5727, sure_no: 15, ayet_no: 15, sure_adi: "Naziat", text: "هَلْ اَتٰيكَ حَد۪يثُ مُوسٰىۢ", translation: "Musa'nın hadisi sana geldi mi?", transliteration: "Hel etake hadisu musa."},
  {id: 5728, sure_no: 16, ayet_no: 16, sure_adi: "Naziat", text: "اِذْ نَادٰيهُ رَبُّهُ بِالْوَادِ الْمُقَدَّسِ طُوًىۚ", translation: "Hani, Rabb'i ona kutsal vadi Tuva'da seslenmişti!", transliteration: "İz nadahu rabbuhu bil vadil mukaddesi tuva."},
  {id: 5729, sure_no: 17, ayet_no: 17, sure_adi: "Naziat", text: "اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهُ طَغٰىۘ", translation: "\"Firavun'a git. Kuşkusuz o azgınlaştı.\"", transliteration: "İzheb ila fir'avne innehu taga."},
  {id: 5730, sure_no: 18, ayet_no: 18, sure_adi: "Naziat", text: "فَقُلْ هَلْ لَكَ اِلٰٓى اَنْ تَزَكّٰىۙ", translation: "\"Git ona de ki: Arınmak ister misin?\"", transliteration: "Fe kul hel leke ila en tezekka."},
  {id: 5731, sure_no: 19, ayet_no: 19, sure_adi: "Naziat", text: "وَاَهْدِيَكَ اِلٰى رَبِّكَ فَتَخْشٰىۚ", translation: "\"Sana Rabb'inin yolunu göstereyim de içtenlikle O'na yönelmiş ol.", transliteration: "Ve ehdiyeke ila rabbike fe tahşa."},
  {id: 5732, sure_no: 20, ayet_no: 20, sure_adi: "Naziat", text: "فَاَرٰيهُ الْاٰيَةَ الْـكُبْرٰىۘ", translation: "Sonra Musa, Firavun'a büyük ayeti gösterdi.", transliteration: "Fe erahul ayetel kubra."},
  {id: 5733, sure_no: 21, ayet_no: 21, sure_adi: "Naziat", text: "فَـكَذَّبَ وَعَصٰىۘ", translation: "Fakat yalanladı ve karşı çıktı.", transliteration: "Fe kezzebe ve asa."},
  {id: 5734, sure_no: 22, ayet_no: 22, sure_adi: "Naziat", text: "ثُمَّ اَدْبَرَ يَسْعٰىۘ", translation: "Sonra aleyhte çalışmaya koyuldu.", transliteration: "Summe edbere yes'a."},
  {id: 5735, sure_no: 23, ayet_no: 23, sure_adi: "Naziat", text: "فَحَشَرَ فَنَادٰىۘ", translation: "Adamlarını toplayarak seslendi:", transliteration: "Fehaşere fe nada."},
  {id: 5736, sure_no: 24, ayet_no: 24, sure_adi: "Naziat", text: "فَقَالَ اَنَا۬ رَبُّكُمُ الْاَعْلٰىۘ", translation: "\"Ben sizin yüce rabbinizim.\" dedi.", transliteration: "Fe kale ene rabbukumul a'la."},
  {id: 5737, sure_no: 25, ayet_no: 25, sure_adi: "Naziat", text: "فَاَخَذَهُ اللّٰهُ نَكَالَ الْاٰخِرَةِ وَالْاُو۫لٰىۜ", translation: "Allah da onu dünya ve ahiret azabıyla cezalandırdı.", transliteration: "Fe ehazehullahu nekalel ahıreti vel ula."},
  {id: 5738, sure_no: 26, ayet_no: 26, sure_adi: "Naziat", text: "اِنَّ ف۪ي ذٰلِكَ لَعِبْرَةً لِمَنْ يَخْشٰىۜ۟", translation: "Kuşkusuz bunda huşu duyan kimse için ibret vardır.", transliteration: "İnne fi zalike le ıbreten li men yahşa."},
  {id: 5739, sure_no: 27, ayet_no: 27, sure_adi: "Naziat", text: "ءَاَنْتُمْ اَشَدُّ خَلْقاً اَمِ السَّمَٓاءُۜ بَنٰيهَا۠", translation: "Sizi yaratmak mı daha zor, yoksa göğü bina etmek mi?", transliteration: "E entum eşeddu halkan emis sema', benaha."},
  {id: 5740, sure_no: 28, ayet_no: 28, sure_adi: "Naziat", text: "رَفَعَ سَمْكَهَا فَسَوّٰيهَاۙ", translation: "Onu yükseltti ve düzene koydu.", transliteration: "Refea semkeha fe sevvaha."},
  {id: 5741, sure_no: 29, ayet_no: 29, sure_adi: "Naziat", text: "وَاَغْطَشَ لَيْلَهَا وَاَخْرَجَ ضُحٰيهَاۖ", translation: "Gecesini kararttı, gündüzünü aydınlattı.", transliteration: "Ve agtaşe leyleha ve ahrece duhaha."},
  {id: 5742, sure_no: 30, ayet_no: 30, sure_adi: "Naziat", text: "وَالْاَرْضَ بَعْدَ ذٰلِكَ دَحٰيهَاۜ", translation: "Yeryüzünü yayıp yuvarlattı.", transliteration: "Vel arda ba'de zalike dehaha."},
  {id: 5743, sure_no: 31, ayet_no: 31, sure_adi: "Naziat", text: "اَخْرَجَ مِنْهَا مَٓاءَهَا وَمَرْعٰيهَاۖ", translation: "Ondan suyunu ve yeşilliğini çıkardı.", transliteration: "Ahrece minha maeha ve mer'aha."},
  {id: 5744, sure_no: 32, ayet_no: 32, sure_adi: "Naziat", text: "وَالْجِبَالَ اَرْسٰيهَاۙ", translation: "Ona dağlar yerleştirdi.", transliteration: "Vel cibale ersaha."},
  {id: 5745, sure_no: 33, ayet_no: 33, sure_adi: "Naziat", text: "مَتَاعاً لَكُمْ وَلِاَنْعَامِكُمْۜ", translation: "Sizin ve hayvanlarınızın geçimi için.", transliteration: "Metaan lekum ve li en amikum."},
  {id: 5746, sure_no: 34, ayet_no: 34, sure_adi: "Naziat", text: "فَاِذَا جَٓاءَتِ الطَّٓامَّةُ الْكُبْرٰىۘ", translation: "Fakat o en büyük olay gerçekleştiği zaman.", transliteration: "Fe iza caetit tammetul kubra."},
  {id: 5747, sure_no: 35, ayet_no: 35, sure_adi: "Naziat", text: "يَوْمَ يَتَذَكَّرُ الْاِنْسَانُ مَا سَعٰىۙ", translation: "O Gün insan ne yaptığını hatırlar.", transliteration: "Yevme yetezekkerul insanu ma sea."},
  {id: 5748, sure_no: 36, ayet_no: 36, sure_adi: "Naziat", text: "وَبُرِّزَتِ الْجَح۪يمُ لِمَنْ يَرٰى", translation: "Gören kimseler için Cehennem açıkça gösterilecek.", transliteration: "Ve burrizetil cahimu li men yera."},
  {id: 5749, sure_no: 37, ayet_no: 37, sure_adi: "Naziat", text: "فَاَمَّا مَنْ طَغٰىۙ", translation: "Fakat kim azgınlaşmış,", transliteration: "Fe emma men taga."},
  {id: 5750, sure_no: 38, ayet_no: 38, sure_adi: "Naziat", text: "وَاٰثَرَ الْحَيٰوةَ الدُّنْيَاۙ", translation: "Yalnızca dünya hayatını tercih etmişse,", transliteration: "Ve aserel hayated dunya."},
  {id: 5751, sure_no: 39, ayet_no: 39, sure_adi: "Naziat", text: "فَاِنَّ الْجَح۪يمَ هِيَ الْمَأْوٰىۜ", translation: "Kuşkusuz Cehennem onun için barınaktır.", transliteration: "Fe innel cahime hiyel me'va."},
  {id: 5752, sure_no: 40, ayet_no: 40, sure_adi: "Naziat", text: "وَاَمَّا مَنْ خَافَ مَقَامَ رَبِّه۪ وَنَهَى النَّفْسَ عَنِ الْهَوٰىۙ", translation: "Fakat Rabb'inin huzurunda suçlu olmaktan korkarak, kendisini hevasından uzak tutana,", transliteration: "Ve emma men hafe makame rabbihi ve nehennefse anil heva."},
  {id: 5753, sure_no: 41, ayet_no: 41, sure_adi: "Naziat", text: "فَاِنَّ الْجَنَّةَ هِيَ الْمَأْوٰىۜ", translation: "Cennet onun için barınaktır.", transliteration: "Fe innel cennete hiyel me'va."},
  {id: 5754, sure_no: 42, ayet_no: 42, sure_adi: "Naziat", text: "يَسْـَٔلُونَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰيهَاۜ", translation: "Sana o Sa'at'ten soruyorlar; ne zaman gelip çatacakmış diye.", transliteration: "Yes'eluneke anis saati eyyane mursaha."},
  {id: 5755, sure_no: 43, ayet_no: 43, sure_adi: "Naziat", text: "ف۪يمَ اَنْتَ مِنْ ذِكْرٰيهَاۜ", translation: "Sen onun hakkında ne bilebilirsin ki?", transliteration: "Fime ente min zikraha."},
  {id: 5756, sure_no: 44, ayet_no: 44, sure_adi: "Naziat", text: "اِلٰى رَبِّكَ مُنْتَهٰيهَاۜ", translation: "Onun sonu Rabb'inedir.", transliteration: "İla rabbike muntehaha."},
  {id: 5757, sure_no: 45, ayet_no: 45, sure_adi: "Naziat", text: "اِنَّمَٓا اَنْتَ مُنْذِرُ مَنْ يَخْشٰيهَاۜ", translation: "Sen ancak ona huşu duyanlar için bir uyarıcısın.", transliteration: "İnnema ente munziru men yahşaha."},
  {id: 5758, sure_no: 46, ayet_no: 46, sure_adi: "Naziat", text: "كَاَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُٓوا اِلَّا عَشِيَّةً اَوْ ضُحٰيهَا", translation: "Onlar onu görecekleri gün, dünyada bir akşam veya kuşluğundan başka kalmamış sanacaklar.", transliteration: "Ke ennehum yevme yerevneha lem yelbesu illa aşiyyeten ev duhaha."},
  {id: 5759, sure_no: 1, ayet_no: 1, sure_adi: "Abese", text: "عَبَسَ وَتَوَلّٰىۙ", translation: "Surat astı ve yüz çevirdi.", transliteration: "Abese ve tevella."},
  {id: 5760, sure_no: 2, ayet_no: 2, sure_adi: "Abese", text: "اَنْ جَٓاءَهُ الْاَعْمٰىۜ", translation: "O gözleri görmeyen geldi diye.", transliteration: "En caehul a'ma."},
  {id: 5761, sure_no: 3, ayet_no: 3, sure_adi: "Abese", text: "وَمَا يُدْر۪يكَ لَعَلَّهُ يَزَّكّٰىۙ", translation: "Sen bilemezsin, belki o tezkiye olacak.", transliteration: "Ve ma yudrike leallehu yezzekka."},
  {id: 5762, sure_no: 4, ayet_no: 4, sure_adi: "Abese", text: "اَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرٰىۜ", translation: "Veya öğüt alır ve böylece öğüt ona yararlı olur.", transliteration: "Ev yezzekkeru fe tenfeahuz zikra."},
  {id: 5763, sure_no: 5, ayet_no: 5, sure_adi: "Abese", text: "اَمَّا مَنِ اسْتَغْنٰىۙ", translation: "Fakat öğüt almayı gereksiz gören o kimseye gelince de;", transliteration: "Emma menistagna."},
  {id: 5764, sure_no: 6, ayet_no: 6, sure_adi: "Abese", text: "فَاَنْتَ لَهُ تَصَدّٰىۜ", translation: "Sen ona ilgi gösteriyorsun.", transliteration: "Fe ente lehu tesadda."},
  {id: 5765, sure_no: 7, ayet_no: 7, sure_adi: "Abese", text: "وَمَا عَلَيْكَ اَلَّا يَزَّكّٰىۜ", translation: "Oysaki onun arınmak istememesinden sorumlu sen değilsin.", transliteration: "Ve ma aleyke ella yezzekka."},
  {id: 5766, sure_no: 8, ayet_no: 8, sure_adi: "Abese", text: "وَاَمَّا مَنْ جَٓاءَكَ يَسْعٰىۙ", translation: "Fakat sana koşarak gelen kimseye gelince;", transliteration: "Ve emma men caeke yes'a."},
  {id: 5767, sure_no: 9, ayet_no: 9, sure_adi: "Abese", text: "وَهُوَ يَخْشٰىۙ", translation: "O huşu duyanla,", transliteration: "Ve huve yahşa."},
  {id: 5768, sure_no: 10, ayet_no: 10, sure_adi: "Abese", text: "فَاَنْتَ عَنْهُ تَلَهّٰىۚ", translation: "Sen onunla ilgilenmiyorsun.", transliteration: "Fe ente anhu telehha."},
  {id: 5769, sure_no: 11, ayet_no: 11, sure_adi: "Abese", text: "كَلَّٓا اِنَّهَا تَذْكِرَةٌۚ", translation: "Hayır! Kuşkusuz o bir öğüttür.", transliteration: "Kella inneha tezkirah."},
  {id: 5770, sure_no: 12, ayet_no: 12, sure_adi: "Abese", text: "فَمَنْ شَٓاءَ ذَكَرَهُۢ", translation: "Dileyen kimse ondan öğüt alır.", transliteration: "Fe men şae zekerah."},
  {id: 5771, sure_no: 13, ayet_no: 13, sure_adi: "Abese", text: "ف۪ي صُحُفٍ مُكَرَّمَةٍۙ", translation: "O şerefli sayfalardadır,", transliteration: "Fi suhufin mukerrameh."},
  {id: 5772, sure_no: 14, ayet_no: 14, sure_adi: "Abese", text: "مَرْفُوعَةٍ مُطَهَّرَةٍۙ", translation: "Yüceltilmiş, temizlenmiş,", transliteration: "Merfuatin mutahherah."},
  {id: 5773, sure_no: 15, ayet_no: 15, sure_adi: "Abese", text: "بِاَيْد۪ي سَفَرَةٍۙ", translation: "Yazıcıların elleriyle,", transliteration: "Bi eydi seferah."},
  {id: 5774, sure_no: 16, ayet_no: 16, sure_adi: "Abese", text: "كِرَامٍ بَرَرَةٍۜ", translation: "Şerefli ve birr sahiplerince.", transliteration: "Kiramin berarah."},
  {id: 5775, sure_no: 17, ayet_no: 17, sure_adi: "Abese", text: "قُتِلَ الْاِنْسَانُ مَٓا اَكْفَرَهُۜ", translation: "O insan kendisini mahvetti, o ne kadar da nankördür.", transliteration: "Kutilel insanu ma ekferah."},
  {id: 5776, sure_no: 18, ayet_no: 18, sure_adi: "Abese", text: "مِنْ اَيِّ شَيْءٍ خَلَقَهُۜ", translation: "Onu hangi şeyden yarattı?", transliteration: "Min eyyi şey'in halakah."},
  {id: 5777, sure_no: 19, ayet_no: 19, sure_adi: "Abese", text: "مِنْ نُطْفَةٍۜ خَلَقَهُ فَقَدَّرَهُۙ", translation: "Bir nutfeden yarattı. Sonra da ona kader takdir etti.", transliteration: "Min nutfeh, halakahu fe kadderah."},
  {id: 5778, sure_no: 20, ayet_no: 20, sure_adi: "Abese", text: "ثُمَّ السَّب۪يلَ يَسَّرَهُۙ", translation: "Sonra yolu ona kolaylaştırdı.", transliteration: "Summes sebile yesserah."},
  {id: 5779, sure_no: 21, ayet_no: 21, sure_adi: "Abese", text: "ثُمَّ اَمَاتَهُ فَاَقْبَرَهُۙ", translation: "Sonra onu öldürdü ve kabre koydurdu.", transliteration: "Summe ematehu fe akberah."},
  {id: 5780, sure_no: 22, ayet_no: 22, sure_adi: "Abese", text: "ثُمَّ اِذَا شَٓاءَ اَنْشَرَهُۜ", translation: "Sonra dilediği zaman onu diriltip ortaya çıkardı.", transliteration: "Summe iza şae enşerah."},
  {id: 5781, sure_no: 23, ayet_no: 23, sure_adi: "Abese", text: "كَلَّا لَمَّا يَقْضِ مَٓا اَمَرَهُۜ", translation: "Hayır! Ona buyurduğu şeyi yerine getirmedi.", transliteration: "Kella lemma yakdı ma emerah."},
  {id: 5782, sure_no: 24, ayet_no: 24, sure_adi: "Abese", text: "فَلْيَنْظُرِ الْاِنْسَانُ اِلٰى طَعَامِه۪ۙ", translation: "O insan yiyeceğine bir baksın!", transliteration: "Felyanzuril insanu ila taamih."},
  {id: 5783, sure_no: 25, ayet_no: 25, sure_adi: "Abese", text: "اَنَّا صَبَبْنَا الْمَٓاءَ صَباًّۙ", translation: "Suyu nasıl akıttıkça akıttık.", transliteration: "Enna sabebnel mae sabba."},
  {id: 5784, sure_no: 26, ayet_no: 26, sure_adi: "Abese", text: "ثُمَّ شَقَقْنَا الْاَرْضَ شَقاًّۙ", translation: "Sonra yeri yardıkça yardık.", transliteration: "Summe şekaknel arda şakka."},
  {id: 5785, sure_no: 27, ayet_no: 27, sure_adi: "Abese", text: "فَاَنْبَتْنَا ف۪يهَا حَباًّۙ", translation: "Böylece orada taneler yetiştirdik;", transliteration: "Fe enbetna fiha habba."},
  {id: 5786, sure_no: 28, ayet_no: 28, sure_adi: "Abese", text: "وَعِنَباً وَقَضْباًۙ", translation: "Üzümler, sebzeler,", transliteration: "Ve ineben ve kadba."},
  {id: 5787, sure_no: 29, ayet_no: 29, sure_adi: "Abese", text: "وَزَيْتُوناً وَنَخْلاًۙ", translation: "Zeytinler, hurmalar,", transliteration: "Ve zeytunen ve nahla."},
  {id: 5788, sure_no: 30, ayet_no: 30, sure_adi: "Abese", text: "وَحَدَٓائِقَ غُلْباًۙ", translation: "Dalları iç içe girmiş bahçeler,", transliteration: "Ve hadaika gulba."},
  {id: 5789, sure_no: 31, ayet_no: 31, sure_adi: "Abese", text: "وَفَاكِهَةً وَاَباًّۙ", translation: "Meyveler ve otlaklar.", transliteration: "Ve fakiheten ve ebba."},
  {id: 5790, sure_no: 32, ayet_no: 32, sure_adi: "Abese", text: "مَتَاعاً لَكُمْ وَلِاَنْعَامِكُمْۜ", translation: "Size ve hayvanlarınıza geçim olarak.", transliteration: "Metaan lekum ve li en'amikum."},
  {id: 5791, sure_no: 33, ayet_no: 33, sure_adi: "Abese", text: "فَاِذَا جَٓاءَتِ الصَّٓاخَّةُۘ", translation: "Fakat o kulakları sağır edici ses geldiği zaman.", transliteration: "Fe iza caetis sahhah."},
  {id: 5792, sure_no: 34, ayet_no: 34, sure_adi: "Abese", text: "يَوْمَ يَفِرُّ الْمَرْءُ مِنْ اَخ۪يهِۙ", translation: "O Gün insan kardeşinden kaçar;", transliteration: "Yevme yefirrul mer'u min ehih."},
  {id: 5793, sure_no: 35, ayet_no: 35, sure_adi: "Abese", text: "وَاُمِّه۪ وَاَب۪يهِۙ", translation: "Annesinden ve babasından,", transliteration: "Ve ummihi ve ebih."},
  {id: 5794, sure_no: 36, ayet_no: 36, sure_adi: "Abese", text: "وَصَاحِبَتِه۪ وَبَن۪يهِۜ", translation: "Eşinden ve çocuklarından.", transliteration: "Ve sahıbetihi ve benih."},
  {id: 5795, sure_no: 37, ayet_no: 37, sure_adi: "Abese", text: "لِكُلِّ امْرِئٍ مِنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْن۪يهِۜ", translation: "Onların hepsinin O Gün başından aşkın uğraşısı vardır.", transliteration: "Li kullimriin minhum yevmeizin şe'nun yugnih."},
  {id: 5796, sure_no: 38, ayet_no: 38, sure_adi: "Abese", text: "وُجُوهٌ يَوْمَئِذٍ مُسْفِرَةٌۙ", translation: "O Gün pırıl pırıl yüzler vardır;", transliteration: "Vucuhun yevmeizin musfirah."},
  {id: 5797, sure_no: 39, ayet_no: 39, sure_adi: "Abese", text: "ضَاحِكَةٌ مُسْتَبْشِرَةٌۚ", translation: "Gülen, sevinen.", transliteration: "Dahıketun mustebşirah."},
  {id: 5798, sure_no: 40, ayet_no: 40, sure_adi: "Abese", text: "وَوُجُوهٌ يَوْمَئِذٍ عَلَيْهَا غَبَرَةٌۙ", translation: "O Gün, kimi yüzler de tozludur,", transliteration: "Ve vucuhun yevmeizin aleyha gaberah."},
  {id: 5799, sure_no: 41, ayet_no: 41, sure_adi: "Abese", text: "تَرْهَقُهَا قَتَرَةٌۜ", translation: "Onu bir karanlık kaplar.", transliteration: "Terhekuha katerah."},
  {id: 5800, sure_no: 42, ayet_no: 42, sure_adi: "Abese", text: "اُو۬لٰٓئِكَ هُـمُ الْكَفَرَةُ الْفَجَرَةُ", translation: "İşte bunlar Kafirlerdir, facirlerdir.", transliteration: "Ulaike humul keferetul fecerah."},
  {id: 5801, sure_no: 1, ayet_no: 1, sure_adi: "Tekvir", text: "اِذَا الشَّمْسُ كُـوِّرَتْۙۖ", translation: "Güneş köreltildiği zaman,", transliteration: "İzeş şemsu kuvviret."},
  {id: 5802, sure_no: 2, ayet_no: 2, sure_adi: "Tekvir", text: "وَاِذَا النُّجُومُ انْكَدَرَتْۙۖ", translation: "Yıldızlar bulanıklaştığı zaman,", transliteration: "Ve izen nucumun kederet."},
  {id: 5803, sure_no: 3, ayet_no: 3, sure_adi: "Tekvir", text: "وَاِذَا الْجِبَالُ سُيِّرَتْۙۖ", translation: "Dağlar yürütüldüğü zaman,", transliteration: "Ve izelcibalu suyyiret."},
  {id: 5804, sure_no: 4, ayet_no: 4, sure_adi: "Tekvir", text: "وَاِذَا الْعِشَارُ عُطِّلَتْۙۖ", translation: "Gebe develer başıboş bırakıldığı zaman,", transliteration: "Ve izel ışaru uttılet."},
  {id: 5805, sure_no: 5, ayet_no: 5, sure_adi: "Tekvir", text: "وَاِذَا الْوُحُوشُ حُشِرَتْۙۖ", translation: "Vahşi hayvanlar bir araya toplandıkları zaman,", transliteration: "Ve izel vuhuşu huşiret."},
  {id: 5806, sure_no: 6, ayet_no: 6, sure_adi: "Tekvir", text: "وَاِذَا الْبِحَارُ سُجِّرَتْۙۖ", translation: "Denizler kaynatıldığı zaman,", transliteration: "Ve izel biharu succiret."},
  {id: 5807, sure_no: 7, ayet_no: 7, sure_adi: "Tekvir", text: "وَاِذَا النُّفُوسُ زُوِّجَتْۙۖ", translation: "Nefisler eşleştirildiği zaman,", transliteration: "Ve izen nufusu zuvvicet."},
  {id: 5808, sure_no: 8, ayet_no: 8, sure_adi: "Tekvir", text: "وَاِذَا الْمَوْءُ۫دَةُ سُئِلَتْۙ", translation: "O diri diri toprağa gömülen kıza sorulduğu zaman;", transliteration: "Ve izel mev'udetu suilet."},
  {id: 5809, sure_no: 9, ayet_no: 9, sure_adi: "Tekvir", text: "بِاَيِّ ذَنْبٍ قُتِلَتْۚ", translation: "Hangi suçtan dolayı öldürüldüğü!", transliteration: "Bi eyyi zenbin kutilet."},
  {id: 5810, sure_no: 10, ayet_no: 10, sure_adi: "Tekvir", text: "وَاِذَا الصُّحُفُ نُشِرَتْۙۖ", translation: "Defterler açıldığı zaman,", transliteration: "Ve izes suhufu nuşiret."},
  {id: 5811, sure_no: 11, ayet_no: 11, sure_adi: "Tekvir", text: "وَاِذَا السَّمَٓاءُ كُشِطَتْۙۖ", translation: "Gök soyulduğu zaman,", transliteration: "Ve izes semau kuşitat."},
  {id: 5812, sure_no: 12, ayet_no: 12, sure_adi: "Tekvir", text: "وَاِذَا الْجَح۪يمُ سُعِّرَتْۙۖ", translation: "Cehennem kızıştırıldığı zaman,", transliteration: "Ve izel cahimu su'ıret."},
  {id: 5813, sure_no: 13, ayet_no: 13, sure_adi: "Tekvir", text: "وَاِذَا الْجَنَّةُ اُزْلِفَتْۙۖ", translation: "Cennet yaklaştırıldığı zaman,", transliteration: "Ve izel cennetu uzlifet."},
  {id: 5814, sure_no: 14, ayet_no: 14, sure_adi: "Tekvir", text: "عَلِمَتْ نَفْسٌ مَٓا اَحْضَرَتْۜ", translation: "İnsan, kendisi ile ne getirdiğini görecektir.", transliteration: "Alimet nefsün ma ahdaret."},
  {id: 5815, sure_no: 15, ayet_no: 15, sure_adi: "Tekvir", text: "فَلَٓا اُقْسِمُ بِالْخُنَّسِۙ", translation: "Hayır, ant olsun sinenlere,", transliteration: "Fe la uksimu bil hunnes."},
  {id: 5816, sure_no: 16, ayet_no: 16, sure_adi: "Tekvir", text: "اَلْجَوَارِ الْكُنَّسِۙ", translation: "Yörüngesinde dönenlere,", transliteration: "El cevaril kunnes."},
  {id: 5817, sure_no: 17, ayet_no: 17, sure_adi: "Tekvir", text: "وَالَّيْلِ اِذَا عَسْعَسَۙ", translation: "Kararmaya başladığı an geceye,", transliteration: "Vel leyli iza as'as."},
  {id: 5818, sure_no: 18, ayet_no: 18, sure_adi: "Tekvir", text: "وَالصُّبْحِ اِذَا تَنَفَّسَۙ", translation: "Soluk almaya başladığı zaman sabaha ant olsun ki,", transliteration: "Ves subhı iza teneffes."},
  {id: 5819, sure_no: 19, ayet_no: 19, sure_adi: "Tekvir", text: "اِنَّهُ لَقَوْلُ رَسُولٍ كَر۪يمٍۙ", translation: "Kuşkusuz o çok şerefli bir resul sözüdür;", transliteration: "İnnehu le kavlu resulin kerim."},
  {id: 5820, sure_no: 20, ayet_no: 20, sure_adi: "Tekvir", text: "ذ۪ي قُوَّةٍ عِنْدَ ذِي الْعَرْشِ مَك۪ينٍۙ", translation: "Güç sahibi, arşın sahibi yanında itibarlıdır.", transliteration: "Zi kuvvetin ınde zil arşi mekin."},
  {id: 5821, sure_no: 21, ayet_no: 21, sure_adi: "Tekvir", text: "مُطَاعٍ ثَمَّ اَم۪ينٍۜ", translation: "Kendisine itaat edilen, orada güvenilendir.", transliteration: "Mutaın semme emin."},
  {id: 5822, sure_no: 22, ayet_no: 22, sure_adi: "Tekvir", text: "وَمَا صَاحِبُكُمْ بِمَجْنُونٍۚ", translation: "Arkadaşınız mecnun değildir.", transliteration: "Ve ma sahıbukum bi mecnun."},
  {id: 5823, sure_no: 23, ayet_no: 23, sure_adi: "Tekvir", text: "وَلَقَدْ رَاٰهُ بِالْاُفُقِ الْمُب۪ينِۚ", translation: "Ant olsun o, onu açık ufukta gördü.", transliteration: "Ve lekad reahu bil ufukıl mubin."},
  {id: 5824, sure_no: 24, ayet_no: 24, sure_adi: "Tekvir", text: "وَمَا هُوَ عَلَى الْغَيْبِ بِضَن۪ينٍۚ", translation: "O vahyi gizlemez.", transliteration: "Ve ma huve alel gaybi bi danin."},
  {id: 5825, sure_no: 25, ayet_no: 25, sure_adi: "Tekvir", text: "وَمَا هُوَ بِقَوْلِ شَيْطَانٍ رَج۪يمٍۚ", translation: "O, taşlanmış şeytanın sözü değildir.", transliteration: "Ve ma huve bi kavli şeytanin recim."},
  {id: 5826, sure_no: 26, ayet_no: 26, sure_adi: "Tekvir", text: "فَاَيْنَ تَذْهَبُونَۜ", translation: "O halde nereye gidiyorsunuz?", transliteration: "Fe eyne tezhebun."},
  {id: 5827, sure_no: 27, ayet_no: 27, sure_adi: "Tekvir", text: "اِنْ هُوَ اِلَّا ذِكْرٌ لِلْعَالَم۪ينَۙ", translation: "O alemler için zikirden başka bir şey değildir.", transliteration: "İn huve illa zikrun lil alemin."},
  {id: 5828, sure_no: 28, ayet_no: 28, sure_adi: "Tekvir", text: "لِمَنْ شَٓاءَ مِنْكُمْ اَنْ يَسْتَق۪يمَ", translation: "Sizden doğru yoldan gitmek isteyenler için.", transliteration: "Li men şae minkum en yestekim."},
  {id: 5829, sure_no: 29, ayet_no: 29, sure_adi: "Tekvir", text: "وَمَا تَشَٓاؤُ۫نَ اِلَّٓا اَنْ يَشَٓاءَ اللّٰهُ رَبُّ الْعَالَم۪ينَ", translation: "Alemlerin Rabb'i Allah dilemedikçe siz dileyemezsiniz.", transliteration: "Ve ma teşaune illa en yeşaallahu rabbul alemin."},
  {id: 5830, sure_no: 1, ayet_no: 1, sure_adi: "İnfitar", text: "اِذَا السَّمَٓاءُ انْفَطَرَتْۙ", translation: "Göğün yasaları yeniden belirlendiği zaman,", transliteration: "İzes semaunfetaret."},
  {id: 5831, sure_no: 2, ayet_no: 2, sure_adi: "İnfitar", text: "وَاِذَا الْـكَوَاكِبُ انْتَثَرَتْۙ", translation: "Yıldızlar dağıldığı zaman,", transliteration: "Ve izel kevakibunteseret."},
  {id: 5832, sure_no: 3, ayet_no: 3, sure_adi: "İnfitar", text: "وَاِذَا الْبِحَارُ فُجِّرَتْۙ", translation: "Denizler kabarıp taştığı zaman,", transliteration: "Ve izel biharu fucciret."},
  {id: 5833, sure_no: 4, ayet_no: 4, sure_adi: "İnfitar", text: "وَاِذَا الْقُبُورُ بُعْثِرَتْۙ", translation: "Kabirler alt üst edildiği zaman,", transliteration: "Ve izel kuburu bu'siret."},
  {id: 5834, sure_no: 5, ayet_no: 5, sure_adi: "İnfitar", text: "عَلِمَتْ نَفْسٌ مَا قَدَّمَتْ وَاَخَّرَتْۜ", translation: "Herkes yaptığını ve yapmadığını bilecek.", transliteration: "Alimet nefsun ma kaddemet ve ahharet."},
  {id: 5835, sure_no: 6, ayet_no: 6, sure_adi: "İnfitar", text: "يَٓا اَيُّهَا الْاِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْـكَر۪يمِۙ", translation: "Ey insan! Kerim olan Rabb'ine karşı seni aldatan nedir?", transliteration: "Ya eyyuhel insanu ma garreke bi rabbikel kerim."},
  {id: 5836, sure_no: 7, ayet_no: 7, sure_adi: "İnfitar", text: "اَلَّذ۪ي خَلَقَكَ فَسَوّٰيكَ فَعَدَلَكَۙ", translation: "Seni yarattı, sonra sana şekil verdi, organlarını uyumlu ve dengeli yaptı.", transliteration: "Ellezi halakake fe sevvake fe adelek."},
  {id: 5837, sure_no: 8, ayet_no: 8, sure_adi: "İnfitar", text: "ف۪ٓي اَيِّ صُورَةٍ مَا شَٓاءَ رَكَّبَكَۜ", translation: "Seni dilediği şekilde düzenledi.", transliteration: "Fi eyyi suretin ma şae rekkebek."},
  {id: 5838, sure_no: 9, ayet_no: 9, sure_adi: "İnfitar", text: "كَلَّا بَلْ تُكَذِّبُونَ بِالدّ۪ينِۙ", translation: "Hayır! Bilakis, siz dini yalanlıyorsunuz!", transliteration: "Kella bel tukezzibune bid din."},
  {id: 5839, sure_no: 10, ayet_no: 10, sure_adi: "İnfitar", text: "وَاِنَّ عَلَيْكُمْ لَحَافِظ۪ينَۙ", translation: "Sizin üzerinizde kesinlikle kaydediciler var,", transliteration: "Ve inne aleykum le hafızin."},
  {id: 5840, sure_no: 11, ayet_no: 11, sure_adi: "İnfitar", text: "كِرَاماً كَاتِب۪ينَۙ", translation: "Şerefli yazıcılar.", transliteration: "Kiramen katibin."},
  {id: 5841, sure_no: 12, ayet_no: 12, sure_adi: "İnfitar", text: "يَعْلَمُونَ مَا تَفْعَلُونَ", translation: "Yaptığınız her şeyi bilirler.", transliteration: "Ya'lemune ma tef'alun."},
  {id: 5842, sure_no: 13, ayet_no: 13, sure_adi: "İnfitar", text: "اِنَّ الْاَبْرَارَ لَف۪ي نَع۪يمٍۚ", translation: "Birr olanlar nimet içindedirler.", transliteration: "İnnel ebrare lefi naim."},
  {id: 5843, sure_no: 14, ayet_no: 14, sure_adi: "İnfitar", text: "وَاِنَّ الْفُجَّارَ لَف۪ي جَح۪يمٍۚ", translation: "Facirler Cehennem'dedirler.", transliteration: "Ve innel fuccare lefi cahim."},
  {id: 5844, sure_no: 15, ayet_no: 15, sure_adi: "İnfitar", text: "يَصْلَوْنَهَا يَوْمَ الدّ۪ينِ", translation: "Din Günü ona yaslanırlar.", transliteration: "Yaslevneha yevmed din."},
  {id: 5845, sure_no: 16, ayet_no: 16, sure_adi: "İnfitar", text: "وَمَا هُمْ عَنْهَا بِغَٓائِب۪ينَۜ", translation: "Onlar, onda yanıp bitecek değillerdir.", transliteration: "Ve ma hum anha bi gaibin."},
  {id: 5846, sure_no: 17, ayet_no: 17, sure_adi: "İnfitar", text: "وَمَٓا اَدْرٰيكَ مَا يَوْمُ الدّ۪ينِۙ", translation: "Din Günü'nün ne olduğunu sen nereden bileceksin?", transliteration: "Ve ma edrake ma yevmud din."},
  {id: 5847, sure_no: 18, ayet_no: 18, sure_adi: "İnfitar", text: "ثُمَّ مَٓا اَدْرٰيكَ مَا يَوْمُ الدّ۪ينِۜ", translation: "Evet, Din Günü'nün ne olduğunu sen nereden bileceksin?", transliteration: "Summe ma edrake ma yevmud din."},
  {id: 5848, sure_no: 19, ayet_no: 19, sure_adi: "İnfitar", text: "يَوْمَ لَا تَمْلِكُ نَفْسٌ لِنَفْسٍ شَيْـٔاًۜ وَالْاَمْرُ يَوْمَئِذٍ لِلّٰهِ", translation: "O, kimsenin kimseye yardım etmeye gücünün yetmeyeceği bir gündür. O gün karar vermek bütünüyle Allah'a aittir.", transliteration: "Yevme la temliku nefsun li nefsin şey'a, vel emru yevmeizin lillah."},
  {id: 5849, sure_no: 1, ayet_no: 1, sure_adi: "Mutaffifin", text: "وَيْلٌ لِلْمُطَفِّف۪ينَۙ", translation: "Hile yapanların vay haline!", transliteration: "Veylun lil mutaffifin."},
  {id: 5850, sure_no: 2, ayet_no: 2, sure_adi: "Mutaffifin", text: "اَلَّذ۪ينَ اِذَا ا‌كْتَالُوا عَلَى النَّاسِ يَسْتَوْفُونَۘ", translation: "Onlar, alırlarken tam olarak ölçer, tartarlar.", transliteration: "Ellezine izektalu alen nasi yestevfun."},
  {id: 5851, sure_no: 3, ayet_no: 3, sure_adi: "Mutaffifin", text: "وَاِذَا كَالُوهُمْ اَوْ وَزَنُوهُمْ يُخْسِرُونَۜ", translation: "Satarlarken eksik ölçer, tartarlar.", transliteration: "Ve iza kaluhum ev vezenuhum yuhsirun."},
  {id: 5852, sure_no: 4, ayet_no: 4, sure_adi: "Mutaffifin", text: "اَلَا يَظُنُّ اُو۬لٰٓئِكَ اَنَّهُمْ مَبْعُوثُونَۙ", translation: "Onlar diriltileceklerini bilmiyorlar mı?", transliteration: "Ela yezunnu ulaike ennehum meb'usun."},
  {id: 5853, sure_no: 5, ayet_no: 5, sure_adi: "Mutaffifin", text: "لِيَوْمٍ عَظ۪يمٍۙ", translation: "Büyük bir gün için.", transliteration: "Li yevmin azim."},
  {id: 5854, sure_no: 6, ayet_no: 6, sure_adi: "Mutaffifin", text: "يَوْمَ يَقُومُ النَّاسُ لِرَبِّ الْعَالَم۪ينَۜ", translation: "O Gün İnsanlar alemlerin Rabb'inin divanında duracaklar.", transliteration: "Yevme yekumun nasu li rabbil alemin."},
  {id: 5855, sure_no: 7, ayet_no: 7, sure_adi: "Mutaffifin", text: "كَلَّٓا اِنَّ كِتَابَ الْفُجَّارِ لَف۪ي سِجّ۪ينٍۜ", translation: "Hayır! Kötülerin kayıtları kesinlikle siccindedir.", transliteration: "Kella inne kitabel fuccari le fi siccin."},
  {id: 5856, sure_no: 8, ayet_no: 8, sure_adi: "Mutaffifin", text: "وَمَٓا اَدْرٰيكَ مَا سِجّ۪ينٌۜ", translation: "Siccinin ne olduğunu sen ne bileceksin!", transliteration: "Ve ma edrake ma siccin."},
  {id: 5857, sure_no: 9, ayet_no: 9, sure_adi: "Mutaffifin", text: "كِتَابٌ مَرْقُومٌۜ", translation: "Her şeyin tek tek yazıldığı bir kitaptır.", transliteration: "Kitabun merkum."},
  {id: 5858, sure_no: 10, ayet_no: 10, sure_adi: "Mutaffifin", text: "وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّب۪ينَۙ", translation: "O Gün, yalanlayanların vay haline!", transliteration: "Veylun yevmeizin lil mukezzibin."},
  {id: 5859, sure_no: 11, ayet_no: 11, sure_adi: "Mutaffifin", text: "اَلَّذ۪ينَ يُكَذِّبُونَ بِيَوْمِ الدّ۪ينِۜ", translation: "Onlar, Din Günü'nü yalanlıyorlar.", transliteration: "Ellezine yukezzibune bi yevmiddin."},
  {id: 5860, sure_no: 12, ayet_no: 12, sure_adi: "Mutaffifin", text: "وَمَا يُكَذِّبُ بِه۪ٓ اِلَّا كُلُّ مُعْتَدٍ اَث۪يمٍۙ", translation: "Onu haddi aşan günahkardan başka kimse yalanlamaz.", transliteration: "Ve ma yukezzıbu bihi illa kullu mu'tedin esim."},
  {id: 5861, sure_no: 13, ayet_no: 13, sure_adi: "Mutaffifin", text: "اِذَا تُتْلٰى عَلَيْهِ اٰيَاتُنَا قَالَ اَسَاط۪يرُ الْاَوَّل۪ينَۜ", translation: "Ona ayetlerimiz okunduğu zaman, \"Eskilerin masalları.\" der.", transliteration: "İza tutla aleyhi ayatuna kale esatirul evvelin."},
  {id: 5862, sure_no: 14, ayet_no: 14, sure_adi: "Mutaffifin", text: "كَلَّا بَلْ۔ رَانَ عَلٰى قُلُوبِهِمْ مَا كَانُوا يَكْسِبُونَ", translation: "Hayır, hayır! Bilakis, onların yapıp ettikleri şeyler kalplerini kararttı.", transliteration: "Kella bel rane ala kulubihim ma kanu yeksibun."},
  {id: 5863, sure_no: 15, ayet_no: 15, sure_adi: "Mutaffifin", text: "كَلَّٓا اِنَّهُمْ عَنْ رَبِّهِمْ يَوْمَئِذٍ لَمَحْجُوبُونَۜ", translation: "Hayır! Onlar, O Gün Rabb'lerinden perdelenmiş olanlardır.", transliteration: "Kella innehum an rabbihim yevmeizin le mahcubun."},
  {id: 5864, sure_no: 16, ayet_no: 16, sure_adi: "Mutaffifin", text: "ثُمَّ اِنَّهُمْ لَصَالُوا الْجَح۪يمِۜ", translation: "Sonra onlar, Cehennem'e girecekler.", transliteration: "Summe innehum le salul cahim."},
  {id: 5865, sure_no: 17, ayet_no: 17, sure_adi: "Mutaffifin", text: "ثُمَّ يُقَالُ هٰذَا الَّذ۪ي كُنْتُمْ بِه۪ تُكَذِّبُونَۜ", translation: "Sonra da: \"İşte bu yalanlayıp durduğunuz şeydir.\" denecek.", transliteration: "Summe yukalu hazellezi kuntum bihi tukezzibun."},
  {id: 5866, sure_no: 18, ayet_no: 18, sure_adi: "Mutaffifin", text: "كَلَّٓا اِنَّ كِتَابَ الْاَبْرَارِ لَف۪ي عِلِّيّ۪ينَۜ", translation: "Hayır! Kuşkusuz ebrar olanların kayıtları kesinlikle İlliyyin'dedir.", transliteration: "Kella inne kitabel ebrari lefi illiyyin."},
  {id: 5867, sure_no: 19, ayet_no: 19, sure_adi: "Mutaffifin", text: "وَمَٓا اَدْرٰيكَ مَا عِلِّيُّونَۜ", translation: "İlliyyin'in ne olduğunu sen ne bileceksin?", transliteration: "Ve ma edrake ma ılliyyun."},
  {id: 5868, sure_no: 20, ayet_no: 20, sure_adi: "Mutaffifin", text: "كِتَابٌ مَرْقُومٌۙ", translation: "Her şeyin tek tek yazıldığı bir kitaptır.", transliteration: "Kitabun merkum."},
  {id: 5869, sure_no: 21, ayet_no: 21, sure_adi: "Mutaffifin", text: "يَشْهَدُهُ الْمُقَرَّبُونَۜ", translation: "Yaklaştırılmış olanlar ona tanık olurlar.", transliteration: "Yeşheduhul mukarrebun."},
  {id: 5870, sure_no: 22, ayet_no: 22, sure_adi: "Mutaffifin", text: "اِنَّ الْاَبْرَارَ لَف۪ي نَع۪يمٍۙ", translation: "Ebrar kesinlikle nimetler içindedir,", transliteration: "İnnel ebrare le fi naim."},
  {id: 5871, sure_no: 23, ayet_no: 23, sure_adi: "Mutaffifin", text: "عَلَى الْاَرَٓائِكِ يَنْظُرُونَۙ", translation: "Tahtlar üzerinde nimetleri seyre dalarlar.", transliteration: "Alel eraiki yenzurun."},
  {id: 5872, sure_no: 24, ayet_no: 24, sure_adi: "Mutaffifin", text: "تَعْرِفُ ف۪ي وُجُوهِهِمْ نَضْرَةَ النَّع۪يمِۚ", translation: "Nimetlerin sevincini yüzlerinden anlarsın.", transliteration: "Ta'rifu fi vucuhihim nadraten naim."},
  {id: 5873, sure_no: 25, ayet_no: 25, sure_adi: "Mutaffifin", text: "يُسْقَوْنَ مِنْ رَح۪يقٍ مَخْتُومٍۙ", translation: "Onlara mühürlenmiş en leziz, en saf içecekler içirilir.", transliteration: "Yuskavne min rahikın mahtum."},
  {id: 5874, sure_no: 26, ayet_no: 26, sure_adi: "Mutaffifin", text: "خِتَامُهُ مِسْكٌۜ وَف۪ي ذٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُونَۜ", translation: "Onun sonu misktir. Yarışanlar bunun için yarışsınlar.", transliteration: "Hitamuhu misk. ve fi zalike fel yetenafesil mutenafisun."},
  {id: 5875, sure_no: 27, ayet_no: 27, sure_adi: "Mutaffifin", text: "وَمِزَاجُهُ مِنْ تَسْن۪يمٍۙ", translation: "Onun katkısı tesnimdir.", transliteration: "Ve mizacuhu min tesnim."},
  {id: 5876, sure_no: 28, ayet_no: 28, sure_adi: "Mutaffifin", text: "عَيْناً يَشْرَبُ بِهَا الْمُقَرَّبُونَۜ", translation: "Bir pınardır, yakınlaştırılmış olanların içtiği.", transliteration: "Aynen yeşrebu bihel mukarrabun."},
  {id: 5877, sure_no: 29, ayet_no: 29, sure_adi: "Mutaffifin", text: "اِنَّ الَّذ۪ينَ اَجْرَمُوا كَانُوا مِنَ الَّذ۪ينَ اٰمَنُوا يَضْحَكُونَۘ", translation: "Suçlular, dünyada İman Edenlere gülüyorlardı.", transliteration: "İnnellezine ecremu kanu minellezine amenu yadhakun."},
  {id: 5878, sure_no: 30, ayet_no: 30, sure_adi: "Mutaffifin", text: "وَاِذَا مَرُّوا بِهِمْ يَتَغَامَزُونَۘ", translation: "İman Edenler yanlarından geçerlerken, birbirlerine kaş-göz ederek onlarla alay ediyorlardı.", transliteration: "Ve iza merru bihim yetegamezune."},
  {id: 5879, sure_no: 31, ayet_no: 31, sure_adi: "Mutaffifin", text: "وَاِذَا انْقَلَـبُٓوا اِلٰٓى اَهْلِهِمُ انْقَلَبُوا فَكِه۪ينَۘ", translation: "Yandaşlarına döndükleri zaman da neşelenmiş olarak dönüyorlardı.", transliteration: "Ve izenkalebu ila ehlihimunkalebu fekihin."},
  {id: 5880, sure_no: 32, ayet_no: 32, sure_adi: "Mutaffifin", text: "وَاِذَا رَاَوْهُمْ قَالُٓوا اِنَّ هٰٓؤُ۬لَٓاءِ لَضَٓالُّونَۙ", translation: "İman Edenleri gördükleri zaman, \"İşte bunlar, kesinlikle sapkın olanlardır.\" diyorlardı.", transliteration: "Ve iza reevhum kalu inne haulai ledallun."},
  {id: 5881, sure_no: 33, ayet_no: 33, sure_adi: "Mutaffifin", text: "وَمَٓا اُرْسِلُوا عَلَيْهِمْ حَافِظ۪ينَۜ", translation: "İman Edenlerin üzerlerine gözetici olarak gönderilmediler.", transliteration: "Ve ma ursilu aleyhim hafızin."},
  {id: 5882, sure_no: 34, ayet_no: 34, sure_adi: "Mutaffifin", text: "فَالْيَوْمَ الَّذ۪ينَ اٰمَنُوا مِنَ الْكُفَّارِ يَضْحَكُونَۙ", translation: "Artık bugün İman Edenler, Kafirlere gülüyorlar.", transliteration: "Felyevmellezine amenu minel kuffarı yadhakun."},
  {id: 5883, sure_no: 35, ayet_no: 35, sure_adi: "Mutaffifin", text: "عَلَى الْاَرَٓائِكِۙ يَنْظُرُونَۜ", translation: "Tahtlar üzerinde seyirdeler:", transliteration: "Alel eraiki yanzurun."},
  {id: 5884, sure_no: 36, ayet_no: 36, sure_adi: "Mutaffifin", text: "هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوا يَفْعَلُونَ", translation: "\"Kafirler, yaptıkları şeylerin karşılığını buldular mı?\" diye.", transliteration: "Hel suvvibel kuffaru ma kanu yef'alun."},
  {id: 5885, sure_no: 1, ayet_no: 1, sure_adi: "İnşikak", text: "اِذَا السَّمَٓاءُ انْشَقَّتْۙ", translation: "Gök yarıldığı zaman,", transliteration: "İzes semaunşakkat."},
  {id: 5886, sure_no: 2, ayet_no: 2, sure_adi: "İnşikak", text: "وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۙ", translation: "Rabb'inin buyruğunu yerine getirdiği zaman,", transliteration: "Ve ezinet li rabbiha ve hukkat."},
  {id: 5887, sure_no: 3, ayet_no: 3, sure_adi: "İnşikak", text: "وَاِذَا الْاَرْضُ مُدَّتْۙ", translation: "Yeryüzü uzatıldığı zaman,", transliteration: "Ve izel ardu muddet."},
  {id: 5888, sure_no: 4, ayet_no: 4, sure_adi: "İnşikak", text: "وَاَلْقَتْ مَا ف۪يهَا وَتَخَلَّتْۙ", translation: "İçinde ne varsa atıp boşaldığı zaman,", transliteration: "Ve elkat ma fiha ve tehallet."},
  {id: 5889, sure_no: 5, ayet_no: 5, sure_adi: "İnşikak", text: "وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۜ", translation: "Rabb'inin buyruğunu yerine getirdiği zaman,", transliteration: "Ve ezinet li rabbiha ve hukkat."},
  {id: 5890, sure_no: 6, ayet_no: 6, sure_adi: "İnşikak", text: "يَٓا اَيُّهَا الْاِنْسَانُ اِنَّكَ كَادِحٌ اِلٰى رَبِّكَ كَدْحاً فَمُلَاق۪يهِۚ", translation: "Ey İnsan! Rabb'ine kavuşuncaya kadar didinip duracak ve sonunda O'na kavuşacaksın.", transliteration: "Ya eyyuhel insanu inneke kadihun ila rabbike kedhan fe mulakih."},
  {id: 5891, sure_no: 7, ayet_no: 7, sure_adi: "İnşikak", text: "فَاَمَّا مَنْ اُو۫تِيَ كِتَابَهُ بِيَم۪ينِه۪ۙ", translation: "Kime kitabı sağından verilirse,", transliteration: "Fe emma men utiye kitabehu bi yeminih."},
  {id: 5892, sure_no: 8, ayet_no: 8, sure_adi: "İnşikak", text: "فَسَوْفَ يُحَاسَبُ حِسَاباً يَس۪يراًۙ", translation: "Hesabı çok kolay görülecek.", transliteration: "Fe sevfe yuhasebu hısaben yesira."},
  {id: 5893, sure_no: 9, ayet_no: 9, sure_adi: "İnşikak", text: "وَيَنْقَلِبُ اِلٰٓى اَهْلِه۪ مَسْرُوراًۜ", translation: "Ve sevinç içinde yakınlarına dönecek.", transliteration: "Ve yenkalibu ila ehlihi mesrura."},
  {id: 5894, sure_no: 10, ayet_no: 10, sure_adi: "İnşikak", text: "وَاَمَّا مَنْ اُو۫تِيَ كِتَابَهُ وَرَٓاءَ ظَهْرِه۪ۙ", translation: "Fakat kime kitabı arkasından verilirse,", transliteration: "Ve emma men utiye kitabehu verae zahrih."},
  {id: 5895, sure_no: 11, ayet_no: 11, sure_adi: "İnşikak", text: "فَسَوْفَ يَدْعُوا ثُبُوراًۙ", translation: "Ölüp yok olmak isteyecek.", transliteration: "Fe sevfe yed'u subura."},
  {id: 5896, sure_no: 12, ayet_no: 12, sure_adi: "İnşikak", text: "وَيَصْلٰى سَع۪يراًۜ", translation: "Fakat alevli ateşe girecek.", transliteration: "Ve yasla saira."},
  {id: 5897, sure_no: 13, ayet_no: 13, sure_adi: "İnşikak", text: "اِنَّهُ  كَانَ ف۪ٓي اَهْلِه۪ مَسْرُوراً", translation: "O, dünyada yakınları içindeyken sevinçliydi.", transliteration: "İnnehu kane fi ehlihi mesrura."},
  {id: 5898, sure_no: 14, ayet_no: 14, sure_adi: "İnşikak", text: "اِنَّهُ ظَنَّ اَنْ لَنْ يَحُورَۚۛ", translation: "O, kesinlikle dönmeyeceğini sanıyordu.", transliteration: "İnnehu zanne en len yahur."},
  {id: 5899, sure_no: 15, ayet_no: 15, sure_adi: "İnşikak", text: "بَلٰىۚۛ  اِنَّ رَبَّهُ  كَانَ بِه۪ بَص۪يراًۜ", translation: "Bilakis! Rabb'i onu çok iyi görüyordu.", transliteration: "Bela, inne rabbehu kane bihi basira."},
  {id: 5900, sure_no: 16, ayet_no: 16, sure_adi: "İnşikak", text: "فَلَٓا اُقْسِمُ بِالشَّفَقِۙ", translation: "Şafak vaktine yemin ederim;", transliteration: "Fe la uksimu biş şefak."},
  {id: 5901, sure_no: 17, ayet_no: 17, sure_adi: "İnşikak", text: "وَالَّيْلِ وَمَا وَسَقَۙ", translation: "Geceye ve barındırdıklarına,", transliteration: "Vel leyli ve ma vesak."},
  {id: 5902, sure_no: 18, ayet_no: 18, sure_adi: "İnşikak", text: "وَالْقَمَرِ اِذَا اتَّسَقَۙ", translation: "Dolunay halindeki Ay'a,", transliteration: "Vel kameri izet tesak."},
  {id: 5903, sure_no: 19, ayet_no: 19, sure_adi: "İnşikak", text: "لَتَرْكَبُنَّ طَبَقاً عَنْ طَبَقٍۜ", translation: "Kesinlikle tabakadan tabakaya bineceksiniz.", transliteration: "Le terkebunne tabakan an tabakın."},
  {id: 5904, sure_no: 20, ayet_no: 20, sure_adi: "İnşikak", text: "فَمَا لَهُمْ لَا يُؤْمِنُونَۙ", translation: "Onların nesi var ki iman etmiyorlar?", transliteration: "Fe ma lehum la yu'minun."},
  {id: 5905, sure_no: 21, ayet_no: 21, sure_adi: "İnşikak", text: "وَاِذَا قُرِئَ عَلَيْهِمُ الْقُرْاٰنُ لَا يَسْجُدُونَۜ", translation: "Kendilerine Kur'an okunduğu zaman secde etmiyorlar.", transliteration: "Ve iza kurıe aleyhimul kur'anu la yescudun."},
  {id: 5906, sure_no: 22, ayet_no: 22, sure_adi: "İnşikak", text: "بَلِ الَّذ۪ينَ كَفَرُوا يُكَذِّبُونَۘ", translation: "Aksine, Kafirler yalanlıyorlar.", transliteration: "Belillezine keferu yukezzibun."},
  {id: 5907, sure_no: 23, ayet_no: 23, sure_adi: "İnşikak", text: "وَاللّٰهُ اَعْلَمُ بِمَا يُوعُونَۘ", translation: "Allah, onların içlerinde ne sakladıklarını en iyi bilendir.", transliteration: "Vallahu a'lemu bima yuun."},
  {id: 5908, sure_no: 24, ayet_no: 24, sure_adi: "İnşikak", text: "فَبَشِّرْهُمْ بِعَذَابٍ اَل۪يمٍۙ", translation: "O halde onları elem verici azapla haberdar et!", transliteration: "Fe beşşirhum bi azabin elim."},
  {id: 5909, sure_no: 25, ayet_no: 25, sure_adi: "İnşikak", text: "اِلَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُونٍ", translation: "Ancak iman eden ve salihatı yapanlar hariç. Onlar için minnet altında bırakmayan bir ödül vardır.", transliteration: "İllellezine amenu ve amilus salihati lehum ecrun gayru memnun."},
  {id: 5910, sure_no: 1, ayet_no: 1, sure_adi: "Büruc", text: "وَالسَّمَٓاءِ ذَاتِ الْبُرُوجِۙ", translation: "Burçlar sahibi gökyüzüne ant olsun,", transliteration: "Ves semai zatil buruc."},
  {id: 5911, sure_no: 2, ayet_no: 2, sure_adi: "Büruc", text: "وَالْيَوْمِ الْمَوْعُودِۙ", translation: "Söz Verilen Gün'e ant olsun,", transliteration: "Vel yevmil mev'ud."},
  {id: 5912, sure_no: 3, ayet_no: 3, sure_adi: "Büruc", text: "وَشَاهِدٍ وَمَشْهُودٍۜ", translation: "Tanığa ve tanıklık edilene ant olsun,", transliteration: "Ve şahidin ve meşhud."},
  {id: 5913, sure_no: 4, ayet_no: 4, sure_adi: "Büruc", text: "قُتِلَ اَصْحَابُ الْاُخْدُودِۙ", translation: "Uhdud'un Ashab'ı kahroldu;", transliteration: "Kutile ashabul uhdud."},
  {id: 5914, sure_no: 5, ayet_no: 5, sure_adi: "Büruc", text: "اَلنَّارِ ذَاتِ الْوَقُودِۙ", translation: "O şiddetli ateşin sahipleri.", transliteration: "Ennari zatil vekud."},
  {id: 5915, sure_no: 6, ayet_no: 6, sure_adi: "Büruc", text: "اِذْ هُمْ عَلَيْهَا قُعُودٌۙ", translation: "Hani onlar, onun başında oturmuşlardı.", transliteration: "İzhum aleyha kuud."},
  {id: 5916, sure_no: 7, ayet_no: 7, sure_adi: "Büruc", text: "وَهُمْ عَلٰى مَا يَفْعَلُونَ بِالْمُؤْمِن۪ينَ شُهُودٌۜ", translation: "Mü'minlere yaptıklarını izliyorlardı.", transliteration: "Ve hum ala ma yef'alune bil mu'minine şuhud."},
  {id: 5917, sure_no: 8, ayet_no: 8, sure_adi: "Büruc", text: "وَمَا نَقَمُوا مِنْهُمْ  اِلَّٓا اَنْ يُؤْمِنُوا بِاللّٰهِ الْعَز۪يزِ الْحَم۪يدِۙ", translation: "Bunu, Mutlak Üstün Olan ve Övgüye Değer Yegane Varlık olan Allah'a iman edenleri cezalandırmak için yapıyorlardı.", transliteration: "Ve ma nekamu minhum illa en yu'minu billahil azizil hamid."},
  {id: 5918, sure_no: 9, ayet_no: 9, sure_adi: "Büruc", text: "اَلَّذ۪ي لَهُ مُلْكُ السَّمٰوَاتِ وَالْاَرْضِۜ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَه۪يدٌۜ", translation: "O Allah ki, göklerin ve yerin mülkü yalnızca O'na aittir. Allah, Her Şeye Tanık'tır.", transliteration: "Ellezi lehu mulkus semavati vel ard, vallahu ala kulli şey'in şehid."},
  {id: 5919, sure_no: 10, ayet_no: 10, sure_adi: "Büruc", text: "اِنَّ الَّذ۪ينَ فَتَنُوا الْمُؤْمِن۪ينَ وَالْمُؤْمِنَاتِ ثُمَّ لَمْ يَتُوبُوا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَر۪يقِۜ", translation: "Mü'min erkeklere ve kadınlara fitne yapıp, sonra da kesin olarak tevbe etmeyenler için Cehennem azabı vardır. Onlar için yakıcı bir azap vardır.", transliteration: "İnnellezine fetenul mu'minine vel mu'minati summe lem yetubu fe lehum azabu cehenneme ve lehum azabul harik."},
  {id: 5920, sure_no: 11, ayet_no: 11, sure_adi: "Büruc", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتٌ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُۜ ذٰلِكَ الْفَوْزُ الْـكَب۪يرُۜ", translation: "İman Eden ve salihatı yapanlar için içinden nehirler akan Cennetler vardır. İşte bu, büyük kurtuluştur.", transliteration: "İnnellezine amenu ve amilus salihati lehum cennatun tecri min tahtihel enhar, zalikel fevzul kebir."},
  {id: 5921, sure_no: 12, ayet_no: 12, sure_adi: "Büruc", text: "اِنَّ بَطْشَ رَبِّكَ لَشَد۪يدٌۜ", translation: "Rabb'inin yakalaması kesinlikle çok şiddetlidir.", transliteration: "İnne batşe rabbike le şedid."},
  {id: 5922, sure_no: 13, ayet_no: 13, sure_adi: "Büruc", text: "اِنَّهُ هُوَ يُبْدِئُ وَيُع۪يدُۚ", translation: "Kuşkusuz başlatan ve tekrarlayan O'dur.", transliteration: "İnnehu huve yubdiu ve yuid."},
  {id: 5923, sure_no: 14, ayet_no: 14, sure_adi: "Büruc", text: "وَهُوَ الْغَفُورُ الْوَدُودُۙ", translation: "O, Çok Bağışlayan'dır, Çok Seven'dir.", transliteration: "Ve huvel gafurul vedud."},
  {id: 5924, sure_no: 15, ayet_no: 15, sure_adi: "Büruc", text: "ذُوالْعَرْشِ الْمَج۪يدُۙ", translation: "Şanı Yüce Arş'ın Sahibi'dir.", transliteration: "Zul arşil mecid."},
  {id: 5925, sure_no: 16, ayet_no: 16, sure_adi: "Büruc", text: "فَعَّالٌ لِمَا يُر۪يدُۜ", translation: "Dilediğini yapandır.", transliteration: "Fa'alun lima yurid."},
  {id: 5926, sure_no: 17, ayet_no: 17, sure_adi: "Büruc", text: "هَلْ اَتٰيكَ حَد۪يثُ الْجُنُودِۙ", translation: "O orduların hadisi sana geldi mi?", transliteration: "Hel etake hadisul cunud."},
  {id: 5927, sure_no: 18, ayet_no: 18, sure_adi: "Büruc", text: "فِرْعَوْنَ وَثَمُودَۜ", translation: "Firavun ve Semud'un.", transliteration: "Fir'avne ve semud."},
  {id: 5928, sure_no: 19, ayet_no: 19, sure_adi: "Büruc", text: "بَلِ الَّذ۪ينَ كَفَرُوا ف۪ي تَكْذ۪يبٍۙ", translation: "Doğrusu Kafirler hala bir yalanlama içindedirler.", transliteration: "Belillezine keferu fi tekzib."},
  {id: 5929, sure_no: 20, ayet_no: 20, sure_adi: "Büruc", text: "وَاللّٰهُ مِنْ وَرَٓائِهِمْ مُح۪يطٌۚ", translation: "Allah, onları arkalarından kuşatmıştır.", transliteration: "Vallahu min veraihim muhit."},
  {id: 5930, sure_no: 21, ayet_no: 21, sure_adi: "Büruc", text: "بَلْ هُوَ قُرْاٰنٌ مَج۪يدٌۙ", translation: "Bilakis, o şanı yüce şerefli bir Kur'an'dır,", transliteration: "Bel huve kur'anun mecid."},
  {id: 5931, sure_no: 22, ayet_no: 22, sure_adi: "Büruc", text: "ف۪ي لَوْحٍ مَحْفُوظٍ", translation: "Levh-i Mahfuz'dadır.", transliteration: "Fi levhın mahfuz."},
  {id: 5932, sure_no: 1, ayet_no: 1, sure_adi: "Tarık", text: "وَالسَّمَٓاءِ وَالطَّارِقِۙ", translation: "Sema'ya ve Tarık'a ant olsun,", transliteration: "Ves semai vet tarık."},
  {id: 5933, sure_no: 2, ayet_no: 2, sure_adi: "Tarık", text: "وَمَٓا اَدْرٰيكَ مَا الطَّارِقُۙ", translation: "Tarık'ın ne olduğunu sen ne bileceksin?", transliteration: "Ve ma edrake met tarik."},
  {id: 5934, sure_no: 3, ayet_no: 3, sure_adi: "Tarık", text: "اَلنَّجْمُ الثَّاقِبُۙ", translation: "O, delici yıldızdır.", transliteration: "En necmus sakıb."},
  {id: 5935, sure_no: 4, ayet_no: 4, sure_adi: "Tarık", text: "اِنْ كُلُّ نَفْسٍ لَمَّا عَلَيْهَا حَافِظٌۜ", translation: "Hiçbir nefs yoktur ki üzerinde koruyucu bulunmasın.", transliteration: "İn kullu nefsin lemma aleyha hafız."},
  {id: 5936, sure_no: 5, ayet_no: 5, sure_adi: "Tarık", text: "فَلْيَنْظُرِ الْاِنْسَانُ مِمَّ خُلِقَۜ", translation: "İnsan neden yaratıldığına bir baksın.", transliteration: "Fel yenzuril insanu mimme hulık."},
  {id: 5937, sure_no: 6, ayet_no: 6, sure_adi: "Tarık", text: "خُلِقَ مِنْ مَٓاءٍ دَافِقٍۙ", translation: "Dökülen bir sıvıdan yaratıldı;", transliteration: "Hulika min main dafik."},
  {id: 5938, sure_no: 7, ayet_no: 7, sure_adi: "Tarık", text: "يَخْرُجُ مِنْ بَيْنِ الصُّلْبِ وَالتَّرَٓائِبِۜ", translation: "Omurga ile göğüs kemikleri arasından çıkan.", transliteration: "Yahrucu min beynis sulbi vet teraib."},
  {id: 5939, sure_no: 8, ayet_no: 8, sure_adi: "Tarık", text: "اِنَّهُ عَلٰى رَجْعِه۪ لَقَادِرٌۜ", translation: "Kuşkusuz O, onu yeniden diriltmeye elbette kadirdir.", transliteration: "İnnehu ala rec'ıhi le kadir."},
  {id: 5940, sure_no: 9, ayet_no: 9, sure_adi: "Tarık", text: "يَوْمَ تُبْلَى السَّرَٓائِرُۙ", translation: "Bütün gizliliklerin ortaya çıkarıldığı gün,", transliteration: "Yevme tubles serair."},
  {id: 5941, sure_no: 10, ayet_no: 10, sure_adi: "Tarık", text: "فَمَا لَهُ مِنْ قُوَّةٍ وَلَا نَاصِرٍۜ", translation: "Onun ne bir gücü ne de bir yardımcısı vardır.", transliteration: "Fema lehu min kuvvetin ve la nasır."},
  {id: 5942, sure_no: 11, ayet_no: 11, sure_adi: "Tarık", text: "وَالسَّمَٓاءِ ذَاتِ الرَّجْعِۙ", translation: "Dönüş sahibi Semaya ant olsun,", transliteration: "Ves semai zatir rec'."},
  {id: 5943, sure_no: 12, ayet_no: 12, sure_adi: "Tarık", text: "وَالْاَرْضِ ذَاتِ الصَّدْعِۙ", translation: "Yarılma sahibi yeryüzüne ant olsun", transliteration: "Vel ardı zatis sad'."},
  {id: 5944, sure_no: 13, ayet_no: 13, sure_adi: "Tarık", text: "اِنَّهُ لَقَوْلٌ فَصْلٌۙ", translation: "Kuşkusuz O, ayırt edici bir sözdür.", transliteration: "İnnehu le kavlun fasl."},
  {id: 5945, sure_no: 14, ayet_no: 14, sure_adi: "Tarık", text: "وَمَا هُوَ بِالْهَزْلِۜ", translation: "O, sıradan bir söz değildir.", transliteration: "Ve ma huve bil hezl."},
  {id: 5946, sure_no: 15, ayet_no: 15, sure_adi: "Tarık", text: "اِنَّهُمْ يَك۪يدُونَ كَيْداًۙ", translation: "Onlar, planlar yapıyorlar,", transliteration: "İnnehum yekidune keyda."},
  {id: 5947, sure_no: 16, ayet_no: 16, sure_adi: "Tarık", text: "وَاَ‌ك۪يدُ كَيْداًۚ", translation: "Ben de plan yapıyorum.", transliteration: "Ve ekidu keyda."},
  {id: 5948, sure_no: 17, ayet_no: 17, sure_adi: "Tarık", text: "فَمَهِّلِ الْـكَافِر۪ينَ اَمْهِلْهُمْ رُوَيْداً", translation: "Kafirlere biraz daha süre tanı, onları kendi hallerine bırak.", transliteration: "Fe mehhilil kafirine emhilhum ruveyda."},
  {id: 5949, sure_no: 1, ayet_no: 1, sure_adi: "Ala", text: "سَبِّحِ اسْمَ رَبِّكَ الْاَعْلٰىۙ", translation: "Rabb'inin yüce adını tesbih et.", transliteration: "Sebbihısme rabbikel a'la."},
  {id: 5950, sure_no: 2, ayet_no: 2, sure_adi: "Ala", text: "اَلَّذ۪ي خَلَقَ فَسَوّٰىۙۖ", translation: "O ki yarattı ve düzene koydu.", transliteration: "Ellezi halaka fesevva."},
  {id: 5951, sure_no: 3, ayet_no: 3, sure_adi: "Ala", text: "وَالَّذ۪ي قَدَّرَ فَهَدٰىۙۖ", translation: "O ki takdir etti ve yol gösterdi.", transliteration: "Vellezi kaddere fe heda."},
  {id: 5952, sure_no: 4, ayet_no: 4, sure_adi: "Ala", text: "وَالَّـذ۪ٓي اَخْرَجَ الْمَرْعٰىۙۖ", translation: "O ki yerden bitkiler, yeşillikler çıkardı.", transliteration: "Vellezi ahrecel mer'a."},
  {id: 5953, sure_no: 5, ayet_no: 5, sure_adi: "Ala", text: "فَجَعَلَهُ غُثَٓاءً اَحْوٰىۜ", translation: "Derken onu kuru, kararmış çer çöp haline getirdi.", transliteration: "Fe cealehu gusaen ahva."},
  {id: 5954, sure_no: 6, ayet_no: 6, sure_adi: "Ala", text: "سَنُقْرِئُكَ فَلَا تَنْسٰىۙ", translation: "Sana okutacağız; bir daha unutmayacaksın;", transliteration: "Senukriuke fe la tensa."},
  {id: 5955, sure_no: 7, ayet_no: 7, sure_adi: "Ala", text: "اِلَّا مَا شَٓاءَ اللّٰهُۜ  اِنَّهُ يَعْلَمُ الْجَهْرَ وَمَا يَخْفٰىۜ", translation: "Allah, aksini dilemedikçe. Kuşkusuz O, açık ve gizli olanı bilir.", transliteration: "İlla ma şaallah, innehu ya'lemul cehre ve ma yahfa."},
  {id: 5956, sure_no: 8, ayet_no: 8, sure_adi: "Ala", text: "وَنُيَسِّرُكَ لِلْيُسْرٰىۚ", translation: "Kolay gelmesi için, sana yeterince kolaylaştıracağız.", transliteration: "Ve nuyessiruke lil yusra."},
  {id: 5957, sure_no: 9, ayet_no: 9, sure_adi: "Ala", text: "فَذَكِّرْ اِنْ نَفَعَتِ الذِّكْرٰىۜ", translation: "O halde öğüt ver, öğüt yararlı olacaksa!", transliteration: "Fe zekkir in nefeatiz zikra."},
  {id: 5958, sure_no: 10, ayet_no: 10, sure_adi: "Ala", text: "سَيَذَّكَّرُ مَنْ يَخْشٰىۙ", translation: "Huşu duyan kimse öğüt alacaktır.", transliteration: "Seyezzekkeru men yahşa."},
  {id: 5959, sure_no: 11, ayet_no: 11, sure_adi: "Ala", text: "وَيَتَجَنَّبُهَا الْاَشْقٰىۙ", translation: "Şaki olan ondan kaçınır.", transliteration: "Ve yetecennebuhel eşka."},
  {id: 5960, sure_no: 12, ayet_no: 12, sure_adi: "Ala", text: "اَلَّذ۪ي يَصْلَى النَّارَ الْـكُبْرٰىۚ", translation: "Ki o, büyük ateşe atılacaktır.", transliteration: "Ellezi yaslen narel kubra."},
  {id: 5961, sure_no: 13, ayet_no: 13, sure_adi: "Ala", text: "ثُمَّ لَا يَمُوتُ ف۪يهَا وَلَا يَحْيٰىۜ", translation: "Sonra orada ölüm de yok, yaşam da.", transliteration: "Summe la yemutu fiha ve la yahya."},
  {id: 5962, sure_no: 14, ayet_no: 14, sure_adi: "Ala", text: "قَدْ اَفْلَحَ مَنْ تَزَكّٰىۙ", translation: "Doğrusu arınan kimse kurtuluşa ermiştir;", transliteration: "Kad efleha men tezekka."},
  {id: 5963, sure_no: 15, ayet_no: 15, sure_adi: "Ala", text: "وَذَكَرَ اسْمَ رَبِّه۪ فَصَلّٰىۜ", translation: "Rabb'inin adını anıp salla eden.", transliteration: "Ve zekeresme rabbihi fe salla."},
  {id: 5964, sure_no: 16, ayet_no: 16, sure_adi: "Ala", text: "بَلْ تُؤْثِرُونَ الْحَيٰوةَ الدُّنْيَاۘ", translation: "Ne var ki siz dünya hayatını tercih ediyorsunuz.", transliteration: "Bel tu'sırunel hayated dunya."},
  {id: 5965, sure_no: 17, ayet_no: 17, sure_adi: "Ala", text: "وَالْاٰخِرَةُ خَيْرٌ وَاَبْـقٰىۜ", translation: "Oysaki ahiret hayatı daha hayırlı ve daha kalıcıdır.", transliteration: "Vel ahıretu hayrun ve ebka."},
  {id: 5966, sure_no: 18, ayet_no: 18, sure_adi: "Ala", text: "اِنَّ هٰذَا لَفِي الصُّحُفِ الْاُو۫لٰىۙ", translation: "Bu, önceki sahifelerde de vardı.", transliteration: "İnne haza le fis suhufil ula."},
  {id: 5967, sure_no: 19, ayet_no: 19, sure_adi: "Ala", text: "صُحُفِ اِبْرٰه۪يمَ وَمُوسٰى", translation: "İbrahim'ın ve Musa'nın sahifelerinde.", transliteration: "Suhufi ibrahime ve musa."},
  {id: 5968, sure_no: 1, ayet_no: 1, sure_adi: "Gaşiye", text: "هَلْ اَتٰيكَ حَد۪يثُ الْغَاشِيَةِۜ", translation: "Ğaşiyenin hadisi sana geldi mi?", transliteration: "Hel etake hadisul gaşiyeh."},
  {id: 5969, sure_no: 2, ayet_no: 2, sure_adi: "Gaşiye", text: "وُجُوهٌ يَوْمَئِذٍ خَاشِعَةٌۙ", translation: "O Gün birtakım yüzler öne düşüktür;", transliteration: "Vucuhun yevmeizin haşiah."},
  {id: 5970, sure_no: 3, ayet_no: 3, sure_adi: "Gaşiye", text: "عَامِلَةٌ نَاصِبَةٌۙ", translation: "Bitmiş, tükenmiş olarak.", transliteration: "Amiletun nasıbeh."},
  {id: 5971, sure_no: 4, ayet_no: 4, sure_adi: "Gaşiye", text: "تَصْلٰى نَاراً حَامِيَةًۙ", translation: "Kızgın ateşe atılacaklar.", transliteration: "Tesla naren hamiyeh."},
  {id: 5972, sure_no: 5, ayet_no: 5, sure_adi: "Gaşiye", text: "تُسْقٰى مِنْ عَيْنٍ اٰنِيَةٍۜ", translation: "Kaynar bir kaynaktan içirilirler.", transliteration: "Tuska min aynin aniyeh."},
  {id: 5973, sure_no: 6, ayet_no: 6, sure_adi: "Gaşiye", text: "لَيْسَ لَهُمْ طَعَامٌ اِلَّا مِنْ ضَر۪يعٍۙ", translation: "Onlar için kuru dikenli bir bitkiden başka yiyecek yoktur;", transliteration: "Leyse lehum taamun illa min dari'."},
  {id: 5974, sure_no: 7, ayet_no: 7, sure_adi: "Gaşiye", text: "لَا يُسْمِنُ وَلَا يُغْن۪ي مِنْ جُوعٍۜ", translation: "O, beslemez ve açlığı gidermez.", transliteration: "La yusminu ve la yugni min cu'."},
  {id: 5975, sure_no: 8, ayet_no: 8, sure_adi: "Gaşiye", text: "وُجُوهٌ يَوْمَئِذٍ نَاعِمَةٌۙ", translation: "O Gün birtakım yüzler nimetler içindedirler.", transliteration: "Vucuhun yevmeizin naımeh."},
  {id: 5976, sure_no: 9, ayet_no: 9, sure_adi: "Gaşiye", text: "لِسَعْيِهَا رَاضِيَةٌۙ", translation: "Yaptıklarından dolayı hoşnutturlar.", transliteration: "Li sa'yiha radiyeh."},
  {id: 5977, sure_no: 10, ayet_no: 10, sure_adi: "Gaşiye", text: "ف۪ي جَنَّةٍ عَالِيَةٍۙ", translation: "Mükemmel bir bahçededirler.", transliteration: "Fi cennetin aliyeh."},
  {id: 5978, sure_no: 11, ayet_no: 11, sure_adi: "Gaşiye", text: "لَا تَسْمَعُ ف۪يهَا لَاغِيَةًۜ", translation: "Orada boş söz duymazlar.", transliteration: "La tesmeu fiha lagıyeh."},
  {id: 5979, sure_no: 12, ayet_no: 12, sure_adi: "Gaşiye", text: "ف۪يهَا عَيْنٌ جَارِيَةٌۢ", translation: "Orada devamlı akan bir pınar vardır.", transliteration: "Fiha aynun cariyeh."},
  {id: 5980, sure_no: 13, ayet_no: 13, sure_adi: "Gaşiye", text: "ف۪يهَا سُرُرٌ مَرْفُوعَةٌۙ", translation: "Yükseklere kurulmuş divanlar,", transliteration: "Fiha sururun merfuah."},
  {id: 5981, sure_no: 14, ayet_no: 14, sure_adi: "Gaşiye", text: "وَاَكْوَابٌ مَوْضُوعَةٌۙ", translation: "İçime hazır içecekler,", transliteration: "Ve ekvabun mevduah."},
  {id: 5982, sure_no: 15, ayet_no: 15, sure_adi: "Gaşiye", text: "وَنَمَارِقُ مَصْفُوفَةٌۙ", translation: "Sıra sıra yastıklar,", transliteration: "Ve nemarıku masfufeh."},
  {id: 5983, sure_no: 16, ayet_no: 16, sure_adi: "Gaşiye", text: "وَزَرَابِيُّ مَبْثُوثَةٌۜ", translation: "Serilmiş halılar vardır.", transliteration: "Ve zerabiyyu mebsuseh."},
  {id: 5984, sure_no: 17, ayet_no: 17, sure_adi: "Gaşiye", text: "اَفَلَا يَنْظُرُونَ اِلَى الْاِبِلِ كَيْفَ خُلِقَتْ۠", translation: "Hiç mi deveye bakmıyorlar, nasıl yaratılmış?", transliteration: "E fe la yanzurune ilel ibili keyfe hulikat."},
  {id: 5985, sure_no: 18, ayet_no: 18, sure_adi: "Gaşiye", text: "وَاِلَى السَّمَٓاءِ كَيْفَ رُفِعَتْ۠", translation: "Ve gökyüzüne, nasıl yükseltilmiş?", transliteration: "Ve iles semai keyfe rufiat."},
  {id: 5986, sure_no: 19, ayet_no: 19, sure_adi: "Gaşiye", text: "وَاِلَى الْجِبَالِ كَيْفَ نُصِبَتْ۠", translation: "Ve dağlara, nasıl dikilmişler?", transliteration: "Ve ilel cibali keyfe nusıbet."},
  {id: 5987, sure_no: 20, ayet_no: 20, sure_adi: "Gaşiye", text: "وَاِلَى الْاَرْضِ كَيْفَ سُطِحَتْ۠", translation: "Ve yeryüzüne, nasıl yayılıp döşenmiş?", transliteration: "Ve ilel ardı keyfe sutıhat."},
  {id: 5988, sure_no: 21, ayet_no: 21, sure_adi: "Gaşiye", text: "فَذَكِّرْ اِنَّـمَٓا اَنْتَ مُذَكِّرٌۜ", translation: "Öğüt ver; sen yalnızca öğüt vericisin.", transliteration: "Fezekkir innema ente muzekkir."},
  {id: 5989, sure_no: 22, ayet_no: 22, sure_adi: "Gaşiye", text: "لَسْتَ عَلَيْهِمْ بِمُصَيْطِرٍۙ", translation: "Kimseyi zorla inandıracak değilsin.", transliteration: "Leste aleyhim bi musaytır."},
  {id: 5990, sure_no: 23, ayet_no: 23, sure_adi: "Gaşiye", text: "اِلَّا مَنْ تَوَلّٰى وَكَفَرَۙ", translation: "Ancak kim yüz çevirir ve kafir olursa,", transliteration: "İlla men tevella ve kefer."},
  {id: 5991, sure_no: 24, ayet_no: 24, sure_adi: "Gaşiye", text: "فَيُعَذِّبُهُ اللّٰهُ الْعَذَابَ الْاَكْبَرَۜ", translation: "Allah, ona en büyük azapla azap eder.", transliteration: "Fe yuazzibuhullahul azabel ekber."},
  {id: 5992, sure_no: 25, ayet_no: 25, sure_adi: "Gaşiye", text: "اِنَّ اِلَيْنَٓا اِيَابَهُمْۙ", translation: "Kuşkusuz onların dönüşü Bizedir.", transliteration: "İnne ileyna iyabehum."},
  {id: 5993, sure_no: 26, ayet_no: 26, sure_adi: "Gaşiye", text: "ثُمَّ اِنَّ عَلَيْنَا حِسَابَهُمْ", translation: "Sonra, kuşkusuz onların hesapları kesinlikle Bize aittir.", transliteration: "Summe inne aleyna hisabehum."},
  {id: 5994, sure_no: 1, ayet_no: 1, sure_adi: "Fecr", text: "وَالْفَجْرِۙ", translation: "Fecre ant olsun.", transliteration: "Vel fecr."},
  {id: 5995, sure_no: 2, ayet_no: 2, sure_adi: "Fecr", text: "وَلَيَالٍ عَشْرٍۙ", translation: "On geceye,", transliteration: "Ve leyalin aşr."},
  {id: 5996, sure_no: 3, ayet_no: 3, sure_adi: "Fecr", text: "وَالشَّفْعِ وَالْوَتْرِۙ", translation: "Çift ve tek olana,", transliteration: "Veş şef'ı vel vetr."},
  {id: 5997, sure_no: 4, ayet_no: 4, sure_adi: "Fecr", text: "وَالَّيْلِ اِذَا يَسْرِۚ", translation: "Geçip giden geceye.", transliteration: "Vel leyli iza yesr."},
  {id: 5998, sure_no: 5, ayet_no: 5, sure_adi: "Fecr", text: "هَلْ ف۪ي ذٰلِكَ قَسَمٌ لِذ۪ي حِجْرٍۜ", translation: "İşte bunlarda aklını kullanan kimse için bir kasem yok mu?", transliteration: "Hel fi zalike kasemun lizi hicr."},
  {id: 5999, sure_no: 6, ayet_no: 6, sure_adi: "Fecr", text: "اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍۙۖ", translation: "Rabb'inin Ad kavmine ne yaptığını biliyor musun?", transliteration: "E lem tere keyfe feale rabbuke bi ad."},
  {id: 6000, sure_no: 7, ayet_no: 7, sure_adi: "Fecr", text: "اِرَمَ ذَاتِ الْعِمَادِۙۖ", translation: "Sütunlara sahip İrem'e!", transliteration: "İreme zatil ımad."},
  {id: 6001, sure_no: 8, ayet_no: 8, sure_adi: "Fecr", text: "اَلَّت۪ي لَمْ يُخْلَقْ مِثْلُهَا فِي الْبِلَادِۙۖ", translation: "Ki, beldeler içinde onun bir eşi yaratılmadı;", transliteration: "Elleti lem yuhlak misluha fil bilad."},
  {id: 6002, sure_no: 9, ayet_no: 9, sure_adi: "Fecr", text: "وَثَمُودَ الَّذ۪ينَ جَابُوا الصَّخْرَ بِالْوَادِۙۖ", translation: "Ve vadide kayaları oyan Semud'a,", transliteration: "Ve semudelleziyne cabussahre bil vad."},
  {id: 6003, sure_no: 10, ayet_no: 10, sure_adi: "Fecr", text: "وَفِرْعَوْنَ ذِي الْاَوْتَادِۙۖ", translation: "Ve kazıklar sahibi Firavun'a;", transliteration: "Ve fir avne zil evtad."},
  {id: 6004, sure_no: 11, ayet_no: 11, sure_adi: "Fecr", text: "اَلَّذ۪ينَ طَغَوْا فِي الْبِلَادِۙۖ", translation: "Onlar ki, ülkelerde tağutlaşmışlardı,", transliteration: "Ellezine tagav fil bilad."},
  {id: 6005, sure_no: 12, ayet_no: 12, sure_adi: "Fecr", text: "فَاَكْثَرُوا ف۪يهَا الْفَسَادَۙۖ", translation: "Böylece oralarda bozgunculuğu çoğaltmışlardı.", transliteration: "Fe ekseru fihel fesad."},
  {id: 6006, sure_no: 13, ayet_no: 13, sure_adi: "Fecr", text: "فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍۙۖ", translation: "Bu yüzden Rabb'in onları azap kırbacıyla kırbaçladı.", transliteration: "Fe sabbe aleyhim rabbuke sevta azab."},
  {id: 6007, sure_no: 14, ayet_no: 14, sure_adi: "Fecr", text: "اِنَّ رَبَّكَ لَبِالْمِرْصَادِۜ", translation: "Çünkü Rabb'in olup -biteni görüp- gözlemektedir.", transliteration: "İnne rabbeke le bil mirsad."},
  {id: 6008, sure_no: 15, ayet_no: 15, sure_adi: "Fecr", text: "فَاَمَّا الْاِنْسَانُ اِذَا مَا ابْتَلٰيهُ رَبُّهُ فَاَكْرَمَهُ وَنَعَّمَهُ فَيَقُولُ رَبّ۪ٓي اَكْرَمَنِۜ", translation: "Fakat o insan; Rabb'i ne zaman onu sınamak için ikramda bulunsa ve ona nimet verse, \"Rabb'im bana ikram etti.\" der.", transliteration: "Fe emmel insanu iza mebtelahu rabbuhu fe ekremehu ve na'amehu fe yekulu rabbi ekremen."},
  {id: 6009, sure_no: 16, ayet_no: 16, sure_adi: "Fecr", text: "وَاَمَّٓا اِذَا مَا ابْتَلٰيهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ فَيَقُولُ رَبّ۪ٓي اَهَانَنِۚ", translation: "Fakat ne zaman ona sınamak için rızkını ölçülü verirse, \"Rabb'im beni alçaltı.\" der.", transliteration: "Ve emma iza mebtelahu fe kadere aleyhi rızkahu fe yekulu rabbi ehanen."},
  {id: 6010, sure_no: 17, ayet_no: 17, sure_adi: "Fecr", text: "كَلَّا بَلْ لَا تُكْرِمُونَ الْيَت۪يمَۙ", translation: "Hayır! Doğrusu siz yetime ikram etmiyorsunuz.", transliteration: "Kella bel la tukrimunel yetim."},
  {id: 6011, sure_no: 18, ayet_no: 18, sure_adi: "Fecr", text: "وَلَا تَحَٓاضُّونَ عَلٰى طَعَامِ الْمِسْك۪ينِۙ", translation: "Yoksulu yedirmede birbirinizi teşvik etmiyorsunuz.", transliteration: "Ve la tehaddune ala taamil miskin."},
  {id: 6012, sure_no: 19, ayet_no: 19, sure_adi: "Fecr", text: "وَتَأْكُلُونَ التُّرَاثَ اَكْلاً لَماًّۙ", translation: "Varis olduğunuz mirası ihtirasla yiyorsunuz.", transliteration: "Ve te'kulunet turase eklen lemma."},
  {id: 6013, sure_no: 20, ayet_no: 20, sure_adi: "Fecr", text: "وَتُحِبُّونَ الْمَالَ حُباًّ جَماًّۜ", translation: "Malı büyük bir sevgiyle, çok seviyorsunuz.", transliteration: "Ve tuhıbbunel male hubben cemma."},
  {id: 6014, sure_no: 21, ayet_no: 21, sure_adi: "Fecr", text: "كَلَّٓا اِذَا دُكَّتِ الْاَرْضُ دَكاًّ دَكاًّۙ", translation: "Hayır, hayır! Yer, paramparça olup dağıldığı zaman,", transliteration: "Kella iza dukketil ardu dekken dekka."},
  {id: 6015, sure_no: 22, ayet_no: 22, sure_adi: "Fecr", text: "وَجَٓاءَ رَبُّكَ وَالْمَلَكُ صَفاًّ صَفاًّۚ", translation: "Rabb'in emri geldiği ve melekler saf saf dizildiği zaman,", transliteration: "Ve cae rabbuke vel meleku saffen saffa."},
  {id: 6016, sure_no: 23, ayet_no: 23, sure_adi: "Fecr", text: "وَج۪ٓيءَ يَوْمَئِذٍ بِجَهَنَّمَ يَوْمَئِذٍ يَتَذَكَّرُ الْاِنْسَانُ وَاَنّٰى لَهُ الذِّكْرٰىۜ", translation: "O Gün Cehennem ortaya getirilir. O Gün insan neyin ne olduğunu anlar, ancak bunun ona bir yararı olmaz.", transliteration: "Ve cie yevmeizin bi cehenneme yevmeizin yetezekkerul insanu ve enna lehuz zikra."},
  {id: 6017, sure_no: 24, ayet_no: 24, sure_adi: "Fecr", text: "يَقُولُ يَا لَيْتَن۪ي قَدَّمْتُ لِحَيَات۪يۚ", translation: "\"Ah keşke hayattayken, ahiret hayatım için hazırlık yapmış olsaydım.\" der.", transliteration: "Yekulu ya leyteni kaddemtu li hayati."},
  {id: 6018, sure_no: 25, ayet_no: 25, sure_adi: "Fecr", text: "فَيَوْمَئِذٍ لَا يُعَذِّبُ عَذَابَهُٓ  اَحَدٌۙ", translation: "Artık O Gün, O'nun azbı hiç kimsenin azabına benzemez.", transliteration: "Fe yevmeizin la yuazzibu azabehu ehad."},
  {id: 6019, sure_no: 26, ayet_no: 26, sure_adi: "Fecr", text: "وَلَا يُوثِقُ وَثَاقَهُٓ اَحَدٌۜ", translation: "O'nun vurduğu bağ, hiç kimsenin vurduğu bağa benzemez.", transliteration: "Ve la yusiku ve sakahu ehad."},
  {id: 6020, sure_no: 27, ayet_no: 27, sure_adi: "Fecr", text: "يَٓا اَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُۗ", translation: "\"Ey mutmain nefs!", transliteration: "Ya eyyetuhen nefsul mutmainneh."},
  {id: 6021, sure_no: 28, ayet_no: 28, sure_adi: "Fecr", text: "اِرْجِع۪ٓي اِلٰى رَبِّكِ رَاضِيَةً مَرْضِيَّةًۚ", translation: "Rızasını kazanarak, razı olarak Rabb'ine dön.", transliteration: "İrcii ila rabbiki radıyeten mardıyyeh."},
  {id: 6022, sure_no: 29, ayet_no: 29, sure_adi: "Fecr", text: "فَادْخُل۪ي ف۪ي عِبَاد۪يۙ", translation: "Kullarıma katıl;", transliteration: "Fedhuli fi ibadi."},
  {id: 6023, sure_no: 30, ayet_no: 30, sure_adi: "Fecr", text: "وَادْخُل۪ي جَنَّت۪ي", translation: "Cennetime gir.\"", transliteration: "Vedhuli cenneti."},
  {id: 6024, sure_no: 1, ayet_no: 1, sure_adi: "Beled", text: "لَٓا اُقْسِمُ بِهٰذَا الْبَلَدِۙ", translation: "Hayır! Bu beldeye yemin ederim!", transliteration: "La uksimu bi hazel beled."},
  {id: 6025, sure_no: 2, ayet_no: 2, sure_adi: "Beled", text: "وَاَنْتَ حِلٌّ بِهٰذَا الْبَلَدِۙ", translation: "Senin de içinde yaşadığın beldeye,", transliteration: "Ve ente hıllun bi hazel beled."},
  {id: 6026, sure_no: 3, ayet_no: 3, sure_adi: "Beled", text: "وَوَالِدٍ وَمَا وَلَدَۙ", translation: "Ant olsun doğurana ve doğurduğuna!", transliteration: "Ve validin ve ma veled."},
  {id: 6027, sure_no: 4, ayet_no: 4, sure_adi: "Beled", text: "لَقَدْ خَلَقْنَا الْاِنْسَانَ ف۪ي كَبَدٍۜ", translation: "Biz insanı gerçekten zorluk arasında yarattık.", transliteration: "Lekad halaknel insane fi kebed."},
  {id: 6028, sure_no: 5, ayet_no: 5, sure_adi: "Beled", text: "اَيَحْسَبُ اَنْ لَنْ يَقْدِرَ عَلَيْهِ اَحَدٌۢ", translation: "Kendisine hiç kimsenin güç yetiremeyeceğini mi sanıyor?", transliteration: "E yahsebu en len yakdira aleyhi ehad."},
  {id: 6029, sure_no: 6, ayet_no: 6, sure_adi: "Beled", text: "يَقُولُ اَهْلَكْتُ مَالاً لُبَداًۜ", translation: "\"Yığınlarca mal harcadım.\" diyor.", transliteration: "Yekulu ehlektu malen lubeda."},
  {id: 6030, sure_no: 7, ayet_no: 7, sure_adi: "Beled", text: "اَيَحْسَبُ اَنْ لَمْ يَرَهُٓ  اَحَدٌۜ", translation: "Kendisini hiç kimsenin görmediğini mi sanıyor?", transliteration: "E yahsebu en lem yerahu ehad."},
  {id: 6031, sure_no: 8, ayet_no: 8, sure_adi: "Beled", text: "اَلَمْ نَجْعَلْ لَهُ عَيْنَيْنِۙ", translation: "Ona iki gözü Biz vermedik mi?", transliteration: "E lem nec'al lehu ayneyn."},
  {id: 6032, sure_no: 9, ayet_no: 9, sure_adi: "Beled", text: "وَلِسَاناً وَشَفَتَيْنِۙ", translation: "Bir dil ve iki dudak.", transliteration: "Ve lisanen ve şefeteyn."},
  {id: 6033, sure_no: 10, ayet_no: 10, sure_adi: "Beled", text: "وَهَدَيْنَاهُ النَّجْدَيْنِۚ", translation: "Ona iki yol gösterdik.", transliteration: "Ve hedeynahun necdeyn."},
  {id: 6034, sure_no: 11, ayet_no: 11, sure_adi: "Beled", text: "فَلَا اقْتَحَمَ الْعَقَبَةَۘ", translation: "Ne var ki o, sarp yokuşu aşmayı göze alamadı.", transliteration: "Fe laktehamel akabete."},
  {id: 6035, sure_no: 12, ayet_no: 12, sure_adi: "Beled", text: "وَمَٓا اَدْرٰيكَ مَا الْعَقَبَةُۜ", translation: "Bilir misin nedir sarp yokuş?", transliteration: "Ve ma edrake mel akabeh."},
  {id: 6036, sure_no: 13, ayet_no: 13, sure_adi: "Beled", text: "فَكُّ رَقَبَةٍۙ", translation: "Boynu çözmektir,", transliteration: "Fekku rekabetin."},
  {id: 6037, sure_no: 14, ayet_no: 14, sure_adi: "Beled", text: "اَوْ اِطْعَامٌ ف۪ي يَوْمٍ ذ۪ي مَسْغَبَةٍۙ", translation: "Veya darlık ve yoklukta doyurmaktır;", transliteration: "Ev ıt'amun fi yevmin zi mesgabeh."},
  {id: 6038, sure_no: 15, ayet_no: 15, sure_adi: "Beled", text: "يَت۪يماً ذَا مَقْرَبَةٍۙ", translation: "Yakınlık sahibi olan yetimi,", transliteration: "Yetimen za makrabeh."},
  {id: 6039, sure_no: 16, ayet_no: 16, sure_adi: "Beled", text: "اَوْ مِسْك۪يناً ذَا مَتْرَبَةٍۜ", translation: "Veya hiçbir şeyi olmayan miskini,", transliteration: "Ev miskinen za metrabeh."},
  {id: 6040, sure_no: 17, ayet_no: 17, sure_adi: "Beled", text: "ثُمَّ كَانَ مِنَ الَّذ۪ينَ اٰمَنُوا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِۜ", translation: "Sonra birbirlerine sabrı ve merhameti tavsiye eden iman edenlerden olmaktır.", transliteration: "Summe kane minellezine amenu ve tevasav bis sabri ve tevasav bil merhame."},
  {id: 6041, sure_no: 18, ayet_no: 18, sure_adi: "Beled", text: "اُو۬لٰٓئِكَ اَصْحَابُ الْمَيْمَنَةِۜ", translation: "İşte onlar, Ashab-ı Meymene'dir.", transliteration: "Ulaike ashabul meymeneh."},
  {id: 6042, sure_no: 19, ayet_no: 19, sure_adi: "Beled", text: "وَالَّذ۪ينَ كَفَرُوا بِاٰيَاتِنَا هُمْ اَصْحَابُ الْمَشْـَٔمَةِۜ", translation: "Ayetlerimizi küfredenler Ashab-ı Meşeme'dir.", transliteration: "Vellezine keferu bi ayatina hum ashabul meş'emeh."},
  {id: 6043, sure_no: 20, ayet_no: 20, sure_adi: "Beled", text: "عَلَيْهِمْ نَارٌ مُؤْصَدَةٌ", translation: "Onlara, üzerlerine kapıları kapatılmış bir ateş vardır.", transliteration: "Aleyhim narun mu'sadeh."},
  {id: 6044, sure_no: 1, ayet_no: 1, sure_adi: "Şems", text: "وَالشَّمْسِ وَضُحٰيهَاۙۖ", translation: "Güneş'e ve onun aydınlığına ant olsun,", transliteration: "Veş şemsi ve duhaha."},
  {id: 6045, sure_no: 2, ayet_no: 2, sure_adi: "Şems", text: "وَالْقَمَرِ اِذَا تَلٰيهَاۙۖ", translation: "Ve onu izleyen Ay'a,", transliteration: "Vel kameri iza telaha."},
  {id: 6046, sure_no: 3, ayet_no: 3, sure_adi: "Şems", text: "وَالنَّهَارِ اِذَا جَلّٰيهَاۙۖ", translation: "Ve onu ortaya çıkaran gündüze,", transliteration: "Ven nehari iza cellaha."},
  {id: 6047, sure_no: 4, ayet_no: 4, sure_adi: "Şems", text: "وَالَّيْلِ اِذَا يَغْشٰيهَاۙۖ", translation: "Ve onu örten geceye,", transliteration: "Vel leyli iza yagşaha."},
  {id: 6048, sure_no: 5, ayet_no: 5, sure_adi: "Şems", text: "وَالسَّمَٓاءِ وَمَا بَنٰيهَاۙۖ", translation: "Ve göğe ve onu düzenleyene,", transliteration: "Ves semai ve ma benaha."},
  {id: 6049, sure_no: 6, ayet_no: 6, sure_adi: "Şems", text: "وَالْاَرْضِ وَمَا طَحٰيهَاۙۖ", translation: "Ve yere ve onu döşeyene,", transliteration: "Vel ardı ve ma tahaha."},
  {id: 6050, sure_no: 7, ayet_no: 7, sure_adi: "Şems", text: "وَنَفْسٍ وَمَا سَوّٰيهَاۙۖ", translation: "Ve nefse ve onu biçimlendirene,", transliteration: "Ve nefsin ve ma sevvaha."},
  {id: 6051, sure_no: 8, ayet_no: 8, sure_adi: "Şems", text: "فَاَلْهَمَهَا فُجُورَهَا وَتَقْوٰيهَاۙۖ", translation: "Sonra ona fucurunu ve takvasını ilham etti.", transliteration: "Fe elhemeha fucureha ve takvaha."},
  {id: 6052, sure_no: 9, ayet_no: 9, sure_adi: "Şems", text: "قَدْ اَفْلَحَ مَنْ زَكّٰيهَاۙۖ", translation: "Elbette zekka eden kurtuluşa ermiştir.", transliteration: "Kad efleha men zekkaha."},
  {id: 6053, sure_no: 10, ayet_no: 10, sure_adi: "Şems", text: "وَقَدْ خَابَ مَنْ دَسّٰيهَاۜ", translation: "Elbette onu kirleten de hüsrana uğramıştır.", transliteration: "Ve kad habe men dessaha."},
  {id: 6054, sure_no: 11, ayet_no: 11, sure_adi: "Şems", text: "كَذَّبَتْ ثَمُودُ بِطَغْوٰيهَاۙۖ", translation: "Semud halkı tağutluğu nedeniyle yalanladı.", transliteration: "Kezzebet semudu bi tagvaha."},
  {id: 6055, sure_no: 12, ayet_no: 12, sure_adi: "Şems", text: "اِذِ انْبَعَثَ اَشْقٰيهَاۙۖ", translation: "Onların en azgını ayaklandığı zaman,", transliteration: "İzin baase eşkaha."},
  {id: 6056, sure_no: 13, ayet_no: 13, sure_adi: "Şems", text: "فَقَالَ لَهُمْ رَسُولُ اللّٰهِ نَاقَةَ اللّٰهِ وَسُقْيٰيهَا۠", translation: "Allah'ın Resul'ü Salih onlara, \"O, Allah'ın dişi devesidir, onun su içme hakkına dokunmayın.\" dedi.", transliteration: "Fe kale lehum resulullahi nakatallahi ve sukyaha."},
  {id: 6057, sure_no: 14, ayet_no: 14, sure_adi: "Şems", text: "فَكَذَّبُوهُ فَعَقَرُوهَاۙۖ فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْبِهِمْ فَسَوّٰيهَاۙۖ", translation: "Fakat onu yalanladılar. Sonra onu kestiler. Rabb'leri suçları nedeniyle üzerlerini azapla kapladı. Orayı yerle bir etti.", transliteration: "Fe kezzebuhu fe akaruha fe demdeme aleyhim rabbuhum bi zenbihim fe sevvaha."},
  {id: 6058, sure_no: 15, ayet_no: 15, sure_adi: "Şems", text: "وَلَا يَخَافُ عُقْبٰيهَا", translation: "O cezalandırmasının sonucundan çekinmez.", transliteration: "Ve la yehafu ukbaha."},
  {id: 6059, sure_no: 1, ayet_no: 1, sure_adi: "Leyl", text: "وَالَّيْلِ اِذَا يَغْشٰىۙ", translation: "Örttüğü zaman geceye ant olsun.", transliteration: "Vel leyli iza yagşa."},
  {id: 6060, sure_no: 2, ayet_no: 2, sure_adi: "Leyl", text: "وَالنَّهَارِ اِذَا تَجَلّٰىۙ", translation: "Ortaya çıktığı zaman gündüze,", transliteration: "Ven nehari iza tecella."},
  {id: 6061, sure_no: 3, ayet_no: 3, sure_adi: "Leyl", text: "وَمَا خَلَقَ الذَّكَرَ وَالْاُنْثٰىۙ", translation: "Erkeği ve dişiyi yaratana,", transliteration: "Ve ma halakaz zekera vel unsa."},
  {id: 6062, sure_no: 4, ayet_no: 4, sure_adi: "Leyl", text: "اِنَّ سَعْيَكُمْ لَشَتّٰىۜ", translation: "Çabalarınız çeşit çeşittir.", transliteration: "İnne sa'yekum le şetta."},
  {id: 6063, sure_no: 5, ayet_no: 5, sure_adi: "Leyl", text: "فَاَمَّا مَنْ اَعْطٰى وَاتَّقٰىۙ", translation: "Fakat kim verir ve takva sahibi olursa,", transliteration: "Fe emma men a'ta vetteka."},
  {id: 6064, sure_no: 6, ayet_no: 6, sure_adi: "Leyl", text: "وَصَدَّقَ بِالْحُسْنٰىۙ", translation: "En iyiyi doğrularsa,", transliteration: "Ve saddeka bil husna."},
  {id: 6065, sure_no: 7, ayet_no: 7, sure_adi: "Leyl", text: "فَسَنُيَسِّرُهُ لِلْيُسْرٰىۜ", translation: "Ona en kolayı kolaylaştıracağız.", transliteration: "Fe senuyessiruhu lil yusra."},
  {id: 6066, sure_no: 8, ayet_no: 8, sure_adi: "Leyl", text: "وَاَمَّا مَنْ بَخِلَ وَاسْتَغْنٰىۙ", translation: "Fakat kim cimrilik eder de kendisini müstağni görürse,", transliteration: "Ve emma men bahıle vestagna."},
  {id: 6067, sure_no: 9, ayet_no: 9, sure_adi: "Leyl", text: "وَكَذَّبَ بِالْحُسْنٰىۙ", translation: "En iyiyi yalanlarsa,", transliteration: "Ve kezzebe bil husna."},
  {id: 6068, sure_no: 10, ayet_no: 10, sure_adi: "Leyl", text: "فَسَنُيَسِّرُهُ لِلْعُسْرٰىۜ", translation: "Ona en zoru kolaylaştıracağız.", transliteration: "Fe senuyessiruhu lil usra."},
  {id: 6069, sure_no: 11, ayet_no: 11, sure_adi: "Leyl", text: "وَمَا يُغْن۪ي عَنْهُ مَالُـهُٓ اِذَا تَرَدّٰىۜ", translation: "Düştüğü zaman malı ona yarar sağlamaz.", transliteration: "Ve ma yugni anhu maluhu iza teredda."},
  {id: 6070, sure_no: 12, ayet_no: 12, sure_adi: "Leyl", text: "اِنَّ عَلَيْنَا لَلْهُدٰىۘ", translation: "Kuşkusuz doğru yolu göstermek Biz'e aittir.", transliteration: "İnne aleyna lel huda."},
  {id: 6071, sure_no: 13, ayet_no: 13, sure_adi: "Leyl", text: "وَاِنَّ لَنَا لَلْاٰخِرَةَ وَالْاُو۫لٰى", translation: "Kuşkusuz ilk de son da Bizimdir.", transliteration: "Ve inne lena lel ahırete vel ula."},
  {id: 6072, sure_no: 14, ayet_no: 14, sure_adi: "Leyl", text: "فَاَنْذَرْتُكُمْ نَاراً تَلَظّٰىۚ", translation: "Alev saçan bir ateşe karşı sizi uyardım.", transliteration: "Fe enzertukum naren telezza."},
  {id: 6073, sure_no: 15, ayet_no: 15, sure_adi: "Leyl", text: "لَا يَصْلٰيهَٓا اِلَّا الْاَشْقٰىۙ", translation: "Ona şaki olandan başkası girmez.", transliteration: "La yaslaha illel eşka."},
  {id: 6074, sure_no: 16, ayet_no: 16, sure_adi: "Leyl", text: "اَلَّذ۪ي كَذَّبَ وَتَوَلّٰىۜ", translation: "O ki yalanladı ve yüz çevirdi.", transliteration: "Ellezi kezzebe ve tevella."},
  {id: 6075, sure_no: 17, ayet_no: 17, sure_adi: "Leyl", text: "وَسَيُجَنَّبُهَا الْاَتْقٰىۙ", translation: "Takva sahibi olan, ondan uzak tutulacak.", transliteration: "Ve seyucennebuhel etka."},
  {id: 6076, sure_no: 18, ayet_no: 18, sure_adi: "Leyl", text: "اَلَّذ۪ي يُؤْت۪ي مَالَهُ يَتَزَكّٰىۚ", translation: "O ki malını vererek arınır.", transliteration: "Ellezi yu'ti ma lehu yetezekka."},
  {id: 6077, sure_no: 19, ayet_no: 19, sure_adi: "Leyl", text: "وَمَا لِاَحَدٍ عِنْدَهُ مِنْ نِعْمَةٍ تُجْزٰىۙ", translation: "Bunu kimseden karşılık beklemeden yapar.", transliteration: "Ve ma li ehadin indehu min ni'metin tucza."},
  {id: 6078, sure_no: 20, ayet_no: 20, sure_adi: "Leyl", text: "اِلَّا ابْتِغَٓاءَ وَجْهِ رَبِّهِ الْاَعْلٰىۚ", translation: "İsteği yalnızca Yüce Rabb'inin rızasını kazanmaktır.", transliteration: "İllebtigae vechi rabbihil a'la."},
  {id: 6079, sure_no: 21, ayet_no: 21, sure_adi: "Leyl", text: "وَلَسَوْفَ يَرْضٰى", translation: "Yakında mutlaka hoşnut olacaktır.", transliteration: "Ve le sevfe yerda."},
  {id: 6080, sure_no: 1, ayet_no: 1, sure_adi: "Duha", text: "وَالضُّحٰىۙ", translation: "Duha vaktine ant olsun.", transliteration: "Ved duha."},
  {id: 6081, sure_no: 2, ayet_no: 2, sure_adi: "Duha", text: "وَالَّيْلِ اِذَا سَجٰىۙ", translation: "Gecenin karanlıktaki dinginliğine,", transliteration: "Vel leyli iza seca."},
  {id: 6082, sure_no: 3, ayet_no: 3, sure_adi: "Duha", text: "مَا وَدَّعَكَ رَبُّكَ وَمَا قَلٰىۜ", translation: "Rabb'in seni bırakmadı ve sana darılmadı.", transliteration: "Ma veddeake rabbuke ve ma kala."},
  {id: 6083, sure_no: 4, ayet_no: 4, sure_adi: "Duha", text: "وَلَلْاٰخِرَةُ خَيْرٌ لَكَ مِنَ الْاُو۫لٰىۜ", translation: "Senin için gelecek, geçmişten hayırlı olacak.", transliteration: "Ve lel ahıretu hayrun leke minel ula."},
  {id: 6084, sure_no: 5, ayet_no: 5, sure_adi: "Duha", text: "وَلَسَوْفَ يُعْط۪يكَ رَبُّكَ فَتَـرْضٰىۜ", translation: "Yakında Rabb'in sana verecek, sen de hoşnut olacaksın.", transliteration: "Ve le sevfe yu'tike rabbuke fe terda."},
  {id: 6085, sure_no: 6, ayet_no: 6, sure_adi: "Duha", text: "اَلَمْ يَجِدْكَ يَت۪ـيـماً فَاٰوٰىۖ", translation: "Seni yetim bulup, barındırmadı mı?", transliteration: "E lem yecidke yetimen fe ava."},
  {id: 6086, sure_no: 7, ayet_no: 7, sure_adi: "Duha", text: "وَوَجَدَكَ ضَٓالاًّ فَهَدٰىۖ", translation: "Seni şaşırmış bulup, doğru yolu göstermedi mi?", transliteration: "Ve vecedeke dallen fe heda."},
  {id: 6087, sure_no: 8, ayet_no: 8, sure_adi: "Duha", text: "وَوَجَدَكَ عَٓائِلاً فَاَغْنٰىۜ", translation: "Seni muhtaç durumda bulup ğına etmedi mi?", transliteration: "Ve vecedeke ailen fe agna."},
  {id: 6088, sure_no: 9, ayet_no: 9, sure_adi: "Duha", text: "فَاَمَّا الْيَت۪يمَ فَلَا تَقْهَرْۜ", translation: "Öyleyse sakın yetimi hor görme.", transliteration: "Fe emmel yetime fe la takher."},
  {id: 6089, sure_no: 10, ayet_no: 10, sure_adi: "Duha", text: "وَاَمَّا السَّٓائِلَ فَلَا تَنْهَرْۜ", translation: "Sakın isteyeni geri çevirme.", transliteration: "Ve emmes saile fe la tenher."},
  {id: 6090, sure_no: 11, ayet_no: 11, sure_adi: "Duha", text: "وَاَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ", translation: "Ve Rabb'inin nimetini her fırsatta hadis et.", transliteration: "Ve emma bi ni'meti rabbike fe haddis."},
  {id: 6091, sure_no: 1, ayet_no: 1, sure_adi: "İnşirah", text: "اَلَمْ نَشْرَحْ لَكَ صَدْرَكَۙ", translation: "Senin için göğsünü açmadık mı?", transliteration: "E lem neşrah leke sadrek."},
  {id: 6092, sure_no: 2, ayet_no: 2, sure_adi: "İnşirah", text: "وَوَضَعْنَا عَنْكَ وِزْرَكَۙ", translation: "Yükünü üzerinden atmadık mı?", transliteration: "Ve vedagna anke vizrek."},
  {id: 6093, sure_no: 3, ayet_no: 3, sure_adi: "İnşirah", text: "اَلَّـذ۪ٓي اَنْقَضَ ظَهْرَكَۙ", translation: "Belini büken yükünü?", transliteration: "Ellezi enkada zahrek."},
  {id: 6094, sure_no: 4, ayet_no: 4, sure_adi: "İnşirah", text: "وَرَفَعْنَا لَكَ ذِكْرَكَۜ", translation: "Senin zikrini yükseltmedik mi?", transliteration: "Ve refa'na leke zikrek."},
  {id: 6095, sure_no: 5, ayet_no: 5, sure_adi: "İnşirah", text: "فَاِنَّ مَعَ الْعُسْرِ يُسْراًۙ", translation: "Zira zorlukla beraber bir kolaylık vardır.", transliteration: "Fe inne maal usri yusra."},
  {id: 6096, sure_no: 6, ayet_no: 6, sure_adi: "İnşirah", text: "اِنَّ مَعَ الْعُسْرِ يُسْراًۜ", translation: "Elbette zorlukla beraber bir kolaylık vardır.", transliteration: "İnne maal usri yusra."},
  {id: 6097, sure_no: 7, ayet_no: 7, sure_adi: "İnşirah", text: "فَاِذَا فَرَغْتَ فَانْصَبْۙ", translation: "O halde her fırsatta kararlılıkla yeni şeyler yapmaya giriş.", transliteration: "Fe iza feragte fensab."},
  {id: 6098, sure_no: 8, ayet_no: 8, sure_adi: "İnşirah", text: "وَاِلٰى رَبِّكَ فَارْغَبْ", translation: "Ve yalnızca Rabb'ine yönel.", transliteration: "Ve ila rabbike fergab."},
  {id: 6099, sure_no: 1, ayet_no: 1, sure_adi: "Tin", text: "وَالتّ۪ينِ وَالزَّيْتُونِۙ", translation: "İncire ve zeytine ant olsun,", transliteration: "Vet tini vez zeytun."},
  {id: 6100, sure_no: 2, ayet_no: 2, sure_adi: "Tin", text: "وَطُورِ س۪ين۪ينَۙ", translation: "Ve Tur-i Sinin'e.", transliteration: "Ve turi sinin."},
  {id: 6101, sure_no: 3, ayet_no: 3, sure_adi: "Tin", text: "وَهٰذَا الْبَلَدِ الْاَم۪ينِۙ", translation: "Ve bu güvenli kente.", transliteration: "Ve hazel beledil emin."},
  {id: 6102, sure_no: 4, ayet_no: 4, sure_adi: "Tin", text: "لَقَدْ خَلَقْنَا الْاِنْسَانَ ف۪ٓي اَحْسَنِ تَقْو۪يمٍۘ", translation: "Ant olsun ki insanı en iyi biçimde yarattık.", transliteration: "Lekad halaknel insane fi ahseni takvim."},
  {id: 6103, sure_no: 5, ayet_no: 5, sure_adi: "Tin", text: "ثُمَّ رَدَدْنَاهُ اَسْفَلَ سَافِل۪ينَۙ", translation: "Sonra onu aşağıların aşağısına indirdik.", transliteration: "Summe redednahu esfele safilin."},
  {id: 6104, sure_no: 6, ayet_no: 6, sure_adi: "Tin", text: "اِلَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ اَجْرٌ غَيْرُ مَمْنُونٍۜ", translation: "Ancak iman eden ve salihatı yapanlar hariç. Onlar için minnet altında bırakmayan bir ödül vardır.", transliteration: "İllellezine amenu ve amilus salihati fe lehum ecrun gayru memnun."},
  {id: 6105, sure_no: 7, ayet_no: 7, sure_adi: "Tin", text: "فَمَا يُكَذِّبُكَ بَعْدُ بِالدّ۪ينِۜ", translation: "Hal böyleyken, sana dini yalanlatan nedir?", transliteration: "Fe ma yukezzibuke ba'du bid din."},
  {id: 6106, sure_no: 8, ayet_no: 8, sure_adi: "Tin", text: "اَلَيْسَ اللّٰهُ بِاَحْكَمِ الْحَاكِم۪ينَ", translation: "Allah; hüküm verenlerin, en iyi hüküm vereni değil mi?", transliteration: "E leysallahu bi ahkemil hakimin."},
  {id: 6107, sure_no: 1, ayet_no: 1, sure_adi: "Alak", text: "اِقْرَأْ بِاسْمِ رَبِّكَ الَّذ۪ي خَلَقَۚ", translation: "Yaratan Rabb'inin adıyla duyur.", transliteration: "Ikra'bismi rabbikellezi halak."},
  {id: 6108, sure_no: 2, ayet_no: 2, sure_adi: "Alak", text: "خَلَقَ الْاِنْسَانَ مِنْ عَلَقٍۚ", translation: "İnsanı alaktan yarattı.", transliteration: "Halakal insane min alak."},
  {id: 6109, sure_no: 3, ayet_no: 3, sure_adi: "Alak", text: "اِقْرَأْ وَرَبُّكَ الْاَكْرَمُۙ", translation: "Duyur, senin Rabb'in en büyük kerem sahibidir.", transliteration: "Ikra' ve rabbukel ekrem."},
  {id: 6110, sure_no: 4, ayet_no: 4, sure_adi: "Alak", text: "اَلَّذ۪ي عَلَّمَ بِالْقَلَمِۙ", translation: "Kalemle öğretendir.", transliteration: "Ellezi alleme bil kalem."},
  {id: 6111, sure_no: 5, ayet_no: 5, sure_adi: "Alak", text: "عَلَّمَ الْاِنْسَانَ مَا لَمْ يَعْلَمْۜ", translation: "İnsana bilmediğini öğretti.", transliteration: "Allemel insane ma lem ya'lem."},
  {id: 6112, sure_no: 6, ayet_no: 6, sure_adi: "Alak", text: "كَلَّٓا اِنَّ الْاِنْسَانَ لَيَطْغٰىۙ", translation: "Bu gerçeğe rağmen insan yine de tuğyan eder.", transliteration: "Kella innel insane le yatga."},
  {id: 6113, sure_no: 7, ayet_no: 7, sure_adi: "Alak", text: "اَنْ رَاٰهُ اسْتَغْنٰىۜ", translation: "Kendisini yeterli gördüğü için.", transliteration: "En reahustagna."},
  {id: 6114, sure_no: 8, ayet_no: 8, sure_adi: "Alak", text: "اِنَّ اِلٰى رَبِّكَ الرُّجْعٰىۜ", translation: "Oysaki dönüş Rabb'inedir.", transliteration: "İnne ila rabbiker ruc'a."},
  {id: 6115, sure_no: 9, ayet_no: 9, sure_adi: "Alak", text: "اَرَاَيْتَ الَّذ۪ي يَنْهٰىۙ", translation: "Gördün mü şu alıkoyanı?", transliteration: "E reeytellezi yenha."},
  {id: 6116, sure_no: 10, ayet_no: 10, sure_adi: "Alak", text: "عَبْداً اِذَا صَلّٰىۜ", translation: "Salla ederken bir kulu!", transliteration: "Abden iza salla."},
  {id: 6117, sure_no: 11, ayet_no: 11, sure_adi: "Alak", text: "اَرَاَيْتَ اِنْ كَانَ عَلَى الْهُدٰىۙ", translation: "Gördün mü? Ya o doğru yolda ise!", transliteration: "E reeyte in kane alel huda."},
  {id: 6118, sure_no: 12, ayet_no: 12, sure_adi: "Alak", text: "اَوْ اَمَرَ بِالتَّقْوٰىۜ", translation: "Veya takvayı buyuruyorsa?", transliteration: "Ev emera bit takva."},
  {id: 6119, sure_no: 13, ayet_no: 13, sure_adi: "Alak", text: "اَرَاَيْتَ اِنْ كَذَّبَ وَتَوَلّٰىۜ", translation: "Gördün mü; ya yalanlayıp ve yüz çevirdiyse?", transliteration: "E reeyte in kezzebe ve tevella."},
  {id: 6120, sure_no: 14, ayet_no: 14, sure_adi: "Alak", text: "اَلَمْ يَعْلَمْ بِاَنَّ اللّٰهَ يَرٰىۜ", translation: "Allah'ın kesinlikle her şeyi gördüğünü bilmiyor mu?", transliteration: "E lem ya'lem bi ennellahe yera."},
  {id: 6121, sure_no: 15, ayet_no: 15, sure_adi: "Alak", text: "كَلَّا لَئِنْ لَمْ يَنْتَهِ۬ لَنَسْفَعاً بِالنَّاصِيَةِۙ", translation: "Hayır, kesinlikle öyle değil! Eğer gerçekten vazgeçmezse perçeminden yakalarız.", transliteration: "Kella le in lem yentehi le nesfean bin nasıyeh."},
  {id: 6122, sure_no: 16, ayet_no: 16, sure_adi: "Alak", text: "نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍۚ", translation: "Yalancı ve yanlış yapan perçeminden.", transliteration: "Nasiyetin kazibetin hatıeh."},
  {id: 6123, sure_no: 17, ayet_no: 17, sure_adi: "Alak", text: "فَلْيَدْعُ نَادِيَهُۙ", translation: "Haydi, yardımcılarını çağırsın.", transliteration: "Felyed'u nadiyeh."},
  {id: 6124, sure_no: 18, ayet_no: 18, sure_adi: "Alak", text: "سَنَدْعُ الزَّبَانِيَةَۙ", translation: "Biz de zebanileri çağıracağız.", transliteration: "Sened'uz zebaniyeh."},
  {id: 6125, sure_no: 19, ayet_no: 19, sure_adi: "Alak", text: "كَلَّاۜ  لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ", translation: "Hayır, kesinlikle öyle değil! Ona boyun eğme; secde et ve yaklaş.", transliteration: "Kella, la tutı'hu vescud vakterib."},
  {id: 6126, sure_no: 1, ayet_no: 1, sure_adi: "Kadir", text: "اِنَّٓا اَنْزَلْنَاهُ ف۪ي لَيْلَةِ الْقَدْرِۚ", translation: "Kuşkusuz Biz onu Kad'r Gecesi'nde indirdik.", transliteration: "İnna enzelnahu fi leyletil kadr."},
  {id: 6127, sure_no: 2, ayet_no: 2, sure_adi: "Kadir", text: "وَمَٓا اَدْرٰيكَ مَا لَيْلَةُ الْقَدْرِۜ", translation: "Kad'r Gecesi'nin ne olduğunu sana bildiren nedir?", transliteration: "Ve ma edrake ma leyletul kadr."},
  {id: 6128, sure_no: 3, ayet_no: 3, sure_adi: "Kadir", text: "لَيْلَةُ الْقَدْرِ خَيْرٌ مِنْ اَلْفِ شَهْرٍۜ", translation: "Kad'r gecesi bin aydan daha hayırlıdır.", transliteration: "Leyletul kadri hayrun min elfi şehr."},
  {id: 6129, sure_no: 4, ayet_no: 4, sure_adi: "Kadir", text: "تَنَزَّلُ الْمَلٰٓئِكَةُ وَالرُّوحُ ف۪يهَا بِاِذْنِ رَبِّهِمْۚ مِنْ كُلِّ اَمْرٍۙۛ", translation: "Melekler ve ruh, onda Rabb'lerinin izniyle her bir emir için iner de iner.", transliteration: "Tenezzelul melaiketu ver ruhu fiha bi izni rabbihim min kulli emrin."},
  {id: 6130, sure_no: 5, ayet_no: 5, sure_adi: "Kadir", text: "سَلَامٌ۠ۛ هِيَ حَتّٰى مَطْلَعِ الْفَجْرِ", translation: "O, fecrin ağarmasına kadar selamdır.", transliteration: "Selamun, hiye hatta matlaıl fecr."},
  {id: 6131, sure_no: 1, ayet_no: 1, sure_adi: "Beyyine", text: "لَمْ يَكُنِ الَّذ۪ينَ كَفَرُوا مِنْ اَهْلِ الْـكِتَابِ وَالْمُشْرِك۪ينَ مُنْفَكّ۪ينَ حَتّٰى تَأْتِيَهُمُ الْبَيِّنَةُۙ", translation: "Kitap Ehli'nden ve Müşriklerden Kafirler kendilerine beyyine gelmeden ayrılığa düşmüş değillerdi.", transliteration: "Lem yekunillizine keferu min ehlil kitabi vel muşrikine munfekkine hatta te'tiye humul beyyineh."},
  {id: 6132, sure_no: 2, ayet_no: 2, sure_adi: "Beyyine", text: "رَسُولٌ مِنَ اللّٰهِ يَتْلُوا صُحُفاً مُطَهَّرَةًۙ", translation: "Onlara tertemiz sayfalar okuyan, Allah tarafından görevlendirilen bir Resul.", transliteration: "Resulun minallahi yetlu suhufen mutahharah."},
  {id: 6133, sure_no: 3, ayet_no: 3, sure_adi: "Beyyine", text: "ف۪يهَا كُتُبٌ قَيِّمَةٌۜ", translation: "Onda değerli kitaplar vardır.", transliteration: "Fiha kutubun kayyimeh."},
  {id: 6134, sure_no: 4, ayet_no: 4, sure_adi: "Beyyine", text: "وَمَا تَفَرَّقَ الَّذ۪ينَ اُو۫تُوا الْـكِتَابَ اِلَّا مِنْ بَعْدِ مَا جَٓاءَتْهُمُ الْبَيِّنَةُۜ", translation: "Kitap verilmiş olanlar, kendilerine beyyine geldikten sonra ayrılığa düştüler.", transliteration: "Ve ma teferrekallezine utul kitabe illa min ba'di ma caet humul beyyineh."},
  {id: 6135, sure_no: 5, ayet_no: 5, sure_adi: "Beyyine", text: "وَمَٓا اُمِرُٓوا اِلَّا لِيَعْبُدُوا اللّٰهَ مُخْلِص۪ينَ لَهُ الدّ۪ينَ حُنَفَٓاءَ وَيُق۪يمُوا الصَّلٰوةَ وَيُؤْتُوا الزَّكٰوةَ وَذٰلِكَ د۪ينُ الْقَيِّمَةِۜ", translation: "Oysa Allah'a kulluktan ve dini hanifler olarak O'na has kılmaktan ve salatı ikame etmekten, zekatı yapmaktan başka bir şeyle emrolunmadılar. İşte doğru din budur.", transliteration: "Ve ma umiru illa li ya'budullahe muhlisine lehud dine hunefae ve yukimus salate ve yu'tuz zekate ve zalike dinul kayyimeh."},
  {id: 6136, sure_no: 6, ayet_no: 6, sure_adi: "Beyyine", text: "اِنَّ الَّذ۪ينَ كَفَرُوا مِنْ اَهْلِ الْـكِتَابِ وَالْمُشْرِك۪ينَ ف۪ي نَارِ جَهَنَّمَ خَالِد۪ينَ ف۪يهَاۜ  اُو۬لٰٓئِكَ هُمْ شَرُّ الْبَرِيَّةِۜ", translation: "Kitap Ehli'nden Kafirler ve Müşrikler, içinde sürekli kalmak üzere Cehennem ateşindedirler. İşte onlar, yaratılmışların şerlileridirler.", transliteration: "İnnellezine keferu min ehlil kitabi velmuşrikine fi nari cehenneme halidine fiha, ulaike hum şerrul beriyeh."},
  {id: 6137, sure_no: 7, ayet_no: 7, sure_adi: "Beyyine", text: "اِنَّ الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِۙ اُو۬لٰٓئِكَ هُمْ خَيْرُ الْبَرِيَّةِۜ", translation: "İman Eden ve salihatı yapan kimseler; işte onlar yaratılanların hayırlı olanlarıdır.", transliteration: "İnnellezine amenu ve amilus salihati ulaike hum hayrul beriyyeh."},
  {id: 6138, sure_no: 8, ayet_no: 8, sure_adi: "Beyyine", text: "جَزَٓاؤُ۬هُمْ عِنْدَ رَبِّهِمْ جَنَّاتُ عَدْنٍ تَجْر۪ي مِنْ تَحْتِهَا الْاَنْهَارُ خَالِد۪ينَ ف۪يهَٓا اَبَداًۜ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوا عَنْهُۜ ذٰلِكَ لِمَنْ خَشِيَ رَبَّهُ", translation: "Onların, Rabb'leri yanındaki ödülleri; onlar için içinden nehirlerin aktığı cennetler vardır. Orada süresiz kalacaklar. Allah onlardan, onlar da O'ndan razı olmuşlardır. İşte bu, Rabb'ine huşu duyan kimseler içindir.", transliteration: "Cezauhum inde rabbihim cennatu adnin tecri min tahtihel enharu halidine fiha ebeda, radıyallahu anhum ve radu anh, zalike li men haşiye rabbeh."},
  {id: 6139, sure_no: 1, ayet_no: 1, sure_adi: "Zilzal", text: "اِذَا زُلْزِلَتِ الْاَرْضُ زِلْزَالَهَاۙ", translation: "Yeryüzü şiddetli sarsıntı ile sarsıldığı zaman,", transliteration: "İza zulziletil ardu zilzaleha."},
  {id: 6140, sure_no: 2, ayet_no: 2, sure_adi: "Zilzal", text: "وَاَخْرَجَتِ الْاَرْضُ اَثْقَالَهَاۙ", translation: "Yeryüzü ağırlıklarını çıkardığı;", transliteration: "Ve ahrecetil ardu eskaleha."},
  {id: 6141, sure_no: 3, ayet_no: 3, sure_adi: "Zilzal", text: "وَقَالَ الْاِنْسَانُ مَا لَهَاۚ", translation: "Ve insan, \"Ona ne oluyor?\" dediği,", transliteration: "Ve kalel insanu ma leha."},
  {id: 6142, sure_no: 4, ayet_no: 4, sure_adi: "Zilzal", text: "يَوْمَئِذٍ تُحَدِّثُ اَخْبَارَهَاۙ", translation: "O Gün, hadislerini anlatacak.", transliteration: "Yevme izin tuhaddisu ahbareha."},
  {id: 6143, sure_no: 5, ayet_no: 5, sure_adi: "Zilzal", text: "بِاَنَّ رَبَّكَ اَوْحٰى لَهَاۜ", translation: "Çünkü Rabb'in ona vahyetmiştir.", transliteration: "Bi enne rabbeke evhaleha."},
  {id: 6144, sure_no: 6, ayet_no: 6, sure_adi: "Zilzal", text: "يَوْمَئِذٍ يَصْدُرُ النَّاسُ اَشْتَاتاً لِيُرَوْا اَعْمَالَهُمْۜ", translation: "O Gün, insanlar amelleri kendilerine gösterilsin diye grup grup ortaya çıkacaklar.", transliteration: "Yevme izin yasdurun nasu eştaten li yurev a'malehum."},
  {id: 6145, sure_no: 7, ayet_no: 7, sure_adi: "Zilzal", text: "فَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْراً يَرَهُۜ", translation: "Artık kim zerre ağırlığınca iyilik yapmışsa onu görür.", transliteration: "Fe men ya'mel miskale zerretin hayren yereh."},
  {id: 6146, sure_no: 8, ayet_no: 8, sure_adi: "Zilzal", text: "وَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ شَراًّ يَرَهُ", translation: "Ve kim zerre ağırlığınca kötülük yapmışsa onu görür.", transliteration: "Ve men ya'mel miskale zerretin şerren yereh."},
  {id: 6147, sure_no: 1, ayet_no: 1, sure_adi: "Adiyat", text: "وَالْعَـادِيَاتِ ضَبْـحاًۙ", translation: "Soluk soluğa koşanlara ant olsun,", transliteration: "Vel adiyati dabha."},
  {id: 6148, sure_no: 2, ayet_no: 2, sure_adi: "Adiyat", text: "فَالْمُـورِيَاتِ قَـدْحاًۙ", translation: "Çarparak ateş çıkaranlara,", transliteration: "Fel muriyati kadha."},
  {id: 6149, sure_no: 3, ayet_no: 3, sure_adi: "Adiyat", text: "فَالْمُغ۪يرَاتِ صُبْحاًۙ", translation: "Sabahleyin akın edenlere,", transliteration: "Fel mugirati subha."},
  {id: 6150, sure_no: 4, ayet_no: 4, sure_adi: "Adiyat", text: "فَاَثَرْنَ بِه۪ نَقْعاًۙ", translation: "Sonra toz koparanlara,", transliteration: "Fe eserne bihi nak'a."},
  {id: 6151, sure_no: 5, ayet_no: 5, sure_adi: "Adiyat", text: "فَوَسَطْنَ بِه۪ جَمْعاًۙ", translation: "Sonra onunla bir topluluğa dalanlara ki,", transliteration: "Fe vesatne bihi cem'a."},
  {id: 6152, sure_no: 6, ayet_no: 6, sure_adi: "Adiyat", text: "اِنَّ الْاِنْسَانَ لِرَبِّه۪ لَكَنُودٌۚ", translation: "Kuşkusuz o insan Rabb'ine karşı çok nankördür.", transliteration: "İnnel insane li rabbihi le kenud."},
  {id: 6153, sure_no: 7, ayet_no: 7, sure_adi: "Adiyat", text: "وَاِنَّهُ عَلٰى ذٰلِكَ لَشَه۪يدٌۚ", translation: "Ve o da buna tanıktır.", transliteration: "Ve innehu ala zalike le şehid."},
  {id: 6154, sure_no: 8, ayet_no: 8, sure_adi: "Adiyat", text: "وَاِنَّهُ لِحُبِّ الْخَيْرِ لَشَد۪يدٌۜ", translation: "Doğrusu o hayrı aşırı derecede sever.", transliteration: "Ve innehu li hubbil hayri le şedid."},
  {id: 6155, sure_no: 9, ayet_no: 9, sure_adi: "Adiyat", text: "اَفَلَا يَعْلَمُ اِذَا بُعْثِرَ مَا فِي الْقُبُورِۙ", translation: "Bilmez mi kabirlerde olanlar çıkarıldığı zaman,", transliteration: "E fe la ya'lemu iza bu'sirama fil kubur."},
  {id: 6156, sure_no: 10, ayet_no: 10, sure_adi: "Adiyat", text: "وَحُصِّلَ مَا فِي الصُّدُورِۙ", translation: "Ve göğüslerde olanlar açığa çıkarıldığı zaman,", transliteration: "Ve hussıle ma fis sudur."},
  {id: 6157, sure_no: 11, ayet_no: 11, sure_adi: "Adiyat", text: "اِنَّ رَبَّهُمْ بِهِمْ يَوْمَئِذٍ لَخَب۪يرٌ", translation: "O Gün Rabb'lerinin her şeylerinden haberdar olduğunu.", transliteration: "İnne rabbehum bihim yevme izin le habir."},
  {id: 6158, sure_no: 1, ayet_no: 1, sure_adi: "Karia", text: "اَلْقَارِعَةُۙ", translation: "Kari'a.", transliteration: "El kariah."},
  {id: 6159, sure_no: 2, ayet_no: 2, sure_adi: "Karia", text: "مَا الْقَارِعَةُۚ", translation: "Karia nedir?", transliteration: "Mel kariah."},
  {id: 6160, sure_no: 3, ayet_no: 3, sure_adi: "Karia", text: "وَمَٓا اَدْرٰيكَ مَا الْقَارِعَةُۜ", translation: "Karia'nın ne olduğunu sen nereden bileceksin?", transliteration: "Ve ma edrake mel kariah."},
  {id: 6161, sure_no: 4, ayet_no: 4, sure_adi: "Karia", text: "يَوْمَ يَكُونُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوثِۙ", translation: "O gün insanlar sağa sola uçuşan pervaneler gibi olurlar.", transliteration: "Yevme yekunun nasu kel feraşil mebsus."},
  {id: 6162, sure_no: 5, ayet_no: 5, sure_adi: "Karia", text: "وَتَكُونُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوشِۜ", translation: "Dağlar etrafa saçılmış renkli yün gibi olur.", transliteration: "Ve tekunul cibalu kel ıhnil menfuş."},
  {id: 6163, sure_no: 6, ayet_no: 6, sure_adi: "Karia", text: "فَاَمَّا مَنْ ثَقُلَتْ مَوَاز۪ينُهُۙ", translation: "Kimin tartısı ağır gelirse,", transliteration: "Fe emma men sekulet mevazinuh."},
  {id: 6164, sure_no: 7, ayet_no: 7, sure_adi: "Karia", text: "فَهُوَ ف۪ي ع۪يشَةٍ رَاضِيَةٍۜ", translation: "O hoşnut edici bir yaşayış içindedir.", transliteration: "Fe huve fi işetin radiyeh."},
  {id: 6165, sure_no: 8, ayet_no: 8, sure_adi: "Karia", text: "وَاَمَّا مَنْ خَفَّتْ مَوَاز۪ينُهُۙ", translation: "Kimin tartısı hafif gelirse,", transliteration: "Ve emma men haffet mevazinuh."},
  {id: 6166, sure_no: 9, ayet_no: 9, sure_adi: "Karia", text: "فَاُمُّهُ هَاوِيَةٌۜ", translation: "Artık onun anası haviyedir.", transliteration: "Fe ummuhu haviyeh."},
  {id: 6167, sure_no: 10, ayet_no: 10, sure_adi: "Karia", text: "وَمَٓا اَدْرٰيكَ مَا هِيَهْۜ", translation: "O, kızgın bir ateştir.", transliteration: "Ve ma edrake mahiyeh."},
  {id: 6168, sure_no: 11, ayet_no: 11, sure_adi: "Karia", text: "نَارٌ حَامِيَةٌ", translation: "Onun ne olduğunu sen nereden bileceksin?", transliteration: "Narun hamiyeh."},
  {id: 6169, sure_no: 1, ayet_no: 1, sure_adi: "Tekasür", text: "اَلْهٰيكُمُ التَّكَاثُرُۙ", translation: "Çoğaltma isteği sizi oyaladı.", transliteration: "Elhakumut tekasur."},
  {id: 6170, sure_no: 2, ayet_no: 2, sure_adi: "Tekasür", text: "حَتّٰى زُرْتُمُ الْمَقَابِرَۜ", translation: "Ta ki ölüp kabirlere girinceye kadar.", transliteration: "Hatta zurtumul mekabir."},
  {id: 6171, sure_no: 3, ayet_no: 3, sure_adi: "Tekasür", text: "كَلَّا سَوْفَ تَعْلَمُونَۙ", translation: "Hayır, asla bildiğiniz gibi değil; yakında bileceksiniz.", transliteration: "Kella sevfe ta'lemun."},
  {id: 6172, sure_no: 4, ayet_no: 4, sure_adi: "Tekasür", text: "ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَۜ", translation: "Yine hayır, bildiğiniz gibi değil; yakında bileceksiniz.", transliteration: "Summe kella sevfe ta'lemun."},
  {id: 6173, sure_no: 5, ayet_no: 5, sure_adi: "Tekasür", text: "كَلَّا لَوْ تَعْلَمُونَ عِلْمَ الْيَق۪ينِۜ", translation: "Hayır, asla bildiğiniz gibi değil, keşke kesin bilgi ile bilseydiniz.", transliteration: "Kella lev ta'lemune ilmel yakin."},
  {id: 6174, sure_no: 6, ayet_no: 6, sure_adi: "Tekasür", text: "لَتَرَوُنَّ الْجَح۪يمَۙ", translation: "Bilesiniz ki kesinlikle alevli ateşle karşı karşıya kalacaksınız.", transliteration: "Le terevunnel cahim."},
  {id: 6175, sure_no: 7, ayet_no: 7, sure_adi: "Tekasür", text: "ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَق۪ينِۙ", translation: "Sonra onu gözlerinizle kesin olarak göreceksiniz.", transliteration: "Summe le terevunneha aynel yakin."},
  {id: 6176, sure_no: 8, ayet_no: 8, sure_adi: "Tekasür", text: "ثُمَّ لَتُسْـَٔلُنَّ يَوْمَئِذٍ عَنِ النَّع۪يمِ", translation: "Sonra O Gün kesinlikle nimetten sorulacaksınız.", transliteration: "Summe le tus'elunne yevmeizin anin naim."},
  {id: 6177, sure_no: 1, ayet_no: 1, sure_adi: "Asr", text: "وَالْعَصْرِۙ", translation: "Asra ant olsun,", transliteration: "Vel asr."},
  {id: 6178, sure_no: 2, ayet_no: 2, sure_adi: "Asr", text: "اِنَّ الْاِنْسَانَ لَف۪ي خُسْرٍۙ", translation: "Kuşkusuz o insan kesinlikle hüsrandadır.", transliteration: "İnnel insane le fi husr."},
  {id: 6179, sure_no: 3, ayet_no: 3, sure_adi: "Asr", text: "اِلَّا الَّذ۪ينَ اٰمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَتَوَاصَوْا بِالْحَقِّ وَتَوَاصَوْا بِالصَّبْرِ", translation: "Ancak iman edenler, salihatı yapanlar, hakkı tavsiye edenler ve sabrı tavsiye edenler hariç.", transliteration: "İllellezine amenu ve amilus salihati ve tevasav bil hakkı ve tevasav bis sabr."},
  {id: 6180, sure_no: 1, ayet_no: 1, sure_adi: "Hümeze", text: "وَيْلٌ لِكُلِّ هُمَزَةٍ لُمَزَةٍۨۙ", translation: "Arkadan çekiştirenlerin, kaş göz hareketiyle alay edenlerin vay haline!", transliteration: "Veylun li kulli humezetin lumezeh."},
  {id: 6181, sure_no: 2, ayet_no: 2, sure_adi: "Hümeze", text: "اَلَّذ۪ي جَمَعَ مَالاً وَعَدَّدَهُۙ", translation: "O ki malı toplayıp, onun hesabını yapıp durur.", transliteration: "Ellezi cemea malen ve addedeh."},
  {id: 6182, sure_no: 3, ayet_no: 3, sure_adi: "Hümeze", text: "يَحْسَبُ اَنَّ مَالَهُٓ اَخْلَدَهُۚ", translation: "Malın kendisini sonsuza dek yaşatacağını sanıyor.", transliteration: "Yahsebu enne malehu ahledeh."},
  {id: 6183, sure_no: 4, ayet_no: 4, sure_adi: "Hümeze", text: "كَلَّا لَيُنْبَذَنَّ فِي الْحُطَمَةِۘ", translation: "Hayır, kesinlikle bildiği gibi değil, kesinlikle o Hutame'ye atılacak.", transliteration: "Kella le yunbezenne fil hutameh."},
  {id: 6184, sure_no: 5, ayet_no: 5, sure_adi: "Hümeze", text: "وَمَٓا اَدْرٰيكَ مَا الْحُطَمَةُۜ", translation: "Hutame'nin ne olduğunu sen nereden bileceksin?", transliteration: "Ve ma edrake mel hutameh."},
  {id: 6185, sure_no: 6, ayet_no: 6, sure_adi: "Hümeze", text: "نَارُ اللّٰهِ الْمُوقَدَةُۙ", translation: "Allah'ın tutuşturulmuş ateşidir.", transliteration: "Narullahil mukadeh."},
  {id: 6186, sure_no: 7, ayet_no: 7, sure_adi: "Hümeze", text: "اَلَّت۪ي تَطَّلِعُ عَلَى الْاَفْـِٔدَةِۜ", translation: "Yüreklere işleyen bir ateş.", transliteration: "Elleti tettaliu alel ef'ideh."},
  {id: 6187, sure_no: 8, ayet_no: 8, sure_adi: "Hümeze", text: "اِنَّهَا عَلَيْهِمْ مُؤْصَدَةٌۙ", translation: "Kuşkusuz o, onların üzerine kapatılmıştır.", transliteration: "İnneha aleyhim mu'sadeh."},
  {id: 6188, sure_no: 9, ayet_no: 9, sure_adi: "Hümeze", text: "ف۪ي عَمَدٍ مُمَدَّدَةٍ", translation: "Uzatılmış sütunlar arasında.", transliteration: "Fi amedin mumeddedeh."},
  {id: 6189, sure_no: 1, ayet_no: 1, sure_adi: "Fil", text: "اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِاَصْحَابِ الْف۪يلِۜ", translation: "Görmedin mi? Rabb'inin fil sahiplerine ne yaptığını?", transliteration: "E lem tere keyfe feale rabbuke bi ashabil fil."},
  {id: 6190, sure_no: 2, ayet_no: 2, sure_adi: "Fil", text: "اَلَمْ يَجْعَلْ كَيْدَهُمْ ف۪ي تَضْل۪يلٍۙ", translation: "Onların tuzaklarını boşa çıkarmadı mı?", transliteration: "E lem yec'al keydehum fi tadlil."},
  {id: 6191, sure_no: 3, ayet_no: 3, sure_adi: "Fil", text: "وَاَرْسَلَ عَلَيْهِمْ طَيْراً اَبَاب۪يلَۙ", translation: "Onların üzerlerine sürü sürü kuşlar gönderdi;", transliteration: "Ve ersele aleyhim tayren ebabil."},
  {id: 6192, sure_no: 4, ayet_no: 4, sure_adi: "Fil", text: "تَرْم۪يهِمْ بِحِجَارَةٍ مِنْ سِجّ۪يلٍۖۙ", translation: "Onlara çamurdan sertleşmiş taşlar atan.", transliteration: "Termihim bi hicaretin min siccil."},
  {id: 6193, sure_no: 5, ayet_no: 5, sure_adi: "Fil", text: "فَجَعَلَهُمْ كَعَصْفٍ مَأْكُولٍ", translation: "Böylece onları yenmiş ekin yaprağı gibi yaptı.", transliteration: "Fe cealehum keasfin me'kul."},
  {id: 6194, sure_no: 1, ayet_no: 1, sure_adi: "Kureyş", text: "لِا۪يلَافِ قُرَيْشٍۙ", translation: "Kureyş'i alıştırdığı için;", transliteration: "Li ilafi kureyş."},
  {id: 6195, sure_no: 2, ayet_no: 2, sure_adi: "Kureyş", text: "ا۪يلَافِهِمْ رِحْلَةَ الشِّتَٓاءِ وَالصَّيْفِۚ", translation: "Onları kış ve yaz yolculuğuna alıştırdığı1 için;", transliteration: "İlafihim rıhleteş şitai ves sayf."},
  {id: 6196, sure_no: 3, ayet_no: 3, sure_adi: "Kureyş", text: "فَلْيَعْبُدُوا رَبَّ هٰذَا الْبَيْتِۙ", translation: "Bu evin Rabb'ine kulluk etsinler.", transliteration: "Fel ya'budu rabbe hazel beyt."},
  {id: 6197, sure_no: 4, ayet_no: 4, sure_adi: "Kureyş", text: "اَلَّذ۪ٓي اَطْعَمَهُمْ مِنْ جُوعٍ وَاٰمَنَهُمْ مِنْ خَوْفٍ", translation: "Onları yedirip açlıktan korudu ve onları korkudan güvene kavuşturdu.", transliteration: "Ellezi at'amehum min cuın ve amenehum min havf."},
  {id: 6198, sure_no: 1, ayet_no: 1, sure_adi: "Maun", text: "اَرَاَيْتَ الَّذ۪ي يُكَذِّبُ بِالدّ۪ينِۜ", translation: "Dini yalanlayanı görüyorsun değil mi?", transliteration: "E raeytellezi yukezzibu bid din."},
  {id: 6199, sure_no: 2, ayet_no: 2, sure_adi: "Maun", text: "فَذٰلِكَ الَّذ۪ي يَدُعُّ الْيَت۪يمَۙ", translation: "İşte o, yetimi itip kakar.", transliteration: "Fe zalikellezi yedu'ul yetim."},
  {id: 6200, sure_no: 3, ayet_no: 3, sure_adi: "Maun", text: "وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْك۪ينِۜ", translation: "Miskini doyurmaya teşvik etmez.", transliteration: "Ve la yahuddu ala taamil miskin."},
  {id: 6201, sure_no: 4, ayet_no: 4, sure_adi: "Maun", text: "فَوَيْلٌ لِلْمُصَلّ۪ينَۙ", translation: "Vay haline salat edenlerin!", transliteration: "Fe veylun lil musallin."},
  {id: 6202, sure_no: 5, ayet_no: 5, sure_adi: "Maun", text: "اَلَّذ۪ينَ هُمْ عَنْ صَلَاتِهِمْ سَاهُونَۙ", translation: "Onlar ki salatlarından gafletteler.", transliteration: "Ellezine hum an salatihim sahun."},
  {id: 6203, sure_no: 6, ayet_no: 6, sure_adi: "Maun", text: "اَلَّذ۪ينَ هُمْ يُرَٓاؤُ۫نَۙ", translation: "Onlar gösteriş yapanlardır.", transliteration: "Ellezine hum yuraun."},
  {id: 6204, sure_no: 7, ayet_no: 7, sure_adi: "Maun", text: "وَيَمْنَعُونَ الْمَاعُونَ", translation: "Ve küçük, basit, sıradan yardımı bile esirgerler.", transliteration: "Ve yemneunel maun."},
  {id: 6205, sure_no: 1, ayet_no: 1, sure_adi: "Kevser", text: "اِنَّٓا اَعْطَيْنَاكَ الْـكَوْثَرَۜ", translation: "Kuşkusuz Biz sana kevseri verdik.", transliteration: "İnna a'taynakel kevser."},
  {id: 6206, sure_no: 2, ayet_no: 2, sure_adi: "Kevser", text: "فَصَلِّ لِرَبِّكَ وَانْحَرْۜ", translation: "O halde Rabb'in için salat et ve zorlukları göğüsle.", transliteration: "Fe salli li rabbike venhar."},
  {id: 6207, sure_no: 3, ayet_no: 3, sure_adi: "Kevser", text: "اِنَّ شَانِئَكَ هُوَ الْاَبْتَرُ", translation: "Kuşkusuz o sana buğzeden, sonu olmayanın ta kendisidir.", transliteration: "İnne şanieke huvel ebter."},
  {id: 6208, sure_no: 1, ayet_no: 1, sure_adi: "Kafirun", text: "قُلْ يَٓا اَيُّهَا الْـكَافِرُونَۙ", translation: "De ki: \"Ey kafirler.\"", transliteration: "Kul ya eyyuhel kafirun."},
  {id: 6209, sure_no: 2, ayet_no: 2, sure_adi: "Kafirun", text: "لَٓا اَعْبُدُ مَا تَعْبُدُونَۙ", translation: "\"Ben, sizin kulluk ettiğinize asla kulluk etmem.\"", transliteration: "La a'budu ma ta'budun."},
  {id: 6210, sure_no: 3, ayet_no: 3, sure_adi: "Kafirun", text: "وَلَٓا اَنْتُمْ عَابِدُونَ مَٓا اَعْبُدُۚ", translation: "\"Siz de benim kulluk ettiğime kulluk etmezsiniz.\"", transliteration: "Ve la entum abidune ma a'bud."},
  {id: 6211, sure_no: 4, ayet_no: 4, sure_adi: "Kafirun", text: "وَلَٓا اَنَا۬ عَابِدٌ مَا عَبَدْتُمْۙ", translation: "\"Ben, sizin kulluk ettiğinize asla kulluk edecek değilim.\"", transliteration: "Ve la ene abidun ma abedtum."},
  {id: 6212, sure_no: 5, ayet_no: 5, sure_adi: "Kafirun", text: "وَلَٓا اَنْتُمْ عَابِدُونَ مَٓا اَعْبُدُۜ", translation: "\"Siz de benim kulluk ettiğime kulluk edecek değilsiniz.\"", transliteration: "Ve la entum abidune ma a'bud."},
  {id: 6213, sure_no: 6, ayet_no: 6, sure_adi: "Kafirun", text: "لَـكُمْ د۪ينُكُمْ وَلِيَ د۪ينِ", translation: "\"Sizin dininiz size, benim dinim de bana.\"", transliteration: "Lekum dinukum ve liye din."},
  {id: 6214, sure_no: 1, ayet_no: 1, sure_adi: "Nasr", text: "اِذَا جَٓاءَ نَصْرُ اللّٰهِ وَالْفَتْحُۙ", translation: "Allah'ın yardımı ve fetih geldiği zaman,", transliteration: "İza cae nasrullahi vel feth."},
  {id: 6215, sure_no: 2, ayet_no: 2, sure_adi: "Nasr", text: "وَرَاَيْتَ النَّاسَ يَدْخُلُونَ ف۪ي د۪ينِ اللّٰهِ اَفْوَاجاًۙ", translation: "İnsanların Allah'ın dinine dalga dalga girdiklerini gördüğün zaman,", transliteration: "Ve reeyten nase yedhulune fi dinillahi efvaca."},
  {id: 6216, sure_no: 3, ayet_no: 3, sure_adi: "Nasr", text: "فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُۜ  اِنَّهُ  كَانَ تَوَّاباً", translation: "O zaman, Rabb'ini hamd ile tesbih et. Ve O'ndan mağfiret dile. Kuşkusuz O, tevbeleri kabul edendir.", transliteration: "Fe sebbih bi hamdi rabbike vestagfirh, innehu kane tevvaba."},
  {id: 6217, sure_no: 1, ayet_no: 1, sure_adi: "Tebbet", text: "تَبَّتْ  يَدَٓا اَب۪ي لَهَبٍ وَتَبَّۜ", translation: "Ebu Leheb'in iki eli kurudu. Ve kendisi de yok oldu.", transliteration: "Tebbet yeda ebi lehebin ve tebb."},
  {id: 6218, sure_no: 2, ayet_no: 2, sure_adi: "Tebbet", text: "مَٓا اَغْنٰى عَنْهُ مَالُهُ وَمَا كَسَبَۜ", translation: "Malı da kazandıkları da ona bir yarar sağlamadı.", transliteration: "Ma agna anhu maluhu ve ma keseb."},
  {id: 6219, sure_no: 3, ayet_no: 3, sure_adi: "Tebbet", text: "سَيَصْلٰى نَاراً ذَاتَ لَهَبٍۚ", translation: "Alevli ateşe atılacak.", transliteration: "Se yasla naren zate leheb."},
  {id: 6220, sure_no: 4, ayet_no: 4, sure_adi: "Tebbet", text: "وَامْرَاَتُهُۜ  حَمَّالَةَ الْحَطَبِۚ", translation: "Odun hamalı karısı da.", transliteration: "Vemreetuh, hammaletel hatab."},
  {id: 6221, sure_no: 5, ayet_no: 5, sure_adi: "Tebbet", text: "ف۪ي ج۪يدِهَا حَبْلٌ مِنْ مَسَدٍ", translation: "Boynunda liften bir urgan.", transliteration: "Fi cidiha hablun min mesed."},
  {id: 6222, sure_no: 1, ayet_no: 1, sure_adi: "İhlas", text: "قُلْ هُوَ اللّٰهُ اَحَدٌۚ", translation: "De ki: \"O Allah Ehad'dır.\"", transliteration: "Kul huvallahu ehad."},
  {id: 6223, sure_no: 2, ayet_no: 2, sure_adi: "İhlas", text: "اَللّٰهُ الصَّمَدُۚ", translation: "\"Allah Samed'dir.\"", transliteration: "Allahus samed."},
  {id: 6224, sure_no: 3, ayet_no: 3, sure_adi: "İhlas", text: "لَمْ يَلِدْ وَلَمْ يُولَدْۙ", translation: "O, ürememiş, üretilmemiştir.", transliteration: "Lem yelid ve lem yuled."},
  {id: 6225, sure_no: 4, ayet_no: 4, sure_adi: "İhlas", text: "وَلَمْ يَكُنْ لَهُ كُفُواً اَحَدٌ", translation: "\"Hiçbir şey O'nun dengi değildir.\"", transliteration: "Ve lem yekun lehu kufuven ehad."},
  {id: 6226, sure_no: 1, ayet_no: 1, sure_adi: "Felak", text: "قُلْ اَعُوذُ بِرَبِّ الْفَلَقِۙ", translation: "De ki: \"Felak'ın Rabb'ine sığınırım.\"", transliteration: "Kul euzu bi rabbil felak."},
  {id: 6227, sure_no: 2, ayet_no: 2, sure_adi: "Felak", text: "مِنْ شَرِّ مَا خَلَقَۙ", translation: "\"Yarattığı şeylerin şerrinden,\"", transliteration: "Min şerri ma halak."},
  {id: 6228, sure_no: 3, ayet_no: 3, sure_adi: "Felak", text: "وَمِنْ شَرِّ غَاسِقٍ اِذَا وَقَبَۙ", translation: "\"Ve çöktüğü zaman karanlığın şerrinden,\"", transliteration: "Ve min şerri gasikın iza vekab."},
  {id: 6229, sure_no: 4, ayet_no: 4, sure_adi: "Felak", text: "وَمِنْ شَرِّ النَّفَّاثَاتِ فِي الْعُقَدِۙ", translation: "\"Düğümlere üfleyenlerin şerrinden,\"", transliteration: "Ve min şerrin neffasati fil ukad."},
  {id: 6230, sure_no: 5, ayet_no: 5, sure_adi: "Felak", text: "وَمِنْ شَرِّ حَاسِدٍ اِذَا حَسَدَ", translation: "\"Ve kıskandığı zaman, kıskananın şerrinden.\"", transliteration: "Ve min şerri hasidin iza hased."},
  {id: 6231, sure_no: 1, ayet_no: 1, sure_adi: "Nas", text: "قُلْ اَعُوذُ بِرَبِّ النَّاسِۙ", translation: "De ki: \"İnsanların Rabb'ine sığınırım;\"", transliteration: "Kul euzu bi rabbin nas."},
  {id: 6232, sure_no: 2, ayet_no: 2, sure_adi: "Nas", text: "مَلِكِ النَّاسِۙ", translation: "\"İnsanların Melik'ine,\"", transliteration: "Melikin nas."},
  {id: 6233, sure_no: 3, ayet_no: 3, sure_adi: "Nas", text: "اِلٰهِ النَّاسِۙ", translation: "\"İnsanların İlah'ına.\"", transliteration: "İlahin nas."},
  {id: 6234, sure_no: 4, ayet_no: 4, sure_adi: "Nas", text: "مِنْ شَرِّ الْوَسْوَاسِ الْخَنَّاسِۙ", translation: "\"Hannasın vesvesesinin şerrinden,\"", transliteration: "Min şerril vesvasil hannas."},
  {id: 6235, sure_no: 5, ayet_no: 5, sure_adi: "Nas", text: "اَلَّذ۪ي يُوَسْوِسُ ف۪ي صُدُورِ النَّاسِۙ", translation: "\"O ki insanların göğüslerine vesvese verir,\"", transliteration: "Ellezi yuvesvisu fi sudurin nas."},
  {id: 6236, sure_no: 6, ayet_no: 6, sure_adi: "Nas", text: "مِنَ الْجِنَّةِ وَالنَّاسِ", translation: "\"Cinnlerden ve insanlardan.\"", transliteration: "Minel cinneti ven nas."},
];
